INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(18, 36, 5, 'Jojakim was vyf en twintig toe hy koning geword het en het elf jaar in Jerusalem geregeer. Hy het gedoen wat verkeerd is in die oë van die Here sy God.', 'AFR83'),
(18, 36, 6, 'Koning Nebukadnesar van Babel het vir Jojakim aangeval en hom geboei met bronskettings om hom na Babel toe te vat.', 'AFR83'),
(18, 36, 7, 'Nebukadnesar het van die goed in die huis van die Here ook na Babel toe gevat en dit in sy tempel in Babel gesit.', 'AFR83'),
(18, 36, 8, 'Die ander verhale oor Jojakim, die afskuwelike dinge wat hy gedoen het en wat met hom gebeur het, is opgeteken in Die Boek van die Konings van Israel en Juda. Sy seun Jojagin het hom as koning opgevolg.', 'AFR83'),
(18, 36, 9, 'Jojagin was agtien jaar toe hy koning geword het en het drie maande en tien dae in Jerusalem geregeer. Hy het gedoen wat verkeerd is in die oë van die Here.', 'AFR83'),
(18, 36, 10, 'Met die wisseling van die jaar het koning Nebukadnesar hom na Babel toe laat haal saam met die kosbare goed in die huis van die Here, en sy broer Sedekia koning oor Juda in Jerusalem gemaak.', 'AFR83'),
(18, 36, 11, 'Sedekia was een en twintig toe hy koning geword het en het elf jaar in Jerusalem geregeer.', 'AFR83'),
(18, 36, 12, 'Hy het gedoen wat verkeerd is in die oë van die Here sy God. Hy het nie berou getoon toe die profeet Jeremia hom op bevel van die Here aangespreek het nie.', 'AFR83'),
(18, 36, 13, 'Ook teen koning Nebukadnesar, aan wie hy voor God trou gesweer het, het hy hom verset. Hy was koppig en onversetlik in sy weiering om hom tot die Here die God van Israel te bekeer.', 'AFR83'),
(18, 36, 14, 'Al die leiers van die priesters en die volk het ook weer in groot troueloosheid verval na die voorbeeld van die afskuwelike optrede van die heidennasies, en hulle het die huis van die Here wat Hy in Jerusalem geheilig het, ontwy.', 'AFR83'),
(18, 36, 15, 'Die Here die God van hulle voorvaders het voortdurend aan hulle sy wil bekend gemaak deur middel van sy boodskappers, want Hy was begaan oor sy volk en sy woning,', 'AFR83'),
(18, 36, 16, 'maar hulle het die boodskappers van God uitgelag, sy woorde geminag en sy profete gespot totdat die toorn van die Here oor sy volk losgebreek het. Die straf was onherroeplik.', 'AFR83'),
(18, 36, 17, 'Die Here het die koning van die Galdeërs teen hulle laat optrek, en dié het die jongmanne van Juda doodgemaak binne in die heiligdom en nie jongman of meisie, grysaard of invalide ontsien nie. Die Here het almal in die mag van die koning van Babel gegee.', 'AFR83'),
(18, 36, 18, 'Alles wat in die tempel was, of dit groot was of klein, die skatte van die huis van die Here, van die koning en sy amptenare, alles het hy na Babel toe gevat.', 'AFR83'),
(18, 36, 19, 'Daarna het hulle die tempel afgebrand, die muur van Jerusalem afgebreek, al die beste huise afgebrand en alles wat van waarde was, verwoes.', 'AFR83'),
(18, 36, 20, 'Almal wat nie doodgemaak is nie, is na Babel toe weggevoer, waar hulle die koning van Babel en sy seuns se onderdane was totdat die Perse die bewind oorgeneem het.', 'AFR83'),
(18, 36, 21, 'So is die woord van die Here vervul wat deur middel van Jeremia gekom het. Die ballingskap het aangehou totdat die land vir sy sabbatsjare vergoed is. Die hele tyd dat die land woes gelê het, het dit ''n sabbatsrus geniet, ''n volle sewentig jaar.', 'AFR83'),
(18, 36, 22, 'In die eerste regeringsjaar van koning Kores van Persië is die belofte vervul wat die Here by monde van Jeremia gegee het: die Here het dit bewerk dat koning Kores van Persië besluit het om dwarsoor sy ryk ''n proklamasie te laat afkondig. Die proklamasie, wat ook skriftelik was, het soos volg gelui:', 'AFR83'),
(18, 36, 23, '“Koning Kores van Persië maak bekend: Die Here, die God van die hemel, het al die koninkryke van die aarde aan my gegee. Hy het my beveel om vir Hom ''n tempel in Jerusalem in Juda te bou. Enigeen onder julle wat aan sy volk behoort, kan teruggaan, en mag die Here sy God by hom wees.”', 'AFR83'),
(19, 1, 1, 'In die eerste regeringsjaar van koning Kores van Persië is die belofte vervul wat die Here by monde van Jeremia uitgespreek het: die Here het koning Kores van Persië laat besluit om dwarsoor sy ryk ''n proklamasie te laat afkondig. Die proklamasie, wat ook skriftelik was, het soos volg gelui:', 'AFR83'),
(19, 1, 2, '“Koning Kores van Persië maak bekend: Die Here die God van die hemel het al die koninkryke van die aarde aan my gegee. Hy het my beveel om vir Hom ''n tempel in Jerusalem in Juda te bou.', 'AFR83'),
(19, 1, 3, 'Enigiemand onder julle wat aan sy volk behoort — mag sy God by hom wees — mag na Jerusalem in Juda toe gaan en die tempel gaan bou vir die Here die God van Israel. Hy is God in Jerusalem.', 'AFR83'),
(19, 1, 4, 'Elkeen wat van dié volk nog oor is, waar hy ook al tydelik gewoon het, moet deur die mense van sy omgewing voorsien word van die nodige silwer, goud, gebruiksgoed en vee, tesame met ''n vrywillige gawe vir die tempel van God in Jerusalem.”', 'AFR83'),
(19, 1, 5, 'Die familiehoofde van Juda en Benjamin, asook die priesters en die Leviete, elkeen in wie God die voorneme gegee het, het toe klaargemaak om die huis van die Here in Jerusalem te gaan bou.', 'AFR83'),
(19, 1, 6, 'Al hulle bure het hulle hande gesterk deur silwerware en goud, gebruiksgoed, vee en allerlei kosbaarhede te gee, bo en behalwe al die vrywillige gawes.', 'AFR83'),
(19, 1, 7, 'Koning Kores het ook die goed laat haal wat in die huis van die Here was en wat Nebukadnesar uit Jerusalem weggevat en in die tempel van sy god gesit het.', 'AFR83'),
(19, 1, 8, 'Koning Kores van Persië het dit aan Mitredat, die sekretaris van die tesourie, laat oorhandig, en dié het ''n lys daarvan gemaak vir Sesbassar, hoof van die stam Juda.', 'AFR83'),
(19, 1, 9, 'Die volgende stukke was op die lys: 30 goue bakke, 1 000 silwerbakke, en — toe dit weer getel is — nog 29 by,', 'AFR83'),
(19, 1, 10, '30 goue komme, 410 silwerkomme na hertelling, en ''n 1 000 ander stukke.', 'AFR83'),
(19, 1, 11, 'Daar was altesame 5 400 stukke goud en silwer. Sesbassar het alles saamgevat toe hy die ballinge van Babel af na Jerusalem toe gebring het.', 'AFR83'),
(19, 2, 1, 'Hier volg ''n opgawe van die mense uit die provinsie Juda wat uit die ballingskap teruggekom het na Jerusalem en Juda toe, elkeen na sy eie stad toe. Hulle is na Babel toe weggevoer deur koning Nebukadnesar van Babel,', 'AFR83'),
(19, 2, 2, 'en hulle het teruggekom saam met Serubbabel, Jesua, Nehemia, Seraja, Reëlaja, Mordegai, Bilsan, Mispar, Bigwai, Regum en Baäna. Die getalle van die mense van die volk Israel was soos volg:', 'AFR83'),
(19, 2, 3, 'Ingedeel volgens families: Paros 2 172,', 'AFR83'),
