(19, 2, 23, 'Anatot 128,', 'AFRNLV'),
(19, 2, 24, 'Bet-Asmawet 42,', 'AFRNLV'),
(19, 2, 25, 'Kirjat-Jearim, Kefira, en Beërot 743,', 'AFRNLV'),
(19, 2, 26, 'Rama en Geba 621,', 'AFRNLV'),
(19, 2, 27, 'Mikmas 122,', 'AFRNLV'),
(19, 2, 28, 'Bet-El en Ai 223,', 'AFRNLV'),
