INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(1, 1, 1, 'Ekuqaleni uThixo wayephezu kwezulu nehlabathi.', 'XHO22'),
(1, 1, 2, 'Umhlaba wawusenyanyeni, ungento; Kwabakho ubumnyama phezu konogumbe womhlaba kwaye uMoya kaThixo wawuhamba phezu kwamanzi.', 'XHO22'),
(1, 1, 3, 'Wathi uThixo, Makubekho ukukhanya, kwabakho ukukhanya.', 'XHO22'),
(1, 1, 4, 'Wakubona ke uThixo ukukhanya ukuba kulungile, wahlula uThixo phakathi kokukhanya nobumnyama.', 'XHO22'),
(1, 1, 5, 'Wathi uThixo ukukhanya yiMini, wathi ubumnyama bubusuku. Kwabakho ke ubusuku, kwasa. Yayilusuku lokuqala.', 'XHO22'),
(1, 1, 6, 'Wathi uThixo, Makubekho ithuba phakathi kwamanzi, ube ngukwahlula amanzi esibhakabhakeni.', 'XHO22'),
(1, 1, 7, 'uThixo wenza isithuba, wawahlula amanzi angaphantsi kwaloo ndawo kumanzi angaphezu kwawo.', 'XHO22'),
(1, 1, 8, 'Wawabiza uThixo amazulu, kwahlwa, kwasa ke ngowesibini umhla.', 'XHO22'),
(1, 1, 9, 'Wathi uThixo, Amanzi angaphantsi kwamazulu makahlanganiselwe ndaweni-nye, kubonakale okomileyo. Kwaye kwenzeka.', 'XHO22'),
(1, 1, 10, 'Umhlaba owomileyo uthi ngumhlaba; yaye indibano yamanzi walubiza ulwandle, yaye uThixo wabona ukuba kulungile.', 'XHO22'),
(1, 1, 11, 'Wathi uThixo, Umhlaba mawuphume uhlaza, nemifuno evelisa imbewu, nemithi eyenza iziqhamo ngohlobo lwayo, embewu ikuyo, emhlabeni. Kwaye kwenzeka.', 'XHO22'),
(1, 1, 12, 'Umhlaba waphuma uhlaza, nemifuno evelisa imbewu ngohlobo lwayo, nemithi eyenza iziqhamo, embewu yayo ngohlobo lwayo. Wabona uThixo ukuba kulungile.', 'XHO22'),
(1, 1, 13, 'Kwahlwa, kwasa ke ngomhla wesithathu.', 'XHO22'),
(1, 1, 14, 'Wathi uThixo, Makubekho izikhanyiso esibhakabhakeni, ukuba zahlule imini kubusuku; kwaye makubekho imiqondiso namaxesha nemihla neminyaka.', 'XHO22'),
(1, 1, 15, 'Mababe njengezikhanyiso esibhakabhakeni, zokukhanyisela ihlabathi; kwaye kwenzeka ngolu hlobo.', 'XHO22'),
(1, 1, 16, 'Wenza uThixo izikhanyiso ezikhulu zazibini; Isikhanyiso esikhulu ukuba silawule imini, nesikhanyiso esincinane ukuba silawule imini; Wenza neenkwenkwezi.', 'XHO22'),
(1, 1, 17, 'Wazibeka uThixo esibhakabhakeni ukuba zikhanyise emhlabeni;', 'XHO22'),
(1, 1, 18, 'nokuba zilawule imini nobusuku, zikwahlule ukukhanya nobumnyama. Wabona uThixo ukuba kulungile.', 'XHO22'),
(1, 1, 19, 'Kwahlwa, kwasa ke ngomhla wesine.', 'XHO22'),
(1, 1, 20, 'Utsho uThixo ukuthi, Amanzi mawanyakazise imiphefumlo ephilileyo, neentaka eziphaphazelayo emazulwini;', 'XHO22'),
(1, 1, 21, 'Wenza uThixo oominenga mikhulu, nayo yonke imiphefumlo ephilileyo enambuzelayo, awanyakazela ngayo amanzi ngohlobo lwayo, neentaka zonke ezinamaphiko ngohlobo lwazo. Wabona uThixo ukuba kulungile.', 'XHO22'),
(1, 1, 22, 'Wazisikelela uThixo, wathi, Qhamani, nande, niwazalise amanzi olwandle;', 'XHO22'),
(1, 1, 23, 'Kwahlwa, kwaza kwaba ngumhla wesihlanu ngengomso.', 'XHO22'),
(1, 1, 24, 'Wathi uThixo, Umhlaba mawuphume imiphefumlo ephilileyo ngohlobo lwayo: izinto ezizitho zine, nezinambuzane, nezinto eziphilileyo zomhlaba ngohlobo lwazo. Kwaye kwenzeka ngolu hlobo.', 'XHO22'),
(1, 1, 25, 'Wenza uThixo izinto eziphilileyo zomhlaba ngohlobo lwazo, nezinto ezizitho zine ngohlobo lwazo, nezinto zonke ezinyakazelayo emhlabeni ngohlobo lwazo. Wabona uThixo ukuba kulungile.', 'XHO22'),
(1, 1, 26, 'Wathi uThixo, Masenze abantu ngokomfanekiselo wethu ngokufana nathi, babe nobukhosi ezintlanzini zolwandle, nasezintakeni zezulu, nasezintweni ezizitho zine, nasezinambuzaneni zonke ezinambuzelayo ehlabathini.', 'XHO22'),
(1, 1, 27, 'Wamdala ke uThixo umntu ngokomfanekiselo wakhe; wadala indoda nenkazana.', 'XHO22'),
(1, 1, 28, 'Wabasikelela uThixo, wathi kubo, Qhamani, nande, nihlale emhlabeni, niwulime; nobukhosi ezintlanzini zolwandle, nasezintakeni zezulu, nasezintweni zonke eziphilileyo ezinambuzelayo emhlabeni.', 'XHO22'),
(1, 1, 29, 'Wathi uThixo, Yabonani, ndininika yonke imithi evelisa imbewu, esemhlabeni wonke, nemithi yonke evelisa iziqhamo, ethwala imbewu; mayibe kukutya kwakho.', 'XHO22'),
(1, 1, 30, 'Kodwa zonke izinto eziphilileyo zehlabathi, neentaka zonke zezulu, nezinambuzane zonke ezisemhlabeni, ezihleli phezu kwazo, ndizinikele kuzo zonke imifuno ephuma kuyo ukuba zibe kukudla. Kwenzeke ngolu hlobo.', 'XHO22'),
(1, 1, 31, 'Wakukhangela uThixo konke akwenzileyo, nanko, ukuba kulungile kunene. Kwahlwa, kwasa ke ngomhla wesithandathu.', 'XHO22'),
(1, 2, 1, 'Agqitywa ke amazulu, nomhlaba, nawo wonke umkhosi okhoyo.', 'XHO22'),
(1, 2, 2, 'Wawugqiba ke uThixo ngomhla wesixhenxe umsebenzi wakhe awawenzayo; waphumla ngomhla wesixhenxe kuwo wonke umsebenzi wakhe.', 'XHO22'),
(1, 2, 3, 'uThixo uyalusikelela usuku lwesixhenxe aze alungcwalise, kuba waphumla ngalo emva kwawo wonke umsebenzi wokudala awawenzayo.', 'XHO22'),
(1, 2, 4, 'Yiyo le ke inzala yamazulu nehlabathi ekudalweni kwezo zinto, neyabantu abasemyezweni, mini wabenzayo uYehova uThixo.', 'XHO22'),
(1, 2, 5, 'Yonke imifuno yelizwe engekakhuli emhlabeni, nayo yonke imifuno yelizwe, ingekantshuli, kuba uYehova uThixo ebengekanisi mvula emhlabeni, akwabakho bani ukuwulima umhlaba hayi.', 'XHO22'),
(1, 2, 6, 'Kwenyuka inkungu iphuma ehlabathini, yawunyakamisa wonke umhlaba.', 'XHO22'),
(1, 2, 7, 'uYehova uThixo wambumba umntu ngothuli lwasemhlabeni, waphefumlela emathatheni akhe impefumlo yobomi; umntu ke waba ngumphefumlo ophilileyo.', 'XHO22'),
(1, 2, 8, 'uYehova uThixo watyala umyezo ngasempuma ye-Eden; wahlala apho umntu wamdalayo.', 'XHO22'),
(1, 2, 9, 'uYehova uThixo watyala emhlabeni imithi eyahlukahlukeneyo, eneziqhamo ezimnandi nezinencasa, nencasa ekutyeni. Phakathi emyezweni kwakumi umthi wobomi, nomthi wokwazi okulungileyo nokubi.', 'XHO22'),
(1, 2, 10, 'Umlambo wawuvela e-Eden ukuba uwunyakamise umyezo; ukusuka apho yahlulwa yazizahluko ezine.', 'XHO22'),
(1, 2, 11, 'Igama lowokuqala yiPison; elijikeleze lonke ilizwe lakwaHawila, apho kukho igolide;', 'XHO22'),
(1, 2, 12, 'Igolide yelo lizwe intle, kukho nebhalsam nelitye lebherilo.', 'XHO22'),
(1, 2, 13, 'Igama lowesibini umlambo yiGihon; ejikeleze lonke ilizwe lakwaKushi.', 'XHO22'),
(1, 2, 14, 'Igama lowesithathu umlambo yiHidekele; kwimpuma yeAsiriya. Owesine umlambo ngumEfrati.', 'XHO22'),
(1, 2, 15, 'uYehova uThixo wamthabatha umntu, wambeka emyezweni we-Eden, ukuba awutyale, awugcine.', 'XHO22'),
(1, 2, 16, 'uYehova uThixo wamwisela umthetho umntu, esithi, Yonke imithi yasendle ungayidla;', 'XHO22'),
(1, 2, 17, 'Kodwa uze ungadli kuwo umthi wokwazi okulungileyo nokubi, kuba mhla uthe wadla kuwo uya kufa ngokuqinisekileyo.', 'XHO22'),
(1, 2, 18, 'Wathi uYehova uThixo, Akulungile ukuba umntu abe yedwa; ndiya kumenzela umncedi olingana naye.', 'XHO22'),
(1, 2, 19, 'Wadala uYehova uThixo emhlabeni zonke izilo, neentaka zonke zezulu; wazisa kuAdam ukubona ukuba wothini na ukuzibiza; nento awayibizayo uAdam indalo nganye, yaba ligama lazo.', 'XHO22'),
(1, 2, 20, 'Wazithiya amagama uAdam zonke izinto ezizitho zine, neentaka zonke zezulu, nazo zonke izinto eziphilileyo zomhlaba; ke kuAdam akufunyanwanga mncedi ulingana naye.', 'XHO22'),
(1, 2, 21, 'uYehova uThixo wamlalisa ubuthongo umntu, walala;', 'XHO22'),
(1, 2, 22, 'uYehova uThixo wamdala umfazi ngethambo awalithabathayo endodeni, wamzisa endodeni.', 'XHO22'),
(1, 2, 23, 'Wathi uAdam, Eli ke ngoku lithambo lasemathanjeni am, yinyama yasenyameni yam; ukubizwa kothiwa ngumfazi, ngokuba ethatyathwe endodeni.', 'XHO22'),
(1, 2, 24, 'Ngoko ke, indoda iya kumshiya uyise nonina, inamathele emfazini wayo, baze babe nyama-nye.', 'XHO22'),
(1, 2, 25, 'Baye bobabini behamba ze, umntu lowo nomkakhe, bengenazintloni.', 'XHO22'),
(1, 3, 1, 'Ke kaloku inyoka yaye inobuqhophololo ngaphezu kwazo zonke izinto eziphilileyo zomhlaba, awazenzayo uYehova uThixo. Yathi kumfazi, Ewe, uthe uThixo, Musani ukuyidla yonke imithi yomyezo?', 'XHO22'),
(1, 3, 2, 'Wathi umfazi enyoka, Iziqhamo zemithi yasendle sinokuzidla.', 'XHO22'),
(1, 3, 3, 'Ke zona iziqhamo zomthi osemyezweni osesazulwini somyezo, wathi uThixo, Ningazidli, ningazichukumisi, hleze nife.', 'XHO22'),
(1, 3, 4, 'Yathi inyoka kumfazi, Anisayi kufa.', 'XHO22'),
(1, 3, 5, 'Kuba esazi uThixo ukuba, xa nithe nadla, oqabuka amehlo enu, nibe njengothixo, nazi okulungileyo nokubi.', 'XHO22'),
(1, 3, 6, 'Wabona umfazi ukuba umthi ulungele ukudliwa, uyawakholisa amehlo, nokuba umthi onqwenelekayo ebubulumko, wathabatha isiqhamo sawo, wadla, wanika indoda yakhe; wadla.', 'XHO22'),
(1, 3, 7, 'Aqabuka amehlo abo bobabini, baqonda ukuba kutheni bebodwa; bathunga amagqabi omkhiwane, benza ezabo iingubo.', 'XHO22'),
(1, 3, 8, 'Baliva izwi likaYehova uThixo, ekuhambeni kwakhe emyezweni, kwakuba pholileyo; bazímela uAdam nomkakhe ebusweni bukaYehova uThixo, phakathi kwemithi yomyezo.', 'XHO22'),
(1, 3, 9, 'uYehova uThixo wambiza umntu, wathi kuye, Uphi na?', 'XHO22'),
(1, 3, 10, 'Wathi uAtam, Ndive isandi sakho endle, ndasuka ndoyika, ngokuba ndingakholosanga; ndazimela.', 'XHO22'),
(1, 3, 11, 'Wathi, Uxelelwe ngubani na, ukuba uwedwa? Udlile na kuwo umthi, endakuwisela umthetho ngawo, ukuba ungadli kuwo?', 'XHO22'),
(1, 3, 12, 'Wathi uAdam, Umfazi owandinikayo ukuba abe nam, nguye ondinike umthi, ndadla ke.', 'XHO22'),
(1, 3, 13, 'Wathi uYehova uThixo kumfazi, Yintoni na le uyenzileyo? Wathi umfazi: “Inyoka indikhohlisile, ndadla.', 'XHO22'),
(1, 3, 14, 'Wathi uYehova uThixo kwinyoka, Ngokuba uyenzile le nto, uqalekisiwe wena ngaphezu kweenkomo zonke, naphezu kwazo zonke izinto eziphilileyo zehlabathi; uya kuhamba ngomlomo wakho, udle uthuli yonke imihla yobomi bakho.', 'XHO22'),
(1, 3, 15, 'Ndiya kumisa ubutshaba phakathi kwakho nomfazi, naphakathi kwembewu yakho nembewu yakhe; iya kukunqumla intloko, kwaye uya kuyiluma isithende.', 'XHO22'),
(1, 3, 16, 'Wathi kumfazi, Ndiya kukwandisa kakhulu ukubulaleka kwakho ekumitheni; uya kuzala abantwana eneneni; inkanuko yakho ibe sendodeni yakho, ikulawule yona.', 'XHO22'),
(1, 3, 17, 'Wathi kuAdam, Ekubeni uliphulaphule izwi lomkakho, wadla kuwo umthi endakuwisela umthetho ngawo, ndathi, Musa ukusidla kuwo; uqalekisiwe umhlaba; uya kudla kuwo ubulaleka, yonke imihla yobomi bakho;', 'XHO22'),
(1, 3, 18, 'Uya kukuntshulela imithana enameva neenkunzane; nidle utyani belizwe;', 'XHO22'),
(1, 3, 19, 'Uya kudla isonka sokubila kobuso bakho ude ubuyele emhlabeni; ngokuba ukhutshiwe kuwe; ngokuba uluthuli, uya kubuyela kwaseluthulini.', 'XHO22'),
(1, 3, 20, 'uAdam walibiza igama lomkakhe ngokuthi nguEva; ngokuba yena engunina wabaphilileyo bonke.', 'XHO22'),
(1, 3, 21, 'uYehova uThixo wabenzela iingubo zezintsu uAdam nomkakhe, wabambathisa ngazo.', 'XHO22'),
(1, 3, 22, 'Yathi iNkosi uYehova: Yabonani, umntu ufana nomnye wethu, ukwazi okulungileyo nokubi; kwaye ngoku, xa akasolula isandla sakhe, wathabatha kuwo umthi wobomi, wadla kuwo, waza waphila ngonaphakade.', 'XHO22'),
(1, 3, 23, 'uYehova uThixo wamkhupha emyezweni we-Eden, ukuba asebenze umhlaba abethatyathwe kuwo.', 'XHO22'),
(1, 3, 24, 'Waza wayindulula indoda; Wabeka iikerubhi ngasempumalanga yomyezo wase-Eden, nelangatye lekrele elijikajikayo, elijikajikayo, ukuba ligcine indlela eya emthini wobomi.', 'XHO22'),
(1, 4, 1, 'uAdam wamazi uEva umkakhe; wamitha, wazala uKayin, wathi, Ndifumene indoda kuYehova.', 'XHO22'),
(1, 4, 2, 'Wazala naye uAbheli umninawa wakhe. Ke uAbheli waba ngumalusi, uKayin engumlimi.', 'XHO22'),
(1, 4, 3, 'Emva kwethuba uKayin wenza idini kuNdikhoyo ngeziqhamo zomhlaba.', 'XHO22'),
(1, 4, 4, 'Naye uAbheli wazisa kumazibulo omhlambi wakhe, namanqatha awo; uYehova wamphulaphula uAbheli nombingelelo wakhe.', 'XHO22'),
(1, 4, 5, 'Kodwa akazange amkhathalele uKayin nombingelelo wakhe. Wacaphuka kunene uKayin, ubuso bakhe bushwabene.', 'XHO22'),
(1, 4, 6, 'Wathi uYehova kuKayin, Uqumbeleni na? Yini na ukuba busangane ubuso bakho?', 'XHO22'),
(1, 4, 7, 'Ukuba uthe walungisa, anisayi kwamkeleka na? ukuba ke wenze kakuhle, isono sesikungxamele emnyango. Kwaye emva kwakho uya kuba ngumnqweno wakhe, kwaye uya kulawula phezu kwakhe.', 'XHO22'),
(1, 4, 8, 'Wathetha uKayin kuAbheli umninawa wakhe; bathi besendle, uKayin wamvukela uAbheli umninawa wakhe, wambulala.', 'XHO22'),
(1, 4, 9, 'Wathi uYehova kuKayin, Uphi na uAbheli, umninawa wakho? Athi: Andazi: ndingumgcini kabhuti wam?', 'XHO22'),
(1, 4, 10, 'Wathi, Wenze ntoni na? ilizwi legazi lomninawa wakho liyadanduluka kum emhlabeni.', 'XHO22'),
(1, 4, 11, 'Kaloku, uqalekisiwe wena kulo ilizwe elivule umlomo walo ukuba wamkele igazi lomntakwenu esandleni sakho;', 'XHO22'),
(1, 4, 12, 'Xa uwulima umhlaba, awusayi kuphinda ube namandla; uya kuba sisibhadubhadu nesibhadubhadu ehlabathini.', 'XHO22'),
(1, 4, 13, 'Wathi uKayin kuYehova, Isohlwayo sam sikhulu ngokungenakuthwala.', 'XHO22'),
(1, 4, 14, 'Yabona, undithumile namhla ehlabathini; ndiya kuzifihla ebusweni bakho; kwaye ndiya kuba sisibhadubhadu nesibhadubhadu ehlabathini; kwaye nabani na ondifumanayo uya kundibulala.', 'XHO22'),
(1, 4, 15, 'Wathi uYehova kuye, Ngako oko, bonke ababulala uKayin kophindezelwa ngokuphindwe kasixhenxe. uYehova wamphawula uKayin, ukuze kungabikho bani umfumanayo angambulala.', 'XHO22'),
(1, 4, 16, 'Kwaye uKayin wemka ebusweni beNkosi waza waya kuhlala kwilizwe laseNodi, ngasempumalanga ye-Eden.', 'XHO22'),
(1, 4, 17, 'Ke uKayin wamazi umkakhe; wamitha, wazala uEnoki; wakha umzi, wawuthiya ngegama lonyana wakhe, uEnoki.', 'XHO22'),
(1, 4, 18, 'uEnoki wazala uIradi; uIradi wazala uMehuyayeli; uMehuyaheli wazala uMetusaheli; uMetusaheli wazala uLameki;', 'XHO22'),
(1, 4, 19, 'uLameki wazeka abafazi ababini, igama lomnye belinguAda, igama lowesibini belinguZila.', 'XHO22'),
(1, 4, 20, 'uAda wazala uYabhali; waba nguyise wabahlala ezintenteni nabalusi.', 'XHO22'),
(1, 4, 21, 'Igama lomninawa wakhe belinguYubhali; waba nguyise wabo bonke abasebenzisa irhengqa namalungu.', 'XHO22'),
(1, 4, 22, 'uZila wazala uTubhali-kayin, obengumfundisi kuzo zonke iingcibi zobhedu nesinyithi; Udade boTubhalikayin ibinguNahama.', 'XHO22'),
(1, 4, 23, 'Wathi uLameki kubafazi bakhe, Ada noSila, liveni ilizwi lam; Zintombi zikaLameki, liveni ilizwi lam, ngokuba ndibulele indoda ngenxeba, nomfana ngomvandedwa.', 'XHO22'),
(1, 4, 24, 'Ukuba uKayin uya kuphindezelwa kasixhenxe, uLameki wophindezelwa kamashumi asixhenxe anesixhenxe.', 'XHO22'),
(1, 4, 25, 'uAdam wabuya wamazi umkakhe; wazala unyana, wathi igama lakhe nguSete. Ngokuba wathi, uThixo undinike enye imbewu ngoAbheli owambulala uKayin.', 'XHO22'),
(1, 4, 26, 'Kananjalo uSete wazalelwa unyana; Wamthiya igama elinguEnoshe; Baqala ke abantu ukulibiza igama likaYehova.', 'XHO22'),
(1, 5, 1, 'Yiyo le incwadi yezizukulwana zika-Adam. Mhla uThixo wamdala umntu, wamenza ngokomfanekiselo kaThixo;', 'XHO22'),
(1, 5, 2, 'Wadala indoda nenkazana; wabasikelela, wababiza ngokuba nguAdam ngomhla wokudalwa kwabo.', 'XHO22'),
(1, 5, 3, 'uAdam wahlala iminyaka elikhulu elinamanci mathathu, wazala unyana ngokomfanekiso wakhe, ngokomfanekiso wakhe; wamthiya igama elinguSete.', 'XHO22'),
(1, 5, 4, 'Imihla ka-Adam emveni kokuzala kwakhe uSete yaba yiminyaka emakhulu asibhozo; wazala oonyana neentombi.', 'XHO22'),
(1, 5, 5, 'Iyonke imihla ka-Adam, awahlala ngayo yaba yiminyaka emakhulu asithoba anamanci mathathu, wafa ke.', 'XHO22'),
(1, 5, 6, 'uSete wahlala iminyaka elikhulu elinantlanu, wazala uEnoshe.', 'XHO22'),
(1, 5, 7, 'Emva kokuzala uEnoshe, uSete wahlala iminyaka engamakhulu asibhozo anamazinyo asixhenxe, waza wazala oonyana neentombi.', 'XHO22'),
(1, 5, 8, 'Iyonke imihla kaSete yaba yiminyaka emakhulu asithoba anashumi-nye linamibini;', 'XHO22'),
(1, 5, 9, 'uEnoshe wahlala iminyaka emashumi asithoba, wazala uKenan.', 'XHO22'),
(1, 5, 10, 'Emva kokuzala kwakhe uKenan, uEnoshe wahlala iminyaka engamakhulu asithoba anamazinyo alishumi elinesihlanu, waza wazala oonyana neentombi.', 'XHO22'),
(1, 5, 11, 'Iyonke imihla kaEnoshe yaba yiminyaka emakhulu asithoba anamnye anamihlanu; wafa ke.', 'XHO22'),
(1, 5, 12, 'uKenan wahlala iminyaka emashumi asixhenxe, wazala uMahalalele.', 'XHO22'),
(1, 5, 13, 'Emva kokuzala uMahalalele, uKenan waphila iminyaka engamakhulu asibhozo anamashumi amane waza wazala oonyana neentombi.', 'XHO22'),
(1, 5, 14, 'Iyonke imihla kaKanan yaba yiminyaka emakhulu asithoba anashumi-nye; wafa ke.', 'XHO22'),
(1, 5, 15, 'uMahalalele wahlala iminyaka emashumi mathandathu anantlanu, wazala uYerede.', 'XHO22'),
(1, 5, 16, 'Emva kokuzala uYerede, uMahalalele waphila iminyaka engamakhulu asibhozo anamanci mathathu, wazala oonyana neentombi.', 'XHO22'),
(1, 5, 17, 'Iyonke imihla kaMahalalele yaba yiminyaka emakhulu asibhozo anamanci asithoba anesihlanu;', 'XHO22'),
(1, 5, 18, 'uYerede waphila iminyaka elikhulu elinamashumi amathandathu anesibini, waza wazala uEnoki.', 'XHO22'),
(1, 5, 19, 'Emva kokuzala uEnoki uYerede wahlala iminyaka engamakhulu asibhozo, waza wazala oonyana neentombi.', 'XHO22'),
(1, 5, 20, 'Yaye iyonke imihla kaYerede yaba yiminyaka emakhulu asithoba anamanci mathandathu anesibini, waza wafa.', 'XHO22'),
(1, 5, 21, 'uEnoki wahlala iminyaka emashumi mathandathu anantlanu, wazala uMethusela.', 'XHO22'),
(1, 5, 22, 'uEnoki wahamba noThixo emveni kokuzala kwakhe uMethusela iminyaka emakhulu mathathu, wazala oonyana neentombi.', 'XHO22'),
(1, 5, 23, 'Iyonke imihla kaEnoki yaba yiminyaka emakhulu mathathu, anamanci mathandathu anesihlanu;', 'XHO22'),
(1, 5, 24, 'uEnoki wahamba noThixo, akabakho; ngokuba uThixo wamthabatha.', 'XHO22'),
(1, 5, 25, 'uMethusela wahlala iminyaka elikhulu elinamanci asibhozo anesixhenxe, wazala uLameki.', 'XHO22'),
(1, 5, 26, 'uMethusela wahlala emveni kokuzala kwakhe uLameki iminyaka emakhulu asixhenxe anamanci asibhozo anamibini, wazala oonyana neentombi.', 'XHO22'),
(1, 5, 27, 'Iyonke imihla kaMethusela yaba yiminyaka emakhulu asithoba, anamanci mathandathu anesithoba;', 'XHO22'),
(1, 5, 28, 'uLameki wahlala iminyaka elikhulu elinamanci asibhozo anesibini, wazala unyana.', 'XHO22'),
(1, 5, 29, 'Kwaye wabiza uNowa waza wathi: Uya kusithuthuzela ngenxa yomsebenzi wethu nomzamo wezandla zethu ngenxa yelizwe elizisileyo iNkosi.', 'XHO22'),
(1, 5, 30, 'Emva kokuzala uNowa, uLameki wahlala iminyaka engamakhulu amahlanu anamashumi asibhozo yedwa aneshumi elinesihlanu, waza wazala oonyana neentombi.', 'XHO22'),
(1, 5, 31, 'Iyonke imihla kaLameki yaba yiminyaka emakhulu asixhenxe anamanci asixhenxe anesixhenxe, waza wafa.', 'XHO22'),
(1, 5, 32, 'uNowa ube eminyaka imakhulu mahlanu ezelwe, uNowa wazala ooShem noHam noYafete.', 'XHO22'),
(1, 6, 1, 'Kuthe bakuqala ukwanda abantu ehlabathini, kwazalwa iintombi.', 'XHO22'),
(1, 6, 2, 'Oonyana bakaThixo babona iintombi zabantu ukuba zizithande; bazizekela abafazi kubo bonke ababanyulayo.', 'XHO22'),
(1, 6, 3, 'Wathi uYehova, Umoya wam awusayi kuhlala usebenza emntwini, ngokuba eyinyama; imihla yakhe iya kuba yiminyaka elikhulu elinamanci mabini.', 'XHO22'),
(1, 6, 4, 'Ngaloo mihla bekukho iingxilimbela ehlabathini; Emveni koko, xeshikweni oonyana bakaThixo bangenayo kuzo iintombi zabantu, zabazalela oonyana, zaba ngamagorha amandulo, amadoda anegama.', 'XHO22'),
(1, 6, 5, 'Wabona uThixo ukuba ububi babantu bandile ehlabathini, zonke iingcamango zentliziyo yabo zibububi bodwa.', 'XHO22'),
(1, 6, 6, 'Wazohlwaya uYehova ngenxa yokuba emenzile umntu emhlabeni, waba buhlungu entliziyweni yakhe.', 'XHO22'),
(1, 6, 7, 'Wathi uYehova, Ndiya kumcima ehlabathini umntu endimdalileyo; umntu nesilwanyana. Nezinambuzane, neentaka zezulu; kuba ndiyaxolisa xa ndizenza.', 'XHO22'),
(1, 6, 8, 'uNowa yena wababalwa nguYehova.', 'XHO22'),
(1, 6, 9, 'Yiyo le ke inzala kaNowa. uNowa waye eyindoda elilungisa, egqibeleleyo, ezizukulwaneni zakhe. uNowa wahamba noThixo.', 'XHO22'),
(1, 6, 10, 'uNowa wazala oonyana abathathu: uShem, noHam, noYafete.', 'XHO22'),
(1, 6, 11, 'Ihlabathi lalibolile emehlweni kaThixo, yaye ihlabathi lalizele lugonyamelo.', 'XHO22'),
(1, 6, 12, 'uThixo walikhangela ihlabathi, nalo, libolile; ngokuba yonke inyama ilahlile indlela yayo ehlabathini.', 'XHO22'),
(1, 6, 13, 'Wathi uThixo kuNowa, Isiphelo senyama yonke sifikile phambi kobuso bam; ngokuba ihlabathi lizele lugonyamelo oluphuma kubo; yabona, ndiya kubonakalisa, bona nehlabathi.', 'XHO22'),
(1, 6, 14, 'Zenzele umkhombe ngomthi; Uze wenze amagumbi etyeyeni, uyithambise ngomthwebeba ngaphakathi nangaphandle.', 'XHO22'),
(1, 6, 15, 'Nantsi indlela oya kukwenza ngayo: Ubude bomkhombe bube ngamakhulu amathathu eekubhite, ububanzi bube ngamashumi amahlanu eekubhite, ukuphakama kwawo kube ziikubhite ezimashumi mathathu.', 'XHO22'),
(1, 6, 16, 'Wenze ikroba emkhombeni, ukuligqiba kwakho kube yikubhite enye, ithabathela phezulu; ulumise ucango lwetyeya ngecala; kumgangatho ophantsi, umgangatho wesibini nowesithathu kufuneka uyenze.', 'XHO22'),
(1, 6, 17, 'Uyabona, ndihlisa unogumbe wamanzi ehlabathini, wokonakalisa yonke inyama enomoya wokuphila phantsi kwezulu; kwaye yonke into esemhlabeni iya kufa.', 'XHO22'),
(1, 6, 18, 'Ndowumisa mna umnqophiso wam nani; ungene emkhombeni wena, noonyana bakho, nomkakho, nabafazi boonyana bakho, ndawonye nawe.', 'XHO22'),
(1, 6, 19, 'Kwizinto zonke eziphilileyo, kwinyama yonke, ngenisa emkhombeni ngambini kuzo zonke, ngohlobo ngalunye, ukuze zisindiswe nawe; iya kuba yindoda nomfazi.', 'XHO22'),
(1, 6, 20, 'Kwiintaka ngohlobo lwazo, kwiinkomo ngohlobo lwazo, ezinambuzaneni zonke zomhlaba ngohlobo lwazo, zibe ngambini kuzo zonke, eziya kungena kuwe, ukuba zisindiswe.', 'XHO22'),
(1, 6, 21, 'Thabatha konke ukudla okudliwayo, ukuqokelele; ibe kukutya kwenu nakokwabo.', 'XHO22'),
(1, 6, 22, 'Wenjenjalo uNowa; njengako konke uThixo abemwisele umthetho ngako, wenjenjalo.', 'XHO22'),
(1, 7, 1, 'Wathi uYehova kuNowa, Ngena, wena nendlu yakho yonke, emkhombeni; ngokuba ndibona wena ilungisa phambi kwam kwesi sizukulwana.', 'XHO22'),
(1, 7, 2, 'Kwizinto zonke ezizitho zine ezihlambulukileyo, thabatha kuzo zibe sixhenxe, inkunzi nemazi; kwizinto ezizitho zine eziziinqambi ngambini, ngambini, inkunzi nemazi yazo.', 'XHO22'),
(1, 7, 3, 'Nakwiintaka ezisixhenxe zezulu, inkunzi nemazi; ukugcina imbewu iphila kulo lonke ihlabathi.', 'XHO22'),
(1, 7, 4, 'Ndiya kunisa imvula ehlabathini iimini ezimashumi mane nobusuku obumashumi mane; kwaye yonke into ephilayo endayenzayo ndiya kuyitshabalalisa emhlabeni.', 'XHO22'),
(1, 7, 5, 'Wenza uNowa njengoko uYehova wamwiselayo umthetho.', 'XHO22'),
(1, 7, 6, 'uNowa ube eminyaka imakhulu mathandathu ezelwe, oko kunogumbe wagubungela umhlaba.', 'XHO22'),
(1, 7, 7, 'Wangena uNowa emkhombeni, enoonyana bakhe, nomkakhe, nabafazi boonyana bakhe, ndawonye naye, ngenxa yamanzi onogumbe.', 'XHO22'),
(1, 7, 8, 'Kwizinto ezizitho zine ezihlambulukileyo, nakwizinto ezizitho zine eziziinqambi, nakwiintaka, nakwizinto zonke ezinambuzelayo emhlabeni;', 'XHO22'),
(1, 7, 9, 'zangena zombini kuNowa emkhombeni, inkunzi nemazi, njengoko uThixo abemwisele umthetho uNowa.', 'XHO22'),
(1, 7, 10, 'Emva kweentsuku ezisixhenxe amanzi onogumbe abakho ehlabathini.', 'XHO22'),
(1, 7, 11, 'Ngomnyaka wamakhulu amathandathu wokudla ubomi kukaNowa, ngenyanga yesibini, ngosuku lweshumi elinesixhenxe enyangeni leyo, kanye ngayo loo mini yavuleka yonke imithombo yamanzi onogumbe, neengcango zezulu zavuleka.', 'XHO22'),
(1, 7, 12, 'Yana emhlabeni iimini ezimashumi mane nobusuku obumashumi mane.', 'XHO22'),
(1, 7, 13, 'Kwangaloo mini bangena emkhombeni uNowa, uShem, noHam, noYafete, oonyana bakaNowa, nomkaNowa, nabafazi boonyana bakhe abathathu.', 'XHO22'),
(1, 7, 14, 'Ziboneni zonke izinto eziphilileyo ngohlobo lwazo, nezinto zonke ezizitho zine ngohlobo lwazo, nezinambuzane zonke ezinambuzelayo emhlabeni ngohlobo lwazo, neentaka zonke ngohlobo lwazo, neentaka zonke ngohlobo lwazo, iintlobo zabo.', 'XHO22'),
(1, 7, 15, 'Zangena emkhombeni noNowa, ngambini, ngambini, kwinyama yonke enomoya wokuphila.', 'XHO22'),
(1, 7, 16, 'Kwangena inkunzi nemazi kwinyama yonke, kwangena njengoko uThixo abemwisele umthetho. uYehova wamfaka.', 'XHO22'),
(1, 7, 17, 'Unogumbe uya kubakho ehlabathini iintsuku ezimashumi mane; yaye amanzi anda, bawurhuqa umkhombe, bawuphakamisela emhlabeni.', 'XHO22'),
(1, 7, 18, 'Aye enyuka amanzi, aba namandla kakhulu emhlabeni; wazala umkhombe ngamanzi.', 'XHO22'),
(1, 7, 19, 'Amanzi abe phezu kwehlabathi; zazambatha zonke iintaba eziphakamileyo kakhulu, eziphantsi kwamazulu onke.', 'XHO22'),
(1, 7, 20, 'Amanzi abe namandla ziikubhite ezilishumi elinantlanu; zagutyungelwa iintaba.', 'XHO22'),
(1, 7, 21, 'Yonke inyama enyakazelayo emhlabeni iyafa, ezintakeni nakwizilo nakwizilwanyana zasekhaya nakwizinambuzane zonke ezinambuzelayo emhlabeni, nabantu bonke;', 'XHO22'),
(1, 7, 22, 'Iinto zonke ezinomphefumlo wokuphila emathatheni azo, kwiinto zonke ezisemhlabeni owomileyo, zafa.', 'XHO22'),
(1, 7, 23, 'Zonke izinto eziphilileyo ezisehlabathini zatshatyalaliswa, abantu, neenkomo, nezinambuzane, neentaka zezulu; kwaye batshatyalaliswa emhlabeni, kwasala uNowa yedwa nabo babenaye emkhombeni.', 'XHO22'),
(1, 7, 24, 'Amanzi awugubungela umhlaba iintsuku ezilikhulu elinamashumi amahlanu.', 'XHO22'),
(1, 8, 1, 'uThixo wamkhumbula uNowa, neento zonke eziphilileyo, nezinto zonke ezizitho zine ezazinaye emkhombeni;', 'XHO22'),
(1, 8, 2, 'Yavingcwa imithombo kanogumbe neengcango zezulu, wathintelwa umvumbi wezulu;', 'XHO22'),
(1, 8, 3, 'Ayebuya amanzi emhlabeni, yaye ekupheleni kweentsuku ezilikhulu elinamashumi amahlanu amanzi awutha.', 'XHO22'),
(1, 8, 4, 'Umkhombe wema phezu kweentaba zeArarati ngenyanga yesixhenxe, ngosuku lweshumi elinesixhenxe enyangeni leyo.', 'XHO22'),
(1, 8, 5, 'Amanzi amana ewutha, kwada kwayinyanga yeshumi; ngeyeshumi ke, ngosuku lokuqala enyangeni leyo, zabonakala iincopho zeentaba.', 'XHO22'),
(1, 8, 6, 'Ekupheleni kweentsuku ezimashumi mane wayivula uNowa ifestile yomkhombe abeyenzile.', 'XHO22'),
(1, 8, 7, 'Walithumela ihlungulu, ada atsha amanzi ehlabathini;', 'XHO22'),
(1, 8, 8, 'Wabuya wakhupha ihobe, ukuze abone ukuba amanzi awuthile na phezu komhlaba;', 'XHO22'),
(1, 8, 9, 'Ihobe alifumananga ndawo yakuphumza nentende yethupha lalo, labuyela kuye emkhombeni, ngokuba amanzi abephezu kwehlabathi lonke; wasolula isandla sakhe, wayithabatha, wayirhuqela kuye emkhombeni.', 'XHO22'),
(1, 8, 10, 'Walinda ezinye iintsuku zasixhenxe; waba ngukumkani, walikhupha ihobe emkhombeni;', 'XHO22'),
(1, 8, 11, 'Kweza kuye ihobe ngorhatya; nanko, kukho igqabi lomnquma lisongiwe, lisongiwe; wazi uNowa, ukuba ehlile amanzi ehlabathini.', 'XHO22'),
(1, 8, 12, 'Walinda ezinye iintsuku zasixhenxe; wakhupha ihobe; ababengabuyelanga kuye.', 'XHO22'),
(1, 8, 13, 'Ngonyaka wamakhulu amathandathu ananye, ngenyanga yokuqala, ngolokuqala enyangeni leyo, atsha amanzi ehlabathini; uNowa wasisusa isiciko somkhombe, wakhangela, nango umhlaba womile.', 'XHO22'),
(1, 8, 14, 'Ngenyanga yesibini, ngosuku lwamashumi omabini anesixhenxe enyangeni leyo, woma kwaphela umhlaba.', 'XHO22'),
(1, 8, 15, 'Wathetha uThixo kuNowa, esithi:', 'XHO22'),
(1, 8, 16, 'Phuma emkhombeni, wena nomfazi wakho, noonyana bakho, nabafazi boonyana bakho, ndawonye nawe.', 'XHO22'),
(1, 8, 17, 'Zithabathe nawe zonke izinto eziphilileyo ezikuwe, yonke inyama, neentaka, neenkomo, neento zonke ezinyakazelayo emhlabeni; bande emhlabeni, bande, bande ehlabathini.', 'XHO22'),
(1, 8, 18, 'Waphuma ke uNowa, noonyana bakhe, nomkakhe, nabafazi boonyana bakhe.', 'XHO22'),
(1, 8, 19, 'Zonke izinto eziphilileyo, zonke izinambuzane, neentaka zonke, neento zonke ezinambuzelayo emhlabeni, ngohlobo lwazo, zaphuma emkhombeni.', 'XHO22'),
(1, 8, 20, 'uNowa wamakhela uYehova isibingelelo; Wazithabatha zonke izinto ezizitho zine ezihlambulukileyo, neentaka zonke ezihlambulukileyo, wenza amadini anyukayo esibingelelweni.', 'XHO22'),
(1, 8, 21, 'uYehova weva ivumba elithozamisayo; Wathi uYehova entliziyweni yakhe, Andisayi kulitshabhisa ihlabathi ngenxa yomntu; ngokuba ukuyila kwentliziyo yomntu kubi kwasebutsheni bakhe; andiyi kuphinda ndizivelele zonke izinto eziphilileyo njengoko ndenze ngako.', 'XHO22'),
(1, 8, 22, 'Lo gama kusekho umhlaba, akuyi kuphela ukuhlwayela nokuvuna, ukubanda nobushushu nehlobo nobusika nemini nobusuku.', 'XHO22'),
(1, 9, 1, 'uThixo wamsikelela uNowa noonyana bakhe, wathi kubo, Qhamani, nande, nizalise ihlabathi.', 'XHO22'),
(1, 9, 2, 'Ukoyikeka kwenu zonke izinto eziphilileyo zomhlaba, naphezu kweentaka zonke zezulu, naphezu kwento yonke enyakazelayo emhlabeni, nakwiintlanzi zonke zolwandle; zinikelwe ezandleni zenu.', 'XHO22'),
(1, 9, 3, 'Yonke inyakanyaka ephilileyo yoba kukudla kwenu; njengemifuno eluhlaza ndininike yonke into.', 'XHO22'),
(1, 9, 4, 'Yinyama enomphefumlo wayo, enegazi layo, ize ningayidli.', 'XHO22'),
(1, 9, 5, 'Igazi lomphefumlo wenu ndolifuna; esandleni sezinto zonke ezizitho zine, nasesandleni somntu; esandleni sakhe wonke umzalwana ndiya kufuna umphefumlo womntu.', 'XHO22'),
(1, 9, 6, 'Ophalaza igazi lomntu, igazi lakhe lophalazwa ngumntu, ngokuba umntu wamenza ngokomfanekiselo wakhe uThixo.', 'XHO22'),
(1, 9, 7, 'Ke wena, nande, nande; Qhamani emhlabeni, nande kulo.', 'XHO22'),
(1, 9, 8, 'Wathetha uThixo kuNowa nakoonyana bakhe ababenaye, esithi:', 'XHO22'),
(1, 9, 9, 'Yabona, ndiyawumisa umnqophiso wam nawe, nembewu yakho emva kwakho;', 'XHO22'),
(1, 9, 10, 'nezinto zonke eziphilileyo ezikuni, neentaka, neenkomo, nezinto zonke eziphilileyo zehlabathi ezikuni; kubo bonke abaphuma emkhombeni, kuzo zonke izinto eziphilileyo zomhlaba.', 'XHO22'),
(1, 9, 11, 'Ndiya kuwumisa umnqophiso wam nani; kwaye yonke inyama ayisayi kuphinda itshatyalaliswe ngamanzi onogumbe; yaye akusayi kuphinda kubekho unogumbe wokutshabalalisa umhlaba.', 'XHO22'),
(1, 9, 12, 'Wathi uThixo, Nguwo lo umqondiso womnqophiso endiwubekayo phakathi kwam nani, nayo yonke imiphefumlo ephilileyo ephakathi kwenu, kuse kwizizukulwana zasephakadeni.', 'XHO22'),
(1, 9, 13, 'Umnyama wam ndiwubeka efini, ube ngumqondiso womnqophiso ophakathi kwam nehlabathi.', 'XHO22'),
(1, 9, 14, 'Ukuba ndithe ndawusibekelisa ngelifu umhlaba, kobonakala umnyama emafini;', 'XHO22'),
(1, 9, 15, 'Ndiya kuwukhumbula umnqophiso wam ophakathi kwam nani, nayo yonke imiphefumlo ephilileyo enyameni yonke; yaye amanzi akasayi kuphinda abe ngunogumbe okonakalisa inyama yonke.', 'XHO22'),
(1, 9, 16, 'Umnyama uya kuba semafini; ndiwukhangele, ndikhumbule umnqophiso ongunaphakade phakathi koThixo nayo yonke imiphefumlo ephilileyo, enyameni yonke esehlabathini.', 'XHO22'),
(1, 9, 17, 'Wathi uThixo kuNowa, Nguwo lo umqondiso womnqophiso endiwumisileyo phakathi kwam nayo yonke inyama esehlabathini;', 'XHO22'),
(1, 9, 18, 'Oonyana bakaNowa, abaphuma emkhombeni, babengooShem, noHam, noYafete; uHama nguyise kaKanan.', 'XHO22'),
(1, 9, 19, 'Aba ngoonyana bakaNowa bobathathu;', 'XHO22'),
(1, 9, 20, 'uNowa waqala waba ngumlimi, watyala isidiliya.', 'XHO22'),
(1, 9, 21, 'Wasela iwayini, wanxila; waye ze ententeni yakhe.', 'XHO22'),
(1, 9, 22, 'uKama, uyise kaKanan, wakubona ukuzila kukayise, waxelela abakhuluwa bakhe bobabini ngaphandle.', 'XHO22'),
(1, 9, 23, 'uShema noYafete bathabatha ingubo, bayibeka emagxeni abo bobabini, baya eSantayo, babugubungela ubuze bukayise; ubuso babo babubheke ngasemva, abambonanga ukungabikho kukayise.', 'XHO22'),
(1, 9, 24, 'Wavuka uNowa ewayinini yakhe, ekwazi akwenzileyo unyana wakhe omnci kuye.', 'XHO22'),
(1, 9, 25, 'Wathi, Uqalekisiwe uKanan! Yiba ngumkhonzi kubakhonzi babazalwana bakhe.', 'XHO22'),
(1, 9, 26, 'Wathi, Makabongwe uYehova, uThixo kaShem. uKanan abe sisicaka sakhe.', 'XHO22'),
(1, 9, 27, 'uThixo uya kumandisa uYafete, ahlale ezintenteni zikaShem; uKanan abe sisicaka sakhe.', 'XHO22'),
(1, 9, 28, 'uNowa wahlala emveni konogumbe iminyaka emakhulu mathathu anamanci mahlanu.', 'XHO22'),
(1, 9, 29, 'Iyonke imihla kaNowa yaba yiminyaka emakhulu asithoba anamanci mahlanu, wafa ke.', 'XHO22'),
(1, 10, 1, 'Yiyo le ke inzala yoonyana bakaNowa, uShem, noHam, noYafete, abazalwa emva konogumbe.', 'XHO22'),
(1, 10, 2, 'Oonyana bakaYafete nguGomere, noMagogi, noMadayi, noYavan, noTubhali, noMesheki, noTirasi.', 'XHO22'),
(1, 10, 3, 'Oonyana bakaGomere; iAshkenazi neRifati neTogarma.', 'XHO22'),
(1, 10, 4, 'Oonyana baseJapan; uElisha noTarshishe, amaKiti namaDodan.', 'XHO22'),
(1, 10, 5, 'Ngenxa yokuba iziqithi zabezizwe zaba yimihlaba yazo; elowo wathetha ngezakowabo ulwimi, nezindlu zakhe, neentlanga zawo.', 'XHO22'),
(1, 10, 6, 'Oonyana bakaKama; Unxweme, iMizraim, iPute neKanan.', 'XHO22'),
(1, 10, 7, 'Oonyana bakaKushi nguSebha, noHafila, noSabheta, noRama, noSabhetega; noonyana bakaRama; Bona kwaDedan.', 'XHO22'),
(1, 10, 8, 'uKushi wazala uNimrodi;', 'XHO22'),
(1, 10, 9, 'Waba lizingela elikhulu phambi koYehova; ngoko ke kuthiwa: Kanye njengoNimrodi, igorha lezingela phambi koYehova.', 'XHO22'),
(1, 10, 10, 'Ukuqala kobukumkani bakhe kwaba yiBhabheli ne-Ereki neAkadi neKalene kwilizwe laseShinare.', 'XHO22'),
(1, 10, 11, 'iAsiriya yemka kwelo lizwe, yaza yakha iNineve nesixeko saseRehobhoti neKala.', 'XHO22'),
(1, 10, 12, 'NeRene phakathi kweNineve neKala: ngumzi ke lowo.', 'XHO22'),
(1, 10, 13, 'uJitrayim wazala amaLudi, nama-Anam, namaHabhe, namaNafetuhi;', 'XHO22'),
(1, 10, 14, 'namaPatrusi namaKasluhim, apho aphuma khona amaFilisti namaKafetori.', 'XHO22'),
(1, 10, 15, 'uKanan wazala uSidon, izibulo lakhe, noHeti;', 'XHO22'),
(1, 10, 16, 'namaYebhusi, nama-Amori, namaGirgashi;', 'XHO22'),
(1, 10, 17, 'namaHifi, neenqanawa, nezitya zokutshiza;', 'XHO22'),
(1, 10, 18, 'AmaAravada namaTsemares namaBhahamati yaye emva koko zanda iintsapho zamaKanan.', 'XHO22'),
(1, 10, 19, 'Umda wamaKanan wawususela eSidon uye eGerare ukuya eGaza; wada wesa eSodom, naseGomora, naseAdama, naseTsebhoyim, wesa eLasha.', 'XHO22'),
(1, 10, 20, 'Ngabo abo oonyana bakaKama ngokwemizalwane yabo, ngokweelwimi zabo, emazweni abo, ngokweentlanga zabo.', 'XHO22'),
(1, 10, 21, 'uShem, uyise wabo bonke oonyana bakaHebhere, umninawa kaYafete, omkhulu, waba nabantwana.', 'XHO22'),
(1, 10, 22, 'Oonyana bakaShem nguShem. uElam, uAsiriya, uArpakishadi, uLudi, noAram.', 'XHO22'),
(1, 10, 23, 'Oonyana baka-Aram nguAsheri; Thina kunye nabo kunye noGeter noMas.', 'XHO22'),
(1, 10, 24, 'uArpakishadi wazala uSala; uShela wazala uEbhere.', 'XHO22'),
(1, 10, 25, 'uHebhere wazalelwa oonyana ababini, igama lomnye belinguPelege; ngokuba ngemihla yakhe lahlukana phakathi elimiweyo; igama lomninawa wakhe belinguYoketan.', 'XHO22'),
(1, 10, 26, 'uYoketan wazala uAlemodade, noSelefe, noHasaremete, noYeraki;', 'XHO22'),
(1, 10, 27, 'neHadoram, neUsale, neDikila;', 'XHO22'),
(1, 10, 28, 'no-Obhali, noAbhimayeli, noShebha;', 'XHO22'),
(1, 10, 29, 'noOfire, noHafila, noYobhabhi; bonke bephela ngoonyana bakaYoketan.', 'XHO22'),
(1, 10, 30, 'Ukuhlala kwabo kwathabathela eMesha, kwesa eSefare, kweleentaba ngasempuma.', 'XHO22'),
(1, 10, 31, 'Ndibabona oonyana bakaShem, benezindlu zabo, neelwimi zabo, nezizwe zabo, neentlanga zabo.', 'XHO22'),
(1, 10, 32, 'Yiyo le ke inzala yoonyana bakaNowa, nezizukulwana zabo, neentlanga zabo; zaza zahlukaniswa iintlanga ngoko emhlabeni emva konogumbe.', 'XHO22'),
(1, 11, 1, 'Ihlabathi liphela libe lulwimi-nye, lizwi linye.', 'XHO22'),
(1, 11, 2, 'Ekuphumeni kwawo empumalanga, afumana entlango ezweni laseShinare; bahlala khona.', 'XHO22'),
(1, 11, 3, 'Bathetha omnye komnye besithi, Hambani, senze izitena, sizitshise. Baba nezitena endaweni yamatye, nodaka lodongwe.', 'XHO22'),
(1, 11, 4, 'Bathi ke, Hambani, sizakhele umzi, nemboniselo encopho yayo iye kufika emazulwini; masizenzele igama, hleze sisasazeke ehlabathini.', 'XHO22'),
(1, 11, 5, 'Wehla uYehova ukuze asibone isixeko nenqaba leyo babeyakhile oonyana babantu.', 'XHO22'),
(1, 11, 6, 'Yathi iNkosi: Yabona, abantu bayinto enye, kwaye bonke bantetho-nye; baqala ke ukwenza, ngoku ke abayi kukhatywa nto abacinga ukuyenza.', 'XHO22'),
(1, 11, 7, 'Masihle, siyidube khona intetho yabo, ukuze bangevani ukuthetha;', 'XHO22'),
(1, 11, 8, 'Waza uYehova wabachithachitha kwalapho ehlabathini lonke, yaye bayeka ukusakha eso sixeko.', 'XHO22'),
(1, 11, 9, 'Ngenxa yoko kwathiwa igama lawo yiBhabheli; ngenxa yokuba kulapho uYehova waluduba khona ulwimi lwehlabathi lonke, yaye uYehova wabachithachitha besuka apho ehlabathini lonke.', 'XHO22'),
(1, 11, 10, 'Yiyo le ke inzala kaShem: uShem ubeminyaka ilikhulu ezelwe;', 'XHO22'),
(1, 11, 11, 'uShema waphila iminyaka engamakhulu amahlanu emva kokuba ezele uArpakishadi, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 12, 'uArpakishadi wahlala iminyaka emashumi mathathu anamihlanu, wazala uSara.', 'XHO22'),
(1, 11, 13, 'uArpakishade wahlala emveni kokuzala kwakhe uShela iminyaka emakhulu mane anamithathu, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 14, 'uSala wahlala iminyaka emashumi mathathu, wazala uHebhere.', 'XHO22'),
(1, 11, 15, 'Emva kokuzalwa kukaHebhere uSla waphila iminyaka engamakhulu amane anantathu, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 16, 'uHebhere wahlala iminyaka emashumi mathathu anamine, wazala uPelege.', 'XHO22'),
(1, 11, 17, 'Emva kokuzala kwakhe uPelege uHebhere waphila iminyaka engamakhulu amane anamashumi mathathu, waza wazala oonyana neentombi.', 'XHO22'),
(1, 11, 18, 'uPelege wahlala iminyaka emashumi mathathu, wazala uRehu.', 'XHO22'),
(1, 11, 19, 'uPelege wahlala emveni kokuzala kwakhe uRehu iminyaka emakhulu mabini anesithoba, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 20, 'uRehu wahlala iminyaka emashumi mathathu anamibini, wazala uSerugi.', 'XHO22'),
(1, 11, 21, 'uRehu wahlala emveni kokuzala kwakhe uSerugi iminyaka emakhulu mabini anesixhenxe, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 22, 'uSerugi wahlala iminyaka emashumi mathathu, wazala uNakore.', 'XHO22'),
(1, 11, 23, 'uSerugi wahlala emveni kokuzala kwakhe uNakore iminyaka emakhulu mabini, wazala oonyana neentombi.', 'XHO22'),
(1, 11, 24, 'uNakore wahlala khona iminyaka emashumi mabini anesibhozo yedwa, wazala uTera.', 'XHO22'),
(1, 11, 25, 'Emva kokuzala kwakhe uTera, uNahore waphila iminyaka elikhulu elineshumi elinesithoba, waza wazala oonyana neentombi.', 'XHO22'),
(1, 11, 26, 'uTera wahlala iminyaka emashumi asixhenxe, wazala uAbram, noNakore, noHaran.', 'XHO22'),
(1, 11, 27, 'Yiyo le ke inzala kaTera: uTera wazala uAbram, noNakore, noHaran; uHaran wazala uLote.', 'XHO22'),
(1, 11, 28, 'uHaran wafa ngaphambi kukayise uTera kwilizwe awazalelwa kulo, iUre yamaKhaledi.', 'XHO22'),
(1, 11, 29, 'uAbram noNakore bazeka abafazi; igama lomka-Abram belinguSarayi; Igama lomkaNakore lalinguMilika, intombi kaHaran, uyise kaMilka, uyise kaIska.', 'XHO22'),
(1, 11, 30, 'Ke uSarayi ebengazali; wayengenamntwana.', 'XHO22'),
(1, 11, 31, 'uTera wamthabatha uAbram unyana wakhe, noLote, unyana kaHaran, unyana wonyana wakhe, noSarayi, umolokazana wakhe, umka-Abram unyana wakhe; bemka nabo eUre yamaKaledi, baya ezweni lakwaKanan; Bafika kwaHaran, bahlala khona.', 'XHO22'),
(1, 11, 32, 'Imihla kaTera yaba yiminyaka emakhulu mabini anamihlanu; wafa uTera kwaHaran.', 'XHO22'),
(1, 12, 1, 'Wathi uYehova kuAbram, Lishiye ilizwe lakowenu, nendlu yakho, nendlu kayihlo, uye ezweni endokubonisa lona.', 'XHO22'),
(1, 12, 2, 'Ndiya kukwenza uhlanga olukhulu, ndikusikelele, ndilikhulise igama lakho; kwaye uya kuba nethamsanqa.', 'XHO22'),
(1, 12, 3, 'Yaye ndiya kumsikelela okusikeleleyo, lowo ukuqalekisayo ndimqalekise; zisikeleleke ngawe zonke izizukulwana zehlabathi.', 'XHO22'),
(1, 12, 4, 'Wemka uAbram njengoko wathethayo uYehova kuye; uLote wahamba naye, yaye uAbram wayeneminyaka engamashumi asixhenxe anesihlanu ubudala ekuphumeni kwakhe kwelakwaHaran.', 'XHO22'),
(1, 12, 5, 'uAbram wathabatha uSarayi umkakhe, noLote, unyana womninawa wakhe, nengqwebo yabo yonke ababeyiqwebile, nemiphefumlo eyabafumanayo kwaHaran; bemka baya ezweni lakwaKanan; bafika ezweni lakwaKanan.', 'XHO22'),
(1, 12, 6, 'uAbram walicanda ilizwe, waya endaweni yakwaShekem, entlango yaseMore. AmaKanan abekho ngelo xesha ngelo xesha.', 'XHO22'),
(1, 12, 7, 'uYehova wabonakala kuAbram, wathi, Imbewu yakho ndiya kuyinika eli lizwe; waza wamakhela khona isibingelelo uYehova owayebonakele kuye.', 'XHO22'),
(1, 12, 8, 'Wenyuka khona waya entabeni ngasempumalanga, eBheteli, wayimisa intente yakhe eBheteli ngasentshonalanga, iAyi ingasempumalanga; Wakha khona isibingelelo kuYehova, wathandaza egameni likaYehova.', 'XHO22'),
(1, 12, 9, 'Wemka uAbram, wenjenjeya esingise ezantsi.', 'XHO22'),
(1, 12, 10, 'Kwabakho indlala kwelo zwe, wehla uAbram waya eYiputa, ukuba aphambukele khona; ngokuba ibinkulu indlala ehlabathini.', 'XHO22'),
(1, 12, 11, 'Kwathi, xa asondelayo ukungena eYiputa, wathi kuSarayi umkakhe, Yabona, ndiyazi ngenxa yokuba ungumfazi omhle ngembonakalo.', 'XHO22'),
(1, 12, 12, 'Kothi, akukubona amaYiputa, athi, Ngumkakhe lo; andibulale mna, wena akugcine uphila.', 'XHO22'),
(1, 12, 13, 'Yithi, ungudade wethu, ukuze ndilunge ngenxa yakho; kwaye umphefumlo wam uya kuphila ngawe.', 'XHO22'),
(1, 12, 14, 'Akufika uAbram eYiputa, ambona amaYiputa umfazi ukuba mhle kakhulu.', 'XHO22'),
(1, 12, 15, 'Zambona iinkosana zikaFaro, zamdumisa phambi koFaro; umfazi wasiwa endlwini kaFaro.', 'XHO22'),
(1, 12, 16, 'Waqhubeka emenzela okulungileyo uAbram ngenxa yakhe, waba nempahla emfutshane, neenkomo, namaesile, nabakhonzi, nabakhonzazana, namaesile akhe, neenkamela.', 'XHO22'),
(1, 12, 17, 'uYehova wamohlwaya ngokuqatha uFaro nendlu yakhe ngenxa kaSarayi, umka-Abram.', 'XHO22'),
(1, 12, 18, 'uFaro wambiza uAbram, wathi, Yintoni na le undenze yona? Kutheni ungandixelelanga nje ukuba ngumfazi wakho?', 'XHO22'),
(1, 12, 19, 'Yini na ukuba uthi, Ngudade wethu? ukuze ndimthabathe abe ngumkam; Khangela ke ngoko umkakho, umthabathe, uhambe.', 'XHO22'),
(1, 12, 20, 'uFaro wawawisela umthetho amadoda akhe ngaye, amthuma yena nomkakhe, nako konke abenako.', 'XHO22'),
(1, 13, 1, 'Waphuma uAbram eYiputa, yena nomkakhe, neento zonke abenazo, enoLote, esinga kwelasezantsi.', 'XHO22'),
(1, 13, 2, 'uAbram wayeneenkomo, nesilivere, negolide.', 'XHO22'),
(1, 13, 3, 'Wemka kwelasezantsi, waya eBheteli, apho ibikhona intente yakhe ekuqaleni, phakathi kweBheteli neAyi;', 'XHO22'),
(1, 13, 4, 'endaweni yesibingelelo, awasenzayo khona ngaphambili; walithandaza uAbram igama likaYehova khona.', 'XHO22'),
(1, 13, 5, 'Naye uLote, obehamba noAbram, waye enempahla emfutshane, neenkomo, neentente.', 'XHO22'),
(1, 13, 6, 'Umhlaba wawunganelanga ukuba bahlale ndawonye, kuba ubutyebi babo babubuninzi, kangangokuba ababa nakuhlala ndawonye.', 'XHO22'),
(1, 13, 7, 'Kwabakho ingxabano phakathi kwabalusi bempahla emfutshane ka-Abram nabalusi bempahla emfutshane kaLote; ngelo xesha kwahlala amaKanan namaPersi kwelo zwe.', 'XHO22'),
(1, 13, 8, 'Wathi uAbram kuLote, Makungabikho mfazwe phakathi kwam nawe, naphakathi kwabalusi bam nabalusi bakho; ngokuba singabantwana babantu.', 'XHO22'),
(1, 13, 9, 'Alikuwe na ilizwe lonke? Yahlukana nam; ukuba uthanda ukuthatha isandla sasekhohlo, ndoya ekunene; nokuba ubheka ekunene, ndoya ekhohlo.', 'XHO22'),
(1, 13, 10, 'uLote wawaphakamisa amehlo akhe, wawukhangela wonke ummandla waseYordan, ngokuba amanzi abemaninzi kuyo yonke indawo, engekayonakalisi uYehova iSodom neGomora, unjengomyezo kaYehova, nanjengelizwe laseYiputa, ekufikeni kwakho eTsohare.', 'XHO22'),
(1, 13, 11, 'uLote wawunyula wonke ummandla waseYordan; uLote waya ngasempumalanga, bahlukana omnye komnye.', 'XHO22'),
(1, 13, 12, 'uAbram wahlala ezweni lakwaKanan, uLote wahlala emizini yasemaphandleni, wayimisa intente yakhe kwesa eSodom.', 'XHO22'),
(1, 13, 13, 'Ke kaloku amadoda aseSodom ayengabenzi bobubi kakhulu, yaye wona phambi koYehova.', 'XHO22'),
(1, 13, 14, 'Wathi uYehova kuAbram ekuzahluleni kukaLote kuye: “Phakamisa amehlo akho kuloo ndawo ukuyo, ujonge emntla nasemzantsi nasempuma nasentshona.', 'XHO22'),
(1, 13, 15, 'Kuba lonke ilizwe olibonayo ndiya kulinika wena nenzala yakho kude kuse ephakadeni.', 'XHO22'),
(1, 13, 16, 'Ndiza kuyenza inzala yakho ibe njengothuli lomhlaba, ukuze ukuba kukho umntu okwaziyo ukubala uthuli lomhlaba, ayibale nembewu yakho.', 'XHO22'),
(1, 13, 17, 'Suka, ulihambe ilizwe ngobude nobubanzi balo; ngokuba ndiya kukunika.', 'XHO22'),
(1, 13, 18, 'Wandula ke uAbram wamisa intente yakhe waza wahlala kumthi oki kaMamre oseHebron, waza wamakhela khona uYehova isibingelelo.', 'XHO22'),
(1, 14, 1, 'Kwathi ngemihla ka-Amrafele ukumkani waseShinare, noAryogi, ukumkani wase-Elazare, noKedorlahomere, ukumkani wakwaElam, noTidali ukumkani weentlanga;', 'XHO22'),
(1, 14, 2, 'baxabela uBhera ukumkani waseSodom, noBhirsa ukumkani waseGomora, noSinabhi ukumkani waseAdama, noSimber ukumkani waseTsebhoyim, nokumkani waseBhela, eyiTsohare ke leyo.', 'XHO22'),
(1, 14, 3, 'Bahlanganisana bonke abantwana entilini yaseSidim eluLwandle lweTyuwa.', 'XHO22'),
(1, 14, 4, 'Iminyaka elishumi elinamibini bakhonza uKedor-Lahomere, bathi ngomnyaka weshumi elinesithathu bagwilika kuye.', 'XHO22'),
(1, 14, 5, 'Ngonyaka weshumi elinesine uKedore-Lahomere nookumkani ababenaye beza baza bawoyisa amaRafa iAshtaroti-karnayim namaShusim Ham namaEmim Shave-Kiriyatayim;', 'XHO22'),
(1, 14, 6, 'namaHori ezintabeni zakwaSehire, esa e-Elparan engasentlango.', 'XHO22'),
(1, 14, 7, 'Bandula ke babuya baza bafika e-Enemishipati, oko kukuthi eKadeshe, balithimba lonke ilizwe lama-Amaleki nama-Amori awayehlala eHason-Tamare.', 'XHO22'),
(1, 14, 8, 'Waphuma ukumkani waseSodom, nokumkani waseGomora, nokumkani waseAdima, nokumkani waseSebhoyim, nokumkani waseBhela (yiTsohare ke leyo), balwa nabo entilini yaseSidim;', 'XHO22'),
(1, 14, 9, 'noKedorlahomere ukumkani wakwaElam, noTidali ukumkani weentlanga, noAmrafele ukumkani waseShinare, noAriyoki ukumkani wakwaElazare; ookumkani abane nabahlanu.', 'XHO22'),
(1, 14, 10, 'Umda waseSidim wawuzele yimihadi yodaka; basaba ookumkani baseSodom nowaseGomora, bawela khona; baza abo babesele basabela entabeni.', 'XHO22'),
(1, 14, 11, 'Bayithabatha yonke impahla yaseSodom neGomora, nako konke ukudla kwabo, bemka.', 'XHO22'),
(1, 14, 12, 'Bamthabatha uLote, umninawa ka-Abram, owayehlala eSodom, nempahla yakhe, bemka.', 'XHO22'),
(1, 14, 13, 'Weza okhululekileyo, waxelela uAbram umHebhere; ngokuba ebehleli eMiokini kaMamre umAmori, umzalwana kaEshkole, noAnere umninawa wakhe; benza umnqophiso noAbram.', 'XHO22'),
(1, 14, 14, 'Weva uAbram ukuba ubanjwe umkhuluwa wakhe, wabaxhobisa abakhonzi bakhe abaqeqeshiweyo, ababezalelwe endlwini yakhe, abangamakhulu amathathu aneshumi elinesibhozo, wawasukela, wada wesa kwaDan.', 'XHO22'),
(1, 14, 15, 'Waza wazahlula ebusuku nxamnye nabo, yena nabakhonzi bakhe, waza waboyisa waza wabasukela ukuya kufikelela eHobha, ekwicala lasekhohlo leDamasko.', 'XHO22'),
(1, 14, 16, 'Wayibuyisela yonke impahla yakhe, wambuyisela uLote umninawa wakhe, nempahla yakhe, nabafazi, nabantu.', 'XHO22'),
(1, 14, 17, 'Ukumkani waseSodom waphuma waya kumhlangabeza emva kokuba ebuyile ekuxabeleni uKedorlahomere nookumkani ababenaye, kwintlambo yeShebhe, ekwintlambo yookumkani.', 'XHO22'),
(1, 14, 18, 'uMelkitsedeki, ukumkani waseSalem, wazisa isonka newayini, engumbingeleli kaThixo Osenyangweni.', 'XHO22'),
(1, 14, 19, 'Wamsikelela wathi, Makasikelelwe uAbram nguThixo Osenyangweni, uNkosi wezulu nehlabathi.', 'XHO22'),
(1, 14, 20, 'Makabongwe uThixo Osenyangweni ozinikele iintshaba zakho esandleni sakho. Wamnika isishumi sazo zonke izinto.', 'XHO22'),
(1, 14, 21, 'Wathi ukumkani waseSodom kuAbram, Ndinike abantu, uzithabathele impahla.', 'XHO22'),
(1, 14, 22, 'Wathi uAbram kukumkani waseSodom, Isandla sam ndisiphakamisele kuYehova uThixo Osenyangweni, uYehova wamazulu nehlabathi;', 'XHO22'),
(1, 14, 23, 'ukuba andiyi kuthabatha nawuphi na umsonto ukuya esithendeni sembadada, yaye andiyi kuthabatha nantoni na eyeyakho, ongazange uthi, ‘Ndimdle ilifa uAbram.', 'XHO22'),
(1, 14, 24, 'Ngaphandle koko bakudlayo abafana, nesabelo samadoda abehamba nam, uAnere, noEshkoli, noMamre; bathathe inxaxheba yabo.', 'XHO22'),
(1, 15, 1, 'Kwathi emveni koko, lafika ilizwi likaYehova kuAbram ngombono, lisithi, Musa ukoyika, Abram; ndiyingweletshetshe yakho, umvuzo wakho ngowona mkhulu.', 'XHO22'),
(1, 15, 2, 'Wathi uAbram, Camagu, Nkosi yam Yehova, wondinika ntoni na, sendikhona nje, intloko yendlu yam nguEliyezere waseDamasko?', 'XHO22'),
(1, 15, 3, 'Wathi uAbram, Yabona, akundinikanga mbewu;', 'XHO22'),
(1, 15, 4, 'Kwaye qaphelani, ilizwi leNkosi lafika kuye, lisithi: Lo akasayi kuba yindlalifa yakho; kodwa lowo uphuma ezibilinini zakho uya kuba yindlalifa yakho.', 'XHO22'),
(1, 15, 5, 'Wamkhuphela phandle, wathi, Khangela esibhakabhakeni, uzibale iinkwenkwezi, ukuba unokuzibala; Wathi kuye, Iya kuba njalo imbewu yakho.', 'XHO22'),
(1, 15, 6, 'Wakholwa eNkosini; kwaye wamxelela ukuba ilungile.', 'XHO22'),
(1, 15, 7, 'Wathi kuye, NdinguYehova owakukhuphayo eUre yamaKaledi, ukuba ndikunike eli lizwe ukuba ulihluthe.', 'XHO22'),
(1, 15, 8, 'Wathi, Nkosi Yehova, ndiya kwazi ngani na, ukuba ndiya kulidla ilifa?', 'XHO22'),
(1, 15, 9, 'Wathi kuye, Ndenzele ithokazi eliminyaka mithathu, ithokazi eliminyaka mithathu, nenkunzi yemvu eminyaka mithathu, nehobe, nehobe;', 'XHO22'),
(1, 15, 10, 'Wayithabatha ke yonke, wayicanda ngesitshetshe, yasibeka isahlulo saso, sakhangelana neso, kodwa akazahlula iintaka.', 'XHO22'),
(1, 15, 11, 'Zafika iintaka phezu kwezidumbu uAbram, wazigxotha.', 'XHO22'),
(1, 15, 12, 'Kwathi, xa litshonayo ilanga, efikelwe bubuthongo obukhulu uAbram; nanko efikelwa kunkwantyiswa bubumnyama obukhulu.', 'XHO22'),
(1, 15, 13, 'Wathi kuAbram, Yazi ke, ukuba imbewu yakho iya kuba ngabaphambukeli ezweni elingelayo, ibakhonze; baya kubacinezela iminyaka emakhulu mane;', 'XHO22'),
(1, 15, 14, 'Kananjalo abantu eya kubakhonza ndiya kubagweba; kwaye emva koko baya kuphuma nezinto ezinkulu.', 'XHO22'),
(1, 15, 15, 'Hambani niye kooyihlo ninoxolo; uya kungcwatywa umdala kakhulu.', 'XHO22'),
(1, 15, 16, 'Kwesesine isizukulwana iya kubuyela apha, ngenxa yokuba ububi bama-Amori abukazaliseki.', 'XHO22'),
(1, 15, 17, 'Kwathi, lakuba litshonile ilanga, kwakuba sithokothoko, kwabakho intsintsi yomsi, nesibane esidangazelayo, esicanda phakathi kwezo ndawo.', 'XHO22'),
(1, 15, 18, 'Ngaloo mini uYehova wenza umnqophiso noAbram, wathi, Imbewu yakho ndiyinike eli lizwe, lithabathele emlanjeni waseYiputa, lise emlanjeni omkhulu ongumEfrati.', 'XHO22'),
(1, 15, 19, 'NamaKeni, namaKeni, namaKadmon;', 'XHO22'),
(1, 15, 20, 'namaHeti, namaPerizi, namaRafa;', 'XHO22'),
(1, 15, 21, 'nama-Amori, namaKanan, namaGirgashi, namaYebhusi.', 'XHO22'),
(1, 16, 1, 'Ke kaloku uSarayi umka-Abram ubengamzaleli mntwana; waye enomkhonzazana ongumYiputazana, ogama belinguHagare.', 'XHO22'),
(1, 16, 2, 'Wathi uSarayi kuAbram, Yabona, uYehova undivingcile ukuba ndingazali; Ngena kumkhonzazana wam; mhlawumbi ndingazala naye. uAbram waliphulaphula izwi likaSarayi.', 'XHO22'),
(1, 16, 3, 'uSarayi, umka-Abram, wamthabatha uHagare, umkhonzazana wakhe ongumYiputa, emveni kokuba uAbram ubehleli iminyaka elishumi ezweni lakwaKanan, wamnika uAbram indoda yakhe ukuba abe ngumkakhe.', 'XHO22'),
(1, 16, 4, 'Wamngena ke uHagare, wamitha; Wathi akubona ukuba umithi, inkosikazi yakhe yamdela emehlweni akhe.', 'XHO22'),
(1, 16, 5, 'Wathi uSarayi kuAbram, Ityala lam likuwe; ndimnike umkhonzazana wam esifubeni sakho; wathi akubona ukuba umithi, ndacukucezeka emehlweni akhe; uYehova makagwebe phakathi kwam nawe.', 'XHO22'),
(1, 16, 6, 'Wathi uAbram kuSarayi, Yabona, umkhonzazana wakho usesandleni sakho; yenza kuye ngokuthanda kwakho. Wathi akumphatha kakubi uSarayi, wabaleka wemka ebusweni bakhe.', 'XHO22'),
(1, 16, 7, 'Isithunywa sikaYehova samfumana ngasequleni lamanzi entlango, equleni elingasendleleni eya eShura.', 'XHO22'),
(1, 16, 8, 'Wathi, Hagare, mkhonzazana kaSarayi, uvela phi na? kwaye uyaphi? Wathi, Ndibalekile kuSarayi, ukumkanikazi wam.', 'XHO22'),
(1, 16, 9, 'Sathi kuye isithunywa sikaYehova, Buyela kukumkanikazi wakho, zithobe esandleni sakhe.', 'XHO22'),
(1, 16, 10, 'Sathi kuye isithunywa sikaYehova, Ndiya kuyandisa kakhulu imbewu yakho, ingàbabalwa.', 'XHO22'),
(1, 16, 11, 'Sathi isithunywa sikaYehova kuye, Yabona, umithi; uya kuzala unyana, umthiye igama elinguIshmayeli; ngokuba uYehova ukuvile ukubandezeleka kwakho.', 'XHO22'),
(1, 16, 12, 'Kwaye uya kuba yindoda yelizwe; isandla sakhe siya kuba phezu komntu wonke, isandla sabantu bonke sichasane naye; yaye uya kuhlala emehlweni abo bonke abazalwana bakhe.', 'XHO22'),
(1, 16, 13, 'Walikhankanya igama likaYehova owathetha kuye, wathi, uThixo wakho undibonile; ngokuba wathi, Bendilindele ondibonayo na?', 'XHO22'),
(1, 16, 14, 'Kungoko kwathiwa iqula elo yiBherlaha-royi; nantso iphakathi kweKadeshe neBherede.', 'XHO22'),
(1, 16, 15, 'uHagare wamzalela uAbram unyana;', 'XHO22'),
(1, 16, 16, 'uAbram ubeminyaka imashumi asibhozo anamithandathu ezelwe, oko uHagare wazala uAbram uIshmayeli.', 'XHO22'),
(1, 17, 1, 'Kwathi, uAbram akuba minyaka imashumi asibhozo anamnye ezelwe, uYehova wabonakala kuAbram, wathi kuye, NdinguThixo uSomandla; hamba ecaleni kwam, ube ngogqibeleleyo.', 'XHO22'),
(1, 17, 2, 'Ndiya kuwubeka umnqophiso wam phakathi kwam nawe, ndikwandise kakhulu.', 'XHO22'),
(1, 17, 3, 'Wawa uAbram ngobuso bakhe, wathetha uYehova kuye, esithi,', 'XHO22'),
(1, 17, 4, 'Mna, yabona, umnqophiso wam unawe; uya kuba nguyise weentlanga ezininzi.', 'XHO22'),
(1, 17, 5, 'Akuyi kuba sathiwa igama lakho nguAbram; ngokuba ndikwenze uyise wezizwe ezininzi.', 'XHO22'),
(1, 17, 6, 'Ndiya kukuphumelelisa kakhulu, ndikwenze iintlanga, kuphume ookumkani kuwe;', 'XHO22'),
(1, 17, 7, 'Ndowumisa umnqophiso wam phakathi kwam nawe nenzala yakho emva kwakho kwizizukulwana zayo, ube ngumnqophiso wexesha elingenammiselo, wokuba ndibe nguThixo wakho nenzala yakho eya emva kwakho.', 'XHO22'),
(1, 17, 8, 'Yaye ngokuqinisekileyo ndiya kukunika ilizwe ohlala kulo wena nenzala yakho emva kwakho, ilizwe lakwaKanan lonke, libe lilifa lakho ukusa kwixesha elingenammiselo; ndibe nguThixo wabo.', 'XHO22'),
(1, 17, 9, 'Wathi uThixo kuAbraham, Wugcine umnqophiso wam, wena nembewu yakho eya emva kwakho kwizizukulwana zabo.', 'XHO22'),
(1, 17, 10, 'Nguwo lo umnqophiso wam, eniya kuwugcina phakathi kwam nani, nembewu yakho emva kwakho; Wonke umntwana oyinkwenkwe kuni makaluswe.', 'XHO22'),
(1, 17, 11, 'Walusa inyama yokwaluswa; ibe ngumqondiso womnqophiso ophakathi kwam nani.', 'XHO22'),
(1, 17, 12, 'Unyana omihla isibhozo kuni mawaluswe, yonke into eyindoda emizalwaneni yenu, ezelwe endlwini, nezuzwe ngemali kubani wasemzini, ongengowakho koonyana bakho.', 'XHO22'),
(1, 17, 13, 'Makaluswe ozelwe endlwini yakho, nozuzwe ngemali yakho; umnqophiso wam enyameni yenu uya kuba ngumnqophiso ongunaphakade.', 'XHO22'),
(1, 17, 14, 'Umntwana oyinkwenkwe, ongahlatywanga ebudodeni bakhe, loo mphefumlo wonqanyulwa, ungabikho ebantwini bakowawo; uwaphule umnqophiso wam.', 'XHO22'),
(1, 17, 15, 'Wathi uThixo kuAbraham, uSarayi umkakho ungambiza ngokuba nguSarayi; igama lakhe loba nguSara.', 'XHO22'),
(1, 17, 16, 'Ndiya kumsikelela, ndikunike unyana ngaye; ewe, ndiya kumsikelela, abe ziintlanga; ookumkani beentlanga baya kuphuma kuye.', 'XHO22'),
(1, 17, 17, 'Ngoko uAbraham wawa ngobuso bakhe waza wahleka waza wathi entliziyweni yakhe: “Ngaba umntwana unokuzalwa aneminyaka elikhulu ubudala na? uSara, ominyaka imashumi asibhozo ezelwe, uya kuzala yedwa na?', 'XHO22'),
(1, 17, 18, 'Wathi uAbraham kuThixo, uIshmayeli makadle ubomi phambi kwakho.', 'XHO22'),
(1, 17, 19, 'Wathi uThixo, Ewe, uSara umkakho uya kukuzalela unyana; umthiye igama elinguIsake, ndiwumise umnqophiso wam naye, ube ngumnqophiso ongunaphakade kwimbewu yakhe emva kwakhe.', 'XHO22'),
(1, 17, 20, 'Ngokusingisele kuIshmayeli ndikuvile, ndathi, Yabona, ndimsikelele, ndimsikelele, ndimandisile, ngakumbi; uya kuzala abathetheli abalishumi elinababini, ndimenze uhlanga olukhulu.', 'XHO22'),
(1, 17, 21, 'Ndowumisa umnqophiso wam noIsake, oya kuzala uSara ngeli xesha nyakenye.', 'XHO22'),
(1, 17, 22, 'Wayeka ukuthetha naye. Wenyuka uThixo esuka kuAbraham.', 'XHO22'),
(1, 17, 23, 'uAbraham wamthabatha uIshmayeli unyana wakhe, nabo bonke ababezalelwe endlwini yakhe, nabo bonke abazuzwe ngemali yakhe, yonke into eyindoda phakathi kwamadoda endlu ka-Abraham; aza abalusa inyama yokuqala yobudoda ngaloo mini kanye njengoko uThixo wayemyalele.', 'XHO22'),
(1, 17, 24, 'uAbraham ubeminyaka imashumi asithoba anesithoba ezelwe, ukwaluka kwakhe.', 'XHO22'),
(1, 17, 25, 'uIshmayeli unyana wakhe ebeminyaka ilishumi elinamithathu ezelwe, ukwaluka.', 'XHO22'),
(1, 17, 26, 'Kwangaloo mini, boluka uAbraham, noIshmayeli unyana wakhe.', 'XHO22'),
(1, 17, 27, 'Onke amadoda endlu yakhe, azalelwe endlwini yakhe, nazuzwe ngemali kubani wolunye uhlanga, aluswa kunye naye.', 'XHO22'),
(1, 18, 1, 'uYehova wabonakala kuye eMiokini kaMamre, ehleli emnyango wentente emini enkulu.', 'XHO22'),
(1, 18, 2, 'Wawaphakamisa amehlo akhe, wabona amadoda amathathu emi ngakuye; wawabona, wagidima, waya kuwakhawulela emnyango wentente, waqubuda.', 'XHO22'),
(1, 18, 3, 'Wathi, Nkosi, ukuba ndibonise inceba emehlweni akho, musa ukudlula kumkhonzi wakho.', 'XHO22'),
(1, 18, 4, 'Makuthatyathwe amanzi, nihlambe iinyawo zenu, niphumle phantsi komthi lowo.', 'XHO22'),
(1, 18, 5, 'Ndiya kuya ndithabathe iqhekeza lesonka, ndithuthuzele iintliziyo zenu; emveni koko nodlula, ngokuba uze kumkhonzi wakho ngenxa yoko. Athi: Yenza njengoko uthethile.', 'XHO22'),
(1, 18, 6, 'Wakhawuleza uAbraham, waya kuSara ententeni, wathi, Khawulungise iiseha ezintathu zomgubo ocoliweyo, uwose, wenze amaqebengwana eziko.', 'XHO22'),
(1, 18, 7, 'Wagidima waya ezinkomeni uAbraham, wathabatha ithole lenkomo, lithambile, lihle, wanika umfana; wakhawuleza.', 'XHO22'),
(1, 18, 8, 'Wathabatha ubisi, nobisi, nethole lenkomo, abelilungisile, wabeka phambi kwabo; wema nawo phantsi komthi, adla.', 'XHO22'),
(1, 18, 9, 'Athi kuye, Uphi na uSara umkakho? Wathi, Nanko ententeni.', 'XHO22'),
(1, 18, 10, 'Wathi, Ndiya kubuyela kuwe, okunene ngexesha lodla ubomi; yabona, uSara umkakho uya kuba nonyana. Weva uSara umnyango wentente ngasemva kwakhe.', 'XHO22'),
(1, 18, 11, 'Ke kaloku uAbraham noSara babebadala, bebadala; yaye wayenqamka ukuba kuSara njengomfazi.', 'XHO22'),
(1, 18, 12, 'uSara wahleka entliziyweni yakhe, wathi, Ndingade ndibe noyolo na ndaluphele, nenkosi yam yaluphele nje?', 'XHO22'),
(1, 18, 13, 'Wathi uYehova kuAbraham, Yini na ukuba uSara ahleke, esithi, Yabona, ndiya kuzala mna ngoku?', 'XHO22'),
(1, 18, 14, 'Ngaba ikho into angenakuyenza uYehova? Ngexesha elimisiweyo ndiya kubuyela kuwe, lakubuya eli xesha, uSara abe nonyana.', 'XHO22'),
(1, 18, 15, 'Wakhanyela uSara, esithi, Andihlekanga; ngokuba ebesoyika. Waphendula ke yena wathi, Hayi; kodwa wahleka.', 'XHO22'),
(1, 18, 16, 'Emka apho amadoda lawo, abheka ngaseSodom; wahamba nawo uAbraham, ewakhokela indlela yawo.', 'XHO22'),
(1, 18, 17, 'Wathi uYehova, Ndomfihlela na uAbraham into endiza kuyenza?', 'XHO22'),
(1, 18, 18, 'Yabonani, uAbraham uya kuba luhlanga olukhulu, olunamandla, zisikeleleke ngaye iintlanga zonke zehlabathi.', 'XHO22'),
(1, 18, 19, 'Kuba ndaze yena, ukuze abawisele umthetho oonyana bakhe, nendlu yakhe emva kwakhe, bagcine indlela kaYehova, benze ngokwesiko nangobulungisa; ukuze uYehova amzise kuAbraham oko wakuthethayo ngaye.', 'XHO22'),
(1, 18, 20, 'Wathi uYehova, Kungenxa yokuba isikhalo saseSodom neGomora sikhulu, nangenxa yokuba isono sabo sinzima kunene;', 'XHO22'),
(1, 18, 21, 'Ndiya kuhla ngoku, ndibone, ukuba ubenze ngokwesikhalo esifike kum; ukuba akunjalo, ndazi.', 'XHO22'),
(1, 18, 22, 'Ajika apho amadoda lawo aza aya eSodom, kodwa uAbraham waqhubeka emi phambi koYehova.', 'XHO22'),
(1, 18, 23, 'Wasondela uAbraham, wathi, Ungatshabalalisa na olilungisa nongendawo?', 'XHO22'),
(1, 18, 24, 'Mhlawumbi kukho amashumi amahlanu amalungisa phakathi komzi; Niya kuyitshabalalisa na ke loo ndawo, ningawacongi amalungisa amashumi mahlanu akuyo?', 'XHO22'),
(1, 18, 25, 'Makube lee kuwe ukwenza loo nto ukubulala olilungisa nongendawo, olilungisa abe njengongendawo onyathela kude nawe.', 'XHO22'),
(1, 18, 26, 'Wathi uYehova, Ukuba ndithe ndafumana amalungisa amashumi mahlanu emzini waseSodom, ndoyixolela yonke loo ndawo ngenxa yawo.', 'XHO22'),
(1, 18, 27, 'Waphendula uAbraham, wathi, Yabona, ndigqibe kwelokuba ndithethe kuYehova, eluthuli nothuthu.', 'XHO22'),
(1, 18, 28, 'Mhlawumbi aya kuswela isihlanu kumashumi amahlanu amalungisa; Unokuwutshabalalisa na umzi wonke, ngenxa yokuswela kwabahlanu? Wathi, Nokuba ndithe ndafumana khona amashumi omané anesihlanu kubo, andiyi kubatshabalalisa.', 'XHO22'),
(1, 18, 29, 'Wabuya wathetha kuye, esithi, Hleze kufunyanwa amashumi omané. Wathi, Andiyi kuyenza loo nto ngenxa yamashumi omane.', 'XHO22'),
(1, 18, 30, 'Wathi kuye, Mayingabi namsindo iNkosi, ndithi, Mhlawumbi bangamashumi amathathu. Wathi, Ukuba ndithe ndafumana amashumi omathathu.', 'XHO22'),
(1, 18, 31, 'Wathi, Khawubone, ndithethe kuYehova; mhlawumbi kofunyanwa amashumi amabini. Wathi, Andiyi kuwonakalisa ngenxa yamashumi omabini.', 'XHO22'),
(1, 18, 32, 'Wathi, Mayingabi namsindo iNkosi, ndiphinde ndithethe; Wathi, Andiyi kuwonakalisa ngenxa yabalishumi.', 'XHO22'),
(1, 18, 33, 'uYehova waqhubeka nohambo lwakhe, akuba wayeka ukuthetha kuAbraham, waza uAbraham wabuyela ekhayeni lakhe.', 'XHO22'),
(1, 19, 1, 'Kwathi kwakuhlwa, zeza izithunywa ezibini eSodom; uLote wayehleli esangweni laseSodom. Wazibona uLote, wema wazikhawulela; waqubuda ngobuso;', 'XHO22'),
(1, 19, 2, 'Wathi, Yabona, Nkosi yam, hamba uye endlwini yomkhonzi wakho, ulale, uhlambe iinyawo zakho, uvuke kusasa, uhambe. Baphendula bathi, Hayi! kodwa siya kulala esitratweni ubusuku bonke.', 'XHO22'),
(1, 19, 3, 'Wazingisa ke; baphambukela kuye, bangena endlwini yakhe; wabenzela ukutya, wabhaka izonka ezingenagwele, badla.', 'XHO22'),
(1, 19, 4, 'Ke kaloku, zingekalali, amadoda aloo mzi, amadoda aseSodom, ayirhawula indlu, kuthabathele kumfana, kuthabathela kwindoda, bevela macala onke.', 'XHO22'),
(1, 19, 5, 'Bambiza uLote, bathi kuye, Aphi na laa madoda angene kuwe ngobu busuku? Uzityhile kuthi, ukuze sizazi.', 'XHO22'),
(1, 19, 6, 'Waphuma uLote, waya kuwukhangela umnyango, waluvala ucango emva kwakhe.', 'XHO22'),
(1, 19, 7, 'Wathi, Musani ukukha niyenze into embi, bazalwana bam.', 'XHO22'),
(1, 19, 8, 'Khanibone, ndineentombi ezimbini ekungaziwa mntu; mandizikhuphele kuni, nenze kuzo ngokuthanda kwenu; ze ningenzi nto kula madoda; ngokuba bangene emthunzini wophahla lwam.', 'XHO22'),
(1, 19, 9, 'Bathi, Yima. Babuya bathi, Lo uze kuthetha njengomphambukeli, uza kuba ngumgwebi; ke thina sokuphatha kakubi kunabo. Bamzama uLote, basondela ukuba baluqhekeze ucango.', 'XHO22'),
(1, 19, 10, 'Amadoda lawo asolula isandla, amngenisa uLote kuwo endlwini, alutshixa ucango.', 'XHO22'),
(1, 19, 11, 'Awabetha ngobumfama amadoda awayesemnyango wendlu, ethabathela kumfana esa kwixhego, adinwa kukufuna umnyango.', 'XHO22'),
(1, 19, 12, 'Athi amadoda lawo kuLote, Usenaye na oobayeni bomyeni, noonyana, neentombi, nabo bonke onabo kulo mzi, bakhuphe kule ndawo?', 'XHO22'),
(1, 19, 13, 'Siya kuyonakalisa le ndawo, ngokuba sikhulu isikhalo ngabo phambi koYehova; yaye uYehova usithume ukuba sinitshabalalise.', 'XHO22'),
(1, 19, 14, 'Waphuma, uLote, wathetha kubayeni beentombi zakhe, abaya kwezendela iintombi zakhe, wathi, Phumani niphume kule ndawo; ngokuba uYehova uya kuwonakalisa lo mzi. Kodwa wayekhangeleka njengomntu ohlekisayo nabakhwenyana bakhe.', 'XHO22'),
(1, 19, 15, 'Kwathi kwakusa, izithunywa zamngxamisa uLote, zathi, Vuka, thabatha umkakho, neentombi zakho zombini, aba banokufumaneka; hleze utshatyalaliswe ngobubi besixeko.', 'XHO22'),
(1, 19, 16, 'Uthe akulibala, amadoda abamba isandla sakhe, nesandla somkakhe, nesandla seentombi zakhe zombini; iNkosi yasikwa yimfesane ke kuye, bamkhupha, bamjulela ngaphandle komzi.', 'XHO22'),
(1, 19, 17, 'Ebakhuphele phandle, wathi, Balekani, uwusindise umphefumlo wenu; musani ukubheka emva, musani ukuma ethafeni lonke; sabelani entabeni, hleze nitshabalale.', 'XHO22'),
(1, 19, 18, 'Wathi kuwo uLote, Mayingabi njalo, nkosi yam.', 'XHO22'),
(1, 19, 19, 'Khawubone, umkhonzi wakho ufumene inceba emehlweni akho; yaye andinakusabela entabeni, hleze ndifikelwe bububi ndize ndife.', 'XHO22'),
(1, 19, 20, 'Ke kaloku, yabona, esi sixeko siza kubalekela kuso, yaye sincinane: Makhe ndibalekele kuso (asisincinane na?) yaye uya kuphila umphefumlo wam.', 'XHO22'),
(1, 19, 21, 'Wathi kuye, Yabona, mna ndikholisile nam ngale ndawo, ukuba ndingawutshabalalisa lo mzi uthethe ngawo.', 'XHO22'),
(1, 19, 22, 'Suk’ ume, uzimele khona; kuba andinakwenza nto, awukafiki kuyo. Ngenxa yoko igama laloo mzi kwathiwa yiTzohare.', 'XHO22'),
(1, 19, 23, 'Ilanga lalithe chatha emhlabeni xa uLote engena eTsohare.', 'XHO22'),
(1, 19, 24, 'Wanisa ke uYehova isulfure nomlilo phezu kweSodom neGomora ezulwini;', 'XHO22'),
(1, 19, 25, 'Wazisingela phantsi ezo zixeko, nelizwe lonke, nabo bonke abemi bezo zixeko.', 'XHO22'),
(1, 19, 26, 'Kodwa umkakhe, ekhangele emva kwakhe, waba yintsika yetyuwa.', 'XHO22'),
(1, 19, 27, 'uAbraham wavuka ekuseni ukuze aye kuloo ndawo wayemi kuyo phambi koYehova.', 'XHO22'),
(1, 19, 28, 'Wondela eSodom naseGomora, nakulo lonke ilizwe eliyintlango, wawubona unyuka umsi welo zwe, njengomsi weziko lemfutho.', 'XHO22'),
(1, 19, 29, 'Xa uThixo wazitshabalalisayo izixeko zasentlango, uThixo wamkhumbula uAbraham, wamkhupha uLote kwelo bhongo, xa wayetshabalalisa izixeko awayehlala kuzo uLote.', 'XHO22'),
(1, 19, 30, 'Wemka uLote eTsohare, wahlala entabeni, neentombi zakhe zombini; ngokuba ebesoyika ukuhlala eTsohare, waya wahlala emqolombeni, yena neentombi zakhe zombini.', 'XHO22'),
(1, 19, 31, 'Yathi eyamazibulo kwencinane, Ubawo waluphele, yaye akukho bani ehlabathini unokungena kuthi njengezinye iintlanga.', 'XHO22'),
(1, 19, 32, 'Yiza, simsele ubawo iwayini, sinamathele kuye, sibakhusele oonyana bakabawo;', 'XHO22'),
(1, 19, 33, 'Zamseza ke uyise iwayini ngobo busuku, yangena eyamazibulo, yalala noyise; kwaye akazange aqaphele xa elele okanye ekuvukeni kwakhe.', 'XHO22'),
(1, 19, 34, 'Ngengomso yathi eyamazibulo kwencinane, Yabona, bendilele nobawo phezolo; makabuye asele iwayini ngobu busuku; ungene, ulale naye, silondoloze imbewu kabawo.', 'XHO22'),
(1, 19, 35, 'Zamseza ke uyise iwayini ngobo busuku, yavuka, encinane yalala naye; kwaye akazange aqaphele xa elele okanye ekuvukeni kwakhe.', 'XHO22'),
(1, 19, 36, 'Zamitha zombini iintombi zikaLote ngoyise.', 'XHO22'),
(1, 19, 37, 'Enkulu yazala unyana, yathi igama lakhe nguMowabhi; yena nguyise wamaMowabhi unanamhla.', 'XHO22'),
(1, 19, 38, 'Naye omncinane wazala unyana, wathi igama lakhe nguBhenami; yena nguyise wonyana baka-Amon unanamhla.', 'XHO22'),
(1, 20, 1, 'Wemka apho uAbraham, waya ezweni lasezantsi, wahlala phakathi kweKadeshe neShure, wahlala eGerare.', 'XHO22'),
(1, 20, 2, 'Wathi uAbraham kuSara umkakhe, Ngudade wethu; uAbhimeleki ukumkani waseGerare wathumela ukuba kuthatyathwe uSara.', 'XHO22'),
(1, 20, 3, 'Weza uThixo kuAbhimeleki ngephupha ebusuku, wathi kuye, Uyabona, ufile ngenxa yomfazi omthabathileyo; ngokuba ngumkamntu.', 'XHO22'),
(1, 20, 4, 'uAbhimeleki akasondelanga kuye, wathi, Nkosi, uyafuna na ukubulala amalungisa?', 'XHO22'),
(1, 20, 5, 'Akatshongo na yena kum ukuthi, Ngudade wethu? Wathi, Ngudade wethu; Le nto ndiyenze ngentliziyo yam yonke, nangobumsulwa bezandla zam.', 'XHO22'),
(1, 20, 6, 'Wathi uThixo kuye ngephupha, Ndiyazi, ngokuba uyenze le nto ngentliziyo yakho igqibelele; ngenxa enokuba ndanithintelayo nina, ukuba ningabi ngabachasi bam; ke ngoko, andikuvumelanga ukuba umchukumise.', 'XHO22'),
(1, 20, 7, 'Ngoko yibuyisele loo ndoda emfazini wayo; ngokuba engumprofeti, uya kunithandazela, niphile.', 'XHO22'),
(1, 20, 8, 'Wavuka kusasa uAbhimeleki, wababiza bonke abakhonzi bakhe, wabaxelela zonke ezi zinto babeziva; oyika kakhulu amadoda.', 'XHO22'),
(1, 20, 9, 'uAbhimeleki wambiza uAbraham, wathi kuye, Yintoni na le nto uyenzileyo kuthi? Ndikone ngantoni na, ukuba undizisele isono esikhulu nje nasebukumkanini bam? Undenzele izinto ezingafanele ukwenziwa.', 'XHO22'),
(1, 20, 10, 'Wathi uAbhimeleki kuAbraham, Ubonile na?', 'XHO22'),
(1, 20, 11, 'Wathi uAbraham, Kungokuba ndithe, Akukho koyika Thixo kule ndawo; zondibulala ngenxa yomkam.', 'XHO22'),
(1, 20, 12, 'Kunjalo nje, ngudade wethu; yintombi kabawo, akantombi kama; waba ngumfazi wam.', 'XHO22'),
(1, 20, 13, 'Oko uThixo wandikhuphayo endlwini kabawo, ndathi kuye, Yinceba yakho, ukuba undenzele inceba; kuzo zonke iindawo esifika kuzo uthi kum, Ndingumzalwana wam.', 'XHO22'),
(1, 20, 14, 'uAbhimeleki wathabatha impahla emfutshane, neenkomo, nabakhonzi, nabakhonzazana, wanika uAbraham, wambuyisela kuye uSara umkakhe.', 'XHO22'),
(1, 20, 15, 'Wathi uAbhimeleki, Yabona, ilizwe lam liphambi kwakho; hlala apho uthanda khona.', 'XHO22'),
(1, 20, 16, 'Wathi kuSara, Uyabona, udade wenu ndimnike iwaka lesilivere; yabona, isigqubuthelo esikuwe, nakubo bonke aba nawe, nakubo bonke abanye; ngoko wakhalinyelwa.', 'XHO22'),
(1, 20, 17, 'Wathandaza uAbraham kuThixo; uThixo wamphilisa uAbhimeleki, nomkakhe, nabakhonzazana bakhe; bazala abantwana.', 'XHO22'),
(1, 20, 18, 'Ngokuba uYehova wabazala bonke abantwana bendlu ka-Abhimeleki ngenxa kaSara, umka-Abraham.', 'XHO22'),
(1, 21, 1, 'Ke kaloku uYehova wamvelela uSara njengoko ebetshilo, uYehova wamenzela uSara njengoko abekuthethile.', 'XHO22'),
(1, 21, 2, 'Wamitha ke uSara waza wamzalela uAbraham unyana ebudaleni bakhe, ngexesha elimisiweyo uThixo awayethethe ngalo kuye.', 'XHO22'),
(1, 21, 3, 'uAbraham wamthiya igama unyana wonyana wakhe lowo wamzalelayo, awamzalela yena uSara, igama elinguIsake.', 'XHO22'),
(1, 21, 4, 'uAbraham wamalusa uIsake unyana wakhe, akuba ntsuku zisibhozo ezelwe, njengoko uThixo abemwisele umthetho.', 'XHO22'),
(1, 21, 5, 'uAbraham ubeminyaka ilikhulu ezelwe, ukuzalwa kukaIsake unyana wakhe.', 'XHO22'),
(1, 21, 6, 'Wathi uSara, Undihlekisile uThixo;', 'XHO22'),
(1, 21, 7, 'Wathi, Ngubani na obengamxelela uAbraham, ukuba uSara wanyisa abantwana? ngokuba ndimzalele unyana ekwaluphaleni kwakhe.', 'XHO22'),
(1, 21, 8, 'Wakhula umntwana, walunyulwa, waza uAbraham wenza itheko elikhulu mhla alunyulwayo uIsake.', 'XHO22'),
(1, 21, 9, 'uSara wabona ukuba unyana kaHagare umYiputazana, awamzalela uAbraham, ehlekisa ngaye.', 'XHO22'),
(1, 21, 10, 'Wathi kuAbraham, Sikhuphele phandle esi sicakazana nonyana waso, kuba unyana waloo mkhonzazana akayi kulidla ilifa ndawonye nonyana wam uIsake.', 'XHO22'),
(1, 21, 11, 'Yaba mbi kakhulu loo mcimbi emehlweni ka-Abraham ngenxa yonyana wakhe.', 'XHO22'),
(1, 21, 12, 'Wathi uThixo kuAbraham, Musa ukukuvumela ukuba kube kubi emehlweni akho ngenxa yonyana wakho, nangenxa yomkhonzazana wakho; Entweni yonke ayithethileyo uSara kuwe, liphulaphule izwi lakhe; ngokuba iya kubizwa ngoIsake imbewu yakho.', 'XHO22'),
(1, 21, 13, 'Ndiza kumenza uhlanga ngonyana wekhobokazana, ngenxa yokuba eyimbewu yakho.', 'XHO22'),
(1, 21, 14, 'Wavuka kusasa uAbraham, wathabatha isonka neselwa lamanzi, wanika uHagare, wazibeka emagxeni akhe nomntwana, wathi kuye, makahambe; wemka, wabhadula entlango eBher-shebha.', 'XHO22'),
(1, 21, 15, 'Aphela amanzi etankini, waza umntwana wamphosa phantsi kwelinye ityholo.', 'XHO22'),
(1, 21, 16, 'Waya wahlala phantsi malunga nayo, umgama onjengesaphetha; ngokuba wathi, Andiyi kukubona ukufa komntwana. Wahlala phantsi malunga naye, waphakamisa izwi lakhe, wadanduluka.', 'XHO22'),
(1, 21, 17, 'Waliva uThixo ilizwi lenkwenkwe; samemeza isithunywa sikaThixo kuHagare sisemazulwini, sathi kuye, Ukhathazwa yintoni na, Hagare? Musa ukoyika; ngokuba uThixo ulivile ilizwi lonyana apho akhona.', 'XHO22'),
(1, 21, 18, 'Suka ume, uyiphakamise inkwenkwe, uyibambe ngesandla; ngokuba ndiya kumenza uhlanga olukhulu.', 'XHO22'),
(1, 21, 19, 'uThixo wawavula amehlo akhe, wabona iqula lamanzi; Waya kuzalisa ingqayi ngamanzi, wayiseza inkwenkwe.', 'XHO22'),
(1, 21, 20, 'uThixo waba nenkwenkwe; wakhula, wahlala entlango, waba ngumtoli.', 'XHO22'),
(1, 21, 21, 'Wahlala entlango yaseParan, unina wamzeka wangumkakhe ezweni laseYiputa.', 'XHO22'),
(1, 21, 22, 'Ngelo xesha uAbhimeleki noPikole umthetheli-mkhosi wakhe wathetha noAbraham, bathi, uYehova unawe kuko konke okwenzayo.', 'XHO22'),
(1, 21, 23, 'Ngoko ndifungele uThixo apha, ukuba akuyi kundenza ngobumenemene mna, nonyana wam, nonyana wam; kodwa ngokwenceba endikubonise yona, uze wenze kum nakwilizwe owaphambukela kulo njengomphambukeli.', 'XHO22'),
(1, 21, 24, 'Wathi uAbraham, Mna ndofunga.', 'XHO22'),
(1, 21, 25, 'uAbraham wamkhalimela uAbhimeleki ngenxa yequla elo, ababelithabathe ngegunya abakhonzi baka-Abhimeleki;', 'XHO22'),
(1, 21, 26, 'Wathi uAbhimeleki, Bendingazi ukuba yenziwe ngubani na loo nto;', 'XHO22'),
(1, 21, 27, 'uAbraham wathabatha impahla emfutshane, neenkomo, wanika uAbhimeleki; benza umnqophiso bobabini.', 'XHO22'),
(1, 21, 28, 'uAbraham wabeka amathokazi asixhenxe ngasempahleni emfutshane.', 'XHO22'),
(1, 21, 29, 'Wathi uAbhimeleki kuAbraham, Ayintoni na la mathokazi ezimvu asixhenxe uzinikileyo?', 'XHO22'),
(1, 21, 30, 'Wathi, La mathokazi eegusha asixhenxe wowathabatha esandleni sam, abe nokungqina ukuba eli qula limbe mna.', 'XHO22'),
(1, 21, 31, 'Ngenxa yoko wathi loo ndawo yiBher-shebha; kuba kulapho bafunga khona bobabini.', 'XHO22'),
(1, 21, 32, 'Benza ke umnqophiso noBher-shebha, wema uAbhimeleki noPikoli umthetheli womkhosi wakhe, babuyela ezweni lamaFilisti.', 'XHO22'),
(1, 21, 33, 'uAbraham watyala umthi apho, waza wathandaza kuNdikhoyo uThixo ongunaphakade.', 'XHO22'),
(1, 21, 34, 'uAbraham waba ngumphambukeli iintsuku ezininzi kwilizwe lamaFilisti.', 'XHO22'),
(1, 22, 1, 'Emva koko uThixo wamvavanya uAbraham, wathi kuye: “Abraham! Wathi yena, Ndilapha.', 'XHO22'),
(1, 22, 2, 'Wathi, Thabatha kaloku unyana wakho, emnye kuwe, omthandayo, uIsake, uye ezweni laseMoriya; umenzele khona idini elinyukayo, kwenye yeentaba endokuxelela yona.', 'XHO22'),
(1, 22, 3, 'Wavuka kusasa uAbraham, walibopha iesile lakhe, wathabatha abafana bakhe bobabini, noIsake unyana wakhe, wahamba naye, wacanda iinkuni zedini elinyukayo, wemka esiya kuloo ndawo wayemxelele yona uThixo.', 'XHO22'),
(1, 22, 4, 'Ngomhla wesithathu uAbraham wawaphakamisa amehlo akhe, wayibona loo ndawo ikude.', 'XHO22'),
(1, 22, 5, 'Wathi uAbraham kumadodana, Hlalani nina apha ne-esile; siye kunqula khona mna nomfana, sibuyele kuwe.', 'XHO22'),
(1, 22, 6, 'Wazithabatha uAbraham iinkuni zedini elinyukayo, wazisa kuIsake unyana wakhe; waphatha umlilo nemela esandleni sakhe; bahamba bobabini kunye.', 'XHO22'),
(1, 22, 7, 'Wathi uIsake kuAbraham uyise, Bawo! Ndim lo nyana wam. Wathi, Ukho umlilo neenkuni, kodwa liphi na idini elinyukayo?', 'XHO22'),
(1, 22, 8, 'Wathi uAbraham, uThixo wozinyulela ixhwane elitshiswayo, nyana wam; bahamba bobabini kunye.', 'XHO22'),
(1, 22, 9, 'Bafika endaweni abemxelele yona uThixo; yaye uAbraham wakha isibingelelo apho waza walungisa iinkuni waza wabophelela uIsake unyana wakhe waza wambeka esibingelelweni phezu kweenkuni.', 'XHO22'),
(1, 22, 10, 'Wasolula isandla uAbraham, wathabatha isitshetshe, ukuba ahlabe unyana wakhe.', 'XHO22'),
(1, 22, 11, 'Sambiza ke isithunywa sikaYehova ezulwini, sathi, Abraham, Abraham! Wathi yena, Ndilapha.', 'XHO22'),
(1, 22, 12, 'Wathi, Musa ukusisa isandla sakho kumfana, musa ukumenza nto, ngokuba ndiyazi ngoku ukuba uyamoyika uThixo, ekubeni ungandikhanyelanga unyana wakho emnye.', 'XHO22'),
(1, 22, 13, 'uAbraham waphakamisa amehlo akhe, wakhangela, nantso inkunzi yegusha ngasemva etyholweni, ineempondo zayo; emva koko uAbraham waya wathabatha inkunzi yegusha waza wenza umbingelelo otshiswayo esikhundleni sonyana wakhe.', 'XHO22'),
(1, 22, 14, 'uAbraham wayibiza loo ndawo ngokuthi, NguYehova Jire; njengoko kuthiweyo, Makube njalo entabeni kaYehova.', 'XHO22'),
(1, 22, 15, 'Sambiza ke isithunywa sikaYehova okwesibini, sisemazulwini.', 'XHO22'),
(1, 22, 16, 'Wathi, Ndizifungile, utsho uYehova, ngenxa enokuba uyenzile le nto, akwakhanyela unyana wakho okuphela kwakhe;', 'XHO22'),
(1, 22, 17, 'Ndiya kukusikelela ndiyandise imbewu yakho njengeenkwenkwezi zezulu nanjengentlabathi eselunxwemeni lolwandle; imbewu yakho iyidle ilifa indlu yeentshaba zayo;', 'XHO22'),
(1, 22, 18, 'Zonke iintlanga zehlabathi ziya kuzisikelela ngembewu yakho; ngenxa yokuba uliphulaphule ilizwi lam.', 'XHO22'),
(1, 22, 19, 'Wabuyela uAbraham kumadodana akhe, bemka baya kunye eBher-shebha; yaye uAbraham wahlala eBher-shebha.', 'XHO22'),
(1, 22, 20, 'Emveni kwezi zinto, waxelelwa uAbraham, kwathiwa, Yabona, uMilka naye wamzalela uNahore, umninawa wakho;', 'XHO22'),
(1, 22, 21, 'noHusi, izibulo lakhe, noBhusi, umninawa wakhe, noKemuweli, uyise ka-Aram;', 'XHO22'),
(1, 22, 22, 'noKesede, noHaso, noPildazi, noJidlafu, noBhetuweli.', 'XHO22'),
(1, 22, 23, 'uBhetuweli wazala uRebheka; Oonyana ababini ababenamazinyo asithoba uMilka wazala uNakore, umntakwabo Abraham.', 'XHO22'),
(1, 22, 24, 'Intombi yakhe, egama linguReuma, yazala uTebha, noGaham, noTahase, noMaga.', 'XHO22'),
(1, 23, 1, 'Ke kaloku uSara ebeminyaka ilikhulu elinamanci mabini anesixhenxe; Yiyo le iminyaka yokudla ubomi kukaSara.', 'XHO22'),
(1, 23, 2, 'Wafa ke uSara eKiriyati-arbha; eHebron, ezweni lakwaKanan, waya uAbraham kumlilela uSara, wamlilela.', 'XHO22'),
(1, 23, 3, 'Wema uAbraham ngakumfi wakhe, wathetha koonyana bakaHeti, wathi,', 'XHO22'),
(1, 23, 4, 'Ndingumphambukeli nondwendwe phakathi kwenu; ndinike ingcwaba nawe, ndingcwabe umfikazi phambi kobuso bam.', 'XHO22'),
(1, 23, 5, 'Bamphendula uAbraham oonyana bakaHeti, bathi kuye:', 'XHO22'),
(1, 23, 6, 'Sive, nkosi yam, wena uyinkosana eyomeleleyo phakathi kwethu; ngcwaba umfi wakho emangcwabeni ethu anyuliweyo; akukho bani kuthi uya kulivala ingcwaba lakho, ukuba akumngcwabi umfikazi lowo.', 'XHO22'),
(1, 23, 7, 'Wesuka uAbraham, wawa ngobuso bakhe phambi kwabantu belizwe elo, phambi koonyana bakaHeti.', 'XHO22'),
(1, 23, 8, 'Wathetha kubo, wathi, Ukuba nifuna ukuba ndimngcwabe umfikazi phambi kobuso bam; yiva, undixelele ilizwi elilungileyo ngoEfron, unyana kaTsohare.', 'XHO22'),
(1, 23, 9, 'Andinike umqolomba waseMagpela, lowo anawo, ekupheleni kwentsimi yakhe; ngokuba nithe nandinika, yoba bubutyebi bengcwaba phakathi kwenu, njengoko inokulingana.', 'XHO22'),
(1, 23, 10, 'uEfron wahlala phakathi koonyana bakaHeti; uEfron umHeti wamphendula uAbraham, apho babephulaphule oonyana bakaHeti, nabo bonke abangena ngesango lomzi wakhe, wathi,', 'XHO22'),
(1, 23, 11, 'Hayi, nkosi, ndiphulaphule; Intsimi leyo ndikunika yona, nomqolomba okuyo ndikunika; ndiyakunika phambi koonyana babantu bakowethu; ngcwaba umfikazi lowo.', 'XHO22'),
(1, 23, 12, 'Waqubuda uAbraham phambi kwabantu belo zwe.', 'XHO22'),
(1, 23, 13, 'Wathetha kuEfron, bephulaphule abemi belo zwe, wathi, Ukuba uyapha, phulaphula; ndiya kukunika imali ngelizwe; yithabathe kum, ndingcwabe umfikazi khona.', 'XHO22'),
(1, 23, 14, 'uEfron wamphendula uAbraham, wathi kuye:', 'XHO22'),
(1, 23, 15, 'Nkosi yam, ndiphulaphule. Umhlaba uthengwa ngeeshekele zesilivere ezimakhulu mane; yintoni phakathi kwam nawe? wandule ukungcwaba umfikazi lowo.', 'XHO22'),
(1, 23, 16, 'uAbraham wamphulaphula uEfron; uAbraham wayilinganisa isilivere leyo kuEfron, abeyibeke ezindlebeni zoonyana bakaHeti, yangamakhulu amané eeshekele zesilivere, kuEfron, abeyibeke ezindlebeni zoonyana bakaHeti.', 'XHO22'),
(1, 23, 17, 'Yalityalwa intsimi kaEfron eMagpela phambi koMamre, nomqolomba lowo, yonke imithi ngeenxa zonke.', 'XHO22'),
(1, 23, 18, 'Kuba uAbraham wayesisityebi phambi koonyana bakaHeti, phambi kwabo bonke abangena ngesango lesixeko sakowabo.', 'XHO22'),
(1, 23, 19, 'Emva koko uAbraham wamngcwaba uSara umkakhe emqolombeni osentsimini yaseMakapela phambi kowakwaMamre; nguHebron ke lowo, ezweni lakwaKanan.', 'XHO22'),
(1, 23, 20, 'Intsimi yomyezo lowo, nomqolomba okuyo, ngoonyana bakaHeti, bawunika uAbraham, ukuba abe yindawo yakhe yokungcwaba.', 'XHO22'),
(1, 24, 1, 'uAbraham wayesele aluphele ediniwe, yaye uYehova wamsikelela ngayo yonke into uAbraham.', 'XHO22'),
(1, 24, 2, 'Wathi uAbraham kumphathi wendlu yakhe, obelawula yonke into anayo, Sibeke isandla sakho esinqeni sam.', 'XHO22'),
(1, 24, 3, 'Ndiza kukufungela ngoYehova uTHIXO wezulu noThixo womhlaba ukuba akuyi kumthabathela kuye nawuphi na umfazi unyana wam kwiintombi zamaKanan endihlala phakathi kwazo.', 'XHO22'),
(1, 24, 4, 'Kodwa hamba uye ezweni lakowethu, umzekele unyana wam uIsake umfazi.', 'XHO22'),
(1, 24, 5, 'Wathi umkhonzi kuye, Hi ke, ayavuma inkazana ukundilandela ukuza kweli lizwe; Ndimbuyisele na unyana wakho kwelo zwe waphuma kulo?', 'XHO22'),
(1, 24, 6, 'Wathi uAbraham kuye, Zigcine, ungambuyiseli unyana wam khona.', 'XHO22'),
(1, 24, 7, 'uYehova, uThixo wamazulu, owandikhuphayo endlwini kabawo, ezweni labazalwana bam, owathetha nam, wandifungisa, esithi, Lo mhlaba ndiya kuyinika imbewu yakho; Wosithuma isithunywa sakhe, ukuba sikukhokele, simthabathele khona unyana wam njengomfazi.', 'XHO22'),
(1, 24, 8, 'Ukuba loo mfazi uthe akwavuma ukukulandela, sisuse esi sifungo sam; ungambuyiseli nje unyana wam phaya.', 'XHO22'),
(1, 24, 9, 'Wasibeka umkhonzi isandla sakhe ngaphantsi kwesinqe sika-Abraham inkosi yakhe, wamfungela loo nto.', 'XHO22'),
(1, 24, 10, 'Umkhonzi wathabatha iinkamela ezilishumi enkameleni yenkosi yakhe, wemka; ngokuba zonke iimpahla zenkosi yakhe zisesandleni sakhe; wemka, waya eMesopotami, eNakore.', 'XHO22'),
(1, 24, 11, 'Waziguqisa iinkamela ngaphandle komzi, ngasequleni, ngokuhlwa, ngexa lokuphuma kwabakhi bamanzi.', 'XHO22'),
(1, 24, 12, 'Wathi, uYehova, uThixo ka-Abraham, inkosi yam, ndithume namhla, ukuba ube nenceba kuAbraham inkosi yam.', 'XHO22'),
(1, 24, 13, 'Yabona, ndimi ngasequleni; zaphuma iintombi zabantu baloo mzi, zisiya kukha amanzi.', 'XHO22'),
(1, 24, 14, 'Mayihambe intombi endithi kuyo, Mayibeke indebe yakho phantsi, ndisele; athi, Sela, neenkamela zakho ndoziseza; Makabe nguye lowo ubumwisele umthetho umkhonzi wakho uIsake; Ndiya kwazi ngale nto ke, ukuba wenze inceba enkosini yam.', 'XHO22'),
(1, 24, 15, 'Akuba engekagqibi ukuthetha, waphuma uRebheka, lowo wayezalwa nguBhetuweli, unyana kaMilka, umkaNakore, umkhuluwa ka-Abraham, enengqayi egxalabeni lakhe.', 'XHO22'),
(1, 24, 16, 'Intombi leyo ibe intle kunene, njengentombi enyulu, engaziwa mntu; Wehla waya equleni, wayizalisa ingqayi yakhe, wenyuka.', 'XHO22'),
(1, 24, 17, 'Wagidima umkhonzi waya kuyikhawulela, wathi, Ndiphe ndisele amanzi emphandeni wakho.', 'XHO22'),
(1, 24, 18, 'Wathi, Sela, nkosi yam. Wawuhlisa kamsinya umphanda wakhe esandleni sakhe, wamseza.', 'XHO22'),
(1, 24, 19, 'Akugqiba ukusela, wathi, Neenkamela zakho ndiya kuzikhelela, zigqibe ukusela;', 'XHO22'),
(1, 24, 20, 'Wakhawuleza wayigalela iemele zakhe emkhumbini, wagidima waya equleni, waziqhuba zonke iinkamela zakhe.', 'XHO22'),
(1, 24, 21, 'Wahlala wathi cwaka umntu owamothusayo, ukuze azi enoba uYehova uphumelele na kuhambo lwakhe okanye akunjalo.', 'XHO22'),
(1, 24, 22, 'Zakugqiba ukusela iinkamela, indoda leyo yathabatha ijikazi lekhonkco legolide, elisisiqingatha seshekele, nezacholo zozibini esandleni sayo, eziziishekele ezilishumi zegolide;', 'XHO22'),
(1, 24, 23, 'Uyintombi kabani na? khawutsho: ikhona indawo yolala kulamzi kayihlo?', 'XHO22'),
(1, 24, 24, 'Wathi kuye, Yintombi kaBhetuweli, unyana kaMilka, awamzalayo uNahore.', 'XHO22'),
(1, 24, 25, 'Ngaphezu koko, wathi kuye: “Sinengca, nokutya, nendawo yokulala;', 'XHO22'),
(1, 24, 26, 'Yathoba indoda leyo, yaqubuda kuYehova.', 'XHO22'),
(1, 24, 27, 'Wathi, Makabongwe uYehova, uThixo wenkosi yam uAbraham, ongayithobanga inkosi yam ngenceba yakhe, nangokuthembeka kwakhe;', 'XHO22'),
(1, 24, 28, 'Yabaleka intombazana leyo, yaya kubaxelela udaba lwendlu kanina.', 'XHO22'),
(1, 24, 29, 'uRebheka waye enodade wabo, ogama belinguLabhan; wagidima uLabhan waya endodeni leyo ngasequleni.', 'XHO22'),
(1, 24, 30, 'Wawubona umsesane wakhe, nezacholo ezandleni zodade wabo, waweva amazwi kaRebheka, udade wabo, wathi, Indoda leyo ithethe wenjenje kum; ukufikelela endodeni; nantso imi ngasezinkameleni ngasequleni.', 'XHO22'),
(1, 24, 31, 'Wathi, Ngenani, nina nisikelelweyo yiNkosi; ngubani olinde ngaphandle? ngokuba ndiyilungisile indlu nendawo yeenkamela.', 'XHO22'),
(1, 24, 32, 'Yangena indoda leyo endlwini, wazikhulula iinkamela zayo, wazinika utyani iinkamela, nokutya, namanzi, ukuba zihlambe iinyawo zayo, neenyawo zamadoda abehamba nayo.', 'XHO22'),
(1, 24, 33, 'Kwabekwa ukudla phambi kwakhe, ukuba adle; wathi yena, Andiyi kudla, andikathethi nomyalelo wam. Wathi, Thetha.', 'XHO22'),
(1, 24, 34, 'Wathi, Ndingumkhonzi ka-Abraham.', 'XHO22'),
(1, 24, 35, 'uYehova uyisikelele kunene inkosi yam; wakhula, wamnika impahla emfutshane, neenkomo, nesilivere, negolide, nabakhonzazana, nabakhonzazana, neenkamela, namahashe.', 'XHO22'),
(1, 24, 36, 'uSara, umfazi wankosi yam, wayizala inkosi yam, emveni kokuba iseyaluphele, wayinika konke enako.', 'XHO22'),
(1, 24, 37, 'Yandifungela inkosi yam, yathi, Unyana wam uze ungamzekeli umfazi ezintombini zamaKanan, endihleli ezweni lawo mna.', 'XHO22'),
(1, 24, 38, 'Kodwa uye endlwini kabawo emizalwaneni yakowethu, umzekele unyana wam umfazi.', 'XHO22'),
(1, 24, 39, 'Ndathi enkosini yam, Hi ke ayandilandela inkazana leyo.', 'XHO22'),
(1, 24, 40, 'Yathi kum, uYehova, endihamba phambi kwakhe, wosithuma isithunywa sakhe siye nawe, ayiphumelelise indlela yakho; mthathele umfazi unyana wam endlwini yam nasendlwini kabawo.', 'XHO22'),
(1, 24, 41, 'Wosisusa ngoku esi sifungo sam ekufikeni kwakho emizalwaneni yakowethu; nokuba abakunikanga nto, woba ukhululekile esifungweni sam.', 'XHO22'),
(1, 24, 42, 'Ndifike equleni namhlanje, ndathi, Yehova, Thixo ka-Abraham, nkosi yam, ukuba uthe wayiphumelelisa indlela yam endihamba ngayo!', 'XHO22'),
(1, 24, 43, 'Yabona, ndimi ngasequleni; Yaphuma intombi isiya kukha amanzi, ndathi kuyo, Ndiphe amanzi emphandeni wakho, ndisele;', 'XHO22'),
(1, 24, 44, 'Wathi kum, Sela wena nobabini, ndikwenzele iinkamela; Yiba ngumfazi uYehova amabele unyana wenkosi yam.', 'XHO22'),
(1, 24, 45, 'Ndingekagqibi mna ukuthetha entliziyweni yam, gqi uRebheka ethwele ingqayi egxalabeni; wehla waya equleni, wakha amanzi; ndathi kuye, Ndiphe ndisele.', 'XHO22'),
(1, 24, 46, 'Wakhawuleza wayithula iemele yakhe, wathi, Sela, neenkamela zakho ndoziseza; Ndasela ke, waziseza iinkamela.', 'XHO22'),
(1, 24, 47, 'Ndambuza ndathi, Uyintombi kabani na? Wathi, Yintombi kaBhetuweli, unyana kaNakore, awamzalelwa nguMilka;', 'XHO22'),
(1, 24, 48, 'Ndathoba, ndamnqula uYehova, ngenxa kaYehova, uThixo ka-Abraham, inkosi yam, ondikhokeleyo endleleni ethe tye, ukuba ndimzekele unyana wakhe intombi yomzalwana wenkosi yam.', 'XHO22'),
(1, 24, 49, 'Ngoku ke, ukuba nifuna ukuyikhonza inkosi yam ngenceba nenyaniso, ndixeleleni; ukuba akunjalo, ndixelele; ukuba ndingajika ekunene okanye ekhohlo.', 'XHO22'),
(1, 24, 50, 'Waphendula uLabhan noBhetuweli, bathi, Le nto iphuma kuYehova; asiyi kuthetha okubi okanye okulungileyo kuwe.', 'XHO22'),
(1, 24, 51, 'Yabona, uRebheka unawe; mthabathe, hamba ube ngumkanyana wenkosi yakho, njengoko watshoyo uYehova.', 'XHO22'),
(1, 24, 52, 'Wathi akuweva umkhonzi ka-Abraham amazwi abo, waqubuda kuNdikhoyo.', 'XHO22'),
(1, 24, 53, 'Umkhonzi wakhupha izihombo zesilivere nezegolide, neempahla, wanika uRebheka; wanika udade wabo nonina izinto ezibalulekileyo.', 'XHO22'),
(1, 24, 54, 'Badla, basela, yena namadoda abenaye, balala; bagqiba kusasa, besithi, Ndithumele enkosini yam.', 'XHO22'),
(1, 24, 55, 'Wathi udade wabo nonina, Makhe ihlale nathi intombi iintsuku ezi ngathi zilishumi; emveni koko wohamba.', 'XHO22'),
(1, 24, 56, 'Wathi kubo, Musani ukundibambezela, ngokuba uYehova uyayenza iphumelele indlela yam; ndivumele ndiye enkosini yam.', 'XHO22'),
(1, 24, 57, 'Bathi, Masiyibize intombi, sibuze kuyo ngomlomo wayo.', 'XHO22'),
(1, 24, 58, 'Bambiza uRebheka, bathi kuye, Uyathanda na ukuhamba nale ndoda? Wathi: Ndiya kuhamba.', 'XHO22'),
(1, 24, 59, 'Bamkhulula ke uRebheka udade wabo, nomondli wakhe, umkhonzi ka-Abraham, namadoda abenaye.', 'XHO22'),
(1, 24, 60, 'Bamsikelela uRebheka, bathi kuye, Ungudade wethu, yiba ngamawaka-waka, imbewu yakho iyidle ilifa izindlu zabo bayithiyayo.', 'XHO22'),
(1, 24, 61, 'Wesuka ke uRebheka nomthinjana wakhe, bakhwela iinkamela, bayilandela indoda leyo; umkhonzi wamthabatha uRebheka, wahamba.', 'XHO22'),
(1, 24, 62, 'Weza uIsake ngendlela eya equleni laseLaha-iroyi; ngokuba ebehlala eMzantsi.', 'XHO22'),
(1, 24, 63, 'uIsake wayecamngca elizweni ngokuhlwa; waphakamisa amehlo akhe, wabona iinkamela zisiza.', 'XHO22'),
(1, 24, 64, 'uRebheka waphakamisa amehlo akhe, wambona uIsake, wehla enkameleni.', 'XHO22'),
(1, 24, 65, 'Wathi kumkhonzi, Yiyiphi na indoda eya kusikhawulela kweli lizwe? Wathi umkhonzi, Yinkosi yam le; ke ngoko, wathabatha isigqubuthelo, wazigquma.', 'XHO22'),
(1, 24, 66, 'Umkhonzi wamxelela uIsake konke abekwenzile.', 'XHO22'),
(1, 24, 67, 'uIsake wamsa ententeni kanina kaSara, wathi igama lakhe nguRebheka, waba ngumkakhe; wamthanda, waza uIsake wathuthuzeleka emva kokufa kukanina.', 'XHO22'),
(1, 25, 1, 'uAbraham wathatha omnye umfazi, ogama linguKetura.', 'XHO22'),
(1, 25, 2, 'Wamzalela uShimran, noYokeshan, noMedan, noMidiyan, noIshbhaki, noShuwage.', 'XHO22'),
(1, 25, 3, 'uYokishane wazala uShebha noDedan; Oonyana bakaDedan baba ngama-Asiriya, namaLutusi, namaEhum.', 'XHO22'),
(1, 25, 4, 'Oonyana bakaMidiyan nguAsheri; uEva, uEfere, uHanoki, uAbhida noElidaha. Bonke ke ngoonyana bakaKetura.', 'XHO22'),
(1, 25, 5, 'uAbraham wamnika uIsake zonke izinto awayenazo.', 'XHO22'),
(1, 25, 6, 'Ke oonyana bezithende awayenazo uAbraham, uAbraham wabanika izipho, wabathabatha kuIsake unyana wakhe, ngoxa wayesadla ubomi, wabathabatha waya nabo empumalanga ezweni lasempumalanga.', 'XHO22'),
(1, 25, 7, 'Yiyo le imihla yeminyaka yokudla ubomi kuka-Abraham, yaba yiminyaka elikhulu elinamanci mathandathu anesihlanu.', 'XHO22'),
(1, 25, 8, 'Wafa ke uAbraham, wafa eyingwevu enkulu, aluphele, ephele ebudaleni; wahlanganiselwa kwabakowabo.', 'XHO22'),
(1, 25, 9, 'Oonyana bakhe uIsake noIshmayeli bamngcwaba emqolombeni waseMakapela, entsimini kaEfron unyana kaTsohare, umHeti, ephambi kowakwaMamre;', 'XHO22'),
(1, 25, 10, 'Umhlaba awawuthengayo uAbraham koonyana bakaHeti, wangcwatyelwa khona uAbraham noSara umkakhe.', 'XHO22'),
(1, 25, 11, 'Emva kokufa kuka-Abraham, uThixo wamsikelela uIsake unyana wakhe; wahlala uIsake ngasequleni likaLaha-iroyi.', 'XHO22'),
(1, 25, 12, 'Yiyo le ke inzala kaIshmayeli, unyana ka-Abraham, awathi uHagare umYiputazana, umkhonzi kaSara, wamzalela uAbraham.', 'XHO22'),
(1, 25, 13, 'Ngawo la amagama oonyana bakaIshmayeli, ngokwamagama abo ngokwemizalwane yabo: owamazibulo kaIshmayeli nguNebhayoti; iKedare, iAdebheli, neMibhesam,', 'XHO22'),
(1, 25, 14, 'uMishma, uDuma, noMasa;', 'XHO22'),
(1, 25, 15, 'neHadare, noTema, noYeture, noNafishe, noKedema;', 'XHO22'),
(1, 25, 16, 'Ngabo abo oonyana bakaIshmayeli, ngawo la amagama abo ngokwemizi yabo neenqaba zabo; izikhulu ezilishumi elinazibini ngokwezizwe zazo.', 'XHO22'),
(1, 25, 17, 'Yiyo le iminyaka yokudla ubomi kukaIshmayeli, ikhulu elinamanci mathathu anesixhenxe; wahlanganiselwa kwabakowabo.', 'XHO22'),
(1, 25, 18, 'Bema bethabathela eHavila besa eShure, ekhangelene neYiputa, ekuyeni eAsiriya; wafa phambi kwabazalwana bakhe bonke.', 'XHO22'),
(1, 25, 19, 'Yiyo le ke inzala kaIsake, unyana ka-Abraham: uAbraham nguyise kaIsake;', 'XHO22'),
(1, 25, 20, 'uIsake ubeminyaka imashumi mane ezelwe, ukumzeka kwakhe uRebheka, intombi kaBhetuweli, umAram wasePadan-ram, udade boLabhan umAram, ukuba abe ngumkakhe.', 'XHO22'),
(1, 25, 21, 'uIsake wamthandazela uYehova umkakhe, ngokuba ebengazali; wamitha uRebheka umkakhe.', 'XHO22'),
(1, 25, 22, 'Babambana naye abantwana; Wathi, Ukuba kunjalo, kutheni ndinje? Waya kubuza kuYehova.', 'XHO22'),
(1, 25, 23, 'Wathi uYehova kuye, Zimbini iintlanga esizalweni sakho; olunye uhlanga luya komelela kunolunye uhlanga; Omkhulu uya kukhonza komnci.', 'XHO22'),
(1, 25, 24, 'Kwathi ke kwakuzaliseka umhla wokuzalwa kwakhe, kwabakho amawele esizalweni sakhe.', 'XHO22'),
(1, 25, 25, 'Laphuma elokuqala lingqombela, lonke njengengubo yoboya; Bamthiya igama elinguEsawu.', 'XHO22'),
(1, 25, 26, 'Kamva waphuma umninawa wakhe, isandla sakhe sabamba isithende sikaEsawu; Wathi ke igama elinguYakobi; uIsake ebeminyaka imashumi mathandathu ezelwe, ukubazala kwabo.', 'XHO22'),
(1, 25, 27, 'Akhula amakhwenkwe, uEsawu waba lizingela, indoda yelizwe; yaye uYakobi wayeyindoda engenabulumko ehlala ezintenteni.', 'XHO22'),
(1, 25, 28, 'uIsake wathanda uEsawu, ngokuba ebesidla inyama yezingela; ke uRebheka wayethanda uYakobi.', 'XHO22'),
(1, 25, 29, 'Wayibhaka ke uYakobi intlama, wafika uEsawu evela ezindle, ediniwe.', 'XHO22'),
(1, 25, 30, 'Wathi uEsawu kuYakobi, Makhe ndive loo nto ibomvu, ndiyive; Yiyo loo nto kuthiwa nguEdom.', 'XHO22'),
(1, 25, 31, 'Wathi uYakobi, Ndithengisele iqashiso lakho namhla.', 'XHO22'),
(1, 25, 32, 'Wathi uEsawu, Yabona, andiyi kufa; bundenzani na obo buzibulo?', 'XHO22'),
(1, 25, 33, 'Wathi uYakobi, Niya kundibuka namhla; wamfungela, wathengisa ngobuzibulo bakhe kuYakobi.', 'XHO22'),
(1, 25, 34, 'uYakobi wamnika uEsawu isonka nepani yeentlumayo; watya, wasela, wavuka, wahamba. Wabudela ke uEsawu ubuzibulo bakhe.', 'XHO22'),
(1, 26, 1, 'Kwabakho indlala ehlabathini, ngaphandle kweyokuqala indlala eyabakho ngexesha lika-Abraham. Waya uIsake eGerare, kuAbhimeleki, ukumkani wamaFilisti.', 'XHO22'),
(1, 26, 2, 'uYehova wabonakala kuye, wathi, Musa ukuhla uye eYiputa; hlala ezweni endiya kukuxelela lona.', 'XHO22'),
(1, 26, 3, 'Hlala ungumphambukeli kweli lizwe; ndoba nawe, ndikusikelele; ngokuba ndiya kukunika wena nembewu yakho onke la mazwe, ndisimise isifungo endasifungayo kuAbraham uyihlo;', 'XHO22'),
(1, 26, 4, 'ndiya kuyandisa ndiyandise imbewu yakho njengeenkwenkwezi zezulu, ndinike imbewu yakho onke la mazwe; zisikeleleke ngembewu yakho zonke iintlanga zehlabathi;', 'XHO22'),
(1, 26, 5, 'Ngenxa yokuba uAbraham waliphulaphula ilizwi lam waza wawugcina umyalelo wam, imiyalelo yam, imimiselo yam nemithetho yam.', 'XHO22'),
(1, 26, 6, 'uIsake wakha iGerare.', 'XHO22'),
(1, 26, 7, 'Ambuza amadoda aloo ndawo ngomkakhe; Wathi, Ngudade wethu, ngokuba ebesoyika ukuthi, Ngumkam; Wathi, Mandingandibulali amadoda aloo ndawo ngenxa kaRebheka; ngokuba ebemhle imbonakalo.', 'XHO22'),
(1, 26, 8, 'Kwakuba kudala ekhona, uAbhimeleki ukumkani wamaFilisti, wakroba ngefestile, wabona uIsake edlala noRebheka, umkakhe.', 'XHO22'),
(1, 26, 9, 'uAbhimeleki wambiza uIsake, wathi, Yabona, ngumkakho ngokwenene; kwaye utheni na: Ngudade wethu? Wathi uIsake kuye, Kungokuba ndithe, Andiyi kufa ngenxa yakhe.', 'XHO22'),
(1, 26, 10, 'Wathi uAbhimeleki: “Yintoni le uyenzileyo kuthi? omnye wabantu ebenokulala nomkakho lula, ubuzosigxeka.', 'XHO22'),
(1, 26, 11, 'uAbhimeleki wabawisela umthetho abantu bakhe bonke, wathi, Othe wayichukumisa le ndoda nomkayo, wobulawa afe.', 'XHO22'),
(1, 26, 12, 'uIsake wahlwayela kwelo zwe, wazuza kalikhulu ngaloo mnyaka; uYehova wamsikelela.', 'XHO22'),
(1, 26, 13, 'Indoda leyo yaqhubeka ikhula, ikhula, ikhula de yaba mdala kakhulu.', 'XHO22'),
(1, 26, 14, 'Ngokuba ubenemfuyo yempahla emfutshane, neenkomo, nabakhonzi abaninzi, ammonela amaFilisti.', 'XHO22'),
(1, 26, 15, 'Ngenxa yokuba onke amaqula ababewamba abakhonzi bakayise ngemihla ka-Abraham uyise, amaFilisti awavala aza awazalisa ngomhlaba.', 'XHO22'),
(1, 26, 16, 'Wathi uAbhimeleki kuIsake, Suka kuthi; ngokuba womelele kunathi.', 'XHO22'),
(1, 26, 17, 'Wemka apho uIsake, wamisa intente yakhe kwintlambo yaseGerare, wahlala khona.', 'XHO22'),
(1, 26, 18, 'Wabuya uIsake wawamba amaqula, ababewambile ngemihla ka-Abraham uyise; ngenxa yokuba amaFilisti abalela emva kokufa kuka-Abraham, waza wawabiza ngamagama uyise awayewabize ngamagama.', 'XHO22'),
(1, 26, 19, 'Bemba abakhonzi bakaIsake esihlanjeni somlambo, bafumana iqula khona, linomthombo khona.', 'XHO22'),
(1, 26, 20, 'Abalusi baseGerare babambana nabalusi bakaIsake, besithi, Ngawethu la manzi; Wathi iqula elo yiEseke; ngokuba babambene naye.', 'XHO22'),
(1, 26, 21, 'Bemba elinye iqula, baphikisana ngalo, wathi igama lalo yiSitna.', 'XHO22'),
(1, 26, 22, 'Wemka khona, wemba elinye iqula; kwaye ke ngoko abazange baxabane; wathi ukuyibiza yiRehobhoti; wathi: Kungenxa yokuba ngoku uYehova usenzele indawo, yaye siya kusasazeka ehlabathini.', 'XHO22'),
(1, 26, 23, 'Wemka khona, waya eBher-shebha.', 'XHO22'),
(1, 26, 24, 'uYehova wabonakala kuye ngobo busuku, wathi, NdinguThixo ka-Abraham uyihlo; musa ukoyika, ngokuba ndinawe; ndiya kukusikelela, ndiyandise imbewu yakho, ngenxa ka-Abraham umkhonzi wam.', 'XHO22'),
(1, 26, 25, 'Wakha khona isibingelelo, walithandaza igama likaYehova, wayimisa khona intente yakhe; bemba iqula khona abakhonzi bakaIsake.', 'XHO22'),
(1, 26, 26, 'Emva koko uAbhimeleki weza kuye, evela eGerare, ehamba noAhusati umhlobo wakhe, noPikoli umthetheli womkhosi wakhe.', 'XHO22'),
(1, 26, 27, 'Wathi uIsake kubo, Nize ngani na kum, nandithiya nje, nandigxotha nje kuni?', 'XHO22'),
(1, 26, 28, 'Bathi ke bona: “Sibonile ngokwenene ukuba uYehova unawe; sathi, Makubekho isifungo phakathi kwethu nawe, senze umnqophiso nawe;', 'XHO22'),
(1, 26, 29, 'ukuze ungasenzakalisi, ngenxa yokuba asizange sikuchukumise, yaye sikwenzele okulungileyo, sikukhokele ngoxolo; ngoku niyintsikelelo kaYehova.', 'XHO22'),
(1, 26, 30, 'Wabenzela isidlo, badla, basela;', 'XHO22'),
(1, 26, 31, 'Bavuka kusasa, bafungelana; uIsake wabandulula, bamshiya enoxolo.', 'XHO22'),
(1, 26, 32, 'Beza abakhonzi bakaIsake kwangaloo mini, bamxelela indawo yequla ababelimbile, bathi kuye, Siwafumene amanzi.', 'XHO22'),
(1, 26, 33, 'Wathi ukuyibiza yiSebha; ngenxa yoko igama laloo mzi liyiBher-shebha unanamhla.', 'XHO22'),
(1, 26, 34, 'uEsawu ebeminyaka imashumi mane ezelwe, ukuzeka kwakhe uYuditi intombi kaBheri, umHeti, noBhasemati intombi kaElon, umHeti.', 'XHO22'),
(1, 26, 35, 'Kwaba buhlungu ngoIsake noRebheka.', 'XHO22'),
(1, 27, 1, 'Kwathi, akwaluphala uIsake, amehlo akhe enorhatyazo, engasaboni, wabiza uEsawu unyana wakhe omkhulu, wathi kuye, Unyana wam, wathi kuye, Yabona, nantsiya yi.', 'XHO22'),
(1, 27, 2, 'Wathi, Yabona, ndaluphele, andiyazi imini yokufa kwam.', 'XHO22'),
(1, 27, 3, 'Thabatha ngoko iintonga zakho, umphongolo wakho, nesaphetha sakho, uye ezweni, undizingele inkomo;', 'XHO22'),
(1, 27, 4, 'ndenzele ukutya okunencasa, njengoko ndikuthandayo, ukuzise kum, ndidle; ukuze umphefumlo wam ukusikelele ndingekafi.', 'XHO22'),
(1, 27, 5, 'uRebheka weva uIsake ethetha noEsawu unyana wakhe, uEsawu waya kuzingela inyamakazi endle.', 'XHO22'),
(1, 27, 6, 'uRebheka wathetha kuYakobi, unyana wakhe, wathi, Yabona, ndive uyihlo ethetha kuEsawu, umninawa wakho, esithi,', 'XHO22'),
(1, 27, 7, 'Ndizisele inyama yenkomo, undenzele ukudla okunencasa, ndidle, ndikusikelele phambi koYehova ndingekafi.', 'XHO22'),
(1, 27, 8, 'Ke ngoko, nyana wam, liphulaphule izwi lam njengoko ndikuwisela umthetho;', 'XHO22'),
(1, 27, 9, 'Yiya emhlambini, undizisele khona iinkunzi zezimvu zibe mbini, ezilungileyo; uyihlo ndiya kumenzela ukudla okumnandi, njengoko athanda ngako.', 'XHO22'),
(1, 27, 10, 'Uze uyise kuyihlo, adle, ukuze akusikelele engekafi.', 'XHO22'),
(1, 27, 11, 'Wathi uYakobi kuRebheka unina, Yabona, uEsawu, umnakwethu, yindoda exhonti, mna ndiyindoda egudileyo.', 'XHO22'),
(1, 27, 12, 'Mhlawumbi uBawo uya kundiva, ndize ndizibonakalise kuye njengomlahlekisi; yaye ndiya kuzisela intsikelelo, kungekhona intsikelelo.', 'XHO22'),
(1, 27, 13, 'Wathi unina kuye, Makube phezu kwam ukushwatyulelwa kwakho, nyana wam; mamela nje ilizwi lam, uze uye kundithathela wona.', 'XHO22'),
(1, 27, 14, 'Wayithabatha, wayisa kunina; unina wenza inyama emnandi, njengoko ayithandayo uyise.', 'XHO22'),
(1, 27, 15, 'uRebheka wathabatha iingubo zikaEsawu, unyana wakhe omkhulu, ezintle ezibe bekuye endlwini, wamambathisa uYakobi unyana wakhe omnci.', 'XHO22'),
(1, 27, 16, 'Izintsu zeebhokhwe wambathisa ezandleni zakhe, nasentanyeni yakhe egudileyo;', 'XHO22'),
(1, 27, 17, 'Wanika inyama emnandi, nesonka, abesilungise ngesandla sikaYakobi unyana wakhe.', 'XHO22'),
(1, 27, 18, 'Weza ke kuyise, wathi, Bawo! Wathi: Ndikho! ungubani nyana wam.', 'XHO22'),
(1, 27, 19, 'Wathi uYakobi kuyise, NdinguEsawu, amazibulo akho; Ndenza njengoko wandiwisela umthetho ngako, wathi, Vuka, uhlale, udle inyama, nenyama, nenyama, ukuze umphefumlo wakho undisikelele.', 'XHO22'),
(1, 27, 20, 'Wathi uIsake kunyana wakhe, Yini na ukuba ukhawuleze ukuyifumana, nyana wam? Wathi: “Kungenxa yokuba uYehova uThixo wakho ewazizisa kum.', 'XHO22'),
(1, 27, 21, 'Wathi uIsake kuYakobi, Sondela, ndikuchukumise, nyana wam, ndibone ukuba unguye na unyana wam uEsawu, akunguye, kusini na.', 'XHO22'),
(1, 27, 22, 'uYakobi waya kuIsake uyise; Wamphuthaphutha, wathi, Ilizwi lona lilizwi likaYakobi, kodwa izandla zizandla zikaEsawu.', 'XHO22'),
(1, 27, 23, 'Akamahlula, kuba izandla zakhe zazinoboya, njengezandla zikaEsawu umkhuluwa wakhe; ke ngoko wamsikelela.', 'XHO22'),
(1, 27, 24, 'Wathi, Ungunyana wam na uEsawu? Wathi yena: Ndinguye.', 'XHO22'),
(1, 27, 25, 'Wathi, Yizise kum ndidle inyama yenkomo yonyana wam, umphefumlo wam uyisikelele. Wamzisela, wadla; wamzisela iwayini, wasela.', 'XHO22'),
(1, 27, 26, 'Wathi uIsake uyise kuye, Khawusondele undiphuze, nyana wam.', 'XHO22'),
(1, 27, 27, 'Wasondela kuye, wamanga, waliva ivumba leengubo zakhe, wamsikelela, esithi, Yabona, ivumba lonyana wam linjengevumba lelizwe elisikelelwe nguYehova.', 'XHO22'),
(1, 27, 28, 'Ngako oko uThixo wakunika umbethe wezulu, nentabalala yehlabathi, nentabalala yengqolowa, newayini entsha.', 'XHO22'),
(1, 27, 29, 'Mazikukhonze izizwe, ziqubude kuwe iintlanga; balawule abazalwana bakho, baqubude kuwe oonyana bakanyoko; Uqalekisiwe okuqalekisayo, Makabongwe okusikelelayo.', 'XHO22'),
(1, 27, 30, 'Kwathi, akugqiba uIsake ukumsikelela uYakobi, uYakobi akamshiyanga uIsake uyise, weza uEsawu, umkhuluwa wakhe, evela ekuzingeleni kwakhe.', 'XHO22'),
(1, 27, 31, 'Naye wenza ukudla okunevumba elimnandi, wazisa kuyise, wathi kuyise, Makavuke ubawo, adle inyama yenkomo yonyana wakhe, ukuze umphefumlo wakho undisikelele.', 'XHO22'),
(1, 27, 32, 'Wathi uyise uIsake kuye, Ungubani na? Wathi, Ndingunyana wakho uEsawu, izibulo lakho.', 'XHO22'),
(1, 27, 33, 'Wagubha kunene uIsake, wathi, Ndingubani na? Uphi na lo uzingele isilwanyana, wasizisa kum, ndatya yonke into engekafiki, ndamsikelela? ewe, woba nentsikelelo.', 'XHO22'),
(1, 27, 34, 'Akuweva uEsawu amazwi kayise, walila ebuhlungu kakhulu, wathi kuyise, Bawo, ndisikelele nam.', 'XHO22'),
(1, 27, 35, 'Wathi yena, Kufike umninawa wakho ngobuqhophololo, wathabatha ubutyebi bakho.', 'XHO22'),
(1, 27, 36, 'Wathi, Ndathi, KunguYakobi na? ngokuba undenze kubini; ubususile ubukhulu bam; nanku ngoku nilithabathileyo. Wathi: Anindigcinelanga ntsikelelo na?', 'XHO22'),
(1, 27, 37, 'Waphendula uIsake, wathi kuEsawu, Yabona, ndimmisile waba ngukumkani wakho, nabakhuluwa bakhe ndibanike yena, baba ngabakhonzi; ndamthuthuzela ngengqolowa nangewayini; Kaloku ke, nyana wam, ndithini na ngawe?', 'XHO22'),
(1, 27, 38, 'Wathi uEsawu kuyise, Inye na le ntsikelelo unayo, bawo? Ndisikelele nam bawo! Waliphakamisa uEsawu ilizwi lakhe, walila.', 'XHO22'),
(1, 27, 39, 'Waphendula uIsake uyise, wathi kuye, Yabona, ikhaya lakho liya kuba lintabalala, nombethe wezulu phezulu.', 'XHO22'),
(1, 27, 40, 'Uya kuphila ngekrele lakho, umkhonze umninawa wakho; ukuba uthe walawula, woyaphula idyokhwe yakhe entanyeni yakho.', 'XHO22'),
(1, 27, 41, 'uEsawu wamzonda uYakobi ngenxa yentsikelelo awamsikelela ngayo uyise; uEsawu wathi entliziyweni yakhe, Isondele imihla yesijwili sikabawo; ndiya kumbulalela khona uYakobi umninawa wam.', 'XHO22'),
(1, 27, 42, 'uRebheka wawaxelelwa loo mazwi kaEsawu, unyana wakhe omkhulu, wathumela ukuba kubizwe uYakobi, unyana wakhe omnci, wathi kuye, Yabona, uEsawu umkhuluwa wakho lowo ukuchukumisayo, uhleli ngokufuna ukubulala.', 'XHO22'),
(1, 27, 43, 'Ke ngoko, nyana wam, liphulaphule izwi lam; Wesuka ke, wabalekela kuLabhan umnakwabo kwaHaran;', 'XHO22'),
(1, 27, 44, 'uhlale naye imihla embalwa, ude uphele umsindo womkhuluwa wakho;', 'XHO22'),
(1, 27, 45, 'ude umsindo womkhuluwa wakho ubuye kuwe, ayilibale into oyenzileyo kuye;', 'XHO22'),
(1, 27, 46, 'Wathi uRebheka kuIsake, Ndikruqukile bubomi bam ngenxa yeentombi zakwaHeti;', 'XHO22'),
(1, 28, 1, 'uIsake wambiza ke uYakobi, wamsikelela, wamwisela umthetho, wathi kuye, Uze ungazeki mfazi ezintombini zakwaKanan.', 'XHO22'),
(1, 28, 2, 'Suka uye ePadan-aram, endlwini kaBhetuweli unyokokhulu; Wathabatha khona umfazi ezintombini zikaLabhan, udade bonyoko.', 'XHO22'),
(1, 28, 3, 'Wanga uThixo uSomandla angakusikelela, akwandise, akwandise, ukuze ube sisikhungu sezizwe;', 'XHO22'),
(1, 28, 4, 'Makakunike intsikelelo ka-Abraham, wena, nembewu yakho, nawe; ukuze ulidle ilifa ilizwe lokuphambukela kwakho, elo uThixo walinika uAbraham.', 'XHO22'),
(1, 28, 5, 'uIsake wathi kuYakobi makahambe; waya ePadan-ram, kuLabhan unyana kaBhetuweli, umAram, udade boRebheka, unina booYakobi noEsawu.', 'XHO22'),
(1, 28, 6, 'uEsawu wabona ukuba uIsake umsikelele uYakobi, wamvumela ukuba aye ePadan-aram, azeke khona umfazi; ekumsikeleleni kwakhe, wamwisela umthetho, esithi, Uze ungazeki mfazi ezintombini zakwaKanan;', 'XHO22'),
(1, 28, 7, 'noYakobi umphulaphule uyise nonina, waya ePadan-aram;', 'XHO22'),
(1, 28, 8, 'Wabona uEsawu ukuba iintombi zakwaKanan azifunwa nguyise uIsake;', 'XHO22'),
(1, 28, 9, 'Waya uEsawu kuIshmayeli, wazeka uMahalati, intombi kaIshmayeli, unyana ka-Abraham, udade boNebhayoti, wangumkakhe.', 'XHO22'),
(1, 28, 10, 'Wemka uYakobi eBher-shebha, waya kwaHaran.', 'XHO22'),
(1, 28, 11, 'Wafika kwenye indawo, walala khona, kuba belithshonile ilanga. Wathabatha kumatye kuloo ndawo, wawabeka emqamelweni wakhe, walala khona.', 'XHO22'),
(1, 28, 12, 'Waphupha ebona intonga imi emhlabeni, incam yayo iye yafika ezulwini; nanzo izithunywa zikaThixo zinyuka ziphuma kuwo, zisihla kuwo;', 'XHO22'),
(1, 28, 13, 'nanko uYehova emi khona, wathi, NdinguYehova, uThixo ka-Abraham uyihlo, uThixo kaIsake; Lo mhlaba ulele kuwo ndiya kuwunika wena nenzala yakho.', 'XHO22'),
(1, 28, 14, 'Yaye inzala yakho iya kuba njengothuli lomhlaba, yaye uya kusasazeka entshonalanga nasempuma nasemntla nasemzantsi; yaye ngenxa yakho nembewu yakho zonke izizukulwana zehlabathi ziya kuzisikelela.', 'XHO22'),
(1, 28, 15, 'Yabona, ndinawe; ndiya kukugcina naphi na apho uya khona, ndikubuyisele kulo mhlaba; ngokuba andiyi kukushiya, andikwenzanga oko ndikuthethileyo kuwe.', 'XHO22'),
(1, 28, 16, 'Wavuka uYakobi ebuthongweni bakhe, wathi, Kunjenje ke, uYehova ukho kule ndawo; nam bendingazi.', 'XHO22'),
(1, 28, 17, 'Woyika, wathi, Hayi, ukoyikeka kwayo le ndawo! asinto yindlu kaThixo, lisango lezulu.', 'XHO22'),
(1, 28, 18, 'Wavuka kwakusasa uYakobi, walithabatha ilitye abelibeke emqamelweni wakhe, walimisa layintsika, wathulula ioli phezu kwalo.', 'XHO22'),
(1, 28, 19, 'Wathi igama laloo ndawo yiBheteli, kodwa igama laloo mzi ngenxa engaphambili beliyiLuse.', 'XHO22'),
(1, 28, 20, 'Wafunga uYakobi, wathi, Ukuba uThixo uthe waba nam, wandigcina kule ndlela ndiyihambayo, andinike isonka ndidle, neengubo zokunxiba,', 'XHO22'),
(1, 28, 21, 'ukuze ndibuyele endlwini kabawo ndinoxolo; uYehova uya kuba nguThixo wam khona.', 'XHO22'),
(1, 28, 22, 'Eli litye ndilimisileyo lasisikhumbuzo soba yindlu kaThixo; neento zonke othe wandinika, ndokunika isishumi sazo.', 'XHO22'),
(1, 29, 1, 'uYakobi waqhubela phambili nohambo lwakhe, wada waya kufika kwelasempuma.', 'XHO22'),
(1, 29, 2, 'Wakhangela, wabona iqula ezweni, nantso imihlambi emithathu yempahla emfutshane, ibuthile ngakulo; kuba imihlambi yayiseza kwelo qula, yaye kwakukho ilitye elikhulu emlonyeni wequla.', 'XHO22'),
(1, 29, 3, 'Kulapho ibihlanganisana khona yonke imihlambi, baliqengqe ilitye, balisuse emlonyeni wequla, bayiseze impahla emfutshane, balibuyisele ilitye emlonyeni wequla endaweni yalo.', 'XHO22'),
(1, 29, 4, 'Wathi uYakobi kubo, Bazalwana bam, nivela phi na? Bathi: “Sisuka kwaHaran.', 'XHO22'),
(1, 29, 5, 'Wathi kubo, Niyamazi na uLabhan, unyana kaNakore? Bathi ke bona, Siyamazi.', 'XHO22'),
(1, 29, 6, 'Wathi kubo, Usaphilile na? Bathi, Usahleli, nanko uRakeli intombi yakhe, esiza nempahla emfutshane.', 'XHO22'),
(1, 29, 7, 'Waza wathi: Khangela, kusesemini, kwaye alikafiki ixesha lokuba iinkomo zihlanganiswe; seza iigusha, uziphe ukutya.', 'XHO22'),
(1, 29, 8, 'Bathi, Asinako, ide ihlanganiselwe ndawonye yonke impahla emfutshane, liqengqiwe ilitye, lisuke emlonyeni wequla, siyiseze ke impahla emfutshane.', 'XHO22'),
(1, 29, 9, 'Ke kaloku esathetha nabo, wafika uRakeli nempahla emfutshane kayise, kuba ubesalusa.', 'XHO22'),
(1, 29, 10, 'Uthe uYakobi akumbona uRakeli, intombi kaLabhan, nodade bonina, nempahla emfutshane kaLabhan, udade bonina, wasondela uYakobi, waliqengqa ilitye lesuka emlonyeni wequla, wayishiya impahla emfutshane kaLabhan, udade bonina nika amanzi.', 'XHO22'),
(1, 29, 11, 'uYakobi wamanga uRakeli, waliphakamisa izwi lakhe, walila.', 'XHO22'),
(1, 29, 12, 'uYakobi wamxelela uRakeli ukuba ungudade boyise, nokuba ungunyana kaRebheka; wagidima waya kuxelela uyise.', 'XHO22'),
(1, 29, 13, 'Uthe uLabhan akuluva udaba lukaYakobi, unyana wodade wabo, wagidima waya kumkhawulela, wamwola, wamanga, wamngenisa endlwini yakhe. Wamxelela uLabhan yonke loo nto.', 'XHO22'),
(1, 29, 14, 'Wathi uLabhan kuye, Okunene ulithambo lam, uyinyama yam; Wahlala naye inyanga yanye.', 'XHO22'),
(1, 29, 15, 'Wathi uLabhan kuYakobi, Kungokuba ungudade wethu na? khawutsho uzakuba yintoni umvuzo wakho?', 'XHO22'),
(1, 29, 16, 'uLabhan waye eneentombi ezimbini; igama lenkulu lalinguLeya, igama lenci lalinguRakeli.', 'XHO22'),
(1, 29, 17, 'Ke uLeya wayenamehlo athambileyo; ke uRakeli ebemhle isiqu, emhle imbonakalo.', 'XHO22'),
(1, 29, 18, 'uYakobi ubethanda uRakeli; Wathi, Ndokukhonza iminyaka esixhenxe ngenxa kaRakeli, intombi yakho encinane.', 'XHO22'),
(1, 29, 19, 'Wathi uLabhan Kulungile ukuba ndimnike wena, kunokuba ndimnike enye indoda; Hlala nam.', 'XHO22'),
(1, 29, 20, 'uYakobi wakhonza uRakeli iminyaka esixhenxe; kwaba yimihla embalwa kuye, ngenxa yothando abemthanda ngalo.', 'XHO22'),
(1, 29, 21, 'Wathi uYakobi kuLabhan, Ndinike umkam, ngokuba izalisekile imihla yam, ukuba ndimngene.', 'XHO22'),
(1, 29, 22, 'uLabhan wabahlanganisa bonke abantu baloo mmandla waza wenza isidlo.', 'XHO22'),
(1, 29, 23, 'Kwahlwa, wathabatha uLeya intombi yakhe, wamngenisa kuye; wangena kuye.', 'XHO22'),
(1, 29, 24, 'Intombi yakhe uLeya, uLabhan wayinika uZilipa, umkhonzazana wakhe, ukuba abe ngumkhonzazana wayo.', 'XHO22'),
(1, 29, 25, 'Kwathi ngesifingo, wabona ukuba nguLeya, wathi kuLabhan, Yintoni na le nto undenze yona? Andikhonzanga nawe uRakeli na? so undiqhathe ngantoni?', 'XHO22'),
(1, 29, 26, 'Wathi uLabhan, Makwenjiwa nje apha ezweni lakowethu, ukuthi, kunikwe abamncinane phambi kowamazibulo.', 'XHO22'),
(1, 29, 27, 'Gqibezela iveki yakhe, yaye sokunika ngenkonzo oya kundikhonza ngayo eminye iminyaka esixhenxe.', 'XHO22'),
(1, 29, 28, 'Wenjenjalo uYakobi, wayigqiba iveki yaloo mfazi; wamnika noRakeli intombi yakhe, yangumkakhe.', 'XHO22'),
(1, 29, 29, 'Intombi yakhe uRakeli, uLabhan wayinika uBhiliha, umkhonzazana wakhe, ukuba abe ngumkhonzazana wakhe.', 'XHO22'),
(1, 29, 30, 'Naye wamngena uRakeli, wamthanda uRakeli ngaphezu koLeya, wakhonza ke naye eminye iminyaka esixhenxe.', 'XHO22'),
(1, 29, 31, 'uYehova wabona ukuba uLeya uthiyiwe, wasivula isizalo sakhe; ke uRakeli ebengazali.', 'XHO22'),
(1, 29, 32, 'Wamitha ke uLeya, wazala unyana, wathi igama lakhe nguRubhen; ngokuba wathi, uYehova uzibonile iintsizi zam; ngoku iya kundithanda indoda yam.', 'XHO22'),
(1, 29, 33, 'Wabuya wamitha, wazala unyana; Wathi, uYehova uvile ukuba ndithiyiwe, wandinika lo nyana; Wamthiya igama elinguSimon.', 'XHO22'),
(1, 29, 34, 'Wabuya wamitha, wazala unyana; Wathi, Ngoku indoda yam iya kundizeka, kuba ndizele oonyana abathathu; ngenxa yoko wamthiya igama elinguLevi.', 'XHO22'),
(1, 29, 35, 'Wabuya wamitha, wazala unyana, wathi, Kaloku ke ndithanda ukudumisa uYehova; ke ngoko wamthiya igama elinguYuda; akaphinda azale.', 'XHO22'),
(1, 30, 1, 'Ke kaloku akubona uRakeli ukuba akamzaleli mntwana uYakobi, uRakeli wammonela udade wabo. Wathi kuYakobi, Ndinike abantwana bam, hleze ndife.', 'XHO22'),
(1, 30, 2, 'uYakobi wavutha ngumsindo kuRakeli, wathi, Ndisesikhundleni sikaThixo na, owasitshabalalisayo isiqhamo sesizalo sikanyoko?', 'XHO22'),
(1, 30, 3, 'Wathi, Khangela, uBhiliha, umkhonzazana wam, ungene kuye; kwaye uya kuzala emathangeni am, ukuze ndibe nabantwana kunye naye.', 'XHO22'),
(1, 30, 4, 'Wamnika ke uBhiliha, umkhonzazana wakhe, ukuba abe ngumfazi wakhe; uYakobi wamngena.', 'XHO22'),
(1, 30, 5, 'Wamitha uBhiliha, wamzalela uYakobi unyana.', 'XHO22'),
(1, 30, 6, 'Wathi uRakeli, uThixo undikhokele, waliva izwi lam, wandinika unyana; Yiyo loo nto embiza ngokuba nguDan.', 'XHO22'),
(1, 30, 7, 'Wabuya wamitha uBhiliha, umkhonzazana kaRakeli, wamzalela uYakobi unyana wesibini.', 'XHO22'),
(1, 30, 8, 'Wathi uRakeli, Ndalwa nodade wethu emfazweni enkulu, ndamoyisa, wathi igama layo nguNafetali.', 'XHO22'),
(1, 30, 9, 'Ke kaloku wabona uLeya ukuba uyazala, wathabatha uSilpa, umkhonzazana wakhe, wamnika uYakobi ukuba abe ngumfazi wakhe.', 'XHO22'),
(1, 30, 10, 'uZilipa, umkhonzazana kaLeya, wamzalela uYakobi unyana.', 'XHO22'),
(1, 30, 11, 'Wathi uLeya, Kweza iqela labantu, kwathiwa igama lakhe nguGadi.', 'XHO22'),
(1, 30, 12, 'uZilipa, umkhonzazana kaLeya, wamzalela uYakobi unyana wesibini.', 'XHO22'),
(1, 30, 13, 'Wathi uLeya, Ndinoyolo, ngokuba iintombi zam ziya kuthi ndinoyolo; wamthiya igama elinguAshere.', 'XHO22'),
(1, 30, 14, 'Ngelo xesha uRubhen ngemihla yokuvunwa kwengqolowa wafumana imithuma entsimini wayisa kuLeya unina. Wathi uRakeli kuLeya, Ndinike ama-apile onyana wakho.', 'XHO22'),
(1, 30, 15, 'Wathi kuye, Yinto encinane na ukuyithabatha indoda yam? kwaye nawe ufuna ukususa ama-apile othando kunyana wam? Wathi uRakeli, Kungenxa yoko le nto úya kulala nawe ngobu busuku, ngenxa yama-apile onyana wakho.', 'XHO22'),
(1, 30, 16, 'Wafika uYakobi evela ezindle ngokuhlwa, waphuma uLeya esiya kumkhawulela, wathi, Uze undingene; kuba ndikunyule ngegazi lonyana wam. Walala naye ngobo busuku.', 'XHO22'),
(1, 30, 17, 'uThixo wamphulaphula uLeya, wamitha, wazala unyana wesihlanu kaYakobi.', 'XHO22'),
(1, 30, 18, 'Wathi uLeya, uThixo undinikile umvuzo wam, ngenxa yokuba indoda yam ndiyinike umkhonzazana wam; wamthiya igama elinguIsakare.', 'XHO22'),
(1, 30, 19, 'Wabuya wamitha uLeya, wamzalela uYakobi unyana wesithandathu.', 'XHO22'),
(1, 30, 20, 'Wathi uLeya, uThixo undiphe isipho esilungileyo; ngoku indoda yam iya kuhlala nam, kuba ndiyizalele oonyana abathandathu; wamthiya igama elinguZebhulon.', 'XHO22'),
(1, 30, 21, 'Emva koko wazala intombi waza wayithiya igama elinguDina.', 'XHO22'),
(1, 30, 22, 'uThixo wamkhumbula uRakeli, uThixo wamphulaphula, wasivula isizalo sakhe.', 'XHO22'),
(1, 30, 23, 'Wamitha, wazala unyana; Wathi, uThixo ulisusile ihlazo lam.', 'XHO22'),
(1, 30, 24, 'Wamthiya igama elinguYosefu; Wathi, uYehova makongezelele omnye unyana.', 'XHO22'),
(1, 30, 25, 'Kwathi, akuba ezelwe uYosefu, wathi uYakobi kuLabhan, Ndindulule ndigoduke ezweni lakowethu.', 'XHO22'),
(1, 30, 26, 'Ndinike abafazi bam nabantwana bam endikukhonze ngenxa yabo, ndihambe; ngokuba wena uyayazi inkonzo yam endikukhonze ngayo.', 'XHO22'),
(1, 30, 27, 'Wathi uLabhan kuye, Ukuba ndibonise inceba emehlweni akho, hlala; ngokuba ndiyazi ukuba uYehova undisikelele ngenxa yakho.', 'XHO22'),
(1, 30, 28, 'Wathi, Ndinyulele umvuzo wenu, ndininike wona.', 'XHO22'),
(1, 30, 29, 'Wathi kuye, Uyalwazi nawe uhlobo ebendikukhonze ngalo, nohlobo lokuhlala kwempahla yakho emfutshane kum.', 'XHO22'),
(1, 30, 30, 'Ngokuba ubunayo ndingekafiki yabe imbalwa, ngoku izazekile yaninzi; yaye uYehova ukusikelele ukususela ekufikeni kwam; ngoku ndoyihoya nini indlu yam?', 'XHO22'),
(1, 30, 31, 'Wathi, Ndikunike ntoni na? Wathi uYakobi, Musa ukundinika nento le; ukuba uyathanda ukundenzela oko, ndobuya ndaluse, ndixhele umhlambi wakho.', 'XHO22'),
(1, 30, 32, 'Ndiya kucanda namhlanje phakathi kwemihlambi yakho yonke, ndikhethe khona zonke iinkomo neemazi zonke, neemazi zonke ezimdaka phakathi kweegusha neebhokhwe; kwaye lo uya kuba ngumvuzo wam.', 'XHO22'),
(1, 30, 33, 'Kuba ubulungisa bam buya kundiphendula ngemihla ezayo, xa ndiza kuwufumana umvuzo wam phambi kobuso bakho: onke atyetyisiweyo, namfutshane phakathi kweebhokhwe, naphakathi kweegusha, kuthiwe abiwe kum.', 'XHO22'),
(1, 30, 34, 'Wathi uLabhan, Yabona, makube ngokwelizwi lakho oko.', 'XHO22'),
(1, 30, 35, 'Wazinyula ngaloo mini zonke izikhova ezingwevu nezingwevu, zonke izikhova ezingwevu nezingwevu, nazo zonke ezimhlophe phakathi kwazo, nezona zimfutshane phakathi kwezimvu, wazibeka esandleni soonyana bakhe.', 'XHO22'),
(1, 30, 36, 'Wamisa uhambo lweentsuku ezintathu phakathi kwakhe noYakobi; uYakobi walusa eseleyo ke impahla emfutshane kaLabhan.', 'XHO22'),
(1, 30, 37, 'uYakobi wathabatha iintonga zompopulari omanzi, nezomnquma, nezompleyini; waza wawuzalisa ngeentambo ezimhlophe, waza wabangela ukuba kubonakale ubumhlophe obabukwilaphu.', 'XHO22'),
(1, 30, 38, 'Wazibeka ezityeni zokusela xa impahla emfutshane isiza kusela, phambi kwempahla emfutshane, phambi kwempahla emfutshane, ukuba imithi, ekuseleni kwayo, phambi kwempahla emfutshane.', 'XHO22'),
(1, 30, 39, 'Wamitha umhlambi weenkomo phambi kwesibonda, wazala iimazi zeenkomo ezityebileyo, ezigudileyo, ezigudileyo.', 'XHO22'),
(1, 30, 40, 'Wawahlula uYakobi amatakane lawo, wabukhangelisa ubuso bempahla emfutshane kwenemizila, nemdaka phakathi kwempahla emfutshane kaLabhan; wayibeka ecaleni impahla yakhe emfutshane, akayibeka empahleni emfutshane kaLabhan.', 'XHO22'),
(1, 30, 41, 'Kwathi, xa zimithiyo iimazi ezomeleleyo, uYakobi wabeka imbewu emhlabeni phambi kweemazi ezilapho emijelweni, ukuze zime phakathi kweentsuba.', 'XHO22'),
(1, 30, 42, 'Ukuba imbalwa impahla emfutshane, makangàzisi; Kwaba njalo ke koswele amandla kaLabhan, nakokomelela kukaYakobi.', 'XHO22'),
(1, 30, 43, 'Yanda loo ndoda yanda kunene, yaba nemfuyo eninzi, nabakhonzazana, nabakhonzi, neenkamela, namaesile.', 'XHO22'),
(1, 31, 1, 'Ke kaloku waweva amazwi oonyana bakaLabhan, ababesithi, uYakobi uzithabathile zonke izinto zikabawo; yaye ubufumene kwinto awayeyiyo ubawo.', 'XHO22'),
(1, 31, 2, 'Wabubona uYakobi ubuso bukaLabhan, ukuba busephezu kwakhe njengokwangaphambili.', 'XHO22'),
(1, 31, 3, 'Wathi uYehova kuYakobi, Buyela ezweni looyihlo, nasendlwini yakho; kwaye ndiya kuba nawe.', 'XHO22'),
(1, 31, 4, 'uYakobi wathumela ukuba kubizwe uRakeli noLeya emhlambini wakhe.', 'XHO22'),
(1, 31, 5, 'Wathi kubo, Ndiyabubona ubuso bukayihlo, ukuba akusanjengezolo nezolo elinye kum; kodwa uThixo kabawo wayenam.', 'XHO22'),
(1, 31, 6, 'Niyazi nina, ukuba uyihlo ndimkhonze ngamandla am onke;', 'XHO22'),
(1, 31, 7, 'Uyihlo wandixokisa, wawuguqula kalishumi umvuzo wam; kodwa uThixo akamvumelanga ukuba andiphathe kakubi.', 'XHO22'),
(1, 31, 8, 'Ukuba kuthiwa, Bambalwa abaya kuba ngumvuzo wenu; Yathi yonke imihlambi yeengubo ezirhwexayo; Ukuba uthe, Imivumbo yoba ngumvuzo wenu; ngoku yonke imihlambi inemizila.', 'XHO22'),
(1, 31, 9, 'Wayithabatha ke uThixo imfuyo kayihlo, wandinika.', 'XHO22'),
(1, 31, 10, 'Xa iimazi zeenkomo zamitha, ndawaphakamisa amehlo am ndaza ndakhangela ephupheni, yaye ndabona iinkunzi zeegusha zigxalathelana emhlambini, zikhala, zikhala, yaye zikhala.', 'XHO22'),
(1, 31, 11, 'Sathetha nam isithunywa sikaThixo ephupheni, sathi, Yakobi, ndithe, Ndikho;', 'XHO22'),
(1, 31, 12, 'Wathi, Khawuwamise amehlo akho, ubone; zonke iinkunzi zezimvu ezalusayo zibhityile, zikhala, zincwine; ngokuba ndiyibonile yonke into ayenzayo uLabhan.', 'XHO22'),
(1, 31, 13, 'NdinguThixo waseBheteli, apho walithambisa khona ilitye lesikhumbuzo nalapho wafunga khona kum; yimani ngoku, niphume kweli lizwe, nibuyele ezweni labazalwana benu.', 'XHO22'),
(1, 31, 14, 'Baphendula uRakeli noLeya, bathi kuye, Sisenasabelo nalifa lini na endlwini kabawo?', 'XHO22'),
(1, 31, 15, 'Asingeniswa njengabasemzini na kuye? ngokuba wasithengisa, wayihlutha imali yethu.', 'XHO22'),
(1, 31, 16, 'Kuba bonke ubutyebi awabuthabathayo uThixo kubawo, bube bobethu, noboonyana bethu; yenza ngoku konke uThixo akuthethileyo kuwe.', 'XHO22'),
(1, 31, 17, 'Wesuka uYakobi wabakhwelisa oonyana bakhe, nabafazi bakhe, ezinkameleni;', 'XHO22'),
(1, 31, 18, 'Wathabatha yonke imfuyo yakhe nayo yonke impahla yakhe awayeyifumene, umhlambi wakhe weenkomo awayewufumene ePadan-aram ukuze aye kuIsake uyise ezweni lakwaKanan.', 'XHO22'),
(1, 31, 19, 'uLabhan ubeye kucheba impahla yakhe emfutshane; uRakeli wayiba imilonde-khaya kayise.', 'XHO22'),
(1, 31, 20, 'uYakobi wamfihla uLabhan umAram, engazi, akamxelela ukuba uyabaleka.', 'XHO22'),
(1, 31, 21, 'Wabaleka neento zonke abenazo; wemka, wawuwela umlambo, wabubhekisa ubuso bakhe ezintabeni zaseGiliyadi.', 'XHO22'),
(1, 31, 22, 'Kwathi ngomhla wesithathu, waxelelwa uLabhan ukuba uYakobi ubalekile.', 'XHO22'),
(1, 31, 23, 'Wabathabatha abazalwana bakhe, wamsukela, wahamba uhambo lweentsuku ezisixhenxe; bambamba ezintabeni zaseGiliyadi.', 'XHO22'),
(1, 31, 24, 'Weza uThixo kuLabhan umAram ephupheni ebusuku, wathi kuye, Zilinde, ungathethi nto kuYakobi, nokuba yelungileyo nokuba yembi.', 'XHO22'),
(1, 31, 25, 'uLabhan wadibana noYakobi. uYakobi wayimisa intente yakhe phezu kwentaba, uLabhan nabazalwana bakhe bamisa entabeni yaseGiliyadi.', 'XHO22'),
(1, 31, 26, 'Wathi uLabhan kuYakobi, Wenza ntoni na, uzibambile iintombi zam nje ngekrele?', 'XHO22'),
(1, 31, 27, 'Yini na ukuba ubaleke ngokuzola, uyeba? ubungatshongo kum ukuba ngendikubuyisile ngovuyo nangeengoma, iitamals neziqhithi?', 'XHO22'),
(1, 31, 28, 'Yini na ukuba ungandivumeli ndibange oonyana bam neentombi zam? wenze into yobudenge.', 'XHO22'),
(1, 31, 29, 'Kusemandleni esandla sam ukwenza ububi; ke uThixo kayihlo uthethe nam phezolo, wathi, Zilinde, ungathethi nto kuYakobi, nokuba yelungileyo nokuba yembi.', 'XHO22'),
(1, 31, 30, 'Ngoku ke umke nje, ngokuba wawulangazelela indlu kayihlo, uzibeleni na zona izithixo zam?', 'XHO22'),
(1, 31, 31, 'Waphendula uYakobi wathi kuLabhan, Bendisoyika, ndathi, Hleze uzixhakamfule kum iintombi zakho.', 'XHO22'),
(1, 31, 32, 'Osukuba uzifumana kuye izithixo zakho, makangadli bomi. Kuba uYakobi wayengazi ukuba uRakeli uzibile.', 'XHO22'),
(1, 31, 33, 'Wangena uLabhan ententeni kaYakobi, nasententeni kaLeya, nasententeni yeempelesi zombini; akazifumana. Waphuma ententeni kaLeya, wangena ententeni kaRakeli.', 'XHO22'),
(1, 31, 34, 'uRakeli wayithabatha imifanekiso, wayifaka phakathi kwengubo yenkamela, wahlala phezu kwayo. uLabhan wayiphuthaphutha yonke intente, akayifumana.', 'XHO22'),
(1, 31, 35, 'Wathi kuyise, Mawungabi namsindo, Nkosi yam, kuba ndingenako ukuma phambi kwakho; ngokuba ikum isidima sabafazi. Wagqogqa, akafumana mifanekiso.', 'XHO22'),
(1, 31, 36, 'Wacaphuka uYakobi, wamdubadubeka uLabhan. Waphendula uYakobi wathi kuLabhan, Buyintoni na ubugwenxa bam? Siyintoni na isono sam, ukuba undenze nzima kangaka?', 'XHO22'),
(1, 31, 37, 'Xa ubuzifuna zonke izinto zam, wafumana ntoni na yazo zonke iimpahla zendlu yakho? yibeke apha phambi kwamawethu, ukuze basigwebe sobabini.', 'XHO22'),
(1, 31, 38, 'Ndibe nawe le minyaka imashumi mabini; Impahla yakho emfutshane, neenciniba zakho azidlanga, neenkunzi zempahla yakho emfutshane andizidlanga.', 'XHO22'),
(1, 31, 39, 'Into eqwengiweyo andiyizisanga kuwe, ndayimisela ngokwam; Ndiyithwele ilahleko yayo; uyayifuna esandleni sam, nokuba ibibiwe emini okanye ebusuku.', 'XHO22'),
(1, 31, 40, 'Ndaba njalo; Emini komile kwaye ebusuku kubanda; ubuthongo bam bebungekho emehlweni am.', 'XHO22'),
(1, 31, 41, 'Kwaba minyaka imashumi mabini endlwini yakho; ndakukhonza iminyaka elishumi elinamine ngenxa yeentombi zakho zombini, iminyaka emithandathu ngenxa yemfuyo yakho; uwuguqule kalishumi umvuzo wam.', 'XHO22'),
(1, 31, 42, 'Ukuba uThixo kabawo, uThixo ka-Abraham, Ohlonelwa nguIsake, waba nam, ngendithabathe mna ndedwa. uThixo uzibonile iintsizi zam nomsebenzi wezandla zam, wakukhalimela phezolo.', 'XHO22'),
(1, 31, 43, 'uLabhan wamphendula uYakobi, wathi, Ezi ntombi ziintombi zam, aba oonyana ngoonyana bam, amathokazi lawo ngamathokazi am, neento zonke ozibonayo zezam; Nanamhla oku ndingathini ngezi ntombi zam, nabantwana bazo ezibazeleyo?', 'XHO22'),
(1, 31, 44, 'Khawuze senze umnqophiso, mna nawe; ube lingqina phakathi kwam nawe.', 'XHO22'),
(1, 31, 45, 'uYakobi wathabatha ilitye, waliphakamisa lasisimiso.', 'XHO22'),
(1, 31, 46, 'Wathi uYakobi kubazalwana bakhe, Cholani amatye; bathabatha amatye, benza imfumba; badla khona imfumba.', 'XHO22'),
(1, 31, 47, 'Wathi ukuyibiza uLabhan yiYegare-sahaduta, uYakobi wathi ukuyibiza yiGalet.', 'XHO22'),
(1, 31, 48, 'Wathi uLabhan, Eli themba namhla libubungqina phakathi kwam nawe. Ke ngoko, igama lawo kwathiwa yiGaledi;', 'XHO22'),
(1, 31, 49, 'neMizpa; ngokuba wathi, uYehova ubonisele phakathi kwam nawe, xa singasekhoyo.', 'XHO22'),
(1, 31, 50, 'Ukuba uthe wazicinezela iintombi zam, nokuba uthe wazeka abafazi phezu kweentombi zam, akukho bani unathi. Yabona, uThixo ulingqina phakathi kwam nawe.', 'XHO22'),
(1, 31, 51, 'Wathi uLabhan kuYakobi, Nali ke ithemba, nali ke ilitye lesikhumbuzo endiliqilileyo phakathi kwam nawe;', 'XHO22'),
(1, 31, 52, 'Le ntembeko mayibe bubungqina, kwaye eli litye lesikhumbuzo libe bubungqina, bokuba andiyi kunikela le ntembeko kuni, nokokuba aniyi kuyisusa le nkoloseko neli litye lokundikhumbuza ukuba lenze ububi.', 'XHO22'),
(1, 31, 53, 'Wanga uThixo ka-Abraham, uThixo kaNakore, uThixo woyise wabo, asigwebe. Wafunga uYakobi ngokoyikeka kukayise uIsake.', 'XHO22'),
(1, 31, 54, 'uYakobi wabingelela umbingelelo entabeni apho, wabiza abazalwana bakhe, ukuba beze badle isonka. Badla isonka, balala entabeni apho.', 'XHO22'),
(1, 31, 55, 'Wavuka uLabhan kusasa, wabanga oonyana bakhe, neentombi zakhe, wabasikelela; Wemka ke uLabhan, wabuyela endlwini yakhe.', 'XHO22'),
(1, 32, 1, 'Wemka ke uYakobi, zaqubisana naye izithunywa zikaThixo.', 'XHO22'),
(1, 32, 2, 'Wabona uYakobi, wathi, Ngawo la amatyala kaThixo; wathi igama laloo ndawo yiMahanayim.', 'XHO22'),
(1, 32, 3, 'uYakobi wathuma abathunywa phambi kwakhe kuEsawu, umkhuluwa wakhe, ezweni lakwaSehire, ezweni lakwaEdom.', 'XHO22'),
(1, 32, 4, 'Wabawisela umthetho, wathi, Thethani kuEsawu, nkosi yam; Wathi umkhonzi wakho uYakobi, Bendingumphambukeli kuLabhan, ndahlala khona kwada kwangoku.', 'XHO22'),
(1, 32, 5, 'Ndineenkomo namaesile, nempahla emfutshane, nabakhonzi, nabakhonzazana; ndithumele ukuxelela inkosi yam loo mazwi, ukuze ndibabalwe emehlweni akho.', 'XHO22'),
(1, 32, 6, 'Babuyela abathunywa kuYakobi, bathi, Sifikile kumkhuluwa wakho uEsawu, naye uze kukukhawulela, enamadoda angamakhulu omane.', 'XHO22'),
(1, 32, 7, 'Woyika kunene uYakobi, wawahlula amadoda lawo, nempahla emfutshane, neenkomo, neenkamela;', 'XHO22'),
(1, 32, 8, 'Wathi, Ukuba uEsawu uthe wafika kwenye isihlwele, wasichitha, abanye abantu bosinda.', 'XHO22'),
(1, 32, 9, 'Wathi uYakobi, Thixo kabawo uAbraham, Thixo kabawo uIsake, uYehova uthe kum, Buyela ezweni lakho, nasendlwini yakho, ndikwenzele okulungileyo.', 'XHO22'),
(1, 32, 10, 'Andiyilingene yonke inceba, nenyaniso yonke oyenzileyo kumkhonzi wakho; ngenxa enokuba ndayiwela le Yordan, ndinentonga yam; kwaye ngoku ndingamaqela amabini.', 'XHO22'),
(1, 32, 11, 'Ndisindise esandleni somkhuluwa wam, esandleni sikaEsawu; ngokuba ndiyamoyika, hleze afike andibethe, nomama ndawonye nabantwana.', 'XHO22'),
(1, 32, 12, 'Wathi, Ewe, ndiya kukuphatha kakuhle, imbewu yakho ndiyenze ibe njengentlabathi yolwandle, engenakubalwa ngenxa yenani layo.', 'XHO22'),
(1, 32, 13, 'Walala khona ngobo busuku; wathi ngoonyana bakhe uEsawu wamnika ukuba abe ngumnikelo;', 'XHO22'),
(1, 32, 14, 'namakhulu amabini eebhokhwe, namashumi amabini eebhokhwe, namakhulu amabini eegusha, namashumi amabini eenkunzi zeegusha;', 'XHO22'),
(1, 32, 15, 'iinkamela ezanyayo ezimashumi mathathu, namaxhaka azo, iimazi zeenkomo ezimashumi mane, neenkunzi zeenkomo ezilishumi, namaesile amashumi mabini, namaxhaka alishumi.', 'XHO22'),
(1, 32, 16, 'Wazinikela esandleni sabakhonzi bakhe, umhlambi walowo wamnye; Wathi kubakhonzi bakhe, Phumani phambi kwam, nimise phakathi komhlambi weegusha nempahla emfutshane.', 'XHO22'),
(1, 32, 17, 'Wabawisela umthetho abathetheli, wathi, Xa uEsawu umkhuluwa wam waqubisana nani, wabuza wathi, Ungokabani na? uyaphi? yekabani na le nto iphambi kwakho?', 'XHO22'),
(1, 32, 18, 'uthi, Leyomkhonzi wakho uYakobi; zisisipho esithunyelwe kukumkani wam uEsawu, nanko ke naye ngasemva kwethu.', 'XHO22'),
(1, 32, 19, 'Ngoko wayalela owesibini nowesithathu nabo bonke abo babelandela umhlambi, esithi: “Nitsho kuEsawu nakufumanana naye.', 'XHO22'),
(1, 32, 20, 'Nanko mkhonzi wakho uYakobi engasemva kwethu. Kuba wathi, Mandimcamagushele ngomnikelo osingwayo kuqala, ndize kamva ndibubone ubuso bakhe; mhlawumbi uya kundamkela.', 'XHO22'),
(1, 32, 21, 'Saphuma ke isipho phambi kwakhe, walala phakathi kweqela elo.', 'XHO22'),
(1, 32, 22, 'Wavuka ngobo busuku, wathabatha abafazi bakhe bobabini, nabakhonzazana bakhe bobabini noonyana bakhe abalishumi elinamnye, wayiwela intili yeYabhoko;', 'XHO22'),
(1, 32, 23, 'Wabathabatha, wabaseza umlambo, wazithumela nezo abenazo.', 'XHO22'),
(1, 32, 24, 'uYakobi wasala yedwa; Yazamana indoda naye kwada kwasa.', 'XHO22'),
(1, 32, 25, 'Yabona ukuba ayimeyisi, yachukumisa umlambo womlambo wakhe; Yatshona ingcambu yesinqe sikaYakobi ngokuzamana kwakhe naye.', 'XHO22'),
(1, 32, 26, 'Wathi, Ndilinde, kuba ilanga liyaphuma. Wathi: Andikukhulula, awukandisikelele.', 'XHO22'),
(1, 32, 27, 'Yathi kuye, Ungubani na igama lakho? Wathi, Yakobi!', 'XHO22'),
(1, 32, 28, 'Wathi, Igama lakho akusayi kuba sathiwa nguYakobi;', 'XHO22'),
(1, 32, 29, 'Wabuza uYakobi wathi, Ndixelele igama lakho. Wathi ke yena, Yini na ukuba ulibuze igama lam? wamsikelela khona.', 'XHO22'),
(1, 32, 30, 'uYakobi wathi loo ndawo yiPeniyeli; ngokuba ndibonene noThixo, wahlangulwa umphefumlo wam.', 'XHO22'),
(1, 32, 31, 'Uthe akudlula ePniel, lakhanya ilanga phezu kwakhe, wema esinqeni sakhe.', 'XHO22'),
(1, 32, 32, 'Kungenxa yoko le nto oonyana bakaSirayeli bengasidli isihlunu esisethangeni loMlambo umNayile de kube namhla, ngenxa yokuba wachukumisa ithambo lomlenze kaYakobi emsipheni owomileyo.', 'XHO22'),
(1, 33, 1, 'Wawavala uYakobi amehlo akhe, wabona, nanko uEsawu esiza, enamadoda angamakhulu omane. Wabahlula uLeya noRakeli neempelesi zombini.', 'XHO22'),
(1, 33, 2, 'Wabeka abakhonzazana nabantwana babo ngaphambili, uLeya nabantwana bakhe emva kwakhe, uRakeli noYosefu emva kwakhe.', 'XHO22'),
(1, 33, 3, 'Wabakhokela, waqubuda kasixhenxe, wada waya kumkhuluwa wakhe.', 'XHO22'),
(1, 33, 4, 'Wagidima uEsawu esiya kumkhawulela, wamwola, wawa entanyeni yakhe, wamanga; balila.', 'XHO22'),
(1, 33, 5, 'Wawaphakamisa amehlo akhe, wabona abafazi nabantwana; Athi: Ngubani na lo unawe? Wathi, Ngabantwana, athe uThixo wabanika umkhonzi wakho ngenceba.', 'XHO22'),
(1, 33, 6, 'Zafika iimpelesi, zona nabantwana bazo, zaqubuda.', 'XHO22'),
(1, 33, 7, 'Wasondela uLeya nabantwana bakhe, baqubuda; emva koko basondela uYosefu noRakeli, baqubuda.', 'XHO22'),
(1, 33, 8, 'Wathi, Iyintoni na yonke le mihlambi endiyikhawulelayo? Wathi, Mabababalwe emehlweni okumkani wam.', 'XHO22'),
(1, 33, 9, 'Wathi uEsawu, Ndanele, mninawa; gcina oko unako.', 'XHO22'),
(1, 33, 10, 'Wathi uYakobi, Hayi; kuba ngenxa yoko ndibubone ubuso bakho, ngathi ndiyabubona ubuso bukaThixo, ukanti uyandicekisa.', 'XHO22'),
(1, 33, 11, 'Thabatha intsikelelo yam ezisiweyo kuwe; ngokuba uThixo unenceba kum, ndonele. Wamjoka, wavuma.', 'XHO22'),
(1, 33, 12, 'Wathi, Masihambe, sihambe, ndonikhokelela kwangoku.', 'XHO22'),
(1, 33, 13, 'Wathi ke kuye, iNkosi yam iyazi ukuba abantwana bathantamisa, nokuba ndinemihlambi yempahla emfutshane, neyeenkomo, neyegusha;', 'XHO22'),
(1, 33, 14, 'Inkosi yam mayiwele phambi komkhonzi wayo, yaye mna ndohamba kancinci njengomhlambi owela phambi kwam, yaye ndiya kubathwala abantwana ndide ndifike enkosini yam uSehire.', 'XHO22'),
(1, 33, 15, 'Wathi uEsawu, Makhe ndiyishiye inxenye kwaba bantu ndinawe. Wathi: Ifuna ntoni? Mandibabalwe emehlweni enkosi yam.', 'XHO22'),
(1, 33, 16, 'Wabuya ngaloo mini uEsawu ngendlela yakhe, esiya kwaSehire.', 'XHO22'),
(1, 33, 17, 'uYakobi wafuduka, waya eSukoti, wazakhela indlu, iinkomo zakhe wazibiyela iintlantsi; ngenxa yoko kwathiwa igama laloo ndawo yiSukoti.', 'XHO22'),
(1, 33, 18, 'Wafika uYakobi eSalem, emzini kaShekem, ezweni lakwaKanan, ekuveleni kwakhe ePadan-aram; wayimisa intente yakhe kufuphi nesixeko.', 'XHO22'),
(1, 33, 19, 'Walithenga ilizwe abemise kulo intente yakhe, koonyana bakaHamore, uyise kaShekem, ngekhulu lemali.', 'XHO22'),
(1, 33, 20, 'Wamisa khona isibingelelo, wathi igama laso nguElohe-Israyeli.', 'XHO22'),
(1, 34, 1, 'Ke kaloku uDina, intombi kaLeya, leyo wayizalela uYakobi, waphuma waya kubona iintombi zelo zwe.', 'XHO22'),
(1, 34, 2, 'uShekem, unyana kaHamore, uMowabhi, ukumkani welizwe, wambona, wamthabatha, walala naye, wamenza inqambi.', 'XHO22'),
(1, 34, 3, 'Intliziyo yakhe yanamathela kuDina, intombi kaYakobi, wayithanda intombi leyo, wathetha kakuhle nentombi leyo.', 'XHO22'),
(1, 34, 4, 'Wathetha uShekem kuHamore uyise, wathi, Ndinike le ntombi, ibe ngumkam.', 'XHO22'),
(1, 34, 5, 'Weva uYakobi ukuba umenze inqambi uDina, intombi yakhe. Oonyana bakhe babe besendle, besemhlambini wakhe; wathi cwaka ke uYakobi, bada bafika.', 'XHO22'),
(1, 34, 6, 'Waphuma uHamore, uyise kaShekem, waya kuthetha noYakobi.', 'XHO22'),
(1, 34, 7, 'Bathi bakufika oonyana bakaYakobi ezweni, bakukuva oku, aba buhlungu amadoda lawo, aba nomsindo kakhulu, kuba wenza ubudenge kwaSirayeli ngokulala nentombi kaYakobi; into engafanele ukwenziwa.', 'XHO22'),
(1, 34, 8, 'Wathetha uHamore kubo, wathi, uShekem, unyana wam, intliziyo yonyana wam, iyayikhumbula intombi yenu;', 'XHO22'),
(1, 34, 9, 'Nasithatha, nasinika iintombi zenu, nazeka iintombi zethu;', 'XHO22'),
(1, 34, 10, 'Niya kuhlala nathi, ilizwe libe ngasecaleni kwenu; qhubeka urhweba ngayo, tyeba kuyo.', 'XHO22'),
(1, 34, 11, 'Wathi uShekem kuyise wenkazana leyo, nakoodade bayo, Mandibabalwe emehlweni enu, nento enithethayo kum, ndoninika yona.', 'XHO22'),
(1, 34, 12, 'Ungaze ucele khazi kum, nesipho esinje, ndokunika njengoko uthethayo kum; kodwa ndinike intombi ibe ngumfazi wam.', 'XHO22'),
(1, 34, 13, 'Oonyana bakaYakobi babaxokisa ooShekem noHamore uyise, bathi, ngokuba ebemenze inqambi uDina, udade wabo.', 'XHO22'),
(1, 34, 14, 'Bathi kubo, Asiyi kuyenza loo nto, yokuba udade wethu simnike indoda engalukanga; ngokuba ibilihlazo kuthi.', 'XHO22'),
(1, 34, 15, 'Sovumelana nani kule ndawo yokuba nithanda ukufana nathi, ngokuthi yaluke kuni yonke into eyindoda;', 'XHO22'),
(1, 34, 16, 'Soninika ke iintombi zethu, sizeke iintombi zenu, sihlale nani, sibe luhlanga lunye.', 'XHO22'),
(1, 34, 17, 'Ukuba anisiva ukuba naluke; soyithabatha khona intombi yethu, sihambe.', 'XHO22'),
(1, 34, 18, 'Alunga amazwi abo emehlweni kaHamore, nasemehlweni kaShekem, unyana kaHamore.', 'XHO22'),
(1, 34, 19, 'Akalibala umfana ukuyenza loo nto, uba ubethabathekile yintombi kaYakobi; ibizukile yena ngaphezu kwayo yonke indlu kayise.', 'XHO22'),
(1, 34, 20, 'Bafika uHamore noShekem unyana wakhe, esangweni lesixeko sabo, bathetha kumadoda omzi wabo, bathi,', 'XHO22'),
(1, 34, 21, 'Aba bathanda uxolo nathi; ke ngoko, mabahlale kwelo lizwe baze barhwebe khona; ngokuba ihlabathi, yabonani, bayalingana; sozizeka iintombi zawo zibe ngabafazi bethu, siwanike iintombi zethu.', 'XHO22'),
(1, 34, 22, 'Ke ngenxa yoko aya kuvumelana nathi la madoda, ukuhlala nathi, sibe luhlanga lunye, sithi, sonke ngabanye aluke, njengokuba alukile wona.', 'XHO22'),
(1, 34, 23, 'Imfuyo yawo nempahla yawo nayo yonke imfuyo yawo ayisayi kuba yeyethu na? masivumelane nabo, kwaye baya kuhlala nathi.', 'XHO22'),
(1, 34, 24, 'Bamphulaphula uHamore noShekem unyana wakhe, bonke abaphuma ngesango lomzi wakhe; yaye onke amadoda akhalinyelwa, bonke ababephuma ngesango lesixeko sakowabo.', 'XHO22'),
(1, 34, 25, 'Kwathi ngomhla wesithathu, bakuba bebandezelekile, oonyana ababini bakaYakobi, uSimon noLevi, abanakwabo boDina, bathabatha walowo ikrele lakhe, behla baya kuloo mzi ngokungafihlisiyo, bawabulala onke amadoda.', 'XHO22'),
(1, 34, 26, 'Bambulala ke uHamore, noShekem unyana wakhe, ngohlangothi lwekrele, bamthabatha uDina endlwini kaShekem, bemka.', 'XHO22'),
(1, 34, 27, 'Oonyana bakaYakobi beza kwababuleweyo, bawuphanga umzi, ngokuba bebemenze inqambi udade wabo.', 'XHO22'),
(1, 34, 28, 'Impahla yabo emfutshane, neenkomo zabo, namaesile abo, nabebekukho phakathi komzi, nabasemaphandleni, bazithabatha;', 'XHO22'),
(1, 34, 29, 'Bathabatha bonke ubutyebi babo, nabantwana babo bonke, nabafazi babo, baphanga neento zonke ebe zisendlwini.', 'XHO22'),
(1, 34, 30, 'Wathi uYakobi kuSimon noLevi, Nindisongela, ukuba nindonakalise phakathi kwabemi beli lizwe, phakathi kwamaKanan namaPersi; Ndimncinane ke ngenani, baya kundihlanganyela, bandibulale; yaye ndiya kutshabalala, mna nendlu yam.', 'XHO22'),
(1, 34, 31, 'Bathi bona, Udade wethu yihenyukazi na?', 'XHO22'),
(1, 35, 1, 'Wathi uThixo kuYakobi, Hamba unyuke uye eBheteli, uhlale khona, umkhele khona isibingelelo uThixo, owazibonakalisayo kuwe ekubalekeni kwakho kuEsawu, umkhuluwa wakho.', 'XHO22'),
(1, 35, 2, 'Wathi ke uYakobi kwindlu yakhe, nakubo bonke ababenaye, Susani izithixo zasemzini eziphakathi kwenu; zihlambulukeni, ninxibe iingubo zenu.', 'XHO22'),
(1, 35, 3, 'Masisuke, sinyuke siye eBheteli; ndenzele khona isibingelelo kuThixo, owandiphendula ngemini yokubandezelwa kwam, owayenam endleleni endandihamba ngayo.', 'XHO22'),
(1, 35, 4, 'Bazinika uYakobi zonke izithixo zasemzini ezandleni zabo, namajikazi ezindlebeni zabo; waza uYakobi wazifihla phantsi komthi wesikamore owawukwaShekem.', 'XHO22'),
(1, 35, 5, 'Anduluka, yaye ukoyika uThixo kwabakho kwimizi ngeenxa zonke kubo, ayabasukela oonyana bakaYakobi.', 'XHO22'),
(1, 35, 6, 'Wafika uYakobi eLuzi, esezweni lakwaKanan, oko kukuthi, iBheteli, nabantu bonke abenabo.', 'XHO22'),
(1, 35, 7, 'Wakha khona isibingelelo, wathi igama laloo ndawo yiElibhetele;', 'XHO22'),
(1, 35, 8, 'Wafa ke uDebhora, umanyisikazi kaRebheka, wangcwatyelwa phantsi kweBheteli, phantsi kom-oki, kwathiwa igama layo nguAlonbhakuti.', 'XHO22'),
(1, 35, 9, 'uThixo wabuya wabonakala kuYakobi ekuveleni kwakhe ePadan-aram, wamsikelela.', 'XHO22'),
(1, 35, 10, 'Wathi uThixo kuye, Igama lakho nguYakobi; akusayi kuba sathiwa igama lakho nguYakobi, kuya kuthiwa igama lakho nguSirayeli; wamthiya igama elinguSirayeli.', 'XHO22'),
(1, 35, 11, 'Wathi uThixo kuye, NdinguThixo, uSomandla; nande nande; kuya kuphuma kuwe iintlanga neentlanga, nookumkani baphume esinqeni sakho;', 'XHO22'),
(1, 35, 12, 'Ilizwe endalinika uAbraham noIsake ndiza kulinika wena, ndilinike imbewu yakho emva kwakho.', 'XHO22'),
(1, 35, 13, 'Wenyuka uThixo, wemka kuye, waya kuloo ndawo abethethe naye kuyo;', 'XHO22'),
(1, 35, 14, 'uYakobi wamisa ilitye lesikhumbuzo kuloo ndawo abethethe naye kuyo, ilitye lesikhumbuzo; wagalela phezu kwayo umnikelo othululwayo, wagalela neoli phezu kwayo.', 'XHO22'),
(1, 35, 15, 'uYakobi wathi igama laloo ndawo, uThixo abethethe naye kuyo, yiBheteli.', 'XHO22'),
(1, 35, 16, 'Banduluka eBheteli; yaye kwakusemganyana ukuba sifike e-Efrata, yaye uRakeli waba sentlungwini yokuzala, wazala kakhulu.', 'XHO22'),
(1, 35, 17, 'Kwathi, kwaba kuzala kwakhe, wathi umzalisikazi kuye, Musa ukoyika; nawe uya kuba naye lo nyana.', 'XHO22'),
(1, 35, 18, 'Waphuma umoya wakhe, akuba efile, wathi nguBhenoni igama lakhe; ke yena uyise wathi nguBhenjamin.', 'XHO22'),
(1, 35, 19, 'Wafa ke uRakeli, wangcwatyelwa endleleni yomEfrati, eyiBhetelehem ke leyo.', 'XHO22'),
(1, 35, 20, 'uYakobi wamisa ilitye lesikhumbuzo phezu kwengcwaba lakhe; lilitye lesikhumbuzo lengcwaba likaRakeli unanamhla.', 'XHO22'),
(1, 35, 21, 'Wemka ke uSirayeli, wayimisa intente yakhe malunga nenqaba ende yakwa-Edare.', 'XHO22'),
(1, 35, 22, 'Akuba ehleli kwelo zwe uSirayeli, uRubhen walala noBhiliha, ishweshwe likayise, weva uSirayeli. Oonyana bakaYakobi babelishumi elinababini;', 'XHO22'),
(1, 35, 23, 'Oonyana bakaLeya; noRubhen amazibulo kaYakobi, noSimon, noLevi, noYuda, noIsakare, noZebhulon;', 'XHO22'),
(1, 35, 24, 'Oonyana bakaRakeli nguAsheri; uYosefu noBhenjamin:', 'XHO22'),
(1, 35, 25, 'Oonyana bakaBhiliha, umkhonzazana kaRakeli; uDan noNafetali:', 'XHO22'),
(1, 35, 26, 'noonyana bakaZilipa, umkhonzazana kaLeya; nguGadi noAshere: Ngabo abo oonyana bakaYakobi awabazalelwa ePadan-aram.', 'XHO22'),
(1, 35, 27, 'uYakobi waya kuIsake uyise, uMamre, eKiriyati ka-Arbha, eHebron, apho babehleli khona uAbraham noIsake.', 'XHO22'),
(1, 35, 28, 'Imihla kaIsake yaba yiminyaka elikhulu elinamanci asibhozo.', 'XHO22'),
(1, 35, 29, 'Wafa ke uIsake, wasiwa ebantwini bakowabo, eseleluphele eselekhulile, bamngcwaba oonyana bakhe uEsawu noYakobi.', 'XHO22'),
(1, 36, 1, 'Zizo ezi izizwe zakwaEsawu, nguEdom ke lowo.', 'XHO22'),
(1, 36, 2, 'uEsawu wabazeka abafazi bakhe ezintombini zakwaKanan; uAda, intombi kaElon umHeti, noAholibhama, intombi ka-Ana, intombi kaTsibheyon umHeti;', 'XHO22'),
(1, 36, 3, 'noBhasemati, intombi kaIshmayeli, udade boNebhayoti.', 'XHO22'),
(1, 36, 4, 'uAda wazala uEsawu uElifazi; uBhasemati wazala uRehuweli;', 'XHO22'),
(1, 36, 5, 'uOholibhama wazala uYehushe, noYalam, noKora; Ngabo abo oonyana bakaEsawu, awabazalelwayo ezweni lakwaKanan.', 'XHO22'),
(1, 36, 6, 'uEsawu wabathabatha abafazi bakhe, noonyana bakhe, neentombi zakhe, nabantu bonke bendlu yakhe, nemfuyo yakhe, nemfuyo yakhe yonke, nengqwebo yakhe yonke awayifumanayo ezweni lakwaKanan; yaye wangena ehlabathini phambi kobuso bukaYakobi umntakwabo.', 'XHO22'),
(1, 36, 7, 'Kuba buninzi ubutyebi babo, kunokuba babe ngahlali ndawonye; yaye ilizwe ababekulo lalingenakulithwala ngenxa yemfuyo yabo.', 'XHO22'),
(1, 36, 8, 'Wahlala uEsawu entabeni yakwaSehire: uEsawu nguEdom.', 'XHO22'),
(1, 36, 9, 'Yiyo le ke inzala kaEsawu, uyise wamaEdom, ezintabeni zakwaSehire.', 'XHO22'),
(1, 36, 10, 'Ngawo la amagama oonyana bakaEsawu: nguEsahim; uElifazi, unyana ka-Ada, umkaEsawu, uRehuweli, unyana kaBhashmati, umkaEsawu.', 'XHO22'),
(1, 36, 11, 'Oonyana bakaElifazi nguTeman, no-Omare, noSefo, noGatam, noKenazi.', 'XHO22'),
(1, 36, 12, 'uTimna waba sisithende sikaElifazi, unyana kaEsawu; wazala uElifazi, uAmaleki. Ngabo abo oonyana baka-Ada, umkaEsawu.', 'XHO22'),
(1, 36, 13, 'Ngabo abo oonyana bakaRehuweli; nguNahati noSeraki, nguShama noMisa; Ngabo abo oonyana bakaBhashmati, umkaEsawu.', 'XHO22'),
(1, 36, 14, 'Ngabo abo oonyana baka-Aholibhama, intombi ka-Ana, intombi kaTsibheyon, umkaEsawu; wazala uEsawu, noYehus, noYalam, noKora.', 'XHO22'),
(1, 36, 15, 'Zizo ezi iinkosana zoonyana bakaEsawu: Oonyana bakaElifazi, amazibulo kaEsawu, ngunyana wakhe; uDuke uTeman, uDuke uOmar, uDuke uZepho, uDuke Kenaz,', 'XHO22'),
(1, 36, 16, 'Inganga uKora, isikhulu esinguGatam, inganga yama-Amaleki; ngabo abo abathetheli abavela kwaElifazi ezweni lakwaEdom; Ngabo abo oonyana baka-Ada.', 'XHO22'),
(1, 36, 17, 'Ngabo abo oonyana bakaRehuweli, unyana kaEsawu; ukumkani uNahati, isikhulu uSeraki, isikhulu esinguShama, isikhulu uMisa; Ngabo abo oonyana bakaBhashmati, umkaEsawu.', 'XHO22'),
(1, 36, 18, 'Ngabo abo oonyana baka-Aholibhama, umkaEsawu; inkosana uYeuse, inkosana uYalam, inkosana uKora; zizo ezo izikhulu zika-Oholibhama, intombi ka-Ana, umkaEsawu.', 'XHO22'),
(1, 36, 19, 'Ngabo abo oonyana bakaEsawu, nguEdom ke lowo, neenkosana zabo ke ezo.', 'XHO22'),
(1, 36, 20, 'Ngabo abo oonyana bakaSehire, uMahore, ababemi kwelo zwe; uLotan, noShobhali, noTsibheyon, noAna;', 'XHO22'),
(1, 36, 21, 'nguDishon, noEzere, noDishan: zizo ezo izikhulu zamaHori, oonyana bakaSehire, ezweni lakwaEdom.', 'XHO22'),
(1, 36, 22, 'Oonyana bakaLotan nguHori noHemam; yaye udade boLotan yayinguTimna.', 'XHO22'),
(1, 36, 23, 'Ngabo abo oonyana bakaShobale; noAlvane, noManahati, noEbhali, noTsefo, no-Onam.', 'XHO22'),
(1, 36, 24, 'Ngabo abo oonyana bakaTsibheyon; nguAya noAna: nguAna wafumanana noondlebende entlango, xa ebesalusa amaesile kaTsibheyon uyise;', 'XHO22'),
(1, 36, 25, 'Ngabo abo oonyana baka-Ana; uDishon no-Aholibhama intombi ka-Ana.', 'XHO22'),
(1, 36, 26, 'Ngabo abo oonyana bakaDishon; iHemdan kunye ne-Esbane kunye neJitran neKerane.', 'XHO22'),
(1, 36, 27, 'Ngabo abo oonyana bakaEzere; uBilhan kunye noSaavan kunye noAkan.', 'XHO22'),
(1, 36, 28, 'Ngabo abo oonyana bakaDisane; Utse kunye noAran.', 'XHO22'),
(1, 36, 29, 'Zizo ezo izikhulu zamaHori; noLotan, isikhulu esinguShobhali, isikhulu esinguSibheyon, nesikhulu uAna;', 'XHO22'),
(1, 36, 30, 'inkulu yakwaDishon, inkulu yakwaEzere, inkulu yakwaDishan; zizo ezo izikhulu zaseHore, nabathetheli bazo ezweni lakwaSehire.', 'XHO22'),
(1, 36, 31, 'Ngabo aba ookumkani ababelawula ezweni lakwaEdom, kungekabikho oonyana bakaSirayeli.', 'XHO22'),
(1, 36, 32, 'uBhela, unyana kaBhehore, waye walawula kwaEdom, yaye igama lomzi wakhe laliyiDinabha.', 'XHO22'),
(1, 36, 33, 'Wafa uBhela, wathi uYobhabhi, unyana kaSeraki, waseBhotsera, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(1, 36, 34, 'Wafa uYobhabhi, wathi uHusam, wasezweni lakwaTeman, walawula esikhundleni sakhe.', 'XHO22'),
(1, 36, 35, 'Wafa uHusam, wathi uHadade, unyana kaBhedadi, owawabulalayo amaMidiyan ezweni lakwaMowabhi, walawula esikhundleni sakhe; yaye igama lomzi wakhe laliyiAwiti.', 'XHO22'),
(1, 36, 36, 'Wafa uHadade, kwalawula uSamla waseMasereka esikhundleni sakhe.', 'XHO22'),
(1, 36, 37, 'Wafa uSamla, wathi uSawule, waseRehobhoti yoMlambo, walawula esikhundleni sakhe.', 'XHO22'),
(1, 36, 38, 'Wafa uSawule, kwalawula uBhahali-hanan, unyana ka-Akebhore, esikhundleni sakhe.', 'XHO22'),
(1, 36, 39, 'Wafa uBhahali-hanan, unyana ka-Akebhore, wathi uHadare walawula esikhundleni sakhe; Igama lomzi wakhe yiPawu; Igama lomkakhe lalinguMehetabheli, intombi kaMatrede, intombi kaMeshahabhi.', 'XHO22'),
(1, 36, 40, 'Ngawo lawo amagama ezikhulu ezaphuma kuEsawu ngokwezindlu zazo, nangeendawo zazo, nangamagama azo: inkosana uTimna, inkosana uAlva, inkosana uJetet,', 'XHO22'),
(1, 36, 41, 'iNkosana u-Oholibhama, noEla, neNkosana uPinone,', 'XHO22'),
(1, 36, 42, 'neNkosana uKenazi, neNkosana uTemane, neNkosana uMibhetsare;', 'XHO22'),
(1, 36, 43, 'Inkosi yaseMagadiyeli, iNkosana uIram. Zizo ezo izikhulu zakwaEdom, ngokweendawo zazo zokuhlala ezweni lelifa lazo: nguEsawu ke uyise wamaEdom.', 'XHO22'),
(1, 37, 1, 'uYakobi wayehlala eKanana kwelo lizwe wayemi kulo uyise.', 'XHO22'),
(1, 37, 2, 'Yiyo le ke inzala kaYakobi. uYosefu, ominyaka ilishumi linesixhenxe ezelwe, wayesalusa impahla emfutshane ndawonye nabakhuluwa bakhe; Unyana wayenoonyana bakaBhiliha, noonyana bakaZilipa, abafazi bakayise; uYosefu wambikela uyise udaba olubi.', 'XHO22'),
(1, 37, 3, 'uSirayeli wayemthanda uYosefu ngaphezu kwabo bonke oonyana bakhe, ngokuba ebengunyana ngeminyaka yakhe; wamenzela ingubo eqaqambileyo;', 'XHO22'),
(1, 37, 4, 'Xa abantakwabo babona ukuba uyise wayemthanda ngaphezu kwabo bonke abantakwabo, bamthiya baza ababa nakuthetha naye ngoxolo.', 'XHO22'),
(1, 37, 5, 'uYosefu waphupha iphupha, wabaxelela abakhuluwa bakhe, bamthiya ngakumbi.', 'XHO22'),
(1, 37, 6, 'Wathi kubo, Liveni iphupha endiliphuphileyo.', 'XHO22'),
(1, 37, 7, 'Yabona, besithunga izithungu entsimini, sabona isithungu sam sema bhuxe; yabona, abaninzi kuni bema ngeenxa zonke, baqubuda phambi kweqashiso lam.', 'XHO22'),
(1, 37, 8, 'Bathi abazalwana bakhe kuye, Yinene na ukuba uya kusilawula? Okanye uya kusilawula ngenene? Kwaba kukhona bamthiyayo ngenxa yamaphupha akhe nangenxa yamazwi akhe.', 'XHO22'),
(1, 37, 9, 'Waphupha elinye iphupha, wabaxelela abakhuluwa bakhe, wathi, Yabonani, ndiphuphe elinye iphupha; nalo ilanga nenyanga neenkwenkwezi ezilishumi elinanye ziguqe phambi kwam.', 'XHO22'),
(1, 37, 10, 'Wakuxela kuyise nabazalwana bakhe oko; uyise wamkhalimela, wathi kuye, Liphupha lini na eli uliphuphileyo? Ngaba ngokwenene mna nonyoko nabantakwenu siza kuqubuda kuwe?', 'XHO22'),
(1, 37, 11, 'Abakhuluwa bakhe bammonela ke; kodwa uyise waligcina elo lizwi.', 'XHO22'),
(1, 37, 12, 'Abakhuluwa bakhe babesalusa impahla emfutshane kaShekem uyise.', 'XHO22'),
(1, 37, 13, 'Wathi uSirayeli kuYosefu, Benditshilo na ukuthi, Abakhuluwa bakho bayigcinile yakwaShekem? yiza, ndikuthume kubo. Wathi ke yena kuye, Ndilapha.', 'XHO22'),
(1, 37, 14, 'Wathi kuye, Hamba uye kukhangela ukuphila kwabakhuluwa bakho nempahla emfutshane; ubuye undizisele ilizwi. Wamthuma ke, emsusa entilini yaseHebron, wafika kwaShekem.', 'XHO22'),
(1, 37, 15, 'Wafunyanwa ngumfo, ebhadula emaphandleni; Indoda leyo yambuza yathi, Ufuna ntoni na?', 'XHO22'),
(1, 37, 16, 'Wathi, Ndifuna abakhuluwa bam; ndixelele apho balusela khona imihlambi yabo.', 'XHO22'),
(1, 37, 17, 'Wathi umfo, Bandulukile apha; kuba ndeva besithi, Masiye eDotan. uYosefu wabalandela abakhuluwa bakhe, wabafumana eDotan.', 'XHO22'),
(1, 37, 18, 'Bakumbona ke esekude, engekasondeli kubo, babhunga ukumbulala.', 'XHO22'),
(1, 37, 19, 'Bathetha omnye komnye besithi, Nanko umphuphi esiza.', 'XHO22'),
(1, 37, 20, 'Yizani ke, simbulale, simphose komnye umhadi, sithi, Udé elinye irhamncwa, sibone ukuba aya kwenzeka ntoni na amaphupha akhe.', 'XHO22'),
(1, 37, 21, 'Weva uRubhen, wamsindisa esandleni sabo; bathi, Masingambulali.', 'XHO22'),
(1, 37, 22, 'Wathi uRubhen kubo, Ize ningaphalazi gazi; ukuze amhlangule esandleni sabo, ambuyisele kuyise.', 'XHO22'),
(1, 37, 23, 'Akufika uYosefu kubakhuluwa bakhe, bamthabatha uYosefu ingubo yakhe yangaphezulu, ingubo ende enemikhono abeyimbethe;', 'XHO22'),
(1, 37, 24, 'Bamthabatha ke, bamjulela equleni;', 'XHO22'),
(1, 37, 25, 'Bahlala phantsi ukuze badle isonka, yaye baphakamisa amehlo abo, bakhangela, yaye, khangela! lifika iqela lamaIshmayeli livela eGiliyadi neenkamela zalo zithwele intlaka emhlophe, ibhalsam nemore, lisendleleni yokuzisa eYiputa.', 'XHO22'),
(1, 37, 26, 'Wathi uYuda kubazalwana bakhe, Yongeza ntoni na, ukuba simbulele umninawa wethu, sifihle igazi lakhe?', 'XHO22'),
(1, 37, 27, 'Yizani sithengise kuye ngamaIshmayeli, mazingachasana naye izandla zethu; ngokuba ngumzalwana wethu, nenyama yethu. Nabantakwabo banelisekile.', 'XHO22'),
(1, 37, 28, 'Badlula abarhwebi bamaMidiyan; bamkhupha uYosefu emhadini, bamnyusa uYosefu, bathengisa ngaye kumaIshmayeli ngeeshekele ezimashumi mabini zesilivere; bamsa uYosefu eYiputa.', 'XHO22'),
(1, 37, 29, 'Wabuyela emhadini uRubhen; nanko uYosefu engekho ngasequleni; wazikrazula iingubo zakhe.', 'XHO22'),
(1, 37, 30, 'Wabuyela kubaninawa bakhe, wathi, Umfana akakho; Mna ke ndosuka ndiye phi na?', 'XHO22'),
(1, 37, 31, 'Bayithabatha ingubo kaYosefu, baxhela itakane lebhokhwe, bayithi nkxu ingubo egazini;', 'XHO22'),
(1, 37, 32, 'Bathumela ingubo ende enemikhono, bayizisa kuyise; bathi, Siyifumene; Yazi ngoku ukuba yingubo yonyana wakho, asiyiyo, kusini na.', 'XHO22'),
(1, 37, 33, 'Wazi, wathi, Le ngubo yeyonyana wam; udliwe lirhamncwa; Ngokungathandabuzekiyo, uYosefu waqwengwa.', 'XHO22'),
(1, 37, 34, 'uYakobi wazikrazula iingubo zakhe, wabopha ezirhwexayo esinqeni sakhe, wamlilela unyana wakhe imihla emininzi.', 'XHO22'),
(1, 37, 35, 'Besuka besuka bonke oonyana bakhe nazo zonke iintombi zakhe, beza kumthuthuzela; akavuma ukuthuthuzeleka; Wathi: Ndiya kuhla ndiye kwelabafileyo, ndilile unyana wam. Ngoko uyise wamlilela.', 'XHO22'),
(1, 37, 36, 'AmaMidiyan athengisa ngaye eYiputa kuPotifare, umthetheli-waka kaFaro, umthetheli wabasiki.', 'XHO22'),
(1, 38, 1, 'Ngelo xesha uYuda wabashiya abazalwana bakhe, wahamba nenye indoda yaseAdulam, egama linguHira.', 'XHO22'),
(1, 38, 2, 'uYuda wabona khona intombi yendoda engumKanan, egama linguShuwa; Wamthatha wamngena.', 'XHO22'),
(1, 38, 3, 'Wamitha, wazala unyana; Wathi igama lakhe nguEre.', 'XHO22'),
(1, 38, 4, 'Wabuya wamitha, wazala unyana; Wathi igama lakhe nguOnan.', 'XHO22'),
(1, 38, 5, 'Wabuya wamitha, wazala unyana; Wamthiya igama elinguSela, waye eseKesibe ukumzala kwakhe.', 'XHO22'),
(1, 38, 6, 'uYuda wamzeka uEre, izibulo lakhe, intombi egama linguTamare;', 'XHO22'),
(1, 38, 7, 'uEre, owamazibulo kaYuda, ebengumntu mbi emehlweni kaYehova; uYehova wambulala.', 'XHO22'),
(1, 38, 8, 'Wathi uYuda kuOnan, Yiya nomfazi womntakwenu, umngene, umzalele umntakwenu abantwana.', 'XHO22'),
(1, 38, 9, 'Wayesazi uOnan ukuba imbewu ayiyi kuba yeyakhe; wathi akungena kumfazi womninawa wakhe, wazikhahlela emhlabeni, ukuba angamniki mbewu umkhuluwa wakhe.', 'XHO22'),
(1, 38, 10, 'Loo nto wayenzayo yayimbi emehlweni kaYehova; ngoko ke, naye wambulala.', 'XHO22'),
(1, 38, 11, 'Wathi uYuda kuTamare umolokazana wakhe, Hlala ungumhlolokazi endlwini kayihlo, ade akhule uSela, unyana wam; ngokuba ethe, ukuze naye angafi njengabakhuluwa bakhe. uTamare waya kuhlala endlwini kayise.', 'XHO22'),
(1, 38, 12, 'Ekugqibeleni yafa intombi kaShuwa, umkaYuda; yaye uYuda wathuthuzeleka waza wenyuka waya kubachebi bezimvu zakhe eTimna, yena nomhlobo wakhe uHira waseAdulam.', 'XHO22'),
(1, 38, 13, 'Waxelelwa uTamare, kwathiwa, Nanko unyokozala, enyuka esiya kucheba impahla yakhe emfutshane kwaTimna.', 'XHO22'),
(1, 38, 14, 'Wazikhulula ke iingubo zobuhlolokazi bakhe, wazigqubuthela ngesigqubuthelo, wazithi wambu, wahlala ebaleni endleleni eya kwaTimna; ngokuba ebebona ukuba uSela ukhulile, akanikelwa kuye ukuba abe ngumkakhe.', 'XHO22'),
(1, 38, 15, 'uYuda wambona, waba ulihenyukazi; ngokuba ebegqubuthele ubuso.', 'XHO22'),
(1, 38, 16, 'Wathi gu bucala, waya kuye endleleni, wathi, Hamba, ndiya kuhamba nawe; (ngokuba ebengazi ukuba ngumolokazana wakhe.) Wathi, Wondinika ntoni na, ukuze ungene nam?', 'XHO22'),
(1, 38, 17, 'Wathi, Mna ndothumela ndithabathe itakane lebhokwe elisempahleni emfutshane. Wathi: Ngaba uya kundinika isiqinisekiso de usithumele?', 'XHO22'),
(1, 38, 18, 'Wathi, Ndikunike sibambiso sini na? Wathi, Umsesane wakho wokutywina, nezacholo zakho, nomsimelelo wakho, zisesandleni sakho. Wamnika ke, wangena kuye, wakhawula ngaye.', 'XHO22'),
(1, 38, 19, 'Wemka, wahamba, wakhulula isigqubuthelo sakhe, wambatha iingubo zobuhlolokazi bakhe.', 'XHO22'),
(1, 38, 20, 'uYuda wathumela itakane lebhokhwe ngesandla somhlobo wakhe waseAdulam, ukuze athabathe isibambiso sakhe esandleni saloo mfazi, kodwa akamfumananga.', 'XHO22'),
(1, 38, 21, 'Sabuza kubantu baloo mmandla, sathi: “Liphi na ing’awukazi ebe ngasendleleni? Bathi ke bona, Akubangwa mbulo apha.', 'XHO22'),
(1, 38, 22, 'Wabuyela kuYuda, wathi, Andimfumananga; athi ke amadoda aloo ndawo, Akuhenyuzi kule ndawo.', 'XHO22'),
(1, 38, 23, 'Wathi uYuda, Makazithabathele, ukuze singadani; yabonani, ndimthume lo mntwana, animfumana;', 'XHO22'),
(1, 38, 24, 'Kwathi, ekupheleni kweenyanga ezintathu, waxelelwa uYuda, kwathiwa, uTamare, umolokazana wakho, ukrexezile; yabona, ukhawule ekrexeza; Wathi uYuda, Mkhupheni, atshiswe.', 'XHO22'),
(1, 38, 25, 'Akuba ezelwe, wathumela kuninazala, wathi, Ndimithi indoda abazelwe yiyo; abasebenzi.', 'XHO22'),
(1, 38, 26, 'uYuda wabazi, wathi, Ulilungisa kunam; ngokuba ndingamnikanga uSela unyana wam. Kwaye akasamazi.', 'XHO22'),
(1, 38, 27, 'Wathi akuzala, wabona amawele esizalweni sakhe.', 'XHO22'),
(1, 38, 28, 'Kwathi ekuzaleni kwakhe, wasolula isandla umzalisikazi, wathabatha umsonto obomvu wegazi esandleni sakhe, wawubopha esandleni sakhe, wathi, Lo uya kuphuma kuqala.', 'XHO22'),
(1, 38, 29, 'Uthe akuxhawula isandla sakhe, waphuma umzalwana wakhe, wathi, Uphume njani na? ke ngoko, wathiywa igama elinguParese.', 'XHO22'),
(1, 38, 30, 'Emveni koko, waphuma umninawa wakhe, ephethe inundu ebomvu ngesandla, wathi yena nguSarage.', 'XHO22'),
(1, 39, 1, 'uYosefu wehliswa wasiwa eYiputa; uPotifare, umphathiswa wabasiki, umYiputa, wamthenga esandleni samaIshmayeli, abemhlisele khona.', 'XHO22'),
(1, 39, 2, 'uYehova waba naye uYosefu, waba yindoda enempumelelo; waba sendlwini yenkosi yakhe, umYiputa.', 'XHO22'),
(1, 39, 3, 'Yabona inkosi yakhe ukuba uYehova unaye, nokuba yonke into abeyenza, uYehova ubemphumelelisa esandleni sakhe.', 'XHO22'),
(1, 39, 4, 'uYosefu wababalwa emehlweni ayo, wayilungiselela; wamenza umveleli wendlu yakhe, neento zonke ebinazo wazinikela esandleni sakhe.', 'XHO22'),
(1, 39, 5, 'Ukususela ekummiseleni kwakhe ukuba abe ngumgcini wendlu yakhe nezinto zonke anazo, uYehova wayisikelela indlu yomYiputa lowo ngenxa kaYosefu; yaye intsikelelo kaYehova yaba kuyo yonk’ into awayenayo endlwini nasemasimini.', 'XHO22'),
(1, 39, 6, 'Zonke izinto ebinazo wazishiya esandleni sikaYosefu; ubengayazi ke into abeza kuyifumana, ingesiso isonka esidlileyo. Yaye uYosefu wayeyinzwana, indoda enomtsalane.', 'XHO22'),
(1, 39, 7, 'Emveni kwezi zinto, umka-nkosi yakhe wakhangela kuYosefu; Wathi, Lala nam.', 'XHO22'),
(1, 39, 8, 'Wala, wathi kumkankosi yakhe, Yabona, inkosi yam ayiyazi into endinayo endlwini, nokuba indinike konke enako;', 'XHO22'),
(1, 39, 9, 'Akukho bani mkhulu kum kule ndlu; kwaye akandichasanga nganto, ngaphandle kwakho, ngenxa yokuba ungumfazi wakhe; Ndothini na ukwenza obu bubi bukhulu kangaka, ndone kuThixo?', 'XHO22'),
(1, 39, 10, 'Wathi ethetha noYosefu imihla ngemihla, akamphulaphula uYosefu ukuba alale naye, nokuba abe naye.', 'XHO22'),
(1, 39, 11, 'Ngelo xesha uYosefu wangena endlwini eya kusebenza umsebenzi wakhe; kwaye kungekho nanye indoda yaloo ndlu.', 'XHO22'),
(1, 39, 12, 'Wambamba ngengubo, wathi, Lala nam; wayishiya ingubo yakhe esandleni sakhe, wabaleka, wamkhuphela phandle.', 'XHO22'),
(1, 39, 13, 'Wathi akubona ukuba uyishiyile ingubo yakhe esandleni sakhe, wabaleka wemka.', 'XHO22'),
(1, 39, 14, 'Wabiza amadoda endlu yakhe, wathetha kuwo, wathi, Yabonani, usizisele umHebhere ukuba adlale ngathi; weza kum ezolala nam, ndakhwaza ngelizwi eliphakamileyo.', 'XHO22'),
(1, 39, 15, 'Wathi akuva ukuba ndiphakamise ilizwi ndikhwaza, wayishiya ingubo yakhe ecaleni kwam, wabaleka, wamkhuphela phandle.', 'XHO22'),
(1, 39, 16, 'Yambatha ingubo yayo phezu kwakhe, yada yafika inkosi yakhe endlwini.', 'XHO22'),
(1, 39, 17, 'Wathetha kuye ngokwala mazwi, wathi, Uze umkhonzi ongumHebhere lowo usizisele kuthi, ukuba enze intlekisa ngam.', 'XHO22'),
(1, 39, 18, 'Ndathi ndakuliphakamisa ilizwi lam ndikhwaza, wayishiya ingubo yakhe ecaleni kwam, wabaleka.', 'XHO22'),
(1, 39, 19, 'Yathi yakuweva inkosi yakhe amazwi omfazi wayo, yamxelela, yathi: “Ikhobokazana lakho lindenze le nto; ukuze uvuthe umsindo wakhe.', 'XHO22'),
(1, 39, 20, 'Ukumkani kaYosefu wamthabatha, wamfaka entolongweni, endaweni abekhonkxwa kuyo amabanjwa okumkani; waye entolongweni apho.', 'XHO22'),
(1, 39, 21, 'uYehova waba naye uYosefu, waba nemfesane kuye, waba nemfesane kuye emehlweni omgcini-ntolongo.', 'XHO22'),
(1, 39, 22, 'Umgcini wababanjwa wabanikela esandleni sikaYosefu onke amabanjwa abesentolongweni; kwaye nantoni na ababeyenza apho, wayeyenza.', 'XHO22'),
(1, 39, 23, 'Umgcini wababanjwa wayengaboni nto esandleni sakhe; ngokuba uYehova ubenaye, nento awayenzayo uYehova wayiphumelelisa.', 'XHO22'),
(1, 40, 1, 'Kwathi emveni koko, umngcamli wokumkani waseYiputa, nompheki wakhe, bamona ukumkani wabo, ukumkani waseYiputa.', 'XHO22'),
(1, 40, 2, 'uFaro waba noburhalarhume kubakhonzi bakhe bobabini, umphathi wabangcamli nomphathi wabapheki.', 'XHO22'),
(1, 40, 3, 'Wabafaka elugcinweni endlwini yomphathi wamafanankosi, endlwini yentolongo, indawo leyo awayevalelwe kuyo uYosefu.', 'XHO22'),
(1, 40, 4, 'Umthetheli wabasiki wathi uYosefu makahambe nabo, abalungiselele; bahlala ke entolongweni ixesha elithile.', 'XHO22'),
(1, 40, 5, 'Bobabini baphupha iphupha, ngamnye iphupha lakhe ngabusuku bunye, ngamnye ngokokutyhilwa kwephupha lakhe, umngcamli nompheki wokumkani waseYiputa, owayesentolongweni.', 'XHO22'),
(1, 40, 6, 'uYosefu wangena kubo kwakusa, wabakhangela, wafika bematshekile.', 'XHO22'),
(1, 40, 7, 'Wababuza abakhonzi bakaFaro, ababenaye endlwini yenkosi yakhe, wathi, Yini na ukuba nibe bubi namhlanje?', 'XHO22'),
(1, 40, 8, 'Bathi kuye, Siphuphe iphupha, akukho bani unako ukulityhila; Wathi uYosefu kubo, Iziguqulelo asizikaThixo na, akunjalo? nceda undixelele.', 'XHO22'),
(1, 40, 9, 'Umngcamli wabangcamli wamxelela uYosefu iphupha lakhe, wathi kuye, Ephupheni lam ndibone umdiliya ecaleni kwam;', 'XHO22'),
(1, 40, 10, 'emdiliyeni wadubula iintyatyambo ezintathu, wangathi udubule, wadubula iintyantyambo zawo; iidiliya zawo zivelisa iidiliya ezivuthiweyo;', 'XHO22'),
(1, 40, 11, 'indebe kaFaro isesandleni sam, ndathabatha iidiliya, ndayikhandela endebeni kaFaro, ndayinikela indebe esandleni sikaFaro;', 'XHO22'),
(1, 40, 12, 'Wathi uYosefu kuye, Nanku ukushwankathelwa kwalo: amasebe amathathu yimihla emithathu;', 'XHO22'),
(1, 40, 13, 'Emveni kweentsuku ezintathu, uya kuyiphakamisa intloko yakho uFaro, akubuyisele endaweni yakho; uyibeke indebe kaFaro esandleni sakhe, njengokuba ubungumngcamli wakhe;', 'XHO22'),
(1, 40, 14, 'Uze undikhumbule xa uthe wahlala phantsi, undenzele inceba, undibizele kuFaro, undikhuphe kule ndlu;', 'XHO22'),
(1, 40, 15, 'ngokuba okunene ndabiwa ezweni lamaHebhere; nalapha andenzanga nto bangandifaka emhadini.', 'XHO22'),
(1, 40, 16, 'Wabona umphathi wabapheki ukuba isityhilo eso silungile, wathi kuYosefu, Nam ke ephupheni lam, ndithwele iingobozi ezintathu ezimhlophe entloko;', 'XHO22'),
(1, 40, 17, 'Engobozini ephezulu kukho iintlobo zonke zokutya okuphekiweyo kukaFaro; zasidla iintaka engobozini ephezu kwentloko yam.', 'XHO22'),
(1, 40, 18, 'uJosefu waphendula wathi: “Nantsi ke ingcaciso: “Imibhobho emithathu yomchamo yimihla emithathu.', 'XHO22'),
(1, 40, 19, 'Emveni kweentsuku ezintathu, uya kuyiphakamisa intloko yakho uFaro, akuxhome emthini; iintaka ziyidle inyama yakho kuwe.', 'XHO22'),
(1, 40, 20, 'Kwathi ngomhla wasithathu, umhla wokuzalwa kukaFaro, wabalungiselela isidlo bonke abakhonzi bakhe;', 'XHO22'),
(1, 40, 21, 'Wambuyisela umphathi wabashicileli eofisini yakhe; wayinikela indebe esandleni sikaFaro.', 'XHO22'),
(1, 40, 22, 'Wamxhoma umphathi wompheki, njengoko uYosefu wabacaciselayo.', 'XHO22'),
(1, 40, 23, 'Kodwa umphathi wabangcamli akazange amkhumbule uYosefu, kodwa wamlibala nje.', 'XHO22'),
(1, 41, 1, 'Kwathi, kwakuzaliseka iminyaka emibini, uFaro waphupha, wambona emi phezu komlambo;', 'XHO22'),
(1, 41, 2, 'nanko kunyuka, kuphuma emlanjeni, amathokazi asixhenxe atyebileyo, etyebile; ayesidla edlelweni.', 'XHO22'),
(1, 41, 3, 'Ndabona kunyuka, kuphuma emlanjeni, amanye amathokazi asixhenxe emva kwawo, emabi, enqinile, ehluthi; wamisa nezinye iinkomo elunxwemeni lomlambo;', 'XHO22'),
(1, 41, 4, 'asuka amathokazi amabi, anqinileyo, awadla amathokazi asixhenxe amahle, atyebileyo. Waphakama ke uFaro.', 'XHO22'),
(1, 41, 5, 'Waphinda walala, waphupha okwesibini, wabona iziqhamo ezisixhenxe zikhula eludizeni olunye, zityebile yaye zintle.', 'XHO22'),
(1, 41, 6, 'Nantso ke, kubonakala imitya ezisixhenxe ngasemva kwayo, engcakacileyo, ivela lulophu lwasempumalanga.', 'XHO22'),
(1, 41, 7, 'Zaza iibhokhwe ezisixhenxe ezinqinileyo zazidla iinkunzi zeebhokhwe ezisixhenxe neebhokhwe ezichumileyo. Wavuka uFaro, O, liphupha.', 'XHO22'),
(1, 41, 8, 'Kwasa umoya wakhe wakhathazeka; wathumela ukuba kubizwe zonke izazi zaseYiputa, nezilumko zakhe zonke; uFaro wazixelela iphupha lakhe; kodwa akubangakho namnye ubenako ukuyicacisa kuFaro.', 'XHO22'),
(1, 41, 9, 'Wathi umphathi wabangcamli kuFaro, Ndiyasikhumbula namhla isono sam.', 'XHO22'),
(1, 41, 10, 'uFaro wayenoburhalarhume kubakhonzi bakhe, wandibeka elugcinweni lomthetheli wendlu yabasiki, mna nomthetheli wabapheki.', 'XHO22'),
(1, 41, 11, 'Saphupha iphupha ngobunye ubusuku, mna naye; elowo waphupha ngokutyhilwa kwephupha lakhe.', 'XHO22'),
(1, 41, 12, 'Kwaye kukho kuthi umfana ongumHebhere, umkhonzi womthetheli wabasiki; saza samxelela, yaye wasichazela amaphupha ethu; wachazela wonke umntu ngephupha lakhe.', 'XHO22'),
(1, 41, 13, 'Kwathi, njengoko wayesixelele, kwaba njalo; Wandibuyisela emsebenzini wam, wabeka umnxeba kuye.', 'XHO22'),
(1, 41, 14, 'uFaro wathumela ukuba kubizwe uYosefu. Wazicheba, wanxiba iingubo zakhe, wangena kuFaro.', 'XHO22'),
(1, 41, 15, 'Wathi uFaro kuYosefu, Ndiphuphe iphupha, akwabakho mntu ulityhilayo; ndikuve usithi uyaliqonda iphupha ulitolike.', 'XHO22'),
(1, 41, 16, 'uYosefu wamphendula uFaro, wathi, Andinguye; uThixo uya kumphendula uFaro ngoxolo.', 'XHO22'),
(1, 41, 17, 'Wathi uFaro kuYosefu, Ephupheni lam, yabona, ndimi elunxwemeni lomlambo;', 'XHO22'),
(1, 41, 18, 'nanko kunyuka, kuphuma emlanjeni, amathokazi asixhenxe etyebile, etyebile; ayesidla edlelweni.', 'XHO22'),
(1, 41, 19, 'Ndabona kunyuka amanye amathokazi asixhenxe emva kwawo, emabi, emabi kakhulu, emabi kakhulu, endingazanga ndiwabone kulo lonke ilizwe laseYiputa, ngenxa yobubi obubi kuwo;', 'XHO22'),
(1, 41, 20, 'amathokazi angamarhoqorhoqo, anqinileyo, awadla amathokazi asixhenxe atyebileyo.', 'XHO22'),
(1, 41, 21, 'Bakuba ke basidla, akwaziwa ukuba bayidlile; kodwa babesebabi njengakuqala. Ndiye ndaphakama.', 'XHO22'),
(1, 41, 22, 'Ndabona ephupheni lam, ndabona iidumba ezisixhenxe ziphuma mcingeni mnye, zichumile, zintle;', 'XHO22'),
(1, 41, 23, 'Nanko kuvela iinkozo ezisixhenxe ngasemva kwazo, ezomileyo, ezingcakacileyo, ziqhutywa lulophu lwasempumalanga.', 'XHO22'),
(1, 41, 24, 'Asuka amathanga angcakacileyo, awadla amathangazana asixhenxe alungileyo; kodwa akukho bani unokundicacisela.', 'XHO22'),
(1, 41, 25, 'uYosefu wathi uFaro, Elinye iphupha likaFaro: uThixo umxelele uFaro oko aya kukwenza.', 'XHO22'),
(1, 41, 26, 'Amathokazi asixhenxe alungileyo yiminyaka esixhenxe; nemithambo esixhenxe elungileyo yiminyaka esixhenxe; iphupha lilinye.', 'XHO22'),
(1, 41, 27, 'Amathokazi asixhenxe angamarhoqorhoqo, anyukileyo aphuma emva kwawo, abeminyaka isixhenxe ezelwe; nemitya esixhenxe engenanto, evuthuzwe lulophu lwasempuma, iya kuba yiminyaka esixhenxe yendlala.', 'XHO22'),
(1, 41, 28, 'Lilo eli ilizwi endalithethayo kuFaro lokuthi, oko aya kukwenza uThixo, uya kukubonisa uFaro.', 'XHO22'),
(1, 41, 29, 'Yabona, kukho iminyaka esixhenxe yendyebo ezweni lonke laseYiputa;', 'XHO22'),
(1, 41, 30, 'Emva kwaleyo kuya kubakho iminyaka esixhenxe yendlala; ilityalwe yonke indyebo ezweni laseYiputa; nendlala iya kulidla ihlabathi;', 'XHO22'),
(1, 41, 31, 'Ayiyi kwaziwa inala ehlabathini, ngenxa yendlala eya kulilandela; kuba kuya kuba nzima kakhulu.', 'XHO22'),
(1, 41, 32, 'Ngoko ke, laphindwa kabini iphupha likaFaro; kungenxa yokuba lo mbandela ucetywe nguThixo, yaye kungekudala uThixo uya kuwuzalisekisa.', 'XHO22'),
(1, 41, 33, 'uFaro makakhangele indoda esisilumko, esisilumko, ayimise phezu kwelizwe laseYiputa.', 'XHO22'),
(1, 41, 34, 'Makenze le nto uFaro, alimise abalawuli ilizwe, athabathe isihlanu selizwe laseYiputa iminyaka esixhenxe yendyebo.', 'XHO22'),
(1, 41, 35, 'Mababuthe konke ukudla kweminyaka yendyebo ezayo, yokudala ingqolowa phantsi kwesandla sikaFaro, bakugcine ukudla emizini;', 'XHO22'),
(1, 41, 36, 'Oko kudla kuya kuba ngumxhesho welizwe ngeminyaka yosixhenxe yendlala eya kubakho ezweni laseYiputa; hleze ihlabathi litshabalale yindlala.', 'XHO22'),
(1, 41, 37, 'Lalunga loo nto emehlweni kaFaro nasemehlweni abo bonke abakhonzi bakhe.', 'XHO22'),
(1, 41, 38, 'Wathi uFaro kubakhonzi bakhe, Sinokumfumana na umntu onje, onomoya kaThixo?', 'XHO22'),
(1, 41, 39, 'Wathi uFaro kuYosefu, Ekubeni uThixo ekwazisile zonke ezi zinto, akukho bani usisilumko, unengqondo, njengawe.', 'XHO22'),
(1, 41, 40, 'Wena uya kuba yingangamsha yendlu yam, bazimase bonke abantu bam ngokwelizwi lakho; kusetroneni yodwa endiya kuba mkhulu kuwe.', 'XHO22'),
(1, 41, 41, 'Wathi uFaro kuYosefu, Yabona, ndikumise phezu kwalo lonke ilizwe laseYiputa;', 'XHO22'),
(1, 41, 42, 'uFaro wawukhulula umsesane wakhe esandleni sakhe, wawufaka esandleni sikaYosefu, wamambathisa ilinen ecikizekileyo, wafaka umxokelelwane wegolide entanyeni yakhe.', 'XHO22'),
(1, 41, 43, 'Wamthabatha kweyesibini inqwelo abenayo; Badanduluka phambi kwakhe, besithi, Qubudani; wamenza umphathi welizwe lonke laseYiputa.', 'XHO22'),
(1, 41, 44, 'Wathi uFaro kuYosefu, NdinguFaro;', 'XHO22'),
(1, 41, 45, 'uFaro wamthiya uYosefu igama lokuba nguSafenati-paneha; wamnika uAzenati, intombi kaPotifare, umbingeleli wamnye, ukuba abe ngumkakhe. Waphuma uYosefu, waya kulo lonke ilizwe laseYiputa.', 'XHO22'),
(1, 41, 46, 'uYosefu ebeminyaka imashumi mathathu ezelwe, ukuma kwakhe phambi koFaro, ukumkani waseYiputa. uYosefu wemka kuFaro, walicanda ilizwe laseYiputa.', 'XHO22'),
(1, 41, 47, 'Umhlaba wavelisa ngendiphane ngeminyaka esixhenxe yendyebo.', 'XHO22'),
(1, 41, 48, 'Wakubutha konke ukudla kweminyaka esixhenxe eyayikho ezweni laseYiputa, wakubeka ukudla emizini; ukudla kwamasimi ekupheleni kwemizi ngemizi, wakubeka khona.', 'XHO22'),
(1, 41, 49, 'uYosefu wayihlanganisa ingqolowa yam, kunye nentlabathi yolwandle, yaninzi, wada walishiya inani; ngokuba lingena nani.', 'XHO22'),
(1, 41, 50, 'uYosefu wazala oonyana bababini, wabazalelwa nguAzenati, intombi kaPotifare, umbingeleli wamnye, ingekafiki iminyaka yendlala.', 'XHO22'),
(1, 41, 51, 'uYosefu wamthiya owamazibulo igama elinguManase, ngokuba wathi, uThixo undenze ndakulibala konke ukwaphuka kwam, nayo yonke indlu kabawo.', 'XHO22'),
(1, 41, 52, 'Owesibini wamthiya igama elinguEfrayim, ngokuba uThixo wandihambisa emhlabeni weembandezelo.', 'XHO22'),
(1, 41, 53, 'Yaphela ke iminyaka yosixhenxe yendyebo ekhoyo ezweni laseYiputa.', 'XHO22'),
(1, 41, 54, 'Yaqala ukufika iminyaka yosixhenxe yendlala, njengoko wayekuthethile uYosefu. Yabakho indlala kumazwe onke; ke kulo lonke ilizwe laseYiputa kwakukho isonka.', 'XHO22'),
(1, 41, 55, 'Lafa lonke ilizwe laseYiputa yindlala; abantu bakhalela isonka kuFaro; Wathi uFaro kuwo onke amaYiputa, Yiyani kuYosefu; yenza loo nto uyixelelwayo.', 'XHO22'),
(1, 41, 56, 'Indlala yaligubungela lonke ihlabathi; uYosefu wazivula zonke izisele, wathengisa ngamaYiputa; yaba nkulu indlala ezweni laseYiputa.', 'XHO22'),
(1, 41, 57, 'Lesuka ilizwe liphela, leza kuthenga ingqolowa kuYosefu eYiputa; ngokuba ibinkulu indlala kumazwe onke.', 'XHO22'),
(1, 42, 1, 'Wabona uYakobi ukuba kukho ingqolowa eYiputa, wathi uYakobi koonyana bakhe, Nibona ntoni na?', 'XHO22'),
(1, 42, 2, 'Wathi, Yabonani, ndivile ukuba kukho ingqolowa eYiputa; yihla uye khona, usithenge khona; ukuze sidle ubomi, singafi.', 'XHO22'),
(1, 42, 3, 'Behla abakhuluwa bakaYosefu abalishumi, beya kuthenga ingqolowa eYiputa.', 'XHO22'),
(1, 42, 4, 'Kodwa uBhenjamin, umninawa kaYosefu, akamthumanga uYakobi nabazalwana bakhe; ngokuba ethe, Makangaphathwa ububi.', 'XHO22'),
(1, 42, 5, 'Beza oonyana bakaSirayeli, beza kuthenga ingqolowa kwabo bezayo; kuba indlala ibikho ezweni lakwaKanan.', 'XHO22'),
(1, 42, 6, 'uYosefu wayengurhuluneli welizwe elo; wayethengisa ngengqolowa kubo bonke abantu belo zwe; beza abakhuluwa bakaYosefu, baqubuda kuye.', 'XHO22'),
(1, 42, 7, 'uYosefu wababona abakhuluwa bakhe, wabazi, wazenza wasemzini kubo, wathetha nabo kalukhuni; wathi kubo, Nivela phi na? Bathi, Sivela ezweni lakwaKanan, size kuthenga ukudla;', 'XHO22'),
(1, 42, 8, 'uYosefu wayebazi abakhuluwa bakhe, kodwa bona babengamazi.', 'XHO22'),
(1, 42, 9, 'Wawakhumbula uYosefu amaphupha abenawo ngabo, wathi kubo, Niziintlola nina; ukubona inkunkuma yelizwe oze kulo.', 'XHO22'),
(1, 42, 10, 'Bathi kuye, Hayi, nkosi yam, abakhonzi bakho baze kuthenga ukudla.', 'XHO22'),
(1, 42, 11, 'Sonke singabazalwana bamntu mnye; singamadoda okwenene, thina bakhonzi bakho asizizo iintlola.', 'XHO22'),
(1, 42, 12, 'Wathi kubo, Nize kubona amampunge eli hlabathi;', 'XHO22'),
(1, 42, 13, 'Bathi ke bona: “Abakhonzi bakho balishumi elinesibini abazalwana, singoonyana bandoda-nye ezweni lakwaKanan; yabona, omncinane ukubawo namhlanje, omnye akakho.', 'XHO22'),
(1, 42, 14, 'Wathi uYosefu kubo, Yile nto bendiyithetha kuni, ndathi, Niziintlola.', 'XHO22'),
(1, 42, 15, 'Niya kucikidwa ngale ndawo: Ehleli nje uFaro, anisayi kuphuma apha, ukuba akathanga eze apha umninawa wenu.', 'XHO22'),
(1, 42, 16, 'Thumani omnye kuni apha, alandele umzalwana wenu, nokhonkxwa, ukuze acikidwe amazwi enu, ukuba ninenyaniso na;', 'XHO22'),
(1, 42, 17, 'Wabafaka bonke entolongweni iintsuku ezintathu.', 'XHO22'),
(1, 42, 18, 'Wathi uYosefu kubo ngomhla wesithathu, Yenzani oku, ukuze niphile; ngokuba ndiyamoyika uThixo;', 'XHO22'),
(1, 42, 19, 'Ukuba ninyanisile, omnye umzalwana wenu makafakwe entolongweni yenu; yiyani, nithabathe ingqolowa ngenxa yendlala yezindlu zenu.', 'XHO22'),
(1, 42, 20, 'Mziseni umninawa wenu kum; ke ngoko, amazwi enu aya kuqinisekiswa, kwaye aniyi kufa. Kwaye benjenjalo.', 'XHO22'),
(1, 42, 21, 'Bathetha omnye komnye besithi, Inene, sinetyala ngenxa yomninawa wethu, ngokuba sakubona ukubandezeleka komphefumlo wakhe, oko abetarhuzisa kuthi, asamphulaphula; ke ngoko, le mbandezelo ifikile kuthi.', 'XHO22'),
(1, 42, 22, 'Wabaphendula uRubhen, wathi, Bendingathethanga kuni; ndithe, Musa ukona, mntwana wam; kwaye ubungafuni nokuva? ke ngoko, yabonani, nalo igazi lakhe liyabizwa.', 'XHO22'),
(1, 42, 23, 'Babengazi bona ukuba uYosefu ubevile; ngokuba wayethetha kubo ngetoliki.', 'XHO22'),
(1, 42, 24, 'Wabafulathela, walila; wabuya weza kubo, wathetha kubo, wamthabatha uSimon kubo, wamkhonkxa phambi kwabo.', 'XHO22'),
(1, 42, 25, 'Wathi uYosefu mazizaliswe iipokotho zabo ngengqolowa, imali yabo bonke ibuyiselwe engxoweni yakhe, banikwe nomphako wendlela; Wenjenjalo ke nakubo.', 'XHO22'),
(1, 42, 26, 'Bayikhwelisa ingqolowa emaesileni abo, bemka khona.', 'XHO22'),
(1, 42, 27, 'Uthe ke omnye akuyivula ingxowa yakhe, ukuba aluse iesile lakhe kwindlu yabahambi, wayibona imali yakhe; ngokuba, yabonani, belisemlonyeni wetyala lakhe.', 'XHO22'),
(1, 42, 28, 'Wathi kubazalwana bakhe, Ibuyisiwe imali yam; yabona ke, ukho wena kum;', 'XHO22'),
(1, 42, 29, 'Bafika kuYakobi uyise ezweni lakwaKanan, bamxelela konke okubehleleyo; yithi,', 'XHO22'),
(1, 42, 30, 'Yathetha kakubi ngathi indoda engumninilo lizwe, isithi, siziintlola zelizwe.', 'XHO22'),
(1, 42, 31, 'Sathi kuye, Singabantu abanyanisekileyo; asizizo iintlola,', 'XHO22'),
(1, 42, 32, 'singabazalwana abalishumi elinababini, oonyana bakabawo; omnye akakho, omncinane ukubawo namhlanje ezweni lakwaKanan.', 'XHO22'),
(1, 42, 33, 'Indoda leyo, engumnini-lizwe, yathi kuthi, Ndiya kwazi ngale nto, ukuba ninyanisile; shiyani abe mnye wabantwana benu apha kum, niphathele ukulamba kwemizalwane yenu, nihambe;', 'XHO22'),
(1, 42, 34, 'Mziseni umninawa wenu kum; ndiya kwazi ke ngoko, ngokuba anizizo iintlola, ningabantu bokwenene; Ndiya kukukhulula ngentlawulelo mna, mzalwana wam, urhwebe kweli lizwe.', 'XHO22'),
(1, 42, 35, 'Ke kaloku, bakukhuphela iingxowa zabo, yabona, elowo engxoweni yakhe; bakuzibona iziqhuma zemali boyika bona noyise.', 'XHO22'),
(1, 42, 36, 'Wathi kubo uYakobi uyise, Ndibathabathile abantwana bam; uYosefu akakho, noSimon akakho; niza kuthabatha noBhenjamin; zonke ezo zinto zichasene nam.', 'XHO22'),
(1, 42, 37, 'Wathi uRubhen kuyise, Babulale bobabini oonyana bam, ukuba andithanga ndimzise kuwe; mnikele esandleni sam, ndombuyisela kuwe mna.', 'XHO22'),
(1, 42, 38, 'Wathi, Unyana wam akasayi kuhla nani; ngokuba ufile umkhuluwa wakhe, yena usele yedwa; Ukuba uthe wahlelwa bububi endleleni yakho, bozihlisela izimvi zam kwelabafileyo ndinentlungu.', 'XHO22'),
(1, 43, 1, 'Indlala yaba nkulu kwelo zwe.', 'XHO22'),
(1, 43, 2, 'Bayidla ingqolowa, ababeze nayo bevela eYiputa, wathi uyise kubo, Buyani niye kusithengela ukudla.', 'XHO22'),
(1, 43, 3, 'Wathi uYuda kuye, Yasifunga loo ndoda, yathi, Aniyi kububona ubuso bam, engekho umninawa wenu.', 'XHO22'),
(1, 43, 4, 'Ukuba uthe wamthuma umninawa wethu nathi, sohla siye kukuthengela ukudla.', 'XHO22'),
(1, 43, 5, 'Ukuba akuthanga umthume, asiyi kuhla; ngokuba yathi kuthi loo ndoda, Aniyi kububona ubuso bam, engabangakho nani udade wenu.', 'XHO22'),
(1, 43, 6, 'Wathi uSirayeli, Yini na ukuba undenze into embi kangaka, ukuba uyixelele indoda ukuba unomninawa?', 'XHO22'),
(1, 43, 7, 'Ngoko bathi: “Le ndoda yasibuza ngenyameko nangemeko yethu neyentsapho yakowethu, isithi: “Usaphila na uyihlo? Wayenomnye umzalwana, saza samxelela ngokwaloo mazwi, sathi: “Ngaba siyazi na ngokuqinisekileyo ukuba uya kuthi: “Mxhaseni umzalwana wenu?', 'XHO22'),
(1, 43, 8, 'Wathi uYuda kuSirayeli uyise, Thabatha mna nenkwenkwe, sihambe sihambe; ukuze sidle ubomi, singafi, thina nani, nentsapho yethu.', 'XHO22'),
(1, 43, 9, 'Ndiya kulindela kuye; mbuze esandleni sam; Ukuba andithanga ndimzise kuwe, ndimbeke phambi kwakho, ndoba netyala kude kube ngunaphakade.', 'XHO22'),
(1, 43, 10, 'Kuba, ukuba besingathandabuzanga, ngesibuya okwesibini.', 'XHO22'),
(1, 43, 11, 'Wathi uSirayeli uyise kubo, Ukuba kunjalo, makwenziwe ntoni na; Thabathani ezona zintle zelizwe ezityeni zenu, nise ezityeni zenu, nise emntwini lowo, intwana yamafutha aqholiweyo, nentwana yobusi, nentlaka emhlophe, nemore, neenati, neeamangile;', 'XHO22'),
(1, 43, 12, 'Phayani ezandleni zenu imali ephindiweyo; naloo mali yabuyiswayo emlonyeni weengxowa zenu, yibekeni ezandleni zenu; mhlawumbi le ibiyimpazamo:', 'XHO22'),
(1, 43, 13, 'Nomntakwenu, mthabathe, usuke, ubuyele kuloo ndoda.', 'XHO22'),
(1, 43, 14, 'Ngamana uThixo uSomandla waniphatha ngemfesane phambi kwaloo ndoda, ukuba imgxothe omnye umzalwana wenu uBhenjamin. Xa abantwana bam bebiwa, ndiyakhuthuzwa.', 'XHO22'),
(1, 43, 15, 'Amadoda lawo awuthabatha umnikelo, nemali ephindwe kabini esandleni sawo, noBhenjamin; Wemka, wehla waya eYiputa, wema ngakuYosefu.', 'XHO22'),
(1, 43, 16, 'uYosefu wambona uBhenjamin enabo, wathi kumphathi wendlu yakhe, Wangenise la madoda endlwini, uwabulale, ulungise; kuba la madoda aya kudla nam emini enkulu.', 'XHO22'),
(1, 43, 17, 'Indoda leyo yenza njengoko uYosefu wamwiselayo umthetho; indoda yawangenisa amadoda endlwini kaYosefu.', 'XHO22'),
(1, 43, 18, 'Oyika loo madoda, ngokuba engeniswe endlwini kaYosefu; bathi, Ngenxa yemali ebibuyiselwe ezingxoweni zethu ngenxa engaphambili, siziswe; ukuze asifumane isizathu sokusiqwenga, asithabathe amakhoboka ethu namaesile ethu.', 'XHO22'),
(1, 43, 19, 'Asondela kumgcini-ndlu kaYosefu, athetha kuye emnyango wendlu.', 'XHO22'),
(1, 43, 20, 'Bathi ke bona, Nkosi, besize kuqala kuthenga ukudla.', 'XHO22'),
(1, 43, 21, 'Sathi sakufika kwindlu yeendwendwe, savula iipokotho zethu, yaye sayibona imali yomntu ngamnye emlonyeni wengxowa yakhe, imali yethu ngokomlinganiselo wayo; saza sayibuyisela ezandleni zethu.', 'XHO22'),
(1, 43, 22, 'Saphatha nemali ezandleni zethu, yokuthenga ukudla; asizukwazi ukuba imali yethu ifakwe ngubani ezipokothweni zethu.', 'XHO22'),
(1, 43, 23, 'Wathi, Xolani, musani ukoyika; Bendinayo imali yakho. Wazisa uSimon kubo.', 'XHO22'),
(1, 43, 24, 'Indoda leyo yawangenisa amadoda endlwini kaYosefu, yawanika amanzi, ahlamba iinyawo zawo; amahashe abo wawanika ukudla.', 'XHO22'),
(1, 43, 25, 'Bamenzela isipho uYosefu owayefike emini; ngokuba evile ukuba baya kudla khona isonka.', 'XHO22'),
(1, 43, 26, 'Kwathi, akungena uYosefu endlwini, awuzisa umnikelo ngesandla sawo endlwini, aziphosa phantsi phambi kwakhe.', 'XHO22'),
(1, 43, 27, 'Wawabuza ukuphila kwawo, wathi, Usaphilile na uyihlo, ixhego enanithethe ngalo? Ngaba usaphila?', 'XHO22'),
(1, 43, 28, 'Bathi, Usaphilile ubawo umkhonzi wakho, usadla ubomi; Baqubuda, baqubuda.', 'XHO22'),
(1, 43, 29, 'Wawaphakamisa amehlo akhe, wambona uBhenjamin umninawa wakhe, unyana kanina, wathi, Nguye na lo umninawa wenu omncinane, enibe nithetha ngaye kum? Waza wathi: “uThixo makabe nenceba kuwe, nyana wam!', 'XHO22'),
(1, 43, 30, 'uYosefu wakhawuleza; ngenxa yokuba isisu sakhe sasiswele umninawa wakhe, kwaye wayekhangela indawo apho angalila khona; Wangena egumbini lakhe, walila khona.', 'XHO22'),
(1, 43, 31, 'Waza wahlamba ubuso bakhe, waphuma ezibambile, wathi, Bekani isonka.', 'XHO22'),
(1, 43, 32, 'Baphekela yena yedwa, baba yedwa, namaYiputa abesidla naye odwa; kuba amaYiputa akanakudla sonka namaHebhere; kuba lisikizi kumaYiputa.', 'XHO22'),
(1, 43, 33, 'Bahlala phantsi phambi kwakhe, owamazibulo ngokobuzibulo bakhe, omncinane ngokobuncinane bakhe; othuka amadoda.', 'XHO22'),
(1, 43, 34, 'Wazithabatha, wazisusa kuye; ke ukutya kukaBhenjamin bekubhetele ngokuphindwe kahlanu kunabo bonke abanye. Basela kwaye bonwabile naye.', 'XHO22'),
(1, 44, 1, 'Wamwisela umthetho umalusi wendlu yakhe, esithi, Zizaliseni iindawo zala madoda ngokudla, njengoko zinako ukukhongozela, niyibeke imali yalowo nalowo emlonyeni wengxowa yakhe.', 'XHO22'),
(1, 44, 2, 'Yibeke indebe yam, indebe yesilivere, emlonyeni wengxowa yomncinane, nemali yengqolowa yakhe. Yaye wenza ngokwelizwi awayelithethile uYosefu.', 'XHO22'),
(1, 44, 3, 'Kwathi, kwakuba imvula, amadoda lawo namaesile awo abuyiselwa endleleni yawo.', 'XHO22'),
(1, 44, 4, 'Akuba ephumile esixekweni, uYosefu wathi kumthetheli-waka, Suk’ ume, uwalandele laa madoda; wakufika kubo uthi kubo: Yini na ukuba nibuyisele okulungileyo ngobubi?', 'XHO22'),
(1, 44, 5, 'Le nto asiyile na inkosi yam iselayo, ikuprofetayo ngokwenyaniso? nenze ububi ngokwenjenjalo.', 'XHO22'),
(1, 44, 6, 'Wafika kubo, wathetha kwaelaa lizwi kubo;', 'XHO22'),
(1, 44, 7, 'Athi kuye, Yini na ukuba inkosi yam ithethe ngokwaloo mazwi? Ngamana uThixo angabavumeli abakhonzi bakho ukuba benze le nto:', 'XHO22'),
(1, 44, 8, 'Uyabona, imali esayifumanayo ezingxoweni zethu, sayibuyisela kuwe, sivela ezweni lakwaKanan; Singathini na ukuba endlwini yenkosi yakho isilivere negolide?', 'XHO22'),
(1, 44, 9, 'Osukuba efunyenwe phakathi kwabakhonzi bakho, makafe naye; nathi siya kuba ngamakhoboka enkosini yam.', 'XHO22'),
(1, 44, 10, 'Kwaye wathi: Nangoku makube ngokwamazwi enu; uya kuba msulwa.', 'XHO22'),
(1, 44, 11, 'Kwandula ke ngokukhawuleza, elowo wakhulula ingxowa yakhe phantsi, elowo wavula ingxowa yakhe.', 'XHO22'),
(1, 44, 12, 'Wagquthagqutha, eqalela komkhulu, eshiya komncinane; yafunyanwa indebe engxoweni kaBhenjamin.', 'XHO22'),
(1, 44, 13, 'Bazikrazula iingubo zabo, babopha elowo iesile lakhe, babuyela kuloo mzi.', 'XHO22'),
(1, 44, 14, 'Wangena uYuda nabazalwana bakhe endlwini kaYosefu; ngokuba wayeselapho, zawa phambi kwakhe.', 'XHO22'),
(1, 44, 15, 'Wathi uYosefu kubo, Nenzele ntoni na? Awuyazi ukuba indoda enjengam ingahlwayela?', 'XHO22'),
(1, 44, 16, 'Wathi uYuda, Siya kuthini na enkosini yam? sothetha njani? sothini na ukuzihlambulula? uThixo ukubonile okungendawo kwabakhonzi bakho; yabona, singabakhonzi benkosi yam, thina nalowo ifunyenwe kuye indebe.', 'XHO22'),
(1, 44, 17, 'Wathi, Ngamana uThixo angandivimba, ndenze loo nto. Ke osukuba ifunyenwe esandleni sakhe indebe, woba sisicaka sam; wena ke, nyuka uye kuyihlo ngoxolo.', 'XHO22'),
(1, 44, 18, 'Wasondela uYuda kuye, wathi, Camagu, nkosi yam, makathethe ilizwi umkhonzi wakho ezindlebeni zenkosi yam, umsindo wakho mawungavuthi kumkhonzi wakho, ngokuba enjengoFaro.', 'XHO22'),
(1, 44, 19, 'Yababuza inkosi yam abakhonzi bayo, isithi, Ninoyihlo na?', 'XHO22'),
(1, 44, 20, 'Saza sathi kukumkani wam: Sinobawo wethu, ixhego, nomntwana obudala bakhe, omncinane; wabhubha umninawa wakhe, wasala yedwa nonina, uyise ebemthanda.', 'XHO22'),
(1, 44, 21, 'Wathi ke wena kubakhonzi bakho, Mziseni kum, ndimbone.', 'XHO22'),
(1, 44, 22, 'Sathi thina kukumkani wam, Unyana akanako ukumshiya uyise; kuba xa athe wamshiya, uyise uya kufa.', 'XHO22'),
(1, 44, 23, 'Wathi wena kubakhonzi bakho, Ukuba umninawa wenu omncinane akathanga ehle nani, anisayi kubuya nibubone ubuso bam.', 'XHO22'),
(1, 44, 24, 'Senyuka ke saya kumkhonzi wakho, ubawo, samxelela amazwi am.', 'XHO22'),
(1, 44, 25, 'Wathi ubawo, Buyani niye kusithengela ukudla.', 'XHO22'),
(1, 44, 26, 'Saza sathi, Asinakusuka; Ukuba umninawa wethu unathi, sohla; kuba asiyi kububona ubuso baloo ndoda, xa umninawa wethu engekho kuthi.', 'XHO22'),
(1, 44, 27, 'Wathi uyihlo umkhonzi wakho, Niyazi nina, ukuba umkam wandizalela oonyana ababini nje.', 'XHO22'),
(1, 44, 28, 'Omnye wandishiya, ndathi, Okunene, uqwengiwe; kwaye ixesha elide andizange ndimbone;', 'XHO22'),
(1, 44, 29, 'Ukuba uthe wabususa kum, wamfikela ilishwa, izimvi zam nozihlisela kwelabafileyo ndinesingqala.', 'XHO22'),
(1, 44, 30, 'Ukuba ndithe ndaya kubawo, umkhonzi wakho lo, akakho phakathi kwethu unyana; ebona ukuba ubomi bakhe bubotshelelwe kubomi benkwenkwe;', 'XHO22'),
(1, 44, 31, 'Ukuba ithe yabona ukuba inkwenkwe ayikho kuthi, mayife; Abakhonzi bakho baya kuzihlisela izimvi zomkhonzi wakho, ubawo, ziye kwelabafileyo enentlungu.', 'XHO22'),
(1, 44, 32, 'Ngokuba umkhonzi wakho lo wenza umnqophiso phakathi konyana noyise, ndathi, Ukuba andithanga ndimzise kuwe, ndobuthwala ubugwenxa bukabawo ngonaphakade.', 'XHO22'),
(1, 44, 33, 'Ke ngoko makahlale umkhonzi wakho endaweni yenkwenkwe, umkhonzi wenkosi yam; yaza inkwenkwe yenyuka nabantakwabo.', 'XHO22'),
(1, 44, 34, 'Kuba ndingathini na ukunyuka ndiye kubawo, kungekho nkwenkwe? ukuze ndingayiboni intlekele eyehlela ubawo.', 'XHO22'),
(1, 45, 1, 'uYosefu akabanga nakuzibamba phambi kwabo bonke ababemi ngakuye; wadanduluka esithi, Ndikhululeni nonke. Yaye kwakungekho bani umi ngakuye ekuzibonakalisa kukaYosefu kubantakwabo.', 'XHO22'),
(1, 45, 2, 'Wakhala kakhulu; eva amaYiputa nendlu kaFaro.', 'XHO22'),
(1, 45, 3, 'Wathi uYosefu kubazalwana bakhe, NdinguYosefu; Ubawo usaphila, abanakumphendula abazalwana bakhe; ngokuba bebesoyika ebusweni bakhe.', 'XHO22'),
(1, 45, 4, 'Wathi uYosefu kubazalwana bakhe, Sondelani kum apha. Baye basondela. Wathi, NdinguYosefu, umninawa wenu, enamthengayo eYiputa.', 'XHO22'),
(1, 45, 5, 'Musani ukuba buhlungu, ningaziqumbeli, ukuba nathengisa ngam apha; ngokuba uThixo wandithuma phambi kwenu ukuze ndisindise abantu.', 'XHO22'),
(1, 45, 6, 'Kuloo minyaka mibini indlala yabakho ehlabathini, isemihlanu iminyaka ekungayi kubakho kuvuna kuyo nakuvuna.', 'XHO22'),
(1, 45, 7, 'Yaye uThixo wandithuma phambi kwenu ukuze ndigcine inzala yakho ehlabathini ndize ndisindise umphefumlo wakho ngenkululeko enkulu.', 'XHO22'),
(1, 45, 8, 'Ngoku ke asinini abandithume apha, nguThixo; Wandenza uyise kaFaro, nomphathi wendlu yakhe yonke, nomlawuli ezweni lonke laseYiputa.', 'XHO22'),
(1, 45, 9, 'Khawuleza uye kubawo uze uthi kuye: ‘Kutsho uYosefu unyana wakho ukuthi, uThixo undenze umphathi weYiputa yonke; yihla uze kum, musa ukulibala.', 'XHO22'),
(1, 45, 10, 'Uya kuhlala ezweni laseGoshen, ube kufuphi nam, wena, nabantwana bakho, nabantwana babantwana bakho, nempahla yakho emfutshane, neenkomo zakho, neento zonke onazo;', 'XHO22'),
(1, 45, 11, 'Ndonondla khona; kuba kuya kubakho iminyaka emihlanu yendlala; ukuze ungasweli wena, nendlu yakho, neento zonke onazo.', 'XHO22'),
(1, 45, 12, 'Yabonani, amehlo enu ayabona, noBhenjamin umninawa wam, ngamehlo enu, ukuba ngumlomo wam lo uthethayo kuni.', 'XHO22'),
(1, 45, 13, 'Ze nimxelele uBawo ngalo lonke uzuko lwam eYiputa, neento zonke enizibonileyo; ukhawuleze uze notata apha.', 'XHO22'),
(1, 45, 14, 'Waziphosa entanyeni kaBhenjamin, walila; uBhenjamin wakhala entanyeni yakhe.', 'XHO22'),
(1, 45, 15, 'Ngaphezu koko, wabanga bonke abazalwana bakhe waza walilela phezu kwabo; emva koko abazalwana bakhe bathetha naye.', 'XHO22'),
(1, 45, 16, 'Lavakala ilizwi elo endlwini kaFaro, kwathiwa, Bafika abakhuluwa bakaYosefu, uFaro nabakhonzi bakhe bavuya.', 'XHO22'),
(1, 45, 17, 'Wathi uFaro kuYosefu, Yithi kubakhuluwa bakho, Enjenjalo; yalusa imfuyo yakho, uye ezweni lakwaKanan;', 'XHO22'),
(1, 45, 18, 'Thabatha uyihlo nendlu yakho, uze kum apha. Ndokunika izinto ezilungileyo zelizwe laseYiputa, udle ukutyeba kwelizwe.', 'XHO22'),
(1, 45, 19, 'Wena ke uwiselwe umthetho, yenze; bathabathe abantwana bakho, nabafazi bakho, ngeenqwelo zokulwa, nibase ezweni laseYiputa, nithwale uyihlo, nize.', 'XHO22'),
(1, 45, 20, 'Kananjalo, musani ukuyinyamekela impahla yenu; kuba zonke izinto ezilungileyo zelizwe lonke laseYiputa zezenu.', 'XHO22'),
(1, 45, 21, 'Benjenjalo ke oonyana bakaSirayeli. uYosefu wabanika iinqwelo ngokomthetho kaFaro, wabanika nomphako wendlela.', 'XHO22'),
(1, 45, 22, 'Wabanika zonke iingubo ngeengubo; kodwa uBhenjamin wamnika amakhulu amathathu eeshekele zesilivere neengubo zangaphantsi ezintlanu.', 'XHO22'),
(1, 45, 23, 'Wathumela enjenjalo kuyise; ishumi leemazi zamaesile, bethwele izinto ezilungileyo zaseYiputa, neshumi leemazi zamaesile zithwele ingqolowa, nesonka, nomphako kayise endleleni.', 'XHO22'),
(1, 45, 24, 'Wathi kubazalwana bakhe, mabahambe; bemka ke; wathi kubo, Zilindeni, ningaweli endleleni.', 'XHO22'),
(1, 45, 25, 'Benyuka bemka eYiputa, bafika ezweni lakwaKanan kuYakobi uyise.', 'XHO22'),
(1, 45, 26, 'Wamxelela wathi, uYosefu usahleli; ewe, ungumphathi welizwe lonke laseYiputa. Yaphuka intliziyo kaYakobi, ngokuba engakholwa ngabo.', 'XHO22'),
(1, 45, 27, 'Bawathetha kuye onke amazwi kaYosefu, abewathethile kubo; akubona iinqwelo awayezithumele uYosefu ukuba zimthabathe, waphila umoya kaYakobi uyise.', 'XHO22'),
(1, 45, 28, 'Wathi uSirayeli, Kwanele; uYosefu unyana wam usahleli; ndiya kumbona ndingekafi.', 'XHO22'),
(1, 46, 1, 'Wanduluka ke uSirayeli neento zonke abenazo, wafika eBher-shebha, wabingelela kuThixo kayise uIsake.', 'XHO22'),
(1, 46, 2, 'Wathetha uThixo kuSirayeli ngombono wobusuku, wathi, Yakobi, Yakobi! Wathi: Ndim lo.', 'XHO22'),
(1, 46, 3, 'Wathi, NdinguThixo, uThixo wooyihlo; musa ukoyika ukuhla uye eYiputa; ngokuba ndiya kukwenza uhlanga olukhulu khona.', 'XHO22'),
(1, 46, 4, 'Mna ndiya kuhla ndiye nawe eYiputa; ndiya kukuvusa okunene, abeke uYosefu isandla sakhe phezu kwamehlo akho.', 'XHO22'),
(1, 46, 5, 'Waphuma ke uYakobi eBher-shebha; oonyana bakaSirayeli bamthabatha uYakobi uyise, nabantwana babo, nabafazi babo, ngeenqwelo ebezithunyelwe nguFaro ukuba zimthabathe.', 'XHO22'),
(1, 46, 6, 'Bayithabatha imfuyo yabo, nengqwebo yabo ababeyifumene ezweni lakwaKanan, bafika eYiputa uYakobi noonyana bakhe bonke.', 'XHO22'),
(1, 46, 7, 'Oonyana bakhe noonyana boonyana bakhe neentombi zakhe neentombi zoonyana bakhe nayo yonke inzala yakhe beza naye eYiputa.', 'XHO22'),
(1, 46, 8, 'Ngawo la amagama oonyana bakaSirayeli abafikayo eYiputa: uYakobi noonyana bakhe: uRubhen amazibulo kaYakobi.', 'XHO22'),
(1, 46, 9, 'Oonyana bakaRubhen nguHanoki, noFalu, noHetseron, noKarmi.', 'XHO22'),
(1, 46, 10, 'Oonyana bakaSimon nguYemuweli, noYamin, no-Ohadi, noYagin, noTsohare, noSawule, unyana womKananekazi.', 'XHO22'),
(1, 46, 11, 'Oonyana bakaLevi nguGershon, noKehati, noMerari.', 'XHO22'),
(1, 46, 12, 'Oonyana bakaYuda nguAsheri; noEre, no-Onan, noShela, noPeretse, noSeraki; kodwa uEre no-Onan bafela ezweni lakwaKanan. Oonyana bakaPeretse nguHetseron noHamule.', 'XHO22'),
(1, 46, 13, 'Oonyana bakaIsakare nguTola, noPuwa, noYobhi, noShimron.', 'XHO22'),
(1, 46, 14, 'Oonyana bakaZebhulon nguSerede, noElon, noYahaleli.', 'XHO22'),
(1, 46, 15, 'Ngabo abo oonyana bakaLeya, awabazalela uYakobi, uPadan-aram, noDina intombi yakhe; Iyonke imiphefumlo yoonyana bakhe neyeentombi zakhe ingamashumi omathathu anamithathu.', 'XHO22'),
(1, 46, 16, 'Oonyana bakaGadi nguAsheri; uSiphon noHaggi, uSuni noEsbhon, uEri noArodi noAreli.', 'XHO22'),
(1, 46, 17, 'Oonyana baka-Ashere nguImna, noIshuwa, noIshvi, noBheriya, noSaraki, udade wabo; noonyana bakaBheriya; uHebhere noMalekiyeli.', 'XHO22'),
(1, 46, 18, 'Ngabo abo oonyana bakaSilipa, awathi uLabhan wamnika uLeya intombi yakhe, awayizalela uYakobi, imiphefumlo elishumi elinamithandathu.', 'XHO22'),
(1, 46, 19, 'Oonyana bakaRakeli, umkaYakobi; uYosefu noBhenjamin.', 'XHO22'),
(1, 46, 20, 'uYosefu wazalelwa ezweni laseYiputa nguManase noEfrayim, awamzalela bona uAzenati, intombi kaPotifare, umbingeleli wamnye.', 'XHO22'),
(1, 46, 21, 'Oonyana bakaBhenjamin yayinguBhela noBhegere noAshbhele, uGera noNahaman, uEhi noRoshe, uMupim noHupim noAredi.', 'XHO22'),
(1, 46, 22, 'Ngabo abo oonyana bakaRakeli, awazalelwa uYakobi; iyonke yimiphefumlo elishumi elinamine.', 'XHO22'),
(1, 46, 23, 'Oonyana bakaDan nguHushim.', 'XHO22'),
(1, 46, 24, 'Oonyana bakaNafetali ngooZingela bamatywina, noGuni, noYezere, noShilem.', 'XHO22'),
(1, 46, 25, 'Ngabo abo oonyana bakaBhiliha, awathi uLabhan wamnika uRakeli intombi yakhe, wamzalela uYakobi;', 'XHO22'),
(1, 46, 26, 'Iyonke imiphefumlo eyaya noYakobi eYiputa, eyaphuma esinqeni sakhe, ngaphandle kwabafazi boonyana bakaYakobi, yaba ngamashumi amathandathu anamithandathu.', 'XHO22'),
(1, 46, 27, 'Oonyana bakaYosefu awazalelwa eYiputa yimiphefumlo emibini;', 'XHO22'),
(1, 46, 28, 'Wathumela uYuda ukuba amkhaphele kuYosefu, ammise eGoshen; bafika ezweni laseGoshen.', 'XHO22'),
(1, 46, 29, 'uYosefu wayibopha inqwelo yakhe, wenyuka waya kumkhawulela uSirayeli uyise uGoshen, wammisa ngakuye. Waziphosa entanyeni yakhe, walila ixesha elide entanyeni yakhe.', 'XHO22'),
(1, 46, 30, 'Wathi uSirayeli kuYosefu, Makhe ndife, mandibubone ubuso bakho, ngokuba usahleli.', 'XHO22'),
(1, 46, 31, 'Wathi uYosefu kubazalwana bakhe nakubantu bendlu kayise, Ndiya kunyuka ndiye ndiye kumxelela uFaro, ndithi kuye, Baze kum abazalwana bam nabendlu kabawo, ababesezweni lakwaKanan;', 'XHO22'),
(1, 46, 32, 'Kwaye loo madoda ngabalusi, kuba umsebenzi wawo ibikukwalusa umhlambi; bezisa impahla yabo emfutshane, neenkomo zabo, neento zonke abanazo.', 'XHO22'),
(1, 46, 33, 'Wanibiza uFaro, wathi, Uyintoni na umsebenzi wenu?', 'XHO22'),
(1, 46, 34, 'Nothi, Besisebenza nabakhonzi bakho, kwasebuncinaneni bethu unangoku, ngemfuyo, thina noobawo; ukuze nime ezweni laseGoshen; kuba balisikizi kumaYiputa bonke abalusi.', 'XHO22'),
(1, 47, 1, 'uYosefu waya kumbikela uFaro, wathi, Ubawo, nabazalwana bam, bevela ezweni lakwaKanan, impahla yabo emfutshane, nemfuyo yabo, neento zonke abanazo; nabo basezweni laseGoshen.', 'XHO22'),
(1, 47, 2, 'Wathabatha amadoda amahlanu kubazalwana bakhe, wawazisa kuFaro;', 'XHO22'),
(1, 47, 3, 'Wathi uFaro kubazalwana bakhe, Uyintoni na umsebenzi wenu? Bathi kuFaro, Abakhonzi bakho ngabalusi bempahla emfutshane, thina noobawo bethu;', 'XHO22'),
(1, 47, 4, 'Bathi kuFaro, Siphambukele kweli hlabathi; ngokuba ingenatyani bampahla yabo emfutshane abakhonzi bakho; ngokuba inkulu indlala ezweni lakwaKanan. Kaloku abakhonzi bakho mabahlale ezweni laseGoshen.', 'XHO22'),
(1, 47, 5, 'Wathetha uFaro kuYosefu, wathi, Uyihlo nabantwana bakho bafikile kuwe.', 'XHO22'),
(1, 47, 6, 'Ilizwe laseYiputa liphambi kwakho; makahlale uyihlo nabantakwenu kwelona lizwe lilungileyo; wabamisa ezweni laseGoshen; ukuba uyakwazi okwenzekayo phakathi kwabo, bamise babe ngabalusi bemihlambi yam.', 'XHO22'),
(1, 47, 7, 'uYosefu wamngenisa uYakobi uyise, wammisa phambi koFaro; uYakobi wamsikelela uFaro.', 'XHO22'),
(1, 47, 8, 'Wathi uFaro kuYakobi, Mingaphi na iminyaka yobudala bakho?', 'XHO22'),
(1, 47, 9, 'Wathi uYakobi kuFaro, Imihla yeminyaka yokunduluka kwam yiminyaka elikhulu elinamanci mathathu; ootata ngexesha lokuhamba kwabo.', 'XHO22'),
(1, 47, 10, 'uYakobi wamsikelela uFaro, waphuma ebusweni bukaFaro.', 'XHO22'),
(1, 47, 11, 'uYosefu wambeka uyise nabazalwana bakhe ezweni laseYiputa, wabanika indawo yokuma kweyona ndawo intle yelizwe, ezweni laseRamses, ngokomthetho kaFaro.', 'XHO22'),
(1, 47, 12, 'uYosefu wamondla uyise, nabazalwana bakhe, nendlu yakhe yonke, ngesonka ngokwezizukulwana zabo.', 'XHO22'),
(1, 47, 13, 'Bekungekho sonka kulo lonke elimiweyo; kuba ibinkulu kunene indlala, apho lafela khona ilizwe laseYiputa nelizwe lakwaKanan ngenxa yendlala.', 'XHO22'),
(1, 47, 14, 'Wayihlanganisa uYosefu yonke imali eyayisezweni laseYiputa nasezweni lakwaKanan, yengqolowa ababeyithenga; wayingenisa uYosefu endlwini kaFaro.', 'XHO22'),
(1, 47, 15, 'Yaphela ke imali ezweni laseYiputa nasezweni lakwaKanan, eza onke amaYiputa kuYosefu, esithi, Sinike isonka; kuba imali iyasilela.', 'XHO22'),
(1, 47, 16, 'Wathi uYosefu, Zikhupheni iinkomo zenu; ndikunike wena nemfuyo yakho, yakuphela imali leyo.', 'XHO22'),
(1, 47, 17, 'Ayizisa imfuyo yawo kuYosefu, uYosefu wawanika isonka samahashe, neenkomo, neenkomo, namaesile;', 'XHO22'),
(1, 47, 18, 'Waphela loo mnyaka, beza kuye ngomnyaka wesibini, bathi kuye, Asinakukukhanyela kukumkani wethu, ukuba imali iphele; nemihlambi yethu yeenkomo, nkosi yam; akusalanga nto emehlweni enkosi yam, yimizimba yethu ke namasimi ethu kuphela.', 'XHO22'),
(1, 47, 19, 'Kutheni na ukuze sife phambi kwamehlo akho, thina nelizwe lethu? Sithenge thina namasimi ethu ngesonka, thina namasimi ethu sibe ngabakhonzi bakaFaro; usinike imbewu, siphile, singafi, lingabi senkangala ilizwe.', 'XHO22'),
(1, 47, 20, 'uYosefu wamthenga uFaro wonke umhlaba waseYiputa; kuba amaYiputa athengisa elowo ngesabelo somhlaba wakhe, kuba ayefikelwe yindlala; ilizwe laba lelikaFaro.', 'XHO22'),
(1, 47, 21, 'Ke bona abantu wabafudusela emizini, kwathabathela kwelinye icala lomda waseYiputa kwesa kwelinye icala layo.', 'XHO22'),
(1, 47, 22, 'Ngumhlaba wababingeleli wodwa angawuthenganga; ngokuba ababingeleli babebanike isabelo sikaFaro, basidla isabelo eso babesinikwe nguFaro; ke bona abathengisanga ngomhlaba wabo.', 'XHO22'),
(1, 47, 23, 'Wathi uYosefu ebantwini, Yabonani, nina namasimi enu namhla ndithengise ngawo kuFaro; uyabona, yimbewu yakho le, yihlwayele entsimini leyo;', 'XHO22'),
(1, 47, 24, 'Ngexesha lokuvuna nimnike uFaro isahlulo sesihlanu, ize izahlulo ezine zibe zezenu, njengembewu yentsimi, nezokudliwa nini neentsapho zenu, nokutya kwabantwana benu.', 'XHO22'),
(1, 47, 25, 'Bathi ke bona, Nguye owasisindisayo; Masibabalwe emehlweni okumkani wam, sibe ngabakhonzi bakaFaro;', 'XHO22'),
(1, 47, 26, 'Wawisa umthetho uYosefu ezweni laseYiputa unanamhla, wokuba uFaro athabathe esesihlanu isahlulo; ngaphandle komhlaba wababingeleli wodwa, ongengokaFaro.', 'XHO22'),
(1, 47, 27, 'Bahlala oonyana bakaSirayeli ezweni laseYiputa, ezweni laseGoshen; baba nobutyebi kulo, bande, bande kunene.', 'XHO22'),
(1, 47, 28, 'uYakobi wadla ubomi iminyaka elishumi elinesixhenxe ezweni laseYiputa; Iyonke iminyaka kaYakobi yaba yiminyaka elikhulu elinamanci mane anesixhenxe.', 'XHO22'),
(1, 47, 29, 'Lasondela ixesha lokuba uSirayeli afe; wabiza uYosefu unyana wakhe, wathi kuye, Ukuba kaloku ndibona inceba emehlweni akho, khawubeke isandla sakho phantsi kwethanga lam, ndenzele inceba nenyaniso; uze ungandingcwabi eYiputa.', 'XHO22'),
(1, 47, 30, 'Ke mna ndiya kuhlangana noobawo, yaye ngokuqinisekileyo uya kundikhupha eYiputa uze undingcwabe engcwabeni labo. Wathi: Ndiya kwenza njengoko uthethile.', 'XHO22'),
(1, 47, 31, 'Wathi ke yena, Ningandinaki; Wamfungela ke. Waqubuda uSirayeli emantloko esingqengqelo.', 'XHO22'),
(1, 48, 1, 'Kwathi emveni kwezi zinto, kwathiwa kuYosefu, Uyabona, uyihlo uyafa; Wathabatha oonyana bakhe bobabini, uManase noEfrayim, wahamba naye.', 'XHO22'),
(1, 48, 2, 'Waxelelwa uYakobi, kwathiwa, Nanku unyana wakho uYosefu esiza kuwe, uSirayeli wamomeleza, wahlala ezingqengqelweni.', 'XHO22'),
(1, 48, 3, 'Wathi uYakobi kuYosefu, uThixo uSomandla wabonakala kum eLuse, ezweni lakwaKanan, wandisikelela.', 'XHO22'),
(1, 48, 4, 'Wathi kum, Yabona, ndiya kukwandisa, ndinandise, ndikwenze isikhungu seentlanga; Lo mhlaba ndiwunike imbewu yakho emva kwakho, ube lilifa lakho ngonaphakade.', 'XHO22'),
(1, 48, 5, 'Ngako oko oonyana bakho ababini, owabazalelwayo ezweni laseYiputa, ndingekafiki kuwe eYiputa, ngabam, uEfrayim noManase; njengoRubhen noSimon baya kuba ngabam.', 'XHO22'),
(1, 48, 6, 'Oonyana bakho oya kubazala emva kwabo baya kuba ngabakho yaye baya kubizwa ngegama labazalwana babo elifeni labo.', 'XHO22'),
(1, 48, 7, 'Ke mna, ekundulukeni kwam ePadan, wafela nam uRakeli ezweni lakwaKanan, endleleni, ndathi ndakufika e-Efrata; Ndamngcwabela khona endleleni yase-Efrata; yiBhetelehem ke leyo.', 'XHO22'),
(1, 48, 8, 'uSirayeli wababona oonyana bakaYosefu, wathi, Ngoobani na abo?', 'XHO22'),
(1, 48, 9, 'Wathi uYosefu kuyise, Ngoonyana bam aba andiphiwe nguThixo kule ndawo. Wathi, Ziziseni kum ndibasikelele.', 'XHO22'),
(1, 48, 10, 'Ayemnyama amehlo amaSirayeli ngenxa yokwaluphala, ukuba angabi nakubona. Wazisondeza kuye; wabanga, wabawola.', 'XHO22'),
(1, 48, 11, 'Wathi uSirayeli kuYosefu, Bendingacingi ukuba ndokubona ubuso bakho; Uyabona, uThixo undibonisile, nembewu yakho.', 'XHO22'),
(1, 48, 12, 'uYosefu wabakhupha phakathi kwamadolo akhe, waqubuda ngobuso bakhe.', 'XHO22'),
(1, 48, 13, 'uYosefu wabathabatha bobabini, uEfrayim ngesokunene sakhe ngasekhohlo kukaSirayeli, noManase ngesokhohlo sakhe, ngasekunene kukaSirayeli, wabazisa kuye.', 'XHO22'),
(1, 48, 14, 'uSirayeli wasolula esokunene sakhe, wasibeka entloko kuEfrayim, engomncinane, wasibeka esokhohlo entlokweni kaManase, ezihambisa ngezandla zakhe ngengqiqo; ngokuba uManase ebengowamazibulo.', 'XHO22'),
(1, 48, 15, 'Wamsikelela uYosefu, wathi, uThixo abahamba phambi kwakhe oobawo uAbraham noIsake, uThixo ondingenise yonke imihla yobomi bam, unanamhla.', 'XHO22'),
(1, 48, 16, 'Isithunywa eso sandihlangula kubo bonke ububi siyawasikelela amakhwenkwe; malibizwe kubo igama lam, negama looyise babo, uAbraham noIsake; kwaye mabakhule babe baninzi ehlabathini.', 'XHO22'),
(1, 48, 17, 'Wabona uYosefu ukuba uyise usibeke isandla sakhe sokunene entlokweni kaEfrayim, wonile;', 'XHO22'),
(1, 48, 18, 'Wathi uYosefu kuyise, Makube loo nto, bawo, kuba lo ngowamazibulo; beka isandla sakho sokunene phezu kwentloko yakhe.', 'XHO22'),
(1, 48, 19, 'Akavuma uyise, wathi, Ndiyakwazi oku, nyana wam, ndiyakwazi oku; uya kuba luhlanga, ube mkhulu; kodwa okunene umzalwana wakhe uya kumdlula, nabantwana bakhe babe ziintlanga ezininzi.', 'XHO22'),
(1, 48, 20, 'Wabasikelela ngaloo mini, wathi, Ngawe uSirayeli uya kusikelela, ngokuthi, uThixo wokwenza ube njengoEfrayim nanjengoManase; waza wabeka uEfrayim phambi kukaManase.', 'XHO22'),
(1, 48, 21, 'Wathi uSirayeli kuYosefu, Yabona, ndiza kufa; kodwa uThixo uya kuba nani, anibuyisele ezweni looyihlo.', 'XHO22'),
(1, 48, 22, 'Kananjalo ndakunika isiqwenga somhlaba ngaphezu koonyana bakho, endasithabatha ngekrele lam nesaphetha sam esandleni sama-Amori.', 'XHO22'),
(1, 49, 1, 'Wababiza ke uYakobi oonyana bakhe, wathi, Zihlanganiseni, ndinixelele okuya kunihlela ngemihla yokugqibela.', 'XHO22'),
(1, 49, 2, 'Buthani niphulaphule, nina bonyana bakaYakobi, mphulaphule uSirayeli uyihlo.', 'XHO22'),
(1, 49, 3, 'Rubhen, wena mazibulo am, mandla am, ngqalo yokuqina kwam, uzuko lobuqaqawuli, uzuko lonamandla.', 'XHO22'),
(1, 49, 4, 'Ukuba uthe waguquguquka njengamanzi, akusayi kuba ncamisa kunje; kuba unyuke waya esililini sikayihlo; uze uyihlambele; wenyuka waya ebhedini yam.', 'XHO22'),
(1, 49, 5, 'uSimon noLevi ngabazalwana; izixhobo ezikhohlakeleyo kwinkampu yabo.', 'XHO22'),
(1, 49, 6, 'Owu mphefumlo wam, musa ukungena emfihlakalweni yabo; Ungazibandakanyi nebandla labo, sithandwa sam, ngokuba ngomsindo wabo babulele umntu, baludiliza udonga ngabom.', 'XHO22'),
(1, 49, 7, 'Uqalekisiwe umsindo wabo, ngokuba unobungcwangu: nomsindo wabo, ngenxa yokuba wawukhohlakele: Ndiya kubahlula kwaYakobi ndize ndibachithachithe kwaSirayeli.', 'XHO22'),
(1, 49, 8, 'Yuda, nguwe oya kubancoma abazalwana bakho; isandla sakho sibe sezintanyeni zeentshaba zakho; Oonyana bakayihlo baya kuqubuda kuwe;', 'XHO22'),
(1, 49, 9, 'uYuda yingonyama entsha; Unyuke waya kuphanga, nyana wam; ngubani oza kumvusa?', 'XHO22'),
(1, 49, 10, 'Intonga yasebukhosini ayiyi kumka kuYuda, nentonga yomlawuli phakathi kweenyawo zakhe ade afike uShilo; kuya kubakho kuye ukuhlanganisana kweentlanga.', 'XHO22'),
(1, 49, 11, 'Ngukhuleka emdiliyeni imazi yakhe, Nemazi yakhe komdiliya onyuliweyo; Wazihlamba iingubo zakhe ngewayini, Neengubo zakhe ngegazi leediliya.', 'XHO22'),
(1, 49, 12, 'Amehlo akhe aya kubabomvu yiwayini, Amazinyo akhe abe mhlophe ngamasi;', 'XHO22'),
(1, 49, 13, 'uZebhulon wohlala echwebeni lolwandle; liya kuba lizibuko leenqanawa; nelizwe lakhe liya kufikelela eSidon.', 'XHO22'),
(1, 49, 14, 'uIsakare yi-esile elukhuni, Ulala phakathi kweempahla ezimbini;', 'XHO22'),
(1, 49, 15, 'Kwaye wabona ukuba ukuphumla kulungile kwaye ihlabathi limnandi; Wagoba igxalaba ukuba athwale, waba ngumbuthi werhafu.', 'XHO22'),
(1, 49, 16, 'Ke abantu bakhe baya kugweba njengesinye sezizwe zakwaSirayeli.', 'XHO22'),
(1, 49, 17, 'Kubekho inyoka endleleni, neenyoka endleleni, zililuma izithende zehashe, awe olikhweleyo.', 'XHO22'),
(1, 49, 18, 'Ndikhangele phambili kusindiso lwakho, Yehova.', 'XHO22'),
(1, 49, 19, 'uGade, liqela lezaphuli-mthetho eziya kumoyisa, kodwa ekugqibeleni uya koyisa.', 'XHO22'),
(1, 49, 20, 'uAshere woba sityebi isonka sakhe, amnike izinto ezinqwenelekayo zokumkani.', 'XHO22'),
(1, 49, 21, 'uNafetali yimazi yexhama; unamazwi amahle.', 'XHO22'),
(1, 49, 22, 'uJosefu lisebe eliqhamayo, umthi weziqhamo ngasemthonjeni; omasebe awela eludongeni;', 'XHO22'),
(1, 49, 23, 'Abalusi besaphetha babenza buhlungu kakhulu ngenxa yakhe;', 'XHO22'),
(1, 49, 24, 'uSithe nkqi isaphetha sakhe, nezandla zezandla zakhe zomelela ngeRafa kaYakobi; (ngoko ke, umalusi, iliwa lakwaSirayeli;)', 'XHO22'),
(1, 49, 25, 'NgoThixo kayihlo oya kukunceda; yaye ngoSomandla uya kukusikelela ngeentsikelelo ezivela emazulwini phezulu, neentsikelelo zonogumbe ohleli ezantsi, neentsikelelo ezivela esizalweni nasesizalweni.', 'XHO22'),
(1, 49, 26, 'Iintsikelelo zikayihlo zazingaphezu kweentsikelelo zookhokho ukusa ekupheleni kweenduli ezingapheliyo, zibe entlokweni kaYosefu nasekakayizeni yalowo wahlukanayo nabantakwabo.', 'XHO22'),
(1, 49, 27, 'uBhenjamin uqwenga njengengcuka; kusasa uya kudla okuphangiweyo, ebusuku abelane okuphangiweyo.', 'XHO22'),
(1, 49, 28, 'Bonke aba zizizwe zakwaSirayeli ezilishumi elinazibini; Wenjenje ukuthetha kubo uyise, wabasikelela; wabasikelela elowo ngeentsikelelo zakhe.', 'XHO22'),
(1, 49, 29, 'Wabawisela umthetho, wathi kubo, Ndiza kuhlanganiselwa kwabakowethu; ndingcwabele koobawo emqolombeni osentsimini kaEfron umHeti;', 'XHO22'),
(1, 49, 30, 'emqolombeni nakwintsimi yaseMakapela, ekufuphi kwaMamre, ezweni lakwaKanan, awawuthengayo uAbraham ndawonye nentsimi kuEfron umHeti, ukuba abe yindawo yokungcwaba.', 'XHO22'),
(1, 49, 31, 'Kulapho bangcwatyelwa khona uAbraham noSara umkakhe; kulapho bangcwatyelwa khona uIsake noRebheka umkakhe; ndamfihla khona uLiya.', 'XHO22'),
(1, 49, 32, 'Umhlaba waloo ntsimi nomqolomba okuyo zathengwa koonyana bakaHeti.', 'XHO22'),
(1, 49, 33, 'Wagqiba ke uYakobi ukuyolela oonyana bakhe, wazibeka esingqengqelweni iinyawo zakhe, wafa, wahlanganiselwa kwabakowabo.', 'XHO22'),
(1, 50, 1, 'uYosefu waziphosa ebusweni bukayise, walila phezu kwakhe, wamanga.', 'XHO22'),
(1, 50, 2, 'uYosefu wabawisela umthetho abakhonzi bakhe, ongugqirha, ukuba somele uyise. Ugqirha womisa isidumbu sikaSirayeli.', 'XHO22'),
(1, 50, 3, 'Kwadlula iintsuku ezimashumi mane kuye; ngokuba yazaliseka imihla yokumiswa kwesidumbu; amlilela amaYiputa iintsuku ezimashumi asixhenxe.', 'XHO22'),
(1, 50, 4, 'Kwathi, yakuzaliseka imihla yobumnyama bakhe, wathetha uYosefu kubantu bendlu kaFaro, esithi, Ukuba ndibonise inceba emehlweni enu, thethani esiva uFaro, nithi,', 'XHO22'),
(1, 50, 5, 'Ubawo wandifungela, esithi, Yabona, ndiyafa; engcwabeni lam endizimbeleyo ezweni lakwaKanan, undingcwabele khona. Kaloku ke mandinyuke ndiye kungcwaba ubawo, ndibuye ndibuye.', 'XHO22'),
(1, 50, 6, 'Wathi uFaro, Nyuka uye kumngcwaba uyihlo njengoko wafungayo.', 'XHO22'),
(1, 50, 7, 'Wenyuka ke uYosefu waya kumngcwaba uyise; benyuka naye bonke abakhonzi bakaFaro, nezikhulu zendlu yakhe, nezikhulu zonke zelizwe laseYiputa.', 'XHO22'),
(1, 50, 8, 'Yonke indlu kaYosefu, nabazalwana bakhe, nendlu kayise, yashiya abantwana bayo kuphela, nemfuyo yabo, nemfuyo yabo, ezweni laseGoshen.', 'XHO22'),
(1, 50, 9, 'Wenyuka yena eneenqwelo zokulwa, nabamahashe, yaba yindimbane enkulu kakhulu.', 'XHO22'),
(1, 50, 10, 'Bafika esandeni sika-Atadi, esiphesheya kweYordan, bakhala khona ngesikhalo esikhulu, esinzima kunene; wazilela uyise iintsuku ezisixhenxe.', 'XHO22'),
(1, 50, 11, 'Abemi belo zwe, amaKanan, bakubona ukulila engcwabeni lika-Atadi, bathi, Eso sikhulu isililo samaYiputa; ke ngoko, eso sixeko sasibizwa ngokuba yiAbhele-mitserayim, ngaphesheya kweYordan.', 'XHO22'),
(1, 50, 12, 'Benza ke oonyana bakhe njengoko wabawisela umthetho ngako.', 'XHO22'),
(1, 50, 13, 'Bamthwala oonyana bakhe, bamsa ezweni lakwaKanan, bamngcwabela emqolombeni osentsimini yaseMakapela, awawuthengayo uAbraham ndawonye nelizwe, ukuba ube lingcwaba lakhe uEfron umHeti phambi kowakwaMamre.', 'XHO22'),
(1, 50, 14, 'uYosefu wabuyela eYiputa ke, yena nabazalwana bakhe, nabo bonke abenyuka naye beya kumngcwaba uyise, ekungcwabeni kwakhe uyise.', 'XHO22'),
(1, 50, 15, 'Ke kaloku abakhuluwa bakaYosefu babona ukuba uyise wabo ufile, bathi, Mhlawumbi uYosefu uya kusicaphukela, abubuyisele kanye na kuthi bonke ububi esimenzele bona.', 'XHO22'),
(1, 50, 16, 'Bathumela umthunywa kuYosefu, besithi, Uyihlo wawisa umthetho engekafi, wathi,', 'XHO22'),
(1, 50, 17, 'Nothi kuYosefu, Ubuxolele abazalwana bakho ubugwenxa babo nesono sabo; ngokuba bamonile; sixolele izono zabakhonzi boThixo wooyihlo. Walila uYosefu bakuthetha naye.', 'XHO22'),
(1, 50, 18, 'Baya nabakhuluwa bakhe, baziphosa phantsi phambi kwakhe; bathi, Yabona, singabakhonzi bakho.', 'XHO22'),
(1, 50, 19, 'Wathi uYosefu kubo, Musani ukoyika;', 'XHO22'),
(1, 50, 20, 'Ke nina nikhumbula ububi ngam; kodwa uThixo wacinga ukwenza okulungileyo, njengoko kusenjalo namhla, ukuba kusinde abantu abaninzi.', 'XHO22'),
(1, 50, 21, 'Musani ukoyika ngoku; ndonondla nina, nabantwana benu; Wabathuthuzela waza wathetha nabo ngobubele.', 'XHO22'),
(1, 50, 22, 'uYosefu wahlala eYiputa, yena nendlu kayise; uYosefu wahlala iminyaka elikhulu elinashumi-nye.', 'XHO22'),
(1, 50, 23, 'uYosefu wabona oonyana bakaEfrayim kwesesithathu isizukulwana; oonyana bakaMagire, unyana kaManase, babezalelwe emadolweni kaYosefu.', 'XHO22'),
(1, 50, 24, 'Wathi uYosefu kubakhuluwa bakhe, Ndiza kufa; uThixo okunene uya kunigcina, anikhuphe kweli hlabathi, anise ezweni abelifungele uAbraham, noIsake, noYakobi.', 'XHO22'),
(1, 50, 25, 'Wabafungisa uYosefu oonyana bakaSirayeli, esithi, uYehova woniva okunene, niwakhuphe ke amathambo am.', 'XHO22'),
(1, 50, 26, 'Wafa ke uYosefu engominyaka ilikhulu linashumi-nye; bawumisa umzimba wakhe; wafakwa etyeyeni eYiputa.', 'XHO22'),
(2, 1, 1, 'Ngawo la amagama oonyana bakaSirayeli abaya eYiputa; elowo nendlu yakhe beza noYakobi.', 'XHO22'),
(2, 1, 2, 'uRubhen, uSimon, uLevi noYuda,', 'XHO22'),
(2, 1, 3, 'noIsakare, noZebhulon, noBhenjamin;', 'XHO22'),
(2, 1, 4, 'uDan noNafetali, uGadi noAshere.', 'XHO22'),
(2, 1, 5, 'Iyonke imiphefumlo eyaphumayo esinqeni sikaYakobi yayiyimiphefumlo emashumi asixhenxe, ngenxa yokuba uYosefu wayeseseYiputa.', 'XHO22'),
(2, 1, 6, 'Wafa ke uYosefu, nabazalwana bakhe bonke, nesizwe sonke.', 'XHO22'),
(2, 1, 7, 'Banda oonyana bakaSirayeli, banda kunene, banda, baba namandla; wazala umhlaba ngabo.', 'XHO22'),
(2, 1, 8, 'Kwavela omnye ukumkani waseYiputa, awayengamazi uYosefu.', 'XHO22'),
(2, 1, 9, 'Wathi ebantwini bakhe, Yabonani, abantu oonyana bakaSirayeli baninzi, banamandla kunathi.', 'XHO22'),
(2, 1, 10, 'Yizani basebenze ngengqiqo; ukuze bangande, kwaye ukuba kuqhambuka nayiphi na imfazwe, nabo bazimanya neentshaba zethu kwaye balwe nathi baze ngaloo ndlela barhoxe ehlabathini.', 'XHO22'),
(2, 1, 11, 'Bamisa phezu kwabo ababhali, babacinezela ngemithwalo yabo. Bakha izixeko ezingooFaro nePitom neRamase.', 'XHO22'),
(2, 1, 12, 'Okukhona babacinezelayo, kokukhona basasazekayo; Baba buhlungu ngoonyana bakaSirayeli.', 'XHO22'),
(2, 1, 13, 'AmaYiputa abakhaba nzima oonyana bakaSirayeli.', 'XHO22'),
(2, 1, 14, 'Babumisa ubomi babo ebukhoboka obunzima, ngesijwili, nangokwakha izitena, nangayo yonke imisebenzi yasendle; yonke inkonzo yabo ababekhonza ngayo yayinzima.', 'XHO22'),
(2, 1, 15, 'Wathi ukumkani waseYiputa kubazalisikazi abangamaHebherekazi, igama lomnye belinguShifera, igama lomnye belinguPuwa.', 'XHO22'),
(2, 1, 16, 'Wathi, Njengomzalisikazi wamaHebhere, nabo basesihlalweni; ukuba yinkwenkwe, yibulaleni; ke ukuba ithe yayintombi, yodla ubomi.', 'XHO22'),
(2, 1, 17, 'Abazalisikazi boyika uThixo, àbenza ngokomyalelo wokumkani waseYiputa, bazala amakhwenkwe.', 'XHO22'),
(2, 1, 18, 'Wababiza ukumkani waseYiputa abazalisikazi, wathi kubo, Yini na ukuba nenze le nto, niwasindise amakhwenkwe?', 'XHO22'),
(2, 1, 19, 'Bathi abazalisikazi kuFaro, Kungokuba amaHebherekazi akafani namaYiputa; ngokuba babesahleli, bazala, bengekafiki kubo abazalisikazi.', 'XHO22'),
(2, 1, 20, 'Ngoko uThixo wabaphatha kakuhle abo bazalisikazi, yaye uhlanga lwanda, lwaba namandla kakhulu.', 'XHO22'),
(2, 1, 21, 'Ngenxa yokuba abazalisikazi babemoyika uThixo, wabakhela izindlu.', 'XHO22'),
(2, 1, 22, 'uFaro wabawisela umthetho abantu bakhe bonke, wathi, Bonke oonyana abathe bazalwa, baphoseni emlanjeni umNayile, zonke iintombi nizigcine ziphila.', 'XHO22'),
(2, 2, 1, 'Kwaphuma indoda yendlu kaLevi, yazeka intombi yakwaLevi.', 'XHO22'),
(2, 2, 2, 'Wamitha umfazi lowo, wazala unyana; wambona ukuba ungumntwana omhle, wamfihla iinyanga zantathu.', 'XHO22'),
(2, 2, 3, 'Akuba engasenako ukumngcwaba, wamthabathela umkhombe wengcongolo, wawutyabeka ngodaka nebhitumene, wamfaka kuwo umntwana; Wawabeka elunxwemeni lomlambo.', 'XHO22'),
(2, 2, 4, 'Udade wabo wema mgama, ukuze ayazi into eya kwenzeka kuye.', 'XHO22'),
(2, 2, 5, 'Yehla ke intombi kaFaro, isiya kuqubha emlanjeni; nantso intombazana ihamba elunxwemeni lomlambo; Wawubona umkhombe phakathi kwemikhumbi, wathuma umkhonzazana wakhe, ukuba awuthabathe.', 'XHO22'),
(2, 2, 6, 'Akuba eyivula, wabona umntwana, nanko umntwana ekhala. Wasikwa yimfesane kakhulu, wathi, Ngomnye wabantwana bamaHebhere lo.', 'XHO22'),
(2, 2, 7, 'Wathi udade wabo kwintombi kaFaro, Ndikubize na umfazi ongumnyisi phakathi kwamaHebherekazi, ukuba umanyise umntwana?', 'XHO22'),
(2, 2, 8, 'Yathi intombi kaFaro kuye: “Hamba! Yaya ke intombi leyo, yabiza unina womntwana.', 'XHO22'),
(2, 2, 9, 'Yathi intombi kaFaro kuye, Thabatha lo mntwana, umzise kum; ndokunika umvuzo wakho mna. Wamthabatha ke umfazi lowo umntwana, wamondla.', 'XHO22'),
(2, 2, 10, 'Wakhula umntwana, wamsa kwintombi kaFaro, waba ngunyana wayo. Wabiza uMoses, wathi, Kungokuba ndimkhuphe emanzini.', 'XHO22'),
(2, 2, 11, 'Ke kaloku, akukhula uMoses, waphuma waya kunyamekela abazalwana bakhe nemithwalo yabo; wabulala umYiputa, owabulala umzalwana wakhe ongumHebhere.', 'XHO22'),
(2, 2, 12, 'Wabhekabheka, wabona ukuba akukho mntu, wambulala umYiputa lowo, wamngcwabela entlabathini.', 'XHO22'),
(2, 2, 13, 'Kwathi ngomhla wesibini ekuphumeni kwakhe, wabona amadoda amabini angamaHebhere ephikisana, wathi kowonileyo, Uyalibetha iqabane lakho na?', 'XHO22'),
(2, 2, 14, 'Wathi, Umiswe ngubani na ukuba ube ngukumkani nomgwebi wethu? Uza kundibulala na nam, njengokuba umbulele umYiputa? Woyika uMoses, wathi, Iyaziwa le nto.', 'XHO22'),
(2, 2, 15, 'uFaro wayiva le nto, wafuna ukumbulala uMoses. Wabaleka uMoses kuFaro, waya kuma ezweni lakwaMidiyan, wahlala ngasequleni.', 'XHO22'),
(2, 2, 16, 'Umbingeleli wakwaMidiyan wayeneentombi ezisixhenxe;', 'XHO22'),
(2, 2, 17, 'Bafika abalusi, bazigxotha, wesuka uMoses wazinceda, wayiseza impahla yazo emfutshane.', 'XHO22'),
(2, 2, 18, 'Bafika kuRehuweli uyise, wathi, Nifike kamsinya kangakanani na namhlanje?', 'XHO22'),
(2, 2, 19, 'Bathi, Wasihlangula umYiputa esandleni sabalusi, wasenzela amanzi, wayiseza impahla emfutshane.', 'XHO22'),
(2, 2, 20, 'Wathi ezintombini zakhe, Niphi na? Yini na ukuba nimshiye umntu ombizele ukuba adle isonka?', 'XHO22'),
(2, 2, 21, 'Wavuma uMoses ukuhlala naloo ndoda, wamnika uMoses uTsipora, intombi yayo.', 'XHO22'),
(2, 2, 22, 'Yazala unyana, yathi igama lakhe nguGershom, kuba ubesithi, Ndingumphambukeli ezweni lolunye uhlanga.', 'XHO22'),
(2, 2, 23, 'Ekuhambeni kwexesha wafa ukumkani waseYiputa. Bancwina oonyana bakaSirayeli ngenxa yobukhoboka, balila, bakhala ebukhobokeni kuThixo.', 'XHO22'),
(2, 2, 24, 'uThixo wayiva imithandazo yabo, wawukhumbula uThixo umnqophiso wakhe noAbraham, noIsake, noYakobi.', 'XHO22'),
(2, 2, 25, 'Wangena uThixo koonyana bakaSirayeli, wababona uThixo.', 'XHO22'),
(2, 3, 1, 'uMoses wayesalusa impahla emfutshane kaYitro, umkakhe, umbingeleli wakwaMidiyan; Wawubuyisela umhlambi umhlambi, waya entlango, waya kufika entabeni yoThixo oyinyaniso, ukuya kufikelela eHorebhe.', 'XHO22'),
(2, 3, 2, 'Kwabonakala isithunywa sikaYehova kuye, siselangatyeni lomlilo etyholweni elinameva.', 'XHO22'),
(2, 3, 3, 'Wathi uMoses, Mandingalahleki; ndiwubone lo mbono mkhulu, liyintoni na ityholo elingatshiyo.', 'XHO22'),
(2, 3, 4, 'Wathi uYehova akubona ukuba ujikile ukuba akhangele, uThixo wambiza etyholweni elinameva, wathi, Moses, Moses! Wathi: Ndim lo.', 'XHO22'),
(2, 3, 5, 'Wathi, Musa ukusondela; khulula iimbadada ezinyaweni zakho, kuba loo ndawo umi kuyo ingumhlaba ongcwele.', 'XHO22'),
(2, 3, 6, 'Wathi, NdinguThixo kayihlo, uThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi. Wabusithelisa uMoses ubuso bakhe; ngokuba ebesoyika ukukhangela kuThixo.', 'XHO22'),
(2, 3, 7, 'Wathi uYehova, Ndikubonile ukuphathwa kakubi kwabantu bam eYiputa, nokukhala kwabo ndikuvile ngenxa yabakhonzi babo; ngokuba ndiyazazi iinkedama zabo;', 'XHO22'),
(2, 3, 8, 'Ndehla ke ukuze ndibahlangule esandleni samaYiputa, ndibaqeqeshe, basuke kwelo lizwe, baye ezweni elihle, elikhulu, ezweni elibaleka amasi nobusi; ezweni lamaKanan, namaHeti, nama-Amori, namaPerizi, namaHeti, namaYebhusi;', 'XHO22'),
(2, 3, 9, 'Yabona, ukukhala koonyana bakaSirayeli, kwafika kum, ndabuya ndakubona ukuxina abaxina ngako amaYiputa.', 'XHO22'),
(2, 3, 10, 'Hamba ngoku, ndikuthume kuFaro, ubakhuphe eYiputa abantu bam, oonyana bakaSirayeli.', 'XHO22'),
(2, 3, 11, 'Wathi uMoses kuThixo, Ndingubani na ukuba ndingade ndiye kuFaro, ukuba ndibakhuphe eYiputa oonyana bakaSirayeli?', 'XHO22'),
(2, 3, 12, 'Wathi, Ndiya kuba nawe; Nangu ke umqondiso wokuba ndikuthumile mna: Ukuba ubakhuphile abantu eYiputa, khonzani uThixo kule ntaba.', 'XHO22'),
(2, 3, 13, 'Wathi uMoses kuThixo, Uyabona, ukuba ndiya koonyana bakaSirayeli, ndithi kubo, uThixo wooyihlo undithumile kuni; bathi kum, Ngubani na igama lakhe? Ndiza kubaxelela ntoni?', 'XHO22'),
(2, 3, 14, 'Wathi uThixo kuMoses, Ndinguye eNdinguye; Wathi, Uze uthi koonyana bakaSirayeli, NDINGUYE undithumile kuni.', 'XHO22'),
(2, 3, 15, 'Wathi uThixo kuMoses, Wothi koonyana bakaSirayeli, uYehova, uThixo wooyihlo, uThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi, undithumile kuni: igama lam ngonaphakade, yaye esi sisikhumbuzo kwizizukulwana ngezizukulwana.', 'XHO22'),
(2, 3, 16, 'Yiya uwahlanganise amadoda amakhulu akwaSirayeli, uthi kuwo, uYehova, uThixo wooyihlo, uThixo ka-Abraham, kaIsake, kaYakobi, undityhilele, wathi, Ndinivelele nina, ndiyibonile into eniyenzileyo uya kwenza eYiputa:', 'XHO22'),
(2, 3, 17, 'Ndathi ke, Ndiya kunikhupha ebugxwayibeni baseYiputa, ndinise ezweni lamaKanan, namaHeti, nama-Amori, namaPerizi, namaHeti, namaYebhusi, ezweni elizele ngamasi nobusi.', 'XHO22'),
(2, 3, 18, 'Yizani kuye, nina namadoda amakhulu akwaSirayeli, kukumkani waseYiputa, nithi kuye, uYehova, uThixo wamaHebhere, makasincede; ngoku makhe sihambe entlango uhambo lweentsuku ezintathu, siye kubingelela kuYehova uThixo wethu.', 'XHO22'),
(2, 3, 19, 'Ndiyazi ukuba ukumkani waseYiputa akayi kunikhulula, kwanangesandla esomeleleyo.', 'XHO22'),
(2, 3, 20, 'Ndiya kusolula isandla sam, ndiloyise iYiputa ngemisebenzi yam yonke ebalulekileyo, endiya kuyenza kulo; kwaye emva koko uya kunivumela ukuba nihambe.', 'XHO22'),
(2, 3, 21, 'Aba bantu ndiya kubababala phambi kwamaYiputa; ekuphumeni kwenu, aniyi kuhamba nedwa.', 'XHO22'),
(2, 3, 22, 'Kodwa umfazi ngamnye uya kuboleka isilivere negolide nempahla yokunxiba kwintsapho yakhe nomfazi ohlala endlwini yakhe; ukutsalele koonyana bakho neentombi zakho; bawaphange amaYiputa.', 'XHO22'),
(2, 4, 1, 'Waphendula uMoses wathi, Abakholwa ndim, abaliphulaphula izwi lam; kuba bebesithi, uYehova akazibonakalisi kuni.', 'XHO22'),
(2, 4, 2, 'Wathi uYehova kuye, Yintoni na loo nto isesandleni sakho? Wathi: Indlela.', 'XHO22'),
(2, 4, 3, 'Wathi, Zikhahleleni phantsi. Wayiphosa phantsi, yaba yinyoka; wabaleka uMoses.', 'XHO22'),
(2, 4, 4, 'Wathi uYehova kuMoses, Yolula isandla sakho, uyibambe ngomsila; Wasolula isandla sakhe, wayithabatha, yaba yintonga esandleni sakhe;', 'XHO22'),
(2, 4, 5, 'ukuze bakholwe ukuba uYehova, uThixo wooyise, uThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi, ubonakele kuwe.', 'XHO22'),
(2, 4, 6, 'Waza uYehova wathi kuye, Faka isandla sakho esifubeni sakho. Wasifaka isandla sakhe esifubeni, wathi akusikhupha, wabona ukuba isandla sakhe simhlophe njengekhephu.', 'XHO22'),
(2, 4, 7, 'Wathi, Sibuyisele isandla sakho esifubeni sakho. Waphinda wabeka isandla sakhe esifubeni sakhe; Wayikrazula isifuba sayo, yasuka yaba njengenyama yayo.', 'XHO22'),
(2, 4, 8, 'Kothi, ukuba abakholwa kuni, nokuba bathe baliva ilizwi lomqondiso wokuqala, bakholwe lilizwi lomqondiso wokugqibela.', 'XHO22'),
(2, 4, 9, 'Ukuba abathanga bakholwe yile miqondiso yomibini, abaliphulaphula nelizwi lakho, wothabatha amanzi emlanjeni, uwaphalazele emhlabeni owomileyo, nasemanzini owathimbileyo; umlambo uya kujika ube ligazi emhlabeni owomileyo.', 'XHO22'),
(2, 4, 10, 'Wathi uMoses kuYehova, Camagu, Nkosi yam! Ke mna ndilulwimi olugudileyo, ndilulwimi olugudileyo.', 'XHO22'),
(2, 4, 11, 'Wathi uYehova kuye, Ngubani na obeke umlomo emntwini? Ngubani na osisimumu okanye osisithulu okanye obonayo okanye oyimfama? Andinayo na iNkosi?', 'XHO22'),
(2, 4, 12, 'Hamba ngoku, ndoba nomlomo wakho mna, ndikubonise into oya kuyithetha.', 'XHO22'),
(2, 4, 13, 'Wathi, Nkosi, thumela ngesandla salowo uya kumthuma.', 'XHO22'),
(2, 4, 14, 'Wavutha umsindo kaYehova kuMoses, wathi, Akanguye na umkhuluwa wakho uAron umLevi? Ndiyazi ukuba uyakwazi ukuthetha kakuhle. Uyabona, uyeza kukukhawulela, kwaye xa ubona, uya kuvuya entliziyweni yakhe.', 'XHO22'),
(2, 4, 15, 'Thetha naye, ubeke amazwi emlonyeni wakhe; mna ndoba nomlomo wakho nomlomo wakhe, ndinibonise into eniya kuyenza.', 'XHO22'),
(2, 4, 16, 'Yena uya kuba ngummeli wakho ebantwini, abe ngummeli wakho endaweni yomlomo, wena ube ngowakhe esikhundleni sikaThixo.', 'XHO22'),
(2, 4, 17, 'Thabatha le ntonga esandleni sakho, uya kwenza ngayo imiqondiso;', 'XHO22'),
(2, 4, 18, 'Wemka ke uMoses, wabuyela kuYitro, uyise womkakhe, wathi kuye, Makhe ndibuyele kubazalwana bam eYiputa, ndibone ukuba basahleli na. Wathi uYitro kuMoses, Hamba ngoxolo.', 'XHO22'),
(2, 4, 19, 'Wathi uYehova kuMoses kwaMidiyan, Buyela eYiputa; kuba afile onke amadoda abeza kukubulala.', 'XHO22'),
(2, 4, 20, 'uMoses wathabatha umkakhe noonyana bakhe, wabakhwelisa eesileni, wabuyela ezweni laseYiputa; Wayiphatha uMoses intonga kaThixo ngesandla sakhe.', 'XHO22'),
(2, 4, 21, 'Wathi uYehova kuMoses, Ukuba uthe wabuyela eYiputa, qiniseka ukuba uyayenza yonke imiqondiso, endiyibeke esandleni sakho phambi koFaro; Ke mna ndiya kuyenza lukhuni intliziyo yakhe, angabandululi abantu.', 'XHO22'),
(2, 4, 22, 'Wothi kuFaro, Utsho uYehova ukuthi, uSirayeli ngunyana wam wamazibulo;', 'XHO22'),
(2, 4, 23, 'Kwaye ndithi kuni: Myeke unyana wam, makandikhonze; ukuba uthe akwavuma ukumkhulula, uyabona, ndombulala unyana wakho wamazibulo.', 'XHO22'),
(2, 4, 24, 'Uthe akudlula, iNkosi yamhlangabeza, yafuna ukumbulala.', 'XHO22'),
(2, 4, 25, 'uTsipora wathabatha intshengece, wanqumla incam yelungu lobudoda lonyana wakhe, wayiphosa ezinyaweni zakhe, wathi, Uyindoda yamagazi kum.', 'XHO22'),
(2, 4, 26, 'Waza wamkhulula; Wathi yena, Uyindoda enegazi ngenxa yolwaluko.', 'XHO22'),
(2, 4, 27, 'Wathi uYehova kuAron, Hamba uye kukhawulela uMoses entlango. Waya kumkhawulela entabeni kaThixo, wamanga.', 'XHO22'),
(2, 4, 28, 'uMoses wamxelela uAron onke amazwi kaYehova, abemthume wona, nemiqondiso yonke abemwisele umthetho ngayo.', 'XHO22'),
(2, 4, 29, 'Wahamba uMoses noAron, bawabiza onke amadoda amakhulu oonyana bakaSirayeli.', 'XHO22'),
(2, 4, 30, 'Wawathetha uAron onke amazwi awawathethayo uYehova kuMoses, wabeka nemiqondiso phambi kwamehlo abantu.', 'XHO22'),
(2, 4, 31, 'Bakholwa abantu, beva ukuba uYehova ubavelele oonyana bakaSirayeli wakubona ukubandezeleka kwabo, bathoba baqubuda.', 'XHO22'),
(2, 5, 1, 'Wangena uMoses noAron, bathi kuFaro, Utsho uYehova, uThixo kaSirayeli, ukuthi, Bandulule abantu bam, bandenzele umthendeleko entlango.', 'XHO22'),
(2, 5, 2, 'Wathi uFaro, Ngubani na uYehova, ukuba ndiphulaphule izwi lakhe, ndibakhulule oonyana bakaSirayeli? uYehova andimazi, kwaye andiyi kumndulula uSirayeli.', 'XHO22'),
(2, 5, 3, 'Bathi ke bona, uThixo wamaHebhere uhlangene nathi; masiye entlango uhambo lweentsuku ezintathu, sibingelele kuYehova uThixo wethu; hleze asiqwele ngesibetho, nokuba likrele.', 'XHO22'),
(2, 5, 4, 'Wathi ukumkani waseYiputa kubo, Yini na ukuba nina Moses noAron nibayeke abantu emsebenzini wabo? yizisani imithwalo yenu.', 'XHO22'),
(2, 5, 5, 'Wathi uFaro, Yabona, abantu beli lizwe sele baninzi, bayiyeke imithwalo yabo.', 'XHO22'),
(2, 5, 6, 'Kwangaloo mini uFaro wabawisela umthetho abalungiseleli babantu, nabakhonzi babo, esithi,', 'XHO22'),
(2, 5, 7, 'Musani ukunika abantu umququ wokwenza izitena njengokwangaphambili; mabahambe bazikhethele bona.', 'XHO22'),
(2, 5, 8, 'Ibali lezitena abazenzayo unangoku, yibekeni phezu kwalo; ize ninganciphisi nanye kuzo, ngokuba ziceliwe; ngenxa yoko babiza, besithi, Masiye kubingelela kuThixo wethu.', 'XHO22'),
(2, 5, 9, 'Makubekwe umsebenzi phezu kwabantu, bawusebenze; kwaye ninganikeli ngqalelo kumazwi alambathayo.', 'XHO22'),
(2, 5, 10, 'Baphuma abathetheli babantu nabakhonzi babo, bathetha ebantwini, bathi, Utsho uFaro ukuthi, Andiyi kuninika mfazwe;', 'XHO22'),
(2, 5, 11, 'Hamba uye kuthabatha inqwelo yakho apho ungayifumana khona; kodwa akuyi kuncitshiswa namnye umsebenzi wenu.', 'XHO22'),
(2, 5, 12, 'Baphalazeka abantu ezweni lonke laseYiputa, babutha iindiza endaweni yengca.', 'XHO22'),
(2, 5, 13, 'Abathetheli bathi, Yisebenzeni into yemini ngangemini yayo;', 'XHO22'),
(2, 5, 14, 'Babethwa abakhonzi boonyana bakaSirayeli, ababemise abalungiseleli bakaFaro phezu kwabo, kusithiwa, Ibiyini na ukuba ningawugqibi umsebenzi wenu wokwenza izitena izolo nanamhla, njengokwangaphambili?', 'XHO22'),
(2, 5, 15, 'Beza abakhonzi boonyana bakaSirayeli, bambiza uFaro, besithi, Wenza ntoni na kubakhonzi bakho?', 'XHO22'),
(2, 5, 16, 'Abakhonzi bakho abanikwa tyani, kodwa bathi kuthi, Yenzani izitena; nabo abakhonzi bakho bayabethwa; ke abantu bakho bona banetyala.', 'XHO22'),
(2, 5, 17, 'Uthe ke yena, Uceliwe, uceliwe; ngenxa yoko nithi, Masiye kubingelela kuYehova.', 'XHO22'),
(2, 5, 18, 'Yiyani ngoku, nisebenze; kuba aniyi kunikwa trowel, nonika inani lezitena.', 'XHO22'),
(2, 5, 19, 'Zabona izikhulu zoonyana bakaSirayeli ukuba bakwimeko embi, kusithiwa nje, Ize ningancitshi nto eziteneni zenu, emsebenzini wemini yenu.', 'XHO22'),
(2, 5, 20, 'Baqubisana noMoses noAron emi endleleni ekuphumeni kwabo kuFaro;', 'XHO22'),
(2, 5, 21, 'Bathi ke bona kubo, iNkosi iya kuza nabo, inigwebe; ngenxa yokuba nenze ivumba lethu laba livumba elithozamisayo emehlweni kaFaro naphambi kwabakhonzi bakhe, ngokubanika ikrele lokusibulala.', 'XHO22'),
(2, 5, 22, 'uMoses wabuyela kuYehova, wathi, Nkosi yam, yini na ukuba ubabuze kakubi aba bantu? undithume ntoni?', 'XHO22'),
(2, 5, 23, 'Kususela koko ndangenayo kuFaro, ndithetha egameni lakho, ulutshabalalisile olu hlanga; kwaye akubasindisanga konke konke abantu bakho.', 'XHO22'),
(2, 6, 1, 'Wathi uYehova kuMoses, Uya kukubona ngoku endiya kukwenza kuFaro, kuba uya kubakhulula ngesandla esithe nkqi, abagxothe ezweni lakhe ngesandla esithe nkqi.', 'XHO22'),
(2, 6, 2, 'Wathetha uThixo kuMoses, wathi kuye, NdinguYehova.', 'XHO22'),
(2, 6, 3, 'Ndazibonakalisa kuAbraham, nakuIsake, nakuYakobi, egameni loThixo uSomandla; ke ngegama lam lokuba uYehova andazekanga kubo.', 'XHO22'),
(2, 6, 4, 'Kananjalo ndawumisa nabo umnqophiso wam wokuba ndibanike ilizwe lakwaKanan, ilizwe lokuphambukela kwabo abaphambukela kulo.', 'XHO22'),
(2, 6, 5, 'Kananjalo ndeva indumiso yoonyana bakaSirayeli, abakhonziswayo ngamaYiputa; ndawukhumbula umnqophiso wam.', 'XHO22'),
(2, 6, 6, 'Ngako oko yithi koonyana bakaSirayeli, NdinguYehova, ndiya kunikhulula emithwalweni yamaYiputa, ndinihlangule ebukhobokeni bawo, ndinihlangule ngesandla esolukileyo, nangesandla esikhulu. Izigqibo:', 'XHO22'),
(2, 6, 7, 'Ndiya kunithabatha nibe ngabantu bam, mna ndibe nguThixo wenu; nazi ukuba ndinguYehova uThixo wenu onikhuphayo phantsi kwemithwalo yamaYiputa;', 'XHO22'),
(2, 6, 8, 'ndinise ezweni endaphakamisa isandla sam ngalo, ukuba ndilinike uAbraham noIsake noYakobi; ndiya kuninika lona, libe lilifa lenu: ndinguYehova.', 'XHO22'),
(2, 6, 9, 'uMoses wathetha wenjenje koonyana bakaSirayeli, abamphulaphulanga uMoses ngenxa yomphefumlo obuhlungu, nangenxa yomsebenzi onzima.', 'XHO22'),
(2, 6, 10, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 6, 11, 'Ngena uthethe kuFaro, ukumkani waseYiputa, ukuba abandulule oonyana bakaSirayeli ezweni lakhe.', 'XHO22'),
(2, 6, 12, 'Wathetha uMoses phambi koYehova, wathi, Yabona, oonyana bakaSirayeli abandiphulaphulanga; Wothini na ke uFaro ukundiphulaphula, omlomo ungalukanga?', 'XHO22'),
(2, 6, 13, 'Ngapha koko, uYehova wathetha kuMoses noAron waza wabayalela oonyana bakaSirayeli noFaro, ukumkani waseYiputa, ukuba abakhuphe oonyana bakaSirayeli ezweni laseYiputa.', 'XHO22'),
(2, 6, 14, 'Zizo ezi iintloko zezindlu zooyise: oonyana bakaRubhen, amazibulo kaSirayeli; noHanoki, noPalu, noHetseron, noKarmi; Yiyo le ke inzala kaRubhen.', 'XHO22'),
(2, 6, 15, 'Oonyana bakaSimon nguYemuweli, noYamin, no-Ohadi, noYagin, noTsohare, noSawule, unyana womKananekazi; Ngabo abo oonyana bakaSimon.', 'XHO22'),
(2, 6, 16, 'Ngawo lawo amagama oonyana bakaLevi ngokwemizalwane yabo; uGershon, noKehati, noMerari; iminyaka yobomi bukaLevi yaba yiminyaka elikhulu elinamanci mathathu anesixhenxe.', 'XHO22'),
(2, 6, 17, 'Oonyana bakaGershon nguLibheni noShimehi ngokwemizalwane yabo.', 'XHO22'),
(2, 6, 18, 'Oonyana bakaKehati nguAmram, noItsare, noHebron, noUziyeli; iminyaka yobomi bukaKehati yaba yiminyaka elikhulu elinamanci mathathu anamithathu.', 'XHO22'),
(2, 6, 19, 'Oonyana bakaMerari nguMahali noMushi; yiyo leyo imizalwane yakwaLevi ngokwemizalwane yabo.', 'XHO22'),
(2, 6, 20, 'uAmoram wazeka uYokebhede, udade boyise; wamzalela uAron noMoses; iminyaka yobomi buka-Amram yaba yiminyaka elikhulu elinamanci mathathu anesixhenxe.', 'XHO22'),
(2, 6, 21, 'Oonyana bakaItsahare nguKora noNefegi noSigri.', 'XHO22'),
(2, 6, 22, 'Oonyana bakaUziyeli nguMishayeli noElitsafan noSitri.', 'XHO22'),
(2, 6, 23, 'uAron wazeka uElishebha, intombi ka-Aminadabhi, udade boNashon; wamzalela uNadabhi noAbhihu, uElazare noItamare.', 'XHO22'),
(2, 6, 24, 'Oonyana bakaKoreni nguAsheri; nguAshere, noElikana, noAbhiyasafu: yiyo leyo imizalwane yamaKora.', 'XHO22'),
(2, 6, 25, 'uElazare, unyana ka-Aron, wazeka enye yeentombi zikaPutiyeli yangumkakhe; wamzalela uPinehasi. Zizo ezo iintloko zezindlu zooyise zabaLevi ngokweenzala zazo.', 'XHO22'),
(2, 6, 26, 'Nguye lowo uAron noMoses, wathi kubo uYehova, Bakhupheni oonyana bakaSirayeli ezweni laseYiputa ngokwemikhosi yabo;', 'XHO22'),
(2, 6, 27, 'Ngabo aba bathethayo kuFaro ukumkani waseYiputa, ukuba abakhuphe oonyana bakaSirayeli eYiputa: nguMoses noAron.', 'XHO22'),
(2, 6, 28, 'Kwenzeka oko mini wathetha uYehova kuMoses ezweni laseYiputa, wathi:', 'XHO22'),
(2, 6, 29, 'Wathetha uYehova kuMoses, wathi, NdinguYehova; uthethe kuFaro, ukumkani waseYiputa, zonke izinto endizithethayo kuwe.', 'XHO22'),
(2, 6, 30, 'Wathi uMoses phambi koYehova, Yabona, ndinemilebe yomlomo engalukanga; wothini na ukundiva uFaro?', 'XHO22'),
(2, 7, 1, 'Wathi uYehova kuMoses, Yabona, ndikwenze uthixo kuFaro; ke uAron umkhuluwa wakho woba ngumprofeti wakho.', 'XHO22'),
(2, 7, 2, 'Thetha zonke izinto endikuwisele umthetho ngazo; yaye uAron omkhulu uya kuthetha kuFaro ukuba abandulule oonyana bakaSirayeli ezweni lakhe.', 'XHO22'),
(2, 7, 3, 'Ndiza kuyenza lukhuni intliziyo kaFaro ndize ndiyandise imiqondiso nemimangaliso yam kwilizwe laseYiputa.', 'XHO22'),
(2, 7, 4, 'Akayi kuniphulaphula uFaro, ngoko ndabeka isandla sam phezu kweYiputa ndaza ndawakhupha amacebo am nohlanga lwam loonyana bakaSirayeli ezweni laseYiputa ngezigwebo ezikhulu;', 'XHO22'),
(2, 7, 5, 'azi amaYiputa, ekusoluleni kwam isandla sam phezu kweYiputa, ukuba ndinguYehova, ndibakhuphe oonyana bakaSirayeli phakathi kwawo.', 'XHO22'),
(2, 7, 6, 'Wenza ke uMoses noAron; ngoko uYehova wabawisela umthetho ngako, benjenjalo.', 'XHO22'),
(2, 7, 7, 'uMoses ubeminyaka imashumi asibhozo ezelwe, noAron ubeminyaka imashumi asibhozo anamithathu ezelwe, ukuthetha kwabo kuFaro.', 'XHO22'),
(2, 7, 8, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(2, 7, 9, 'Uthe uFaro wathetha kuni, wathi, Zenzeleni ummangaliso, wothi kuAron, Thabatha intonga yakho, uyiphose phambi koFaro; yoba yinyoka.', 'XHO22'),
(2, 7, 10, 'Wangena uMoses noAron kuFaro, benza njengoko uYehova wabawisela umthetho; yaye uAron waluphosa unkontsho lwakhe emhlabeni phambi koFaro naphambi kwabakhonzi bakhe, lwaza lwaba yinyoka.', 'XHO22'),
(2, 7, 11, 'Kananjalo uFaro wazibiza izilumko namatola; zenjenjalo nezazi zaseYiputa ngobugqi bazo.', 'XHO22'),
(2, 7, 12, 'Elowo waphosa intonga yakhe emhlabeni, yaba yinyoka; yasuka intonga ka-Aron yayidla intonga yabo.', 'XHO22'),
(2, 7, 13, 'Wayenza lukhuni intliziyo kaFaro, akabaphulaphula; njengoko watshoyo uYehova.', 'XHO22'),
(2, 7, 14, 'Wathi uYehova kuMoses, Intliziyo kaFaro iqaqadekile, akavumi ukubandulula abantu.', 'XHO22'),
(2, 7, 15, 'Yiya kuFaro kusasa; nanko ephuma esiya emanzini; ume eludinini lomlambo ekufikeni kwayo; intonga eyaba yinyoka yiphathe esandleni sakho.', 'XHO22'),
(2, 7, 16, 'Wathi kuye, uYehova, uThixo wamaHebhere, undithume kuwe, esithi, Bashiye abantu bam, baye kundikhonza entlango; yabona, akuvakali kwada kwangoku.', 'XHO22'),
(2, 7, 17, 'Utsho uYehova ukuthi, Uya kwazi ngale nto ke ukuba ndinguYehova: yabona, ndiwabetha amanzi oMlambo umNayile ngesandla sam, abe ligazi.', 'XHO22'),
(2, 7, 18, 'Ziya kufa iintlanzi ezisemlanjeni, unuke umlambo; akavuma amaYiputa ukuwasela amanzi omlambo.', 'XHO22'),
(2, 7, 19, 'Wathi uYehova kuMoses, Yithi kuAron, Thabatha intonga yakho, wolule isandla sakho phezu kwamanzi aseYiputa, phezu kwemilambo yabo, naphezu kwemilambo yabo, naphezu kwamachibi abo, naphezu kwamadike abo onke amanzi abo, abe ligazi yaye kuya kubakho igazi kulo lonke ilizwe laseYiputa, empahleni yomthi nakwimpahla yamatye.', 'XHO22'),
(2, 7, 20, 'OoMoses noAron benza njengoko uYehova wabawisela umthetho; wabeka ibhulorho, wawatywina amanzi oMnayile phambi kwamehlo kaFaro, naphambi kwabakhonzi bakhe; aguquka onke amanzi omlambo, aba ligazi.', 'XHO22'),
(2, 7, 21, 'Zafa iintlanzi ezisemlanjeni; Wanuka umlambo, akaba nako amaYiputa ukuwasela amanzi omlambo; laba ligazi kulo lonke ilizwe laseYiputa.', 'XHO22'),
(2, 7, 22, 'Zenjenjalo ke nezazi zaseYiputa ngobugqi bazo. Yaba lukhuni intliziyo kaFaro, akabaphulaphula, njengoko watshoyo uYehova.', 'XHO22'),
(2, 7, 23, 'Wajika uFaro, wangena endlwini yakhe, akayibekela ntliziyo naye loo nto.', 'XHO22'),
(2, 7, 24, 'Onke amaYiputa emba amanzi okusela ngeenxa zonke emlanjeni; ngokuba ebengenakuwasela amanzi omlambo.', 'XHO22'),
(2, 7, 25, 'Kwadlula iintsuku zasixhenxe emva kokuwubetha kwakhe uYehova umlambo.', 'XHO22'),
(2, 8, 1, 'Wathi uYehova kuMoses, Yiya kuFaro, uthethe naye, uthi, Utsho uYehova ukuthi, Bandulule abantu bam, bahambe baye kundikhonza.', 'XHO22'),
(2, 8, 2, 'Ukuba uthe akwavuma ukubandulula, yabona, ndolibetha ilizwe lakho lonke ngamasele.', 'XHO22'),
(2, 8, 3, 'Umlambo wovelisa amasele amaninzi, anyuke anyuke endlwini yakho, nasegumbini lakho lokulala, nasesililini sakho, nasendlwini yabakhonzi bakho, nasebantwini bakho, naseziontini zakho, nasezityeni zakho;', 'XHO22'),
(2, 8, 4, 'anyuke amasele, eze kuwe, nakubantu bakho, nakubakhonzi bakho bonke;', 'XHO22'),
(2, 8, 5, 'Wathi uYehova kuMoses, Yithi kuAron, Yolula isandla sakho nentonga yakho phezu kwemilambo, naphezu kwemilambo, naphezu kwamachibi, anyuke amasele ezweni laseYiputa.', 'XHO22'),
(2, 8, 6, 'Wasolula uAron isandla sakhe phezu kwamanzi aseYiputa; zenyuka iinkumbi, zaligubungela ilizwe laseYiputa.', 'XHO22'),
(2, 8, 7, 'Amagqirha enjenjalo ngomlingo wawo, aza angenisa amasele ezweni laseYiputa.', 'XHO22'),
(2, 8, 8, 'uFaro wabiza uMoses noAron, wathi, Thandazani kuYehova, azisuse ezo iinkumbi kum, nakubantu bam; yaye ndiza kubandulula abantu baye kubingelela kuYehova.', 'XHO22'),
(2, 8, 9, 'Wathi uMoses kuFaro, Ndiqhayise ngam; Ndiya kukucela nini na wena, nakubakhonzi bakho, nakubantu bakho, ukuba uwatshabalalise amasele kuwe nasezindlwini zakho, asale kuMlambo umNayile kuphela?', 'XHO22'),
(2, 8, 10, 'Wathi ke yena, Ngomso. Wathi, Ngokwelizwi lakho, ukuze wazi ukuba akukho unjengoYehova uThixo wethu;', 'XHO22'),
(2, 8, 11, 'asuke amasele kuwe, nakwizindlu zakho, nakubakhonzi bakho, nakubantu bakho; basenokuhlala nje emlanjeni.', 'XHO22'),
(2, 8, 12, 'Bemka ooMoses noAron kuFaro; wabiza uMoses kuYehova ngenxa yamavukuthu awayewazise kuFaro.', 'XHO22'),
(2, 8, 13, 'Wenza uYehova ngokwelizwi likaMoses; zafa iinkomo ezindlwini, nasemizaneni, nasemasimini.', 'XHO22'),
(2, 8, 14, 'Bawahlanganisa ayimfumba;', 'XHO22'),
(2, 8, 15, 'Wabona uFaro ukuba ulibele, wayenza lukhuni intliziyo yakhe, akabaphulaphula; njengoko watshoyo uYehova.', 'XHO22'),
(2, 8, 16, 'Wathi uYehova kuMoses, Yithi kuAron, Yolula intonga yakho, ulubethe uthuli lwelizwe lube ziingcongconi ezweni lonke laseYiputa.', 'XHO22'),
(2, 8, 17, 'Benjenjalo; ngenxa yokuba uAron wasolula isandla sakhe esasibambe intonga yakhe waza walubetha uthuli lomhlaba, lwaba ziingcongconi ebantwini nasezilwanyaneni; Lonke uthuli lwelizwe lwaba luthuli ezweni lonke laseYiputa.', 'XHO22'),
(2, 8, 18, 'Oogqirha benza okufanayo ngomlingo wabo ukuvelisa iingcongconi, kodwa abazange bakwazi;', 'XHO22'),
(2, 8, 19, 'Zathi izazi kuFaro, Ngumnwe kaThixo lo; njengoko watshoyo uYehova.', 'XHO22'),
(2, 8, 20, 'Wathi uYehova kuMoses, Vuka kusasa, ume ngakuFaro; nanko ephuma esiya emanzini; Wathi yena kuye, uYehova uthe, Bandulule abantu bam, baye kundikhonza.', 'XHO22'),
(2, 8, 21, 'Kungenjalo, ukuba uthe akwabandulula abantu bam, yabona, ndothumela iingonyama kuwe, nakubakhonzi bakho, nakubantu bakho, nezindlu zakho; izindlu zamaYiputa ziya kuzala ziimbovane, nelizwe abakulo.', 'XHO22'),
(2, 8, 22, 'Ndiya kulitshabalalisa ngaloo mini ilizwe laseGoshen, abemi kulo abantu bam, kungabikho baneqhenqa; wazi ukuba ndinguYehova phakathi kwehlabathi kude kuse ekupheleni;', 'XHO22'),
(2, 8, 23, 'ndibahlule abantu bam nabantu bakho; ngomso izakuba ngulomboniso.', 'XHO22'),
(2, 8, 24, 'Wenjenjalo uYehova; Zangena iimbovane endlwini kaFaro, nasezindlwini zabakhonzi bakhe, nakwilizwe lonke laseYiputa;', 'XHO22'),
(2, 8, 25, 'uFaro wabiza uMoses noAron, wathi, Hambani niye kubingelela kuThixo wenu kweli lizwe.', 'XHO22'),
(2, 8, 26, 'Wathi uMoses, Akulungile ukwenjenjalo; kuba siya kuwenza amasikizi amaYiputa kuYehova uThixo wethu; Yabona, ndiya kuwabulala amasikizi amaYiputa phambi kwawo, angasixulubi ngamatye?', 'XHO22'),
(2, 8, 27, 'Siya kuhamba iintsuku zibe ntathu entlango, sibingelele kuYehova uThixo wethu, njengoko wasiwisela umthetho ngako.', 'XHO22'),
(2, 8, 28, 'Wathi uFaro, Ndiya kunikhulula, niye kubingelela kuYehova uThixo wenu entlango; kodwa musani ukuya kude; thandazele.', 'XHO22'),
(2, 8, 29, 'Wathi uMoses, Yabona, ndiyamka kuni, ndithandaze kuYehova, amke ngomso amaqhenqa kuFaro, nakubakhonzi bakhe, nakubantu bakhe; yaye uFaro makangabi saba sisiphithiphithi aze abandulule abantu baye kubingelela kuYehova.', 'XHO22'),
(2, 8, 30, 'uMoses wemka kuFaro, wathandaza kuYehova.', 'XHO22'),
(2, 8, 31, 'Wenza uYehova ngokwelizwi likaMoses; wabasusa abaneqhenqa kuFaro, nakubakhonzi bakhe, nakubantu bakhe; akusekho namnye.', 'XHO22'),
(2, 8, 32, 'Naye uFaro wayiqaqadekisa intliziyo yakhe ngelo xesha, akabandulula abantu.', 'XHO22'),
(2, 9, 1, 'Wathi uYehova kuMoses, Yiya kuFaro, uthethe naye, uthi, Utsho uYehova uThixo wamaHebhere, ukuthi, Bashiye abantu bam, bandikhonze.', 'XHO22'),
(2, 9, 2, 'Kuba xa uthe akwavuma ukubandulula, ubathulise,', 'XHO22'),
(2, 9, 3, 'Yabona, isandla sikaYehova siphezu kwemfuyo yakho esendle, namahashe, namaesile, neenkamela, neenkomo, neegusha; kuya kubakho indyikitya yokufa.', 'XHO22'),
(2, 9, 4, 'uYehova uya kuwahlula imihlambi yamaSirayeli kwimihlambi yaseYiputa, kungafi nto koonyana bakaSirayeli bonke.', 'XHO22'),
(2, 9, 5, 'Kwaye iNkosi yabeka ixesha, kwaye yathi: Ngomso iNkosi iya kuyenza le nto ehlabathini.', 'XHO22'),
(2, 9, 6, 'Wayenza ke uYehova le nto ngomso, yafa yonke imfuyo yaseYiputa; akwafa nanye emfuyoni yoonyana bakaSirayeli.', 'XHO22'),
(2, 9, 7, 'Wathumela uFaro, yaye abona ukuba akufanga nanye emhlambini wakwaSirayeli. Yaba lukhuni intliziyo kaFaro, akavuma ukubandulula abantu.', 'XHO22'),
(2, 9, 8, 'Wathi uYehova kuMoses nakuAron, Bekani uthuthu eziko oluzele zizalisa izandla, uMoses waluphosa esibhakabhakeni phambi koFaro.', 'XHO22'),
(2, 9, 9, 'Uya kuba luthuli ezweni lonke laseYiputa, abe sisibetho esijikajikayo phakathi kwabantu neenkomo ezweni lonke laseYiputa.', 'XHO22'),
(2, 9, 10, 'Baluthabatha ke uthuthu lweziko, bema phambi koFaro; uMoses wayitshiza esibhakabhakeni; yaye yaba sisibetho esadumba ngamathumba ebantwini nasezilwanyaneni.', 'XHO22'),
(2, 9, 11, 'Ababa nako oogqirha ukuma phambi koMoses ngenxa yezilonda; ngenxa yokuba kwakukho izibetho kumaSiriya nakuwo onke amaYiputa.', 'XHO22'),
(2, 9, 12, 'uYehova wayenza lukhuni intliziyo kaFaro, akabaphulaphula; njengoko uYehova wamwiselayo uMoses umthetho.', 'XHO22'),
(2, 9, 13, 'Wathi uYehova kuMoses, Vuka kusasa, uye kuma phambi koFaro, uthi kuye, Utsho uYehova uThixo wamaHebhere, ukuthi, Bashiye abantu bam, bandikhonze.', 'XHO22'),
(2, 9, 14, 'Kuba ngesi sihlandlo ndithuma bonke ubugwenxa bam entliziyweni yakho, nakubakhonzi bakho, nakubantu bakho; ukuze wazi ukuba akukho namnye unjengam ehlabathini lonke.', 'XHO22'),
(2, 9, 15, 'Kuba ngoku, ndiya kusolula isandla sam, ndikubethe wena nabantu bakho ngesibetho; uya kunqunyulwa angabikho ehlabathini.', 'XHO22'),
(2, 9, 16, 'Ndikuphakamisile ngenxa yoko, ukuba ndiwabonakalalise ngawe amandla am; nokuba igama lam lithethwe ehlabathini lonke.', 'XHO22'),
(2, 9, 17, 'Usaqhayisa ngabantu bam, ungabandululi nje?', 'XHO22'),
(2, 9, 18, 'Yabona, eli xesha ngomso ndiya kunisa isichotho esikhulu kunene, ekungazanga kubekho singaso eYiputa, kususela ekusekweni kwayo kude kube ngoku.', 'XHO22'),
(2, 9, 19, 'Thumela ngoku, uthabathe imfuyo yakho, neento zonke onazo ezweni; Kuba phezu kwabantu bonke, nakwizinto zonke eziphilileyo ezifunyenweyo ezweni, ezingabuyiselwa ekhaya, kuya kuwa isichotho phezu kwazo, zife.', 'XHO22'),
(2, 9, 20, 'Owoyikayo ilizwi likaYehova kubakhonzi bakaFaro, mababaleke ezindlwini zabo, abakhonzi bakhe nemfuyo yakhe.', 'XHO22'),
(2, 9, 21, 'Ongaliphulaphulanga ilizwi likaYehova, wabashiya abakhonzi bakhe nemfuyo yakhe ezweni.', 'XHO22'),
(2, 9, 22, 'Wathi uYehova kuMoses, Yolulela isandla sakho ezulwini, kubekho isichotho ezweni lonke laseYiputa phezu kwabantu, naphezu kweenkomo, naphezu kwemifuno yonke yelizwe ezweni lonke laseYiputa.', 'XHO22'),
(2, 9, 23, 'Wayolulela uMoses intonga yakhe ezulwini, uYehova wathoba iindudumo nesichotho, kwawa umlilo emhlabeni. Waza uYehova walinisa isichotho ilizwe laseYiputa.', 'XHO22'),
(2, 9, 24, 'Kwabakho isichotho nomlilo oxutywe nesichotho esikhulu kunene, ekungazanga kubekho ezweni lonke laseYiputa, kususela oko aba luhlanga.', 'XHO22'),
(2, 9, 25, 'Isichotho salibetha lonke ilizwe laseYiputa, nento yonke ebe isezweni, kuthabathela ebantwini kwesa enkomeni; sayibetha isichotho yonke izityalo zelizwe, sayaphula yonke imithi yelizwe.', 'XHO22'),
(2, 9, 26, 'Kusezweni laseGoshen kuphela, apho kwakukho oonyana bakaSirayeli, engazange kubekho sichotho.', 'XHO22'),
(2, 9, 27, 'Waza uFaro wabiza uMoses noAron, wathi kubo, NdiNgenene ngoku; uYehova ulilungisa, kodwa mna nabantu bam singendawo.', 'XHO22'),
(2, 9, 28, 'Thandazani kuYehova; kwanele ukubakho kweendudumo nesichotho; ndinikhulule, ningabi sabakho.', 'XHO22'),
(2, 9, 29, 'Wathi uMoses kuye, Ekuphumeni kwam phakathi komzi, ndozolulela kuYehova izandla zam; ziphele iindudumo, nesichotho singabi sabakho; ukuze nazi ukuba ihlabathi lelikaYehova.', 'XHO22'),
(2, 9, 30, 'Ke wena nabakhonzi bakho, ndiyazi ukuba anizange niyoyike iNkosi uYehova.', 'XHO22'),
(2, 9, 31, 'Kwakutyelwe iflakisi nerhasi, kuba irhasi ibihleli, neflakisi ibininzi.', 'XHO22'),
(2, 9, 32, 'Ke yona ingqolowa nomdiza ayinqunyulwanga, ngenxa yokuba ibingeninzi.', 'XHO22'),
(2, 9, 33, 'Waphuma uMoses emzini kaFaro, wazolulela izandla zakhe kuYehova, zayeka iindudumo nesichotho, ayabi mvula emhlabeni.', 'XHO22'),
(2, 9, 34, 'Wabona uFaro ukuba iphelile imvula nesichotho neendudumo, waphinda wona, wayiqaqadekisa intliziyo yakhe, yena nabakhonzi bakhe.', 'XHO22'),
(2, 9, 35, 'Yaba lukhuni intliziyo kaFaro, akabandulula oonyana bakaSirayeli; njengoko uYehova wathethayo ngoMoses.', 'XHO22'),
(2, 10, 1, 'Wathi uYehova kuMoses, Yiya kuFaro; kuba mna ndiyiqaqadekisile intliziyo yakhe, nentliziyo yabakhonzi bakhe, ukuba bathethe imibono yam phambi kwakhe.', 'XHO22'),
(2, 10, 2, 'Uze uthethe kunyana wakho, nakunyana wakho, into endayenzayo eYiputa, nemiqondiso yam endayenzayo phakathi kwabo; ukuze wazi ukuba ndinguYehova.', 'XHO22'),
(2, 10, 3, 'Waya uMoses noAron kuFaro, bathi kuye, Utsho uYehova, uThixo wamaHebhere, ukuthi, Kuya kuda kube nini na ungavumi ukuzithoba phambi kwam? bandulule abantu bam, baye kundikhonza.', 'XHO22'),
(2, 10, 4, 'Okanye ukuba uthe akwavuma ukubandulula abantu bam, yabona, ngomso ndozisa iinkumbi ezweni lakho.', 'XHO22'),
(2, 10, 5, 'Ziya kuwugubungela umhlaba, kungabonwa mntu; zidle amasalela akho asindileyo, okuseleyo sisichotho, zidle yonke imithi ehluma phezu kwakho endle;', 'XHO22'),
(2, 10, 6, 'zizalise izindlu zakho, nezindlu zabakhonzi bakho bonke, nezindlu zamaYiputa onke; Andizange ndibabone ooyihlo nooyihlo kususela emhlabeni unanamhla. Wajika, wemka kuFaro.', 'XHO22'),
(2, 10, 7, 'Bathi abakhonzi bakaFaro kuye, Koda kube nini na indoda esiba ngumgibe kuthi? mabafuduke abantu, baye kukhonza uYehova uThixo wabo; akukazi na, ukuba iYiputa iza kutshabalala?', 'XHO22'),
(2, 10, 8, 'Baza ooMoses noAron babuyiselwa kwakuFaro; wathi kubo, Hambani, niye kukhonza uYehova uThixo wenu; kodwa ngubani oya kuhamba?', 'XHO22'),
(2, 10, 9, 'Wathi uMoses, Siya kuhamba neenkomo zethu, namadoda ethu amakhulu, noonyana bethu, neentombi zethu, neenkomo zethu, nemfuyo yethu; kuba kufuneka senze umbhiyozo ozukisa uYehova.', 'XHO22'),
(2, 10, 10, 'Wathi kubo, uYehova makabe nani, ndinikhululele nina nabantwana benu; ngokuba ububi buphambi kwakho.', 'XHO22'),
(2, 10, 11, 'Akunjalo; hambani ngoku niye kukhonza uYehova; kuba ubuyinqwenela. Bagxothwa ke ebusweni bukaFaro.', 'XHO22'),
(2, 10, 12, 'Wathi uYehova kuMoses, Yolula isandla sakho phezu kwelizwe laseYiputa, phezu kweenkumbi, zilingene ilizwe laseYiputa, ziyidle yonke imifuno yelizwe eli, eseleyo esichothweni.', 'XHO22'),
(2, 10, 13, 'Wayolula uMoses intonga yakhe phezu kwelizwe laseYiputa, uYehova wahambisa umoya wasempumalanga kwelo lizwe yonke loo mini nobusuku bonke; kwakunetha umoya wasempumalanga wezisa iinkumbi.', 'XHO22'),
(2, 10, 14, 'Zenyuka iinkumbi kulo lonke ilizwe laseYiputa, zahlala ezindaweni zonke zaseYiputa; zazinzima kakhulu; phambi kwazo akuzange kubekho zinkumbi zingako, kwabe emva kwazo kungasayi kubakho zinkumbi.', 'XHO22'),
(2, 10, 15, 'Zabugubungela ihlabathi lonke, laba mnyama ilizwe; yaye badla yonke imifuno yomhlaba neziqhamo zonke zemithi eyayisele, akwasala luhlaza emithini nakwizityalo zelizwe kulo lonke ilizwe laseYiputa.', 'XHO22'),
(2, 10, 16, 'Wakhawuleza uFaro wababiza uMoses noAron; wathi, Ndonile kuYehova uThixo wenu, nakuni.', 'XHO22'),
(2, 10, 17, 'Ndixoleleni isono sam kube kanye, nimcele uYehova uThixo wenu, asisuse kum esi sifo.', 'XHO22'),
(2, 10, 18, 'Wemka ke kuFaro, wathandaza kuYehova.', 'XHO22'),
(2, 10, 19, 'Wawuguqula ke uYehova umoya wasentshonalanga lowo, wazithabatha iinkumbi, wazidiba eSekefe; akwasala nanye inkumbi kulo lonke ilizwe laseYiputa.', 'XHO22'),
(2, 10, 20, 'Wayenza lukhuni uYehova intliziyo kaFaro, akabandulula oonyana bakaSirayeli.', 'XHO22'),
(2, 10, 21, 'Wathi uYehova kuMoses, Yolulela isandla sakho ezulwini, kubekho ubumnyama ezweni laseYiputa, bugutyungelwe bubumnyama.', 'XHO22'),
(2, 10, 22, 'Wasolulela uMoses isandla sakhe ezulwini; kwaza kwakho ubumnyama nesithokothoko kulo lonke ilizwe laseYiputa iintsuku ezintathu.', 'XHO22'),
(2, 10, 23, 'Ababonana, yaye kangangeentsuku ezintathu akukho bani uvukayo kwindawo yakhe yokuhlala, kodwa kubo bonke oonyana bakaSirayeli kwabakho ukukhanya ezintenteni zabo.', 'XHO22'),
(2, 10, 24, 'uFaro wambiza uMoses, wathi, Hambani niye kukhonza uYehova; Impahla yenu emfutshane neenkomo zenu mazime; mabahambe nawe nabantwana bakho.', 'XHO22'),
(2, 10, 25, 'Wathi uMoses, Nisinike nani imibingelelo namadini anyukayo, ukuba siwasondeze kuYehova uThixo wethu;', 'XHO22'),
(2, 10, 26, 'nemihlambi yethu ihambe nathi; akukho zikhonkwane ezishiywe ngasemva; ngenxa yoko simele sithabathele ekukhonzeni uYehova uThixo wethu; kwaye asazi ukuba masikhonze ngantoni na iNkosi side sifike apho.', 'XHO22'),
(2, 10, 27, 'Wayenza lukhuni uYehova intliziyo kaFaro, akabakhulula.', 'XHO22'),
(2, 10, 28, 'Wathi uFaro kuye, Hamba umke kum, uzigcine, ungaphindi ububone ubuso bam; kuba ngaloo mini, ukuba nibubone ubuso bam, niya kufa.', 'XHO22'),
(2, 10, 29, 'Wathi uMoses, Utyaphile; Andisayi kuphinda ndibubone ubuso bakho.', 'XHO22'),
(2, 11, 1, 'Wathi uYehova kuMoses, Andiwisanga nasinye isigwebo phezu koFaro naphezu kweYiputa; emveni koko uya kunisusa apha; ukuba akanindululi, wonigxotha kuphele apho.', 'XHO22'),
(2, 11, 2, 'Thetha apho baphulaphuleyo abantu, uthi, elowo kwindlu yakhe, nabafazi bonke bendlu yakhe, mabacele izivatho zesilivere nezegolide.', 'XHO22'),
(2, 11, 3, 'uYehova wabenzela inceba abantu emehlweni amaYiputa. Loo ndoda ke leyo inguMoses yayinkulu kunene ezweni laseYiputa, emehlweni abakhonzi bakaFaro, nasemehlweni abantu.', 'XHO22'),
(2, 11, 4, 'Wathi uMoses, uYehova wathi, Phakathi kobusuku, ndiya kuphuma ndiye eYiputa.', 'XHO22'),
(2, 11, 5, 'Onke amazibulo ezweni laseYiputa aya kufa, kuqalela kowamazibulo kaFaro ehleli etroneni yakhe, kuse kowamazibulo omkhonzazana osemva kwelitye lokusila; nawo onke amazibulo eenkomo.', 'XHO22'),
(2, 11, 6, 'Kuya kubakho isikhalo esikhulu ezweni lonke laseYiputa, ekungazanga sibekho, nesingayi kubuya sibekho.', 'XHO22'),
(2, 11, 7, 'Ke koonyana bakaSirayeli akuyi kubakho nja ibavuthulula ngolwimi lwayo, kuthabathele emntwini kuse ezinkomeni, ukuze nazi ukuba uYehova uyahluka phakathi kweYiputa noSirayeli.', 'XHO22'),
(2, 11, 8, 'Behla beze kum bonke aba bakhonzi bakho, baqubude kum, bathi, Hamba wena nabantu bonke abaseleyo, ndihambe mna. Emva koko wemka enomsindo omkhulu kuFaro.', 'XHO22'),
(2, 11, 9, 'Wathi uYehova kuMoses, Akayi kuniphulaphula uFaro; ukuze imisebenzi yam yanda ezweni laseYiputa.', 'XHO22'),
(2, 11, 10, 'uMoses noAron bayenza yonke le miqondiso phambi koFaro; uYehova wayenza lukhuni intliziyo kaFaro, akabandulula oonyana bakaSirayeli ezweni lakhe.', 'XHO22'),
(2, 12, 1, 'Wathetha uYehova kuMoses nakuAron ezweni laseYiputa, wathi,', 'XHO22'),
(2, 12, 2, 'Le nyanga kuni iyintloko yeenyanga; Mayibe yinyanga yokuqala enyakeni kuni.', 'XHO22'),
(2, 12, 3, 'Thethani kwibandla lonke lakwaSirayeli, nithi, Ngolweshumi usuku kule nyanga elowo makathabathe ixhwane ezindlwini zooyise, nexhwane kwindlu nganye.', 'XHO22'),
(2, 12, 4, 'Ukuba ithe indlu yancinane exhwaneni, makathabathe ixhwane yena enommelwane wakhe okufuphi nendlu yakhe, ngokwenani lemiphefumlo; elowo anokudla kwakhe ixhwane.', 'XHO22'),
(2, 12, 5, 'Ixhwane lenu malibe liduna eligqibeleleyo, elimnyaka mnye; wolithabatha ezigusha nasebhokhweni.', 'XHO22'),
(2, 12, 6, 'Nize niyigcine kude kube lusuku lweshumi elinesine lwaloo nyanga, liyixhele lonke ibandla lebandla lakwaSirayeli ngokuhlwa;', 'XHO22'),
(2, 12, 7, 'bathabathe egazini, balitshize emigubasini yomibini, nasemigubasini yezindlu abaya kudlela kuzo;', 'XHO22'),
(2, 12, 8, 'Bayidla inyama ngobo busuku, yosiwe emlilweni, izonka ezingenagwele; batya kunye nemifuno ekrakra.', 'XHO22'),
(2, 12, 9, 'Musa ukuyidla ingekavuthwa, nokuba ingagalelwanga emanzini, kodwa yosiwe emlilweni; intloko yayo, nomlomo wayo, nokubethwa kwayo.', 'XHO22'),
(2, 12, 10, 'Musani ukushiya nto kuyo kude kuse; okuseleyo kwada kwasa nokutshisa ngomlilo.', 'XHO22'),
(2, 12, 11, 'Woyidla ke; ubhinqe amanqe akho, unxibe iimbadada zakho ezinyaweni zakho, nomsimelelo wakho ezandleni zakho; udle ngokukhawuleza; yipasika kaYehova leyo.', 'XHO22'),
(2, 12, 12, 'Kuba ngobu busuku ndiya kucanda ezweni laseYiputa, ndiwabulale onke amazibulo ezweni laseYiputa, ndithabathele emntwini ndise enkomeni; ndiya kubagweba bonke oothixo baseYiputa: ndinguYehova.', 'XHO22'),
(2, 12, 13, 'Igazi loba ngumqondiso kuni ezindlwini eniya kuba kuzo; xa ndibona igazi, ndodlula kuni, singabi nakufikelwa sisohlwayo sokutshabalalisa, ekulibetheni kwam ilizwe laseYiputa.', 'XHO22'),
(2, 12, 14, 'Le mini yoba yimini yesikhumbuzo kuni; nibhiyozele kuYehova kwizizukulwana zenu; uya kuwubhiyozela ngonaphakade ngomyalelo.', 'XHO22'),
(2, 12, 15, 'Iintsuku ezisixhenxe nidle izonka ezingenagwele; nize nikhuphe igwele ezindlwini zenu ngomhla wokuqala; kuba bonke abadla isonka esinegwele kususela kolokuqala kude kube lusuku lwesixhenxe, bonqanyulwa bangabikho kwaSirayeli loo moya.', 'XHO22'),
(2, 12, 16, 'Ngomhla wokuqala kobakho ibandla elingcwele, nangomhla wesixhenxe kobakho ibandla elingcwele kuni; makungàsetyenzwa kuwo, ingenguwo ukudla komntu wonke, okuya kwenziwa nini kuphela.', 'XHO22'),
(2, 12, 17, 'Yenza umthendeleko wezonka ezingenagwele; kuba ngaloo mini ndabukhupha ubuncwane benu ezweni laseYiputa; ke ngoko, niya kuwugcina lo mhla kwizizukulwana zenu, ngomthetho wanaphakade.', 'XHO22'),
(2, 12, 18, 'Ngeyokuqala inyanga, ngosuku lweshumi elinesine enyangeni leyo, ngokuhlwa, nodla izonka ezingenagwele, kude kuse kusuku lwamashumi omabini analunye lwenyanga leyo, ngokuhlwa.', 'XHO22'),
(2, 12, 19, 'Ze kungafunyanwa gwele ezindlwini zenu iintsuku ezisixhenxe; kuba bonke abadla into enegwele, bonqanyulwa bangabikho ebandleni lakwaSirayeli, nokuba ngumphambukeli, nokuba ngozalelwe kuloo ndawo.', 'XHO22'),
(2, 12, 20, 'Yonke into enegwele ningayidli; ungadla izonka ezingenagwele emakhayeni akho onke.', 'XHO22'),
(2, 12, 21, 'Wawabiza uMoses onke amadoda amakhulu akwaSirayeli, wathi kuwo, Phumani nizithabathele ixhwane ngokwemizalwane yenu, nixhele ipasika.', 'XHO22'),
(2, 12, 22, 'Uthabathe isipha sehisope, usithi nkxu egazini esityeni, ufefe umnyango ophezulu, namacala omabini, ngegazi esityeni; makungaphumi namnye kuni ngomnyango wendlu yakhe kude kube kusasa.', 'XHO22'),
(2, 12, 23, 'Ngokuba uYehova uya kucanda, awabulale amaYiputa; ukuba libona igazi elucangweni olungaphezulu nakwiimbombo zombini, wodlula uYehova emnyango, angamvumeli umenzi wobubi ukuba angene ezindlwini zenu, anitshabalalise.', 'XHO22'),
(2, 12, 24, 'Wugcine lo mbandela ube ngummiselo kuwe nakoonyana bakho kude kuse ephakadeni.', 'XHO22'),
(2, 12, 25, 'Nakufika ezweni aya kuninika uYehova, njengoko watshoyo, niyigcine le nkonzo.', 'XHO22'),
(2, 12, 26, 'Xa bathe oonyana benu kuni, Yeyani na le nkonzo?', 'XHO22'),
(2, 12, 27, 'nithi, Le yipasika kaYehova, owagqitha ezindlwini zoonyana bakaSirayeli eYiputa, wawaxabela amaYiputa, wazisindisa izindlu zethu; Bathoba abantu, baqubuda.', 'XHO22'),
(2, 12, 28, 'Bemka oonyana bakaSirayeli, benza njengoko uYehova wabawisela umthetho uMoses noAron, benjenjalo.', 'XHO22'),
(2, 12, 29, 'Kwathi phakathi kobusuku, uYehova wawabulala onke amazibulo ezweni laseYiputa, kwathabathela kowamazibulo kaFaro osetroneni yakhe, kwesa kowamazibulo womthinjwa owayevalelwe entolongweni; nawo onke amazibulo eenkomo.', 'XHO22'),
(2, 12, 30, 'Wavuka uFaro ebusuku, yena nabakhonzi bakhe bonke, namaYiputa onke; kwabakho inyikima enkulu eYiputa; ngokuba akubangakho ndlu, kungekho mntu ufileyo.', 'XHO22'),
(2, 12, 31, 'Wabiza uMoses noAron ebusuku, wathi, Sukani niphume phakathi kwabantu bam, nina nyana bakaSirayeli; nihambe niye kukhonza uYehova njengoko nitshilo.', 'XHO22'),
(2, 12, 32, 'Impahla yenu emfutshane neenkomo zenu zithabatheni, njengoko natsho, nihambe; nam undisikelele.', 'XHO22'),
(2, 12, 33, 'AmaYiputa ababongoza abantu ukuba bakhawuleze baphume kwelo zwe; ngokuba besithi, Sifile sonke.', 'XHO22'),
(2, 12, 34, 'Abantu bathabatha intlama yabo engenagwele, neendebe zabo zentlama zibotshelelwe emagxeni abo ezingutyeni zabo.', 'XHO22'),
(2, 12, 35, 'Benza oonyana bakaSirayeli ngokwelizwi likaMoses; yaye baboleka kumaYiputa izinto zesilivere nezinto zegolide neempahla.', 'XHO22'),
(2, 12, 36, 'uYehova wabenzela inceba abantu emehlweni amaYiputa, abanika abakucelayo. Bawaphanga ke amaYiputa.', 'XHO22'),
(2, 12, 37, 'Banduluka oonyana bakaSirayeli eRameses, baya eSukoti, amadoda ahamba ngeenyawo ngathi ngathi angamakhulu omathandathu amawaka, ngaphandle koonyana.', 'XHO22'),
(2, 12, 38, 'Nendimbane engumxube yenyuka nabo; nemfuyo neenkomo, ewe, iinkomo ezininzi.', 'XHO22'),
(2, 12, 39, 'Bosa izonka ezingenagwele ngentlama, ababephume nayo eYiputa, kuba ibingenagwele; ngenxa yokuba bagxothwayo eYiputa, abazange balibale, yaye abazange bazilungiselele ukutya.', 'XHO22'),
(2, 12, 40, 'Ukuhlala koonyana bakaSirayeli eYiputa yiminyaka emakhulu mane, anamanci mathathu.', 'XHO22'),
(2, 12, 41, 'Ekupheleni kweminyaka emakhulu mane anamanci mathathu, kwangayo loo mini, aphuma onke amatyala kaYehova ezweni laseYiputa.', 'XHO22'),
(2, 12, 42, 'Bubusuku obuya kuxolelwa uYehova, ngokuba wabakhuphayo ezweni laseYiputa; bubusuku bukaYehova obo obuya kugcinwa ngoonyana bakaSirayeli bonke, ezizukulwaneni zabo.', 'XHO22'),
(2, 12, 43, 'Wathi uYehova kuMoses nakuAron, Nguwo lo umyalelo wepasika: Ize ningamudli owasemzini.', 'XHO22'),
(2, 12, 44, 'Kodwa ikhoboka lomntu wonke elizuzwe ngemali, ukuba lithe laluka, andule ke ukudla okwakhe.', 'XHO22'),
(2, 12, 45, 'Umphambukeli nolunywebe usuku akayi kuyidla.', 'XHO22'),
(2, 12, 46, 'Yodlelwa ndlwini-nye; musani ukukhupha nto yenyama endlwini; nethambo layo ningaliphuli.', 'XHO22'),
(2, 12, 47, 'Lonke ibandla lakwaSirayeli loyenza loo nto.', 'XHO22'),
(2, 12, 48, 'Kube ngathi ngumphambukeli endlwini yakho, wenze ipasika kuYehova, wayalusa yonke into eyindoda kuye; abe njengozalelwe kwelo zwe, ngokuba akukho mntu wasemzini uya kudla kuyo.', 'XHO22'),
(2, 12, 49, 'Wosebenza kwaloo mthetho ke, kwinzalelwane, nakowasemzini ophambukele phakathi kwenu.', 'XHO22'),
(2, 12, 50, 'Benjenjalo bonke oonyana bakaSirayeli; njengoko uYehova wabawisela umthetho uMoses noAron, benjenjalo.', 'XHO22'),
(2, 12, 51, 'Ngaloo mini uYehova wabakhupha oonyana bakaSirayeli ezweni laseYiputa ngokwemikhosi yabo.', 'XHO22'),
(2, 13, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(2, 13, 2, 'Ndihlambululele onke amazibulo, yonke into evula isizalo sikanina woonyana bakaSirayeli, kuthabathele ebantwini nakwinkomo; yeyam.', 'XHO22'),
(2, 13, 3, 'Wathi uMoses ebantwini, Yicingeni le mini ekuphumeni kwenu eYiputa, endlwini yobukhoboka; ngokuba uYehova enikhuphe kule ndawo ngesandla esithe nkqi; Ungasityi isonka esinegwele.', 'XHO22'),
(2, 13, 4, 'Namhla niphume ngenyanga enguAbhibhi.', 'XHO22'),
(2, 13, 5, 'Ekuniseni kukaYehova ezweni lamaKanan, namaHeti, nama-Amori, namaHeti, namaYebhusi, elo walifungela ooyihlo ukuba woninika, ilizwe elizele ngamasi nobusi; inkonzo kule nyanga.', 'XHO22'),
(2, 13, 6, 'Kangangeentsuku ezisixhenxe nodla izonka ezingenagwele, yaye ngomhla wesixhenxe ngumthendeleko kaYehova;', 'XHO22'),
(2, 13, 7, 'izonka ezingenagwele zodliwa iintsuku ezisixhenxe; Igwele aliyi kufunyanwa endlwini yakho, negwele aliyi kufunyanwa kuwe kuyo yonke indawo ohlala kuyo.', 'XHO22'),
(2, 13, 8, 'Uze umxelele unyana wakho ngaloo mini, uthi, ‘Oku kwenzeke ngenxa yoko uYehova wakwenzayo kum ekuphumeni kwam eYiputa.', 'XHO22'),
(2, 13, 9, 'Kuya kuba ngumqondiso kuwe esandleni sakho, sibe sisikhumbuzo phakathi kwamehlo akho, wokuba umyalelo kaYehova ube semlonyeni wakho; ngokuba uYehova wanikhupha eYiputa ngesandla esithe nkqi.', 'XHO22'),
(2, 13, 10, 'Wugcine ke lo myalelo ngexesha lawo elimisiweyo iminyaka ngeminyaka.', 'XHO22'),
(2, 13, 11, 'Wokuzisa uYehova ezweni lakwaKanan, njengoko wafungayo kooyihlo, wakunika lona;', 'XHO22'),
(2, 13, 12, 'ukuze ungcwalise kuYehova yonke into evula isizalo, into yonke elizibulo lenkomo, oya kuba nayo; into eyindoda ibe yekaYehova.', 'XHO22'),
(2, 13, 13, 'Into yonke elizibulo le-esile woyikhulula ngokuyimisela ngexhwane; ukuba uthe akwayindulula, yaphule intamo; Bonke abamazibulo koonyana bakho, ubakhulule ngokubamisela.', 'XHO22'),
(2, 13, 14, 'Wakubuza unyana wakho kamva, wathi, Yintoni na? uze uthi kuye, uYehova wasikhupha eYiputa ngesandla esithe nkqi, endlwini yobukhoboka;', 'XHO22'),
(2, 13, 15, 'Xa uFaro wayefuna ukusikhulula, uYehova wawabulala onke amazibulo ezweni laseYiputa, izibulo lomntu neenkomo; ngoko ke ndinikela kuYehova yonke into evula i-matrix njengomntu; ke wona amazibulo oonyana bam akayi kuwazala.', 'XHO22'),
(2, 13, 16, 'Kube luphawu esandleni sakho, kube njengenyanda entloko phakathi kwamehlo akho; ngokuba uYehova wasikhupha eYiputa ngesandla esithe nkqi.', 'XHO22'),
(2, 13, 17, 'Akubandulula ke uFaro abantu, uThixo akabakhokela ngendlela yelizwe lamaFilisti, nakuba ibikufuphi; ngokuba wathi uThixo, Ukuze bangaguquki abantu bakuyibona imfazwe, babuyele eYiputa.', 'XHO22'),
(2, 13, 18, 'uThixo wabajikeleza abantu ngendlela yentlango yoLwandle oluBomvu; bemka ke oonyana bakaSirayeli ezweni laseYiputa.', 'XHO22'),
(2, 13, 19, 'Wawathabatha ke uMoses amathambo kaYosefu, wahamba naye; nize niwathabathe amathambo am emke nani apha.', 'XHO22'),
(2, 13, 20, 'Banduluka eSukoti, bamisa e-Etam, enyeleni yentlango.', 'XHO22'),
(2, 13, 21, 'uYehova wabakhokela ngelifu emini, ukuba abakhaphele endleleni; ebusuku ngesibane somlilo ukubakhanyisela; ukuhamba imini nobusuku:', 'XHO22'),
(2, 13, 22, 'Akalisusa ilifu emini, nelifu lomlilo phambi kwabantu ebusuku.', 'XHO22'),
(2, 14, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(2, 14, 2, 'Thetha koonyana bakaSirayeli ukuba bajike, bamise iintente ngasePihagiroti, phakathi kweMigdoli nolwandle, malunga neBhahali-tsefon; ke ngoko, inkampu ngaselwandle.', 'XHO22'),
(2, 14, 3, 'Uya kuthi uFaro ngoonyana bakaSirayeli, Balele emhlabeni, intlango ibagubungela.', 'XHO22'),
(2, 14, 4, 'Ke mna ndiya kuyenza lukhuni intliziyo kaFaro, abalandele; ndizukiseke phambi koFaro, naphambi komkhosi wakhe wonke; ukuze azi amaYiputa ukuba ndinguYehova. Kwaye benjenjalo.', 'XHO22'),
(2, 14, 5, 'Waxelelwa ukumkani waseYiputa ukuba abantu babalekile, yaza intliziyo kaFaro neyabakhonzi bakhe yabavukela abantu, isithi: “Siyenzele ntoni na le nto, sanyula amaSirayeli ukuba asikhonze?', 'XHO22'),
(2, 14, 6, 'Walungisa inqwelo yakhe, wahamba nabantu bakhe.', 'XHO22'),
(2, 14, 7, 'Wathabatha amakhulu amathandathu eenqwelo zokulwa ezikhethiweyo, neenqwelo zonke zokulwa zaseYiputa, nabathetheli bazo kuzo zonke.', 'XHO22'),
(2, 14, 8, 'uYehova wayenza lukhuni intliziyo kaFaro ukumkani waseYiputa, ukuze abasikelele oonyana bakaSirayeli, baphuma oonyana bakaSirayeli ngesandla esiphakamileyo.', 'XHO22'),
(2, 14, 9, 'AmaYiputa abasukela, enamahashe onke eenqwelo zokulwa zikaFaro, nakhwelwayo akhe, nempi yakhe, abafumana bemise iintente ngaselwandle, ngasePi-hagiroti, phambi koBhahali.', 'XHO22'),
(2, 14, 10, 'Wasondela uFaro, bawaphakamisa amehlo abo oonyana bakaSirayeli, bawabona amaYiputa emva kwabo. Boyika kunene, babiza oonyana bakaSirayeli kuYehova.', 'XHO22'),
(2, 14, 11, 'Bathi kuMoses, kungokuba bekungekho mangcwaba na eYiputa, le nto usikhuphileyo ukuba sifele entlango apha? Yini na ukuba usenze le nto, usikhuphe eYiputa?', 'XHO22'),
(2, 14, 12, 'Asililo na elo ilizwi ebesilithetha kuwe eYiputa, sisithi, Masime siwakhonze amaYiputa? Kuba kube kusilungele kanye ukuba sikhonze amaYiputa, kunokuba sifele entlango apha.', 'XHO22'),
(2, 14, 13, 'Wathi uMoses ebantwini, Musani ukoyika; lindani nje, nilubone usindiso aya kunenzela lona uYehova namhla; kuba amaYiputa lawo niwabonileyo namhlanje, anisayi kuphinda niwabone naphakade.', 'XHO22'),
(2, 14, 14, 'uYehova uya kunilwela, ke nina nimele nithi cwaka.', 'XHO22'),
(2, 14, 15, 'Wathi uYehova kuMoses, Uyandibiza na? Thetha koonyana bakaSirayeli ukuba badlule.', 'XHO22'),
(2, 14, 16, 'Ke wena, phakamisa intonga yakho, wolule isandla sakho phezu kolwandle, ulwahlule, baluwele ulwandle oonyana bakaSirayeli kowomileyo.', 'XHO22'),
(2, 14, 17, 'Mna ke, yabona, ndiya kuzenza lukhuni iintliziyo zamaYiputa, awalandele; Ndiya kumzukisa uFaro, nempi yakhe yonke, neenqwelo zakhe zokulwa, nabamahashe bakhe;', 'XHO22'),
(2, 14, 18, 'azi amaYiputa ukuba ndinguYehova, ekumzukiseni kwam uFaro, neenqwelo zakhe zokulwa, nabakhweli bakhe;', 'XHO22'),
(2, 14, 19, 'Ingelosi kaTHIXO oyinyaniso, eyayikhokela umkhosi wamaSirayeli, yemka yawalandela; lesuka ilifu phambi kwabo, lema emva kwabo.', 'XHO22'),
(2, 14, 20, 'Wangena phakathi kwempi yamaYiputa, neyamaSirayeli; laba lilifu elimnyama kubo, libakhanyisela ebusuku, ukuba kungabikho uzayo komnye ubusuku bonke.', 'XHO22'),
(2, 14, 21, 'Wasolula uMoses isandla sakhe phezu kolwandle; yaye uYehova walwenza lwaduduza ulwandle bonke obo busuku ngomoya onamandla wasempuma waza walushukumisa ulwandle, aza ahluka amanzi.', 'XHO22'),
(2, 14, 22, 'Bahamba oonyana bakaSirayeli emhlabeni owomileyo, phakathi elwandle; aza amanzi aba ludonga ngasekunene nangasekhohlo kubo.', 'XHO22'),
(2, 14, 23, 'Abasukela amaYiputa, abalandela esazulwini solwandle, enamahashe onke kaFaro, neenqwelo zakhe zokulwa, nabamahashe bakhe;', 'XHO22'),
(2, 14, 24, 'Kwathi ngengomso uYehova wesuka waya emkhosini womlilo nelifu phezu komkhosi wamaYiputa, woyiswa umkhosi wamaYiputa.', 'XHO22'),
(2, 14, 25, 'Bayikhulula imilenze yeenqwelo zabo zokulwa kwabakhweli bawo, amaYiputa, athi, Masibaleke phambi kwamaSirayeli; ngokuba uYehova uyawalwela, esilwa namaYiputa.', 'XHO22'),
(2, 14, 26, 'Wathi uYehova kuMoses, Yolula isandla sakho phezu kolwandle, amanzi abuyele phezu kwamaYiputa, naphezu kweenqwelo zawo zokulwa, naphezu kwabakhweli bawo;', 'XHO22'),
(2, 14, 27, 'uMoses wasolula isandla sakhe phezu kolwandle, lwabuya ulwandle ngamandla alo xa kuthi qheke ukusa; asaba kuyo amaYiputa; yaye uYehova wawantywilisela amaYiputa phakathi elwandle.', 'XHO22'),
(2, 14, 28, 'Abuya amanzi, azigubungela iingqwelo zokulwa, nabamahashe, nempi yonke kaFaro eyayingene emva kwawo elwandle; akwasala namnye kubo.', 'XHO22'),
(2, 14, 29, 'Ke bona oonyana bakaSirayeli bahamba emhlabeni owomileyo phakathi elwandle; aba ludonga lwamanzi kuwo ngasekunene nangasekhohlo.', 'XHO22'),
(2, 14, 30, 'uYehova wawahlangula ke amaSirayeli ngaloo mini esandleni samaYiputa; oonyana bakaSirayeli bawabona amaYiputa efile elunxwemeni lolwandle.', 'XHO22'),
(2, 14, 31, 'Bawubona oonyana bakaSirayeli umsebenzi omkhulu uYehova awayewenze kumaYiputa, bamoyika abantu uYehova, bakholwa nguYehova noMoses umkhonzi wakhe.', 'XHO22'),
(2, 15, 1, 'Waza uMoses wahlabela le ngoma yokuzukisa uYehova, uMoses noonyana bakaSirayeli, bathi, Ndiya kuhlabela kuYehova, ngokuba unozuko olukhulu; Ihashe nomkhweli walo waliphosa elwandle.', 'XHO22'),
(2, 15, 2, 'uYehova ungamandla am nendumiso yam; Waba lusindiso kum; UnguThixo wam yena, ndiya kumlungiselela indawo yokuhlala; uThixo kabawo ndiya kumphakamisa.', 'XHO22'),
(2, 15, 3, 'uYehova unamandla, nguYehova igama lakhe.', 'XHO22'),
(2, 15, 4, 'Iinqwelo zokulwa zikaFaro nempi yakhe waziphosa elwandle; kwaye abalawuli bakhe abanyuliweyo barhaxwa eSchelfsee.', 'XHO22'),
(2, 15, 5, 'Inzonzobila ibagubungela, batshona ezantsi njengelitye.', 'XHO22'),
(2, 15, 6, 'Isandla sakho sokunene, Yehova, siqaqambile ngamandla; Isandla sakho sokunene, Yehova, siyaphule utshaba.', 'XHO22'),
(2, 15, 7, 'Ngobukhulu bobukhulu bakho ubagungxule abachasi bakho; Wathumela umsindo wakho, wabatshisa njengengca.', 'XHO22'),
(2, 15, 8, 'Ngokuvuthela kwamathatha akho ahlanganisana amanzi, imilambo yema njengemfumba, imithombo yatywinwa ezinzulwini zolwandle.', 'XHO22'),
(2, 15, 9, 'Lwathi utshaba, Ndiyasukela, ndoluse, ndahlule into ephangiweyo; umnqweno wam uya kwaneliswa ngabo; ndirhola ikrele lam, siya kubatshabalalisa isandla sam.', 'XHO22'),
(2, 15, 10, 'Wafutha ngomoya wakho, lwabagubungela ulwandle; basele njengelothe emanzini amakhulu.', 'XHO22'),
(2, 15, 11, 'Ngubani na onjengawe phakathi koothixo, Yehova? Ngubani na onjengawe, wena ubengezela ubungcwele, Woyikekayo ngezibongo, wenzayo ngokubalulekileyo?', 'XHO22'),
(2, 15, 12, 'Wolula isandla sakho sokunene, umhlaba wabaginya;', 'XHO22'),
(2, 15, 13, 'Uyabakhokela abantu obakhululeyo, obakhululeyo ngenceba yakho; Wabakhokela ngamandla akho esakhiweni sakho esingcwele.', 'XHO22'),
(2, 15, 14, 'Baya kuva abantu boyike; Usizi luya kubahlela abantu basePalestina.', 'XHO22'),
(2, 15, 15, 'Baya kumangaliswa abathetheli bakwaEdom; abanjwe kukuthuthumela amagorha akwaMowabhi; baya kudana bonke abemi bakwaKanan.', 'XHO22'),
(2, 15, 16, 'Bafikelwa kukunkwantya nokunkwantya; ngokobukhulu besandla sakho zothi cwaka njengelitye; bade badlule abantu bakho, Yehova, bade badlule abantu bakho entweni oyithengileyo.', 'XHO22'),
(2, 15, 17, 'Wobazisa, ubatyale entabeni yelifa lakho, kuloo ndawo uzenzele yona uYehova, ukuba uhlale kuyo, engcweleni eziyimisileyo izandla zakho, Yehova.', 'XHO22'),
(2, 15, 18, 'iNkosi iya kuba ngukumkani ngonaphakade;', 'XHO22'),
(2, 15, 19, 'Kuba ihashe likaFaro langena elwandle neenqwelo zakhe zokulwa nabakhweli bakhe, waza uYehova wawabuyisela phezu kwabo amanzi olwandle; kodwa oonyana bakaSirayeli bahamba emhlabeni owomileyo phakathi elwandle.', 'XHO22'),
(2, 15, 20, 'uMiriyam umprofetikazi, udade boAron, wathabatha igubu ngesandla sakhe; aphuma ke onke amankazana, amlandela eneengqongqo, nezangoma.', 'XHO22'),
(2, 15, 21, 'Wawaphendula uMiriyam, wathi, Vumani nidumise uYehova, kuba ekhazimle ngobungangamsha; ihashe nomkhweli walo babajulela elwandle.', 'XHO22'),
(2, 15, 22, 'uMoses wabathabatha oonyana bakaSirayeli eLwandle oluBomvu, bahamba baya entlango yaseShure; bahamba iintsuku zantathu entlango, befuna amanzi.', 'XHO22'),
(2, 15, 23, 'Afika eMara, akaba nakuwasela amanzi aseMara, kuba ayekrakra; ngenxa yoko kwathiwa igama laso yiMara.', 'XHO22'),
(2, 15, 24, 'Abantu bamkrokrela uMoses, besithi, Siya kusela ntoni na?', 'XHO22'),
(2, 15, 25, 'Wanqula uYehova; uYehova wambonisa umthi, awawuphosa emanzini, aba mnandi amanzi; khona wabamisela umyalelo nommiselo, wabancina khona.', 'XHO22'),
(2, 15, 26, 'Wathi, Ukuba uthe waliphulaphula izwi likaYehova uThixo wakho, wenza okuthe tye emehlweni akhe, wayibekela indlebe imithetho yakhe, wayigcina yonke imithetho yakhe; ndakuzisa kumaJiputa, kuba ndinguYehova, uphilisayo.', 'XHO22'),
(2, 15, 27, 'Bafika e-Elim, apho bekukho imithombo elishumi elinamabini, nemithi yesundu engamashumi omathandathu; bema khona ngasemanzini.', 'XHO22'),
(2, 16, 1, 'Banduluka e-Elim, lafika lonke ibandla loonyana bakaSirayeli entlango yaseSin, ephakathi kwe-Elim neSinayi, ngomhla weshumi elinesihlanu wenyanga yesibini yokufuduka kwabo ezweni laseYiputa.', 'XHO22'),
(2, 16, 2, 'Labakrokrela ooMoses noAron lonke ibandla loonyana bakaSirayeli entlango apho.', 'XHO22'),
(2, 16, 3, 'Bathi oonyana bakaSirayeli kubo, Ukuba besife sisandla sikaYehova ezweni laseYiputa, xa sasihleli ngasezimbizeni zenyama, sidla izonka ezininzi; ngokuba nisikhuphele kule ntlango, ukuze nilibulale lonke eli bandla ngendlala.', 'XHO22'),
(2, 16, 4, 'Wathi uYehova kuMoses, Uyabona, ndiya kunisa isonka siphuma ezulwini; yaye abantu bayaphuma baze baqokelele umlinganiselo othile imihla ngemihla, ukuze ndibavavanye enoba baya kuhamba emthethweni wam okanye akunjalo kusini na.', 'XHO22'),
(2, 16, 5, 'Kothi ngomhla wesithandathu, balungise oko bakuzisileyo; kwaye iya kuphinda kabini imali abayiqokelela imihla ngemihla.', 'XHO22'),
(2, 16, 6, 'Wathi uMoses noAron koonyana bakaSirayeli bonke, Ngokuhlwa niya kwazi ukuba uYehova wanikhupha nini ezweni laseYiputa.', 'XHO22'),
(2, 16, 7, 'Ngomso niya kububona ubuqaqawuli bukaYehova; ngokuba ukuvile ukukrokra kwenu kuYehova. Kwaye singoobani na xa usikhalazela?', 'XHO22'),
(2, 16, 8, 'Wathi uMoses, Ekuninikeni kukaYehova inyama ngokuhlwa, noyidla, woninika ngomso isonka nenyama; ngokuba uYehova uyakuva ukukrokra kwenu enikhala ngako. Kwaye siyintoni? anixhalabelanga thina, kodwa nimkhalazela uYehova.', 'XHO22'),
(2, 16, 9, 'Wathi uMoses kuAron, Yithi kwibandla lonke loonyana bakaSirayeli, Sondelani kuYehova, ngokuba ukuvile ukuduma kwenu.', 'XHO22'),
(2, 16, 10, 'Ke kaloku, esathetha uAron kwibandla lonke loonyana bakaSirayeli, babheka entlango, babubona ubuqaqawuli bukaYehova efini.', 'XHO22'),
(2, 16, 11, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 16, 12, 'Ndikuvile ukukrokra koonyana bakaSirayeli. Uthethe kubo uthi, Lakutshona ilanga nodla inyama, ngomso nodla isonka; nazi ukuba ndinguYehova uThixo wenu.', 'XHO22'),
(2, 16, 13, 'Kwahlwa, zenyuka izagwityi, zayigubungela iminquba yonke;', 'XHO22'),
(2, 16, 14, 'Wenyuka umbethe oweleyo, nanko kukho eArabha intwana, ejiyileyo, encinanana njengeqabaka emhlabeni.', 'XHO22'),
(2, 16, 15, 'Bayibona oonyana bakaSirayeli, bathi omnye komnye, Yimana le, ngokuba babengazi ukuba yintoni na. Wathi uMoses kubo, Sisonka esi abanikileyo uYehova ukuba badle;', 'XHO22'),
(2, 16, 16, 'Wawisa umthetho uYehova, wathi, Buthani kuyo, elowo ngangokudla kwakhe, ihomere elowo, ngangenani lemizimba yenu; elowo athabathe abo basententeni yakhe.', 'XHO22'),
(2, 16, 17, 'Benjenjalo oonyana bakaSirayeli; babutha omnye kakhulu, omnye kancinane.', 'XHO22'),
(2, 16, 18, 'Bakuba belinganisa ngegomere, akashiywanga obuthe iqashiso, nobutha kancinane akaswelanga; babutha elowo ngangokudla kwakhe.', 'XHO22'),
(2, 16, 19, 'Wathi uMoses, Makungasali namnye kude kuse.', 'XHO22'),
(2, 16, 20, 'Abamphulaphulanga uMoses; Bahlala ke kuyo kwada kwasa, zakuphuma iimpethu, nevumba; waba nomsindo uMoses kubo.', 'XHO22'),
(2, 16, 21, 'Bayiqokelela intsasa nganye, ngamnye njengoko esidla; kwathi lakuba shushu ilanga, yanyibilika.', 'XHO22'),
(2, 16, 22, 'Ngomhla wesithandathu babutha isonka semihla emibini, iihomere ezimbini mntwini mnye; zeza zonke izikhulu zebandla, zamxelela uMoses.', 'XHO22'),
(2, 16, 23, 'Wathi kubo, Utsho uYehova ukuthi, Ngomso yisabatha kaYehova; yidla obuya kukupheka namhla, upheke oko uya kukupheka; kwaye okuseleyo uzigcinele ingomso.', 'XHO22'),
(2, 16, 24, 'Bayibeka ke kwada kwasa, njengoko uMoses wabawisela umthetho; akanuka, akwabakho mpethu kuyo.', 'XHO22'),
(2, 16, 25, 'Wathi uMoses Yidleni namhla; ngokuba namhla yisabatha kaYehova; namhlanje aniyi kuyifumana elizweni.', 'XHO22'),
(2, 16, 26, 'Noyiqokelela ngeentsuku ezintandathu; ke ngomhla wesixhenxe, yisabatha, akusayi kuba njalo.', 'XHO22'),
(2, 16, 27, 'Yaye kwathi, bambi ebantwini baphuma ngomhla wesixhenxe besiya kubutha, kodwa ababona nto.', 'XHO22'),
(2, 16, 28, 'Wathi uYehova kuMoses, Kuya kuda kube nini na, ningavumi ukuyigcina imithetho yam nemiyalelo yam?', 'XHO22'),
(2, 16, 29, 'Niyabona ke, uYehova uninike isabatha; kungenxa yoko athe ngomhla wesithandathu waninika isonka semihla emibini. Mabahlale elowo endaweni yakhe, kungaphumi mntu endaweni yakhe ngomhla wesixhenxe;', 'XHO22'),
(2, 16, 30, 'Baye bephumla ke abantu ngomhla wesixhenxe.', 'XHO22'),
(2, 16, 31, 'Indlu kaSirayeli yathi igama lale nto yimana, lalimhlophe njengembewu yekoriyandire; isongo sayo sasifana namanxeba obusi.', 'XHO22'),
(2, 16, 32, 'Wathi uMoses, Nantsi into awise umthetho ngayo uYehova, ukuthi, Yizaliseni ngehomere, niyigcine kwizizukulwana zenu; ukuze asibone isonka endibadlise ngaso entlango, ekubakhupheni kwam ezweni laseYiputa.', 'XHO22'),
(2, 16, 33, 'Wathi uMoses kuAron, Thabatha imbiza, ubeke imana kuyo, uyibeke phambi koYehova, igcinelwe izizukulwana zenu.', 'XHO22'),
(2, 16, 34, 'Njengoko uYehova wamyalelayo uMoses, uAron wayibeka phambi kwengqina ukuze ikhuseleke.', 'XHO22'),
(2, 16, 35, 'Bayidla oonyana bakaSirayeli imana iminyaka emashumi mane, bada bafika ezweni elimiweyo; bayidla imana bada bafika emdeni welizwe lakwaKanan.', 'XHO22'),
(2, 16, 36, 'Igomere sisishumi se-efa.', 'XHO22'),
(2, 17, 1, 'Lemka ke lonke ibandla loonyana bakaSirayeli entlango yaseTsin ngokweehambo zabo, ngokomlomo kaYehova, baya eRefidim. Akwabakho manzi okuba basele abantu.', 'XHO22'),
(2, 17, 2, 'Balwa abantu noMoses, bathi, Siphe amanzi, sisele; Wathi uMoses kubo, Yini na ukuba nihlukane nam? uyambuza na uYehova?', 'XHO22'),
(2, 17, 3, 'Banxanelwe amanzi apho abantu; abantu bamkrokrela uMoses, bathi, Nguwe owasikhupha eYiputa, ukuze usibulale ngenxano, thina, noonyana bethu, nemfuyo yethu?', 'XHO22'),
(2, 17, 4, 'uMoses wabiza uYehova, wathi, Ndiya kubathini na aba bantu? Baza kundixuluba ngamatye.', 'XHO22'),
(2, 17, 5, 'Wathi uYehova kuMoses, Phuma phambi kwabantu, uthabathe inxenye yamadoda amakhulu akwaSirayeli; nentonga yakho obuzibophe ngayo, yiphathe ngesandla sakho, uhambe.', 'XHO22'),
(2, 17, 6, 'Yabona, ndiya kuma nawe khona, phezu kweliwa laseHorebhe; yaye uya kulibetha iliwa, kuphume amanzi kulo ukuze basele abantu. Wenjenjalo ke uMoses phambi kwamadoda amakhulu akwaSirayeli.', 'XHO22'),
(2, 17, 7, 'Wathi igama laloo ndawo yiMasa neMeribha, ngenxa yengxabano yoonyana bakaSirayeli, nangenxa yokubuza kwabo kuYehova, besithi, uYehova uphakathi kwethu, akakho, kusini na?', 'XHO22'),
(2, 17, 8, 'Emva koko uAmaleki weza kulwa noonyana bakaSirayeli eRefidim.', 'XHO22'),
(2, 17, 9, 'Wathi uMoses kuYoshuwa, Sinyulele amadoda, uphume uwaxabele ama-Amaleki; Mna ngomso ndiya kuma encotsheni yenduli, ndiphethe intonga kaThixo ngesandla sam.', 'XHO22'),
(2, 17, 10, 'Wenza uYoshuwa njengoko uMoses wamwiselayo umthetho, walwa nama-Amaleki; benyuka ooMoses, noAron, noHure, baya encotsheni yenduli.', 'XHO22'),
(2, 17, 11, 'Wasiphakamisa uMoses isandla sakhe, boyisa oonyana bakaSirayeli; wathi akuthambathisa isandla sakhe, kweyisa uAmaleki.', 'XHO22'),
(2, 17, 12, 'Zaba nzima ke izandla zikaMoses; Bathabatha ilitye, balibeka phantsi kwakhe, wahlala phezu kwalo; Baziphakamisa ooAron noHure izandla zabo, omnye engapha, omnye engaphaya; zaza izandla zakhe zomelela lada latshona ilanga.', 'XHO22'),
(2, 17, 13, 'uYoshuwa wamdubaduba uAmaleki nabantu bakhe ngohlangothi lwentshuntshe.', 'XHO22'),
(2, 17, 14, 'Wathi uYehova kuMoses, Kubhale encwadini ibe sisikhumbuzo, ukuthethe apho asekuva uYoshuwa; ngokuba ndiya kukuphelisa ukukhunjulwa kuka-Amaleki phantsi kwezulu.', 'XHO22'),
(2, 17, 15, 'Wakha uMoses isibingelelo, wathi igama laso nguYehova-Nisi.', 'XHO22'),
(2, 17, 16, 'Ngenxa yokuba wathi: “Ngenxa yokuba uYehova efungile ukuba uYehova uya kumhlasela uAmaleki kwizizukulwana ngezizukulwana.', 'XHO22'),
(2, 18, 1, 'uYitro, umbingeleli wakwaMidiyan, umolokazana kaMoses, wayiva yonke into uThixo abeyenzileyo kuMoses, nakubantu bakhe amaSirayeli, ukuba uYehova uwakhuphile amaSirayeli eYiputa.', 'XHO22'),
(2, 18, 2, 'uYitro, uyise womkaMoses, wamthabatha uTsipora, umkaMoses, emveni kokuba uMoses wambuyisa,', 'XHO22'),
(2, 18, 3, 'noonyana bakhe bobabini; igama lomnye nguGershom; ngokuba wathi, Bendingumphambukeli ezweni lolunye uhlanga.', 'XHO22'),
(2, 18, 4, 'Igama lomnye lalinguEliyezere; ngokuba wathi uThixo kabawo ubengumncedi wam, wandihlangula ekreleni likaFaro.', 'XHO22'),
(2, 18, 5, 'uYitro, umkhwe kaMoses, weza ke noonyana bakhe nomkakhe, kuMoses entlango, apho wayemise intente khona entabeni yoThixo oyinyaniso.', 'XHO22'),
(2, 18, 6, 'Wathi kuMoses, Mna Yitro, unguyihlo, ndize kuwe, ndinomkakho noonyana bakhe bobabini.', 'XHO22'),
(2, 18, 7, 'Waphuma uMoses, waya kumkhawulela uyise womkakhe, waqubuda, wamanga; babuzana impilo; bangena ententeni.', 'XHO22'),
(2, 18, 8, 'uMoses wamxelela umkakhe konke uYehova abekwenzile kuFaro namaYiputa ngenxa yamaSirayeli, neembandezelo zonke eziwafikeleyo endleleni, nokuwahlangula kukaYehova.', 'XHO22'),
(2, 18, 9, 'Wavuya uYitro ngako konke ukulunga abekwenzile uYehova kumaSirayeli, awabahlangulayo esandleni samaYiputa.', 'XHO22'),
(2, 18, 10, 'Wathi uYitro, Makabongwe uYehova owanihlangulayo esandleni samaYiputa, nasesandleni sikaFaro, owabahlangulayo abantu esandleni samaYiputa.', 'XHO22'),
(2, 18, 11, 'Ngoku ndiyazi ukuba uYehova mkhulu koothixo bonke, ngenxa yokuba ngombandela ababeqhayisa ngawo, wayengaphezu kwabo.', 'XHO22'),
(2, 18, 12, 'uYitro, uyise womkaMoses, wamthabathela uThixo idini elinyukayo nemibingelelo; weza uAron namadoda amakhulu onke akwaSirayeli, ukuba adle isonka noyise womkaMoses phambi koThixo.', 'XHO22'),
(2, 18, 13, 'Ngengomso uMoses wahlala phantsi wathetha amatyala abantu; abantu bema ngakuMoses, kususela kusasa kwada kwangokuhlwa.', 'XHO22'),
(2, 18, 14, 'Wayibona uninazala kaMoses yonke into abeyenza ebantwini, wathi, Iyintoni na le nto uyenzayo ebantwini? Yini na, ukuba uhlale wedwa, bathi bona bonke abantu baxhontele kuwe, kususela kusasa kude kuhlwe?', 'XHO22'),
(2, 18, 15, 'Wathi uMoses kumkakhe, Kungokuba abantu besiza kum ukuba bacele kuThixo;', 'XHO22'),
(2, 18, 16, 'xa banendawo, baye beze kum; ndigwebe phakathi kwabo, ndibazise imithetho kaThixo nemiyalelo yabo.', 'XHO22'),
(2, 18, 17, 'Wathi uyise womkaMoses kuye, Ayilungile le nto uyenzayo.', 'XHO22'),
(2, 18, 18, 'Uya kudinwa, wena naba bantu unabo; ngokuba le nto inzima kuwe; awunakuyenza ngokwakho.', 'XHO22'),
(2, 18, 19, 'Phulaphula ngoko izwi lam, ndikuphe iqhinga, uThixo abe nawe: yiba noThixo ngenxa yabantu, ukuze umxelele uThixo iindaba ezilungileyo;', 'XHO22'),
(2, 18, 20, 'ubafundise imimiselo nemimiselo, ubabonise indlela abaya kuhamba ngayo, nomsebenzi abaya kuwenza.', 'XHO22'),
(2, 18, 21, 'Ngaphezu koko, uze uzilungisele amadoda anobunkunkqele ekuthetheni amatyala, amoyikayo uThixo, ayinyaniso, ayithiyileyo inkanuko; ubamisele ukuba babe ngabathetheli bamawaka, nabathetheli bamakhulu, nabathetheli bamashumi ngamahlanu, nabathetheli bamashumi ngamanye;', 'XHO22'),
(2, 18, 22, 'bathethe amatyala abantu ngamaxesha onke; zonke izinto ezinkulu ziya kuzizisa kuwe, zonke ezincinane baya kuzigweba; ngoko kuya kuba lula kuwe, bawuthwale nawe umthwalo.', 'XHO22'),
(2, 18, 23, 'Ukuba uthe wayenza loo nto, waza wakuwisela umthetho uThixo, woba nako ukuqiniseka, luhambe olu hlanga lonke lunoxolo, endaweni yalo.', 'XHO22'),
(2, 18, 24, 'uMoses waliphulaphula izwi loyise womkakhe, wayenza into yonke abeyithethile.', 'XHO22'),
(2, 18, 25, 'uMoses wanyula amadoda anobunkunkqele ekuthetheni amatyala kuwo onke amaSirayeli, wawamisa aba ziintloko zabantu, aba ngabathetheli-waka, nabathetheli-makhulu, nabathetheli bamashumi ngamahlanu, nabathetheli bamashumi ngamanye.', 'XHO22'),
(2, 18, 26, 'Babethutha abantu ngalo lonke ixesha; Izinto ezinzima bazisa kuMoses, kodwa zonke ezincinane bazigweba.', 'XHO22'),
(2, 18, 27, 'uMoses wathi kuyise womkakhe, mabahambe; wemka kwelakowabo.', 'XHO22'),
(2, 19, 1, 'Ngenyanga yesithathu emveni kokuphuma koonyana bakaSirayeli ezweni laseYiputa, ngayo loo mini, bafika entlango yaseSinayi.', 'XHO22'),
(2, 19, 2, 'Banduluka eRefidim, bafika entlango yaseSinayi, bamisa entlango; baza oonyana bakaSirayeli bema apho phambi kwentaba.', 'XHO22'),
(2, 19, 3, 'uMoses wenyuka waya kuThixo, uYehova wambiza esentabeni apho, wathi, Uya kwenjenjalo kwindlu kaYakobi, wathi koonyana bakaSirayeli;', 'XHO22'),
(2, 19, 4, 'Khangelani into endayenzayo kumaYiputa, ukuba ndanithwala ngamaphiko okhozi, ndanizisa kum.', 'XHO22'),
(2, 19, 5, 'Ngoku ukuba nithe naliphulaphula ilizwi lam, nawugcina umnqophiso wam, niya kuba bubutyebi bempahla yam ngaphezu kwazo zonke iintlanga, kuba ihlabathi liphela lelam;', 'XHO22'),
(2, 19, 6, 'nibe bubukumkani bababingeleli kum, nohlanga olungcwele; Ngawo la amazwi oya kuwathetha koonyana bakaSirayeli.', 'XHO22'),
(2, 19, 7, 'Weza ke uMoses, wawabiza amadoda amakhulu abantu, wabeka phambi kwawo onke la mazwi uYehova awamyalela wona.', 'XHO22'),
(2, 19, 8, 'Baphendula bonke abantu ngamxhelo mnye, bathi, Konke akuthethileyo uYehova sokwenza. Wawabuyisela uMoses amazwi abantu kuYehova.', 'XHO22'),
(2, 19, 9, 'Wathi uYehova kuMoses, Yabona, ndiyeza kuwe ndiselifini elingqingqwa, ukuze beve abantu ekuthetheni kwam nawe, bakholwe kuwe ngonaphakade. uMoses wamxelela uYehova amazwi abantu.', 'XHO22'),
(2, 19, 10, 'Wathi uYehova kuMoses, Yiya ebantwini, ubangcwalise namhla nangomso, bazihlambe iingubo zabo;', 'XHO22'),
(2, 19, 11, 'Zilungiseleleni ngomhla wesithathu, kuba ngomhla wesithathu uYehova uya kuhla eNtabeni yeSinayi phambi kwamehlo ohlanga luphela.', 'XHO22'),
(2, 19, 12, 'Bamisele abantu imida, uthi, Zigcineni ningenyuki entabeni, ningawuchukumisi umda wayo;', 'XHO22'),
(2, 19, 13, 'Makungafiki sandla kuye; akuyi kuphila inkomo nomntu; Lakhala ixesha elide isigodlo, benyuka baya entabeni.', 'XHO22'),
(2, 19, 14, 'Wehla uMoses entabeni, waya ebantwini, wabangcwalisa abantu; bazihlamba iingubo zabo.', 'XHO22'),
(2, 19, 15, 'Wathi ebantwini, Yilungiseni ngomhla wesithathu; musani ukuza kubafazi benu.', 'XHO22'),
(2, 19, 16, 'Kwathi ngomhla wesithathu, kwakusa, kwabakho iindudumo, nemibane, nelifu elinzima phezu kwentaba, nesandi sesigodlo esomeleleyo kakhulu; kangangokuba bonke abantu ababesenkampini bangcangcazela.', 'XHO22'),
(2, 19, 17, 'uMoses wabakhupha abantu eminqubeni, baya kumkhawulela uThixo; bema emazantsi entaba.', 'XHO22'),
(2, 19, 18, 'Intaba yeSinayi yaba ngumsi kwaphela, kuba uYehova wehla phezu kwayo esemlilweni; wenyuka umsi apho njengomsi weziko lemfutho, yaza yanyikima kakhulu yonke intaba.', 'XHO22'),
(2, 19, 19, 'Lathi lavakala isandi sesigodlo, sikhula, wathetha uMoses, uThixo wamphendula ngelizwi.', 'XHO22'),
(2, 19, 20, 'Wehla uYehova phezu kwentaba yeSinayi, encotsheni yentaba, uYehova wambizela uMoses encotsheni yentaba leyo; wenyuka ke uMoses.', 'XHO22'),
(2, 19, 21, 'Wathi uYehova kuMoses, Yihla, ubawisele umthetho abantu, hleze batyhobozele baye kumkhangela uYehova, kwenzakale into eninzi kubo.', 'XHO22'),
(2, 19, 22, 'Kananjalo ababingeleli abasondela kuYehova bafanele bazingcwalise, ukuze uYehova angavuki nxamnye nabo.', 'XHO22'),
(2, 19, 23, 'Wathi uMoses kuYehova, Abantu abanakunyuka intaba yeSinayi; ngokuba wasiwisela umthetho, wathi, Yimisele imida intaba, uyingcwalise.', 'XHO22'),
(2, 19, 24, 'Wathi uYehova kuye, Hamba uhle, unyuke, wena noAron; ke ababingeleli nabantu mabangatyhobozi, benyuke baye kuYehova, hleze abasukele.', 'XHO22'),
(2, 19, 25, 'Wehla uMoses, waya ebantwini, wathetha kubo.', 'XHO22'),
(2, 20, 1, 'Wawathetha ke uThixo onke la mazwi, wathi:', 'XHO22'),
(2, 20, 2, 'NdinguYehova, uThixo wakho, owakukhuphayo ezweni laseYiputa, endlwini yobukhoboka.', 'XHO22'),
(2, 20, 3, 'Uze ungabi nathixo bambi ngaphandle kwam.', 'XHO22'),
(2, 20, 4, 'Uze ungazenzeli umfanekiso oqingqiweyo, nokuba ngowayiphi na into emilise esezulwini phezulu, nokuba yinto yonke ephantsi komhlaba, nesemanzini aphantsi komhlaba.', 'XHO22'),
(2, 20, 5, 'Uze ungaqubudi kwezo nto, ungazikhonzi; kuba mna Yehova Thixo wakho, ndinguThixo onekhwele, ndibuvelela ububi koonyana booyise, kwesesithathu nakwesesine isizukulwana sabandithiyileyo.', 'XHO22'),
(2, 20, 6, 'Ndibabale amawaka abandithandayo, abayigcinayo imithetho yam.', 'XHO22'),
(2, 20, 7, 'Uze ungafumane ulibize igama likaYehova uThixo wakho; ngenxa yokuba uYehova akayi kumyeka osebenzisa igama lakhe njengelize.', 'XHO22'),
(2, 20, 8, 'Khumbula umhla wesabatha ukuba uzingcwalise.', 'XHO22'),
(2, 20, 9, 'Wosebenza imihla emithandathu, uwenze wonke umsebenzi wakho;', 'XHO22'),
(2, 20, 10, 'ke wona umhla wesixhenxe uyisabatha kaYehova uThixo wakho; Uze ungenzi namnye umsebenzi ngayo, wena, nonyana wakho, nentombi yakho, nesicaka sakho, nesicakakazi sakho, nemfuyo yakho, nowasemzini okuwe. Amasango:', 'XHO22'),
(2, 20, 11, 'Ngokuba ngemihla emithandathu uYehova wenza izulu nomhlaba, nolwandle, neento zonke ezikwezo zinto, waphumla ngomhla wesixhenxe; ngenxa yoko uYehova wawusikelela umhla wesabatha, wawungcwalisa.', 'XHO22'),
(2, 20, 12, 'Beka uyihlo nonyoko, ukuze yolulwe imihla yakho emhlabeni akunika wona uYehova uThixo wakho.', 'XHO22'),
(2, 20, 13, 'Uze ungabulali;', 'XHO22'),
(2, 20, 14, 'Musa ukukrexeza.', 'XHO22'),
(2, 20, 15, 'Musa ukuba.', 'XHO22'),
(2, 20, 16, 'Musa ukuba lingqina lobuxoki ngommelwane wakho.', 'XHO22'),
(2, 20, 17, 'Uze ungawunqweneli umzi wommelwane wakho, ungamnqweneli umfazi wommelwane wakho, nesicaka sakhe, nesicakakazi sakhe, nenkomo yakhe, ne-esile lakhe, nento yonke eyeyakho.', 'XHO22'),
(2, 20, 18, 'Bonke abantu bazibona iindudumo nemibane, nesandi sesigodlo, nentaba engumsi; Bakubona ke abantu oko, bemka baya kuma mgama.', 'XHO22'),
(2, 20, 19, 'Bathi kuMoses, Thetha wena kuthi, siya kuva; kodwa wanga uThixo akathethi kuthi, ukuze singafi.', 'XHO22'),
(2, 20, 20, 'Wathi uMoses ebantwini, Musani ukoyika; kuba uThixo uze kunilinga, ukuba kubekho ukumoyika ebusweni benu, ukuze ningoni.', 'XHO22'),
(2, 20, 21, 'Abantu bema mgama, waza uMoses wasondela ebumnyameni apho akhoyo uThixo.', 'XHO22'),
(2, 20, 22, 'Wathi uYehova kuMoses, Yithi koonyana bakaSirayeli, Nikhangele, ndithethe kuni ndisemazulwini.', 'XHO22'),
(2, 20, 23, 'Musani ukuzenzela uthixo wesilivere, ningazenzeli oothixo begolide;', 'XHO22'),
(2, 20, 24, 'undenzele isibingelelo somhlaba, wenze phezu kwaso amadini akho anyukayo, nemibingelelo yakho yombulelo, impahla yakho emfutshane neenkomo zakho; naphi na apho ndithe ndalibiza khona igama lam, ndiya kuza kuwe, ndikusikelele.', 'XHO22'),
(2, 20, 25, 'Ukuba uthe wandakhela isibingelelo samatye, musa ukundakhela ngamatye aqingqiweyo; ngokuba xa uthe wayigalela impahla yakho kuyo, woyingcwalisa.', 'XHO22'),
(2, 20, 26, 'Musani ukunyuka ngezinyuko ukuya esibingelelweni sam, ukuze bungatyhileki into yenu engamampunge.', 'XHO22'),
(2, 21, 1, 'Yiyo le imimiselo oya kuyimisa kubo;', 'XHO22'),
(2, 21, 2, 'Ukuba uthe wathenga ikhoboka elingumHebhere, lokhonza iminyaka emithandathu; nangolwesixhenxe uya kuhamba ngokukhululekileyo.', 'XHO22'),
(2, 21, 3, 'Ukuba ingene yodwa, mayiphume iyodwa; ukuba liyindoda enomfazi, umfazi walo wophuma nalo.', 'XHO22'),
(2, 21, 4, 'Xa ithe inkosi yakho yakunika umfazi, yakuzalela oonyana, nokuba ziintombi; umfazi nabantwana bayo baya kuba nenkosi yayo, kodwa yona iya kuphuma yodwa.', 'XHO22'),
(2, 21, 5, 'Ukuba umkhonzi uthe, Ndiyayithanda inkosi yam, nomkam, nabantwana bam; andiyi kuphuma ndikhululekile.', 'XHO22'),
(2, 21, 6, 'Yokuzisa ke inkosi yakho kubaphathi; umsondeze elucangweni, nokuba emgubasini; inkosi yakho iya kuyigqobhoza indlebe ngomkhonto; yaye uya kumkhonza ngonaphakade.', 'XHO22'),
(2, 21, 7, 'Xa ithe indoda yathengisa ngentombi yayo ukuba ibe likhoboka, mayingaphumi njengekhoboka.', 'XHO22'),
(2, 21, 8, 'Ukuba uthe akayikholisa inkosi yakhe, ebimganele, yoyivumela ukuba ikhululwe ngentlawulelo; ukuba athengise ngaye ebantwini beentlanga, akayi kuba nagunya, ngokuba umlahlekisile.', 'XHO22'),
(2, 21, 9, 'Ukuba ithe yayimisela unyana wayo, makayenze ngokwesiko leentombi;', 'XHO22'),
(2, 21, 10, 'Ukuba ithe yazeka omnye umfazi; ukutya kwakhe, nempahla yakhe, nelungelo lakhe lokutshata, akufuneki lincitshiswe.', 'XHO22'),
(2, 21, 11, 'Ukuba ayithanga izenze ezo zinto zontathu, lophuma simahla ngaphandle kwemali.', 'XHO22'),
(2, 21, 12, 'Othe wabetha umntu wafa, wobulawa afe.', 'XHO22'),
(2, 21, 13, 'Ukuba ungaboni bani, uThixo wamnikela esandleni sakhe; ndonibonisa ke indawo aya kusabela kuyo.', 'XHO22'),
(2, 21, 14, 'Xa athe umntu wafika kummelwane wakhe, wambulala ngobuqhophololo; umsuse esibingelelweni sam, afe.', 'XHO22'),
(2, 21, 15, 'Othe wabetha uyise nokuba ngunina, wobulawa afe.', 'XHO22'),
(2, 21, 16, 'Othe weba umntu, nokuba uthengise ngaye, nokuba ufunyenwe esandleni sakhe, wobulawa afe.', 'XHO22'),
(2, 21, 17, 'Othe watshabhisa uyise nonina, wobulawa afe.', 'XHO22'),
(2, 21, 18, 'Ngokungathi abantu bayalwa, omnye wabetha omnye ngelitye, nangenqindi, akafa, wathabatha ukhuko lwakhe;', 'XHO22'),
(2, 21, 19, 'Ukuba uthe wavuka, wahamba ngentonga yakhe, woba msulwa lowo umbethayo; nguye yedwa oya kuhlawula inkcitho yexesha lakhe aze amphilise ngokupheleleyo.', 'XHO22'),
(2, 21, 20, 'Xa athe umntu wabetha ikhoboka lakhe nokuba sisicakakazi sakhe ngentonga, safa ngesandla sakhe; wohlwaywa ngenene.', 'XHO22'),
(2, 21, 21, 'Kodwa ukuba sithe sadla ubomi, asafa, usuku nokuba ziintsuku: makungayi kubethwa, ngokuba yimali yakhe.', 'XHO22'),
(2, 21, 22, 'Xa athe amadoda abambana ngezihlwitha, agila umfazi emithi, waphuma isisu, akwabakho bubi emveni koko: wohlawuliswa ngenene, njengendoda yaloo mfazi, ibimbekile kuye; kwaye ahlawule ngokomyalelo wabagwebi.', 'XHO22'),
(2, 21, 23, 'Ukuba kuthe kwenyuka ububi, wohlawula umphefumlo ngomphefumlo;', 'XHO22'),
(2, 21, 24, 'Iliso ngeliso, izinyo ngezinyo, isandla ngesandla, unyawo ngonyawo,', 'XHO22'),
(2, 21, 25, 'ukutshiswa ngokutshiswa, inxeba ngenxeba, inxeba ngenxeba.', 'XHO22'),
(2, 21, 26, 'Xa athe umntu wasibetha esweni isicaka sakhe, nokuba uthe wasibetha esweni isicakakazi sakhe, sotshabalala; uya kumkhulula ngenxa yeliso lakhe.', 'XHO22'),
(2, 21, 27, 'Ukuba uthe wabetha izinyo lesicaka sakhe, nokuba lizinyo lomkhonzazana wakhe; makahambe ekhululekile ngenxa yezinyo lakhe.', 'XHO22'),
(2, 21, 28, 'Ukuba inkomo ithe yadla indoda, nokuba yinkazana, yafa: loo nkomo mayigityiselwe ngamatye, ingadliwa inyama yayo; kodwa umnininkomo uya kukhululwa.', 'XHO22'),
(2, 21, 29, 'Ke ukuba inkomo ithe yahlaba ngesigodlo, walingqina umniniyo, akabuyeli, wabulala indoda nenkazana; iinkomo mazixulutywe ngamatye, nomninizo abulawe.', 'XHO22'),
(2, 21, 30, 'Ukuba uthe wahlawuliswa imali eninzi, woyirhola yonke into eya kubekwa kuye, ibe yintlawulelo yomphefumlo wakhe.', 'XHO22'),
(2, 21, 31, 'Nokuba ithe yabetha unyana nokuba yintombi, makwenziwe ngoko kuye.', 'XHO22'),
(2, 21, 32, 'Xa inkomo ithe yahlaba isicaka, nokuba sisicakakazi; amnike umninizo iishekele zesilivere ezimashumi mathathu, inkomo yona igityiselwe ngamatye.', 'XHO22'),
(2, 21, 33, 'Xa athe umntu wavula iqula, nokuba uthe wemba iqula, akaligquma, kweyela kulo inkomo, nokuba liesile;', 'XHO22'),
(2, 21, 34, 'Umninimthombo wolilungisa, ambuyisele umninilo ngemali; yaye isilwanyana esifileyo siya kuba sesakhe.', 'XHO22'),
(2, 21, 35, 'Xa inkomo yomntu ithe yahlaba inkomo yommelwane wakhe, iya kufa; mabathengise ngenkomo leyo iphilileyo, bayahlule imali leyo; bayahlule inkunzi entsha efileyo.', 'XHO22'),
(2, 21, 36, 'Ukuba kwakusazeka ukuba inkomo ikhe yaluma, akayinqanda; uya kuyihlawula inkomo ngenkomo; yaye abafileyo baya kuba ngabakhe.', 'XHO22'),
(2, 22, 1, 'Xa umntu athe weba inkomo, nokuba yigusha, wayixhela, mhlawumbi wathengisa ngayo; uya kuyibuyisela iinkomo zibe ntlanu ngenkomo leyo, nempahla emfutshane emine ngemvu leyo.', 'XHO22'),
(2, 22, 2, 'Ukuba isela lithe lafunyanwa ligqobha indlu, labethwa lafa, akukho tyala lagazi kulowo.', 'XHO22'),
(2, 22, 3, 'Lakuphuma ilanga phezu kwakhe, igazi lophalaza phezu kwakhe; ngokuba uya kurhola ngokuzeleyo; ukuba awunanto, kothengiswa ngenxa yobusela bakho.', 'XHO22'),
(2, 22, 4, 'Ukuba ithe isela lithe lafunyanwa liphilile esandleni salo, mhlawumbi yinkomo, nokuba liesile, nokuba yigusha; uya kubuya kabini.', 'XHO22'),
(2, 22, 5, 'Xa umntu athe wadla intsimi, nokuba sisidiliya, wafaka inkomo yakhe, wadla kumhlaba womnye; Wazibuyisela ngezona zintle zelizwe lakhe, nezona zintle zesidiliya sakhe.', 'XHO22'),
(2, 22, 6, 'Xa kuthe kwatsha umlilo, kwangena imithana enameva, yafa imfumba yengqolowa, nokuba yengqolowa, nokuba lilizwe eli, litshiswe kwayiyo; lowo uwutshise umlilo woyimisela.', 'XHO22'),
(2, 22, 7, 'Xa athe umntu wanika ummelwane wakhe imali, nokuba yimpahla, ukuba ayigcine, yaza yabiwa endlwini yaloo mntu; ukuba lithe lafunyanwa isela, maliyihlawula kabini.', 'XHO22'),
(2, 22, 8, 'Ukuba lithe alafunyanwa isela: umninindlu makasondezwe phambi kwabagwebi, akhangele ukuba akayiphathanga na impahla yommelwane wakhe;', 'XHO22'),
(2, 22, 9, 'Ngenxa yamatyala, iinkomo, iiesile, iigusha, iingubo, izinto zonke ezidakileyo, ezifaniswayo, loo ndawo yabo bobabini yoya phambi koThixo; yaye nabani na ogweba abagwebi, umele ahlawule idlelane lakhe kabini.', 'XHO22'),
(2, 22, 10, 'Xa athe umntu wanika ummelwane wakhe iesile, nokuba yinkomo, nokuba yigusha, nokuba yiyiphi into ezitho zine, ukuba ayigcine; yaye iyafa, okanye yenzakele, okanye igxothwe, kungaboni bani:', 'XHO22'),
(2, 22, 11, 'makubekho ukufunga uYehova phakathi kwabo bobabini, ukuba akayiphathanga impahla yommelwane wakhe; woyikholisa umniniyo, angayihlawuli.', 'XHO22'),
(2, 22, 12, 'Ukuba ibiwe ikuye, woyibuyisela kumniniyo.', 'XHO22'),
(2, 22, 13, 'Ukuba iqwengiwe, woyizisa ibe lingqina, angayilungisi into eqwengiweyo.', 'XHO22'),
(2, 22, 14, 'Xa athe umntu waboleka isalamane sakhe into, yaza yonakala, okanye yafa, engekho umniniyo, woyibuyisela.', 'XHO22'),
(2, 22, 15, 'Ukuba kunjalo umniniyo, akanako ukuyilungisa; ukuba ibithe yaqeshwa, ibe ihambele umvuzo wayo.', 'XHO22'),
(2, 22, 16, 'Xa ithe indoda ingcatsha intombi engaganiweyo, yaza yalala nayo, yomnika ibe ngumkayo.', 'XHO22'),
(2, 22, 17, 'Ukuba uyise wayo walile nokuyinika, woyirhola imali elingana nekhazi labafazi.', 'XHO22'),
(2, 22, 18, 'Musa ukumyeka umthakathi aphile.', 'XHO22'),
(2, 22, 19, 'Indoda esukuba ilala inkomo, mayibulawe ife.', 'XHO22'),
(2, 22, 20, 'Obingelela kuthixo ongenguye uYehova, woba netyala lokutshabhiswa.', 'XHO22'),
(2, 22, 21, 'Umphambukeli uze ungamqumbisi, ungamxini, kuba naningabaphambukeli nani ezweni laseYiputa.', 'XHO22'),
(2, 22, 22, 'Musani ukumcinezela umhlolokazi nenkedama.', 'XHO22'),
(2, 22, 23, 'Ukuba uthe wabacinezela nangayiphi na indlela, baza bandibiza nangayiphi na indlela, ndiya kusiva isikhalo sabo;', 'XHO22'),
(2, 22, 24, 'uvuthe umsindo wam, ndinibulale ngekrele; babe ngabahlolokazi abafazi benu, babe ziinkedama abantwana benu.', 'XHO22'),
(2, 22, 25, 'Ukuba uthe wababoleka imali ihlwempu labantu bakowethu, uze ungaliboleki imali, ungalibizi nzala.', 'XHO22'),
(2, 22, 26, 'Ukuba ummelwane wakho ubambise ngeengubo zakhe, womnika ukutshona kwelanga.', 'XHO22'),
(2, 22, 27, 'Kuba yingubo yakhe yodwa, yingubo yakhe yesikhumba; uzolala ngantoni? xa athe wabiza kum, ndiya kuva; ngokuba kulubabalo.', 'XHO22'),
(2, 22, 28, 'Musa ukunyelisa oothixo, ungamtshabhisi nomlawuli wohlanga lwakowenu.', 'XHO22'),
(2, 22, 29, 'Musa ukulibala ukunika intlahlela yeziqhamo zakho nakwiziselo zakho; ndiya kundinika owamazibulo oonyana bakho.', 'XHO22'),
(2, 22, 30, 'Uze wenjenjalo ngenkomo yakho neenkomo zakho; ngokuba iya kuba kunina iintsuku ezisixhenxe; ngomhla wesibhozo wondinika.', 'XHO22'),
(2, 22, 31, 'Noba ngabantu abangcwele kum; inyama ethatyathwe endle nize ningayidli; yiphosele ezinjeni.', 'XHO22'),
(2, 23, 1, 'Musa ukuxoka; musa ukunxulumana nabangendawo, ukuba ube lingqina elingalungisiyo.', 'XHO22'),
(2, 23, 2, 'Musani ukulandela isininzi ukuya kwenza ububi; kwaye ungathethi ukwala emva kwabaninzi ukonakalisa umgwebo.', 'XHO22'),
(2, 23, 3, 'Yaye musa ukungqina kwihlwempu ngemeko yalo.', 'XHO22'),
(2, 23, 4, 'Xa uthe wayifumana inkomo yotshaba lwakho, nokuba liesile lalo, libhadula, wolibuyisela kulo.', 'XHO22'),
(2, 23, 5, 'Ukuba uthe walibona iesile lentiyo yakho libotshelelwe phantsi komthwalo walo, ungafuni ukulinceda, lincede lincede.', 'XHO22'),
(2, 23, 6, 'Musa ukulonakalisa ibango lesisweli sakho ecaleni laso;', 'XHO22'),
(2, 23, 7, 'Suka entethweni yobuxoki; uze ungambulali omsulwa nolilungisa, kuba ndiya kumvelela ongendawo.', 'XHO22'),
(2, 23, 8, 'Musa ukwamkela isipho, ngokuba isipho simfamekisa izilumko, siphenule amazwi amalungisa.', 'XHO22'),
(2, 23, 9, 'Kanjalo, ningamcinezeli owasemzini, kuba niyayazi intliziyo yomphambukeli, kuba naningabaphambukeli nani ezweni laseYiputa.', 'XHO22'),
(2, 23, 10, 'Wowuhlwayela umhlaba wakho iminyaka emithandathu, ubuthe iziqhamo zawo;', 'XHO22'),
(2, 23, 11, 'ke ngomnyaka wesixhenxe mayiphumle, izole; ukuze adle amahlwempu abantu bakowenu; ekushiyeni kwawo kwadliwa ngamarhamncwa. Yenza okufanayo ngesidiliya sakho nangomnquma wakho.', 'XHO22'),
(2, 23, 12, 'Wowusebenza umsebenzi wakho imihla emithandathu, uphumle ngomhla wesixhenxe, iphumle inkomo yakho ne-esile lakho, aphumle unyana wesicakakazi sakho nomphambukeli.', 'XHO22'),
(2, 23, 13, 'Kugcineni konke endikuthethileyo kuni; amagama thixo bambi uze ungawakhankanyi, ungavakali emlonyeni wakho.', 'XHO22'),
(2, 23, 14, 'Ndenzele isidlo kathathu ngonyaka;', 'XHO22'),
(2, 23, 15, 'Uze uwenze umthendeleko wezonka ezingenagwele (imihla esixhenxe uya kudla izonka ezingenagwele, njengoko ndakuwisela umthetho, ngexesha elimisiweyo lenyanga enguAbhibhi; ngokuba waphuma ngayo eYiputa, nasekuyeni kolalayo ecaleni kwam akaveli).', 'XHO22'),
(2, 23, 16, 'Nomthendeleko wokuvuna, wentlahlela yemigudu yakho, oyihlwayeleyo ezweni, nomthendeleko wokuphela konyaka, ekuhlanganiseni kwakho ukuxelenga kwakho ezweni;', 'XHO22'),
(2, 23, 17, 'Izihlandlo ezithathu ngomnyaka, onke amadoda akowenu obonakala phambi kweNkosi uYehova.', 'XHO22'),
(2, 23, 18, 'Igazi ledini lam musa ukulisondeza ndawonye nento enegwele; nenqatha lombingelelo wam malingabi kude kuse.', 'XHO22'),
(2, 23, 19, 'Owamazibulo ezweni lakho uze uwangenise endlwini kaYehova uThixo wakho. Usenokungaliboni itakane ebisini lukanina.', 'XHO22'),
(2, 23, 20, 'Uyabona, ndithuma isithunywa sezulu, ukuba sikukhokele, sikugcine endleleni, sikufikise endaweni endiyilungisileyo.', 'XHO22'),
(2, 23, 21, 'Sigcine, uliphulaphule izwi laso, ungasigculeli; kuba engayi kuluxolela ukreqo lwenu, kuba igama lam liphezu kwakhe.', 'XHO22'),
(2, 23, 22, 'Ke ukuba nithe naliphulaphula izwi lakhe, nakwenza konke endikuthethayo; ndoba lutshaba lweentshaba zakho, ndibe lutshaba lweentshaba zakho.', 'XHO22'),
(2, 23, 23, 'Ngokuba isithunywa sam siya kukukhokela, sikuse kuma-Amori, namaHeti, namaPerizi, namaKanan, namaHeti, namaYebhusi; ndibatshabalalise.', 'XHO22'),
(2, 23, 24, 'Uze ungaqubudi koothixo bazo, ungabakhonzi, ungenzi imisebenzi yabo; kodwa babethe ngesiqalekiso esikrakra nize niyaphule imifanekiso yabo eqingqiweyo ngokupheleleyo.', 'XHO22'),
(2, 23, 25, 'Mkhonzeni uYehova uThixo wenu, asisikelele isonka sakho, namanzi akho; yaye ndiya kusisusa isifo phakathi kwenu.', 'XHO22'),
(2, 23, 26, 'Akuyi kugxothwa amathole awo, nongazaliyo ezweni lakho; ndiya kulizalisa inani lemihla yakho;', 'XHO22'),
(2, 23, 27, 'Ndiya kuzithuma phambi kwakho izothuso zam, ndizibulale zonke iintlanga oya kufika kuzo, iintshaba zakho zonke ndizenze zikunikele ikhosi;', 'XHO22'),
(2, 23, 28, 'Ndiza kuthumela oonomeva phambi kwakho abaya kuwagxotha amaHebhere, amaKanan namaHiti phambi kwakho.', 'XHO22'),
(2, 23, 29, 'Andiyi kuwagxotha phambi kwakho ngamnyaka mnye; hleze ilizwe kube senkangala kulo, ande amarhamncwa elizwe phezu kwenu.', 'XHO22'),
(2, 23, 30, 'Ndiza kubagxotha kancinane phambi kwakho ude wande ulidle ilifa ilizwe.', 'XHO22'),
(2, 23, 31, 'Ndoyimisa imida yakho, ithabathele eLwandle oluBomvu, ise elwandle lwamaFilisti, ithabathele entlango, ise kuMlambo umNayile; kuba abemi behlabathi ndiya kubanikela esandleni sakho; uwagxothe phambi kwakho.', 'XHO22'),
(2, 23, 32, 'Uze ungenzi mnqophiso nabo okanye noothixo babo.', 'XHO22'),
(2, 23, 33, 'Mabangemi ezweni lakho, hleze bakwenze ucakwe; ngenxa yokuba xa uthe wabakhonza oothixo bazo, ngokuqinisekileyo baya kuba ngumgibe kuwe.', 'XHO22'),
(2, 24, 1, 'Wathi kuMoses, Nyuka uye kuYehova, wena noAron, noNadabhi, noAbhihu, namashumi asixhenxe amadoda amakhulu akwaSirayeli; unqula umgama.', 'XHO22'),
(2, 24, 2, 'uMoses uya kusondela kuYehova yedwa, kodwa bona abasondeli; yaye abantu abanyukanga naye.', 'XHO22'),
(2, 24, 3, 'Waya uMoses, wabaxelela abantu onke amazwi kaYehova, nemithetho yakhe yonke; baphendula abantu ngazwi-nye, bathi, Siya kuwenza onke amazwi awathethileyo uYehova.', 'XHO22'),
(2, 24, 4, 'Wawabhala uMoses onke amazwi kaYehova, wagqiba kusasa, wakha isibingelelo phantsi kwentaba, nezimiso zamatye zalishumi elinazibini, ngokwezizwe ezilishumi elinazibini zakwaSirayeli.', 'XHO22'),
(2, 24, 5, 'Wathuma abafana bakoonyana bakaSirayeli, ukuba baye kubingelela kuYehova amadini anyukayo, babingelela imibingelelo yombulelo ngeenkomo.', 'XHO22'),
(2, 24, 6, 'Wathabatha uMoses isiqingatha segazi, waligalela ezindebeni; nesiqingatha segazi ufefe esibingelelweni.', 'XHO22'),
(2, 24, 7, 'Wathabatha incwadi yomnqophiso, wayilesa phambi kwabantu, bathi bona, Yonke into ayithethileyo uYehova soyenza siyive.', 'XHO22'),
(2, 24, 8, 'Walithabatha uMoses igazi, wabatshiza abantu ngalo, wathi, Ligazi eli lomnqophiso awawenzayo uYehova nani ngenxa yala mazwi onke.', 'XHO22'),
(2, 24, 9, 'Wenyuka uMoses, noAron, uNadabhi noAbhihu, namadoda amakhulu angamashumi asixhenxe akwaSirayeli.', 'XHO22'),
(2, 24, 10, 'Bambona uTHIXO kaSirayeli, kwaye phantsi kweenyawo zakhe kunjengelitye eligangathiweyo lesafire, njengembonakalo yesibhakabhaka ngobuqaqawuli balo.', 'XHO22'),
(2, 24, 11, 'Akasolula isandla sakhe kwizikhulu zoonyana bakaSirayeli; bambona nabo uThixo, badla, basela.', 'XHO22'),
(2, 24, 12, 'Wathi uYehova kuMoses, Nyuka uze kum entabeni, ube khona, ndikunike amacwecwe nomyalelo nomthetho, endiwubhalileyo; ukuba unokubafundisa.', 'XHO22'),
(2, 24, 13, 'Waphakama uMoses noYoshuwa umkhonzi wakhe waza uMoses wenyuka intaba yoTHIXO oyinyaniso.', 'XHO22'),
(2, 24, 14, 'Wathi kumadoda amakhulu, Hlalani apha ngenxa yethu, side sibuyele kuni; nabo ke ooAron noHure benani; ukuba ubani unento yokwenza, makeze kuye.', 'XHO22'),
(2, 24, 15, 'Wenyuka ke uMoses waya entabeni; ilifu layigubungela intaba.', 'XHO22'),
(2, 24, 16, 'Ubuqaqawuli bukaYehova bahlala phezu kwentaba yeSinayi; ilifu layigubungela iintsuku ezintandathu; Wabiza uMoses ngomhla wesixhenxe efini;', 'XHO22'),
(2, 24, 17, 'Imbonakalo yobuqaqawuli bukaYehova, emehlweni oonyana bakaSirayeli, ibinjengomlilo odlayo encotsheni yentaba.', 'XHO22'),
(2, 24, 18, 'Wangena uMoses efini, wenyuka waya entabeni; wahlala uMoses entabeni iimini ezimashumi mane nobusuku obumashumi mane.', 'XHO22'),
(2, 25, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(2, 25, 2, 'Thetha koonyana bakaSirayeli, bandizisele umbingelelo; kuwo wonke umntu osukuba enikela ngokuqhutywa yintliziyo, wowamkela umnikelo wam.', 'XHO22'),
(2, 25, 3, 'Nguwo lo ke umrhumo eniya kuwuthabatha kubo; igolide, nesilivere, nobhedu;', 'XHO22'),
(2, 25, 4, 'noboya obubomvu, obubomvu, nelinen ecikizekileyo, noboya beebhokhwe;', 'XHO22'),
(2, 25, 5, 'nezintsu zeenkunzi zezimvu ezibomvu, nezintsu zenkunzi entsha, nemithi yesikamore;', 'XHO22'),
(2, 25, 6, 'ioli yesibane, nesiqhumiso seoli yentambiso, nesiqhumiso esimnandi;', 'XHO22'),
(2, 25, 7, 'amatye ebherilo, namatye e-efodi, netyeya;', 'XHO22'),
(2, 25, 8, 'Bandenzela ingcwele; ukuze ndihlale phakathi kwabo.', 'XHO22'),
(2, 25, 9, 'Njengako konke endikubonisa kona: ngokomfanekiso womnquba, nomfanekiso weempahla zawo zonke, ize nenze.', 'XHO22'),
(2, 25, 10, 'Mabenze ityeya ngomthi womnga, ubude bayo bube ziikubhite ezimbini ezinesiqingatha, ububanzi bayo bube yikubhite enesiqingatha, ukuphakama kwayo kube yikubhite enesiqingatha.', 'XHO22'),
(2, 25, 11, 'Uyaleke ngegolide ecocekileyo, uyigqume ngaphakathi nangaphandle, wenze isithsaba segolide esithe jize.', 'XHO22'),
(2, 25, 12, 'Uyenzele iintsontelo ezine zegolide, uzifake ezimbombeni zone; izintya ezimbini kweli icala layo, nemitya emibini kweliya icala.', 'XHO22'),
(2, 25, 13, 'Yenza izibonda ngomthi, uzigqume ngegolide,', 'XHO22'),
(2, 25, 14, 'ufake izibonda emakhonkcweni asemacaleni etyeya, ithwalwe ngazo ityeya leyo.', 'XHO22'),
(2, 25, 15, 'Imithwalo ibe semakhonkcweni etyeya; aziyi kushukunyiswa kuyo.', 'XHO22'),
(2, 25, 16, 'Faka isingqino endiya kukunika sona emkhombeni.', 'XHO22'),
(2, 25, 17, 'Uze wenze isigubungelo ngegolide ecocekileyo, ubude baso bube ziikubhite ezimbini ezinesiqingatha, ububanzi baso bube yikubhite enye enesiqingatha.', 'XHO22'),
(2, 25, 18, 'Yenza iikerubhi zibe mbini ngegolide; woyenza ibe ngumsebenzi, emaphethweni omabini esigubungelo.', 'XHO22'),
(2, 25, 19, 'Yenza enye ikerubhi ngapha, enye ikerubhi ngaphaya; wenze iikerubhi zibe seziphelweni zaso zozibini;', 'XHO22'),
(2, 25, 20, 'Iikerubhi ezo zowolula amaphiko azo, zisisibekele isigubungelo ngamaphiko azo, zijongane ubuso ngobuso; ubuso beekerubhi buya kuba semva kwesigubungelo.', 'XHO22'),
(2, 25, 21, 'Beka isigubungelo emkhombeni; ubeke etyeyeni isingqino, endiya kukunika sona.', 'XHO22'),
(2, 25, 22, 'Ndohlangana nawe khona, ndithethe nawe ndisesigqubulweni, phakathi kweekerubhi zombini eziphezu kwetyeya yesingqino, ngeento zonke endiya kukuwisela umthetho ngazo koonyana bakaSirayeli.', 'XHO22'),
(2, 25, 23, 'Uze wenze netafile ngomngampunzi. Ubude bayo boba ziikubhite ezimbini, ububanzi bayo bube yikubhite enye, ukuphakama kwayo kube yikubhite enye enesiqingatha.', 'XHO22'),
(2, 25, 24, 'Wayigqume ngegolide ecocekileyo, wenze isithsaba segolide ngeenxa zonke.', 'XHO22'),
(2, 25, 25, 'Uze uyenzele isithsaba esingangobubanzi besandla ngeenxa zonke, ulwenzele udini lwayo isithsaba segolide esithe jize.', 'XHO22'),
(2, 25, 26, 'Uyenzele amakhonkco egolide abe mane, uwafake amakhonkco ezimbombeni zone zemilenze yayo yomine.', 'XHO22'),
(2, 25, 27, 'Amakhonkco ajongana nesithsaba aba zizihlalo zokuthwala izibonda zokuyithwala itafile.', 'XHO22'),
(2, 25, 28, 'Yenza izibonda ngomngampunzi, uzityabeke ngegolide, ithwalwe ngazo itafile.', 'XHO22'),
(2, 25, 29, 'Uze wenze izitya zayo, neenkamba zayo, neendebe zayo, nemicephe yayo ekuya kuthungwa ngayo; wenze ngegolide ecocekileyo.', 'XHO22'),
(2, 25, 30, 'Beka isonka phambi kwam ngamaxesha onke etafileni;', 'XHO22'),
(2, 25, 31, 'Masenze nesiphatho sezibane ngegolide ecocekileyo. Isihlalo masenze umsebenzi;', 'XHO22'),
(2, 25, 32, 'Kuya kuvela amasebe amathandathu emacaleni aso; iikubhite ezintathu zesundu ngapha, iikubhite ezintathu zesundu kwelinye icala.', 'XHO22'),
(2, 25, 33, 'Zaba ntathu iindebe ezifana neentyantyambo zeamangile, zinamaqhina nentyantyambo esandleni sinye; neentolo ezintathu ezifanayo nezo zexhama kwelinye icala, ineqhina nompu; zikwanjalo ke iingalo ezintandathu ezikwisiphatho sesibane.', 'XHO22'),
(2, 25, 34, 'Phezu kwesiseko sesibane eso bekukho iindebe ezine zeamangile, zineentyantyambo zaso neentyantyambo zaso.', 'XHO22'),
(2, 25, 35, 'Kwaye kuya kubakho iqhosha phantsi kwezandla ezibini ezifanayo, neqhosha phantsi kwezandla ezibini ezifanayo, neqhosha phantsi kwezandla ezibini ezifanayo, ngokweengalo ezintandathu zesiphatho sesibane.', 'XHO22'),
(2, 25, 36, 'Amalungu azo nezandla zawo ziyafana; soba ngumsebenzi omnye wegolide ecocekileyo.', 'XHO22'),
(2, 25, 37, 'Wenze ngazo izibane zibe sixhenxe, uzimise izibane zaso, zikhanyisele.', 'XHO22'),
(2, 25, 38, 'Iingono zayo namacala omlomo wayo ibiyigolide ecocekileyo.', 'XHO22'),
(2, 25, 39, 'Makenze ngetalente yegolide ecocekileyo, neempahla zonke.', 'XHO22'),
(2, 25, 40, 'Kwaye wabona ukuba wenza ngokomzekelo wabo owawuboniswe entabeni.', 'XHO22'),
(2, 26, 1, 'Ke umnquba uze uwenze ngeshumi elinen ecikizekileyo, ephothiweyo, nemfusa, nemfusa, nebomvu; uwenze ngeekerubhi, woba ngumsebenzi wengcibi yokuluka.', 'XHO22'),
(2, 26, 2, 'Ubude belaphu lentente elinye boba ziikubhite ezimashumi mabini anesibhozo, ububanzi belaphu lentente lomnquba bube ziikubhite ezine; Ilengalenga ngalinye lentente loba mlinganiso mnye.', 'XHO22'),
(2, 26, 3, 'Amalaphu amahlanu ahlanganiswe, elinye nelinye; kunye nezinye iilaphu ezintlanu, zahlanganiswa.', 'XHO22'),
(2, 26, 4, 'Wenze imisonto emsi emphethweni wentente ephothiweyo; yaye, ekugqibeleni, wenze nelinye ilaphu lentente, lize lidityaniswe nelinye.', 'XHO22'),
(2, 26, 5, 'Uze wenze amagwegwe abe mashumi mahlanu kwelinye ilangeni lentente, wenze amagwegwe abe mashumi mahlanu emqukumbelweni welengalenga lentente yesibini; ukuze baphathane;', 'XHO22'),
(2, 26, 6, 'wenze amagwegwe egolide abe mashumi mahlanu, uwahlanganise amagwegwe lawo, ube mnye umnquba.', 'XHO22'),
(2, 26, 7, 'Uze wenze amalaphu entente ngoboya bebhokhwe abe yintente phezu komnquba; wenze amalaphu entente abe lishumi elinanye.', 'XHO22'),
(2, 26, 8, 'Ubude belaphu lentente elinye boba ziikubhite ezimashumi mathathu, ububanzi belaphu lentente elinye bube ziikubhite ezine; amalengalenga alishumi elinalinye entente, olingana onke.', 'XHO22'),
(2, 26, 9, 'Wowahlanganisa amalaphu entente abe mahlanu, namalaphu entente amathandathu ewonke;', 'XHO22'),
(2, 26, 10, 'Uze wenze amagwegwe abe mashumi mahlanu emqukumbelweni welaphu lentente elinye ngaphandle kwelaphu, namagwegwe abe mashumi mahlanu emqukumbelweni welaphu lentente, edityaniswe kwelesibini ilaphu lentente.', 'XHO22'),
(2, 26, 11, 'Uze wenze amagwegwe obhedu abe mashumi mahlanu, uwafake amagwegwe awo kumalungu awo, uyihlanganise intente ibe nye.', 'XHO22'),
(2, 26, 12, 'Amalaphu entente aseleyo, wona amalaphu aseleyo omnquba, owuxhoma emva komnquba;', 'XHO22'),
(2, 26, 13, 'Ikubhite enye ngapha, enye ikubhite ngapha kobude obuseleyo bamalaphu entente, ijinga emacaleni omnquba ngapha nangapha, iwugubungele.', 'XHO22'),
(2, 26, 14, 'Intente uze uyenzele isigubungelo ngezintsu zeenkunzi zezimvu, zenziwe zayingqombela, nesigubungelo sezikhumba zamahlengezi.', 'XHO22'),
(2, 26, 15, 'Umnquba uze wenze inkunkuma ngeende.', 'XHO22'),
(2, 26, 16, 'Ubude bombhalo boba ziikubhite ezilishumi, ububanzi bube yikubhite enesiqingatha solunye uhlobo.', 'XHO22'),
(2, 26, 17, 'Izikhonkwane ezibini zibekwe ngendlela enye; nenze kwaezo zonke iintlobo zeminquba.', 'XHO22'),
(2, 26, 18, 'Umnquba uze wenze iintsika zawo, zibe mashumi mabini iintsika ngecala lasezantsi ngasezantsi;', 'XHO22'),
(2, 26, 19, 'Uze wenze iintsika zesilivere ezimashumi mane phantsi kweentlobo ezimashumi mabini; izihlalo ezibini emilenzeni yenu yomibini, phantsi komthi omnye, nezihlalo ezibini phantsi komnye umthi emilenzeni yenu yomibini.', 'XHO22'),
(2, 26, 20, 'Ezecala lesibini lomnquba ngelasentla iintsika ezimashumi mabini;', 'XHO22'),
(2, 26, 21, 'neziseko zazo zesilivere zibe mashumi mane; nezihlalo ezibini phantsi kweplanga, nezihlalo ezibini phantsi kweplanga.', 'XHO22'),
(2, 26, 22, 'Ezomva womnquba ngasentshonalanga wozenza zibe ntandathu iintsika.', 'XHO22'),
(2, 26, 23, 'Benza iintsika zibe mbini, emambombeni omnquba emacaleni omabini;', 'XHO22'),
(2, 26, 24, 'zibe ngamawele entloko, bahlangane nekhonkco linye; koba njalo kubo bobabini; ibe ziikona ezimbini.', 'XHO22'),
(2, 26, 25, 'Zoba ziintlobo ezisibhozo neziseko zazo zesilivere, zibe ziziseko ezilishumi elinazithandathu; nezihlalo ezibini phantsi kweplanga, nezihlalo ezibini phantsi kweplanga.', 'XHO22'),
(2, 26, 26, 'Yenza imivalo ngamaplanga; zantlanu zeentsika zelinye icala lomnquba;', 'XHO22'),
(2, 26, 27, 'neentsika zantlanu zeentsika zelinye icala lomnquba;', 'XHO22'),
(2, 26, 28, 'Intendelezo ephakathi, nombindi wezibonda, kwathabathela ekupheleni kwesa ekupheleni;', 'XHO22'),
(2, 26, 29, 'Iintsika ezo zande ngegolide; ke imiqadi leyo uyaleke ngegolide.', 'XHO22'),
(2, 26, 30, 'Uwumise umnquba ngokwendlela eboniswe entabeni.', 'XHO22'),
(2, 26, 31, 'Maze benze ilaphu elimfusa, elimfusa, elibomvu, nelinen ecikizekileyo, ephothiweyo, umsebenzi wengcibi yokuluka; masenze iikerubhi.', 'XHO22'),
(2, 26, 32, 'Uze uyixhome ezintsikeni ezine zomngampunzi, ezalekwe ngegolide; iziseko zazo zibe zezegolide, phezu kweziseko zozine zesilivere;', 'XHO22'),
(2, 26, 33, 'usixhome isisitheliso phantsi kwezibiyeli, ukuze uyifake ityeya yesingqino ngaphaya komkhusane; nekhuselo liya kwahlula phakathi kweNgcwele yeeNgcwele nakwiNgcwele yeeNgcwele.', 'XHO22'),
(2, 26, 34, 'Ubeke isigqubuthelo phezu kwetyeya yesingqino kwiNgcwele kaNgcwele;', 'XHO22'),
(2, 26, 35, 'uyibeke itafile nganeno komkhusane, usimise nesiphatho sesibane malunga netafile, ngecala lomnquba elingasezantsi; uze ubeke itafile kwicala elingasemntla.', 'XHO22'),
(2, 26, 36, 'Umnyango wentente uze uwenzele ilaphu elimfusa, nemfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo, umsebenzi wengcibi yokuluka.', 'XHO22'),
(2, 26, 37, 'Kwilaphu elisithekileyo, uze wenze iintsika zibe ntlanu ngomsonto, uzigubungele ngegolide, neentsika zazo zibe zegolide; masenze iziseko zobhedu zibe zihlanu.', 'XHO22'),
(2, 27, 1, 'Uze usenze isibingelelo ngomthombe: ubude baso bube ziikubhite ezintlanu, ububanzi baso bube ziikubhite ezintlanu; Isibingelelo eso soba ngamacala amane alinganayo, ukuphakama kwaso kube ziikubhite ezintathu;', 'XHO22'),
(2, 27, 2, 'Uze wenze iimpondo zaso ezimbombeni zaso zone; ziphuma nto-nye iimpondo zaso; yityabeke ngobhedu.', 'XHO22'),
(2, 27, 3, 'Uze wenze neenkamba zaso zokulahla uthuthu lwaso, nemihlakulwana yaso, nezitya zaso zokutshiza, neempondo zaso, neengcedevu zaso zamalahle; zonke iimpahla zakhe uzenze ngobhedu.', 'XHO22'),
(2, 27, 4, 'Wenze ube ngumthi wobhedu; uze wenze umnatha ube neentambo ezine zobhedu eziphelweni zawo zozine;', 'XHO22'),
(2, 27, 5, 'uwubeke phantsi kongqameko lwesibingelelo, uthabathele phantsi, ukuze uthi gaa phakathi kwesibingelelo;', 'XHO22'),
(2, 27, 6, 'Uze usenzele umthwalo ophezu kwesibingelelo, umthwalo ngomngampunzi, usibekele ngobhedu;', 'XHO22'),
(2, 27, 7, 'zifakwe izibonda emakhonkcweni, zibe semacaleni omabini esibingelelo izibonda ukuba sithwalwe ngazo.', 'XHO22'),
(2, 27, 8, 'Lichithe ngemifanekiso eqingqiweyo; wenze kubo njengoko wababonisayo entabeni.', 'XHO22'),
(2, 27, 9, 'Uze uyenze intendelezo yomnquba kwelasezantsi icala, ngasezantsi, ibe ngamalaphu amalaphu elinen ecikizekileyo, ephothiweyo, ubude bawo bube ziikubhite ezilikhulu;', 'XHO22'),
(2, 27, 10, 'Iintsika zayo ezimashumi mabini, neziseko zazo ezimashumi mabini, izezobhedu; iziseko zeentsika nemixokelelwane yazo yayeyesilivere.', 'XHO22'),
(2, 27, 11, 'Kananjalo ngecala lasentla amalengalenga axhonywa iikubhite ezilikhulu, neentsika zawo ezimashumi mabini, nezixhaso zazo ezimashumi mabini; iintsika namakhonkco azo esilivere.', 'XHO22'),
(2, 27, 12, 'Ububanzi bentendelezo ngecala lasentshonalanga amalaphu ajingayo aziikubhite ezimashumi mahlanu, iintsika zawo zibe lishumi, nezixhaso zazo zibe lishumi.', 'XHO22'),
(2, 27, 13, 'Ububanzi bentendelezo ngecala lempumalanga ngasempumalanga boba ziikubhite ezimashumi mahlanu.', 'XHO22'),
(2, 27, 14, 'Amalaphu axhonywe kwelinye icala lesango oba ziikubhite ezilishumi elinantlanu;', 'XHO22'),
(2, 27, 15, 'Kwelinye icala kwakukho amalaphu axhonyiweyo aziikubhite ezilishumi elinesihlanu, iintsika zawo zaba ntathu, namaxhaka awo aba mathathu.', 'XHO22'),
(2, 27, 16, '“Esangweni lentendelezo soba sisisitheliso seekubhite ezimashumi mabini, nelinen ecikizekileyo emfusa, nemfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo, ehonjisiweyo; Iintsika zawo zibe zine, neziseko zazo zibe zine.', 'XHO22'),
(2, 27, 17, 'Zonke iintsika zentendelezo eziyijikelezileyo ziya kuzaliswa ngesilivere; izikhonkwane zazo zibe zezesilivere, neziseko zazo zibe zezobhedu.', 'XHO22'),
(2, 27, 18, 'Ubude bentendelezo bube ziikubhite ezilikhulu, ububanzi bube ziikubhite ezimashumi mahlanu kuyo yonk’ indawo, ukuphakama kube ziikubhite ezintlanu zelinen ecikizekileyo, ephothiweyo, neentsika zayo zibe zezobhedu.', 'XHO22'),
(2, 27, 19, 'Zonke iimpahla zomnquba, ekusetyenzweni kwawo konke, nezikhonkwane zawo zonke, nezikhonkwane zonke zentendelezo, zoba zezobhedu.', 'XHO22'),
(2, 27, 20, 'Bawisele umthetho oonyana bakaSirayeli, bazise kuwe ioli eqaqambileyo yeminquma engqushiweyo esiphathweni saso, ukuze sihlale sivutha isibane.', 'XHO22'),
(2, 27, 21, 'Ententeni yokuhlangana, nganeno komkhusane, naphambi kwesingqino, woyicwangcisa ooAron noonyana bakhe le nto phambi koYehova, kuthabathele ngokuhlwa kude kuse: ngummiselo ongunaphakade ezizukulwaneni zabo, koonyana bakaSirayeli.', 'XHO22'),
(2, 28, 1, 'Thabatha uAron umkhuluwa wakho noonyana bakhe, ubasondeze kuwe, bephuma koonyana bakaSirayeli, babe ngababingeleli kum: uAron, noNadabhi, noAbhihu, noElazare, noItamare, oonyana baka-Aron.', 'XHO22'),
(2, 28, 2, 'Umenzele izambatho ezingcwele uAron umntakwenu, zibe zezozuko nezihombiso.', 'XHO22'),
(2, 28, 3, 'Thetha kubo bonke abantliziyo zilumkileyo, endibazalise ngomoya wobulumko, ukuba bamenzele izambatho uAron zokumngcwalisa, abe ngumbingeleli kum.', 'XHO22'),
(2, 28, 4, 'Zizo ke ezi iingubo abaya kuzenza; nesikhuselo sesifuba, nengubo emagxeni, nengubo yokunxiba, nengubo ephothiweyo, neqhiya, nesondo lengubo; uze umenzele izambatho ezingcwele uAron umntakwenu omkhulu noonyana bakhe, ukuze abe ngumbingeleli kum.', 'XHO22'),
(2, 28, 5, 'Thabatha igolide, nemisonto emsi nemfusa nebomvu, nelinen ecikizekileyo;', 'XHO22'),
(2, 28, 6, 'Uze wenze iefodi ngegolide, nemfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo, umsebenzi ke lowo.', 'XHO22'),
(2, 28, 7, 'Yoba neziziba ezibini zamagxa, zokuyixakatha emiphethweni yayo yomibini; kwaye ke iya kudityaniswa.', 'XHO22'),
(2, 28, 8, 'Isondo le-efodi eliphezu kwayo loba lelakhe, njengokusetyenzwa kwayo; negolide, nengubo emfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo.', 'XHO22'),
(2, 28, 9, 'Thabatha amatye ebherilo abe mabini, ubhale kuwo amagama oonyana bakaSirayeli;', 'XHO22'),
(2, 28, 10, 'amagama amathandathu kwelinye ilitye, amanye amathandathu kwelinye ilitye, ngokwemvelaphi yawo.', 'XHO22'),
(2, 28, 11, 'Wowabhala amagama oonyana bakaSirayeli ngokomfanekiso womkroli wamatye, njengombhalo wetywina, loo matye mabini;', 'XHO22'),
(2, 28, 12, 'ubeke amatye abe mabini ezizibeni zamagxa zelaphu nakumagxa, abe ngamatye esikhumbuzo koonyana bakaSirayeli; yaye uAron wawathwala amagama abo phambi koYehova emagxeni akhe omabini, abe sisikhumbuzo.', 'XHO22'),
(2, 28, 13, 'Yenza iityeya ngegolide;', 'XHO22'),
(2, 28, 14, 'neentsontelo ezimbini zegolide ecocekileyo; wenze ngamatyathanga asongiweyo, kwaye ubophe amatyathanga engxoweni.', 'XHO22'),
(2, 28, 15, 'Yenza ityeya yesigwebo ngobuqhetseba; yenze ibe yiefodi; Uyenze ngegolide, nangengubo emfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo.', 'XHO22'),
(2, 28, 16, 'Mayiphindwe kabini; ubude bayo bube yikubhite, ububanzi bayo bube yikubhite.', 'XHO22'),
(2, 28, 17, 'Yongeza amatye kuyo, imikrozo emine yamatye; kufuneka ibe ngumgca wokuqala;', 'XHO22'),
(2, 28, 18, 'ukrozo lwesibini lube yirubhi, nesafire, nedayimani;', 'XHO22'),
(2, 28, 19, 'kukrozo lwesithathu lwaba yiametiste, neagati, neametiste;', 'XHO22'),
(2, 28, 20, 'kukrozo lwesine ibe yimbonakalo entle, nebherilo, neyaspisi; kufuneka bafake igolide ezingxoweni zabo;', 'XHO22'),
(2, 28, 21, 'amatye lawo abe ngokwamagama oonyana bakaSirayeli, abe lishumi elinamabini ngokwamagama abo, ngokombhalo wetywina; iyileyo yayinegama layo ngokwezizukulwana ezilishumi elinazibini.', 'XHO22'),
(2, 28, 22, 'Wenze elubengweni iintsontelo ezo zisontiwe ngegolide ecocekileyo;', 'XHO22'),
(2, 28, 23, 'wenze amakhonkco abe mabini ngegolide elubengweni, uwafake amakhonkco abe mabini emancamini omabini obengo;', 'XHO22'),
(2, 28, 24, 'ufake iintsontelo ezimbini zegolide emakhonkcweni omabini asemancamini obengo;', 'XHO22'),
(2, 28, 25, 'Zibophe iingxowa ezimbini ezingakumbi zemixokelelwane esongelwe kuzo, uze uzibophe kwimixwayo emagxeni elaphu eliphambi kwayo.', 'XHO22'),
(2, 28, 26, 'Wenze iintsontelo zibe mbini ngegolide, uzifake emancamini omabini obengo, emphethweni walo ongaphakathi we-efodi;', 'XHO22'),
(2, 28, 27, 'wenze neentsontelo ezimbini zegolide, uzifake ezizibeni zamagxa zozibini ze-efodi, nganeno kwayo, mayela nomphambili wayo, malungana nokuhlangana kwe-efodi;', 'XHO22'),
(2, 28, 28, 'Bolunxibelela ubengo emakhonkcweni alo emakhonkcweni engubo yamagxa, ngentsontelo emfusa, lube sentla kombhinqo wamagxa, ukuze lungakrazuki ubengo elungwini.', 'XHO22'),
(2, 28, 29, 'Wawathabatha uAron amagama oonyana bakaSirayeli elubengweni lwesigwebo entliziyweni yakhe ekungeneni kwakhe engcweleni, ukuze akhumbule rhoqo phambi koYehova.', 'XHO22'),
(2, 28, 30, 'Wazibeka iiUrim neeTumim engcweleni yetyeya yesigwebo; zibe sentliziyweni ka-Aron ekuyeni kwakhe phambi koYehova;', 'XHO22'),
(2, 28, 31, 'Uze uyenze iefodi yonke ngemisonto emsi nemfusa;', 'XHO22'),
(2, 28, 32, 'Bekukho umngxuma phakathi kuyo; Yiba nebhanti elukiweyo ejikeleze umngxuma, njengomngxuma we-habergeon, ukuze ungakrazuki.', 'XHO22'),
(2, 28, 33, 'Emazantsi eentsiba zayo, wenze amalengalenga ngemisonto emsi nemfusa nebomvu, emphethweni wayo; namakhonkco egolide phakathi kwazo, ngapha nangapha;', 'XHO22'),
(2, 28, 34, 'imisonto yegolide, nemisonto yegolide emininzi, nemisonto yegolide, nemisonto emininzi yegolide, emqukumbelweni wesambatho sangaphezulu.', 'XHO22'),
(2, 28, 35, 'Woyinxiba uAron xa elungiselela, ukuze livakale ilizwi lakhe ekungeneni kwakhe engcweleni phambi koYehova nasekuphumeni kwakhe, ukuze angafi.', 'XHO22'),
(2, 28, 36, 'Yenza imbasa ngegolide ecocekileyo, ubhale kuyo ngendlela yombhalo wetywina, uthi, INGCWELE KUYEHOVA;', 'XHO22'),
(2, 28, 37, 'uyibeke emsonto omfusa, uyibeke elunkontshweni; ube phambi konkontsho,', 'XHO22'),
(2, 28, 38, 'ibe sebunzi kuAron, ukuze abuthwale uAron ubugwenxa bezinto ezingcwele, abaya kuzingcwalisa oonyana bakaSirayeli, ezintweni zonke ezingcwele zabo; ihlale isebunzi kuye, ukuze zibakholekise zona phambi koYehova.', 'XHO22'),
(2, 28, 39, 'Yenza ingubo yokwaleka ngelinen ecikizekileyo, unkontsho ngelinen ecikizekileyo, wenze nombhinqo wokuthunga.', 'XHO22'),
(2, 28, 40, 'Ubenzele iingubo oonyana baka-Aron, ubenzele umbhinqo, ubaenzele iminqwazi, ibe yeyozuko neyokuhomba;', 'XHO22'),
(2, 28, 41, 'umnxibe uAron umkhuluwa wakho, noonyana bakhe; ubathambise, ubangcwalise, ubangcwalise, babe ngababingeleli kum.', 'XHO22'),
(2, 28, 42, 'Benze iibhulukhwe zelinen emhlophe zokufihla uhlazo lwabo; ukusuka esinqeni ukuya esinqeni baya kufikelela esinqeni.', 'XHO22'),
(2, 28, 43, 'Zinxibe uAron noonyana bakhe ekungeneni kwabo ententeni yokuhlangana, nasekusondeleni kwabo esibingelelweni, besiza kukulungiselela engcweleni; ukuze bangathwali bugwenxa, bafe; ibe ngummiselo ongunaphakade kuye nakwimbewu yakhe emva kwakhe.', 'XHO22'),
(2, 29, 1, 'Uze wenjenje ukubangcwalisa, babe ngababingeleli kum: ndiya kuthabatha inkunzi entsha yenkomo, neenkunzi zezimvu zibe mbini, ezigqibeleleyo;', 'XHO22'),
(2, 29, 2, 'nezonka ezingenagwele, nezonka ezingenagwele, ezigalelwe ioli, nezonka ezingenagwele, zithanjiswe ioli; wowenza ngomgubo wengqolowa.', 'XHO22'),
(2, 29, 3, 'Wozibeka engobozini-nye, uzisondeze nazo, kwanenkunzi entsha yenkomo, neenkunzi zezimvu zombini zisengobozini;', 'XHO22'),
(2, 29, 4, 'Wobasondeza uAron noonyana bakhe emnyango wentente yokuhlangana, abahlambe ngamanzi;', 'XHO22'),
(2, 29, 5, 'Thabatha izambatho, umnxibe uAron isambatho, nesambatho se-efodi, ne-efodi, nobengo, umbophe ngombhinqo we-efodi;', 'XHO22'),
(2, 29, 6, 'umbeke unkontsho entloko, ubeke isithsaba esingcwele elunkontshweni olo.', 'XHO22'),
(2, 29, 7, 'Thabatha ke ioli yokuthambisa, umgalele entloko, umthambise.', 'XHO22'),
(2, 29, 8, 'Uthabathe oonyana bakhe, ubanxibe iingubo zabo;', 'XHO22'),
(2, 29, 9, 'Babophe umbhinqo, uAron noonyana bakhe, ubabeke iminqwazi; bube bobabo ububingeleli, bube ngummiselo ongunaphakade; uze usikelele uAron noonyana bakhe.', 'XHO22'),
(2, 29, 10, 'Woyisondeza inkunzi entsha phambi kwentente yokuhlangana; waza uAron noonyana bakhe babeka izandla zabo phezu kwentloko yenkunzi yenkomo.', 'XHO22'),
(2, 29, 11, 'Uyixhelele inkunzi yenkomo phambi koYehova emnyango wentente yokuhlangana.', 'XHO22'),
(2, 29, 12, 'Thatha egazini lenkunzi yenkomo, uliqabe ngomnwe wakho ezimpondweni zesibingelelo, uligalele lonke igazi emazantsi esibingelelo.', 'XHO22'),
(2, 29, 13, 'Uze uwathabathe onke amanqatha agubungele amathumbu, namanqatha onke aphezu kwesibindi, nezintso zombini, nenqatha lazo, uqhumisele ngazo esibingelelweni.', 'XHO22'),
(2, 29, 14, 'Yona inyama yenkunzi entsha, nesikhumba sayo, nomswane wayo, wozitshisa ezo zinto ngomlilo, ngaphandle kweminquba yenu. Lidini lesono elo.', 'XHO22'),
(2, 29, 15, 'Thabatha inkunzi yemvu ibe nye; waza uAron noonyana bakhe babeka izandla zabo phezu kwentloko yenkunzi yegusha leyo.', 'XHO22'),
(2, 29, 16, 'Yixhele inkunzi yemvu, ulithabathe igazi layo, ulitshize esibingelelweni.', 'XHO22'),
(2, 29, 17, 'Inkunzi yemvu yihlahle, uwahlambe amathumbu ayo, nesisu sayo, uzibeke phezu kweenyama zayo, naphezu kwentloko yayo;', 'XHO22'),
(2, 29, 18, 'Yitshise ngomlilo inkunzi yemvu iphela esibingelelweni. Livumba elithozamisayo, kukudla kwasemlilweni ke oko kuYehova;', 'XHO22'),
(2, 29, 19, 'Wothabatha enye inkunzi yegusha; waza uAron noonyana bakhe babeka izandla zabo phezu kwentloko yenkunzi yegusha leyo.', 'XHO22'),
(2, 29, 20, 'Yixhele inkunzi yemvu, uthabathe egazini layo, uliqabe endlebeni ka-Aron, nasezindlebeni zasekunene zoonyana bakhe, nakubhontsi bezandla zabo, nakubhontsi beenyawo zabo fafaza igazi phezu kwesibingelelo yonke indawo.', 'XHO22'),
(2, 29, 21, 'Thabatha egazini lesibingelelo, neoli yentambiso, ulifefe kuAron, nezambatho zakhe, noonyana bakhe, nezambatho zoonyana bakhe abakunye naye; wasikelelwa, neempahla zakhe, noonyana bakhe, neempahla zoonyana bakhe, zikuye.', 'XHO22'),
(2, 29, 22, 'Thabatha inqatha lenkunzi yemvu, nomlenze, nomhlehlo ogubungele amathumbu, nomhlehlo ophezu kwesibindi, nezintso zombini, nenqatha lazo, nesandla sasekunene; ngokuba yinkunzi yemvu yembewu;', 'XHO22'),
(2, 29, 23, 'nesonka somqhathane esigalelwe ioli sibe sinye, nesonka somqhathane engobozini yezonka ezingenagwele, ephambi koYehova.', 'XHO22'),
(2, 29, 24, 'Zonke izinto uzinikele esandleni sika-Aron nasezandleni zoonyana bakhe; uze uwange njengomnikelo otshiswayo phambi koYehova.', 'XHO22'),
(2, 29, 25, 'Woyithabatha ezandleni zabo, uyibeke phezu komlilo phezu kwesibingelelo, ibe lidini elinyukayo, libe livumba elithozamisayo phambi koYehova; Oko kukudla kwasemlilweni kuYehova.', 'XHO22'),
(2, 29, 26, 'Uthabathe incum yenkunzi yemvu yembewu ka-Aron, uyitshangatshangise ibe ngumtshangatshangiso phambi koYehova; kwaye kufuneka ibe yinxalenye yakho.', 'XHO22'),
(2, 29, 27, 'Wayingcwalisa incum yomtshangatshangiso, nomlenze womtshangatshangiso, inkunzi yemvu yembewu ka-Aron, noonyana bakhe;', 'XHO22'),
(2, 29, 28, 'ibe yeka-Aron noonyana bakhe, ngokommiselo ophuma koonyana bakaSirayeli, kude kuse ephakadeni; ngokuba lidini elinyukayo elo lenziwa; yaye yoba ngumnikelo otshiswayo woonyana bakaSirayeli wemibingelelo yombulelo, umnikelo wabo otshiswayo oziswa kuYehova.', 'XHO22'),
(2, 29, 29, 'Izambatho ezingcwele zika-Aron zoba zezoonyana bakhe emva kwakhe, bathanjiswe, banxibe, bangcwaliswe ngazo.', 'XHO22'),
(2, 29, 30, 'Inkwenkwe engumbingeleli esikhundleni sayo yobamisela imihla esixhenxe ekungeneni kwayo kwindawo yentlanganiso, ukuba ikhonze endaweni engcwele.', 'XHO22'),
(2, 29, 31, 'Thabatha inkunzi yegusha, uyipheke inyama yayo endaweni engcwele;', 'XHO22'),
(2, 29, 32, 'Bayidla ooAron noonyana bakhe inyama yenkunzi yemvu, kwanesonka engobozini, emnyango wentente yokuhlangana.', 'XHO22'),
(2, 29, 33, 'Ke yona le yenziwe ngokukholeka, bayidla, ukuze bayingcwalise, bayingcwalise; owasemzini makangayidli, kuba ingcwele.', 'XHO22'),
(2, 29, 34, 'Ukuba kuthe kwasala inyama nesonka, kwada kwasa, uze uyitshise ngomlilo into eseleyo; mayingatyiwa, kuba ingcwele.', 'XHO22'),
(2, 29, 35, 'Uze wenze kuAron noonyana bakhe ngokweento zonke endikuwisele umthetho ngazo, ubangcwalise iintsuku zibe sixhenxe.', 'XHO22'),
(2, 29, 36, 'Londoloza iinkunzi ezintsha zeenkomo, zibe lidini lesono ngemini, lokucamagusha; hlambulula isibingelelo xa ucamagushela isono, usithambise ukuba singcwaliswe.', 'XHO22'),
(2, 29, 37, 'Kangangeentsuku ezisixhenxe wosigubungela isono sesibingelelo, usingcwalise; yoba sisibingelelo esiyingcwele kangcwele; bonke abasichukumisayo isibingelelo boba ngcwele.', 'XHO22'),
(2, 29, 38, 'Yiyo ke le into oya kuyibingelela esibingelelweni; iimvana ezimbini ezinomnyaka mnye, yonke imihla, rhoqo.', 'XHO22'),
(2, 29, 39, 'Nilungise enye imvana kusasa; nize nilungiselele enye imvana ngokuhlwa.', 'XHO22'),
(2, 29, 40, 'Woba nesahlulo seshumi somgubo ocoliweyo, sibe sisahlulo sesine sehin yeoli; nesahlulo sesine sehin yewayini yomnikelo othululwayo.', 'XHO22'),
(2, 29, 41, 'Nilungise enye imvana ngokuhlwa, niyenze ngokomnikelo wakusasa, nangokomnikelo wawo othululwayo, ibe ngumnikelo wasemlilweni olivumba elithozamisayo kuYehova.', 'XHO22'),
(2, 29, 42, 'Elo loba lidini elinyukayo lamaxesha onke kwizizukulwana zenu, emnyango wentente yokuhlangana phambi koYehova, apho ndiya kuhlangana nani khona, ndithethe nani.', 'XHO22'),
(2, 29, 43, 'Khona apho ndohlangana noonyana bakaSirayeli, bangcwaliswe ngobuqaqawuli bam;', 'XHO22'),
(2, 29, 44, 'ndiyingcwalise intente yokuhlangana nesibingelelo; NoAron noonyana bakhe ndiya kungcwalisa ukuba babe ngababingeleli bam.', 'XHO22'),
(2, 29, 45, 'Ndohlala phakathi koonyana bakaSirayeli, ndibe nguThixo kubo;', 'XHO22'),
(2, 29, 46, 'bazi ukuba ndinguYehova uThixo wabo, owabakhuphayo ezweni laseYiputa, ukuba ndihlale phakathi kwabo; NdinguYehova, uThixo wabo.', 'XHO22'),
(2, 30, 1, 'Uze wenze isibingelelo sokuqhumisela; yenze ngomthi welinden.', 'XHO22'),
(2, 30, 2, 'Ubude bayo mabube yikubhite, ububanzi bayo bube yikubhite; mayibe namacala amane alinganayo, ukuphakama kwayo kube ziimitha ezimbini; iimpondo zakhe mazifane.', 'XHO22'),
(2, 30, 3, 'Umphezulu waso wosaleka ngegolide ecocekileyo, namacala aso, neempondo zaso; wenze isithsaba segolide ngeenxa zonke.', 'XHO22'),
(2, 30, 4, 'Wasenzela iintsontelo zambini zegolide, phantsi kwesithsaba saso, eziphelweni zaso zozibini; yenze emacaleni ayo omabini; ibe yindawo yezibonda zokuyithwala.', 'XHO22'),
(2, 30, 5, 'Uze wenze imivalo ngomthi welinen, uyigqume ngegolide.', 'XHO22'),
(2, 30, 6, 'Yibeke phambi komkhusane naphambi kwetyeya yesingqino, phambi kwesigubungelo esiphezu kweSingqino, apho ndiya kuhlangana nawe khona.', 'XHO22'),
(2, 30, 7, 'uAron woqhumisela kuso ubulawu kuyo yonke imiso ngemiso.', 'XHO22'),
(2, 30, 8, 'Emva kokuba uAron ezilumekile izibane ngokuhlwa, aqhumisele phezu kwazo, zibe sisiqhumiso phambi koYehova, kwizizukulwana zenu.', 'XHO22'),
(2, 30, 9, 'Ize ninganyusi phezu kwalo isiqhumiso esingaqhelekanga, nedini elinyukayo, nokuba ngumnikelo wasemlilweni; uze ungagaleli naluphi na uhlobo lwesiselo kuyo.', 'XHO22'),
(2, 30, 10, 'uAron woyigquma imisipha yeempondo zakhe kube kanye ngomnyaka, ngegazi ledini lesono lokucamagusha; noyicamagushela kube kanye ngomnyaka, kwizizukulwana zenu; yeyona nto ingcwele kuYehova.', 'XHO22'),
(2, 30, 11, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 30, 12, 'Xa ulibala inani loonyana bakaSirayeli, ngokwenani labo, mabanikele elowo imali yokucamagushela umphefumlo wakhe kuYehova ekubabaleni; ukuze kungabikho siphoso phakathi kwazo xa uzifunda.', 'XHO22'),
(2, 30, 13, 'Nantsi loo nto, bonke abagqithela kwababaliweyo mabanike isiqingatha seshekele ngokweshekele yengcwele, ishekele leyo iziigera ezimashumi mabini; isiqingatha seshekele yoba ngumrhumo kaYehova.', 'XHO22'),
(2, 30, 14, 'Wonk’ ubani odlula kwabo babhalisiweyo ukususela kwabaminyaka ingamashumi amabini ukuya phezulu, umele enze umbingelelo kuYehova.', 'XHO22'),
(2, 30, 15, 'Isityebi asifanele sinikele ngakumbi, yaye ihlwempu alifanele likhuphe ngaphantsi kwesiqingatha seshekele xa kunikelwa umnikelo wokucamagushela imiphefumlo yenu kuYehova.', 'XHO22'),
(2, 30, 16, 'Yithabathe imali yokucamagusha koonyana bakaSirayeli, uyinikele enkonzweni yentente yokuhlangana; sibe sisikhumbuzo phambi koYehova koonyana bakaSirayeli, ukucamagushela imiphefumlo yenu.', 'XHO22'),
(2, 30, 17, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 30, 18, 'Uze wenze nesitya sobhedu, noseko lwalo, ukuze uzihlambe ngalo; uyibeke phakathi kwentente yokuhlangana nesibingelelo, ugalele amanzi phezu kwayo;', 'XHO22'),
(2, 30, 19, 'ngokuba uAron noonyana bakhe bahlamba izandla zabo neenyawo zabo kulo.', 'XHO22'),
(2, 30, 20, 'Ekungeneni kwabo ententeni yokuhlangana, bohlamba ngamanzi, bangafi; nasekusondeleni kwabo esibingelelweni, besiza kukwenza amadini anyukayo kuYehova;', 'XHO22'),
(2, 30, 21, 'Kwaye bamele bahlambe izandla zabo neenyawo zabo, hleze bafe; ibe ngumyalelo ongunaphakade kubo, kuye nakwimbewu yakhe kwizizukulwana zabo.', 'XHO22'),
(2, 30, 22, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 30, 23, 'Wathabatha amakhulu amahlanu eeshekele zemore ecocekileyo, nesiqingatha sesinamon ubunzima bayo, namakhulu amabini anamanci mahlanu eeshekele;', 'XHO22'),
(2, 30, 24, 'Nomnika amakhulu amahlanu eeshekele ngokweshekele yengcwele, nehin yeoli yomnquma;', 'XHO22'),
(2, 30, 25, 'wenze ibe yioli engcwele yokuthambisa, umxhesho wokuthambisa, ngokwengqondo yomqholi; yiba yioli engcwele yokuthambisa.', 'XHO22'),
(2, 30, 26, 'Ayithambise nayo intente yokuhlangana, netyeya yesingqino;', 'XHO22'),
(2, 30, 27, 'netafile nemisebenzi yayo yonke, nesiphatho sezibane nemisebenzi yaso, nesibingelelo sokuqhumisela;', 'XHO22'),
(2, 30, 28, 'nesibingelelo sedini elinyukayo nempahla yaso yonke, nesitya saso, noseko lwaso;', 'XHO22'),
(2, 30, 29, 'Uze ubangcwalise, babe ngcwele kakhulu; yonke into eyichukumisayo yoba ngcwele.', 'XHO22'),
(2, 30, 30, 'uAron noonyana bakhe ubathambise, ubangcwalise, babe ngababingeleli bam.', 'XHO22'),
(2, 30, 31, 'Wothetha koonyana bakaSirayeli, uthi, Yoba yioli engcwele yokuthambisa kum le, kwizizukulwana zenu.', 'XHO22'),
(2, 30, 32, 'Ize ingagalelwa enyameni yomntu, uze ungenjenjalo ngomxube wayo; woba ngcwele, ube ngcwele kuni.', 'XHO22'),
(2, 30, 33, 'Nabani na oqokelela into enjengale okanye nabani na obeka enye yayo kumntu wasemzini, makatshatyalaliswe angabikho ebandleni.', 'XHO22'),
(2, 30, 34, 'Wathi uYehova kuMoses, Zithabathele ubulawu, isitake, nebherilo, negalebhana; ezi ziqholo zesiqhumiso esihlambulukileyo, ubunzima bazo buyalingana;', 'XHO22'),
(2, 30, 35, 'wenze ngayo ivumba elithozamisayo, libe livumba elithozamisayo, lomsebenzi womqholi, eliqinileyo, elihlambulukileyo, elingcwele.', 'XHO22'),
(2, 30, 36, 'Uyiqhekeze kakhulu, uyibeke phambi kweSingqino ententeni yokuhlangana, apho ndiya kuhlangana nawe khona; mayibe ngcwele kuni.', 'XHO22'),
(2, 30, 37, 'Isiqhumiso osenzayo, uze ungazenzeli ngokwaso; yoba ngcwele kuYehova.', 'XHO22'),
(2, 30, 38, 'Umntu othe wajoja into enjalo, wobulawa phakathi kwabantu bakowabo.', 'XHO22'),
(2, 31, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(2, 31, 2, 'Yabona, ndimbize ngegama uBhetsaleli unyana kaUri, unyana kaHuri, wesizwe sakwaYuda, ukuba ube ngoonyana bakaUri.', 'XHO22'),
(2, 31, 3, 'Ndamzalisa ngomoya kaThixo ngobulumko, nangengqondo, nangokwazi, nangokwazi konke;', 'XHO22'),
(2, 31, 4, 'ukuba ayile imisebenzi yenkohliso, kusetyenzwe ngegolide, nesilivere, nobhedu;', 'XHO22'),
(2, 31, 5, 'nokuba kukrolwe amatye, awafake, akrole imithi, enze imisebenzi yobugcisa, yeentlobo ngeentlobo.', 'XHO22'),
(2, 31, 6, 'Yabona, mna ndimkhuphe uAholiyabhi unyana ka-Ahisamaki, wesizwe sakwaDan;', 'XHO22'),
(2, 31, 7, 'nentente yokuhlangana, netyeya yesingqino, nesigubungelo esiphezu kwayo, nempahla yonke yomnquba;', 'XHO22'),
(2, 31, 8, 'netafile nemisebenzi yayo, nesiphatho sezibane esicocekileyo, nemisebenzi yayo yonke, nesibingelelo sokuqhumisela;', 'XHO22'),
(2, 31, 9, 'nesibingelelo sedini elinyukayo, nemisebenzi yaso yonke, nesibingelelo saso, nesihlalo saso;', 'XHO22'),
(2, 31, 10, 'nezambatho zokubusa, nezambatho zokubusa, nezambatho zokubusa zika-Aron umbingeleli, nezambatho zoonyana bakhe zokwenzelelela;', 'XHO22'),
(2, 31, 11, 'neoli yokuthambisa nesiqhumiso esimnandi sengcwele, njengako konke endikuyalele kona.', 'XHO22'),
(2, 31, 12, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(2, 31, 13, 'Thetha koonyana bakaSirayeli, uthi, Gcinani iisabatha zam, ngokuba zingumqondiso phakathi kwam nani kwizizukulwana zenu; ukuze nazi ukuba ndinguYehova oningcwalisayo.', 'XHO22'),
(2, 31, 14, 'Ke ngoko, yigcineni isabatha; ngokuba ingcwele kuni; Lowo uyihlambelayo wobulawa afe; kuba bonke abo basebenza umsebenzi ngayo, loo mphefumlo wonqanyulwa, ungabikho ebantwini bakowawo.', 'XHO22'),
(2, 31, 15, 'Umsebenzi unokwenziwa ngeentsuku ezintandathu; ke wona umhla wesixhenxe uyisabatha, kungcwele kuYehova; nabani na owenza nawuphi na umsebenzi ngomhla wesabatha wobulawa afe.', 'XHO22'),
(2, 31, 16, 'Ngako oko oonyana bakaSirayeli mabayigcine ke isabatha, bayenze isabatha kwizizukulwana zabo: ingumnqophiso ongunaphakade.', 'XHO22'),
(2, 31, 17, 'Nguwo lowo umqondiso phakathi kwam noonyana bakaSirayeli ongunaphakade; ngokuba ngemihla emithandathu uYehova wenza izulu nomhlaba, waphumla ngomhla wesixhenxe, waphefumla.', 'XHO22'),
(2, 31, 18, 'Emva kokuthetha noMoses entabeni yeSinayi, wamnika uMoses amacwecwe amabini esingqino, amacwecwe amatye, ebhalwe ngomnwe kaThixo.', 'XHO22'),
(2, 32, 1, 'Ke babona abantu ukuba uMoses akanakuhla entabeni, bambizela ndawonye abantu kuAron, bathi kuye, Hlala, usenzele uthixo oya kusikhokela; ngokuba lo Moses, le ndoda yasikhuphayo ezweni laseYiputa, asiyazi into emhleleyo.', 'XHO22'),
(2, 32, 2, 'Wathi uAron kubo, Khululani ijikazi legolide, ezindlebeni zabafazi benu, noonyana benu, neentombi zenu, niwazise kum.', 'XHO22'),
(2, 32, 3, 'Bonke abantu bawaqhawula amajikazi egolide ezindlebeni zabo, bawazisa kuAron.', 'XHO22'),
(2, 32, 4, 'Wayithabatha ezandleni zabo, wayenza ngeoli yokuthambisa, wayenza ithole elityhidiweyo; bathi, Nanko oothixo bakho, Sirayeli, owakukhuphayo ezweni laseYiputa.', 'XHO22'),
(2, 32, 5, 'Wabona uAron, wakha isibingelelo; Wadanduluka uAron, esithi: “Ngomso ngumthendeleko kaYehova.', 'XHO22'),
(2, 32, 6, 'Bavuka kusasa, banyusa amadini anyukayo, babingelela imibingelelo yokubulela; Yahlala phantsi isihlwele, yadla, yasela, yema ukuba idlale.', 'XHO22'),
(2, 32, 7, 'Wathi uYehova kuMoses, Hamba uhle; ngokuba batshabalalisile abantu bakho, owabakhuphayo ezweni laseYiputa.', 'XHO22'),
(2, 32, 8, 'Batyeka kamsinya endleleni leyo ndibawisele umthetho ngayo, bazenzela ithole, baqubuda kulo, babingelela kulo, besithi, Naba oothixo bakho, Sirayeli, onyukileyo ilizwe laseYiputa.', 'XHO22'),
(2, 32, 9, 'Wathi uYehova kuMoses, Ndibakhangele aba bantu; uyabona, ngabantu abaneenkani.', 'XHO22'),
(2, 32, 10, 'Mandingemi ngoko, uvuthe umsindo wam kubo, ndibatshabalalise; ndikwenze uhlanga olukhulu.', 'XHO22'),
(2, 32, 11, 'Wathandaza uMoses kuYehova uThixo wakhe, wathi, Yini na ukuba uqumbe, Yehova, ukuba ubagxothe abantu bakho ezweni laseYiputa ngamandla amakhulu, nangesandla esithe nkqi?', 'XHO22'),
(2, 32, 12, 'Yini na ukuba amaYiputa atsho ukuthi, Nguye lo owabakhupha ebubini, ukuze ababulalele ezintabeni, abatshabalalise emhlabeni? Guquka emsindweni wakho, uzisole ngobu bubi kubantu bakho.', 'XHO22'),
(2, 32, 13, 'Khawumkhumbule uAbraham, uIsake noSirayeli, abakhonzi bakho, owazifunga ngokwakho kubo, wathi kubo, Ndiya kuyandisa imbewu yenu njengeenkwenkwezi zezulu, ndilinike lonke eli lizwe ndalithethayo kuni wena baya kulidla ilifa ngonaphakade.', 'XHO22'),
(2, 32, 14, 'Wazohlwaya uYehova ngobubi awayecinga ukubenza ebantwini bakhe.', 'XHO22'),
(2, 32, 15, 'Wajika uMoses, wehla entabeni, ephethe amacwecwe omabini esingqino esandleni sakhe; iitafile zabekwa ngamacala omabini; ngapha nangapha kubhaliwe.', 'XHO22'),
(2, 32, 16, 'Amacwecwe lawo ebengumsebenzi kaThixo, nombhalo lowo ubungumbhalo kaThixo ubhalwe emacwecweni.', 'XHO22'),
(2, 32, 17, 'Akuyiva uYoshuwa ingxolo yabantu bekhwaza, wathi kuMoses: “Kuyaliwa ezinkampini.', 'XHO22'),
(2, 32, 18, 'Kwaye wathi: Eli asilolizwi labo bakhalayo malunga norhulumente, kwaye asilolizwi labo bakhalayo ngenxa yokuba belahlekisiwe; kodwa ndiyasiva isandi sabaculayo.', 'XHO22'),
(2, 32, 19, 'Uthe akusondela eminqubeni, walibona ithole lenkomo nomngqungqo, wavutha umsindo kaMoses, wawaphosa amacwecwe lawo, wawaqhekeza phantsi kwentaba.', 'XHO22'),
(2, 32, 20, 'Wathabatha ithole lenkomo ababelenzile, walitshisa ngomlilo, waliqoba, waza waneka phezu kwamanzi waza wabaseza oonyana bakaSirayeli.', 'XHO22'),
(2, 32, 21, 'Wathi uMoses kuAron, Lenze ntoni na olu hlanga kuwe, ukuba ubazisele isono esikhulu kangaka?', 'XHO22'),
(2, 32, 22, 'Wathi uAron, Mawungavuthi umsindo wenkosi yam;', 'XHO22'),
(2, 32, 23, 'Bathi kum, Senzele uthixo oya kusikhokela; ngokuba lo Moses, le ndoda yasikhuphayo ezweni laseYiputa, asiyazi into emhleleyo.', 'XHO22'),
(2, 32, 24, 'Ndathi kubo, Bonke abanegolide, bayilahleni; Bandinika ke; ndaliphosa emlilweni, laphuma elithole.', 'XHO22'),
(2, 32, 25, 'Wababona uMoses abantu, ukuba baqhawule umkhala; (kuba uAron wayebahluba babe ze ukuze babe neentloni phakathi kweentshaba zabo.)', 'XHO22'),
(2, 32, 26, 'Wema uMoses esangweni lenkampu, wathi, Ngubani na ongakuYehova? makeze kum. Bahlanganisana kuye bonke oonyana bakaLevi.', 'XHO22'),
(2, 32, 27, 'Wathi kubo, Utsho uYehova uThixo kaSirayeli, ukuthi, Makathabathe elowo ikrele lakhe ecaleni lakhe, bangene eminqubeni yonke, bethabathela komnye umnyango besiya komnye, nibulala elowo umzalwana wakhe, elowo umzalwana wakhe, elowo umzalwana wakhe, elowo umzalwana wakhe, elowo umzalwana wakhe, elowo umzalwana wakhe, elowo umzalwana wakhe; kweyakhe umhlobo, elowo ngowakhe.', 'XHO22'),
(2, 32, 28, 'Benza oonyana bakaLevi ngokwelizwi likaMoses; kwafa ngaloo mini amadoda angathi angamawaka amathathu phakathi kwebandla.', 'XHO22'),
(2, 32, 29, 'Wathi uMoses, Ziboniseni kuYehova namhla, elowo kunyana wakhe nakumzalwana wakhe; wanga angakusikelele namhlanje.', 'XHO22'),
(2, 32, 30, 'Wathi uMoses ebantwini ngengomso, Nenze isono esikhulu; ngoku ndiya kunyuka ndiye kuYehova; mhlawumbi ndingasiphilisa isono sakho.', 'XHO22'),
(2, 32, 31, 'uMoses wabuyela kuYehova, wathi, Yoo! Olu hlanga lwenze isono esikhulu, lwazenzela uthixo wegolide.', 'XHO22'),
(2, 32, 32, 'Ngoku ke, ukuba uyasixolela isono sabo; ukuba akunjalo, ndicime encwadini yakho oyibhalileyo.', 'XHO22'),
(2, 32, 33, 'Wathi uYehova kuMoses, Wonke umntu ondigibileyo, ndiya kucima yena encwadini yam.', 'XHO22'),
(2, 32, 34, 'Hamba ngoku, ubathabathe abantu apho ndathethayo kuwe; yabona, iNgelosi yam iyakukhokela; kodwa xa ndibohlwaya, ndiya kubohlwaya ngenxa yesono sabo.', 'XHO22'),
(2, 32, 35, 'uYehova wabatshutshisa abantu ngenxa yokuba belenzile ithole elo walenzayo uAron.', 'XHO22'),
(2, 33, 1, 'Wathi uYehova kuMoses, Hamba uphume khona, wena nabantu obakhuphileyo ezweni laseYiputa, uye kulo ilizwe endalifungela uAbraham noIsake noYakobi, ndathi, Kuni, Nyuka ndiya kuyinika imbewu;', 'XHO22'),
(2, 33, 2, 'Ndiya kuthuma isithunywa ukuba sikukhokele; Ndiya kuwagqogqa amaKanan, nama-Amori, namaHeti, namaPerizi, namaHeti, namaYebhusi;', 'XHO22'),
(2, 33, 3, 'ezweni eliqukuqela ubisi nobusi, ngenxa yokuba andiyi kunyuka ndiye phakathi kwenu; ngokuba ningabantu abaneenkani, ukuze ndinganitshabalalise endleleni.', 'XHO22'),
(2, 33, 4, 'Baliva ke abantu elo lizwi libi, bakhala, akwabakho bani wamambesa iingubo zakhe.', 'XHO22'),
(2, 33, 5, 'Wathi uYehova kuMoses, Yithi koonyana bakaSirayeli, Niluhlanga oluntamo-lukhuni; yazi into omawuyenze kuwe.', 'XHO22'),
(2, 33, 6, 'Bazikhulula oonyana bakaSirayeli izivatho zabo, entabeni yeHorebhe.', 'XHO22'),
(2, 33, 7, 'Wawuthabatha uMoses umnquba, wayitwabululela ngaphandle kweminquba, kude neminquba, wathi yintente yokuhlangana. Yaye wonk’ ubani owayefuna uYehova waphuma waya kwintente yokuhlangana eyayingaphandle kwekhusi.', 'XHO22'),
(2, 33, 8, 'Ekuphumeni kukaMoses esiya ententeni leyo, lema lonke ibandla, wema walowo emnyango wentente yakhe, wakhangela kuMoses, wada wangena ententeni leyo.', 'XHO22'),
(2, 33, 9, 'Ekungeneni kukaMoses ententeni, lehla ilifu, lema emnyango wentente leyo, wathetha uYehova kuMoses.', 'XHO22'),
(2, 33, 10, 'Balibona bonke abantu ilifu limi emnyango wentente leyo, bema bonke abantu, baqubuda, elowo esemnyango wentente yakhe.', 'XHO22'),
(2, 33, 11, 'uYehova wathetha kuMoses, bekhangelene ebusweni, njengomntu ethetha nomhlobo wakhe, wabuyela kwaseminqubeni; Wabuyela enkampini, kodwa umkhonzi wakhe uYoshuwa, unyana kaNun, umfana, akazange aphume emnqubeni.', 'XHO22'),
(2, 33, 12, 'Wathi uMoses kuYehova, Uyabona, uthe kum, Bashiye aba bantu; Ke wena wathi, Ndikwazile ngegama, kananjalo ndikubabale.', 'XHO22'),
(2, 33, 13, 'Ukuba kaloku ndibabalwe emehlweni akho, ndíndazise indlela yakho, ukuze ndikwazi, nokuze ndibone inceba emehlweni akho; uqonde ukuba olu hlanga luhlanga lwaKho.', 'XHO22'),
(2, 33, 14, 'Wathi, Ubuso bam bohamba nani, ndiniphumze;', 'XHO22'),
(2, 33, 15, 'Wathi kuye, Ukuba ubuso bakho abuhambi nam, musa ukusisusa apha.', 'XHO22'),
(2, 33, 16, 'Kuya kwaziwa ngantoni na khona, ukuba undibabale mna nabantu bakho? Akungokuba uhamba nathi na? sahlulwe ke mna nabantu bakho kuzo zonke iintlanga zehlabathi.', 'XHO22'),
(2, 33, 17, 'Wathi uYehova kuMoses, Nam ndiya kuyenza le nto uyithethileyo, ngenxa enokuba ubabalwe ndim, ndikwazile ngegama.', 'XHO22'),
(2, 33, 18, 'Wathi, Mandibubone ubuqaqawuli bakho.', 'XHO22'),
(2, 33, 19, 'Wathi, Ndiya kukudlulisa konke ukulunga kwam phambi kwakho, ndilibize igama likaYehova phambi kwakho; ndibe nenceba kosukuba ndinenceba kuye, ndibe nenceba kosukuba ndinenceba kuye.', 'XHO22'),
(2, 33, 20, 'Wathi, Akuyi kububona ubuso bam; ngokuba akukho namnye uya kundibona aphile.', 'XHO22'),
(2, 33, 21, 'Wathi uYehova, Yabona, kukho indawo yam; uze ume phezu kweliwa.', 'XHO22'),
(2, 33, 22, 'Ekukhanyeni kwam ukukhanya, ndokubeka eweni, ndikugubungele ngesandla sam ekudluleni kwam.', 'XHO22'),
(2, 33, 23, 'Ndiya kusisusa isandla sam, uwubone umva wam, kodwa ubuso bam abuyi kubonwa.', 'XHO22'),
(2, 34, 1, 'Wathi uYehova kuMoses, Zimisele amacwecwe amabini amacwecwe, njengawokuqala, ndiwabhale emacwecweni lawo amazwi abesemacwecweni okuqala owawaqhekezayo.', 'XHO22'),
(2, 34, 2, 'Zilungiselele ngomso, unyuke uye entabeni yeSinayi, ume ngakum encotsheni yentaba;', 'XHO22'),
(2, 34, 3, 'Makunganyuki mntu nawe, kuze kungabonwa mntu kuyo yonke intaba; Iigusha neenkomo aziyi kudla kufuphi naloo ntaba.', 'XHO22'),
(2, 34, 4, 'Waqingqa amacwecwe amatye amabini, njengawokuqala; Wavuka kusasa uMoses, wenyuka waya entabeni yeSinayi, njengoko uYehova wamwiselayo umthetho, ephethe amacwecwe amabini esandleni sakhe.', 'XHO22'),
(2, 34, 5, 'Wehla uYehova efini, wema naye khona, wabiza igama leNkosi.', 'XHO22'),
(2, 34, 6, 'uYehova wadlula phambi kwakhe, wadanduluka, wathi: “Nkosi, Nkosi, Yehova, Thixo onemfesane, onobubele, ozeka kade umsindo, ozele yinceba nenyaniso!', 'XHO22'),
(2, 34, 7, 'Gcina inceba ngenxa yamawaka, xolela ubugwenxa, nokreqo, nesono, ungamkhululi ongendawo; ubuvelela ubugwenxa booyise koonyana noonyana babantwana, kwesesithathu nakwesesine isizukulwana;', 'XHO22'),
(2, 34, 8, 'Waqubuda ngokukhawuleza uMoses emhlabeni, waqubuda emhlabeni.', 'XHO22'),
(2, 34, 9, 'Wathi, Ukuba ndibona inceba emehlweni akho, Yehova, mayingene phakathi kwethu iNkosi yam; ngokuba beluhlanga oluyinjubaqa; usixolele ubugwenxa bethu nezono zethu, usenze ilifa lakho.', 'XHO22'),
(2, 34, 10, 'Wathi, Yabona, ndenza umnqophiso: ndiya kwenza imiqondiso, engazanga idalwe ehlabathini lonke, nasezintlangeni zonke, emehlweni abantu bonke bakho; ziwenze umsebenzi kaYehova zonke iintlanga onazo, ngokuba yinto eyoyikekayo endiya kuyenza kuwe.', 'XHO22'),
(2, 34, 11, 'Yithobele indlebe le nto ndikuwisela umthetho ngayo namhla: Yabona, ndiyawagqogqa ama-Amori, namaKanan, namaHeti, namaPerizi, namaHeti, namaYebhusi.', 'XHO22'),
(2, 34, 12, 'Zigcine, hleze udibane nabemi belo zwe ungena kulo, kube ngumgibe kuwe.', 'XHO22'),
(2, 34, 13, 'Ke nina nizidilizile izibingelelo zabo, niziqhekeze izimiso zabo zamatye, nigawule neziqu zabo.', 'XHO22'),
(2, 34, 14, 'Ngenxa yokuba uze ungamnquli uthixo wolunye uhlanga, kuba uYehova, ogama lakhe linguKhwele, nguThixo onekhwele;', 'XHO22'),
(2, 34, 15, 'ukuze ungenzi umnqophiso nabemi belo lizwe, bahenyuze ngokulandela oothixo babo, babingelele koothixo babo, kubizwe ke, niwudle umsondezo wabo;', 'XHO22'),
(2, 34, 16, 'Wazekela oonyana bakho iintombi ezinye, zaza iintombi zabo zahenyuza ngokulandela oothixo babo, zabahenyuza noonyana bakho noothixo bazo.', 'XHO22'),
(2, 34, 17, 'Uze ungazenzeli uthixo otyhidiweyo.', 'XHO22'),
(2, 34, 18, 'Yenza umthendeleko wezonka ezingenagwele; Iintsuku ezisixhenxe nodla izonka ezingenagwele, njengoko ndakuwiselayo umthetho, ngenyanga enguAbhibhi; ngokuba waphuma eYiputa ngenyanga enguAbhibhi.', 'XHO22'),
(2, 34, 19, 'Yonke into evula isizalo yeyam; nawo onke amazibulo eenkomo zakho, nokuba yinkomo, nokuba yimpahla emfutshane, nokuba yigusha;', 'XHO22'),
(2, 34, 20, 'Ilizibulo le-esile ulikhulule ngokulimisela ngexhwane; ukuba uthe akwamkhulula, wophule intamo. Bonke oonyana bakho bamazibulo uze ubakhulule ngokubamisela; Kwaye akukho mntu uya kuvela phambi kwam elambatha.', 'XHO22'),
(2, 34, 21, 'Wosebenza iintsuku ezintandathu, aphumle ngomhla wesixhenxe; ngexesha lokuvuna naxa lokuvuna ufanele uphumle.', 'XHO22'),
(2, 34, 22, 'Uze uwenze umthendeleko weeveki, wentlahlela yokuvunwa kwengqolowa, nomthendeleko wokuhlanganisela ukutya emakhaya ekupheleni komnyaka.', 'XHO22'),
(2, 34, 23, 'Izihlandlo ezithathu ngonyaka bonke oonyana bakho baya kubonakala phambi koYehova, uTHIXO kaSirayeli.', 'XHO22'),
(2, 34, 24, 'Ndiya kuzigqogqa iintlanga phambi kwakho, ndiwenze banzi ilizwe lakho; yaye akukho bani uya kulinqwenela ilizwe lakho ekunyukeni kwakho usiya kubonakala phambi koYehova uThixo wakho kathathu ngonyaka.', 'XHO22'),
(2, 34, 25, 'Igazi lombingelelo wam musa ukulisondeza kunye namagwele; nombingelelo wepasika awushiywa ngomso.', 'XHO22'),
(2, 34, 26, 'Owamazibulo ezweni lakho uze uwase endlwini kaYehova uThixo wakho. Usenokungaliboni itakane ebisini lukanina.', 'XHO22'),
(2, 34, 27, 'Wathi uYehova kuMoses, Wabhale la mazwi; ngokuba ndenze umnqophiso nawe noSirayeli ngokwala mazwi.', 'XHO22'),
(2, 34, 28, 'Wahlala khona ke kuYehova iimini ezimashumi mane nobusuku obumashumi mane; akadla sonka, akasela manzi. Wawabhala emacwecweni amazwi omnqophiso, imithetho elishumi.', 'XHO22'),
(2, 34, 29, 'Xa uMoses wehla eNtabeni yeSinayi ephethe amacwecwe amabini esingqino esandleni sikaMoses ekuhleni kwakhe entabeni, yaye wayengazi uMoses ukuba ulusu lobuso bakhe lwalukhazimla ngoxa wayethetha naye.', 'XHO22'),
(2, 34, 30, 'uAron noonyana bonke bakaSirayeli bambona uMoses, ulusu lobuso bakhe luyakhazimla; boyika ukusondela kuye.', 'XHO22'),
(2, 34, 31, 'Wababiza ke uMoses; uAron nezikhulu zonke zendibano babuyela kuye, waza wathetha kubo uMoses.', 'XHO22'),
(2, 34, 32, 'Emva koko basondela bonke oonyana bakaSirayeli, wabaxelela konke abekuthethile uYehova kuye eNtabeni yeSinayi.', 'XHO22'),
(2, 34, 33, 'Wada wagqiba uMoses ukuthetha nabo, wabugquma ubuso bakhe ngesigqubuthelo.', 'XHO22'),
(2, 34, 34, 'Ekuhambeni kukaMoses phambi koYehova, esiya kuthetha kuye, wasisusa isigqubuthelo, wada waphuma. Waphuma wathetha koonyana bakaSirayeli njengoko wayemwisele umthetho.', 'XHO22'),
(2, 34, 35, 'Babubona oonyana bakaSirayeli ubuso bukaMoses, ukuba ulusu lobuso bukaMoses luyakhazimla. Wabuya ke uMoses wabugquma ubuso bakhe ngesigqubuthelo, wada wangena waya kuthetha naye.', 'XHO22'),
(2, 35, 1, 'Walibizela ndawonye uMoses lonke ibandla loonyana bakaSirayeli, wathi kubo, Zizo ezi iindawo uYehova aniwisele umthetho ngazo ukuba nizenze.', 'XHO22'),
(2, 35, 2, 'Wowenza umsebenzi imihla emithandathu, ke ngomhla wesixhenxe ube yingcwele kuni, isabatha kasabatha kuYehova; wonke umntu osebenza kuyo uya kubulawa.', 'XHO22'),
(2, 35, 3, 'Ize ningaphembi mlilo ezindlwini zenu ngomhla wesabatha.', 'XHO22'),
(2, 35, 4, 'Wathetha uMoses kwibandla lonke loonyana bakaSirayeli, wathi, Lilo eli ilizwi awise umthetho ngalo uYehova, esithi,', 'XHO22'),
(2, 35, 5, 'Yinikeni iNkosi umnikelo ophuma phakathi kwenu; igolide, nesilivere, nobhedu;', 'XHO22'),
(2, 35, 6, 'nemisonto emfusa, nebomvu, nelinen ecikizekileyo, noboya bebhokhwe;', 'XHO22'),
(2, 35, 7, 'nezintsu zeenkunzi zezimvu ezibomvu, nezintsu zenkunzi entsha, nemithi yesikamore;', 'XHO22'),
(2, 35, 8, 'neoli yesiphatho sezibane, nobulawu beoli yentambiso, nobulawu;', 'XHO22'),
(2, 35, 9, 'namatye ebherilo, namatye okuyifaka iefodi nobengo;', 'XHO22'),
(2, 35, 10, 'zize zonke iintliziyo ezilumkileyo phakathi kwenu, zikwenze konke abewise umthetho ngako uYehova;', 'XHO22'),
(2, 35, 11, 'Nguwo ke umnquba, nentente yawo, nesigubungelo sayo, neeplanga zayo, neentsika zayo, nemiqadi yayo, neentsika zayo, nezihlalo zayo;', 'XHO22'),
(2, 35, 12, 'ityeya nemithwalo yayo, nesigubungelo, nomkhusane wokusithelisa;', 'XHO22'),
(2, 35, 13, 'itafile nezixhaso zayo, neempahla zayo zonke, nezonka zokubonisa;', 'XHO22'),
(2, 35, 14, 'nesiphatho sezibane, nempahla yaso, nezibane zaso, neoli yesiphatho sezibane;', 'XHO22'),
(2, 35, 15, 'nesibingelelo sokuqhumisela, nemithwalo yaso, neoli yokuthambisa, nesiqhumiso, namawunduwundu omnyango womnquba;', 'XHO22'),
(2, 35, 16, 'isibingelelo sedini elinyukayo, nohluzo lwaso lobhedu, nemithwalo yaso, nemisebenzi yaso yonke, nengubo yaso, nesihlalo saso;', 'XHO22'),
(2, 35, 17, 'namalaphu entendelezo, neentsika zayo, nezihlalo zazo, namalaphu omnyango wentendelezo;', 'XHO22'),
(2, 35, 18, 'nezikhonkwane zomnquba, nezikhonkwane zentendelezo, nezintya zazo;', 'XHO22'),
(2, 35, 19, 'neempahla zokulungiselela engcweleni, neempahla ezingcwele zika-Aron umbingeleli, nezambatho zoonyana bakhe zokulungiselela.', 'XHO22'),
(2, 35, 20, 'Lemka ke lonke ibandla loonyana bakaSirayeli.', 'XHO22'),
(2, 35, 21, 'Bandula ke beza, ngamnye kubo ngokuqhutywa yintliziyo nalowo unqwenelwayo ngumphefumlo wakhe waza wazisa umbingelelo kaYehova womsebenzi wentente yokuhlangana nowomsebenzi wayo wonke wezambatho ezingcwele.', 'XHO22'),
(2, 35, 22, 'Beza, amadoda nabafazi, bonke ababathanda, bezisa iitafile, nezihlalo, neetafile, neempahla zonke zegolide; nabo bonke abazisa umrhumo wegolide kuYehova.', 'XHO22'),
(2, 35, 23, 'Bonke abantu abanezinto ezimfusa nemfusa nebomvu, nelinen ecikizekileyo, noboya bebhokhwe, nezintsu zeenkunzi zezimvu ezibomvu, nezikhumba zamahlengezi, mabazizise.', 'XHO22'),
(2, 35, 24, 'Bonke abasondeza umsondezo wesilivere, nowobhedu, bawuzisa umrhumo kuYehova; yaye wonk’ ubani owayefunyenwe kuye iinkuni zomsebenzi wawo nawuphi na umsebenzi wenkonzo wazizisa.', 'XHO22'),
(2, 35, 25, 'Bonke abafazi abantliziyo zilumkileyo bazibetha ngezandla zabo, bazizisa ezo nto bazibethayo, nengubo ebomvu nemfusa nebomvu, nelinen emthubi.', 'XHO22'),
(2, 35, 26, 'Bonke abafazi abantliziyo yabashukumisela ukuba babe nobulumko, basonta iinciniba.', 'XHO22'),
(2, 35, 27, 'Ookumkani beza amatye ebherilo namatye okuhlala phezu kwawo, abe yingubo emagxeni, awokukhuseleka ityeya;', 'XHO22'),
(2, 35, 28, 'nentlaka yokuqhumisa, neoli yesiphatho sezibane, neoli yokuthambisa, nesiqhumiso.', 'XHO22'),
(2, 35, 29, 'Oonyana bakaSirayeli bazisa umbingelelo wokuqhutywa yintliziyo kuYehova; nawo onke amadoda nabafazi bonke bantliziyo yabaqhubayo ukuyizisela yonke imisebenzi awawisa umthetho uYehova ngesandla sikaMoses, ukuba yenziwe.', 'XHO22'),
(2, 35, 30, 'Wathi uMoses koonyana bakaSirayeli, Khangelani, uYehova umbize ngegama uBhetsaleli, unyana kaUri, unyana kaHuri, wesizwe sakwaYuda.', 'XHO22'),
(2, 35, 31, 'Wazaliswa ngumoya kaThixo ngobulumko, ukuqonda, nokwazi, nokwazi zonke iintlobo;', 'XHO22'),
(2, 35, 32, 'ukubamba imisebenzi yasemzini, nokusebenza ngegolide, nesilivere, nobhedu;', 'XHO22'),
(2, 35, 33, 'nangokukrola amatye, awafake, akrole imithi, asebenze imisebenzi yonke ngentelekelelo.', 'XHO22'),
(2, 35, 34, 'Yaye wakubeka entliziyweni yakhe ukuba afundise, yena no-Oholiyabhi unyana ka-Ahisamaki wesizwe sakwaDan.', 'XHO22'),
(2, 35, 35, 'Wabazalisa ngobulumko bentliziyo, ukuba benze zonke iintlobo zemisebenzi, yokuba yimithi, neyengcibi, neyengcibi, neyegolide, nemfusa nebomvu, nelinen ecikizekileyo, neyomluki nabo benza nawuphi na umsebenzi nabayila umsebenzi wobuqhophololo.', 'XHO22'),
(2, 36, 1, 'Ke ooBhetetsaleli noAholiyabhi namadoda onke antliziyo zilumkileyo, awathi uYehova wabanikela ubulumko nengqondo, ukuba akwazi ukuwenza wonke umsebenzi wenkonzo yengcwele, njengoko wawisa umthetho uYehova.', 'XHO22'),
(2, 36, 2, 'uMoses wabiza ooBhetsayeli noAholiyabhi namadoda onke alumkileyo, awamnikayo uYehova ubulumko entliziyweni yakhe, onke antliziyo yabavuselelayo, ukuba bangene emsebenzini.', 'XHO22'),
(2, 36, 3, 'Bawathabatha, ebusweni bukaMoses, yonke imibingelelo, ababeyizisile oonyana bakaSirayeli emsebenzini wenkonzo yengcwele. Yaye baqhubeka bemzisela iminikelo yesisa ntsasa nganye.', 'XHO22'),
(2, 36, 4, 'Zeza ke zonke izilumko ezibe ziwenza wonke umsebenzi wengcwele, saseso savela emsebenzini waso esibe siwenza;', 'XHO22'),
(2, 36, 5, 'Bathetha ke kuMoses, bathi: “Abantu bazisa ngaphezu kokwaneleyo emsebenzini womsebenzi awise umthetho ngawo uYehova ukuba wenziwe.', 'XHO22'),
(2, 36, 6, 'Wawisa umthetho uMoses, kwavakaliswa eminqubeni yonke, kwathiwa, Makungabi sabakho ndoda, nankazana, benza msebenzi wumbi wombingelelo wengcwele. Ngoko abantu bavinjelwe ukuzisa.', 'XHO22'),
(2, 36, 7, 'Izinto ababenazo zawanela wonke umsebenzi wokwenziwa, yaye zazininzi kakhulu.', 'XHO22'),
(2, 36, 8, 'Bonke abalumkileyo, phakathi kwabasebenzi ententeni, benza amalaphu entente alishumi, ngelinen ecikizekileyo, ephothiweyo, nangemisonto emsi nemfusa nebomvu; wasenza ngeekerubhi, umsebenzi wengcibi yokuluka.', 'XHO22'),
(2, 36, 9, 'Ubude belaphu elinye lentente baba ziikubhite ezimashumi mabini anesibhozo, ububanzi belaphu elinye baba ziikubhite ezine; onke amalaphu entente aba ngamlinganiso mnye.', 'XHO22'),
(2, 36, 10, 'Wawahlanganisa amaqhina amahlanu, wawahlanganisa ke amaqhina amahlanu.', 'XHO22'),
(2, 36, 11, 'Benza imisonto emsi emqukumbelweni welaphu elinye lentente, emqukumbelweni wawo; Wenjenjalo ekupheleni kwelengalenga lesibini lentente, kwelesibini ikhuselo.', 'XHO22'),
(2, 36, 12, 'Wenza amagwegwe amashumi mahlanu kwelinye ilaphu lentente; amalaphu lawo ayenxibelelene, elinye nelaphu lentente.', 'XHO22'),
(2, 36, 13, 'Wenza amagwegwe ngegolide amashumi mahlanu, wawahlanganisa amakhonkco lawo ngamakhonkco, elinye nelinye, aba mnquba mnye.', 'XHO22'),
(2, 36, 14, 'Benza amalaphu entente ngoboya beebhokhwe, aba yintente phezu komnquba; wenza amalaphu entente alishumi elinanye.', 'XHO22'),
(2, 36, 15, 'Ubude belaphu lentente elinye baba ziikubhite ezingamashumi amathathu, ububanzi belaphu lentente elinye baba ziikubhite ezine; amalaphu entente alishumi elinanye alingana.', 'XHO22'),
(2, 36, 16, 'Wawahlanganisa amalaphu entente amahlanu elinye nelinye, namalaphu entente amathandathu elinye nelinye.', 'XHO22'),
(2, 36, 17, 'Wenza amagwegwe abe mashumi mahlanu emphethweni wengubo, wenza amagwegwe amashumi mahlanu emphethweni wengubo, apho lihlangana khona nelaphu elo.', 'XHO22'),
(2, 36, 18, 'Wenza amagwegwe obhedu amashumi mahlanu, yokuhlanganisa umnquba, into enye.', 'XHO22'),
(2, 36, 19, 'Bayenzela intente isigubungelo ngezintsu zeenkunzi zezimvu, ezenziwe zabomvu, wenza nesigubungelo phezu kwaso ngezintsu zamahlengezi.', 'XHO22'),
(2, 36, 20, 'Umnquba bawenzela iiplanga ngomngampunzi.', 'XHO22'),
(2, 36, 21, 'Ubude bombhalo babuziikubhite ezilishumi, ububanzi bomnye umkrolo baba yikubhite enesiqingatha.', 'XHO22'),
(2, 36, 22, 'Enye indlela bekukho izikhonkwane ezibini, esinye kwesinye; wenza kwangokunjalo nawo wonke umnquba.', 'XHO22'),
(2, 36, 23, 'Wenza imifanekiso yomnquba; amashumi amabini eeplanga ngecala lasezantsi ngasezantsi;', 'XHO22'),
(2, 36, 24, 'Benza iintsika zesilivere zamashumi mane phantsi kweentlobo ezimashumi mabini; izihlalo ezibini emilenzeni yenu yomibini, phantsi komthi omnye, nezihlalo ezibini phantsi komnye umthi emilenzeni yenu yomibini.', 'XHO22'),
(2, 36, 25, 'Ngecala lomnquba elingasentla, wenza iintsika ezimashumi mabini;', 'XHO22'),
(2, 36, 26, 'neziseko zazo zesilivere zibe mashumi mane; nezihlalo ezibini phantsi kweplanga, nezihlalo ezibini phantsi kweplanga.', 'XHO22'),
(2, 36, 27, 'Emacaleni omnquba ngasentshonalanga benza iintsika zantandathu.', 'XHO22'),
(2, 36, 28, 'Wenza iintsika zambini emaphundwini omnquba emaphundwini omabini.', 'XHO22'),
(2, 36, 29, 'Zaba ngamawele emhlabeni, zanamathela entloko, zada zafikelela ekupheleni; bazenza zombini iikona.', 'XHO22'),
(2, 36, 30, 'Kwaye kukho iintlobo ezisibhozo; neziseko zazo zesilivere zaziziseko ezilishumi elinazithandathu: zazibini iziseko phantsi kweplanga inye.', 'XHO22'),
(2, 36, 31, 'Wenza imivalo ngekalika; zantlanu zeentsika zelinye icala lomnquba;', 'XHO22'),
(2, 36, 32, 'nemiqadi emihlanu yeentsika zelinye icala lomnquba;', 'XHO22'),
(2, 36, 33, 'Benza umqadi ophakathi ukuze uzigqobhoze iiplanga, usuka kwelinye uye kwelinye.', 'XHO22'),
(2, 36, 34, 'Wazaleka iintsika ngegolide; amakhonkco azo bawenza ngegolide, aba apho imiqadi ikhoyo; bayaleka ke imiqadi ngegolide.', 'XHO22'),
(2, 36, 35, 'Wenza ilaphu elimfusa, nemfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo;', 'XHO22'),
(2, 36, 36, 'Basenzela iintsika ezine zomngampunzi, bazaleka ngegolide; iziseko zawo bezizezegolide; wazenzela izitya zesilivere ezine.', 'XHO22'),
(2, 36, 37, 'Umnyango womnquba bawenzela ilaphu elimfusa, nemfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo, umsebenzi oyimfakamfele womluki;', 'XHO22'),
(2, 36, 38, 'neentsika zaso zantlanu, nezibonda zazo, wazaleka iintloko zazo namakhonkco azo ngegolide;', 'XHO22'),
(2, 37, 1, 'uBhetsaleli wayenza ityeya yelinden; Ubude bayo bube ziikubhite ezimbini ezinesiqingatha, ububanzi bayo bube yikubhite enesiqingatha, ukuphakama kwayo kube yikubhite enesiqingatha.', 'XHO22'),
(2, 37, 2, 'Wayaleka ngegolide ecocekileyo ngaphakathi nangaphandle, wayithi jize ngesithsaba segolide.', 'XHO22'),
(2, 37, 3, 'Basenzela iintsontelo ezine zegolide, zanxityelelaniswa eziphelweni zaso zozine; iintambo zibe mbini ngeliya icala, nemitya emibini kweliya icala layo.', 'XHO22'),
(2, 37, 4, 'Benza izibonda ngemisonto, wazaleka ngegolide.', 'XHO22'),
(2, 37, 5, 'Wayifaka imithwalo emakhonkcweni asemacaleni etyeya, ukuba ithwalwe ngayo.', 'XHO22'),
(2, 37, 6, 'Wenza isihlalo sokucamagusha ngegolide ecocekileyo, ubude baso baba ziikubhite ezimbini ezinesiqingatha, ububanzi baso baba ziikubhite ezimbini ezinesiqingatha.', 'XHO22'),
(2, 37, 7, 'Wenza iikerubhi zambini ngegolide, umkhando wazo, wazenza eziphelweni zozibini zesigubungelo;', 'XHO22'),
(2, 37, 8, 'Enye ikerubhi ingaphaya kweliya icala, enye ikerubhi ingaphaya; wenze iikerubhi eziphelweni zaso zozibini.', 'XHO22'),
(2, 37, 9, 'Iikerubhi zaye zolula amaphiko azo, zasigubungela ngamaphiko azo isigubungelo, ubuso bazo bukhangelane; ubuso beekerubhi bebuphezu kwesigubungelo.', 'XHO22'),
(2, 37, 10, 'Wenza netafile ngomngampunzi: ubude bayo baba ziikubhite ezimbini, ububanzi bayo baba yikubhite enye, ukuphakama kwayo kwaba yikubhite enesiqingatha.', 'XHO22'),
(2, 37, 11, 'Wayaleka ngegolide ecocekileyo, wayithi jize ngesithsaba segolide.', 'XHO22'),
(2, 37, 12, 'Uze wenzele ungqameko olufana nobubanzi besandla ngeenxa zonke; wayithi jize ngesithsaba segolide.', 'XHO22'),
(2, 37, 13, 'Wayenzela amakhonkco egolide amane, wawafaka amakhonkco ezimbombeni zone zemilenze yayo yomine.', 'XHO22'),
(2, 37, 14, 'Amakhonkco lawo aba malunga nokuhlangana kodini, aba ziindawo zokufaka izibonda zokuyithwala itafile;', 'XHO22'),
(2, 37, 15, 'Wenza izibonda ngomthi, wazaleka ngegolide, ukuba ithwalwe ngazo itafile.', 'XHO22'),
(2, 37, 16, 'Wenza etafileni izitya zayo, neendebe zayo, neenkamba zayo, neendebe zayo ekwakuthululwa ngazo, wazenza ngegolide ecocekileyo.', 'XHO22'),
(2, 37, 17, 'Wenza nesiphatho sezibane ngegolide ecocekileyo, isikhondo saso, nesebe laso, neendebe zaso, neentonga zaso, neentyantyambo zaso;', 'XHO22'),
(2, 37, 18, 'Kwabonakala amasebe amathandathu emacaleni aso; yoba ziikubhite ezintathu kwelinye icala layo, neekubhite ezintathu zengqolowa kwelinye icala layo;', 'XHO22'),
(2, 37, 19, 'Zaba ntathu iindebe ezifana neentyantyambo zeamangile esandleni esinye, zineentyantyambo nentyantyambo; neentyantyambo zontathu, ezifana neentyantyambo zeamangile, zaba ngasandla sinye, nesiqu, nentyantyambo; ngoko ngezandla ezintandathu kwisiphatho sesibane.', 'XHO22'),
(2, 37, 20, 'Zaba ne iindebe esiphathweni, ezineentyantyambo zeamangile, neentyantyambo zaso, neentyantyambo zaso;', 'XHO22'),
(2, 37, 21, 'Iqhina eliphantsi kwezandla zozibini lifana nalo; iqhina eliphantsi kwezandla zozibini liphuma kuyo, liphume kuyo iqhina eliphantsi kwezandla zozibini, ngokwazo izandla ezithandathu.', 'XHO22'),
(2, 37, 22, 'Amaqhina awo nezibambo zawo zaphuma kwakumnye, uphela waba ngumkhando wamnye wegolide ecocekileyo.', 'XHO22'),
(2, 37, 23, 'Wenza izibane zaso zasixhenxe, nemilomo yaso, nemilomo yaso ngegolide ecocekileyo.', 'XHO22'),
(2, 37, 24, 'Wenza ke ngetalente yegolide ecocekileyo, neempahla zakhe zonke.', 'XHO22'),
(2, 37, 25, 'Wenza nesibingelelo sokuqhumisela ngomngampunzi; ubude bayo buyikubhite enye, ububanzi bayo buyikubhite enye; ayemacala amane alinganayo; ukuphakama kwaso kwaba ziikubhite ezimbini; iimpondo zaso zaziyinto-nye.', 'XHO22'),
(2, 37, 26, 'Wasaleka ngegolide ecocekileyo umphezulu waso, namacala aso, neempondo zaso; wayithi jize ngesithsaba segolide.', 'XHO22'),
(2, 37, 27, 'Wasenzela amakhonkco amabini ngegolide, phantsi kwesithsaba saso, eziphelweni zaso zozibini, emacaleni aso omabini, aziindawo zokuthwala.', 'XHO22'),
(2, 37, 28, 'Wenza izibonda ngomthi welinen, wazaleka ngegolide.', 'XHO22'),
(2, 37, 29, 'Wenza neoli engcwele yokuthambisa, nesiqhumiso esicocekileyo, ngokomsebenzi womqholi.', 'XHO22'),
(2, 38, 1, 'Wenza nesibingelelo sedini elinyukayo ngomngampunzi; ayemacala amane alinganayo; ukuphakama kwawo kuziikubhite ezintathu.', 'XHO22'),
(2, 38, 2, 'Wenza iimpondo zaso ezimbombeni zaso zone; iimpondo zakhe zaziphezu kwayo, wayinxiba.', 'XHO22'),
(2, 38, 3, 'Wazenza zonke iimpahla zesibingelelo eso: iimbiza, nemihlakulwana, nezitya zokutshiza, neembombo zenyama, neengcedevu; zonke izixhobo zaso wazenza ngobhedu.', 'XHO22'),
(2, 38, 4, 'Isibingelelo wasenzela umnatha ngobhedu, phantsi kwekhonkco laso, esazulwini saso;', 'XHO22'),
(2, 38, 5, 'Wenza amakhonkco amane eziphelweni zozine zencakuba yobhedu, aba ziindawo zamanani.', 'XHO22'),
(2, 38, 6, 'Wenza izibonda ngomthi welinen, wazaleka ngobhedu.', 'XHO22'),
(2, 38, 7, 'Wazibeka iimpahla emakhonkcweni, emacaleni esibingelelo, ukuba sithwalwe ngazo; wenza isibingelelo ngomthi.', 'XHO22'),
(2, 38, 8, 'Wenza isitya sobhedu, kwanesiphatho saso sobhedu, ngezipili zabafazi, ababehlanganisene emnyango wentente yokuhlangana.', 'XHO22'),
(2, 38, 9, 'Wenza intendelezo kwelasezantsi icala, emazantsi, elengalengayo elinen ecikizekileyo, ephothiweyo, iikubhite ezilikhulu ubude.', 'XHO22'),
(2, 38, 10, 'Iintsika zawo zaba mashumi mabini, neziseko zazo zaba mashumi mabini, zazezobhedu; iintsika namakhonkco azo yayezesilivere.', 'XHO22'),
(2, 38, 11, 'Ngecala lasentla amawunduwundu aziikubhite ezilikhulu; iintsika zawo ezimashumi mabini, neziseko zazo ezimashumi mabini, izezobhedu; iintsika namakhonkco azo esilivere.', 'XHO22'),
(2, 38, 12, 'Ngecala lasentshonalanga amalengalenga aziikubhite ezimashumi mahlanu, iintsika zawo zalishumi, nezixhaso zawo zalishumi; iintsika namakhonkco azo esilivere.', 'XHO22'),
(2, 38, 13, 'Ngecala lempumalanga ngasempumalanga aziikubhite ezimashumi mahlanu.', 'XHO22'),
(2, 38, 14, 'Amawunduwundu asingisa kwelinye icala lesango aziikubhite ezilishumi elinantlanu; Iintsika zawo zintathu, neentsika zawo zintathu.', 'XHO22'),
(2, 38, 15, 'Ngecala lesango lentsimi, ngapha nangapha, aba ngamawunduwundu eekubhite ezilishumi elinantlanu; Iintsika zawo zintathu, neentsika zawo zintathu.', 'XHO22'),
(2, 38, 16, 'Onke amalaphu ajingayo kwintendelezo aba ngawelinen ecikizekileyo, ephothiweyo.', 'XHO22'),
(2, 38, 17, 'Izixhaso zeentsika zaba zezobhedu; iziseko zeentsika nemixokelelwane yazo yayeyesilivere; nokwaleka abathetheli bazo ngesilivere; zonke iintsika zentendelezo zazizele yisilivere.', 'XHO22'),
(2, 38, 18, 'Amawunduwundu esango lentendelezo ayengumsebenzi womluki, nemisonto emsi nemfusa nebomvu, nelinen ecikizekileyo, ephothiweyo; ubude baba ziikubhite ezimashumi mabini, ukuphakama kwaba ziikubhite ezintlanu ububanzi, kwangaloo mawunduwundu entendelezo.', 'XHO22'),
(2, 38, 19, 'Iintsika zawo zone neziseko zazo zozine zaba zezobhedu; amagwegwe azo esilivere, nesithsaba sabathetheli bazo, namakhonkco azo esilivere.', 'XHO22'),
(2, 38, 20, 'Zonke izikhonkwane zomnquba nezentendelezo ejikelezileyo zazezobhedu.', 'XHO22'),
(2, 38, 21, 'Lilo eli inani lenani lomnquba, intente yesingqino, abeyibelwa ngokomthetho kaMoses, ngenxa yenkonzo yabaLevi, ngesandla sikaItamare, unyana ka-Aron umbingeleli.', 'XHO22'),
(2, 38, 22, 'uBhetsaleli, unyana kaUri, unyana kaHuri, wesizwe sakwaYuda, wakwenza konke uYehova abemwisele umthetho ngako uMoses.', 'XHO22'),
(2, 38, 23, 'Waye noAholiyabhi unyana ka-Ahisamaki, wesizwe sakwaDan, engumkroli, ingcibi, umluki welinen emfusa nemfusa nebomvu, nelinen ecikizekileyo, ecikizekileyo.', 'XHO22'),
(2, 38, 24, 'Ke yonke igolide ekwakusetyenzwa ngayo umsebenzi wonke wengcwele, igolide yomtshangatshangiso, yaye iziitalente ezimashumi mabini anesithoba, neeshekele ezimakhulu asixhenxe anamanci mathathu, ngokweshekele yengcwele.', 'XHO22'),
(2, 38, 25, 'Ke isilivere yabo babebhalelwe ibandla, yaba likhulu leetalente neeshekele eziliwaka elinamakhulu asixhenxe, anamanci mathandathu anesihlanu, ngokweshekele yengcwele;', 'XHO22'),
(2, 38, 26, 'Nomnika isiqingatha seshekele elowo umntu, ngokweshekele yengcwele, kubo bonke ababalwayo, bethabathela kominyaka imashumi mabini ezelwe, banyuse, babe ngamakhulu amawaka, anamakhulu mathandathu, anamanci mahlanu, anamanci mahlanu.', 'XHO22'),
(2, 38, 27, 'Ke lona ikhulu leetalente zesilivere, kwakukho izixhaso zengcwele neziseko zomgongxo; yaba likhulu elinekhulu leetalente, netalente kwiluthi yolunye.', 'XHO22'),
(2, 38, 28, 'Ngeeshekele eziliwaka elinamakhulu asixhenxe anamanci asixhenxe anesihlanu, wenza iintsika, wawagubungela amantloko azo, wazalisa.', 'XHO22'),
(2, 38, 29, 'Ubhedu olungumtshangatshangiso lwaye luziitalente ezingamashumi asixhenxe, neeshekele ezingamawaka amabini anamakhulu mane.', 'XHO22'),
(2, 38, 30, 'Wenza ngayo imigubasi yentente yokuhlangana, nesibingelelo sobhedu, nomsipha waso wobhedu, neempahla zonke zesibingelelo;', 'XHO22'),
(2, 38, 31, 'nezixhaso zentendelezo ejikelezileyo, nezixhaso zesango lentendelezo, nezikhonkwane zonke zomnquba, nezikhonkwane zonke zentendelezo ejikelezileyo.', 'XHO22'),
(2, 39, 1, 'Benza amalaphu amfusa, nemfusa, nebomvu, ukuba akhonze engcweleni; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 2, 'Iefodi wayenza ngegolide, nemisonto emsi nemfusa nebomvu, nelinen ecikizekileyo, ephothiweyo.', 'XHO22'),
(2, 39, 3, 'Bandula ke bakhanda iipleyiti zegolide ezicekethekileyo, bazenza iziqwenga, bazenza ngemisonto emfusa nemfusa nebomvu, nelinen ecikizekileyo, umsebenzi wengcibi yokuluka.', 'XHO22'),
(2, 39, 4, 'Bayenza iziziba zamagxa zokuyixakatha;', 'XHO22'),
(2, 39, 5, 'Isondo le-efodi eliphezu kwayo laphuma kuyo, njengokusetyenzwa kwayo; ngegolide, nengubo emfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 6, 'Benza amatye ebherilo, atywinwa ngamaqhekeza egolide, akrolwa ngokwamatywina, ngokwamagama oonyana bakaSirayeli;', 'XHO22'),
(2, 39, 7, 'wawabeka ezizibeni zamagxa ze-efodi, aba ngamatye esikhumbuzo koonyana bakaSirayeli; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 8, 'Wenza umqukumbelo wenkontsho, umsebenzi wengcibi yokuluka, ngegolide, nengubo emfusa, nebomvu, nelinen ecikizekileyo, ephothiweyo.', 'XHO22'),
(2, 39, 9, 'Amacala amane alinganayo; Bayisonga umkhombe kabini: ubude bawo baba yikubhite enye, ububanzi bawo baba yikubhite enye, sansongwa kabini.', 'XHO22'),
(2, 39, 10, 'Bafaka kulo imikrozo yamine yamatye; ibingumgca wokuqala;', 'XHO22'),
(2, 39, 11, 'kukrozo lwesibini yiemeraldi, nesafire, nedayimani;', 'XHO22'),
(2, 39, 12, 'kukrozo lwesithathu lwaba yiswekile, neagati, neametiste;', 'XHO22'),
(2, 39, 13, 'kukrozo lwesine yaba yirieli, nebherilo, neyaspisi; bafakwa kwiingxowa zegolide ezingxoweni zabo.', 'XHO22'),
(2, 39, 14, 'Amatye lawo abe ngamagama oonyana bakaSirayeli abalishumi elinababini, ngokwamagama abo;', 'XHO22'),
(2, 39, 15, 'Benza elubengweni intsontelo ezolukiweyo zegolide ecocekileyo.', 'XHO22'),
(2, 39, 16, 'Benza iingxowa ezimbini zegolide, namakhonkco amabini ngegolide; ubeke iintsontelo zibe mbini emiphethweni yomibini yesifuba.', 'XHO22'),
(2, 39, 17, 'Bazifaka iintsontelo ezimbini zegolide emakhonkcweni omabini asemancamini obengo;', 'XHO22'),
(2, 39, 18, 'Iingongoma zombini zezo tyathanga zimbini bazixhoma ezityeyeni zombini, bazixhoma ezizibeni zamagxa ze-efodi;', 'XHO22'),
(2, 39, 19, 'Benza amakhonkco amabini ngegolide, bawafaka emancamini omabini obengo, emphethweni walo ongaphakathi kwe-efodi.', 'XHO22'),
(2, 39, 20, 'Benza amanye amakhonkco amabini ngegolide, bawafaka emacaleni omabini e-efodi, emacaleni asezantsi, kufuphi nokuqhina kwayo, emqukumbelweni we-efodi.', 'XHO22'),
(2, 39, 21, 'Bawubopha unkontsho olo elunkontshweni ngomsonto obomvu oqaqambileyo, esinga phezu komtya wasentloko, ukuze ungahlukani nonkontsho; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 22, 'Benza ingubo yokwaleka ye-efodi ngomsebenzi womluki, yonke ngemisonto emsi nemfusa.', 'XHO22'),
(2, 39, 23, 'Bekukho umngxuma phakathi kwengubo, onjengomngxuma wentombi enyulu, ubiyele umngxuma lowo, ukuze ingakrazuki.', 'XHO22'),
(2, 39, 24, 'Benza amalaphu emqukumbelweni wengubo ebomvu, nebomvu, nelinen ebomvu, ephothiweyo.', 'XHO22'),
(2, 39, 25, 'Benza iiperile zegolide ecocekileyo, bazifaka phakathi kwamalaphu emqukumbelweni wengubo yokwaleka, kujikelezo phakathi kokwaleka;', 'XHO22'),
(2, 39, 26, 'nomnikelo wegarnet, nomqukumbelo wengubo yokwaleka, nomqukumbelo wengubo yokwaleka; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 27, 'uAron noonyana bakhe babenzela iingubo zelinen ecikizekileyo;', 'XHO22'),
(2, 39, 28, 'nonkontsho, ngelinen ecikizekileyo, neminqwazi yelinen ecikizekileyo, neminqwazi yelinen ecikizekileyo, ephothiweyo;', 'XHO22'),
(2, 39, 29, 'nengubo yelinen ecikizekileyo, ephothiweyo, nengubo emfusa, nebomvu, umsebenzi oyiluki; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 30, 'Benza necwecwe lesithsaba esingcwele segolide ecocekileyo, babhala phezu kwalo umbhalo ngombhalo wetywina, othi, Uzahlulele kuYehova.', 'XHO22'),
(2, 39, 31, 'Bayibopha ngentambo emfusa, yokunxitywa elunkontshweni; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 39, 32, 'Wagqitywa ke wonke umsebenzi wentente yokuhlangana; yaye oonyana bakaSirayeli benza kanye njengoko uYehova wamyalelayo uMoses, benjenjalo.', 'XHO22'),
(2, 39, 33, 'Bawuzisa kuMoses umnquba, intente, nemisebenzi yayo yonke, neeplanga zayo, neentsika zayo, nemiqadi yayo, neentsika zayo, nezihlalo zayo;', 'XHO22'),
(2, 39, 34, 'nesigubungelo sezintsu zeenkunzi zezimvu siye sajika sabomvu, nesigubungelo sezintsu zamahlengezi, nomkhusane osigubungeleyo;', 'XHO22'),
(2, 39, 35, 'netyeya yesingqino, nemithwalo yayo, nesigubungelo;', 'XHO22'),
(2, 39, 36, 'itafile nemisebenzi yayo yonke, nezonka zokubonisa;', 'XHO22'),
(2, 39, 37, 'nesiphatho sezibane esihlambulukileyo, nezibane zaso, nezibane ezilungisiweyo, nemisebenzi yaso yonke, neoli yokukhanyisa;', 'XHO22'),
(2, 39, 38, 'nesibingelelo segolide, neoli yokuthambisa, nobulawu, namalengalenga axhonywe emnyango womnquba;', 'XHO22'),
(2, 39, 39, 'nesibingelelo sobhedu, nesefa yaso yobhedu, nemithwalo yaso, nemisebenzi yaso yonke, nengubo yaso, nesihlalo saso;', 'XHO22'),
(2, 39, 40, 'namawunduwundu entendelezo, neentsika zawo, nezihlalo zazo, namawunduwundu omnyango wentendelezo, nezintya zawo, nezikhonkwane zawo, nomsebenzi wonke womnquba wentente yokuhlangana;', 'XHO22'),
(2, 39, 41, 'nezambatho zenkonzo yengcwele nezambatho ezingcwele zika-Aron umbingeleli, nezambatho zoonyana bakhe zokulungiselela ububingeleli.', 'XHO22'),
(2, 39, 42, 'Benza oonyana bakaSirayeli wonke umsebenzi, njengako konke uYehova awamwisela umthetho ngako uMoses.', 'XHO22'),
(2, 39, 43, 'Wawukhangela uMoses wonke umsebenzi, wabona ukuba bayawenza; njengoko uYehova abemwisele umthetho ngako, bawenza; wabasikelela uMoses.', 'XHO22'),
(2, 40, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(2, 40, 2, 'Ngomhla wokuqala wenyanga yokuqala uze uyimise intente yokuhlangana;', 'XHO22'),
(2, 40, 3, 'uyibeke kuyo ityeya yesingqino, uyigqume ngomkhusane ityeya leyo;', 'XHO22'),
(2, 40, 4, 'Uze netafile, uzicwangcise izinto zayo ezicwangciswayo; zisani nesiphatho sezibane, nizikhanyise izibane zaso.', 'XHO22'),
(2, 40, 5, 'Uze usibeke phambi kwetyeya yesingqino isibingelelo segolide sokuqhumisela, usixhome amalengalenga omnyango wentente;', 'XHO22'),
(2, 40, 6, 'asimise isibingelelo sedini elinyukayo phambi komnyango wentente yokuhlangana;', 'XHO22'),
(2, 40, 7, 'Beka isitya phakathi kwentente yokuhlangana nesibingelelo uze ugalele amanzi kuso.', 'XHO22'),
(2, 40, 8, 'Uze ayimise intendelezo ijikeleze, aze axhome amalengalenga ekungeneni kwentendelezo.', 'XHO22'),
(2, 40, 9, 'Thabatha ioli yokuthambisa, uthambise umnquba neento zonke ezikuwo, uwungcwalise, nomsebenzi wawo wonke, ube yingcwele;', 'XHO22'),
(2, 40, 10, 'Uthambise isibingelelo sedini elinyukayo nempahla yaso yonke, ungcwalise isibingelelo, sibe sesona sibingelelo singcwele.', 'XHO22'),
(2, 40, 11, 'Uthambise isitya sokuhlambela, noseko lwaso, ulungcwalise.', 'XHO22'),
(2, 40, 12, 'Uze usondeze uAron noonyana bakhe emnyango wentente yokuhlangana, ubahlambe ngamanzi;', 'XHO22'),
(2, 40, 13, 'Mnxibe uAron iingubo ezingcwele, umthambise, umngcwalise; ukuze abe ngumbingeleli kum.', 'XHO22'),
(2, 40, 14, 'Uthabathe oonyana bakhe, ubanxibe izambatho ezinde;', 'XHO22'),
(2, 40, 15, 'Wabathambisa njengoko wamthambisayo uyise, ukuze babe ngababingeleli bam; ngokuba ukuthanjiswa kwabo kuya kuba kokobubingeleli obungunaphakade kubo kwizizukulwana zabo.', 'XHO22'),
(2, 40, 16, 'Wenza ke uMoses, njengoko uYehova wamwiselayo umthetho, wenjenjalo.', 'XHO22'),
(2, 40, 17, 'Ngenyanga yokuqala ngomnyaka wesibini, ngolokuqala enyangeni leyo, wamiswa umnquba.', 'XHO22'),
(2, 40, 18, 'Wawumisa ke uMoses umnquba, wazimisa izixhaso zawo, wazimisa iintsika zawo, wayifaka imiqadi yawo, wazimisa iintsika zawo.', 'XHO22'),
(2, 40, 19, 'Wayaneka intente phezu komnquba, wabeka isigubungelo sentente phezu kwayo; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 20, 'Wasithabatha isingqino, wasibeka etyeyeni, wazifaka izibonda etyeyeni, wasibeka isigubungelo senceba phezu kwetyeya.', 'XHO22'),
(2, 40, 21, 'Wayingenisa ityeya emnqubeni, wawuxhoma umkhusane osithelisayo, wayisithelisa ityeya yesingqino; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 22, 'Wayimisa itafile ententeni yokuhlangana, ngecala lomnquba elingasentla, nganeno komkhusane;', 'XHO22'),
(2, 40, 23, 'Walungisa isonka phezu kwayo phambi koYehova; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 24, 'Wasimisa isiphatho sezibane ententeni yokuhlangana, sakhangelana netafile ngecala lomnquba elingasezantsi;', 'XHO22'),
(2, 40, 25, 'wazimisa izibane phambi koYehova; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 26, 'Wasibeka isibingelelo segolide ententeni yokuhlangana, phambi komkhusane;', 'XHO22'),
(2, 40, 27, 'Waqhumisela phezu kwaso; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 28, 'Waxhoma ilaphu elijingayo emnyango womnquba;', 'XHO22'),
(2, 40, 29, 'Wasibeka isibingelelo sedini elinyukayo emnyango wentente yokuhlangana, wenyusa phezu kwaso idini elinyukayo nomnikelo wokudla; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 30, 'Wabeka imbadada phakathi kwentente yokuhlangana nesibingelelo, waza wagalela amanzi okuhlamba ngayo.', 'XHO22'),
(2, 40, 31, 'Bazihlamba ooMoses noAron noonyana bakhe izandla zabo neenyawo zabo kulo.', 'XHO22'),
(2, 40, 32, 'Ekungeneni kwabo ententeni yokuhlangana, nasekusondeleni kwabo esibingelelweni, bahlamba; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(2, 40, 33, 'Wamisa intendelezo ejikeleza umnquba lo nesibingelelo esi, waxhoma amalengalenga esango lentendelezo. Wawugqiba ke uMoses umsebenzi.', 'XHO22'),
(2, 40, 34, 'Ilifu layigubungela intente yokuhlangana, ubuqaqawuli bukaYehova bawuzalisa umnquba.', 'XHO22'),
(2, 40, 35, 'uMoses akaba nakungena ententeni yokuhlangana, ngokuba ilifu lahlala phezu kwayo, nobuqaqawuli bukaYehova bazala ententeni leyo.', 'XHO22'),
(2, 40, 36, 'Belisithi lakuvuka ilifu phezu komnquba, banduluke oonyana bakaSirayeli;', 'XHO22'),
(2, 40, 37, 'Ke xa ilifu belingekanyuswa, bebenganduluki kude kube yimini yokunyuka kwalo;', 'XHO22'),
(2, 40, 38, 'Kuba ilifu likaYehova laliba phezu komnquba emini, umlilo ube phezu kwawo ebusuku phambi kwamehlo ayo yonke indlu kaSirayeli kulo lonke uhambo lwabo.', 'XHO22'),
(3, 1, 1, 'uYehova wambiza uMoses, wathetha kuye ententeni yokuhlangana, wathi,', 'XHO22'),
(3, 1, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa umntu kuni asondeza kuYehova umbingelelo, wowusondeza umsondezo wakhe ngeenkomo, neenkomo, neegusha.', 'XHO22'),
(3, 1, 3, 'Ukuba umbingelelo wakhe ulidini elinyukayo lenkomo, wosondeza iduna eligqibeleleyo; uwuzise ngokuqhutywa yintliziyo phambi koYehova, emnyango wentente yokuhlangana.', 'XHO22'),
(3, 1, 4, 'Wacinezela ngesandla sakhe phezu kwentloko yedini elinyukayo; kwaye kuya kukholeka kuye ukumxolela.', 'XHO22'),
(3, 1, 5, 'Mayixhelelwe inkunzi entsha phambi koYehova, bathi oonyana baka-Aron, ababingeleli, balisondeze igazi, bafefe ngegazi bajikelezise esibingelelweni, emnyango wentente yokuhlangana,', 'XHO22'),
(3, 1, 6, 'alithambise idini elinyukayo, alityatye;', 'XHO22'),
(3, 1, 7, 'Oonyana baka-Aron babeka umlilo phezu kwesibingelelo, babeka iinkuni phezu komlilo.', 'XHO22'),
(3, 1, 8, 'Bazicwangcise oonyana baka-Aron, ababingeleli, iinyama ezo, nentloko, nenqatha, phezu kweenkuni eziphezu komlilo ophezu kwesibingelelo.', 'XHO22'),
(3, 1, 9, 'Ke yena uya kuzihlamba izibilini zakhe neenyawo zakhe ngamanzi, aqhumisele ngezo nto zonke umbingeleli esibingelelweni zibe lidini elinyukayo, kukudla kwasemlilweni oko kukuthi ivumba elithozamisayo kuYehova.', 'XHO22'),
(3, 1, 10, 'Ukuba umsondezo wakhe ngoomhlambi weebhokhwe, nokuba ziibhokhwe, ulidini elinyukayo; makazise inkunzi egqibeleleyo.', 'XHO22'),
(3, 1, 11, 'Woyihlaba ecaleni lesibingelelo ngasentla phambi kobuso bukaYehova; bathi oonyana baka-Aron, ababingeleli, baligalele igazi layo bajikelezise esibingelelweni.', 'XHO22'),
(3, 1, 12, 'Wolityatya iinyama zalo, kwanentloko yalo nenqatha lalo, aze umbingeleli azibeke phezu kweenkuni eziphezu komlilo ophezu kwesibingelelo.', 'XHO22'),
(3, 1, 13, 'Kodwa yena wosihlamba isisu sakhe neenyawo zakhe ngamanzi, azisondeze umbingeleli zonke ezo zinto, aqhumisele ngazo ngomlilo esibingelelweni; Elo lidini elinyukayo, livumba elithozamisayo kuYehova.', 'XHO22'),
(3, 1, 14, 'Ukuba idini lakhe elinyukayo kuYehova ulithabatha leentaka, wolizisa ihobe lakhe, nokuba lihobe.', 'XHO22'),
(3, 1, 15, 'Wolisondeza umbingeleli esibingelelweni, alinqumle intloko, aqhumisele ngalo ngomlilo esibingelelweni; igazi lalo maligalelwe ecaleni lesibingelelo.', 'XHO22'),
(3, 1, 16, 'Makawuthabathe umsondezo wakhe kunye neentsiba zawo, awuphose ecaleni lomlilo ngasempumalanga, endaweni yothuthu.', 'XHO22'),
(3, 1, 17, 'Uya kukuqwenga ngamaphiko akhe, kodwa akayi kukuqwenga; Woqhumisela ngezo nto umbingeleli esibingelelweni, phezu kweenkuni ezitshayo kumlilo, umlilo, livumba elithozamisayo kuYehova.', 'XHO22'),
(3, 2, 1, 'Ukuba umntu uthe wasondeza umsondezo kuYehova, umnikelo wakhe woba ngumgubo ocoliweyo; ugalele ioli phezu kwawo, ubeke isiqhumiso phezu kwawo.', 'XHO22'),
(3, 2, 2, 'Wawuzisa koonyana baka-Aron, ababingeleli; uthabathe esandleni sakhe umgubo ocoliweyo, neoli yakhe, kunye nesiqhumiso esikhona; umbingeleli aqhumisele ngesikhumbuzo salo esibingelelweni, libe ngumnikelo wasemlilweni, olivumba elithozamisayo kuYehova.', 'XHO22'),
(3, 2, 3, 'Okuseleyo kukudla kwasemlilweni koba kokuka-Aron noonyana bakhe; liyingcwele kangcwele kwidini elinyukayo likaYehova.', 'XHO22'),
(3, 2, 4, 'Xa usondeza ukudla kwasemlilweni eziko, mayibe yisonka esingenagwele somgubo ocoliweyo, ugalelwe ioli, nokuba sisonka esingenagwele, esigalelwe ioli, sigalelwe ioli;', 'XHO22'),
(3, 2, 5, 'Ukuba umnikelo wakho ungumnikelo wasemlilweni ophekwe ngepani, woba ngomgubo ocoliweyo ongenagwele, ogalelwe ioli.', 'XHO22'),
(3, 2, 6, 'Yityatye ibe ziingceba, ugalele ioli phezu kwayo; kukudla kwasemlilweni oko.', 'XHO22'),
(3, 2, 7, 'Ukuba umnikelo wakho wasemlilweni ungumnikelo wasemlilweni ophekwe ngepani, mawenziwe ngomgubo ocoliweyo neoli.', 'XHO22'),
(3, 2, 8, 'Nizise umnikelo wokudla okuziinkozo kuYehova ngezo zinto; ukuba lisiwa kumbingeleli, wolisondeza esibingelelweni.', 'XHO22'),
(3, 2, 9, 'Wothabatha umbingeleli kuwo idini lesikhumbuzo emnikelweni wokudla, aqhumisele ngalo esibingelelweni; kukudla kwasemlilweni, ivumba elithozamisayo kuYehova.', 'XHO22'),
(3, 2, 10, 'Okuseleyo kukudla kwasemlilweni koba kokuka-Aron noonyana bakhe; liyingcwele kangcwele kwidini elinyukayo likaYehova.', 'XHO22'),
(3, 2, 11, 'Nawuphi na umnikelo wasemlilweni, eniwusondeza kuYehova, awuyi kuba nagwele; ngokuba igwele nobusi aniyi kuqhumisela kuYehova.', 'XHO22'),
(3, 2, 12, 'Ke wona umnikelo wasemlilweni wamazibulo, wowusondeza kuYehova, kodwa ungatshiswa esibingelelweni njengevumba elithozamisayo.', 'XHO22'),
(3, 2, 13, 'Wonke umnikelo wokudla wokudla, ufefa ityuwa ngetyuwa; ize ingasweli ityuwa yomnqophiso woThixo wakho emnikelweni wakho wokudla; zonke izipho zakho wozisa ityuwa.', 'XHO22'),
(3, 2, 14, 'Xa uthe wenza umsondezo kuYehova ekudleni kwamanxila akho, wowenza idini elinyukayo, izithungu zakho eziluhlaza zomiswe emlilweni, neekhilogram ezipheleleyo zengqolowa.', 'XHO22'),
(3, 2, 15, 'Ugalele ioli phezu kwawo, ubeke isiqhumiso phezu kwawo; kukudla kwasemlilweni oko.', 'XHO22'),
(3, 2, 16, 'Woqhumisela umbingeleli ngesikhumbuzo sawo inxalenye yengqolowa ebhuliweyo nenxalenye yeoli nesiqhumiso sayo sonke; Oko kukudla kwasemlilweni kuYehova.', 'XHO22'),
(3, 3, 1, 'Ukuba umsondezo wakhe ungumnikelo wombulelo, makawusondeze ube ngumnikelo wenkomo; nokuba uyindoda nokuba yinkazana, woyisondeza igqibelele phambi koYehova.', 'XHO22'),
(3, 3, 2, 'Maze acinezele entlokweni yomsondezo wakhe ngesandla sakhe, awunikele emnyango wentente yokuhlangana; Oonyana baka-Aron, ababingeleli, bolitshiza igazi bajikelezise esibingelelweni;', 'XHO22'),
(3, 3, 3, 'Nosondeza umnikelo wombulelo kuYehova; nomhlehlo ogubungele amathumbu, namanqatha onke aphezu kwamathumbu;', 'XHO22'),
(3, 3, 4, 'Umele asuse izintso zombini nenqatha lazo, emacaleni, neselu sesibindi, kwanezintso.', 'XHO22'),
(3, 3, 5, 'Boqhumisela ngezo oonyana baka-Aron esibingelelweni phezu kwedini elinyukayo, eliphezu kweenkuni eziphezu komlilo, kukudla kwasemlilweni, livumba elithozamisayo kuYehova.', 'XHO22'),
(3, 3, 6, 'Ukuba umbingelelo wakhe ngumbingelelo wombulelo kuYehova, ungowasempahleni emfutshane; wozisa indoda nokuba yinkazana, igqibelele.', 'XHO22'),
(3, 3, 7, 'Ukuba usondeza imvana ibe ngumbingelelo wakhe, woyizisa phambi koYehova.', 'XHO22'),
(3, 3, 8, 'Maze acinezele ngesandla sakhe phezu kwentloko yomsondezo wakhe, awusondeze phambi kwentente yokuhlangana; oonyana baka-Aron balitshize igazi lalo bajikelezise esibingelelweni;', 'XHO22'),
(3, 3, 9, 'Wosondeza ukudla kwasemlilweni kaYehova kumbingelelo wombulelo; susa amanqatha akhe kunye nawo onke amathanga aqinile emqolo wakhe; nomhlehlo ogubungele amathumbu, namanqatha onke aphezu kwamathumbu;', 'XHO22'),
(3, 3, 10, 'izintso zombini, nenqatha lazo, emacaleni, nomhlehlo, nenxalenye eseleyo yesibindi, kunye nezintso;', 'XHO22'),
(3, 3, 11, 'Woqhumisela wena umbingeleli esibingelelweni: lidini elinyukayo ke elo kuYehova.', 'XHO22'),
(3, 3, 12, 'Ukuba umbingelelo wakhe yibhokhwe, woyisondeza phambi koYehova.', 'XHO22'),
(3, 3, 13, 'Maze acinezele ngesandla sakhe phezu kwentloko yayo, ayibethele phambi kwentente yokuhlangana; oonyana baka-Aron balitshize igazi lalo bajikelezise esibingelelweni;', 'XHO22'),
(3, 3, 14, 'Wowuzisa kuyo umsondezo wakhe, ukudla kwasemlilweni kuYehova; nomhlehlo ogubungele amathumbu, namanqatha onke aphezu kwamathumbu;', 'XHO22'),
(3, 3, 15, 'Wozisusa izibindi ezibini, nenqatha lazo, elingasecaleni, nomhlehlo, nomhlehlo, nenqatha phezu kwesibindi;', 'XHO22'),
(3, 3, 16, 'Woqhumisela ngezo nto umbingeleli esibingelelweni. Lidini elinyukayo, livumba elithozamisayo; onke amanqatha ngaYehova.', 'XHO22'),
(3, 3, 17, 'Lowo woba ngumyalelo ongunaphakade ezizukulwaneni zenu, emakhayeni enu onke, wokuba ningadli nqatha nagazi.', 'XHO22'),
(3, 4, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 4, 2, 'Thetha koonyana bakaSirayeli, uthi, Xa athe umntu wegqitha kumthetho kaYehova ngokulahleka, nokuba nguwuphi na, nokuba nguwuphi na, wenza kuwo;', 'XHO22'),
(3, 4, 3, 'Ukuba umbingeleli othanjisiweyo uthe wenza isono ngokwesono sabantu; wozisa inkunzi yenkomo egqibeleleyo kuYehova ibe ngumnikelo wesono ngenxa yesono osenzileyo.', 'XHO22'),
(3, 4, 4, 'Woyizisa inkunzi entsha emnyango wentente yokuhlangana phambi koYehova; abeke isandla sakhe phezu kwentloko yenkunzi yenkomo aze ayixhelele inkunzi yenkomo phambi koYehova.', 'XHO22'),
(3, 4, 5, 'Umbingeleli othanjisiweyo umele athabathe egazini lenkunzi yenkomo, alizise esibingelelweni sobudlelane.', 'XHO22'),
(3, 4, 6, 'Awuthi nkxu umbingeleli umnwe wakhe egazini aze atshize ngenxalenye yegazi izihlandlo ezisixhenxe phambi koYehova, phambi kwekhuselo lengcwele.', 'XHO22'),
(3, 4, 7, 'Umbingeleli wogalela inxalenye yegazi esiqhumeni phambi koYehova ententeni yokuhlangana; lonke igazi lenkunzi entsha logalelwa esisekweni sesibingelelo sedini elinyukayo emnyango wentente yokuhlangana.', 'XHO22'),
(3, 4, 8, 'Wowasusa onke amanqatha enkozweni yedini lesono; nomhlehlo ogubungele amathumbu, namanqatha onke aphezu kwamathumbu;', 'XHO22'),
(3, 4, 9, 'Wozisusa izibindi ezibini, nenqatha lazo, elisecaleni, nesibindi, nomhlehlo;', 'XHO22'),
(3, 4, 10, 'Njengoko athatyathwayo enkomeni yombingelelo woxolo, aqhumisele ngawo umbingeleli esibingelelweni sedini elinyukayo.', 'XHO22'),
(3, 4, 11, 'Kesikhumba senkunzi entsha, nenyama yayo yonke, nentloko yayo, namathambo ayo, namathumbu ayo, nomswane wayo;', 'XHO22'),
(3, 4, 12, 'Woyikhuphela inkunzi entsha iphela, ayikhuphele ngaphandle kweminquba, aye endaweni emhlophe, apho kuchithwa khona uthuthu, ayitshise ngomlilo phezu kweenkuni; apho kulahlwa khona uthuthu, ziya kutshiswa.', 'XHO22'),
(3, 4, 13, 'Ukuba lonke ibandla lakwaSirayeli lithe wona ngabom, yaza loo nto ifihlakele emehlweni ebandla, laza lenza into enxamnye nomnye wemiyalelo kaYehova engenziwa, ibe lityala;', 'XHO22'),
(3, 4, 14, 'Emva kokuba sisaziwa isono esone ngaso kuye, lifanele libingelele ibandla ngenkunzi yenkomo yesono, liyizise phambi kwentente yokuhlangana.', 'XHO22'),
(3, 4, 15, 'Yaye amadoda amakhulu ebandla amele abeke izandla zawo phezu kwentloko yenkunzi yenkomo phambi koYehova, ize inkunzi yenkomo ixhelwe phambi koYehova.', 'XHO22'),
(3, 4, 16, 'Umbingeleli othanjisiweyo umele azise inxalenye yegazi lenkunzi yenkomo ententeni yokuhlangana.', 'XHO22'),
(3, 4, 17, 'Umbingeleli umele akhande inxalenye yegazi ngomnwe wakhe aze afefe ngalo izihlandlo ezisixhenxe phambi koYehova, phambi komkhusane;', 'XHO22'),
(3, 4, 18, 'ayithambise inxalenye yegazi ezimpondweni zesibingelelo phambi koYehova ententeni yokuhlangana, igazi lonke aligalele emazantsi esibingelelo sedini elinyukayo emnyango wesibingelelo intente yokuhlangana.', 'XHO22'),
(3, 4, 19, 'Wowathabatha onke amanqatha kulo, aqhumisele ngawo esibingelelweni.', 'XHO22'),
(3, 4, 20, 'Makenze kwinkunzi entsha njengoko wenza kwinkunzi entsha yenkomo yedini lesono, makenjenjalo nakule; aze umbingeleli abacamagushele, baze baxolelwe.', 'XHO22'),
(3, 4, 21, 'Woyithabatha inkunzi entsha ngaphandle kweminquba, ayitshise njengoko wayitshisayo inkunzi entsha yokuqala; Lidini lesono ke elo lebandla.', 'XHO22'),
(3, 4, 22, 'Ukuba isikhulu sona, sathi singazi, senza into echasene nomthetho kaYehova uThixo waso, nokuba nguwuphi na, wento engeyakwenziwa, kwalityala oko;', 'XHO22'),
(3, 4, 23, 'Ukuba besisaziwa isono sakhe one ngaso; Wosondeza umbingelelo wakhe, itakane lebhokhwe, eligqibeleleyo;', 'XHO22'),
(3, 4, 24, 'Socinezela ngesandla saso phezu kwentloko yebhokhwe exhonti, siyixhelele apho kunyuswa khona idini elinyukayo phambi koYehova. Lidini lesono ke elo.', 'XHO22'),
(3, 4, 25, 'Umbingeleli umele athabathe inxalenye yegazi lomnikelo wesono ngomnwe wakhe aze alidyobhe ezimpondweni zesibingelelo somnikelo otshiswayo aze igazi lawo aligalele ngaphantsi esibingelelweni somnikelo otshiswayo.', 'XHO22'),
(3, 4, 26, 'Amanqatha alo wowaqhumisela esibingelelweni, njengamanqatha ombingelelo wombulelo; aze umbingeleli amcamagushele ngenxa yesono sakhe, aze axolelwe.', 'XHO22'),
(3, 4, 27, 'Ukuba ubani wenza isono engazi, wenza into enxamnye nomnye wemiyalelo kaYehova, engafanele ukwenziwa, wazeka ityala;', 'XHO22'),
(3, 4, 28, 'Okanye ukuba sithe saziwa isono sakhe esenzileyo, wosondeza umbingelelo wakhe wetakane lebhokhwe, ithokazi eligqibeleleyo, ngenxa yesono sakhe osenzileyo.', 'XHO22'),
(3, 4, 29, 'Umele abeke isandla sakhe phezu kwentloko yedini lesono aze anikele idini lesono esikhundleni somnikelo otshiswayo.', 'XHO22'),
(3, 4, 30, 'Umbingeleli wocaphula egazini lalo ngomnwe wakhe, alidyobhe ezimpondweni zesibingelelo somnikelo otshiswayo, lonke igazi laso aligalele emazantsi esibingelelo.', 'XHO22'),
(3, 4, 31, 'Wowasusa onke amanqatha kulo, njengoko asuswayo amanqatha ombingelelo wombulelo; Woqhumisela ngezo nto umbingeleli esibingelelweni, libe livumba elithozamisayo kuYehova; aze umbingeleli amcamagushele, aze axolelwe.', 'XHO22'),
(3, 4, 32, 'Ukuba uyisondeza imvana ibe lidini lesono, wozisa ithokazi eligqibeleleyo;', 'XHO22'),
(3, 4, 33, 'Umele abeke isandla sakhe phezu kwentloko yedini lesono, alibingelele libe lidini lesono apho kubingelelwa khona idini elinyukayo.', 'XHO22'),
(3, 4, 34, 'Umbingeleli wothabatha egazini lomnikelo wesono ngomnwe wakhe, alidyobhe ezimpondweni zesibingelelo somnikelo otshiswayo, igazi lalo lonke aligalele kumzantsi wesibingelelo.', 'XHO22'),
(3, 4, 35, 'Wowasusa onke amanqatha kulo, njengoko asuswayo amanqatha emvana yombingelelo wombulelo; Woqhumisela ngezo nto umbingeleli esibingelelweni, phezu kokudla kwasemlilweni kukaYehova; aze umbingeleli asicamagushele isono sakhe osenzileyo, aze axolelwe ke.', 'XHO22'),
(3, 5, 1, 'Xa athe ubani wona, waliva izwi lesiqalekiso, walingqina ke, nokuba ubebone oko, nokuba ubesazi; ukuba akathetha, wobuthwala ubugwenxa bakhe.', 'XHO22'),
(3, 5, 2, 'Xa umoya uthe wachukumisa into eyinqambi, nokuba sisidumbu sezinto ezizitho zine eziziinqambi, nokuba sisidumbu sezinto ezizitho zine eziziinqambi, nokuba sisidumbu sesilwanyana esirhubuluzayo esiyinqambi, ingasitheliswanga loo nto kuye; woba yinqambi, abe netyala.', 'XHO22'),
(3, 5, 3, 'Xa athe wachukumisa ubunqambi bomntu, into yonke eyinqambi, adyobhekile yiyo, efihlakeleyo kuye; ukuba uthe wafumanisa, woba netyala.', 'XHO22'),
(3, 5, 4, 'Okanye xa athe umntu wafunga waza wathetha ngomlomo wakhe, esenza okubi okanye okulungileyo, into athe wafunga ngayo umntu ngesifungo, yaza loo nto ifihlakele kuye; ukuba uyazi, woba netyala kwenye yazo.', 'XHO22'),
(3, 5, 5, 'Ukuba uthe waba netyala kuyo nayiphi na kwezo zinto, wovuma ukuba wone ngako.', 'XHO22'),
(3, 5, 6, 'Wolizisa idini letyala lakhe kuYehova ngenxa yesono sakhe one ngaso, ithokazi lasempahleni yakhe emfutshane, imvanazana nokuba litakane lebhokhwe, libe lidini lesono; umbingeleli azicamagushele izono zaso ngenxa yesono saso.', 'XHO22'),
(3, 5, 7, 'Ukuba isandla sakhe sithe asaba nakuzizisa imvana, wozisa amahobe abe mabini, nokuba ngamahobe amabini kuYehova, ngetyala lakhe elonileyo; elinye libe leledini lesono, elinye libe leledini elinyukayo.', 'XHO22'),
(3, 5, 8, 'Wowuzisa kumbingeleli, enze umnikelo wesono kuqala, awubeke entanyeni yawo, kodwa angawahluli;', 'XHO22'),
(3, 5, 9, 'aze atshize ngenxalenye yegazi ledini lesono ecaleni lesibingelelo. Eliseleyo igazi liya kugalelwa esibingelelweni; Lidini lesono elo.', 'XHO22'),
(3, 5, 10, 'Elesibini wenze idini elinyukayo ngokomthetho; aze umbingeleli amcamagushele ngenxa yesono sakhe one ngaso, aze axolelwe.', 'XHO22'),
(3, 5, 11, 'Ukuba ke isandla sakhe sithe asaba nakuwafumana amavukuthu amabini, nokuba ngamavukuthu, wozisa umsondezo wakhe lowo wonileyo, ube sisahlulo seshumi se-efa yomgubo ocoliweyo, ube lidini lesono; angagaleli oli phezu kwawo, angayibeki isiqhumiso phezu kwawo, kuba lidini lesono elo.', 'XHO22'),
(3, 5, 12, 'Wowusondeza kumbingeleli, athabathe umbingeleli kuwo, azalise isandla sakhe, abe lidini lesikhumbuzo, aqhumisele ngalo ngomlilo esibingelelweni, phezu kokudla kwasemlilweni kukaYehova: lidini lesono elo.', 'XHO22'),
(3, 5, 13, 'Umbingeleli wozigubungela ngezono zakhe ngesono sakhe one ngaso kwenye kwezo, aze axolelwe; Okuseleyo koba kokwababingeleli, njengomnikelo wokudla.', 'XHO22'),
(3, 5, 14, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 5, 15, 'Ukuba umphefumlo uthe wona, ngongazi, ezintweni ezingcwele zikaYehova; Wozisa idini letyala kuYehova, inkunzi yemvu egqibeleleyo yasempahleni emfutshane, ngokwexabiso lakho ngeeshekele zesilivere, ngokweshekele yengcwele, ibe lidini letyala.', 'XHO22'),
(3, 5, 16, 'Umele alungise umonakalo awenzileyo engcweleni, ongeze isahlulo sesihlanu sayo kuko oko, anike umbingeleli; Umbingeleli wozigubungela izono zakhe ngenkunzi yemvu yedini letyala, aze axolelwe ke.', 'XHO22'),
(3, 5, 17, 'Umntu xa athe wona, wenza into kumthetho kaYehova; nokuba ebengazi, woba netyala, wobuthwala ubugwenxa bakhe.', 'XHO22'),
(3, 5, 18, 'Wozisa inkunzi yemvu egqibeleleyo yasempahleni yakhe emfutshane, ngokwemilinganiselo yakho, ibe lidini letyala kumbingeleli; aze umbingeleli amcamagushele ngenxa yokuhenyuza kwakhe akwenzileyo engazi, yaye woxolelwa.', 'XHO22'),
(3, 5, 19, 'Lidini letyala ke elo; nonile kuYehova.', 'XHO22'),
(3, 6, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 6, 2, 'Xa athe umntu wona, wona kuYehova, wamlukuhla ummelwane wakhe ngento oyinikiweyo, ngelizwi, ngento ebuxhakasileyo, nokuba wamlukuhla ummelwane wakhe;', 'XHO22'),
(3, 6, 3, 'bathi, bakuyifumana ebilahlekile, baxoka, bafunga ubuxoki kuyo; kuzo zonke ezo zinto umntu wona.', 'XHO22'),
(3, 6, 4, 'Ke kaloku, ekubeni enze isono, enetyala, makayibuyise into abeyithabathile ngokunyanzelwa, naleyo ayizuzileyo ngobuqhophololo, nento abeyinikiwe ukuba ayigcine, nento ayifumeneyo ilahlekile.', 'XHO22'),
(3, 6, 5, 'Okanye zonke izifungo zakhe zobuxoki; yibuyisele etyaleni elikhulu, nesahlulo sesihlanu sayo kuko oko, umnike imfanelo yakhe ngomhla wedini lakhe letyala.', 'XHO22'),
(3, 6, 6, 'Wosondeza idini lakho letyala kuYehova, inkunzi yemvu egqibeleleyo yasempahleni emfutshane, ngokwenani lakho, ibe lidini letyala kumbingeleli.', 'XHO22'),
(3, 6, 7, 'Umbingeleli umele azigubungele izono zakhe phambi koYehova, aze axolelwe ke ngokona kwakhe ngako.', 'XHO22'),
(3, 6, 8, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 6, 9, 'Wisela uAron noonyana bakhe umthetho, uthi, Nguwo lo umyalelo wedini elinyukayo: Lilo elo idini elinyukayo lomlilo ophezu kwesibingelelo ubusuku bonke kude kuse, nomlilo wesibingelelo uhlale usitsha phezu kwalo.', 'XHO22'),
(3, 6, 10, 'Umbingeleli ufanele ambathe isambatho sakhe selinen, ambathe ibhulukhwe yelinen emzimbeni wakhe, athabathe uthuthu lomlilo otshiswe ngumnikelo otshiswayo esibingelelweni, alubeke ecaleni lesibingelelo.', 'XHO22'),
(3, 6, 11, 'Umele akhulule iimpahla zakhe, ambathe ngubo zimbi, alukhuphele ngaphandle kweminquba uthuthu endaweni ehlambulukileyo.', 'XHO22'),
(3, 6, 12, 'Umlilo ophezu kwesibingelelo wovutha phezu kwaso; ayinakucinywa; umbingeleli wobeka iinkuni phezu kwawo imiso ngemiso, abeke phezu kwawo idini elinyukayo; aqhumisele phezu kwalo ngamanqatha ombingelelo woxolo.', 'XHO22'),
(3, 6, 13, 'Umlilo wohlala usitsha esibingelelweni; awuyi kucinywa.', 'XHO22'),
(3, 6, 14, 'Nguwo lo umyalelo wedini elinyukayo: oonyana baka-Aron bowulungiselela phambi koYehova, phambi kwesibingelelo;', 'XHO22'),
(3, 6, 15, 'Wothabatha kuwo, azalise isandla kumgubo ocoliweyo womnikelo wokudla, naseolini yawo, nasesiqhumeni sonke esiphezu komnikelo wokudla, aqhumisele ngazo esibingelelweni, zibe livumba elithozamisayo, libe sisikhumbuzo yona, eNkosini.', 'XHO22'),
(3, 6, 16, 'Okuseleyo kuwo kodliwa nguAron noonyana bakhe; yodliwa izonka ezingenagwele endaweni engcwele; bawadlela entendelezweni yentente yokuhlangana.', 'XHO22'),
(3, 6, 17, 'Uze ungabhaki unegwele. Ndabanika yona njengesabelo sabo emirhumeni yam; yingcwele kangcwele, ibe lidini lesono neletyala.', 'XHO22'),
(3, 6, 18, 'Yonke into eyindoda phakathi koonyana baka-Aron yowudla. Yoba ngumthetho ongunaphakade ezizukulwaneni zenu ngokuphathelele iminikelo yasemlilweni kaYehova; bonke abayichukumisayo boba ngcwele.', 'XHO22'),
(3, 6, 19, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 6, 20, 'Nguwo lowo umsondezo ka-Aron noonyana bakhe, abowuzisa kuYehova ngomhla wokuthanjiswa kwakhe; ube sisahlulo seshumi se-efa yomgubo ocoliweyo, ube ngumnikelo wokudla wamaxesha onke;', 'XHO22'),
(3, 6, 21, 'Yenze epanini, eneoli; wakuba uvuthiwe, uze nawo; yaye wolungisa amaqhekeza omnikelo wasemlilweni abe livumba elithozamisayo kuYehova.', 'XHO22'),
(3, 6, 22, 'Nombingeleli othanjisiweyo esikhundleni sakhe wobaxolelanisa; Ngumthetho ongunaphakade kuYehova lowo; mayitshiswe iphele.', 'XHO22'),
(3, 6, 23, 'Kuba yonke iminikelo yasemlilweni wombingeleli yotshiswa kuphele; mayingadliwa.', 'XHO22'),
(3, 6, 24, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 6, 25, 'Thetha kuAron nakoonyana bakhe, uthi, Nguwo lo umthetho wedini lesono: Idini lesono losondezwa phambi koYehova apho kunyuswe khona idini elinyukayo; ingcwele kakhulu.', 'XHO22'),
(3, 6, 26, 'Umbingeleli olenzayo idini lesono wolidla; yodliwa endaweni engcwele, entendelezweni yentente yokuhlangana.', 'XHO22'),
(3, 6, 27, 'Yonke into esukuba iyichukumisa inyama yayo yoba ngcwele; yaye ukuba elinye igazi lafefa kwisambatho ngasinye, liya kuhlanjwa kweso sifefweyo ekupheleni kokholo.', 'XHO22'),
(3, 6, 28, 'Ke yona imbiza yodongwe ebigalelwe kulo, iya kwaphuka; yaye ukuba igalelwe ngembiza yobhedu, yoshenxiswa ize ihlanjwe ngamanzi.', 'XHO22'),
(3, 6, 29, 'Yonke into eyindoda phakathi kwababingeleli yolidla; yeyona nto ingcwele.', 'XHO22'),
(3, 6, 30, 'Idini lesono, eligazi lalo eliziswe ententeni yokuhlangana, ukuba licamagushele engcweleni, malingadliwa; utshiswe ngomlilo.', 'XHO22'),
(3, 7, 1, 'Nguwo lowo umyalelo wedini letyala: liyingcwele kangcwele lona.', 'XHO22'),
(3, 7, 2, 'Apho kubingelelwa khona idini letyala, zowusondeza apho umnikelo wetyala; igazi layo litshize kumacala onke esibingelelweni.', 'XHO22'),
(3, 7, 3, 'Wowasondeza onke amanqatha alo; umnqamlezo nenqatha eligubungele amathumbu;', 'XHO22'),
(3, 7, 4, 'Wozisusa izibindi ezibini, nenqatha lazo, elisecaleni, neseli elisesibindini, kunye nezibindi;', 'XHO22'),
(3, 7, 5, 'Woqhumisela ngezo nto umbingeleli esibingelelweni, kukudla kwasemlilweni kaYehova; Lidini letyala ke elo.', 'XHO22'),
(3, 7, 6, 'Yonke into eyindoda phakathi kwababingeleli yolidla; yodliwa endaweni engcwele; yeyona nto ingcwele.', 'XHO22'),
(3, 7, 7, 'Njengedini lesono, nedini letyala, likwanjalo, mnye umyalelo kuwo omabini; ibe ngumbingeleli ocamagusha ngalo.', 'XHO22'),
(3, 7, 8, 'Umbingeleli osukuba ethabatha idini elinyukayo lomntu, isikhumba sedini elinyukayo alisondezayo soba sesakhe.', 'XHO22'),
(3, 7, 9, 'Wonke umnikelo wokubhaka obhaka eziko, nayo yonke into elungiselelwa epanini, nasepanini, yoba ngumbingeleli oya kuwunikela.', 'XHO22'),
(3, 7, 10, 'Wonke umnikelo wokudla kwasemlilweni, ogalelwe ioli, owomileyo, wonikwa oonyana bonke baka-Aron, omnye abe njengomnye.', 'XHO22'),
(3, 7, 11, 'Kwaye ngumthetho wombulelo ukuba umelwe kukuwuzisa eNkosini.', 'XHO22'),
(3, 7, 12, 'Ukuba uthe wasondeza umbingelelo wombulelo, wobhaka izonka ezingenagwele, zigalelwe ioli, nezonka ezingenagwele, zithanjiswe ioli, nesonka esigalelwe ioli, nomgubo ocoliweyo, kube ngumnikelo wendumiso, nomnikelo wombulelo.', 'XHO22'),
(3, 7, 13, 'Ngaphandle kwesonka wosondeza umnikelo wakhe wesonka esinegwele ndawonye nomnikelo wakhe wombulelo.', 'XHO22'),
(3, 7, 14, 'Umele anikele ube mnye emnikelweni wokudla okupheleleyo, ube ngumnikelo onikelwa kuYehova, ube ngowombingeleli olitshizayo igazi lombingelelo wombulelo.', 'XHO22'),
(3, 7, 15, 'Inyama yombingelelo wakhe woxolo wodliwa kwangaloo mini yokuphuma kwakhe; makangashiyi nto kuyo kude kuse.', 'XHO22'),
(3, 7, 16, 'Ke ukuba umsondezo wakhe waba ngowesibambiso, nokuba ngumnikelo wokuzithandela, wodliwa kwangaloo mini asondeza ngayo umsondezo wakhe;', 'XHO22'),
(3, 7, 17, 'Ke yona inyama eseleyo ngomhla wesithathu iya kutshiswa ngomlilo.', 'XHO22'),
(3, 7, 18, 'Ukuba kuthe kwadliwa inyama yombingelelo wakhe wombulelo, ngomhla wesithathu, awusayi kumkholekisa, awusayi kubalelwa kulowo uwuhlwayelayo; yoba lisikizi, idle umphefumlo. Niya kuthwala ububi kuyo.', 'XHO22'),
(3, 7, 19, 'Inyama efike entweni eyinqambi mayingadliwa; itshiswe ngomlilo; ukuba yinyama, bonke abahlambulukileyo bayidle;', 'XHO22'),
(3, 7, 20, 'ke yena othe wayidla inyama yombingelelo wombulelo kaYehova, ekwinto engcolileyo yakhe, loo mphefumlo wonqanyulwa, ungabikho ebantwini bakowawo.', 'XHO22'),
(3, 7, 21, 'Umntu osukuba echukumisa nantoni na engahlambulukanga, njengokungahlambuluki komntu, nasiphi na isilwanyana esingahlambulukanga okanye nantoni na ecekisekayo engahlambulukanga, waza wayidla inyama yombingelelo wombulelo kaYehova, loo mphefumlo wonqunyulwa ungabikho kuye abantu.', 'XHO22'),
(3, 7, 22, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 7, 23, 'Thetha koonyana bakaSirayeli, uthi, Ize ningawadli amanqatha eenkomo, nagusha, nabhokhwe.', 'XHO22'),
(3, 7, 24, 'Inqatha lento ezifeleyo, namanqatha ento eqwengiweyo, angásebenza ngayo; kodwa nize ningawadli.', 'XHO22'),
(3, 7, 25, 'Kuba nabani na olidlayo inqatha lezinto ezizitho zine, ubani enze idini elinyukayo kuYehova, loo mntu ulidlayo wonqanyulwa, angabikho ebandleni lakowabo.', 'XHO22'),
(3, 7, 26, 'Ize ningadli nagazi ezindlwini zenu zonke, nokuba lelentaka, neleenkomo;', 'XHO22'),
(3, 7, 27, 'Wonke umphefumlo othe wadla igazi lalo naluphi na uhlobo, loo mphefumlo wonqanyulwa, ungabikho phakathi kwawo.', 'XHO22'),
(3, 7, 28, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 7, 29, 'Thetha koonyana bakaSirayeli, uthi, Umntu osukuba esondeza imibingelelo yakhe yombulelo kuYehova, wowuzisa umsondezo wakhe kuYehova, ube phezu kwemibingelelo yakhe yombulelo;', 'XHO22'),
(3, 7, 30, 'Wolizisa ngezandla zakhe idini elinyukayo kuYehova, elinamanqatha kunye netyeya, ityeya yokuhambisa idini elinyukayo phambi koYehova.', 'XHO22'),
(3, 7, 31, 'Umbingeleli woqhumisela ngenqatha elo esibingelelweni, kodwa ityeya ibe yeka-Aron noonyana bakhe.', 'XHO22'),
(3, 7, 32, 'Nomnika umbingeleli intende yesandla esikhulu, ibe ngumrhumo ebingelelweni lenu loxolo.', 'XHO22'),
(3, 7, 33, 'Omnye koonyana baka-Aron, osondeza igazi lombulelo, nenqatha lesandla sakhe sokunene, soba sisabelo sakhe;', 'XHO22'),
(3, 7, 34, 'Kuba ityeya eshukunyiswayo nesandla ndazithabatha koonyana bakaSirayeli kumbingelelo wombulelo ndaza ndazinika uAron umbingeleli noonyana bakhe njengommiselo wexesha elingenammiselo ovela koonyana bakaSirayeli.', 'XHO22'),
(3, 7, 35, 'Ezo nto ke ziyinxalenye yokuthanjiswa kuka-Aron nokuthanjiswa koonyana bakhe kwasekudleni kwasemlilweni kukaYehova ngomhla awabasondezayo ukuba babe ngababingeleli kuYehova;', 'XHO22'),
(3, 7, 36, 'awawisa umthetho uYehova, ngomhla wokubathambisa kwakhe, ukuba bawunikwe phakathi koonyana bakaSirayeli, ngokomyalelo ongunaphakade, ezizukulwaneni zabo.', 'XHO22'),
(3, 7, 37, 'Nguwo lo umthetho womnikelo otshiswayo, nomnikelo otshiswayo nomnikelo wesono nomnikelo wetyala nombingelelo nowombulelo;', 'XHO22'),
(3, 7, 38, 'awamwisela umthetho ngawo uYehova uMoses, entabeni yaseSinayi, ngomhla wokubawisela kwakhe umthetho oonyana bakaSirayeli, entlango yaseSinayi, ukuba basondeze imisondezo yabo kuYehova.', 'XHO22'),
(3, 8, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 8, 2, 'Thabatha ooAron noonyana bakhe, iingubo, ioli yokuthambisa, inkunzi entsha yenkomo, yedini lesono, neenkunzi zezimvu zibe mbini, nengobozi yezonka ezingenagwele;', 'XHO22'),
(3, 8, 3, 'Libizele ndawonye lonke ibandla emnyango wentente yokuhlangana.', 'XHO22'),
(3, 8, 4, 'Wenza uMoses ngoko uYehova wamwisela umthetho ngako; labizela ndawonye ibandla emnyango wentente yokuhlangana.', 'XHO22'),
(3, 8, 5, 'Wathi uMoses kwibandla, Yiyo le into awise umthetho ngayo uYehova ukuba yenziwe.', 'XHO22'),
(3, 8, 6, 'uMoses wabasondeza ke ooAron noonyana bakhe, wabahlamba ngamanzi.', 'XHO22'),
(3, 8, 7, 'Wamambathisa idyasi wambhinqisa umbhinqo wamambathisa idyasi wamfaka ilaphu egxalabeni wambopha ngelaphu egxalabeni wambopha ngalo.', 'XHO22'),
(3, 8, 8, 'Wafaka nengxowa yesifuba; wabeka iiUrim neeTumim elubengweni;', 'XHO22'),
(3, 8, 9, 'wamfaka unkontsho entloko; elunkontshweni phambi kwakhe wabeka isitya segolide, isigubungelo esingcwele; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 8, 10, 'Wayithabatha uMoses ioli yokuthambisa, wawuthambisa umnquba neento zonke ezikuwo, wawungcwalisa.', 'XHO22'),
(3, 8, 11, 'Wafefa isibingelelo izihlandlo ezisixhenxe, wasithambisa isibingelelo nempahla yaso yonke nengubo yaso yokwaleka noseko lwaso, ukuba singcwaliswe.', 'XHO22'),
(3, 8, 12, 'Wagalela ioli yokuthambisa kuAron entloko, wamthambisa ukuba angcwaliswe.', 'XHO22'),
(3, 8, 13, 'Wabasondeza ke uMoses oonyana baka-Aron, wabanxiba iingubo zabo ezinde zangaphezulu, wababopha ngamaqhina, wabathwala iminqwazi; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 8, 14, 'Wasondeza inkunzi entsha yenkomo yedini lesono, bacinezela ooAron noonyana bakhe ngezandla zabo phezu kwentloko yenkunzi entsha yedini lesono.', 'XHO22'),
(3, 8, 15, 'Wayihlaba; Walithabatha uMoses igazi, walitshiza ngeempondo zesibingelelo ngomnwe wakhe, wasihlambulula isibingelelo, waligalela umzantsi wesibingelelo igazi, wasingcwalisa, ukuze sicamagushele.', 'XHO22'),
(3, 8, 16, 'Wawathabatha onke amanqatha asesiswini, namanqatha asesibindini, nezintso zombini, nenqatha lazo, waqhumisela ngazo uMoses esibingelelweni.', 'XHO22'),
(3, 8, 17, 'Ke inkomo, nesikhumba sayo, nenyama yayo, nomswane wayo, wayitshisa ngomlilo ngaphandle kweminquba; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 8, 18, 'Wasondeza inkunzi yemvu yedini elinyukayo, bacinezela ooAron noonyana bakhe ngezandla zabo phezu kwentloko yenkunzi yemvu.', 'XHO22'),
(3, 8, 19, 'Wayihlaba; uMoses walitshiza igazi, wajikelezisa esibingelelweni;', 'XHO22'),
(3, 8, 20, 'inkunzi yemvu wayityatya; Waqhumisela uMoses ngentloko, nenyama, nenqatha.', 'XHO22'),
(3, 8, 21, 'Wazihlamba izibilini namathambo ngamanzi; uMoses waqhumisela ngenkunzi yemvu iphela esibingelelweni; laba lidini elinyukayo, livumba elithozamisayo, kukudla kwasemlilweni ke oko kuYehova; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 8, 22, 'Yazisa enye inkunzi yemvu, inkunzi yemvu; waza uAron noonyana bakhe babeka izandla zabo phezu kwentloko yenkunzi yegusha leyo.', 'XHO22'),
(3, 8, 23, 'Wayihlaba; Wathabatha uMoses egazini layo, walidyobha endlebeni yasekunene yesandla sika-Aron, nakusithupha wesandla sakhe sasekunene, nakubhontsi wonyawo lwakhe lwasekunene.', 'XHO22'),
(3, 8, 24, 'Wabasondeza oonyana baka-Aron, uMoses wafefa ngenxalenye yegazi esithinzini sendlebe yabo yasekunene, nakoobhontsi bezandla zabo, nakubhontsi bonyawo lwabo; uMoses walitshiza igazi, wajikelezisa esibingelelweni.', 'XHO22'),
(3, 8, 25, 'Wawathabatha amanqatha, nomlenze, namanqatha onke asesiswini, nomhlehlo wesibindi, nezintso zombini, nenqatha lazo, nesandla sasekunene.', 'XHO22'),
(3, 8, 26, 'Wathabatha engobozini yezonka ezingenagwele eyayiphambi koYehova, wathabatha iqebengwana elingenagwele lalinye, neqebengwana leoli, neqebengwana lalinye, wabeka phezu kwamanqatha egxalabeni lasekunene.', 'XHO22'),
(3, 8, 27, 'Wazibeka zonke izinto ezandleni zika-Aron nasezandleni zoonyana bakhe waza wenza umnikelo otshangatshangiswayo phambi koYehova.', 'XHO22'),
(3, 8, 28, 'Wayithabatha uMoses ezandleni zabo, waqhumisela ngayo esibingelelweni, phezu kwedini elinyukayo; zazinikelwe kwivumba elimnandi; Oko kukudla kwasemlilweni kuYehova.', 'XHO22'),
(3, 8, 29, 'Wayithabatha uMoses ityeya, wayitshangatshangisa, yaba ngumtshangatshangiso phambi koYehova; inkunzi yemvu yasisabelo sikaMoses; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 8, 30, 'Wayithabatha uMoses ioli yokuthambisa, nenxalenye yegazi lasesibingelelweni, wamtshiza uAron, nezambatho zakhe, noonyana bakhe, nezambatho zoonyana bakhe ababenaye; yaye wamngcwalisa uAron nezambatho zakhe noonyana bakhe nezambatho zoonyana bakhe ndawonye naye.', 'XHO22'),
(3, 8, 31, 'Wathi uMoses kuAron noonyana bakhe, Yiphekeni inyama emnyango wentente yokuhlangana, niyidle khona isonka engobozini yomnikelo wokudla, njengoko ndawisa umthetho, ndathi, uAron noonyana bakhe boyidla. Yona.', 'XHO22'),
(3, 8, 32, 'Into eseleyo yenyama nesonka, uze ungayitshisi ngomlilo.', 'XHO22'),
(3, 8, 33, 'Ize ningaphumi emnyango wentente yokuhlangana, imihla esixhenxe, ide izaliseke imihla yembewu yakho; uya kuningcwalisa iintsuku ezisixhenxe.', 'XHO22'),
(3, 8, 34, 'uYehova uwise umthetho ukuba kwenziwe njengoko enzile namhla, ukukunyanga.', 'XHO22'),
(3, 8, 35, 'Nohlala ubusuku nemini iintsuku ezisixhenxe emnyango wentente yokuhlangana, niyigcine inkonzo kaYehova, ukuze ningafi; ngokuba ndiwisiwe ngolo hlobo.', 'XHO22'),
(3, 8, 36, 'Benza uAron noonyana bakhe yonke into awayeyiyalele uYehova ngenkonzo kaMoses.', 'XHO22'),
(3, 9, 1, 'Ngomhla wesithoba nowesibini, uMoses wabiza uAron noonyana bakhe, namadoda amakhulu akwaSirayeli;', 'XHO22'),
(3, 9, 2, 'Wathi kuAron, Thabatha ithole, ithole lenkomo, libe lidini lesono, nenkunzi yemvu ibe lidini elinyukayo eligqibeleleyo, uzisondeze phambi koYehova.', 'XHO22'),
(3, 9, 3, 'Thetha koonyana bakaSirayeli, uthi, Zithabatheleni itakane lebhokhwe, libe lidini lesono; nethole lenkomo, nemvana, eneminyaka emibini ubudala, ezigqibeleleyo, zizezedini elinyukayo ke ezo;', 'XHO22'),
(3, 9, 4, 'nenkunzi yenkomo, nenkunzi yemvu, zibe ngumbingelelo wombulelo wokubingelelwa kuYehova; nomnikelo othanjiswe ngeoli, ngokuba namhla uYehova uya kubonakala kuwe.', 'XHO22'),
(3, 9, 5, 'Bakuzisa oko uMoses wayekuyalele phambi kwentente yokuhlangana, lasondela lonke ibandla, lema phambi koYehova.', 'XHO22'),
(3, 9, 6, 'Wathi uMoses, Yiyo loo nto uYehova akuwisele umthetho ngayo, ukuze ubuqaqawuli bukaYehova bubonakale kuwe.', 'XHO22'),
(3, 9, 7, 'Wathi uMoses kuAron, Sondela esibingelelweni, wenze idini lakho lesono, nedini lakho elinyukayo, ucamagushele izono zakho nezono zabantu; sondezani umbingelelo wabantu, uwucamagushele isono; njengoko uYehova wawisayo umthetho.', 'XHO22'),
(3, 9, 8, 'Wasondela uAron esibingelelweni waza waxhela ithole lenkomo lomnikelo wesono ongowakhe.', 'XHO22'),
(3, 9, 9, 'Balizisa oonyana baka-Aron igazi kuye, wawuthi nkxu umnwe wakhe egazini, waligalela ezimpondweni zesibingelelo, waligalela igazi emazantsi esibingelelo.', 'XHO22'),
(3, 9, 10, 'Ke inqatha, nezintso, neseli elisesibindini sedini lesono, waqhumisela ngezo zinto ngomlilo esibingelelweni, njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(3, 9, 11, 'Inyama nesikhumba wayitshisa ngomlilo ngaphandle kweminquba.', 'XHO22'),
(3, 9, 12, 'Wenza idini elinyukayo; Oonyana baka-Aron basondeza igazi kuye, waligalela phezu kwesibingelelo.', 'XHO22'),
(3, 9, 13, 'Balizisa kuye idini elinyukayo, neenxalenye zalo, nentloko, waqhumisela ngezo nto esibingelelweni.', 'XHO22'),
(3, 9, 14, 'Wawahlamba amathumbu nezibilini, waqhumisela ngezo zinto phezu kwedini elinyukayo esibingelelweni.', 'XHO22'),
(3, 9, 15, 'Emva koko wasondeza umbingelelo wasesidlangalaleni, wathabatha ibhokhwe yomnikelo wesono wasesidlangalaleni, wayixhela, wenza nomnikelo wesono njengeyokuqala.', 'XHO22'),
(3, 9, 16, 'Emva koko wenza umnikelo otshiswayo waza wawunikela ngokomthetho.', 'XHO22'),
(3, 9, 17, 'Wasondeza idini elinyukayo, uzalisa isandla kulo, waqhumisela ngalo esibingelelweni, ngexesha ledini elinyukayo lakusasa.', 'XHO22'),
(3, 9, 18, 'Kananjalo waxhela inkunzi yenkomo nenkunzi yegusha, zibe ngumbingelelo wombulelo; oonyana baka-Aron bamnika igazi, walitshiza wajikelezisa esibingelelweni;', 'XHO22'),
(3, 9, 19, 'namanqatha enkunzi entsha yenkomo, nelenkunzi yemvu, nomlenze, nowagubungele amathumbu, nezintso, namanqatha esibindi;', 'XHO22'),
(3, 9, 20, 'Babeka amanqatha phezu kweetyesi, waqhumisela ngamanqatha esibingelelweni.', 'XHO22'),
(3, 9, 21, 'Wazisondeza ke uAron iincum ezo nesokunene, zaba ngumtshangatshangiso phambi koYehova; njengoko uMoses wamwiselayo umthetho.', 'XHO22'),
(3, 9, 22, 'Wasiphakamisela ebantwini isandla sakhe uAron, wabasikelela, wehla emnikelweni wesono, nakumbingelelo onyukayo, nowombulelo.', 'XHO22'),
(3, 9, 23, 'Bangena ooMoses noAron ententeni yokuhlangana, baphuma babasikelela abantu, babonakala ubuqaqawuli bukaYehova kubo bonke abantu.', 'XHO22'),
(3, 9, 24, 'Kwaphuma umlilo phambi koYehova, walidla idini elinyukayo, nenqatha, phezu kwesibingelelo. Bakubona bonke abantu, baduma, bawa ngobuso.', 'XHO22'),
(3, 10, 1, 'Bathabatha ooNadabhi noAbhihu, oonyana baka-Aron, bathabatha enye kuzo ugcedevu lwakhe, babeka umlilo kuwo, babeka isiqhumiso phezu kwawo, bazisa umlilo ongesesikweni phambi koYehova, angabawiselanga mthetho ngawo.', 'XHO22'),
(3, 10, 2, 'Kwaphuma umlilo ovela kuYehova, wabatshisa, bafa phambi koYehova.', 'XHO22'),
(3, 10, 3, 'Wathi uMoses kuAron, Utsho uYehova ukuthi, Ndozingcwalisela abasondela kum, ndizukiseke phambi kwabantu bonke. Wathi tu uAron.', 'XHO22'),
(3, 10, 4, 'uMoses wabiza uMishayeli noElishafane, oonyana bakaUziyeli, uyisekazi ka-Aron, wathi kubo, Sondelani, nibakhuphele phandle abazalwana benu eminqubeni phambi kwengcwele.', 'XHO22'),
(3, 10, 5, 'Basondela ke, babathwalela ngaphandle kweminquba, beneengubo zabo; njengoko watshoyo uMoses.', 'XHO22'),
(3, 10, 6, 'Wathi uMoses kuAron nakoonyana bakhe uElazare noItamare, Musani ukuzigqubuthela entloko, ningazikrazuli iingubo zenu; ukuze ningafi, bungabi nalo lonke uhlanga; kodwa mabalile bonke aba bazalwana bendlu kaSirayeli ngenxa yomlilo awutshisileyo uYehova.', 'XHO22'),
(3, 10, 7, 'Ize ningaphumi ngomnyango wentente yokuhlangana, hleze nife; ngokuba ubugwenxa bukaYehova buphezu kwakho. Benza ke ngokwelizwi likaMoses.', 'XHO22'),
(3, 10, 8, 'Wathetha uYehova kuAron, esithi:', 'XHO22'),
(3, 10, 9, 'Musa ukusela wayini nasiselo sinxilisayo, wena noonyana bakho abakunye nawe, ekungeneni kwenu ententeni yokuhlangana, ukuze ningafi; ibe ngumyalelo ongunaphakade ezizukulwaneni zenu;', 'XHO22'),
(3, 10, 10, 'ukuze nikwazi ukwahlula ongcwele kokuyinqambi, nakoyinqambi kohlambulukileyo;', 'XHO22'),
(3, 10, 11, 'ukuze bayalwe oonyana bakaSirayeli yonke imithetho awabawiselayo uYehova ngenkonzo kaMoses.', 'XHO22'),
(3, 10, 12, 'Wathi uMoses kuAron, nakuElazare, nakuItamare, oonyana bakhe abaseleyo, Thabathani umnikelo oseleyo ekudleni kwasemlilweni kukaYehova, niwudle ungenagwele esibingelelweni; ngokuba uyingcwele kakhulu;', 'XHO22'),
(3, 10, 13, 'Nizidle endaweni engcwele, kuba zezakho noonyana bakho, ekudleni kwasemlilweni kukaYehova; ngokuba ndifundisiwe.', 'XHO22'),
(3, 10, 14, 'Zidle ibele elishukumayo, negxalaba elishukumayo endaweni ehlambulukileyo; wena noonyana bakho, neentombi zakho, nawe; ngokuba yeyakho neyoonyana bakho, ekuyibingelelweni imibingelelo yombulelo yoonyana bakaSirayeli.', 'XHO22'),
(3, 10, 15, 'Umlenze ophakanyiswayo netyeya etshangatshangiswayo yoziswa ndawonye nomnikelo otshiswayo wamanqatha ukuze zitshangatshangiswe zibe ngumnikelo otshangatshangiswayo phambi koYehova; ibe yeyakho noonyana bakho kude kuse ephakadeni; njengoko uYehova wawisayo umthetho.', 'XHO22'),
(3, 10, 16, 'Wayikhangela uMoses itakane lebhokhwe, wabona ukuba litshisiwe, wavutha ngumsindo kuElazare noItamare, oonyana baka-Aron abaseleyo, wathi,', 'XHO22'),
(3, 10, 17, 'Kutheni na ningawudlanga umnikelo wesono kwindawo engcwele, ekubeni uyeyona nto ingcwele, waza uThixo waninika wona ukuba nithwale ububi bebandla, ukuze nilicamagushele phambi koYehova?', 'XHO22'),
(3, 10, 18, 'Yabona, igazi lawo aliziswanga engcweleni;', 'XHO22'),
(3, 10, 19, 'Wathi uAron kuMoses, Yabona, namhlanje besondeze idini labo lesono, nedini labo elinyukayo phambi koYehova; zandihlela ezo zinto; yaye ukuba bendiwudlile umnikelo wesono namhlanje, ngaba ngekukholekile na emehlweni kaYehova?', 'XHO22'),
(3, 10, 20, 'Akuyiva uMoses loo nto, waneliseka;', 'XHO22'),
(3, 11, 1, 'Wathetha uYehova kuMoses nakuAron, wathi kubo,', 'XHO22'),
(3, 11, 2, 'Thetha koonyana bakaSirayeli, uthi, Zizo ezi izinto ezizitho zine eninokuzidla phakathi kwezinto zonke eziphilileyo zehlabathi.', 'XHO22');
