INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(7, 3, 4, 'Uthe ke uPetros, enoYohane, eqwalasele kuyo wathi, Khangela kuthi apha.', 'XHO22'),
(7, 3, 5, 'Wabagqala ke, elindele ukwamkela into kubo.', 'XHO22'),
(7, 3, 6, 'Uthe ke uPetros, Isilivere negolide andinayo; kodwa endinako ndikunika kona: Egameni likaYesu Kristu umNazarete, vuka uhambe.', 'XHO22'),
(7, 3, 7, 'Wayibamba ngesandla sokunene, wayiphakamisa; zomelezeka kwaoko iinyawo zayo namaqatha ayo.', 'XHO22'),
(7, 3, 8, 'Yesuka umtsi, yema, yahambahamba, yangena nabo etempileni, ihambahamba itsiba, imdumisa uThixo.', 'XHO22'),
(7, 3, 9, 'Baza bonke abantu bayibona ihambahamba, idumisa uThixo;', 'XHO22'),
(7, 3, 10, 'Babeqonda kakuhle, ukuba ingulowa ubehlalela amalizo ngasemnyango oMhle wetempile, bathi qhiphu umbilini, bathi nqa yiloo nto ihlileyo kuye.', 'XHO22'),
(7, 3, 11, 'Ke kaloku, sakubon’ ukuba isisiqhwala sibambe ooPetros noYohane, abantu babalekela kubo evarandeni ekuthiwa yekaSolomon, bemangalisiwe kakhulu.', 'XHO22'),
(7, 3, 12, 'Akukubona ke oko uPetros, waphendula wathi kubo abantu, Madoda, maSirayeli, yini na ukuba nimangaliswe yile nto? Kanjalo yini na ukuba nisijonge, ngathi senze ukuba lo mntu ahambe ngawethu amandla, nangobungcwele bethu?', 'XHO22'),
(7, 3, 13, 'uThixo ka-Abraham, kaIsake, kaYakobi, uThixo woobawo bethu, umzukisile uYesu uNyana wakhe; enamnikelayo, namkhanyela phambi koPilato, ekubeni yena ugqibe kwelokuba amkhulule.', 'XHO22'),
(7, 3, 14, 'Ke nina namkhanyela oyiNgcwele, oliLungisa, nacela ukuba ninikwe indoda engumbulali.', 'XHO22'),
(7, 3, 15, 'Ke yona iMbangi yobomi nayibulala, awayivusayo uThixo kwabafileyo; esingamangqina ako oko.', 'XHO22'),
(7, 3, 16, 'Nangenxa yokukholwa kwigama layo, lo nimbonayo, nimaziyo, limomelezile igama layo; lwathi ukholo olungayo lwamnika oku kuphila kupheleleyo phambi kwenu nonke.', 'XHO22'),
(7, 3, 17, 'Nangoku, bazalwana, ndiyazi ukuba nenze ngokungazi, njengabo nabo abaphathi benu.', 'XHO22'),
(7, 3, 18, 'Ke zona izinto uThixo, awazixelayo ngenxa engaphambili ngomlomo wabo bonke abaprofeti bakhe, ukuba uKristu wayeza kuva ubunzima, wenjenjalo ukuzalisekisa oko.', 'XHO22'),
(7, 3, 19, 'Guqukani ke ngoko, nijike, ukuze zicinywe izono zenu;', 'XHO22'),
(7, 3, 20, 'ithume uYesu Kristu, owavakaliswayo kuni ngenxa engaphambili;', 'XHO22'),
(7, 3, 21, 'elimelwe kukumamkela izulu, kude kufike amaxesha okubuyiswa kweento zonke, awawathethayo uThixo ngomlomo wabo bonke abaprofeti bakhe abangcwele, kususela kunaphakade.', 'XHO22'),
(7, 3, 22, 'Kaloku uMosis wathi koobawo: “iNkosi uThixo wenu iya kunivelisela umprofeti kubazalwana benu onjengam nje; ize nimve yena kuzo zonke izinto asukuba eya kuzithetha kuni.', 'XHO22'),
(7, 3, 23, 'Yothi ke yonke imiphefumlo engathanga imve loo mprofeti, itshatyalaliswe, ingabikho phakathi kwabantu.', 'XHO22'),
(7, 3, 24, 'Nabo bonke abaprofeti, ukususela kuSamuweli nabalandelayo, bonke abo bathethayo, baprofeta ngokunjalo ngale mihla.', 'XHO22'),
(7, 3, 25, 'Nina ningoonyana babo abaprofeti, nabomnqophiso awanqophisana ngawo noobawo bethu uThixo, esithi kuAbraham, Yaye ke iya kusikeleleka embewini yakho yonke imizalwane yehlabathi.', 'XHO22'),
(7, 3, 26, 'uThixo ke, akuba emvusile uNyana wakhe uYesu, wamthuma kuni kuqala, ukuba anisikelele ngokuniguqula nonke ngabanye kwiinkohlakalo zenu.', 'XHO22'),
(7, 4, 1, 'Ke kaloku, bakubon’ ukuba bayathetha kubo abantu, bafika ababingeleli, nomphathi wetempile, nabaSadusi;', 'XHO22'),
(7, 4, 2, 'bebindekile ngenxa yokuba befundisa abantu, bekuxela ukuvuka kubo abafileyo ngoku kukaYesu ukuvuka.', 'XHO22'),
(7, 4, 3, 'Babasa izandla, babafaka entolongweni, kwada kwaba ngengomso; kuba bekuse kuhlwile.', 'XHO22'),
(7, 4, 4, 'Ke into eninzi yabo babelivile ilizwi, yakholwa; lathi inani lamadoda lingathi kumawaka amahlanu.', 'XHO22'),
(7, 4, 5, 'Ke kaloku kwathi ngengomso, abaphathi babo, namadoda amakhulu, nababhali, bathi,', 'XHO22'),
(7, 4, 6, 'Kwahlanganisana eYerusalem uAnas umbingeleli omkhulu, noKayafa, noYohane, noAlesandire, kwanabanye bemizalwane yombingeleli omkhulu;', 'XHO22'),
(7, 4, 7, 'Bakuba bebamise phakathi, bababuza besithi, Le nto nina niyenze ngamandla mani na, nangagama lini na?', 'XHO22'),
(7, 4, 8, 'Wathi ke uPetros, ezele nguMoya oyiNgcwele, wathi kubo, Nina baphathi babantu, nani madoda makhulu akwaSirayeli,', 'XHO22'),
(7, 4, 9, 'ukuba thina namhlanje siyacikideka ngomsebenzi omhle owenziweyo lowo ungenamandla, ukuba ungáphiliswa njani na;', 'XHO22'),
(7, 4, 10, 'Makwazeke kuni nonke, nakubo bonke abantu bakwaSirayeli, ukuba kusegameni likaYesu Kristu waseNazarete, enambethelelayo emnqamlezweni nina, athe uThixo wamvusa kwabafileyo; kungaye lowo, ukuba lo mntu eme apha phambi kwenu ephilile.', 'XHO22'),
(7, 4, 11, 'Lo ngulo ilitye elenziwa into engento nini bakhi, elisuke laba yintloko yembombo.', 'XHO22'),
(7, 4, 12, 'Kananjalo usindiso alukho kuwumbi; kuba akukho gama limbi phantsi kwezulu, linikiweyo phakathi kwabantu, esimele ukusindiswa ngalo.', 'XHO22'),
(7, 4, 13, 'Ke kaloku, bakubona ukungafihlisikuthetha koPetros noYohane, beqiqa nokuba ngabantu abangenamfundo, abangenakwazi, bamangaliswa; babaqonda ukuba babenaye uYesu.', 'XHO22'),
(7, 4, 14, 'Bakumbona ke umntu emi nabo, yena lowo uphilisiweyo, ababanga nandawo yakuphika.', 'XHO22'),
(7, 4, 15, 'Bakuba bethe mabaphume entlanganisweni yamatyala, bacebisana bodwa,', 'XHO22'),
(7, 4, 16, 'besithi, Siya kubathini na aba bantu? Kuba okunene, ukuba kuhle ngabo umqondiso owazekayo, oyinto ebonakalayo kubo bonke abemi eYerusalem, kwaye asinakuyikhanyela loo nto.', 'XHO22'),
(7, 4, 17, 'Ukuze ke loo nto ingahlakazeki ngakumbi ebantwini, masise sibasongela ngelokuba bangabi sathetha mntwini ngeli gama.', 'XHO22'),
(7, 4, 18, 'Bebabizile, babathethela ngelithi, mabangabi sakhankanya nakanye, bangafundisi nokufundisa ngegama likaYesu.', 'XHO22'),
(7, 4, 19, 'Ke kaloku uPetros noYohane baphendula, bathi kubo, Yahlulani nina, ukuba bubulungisa na emehlweni kaThixo ukuva nina, kunokuva uThixo;', 'XHO22'),
(7, 4, 20, 'Kaloku thina asinakuyeka ukuthetha ngezinto esizibonileyo nesizivileyo.', 'XHO22'),
(7, 4, 21, 'Bathe ke bona, beqokele ngokubasongela, babandulula, bengafumani sizathu bangababetha ngaso, ngenxa yabantu; ngokuba bonke babemzukisa uThixo ngoko kwakuhlile.', 'XHO22'),
(7, 4, 22, 'Kuba umntu owathi waphiliswa phezu kwakhe lo mqondiso wokuphiliswa wayeneminyaka engaphezu kwamashumi amane.', 'XHO22'),
(7, 4, 23, 'Bakuba ke bekhululwe, baya kowabo, bababikela zonke izinto ababezithethile kubo ababingeleli abakhulu namadoda amakhulu.', 'XHO22'),
(7, 4, 24, 'Bona ke, bakukuva oko, baphakamisa izwi kuThixo ngamxhelo mnye, bathi, Nkosi, wena unguThixo owalenzayo izulu nomhlaba nolwandle, neento zonke ezikwezo nto;', 'XHO22'),
(7, 4, 25, 'owatshoyo ngomlomo kaDavide umkhonzi wakho, ukuthi, Yini na ukuba iintlanga zenze imbuthumbuthu, nabantu bacamange into engeyakonto?', 'XHO22'),
(7, 4, 26, 'Bazimisa ookumkani behlabathi, Nabaphathi bahlanganisana ndawonye Ngokuyichasa iNkosi, naye uKristu wayo.', 'XHO22'),
(7, 4, 27, 'Kuba okunene uHerode, kwanoPontiyo Pilato, ndawonye neentlanga nabantu bakwaSirayeli, babuthelana ndawonye nxamnye noNyana wakho ongcwele uYesu, owamthambisayo,', 'XHO22'),
(7, 4, 28, 'ukuba benze izinto zonke ezamiswayo ngenxa engaphambili sisandla sakho nalicebo lakho, ukuba zibekho.', 'XHO22'),
(7, 4, 29, 'Ngoku ke, Nkosi, zikhangele izisongelo zabo, ubanike abakhonzi bakho ukuba balithethe ilizwi lakho ngako konke ukungafihlisi.', 'XHO22'),
(7, 4, 30, 'Ngokusolula isandla sakho uphilise; nokuba kwenzeke imiqondiso nezimanga ngegama loMkhonzi wakho ongcwele, uYesu.', 'XHO22'),
(7, 4, 31, 'Bakubon’ ukuba bakhungile, yazanyazanyiswa indawo ababehlanganisene kuyo; Bazaliswa bonke nguMoya oyiNgcwele, balithetha ilizwi likaThixo ngokungafihlisiyo.', 'XHO22'),
(7, 4, 32, 'Ke kaloku inkitha yababekholiwe yayintliziyo-nye, imphefumlo mnye; kwaye kungekho namnye ubesithi kukho nto iyeyakhe empahleni yakhe; baye badlelana zonke izinto.', 'XHO22'),
(7, 4, 33, 'Baye abapostile bekungqinela ngamandla amakhulu ukuvuka kweNkosi uYesu; kwaye kukho ukubabalwa okukhulu kubo bonke.', 'XHO22'),
(7, 4, 34, 'Kwakungekho namnye phakathi kwabo, wayeswele; kuba bonke ababenemihlaba, nokuba zizindlu, babethengisa ngazo, baze bazise amaxabiso ezinto ezithengisiweyo,', 'XHO22'),
(7, 4, 35, 'bawabeke ezinyaweni zabapostile; waye ke elowo esabelwa njengoko ebesukuba eswele ngako.', 'XHO22'),
(7, 4, 36, 'noYose, owathiywa ngabapostile ukuba nguBharnabhas, (oko kukuthi ngentetho evakalayo, unyana wentuthuzelo), umLevi waseSipro,', 'XHO22'),
(7, 4, 37, 'Enomhlaba, wathengisa ngawo, wayizisa imali leyo, wayibeka ezinyaweni zabapostile.', 'XHO22'),
(7, 5, 1, 'Ke kaloku indoda ethile, uHananiya, inoSafira umkayo, yathengisa ngendawo.', 'XHO22'),
(7, 5, 2, 'Yashinta kulo ixabiso layo, esazi naye umkayo; yazisa nxenye ithile, yayibeka ezinyaweni zabapostile.', 'XHO22'),
(7, 5, 3, 'Uthe ke uPetros, Hananiya, kungani na ukuba uSathana azalise intliziyo yakho, ukuba umxokise uMoya oyiNgcwele, ushinte kulo ixabiso lomhlaba?', 'XHO22'),
(7, 5, 4, 'Xa ihleliyo, ibingeyakho na? Wakuba uyithengisile, ibingabisemandleni akho na? Yini na ukuba uyibeke le nto entliziyweni yakho? Akuxoke ebantwini, uxoke kuThixo.', 'XHO22'),
(7, 5, 5, 'Ewavile ke uHananiya la mazwi, wawa, waphuma umphefumlo; kwabakho ukoyika okukhulu kubo bonke ababezivile ezi zinto.', 'XHO22'),
(7, 5, 6, 'Besuka abafana, bamsongela, bamthwalela phandle, bamngcwaba.', 'XHO22'),
(7, 5, 7, 'Kwakuba kuhambe iiyure ezintathu, wathi umkakhe, engayazi loo nto ihlileyo, wangena.', 'XHO22'),
(7, 5, 8, 'Wamphendula ke uPetros wathi, Ndixelele, umhlaba lo nawuthengisayo ngale ndawo na? Wathi ke yena, Ewe, ngako oko.', 'XHO22'),
(7, 5, 9, 'Uthe ke uPetros kuye, Yini na ukuba nivumelane ngokumlinga uMoya weNkosi? Uyabona, iinyawo zabo bangcwabe indoda yakho zisemnyango, baya kukuthwalela phandle.', 'XHO22'),
(7, 5, 10, 'Wawa phantsi kwangoko ezinyaweni zakhe, waphuma umphefumlo. Bangena abafana, bamfumana efile, bamkhuphela phandle, bamngcwaba ngasendodeni yakhe.', 'XHO22'),
(7, 5, 11, 'Kwabakho ukoyika okukhulu kulo lonke ibandla, nabo bonke ababeziva ezi zinto.', 'XHO22'),
(7, 5, 12, 'Kwaye kwenzeka ngezandla zabapostile imiqondiso nezimanga ezininzi phakathi kwabantu; baye bonke bemxhelo mnye evarandeni kaSolomon.', 'XHO22'),
(7, 5, 13, 'Ke kaloku, kwabanye akubangakho namnye waba nabuganga bakusinamathelisa kubo;', 'XHO22'),
(7, 5, 14, 'Kwaba kukhona bekongezwa abakholwayo kwiNkosi, into eninzi yamadoda neyabafazi;', 'XHO22'),
(7, 5, 15, 'bada bakhutshelwa phandle imilwelwe ezitratweni, bayibeka ezingqengqelweni nasezingqengqelweni, ukuze kuthi, akubon’ ukuba uPetros uyeza, kubekho kuyo sisithunzi sakhe;', 'XHO22'),
(7, 5, 16, 'Yaye ke ibuthelana eYerusalem nenkitha yabemizi ejikelezileyo, ithwele imilwelwe nabakhathazwa ngoomoya abangcolileyo, baye bephiliswa bonke.', 'XHO22'),
(7, 5, 17, 'Waza wesuka umbingeleli omkhulu nabo bonke ababenaye (abangabehlelo labaSadusi), bazala ngumsindo;', 'XHO22'),
(7, 5, 18, 'bababamba abapostile, babafaka entolongweni.', 'XHO22'),
(7, 5, 19, 'Sazivula ke isithunywa seNkosi ebusuku iingcango zentolongo, sabakhupha, sathi,', 'XHO22'),
(7, 5, 20, 'Hambani, niye kuma nithethe etempileni kubo abantu onke amazwi obu bomi.', 'XHO22'),
(7, 5, 21, 'Bathe ke bona bakukuva oko, bangena kwakusasa etempileni, bafundisa. Wafika ke umbingeleli omkhulu nabo babenaye, wayibizela ndawonye intlanganiso yamatyala, nawo onke amadoda amakhulu akoonyana bakaSirayeli, bathumela entolongweni ukuba baziswe.', 'XHO22'),
(7, 5, 22, 'Ke kaloku, akufika amadindala, akabafumananga entolongweni, abuya abika, athi,', 'XHO22'),
(7, 5, 23, 'Intolongo siyifumene okunene ivalwe ngokuqinileyo kanye, nabalindi bemi ngaphandle phambi kweminyango. Sivulile ke, asifumananga mntu ngaphakathi.', 'XHO22'),
(7, 5, 24, 'Ke kaloku, bakukuziva ezo zinto umbingeleli omkhulu, nomphathi wetempile, kwanababingeleli abakhulu, bathandabuza kubo, ukuba ingaba ingaba ingaba yintoni na le nto.', 'XHO22'),
(7, 5, 25, 'Kwafika omnye wababikela, esithi, Yabonani, amadoda ebeniwafake entolongweni, emi etempileni, efundisa abantu.', 'XHO22'),
(7, 5, 26, 'Wesuka umphathi enamadindala, wabazisa kungengamandla; kuba babesoyika abantu, hleze baxulutywe ngamatye.', 'XHO22'),
(7, 5, 27, 'Bebazisile ke, babamisa entlanganisweni yamatyala; wababuza umbingeleli omkhulu.', 'XHO22'),
(7, 5, 28, 'Esithi, Besinganithethelanga na, ukuba ningafundisi ngeli gama? niyabona, niyizalisile iYerusalem ngemfundiso yenu, nizimisele ukulizisa igazi lalo mntu phezu kwethu.', 'XHO22'),
(7, 5, 29, 'Waphendula ke uPetros nabapostile, bathi, Simelwe kukuthi sive uThixo kunokuba sive abantu.', 'XHO22'),
(7, 5, 30, 'uThixo woobawo bethu wamvusa uYesu, enambulalayo nina ngokumxhoma emthini.', 'XHO22'),
(7, 5, 31, 'Lowo uThixo wamphakamisa ngesandla sakhe sokunene, waba nguMthetheli noMsindisi, ukuze amnike uSirayeli inguquko noxolelo lwezono.', 'XHO22'),
(7, 5, 32, 'Thina ke singamangqina akhe ezo zinto, ukwanjalo noMoya oyiNgcwele, awamnika abo bamvayo yena uThixo.', 'XHO22'),
(7, 5, 33, 'Bathe ke bona bakukuva oko, bahlabeka ezintliziyweni zabo, babhunga ukuba babasike.', 'XHO22'),
(7, 5, 34, 'Ke kaloku kwesuka kwema othile entlanganisweni yamatyala, umFarisi ogama linguGamaliyeli, umfundisi womthetho, obekekileyo kubantu bonke, wathi makhe bakhutshelwe phandle kancinane abapostile;', 'XHO22'),
(7, 5, 35, 'Wathi kubo, Madoda, maSirayeli, zilumkeleni ngento eniza kuyenza kwaba bantu.', 'XHO22'),
(7, 5, 36, 'Kuba ngaphambi kwale mihla kwesuka uTuda, esithi yena ngokwakhe unguthile; ekwathi kwanamathela kuye inani lamadoda angathi kumakhulu mane; bathi bonke ababemlulamele babhangazeka, ababa nto.', 'XHO22'),
(7, 5, 37, 'Emva kwalowo kwesuka uYudas waseGalili ngemihla yobhalo lomlibo wokuzalwa, wakhukulisa abantu banela; naye lowo watshabalala; baza bonke ababemlulamele bachithwachithwa.', 'XHO22'),
(7, 5, 38, 'Kungoku ke ndithi kuni, Khwelelani kwaba bantu nibayeke; ngokuba eli cebo, nalo msebenzi, ukuba lingaba lelabantu, lotshitsha.', 'XHO22'),
(7, 5, 39, 'Ukuba ke lelikaThixo, aninakulitshitshisa; hleze nifunyanwe ningabalwa kwanoThixo.', 'XHO22'),
(7, 5, 40, 'Bamthambela ke; bebabizile abapostile, babatyabula, babathethela ngelithi, mabangathethi ngegama likaYesu; babandulula ke.', 'XHO22'),
(7, 5, 41, 'Bemka ke ngoko bona ebusweni bentlanganiso yamatyala, bevuya, ngokuba kuthiwe bakufanele ukuhlaziswa ngenxa yegama lakhe.', 'XHO22'),
(7, 5, 42, 'Baye bangayeki ukufundisa nokushumayela iindaba ezilungileyo zikaYesu Kristu, imihla yonke etempileni, nasemizini ngemizi.', 'XHO22'),
(7, 6, 1, 'Ke kaloku ngaloo mihla, bakubon’ ukuba bayanda abafundi, kwabakho ukukrokra kwawesiGrike amaYuda, esithi, ngenxa yokuba abahlolokazi bawo babengananzwa kulungiselelo lwemihla ngemihla.', 'XHO22'),
(7, 6, 2, 'Bathe ke abalishumi elinababini bawubizela kubo indimbane yabafundi, bathi, Akukholekile ukuba thina silishiye ilizwi likaThixo, silungiselele ezitafileni.', 'XHO22'),
(7, 6, 3, 'Ngoko ke, bazalwana, khangelani ukujonga phakathi kwenu amadoda asixhenxe avunyiweyo, azele nguMoya oyiNgcwele nobulumko, esingawamisela phezu kwalo mcimbi.', 'XHO22'),
(7, 6, 4, 'Thina ke siya kuzingisa emthandazweni, nakubulungiseleli belizwi.', 'XHO22'),
(7, 6, 5, 'Labakholisa ke ilizwi elo zwi emehlweni abo bonke abantu, baza banyula uStefano, indoda ezele lukholo nanguMoya oyiNgcwele, noFilipu, noProkoro, noNikanore, noTimon, noParmenas, noNikolawo owaguqukela ebuYudeni, wakwa-Antiyokwe;', 'XHO22'),
(7, 6, 6, 'abathe babamisa phambi kwabapostile, bathi, bakuba bethandazile, babeka izandla phezu kwabo.', 'XHO22'),
(7, 6, 7, 'Laye liya likhula ilizwi likaThixo; Laye liya lisanda kakhulu inani labafundi eYerusalem; laye neqela elikhulu lababingeleli lilululamela ukholo.', 'XHO22'),
(7, 6, 8, 'Ke kaloku uStefano, ezele lukholo namandla, wayesenza izimanga nemiqondiso emikhulu phakathi kwabantu.', 'XHO22'),
(7, 6, 9, 'Kwesuka ke kwesuka kwesuka abathile bendlu yesikhungu ebizwa ngokuba yelabaKhululwa, neyabaseKirene, neyamaAleksandriya, neyabaseKilikiya naseAsiya, bephikisana noStefano.', 'XHO22'),
(7, 6, 10, 'Baye ke bengabanga nako ukumelana nobulumko, nomoya awayethetha ngawo.', 'XHO22'),
(7, 6, 11, 'Baza balukuhla amadoda, awathi, Simvile ethetha amazwi okunyelisa ngoMoses nangoThixo.', 'XHO22'),
(7, 6, 12, 'Baza babaxhokonxa abantu, namadoda amakhulu nababhali; bamfikela, bambamba, bamzisa entlanganisweni yamatyala.', 'XHO22'),
(7, 6, 13, 'Bamisa amangqina obuxoki, awathi, Lo mntu akayeki ukuthetha amazwi okunyelisa le ndawo ingcwele nomthetho;', 'XHO22'),
(7, 6, 14, 'Kuba simve esithi, uYesu waseNazarete lo uya kuyichitha le ndawo, awenze wambi amasiko awawanikela kuthi uMoses.', 'XHO22'),
(7, 6, 15, 'Baye bonke ababehleli entlanganisweni yamatyala, beqwalasele kuye, babubona ubuso bakhe, ngathi bubuso besithunywa sezulu.', 'XHO22'),
(7, 7, 1, 'Wathi ke umbingeleli omkhulu, Zinjalo na ke ezi ndawo?', 'XHO22'),
(7, 7, 2, 'Uthe ke yena: “Madoda, bazalwana, nani bobawo, phulaphulani. uThixo wozuko wabonakala kubawo wethu uAbraham, eseMesopotami, engekami kwelakwaHaran.', 'XHO22'),
(7, 7, 3, 'Wathi kuye, Phuma ezweni lakowenu, kwelokuzalwa kwakho, uye ezweni endiya kukubonisa lona.', 'XHO22'),
(7, 7, 4, 'Wesuka ke ezweni lamaKaledi, wahlala eHaran. Wathi, akuba efile uyise, wamfudusela kweli lizwe nihleli kulo nina ngoku.', 'XHO22'),
(7, 7, 5, 'Akamnika lifa khona, kwanendawo engangonyawo, kodwa wathembisa ukuba uya kumnika ibe yeyakhe, nembewu yakhe emva kwakhe, engekabi namntwana.', 'XHO22'),
(7, 7, 6, 'Wenjenje uThixo ukuthetha, wathi, Imbewu yakhe iya kuba ngabaphambukeli ezweni lasemzini; ukuba baye ebukhobokeni, babaphathe kakubi, iminyaka emakhulu mane.', 'XHO22'),
(7, 7, 7, 'Nohlanga olo, ethe yangamakhoboka kulo, ndiya kulugweba mna, watsho uThixo; yaye, emveni kwezi zinto, iya kuphuma indikhonze kule ndawo.', 'XHO22'),
(7, 7, 8, 'Waza wamnika umnqophiso wolwaluko, wathi ngokunjalo uAbraham wamzala uIsake, wamalusa ngomhla wesibhozo; uIsake ke wazala uYakobi; uYakobi wazala oomawokhulu abalishumi elinababini.', 'XHO22'),
(7, 7, 9, 'Bathi oomawokhulu, bemmonele, bathengisa ngoYosefu eYiputa;', 'XHO22'),
(7, 7, 10, 'wamncothula kuzo zonke iimbandezelo zakhe, wambabala ngobulumko phambi koFaro, ukumkani waseYiputa; wammisa ukuba abe yirhuluneli phezu kweYiputa nendlu yakhe yonke.', 'XHO22'),
(7, 7, 11, 'Ke kaloku kwafika indlala enkulu kulo lonke elaseJiputa nelaseKanana, baza oobawo-mkhulu abazange bafumane kudla.', 'XHO22'),
(7, 7, 12, 'Akuva ke uYakobi ukuba kukho ingqolowa eYiputa, wesusa oobawo bethu okokuqala.', 'XHO22'),
(7, 7, 13, 'Kwathi ngokwesibini uYosefu wabonakalaliswa kubazalwana bakhe; Saza ke isizalwana sikaYosefu saziswa kuFaro.', 'XHO22'),
(7, 7, 14, 'Wathumela uYosefu, wambiza uyise uYakobi, nemizalwane yakhe yonke, imiphefumlo emashumi asixhenxe anantlanu.', 'XHO22'),
(7, 7, 15, 'Wehla ke uYakobi, waya eYiputa, wabhubha yena noobawo bethu;', 'XHO22'),
(7, 7, 16, 'baza bathwalwa basiwa kwaShekem, babekwa engcwabeni awalithengayo uAbraham ngexabiso lemali koonyana bakaHamore, usoShekem.', 'XHO22'),
(7, 7, 17, 'Kuthe ke, lakuba lisondele ixesha ledinga, awalifungayo uThixo kuAbraham, bakhula abantu, banda eYiputa,', 'XHO22'),
(7, 7, 18, 'kwada kwavela kumkani wumbi, ubengamazi uYosefu.', 'XHO22'),
(7, 7, 19, 'Lowo wabalumkisa abakowethu, wabaphatha kakubi oobawo bethu, ngokokuze bazilahle iintsana zabo, ukuze zingabudli ubomi.', 'XHO22'),
(7, 7, 20, 'Ekuthe ke kwelo xesha, kwazalwa uMoses; waye emhle kuThixo, wondliwa iinyanga zantathu endlwini kayise.', 'XHO22'),
(7, 7, 21, 'Wagxothwa ke, yamchola intombi kaFaro, yazondlela ukuba abe ngunyana wayo.', 'XHO22'),
(7, 7, 22, 'Waqeqeshwa ke uMoses kubo bonke ubulumko bamaYiputa; waye ke eyinkunkqele ngamazwi nangezenzo.', 'XHO22'),
(7, 7, 23, 'Kwathi, kwakuba yiminyaka emashumi mane, kwafika entliziyweni yakhe ukukha aye kuvelela abazalwana bakhe, oonyana bakaSirayeli.', 'XHO22'),
(7, 7, 24, 'Waza wabona omnye ephathwa kakubi, wamkhusela, wamphindezelela lowo ucinezelweyo, wambulala umYiputa.', 'XHO22'),
(7, 7, 25, 'Wayeba ke yena boqonda abazalwana bakhe ukuba uThixo ebeya kubasindisa ngesandla sakhe; abaqondanga ke bona.', 'XHO22'),
(7, 7, 26, 'Kwathi ngengomso wabonakala kubo besilwa, wabanyanzela ukuba baxolisane, esithi, Madoda, niyazalana nina; yini na ukuba nonane?', 'XHO22'),
(7, 7, 27, 'Kodwa yena lowo wayemonile ummelwane wakhe wamgiba, esithi, Umiswe ngubani na ukuba ube ngumphathi nomahluli phakathi kwethu?', 'XHO22'),
(7, 7, 28, 'Uza kundibulala na nam, njengokuba umbulele umYiputa izolo?', 'XHO22'),
(7, 7, 29, 'Wasaba ke uMoses ngelo lizwi, waba ngumphambukeli ezweni lakwaMidiyan, apho wazalela khona oonyana ababini.', 'XHO22'),
(7, 7, 30, 'Kuthe ke kwakuzaliseka iminyaka emashumi mane, kwabonakala kuye kwintlango yentaba yeSinayi isithunywa seNkosi, elangatyeni lomlilo etyholweni.', 'XHO22'),
(7, 7, 31, 'Akukubona oko uMoses, wamangaliswa ngumbono lowo; akusondela ukuba awukhangele, kwafika ilizwi likaYehova kuye, lisithi,', 'XHO22'),
(7, 7, 32, 'Mna ndinguThixo wabo ooyihlo, uThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi. Wagubha uMoses, akaba nabuganga, akukhangela.', 'XHO22'),
(7, 7, 33, 'Ithe ke iNkosi kuye, Khulula iimbadada zakho ezinyaweni zakho; kuba indawo leyo umi kuyo ingumhlaba ongcwele.', 'XHO22'),
(7, 7, 34, 'Ndikubonile ndakubona ukuphathwa kakubi kwabantu bam abaseYiputa, ndakuva nokuncwina kwabo, ndihlile, ukuze ndibahlangule. Yiza ngoku, ndikuthume eYiputa.', 'XHO22'),
(7, 7, 35, 'Lo Moses bamkhanyelayo, besithi, Umiswe ngubani na, ukuba ube ngumphathi nomahluli? yena uThixo wamthuma ukuba abe ngumphathi nomhlanguli ngesandla sesithunywa esabonakalayo kuye etyholweni.', 'XHO22'),
(7, 7, 36, 'Yena lowo wabakhupha, emveni kokuba wenze izimanga nemiqondiso ezweni laseYiputa, naseLwandle oluBomvu, nasentlango, iminyaka emashumi mane.', 'XHO22'),
(7, 7, 37, 'Lo nguloo Moses wathi koonyana bakaSirayeli, iNkosi, uThixo wenu, iya kunivelisela umprofeti kubazalwana benu, onjengam nje; nize nimve yena.', 'XHO22'),
(7, 7, 38, 'Nguye lo owayesebandleni entlango, ndawonye nesithunywa sezulu, ebesithetha naye entabeni yeSinayi, enoobawo bethu;', 'XHO22'),
(7, 7, 39, 'Abavumanga ukumphulaphula oobawo, besuka bamgxotha, babuyela eYiputa ezintliziyweni zabo;', 'XHO22'),
(7, 7, 40, 'besithi kuAron, Senzele uthixo oya kuhamba phambi kwethu; kuba lo Moses wasikhuphayo ezweni laseYiputa, asazi ukuba uhlelwe yintoni na.', 'XHO22'),
(7, 7, 41, 'Benza ithole ngaloo mihla, benyusa amadini, bewanyusa amadini, baba nemihlali yimisebenzi yezandla zabo.', 'XHO22'),
(7, 7, 42, 'Wajika ke uThixo, wabanikela ukuba bakhonze umkhosi wezulu; njengoko kubhaliweyo encwadini yabaprofeti kwathiwa, Nibe nisondeza kum na kum amadini amadini, ndlu kaSirayeli, iminyaka emashumi mane entlango?', 'XHO22'),
(7, 7, 43, 'Nathwala umnquba kaMoleki, Nenkwenkwezi yothixo wenu uRemfan, Imifanekiso ke leyo enayenzayo ukuba niqubude kuyo; Ndaye ke ndiya kunifudusela ngaphaya kweBhabheli.', 'XHO22'),
(7, 7, 44, 'Umnquba wesingqino wawukoobawo bethu entlango, njengoko wayemisele ngako owayethetha noMoses, ukuba awenze ngokomfanekiso abewubonile;', 'XHO22'),
(7, 7, 45, 'Lowo bawungenisayo, bawungenisayo noYesu ekuthinjweni kweentlanga, ezo wazigxothayo uThixo ebusweni boobawo bethu, kwada kwayimihla kaDavide;', 'XHO22'),
(7, 7, 46, 'owababalwayo nguThixo, wacela ukuba amfumanele uThixo kaYakobi umnquba.', 'XHO22'),
(7, 7, 47, 'Kodwa uSolomon wamakhela indlu.', 'XHO22'),
(7, 7, 48, 'Kodwa Osenyangweni yena akahlali zitempileni zenziwe ngazandla; njengoko watshoyo umprofeti.', 'XHO22'),
(7, 7, 49, 'Izulu liyitrone yam, Ke ihlabathi sisihlalo seenyawo zam; Yindlu enjani na eniya kundakhela yona? itsho iNkosi; Yiyiphi na yona indawo yokuphumla kwam?', 'XHO22'),
(7, 7, 50, 'Asisandla sam na sazenza zonke ezo zinto?', 'XHO22'),
(7, 7, 51, 'Nina bantamo zilukhuni, bangalukileyo ngentliziyo, nangendlebe, nihleli nibambene noMoya oyiNgcwele; kwanjengoko ooyihlo, nani ninjalo.', 'XHO22'),
(7, 7, 52, 'Kubo abaprofeti nguwuphi na ongatshutshiswanga ngooyihlo? bababulala abo babekuxela ngenxa engaphambili ukuza koliLungisa; enithe ke ngoku ningabangcatshi nababulali bakhe;', 'XHO22'),
(7, 7, 53, 'nina nawamkelayo umthetho, niwumiselwa zizithunywa zezulu, anawugcina.', 'XHO22'),
(7, 7, 54, 'Ke kaloku, bathi, bakukuziva ezi zinto, bahlabeka ezintliziyweni zabo, bamtshixizela amazinyo.', 'XHO22'),
(7, 7, 55, 'Ke yena, ezele nguMoya oyiNgcwele, waqwalasela ezulwini, wabona ubuqaqawuli bukaThixo, noYesu emi ngasekunene kukaThixo.', 'XHO22'),
(7, 7, 56, 'Wathi, Yabonani, ndiwabona amazulu evulekile, ndimbona uNyana woMntu emi ngasekunene kukaThixo.', 'XHO22'),
(7, 7, 57, 'Badanduluka ke ngezwi elikhulu, bazivingca iindlebe zabo, besuka bemka phezu kwakhe ngamxhelo mnye,', 'XHO22'),
(7, 7, 58, 'bamkhuphela ngaphandle komzi, bamgibisela ngamatye. Amangqina azibeka iingubo zawo ezinyaweni zendodana egama linguSawule.', 'XHO22'),
(7, 7, 59, 'Ayemgibisela ngamatye uStefano, yena ebiza esithi, Nkosi Yesu, wamkele umoya wam.', 'XHO22'),
(7, 7, 60, 'Waguqa ngamadolo, wadanduluka ngezwi elikhulu, esithi, Nkosi, musa ukubabeka tyala ngesi sono. Akutsho walala ubuthongo.', 'XHO22'),
(7, 8, 1, 'Waye ke uSawule ekuthakazela ukufa kwakhe. Ke kaloku kwehla ngelo xesha intshutshiso enkulu phezu kwalo ibandla eliseYerusalem; baye bonke baba ziintsali kulo lonke ilizwe lakwaYuda nelaseSamariya, kwasala abapostile bodwa.', 'XHO22'),
(7, 8, 2, 'Aza amadoda amoyikayo uThixo amngcwaba uStefano, amenzela isijwili esikhulu.', 'XHO22'),
(7, 8, 3, 'Waye ke uSawule elidlavula ibandla, engena kwizindlu ngezindlu, erhola amadoda kwanabafazi, ebafaka entolongweni.', 'XHO22'),
(7, 8, 4, 'Bona ke ngoko ababeziintsali batyhutyha ilizwe, beshumayela iindaba ezilungileyo zelizwi.', 'XHO22'),
(7, 8, 5, 'Wehla ke uFilipu, waya emzini welaseSamariya, wamvakalisa kubo uKristu.', 'XHO22'),
(7, 8, 6, 'Zaye izihlwele zizinyamekela ngamxhelo mnye izinto ezibe zithethwa nguFilipu, ekuyiveni nasekuyiboneni kwazo imiqondiso abeyenza.', 'XHO22'),
(7, 8, 7, 'Kuba oomoya abangcolileyo babephuma kwinto eninzi ebinabo, bebhomboloza ngezwi elikhulu; yathi ke into eninzi enedumbe neziziqhwala yaphiliswa.', 'XHO22'),
(7, 8, 8, 'Kwabakho uvuyo olukhulu kuloo mzi.', 'XHO22'),
(7, 8, 9, 'Ke kaloku bekukho ndoda ithile, kuthiwa nguSimon, eyayifudula ithakatha kuloo mzi, ibetha abantu baseSamariya, isithi yona ngokwayo inguthile omkhulu;', 'XHO22'),
(7, 8, 10, 'ababeyiphulaphula bonke, bethabathela komncinane besa komkhulu, besithi, Lo ungamandla kaThixo amakhulu.', 'XHO22'),
(7, 8, 11, 'Baye ke bemphulaphula, ngenxa yokuba ixesha elide wabetha bee nqa ngobunyangi bakhe.', 'XHO22'),
(7, 8, 12, 'Xenikweni ke bakholwayo nguFilipu, eshumayela iindaba ezilungileyo zezinto zobukumkani bukaThixo, nezegama likaYesu Kristu, babhaptizwa amadoda kwanabafazi.', 'XHO22'),
(7, 8, 13, 'Naye uSimon wakholwa, waza akuba ebhaptiziwe, wahlala enoFilipu. Wamangaliswa yimimangaliso nemiqondiso eyayisenzeka.', 'XHO22'),
(7, 8, 14, 'Ke kaloku, bakuva abapostile ababeseYerusalem ukuba abaseSamariya balamkele ilizwi likaThixo, bathumela kubo uPetros noYohane;', 'XHO22'),
(7, 8, 15, 'abathi bona bakuhla, babathandazela, ukuba bamkele uMoya oyiNgcwele;', 'XHO22'),
(7, 8, 16, '(Kuba ebengekaweli namnye kubo; babebhaptizwe nje egameni leNkosi uYesu.)', 'XHO22'),
(7, 8, 17, 'Baza bababeka izandla, bamamkela ke uMoya oyiNgcwele.', 'XHO22'),
(7, 8, 18, 'Uthe ke uSimon, akubona ukuba ngokubekwa kwezandla zabapostile uyanikwa uMoya oyiNgcwele, wezisa kubo imali,', 'XHO22'),
(7, 8, 19, 'esithi, Ndinikeni nam eli gunya, ukuze lowo, ndithe ndazibeka izandla phezu kwakhe, amkele uMoya oyiNgcwele.', 'XHO22'),
(7, 8, 20, 'Uthe ke uPetros kuye, Isilivere yakho mayiye kutshabalala nawe; ngokuba ubusiba isipho sikaThixo singazuzwa ngemali.', 'XHO22'),
(7, 8, 21, 'Akunasabelo naqashiso kulo mbandela, kuba intliziyo yakho ayithe tye emehlweni kaThixo.', 'XHO22'),
(7, 8, 22, 'Guquka ngoko kobo bubi bakho, ukhunge kuThixo, ukuba ingathi mhlawumbi ixolelwe ingcingane yentliziyo yakho.', 'XHO22'),
(7, 8, 23, 'Kuba ndiyabona ukuba usenyongweni yobukrakra, nasentanjeni yentswela-bulungisa.', 'XHO22'),
(7, 8, 24, 'Waphendula ke uSimon wathi, Ndikhungeleni nina eNkosini, ukuze ndingahlelwa nanye yezo zinto nizithethileyo.', 'XHO22'),
(7, 8, 25, 'Bona ke, bakuba beqononondisile, belithetha ilizwi leNkosi, babuya beza eYerusalem; kanjalo imizi emininzi yelaseSamariya bayishumayeza iindaba ezilungileyo.', 'XHO22'),
(7, 8, 26, 'Ke kaloku kwathetha isithunywa seNkosi kuFilipu, sisithi, Suka usinge ezantsi, ngendlela ehla ivela eYerusalem, eya eGaza, yona iyintlango.', 'XHO22'),
(7, 8, 27, 'Wesuka ke, wahamba, kwaye kukho indoda engumTiyopiya, ithenwa, igunya likaKandase ukumkanikazi wamaTiyopiya, eliphethe bonke ubutyebi bakhe, elibe lize kunqula eYerusalem;', 'XHO22'),
(7, 8, 28, 'Yayibuya ke, ihleli enqwelweni yayo, ilesa incwadi yomprofeti uIsaya.', 'XHO22'),
(7, 8, 29, 'Uthe ke uMoya kuFilipu, Yiya ukhwele kule nqwelo.', 'XHO22'),
(7, 8, 30, 'Wabaleka ke uFilipu, waya kuyo, wayiva ilesa incwadi yomprofeti uIsaya, wathi, Uyaziqonda na kodwa izinto ozilesayo?', 'XHO22'),
(7, 8, 31, 'Ithe ke yona, Ndingaba nako ngani na, kungekho undikhokelayo nje? Kanjalo yamcela uFilipu ukuba akhwele ahlale nayo.', 'XHO22'),
(7, 8, 32, 'Ke kaloku indawo yesibhalo eyayilesa yona ibisithi, Njengemvu esiwa ekuxhelweni; nanjengemvana esisimumu phambi komchebi wayo, wenjenjalo ukungawuvuli umlomo wakhe.', 'XHO22'),
(7, 8, 33, 'Ekuthotyweni kwakhe umgwebo wakhe wasuswa. Ngubani na ke owosixela isizukulwana sakhe? ngokuba buyasuswa ubomi bakhe emhlabeni.', 'XHO22'),
(7, 8, 34, 'Laphendula ke ithenwa lathi kuFilipu, Ndiyakukhunga, umprofeti lo uyithetha ngabani na le nto; Ithetha yena, okanye mntu wumbi?', 'XHO22'),
(7, 8, 35, 'Wawuvula ke uFilipu umlomo wakhe, eqalela kweso sibhalo, walishumayeza iindaba ezilungileyo zikaYesu.', 'XHO22'),
(7, 8, 36, 'Ke kaloku, xa babehamba ngendlela, bafika manzini athile; lathi ithenwa, Nanga amanzi; Kwala ntoni na ukuba ndingabhaptizwa?', 'XHO22'),
(7, 8, 37, 'Uthe ke uFilipu, Ukuba uyakholwa ngentliziyo yakho iphela, kuvumelekile. Waphendula ke wathi, Ndiyakholwa ukuba uYesu Kristu unguye uNyana kaThixo.', 'XHO22'),
(7, 8, 38, 'Lathi mayime inqwelo; behla bobabini, baya emanzini, uFilipu kwanethenwa; wambhaptiza.', 'XHO22'),
(7, 8, 39, 'Xeshikweni ke babenyukile, baphuma emanzini, uMoya weNkosi wamthi xwi uFilipu; alaba sambona ithenwa, kuba lalihamba ngeyalo indlela livuya.', 'XHO22'),
(7, 8, 40, 'uFilipu ke wafunyanwa eAzoto, wayityhutyha imizi yonke, wayishumayeza iindaba ezilungileyo, wada wafika eKesareya.', 'XHO22'),
(7, 9, 1, 'Ke kaloku uSawule, esabafuthela ngezigrogriso nokubabulala abafundi beNkosi, waya kumbingeleli omkhulu.', 'XHO22'),
(7, 9, 2, 'Wacela kuye iincwadi zokuya eDamasko ezindlwini zesikhungu, ukuze athi, ethe wafumana abathile bale Ndlela, amadoda kwanabafazi, abazise bekhonkxiwe eYerusalem.', 'XHO22'),
(7, 9, 3, 'Kuthe ke ekuhambeni kwakhe, wasondela eDamasko; waza ngesiquphe wabanekelwa ngeenxa zonke lukhanyiso oluvela ezulwini.', 'XHO22'),
(7, 9, 4, 'Wawa emhlabeni, weva ilizwi lisithi kuye, Sawule, Sawule, unditshutshiselani na?', 'XHO22'),
(7, 9, 5, 'Wathi ke yena, Ungubani na, Nkosi? Ithe ke iNkosi, Mna ndinguye uYesu omtshutshisayo wena; wenzakala ukuba ukhabe iimviko nje.', 'XHO22'),
(7, 9, 6, 'Waza egubha, eqhiphuke umbilini, wathi, Nkosi, ufuna ukuba ndenze ntoni na? Ithe ke iNkosi kuye, Vuka, uye kuwo umzi; woyixelelwa into omelwe kukuyenza.', 'XHO22'),
(7, 9, 7, 'Ke kaloku amadoda abehamba naye ema engenakuthetha, eliva okunene lona izwi, kodwa engaboni mntu.', 'XHO22'),
(7, 9, 8, 'Wavuka uSawule emhlabeni; Uthe ke avuleka amehlo, akabona mntu; bamrhola ke ngesandla, bamngenisa eDamasko.', 'XHO22'),
(7, 9, 9, 'Kwaba ntathu iintsuku engaboni, engadli, engaseli.', 'XHO22'),
(7, 9, 10, 'Ke kaloku bekukho mfundi uthile eDamasko, ugama linguHananiya; yathi iNkosi kuye embonweni, Hananiya! Wathi ke yena, Ndilapha, Nkosi.', 'XHO22'),
(7, 9, 11, 'Ithe ke iNkosi kuye, Phakama, uye esitratweni ekuthiwa sesithe Tye, ubuzise endlwini kaYuda lowo kuthiwa nguSawule, waseTarso; kuba nanko ethandaza;', 'XHO22'),
(7, 9, 12, 'Ubone embonweni indoda egama linguHananiya, ingena, imbeka isandla, ukuze abuye abone.', 'XHO22'),
(7, 9, 13, 'Waphendula ke uHananiya wathi, Nkosi, ndivile kwabaninzi ngale ndoda, ukuba zininzi kwazo izinto ezimbi ezenze kwabangcwele bakho eYerusalem;', 'XHO22'),
(7, 9, 14, 'Nalapha, inegunya kubabingeleli abakhulu lokukhonkxa bonke abo balinqulayo igama lakho.', 'XHO22'),
(7, 9, 15, 'Yathi ke kuye iNkosi, Hamba uhambe; ngokuba lowo usisitya esinyuliweyo kum, sokuphatha igama lam phambi kweentlanga, nookumkani, noonyana bakaSirayeli;', 'XHO22'),
(7, 9, 16, 'kuba mna ndiya kumbonisa ukuba zinkulu kwezinto ezinzima, amelwe kukubandezeleka ngazo ngenxa yegama lam.', 'XHO22'),
(7, 9, 17, 'Wemka ke uHananiya, waya kungena endlwini; Wabeka izandla phezu kwakhe, wathi, Sawule, mzalwana, indithumile iNkosi uYesu, ebonakeleyo kuwe endleleni oze ngayo, ukuze ubuye ubuye ubone, uzaliswe nguMoya oyiNgcwele.', 'XHO22'),
(7, 9, 18, 'Kwangoko kwawa emehlweni akhe okungamaxolo, wabuya wabona kwaoko, wavuka, wabhaptizwa.', 'XHO22'),
(7, 9, 19, 'Ekudleni kwakhe, womelela. Ke kaloku uSawule waba neentsuku ezithile nabafundi baseDamasko.', 'XHO22'),
(7, 9, 20, 'Waye kwaoko, ezindlwini zesikhungu, emvakalisa uYesu, ukuba lowo unguye uNyana kaThixo.', 'XHO22'),
(7, 9, 21, 'Bathi nqa ke bonke ababemva, bathi; Lo asinguye na owababhuqa eYerusalem abo babelinqula eli gama, weza nalapha ngenjongo, ukuba abase bekhonkxiwe kubabingeleli abakhulu?', 'XHO22'),
(7, 9, 22, 'Kwaba kukhona womelelayo uSawule; wabetha adubadubeka amaYuda abemi eDamasko, ebonakalalisa ukuba lo nguye uKristu.', 'XHO22'),
(7, 9, 23, 'Ke kaloku, kwakuzaliseka imihla eyaneleyo, abhunga amaYuda ukuba amsike.', 'XHO22'),
(7, 9, 24, 'Kodwa iyelenqe lawo laziwa nguSawule. Awagcina ke namasango imini nobusuku, ukuba amsike.', 'XHO22'),
(7, 9, 25, 'Bamthabatha ke abafundi ebusuku, bamhlisa ngodonga, bemthoba ngengobozi.', 'XHO22'),
(7, 9, 26, 'Ke kaloku, akufika uSawule eYerusalem, wamana elinga ukuzibandakanya nabafundi; kodwa bamoyika bonke, bengakholwa ukuba ungumfundi.', 'XHO22'),
(7, 9, 27, 'uBharnabhas ke wamthabatha wamsa kubapostile, wabacacisela ukuyibona kwakhe iNkosi endleleni, nokuba yathetha naye, nokuthetha kwakhe ngokungafihlisiyo eDamasko egameni likaYesu.', 'XHO22'),
(7, 9, 28, 'Waye enabo ke, engena ephuma eYerusalem.', 'XHO22'),
(7, 9, 29, 'Waza wathetha ngokungafihlisiyo egameni leNkosi uYesu, ephikisana namaYuda amaGrike. Babefuna ukumbulala.', 'XHO22'),
(7, 9, 30, 'Bekwazile ke oko abazalwana, bamhlisela eKesareya, bamthumela eTarso.', 'XHO22'),
(7, 9, 31, 'Laye ke ngoko ibandla kulo lonke elakwaYuda, nelaseGalili, nelaseSamariya, linoxolo, lisakheka. Baye behamba beyoyika iNkosi, besanda ngentuthuzelo yoMoya oyiNgcwele.', 'XHO22'),
(7, 9, 32, 'Ke kaloku, akuba uPetros etyhutyha iindawo zonke, wehla weza nakwabangcwele ababemi eLida.', 'XHO22'),
(7, 9, 33, 'Wafumana ke khona umntu othile, ogama linguEneya, eminyaka isibhozo elele elukhukweni enedumbe.', 'XHO22'),
(7, 9, 34, 'Wathi ke uPetros kuye, Eneya, uyakuphilisa uYesu Kristu; vuka, wandlule. Wavuka kwangoko.', 'XHO22'),
(7, 9, 35, 'Bambona bonke abemiyo eLida naseSaron, baguqukela eNkosini.', 'XHO22'),
(7, 9, 36, 'Ke kaloku, eYopa kwakukho mfundikazi uthile, ugama linguTabhita, ekuthiwa ngentetho evakalayo nguDorkas;', 'XHO22'),
(7, 9, 37, 'Kwathi ke ngaloo mihla, wahliwa sisifo, wafa; bathe ke bakuba bemhlambile, bambeka egumbini eliphezulu.', 'XHO22'),
(7, 9, 38, 'Ke kaloku iLida ibikufuphi nje neYopa; bathi abafundi, bevile ukuba uPetros ukhona, bathuma amadoda amabini kuye, bembongoza ukuba eze kubo, angalibali.', 'XHO22'),
(7, 9, 39, 'Wesuka ke uPetros wahamba nabo. Efikile, bamsa egumbini eliphezulu; bema ngakuye bonke abahlolokazi, belila, bembonisa iingubo zangaphantsi nezokwaleka abezenza uDorkas oko ebenabo.', 'XHO22'),
(7, 9, 40, 'uPetros wabakhuphela phandle bonke, waguqa wathandaza; wamjikela emzimbeni, wathi, Tabhita, vuka. Yawavula ke amehlo ayo, yathi yakumbona uPetros, yavuka yathi nkqo.', 'XHO22'),
(7, 9, 41, 'Waza wambamba ngesandla, wamphakamisa, wababiza abangcwele nabahlolokazi, wammisa phambi kwabo ehleli.', 'XHO22'),
(7, 9, 42, 'Kwazeka oko kuyo yonke iYopa; bakholwa eNkosini into eninzi.', 'XHO22'),
(7, 9, 43, 'Uthe ke wahlala imihla emininzi eYopa kunye noSimon uthile, umsuki wezikhumba.', 'XHO22'),
(7, 10, 1, 'Kwakukho ndoda ithile eKesareya, ekuthiwa nguKorneli, umthetheli-khulu webutho elibizwa ngokuba libutho lamaTaliyane,', 'XHO22'),
(7, 10, 2, 'emoyikayo uThixo, kunye nayo yonke indlu yakhe, ibaliza abantu;', 'XHO22'),
(7, 10, 3, 'Yabona embonweni kakuhle, malunga nelixa lesithoba lenini, kufika kuyo isithunywa sikaThixo, sisithi kuyo, Korneli!', 'XHO22'),
(7, 10, 4, 'Eqwalasele ke kuso, wangenelwa kukoyika, wathi, Yintoni na, Nkosi? Uthe ke yena kuye, Imithandazo yakho namalizo akho anyukile, aba sisikhumbuzo phambi koThixo.', 'XHO22'),
(7, 10, 5, 'Thumela ngoku amadoda eYopa, ubize uSimon, ogama limbi linguPetros;', 'XHO22'),
(7, 10, 6, 'yena ulundwendwe lukaSimon, umsuki wezikhumba, ondlu ingaselwandle; yena lowo uya kukuxelela into omelwe kukuyenza.', 'XHO22'),
(7, 10, 7, 'Ke kaloku, sakuba simkile isithunywa eso besithetha noKorneli, wabiza zazibini kwizicaka zakhe zasendlwini, nesoldati elimhlonelayo uThixo kwabehlala ehleli naye;', 'XHO22'),
(7, 10, 8, 'Uthe ke, ebacacisele zonke ezi ndawo, wabathuma eYopa.', 'XHO22'),
(7, 10, 9, 'Ke kaloku ngengomso, bakubon’ ukuba basendleleni bona, besondela kuwo umzi, wenyuka uPetros, waya phezu kwendlu, malunga nelixa lesithandathu, ukuba athandaze.', 'XHO22'),
(7, 10, 10, 'Wesuka walamba kakhulu, wanga angakhe adle;', 'XHO22'),
(7, 10, 11, 'Walibona izulu livulekile, kusihla kuye isitya esithile, ngathi yilinen enkulu ebotshwe ngamasondo omane, sithotyelwa emhlabeni.', 'XHO22'),
(7, 10, 12, 'Ekwaye kukho kuzo zonke izinto zomhlaba ezizitho zine, namarhamncwa, nezinambuzane, neentaka zezulu.', 'XHO22'),
(7, 10, 13, 'Kwabakho nezwi kuye lisithi, Vuka, Petros; xhela, udle.', 'XHO22'),
(7, 10, 14, 'Uthe ke uPetros, Hayi, Nkosi; kuba yonke into eyinqambi, nokuba yengcolileyo, andizanga ndiyidle.', 'XHO22'),
(7, 10, 15, 'Labuya ilizwi okwesibini lisithi kuye, Izinto azihlambululeyo uThixo, musa ukuzenza iinqambi wena.', 'XHO22'),
(7, 10, 16, 'Kwabakho ke oku kathathu; sabuya isitya eso senyuswa, sasiwa ezulwini.', 'XHO22'),
(7, 10, 17, 'Ke kaloku, xa uPetros wayethingaza ngaphakathi kwakhe, ukuba ungaba uyintoni na umbono lowo awubonileyo, kwasuka kwathi gqi amadoda abethunyiwe evela kuKorneli, ebuza ngendlu kaSimon, emi esangweni.', 'XHO22'),
(7, 10, 18, 'Akhwaza ebuza ukuba uSimon, ogama limbi linguPetros, ulundwendwe apha na.', 'XHO22'),
(7, 10, 19, 'Ke kaloku, akubon’ ukuba uPetros uyacinga ngawo umbono lowo, wathi uMoya kuye, Nanga amadoda amathathu ekufuna;', 'XHO22'),
(7, 10, 20, 'Vuka ngoko, uhle, uhambe nawo, ungathandabuzi nto, kuba athunywe ndim.', 'XHO22'),
(7, 10, 21, 'Wehla ke uPetros, waya kuwo amadoda abethunyiwe kuye evela kuKorneli; wathi, Niyabona, ndingulowo nimfunayo;', 'XHO22'),
(7, 10, 22, 'Bathe ke bona, uKorneli umthetheli-khulu, indoda elilungisa, emoyikayo uThixo, engqinelwe kakuhle lulo lonke uhlanga lwamaYuda, uhlatyelwe nguThixo ngesithunywa esingcwele, ukuba akuthumele endlwini yakhe, eve amazwi alungileyo, wena.', 'XHO22'),
(7, 10, 23, 'Wababizela phakathi ngoko, aba lundwendwe lwakhe. Ngengomso uPetros wemka nawo, nabazalwana abathile baseYopa bahamba naye.', 'XHO22'),
(7, 10, 24, 'Ngengomsomnye bangena eKesareya. Waye ke uKorneli ebalindile; wayebizele ndawonye imizalwane yakhe, nezihlobo ezisondeleyo.', 'XHO22'),
(7, 10, 25, 'Uthe ke akungena uPetros, uKorneli wamhlangabeza, wawa ezinyaweni zakhe, waqubuda kuye.', 'XHO22'),
(7, 10, 26, 'uPetros ke wamvusa, esithi: “Phakama! nam ngokwam ndingumntu.', 'XHO22'),
(7, 10, 27, 'Wangena ethetha naye, wabafumana bebuthelene bebaninzi;', 'XHO22'),
(7, 10, 28, 'Uthe ke kubo, Niyazi kakuhle nina, ukuba akusesikweni ukuthi indoda engumYuda inamathele isondele kowolunye uhlanga; ke yena uThixo undibonisile, ukuba ndingabizi mntu ngokuthi uyinqambi, ungongcolileyo.', 'XHO22'),
(7, 10, 29, 'Kungoko ke ndizileyo ndingaphikanga ndakubizwa. Ndiyabuza ngoko, ukuba nindibize ngandawoni na?', 'XHO22'),
(7, 10, 30, 'uKorneli wathi, Kuse kuntsuku ne ndizila ukudla, kwada kwaleli lixa lemini. Ngelixa lesithoba ke bendithandaza endlwini yam, kwesuka kwathi gqi indoda, yema phambi kwam, inengubo eqaqambileyo;', 'XHO22'),
(7, 10, 31, 'Yathi, Korneli, umthandazo wakho uviwe, namalizo akho akhunjulwe phambi koThixo.', 'XHO22'),
(7, 10, 32, 'Thumela ngoko eYopa, ubize eze uSimon, ogama limbi linguPetros; yena ulundwendwe endlwini kaSimon, umsuki wezikhumba ngaselwandle; oya kuthi akufika athethe nawe.', 'XHO22'),
(7, 10, 33, 'Ndithumele ngoko kwaoko kuwe; wenze kakuhle ngokufika kwakho. Ke ngoko sikho sonke apha phambi koThixo, ukuze sive zonke izinto ozimiselweyo nguThixo.', 'XHO22'),
(7, 10, 34, 'Wawuvula ke uPetros umlomo wakhe, wathi, Ndiyaqiqa okwenyaniso ukuba uThixo akamkhethi wabuso bamntu;', 'XHO22'),
(7, 10, 35, 'kwiintlanga zonke, lowo umoyikayo, asebenze ubulungisa, wamkelekile kuye.', 'XHO22'),
(7, 10, 36, 'Ilizwi elo walithumelayo koonyana bakaSirayeli, eshumayela iindaba ezilungileyo zoxolo ngoYesu Kristu, (nguye iNkosi yabo bonke);', 'XHO22'),
(7, 10, 37, 'Elo lizwi lavakala kulo lonke elakwaJuda, elaqalwa kwelaseGalili, emva kobhaptizo awayelushumayela ngoYohane:', 'XHO22'),
(7, 10, 38, 'okokuba uThixo wamthambisa njani uYesu waseNazarete ngoMoya oyiNgcwele nangamandla, owatyhutyha ilizwe, esenza okulungileyo, ephilisa bonke ababecinezelwe nguMtyholi; ngokuba uThixo ubenaye.', 'XHO22'),
(7, 10, 39, 'Thina singamangqina azo zonke izinto awazenzayo ezweni lamaYuda, kwanaseYerusalem; abambulalayo, bamxhoma emthini;', 'XHO22'),
(7, 10, 40, 'Lowo uThixo wamvusa ngomhla wesithathu, wambeka ekuhleni;', 'XHO22'),
(7, 10, 41, 'kungekhona kubo bonke abantu, kodwa kumangqina awayenyulwe ngenxa engaphambili nguThixo: kuthi ke thina, sadlayo sasela naye emveni kokuvuka kwakhe kwabafileyo.', 'XHO22'),
(7, 10, 42, 'Wasiyalela ke, ukuba sishumayele ebantwini, singqine ukuba yena nguye omisiweyo nguThixo, ukuba abe ngumgwebi wabaphilileyo nabafileyo.', 'XHO22'),
(7, 10, 43, 'Ngaye bonke abaprofeti bangqina ngaye ukuba ngegama lakhe bonke abakholwayo kuye bofumana uxolelo lwezono.', 'XHO22'),
(7, 10, 44, 'Kwathi, esathetha uPetros la mazwi, wasuka wabawela uMoya oyiNgcwele bonke ababeliva ilizwi.', 'XHO22'),
(7, 10, 45, 'Bamangaliswa ke abolwaluko, ababeze noPetros, ngokuba isipho soMoya oyiNgcwele sathululwa naphezu kweentlanga;', 'XHO22'),
(7, 10, 46, 'kuba aziva zithetha ngalwimi zimbi, zimenza mkhulu uThixo. Waphendula ke uPetros wathi,', 'XHO22'),
(7, 10, 47, 'Kukho unako yini na ukuthintela amanzi, ukuba bangabhaptizwa aba bamamkeleyo uMoya oyiNgcwele, kwanjengathi nje?', 'XHO22'),
(7, 10, 48, 'Waza ke wabayalela ukuba babhaptizwe egameni leNkosi. Bamcela ngoko ukuba akhe ahlale iintsuku ezithile.', 'XHO22'),
(7, 11, 1, 'Ke kaloku abapostile nabazalwana ababekwelakwaYuda beva ukuba nazo iintlanga zilamkele ilizwi likaThixo.', 'XHO22'),
(7, 11, 2, 'Ke kaloku, akuba enyuke uPetros waya eYerusalem, babambana naye abolwaluko,', 'XHO22'),
(7, 11, 3, 'besithi, Uye wangena kumadoda angalukileyo, wadla nawo.', 'XHO22'),
(7, 11, 4, 'uPetros wayithetha ke loo nto kwasekuqalekeni, wabacacisela ngokomthetho, esithi,', 'XHO22'),
(7, 11, 5, 'Mna ndandikwisixeko saseYopa ndithandaza, ndabona ethongweni umbono, kusihla isitya esithile, ngathi yilinen enkulu, ithotywa ezulwini ngamasondo omane, yafika ke nakum.', 'XHO22'),
(7, 11, 6, 'Ndithe ndakuqwalasela kuyo, ndakuqwalasela kuyo, ndabona izinto zomhlaba ezizitho zine, namarhamncwa, nezinambuzane, neentaka zezulu.', 'XHO22'),
(7, 11, 7, 'Ndeva ilizwi lisithi kum, Vuka, Petros; xhela utye.', 'XHO22'),
(7, 11, 8, 'Ndithe ke mna, Hayi, Nkosi; ngokuba yonke into eyinqambi, nokuba yengcolileyo, ayizanga ingene emlonyeni wam.', 'XHO22'),
(7, 11, 9, 'Landiphendula ke ilizwi okwesibini, liphuma ezulwini, lathi, Izinto azihlambululeyo uThixo, musa ukuzenza iinqambi wena.', 'XHO22'),
(7, 11, 10, 'Kwabakho ke oku kathathu, zaza zonke ezo nto zabuya zarholelwa ezulwini.', 'XHO22'),
(7, 11, 11, 'Gqi, kwaoko amadoda amathathu, esele efikile endlwini ebendikuyo, esuswe eKesareya ukuba eze kum.', 'XHO22'),
(7, 11, 12, 'Wathi ke uMoya kum, mandihambe nawo, ndingathandabuzi nto. Bahamba nam ke aba bazalwana bathandathu, sangena endlwini yendoda leyo.', 'XHO22'),
(7, 11, 13, 'Yasichazela ukuba yasibona isithunywa sezulu endlwini yayo, simi sathi kuyo, Thuma amadoda eYopa, aye kubiza uSimon, ogama limbi linguPetros;', 'XHO22'),
(7, 11, 14, 'oya kuthetha kuwe amazwi, owosindiswa ngawo wena nayo yonke indlu yakho.', 'XHO22'),
(7, 11, 15, 'Ke kaloku, ndakuba ndithetha, uMoya oyiNgcwele wabawela, njengoko wehlayo nathi ekuqaleni.', 'XHO22'),
(7, 11, 16, 'Ndakhumbula ke izwi leNkosi lokokuba ibithe, uYohane okunene wabhaptiza ngamanzi; kodwa nina niya kubhaptizwa ngoMoya oyiNgcwele.', 'XHO22'),
(7, 11, 17, 'Kaloku uThixo wabapha isipho esifana neso wasiphayo nathi thina sakholwayo kwiNkosi uYesu Krestu. Bendiyintoni na mna, ukuba ndingade ndimchase uThixo?', 'XHO22'),
(7, 11, 18, 'Bakuziva ke ezo zinto, bathi cwaka, bamzukisa uThixo, besithi, Kanti na ke neentlanga ezi uThixo uziphe ukuguqukela ebomini.', 'XHO22'),
(7, 11, 19, 'Abo ke babeziintsali ngembandezelo eyabakho ngoStefano, batyhutyha ilizwe, bada beza kwelaseFenike, naseSipro, nakwa-Antiyokwe, bengalithethi bantwini bambi, belithetha kumaYuda odwa.', 'XHO22'),
(7, 11, 20, 'Yaye ke inxenye kubo ingamadoda aseSipro naseKirene; eyathi yona yakufika kwa-Antiyokwe, yathetha kwawesiGrike amaYuda, ishumayela iindaba ezilungileyo zeNkosi uYesu.', 'XHO22'),
(7, 11, 21, 'Saye isandla seNkosi sinabo, laza lakholwa into eninzi, yabuyela eNkosini.', 'XHO22'),
(7, 11, 22, 'Lwavakala ke udaba lwabo ezindlebeni zebandla laseYerusalem; bakhupha uBharnabhas, ukuba ahambe, aye kufika kwa-Antiyokwe;', 'XHO22'),
(7, 11, 23, 'Othe akufika, walubona ubabalo lukaThixo, wavuya, wabavuselela bonke, ukuba bathi ngengqibo yentliziyo bahlale bethe nca eNkosini.', 'XHO22'),
(7, 11, 24, 'Ngokuba ebeyindoda elungileyo, ezele nguMoya oyiNgcwele nalukholo. Kongezelelwa eNkosini inani elaneleyo.', 'XHO22'),
(7, 11, 25, 'Wemka ke uBharnabhas, waya eTarso, esiya kufuna uSawule;', 'XHO22'),
(7, 11, 26, 'Uthe akuba emfumene, wamsa kwa-Antiyokwe. Ke kaloku kwathi, umnyaka uphela, bahlangana nebandla, bafundisa inani elaneleyo. Kwaye abafundi babizwa ngokuba ngamaKristu kuqala kwa-Antiyokwe.', 'XHO22'),
(7, 11, 27, 'Ke kaloku ngaloo mihla, kwehla abaprofeti, besuka eYerusalem, beza kwa-Antiyokwe.', 'XHO22'),
(7, 11, 28, 'Kwesuka ke omnye wabo ogama linguAgabho, waqondisa ngaye uMoya ukuba kuza kubakho indlala enkulu kulo lonke elimiweyo; eyabakho ke ngemihla kaKlawudiyo Kesare.', 'XHO22'),
(7, 11, 29, 'Kubo ke abafundi, njengoko uthile ebenabo ubutyebi, wathi elowo kubo wagqiba ukuthumela into yokubanceda abazalwana ababemi kwelakwaYuda;', 'XHO22'),
(7, 11, 30, 'Benjenjalo ke, bathumela kumadoda amakhulu ngesandla sikaBharnabhas noSawule.', 'XHO22'),
(7, 12, 1, 'Ke kaloku, ngelo xesha uHerode ukumkani wabesa izandla abathile balo ibandla, ukuba abaphathe kakubi;', 'XHO22'),
(7, 12, 2, 'Wambulala ke uYakobi, umzalwana kaYohane, ngekrele.', 'XHO22'),
(7, 12, 3, 'Uthe ke akubona ukuba oko kuwakholisile amaYuda, waqokela ngokubamba noPetros. Loo mihla yayiyimihla yezonka ezingenagwele.', 'XHO22'),
(7, 12, 4, 'Akuba ke embambile, wamfaka entolongweni, wamnikela kumaqela amane amasoldati angamane, ukuba amgcine; engxamele ukumzisa ebantwini emva kwepasika.', 'XHO22'),
(7, 12, 5, 'uPetros wayegcinwa entolongweni, kodwa ibandla laqhubeka limthandazela kuThixo.', 'XHO22'),
(7, 12, 6, 'Xa ke uHerode wayeza kumkhupha, uPetros ngobo busuku wayelele phakathi kwamasoldati amabini, ekhonkxiwe ngemixokelelwane emibini; baye nabalindi beyigcinile intolongo phambi kwesango.', 'XHO22'),
(7, 12, 7, 'Naso isithunywa seNkosi, sisithi gaga phezu kwakhe, kwakhanya nokukhanya entolongweni, sambetha ecaleni uPetros, samvusa, sisithi, Phakama ngokukhawuleza. Yawa imixokelelwane yakhe ezandleni zakhe.', 'XHO22'),
(7, 12, 8, 'Sathi kuye isithunywa, Bhinqa, unxibe iimbadada zakho. Wenjenjalo ke. Athi kuye, Yambathisa ingubo yakho, undilandele.', 'XHO22'),
(7, 12, 9, 'Waphuma ke, wamlandela; ebengazi ukuba iyinene le nto yenziweyo ngesithunywa sezulu; ebeba ke ubone umbono.', 'XHO22'),
(7, 12, 10, 'Begqithile ke kwelokuqala nakwelesibini iqela labalindi, bafika esangweni lentsimbi elibhekisa kuwo umzi; eyabavulela yona ngoku iphumayo, baphuma ke, bahamba isitrato sasinye; semka kwaoko isithunywa eso kuye.', 'XHO22'),
(7, 12, 11, 'Wathi uPetros akuziqonda, wathi, Ngoku ndiyazi okwenyaniso, ukuba iNkosi ithume isithunywa sayo, yandihlangula esandleni sikaHerode, nakuko konke ulindelo lwabantu bamaYuda.', 'XHO22'),
(7, 12, 12, 'Eyibonile ke loo nto, waya kungena endlwini kaMariya, unina kaYohane, ogama limbi linguMarko, apho kwakuhlanganisene khona into eninzi ithandaza.', 'XHO22'),
(7, 12, 13, 'Akubon’ ukuba ke unkqonkqozile uPetros elucangweni lwesango, kweza kuphulaphula isicakazana esigama linguRoda.', 'XHO22'),
(7, 12, 14, 'Elazisile ilizwi likaPetros, akalivula isango, ngenxa yovuyo;', 'XHO22'),
(7, 12, 15, 'Bathi ke kuye, Uyageza. Kodwa wayesoloko eqinisekisa ukuba kunjalo. Bathi ke bona, Sisithunywa sakhe.', 'XHO22'),
(7, 12, 16, 'Uthe ke uPetros wamana enkqonkqoza; bathi ke bakuluvula bambona, bathi nqa.', 'XHO22'),
(7, 12, 17, 'Uwangawangisile ke ngesandla kubo, ukuba bathi cwaka, wabacacisela ukumkhupha kwayo iNkosi entolongweni. Wathi ke, Babikeleni ooYakobi nabazalwana ezi zinto. Waphuma waya kwenye indawo.', 'XHO22'),
(7, 12, 18, 'Ke kaloku, kwakusa, ayibanga ncinane inkathazeko phakathi kwamasoldati, yokuba angaba utheni na uPetros.', 'XHO22'),
(7, 12, 19, 'Uthe ke uHerode, akuba emfuna engamfumani, wabancina abalindi, wathi makumkiwe nabo, baye kubulawa. Wehla kwelakwaYuda, waya eKesareya, wahlala khona.', 'XHO22'),
(7, 12, 20, 'Ke kaloku uHerode wayebaqumbele kakhulu abaseTire nabaseTsidon. Beza ke kuye bemxhelo mnye, bathi, bemthomalalisile uBlasto, obephethe igumbi lokulala ukumkani, bazicelela uxolo; ngokuba ilizwe labo belisondliwa lilizwe lokumkani.', 'XHO22'),
(7, 12, 21, 'Ke kaloku uHerode, ngemini emisiweyo, ambethe ingubo yobukumkani, wahlala etroneni yakhe, wawisa ukuthetha kwakhe kubo.', 'XHO22'),
(7, 12, 22, 'Baduma ke abantu, besithi, Lilizwi lothixo elo, asilamntu.', 'XHO22'),
(7, 12, 23, 'Samthi qwaka ke kwaoko isithunywa seNkosi, ngenxa enokuba engamzukisanga uThixo; wadliwa ziimpethu, waphuma umphefumlo.', 'XHO22'),
(7, 12, 24, 'Laye ke likhula, lisanda ilizwi likaThixo.', 'XHO22'),
(7, 12, 25, 'Ke kaloku uBharnabhas noSawule babuya eYerusalem, belufezile ulungiselelo olo, bethabathe nabo uYohane, ogama limbi linguMarko.', 'XHO22'),
(7, 13, 1, 'Ke kaloku, kwa-Antiyokwe kwakukho abaprofeti nabafundisi abathile ebandleni, njengoBharnabhas, noSimon ekuthiwa nguNigere, noLukiyo waseKirene, noManayen owayondlwa noHerode umphathi wesahlulo sesine, noSawule.', 'XHO22'),
(7, 13, 2, 'Bakubon’ ukuba ke bayabusa eNkosini, bezila ukudla, wathi uMoya oyiNgcwele, Ndahluleleni ke uBharnabhas kwanoSawule, baye emsebenzini endibabizele kuwo.', 'XHO22'),
(7, 13, 3, 'Bathe ke, bakuba bezile ukudla, bathandaza, babeka nezandla phezu kwabo, babandulula.', 'XHO22'),
(7, 13, 4, 'Bona ke ngoko, bakuthunywa nguMoya oyiNgcwele, behla baya kwaSeluki; besuka khona apho ngomkhombe, baya eSipro.', 'XHO22'),
(7, 13, 5, 'Bathe bakufika eSalamis, balishumayela ilizwi likaThixo ezindlwini zesikhungu zamaJuda. BabenoYohane umlungiseleli wabo.', 'XHO22'),
(7, 13, 6, 'Besigqibile ke isiqhithi, bada beza kufika ePafo, bafumana siyazi uthile, ungumprofeti wobuxoki, umYuda ogama linguBhar-yesu,', 'XHO22'),
(7, 13, 7, 'owayehlala kwibamba uSergiyo Pawulos, indoda enokuqonda; owababizela kuye ooBharnabhas noSawule, efuna ukuliva ilizwi likaThixo.', 'XHO22'),
(7, 13, 8, 'Wamelana nabo ke uElima, usiyazi lowo (kuba litsho igama lakhe lakuthethwa ngentetho evakalayo), efuna ukulijika ibamba elukholweni.', 'XHO22'),
(7, 13, 9, 'Waza uSawule, okwabizwa ngokuba nguPawulos, ezele nguMoya oyiNgcwele, wamjongisa kuye,', 'XHO22'),
(7, 13, 10, 'wathi, Wena uzeleyo yiyo yonke inkohliso, nabubo bonke ubutshijolo, nyanandini woMtyholi, lutshabandini lobulungisa bonke, akuyi kupheza na ukuzijika iindlela ezithe tye zeNkosi?', 'XHO22'),
(7, 13, 11, 'Uyabona, uyabona, isandla seNkosi siphezu kwakho; ùya kuba yimfama, kube mzuzu ungaliboni ilanga. Kwaoko wawelwa yinkungu nobumnyama; wayejikeleza, efuna abangamrhola ngesandla.', 'XHO22'),
(7, 13, 12, 'Lithe ke ibamba, lakukubona oko kwenzekileyo, lakholwa, likhwankqisiwe yiyo imfundiso yeNkosi.', 'XHO22'),
(7, 13, 13, 'Ke kaloku, bakunduluka ePafo, benduluka uPawulos neqela lakhe, beza ePerga yasePamfili. Wemka ke kubo uYohane, wabuyela eYerusalem.', 'XHO22'),
(7, 13, 14, 'Bendulukile ke bona ePerga, batyhutyha beza kufika kwa-Antiyokwe kwelasePisidi. Bangena endlwini yesikhungo ngoMhla wokuPhumla, bahlala phantsi.', 'XHO22'),
(7, 13, 15, 'Ke kaloku, emva kokufundwa komthetho nabaprofeti, abaphathi bendlu yesikhungu bathumela kubo, besithi, Madoda, bazalwana, ukuba ninelizwi lovuselelo olu ebantwini, thethani.', 'XHO22'),
(7, 13, 16, 'Waphakama ke uPawulos, wawangawangisa ngesandla, wathi: “Madoda, maSirayeli, nani nimoyikayo uThixo, phulaphulani.', 'XHO22'),
(7, 13, 17, 'uThixo waba bantu bakwaSirayeli wabanyula oobawo bethu, wabaphakamisa abantu ekuphambukeleni kwabo ezweni laseYiputa, wabakhupha kulo ngengalo ephakamileyo.', 'XHO22'),
(7, 13, 18, 'Kwathi ngexesha eliyiminyaka engathi imashumi mane, wayithwala imikhwa yabo entlango.', 'XHO22'),
(7, 13, 19, 'Wathi akuwisa iintlanga ezisixhenxe emhlabeni wakwaKanan, wababela umhlaba wazo ngeqashiso.', 'XHO22'),
(7, 13, 20, 'Emva koko, iminyaka engathi imakhulu mane anamanci mahlanu, wabanika abagwebi, kwada kwakuSamuweli umprofeti.', 'XHO22'),
(7, 13, 21, 'Emva koko bacela ukumkani, uThixo wabanika uSawule, unyana kaKishe, indoda yesizwe sakwaBhenjamin, iminyaka emashumi mane.', 'XHO22'),
(7, 13, 22, 'Akuba ke emsusile, wabavelisela uDavide njengokumkani; awamngqinelayo kanjalo, wathi, Ndimfumene uDavide okaYese, indoda engantliziyo yam, eya kukwenza konke ukuthanda kwam.', 'XHO22'),
(7, 13, 23, 'Embewini yalo mntu uthe uThixo ngokwedinga wavelisela uSirayeli uMsindisi uYesu;', 'XHO22'),
(7, 13, 24, 'kwakubon’ ukuba uYohane uvakalise tanci, phambi kokungena kwakhe, ubhaptizo loguquko kubo bonke abantu bakwaSirayeli.', 'XHO22'),
(7, 13, 25, 'Xa ke uYohane ebesekulufezeni uhambo lwakhe, ubesithi, Nithi nina ndingubani na? Andinguye. Kodwa, yabonani, uyeza emva kwam, endingafanelekileyo mna ukuba ndizithukulule iimbadada ezinyaweni zakhe.', 'XHO22'),
(7, 13, 26, 'Madoda, bazalwana, nyana bomlibo wakwa-Abraham, nabo phakathi kwenu bamoyikayo uThixo, ilizwi lolu lusindiso lathunyelwa kuni.', 'XHO22'),
(7, 13, 27, 'Kuba abo bemiyo eYerusalem, nabaphathi babo, bengamazanga lowo, kwanamazwi abaprofeti aleswayo iisabatha ngeesabatha zonke, bawazalisa ngokumgweba;', 'XHO22'),
(7, 13, 28, 'bathi, bengafumananga nalinye ityala lokufa, bacela kuPilato ukuba asikwe.', 'XHO22'),
(7, 13, 29, 'Xa ke babezifezile zonke izinto ezibhaliweyo ngaye, bamthula emthini, bambeka engcwabeni.', 'XHO22'),
(7, 13, 30, 'Kodwa uThixo wamvusa kwabafileyo.', 'XHO22'),
(7, 13, 31, 'Yena lowo wabonwa imihla emininzi ngabo babenyuke naye, bevela kwelaseGalili besiya eYerusalem; ngabo ke abangamangqina akhe ebantwini.', 'XHO22'),
(7, 13, 32, 'Nathi ke sinishumayeza iindaba ezilungileyo zokuba idinga elo labakho koobawo,', 'XHO22'),
(7, 13, 33, 'zokokuba uThixo ulizalisile lona kuthi, bantwana babo, ngokumvusa uYesu; njengokuba nakwiNdumiso yesibini kubhaliwe kwathiwa, UnguNyana wam wena, Mna ndikuzele namhla.', 'XHO22'),
(7, 13, 34, 'Malunga ke nokumvusa kwakhe kwabafileyo, angabi sabuyela ekuboleni, uselethe ukuthetha, Ndiya kuninika iinceba eziqinisekileyo zikaDavide.', 'XHO22'),
(7, 13, 35, 'Kungoko athi nakwenye iNdumiso, Akuyi kumyekela oyiNgcwele wakho ukuba abone ukubola.', 'XHO22'),
(7, 13, 36, 'Kuba uDavide okunene, akuba elisebenzele kwesakhe isizukulwana icebo likaThixo, walala ubuthongo, wabekwa kooyise, wakubona ukubola;', 'XHO22'),
(7, 13, 37, 'Ke yena owavuswayo nguThixo, akabonanga kubola.', 'XHO22'),
(7, 13, 38, 'Makwazeke ngoko kuni, madoda, bazalwana, ukuba ngaye lowo kuxelwa kuni uxolelo lwezono;', 'XHO22'),
(7, 13, 39, 'nakuzo zonke izinto ebeningenako ukugwetyelwa nikhululeke kuzo ngomthetho kaMoses, bonke abakholwayo bayagwetyelwa ngaye yena lowo.', 'XHO22'),
(7, 13, 40, 'Lumkani ke, ningafikelwa koko kwathethwa ngako ebaprofetini.', 'XHO22'),
(7, 13, 41, 'Khangelani, nina badeli, nimangaliswe, nitshabalale; ngokuba mna ndisebenza umsebenzi ngemihla yenu, Umsebenzi eningasayi kukholwa kuwo, nokuba kukho othi anicacisele.', 'XHO22'),
(7, 13, 42, 'Ke kaloku, akubon’ ukuba ayaphuma amaYuda endlwini yesikhungu, babongoza abeentlanga, ukuba la mazwi athethwe kubo ngesabatha ezayo.', 'XHO22'),
(7, 13, 43, 'Sakubon’ ukuba ke sichithakele isikhungu, into eninzi yamaYuda neyabangene ebuYudeni, abamhlonelayo uThixo, yabalandela ooPawulos noBharnabhas;', 'XHO22'),
(7, 13, 44, 'Kuthe ke ngelandelayo isabatha, waphantsa ukuhlanganisana umzi wonke, ukuba ulive ilizwi likaThixo.', 'XHO22'),
(7, 13, 45, 'Athe ke amaYuda, akuzibona izihlwele, azala ngumona, aziphika izinto ezibe zithethwa nguPawulos, aphika enyelisa.', 'XHO22'),
(7, 13, 46, 'Bathetha ke ngokungafihlisiyo uPawulos noBharnabhas, bathi, Bekufanele ukuba lithethwe kuni kuqala ilizwi likaThixo; ekubeni ke nina nililahlile, nazigweba ukuba anibufanele ubomi obungunaphakade, niyabona, sibheka kuzo iintlanga.', 'XHO22'),
(7, 13, 47, 'Kuba isiwisele umthetho ngokunjalo iNkosi, yathi, Ndikubekile ukuba ube lukhanyiselo lweentlanga, ukuze ube ngowosindiso, kude kuse ekupheleni kwehlabathi.', 'XHO22'),
(7, 13, 48, 'Zakuva ke oko iintlanga, zavuya, zalizukisa ilizwi leNkosi, bakholwa bonke ababemiselwe ubomi obungunaphakade.', 'XHO22'),
(7, 13, 49, 'Laye ke ilizwi leNkosi lihanjiswa kulo lonke ilizwe elo.', 'XHO22'),
(7, 13, 50, 'Ke kaloku amaYuda abaxhokonxa abafazi ababehlonela uThixo, ababekekileyo, ababekekileyo, nabaninimzi; abaxhoxhela intshutshiso ooPawulos noBharnabhas, abakhupha emideni yawo.', 'XHO22'),
(7, 13, 51, 'Bathe ke bona bawavuthululela uthuli lweenyawo zabo, beza eIkoniyo.', 'XHO22'),
(7, 13, 52, 'Baye ke abafundi bezaliswe luvuyo nanguMoya oyiNgcwele.', 'XHO22'),
(7, 14, 1, 'Ke kaloku kwathi eIkoniyo, bangena kunye kwindlu yesikhungu yamaYuda, bathetha ngokokude kukholwe inkitha enkulu yamaYuda kwanamaGrike.', 'XHO22'),
(7, 14, 2, 'Asuka ke wona amaYuda angavumiyo ukuva, ayenza yamibi imiphefumlo yabeentlanga ngakubo abazalwana.', 'XHO22'),
(7, 14, 3, 'Balibala ke ngoko ixesha elaneleyo, bethetha ngokungafihlisiyo ngeNkosi, eyalingqinelayo ilizwi lobabalo lwayo, inika nokuba kwenzeke imiqondiso nezimanga ngezandla zabo.', 'XHO22'),
(7, 14, 4, 'Yahlulelana ke inkoliso yomzi, inxenye yangakumaYuda, inxenye yangakubapostile.', 'XHO22'),
(7, 14, 5, 'Kuthe ke, kuthe kwabakho ukuhlasela kwabeentlanga, kwanamaYuda, kunye nabaphathi bawo, ukuba babaphathe kakubi, babagibisele ngamatye:', 'XHO22'),
(7, 14, 6, 'bakuqiqa oko bona, basabela emizini yelaseLikawoni, iListra neDerbhe, nakuloo mimandla;', 'XHO22'),
(7, 14, 7, 'Baye bezishumayela nalapho iindaba ezilungileyo.', 'XHO22'),
(7, 14, 8, 'Ke kaloku eListra bekuhleli indoda ethile, ikhubazekile ezinyaweni zayo, isisiqhwala kwasesizalweni sikanina, engazange ihambe.', 'XHO22'),
(7, 14, 9, 'Yona leyo yayimva uPawulos ethetha; owathi, eqwalasele kuyo, ebona ukuba inokholo lokusindiswa.', 'XHO22'),
(7, 14, 10, 'Sathi ngezwi elikhulu, Yima, ume ngeenyawo zakho, uthi nkqo. Yatsiba, yahambahamba.', 'XHO22'),
(7, 14, 11, 'Ke kaloku iindimbane, zakukubona oko akwenzileyo uPawulos, zaphakamisa izwi lazo ngokwesiLikawoni, zisithi, Oothixo bahlile beza kuthi, befana nabantu.', 'XHO22'),
(7, 14, 12, 'Bambiza ke uBharnabhas ngokuthi nguZeyus; noPawulos ke, uMerkuriyo, ngenxa yokuba ebesisithethi esiyintloko.', 'XHO22'),
(7, 14, 13, 'Kuthe ngoko umbingeleli kaZeyus, obephambi komzi wabo, wazisa iinkunzi zeenkomo nezidanga zeentyantyambo emasangweni, wafuna ukubingelela, kunye neendimbane.', 'XHO22'),
(7, 14, 14, 'Bekuvile ke oko abapostile ooBharnabhas noPawulos, bazikrazula iingubo zabo, babalekela phakathi kwabantu, bedanduluka;', 'XHO22'),
(7, 14, 15, 'besithi, Madoda, yini na ukuba nenze ezi zinto? Nathi singabantu abeva enikuvayo nina; sinishumayeza iindaba ezilungileyo zokuba nibuye kwaba thixo bangento, nibuyele kuThixo ophilileyo, owenza izulu nomhlaba nolwandle, neento zonke ezikwezo zinto;', 'XHO22'),
(7, 14, 16, 'owathi kudala waziyeka zonke iintlanga, ukuba zihambe ngezazo iindlela;', 'XHO22'),
(7, 14, 17, 'Kambe ke noko akayekanga ukuzingqinela, esenza okulungileyo, esinika imvula evela ezulwini, namaxesha aneendyebo, ezanelisa iintliziyo zethu ngokudla nangemihlali.', 'XHO22'),
(7, 14, 18, 'Besitsho, kwaba ngenkankulu ukuzidambisa iindimbane, ukuba zingabingeleli kubo.', 'XHO22'),
(7, 14, 19, 'Ke kaloku kwafika amaYuda evela kwa-Antiyokwe naseIkoniyo, aza azixhokonxa iindimbane, amxuluba ngamatye uPawulos, amrholela ngaphandle komzi, eba ufile.', 'XHO22'),
(7, 14, 20, 'Kodwa, bakubon’ ukuba bamngqongile ke abafundi, wavuka waya kungena kuwo umzi. Ngengomso waphuma enoBharnabhas, waya eDerbhe.', 'XHO22'),
(7, 14, 21, 'Bakuba ke bezishumayele iindaba ezilungileyo kuloo mzi, befundisa abantu abaninzi, babuya beza eListra, naseIkoniyo, nakwa-Antiyokwe;', 'XHO22'),
(7, 14, 22, 'besomeleza imiphefumlo yabafundi, bebavuselela ukuba bahlale behleli elukholweni, nokokuba simelwe kukuphumela ezimbandezelweni ezininzi, ukungena ebukumkanini bukaThixo.', 'XHO22'),
(7, 14, 23, 'Bathe ke bakuba benyule amadoda amakhulu kwibandla ngebandla, bethandazile kunye nokuzila ukudla, bawayaleza eNkosini awayekholwe kuyo.', 'XHO22'),
(7, 14, 24, 'Betyhutyhe elasePisidi, bafika kwelasePamfili.', 'XHO22'),
(7, 14, 25, 'Bathi, belithethile ilizwi ePerga, behla baya kwa-Atali.', 'XHO22'),
(7, 14, 26, 'Besuka khona apho ngomkhombe, baya kwa-Antiyokwe, apho babenikelwe khona elubabalweni lukaThixo, ukuba baye emsebenzini, lo ke bawufezileyo.', 'XHO22'),
(7, 14, 27, 'Befikile ke, balihlanganisela ndawonye ibandla, balibikela oko zingako izinto awazenzayo uThixo enabo, nokokuba iintlanga wazivulela ucango lokholo.', 'XHO22'),
(7, 14, 28, 'Balibala ke khona apho ixesha elingelincinane, ndawonye nabafundi.', 'XHO22'),
(7, 15, 1, 'Kwehla abathile bevela kwelakwaYuda, babafundisa abazalwana, besithi, Ukuba anithanga naluswe ngokwesiko likaMoses, aninako ukusindiswa.', 'XHO22'),
(7, 15, 2, 'Kuthe ngoko, kwakubon’ ukuba uPawulos noBharnabhas baba nempikiswano nembuzwano engencinane nabo, kwagqitywa kwelokuba uPawulos noBharnabhas, nabanye abathile kubo, mabenyuke baye kubapostile namadoda amakhulu eYerusalem, ngawo lo mbuzo.', 'XHO22'),
(7, 15, 3, 'Bathi ke, bephelekelelwe libandla, balityhutyha elaseFenike nelaseSamariya, bexela ukuguquka kwazo iintlanga; babanga uvuyo olukhulu kubo bonke abazalwana.', 'XHO22'),
(7, 15, 4, 'Befikile ke eYerusalem, bamkelwa libandla nangabapostile nangamadoda amakhulu; kananjalo babika ukuba zinkulu kwazo izinto awazenzayo uThixo enabo.', 'XHO22'),
(7, 15, 5, 'Kodwa kwesuka kwavuka amakholwa athile ehlelo labaFarisi, besithi, Mabaluke, nokuba bathethelwe ngelithi, mabawugcine umthetho kaMoses.', 'XHO22'),
(7, 15, 6, 'Bahlanganisana ke abapostile namadoda amakhulu, ukuba bayikhangele loo ndawo.', 'XHO22'),
(7, 15, 7, 'Ke kaloku, kwakubon’ ukuba kubekho imbuzwano enkulu, wesuka uPetros wathi kubo, Madoda, bazalwana, niyazi nina, ukuba uThixo kwangemihla yakudala wanyula phakathi kwethu, ukuba iintlanga zilive ngomlomo wam ilizwi leendaba ezilungileyo ezi, zihambe ngomlomo wam, kholwani.', 'XHO22'),
(7, 15, 8, 'uThixo umazi-ntliziyo wazingqinela, ezinika uMoya oyiNgcwele kwanjengathi;', 'XHO22'),
(7, 15, 9, 'akacalula phakathi kwethu nazo, ezihlambulule iintliziyo zazo ngokholo.', 'XHO22'),
(7, 15, 10, 'Ngoku ke nimlingelani na uThixo, ngokubeka idyokhwe entanyeni yabafundi, esibe singenamandla okuyithwala, thina kwanoobawo bethu?', 'XHO22'),
(7, 15, 11, 'Ke ngalo ubabalo lweNkosi uYesu Kristu, siyakholwa ukuba sosindiswa kwangohlobo ezisindiswa ngalo nazo.', 'XHO22'),
(7, 15, 12, 'Yathi cwaka ke yonke inkitha yabantu, yaphulaphula ooBharnabhas noPawulos, becacisa oko yayingako imiqondiso nezimanga, awazenzayo uThixo phakathi kwazo iintlanga ngabo.', 'XHO22'),
(7, 15, 13, 'Ke kaloku, emva kokuthi cwaka kwabo, waphendula uYakobi wathi, Madoda, bazalwana, ndiphulaphuleni.', 'XHO22'),
(7, 15, 14, 'uSimon lo ukucacisile ukuba wathini na ekuqaleni uThixo ukuzivelela iintlanga, ukuba kuzo alinyulele abantu elakhe igama.', 'XHO22'),
(7, 15, 15, 'Ayavumelana nako oko amazwi abaprofeti; njengoko kubhaliwe kwathiwa,', 'XHO22'),
(7, 15, 16, 'Emva koko ndiya kubuya ndize ndibuye ndiwakhe umnquba owileyo kaDavide, ndiya kubuya ndiwakhe amanxuwa ayo, ndiyimise;', 'XHO22'),
(7, 15, 17, 'Okokuze amaqongqolo abantu ayifune iNkosi, Kwaneentlanga zonke ezibizwe ngalo igama lam; Itsho iNkosi, leyo izenzayo zonke ezi zinto.', 'XHO22'),
(7, 15, 18, 'Iyaziwa ke nguThixo kuselokwaphakade yonke imisebenzi yakhe.', 'XHO22'),
(7, 15, 19, 'Ngoko ke mna ndithi, masingabakhathazi abo baphuma ezintlangeni babuyela kuThixo;', 'XHO22'),
(7, 15, 20, 'kodwa masibabhalele ukuba bazikhwebule kwizingcoliso zezithixo, nombulo, nento ekrwitshiweyo, negazi.', 'XHO22'),
(7, 15, 21, 'Kuba uMosis kwakudala unabamvakalisayo kwimizi ngemizi, eleswa ezindlwini zesikhungu ezi iisabatha ngeesabatha.', 'XHO22'),
(7, 15, 22, 'Kwalunga ke abapostile namadoda amakhulu, kunye nebandla liphela, ukuba banyule amadoda phakathi kwabo, ukuba baye kwa-Antiyokwe kunye noPawulos noBharnabhas; noYuda ogama limbi linguBharsabhas, noSilas, amadoda aziintloko phakathi kwabazalwana.', 'XHO22'),
(7, 15, 23, 'Babhala ke iincwadi ngesandla ngabo; Abapostile namadoda amakhulu nabazalwana bayabulisa kubo abazalwana basezintlangeni, kwa-Antiyokwe, naseSiriya, naseKiliki;', 'XHO22'),
(7, 15, 24, 'Ngenxa yokuba sivile ukuba abathile baphuma kuthi banikhathaza ngamazwi, beyiqweqwedisa imiphefumlo yenu, besithi, Naluke, niwugcine umthetho, thina singabawiselanga mthetho unjalo.', 'XHO22'),
(7, 15, 25, 'Kwabonakala kulungile kuthi, simxhelo mnye, ukuba sinyule amadoda kuni, kunye neentanda zethu, uBharnabhas noPawulos;', 'XHO22'),
(7, 15, 26, 'abantu ababuncamileyo ubomi babo ngenxa yegama leNkosi yethu uYesu Kristu.', 'XHO22'),
(7, 15, 27, 'Sibathumile ngoko ooYuda noSilas, abothi nabo banibikele ezi ndawo ngomlomo.', 'XHO22'),
(7, 15, 28, 'Kuba kwabonakala kulungile kuye uMoya oyiNgcwele, nakuthi, ukuba kungabekwa phezu kwenu mthwalo ungaphezu kwezo zifunekayo;', 'XHO22'),
(7, 15, 29, 'zokuba nizile izinto ezibingelelwe kwizithixo, negazi, nento ekrwitshiweyo, nombulo; enothi nakuzigcina kuzo, nibe niyalungisa, nihambe kakuhle.', 'XHO22'),
(7, 15, 30, 'Ke kaloku, bakubon’ ukuba bayenduluka ke, beza kwa-Antiyokwe;', 'XHO22'),
(7, 15, 31, 'Bathe ke bona bakuba balesa, bavuya ngenxa yothuthuzeleko olo.', 'XHO22'),
(7, 15, 32, 'Ke kaloku ooYuda noSilas, bengabaprofeti nabo, babakhuthaza abazalwana ngokuthetha okukhulu, babomeleza.', 'XHO22'),
(7, 15, 33, 'Bakuba ke benze ixesha, banduluka bexolile kubazalwana, baya kubapostile.', 'XHO22'),
(7, 15, 34, 'Kodwa kuSilas kwabonakala kulungile ukuba akhe ahlale khona.', 'XHO22'),
(7, 15, 35, 'Balibala ke ooPawulos noBharnabhas kwa-Antiyokwe, befundisa, beshumayela iindaba ezilungileyo zelizwi leNkosi, kwanabanye abaninzi.', 'XHO22'),
(7, 15, 36, 'Ke kaloku emva kwemihla ethile, wathi uPawulos kuBharnabhas, Makhe sibuye siye kuvelela abazalwana bethu kuyo yonke imizi esalazisa kuyo ilizwi leNkosi, sibone ukuba banjani na.', 'XHO22'),
(7, 15, 37, 'Ke kaloku uBharnabhas wangxamela ukuba bathabathe nabo uYohane, ogama limbi linguMarko;', 'XHO22'),
(7, 15, 38, 'kodwa uPawulos wathi akufanelekile ukumthabatha, ahambe nabo, lowo wemkayo kubo kwelasePamfili, akaya nabo emsebenzini.', 'XHO22'),
(7, 15, 39, 'Kwabakho ingxabano enkulu phakathi kwabo, ngokokude bahlukane; uBharnabhas wamthabatha ke uMarko, wemka ngomkhombe, waya eSipro.', 'XHO22'),
(7, 15, 40, 'uPawulos wanyula uSilas, wemka, enikelwe kubabalo lukaThixo ngabazalwana.', 'XHO22'),
(7, 15, 41, 'Walityhutyha ke elaseSiriya nelaseKiliki, ewomeleza amabandla.', 'XHO22'),
(7, 16, 1, 'Ke kaloku wafika eDerbhe naseListra. Kwabonakala kukho mfundi uthile apho, ugama linguTimoti, unyana wentokazi ethile engumYudakazi okholiweyo, kodwa uyise engumGrike; ke uyise yena engumGrike;', 'XHO22'),
(7, 16, 2, 'Ekungqinelwe kakuhle ngako ngabazalwana baseListra naseIkoniyo.', 'XHO22'),
(7, 16, 3, 'Lowo uPawulos wathanda ukuba aphume naye; wamthabatha wamalusa, ngenxa yamaYuda abekwezo ndawo; kuba bonke babemazi uyise ukuba ungumGrike.', 'XHO22'),
(7, 16, 4, 'Ke kaloku, xa babeyihamba imizi, babanikela imimiselo ukuba bayigcine, leyo ibimiswe ngabapostile namadoda amakhulu aseYerusalem.', 'XHO22'),
(7, 16, 5, 'Ayezimaseka ke ngoko amabandla elukholweni, esanda ngenani imihla ngemihla.', 'XHO22'),
(7, 16, 6, 'Ke kaloku, bakulityhutyha ilizwe laseFrigi nelaseGalati, balelwa nguMoya oyiNgcwele ukulithetha ilizwi kwelaseAsiya.', 'XHO22'),
(7, 16, 7, 'Bakuba befikile eMisiya, balinga ukuya eBhithiniya, kodwa akabavumelanga uMoya.', 'XHO22'),
(7, 16, 8, 'Badlula eMisiya behla baya eTrowa.', 'XHO22'),
(7, 16, 9, 'Kwabonakala umbono kuPawulos ebusuku. Indoda ethile yaseMakedoni yayimi imbongoza isithi, Welela ngapha kwelaseMakedoni, usincede.', 'XHO22'),
(7, 16, 10, 'Ke kaloku, akuwubona umbono lowo, saza ke sazabalazela ukuba siye kwelaseMakedoni, sigqibe kwelokuba iNkosi isibizele ukuba sibashumayeze iindaba ezilungileyo.', 'XHO22'),
(7, 16, 11, 'Sindulukile ngoko eTrowa, seza ngqo eSamotraki, sathi ngengomso seza eNeyapoli;', 'XHO22'),
(7, 16, 12, 'ukusuka apho, seza eFilipi, owona mzi wokuqala wecala elo lilelaseMakedoni, uyikoloni, salibala ke kuloo mzi.', 'XHO22'),
(7, 16, 13, 'NgoMhla wokuPhumla saphuma saya ngaphandle kwedolophu ngasemlanjeni, apho kuthandazwa khona; sahlala phantsi, sathetha kubafazi ababebuthelene khona.', 'XHO22'),
(7, 16, 14, 'Yasiva intokazi ethile egama linguLidiya, umthengisi weengubo ezibomvu, womzi weTiyatira, emhlonelayo uThixo, entliziyo yayivulayo iNkosi ukuba izinyamekele izinto ezazithethwa nguPawulos.', 'XHO22'),
(7, 16, 15, 'Ke kaloku, yakuba ibhaptiziwe nendlu yayo, yasibongoza isithi, Ukuba nithi ndikholiwe eNkosini, ngenani endlwini yam, nihlale khona. Wasinyanzela.', 'XHO22'),
(7, 16, 16, 'Ke kaloku kwathi, kwakubon’ ukuba siyaya endaweni yokuthandaza, sahlangatyezwa sisicakazana esithile, sinomoya wemilozi; sona besizizuzisa iinkosi zaso inzuzo enkulu ngokuvumisa.', 'XHO22'),
(7, 16, 17, 'Sona eso sithene mbende noPawulos nathi, samana sidanduluka sisithi, Aba bantu bangabakhonzi bakaThixo Osenyangweni, bona basazisayo indlela yosindiso.', 'XHO22'),
(7, 16, 18, 'Oko sakwenza sona imihla emininzi. Ebindekile ke uPawulos, wathi guququ, wathi kuwo umoya, Ndiyakuthethela egameni likaYesu Kristu ukuba uphume kuso. Waphuma kwangelo lixa.', 'XHO22'),
(7, 16, 19, 'Zithe ke iinkosi zayo, zakubona ukuba liphelile ithemba lenzuzo yawo, zabathabatha ooPawulos noSilas, zabarholela endaweni yembutho, phambi kwabaphathi;', 'XHO22'),
(7, 16, 20, 'Babazisa kubathetheli, besithi, Aba bantu bayawuphazamisa kakhulu umzi wethu, bengamaYuda;', 'XHO22'),
(7, 16, 21, 'Basifundisa amasiko ekungavumelekileyo ukuba siwamkele, nokuba siwenze, singamaRoma.', 'XHO22'),
(7, 16, 22, 'Isihlwele sabahlanganyela; nabathetheli bazithi qwenge iingubo zabo, bathi mababethwe ngeentswazi.', 'XHO22'),
(7, 16, 23, 'Bathi, bakuba bababethe imivumbo emininzi, babaphosa entolongweni, bamthethela umgcini wababanjwa ukuba abagcine ngokunqabileyo;', 'XHO22'),
(7, 16, 24, 'owathi, amkele umyalelo onjalo, wabaphosa kwengaphakathi intolongo, waziqinisa iinyawo zabo emithini.', 'XHO22'),
(7, 16, 25, 'Ke kaloku, ezinzulwini zobusuku uPawulos noSilas babethandaza, bedumisa uThixo; yaye amabanjwa abeva ke.', 'XHO22'),
(7, 16, 26, 'Kwabakho ke ngequbuliso unyikimo olukhulu lomhlaba, ngokokude zizanyazanyiswe iziseko zentolongo; zavuleka kwaoko iingcango zonke, athukululeka amakhamandela abo bonke.', 'XHO22'),
(7, 16, 27, 'Ethe tyithi ke ebuthongweni umgcini wababanjwa, ebona iingcango zentolongo zivulekile, warhola ikrele, eza kuzibulala, eba ababanjwa babalekile.', 'XHO22'),
(7, 16, 28, 'Wadanduluka ke uPawulos ngezwi elikhulu, esithi, Musa ukuzenzakalisi; kuba silapha sonke.', 'XHO22'),
(7, 16, 29, 'Uthe ngoko, ebize izibane, waxhumela phakathi, egubha, wawa phambi koPawulos noSilas.', 'XHO22'),
(7, 16, 30, 'Wabazisa phandle, wathi, Zinkosi, ndimelwe kukuthini na, ukuze ndisindiswe?', 'XHO22'),
(7, 16, 31, 'Bathi ke bona, Kholwa kuyo iNkosi uYesu Kristu, wosindiswa wena nendlu yakho.', 'XHO22'),
(7, 16, 32, 'Balithetha kuye ilizwi leNkosi, nakubo bonke ababesendlwini yakhe.', 'XHO22'),
(7, 16, 33, 'Wabathabatha ngelo lixa lobusuku, wayihlamba imivumbo yabo; babhaptizwa kwaoko, yena nabo bonke abakhe.', 'XHO22'),
(7, 16, 34, 'Kananjalo, ebanyuse wabasa endlwini yakhe, wabeka ukudla phambi kwabo, wagcoba nendlu yakhe yonke, kuba ekholwa kuThixo.', 'XHO22'),
(7, 16, 35, 'Kwakusa, abathetheli bathuma amadindala, besithi, Bakhulule abo bantu;', 'XHO22'),
(7, 16, 36, 'Uwaxelile ke umgcini wababanjwa la mazwi kuPawulos, wathi, Abathetheli bathumele ukuba nikhululwe; ke ngoko phumani, nihambe ngoxolo.', 'XHO22'),
(7, 16, 37, 'Uthe ke uPawulos kuwo, Basityabule ekuhleni singagwetywanga, sibantu bangamaRoma; basiphosa entolongweni; ngoku basikhupha ngasese na? hayi inene; mabeze bona ngokwabo, basikhuphe.', 'XHO22'),
(7, 16, 38, 'Awaxela ke la mazwi amadindala kubathetheli; boyika, bakuva ukuba bangamaRoma.', 'XHO22'),
(7, 16, 39, 'Beza bababongoza, babakhupha, babacela ukuba baphume kuwo umzi.', 'XHO22'),
(7, 16, 40, 'Bephumile ke entolongweni, baya kungena kwaLidiya; bathi, bebabonile abazalwana, babathuthuzela, bemka.', 'XHO22'),
(7, 17, 1, 'Ke kaloku, bakuba beyihambile iAmfipoli neApoloni, beza eTesalonika, apho yayikhona indlu yesikhungu yamaYuda.', 'XHO22'),
(7, 17, 2, 'Ke kaloku, ngokwesiko lakhe ke uPawulos wangena kubo, wathi iisabatha ezintathu, exoxa nabo ngezibhalo,', 'XHO22'),
(7, 17, 3, 'Etyhila, ebeka phambi kwakhe ukuba uKristu ubemelwe kukuva ubunzima, aze avuke kwabafileyo; nokuba, lo Yesu, ndivakalisayo kuni, nguye uKristu.', 'XHO22'),
(7, 17, 4, 'Inxenye kuwo yoyiseka, yazibandakanya noPawulos noSilas, kwanenkitha enkulu yamaGrike amhlonelayo uThixo, nakubafazi abaziintloko ababa mbalwa.', 'XHO22'),
(7, 17, 5, 'Ke kaloku amaYuda angengakholwayo, emmonele, azithabathela amadoda athile angamatshijolo, angamatshijolo, ahlanganisana ihlokondiba, awuqhumisela umzi uphela; abantu.', 'XHO22'),
(7, 17, 6, 'Akuba engabafumani, amrholela uYason nabazalwana abathile kubaphathi bomzi, edanduluka esithi, Aba baliqungaqungileyo elimiweyo, bafikile nalapha;', 'XHO22'),
(7, 17, 7, 'athe uYason wabamkela ngobubele; baye bonke aba besenza okuchasene nemimiselo kaKesare, besithi, Kukho kumkani wumbi, onguYesu.', 'XHO22'),
(7, 17, 8, 'Zazikhathaza iindimbane nabaphathi bomzi, bakuziva ezi zinto.', 'XHO22'),
(7, 17, 9, 'Bethabathe isibambiso kuYason nakwabanye, babakhulula.', 'XHO22'),
(7, 17, 10, 'Ke kaloku bathi abazalwana kwaoko bamndulula uPawulos noSilas ngobusuku, ukuba baye eBhereya; abathi bona bakufika, baya endlwini yesikhungu yamaYuda.', 'XHO22'),
(7, 17, 11, 'La ke aye enobuntu kunawaseTesalonika; la wona alamkela ilizwi ngentumekelelo yonke, ezincina izibhalo imihla ngemihla, ukuba zingaba zinjalo na ezo zinto.', 'XHO22'),
(7, 17, 12, 'Into eninzi ngoko yakholwa; nakumaGrikekazi abekekileyo, nakumadoda, nokuba mbalwa.', 'XHO22'),
(7, 17, 13, 'Akwazi ke amaYuda aseTesalonika, ukuba ilizwi likaThixo lazisiwe nguPawulos naseBhereya, eza azizamazamisa nalapho iindimbane.', 'XHO22'),
(7, 17, 14, 'Bandula ke kwaoko abazalwana, bamsusa uPawulos ukuba abheke ngaselwandle; kwasala uSilas noTimoti khona.', 'XHO22'),
(7, 17, 15, 'Bathe ke abakhapha uPawulos, bamsa eAtene; bakuba bakuba bamkele umthetho oya kooSilas noTimoti, wokuba beze kuye kwakamsinya, bemka.', 'XHO22'),
(7, 17, 16, 'Ke kaloku, akubon’ ukuba uPawulos ubalindile eAtene, wacaphuka, akuwubona umzi uzizithixo kuphela.', 'XHO22'),
(7, 17, 17, 'Wayexoxa ke ngoko endlwini yesikhungu namaYuda, nabamhlonelayo uThixo; nasendaweni yembutho imihla ngemihla, nabasukuba bekhona.', 'XHO22'),
(7, 17, 18, 'Ke kaloku izithanda-bulumko ezithile zamaEpikure nezamaStoyike zamquqela; Babesithi inxenye, Liya kuthini na eli pholopholo? Abanye ke bathi, Ngathi ngumazisi wezithixo zasemzini; ngokuba wayebashumayeza iindaba ezilungileyo zikaYesu, novuko lwabafileyo.', 'XHO22'),
(7, 17, 19, 'Bamthabatha ke bamsa eAreyopago, besithi, Singakhe siyive na le mfundiso intsha ithethwa nguwe, ukuba iyini na?', 'XHO22'),
(7, 17, 20, 'Kuba uzisa izinto ezithile zasemzini ezindlebeni zethu; singa singazi ngoko, ukuba zingaba zithetha ukuthini na ezi zinto.', 'XHO22'),
(7, 17, 21, 'Kuba onke ama-Atene nabasemzini babechithachitha ixesha labo engenzi nto yimbi, ingekuxeleli nokuva entsha.', 'XHO22'),
(7, 17, 22, 'Wema ke uPawulos esazulwini seAreta, wathi, Madoda aseAtene, ndiyabona ukuba ezintweni zonke nincamisile ukuhlonela izithixo.', 'XHO22'),
(7, 17, 23, 'Kuba ndithe, ndihamba ndisingasinga izinto enizihlonelayo, ndafumana nesibingelelo ekubhalwe kuso, kwathiwa, kongaziwayo uThixo. Lowo ke ngoko nina nimnqula ningamazi, ndinazisa yena.', 'XHO22'),
(7, 17, 24, 'uThixo yena, owenza ihlabathi neento zonke ezikulo, lowo, eyiNkosi nje yezulu nomhlaba, akahlali zitempileni zenziwe ngazandla.', 'XHO22'),
(7, 17, 25, 'Akaphiliswa zizandla zabantu, ngokoswele nto, inguye nje obanika bonke ubomi, nomphefumlo, nazo zonke izinto.', 'XHO22'),
(7, 17, 26, 'Kanti wazenza ngagazi-nye zonke iintlanga zabantu, ukuba zime phezu kwawo wonke umhlaba, emisa amaxesha amisiweyo ngenxa engaphambili, nemida yokuma kwazo;', 'XHO22'),
(7, 17, 27, 'ukuze bayifune iNkosi, ukuba kambe bangaphuthaphutha bayifumane, nakuba ingekude kuthi sonke ngabanye.', 'XHO22'),
(7, 17, 28, 'Kuba kuyo sihleli, siyashukuma, sikho; njengokuba neembongi ezithile zakowenu zitshilo ukuthi, Kuba nathi siyinzala yakhe.', 'XHO22'),
(7, 17, 29, 'Siyinzala kaThixo nje ke, masingacingi ukuba ubuthixo bufana negolide nesilivere nelitye, into eqingqwe ngobungcibi nangengcinga yomntu.', 'XHO22'),
(7, 17, 30, 'Loo maxesha ke oko kungazi, uThixo wawayeka ngabomi; kungoku uyala abantu bonke ezindaweni zonke ukuba baguquke;', 'XHO22'),
(7, 17, 31, 'Ngokuba umise umhla, aza kuligweba ngawo elimiweyo ngobulungisa, ngendoda ayimiseleyo; abanike isiqinisekiso bonke abantu ngokumvusa kwabafileyo.', 'XHO22'),
(7, 17, 32, 'Ke kaloku, bakuva ngokuvuka kwabafileyo, inxenye yayihlekisa ngaloo nto, abanye bathi, Sobuya sikuve wena ngale nto.', 'XHO22'),
(7, 17, 33, 'Wemka ke uPawulos phakathi kwabo.', 'XHO22'),
(7, 17, 34, 'Kodwa amadoda athile anamathele kuye, akholwa, anoDiyonisiyo waseAreyopago, nomfazi ogama linguDamari, nabanye ndawonye nabo.', 'XHO22'),
(7, 18, 1, 'Ke kaloku, emva kwezi zinto, wahlukana uPawulos neAtene, weza eKorinte.', 'XHO22'),
(7, 18, 2, 'Wafumana umYuda othile wasePonto ngokuzalwa, ogama linguAkwila, esandula ukuvela eItali, noPrisila umkakhe, weza kubo; (ngenxa yokuba uKlawudiyo ebemise ukuthi, makahlukane neRoma onke amaYuda); weza kubo.', 'XHO22'),
(7, 18, 3, 'Kuthe ke, ngenxa yokuba belilinye ishishini labo, waselehlala nabo esebenza, kuba babengabenzi beentente, ishishini labo.', 'XHO22'),
(7, 18, 4, 'Wayexoxa ke endlwini yesikhungu iisabatha ngeesabatha zonke, ezamela ukweyisa amaYuda namaGrike.', 'XHO22'),
(7, 18, 5, 'Ke kaloku, bakuhla bevela eMakedoni ooSilas noTimoti, uPawulos waxinwa ngumoya, waqononondisa kumaYuda ukuba uYesu lo nguye uKristu.', 'XHO22'),
(7, 18, 6, 'Uthe ke, akuchasa, akunyelisa, wavuthulula iingubo zakhe, wathi kuwo, Igazi lenu malibe phezu kwentloko yenu; mna ndihlambulukile; kuthabathela kweli xesha, ndiya kuya kuzo iintlanga.', 'XHO22'),
(7, 18, 7, 'Esukile ke apho, waya kungena endlwini yothile, ogama linguYusto, umhlonelayo uThixo, ondlu yakhe ibikufuphi nendlu yesikhungu.', 'XHO22'),
(7, 18, 8, 'Ke kaloku uKrispo, umphathi wendlu yesikhungu, wakholwa yiNkosi, nendlu yakhe yonke; nento eninzi yamaKorinte yakuva yakholwa, yabhaptizwa.', 'XHO22'),
(7, 18, 9, 'Yathi ke iNkosi ngombono ebusuku kuPawulos, Musa ukoyika, thetha, ungathi tu;', 'XHO22'),
(7, 18, 10, 'kuba mna ndinawe, kanjalo akukho namnye uya kukusa sandla, akuphathe kakubi; ngokuba ndinabantu abaninzi kulo mzi.', 'XHO22'),
(7, 18, 11, 'Wahlala khona ke unyaka oneenyanga ezintandathu, efundisa ilizwi likaThixo phakathi kwabo.', 'XHO22'),
(7, 18, 12, 'Ke kaloku, akubon’ ukuba uGaliyo wayelibamba kwelaseAkaya, amaYuda amvukela ngamxhelo mnye uPawulos, amzisa esihlalweni sokugweba,', 'XHO22'),
(7, 18, 13, 'besithi, Lo ukweyisa abantu ukuba bamnqule uThixo ngokunxamnye nomthetho.', 'XHO22'),
(7, 18, 14, 'Ke kaloku, xa uPawulos aza kuvula umlomo, wathi uGaliyo kumaYuda, Ukuba ibiyinto yentswela-bulungisa, nokuba ibiyinto yobutshinga, nokuba ibiyeyobutshijolo, maYuda, ngekungene kuni;', 'XHO22'),
(7, 18, 15, 'Kodwa ukuba yimbuzwano ngelizwi, namagama, nomthetho wenu, zikhangeleleni nina; kuba mna andiyi kuba ngumgwebi wezinto ezinjalo.', 'XHO22'),
(7, 18, 16, 'Waza wabagxotha esihlalweni sokugweba.', 'XHO22'),
(7, 18, 17, 'Ambamba ke onke amaGrike uSostene, umphathi wendlu yesikhungu, ambetha phambi kwesihlalo sokugweba. Ke yena uGaliyo akakhathalelanga nanye yezo zinto.', 'XHO22'),
(7, 18, 18, 'Ke kaloku uPawulos ukhe wahlala imihla eyaneleyo. Waza, ebabulisile abazalwana, wemka ngomkhombe waya kwelaseSiriya, enoPrisila noAkwila; eyichebile intloko eKenkreya, kuba waye enesibambiso.', 'XHO22'),
(7, 18, 19, 'Wehla ke wafika e-Efese, wabashiya apho, wangena endlwini yesikhungo, waxoxa namaYuda.', 'XHO22'),
(7, 18, 20, 'Uthe ke, akumcela ukuba akhe ahlale umzuzu kuwo, akavuma;', 'XHO22'),
(7, 18, 21, 'Wababulisa ke esithi, Nokuba kutheni, ndimelwe kukuwenzela eYerusalem umthendeleko ozayo; kodwa ndobuya ndibuyele kuni, ukuba uThixo uyavuma. Waza wemka ngomkhombe e-Efese.', 'XHO22'),
(7, 18, 22, 'Wehla eKesareya, wenyuka waya kubulisa ibandla, wehla waya kwa-Antiyokwe.', 'XHO22'),
(7, 18, 23, 'Enze ixesha elithile khona, wemka, walityhutyha ilizwe laseGalati, nelaseFrigi, ebomeleza bonke abafundi.', 'XHO22'),
(7, 18, 24, 'Ke kaloku umYuda othile ogama linguApolo, owazalelwa eAleksandriya, indoda eliciko, enobunkunkqele ezibhalweni, weza e-Efese.', 'XHO22'),
(7, 18, 25, 'Lo mntu wayeyifundisiwe ngomlomo indlela yeNkosi; waye evutha ngumoya wathetha, efundisa ngokucokisekileyo izinto zeNkosi, esazi kakuhle ubhaptizo lukaYohane lodwa.', 'XHO22'),
(7, 18, 26, 'Waza yena wathetha ngokungafihlisiyo endlwini yesikhungu. Bathe ke bakumva ooAkwila noPrisila, bamthabathela kubo, bamtyhilela ngokucokisekileyo ngakumbi indlela kaThixo.', 'XHO22'),
(7, 18, 27, 'Ke kaloku, akubon’ ukuba ungadlula aye kwelaseAkaya, abazalwana babhala, bababongoza abafundi ukuba bamamkele; owathi akufika, wabanceda kakhulu abo bakholwayo ngalo ubabalo;', 'XHO22'),
(7, 18, 28, 'kuba waweyisa, wawancamisa ekuhleni amaYuda, ebonakalalisa ngazo izibhalo ukuba uYesu lo unguye uKristu.', 'XHO22'),
(7, 19, 1, 'Ke kaloku kwathi, xa uApolo eseKorinte, uPawulos ewatyhutyhile amacala asentla, weza e-Efese;', 'XHO22'),
(7, 19, 2, 'Wathi kubo, uMoya oyiNgcwele namamkela na, nakholwayo nje? Bathe ke bona kuye, Asizanga sive nokuva ukuba kukho uMoya oyiNgcwele.', 'XHO22'),
(7, 19, 3, 'Wathi kubo, Nabhaptizelwa ngantoni na phofu? Bathe ke bona, Elubhaptizweni lukaYohane.', 'XHO22'),
(7, 19, 4, 'Wathi uPawulos, uYohane okunene wabhaptiza ngobhaptizo lwenguquko, esithi ebantwini, mabakholwe kulowo uzayo emva kwakhe, oko kukuthi, kuKristu Yesu.', 'XHO22'),
(7, 19, 5, 'Bathe ke bona bakukuva oko, babhaptizwa egameni leNkosi uYesu.', 'XHO22'),
(7, 19, 6, 'Akuba uPawulos ebabeke izandla, wehla uMoya oyiNgcwele phezu kwabo; Baye bethetha ngeelwimi, beprofeta.', 'XHO22'),
(7, 19, 7, 'Aye onke amadoda engathi alishumi elinababini.', 'XHO22'),
(7, 19, 8, 'Ke kaloku, wangena endlwini yesikhungu, wathetha ngokungafihlisiyo iinyanga zantathu, exoxa nabantu, ebeyisela kwizinto ezingabo ubukumkani bukaThixo.', 'XHO22'),
(7, 19, 9, 'Kodwa abanye bakuba beqaqadekile, bengakholwa, bethetha kakubi ngayo iNdlela leyo phambi kwesihlwele, wemka kubo, wabahlula abafundi bakhe, exoxa imihla ngemihla esikolweni sikaTirano.', 'XHO22'),
(7, 19, 10, 'Loo nto ke yenzeka iminyaka emibini; ngokokude bonke abemiyo kwelaseAsiya balive ilizwi leNkosi, amaYuda kwanamaGrike.', 'XHO22'),
(7, 19, 11, 'uThixo ke wenza imisebenzi yamandla ebalulekileyo ngesandla sikaPawulos.', 'XHO22'),
(7, 19, 12, 'Kaloku iiqhiya neefaskoti ziphuma emzimbeni wakhe, zisiwe kwabagulayo, zisuke kubo izifo, bathi noomoya abakhohlakeleyo baphume kubo.', 'XHO22'),
(7, 19, 13, 'Ke kaloku abathile bakumaYuda abhadulayo, angabafungisi, bazamela ukulibiza igama leNkosi uYesu phezu kwabanoomoya abakhohlakeleyo, besithi, Sinifungisa uYesu lowo uvakaliswayo nguPawulos.', 'XHO22'),
(7, 19, 14, 'Kwaye kukho ke nyana bathile basixhenxe bakaSkeva, umYuda, umbingeleli omkhulu, ababekwenza oko.', 'XHO22'),
(7, 19, 15, 'Waphendula ke umoya okhohlakeleyo, wathi, uYesu ndiyamazi, noPawulos ndiyamazi kakuhle; Ningoobani na ke?', 'XHO22'),
(7, 19, 16, 'Wesuka umntu obukuye umoya okhohlakeleyo, wabatsibela, wabagagamela, wabeyisa, ngokokude babaleke baphume kuloo ndlu bengxwelerhiwe, beze.', 'XHO22'),
(7, 19, 17, 'Kwathi ke oko kwazeka kuwo onke amaYuda, kwanakumaGrike amiyo e-Efese; bawelwa kukoyika bonke, lenziwa likhulu igama leNkosi uYesu.', 'XHO22'),
(7, 19, 18, 'Yaye into eninzi yabakholiweyo isiza, izivuma izixela izenzo zabo.', 'XHO22'),
(7, 19, 19, 'Yathi ke nento eyaneleyo yabasebenza izenzo zobugqi, yazizisa ndaweni-nye iincwadi zayo, bawayazitshisa phambi kwabo bonke; bawabala amaxabiso azo, bafumana engamawaka amashumi mahlanu esilivere.', 'XHO22'),
(7, 19, 20, 'Lenjenjalo ke ilizwi leNkosi ukwanda ngamandla nokweyisa.', 'XHO22'),
(7, 19, 21, 'Ke kaloku, zakuzaliseka ezi zinto, wamisa emoyeni wakhe uPawulos ukuba athi, elityhutyhile elaseMakedoni nelaseAkaya, aye eYerusalem, esithi, Emveni kokuba ndithe ndakhona, ndimelwe kukuba ndibone neRoma.', 'XHO22'),
(7, 19, 22, 'Ke ngoko wathumela eMakedoni bababini kwabalungiselelayo, ooTimoti noErasto; yena ke ukhe wahlala umzuzu kwelaseAsiya.', 'XHO22'),
(7, 19, 23, 'Ke kaloku kwabakho ngelo xesha inkathazeko engencinane ngayo iNdlela le.', 'XHO22'),
(7, 19, 24, 'Kuba uthile, ogama linguDemetriyo, umkhandi wesilivere, obesenza iitempile zesilivere zika-Artemis, wayezizuzisa iingcibi inzuzo enkulu.', 'XHO22'),
(7, 19, 25, 'Waziqukela ndawonye, kwanabasebenzi bezinto ezinjalo, wathi, Madoda, niyazi kakuhle ukuba buphuma kule nzuzo ubutyebi bethu.', 'XHO22'),
(7, 19, 26, 'Kananjalo niyabona, niyeva, ukuba akuse-Efese kodwa, kuphantse ukuba kulo lonke elaseAsiya, athe lo Pawulos waphambukisa isihlwele esaneleyo, ngokusoyisa ngokusoyisa, esithi, asingoothixo abo benziwa ngezandla.', 'XHO22'),
(7, 19, 27, 'Ngoko ke asiyiyo lo msebenzi wethu wodwa osisichengeni sokungabi nanto; kwanetempile yothixokazi omkhulu uArtemis ibe yinto engento, butshabalale nobungangamela bakhe, oqubuda kulo lonke elaseAsiya nelimiweyo.', 'XHO22'),
(7, 19, 28, 'Bakukuva ke oko, bazala ngumsindo, badanduluka, besithi, Mkhulu uArtemis wabase-Efese!', 'XHO22'),
(7, 19, 29, 'Wazala umzi uphela sisidubedube; besuka indulumbane ngamxhelo mnye endaweni yokubonela, bamthi hlasi uGayo noAristarko, abelaseMakedoni, ababehamba noPawulos.', 'XHO22'),
(7, 19, 30, 'Ke kaloku, akubon’ ukuba uPawulos angangena esihlweleni, abamvumela abafundi.', 'XHO22'),
(7, 19, 31, 'Abathile ke bakubaphathi belaseAsiya, benobuhlobo kuye, bathumela kuye, bambongoza ukuba angazinikeli endaweni yokubonela.', 'XHO22'),
(7, 19, 32, 'Abanye ke ngoko babenkqangaza enye into, abanye yimbi, kuba ibandla labe lidubadubekile; baye abaninzi bengazi ukuba babuthelene ngenxa yantoni na.', 'XHO22'),
(7, 19, 33, 'Baza bamrholela uAlesandire esihlweleni, amaYuda emtyhalela phambili. Uwangawangisile ke uAlesandire ngesandla, wafuna ukuziphendulela ebantwini.', 'XHO22'),
(7, 19, 34, 'Kodwa bakuqonda ukuba ungumYuda, benza inzwinini enye bonke bephela, benkqangaza umzuzu omaxa amabini, besithi, Mkhulu uArtemis wabase-Efese!', 'XHO22'),
(7, 19, 35, 'Esizolisile ke umbhali isihlwele, wathi, Madoda ase-Efese, nguwuphi na umntu ongaziyo, ukuba umzi wase-Efese ungumkhonzi wothixokazi omkhulu uArtemis, nowomfanekiselo owawa kuZeyus?', 'XHO22'),
(7, 19, 36, 'Ekubeni ngoko ezi zinto zingenakuphikiswa, nifanele ukuthi cwaka, ningenzi nto ngokungxama.', 'XHO22'),
(7, 19, 37, 'Kuba niwazise apha la madoda, engebaphangi bamabandla kaThixo, nabanyelisi bothixokazi wenu.', 'XHO22'),
(7, 19, 38, 'Ukuba ke ngoko uDemetriyo neengcibi ezinaye banendawo emntwini, zikho iimini zamatyala, akho namabamba; mabamangalelane.', 'XHO22'),
(7, 19, 39, 'Ke ukuba nifuna into nganto zimbi, yoconjululwa kwesesikweni intlanganiso.', 'XHO22'),
(7, 19, 40, 'Kuba sisesichengeni sokumangalelwa ngenxa yesaqunge sanamhlanje, kungekho sizathu sakuziphendulela ngale ngxokolo.', 'XHO22'),
(7, 19, 41, 'Akutsho, wayindulula intlanganiso.', 'XHO22'),
(7, 20, 1, 'Ke kaloku, sakuba sidambile isiphithiphithi eso, uPawulos wababizela kuye abafundi bakhe, wababulisa, wemka waya kwelaseMakedoni.', 'XHO22'),
(7, 20, 2, 'Ewatyhutyhile ke loo macala, ebayalile kakhulu, weza kwelamaGrike.', 'XHO22'),
(7, 20, 3, 'Wahlala apho iinyanga zantathu. Ke kaloku, xa abezalelwe kuye amaYuda, xa aza kuya ngomkhombe kwelaseSiriya, wagqiba kwelokuba acande kwelaseMakedoni ngokubuya.', 'XHO22'),
(7, 20, 4, 'Waphelekelelwa ke, kwada kweza kwelaseAsiya nguSopatro waseBhereya; nabaseTesalonika, uAristarko noSekundo; noGayo waseDerbhe, noTimoti; kwabaseAsiya, ooTikiko noTrofimo.', 'XHO22'),
(7, 20, 5, 'Aba ke bona basandulela, basilinda eTrowa.', 'XHO22'),
(7, 20, 6, 'Semka ngomkhombe eFilipi, emva kwemihla yezonka ezingenagwele, seza kubo eTrowa ngeentsuku ezintlanu; sahlala khona iintsuku zasixhenxe.', 'XHO22'),
(7, 20, 7, 'Ke kaloku, ngolokuqala usuku lweveki, bakubon’ ukuba abafundi bahlanganisene ukuba baqhekeze isonka, uPawulos wayeshumayela kubo, eza kumka ngengomso; Wayiqhuba intetho yakhe kwada kwaphakathi kobusuku.', 'XHO22'),
(7, 20, 8, 'Kwaye kukho izibane ezininzi kwigumbi eliphezulu, apho babehlanganisene khona.', 'XHO22'),
(7, 20, 9, 'Kwakuhleli efestileni ndodana ithile ingama linguYutiko, isindwa bubuthongo obunzulu;', 'XHO22'),
(7, 20, 10, 'Wehla ke uPawulos, waziphosa phezu kwayo, wayiwola, wathi, Musani ukuzikhathaza; ngokuba umphefumlo wakhe ukuye.', 'XHO22'),
(7, 20, 11, 'Wenyuka ke, waqhekeza isonka, wadla; kananjalo waxoxa ngokwaneleyo kwada kwasa, wemka njalo.', 'XHO22'),
(7, 20, 12, 'Bamzisa ke umfana ehleli, bethuthuzelekile noko.', 'XHO22'),
(7, 20, 13, 'Ke kaloku thina sandulela ukuya emkhombeni, sasinga eAso, apho besiza kumthabatha khona uPawulos; kuba ebemisele ngokunjalo, yena eza kuhamba ngeenyawo.', 'XHO22'),
(7, 20, 14, 'Uthe akuhlangana nathi eAso, samthabatha, saya kufika eMitilene.', 'XHO22'),
(7, 20, 15, 'Semka apho ngomkhombe, ngengomso safika malungana neKiyo; Sathi ngengomso safika eSamo, sahlala eTrogiliyo; saza ngengomso seza eMileto.', 'XHO22'),
(7, 20, 16, 'Kuba uPawulos wagqiba kwelokuba kudlulwe ngomkhombe e-Efese, ukuze angabi nakwenza xesha lide kwelaseAsiya; kuba wayekhawulezele ukuthi, ukuba unako, awenze umhla wepentekoste eYerusalem.', 'XHO22'),
(7, 20, 17, 'EseMileto wathumela e-Efese, wabiza amadoda amakhulu alo ibandla.', 'XHO22'),
(7, 20, 18, 'Befikile ke kuye, wathi kubo, Niyazi ukuba bendihleli nani ngamaxesha onke, kususela kwimini yokuqala endafikayo kwelaseAsiya;', 'XHO22'),
(7, 20, 19, 'ndikhonza iNkosi ngako konke ukuthobeka kwentliziyo, ndineenyembezi ezininzi, nokulingwa okwandihlelayo ngokulalela amaYuda;', 'XHO22'),
(7, 20, 20, 'nokokuba andinifihlelanga nto, ndanibonisa nje, ndanifundisa esidlangalaleni nakwizindlu ngezindlu,', 'XHO22'),
(7, 20, 21, 'ndiqononondisa kuma Yuda nakumaGrike ukuguqukela kuThixo, nokukholwa kuyo iNkosi yethu, uYesu Kristu.', 'XHO22'),
(7, 20, 22, 'Nangoku, yabonani, mna ndiya eYerusalem, ndibotshiwe nguMoya, ndingazazi izinto eziya kundihlela khona;', 'XHO22'),
(7, 20, 23, 'ndanele ukwazi ukuba uMoya oyiNgcwele uqononondisa kwimizi ngemizi, esithi ndilindwe ngamakhamandela naziimbandezelo.', 'XHO22'),
(7, 20, 24, 'Kambe ke ubomi bam andibugqali njengobalulekileyo kum, ukuze ndilufeze ugqatso lwam ngovuyo, nolungiselelo endalwamkelayo lweNkosi uYesu, lokungqina iindaba ezilungileyo zobabalo lukaThixo.', 'XHO22'),
(7, 20, 25, 'Nangoku, yabonani, ndiyazi mna, ukuba nina nonke ndahambayo phakathi kwenu, ndibuvakalisa ubukumkani bukaThixo, anisayi kubuya nibubone ubuso bam.', 'XHO22'),
(7, 20, 26, 'Kungoko ndingqinayo kuni ngayo le mini yanamhla, ukuba mna ndimsulwa egazini labo bonke;', 'XHO22'),
(7, 20, 27, 'Kuba ndanixelela lonke icebo likaThixo.', 'XHO22'),
(7, 20, 28, 'Zilumkeleni ngoko, nawo wonke umhlambi, athe uMoya oyiNgcwele wanimisa ukuba ngabaveleli bawo, ukuba nilaluse ibandla likaThixo, awazizuzelayo ngelilelakhe igazi.', 'XHO22'),
(7, 20, 29, 'Kuba mna ndiyakwazi oku, ukuba emva kokumka kwam, kuya kungena phakathi kwenu iingcuka ezibawayo, zingawucongi umhlambi.', 'XHO22'),
(7, 20, 30, 'Nakuni ngokwenu kuya kusuka amadoda athetha izinto ezigwenxa, ukuba abarhole abafundi emva kwawo.', 'XHO22'),
(7, 20, 31, 'Kungoko ndithi, Phaphani, nikhumbule, ukuba iminyaka emithathu, ubusuku nemini, andiyekanga ukululeka bonke ngabanye, ndineenyembezi.', 'XHO22'),
(7, 20, 32, 'Ngoku ke, bazalwana, ndiyaniyaleza kuThixo, nakwilizwi lobabalo lwakhe, enako ukunakha, aninike ilifa phakathi kwabo bonke abangcwalisiweyo.', 'XHO22'),
(7, 20, 33, 'Andinqwenelanga silivere nagolide nangubo kabani.', 'XHO22'),
(7, 20, 34, 'Nani ngokwenu niyazi ukuba ezi zandla zazisebenzela iintswelo zam, nezababenam.', 'XHO22'),
(7, 20, 35, 'Ndanibonisa zonke izinto, ukuba ngokwenjenjalo ukubulaleka nimelwe kukuthi niyisize imilwelwe, nikhumbule namazwi eNkosi uYesu, yokuba yona yathi, Ukupha kunoyolo ngaphezu kokuphiwa.', 'XHO22'),
(7, 20, 36, 'Akuba etshilo, waguqa ngamadolo, wathandaza kunye nabo bonke.', 'XHO22'),
(7, 20, 37, 'Basuka balila kakhulu bonke, bawa entanyeni kaPawulos, bamanga,', 'XHO22'),
(7, 20, 38, 'bebuhlungu kakhulu, ngokukodwa lilizwi alithethileyo lokuthi, abasayi kubuya babubone ubuso bakhe. Bamkhapha ke bamsa emkhombeni.', 'XHO22'),
(7, 21, 1, 'Ke kaloku kwathi, sindulukile kubo, sindulukile, seza ngqo eKose; ngengomso safika eRodo, sesuka apho seza ePatara.', 'XHO22'),
(7, 21, 2, 'Sathi, siyifumene inqanawa ewelela eFenike, sangena kuyo, sanduluka.', 'XHO22'),
(7, 21, 3, 'Ke kaloku, sakuba siyifumene iSipro, sayishiya ngasekhohlo, saya ngomkhombe kwelaseSiriya, sehla eTire; kuba umkhombe uza kuwuthula khona apho umthwalo.', 'XHO22'),
(7, 21, 4, 'Sathi sibafumene abafundi, sahlala khona iintsuku zasixhenxe; bona bathi kuPawulos ngoMoya, makangenyuki aye eYerusalem.', 'XHO22'),
(7, 21, 5, 'Ke kaloku, xa sizigqibileyo iintsuku ezo, saphuma sahamba; basiphelekelela bonke, ndawonye nabafazi nabantwana, sada sangaphandle komzi; saguqa ngamadolo elunxwemeni, sathandaza.', 'XHO22'),
(7, 21, 6, 'Sathi sabulisana, sakhwela enqanaweni; babuya bagoduka.', 'XHO22'),
(7, 21, 7, 'Ke kaloku, silufezile uhambo lwethu ngohambo lwethu ukusuka eTire, safika eTolemayi, sababulisa abazalwana, sahlala nabo usuku lwalunye.', 'XHO22'),
(7, 21, 8, 'Ke kaloku ngengomso, thina besinoPawulos, semka seza eKesareya. Sangena endlwini kaFilipu, umshumayeli weendaba ezilungileyo, obengowabaya basixhenxe; wahlala naye.', 'XHO22'),
(7, 21, 9, 'Lowo ke wayeneentombi ezine, ziseziintombi, ziprofeta.', 'XHO22'),
(7, 21, 10, 'Sakubon’ ukuba ke sihleli khona iintsuku ezininzi, kwehla kwelakwaYuda mprofeti uthile, ugama linguAgabho.', 'XHO22'),
(7, 21, 11, 'Wathi akufika kuthi, wawuthabatha umbhinqo kaPawulos, wazibopha izandla neenyawo, wathi, Utsho uMoya oyiNgcwele ukuthi, Aya kwenjenjalo ukumbopha indoda engowalo mbhinqo amaYuda eYerusalem, amnikele entolongweni, izandla zeentlanga.', 'XHO22'),
(7, 21, 12, 'Sakuva ke ezi zinto, sambongoza thina kwanabakuloo ndawo, ukuba angenyuki aye eYerusalem.', 'XHO22'),
(7, 21, 13, 'Waphendula uPawulos wathi, Nenzani na ukulila niyityumze intliziyo yam? kuba mna ndingalungele kubotshwa kodwa; ndilungele nokufa eYerusalem, ngenxa yegama leNkosi uYesu.', 'XHO22'),
(7, 21, 14, 'Akuba ke engoyiseki, sathi cwaka, sisithi, Makwenzeke ukuthanda kweNkosi.', 'XHO22'),
(7, 21, 15, 'Ke kaloku, emva kwezo ntsuku, sabopha iinqwelo, senyuka saya eYerusalem.', 'XHO22'),
(7, 21, 16, 'Kwahamba nathi abathile nakubafundi baseKesareya, besisa thina kuMnaso waseSipro, obengumfundi kwakudala, esaba lundwendwe lwakhe.', 'XHO22'),
(7, 21, 17, 'Ke kaloku, sakufika eYerusalem, basamkela ngovuyo abazalwana.', 'XHO22'),
(7, 21, 18, 'Ngengomso ke uPawulos wangena kunye nathi kuYakobi; ayekho ke onke amadoda amakhulu.', 'XHO22'),
(7, 21, 19, 'Ubabulisile, wamana ebacacisela zonke izinto awazenzayo uThixo phakathi kweentlanga, ngalo ulungiselelo lwakhe.', 'XHO22'),
(7, 21, 20, 'Bathe ke bona bakukuva oko, bayizukisa iNkosi, batsho nokuthi kuye, Uyabona, mzalwana, oko angako amawakawaka amaYuda akholiweyo; baye bonke bewuzondelele umthetho;', 'XHO22'),
(7, 21, 21, 'Atyelwe ke ngawe, ukuba uyawafundisa onke amaYuda aphakathi kweentlanga, ukuba ahlukane noMoses, usithi, makangabalusi abantwana bawo, angahambi ngawo amasiko.', 'XHO22'),
(7, 21, 22, 'Kuyintoni na ke ngoko? Kumelwe kuhlanganisana inkitha; kuba baya kuva ukuba ufikile.', 'XHO22'),
(7, 21, 23, 'Ngoko yenza le nto sikuxelela yona: Kukho kuthi apha amadoda amane, anesibambiso azibophe ngaso;', 'XHO22'),
(7, 21, 24, 'Wathabathe wona, uzenze nyulu kunye nawo, ube nokuhlawulisa, ukuze achebe iintloko zawo; bazi bonke ukuba azinto zanto iindaba ezilungileyo ezi uzisiwe ngawe; kodwa ukuba nawe uhambe ngokufanelekileyo, uwugcina umthetho.', 'XHO22'),
(7, 21, 25, 'Kodwa ngazo iintlanga ezikholiweyo, sabhala sagqiba kwelokuba zingabambi nto injalo; kuphela ibe kukuzigcina entweni ebingelelwe izithixo, nasegazini, nasentweni ekrwitshiweyo, nasembulweni.', 'XHO22'),
(7, 21, 26, 'Waza uPawulos wawathabatha ke amadoda lawo, wathi ngengomso, ezenze nyulu kunye nawo, wangena etempileni, eqondisisa inzaliseko yeentsuku zokwenza nyulu, ude usondezwe umnikelo ngenxa yabo bonke ngabanye.', 'XHO22'),
(7, 21, 27, 'Ke kaloku, xa iintsuku ezisixhenxe ezo beziza kuphela, athi amaYuda avela kwelaseAsiya, akumbona etempileni, asidubaduba sonke isihlwele, amsa izandla,', 'XHO22'),
(7, 21, 28, 'enkqangaza esithi, Madoda, maSirayeli, sizani. Nanku lo mntu ufundisa bonke ezindaweni zonke okuchasene nabantu, nomthetho, nale ndawo; othe ngaphezu koko wawangenisa amaGrike etempileni, wayenza inqambi le ndawo ingcwele.', 'XHO22'),
(7, 21, 29, '(Kuba abebone ngaphambili uTrofimo wase-Efese, ndawonye naye esixekweni, abeba uPawulos umngenise etempileni.)', 'XHO22'),
(7, 21, 30, 'Washukuma umzi uphela, babalekela ndawonye abantu; bamthabatha uPawulos, bamrholela ngaphandle kwetempile; zavalwa kwaoko iingcango.', 'XHO22'),
(7, 21, 31, 'Ke kaloku, xa babeza kumbulala, lwafika udaba kumthetheli-waka webutho, lokuba iYerusalem iphela idubadubekile;', 'XHO22'),
(7, 21, 32, 'owathi kwangelo lixa wathabatha amasoldati nabathetheli-khulu, wabaleka, wehla waya kubo. Bakumbona ke umthetheli-waka namasoldati, bayeka ukumbetha uPawulos.', 'XHO22'),
(7, 21, 33, 'Waza wasondela umthetheli-waka, wambamba, wathi makakhonkxwe ngemixokelelwane emibini; wayebuza ukuba ungubani na, nokuba wenze ntoni na.', 'XHO22'),
(7, 21, 34, 'Abanye abantu benkqangaza enye into, abanye yimbi. Akuba engenako ke ukuyazi inyaniso ngenxa yesiphithiphithi eso, wathi makasiwe enqabeni yomkhosi.', 'XHO22'),
(7, 21, 35, 'Uthe akufika kwizinyuko, wathwalwa ngamasoldati, ngenxa yesiphithiphithi sabantu;', 'XHO22'),
(7, 21, 36, 'kuba yayilandela inkitha yabantu, inkqangaza isithi, Msuseni!', 'XHO22'),
(7, 21, 37, 'Xa ke wayeza kungeniswa enqabeni yomkhosi, wathi uPawulos kumthetheli-waka, Kuvumelekile na ukuba ndithethe kuwe? Wathi, Uyasazi na isiGrike?', 'XHO22'),
(7, 21, 38, 'Asinguye na laa mYiputa, wathi ngaphambi kwale mihla wenza isaqunge, wakhokelela entlango amawaka amane amadoda angababulali?', 'XHO22'),
(7, 21, 39, 'Uthe ke yena uPawulos, Mna ndingumYuda waseTarso, umzi waseKiliki, ummi womzi ongadume ngako.', 'XHO22'),
(7, 21, 40, 'Ke kaloku, akubon’ ukuba umvumele, wema uPawulos ematyeni okunyuka, wawangawangisa ngesandla kubo abantu. Uthe ke, kwathi cwaka kakhulu, wathetha kubo ngesiHebhere, esithi,', 'XHO22'),
(7, 22, 1, 'Madoda, bazalwana, nani bobawo, kuphulaphuleni ukuziphendulela kwam kuni ngoku.', 'XHO22'),
(7, 22, 2, 'Bakuva ke ukuba udanduluka ngesiHebhere kubo, bazola okunye.', 'XHO22'),
(7, 22, 3, 'Mna okunene ndingumntu ongumYuda, owazalelwa eTarso, umzi waseKiliki; ukanti ndakhulela kulo mzi ezinyaweni zikaGamaliyeli, ndafundiswa ngomyalelo ogqibeleleyo wemithetho yooyihlo, ndinenzondelelo kuThixo, njengokuba ninjalo nina nonke namhlanje.', 'XHO22'),
(7, 22, 4, 'Ndayitshutshisa le Ndlela, ndada ndesa ekufeni, ndibopha, ndifaka ezintolongweni amadoda kwanabafazi;', 'XHO22'),
(7, 22, 5, 'njengokuba nombingeleli omkhulu endingqinela, nayo yonke intlanganiso yamadoda amakhulu; endamkela kubo iincwadi zokuya kubazalwana, ndaya eDamasko, ndiya kuzisa ababekhona bebotshiwe eYerusalem, ukuze babethwe.', 'XHO22'),
(7, 22, 6, 'Ke kaloku kwathi, ndihamba, ndisondela eDamasko, malunga emini enkulu, ngesiquphe ndabanekelwa ngeenxa zonke lukhanyiselo olukhulu luphuma ezulwini.', 'XHO22'),
(7, 22, 7, 'Ndawa emhlabeni, ndeva ilizwi lisithi kum, Sawule, Sawule, unditshutshiselani na?', 'XHO22'),
(7, 22, 8, 'Ndaphendula ke mna ndathi, Ungubani na, Nkosi? Yathi kum, Mna ndinguYesu waseNazarete, omtshutshisayo wena.', 'XHO22'),
(7, 22, 9, 'Bona ke ababenam balubona okunene ukhanyiselo olo, besuka boyika; ke abalivanga ilizwi lalowo wayethetha nam.', 'XHO22'),
(7, 22, 10, 'Ndathi ke, Ndithini na, Nkosi? Yathi ke iNkosi kum, Vuka, uye eDamasko; woxelelwa khona ngazo zonke izinto ozimiselweyo ukuba uzenze.', 'XHO22'),
(7, 22, 11, 'Ndakuba ke ndingenakondela ngenxa yobuqaqawuli bolo lukhanyiselo, ndarholwa ngesandla ngabo babenam, ndafika eDamasko.', 'XHO22'),
(7, 22, 12, 'Ke kaloku, uHananiya uthile, indoda emhlonelayo uThixo ngokomthetho, engqinelwe kakuhle ngawo onke amaYuda ami khona,', 'XHO22'),
(7, 22, 13, 'weza kum, wema, wathi kum, Sawule, mzalwana, buya ubone. Ndaza ndajonga kuye kwangelo lixa.', 'XHO22'),
(7, 22, 14, 'Uthe ke yena, uThixo woobawo bethu ukumisile wena ukuba ukwazi ukuthanda kwakhe, umbone lowo uliLungisa, uve ilizwi eliphuma emlonyeni wakhe;', 'XHO22'),
(7, 22, 15, 'ngokuba uya kuba lingqina lakhe kubo bonke abantu, lezinto ozibonileyo nozivileyo.', 'XHO22'),
(7, 22, 16, 'Ngoku ke ulibelelani na? Suk’ ume, ubhaptizwe, uhlambulule izono zakho, unqula igama leNkosi.', 'XHO22'),
(7, 22, 17, 'Ke kaloku kwathi, ndakuba ndibuyele eYerusalem, ndakubon’ ukuba ndiyathandaza etempileni, ndaba sethongweni;', 'XHO22'),
(7, 22, 18, 'Ndayibona isithi kum, Khawuleza, uphume kamsinya eYerusalem; ngokuba bengayi kubamkela ubungqina bakho ngam.', 'XHO22'),
(7, 22, 19, 'Ndathi ke mna, Nkosi, bayazi kakuhle bona ukuba mna ndandibafaka entolongweni, ndibatyabula kuzo zonke izindlu zesikhungu abo bakholwayo kuwe;', 'XHO22'),
(7, 22, 20, 'naxeshikweni laphalazwayo igazi likaStefano, ingqina lakho, ndaye ndimi kufuphi nam, ndikuthakazela ukusikwa kwakhe, ndizigcinile iingubo zabo babemsika.', 'XHO22'),
(7, 22, 21, 'Yathi kum, Hamba; ngokuba mna ndiya kukuthuma kude kwiintlanga.', 'XHO22'),
(7, 22, 22, 'Bamphulaphula ke, wada wafika kwelo lizwi, baza baphakamisa amazwi abo besithi, Msuseni emhlabeni umntu onje; kuba akufanelekile ukuba adle ubomi.', 'XHO22'),
(7, 22, 23, 'Ke kaloku, bakubon’ ukuba bayakhala, bezilahla iingubo zabo, bephosa uthuli phezulu;', 'XHO22'),
(7, 22, 24, 'Wathi umthetheli-waka, makangeniswe enqabeni yomkhosi; wathi makancinwe ngekatsi; ukuze azi ukuba kungani na ukuba benjenje ukunkqangaza ngaye.', 'XHO22'),
(7, 22, 25, 'Ke kaloku, xa bamolulayo ngeentambo, wathi uPawulos kumthetheli-khulu obemi khona, Kuvumelekile na, ukuba nimtyakatye umntu ongumRoma, engagwetywanga nokugwetywa?', 'XHO22'),
(7, 22, 26, 'Evile ke umthetheli-khulu, waya wabika kumthetheli-waka, esithi, Yilumkele into oza kuyenza; kuba lo mntu ungumRoma.', 'XHO22'),
(7, 22, 27, 'Weza ke umthetheli-waka wathi kuye, Ndixelele, ungumRoma na? Wathi, Ewe.', 'XHO22'),
(7, 22, 28, 'Waphendula umthetheli-waka wathi, Le nkululeko ndayizuza ngexabiso elikhulu. Wathi ke uPawulos, Mna ndizelwe ndizelwe.', 'XHO22'),
(7, 22, 29, 'Basuka kuye kwaoko ngoko abo babeza kumncina; woyika ke nomthetheli-waka akuqonda ukuba ungumRoma, nangenxa yokuba ebembophile.', 'XHO22'),
(7, 22, 30, 'Ke kaloku ngengomso, enga angayazi inyaniso yento amangalelwe ngayo ngamaYuda, wamkhulula ezintanjeni, wathi, mabeze ababingeleli abakhulu, nayo yonke intlanganiso yabo yamatyala; waza wamhlisa uPawulos, wammisa phambi kwabo.', 'XHO22'),
(7, 23, 1, 'Waqwalasela ke uPawulos entlanganisweni yamatyala, wathi, Madoda, bazalwana, mna ndahlala ngokwalowo ungowakwaThixo, ndinesazela esilungileyo, kwada kwayile mini.', 'XHO22'),
(7, 23, 2, 'Umbingeleli omkhulu uHananiya wayalela abo babemi ngakuye ukuba bambethe emlonyeni.', 'XHO22'),
(7, 23, 3, 'Wathi uPawulos kuye, Uza kukubetha uThixo, ludongandini luqatyiweyo;', 'XHO22'),
(7, 23, 4, 'Bathi ke abo babemi khona, Uyamthuka na umbingeleli omkhulu kaThixo?', 'XHO22'),
(7, 23, 5, 'Wathi uPawulos, Bendingazi, bazalwana, ukuba ungumbingeleli omkhulu; kuba kubhaliwe kwathiwa, Uze ungathethi kakubi ngomphathi wabantu bakowenu.', 'XHO22'),
(7, 23, 6, 'Ke kaloku uPawulos, esazi ukuba elinye icala lelabaSadusi, elinye ke lingabaFarisi, wadanduluka entlanganisweni yamatyala, wathi, Madoda, bazalwana, mna ndingumFarisi, unyana womFarisi; umbuzo.', 'XHO22'),
(7, 23, 7, 'Akuba etshilo, kwabakho impambano phakathi kwabaFarisi nabaSadusi, yahlulelana inkitha leyo.', 'XHO22'),
(7, 23, 8, 'Kuba okunene abaSadusi babesithi, akukho luvuko, nasingelosi, namoya; kodwa abaFarisi bazivuma zombini ezo ndawo.', 'XHO22'),
(7, 23, 9, 'Kwabakho ke unkqangazo olukhulu; besuka ababhali becala labaFarisi balwa kakhulu, besithi, Asifumani nanye into embi kulo mntu; ke ukuba kuthethe kuye umoya, nokuba sisithunywa sezulu, masingalwi noThixo.', 'XHO22'),
(7, 23, 10, 'Kuthe ke, yakuba nkulu impambano, wathi umthetheli-waka esoyikela ukuba uPawulos angaqwengwa ngabo, wathi mawuhle umkhosi, umxwile phakathi kwabo, umzise enqabeni yawo.', 'XHO22'),
(7, 23, 11, 'Ngobusuku obulandelayo iNkosi yema ngakuye, yathi, Yomelela, Pawulos; kuba njengokuba waqononondisa ngam eYerusalem, kwangokunjalo umelwe kukuthi ungqine naseRoma.', 'XHO22'),
(7, 23, 12, 'Kwathi kwakusa, amaYuda athile enza ibhunga, azibopha ngesiqalekiso, esithi akayi kudla, akayi kusela, ade ambulale uPawulos.', 'XHO22'),
(7, 23, 13, 'Baye ke bengaphezu kwamashumi omane ababefungele ukuyenza loo nto.', 'XHO22'),
(7, 23, 14, 'Afika ke kubabingeleli abakhulu nakumadoda amakhulu, athi, Sizibophe ngesiqalekiso esikhulu, ukuba singadli nto, side simbulale uPawulos.', 'XHO22'),
(7, 23, 15, 'Ngoku ke nina bhekisani kumthetheli-waka kunye nentlanganiso yamatyala, ukuba amhlise, eze naye kuni ngomso, ngokungathi niza kuziqiqa ngokukhona kucokisekileyo iindawo ezithethwa ngaye; sibe ke thina, engekasondeli, sesilungele ukumbulala.', 'XHO22'),
(7, 23, 16, 'Ke kaloku, akukuva unyana wodade boPawulos ukumlalela kwabo, wafika wangena enqabeni yomkhosi, wambikela uPawulos.', 'XHO22'),
(7, 23, 17, 'uPawulos ubizele ke kuye omnye wakubathetheli-khulu, wathi, Yisa le ndodana kumthetheli-waka; kuba inento eza kuyibika kuye.', 'XHO22'),
(7, 23, 18, 'Uyithabathile ke yena, wayizisa kumthetheli-waka, wathi, uPawulos, umbanjwa undibizele kuye, wacela ukuba ndizise le ndodana kuwe; inento eza kuyithetha kuwe.', 'XHO22'),
(7, 23, 19, 'Wayibamba ke ngesandla umthetheli-waka, waya nayo ngasese, wayibuza wathi, Unantoni na oza kuyibika kum?', 'XHO22'),
(7, 23, 20, 'Yathi ke, AmaYuda avumelene ukukucela ukuba umhlise uPawulos, umse ngomso entlanganisweni yamatyala, ngokungathi aza kubuza ngokukhona kucokisekileyo into ngaye.', 'XHO22'),
(7, 23, 21, 'Ke wena uze ungabayekeleli; kuba bamlalele kubo phezu kwawo amadoda angaphezu kwamashumi omane, awabophe ngesifungo ukuba akayi kudla, akayi kusela, ade ambulale; ukanti sewulungile, ukhangele; ngenxa yedinga elivela kuwe.', 'XHO22'),
(7, 23, 22, 'Umthetheli-waka wayindulula ke indodana leyo, wayithethela ngelithi, mayingaxeleli bani, ukuba izibonakalalisile kuye ezi zinto.', 'XHO22'),
(7, 23, 23, 'Ebizele kuye abathetheli-khulu ababini, wathi, Lungisani amasoldati abe makhulu mabini, ukuba aye eKesareya, nabamahashe babe mashumi asixhenxe, nababinzi babe makhulu mabini, ngelesithathu ilixa lobusuku;', 'XHO22'),
(7, 23, 24, 'nilungise namaqegu, ukuze bamkhwelise uPawulos, bamse kuFelikis irhuluneli, asindile.', 'XHO22'),
(7, 23, 25, 'Wabhala ke incwadi ngolu hlobo.', 'XHO22'),
(7, 23, 26, 'uKlawudiyo Lisiya uyabulisa kuFelikis irhuluneli edume kunene.', 'XHO22'),
(7, 23, 27, 'Le ndoda yayibanjwe ngamaYuda, iza kubulawa ngawo, koko ndathi gaxa kuwo ndinomkhosi, ndayihlangula, ndakuqonda ukuba ingumRoma.', 'XHO22'),
(7, 23, 28, 'Ndakuba ke ndingalazi ityala abeyimangalela ngalo, ndayihlisa, ndayisa entlanganisweni yawo yamatyala;', 'XHO22'),
(7, 23, 29, 'Ndayifumana imangalelwe ngeembuzwano zomthetho wawo, kodwa ingenalo nalinye ityala elilingene ukufa, nokufakwa iintambo.', 'XHO22'),
(7, 23, 30, 'Ke kaloku, kwakubon’ ukuba ndilihletyelwe iyelenqe ebeliza kwenziwa ngayo le ndoda ngamaYuda, ndithumele kuwe kwangelo lixa, ndathi nakubamangaleli bayo, mabazithethe kuwe iindawo abanazo ngakuyo. Sala kakuhle.', 'XHO22'),
(7, 23, 31, 'Aza amasoldati, njengoko abeyalelwe, amthabatha uPawulos, amzisa kwa-Antipatri ngobusuku.', 'XHO22'),
(7, 23, 32, 'Ngengomso abashiya abamahashe, ukuba bahambe naye, abuyela enqabeni yomkhosi;', 'XHO22'),
(7, 23, 33, 'abathi bona, bakufika eKesareya, bayinikela incwadi kwirhuluneli, bammisa noPawulos phambi kwayo.', 'XHO22'),
(7, 23, 34, 'Iyilesile ke irhuluneli, yabuza ukuba ungowabuphi na uburhulumente; Uthe ke akuqonda ukuba ungowaseKiliki;', 'XHO22'),
(7, 23, 35, 'Wathi ndokuva kakuhle, xa bathe bafika nabamangaleli bakho; wathi makagcinwe endlwini yerhuluneli kaHerode.', 'XHO22'),
(7, 24, 1, 'Ke kaloku, emva kweentsuku ezintlanu, wehla umbingeleli omkhulu, uHananiya, enamadoda amakhulu, neciko elithile elinguTertulo, bona bengabamangaleli bakaPawulos kwirhuluneli.', 'XHO22'),
(7, 24, 2, 'Ke kaloku, akubizelwa ndawonye, waqala uTertulo ukummangalela, esithi, Sizola kakhulu ngawe, nokuthi lusenzelwa olu hlanga imisebenzi emihle kakhulu, ngolungiselelo lwakho;', 'XHO22'),
(7, 24, 3, 'Sikwamkela oko, nasezindaweni zonke, Felikis odume kunene, sinombulelo.', 'XHO22'),
(7, 24, 4, 'Ke ukuze ndingabi sakubambezela ngakumbi, ndiyakubongoza ukuba ukhe usive kancinane ngokuthantamisa kwakho.', 'XHO22'),
(7, 24, 5, 'Kuba siyifumene le ndoda isisitshididi, umphambanisi kuwo onke amaYuda akwelimiweyo, ingumkhokeli walo ihlelo lamaNazarete;', 'XHO22'),
(7, 24, 6, 'owazama ukuhlambela itempile, esathi samthabatha, safuna ukubagweba ngomthetho wethu.', 'XHO22'),
(7, 24, 7, 'Kodwa umthetheli-waka uLisiya wafika phezu kwethu, wamhlutha esandleni sethu ngogonyamelo olukhulu;', 'XHO22'),
(7, 24, 8, 'ebathethela ngelithi, mabeze kuwe abamangaleli bakhe, wothi wena ngokwakho, wakuzincina zonke iindawo, ube nako ukuziqonda kuye ezo siyimangalela ngazo thina.', 'XHO22'),
(7, 24, 9, 'Avumelana nawo ke amaYuda, esithi zinjalo ezo zinto.', 'XHO22'),
(7, 24, 10, 'Waphendula ke uPawulos, yakunqwala kuye irhuluneli ukuba athethe, waphendula wathi, Ndisazi nje, ukuba sekuyiminyaka emininzi ungumgwebi kolu hlanga, kokukhona ndiziphendulela ngomxhelo owonwabileyo.', 'XHO22'),
(7, 24, 11, 'Kungokuba uqonde, ukuba ziselishumi elinambini iintsuku, ndenyuka ndaya kunqula eYerusalem;', 'XHO22'),
(7, 24, 12, 'nasetempileni abandifumananga ndixoxa namntu, ndisenza nasiphendu, nasezindlwini zesikhungu, naphi phakathi komzi;', 'XHO22'),
(7, 24, 13, 'Kananjalo abanako ukuzimisa izinto abandimangalela ngazo ngoku.', 'XHO22'),
(7, 24, 14, 'Ke ndiyakuvuma oku kuwe, ukuba ngeNdlela abathi bona sisiphambusa, ndenjenjalo ukumnqula uThixo woobawo, ndikholwa zizinto zonke ezibhaliweyo emthethweni nasebaprofetini;', 'XHO22'),
(7, 24, 15, 'ndinethemba kuThixo, into ke abayilindileyo nabo ngokwabo, ukuba kuza kubakho uvuko lwabafileyo, lwamalungisa kwanolwabangemalungisa.', 'XHO22'),
(7, 24, 16, 'Ke kaloku, kule nto nam ngokwam ndiyazama, ukuba ngamaxesha onke ndingabi nasazela sikhubekisa nganto ngakuThixo nangakubantu.', 'XHO22'),
(7, 24, 17, 'Emva kweminyaka emininzi, ndafika ndizisa amalizo neminikelo kuhlanga lwakowethu.', 'XHO22'),
(7, 24, 18, 'Kwezo nto andifumana etempileni ndenziwe nyulu, ndingenzi siphendu, ndingenzi nasiphithiphithi amaYuda athile elaseAsiya;', 'XHO22'),
(7, 24, 19, 'abemelwe kukubakho apha phambi kwakho, andimangalele, ukuba angaba anendawo ngakum;', 'XHO22'),
(7, 24, 20, 'okanye mabathi aba balapha, ukuba kukho nto imbi bayifumene kum, bakubon’ ukuba ndimi entlanganisweni yamatyala;', 'XHO22'),
(7, 24, 21, 'ingelilo elo lizwi linye, ndadandulukayo ngalo ndimi phakathi kwabo, lokuthi, Kungalo uvuko lwabafileyo endimangalelwayo mna nini namhla.', 'XHO22'),
(7, 24, 22, 'Ke kaloku, akuziva ezi zinto uFelikis, ezazi ngokhona kucokisekileyo iindawo zaloo Ndlela, wabalibazisa, wathi, Xa athe wehla uLisiya umthetheli-waka, ndoziqiqa kakuhle iindawo zenu.', 'XHO22'),
(7, 24, 23, 'Wamisela umthetheli-khulu, ukuba agcinwe uPawulos, abe nokuyekelelwa, kungalelwa nabani wakowabo ukumnceda, nokuba eze kuye.', 'XHO22'),
(7, 24, 24, 'Ke kaloku, emva kwemihla ethile, wafika uFelikis enoDrusila umkakhe, umYudakazi, wathumela ukuba kubizwe uPawulos, wamva ethetha ngokukholwa kuKristu.', 'XHO22'),
(7, 24, 25, 'Ethetha ngobulungisa, nokuzeyisa, nangogwebo oluza kubakho, wangenwa kukoyika uFelikis, waphendula wathi, Hamba okwakalokunje; ndakuba ndinethuba, ndokubiza uze.', 'XHO22'),
(7, 24, 26, 'Kunye ke noko, wayethembe ukuba wonikwa imali nguPawulos, ukuze amkhulule; ngako oko kwaba kukhona ambiza futhi, ukuba eze axoxe naye.', 'XHO22'),
(7, 24, 27, 'Ke kaloku, kwakubon’ ukuba kudlule iminyaka emibini, kwangena uFelikis uPorkiyo Festo;', 'XHO22'),
(7, 25, 1, 'Uthe ngoko uFesto, akuba engene eburhulumenteni, wathi emva kweentsuku ezintathu, wenyuka waya eYerusalem, esuka eKesareya.', 'XHO22'),
(7, 25, 2, 'Umbingeleli omkhulu nabaphathi bamaYuda bammangalela uPawulos kuye, bambongoza,', 'XHO22'),
(7, 25, 3, 'becela ukuba abababale abe ebize eze eYerusalem, bemlalele ukuze bambulale endleleni.', 'XHO22'),
(7, 25, 4, 'Waphendula ke uFesto wathi, ugcinwe eKesareya uPawulos; naye ke ngokwakhe uza kuya kamsinya.', 'XHO22'),
(7, 25, 5, 'Wathi, Mabathi ngoko abanako phakathi kwenu, behle nam, baye kuyimangalela le ndoda, ukuba kukho bubi kuye.', 'XHO22'),
(7, 25, 6, 'Akuba ke elibele kubo iintsuku ezingaphezu kweshumi, wehla waya eKesareya; ngengomso, akuba ehleli esihlalweni sokugweba, wathi uPawulos makaziswe.', 'XHO22'),
(7, 25, 7, 'Akufika, amaYuda awayehle evela eYerusalem amngqonga, emtyhola ngezityholo ezininzi ezinzulu uPawulos, awayengenako ukuzingqina.', 'XHO22'),
(7, 25, 8, 'Uthe ke eziphendulela wathi, Kumthetho wamaYuda, nasetempileni, nakuKesare, andonanga nto.', 'XHO22'),
(7, 25, 9, 'Ke uFesto, ethanda ukukholisa amaYuda, wamphendula uPawulos wathi, Uyafuna na ukunyuka uye eYerusalem, lithethwe khona ityala lezi zinto phambi kwam?', 'XHO22'),
(7, 25, 10, 'Wathi ke uPawulos, Ndimi esihlalweni sokugweba sikaKesare, apho limele ukuthethwa khona ityala lam. AmaYuda andiwonanga nganto, njengokuba nawe usazi kakuhle.', 'XHO22'),
(7, 25, 11, 'Ke ukuba ndonile, nokuba ndenze into elingene ukufa, andali ukufa; kodwa ukuba akukho nanye yezo zinto bandimangalela ngazo aba, akukho namnye unako ukundinikela kubo. Ndibhenela kuKesare.', 'XHO22'),
(7, 25, 12, 'Waza uFesto, akuba ethethe nentlanganiso yamatyala, waphendula wathi, Ubhenele kuKesare; uya kuya kuKesare.', 'XHO22'),
(7, 25, 13, 'Kwakuba ntsuku, uAgripa, ukumkani, noBhernike beza eKesareya, besiza kubulisa uFesto.', 'XHO22'),
(7, 25, 14, 'Bakuba ke behleli khona imihla emininzi, uFesto wamcacisela ukumkani iindawo ezingoPawulos, esithi, Kukho ndoda ithile ishiyiweyo nguFelikis,', 'XHO22'),
(7, 25, 15, 'abathi, oko ndibe ndiseYerusalem, ababingeleli abakhulu namadoda amakhulu amaYuda bammangalela, befuna ukuba igwetywe.', 'XHO22'),
(7, 25, 16, 'Endathi kubo, Asilosiko lamaRoma ukumnikela umntu ekufeni, ngaphambi kokuba lowo umangalelweyo ajongane nabamangaleli, aze afumane ithuba lokuziphendulela ngalo ityala analo.', 'XHO22'),
(7, 25, 17, 'Bathe ke bakuba befikile apha, andizange ndilibazise, ngengomso ndahlala esihlalweni sokugweba, ndathi mayiziswe le ndoda.', 'XHO22'),
(7, 25, 18, 'Abamangaleli bakhe, bakuvela, abamisanga nalinye ityala lezo zinto bendizicingela mna;', 'XHO22'),
(7, 25, 19, 'Kodwa ke baye beneembuzwano ezithile naye ngokuhlonela kwabo izithixo, nangaYesu uthile ufileyo, abesithi yena uPawulos uhleli.', 'XHO22'),
(7, 25, 20, 'Ndakuthingaza ke mna ngawo umbuzo wale ndawo, ndathi, Unga ungaya eYerusalem na, lithethwe khona ityala lezi zinto?', 'XHO22'),
(7, 25, 21, 'Akubon’ ukuba ke uPawulos ubhenele ukuba agcinelwe isigwebo soMhlekazi, ndathi makagcinwe, ndide ndimthumele kuKesare.', 'XHO22'),
(7, 25, 22, 'Wathi ke uAgripa kuFesto, Bendinga nam ndingakhe ndimve umntu lowo. Wathi yena, Uya kumva ngomso.', 'XHO22'),
(7, 25, 23, 'Kwathi ngengomso, akufika uAgripa enoBhernike, benobukhazikhazikhazi obukhulu, bangene kwindawo yetyala, benabathetheli-waka namadoda amakhulu omzi, waziswa uPawulos ngokomlomo kaFesto.', 'XHO22'),
(7, 25, 24, 'Wathi ke uFesto, Agripa, kumkani, nani nonke madoda akhoyo apha kunye nathi, niyayibona le ndoda, ethe yonke inkitha yamaYuda yandenzela yona naseYerusalem nalapha, ibhomboloza isithi, akafanele kuphila, hayi kwaphela.', 'XHO22'),
(7, 25, 25, 'Kodwa ndithe ndakufumana ukuba akenzanga nto ilingene ukufa, nokuba yena ngokwakhe ubhenele kuMhlekazi, ndagqiba kwelokuba ndimthumele kuye,', 'XHO22'),
(7, 25, 26, 'endingenanto iyinyaniso ndingayibhalayo ngaye enkosini yam. Ngako oko ndimzise phambi kwenu, ngokukodwa phambi kwakho, kumkani Agripa, ukuze, kwakubon’ ukuba kunciniwe, ndibe nento endingayibhalayo;', 'XHO22'),
(7, 25, 27, 'Kum kubonakala ibubudenge, ukuba kuthunyelwe ikhonkxwa, lingaqondakaliswa netyala elinalo.', 'XHO22'),
(7, 26, 1, 'Wathi ke uAgripa kuPawulos, Uvunyelwe ukuba uziphendulele. Wasolula ke isandla uPawulos, waziphendulela;', 'XHO22'),
(7, 26, 2, 'Ndiba onoyolo, kumkani Agripa, ngokuba ndiza kuziphendulela namhlanje phambi kwakho, ngazo zonke iindawo endimangalelwe ngazo ngamaYuda;', 'XHO22'),
(7, 26, 3, 'ngokukodwa ekubeni unolwazi ngawo onke amasiko neembuzwano zamaYuda. Kungoko ndiyakubongoza ukuba undive, undinyamezele.', 'XHO22'),
(7, 26, 4, 'Indlela endandiphila ngayo kwasebuncinaneni bam, eyayikho ekuqaleni kohlanga lwakowethu eYerusalem, ayawazi onke amaYuda.', 'XHO22'),
(7, 26, 5, 'Endaziyo kwasekuqalekeni, ukuba angathanda ukungqina, ukuba, ngelona licokisekileyo ihlelo lonqulo lwakowethu, ndahlala ndingumFarisi.', 'XHO22'),
(7, 26, 6, 'Ngoku ke ndimi ndigwetyiwe, ngenxa yethemba ledinga elenziwa nguThixo koobawo;', 'XHO22'),
(7, 26, 7, 'ezithembe ukufika kulo izizwe zakowethu ezilishumi elinazibini, zimkhonza nje uThixo ngenyameko imini nobusuku; Elo themba elo ndimangalelwe ngamaYuda, kumkani Agripa.', 'XHO22'),
(7, 26, 8, 'Yini na ukuba nithi, yinto eningenakukholwa yiyo, ukuba uThixo uyabavusa abafileyo?', 'XHO22'),
(7, 26, 9, 'Mna okunene ke bendiba nam ngokwam, ndimelwe kukukwenza izinto ezininzi ezichasene negama likaYesu waseNazarete.', 'XHO22'),
(7, 26, 10, 'Endakwenzayo ke oko eYerusalem; nento eninzi yabangcwele ndayitshixela ezintolongweni, ndakuba ndamkele igunya kubabingeleli abakhulu; kwaye xa bebulawa, ndakhupha ilizwi lam ngokuchasene nabo.', 'XHO22'),
(7, 26, 11, 'Nakuzo zonke izindlu zesikhungu ndibe ndibohlwaya futhi, ndibanyanzela ukuba banyelise; ndathi, ndibagezela ngokuncamisileyo, ndabatshutshisa, ndada ndesa kwimizi engaphandle.', 'XHO22'),
(7, 26, 12, 'Endathi ke, xa ndisiya eDamasko, ndinegunya nomyalelo kubabingeleli abakhulu;', 'XHO22'),
(7, 26, 13, 'Emini enkulu endleleni, kumkani, ndabona ukukhanya kuvela ezulwini, okungaphezu kokuqaqamba kwelanga, kundibanekela ngeenxa zonke, nabo babehamba nam.', 'XHO22'),
(7, 26, 14, 'Sakubon’ ukuba siwile sonke emhlabeni, ndeva izwi lithetha nam ngentetho yesiHebhere, lisithi, Sawule, Sawule, unditshutshiselani na? wenzakala, ukhaba iimviko nje.', 'XHO22'),
(7, 26, 15, 'Ndathi ke mna, Ungubani na, Nkosi? Wathi ke yena, Mna ndinguYesu omtshutshisayo wena.', 'XHO22'),
(7, 26, 16, 'Kodwa sukuma, ume ngeenyawo; kuba ndibonakele kuwe ngenxa yoku, ukuze ndikwenze umlungiseleli nengqina lezi zinto uzibonileyo, nendiya kubonakala kuwe ngazo;', 'XHO22'),
(7, 26, 17, 'ndikuhlangula ebantwini nasezintlangeni, endikuthumela kuzo ngoku;', 'XHO22'),
(7, 26, 18, 'ukuba uvule amehlo abo, ukuze babuye ebumnyameni, beze ebumhlotsheni; babuye egunyeni likaSathana, beze kuThixo; ukuze bamkele uxolelo lwezono, nelifa phakathi kwabangcwalisiweyo, ngokukholwa kum.', 'XHO22'),
(7, 26, 19, 'Kuthe ngoko, kumkani Agripa, andibanga ngongawuthobeliyo umbono wasemazulwini;', 'XHO22'),
(7, 26, 20, 'ndesuka ndababikela abaseDamasko kuqala, nabaseYerusalem, nakulo lonke ilizwe lakwaYuda, nakuzo iintlanga, ukuba baguquke babuyele kuThixo, benze imisebenzi efanele inguquko.', 'XHO22'),
(7, 26, 21, 'Ngenxa yezi zinto amaYuda andibamba etempileni, azamela ukundibulala.', 'XHO22'),
(7, 26, 22, 'Ngoko ndizuze uncedo kuThixo nje, ndihleli kwada kwayile mini, ndingqina kwabancinane kwanabakhulu, ndingathethi nanye into ngaphandle kwezo, bathi abaprofeti noMoses ziya kubakho;', 'XHO22'),
(7, 26, 23, 'zokuba uKristu ubeza kuva iintlungu, nokuba ubeza kuthi, engowokuqala eluvukweni lwabafileyo, azise ukukhanya ebantwini nasezintlangeni.', 'XHO22'),
(7, 26, 24, 'Ke kaloku, akubon’ ukuba uyaziphendulela ngokunjalo, uFesto wathi ngezwi elikhulu, Uyageza, Pawulos; imfundo eninzi iyakugezisa.', 'XHO22'),
(7, 26, 25, 'Wathi ke yena, Andigezi, Festo odumileyo; kodwa ndithetha amazwi enyaniso nawengqondo ephilileyo.', 'XHO22'),
(7, 26, 26, 'Kuba ezo ndawo uyazazi kakuhle ukumkani, endithetha kuye ngokungafihlisiyo; kuba ndikholosekile kukuba akukho nanye yezi zinto ifihlakeleyo kuye; kuba le nto ibingenzelwanga egumbini.', 'XHO22'),
(7, 26, 27, 'Uyakholwa na, kumkani Agripa, ngabaprofeti? Ndiyazi ukuba uyakholwa.', 'XHO22'),
(7, 26, 28, 'Wathi ke uAgripa kuPawulos, Ngokuncinane undeyisela ukuba ndibe ngumKristu.', 'XHO22'),
(7, 26, 29, 'Wathi uPawulos, Ndithandaza kuThixo, ukuba ingabi nguwe wedwa, ibe ngabo bonke abandivayo namhlanje, abaya kuthi, nangokuncinane nangokukhulu, babe njengam nje, ngaphandle kwezi ntambo.', 'XHO22'),
(7, 26, 30, 'Akuba etshilo, besuka ukumkani, nerhuluneli, noBhernike, kwanababehleli nabo;', 'XHO22'),
(7, 26, 31, 'beshenxile, bathetha bodwa besithi, Lo mntu akenze nanye into elingene ukufa neentambo.', 'XHO22'),
(7, 26, 32, 'Wathi ke uAgripa kuFesto, Lo mntu ange enokukhululwa, ukuba ubengabhenele kuKesare.', 'XHO22'),
(7, 27, 1, 'Ke kaloku, kwakugqitywa kwelokuba sisuke ngomkhombe siye kwelaseItali, bamnikela uPawulos kwanabanye ababanjwa abathile kumthetheli-khulu, ogama linguYuliyo, webutho loMhlekazi.', 'XHO22'),
(7, 27, 2, 'Sangena enqanaweni yaseAdramitatiyo, iza kunduluka isiya kwimida yelaseAsiya, sanduluka, wayekunye nathi uAristarko, umMakedoni waseTesalonika.', 'XHO22'),
(7, 27, 3, 'Ngemini elandelayo sagaleleka eSidon; waza uYuliyo wamphatha uPawulos ngobubele, wamvumela ukuba aye ezihlotyeni zakhe, ongiwe.', 'XHO22'),
(7, 27, 4, 'Sanduluka apho, sahamba ngomkhombe, sabetha ezantsi kweSipro, ngenxa yokuba imimoya ibivela ngaphambili.', 'XHO22'),
(7, 27, 5, 'Sathi sakuwela ulwandle lwaseKiliki nasePamfili, safika eMira yelaseLikiya.', 'XHO22'),
(7, 27, 6, 'Apho umthetheli-khulu wafumana umkhombe wakwa-Alesandire, usiya kwelaseItali, wasibeka khona.', 'XHO22'),
(7, 27, 7, 'Ke kaloku iintsuku zanela sihamba kancinane, kwaba ngenkankulu ukuba sifike ngaseKinido. Uthe wakuba umoya ungasivumeli ukuba sihambe, sabetha ezantsi kweKrete ngaseSalimone.', 'XHO22'),
(7, 27, 8, 'Sahamba ngenkankulu, safika kwindawo ebizwa ngokuba ngamaChweba aMahle; kufuphi nawo umzi waseLaseya.', 'XHO22'),
(7, 27, 9, 'Ke kaloku, kwakubon’ ukuba ixesha laligqithile, nakubon’ ukuba kuseyingozi ukuhamba ngomkhombe, ekubeni kaloku ukuzila kwase kusele kudlule, wabayala uPawulos,', 'XHO22'),
(7, 27, 10, 'wathi kubo, Madoda, ndiyabona ukuba olu hambo luza kuba nenkxwaleko nokonakala okukhulu, kungekwamthwalo namkhombe wodwa, kukokobomi bethu nabo.', 'XHO22'),
(7, 27, 11, 'Kodwa umthetheli-khulu wayethambele okomqhubi nokomninimkhombe ukuthetha, kunokukaPawulos.', 'XHO22'),
(7, 27, 12, 'Ke kaloku, ichweba lingakulungele nje ukuba kudluliswe busika kulo, abona baninzi kubo baceba ukuba kundulukwe khona, ukuba mhlawumbi bangaba bafike eFenike, badlulise ubusika khona apho. Eli lizibuko laseKrete, elibheke emzantsi-ntshona, nakumntla-ntshona.', 'XHO22'),
(7, 27, 13, 'Kuthe ke, wakubetha impepho umoya wasezantsi, beba bakuzuzile ababekungxamele, banduluka, badlula kufuphi neKrete.', 'XHO22'),
(7, 27, 14, 'Kuthe kungekabi ntsuku zininzi, kwabakho umoya ovuthuzayo, ogama liyiYurokelo.', 'XHO22'),
(7, 27, 15, 'Kuthe ke kwakubon’ ukuba uthiwe hlasi umkhombe, awaba nako ukumelana nomoya, sasesiwuyeka; saqhutywa.', 'XHO22'),
(7, 27, 16, 'Sathi sakubaleka ke, sibetha ngaphantsi kwesiqithana esithile, ekuthiwa yiKlawuda, kwaba ngenkankulu ukuba sibe nako ukuwubamba umkhonjana;', 'XHO22'),
(7, 27, 17, 'abathe bakuwufunqula, basebenza ngezinto zokunceda, bewubhinqisa umkhombe; kananjalo bathi, besoyika ukuba bangeyela entlabathini, bazifinyeza iiseyile, baqhutywa benjalo.', 'XHO22'),
(7, 27, 18, 'Sakuba ke sivuthuza ngamandla, ngengomso bawenza khaphukhaphu umkhombe;', 'XHO22'),
(7, 27, 19, 'Ngosuku lwesithathu, sazilahla ngezandla zethu iimpahla zomkhombe.', 'XHO22'),
(7, 27, 20, 'Kuthe ke, kwakubon’ ukuba akubonakali langa nankwenkwezi iintsuku ezininzi, kucinezele neliqhwa elingelincinane, laphela kwathi tu lonke ithemba lokuba singasindiswa.', 'XHO22'),
(7, 27, 21, 'Emva kokuzila ukudla ixesha elide, wesuka wema uPawulos phakathi kwabo, wathi, Hina madoda, ninge naniphulaphule kum, ananduluka eKrete, nizuzane nale nkxwaleko noku konakala.', 'XHO22'),
(7, 27, 22, 'Nangoku ndiyaniluleka ukuba nibe nomxhelo owonwabileyo; kuba kuni aba, akukho uya kulahlekelwa bubomi bakhe, yoba ngumkhombe wodwa.', 'XHO22'),
(7, 27, 23, 'Kuba ngobu busuku bekumi ngakum isithunywa sikaThixo, endingowakhe, endimkhonzayo,', 'XHO22'),
(7, 27, 24, 'sisithi, Musa ukoyika, Pawulos; umelwe kukuma phambi koKesare; uyabona, uThixo ukubabale ngabo bonke abasemkhombeni nawe.', 'XHO22'),
(7, 27, 25, 'Kungoko ndithi, Yibani nomxhelo owonwabileyo, madoda; kuba ndiyakholwa nguThixo, ukuba kuya kuba njalo ngolo hlobo athethe ngalo kum.', 'XHO22'),
(7, 27, 26, 'Kodwa simelwe kukuphoswa siqithini sithile.', 'XHO22'),
(7, 27, 27, 'Ke kaloku, kwakufika ubusuku beshumi elinesine, sakubon’ ukuba siqhutywaqhutywaqhutywa ngumoya eAdriya, mayela phakathi kobusuku, oomatrose bazindla ukuba kukho mhlaba uthile basondelayo kuwo.', 'XHO22'),
(7, 27, 28, 'Baza bakuba beyiphosile ilothe, bafumana ukuba ziifatom ezimashumi mabini; bethe tyi ke kancinane, baphinda bayiphosa ilothe, bafumana ukuba ziifatom ezilishumi elinantlanu.', 'XHO22'),
(7, 27, 29, 'Bandula ke, besoyika ukuba hleze siwele ematyeni, baphosa iiankile ezine ngasemva, banqwenela ukuba kuse.', 'XHO22'),
(7, 27, 30, 'Ke kaloku, bakubon’ ukuba bayabaleka oomatiloshe emkhombeni, bawuhlisa umkhombe elwandle, babonakala beza kuphosa iiankile enqanaweni.', 'XHO22'),
(7, 27, 31, 'Wathi uPawulos kumthetheli-khulu nakumajoni, Ukuba aba abathanga bahlale emkhombeni, aninakusindiswa.', 'XHO22'),
(7, 27, 32, 'Andula amasoldati azinqamla iintsontelo zomkhonjana, awuyeka wawa, wemka.', 'XHO22'),
(7, 27, 33, 'Kwakuba kusile, uPawulos wabacenga bonke ukuba batye, esithi, Namhlanje ngumhla weshumi elinesine nilindile, nilindile, ningadli nto;', 'XHO22'),
(7, 27, 34, 'ke ngoko ndiyanibongoza ukuba nidle ukudla; kuba oko kuyelelene nokusindiswa kwenu; kuba akuyi kuwa nalunwele lwentloko kabani kuni.', 'XHO22'),
(7, 27, 35, 'Akuba etshilo, wathabatha isonka, wabulela kuThixo phambi kwabo bonke, waqhekeza, waqala wadla.', 'XHO22'),
(7, 27, 36, 'Bonke ke baba nomxhelo owonwabileyo, bathabatha ukudla nabo.', 'XHO22'),
(7, 27, 37, 'Saye sonke emkhombeni siyimiphefumlo engamakhulu amabini anamashumi asixhenxe anesithandathu.', 'XHO22'),
(7, 27, 38, 'Bakuba ke behluthi, bawenza khaphukhaphu umkhombe, beyiphosa elwandle ingqolowa.', 'XHO22'),
(7, 27, 39, 'Kwakuba sekusile, abawazanga loo mhlaba, kodwa babona ichwetyana elithile linonxweme, babhunga ukuwutyhalela kulo umkhombe, ukuba bangaba nako.', 'XHO22'),
(7, 27, 40, 'Baza, bezinqumla zonke iiankile, baziyekela elwandle, bezikhulule kunye nezibophelelo zemithi yokujika;', 'XHO22'),
(7, 27, 41, 'Bakuba ke begaxeleke endaweni enolwandle ngeenxa zombini, wangqubeka khona umkhombe; umphambili wabambelela ngokuqinileyo, wahlala engenakushukunyiswa;', 'XHO22'),
(7, 27, 42, 'Ke kaloku icebo lamasoldati lalisithi mababulawe amabanjwa, ukuze kungabikho bani udadayo, basabe.', 'XHO22'),
(7, 27, 43, 'Kodwa umthetheli-khulu, enga angamsindisa uPawulos, wawalela kwinjongo yawo; Wathi abo banako ukudada, mabaziphose kuqala elwandle, bafike emhlabeni;', 'XHO22'),
(7, 27, 44, 'Abaseleyo ke, inxenye ikhwele eziplangeni, inxenye iphezu kwamaqhekeza omkhombe. Kwaye kwenzeka ukuba basinda bonke bekhuselekile emhlabeni.', 'XHO22'),
(7, 28, 1, 'Bakuba besindile, baqala ukuqonda ukuba isiqhithi eso kuthiwa yiMelita.', 'XHO22'),
(7, 28, 2, 'Amabharbhari ke lawo asenzela ububele obungasincinane; kuba aphemba umlilo, asamkela sonke ngenxa yemvula esifikeleyo, nangenxa yokubanda.', 'XHO22'),
(7, 28, 3, 'Ke kaloku, akubon’ ukuba uPawulos utheze inyanda yeentsasa, wayibeka phezu komlilo, kwaphuma inyoka ebushushwini obo, yanamathela esandleni sakhe.', 'XHO22'),
(7, 28, 4, 'Athe ke amabharbhari, akulibona irhamncwa lijinga esandleni sakhe, athi omnye komnye, Kanene lo mntu ungumbulali, nakuba esindile elwandle, impindezelo ayimvumeli ukuba adle ubomi.', 'XHO22'),
(7, 28, 5, 'Lalivuthululela emlilweni irhamncwa, akeva kwenzakala.', 'XHO22'),
(7, 28, 6, 'Ke bona babelinde ukuba uza kudumba, mhlawumbi athi qwaka, afe. Bakulinda ke umzuzwana, bakubona ukuba akahliwa nto, bajika bathi unguthixo.', 'XHO22'),
(7, 28, 7, 'Ke kaloku, kuloo mmandla bekukho imihlaba yomntu omkhulu wesiqithi, ogama linguPubliyo; owasamkela ngobubele, wasenza undwendwe lwakhe iintsuku zantathu.', 'XHO22'),
(7, 28, 8, 'Ke kaloku kwathi, uyise kaPubliyo wayelele phantsi ebanjwe yicesina nokuphalaza igazi; awathi uPawulos wangena kuye, wathandaza, wabeka izandla phezu kwakhe, wamphilisa.', 'XHO22'),
(7, 28, 9, 'Kwakuba kusenzeka oko, beza nabanye ababenezifo esiqithini apho, baphiliswa;', 'XHO22'),
(7, 28, 10, 'bathi bona basenzela iimbeko ezininzi; sanduluka, basithwalisa ngezinto esizisweleyo.', 'XHO22'),
(7, 28, 11, 'Ke kaloku, emva kweenyanga ezintathu, sanduluka ngomkhombe wakwa-Alesandire, obudlulise ubusika esiqithini, ogama linguCastor noPoluks.', 'XHO22'),
(7, 28, 12, 'Sathi sakufika eSirakusi, sahlala khona iintsuku zantathu.', 'XHO22'),
(7, 28, 13, 'Sisuka khona sajikeleza, safika eRegiyo; kwakuba ntsuku mnye, kwavuthuza umoya wasezantsi, safika ngengomso ePotiyoli;', 'XHO22'),
(7, 28, 14, 'Safumana abazalwana apho, basibongoza ukuba sihlale nabo iintsuku zibe sixhenxe; seza ngokunjalo eRoma.', 'XHO22'),
(7, 28, 15, 'Bathe, bakukuva oko abazalwana, beza kusikhawulela, bada beza kufika kwintlanganiso ka-Apiyo naseTavene-Ntathu; awathi uPawulos, akubabona, wambulela uThixo, womelela.', 'XHO22'),
(7, 28, 16, 'Ke kaloku, sakufika eRoma, umthetheli-khulu wabanikela ababanjwa kumthetheli-mkhosi, kodwa uPawulos wavunyelwa ukuba ahlale yedwa, nesoldati elimgcinayo.', 'XHO22'),
(7, 28, 17, 'Ke kaloku kwathi, emva kweentsuku ezintathu, uPawulos wababizela ndawonye abaziintloko bamaYuda, ababebuthelene, wathi kubo, Madoda, bazalwana, nakuba ndingenzanga nto ichasene nabantu, namasiko awobawo, noko ndesuka ndaba ngumbanjwa eYerusalem, ndanikelwa ezandleni zamaRoma.', 'XHO22'),
(7, 28, 18, 'Athe wona akundincina, afuna ukundikhulula, ngenxa yokuba kungekho tyala lakufa kum.', 'XHO22'),
(7, 28, 19, 'Athe ke amaYuda akuphika, ndanyanzeleka ukuba ndibhenele kuKesare; kungengakuba ndibe ndibe ndilumangalela ngako uhlanga lwakowethu.', 'XHO22'),
(7, 28, 20, 'Ndinibongoze ke ngenxa yeso sizathu ukuba nize, ndinibone, ndithethe nani, kuba ngenxa yethemba lakwaSirayeli ndikhonkxiwe ngalo mxokelelwane.', 'XHO22'),
(7, 28, 21, 'Bathe ke bona kuye, Thina asizuzanga zincwadi zithetha ngawe, zivele kwelakwaYuda; kananjalo akufikanga bani wakubazalwana, owabika, wathetha nto ikhohlakeleyo ngawe.', 'XHO22'),
(7, 28, 22, 'Sithanda ukuva ngawe, ukuba ucinga ntoni na; kuba ngalo eli hlelo siyazi ukuba lichasiwe ezindaweni zonke.', 'XHO22'),
(7, 28, 23, 'Ke kaloku, bakummisela imini, kweza kuye into eninzi endlwini abehlala kuyo, wabatyhilela; awabacaciselayo, ebungqinela ubukumkani bukaThixo, ebebeyisela kokungoYesu, ethabatha emthethweni kaMoses kwanakubaprofeti, ethabathela kusasa kwada kwahlwa.', 'XHO22'),
(7, 28, 24, 'Inxenye ke yakholwa zizo zinto, yaza inxenye ingakholwa.', 'XHO22'),
(7, 28, 25, 'Bakuba bengavumelananga bodwa, bemka, akuba uPawulos ethethe ilizwi lalinye, wathi, uMoya oyiNgcwele wathetha kakuhle ngoIsaya umprofeti, koobawo bethu,', 'XHO22'),
(7, 28, 26, 'esithi, Hamba uye kwaba bantu, uthi, Ukuva oku niya kuva, ningaqondi; nokubona niya kubona, ningaqiqi;', 'XHO22'),
(7, 28, 27, 'Kuba intliziyo yaba bantu ithe fithi kukutyeba, Beva ngeendlebe ezithe nkqi, Bayawacima namehlo abo; hleze babone ngamehlo, beve ngeendlebe, baqonde ngentliziyo, bajike, ndibaphilise.', 'XHO22'),
(7, 28, 28, 'Makwazeke ngoko kuni, ukuba usindiso lukaThixo luthunyelwe kuzo iintlanga; ziya kuluva zona.', 'XHO22'),
(7, 28, 29, 'Akuba ezithethile ezi zinto, emka amaYuda, eneempikiswano ezinkulu phakathi kwawo.', 'XHO22'),
(7, 28, 30, 'uPawulos wahlala iminyaka emibini iphela kweyakhe indlu ayiqeshileyo, wabamkela bonke ababesiza kuye,', 'XHO22'),
(7, 28, 31, 'ebuvakalisa ubukumkani bukaThixo, efundisa iindawo ezingeNkosi uYesu Kristu ngako konke ukungafihlisi, engalelwa bani.', 'XHO22'),
(8, 1, 1, 'uPawulos, umkhonzi kaYesu Kristu, owabizwayo ukuba abe ngumpostile, owahlulelwa iindaba ezilungileyo zikaThixo,', 'XHO22'),
(8, 1, 2, '(awathi ngedinga ngenxa engaphambili ngabo abaprofeti bakhe ezibhalweni ezingcwele),', 'XHO22'),
(8, 1, 3, 'ethetha ngoNyana wakhe, uYesu Krestu iNkosi yethu, owazalwa engowembewu kaDavide ngokwenyama;', 'XHO22'),
(8, 1, 4, 'wamiswa ukuba unguNyana kaThixo, enamandla, ngokomoya wobungcwele, ngokuvuka kwabafileyo;', 'XHO22'),
(8, 1, 5, 'Esathi ngaye samkela ubabalo nobupostile, ukuze ngenxa yegama lakhe kubekho ukulululamela ukholo phakathi kwazo zonke iintlanga;', 'XHO22'),
(8, 1, 6, 'eniphakathi kwabo nani, ababiziweyo bakaYesu Kristu;', 'XHO22'),
(8, 1, 7, 'ubhalela bonke abaseRoma, abaziintanda zikaThixo, abangcwele ababiziweyo, esithi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(8, 1, 8, 'Kuqala ke, ndiyabulela kuThixo wam ngoYesu Kristu ngenxa yenu nonke, kuba ukholo lwenu luxelwa ehlabathini liphela.', 'XHO22'),
(8, 1, 9, 'Kaloku ulingqina lam uThixo, endimkhonzayo ngomoya wam kwiindaba ezilungileyo zoNyana wakhe, okokuba andiyeki ukunikhankanya;', 'XHO22'),
(8, 1, 10, 'ndihlala ndikhunga, ukuba ndingade ngoku ndibe nempumelelo, ndize kuni nangakuphi, ngako ukuthanda kukaThixo.', 'XHO22'),
(8, 1, 11, 'Kuba ndilangazelela ukunibona, ukuze ndinabele sibabalo sithile sisesomoya, ukuze nizimaseke;', 'XHO22'),
(8, 1, 12, 'oko ke kukuze ndivuseleleke nam phakathi kwenu, ngalo ukholo olukuthi sobabini, ngolwenu kwanangolwam.', 'XHO22'),
(8, 1, 13, 'Ke andithandi ukuba ningazi, bazalwana, ukuba kufuthi ndizimisela entliziyweni yam ukuza kuni (ndathintelwa kwada kwanamhla), ukuze ndibe nasiqhamo sithile naphakathi kwenu, kwanjengaphakathi kwezinye iintlanga.', 'XHO22'),
(8, 1, 14, 'KumaGrike kwanakumabharbhari, ndimelwe kukushumayela, kwakwizilumko kwanakwabaswele ukuqonda.', 'XHO22'),
(8, 1, 15, 'Ngoko ke ndingxamele ukuzishumayela iindaba ezimnandi nakuni baseRoma.', 'XHO22'),
(8, 1, 16, 'Kaloku andinazintloni ngazo iindaba ezimnandi zikaKrestu, kuba zingamandla kaThixo okusindisa bonke abakholwayo, kumYuda kuqala, kwanakumGrike.', 'XHO22'),
(8, 1, 17, 'Kuba ngazo kutyhilwa ubulungisa bukaThixo, buphuma elukholweni buse elukholweni, njengokuba kubhaliwe kwathiwa, Olilungisa ke uya kudla ubomi ngokokholo.', 'XHO22'),
(8, 1, 18, 'Kuba ityhilekile ingqumbo kaThixo ivela emazulwini, ichase konke ukungahloneli Thixo, nentswela-bulungisa yabantu, abayithintelayo inyaniso ngokuswela ukulungisa.', 'XHO22'),
(8, 1, 19, 'Ngokuba oko kwazekayo ngoThixo kuyabonakala ngaphakathi kwabo; ngokuba uThixo wabonakalalisa kubo.', 'XHO22'),
(8, 1, 20, 'Kuba kwasekudalweni kwehlabathi, iindawo zakhe ezibe zingenakubonwa zibonwa kakuhle, ziqondeke, ngezinto ezenziweyo, kwa-amandla akhe angunaphakade, nobuThixo bakhe, ukuze bangabi nakuziphendulela;', 'XHO22'),
(8, 1, 21, 'Ngenxa enokuba bathe, bemazi uThixo, abamzukisa ngokoThixo, ababulela kuye; basuka bazizinto ezingento ezicamangweni zabo, yaza intliziyo yabo engenakuqonda yenziwa yamnyama;', 'XHO22'),
(8, 1, 22, 'bathi besiba bazizilumko, besuka baba ziziyatha;', 'XHO22'),
(8, 1, 23, 'baza ubuqaqawuli bukaThixo ongenakonakala babananisela intsobi yomfanekiselo womntu onokonakala, noweentaka, nowezinto ezizitho zine, nowezinambuzane.', 'XHO22'),
(8, 1, 24, 'Kungoko athe, besezinkanukweni zeentliziyo zabo, uThixo wabanikela nokubanikela ekungcoleni, ukuba bahlazisane ngemizimba yabo;', 'XHO22'),
(8, 1, 25, 'bona bananisele ubuxoki inyaniso kaThixo, basihlonela basikhonza isidalwa, begqitha kuMdali, ongowokubongwa kuse kuwo amaphakade. Amen.', 'XHO22'),
(8, 1, 26, 'Ngenxa yoko, uThixo wabanikela ekuthabathekeni okulihlazo; kuba namankazana abo umsebenzi wasemvelweni awananisela okunxamnye nemvelo;', 'XHO22'),
(8, 1, 27, 'Kwaza kwangokunjalo ke namadoda, ewushiya umsebenzi wenkazana wasemvelweni, avutha ngokukhanukana kwawo; amadoda esenza okuziintloni kumadoda, ezamkelela umbuyekezo wokulahleka kwawo, abemelwe ke nguwo.', 'XHO22'),
(8, 1, 28, 'Njengokuba ke bekumangele ukuhlala bemazi kakuhle uThixo, uThixo wabanikela engqiqweni engacikidekileyo, ukuba benze izinto ezingafanelekileyo;', 'XHO22'),
(8, 1, 29, 'bezele yiyo yonke intswela-bulungisa, okungendawo, ukubawa, ulunya; kuzele ngumona, ukubulala, iimpikiswano, inkohliso, ubutshijolo; abahlebezi,', 'XHO22'),
(8, 1, 30, 'abahlebi, abathiyekileyo kuThixo, abasileyo, abanekratshi, abaqhankqalazi, abacebi bezinto ezimbi, abangeva bazali;', 'XHO22'),
(8, 1, 31, 'bangenakuqonda, bagqithise iminqophiso, bangenamhawu, abangenatarhu, abangenanceba;', 'XHO22'),
(8, 1, 32, 'bona bathi, besazi kakuhle isimiselo sikaThixo, sokuba abo bazenzayo izinto ezinjalo bafanelwe kukufa, banganeli kukuzenza, basuka bathakazelele nabo bazenzayo.', 'XHO22'),
(8, 2, 1, 'Kungoko ndithi, Akunakuziphendulela, mntundini ugwebayo, nokuba ungubani; kuba ngokugweba omnye, uzigweba kwawena; kuba wena ugwebayo wenza kwaezo zinto.', 'XHO22'),
(8, 2, 2, 'Siyazi ke ukuba isigwebo sikaThixo siwa ngokwenyaniso phezu kwabo bazenzayo izinto ezinjalo.', 'XHO22'),
(8, 2, 3, 'Ucinga na ke, mntundini ugweba abo bazenzayo izinto ezinjalo, ube usenza kwazona nawe, ukuthi, uya kusisaba na wena isigwebo sikaThixo?', 'XHO22'),
(8, 2, 4, 'Okanye uyabudela na ubutyebi bobubele bakhe, nonyamezelo, nokuzeka kade umsindo; ungazi ukuba ububele bukaThixo bukusa enguqukweni?', 'XHO22'),
(8, 2, 5, 'Ke uthi, ngokuba lukhuni kwakho, nentliziyo yakho engenakuguquka, uziqwebele ingqumbo, ngemini yengqumbo nokutyhilwa komgwebo onobulungisa kaThixo;', 'XHO22'),
(8, 2, 6, 'eya kubuyekeza elowo ngokwemisebenzi yakhe;', 'XHO22'),
(8, 2, 7, 'Abo bathi ngomonde ekwenzeni okulungileyo bafune uzuko nembeko nokungonakali ubomi obungunaphakade;', 'XHO22'),
(8, 2, 8, 'Ke bona abaneenkani, bengayilulameli inyaniso, balulamela intswela-bulungisa, nengqumbo, nengqumbo;', 'XHO22'),
(8, 2, 9, 'imbandezelo noxinaniso emphefumlweni wabantu bonke abasebenza okubi, kowomYuda ukuqala, kwanakowomGrike;', 'XHO22'),
(8, 2, 10, 'kubekho uzuko ke, nembeko, noxolo, kubantu bonke abasebenza okulungileyo, kumYuda ukuqala, kwanakumGrike.', 'XHO22'),
(8, 2, 11, 'Kuba kuThixo akukho kukhetha buso.', 'XHO22'),
(8, 2, 12, 'Kuba bonke abonileyo bengenamthetho, baya kutshabalala ngokunjalo bengenamthetho; nabo bonke abonileyo benomthetho, baya kugwetywa ngomthetho;', 'XHO22'),
(8, 2, 13, 'Ngokuba angabevayo umthetho asingabo abangamalungisa phambi koThixo; kodwa abenzi bomthetho abaya kugwetyelwa.', 'XHO22'),
(8, 2, 14, 'Kuba xa abeentlanga abangenamthetho, besenza ngemvelo izinto ezisemthethweni, abo bengenawo umthetho, bangumthetho kubo ngokwabo:', 'XHO22'),
(8, 2, 15, 'abawubonakalalisa umsebenzi womthetho obhalwe ezintliziyweni zabo, singqinela isazela sabo, singqinelane nesazela sabo, neecamango zabo ziyamangalelana, nokuba ziyabagweba;', 'XHO22'),
(8, 2, 16, 'Kuya kuba njalo mhlenikweni uThixo aya kuzigweba izinto ezifihlakeleyo zabantu ngoYesu Kristu, ngokweendaba zam ezilungileyo.', 'XHO22'),
(8, 2, 17, 'Uyabona, wena unegama lokuba ungumYuda, wayame ngomthetho, uqhayisa ngoThixo;', 'XHO22'),
(8, 2, 18, 'uyakwazi ukuthanda kwakhe; uzicikida izinto eziyiyo, uwutyelwe umthetho;', 'XHO22'),
(8, 2, 19, 'kwaye ukholose nje, ukuba wena ungumkhokeli weemfama, ukhanyiselo lwabasebumnyameni;', 'XHO22'),
(8, 2, 20, 'umluleki wabaswele ukuqonda, umfundisi weentsana, onesimilo sokwazi nesenyaniso emthethweni.', 'XHO22'),
(8, 2, 21, 'Ndithi, wena ke umfundisayo omnye, akuzifundisi? wena ushumayela ukuba makubekho, uyeba na?', 'XHO22'),
(8, 2, 22, 'Wena ke, uthi mawungakrexezi, uyakrexeza na? Wena ukruqukileyo zizithixo, uhlambela na?', 'XHO22'),
(8, 2, 23, 'Wena ke uqhayisa ngomthetho, uyamhlazisa uThixo ngokugqitha umthetho?', 'XHO22'),
(8, 2, 24, 'Kaloku igama likaThixo linyeliswa ngenxa yenu phakathi kweentlanga, njengoko kubhaliweyo.', 'XHO22'),
(8, 2, 25, 'Ulwaluko luyanceda okunene, ukuba uyawenza umthetho; kodwa ukuba ungumgqithi womthetho, ulwaluko lwakho lusuke lwaba kukungaluki.', 'XHO22'),
(8, 2, 26, 'Ngoko ke, ukuba ongalukileyo uthi azigcine izimiselo zomthetho, akuyi kuthi na ukungaluki kwakhe kubalelwe ekuthini kukwaluka?', 'XHO22'),
(8, 2, 27, 'Kuthi ukungaluki, oko kwasemvelweni, ngokuwuzalisa umthetho, kukugwebe wena ungumgqithi-mthetho, noko unesibhalo nolwaluko?', 'XHO22'),
(8, 2, 28, 'Kuba umYuda asingulowo unguye ngokwangaphandle; Nolwaluko asilulo olo lwangaphandle enyameni;', 'XHO22'),
(8, 2, 29, 'umYuda ngulowo wasemfihlekweni; Ulwaluko lolwentliziyo ngomoya, kungengantetho; lowo ukudunyiswa kwakhe kungaphumi bantwini, kuphuma kuThixo.', 'XHO22'),
(8, 3, 1, 'Ugqithisele ngantoni na ngoko umYuda kwabanye? Kunceda ntoni na ukwaluka?', 'XHO22'),
(8, 3, 2, 'Kukhulu, ngeendlela zonke. Kuba ke, ukuqala, baphathiswa izihlabo zikaThixo.', 'XHO22'),
(8, 3, 3, 'Kuyini na khona, ukuba abathile abakholwanga? Ngaba ukungakholwa kwabo kuya kuluphuthisa na ukholo lukaThixo?', 'XHO22'),
(8, 3, 4, 'Nakanye! uThixo yena makabe nenyaniso, bonke abantu ke babe ngamaxoki; njengokuba kubhaliwe kwathiwa, Ukuze ube nokugwetyelwa emazwini akho, Weyise ekugwetyweni.', 'XHO22'),
(8, 3, 5, 'Ke ukuba ukuswela-kulungisa kwethu kuqondakalisa ubulungisa bukaThixo, sothini na? Ulilungisa nguThixo na, ophindezelayo? (Ndithetha njengendoda)', 'XHO22'),
(8, 3, 6, 'Nakanye! Oko ubengathini na uThixo ukuligweba ihlabathi?', 'XHO22'),
(8, 3, 7, 'Ukuba inyaniso kaThixo iphuphumele ekuthini azukiswe ngobuxoki bam; yini na ukuba ndisagwetywe njengomoni?', 'XHO22'),
(8, 3, 8, 'Yini na ukuba singabi sesisithi (ngoko ke sinyeliswayo, ngoko ke abathile bathi, sithi thina), Masenze okubi, ukuze kuvele okulungileyo? abakugwetywa kuyeleleyo.', 'XHO22'),
(8, 3, 9, 'Kuthiweni na ngoko? Ngaba thina sibhetele kunabo? Hayi, nakanye! Kuba besesiwamisele ngenxa engaphambili amaYuda kwanamaGrike, ukuba baphantsi kwesono bonke;', 'XHO22'),
(8, 3, 10, 'njengokuba kubhaliwe kwathiwa, Akukho lungisa, hayi, nalinye;', 'XHO22'),
(8, 3, 11, 'Akukho uqondayo; akukho umfunisisayo uThixo.', 'XHO22'),
(8, 3, 12, 'Bonke baphambukile, abancedi lutho xa bebonke; Akukho wenza okulungileyo, hayi, nokuba abe mnye.', 'XHO22'),
(8, 3, 13, 'Lingcwaba elivulekileyo umqala wabo; Ngeelwimi zabo bamana ukukhohlisa; Ubuhlungu bephimpi buphantsi komlomo wabo.', 'XHO22'),
(8, 3, 14, 'Abamlomo uzele ziziqalekiso nobukrakra.', 'XHO22'),
(8, 3, 15, 'Iinyawo zabo zinamendu okuphalaza igazi;', 'XHO22'),
(8, 3, 16, 'Lityumtyum neentsizi ezindleleni zabo;', 'XHO22'),
(8, 3, 17, 'Nendlela yoxolo abayazanga.', 'XHO22'),
(8, 3, 18, 'Akukho koyika Thixo phambi kwamehlo abo.', 'XHO22'),
(8, 3, 19, 'Siyazi ke ukuba izinto zonke ozithethayo umthetho, uzithetha kwabaphantsi komthetho; ukuze imilomo yonke ivingcwe, nehlabathi lonke libe netyala kuThixo.', 'XHO22'),
(8, 3, 20, 'Ngoko ke, ngokwasemisebenzini yomthetho, akuyi kugwetyelwa nyama emehlweni akhe; kuba isono saziwe ngokubakho komthetho.', 'XHO22'),
(8, 3, 21, 'Ngoku ke kubonakaliswe ubulungisa bukaThixo, kungekho mthetho, bungqinelwa nguwo umthetho nabaprofeti;', 'XHO22'),
(8, 3, 22, 'ubulungisa ke bukaThixo, obungokukholwa kuYesu Kristu, bube bobabo bonke, buphezu kwabo bonke abakholwayo;', 'XHO22'),
(8, 3, 23, 'Kuba bonile bonke, basilelela eluzukweni lukaThixo;', 'XHO22'),
(8, 3, 24, 'begwetyelwe ngesisa lubabalo lwakhe ngako ukukhululwa ngentlawulelo ekuYesu Kristu;', 'XHO22'),
(8, 3, 25, 'athe uThixo wammisa, ukuba abe sisicamagushelo ngegazi lakhe, ngako ukukholwa, ukuze bubonakalaliswe ubulungisa bakhe ngenxa yokuyekelelwa kwezono ezibe zenziwe ngenxa engaphambili, ekunyamezeleni kukaThixo;', 'XHO22'),
(8, 3, 26, 'ukuze bubonakalaliswe ubulungisa bakhe ngeli xesha lakalokunje, ukuze abe lilungisa, kwanomgwebeli walowo waselukholweni lukaYesu.', 'XHO22'),
(8, 3, 27, 'Luphi na ngoko uqhayiso? Ayibandakanywa. Ngawuphi umthetho? yemisebenzi? Hayi, kungomthetho wokholo.', 'XHO22'),
(8, 3, 28, 'Ngoko ke sigqiba kwelithi, umntu ugwetyelwa ngokholo, kungekho misebenzi yamthetho.', 'XHO22'),
(8, 3, 29, 'Ngaba unguThixo wamaJuda kuphela? Akanguye na noweentlanga? Ewe, ngoweentlanga nazo;', 'XHO22'),
(8, 3, 30, 'ekubeni emnye uThixo, oya kugwebela abolwaluko ngokwaselukholweni, nabangengabolwaluko ngalo ukholo.', 'XHO22'),
(8, 3, 31, 'Umthetho ngoko siyawuphuthisa na ngalo ukholo? Nakanye! Siyawumisa umthetho.', 'XHO22'),
(8, 4, 1, 'Yintoni na ngoko esiya kuthi, uAbraham, ubawo wethu, ufumene yona ngokwenyama?', 'XHO22'),
(8, 4, 2, 'Kuba uAbraham, ukuba wayegwetyelwe ngokwasemisebenzini, ange eneqhayiya; kodwa phambi koThixo.', 'XHO22'),
(8, 4, 3, 'Kuba sithini na isibhalo? uAbraham wakholwa kuThixo, kwaza oko kwabalelwa kuye ebulungiseni.', 'XHO22'),
(8, 4, 4, 'Ke kaloku, kulowo usebenzayo, umvuzo awubalelwa ngokwaselubabalweni;', 'XHO22'),
(8, 4, 5, 'Ke kulowo ungasebenziyo, ekholwa kuye lowo umgwebelayo umntu ongahloneli Thixo, ukholo lwakhe lubalelwa ebulungiseni.', 'XHO22'),
(8, 4, 6, 'Kwanjengokuba noDavide eluchaza ubabalo lomntu, athe uThixo wabalela ubulungisa ngaphandle kwemisebenzi;', 'XHO22'),
(8, 4, 7, 'Esithi, Banoyolo ababugwenxa babo buxolelweyo, nabazono zigutyungelweyo;', 'XHO22'),
(8, 4, 8, 'Inoyolo indoda eya kuthi iNkosi ingakhe ibalele sono kuyo.', 'XHO22'),
(8, 4, 9, 'Olu luyolo ngoko lunconywayo lulolwabolwaluko bodwa na? Asilulo olwabo bangebolwaluko nabo na? Kuba sithi, kuAbraham ukholo lwabalelwa ebulungiseni.', 'XHO22'),
(8, 4, 10, 'Kwathiwani na ke ngoko ukubalelwa kuye? Wayekuko nokuba walukile na? Hayi, ndisekungalukini.', 'XHO22'),
(8, 4, 11, 'Waza wamkela umqondiso wolwaluko, waba litywina lokuqinisa ubulungisa bokholo awayenalo esekungalukini, ukuze abe nguyise wabo bonke abakholwayo besekubeni bengalukanga; ukuze babalelwe kubo ubulungisa;', 'XHO22'),
(8, 4, 12, 'abe nguyise wabalukileyo, kungengabolwaluko bodwa;', 'XHO22'),
(8, 4, 13, 'Kuba ukumbeka ngedinga uAbraham, nembewu yakhe, lokuba yindlalifa yalo ihlabathi, bekungengomthetho; bekungobulungisa bokholo.', 'XHO22'),
(8, 4, 14, 'Kuba abo basemthethweni, ukuba ngabo iindlalifa, ukholo lungaba lwenziwe into engento, nedinga lingaba liphuthisiwe;', 'XHO22'),
(8, 4, 15, 'Kaloku umthetho usebenza ingqumbo. Ke apho kungekho mthetho, akukho sigqitho.', 'XHO22'),
(8, 4, 16, 'Ngoko ke ilifa liphuma elukholweni, ukuze libe ngokobabalo; ukuze idinga liqiniseke kuyo yonke imbewu; kungekuphela nje abo basemthethweni, kodwa kwanabo baselukholweni luka-Abraham; ngubani ubawo wethu sonke.', 'XHO22'),
(8, 4, 17, 'Njengokuba kubhaliwe kwathiwa, Ndikumisile ukuba ube nguyise weentlanga ezininzi;', 'XHO22'),
(8, 4, 18, 'Owakholwayo ngethemba, kungekho nethemba eli, ukuze abe nguyise weentlanga ezininzi, njengoko kwakutshiwoyo ukuthi, Iya kuba njalo imbewu yakho.', 'XHO22'),
(8, 4, 19, 'Akazange abe buthathaka elukholweni, akawukhathalelanga umzimba wakhe owawusele ufile, xa wayeneminyaka emalunga nekhulu ubudala, kwanakukufa kwesizalo sikaSara.', 'XHO22'),
(8, 4, 20, 'Akazange athandabuze idinga likaThixo ngokungakholwa; womelela elukholweni, ezukisa uThixo;', 'XHO22'),
(8, 4, 21, 'Eqinisekile ukuba oko akuthembisileyo, unako nokukwenza.', 'XHO22'),
(8, 4, 22, 'Kungoko kwabalelwa nokubalelwa ebulungiseni kuye.', 'XHO22'),
(8, 4, 23, 'Ke kaloku, ukuba kwabalelwa kuye, akubhalwanga ngenxa yakhe yedwa;', 'XHO22'),
(8, 4, 24, 'Kodwa nangenxa yethu, baza kubalelwa kuthi, thina bakholwayo kulowo wamvusayo uYesu iNkosi yethu kwabafileyo;', 'XHO22'),
(8, 4, 25, 'owanikelwa ngenxa yezono zethu, waza wabuya wavuswa ngenxa yokugwetyelwa kwethu.', 'XHO22'),
(8, 5, 1, 'Ngoko, sigwetyelwe nje ngokwaselukholweni, sinoxolo kuThixo ngayo iNkosi yethu uYesu Kristu;', 'XHO22'),
(8, 5, 2, 'Esaba nako ngaye ukuthi singene ngokholo kolu lubabalo simiyo kulo, siqhayise ngokuthemba uzuko lukaThixo.', 'XHO22'),
(8, 5, 3, 'Asikukuphela ke oko; sizingca nangeembandezelo ezi, sisazi ukuba imbandezelo isebenza unyamezelo;', 'XHO22'),
(8, 5, 4, 'unyamezelo ke lusebenza ukucikideka; namava, ithemba;', 'XHO22'),
(8, 5, 5, 'Ithemba alidanisi; ngokuba uthando lukaThixo luthululelwe ezintliziyweni zethu ngoMoya oyiNgcwele, esamnikwayo.', 'XHO22'),
(8, 5, 6, 'Kuba uKristu uthe, sakuba sisengama-athalala, wabafela ngexesha elimisiweyo abangahloneli Thixo.', 'XHO22'),
(8, 5, 7, 'Kuba kungangenkankulu ukuthi ubani afele ilungisa; kuba uthi mhlawumbi ubani abe nobuganga bokumfela olungileyo;', 'XHO22'),
(8, 5, 8, 'Ke yena uThixo uqondakalisa okwakhe ukusithanda ngokuthi, sakubon’ ukuba sisengaboni, uKristu asifele.', 'XHO22'),
(8, 5, 9, 'Kokukhona sigwetyelwe nje ngegazi lakhe, siya kusindiswa ngaye kuyo ingqumbo.', 'XHO22'),
(8, 5, 10, 'Kuba xa sithe, besiziintshaba nje, saxolelaniswa noThixo ngako ukufa koNyana wakhe: kokhona, sixolelanisiwe nje, siya kusindiswa ngobomi bakhe.', 'XHO22'),
(8, 5, 11, 'Asikukuphela ke oko; singabazingca nokuzingca nangaye uThixo, ngayo iNkosi yethu uYesu Kristu, esamkele ngayo kalokunje ucamagushelo.', 'XHO22'),
(8, 5, 12, 'Ngenxa yoko, njengokuba isono sangena ngamntu mnye ehlabathini, kwangena ke ukufa ngaso isono; kwaza ngokunjalo ukufa kwabatyhutyha abantu bonke, ekubeni bonke bonayo;', 'XHO22'),
(8, 5, 13, 'Kuba kude kwakho umthetho, kukho isono ehlabathini, sibe ke isono singabalelwa ekubeni ungekho umthetho.', 'XHO22'),
(8, 5, 14, 'Ke ukufa kwalawula, ukususela kuAdam kwezisa kuMoses, nakwabo bangonanga ngendlela efana nesigqitho sika-Adam, ongumfanekiselo walowo ubeza kubakho.', 'XHO22'),
(8, 5, 15, 'Kodwa ke sona isipho asibanga njengesiphoso. Kuba, xa abaninzi bafa sisiphoso somnye, kokukhona ubabalo lukaThixo, nesipho esingobabalo olu, esa ngamntu mnye, uYesu Kristu, lwaphuphumela kubo abaninzi aba.', 'XHO22'),
(8, 5, 16, 'Kaloku isipho sesiphoso asinjengokuba kwaba njalo ngalowo mnye wonayo. Kuba okunene isigwebo seza ngenxa yalowo mnye, sesa ekugwetyweni; ke sona isibabalo seza ngenxa yeziphoso ezininzi, sesa ekugwetyelweni.', 'XHO22'),
(8, 5, 17, 'Kaloku xa kwathi ngesono samntu mnye ukufa kwalawula ngalowo mnye; kobeka phi na ke abamkela ukuphuphuma kobabalo, noko kuphiwa ubulungisa, baya kulawula ebomini ngaye lo umnye, uYesu Kristu.', 'XHO22'),
(8, 5, 18, 'Ngoko ke, njengokuba kwathi ngasiphoso sinye kwabakho ukugwetywa kwabo bonke abantu; Ngokunjalo nangesenzo esinye sobulungisa, isipho sesisa kwabafikela bonke abantu, begwebela ubomi.', 'XHO22'),
(8, 5, 19, 'Kuba njengokuba kwathi, ngokungeva komntu omnye lowo, abaninzi benziwa aboni, ngokunjalo kuya kuthi, nangokuva komnye lo, abaninzi benziwe amalungisa.', 'XHO22'),
(8, 5, 20, 'Ke kaloku umthetho ungene, ukuze isikreqo sande. Ke apho sandayo isono, lwaphuphuma ngakumbi ubabalo;', 'XHO22'),
(8, 5, 21, 'ukuze, njengokuba isono saba nokulawula ngokufa, ngokunjalo nobabalo lube nokulawula ngobulungisa, luse ebomini obungunaphakade ngoYesu Kristu, iNkosi yethu.', 'XHO22'),
(8, 6, 1, 'Sithini na ke ngoko? Sihlale esonweni na, ukuze ubabalo lwande?', 'XHO22'),
(8, 6, 2, 'Nakanye! Thina safayo nje kwisono, singathini na ukuba sahlala kuso?', 'XHO22'),
(8, 6, 3, 'Anazi na ukuba thina sonke, sabhaptizelwayo kuKristu Yesu, sabhaptizelwa ekufeni kwakhe?', 'XHO22'),
(8, 6, 4, 'Ngoko ke sangcwatywa naye ngokubhaptizelwa ekufeni, ukuze, njengokuba uKrestu wavuswayo kwabafileyo ngalo uzuko loYise, sithi ngokunjalo nathi sihambe sinobomi obutsha.', 'XHO22'),
(8, 6, 5, 'Kuba xa similiselwe kuye ngemfano yokufa kwakhe, somiliselwa kuye nangeyokuvuka kwakhe;', 'XHO22'),
(8, 6, 6, 'sikwazi nje oku, ukuba umntu wethu omdala wabethelelwa emnqamlezweni kunye naye, ukuze umzimba wesono uphuthiswe, size singabi sasikhonza isono;', 'XHO22'),
(8, 6, 7, 'Kaloku owafayo ugwetyelwe, wakhululwa kuso isono.', 'XHO22'),
(8, 6, 8, 'Ukuba ke safa kunye noKristu, siyakholwa ukuba sodla ubomi kwandawonye naye;', 'XHO22'),
(8, 6, 9, 'Siyazi ukuba uKristu, evusiwe nje kwabafileyo, akabi safa; ukufa akusayi kuba nagunya kuye.', 'XHO22'),
(8, 6, 10, 'Kuba ukufa awakufayo, wafela isono kwaba kanye;', 'XHO22'),
(8, 6, 11, 'Ngokunjalo nani zibaleleni ekuthini, ningabafileyo okunene kwisono; kodwa ningabadla ubomi kuThixo, nikuKristu Yesu, iNkosi yethu.', 'XHO22'),
(8, 6, 12, 'Ngoko ke isono masingalawuli emzimbeni wenu onokufa, ukuze nisilulamele ngokwenza iinkanuko zawo.', 'XHO22'),
(8, 6, 13, 'Kaloku amalungu enu maningawanikeli kuso isono, ukuba abe ziintonga zentswela-bulungisa;', 'XHO22'),
(8, 6, 14, 'Kaloku isono asiyi kuniphatha ngabukhosi; kuba aniphantsi komthetho, niphantsi kobabalo.', 'XHO22'),
(8, 6, 15, 'Kuthiweni na ngoko? Sone na, kuba singekho phantsi komthetho, siphantsi kobabalo? Makube lee oko.', 'XHO22'),
(8, 6, 16, 'Anazi na ukuba lowo nizinikela kuye ukuba ngabakhonzi bokumlulamela, ningabakhonzi ngoku kulowo nimlulamelayo; nokuba kungokwesono, kusingise ekufeni, nokuba kungokululama, kusingise ebulungiseni?', 'XHO22'),
(8, 6, 17, 'Makabulelwe ke kuThixo, kuba nithe, beningabakhonzi besono, nawululamela ngokwentliziyo umfuziselo wemfundiso enanikelwayo kuwo;', 'XHO22'),
(8, 6, 18, 'Nakhululwa ke kuso isono, naba ngabalawulwa bobulungisa.', 'XHO22'),
(8, 6, 19, 'Ndithetha ngokwabantu, ngenxa yobulwelwe benyama yenu; kuba njengoko nawanikelayo amalungu enu ukuba abe ngabakhonzi bako ukungcola nokuchasa umthetho, ukuba nichase umthetho; ngokunjalo ke ngoku, wanikeleni amalungu enu ukuba abe ngabakhonzi kubo ubulungisa, ukuba ningcwaliseke.', 'XHO22'),
(8, 6, 20, 'Kuba, oko nibe ningabakhonzi besono, beningabakhululekileyo kubo ubulungisa.', 'XHO22'),
(8, 6, 21, 'Naye ninasiqhamo sini na ke ngelo xesha, ngezi zinto nineentloni ngazo ngoku? Kuba isiphelo sezo zinto kukufa.', 'XHO22'),
(8, 6, 22, 'Ngoku ke, nikhululwe nje esonweni, naba ngabakaThixo, ninaso isiqhamo senu esisabungcwele, isiphelo ke eso bubomi obungunaphakade.', 'XHO22'),
(8, 6, 23, 'Kaloku umvuzo wesono kukufa; ke sona isipho sikaThixo bubomi obungunaphakade, kuKristu Yesu iNkosi yethu.', 'XHO22'),
(8, 7, 1, 'Anazi na, bazalwana (kuba ndithetha kwabawaziyo umthetho), ukuba umthetho umphatha ngobukhosi umntu ngexesha lonke adla ubomi ngalo?', 'XHO22'),
(8, 7, 2, 'Kuba umfazi owendileyo, ubotshiwe ngumthetho endodeni yakhe, lo gama idla ubomi; ke ukuba ithe yafa indoda, ukhululekile kuwo umthetho wendoda.', 'XHO22'),
(8, 7, 3, 'Ngoko ke, ukuba uthe waba kuyimbi indoda, yakubon’ ukuba indoda yakhe isadla ubomi, kothiwa ngumkrexezikazi, ukuba angabi mkrexezikazi, ethe waba kuyimbi indoda.', 'XHO22'),
(8, 7, 4, 'Ngoko ke, bazalwana bam, nani n’abulawa nafa kuwo umthetho ngawo umzimba kaKristu; ukuze nibe kuwumbi, kulowo ke uvukileyo kwabafileyo, ukuze sixakathe isiqhamo kuThixo thina.', 'XHO22'),
(8, 7, 5, 'Kuba oko besikwinyama, ukuthabatheka zizono, okube kuxhokonxwa ngawo umthetho, kwakusebenza emalungwini ethu ekuxakatha isiqhamo esisa ekufeni.', 'XHO22'),
(8, 7, 6, 'Ke ngoku sikhululwe thina kuwo umthetho, safayo nje kwesasibanjwe kuko, ukuze sikhonze sinobutsha bomoya, singakhonzi sinobudala bomthetho obhaliweyo.', 'XHO22'),
(8, 7, 7, 'Sithini na ke ngoko? Umthetho usisono na? Makube lee oko. Ke ngendingasazanga isono, bekungekabikho emthethweni; kuba nenkanuko ngendingayazanga, ukuba umthetho ubungatshongo ukuthi, Uze ungakhanuki.', 'XHO22'),
(8, 7, 8, 'Isono ke sizuze ithuba ngawo umthetho, sasebenza ngaphakathi kwam yonke inkanuko; kuba, kungekho mthetho, isono sifile.', 'XHO22'),
(8, 7, 9, 'Mna ke bendifudula ndiphilile, ungekho umthetho; uthe ke wakufika umthetho, sabuya saphila isono, ndafa ke mna;', 'XHO22'),
(8, 7, 10, 'waza umthetho obumiselwe ukusa ebomini, ndawufumana usingisa ekufeni.', 'XHO22'),
(8, 7, 11, 'Kuba isono, sizuze ithuba ngawo umthetho, sandilukuhla, saza ngawo sandibulala.', 'XHO22'),
(8, 7, 12, 'Ngoko ke umyalelo okunene ungcwele; nomthetho ungcwele, unobulungisa, ulungile.', 'XHO22'),
(8, 7, 13, 'Oko ke kulungileyo kwaba kukufa na ke kum? Makube lee oko. Isono ke, ukuze sibonakale sisisono, sisebenza ukufa kum ngako oko kulungileyo; ukuze isono ngawo umthetho sibe sisono ngokuncamisileyo.', 'XHO22'),
(8, 7, 14, 'Siyazi ke ukuba umthetho ungowomoya; ke mna ndingowenyama; kuthengiswe ngam, ndaba phantsi kwesono.', 'XHO22'),
(8, 7, 15, 'Kuba oko ndikwenzayo andikwazi mna; kuba endikuthandayo, oko andikwenzi; kodwa endikuthiyayo, oko ndiyakwenza.', 'XHO22'),
(8, 7, 16, 'Ukuba ke endingakuthandiyo ndenza khona oko, ndivumelana nomthetho ukuba mhle.', 'XHO22'),
(8, 7, 17, 'Ngoko ke ayisendim osebenza oko, seyisisono esi simiyo ngaphakathi kwam.', 'XHO22'),
(8, 7, 18, 'Kuba ndiyazi ukuba ngaphakathi kwam, oko kukuthi enyameni yam, akumi okulungileyo; kuba ukuthanda kukho ukubakho oku kum; ke kona ukwenza okulungileyo andikufumani.', 'XHO22'),
(8, 7, 19, 'Kuba okulungileyo endikuthandayo, andikwenzi; khona okubi, endingakuthandiyo, oko ndiyakwenza.', 'XHO22'),
(8, 7, 20, 'Ukuba ke endingakuthandiyo ukwenza oko, ayisendim okwenzayo oko, seyisisono esi simiyo ngaphakathi kwam.', 'XHO22'),
(8, 7, 21, 'Ndifumana ngoko lo mthetho kum, wokuba kuthi, ndifuna ukwenza okulungileyo, kusuke kubekho okubi kum.', 'XHO22'),
(8, 7, 22, 'Kuba ndikholene nomthetho kaThixo ngokomntu ongaphakathi;', 'XHO22'),
(8, 7, 23, 'Kodwa ndibona mthetho wumbi, emalungwini am, usilwa nomthetho wengqiqo yam, undithimbela emthethweni wesono, lowo usemalungwini am.', 'XHO22'),
(8, 7, 24, 'Athi ke mna, mntu uludwayi! Ngubani na oya kundihlangula kulo mzimba woku kufa?', 'XHO22'),
(8, 7, 25, 'Ndiyambulela uThixo ngoYesu Krestu iNkosi yethu. Ngoko ke mna ngokwam ngayo ingqiqo okunene ndikhonza umthetho kaThixo; kodwa ngayo inyama umthetho wesono.', 'XHO22'),
(8, 8, 1, 'Ngoko ke, ngoku akukho kugwetywa kwabakuKristu Yesu; abangahambiyo ngokwenyama, abahamba ngokoMoya.', 'XHO22'),
(8, 8, 2, 'Kaloku ngokumanywa kwam noKrestu Yesu, uMoya umniki-bomi undikhulule ekulawulweni sisono nakukufa.', 'XHO22'),
(8, 8, 3, 'Kuba oko wawungenako umthetho, ngenxa yokuba uswele amandla ngayo inyama, uThixo wamthuma owakhe uNyana, ekwimeko yenyama enesono, ngenxa yesono, wasigweba isono esenyameni;', 'XHO22'),
(8, 8, 4, 'ukuze ubulungisa bomthetho buzaliseke kuthi, thina singahambiyo ngokwenyama, sihamba ngokoMoya.', 'XHO22'),
(8, 8, 5, 'Abo bangokwenyama banyamekela izinto zasenyameni; abo ke bangokoMoya banyamekela izinto zoMoya.', 'XHO22'),
(8, 8, 6, 'Kaloku ukunyameka kwenyama ikukufa; ke ukunyamekela ezoMoya bubomi noxolo.', 'XHO22'),
(8, 8, 7, 'Kaloku ukunyameka kwenyama kububutshaba kuThixo, kuba umthetho kaThixo kungawululameli, kuba kungenako nokuba nako ukwenjenjalo.', 'XHO22'),
(8, 8, 8, 'Abo ke bakwinyama, abanakumkholisa uThixo.', 'XHO22'),
(8, 8, 9, 'Ke nina aningabo abaphila ngokwesimo esonakeleyo, ningabo bakhokelwa nguMoya, ukuba kambe uMoya kaThixo uhleli kuni. Ukuba ke umntu akanaye uMoya kaKristu, akangowakhe lowo.', 'XHO22'),
(8, 8, 10, 'Ukuba ke uKristu ungaphakathi kwenu, umzimba okunene ufile ngenxa yesono; kodwa uMoya ububomi ngenxa yobulungisa.', 'XHO22'),
(8, 8, 11, 'Ukuba ke uMoya walowo wamvusayo uYesu kwabafileyo, umi ngaphakathi kwenu, yena owamvusayo uKristu kwabafileyo uya kuyenza iphile nemizimba yenu enokufa, ngaye uMoya wakhe omiyo ngaphakathi kwenu.', 'XHO22'),
(8, 8, 12, 'Ngoko ke, bazalwana, singabanetyala; kodwa kungekuyo inyama ukuze siphile ngokwenyama.', 'XHO22'),
(8, 8, 13, 'Kuba xa niphila ngokwenyama, niza kufa; ke ukuba niyazibulala ngoMoya, zife iintlondi zomzimba, nophila.', 'XHO22'),
(8, 8, 14, 'Kaloku abo bakhokelwa nguMoya kaThixo, bangoonyana bakaThixo.', 'XHO22'),
(8, 8, 15, 'Kuba anamkelanga moya wabukhoboka, ukuba nibuye noyike; namkele uMoya wokwenziwa oonyana, esidanduluka ngaye sithi, Abha, Bawo.', 'XHO22'),
(8, 8, 16, 'uMoya ngokwakhe ungqinelana nomoya wethu, ukuba singabantwana bakaThixo.', 'XHO22'),
(8, 8, 17, 'Ukuba ke singabantwana, sikwaziindlalifa; iindlalifa zikaThixo, iindlalifa ke kunye noKristu; ukuba okunene siva ubunzima kunye naye, ukuze sizukiswe nokuzukiswa kunye naye.', 'XHO22'),
(8, 8, 18, 'Ndigqiba kwelithi, iintlungu zeli xesha lakalokunje azinakulinganiswa nobuqaqawuli obu buza kutyhilwa kuthi.', 'XHO22'),
(8, 8, 19, 'Kuba ukulangazelela kwendalo kulinde ukutyhileka koonyana bakaThixo.', 'XHO22'),
(8, 8, 20, 'Kuba indalo yathotyelwa kwimeko ephuthileyo, kungengakuthanda kwayo, kodwa kungokuthanda kwalowo wayithobayo, enethemba;', 'XHO22'),
(8, 8, 21, 'Ngenxa yokuba nayo indalo ngokwayo iya kukhululwa ebukhobokeni bokonakala, isingiswe enkululekweni yozuko lwabantwana bakaThixo.', 'XHO22'),
(8, 8, 22, 'Kuba siyazi ukuba kude kube ngokunje yonke indalo iyancwina inenimba.', 'XHO22'),
(8, 8, 23, 'Asiyiyo ke yodwa; sisuka nathi, banayo intlahlela yoMoya, thina aba ke ngokwethu, sincwine ngaphakathi kwethu, silinde ukwenziwa oonyana; inkululeko yomzimba wethu ke leyo.', 'XHO22'),
(8, 8, 24, 'Kaloku sasindiselwa ethembeni; ke ithemba elise libonwa asithemba; kuba oko akubonayo umntu, angaba usakuthembeleni na?', 'XHO22'),
(8, 8, 25, 'Kodwa ke into esingayiboniyo, esithembe ukuyifumana, siyilindela ngomonde.', 'XHO22'),
(8, 8, 26, 'Ngokukwanjalo ke noMoya uncedisana nokuswela kwethu amandla, kuba esingakuthandazelayo ngokoko simelwe kukuthandaza ngako, asikwazi; uthi ke uMoya ngokwakhe asithethelele ngezincwino ezingathethekiyo.', 'XHO22'),
(8, 8, 27, 'Ke oziphengululayo iintliziyo uyakwazi into okuyiyo ukunyameka koMoya; ngokuba ubathethelela abangcwele ngokukaThixo.', 'XHO22'),
(8, 8, 28, 'Siyazi ke ukuba zonke izinto zisebenziselana okulungileyo kwabo bamthandayo uThixo, kwababiziweyo ngokwengqibo yakhe.', 'XHO22'),
(8, 8, 29, 'Ngokuba abo wabaziyo ngenxa engaphambili, wabamisela ngenxa engaphambili ukuba bafane nomfanekiselo woNyana wakhe, ukuze abe ngowamazibulo kubazalwana abaninzi.', 'XHO22'),
(8, 8, 30, 'Abo ke wabamiselayo kwangaphambili, wababiza kananjalo; abo ke wababizayo, wabuya wabagwebela bona; abo ke wabagwebelayo, wabuya wabazukisa kananjalo.', 'XHO22'),
(8, 8, 31, 'Sithini na ke ngoko kwezi zinto? Ukuba uThixo ungakuthi, ngubani na ongasichasayo?', 'XHO22'),
(8, 8, 32, 'Lowo, gxebe, ungamconganga owakhe uNyana, wesuka wamnikela ngenxa yethu sonke, angathini na ukuba angasibabali ngazo zonke ezo zinto, ndawonye naye?', 'XHO22'),
(8, 8, 33, 'Ngubani na oya kubamangalela abanyulwa bakaThixo, inguThixo nje obagwebelayo? nguThixo ogwebelayo.', 'XHO22'),
(8, 8, 34, 'Ngubani na ogwebayo? nguKristu owafayo, wathi ngaphezu koko, wabuya wavuka, ongasekunene kukaThixo, osithethelelayo.', 'XHO22'),
(8, 8, 35, 'Ngubani na oya kusahlula eluthandweni lukaKristu? Yimbandezelo na, luxinaniso na, yintshutshiso na, yindlala na, bubuze na, yingozi na, likrele na?', 'XHO22'),
(8, 8, 36, 'Njengoko kubhaliwe kwathiwa, Ngenxa yakho sibulawa imini yonke; yiyo loo nto sesifana neegusha eziya kuxhelwa nje.', 'XHO22'),
(8, 8, 37, 'Kanti ke, kwezi zinto zonke sisuka soyise sitshatshele ngalowo wasithandayo.', 'XHO22'),
(8, 8, 38, 'Kuba ndeyisekile kukuba nakufa, nabomi, nazithunywa zezulu, nazilawuli, nanto zikhoyo, nanto zizayo, nanto zizayo;', 'XHO22'),
(8, 8, 39, 'Nabuphakamo, nabunzulu, nasinye isidalwa, asiyi kuba nako ukusahlula thina eluthandweni lukaThixo, olukuye uKristu Yesu, iNkosi yethu.', 'XHO22'),
(8, 9, 1, 'Ndithetha inyaniso ndikuKristu, andixoki, sanesazela sam sindingqinela nakuMoya oyiNgcwele;', 'XHO22'),
(8, 9, 2, 'ngokuba ndineentlungu kakhulu, nentuthumbo enkulu engapheliyo, entliziyweni yam.', 'XHO22'),
(8, 9, 3, 'Kuba bendingade ndinqwenele ukusingelwa phantsi mna ngokwam, ndaphuma kuKristu ngenxa yabazalwana bam, abayimizalwane yam ngokwenyama;', 'XHO22'),
(8, 9, 4, 'Ngoobani bona oonyana bakaSirayeli; ekukokwabo ukwenziwa oonyana, nokuzukiswa, nokuphiwa iminqophiso, nokuwiswa komthetho, nenkonzo kaThixo, namadinga;', 'XHO22'),
(8, 9, 5, 'abangababo ooyise; awaphuma kubo uKristu ngokwenyama, uThixo ongophezu konke, engowokubongwa kude kube ngunaphakade. Amen.', 'XHO22'),
(8, 9, 6, 'Ke, xa nditshoyo, andithethi nto nje, ngokungathi ilizwi likaThixo liphalele. Kuba asingabo bonke abaphuma kuSirayeli abangamaSirayeli;', 'XHO22'),
(8, 9, 7, 'kananjalo abathe, ngakuba beyimbewu ka-Abraham, baba ngabantwana bonke; kusuke kwathiwa, Imbewu yakho iya kubizwa ngoIsake;', 'XHO22'),
(8, 9, 8, 'Oko kukuthi, asingabantwana benyama abangabantwana bakaThixo; ngabantwana bedinga ababalelwa ekuthini bayimbewu.', 'XHO22'),
(8, 9, 9, 'Kuba lilo eli ilizwi ledinga, Ngeli xesha nyakenye ndiya kuza, waye uSara eya kuba nonyana.', 'XHO22'),
(8, 9, 10, 'Asiyiyo le yodwa; kwathi nakuRebheka, akubon’ ukuba ukhawule ngamnye, ngoIsake ubawo wethu.', 'XHO22'),
(8, 9, 11, '(Kuba abantwana bengekazalwa, bengenzanga nto ilungileyo, nokuba imbi, ukuze icebo likaThixo elingokonyulo lime, lingabi ngokwasemisebenzini, libe liphuma kulowo ubizayo);', 'XHO22'),
(8, 9, 12, 'Kwathiwa kuye, Omkhulu uya kukhonza komnci;', 'XHO22'),
(8, 9, 13, 'njengokuba kubhaliwe kwathiwa, uYakobi ndamthanda, ke uEsawu ndamthiya.', 'XHO22'),
(8, 9, 14, 'Sithini na ke ngoko? Kukho ukuswela kulungisa kusini na kuye uThixo? Makube lee oko.', 'XHO22'),
(8, 9, 15, 'Kuba esithi kuMoses, Ndiya kuba nenceba kosukuba ndinenceba kuye, ndibe netarhu kosukuba ndinetarhu kuye.', 'XHO22'),
(8, 9, 16, 'Ngoko ke asikokothandayo, asikokobalekayo; kokukaThixo onenceba.', 'XHO22'),
(8, 9, 17, 'Kuba isibhalo sithi kuFaro, Ndakuvelisela yona le nto, yokuba ndiwabonakalalisele kuwe amandla am, nokuthi laziswe igama lam kulo lonke ihlabathi.', 'XHO22'),
(8, 9, 18, 'Ngoko ke unenceba kwathanda ukumenzela inceba, athi ke athandayo amenze lukhuni.', 'XHO22'),
(8, 9, 19, 'Uya kuthi kum ngoko, Usasolelani na ke? Kuba ngubani na omelana necebo lakhe?', 'XHO22'),
(8, 9, 20, 'Ehla ke, mntundini! ungubani na wena, ukuba uphendulane noThixo? Into exonxiweyo ingatsho na kumxonxi wayo ukuthi, Yini na ukuba undenjenje ukundenza?', 'XHO22'),
(8, 9, 21, 'Akanagunya na umbumbi phezu kodongwe ukuba enze kwangaloo ntlama, esinye isitya sibe sebekeka, nesinye esingabekekanga?', 'XHO22'),
(8, 9, 22, 'Hi, ke, ukuba uThixo, ethanda ukubonakalalisa ingqumbo yakhe, nokwazisa amandla akhe, wathwala enokuzeka kade okukhulu izitya zengqumbo, zilungiselwe intshabalalo;', 'XHO22'),
(8, 9, 23, 'ukuze abaze ubutyebi bozuko lwakhe ezityeni zenceba, abezilungisele uzuko ngenxa engaphambili;', 'XHO22'),
(8, 9, 24, 'ezi wazibizayo, kwathina aba, kungekuwo amaYuda odwa, kukwiintlanga nazo?', 'XHO22'),
(8, 9, 25, 'Njengokuba esithi nakwekaHoseya, Ndiya kubabiza abangebantu bam ngokuba ngabantu bam; nentanda yakhe, engathandwa.', 'XHO22'),
(8, 9, 26, 'Kwathi, apho kwabe kusithiwa kubo, Aningobantu bam nina; baya kubizwa khona ngokuthi, bangoonyana bakaThixo ophilileyo.', 'XHO22'),
(8, 9, 27, 'Kananjalo uIsaya udanduluka ngoSirayeli, esithi, Nangona inani loonyana bakaSirayeli lingangentlabathi yaselwandle, kosinda amasalela odwa.', 'XHO22'),
(8, 9, 28, 'Kuba elifeza ilizwi, aligqibe ngobulungisa; ngokuba iNkosi iya kwenza ilizwi layo phezu komhlaba, ilifinyeze.', 'XHO22'),
(8, 9, 29, 'Kwanjengokuba uIsaya ebetshilo ngenxa engaphambili ukuthi, Ukuba iNkosi wemikhosi ibingasishiyelanga mbewu, singe sisuke saba njengeSodom, sisuke safana neGomora.', 'XHO22'),
(8, 9, 30, 'Sithini na ke ngoko? Sithi, iintlanga ebezingasukeli bulungisa, zabubamba ubulungisa, ubulungisa ke baselukholweni.', 'XHO22'),
(8, 9, 31, 'Kodwa yena uSirayeli obesukela umthetho wobulungisa, akafikelelanga emthethweni wobulungisa.', 'XHO22'),
(8, 9, 32, 'Ngani? Ngokuba abakufunanga ngokwaselukholweni, bakufuna ngokwasemisebenzini yomthetho. Kuba babetheka elityeni eli lokubetheka;', 'XHO22'),
(8, 9, 33, 'njengokuba kubhaliwe kwathiwa, Yabona, ndibeka eZiyon ilitye lokubetheka, Iliwa lesikhubekiso; bonke abakholwayo kuye abayi kudaniswa.', 'XHO22'),
(8, 10, 1, 'Bazalwana, inkolelo yeyam intliziyo, nesikhungo sam kuye uThixo, ngenxa yamaSirayeli, sesokuba asindiswe.', 'XHO22'),
(8, 10, 2, 'Ndiyabangqinela ukuba banokumzondelela uThixo, koko bengamzondeleli ngokokwazi okukuko.', 'XHO22'),
(8, 10, 3, 'Kuba bathe, bengabazi ubulungisa bukaThixo, befuna nokumisa obabo ubulungisa, ababululamela ubulungisa bukaThixo.', 'XHO22'),
(8, 10, 4, 'Kaloku intsingiselo yomthetho nguKrestu, ukuze abe bubulungisa kubo bonke abakholwayo.', 'XHO22'),
(8, 10, 5, 'Kuba uMoses ubulungisa basemthethweni uthi ukububhala, Umntu ozenzileyo ezi zinto uya kudla ubomi ngazo.', 'XHO22'),
(8, 10, 6, 'Ke bona obaselukholweni ubulungisa buthetha ngolu hlobo, Musa ukuthi entliziyweni yakho, Ngubani na oya kunyuka aye emazulwini ukuba ahlise uKristu? oko kukuthi, ukumhlisa uKristu phezulu;', 'XHO22'),
(8, 10, 7, 'nokuthi, Ngubani na oya kuhla aye enzonzobileni? oko kukuthi, ukumnyusa uKristu kwabafileyo.', 'XHO22'),
(8, 10, 8, 'Ithini na ke le nto? Ilizwi likufuphi kuwe, emlonyeni wakho nasentliziyweni yakho: elo ke lilizwi lokholo, esilivakalisayo.', 'XHO22'),
(8, 10, 9, 'Ngokuba, xa uthe wamvuma ngomlomo wakho ukuba uyiNkosi uYesu, wakholwa ngentliziyo yakho ukuba uThixo wamvusa kwabafileyo, wosindiswa.', 'XHO22'),
(8, 10, 10, 'Kuba umntu ukholwa ngentliziyo, ukuze abe nobulungisa; kwaye ngomlomo uvumo lwenziwa losindiso.', 'XHO22'),
(8, 10, 11, 'Kuba isibhalo sithi, Bonke abakholwayo kuye abayi kudaniswa.', 'XHO22'),
(8, 10, 12, 'Akukho kwahluka kwamYuda namGrike; kuba ikwayiloo Nkosi eyiNkosi yabo bonke, ibubutyebi kubo bonke abayinqulayo.', 'XHO22'),
(8, 10, 13, 'Kuba bonke abasukuba belinqula igama leNkosi bosindiswa.', 'XHO22'),
(8, 10, 14, 'Bothini na ke ukumnqula lowo bangakholwanga kuye? Bothini na ukukholwa kulowo bangevanga ngaye? Bothini na ke ukuva, kungekho mshumayeli?', 'XHO22'),
(8, 10, 15, 'Bangathini na ke ukushumayela, bengathunywanga? njengokuba kubhaliwe kwathiwa, Hayi, ukuba zintle kwazo iinyawo zabashumayela iindaba ezilungileyo zoxolo, zabashumayela iindaba ezilungileyo zezinto ezilungileyo!', 'XHO22'),
(8, 10, 16, 'Kodwa abazilulamelanga bonke iindaba ezilungileyo. Kuba uIsaya uthi, Nkosi, ngubani na okholiweyo ludaba lwethu?', 'XHO22'),
(8, 10, 17, 'Ngoko ke ukholo luphuma eludabeni, udaba ke lukho ngelizwi likaThixo.', 'XHO22'),
(8, 10, 18, 'Ndithi ke, Abevanga na? Ilizwi labo laphuma laya kulo lonke ihlabathi, Namazwi abo aya eziphelweni zelimiweyo.', 'XHO22'),
(8, 10, 19, 'Ndithi ke, Kodwa ubengazi na uSirayeli? Kuqala uMoses uthi, Ndiya kunikhweletisa ngabangebantu, ndiniqumbise ngohlanga olunobudenge.', 'XHO22'),
(8, 10, 20, 'Ke yena uIsaya akanqwanqwi, uthi, Ndifunyenwe ngababengandifuni; ndabonakalaliswa kwababengandifuni.', 'XHO22'),
(8, 10, 21, 'Kodwa kuSirayeli uthi, Imini yonke izandla zam ndizolulele kubantu abangevayo, abaphikisayo.', 'XHO22'),
(8, 11, 1, 'Ngoko ke ndithi, uThixo ubagibile na abantu bakhe? Makube lee oko. Kuba nam ndingumSirayeli, wembewu ka-Abraham, wesizwe sikaBhenjamin.', 'XHO22'),
(8, 11, 2, 'uThixo akabagibanga bona abantu bakhe, abebazi ngenxa engaphambili. Aniyazi na into esiyithethayo isibhalo ngoEliya? ukuba awangxengxezeleyo kuThixo ngoSirayeli, esithi,', 'XHO22'),
(8, 11, 3, 'Nkosi, abaprofeti bakho ababulele, nezibingelelo zakho azichithile; mna ndisele ndedwa; afuna ke umphefumlo wam.', 'XHO22'),
(8, 11, 4, 'Lithini na ke ilizwi likaThixo kuye? Ndizishiyele amadoda angamawaka asixhenxe, angaguqanga dolo kuBhahali.', 'XHO22'),
(8, 11, 5, 'Ngokukwanjalo ke ngoko, nakweli xesha lakalokunje, kukho amasalela ngokonyulo lobabalo.', 'XHO22'),
(8, 11, 6, 'Ukuba ke kungobabalo, akusengamisebenzi; okanye ubabalo alungebi saba lubabalo. Ukuba ke kungokwasemisebenzini, akuselubabalo; okanye umsebenzi awusengumsebenzi.', 'XHO22'),
(8, 11, 7, 'Kuthiweni na ke ngoko? uSirayeli akakufumananga oko abekufuna; ke abanyuliweyo bakufumana, bathi ke abanye baqaqadekiswa', 'XHO22'),
(8, 11, 8, 'njengokuba kubhaliwe kwathiwa, uThixo ubanike umoya wobuthongo, amehlo okuba bangaboni, neendlebe zokuba bangeva, kwada kwanamhlanje.', 'XHO22'),
(8, 11, 9, 'Uthi ke uDavide, Isithebe sabo masibe ngumgibe, nesirhintyelo, nesikhubekiso, nembuyekezo kubo;', 'XHO22'),
(8, 11, 10, 'Makabe mnyama amehlo abo, ukuba bangaboni; Nomhlana wabo wugobe ngamaxesha onke.', 'XHO22'),
(8, 11, 11, 'Ndithi ngoko, Bakhubeka ukuze bawe na? Nakanye! Ngesiphoso sabo usindiso lwafika kuzo iintlanga, ukuze zibakhweletise.', 'XHO22'),
(8, 11, 12, 'Ukuba ke isiphoso sabo saba bubutyebi behlabathi, nokuncipha kwabo kwaba bubutyebi beentlanga; Kobeka phi na ke inzaliseko yazo?', 'XHO22'),
(8, 11, 13, 'Kuba ndithetha kuni, zintlanga. Ekubeni okunene mna ndingumpostile weentlanga, ndiyaluzukisa ulungiselelo lwam;', 'XHO22'),
(8, 11, 14, 'ukuba ndingade ndibakhweletise abenyama yam, ndisindise inxenye yabo.', 'XHO22'),
(8, 11, 15, 'Kuba xa ukulahlwa kwabo kuluxolelaniso lwehlabathi, koba yintoni na ukwamkelwa kwabo? Akububomi obuphuma kwabafileyo, yini na?', 'XHO22'),
(8, 11, 16, 'Ukuba ke intlahlela ingcwele, ikwanjalo nentlama; ukuba ke ingcambu ingcwele, akwanjalo namasebe.', 'XHO22'),
(8, 11, 17, 'Ukuba inxenye yamasebe yavuthululwa, wathi ke wena, ungumnquma wasendle, wamiliselwa phakathi kwawo, wabelana nawo ngeengcambu nasekutyebeni komnquma;', 'XHO22'),
(8, 11, 18, 'Musani ukuwaqhayisela amasebe; ke ukuba uyawaqhayisela, yazi ukuba asinguwe oyithweleyo ingcambu;', 'XHO22'),
(8, 11, 19, 'Uya kuthi ngoko, Axhuzulwa amasebe, ukuze mna ndimiliselwe.', 'XHO22'),
(8, 11, 20, 'Ke; aqhuzulwa ngenxa yokungakholwa, ke wena umi ngokwaselukholweni; musa ukukratsha, yoyika;', 'XHO22'),
(8, 11, 21, 'Kuba, xa uThixo angawaconganga awasemvelweni amasebe, lumka hleze angakucongi nawe lo.', 'XHO22'),
(8, 11, 22, 'Khawububone ke ububele nobukhali bukaThixo; ubukhali okunene kwabo bawayo; kodwa kuwe, ububele, ukuba uthe wahlala kobo bubele; okanye wogawulwa nawe lo.', 'XHO22'),
(8, 11, 23, 'Nabo ke, ukuba abathanga bahlale ekungakholweni, bomiliselwa; kuba uThixo unako ukubuya abamilisele.', 'XHO22'),
(8, 11, 24, 'Kuba wena, xa wagawulwa kumnquma ongowasendle ngokwemvelo, waza wamiliselwa ngokunxamnye nemvelo komhle umnquma: bobeka phi na aba bangamasebe ngokwemvelo, ukumiliselwa kwabo kowabo umnquma?', 'XHO22'),
(8, 11, 25, 'Kuba andithandi, bazalwana, ukuba ningayazi le mfihlelo, ukuze ningabi ziingqondi ngokwenu; ukuba ukuqaqadeka kuwahlele amaSirayeli ngenxenye, ide ingene inzaliseko yazo iintlanga;', 'XHO22'),
(8, 11, 26, 'aze ngokunjalo ke wonke uSirayeli asindiswe, njengokuba kubhaliwe kwathiwa, Uya kuvela eZiyon uMhlanguli, Akusonge kumke kwaYakobi ukungahloneli Thixo.', 'XHO22'),
(8, 11, 27, 'Nguwo lo umnqophiso wam nabo, Xa ndithe ndazisusa izono zabo.', 'XHO22'),
(8, 11, 28, 'Okunene ngokweendaba ezilungileyo, baziintshaba ngenxa yenu; kodwa ngokonyulo, ngabaziintanda ngenxa yoobawo.', 'XHO22'),
(8, 11, 29, 'Kuba azinabuyambo izibabalo ezi, nobizo olu lukaThixo.', 'XHO22'),
(8, 11, 30, 'Kuba, njengokuba nani benifudula ningamvi uThixo, ngoku ke nenzelwe inceba kukungakholwa kwabo;', 'XHO22'),
(8, 11, 31, 'kwangokunjalo, nabo ababanga namvo ngoku, ukuze ngokwenzelwa kwenu inceba, benzelwe nabo inceba.', 'XHO22'),
(8, 11, 32, 'Kuba kaloku uThixo ubavalele ndawonye entsweleni-kuva bonke, ukuze abenzele inceba bonke.', 'XHO22'),
(8, 11, 33, 'Hayi, ubunzulu bobutyebi nobobulumko nobokwazi kukaThixo! Asikuko nokuba ayiphengululeki imigwebo yakhe, azilandeki iindlela zakhe!', 'XHO22'),
(8, 11, 34, 'Kuba ngubani na oyazileyo ingqiqo yeNkosi? Ngubani na obuye waba ngumcebisi wakhe?', 'XHO22'),
(8, 11, 35, 'Ngubani na khona owayinikayo ngenxa engaphambili, kwaza kwabuyekezwa kuye?', 'XHO22'),
(8, 11, 36, 'Ngokuba ziphuma kuyo ke, zikho ngayo, zikholo yona, zonke ezo zinto. Malube kuyo uzuko, kude kube ngunaphakade. Amen.', 'XHO22'),
(8, 12, 1, 'Ndiyanivuselela ngoko, bazalwana, ngeemfefe zikaThixo, ukuba niyinikele imizimba yenu ibe lidini eliphilileyo, elingcwele, elikholekileyo kuThixo: inkonzo yenu ke leyo yasengqondweni.', 'XHO22'),
(8, 12, 2, 'Musani ke ukuthatha isimilo sabantu beli phakade, kodwa iingqondo zenu maziguquke zihlaziyeke, nize niyivume intando kaThixo, yona ilungileyo, ikholekileyo, igqibeleleyo.', 'XHO22'),
(8, 12, 3, 'Ngalo ubabalo endababalwa ngalo ndithi kuni nonke: Musani ukuzicingela ngaphezu koko umntu amelwe kukuzicingela ngako; mabacinge ingcinga ephilileyo, njengoko uThixo abele ulowo umlinganiso wokholo.', 'XHO22'),
(8, 12, 4, 'Kuba, kwanjengokuba sinamalungu amaninzi mzimbeni mnye, abe ke onke amalungu la engenamsebenzi mnye.', 'XHO22'),
(8, 12, 5, 'Ngokunjalo thina, sibaninzi nje, simzimba mnye ngokumanywa noKrestu, yaye sonke ngabanye singamalungu omnye elomnye.', 'XHO22'),
(8, 12, 6, 'Masizisebenzise ke iziphiwo ezahlukeneyo esababalwa ngazo nguThixo. Ukuba sinesiphiwo sokuvakalisa ilizwi elivela kuThixo, masilivakalise ngokokholo esinalo;', 'XHO22'),
(8, 12, 7, 'nokuba sinobulungiseleli, masihlale ebulungiselelini obo; nokuba ngofundisayo, makahlale emfundisweni leyo;', 'XHO22'),
(8, 12, 8, 'nokuba ngovuselelayo, makahlale ekuvuseleleni oko; owabayo makabe engenakumbi; owongamelayo, makahlale enyamekile; owenza inceba makayenze inceba echwayithile.', 'XHO22'),
(8, 12, 9, 'Uthando malungabi naluhanahaniso. Kwenyanyeni okungendawo; namathelani kokulungileyo.', 'XHO22'),
(8, 12, 10, 'Mayela nothando olu lobuzalwana, ndithi, yenzelanani ububele; mayela nembeko leyo, phangelanani;', 'XHO22'),
(8, 12, 11, 'Musani ukunqena ishishini; ushushu emoyeni; khonzani iNkosi;', 'XHO22'),
(8, 12, 12, 'Vuyani ninethemba; nyamezelani embandezelweni; nizingisa emthandazweni;', 'XHO22'),
(8, 12, 13, 'Yiboneleleni iintswelo zamakholwa; ziphatheni ngokubuka iindwendwe.', 'XHO22'),
(8, 12, 14, 'Basikeleleni abanitshutshisayo; sikelelani, ningaqalekisi.', 'XHO22'),
(8, 12, 15, 'Vuyani nabavuyayo, nilile nabalilayo.', 'XHO22'),
(8, 12, 16, 'Cingelanani nto-nye; musani ukucinga izinto eziphakamileyo, tsalelani kwezithobekileyo. Musani ukuba ziingqondi ngokwenu.', 'XHO22'),
(8, 12, 17, 'Musani ukubuyekeza ububi ngobubi nakubani. Yenzani okulungileyo phambi kwabantu bonke.', 'XHO22'),
(8, 12, 18, 'Yenzani unako-nako ukuhlalisana ngoxolo nabantu bonke.', 'XHO22'),
(8, 12, 19, 'Zintanda, musani ukuziphindezelela, yikhweleleni ingqumbo; kuba iziBhalo zithi: “Impindezelo yeyam; kuya kubuyekeza mna, utsho uYehova.', 'XHO22'),
(8, 12, 20, 'Ngoko ke, ukuba utshaba lwakho lulambile, luphe ludle; ukuba lunxaniwe, luseze; kuba ngokwenjenjalo ke, woba ufumba amalahle omlilo phezu kwentloko yalo.', 'XHO22'),
(8, 12, 21, 'Musa ukoyiswa bububi; boyise ububi ngokulungileyo.', 'XHO22'),
(8, 13, 1, 'Yonke imiphefumlo mayiwalulamele amagunya awongamileyo. Kuba akukho gunya lingelilo elivela kuThixo; amagunya ke akhoyo amiswe nguThixo.', 'XHO22'),
(8, 13, 2, 'Ngoko ke, umntu olichasayo igunya umelana nommiselo kaThixo;', 'XHO22'),
(8, 13, 3, 'Kuba abaphathi aba asingabokoyikwa kwimisebenzi elungileyo, ngabokoyikwa kwemibi, ungawoyiki na ke amandla? yenza okulungileyo, wodunyiswa kwalilo;', 'XHO22'),
(8, 13, 4, 'kuba lingumlungiseleli kaThixo, wokuba kulungele wena. Ke, ukuba uthe wenza okubi, yoyika; kuba alifumane aliphathe ikrele; kuba lingumlungiseleli kaThixo, umphindezeli ngengqumbo kowenza okubi.', 'XHO22'),
(8, 13, 5, 'Kungoko ndithi, kufuneka ukulilulamela, ningakwenzi oko ngenxa yengqumbo yodwa, nikwenze nangenxa yesazela.', 'XHO22'),
(8, 13, 6, 'Kungenxa yoko le nto ninikela irhafu; kuba ngababusi bakaThixo, bezingisa ukusebenza loo nto.', 'XHO22'),
(8, 13, 7, 'Ngoko ke bonke banikeni okubafaneleyo: lowo kufuneka irhafu kuye, mnike irhafu; isiko kubani; ukoyika kofanelwe kukoyikwa; imbeko kofanelwe yimbeko.', 'XHO22'),
(8, 13, 8, 'Musani ukuba natyala lanto mntwini, lingelilo elokuthandana; kuba omthandayo omnye uzalise umthetho.', 'XHO22'),
(8, 13, 9, 'Kuba oku kuthi, Uze ungakrexezi, uze ungabulali, uze ungebi, uze ungangqini ubuxoki, uze ungakhanuki; Ukuba ke kukho wumbi umthetho, uqondwa ngokufutshane kweli lizwi lokuthi, Uze umthande ummelwane wakho ngoko uzithanda ngako.', 'XHO22'),
(8, 13, 10, 'Uthando alusebenzi bubi kuye ummelwane; ngoko ke uthando luyinzaliseko yomthetho.', 'XHO22'),
(8, 13, 11, 'Ke oko masikwenze, silazi nje ixesha eli, ukuba lilixa ngoku lokuba sivuke ebuthongweni; kuba ngoku usindiso lukufuphi kuthi, kunokuya saqalayo ukukholwa.', 'XHO22'),
(8, 13, 12, 'Ubusuku buhambile, ukusa ke kusondele. Masiyilahle ngoko imisebenzi yobumnyama, sixhobe iintonga zokukhanya.', 'XHO22'),
(8, 13, 13, 'Masihambe sinyanisekile, njengasemini; kungekhona eburheletyweni, nasekunxileni, kungekhona eburheletyweni;', 'XHO22'),
(8, 13, 14, 'Yambathani ke iNkosi uYesu Kristu;', 'XHO22'),
(8, 14, 1, 'Lowo uswele amandla elukholweni, mamkeleni, kodwa ke kungabi ngokucalula zicamango.', 'XHO22'),
(8, 14, 2, 'Omnye ukholwa ukuba angazidla izinto zonke; kodwa yena oswele amandla udla imifuno.', 'XHO22'),
(8, 14, 3, 'Lowo udlayo, makangamenzi into engeyakonto ongadliyo; nalowo ungadliyo, makangamgwebi odlayo; kuba uThixo umamkele.', 'XHO22'),
(8, 14, 4, 'Ungubani na wena ugweba isicaka somnye? Ukuma kwaso, nokuba kukuwa kwaso, kusenkosini yaso, ewe, uya kuma, kuba uThixo unako ukummisa.', 'XHO22'),
(8, 14, 5, 'Omnye umntu ubeka umhla othile kunomnye umhla, wumbi ke ubeka imihla yonke. Elowo ke makaqiniseke kweyakhe ingqiqo.', 'XHO22'),
(8, 14, 6, 'Lowo uwunyamekelayo umhla, uwunyamekelela iNkosi; nalowo ungawunyamekeliyo umhla, engawunyamekeli nje, wenzela iNkosi; lowo udlayo, udlela iNkosi, kuba ebulela kuThixo; nalowo ungadliyo, akadli nje, wenzela iNkosi, ube ebulela kuThixo.', 'XHO22'),
(8, 14, 7, 'Kuba akukho namnye kuthi uzidlelayo ubomi, akukho namnye uzifelayo.', 'XHO22'),
(8, 14, 8, 'Kuba nokuba sithi sidle ubomi, sibudlela iNkosi; nokuba sithi sife, sifela iNkosi. Ngoko ke, nokuba sithi sidle ubomi, nokuba sithi sife, singabeNkosi.', 'XHO22'),
(8, 14, 9, 'Kuba uKristu wafa, wabuya wavuka, wabuya wadla ubomi, ngenxa yoku, ukuze aphathe ubukhosi kwabafileyo, kwanakwabaphilileyo.', 'XHO22'),
(8, 14, 10, 'Ke wena, yini na ukuba umgwebe umzalwana wakho? Yini na ukuba umenze into engeyakonto umzalwana wakho? kuba siya kuma sonke phambi kwesihlalo sokugweba sikaKristu.', 'XHO22'),
(8, 14, 11, 'Kuba kubhaliwe kwathiwa, Ndihleli nje mna, itsho iNkosi, aya kugoba kum onke amadolo, Zidumise uThixo zonke iilwimi.', 'XHO22'),
(8, 14, 12, 'Ngoko ke elowo kuthi uya kuziphendulela kuThixo.', 'XHO22'),
(8, 14, 13, 'Ngoko ke masingabi sagwebana, kodwa makungabikho bani ubeka isikhubekiso okanye umkhubekiso komnye nomnye.', 'XHO22'),
(8, 14, 14, 'Ndiyazi, ndeyisekile, ndiseNkosini uYesu nje, ukuba akukho nto iyinqambi ngokwayo; kodwa ke kuye obalela into ekuthini iyinqambi, kulowo iyinqambi.', 'XHO22'),
(8, 14, 15, 'Ukuba ke umzalwana wakho wenziwa buhlungu ngenxa yokusuka udle, akusahambi ngokothando. Musa ukumtshabalalisa ngokudla kwakho, yena lowo uKristu wamfelayo.', 'XHO22'),
(8, 14, 16, 'Ngoko okulungileyo kwenu makunganyeliswa.', 'XHO22'),
(8, 14, 17, 'Kaloku ubukumkani bukaThixo abukukudla nakusela; bubulungisa, noxolo, novuyo kuMoya oyiNgcwele.', 'XHO22'),
(8, 14, 18, 'Kuba omkhonzayo uKristu kwezo zinto, ukholekile kakuhle kuye uThixo;', 'XHO22'),
(8, 14, 19, 'Masiphuthume ke izinto ezisingisa eluxolweni, nezinto zokwakhana.', 'XHO22'),
(8, 14, 20, 'Musani ukuwuchitha umsebenzi kaThixo ngenxa yokutya. Zonke izinto okunene zinyulu; kodwa kubi kuloo mntu udlayo esikhubekisa.', 'XHO22'),
(8, 14, 21, 'Kulungile ukuba ungatyi nyama, ungaseli wayini, nokuba ungamkhubekisi umzalwana wakho, nokuba akhubeke, nokuba buthathaka.', 'XHO22'),
(8, 14, 22, 'Unokholo na? Yiba nalo kuwe phambi koThixo. Unoyolo lowo ungazigwebiyo kuloo nto ayicikidayo.', 'XHO22'),
(8, 14, 23, 'Ke lowo uthandabuzayo, ukuba uthe wadla, uselegwetyiwe, kuba engadli ngokwaselukholweni;', 'XHO22'),
(8, 15, 1, 'Thina ke bomeleleyo sifanele ukukuthwala ukuswela amandla kwabangomeleleyo, singazikholisi.', 'XHO22'),
(8, 15, 2, 'Masithi sonke ngabanye simkholise ummelwane kokulungileyo ukuze akheke.', 'XHO22'),
(8, 15, 3, 'Kuba naye uKristu akazikholisanga; wathi, njengokuba kubhaliwe, Iingcikivo zabakungcikivayo zawa phezu kwam.', 'XHO22'),
(8, 15, 4, 'Kuba zonke izinto ezabhalwayo ngenxa engaphambili, zaye zibhalelwa okwethu ukufundiswa, ukuze sithi ngalo unyamezelo novuselelo lwezibhalo sibe nalo ithemba.', 'XHO22'),
(8, 15, 5, 'Wanga ke uThixo wonyamezelo novuselelo anganinika ukucinga nto-nye phakathi kwenu, ngokukaKristu Yesu;', 'XHO22'),
(8, 15, 6, 'ukuze nithi, nimxhelo mnye, engamlomo mnye uThixo, uYise weNkosi yethu uYesu Kristu.', 'XHO22'),
(8, 15, 7, 'Kungoko ndithi, Yamkelanani, njengokuba naye uKristu wasamkelayo, ukuba kuzukiswe uThixo.', 'XHO22'),
(8, 15, 8, 'Ndithi ke, uYesu Kristu waba ngumlungiseleli wolwaluko ngenxa yenyaniso kaThixo, ukuze aqinise izithembiso zakhe koobawo;', 'XHO22'),
(8, 15, 9, 'zithi ke iintlanga zimzukise uThixo ngenxa yenceba yakhe; njengokuba kubhaliwe kwathiwa, Ngenxa yoko, ndiya kukudumisa phakathi kweentlanga, Ndilibethele uhadi igama lakho.', 'XHO22'),
(8, 15, 10, 'Kananjalo athi, Yibani nemihlali, zintlanga, ndawonye nabantu bakhe.', 'XHO22'),
(8, 15, 11, 'Kananjalo, Yidumiseni iNkosi, nonke nina zintlanga; nimdumise, nonke nina bantu.', 'XHO22'),
(8, 15, 12, 'Kananjalo uIsaya uthi, Liya kubakho iHlumelo likaYese; Uya kuvela oya kulawula iintlanga; ziya kuthembela kuye iintlanga.', 'XHO22'),
(8, 15, 13, 'Wanga ke uThixo wethemba angcwalise ngalo lonke uvuyo noxolo ekukholweni, ukuze niphuphume ithemba ngamandla oMoya oyiNgcwele.', 'XHO22'),
(8, 15, 14, 'Ke kaloku, bazalwana bam, ndeyisekile nam ngokwam, mayelana nani, kukuba nani ngokwenu nizele nazaliswa kukulunga, nizele kuko konke ukwazi, ninako nokululekana.', 'XHO22'),
(8, 15, 15, 'Ke kaloku, mawethu, ndinibhalele ngobubele obungasifanelanga, ndisithi, ndinikhumbuza, ngako ubabalo endababalwa ngalo nguThixo;', 'XHO22'),
(8, 15, 16, 'ukuze ndibe ngumlungiseleli kaYesu Kristu kuzo iintlanga, osebenza ubulungiseleli beendaba ezilungileyo zikaThixo, ukuze ukusondezwa kweentlanga kwamkeleke kakuhle, kungcwalisiwe ngoMoya oyiNgcwele.', 'XHO22'),
(8, 15, 17, 'Ngoko ke ndiyazingca, ndikuKristu Yesu, ngezinto ezilunge kuThixo.', 'XHO22'),
(8, 15, 18, 'Kuba mna andiyi kuba nabuganga bakuthetha nto ngazinto angazisebenzanga uKristu ngam, ukuze zimlulamele iintlanga, ngelizwi ke nangomsebenzi;', 'XHO22'),
(8, 15, 19, 'Ngamandla emiqondiso nezimanga, ngamandla oMoya kaThixo; endide ndathi, ndithabathela eYerusalem, ndajikelezisa, ndezisa kwelaseIliriko, ndazizalisa iindaba ezilungileyo zikaKristu.', 'XHO22'),
(8, 15, 20, 'Ndenjenjalo ke ukuzondelela ukushumayela iindaba ezilungileyo; kungekhona apho uKristu wakhankanywayo, ukuze ndingakhi phezu kwesiseko somnye;', 'XHO22'),
(8, 15, 21, 'kube njengokuba kubhaliwe kwathiwa, Abo bangabikelwanga nto ngaye, baya kubona; nabangevanga nto baya kuqonda.', 'XHO22'),
(8, 15, 22, 'Kungoko ndabanjezelwayo nokubanjezelwa futhi, ukuba ndingafiki kuni.', 'XHO22'),
(8, 15, 23, 'Ngoku ke ndingasenandawo nje kule mimandla, ndiseneminyaka emininzi ndilangazelela ukuza kuni,', 'XHO22'),
(8, 15, 24, 'xa ndihambela kwelaseSpani, ndoza kuni; kuba ndithemba ukunibona xa ndicandayo kuni, nokuphelekelelwa nini ukuya khona, ukuba ndithe kuqala ndakha ndadlelana nani ngokwaneleyo.', 'XHO22'),
(8, 15, 25, 'Ngoku ke ndisinga eYerusalem, ndisiya kulungiselela abangcwele.', 'XHO22'),
(8, 15, 26, 'Kuba kwakholeka kwabelaseMakedoni nabelaseAkaya, ukuba badlelane namahlwempu abo bangcwele baseYerusalem ngokuwupha okuthile.', 'XHO22'),
(8, 15, 27, 'Inene, kukholekile kubo; banetyala kubo. Kuba iintlanga, xa zadlelanayo nabo ngezinto zabo ezizezomoya, zikwafanele ukubabusela ngezizezenyama.', 'XHO22'),
(8, 15, 28, 'Ngoko ke, ndakuba ndikufinyezile oko, ndasinqabisela kubo eso siqhamo, ndobuya ngani ukuya kwelaseSpani.', 'XHO22'),
(8, 15, 29, 'Ndiyazi ke ukuba ndothi, ndakuza kuni, ndize ndinentsikelelo ezalisekileyo yeendaba ezilungileyo zikaKristu.', 'XHO22'),
(8, 15, 30, 'Ke kaloku, ndiyaniyala, bazalwana, ngayo iNkosi yethu uYesu Kristu, nangenxa yothando loMoya, ukuba nikhuthalele kunye nam ekundithandazeleni kuThixo;', 'XHO22'),
(8, 15, 31, 'ukuze ndihlangulwe kwabangakholwayo kwelakwaYuda; ukuze ulungiselelo lwam olusingisele kwiYerusalem lwamkeleke kubo abangcwele;', 'XHO22'),
(8, 15, 32, 'ukuze ndize kuni ndinovuyo, ngokuthanda kukaThixo, siphumzane.', 'XHO22'),
(8, 15, 33, 'uThixo ke woxolo makabe nani nonke. Amen.', 'XHO22'),
(8, 16, 1, 'Ke kaloku ndimyaleza kuni uFibhi, udade wethu, ongumlungiseleli webandla eliseKenkreya;', 'XHO22'),
(8, 16, 2, 'ukuze nimamkele ngokwaseNkosini, ngokufanele abangcwele, nimncede emcimbini asukuba enifunela wona; kuba yena ubengumongi wabaninzi, nowam lo.', 'XHO22'),
(8, 16, 3, 'Imibuliso kuPrisila noAkwila, abasebenzisi bam abakuKristu Yesu;', 'XHO22'),
(8, 16, 4, 'bona bathi, ngenxa yobomi bam, bazibeka emgoqweni ezabo iintamo, ekungabuleli mna ndedwa kubo, ekubulela nawo onke amabandla azo iintlanga.', 'XHO22'),
(8, 16, 5, 'Bulisani nakwibandla elisendlwini yabo. Bulisani kuPeneto intanda yam, oyintlahlela yelaseAkaya kuKristu.', 'XHO22'),
(8, 16, 6, 'Bulisani kuMariya, yena wabulalekayo kakhulu ngenxa yethu.', 'XHO22'),
(8, 16, 7, 'Imibuliso kuAndroniko noYuniya, imizalwane yam nabathinjwa ndawonye nam, bona badumileyo phakathi kwabapostile, kanjalo baba kuKristu ngaphambi kwam.', 'XHO22'),
(8, 16, 8, 'Imibuliso kuAmpliya, intanda yam ngokwaseNkosini.', 'XHO22'),
(8, 16, 9, 'Imibuliso ku-Urbhano, umncedisi wethu kuKristu, noStaki intanda yam.', 'XHO22'),
(8, 16, 10, 'Bulisani kuApele ocikidekileyo kuKristu. Bulisani kwabendlu ka-Aristobhulo.', 'XHO22'),
(8, 16, 11, 'Bulisani kuHerodiyon, umzalwane wam. Bulisani kwabakuloNarkiso, abaseNkosini.', 'XHO22'),
(8, 16, 12, 'Imibuliso kuTrifena noTrifosa, abo babulalekayo eNkosini. Bulisani kwintanda yakhe kuPersi, lowo wabulalekayo kakhulu eNkosini.', 'XHO22'),
(8, 16, 13, 'Imibuliso kuRufo onyuliweyo waseNkosini, nonina, uma.', 'XHO22'),
(8, 16, 14, 'Imibuliso kuAsinkrito, kuFilego, kuHerma, kuPatrobha, kuHerma, nabazalwana abanabo.', 'XHO22'),
(8, 16, 15, 'Imibuliso kuFilologo noYuliya, uNere nodade wabo, no-Olimpa, nabo bonke abangcwele bakunye nabo.', 'XHO22'),
(8, 16, 16, 'Bulisanani ngolwango olungcwele. Ayanibulisa amabandla kaKrestu.', 'XHO22'),
(8, 16, 17, 'Ke kaloku ndiyaniyala, bazalwana, balumkeleni abo benza iimbambaniso nezikhubekiso, ezinxamnye nemfundiso enayifundayo nina, kwaye uziphephe.', 'XHO22'),
(8, 16, 18, 'Kuba abanjalo abakhonzi yona iNkosi yethu uYesu Kristu, bakhonza esabo isisu; baze, ngamazwi anencasa nalalanisayo, balukuhle iintliziyo zabangazani nabubi.', 'XHO22'),
(8, 16, 19, 'Kuba ukululama kwenu kuye kwafika kubo bonke. Ndiyavuya ngoko ngani; ndinga ke ningaba ngabalumkileyo okunene kokulungileyo; kodwa nibe ngabangenabuqhinga kokubi.', 'XHO22'),
(8, 16, 20, 'uThixo ke woxolo uya kumtyumza uSathana kamsinya phantsi kweenyawo zenu. Ubabalo lweNkosi yethu uYesu Kristu malube nani. Amen.', 'XHO22'),
(8, 16, 21, 'Uyanibulisa uTimoti, osebenza nam, noLukiyo, noYason, noSosipatro, imizalwane yam.', 'XHO22'),
(8, 16, 22, 'Ndiyanibulisa ngokwaseNkosini, mna Tertiyo, uyibhalileyo le ncwadi.', 'XHO22'),
(8, 16, 23, 'Uyanibulisa uGayo, esilundwendwe lwakhe, mna nebandla liphela. Uyanibulisa uErasto, igosa lomzi, noKwarto umzalwana.', 'XHO22'),
(8, 16, 24, 'Ubabalo lweNkosi yethu uYesu Kristu malube nani nonke. Amen.', 'XHO22'),
(8, 16, 25, 'Ke kaloku, kuye onako ukunizimasa ngokweendaba zam ezilungileyo, novakaliso lukaYesu Kristu, ngokokutyhilwa kwemfihlelo ebifihliwe kwasephakadeni;', 'XHO22'),
(8, 16, 26, 'Ngoku ke ibonakalisiwe, nangezibhalo zabaprofeti, ngokomthetho kaThixo ongunaphakade, yabonakalaliswa kuzo zonke iintlanga, ukuze lumlulamele ukholo;', 'XHO22'),
(8, 16, 27, 'kuThixo olumke yedwa, ngaye uYesu Kristu, malubekho uzuko kude kube ngunaphakade. Amen. (Yabhalelwa amaRoma eseKorinte, yaza yathunyelwa nguFebhi umkhonzi webandla laseKenkreya.)', 'XHO22'),
(47, 1, 1, 'uPawulos, obizwe ngokuthanda kukaThixo, ukuba abe ngumpostile kaYesu Kristu, noSostene umzalwana,', 'XHO22'),
(47, 1, 2, 'Ubhalela ibandla likaThixo eliseKorinte, abangcwalisiweyo kuKristu Yesu, abangcwele ababiziweyo, kunye nabo bonke abalinqulayo ezindaweni zonke igama leNkosi yethu uYesu Kristu, eyabo neyethu;', 'XHO22'),
(47, 1, 3, 'bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(47, 1, 4, 'Ndihlala ndibulela kuye uThixo wam ngani, ngenxa yobabalo lukaThixo, awanibabala ngalo nikuKristu Yesu;', 'XHO22'),
(47, 1, 5, 'kuba natyetyiswa kuye ngezinto zonke, ngokuthetha konke, nangokwazi konke;', 'XHO22'),
(47, 1, 6, 'njengokuba ubungqina bukaKristu baqiniswayo kuni;', 'XHO22'),
(47, 1, 7, 'Ngoko ke anisileli nakwesinye isipho enisibabalweyo; nilinde ukufika kweNkosi yethu uYesu Kristu;', 'XHO22'),
(47, 1, 8, 'oya kuniqinisa kude kube sekupheleni, ukuze ningabi nakusoleka ngemini yeNkosi yethu uYesu Kristu.', 'XHO22'),
(47, 1, 9, 'Uthembekile uThixo, enabizelwa nguye ebudlelaneni boNyana wakhe, uYesu Kristu iNkosi yethu.', 'XHO22'),
(47, 1, 10, 'Ke kaloku, ndiyaniyala, bazalwana, ngalo igama leNkosi yethu, uYesu Kristu, ukuba nithethe nto-nye nonke, nokuba kungabikho zahlukahlukano kuni; ke nibe ngabalungisiweyo, nigqibelele ngqiqweni-nye, nasicamangweni sinye.', 'XHO22'),
(47, 1, 11, 'Kuba ndazisiwe ngani, bazalwana bam, ngabakuloKlowe, ukuba kukho iimpikiswano phakathi kwenu.', 'XHO22'),
(47, 1, 12, 'Ukutsho ke ndithi kuthiwa, ulowo kuni uthi, Mna ndingokaPawulos; omnye ke athi, Mna ndingoka-Apolo; omnye ke athi, Mna ndingokaKefas; nam ke ndingokaKristu.', 'XHO22'),
(47, 1, 13, 'Wahluliwe na uKristu? Kwabethelelwa uPawulos yini na emnqamlezweni ngenxa yenu? Nabhaptizelwa egameni likaPawulos na?', 'XHO22'),
(47, 1, 14, 'Ndiyambulela uThixo, kuba ndingabhaptizanga namnye kuni, ingenguKrispo noGayo;', 'XHO22'),
(47, 1, 15, 'hleze kubekho othi, ndabhaptiza egameni lam.', 'XHO22'),
(47, 1, 16, 'Ndabhaptiza ke nendlu kaStefana; ngaphandle kwabo, andazi ukuba ndabhaptiza wumbi.', 'XHO22'),
(47, 1, 17, 'Kaloku uKristu akandithumanga kubhaptiza; wandithuma ukushumayela iindaba ezilungileyo, kungabi ngabulumko bakuthetha, ukuze umnqamlezo kaKristu ungalanjathiswa.', 'XHO22'),
(47, 1, 18, 'Kaloku ilizwi lomnqamlezo libubudenge okunene kwabatshabalalayo; ke kuthi, thina basindiswayo, lingamandla kaThixo.', 'XHO22'),
(47, 1, 19, 'Kuba kubhaliwe kwathiwa, Ndiya kubutshabalalisa ubulumko bezilumko, Ingqondo yeengqondi ndiyitshitshise.', 'XHO22'),
(47, 1, 20, 'Siphi na ke isilumko? uphi na umbhali? Uphi na umcalula-ndaba weli hlabathi? uThixo akabenzanga baba bubudenge na ubulumko beli hlabathi?', 'XHO22'),
(47, 1, 21, 'Kuthe ke, ebulumkweni bukaThixo, ihlabathi alamazi uThixo ngobulumko balo, kwakholeka kuThixo ukusindisa abo bakholwayo, ngabo ubudenge bokuvakalisa iindaba ezilungileyo.', 'XHO22'),
(47, 1, 22, 'Kuba amaYuda efuna umqondiso, namaGrike efuna ubulumko;', 'XHO22'),
(47, 1, 23, 'Ke thina sishumayela uKrestu ebethelelwe emnqamlezweni, isikhubekiso kumaJuda nebubudenge kumaGrike.', 'XHO22'),
(47, 1, 24, 'Ke kwabo babiziweyo, amaYuda kwanamaGrike, sivakalisa uKristu ongamandla kaThixo, nobulumko bukaThixo.', 'XHO22'),
(47, 1, 25, 'Kaloku into kaThixo ebubudenge inobulumko kunabantu; nento kaThixo engenamandla yomelele kunabantu.', 'XHO22'),
(47, 1, 26, 'Kuba luboneni ubizo lwenu, bazalwana, ukuba azizininzi izilumko ngokwenyama, ababaninzi abanamandla, akamaninzi amanene.', 'XHO22'),
(47, 1, 27, 'Kodwa uThixo usuke wanyula izinto ezibubudenge zehlabathi, ukuze ngokwenjenjalo adanise izilumko; Kananjalo uThixo usuke wanyula izinto ezibonakala ngathi azinamandla ehlabathini, ukuze azidanise izinto ezinamandla;', 'XHO22'),
(47, 1, 28, 'nezinto zehlabathi ezingenabuntu, nezingento yanto, wazinyula uThixo, nezingathi azikho, ukuba aziphuthise izinto ezikhoyo;', 'XHO22'),
(47, 1, 29, 'ukuze kungaqhayisi namnye umntu emehlweni akhe.', 'XHO22'),
(47, 1, 30, 'Ke nina nikuKristu Yesu, owathi ngenxa yethu waba bubulumko obuvela kuThixo, nobulungisa, nobungcwalisa, nentlawulelo;', 'XHO22'),
(47, 1, 31, 'Ukuze, njengoko kubhaliweyo, Oqhayisayo aqhayise ngeNkosi.', 'XHO22'),
(47, 2, 1, 'Mna ke, ndakuba ndizile kuni, bazalwana, andizanga ngabugcisa bakuthetha nabulumko, ndinazisa ubungqina bukaThixo.', 'XHO22'),
(47, 2, 2, 'Kuba ndagqiba kwelokuba ndingazi nto phakathi kwenu, ingenguYesu Kristu, naye ke ebethelelwe emnqamlezweni.', 'XHO22'),
(47, 2, 3, 'Mna kuni ndafika ndaba ngothambileyo, onokoyika, onokugubha okukhulu.', 'XHO22'),
(47, 2, 4, 'Nokuthetha kwam, nokuvakalisa kwam uKristu kube kungengamazwi oyisa ngabulumko babantu;', 'XHO22'),
(47, 2, 5, 'ukuze ukholo lwenu lungabi sebulumkweni babantu, lube semandleni kaThixo.', 'XHO22'),
(47, 2, 6, 'Ke sithetha ubulumko phakathi kwabagqibeleleyo, ubulumko ke obungebubo obeli phakade, nobabaphathi beli phakade ababhangayo.', 'XHO22'),
(47, 2, 7, 'Sithetha ubulumko bukaThixo obusemfihlekweni, obo ke bufihlakeleyo, awathi uThixo kwangaphambi kwawo amaphakade, wabumisela uzuko lwethu;', 'XHO22'),
(47, 2, 8, 'ekungabangakho namnye wabaphathi beli phakade ubaziyo; kuba, ukuba babebazi, ngebengayibethelelanga emnqamlezweni iNkosi yozuko.', 'XHO22'),
(47, 2, 9, 'Kodwa njengokuba kubhaliwe kwathiwa, Izinto ezingabonwanga liso, nezingaviwanga ndlebe, Nezingathanga qatha entliziyweni yomntu, Zizo uThixo awazilungisela abo bamthandayo.', 'XHO22'),
(47, 2, 10, 'uThixo wazityhila kuthi ke ngoMoya wakhe; kuba uMoya uphengulula zonke izinto, neenzulu zikaThixo.', 'XHO22'),
(47, 2, 11, 'Kuba nguwuphi na kubantu ozaziyo izinto zomntu, ingenguwo umoya womntu okuye? Ngokunjalo nezinto zikaThixo akukho namnye uzaziyo, ingenguye uMoya kaThixo.', 'XHO22'),
(47, 2, 12, 'Ke thina asamkelanga moya wehlabathi; samkele uMoya ophuma kuThixo; ukuze sizazi izinto esazibabalwa nguThixo.', 'XHO22'),
(47, 2, 13, 'Esizithethayo nokuzithetha, kungengamazwi anokufundiswa bubulumko babantu, kungawo anokufundiswa nguMoya oyiNgcwele; sithelekisa izinto zoMoya nezoMoya.', 'XHO22'),
(47, 2, 14, 'Owemvelo ke umntu akazamkeli izinto zoMoya kaThixo; kuba zibubudenge kuye; kanjalo akanakuzazi, ngokuba ziphicothwa ngokoMoya.', 'XHO22'),
(47, 2, 15, 'Ke ongowoMoya okunene uphicotha zonke izinto, kodwa yena ngokwakhe akaphicothwa mntu.', 'XHO22'),
(47, 2, 16, 'Kuba ngubani na oyaziyo ingqiqo yeNkosi, oya kuthi ayifundise? Ke thina sinayo ingqiqo kaKristu.', 'XHO22'),
(47, 3, 1, 'Nam ke, bazalwana, bendingenakuthetha kuni njengakwaboMoya; bendinokuthetha njengakwabenyama, njengakwabaziintsana kuKristu.', 'XHO22'),
(47, 3, 2, 'Ndaniseza amasi, andaninika kudla kuqinileyo, kuba nibe ningekabi nakomelela, unangoku anikabi nako.', 'XHO22'),
(47, 3, 3, 'Kuba nisengabenyama; oku nje kukhoyo phakathi kwenu umona, neenkani, neenkani, neembambaniso, aningabenyama na? Anihambi ngokwabantu na?', 'XHO22'),
(47, 3, 4, 'Kuba xa uthile athi, Mna ndingokaPawulos; omnye ke athi, Mna ndingoka-Apolo; aningabenyama na?', 'XHO22'),
(47, 3, 5, 'Engubani na yena uPawulos? engubani na yena uApolo? Ingabalungiseleli nje kuphela, enakholwa ngabo, njengokuba iNkosi yamnikayo ulowo nalowo?', 'XHO22'),
(47, 3, 6, 'Mna ndatyala, uApolo wankcenkcesha; ke nguThixo owahlumisayo.', 'XHO22'),
(47, 3, 7, 'Ngoko ke lowo utyalayo akalutho, nalowo unkcenkceshayo akalutho; ke nguThixo okhulisayo.', 'XHO22'),
(47, 3, 8, 'Lowo utyalayo ke, nalowo unkcenkceshayo, banto-nye; kodwa elowo uya kwamkela owakhe umvuzo ngokukokwakhe ukubulaleka.', 'XHO22'),
(47, 3, 9, 'Kuba singabasebenzi kunye noThixo; intsimi kaThixo nina, nisisakhiwo sikaThixo.', 'XHO22'),
(47, 3, 10, 'Ngokobabalo lukaThixo endababalwa ngalo, ndithi, ngokomakhi osisilumko, ndibeke ilitye lesiseko, athi ke omnye akhe phezu kwalo; Ke elowo makakhangele ukuba uthini na ukwakha kwakhe phezu kwalo.', 'XHO22'),
(47, 3, 11, 'Kuba akukho namnye unako ukubeka litye limbi lesiseko, nxamnye nelo libekwe kade, linguYesu onguKristu.', 'XHO22'),
(47, 3, 12, 'Ukuba ke umntu wakha phezu kwesi siseko igolide, isilivere, amatye anqabileyo, imithi, ingca, iindiza;', 'XHO22'),
(47, 3, 13, 'Umsebenzi walowo nalowo wobonakalaliswa; kuba loo mini iya kuwazisa, ngokuba iya kuvela inomlilo; nomsebenzi walowo nalowo, umlilo uya kuwucikida ubunjani bawo;', 'XHO22'),
(47, 3, 14, 'Ukuba umsebenzi awawakhayo umntu phezu kwalo, uthe wahlala uhleli, wowamkela umvuzo.', 'XHO22'),
(47, 3, 15, 'Ukuba umsebenzi womntu uthe watshiswa, wolahlekelwa ngumvuzo wakhe; ke yena okwakhe wosindiswa; kanti kungomlilo.', 'XHO22'),
(47, 3, 16, 'Anazi na ukuba niyitempile kaThixo? Anazi na ukuba uMoya kaThixo umi ngaphakathi kwenu?', 'XHO22'),
(47, 3, 17, 'Ukuba umntu uyayonakalisa itempile kaThixo, uThixo womonakalisa lowo; kuba itempile kaThixo ingcwele, niyitempile ke nina.', 'XHO22'),
(47, 3, 18, 'Makungabikho namnye uzikhohlisayo. Ukuba ubani kuni uba usisilumko kweli hlabathi, makabe sisidenge, ukuze abe sisilumko.', 'XHO22'),
(47, 3, 19, 'Kaloku ubulumko beli hlabathi bubudenge kuye uThixo; kuba kubhaliwe kwathiwa, Yena uyazibambisa izilumko kubuqhetseba bazo;', 'XHO22'),
(47, 3, 20, 'kwathiwa kananjalo, iNkosi iyazazi izicamango zezilumko, ukuba azinto.', 'XHO22'),
(47, 3, 21, 'Ngoko ke makungabikho namnye uqhayisa ngabantu; Ngokuba zonke izinto zezenu;', 'XHO22'),
(47, 3, 22, 'nokuba nguPawulos, nokuba nguApolo, nokuba nguKefas, nokuba lihlabathi, nokuba bubomi, nokuba kukufa, nokuba zizinto ezingeneyo, nokuba zezizayo; zonke zezenu;', 'XHO22'),
(47, 3, 23, 'Nina ke ningabakaKristu; noKristu ngokaThixo.', 'XHO22'),
(47, 4, 1, 'Makenjenjalo ke umntu ukucinga ngathi, athi singabakhonzi bakaKristu, athi singamagosa eemfihlelo zikaThixo.', 'XHO22'),
(47, 4, 2, 'Ke kaloku ke, kufuneka kuwo amagosa, ukuba ulowo kuwo afumaneke ethembekile.', 'XHO22'),
(47, 4, 3, 'Kum ke yeyona nto incinanana, ukuba ndincinwe nini, nayimini yamatyala yabantu; kodwa andizigwebi okwam,', 'XHO22'),
(47, 4, 4, 'kuba andizazeleli, nto; noko andigwetyelwe, koko; ondincinayo ke yiNkosi.', 'XHO22'),
(47, 4, 5, 'Ngoko ke, musani ukugweba nto phambi kwelona xesha, ide ifike iNkosi, eya kuthi izikhanyise nokuzikhanyisa izinto ezifihlakeleyo zobumnyama, iwabonakalalise amacebo entliziyo; andule ke ulowo nalowo abe nodumo oluvela kuThixo.', 'XHO22'),
(47, 4, 6, 'Ezi zinto ke, bazalwana, ndizilinganisele ngam noApolo, ngenxa yenu; ukuze nifunde kuthi, ukuba masingacingeli mntu ngaphezu koko kubhaliweyo, ukuze kungabikho namnye uzikhukhumalisa ngakomnye ngenxa yomnye.', 'XHO22'),
(47, 4, 7, 'Kuba ngubani na okubalulayo kwabanye? Yintoni na ke onayo ongayamkeliswanga? Phofu ke, ukuba wamkelisiwe, uqhayiselani na, ngathi akwamkeliswanga?', 'XHO22'),
(47, 4, 8, 'Ngoku senihluthi, senizizityebi, nalawula ningenathi. Ndiyathanda ke ukuba kambe beningabalawuli, ukuze nathi silawule kunye nani.', 'XHO22'),
(47, 4, 9, 'Kuba ngathi mna uThixo thina bapostile usivelise mva, sanga singabamiselwe ukufa; kuba sanekwe senziwa intlekisa kulo ihlabathi, nakwizithunywa zezulu, nakubantu.', 'XHO22'),
(47, 4, 10, 'Thina siziziyatha ngenxa kaKristu, ke nina niziingqondi kuKristu; Thina sibuthathaka, ke nina nomelele; Nizukile, ke thina sideliwe.', 'XHO22'),
(47, 4, 11, 'Kude kube ngoku silambile, sinxaniwe, sihamba ze, siyantlithwa, asinakhaya;', 'XHO22'),
(47, 4, 12, 'Sibulaleka sisebenza ngezandla; xa sinyeliswayo, siyasikelela; sitshutshiswa, siyanyamezela;', 'XHO22'),
(47, 4, 13, 'Sinyeliswa, siyakhala; senziwe inkunkuma yehlabathi, inkuthu yezinto zonke, unanamhla.', 'XHO22'),
(47, 4, 14, 'Ezi zinto andizibhaleli ukuba nidane; ndiyaniluleka njengabantwana bam abathandekayo.', 'XHO22'),
(47, 4, 15, 'Kuba ningafanelana ninabakhaphi bemawaka alishumi kuKristu, ababaninzi bona ooyihlo; kuba kuKristu Yesu nizelwe ndim lo, ngazo iindaba ezilungileyo.', 'XHO22'),
(47, 4, 16, 'Ndiyaniyala ngoko, yibani ngabaxelisa mna.', 'XHO22'),
(47, 4, 17, 'Ngenxa yoko ndinithumele uTimoti, ongumntwana wam oyintanda, othembekileyo, waseNkosini, oya kunikhumbuza iindlela zam ezikuKristu, njengoko ndifundisayo ezindaweni zonke, kuwo onke amabandla.', 'XHO22'),
(47, 4, 18, 'Ke kaloku inxenye ithe yakhukhumala, ngathi andiyi kuza kuni.', 'XHO22'),
(47, 4, 19, 'Ndiya kuza ke kuni kamsinya, ukuba iNkosi ithe yavuma, ukuze ndinganeli kukwazi ukuthetha kwabakhukhumeleyo, ndazi amandla abo.', 'XHO22'),
(47, 4, 20, 'Kaloku ubukumkani bukaThixo abusekuthetheni, busemandleni.', 'XHO22'),
(47, 4, 21, 'Nifuna ntoni na? Ndoza kuni ndinoswazi, kuko ukuza ndinothando, nomoya wobulali?', 'XHO22'),
(47, 5, 1, 'Kwandile kwavakala ukuba kukho umbulo phakathi kwenu; ngumbulo ke lowo ongazanga ukhankanywe naphakathi kweentlanga, owokuba uthile abe nomfazi kayise.', 'XHO22'),
(47, 5, 2, 'Ke nina nithe nasuka nazidla, nesuka nenze isijwili, ukuze asuswe kuni lowo uyenzileyo le nto.', 'XHO22'),
(47, 5, 3, 'Kuba okunene mna, ndingekho nje okunene ngesiqu, ndikho ngomoya;', 'XHO22'),
(47, 5, 4, 'Egameni leNkosi yethu uYesu Kristu, xa nithe nahlangana ndawonye nomoya wam, ngokwamandla eNkosi yethu uYesu Kristu,', 'XHO22'),
(47, 5, 5, 'ukunikelwa onjalo kuSathana, ukuba inyama itshatyalaliswe, ukuze umoya usindiswe ngemini yeNkosi uYesu.', 'XHO22'),
(47, 5, 6, 'Iqhayiya lenu alilungile. Anazi na, ukuba igwele elincinane liyayibilisa intlama iphela?', 'XHO22'),
(47, 5, 7, 'Likhupheni ngoko kuthi tu igwele elidala, ukuze nibe yintlama entsha, ekubeni ningabangenagwele. Kuba ipasika yethu ubingelelwe thina nguKristu;', 'XHO22'),
(47, 5, 8, 'Ngoko ke masiyenze umthendeleko, kungengagwele elidala, negwele lobubi, nelokungendawo; kodwa ngezonka ezingenagwele zokunyaniseka nenyaniso.', 'XHO22'),
(47, 5, 9, 'Ndanibhalela encwadini ukuba maningazidibanisi nabahenyuzi;', 'XHO22'),
(47, 5, 10, 'Kodwa ke kungabi ngoku kwanehlabathi elihenyuzayo, nokubawa, nabaphangi, nabakhonza izithixo; kuba oko nimelwe kukuthi nimke ehlabathini.', 'XHO22'),
(47, 5, 11, 'Kungoku ke ndinibhalela ukuba maningazidibanisi nosukuba enegama lokuba ngumzalwana, ukanti wenza umbulo, nokuba ulibawa, nokuba ungumkhonzi wezithixo, nokuba ungumtshabhisi, nokuba ulinxila, nokuba ungumphangi; Maningatyi nonjalo.', 'XHO22'),
(47, 5, 12, 'Ndinamsebenzi mni na ukuba ndigwebe nabangaphandle? Anigwebi abangaphakathi na?', 'XHO22'),
(47, 5, 13, 'Ke abangaphandle bagwetywa nguThixo. Ize nimnyothule kuni nina lowo ungendawo.', 'XHO22'),
(47, 6, 1, 'Nabani na kuni othi, xa athe wamangalela omnye, makaye kumangala phambi kwabangemalungisa, kungabi phambi kwabangcwele?', 'XHO22'),
(47, 6, 2, 'Anazi na, ukuba abangcwele aba baya kuligweba ihlabathi? Ukuba ke ihlabathi ligwetywa phambi kwenu, anikufanele na ukuwathetha awona mancinanana amatyala?', 'XHO22'),
(47, 6, 3, 'Anazi na, ukuba siya kugweba izithunywa zezulu? Kobeka phi na ke kwizinto zobu bomi?', 'XHO22'),
(47, 6, 4, 'Xa sukuba ke ngoko nisiya ematyaleni ngezinto zobu bomi, nimisa abo bangento kulo ibandla, ukuba kugwebe bona na?', 'XHO22'),
(47, 6, 5, 'Nditsho ukuba nidane. Akukho silumko na phakathi kwenu? Akukho namnye unako ukugweba phakathi kwabazalwana bakhe?', 'XHO22'),
(47, 6, 6, 'Kodwa umzalwana umangalela umzalwana, naphambi kwabangakholwayo.', 'XHO22'),
(47, 6, 7, 'Kaloku ngoku ke ninesono na phakathi kwenu, ukuba niyamangalelana. Yini na ukuba ningathi kanye nimeneze? Yini na ukuba ningavumi kanye?', 'XHO22'),
(47, 6, 8, 'Nisuka nina none abanye, nilumkise, nako oko nibe nikwenza kubazalwana.', 'XHO22'),
(47, 6, 9, 'Anazi na, ukuba abangemalungisa abayi kubudla ilifa ubukumkani bukaThixo? Musani ukulahlekiswa; abenza umbulo, nabakhonzi bezithixo, nabakrexezi, nabafeba, nabakrexezi', 'XHO22'),
(47, 6, 10, 'Kwanamasela, namabawa, namanxila, nabatshabhisi, nabaphangi, abayi kubudla ilifa ubukumkani bukaThixo.', 'XHO22'),
(47, 6, 11, 'Naye ninjalo ke inxenye yenu; kuloko nahlanjululwayo, kuloko nangcwaliswayo, kuloko nagwetyelwayo egameni leNkosi uYesu, nangoMoya kaThixo wethu.', 'XHO22'),
(47, 6, 12, 'Zonke izinto zivumelekile kum, noko azindilungele zonke. Zonke izinto zivumelekile kum, noko asindim oya kubekwa phantsi kwagunya lelayiphi;', 'XHO22'),
(47, 6, 13, 'izidlo zezesisu, nesisu sesezidlo; kodwa uThixo uya kubhangisa kokubini, sona nazo. Ke wona umzimba asingowombulo, ngoweNkosi; nayo iNkosi yeyomzimba.', 'XHO22'),
(47, 6, 14, 'uThixo ke wayivusayo iNkosi, uya kusivusa nathi ngamandla akhe.', 'XHO22'),
(47, 6, 15, 'Anazi na ukuba imizimba yenu ngamalungu kaKristu? Ndiya kuthi na ke, ndiwathabathe amalungu kaKristu lowo, ndiwenze amalungu ehenyukazi? Makube lee oko.', 'XHO22'),
(47, 6, 16, 'Yintoni na? Anazi na, ukuba onamathele kulo ihenyukazi, umzimba mnye nalo? Kuba babini, utsho, baya kuba nyama-nye.', 'XHO22'),
(47, 6, 17, 'Ke onamathele eNkosini, umoya mnye nayo.', 'XHO22'),
(47, 6, 18, 'Wubalekeni umbulo. Zonke izono athe umntu wazenza, zingaphandle komzimba; ke yena owenza umbulo, wona owakhe umzimba.', 'XHO22'),
(47, 6, 19, 'Yintoni na? Anazi na, ukuba umzimba wenu uyitempile yoMoya oyiNgcwele ongaphakathi kwenu, eninaye evela kuThixo; nokuba ke aningabenu?', 'XHO22'),
(47, 6, 20, 'Kuba nathengwa ngexabiso. Mzukiseni ke uThixo ngomzimba wenu nangomoya wenu, izinto ezizezikaThixo ezo.', 'XHO22'),
(47, 7, 1, 'Ke kaloku, ngokubhekisele kwiindawo enandibhalela ngazo, ndithi, Kumlungele umntu ukuba angamchukumisi umntu oyinkazana.', 'XHO22'),
(47, 7, 2, 'Kodwa ke, ngenxa yohenyuzo, elowo makabe nowakhe umfazi, athi elowo abe neyakhe indoda.', 'XHO22'),
(47, 7, 3, 'Indoda mayimenzele okumfaneleyo umfazi wayo; enjenjalo ke nomfazi endodeni.', 'XHO22'),
(47, 7, 4, 'Umfazi akanagunya kowakhe umzimba, yindoda enalo; kwangokunjalo ke, nendoda ayinagunya kowayo umzimba, ngumfazi onalo.', 'XHO22'),
(47, 7, 5, 'Musani ukwazana, ngaphandle kokuba nivumelene okomzuzwana, ukuze nizinikele nina ziqu nizila ukutya nokuthandaza; nibuye nihlangane, ukuze anganihendi uSathana ngenxa yokungazithibi kwenu.', 'XHO22'),
(47, 7, 6, 'Nditsho ngegunya, andiwisele umthetho.', 'XHO22'),
(47, 7, 7, 'Kuba ndinga bonke abantu bangaba njengam nje; Kodwa elowo unesakhe isipho, asibabalwe nguThixo;', 'XHO22'),
(47, 7, 8, 'Ndithi ke kwabangazekanga nabangendanga, nakubahlolokazi, kubalungele ukuba bathe bahlala njengam nje.', 'XHO22'),
(47, 7, 9, 'Kodwa ke, ukuba abanakuzibamba, mabazeke, mabende, kuba kukhona kulungileyo ukuba kuzekwe, kwendiwe, kunokutshiseka.', 'XHO22'),
(47, 7, 10, 'Ke kaloku abazekileyo nabendileyo ndiyabathethela, phofu ingendim, iyiNkosi;', 'XHO22'),
(47, 7, 11, 'Kodwa ke ukuba uthe wahlukana nayo, makahlale angendi, okanye makaxolelaniswe nendoda yakhe; indoda mayingamali umfazi wayo.', 'XHO22'),
(47, 7, 12, 'Ke kaloku kwabanye ndithi mna, akutsho iNkosi, Ukuba kukho mzalwana uthile unomfazi ongakholiweyo, aze umfazi lowo athande ukuhlala naye, makangamali.', 'XHO22'),
(47, 7, 13, 'Nomfazi onendoda engelokholwa, ize yona ithande ukuhlala naye, makangahlukani nayo.', 'XHO22'),
(47, 7, 14, 'Kuba indoda leyo ingakholiweyo ingcwalisiwe nguye umfazi, nomfazi lowo ungakholiweyo ungcwalisiwe yiyo indoda; okanye abantwana benu ngebeba baziinqambi; kungokunje bangcwele.', 'XHO22'),
(47, 7, 15, 'Kodwa ke ukuba lowo ungakholiweyo uyahluka, makahluke; umzalwana nokuba ngudade akabotshelelwe, xa kunjalo; ke uThixo usibizele eluxolweni.', 'XHO22'),
(47, 7, 16, 'Kuba, wazi ngani na, mfazi, ukuba woyisindisa indoda yakho? Wazi ngani na, ndoda, ukuba womsindisa umkakho?', 'XHO22'),
(47, 7, 17, 'Ke, njengoko uThixo wamabelayo ulowo, njengoko iNkosi imbizileyo ulowo, makahambe ngokunjalo. Ndimisa ngokunjalo ke kuwo onke amabandla.', 'XHO22'),
(47, 7, 18, 'Kukho mntu wabizwa engowalukileyo na? Makangazenzi ongalukileyo. Kukho mntu wabizwa engalukanga na? makangaluki.', 'XHO22'),
(47, 7, 19, 'Ukwaluka oku akunto, nokungaluki oku akunto; okuyinto kukubamba imithetho kaThixo.', 'XHO22'),
(47, 7, 20, 'Elowo makahlale elubizweni abizwe ekulo.', 'XHO22'),
(47, 7, 21, 'Wabizwa ulikhoboka na? ungakukhathaleli oko; nokuba unokukhululeka, sebenzisa kanye oko.', 'XHO22'),
(47, 7, 22, 'Ukuba lowo wabizwayo waba seNkosini elikhoboka, ungumkhululwa weNkosi; ngokunjalo nalowo wabizwayo engokhululekileyo, ulikhoboka likaKristu.', 'XHO22'),
(47, 7, 23, 'Nathengwa ngexabiso; musani ukuba ngabakhonzi babantu.', 'XHO22'),
(47, 7, 24, 'Bazalwana, elowo ekubizweni awabizwa ekuko, makahlale kuko ekuThixo.', 'XHO22'),
(47, 7, 25, 'Ke kaloku, ngazo iintombi andinammiselo weNkosi; ndibona oku mna, njengowenzelwe inceba yiNkosi ukuba athembeke.', 'XHO22'),
(47, 7, 26, 'Ndiba ngoko, kulungile oku ngenxa yengxakeko engeneyo, kulungile emntwini ukuthi aselehlala njengoko anjalo.', 'XHO22'),
(47, 7, 27, 'Ubotshelelwe na emfazini? musa ukufuna ukukhululwa. Ukhululekile na emfazini? musa ukufuna umfazi.', 'XHO22'),
(47, 7, 28, 'Kodwa ke ukuba uthe wazeka, akonanga; nentombi, ukuba ithe yatshata, ayonanga. Kodwa ke abanjalo baya kuba neengxaki enyameni;', 'XHO22'),
(47, 7, 29, 'Nditsho ke, bazalwana, ukuthi, ixesha lifinyele, ukuze kwelisaseleyo bathi nabo banabafazi, babe njengabangenabo;', 'XHO22'),
(47, 7, 30, 'Bathi ke abalilayo, babe njengabangalili; nabavuyayo, mababe njengabangavuyanga; nabathengayo, babe njengabangenanto;', 'XHO22'),
(47, 7, 31, 'bathi abasebenzayo ngeli hlabathi, babe njengabangalixhamliyo; kuba ukumila kweli hlabathi kuyadlula.', 'XHO22'),
(47, 7, 32, 'Ndithanda ukuba ningabi naxhala. Ongazekileyo uxhalela izinto zeNkosi, ukuba angathini na ukuyikholisa iNkosi;', 'XHO22'),
(47, 7, 33, 'Ozekileyo ke uxhalela izinto zehlabathi, ukuba angathini na ukumkholisa umkakhe.', 'XHO22'),
(47, 7, 34, 'Kukho umahluko phakathi komfazi nentombi enyulu. Ongendileyo uxhalela izinto zeNkosi, ukuze abe ngcwele ngomzimba nangomoya; owendileyo ke uxhalela izinto zehlabathi, ukuba angathini na ukuyikholisa indoda yakhe.', 'XHO22'),
(47, 7, 35, 'Oku ke ndikuthetha ndisingisele kokunilungeleyo; ndingenzeli ukunirhintyela, koko ndiniphose ngokufanelekileyo, nikhonze iNkosi, ningenasiphako.', 'XHO22'),
(47, 7, 36, 'Kodwa ke ukuba umntu uba uhlaza intombi yakhe, xa ithe yegqitha entlahleni yayo, emelwe kukwenjenjalo, makenze akuthandayo; akoni; makuzekwe.', 'XHO22'),
(47, 7, 37, 'Noko ke omiyo eqinile entliziyweni, engaxakwe nto, enegunya ke lokwenza ngokuthanda kwakhe, kananjalo egqibile entliziyweni yakhe kwelokuba ayigcine intombi yakhe, uyatyapha ukwenjenjalo.', 'XHO22'),
(47, 7, 38, 'Ngoko ke lowo wendisayo uyatyapha; kodwa ke lowo ungendisiyo nguyena ukholisayo.', 'XHO22'),
(47, 7, 39, 'Umfazi ubotshiwe ngumthetho ngalo lonke ixesha isaphilileyo indoda yakhe; ke ukuba indoda yakhe ithe yafa, ukhululekile, angendiselwa kwayithandayo; kuphela eNkosini.', 'XHO22'),
(47, 7, 40, 'Kodwa unoyolo ngakumbi, ukuba uthe wahlala enjengoko anjalo, ngokwam mna ukubona; ndiba ke ndinaye nam uMoya kaThixo.', 'XHO22'),
(47, 8, 1, 'Ke kaloku, ngeendawo zamadini enziwa kwizithixo, ndithi, Siyazi ukuba sinokwazi sonke. Ulwazi luyakhukhumalisa, ke lona uthando luyakha.', 'XHO22'),
(47, 8, 2, 'Ukuba umntu uba wazi nto, akakazi nento ngoko amelwe kukwazi ngako.', 'XHO22'),
(47, 8, 3, 'Ukuba ke umntu uyamthanda uThixo, lowo uyaziwa nguye.', 'XHO22'),
(47, 8, 4, 'Ngayo ke ukudliwa kwamadini enziwa kwizithixo, siyazi ukuba akukho nto isisithixo ehlabathini; siyazi ukuba mnye kwaphela uThixo, akukho wumbi.', 'XHO22'),
(47, 8, 5, 'Kuba kambe, nokuba kukho ekuthiwa bangoothixo, nokuba kusezulwini, nokuba kusemhlabeni, njengokuba kukho oothixo abaninzi, neenkosi ezininzi,', 'XHO22'),
(47, 8, 6, 'Kodwa kuthi thina mnye uThixo, uYise, eziphuma kuye zonke izinto; neNkosi inye, uYesu Kristu, ezingaye zonke izinto;', 'XHO22'),
(47, 8, 7, 'Ke ukwazi oko akubantwini bonke. Inxenye ke ithi, isenesazela ngesithixo unangoku, idle ngokwedini elenziwa kwisithixo; sisuke isazela sabo siswele amandla.', 'XHO22'),
(47, 8, 8, 'Ke ukudla akusithandisi phambi koThixo; kuba nokuba sithe sadla, asigqithe nganto; nokuba asidli, asigqithe nganto.', 'XHO22'),
(47, 8, 9, 'Lumkani ke, hleze elo gunya lenu libe sisiwiso kwabaswele amandla.', 'XHO22'),
(47, 8, 10, 'Kuba xa athe umntu wakubona, wena unokwazi, uhleli esithebeni etempileni yesithixo, asisayi kuthi na isazela salowo uswele amandla, somelele ukuba adle amadini enziwa kwizithixo?', 'XHO22'),
(47, 8, 11, 'Ngokwazi kwakho uya kutshabalala umzalwana obuthathaka, awathi uKristu wamfela.', 'XHO22'),
(47, 8, 12, 'Kambe ke, xa nisona ngokunjalo kubazalwana, nisilimaza isazela sabo esiswele amandla, nona kuKristu.', 'XHO22'),
(47, 8, 13, 'Ngenxa yoko ndithi, Ukuba ukudla kuyamkhubekisa umzalwana wam, andisayi kudla nyama, lisahleli ihlabathi, ukuze ndingamkhubekisi umzalwana wam.', 'XHO22'),
(47, 9, 1, 'Andingompostile na? Andingokhululekileyo na? Andimbonanga na uYesu Kristu, iNkosi yethu? aningumsebenzi wam na eNkosini?', 'XHO22'),
(47, 9, 2, 'Ukuba andimpostile kwabanye, kuni nina ndinguye; kuba itywina lokuqinisa obam ubupostile linini aba baseNkosini.', 'XHO22'),
(47, 9, 3, 'Okwam ukuziphendulela kwabandincinayo kuko oku,', 'XHO22'),
(47, 9, 4, 'Asinagunya na lokutya nokusela?', 'XHO22'),
(47, 9, 5, 'Asinagunya na, njengabanye abapostile, nabazalwana beNkosi, noKefas, ukuba siphelekwe ngabafazi bethu bengamakholwa?', 'XHO22'),
(47, 9, 6, 'Okanye ndim ndedwa noBharnabhas, asinagunya lakuyeka ukusebenza?', 'XHO22'),
(47, 9, 7, 'Ngubani na okha aphume umkhosi ngeyakhe indleko? Ngubani na otyala isidiliya, aze angadli siqhamo saso? Ngubani na owalusa umhlambi, aze angadli kubisi lomhlambi?', 'XHO22'),
(47, 9, 8, 'Ndizithetha ngokomntu yini na ezo zinto? Awutsho nawo na umthetho?', 'XHO22'),
(47, 9, 9, 'Emthethweni kaMoses kubhaliwe kwathiwa, Uze ungayibophi umlomo inkomo ibhula. Ziinkomo yini na uThixo?', 'XHO22'),
(47, 9, 10, 'Kanene akatsho ngenxa yethu na? Kambe ke kubhaliwe ngenxa yethu: Lowo ulimayo ufanele ukulima enethemba; nalowo ubhulayo enethemba lokuba uya kubelana nabanye ngethemba lakhe.', 'XHO22'),
(47, 9, 11, 'Ukuba ke thina sanihlwayelela izinto ezizezoMoya, yinto enkulu na, ukuba siya kuvuna izinto zenu ezizezenyama?', 'XHO22'),
(47, 9, 12, 'Ukuba abanye babelana ngokwenza ngeli gunya kuni, asikukhona singenjenjalo na thina ngakumbi? Asilisebenzisanga ke elo gunya; sisuke sithwale izinto zonke, ukuze singazibambezeli ngani iindaba ezilungileyo zikaKristu.', 'XHO22'),
(47, 9, 13, 'Anazi na ukuba abo bazisebenzayo izinto ezingcwele badla okwetempile? abo bahlala behleli esibingelelweni bahlulelana nesibingelelo eso?', 'XHO22'),
(47, 9, 14, 'Ngokunjalo neNkosi le yabamisela abazazisayo iindaba ezilungileyo, ukuba baphile ngeendaba ezilungileyo ezi.', 'XHO22'),
(47, 9, 15, 'Ke mna andiphathanga nanye kwezo zinto. Ezi zinto andizibhaleli ukuze kwenjiwe njalo kum; kuba bekungilungele kanye ukuba ndife, kunokuba ubani alilambathise iqhayiya lam.', 'XHO22'),
(47, 9, 16, 'Kuba xa ndithi ndizishumayele iindaba ezilungileyo, andinaqhayiya; kuba ndimelwe kukuzishumayela. Yeha ke mna, ukuba ndisuka ndingazishumayeli iindaba ezilungileyo!', 'XHO22'),
(47, 9, 17, 'Kaloku xa ndikwenzayo oko ndinokuzithandela, ndoba ndinomvuzo; ke xa ndikwenza ndingenakuzithandela, ndoba ndingophathiswe ubugosa nje.', 'XHO22'),
(47, 9, 18, 'Uyintoni na ke ngoko umvuzo wam? Okunene ke, ndithi ndakuzishumayela iindaba ezilungileyo, ndizenze zingabi nandleko iindaba ezilungileyo zikaKristu, ukuze ndingalisebenzisi igunya lam ezindabeni ezilungileyo ezi.', 'XHO22'),
(47, 9, 19, 'Kuba ndikhululekile nje kubo bonke, ndizikhonzisile kubo bonke, ukuze ndibazuze abona baninzi.', 'XHO22'),
(47, 9, 20, 'KumaYuda ndaba njengomYuda, ukuze ndiwazuze amaYuda; kwabo baphantsi komthetho ndaba njengophantsi komthetho, ukuze ndibazuze abaphantsi komthetho;', 'XHO22'),
(47, 9, 21, 'kwabo bangenamthetho, ndaba njengongenamthetho, (ndingenguye phofu ongenamthetho kuThixo, ndingosemthethweni kuKristu), ukuze ndibazuze abangenamthetho;', 'XHO22'),
(47, 9, 22, 'kwabo bangenamandla, ndaba njengongenamandla, ukuze ndibazuze abangenamandla; kubo bonke ndesuka ndaba ziinto zonke, ukuze ngeendlela zonke ndisindise inxenye.', 'XHO22'),
(47, 9, 23, 'Oko ke ndikwenza ngenxa yeendaba ezilungileyo, ukuze ndibe lidlelane nabo ngazo.', 'XHO22'),
(47, 9, 24, 'Anazi na, ukuba abo babalekayo elugqatsweni, babaleka bonke okunene, kodwa ke mnye owamkelayo umvuzo? Balekani ngokunjalo ke, ukuze nizuze.', 'XHO22'),
(47, 9, 25, 'Bonke ke abantu abazamayo, baba ziingcathu ezintweni zonke. Oko ke ukuze bazuze isithsaba esinokonakala; ke thina, singenakonakala.', 'XHO22'),
(47, 9, 26, 'Mna ke ngoko ndenjenjalo ukubaleka, ndingangi andiqondi; ndenjenjalo ukulwa, ndingangi ndibetha umoya;', 'XHO22'),
(47, 9, 27, 'Ndisuka ndiwuqobe umzimba wam, ndiwuxinzelele phantsi, hleze ndithi, ndakuba ndizivakalisile iindaba ezilungileyo kwabanye, ndibe mna ndingumlahlelwa phandle.', 'XHO22'),
(47, 10, 1, 'Ke andithandi ukuba ningazi, bazalwana, ukuba oobawo babephantsi kwalo ilifu bonke; balucanda ulwandle bonke;', 'XHO22'),
(47, 10, 2, 'Babhaptizelwa bonke kuye uMoses efini elo naselwandle;', 'XHO22'),
(47, 10, 3, 'Badla bonke oko kudla kunye kukokomoya;', 'XHO22'),
(47, 10, 4, 'basela bonke olo luselo lunye lulolomoya, kuba babesela kwiliwa elilelomoya elibe libalandela, iliwa elo lalinguye uKristu.', 'XHO22'),
(47, 10, 5, 'Kodwa noko uThixo akakholiswanga ngabaninzi kubo; ngokuba bathiwa entlango.', 'XHO22'),
(47, 10, 6, 'Ezo zinto ke zabe ziyimizekelo kuthi, ukuze singabi ngabakhanuki bezinto ezimbi, njengokuba bakhanukayo bona abo.', 'XHO22'),
(47, 10, 7, 'Kanjalo musani ukuba ngababusi bezithixo, njengenxenye yabo; njengokuba kubhaliwe kwathiwa, Abantu bahlala phantsi ukuba badle, basele, baza besuka bema ukuba badlale.', 'XHO22'),
(47, 10, 8, 'Kanjalo masingenzi mbulo, njengokuba inxenye yabo yenzayo umbulo, kwawa ngamini-nye amawaka amashumi mabini anamathathu.', 'XHO22'),
(47, 10, 9, 'Kanjalo masingamhendi uKristu, njengokuba inxenye yabo yahendayo, yaza yatshabalala ziinyoka.', 'XHO22'),
(47, 10, 10, 'Kanjalo musani ukukrokra njengoko inxenye yabo yakrokrayo, yaza yatshabalala ngumtshabalalisi.', 'XHO22'),
(47, 10, 11, 'Zonke ke ezo zinto zabahlelayo bona, beziyimifuziselo; zabhalelwa ke ukululeka thina aba, bafikelweyo ziziphelo zawo amaphakade.', 'XHO22'),
(47, 10, 12, 'Ngoko ke lowo uba umi, makalumke angawi.', 'XHO22'),
(47, 10, 13, 'Anibanjwanga sihendo singesabantu; kodwa uThixo uthembekile, ongayi kuvumela ukuba nilingwe ngaphezu kweninako; owothi, kunye nesilingo eso, avelise ithuba lokuphuma kuso, nibe nako ukuthwala.', 'XHO22'),
(47, 10, 14, 'Kungoko ndithi, zintanda zam, kubalekeni ukubusa izithixo.', 'XHO22'),
(47, 10, 15, 'Ndithetha njengakwizilumko; kugwebeni endikuthethayo.', 'XHO22'),
(47, 10, 16, 'Indebe yentsikelelo le siyisikelelayo, asibubudlelane na begazi likaKristu? Isonka esisiqhekezayo, asibubudlelane na bomzimba kaKristu?', 'XHO22'),
(47, 10, 17, 'Kaloku thina, sibaninzi nje, sisonka sinye, simzimba mnye, kuba sonke sabelana ngeso sonka sinye.', 'XHO22'),
(47, 10, 18, 'Khangelani uSirayeli ngokwenyama. Abo bawadlayo amadini, abadlelani nesibingelelo eso?', 'XHO22'),
(47, 10, 19, 'Ndithini na ke ngoko? Okanye amadini enziwa into na?', 'XHO22'),
(47, 10, 20, 'Ndithi ke, Amadini eziwenzayo iintlanga, ziwenzela iindimoni, aziwenzeli uThixo. Andithandi ke ukuba nibe ngamadlelane needemon nina.', 'XHO22'),
(47, 10, 21, 'Aninako ukusela indebe yeNkosi nendebe yeendimoni. Aninako ukuba nenxaxheba esithebeni seNkosi, nasesithebeni seendimoni.', 'XHO22'),
(47, 10, 22, 'Siyayikhweletisa na iNkosi? Ngaba sinamandla kunaye?', 'XHO22'),
(47, 10, 23, 'Zonke izinto zivumelekile kum, noko azindilungele zonke. Zonke izinto zivumelekile kum, noko azakhi zonke.', 'XHO22'),
(47, 10, 24, 'Makungabikho namnye ufuna okukokwakhe ukulungelwa;', 'XHO22'),
(47, 10, 25, 'Yonke into ethengelwa endaweni yokuthenga inyama, yidleni, ningaphicothi nto ngenxa yesazela;', 'XHO22'),
(47, 10, 26, 'Kuba umhlaba lo ngoweNkosi, nenzaliseko yawo.', 'XHO22'),
(47, 10, 27, 'Ukuba ke ubani wakubo abangakholwayo uthi animeme, nithande ukuya; yonke into ebekwa phambi kwenu yidleni, ningaphicothi nto ngenxa yesazela.', 'XHO22'),
(47, 10, 28, 'Kodwa ke ukuba ubani uthe kuni, Le nto ilidini elenzelwe izithixo, musani ukudla, ngenxa ke yalowo uyithe thupha loo nto, nangenxa yesazela; kuba umhlaba lo ngoweNkosi, nenzaliseko yawo.', 'XHO22'),
(47, 10, 29, 'Ndithi, isazela ke, singesiso esakho wena; sisesomnye lowo. Kuba yini na ukuba inkululeko yam igwetywe sesazela somnye?', 'XHO22'),
(47, 10, 30, 'Kaloku ukuba mna ndidlelana ngobabalo, yini na ukuba ndinyeliswe ngenxa yoko ndikubulelayo mna?', 'XHO22'),
(47, 10, 31, 'Ke ngoko, nokuba niyadla, nokuba niyasela, nokuba nenza yiphi na into, zenzeleni uzuko lukaThixo izinto zonke.', 'XHO22'),
(47, 10, 32, 'Musani ukuba sisikhubekiso nakumaYuda, nakumaGrike, nakuwo ibandla likaThixo;', 'XHO22'),
(47, 10, 33, 'njengokuba nam ndikholisa bonke ezintweni zonke, ndingafuni okwam ukulungelwa, ndifuna okwabaninzi, ukuze basindiswe.', 'XHO22'),
(47, 11, 1, 'Yibani ngabaxelisa mna, njengokuba nam ndixelisa uKristu.', 'XHO22'),
(47, 11, 2, 'Ke kaloku ndiyanidumisa, bazalwana, kuba nindikhumbula ngeendawo zonke, nizibambile izithethe, njengoko ndaninikelayo.', 'XHO22'),
(47, 11, 3, 'Kodwa ndithanda ukuba nazi, ukuba intloko yomntu wonke oyindoda nguKristu; intloko yomfazi yindoda; intloko ke kaKristu nguThixo.', 'XHO22'),
(47, 11, 4, 'Umntu wonke oyindoda, ethandaza, mhlawumbi eprofeta, enento entloko, uya intloko yakhe.', 'XHO22'),
(47, 11, 5, 'Umntu wonke ke ongumfazi, ethandaza, mhlawumbi eprofeta, engenasigqubuthelo entloko, uyayihlaza eyakhe intloko; kuba oko kunto-nye nokuba uchetyiwe.', 'XHO22'),
(47, 11, 6, 'Kuba umfazi, ukuba akagqubuthele, makaselenquthulwa nokunquthulwa. Ke ukuba kulihlazo kumfazi ukuba anquthulwe, mhlawumbi achetywe, makabe nesigqubuthelo.', 'XHO22'),
(47, 11, 7, 'Kuba indoda okunene ayifanele ukuba nesigqubuthelo entloko, ekubeni ingumfanekiselo nozuko lukaThixo; ke yena umfazi luzuko lwendoda.', 'XHO22'),
(47, 11, 8, 'Kuba indoda ayiphume emfazini; ngumfazi oyindoda;', 'XHO22'),
(47, 11, 9, 'kuba indoda ayidalwanga ngenxa yomfazi; umfazi ke ngendoda.', 'XHO22'),
(47, 11, 10, 'Ngenxa yoko umfazi ufanele ukuba abe nomqondiso wegunya phezu kwentloko, ngenxa yezithunywa zezulu.', 'XHO22'),
(47, 11, 11, 'Kodwa eNkosini apha indoda ayingaphandle komfazi, nomfazi akangaphandle kwendoda;', 'XHO22'),
(47, 11, 12, 'kuba, njengokuba umfazi ephume kwindoda, ngokunjalo nayo indoda ingaye umfazi; kodwa zonke izinto zikaThixo.', 'XHO22'),
(47, 11, 13, 'Zigwebeleni ngokwenu. Kumfanele na ukuba umfazi athandaze kuThixo, engagqubuthele?', 'XHO22'),
(47, 11, 14, 'Kananjalo imvelo ngokwayo ayinifundisi na, ukuba indoda okunene ethi ibe neenwele ezinde, kulihlazo oko kuyo?', 'XHO22'),
(47, 11, 15, 'Ke yena umfazi, ukuba uthi abe neenwele ezinde, kuluzuko oko kuye; ngokuba wazinikwa iinwele ezinde endaweni yesigqubuthelo.', 'XHO22'),
(47, 11, 16, 'Ke ukuba ubani ubonakala ethanda ukuphika, thina asinasiko linjalo, kwanawo amabandla kaThixo.', 'XHO22'),
(47, 11, 17, 'Ke kaloku, ndinithethela le ndawo nje, andinincomi, ukuba ukuhlangana kwenu ndawonye akusingisi kokhona kulungileyo;', 'XHO22'),
(47, 11, 18, 'Kuba kuqalake, nakuhlangana ebandleni, ndiyeva ukuba kubakho izahlukahlukano phakathi kwenu; nam ke ndiyakholwa yiyo.', 'XHO22'),
(47, 11, 19, 'Kuba kumele ukuba kubekho nezahlukano phakathi kwenu, ukuze abacikidekileyo babonakale phakathi kwenu.', 'XHO22'),
(47, 11, 20, 'Xenikweni ngoko nithe nahlangana ndawonye, asikuko ukuba nidle isidlo seNkosi;', 'XHO22'),
(47, 11, 21, 'Kaloku xa batyayo elowo udla owakhe isidlo, aze omnye alambe, omnye anxile.', 'XHO22'),
(47, 11, 22, 'Yintoni na? Aninazindlu zokudlela nokusela na? Okanye nidela ibandla likaThixo na, nibadanisa nje abangenanto? Ndithini na kuwe? Ndinidumise na kule nto? andikuncomi;', 'XHO22'),
(47, 11, 23, 'Kuba mna ndakwamkela eNkosini oko ndikunikeleyo nokukunikela kuni: Okokuba iNkosi uYesu, ngobusuku eyanikelwa ngabo, yathabatha isonka;', 'XHO22'),
(47, 11, 24, 'yaza yakuba ibulele, yasiqhekeza, yathi, Thabathani, nidle, ngumzimba wam lo, owaphulelwa nina; oku kwenzeleni ukundikhumbula.', 'XHO22'),
(47, 11, 25, 'Kwangokunjalo yathabatha nayo indebe, emva kokuba kudliwe, isithi, Le ndebe ingumnqophiso omtsha osegazini lam; ngamaxesha onke enisukuba niyisela, oku kwenzeleni ukundikhumbula.', 'XHO22'),
(47, 11, 26, 'Kuba ngamaxesha onke enisukuba nisidla eso sonka, niyisele loo ndebe, niyakwazisa ukufa kweNkosi, ide ifike.', 'XHO22'),
(47, 11, 27, 'Ngoko ke, osukuba esidla eso sonka, ayisele indebe yeNkosi, ngokungafanelekileyo, woba netyala lomzimba negazi leNkosi.', 'XHO22'),
(47, 11, 28, 'Makazicikide ke ngokwakhe umntu, aze ngokunjalo adle kwisonka eso, ayisele nendebe leyo;', 'XHO22'),
(47, 11, 29, 'kuba lowo udlayo, uselayo, ngokungafanelekileyo, uzidlela aziselele ukugwetywa, engawucaluli nje umzimba weNkosi.', 'XHO22'),
(47, 11, 30, 'Ngenxa yoku baninzi phakathi kwenu abaswele amandla, nabayimilwelwe, banele nabalele ukufa.', 'XHO22'),
(47, 11, 31, 'Kuba xa besizicalula, ngesiba asigwetywa.', 'XHO22'),
(47, 11, 32, 'Sigwetywa nje ke, siyaqeqeshwa yiNkosi, ukuze singasingelwa phantsi ndawonye nehlabathi.', 'XHO22'),
(47, 11, 33, 'Ngoko ke, bazalwana bam, xa nihlangene ndawonye ukuba nidle, lindanani.', 'XHO22'),
(47, 11, 34, 'Ukuba ubani ulambile, makatye ekhaya; ukuze ningahlangani ndawonye ukuze nigwetywe. Eziseleyo ke iindawo ndozilungisa ndakufika.', 'XHO22'),
(47, 12, 1, 'Ke ngezipho ezizezoMoya andithandi ukuba ningazi, bazalwana.', 'XHO22'),
(47, 12, 2, 'Niyazi ukuba nanifudula niziintlanga, nikhukuliselwe ezithixweni ezizizidenge, njengoko nisakuthi nikhokelwe ngako.', 'XHO22'),
(47, 12, 3, 'Kungoko ndinazisayo, ukuba akukho namnye, ethetha ekuMoya kaThixo, unokuthi ungosingelwe phantsi uYesu; kanjalo akukho namnye unako ukuthi uyiNkosi uYesu, engekuye uMoya oyiNgcwele.', 'XHO22'),
(47, 12, 4, 'Ke kaloku kukho izabelo ngezabelo zezibabalo, kodwa ke ikwanguloo Moya mnye.', 'XHO22'),
(47, 12, 5, 'Kukho nezabelo ngezabelo zezabelo zemisebenzi, kodwa ikwayiloo Nkosi inye.', 'XHO22'),
(47, 12, 6, 'Kukho nezabelo ngezabelo zeentsebenzo, kodwa ke ikwanguloo Thixo mnye uzisebenzayo izinto zonke kubo bonke.', 'XHO22'),
(47, 12, 7, 'Elowo ke uphiwa ukubonakalalisa uMoya, kuze kubekho okulungele bonke;', 'XHO22'),
(47, 12, 8, 'Kuba omnye okunene uphiwa ngaye uMoya ilizwi lobulumko; omnye ke, ilizwi lokwazi kwangaloo Moya;', 'XHO22'),
(47, 12, 9, 'wumbi ke uphiwa ukholo kwangaloo Moya; omnye ke, izibabalo zeziphiliso kwangaloo Moya;', 'XHO22'),
(47, 12, 10, 'omnye ke uphiwa imimangaliso; omnye isiprofeto esi; omnye ke, ukucalula oomoya; wumbi ke, iintlobo zeelwimi; omnye ke, ukucaciswa kweelwimi;', 'XHO22'),
(47, 12, 11, 'Zonke ke ezi zinto zisetyenzwa kwangaloo Moya mnye; umabela elowo ngokwakhe njengoko athandayo.', 'XHO22'),
(47, 12, 12, 'Kuba, kwanjengokuba umzimba umnye, unamalungu amaninzi, abe ke onke amalungu aloo mzimba mnye, emaninzi nje, emzimba mnye, ukwanjalo ke noKristu lo.', 'XHO22'),
(47, 12, 13, 'Kaloku thina sonke sabhaptizelwa mzimbeni mnye ngaMoya mnye, nokuba singamaYuda, nokuba singamaGrike, nokuba singamakhoboka, nokuba singabakhululekileyo; nokuba sasezwa Moyeni mnye sonke.', 'XHO22'),
(47, 12, 14, 'Kaloku nomzimba lo ungelungu linye, ungamaninzi.', 'XHO22'),
(47, 12, 15, 'Ukuba luthe unyawo, Ngokuba ndingesiso isandla, andingowasemzimbeni; Lo asingowasemzimbeni na?', 'XHO22'),
(47, 12, 16, 'Nokuba ithe indlebe, Ngokuba ndingelilo iliso, andingowasemzimbeni; Lo asingowasemzimbeni na?', 'XHO22'),
(47, 12, 17, 'Ukuba umzimba lo uphela ubuliliso, bekuya kuba phi na ukuva? Ukuba ubukukuva uphela, bekuya kuba phi na ukusezela?', 'XHO22'),
(47, 12, 18, 'Kungoku ke, uThixo uthe wawamisa amalungu onke emzimbeni, njengoko ethanda.', 'XHO22'),
(47, 12, 19, 'Ukuba ke onke ebelungu linye, ubuya kuba phi na umzimba?', 'XHO22'),
(47, 12, 20, 'Kungoku ke maninzi okunene amalungu, kodwa mnye wona umzimba.', 'XHO22'),
(47, 12, 21, 'Alinako ke iliso ukuthi kwisandla, Akufuneki kum; kanjalo intloko ayinakuthi kwiinyawo, Anifuneki kum.', 'XHO22'),
(47, 12, 22, 'Kambe ke wona amalungu la malungu omzimba abonakala ebuthathaka ngakumbi.', 'XHO22'),
(47, 12, 23, 'Namalungu omzimba esiba ngaswele imbeko kunamanye, lawo ngawona siwabeka embekweni; aze amalungu ethu abekekileyo, abe ngawona abekekileyo.', 'XHO22'),
(47, 12, 24, 'Amalungu ethu ke abekekileyo akufuneki nto kuwo. Usuke uThixo wawumanya umzimba, wathi ilungu elingento walibeka kweyona mbeko inkulu;', 'XHO22'),
(47, 12, 25, 'ukuze kungabikho kwahlukahlukana emzimbeni; kodwa ukuba amalungu anyamekelane;', 'XHO22'),
(47, 12, 26, 'kuthi, nokuba lilungu elinye eliva ubunzima, avelane amalungu onke; nokuba lilungu elinye elizukiswayo, avuyisane amalungu onke.', 'XHO22'),
(47, 12, 27, 'Nina ke ningumzimba kaKristu, ngokwamalungu mnye.', 'XHO22'),
(47, 12, 28, 'Inxenye uThixo wayimisa ebandleni, kuqala yangabapostile, okwesibini abaprofeti, okwesithathu abafundisi; kwaza kwaba yimisebenzi yamandla, kwaba zizibabalo zeziphiliso, iintsizo, izilawulo, iintlobo zeelwimi.', 'XHO22'),
(47, 12, 29, 'Bangabapostile bonke na? Bangabaprofeti bonke na? Ngabafundisi bonke? Banemisebenzi yamandla bonke na?', 'XHO22'),
(47, 12, 30, 'Baneziphiwo zokuphilisa bonke? Bathetha ngalwimi zimbi bonke yini na? Batolika bonke bephela?', 'XHO22'),
(47, 12, 31, 'Zondelelani ke ezona zibabalo zilungileyo, kanti ke ndisanibonisa eyona ndlela incamisileyo.', 'XHO22'),
(47, 13, 1, 'Ndingafanelana ndithetha ngeelwimi zabantu nezezithunywa zezulu; uthando ke ndingenalo, ndisuke ndaba yixina ehlokomayo, necangci elikhenkcezayo.', 'XHO22'),
(47, 13, 2, 'Ndingafanelana ndinabo nobuprofeto, ndizazi iimfihlelo zonke, ndinako konke ukwazi; ndingafanelana ndinalo lonke ukholo, ngokokude ndisuse iintaba, uthando ke ndingenalo, andinto yanto.', 'XHO22'),
(47, 13, 3, 'Ndingafanelana ndisamkelisa amahlwempu ngempahla yam yonke, nokuba ndithe umzimba wam ndawunikela ukuba utshiswe, uthando ke ndingenalo, akundincedi lutho.', 'XHO22'),
(47, 13, 4, 'Uthando luzeka kade umsindo, lunobubele; uthando alunamona; Uthando alukhukhumali, alukhukhumali;', 'XHO22'),
(47, 13, 5, 'Aluziphathi okuziintloni; alufuni okukokwalo; alucaphuki; alucaphuki;', 'XHO22'),
(47, 13, 6, 'Aluvuyeli kungalungisi; luvuyisana nenyaniso;', 'XHO22'),
(47, 13, 7, 'Luthwala iinto zonke; lukholwa ziinto zonke; luthemba iinto zonke;', 'XHO22'),
(47, 13, 8, 'Uthando aluze lutshitshe; nokuba ke ziziprofeto, ziya kutshitshiswa; nokuba ziilwimi, ziya kupheza; nokuba kukwazi, kuya kuphuthiswa.', 'XHO22'),
(47, 13, 9, 'Kuba siyazi ngokuyinxenye, siprofeta ngokuyinxenye.', 'XHO22'),
(47, 13, 10, 'Xa kuthe ke kwafika okuzalisekileyo, kuya kwandula ukuphuthiswa okuyinxenye.', 'XHO22'),
(47, 13, 11, 'Oko bendingumntwana, bendithetha ngokomntwana, bendiqonda ngokomntwana, bendicamanga ngokomntwana; ndithe ke, ndakuba yindoda, ndazibhangisa izinto zobuntwana.', 'XHO22'),
(47, 13, 12, 'Kuba ngoku sikhangela esipilini ngokwamanakanibe; oko ke, siya kukhangelana ebusweni. Ngoku ndiyazi ngokuyinxenye; oko ke ndiya kwazi, njengokuba ndaziwayo nam.', 'XHO22'),
(47, 13, 13, 'Kungoku ke kuhleli ukholo, ithemba, uthando, ezo zinto zontathu; eyona inkulu ke kuzo apho luthando.', 'XHO22'),
(47, 14, 1, 'Phuthumani uthando olo, nibe kodwa nizizondelela izipho ezizezoMoya, ngokukodwa ke ukuba niprofete.', 'XHO22'),
(47, 14, 2, 'Kaloku lowo uthetha ngalulwimi lumbi akathethi bantwini, uthetha kuThixo, kuba akukho namnye umvayo; kanti ke ngomoya uthetha iimfihlelo.', 'XHO22'),
(47, 14, 3, 'Ke yena oprofetayo uthetha ebantwini ulwakheko, noyalo, nokhuzo.', 'XHO22'),
(47, 14, 4, 'Lowo uthetha ngalulwimi lumbi uzakha yena; ke yena oprofetayo wakha ibandla.', 'XHO22'),
(47, 14, 5, 'Ndiyathanda ke ukuba nithethe ngalwimi zimbi nonke, noko ke ndithanda ngokukodwa ukuba niprofete. Kuba yena oprofetayo mkhulu kunalowo uthetha ngalwimi zimbi, xa lo angayicacisiyo intetho, ukuze ibandla libe nokwakheka.', 'XHO22'),
(47, 14, 6, 'Kunjalo ke, bazalwana, ukuba ndithe ndeza kuni, ndithetha ngalwimi zimbi, bendiya kuninceda ntoni na, xa ndingathanga ndithethe kuni ndinesityhilelo, ndinokwazi, ndinesiprofeto, ndinemfundiso?', 'XHO22'),
(47, 14, 7, 'Khona kwizinto ezihlokomayo ezingenamphefumlo, ezinjengembande, nohadi, ukuba azithanga zahlule ukuhlokoma kwazo, bekungathiwani na ukwaziwa oko kubethwayo ngembande, noko kubethwayo ngohadi?', 'XHO22'),
(47, 14, 8, 'Kuba kanjalo, xa ixilongo lithe lalila ngezwi elingaqondakaliyo, ngubani na owoyixhobela imfazwe?', 'XHO22'),
(47, 14, 9, 'Ngokukwanjalo nani, ukuba anithanga nithethe ngalo ulwimi intetho eqondeka kakuhle, yothiwani na ukwaziwa into enithetha yona? kuba nothethela esibhakabhakeni.', 'XHO22'),
(47, 14, 10, 'Nokuba zingaba sezingakanani na ukuba zininzi kwazo iintlobo zeentetho ehlabathini, akukho nalunye kuzo olungenanto luthetha yona.', 'XHO22'),
(47, 14, 11, 'Ukuba ngoko andithanga ndiyazi intsingiselo yentetho, ndoba ngumbharbhari kothethayo, nothethayo woba ngumbharbhari kum.', 'XHO22'),
(47, 14, 12, 'Ngokukwanjalo nani, ekubeni kaloku nizondelela izipho ezizezoMoya, zifuneleni ukuba nibe nazo ngokugqithiseleyo, ukuze lakheke ibandla.', 'XHO22'),
(47, 14, 13, 'Ngenxa yoko ndithi, othethayo ngalwimi zimbi, makathandazele ukuba icace intetho yakhe.', 'XHO22'),
(47, 14, 14, 'Kuba xa ndithandaza ngalulwimi lumbi, umoya wam uyathandaza okunene wona, ke yona ingqiqo yam ayinasiqhamo.', 'XHO22'),
(47, 14, 15, 'Kuthini na ke ngoko? Ndiya kuthandaza ngomoya, ndithandaze nangengqiqo; ndiya kuvuma ngomoya, ndivume nangengqiqo.', 'XHO22'),
(47, 14, 16, 'Okanye, ukuba uthe wasikelela ngomoya wodwa, wothini na lowo uhleliyo endaweni yongenakwazi ukuthi, Amen, ekubuleleni kwakho, ekubeni kaloku into oyithethayo engayazi?', 'XHO22'),
(47, 14, 17, 'Kuba okunene wena uyatyapha ukubulela, kodwa onguwumbi akakheki.', 'XHO22'),
(47, 14, 18, 'Ndiyabulela kuThixo wam, kuba ndithetha ngalwimi zimbi ngaphezu kwenu nonke;', 'XHO22'),
(47, 14, 19, 'Kodwa ke ebandleni ndithanda ukuthetha amazwi abe mahlanu ngayo ingqiqo yam, ukuze ndibafundise nabanye, kunokuba ndithethe amazwi angamawaka alishumi, ngalulwimi lumbi.', 'XHO22'),
(47, 14, 20, 'Bazalwana, musani ukuba ngabantwana engqondweni, ebubini yibani ziintsana, engqondweni ke yibani ngabantu abakhulu.', 'XHO22'),
(47, 14, 21, 'Emthethweni kubhaliwe kwathiwa, Ndiya kuthetha kwaba bantu ngalwimi zimbi nangamlomo wumbi; noko kunjalo, abayi kundiva, itsho iNkosi.', 'XHO22'),
(47, 14, 22, 'Ngoko ke iilwimi ezi zikho ukuba zibe ngumqondiso kwabo bangakholwayo, kungekubo abakholwayo: ke sona isiprofeto asisesabangakholwayo, sesabakholwayo.', 'XHO22'),
(47, 14, 23, 'Xa lithe lahlangana ndawonye ibandla liphela, bathetha bonke ngalwimi zimbi, kwaza kwangena abangenakwazi, nokuba ngabangakholwayo, abasayi kuthi na, niyageza?', 'XHO22'),
(47, 14, 24, 'Ke ukuba bathi bonke baprofete, kuze ke kungene umntu ongakholwayo, nokuba ngongenakwazi, uyohlwaywa ngabo bonke, adane;', 'XHO22'),
(47, 14, 25, 'zize ngokunjalo izinto ezifihlakeleyo zentliziyo yakhe zibonakalaliswe; wothi ngokunjalo, esiwa ngobuso, amnqule uThixo, exela ukuba uThixo uphakathi kwenu okwenyaniso.', 'XHO22'),
(47, 14, 26, 'Kuthini na ke ngoko, bazalwana? Xa sukuba nihlangene, uthi elowo kuni abe nengoma, abe nemfundiso, abe nalulwimi lumbi, abe nesityhilelo, abe nokuchaza intetho. Zonke izinto mazenziwe ukuze zakheke.', 'XHO22'),
(47, 14, 27, 'Kaloku ukuba umntu uthetha ngalulwimi lumbi, makuthethe babini, mhlawumbi bathathu, bangegqithi, kube ngokulandelelana, aze omnye atolike.', 'XHO22'),
(47, 14, 28, 'Ke xa kungekho mchazi-ntetho, makathi cwaka ebandleni; makathethe kuye ngokwakhe nakuThixo.', 'XHO22'),
(47, 14, 29, 'Abaprofeti ke, makuthethe babini, mhlawumbi bathathu, abanye bacalule.', 'XHO22'),
(47, 14, 30, 'Ukuba ke kuthe watyhilelwa into omnye ehleli khona, owokuqala makathi cwaka.', 'XHO22'),
(47, 14, 31, 'Kuba ninako ukuprofeta ngabanye nonke, ukuze bafunde bonke, bavuselelwe bonke.', 'XHO22'),
(47, 14, 32, 'Kwaye oomoya babaprofeti bayabathobela abaprofeti.', 'XHO22'),
(47, 14, 33, 'Kuba uThixo akangowesiphithiphithi, ngowoxolo; njengokuba kunjalo kuwo onke amabandla abangcwele.', 'XHO22'),
(47, 14, 34, 'Abafazi benu mabathi tu emabandleni; kuba abavunyelwa bona ukuba bathethe. Basuka bathobele, njengokuba usitsho nomthetho.', 'XHO22'),
(47, 14, 35, 'Ukuba ke banga bangaqonda into, mabayibuze ekhaya kwawabo amadoda; kuba lihlazo kubafazi ukuthetha ebandleni.', 'XHO22'),
(47, 14, 36, 'Yintoni na? Kanene ilizwi likaThixo laphuma livela kuni na? Ngaba lize kuni nedwa?', 'XHO22'),
(47, 14, 37, 'Ukuba umntu uba ungumprofeti, nokuba uba ungowoMoya, makazazi kakuhle izinto endinibhalela zona, ukuba yimithetho yeNkosi.', 'XHO22'),
(47, 14, 38, 'Ke ukuba umntu akazi, makangazi.', 'XHO22'),
(47, 14, 39, 'Kungoko ndithi, bazalwana, kuzondeleleni ukuprofeta, ningakwaleli ukuthetha ngalwimi zimbi.', 'XHO22'),
(47, 14, 40, 'Zonke izinto mazenziwe ngokubekekileyo, nangolungelelwano.', 'XHO22'),
(47, 15, 1, 'Ke kaloku, bazalwana, ndiyanazisa iindaba ezilungileyo, ezo ndazishumayela kuni, nazamkelayo nokuzamkela, nimiyo kuzo;', 'XHO22'),
(47, 15, 2, 'Nisindiswayo nokusindiswa ngazo, ukuba nizibambile ngelizwi endazishumayela ngalo kuni; ukuba anithe nafumana nakholwa.', 'XHO22'),
(47, 15, 3, 'Kuba ndaninikela kweziyintloko izinto oko ndakwamkelayo nokukwamkela: okokuba uKristu wazifela izono zethu, ngokwezibhalo;', 'XHO22'),
(47, 15, 4, 'nokokuba wangcwatywa, nokokuba uvukile ngomhla wesithathu ngokwezibhalo;', 'XHO22'),
(47, 15, 5, 'nokokuba wabonwa nguKefas, emva koko ngabalishumi elinababini.', 'XHO22'),
(47, 15, 6, 'Emveni kuko wabonwa ngaxesha linye ngabazalwana abangaphezu kwamakhulu amahlanu, ababengaphezu kwamakhulu amahlanu; abasahleliyo nangoku abona baninzi kubo; inxenye ke yalala ukufa.', 'XHO22'),
(47, 15, 7, 'Emva koko wabonwa nguYakobi; emva koko kubo bonke abapostile.', 'XHO22'),
(47, 15, 8, 'Ekugqibeleni ke kwabo bonke, kwanga kukwinto ezelwe lingekabi lilixa layo, wabonwa nandim.', 'XHO22'),
(47, 15, 9, 'Kuba mna ndingoyena mncinanana kubapostile, endingafanele kubizwa ngokuba ngumpostile, ngenxa enokuba ndalitshutshisa ibandla likaThixo.', 'XHO22'),
(47, 15, 10, 'Ke ngobabalo lukaThixo ndiyinto endiyiyo, nobabalo lwakhe kum alubanga nakulambatha; ndesuka ndabagqitha bonke ngokubulaleka; ingendim ke, ilubabalo lukaThixo olunam.', 'XHO22'),
(47, 15, 11, 'Ngoko ke, nokuba ndim, nokuba ngabo, senjenjalo thina ukushumayela; nenjenjalo ke nina ukukholwa.', 'XHO22'),
(47, 15, 12, 'Ke, ukuba uKristu uyashunyayelwa ukuba uvukile kwabafileyo, batsho ngani na abathile phakathi kwenu ukuthi, akukho luvuko lwabafileyo?', 'XHO22'),
(47, 15, 13, 'Ke, ukuba akukho luvuko lwabafileyo, woba akavukanga noKristu;', 'XHO22'),
(47, 15, 14, 'Ukuba ke uKristu akavukanga, koba akunto ukushumayela kwethu, loba luyalambatha nokholo lwenu.', 'XHO22'),
(47, 15, 15, 'Sofunyanwa ke singawo namangqina amxokayo uThixo; ngokuba sangqinayo ngoThixo ukuba wamvusa uKristu, angamvusanga, ukuba ke kanti abafileyo abavuki.', 'XHO22'),
(47, 15, 16, 'Kuba, ukuba abafileyo abavuki, noKristu woba akavukanga.', 'XHO22'),
(47, 15, 17, 'Ukuba ke uKristu akavukanga, loba luyinto engeyanto ukholo lwenu; nisesezonweni zenu;', 'XHO22'),
(47, 15, 18, 'ngoko nabalele ukufa bekuKristu, boba batshabalele.', 'XHO22'),
(47, 15, 19, 'Ukuba singabathembele kuKristu kobu bomi bodwa, soba sizezona ntsizana kubantu bonke.', 'XHO22'),
(47, 15, 20, 'uKristu yena kunje uvukile kwabafileyo, waba yintlahlela yabalele ukufa.', 'XHO22'),
(47, 15, 21, 'Kuba ekubeni kaloku kungomntu ukufa, kukwangomntu ukuvuka kwabafileyo.', 'XHO22'),
(47, 15, 22, 'Kuba, njengokuba kuye uAdam bafa bonke, ngokunjalo nakuye uKristu baya kudliswa ubomi bonke.', 'XHO22'),
(47, 15, 23, 'Elowo ke kolwakhe uluhlu; kamva abakhe bangabakaKristu ekufikeni kwakhe.', 'XHO22'),
(47, 15, 24, 'Kwandule ke ukubakho isiphelo, xa athe wabunikela ubukumkani kuye uThixo uYise, xa athe wabubhangisa bonke ubulawuli, nalo lonke igunya, namandla.', 'XHO22'),
(47, 15, 25, 'Kuba umelwe yena kukuthi alawule, ade azibeke phantsi kweenyawo zakhe zonke iintshaba.', 'XHO22'),
(47, 15, 26, 'Olokugqibela utshaba oluya kubhangiswa kukufa;', 'XHO22'),
(47, 15, 27, 'Kuba izinto zonke wazithobela phantsi kweenyawo zakhe. Ke xa athi, zonke izinto zithotyiwe, kuyabonakala ukuba akabalwa lowo wazithobela phantsi kwakhe zonke izinto.', 'XHO22'),
(47, 15, 28, 'Xa ke zithe zathotyelwa phantsi kwakhe zonke izinto, uya kwandula noNyana ngokwakhe athotyelwe phantsi kwalowo wazithobela phantsi kwakhe zonke izinto, ukuze uThixo abe zizinto zonke kubo bonke.', 'XHO22'),
(47, 15, 29, 'Okanye baya kuthini na abo babhaptizelwa abafileyo, ukuba abafileyo abavuki? yini na ke ukuba babhaptizelwe abafileyo?', 'XHO22'),
(47, 15, 30, 'Nathi, yini na ukuba sibe semngciphekweni amaxa onke?', 'XHO22'),
(47, 15, 31, 'Ndisekufeni imihla ngemihla; qondani ngokuzingca kwam ngani, endinako ndikuKristu Yesu, iNkosi yethu.', 'XHO22'),
(47, 15, 32, 'Ukuba ndithe ngokwabantu ndalwa namarhamncwa e-Efese, kundinceda ntoni na? masidle, sisele; kuba ngomso siya kufa.', 'XHO22'),
(47, 15, 33, 'Musani ukulahlekiswa. Incoko embi yonakalisa izimilo ezilungileyo.', 'XHO22'),
(47, 15, 34, 'Vumelani ubulungisa, ningoni; kuba inxenye inentswela-kumazi uThixo; nditsho ukuba nidane.', 'XHO22'),
(47, 15, 35, 'Wosuka ubani athi, Bathini na ukuvuka abafileyo? beza benamzimba mni na?', 'XHO22'),
(47, 15, 36, 'Msweli-kuqonda wena, into oyihlwayelayo ayenziwa iphile, ukuba ayikhanga ife;', 'XHO22'),
(47, 15, 37, 'Kanjalo loo nto uyihlwayelayo, akuhlwayeli sona isiqu sento esiya kubakho; uhlwayela ukhozo nje, lungathi lube lolwengqolowa, nokuba lolwezinye iintlobo;', 'XHO22'),
(47, 15, 38, 'Kodwa uThixo ulunika isiqu, njengoko athandileyo; iyileyo imbewu uyinika esisesayo isiqu.', 'XHO22'),
(47, 15, 39, 'Yonke inyama ayinyama-nye; enye yinyama yabantu, enye yinyama yeenkomo, enye yeyeentlanzi, enye yeyeentaka.', 'XHO22'),
(47, 15, 40, 'Kukho nemizimba yasezulwini, nemizimba yasemhlabeni; kodwa ubuqaqawuli bowasezulwini bunye, bubodwa ubuqaqawuli bowasemhlabeni.', 'XHO22'),
(47, 15, 41, 'Bubodwa ubuqaqawuli belanga, bubodwa ubuqaqawuli benyanga, bubodwa ubuqaqawuli beenkwenkwezi; kuba inkwenkwezi iyahluka kwenye inkwenkwezi ngobuqaqawuli.', 'XHO22'),
(47, 15, 42, 'Lunjalo ke kanye novuko lwabafileyo. Uhlwayelwa unokonakala; luvuswa lungenakonakala;', 'XHO22'),
(47, 15, 43, 'Uhlwayelwa uswele imbeko; Uhlwayelwa uswele amandla, uvuka unobuqaqawuli; luvuka lusemandleni;', 'XHO22'),
(47, 15, 44, 'Kuhlwayelwa umzimba wemvelo; kuvuka umzimba womoya. Kukho umzimba ongowemvelo, kukho nomzimba ongowomoya.', 'XHO22'),
(47, 15, 45, 'Ngokunjalo kubhaliwe kwathiwa, Owokuqala umntu uAdam waba ngumphefumlo odla ubomi; owokugqibela uAdam waba ngumoya odlisa ubomi.', 'XHO22'),
(47, 15, 46, 'Ke akuqali okomoya; kuqala okwemvelo, kwandule ukuza okomoya, kwandule ke oko koMoya.', 'XHO22'),
(47, 15, 47, 'Owokuqala umntu uphume emhlabeni, ungowomhlaba; owesibini umntu ke, iNkosi, uphume emazulwini.', 'XHO22'),
(47, 15, 48, 'Njengoko enjalo owomhlaba, bakwayiyo abomhlaba; kanjalo nento ayiyo osemazulwini, bakwayiyo abo basemazulwini.', 'XHO22'),
(47, 15, 49, 'Njengoko sawambathayo umfanekiselo wowomhlaba, siya kuwambatha nomfanekiselo wosemazulwini.', 'XHO22'),
(47, 15, 50, 'Ke, bazalwana, nditsho ukuthi, inyama negazi azinako ukubudla ilifa ubukumkani bukaThixo; nokonakala akunakukudla ilifa ukungonakali.', 'XHO22'),
(47, 15, 51, 'Niyabona, ndinixelela imfihlelo: Okunene asiyi kulala ukufa sonke; Asiyi kulala sonke, kodwa siya kwenziwa ngakumbi sonke;', 'XHO22'),
(47, 15, 52, 'Ngesiquphe, ngephanyazo, ngexilongo lokugqibela; kuba liya kukhala ixilongo, bavuke abafileyo bengenakonakala, senziwe ngakumbi ke thina.', 'XHO22'),
(47, 15, 53, 'Kuba kumele oku konakalayo ukwambatha ukungonakali, noku kufayo ukwambatha ukungafi.', 'XHO22'),
(47, 15, 54, 'Xa ke oku konakalayo kuthe kwambatha ukungonakali, noku kufayo kuthe kwambatha ukungafi, liya kwandula ukwenzeka ilizwi elibhaliweyo lokuthi, Ukufa kufincelwe eloyisweni.', 'XHO22'),
(47, 15, 55, 'Luphi na, kufa, ulwamvila lwakho? luphi na, bethuna, uloyiso lwakho?', 'XHO22'),
(47, 15, 56, 'Ulwamvila lokufa sisono; amandla esono ngumthetho.', 'XHO22'),
(47, 15, 57, 'Makubulelwe ke kuThixo osinikayo uloyiso olo, ngayo iNkosi yethu uYesu Kristu.', 'XHO22'),
(47, 15, 58, 'Ngoko ke, bazalwana bam baziintanda, qinani, ningabi nakushukuma; nihlale niphuphuma umsebenzi weNkosi, nisazi nje ukuba ukubulaleka kwenu akulambathi eNkosini.', 'XHO22'),
(47, 16, 1, 'Ke kaloku, ngokubuthwa kwemali yokusiza amakholwa, yenzani ngokunjalo nani ndawamisela amabandla kaThixo akwelaseGalati.', 'XHO22'),
(47, 16, 2, 'Ngolokuqala usuku, iiveki ngeeveki, elowo kuni makabeke ekhaya, eqweba, into asukuba esikelelwe ngayo;', 'XHO22'),
(47, 16, 3, 'Ndakuba ndifikile, enisukuba nibacikidile nakholwa ngabo, ndobathuma bona benencwadi, base isipho senu esi eYerusalem.', 'XHO22'),
(47, 16, 4, 'Ke ukuba kuthe kwabonakala ukuba nam ndiye, bohamba kunye nam.', 'XHO22'),
(47, 16, 5, 'Emva koko ndiya kuza kuni, xa ndithe ndacanda kwelaseMakedoni, kuba ndiya kwanela ukucanda nje elaseMakedoni.', 'XHO22'),
(47, 16, 6, 'Ke kuni mhlawumbi ndingathi ndihlale ndidlulise nobusika, ukuze nina nindiphelekelele apho ndisukuba ndisiya khona.', 'XHO22'),
(47, 16, 7, 'Kuba andithandi ukuba ndinibone ngoku ndidlulayo; kodwa ndithemba ukuba ndokha ndihlale kuni ixesha elithile, ukuba iNkosi ithe yavuma.', 'XHO22'),
(47, 16, 8, 'Kodwa ndiya kuhlala e-Efese kude kubekho ipentekoste.', 'XHO22'),
(47, 16, 9, 'Kuba ndivulelwe umnyango omkhulu onamandla, bebaninzi nabachasi.', 'XHO22'),
(47, 16, 10, 'Ukuba uTimoti uthe wafika, ize nikhangele ukuba angabi nakoyika phakathi kwenu; kuba uyawusebenza umsebenzi weNkosi, kwanjengam.', 'XHO22'),
(47, 16, 11, 'Makungabikho mntu ngoko umenza into engento. Mphelekeleleni ke ninoxolo, ukuze eze kum; kuba ndimlindile kunye nabazalwana.', 'XHO22'),
(47, 16, 12, 'Ke kaloku, ngaye nApolo umzalwana, ndamvuselela kakhulu, ukuba eze kuni kunye nabazalwana. Kuye akubangakho kanye ukuthanda ukuza okwakalokunje; woza ke, xa athe wanethuba elilungileyo.', 'XHO22'),
(47, 16, 13, 'Phaphani, yimani elukholweni; yibani ngamadoda; yomelelani.', 'XHO22'),
(47, 16, 14, 'Zonke izinto zenu mazenziwe ngothando.', 'XHO22'),
(47, 16, 15, 'Ndiyanivuselela ke nina, bazalwana, niyayazi indlu kaStefana, ukuba iyintlahlela yelaseAkaya, yazinikela ekulungiseleleni abangcwele;', 'XHO22'),
(47, 16, 16, 'ukuba nibalulamele abanjalo, nakubo bonke abasincedayo nababulalekayo.', 'XHO22'),
(47, 16, 17, 'Ndiyavuya ke ngokufika kukaStefana noFortunato noAkayiko; ngokuba bakwenzelelele bona ukungabikho kwenu;', 'XHO22'),
(47, 16, 18, 'Kuba bawuphumzile umoya wam nowenu. Bazini kakuhle ngoko abanjalo.', 'XHO22'),
(47, 16, 19, 'Ayanibulisa amabandla aseAsiya. Bayanibulisa kunene eNkosini uAkwila noPrisila, kunye nebandla elisendlwini yabo.', 'XHO22'),
(47, 16, 20, 'Bayanibulisa abazalwana bonke. Bulisanani ngolwango olungcwele.', 'XHO22'),
(47, 16, 21, 'Umbuliso ungesam isandla, mna Pawulos.', 'XHO22'),
(47, 16, 22, 'Ukuba ubani akayithandi iNkosi uYesu Kristu, makasingelwe phantsi.', 'XHO22'),
(47, 16, 23, 'Ubabalo lweNkosi yethu uYesu Kristu malube nani.', 'XHO22'),
(47, 16, 24, 'Uthando lwam malube nani nonke bakuKristu Yesu. Amen. (Incwadi yokuqala eya kwabaseKorinte yabhalwa nguStefano noFortunato noAkayiko noTimoti eFilipi.)', 'XHO22'),
(48, 1, 1, 'uPawulos, umpostile kaYesu Kristu ngokuthanda kukaThixo, noTimoti umzalwana, ubhalela ibandla likaThixo eliseKorinte, ndawonye nabo bonke abangcwele abakulo lonke elaseAkaya;', 'XHO22'),
(48, 1, 2, 'bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(48, 1, 3, 'Makabongwe uThixo, uYise weNkosi yethu uYesu Krestu, uYise onenceba, uThixo wentuthuzelo yonke,', 'XHO22'),
(48, 1, 4, 'osithuthuzelayo kuyo yonke imbandezelo yethu, ukuze sibe nako ukubathuthuzela abakwinto yonke eyimbandezelo, ngayo intuthuzelo esithuthuzelwa ngayo nathi nguye uThixo.', 'XHO22'),
(48, 1, 5, 'Kaloku, njengokuba iintlungu zikaKristu ziphuphumela kuthi, ngokunjalo ithi nentuthuzelo yethu iphuphume ngoKristu.', 'XHO22'),
(48, 1, 6, 'Ukuba ke siyabandezelwa, oko kungenxa yentuthuzelo yenu nosindiso lona olusebenzayo, ukuze ninyamezele kwaezo ntlungu sizivayo nathi;', 'XHO22'),
(48, 1, 7, 'Kanjalo ithemba lethu ngani liqinisekile; sisazi ukuba, njengokuba ningamadlelane nathi ngazo iintlungu, ngokunjalo niyakuba ngawo nangentuthuzelo.', 'XHO22'),
(48, 1, 8, 'Kuba asithandi, bazalwana, ukuba ningayazi, imbandezelo yethu eyasihlelayo kwelaseAsiya: ukuba saxineka ngokuncamisileyo ngokomelela, ngokokude sibuncame kanye nobomi.', 'XHO22'),
(48, 1, 9, 'Ke kaloku besinesigwebo sokufa ngokwethu, ukuze singakholosi ngokwethu, sithembele kuThixo ovusa abafileyo;', 'XHO22'),
(48, 1, 10, 'owasihlangulayo ekufeni okungaka, ohlangulayo; esithembele ngaye ukuba usaya kusihlangula;', 'XHO22'),
(48, 1, 11, 'Nani nincedisana nani ngokusikhungela, ukuze isipho esingokubabalwa, esenzelwe sona thina ngabantu abaninzi, sibulelwe ngabaninzi ngenxa yethu.', 'XHO22'),
(48, 1, 12, 'Kuba oko kuqhayisa kwethu kukungqina kwesazela sethu, ukuba sithe ehlabathini apha, ngokukodwa ke kuni, sahlala singabangenakumbi, sinokungcwengeka okuvela kuThixo, singenabulumko bubobenyama, sinobabalo lukaThixo.', 'XHO22'),
(48, 1, 13, 'Kuba asinibhaleli zimbi izinto, ikwazezo nizilesayo, nizaziyo kakuhle; Ndiyathemba ke ukuba niya kuvuma kude kuse ekupheleni;', 'XHO22'),
(48, 1, 14, 'njengokuba nasaziyo kakuhle ngokuyinxenye, ukuba siliqhayiya lenu, kwanjengokuba nani nilelethu ngemini yeNkosi uYesu.', 'XHO22'),
(48, 1, 15, 'Ke kaloku ndinaloo nkoloseko ke, ndanqwenela ukunga ndingeza kuni kuqala, ukuze nibabalwe okwesibini;', 'XHO22'),
(48, 1, 16, 'ndize ndicande kuni ukuya kwelaseMakedoni, ndibuye, ndivela kwelaseMakedoni, ndize kuni, ndiphelekelelwe nini ukuya kwelakwaYuda.', 'XHO22'),
(48, 1, 17, 'Xa ngoko ndandifuna oko, ndandifeketha na kanti? Okanye izinto endizicebayo, ndiziceba ngokwenyama yini na ukuze nje kubekho kum nje, ukuba ube nguewe, ewe; nohayi, hayi?', 'XHO22'),
(48, 1, 18, 'Ethembekile nje ke uThixo, ilizwi lethu kuni alibanga nguewe nohayi.', 'XHO22'),
(48, 1, 19, 'Kuba yena okaThixo uNyana, uYesu Kristu, owavakaliswayo phakathi kwenu sithi (ndim ke noSilvano noTimoti), akabanga nguewe nohayi; kuye nguewe kwakanye.', 'XHO22'),
(48, 1, 20, 'Kuye onke amadinga kaThixo anguewe, ukuye uAmen, ukuze kuzukiswe uThixo ngathi.', 'XHO22'),
(48, 1, 21, 'Osiqiniselayo ke ndawonye nani kuKristu, osithambisayo ke, nguThixo;', 'XHO22'),
(48, 1, 22, 'ositywine wasiqinisa, wasinika isinyaniso soMoya, saba sezintliziyweni zethu.', 'XHO22'),
(48, 1, 23, 'Ke mna ndibiza uThixo, ukuba abe lingqina emphefumlweni wam, ukuba kungokunivela, endithe ndaba andikezi eKorinte.', 'XHO22'),
(48, 1, 24, 'Anditsho ngakulugagamela ukholo lwenu; nditsho ngokuba singabasebenza nani uvuyo lwenu; kuba nimi ngokholo.', 'XHO22'),
(48, 2, 1, 'Mna ke okwam, ndagqiba kweli lokuba ndingabuyi ndize kuni ndibuhlungu.', 'XHO22'),
(48, 2, 2, 'Kuba mna ukuba ndinenza buhlungu, kusekho bani na ke ondenza ndibe nemihlali, ongenguye lowo wenziwe wabuhlungu kwandim?', 'XHO22'),
(48, 2, 3, 'Ndanibhalela ke oku, ukuze ndakufika ndingabi buhlungu ngabo bendimelwe kukuvuya ngabo; ndikholose ngani nonke, ukuba uvuyo lwam ikwalolwenu nonke.', 'XHO22'),
(48, 2, 4, 'Kuba ndanibhalela ekubandezelekeni nasekuxinezelekeni okukhulu kwentliziyo yam, ndineenyembezi ezininzi; ndingenzeli ukuze nenziwe buhlungu, ndisenzela ukuze nilwazi uthando endinithanda ngalo ngokugqithiseleyo.', 'XHO22'),
(48, 2, 5, 'Ke ukuba ubani wenze kwabuhlungu, akenze kwabuhlungu mna, wenze ngenxenye nina nonke;', 'XHO22'),
(48, 2, 6, 'Kumanele lowo unjalo oko kumkhalimela kwabaninzi;', 'XHO22'),
(48, 2, 7, 'Ngoko ke senithi kanye senimxolela, nimthuthuzele, hleze lowo unjalo aselesuka edliwa kukuba buhlungu ngokugqithiseleyo.', 'XHO22'),
(48, 2, 8, 'Kungoko ndiyaniyala ukuba niluqinisekise uthando lwenu kuye.', 'XHO22'),
(48, 2, 9, 'Kuba ndabhalela kona oku, ukuze ndikwazi ukucikideka kwenu, ukuba ningábevayo na ezintweni zonke.', 'XHO22'),
(48, 2, 10, 'Enimxolelayo ke into, ndiyamxolela nam;', 'XHO22'),
(48, 2, 11, 'hleze asidleke uSathana, kuba amaqhinga akhe siyawazi.', 'XHO22'),
(48, 2, 12, 'Ke kaloku, ndakuba ndifikile eTrowa, ndishumayela iindaba ezilungileyo zikaKristu, ndakuba ndivulelwe nomnyango weNkosi;', 'XHO22'),
(48, 2, 13, 'Andibanga nakonwaba emoyeni wam, ngokungamfumani uTito, umzalwana wam; ndesuka ndababulisa, ndemka ndaya kwelaseMakedoni.', 'XHO22'),
(48, 2, 14, 'Makubulelwe ke kuThixo, ohlala esiphuphuma uqhayiso ngoKristu, elibonakalalisa ngathi ivumba lokwaziwa kwakhe ezindaweni zonke.', 'XHO22'),
(48, 2, 15, 'Kuba kuye uThixo silivumba elimnandi likaKristu, phakathi kwabasindiswayo naphakathi kwabatshabalalayo.', 'XHO22'),
(48, 2, 16, 'Kwabanye silivumba lokufa elisa ekufeni; kwabanye ilivumba lobomi elisa ebomini. Ngubani na ke ozilingeneyo ezo zinto?', 'XHO22'),
(48, 2, 17, 'Kaloku asithi, njengabaninzi abo, silingxengelele ilizwi likaThixo; sisuka ngokwabokungcwengeka, sisuka ngokwabakwaThixo, sithethe phambi kwamehlo kaThixo, sikuKristu.', 'XHO22'),
(48, 3, 1, 'Xa sitshoyo, singaba siyawaqala siziyaleze na? Okanye sifuna, njengabathile, kusini na, iincwadi zokuyalezwa kuni, nokuba zezokuyalezwa nini?', 'XHO22'),
(48, 3, 2, 'Incwadi yethu thina, ebhalwe ezintliziyweni zethu, nini; iyaziwa, ileswa ngabantu bonke;', 'XHO22'),
(48, 3, 3, 'Nibonakalalisiwe nje ukuba niyincwadi kaKristu, eyalungiselelwa sithi, engabhalwanga ngainki, ebhalwe ngoMoya kaThixo ophilileyo; kungekhona kumacwecwe amatye, kusemacwecweni angawenyama entliziyo.', 'XHO22'),
(48, 3, 4, 'Sinenkoloseko enjalo ke kuThixo ngaye uKristu.', 'XHO22'),
(48, 3, 5, 'Kungekuko ukuba silingene ngokwethu ukucamanga nto engathi iphuma kuthi ngokwethu; ukukulingana kwethu oko kuphuma kuThixo;', 'XHO22'),
(48, 3, 6, 'osenze nokusenza sakulingana ukuba ngabalungiseleli bomnqophiso omtsha; asingowombhalo lo, singawomoya; kuba ubhalo luyabulala, umoya ke udlisa ubomi.', 'XHO22'),
(48, 3, 7, 'Ke ukuba ulungiselelo lokufa, lukrolwe ematyeni ngokrolo, lwaba nobuqaqawuli, ngokokude oonyana bakaSirayeli bangabi nakuqwalasela ebusweni bukaMoses, ngenxa yobuqaqawuli bobuso bakhe; uzuko olo lwaluza kubhangiswa.', 'XHO22'),
(48, 3, 8, 'Luthini na ulungiselelo lwakhe uMoya, ukungabi nobuqaqawuli ngokugqithiseleyo?', 'XHO22'),
(48, 3, 9, 'Kuba, ukuba ulungiselelo olugwebayo lunobuqaqawuli, kokukhona luya kugqithisela ngobuqaqawuli ulungiselelo lobulungisa.', 'XHO22'),
(48, 3, 10, 'Kaloku kwanoko kwenziwe kwaba nobuqaqawuli, akwenziwanga kwaba nobuqaqawuli ngale ndawo, ngenxa ke yobuqaqawuli obu bona bubalaselayo.', 'XHO22'),
(48, 3, 11, 'Kuba xa oko bekuza kubhanga kunobuqaqawuli, kokukhona oku kuhleliyo kunobuqaqawuli.', 'XHO22'),
(48, 3, 12, 'Sinethemba elinjalo nje ke ngoko, kokukhona singafihlisiyo ukuthetha;', 'XHO22'),
(48, 3, 13, 'singabi njengoMoses, wabeka isigqubuthelo ebusweni bakhe, ukuze oonyana bakaSirayeli bangaqwalaseli esiphelweni soko bekuza kubhanga.', 'XHO22'),
(48, 3, 14, 'Zesuka noko zaqaqadekiswa iingqiqo zabo, kuba nanamhla sisahleli kwaeso sigqubuthelo ekulesweni kwawo umnqophiso omdala, singabhenqwanga, isigqubuthelo eso sibhangiswa kuKristu.', 'XHO22'),
(48, 3, 15, 'Kude kube namhla, xa kufundwa uMoses, kusuke kubekho isigqubuthelo entliziyweni yabo.', 'XHO22'),
(48, 3, 16, 'Ke xenikweni ithe ke yona yabuyela eNkosini sosuswa kuphele isigqubuthelo eso.', 'XHO22'),
(48, 3, 17, 'iNkosi ke inguye uMoya; apho ke akhoyo uMoya weNkosi, kubakho inkululeko.', 'XHO22'),
(48, 3, 18, 'Ke thina sonke, sinobuso nje obubhenqwe isigqubuthelo, sibukhangela njengokwasesipilini ubuqaqawuli beNkosi, senziwa similise okwaloo mfanekiselo, sivela ebuqaqawulini sisinge ebuqaqawulini, kwanjengokuvela eNkosini enguMoya.', 'XHO22'),
(48, 4, 1, 'Ngoko ke, sinalo nje olu lungiselelo, sithi, njengoko senzelwe inceba, singethi mandla;', 'XHO22'),
(48, 4, 2, 'sisuke sazilahla izinto ezifihlakeleyo eziziintloni, singahambi ngobuqhetseba, singalikhohlakalisi ilizwi likaThixo; sisuka ngokuyibonakalalisa inyaniso siziyaleze kuso sonke isazela sabantu emehlweni kaThixo.', 'XHO22'),
(48, 4, 3, 'Kodwa ke ukuba iindaba ezimnandi esizishumayelayo azicacanga, koba zifihlakele kwabatshabalalayo.', 'XHO22'),
(48, 4, 4, 'Athi kubo uthixo weli phakade wazimfamekisa ngaphakathi kwabo iingqiqo zabangakholwayo, ukuze singabengezeli kubo isikhanyiso seendaba ezilungileyo zobuqaqawuli bukaKristu, ongumfanekiselo kaThixo.', 'XHO22'),
(48, 4, 5, 'Kaloku asizishumayeli thina, sivakalisa uKrestu Yesu ukuba uyiNkosi; sibe ke thina singabakhonzi benu ngenxa kaYesu.', 'XHO22'),
(48, 4, 6, 'Ngokuba nguThixo, yena wathi ukhanyiso malukhanye ebumnyameni, okhanyisele iintliziyo zethu, ukuze kukhanye ukwaziwa kobuqaqawuli bukaThixo ebusweni bukaYesu Kristu.', 'XHO22'),
(48, 4, 7, 'Ke kaloku obu butyebi sibuphethe ngezitya zodongwe, ukuze incamisa yamandla ibe yekaThixo, ingaphumi kuthi.', 'XHO22'),
(48, 4, 8, 'Siyabandezelwa ngeenxa zonke, singaxineki; siyathingaza, kodwa asincami;', 'XHO22'),
(48, 4, 9, 'siyatshutshiswa, kodwa asishiywa; sikhahlelwa phantsi, asitshatyalaliswa;', 'XHO22'),
(48, 4, 10, 'sihlala sikuthwele ngomzimba ukufa kweNkosi uYesu ukuze nobomi bukaYesu bubonakalaliselwe emizimbeni yethu.', 'XHO22'),
(48, 4, 11, 'Kuba thina badla ubomi simana sinikelwa ekufeni ngenxa kaYesu, ukuze nobomi bukaYesu bubonakalaliselwe enyameni yethu enokufa.', 'XHO22'),
(48, 4, 12, 'Ngoko ke kusebenza ukufa kuthi, ke kuni kusebenza ubomi.', 'XHO22'),
(48, 4, 13, 'Sinawo nje ke kwaloo moya wokholo, ngokoko kubhaliweyo kwathiwa, Ndakholwa, kungoko ndathethayo; nathi ke siyakholwa, kungoko sithethayo nokuthetha;', 'XHO22'),
(48, 4, 14, 'sisazi ukuba lowo wayivusayo iNkosi uYesu, uya kusivusa nathi ngoYesu, asimise phambi kwakhe, ndawonye nani.', 'XHO22'),
(48, 4, 15, 'Kuba konke oko kungenxa yenu, ukuze luthi ubabalo, lwakuba lwandile ngabona baninzi, luwuphuphumise umbulelo, use ekuzukiseni uThixo.', 'XHO22'),
(48, 4, 16, 'Kungoko singethi mandla; kuba nokuba umntu wethu wangaphandle uya esonakala, usuka yena owangaphakathi aye ehlaziyeka imihla ngemihla.', 'XHO22'),
(48, 4, 17, 'Kaloku imbandezelo yethu, eyeyomzuzwana, isisebenzela ubunzima obuncamisileyo, obuncamisileyo.', 'XHO22'),
(48, 4, 18, 'Kaloku thina asixuneli ezintweni ezibonwayo, sixunela kwezingabonwayo; kuba ezibonwayo zezexeshana; ke zona ezingabonwayo zezonaphakade.', 'XHO22'),
(48, 5, 1, 'Siyazi ke ukuba xa ithe yachithwa indlu yethu esemhlabeni, engulo mnquba, sinesakhiwo esiphuma kuThixo, yona emazulwini, yona engenakwenziwa ngazandla, engunaphakade.', 'XHO22'),
(48, 5, 2, 'Kuba kulo siyancwina, silangazelela ukwambatha, saleke indlu yethu yasemazulwini;', 'XHO22'),
(48, 5, 3, 'ukuba siya kufunyanwa sambethe okunene, singeze.', 'XHO22'),
(48, 5, 4, 'Kuba kwathina aba bakulo mnquba siyancwina sisindwa, kungengakuba sithanda ukuhlutywa;', 'XHO22'),
(48, 5, 5, 'Ke kaloku lo usisebenzele kwaloo nto nguThixo, osinike nokusinika isinyaniso sakhe uMoya.', 'XHO22'),
(48, 5, 6, 'Ngoko ke sihlala somelele, sisazi ukuba, sikowethu nje emzimbeni, sikude kowethu eNkosini.', 'XHO22'),
(48, 5, 7, '(Kuba sihamba ngokholo, asihambi ngokubona;)', 'XHO22'),
(48, 5, 8, 'Singaxhalaba, ndithi ke, síthanda kanye ukuba kude nenyama yethu, siye kuhlala neNkosi.', 'XHO22'),
(48, 5, 9, 'Kungoko sizondelela ukuthi, nokuba sisekhaya, nokuba sikude nekhaya, sibe ngabakholekileyo kuyo.', 'XHO22'),
(48, 5, 10, 'Kuba sonke simele sibonakale phambi kwesihlalo sokugweba sikaKristu; ukuze elowo azuziswe okwenziwe ngomzimba, ngoko akwenzileyo, nokuba kokulungileyo, nokuba kokubi.', 'XHO22'),
(48, 5, 11, 'Sikwazi njalo ke ukoyikeka kweNkosi, sizamela ukuba beyiseke abantu; sibonakalalisiwe ke kuThixo; ndiyathemba ke ukuba nakuzo izazela zenu sibonakalalisiwe.', 'XHO22'),
(48, 5, 12, 'Kuba asibuyi siziyaleze kuni;', 'XHO22'),
(48, 5, 13, 'Kuba, nokuba siyageza, kungenxa kaThixo; nokuba sinengqondo ephilileyo, kungenxa yenu.', 'XHO22'),
(48, 5, 14, 'Kaloku uthando lukaKristu lusifingile nje; Kuba sigqiba kwelithi, omnye ufele bonke emnye, ngoko bafile bonke abo;', 'XHO22'),
(48, 5, 15, 'wabafela ke bonke, ukuze abo badla ubomi bangabi sazidlela ubomi, babudlele owafayo wavuka ngenxa yabo.', 'XHO22'),
(48, 5, 16, 'Ngoko ke thina sisusela kweli xesha, asisazi namnye ngokwenyama; nokuba ke besimazi uKristu ngokwenyama, kungoku asisamazi ngokwenyama.', 'XHO22'),
(48, 5, 17, 'Ngoko ke, ukuba ubani ukuKristu, usisidalwa esitsha; izinto ezindala zidlule; yabonani, zisuke zaba ntsha izinto zonke.', 'XHO22'),
(48, 5, 18, 'Zonke izinto ke ziphuma kuThixo, owasixolelanisa naye ngokwakhe ngoYesu Kristu, wasinika ulungiselelo loxolelaniso;', 'XHO22'),
(48, 5, 19, 'Oko ke, uThixo waye ekuKristu, elixolelanisa naye ngokwakhe ihlabathi, engazibaleli kubo iziphoso zabo; yaye uphathise kuthi ilizwi loxolelaniso.', 'XHO22'),
(48, 5, 20, 'Sizizigidimi ke ngoko ngenxa kaKristu, ngokungathi uThixo uniyala ngathi; siyanikhunga ngenxa kaKristu, manixolelaniswe naye uThixo;', 'XHO22'),
(48, 5, 21, 'kuba lowo ungazani nasono, wamenza isono ngenxa yethu; ukuze thina sibe bubulungisa bukaThixo kuye.', 'XHO22'),
(48, 6, 1, 'Sisebenzisana naye nje ke, siyaniyala, ukuba ubabalo lukaThixo ningafumane nilwamkele;', 'XHO22'),
(48, 6, 2, 'Kuba uthi, Ndakuva ngexesha elamkelekileyo, ndakusiza ngomhla wosindiso; yabonani, lingoku ixesha elamkeleke kakuhle; yabonani, ungoku umhla wosindiso:', 'XHO22'),
(48, 6, 3, 'singabakhubekisi nanye into, ukuze ubulungiseleli bungabi nakubekwa bala;', 'XHO22'),
(48, 6, 4, 'sithi ke, ezintweni zonke sibonakalalisa njengabalungiseleli bakaThixo, sinokuzeka kade umsindo, sibandezelekile, sisezingxalweni, sibandezelekile;', 'XHO22'),
(48, 6, 5, 'Ngokuba ngemivumbo, ngokuba sezintolongweni, nasezidubedukeni, ngokubulaleka, ekuphandeni, ngokuzila ukudla;', 'XHO22'),
(48, 6, 6, 'ngobunyulu, ngolwazi, ukuzeka kade umsindo, ngobubele, ngoMoya oyiNgcwele, naseluthandweni olungenaluhanahaniso,', 'XHO22'),
(48, 6, 7, 'Ngelizwi lenyaniso, ngamandla kaThixo, ngesikrweqe sobulungisa ngasekunene nangasekhohlo;', 'XHO22'),
(48, 6, 8, 'sikuzuko nakwintswela-mbeko, sikudumo olubi nakudumo oluhle; njengabakhohlisi, ukanti siyinyaniso;', 'XHO22'),
(48, 6, 9, 'ngathi singabangaziwayo, ukanti saziwa kakuhle; ngathi singabafileyo, nanku sidla ubomi; singabohlwaywa, singabulawa;', 'XHO22'),
(48, 6, 10, 'ngathi sibuhlungu, ukanti sihlala sivuya; ngathi singamahlwempu, ukanti singabatyebisa abaninzi; ngokungathi singenanto, ukanti sinezinto zonke.', 'XHO22'),
(48, 6, 11, 'Umlomo wethu uvulekile kuni, baseKorinte; intliziyo yethu yenziwe yabanzi.', 'XHO22'),
(48, 6, 12, 'Anibandezanga nto kuthi, nibandezelekile ezibilini zenu.', 'XHO22'),
(48, 6, 13, 'Umbuyekezo ke lowo (ndithetha njengakubantwana bam), ndithi yibani banzi nani.', 'XHO22'),
(48, 6, 14, 'Musani ukunxulumana nabolunye uhlobo, abangakholwayo ke; kuba kunakwabelana kuni na ubulungisa nokuchasa umthetho? Kunabudlelane buni na ke ukukhanya nobumnyama?', 'XHO22'),
(48, 6, 15, 'Unakuvumelana kuni na ke uKristu noBheliyali? Unasabelo sini na ke okholwayo kunye nongakholwayo?', 'XHO22'),
(48, 6, 16, 'Inakudibana kuni na ke itempile kaThixo nezithixo? Kuba nina niyitempile kaThixo ophilileyo; njengokuba watshoyo uThixo ukuthi, Ndiya kuhlala phakathi kwabo, ndihambe phakathi kwabo; ndibe nguThixo wabo, bona babe ngabantu bam.', 'XHO22'),
(48, 6, 17, 'Kungoko ndithi, Phumani phakathi kwabo, nizahlule, itsho iNkosi, nento engcolileyo ningayichukumisi; kwaye ndiya kunamkela;', 'XHO22'),
(48, 6, 18, 'Ndiya kuba nguyihlo, nina nibe ngoonyana neentombi kum; itsho iNkosi uSomandla.', 'XHO22'),
(48, 7, 1, 'Sinawo nje ngoko la madinga, zintanda, masizihlambulule kuko konke ukudyobheka kwenyama nokomoya sibufeza ubungcwele, sisoyika uThixo.', 'XHO22'),
(48, 7, 2, 'Samkeleni; asonanga mntu, asonakalisanga mntu, asonakalisanga mntu.', 'XHO22'),
(48, 7, 3, 'Anditsho ngakunigweba; kuba sendithe ngaphambili, nisezintliziyweni zethu ukuba sife nani, sidle ubomi nani.', 'XHO22'),
(48, 7, 4, 'Ndinokungafihlisi okukhulu kuni, ukuzingca kwam ngani; ndizele yintuthuzelo, ndinovuyo olugqithileyo kuyo yonke imbandezelo yethu.', 'XHO22'),
(48, 7, 5, 'Kuba sithe sakufika kwelaseMakedoni, ayaba nakuphumla nakanye inyama yethu; sabandezelwa ngeenxa zonke; ngaphandle ikukulwa, ngaphakathi kukoyika.', 'XHO22'),
(48, 7, 6, 'Kodwa ke uThixo, obathuthuzelayo abathobekileyo, wasithuthuzela ngokufika kukaTito;', 'XHO22'),
(48, 7, 7, 'akwaba ngakufika kwakhe ke kuphela, kwaba ngayo intuthuzelo awathuthuzeleka ngayo kuni, esityela ulangazelelo lwenu, isijwili senu, nokuzohlwaya kwenu ngam; kangangokuba ndaba nemihlali ngakumbi.', 'XHO22'),
(48, 7, 8, 'Kuba nakuba ndikhe ndanenza buhlungu ngencwadi leyo, andizohlwayi, nakuba ndikhe ndazohlwaya;', 'XHO22'),
(48, 7, 9, 'Ngoku ke ndiyavuya, ndingavuyeli kuba neenziwa buhlungu, ndivuyela ukuba nenziwa buhlungu ngokwasinga enguqukweni;', 'XHO22'),
(48, 7, 10, 'Kaloku ukuba buhlungu ngokukaThixo kusebenza inguquko esingisa elusindisweni, ekungekho kuzohlwaya ngayo; ke kona okwehlabathi ukuba buhlungu kusebenza ukufa.', 'XHO22'),
(48, 7, 11, 'Kuba, yabonani kwaezo zinto, ukuba nenziwa buhlungu ngokokukaThixo, kwathi ukunyameka okungako kuni, kwanenzondelelo kuni; yimpindezelo enjani! Ezintweni zonke niziqondakalisile, ukuba nimsulwa kulo mcimbi.', 'XHO22'),
(48, 7, 12, 'Ngoko ke, noko ndanibhalelayo, andikwenzanga oko ngenxa yalowo wamonayo omnye, kanjalo andikwenzanga ngenxa yalowo woniwayo; ndakwenzela ukuze kubonakalaliswe kuni ukuninyamekela kwethu emehlweni kaThixo.', 'XHO22'),
(48, 7, 13, 'Ngoko ke, sithuthuzelekile ngenxa yokuthuthuzeleka kwenu; saza savuya kakhulu ngakumbi ngokuvuya kukaTito; ngokuba waphunyuzwayo umoya wakhe nini nonke.', 'XHO22'),
(48, 7, 14, 'Ngokuba ukuqhayisa endaqhayisa ngako ngani, andidaniswanga ngako; njengokuba sazithetha kuni izinto zonke sinyanisile, ngokunjalo nokuqhayisa kwethu oko phambi koTito kwaba yinyaniso.', 'XHO22'),
(48, 7, 15, 'Imfesane yakhe isenkulu ngakuni ngokugqithiseleyo, akukhumbula ukumva kwenu nonke nokokuba namamkelayo ninokoyika nokugubha.', 'XHO22'),
(48, 7, 16, 'Ndiyavuya ngoko, kuba ndomelezekile kuni ezintweni zonke.', 'XHO22'),
(48, 8, 1, 'Ke kaloku, bazalwana, siyanazisa ubabalo lukaThixo oluhlakaziweyo phakathi kwamabandla akwelaseMakedoni;', 'XHO22'),
(48, 8, 2, 'okokuba ekucikidweni kwawo okukhulu yimbandezelo, kuthe ukuphuphuma kovuyo lwawo, nobuhlwempu bawo obendeleyo, kwaphuphumela ekutyebiseni ukunyanisa kwawo.', 'XHO22'),
(48, 8, 3, 'Kuba ndiyangqina, ewe, nangaphezu kwamandla abo, bazinikela ngokuqhutywa yintliziyo;', 'XHO22'),
(48, 8, 4, 'besikhunga kakhulu, besikhunga, ukuba silwamkele ubabalo olo, nobudlelane ke obo bokulungiselela abangcwele.', 'XHO22'),
(48, 8, 5, 'Abakwenza njengoko sathemba ngako, basuka bazinikela eNkosini, nakuthi, ngokuthanda kukaThixo;', 'XHO22'),
(48, 8, 6, 'ngokokude simyale uTito, ukuba athi, njengokuba ebeqalile, alufezele kuni ngokunjalo nolo lubabalo.', 'XHO22'),
(48, 8, 7, 'Ke, kwanjengokuba niphuphuma iinto zonke, ukholo, nokuthetha, nokwazi, nokunyameka konke, nokusithanda kwenu, maniphuphume nakulo olu lubabalo.', 'XHO22'),
(48, 8, 8, 'Anditsho ngakwammiselo; ukutsho, ndenziwa kukunyameka kwabanye, ndicikida nenyaniso yolwenu uthando;', 'XHO22'),
(48, 8, 9, 'Kuba niyalwazi ubabalo lweNkosi yethu uYesu Kristu, okokuba yathi, ibisisityebi nje, yaba lihlwempu ngenxa yenu, ukuze nina nibe zizityebi ngobuhlwempu bayo obo.', 'XHO22'),
(48, 8, 10, 'Ngale ndawo ke ndinipha okwam ukubona; kuba oko kunilungele nina aba nayiqalayo kwanyakenye le nto; ananela kuyenza, nayithanda nokuyithanda.', 'XHO22'),
(48, 8, 11, 'Ngoko ke yifezekiseni le nto; ukuze, kwanjengokuba bekukho intumekelelo yokuthanda, kwangokunjalo kubekho nokuyifeza ngeninako.', 'XHO22'),
(48, 8, 12, 'Kuba intumekelelo, ukuba selikho, yamkeleka kakuhle ngokoko asukuba enako umntu, kungabi ngokoko angenako.', 'XHO22'),
(48, 8, 13, 'Kuba andithi mabaphumle abanye abantu, nibandezeleke nina;', 'XHO22'),
(48, 8, 14, 'ukuze kubekho ukulingana ngexesha lakalokunje, ukuphuphuma kwenu makufikelele ekusileleni kwabo, ukuze nokuphuphuma kwabo kufikelele ekusileleni kwenu;', 'XHO22'),
(48, 8, 15, 'njengokuba kubhaliwe kwathiwa, Lowo wabutha kakhulu akabanga nakusalelayo; nowayebutha kancinane akabanga nakusilela.', 'XHO22'),
(48, 8, 16, 'Makubulelwe ke kuThixo, ongenise kwaoko kuninyamekela entliziyweni kaTito.', 'XHO22'),
(48, 8, 17, 'Ngokuba wasamkela okunene isiyalo; ke uthe, enyamekile, uthe ngokuqhutywa yintliziyo, waya kuni.', 'XHO22'),
(48, 8, 18, 'Sathuma ke kunye naye umzalwana odume phakathi kwawo onke amabandla, ngokuvakalisa iindaba ezilungileyo.', 'XHO22'),
(48, 8, 19, 'Asiyikuphela ke loo nto; wanyulwa nokunyulwa ngawo amabandla, ukuba abe ngohamba nathi ngolu lubabalo silulungiselelweyo sithi, ekuzukiseni kwayo iNkosi, nasekubonakalaliseni nina ngokwentumekelelo yenu;', 'XHO22'),
(48, 8, 20, 'silumkele oku, ukuze kungabikho bani usigxekayo kobu bu butyebi bulungiselelwa sithi;', 'XHO22'),
(48, 8, 21, 'Silungiselela izinto ezilungileyo, kungekuphela nje emehlweni eNkosi, kodwa nasemehlweni abantu.', 'XHO22'),
(48, 8, 22, 'Sithume ke kunye nabo umzalwana wethu, esimcikide futhi ngeendawo ezininzi, samfumana ekhuthele;', 'XHO22'),
(48, 8, 23, 'Nokuba kuthethwa ntoni ngoTito, yazini ukuba ulidlelane nam, nomsebenzisi wam kuni; nokuba kuthethwa ngabazalwana bethu, yazini ukuba bangabathunywa bamabandla kaThixo, baluzuko lukaKristu.', 'XHO22'),
(48, 8, 24, 'Lo mbonakalaliso ngoko wothando lwenu, nowokuzingca kwethu ngani, wubonakalaliseni kubo, naphambi kwamabandla.', 'XHO22'),
(48, 9, 1, 'Kuba okunene ngako ukulungiselela abangcwele, okunene akufuneki ukuba ndinibhalele;', 'XHO22'),
(48, 9, 2, 'Kuba ndiyakwazi ukuthumekelela kwenu, endiqhayisa ngako ngani kwabelaseMakedoni, ndisithi elaseAkaya beliselilunge kwanyakenye; nokuzondelela kwenu kwaxhokonxa abaninzi.', 'XHO22'),
(48, 9, 3, 'Ke kaloku ndithume abazalwana, ukuze iqhayiya lethu ngani lingalanjathiswa ngale ndawo; ukuze nithi, njengokuba benditshilo, nibe senilungile;', 'XHO22'),
(48, 9, 4, 'hleze kuthi, ukuba bathe bafika nam abelaseMakedoni, banifumana ningekalungi, sidaniswe thina (ukuba asingethi, kudane nina) koko kukholosa kokuqhayisa.', 'XHO22'),
(48, 9, 5, 'Ngoko ke ndibe kuyafuneka ukubavuselela abazalwana, ukuba baqale beze kuni, bayilungise ngaphambili isisa senu, enisixelelwe ngenxa engaphambili, ukuze sibe silungile, sibe sinjengesibabalo, singabi sesokubawa.', 'XHO22'),
(48, 9, 6, 'Nditsho ke ukuthi, Lowo uhlwayela ngokuvimba wovuna kwangokuvimba; nalowo uhlwayela ngesisa wovuna kwangesisa.', 'XHO22'),
(48, 9, 7, 'Elowo makanike njengoko agqibe ngako entliziyweni; kungabi ngokuphuma kabuhlungu, nangokunyanzelwa; kuba uThixo uthanda umphi ochwayithileyo.', 'XHO22'),
(48, 9, 8, 'Unako ke uThixo ukuphuphumisela kuni ubabalo lonke; ukuze nithi, nihlala ninako nje ukwanela konke ngeendawo zonke, niphuphumele emsebenzini wonke olungileyo;', 'XHO22'),
(48, 9, 9, 'njengokuba kubhaliwe kwathiwa, Wasasaza, wapha abasweleyo, ubulungisa bakhe buhlala buhleli.', 'XHO22'),
(48, 9, 10, 'Lowo unika imbewu umhlwayeli wokulungiselela isonka sokudla kwenu, aphinde ayandise imbewu yenu, andise isiqhamo sobulungisa benu;', 'XHO22'),
(48, 9, 11, 'nithi ngeendawo zonke nityetyiselwe ekunyaniseni konke, kona kusebenzayo ngathi umbulelo kuThixo.', 'XHO22'),
(48, 9, 12, 'Ngokuba ulungiselelo lwalo mbuso alwaneli kuzalisela iintsilelo zabangcwele; lusuka kananjalo luphuphume imibulelo emininzi kuye uThixo;', 'XHO22'),
(48, 9, 13, 'besithi nje, ngako ukucikideka kolo lulungiselelo, bamzukise uThixo ngenxa yokulululamela kwenu uvumo lwenu kwiindaba ezilungileyo zikaKristu, nangenxa yokunyanisa kokudlelana kwenu ngokusingisele kubo nakubantu bonke;', 'XHO22'),
(48, 9, 14, 'Ngoku ke benikhungela ngenxa yenu, benilangazelela ngenxa yobabalo lukaThixo oluncamisileyo kuni aba.', 'XHO22'),
(48, 9, 15, 'Makubulelwe ke kuThixo ngenxa yesipho sakhe esingathethekiyo.', 'XHO22'),
(48, 10, 1, 'Ke kaloku, ndiyaniyala ngabo ubulali nokuthantamisa kukaKristu, mna Pawulos ngokwam, ndingothobekileyo kambe phakathi kwenu, ndakuba ndingekho ndiba nobuganga ngakuni;', 'XHO22'),
(48, 10, 2, 'Ndiyakhunga ke, ukuze ndingathi, ndakuba ndikho, ndibe nobugagu ngoko kukholosa, ndiba ndoba nobuganga ngako kubo abathile, ababa sihamba ngokwenyama;', 'XHO22'),
(48, 10, 3, 'Kuba noko sihamba sisenyameni, asiphumi mkhosi ngokwenyama;', 'XHO22'),
(48, 10, 4, '(Kuba izixhobo zethu zemfazwe ayizizo ezenyama, kodwa ngaye uThixo zinamandla okuwisa iinqaba;)', 'XHO22'),
(48, 10, 5, 'siwisa ngazo nje amabhongo, nayo yonke into ephakamileyo, eziphakamisayo ngokuchasa ukwazi uThixo; iingqiqo zonke sizithimbela ekumlulameleni uKristu;', 'XHO22'),
(48, 10, 6, 'sikulungele ukuphindezela konke ukungathobeli, kwakufika ukululama kwenu.', 'XHO22'),
(48, 10, 7, 'Izinto nizikhangela ngokwembonakalo yangaphandle na? Ukuba umntu ukholose ngokuthi ungokaKristu, makabuye acinge oku ngokwakhe, ukuba njengokuba engokaKristu yena, ngokunjalo singabakaKristu thina.', 'XHO22'),
(48, 10, 8, 'Kuba, naxa bendingathi ndigqithisele ukuqhayisa ngalo igunya lethu, esalinikwayo yiNkosi ukuba sinakhe, singanichithi, bendingedaniyo;', 'XHO22'),
(48, 10, 9, 'ukuze ndingangi ndifuna ukunoyikisa ngazo iincwadi ezi;', 'XHO22'),
(48, 10, 10, 'Kuba kuthiwa, iincwadi zakhe zinzima, zinamandla; kodwa ubukho bakhe ngenyama bubuthathaka, nokuthetha kwakhe akunto yanto.', 'XHO22'),
(48, 10, 11, 'Onjalo ke makacinge le nto: njengoko sithetha ngako ngeencwadi, sakuba singekho, sikwanjalo ngawo umsebenzi, sakuba sikho.', 'XHO22'),
(48, 10, 12, 'Kuba asinabuganga bakuzibalela nakuzifanisa nabo abathile abaziyalezayo;', 'XHO22'),
(48, 10, 13, 'Ke thina asisayi kuqhayisa sigqithise emlinganiselweni; soqhayisa ngokomlingani selo womda esiwabelwe nguThixo, ukuba ube ngumlinganiselo oya kufikelela nakuni.', 'XHO22'),
(48, 10, 14, 'Kuba asizoluli ngokugqithiseleyo, ngathi asifikanga kuni;', 'XHO22'),
(48, 10, 15, 'Singaqhayisi sigqithise emlinganiselweni ngokubulaleka kwethu abanye; ke sinethemba, lwakukhula ukholo lwenu, siya kwenziwa bakhulu phakathi kwenu ngokomda wethu, ngokugqithiseleyo;', 'XHO22'),
(48, 10, 16, 'side sizishumayele iindaba ezilungileyo ezindaweni ezingaphaya kwenu; singaqhayisi ngesilinganiselo somnye, ngezinto ke esezilungisiwe.', 'XHO22'),
(48, 10, 17, 'Oqhayisayo ke makaqhayise ngeNkosi.', 'XHO22'),
(48, 10, 18, 'Kaloku oyena ucikidekileyo asingoziyalezayo, ngoyalezwa yiNkosi.', 'XHO22'),
(48, 11, 1, 'Akwaba ningandi nyamezela kancinane ekusweleni kuqonda kwam!', 'XHO22'),
(48, 11, 2, 'Kuba ndiyanikhweletela ngekhwele likaThixo; kuba ndanendisa ndodeni-nye, ukuze ndinimise phambi koKristu niyintombi enyulu.', 'XHO22'),
(48, 11, 3, 'Ndiyoyika ke, hleze kuthi, njengokuba inyoka yamlukuhlayo uEva ngobuqhetseba bayo, zonakaliswe ngokunjalo nani iingqiqo zenu, zimke ekunyaniseni kuye uKristu.', 'XHO22'),
(48, 11, 4, 'Kuba kaloku, xa lowo uzayo avakalisa omnye uYesu, esingamvakalisanga thina, nokuba namkela moya wumbi enaningamkelanga wona, nokuba ziindaba ezilungileyo ezizimbi enaningamkelanga zona, beningaba kambe niyatyapha ukumnyamezela.', 'XHO22'),
(48, 11, 5, 'Kuba ndiba andisilele nganto kwabona bapostile baziincamisa.', 'XHO22'),
(48, 11, 6, 'Nakuba ke ndiliyilo ngentetho, andinjalo ngokwazi; sabonakalaliswa kuni ngeendawo zonke.', 'XHO22'),
(48, 11, 7, 'Ndenza isono na ngokuzithoba, ukuze niphakanyiswe nina, ngokuba ndanishumayezayo ngesisa iindaba ezilungileyo zikaThixo?', 'XHO22'),
(48, 11, 8, 'Amanye amabandla kaThixo ndawabhunyula, ndisamkela umvuzo ukuze ndilungiselele nina.', 'XHO22'),
(48, 11, 9, 'Kanjalo xa ndandikho kuni ndiswele, andibanga mthwalo mntwini; kuba ukuswela kwam bakwenzelela abazalwana, ababevela kwelaseMakedoni; Ndiyazigcina.', 'XHO22'),
(48, 11, 10, 'Ikum nje inyaniso kaKristu, ndithi olo luqhayiso aluyi kuvingcwa mlomo mayela nam, mna, kwimimandla yelaseAkaya.', 'XHO22'),
(48, 11, 11, 'Ngani na? ngokuba ndinganithandi na? uThixo uyazi.', 'XHO22'),
(48, 11, 12, 'Endikwenzayo ke ndiya kukwenza, ukuze ndibahluthe ithuba lokujinda abafuna ithuba; ukuze entweni abaqhayisela ngayo, bafunyanwe bekwanjengathi.', 'XHO22'),
(48, 11, 13, 'Kuba abanjalo ngabapostile ababuxoki, ngabasebenzi abakhohlisayo, bezimilisa okwabapostile bakaKristu.', 'XHO22'),
(48, 11, 14, 'Ke akummangaliso oko; kuba noSathana ngokwakhe uzimilisa okwesithunywa sokukhanya.', 'XHO22'),
(48, 11, 15, 'Ngoko ke akunto inkulu ukuba nabalungiseleli bakhe bazimilise okwabalungiseleli bobulungisa; abasiphelo siya kuba ngokwemisebenzi yabo.', 'XHO22'),
(48, 11, 16, 'Ndibuya ndithi, Makungabikho mntu uba ndingoswele ukuqonda; okanye ke kambe, ndamkeleni njengoswele ukuqonda, ukuze ke ndiqhayise kancinane nam.', 'XHO22'),
(48, 11, 17, 'Oku ndikuthethayo, andikuthethi ngokweNkosi; ndikuthetha ngokoswele ukuqonda, ndinoko kukholosa koqhayiso.', 'XHO22'),
(48, 11, 18, 'Ekubeni bebaninzi ke abaqhayisa ngokwenyama, ndoqhayisa nam.', 'XHO22'),
(48, 11, 19, 'Kaloku niyabanyamezela ngovuyo abaswele ukuqonda, niziingqondi nje nina.', 'XHO22'),
(48, 11, 20, 'Kuba niyanyamezela, ukuba umntu unenza amakhoboka, ukuba umntu uyanidla, ukuba umntu uyanibambisa, ukuba umntu uyaziphakamisa, ukuba umntu uyanibetha ebusweni.', 'XHO22'),
(48, 11, 21, 'Ndithetha ngengcikivo, ngathi siswele amandla thina. Entweni ke asukuba umntu enobuganga (ndithetha ndinokuswela ukuqonda), ndinobuganga nam.', 'XHO22'),
(48, 11, 22, 'BangamaHebhere na? Ndinguye nam. BangamaSirayeli na? nam ndinjalo. Bayimbewu ka-Abraham na? nam kunjalo.', 'XHO22'),
(48, 11, 23, 'Bangabalungiseleli bakaKristu na? (Ndithetha njengesidenge) Ndingaphezulu; ngokubulaleka okugqithiseleyo, ngemivumbo egqithe emgceni, ngokuba sezintolongweni ngokugqithiseleyo, ngokuba sekufeni futhi.', 'XHO22'),
(48, 11, 24, 'KumaYuda ndafumana izihlandlo ezihlanu imivumbo emashumi mane, kwasala omnye.', 'XHO22'),
(48, 11, 25, 'Kukathathu ndibethwa ngeentswazi, okunye ndaxulutywa ngamatye, ndaphukelwa ngumkhombe kathathu, ndenze ubusuku nemini enzulwini;', 'XHO22'),
(48, 11, 26, 'ngokuhamba okufuthi, ngeengozi zamanzi, ngeengozi zezihange, ngeengozi ngabakowethu, ngeengozi ngenxa yeentlanga, ngeengozi phakathi komzi, ngeengozi entlango, ngeengozi elwandle, ngeengozi phakathi kwabazalwana ababuxoki.', 'XHO22'),
(48, 11, 27, 'Ekubulalekeni nasekubulalekeni, ngokuphapha okufuthi, ngokulamba nokunxanwa, ngokuzila ukudla okufuthi, ngengqele nobuze.', 'XHO22'),
(48, 11, 28, 'Ndingasazibali zona izinto ezingaphandle kwezo, ndingungelwe imihla ngemihla kukuwaxhalela onke amabandla kaThixo.', 'XHO22'),
(48, 11, 29, 'Ngubani na okha aswele amandla, ndingasweli mandla nam? Ngubani na okha akhutyekiswe, ndingatshi?', 'XHO22'),
(48, 11, 30, 'Ukuba ndimelwe kukuqhayisa, ndoqhayisa ngezinto zokuswela amandla kwam.', 'XHO22'),
(48, 11, 31, 'uThixo uYise weNkosi yethu uYesu Krestu, ongowokubongwa kude kube ngunaphakade, uyazi ukuba andixoki.', 'XHO22'),
(48, 11, 32, 'eDamasko irhuluneli ephantsi kokumkani uAreta yayiwugcina umzi waseDamasko, ifuna ukundibamba;', 'XHO22'),
(48, 11, 33, 'Ndaza ngekroba ndathotywa ngomnyazi eludongeni, ndasinda ezandleni zakhe.', 'XHO22'),
(48, 12, 1, 'Ukuqhayisa okunene akundilungele; Ndiya kuza kwimibono nezityhilelo zeNkosi.', 'XHO22'),
(48, 12, 2, 'Ndazi umntu okuKristu, ekusekuyiminyaka elishumi elinamine (enoba ubesemzimbeni na andazi, nokuba ubengekho na emzimbeni, andazi; kwaziwa nguThixo), ethe lowo waxwilwa, wada wasiwa nakwelesithathu izulu.', 'XHO22'),
(48, 12, 3, 'Ndamazi ke umntu onjalo (nokuba ubesemzimbeni na, nokuba ubengekho na emzimbeni, andazi; kwaziwa nguThixo);', 'XHO22'),
(48, 12, 4, 'okokuba waxwilwa wasiwa eParadisi, weva amazwi angenakuthethwa, ekungavumelekileyo ukuba umntu awathethe.', 'XHO22'),
(48, 12, 5, 'Onjalo ke ndiya kuqhayisa ngaye, kodwa andiyi kuqhayisa ngokwam nganto ikum; ingaba kungokuswela kwam amandla kodwa.', 'XHO22'),
(48, 12, 6, 'Kuba xa bendingathi ndithande ukuqhayisa, bendingayi kuba ndiswele ukuqonda, kuba bendiya kuba ndithetha inyaniso; kuba ndiya kuthetha inyaniso; kodwa ndiyayeka, ukuze kungabikho bani ucinga ngam ngaphezu koko abona ndikuko, athi mhlawumbi akuve kum.', 'XHO22'),
(48, 12, 7, 'Kwathi, ukuze ndingaziphakamisi ngokungaphezulu ngenxa yezityhilelo eziyincamisa, ndanikwa uviko enyameni, isithunywa sikaSathana, ukuze sindintlithe, ukuze ndingaziphakamisi ngokungaphezulu.', 'XHO22'),
(48, 12, 8, 'Ndayibongoza kathathu iNkosi ngaso isithunywa eso, ukuba sisuke kum.', 'XHO22'),
(48, 12, 9, 'Ithe yona kum, Ubabalo lwam lukwanele; kuba amandla am azalisekiswa ekuswelekeni kwamandla. Ngoko ke sendiya kuqhayisa kamnandi kakhulu ngokuswela kwam amandla, ukuze ahlale phezu kwam amandla kaKristu.', 'XHO22'),
(48, 12, 10, 'Kungoko ndikholisiweyo kukuswela amandla, kukuphathwa kakubi, ziingxakeko, ziintshutshiso, ziingxinano, ngenxa kaKristu; kuba xa ndibuthathaka, kuxa kanye ndinamandla.', 'XHO22'),
(48, 12, 11, 'Ndisuke ndaba sisidenge ngokuqhayisa; Nindinyanzelile nina; kuba mna ndibe ndifanele ukuyalezwa nini; kuba andisilelanga nganto kwabona bapostile baziincamisa, nakuba ndingento yanto.', 'XHO22'),
(48, 12, 12, 'Imiqondiso yokuba ngumpostile, inene, yasetyenzwa phakathi kwenu ngokunyamezela konke, ngemiqondiso, nezimanga, nemisebenzi yamandla.', 'XHO22'),
(48, 12, 13, 'Kaloku yintoni na enasilela ngayo kwamanye amabandla kaThixo, ingekukuba mna ngokwam ndingabanga mthwalo kuni? ndixoleleni ngobugwenxa bam.', 'XHO22'),
(48, 12, 14, 'Yabonani, sendilungele ukuza kuni okwesithathu; ndaye ndingayi kuba mthwalo kuni; kuba andifuni zinto zenu, ndifuna nina; kuba asingabantwana abafanele ukuqwebela abazali, ngabazali abafanele ukuqwebela abantwana.', 'XHO22'),
(48, 12, 15, 'Mna ke, kuya kuba mnandi kakhulu ukuba ndichithe, ndidleke kuphele, ngenxa yemiphefumlo yenu; nakuba okukhona ndinithanda kakhulu, kokukhona ndithandwa kancinane.', 'XHO22'),
(48, 12, 16, 'Kungade kube kunjalo, mna kambe andibanga mthwalo kuni; mandibe, ndinobuqhetseba nje, ndisuke ndanibambisa ngenkohliso.', 'XHO22'),
(48, 12, 17, 'Kukho mntu na ke kwabo ndabathumayo kuni, endanidlayo ngaye?', 'XHO22'),
(48, 12, 18, 'Ndamyala uTito, ndathuma umzalwana lowo kunye naye. Wanidla na ke uTito? asihambanga ngamoya mnye na? Asihambanga ngamanyathelo mnye na?', 'XHO22'),
(48, 12, 19, 'Niyawaseniba siyaziphendulela kusini na kuni? sithetha phambi kukaThixo, sikuKristu; sisuke senze izinto zonke, zintanda, ngenxa yokwakheka kwenu.', 'XHO22'),
(48, 12, 20, 'Kuba ndiyoyika, hleze ndithi ndakufika, ndinifumane ningenjengendikuthandayo, ndifunyanwe kuni ndingenjengenikuthandayo; hleze kubekho iimbambaniso, amakhwele, imisindo, amayelelwano, intlebendwane, iintsebenzo, ukudumba, iziphithiphithi:', 'XHO22'),
(48, 12, 21, 'hleze ndithi, ndakuba ndifikile, andithobe uThixo wam phakathi kwenu, ndize ndikhedame ngenxa yabaninzi asebebonile, abaguquka ekungcoleni, nasembulweni, naseburheletyweni ababenzayo.', 'XHO22'),
(48, 13, 1, 'Eli liza kuba lixesha lesithathu ndisiza kuni. Onke amazwi aya kumiswa ngomlomo wamangqina amabini, nokuba mathathu.', 'XHO22'),
(48, 13, 2, 'Sendinixelele ngenxa engaphambili, njengoko ndandikho okwesibini, ndinixela ngenxa engaphambili, naxa ndingekhoyo ngoku, ndibhalela abo bone ngenxa engaphambili, nabanye bonke, ukuba xa ndithe ndabuya ndeza, andiyi kubaconga;', 'XHO22'),
(48, 13, 3, 'Njengokuba nifuna ubungqina bokuba uKristu uthetha kum, ongenamandla phakathi kwenu, unamandla kuni.', 'XHO22'),
(48, 13, 4, 'Kuba nakuba wabethelelwayo emnqamlezweni ngokwasekusweleni amandla, udla ubomi ngokwasemandleni kaThixo. Kuba nathi siswele amandla kuye, noko siya kudla ubomi naye ngokwasemandleni kaThixo, ngokubhekisele kuni.', 'XHO22'),
(48, 13, 5, 'Zivavanyeni ukuba niselukholweni kusini na; zibonakaliseni ngokwenu. Anazi na ngokwenu ukuba uYesu Kristu ungaphakathi kwenu?', 'XHO22'),
(48, 13, 6, 'Ndiyathemba ke ukuba niyakwazi, ukuba asingabo abo bangenakucikideka thina.', 'XHO22'),
(48, 13, 7, 'Ke kaloku ndiyathandaza kuThixo, ukuba ningenzi nanye into embi; kungekuko ukuze sibonakale sicikidekile thina, ikukuze nenze into entle nina, ke thina sibe njengabangenakucikideka.', 'XHO22'),
(48, 13, 8, 'Kuba asinakwenza nto ngokuchasa inyaniso; sinokwenzela inyaniso.', 'XHO22'),
(48, 13, 9, 'Kuba siyavuya, xa sukuba singenamandla thina, nibe nina ninamandla;', 'XHO22'),
(48, 13, 10, 'Ngenxa yoko ndibhala ezi zinto ndingekho, ukuze ndingathi, ndakuba ndikho, ndenze kabukhali ngokwegunya, endalinikwa yiNkosi ukuba libe lelokwakha, lingabi lelokuchitha.', 'XHO22'),
(48, 13, 11, 'Elokugqiba, bazalwana, ndithi, Hambani kakuhle. Yibani ngabafezekileyo, thuthuzelekani, cingani nto-nye, hlalani ngoxolo; waye uThixo wothando noxolo uya kuba nani.', 'XHO22'),
(48, 13, 12, 'Bulisanani ngolwango olungcwele.', 'XHO22'),
(48, 13, 13, 'Bayanibulisa bonke abangcwele.', 'XHO22'),
(48, 13, 14, 'Ubabalo lweNkosi yethu uYesu Kristu, nothando lukaThixo, nodlelano loMoya oyiNgcwele, malube nani nonke. Amen. (Incwadi yesibini eya kwabaseKorinte yabhalwa eFilipi, isixeko saseMakedoni, nguTito noLucas.)', 'XHO22'),
(49, 1, 1, 'uPawulos, umpostile (okungengabantu, kungengamntu, kungoYesu Kristu, noThixo uYise, owamvusayo kwabafileyo);', 'XHO22'),
(49, 1, 2, 'Nabo bonke abazalwana abakunye nam babhalela amabandla aseGalati:', 'XHO22'),
(49, 1, 3, 'bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uYise, neNkosi yethu uYesu Kristu;', 'XHO22'),
(49, 1, 4, 'owazinikelayo ngenxa yezono zethu, ukuze asihlangule kweli phakade langoku lingendawo, ngokokuthanda kukaThixo uBawo wethu;', 'XHO22'),
(49, 1, 5, 'Makube kuye uzuko, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(49, 1, 6, 'Ndimangalisiwe kukuba niphambuke kamsinya kangaka kulowo wanibizayo ngobabalo lukaKristu, niye kwiindaba ezilungileyo ezizimbi;', 'XHO22'),
(49, 1, 7, 'Le ingeyiyo enye; koko kukho bathile banikhathazayo, bathandayo ukuzigqwetha iindaba ezilungileyo zikaKristu.', 'XHO22'),
(49, 1, 8, 'Ke, nokokuba sisuke thina aba, nokokuba sisuke isithunywa sasezulwini sanishumayeza iindaba ezilungileyo ezinxamnye nezo sanishumayezayo, masisingelwe phantsi.', 'XHO22'),
(49, 1, 9, 'Njengoko besesitshilo, ndiyaphinda, ndithi nangoku, Ukuba ubani unishumayeza iindaba ezilungileyo ezinxamnye nezo nazamkelayo, makasingelwe phantsi.', 'XHO22'),
(49, 1, 10, 'Kuba ngoku ndithomalalisa abantu, ndithomalalisa uThixo, kusini na? Ndifuna ukukholisa abantu na? Kuba, ukuba ndingaba ndisakholisa abantu, ndingaba andingumkhonzi kaKristu.', 'XHO22'),
(49, 1, 11, 'Ke kaloku, ndiyanazisa, bazalwana, ukuba azingokomntu iindaba ezilungileyo ezi, zashunyayelwayo ndim;', 'XHO22'),
(49, 1, 12, 'Kuba nam lo andizamkelanga mntwini, andizifundiswanga nokuzifundiswa; ndazamkela ngokutyhilelwa nguYesu Kristu.', 'XHO22'),
(49, 1, 13, 'Kuba nayiva eyam ihambo eyayifudula isebuYudeni, ukuba ndaye ndilitshutshisa ndilibhuqa ibandla likaThixo ngokuncamisileyo;', 'XHO22'),
(49, 1, 14, 'Ndaye ndihambela phambili ebuYudeni, ngaphezu kwabaninzi ababeziintanga zam eluhlangeni lwakowethu, ndizizondelele ngokugqithiseleyo izithethe zoomawokhulu.', 'XHO22'),
(49, 1, 15, 'Ke, xenikweni kwakholekayo kuThixo, owandahlulayo kwasesizalweni sikama, wandibiza ngalo ubabalo lwakhe,', 'XHO22'),
(49, 1, 16, 'ukuba amtyhile uNyana wakhe ngaphakathi kwam, ukuze ndimshumayele ngeendaba ezilungileyo phakathi kweentlanga; kwaoko andibhekisanga nto kwinyama negazi.', 'XHO22'),
(49, 1, 17, 'Kanjalo andinyukanga ndiye eYerusalem, kubo ababengabapostile ngaphambi kwam; ndesuka ndaya kwelaseArabhi, ndaza ndabuyela eDamasko.', 'XHO22'),
(49, 1, 18, 'Ndandula ndathi, emva kweminyaka emithathu, ndenyuka ndaya eYerusalem, ukuba ndazane noPetros; ndahlala naye iintsuku ezilishumi elinantlanu.', 'XHO22'),
(49, 1, 19, 'Andibonanga wumbi ke kubapostile, yaba nguYakobi, umzalwana weNkosi, yedwa.', 'XHO22'),
(49, 1, 20, 'Izinto ke endinibhalela zona, yabonani, ndithetha emehlweni kaThixo, andixoki.', 'XHO22'),
(49, 1, 21, 'Emva koko ndeza kwimimandla yelaseSiriya nelaseKilikiya;', 'XHO22'),
(49, 1, 22, 'Ndaye ke ndingaziwa ngesiqu ngawo amabandla akwelamaYuda, akuKristu;', 'XHO22'),
(49, 1, 23, 'banela ukuva kwakuphela ukuba, Lowo wayefudula esitshutshisa, ngoku ushumayela iindaba ezilungileyo zokholo abefudula elubhuqa.', 'XHO22'),
(49, 1, 24, 'Bamzukisa ke uThixo ngam.', 'XHO22'),
(49, 2, 1, 'Ndaza emva kweminyaka elishumi elinamine, ndabuya ndenyuka ndaya eYerusalem ndinoBharnabhas, ndathabatha noTito.', 'XHO22'),
(49, 2, 2, 'Ndenyuka ke ngokwesityhilelo, ndazandlala phambi kwabo iindaba ezilungileyo endizivakalisayo phakathi kweentlanga; ndazandlala ke phambi kwabo badumileyo bebodwa, hleze ndibe ndifumana ndibaleka, mhlawumbi ndibe ndafumana ndabaleka.', 'XHO22'),
(49, 2, 3, 'Kodwa noTito lo, lo ubenam, ekubeni engumGrike, akanyanzelekanga ukuba aluke;', 'XHO22'),
(49, 2, 4, 'ngenxa yabazalwana ababuxoki, abangeniswayo bengaqondi, bona bangene ngokufihlakeleyo, besiza kuhlola inkululeko yethu esinayo kuKristu Yesu, ukuze basikhonze ebukhobokeni;', 'XHO22'),
(49, 2, 5, 'Esathi abo asabavumela ngolulamo nokwexa elinye; ukuze inyaniso yeendaba ezilungileyo ihlale ihleli kuni.', 'XHO22'),
(49, 2, 6, 'Ke aba babonakala bayinto, nokuba babefudula benjani, akunamsebenzi kum; uThixo akakhethi buso bamntu;', 'XHO22'),
(49, 2, 7, 'Kodwa bathi bakubona ukuba ndiphathiswe iindaba ezimnandi zamaJuda, kanye njengoko zaziphathisiwe uPetros iindaba ezimnandi zolwaluko.', 'XHO22'),
(49, 2, 8, '(Kuba lo wasebenza ngokusebenzayo ngoPetros ebupostile bolwaluko, waye waba namandla kum phakathi kweentlanga);', 'XHO22'),
(49, 2, 9, 'bathi ooYakobi noKefas noYohane, abo ke babedume ukuba baziintsika, baluqonda ubabalo endababalwa ngalo, basinika thina noBharnabhas izandla zokunene zobudlelane; ukuze thina siye kwiintlanga, bona baye kwabolwaluko.', 'XHO22'),
(49, 2, 10, 'Kuphela nje banqwenela ukuba siwakhumbule amahlwempu; Oko ke ndandikukhuthalele ukukwenza.', 'XHO22'),
(49, 2, 11, 'Ke kaloku, xenikweni uPetros wafikayo kwa-Antiyokwe, ndamelana naye ngokusekuhleni, ngokuba ube engogwetyiweyo.', 'XHO22'),
(49, 2, 12, 'Kuba phambi kokuba kufike abathile bevela kuYakobi, wadla ndawonye nabeentlanga; bathi bakufika, wesuka wanqwanqwa, wazahlulahlula, esoyika abo bolwaluko.', 'XHO22'),
(49, 2, 13, 'Namanye amaYuda amkhohlisa ngokunjalo; wada noBharnabhas wakhukuliswa luhanahaniso lwabo.', 'XHO22'),
(49, 2, 14, 'Ndithe ke ndakubona ukuba abahambi ngokuthe tye ngokwenyaniso yeendaba ezilungileyo, ndathi kuPetros phambi kwabo bonke, Ukuba wena ungumYuda nje, uphila ngokwesiko lamaYuda, yini na ukuba ubanyanzele abeentlanga? IiNtlanga zazifanele ziphile njengamaYuda?', 'XHO22'),
(49, 2, 15, 'Thina singamaYuda ngemvelo, singengabo aboni beentlanga;', 'XHO22'),
(49, 2, 16, 'sisazi nje ukuba umntu akagwetyelwa ngokwasemisebenzini yomthetho; kungokukholwa kuYesu Kristu, nathi sakholwa kuYesu Kristu, ukuze sigwetyelwe ngokwaselukholweni lukaKrestu, kungabi ngokwasemisebenzini yomthetho; kuba ngokwasemisebenzini yomthetho akuyi kugwetyelwa nyama.', 'XHO22'),
(49, 2, 17, 'Ke ukuba, sifuna nje ukugwetyelwa ngoKristu, sifunyanwa singaboni nathi ngokwethu, ndiyabuza ndithi, uKristu ungumlungiseleli wesono na ke ngoko? Makube lee oko.', 'XHO22'),
(49, 2, 18, 'Kuba, ukuba izinto endazichithayo, ndibuya ndakhe zona, ndiyaziqondakalisa ngokwam ukuba ndingumgqithi.', 'XHO22'),
(49, 2, 19, 'Kuba mna ngomthetho ndafa kumthetho, ukuze ubomi ndibudlele kuThixo.', 'XHO22'),
(49, 2, 20, 'Ndibethelelwe emnqamlezweni noKristu; ingendim, ke nguKristu oselesidla ubomi kum. Ubomi ke endibudlayo ngoku, ndisenyameni, ndibudlela ekukholweni kuNyana kaThixo, owandithandayo, wazinikela ngenxa yam.', 'XHO22'),
(49, 2, 21, 'Andilutshitshisi ubabalo lukaThixo; kuba, ukuba bungomthetho ubulungisa, oko uKristu angaba wafumana wafa.', 'XHO22'),
(49, 3, 1, 'Ehlani, maGalati aswele ukuqonda! Ngubani na oninyangileyo, ukuba ningayithambeli nje inyaniso, ebethe nje phambi kwamehlo enu uYesu Kristu wabhalwa, wabhegezwa phakathi kwenu, ebethelelwe emnqamlezweni?', 'XHO22'),
(49, 3, 2, 'Ndithanda ukukha ndiqonde kuni le ndawo yodwa: Namamkela uMoya emisebenzini yomthetho, namamkela ezindabeni zokholo, kusini na?', 'XHO22'),
(49, 3, 3, 'Niziziyatha na? Nakuba niqale kuMoya, ngoku nigqibela kwinyama na?', 'XHO22'),
(49, 3, 4, 'Izinto ezinzima kangaka nafumana naziva na? ukuba bekulilize.', 'XHO22'),
(49, 3, 5, 'Lowo uniphayo uMoya, esebenza imisebenzi yamandla phakathi kwenu, ukwenza ngokwasemisebenzini yomthetho, ukwenza oko, ukwenza ngokwasezindabeni zokholo, kusini na?', 'XHO22'),
(49, 3, 6, 'njengokuba uAbraham wakholwayo nguThixo, kwaza oko kwabalelwa ebulungiseni kuye.', 'XHO22'),
(49, 3, 7, 'Yazini ngoko ukuba abaselukholweni, ngabo abo abangabantwana baka-Abraham.', 'XHO22'),
(49, 3, 8, 'Isibhalo, sabona ngenxa engaphambili ukuba uThixo ubeya kuzigwebela iintlanga ngokholo, sazishumayela ngenxa engaphambili iindaba ezilungileyo kuAbraham, sisithi, Ziya kusikeleleka ngawe zonke iintlanga.', 'XHO22'),
(49, 3, 9, 'Ngoko ke abaselukholweni bayasikelelwa ndawonye noAbraham okholwayo.', 'XHO22'),
(49, 3, 10, 'Kuba bonke abangabasemisebenzini yomthetho, baphantsi kwesiqalekiso;', 'XHO22'),
(49, 3, 11, 'Kodwa ke kuyabonakala ukuba akukho mntu ugwetyelwa ngomthetho kuye uThixo, kuba lowo ulilungisa uya kudla ubomi obuphuma elukholweni.', 'XHO22'),
(49, 3, 12, 'Ke wona umthetho asingowaselukholweni; uthi, Umntu owazenzayo ezo zinto uya kudla ubomi ngazo.', 'XHO22'),
(49, 3, 13, 'uKristu wasikhulula, wasikhulula esiqalekisweni somthetho, ngokwenziwa isiqalekiso ngenxa yethu, kuba kubhaliwe kwathiwa, Uqalekisiwe wonke umntu oxhonywe emthini;', 'XHO22'),
(49, 3, 14, 'ukuze intsikelelo ka-Abraham ibe sezintlangeni ngoKristu Yesu; ukuze idinga loMoya silamkele ngalo ukholo.', 'XHO22'),
(49, 3, 15, 'Bazalwana, ndithetha ngokwabantu; nokuba ungumnqophiso wabantu, nokuba uthe waqiniselwa, akukho uwutshitshisayo, athi mhlawumbi ongeze kuwo.', 'XHO22'),
(49, 3, 16, 'Enziwa ke kuye uAbraham idinga, nakuyo imbewu yakhe. Akathi, nakuzo iimbewu, ngathi uthetha ezininzi; usuka ngathi uthetha nye, uthi, Nakuyo imbewu yakho: nguKristu ke lowo.', 'XHO22'),
(49, 3, 17, 'Ukutsho ke ndithi, Umnqophiso ozinzisiweyo ngenxa engaphambili nguThixo, usingisele kuKristu, awunakuphangwa igunya ngumthetho, owabakhoyo emva kweminyaka engamakhulu omane anamanci mathathu, ukuze ulibhangise idinga elo.', 'XHO22'),
(49, 3, 18, 'Kaloku ilifa elo, ukuba liphuma emthethweni, loba alisaphumi edingeni; ke yena uThixo umbabale uAbraham ngedinga.', 'XHO22'),
(49, 3, 19, 'Uyintoni na ke ngoko umthetho? Walekelwa ngenxa yezigqitho, ide ifike imbewu leyo lalibekwe kuyo idinga; wawumiswe ngezithunywa zezulu, usesandleni somlamleli.', 'XHO22'),
(49, 3, 20, 'Ke umlamleli akamnye, kodwa uThixo mnye.', 'XHO22'),
(49, 3, 21, 'Umthetho ngoko uchasene na namadinga kaThixo? Nakanye! Kuba, ukuba bekuwiswe umthetho onako ukudlisa ubomi, inene, ngebuphume emthethweni lowo ubulungisa.', 'XHO22'),
(49, 3, 22, 'Ke sona isibhalo sisuke sazivalela zonke phantsi kwesono, ukuze idinga eliphuma ekukholweni kuYesu Kristu linikwe abakholwayo.', 'XHO22'),
(49, 3, 23, 'Kodwa ngaphambi kokufika kokholo, sasilindwa siphantsi komthetho, sivalelwe ndawonye kude kufike ukholo olu, luza kutyhilwa.', 'XHO22'),
(49, 3, 24, 'Ngoko ke umthetho waba ngosikhaphela kuKristu, ukuze sigwetyelwe ngokwaselukholweni.', 'XHO22'),
(49, 3, 25, 'Lwakuba lufikile ke ukholo, asibi saba phantsi kwamkhapheli.', 'XHO22'),
(49, 3, 26, 'Kaloku nina nonke ningoonyana bakaThixo ngako ukukholwa, nikuKristu Yesu.', 'XHO22'),
(49, 3, 27, 'Kuba nina nonke, nabhaptizelwayo kuKristu, namambatha uKristu.', 'XHO22'),
(49, 3, 28, 'Akusekho mYuda namGrike; akusekho khoboka nakhululekileyo; akusekho ndoda nankazana; kuba nina nonke nimntu mnye, nikuKristu Yesu.', 'XHO22'),
(49, 3, 29, 'Ukuba ke nina ningabakaKristu, niyimbewu ka-Abraham ngoko, neendlalifa ngokwedinga.', 'XHO22'),
(49, 4, 1, 'Ndithi ke, indlalifa, ngexesha lonke engumntwana, ayahluke nganto kumkhonzi, nakuba iyinkosi yeento zonke;', 'XHO22'),
(49, 4, 2, 'iphantsi kwabagcini namagosa, kude kufike ixesha elimiswe ngaphambili nguyise.', 'XHO22'),
(49, 4, 3, 'Ngokunjalo nathi, xenikweni besingabantwana, besingamakhoboka aphantsi kweziqalelo zehlabathi.', 'XHO22'),
(49, 4, 4, 'Lithe ke lakuzaliseka ixesha, uThixo wamkhupha weza uNyana wakhe, othe wazalwa ngumntu oyinkazana;', 'XHO22'),
(49, 4, 5, 'ukuze abakhulule ngokubathenga abaphantsi komthetho, ukuze sikuzuze ukwenziwa oonyana.', 'XHO22'),
(49, 4, 6, 'Ngokuba ke ningoonyana, uThixo wamkhupha uMoya woNyana wakhe, weza ezintliziyweni zenu, edanduluka esithi, Abha, Bawo.', 'XHO22'),
(49, 4, 7, 'Ngoko ke akusengumkhonzi, ungunyana; ukuba ke ungunyana, ukwayindlalifa kaThixo ngoKristu.', 'XHO22'),
(49, 4, 8, 'Kaloku, naningamazi uThixo, nanikhonza kwabo ngemvelo bangethixo.', 'XHO22'),
(49, 4, 9, 'Ngoku ke, nakuba nimazile uThixo, hayi kanye mandithi, nakuba naziwe nguThixo, nenza ntoni na ukubuya niguqukele kwiziqalelo ezingenamandla, ezingenaluncedo, enithanda ukubuya nikhonze zona ngokutsha?', 'XHO22'),
(49, 4, 10, 'Nimana nigcina imihla, neenyanga, namaxesha, neminyaka.', 'XHO22'),
(49, 4, 11, 'Ndiyanoyikela, hleze ndibe ndifumane ndabulaleka ngenxa yenu.', 'XHO22'),
(49, 4, 12, 'Ndiyanikhunga, bazalwana, yibani njengam; ngokuba ndinjengani; anindenzanga nto konke konke.', 'XHO22'),
(49, 4, 13, 'Niyazi ke ukuba bekungenxa yokuswela amandla kwenyama yam, ukunishumayeza kwam iindaba ezilungileyo ngokokuqala.', 'XHO22'),
(49, 4, 14, 'Ukulingwa kwam okwakusenyameni yam, anikwenzanga into engento, anikucekisanga nina nokukucekisa; nesuka nandamkela njengesithunywa sikaThixo, njengoKristu Yesu ngokwakhe.', 'XHO22'),
(49, 4, 15, 'Luphi na ngoko ubabalo enanithetha ngalo? Kuba ndiyaningqinela, ukuba bekunokwenzeka, ninge nawakrukrayo amehlo enu, nandinika.', 'XHO22'),
(49, 4, 16, 'Ndisuke ndaba lutshaba lwenu na ke, ngokuthetha inyaniso kuni?', 'XHO22'),
(49, 4, 17, 'Bayanizondelela, ingekuko okulungileyo; ewe, bathanda ukunivalela ngaphandle, ukuze nibachaphazele bona.', 'XHO22'),
(49, 4, 18, 'Kuhle ke ukuba kuncekelelwe ngamaxesha onke entweni entle, kunganeli ukuba njalo ndakuba ndikho kuni.', 'XHO22'),
(49, 4, 19, 'Bantwanana bam, endibuye ndanenimba ngabo, ade uKristu amiliswe ngaphakathi kwenu;', 'XHO22'),
(49, 4, 20, 'bendithanda ke ukubakho kuni ngoku, ndilenze libe lilimbi ilizwi lam; kuba ndiyathandabuza ngani.', 'XHO22'),
(49, 4, 21, 'Ndixeleleni, nina nithanda ukuba phantsi komthetho, aniwuva na umthetho?', 'XHO22'),
(49, 4, 22, 'Kuba kubhaliwe kwathiwa, uAbraham wayenoonyana ababini: omnye engowekhobokazana, omnye engowomfazi okhululekileyo.', 'XHO22'),
(49, 4, 23, 'Kodwa lowo wekhobokazana wayezelwe ngokwenyama; kodwa lowo womfazi okhululekileyo wayephume ngalo idinga.', 'XHO22'),
(49, 4, 24, 'Eziyintsonkotha ezo zinto; kuba aba bayiyo iminqophiso emibini; Omnye uvela entabeni yeSinayi, uzalela ebukhobokeni, onguHagare ke wona lowo.', 'XHO22'),
(49, 4, 25, 'Kuba loo nto inguHagare yiSinayi, intaba ekwelama-Arabhi, elungelelene neYerusalem yakalokunje, esebukhobokeni ke nabantwana bayo.', 'XHO22'),
(49, 4, 26, 'Ke yona ephezulu iYerusalem ikhululekile, enguma wethu leyo.', 'XHO22'),
(49, 4, 27, 'Kuba kubhaliwe kwathiwa, Yiba nemihlali, ludlolo lungazaliyo; Gqabhuka umemelele, wena ungenanimba; ngokuba baninzi abantwana besishiywa, ngaphezu kwabonendoda.', 'XHO22'),
(49, 4, 28, 'Thina ke, bazalwana, ngokokukaIsake, singabantwana bedinga.', 'XHO22'),
(49, 4, 29, 'Kusuke, kwanjengokuba wayesithi oko owazalwa ngokwenyama amtshutshise owazalwa ngokoMoya, kwaba njalo nakaloku.', 'XHO22'),
(49, 4, 30, 'Sithini na ke sona isibhalo? Likhuphele phandle ikhobokazana nonyana walo; kuba unyana wekhobokazana akasayi kuba ndlalifa kunye nonyana wokhululekileyo.', 'XHO22'),
(49, 4, 31, 'Ngoko ke, bazalwana, asingabantwana bekhobokazana thina; singabowokhululekileyo.', 'XHO22'),
(49, 5, 1, 'Yimani ngoko enkululekweni, athe uKristu wasikhululela kuyo; nize ningabuyi nibanjwe yidyokhwe yobukhoboka.', 'XHO22'),
(49, 5, 2, 'Niyabona, mna, Pawulos, ndithi kuni, ukuba nithe naluka, uKristu akasayi kuninceda lutho.', 'XHO22'),
(49, 5, 3, 'Ndiphinda ndingqina ke kumntu wonke owalukileyo, ukuba ungonetyala lokuwenza umthetho uphela.', 'XHO22'),
(49, 5, 4, 'Nibhambathisiwe nguKristu kuni, nina nigwetyelwayo emthethweni; niwile elubabalweni.', 'XHO22'),
(49, 5, 5, 'Kaloku thina sihlala silindile ngoMoya, ithemba lobulungisa ngokwaselukholweni.', 'XHO22'),
(49, 5, 6, 'Kuba kwabakuKristu Yesu, akunamandla anto ukwaluka, kwanokungaluki; lukholo ke olusebenza ngothando.', 'XHO22'),
(49, 5, 7, 'Nibaleke kakuhle; ngubani na osuke wanithintela ukuba ningayithambeli inyaniso?', 'XHO22'),
(49, 5, 8, 'Olo loyiseko lwenu aluphumi konibizayo.', 'XHO22'),
(49, 5, 9, 'Igwele elincinane liyayibilisa intlama iphela.', 'XHO22'),
(49, 5, 10, 'Mna ndikholosekile malunga nani eNkosini, ukuba aniyi kucinga nto yimbi; ke onikhathazayo uya kuluthwala ugwebo, nokuba ngongubani.', 'XHO22'),
(49, 5, 11, 'Mna ke, bazalwana, ukuba ndingaba ndisavakalisa ulwaluko, ndisatshutshiselwani na? Isono somnqamlezo sithe sayekwa.', 'XHO22'),
(49, 5, 12, 'Akwaba bebengade banqanyulwe abo banibandezelayo!', 'XHO22'),
(49, 5, 13, 'Kuba nina nabizelwa enkululekweni, bazalwana; kodwa ke musani ukusebenzisa inkululeko yokuxhelela inyama; kodwa khonzanani niqhutywa luthando.', 'XHO22'),
(49, 5, 14, 'Kuba wonke umthetho uzaliswe ngazwi linye, ngeli leli; Uze umthande ummelwane wakho ngoko uzithanda ngako.', 'XHO22'),
(49, 5, 15, 'Ukuba niyagrenyana, nidlane, lumkani ukuba ningagqitywa omnye ngomnye.', 'XHO22'),
(49, 5, 16, 'Ke ndithi, Hambani ngokoMoya, naningasayi kuthi ke niyizalise inkanuko yenyama.', 'XHO22'),
(49, 5, 17, 'Kaloku inyama ikhanuka ngokuchasene noMoya, uMoya ke ukhanuka ngokuchasene nenyama; ezo zinto ke ziphambene, ukuze ningazenzi izinto enisukuba nizithanda.', 'XHO22'),
(49, 5, 18, 'Ukuba ke nikhokelwa nguMoya, anikho phantsi komthetho.', 'XHO22'),
(49, 5, 19, 'Iyabonakala ke yona imisebenzi yenyama, eyile; uhenyuzo, ukungcola, uburheletya,', 'XHO22'),
(49, 5, 20, 'Unqulo-zithixo, ubugqwirha, intiyo, amayelenqe, ingqumbo, iinkani, iinkani, amayelenqe,', 'XHO22'),
(49, 5, 21, 'oomona, ookubulala, ookunxila, iindywala, nezinto ezinjengezo; endinixelelayo ngenxa engaphambili, njengokuba besendinixelele kade, ukuba abazenzayo izinto ezinjalo abayi kubudla ilifa ubukumkani bukaThixo.', 'XHO22'),
(49, 5, 22, 'Ke sona esoMoya isiqhamo luthando, uvuyo, uxolo, ukuzeka kade umsindo, ububele, ukulunga, intembeko,', 'XHO22'),
(49, 5, 23, 'akukho mthetho uchasene nezinto ezinjalo.', 'XHO22'),
(49, 5, 24, 'Ke kaloku abo ke bakaKristu, inyama bayibethelele emnqamlezweni, kunye neminqweno neenkanuko zayo.', 'XHO22'),
(49, 5, 25, 'Ukuba sidla ubomi ngokoMoya, masihambe nokuhamba kwangokoMoya.', 'XHO22'),
(49, 5, 26, 'Masingafuni luzuko olulambathayo, sicaphukisana, simonelana.', 'XHO22'),
(49, 6, 1, 'Bazalwana, ukuba umntu uthe waqutyulwa nokuqutyulwa sisiphoso esithile, nina bangaboMoya, mlulekeni onjalo ninomoya wobulali; uzilumkele, ukuze ungahendwa nawe.', 'XHO22'),
(49, 6, 2, 'Thwalisanani ubunzima, xa nenjenjalo ke nithobela umyalelo kaKrestu.', 'XHO22'),
(49, 6, 3, 'Kuba umntu, ukuba uba uyinto, engento, uyazikhohlisa.', 'XHO22'),
(49, 6, 4, 'Elowo ke makawucikide owakhe umsebenzi, wayeya kwandula ukuba neqhayiya ngokwakhe yedwa, angabi nalo ngomnye;', 'XHO22'),
(49, 6, 5, 'Kaloku wonke umntu uya kuwuthwala owakhe umthwalo.', 'XHO22'),
(49, 6, 6, 'Makathi ke lowo ufundiswayo ilizwi, abelane nalowo ufundisayo ngeento zonke ezilungileyo.', 'XHO22'),
(49, 6, 7, 'Musani ukulahlekiswa; uThixo yena asingowokuhlekisa; kuba into athe wahlwayela yona umntu, wovuna kwayona;', 'XHO22'),
(49, 6, 8, 'ngokuba lowo uhlwayelela eyakhe inyama, wovuna ukonakala ngokwasenyameni; lowo uhlwayelela uMoya ke, wovuna ubomi obungunaphakade ngokwaseMoyeni.', 'XHO22'),
(49, 6, 9, 'Ke ekwenzeni okulungileyo masingethi amandla; kuba sovuna ngexesha elililo, ukuba asityhafi.', 'XHO22'),
(49, 6, 10, 'Njengoko singathi sibe nethuba, masisebenze okulungileyo kubo bonke, ngokukodwa ke kwabendlu yokholo.', 'XHO22'),
(49, 6, 11, 'Bonani ukuba kukhulu kokubhala endinibhalele ngako ngesam isandla.', 'XHO22'),
(49, 6, 12, 'Bonke abathanda ukuba babe nesimilo esihle ngokwangaphandle, abo ke baninyanzela ukuba naluswe; benzela nje kodwa ukuze bangatshutshiswa ngawo umnqamlezo kaKristu.', 'XHO22'),
(49, 6, 13, 'Kuba kwanabo balukayo, bengawubambi umthetho; bafuna nje ukuba nalùswe, ukuze baqhayise ngeyiyeyenu inyama.', 'XHO22'),
(49, 6, 14, 'Kodwa mna mandingakhe ndiqhayise nganto, ingenguwo umnqamlezo weNkosi yethu uYesu Krestu, ekuthe ngaye ihlabathi kum labethelelwa emnqamlezweni, ndathi nam kwihlabathi ndabethelelwa emnqamlezweni.', 'XHO22'),
(49, 6, 15, 'Kuba kwabakuKristu Yesu, akunamandla anto ukwaluka nokungaluki; into enamandla sisidalwa esitsha.', 'XHO22'),
(49, 6, 16, 'Nabani na ohamba ngalo mgaqo ndimnqwenelela uxolo nenceba, kunye nalo lonke usapho lukaThixo – nguSirayeli omtsha ke lowo.', 'XHO22'),
(49, 6, 17, 'Elokugqiba ndithi, Makungabikho namnye undixhamlayo; kuba mna, iimpawu zeNkosi uYesu ndiziphethe ngomzimba wam.', 'XHO22'),
(49, 6, 18, 'Ubabalo lweNkosi yethu uYesu Krestu malube nomoya wenu, mawethu. Amen. (KwabaseGalati ibhalwe eRoma.)', 'XHO22'),
(50, 1, 1, 'uPawulos, umpostile kaYesu Kristu ngokuthanda kukaThixo, ubhalela abangcwele abase-Efese, abakholwayo kuKristu Yesu;', 'XHO22'),
(50, 1, 2, 'Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(50, 1, 3, 'Makabongwe uThixo, uYise weNkosi yethu uYesu Kristu, osibabale ngazo zonke iintsikelelo zoMoya kwezasemazulwini iindawo, sikuKristu;', 'XHO22'),
(50, 1, 4, 'njengokuba wathi, ngaphambi kokusekwa kwehlabathi, wasinyulela kuye, ukuba sibe ngcwele, singabi nakusoleka phambi kwakhe;', 'XHO22'),
(50, 1, 5, 'ethe ngenxa engaphambili wasimisela ngenxa engaphambili ukuba senziwe oonyana ngoYesu Kristu kuye ngokwakhe, ngokwenkolelo yokuthanda kwakhe;', 'XHO22'),
(50, 1, 6, 'Ngenxa yozuko lobabalo lwakhe, awasibabala ngalo sikuye othandiweyo;', 'XHO22'),
(50, 1, 7, 'esinayo sikuye inkululeko ngokuhlawulelwa ngegazi lakhe, uxolelo lwezono, ngokobutyebi bobabalo lwakhe;', 'XHO22'),
(50, 1, 8, 'Uye waphuphumela kuthi kubo bonke ubulumko nokuqonda;', 'XHO22'),
(50, 1, 9, 'esazisile ke imfihlelo yokuthanda kwakhe, ngokokuthanda kwakhe, awamisa ngako ngokwakhe;', 'XHO22'),
(50, 1, 10, 'ukuze ngexesha lokuzaliseka kwamaxesha, azihlanganisele zonke izinto kuye uKristu, ezisemazulwini kwanezisemhlabeni; nakuye:', 'XHO22'),
(50, 1, 11, 'Esathi kuye nathi sazuza ilifa, simiselwe ngenxa engaphambili ngokwengqibo yozisebenzayo zonke ezo nto ngokwecebo lokuthanda kwakhe;', 'XHO22'),
(50, 1, 12, 'ukuze sibe ngabokudumisa uzuko lwakhe, thina aba sathembela kuKristu kuqala;', 'XHO22'),
(50, 1, 13, 'enithe nakholwa kuye nani, emveni kokuliva kwenu ilizwi lenyaniso, iindaba ezilungileyo zosindiso lwenu; enathi, nakholwa kuye, natywinwa natywinwa nguMoya oyiNgcwele wedinga;', 'XHO22'),
(50, 1, 14, 'osisinyaniso selifa lethu, ukude kugqibelele ukukhululeka ngentlawulelo kwabo baziinqobo kuye, ukuba kudunyiswe uzuko lwakhe.', 'XHO22'),
(50, 1, 15, 'Ngoko ke, ndakuba ndivile ngokholo eninalo kwiNkosi uYesu, nangothando eninalo kubo bonke abangcwele.', 'XHO22'),
(50, 1, 16, 'Andiphezi ukubulela ngenxa yenu, xa ndinikhumbula emithandazweni yam;', 'XHO22'),
(50, 1, 17, 'ukuze uThixo weNkosi yethu uYesu Kristu, uYise wozuko, aninike umoya wobulumko wokunityhilela, nimazi kakuhle;', 'XHO22'),
(50, 1, 18, 'Akhanyiselwe amehlo engqondo yakho; ukuze nilazi ithemba lobizo lwakhe, nento obuyiyo ubutyebi bozuko lwelifa lakhe phakathi kwabangcwele;', 'XHO22'),
(50, 1, 19, 'nokuba buyintoni na ubukhulu bamandla akhe kuthi bakholwayo, ngokokusebenza kwamandla akhe amakhulu;', 'XHO22'),
(50, 1, 20, 'awayenzayo ekuKristu, ekumvuseni kwabafileyo, wammisa ngasekunene kwakhe kwezasezulwini iindawo;', 'XHO22'),
(50, 1, 21, 'ngaphezu kwabo bonke ubulawuli, negunya, namandla, nobukhosi, nalo lonke igama elibizwayo, kungabi kweli hlabathi lodwa, kwanakwelo lizayo;', 'XHO22'),
(50, 1, 22, 'Waza izinto zonke wazithobela phantsi kweenyawo zakhe, wammisa ukuba abe yintloko yezinto zonke kulo ibandla.', 'XHO22'),
(50, 1, 23, 'Lingumzimba wakhe ke lowo, ozalise konke ngako konke.', 'XHO22'),
(50, 2, 1, 'Nani unidlise ubomi, nifile nje ziziphoso na zizono;', 'XHO22'),
(50, 2, 2, 'enanifudula nihamba kuzo ngokwesimo seli hlabathi, ngokomphathi wegunya lesibhakabhaka, womoya lo usebenzayo ngoku kubo abangevayo;', 'XHO22'),
(50, 2, 3, 'Esathi sonke phakathi kwethu sasifudula sihamba ngokweenkanuko zenyama yethu, sifeza iinkanuko zenyama nezengqondo; kwaye ngemvelo sasingabantwana bengqumbo, njengabanye.', 'XHO22'),
(50, 2, 4, 'Ke yena uThixo, esisityebi ngenceba, uthe ngenxa yothando lwakhe olukhulu awasithanda ngalo.', 'XHO22'),
(50, 2, 5, 'Nakuba besifile zizono, usidlise ubomi ndawonye noKristu, nisindiswe ngokubabalwa.', 'XHO22'),
(50, 2, 6, 'Wasivusa naye ndawonye, wasihlalisa naye kwezasemazulwini iindawo, sikuKristu Yesu;', 'XHO22'),
(50, 2, 7, 'ukuze ubutyebi obuncamisileyo bobabalo lwakhe abubonakalalise emaphakadeni azayo, ngokusenzela ububele ngoKristu Yesu.', 'XHO22'),
(50, 2, 8, 'Kuba nisindiswe ngokubabalwa nangalo ukholo; nako oko akuphumi kuni, kusisipho sikaThixo;', 'XHO22'),
(50, 2, 9, 'Akuphumi misebenzini, hleze kubekho bani uqhayisayo.', 'XHO22'),
(50, 2, 10, 'Kuba thina singumsebenzi wakhe kuKristu Yesu, sidalelwe imisebenzi emihle, awayilungisayo uThixo ngenxa engaphambili, ukuze sihambe kuyo.', 'XHO22'),
(50, 2, 11, 'Ngoko ke khumbulani ukuba nina nifudula niziintlanga ngokwenyama, nibizwa ngokuba ningabangalukileyo, ngabo kuthiwa ngabolwaluko olwenziwe ngezandla enyameni;', 'XHO22'),
(50, 2, 12, 'Okuba ngelo xesha nibe ningenaKristu, ningazani nawo umzi wakwaSirayeli, ningabasemzini kuyo iminqophiso yedinga, ningenathemba, ningenaThixo ehlabathini.', 'XHO22'),
(50, 2, 13, 'Ke ngoku ngoKristu Yesu, nina, enibe nifudula nikude, nithe naba kufuphi ngalo igazi likaKristu.', 'XHO22'),
(50, 2, 14, 'Kaloku yena uluxolo lwethu, yena owasenza izinto zombini, waba nye, waluchitha ke udonga lothango oluphakathi;', 'XHO22'),
(50, 2, 15, 'ewubhangisile ngenyama yakhe ubutshaba, nomthetho wemithetho ekwimimiselo; ukuze abo babini benze ngaphakathi kwakhe, babe mntu mnye, mtsha, esenza uxolo;', 'XHO22'),
(50, 2, 16, 'nokuze abaxolelanise bobabini noThixo, babe mzimbeni mnye ngawo umnqamlezo, ebubulala ubutshaba ngawo;', 'XHO22'),
(50, 2, 17, 'Weza ke washumayela iindaba ezilungileyo zoxolo kuni abo bakude, nakwabakufuphi;', 'XHO22'),
(50, 2, 18, 'Kuba kungaye ukungeniswa kwethu sobabini, siMoya mnye kuye uYise.', 'XHO22'),
(50, 2, 19, 'Ngoko ke anisengabasemzini nabasemzini;', 'XHO22'),
(50, 2, 20, 'Nakhelwe phezu kwesiseko sabapostile nabaprofeti, inguYesu Kristu ngokwakhe ilitye lembombo;', 'XHO22'),
(50, 2, 21, 'Ekuye sonke isakhiwo eso sihlangene, sikhula, sibe yitempile engcwele eNkosini.', 'XHO22'),
(50, 2, 22, 'Enithi nani nakhelwe ndawonye kuye, ukuze nibe yindawo yokuhlala kaThixo ngaye uMoya.', 'XHO22'),
(50, 3, 1, 'Ngenxa yoko mna Pawulos, mbanjwa kaYesu Kristu ngenxa yenu zintlanga,', 'XHO22'),
(50, 3, 2, 'Ukuba nikuvile ulungiselelo lobabalo lukaThixo, endababalwa ngalo kuni;', 'XHO22'),
(50, 3, 3, 'okokuba wandazisa imfihlelo leyo ngokotyhilelo; (njengoko ndibhale ngaphambili ngamafutshane),', 'XHO22'),
(50, 3, 4, 'enothi ngoko, nakulesa, nibe nokuqiqa ukwazi kwam kwimfihlelo kaKristu;', 'XHO22'),
(50, 3, 5, 'ababengathanga kuzimbi izizukulwana bayaziswe oonyana babantu, njengoko bayityhilelweyo ngoku abapostile bakhe abangcwele, nabaprofeti bakhe, ngaye uMoya;', 'XHO22'),
(50, 3, 6, 'ukuze iintlanga zibe ziindlalifa kunye naye, zibe zimzimba mnye, zibe ngamadlelane ngedinga lakhe elikuKristu, ngazo iindaba ezilungileyo;', 'XHO22'),
(50, 3, 7, 'endabamiselayo mna ngokwesipho sobabalo lukaThixo, endasibabalwa ngalo ngokusebenza kwamandla akhe.', 'XHO22'),
(50, 3, 8, 'Mna lo ungoyena mncinanana kubo bonke abangcwele, ndababalwa ngolo lubabalo, lokuba ndishumayele phakathi kweentlanga iindaba ezilungileyo zobutyebi obungalandekiyo bukaKristu;', 'XHO22'),
(50, 3, 9, 'ndibakhanyisele bonke abantu, bayibone into obuyiyo ubudlelane bemfihlelo ebifihliwe kwasemaphakadeni, ikuye uThixo owazidalayo zonke izinto ngoYesu Kristu;', 'XHO22'),
(50, 3, 10, 'ukuze ngoku izilawuli namandla, kwezasezulwini iindawo, zaziswe ngalo ibandla ubulumko bukaThixo obuziintlobo ezininzi;', 'XHO22'),
(50, 3, 11, 'ngokwengqibo yaphakade, awayenzayo ngoKristu Yesu, iNkosi yethu;', 'XHO22'),
(50, 3, 12, 'Esikuye ke sinokungafihlisi, nokungeniswa kwethu sikholosile, ngokukholwa kuye;', 'XHO22'),
(50, 3, 13, 'Kungoko ndicelayo ukuba ningethi amandla ziimbandezelo zam, ezingenxa yenu, zona ezi ziluzuko lwenu.', 'XHO22'),
(50, 3, 14, 'Ngenxa yoko ndiguqa ngamadolo am kuye uYise weNkosi yethu uYesu Kristu;', 'XHO22'),
(50, 3, 15, 'Ekuthiywe ngaye yonke imizalwane esemazulwini nesemhlabeni,', 'XHO22'),
(50, 3, 16, 'ukuze aninike, ngokobutyebi bozuko lwakhe, ukomelezwa ngamandla ngaye uMoya wakhe, ngokusingisele emntwini ongaphakathi;', 'XHO22'),
(50, 3, 17, 'ukuze uKristu ahlale ngokholo ezintliziyweni zenu; ukuze nina, nimiliselwe nisekelwe eluthandweni;', 'XHO22'),
(50, 3, 18, 'ninako ke ukubuqiqa into obuyiyo ububanzi, nobude, nobunzulu, nokuphakama;', 'XHO22'),
(50, 3, 19, 'kwanokulwazi uthando lukaKristu oluncamise ukwazi, ukuze niye nizala ngako konke ukuzala kukaThixo.', 'XHO22'),
(50, 3, 20, 'Kuye ke onako ukwenza okungaphezu kweento zonke ngokugqithiseleyo kakhulu, ngaphezu kwesikucelayo nesinokukuqiqa, ngokokwamandla asebenzayo ngaphakathi kwethu;', 'XHO22'),
(50, 3, 21, 'kuye malubekho uzuko ebandleni elikuKristu Yesu, kuse kuzo zonke izizukulwana zalo iphakade lasemaphakadeni. Amen.', 'XHO22'),
(50, 4, 1, 'Ndiyanivuselela ngoko, mna mbanjwa waseNkosini, ukuba nihambe ngokuwufaneleyo ubizo enabizwa ngalo;', 'XHO22'),
(50, 4, 2, 'Ninako konke ukuthobeka kwentliziyo, ubulali, ukuzeka kade umsindo, ninyamezelana ngothando.', 'XHO22'),
(50, 4, 3, 'Zabalazelani ukubugcina ubunye boMoya ngentambo yoxolo.', 'XHO22'),
(50, 4, 4, 'Mnye umzimba, mnye noMoya, njengokuba nabizwayo nokubizwa, naba nathemba linye lobizo lwenu.', 'XHO22'),
(50, 4, 5, 'Inye iNkosi, lunye ukholo, lunye ubhaptizo,', 'XHO22'),
(50, 4, 6, 'Mnye uThixo, uYise wabo bonke, ophezu kwabo bonke, okubo bonke, ophakathi kwenu nonke.', 'XHO22'),
(50, 4, 7, 'Kodwa elowo kuthi ubabalwa ngokomlinganiselo wesisa sikaKrestu.', 'XHO22'),
(50, 4, 8, 'Kungoko athi, Enyuke waya enyangweni, wathimba intimbo, Wabapha izipho abantu.', 'XHO22'),
(50, 4, 9, 'Oko ke kuthi, wenyuka, kuyintoni na, kungekwakukuthi, wehla nokuhla kuqala, waya kwezona zisezantsi iindawo zomhlaba?', 'XHO22'),
(50, 4, 10, 'Lowo wehlayo ikwangulowo wenyukayo, waya ngaphezu kwawo onke amazulu, ukuze azalise zonke izinto.', 'XHO22'),
(50, 4, 11, 'Wabapha abanye ukuba babe ngabapostile; abanye ke bangabaprofeti; inxenye yaba ngabashumayeli; inxenye ke yaba ngabalusi nabafundisi;', 'XHO22'),
(50, 4, 12, 'ukuze abangcwele bagqibelele, kuse umsebenzi wolungiselelo, ukuze uwakhe umzimba kaKristu;', 'XHO22'),
(50, 4, 13, 'side thina sonke sifike kubo ubunye bokholo, nobokumazi okumazi kakuhle uNyana kaThixo, ebudodeni obupheleleyo, emlinganisweni wobukhulu bokuzala kukaKristu;', 'XHO22'),
(50, 4, 14, 'ukuze singabi saba ziintsana, silatyuzwa, siphetshethwa ngumoya wonke wemfundiso, ngokukhohlisa kwabantu, ngobuqhetseba bobuqhinga;', 'XHO22'),
(50, 4, 15, 'Ke sithetha inyaniso ngothando, sikhule ngeendawo zonke kuye, lo uyintloko, uKristu;', 'XHO22'),
(50, 4, 16, 'ekuphuma kuye ukuthi, umzimba wonke uhlangane kakuhle, ubandakanywe ngako konke ukwabanayo ukwasebenza ilungu ngalinye, ukhulisa umzimba ekuzakhenise eluthandweni.', 'XHO22'),
(50, 4, 17, 'Ke ngoko, oku ndiyakutsho, ndikungqinela ngokwaseNkosini, ukuba ningabi sahamba njengoko zihamba ngako ezinye iintlanga, ngamampunge engqiqo yazo;', 'XHO22'),
(50, 4, 18, 'Zenziwe mnyama iingqondo, zahlukanisiwe nobomi bukaThixo ngenxa yokungazi okukuzo, ngenxa yobumfama bentliziyo;', 'XHO22'),
(50, 4, 19, 'abathe, bengaziva, bazinikela eburheletyweni, besebenza ubunqambi bonke, bebawa.', 'XHO22'),
(50, 4, 20, 'Ke nina animfundanga ngokunjalo uKristu;', 'XHO22'),
(50, 4, 21, 'Ukuba okunene nimvile, nafundiswa nguye, njengokuba inyaniso ikuye uYesu;', 'XHO22'),
(50, 4, 22, 'yokuba nithi ngakwihambo yangaphambili nimlahle umntu omdala, owonakaliswayo ngokokweenkanuko zokulukuhla;', 'XHO22'),
(50, 4, 23, 'manihlaziywe ke kuwo umoya wengqiqo yenu;', 'XHO22'),
(50, 4, 24, 'Nambathe umntu omtsha, othe ngokukaThixo wadalelwa ebulungiseni, nasebungcweleni benyaniso.', 'XHO22'),
(50, 4, 25, 'Kungoko ke ndithi, lahlani ubuxoki, thethani inyaniso elowo nommelwane wakhe; ngokuba singamalungu, omnye elelomnye.', 'XHO22'),
(50, 4, 26, 'Qumbani, ningoni; ilanga malingade litshone nicaphukile;', 'XHO22'),
(50, 4, 27, 'kananjalo musani ukumkhwelela indawo uMtyholi lo.', 'XHO22'),
(50, 4, 28, 'Lowo ubayo makangabi seba; kanye ke makabulaleke esebenza okulungileyo ngezandla zakhe, ukuze abe nako ukwabela osweleyo.', 'XHO22'),
(50, 4, 29, 'Makungaphumi nanye intetho ebolileyo emlonyeni wenu;', 'XHO22'),
(50, 4, 30, 'Kanjalo musani ukumenza buhlungu uMoya oyiNgcwele kaThixo, enathi kuye natywinwa, naqiniselelwa imini yentlawulelo.', 'XHO22'),
(50, 4, 31, 'Mabukhweleliswe kuni bonke ubukrakra, nomsindo, nengqumbo, nengcolo, nonyeliso, ndawonye nolunya lonke;', 'XHO22'),
(50, 4, 32, 'Ke nina yibani nobubele omnye komnye, nibe nemfesane kakhulu, nixolelane, njengokuba naye uThixo wanixolelayo ekuKristu.', 'XHO22'),
(50, 5, 1, 'Ngoko yibani ngabaxelisa uThixo, ngokwabantwana abaziintanda;', 'XHO22'),
(50, 5, 2, 'nihambe eluthandweni, kwanjengokuba naye uKristu wasithandayo, wazinikela ngenxa yethu, ukuba abe ngumnikelo nedini kuye uThixo, ukuba abe livumba elimnandi.', 'XHO22'),
(50, 5, 3, 'Ke umbulo, nako konke ukungcola, nokubawa, makungakhe kukhankanywe phakathi kwenu, njengoko kubafaneleyo abangcwele;', 'XHO22'),
(50, 5, 4, 'kwanamanyala, nentetho yobuyatha, nobuhiba, izinto ezo ezingafanelekileyo. Makube kokukhona ninemibulelo.', 'XHO22'),
(50, 5, 5, 'Kuba oku nikwazi, ukuba wonke umenzi wombulo, nomntu ongcolileyo, nolibawa, engumkhonzi wezithixo, akanalifa ebukumkanini bukaKristu noThixo.', 'XHO22'),
(50, 5, 6, 'Makungabikho namnye unilukuhlayo ngamazwi alambathayo; kuba iza ngenxa yezo zinto ingqumbo kaThixo phezu kwabangevayo.', 'XHO22'),
(50, 5, 7, 'Ngoko musani ukuba ngamahlulelana nabo.', 'XHO22'),
(50, 5, 8, 'Kuba nina naye nifudula nibubumnyama, ke ngoku nikukukhanya, niseNkosini nje; hambani ke ngokwabantwana bokukhanya.', 'XHO22'),
(50, 5, 9, '(Kuba sona isiqhamo soMoya sikuko konke ukulunga, nobulungisa, nenyaniso;)', 'XHO22'),
(50, 5, 10, 'Nicikida ukuba yintoni na ekholekileyo kakuhle kwiNkosi.', 'XHO22'),
(50, 5, 11, 'Kanjalo musani ukuba nabudlelane ngemisebenzi yobumnyama engenasiqhamo;', 'XHO22'),
(50, 5, 12, 'Kuba izinto ezenziwa kubo emfihlekweni kulihlazo nokuzithetha.', 'XHO22'),
(50, 5, 13, 'Ke zonke ezi zinto, zakohlwaywa, ziyabonakalaliswa kukukhanya; kuba konke okubonakalaliswayo kukukhanya.', 'XHO22'),
(50, 5, 14, 'Kungoko athi, Vuka wena uleleyo, uvuke kwabafileyo; uKristu wokukhanyisela.', 'XHO22'),
(50, 5, 15, 'Khangelani ngoko, ukuba ningathini na, ukuze kucokiseke ukuhamba kwenu; ningabi njengabaswele ubulumko, yibani njengezilumko;', 'XHO22'),
(50, 5, 16, 'nizongela ixesha eli, ngokuba le mihla ayindawo.', 'XHO22'),
(50, 5, 17, 'Ngenxa yoko musani ukuba ziintsweli-kuqonda; yibani ngabakuqondayo into okuyiyo ukuthanda kweNkosi.', 'XHO22'),
(50, 5, 18, 'Musani ukunxila yiwayini, ekukhoyo kuloo nto ukuzibhubhisa; kodwa nizaliswe nguMoya;', 'XHO22'),
(50, 5, 19, 'nithetha omnye komnye ngeendumiso neengoma nezango ezizezomoya, nivuma, nibetha uhadi entliziyweni yenu kuyo iNkosi,', 'XHO22'),
(50, 5, 20, 'nimana nibulela ngenxa yeento zonke, egameni leNkosi yethu uYesu Kristu, kuye uThixo uYise;', 'XHO22'),
(50, 5, 21, 'Nilulamelana ngokoyika uThixo.', 'XHO22'),
(50, 5, 22, 'Nina bafazi, walulameleni awenu amadoda, ngokokululamela iNkosi;', 'XHO22'),
(50, 5, 23, 'Ngokuba indoda le iyintloko yomfazi, njengokuba naye uKristu eyintloko yebandla, eli; waye yena ngokwakhe enguMsindisi wawo umzimba.', 'XHO22'),
(50, 5, 24, 'Ke, kwanjengokuba ibandla eli limlulamela uKristu, mabathi ngokunjalo nabafazi bawalulamele awabo amadoda ezintweni zonke.', 'XHO22'),
(50, 5, 25, 'Nina madoda, bathandeni abafazi benu, njengokuba naye uKristu walithandayo ibandla, wazinikela ngenxa yalo;', 'XHO22'),
(50, 5, 26, 'ukuze asingcwalise, asihlambulule ngokuhlamba kwamanzi ngelizwi;', 'XHO22'),
(50, 5, 27, 'ukuze alimise phambi kwakhe linobuqaqawuli, lilibandla elingenabala, elingenamibimbi nanye into enjalo; mayibe ngcwele, ingabi nasiphako.', 'XHO22'),
(50, 5, 28, 'Ngoko amelwe amadoda ukubathanda abawo abafazi, njengokuba beyimizimba yawo. Lowo umthandayo umkakhe, uyazithanda;', 'XHO22'),
(50, 5, 29, 'kuba akukho namnye wakha wayithiya eyakhe inyama; usuka ayondle, ayigcine, njengokuba nayo iNkosi ilenjenjalo ibandla.', 'XHO22'),
(50, 5, 30, 'Ngokuba singamalungu omzimba wayo, singabenyama yayo, singabamathambo ayo.', 'XHO22'),
(50, 5, 31, 'Ngenxa yoko ke, umntu womshiya uyise nonina, anamathele kuye umkakhe, baze abo babini babe nyama-nye.', 'XHO22'),
(50, 5, 32, 'Loo mfihlelo inkulu; ke mna ndithetha ndisingisele kuKristu, ndisingisele nakulo ibandla.', 'XHO22'),
(50, 5, 33, 'Kodwa ke manithi nani ngabanye, elowo amthande umkakhe, njengoko azithanda ngako; Umfazi ke makayoyike indoda.', 'XHO22'),
(50, 6, 1, 'Nina bantwana, baveni abazali benu ngokwabaseNkosini; kuba oko kububulungisa.', 'XHO22'),
(50, 6, 2, 'Beka uyihlo nonyoko; wona lowo ngumthetho wokuqala onedinga;', 'XHO22'),
(50, 6, 3, 'ukuze kulunge kuwe, ube nexesha elide emhlabeni.', 'XHO22'),
(50, 6, 4, 'Nani boyise, musani ukubacaphukisa abantwana benu; bondleleni ekuqeqesheni nasekululekeni kweNkosi.', 'XHO22'),
(50, 6, 5, 'Nina bakhonzi, baveni abaziinkosi zenu ngokwenyama, ninokuzoyikela nokugubha, ningenakumbi ezintliziyweni zenu, ninge niva uKristu ngokwakhe,', 'XHO22'),
(50, 6, 6, 'kungabi ngankonzo yamehlo, ngathi ningabakholisi-bantu; makube njengabakhonzi bakaKristu, nikwenza ukuthanda kukaThixo ngokwasentliziyweni;', 'XHO22'),
(50, 6, 7, 'nisebenza ngentumekelelo, ngathi kukwiNkosi, akukhona kubantu;', 'XHO22'),
(50, 6, 8, 'nisazi nje ukuba into esukuba ilungile, athe ulowo wayenza, uya kwamkeliswa kwayona yiNkosi, nokuba ungumkhonzi nokuba ungokhululekileyo.', 'XHO22'),
(50, 6, 9, 'Nani baziinkosi zabo, yenzani kwaezo zinto kubo, nikuyeke ukusongela; kananjalo akukho kukhetha buso kuye.', 'XHO22'),
(50, 6, 10, 'Elokugqiba, bazalwana bam, ndithi, Yomelelani eNkosini, nasekuqineni kokomelela kwayo.', 'XHO22'),
(50, 6, 11, 'Xhobani sonke isikrweqe sikaThixo, ukuze nibe nako ukumisa nibuchase ubuqhinga boMtyholi.', 'XHO22'),
(50, 6, 12, 'Kuba umzamo wethu asingowokuzamana negazi nenyama; ngowokuzamana nazo izilawuli, nawo amagunya, nazo iziphatha-hlabathi zobumnyama balo eli phakade, nayo imimoya ekhohlakeleyo kwezasezulwini iindawo.', 'XHO22'),
(50, 6, 13, 'Ngoko ke, qubulani sonke isikrweqe sikaThixo, ukuze nibe nako ukuchasa ngomhla ombi, nithi nikufezile konke, nime nimi.', 'XHO22'),
(50, 6, 14, 'Yimani ngoko, nibhinqe inyaniso esinqeni senu, ninxibe isigcina-sifuba sobulungisa;', 'XHO22'),
(50, 6, 15, 'Nibophe ezinyaweni zenu ulungiselelo lweendaba ezilungileyo zoxolo.', 'XHO22'),
(50, 6, 16, 'Ngaphezu kweento zonke, qubulani ikhaka lokholo, enothi ngalo nibe nako ukuzicima zonke iintolo ezinomlilo zongendawo.', 'XHO22'),
(50, 6, 17, 'Yamkelani isigcina-ntloko sosindiso, nalo ikrele loMoya, elililizwi likaThixo;', 'XHO22'),
(50, 6, 18, 'ngako konke ukuthandaza nokukhunga, nithandaza amaxesha onke nikuye uMoya; niyiphaphamele loo nto ngako konke ukuzingisa nokukhunga, ngenxa yabo bonke abangcwele;', 'XHO22'),
(50, 6, 19, 'nakum, ukuze ndilinikwe ilizwi lokuba ndiwuvule umlomo wam ngokungafihlisiyo, ukuyazisa imfihlelo yeendaba ezi zilungileyo;', 'XHO22'),
(50, 6, 20, 'endisisigidimi ngenxa yazo ndisezintanjeni; ukuze ndithethe ngako ukungafihlisi, njengoko ndimelwe kukuthetha ngako.', 'XHO22'),
(50, 6, 21, 'Ke kaloku, ukuze nani nikwazi okukokwam, ukuba ndinjani na, wonazisa konke uTikiko, umzalwana oyintanda, umlungiseleli othembekileyo ngokwaseNkosini;', 'XHO22'),
(50, 6, 22, 'endimthumele khona oku kuni, ukuze nizazi iindaba zethu, nokuze azithuthuzele iintliziyo zenu.', 'XHO22'),
(50, 6, 23, 'Makube luxolo kubo abazalwana, nothando, ndawonye nokholo oluvela kuThixo uYise, neNkosi yethu uYesu Kristu.', 'XHO22'),
(50, 6, 24, 'Ubabalo malube nabo bonke abayithandayo iNkosi yethu uYesu Kristu ngokungenakonakala. Amen. (Ibhalelwa kwabase-Efese eseRoma, nguTikiko.)', 'XHO22'),
(51, 1, 1, 'uPawulos noTimoti, abakhonzi bakaYesu Kristu, babhalela bonke abangcwele abakuKristu Yesu, abaseFilipi, ndawonye nabaveleli nabalungiseleli;', 'XHO22'),
(51, 1, 2, 'Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(51, 1, 3, 'Ndiyambulela uThixo wam xa ndinikhumbula.', 'XHO22'),
(51, 1, 4, 'Ekukhungeni kwam konke ndikhunga ngenxa yenu nonke, ndivuyiswa;', 'XHO22'),
(51, 1, 5, 'Ngenxa yobudlelane benu obubhekisele kuzo iindaba ezilungileyo, kususela kwimini yokuqala kude kube ngoku;', 'XHO22'),
(51, 1, 6, 'ndikholose ngayo le nto, yokuba lowo wawuqalayo ngaphakathi kwenu umsebenzi olungileyo, wowufeza, ide ibe yiloo mini kaYesu Kristu;', 'XHO22'),
(51, 1, 7, 'njengokuba kufanelekile ukuba ndicinge oku ngani nonke, ngokuba nikum entliziyweni; nithe, nasezintanjeni zam, nasekuziphenduleleni kwam, nasekuziqiniseni kwam iindaba ezilungileyo, naba ngamadlelana nam ngobabalo olu, nina nonke.', 'XHO22'),
(51, 1, 8, 'Kaloku uThixo ulingqina lam lokuba kukhulu ukunilangazelela kwam nonke, ndineemfesane zikaYesu Kristu.', 'XHO22'),
(51, 1, 9, 'Ndithandazela ke oku, ukuba uthando lwenu luphuphume ngakumbi nangakumbi ekwazini nasekucingeni konke,', 'XHO22'),
(51, 1, 10, 'ukuze nizicikide izinto eziziincamisa; ukuze nibe msulwa, ningabi nasiphako, ide ibe yiloo mini kaKristu;', 'XHO22'),
(51, 1, 11, 'nizele ziziqhamo zobulungisa ezingoYesu Kristu, nikhangelise ekuzukiseni nasekudumiseni uThixo.', 'XHO22'),
(51, 1, 12, 'Ke kaloku ndinga ningàzi, bazalwana, ukuba izinto ezindihleleyo, zibethe kwakukhona zihambayo iindaba ezilungileyo;', 'XHO22'),
(51, 1, 13, 'ngokokude iintambo zam ezi zibonakale kuyo yonke indlu yomkhosi nakwabanye bonke, ukuba ndikuKristu;', 'XHO22'),
(51, 1, 14, 'baze abona bazalwana baninzi bathi, beseNkosini, bekholose ngeentambo zam ezi, babe kukhona benobuganga bokulithetha ilizwi, bengenakoyika.', 'XHO22'),
(51, 1, 15, 'Okunene abathile bamvakalisa uKristu ngomona neenkani; ukanti ke inxenye ikholwa;', 'XHO22'),
(51, 1, 16, 'Abanye bamvakalisa uKristu ngeyelenqe, benganyanisekanga, beba baya kongezelela imbandezelo kwiintambo zam;', 'XHO22'),
(51, 1, 17, 'ke aba, benziwa luthando, bamazisa wKristu besazi ukuba ndimiselwe ukuziphendulela iindaba ezilungileyo.', 'XHO22'),
(51, 1, 18, 'Kuthiweni na ngoko? Noko kunjalo, ngeendlela zonke, nokuba kungokunyhwalaza, nokuba kungenyaniso, kwaziswa uKristu. Ndiyavuya kuko oko, ndaye ndisaya kuvuya.', 'XHO22'),
(51, 1, 19, 'Kuba ndiyazi ukuba oku kuya kuphumelela elusindisweni lwam ngako ukundithandazela, nangoncedo loMoya kaYesu Kristu;', 'XHO22'),
(51, 1, 20, 'kuye ngokulangazelela nangokuthemba kwam, ukuba andiyi kudaniswa nganto; uya kusuka ngako konke ukungafihlisi, njengokwamaxesha onke, athi nangoku enziwe mkhulu uKristu emzimbeni wam, nokuba na kungobomi, nokuba kungokufa.', 'XHO22'),
(51, 1, 21, 'Kuba kum mna, ukudla ubomi kunguKristu, ukufa kuyinzuzo.', 'XHO22'),
(51, 1, 22, 'Ke ukuba ukudla ubomi ndisenyameni kusisiqhamo somsebenzi kum oko, into ke endonyula yona andiyazi.', 'XHO22'),
(51, 1, 23, 'Kuba ndixinekile phakathi kwezinto ezimbini; ndinawo umnqweno wokunga ndinganduluka, ndiye kuba noKristu; eyona nto ingcono kakhulu:', 'XHO22'),
(51, 1, 24, 'Kodwa ukuhlala ndihleli ndisenyameni yeyona nto ifunekayo ngenxa yenu.', 'XHO22'),
(51, 1, 25, 'Kananjalo, ndikholose ngoko nje, ndiyazi ukuba ndiya kuhlala ndihlale, ndihleli nani nonke, ukuze nihambele phambili, nivuyiswe lukholo;', 'XHO22'),
(51, 1, 26, 'ukuze iqhayiya lenu kuKristu Yesu ngam liye lisanda ngokwanda, ngako ukubuya ndibekho kuni.', 'XHO22'),
(51, 1, 27, 'Kuphela nje ihambo yenu mayibe njengokufanele ukuzishumayela iindaba ezilungileyo zikaKristu; ukuze, nokuba ndithe ndeza kunibona, nokuba ndingekhoyo, ndive iindaba zenu, ukuba nimi moyeni mnye, nicinga nto-nye, nizama ukholo lwenu, ivangeli;', 'XHO22'),
(51, 1, 28, 'Into ke leyo engumbonakalaliso wentshabalalo okunene kubo, kodwa ingumbonakalaliso wosindiso kuni, kube ke oko kuvela kuThixo.', 'XHO22'),
(51, 1, 29, 'Kaloku naninikwe igunya ngenxa kaKrestu, ukuze ninganeli kukholwa kuye kuphela, koko nive ubunzima ngenxa yakhe,', 'XHO22'),
(51, 1, 30, 'ninawo umzamo onjengalowo nawubonayo kum, nangoku niwuvayo ukuba ukho kum.', 'XHO22'),
(51, 2, 1, 'Ukuba ngoko kukho intuthuzelo kuKristu, ukuba kukho intuthuzelo yothando, ukuba kukho ubudlelane boMoya, ukuba kukho imfesane neemfefe,', 'XHO22'),
(51, 2, 2, 'Luzaliseni uvuyo lwam, ukuze nicinge nto-nye, ninaluthando lunye, ninamphefumlo mnye, nicinga nto-nye.', 'XHO22'),
(51, 2, 3, 'Ningenzi nanye into ngokweyelenqe, nangokozuko olulambathayo. Yithini ngokuthobeka kwentliziyo omnye ambalele omnye ekuthini nguyena wongamileyo kunaye.', 'XHO22'),
(51, 2, 4, 'Elowo makaxunele kwezakhe izinto zodwa, elowo makaxunele nakwezabanye.', 'XHO22'),
(51, 2, 5, 'Yibani nale ngcinga kuni, wayenayo kuye uKristu Yesu;', 'XHO22'),
(51, 2, 6, 'Owathi, esebumeni bukaThixo, akathi ukulingana noThixo akukuphanga;', 'XHO22'),
(51, 2, 7, 'usuke wazenzisa, wathabatha ubume bomkhonzi, wenziwa wafana nabantu;', 'XHO22'),
(51, 2, 8, 'wathi ngemo efunyenwe enjengomntu, wazithoba, waba ngolulamileyo, wada wesa ekufeni, ukufa ke kwasemnqamlezweni.', 'XHO22'),
(51, 2, 9, 'Kungoko athe uThixo wamphakamisa kakhulu, wamnika igama elingaphezu kwawo onke amagama;', 'XHO22'),
(51, 2, 10, 'ukuze ngegama likaYesu onke amadolo agobe, awabasemazulwini, nawabasemhlabeni, nawabaphantsi komhlaba;', 'XHO22'),
(51, 2, 11, 'zithi zonke iilwimi zixele ukuba uYesu Kristu uyiNkosi, kuze kuzukiswe uThixo uYise.', 'XHO22'),
(51, 2, 12, 'Ngoko ke, zintanda zam, njengokuba nahlala ninokuva, lusebenzeni nilufeze olwenu usindiso, ninokuzoyikela nokungcangcazela;', 'XHO22'),
(51, 2, 13, 'Kuba nguThixo okusebenzayo Ngaphakathi kwenu ukuthanda oko, kwanokusebenza oko, ngenxa yenkolelo yakhe.', 'XHO22'),
(51, 2, 14, 'Yenzani zonke izinto ngaphandle kokumbombozela nokuxambulisana,', 'XHO22'),
(51, 2, 15, 'ukuze nibe ngabantwana bakaThixo abangenakusoleka, abangenatyala phakathi kohlanga olujijekileyo, olugwenxa, enikhanya phakathi kwalo njengezikhanyiso ehlabathini.', 'XHO22'),
(51, 2, 16, 'Nibambelele elizwini lobomi; nibe liqhayiya lam, nide nise kwimini kaKristu, ukuba andifumananga ndabaleka, kanjalo andifumananga ndabulaleka.', 'XHO22'),
(51, 2, 17, 'Ewe, nokuba ndiyathululwa ngokomnikelo phezu kombingelelo nombuso wokholo lwenu, ndiyavuya, ndivuyisana nani nonke.', 'XHO22'),
(51, 2, 18, 'Yithini ke ngokoko nani nivuye, nivuyisane nam.', 'XHO22'),
(51, 2, 19, 'Ke kaloku ndithembele eNkosini uYesu, ukuba ndonithumela uTimoti kamsinya, ukuze nam ndonwabe, ndakuzazi iindawo ezingani.', 'XHO22'),
(51, 2, 20, 'Kuba andinamntu wumbi, umphefumlo uvana nowam, owothi ayikhathalele ngokwenyaniso imicimbi engani.', 'XHO22'),
(51, 2, 21, 'Kuba bonke bafuna ezabo izinto, zingezizo ezikaYesu Kristu.', 'XHO22'),
(51, 2, 22, 'Ukucikideka ke kwakhe niyakwazi: ukuba wathi, njengomntwana kuyise, wakhonza kunye nam kuzo iindaba ezilungileyo.', 'XHO22'),
(51, 2, 23, 'Lowo ke ngoko ndithembe ukumthuma kwaoko, xa ndithe ndayiqonda imicimbi engam.', 'XHO22'),
(51, 2, 24, 'Ke ndithembele eNkosini, ukuba nam ngokwam ndoza kamsinya.', 'XHO22'),
(51, 2, 25, 'Kodwa ke ndibe kuyafuneka ukuba ndinithumele uEpafrodito, umzalwana wam osebenza nam, umphumi-mkhosi nam, umthunywa wenu ke, umlungiseleli wam kwiintswelo zam;', 'XHO22'),
(51, 2, 26, 'ekubeni ke ebenilangazelela nina nonke, edandatheka, ngenxa enokuba nevayo ukuba wayesifa.', 'XHO22'),
(51, 2, 27, 'Kuba okunene wayesifa, ephantse ukufa. Ke uThixo wamenzela inceba; akenzela yena yedwa, wenzela nam lo, ukuze ndingabi buhlungu, ndibuhlungu kade.', 'XHO22'),
(51, 2, 28, 'Kukhona ngoko ndimthuma ngokukhawuleza, ukuze nithi nakumbona nibuye nivuye, kuthi nokwam ukuba buhlungu kudambe.', 'XHO22'),
(51, 2, 29, 'Mamkeleni ke ngokwaseNkosini, ninovuyo lonke; nibabeke abanjalo.', 'XHO22'),
(51, 2, 30, 'Kaloku, ngenxa yomsebenzi kaKristu, wada waphantsa ukufa, engabucongi ubomi bakhe, ukuze enzelelele ukusilela kwenu ekundilungiseleleni.', 'XHO22'),
(51, 3, 1, 'Elokugqiba, bazalwana bam, vuyani niseNkosini. Ukunibhalela kwaezo zinto akunqenekile okunene kum, ke nina kuyaninqabisa.', 'XHO22'),
(51, 3, 2, 'Zilumkeleni izinja, balumkeleni abasebenzi beento ezimbi, balumkeleni abo basika.', 'XHO22'),
(51, 3, 3, 'Kuba abolwaluko sithi, thina bakhonza uThixo ngomoya, siqhayise ngoKristu Yesu, singakholosi ngenyama;', 'XHO22'),
(51, 3, 4, 'nakuba mna ndibe ndingakholosayo ngenyama; Ukuba kukho bani uba unokukholosa ngenyama, bendingaba ngakumbi mna.', 'XHO22'),
(51, 3, 5, 'Ndaluswa ngomhla wesibhozo, ndingowembewu kaSirayeli, ndingowesizwe sikaBhenjamin, ndingumHebhere wamaHebhere; ngokomthetho ndingumFarisi;', 'XHO22'),
(51, 3, 6, 'ngokwenzondelelo ndilitshutshisa ibandla; ngobulungisa obo basemthethweni ndingongenakusoleka.', 'XHO22'),
(51, 3, 7, 'Ndisuke, zona ezo zinto zabe ziyinzuzo kum, zona ezo ndazibalela ekuthini ziyinkxwaleko ngenxa kaKristu.', 'XHO22'),
(51, 3, 8, 'Ewe, okunene, ndisuka izinto zonke ndizibalele ekuthini ziyinkxwaleko, ngenxa yokongama kokumazi uKristu Yesu iNkosi yam;', 'XHO22'),
(51, 3, 9, 'ndifunyanwe kuye, ndingenabo obam ubulungisa, bona obuphuma emthethweni;', 'XHO22'),
(51, 3, 10, 'ukuze ndimazi, kwanamandla ovuko lwakhe, nobudlelane naye ngeentlungu zakhe, ndifaniswe nokufa kwakhe;', 'XHO22'),
(51, 3, 11, 'ukuba ndingade ndifikelele ekuvukeni kwabafileyo.', 'XHO22'),
(51, 3, 12, 'Kungekuko ukuthi sendamkele, nokuthi sendigqibelele;', 'XHO22'),
(51, 3, 13, 'Bazalwana, mna andizibaleli ekuthini ndigangile; ke ndenza nto-nye: ndithi, ndizilibala izinto ezisemva, ndisolulela kwezingaphambili;', 'XHO22'),
(51, 3, 14, 'Ndiphuthume ngokoxunele umvuzo wobizo lwaphezulu lukaThixo kuKristu Yesu.', 'XHO22'),
(51, 3, 15, 'Masithi ngoko, ngangoko singako thina bagqibeleleyo, sicinge oko; kanjalo ukuba nicinga into ngokukumbi, uThixo wonityhilela naleyo.', 'XHO22'),
(51, 3, 16, 'Noko ke, apho sifikelele khona, masihambe ngamgca mnye, masicinge nto-nye.', 'XHO22'),
(51, 3, 17, 'Yibani ngabaxelisa mna, bazalwana, nixunele kubo abahamba ngokunjalo, njengokuba ninomfuziselo, thina aba ke.', 'XHO22'),
(51, 3, 18, 'Kuba baninzi abahambayo, endathetha futhi kuni ngabo, ngoku ke ndithetha ndilila nokulila, ndisithi, bazizo iintshaba zomnqamlezo kaKristu;', 'XHO22'),
(51, 3, 19, 'Abasiphelo siyintshabalalo, abaThixo usisisu sabo, abaluzuko lulihlazo labo, abanyamekela izinto zomhlaba.', 'XHO22'),
(51, 3, 20, 'Kaloku thina ngokwethu sisemazulwini; apho kananjalo silinde khona uMsindisi, iNkosi uYesu Kristu;', 'XHO22'),
(51, 3, 21, 'eya kuwenza kumila kumbi umzimba wokuthobeka kwethu, ukuze wona wenziwe ufane nawo umzimba wozuko lwakhe, ngokokusebenza kwalowo unako ukuzithobela phantsi kwakhe zonke izinto.', 'XHO22'),
(51, 4, 1, 'Ngoko ke, bazalwana bam abaziintanda, balangazelelwayo, luvuyo lwam, sithsaba sam, yimani ngokunjalo eNkosini, zintanda.', 'XHO22'),
(51, 4, 2, 'uWodiya ndiyamyala, noSintike ndiyamyala, ukuba bacinge nto-nye beseNkosini.', 'XHO22'),
(51, 4, 3, 'Ndiyakucela nawe, sinxulumana senyaniso, bambisana nabo bafazi bazama nam ezindabeni ezilungileyo, kwanoKlemente, nabanye abasebenzisi bam, abamagama asencwadini yobomi.', 'XHO22'),
(51, 4, 4, 'Vuyani eNkosini amaxesha onke; ndiyaphinda ndithi, Vuyani!', 'XHO22'),
(51, 4, 5, 'Ukuba nengqiqo kwenu makwazeke ebantwini bonke. iNkosi isondele.', 'XHO22'),
(51, 4, 6, 'Ningaxhaleli nto; mazithi iingcelo zenu ezintweni zonke, ngako ukuthandaza nokukhunga, ndawonye nokubulela, zaziswe kuye uThixo.', 'XHO22'),
(51, 4, 7, 'Lwaye uxolo lukaThixo, olugqithisele kuko konke ukuqonda, luya kuzigcina iintliziyo zenu neengqiqo zenu kuKristu Yesu.', 'XHO22'),
(51, 4, 8, 'Elokugqiba, bazalwana, ndithi, iinto zonke eziyinene, iinto zonke ezindilekileyo, iinto zonke ezibubulungisa, iinto zonke ezinyulu, iinto zonke ezithandekayo, iinto zonke eziludumo lulungileyo; Ukuba ke kukho sidima, ukuba kukho ndumiso, zicingeni ezo zinto.', 'XHO22'),
(51, 4, 9, 'Kwaezo zinto nazifundayo, nazamkela, naziva, nazibona kum, yenzani zona ezo; waye uThixo woxolo eya kuba nani.', 'XHO22'),
(51, 4, 10, 'Ke kaloku ndaye ndivuya eNkosini kakhulu, ukuba ngoku kokugqibela kude kwathi ukukunyamekela kwenu okwam, kwabuya kwatyatyamba: enikuxhalabeleyo ke, koko naniswele ithuba.', 'XHO22'),
(51, 4, 11, 'Kungekuko ukuba ndithetha ngokuswela; kuba mna ndafunda ukuthi entlalweni endikuyo ndanele.', 'XHO22'),
(51, 4, 12, 'Ndiyakwazi nokuba kunjani ukuhlwempuzeka, ndiyazana nobutyebi; kuko konke, nasezintweni zonke, ndiqhelanisiwe nokuhlutha kwanokulamba, nokutyeba kwanokuswela.', 'XHO22'),
(51, 4, 13, 'Ndinokuzenza izinto zonke, ndikulowo undomelezayo, uKristu.', 'XHO22'),
(51, 4, 14, 'Noko ke natyapha ukuba nobudlelane nam ngembandezelo.', 'XHO22'),
(51, 4, 15, 'Nani ke baseFilipi, niyazi ukuba, ekuqalekeni kweendaba ezilungileyo ezi, xenikweni ndaphumayo kwelaseMakedoni, akubangakho nalinye ibandla eladlelana nam, ngokusingisele emcimbini wokunikela nowokwamkela, yaba nini nedwa.', 'XHO22'),
(51, 4, 16, 'Ngokuba, naseTesalonika, kwaba kanye nakabini nithumela, nindinceda ekusweleni kwam.', 'XHO22'),
(51, 4, 17, 'Ingekuko ukuba ndibe ndifuna ukuphiwa; ikukuba ndibe ndifuna isiqhamo esandela emcimbini wenu.', 'XHO22'),
(51, 4, 18, 'Ke ndinezinto zonke nje, ndityebile, ndihluthi, ndizamkele nje kuEpafrodito izinto ezavela kuni: ivumba elimnandi, umbingelelo owamkelekileyo, okholekileyo kakuhle kuye uThixo.', 'XHO22'),
(51, 4, 19, 'Kodwa ke uThixo wam wokwenzelelela konke ukuswela kwenu ngokobutyebi bakhe, ngokozuko olukuKristu Yesu.', 'XHO22'),
(51, 4, 20, 'Makubekho uzuko kuThixo uBawo wethu, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(51, 4, 21, 'Imibuliso kubo bonke abangcwele abakuKristu Yesu. Bayanibulisa abazalwana abanam.', 'XHO22'),
(51, 4, 22, 'Bayanibulisa bonke abangcwele, ngokukodwa ke aba bendlu kaKesare.', 'XHO22'),
(51, 4, 23, 'Ubabalo lweNkosi yethu uYesu Kristu malube nani nonke. Amen. (Ibhalelwa kwabaseFilipi eseRoma, nguEpafrodito.)', 'XHO22'),
(52, 1, 1, 'uPawulos, umpostile kaYesu Kristu ngokuthanda kukaThixo, noTimoti umzalwana,', 'XHO22'),
(52, 1, 2, 'ubhalela abaseKolose, abazalwana abangcwele abakholwayo kuKristu; uthi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(52, 1, 3, 'Sibulela kuye uThixo uYise weNkosi yethu uYesu Kristu, sihlala sinithandazela;', 'XHO22'),
(52, 1, 4, 'Njengoko seva ngokholo lwenu kuKristu Yesu, nothando eninalo kubo bonke abangcwele.', 'XHO22'),
(52, 1, 5, 'Nangenxa yethemba enalibekela lona emazulwini, nalivayo ngenxa engaphambili ngelizwi lenyaniso leendaba ezilungileyo;', 'XHO22'),
(52, 1, 6, 'Lowo ufikileyo kuni, njengokuba kunjalo kuwo wonke umhlaba; luxakatha isiqhamo, kwanjengoko lwenjenjalo nangaphakathi kuni, kususela kwimini enaluvayo, nalwazi ubabalo lukaThixo ngenyaniso;', 'XHO22'),
(52, 1, 7, 'Njengoko nafundayo nakuEpafra, ongumkhonzi oyintanda kunye nathi, engumlungiseleli othembekileyo kaKristu ngenxa yenu;', 'XHO22'),
(52, 1, 8, 'owasazisayo nokusazisa ngothando lwenu lwaseMoyeni.', 'XHO22'),
(52, 1, 9, 'Ngenxa yoko nathi, kususela kwimini esakuvayo oko, asiyeki ukunithandazela, nokukhunga, ukuze nizaliswe kukukwazi kakuhle ukuthanda kwakhe, ninobulumko bonke, nangokuqonda koMoya;', 'XHO22'),
(52, 1, 10, 'ukuze nihambe ngokuyifaneleyo iNkosi, nise kuko konke ukukholisa, nixakathe isiqhamo kuwo wonke umsebenzi olungileyo, nikhulela ekumazini uThixo;', 'XHO22'),
(52, 1, 11, 'nisomelezwa ngokomelela konke, ngokokuqina kozuko lwakhe, nide nise kuko konke ukunyamezela, nokuzeka kade umsindo, kunye novuyo;', 'XHO22'),
(52, 1, 12, 'nibulela kuye uYise, osenze sakulingana ukuba nesabelo kwilifa labangcwele ekukhanyeni;', 'XHO22'),
(52, 1, 13, 'owasihlangulayo egunyeni lobumnyama, wasifudusela ebukumkanini boNyana wakhe oyintanda;', 'XHO22'),
(52, 1, 14, 'esinayo sikuye inkululeko ngokuhlawulelwa ngegazi lakhe, uxolelo lwezono;', 'XHO22'),
(52, 1, 15, 'Ungumfanekiselo kaThixo ongenakubonwa, owamazibulo kwindalo yonke.', 'XHO22'),
(52, 1, 16, 'Ngokuba zadalelwa kuye zonke izinto, ezisemazulwini nezisemhlabeni, ezinokubonwa nezingenakubonwa, nokuba ziitrone, nokuba zibubukhosi, nokuba zizilawuli, nokuba ngamagunya; zonke izinto zidalwe nguye, zidalelwe yena.', 'XHO22'),
(52, 1, 17, 'Yena ke ungaphambi kweento zonke, nazo zonke izinto zima zimi kuye.', 'XHO22'),
(52, 1, 18, 'Yena ke uyintloko yomzimba, yebandla; oyingqalo, owamazibulo kubo abafileyo; ukuze ezintweni zonke abe ngovelela.', 'XHO22'),
(52, 1, 19, 'Kuba kwakholeka kuye uYise ukuba kuhlale kuye konke ukuzaliseka;', 'XHO22'),
(52, 1, 20, 'nokuthi ngaye azixolelanise naye zonke izinto, elwenzile uxolo ngalo igazi lomnqamlezo; ndithi ngaye, nokuba zizinto ezisemhlabeni, nokuba zezisemazulwini.', 'XHO22'),
(52, 1, 21, 'Nani, enibe nifudula ningazani, niziintshaba ngengqondo yenu ngokwasemisebenzini engendawo, kungoku unixolelanisile;', 'XHO22'),
(52, 1, 22, 'ngomzimba wenyama yakhe ngokufa ukuze animise phambi kwakhe ningcwele, ningenakusoleka, ningenakusoleka;', 'XHO22'),
(52, 1, 23, 'Ukuba kaloku nihlala nihleli elukholweni, nisekelwe niqinile, ningashukunyiswa nimke ethembeni leendaba ezilungileyo ezi nazivayo, zona zashunyayelwayo kwindalo yonke ephantsi kwezulu; endaba ngumlungiseleli wazo, mna Pawulos;', 'XHO22'),
(52, 1, 24, 'Ngoku ndiyavuya ezintlungwini zam ngenxa yenu, ndikuzalisa enyameni yam okusileleyo kwiimbandezelo zikaKristu, ngenxa yomzimba wakhe olilo ibandla;', 'XHO22'),
(52, 1, 25, 'endithe ndaba ngumlungiseleli wawo ngokokugosisa kukaThixo, awandinikela ngako ngenxa yenu, ukuba ndilizalise ilizwi likaThixo;', 'XHO22'),
(52, 1, 26, 'Kwanaloo mfihlelo, ibifihliwe kwasemaphakadeni nasezizukulwaneni, nakungokunje ibonakalalisiweyo kubo abangcwele bakhe;', 'XHO22'),
(52, 1, 27, 'awathandayo uThixo ukubazisa bona into obuyiyo ubutyebi bozuko lwaloo mfihlelo phakathi kwazo iintlanga; nguKristu ke ongaphakathi kwenu, ithemba lozuko;', 'XHO22'),
(52, 1, 28, 'Sazisa yena ke thina, siluleka wonke umntu, sifundisa wonke umntu ngabo bonke ubulumko; ukuze simise wonke umntu egqibelele ekuKristu Yesu;', 'XHO22'),
(52, 1, 29, 'endibulalekela kona oko, ndizama ngokokusebenza kwakhe okusebenzayo kum ngamandla.', 'XHO22'),
(52, 2, 1, 'Kuba ndithanda ukuba nikwazi ukuba kukhulu kwako ukuzama endikuzamayo ngenxa yenu, nabo baseLawodike, nabo bonke abangabubonanga ubuso bam ngokwenyama;', 'XHO22'),
(52, 2, 2, 'ukuze zithuthuzeleke iintliziyo zabo, bebandakanywe ngothando, kuse kubo bonke ubutyebi benzaliseko yokuqonda, kuse ekwazini imfihlelo engcwele kaThixo, nekaYise, nekaKristu;', 'XHO22'),
(52, 2, 3, 'efihlwe kuye bonke ubutyebi bobulumko nobokwazi.', 'XHO22'),
(52, 2, 4, 'Nditsho ke, ukuze kungabikho bani unilukuhlayo ngentetho egudileyo.', 'XHO22'),
(52, 2, 5, 'Kuba nangona ndingekhoyo ngenyama, khona ngomoya ndinani, ndivuya, ndibona ulungelelwano lwenu, nokuzimaseka kokukholwa kwenu kuKristu.', 'XHO22'),
(52, 2, 6, 'Njengokuba ngoko namamkelayo uKristu Yesu iNkosi, hambani kuye;', 'XHO22'),
(52, 2, 7, 'Nndele nakhelwe kuye, naqiniswa elukholweni njengoko nafundiswayo nafezeka kulo ngumbulelo.', 'XHO22'),
(52, 2, 8, 'Lumkani, kungabikho bani unithimbayo ngayo intanda-bulumko, nangokulukuhla okungento yanto, ngokwesithethe sabantu, ngokweziqalelo zehlabathi, kungengokukaKristu.', 'XHO22'),
(52, 2, 9, 'Kuba kuhleli kuye konke ukuzaliseka kobuThixo ngokomzimba;', 'XHO22'),
(52, 2, 10, 'nigqibelele kuye, oyintloko yabo bonke ubulawuli negunya;', 'XHO22'),
(52, 2, 11, 'Nathi kuye naluswa nokwaluswa ngolwaluko olungenakwenziwa ngazandla, ngako ukuhluba umzimba wenyama, ngalo ulwaluko lukaKristu;', 'XHO22'),
(52, 2, 12, 'ningcwatywe naye elubhaptizweni; enathi nikuye, navuswa nokuvuswa naye, ngokukholwa kwenu kukusebenza kukaThixo, owamvusa kubo abafileyo.', 'XHO22'),
(52, 2, 13, 'Nani, nifile nje ziziphoso nakukungaluki kwenyama yenu, unidlise ubomi ndawonye naye, enixolele zonke iziphoso.', 'XHO22'),
(52, 2, 14, 'Wawucima umbhalo wemithetho ochasene nathi, owawuchasene nathi, wasisusa ngokuwubethelela emnqamlezweni;', 'XHO22'),
(52, 2, 15, 'ebabhunyule ozilawuli noomagunya, wabenza isibonelo ekuhleni, ememelela ngabo ekuwo kuyo.', 'XHO22'),
(52, 2, 16, 'Makungabikho mntu ngoko unigwebayo ngento edliwayo, nangento eselwayo, nangendawo yomthendeleko, nangenyanga ethwasileyo, nangeesabatha;', 'XHO22'),
(52, 2, 17, 'Ezi sisithunzi sezinto ezizayo; ke wona umzimba ngokaKristu.', 'XHO22'),
(52, 2, 18, 'Makungabikho namnye unilukuhlayo emvuzweni wenu, ngokuqhutywa yintliziyo ethobekileyo, enqula izithunywa zezulu, egxuphuleka kwizinto angazibonanga, ephuthunyiswe enyameni yakhe,', 'XHO22'),
(52, 2, 19, 'engabambe yona iNtloko, ekuphuma kuyo ukuthi umzimba wonke ukhule ngokukhulisa kukaThixo, uncedwa, ubandakanywa ngako konke ukuxhomekelelana kwamalungu nemisipha.', 'XHO22'),
(52, 2, 20, 'Ngoko ke, ukuba nifile noKristu, niphuma kuzo iziqalelo zehlabathi, yini na ukuba niyithobele imimiselo, ningathi niphila kweli hlabathi?', 'XHO22'),
(52, 2, 21, '(Musani ukuphatha, musa ukungcamla, musa ukuphatha;', 'XHO22'),
(52, 2, 22, 'ezize zonke zitshabalale) ngenxa yemithetho neemfundiso zabantu?', 'XHO22'),
(52, 2, 23, 'Okunene zizinto ezo ezidunyelwe bubulumko ngokunqula okuzithandelayo, nangokuthobeka kwentliziyo, nokulahla umzimba; kungengambeko yanelisa inyama.', 'XHO22'),
(52, 3, 1, 'Ukuba ngoko navuswa kunye noKristu, funani izinto zaphezulu, apho akhona uKristu, ehleli ngasekunene kukaThixo.', 'XHO22'),
(52, 3, 2, 'Nyamekelani izinto zaphezulu, ingabi kwizinto zasemhlabeni.', 'XHO22'),
(52, 3, 3, 'Kaloku nafa, baye ubomi benu bufihlakele ndawonye noKrestu kuye uThixo.', 'XHO22'),
(52, 3, 4, 'Xa athe wabonakalaliswa uKristu, obubomi bethu, ngelo xesha niya kubonakalaliswa nani, ninaye ebuqaqawulini.', 'XHO22'),
(52, 3, 5, 'Bhubhisani ngoko amalungu enu asemhlabeni; uhenyuzo, ukungcola, ukuthabatheka okuhlazisayo, inkanuko embi, nokubawa, okuyinkonzo yezithixo kona oko;', 'XHO22'),
(52, 3, 6, 'eza ngenxa yezo zinto ingqumbo kaThixo phezu kwabangevayo;', 'XHO22'),
(52, 3, 7, 'enanifudula nihamba kuzo nani, oko nibe nisidla ubomi nikuzo.', 'XHO22'),
(52, 3, 8, 'Kodwa ke ngoku nani zilahleni zonke ezi zinto; ingqumbo, umsindo, ulunya, ukunyelisa; ukuthetha amanyala kungabikho emlonyeni wenu.', 'XHO22'),
(52, 3, 9, 'Musani ukuxokisana, nizihlubile nje umntu omdala, kunye nezenzo zakhe;', 'XHO22'),
(52, 3, 10, 'nambatha lowo umtsha, uhlaziyelwa ekwazini okuhle, ngokomfanekiselo walowo wamdalayo;', 'XHO22'),
(52, 3, 11, 'apho kungekho mGrike namYuda, kwaluka nokungaluki, mbharbhari namSkitiya, khoboka namkhululeki; usuke uKristu waba zizinto zonke, waba kubo bonke.', 'XHO22'),
(52, 3, 12, 'Yambathani ngoko, njengabanyulwa bakaThixo, abangcwele, abathandiweyo, iimfesane zeemfefe, ububele, ukuthobeka kwentliziyo, ubulali, ukuzeka kade umsindo;', 'XHO22'),
(52, 3, 13, 'ninyamezelana, nixolelana, ukuba ubani uthi abe nokusola ngakubani; njengokuba naye uKristu wanixolelayo, yenjani njalo nani.', 'XHO22'),
(52, 3, 14, 'Ke, phezu kwazo zonke ezo zinto, yalekani uthando, oluyintambo yogqibelelo.', 'XHO22'),
(52, 3, 15, 'Uxolo lukaThixo, enabizelwa kwakulo mzimbeni mnye, malulawule ezintliziyweni zenu, nibe nokubulela.', 'XHO22'),
(52, 3, 16, 'Ilizwi likaKristu malihlale ngaphakathi kwenu ngokobutyebi; nifundisana, nilulekana ngeendumiso neengoma nezango ezizezomoya, nivume kamnandi entliziyweni yenu kuyo iNkosi.', 'XHO22'),
(52, 3, 17, 'Nako konke enisukuba nikwenza ngelizwi, nokuba kungomsebenzi, yenzani izinto zonke egameni leNkosi uYesu, nibulela ngayo kuye uThixo uYise.', 'XHO22'),
(52, 3, 18, 'Nina bafazi, walulameleni awenu amadoda, njengoko kufanele eNkosini.', 'XHO22'),
(52, 3, 19, 'Nina madoda, bathandeni abafazi benu, ningabi krakra ngakubo.', 'XHO22'),
(52, 3, 20, 'Nina bantwana, balulameleni abazali benu ezintweni zonke; kuba oko kukholekile kuyo iNkosi.', 'XHO22'),
(52, 3, 21, 'Nina boyise, musani ukubacaphukisa abantwana benu, ukuze bangafi iintliziyo.', 'XHO22'),
(52, 3, 22, 'Nina bakhonzi, balulameleni ezintweni zonke abaziinkosi zenu ngokwenyama; kungabi ngankonzo yamehlo, ngathi ningabakholisi-bantu; makube ngokwentliziyo engenakumbi, nisoyika uThixo;', 'XHO22'),
(52, 3, 23, 'Nako konke enisukuba nikwenza, kusebenzeni ngokwasemphefumlweni, kunge kukwiNkosi, akukhona kubantu;', 'XHO22'),
(52, 3, 24, 'nisazi nje ukuba niya kuyamkela eNkosini imbuyekezo yelifa; kuba nikhonza iNkosi uKristu.', 'XHO22'),
(52, 3, 25, 'Lowo umonayo omnye uya kukuthwala oko kona one ngako; akukho kukhetha buso bamntu.', 'XHO22'),
(52, 4, 1, 'Nina baziinkosi, baphatheni ngokufanelekileyo nangokulungeleleneyo abanikhonzayo; nisazi nje ukuba nani ngokwenu nineNkosi emazulwini.', 'XHO22'),
(52, 4, 2, 'Hlalani nithandaza, nizilinde kuko oko ninombulelo;', 'XHO22'),
(52, 4, 3, 'nisithandazela kananjalo, ukuba uThixo asivulele umnyango wentetho, ukuba siyithethe imfihlelo kaKristu, endikhonkxwe ngenxa yayo;', 'XHO22'),
(52, 4, 4, 'ukuze ndiyibonakalalise, njengoko ndimelwe kukuthetha ngako.', 'XHO22'),
(52, 4, 5, 'Hambani ngobulumko ngakwabo bangaphandle, nilonga ixesha eli.', 'XHO22'),
(52, 4, 6, 'Ukuthetha kwenu makuhlale kumnandi, kuvakalisiwe ngetyuwa, ukuze nazi ukuba nimelwe kukuthini na ukuphendula bonke ngabanye.', 'XHO22'),
(52, 4, 7, 'Izinto zonke ezilunge nam uya kunazisa uTikiko, umzalwana oyintanda, umlungiseleli othembekileyo, ongumkhonzi kunye nam eNkosini;', 'XHO22'),
(52, 4, 8, 'endimthumele khona oku kuni, ukuze azazi iindawo ezingani, azithuthuzele iintliziyo zenu;', 'XHO22'),
(52, 4, 9, 'kunye no-Onesimo, umzalwana othembekileyo, oyintanda, ophuma kuni. Baya kunazisa zonke izinto ezenzekayo apha.', 'XHO22'),
(52, 4, 10, 'Uyanibulisa uAristarko, ongumbanjwa ndawonye nam, noMarko umtshana kaBharnabhas, enamkela imithetho ngaye; ukuba uthe weza kuni, mamkeleni;', 'XHO22'),
(52, 4, 11, 'noYesu, ekuthiwa nguYusto, kwabolwaluko; ngabo bodwa abasebenzisana nam ngenxa yobukumkani bukaThixo, abathe bona baba sisonwabiso kum.', 'XHO22'),
(52, 4, 12, 'Uyanibulisa uEpafra ongomnye wenu, umkhonzi kaKristu, ehlala ezama ngenxa yenu emithandazweni, ukuze nime nigqibelele, nizalisekile kuko konke ukuthanda kukaThixo.', 'XHO22'),
(52, 4, 13, 'Kuba ndiyamngqinela ukuba unenzondelelo enkulu ngenxa yenu, nabo baseLawodike, nabo baseHirapoli.', 'XHO22'),
(52, 4, 14, 'Uyanibulisa uLuka, igqirha eliyintanda, noDemas.', 'XHO22'),
(52, 4, 15, 'Imibuliso kubazalwana abaseLawodike, nakuNimfa, nakulo ibandla elisendlwini yakhe.', 'XHO22'),
(52, 4, 16, 'Xa ke le ncwadi ithe yaleswa kuni, ize nenze ukuba ileswe nasebandleni eliseLawodike; nani niyilese leyo iphuma eLawodike.', 'XHO22'),
(52, 4, 17, 'Yithini kuArkipo, Lukhangele ulungiselelo owalwamkelayo lwaseNkosini, ukuze uluzalise.', 'XHO22'),
(52, 4, 18, 'Umbuliso lo ungesam isandla, mna Pawulos. Khumbulani iintambo zam. Ubabalo malube nani. Amen. (Yabhalwa eRoma ukuya kwabaseKolose nguTikiko no-Onesimo.)', 'XHO22'),
(53, 1, 1, 'uPawulos noSilvano noTimoti babhalela ibandla labaseTesalonika, elikuThixo uYise, neNkosi yethu uYesu Kristu; bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(53, 1, 2, 'Sihlala sibulela kuye uThixo ngenxa yenu nonke; sinikhumbula emithandazweni yethu;', 'XHO22'),
(53, 1, 3, 'Ndiwukhumbula ngokungayeki umsebenzi wenu wokholo, nokubulaleka kwenu kothando, nomonde wenu wokuthemba iNkosi yethu uYesu Kristu, phambi koThixo uBawo wethu;', 'XHO22'),
(53, 1, 4, 'Sisazi, bazalwana baziintanda, ukunyulwa kwenu nguThixo.', 'XHO22'),
(53, 1, 5, 'Kaloku iindaba ezimnandi endaniphathela zona azizange zibekho kuni ngantetho yodwa; njengokuba nisazi ukuba sasinjani na phakathi kwenu ngenxa yenu.', 'XHO22'),
(53, 1, 6, 'Nani naba ngabaxelisa thina nayo iNkosi, nalamkelayo nje ilizwi nisembandezelweni eninzi, ninovuyo loMoya oyiNgcwele;', 'XHO22'),
(53, 1, 7, 'Nada naba yimifuziselo kubo bonke abakholwayo, abakwelaseMakedoni nelaseAkaya.', 'XHO22'),
(53, 1, 8, 'Kaloku lahlokoma livela kuni ilizwi leNkosi, akwaba khona kwelaseMakedoni nelaseAkaya lodwa, kusuke nakwiindawo zonke ukukholwa kwenu kuThixo; ngokokude singabi sathetha nto.', 'XHO22'),
(53, 1, 9, 'Kaloku bona aba bayasichazela ukuba kunjani na ukungena kwethu kuni, nendlela enaniguqukela ngayo kuThixo, nisuka kwizithixo, nize nikhonze uThixo ophilayo noyinyaniso,', 'XHO22'),
(53, 1, 10, 'nilinde uNyana wakhe evela emazulwini, awamvusayo kwabafileyo, kwauYesu osihlangulayo kuyo ingqumbo ezayo.', 'XHO22'),
(53, 2, 1, 'Kuba niyakwazi ngokwenu, bazalwana, ukungena kwethu kuni, ukuba akulambathanga;', 'XHO22'),
(53, 2, 2, 'Sesuka, nakuba besive ubunzima ngenxa engaphambili, siphethwe kakubi eFilipi, njengoko naziyo, asafihlisa, sikuye uThixo wethu, ukuzithetha kuni iindaba ezilungileyo zikaThixo, sinomzamo omkhulu.', 'XHO22'),
(53, 2, 3, 'Kuba uvuselelo lwethu aluphumi enkohlisweni, lungaphumi ekungcoleni, lungekho enkohlisweni.', 'XHO22'),
(53, 2, 4, 'Sithi, njengoko sicikidiweyo nguThixo, ukuba siphathiswe iindaba ezilungileyo, senjenjalo ukuthetha; singangi sikholisa abantu, kuba sikholisa uThixo ozicikidayo iintliziyo zethu.', 'XHO22'),
(53, 2, 5, 'Kaloku, njengoko naziyo, asizanga sibe nantetho yakucengacenga, namkhusane wakubawa; uThixo ulingqina:', 'XHO22'),
(53, 2, 6, 'Asifuni nokufuna luzuko bantwini, nokuba kukuni, nokuba kukwabanye; nakuba besinako ukuba ngumthwalo ngokwabapostile bakaKristu.', 'XHO22'),
(53, 2, 7, 'Sisuke saba ngabathantamisayo phakathi kwenu, njengokuba umama ebagcinile abantwana bakhe;', 'XHO22'),
(53, 2, 8, 'Ngoko ke sinilangazelela nje, kwakholeka kuthi ukuba singaneli kunabela iindaba ezilungileyo zikaThixo nje kodwa, sinabele nobomi bethu, ngenxa enokuba naba ziintanda kuthi.', 'XHO22'),
(53, 2, 9, 'Kaloku niyakukhumbula, bazalwana, ukubulaleka kwethu nemigu yethu; sazivakalisa kuni iindaba ezilungileyo zikaThixo, sisebenza ubusuku nemini, ukuze singabi mthwalo nakubani kuni.', 'XHO22'),
(53, 2, 10, 'Ningamangqina nina, kwanoThixo, ukuba saba njani na ukuba ngcwele, nokuba nobulungisa, nokungabi nakusoleka phakathi kwenu bakholwayo;', 'XHO22'),
(53, 2, 11, 'Njengoko nisazi, ukuba besiniyala, sinikhuza, siniyala elowo wenu, njengoyise esenza kubantwana bakhe;', 'XHO22'),
(53, 2, 12, 'ukuze nihambe ngokumfaneleyo uThixo, onibizelayo kobakhe ubukumkani nozuko.', 'XHO22'),
(53, 2, 13, 'Ngenxa yoko nathi siyabulela kuye uThixo singayeki, ukuba nathi, ukulamkela ilizwi likaThixo lodaba kuthi, analamkela lililizwi labantu; nalamkela njengokuba kunjalo okwenyaniso, lililizwi likaThixo elisebenzayo nokusebenzayo, nakuni bakholwayo.', 'XHO22'),
(53, 2, 14, 'Kuba nani, bazalwana, naba ngabaxelisa amabandla kaThixo akwelakwaYuda, akuKristu Yesu;', 'XHO22'),
(53, 2, 15, 'wona ayibulalayo iNkosi uYesu kwanabawo abaprofeti, asitshutshisa nathi; abamkholisi uThixo, bachasene nabantu bonke;', 'XHO22'),
(53, 2, 16, 'esalela thina ukuba sithethe kuzo iintlanga, ukuze zisindiswe, esenzela ukuba azalise izono zawo ngamaxesha onke. Iwafikele ke ingqumbo, kwada kwasekupheleni.', 'XHO22'),
(53, 2, 17, 'Thina ke, bazalwana, sanihluthwayo nje ixesha elifutshane ngesiqu, kungengantliziyo, sakhuthalela ngokugqithiseleyo ukububona ubuso benu, sinqwena kakhulu.', 'XHO22'),
(53, 2, 18, 'Kungoko sathandayo ukuza kuni, mna Pawulos lo, kwakanye nakabini; kodwa uSathana wasithintela.', 'XHO22'),
(53, 2, 19, 'Kaloku liyintoni na ithemba lethu, novuyo lwethu, nesithsaba sethu soqhayiso? Asinini na, phambi kweNkosi yethu uYesu Kristu ekufikeni kwayo?', 'XHO22'),
(53, 2, 20, 'Inene, nini uzuko novuyo lwethu.', 'XHO22'),
(53, 3, 1, 'Kungoko kwathi, singasenakuzibamba, kwakholeka kuthi ukuba sisale eAtene sedwa.', 'XHO22'),
(53, 3, 2, 'Sithume uTimoti, umzalwana wethu, umlungiseleli kaThixo, ongugxa wethu kwiindaba ezilungileyo zikaKristu, ukuze anizimase, anithuthuzele ngenxa yokholo lwenu;', 'XHO22'),
(53, 3, 3, 'ukuze kungabikho namnye ushukunyiswayo zezi mbandezelo, kuba nisazi ngokwenu ukuba simiselwe zona.', 'XHO22'),
(53, 3, 4, 'Kuba xenikweni besikuni, sanixelela ngenxa engaphambili ukuba siza kubandezelwa; njengokuba kwaba njalo, nani niyazi.', 'XHO22'),
(53, 3, 5, 'Ngenxa yoko ke ndingasenakuzibamba, ndathumela ukuba ndilwazi ukholo lwenu, hleze abe unihendile uMhendi, saba sifumane sabulaleka.', 'XHO22'),
(53, 3, 6, 'Ke kaloku, ngoku uTimoti, akufika evela kuni, weza kuthi, wasishumayeza iindaba ezilungileyo zokholo nothando lwenu;', 'XHO22'),
(53, 3, 7, 'Sithuthuzelekile ngoko, bazalwana, ngani, kuyo yonke imbandezelo nembandezelo yethu, ngalo ukholo lwenu;', 'XHO22'),
(53, 3, 8, 'Kuba ngoku siphilile, ukuba nina nithi nime eNkosini.', 'XHO22'),
(53, 3, 9, 'Kuba singambuyekeza ngambulelo mni na uThixo ngani, kulo lonke uvuyo esivuya ngalo ngenxa yenu phambi koThixo wethu?', 'XHO22'),
(53, 3, 10, 'Ubusuku nemini sikhunga ngokugqithiseleyo kakhulu, ukuba sibubone ubuso benu, sikuzalise ukusilela kokholo lwenu?', 'XHO22'),
(53, 3, 11, 'Wanga ke uThixo ngokwakhe, uBawo wethu, neNkosi yethu uYesu Kristu, angayilungisa indlela yethu yokuza kuni.', 'XHO22'),
(53, 3, 12, 'Yanga ke iNkosi inganandisa, niphuphume eluthandweni, omnye komnye nakubo bonke, kwanjengokuba sisenjenjalo kuni;', 'XHO22'),
(53, 3, 13, 'ukuze izizimase iintliziyo zenu, zingabi nakusoleka, zibe ngcwele phambi koThixo uBawo wethu, ekufikeni kweNkosi yethu uYesu Kristu nabo bonke abangcwele bayo.', 'XHO22'),
(53, 4, 1, 'Elokugqiba ngoko, bazalwana, siyanicela siniyala siseNkosini uYesu, ukuba nithi, njengokuba nakwamkelayo kuthi, okokuba nimelwe kukuthini na ukuhamba nokumkholisa uThixo, nikhule kuko ngakumbi.', 'XHO22'),
(53, 4, 2, 'Kaloku niyayazi imiyalelo esaninika yona ngeNkosi uYesu.', 'XHO22'),
(53, 4, 3, 'Kuba oku kukuthanda kukaThixo, ukungcwaliswa kwenu, ukuba nikhumke kulo uhenyuzo;', 'XHO22'),
(53, 4, 4, 'ukuze elowo kuni akwazi ukusizuza asiphathe esakhe isitya enobungcwalisa, enembeko;', 'XHO22'),
(53, 4, 5, 'kungabi ngenkanuko, njengeentlanga ezingamaziyo uThixo;', 'XHO22'),
(53, 4, 6, 'Ukuba kungabikho ugqithisayo, amlumkise umzalwana wakhe emcimbini lowo, ngenxa enokuba iNkosi ngumphindezeli wazo zonke ezo zinto, njengoko sasinixelele ngenxa engaphambili, saniqononondisa.', 'XHO22'),
(53, 4, 7, 'Kuba uThixo akasibizelanga ekungcoleni; usibizele ebungcwaliseni.', 'XHO22'),
(53, 4, 8, 'Ngoko ke lowo ugibayo, akagibi mntu, ugiba uThixo, owasinikayo nokusinika uMoya wakhe oyiNgcwele.', 'XHO22'),
(53, 4, 9, 'Ke kaloku, ngalo uthando lobuzalwana, akufuneki ukuba ndinibhalele, kuba nina ngokwenu ningabafundiswe nguThixo ukuba nithandane.', 'XHO22'),
(53, 4, 10, 'Okunene niyakwenza oko kubo bonke abazalwana abakwelaseMakedoni liphela. Ke siyanibongoza, bazalwana, ukuba nikhule kuko oko ngakumbi;', 'XHO22'),
(53, 4, 11, 'Nikhuthalele ukuzola, nenze imicimbi yenu, nisebenze ngezandla zenu, njengoko saniwisela umthetho ngako;', 'XHO22'),
(53, 4, 12, 'ukuze nihambe ngokunyanisekileyo ngakwabo bangaphandle, ningasweli nto.', 'XHO22'),
(53, 4, 13, 'Ke kaloku andithandi ukuba ningazi, bazalwana, mayela nabalele ukufa, ukuze ningabi buhlungu njengabanye abangenathemba bona.', 'XHO22'),
(53, 4, 14, 'Kuba xa sikholwa ukuba uYesu wafa, wabuya wavuka, kwangokunjalo naye uThixo woza nabo abalele ukufa ngaye uYesu, kunye naye.', 'XHO22'),
(53, 4, 15, 'Kuba oku siyakutsho kuni ngelizwi leNkosi ukuthi, thina babudlayo ubomi, baya kuba besekho ekufikeni kweNkosi, asisayi kubaphangela abalele ukufa.', 'XHO22'),
(53, 4, 16, 'Ngokuba iNkosi ngokwayo iya kuhla emazulwini inendanduluko, inelizwi lesiphatha-zithunywa, inexilongo likaThixo, baze abafele kuKristu bavuke kuqala;', 'XHO22'),
(53, 4, 17, 'sandule thina babudlayo ubomi, basaseleyo, sixwilelwe emafini kwakunye nabo, siye kuyikhawulela iNkosi esibhakabhakeni; size ngokunjalo sihlale sihleli, sindawonye neNkosi.', 'XHO22'),
(53, 4, 18, 'Ngoko ke, thuthuzelanani ngala mazwi.', 'XHO22'),
(53, 5, 1, 'Ke kaloku ngawo amaxesha namathuba, bazalwana, akufuneki nganto ukuba ndinibhalele;', 'XHO22'),
(53, 5, 2, 'kuba nani ngokwenu niyazi ngokucacileyo, ukuba imini yeNkosi, njengesela ebusuku, yenjenjalo ukuza kwayo.', 'XHO22'),
(53, 5, 3, 'Kuba xenikweni baya kuthi, Luxolo nokunqaba; oko baya kufikelwa yintshabalaliso ngebhaqo, kwanjengenimba komithiyo; baye bengayi kusinda.', 'XHO22'),
(53, 5, 4, 'Ke nina, bazalwana, ànisebumnyameni, ukuba imini leyo iniqubule njengesela.', 'XHO22'),
(53, 5, 5, 'Nina nonke ningoonyana bokukhanya, ningabantwana bemini; asingabobusuku, asingabobumnyama.', 'XHO22'),
(53, 5, 6, 'Ngoko masingalali, njengabanye aba; masiphaphe sibe ziingcathu.', 'XHO22'),
(53, 5, 7, 'Kuba abaleleyo balala ebusuku; nabanxilayo banxila ebusuku.', 'XHO22'),
(53, 5, 8, 'Ke thina bangabemini masibe ziingcathu; masinxibe isigcina-sifuba sokholo nothando; nesigcina-ntloko esilithemba losindiso.', 'XHO22'),
(53, 5, 9, 'Kuba uThixo akasimiselanga ngqumbo; usimisele ukuzuza usindiso ngayo iNkosi yethu uYesu Kristu;', 'XHO22'),
(53, 5, 10, 'owasifelayo, ukuze nokuba sihleli, nokuba silele, sidle ubomi kwakunye naye.', 'XHO22'),
(53, 5, 11, 'Kungoko ndithi, Thuthuzelanani, nakhane, kwanjengokuba nisenjenjalo.', 'XHO22'),
(53, 5, 12, 'Ke kaloku siyanicela, bazalwana, ukuba nibazi ababulalekayo phakathi kwenu, abanongamelayo eNkosini, abanilulekayo,', 'XHO22'),
(53, 5, 13, 'nibabeke ngokugqithiseleyo kakhulu ukubathanda, ngenxa yomsebenzi wabo. Hlalani ngoxolo phakathi kwenu.', 'XHO22'),
(53, 5, 14, 'Siyanicela ke, bazalwana, bakhalimeleni abanxaxhayo, bathuthuzeleni abathambileyo;', 'XHO22'),
(53, 5, 15, 'Lumkani, kungabikho bani ubuyekeza ububi ngobubi nakubani; hlalani niphuthuma okulungileyo omnye komnye, nakubantu bonke.', 'XHO22'),
(53, 5, 16, 'Vuyani amaxesha onke;', 'XHO22'),
(53, 5, 17, 'Thandazani ningaphezi;', 'XHO22'),
(53, 5, 18, 'Ezintweni zonke bulelani; kuba oko kukuthanda kukaThixo kuKristu Yesu ngani.', 'XHO22'),
(53, 5, 19, 'Musani ukuwucima uMoya;', 'XHO22'),
(53, 5, 20, 'Iziprofeto musani ukuzenza into engento.', 'XHO22'),
(53, 5, 21, 'Zicikideni zonke izinto; nibambe leyo intle.', 'XHO22'),
(53, 5, 22, 'Khumkani kuzo zonke iintlobo zenkohlakalo.', 'XHO22'),
(53, 5, 23, 'Wanga ke uThixo woxolo ngokwakhe anganingcwalisa niphelelisekile; uthi, ugqibelele owenu umoya nomphefumlo nomzimba, ugcinwe ngokungenakusoleka ekufikeni kweNkosi yethu uYesu Kristu.', 'XHO22'),
(53, 5, 24, 'Uthembekile onibizayo, oya kukwenza nokukwenza oko.', 'XHO22'),
(53, 5, 25, 'Mawethu, sithandazeleni.', 'XHO22'),
(53, 5, 26, 'Babuliseni abazalwana bonke ngolwango olungcwele.', 'XHO22'),
(53, 5, 27, 'Ndiyanifungisa iNkosi ukuba le ncwadi ileswe kubo bonke abazalwana abangcwele.', 'XHO22'),
(53, 5, 28, 'Ubabalo lweNkosi yethu uYesu Krestu malube nani. Amen. (Incwadi yokuqala eya kwabaseTesalonika yabhalwa eAtene.)', 'XHO22'),
(54, 1, 1, 'uPawulos noSilvano noTimoti babhalela ibandla labaseTesalonika elikuThixo uBawo wethu, neNkosi yethu uYesu Kristu;', 'XHO22'),
(54, 1, 2, 'bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(54, 1, 3, 'Simelwe kukuhlala sibulela kuThixo ngenxa yenu, bazalwana, njengokuba kufanelekile, ngokuba lukhula ngokugqithiseleyo ukholo lwenu, lusanda uthandano lwenu nonke ngabanye;', 'XHO22'),
(54, 1, 4, 'Ngoko ke siyaqhayisa ngani phakathi kwamabandla kaThixo, ngenxa yonyamezelo lwenu, nokholo lwenu, ezintshutshisweni zenu zonke, neembandezelo enizinyamezelayo.', 'XHO22'),
(54, 1, 5, 'Nguwo lowo umbonakalaliso womgwebo onobulungisa kaThixo, ukuze nibalelwe ekuthini nibufanele ubukumkani bukaThixo, enithi ngenxa yabo nive nokuva ubunzima;', 'XHO22'),
(54, 1, 6, 'ukuba kanti yinto ebubulungisa kuThixo ukubabuyekeza ngembandezelo abanibandezelayo;', 'XHO22'),
(54, 1, 7, 'Nina ke, nina babandezelwayo, aniphumle kunye nathi, ekutyhilekeni kweNkosi uYesu, ivela emazulwini inezithunywa zayo ezinamandla;', 'XHO22'),
(54, 1, 8, 'inomlilo odangazelayo, ibaphindezela abangamaziyo uThixo, nabangazilulameliyo iindaba ezilungileyo zeNkosi yethu uYesu Kristu;', 'XHO22'),
(54, 1, 9, 'bona abo baya kuva ukubethwa, intshabalalo engunaphakade ke leyo bemke ebusweni beNkosi, nasebuqaqawulini bokomelela kwayo;', 'XHO22'),
(54, 1, 10, 'Xa ithe yeza kuzukiswa kubangcwele bayo, imangaliseke kubo bonke abakholwayo (ngokuba nakholwa bubungqina bethu kuni), ngaloo mini.', 'XHO22'),
(54, 1, 11, 'Kungoko sihlala sinithandazela, ukuba uThixo wethu anibalele ekuthini nilufanele ubizo olu, azalise ngamandla onke ukuthanda kwakhe ukulunga, nomsebenzi wokholo;', 'XHO22'),
(54, 1, 12, 'ukuze lizukiswe igama leNkosi yethu uYesu Kristu kuni, nani nizukiswe kuye, ngokobabalo lukaThixo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(54, 2, 1, 'Ke kaloku siyanicela, bazalwana, ngokufika kweNkosi yethu uYesu Kristu, nangokuhlanganiselwa kwethu kuyo,', 'XHO22'),
(54, 2, 2, 'ukuze ningazanyazanyiswa kwamsinya, nahlukane nengqiqo;', 'XHO22'),
(54, 2, 3, 'Makungabikho bani unilukuhlayo nangalunye uhlobo; ngokuba ayinakufika yona, lungakhanga lufike ulahlo kuqala, atyhileke loo mntu wesono, unyana wentshabalalo;', 'XHO22'),
(54, 2, 4, 'Lowo uchasayo, uziphakamisayo ngaphezu kwento yonke ekuthiwa nguThixo, nehlonelwayo; ngokokude azihlalise etempileni kaThixo, ezenza uThixo, ezibonisa ukuba unguye uThixo.', 'XHO22'),
(54, 2, 5, 'Anikhumbuli na ukuba oko bendisekuni ndanixelela ezi zinto?', 'XHO22'),
(54, 2, 6, 'Nangoku niyakwazi okubambezelayo, ukuze atyhileke ngelakhe ixesha.', 'XHO22'),
(54, 2, 7, 'Kuba imfihlelo yokuchasana nomthetho iselisebenza;', 'XHO22'),
(54, 2, 8, 'Uya kutyhileka ke lowo ungendawo, eya kuthi iNkosi imtshabalalise ngomoya womlomo wayo, imbhangise ngokubonakala kokufika kwayo;', 'XHO22'),
(54, 2, 9, 'Lowo ukufika kwakhe kungokokusebenza kukaSathana, ngayo yonke imisebenzi yamandla, nemiqondiso, nezimanga zobuxoki,', 'XHO22'),
(54, 2, 10, 'nangako ukulukuhla konke kokungalungisi kwabo batshabalalayo; ngokuba bengakwamkelanga ukuyithanda inyaniso, ukuze basindiswe.', 'XHO22'),
(54, 2, 11, 'Ngenxa yoko uThixo uya kubathumela ulahlekiso olusebenza ngamandla, ukuze bakholwe bubuxoki;', 'XHO22'),
(54, 2, 12, 'ukuze bagwetywe bonke abangakholwanga yiyo inyaniso, basuka bakholiswa kuko ukungalungisi.', 'XHO22'),
(54, 2, 13, 'Ke thina simelwe kukuhlala sibulela kuye uThixo ngenxa yenu, bazalwana bathandiweyo yiNkosi, ukuba uThixo kwasekuqalekeni waninyulela ukusindiswa, ngokungcwalisa koMoya nokukholwa yinyaniso;', 'XHO22'),
(54, 2, 14, 'awanibizela kuko oko ngazo iindaba ezilungileyo zethu, ukuba nizuze uzuko lweNkosi yethu uYesu Kristu.', 'XHO22'),
(54, 2, 15, 'Ngoko ke, bazalwana, yimani, nizibambe izithethe enazifundiswayo, nokuba kungelizwi, nokuba kungencwadi yethu.', 'XHO22'),
(54, 2, 16, 'Ke kaloku iNkosi yethu uYesu Kristu ngokwayo, noThixo uBawo wethu owasithandayo, ngobabalo wasinika intuthuzelo engunaphakade, nethemba elilungileyo;', 'XHO22'),
(54, 2, 17, 'Wazithuthuzela iintliziyo zenu, anizimase kuwo onke amazwi nemisebenzi elungileyo.', 'XHO22'),
(54, 3, 1, 'Elokugqiba, sithandazeleni, bazalwana, ukuba ilizwi leNkosi likhululeke, lizukiswe, njengoko lenjenjalo nakuni;', 'XHO22'),
(54, 3, 2, 'nokuba sihlangulwe ebantwini abangendawo, abakhohlakeleyo; kuba ukholo alubantwini bonke.', 'XHO22'),
(54, 3, 3, 'Ithembekile ke yona iNkosi, eya kunizimasa, inigcine kuyo inkohlakalo.', 'XHO22'),
(54, 3, 4, 'Sikholose ngani ke ngokwaseNkosini, ukuba izinto esinithethela zona niyazenza, nabe nisaya kuzenza.', 'XHO22'),
(54, 3, 5, 'Yayingathi iNkosi iintliziyo zenu izikhokelele eluthandweni lukaThixo, nasekunyamezeleni uKrestu.', 'XHO22'),
(54, 3, 6, 'Ke kaloku siyanithethela, bazalwana, egameni leNkosi yethu uYesu Kristu, ukuba nikhwebuke kubo bonke abazalwana abahamba ngokunxaxha, bengahambi ngokwesithethe abasamkelayo kuthi.', 'XHO22'),
(54, 3, 7, 'Kuba niyazi ngokwenu, ukuba nimelwe kukuthini na ukusilinganisa; ngokuba asinxaxhanga phakathi kwenu;', 'XHO22'),
(54, 3, 8, 'Kuba asidlanga sonka samntu ngelize; sisebenza nzima nangemigudu ubusuku nemini, ukuze singabi mthwalo nakubani kuni;', 'XHO22'),
(54, 3, 9, 'kungekuba singenagunya; senzela ukuba sibe ngumzekelo kuni, ukuze nisilandele.', 'XHO22'),
(54, 3, 10, 'Kuba naxenikweni besikuni, sanithethela ngelithi, Ukuba umntu akathandi kusebenza, makangadli nokudla.', 'XHO22'),
(54, 3, 11, 'Kuba siyeva ukuba inxenye phakathi kwenu ihamba ngokunxaxha, ingasebenzi nento, izifaka kwizinto zabanye.', 'XHO22'),
(54, 3, 12, 'Abanjalo ke siyabathethela, sibavuselela ngayo iNkosi yethu uYesu Kristu, ukuba bathi, besebenza ngokuzola, badle esisesabo isonka.', 'XHO22'),
(54, 3, 13, 'Ke nina, bazalwana, musani ukutha amandla ekwenzeni okulungileyo.', 'XHO22'),
(54, 3, 14, 'Ukuba ke kukho bani ungalilulameliyo ilizwi lethu ngencwadi le, mqondeni lowo, ningazidibanisi naye, ukuze adane.', 'XHO22'),
(54, 3, 15, 'Noko ke musani ukumgqala njengotshaba;', 'XHO22'),
(54, 3, 16, 'Yaye ke iNkosi yoxolo ngokwayo inganinika uxolo ngamaxa onke, ngeendlela zonke. iNkosi ibe nani nonke.', 'XHO22'),
(54, 3, 17, 'Umbuliso ngesam isandla, mna Pawulos. Nguwo lowo umqondiso kuzo zonke iincwadi; ndenjenjalo ukubhala.', 'XHO22'),
(54, 3, 18, 'Ubabalo lweNkosi yethu uYesu Krestu malube nani nonke. Amen. (Incwadi yesibini eya kwabaseTesalonika yabhalwa eAtene.)', 'XHO22'),
(55, 1, 1, 'uPawulos, umpostile kaYesu Kristu ngokomthetho kaThixo uMsindisi wethu, neNkosi yethu uYesu Kristu, ithemba lethu;', 'XHO22'),
(55, 1, 2, 'ubhalela uTimoti, umntwana wakhe kanye ngokwaselukholweni; uthi, Makube lubabalo, inceba, uxolo, oluvela kuThixo uBawo, noKristu Yesu iNkosi yethu.', 'XHO22'),
(55, 1, 3, 'Njengoko ndakuyalayo ukuba uhlale e-Efese, ndakuba mna ndisiya kwelaseMakedoni, uhlale e-Efese, ukuze ubathethele abanye, bangafundisi mfundiso yimbi;', 'XHO22'),
(55, 1, 4, 'kananjalo banganyamekeli zintsomi namilibo yakuzalwa engaphele ndawo, izinto ezo ezibanga iimbuzwano, zisingise elukholweni;', 'XHO22'),
(55, 1, 5, 'Ke intsingiselo yomthetho iluthando oluphuma entliziyweni ehlambulukileyo, nasesazeleni esilungileyo, naselukholweni olungahanahanisiyo;', 'XHO22'),
(55, 1, 6, 'abathe abathile, beziphosile kuzo, baphambukela ekuthetheni okungento yanto;', 'XHO22'),
(55, 1, 7, 'bethanda ukuba ngabafundisi bomthetho; bengaziqondi izinto abazithethayo, kwanezingqinayo.', 'XHO22'),
(55, 1, 8, 'Siyazi ke ukuba umthetho mhle, ukuba umntu uthi awuphathe ngokomthetho;', 'XHO22'),
(55, 1, 9, 'nisazi nje oku, ukuba umthetho lo awuwiselwe lilungisa, uwiselwe abachasene nomthetho, nabangevayo, abangahloneli Thixo, nabangaboni, abangengcwele, abahlambelayo, ababulali ooyise, ababulali oonina, izigwinta;', 'XHO22'),
(55, 1, 10, 'Abenzi bombulo, amadoda azenza iinqambi, amasela abantu, amaxoki, amafunga-buxoki, nokuba kukho nto yimbi ichasene nemfundiso ephilileyo;', 'XHO22'),
(55, 1, 11, 'ngokweendaba ezilungileyo zozuko lukaThixo osikelelekileyo, endaphathiswa zona mna.', 'XHO22'),
(55, 1, 12, 'Ndiyambulela uKrestu Yesu iNkosi yethu, owandinika amandla, kuba wandibalela ekuthini ndithembekile, endimisela ebulungiselelini.', 'XHO22'),
(55, 1, 13, 'Ndandifudula ndingumnyelisi, nomtshutshisi, nomphathi kakubi; ndesuka ndenzelwa inceba, ngokuba ndakwenza oko ndingazi, ndisekungakholweni;', 'XHO22'),
(55, 1, 14, 'Yanda ngokuncamisileyo ubabalo lweNkosi yethu, lunokholo nothando olukuKristu Yesu.', 'XHO22'),
(55, 1, 15, 'Lithembekile ilizwi eli, lifanelwe kukwamkelwa konke, eli lokuba uKristu Yesu weza ehlabathini ukuba asindise aboni; endingomkhulu kubo;', 'XHO22'),
(55, 1, 16, 'Noko ke ndesuka ndenzelwa inceba ngenxa yoku, ukuze uYesu Kristu akubonakalalisele kum, mna ungomkhulu, konke ukuzeka kade umsindo, ndibe ngumzekelo kwabo baza kukholwa kuye, base ebomini obungunaphakade.', 'XHO22'),
(55, 1, 17, 'Kuye uKumkani wawo amaphakade, ongenakonakala, ongenakubonakala, kuThixo olumkileyo yedwa, makubekho imbeko nozuko, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(55, 1, 18, 'Loo myalelo ndiwunika wena Timoti, mntwana wam, ngokweziprofeto ezandulelayo ngawe, ukuze ulwe imfazwe entle ngazo.', 'XHO22'),
(55, 1, 19, 'Yiba nokholo, nesazela esilungileyo; abathe abathile balululahlile ukholo, baphukelwa ngumkhombe;', 'XHO22'),
(55, 1, 20, 'inoHimenayo noAlesandire; endibanikele kuSathana, ukuze bafunde ukunganyelisi.', 'XHO22'),
(55, 2, 1, 'Ndiyavuselela ngoko kuqala kwiinto zonke, ukuba kwenziwe izikhungo, imithandazo, izibongozo, imibulelo, ngenxa yabantu bonke;', 'XHO22'),
(55, 2, 2, 'Ngenxa yookumkani nabo bonke abasekongameni; ukuze sihlale ngokonwaba nangokuzola, sinokuhlonela uThixo, nokundileka konke.', 'XHO22'),
(55, 2, 3, 'Kuba oku kuhle, kwamkelekile emehlweni oMsindisi wethu uThixo;', 'XHO22'),
(55, 2, 4, 'onga yena bangasindiswa bonke abantu, bafikelele ekuyazini inyaniso.', 'XHO22'),
(55, 2, 5, 'Kuba mnye uThixo, mnye nomlamli phakathi koThixo nabantu, umntu uKristu Yesu;', 'XHO22'),
(55, 2, 6, 'lowo wazinikelayo ukuba abe yintlawulelo yokukhulula abantu bonke; ubungqina ke obo bokungqinwa ngamaxesha angawabo;', 'XHO22'),
(55, 2, 7, 'endithe ndamiselwa bona mna, ukuba ndibe ngumshumayeli nompostile (ndithetha inyaniso ndikuKristu, andixoki), umfundisi weentlanga elukholweni nasenyanisweni.', 'XHO22'),
(55, 2, 8, 'Ngoko ndinga angathandaza amadoda kuzo zonke iindawo, ephakamisa izandla ezingcwele, engenangqumbo, engenantandabuzo.', 'XHO22'),
(55, 2, 9, 'Kwangokunjalo mabathi nabafazi banxibe iingubo ezifanelekileyo, beneentloni, benesidima; Ingabi kukuphotha iinwele, nokunxitywa kwegolide neeperile, nesambatho esixabiso likhulu;', 'XHO22'),
(55, 2, 10, 'mabathi (ngokufanele abafazi abathi bahlonela uThixo), bavathe imisebenzi elungileyo.', 'XHO22'),
(55, 2, 11, 'Umfazi makafunde ethe cwaka, enokuzithoba konke.', 'XHO22'),
(55, 2, 12, 'Kodwa andimvumeli umfazi ukuba afundise, nokuba ayilawule indoda; kodwa makathi cwaka.', 'XHO22'),
(55, 2, 13, 'Kuba kwabunjwa uAdam kuqala, kwandula ke ukubunjwa uEva.', 'XHO22'),
(55, 2, 14, 'Ke kaloku akakhohliswanga uAdam, kodwa ngumfazi owalukuhlwayo waba sisigqitho.', 'XHO22'),
(55, 2, 15, 'Noko ke wosindiswa ngokuzala, ukuba bathe bema elukholweni, naseluthandweni, nasebungcwaliseni, benesidima.', 'XHO22'),
(55, 3, 1, 'Lithembekile ilizwi eli lithi, Ukuba ubani ungxamele ubuveleli, unqwenela umsebenzi omhle.', 'XHO22'),
(55, 3, 2, 'Umveleli ngoko umelwe kukuthi abe ngongenakukhalazelwa, eyindoda yamfazi mnye, eyingcathu, enesidima, ezibekile, ebuka iindwendwe, enokufundisa;', 'XHO22'),
(55, 3, 3, 'Engasengazilahleli kwiwayini, engengomntu unxanela inzuzo embi; Uzeke kade umsindo, angabi ngolwa, angabi ngabawa;', 'XHO22'),
(55, 3, 4, 'eyongamele kakuhle eyakhe indlu, abantwana bakhe bemthobele, benokundileka konke.', 'XHO22'),
(55, 3, 5, '(Kuba ukuba umntu akakwazi ukuyongamela eyakhe indlu, angathini na ukuphatha ibandla likaThixo?)', 'XHO22'),
(55, 3, 6, 'angabi ngosandula ukuvela, hleze akhukhumele, awele ekugwetyweni nguMtyholi.', 'XHO22'),
(55, 3, 7, 'Ngaphezu koko ke umelwe kukuthi abe nobungqina obuhle obuvela kwabangaphandle; hleze awele engcikivweni nasesibatheni soMtyholi.', 'XHO22'),
(55, 3, 8, 'Kwangokunjalo abalungiseleli bamelwe kukuba nokundileka, bangabi ntlolela yombini, bangazinikeli kwiwayini eninzi, bangangxameli nzuzo imbi;', 'XHO22'),
(55, 3, 9, 'mababe nemfihlo yokholo esazeleni esihlambulukileyo.', 'XHO22'),
(55, 3, 10, 'Nabo aba ke mabacikidwe kuqala; mabandule ke benze umsebenzi wobudikoni, bengenakusoleka.', 'XHO22'),
(55, 3, 11, 'Kwangokunjalo abafazi bamelwe kukuba nokundileka, bangabi ngabahlebi; mababe nesidima, bathembeke ezintweni zonke.', 'XHO22'),
(55, 3, 12, 'Abalungiseleli mababe ngamadoda abafazi ngabanye, besongamela kakuhle abantwana nezindlu ezizezabo.', 'XHO22'),
(55, 3, 13, 'Kuba abo balungiselela kakuhle bazizuzela isimo esihle, nokungafihlisi okukhulu ekukholweni kuKristu Yesu.', 'XHO22'),
(55, 3, 14, 'Ezi zinto ndizibhala kuwe, ndithembe ukuba ndiya kuza kuwe kamsinya.', 'XHO22'),
(55, 3, 15, 'Ukuba ke ndithe ndalibala, ndikubhalela ukuze wazi, ukuba kumelwe kukuthiwani na ukuhanjwa endlwini kaThixo, yona ilibandla likaThixo ophilileyo, intsika nesiseko sayo inyaniso.', 'XHO22'),
(55, 3, 16, 'Kananjalo, ngokuvunyiweyo, inkulu imfihlelo yona leyo yokuhlonela uThixo: wabonakalaliswa esenyameni, wagwetyelwa esemoyeni, wabonakala kwizithunywa zezulu, wavakaliswa phakathi kweentlanga, kwakholwa kuye ehlabathini, wenyuswa waba sebuqaqawulini.', 'XHO22'),
(55, 4, 1, 'Ke kaloku uMoya uthi ngokucacileyo inxenye iya kumka elukholweni, ngamaxesha asemva, inyamekele oomoya abalahlekisayo, neemfundiso zeedemon;', 'XHO22'),
(55, 4, 2, 'Othetha ubuxoki ngohanahaniso; betshiswe isazela sabo ngentsimbi;', 'XHO22'),
(55, 4, 3, 'bengavumi ukuba kuzekwe, nokuba kudliwe izinto ezidalwe nguThixo, ukuba zamkelwe ngombulelo ngabo bakholwayo, bayaziyo inyaniso.', 'XHO22'),
(55, 4, 4, 'Kuba sonke isidalwa sikaThixo sihle; asiyonto yakulahlwa, ukuba samkelwe ngombulelo;', 'XHO22'),
(55, 4, 5, 'Kuba singcwaliswa ngelizwi likaThixo nangomthandazo.', 'XHO22'),
(55, 4, 6, 'Ukuba uthe ezi zinto wazibeka phambi kwabazalwana, woba ngumlungiseleli omhle kaYesu Kristu, wondliwe ngamazwi okholo, nawemfundiso entle oyilandelayo.', 'XHO22'),
(55, 4, 7, 'Ke zona iintsomi ezihlambelayo zamaxhegokazi zigatye;', 'XHO22'),
(55, 4, 8, 'Kuba eyeyomzimba ingqeqesho inceda kancinane; ke kona ukuhlonela uThixo kunceda ngako konke, kunedinga lobomi bakalokunje, nobobo buza kubakho.', 'XHO22'),
(55, 4, 9, 'Lithembekile ilizwi eli, lifanelwe kukwamkelwa konke.', 'XHO22'),
(55, 4, 10, 'Kuba sibulalekela, singcikivelwe oko; ngokuba sithembele kuThixo ophilileyo, onguMsindisi wabantu bonke, ngokukodwa owabakholwayo.', 'XHO22'),
(55, 4, 11, 'Bathethele ezi zinto, ufundise zona.', 'XHO22'),
(55, 4, 12, 'Makungabikho namnye ubudelayo ubuncinane bakho; suka ube ngumzekelo wabakholwayo ngentetho, ngehambo, ngothando, ngokholo, ngobunyulu.', 'XHO22'),
(55, 4, 13, 'Nyamekela ukufunda, uvuselelo, imfundiso, ndide ndifike.', 'XHO22'),
(55, 4, 14, 'Ungasihilizeli isipho esingokubabalwa, esikuwe; owasinikwayo ngesiprofeto, kunye nokubekwa izandla yintlanganiso yamadoda amakhulu.', 'XHO22'),
(55, 4, 15, 'Zinyamekele ezo zinto; zinikezele ngokupheleleyo kuzo; ukuze ukuphumelela kwakho kubonakale kwinto yonke.', 'XHO22'),
(55, 4, 16, 'Zilumkele wena nemfundiso yakho; hlala uhleli kuzo; kuba ngokwenza oko, wozisindisa wena, ubasindise nabo bakuvayo.', 'XHO22'),
(55, 5, 1, 'Indoda enkulu musa ukuyithethisa kabukhali, yiyale njengoyihlo; amancinane, njengabazalwana;', 'XHO22'),
(55, 5, 2, 'Abafazi abakhulu, njengoonyoko; abancinane, njengoodade, unobunyulu bonke.', 'XHO22'),
(55, 5, 3, 'Babeke abahlolokazi abangabahlolokazi ngenene.', 'XHO22'),
(55, 5, 4, 'Ke ukuba kukho mhlolokazi uthile unabantwana, nokuba ngabazukulwana, ukuqala mabafunde ukuhlonela elabo ikhaya, bababuyekezele umbuyekezo ooninakhulu; kuba oko kuhle, kwamkelekile emehlweni kaThixo.', 'XHO22'),
(55, 5, 5, 'Ke yena ongumhlolokazi ngenene, eshiywe yedwa, uthembela kuThixo;', 'XHO22'),
(55, 5, 6, 'Kodwa yena oxhamla iziyolo ufile, nakuba ngathi udla ubomi.', 'XHO22'),
(55, 5, 7, 'Bathethele ezi zinto, ukuze bangabi nakusoleka.', 'XHO22'),
(55, 5, 8, 'Ukuba ke umntu akabakhathalele abakhe, ngokukodwa abendlu yakhe, ulukhanyele ukholo, unobubi ngaphezu kongakholwayo.', 'XHO22'),
(55, 5, 9, 'Makangabalelwa enanini lomhlolokazi ongaphantsi kweminyaka emashumi mathandathu, engumfazi wandoda-nye;', 'XHO22'),
(55, 5, 10, 'engqinelwe ngemisebenzi emihle; ukuba wondle abantwana, ukuba wamkele iindwendwe, ukuba uzihlambile iinyawo zabangcwele, ukuba ubasizile ababandezelweyo, ukuba usukelene nemisebenzi yonke elungileyo.', 'XHO22'),
(55, 5, 11, 'Ke bona abahlolokazi abatsha bamangale; kuba xa bathe badimala kuKristu, baya kutshata;', 'XHO22'),
(55, 5, 12, 'benesigwebo ngenxa yokuba belulahle ukholo lwabo lokuqala.', 'XHO22'),
(55, 5, 13, 'Kunye noko ke bafunda ukungasebenzi nto, bethungelana nezindlu; banganeli kungasebenzi nto kodwa, besuka kananjalo babe ngamapholopholo, bazifake kwizinto zabanye, bathethe izinto ezingafanelekileyo.', 'XHO22'),
(55, 5, 14, 'Ngoko ndinga abahlolokazi abatsha bangenda, bazale, babe ngabanikazi-mizi, bangamniki ochasayo nalinye ithuba lokutshabhisa.', 'XHO22'),
(55, 5, 15, 'Kuba inxenye iseyiphambukele emva koSathana.', 'XHO22'),
(55, 5, 16, 'Ukuba kukho abahlolokazi abakholwayo, indoda nokuba yinkosikazi, makabancede, bangathwali ibandla; ukuze ibasize abahlolokazi okwenyaniso.', 'XHO22'),
(55, 5, 17, 'Amadoda amakhulu alongamela kakuhle makabalelwe ekuthini afanelwe yimbeko ephindiweyo, ngokukodwa lawo abulalekayo elizwini nasemfundisweni.', 'XHO22'),
(55, 5, 18, 'Kuba isibhalo sithi, Uze ungayibophi umlomo inkomo ibhula; nokuthi, Umsébenzi uwufanele umvuzo wakhe.', 'XHO22'),
(55, 5, 19, 'Ukumangalelwa kwendoda enkulu musa ukukwamkela, kungabangakho mangqina mabini nokuba mathathu.', 'XHO22'),
(55, 5, 20, 'Abo bonayo, bohlwaye emehlweni abantu bonke, ukuze nabanye aba babe nokoyika.', 'XHO22'),
(55, 5, 21, 'Ndiyakuyala phambi koThixo, neNkosi yethu uYesu Kristu, nezithunywa zezulu ezinyuliweyo, ukuba uzigcine ezi zinto ungaqali ugwebe, ungenzi nto ngokwayama bani.', 'XHO22'),
(55, 5, 22, 'Uze ungabeki mntu izandla ngesiquphe, ungadlelani nabo ngezono zabanye; zigcine nyulu.', 'XHO22'),
(55, 5, 23, 'Musa ukuba sasela manzi; sela intwana yewayini, ngenxa yesisu sakho, nobulwelwe bakho obufuthi.', 'XHO22'),
(55, 5, 24, 'Izono zabanye abantu zisekuhleni kanye, ziyandulela ukuya emgwebeni; abanye bayawalandela.', 'XHO22'),
(55, 5, 25, 'Kwangokunjalo nemisebenzi emihle isekuhleni kanye; nezingenjalo azinakufihlakala.', 'XHO22'),
(55, 6, 1, 'Onke ke amakhoboka aphantsi kwedyokhwe, ndinga ke angababalela abaniniwo ekuthini, bafanelwe yimbeko yonke, ukuze igama likaThixo nemfundiso inganyeliswa.', 'XHO22'),
(55, 6, 2, 'Lawo ke ankosi zikholiweyo, makangazideli ngakuba zingabazalwana; kanye ke bakhonze, ngenxa yokuba bethembekile, beziintanda, babelana ngenzuzo. Bafundise ezi zinto, uvuselele.', 'XHO22'),
(55, 6, 3, 'Ukuba nabani na ufundisa enye into, kwaye engavumelani namazwi aphilileyo angaweNkosi yethu uYesu Kristu, kunye nemfundiso engokobuthixo;', 'XHO22'),
(55, 6, 4, 'unekratshi, engazi nto;', 'XHO22'),
(55, 6, 5, 'Iimpikiswano ezigwenxa zabantu abonakele iingqiqo, abahluthwe inyaniso, beba ukuhlonela uThixo kukuzuzela inzuzo embi.', 'XHO22'),
(55, 6, 6, 'Ke kona ukuhlonela uThixo, kundawonye nokwanela, kusekeleze inzuzo enkulu.', 'XHO22'),
(55, 6, 7, 'Kuba sibe singangenanga nanto ehlabathini; kananjalo asinakuphuma nanto.', 'XHO22'),
(55, 6, 8, 'Sinento ke edliwayo neyambathwayo, masaneliswe zezo zinto.', 'XHO22'),
(55, 6, 9, 'Abo banga bangaba zizityebi, beyela ekuhendweni, nasesibatheni, nasezinkanukweni ezininzi ezibubudenge ezenzakalisayo; zona zibatshonisayo abantu ekonakaleni, nasekutshabalaleni.', 'XHO22'),
(55, 6, 10, 'Kuba ukuthanda imali yingcambu yeento zonke ezimbi: abathe abathile, ngokuzolukileyo, balahlekana nokholo, bazibhodloza ngobuhlungu obuninzi.', 'XHO22'),
(55, 6, 11, 'Ke wena, mntu kaThixo, zibaleke ezo zinto; phuthuma ubulungisa, ukuhlonela uThixo, ukholo, uthando, umonde, ubulali.', 'XHO22'),
(55, 6, 12, 'Yilwa umlo omhle wokholo, bubambe ubomi obungunaphakade, owabizelwa kubo, waza wenza uvumo lwasesidlangalaleni oluhle phambi kwamangqina amaninzi.', 'XHO22'),
(55, 6, 13, 'Ndiyakuthethela phambi koThixo lowo uzidlisa ubomi zonke izinto, naphambi koYesu Krestu, owangqina uvumo lwesivumo esihle phambi koPontiyo Pilato,', 'XHO22'),
(55, 6, 14, 'ukuba uwugcine lo mthetho, ungabi nabala, ungabi nakusoleka, ide ifike iNkosi yethu uYesu Kristu;', 'XHO22'),
(55, 6, 15, 'aya kubonakaliswa ngamaxesha akhe, lo ungowokuphela kwakhe onegunya, uKumkani kakumkani, uNkosi kankosi;', 'XHO22'),
(55, 6, 16, 'okuphela konokungafi, ehleli ekukhanyeni, ekungekho namnye unako ukusondela kuye; ekungekho namnye wakha wambona, nonako ukumbona; kuye makubekho imbeko namandla angunaphakade. Amen.', 'XHO22'),
(55, 6, 17, 'Bathethele abazizityebi kweli hlabathi ukuba bangaphakami, bangathembeli butyebini bungagqalekiyo, mabathembele kuThixo ophilileyo, obeka phambi kwethu zonke izinto ngokobutyebi, ukuba zidliwe;', 'XHO22'),
(55, 6, 18, 'benze okulungileyo, babe zizityebi zemisebenzi emihle, babe nokwaba, babe nobudlelane;', 'XHO22'),
(55, 6, 19, 'baziqwebele isiseko esihle, base exesheni eliza kuza, ukuze babubambe ubomi obungunaphakade.', 'XHO22'),
(55, 6, 20, 'Timoti, loo nto uyiphathisiweyo yigcine, uphambuke ezintethweni ezingento, ezihlambelayo, nakwiinkcaso zemfundo leyo, kubizwa ngokuba kuxokwa;', 'XHO22'),
(55, 6, 21, 'abasuke abathile bethe banako, baluphosa ukholo. Ubabalo malube nawe. Amen. (Eyokuqala eya kuTimoti yabhalwa eLawodike, eyona dolophu inkulu eFrigiya Pacatiana.)', 'XHO22'),
(56, 1, 1, 'uPawulos, umpostile kaYesu Kristu ngokuthanda kukaThixo, ngokwedinga lobomi obukuKristu Yesu,', 'XHO22'),
(56, 1, 2, 'ubhalela uTimoti, unyana wam oyintanda; uthi, Makube lubabalo, inceba, uxolo, oluvela kuThixo uYise, noKristu Yesu iNkosi yethu.', 'XHO22'),
(56, 1, 3, 'Ndiyabulela kuye uThixo, endamkhonzayo kwakoomawokhulu ndinesazela esihlambulukileyo, ndikukhumbula njengokungapheliyo, ekukhungeni kwam ubusuku nemini;', 'XHO22'),
(56, 1, 4, 'Ndilangazelela ukunga ndingakubona, ndizikhumbula nje iinyembezi zakho, ukuze ndizaliswe luvuyo;', 'XHO22'),
(56, 1, 5, 'Ndikhunjuzwa nje ukholo olukuwe olungenakuhanahanisa, lona lwahlalayo kuqala kunyokokhulu uLoyisi, nakunyoko uYunike; ndeyisekile ke kukuba nakuwe.', 'XHO22'),
(56, 1, 6, 'Endithi ngenxa yelo thuba ndikukhumbuze, ukuba usikhwezele isipho sokubabalwa kwakho nguThixo, esikhoyo kuwe ngokubekwa kwezandla zam phezu kwakho.', 'XHO22'),
(56, 1, 7, 'Kuba uThixo akasinikanga moya wabugwala; koko ngamandla, nangothando, nangengqondo ephilileyo.', 'XHO22'),
(56, 1, 8, 'Ngoko ke musa ukuba nazintloni ngabo ubungqina beNkosi yethu, nangam, mna mbanjwa wayo;', 'XHO22'),
(56, 1, 9, 'owasisindisayo, wasibiza ngobizo olungcwele, engenzi ngokwemisebenzi yethu, esenza ngokweyakhe ingqibo, nobabalo, esababalwa ngalo ngoKristu Yesu phambi kwamaxesha aphakade.', 'XHO22'),
(56, 1, 10, 'Kodwa ngoku yabonakaliswa ngokubonakala koMsindisi wethu uYesu Krestu, owakubhangisayo ukufa, waza waveza ubomi nokungonakali ekukhanyeni ngazo iindaba ezilungileyo;', 'XHO22'),
(56, 1, 11, 'endithe ndamiselwa bona mna, ukuba ndibe ngumshumayeli nompostile nomfundisi weentlanga.', 'XHO22'),
(56, 1, 12, 'Kungenxa yoko le nto ndibandezelekayo; noko ke andinazintloni, kuba ndiyamazi endikholiweyo nguye; kananjalo ndeyisekile kukuba unako ukuyigcina into endimphathisileyo, ese kuloo mini.', 'XHO22'),
(56, 1, 13, 'Umzekelo wamazwi aphilileyo owawevayo kum, wubambe elukholweni naseluthandweni olukuKristu Yesu.', 'XHO22'),
(56, 1, 14, 'Loo nto ilungileyo uyiphathisiweyo, yigcine ngoMoya oyiNgcwele ohleliyo ngaphakathi kwethu.', 'XHO22'),
(56, 1, 15, 'Oku uyakwazi, ukuba bandinikele umva bonke abaseAsiya; ekubo inguFigelo noHermogene.', 'XHO22'),
(56, 1, 16, 'iNkosi mayiyenzele inceba indlu kaOnesiforo; ngokuba kwaba futhi endiphumza, akaba nazintloni nguwo umxokelelwane wam.', 'XHO22'),
(56, 1, 17, 'Uthe akuba eseRoma, wandifuna ngokukhuthala okukhulu, wandifumana.', 'XHO22'),
(56, 1, 18, 'Yanga iNkosi ingamfumanisa inceba eNkosini ngaloo mini. Wena uzazi kakuhle oko zingako izinto awalungiselela ngazo e-Efese.', 'XHO22'),
(56, 2, 1, 'Wena ngoko, mntwana wam, yomelela elubabalweni olukuKristu Yesu;', 'XHO22'),
(56, 2, 2, 'uthi nezinto owazivayo kum ngamangqina amaninzi, ezo uzibeke phambi kwabantu abathembekileyo, bona baya kukulingana ukufundisa nabanye.', 'XHO22'),
(56, 2, 3, 'Wena ngoko bunyamezele ububi, njengomphumi-mkhosi olungileyo kaKristu Yesu.', 'XHO22'),
(56, 2, 4, 'Akukho namnye uthi emfazweni azibhijele ngemicimbi yobu bomi; ukuze amkholise lowo umnyulele ukulwa.', 'XHO22'),
(56, 2, 5, 'Nakuba ke ubani esithi azame emdlalweni, akathweswa sithsaba, ukuba akathanga azame ngokomthetho.', 'XHO22'),
(56, 2, 6, 'Umlimi lowo ubulalekayo, umelwe kukwamkela kuqala kwiziqhamo.', 'XHO22'),
(56, 2, 7, 'Ziqiqe izinto endizithethayo; iNkosi yokunika ukuqonda ezintweni zonke.', 'XHO22'),
(56, 2, 8, 'Khumbula ukuba uYesu Krestu wavuka kwabafileyo, engowembewu kaDavide, ngokweendaba zam ezilungileyo.', 'XHO22'),
(56, 2, 9, 'Endiva ububi ngenxa yazo, kude kuse ezintanjeni njengomenzi wobubi; ke lona ilizwi likaThixo alibotshiwe.', 'XHO22'),
(56, 2, 10, 'Ngenxa yoko izinto zonke ndiyazinyamezela ngenxa yabanyuliweyo, ukuze nabo bazuze usindiso olukuKristu Yesu, ndawonye nozuko olungunaphakade.', 'XHO22'),
(56, 2, 11, 'Lithembekile ilizwi eli lithi, Kuba xa safa naye, sodla ubomi kwandawonye naye;', 'XHO22'),
(56, 2, 12, 'ukuba siyanyamezela, solawula kwandawonye naye; ukuba siyamkhanyela, naye wosikhanyela;', 'XHO22'),
(56, 2, 13, 'Ukuba asikholwa, yena uhleli ethembekile; akanako ukuzikhanyeza.', 'XHO22'),
(56, 2, 14, 'Bakhumbuze ezo zinto, uqononondise emehlweni eNkosi, ukuba kungenziwa mfazwe yamazwi okungancedi lutho, kuze kubhukuqeke abaphulaphuli.', 'XHO22'),
(56, 2, 15, 'Kukhuthalele ukuzimisa phambi koThixo ucikidekile, ungumsebénzi ongenazintloni, ulungisa ukulahlula ilizwi lenyaniso.', 'XHO22'),
(56, 2, 16, 'Ke zona iintetho ezingento, ezihlambelayo, zinxweme; kuba ziya kuhambela phambili kokukhona kungahloneli Thixo.', 'XHO22'),
(56, 2, 17, 'Nelizwi labo liya kudla njengesilonda esiqhenqethayo; abanoHimenayo noFileto;', 'XHO22'),
(56, 2, 18, 'abathe bona bayiphosa inyaniso, besithi uvuko seluthe lwakho; silubhukuqa ukholo lwenxenye.', 'XHO22'),
(56, 2, 19, 'Noko ke limi, lithe nkqi, ilitye lesiseko likaThixo, linambhalo othi, iNkosi iyabazi abangabayo. Kwanokuthi, Mabahlukane nobugwenxa, bonke abalibizayo igama likaKristu.', 'XHO22'),
(56, 2, 20, 'Ke endlwini enkulu akubikho zitya zagolide nazasilivere zodwa; kubakho nezomthi nezomdongwe. Inxenye ke zezembeko, inxenye azizambeko.', 'XHO22'),
(56, 2, 21, 'Ukuba ke umntu uthe wazikhuphulula kwezo zinto, woba sisitya sembeko, singcwalisiwe, simlungele kakuhle umninindlu, silungiselwe wonke umsebenzi olungileyo.', 'XHO22'),
(56, 2, 22, 'Ke zibaleke iinkanuko zobutsha, phuthuma ubulungisa, ukholo, uthando, uxolo nabo bayinqulayo iNkosi ngokwasentliziyweni ehlambulukileyo.', 'XHO22'),
(56, 2, 23, 'Yinxweme imibuzwana yobudenge engenangqeqesho, usazi ukuba izala imfazwe.', 'XHO22'),
(56, 2, 24, 'Ke umkhonzi weNkosi akamele kusukuzana; kodwa abe nokuthantamisa kubo bonke, enokufundisa, enomonde;', 'XHO22'),
(56, 2, 25, 'Uyabaqeqesha ngobulali abo baphikayo; ukuba mhlawumbi uThixo angabanika inguquko esa ekuyazini inyaniso;', 'XHO22'),
(56, 2, 26, 'babuye baqabuke, baphume emgibeni kaMtyholi, ababanjiswe nguye ekuthandeni kwakhe.', 'XHO22'),
(56, 3, 1, 'Ke, kwazi oku, ukuba ngemihla yokugqibela kuya kufika amaxesha anomngcipheko.', 'XHO22'),
(56, 3, 2, 'Kuba abantu baya kuba ngabazithandayo, ababawayo, abaqhayisi, abanekratshi, abanyelisi, abangeva bazali, abangenambulelo, abangengcwele;', 'XHO22'),
(56, 3, 3, 'ngaphandle komsa wemvelo, abachasi boxolo, abatyholi, abangenakuzeyisa, iindlobongela, abangabafuniyo abalungileyo;', 'XHO22'),
(56, 3, 4, 'abancethezi, izityhuthutyhuthu, abathiwe lwale likratshi, abathandi beziyolo kunokuba bathande uThixo;', 'XHO22'),
(56, 3, 5, 'benokumila kokuhlonela uThixo, ukanti bewakhanyela amandla ako;', 'XHO22'),
(56, 3, 6, 'Kuba kwabalulo kwabo abo banyebelezela ezindlwini, bathimbe abafazi abasisidenge, abasindwe zizono, beqhutywa ziinkanuko ngeenkanuko;', 'XHO22'),
(56, 3, 7, 'Uhlala efunda, kodwa akanako ukufika ekuyazini inyaniso.', 'XHO22'),
(56, 3, 8, 'Kwangolo hlobo ke, bathi ooYanes noYambres bamchasa uMoses, ngokunjalo nabo aba bayayichasa inyaniso. Bangabantu abonakele iingqiqo, abangenakucikideka ngakulo ukholo.', 'XHO22'),
(56, 3, 9, 'Abayi kuhambela phambili ngakumbi; kuba ukungabi nangqiqo kwabo kuya kubonakala kubantu bonke, njengokuba kwaba njalo okwabo.', 'XHO22'),
(56, 3, 10, 'Ke wena uyayazi imfundiso yam, ihambo, ingqibo, ukholo, ukuzeka kade umsindo, uthando, unyamezelo,', 'XHO22'),
(56, 3, 11, 'Iintshutshiso, iintlungu ezinjengezo zandihlelayo kwa-Antiyokwe, eIkoniyo, eListra; ukutshutshiswa okunjengoko ndakuthwalayo, yaza iNkosi yandihlangula kuko konke.', 'XHO22'),
(56, 3, 12, 'Nabo bonke ke abathanda ukuhlala ngokuhlonela uThixo kuKristu Yesu baya kutshutshiswa.', 'XHO22'),
(56, 3, 13, 'Ke bona abantu abangendawo noosiyazi, kukhona baya kuhambela phambili ebubini, belahlekisa, belahlekiswa.', 'XHO22'),
(56, 3, 14, 'Wena ke, hlala kwizinto owazifundayo, waqiniseka ngazo, ukuba wazi ukuba ufunde kubani na;', 'XHO22'),
(56, 3, 15, 'nokuba uzaze kwasebuntwaneni izibhalo ezingcwele, ezinako ukukulumkisela elusindisweni ngokukholwa kuKristu Yesu.', 'XHO22'),
(56, 3, 16, 'Sonke isibhalo, siphefumlelwe nguThixo nje, sikwancedela ukufundisa, ukohlwaya, ukululeka, ukuqeqesha okusebulungiseni,', 'XHO22'),
(56, 3, 17, 'ukuze umntu kaThixo afaneleke, exhobele wonke umsebenzi olungileyo.', 'XHO22'),
(56, 4, 1, 'Ndiyaqononondisa mna ngoko emehlweni kaThixo, neNkosi yethu uYesu Kristu, oza kugweba abaphilileyo nabafileyo, kwanangokubonakala kwakhe nobukumkani bakhe.', 'XHO22'),
(56, 4, 2, 'Lishumayele ilizwi; yomelela ngexesha elililo, ngexesha elililo; yohlwaya, khalimela, vuselela ngako konke ukuzeka kade umsindo nokufundisa.', 'XHO22'),
(56, 4, 3, 'Kuba kuya kubakho ixesha abangayi kuyinyamezela imfundiso ephilileyo; baya kusuka ngokweenkanuko zabo bazifumbele abafundisi, bebaba iindlebe;', 'XHO22'),
(56, 4, 4, 'babeya kuzisonga zimke iindlebe kuyo inyaniso, baphambukele ke kuzo iintsomi.', 'XHO22'),
(56, 4, 5, 'Ke wena yiba yingcathu ezintweni zonke, nyamezela ububi, umsebenzi womshumayeli weendaba ezilungileyo wenze, buzalise ubulungiseleli bakho.', 'XHO22'),
(56, 4, 6, 'Kuba mna sendithululwa ngokomnikelo; nalo ixesha lokunduluka kwam likufuphi.', 'XHO22'),
(56, 4, 7, 'Umzamo omhle ndiwuzamile, ugqatso ndilufezile, ukholo ndilugcinile;', 'XHO22'),
(56, 4, 8, 'Okokugqibela ndibekelwe isithsaba sobulungisa, eya kundibuyisela ngaso ngaloo mini iNkosi, umgwebi olilungisa; ingabi ndim ndedwa ke, ibe ngabo bonke abakuthandayo ukubonakala kwayo.', 'XHO22'),
(56, 4, 9, 'Khawuleza uze kum kamsinya;', 'XHO22'),
(56, 4, 10, 'kuba uDemas wandishiya ngokuthanda iphakade langoku, waya eTesalonika; uKreske uye kwelaseGalati, uTito uye kwelaseDalmati.', 'XHO22'),
(56, 4, 11, 'NguLuka yedwa onam. Thabatha uMarko uze naye; kuba unokundinceda kakuhle emsebenzini.', 'XHO22'),
(56, 4, 12, 'Ke kaloku uTikiko ndimthumele e-Efese.', 'XHO22'),
(56, 4, 13, 'Ingubo endayishiya eTrowa kuKarpo, uze uyiphathe ekuzeni kwakho, neencwadi ezo, ngokukodwa ezezintsu.', 'XHO22'),
(56, 4, 14, 'uAlesandire, umkhandi wobhedu, wandenza ububi obuninzi;', 'XHO22'),
(56, 4, 15, 'Mlumkele nawe; ngokuba wawachase kunene amazwi ethu.', 'XHO22'),
(56, 4, 16, 'Kokokuqala ukuziphendulela kwam, akubangakho namnye unam, basuka bonke bandishiya;', 'XHO22'),
(56, 4, 17, 'Noko ke iNkosi yandinceda, yandomeleza; ukuze ngam uvakaliso lweendaba ezilungileyo luzaliseke, neentlanga zonke zive; ndaza ndahlangulwa emlonyeni wengonyama.', 'XHO22'),
(56, 4, 18, 'Yandihlangula yona iNkosi kuwo wonke umsebenzi ongendawo, indisindisele ebukumkanini bayo obusemazulwini; kuyo makubekho uzuko, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(56, 4, 19, 'Bulisa kuPriska noAkwila, nakwindlu kaOnesiforo.', 'XHO22'),
(56, 4, 20, 'uErasto wasala eKorinte, kodwa uTrofimo ndamshiya eMileto egula.', 'XHO22'),
(56, 4, 21, 'Khawuleza uze, kungekabi sebusika. Uyakubulisa uYubhulo, noPudens, noLino, noKlawudiya, nabo abazalwana bonke.', 'XHO22'),
(56, 4, 22, 'iNkosi uYesu Krestu mayibe nomoya wakho. Ubabalo malube nani. Amen. (Incwadi yesibini eya kuTimoti, owamiselwa ubhishophu wokuqala webandla lase-Efese, yabhalwa eRoma, xa uPawulos waziswa phambi koNero okwesibini.)', 'XHO22'),
(57, 1, 1, 'uPawulos, umkhonzi kathixo, umpostile ke kaYesu Kristu, ngokokholo lwabanyulwa bakaThixo, nokuyazi inyaniso engokokuhlonela uThixo;', 'XHO22'),
(57, 1, 2, 'ngethemba lobomi obungunaphakade, awathi uThixo, ongenakuxoka, wabubeka idinga kwaphambi kwamaxesha aphakade;', 'XHO22'),
(57, 1, 3, 'Walibonakalalisa ke ilizwi lakhe ngamaxesha amisiweyo, ngesigidimi endasiphathiswa mna, ngokomthetho kaThixo uMsindisi wethu;', 'XHO22'),
(57, 1, 4, 'ubhalela uTito, umntwana wam wenene ngokwaselukholweni, esinobudlelane ngalo: Makube lubabalo, nenceba, noxolo oluvela kuThixo uYise, neNkosi yethu uYesu Kristu.', 'XHO22'),
(57, 1, 5, 'Ndakushiya eKrete ngenxa yoku, ukuze uhambise ukuzilungisa izinto eziseleyo, umise amadoda amakhulu kwimizi ngemizi, njengoko ndakumisela ngako mna;', 'XHO22'),
(57, 1, 6, 'ukuba umntu ungongenakumangaleleka, eyindoda yamfazi mnye enabantwana abakholiweyo, abangamiselwa tyala lakuzibhubhisa, abangaswele kuthobela mntu.', 'XHO22'),
(57, 1, 7, 'Kuba umveleli umelwe kukuba ngongenakumangaleleka, njengegosa likaThixo; ungengazithandi, ucaphuka msinya;', 'XHO22'),
(57, 1, 8, 'Umelwe kukuthi abe ngobuka iindwendwe, umthandi wokulungileyo, onesidima, olilungisa, ongcwele, ozingcayo;', 'XHO22'),
(57, 1, 9, 'ebambelele ezwini elithembekileyo ngokwemfundiso, ukuze abe nako ukuvuselela ngayo imfundiso ephilileyo, nokubohlwaya badane abaphikayo.', 'XHO22'),
(57, 1, 10, 'Kuba baninzi abathetha izinto ezingento, nabakhohlisayo, ngokukodwa abo bolwaluko;', 'XHO22'),
(57, 1, 11, 'abamelwe kukuvingcwa imilomo; abathi izindlu ziphela bazibhukuqe, befundisa izinto ezingafanelekileyo ngenxa yenzuzo embi.', 'XHO22'),
(57, 1, 12, 'Uthi omnye wabo, kwaumprofeti wakowabo, AmaKrete ahlala engamaxoki, amarhamncwa amabi, amarhamncwa axhwalekileyo.', 'XHO22'),
(57, 1, 13, 'Obu bungqina buyinene. Ngenxa yoko bohlwaye kabukhali, badane, ukuze baphile elukholweni;', 'XHO22'),
(57, 1, 14, 'banganyamekeli zintsomi zabuYuda, namithetho yabantu abayinikela umva inyaniso.', 'XHO22'),
(57, 1, 15, 'Zonke izinto zihlambulukile kwabahlambulukileyo; kwabadyobhekileyo nabangakholwayo, akukho nto ihlambulukileyo; kusuke kwadyobheka nengqiqo nesazela sabo.', 'XHO22'),
(57, 1, 16, 'Bayavuma ukuba bayamazi uThixo; kanti ngayo imisebenzi bayamkhanyela, bengamasikizi, bengabangevayo, nakumsebenzi wonke olungileyo bengenakucikideka.', 'XHO22'),
(57, 2, 1, 'Ke wena, thetha izinto eziyifaneleyo imfundiso ephilileyo;', 'XHO22'),
(57, 2, 2, 'ukuba amadoda amakhulu abe ziingcathu, andileke, abe nesidima, aphile elukholweni, eluthandweni, ekunyamezeleni;', 'XHO22'),
(57, 2, 3, 'kwangokunjalo abafazi abakhulu mababe nesimilo esibafaneleyo, bangabi ngabatyholi; bangazilahleli kwiwayini eninzi;', 'XHO22'),
(57, 2, 4, 'ukuze bafundise abafazana ukuba babe nesidima, bawathande amadoda abo, babathande abantwana babo;', 'XHO22'),
(57, 2, 5, 'babe nesidima, babe nyulu, bagcine ezindlwini zabo, babe nokulunga, bawathobele awabo amadoda, ukuze ilizwi likaThixo linganyeliswa.', 'XHO22'),
(57, 2, 6, 'Kwangokunjalo amadoda amatsha wayale ukuba abe nesidima.', 'XHO22'),
(57, 2, 7, 'Ezintweni zonke uze uzibonakalise ungumzekelo wemisebenzi emihle: emfundisweni ubonakala ekungonakali, ukundileka, nokunyaniseka,', 'XHO22'),
(57, 2, 8, 'Ilizwi eliphilileyo, elingenakugwetywa; ukuze ochasayo abe neentloni, engenanto imbi yakuthetha ngani.', 'XHO22'),
(57, 2, 9, 'Amakhoboka wathethele ukuba azithobele iinkosi zawo, abe ngakholekileyo kakuhle ezintweni zonke, engaphenduli kwakhona;', 'XHO22'),
(57, 2, 10, 'ingabi ngeentlondi, isuke ibonakalalise konke ukuthembeka okulungileyo; ukuze bayivathise imfundiso yoMsindisi wethu uThixo ezintweni zonke.', 'XHO22'),
(57, 2, 11, 'Kuba lubonakele kubantu bonke ubabalo lukaThixo, lulolusindisayo;', 'XHO22'),
(57, 2, 12, 'lusiqeqesha ukuze sithi, sikuncama ukungahloneli Thixo nokukhanuka okukokwehlabathi, sihlale ngokunesidima, nangokubulungisa, nangokuhlonela uThixo, kweli phakade langoku;', 'XHO22'),
(57, 2, 13, 'silinde ithemba elinoyolo, nembonakalo yobuqaqawuli boThixo omkhulu, uMsindisi wethu uYesu Kristu;', 'XHO22'),
(57, 2, 14, 'owazinikelayo ngenxa yethu, ukuze asikhulule kubo bonke ubugwenxa, asihlambululele abantu abayinqobo kuye, abazondelela imisebenzi emihle.', 'XHO22'),
(57, 2, 15, 'Ezi zinto zithethe, uvuselele, wohlwaye ngawo wonke ummiselo. Makungabikho namnye ukudelayo;', 'XHO22'),
(57, 3, 1, 'Bakhumbuze ukuba bathobele oozilawuli noomagunya, bathobele abathetheli, bawulungele wonke umsebenzi olungileyo;', 'XHO22'),
(57, 3, 2, 'Mabanganyelisi mntu, bangabi nakulwa, babe nokuthantamisa, bebonakala benobulali bonke kubantu bonke.', 'XHO22'),
(57, 3, 3, 'Kaloku nathi besifudula sizizidenge, singeva, silahlekiswa, sikhonza iinkanuko neziyolo ngeenkanuko, siphila ebubini nasekhweleni, sithiyayo.', 'XHO22'),
(57, 3, 4, 'Kwathi ke emva koko kwabonakala ububele nothando lukaThixo, uMsindisi wethu, kubantu;', 'XHO22'),
(57, 3, 5, 'kungengamisebenzi yobulungisa esayenzayo thina, kungokwenceba yakhe, wasisindisa ngentlambululo yokuzalwa ngokutsha, nangokuhlaziya koMoya oyiNgcwele;', 'XHO22'),
(57, 3, 6, 'Owasithululayo phezu kwethu ngokobutyebi, ngoYesu Kristu uMsindisi wethu;', 'XHO22'),
(57, 3, 7, 'ukuze sithi, sigwetyelwe ngobabalo lwakhe, sibe ziindlalifa zobomi obungunaphakade ngokwethemba.', 'XHO22'),
(57, 3, 8, 'Lithembekile ilizwi elo; nezo zinto ndinga ungaziqinisela, ukuze banyameke, benze imisebenzi emihle, abo bakholiweyo kuThixo. Ezo zinto zilungile, zinokubanceda abantu.', 'XHO22'),
(57, 3, 9, 'Kodwa yona imibuzo yobudenge, nemilibo yokuzalwa, neenkani, nokulwa ngawo umthetho, zinxweme, kuba azincedi nganto, zililize.', 'XHO22'),
(57, 3, 10, 'Umntu ongumahlukanisi emva kokululekwa kokuqala nokwesibini mmangale;', 'XHO22'),
(57, 3, 11, 'usazi nje ukuba onjalo ugwenxeke kwaphela; uyona, ezigwebile ngokwakhe.', 'XHO22'),
(57, 3, 12, 'Xa ndithe ndathuma kuwe uArtemas, nokuba nguTikiko, khawuleza uze kum eNikopoli; kuba ndigqibe kwelokuba ndobudlulisa ubusika khona apho.', 'XHO22'),
(57, 3, 13, 'uZenas, umqondisi-mthetho, noApolos, baxhobisele indlela ngokunyameka, ukuze bangasweli nto.', 'XHO22'),
(57, 3, 14, 'Kwanewethu ke masifunde ukuhlala sisenza imisebenzi emihle, siswele amandla, singabi ngabaswele isiqhamo.', 'XHO22'),
(57, 3, 15, 'Bayakubulisa bonke abanam. Ndibulisele kwabasithandayo ngokwaselukholweni. Ubabalo malube nani nonke. Amen. (Yabhalelwa uTito, owamiselwa ukuba abe ngubhishophu wokuqala webandla lamaKrete, eNikopolis yaseMakedoni.)', 'XHO22'),
(58, 1, 1, 'uPawulos, umbanjwa kaYesu Kristu, noTimoti umzalwana, babhalela uFilemon intanda yethu, ongumsebenzi wethu;', 'XHO22'),
(58, 1, 2, 'Sibhalela uApiya oyintanda, noArkipo umphumi-mkhosi wethu, nakulo ibandla elisendlwini yakho;', 'XHO22'),
(58, 1, 3, 'bathi, Makube lubabalo kuni, noxolo oluvela kuThixo uBawo wethu, neNkosi yethu uYesu Kristu.', 'XHO22'),
(58, 1, 4, 'Ndihlala ndibulela kuye uThixo wam, ndikukhankanya emithandazweni yam ngamaxesha onke;', 'XHO22'),
(58, 1, 5, 'Ndiluvile nje uthando lwakho nokholo onalo kuyo iNkosi uYesu nakubo bonke abangcwele,', 'XHO22'),
(58, 1, 6, 'ukuze ubudlelane bokholo lwakho bube nokusebenza ngokwazi konke okulungileyo okukuni, okuKristu Yesu.', 'XHO22'),
(58, 1, 7, 'Kuba sinovuyo nothuthuzeleko olukhulu ngalo uthando lwakho, ngokuba izibilini zabangcwele ziphunyuziwe ngawe, mzalwana.', 'XHO22'),
(58, 1, 8, 'Ngoko ke, nangona ndinobuganga obukhulu kuKristu, ukuba ndikuthethele into esukuba ifanelekile,', 'XHO22'),
(58, 1, 9, 'noko ngenxa yothando ndisuka kanye ndikubongoze, ndingonjengoPawulos ixhego, ngoku ke ndikwangumbanjwa kaYesu Kristu.', 'XHO22'),
(58, 1, 10, 'Ndikubongoza ngenxa yonyana wam uOnesimo, endimzeleyo ndisezintanjeni;', 'XHO22'),
(58, 1, 11, 'obefudula engeluncedo lwanto kuwe, kodwa ngoku kuwe nakum.', 'XHO22'),
(58, 1, 12, 'Endimbuyisele kuye, wena ke mamkele, oko kukuthi, izibilini zam;', 'XHO22'),
(58, 1, 13, 'ebendinga ndingaya kuhlala nam, ukuze athi esikhundleni sakho andilungiselele, ndisezintanjeni ngenxa yeendaba ezilungileyo ezi;', 'XHO22'),
(58, 1, 14, 'Kodwa andithandanga kwenza nto ngaphandle kwakho; ukuze ukulunga kwakho kungangi kokokunyanzelwa, kube kokokuvuma.', 'XHO22'),
(58, 1, 15, 'Kuba mhlawumbi waphuma umzuzwana, ukuze umzuze ngokwaphakade;', 'XHO22'),
(58, 1, 16, 'Engasenjengekhoboka, esuke wangaphezu kwekhoboka, engumzalwana oyintanda, ngokukodwa kum; kubeke phi na ke kuwe, ngokwenyama nangokweNkosi?', 'XHO22'),
(58, 1, 17, 'Ngoko ke, ukuba unobudlelane nam, mamkele njengam.', 'XHO22'),
(58, 1, 18, 'Ukuba wakona, nokuba unetyala kuwe, oko kubalele kum.', 'XHO22'),
(58, 1, 19, 'Mna Pawulos ndikubhale ngesam isandla ukuthi, mna ndokuhlawula; endingatshoyo kuwe ukuthi, kum unetyala elingaphezulu, elinguwe ngokwakho.', 'XHO22'),
(58, 1, 20, 'Ewe, mzalwana, mandincedeke ngawe mna ngokwaseNkosini; ziphumze izibilini zam ngokwaseNkosini.', 'XHO22'),
(58, 1, 21, 'Ndikubhalele ndeyisekile kukundilulamela kwakho, ndisazi ukuba ùya kwenza nangaphezulu koko ndikuthethayo.', 'XHO22'),
(58, 1, 22, 'Kunye ke noko, ndilungisele indawo yokuhlala; kuba ndithembile ukuba ngayo imithandazo yenu nondiphiwa.', 'XHO22'),
(58, 1, 23, 'Uyakubulisa uEpafra, umbanjwa ndawonye nam wakwaKristu Yesu.', 'XHO22'),
(58, 1, 24, 'uMarko, uAristarko, uDemas, uLucas, abasebenzisi bam.', 'XHO22'),
(58, 1, 25, 'Ubabalo lweNkosi yethu uYesu Kristu malube nomoya wenu. Amen. (Yabhalwa eRoma isiya kuFilemon, nguOnesimo umkhonzi.)', 'XHO22'),
(59, 1, 1, 'uThixo wayethethile nje kudala, nangeendlela ezininzi, koobawo ngabo oobawo ngabaprofeti;', 'XHO22'),
(59, 1, 2, 'Ngale mihla yokugqibela uthe wathetha nathi ngoNyana wakhe, awammisayo ukuba abe yindlalifa yeento zonke, awathi kwangaye wawenza amahlabathi;', 'XHO22'),
(59, 1, 3, 'othe, elubengezelo lobuqaqawuli bakhe, nombhalo wenkqu yakhe, ezithwele nokuzithwala zonke izinto ngalo ilizwi lamandla akhe, wesuka, akuba eyenzile ngokwakhe intlambululo yezono, wahlala phantsi ngasekunene kobuKhulu, enyangweni;', 'XHO22'),
(59, 1, 4, 'ethe waba ngonamandla ngaphezu kwezithunywa zezulu, ngangoko azuze ilifa legama eligqithiseleyo kunelazo.', 'XHO22'),
(59, 1, 5, 'Kuba sisiphi na isithunywa, awakha wathi kuso, UnguNyana wam wena, Mna ndikuzele namhla? Nokuthi, Mna ndiya kuba nguYise kuye, Yena abe nguNyana wam?', 'XHO22'),
(59, 1, 6, 'Nangexa athi amngenise owamazibulo kwelimiweyo, uthi, Maziqubude kuye zonke izithunywa zikaThixo.', 'XHO22'),
(59, 1, 7, 'Ngokuphathelele kwizithunywa zezulu uthi: “Ulozenza izithunywa zakhe imimoya, nabalungiseleli bakhe ilangatye lomlilo.', 'XHO22'),
(59, 1, 8, 'Kuye yena uNyana uthi, Itrone yakho, Thixo, ingunaphakade kanaphakade; yintonga yobulungisa intonga yobukumkani bakho.', 'XHO22'),
(59, 1, 9, 'Uthanda ubulungisa, uthiye okungendawo; Ngenxa yoko, uThixo, uThixo wakho, ukuthambise Ngeoli yomhlali ngaphezu kwabalingane bakho.', 'XHO22'),
(59, 1, 10, 'Kananjalo wathi, Wena, Nkosi, waliseka ihlabathi kwasekuqalekeni; Liyimisebenzi yezandla zakho izulu;', 'XHO22'),
(59, 1, 11, 'Ezo zinto ziya kutshabalala; ke wena uhlala uhleli; Aye onke njengengubo eya konakala;', 'XHO22'),
(59, 1, 12, 'Uya kuzisonga njengesambatho, ziguqulwe; ke wena ukwanguwe, neminyaka yakho ayiyi kuphela.', 'XHO22'),
(59, 1, 13, 'Sisiphi na ke isithunywa, awakha wathi kuso, Hlala ngasekunene kwam, Ndide iintshaba zakho ndizenze isihlalo seenyawo zakho?', 'XHO22'),
(59, 1, 14, 'Azingabo na zonke oomoya ababusayo, zithunywe ukuba zilungiselele abo baza kuba ziindlalifa zosindiso?', 'XHO22'),
(59, 2, 1, 'Ngenxa yoko, simelwe kukuzinyamekela ngokugqithiseleyo izinto esazivayo, hleze sikhukuliswe simke kuzo.', 'XHO22'),
(59, 2, 2, 'Kuba, ukuba ilizwi elathethwa ngezithunywa zezulu laqina, kwathi konke ukugqitha nokungeva kwamkela umvuzo oyeleleyo;', 'XHO22'),
(59, 2, 3, 'Sothini na thina ukusinda, sakuba siluhilizele usindiso olungakanana? eyathi ekuqaleni ukuthethwa yiyo iNkosi, yaza yaqiniselwa kuthi ngabo bayivayo;', 'XHO22'),
(59, 2, 4, 'Naye uThixo engqina ngabo ngemiqondiso, kwanezimanga, nemisebenzi ngemisebenzi yamandla, nezabelo zoMoya oyiNgcwele, ngokokuthanda kwakhe?', 'XHO22'),
(59, 2, 5, 'Kaloku akalithobelanga phantsi kwezithunywa zezulu elimiweyo eliza kubakho, eli sithetha ngalo.', 'XHO22'),
(59, 2, 6, 'Ke, wakha wangqina ubani, esithi, Uyintoni na umntu, le nto umkhumbulelayo? Unyana womntu, le nto umvelelayo?', 'XHO22'),
(59, 2, 7, 'Umsilelise kancinane kunezithunywa zezulu; Wamthwesa uzuko nembeko, Wammisa phezu kwemisebenzi yezandla zakho.', 'XHO22'),
(59, 2, 8, 'Zonke izinto wazithobela phantsi kweenyawo zakhe. Kuba ekubeni wazithobela phantsi kwakhe zonke izinto, akashiyanga nto ingathotyelwanga phantsi kwakhe. Kungokunje asikaziboni zithotyelwe phantsi kwakhe zonke izinto.', 'XHO22'),
(59, 2, 9, 'Ke sibona lo uncitshiswe kancinane kunezithunywa zezulu, uYesu ke, ethe ngenxa yeentlungu zokufa, ethweswe uzuko nembeko; ukuze ngobabalo lukaThixo eve ukufa ngenxa yabo bonke.', 'XHO22'),
(59, 2, 10, 'Kuba bekumfanele lowo, ezingenxa yakhe zonke izinto ezo, nezingaye zonke izinto ezo, ekubeni ezise oonyana abaninzi eluzukweni, ukuthi ayenze ngeentlungu igqibelele imbangi yosindiso lwabo.', 'XHO22'),
(59, 2, 11, 'Kuba lowo ungcwalisayo, kwanabo bangcwaliswayo, baphuma kumnye bonke; kungoko angenazintloni ukubabiza ngokuthi ngabazalwana;', 'XHO22'),
(59, 2, 12, 'Esithi, Ndiya kulazisa igama lakho kubo abazalwana bam, Ndivume kuwe indumiso phakathi kwebandla.', 'XHO22'),
(59, 2, 13, 'Abuye athi, Ndiya kukholosa ngaye. Abuye athi, Yabona, mna nabantwana andinike bona uThixo.', 'XHO22'),
(59, 2, 14, 'Ekubeni ngoko abantwana abo bengamadlelane naye ngenyama negazi, wathi naye ngokwakhe ngokukwanjalo wabelana nabo ngezo zinto, ukuze ngako ukufa ambhangise lowo unamandla okufa, oko kukuthi, uMtyholi lo;', 'XHO22'),
(59, 2, 15, 'abakhulule ebukhobokeni bonke abo babethe ngalo lonke ixesha lokudla kwabo ubomi bazizisulu zokoyika ukufa.', 'XHO22'),
(59, 2, 16, 'Kuba ndithi, akathabatheli kuye zithunywa; wathabathela kuye imbewu ka-Abraham.', 'XHO22'),
(59, 2, 17, 'Ngoko ke wafanelwa kukuba afane nabazalwana bakhe ngezinto zonke, ukuze abe ngonenceba, kwanombingeleli othembekileyo omkhulu ezintweni ezilunge kuThixo, ukuze azicamagushele izono zabantu.', 'XHO22'),
(59, 2, 18, 'Kuba ekubeni eve ubunzima, ehendwa naye ngokwakhe, unako ukubasiza abahendwayo.', 'XHO22'),
(59, 3, 1, 'Ngoko ke, bazalwana bangcwele, mabelana ngobizo lwasemazulwini, mondeleni uMpostile, uMbingeleli oMkhulu wovumo lwethu, uKristu Yesu;', 'XHO22'),
(59, 3, 2, 'ethembekile kulowo wamenzayo, kwanjengoMoses endlwini yakhe iphela.', 'XHO22'),
(59, 3, 3, 'Kuba lo ubalelwe ekuthini ufanelwe luzuko olungaphezu kolukaMoses; ngangokuba lowo wayakhayo indlu, enembeko engaphezu kwayo indlu.', 'XHO22'),
(59, 3, 4, 'Kuba yonke into eyindlu inoyakhayo; ke lowo wazakhayo zonke izinto nguThixo.', 'XHO22');
