INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(59, 3, 5, 'Ke yena uMosis wayethembekile endlwini yakhe iphela ngokomkhonzi, ukuze bube bubungqina obo bezinto eziya kuthethwa.', 'XHO22'),
(59, 3, 6, 'Ke yena uKristu uthembeke ngokonyana phezu kwendlu yakhe; esiyindlu yakhe ke thina, ukuba sithe sakubamba ngokuthe nkqi ukungafihlisi kwethu, neqhayiya lethemba lethu, sada sesa ekupheleni.', 'XHO22'),
(59, 3, 7, 'Ngoko ke, njengoko atshoyo uMoya oyiNgcwele, ukuthi, Namhla, ukuba nithe naliva ilizwi lakhe,', 'XHO22'),
(59, 3, 8, 'Musani ukuzenza lukhuni iintliziyo zenu, njengasengcaphukisweni, ngomhla wokuhendwa entlango;', 'XHO22'),
(59, 3, 9, 'Oko bandilingayo ooyihlo, bandilinga, Bayibona imisebenzi yam iminyaka emashumi mane.', 'XHO22'),
(59, 3, 10, 'Kungoko ndaba buhlungu ngenxa yeso sizukulwana, ndathi, Bahlala belahleka ezintliziyweni zabo; abazazi iindlela zam.', 'XHO22'),
(59, 3, 11, 'Ngoko ndafunga ndinomsindo, ndathi, Abayi kungena ekuphumleni kwam.', 'XHO22'),
(59, 3, 12, 'Lumkani, bazalwana, hleze kubekho kubani wenu intliziyo engendawo, engakholwayo, enokumka kuThixo ophilileyo;', 'XHO22'),
(59, 3, 13, 'manivuselelane imihla ngemihla, xa kusathiwa kunamhla; ukuze kungabikho mntu kuni wenziwa lukhuni kukulukuhla kwesono.', 'XHO22'),
(59, 3, 14, 'Kaloku siba ngamabelana ngoKristu, ukuba sithe ingqalo yokukholosa kwethu sayibamba ngokuthe nkqi, sada sesa ekupheleni;', 'XHO22'),
(59, 3, 15, 'ekubeni kusithiwa, Namhla, ukuba nithe naliva ilizwi lakhe, Musani ukuzenza lukhuni iintliziyo zenu, njengasengcaphukisweni.', 'XHO22'),
(59, 3, 16, 'Kuba abathile, bevile, bacaphukisa, phofu ingengabo bonke abo baphumayo eYiputa ngoMoses.', 'XHO22'),
(59, 3, 17, 'Ngoobani ke owakruquka ngabo iminyaka emashumi mane? Yayingengabo na abo bonayo, bazidumbu zawayo entlango?', 'XHO22'),
(59, 3, 18, 'Wafungela oobani na ke, ukuba abayi kungena ekuphumleni kwakhe, ingengabo abo bangevanga?', 'XHO22'),
(59, 3, 19, 'Siyabona ke, ukuba babengenakungena, ngenxa yokungakholwa.', 'XHO22'),
(59, 4, 1, 'Masoyike ngoko ke, hleze kuthi, xa lithe lalisakho idinga lokungena ekuphumleni kwakhe, kubekho namnye kuni ongathi usilele kulo.', 'XHO22'),
(59, 4, 2, 'Kuba sizishunyayeziwe iindaba ezilungileyo kwanjengabo; kodwa lona ilizwi lodaba lingadibananga nje nokholo kwabo balivayo, alabanceda nto.', 'XHO22'),
(59, 4, 3, 'Kuba thina sakholwayo, siyangena ekuphumleni, ngokoko athe, Njengoko ndafungayo, ndinengqumbo, Ndathi, Unakanye ukuba bokha bangene ekuphumleni kwam! nakubeni imisebenzi yenziwayo kwasekusekweni kwehlabathi.', 'XHO22'),
(59, 4, 4, 'Kuba wakha wathetha ngawo umhla wesixhenxe, ukuthi, Waza waphumla uThixo ngomhla wesixhenxe kuyo yonke imisebenzi yakhe.', 'XHO22'),
(59, 4, 5, 'Athi kule ndawo, Ukuba bokha bangene ekuphumleni kwam.', 'XHO22'),
(59, 4, 6, 'Njengokuba ke kusasele abathile ukuba bangene kuwo, bathi ke abo, kwashunyayelwa iindaba ezilungileyo kuqala kubo, bangangenanga ngenxa yokungakholwa.', 'XHO22'),
(59, 4, 7, 'Ubuya amise mini ithile, esithi ngoDavide, Namhlanje, emveni kwexesha elingakanana; njengokuba kuthiwe, Namhla, ukuba nithe naliva ilizwi lakhe, Musani ukuzenza lukhuni iintliziyo zenu.', 'XHO22'),
(59, 4, 8, 'Kuba uYesu, ukuba ubebaphumzile, ange engabuyanga athethe ngawo omnye umhla.', 'XHO22'),
(59, 4, 9, 'Ngoko ke abantu bakaThixo balindwe luphumlo olunjengolwesabatha.', 'XHO22'),
(59, 4, 10, 'Kuba lowo ungeneyo ekuphumleni kwakhe, naye ngokwakhe uphumle kuyo imisebenzi yakhe, njengoThixo kweyeyakhe.', 'XHO22'),
(59, 4, 11, 'Masikhuthalele ngoko ukungena koko kuphumla, ukuze kungabikho namnye ubuya eyele kwakuloo mzekelo wokungeva.', 'XHO22'),
(59, 4, 12, 'Kuba ilizwi likaThixo liphilile, linamandla, libukhali ngaphezu kwamakrele onke antlangothi mbini; lihlaba liphumele, lide lahlule umphefumlo kwanomoya, amalungu kwanomongo; linokugweba iingcingane nezicamango zentliziyo.', 'XHO22'),
(59, 4, 13, 'Kananjalo akukho sidalwa singabonakaliyo emehlweni akhe; zonke izinto ke zize, zityhilekile, emehlweni alowo esinokuthana ni naye.', 'XHO22'),
(59, 4, 14, 'Ngoko, sinombingeleli omkhulu nje ocande emazulwini, uYesu ke, uNyana kaThixo, masilubambe uvumo lwethu.', 'XHO22'),
(59, 4, 15, 'Kaloku asinambingeleli mkhulu ungenako ukuvelana nezilwelwe zethu; ke, ulingwa ngeento zonke, ngokokufana nathi, engenasono.', 'XHO22'),
(59, 4, 16, 'Masisondele ke ngoko sinokungafihlisi kuyo itrone yobabalo, ukuze samkeliswe inceba, sifumane ubabalo lokusiza ngexesha elililo.', 'XHO22'),
(59, 5, 1, 'Kuba wonke umbingeleli omkhulu, engovele ebantwini nje, umiselwa abantu ezintweni ezilunge kuThixo, ukuze asondeze iminikelo kwanamadini ngenxa yezono;', 'XHO22'),
(59, 5, 2, 'Ngubani na onokuba nemfesane kwabangazi nto nakwabalahlekayo; kuba naye ngokwakhe enxitywe ubulwelwe.', 'XHO22'),
(59, 5, 3, 'Ngenxa yoko ke ufanelwe kukuthi, njengokuba esondezela abantu, kwangokunjalo azisondezele amadini ngenxa yezono.', 'XHO22'),
(59, 5, 4, 'Kanjalo akukho mntu uzithabathela ngokwakhe imbeko leyo; oyithabathayo ngobizwe nguThixo, kwanjengoAron.', 'XHO22'),
(59, 5, 5, 'Ngokunjalo naye uKristu akazizukisanga ngokwakhe, ukuba abe ngumbingeleli omkhulu; wazukiswa ngowathethayo kuye, wathi, UnguNyana wam wena, Mna ndikuzele namhla.', 'XHO22'),
(59, 5, 6, 'Kunjengokuba esithi nakwenye indawo, Wena ungumbingeleli ngonaphakade, Ngokohlobo lukaMelkitsedeke.', 'XHO22'),
(59, 5, 7, 'Owathi ngemihla yenyama yakhe, xa wayesondeze izikhungo kwanezibongozo kulowo unako ukumsindisa ekufeni, kunye nodanduluko olunamandla, neenyembezi, waviwa ngenxa yokoyika kwakhe uThixo;', 'XHO22'),
(59, 5, 8, 'Nakuba ebenguNyana, wafunda ukululama ngobunzima awabuvayo;', 'XHO22'),
(59, 5, 9, 'waza, egqibelele, waba yimbangi yosindiso olungunaphakade kubo bonke abamlulamelayo;', 'XHO22'),
(59, 5, 10, 'ebizwe nguThixo, ukuba abe ngumbingeleli omkhulu ngokohlobo lukaMelkitsedeke.', 'XHO22'),
(59, 5, 11, 'Esineendawo ezininzi zokuthetha ngaye, ezilukhuni ukuchazwa, ekubeni nina naba buthuntu iindlebe.', 'XHO22'),
(59, 5, 12, 'Kuba, nakuba benifanele ukuba ngabafundisi, ngenxa yokuba sekumzuzu nakholwayo, nibuye naswela umntu wokunifundisa iziqalelo zokuqala zezihlabo zikaThixo; nisuke naba ngabafanele ukusezwa amasi, aningabokutya okuqinileyo.', 'XHO22'),
(59, 5, 13, 'Kuba bonke abasasezwa amasi abanamava elizwi lobulungisa, kuba beziintsana.', 'XHO22'),
(59, 5, 14, 'Kodwa ukudla okuqinileyo kokwabakhulileyo, abo bathi, ngenxa yobugcisa, babe nezimvo eziqheliselwe ukucalula okuhle kwanokubi.', 'XHO22'),
(59, 6, 1, 'Ngoko ke masithi, siyeka elengqalo ilizwi elithetha ngoKristu, siqhubele kwelokukhula; singabuyi sibeke isiseko senguquko emisebenzini efileyo, nokukholwa kuThixo;', 'XHO22'),
(59, 6, 2, 'yemfundiso yeembhaptizo, neyokubekwa izandla, neyovuko lwabafileyo, neyomgwebo ongunaphakade.', 'XHO22'),
(59, 6, 3, 'Oko ke sokwenza, ukuba angaba uthe uThixo wavuma.', 'XHO22'),
(59, 6, 4, 'Kuba akunakwenzeka ukuba abo bakha bakhanyiselwa, basiva isipho sasezulwini, baba nesabelo kuMoya oyiNgcwele;', 'XHO22'),
(59, 6, 5, 'Baliva ilizwi elilungileyo likaThixo, kwanamandla ehlabathi elizayo;', 'XHO22'),
(59, 6, 6, 'ukuba bathe bakreqa, ukuze babuye bahlaziyeke enguqukweni; ekubeni bephinda ukuzibethelelela emnqamlezweni uNyana kaThixo, bamhlaze ekuhleni.', 'XHO22'),
(59, 6, 7, 'Kuba umhlaba lo, uyiselayo imvula eza futhi phezu kwawo, uvelise imifuno elungele bona abo ulinywa nokulinywa kubo, wamkela intsikelelo kuThixo.', 'XHO22'),
(59, 6, 8, 'Ke wona, othi uvelise imithana enameva neenkunzane, awunto yanto, ukufuphi nengqalekiso; okuphela kwawo kukutshiswa.', 'XHO22'),
(59, 6, 9, 'Ke ngani nina, zintanda, sikholose ngelokuthi, nineendawo ezilungileyo kunezo, iindawo eziyelelene nosindiso, nakuba sisenjenje ukuthetha;', 'XHO22'),
(59, 6, 10, 'Kuba akanantswela-bulungisa uThixo, ukude awulibale umsebenzi wenu, nomzamo wothando enalubonakalalisa kulo igama lakhe, nabalungiselelayo nje abangcwele, nisamana nibalungiselela.', 'XHO22'),
(59, 6, 11, 'Sinqwenela ke, ukuba ulowo kuni alubonakalalise kwaolo lukhuthalo, asingise kwinzaliseko yethemba, ade ase ekupheleni;', 'XHO22'),
(59, 6, 12, 'Kodwa ke nibe ngabaxelisa abo bathi ngokholo nangomonde bawadla ilifa amadinga.', 'XHO22'),
(59, 6, 13, 'Kuba wathi uThixo, akumbeka ngedinga uAbraham, ekubeni bekungekho namnye umkhulu kunaye angamfungayo, wazifunga yena;', 'XHO22'),
(59, 6, 14, 'Esithi, Inene, ndiya kuthi, ndisikelela, ndikusikelele; ndithi, ndisandisa, ndikwandise.', 'XHO22'),
(59, 6, 15, 'Waza ngokunjalo, enokunyamezela, walifumanisa idinga.', 'XHO22'),
(59, 6, 16, 'Kuba abantu okunene bafunga lowo umkhulu kubo, sithi kubo isifungo siphelise yonke impikiswano, sise engqinisekweni.', 'XHO22'),
(59, 6, 17, 'Uthe ngoko uThixo, athe wathi, ethanda kakhulu ukukubonakalalisa kwiindlalifa zedinga ukungaguquleki kwecebo lakhe, wasuka wakwenza ngesifungo;', 'XHO22'),
(59, 6, 18, 'ukuze kuthi, ngezinto ezimbini ezingenakuguquleka, angenako ukuxoka ngazo uThixo, sithi, thina basindileyo, sabamba ithemba elibekwe phambi kwethu, sibe nentuthuzelo eqinileyo;', 'XHO22'),
(59, 6, 19, 'Ithemba ke esinalo ngokweankile yomphefumlo, linqabile, likwaqinile, lingena liye ngaphaya kwesikhuselo;', 'XHO22'),
(59, 6, 20, 'apho umanduleli wangena khona ngenxa yethu, uYesu, ethe ngokohlobo lukaMelkitsedeke, waba ngumbingeleli omkhulu ongunaphakade.', 'XHO22'),
(59, 7, 1, 'Kaloku uMelkitsedeki lo, ukumkani waseSalem, umbingeleli kaThixo Osenyangweni, wahlangabeza uAbraham, ekubuyeni kwakhe ekubulaleni ookumkani, wamsikelela;', 'XHO22'),
(59, 7, 2, 'Athi uAbraham wamnika nesishumi sazo zonke izinto; kuqala ke, ngentetho evakalayo, ukumkani wobulungisa, aze emveni koko abe ngukumkani waseSalem, oko kukuthi, ngukumkani woxolo;', 'XHO22'),
(59, 7, 3, 'Engenayise nanina, namlibo wakuzalwa; kodwa enziwe wafana noNyana kaThixo; uhlala engumbingeleli ngamaxesha onke.', 'XHO22'),
(59, 7, 4, 'Khangelani ke ngoko ukuba mkhulu kwalo mntu, awathi uAbraham usolukazi wamnika isishumi samaxhoba.', 'XHO22'),
(59, 7, 5, 'Oonyana bakaLevi okunene, babamkelayo ububingeleli, banomthetho wokubanga isishumi kubo abantu ngokomthetho, oko kukuthi, kubo abazalwana babo, nakuba bephume esinqeni sika-Abraham;', 'XHO22'),
(59, 7, 6, 'Kodwa yena, ongenamlibo wakuzalwa uphumayo kubo, ubange isishumi kuAbraham, wasikelela lowo unamadinga.', 'XHO22'),
(59, 7, 7, 'Ke ngokungenakuphikwa, omncinane usikelelwa yeyona inkulu.', 'XHO22'),
(59, 7, 8, 'Kananjalo apha okunene ngabantu abanokufa, abathabatha isishumi; kodwa khona apho owamkelayo, ngongqinelweyo ukuba uhleli.', 'XHO22'),
(59, 7, 9, 'Ukutsho ke ndithi, kubangwe ngoAbraham isishumi nakuLevi, lo wamkelayo izishumi;', 'XHO22'),
(59, 7, 10, 'Kuba wayesesinqeni sikayise, oko uMelkitsedeki wamhlangabezayo.', 'XHO22'),
(59, 7, 11, 'Ke ngoko, ukuba inzaliseko ibingabakho ngobubingeleli bakwaLevi (kuba abantu bewiselwe owobubingeleli umthetho), bekusafuneka ngantoni na ke, ukuba kubuye kuvele mbingeleli wumbi ngokohlobo lukaMelkitsedeke, angabizwa ngokohlobo luka-Aron?', 'XHO22'),
(59, 7, 12, 'Kuba ububingeleli baguqulwa nje, kufuneka ukuba kuguqulwe nomthetho.', 'XHO22'),
(59, 7, 13, 'Kuba lowo, zithethwa ngaye ezi zinto, engowasizwe simbi, ebekungekho namnye kuso ubesinyamekele isibingelelo.', 'XHO22'),
(59, 7, 14, 'Kuba kusekuhleni kanye, ukuba iNkosi yethu yaphuma kwaYuda; isizwe eso angathethanga nto ngaso uMoses ngobubingeleli.', 'XHO22'),
(59, 7, 15, 'Ngoku lo mcimbi ucaca ngakumbi, kuba kuvele mbingeleli wumbi ungowodidi lukaMelkitsedeke,', 'XHO22'),
(59, 7, 16, 'ongathanga amiswe ngokomyalelo womthetho ongowenyama, wamiselwa ngokwamandla obomi obungenasiphelo.', 'XHO22'),
(59, 7, 17, 'Kuba ungqina ngokuthi, Wena ungumbingeleli ngonaphakade, Ngokohlobo lukaMelkitsedeke.', 'XHO22'),
(59, 7, 18, 'Kuba okunene kutshitshiswa umthetho owandulelayo, ngenxa yokuswela amandla, nokungancedi lutho kwawo.', 'XHO22'),
(59, 7, 19, 'Kaloku umthetho lowo awuzalisanga nto; ke kungeniswe phezu kwawo elona themba lilungileyo; esisondela ngayo kuye uThixo.', 'XHO22'),
(59, 7, 20, 'Kaloku ke, wenziwa waba umbingeleli ngaphandle kwesifungo;', 'XHO22'),
(59, 7, 21, '(Kuba abo babingeleli bebengenakufungiswa, kodwa yena ufungelwe ngesifungo salowo wathi kuye, Yafunga iNkosi, ayiyi kuzohlwaya, yathi, Wena ungumbingeleli ngonaphakade, Ngokohlobo lukaMelkitsedeke;)', 'XHO22'),
(59, 7, 22, 'ngangoko ke uYesu waba ngummeli wowona ulungileyo umnqophiso.', 'XHO22'),
(59, 7, 23, 'Bathe bona ababingeleli baba baninzi, ngenxa yokuba ukufa kwakungavumi ukuba bahlale behleli;', 'XHO22'),
(59, 7, 24, 'Ke yena, ngenxa yokuba ehleli ngonaphakade, unobubingeleleli obungenakudlula.', 'XHO22'),
(59, 7, 25, 'Ngoko unako nokubasindisa kanye abo bezayo ngaye kuThixo, ehlala ehleli nje ukuba abathethelele.', 'XHO22'),
(59, 7, 26, 'Kuba ubesifanele umbingeleli omkhulu onjalo, ongcwele, ongenabubi, ongenabala, owahlukileyo kubo aboni, othe waba ngaphezu kwalo izulu;', 'XHO22'),
(59, 7, 27, 'ekungafunekiyo ukuba athi imihla ngemihla, njengababingeleli abakhulu aba, anyuse kuqala amadini ngenxa yezono ezizezakhe, andule ukunyusa ngenxa yezabantu; kuba oko wakwenza kwaba kanye, ekuzinyuseni ngokwakhe ngokwedini.', 'XHO22'),
(59, 7, 28, 'Kuba umthetho umisa abantu abanobulwelwe, babe ngababingeleli abakhulu; Ke lona ilizwi lesifungo, esingasemva komthetho, limise uNyana, ongcwaliswe ngonaphakade.', 'XHO22'),
(59, 8, 1, 'Ke kaloku, eyona nto ibalulekileyo kwezi zinto sizithethileyo: Sinombingeleli omkhulu onje, othe wahlala phantsi ngasekunene kwetrone yobuKhulu emazulwini;', 'XHO22'),
(59, 8, 2, 'umlungiseleli wengcwele, nowomnquba wenyaniso owamiswayo nguYehova, awamiswa ngumntu.', 'XHO22'),
(59, 8, 3, 'Kuba wonke umbingeleli omkhulu umiselwa ukuba asondeze iminikelo kwanamadini; ekuthe ngenxa yoko kwafuneka ukuba naye lo abe nento ayisondezayo.', 'XHO22'),
(59, 8, 4, 'Kuba, ukuba ubelapha emhlabeni, ubengayi kuba ngumbingeleli, ekubeni kukho ababingeleli abanikela izipho ngokomthetho:', 'XHO22'),
(59, 8, 5, 'bona bakhonza umzekelo nesithunzi sezinto zasemazulwini, kwanjengokuba uMoses wayehlatyelwe nguThixo, xa abeza kuwufeza umnquba; kuba wathi, Khangela ukuba uzenze zonke izinto ngokomfuziselo owawuboniswayo entabeni.', 'XHO22'),
(59, 8, 6, 'Kodwa kungoku, uzuze umbuso ogqithiseleyo kulowo, kangangokuba engumlamleli womnqophiso olungileyo ngakumbi, wona usekwe phezu kwawona madinga alungileyo.', 'XHO22'),
(59, 8, 7, 'Kuba, ukuba ubungenakusoleka lowo wokuqala, ubungayi kufunelwa ndawo owesibini.', 'XHO22'),
(59, 8, 8, 'Kuba uthi, esola, Yabona, kuza imihla, itsho iNkosi, endiya kwenza umnqophiso omtsha nendlu kaSirayeli, nendlu kaYuda;', 'XHO22'),
(59, 8, 9, 'Ungahambi ngokomnqophiso endawenzela ooyise, mini ndababamba ngesandla, ndibakhupha ezweni laseYiputa; ngenxa enokuba bengahlalanga emnqophisweni wam, nam andabakhathalela; utsho uYehova.', 'XHO22'),
(59, 8, 10, 'Nguwo lo umnqophiso, endiya kuwenza nendlu kaSirayeli emva kwaloo mihla, utsho uYehova; ndiya kuyibeka imithetho yam engqondweni yabo, ndiyibhale ezintliziyweni zabo, ndibe nguThixo wabo, babe ngabantu bam;', 'XHO22'),
(59, 8, 11, 'Kanjalo abayi kukha bafundise elowo ummelwane wakhe, elowo umzalwana wakhe, esithi, Yazi iNkosi; ngokuba baya kundazi bonke, bethabathela komncinane base komkhulu.', 'XHO22'),
(59, 8, 12, 'Ngokuba ndiya kukuxolela ukungalungisi kwabo, Nezono zabo, nokuchasa kwabo umthetho, ndingabi sakukhumbula.', 'XHO22'),
(59, 8, 13, 'Ngokuthi, Umnqophiso omtsha, uwaluphalisile owokuqala. Ke into ebolileyo, yasihasha, seyikufuphi ukuba ithi shwaka.', 'XHO22'),
(59, 9, 1, 'Ke ngoko nowokuqala umnqophiso wawunezimiselo zombuso, kwanendawo engcwele yehlabathi.', 'XHO22'),
(59, 9, 2, 'Kuba kwakhiwa umnquba lowo, elokuqala lalinesiphatho sezibane, kwanetafile, nesiboniso sezonka; ebizwa ngokuba yingcwele.', 'XHO22'),
(59, 9, 3, 'Ngemva kwekhuselo lesibini kwabakho nomnquba ekuthiwa yingcwele kangcwele;', 'XHO22'),
(59, 9, 4, 'Yayinesitya segolide sokuqhumisela, nayo ityeya yomnqophiso, yalekwe ngegolide ngeenxa zonke, inesitya segolide ngaphakathi esinemana, inentonga ka-Aron eyadubulayo, inawo amacwecwe omnqophiso.', 'XHO22'),
(59, 9, 5, 'Ke ngaphezu kwayo kwema iikerubhi zobuqaqawuli, zisibekele isicamagushelo; esingenakuthetha ngakumbi ngayo ngoku.', 'XHO22'),
(59, 9, 6, 'Ke kaloku, zakubon’ ukuba zenziwe ngokunjalo ezo zinto, ababingeleli bangena okunene amaxa onke kowokuqala umnquba, beyifeza imisebenzi kaThixo.', 'XHO22'),
(59, 9, 7, 'Ke kowesibini kungena kube kanye ngomnyaka umbingeleli omkhulu yedwa, kungengagazi, alisondezayo ngenxa yakhe, nangenxa yeziphoso zabantu.', 'XHO22'),
(59, 9, 8, 'uMoya oyiNgcwele eqondisa ke, ukuba indlela eya kweyona ngcwele ingekabonakali ke, umnquba wokuqala umi.', 'XHO22'),
(59, 9, 9, 'Lowo ube ngumfuziselo wexesha elililo, ekuthi ngalo kusondezwe iminikelo kwanamadini, izinto ezo ezingenako ukumenza agqibelele lowo ukhonzayo, ngokwesazela;', 'XHO22'),
(59, 9, 10, 'ezithe, ndawonye nezinto ezidliwayo, neziselwayo, neentlambululo ngeentlambululo, ezizizimiselo zenyama kuphela, zamiselwa ukuba kude kufike ixesha lolungiso.', 'XHO22'),
(59, 9, 11, 'Kodwa uKristu, ethe weza, engumbingeleli omkhulu wezinto ezilungileyo eziza kubakho, uthe ngowona mkhulu ugqibeleleyo umnquba, ongenakwenziwa ngazandla, oko kukuthi, ongekuko oku kwakhiwa;', 'XHO22'),
(59, 9, 12, 'wangena kwaba kanye kweyona ngcwele, kungengagazi leebhokhwe nelamathole, kungegazi elilelakhe; wasizuzela inkululeko engunaphakade ngokusihlawulela.', 'XHO22'),
(59, 9, 13, 'Kuba, xa igazi leenkomo neleebhokhwe, nothuthu lwethokazi olufefa oyinqambi, lungcwalisela ekuhlambululeni inyama;', 'XHO22'),
(59, 9, 14, 'Lobeka phi na ukugqithisela lona igazi likaKristu, owazisondeza ngoMoya ongunaphakade, engenasiphako, kuye uThixo, ukusihlambulula isazela senu emisebenzini efileyo, ukuze nikhonze uThixo ophilileyo?', 'XHO22'),
(59, 9, 15, 'Ngoko ke ungumlamleli womnqophiso omtsha, ukuze bathi abo babiziweyo (kwakubon’, ukuba kubekho ukufa okuhlawulela izigqitho, ezibe ziphantsi kwawo umnqophiso wokuqala), balamkele idinga lelifa elingunaphakade.', 'XHO22'),
(59, 9, 16, 'Kuba apho kukhoyo umnqophiso womyoleli, kufuneka ukuba kucace ukufa kwalowo unqophisileyo.', 'XHO22'),
(59, 9, 17, 'Kaloku umnqophiso uqiniseka bakuba befile, okanye awuba namandla xenikweni asadla ubomi umyoleli.', 'XHO22'),
(59, 9, 18, 'Ngoko ke nowokuqala umnqophiso awusungulwanga ngaphandle kwegazi.', 'XHO22'),
(59, 9, 19, 'Kuba kuthe, akuwuthetha uMoses umthetho wonke ngokomthetho ebantwini bonke, walithabatha igazi lamathole neleebhokhwe, ndawonye namanzi, noboya obubomvu, nehisope, wayifefa incwadi, kwanabantu bonke;', 'XHO22'),
(59, 9, 20, 'esithi, Eli ligazi lomnqophiso aniwisele umthetho ngawo uThixo.', 'XHO22'),
(59, 9, 21, 'Nomnquba ke, nazo zonke iimpahla zombuso, wazifefa ngegazi.', 'XHO22'),
(59, 9, 22, 'Kananjalo ngokomthetho ndingade ndithi, zonke izinto ziphantse ukuba zihlanjululwe ngegazi; ngaphandle kokuphalala kwegazi akukho kuxolelwa.', 'XHO22'),
(59, 9, 23, 'Kwafuneka ngoko, ukuba okunene izizekelo zezinto ezisemazulwini zihlanjululwe ngezinto ezinjalo; zona zona ezo zinto zasemazulwini, zinamadini alungileyo kunalawo.', 'XHO22'),
(59, 9, 24, 'Kuba uKristu akangenanga kweyona ngcwele inokwenziwa ngezandla, engumfuziselo weyenyaniso; ungene kuwo amazulu ngokwawo, ukuba abonakale ngoku ebusweni bukaThixo ngenxa yethu.', 'XHO22'),
(59, 9, 25, 'Kwakungekho ukuze azinikele futhi, njengokuba umbingeleli omkhulu ebengena kweyona ngcwele iminyaka ngeminyaka, enegazi lenye into;', 'XHO22'),
(59, 9, 26, 'Ange ebemelwe kukuba ebeve ubunzima futhi, kuseloko lasekwayo ihlabathi. Ke uthe ngoku, ekufinyezweni kwawo amaphakade, wabonakalaliswa kwaba kanye, ukuba atshitshise isono ngelilelakhe idini.', 'XHO22'),
(59, 9, 27, 'Kwanjengokuba ke abantu bamiselwe ukuba bafe kube kanye, ize ke emveni koko ibe ngumgwebo;', 'XHO22'),
(59, 9, 28, 'ngokunjalo uKristu, esondezwe kwaba kanye, ukuba asithwale asisuse isono sabaninzi; uya kuthi okwesibini, ngaphandle kwesono, abonakale kwabo bamlindileyo, ezisa usindiso.', 'XHO22'),
(59, 10, 1, 'Kaloku umthetho unesithunzi nje sodwa sezinto ezilungileyo eziza kubakho, ungenaso sona isimilo sazo izinto, awuze ube nako, iminyaka ngeminyaka, ngaloo madini bawasondeza ngokungapheliyo, ukubenza bagqibelele abo basondelayo.', 'XHO22'),
(59, 10, 2, 'Okanye ange engayekwanga na ukusondezwa? ngenxa yokuba abo bakhonzayo, behlanjululwe kwaba kanye, bengasenasazela sazono.', 'XHO22'),
(59, 10, 3, 'Kambe ke, kuwo onke amadini, kubakho nokukhumbula izono unyaka nonyaka.', 'XHO22'),
(59, 10, 4, 'Kaloku igazi leenkunzi zeenkomo neleebhokhwe alinakuzisusa izono.', 'XHO22'),
(59, 10, 5, 'Kungoko, akungena ehlabathini, uthi, Akuthandanga mbingelelo, namnikelo wakudla, ke undilungisele umzimba.', 'XHO22'),
(59, 10, 6, 'Akuwathandanga amadini atshiswayo nawesono.', 'XHO22'),
(59, 10, 7, 'Ndaza ndathi, Yabona, ndiyeza (Encwadini esongwayo kubhaliwe ngam), ndiza kwenza ukuthanda kwakho, Thixo.', 'XHO22'),
(59, 10, 8, 'Entla xa athi, Amadini, amadini, namadini anyukayo, nawesono, akunaliswanga; ezisondezwe ngumthetho;', 'XHO22'),
(59, 10, 9, 'Uthe ke, Yabona, ndiza kwenza ukuthanda kwakho, Thixo. Ususa esokuqala, ukuze amise esesibini.', 'XHO22'),
(59, 10, 10, 'Ngoko kuthanda kwakhe, sangcwaliswa ngokusondezwa komzimba kaYesu Krestu kwaba kanye.', 'XHO22'),
(59, 10, 11, 'Naye wonke umbingeleli umi imihla ngemihla elungiselela, esondeza futhi amaxesha amaninzi kwaloo madini angenako ukuzisusa izono.', 'XHO22'),
(59, 10, 12, 'Kodwa yena lo, esondeze idini lalinye ngenxa yezono, usuke wahlala phantsi ngasekunene kukaThixo ngokungapheliyo;', 'XHO22'),
(59, 10, 13, 'Kususela ngoku elinde ukuba zide iintshaba zakhe zenziwe isihlalo seenyawo zakhe.', 'XHO22'),
(59, 10, 14, 'Kuba ngamnikelo mnye ubenze bagqibelela ngokungapheliyo abo bangcwaliswayo.', 'XHO22'),
(59, 10, 15, 'Usingqinela ke naye uMoya oyiNgcwele;', 'XHO22'),
(59, 10, 16, 'Nguwo lo ke umnqophiso endiya kuwenza nabo emva kwaloo mihla, itsho iNkosi: Ndiya kuyibeka imithetho yam ezintliziyweni zabo, ndize ndiyibhale ezingqondweni zabo;', 'XHO22'),
(59, 10, 17, 'Izono zabo nokuchasa umthetho kwabo ndingabi sakukhumbula.', 'XHO22'),
(59, 10, 18, 'Ke apho uxolelo lwezo zinto lukhoyo, akusekho mnikelo ngenxa yesono.', 'XHO22'),
(59, 10, 19, 'Ngoko ke, bazalwana, ekubeni sinobuganga bokungena kweyona ngcwele, ngegazi likaYesu;', 'XHO22'),
(59, 10, 20, 'Ngendlela entsha, ephilileyo, athe wayilungiselela thina, ephumela ekhusini, oko kukuthi, inyama yakhe;', 'XHO22'),
(59, 10, 21, 'Sinombingeleli omkhulu nje phezu kwendlu kaThixo;', 'XHO22'),
(59, 10, 22, 'masisondele sinentliziyo eyinyaniso, sinenzaliseko yokholo, sifefiwe iintliziyo zethu, sisusiwe isazela esingendawo, nemizimba yethu ihlanjwe ngamanzi amsulwa.', 'XHO22'),
(59, 10, 23, 'Silubambe uvumo lwethu lokholo, singabi nakuxengaxenga; (kuba uthembekile lowo uthembisileyo);', 'XHO22'),
(59, 10, 24, 'Masinyamekelane, sivuselelane eluthandweni nasemisebenzini emihle;', 'XHO22'),
(59, 10, 25, 'singakutyesheli ukuhlanganisana kwethu ndawonye, njengoko kulisiko labathile; masivuselelane ngokungakumbi, niyibona nje ukuba imini leyo iyasondela.', 'XHO22'),
(59, 10, 26, 'Kuba, xa sithi sone ngabomi, emveni kokuba sakwamkelayo ukwazi inyaniso, akusasele dini ngenxa yezono;', 'XHO22'),
(59, 10, 27, 'Kusele ke ukulinda okoyikekayo kakhulu, umgwebo nomsindo womlilo, oza kudla abo bachasileyo.', 'XHO22'),
(59, 10, 28, 'Lowo uwugatyayo umthetho kaMoses, wafa engenanceba, phantsi kwamangqina amabini, nokuba ngamathathu.', 'XHO22'),
(59, 10, 29, 'Niba, woba ufanelwe yisohlwayo esiqatha kangakanani na, lowo umnyathelele phantsi uNyana kaThixo, wabalelela igazi lomnqophiso, angcwaliswe ngalo, ukuba liyinto engeyiyo, ewenza into engento uMoya, lobabalo?', 'XHO22'),
(59, 10, 30, 'Kaloku siyamazi lowo watshoyo ukuthi, Impindezelo yeyam, kuya kubuyekeza mna, itsho iNkosi. Kananjalo wathi, iNkosi iya kubagweba abantu bayo.', 'XHO22'),
(59, 10, 31, 'Kuyoyikeka ke ukweyela ezandleni zoThixo ophilileyo.', 'XHO22'),
(59, 10, 32, 'Khumbulani ke imihla yangaphambili, enakhanyiselwa ngayo, nanyamezela umzamo omkhulu weembandezelo;', 'XHO22'),
(59, 10, 33, 'Nani ke nenziwa intlekisa nokungcikivwa nangeembandezelo; ngokuyinxenye, nani baba ngamadlelane abenzi bobubi.', 'XHO22'),
(59, 10, 34, 'Kuba navelana nam ndisezintanjeni; kananjalo nakwamkela ngovuyo ukuphangwa kweempahla zenu, nisazi ukuba ninayo kuni ngokwenu emazulwini eyona mpahla ilungileyo, ihlala ihleli.', 'XHO22'),
(59, 10, 35, 'Musani ukukulahla ngoko ukungafihlisi kwenu, kona kunokwamkela umvuzo omkhulu.', 'XHO22'),
(59, 10, 36, 'Kufuneka ukuba nibe nomonde, ukuze nithi, nakuba nikwenzile ukuthanda kukaThixo, nizuzane nalo idinga.', 'XHO22'),
(59, 10, 37, 'Kuba kusaya kuba mzuzwana mncinanana, Athi lowo uzayo afike, angalibali.', 'XHO22'),
(59, 10, 38, 'Ke lowo ulilungisa uya kudla ubomi obuphuma elukholweni; kodwa ukuba uthe wahlehla, awuyi kukholiswa nguye umphefumlo wam.', 'XHO22'),
(59, 10, 39, 'Ke thina asingabahlehla baye entshabalalweni; kodwa ke kwabakholwayo, kukusindiswa komphefumlo.', 'XHO22'),
(59, 11, 1, 'Ukholo ke kukukholosa ngezinto ezithenjiweyo; kukweyiseka bubukho bezinto ezingabonwayo.', 'XHO22'),
(59, 11, 2, 'Kuba amanyange angqinelwa ngokuba nalo.', 'XHO22'),
(59, 11, 3, 'Ngokholo siyaqonda ukuba indalo le yonke yenziwe yabakho ngelizwi likaThixo, ukuze izinto ezi zibonwayo zibe aziphume kwezibonakalayo.', 'XHO22'),
(59, 11, 4, 'Ngokholo uAbheli wasondeza kuye uThixo umbingelelo ogqithileyo kunokaKayin, awangqinelwa ngawo ukuba ulilungisa, uThixo wayingqinelayo nje iminikelo yakhe; ngalo ke, efile nje, usathetha.', 'XHO22'),
(59, 11, 5, 'Ngokholo uEnoki wafuduselwa emazulwini ukuba angaboni kufa; akaba nakufunyanwa ke, ngenxa enokuba uThixo wamfudusayo; kuba ngaphambi kokufuduswa kwakhe, wangqinelwa ukuba umkholise kakuhle uThixo.', 'XHO22'),
(59, 11, 6, 'Ke, lungekho ukholo, akunakubakho ukumkholisa kakuhle; kuba lowo uzayo kuThixo, umelwe kukukholwa ukuba ukho, nokuba ungumvuzi wabo bamfunayo.', 'XHO22'),
(59, 11, 7, 'Ngokholo uNowa, ehlatyelwe nguThixo ngazo izinto ebezingekabonwa, wathi ngokoyika uThixo wawakha umkhombe wokusindisa indlu yakhe, athe ngalo waligweba ihlabathi, waba yindlalifa yobulungisa obungokokholo.', 'XHO22'),
(59, 11, 8, 'Ngokholo uAbraham wathi, akubizwa, Wakululamela ukuba aphume, aye kuloo ndawo abeza kuyamkela ukuba ibe lilifa; waphuma engazi apho aya khona.', 'XHO22'),
(59, 11, 9, 'Ngokholo waba ngumphambukeli emhlabeni wedinga, wanga ukowasemzini, ehleli eminqubeni, ndawonye noIsake noYakobi, beziindlalifa zalo elo dinga kunye naye.', 'XHO22'),
(59, 11, 10, 'Kuba wayewulindele umzi lo uneziseko, ungcibi, umbangi wawo, inguThixo.', 'XHO22'),
(59, 11, 11, 'Ngokholo noSara ngokwakhe wamkela amandla okumisa umzi, waza wazala, liseligqithile ixesha lokuba amithe, ekubeni wambalela ekuthini uthembekile lowo wabekayo ngedinga.', 'XHO22'),
(59, 11, 12, 'Kungoko kwazalwayo nangomnye, engofileyo ke lowo, into engangeenkwenkwezi zezulu ukuba yintabalala, nanjengentlabathi eselunxwemeni lolwandle, engenakubalwa.', 'XHO22'),
(59, 11, 13, 'Aba bonke bafa ngokokholo, bengawafumananga amadinga; besuka bawabona kude, bakholosa, bathakazela, bavuma ukuba bangabasemzini, balundwendwe emhlabeni.', 'XHO22'),
(59, 11, 14, 'Kuba abo bathetha izinto ezinjalo, babonakalalisa ukuba bangxamele ikowabo.', 'XHO22'),
(59, 11, 15, 'Ke, ukuba okunene babekhumbula elo babephuma kulo, ngebaba nethuba lokubuyela kulo.', 'XHO22'),
(59, 11, 16, 'Kodwa ngoku bazolulela kwelona lilungileyo, oko kukuthi, kwelasemazulwini. Kungoko uThixo angenazintloni ngabo, ukuba kuthiwe unguThixo wabo, kuba ebalungisele umzi.', 'XHO22'),
(59, 11, 17, 'Ngokholo uAbraham wamnikela uIsake, ekuvavanyweni kwakhe;', 'XHO22'),
(59, 11, 18, 'Kwakusithiwa ngaye, Imbewu yakho iya kubizwa ngoIsake;', 'XHO22'),
(59, 11, 19, 'ekubalele ngokuba uThixo unako ukuvusa nakwabafileyo; apho athe wamthabatha khona ngokomfanekiso.', 'XHO22'),
(59, 11, 20, 'Ngokholo uIsake wabasikelela uYakobi noEsawu, ngazo izinto eziza kubakho.', 'XHO22'),
(59, 11, 21, 'Ngokholo uYakobi wabasikelela bobabini oonyana bakaYosefu, xa aqhawukayo; waqubuda esimelele ngentonga yakhe.', 'XHO22'),
(59, 11, 22, 'Ngokholo uYosefu uthe, xa abhubhayo, wakukhankanya ukuphuma koonyana bakaSirayeli; Wawisa umthetho ngawo amathambo akhe.', 'XHO22'),
(59, 11, 23, 'Ngokholo uMoses uthe akuba ezelwe, wafihlwa iinyanga zantathu ngabazali bakhe, ngenxa enokuba bambona ukuba ungumntwana omhle; abawoyika umthetho wokumkani.', 'XHO22'),
(59, 11, 24, 'Ngokholo uMoses uthe, akuba mkhulu, akavuma ukubizwa ngokuba ungunyana wentombi kaFaro;', 'XHO22'),
(59, 11, 25, 'enyula kanye ukuba aphathwe kakubi, ndawonye nabantu bakaThixo, kunokuba axhamle isono okomzuzwana;', 'XHO22'),
(59, 11, 26, 'ekubalele ukungcikivwa kukaKristu kububutyebi obungaphezu kobutyebi baseYiputa, kuba wayekhangele ukwamkela umvuzo.', 'XHO22'),
(59, 11, 27, 'Ngokholo wayishiya iYiputa, akawoyika umsindo wokumkani; kuba wakrota, wanga uyambona lowo ungenakubonwa.', 'XHO22'),
(59, 11, 28, 'Ngokholo uyenzile ipasika nokutshizwa kwegazi, ukuze lowo wayewatshabalalisa amazibulo angawachukumisi.', 'XHO22'),
(59, 11, 29, 'Ngokholo baluwela uLwandle oluBomvu, kwanga kusemhlabeni owomileyo; athe amaYiputa, akukulinga oko, antywiliselwa.', 'XHO22'),
(59, 11, 30, 'Ngokholo zawa iindonga zaseYeriko, zakuba zijikeleziwe iintsuku zasixhenxe.', 'XHO22'),
(59, 11, 31, 'Ngokholo uRahabhi ihenyukazi akatshabalalanga nabo bangevayo, akuba ezamkele ngoxolo iintlola.', 'XHO22'),
(59, 11, 32, 'Ndisathetha ntoni na? Kuba ndingaphelelwa lixesha ndibalisa ngoGidiyon, kwanoBharaki, noSamson, noYifeta; ekaDavide, noSamuweli, neyabaprofeti;', 'XHO22'),
(59, 11, 33, 'abathe ngokholo beyisa izikumkani, basebenza ubulungisa, bazuza amadinga, bavingca imilomo yeengonyama;', 'XHO22'),
(59, 11, 34, 'bacima amandla omlilo, basinda kwiintlangothi zekrele, bomelela ekubeni bebeswele amandla, baba ngamagorha emfazweni, bagxotha imikhosi yabasemzini.', 'XHO22'),
(59, 11, 35, 'Abafazi babazuza abafileyo babo bevukile; abanye ke batyakatywa, bengalwamkelanga ukhululo; ukuze bazuze uvuko olulungileyo;', 'XHO22'),
(59, 11, 36, 'Bambi ke benziwa intlekisa, batyakatywa; phezu koko ke baviswa imixokelelwane nentolongo;', 'XHO22'),
(59, 11, 37, 'baxulutywa ngamatye, basarhwa kubini, bahendwa; babulawa ngekrele; beswele, bebandezelwa, bengcungcuthekiswa;', 'XHO22'),
(59, 11, 38, '(elalingabafanelanga ihlabathi), bedinga koozintlango, noozintaba, noomiqolomba, nakwimiqolomba yomhlaba.', 'XHO22'),
(59, 11, 39, 'Aba ke bonke, ngenxa yokuba bangqinelwe ngokholo, abalamkelanga idinga;', 'XHO22'),
(59, 11, 40, 'uThixo esazalelele thina ngeyona nto ilungileyo, ukuze bangenziwa bagqibelele ngaphandle kwethu.', 'XHO22'),
(59, 12, 1, 'Ngoko ke, nathi ke, siphahlwe lilifu elingakanana nje lamangqina, masithi nathi, siwubekile nxamnye wonke umthwalo, naso isono esinokusirhawula ngokusisulu, silubaleke ngomonde ugqatso olubekwe phambi kwethu;', 'XHO22'),
(59, 12, 2, 'sise amehlo kuyo iMbangi noMgqibelelisi wokholo lwethu, uYesu; owathi, ngenxa yovuyo olwalubekwe phambi kwakhe, wawunyamezela umnqamlezo, elidelile ihlazo, waza wahlala phantsi ngasekunene kwetrone kaThixo.', 'XHO22'),
(59, 12, 3, 'Mqondeni lowo ukunyamezeleyo ukumphikisa okungaka kwaboni, ukuze ningadinwa, ingatyhafi imiphefumlo yenu.', 'XHO22'),
(59, 12, 4, 'Nizamana nesono kude kube segazini.', 'XHO22'),
(59, 12, 5, 'Kananjalo senilulibele uvuselelo olu, luthetha kuni njengakubantwana, lusithi, Nyana wam, musa ukukucekisa ukuqeqesha kweNkosi, Kananjalo ungatyhafi wakohlwaywa yiyo;', 'XHO22'),
(59, 12, 6, 'Kuba lowo imthandayo iNkosi iyamqeqesha, Ibatyakatye ke bonke oonyana ebamkelayo.', 'XHO22'),
(59, 12, 7, 'Ukuba ninyamezela uqeqesho, uThixo woba uniphethe njengoonyana; kuba nguwuphi na unyana ongaqeqeshwayo nguyise?', 'XHO22'),
(59, 12, 8, 'Ukuba ke aniqeqeshwa, abathe baba ngamadlelane ngalo bonke, noba nina niyimigqakhwe ngoko, aningoonyana.', 'XHO22'),
(59, 12, 9, 'Kananjalo sibe sinabo oobawo benyama okunene besiqeqesha, sabahlonela; asiyi kuthi ngakumbi na sithobele uYise woomoya bonke, sidle ubomi?', 'XHO22'),
(59, 12, 10, 'Kuba bona okunene basiqeqesha imihla embalwa, ngokokuzithandela kwabo; kodwa yena usilungele, ukuze sahlulelane ngobungcwele bakhe.', 'XHO22'),
(59, 12, 11, 'Lonke ke uqeqesho okunene ngokwakalokunje alubonakali luluvuyo, lubonakala luyintlungu; kodwa kamva lubanika isiqhamo esiluxolo, oko kukuthi sobulungisa, abo baqhelisiweyo lulo.', 'XHO22'),
(59, 12, 12, 'Kungoko ndithi, Zimiseni izandla eziwileyo, namadolo anedumbe;', 'XHO22'),
(59, 12, 13, 'iinyawo zenu nizenzele imizila ethe tye, ukuze into eqhwalelayo ingaphambuki endleleni; kanye ke mayiphiliswe.', 'XHO22'),
(59, 12, 14, 'Phuthumani uxolo nabantu bonke, nobungcwele, ekungekho namnye uya kuyibona iNkosi, engenabo;', 'XHO22'),
(59, 12, 15, 'Lumkani kungabikho mntu uphulukana nalo ubabalo lukaThixo, hleze kuvele ingcambu yobukrakra, inikhathaze, badyobhe abaninzi ngayo;', 'XHO22'),
(59, 12, 16, 'kungabikho bani ulihenyu, nokuba ngumhlambeli, njengoEsawu, owathi ngenxa yentwana yokudla wananisa ngobuzibulo bakhe.', 'XHO22'),
(59, 12, 17, 'Niyazi ukuba wathi kamva, ethanda ukuyidla ilifa intsikelelo, walahlwa; kuba akafumananga thuba languquko, noko wayingxamelayo ngeenyembezi.', 'XHO22'),
(59, 12, 18, 'Kuba anizanga entabeni echukunyiswayo, isitsha ngumlilo, nakubumnyama, nakubumnyama, nakuqhwithela;', 'XHO22'),
(59, 12, 19, 'nesandi sesigodlo, nesandi samazwi; elathi bona abalivayo babùya ukuba lingabi sathethwa kubo ilizwi;', 'XHO22'),
(59, 12, 20, 'Kuba babengenakuwuthwala loo umthetho, othi, Nokuba inkomo yayichukumise intaba, yayiza kuxulutywa ngamatye, okanye ihlabe ngomkhonto.', 'XHO22'),
(59, 12, 21, 'Yaye loo mbono wawusoyikeka kangangokuba uMoses athi, Ndiyoyika kakhulu, ndingcangcazela.', 'XHO22'),
(59, 12, 22, 'Ke nina nifikile entabeni yeZiyon, nasemzini kaThixo ophilileyo, iYerusalem yasemazulwini, nakumkhosi ongenakubalwa wezithunywa zezulu;', 'XHO22'),
(59, 12, 23, 'Ibandla, nebandla lamazibulo, abhaliweyo emazulwini, nakuThixo uMgwebi wabo bonke, nakumoya wamalungisa agqibelele;', 'XHO22'),
(59, 12, 24, 'nakuMlamleli womnqophiso omtsha, uYesu, nakwigazi lokufefa, elithetha ezona zinto zilungileyo kunelika-Abheli.', 'XHO22'),
(59, 12, 25, 'Lumkani, ningamali lowo uthethayo. Kuba, ukuba abo bamalayo lowo wathethayo emhlabeni, abasindanga, kobeka phi na ke ukugqithisela kuthi, ukuba siyasuka simfulathele lowo uthethayo esemazulwini;', 'XHO22'),
(59, 12, 26, 'olizwi oko lawuzamazamisayo umhlaba; ngoku ke ubeke ngedinga, esithi, Kusaya kuba ngumzuzwana endiya kuthi mna ndinganyikimisi mhlaba wodwa, ndinyikimise nalo izulu.', 'XHO22'),
(59, 12, 27, 'Eli ke lithi: Kusaya kuba ngumzuzwana, kubonakalalisa ukuguqulwa kwezinto ezizanyazanyiswayo, ngathi kokwezenziweyo, ukuze zihlale zona ezingenakuzanyazanyiswa.', 'XHO22'),
(59, 12, 28, 'Masithi ngoko, sisamkela ubukumkani obungenakuzanyazanyiswa nje, sibe nombulelo, simkhonze ngawo uThixo ngokukholekileyo, sinokuhlonela nokoyika;', 'XHO22'),
(59, 12, 29, 'Kaloku uThixo wethu ungumlilo odlayo.', 'XHO22'),
(59, 13, 1, 'Uthando lobuzalwana maluhlale luhleli.', 'XHO22'),
(59, 13, 2, 'Musani ukukulibala ukubuka iindwendwe; kuba ngokwenjenjalo inxenye ibuke izithunywa zezulu, ingazi.', 'XHO22'),
(59, 13, 3, 'Bakhumbuleleni abakhonkxiweyo, ninge nikhonkxiwe nani; nabo ke baphathwa kakubi, njengokuba nisemzimbeni nani.', 'XHO22'),
(59, 13, 4, 'Umtshato mawubekeke ngeendlela zonke, nesilili masingadyojwa; ke abenza umbulo nabakrexezi, uThixo uya kubagweba.', 'XHO22'),
(59, 13, 5, 'Iihambo zenu mazingabi nankanuko; yanelani zizinto eninazo; kuba etshilo ukuthi, Andiyi kukha ndikushiye, ndingayi kukushiya;', 'XHO22'),
(59, 13, 6, 'ngokokude somelele sithi, iNkosi ilusizo lwam, andiyi koyika;', 'XHO22'),
(59, 13, 7, 'Bakhumbuleleni abakhokeli benu, abalithethayo kuni ilizwi likaThixo; nisingasinga impumelelo yehambo yabo, nixelise bona elukholweni lwabo.', 'XHO22'),
(59, 13, 8, 'uYesu Krestu ikwanguye izolo, nanamhla, kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(59, 13, 9, 'Musani ukuphetshethwa ziimfundiso ngeemfundiso nezingezizo. Kuba kuhle ukuba intliziyo iqine lubabalo; kungabi ngeento ezidliwayo, abangancedekanga zizo abo bahamba kuzo.', 'XHO22'),
(59, 13, 10, 'Sinesibingelelo thina, abangenagunya lakudla kuso abo bakhonza umnquba.', 'XHO22'),
(59, 13, 11, 'Kaloku ezo zinto ziphilileyo, zigazi lazo lingeniswa ngenxa yesono kweyona ngcwele nguye umbingeleli omkhulu, imizimba yazo itshiselwa ngaphandle kweminquba.', 'XHO22'),
(59, 13, 12, 'Kungoko athe noYesu, ukuze abangcwalise abantu ngegazi elilelakhe, weva ubunzima ngaphandle kwesango.', 'XHO22'),
(59, 13, 13, 'Masiphume ke ngoko siye kuye ngaphandle kweminquba, sithwele ingcikivo yakhe.', 'XHO22'),
(59, 13, 14, 'Kuba apha asinamzi uhleliyo, singxamele lowo uza kuza.', 'XHO22'),
(59, 13, 15, 'Ngoko masithi ngaye sinyuse ngamaxesha onke kuye uThixo umbingelelo wendumiso, oko kukuthi, isiqhamo somlomo olivumayo igama lakhe.', 'XHO22'),
(59, 13, 16, 'Ke ukwenza okulungileyo nobudlelane, musani ukukulibala; kuba imibingelelo enjalo uyakholiswa kakuhle yiyo uThixo.', 'XHO22'),
(59, 13, 17, 'Bathambeleni abakhokeli benu, nibathobele; kuba bona abalali, ngenxa yemiphefumlo yenu, ngokwabaza kuphendula, ukuze bakwenze oko bevuyile, bengancwini; kuba anisizakali kuko oko.', 'XHO22'),
(59, 13, 18, 'Sithandazeleni; kuba sikholosekile kukuthi sinesazela esilungileyo, singa ezintweni zonke singahamba kakuhle.', 'XHO22'),
(59, 13, 19, 'Ndiyavuselela ke ngokugqithiseleyo, ukuba nenze oko, ukuze ndibuyiselwe kuni kamsinyane.', 'XHO22'),
(59, 13, 20, 'Ke kaloku uThixo woxolo, owamvusayo kwabafileyo iNkosi yethu uYesu Kristu, uMalusi omkhulu wezimvu, ngegazi lomnqophiso ongunaphakade,', 'XHO22'),
(59, 13, 21, 'wanga anganilungisa, nigqibelele emisebenzini yonke elungileyo, ukuze nikwenze ukuthanda kwakhe, esenza ngaphakathi kwenu oko kukholekileyo emehlweni akhe, ngoYesu Kristu. Kuye makubekho uzuko, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(59, 13, 22, 'Ndiyanivuselela ke nina, bazalwana, linyamezeleni ilizwi lovuselelo olu; kuba ke ndinibhalele ngokufutshane.', 'XHO22'),
(59, 13, 23, 'Yazini ukuba umzalwana wethu uTimoti ukhululwe; endothi, ukuba uthe wafika kamsinya, ndinibone ndinaye.', 'XHO22'),
(59, 13, 24, 'Babuliseni bonke abakhokeli benu, nakubo bonke abangcwele. Bayanibulisa abemi baseItali.', 'XHO22'),
(59, 13, 25, 'Ubabalo malube nani nonke. Amen. (Ibhalelwe amaHebhere evela eItali, nguTimoti.)', 'XHO22'),
(60, 1, 1, 'uYakobi, umkhonzi kaThixo neNkosi uYesu Kristu, ubhalela izizwe ezilishumi elinazibini eziziintsali, ebulisa.', 'XHO22'),
(60, 1, 2, 'Kugqaleni kuluvuyo olukhulu, bazalwana bam, xa niwela kwizilingo ngezilingo;', 'XHO22'),
(60, 1, 3, 'nisazi nje ukuba ucikido lokholo lwenu lusebenza umonde.', 'XHO22'),
(60, 1, 4, 'Umonde ke mawube nomsebenzi ozalisekileyo, ukuze nibe ngabazalisekileyo, abagqibeleleyo, ningaswele nto.', 'XHO22'),
(60, 1, 5, 'Ukuba ubani kuni uswele ubulumko, makacele kuThixo, onika bonke engenakumbi, engangcikivi; kwaye uya kuyinikwa.', 'XHO22'),
(60, 1, 6, 'Makacele ekholwa, engathandabuzi; Kuba lowo uthandabuzayo ufana nokulatyuza kolwandle, luqhutywa ngumoya, luntsileka.', 'XHO22'),
(60, 1, 7, 'Kuba loo mntu makangabi uya kwamkela nto kuyo iNkosi.', 'XHO22'),
(60, 1, 8, 'Indoda emphefumlo umbaxa iyahlozinga kuzo zonke iindlela zayo.', 'XHO22'),
(60, 1, 9, 'Ke umzalwana othobekileyo makaqhayise ngako ukuphakama kwakhe;', 'XHO22'),
(60, 1, 10, 'osisityebi ke makaqhayise ngako ukuthobeka kwakhe; ngokuba uya kudlula njengentyantyambo yengca.', 'XHO22'),
(60, 1, 11, 'Kuba lithi lakuphuma ilanga, kunye nolophu, liyitshise ingca, ize ke intyantyambo yayo ivuthuluke, buthi nobuhle bembonakalo yayo butshabalale; siya kwenjenjalo nesityebi ukungcumbeka eluhambeni lwaso.', 'XHO22'),
(60, 1, 12, 'Inoyolo indoda ekunyamezelayo ukulingwa; ngokuba yothi, yakuba icikidekile, isamkele isithsaba sobomi, ebabeke ngedinga iNkosi abo bayithandayo.', 'XHO22'),
(60, 1, 13, 'Makungabikho namnye utshoyo akuhendwa ukuthi, Ndihendwa kwaThixo;', 'XHO22'),
(60, 1, 14, 'Elowo ke uhendwa akutsalwa, abanjiswe, yeyakhe inkanuko.', 'XHO22'),
(60, 1, 15, 'Ithi inkanuko, yakuba ithabathile, izale isono; isono, xa sithe safezwa, sivelisa ukufa.', 'XHO22'),
(60, 1, 16, 'Musani ukulahlekiswa, bazalwana bam baziintanda.', 'XHO22'),
(60, 1, 17, 'Sonke isipho esilungileyo, naso sonke isipho esigqibeleleyo, sesaphezulu; sihla sivela kuye uYise wezikhanyiso, ongenabuyambo nasithunzi sakujika.', 'XHO22'),
(60, 1, 18, 'Uthandile, wasizala ngelizwi lenyaniso, ukuze sibe yintlahlela yazo izidalwa zakhe.', 'XHO22'),
(60, 1, 19, 'Ngoko ke, bazalwana bam baziintanda, makathi umntu wonke enze msinya ukuva, enze kade ukuthetha, enze kade ukuqumba;', 'XHO22'),
(60, 1, 20, 'Kuba ingqumbo yendoda ayisebenzi bulungisa bukaThixo.', 'XHO22'),
(60, 1, 21, 'Kungoko endithi, nibubekile nxamnye bonke ubunyhukunyhuku nokuphuphuma ulunya, namkele ninobulali ilizwi elimiselweyo kuni, elinako ukuyisindisa imiphefumlo yenu.', 'XHO22'),
(60, 1, 22, 'Yibani ke ngabenzi belizwi, ningabi ngabaphulaphuli kodwa, nizikhohlisa.', 'XHO22'),
(60, 1, 23, 'Kuba umntu, ukuba ungumphulaphuli nje welizwi, engemenzi walo, lowo ufana nendoda eqiqa ubuso bayo bobuntu esipilini;', 'XHO22'),
(60, 1, 24, 'kuba iyaziqiqa, imke, ilibale kwaoko ukuba ibinjani na.', 'XHO22'),
(60, 1, 25, 'Ke yena oqondela emthethweni ogqibeleleyo wayo inkululeko, ahlale ehleli kuwo, ethe nje akaba ngumphulaphuli olibalayo, esuke waba ngumenzi womsebenzi: yena uya kuba noyolo ekwenzeni kwakhe.', 'XHO22'),
(60, 1, 26, 'Ukuba ubani kuni uba ungonqulayo, abe engalubambi ngomkhala ulwimi lwakhe, elukuhla intliziyo yakhe, unqulo lwalowo alunto yanto.', 'XHO22'),
(60, 1, 27, 'Unqulo lona oluhlambulukileyo, olu ngadyobhekileyo phambi koThixo uBawo, lulo olu: kukuvelela iinkedama nabahlolokazi embandezelweni, yabo: kukuzigcina engenabala kulo ihlabathi.', 'XHO22'),
(60, 2, 1, 'Bazalwana bam, musani ukukuphatha ngokukhetha buso bamntu, ukukholwa yiNkosi yethu yozuko, uYesu Kristu.', 'XHO22'),
(60, 2, 2, 'Kuba, xa kuthe kwangena endlwini yenu yesikhungu indoda enemisesane yegolide, inengubo eqaqambileyo, kwaza ke kwangena ihlwempu, lineengubo ezimdaka;', 'XHO22'),
(60, 2, 3, 'naza nakhangela kulowo uvethe ingubo eqaqambileyo, nathi kuye, Hlala apha wena kakuhle; ndithi kwihlwempu, Yima wena phaya, mhlawumbi uhlale phantsi phantsi kwesihlalo seenyawo zam;', 'XHO22'),
(60, 2, 4, 'Anikhethi buso na phakathi kwenu, naba ngabagwebi bengcinga embi?', 'XHO22'),
(60, 2, 5, 'Yivani, bazalwana bam baziintanda: uThixo akawanyulanga na amahlwempu eli hlabathi, ukuba abe zizityebi zokholo, neendlalifa zobukumkani, awababeka ngedinga abo bamthandayo?', 'XHO22'),
(60, 2, 6, 'Kodwa nina nibadelile abasweleyo. Izityebi azinishicileli na? Azinirholeli ematyaleni na?', 'XHO22'),
(60, 2, 7, 'Azilinyelisi na igama elihle enabizwa ngalo?', 'XHO22'),
(60, 2, 8, 'Ukuba niyawuzalisa umthetho wakomkhulu, ngokwesibhalo esithi, Uze umthande ummelwane wakho ngoko uzithanda ngako, nityaphile;', 'XHO22'),
(60, 2, 9, 'Kodwa ukuba nikhetha ubuso bomntu, nisebenza isono, niyohlwaywa ngumthetho ngokwabagqithi.', 'XHO22'),
(60, 2, 10, 'Kuba nabani na oya kuwugcina umthetho uphela, aze aphule umthetho omnye, unetyala layo yonke.', 'XHO22'),
(60, 2, 11, 'Kuba lowo wathi, Musa ukukrexeza, ubuye wathi, Musa ukubulala. Ke, ukuba akukrexezi, kodwa ubulala, usewuthe waba ngumgqithi-mthetho.', 'XHO22'),
(60, 2, 12, 'Yithini ukuthetha, yithini ukwenza, nenze njengabaza kugwetywa ngomthetho wenkululeko.', 'XHO22'),
(60, 2, 13, 'Kuba umgwebo awuyi kuba natarhu kulowo ungenzanga nceba; yaye inceba iyawuqhayisela umgwebo.', 'XHO22'),
(60, 2, 14, 'Kunceda ntoni na, bazalwana bam, ukuba umntu uthi unokholo, abe imisebenzi ke engenayo? Lunako na ukumsindisa ukholo?', 'XHO22'),
(60, 2, 15, 'Ukuba ke umzalwana, nokuba ngudade, uhamba ze, eswele ukudla kwemihla ngemihla,', 'XHO22'),
(60, 2, 16, 'Athi ke omnye wenu kubo, Hambani ninoxolo, yothani, hluthani; nibe ke ningabaniki izinto ezo ziwufaneleyo umzimba; inceda ntoni na?', 'XHO22'),
(60, 2, 17, 'Ngokunjalo nalo ukholo, ukuba luthi lungabi namisebenzi, lufile ngokukokwalo.', 'XHO22'),
(60, 2, 18, 'Wosuka omnye, athi, Wena unokholo, ke mna ndinemisebenzi; ndibonise ukholo lwakho olungenamisebenzi yakho; nam ndokubonisa ngokwasemisebenzini yam ukholo lwam.', 'XHO22'),
(60, 2, 19, 'Wena uyakholwa ukuba uThixo mnye; uyatyapha; nazo iidemon ziyakholwa, zibe namanwele.', 'XHO22'),
(60, 2, 20, 'Uyathanda na ke ukwazi, mntundini ungento, ukuba ukholo olu, ingekho imisebenzi, lufile?', 'XHO22'),
(60, 2, 21, 'uAbraham, ubawo wethu, akagwetyelwanga na ngokwasemisebenzini, akumnikela uIsake, unyana wakhe, phezu kwesibingelelo?', 'XHO22'),
(60, 2, 22, 'Uyabona ukuba ukholo lwalusebenzisana nemisebenzi yakhe, lwenziwa wagqibelela ukholo ngokwasemisebenzini?', 'XHO22'),
(60, 2, 23, 'Saza sazaliseka isibhalo esithi, Wakholwa ke uAbraham nguye uThixo, kwaza oko kwabalelwa ebulungiseni kuye, wabizwa ngokuba sisihlobo sikaThixo.', 'XHO22'),
(60, 2, 24, 'Niyabona ke ngoko, ukuba umntu uyagwetyelwa ngokwasemisebenzini; kungabi ngokwaselukholweni lodwa.', 'XHO22'),
(60, 2, 25, 'Ngokunjalo ke, noRahabhi, ihenyukazi, akagwetyelwanga na ngokwasemisebenzini, akubamkela ngobubele abathunywa, abakhuphele phandle ngayimbi indlela?', 'XHO22'),
(60, 2, 26, 'Kaloku, kwanjengokuba umzimba, ungekho umoya, ufile, ngokunjalo ukholo, ingekho imisebenzi, lufile nalo.', 'XHO22'),
(60, 3, 1, 'Bana beso, le se ke la ba baruti ba bangata, le ntse le tseba hobane re tla fumana kahlolo e fetisang ka boholo.', 'XHO22'),
(60, 3, 2, 'Hobane bohle re kgopisa bohle. Haeba leha e le mang a sa khoptjoe puong, eo ke monna ea phethahetseng, ea khonang ho laola le mele oohle ka tōmo.', 'XHO22'),
(60, 3, 3, 'Bona, re kenya ditomo melomong ya dipere, hore di tle di re utlwe; mme re fetola mmele wa bona kaofela.', 'XHO22'),
(60, 3, 4, 'Bonang, le dikepe, leha di le kgolo hakalo, mme di kgweetswa ke meya e bohale, di sokololwa ka kerama e nyenyane haholo, di ya moo mmusisi a ratang.', 'XHO22'),
(60, 3, 5, 'Ka mokgwa o jwalo, leleme ke setho se senyenyane, mme le ithorisa ka tse kgolo. Bonang, mollo o monyenyane o hotetsa ntho e kgolo hakakang!', 'XHO22'),
(60, 3, 6, 'Leleme ke mollo, ke lefatshe la bokgopo; mme e tjheswa ka mollo wa dihele.', 'XHO22'),
(60, 3, 7, 'Hobane mefuta yohle ya diphoofolo, le ya dinonyana, le ya dinoha, le ya tse lewatleng, e a thapisoa, mme e se e thapisitswe ke batho.', 'XHO22'),
(60, 3, 8, 'Empa leleme ha ho motho ya ka le thapisang; ke bobe bo sa laoleheng, bo tletse chefo e bolaeang.', 'XHO22'),
(60, 3, 9, 'Ka lona re boka Modimo, Ntate; mme ka lona re rohaka batho, ba bopilweng ka setshwantsho sa Modimo.', 'XHO22'),
(60, 3, 10, 'Molomong o le mong ho tswa poko le thohako. Bana beso, dintho tsena ha di a tshwanela ho ba jwalo.', 'XHO22'),
(60, 3, 11, 'A sediba se ka kolla nqalong e le nngwe metsi a monate le a babang?', 'XHO22'),
(60, 3, 12, 'Banabeso, na sefate sa feiye se ka beha ditholwana tsa mohlwaare na? kapa sefate sa morara, lifeiga? ka mokgwa o jwalo ha ho sediba se ka ntshang metsi a letswai le a monate.', 'XHO22'),
(60, 3, 13, 'Ke mang hara lona ya bohlale, ya nang le tsebo? a ke a bonahatse ka metsamao e metle mesebetsi ya hae ka bonolo ba bohlale.', 'XHO22'),
(60, 3, 14, 'Empa ekare ha le ena le mohono o babang, le kgang ka dipelong tsa lona, le se ke la ithorisa, mme le se ke la bua leshano le hanyetsa nnete.', 'XHO22'),
(60, 3, 15, 'Bohlale bona ha bo tswe hodimo, empa ke ba lefatshe, ba nama, ba bodiabolose.', 'XHO22'),
(60, 3, 16, 'Hobane moo mona le dikomang di leng teng, ho na le moferefere le mesebetsi yohle e mebe.', 'XHO22'),
(60, 3, 17, 'Empa bohlale bo tswang hodimo, pele bo hlwekile, hape bo na le kgotso, bo bonolo, bo bonolo, bo tletse mohau le ditholwana tse molemo, ha bo na leeme, ha bo na boikaketsi.', 'XHO22'),
(60, 3, 18, 'Mme ditholwana tsa ho loka di jalwa kgotsong ho ba etsang kgotso.', 'XHO22'),
(60, 4, 1, 'Zivela phi na iimfazwe nokulwa phakathi kwenu? Aziphumi na apha ezinkanukweni zenu, eziwaphumela umkhosi amalungu enu?', 'XHO22'),
(60, 4, 2, 'Niyakhanuka, ningabi nakuzuza nto noko; niyabulala, nizonde, ningabi nakuzuza nto noko; niyalwa, nenze imfazwe, ningabi nakuzuza nto ke, ngenxa yokuba ningaceli.', 'XHO22'),
(60, 4, 3, 'Niyacela, ningamkeli noko, ngenxa enokuba nicela kakubi, ukuze oko nikuchithele iziyolo zenu.', 'XHO22'),
(60, 4, 4, 'Bakrexezindini, bakrexezikazindini, anazi na ukuba ubuhlobo balo ihlabathi bubutshaba kuye uThixo? Othe ngoko angaba sisihlobo salo ihlabathi, utshaba lukaThixo.', 'XHO22'),
(60, 4, 5, 'Ngaba isibhalo sifumana sithetha ukuthi: “Umoya ohleli ngaphakathi kwethu ukhweletisa?', 'XHO22'),
(60, 4, 6, 'Ke ubabala kakhulu ngakumbi. Kungoko athi, uThixo uyabachasa abanekratshi, abababale abazithobileyo.', 'XHO22'),
(60, 4, 7, 'Mthobeleni ngoko uThixo. Mchaseni ke uMtyholi, wonibaleka.', 'XHO22'),
(60, 4, 8, 'Sondelani kuye uThixo, wosondela kuni. Hlambululani izandla, boni; nenze nyulu iintliziyo zenu, nina bamphefumlo umbaxa.', 'XHO22'),
(60, 4, 9, 'Yibani ziindwayi, nikhedame, nilile; ukuhleka kwenu makujike kube kukukhedama, novuyo lwenu lube kukuthi dakumba.', 'XHO22'),
(60, 4, 10, 'Zithobeni emehlweni eNkosi, yoniphakamisa.', 'XHO22'),
(60, 4, 11, 'Musani ukuthetha okubi omnye ngomnye, bazalwana. Lowo uthetha kakubi ngomzalwana wakhe, amgwebe umzalwana wakhe, uthetha okubi ngomthetho, egweba umthetho; kodwa ukuba uyawugweba umthetho, akumenzi wamthetho, ungumgwebi.', 'XHO22'),
(60, 4, 12, 'Mnye uMmisi-mthetho, lowo unako ukusindisa nokutshabalalisa; ungubani na wena, umgwebayo omnye?', 'XHO22'),
(60, 4, 13, 'Wenani kaloku, nina nitshoyo ukuthi, Namhla, nokuba kungomso, siya kuya kulaa mzi, senze khona umnyaka ube mnye, sirhwebe, sizuze;', 'XHO22'),
(60, 4, 14, 'Kaloku ningayazi nje into eya kwenzeka ngomso. Kuba buyintoni na ubomi bakho? Kungumphunga obonakala ixesha elifutshane, uze ke uthi shwaka.', 'XHO22'),
(60, 4, 15, 'Kungoko ningathi, Ukuba iNkosi ithe yathanda, saphila, siya kwenza le, nalaa nto.', 'XHO22'),
(60, 4, 16, 'Ngoku ke niqhayisa ngokuqhankqalaza kwenu; konke ukuqhayisa okunjalo kubi.', 'XHO22'),
(60, 4, 17, 'Ngoko ke lowo ukwaziyo ukwenza okulungileyo angakwenzi, kusisono kuye.', 'XHO22'),
(60, 5, 1, 'Wenani kaloku, zityebi, lilani nenze isijwili ngeenkxwaleko zenu ezizayo.', 'XHO22'),
(60, 5, 2, 'Ubutyebi benu bubolile, neengubo zenu zidliwe ngamanundu.', 'XHO22'),
(60, 5, 3, 'Igolide nesilivere yenu idliwe ngumhlwa; umhlwa wazo uya kuba lingqina ngakuni, uyidle inyama yenu njengomlilo. Niziqwebele ubutyebi ngemihla yokugqibela.', 'XHO22'),
(60, 5, 4, 'Yabonani, umvuzo wabasebenzi, abavune amasimi enu, lowo nibandezwe kuni ngobuqhetseba, uyakhala; kwaye ukubhomboloza kwabavuni bangene ezindlebeni zeNkosi yemikhosi.', 'XHO22'),
(60, 5, 5, 'Nadla ubuncwane emhlabeni, naxhamla iziyolo; nizondle iintliziyo zenu njengamhla kuxheliweyo.', 'XHO22'),
(60, 5, 6, 'Niligwebe nambulala olilungisa; kanjalo akanichasi.', 'XHO22'),
(60, 5, 7, 'Nyamezelani ngoko, bazalwana, ide ifike iNkosi. Yabonani, umlimi uyasilinda isiqhamo somhlaba esinexabiso elikhulu, anyamezele ngaso, ade amkele imvula yokuqala neyakwindla.', 'XHO22'),
(60, 5, 8, 'Nyamezelani nani; zimiseni iintliziyo zenu, kuba ukufika kweNkosi kusondele.', 'XHO22'),
(60, 5, 9, 'Musani ukukrokrelana omnye nomnye, bazalwana, ukuze ningagwetywa; yabonani, uMgwebi umi phambi komnyango.', 'XHO22'),
(60, 5, 10, 'Bathabatheni, bazalwana bam, abaprofeti ababethetha egameni leNkosi, babe ngumzekelo wokuva ubunzima, nowokunyamezela.', 'XHO22'),
(60, 5, 11, 'Yabonani, sithi, banoyolo abo banyamezelayo. Nivile ngonyamezelo lukaYobhi, nayibona nendlela iNkosi eya kwenza ngayo, ngokuba iNkosi inemfesane kakhulu, inenceba.', 'XHO22'),
(60, 5, 12, 'Ngaphezu kweento zonke, bazalwana bam, musani ukufunga, nokuba lizulu, nokuba ngumhlaba, nokuba sesinye isifungo esiyini; nohayi wenu, hayi; ukuze ningaweli ekugwetyweni.', 'XHO22'),
(60, 5, 13, 'Kukho mntu na phakathi kwenu uva ububi? makathandaze. Ngaba kukho ukonwaba? makavume iindumiso.', 'XHO22'),
(60, 5, 14, 'Kukho mntu na ufayo phakathi kwenu? makabize amadoda amakhulu ebandla; bathandaze phezu kwakhe, bamthambise ngeoli, egameni leNkosi.', 'XHO22'),
(60, 5, 15, 'Umthandazo wokholo uya kumsindisa lowo ugulayo, iNkosi imvuse; nokuba ubethe wenza izono, wozixolelwa.', 'XHO22'),
(60, 5, 16, 'Vumani iziphoso omnye komnye, nithandazelane, ukuze niphiliswe. Umthandazo welungisa unamandla kakhulu.', 'XHO22'),
(60, 5, 17, 'uEliya wayengumntu oneemvakalelo ezibuhlungu njengathi, wathandaza enyamekile ukuba ingani; akwabakho mvula emhlabeni iminyaka emithathu eneenyanga ezintandathu.', 'XHO22'),
(60, 5, 18, 'Waphinda wathandaza, laza izulu lanika imvula, nomhlaba wavelisa iziqhamo zawo.', 'XHO22'),
(60, 5, 19, 'Bazalwana, ukuba ubani kuni uthe walahlekana enyanisweni, wathi ubani wamguqula;', 'XHO22'),
(60, 5, 20, 'Mazi ukuba lowo wamguqulayo umoni ekulahlekeni kwakhe uya kuwusindisa umphefumlo ekufeni, wagubungela inkitha yezono.', 'XHO22'),
(61, 1, 1, 'uPetros, umpostile kaYesu Kristu, ubhalela abasemzini abachithachitheke kuyo yonke iPonto, neGalati, neKapadoki, neAsiya, neBhithiniya;', 'XHO22'),
(61, 1, 2, 'nabanyuliweyo ngokokwazi kwenxa engaphambili kukaThixo uYise, ebungcweleni boMoya, kuse ekululameleni nasekufefweni kwegazi likaYesu Kristu: uthi, Makwandiswe kuni ubabalo noxolo.', 'XHO22'),
(61, 1, 3, 'Makabongwe uThixo, uYise weNkosi yethu uYesu Kristu, othe ngokobuninzi benceba yakhe, wasizala ngokutsha, ukuba sibe nethemba eliphilileyo ngokuvuka kukaYesu Kristu kwabafileyo;', 'XHO22'),
(61, 1, 4, 'ukuba sibe nelifa elingenakonakala, elingenakudyobheka, elingenakubuna, eligcinelwe nina emazulwini;', 'XHO22'),
(61, 1, 5, 'nina bathe ngamandla kaThixo ngokholo bagcinelwa usindiso olulungiselwe ukutyhilwa ngexesha lokugqibela.', 'XHO22'),
(61, 1, 6, 'Enigcoba ngalo, nokuba nithe naba buhlungu umzuzwana ngoku, ukuba nimelwe kuko, phakathi kwezilingo ngezilingo;', 'XHO22'),
(61, 1, 7, 'ukuze ukucikideka kokholo lwenu kufunyanwe kunqabile kakhulu, ngaphezu kwegolide le itshabalalayo, noko icikidwa ngomlilo; kuse kuyo indumiso nembeko nozuko ekutyhilekeni kukaYesu Kristu.', 'XHO22'),
(61, 1, 8, 'Enithi ningambonanga, niyamthanda; enithi ke nikholwa kuye, noko ngoku ningamboniyo, nigcobe ngovuyo olungathethekiyo, oluzukisiweyo;', 'XHO22'),
(61, 1, 9, 'nisamkela isiphelo sokholo lwenu, usindiso ke lwemiphefumlo yenu.', 'XHO22'),
(61, 1, 10, 'Usindiso olo babuphengulula, baphengulula ngalo abaprofeti, ababeprofeta ngobabalo oluya kunifikela;', 'XHO22'),
(61, 1, 11, 'bephengulula ukuba wayesalatha xesha lini na, linjani na, uMoya kaKristu, awayezingqinela kubo ngenxa engaphambili iintlungu ezamiselwa uKristu, nozuko oluya kuzilandela.', 'XHO22'),
(61, 1, 12, 'Batyhilelwa ukuba ezo ndaba zazishunyayelwa ngabo bazivakalisayo kuni ngoku iindaba ezimnandi ngoMoya oyiNgcwele owavela ezulwini; izinto ezo ezizinqwenelayo izithunywa zezulu ukuqondela kuzo.', 'XHO22'),
(61, 1, 13, 'Ngoko, bhinqani izinqe zengqondo yenu, ninobungcathu, niluthembe ngokugqibeleleyo ubabalo oluziswa kuni ekutyhilekeni kukaYesu Kristu;', 'XHO22'),
(61, 1, 14, 'ngokwabantwana bolulamo, ningazimimeli ngokweenkanuko zangaphambili ekungazini kwenu;', 'XHO22'),
(61, 1, 15, 'nithi ke, ngokokwalowo ungcwele wanibizayo, nibe ngcwele nani ngokwenu kuyo yonke ihambo.', 'XHO22'),
(61, 1, 16, 'Ngenxa yokuba kubhaliwe kwathiwa, Yibani ngcwele; ngokuba ndingcwele mna.', 'XHO22'),
(61, 1, 17, 'Nani nimnqula nithi uYihlo, lowo ugwebayo ngokokwenza komntu wonke, engakhethi buso, lihlaleni ixesha lokuphambukela kwenu apha ninoloyiko;', 'XHO22'),
(61, 1, 18, 'nisazi nje ukuba anihlawulelwanga nikhululwe ngazinto zinakonakala, oozisilivere noozigolide, kuyo ihambo yenu engento, enayinikelwayo ngooyihlo.', 'XHO22'),
(61, 1, 19, 'Kungegazi elinqabileyo likaKristu, njengelemvana engenasiphako, engenasiphako;', 'XHO22'),
(61, 1, 20, 'owathi okunene wanyulwa ngenxa engaphambili, lingekasekwa ihlabathi, wabonakalaliswa kuni ekupheleni kwala maxesha;', 'XHO22'),
(61, 1, 21, 'enithi ngaye nakholwa kuye uThixo, owamvusayo kwabafileyo, wamnika uzuko; ukuze ukholo lwakho nethemba lakho libe kuThixo.', 'XHO22'),
(61, 1, 22, 'Niyenze nyulu nje imiphefumlo yenu ngokuyilulamela inyaniso ngoMoya, kuse ekuthandeni abazalwana okungenaluhanahaniso, thandanani ke ngokwasentliziyweni ehlambulukileyo;', 'XHO22'),
(61, 1, 23, 'Nizelwe ngokutsha, kungengokwasembewini enokonakala, kungokwasembewini engenakonakala, ngelizwi likaThixo eliphilileyo, elihleliyo ngonaphakade.', 'XHO22'),
(61, 1, 24, 'Kuba Yonke inyama injengengca, Nabo bonke ubuqaqawuli bomntu bunjengentyantyambo yengca. Ingca iyoma, ivuthuluke nentyantyambo yayo;', 'XHO22'),
(61, 1, 25, 'Ke lona ilizwi leNkosi lihleli ngonaphakade. Elo ke leli lizwi lashunyayelwayo kuni ngeendaba ezilungileyo.', 'XHO22'),
(61, 2, 1, 'Kungoko ndithi, nibulahle ke bonke ububi, nalo lonke ulunya, nokuhanahanisa, nomona, nazo zonke izinyeliso;', 'XHO22'),
(61, 2, 2, 'Njengeentsana ezisandul’ ukuzalwa, langazelelani ubisi lwelizwi, ukuze nikhule ngalo;', 'XHO22'),
(61, 2, 3, 'ukuba okunene nikuvile, ukuba iNkosi inobubele.', 'XHO22'),
(61, 2, 4, 'Lowo usiza kuye njengelitye eliphilileyo, elicekiswe okunene ngabantu, kodwa linyuliwe nguThixo, linqabileyo;', 'XHO22'),
(61, 2, 5, 'Nani ngokwenu, njengamatye aphilileyo, yakhekani nibe yindlu eyiyeyomoya, isibingeleli esingcwele sokunyusa amadini angoMoya, awakholekileyo kakuhle kuye uThixo ngoYesu Kristu.', 'XHO22'),
(61, 2, 6, 'Kungoko ikhoyo nokubakho esibhalweni indawo ethi, Yabona, ndibeka eZiyon ilitye lembombo, elinyuliweyo, elinqabileyo; Lowo ukholwayo kuye akayi kukha adaniswe.', 'XHO22'),
(61, 2, 7, 'Kuni ngoko bakholwayo, liyimbeko; ke kwabangevayo, ilitye abalicekisayo abakhi, Lelona laba yintloko yembombo;', 'XHO22'),
(61, 2, 8, 'Ilitye ke lokuwisa, iliwa lesikhubekiso, kwabo bakhubekayo, bengaliva ilizwi;', 'XHO22'),
(61, 2, 9, 'Ke nina nisisizukulwana esinyuliweyo, isibingeleli esibukumkani, uhlanga olungcwele, abantu abayinqobo; ukuze nivakalise isidima salowo wanibizayo ukuba niphume ebumnyameni, ningene ekukhanyeni kwakhe okungummangaliso;', 'XHO22'),
(61, 2, 10, 'enanifudula ningebantu, ke ngoku ningabantu bakaThixo; enaningenzelwanga nceba, ke ngoku nenzelwe inceba.', 'XHO22'),
(61, 2, 11, 'Zintanda, ndiyanivuselela njengabaphambukeli nabasemzini, ukuba nikhumke ezinkanukweni ezizezenyama, zona ziwuphumele umkhosi umphefumlo.', 'XHO22'),
(61, 2, 12, 'Ihambo yenu mayibe ntle phakathi kweentlanga; ukuze kulo nto bathetha okubi ngani, ngathi ningabenzi bokubi, bathi ngokubona imisebenzi yenu emihle, bamzukise uThixo ngomhla wokuvelelwa.', 'XHO22'),
(61, 2, 13, 'Wuthobeleni ngoko wonke ummiso ongowomntu, ngenxa yeNkosi; nokuba ngukumkani, njengowongamileyo;', 'XHO22'),
(61, 2, 14, 'nokuba ngabalawuli, njengabathunyiweyo nguye, ukuze baphindezele okunene kubenzi bokubi, badumise abenzi bokulungileyo.', 'XHO22'),
(61, 2, 15, 'Kuba ukuthanda kukaThixo kukuba nithi, ngokwenza okulungileyo, nibethe ithi cwaka intswela-kwazi yabantu abaziintsweli-kuqonda;', 'XHO22'),
(61, 2, 16, 'nithi ningabakhululekileyo nje, ningabi ngabayenza inkululeko ibe sisigqubuthelo sobubi; nibe ngabakhonzi bakaThixo.', 'XHO22'),
(61, 2, 17, 'Bekani bonke abantu. Thandani abazalwana. Yoyika uThixo. Beka ukumkani.', 'XHO22'),
(61, 2, 18, 'Nina bakhonzi, bathobeleni ninako konke ukoyika iinkosi zenu; ingabi kwabalungileyo nabathantamisayo bodwa, ibe kwanabagwenxa.', 'XHO22'),
(61, 2, 19, 'Kuba oku kuyabuleleka, ukuba umntu uthwala iintsizi, ngenxa yokumazi uThixo, eve ubunzima esoniwa.', 'XHO22'),
(61, 2, 20, 'Kuba kuya kuba ludumo luni na, ukuba ninyamezele nakuntlithwa nisona? Ke ukuba nithi, nisenza okulungileyo, nive ubunzima, nize ninyamezele, oko kuyathandeka kuThixo.', 'XHO22'),
(61, 2, 21, 'Nabizelwa oko; ngokuba noKristu weva ubunzima ngenxa yethu, esishiya nomzekelo, ukuze silandele emkhondweni wakhe:', 'XHO22'),
(61, 2, 22, 'ongenzanga sono, ekungafunyanwanga nkohliso emlonyeni wakhe;', 'XHO22'),
(61, 2, 23, 'owathi, etshabhiswa, akaphindi atshabhise; Uthe esiva ubunzima, akasongela; ke ubeyekele kulowo ugwebayo ngobulungisa;', 'XHO22'),
(61, 2, 24, 'Owathi ngokwakhe, izono zethu wazithwala ngomzimba wakhe emthini, ukuze sithi, sifile ngakwizono, siphilele ebulungiseni;', 'XHO22'),
(61, 2, 25, 'Kuba beninjengezimvu zilahlekile; kodwa ngoku nibuyele kuye uMalusi noMveleli wemiphefumlo yenu.', 'XHO22'),
(61, 3, 1, 'Ngokunjalo, nina bafazi, wathobeleni awenu amadoda; ukuze kuthi, nokuba kukho kuwo angalivayo ilizwi, athi ngehambo yabafazi, lingekho ilizwi, azuzeke;', 'XHO22'),
(61, 3, 2, 'ebona ihambo yenu enyulu, enoloyiko.', 'XHO22'),
(61, 3, 3, 'Ukuhomba kwenu makungabi kokwangaphandle, kokulukwa kweenwele, nokunxitywa kwegolide, nokwambathwa kweengubo;', 'XHO22'),
(61, 3, 4, 'Makube kokomntu ofihlakeleyo wentliziyo, ovethe ukungonakali, umoya onobulali, ozolileyo, oxabiso likhulu emehlweni kaThixo.', 'XHO22'),
(61, 3, 5, 'Kuba besenjenjalo ukuhomba abafazi abangcwele, ababekholose ngoThixo, bewathobele awabo amadoda;', 'XHO22'),
(61, 3, 6, 'Kwanjengokuba uSara wamlulamelayo uAbraham, embiza ngokuthi yinkosi; eniziintombi eniziintombi zenu, ukuba nisenza okulungileyo, ningoyiki nakuqhiphuka umbilini.', 'XHO22'),
(61, 3, 7, 'Ngokunjalo, nina madoda, hlalani nabo ngokokwazi, nimbeka umfazi, njengesona sitya siethe-ethe, njengeendlalifa kunye nani zobabalo lobomi, ukuze imithandazo yenu ingathinteleki.', 'XHO22'),
(61, 3, 8, 'Elokugqiba, cingani nto-nye nonke, velanani, thandanani njengabazalwana, yibani nemfesane kakhulu, yibani nembeko;', 'XHO22'),
(61, 3, 9, 'Ningabuyekezi ububi ngobubi, nokutshabhisa ngokutshabhisa; nisazi nje ukuba nabizelwa oko, ukuze niyidle ilifa intsikelelo.', 'XHO22'),
(61, 3, 10, 'Kuba Lowo unga angathanda ubomi, Ayibone imihla elungileyo, Makaluphezise ulwimi lwakhe ebubini, Nomlomo wakhe ungathethi nkohliso.', 'XHO22'),
(61, 3, 11, 'Makaphambuke ebubini, enze okulungileyo; makafune uxolo, aluphuthume.', 'XHO22'),
(61, 3, 12, 'Ngokuba amehlo eNkosi aphezu kwamalungisa, neendlebe zayo zisingisele esikhungweni sawo; ke ubuso beNkosi buphezu kwabenza okubi.', 'XHO22'),
(61, 3, 13, 'Ngubani na yena owonona, ukuba nithe naba ngabalandeli bokulungileyo?', 'XHO22'),
(61, 3, 14, 'Nokuba nive ubunzima ngenxa yobulungisa, ninoyolo;', 'XHO22'),
(61, 3, 15, 'iNkosi ke iNkosi yingcwaliseni ezintliziyweni zenu, nihlale ke nikulungele ukuziphendulela kumntu wonke obuzayo kuni ilizwi, ngalo ithemba elingaphakathi kwenu, ninobulali noloyiko;', 'XHO22'),
(61, 3, 16, 'ukuba nesazela esilungileyo; ukuze kuloo nto banithethela kakubi, ngathi ningabenzi bokubi, badane abo bayigxekayo ihambo yenu elungileyo kuKristu.', 'XHO22'),
(61, 3, 17, 'Ukuba ke kukuthanda kukaThixo ukuba nive ubunzima, okona kunilungeleyo kukubuva nisenza okulungileyo, kunokubuva nisenza okubi.', 'XHO22'),
(61, 3, 18, 'Kuba noKristu wabuva kwakanye ubunzima ngenxa yezono, ilungisa lisiva ubunzima ngenxa yabangemalungisa, ukuze asisondeze kuye uThixo, ebulewe esenyameni, kodwa edliswe ubomi ekuwo umoya.', 'XHO22'),
(61, 3, 19, 'Wahamba ngayo ke, washumayela koomoya abasentolongweni;', 'XHO22'),
(61, 3, 20, 'ababefudula bengeva, xenikweni ukuzeka kade umsindo kukaThixo kwakulindile ngemihla kaNowa, xeshikweni kwakwakhiwa umkhombe, ekwathi kuwo abantu abambalwa, oko kukuthi, imiphefumlo esibhozo, yasindiswa ngamanzi.', 'XHO22'),
(61, 3, 21, 'Lowo ufana nqwa nobhaptizo olusisindisayo ngoku, ingekuko ukulahla ubunyhukunyhuku benyama;', 'XHO22'),
(61, 3, 22, 'osekunene kukaThixo, eye emazulwini; kuthotyelwe phantsi kwakhe izithunywa zezulu, noomagunya, namandla.', 'XHO22'),
(61, 4, 1, 'Ekubeni ngoko uKristu eve ubunzima ngenxa yethu esenyameni, xhobani nani kwaloo ngcingane; ngokuba lowo uve ubunzima esenyameni, ukhunyulwe ekoneni;', 'XHO22'),
(61, 4, 2, 'ukuze ixesha eliseleyo enyameni ningabi saliphilela ezinkanukweni zabantu, niliphilele ekuthandeni kukaThixo.', 'XHO22'),
(61, 4, 3, 'Kuba lanele ixesha elidluleyo ukuba sisebenze ukuthanda kweentlanga, esahamba nje eburheletyweni, ezinkanukweni, ekutshisweni yiwayini, ezindywaleni, ezindywaleni, nasekukhonzeni izithixo, okungekhona esikweni;', 'XHO22'),
(61, 4, 4, 'Ekubeni benithe nqa, ukuba anibaleki nabo kuloo myezo wonakeleyo, benyelisa;', 'XHO22'),
(61, 4, 5, 'abaya kuphendula kulowo ulungele ukugweba abaphilileyo nabafileyo.', 'XHO22'),
(61, 4, 6, 'Kuba ngenxa yoku zashunyayelwa iindaba ezilungileyo nakwabafileyo, ukuze bagwetywe ngokwabantu besenyameni, kodwa badle ubomi ngokukaThixo, bekuwo umoya.', 'XHO22'),
(61, 4, 7, 'Ke kaloku ukuphela kweento zonke kusondele. Yibani nobungcathu, niphaphamele ukuthandaza.', 'XHO22'),
(61, 4, 8, 'Ngaphezu kweento zonke ke yibani nothandano olunyamekileyo, ngokuba uthando luya kugubungela inkitha yezono.', 'XHO22'),
(61, 4, 9, 'Bukani iindwendwe ngaphandle kokukrokra.', 'XHO22'),
(61, 4, 10, 'Ngoko wonke umntu wasamkelayo isiphiwo eso, khonzanani ngaso, njengamagosa amahle obabalo lukaThixo.', 'XHO22'),
(61, 4, 11, 'Ukuba umntu uyathetha, makathethe ngokwezihlabo zikaThixo; ukuba umntu uyalungiselela, makalungiselele ngokwasekomeleleni akuphiwa nguThixo, ukuze ezintweni zonke azukiswe uThixo ngoYesu Kristu, elulolwakhe uzuko namandla, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(61, 4, 12, 'Zintanda, musani ukuwuthela nqa umlilo okhoyo phakathi kwenu;', 'XHO22'),
(61, 4, 13, 'Yithini, njengoko ningamadlelane ngeentlungu zikaKristu, nivuye; ukuze, nasekutyhilekeni kobuqaqawuli bakhe, nivuye nigcoba.', 'XHO22'),
(61, 4, 14, 'Ukuba niyangcikivwa ngenxa yegama likaKristu, ninoyolo; kuba uMoya wozuko nokaThixo uphumle phezu kwenu; ngokukokwabo uyanyeliswa, kodwa ngokukokwenu uyazukiswa.', 'XHO22'),
(61, 4, 15, 'Makungabikho ke bani kuni uva ubunzima ngokombulali, nangokwesela, nangokomenzi wobubi, nangokovelela izinto ezimbi;', 'XHO22'),
(61, 4, 16, 'Ukuba ke ubani uva ubunzima ngokuba engumKristu, makangadani; makamzukise ke uThixo ngenxa yoko.', 'XHO22'),
(61, 4, 17, 'Kaloku ixesha lokuba umgwebo uqale ngendlu kaThixo. Ukuba ke luqale kuthi, koba yintoni na ukuphela kwabo bangazithobeliyo iindaba ezimnandi zikaThixo?', 'XHO22'),
(61, 4, 18, 'Ke ukuba ilungisa lisindiswa ngenkankulu, wobonakala phi na umntu ongahloneli Thixo nomoni?', 'XHO22'),
(61, 4, 19, 'Ngoko ke abo babuvayo ubunzima ngokokuthanda kukaThixo, mabayiyaleze kuye imiphefumlo yabo, enguMdali othembekileyo ekwenzeni okulungileyo.', 'XHO22'),
(61, 5, 1, 'Amadoda amakhulu aphakathi kwenu ndiyawavuselela, mna ndikwayindoda enkulu, nengqina leentlungu zikaKristu, ndinesabelo ebuqaqawulini obuya kutyhileka;', 'XHO22'),
(61, 5, 2, 'Waluseni umhlambi kaThixo ophakathi kwenu, niwuvelela, kungengakunyanzelwa, makube ngokokuzithandela; kungengenxa yenzuzo embi, makube ngokwentumekelelo;', 'XHO22'),
(61, 5, 3, 'kungabi njengabazigagamelayo izahlulo zawo, yibani yimizekelo kumhlambi.', 'XHO22'),
(61, 5, 4, 'Nothi, akubonakala uMalusi oyintloko, nisifumane isithsaba salo uzuko esingabuniyo.', 'XHO22'),
(61, 5, 5, 'Ngokunjalo, nina batsha, wathobeleni amadoda amakhulu. Yambathani ukuthobeka kwentliziyo nonke; kuba uThixo uyabachasa abanekratshi, abababale abazithobileyo.', 'XHO22'),
(61, 5, 6, 'Zithobeni ngoko phantsi kwesandla esinamandla sikaThixo, ukuze aniphakamise ngexesha elifanelekileyo,', 'XHO22'),
(61, 5, 7, 'liphoseni kuye lonke ixhala lenu; ngokuba unikhathalele.', 'XHO22'),
(61, 5, 8, 'Yibani nobungcathu, phaphani; ngokuba ummangaleli wenu, uMtyholi, uhamba njengengonyama egqumayo, efuna ubani angamginyayo;', 'XHO22'),
(61, 5, 9, 'mchaseni lowo, nizimasekile elukholweni, nisazi ukuba kwaezo ntlungu ziyenzeka kubo abazalwana benu abasehlabathini.', 'XHO22'),
(61, 5, 10, 'Ke kaloku uThixo walo lonke ubabalo, owasibizelayo eluzukweni lwakhe olungunaphakade, sikuKristu Yesu, wanga angathi, nakuba nive ubunzima umzuzwana, anilungise nigqibelele, anizimase, anomeleze, anisekele.', 'XHO22'),
(61, 5, 11, 'Kuye makubekho uzuko namandla, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(61, 5, 12, 'NgoSilvano, umzalwana othembekileyo, njengoko ndicinga ngako, ndibhale kuni ngokufutshane, ndivuselela, ndingqina ukuba olu lubabalo oluyinene lukaThixo, nimiyo kulo.', 'XHO22'),
(61, 5, 13, 'Liyanibulisa ibandla eliseBhabheli, elinyuliweyo kunye nani; ngokunjalo noMarcus unyana wam.', 'XHO22'),
(61, 5, 14, 'Bulisanani ngolwango lothando. Uxolo malube nani nonke enikuKristu Yesu. Amen.', 'XHO22'),
(62, 1, 1, 'uSimon Petros, umkhonzi nompostile kaYesu Kristu, ubhalela abo baluzuzileyo ukholo olunqabileyo ngokufana nathi, ngobulungisa bukaThixo, uMsindisi wethu uYesu Kristu:', 'XHO22'),
(62, 1, 2, 'Makwandiswe kuni ubabalo noxolo, ngokumazi uThixo, noYesu iNkosi yethu;', 'XHO22'),
(62, 1, 3, 'Njengokuba amandla akhe obuThixo asiphile zonke izinto ezisingise ebomini nasekuhloneleni uThixo, ngako ukumazi lowo wasibizayo ngozuko nesidima;', 'XHO22'),
(62, 1, 4, 'Esithe ngazo saphiwa amadinga amakhulu kakhulu, anqabileyo, ukuze ngawo nibe ngamadlelane ngemvelo yobuThixo, nisinde ekonakaleni okukhoyo ehlabathini ngenxa yenkanuko.', 'XHO22'),
(62, 1, 5, 'Ngoko ke, ngenxa yoko, nakuba niqokele ukukhuthala konke, nongezelele isidima elukholweni lwenu; nasesidimani ukwazi;', 'XHO22'),
(62, 1, 6, 'ekwazini ukuzeyisa; nasekuzeyiseni umonde; nasekunyamezeleni ukuhlonela uThixo;', 'XHO22'),
(62, 1, 7, 'nasekuhloneleni uThixo, ukuthanda abazalwana; nakuthando lobuzalwana.', 'XHO22'),
(62, 1, 8, 'Kuba xa zikhoyo ezo zinto kuni, zisanda, zinenza ukuba ninganqeni, ningabi ngabaswele isiqhamo ekuyazini iNkosi yethu uYesu Kristu.', 'XHO22'),
(62, 1, 9, 'Lowo zingekhoyo ezo zinto, uyimfama; ubona ke kusekude, ekulibele nokuhlanjululwa kwezono zakhe zakudala.', 'XHO22'),
(62, 1, 10, 'Endithi ngoko, bazalwana, makube kukhona nikhuthalele ukulwenza luqine ubizo nonyulo lwenu;', 'XHO22'),
(62, 1, 11, 'Kuba ngokunjalo nophiwa ngokobutyebi ukungena ebukumkanini obungunaphakade beNkosi yethu, uMsindisi uYesu Kristu.', 'XHO22'),
(62, 1, 12, 'Kungoko ndingakuyekiyo ukuhlala ndinikhumbuza ngezo zinto, noko nizaziyo, nizimasekileyo enyanisweni leyo ikhoyo kuni.', 'XHO22'),
(62, 1, 13, 'Ndiba ke kububulungisa, ukuba ngalo gama ndikulo mnquba, ndinivuse ngokunikhumbuza;', 'XHO22'),
(62, 1, 14, 'ndisazi nje ukuba kukufuphi ukususwa kwalo mnquba wam, njengoko nayo iNkosi yethu uYesu Kristu yandazisayo.', 'XHO22'),
(62, 1, 15, 'Kananjalo ndiya kukhuthalela ke ukuba nibe nako, amaxa onke emva kokumka kwam, ukuzikhunjuzwa ezo zinto.', 'XHO22'),
(62, 1, 16, 'Kuba sithe, singalandelanga zintsomi zabuqhinga, sanazisa amandla nokufika kweNkosi yethu uYesu Kristu; sithe saba ngababoneli bobungangamsha bakhe.', 'XHO22'),
(62, 1, 17, 'Kuba wamkela kuThixo uYise imbeko nozuko, ekuzisweni kuye, bubuqaqawuli obubalaseleyo, izwi elinjengeli: Lo nguNyana wam oyintanda, endikholisiweyo mna nguye.', 'XHO22'),
(62, 1, 18, 'Elo lizwi salivayo liphuma ezulwini, sikunye naye entabeni engcwele.', 'XHO22'),
(62, 1, 19, 'Kaloku sinalo nelona lizwi liqinisekileyo lesiprofeto, enityaphayo ukuyigcina loo nto niyilumkela, ngathi sisikhanyiso esikhanyisa endaweni emnyama, kude kuse, liphume ikhwezi ezintliziyweni zenu;', 'XHO22'),
(62, 1, 20, 'nikwazi oku kuqala, ukuba akukho siprofeto seSibhalo siphuma nakuyiphi na inkcazelo yobuqu.', 'XHO22'),
(62, 1, 21, 'Kaloku akukhanga kubekho siprofeto ngokuthanda komntu, kodwa abantu abangcwele bakaThixo bathetha beqhutywa nguMoya oyiNgcwele.', 'XHO22'),
(62, 2, 1, 'Ke kaloku, kwakukho nabaprofeti ababuxoki phakathi kwabantu; njengokuba naphakathi kwenu kuya kubakho abafundisi ababuxoki, bona baya kungenisa bucala amahlelo entshabalalo, bemkhanyela uMnini-nto-zonke owabathengayo, bezizisela intshabalalo emsinya.', 'XHO22'),
(62, 2, 2, 'Baninzi abaya kulandela intshabalalo yabo; eya kunyeliswa ngenxa yabo indlela yenyaniso.', 'XHO22'),
(62, 2, 3, 'Bathi benokubawa, banibonelele ngamazwi alalanisayo; abakugwetywa kungaphumliyo kwakudala, nayo intshabalalo yabo ayozeli.', 'XHO22'),
(62, 2, 4, 'Kuba, xa uThixo angazisindisanga izithunywa zezulu ezonayo, wesuka waziphosa kwelabafileyo, wabanikela imixokelelwane yobumnyama, ukuba zigcinelwe umgwebo;', 'XHO22'),
(62, 2, 5, 'Engaliyekanga ihlabathi lakudala, wesuka walondoloza uNowa, umshumayeli wobulungisa, engowesibhozo, elihlisela unogumbe ihlabathi labangahloneli Thixo;', 'XHO22'),
(62, 2, 6, 'Wayitshisa iSodom neGomora, wayitshisa yaluthuthu, wayigweba ukuba ibhukuqeke, wayenza umzekelo wabaza kuba ngabangahloneli Thixo;', 'XHO22'),
(62, 2, 7, 'Wamhlangula uLote olilungisa, owayephithikezwe yihambo engcolileyo yabangendawo;', 'XHO22'),
(62, 2, 8, '(Kuba loo mntu ulilungisa, ehleli phakathi kwabo, ngokubona nokuva, wayewuthuthumbisa imihla ngemihla umphefumlo walo olungisayo, ngemisebenzi yabo echasene nomthetho;)', 'XHO22'),
(62, 2, 9, 'iyakwazi iNkosi ukubahlangula ekuhendweni abahlonela uThixo;', 'XHO22'),
(62, 2, 10, 'ngokukodwa ke abo balandela inyama ngokukhanuka ukuzidyobha, abadela ubukhosi, bakhukhumele, abangoyikiyo ukunyelisa izikhakhamela;', 'XHO22'),
(62, 2, 11, 'Kaloku izithunywa zezulu, zingaphezulu koko ngamandla nokomelela, azibamangaleli ngenxa yeNkosi.', 'XHO22'),
(62, 2, 12, 'Ke aba, njengezinto eziphilileyo, ezingenangqondo, ezemvelo, ezizalelwe ukubanjwa nokutshatyalaliswa, banyelisa izinto abangazaziyo; baya konakala kanye ekonakaleni kwabo;', 'XHO22'),
(62, 2, 13, 'Baya kwamkela umvuzo wobutshijolo, njengokuba kwabanjalo ukubalela emini ukuzonwabisa; bangamabala neziphako, bexhamla ubuncwane ngokulukuhla kwabo, bezingxala ukudla kwabo kunye nani;', 'XHO22'),
(62, 2, 14, 'benamehlo azele lukrexezo, angenakuphunyuzwa esonweni; balukuhla imiphefumlo engazinzanga, intliziyo eqheliswe ukubawa; abantwana abaqalekisiweyo:', 'XHO22'),
(62, 2, 15, 'Beyishiyile indlela ethe tye, balahleka, belandela indlela kaBhileham unyana kaBhohore, owathanda umvuzo wokungalungisi;', 'XHO22'),
(62, 2, 16, 'Wohlwaywa ke ngokugqitha kwakhe umthetho; lathi iesile, lingenakuthetha nje, lathetha ngezwi lomntu, lamalela umprofeti lowo, ukuba angabi nxamnye nengqondo.', 'XHO22'),
(62, 2, 17, 'Aba ngamaqula angenamanzi, bangamafu aqhutywa ngumoya ovuthuzayo; abagcinelwe inkungu yobumnyama ngonaphakade.', 'XHO22'),
(62, 2, 18, 'Kuba, bevakalisa nje iindumbisa zamazwi angento yanto, babambisa ngeenkanuko zenyama, ngoburheletyo, abo babesindile, kanye kwabo bahambayo ekulahlekisweni.', 'XHO22'),
(62, 2, 19, 'Bebathembisa nje inkululeko, bona ngokwabo bangabakhonzi bokonakala;', 'XHO22'),
(62, 2, 20, 'Kuba xa bathe, besindile kwiidyokhwe zehlabathi ngokuyazi iNkosi, uMsindisi uYesu Kristu, babuya ke bazibhijele zizo, boyise, isiphelo sabo sibi ngaphezu kwesiqalo.', 'XHO22'),
(62, 2, 21, 'Kuba ngekubalungele kanye, ukuba bebengayazanga indlela yobulungisa, kunokuba bathi beyazile, babuye umva, baphume kuwo umthetho ongcwele abawuwiselwayo.', 'XHO22'),
(62, 2, 22, 'Kodwa bahlelwa ngumzekeliso oyinyani othi, Inja ibuyele emhlanzweni wayo; nemazi yehagu ehlanjiweyo ekuzityibilikeni eludakeni.', 'XHO22'),
(62, 3, 1, 'Ngoku ndinibhalela, zintanda, le ncwadi iyeyesibini; endithi kuzo zombini ndiyivuselele ingqondo yenu engcwengekileyo ngokunikhumbuza;', 'XHO22'),
(62, 3, 2, 'ukuze niwakhumbule amazwi abethethiwe ngenxa engaphambili ngabaprofeti abangcwele, nawo umthetho wabapostile bethu beNkosi uMsindisi;', 'XHO22'),
(62, 3, 3, 'nikwazi oku kuqala, ukuba kuya kufika ngemihla yokugqibela abagxeki, behamba ngokwezabo iinkanuko;', 'XHO22'),
(62, 3, 4, 'besithi, Liphi na idinga lokufika kwakhe? Kuba kuseloko oobawo balalayo ukufa, zonke izinto zahlala zihleli zinjengokwasekuqalekeni kwendalo.', 'XHO22'),
(62, 3, 5, 'Kuba oku kufihlakele kubo ngokuthanda kwabo, ukuba izulu laye likho kwakudala, nomhlaba uvele emanzini, umi emanzini, ngalo ilizwi likaThixo;', 'XHO22'),
(62, 3, 6, 'elathi ihlabathi langoko lantywiliselwa ngamanzi, latshabalala.', 'XHO22'),
(62, 3, 7, 'Ke lona izulu langoku, nawo umhlaba lo, kwangelo lizwi ezo zinto ziqwetyelwe, zigcinelwe, umlilo, kuse kwimini yomgwebo nentshabalalo yabantu abangahloneli Thixo.', 'XHO22'),
(62, 3, 8, 'Le nto ke inye mayingafihlakali kuni, zintanda, ukuba phambi kweNkosi imini enye injengeminyaka eliwaka, neminyaka eliwaka injengemini enye.', 'XHO22'),
(62, 3, 9, 'iNkosi ayilibali dinga, njengoko abathile baba iyalibala; uzeka kade umsindo wethu ngenxa yethu, engathandi ukuba kutshabalale nabani na kodwa ukuze bonke bafikelele enguqukweni.', 'XHO22'),
(62, 3, 10, 'Imini yeNkosi iya kufika njengesela ebusuku; eliya kuthi ngalo izulu lidlule ingxokozelo, zithi neziqalelo zitshe zinyibilike, nomhlaba nayo imisebenzi ekuwo iya kutsha itshiswe.', 'XHO22'),
(62, 3, 11, 'Ekubeni ngoko zonke ezi zinto ziya kuchithakala, nimele ukuba ngabanjani na ngeehambo ezingcwele nokuhlonela uThixo;', 'XHO22'),
(62, 3, 12, 'Niyilindele, nikungxamile, ukufika kwayo imini kaThixo, eliya kuthi ngayo izulu litshe lichithakale, zithi neziqalelo zitshe zinyibilike.', 'XHO22'),
(62, 3, 13, 'Thina ke ngokwedinga lakhe silindele amazulu amatsha nomhlaba omtsha, apho kumi ubulungisa.', 'XHO22'),
(62, 3, 14, 'Kungoko, zintanda, nilindele ezo zinto nje, khuthalelani ukuba nifunyanwe kuye niseluxolweni, ningenabala, ningenasiphako.', 'XHO22'),
(62, 3, 15, 'Ukuzeka kade umsindo kweNkosi yethu kubaleleni ekuthini lusindiso; njengokuba wanibhalelayo nomzalwana wethu oyintanda, uPawulos, ngokobulumko awabunikwayo;', 'XHO22'),
(62, 3, 16, 'njengokuba, nasezincwadini zakhe zonke, ethetha kuzo ngezo ndawo; ekukhoyo kuzo zithile ziqiqeka nzima, abathi abangafundileyo, nabangazimasekileyo, bazigqwethele kokwabo ukutshabalala, njengokuba bezenjenjalo nezinye izibhalo.', 'XHO22'),
(62, 3, 17, 'Nani ngoko, zintanda, nizazi nje ngenxa engaphambili ezo zinto, zigcineni okwenu, ukuze ningathi, nikhukuliswe lulahlekiso lwabangenammiselo, niphalale kokukokwenu ukuqina.', 'XHO22'),
(62, 3, 18, 'Kodwa khulani elubabalweni nasekuyazini iNkosi yethu, uMsindisi uYesu Kristu. Kuye malubekho uzuko nangoku, kuse nakwimini engunaphakade. Amen.', 'XHO22'),
(63, 1, 1, 'Le nto yayikho kwasekuqalekeni, esakuvayo, esiyibonileyo ngamehlo ethu, esayibonayo, zaza izandla zethu zayiphatha, ngelizwi lobomi;', 'XHO22'),
(63, 1, 2, 'Kuba obo bomi babonakaliswayo, nathi sabubona, siyangqina, siyanibonisa, obo bomi bungunaphakade, bona bube bunaye uYise, babonakaliswa kuthi;', 'XHO22'),
(63, 1, 3, 'Oko ke sikubonileyo, sikuvileyo, siyakuxela kuni, ukuze nani nibe nobudlelane nathi. Baye ke ubudlelane bethu bunaye uYise, bunaye uNyana wakhe uYesu Kristu.', 'XHO22'),
(63, 1, 4, 'Ezi zinto sinibhalela ukuze uvuyo lwenu luzaliseke.', 'XHO22'),
(63, 1, 5, 'Lulo olu ke udaba esiluvileyo kuye, esinityela lona, lokuba uThixo ukukukhanya, akukho bumnyama kuye nakanye.', 'XHO22'),
(63, 1, 6, 'Ukuba sithi sinobudlelane naye, sibe sihamba ebumnyameni, soba siyaxoka, asiyenzi inyaniso;', 'XHO22'),
(63, 1, 7, 'ke, ukuba sihamba ekukhanyeni, njengokuba yena esekukhanyeni, soba sinobudlelane omnye nomnye, lithi negazi likaYesu Kristu, uNyana wakhe, lisihlambulule kuso sonke isono.', 'XHO22'),
(63, 1, 8, 'Ukuba sithi asinasono, siyazikhohlisa, inyaniso ayikho kuthi.', 'XHO22'),
(63, 1, 9, 'Ukuba sithi sizivume izono zethu, uthembekile, ulilungisa, ukuba asixolele izono zethu, asihlambulule kuko konke ukungalungisi.', 'XHO22'),
(63, 1, 10, 'Ukuba sithi asonanga, simenza ixoki; ilizwi lakhe alikho kuthi.', 'XHO22'),
(63, 2, 1, 'Bantwanana bam, ndinibhalela ezi zinto, ukuze ningoni. Ukuba kukho othe wona, sinoMthetheleli kuye uYise, uYesu Kristu ilungisa.', 'XHO22'),
(63, 2, 2, 'Yena usisicamagushelo sezono zethu; engesazethu zodwa, usiso nesehlabathi liphela.', 'XHO22'),
(63, 2, 3, 'Sazi ngale nto ukuba siyamazi, ngokuthi siyigcine imithetho yakhe.', 'XHO22'),
(63, 2, 4, 'Lowo uthi, Ndimazile, abe engayigcini imithetho yakhe, ulixoki, kulowo ayikho inyaniso.', 'XHO22'),
(63, 2, 5, 'Ke othi aligcine ilizwi lakhe, okwenyaniso uthando lukaThixo lugqibelele kulowo; sazi ngaloo nto ukuba sikuye.', 'XHO22'),
(63, 2, 6, 'Lowo uthi uhleli kuye, ufanele ukuthi, njengoko wahambayo yena, enjenjalo ukuhamba naye.', 'XHO22'),
(63, 2, 7, 'Bazalwana, andinibhaleli mthetho mtsha; ngumthetho omdala, ebeninawo kwasekuqalekeni. Umthetho omdala ke lilizwi enalivayo kwasekuqalekeni.', 'XHO22'),
(63, 2, 8, 'Ndibuya ndinibhalele umthetho omtsha, okuyinene oko kuye nakuni; ngokuba ubumnyama buyadlula, kwaye ukukhanya okuyinyaniso sekubonakala.', 'XHO22'),
(63, 2, 9, 'Lowo uthi usekukhanyeni, abe emthiyile umzalwana wakhe, usebumnyameni unangoku.', 'XHO22'),
(63, 2, 10, 'Lowo umthandayo umzalwana wakhe uhleli ekukhanyeni, akukho sikhubekiso kuye.', 'XHO22'),
(63, 2, 11, 'Ke lowo umthiyileyo umzalwana wakhe usebumnyameni, uhamba ebumnyameni. Akazi apho aya khona, ngokuba ubumnyama buwamfamekisile amehlo akhe.', 'XHO22'),
(63, 2, 12, 'Ndiyanibhalela, bantwanana, ngokuba nizixolelwe izono zenu ngenxa yegama lakhe.', 'XHO22'),
(63, 2, 13, 'Ndiyanibhalela, boyise, ngokuba nimazile owasekuqalekeni. Ndiyanibhalela, madodana, ngokuba nimeyisile ongendawo. Ndiyanibhalela, bantwanana, ngokuba nimazile uBawo.', 'XHO22'),
(63, 2, 14, 'Ndanibhalela, boyise, ngokuba nimazile owasekuqalekeni. Ndanibhalela, madodana, ngokuba nomelele, laye ilizwi likaThixo lihleli kuni, nimoyisile ke ongendawo.', 'XHO22'),
(63, 2, 15, 'Musani ukulithanda ihlabathi, kwanezinto ezisehlabathini. Ukuba kukho othi alithande ihlabathi, uthando lukaYise alukho kuye.', 'XHO22'),
(63, 2, 16, 'Kaloku konke okusehlabathini, inkanuko yenyama, nenkanuko yamehlo, noqhankqalazo lobu bomi, asikokuphuma kuye uYise, kokuphuma ehlabathini.', 'XHO22'),
(63, 2, 17, 'Ihlabathi ke liyadlula, kwanenkanuko yalo; ke yena owenza ukuthanda kukaThixo uhleli ngonaphakade.', 'XHO22'),
(63, 2, 18, 'Bantwana, lilixa lokugqibela eli; njengoko nevayo ukuba umchasi-Kristu uyeza, nangoku kuthe kwabakho abachasi-Kristu abaninzi, esaziyo ngalo ukuba lilixa lokugqibela.', 'XHO22'),
(63, 2, 19, 'Baphuma kuthi, kodwa babengengabo abethu; kuba, ukuba babengabethu, ngebahlalayo nathi; baphuma ukuze babonakale, ukuba bonke babengengabo abethu.', 'XHO22'),
(63, 2, 20, 'Ke nina ninentambiso evela koyiNgcwele, nizazi ke zonke izinto.', 'XHO22'),
(63, 2, 21, 'Andinibhalelanga ngakuba ningayazi inyaniso; ndinibhalele ngokuba niyazi, nangokuba kungekho buxoki buphuma enyanisweni.', 'XHO22'),
(63, 2, 22, 'Ngubani na ixoki, ingenguye okhanyelayo ukuba uYesu unguye uKristu? Lowo ungumchasi-Kristu, omkhanyelayo uYise noNyana.', 'XHO22'),
(63, 2, 23, 'Lowo umkhanyelayo uNyana, akanaye noYise;', 'XHO22'),
(63, 2, 24, 'Nina ngoko, oko nakuvayo kwasekuqalekeni makuhlale kuni. Ukuba kuthi kuhlale kuni oko nakuvayo kwasekuqalekeni, nani niya kuhlala kuye uNyana, nakuye uYise.', 'XHO22'),
(63, 2, 25, 'Lilo eli idinga, athe yena wasinika lona, ubomi obungunaphakade.', 'XHO22'),
(63, 2, 26, 'Ezo zinto ndinibhalele ngabo banilahlekisayo.', 'XHO22'),
(63, 2, 27, 'Nina intambiso enayamkelayo kuye ihleli kuni, anifuni ukuba nifundiswe ngubani; kunjengokuba kwaloo ntambiso inifundisa ngeento zonke, iyinyani, ingebubuxoki, njengoko yanifundisileyo, hlalani kuye.', 'XHO22'),
(63, 2, 28, 'Ngoku ke, bantwanana, hlalani kuye; ukuze xa athe wabonakalaliswa, sibe nokungafihlisi singadani, simbaleke ekufikeni kwakhe.', 'XHO22'),
(63, 2, 29, 'Ukuba niyazi ukuba ulilungisa, yazini ukuba bonke abenza ubulungisa bazelwe nguye.', 'XHO22'),
(63, 3, 1, 'Bonani uthando olungaka asenzele lona uYise, olu lokuba sibizwe ngokuba singabantwana bakaThixo. Ngenxa yoko ihlabathi alisazi thina, ngokuba lingamazanga yena.', 'XHO22'),
(63, 3, 2, 'Zintanda, ngoku singabantwana bakaThixo. Akukabonakali ke into esiya kuba yiyo; ke siyazi ukuba, xa athe wabonakala, sofana naye; ngokuba siya kumbona njengoko anjalo.', 'XHO22'),
(63, 3, 3, 'Bonke abanalo eli themba kuye, bazenza nyulu, njengokuba yena enyulu.', 'XHO22'),
(63, 3, 4, 'Bonke abasenzayo isono benza okuchasene nomthetho, kuba isono sikukwaphula umthetho.', 'XHO22'),
(63, 3, 5, 'Niyazi ke ukuba yena wabonakaliswa, ukuze azithwale azisuse izono zethu; kananjalo akukho sono kuye.', 'XHO22'),
(63, 3, 6, 'Bonke abahlala kuye aboni; bonke abonayo abambonanga, abamazi nokumazi.', 'XHO22'),
(63, 3, 7, 'Bantwanana, makungabikho namnye unikhohlisayo; lowo ubenzayo ubulungisa lilungisa, njengokuba yena elilungisa.', 'XHO22'),
(63, 3, 8, 'Lowo usenzayo isono ungokaMtyholi; ngokuba uMtyholi wona kwasekuqalekeni. Wabonakaliselwa oku uNyana kaThixo, ukuze ayichithe imisebenzi kaMtyholi.', 'XHO22'),
(63, 3, 9, 'Bonke abazelwe nguThixo abenzi sono; ngokuba imbewu yakhe ihleli kuye; kanjalo akanakona, ngokuba ezelwe nguThixo.', 'XHO22'),
(63, 3, 10, 'Babonakala ngaloo nto abantwana bakaThixo, nabantwana bakaMtyholi. Bonke abangenzi bulungisa abangabakaThixo, nalowo ungamthandiyo umzalwana wakhe.', 'XHO22'),
(63, 3, 11, 'Ngokuba le yintshumayelo enayivayo kwasekuqalekeni, ukuba sithandane;', 'XHO22'),
(63, 3, 12, 'singabi njengoKayin, obengowongendawo, wambulala umninawa wakhe. Yini na ukuba ambulale? Ngokuba eyakhe imisebenzi ibingendawo, ke yona eyomninawa ibinobulungisa.', 'XHO22'),
(63, 3, 13, 'Musani ukumangaliswa, bazalwana bam, ukuba ihlabathi linithiyile.', 'XHO22'),
(63, 3, 14, 'Siyazi ke ukuba sidlule ekufeni, sangena ebomini, ngokuba sibathanda abazalwana. Lowo ungamthandiyo umzalwana uhleli ekufeni.', 'XHO22'),
(63, 3, 15, 'Lowo umthiyileyo umzalwana wakhe usisibulala-mntu; niyazi ukuba akukho sibulala-mntu sinobomi bungunaphakade buhleliyo kuso.', 'XHO22'),
(63, 3, 16, 'Ngale nto silwazile uthando, kuba yena wasincamela ubomi bakhe; nathi ke sifanele ukubancamela abazalwana ubomi bethu.', 'XHO22'),
(63, 3, 17, 'Osukuba ke enayo impilo yeli hlabathi, aze ambone umzalwana wakhe eswele, azivalele iimfesane zakhe kuye, luthini na ukuhlala uthando lukaThixo ngaphakathi kwakhe?', 'XHO22'),
(63, 3, 18, 'Bantwanana bam, masingathandi ngazwi nangamlomo; kodwa ngezenzo nangenyaniso.', 'XHO22'),
(63, 3, 19, 'Siyazi ngale nto ke ukuba singabenyaniso; siya kuzileleza iintliziyo zethu phambi kwakhe.', 'XHO22'),
(63, 3, 20, 'Kaloku ukuba intliziyo yethu iyasigweba, uThixo mkhulu kunentliziyo yethu, ezazi izinto zonke.', 'XHO22'),
(63, 3, 21, 'Zintanda, ukuba intliziyo yethu ayisigwebi, sinokungafihlisi kuye uThixo;', 'XHO22'),
(63, 3, 22, 'nesithi sikucele siyakwamkela kuye, ngokuba imithetho yakhe siyigcina, sizenze izinto ezikholekileyo emehlweni akhe.', 'XHO22'),
(63, 3, 23, 'Nguwo lo umthetho wakhe, ukuba sikholwe ligama lonyana wakhe, uYesu Kristu, sithandane njengoko wasiwiselayo umthetho.', 'XHO22'),
(63, 3, 24, 'Lowo uyigcinayo imithetho yakhe uhleli kuye, naye uhleli kuye. Sazi ngale nto ke, ukuba uhleli kuthi ngoMoya lo wasinikayo.', 'XHO22'),
(63, 4, 1, 'Zintanda, musani ukukholwa ngoomoya bonke, bacikideni oomoya ukuba ngabakaThixo na; ngokuba baninzi abaprofeti ababuxoki, abaphume bangena ehlabathini.', 'XHO22'),
(63, 4, 2, 'Mazini ngale nto uMoya kaThixo: umoya wonke omvumayo uYesu Kristu eze esenyameni, ungokaThixo;', 'XHO22'),
(63, 4, 3, 'Nomoya wonke ongamvumiyo uYesu Kristu eze esenyameni, asingokaThixo; lo ngokamchasi-Kristu, enawuvayo ukuba uyeza; nangoku uselukho ehlabathini.', 'XHO22'),
(63, 4, 4, 'Nina ningabakaThixo, bantwanana, nibeyisile bona, ngokuba lowo ukuni nguyena mkhulu kunalowo usehlabathini.', 'XHO22'),
(63, 4, 5, 'Bona bangabehlabathi, ngenxa yoko bathetha okwehlabathi, ihlabathi liyabeva ke.', 'XHO22'),
(63, 4, 6, 'Thina singabakaThixo, lowo umaziyo uThixo uyasiva thina; ongengokaThixo akasiva. Sazi ngale nto ke umoya wayo inyaniso, nomoya walo ulahlekiso.', 'XHO22'),
(63, 4, 7, 'Zintanda, masithandane; ngokuba uthando lwaphuma kuThixo; Bonke abanothando bazelwe nguThixo, bayamazi uThixo.', 'XHO22'),
(63, 4, 8, 'Lowo ungenaluthando akazanga amazi uThixo; ngokuba uThixo uluthando.', 'XHO22'),
(63, 4, 9, 'Kwabonakaliswa ngale nto ukusithanda kukaThixo, ngokuthi uThixo amthume ehlabathini uNyana wakhe, ekuphela kwamzeleyo, ukuze sidle ubomi naye.', 'XHO22'),
(63, 4, 10, 'Uthando lulapha, ingekukuba samthandayo uThixo thina, ikukuba wasithandayo yena, wamthuma uNyana wakhe ukuba abe sisicamagushelo sezono zethu.', 'XHO22'),
(63, 4, 11, 'Zintanda, ukuba wenjenjalo uThixo ukusithanda, nathi sifanele ukuthandana.', 'XHO22'),
(63, 4, 12, 'Akukho namnye wakha wambona uThixo; Ukuba sithi siyathandana, uThixo uhleli kuthi, nothando lwakhe lugqibelele ngaphakathi kwethu.', 'XHO22'),
(63, 4, 13, 'Sazi ngale nto ukuba sihleli kuye, naye uhleli kuthi; ngokuba usinike okoMoya wakhe.', 'XHO22'),
(63, 4, 14, 'Nathi sibonile, siyangqina, ukuba uYise wamthuma uNyana ukuba nguMsindisi wehlabathi.', 'XHO22'),
(63, 4, 15, 'Othe wavuma ukuba uYesu unguye uNyana kaThixo, uThixo uhleli kuye, naye uhleli kuye uThixo.', 'XHO22'),
(63, 4, 16, 'Thina siyalwazi, sakholwa nokusithanda kukaThixo. Uthixo Luthando; Lowo uhleli eluthandweni uhleli kuye uThixo, noThixo uhleli kuye.', 'XHO22'),
(63, 4, 17, 'Lugqibelele ngale nto uthando olukuthi, ukuze sibe nokungafihlisi ngomhla womgwebo; ngokuba, njengoko anjalo yena, sinjalo nathi kweli hlabathi.', 'XHO22'),
(63, 4, 18, 'Akukho loyiko eluthandweni; lusuka uthando olugqibeleleyo luluphose phandle uloyiko; ngokuba uloyiko lunexhala; Lowo ke woyikayo akagqibelele eluthandweni.', 'XHO22'),
(63, 4, 19, 'Thina siyamthanda, ngokuba yena wasithanda kuqala.', 'XHO22'),
(63, 4, 20, 'Ukuba umntu uthe, Ndiyamthanda uThixo, wamthiya umzalwana wakhe, ulixoki; kuba lowo ungamthandiyo umzalwana wakhe ambonileyo, angathini na ukumthanda uThixo angambonanga?', 'XHO22'),
(63, 4, 21, 'Nguwo lo umthetho esinawo uvela kuye: ukuba athi lowo umthandayo uThixo, amthande nomzalwana wakhe.', 'XHO22'),
(63, 5, 1, 'Bonke abakholwayo ukuba uYesu unguye uKristu, bazelwe nguThixo; bonke abamthandayo lowo wazalayo, bayamthanda nalowo uzelweyo nguye.', 'XHO22'),
(63, 5, 2, 'Sazi ngale nto ukuba siyabathanda abantwana bakaThixo, xa sukuba simthanda uThixo, siyigcina imithetho yakhe.', 'XHO22'),
(63, 5, 3, 'Kuba ukumthanda uThixo kuko ukuthi, siyigcine imithetho yakhe; ayinzima ke imithetho yakhe.', 'XHO22'),
(63, 5, 4, 'Ngokuba yonke into ezelwe nguThixo iyaleyisa ihlabathi; lulo ke olu uloyiso oluleyisayo ihlabathi, ukholo lwethu.', 'XHO22'),
(63, 5, 5, 'Ngubani na lowo uleyisayo ihlabathi, ingenguye okholwayo ukuba uYesu unguye uNyana kaThixo?', 'XHO22'),
(63, 5, 6, 'Lo ngulowo wezayo ngamanzi negazi, uYesu Kristu; kungengamanzi odwa, kungengamanzi negazi. uMoya ke ungqinayo, ngokuba uMoya uyinyaniso.', 'XHO22'),
(63, 5, 7, 'Ngokuba bathathu abo bangqinayo emazulwini: uYise, noLizwi, noMoya oyiNgcwele; abo bathathu banye.', 'XHO22'),
(63, 5, 8, 'Kaloku kukho amangqina amathathu ehlabathini, uMoya, namanzi, negazi. Aba bathathu bavumelana nto-nye.', 'XHO22'),
(63, 5, 9, 'Ukuba ubungqina babantu siyabamkela, ubungqina bukaThixo bungaphezulu; ngokuba bubo obu ubungqina bukaThixo, angqinele uNyana wakhe ngabo.', 'XHO22'),
(63, 5, 10, 'Lowo ukholwayo kuNyana kaThixo unabo ubungqina ngaphakathi kwakhe; ngokuba engakholwanga bubungqina abungqinele uThixo ngoNyana wakhe.', 'XHO22'),
(63, 5, 11, 'Bubo obu ke ubungqina: ukuba uThixo usinike ubomi obungunaphakade; baye obo bomi bukuye uNyana wakhe.', 'XHO22'),
(63, 5, 12, 'Lowo unaye uNyana, unabo ubomi; lowo ungenaye uNyana kaThixo, akanabo ubomi.', 'XHO22'),
(63, 5, 13, 'Ndinibhalele ezi zinto, nina nikholwayo kwigama loNyana kaThixo; ukuze nazi ukuba ninobomi obungunaphakade, nokuze nikholwe kwigama loNyana kaThixo.', 'XHO22'),
(63, 5, 14, 'Kuko oku ukungafihlisi esinako kuye: ukuba sithi sicele into ngokokuthanda kwakhe, uyasiva;', 'XHO22'),
(63, 5, 15, 'Ukuba siyazi ukuba uyasiva esisukuba sikucela, siyazi ukuba sinazo izinto ezicelwayo, esizicelileyo kuye.', 'XHO22'),
(63, 5, 16, 'Ukuba umntu uthe wambona umzalwana wakhe esona isono esingasingisi kufeni, wocela, uThixo amnike ubomi, enika abo bonayo ngokungasingisi kufeni. Kukho isono esisingisa ekufeni; anditsho ukuba makasithandazele.', 'XHO22'),
(63, 5, 17, 'Konke ukungalungisi kusisono; kukho isono esingasingisi kufeni.', 'XHO22'),
(63, 5, 18, 'Siyazi ukuba bonke abazelwe nguThixo aboni; kodwa lowo uzelweyo nguThixo uyazigcina, aze ongendawo angamchukumisi.', 'XHO22'),
(63, 5, 19, 'Siyazi ukuba singabakaThixo, nokuba ihlabathi liphela lilele kongendawo.', 'XHO22'),
(63, 5, 20, 'Siyazi ke ukuba uNyana kaThixo ufikile, wasinika ingqondo, ukuze simazi oyinyaniso. Sikuye ke oyinyaniso, kuye uNyana wakhe uYesu Krestu. Nguye lo uThixo oyinyaniso, nobomi obungunaphakade.', 'XHO22'),
(63, 5, 21, 'Bantwanana, zigcineni kuzo izithixo. Amen.', 'XHO22'),
(64, 1, 1, 'Indoda enkulu ibhalela inkosikazi enyuliweyo nabantwana bayo, endibathandayo mna ngenyaniso; ingendim ndedwa, ingabo bonke abayazileyo inyaniso;', 'XHO22'),
(64, 1, 2, 'ngenxa yenyaniso ehleliyo kuthi, eya kuba nathi ngonaphakade.', 'XHO22'),
(64, 1, 3, 'Makube lubabalo kuni, nenceba, noxolo oluvela kuThixo uYise, neNkosi yethu uYesu Kristu, uNyana kaYise, kuni basenyanisweni naseluthandweni.', 'XHO22'),
(64, 1, 4, 'Ndavuya kunene kuba ndifumene kubantwana bakho kukho abahamba enyanisweni, njengoko samkelayo umthetho kuye uBawo.', 'XHO22'),
(64, 1, 5, 'Nangoku ndiyakucela, nkosikazi, kungengakuba ndikubhalela umthetho omtsha; ndikubhalela lowo sasinawo kwasekuqalekeni: ukuba sithandane.', 'XHO22'),
(64, 1, 6, 'Lulo ke olu uthando, ukuba sihambe ngokwemithetho yakhe. Nguwo lo umthetho, njengokuba nevayo kwasekuqalekeni, ukuba nihambe kuwo.', 'XHO22'),
(64, 1, 7, 'Ngokuba kungene abalahlekisi abaninzi ehlabathini, abangamvumiyo uYesu Kristu esiza esenyameni. Lowo ngumkhohlisi nomchasi-Kristu.', 'XHO22'),
(64, 1, 8, 'Zilumkeleni, ukuba singalahlekani nezinto esizisebenzileyo; masamkele umvuzo ozalisekileyo.', 'XHO22'),
(64, 1, 9, 'Bonke abagqithayo, abangahlaliyo emfundisweni kaKristu, abanaThixo; ohleliyo emfundisweni kaKristu, lowo unaye uYise noNyana.', 'XHO22'),
(64, 1, 10, 'Ukuba ubani uthe weza kuni, engazisi yona le mfundiso, musani ukumamkela endlwini yenu, ningambulisi nokumbulisa;', 'XHO22'),
(64, 1, 11, 'Kuba lowo umbulisayo unobudlelane naye ngemisebenzi yakhe engendawo.', 'XHO22'),
(64, 1, 12, 'Ndinezinto ezininzi nje zokunibhalela, andingi ndinibhalele ngaphepha nangainki; koko ndithembe ukuza kuni, sithethe ngomlomo, ukuze uvuyo lwethu luzaliseke.', 'XHO22'),
(64, 1, 13, 'Bakubulisa abantwana bodade wenu onyuliweyo. Amen.', 'XHO22'),
(65, 1, 1, 'Indoda enkulu ibhalela uGayo oyintanda, endimthandayo mna ngenyaniso.', 'XHO22'),
(65, 1, 2, 'Ntanda, ndinga ungathi ngezinto zonke usikeleleke uphile, njengokuba usikelelekile umphefumlo wakho.', 'XHO22'),
(65, 1, 3, 'Kaloku ndavuya kunene, bakufika abazalwana, bangqina ngenyaniso ekuwe, njengokuba wena uhamba enyanisweni.', 'XHO22'),
(65, 1, 4, 'Andinaluvuyo lungaphezu kolu, lokuba ndive ukuba abantwana bam bahamba enyanisweni.', 'XHO22'),
(65, 1, 5, 'Ntanda, wenza okuthembekileyo, oko sukuba ukwenzela abazalwana nabasemzini;', 'XHO22'),
(65, 1, 6, 'abalungqineleyo uthando lwakho phambi kwebandla; owathi, ukuba uthe wabaqhubela ngendlela yobuthixo, ube uyatyapha;', 'XHO22'),
(65, 1, 7, 'Kuba baphuma ngenxa yegama lakhe, bengathabathanga nto kuzo iintlanga.', 'XHO22'),
(65, 1, 8, 'Thina ngoko sifanele ukubamkela abanjalo, ukuze sibe ngabasebenzisana nabo kuyo inyaniso.', 'XHO22'),
(65, 1, 9, 'Ndalibhalela ibandla, kodwa uDiyotrefe, othanda ukuba mkhulu phakathi kwabo, akasamkeli.', 'XHO22'),
(65, 1, 10, 'Ngoko ke, ukuba ndithe ndafika, ndoyikhumbula imisebenzi yakhe ayenzayo, ephololoza ngathi ngamazwi angendawo; enganeli koko, akabamkeli abazalwana ngokwakhe, abathintele abo bathanda ukubalela, abakhuphe ebandleni.', 'XHO22'),
(65, 1, 11, 'Ntanda, musa ukulandela okubi, landela okulungileyo. Lowo wenza okulungileyo ungokaThixo; ke lowo wenza okubi akambonanga uThixo.', 'XHO22'),
(65, 1, 12, 'uDemetriyo ungqinelwe ngabo bonke, nayiyo inyaniso; nathi ke siyangqina; niyazi ke ukuba ubungqina bethu buyinene.', 'XHO22'),
(65, 1, 13, 'Ndandinezinto ezininzi endingazibhalayo, kodwa andithandanga kuzibhala ngainki nangalusiba;', 'XHO22'),
(65, 1, 14, 'Kodwa ndiyathemba ukuba ndokubona kwakufuphi nje, sithethe ngomlomo. Uxolo malube kuwe. Izihlobo zethu ziyakubulisa. Bulisa abahlobo ngamagama.', 'XHO22'),
(66, 1, 1, 'uYuda, umkhonzi kaYesu Kristu, umzalwana kaYakobi, ubhalela abangcwalisiweyo nguThixo uYise, nabagcinelwe uYesu Kristu, ababiziweyo;', 'XHO22'),
(66, 1, 2, 'Makwandiswe kuni inceba, noxolo, nothando.', 'XHO22'),
(66, 1, 3, 'Zintanda, ndikukhuthalele ngenyameko yonke nje ukunibhalela ngalo usindiso esidlelana ngalo, kwafuneka ukuba ndinibhalele, ndinivuselele, ukuba niluzamele ukholo abalunikelwayo kwakanye abangcwele.', 'XHO22'),
(66, 1, 4, 'Kuba kuthwethwe bantu bathile; abo babebalulelwe ngenxa engaphambili kolo lugwebo, abangahloneli Thixo, belujikela eburheletyweni ubabalo lukaThixo wethu, bemkhanyela okuphela koMnini-nto-zonke, uThixo wethu, iNkosi yethu uYesu Kristu.', 'XHO22'),
(66, 1, 5, 'Ndithanda ukunikhumbuza, nangona nikwazi oku kakade, ukuba iNkosi, yakuba ibasindisile abantu emhlabeni waseYiputa, yathi ngokwesibini yabatshabalalisa abo bangakholwanga.', 'XHO22'),
(66, 1, 6, 'Kananjalo izithunywa zezulu, ezo zazingalugcinanga ulawulo lwazo, zesuka zalishiya ikhaya lazo, izigcinele umgwebo womhla omkhulu ngemixokelelwane engunaphakade, phantsi kwesithokothoko.', 'XHO22'),
(66, 1, 7, 'Njengokuba iSodom neGomora, nayo imizi engakuyo, yenza kakhulu umbulo ngohlobo olufana nolwazo ezo, imka ilandela nyama yimbi, ibekwe ekuhleni ukuba ibe ngumqondiso, iviswa ubetho lomlilo ongunaphakade.', 'XHO22'),
(66, 1, 8, 'Kwangokunjalo nabo aba baphuphayo badyobha inyama, batshitshise ubukhosi, banyelise izikhakhamela.', 'XHO22'),
(66, 1, 9, 'Kodwa uMikayeli, isiphatha-zithunywa zezulu, oko babebambene naye uMtyholi, bebangisana ngawo umzimba kaMoses, akabanga nabuganga bakumzisela mgwebo wakunyelisa; wesuka wathi, iNkosi mayikukhalimele.', 'XHO22'),
(66, 1, 10, 'Ke bona aba banyelisa izinto abangazaziyo; kodwa izinto abazaziyo kakuhle ngemvelo, njengezinto eziphilileyo ezingenangqondo, bayazonakalisa ngazo ezo.', 'XHO22'),
(66, 1, 11, 'Yeha ke bona! Kuba bahamba ngendlela kaKayin; bakhukuliswa lulahlekiso lukaBhileham ngenxa yomvuzo; batshabalala ephikeni likaKora.', 'XHO22'),
(66, 1, 12, 'Aba zizixhobo zamatye aselwandle ezidlweni zenu zothando, bezingxala ukudla nani, bezidlisa okwabo ngokungenakoyika. Bangamafu angenamanzi, aqhutywaqhutywa yimimoya; yimithi ebunayo, engenasiqhamo, efe kabini, enyothulwe iingcambu;', 'XHO22'),
(66, 1, 13, 'Ngamaza alatyuzayo olwandle, ebila ephuphuma ihlazo lawo; iinkwenkwezi ezibhadulayo, ezigcinelwe ubumnyama bobumnyama ngonaphakade.', 'XHO22'),
(66, 1, 14, 'NoEnoki, ongowesixhenxe kuAdam, waprofeta ngabo, esithi, Yabonani, iNkosi iyeza enamawaka alishumi abangcwele bayo,', 'XHO22'),
(66, 1, 15, 'ukuba ibagwebe bonke, ibohlwaye bonke abangahloneli Thixo kubo, ngenxa yemisebenzi yabo yonke yokungahloneli Thixo, abayenze bengahloneli Thixo, nangenxa yazo zonke izinto ezilukhuni, ababezithethile ngaye aboni abangahloneli Thixo.', 'XHO22'),
(66, 1, 16, 'Aba ngabakrokri abasolayo, behamba ngokweenkanuko zabo; umlomo wabo uthetha iindumbisa zamazwi, bencoma ubuso bomntu ngenxa yenzuzo.', 'XHO22'),
(66, 1, 17, 'Ke nina, zintanda, wakhumbuleni amazwi abethethwe ngenxa engaphambili ngabapostile beNkosi yethu uYesu Kristu;', 'XHO22'),
(66, 1, 18, 'Ngokuba babesithi kuni, Kuya kubakho abagxeki ngexesha lokugqibela, behamba ngokwezabo iinkanuko zokungahloneli Thixo.', 'XHO22'),
(66, 1, 19, 'Aba ngabazahlulayo, bengabenyama, abangenaye uMoya.', 'XHO22'),
(66, 1, 20, 'Ke nina, zintanda, zakheni elukholweni lwenu olungcwele kanye, nithandaza nikuMoya oyiNgcwele;', 'XHO22'),
(66, 1, 21, 'Hlalani nihleli eluthandweni lukaThixo, niyilinde inceba yeNkosi yethu uYesu Kristu, kuse ebomini obungunaphakade.', 'XHO22'),
(66, 1, 22, 'Inxenye ke yibani novelwano, nicalula;', 'XHO22'),
(66, 1, 23, 'kodwa inxenye yisindiseni nizoyika, nibakhupha emlilweni; niyithiya nengubo edyojiweyo yinyama.', 'XHO22'),
(66, 1, 24, 'Ke kaloku, kulowo unako ukunigcina ningakhubeka, nokunimisa phambi kobuqaqawuli bakhe ningenabala, ninovuyo olugqithileyo:', 'XHO22'),
(66, 1, 25, 'kuThixo olumke yedwa, uMsindisi wethu, makubekho uzuko nobukhulu, amandla negunya, nangoku, kude kuse ephakadeni. Amen.', 'XHO22'),
(67, 1, 1, 'Isityhilelo sikaYesu Kristu, awamnika sona uThixo, ukuba ababonise abakhonzi bakhe izinto ezimele ukubakho kamsinya; waqondisa wathumela ngaso isithunywa sakhe sasemazulwini kumkhonzi wakhe uYohane,', 'XHO22'),
(67, 1, 2, 'owalingqinelayo ilizwi likaThixo nobungqina bukaYesu Kristu, kwaneento zonke awazibonayo.', 'XHO22'),
(67, 1, 3, 'Unoyolo lowo uwalesayo, kwanabo bawevayo amazwi aso isiprofeto esi, bazigcine izinto ezibhaliweyo kuso; kuba ixesha likufuphi.', 'XHO22'),
(67, 1, 4, 'uYohane ubhalela amabandla asixhenxe akwelaseAsiya; uthi, Makube lubabalo kuni, noxolo oluvela kulowo ukhoyo, wayekho, uzayo; nakubo ooMoya abasixhenxe abaphambi kwetrone yakhe;', 'XHO22'),
(67, 1, 5, 'nakuYesu Kristu, olingqina elithembekileyo, Owamazibulo kubo abafileyo, uMphathi wookumkani bomhlaba. Kuye lowo wasithandayo, owasihlambayo ezonweni zethu ngegazi lakhe;', 'XHO22'),
(67, 1, 6, 'Wasenza ookumkani nababingeleli kuye uThixo uYise; kuye makubekho uzuko namandla, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(67, 1, 7, 'Yabonani, esiza namafu; aye eya kumbona amehlo onke, kwanabo bamhlabayo; zaye ziya kummbambazelela zonke izizwe zomhlaba. Ewe kunjalo, Amen.', 'XHO22'),
(67, 1, 8, 'Mna ndinguAlfa no-Omega, isiqalo nesiphelo, itsho iNkosi, leyo ikhoyo, yayikho, izayo, uSomandla.', 'XHO22'),
(67, 1, 9, 'Mna Yohane, endikwangumzalwana wenu, nedlelane nani ngayo imbandezelo, nangabo ubukumkani, nangonyamezelo lukaYesu Kristu, ndaye ndisesiqithini ekuthiwa yiPatmos, ngenxa yelizwi likaThixo, nangenxa yobungqina bukaYesu Kristu.', 'XHO22'),
(67, 1, 10, 'Ndaye ndikuMoya ngemini eyeyeNkosi; ndeva emva kwam izwi elikhulu, ngathi lelexilongo,', 'XHO22'),
(67, 1, 11, 'esithi, Mna ndinguAlfa no-Omega, owokuqala nowokugqibela; nokuthi, Okubonileyo, kubhale encwadini, ukuthumele kuwo amabandla asixhenxe akwelaseAsiya; e-Efese, eSmirna, ePergamo, naseTiyatira, naseSardesi, eFiladelfi, naseLawodike.', 'XHO22'),
(67, 1, 12, 'Ndaza ndee guququ, ukuba ndilibone ilizwi elo belithetha nam. Ndithe guququ, ndabona iziphatho zezibane zegolide zisixhenxe;', 'XHO22'),
(67, 1, 13, 'Naphakathi kweziphatho zosixhenxe ezo, ofana noNyana woMntu, ambethe ingubo efike ezinyaweni, ebhinqe emabeleni umbhinqo wegolide.', 'XHO22'),
(67, 1, 14, 'Intloko yakhe ke neenwele zimhlophe, njengoboya bezimvu obumhlophe njengekhephu; amehlo akhe enjengelangatye lomlilo;', 'XHO22'),
(67, 1, 15, 'iinyawo zakhe zifana nobhedu olubengezelayo, zingathi zivuthiswe eziko; nelizwi lakhe ngathi sisandi samanzi amaninzi.', 'XHO22'),
(67, 1, 16, 'Waye eneenkwenkwezi ezisixhenxe esandleni sakhe sokunene, emlonyeni wakhe kuphuma ikrele elibukhali, elintlangothi mbini; ubuso bakhe bungathi lilanga, likhanya ngamandla alo.', 'XHO22'),
(67, 1, 17, 'Ndithe ndakumbona, ndasuka ndawa ezinyaweni zakhe, ndaba njengofileyo. Waza wabeka isandla sakhe sokunene phezu kwam, esithi kum, Musa ukoyika; ndingowokuqala nowokugqibela;', 'XHO22'),
(67, 1, 18, 'Ndingulowo udla ubomi, ndaza ndafa; yabona, ndingodla ubomi kuse emaphakadeni asemaphakadeni, Amen; Ndinezitshixo zelabafileyo nezokufa.', 'XHO22'),
(67, 1, 19, 'Zibhale izinto ozibonileyo, nezikhoyo, kwaneziya kubakho emva kwezo.', 'XHO22'),
(67, 1, 20, 'Imfihlakalo yeenkwenkwezi ezisixhenxe ozibonileyo esandleni sam sokunene, neziphatho zezibane zosixhenxe zegolide; Iinkwenkwezi ezo zisixhenxe zizithunywa zawo amabandla asixhenxe;', 'XHO22'),
(67, 2, 1, 'Isithunywa sebandla elise-Efese, sibhalele uthi: Utsho lowo uzibambayo iinkwenkwezi ezisixhenxe ngesandla sokunene, lowo uhamba phakathi kweziphatho zezibane zosixhenxe zegolide;', 'XHO22'),
(67, 2, 2, 'Ndiyayazi imisebenzi yakho, nokubulaleka kwakho, nomonde wakho, nokuba ungenako ukubathwala abanobubi; nokuba wabalinga abathi bangabapostile, bengengabo, wabafumana bengamaxoki.', 'XHO22'),
(67, 2, 3, 'Wanyamezela, waba nomonde, ngenxa yegama lam wabulaleka, akwatyhafa.', 'XHO22'),
(67, 2, 4, 'Noko ke ndinale nto ngakuwe, yokuba uthando lwakho lokuqala walushiyayo.', 'XHO22'),
(67, 2, 5, 'Khumbula ngoko apho uwe usuka khona, uguquke, wenze imisebenzi yokuqala; okanye ndoza kuwe kamsinya, ndisishenxise isiphatho sesibane sakho endaweni yaso, ukuba akuthanga uguquke.', 'XHO22'),
(67, 2, 6, 'Kodwa unale ndawo, yokuba uyithiyile imisebenzi yamaNikolawo, endiyithiyileyo nam.', 'XHO22'),
(67, 2, 7, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla. Lowo weyisayo ndiya kumnika ukuba adle kuwo umthi wobomi, ophakathi kweParadisi kaThixo.', 'XHO22'),
(67, 2, 8, 'Isithunywa sebandla eliseSmirna, sibhalele uthi: Utsho owokuqala nowokugqibela, owayefile, wabuya wadla ubomi;', 'XHO22'),
(67, 2, 9, 'Ndiyayazi imisebenzi yakho, nembandezelo, nobuhlwempu, (noko usisityebi), nonyeliso lwabo bathi bangamaYuda bona, bengengawo, besisikhungu sikaSathana.', 'XHO22'),
(67, 2, 10, 'Musa ukuzoyika izinto oza kubandezeleka ngazo. Yabona, uMtyholi uza kuyiphosa inxenye yenu entolongweni, ukuze nilingwe; Ke niya kuba nembandezelo imihla elishumi; thembekile kude kuse ekufeni, ndokunika isithsaba sobomi.', 'XHO22'),
(67, 2, 11, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla. Lowo weyisayo akayi kwenziwa kukufa kwesibini.', 'XHO22'),
(67, 2, 12, 'Isithunywa sebandla elisePergamo, sibhalele uthi: Utsho lowo unekrele elibukhali, elintlangothi mbini;', 'XHO22'),
(67, 2, 13, 'Ndiyayazi imisebenzi yakho, nalapho uhleli khona, apho sikhona isihlalo sikaSathana; nokuba ulibambe waliqinisa igama lam, akwakukhanyela ukukholwa kwam, nasemihleni uAntipas, owabulawelwa kuni, apho ahleli khona uSathana, wayelingqina lam elithembekileyo.', 'XHO22'),
(67, 2, 14, 'Noko ke ndineendawana ngakuwe: ezi zokuba unabo apho abayibambe bayibambe imfundiso kaBhileham, owamfundisayo uBhalaki ukuphosa isikhubekiso phambi koonyana bakaSirayeli, ukuba badle izinto ezibingelelwe izithixo, benze nombulo.', 'XHO22'),
(67, 2, 15, 'Ngokunjalo nawe unabayibambe bayiqinisa imfundiso yamaNikolawo, into leyo endiyithiyileyo.', 'XHO22'),
(67, 2, 16, 'Guquka; okanye ndoza kuwe kamsinya, ndenze imfazwe nabo ngalo ikrele lomlomo wam.', 'XHO22'),
(67, 2, 17, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla. Lowo weyisayo ndiya kumnika ukuba adle kuyo imana efihlakeleyo, ndimnike ilitye elimhlophe;', 'XHO22'),
(67, 2, 18, 'Isithunywa sebandla eliseTiyatira, sibhalele uthi; Utsho uNyana kaThixo, omehlo akhe anjengelangatye lomlilo, neenyawo zakhe zifana nobhedu olubengezelayo;', 'XHO22'),
(67, 2, 19, 'Ndiyayazi imisebenzi yakho, nothando, nolungiselelo, nokholo, nomonde wakho, nemisebenzi yakho; aze abokugqibela abe ngaphezu kwabokuqala.', 'XHO22'),
(67, 2, 20, 'Kambe ke ndineendawana ngakuwe: ezi zokuba umvumela laa mfazi uIzebhele, othi yena ungumprofetikazi, ukuba abafundise abalahlekise abakhonzi bam, ukuba benze umbulo, badle izinto ezibingelelwe izithixo.', 'XHO22'),
(67, 2, 21, 'Ndamnika ixesha lokuba aguquke kuwo umbulo wakhe; akaguquka.', 'XHO22'),
(67, 2, 22, 'Yabona, mna ndiya kumphosa elukhukweni, nabo bakrexeza kunye naye, embandezelweni enkulu, ukuba abathanga baguquke emisebenzini yabo;', 'XHO22'),
(67, 2, 23, 'nabantwana bakhe ndiya kubabulala ngokufa; azi onke amabandla ukuba mna ndingulowo uphengulula izintso neentliziyo, ndinike elowo ngokwemisebenzi yenu.', 'XHO22'),
(67, 2, 24, 'Ndithi ke kuni, nakwabanye abaseTiyatira, abo bonke bangenayo loo mfundiso, nabo abo bangazazanga iinzulu zikaSathana, njengoko batshoyo; andiyi kubeka phezu kwenu mthwalo wumbi.', 'XHO22'),
(67, 2, 25, 'Kodwa oko ninako kubambeni nikuqinise ndide ndifike.', 'XHO22'),
(67, 2, 26, 'Lowo weyisayo, lowo uyigcinayo imisebenzi yam, ade ase ekupheleni, ndomnika igunya phezu kweentlanga;', 'XHO22'),
(67, 2, 27, 'Uya kuzalusa ngentonga yentsimbi; njengokuba izitya zomdongwe zityunyuzwa, njengokuba nam ndamkele kuye uBawo;', 'XHO22'),
(67, 2, 28, 'ndiya kumnika ikhwezi lokusa.', 'XHO22'),
(67, 2, 29, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla.', 'XHO22'),
(67, 3, 1, 'Isithunywa sebandla eliseSardesi, sibhalele uthi; Utsho lowo unabo ooMoya abasixhenxe bakaThixo, neenkwenkwezi ezisixhenxe; Ndiyayazi imisebenzi yakho, ukuba unalo igama lokuba uphilile, ukanti ufile.', 'XHO22'),
(67, 3, 2, 'Phapha, uzizimase izinto eziseleyo eziza kufa; kuba andiyifumananga izalisekile imisebenzi yakho phambi koThixo.', 'XHO22'),
(67, 3, 3, 'Khumbula ngoko ukuba utheni na ukwamkela kwakho, nokuva, nokuba utheni na, uguquke. Ukuba ngoko akuthanga ulinde, ndokufikela njengesela, yaye akusayi kulazi ilixa endokufikela ngalo.', 'XHO22'),
(67, 3, 4, 'Unamagama ambalwa naseSardesi apho, angazidyobhanga wona iingubo zawo; baya kuhamba nam benezimhlophe; ngokuba befanelekile.', 'XHO22'),
(67, 3, 5, 'Lowo weyisayo uya kuthiwa wambu ngeengubo ezimhlophe; Andiyi kulicima igama lakhe encwadini yobomi; ndiya kulivuma igama lakhe phambi koBawo, naphambi kwezithunywa zakhe zasemazulwini.', 'XHO22'),
(67, 3, 6, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla.', 'XHO22'),
(67, 3, 7, 'Isithunywa sebandla eliseFiladelfi, sibhalele uthi; Utsho lowo ungcwele, oyiNyaniso, lowo unaso isitshixo sikaDavide, lowo uvulayo kungavali namnye; avale kungabikho uvulayo;', 'XHO22'),
(67, 3, 8, 'Ndiyayazi imisebenzi yakho. Yabona, ndimise phambi kwakho ucango oluvulekileyo, akukho namnye unako ukuluvala; kuba uthe, unamandla amancinane, waligcina ilizwi lam, akwalikhanyela igama lam.', 'XHO22'),
(67, 3, 9, 'Yabona, ndibanika abendlu yesikhungu kaSathana, abathi bangamaYuda bona, bengengawo, bexoka; yabona, ndiya kubenza beze baqubude phambi kweenyawo zakho, bazi ukuba mna ndikuthandile.', 'XHO22'),
(67, 3, 10, 'Ngokuba uligcinile ilizwi lomonde wam, nam ndiya kukugcina, ndikusindise kwilixa lokulingwa, eliya kulifikela lonke elimiweyo, ukulinga abo bemiyo phezu kwehlabathi.', 'XHO22'),
(67, 3, 11, 'Yabona, ndiyeza kamsinya; kubambe nkqi oko unako, ukuze kungabikho namnye usithabathayo isithsaba sakho.', 'XHO22'),
(67, 3, 12, 'Lowo weyisayo ndiya kumenza intsika etempileni yoThixo wam, angabi saphuma ephuma, ndaye ndiya kulibhala phezu kwakhe igama loThixo wam, negama lomzi woThixo wam, eyiYerusalem entsha; lowo uhla ephuma emazulwini evela kuThixo wam, ndaye ndiya kulibhala kuye igama lam elitsha.', 'XHO22'),
(67, 3, 13, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla.', 'XHO22'),
(67, 3, 14, 'Usibhalele isithunywa sebandla eliseLawodike; Utsho uAmen, ingqina elithembekileyo, eliyinyaniso, ingqalo yayo indalo kaThixo;', 'XHO22'),
(67, 3, 15, 'Ndiyayazi imisebenzi yakho, ukuba akubandi, akushushu; akwaba uyabanda, ungeshushu.', 'XHO22'),
(67, 3, 16, 'Ngenxa enokuba ke udikidiki, ungabandi, ungeshushu, ndiza kukuhlanza uphume emlonyeni wam.', 'XHO22'),
(67, 3, 17, 'Ngenxa yokuba uthi, Ndisisityebi, ndizityebisile, andiswele nto; ube ungazi ukuba uludwayinge, ulusizana, ulihlwempu, uyimfama, uhamba ze;', 'XHO22'),
(67, 3, 18, 'Ndiyakucebisa, ukuba uthenge kum igolide evuthiswe emlilweni, ukuze ube nobutyebi; neengubo ezimhlophe, ukuze wambeswe, lingabonakali ihlazo lobuze bakho; nentambiso yamehlo, uthambise amehlo akho, ukuze ubone.', 'XHO22'),
(67, 3, 19, 'Mna, bonke endibathandayo ndiyabohlwaya, ndibaqeqeshe; zondelela ngoko uguquke.', 'XHO22'),
(67, 3, 20, 'Yabona, ndimi emnyango ndinkqonkqoza; ukuba uthe umntu waliva izwi lam, waluvula ucango, ndongena kuye, ndidle naye, naye adle nam.', 'XHO22'),
(67, 3, 21, 'Lowo weyisayo, ndiya kumvumela ukuba ahlale nam etroneni yam, njengokuba nam ndeyisayo, ndaza ndahlala phantsi noBawo etroneni yakhe.', 'XHO22'),
(67, 3, 22, 'Lowo unendlebe makayive into ayithethayo uMoya kuwo amabandla.', 'XHO22'),
(67, 4, 1, 'Emva koko ndabona, nango umnyango uvulekile emazulwini; ilizwi lokuqala endalivayo lalingathi lelexilongo, lithetha nam; elathi, Nyukela apha, ndokubonisa izinto ezimele ukubakho emva kwezi.', 'XHO22'),
(67, 4, 2, 'Kwangoko ndaba kuMoya; nanko kumi itrone emazulwini, kukho ohleli phezu kwetrone.', 'XHO22'),
(67, 4, 3, 'Lowo uhleliyo, ukubonakala kwakhe, efana nelitye leyaspisi nelesardiyo, waye umnyama wenze isangqa etroneni apho, ufana nelesmarado ukubonakala kwawo.', 'XHO22'),
(67, 4, 4, 'Kwaye kwenze isangqa etroneni apho iitrone ezimashumi mabini anesine; benezithsaba zegolide entloko.', 'XHO22'),
(67, 4, 5, 'Nasetroneni apho kuphuma imibane, neendudumo, nezandi; kuvutha izibane zomlilo ezisixhenxe phambi kwetrone, ezingabo ooMoya abasixhenxe bakaThixo.', 'XHO22'),
(67, 4, 6, 'Phambi kwetrone kukho ulwandle lwegilasi, lufana nomkhenkce. Esazulwini setrone apho, nasesangqeni setrone, kukho izinto eziphilileyo ezine, zizele ngamehlo ngaphambili nangasemva.', 'XHO22'),
(67, 4, 7, 'Eyokuqala into ephilileyo ifana nengonyama; eyesibini into ephilileyo ifana nethole; eyesithathu into ephilileyo inobuso obungathi bobomntu; eyesine into ephilileyo ifana nokhozi luphaphazela.', 'XHO22'),
(67, 4, 8, 'Zaye zone izinto eziphilileyo ezo, iyileyo naleyo, zinamaphiko ngamathandathu ngeenxa zonke; zizele ngamehlo ngaphakathi, zingaphumli imini nobusuku, zisithi, Ingcwele, ingcwele, ingcwele, iNkosi uThixo uSomandla, leyo yayikho, ikhoyo, izayo.', 'XHO22'),
(67, 4, 9, 'Xa la marhamncwa anika uzuko nembeko nombulelo kulowo uhleli phezu kwetrone, odla ubomi kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(67, 4, 10, 'Amadoda amakhulu amashumi mabini anamane, awa phambi kwalowo uhleli etroneni, amnqula lowo udla ubomi kuse emaphakadeni asemaphakadeni, eziphose izithsaba zawo phambi kwetrone, esithi,', 'XHO22'),
(67, 4, 11, 'Ufanele, Nkosi yam, ukwamkela uzuko, nembeko, namandla, ngokuba inguwe owadala zonke izinto, nangenxa yokuthanda kwakho zikho, zadalelwa oko.', 'XHO22'),
(67, 5, 1, 'Ndabona esandleni sokunene salowo uhleli etroneni, incwadi ebhalwe ngaphakathi nangaphandle, itywinwe ngamatywina asixhenxe.', 'XHO22'),
(67, 5, 2, 'Ndabona isithunywa sezulu esomeleleyo, sivakalisa ngezwi elikhulu, sisithi, Ngubani na ofanele ukuyivula incwadi, nokuwanqika amatywina ayo?', 'XHO22'),
(67, 5, 3, 'Akabakho namnye ezulwini, nasemhlabeni, naphantsi komhlaba, unako ukuyivula incwadi, nokuyikhangela oku.', 'XHO22'),
(67, 5, 4, 'Ndandilila kakhulu mna, ngokuba kungafunyanwanga bani ufanele ukuyivula nokuyifunda le ncwadi, nokuyikhangela nakuyo.', 'XHO22'),
(67, 5, 5, 'Yathi enye indoda enkulu kum, Musa ukulila; yabona, iNgonyama yesizwe sakwaYuda, iNgcambu kaDavide, ikweyisile ukuyivula incwadi, nokuwanqika amatywina ayo asixhenxe.', 'XHO22'),
(67, 5, 6, 'Ndabona, nantso, esazulwini setrone nesezinto eziphilileyo zone, naphakathi kwamadoda amakhulu, kumi iMvana, engathi ixheliwe, ineempondo ezisixhenxe namehlo asixhenxe, angabo ooMoya abasixhenxe boMoya, uThixo wathumela ehlabathini lonke.', 'XHO22'),
(67, 5, 7, 'Yeza yaza yathabatha incwadi esandleni sokunene salowo uhleli etroneni.', 'XHO22'),
(67, 5, 8, 'Yathi yakuyithabatha incwadi, izinto eziphilileyo zoné, namadoda amakhulu angamashumi amabini anesine, awa phambi kweMvana, iyileyo inohadi, nengqayi yegolide izele ziziqhumiso, eziyiyo imithandazo yabangcwele.', 'XHO22'),
(67, 5, 9, 'Bavuma ingoma entsha, besithi, Ufanele ukuyithabatha incwadi, nokuwavula amatywina ayo; ngokuba wena waxhelwa, wasithenga ukuba sibe ngabakaThixo ngalo igazi lakho, siphuma kuzo zonke izizwe, neelwimi, nabantu, isizwe;', 'XHO22'),
(67, 5, 10, 'Wasenza ookumkani nababingeleli kuye uThixo wethu;', 'XHO22'),
(67, 5, 11, 'Ndabona, ndeva izwi lezithunywa zezulu ezininzi, zenze isangqa etroneni, nezinto eziphilileyo, nawo amadoda amakhulu; laye inani lawo lizizigidi ngezigidi;', 'XHO22'),
(67, 5, 12, 'besithi ngezwi elikhulu, iMvana eyaxhelwayo ifanele ukwamkela amandla, nobutyebi, nobulumko, nokomelela, nembeko, nozuko, nentsikelelo.', 'XHO22'),
(67, 5, 13, 'Ndaziva zonke izidalwa ezisemazulwini, nezisemhlabeni, naphantsi komhlaba, neziselwandle, neento zonke ezikwezo zinto, zisithi, Makubekho ukubongwa, nembeko, nozuko, namandla, lowo uhleli phezu kwetrone, nakuyo iMvana, kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(67, 5, 14, 'Zathi izinto eziphilileyo zone, Amen. Aza amadoda amakhulu angamashumi amabini anesine awa phantsi, aqubuda kulowo udla ubomi kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(67, 6, 1, 'Ndabona, xa iMvana yalivulayo elinye lamatywina, ndeva enye kuzo izinto eziphilileyo zone, isithi, ngathi kungesandi sokududuma, Yiza ubone.', 'XHO22'),
(67, 6, 2, 'Ndabona, nalo ihashe elimhlophe, lowo wayehleli phezu kwalo enesaphetha; wanikwa isithsaba, waphuma eseyisa, nokuze eyise.', 'XHO22'),
(67, 6, 3, 'Xa yalivulayo elesibini itywina, ndeva eyesibini into ephilileyo isithi, Yiza ubone.', 'XHO22'),
(67, 6, 4, 'Kwaphuma elinye ihashe elibomvu; lowo wayehleli phezu kwalo wanikwa igunya lokulususa uxolo emhlabeni, nokuba babulalane, wanikwa ikrele elikhulu.', 'XHO22'),
(67, 6, 5, 'Xa yalivulayo elesithathu itywina, ndeva eyesithathu into ephilileyo isithi, Yiza ubone. Ndabona, nalo ihashe elimnyama; lowo ke wayehleli phezu kwalo wayenesikali esandleni sakhe.', 'XHO22'),
(67, 6, 6, 'Ndaza ndeva izwi esazulwini sezinto eziphilileyo zone, lisithi, Ibhekile yengqolowa iya kudla idenariyo, neebhekile ezintathu zerhasi zidle idenariyo; ioli newayini uze ungayoni.', 'XHO22'),
(67, 6, 7, 'Xa yalivulayo elesine itywina, ndeva izwi lento ephilileyo yesine lisithi, Yiza ubone.', 'XHO22'),
(67, 6, 8, 'Ndabona, nalo ihashe eliluthuthu; lowo ke wayehleli phezu kwalo enguKufa, nelabafileyo lilandela naye. Banikwa igunya lokubulala kwisahlulo sesine somhlaba ngekrele, nangendlala, nangokufa, nangawo amarhamncwa omhlaba.', 'XHO22'),
(67, 6, 9, 'Xa yalivulayo elesihlanu itywina, ndabona phantsi kwesibingelelo imiphefumlo yababuleweyo ngenxa yelizwi likaThixo, nangenxa yobungqina ababenabo.', 'XHO22'),
(67, 6, 10, 'Baye bedanduluka ngezwi elikhulu, besithi, Koda kube nini na, Nkosi engcwele, oyinyaniso, ungagwebi, ungaliphindezeleli igazi lethu kwabo bemiyo phezu komhlaba?', 'XHO22'),
(67, 6, 11, 'Banikwa bonke ngabanye iingubo ezinde ezimhlophe; kwathiwa kubo, basaya kuphumla ixesha elincinane, bade bazaliseke nabakhonza kunye nabo, nabazalwana babo, abaza kubulawa kwanjengabo.', 'XHO22'),
(67, 6, 12, 'Ndabona, xa yalivulayo elesithandathu itywina, kwasuka kwabakho unyikimo lomhlaba olukhulu; Ilanga laba mnyama, njengengubo erhwexayo yoboya, nenyanga yaba njengegazi;', 'XHO22'),
(67, 6, 13, 'Zathi iinkwenkwezi zezulu zawela emhlabeni, njengokuba umkhiwane uvuthulula amakhiwane awo avuthuzayo, wakuzanyazanyiswa ngumoya omkhulu.', 'XHO22'),
(67, 6, 14, 'Lathi izulu lemka, njengencwadi esongwayo; zathi zonke iintaba neziqithi zashenxiswa ezindaweni zazo.', 'XHO22'),
(67, 6, 15, 'Ke bona ookumkani bomhlaba, nezinhanha, nezityebi, nabaphathi-mikhosi, nabanamandla, nabakhonzi bonke, nabakhululekileyo, bazifihla emiqolombeni nasemaweni eentaba.', 'XHO22'),
(67, 6, 16, 'Bathi kwiintaba namawa, Siweleni, nisifihle ebusweni balowo uhleli phezu kwetrone, nasengqumbo yeMvana.', 'XHO22'),
(67, 6, 17, 'Ngokuba ufikile umhla omkhulu wengqumbo yayo; ngubani na ke onako ukuma?', 'XHO22'),
(67, 7, 1, 'Emva koko ndabona izithunywa zezulu zine, zimi kwiimbombo zone zomhlaba, zibambe imimoya yomine yomhlaba, ukuze kungabikho moya uvuthuza emhlabeni, naphezu kolwandle, nakuwuphi na umthi.', 'XHO22'),
(67, 7, 2, 'Ndabona esinye isithunywa sezulu, sinyuka siphuma empumalanga, sinetywina loThixo ophilileyo;', 'XHO22'),
(67, 7, 3, 'esithi, Musani ukulonakalisa ilizwe, nolwandle, nemithi, side sibatywine abakhonzi boThixo wethu emabunzini abo.', 'XHO22'),
(67, 7, 4, 'Ndaliva inani labo babetywiniwe: kwatywinwa ikhulu elinamanci mane anesine lamawaka, kuzo zonke izizwe zoonyana bakaSirayeli.', 'XHO22'),
(67, 7, 5, 'Kwisizwe sakwaYuda, kwatywinwa ishumi elinambini lamawaka. Kwisizwe sakwaRubhen, elinambini lamawaka. Kwisizwe sakwaGadi, elinambini lamawaka.', 'XHO22'),
(67, 7, 6, 'Kwisizwe sakwa-Ashere, kwatywinwa ishumi elinambini lamawaka. Kwisizwe sakwaNafetali, kwatywinwa ishumi elinambini lamawaka. Kwisizwe sakwaManase, elinambini lamawaka,', 'XHO22'),
(67, 7, 7, 'kwisizwe sakwaSimon, kwatywinwa ishumi elinambini lamawaka. Kwisizwe sakwaLevi, elinambini lamawaka. Kwisizwe sakwaIsakare, elinambini lamawaka.', 'XHO22'),
(67, 7, 8, 'Kwisizwe sakwaZebhulon kwatywinwa ishumi elinambini lamawaka. Kwisizwe sikaYosefu, elinambini lamawaka. Kwisizwe sakwaBhenjamin, kwatywinwa ishumi elinambini lamawaka.', 'XHO22'),
(67, 7, 9, 'Emva koko ndabona, naso isihlwele esikhulu, ebekungekho namnye unako ukusibala, siphuma kuzo zonke iintlanga, nezizwe, nezizwe, nabantu, neelwimi, simi phambi kwetrone naphambi kweMvana, sambethe iingubo ezinde ezimhlophe, namasundu enziwe ngamasundu, izandla zabo;', 'XHO22'),
(67, 7, 10, 'Badanduluka ngezwi elikhulu, besithi, Usindiso kuye uThixo wethu, lowo uhleli phezu kwetrone, nakuyo iMvana.', 'XHO22'),
(67, 7, 11, 'Zaye zonke izithunywa zezulu zimi, zenze isangqa etroneni, nakuwo amadoda amakhulu, nakuzo izinto eziphilileyo zone, zawa ngobuso phambi kwetrone, zaqubuda kuye uThixo;', 'XHO22'),
(67, 7, 12, 'zisithi, Amen, Ukubongwa, nozuko, nobulumko, nombulelo, nembeko, namandla, nokomelela, Makube kuye uThixo wethu, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(67, 7, 13, 'Yaphendula enye indoda enkulu, isithi kum, Aba bathiwe wambu ngeengubo ezimhlophe bangoobani na? avela phi na?', 'XHO22'),
(67, 7, 14, 'Ndathi kuye, Nkosi, nguwe owaziyo. Yathi kum, Aba ngabaphuma embandezelweni enkulu, bazihlamba iingubo zabo ezinde, bazenza mhlophe egazini leMvana.', 'XHO22'),
(67, 7, 15, 'Ngenxa yoko baphambi kwetrone kaThixo, bemkhonza imini nobusuku etempileni yakhe; nalowo uhleli phezu kwetrone uya kuba phakathi kwabo.', 'XHO22'),
(67, 7, 16, 'Abayi kuba salamba, abasayi kunxanwa; nelanga aliyi kubakhanyisela, nabuphi na ubushushu.', 'XHO22'),
(67, 7, 17, 'Ngokuba iMvana esesazulwini setrone iya kubalusa, ibakhokelele emithonjeni ephilileyo yamanzi; waye uThixo eya kuzisula zonke iinyembezi emehlweni abo.', 'XHO22'),
(67, 8, 1, 'Xa yalivulayo elesixhenxe itywina, kwabakho ukuzola ezulwini, kwangathi sisiqingatha seyure.', 'XHO22'),
(67, 8, 2, 'Ndazibona izithunywa zezulu ezisixhenxe, ezimayo phambi koThixo; zaza zanikwa izigodlo ezisixhenxe.', 'XHO22'),
(67, 8, 3, 'Seza esinye isithunywa, sema ngasesibingelelweni, sinesiqhumiselo segolide; wanikwa iziqhumiso ezininzi, ukuze azinikele, kunye nemithandazo yabo bonke abangcwele, phezu kwesibingelelo segolide, esiphambi kwetrone.', 'XHO22'),
(67, 8, 4, 'Wenyuka ke umsi wesiqhumiso, kunye nemithandazo yabangcwele, uphuma esandleni sesithunywa eso phambi koThixo.', 'XHO22'),
(67, 8, 5, 'Sathi isithunywa sathabatha isiqhumiselo, sasizalisa ngomlilo wesibingelelo, sawuphosa emhlabeni; kwabakho izandi, neendudumo, nemibane, nenyikima.', 'XHO22'),
(67, 8, 6, 'Zaza izithunywa ezisixhenxe ezinamaxilongo osixhenxe zalungiselela ukuvuthela.', 'XHO22'),
(67, 8, 7, 'Esokuqala isithunywa savuthela ixilongo, kwabakho isichotho nomlilo, ziphithikezwe negazi, zaphoswa emhlabeni, saza isahlulo sesithathu semithi satsha, nayo yonke ingca eluhlaza yatsha yatsha.', 'XHO22'),
(67, 8, 8, 'Sathi esesibini isithunywa savuthela ixilongo, kwanga kuphoswa intaba enkulu, isitsha ngumlilo elwandle, saza isahlulo sesithathu solwandle saba ligazi;', 'XHO22'),
(67, 8, 9, 'Safa isahlulo sesithathu sezinto eziphilileyo eziselwandle; saza isahlulo sesithathu semikhombe satshatyalaliswa.', 'XHO22'),
(67, 8, 10, 'Sathi esesithathu isithunywa savuthela ixilongo, kwawa inkwenkwezi enkulu ezulwini, ivutha ngathi sisibane, yawa phezu kwesahlulo sesithathu semilambo naphezu kwemithombo yamanzi.', 'XHO22'),
(67, 8, 11, 'Igama lenkwenkwezi leyo kuthiwa nguMhlonyane; saza isahlulo sesithathu samanzi saba ngumhlonyane; into eninzi yabantu yafa ngawo amanzi, ngokuba aba krakra.', 'XHO22'),
(67, 8, 12, 'Sathi esesine isithunywa savuthela ixilongo, saza sabethwa isahlulo sesithathu selanga, nesahlulo sesithathu senyanga, nesahlulo sesithathu seenkwenkwezi; njengokuba isahlulo sesithathu sazo saba mnyama, yathi isahlulo sesithathu sayo imini ayikhanyi, kwangokunjalo nobusuku.', 'XHO22'),
(67, 8, 13, 'Ndabona, ndasiva isithunywa sezulu, siphaphazela esazulwini sezulu, sisithi ngezwi elikhulu, Yeha, yeha, yeha, abo bawumiyo umhlaba, ngenxa yezinye izandi zamaxilongo ezithunywa zezulu ezithathu, ezisahleliyo, ukuvakala!', 'XHO22'),
(67, 9, 1, 'Sathi esesihlanu isithunywa savuthela ixilongo. Ndabona inkwenkwezi iphuma ezulwini, isiwa emhlabeni, yaza yanikwa isitshixo senzonzobila.', 'XHO22'),
(67, 9, 2, 'Walivula enzonzobileni; kwenyuka umsi emhadini, njengomsi weziko leziko elikhulu; lesuka laba mnyama ilanga nezulu ngumsi womhadi.', 'XHO22'),
(67, 9, 3, 'Emsini apho kwaphuma iinkumbi, zeza emhlabeni, zanikwa igunya, njengokuba benegunya oonomadudwane bomhlaba.', 'XHO22'),
(67, 9, 4, 'Waziwisela umthetho wokuba zingoni utyani bomhlaba, nanye into eluhlaza, nanye imithi; kodwa ngamadoda kuphela, angenalo itywina likaThixo emabunzini abo.', 'XHO22'),
(67, 9, 5, 'Zanikwa igunya lokungababulali, kodwa bathuthunjelwe iinyanga ezintlanu; intuthumbo yabo yayinjengentuthumbo yoonomadudwane, xa athe wamhlaba umntu.', 'XHO22'),
(67, 9, 6, 'Ngaloo mihla abantu baya kukufuna ukufa, bangakufumani; baya kukunqwenela ukufa, kusuke ukufa kusabe kubo.', 'XHO22'),
(67, 9, 7, 'Imo yeenkumbi ezo, ibifana namahashe, elungiselwe ukulwa; entloko izizithsaba ezifana negolide, nobuso bazo bunjengobuso babantu.', 'XHO22'),
(67, 9, 8, 'Zaye zineenwele ezingathi ziinwele zabafazi, namazinyo azo engathi ngawengonyama.', 'XHO22'),
(67, 9, 9, 'Zaye zinezigcina-zifuba ezingathi zezentsimbi; isandi samaphiko azo sasingathi sisandi seenqwelo zamahashe amaninzi, ebalekela emfazweni.', 'XHO22'),
(67, 9, 10, 'Zaye zinemisila efana neyoonomadudwane, ineemvila emisileni yazo, yaye igunya lazo lilelokwenzakalisa abantu iinyanga ezintlanu.', 'XHO22'),
(67, 9, 11, 'Zaye zinokumkani phezu kwazo, usisithunywa senzonzobila, ogama ngesiHebhere linguAbhadon, kodwa ngesiGrike unegama elinguApoliyon.', 'XHO22'),
(67, 9, 12, 'Yeha mnye udlule; yabona, kuza ooyeha ababini emva koko.', 'XHO22'),
(67, 9, 13, 'Sathi esesithandathu isithunywa savuthela ixilongo, ndeva izwi liphuma ezimpondweni zone zesibingelelo segolide, esiphambi koThixo,', 'XHO22'),
(67, 9, 14, 'lisithi kwisithunywa sesithandathu, esinexilongo, Zikhulule izithunywa zezulu ezine, ezibotshwe emlanjeni omkhulu ongumEfrati.', 'XHO22'),
(67, 9, 15, 'Zaza zakhululwa izithunywa zozine, ezazilungiselwe ilixa, nemini, nenyanga, nomnyaka, ukuba zibulale isahlulo sesithathu sabantu.', 'XHO22'),
(67, 9, 16, 'Inani lempi yabamahashe lalingamakhulu amabini amawaka; ndaliva ke inani labo.', 'XHO22'),
(67, 9, 17, 'Kwaye ngokunjalo ndawabona embonweni amahashe, nabahleli phezu kwawo, benezigcina-zifuba ezivuthayo, ezibomvu, nezesalfure, neentloko zamahashe zazingathi ziintloko zeengonyama; emlonyeni wawo kuphuma umlilo, nomsi, nesulfure.', 'XHO22'),
(67, 9, 18, 'Ngezo zinto zontathu sabulawa isahlulo sesithathu sabantu ngomlilo, nangomsi, nangesulfure, ephuma emlonyeni wawo.', 'XHO22'),
(67, 9, 19, 'Kuba amandla awo asemlonyeni wawo nasemisileni yawo; kuba imisila yawo ifana neenyoka, ineentloko; ngayo ayonakalisa.', 'XHO22'),
(67, 9, 20, 'Nabanye abantu ababengabulawanga ngezi zibetho abaguqukanga kwimisebenzi yezandla zabo, ukuba bangazinquli iidemon, nezithixo zegolide, nezesilivere, nezobhedu, nezelitye, nezemithi; akaboni, angeva, angahambi;', 'XHO22'),
(67, 9, 21, 'abaguqukanga ekubulaleni kwabo, nasekukhafuleni kwabo, nasembulweni wabo, nakubusela bawo.', 'XHO22'),
(67, 10, 1, 'Ndabona esinye isithunywa sezulu esinamandla, sisihla ezulwini, sithiwe wambu ngelifu; kukho umnyama entloko, ubuso baso bungathi lilanga, iinyawo zaso zingathi ziintsika zomlilo.', 'XHO22'),
(67, 10, 2, 'Yayiphethe ngesandla incwadi encinane evulekileyo, yabeka unyawo lwayo lokunene phezu kolwandle, nolwasekhohlo olwasemhlabeni.', 'XHO22'),
(67, 10, 3, 'Sadanduluka ngezwi elikhulu, ngathi yingonyama igquma; sathi sakudanduluka, iindudumo ezisixhenxe zazivakalisa izandi zazo.', 'XHO22'),
(67, 10, 4, 'Xa iindudumo ezisixhenxe zazivakalisayo izandi zazo, ndandiza kubhala;', 'XHO22'),
(67, 10, 5, 'Saza isithunywa eso ndasibonayo simi phezu kolwandle naphezu komhlaba, sasiphakamisela ezulwini isandla saso,', 'XHO22'),
(67, 10, 6, 'wafunga lowo udla ubomi kuse emaphakadeni asemaphakadeni, owadala izulu nezinto ezikulo, nomhlaba nezinto ezikuwo, nolwandle, nezinto ezikuwo, ukuba akuyi kubakho xesha limiyo, ixesha elide:', 'XHO22'),
(67, 10, 7, 'Kodwa ngemihla yezwi lesesixhenxe isithunywa sezulu, xa sukuba siza kuvuthela ixilongo, iya kuzaliseka imfihlelo kaThixo, njengoko wawazisayo abakhonzi bakhe abaprofeti.', 'XHO22'),
(67, 10, 8, 'Lathi ilizwi endalivayo liphuma ezulwini, labuya lathetha nam, lisithi, Hamba uye uyithabathe incwadana evulekileyo, esesandleni sesithunywa esimi phezu kolwandle naphezu komhlaba.', 'XHO22'),
(67, 10, 9, 'Ndesuka ke ndaya kwisithunywa eso, ndathi kuso, Ndinike incwadana leyo. Wathi kum, Yithabathe, uyidle uyigqibe; yenze krakra isisu sakho, kodwa emlonyeni wakho yoba mnandi njengobusi.', 'XHO22'),
(67, 10, 10, 'Ndayithabatha ke incwadana esesandleni sesithunywa eso, ndayidla ndayigqiba; yaye emlonyeni wam inencasa enjengeyobusi; yaye xa ndayidlayo, saba krakra isisu sam.', 'XHO22'),
(67, 10, 11, 'Sathi kum, Umele ukuba ubuye uprofete ngabantu, neentlanga, neelwimi, nookumkani, abaninzi.', 'XHO22'),
(67, 11, 1, 'Ndaza ndanikwa ingcongolo, ifana nentonga; saye isithunywa eso simi, sisithi, Suka uyilinganise itempile kaThixo, nesibingelelo, nabo banqulayo bekuyo.', 'XHO22'),
(67, 11, 2, 'Kodwa intendelezo engaphandle kwetempile yishiye ngaphandle, ungayilinganisi; ngokuba linikelwe iintlanga; umzi ongcwele ziya kuwunyathela ngeenyawo iinyanga ezimashumi mane anesibini.', 'XHO22'),
(67, 11, 3, 'Ndiya kuwanika amangqina am amabini, aprofete imihla eliwaka elinamakhulu mabini anamanci mathandathu, ethiwe wambu ngeengubo ezirhwexayo.', 'XHO22'),
(67, 11, 4, 'Lawo yile minquma mibini, nezi ziphatho zezibane zibini, zimiyo phambi koThixo womhlaba.', 'XHO22'),
(67, 11, 5, 'Ukuba kukho bani unga angawonakalisa, kophuma umlilo emlonyeni wawo, uziqwenge iintshaba zawo;', 'XHO22'),
(67, 11, 6, 'Lawo anegunya lokuvala izulu ukuze kungani ngeentsuku zokuprofeta kwawo, yaye anegunya phezu kwamanzi lokuwajika abe ligazi nelokuwubetha umhlaba ngazo zonke izibetho ngokufuthi kangangoko enqwenela.', 'XHO22'),
(67, 11, 7, 'Xa athe abufeza ubungqina bawo, irhamncwa elinyuka liphuma enzonzobileni liya kwenza imfazwe nawo, liweyise, liwabulale.', 'XHO22'),
(67, 11, 8, 'Izidumbu zawo ziya kuqungquluza esitratweni somzi omkhulu, ekuthiwa ngokomoya yiSodom neYiputa, apho neNkosi yethu yabethelelwa khona emnqamlezweni.', 'XHO22'),
(67, 11, 9, 'Ababantu, nezizwe, neelwimi, neentlanga, baya kuzibona izidumbu zawo imihla emithathu enesiqingatha somhla, bangavumi ukuba izidumbu zawo zibekwe emangcwabeni.', 'XHO22'),
(67, 11, 10, 'Nabo bemiyo phezu komhlaba baya kuba nemihlali ngabo, babe nemihlali, bathumelelane izipho; ngokuba abo baprofeti babini babathuthumbisa abo bemiyo phezu komhlaba.', 'XHO22'),
(67, 11, 11, 'Emva kweentsuku ezintathu ezinesiqingatha uMoya wobomi ophuma kuThixo wangena kuwo, ema ngeenyawo zawo; baza bawelwa luloyiko olukhulu abo bawabonayo.', 'XHO22'),
(67, 11, 12, 'Eva izwi elikhulu liphuma ezulwini, lisithi kubo, Nyukelani apha. Benyuka baya ezulwini besefini; zawabona iintshaba zawo.', 'XHO22'),
(67, 11, 13, 'Kwangelo lixa kwabakho unyikimo olukhulu lomhlaba, isahlulo seshumi somzi sawa, kwabulawa kolo lunyikimo abantu abangamawaka asixhenxe; abaseleyo bangenelwa kukoyika, bamzukisa uThixo wezulu.', 'XHO22'),
(67, 11, 14, 'Uyeha wesibini udlule; uyabona, uyeha wesithathu uyeza kamsinya.', 'XHO22'),
(67, 11, 15, 'Sathi esesixhenxe isithunywa savuthela ixilongo; kwabakho amazwi amakhulu emazulwini, esithi, Izikumkani zehlabathi zithe zaba zezeNkosi yethu, nezikaKristu wayo; uya kulawula ngonaphakade kanaphakade.', 'XHO22'),
(67, 11, 16, 'Athi amadoda amakhulu amashumi mabini anamane, abehleli phambi koThixo ezitroneni zawo, awa ngobuso, aqubuda kuye uThixo,', 'XHO22'),
(67, 11, 17, 'Sithi, Siyabulela kuwe, Nkosi, Thixo Somandla, wena ukhoyo, wawukho, uzayo; ngokuba uwathabathile amandla akho amakhulu, waba ngukumkani.', 'XHO22'),
(67, 11, 18, 'Zaqumba iintlanga, lafika ingqumbo yakho, nexesha labafileyo lokuba bagwetywe, nokuba ubanike umvuzo kubakhonzi bakho abaprofeti, nabangcwele, nabo baloyikayo igama lakho, abancinane, kwaye mkhulu; kwaye uya kubatshabalalisa abo bawonakalisayo umhlaba.', 'XHO22'),
(67, 11, 19, 'Yavulwa itempile kaThixo emazulwini, yabonakala ityeya yomnqophiso wakhe etempileni yakhe; kwabakho imibane, nezandi, neendudumo, nonyikimo lomhlaba, nesichotho esikhulu.', 'XHO22'),
(67, 12, 1, 'Kwabonakala umqondiso omkhulu ezulwini: inkazana ethiwe wambu ngelanga, inyanga iphantsi kweenyawo zakhe, entloko kuye isisithsaba seenkwenkwezi ezilishumi elinambini.', 'XHO22'),
(67, 12, 2, 'Uthe ke emithi, walila, eneneni, egcuma ekuzaleni.', 'XHO22'),
(67, 12, 3, 'Kwabonakala omnye umqondiso ezulwini: nantso, inamba enkulu ebomvu, ineentloko ezisixhenxe neempondo ezilishumi, kwiintloko zayo izizithsaba ezisixhenxe.', 'XHO22'),
(67, 12, 4, 'Umsila wayo wawurhola isahlulo sesithathu seenkwenkwezi, waziphosa emhlabeni. Yathi inamba leyo yema phambi komfazi owayeza kuzala, ukuze, xa athe wazala, imdle imgqibe umntwana wakhe.', 'XHO22'),
(67, 12, 5, 'Wazala umntwana oyinkwenkwe, oza kuzalusa zonke iintlanga ngentonga yentsimbi, umntwana wakhe lowo wathiwa xwi, wasiwa kuye uThixo, nakuyo itrone yakhe.', 'XHO22'),
(67, 12, 6, 'Umfazi wasabela entlango, apho ikhona indawo ayilungiselwe nguThixo, ukuze bamnqake khona iintsuku eziliwaka elinamakhulu mabini anamanci mathandathu.', 'XHO22'),
(67, 12, 7, 'Kwaza kwabakho imfazwe emazulwini, uMikayeli nezithunywa zakhe besilwa nenamba; yasilwa inamba nezithunywa zayo;', 'XHO22'),
(67, 12, 8, 'Akazange abeyise; azaba safunyanwa indawo yazo emazulwini.', 'XHO22'),
(67, 12, 9, 'Yaphoswa phantsi inamba enkulu, inyoka yakudala, leyo kuthiwa nguMtyholi, uSathana ke, lowo ulilahlekisayo elimiweyo liphela; yaphoswa phantsi emhlabeni, nezithunywa zayo zaphoswa phantsi kunye nayo.', 'XHO22'),
(67, 12, 10, 'Ndeva izwi elikhulu emazulwini lisithi, Ngoku luthe usindiso, namandla, nobukumkani bukaThixo wethu, namandla kaKristu wakhe; ngokuba ukhahlelwe phantsi ummangaleli wabazalwana bethu, lowo ubamangalelayo phambi koThixo wethu imini nobusuku, busuku.', 'XHO22'),
(67, 12, 11, 'Bameyisile bona ngenxa yegazi leMvana, nangenxa yelizwi lobungqina babo; ababuthandanga ubomi babo kwada kwasekufeni.', 'XHO22'),
(67, 12, 12, 'Ngoko ke yibani nemihlali, nina mazulu, nani ninomnquba kuwo. Yeha, abo bawumiyo umhlaba nolwandle! ngokuba uhlile weza kuni uMtyholi, enomsindo omkhulu, esazi nje ukuba ixesha analo lincinane.', 'XHO22'),
(67, 12, 13, 'Yathi inamba, yakubona ukuba iphoswe emhlabeni, yamtshutshisa umfazi ozele umntwana oyinkwenkwe.', 'XHO22'),
(67, 12, 14, 'Umfazi wanikwa amaphiko amabini okhozi olukhulu, ukuze aphaphazele aye entlango, endaweni yakhe, apho anqakwa khona ixesha, namaxesha, nesiqingatha sexesha, kude nobuso benyoka.', 'XHO22'),
(67, 12, 15, 'Inyoka yakhupha emlonyeni wayo amanzi anjengomlambo emva komfazi, ukuze imkhukulise nomlambo.', 'XHO22'),
(67, 12, 16, 'Waza umhlaba wamncedisa umfazi, waza umhlaba wawuvula umlomo wawo, wawuginya umlambo eyawukhuphayo inamba emlonyeni wayo.', 'XHO22'),
(67, 12, 17, 'Yamqumbela umfazi inamba, yemka yaya kulwa nabaseleyo bembewu yakhe, abo bayigcinayo imithetho kaThixo, abanobungqina bukaYesu Kristu.', 'XHO22'),
(67, 13, 1, 'Ndaza ndema phezu kwentlabathi yolwandle. Ndabona kunyuka, kuphuma elwandle, irhamncwa, lineentloko ezisixhenxe neempondo ezilishumi, ezimpondweni zalo izizithsaba ezilishumi, kwiintloko zalo iligama lonyeliso.', 'XHO22'),
(67, 13, 2, 'Laye irhamncwa elo ndalibonayo lifana nengwe, iinyawo zalo zinjengezebhere, umlomo walo unjengomlomo wengonyama. Inamba yalinika amandla alo, netrone yalo, negunya elikhulu.', 'XHO22'),
(67, 13, 3, 'Ndabona enye yazo iintloko zalo, ingathi ixatyelwe yafa; lathi inxeba lalo elibanga ukufa laphila. Lamangaliswa ihlabathi liphela, lilandela irhamncwa.', 'XHO22'),
(67, 13, 4, 'Bayinqula inamba eyalinika igunya irhamncwa, balinqula irhamncwa, besithi, Ngubani na ofana nerhamncwa elo? ngubani na onako ukulwa nayo?', 'XHO22'),
(67, 13, 5, 'Sanikwa umlomo othetha izinto ezinkulu, nezinyeliso; lanikwa igunya lokuqhubeka iinyanga ezimashumi mane anesibini.', 'XHO22'),
(67, 13, 6, 'Lawuvulela umlomo walo ekumnyeliseni uThixo, ukuba lilinyelise igama lakhe, nomnquba wakhe, nabo bahleli emazulwini.', 'XHO22'),
(67, 13, 7, 'Lanikwa igunya lokulwa nabangcwele, libeyise; lanikwa igunya kuzo zonke izizwe, neelwimi, neentlanga.', 'XHO22'),
(67, 13, 8, 'Baya kulinqula bonke abo bemiyo phezu komhlaba, abamagama angabhalwanga encwadini yobomi yayo iMvana, exheliweyo kususela ekusekweni kwehlabathi.', 'XHO22'),
(67, 13, 9, 'Ukuba ubani unendlebe, makeve.', 'XHO22'),
(67, 13, 10, 'Ukuba ubani ukhokelela ekuthinjweni, uya kusiwa ekuthinjweni; lowo ubulala ngekrele, umelwe kukubulawa naye ngekrele. Nalu unyamezelo nokholo lwabangcwele.', 'XHO22'),
(67, 13, 11, 'Ndabona elinye irhamncwa linyuka liphuma emhlabeni; laye lineempondo ezimbini zifana nezemvana, laye lithetha njengenamba.', 'XHO22'),
(67, 13, 12, 'Lenza lonke igunya lelokuqala irhamncwa, elalingaphambi kwalo; lenza umhlaba nabo bawumiyo ukuba balinqule irhamncwa lokuqala, elaphiliswayo inxeba lalo elibanga ukufa.', 'XHO22'),
(67, 13, 13, 'Senza imiqondiso emikhulu, ngokokude senze umlilo uhle ezulwini, uze emhlabeni phambi kwabantu;', 'XHO22'),
(67, 13, 14, 'Lalahlekisa abo bemiyo phezu komhlaba, ngemiqondiso elayinikwayo ukuba liyenze emehlweni erhamncwa; lisithi kwabo bemiyo phezu komhlaba, ukuba balenze umfanekiselo irhamncwa, elibe linenxeba lalo ikrele, laphila.', 'XHO22'),
(67, 13, 15, 'Lanikwa igunya lokuwunika umfanekiselo werhamncwa umoya, ukuze umfanekiselo werhamncwa elo uthethe nokuthetha, wenze ukuba babulawe abasukuba bengaqubudi kuwo umfanekiselo werhamncwa.', 'XHO22'),
(67, 13, 16, 'Lenza ukuba bonke, abancinane nabakhulu, abazizityebi nabangamahlwempu, abakhululekileyo nabangamakhoboka, ukuba baphawulwe esandleni sabo sokunene, nokuba kusebunzi;', 'XHO22'),
(67, 13, 17, 'Kwaye ukuze kungabikho bani unako ukuthenga nokuthengisa, ingenguye lowo unophawu, nokuba ligama lerhamncwa, nokuba linani legama lalo.', 'XHO22'),
(67, 13, 18, 'Nabu ke ubulumko. Lowo unengqiqo makalibale inani lerhamncwa; kuba linani lomntu; inani lakhe ngamakhulu amathandathu anamanci mathandathu anesithandathu.', 'XHO22'),
(67, 14, 1, 'Ndabona, nantso iMvana imi phezu kwentaba yeZiyon, kumi nayo ikhulu elinamanci mane anesine lamawaka, benalo igama loYise libhaliwe emabunzini abo.', 'XHO22'),
(67, 14, 2, 'Ndeva izwi liphuma ezulwini, ngathi sisandi samanzi amaninzi, ngathi sisandi sendudumo enkulu;', 'XHO22'),
(67, 14, 3, 'Baye bevuma ingoma engathi intsha, phambi kwetrone, naphambi kwezinto eziphilileyo zone, namadoda amakhulu; kwaye kungekho namnye unako ukuyifunda ingoma leyo, lingelilo ikhulu elinamanci mane anesine lamawaka, ababethengiwe emhlabeni.', 'XHO22'),
(67, 14, 4, 'Aba ngabo bangadyobhekanga ngabafazi; ngokuba banyulu. Aba ngabo bayilandelayo iMvana apho isukuba isiya khona. Aba bathengwa bephuma ebantwini, baziintlahlela kuye uThixo nakuyo iMvana.', 'XHO22'),
(67, 14, 5, 'Emlonyeni wabo akufunyanwanga nkohliso, kuba bengenakubekwa bala phambi kwetrone kaThixo.', 'XHO22'),
(67, 14, 6, 'Ndabona esinye isithunywa sezulu, siphaphazela esazulwini samazulu, sineendaba ezilungileyo ezingunaphakade, ukuba sizishumayele kwabo bemiyo phezu komhlaba, nakwiintlanga zonke, nezizwe, neelwimi, nabantu;', 'XHO22'),
(67, 14, 7, 'sisithi ngezwi elikhulu, Moyikeni uThixo, nimzukise; ngokuba lifikile ilixa lokugweba kwakhe; nimnqule lowo wenza izulu, nomhlaba, nolwandle, nemithombo yamanzi.', 'XHO22'),
(67, 14, 8, 'Kwaza kwalandela esinye isithunywa sezulu, sisithi, Iwile, iwile iBhabheli, loo mzi mkhulu, ngokuba iseze zonke iintlanga ngewayini yomsindo wobuhenyu bayo.', 'XHO22'),
(67, 14, 9, 'Saza esesithathu isithunywa sazilandela, sithetha ngezwi elikhulu, sisithi, Ukuba ubani ulinqula irhamncwa nomfanekiselo walo, walwamkela uphawu lwakhe ebunzini lakhe, nokuba kusesandleni sakhe;', 'XHO22'),
(67, 14, 10, 'Lowo uya kusela kwiwayini yomsindo kaThixo, egalelwe kwindebe yokucaphuka, ingaxutywanga; uya kuthuthunjiswa ngomlilo nesulfure, phambi kwezithunywa ezingcwele, naphambi kweMvana.', 'XHO22'),
(67, 14, 11, 'Unyuka umsi wokuthuthunjelwa kwawo, use emaphakadeni asemaphakadeni; abanakuphumla imini nobusuku, abo banqula irhamncwa nomfanekiselo walo, nabani na owamkelayo uphawu lwegama lalo.', 'XHO22'),
(67, 14, 12, 'Nalu unyamezelo lwabangcwele, naba abayigcinayo imithetho kaThixo nokukholwa kuYesu.', 'XHO22'),
(67, 14, 13, 'Ndaza ndeva ilizwi liphuma ezulwini lisithi kum: “Bhala uthi: ‘Banoyolo abo bafa bezinikele eNkosini, nemisebenzi yabo iyabalandela.', 'XHO22'),
(67, 14, 14, 'Ndabona, nalo ilifu elimhlophe, efini apho kuhleli ofana noNyana woMntu, enesithsaba segolide entloko, ephethe irhengqa elibukhali esandleni sakhe.', 'XHO22'),
(67, 14, 15, 'Kwaphuma esinye isithunywa sezulu etempileni, sidanduluka ngezwi elikhulu kulowo uhleli efini, sisithi, Lifake irhengqa lakho, uvune; ngokuba isivuno sehlabathi sivuthiwe.', 'XHO22'),
(67, 14, 16, 'Lowo uhleli efini waliphosa irhengqa lakhe emhlabeni; wavunwa ke umhlaba.', 'XHO22'),
(67, 14, 17, 'Kwaphuma nesinye isithunywa sezulu, siphuma etempileni esemazulwini, naso sinerhengqa elibukhali.', 'XHO22'),
(67, 14, 18, 'Kwaphuma esinye isithunywa sezulu esibingelelweni, sinegunya phezu komlilo; sadanduluka ngezwi elikhulu kuleyo yayinerhengqa elibukhali, sisithi, Lifake irhengqa lakho elibukhali, uzihlanganise izihloko zomdiliya womhlaba; ngokuba iidiliya zayo zivuthiwe;', 'XHO22'),
(67, 14, 19, 'Saza isithunywa saliphosa irhengqa laso emhlabeni, sawubutha umdiliya womhlaba, sawuphosa kwisixovulelo esikhulu sewayini yomsindo kaThixo.', 'XHO22'),
(67, 14, 20, 'Isixovulelo eso saxovulwa ngaphandle komzi, kwaphuma igazi kwisixovulelo eso, lada lada lafika emikhaleni yamahashe, umgama wezitadiya eziliwaka elinamakhulu amathandathu.', 'XHO22'),
(67, 15, 1, 'Ndabona ezulwini omnye umqondiso omkhulu, ongummangaliso; izithunywa zezulu ezisixhenxe zinazo izibetho ezisixhenxe zokugqibela; ngokuba izalisekile ingqumbo kaThixo ngazo.', 'XHO22'),
(67, 15, 2, 'Ndabona ngathi lulwandle lwegilasi, luphithikezene nomlilo; bathi abo baloyisayo irhamncwa, nomfanekiselo walo, naphezu kophawu lwalo, naphezu kwenani legama lalo, bemi phezu kolwandle lwegilasi; enemirhubhe kaThixo.', 'XHO22'),
(67, 15, 3, 'Bayivuma ingoma kaMoses, umkhonzi kaThixo, nengoma yeMvana, besithi: Mikhulu, ingummangaliso imisebenzi yakho, Nkosi, Thixo Somandla; zinobulungisa, ziyinyaniso iindlela zakho, Kumkani wenceba.', 'XHO22'),
(67, 15, 4, 'Ngubani na ongekhe akoyike, Yehova, Angalizukisi igama lakho? Ngokuba nguwe wedwa ongcwele; ngokuba zonke iintlanga ziya kuza ziqubude phambi kwakho; ngokuba ibonakalisiwe imigwebo yakho.', 'XHO22'),
(67, 15, 5, 'Ndaza emva koko ndabona, nantso ivuliwe itempile yomnquba wobungqina emazulwini.', 'XHO22'),
(67, 15, 6, 'Zaphuma ke etempileni izithunywa zezulu ezisixhenxe, zinezibetho ezisixhenxe, zambethe ilinen emhlophe eqaqambileyo, ziyibhinqile ezifubeni imibhinqo yegolide.', 'XHO22'),
(67, 15, 7, 'Yathi enye kuzo izinto eziphilileyo zone, yazinika izithunywa ezisixhenxe iingqayi zegolide ezisixhenxe, zizele ngumsindo kaThixo, odla ubomi kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(67, 15, 8, 'Yazala itempile ngumsi ophuma ebuqaqawulini bukaThixo, nasemandleni akhe; akwabakho namnye unako ukungena etempileni, zada zazaliseka izibetho ezisixhenxe zezithunywa zosixhenxe.', 'XHO22'),
(67, 16, 1, 'Ndeva izwi elikhulu liphuma etempileni, lisithi kwizithunywa zosixhenxe, Hambani niye kuziphalaza iingqayi zomsindo kaThixo phezu komhlaba.', 'XHO22'),
(67, 16, 2, 'Sahamba ke esokuqala, sayiphalaza ingqayi yaso emhlabeni; kwabakho isibetho esibi esibuhlungu ebantwini, abanophawu lwerhamncwa, abo baqubudayo kuwo umfanekiselo walo.', 'XHO22'),
(67, 16, 3, 'Esesibini isithunywa sayiphalaza ingqayi yaso elwandle; lwaba ligazi elinjengelofileyo, yaza yonke imiphefumlo ephilileyo yafa elwandle.', 'XHO22'),
(67, 16, 4, 'Saza esesithathu isithunywa sayiphalaza ingqayi yaso emilanjeni nasemithonjeni yamanzi; zaba ligazi.', 'XHO22'),
(67, 16, 5, 'Ndasiva isithunywa samanzi, sisithi: “Ulilungisa wena ukhoyo, wawukho, uyingcwele, ngokuba ugwebile wenjenje.', 'XHO22'),
(67, 16, 6, 'Ngokuba baliphalazile igazi labangcwele nelabaprofeti, wabanika igazi ukuba balisele; ngokuba befanelekile.', 'XHO22'),
(67, 16, 7, 'Ndaza ndeva esinye siphuma esibingelelweni sisithi: “Ewe, Nkosi, Thixo Somandla, ziyinyaniso, zinobulungisa izigwebo zakho.', 'XHO22'),
(67, 16, 8, 'Saza esesine isithunywa sayiphalaza ingqayi yaso elangeni; laza lanikwa igunya lokutshisa abantu ngomlilo.', 'XHO22'),
(67, 16, 9, 'Batsha abantu bubushushu obukhulu, balinyelisa igama likaThixo, onalo igunya kwezo zibetho; abaguquka bamzukise.', 'XHO22'),
(67, 16, 10, 'Saza esesihlanu isithunywa sayiphalaza ingqayi yaso etroneni yerhamncwa; baba mnyama ubukumkani bakhe; bamana beziluma iilwimi ngenxa yeentlungu;', 'XHO22'),
(67, 16, 11, 'bamnyelisa uThixo wamazulu ngenxa yeentlungu zabo, nangenxa yezilonda zabo; abaguquka emisebenzini yabo.', 'XHO22'),
(67, 16, 12, 'Esesithandathu isithunywa sayiphalaza ingqayi yaso emlanjeni omkhulu ongumEfrati; atsha amanzi awo, ukuze ilungiswe indlela yookumkani abavela empumalanga.', 'XHO22'),
(67, 16, 13, 'Ndabona kuphuma emlonyeni wenamba, nasemlonyeni werhamncwa, nasemlonyeni womprofeti wobuxoki, oomoya abathathu abangcolileyo, befana namasele.', 'XHO22'),
(67, 16, 14, 'Kuba bangoomoya beedemon, besenza imiqondiso; abaphuma baye kubo ookumkani bomhlaba, nabelimiweyo liphela, ukubahlanganisela emfazweni yaloo mini inkulu kaThixo, uSomandla.', 'XHO22'),
(67, 16, 15, 'Uyabona, ndiza njengesela. Unoyolo lowo uphaphileyo, uzigcinayo iingubo zakhe, ukuze angahambi ze, balibone ihlazo lakhe.', 'XHO22'),
(67, 16, 16, 'Wabahlanganisela endaweni ekuthiwa ngesiHebhere yiArmagedon.', 'XHO22'),
(67, 16, 17, 'Saza esesixhenxe isithunywa sayiphalaza ingqayi yaso esibhakabhakeni; kwaphuma izwi elikhulu etempileni yezulu, liphuma etroneni, lisithi, Kwenzekile;', 'XHO22'),
(67, 16, 18, 'Kwabakho izandi, neendudumo, nemibane; kwabakho nonyikimo lomhlaba olukhulu, ekungazanga kubekho lunjalo unyikimo, kuseloko kwabakhoyo abantu emhlabeni.', 'XHO22'),
(67, 16, 19, 'Umzi lowo mkhulu waba zahlulo zithathu, yathi imizi yeentlanga yawa; iBhabheli enkulu yaba sisikhumbuzo phambi koThixo, ukuba inikwe indebe yewayini yomsindo wengqumbo yakhe.', 'XHO22'),
(67, 16, 20, 'Zasaba, zonke iziqithi zabaleka, neentaba azafunyanwa.', 'XHO22'),
(67, 16, 21, 'Kwesuka kwehla ezulwini phezu kwabantu isichotho esikhulu, esibunzima bubunzima bumalunga netalente ngalinye; baza abantu bamnyelisa uThixo ngenxa yesibetho sesichotho eso; ngokuba sibe sikhulu kakhulu isibetho sayo.', 'XHO22'),
(67, 17, 1, 'Saza seza esinye sezithunywa zezulu ezisixhenxe, ezineengqayi zosixhenxe, sathetha nam, sisithi kum, Yiza apha, ndikubonise ukugwetywa kwalo ihenyukazi elikhulu; Ndiya kukubonisa ukugwetywa kwehenyukazi elikhulu, elihleli phezu kwamanzi amaninzi;', 'XHO22'),
(67, 17, 2, 'abahenyuze nalo ookumkani bomhlaba, bathi abo bawumiyo umhlaba banxiliswe yiwayini yobuhenyu balo.', 'XHO22'),
(67, 17, 3, 'Sandisa entlango ndikuMoya; ndabona inkazana ihleli phezu kwerhamncwa elimfusa, lizele ngamagama onyeliso, lineentloko ezisixhenxe neempondo ezilishumi.', 'XHO22'),
(67, 17, 4, 'Yaye inkazana leyo ithiwe wambu ngengubo emfusa nemfusa, ihonjiswe ngegolide, namatye anqabileyo, neeperile, inendebe yegolide esandleni sayo, izele ngamasikizi nezingcoliso zobuhenyu bayo.', 'XHO22'),
(67, 17, 5, 'Ebunzi kubhalwe igama elithi, IMFIHLO, IBHABHILONI ENKULU, UNINA WAMAhenyukazi nowamasikizi omhlaba.', 'XHO22'),
(67, 17, 6, 'Ndayibona inkazana leyo inxila ligazi labangcwele, naligazi lamangqina kaYesu; ndamangaliswa ngokumangaliswa okukhulu ndakuyibona.', 'XHO22'),
(67, 17, 7, 'Sathi kum isithunywa eso, Yini na ukuba wamangaliswe? Ndokuxelela mna imfihlelo yenkazana le, neyerhamncwa eli liyithweleyo, lineentloko ezisixhenxe neempondo ezilishumi.', 'XHO22'),
(67, 17, 8, 'Irhamncwa owalibonayo lalikho, alikho; Liya kunyuka liphume enzonzobileni, liye entshabalalweni; baya kumangaliswa abo bemiyo phezu komhlaba, abamagama angabhalwanga encwadini yobomi kususela ekusekweni kwehlabathi, xa balibonayo irhamncwa elaye likho, lisithi, akakho, ukanti ukho.', 'XHO22'),
(67, 17, 9, 'Nantsi ingqiqo enobulumko. Iintloko ezisixhenxe ezi ziintaba ezisixhenxe, ehleli phezu kwazo inkazana leyo.', 'XHO22'),
(67, 17, 10, 'Kananjalo zona zingookumkani abasixhenxe: abahlanu bawile, omnye ukho, omnye akakafiki; athi ke akufika, athabathe ixesha elifutshane.', 'XHO22'),
(67, 17, 11, 'Irhamncwa elaye likho, elingasekhoyo, lona lingowesibhozo, ukwangowabasixhenxe, liye entshabalalweni.', 'XHO22'),
(67, 17, 12, 'Iimpondo ezilishumi ozibonileyo zingookumkani abalishumi, bona abo bangekabi nabukumkani. 11 R kodwa ke bamkela igunya njengookumkani ilixa elinye kunye nerhamncwa.', 'XHO22'),
(67, 17, 13, 'Aba banambono-nye; baya kunikela amandla negunya labo kulo irhamncwa.', 'XHO22'),
(67, 17, 14, 'Aba baya kwenza imfazwe neMvana. iMvana iya kubeyisa, ngokuba inguNkosi kankosi, inguKumkani kakumkani; nabo banayo babiziweyo, banyuliwe, bathembekileyo.', 'XHO22'),
(67, 17, 15, 'Sathi kum, Amanzi lawa manzi uwabonileyo, apho lihleli khona ihenyukazi, ngabantu, nezihlwele, neentlanga, neelwimi.', 'XHO22'),
(67, 17, 16, 'Iimpondo ezilishumi owazibonayo phezu kwerhamncwa, ngabo baya kulithiya ihenyukazi, baliphanzise, balihlube, bayidle inyama yalo, balitshise ngomlilo.', 'XHO22'),
(67, 17, 17, 'Kuba uThixo ubeke ezintliziyweni zabo ukuba benze ingcinga yakhe, benze ngcinga-nye, babunike irhamncwa ubukumkani babo, ade azaliseke amazwi kaThixo.', 'XHO22'),
(67, 17, 18, 'Inkazana owayibonayo nguloo mzi mkhulu, ulawula ookumkani bomhlaba.', 'XHO22'),
(67, 18, 1, 'Ndaza emva koko ndabona isithunywa sezulu sisihla, siphuma ezulwini, sinegunya elikhulu; kwaye umhlaba wakhanyiselwa bubuqaqawuli bakhe.', 'XHO22'),
(67, 18, 2, 'Sadanduluka ngamandla, ngezwi elikhulu, sisithi, Iwile, iwile iBhabheli enkulu, yaba likhaya leedemon, nentolongo yoomoya bonke abangcolileyo, nentolongo yeentaka zonke ezingcolileyo, ezithiyekileyo.', 'XHO22'),
(67, 18, 3, 'Ngokuba zonke iintlanga zisele kuyo iwayini yomsindo wobuhenyu bayo, Nookumkani bomhlaba bahenyuza nayo, Nabarhwebi bomhlaba baba zizityebi, ngenxa yobuninzi bokudla kwayo.', 'XHO22'),
(67, 18, 4, 'Ndaza ndeva elinye izwi liphuma ezulwini lisithi: “Phumani kuyo, bantu bam, ukuze ningadlelani nayo ngezono zayo, nokuze ningamkeliswa kwizibetho zayo.', 'XHO22'),
(67, 18, 5, 'Izono zayo ziye zafikelela emazulwini, Nezinto zayo zokungalungisi uzikhumbule uThixo.', 'XHO22'),
(67, 18, 6, 'Mbuyekezeni njengoko naye ndanivuzayo, niyibuyekeze kabini ngokwemisebenzi yayo;', 'XHO22'),
(67, 18, 7, 'Yinikeni intuthumbo nesijwili, ngangoko yazizukisayo, yaxhamla iziyolo; ngokuba ibisithi entliziyweni yayo, Ndihleli ndingukumkanikazi, andimhlolokazi, andisayi kubona sijwili.', 'XHO22'),
(67, 18, 8, 'Ngenxa yoko ziya kufika ngamini-nye izibetho zayo, Ukufa, nesijwili, nendlala; Iya kutsha ngumlilo; ngokuba ligorha iNkosi uYehova, lowo uyigwebayo.', 'XHO22'),
(67, 18, 9, 'Ookumkani bomhlaba, abo bahenyuza nayo, baxhamla iziyolo nayo, baya kuyimbambazela, bayenzele isijwili, bakuwubona umsi wokutsha kwayo;', 'XHO22'),
(67, 18, 10, 'Bemi kude, ngenxa yokoyika intuthumbo yayo, Besithi, Yeha, yeha, mzi mkhulu iBhabheli, mzi unamandla! ngokuba kufike ngaxa linye umgwebo wakho.', 'XHO22'),
(67, 18, 11, 'Nabarhwebi bomhlaba baya kuyililela, benze isijwili ngenxa yayo; kuba akusekho bani uluthengayo urhwebo lwabo;', 'XHO22'),
(67, 18, 12, 'Urhwebo lwegolide, nesilivere, namatye anqabileyo, neeperile, nelinen ecikizekileyo, nemfusa, nesilika, nebomvu, nomthi wakho wonke, neempahla zonke zeempondo zeendlovu, neempahla zonke zomthi onqabileyo; nobhedu, nesinyithi, nelitye lenyengane;', 'XHO22'),
(67, 18, 13, 'nesinamon, neziqhumiso, namafutha aqholiweyo, nentlaka yokuqhumisa, newayini, neoli, nomgubo ocoliweyo, nengqolowa, neenkomo, nempahla emfutshane, namahashe, neenqwelo, nezicaka, nemiphefumlo yabantu.', 'XHO22'),
(67, 18, 14, 'Neziqhamo ezikhanukwa ngumphefumlo wakho zimkile kuwe, nazo zonke izinto ezityebileyo neziqaqambileyo zimkile kuwe; akusayi kuphinda uzifumane.', 'XHO22'),
(67, 18, 15, 'Abarhwebi bezo zinto, abo batyeba ngayo, Baya kuma kude, ngenxa yokoyika intuthumbo yayo, belila nokwenza isijwili;', 'XHO22'),
(67, 18, 16, 'Besithi, Yeha, yeha, mzi mkhulu Owawuthiwe wambu ngelinen ecikizekileyo, nengubo ebomvu, nengubo emfusa, Uhonjiswe ngegolide, namatye anqabileyo, neeperile!', 'XHO22'),
(67, 18, 17, 'Ngokuba buphanziswe ngalixa linye ubutyebi obungaka. Bonke oomatiloshe, nebandla lonke lasezinqanaweni, noomateloshe, nabo bonke abashishina elwandle, bema kude.', 'XHO22'),
(67, 18, 18, 'Baye bedanduluka, bakuwubona umsi wokutsha kwayo, besithi, Nguwuphi na ofana nalo mzi mkhulu?', 'XHO22'),
(67, 18, 19, 'Bagalela uthuli entloko, Bamana bedanduluka, belila, besenza isijwili, Besithi, Yeha, yeha, mzi mkhulu, Apho baba nobutyebi khona bonke aba baneenqanawa elwandle, ngeento ezinqabileyo zawo! ngokuba ngalixa linye uphanzisiwe.', 'XHO22'),
(67, 18, 20, 'Wenzele imihlali, nina mazulu, nani bapostile bangcwele, nani baprofeti; ngokuba uThixo ukuphindezele phezu kwayo.', 'XHO22'),
(67, 18, 21, 'Saza isithunywa esinamandla safunqula ilitye, langa lilitye lokusila, ukuba likhulu kwalo, saliphosa elwandle, sisithi, Iya kwenjiwa nje ke ukugadlelwa kwayo iBhabheli, umzi omkhulu, ingabi safunyanwa.', 'XHO22'),
(67, 18, 22, 'Nesandi sababethi beehadi, neseemvumi, nesabagwali, nesabavutheli-xilongo, asisayi kuba saviwa kuwe; Neengcibi zonke zobungcibi bonke azisayi kuba safunyanwa kuwe; Nesandi selitye lokusila asisayi kuba saviwa kuwe;', 'XHO22'),
(67, 18, 23, 'Nokukhanya kwesibane akusayi kuba sakhanya kuwe; Nezwi lomyeni nelomtshakazi alisayi kuba saviwa kuwe; ngokuba abarhwebi bakho bebengamanene omhlaba; ngokuba zonke iintlanga zalahlekiswa kukukhafula kwakho.', 'XHO22'),
(67, 18, 24, 'Kuwo kwafunyanwa igazi labaprofeti, nelabangcwele, nelabo bonke abasikiweyo emhlabeni.', 'XHO22'),
(67, 19, 1, 'Ndaza emva koko ndeva izwi elikhulu lesihlwele esikhulu emazulwini lisithi: Haleluya! Usindiso, nozuko, nembeko, namandla, ngaYehova uThixo wethu;', 'XHO22'),
(67, 19, 2, 'Ngokuba iyinyaniso, inobulungisa, imigwebo yakhe;', 'XHO22'),
(67, 19, 3, 'Babuya bathi, Haleluya! Umsi wayo unyuka ngonaphakade kanaphakade.', 'XHO22'),
(67, 19, 4, 'Awa amadoda amakhulu amashumi mabini anamane, nezinto eziphilileyo zone, amnqula uThixo ohleli etroneni, esithi, Amen! Alleluya.', 'XHO22'),
(67, 19, 5, 'Kwaphuma ilizwi etroneni, lisithi, Mdumiseni uThixo wethu, nonke nina bakhonzi bakhe, Nani bamoyikayo, abancinane kwanabakhulu.', 'XHO22'),
(67, 19, 6, 'Ndaza ndeva sanga sisandi sesihlwele esikhulu, sanga sisandi samanzi amaninzi, sanga sisandi seendudumo ezinamandla, sisithi: “Haleluya!', 'XHO22'),
(67, 19, 7, 'Masigcobe, sigcobe, siyizukise; ngokuba ufikile umsitho weMvana, nomfazi wayo uzilungisile.', 'XHO22'),
(67, 19, 8, 'Wanikwa ukuba athiwe wambu ngelinen ecikizekileyo, eqaqambileyo, emhlophe; kuba ilinen ecikizekileyo leyo bubulungisa babangcwele.', 'XHO22'),
(67, 19, 9, 'Yathi kum, Bhala uthi, Banoyolo abo bamenyelwe kwisidlo somtshato weMvana. Sathi kum, La ngamazwi ayinyaniso kaThixo.', 'XHO22'),
(67, 19, 10, 'Ndawa ezinyaweni zaso, ukuba ndiqubude kuso. Sithi kum, Lumka, ungenjenjalo; ndingumkhonzi kunye nawe, nabazalwana bakho abanobungqina bukaYesu; qubuda kuThixo, kuba ubungqina bukaYesu bungumoya wesiprofeto.', 'XHO22'),
(67, 19, 11, 'Ndalibona izulu livulekile, nalo ihashe elimhlophe; Lowo ke wayehleli phezu kwalo ubizwa ngokuba nguThembekile, nguNyaniso; ugweba ngobulungisa, enze imfazwe.', 'XHO22'),
(67, 19, 12, 'Amehlo akhe enjengelangatye lomlilo, entloko izizithsaba ezininzi; waye negama libhaliwe, ekungekho bani ulaziyo, ingenguye ngokwakhe.', 'XHO22'),
(67, 19, 13, 'Wayethiwe wambu ngengubo ethiwe nkxu egazini; igama lakhe kuthiwa nguLizwi likaThixo.', 'XHO22'),
(67, 19, 14, 'Wayelandelwa yimikhosi yasezulwini, ikhwele amahashe amhlophe, yambethe ilinen ecikizekileyo, emhlophe, ecocekileyo.', 'XHO22'),
(67, 19, 15, 'Emlonyeni wakhe kuphuma ikrele elibukhali, ukuze azithi qwaka ngalo iintlanga. Uya kuzalusa ngentonga yentsimbi, exovula isixovulelo sewayini yomsindo nengqumbo kaThixo uSomandla.', 'XHO22'),
(67, 19, 16, 'Engutyeni yakhe nasethangeni lakhe wayenegama libhaliwe kwathiwa, uKumkani kaKumkani, uNkosi kaNkosi.', 'XHO22'),
(67, 19, 17, 'Ndabona isithunywa sezulu, simi elangeni; sadanduluka ngezwi elikhulu, sisithi kuzo zonke iintaka eziphaphazelayo esazulwini sezulu, Yizani nihlanganisele esidlweni sikaThixo omkhulu;', 'XHO22'),
(67, 19, 18, 'ukuze nidle inyama yookumkani, nenyama yabathetheli, nenyama yamagorha, nenyama yamahashe, neyabakhweli bawo, nenyama yabantu bonke, abakhululekileyo nabangamakhoboka, nabancinane, kwaye mkhulu.', 'XHO22'),
(67, 19, 19, 'Ndalibona irhamncwa, nookumkani bomhlaba, nemikhosi yabo, behlanganisene ndawonye ukuba benze imfazwe nalowo uhleli ehasheni, nomkhosi wakhe.', 'XHO22'),
(67, 19, 20, 'Labanjwa ke irhamncwa; kunye nalo kwabanjwa umprofeti obuxoki, lowo wayenzayo imiqondiso emehlweni alo, wabalahlekisa ngayo abo balwamkelayo uphawu lwerhamncwa, nabo baqubuda kuwo umfanekiselo walo; Bobabini baphoswa behleli edikeni lomlilo elivutha isulfure.', 'XHO22'),
(67, 19, 21, 'Abaseleyo babulawa ngekrele lalowo uhleli ehasheni, ikrele eliphuma emlonyeni wakhe; zathi zonke iintaka zahlutha yinyama yabo.', 'XHO22'),
(67, 20, 1, 'Ndabona isithunywa sezulu, sisihla siphuma emazulwini, sinesitshixo senzonzobila, nomxokelelwane omkhulu esandleni saso.', 'XHO22'),
(67, 20, 2, 'Yayibamba inamba, loo nyoka yakudala enguMtyholi, uSathana ke, yayibopha iminyaka eliwaka.', 'XHO22'),
(67, 20, 3, 'Sayiphosa enzonzobileni, samvalela, satywina phezu kwayo, ukuze ingabi sazilahlekisa iintlanga, ide izaliseke iminyaka eliwaka; emva koko umelwe kukuthi akhululwe ixeshana.', 'XHO22'),
(67, 20, 4, 'Ndabona iitrone, nabahleli phezu kwazo, banikwa ugwebo; ndayibona imiphefumlo yababenqunyulwe iintloko ngenxa yobungqina bukaYesu, nangenxa yelizwi likaThixo, ababengalinquli irhamncwa nerhamncwa, umfanekiso wakhe, bengalwamkelanga uphawu lwakhe emabunzini abo, nasezandleni zabo; badla ubomi, baba ngookumkani kunye noKristu iminyaka eliwaka.', 'XHO22'),
(67, 20, 5, 'Ke abafileyo abaseleyo ababuyanga badle bomi, yada yazaliseka iminyaka eliwaka. Olo luvuko lokuqala.', 'XHO22'),
(67, 20, 6, 'Unoyolo, ungcwele, lowo unesabelo eluvukweni lokuqala; ukufa kwesibini akunagunya kubo abanjalo; baya kusuka babe ngababingeleli bakaThixo nabakaKristu, babe ngookumkani kunye naye iminyaka eliwaka.', 'XHO22'),
(67, 20, 7, 'Xa ithe yazaliseka iminyaka eliwaka, uSathana uya kukhululwa entolongweni.', 'XHO22'),
(67, 20, 8, 'Uya kuphuma, aye kuzilahlekisa iintlanga eziseziphelweni zone zomhlaba, uGogi noMagogi, ukubahlanganisela emfazweni, inani labo linjengentlabathi yolwandle.', 'XHO22'),
(67, 20, 9, 'Banyuka, baya kububanzi behlabathi, bayirhawula inqaba yomkhosi wabangcwele, nomzi othandiweyo; kwehla umlilo uphuma kuThixo, uphuma ezulwini, wabadla wabadla.', 'XHO22'),
(67, 20, 10, 'Umtyholi lowo ubalahlekisayo waphoswa edikeni lomlilo nesulfure, apho likhona irhamncwa nomprofeti wobuxoki; baya kuthuthunjiswa imini nobusuku, kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(67, 20, 11, 'Ndabona itrone enkulu emhlophe, nalowo uhleli phezu kwayo, owathi umhlaba nezulu wasaba ebusweni bakhe, azafunyanelwa ndawo ke.', 'XHO22'),
(67, 20, 12, 'Ndababona abafileyo, abancinane nabakhulu, bemi phambi koThixo; Zavulwa iincwadi, kwavulwa nenye incwadi, eyeyobomi; bagwetywa abafileyo ngokubhaliweyo ezincwadini ezo, ngokwemisebenzi yabo.', 'XHO22'),
(67, 20, 13, 'Lwathi ulwandle lwabakhupha abafileyo abakulo; kwathi ukufa nelabafileyo kwabakhupha abafileyo abakhona; bagwetywa elowo ngokwemisebenzi yakhe.', 'XHO22'),
(67, 20, 14, 'Kwaza ukufa nelabafileyo kwaphoswa edikeni lomlilo. Oko kukufa kwesibini.', 'XHO22'),
(67, 20, 15, 'Uthe ongafunyanwanga ebhaliwe encwadini yobomi, waphoswa edikeni lomlilo.', 'XHO22'),
(67, 21, 1, 'Ndabona izulu elitsha, nomhlaba omtsha; kuba elokuqala izulu nowokuqala umhlaba udlule, nolwandle alusekho, akwaba sabakho lwandle.', 'XHO22'),
(67, 21, 2, 'Ndaza mna, Yohane, ndawubona umzi ongcwele, iYerusalem entsha, usihla uphuma kuThixo, uphuma emazulwini, ulungisiwe njengomtshakazi ehonjiselwe indoda yakhe.', 'XHO22'),
(67, 21, 3, 'Ndeva izwi elikhulu liphuma emazulwini, lisithi, Yabona, umnquba lo kaThixo unabantu; uya kuhlala nabo emnqubeni, bona babe ngabantu bakhe, yena uThixo abe nabo, enguThixo wabo.', 'XHO22'),
(67, 21, 4, 'Azisule uThixo zonke iinyembezi emehlweni abo; kungabi sabakho kufa, kungabi sabakho nasijwili, nakukhala, nantlungu; ngokuba izinto zokuqala zigqithile.', 'XHO22'),
(67, 21, 5, 'Waza lowo uhleli phezu kwetrone wathi, Uyabona, izinto zonke ndizenza ntsha. Wathi kum, Bhala; ngokuba la mazwi ayinyaniso, athembekileyo.', 'XHO22'),
(67, 21, 6, 'Wathi kum, Kwenzekile. Mna ndinguAlfa no-Omega, isiqalo nesiphelo; mna ndomnika lowo unxaniweyo emthonjeni wamanzi obomi, ngesisa.', 'XHO22'),
(67, 21, 7, 'Lowo weyisayo uya kuzidla ilifa izinto zonke; kwaye ndiya kuba nguThixo wakhe, yena abe ngunyana wam.', 'XHO22'),
(67, 21, 8, 'Ke wona amagwala, nabangakholwayo, nabangamasikizi, nababulali, nabenzi bombulo, nabakhafuli, nabakhonzi bezithixo, nawo onke amaxoki, isabelo sawo siya kuba sedikeni elivutha umlilo nesulfure. Oko kukufa kwesibini.', 'XHO22'),
(67, 21, 9, 'Saza seza kum esinye sezithunywa zezulu ezisixhenxe, ezineengqayi ezisixhenxe, ezizele zizibetho zosixhenxe zokugqibela, sathetha nam sisithi, Yiza apha, ndikubonise umtshakazi, umfazi wayo iMvana.', 'XHO22'),
(67, 21, 10, 'Sandisa ndikuMoya entabeni enkulu, ephakamileyo, sandibonisa umzi omkhulu, iYerusalem engcwele, usihla uphuma emazulwini, uvela kuThixo;', 'XHO22'),
(67, 21, 11, 'Isikhanyiso sawo sifana nelitye elinqabileyo kakhulu, langa leyaspisi ebengezela njengekristale.', 'XHO22'),
(67, 21, 12, 'Unodonga olukhulu oluphakamileyo, unamasango alishumi elinamabini, phezu kwamasango unezithunywa zezulu ezilishumi elinazibini, kubhalwe kuwo amagama angawezizwe ezilishumi elinazibini zoonyana bakaSirayeli.', 'XHO22'),
(67, 21, 13, 'Ngasempumalanga kukho amasango amathathu; ngasentla ngamasango mathathu; ngasezantsi ngamasango mathathu; ngasentshonalanga ingamasango mathathu.', 'XHO22'),
(67, 21, 14, 'Udonga lomzi lunamatye eziseko alishumi elinamabini, kuwo ingamagama abapostile abalishumi elinababini bayo iMvana.', 'XHO22'),
(67, 21, 15, 'Lowo ubethetha nam wayenengcongolo yegolide, ukuba awulinganise umzi lowo, namasango awo, nodonga lwawo.', 'XHO22'),
(67, 21, 16, 'Isixeko eso sasinamacala amane ambombo-ne, ubude bungangobubanzi bawo. Wawulinganisa umzi lowo ngengcongolo, wazitadiya ezilishumi elinesibini lamawaka; ubude nobubanzi nokuphakama kwawo kuyalingana.', 'XHO22'),
(67, 21, 17, 'Walulinganisa nodonga lwaso, ikhulu elinamanci mane anesine leekubhite, ngokomlinganiso womntu, oko kukuthi, owengelosi.', 'XHO22'),
(67, 21, 18, 'Isakhiwo sodonga lwaso yiyaspisi; umzi ke yigolide engcwengekileyo, efana negilasi ebonakalelayo;', 'XHO22'),
(67, 21, 19, 'Namatye eziseko zodonga lomzi ayevathiswe ngamatye anqabileyo onke. Isiseko sokuqala yiyaspisi; eyesibini yisafire; eyesithathu yaba yikalkedoni; esesine yiemeraldi;', 'XHO22'),
(67, 21, 20, 'Eyesihlanu ibiyisardoniki; eyesithandathu ibiyisardiyo; eyesixhenxe yikrizolite; elesibhozo libe yikrizoli; elesithoba yitopazi; eleshumi yikrisopraso; eleshumi elinanye libe yihakinto; eleshumi elinesibini yiametiste.', 'XHO22'),
(67, 21, 21, 'Namasango lawo alishumi elinamabini aziiperile ezilishumi elinambini; isango ngalinye lenziwe ngaperile-nye; nesitrato somzi yigolide engcwengekileyo, njengegilasi ebonakalelayo.', 'XHO22'),
(67, 21, 22, 'Andibonanga tempile kuwo, kuba iNkosi uThixo uSomandla nayo iMvana yitempile yawo.', 'XHO22'),
(67, 21, 23, 'Ke umzi lowo awufuni langa, kwanayo inyanga, kuba ubuqaqawuli bukaThixo buwukhanyisela, nayo iMvana isisibane sawo.', 'XHO22'),
(67, 21, 24, 'Iintlanga zabo basindiswayo ziya kuhamba ekukhanyeni kwawo; ookumkani bomhlaba bazise kuwo uzuko nembeko yabo.', 'XHO22'),
(67, 21, 25, 'Amasango awo akasayi kuvalwa mini kwaphela, kuba ubusuku abuyi kubakho khona.', 'XHO22'),
(67, 21, 26, 'Baya kuzisa uzuko nembeko yeentlanga ezo kuwo.', 'XHO22'),
(67, 21, 27, 'Akuyi kungena kuwo nanye into eyenza inqambi, eyenza isikizi, nobuxoki; abaya kungena ngabo bodwa babhaliweyo encwadini yobomi yeMvana.', 'XHO22'),
(67, 22, 1, 'za sandibonisa umlambo wamanzi obomi, uqaqambile, ngathi ngumkhenkce, uphuma etroneni kaThixo neyeMvana.', 'XHO22'),
(67, 22, 2, 'Phakathi kwesitrato sawo namacala omabini omlambo, kukho umthi wobomi, ovelisa iintlobo zeziqhamo ezilishumi elinambini, unika iinyanga ngeenyanga zonke isiqhamo sawo; abe amagqabi omthi lowo engawokuphilisa imimoya, izizwe.', 'XHO22'),
(67, 22, 3, 'Akusayi kuba sabakho nasinye isiqalekiso; kodwa itrone kaThixo neyeMvana iya kubakho kuwo; abakhonzi bakhe bamkhonze;', 'XHO22'),
(67, 22, 4, 'Aya kububona ubuso bakhe; negama lakhe liya kuba semabunzini abo.', 'XHO22'),
(67, 22, 5, 'Akuyi kubakho busuku khona; abafuni sibane, nakukhanya kwelanga; ngokuba iNkosi uThixo iya kuzikhanyisela, zilawule ngonaphakade kanaphakade.', 'XHO22'),
(67, 22, 6, 'Sathi kum, La mazwi athembekile, ayinyaniso; iNkosi, uThixo wabaprofeti abangcwele, yathuma isithunywa sayo sasemazulwini ukubabonisa abakhonzi bayo izinto ezimele ukubakho kamsinya.', 'XHO22'),
(67, 22, 7, 'Yabona, ndiyeza kamsinya; unoyolo lowo uwagcinayo amazwi esiprofeto sayo le ncwadi.', 'XHO22'),
(67, 22, 8, 'Mna, Yohane, ndingulowo ubezibona ezi zinto, eziva; Ndathi ndakuva, ndakubona, ndawa ndaqubuda phambi kweenyawo zesithunywa sezulu esandibonisa ezi zinto.', 'XHO22'),
(67, 22, 9, 'Sithi kum, Lumka, ungenjenjalo; ngokuba ndingumkhonzi kunye nawe, nabazalwana bakho abaprofeti, nabo bawagcinayo amazwi ale ncwadi; qubuda kuThixo.', 'XHO22'),
(67, 22, 10, 'Sathi kum, Musa ukuwatywina amazwi esiprofeto sale ncwadi; kuba ixesha likufuphi.', 'XHO22'),
(67, 22, 11, 'Lowo ungongendawo makahlale engongendawo; nalowo uyinyhukunyhuku, makaselehlala eyinyhukunyhuku; lowo ulilungisa makahlale elilungisa; nongcwele makahlale ngcwele.', 'XHO22'),
(67, 22, 12, 'Yabona, ndiyeza kamsinya; umvuzo wam unam, wokuba ndivuze elowo njengoko umsebenzi wakhe uya kuba njalo.', 'XHO22'),
(67, 22, 13, 'Mna ndinguAlfa no-Omega, isiqalo nesiphelo, owokuqala nowokugqibela.', 'XHO22'),
(67, 22, 14, 'Banoyolo abo bayenzayo imithetho yakhe, ukuze babe negunya kuwo umthi wobomi, bangene ngamasango kuwo umzi.', 'XHO22'),
(67, 22, 15, 'Ngaphandle ke zizinja, nabakhafuli, nabenzi bombulo, nababulali, nabakhonzi bezithixo, nabo bonke ababuthandayo, ababenzayo ubuxoki.', 'XHO22'),
(67, 22, 16, 'Mna, Yesu, ndasithuma isithunywa sam sasemazulwini, ukuba ndizingqine kuni ezi zinto ngawo amabandla. Mna ndiyingcambu nenzala kaDavide, ikhwezi eliqaqambileyo lokusa.', 'XHO22'),
(67, 22, 17, 'uMoya nomtshakazi bathi, Yiza. Lowo uvayo makathi, Yiza. Lowo unxaniweyo makeze; nosukuba ethanda, makawathabathe amanzi obomi ngesisa.', 'XHO22'),
(67, 22, 18, 'Kuba ndiyangqina kubo bonke abawevayo amazwi esiprofeto sale ncwadi, ukuba, Ukuba uthe umntu wongeza kwezo zinto, uThixo uya kongeza kuye izibetho ezibhaliweyo kule ncwadi;', 'XHO22'),
(67, 22, 19, 'Ukuba ke uthe umntu wasusa kuwo amazwi encwadi yesi siprofeto, uThixo uya kusisusa isabelo sakhe encwadini yobomi, nakuwo umzi ongcwele, nakuzo izinto ezibhaliweyo kule ncwadi.', 'XHO22'),
(67, 22, 20, 'Uthi lowo uzingqinayo ezi zinto, Ewe, ndiyeza kamsinya. Amen. Ewe, yiza, Nkosi Yesu.', 'XHO22'),
(67, 22, 21, 'Ubabalo lweNkosi yethu uYesu Kristu malube nani nonke. Amen.', 'XHO22');
