INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(3, 11, 3, 'Yonke into ethiwe gqiphu uphuphu, neyahlukene iinyawo, nebetha phakathi kwezilwanyana, nozidla zona.', 'XHO22'),
(3, 11, 4, 'Kwezibetha uphuphu, nakwezithiwe gqiphu uphuphu, musani ukuyidla; njengenkamela, ngokuba ibetha, kodwa ayiluthe gqiphu uphuphu; akuzihlambulukanga kuwe;', 'XHO22'),
(3, 11, 5, 'nentsika, ngokuba ihlaba, kodwa azikahlulwa izikhonkwane; akuzihlambulukanga kuwe;', 'XHO22'),
(3, 11, 6, 'Nomvundla, ngokuba ubetha, kodwa iinzipho azahlukanga; akuzihlambulukanga kuwe;', 'XHO22'),
(3, 11, 7, 'neehagu, nakuba zicandeke kabini, zabe zithe gqiphu uphuphu, azilawuli; akuzihlambulukanga kuwe;', 'XHO22'),
(3, 11, 8, 'Ize ningayidli inyama yazo, ningazichukumisi izidumbu zazo; azihlambulukanga kuni.', 'XHO22'),
(3, 11, 9, 'Nozidla ezintweni zonke ezisemanzini: Zonke izinto ezinamaphiko namaxolo emanzini, elwandle nasemilanjeni, nozidla.', 'XHO22'),
(3, 11, 10, 'Yaye yonk’ into engenamaphiko namaxolo elwandle nasemilanjeni, kuyo yonke into enyakazelayo emanzini nayo yonke into ephilayo esemanzini, iya kukrazulwa kuwe.', 'XHO22'),
(3, 11, 11, 'Yoba lisikizi kuni; nize ningayidli inyama yazo, nesidumbu sazo soba lisikizi kuni.', 'XHO22'),
(3, 11, 12, 'Yonke into engenamaphiko namaxolo emanzini yoba nezothe kuni.', 'XHO22'),
(3, 11, 13, 'Zezo ezintakeni niya kuba lisikizi; mazingadliwa, ngamasikizi: lixhalanga, nentaka, nentaka;', 'XHO22'),
(3, 11, 14, 'nexhalanga ngohlobo lwazo;', 'XHO22'),
(3, 11, 15, 'nehlungulu lonke ngohlobo lwalo;', 'XHO22'),
(3, 11, 16, 'nenciniba, nenqu, nenqu, nenquba ngohlobo lwazo;', 'XHO22'),
(3, 11, 17, 'nesikhova, nesikhova, nesikhova;', 'XHO22'),
(3, 11, 18, 'nokhetshe, nexhalanga, nexhalanga;', 'XHO22'),
(3, 11, 19, 'nengabangaba ngohlobo lwayo, nengabangaba, nengabangaba.', 'XHO22'),
(3, 11, 20, 'Zonke iintaka ezirhubuluzayo nezinemilenze emine yoba nezothe kuni.', 'XHO22'),
(3, 11, 21, 'Kodwa ungadla kuzo zonke izinambuzane ezihamba ngemilenze emine, ezineenyawo ezinyaweni zazo zokutsiba nazo emhlabeni;', 'XHO22'),
(3, 11, 22, 'Ungazidla nawe; iinkumbi ngohlobo lwazo, iinkumbi ngohlobo lwazo, iinkumbi ngohlobo lwazo, neenkumbi ngohlobo lwazo.', 'XHO22'),
(3, 11, 23, 'Zonke ezinye izidalwa eziphaphazelayo, ezinemilenze yomine, zoba nezothe kuni.', 'XHO22'),
(3, 11, 24, 'Ngako oko noba ziinqambi; bonke abachukumisa ingubo yakhe boba ziinqambi, bahlwise.', 'XHO22'),
(3, 11, 25, 'Nabani na othe wathabatha esidumbini sazo, wozihlamba iingubo zakhe, abe ngongahlambulukanga kude kuhlwe.', 'XHO22'),
(3, 11, 26, 'Zonke izidumbu eziluthe gqiphu uphuphu, ukulucanda kubini, zingaboli, ziziinqambi kuni; nabani na oyichukumisayo woba yinqambi.', 'XHO22'),
(3, 11, 27, 'Yonke into ehamba ngeenyawo zayo, ezintweni zonke ezizitho zine, yoba ziinqambi kuni; osichukumisayo isidumbu sazo woba yinqambi, ahlwise.', 'XHO22'),
(3, 11, 28, 'Lowo uyithwalayo, wozihlamba iingubo zakhe, abe yinqambi, ahlwise; azihlambulukanga kuni.', 'XHO22'),
(3, 11, 29, 'Nazo ezo zoba ziinqambi kuni enyakanyakeni enyakazelayo emhlabeni; impuku, impuku, nehobe ngohlobo lwazo;', 'XHO22'),
(3, 11, 30, 'intaka, nexhalanga, necilishe, necilishe, nesele.', 'XHO22'),
(3, 11, 31, 'Yoba lisikizi kuni ezinambuzaneni zonke; bonke abayichukumisayo into efileyo kuyo, woba yinqambi, bahlwise.', 'XHO22'),
(3, 11, 32, 'Yonke into ethe yawelwa enye kuzo ekufeni, yoba yinqambi; nokuba yimpahla yomthi, nokuba yingubo, nokuba yimpahla, nokuba yisikhumba, nokuba yingxowa, yonke into ekusetyenzwa ngayo, yaphoswa emanzini, ibe yinqambi, ihlwise; ngoko iya kuhlanjululwa.', 'XHO22'),
(3, 11, 33, 'Limpahla zonke zomdongwe, ethe yeyela kuzo enye kuzo, zoba ziinqambi zonke izinto eziphakathi kwazo; kwaye uya kuyiqhekeza.', 'XHO22'),
(3, 11, 34, 'Yonke inyama edliwa phezu kwamanzi anjalo yoba yinqambi; newayini yonke engaseselwa kweso sitya, yoba yinqambi.', 'XHO22'),
(3, 11, 35, 'Yonke into ethe yawelwa ngumgibe wabo, yoba yinqambi; nokuba liziko, nokuba ziimbiza, zoqhekezwa, ngokuba azihlambulukanga kuni, yoba ziinqambi kuni.', 'XHO22'),
(3, 11, 36, 'Kodwa iqula, nequla elinamanzi amaninzi, lohlambuluka; bonke abasichukumisayo isidumbu sazo woba yinqambi.', 'XHO22'),
(3, 11, 37, 'Xa kuthe kwawa nandawoni yesidumbu sazo embewini yonke eza kuhlwayelwa, yohlambuluka yona.', 'XHO22'),
(3, 11, 38, 'Kodwa xa imbewu igalelwe emanzini, kwawa nantoni na efileyo phezu kwayo, yoba yinqambi kuni.', 'XHO22'),
(3, 11, 39, 'Ukuba kuthe kwafa nto izitho zine enisukuba nisidla, yiyo edliwayo; osichukumisayo isidumbu saso woba yinqambi, ahlwise.', 'XHO22'),
(3, 11, 40, 'Lowo uyidlayo inyama yesidumbu sayo, wozihlamba iingubo zakhe, abe yinqambi, ahlwise; lowo uwuthwalayo, wozihlamba iingubo zakhe, abe yinqambi, ahlwise.', 'XHO22'),
(3, 11, 41, 'Yonke into enyakazelayo emhlabeni yoba ngamasikizi; mayingadliwa.', 'XHO22'),
(3, 11, 42, 'Yonke into enambuzelayo ngesisu sayo, nayo yonke into ehamba ngemilenze emine, okanye nantoni na enemilenze emininzi phakathi kwezinambuzane zonke ezisehlabathini, ize ningazidli; ngokuba lisikizi.', 'XHO22'),
(3, 11, 43, 'Musani ukuyenza imiphefumlo yenu ibe nezothe ngenyakanyaka yonke, ningazenzi iinqambi ngayo, nizenze iinqambi ngayo.', 'XHO22'),
(3, 11, 44, 'Kuba ndiyiNkosi uThixo wenu; ke ngoko, kufuneka nibe ngcwele, nibe ngcwele; ngokuba ndingcwele mna; musani ukuzenza iinqambi ngenyakanyaka yonke enyakazelayo emhlabeni.', 'XHO22'),
(3, 11, 45, 'Ngokuba ndinguYehova onikhuphileyo ezweni laseYiputa, ukuba ndibe nguThixo wenu; ke ngoko, kufuneka nibe ngcwele, ngokuba ndingcwele mna.', 'XHO22'),
(3, 11, 46, 'Nguwo lo umyalelo weento ezizitho zine, neentaka, neento zonke eziphilileyo ezinambuzelayo emanzini, nezinambuzane zonke ezinambuzelayo emhlabeni:', 'XHO22'),
(3, 11, 47, 'woyahlula into eyinqambi nehlambulukileyo, nento ezitho zine edliwayo nengadliwayo.', 'XHO22'),
(3, 12, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 12, 2, 'Thetha koonyana bakaSirayeli, uthi, Umfazi, xa athe wathabatha, wazala indoda, woba yinqambi imihla esixhenxe; njengemihla yokuhlanjululwa kwesigulo sakhe woba yinqambi.', 'XHO22'),
(3, 12, 3, 'Ngomhla wesibhozo uya kwaluswa ijwabi.', 'XHO22'),
(3, 12, 4, 'Wohlala egazini lokuhlanjululwa kwakhe iintsuku ezimashumi mathathu anantathu; angachukumisi nanye ingcwele, angangeni engcweleni, zide zizaliseke iintsuku zokuhlanjululwa kwakhe.', 'XHO22'),
(3, 12, 5, 'Ke ukuba uthe wazala intombi, woba yinqambi iiveki ezimbini, njengokuqhawula kwakhe umtshato; ahlale egazini lokuhlanjululwa kwakhe iintsuku ezimashumi mathandathu anesithandathu.', 'XHO22'),
(3, 12, 6, 'Ekupheleni kweentsuku zokuhlanjululwa kwakhe, ngenxa yonyana nokuba yentombi, wozisa imvana emnyaka mnye, ibe lidini elinyukayo, nehobe, nokuba lihobe, libe lidini lesono emnyango, lithabathele ententeni yokuhlangana, liye kumbingeleli;', 'XHO22'),
(3, 12, 7, 'uyisondeze phambi koYehova, umcamagushele; alihlambe ithombo legazi lakhe. Nguwo lowo umthetho ngomntwana oyinkwenkwe okanye oyinkazana.', 'XHO22'),
(3, 12, 8, 'Ukuba ke akanako ukuzisa imvana, wozisa amahobe amabini, nokuba ngamahobe amabini; elinye libe leledini elinyukayo, elinye libe leledini lesono; aze umbingeleli amcamagushele, aze azisulungekise.', 'XHO22'),
(3, 13, 1, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(3, 13, 2, 'Umntu xa kuthe kwakho eluswini lwenyama yakhe itshatshazi, nokuba litshatshazi, nokuba litshatshazi, libonakala linjengeqhenqa eluswini lwenyama yakhe; woziswa kuAron umbingeleli, nakomnye woonyana bakhe, ababingeleli.', 'XHO22'),
(3, 13, 3, 'Akukhangele umbingeleli ukufa eluswini lwenyama; ukuba iinwele zomfi zithe zaba mhlophe, waza umfi wabonakala emhlophe kunolusu lwenyama yakhe: liqhenqa ke elo; umbingeleli amkhangele, athi uhlambulukile.', 'XHO22'),
(3, 13, 4, 'Xa kuthe kwakho itshatshazi eluswini lwenyama yayo, alabunanga eluswini, alwasuka lwaba mhlophe unwele lwalo; umbingeleli womvalela onesi sifo iintsuku ezisixhenxe.', 'XHO22'),
(3, 13, 5, 'Wothi ke umbingeleli amkhangele ngomhla wesixhenxe, akhangele ukuba isibetho siphelile emehlweni akhe, asinwenwanga eluswini isibetho; aphinde umbingeleli amvalele imihla esixhenxe;', 'XHO22'),
(3, 13, 6, 'abuye ambone umbingeleli ngomhla wesixhenxe; isibetho asiqhenqethanga eluswini, umbingeleli wothi uhlambulukile; ndimhlophe kuphela; anxibe iingubo zakhe, ahlambuluke.', 'XHO22'),
(3, 13, 7, 'Ukuba ukujaduka kuqhenqethile eluswini, emva kokubonakala kwakhe kumbingeleli ngenxa yokuhlanjululwa kwakhe, wophinda abonakale kumbingeleli;', 'XHO22'),
(3, 13, 8, 'umbingeleli uthe, akuba liqhenqethile eluswini, umbingeleli wothi uyinqambi; liqhenqa.', 'XHO22'),
(3, 13, 9, 'Ukuba lithe labakho emntwini iqhenqa, woziswa kumbingeleli;', 'XHO22'),
(3, 13, 10, 'umbingeleli amkhangele, O! ukujaduka kumhlophe eluswini, unwele lusuke lwamhlophe, kukho inyama ephilileyo elukhweni;', 'XHO22'),
(3, 13, 11, 'liqhenqa elidala eli elùswini lwenyama yakhe, umbingeleli wovakalisa ukuba uyinqambi, angamvaleli entolongweni, ngokuba akahlambulukanga.', 'XHO22'),
(3, 13, 12, 'Ukuba lithe labonakala iqhenqa eluswini, lasuka iqhenqa lalugubungela lonke ulusu lomntu onesibetho, lithabathela entloko lesa ezinyaweni, apho akhangele umbingeleli;', 'XHO22'),
(3, 13, 13, 'akhangele umbingeleli, O! ukuba iqhenqa liwugubungele wonke umzimba wakhe, wovakalisa ukuba uhlambulukile onesibetho; yonke into yaba mhlophe; uhlambulukile.', 'XHO22'),
(3, 13, 14, 'Ke ukuba kuthe kwabonakala kuye inyama ekrwada, yoba yinqambi.', 'XHO22'),
(3, 13, 15, 'Woyikhangela umbingeleli inyama ekrwada, athi ayihlambulukile; ngokuba inyama ephekiweyo iyinqambi: liqhenqa ke elo.', 'XHO22'),
(3, 13, 16, 'Naxa ithe yabuya inyama ekrwada, yasuka yaba mhlophe, woza kumbingeleli;', 'XHO22'),
(3, 13, 17, 'umbingeleli amkhangele, O! wothi umbingeleli uhlambulukile onesibetho; uhlambulukile.', 'XHO22'),
(3, 13, 18, 'Ke inyama eyayinesilonda eluswini lwayo, yaphiliswa;', 'XHO22'),
(3, 13, 19, 'endaweni yesilonda kwabakho itshatshazi elimhlophe, nokuba litshatshazi, elimhlophe elifuna ukuba yingqombela, woboniswa umbingeleli;', 'XHO22'),
(3, 13, 20, 'akubona umbingeleli, O! utyekile kunolusu, neenwele zakho zimhlophe; umbingeleli wothi akahlambulukile; liqhenqa elisuka ethumbeni.', 'XHO22'),
(3, 13, 21, 'Ukuba umbingeleli uthe wakubona, wakhangela, akukho lunwele lumhlophe kuwe, aligqubuthele eluswini, lisuke laburhalarhuqa; Umbingeleli womvalela imihla esixhenxe.', 'XHO22'),
(3, 13, 22, 'Ke ukuba liselithe elùswini, umbingeleli wothi uyinqambi; ngubhubhani.', 'XHO22'),
(3, 13, 23, 'Ke ukuba itshatshazi lithe lema endaweni yalo, alaphilisa: sisibetho esitshisayo eso; aze umbingeleli athi uhlambulukile.', 'XHO22'),
(3, 13, 24, 'Xa kuthe kwakho eluswini lwenyama, kwabakho itshatshazi, kwabakho itshatshazi elimhlophe elibomvu, nokuba limhlophe;', 'XHO22'),
(3, 13, 25, 'wokukhangela umbingeleli, O! lusuke lwaba mhlophe unwele olusebunzi, libonakala limhlophe eluswini; liqhenqa lomlilo; wothi umbingeleli akahlambulukile; liqhenqa ke elo.', 'XHO22'),
(3, 13, 26, 'Wabona umbingeleli, O! akukho lunwele lumhlophe ekreleni, lingagqubuthelenga ulusu, lisuke lamnyama; Umbingeleli womvalela imihla esixhenxe.', 'XHO22'),
(3, 13, 27, 'Umbingeleli womkhangela ngomhla wesixhenxe; umbingeleli wothi uyinqambi; liqhenqa ke elo.', 'XHO22'),
(3, 13, 28, 'Ukuba itshatshazi lithe lema endaweni yalo, alaqhenqetha eluswini, lisuke labuna: yimfumba yomlilo leyo, aze umbingeleli athi ihlambulukile, ngokuba kukudla komlilo oko.', 'XHO22'),
(3, 13, 29, 'Xa kuthe kwakho isibetho kwindoda nenkazana entloko, nokuba kusesilevini;', 'XHO22'),
(3, 13, 30, 'umbingeleli akukhangele ukufa, O! kukho iinwele ezicekethekileyo ezimthubi; wovakalisa umbingeleli ukuba akahlambulukile: litshatshazi elimhlophe elomileyo, liqhenqa entloko, nokuba kusesilevini eso.', 'XHO22'),
(3, 13, 31, 'Wasibona umbingeleli isibetho sokujaduka, wasikhangela, ukuba lithe gangqa eluswini ukubonakala kwalo, kungekho lunwele lumnyama kuso; umbingeleli amvalele imihla esixhenxe onendawo edlathukileyo;', 'XHO22'),
(3, 13, 32, 'Umbingeleli wowukhangela umsebenzi ngomhla wesixhenxe, akhangele ukuba ityholo alinwenwanga, akukho lunwele kulo, libhungana lithe gangqa eluswini, libonakalalisiwe;', 'XHO22'),
(3, 13, 33, 'Wozicheba, kodwa angayichebi emhlophe; umbingeleli amgqume ngelaphu elimhlophe ezinye iintsuku ezisixhenxe.', 'XHO22'),
(3, 13, 34, 'Wothi ngomhla wesixhenxe umbingeleli akukhangele oko kumhlophe, akhangele ukuba ukudlathuka akunwenwanga eluswini, akubonakali eluswini; wothi uhlambulukile umbingeleli; azihlambe iingubo zakhe, ahlambuluke.', 'XHO22'),
(3, 13, 35, 'Ukuba ukujaduka kuthe kwaqhenqetha eluswini emveni kokuba ehlambulukile;', 'XHO22'),
(3, 13, 36, 'Wothi ke umbingeleli amkhangele, O! ukuba ukujaduka kuqhenqethile eluswini, umbingeleli akasayi kuba sabheka ngwevu; akahlambulukile.', 'XHO22'),
(3, 13, 37, 'Ukuba kuthe kwakho ilifu emehlweni akhe, kwaphuma unwele olumnyama kulo; umhlophe uphilile, uhlambulukile; aze umbingeleli athi uhlambulukile.', 'XHO22'),
(3, 13, 38, 'Indoda nenkazana xa kuthe kwakho iintsumpa eluswini lwenyama yayo, ziintsumpa ezimhlophe;', 'XHO22'),
(3, 13, 39, 'akhangele umbingeleli, O! amatshatshazi eluswini lwenyama yabo amhlophe na; litshatshazi elo elithe qwele eluswini; uhlambulukile.', 'XHO22'),
(3, 13, 40, 'Indoda ezinwele zayo zilukiwe yinkqayi; uhlambulukile.', 'XHO22'),
(3, 13, 41, 'Xa ithe intloko yayo yaguda, yathabathela entloko, yesa ebusweni bakhe: yinkqayi leyo, uhlambulukile.', 'XHO22'),
(3, 13, 42, 'Xa kuthe kwakho isibetho esimhlophe bubomvu entloko, nokuba kusesikhundleni; liqhenqa elibonakala ebusweni bakhe, nokuba kusekhanda lakhe.', 'XHO22'),
(3, 13, 43, 'Wothi ke umbingeleli asikhangele, O!', 'XHO22'),
(3, 13, 44, 'yindoda eneqhenqa, ayihlambulukile; umbingeleli wothi akahlambulukile; ukufa kwakhe kusentlokweni yakhe.', 'XHO22'),
(3, 13, 45, 'Lowo uneqhenqa, ekufelwe kuye, wazikrazula iingubo zakhe, wayisusa intloko yakhe, wazigquma umlomo wakhe, wadanduluka, esithi, Andihlambulukile, andihlambulukile.', 'XHO22'),
(3, 13, 46, 'Yonke imihla esikhoyo ngayo isibetho, woba yinqambi; akahlambulukile; uya kuhlala yedwa; ngaphandle kweminquba yoba yindawo yakhe yokuhlala.', 'XHO22'),
(3, 13, 47, 'Isambatho esikhoyo kuyo iqhenqa, yingubo yoboya, nelinen emhlophe;', 'XHO22'),
(3, 13, 48, 'Nokuba ingaphakathi kweendonga eziphantsi okanye ngamalaphu; ngelinen okanye uboya; neempahla zonke zesikhumba, neempahla zonke;', 'XHO22'),
(3, 13, 49, 'Ukuba isibetho sithe saluhlaza, nokuba singqombela engutyeni, nasesikhumbeni, naselusingeni, nasesinqeni, nasemlanjeni; liqhenqa ke elo, woxelwa kumbingeleli.', 'XHO22'),
(3, 13, 50, 'Umbingeleli wokukhangela oko kufelweyo aze amvalele imihla esixhenxe;', 'XHO22'),
(3, 13, 51, 'awukhangele umsebenzi ngomhla wesixhenxe, ukuba uthe waqhenqetha ingubo engutyeni, nasemasongweni, nasemasongweni, naseqhingeni, nasesikhumbeni, nasemisebenzini yonke yesikhumba; ukufa liqhenqa elibuhlungu; ayicocekanga.', 'XHO22'),
(3, 13, 52, 'Woyitshisa loo ngubo, nokuba yeyelinen, noboya, noboya, nempahla yonke yesikhumba esikhoyo kuyo isibetho, ngokuba liqhenqa elibuhlungu; utshiswe ngomlilo.', 'XHO22'),
(3, 13, 53, 'Ukuba umbingeleli uthe wakhangela, wabona ukuba isibetho asiqhenqethanga engutyeni, nasesiguqweni, nasengutyeni, nasengubombeni yonke;', 'XHO22'),
(3, 13, 54, 'Woyalela umbingeleli ukuba ihlanjwe loo nto kufele kuyo, aze ayivale ezinye iintsuku ezisixhenxe.', 'XHO22'),
(3, 13, 55, 'Wokukhangela umbingeleli emva kokuhlanjwa, akhangele ukuba ukufa akujikalanga, nokuba ukufa akunwenwanga; ayicocekanga; umtshise ngomlilo; ingaphakathi, nokuba ingaphakathi okanye ngaphandle.', 'XHO22'),
(3, 13, 56, 'Wakhangela umbingeleli, wabona ukufa kwenyama emva kokuhlanjwa; Wasikrazula engutyeni, nasesikhumbeni, nasengutyeni, nasengutyeni.', 'XHO22'),
(3, 13, 57, 'Xa uthe wabuya wabonakala engutyeni, nasempahleni, naseluswini, nasempahleni yonke yesikhumba; sisitshabalalisi esiqhelekileyo; yitshise ngomlilo loo nto ikuyo.', 'XHO22'),
(3, 13, 58, 'Kodwa isambatho okanye umsonto okanye umsonto okanye nantoni na yesikhumba ehlanjwayo, ukuba isibetho sithe sancothuka kuyo, yophinda ihlanjwe ihlanjwe.', 'XHO22'),
(3, 13, 59, 'Nguwo lowo ke umyalelo weqhenqa engutyeni yoboya, nelinen, nelinen, nentambo, nempahla yonke yesikhumba, wokuvakalisa ukuba iyinqambi, nokuba iyinqambi.', 'XHO22'),
(3, 14, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 14, 2, 'Lo woba ngumyalelo woneqhenqa ngomhla wokuhlanjululwa kwakhe: woziswa kumbingeleli;', 'XHO22'),
(3, 14, 3, 'Wophuma umbingeleli ngaphandle kweminquba, akhangele umbingeleli, O! akhangele umbingeleli, O! liphilile iqhenqa koneqhenqa;', 'XHO22'),
(3, 14, 4, 'Wawisa umthetho umbingeleli, wathi, Iintaka ezimbini eziphilileyo, ezihlambulukileyo, nomsedare, nezinto ezibomvu, nehisope, kulowo uzihlambululayo.', 'XHO22'),
(3, 14, 5, 'Woyalela umbingeleli ukuba kubingelelwe enye intaka embizeni yodongwe phezu kwamanzi aphilileyo.', 'XHO22'),
(3, 14, 6, 'Ke yona intaka ephilileyo, woyithabatha kunye nomsedare, nelaphu elibomvu, nehisope, azithi nkxu ndawonye nentaka ephilileyo egazini lentaka exhelelwe phezu kwamanzi aphilileyo;', 'XHO22'),
(3, 14, 7, 'Ufanele afefe phezu kwakhe ngento yokulihlambulula iqhenqa izihlandlo zibe sixhenxe, athi uhlambulukile, ayaluse intaka esendle.', 'XHO22'),
(3, 14, 8, 'Lowo uzihlambululayo wozihlamba iingubo zakhe, achebe zonke iinwele zakhe, ahlambe emanzini, ahlambuluke; emva koko uya kungena enkampini aze ahlale ngaphandle kwentente yakhe iintsuku ezisixhenxe iintsuku.', 'XHO22'),
(3, 14, 9, 'Kothi ngomhla wesixhenxe, azichebe zonke iinwele zakhe entloko, nasesilevini sakhe, nasemashiyini akhe, azichebe zonke iinwele zakhe, azichebe zonke iinwele zakhe, ahlambe iingubo zakhe, awuhlambe umzimba wakhe emanzini, wohlambuluka ke.', 'XHO22'),
(3, 14, 10, 'Ngomhla wesibhozo wosondeza iimvana ezimbini ezigqibeleleyo, nemvanazana emnyaka mnye egqibeleleyo, nezahlulo ezithathu zesishumi zomgubo ocoliweyo, ube ngumnikelo wokudla, ugalelwe ioli, neloge yeoli ibe nye;', 'XHO22'),
(3, 14, 11, 'Umbingeleli lowo uzicikidayo wowuzisa umphefumlo wakhe lowo, kunye nezi zinto, phambi koYehova emnyango wentente yokuhlangana.', 'XHO22'),
(3, 14, 12, 'Umbingeleli wothabatha imvana enye, ayilungise ibe ngumnikelo wetyala neloge yeoli, ayitshangatshangise ibe ngumnikelo otshangatshangiswayo phambi koYehova.', 'XHO22'),
(3, 14, 13, 'Woyibingelela imvana apho kunyuselwa khona idini lesono nedini elinyukayo, endaweni engcwele; ngokuba idini lesono lelikambingeleli, nedini letyala lingcwele kakhulu.', 'XHO22'),
(3, 14, 14, 'Wocaphula umbingeleli egazini ledini letyala, aliqabe umbingeleli esithinzini lendlebe yalowo uzihlolayo, nasesithupheni sesandla sakhe sasekunene, nakubhontsi wobhontsi wendlebe umnwe wedini letyala umlenze wakhe wasekunene:', 'XHO22'),
(3, 14, 15, 'Woyithabatha umbingeleli ioli entongeni, ayibeke entendeni yesandla sakhe sasekunene.', 'XHO22'),
(3, 14, 16, 'Awuthi nkxu umbingeleli umnwe wesandla sakhe sasekunene eolini esesandleni sakhe sasekhohlo aze afefe inxalenye yeoli ngomnwe wakhe izihlandlo ezisixhenxe phambi koYehova.', 'XHO22'),
(3, 14, 17, 'Ioli eseleyo esesandleni sakhe, umbingeleli woyiqaba esithinzini sendlebe yendoda yalowo uzihlambayo, nakusithupha wesandla sakhe sasekunene, nakubhontsi wonyawo lwakhe lwasekunene egazini ledini letyala.', 'XHO22'),
(3, 14, 18, 'Ioli eseleyo esandleni sombingeleli yogalelwa entlokweni yalowo uzihlambululayo; aze umbingeleli amcamagushele phambi koYehova.', 'XHO22'),
(3, 14, 19, 'Umbingeleli wosondeza umnikelo wesono aze azigubungele izono zalowo uzihlambululayo ekungcoleni kwakhe; emveni koko wolenza idini elinyukayo.', 'XHO22'),
(3, 14, 20, 'Umbingeleli wolenza idini elinyukayo, nomnikelo womlilo esibingelelweni, umbingeleli amcamagushele, ahlambuluke ke.', 'XHO22'),
(3, 14, 21, 'Kwaye ukuba ulihlwempu kwaye akanako ukuwubona loo mlinganiselo; aze athabathe imvana ibe nye njengomnikelo wetyala amcamagushele nesahlulo seshumi somgubo ocoliweyo, ogalelwe ioli, ube ngumnikelo otshiswayo neloge yeoli;', 'XHO22'),
(3, 14, 22, 'Nofumana amahobe amabini, nokuba ngamahobe amabini; elinye libe leledini lesono, elinye libe leledini elinyukayo.', 'XHO22'),
(3, 14, 23, 'Wolizisa ngomhla wesibhozo kumbingeleli emnyango wentente yokuhlangana, ukuba ahlanjululwe phambi koYehova;', 'XHO22'),
(3, 14, 24, 'Umbingeleli woyithabatha imvana yedini letyala, neloge yeoli, ezo nto umbingeleli azitshangatshangise zibe ngumtshangatshangiso phambi koYehova;', 'XHO22'),
(3, 14, 25, 'Wayixhela imvana yedini letyala, wathabatha umbingeleli egazini ledini letyala, walidyobha esithinzini sendlebe yalowo uzihlolayo, nasesithupheni sesandla sakhe sasekunene indlebe isandla nobhontsi wonyawo lwakhe lwasekunene;', 'XHO22'),
(3, 14, 26, 'Wogalela inxalenye yeoli umbingeleli entendeni yesandla sakhe sasekhohlo,', 'XHO22'),
(3, 14, 27, 'umbingeleli afefe ngomnwe wesandla sakhe sokunene ioli esesandleni sakhe sasekhohlo, izihlandlo zibe sixhenxe phambi koYehova;', 'XHO22'),
(3, 14, 28, 'Wothi ke umbingeleli ayibeke inxalenye yeoli esesandleni sakhe endlebeni yendlebe yalowo uvavanywayo, nasesithupheni sesandla sakhe sasekunene, nakubhontsi wonyawo lwakhe lwasekunene, nasesithupheni sesandla sakhe sasekunene, nakubhontsi wonyawo lwakhe lwasekunene indlebe yalowo uzicikidayo indawo igazi ledini letyala;', 'XHO22'),
(3, 14, 29, 'Ioli eseleyo esandleni sombingeleli yothanjiswa entlokweni yalowo umcamagushela phambi koYehova.', 'XHO22'),
(3, 14, 30, 'Makeze nelinye ihobe nehobe njengoko anako;', 'XHO22'),
(3, 14, 31, 'Kwanaloo nto angayifumanayo, elinye libe lidini lesono, elinye libe lidini elinyukayo, ndawonye nomnikelo wokudla; yaye umbingeleli umele amkhonze lowo uwuhlambululayo umnqophiso phambi koYehova.', 'XHO22'),
(3, 14, 32, 'Nguwo lowo umyalelo walowo uneqhenqa, osandla singayifumaniyo into yokuhlanjululwa kwakhe;', 'XHO22'),
(3, 14, 33, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(3, 14, 34, 'Xa uthe wafika ezweni lakwaKanan, endikunikayo ukuba libe lilifa lakho, ndalibeka iqhenqa endlwini yelo zwe ulihluthileyo.', 'XHO22'),
(3, 14, 35, 'Yaye umninindlu wathi kumbingeleli, ‘Ndiyabona ukuba kukho into eyenzekayo apha endlwini.', 'XHO22'),
(3, 14, 36, 'Wowisa umthetho umbingeleli, ukuba kukhutshwe izinto endlwini, engekangeni umbingeleli ukuba akhangele ukufa, ukuze ingabi yihlambulukileyo yonke into esendlwini; emveni koko wangena umbingeleli ukuba ayikhangele indlu.', 'XHO22'),
(3, 14, 37, 'Makakhangele ekufeni, aze abone ukuba ukufa kusezindongeni zendlu ngemisonto, eluhlaza okanye ebomvu, ebonakala phantsi kodonga;', 'XHO22'),
(3, 14, 38, 'Wophuma umbingeleli endlwini, aye emnyango wendlu, ayitshixe indlu imihla esixhenxe;', 'XHO22'),
(3, 14, 39, 'Wobuya umbingeleli ngomhla wesixhenxe akhangele, O!', 'XHO22'),
(3, 14, 40, 'Wathi umbingeleli makasuswe amatye okufa kwawo, aphoswe ngaphandle komzi endaweni eyinqambi;', 'XHO22'),
(3, 14, 41, 'Woyicamagushela indlu ngaphakathi macala onke, aluphose uthuli lwalo ngaphandle kwesixeko endaweni engahlambulukanga;', 'XHO22'),
(3, 14, 42, 'bathabatha amanye amatye, bawabeka endaweni yaloo matye; thatha enye isamente uyichithe indlu.', 'XHO22'),
(3, 14, 43, 'Xa kuthe kwabakho ukufa, kwabakho ke endlwini, emveni kokuvela kwamatye, nasemva kokuhluma kwendlu, nasemva kokubethwa kwayo;', 'XHO22'),
(3, 14, 44, 'wongena umbingeleli, akhangele, O! ayicocekanga.', 'XHO22'),
(3, 14, 45, 'Wayidiliza indlu namatye ayo, nemithi yayo, nemithi yonke yendlu; wabakhuphela ngaphandle komzi endaweni eyinqambi.', 'XHO22'),
(3, 14, 46, 'Nabani na othe wangena endlwini, ngalo lonke ixesha yokuvalwa kwayo, woba ngongahlambulukanga kude kuhlwe.', 'XHO22'),
(3, 14, 47, 'Lowo ulele kuloo ndlu, wozihlamba iingubo zakhe; nalowo udlele kuloo ndlu, wozihlamba iingubo zakhe.', 'XHO22'),
(3, 14, 48, 'Wangena umbingeleli, wakhangela, wabona ukuba akunwenwanga kufa endlwini emva kokwaluswa kwendlu; wothi umbingeleli ihlambuluke indlu, ngokuba isibetho siphilisiwe.', 'XHO22'),
(3, 14, 49, 'Makathabathe iintaka zibe mbini zokucoca indlu, nomsedare, nobomvu, nehisope;', 'XHO22'),
(3, 14, 50, 'Waxhela enye intaka empahleni yodongwe phezu kwamanzi omthombo;', 'XHO22'),
(3, 14, 51, 'Wathabatha ilogi yomsedare, nekiam, nevelvet ebomvu, nentaka ephilileyo, wayithi nkxu egazini lentaka exheliweyo nasemanzini omthombo, wayihlamba indlu izihlandlo ezisixhenxe.', 'XHO22'),
(3, 14, 52, 'Ufanele ayihlambulule indlu ngegazi lentaka nangegazi eliqukuqelayo nangentaka ephilayo nangomsedare nangehisope nangezinto ezibomvu.', 'XHO22'),
(3, 14, 53, 'Kodwa intaka ephilileyo mayiphume iye ngaphandle komzi, iye ezindle, iye kugubungela izono zendlu; yohlambuluka ke.', 'XHO22'),
(3, 14, 54, 'Nguwo lowo umyalelo walo lonke uhlobo lweqhenqa neqhenqa,', 'XHO22'),
(3, 14, 55, 'ngeqhenqa lengubo nelendlu;', 'XHO22'),
(3, 14, 56, 'Nokunyuka nokumhlophe kunye nebala elikhanyayo;', 'XHO22'),
(3, 14, 57, 'Imfundo ayihlambulukile, ayihlambulukile: Nguwo lo umyalelo weqhenqa.', 'XHO22'),
(3, 15, 1, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(3, 15, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa kuthe kwavuza incindi enyameni yakhe, woba neentloni ngenxa yokuvuza kwakhe.', 'XHO22'),
(3, 15, 3, 'Boba bobu ubunqambi bakhe ekumpompozeni kwakhe;', 'XHO22'),
(3, 15, 4, 'Zonke izilili alala kuzo umntu ovuzayo zoba ziinqambi; nento yonke asukuba ehlala phezu kwayo, yoba yinqambi.', 'XHO22'),
(3, 15, 5, 'Indoda esukuba isichukumisa isilili salowo, yozihlamba iingubo zayo, ihlambe emanzini, ibe yinqambi, ihlwise.', 'XHO22'),
(3, 15, 6, 'Lowo uhlala phezu kwayo nantoni na ahlala kuyo obhobhozayo, wozihlamba iimpahla zakhe, ahlambe umzimba emanzini aze abe ngongahlambulukanga kude kuhlwe.', 'XHO22'),
(3, 15, 7, 'Umntu osukuba echukumisa inyama yobhobhozayo, wozihlamba iingubo zakhe, ahlambe emanzini, abe yinqambi, ahlwise.', 'XHO22'),
(3, 15, 8, 'Ukuba ke onqwenela ukudla, lowo udlayo, nguye odlayo; wozihlamba iingubo zakhe, ahlambe emanzini, azihlambulule isono kude kuhlwe.', 'XHO22'),
(3, 15, 9, 'Zonke iisali asukuba ekhwela kuzo umntu onesali evuzayo, yoba yinqambi.', 'XHO22'),
(3, 15, 10, 'Bonke abo bachukumisa nantoni ibiphantsi kwakhe, boba ziinqambi, bahlwise;', 'XHO22'),
(3, 15, 11, 'Lowo umchukumisayo, ongazihlambanga izandla zakhe emanzini, wozihlamba iingubo zakhe, ahlambe emanzini, abe yinqambi, ahlwise.', 'XHO22'),
(3, 15, 12, 'Umhlaba onesikele uya kutyunyuzwa, yonke into eyenziwe ngomthi ihlanjwe ngamanzi.', 'XHO22'),
(3, 15, 13, 'Xa athe wahlanjwa emhlambini wakhe ohlambulukileyo, emhlambini wakhe; wozibalela iintsuku zibe sixhenxe zokuhlanjululwa kwakhe, azihlambe iingubo zakhe, awuhlambe umzimba wakhe emanzini aphilileyo;', 'XHO22'),
(3, 15, 14, 'Ngomhla wesibhozo wothabatha amavukuthu abe mabini okanye amavukuthu abe mabini, eze phambi koYehova emnyango wentente yokuhlangana, awanike umbingeleli;', 'XHO22'),
(3, 15, 15, 'awanikele umbingeleli, elinye libe lidini lesono, elinye libe lidini elinyukayo; aze umbingeleli amcamagushele phambi koYehova ngenxa yekhwele lakhe.', 'XHO22'),
(3, 15, 16, 'Xa kuthe kwaphuma amadlozi kuyo, yowuhlamba umzimba wayo wonke emanzini, ibe yinqambi, ihlwise.', 'XHO22'),
(3, 15, 17, 'Zonke iimpahla, nolusu lonke ephuma kulo imbewu, kufuneka zihlanjwe ngamanzi, zibe ziinqambi kude kuhlwe.', 'XHO22'),
(3, 15, 18, 'Ke umfazi ethe yalala naye indoda, bohlamba ngamanzi, babe ziinqambi bobabini, bahlwise bobabini.', 'XHO22'),
(3, 15, 19, 'Umfazi xa athe wampompoza, kwaba ligazi ukumpompoza kwakhe enyameni yakhe, makavalwe imihla esixhenxe; yaye wonk’ ubani enimchukumisayo woba ngongahlambulukanga kude kuhlwe.', 'XHO22'),
(3, 15, 20, 'Yonke into alala ngayo xa usexesheni, yoba yinqambi; nento yonke asukuba ehlala phezu kwayo, yoba yinqambi.', 'XHO22'),
(3, 15, 21, 'Indoda esukuba isichukumisa isilili salowo, yozihlamba iingubo zayo, ihlambe emanzini, ibe yinqambi, ihlwise.', 'XHO22'),
(3, 15, 22, 'Bonke abo bachukumisa nantoni ahlala phezu kwayo, bozihlamba iingubo zabo, bahlambe emanzini, babe ziinqambi, bahlwise.', 'XHO22'),
(3, 15, 23, 'Ukuba lisesililini, nokuba lisempahleni ahlala phezu kwayo, ukuba uthe wayichukumisa, woba yinqambi, ahlwise.', 'XHO22'),
(3, 15, 24, 'Ukuba ubani uthe walala nayo, laza ithambo layo liphezu kwayo, yoba ngongahlambulukanga iintsuku ezisixhenxe; nesilili sonke alala kuso soba yinqambi.', 'XHO22'),
(3, 15, 25, 'Umfazi xa athe wopha imihla emininzi, emveni komhla wokuhlambuluka kwakhe, nokuba lithe lagqithisa ekungcoleni kwakhe, nokuba lithe ladlula ithuba lokuba sexesheni lakhe; yonke imihla yokumpompoza kobunqambi bakhe yoba njengemihla yokubonakala kwakhe; woba yinqambi.', 'XHO22'),
(3, 15, 26, 'Wonke umandlalo athe walala kuwo yonke imihla yokubhobhoza kwakhe, woba njengomandlalo wabo; yaye yonk’ into ahlala kuyo iya kuba yenqambi njengencam yozipho lwakhe.', 'XHO22'),
(3, 15, 27, 'Nabani na ozichukumisayo ezo zinto woba yinqambi, ahlambe iingubo zakhe, ahlambe emanzini, abe ngongahlambulukanga kude kuhlwe.', 'XHO22'),
(3, 15, 28, 'Kodwa ukuba uhlambulukile ekuhambeni kwakhe, wozibalela imihla esixhenxe, aze emva koko ahlambuluke.', 'XHO22'),
(3, 15, 29, 'Ngomhla wesibhozo wothabatha amavukuthu abe mabini, nokuba ngamavukuthu, awazise kumbingeleli emnyango wentente yokuhlangana.', 'XHO22'),
(3, 15, 30, 'Umbingeleli wolenza elinye libe ngumnikelo wesono, elinye libe lidini elinyukayo; aze umbingeleli amcamagushele phambi koYehova ngenxa yekhwele lakhe.', 'XHO22'),
(3, 15, 31, 'Wobahlula ke oonyana bakaSirayeli ebunqambini babo; hleze bafe ngezono zabo, ngokuwenza inqambi umnquba wam ophakathi kwabo.', 'XHO22'),
(3, 15, 32, 'Nguwo lo umthetho walowo ubhobhozayo, nowophuma kuye oonyana, bazenza iinqambi;', 'XHO22'),
(3, 15, 33, 'nakumfazi owayesifa sisifo sakhe, nowabhobhozayo, endodeni, nakumfazi, nakulowo ulala nomfazi oyinqambi.', 'XHO22'),
(3, 16, 1, 'Wathetha uYehova kuMoses emveni kokufa koonyana ababini baka-Aron, ekubingelela kwabo phambi koYehova, baza bafa.', 'XHO22'),
(3, 16, 2, 'Wathi uYehova kuMoses, Yithi kuAron umkhuluwa wakho, Makangangeni engcweleni ngamaxesha onke, ngaphaya komkhusane, esisithelweni setyeya; hleze afe, kuba ndiya kubonakala efini phezu kwesigubungelo.', 'XHO22'),
(3, 16, 3, 'Wangena ke uAron engcweleni enenkunzi yenkomo yomnikelo wesono nenkunzi yegusha yomnikelo otshiswayo.', 'XHO22'),
(3, 16, 4, 'Uya kwambatha ingubo ende engcwele yelinen emhlophe, ibe nebhulukhwe yelinen emhlophe emzimbeni wakhe, abhinqiswe ngombhinqo welinen emhlophe, abhinqe unkontsho lwelinen emhlophe: ziingubo ezingcwele ezo. Ngoko ke kufuneka awuhlambe umzimba wakhe emanzini kwaye awunxibe ngolu hlobo.', 'XHO22'),
(3, 16, 5, 'Makathabathe ebandleni loonyana bakaSirayeli iinkunzi zezimvu zibe mbini, zibe lidini lesono, nenkunzi yemvu ibe nye, ibe lidini elinyukayo.', 'XHO22'),
(3, 16, 6, 'uAron makalungise inkunzi yakhe yenkomo yomnikelo wakhe wesono, acamagushele izono zakhe nezendlu yakhe.', 'XHO22'),
(3, 16, 7, 'Wathabatha izikhova zambini waza wazibeka phambi koYehova emnyango wentente yokuhlangana.', 'XHO22'),
(3, 16, 8, 'uAron wenza amaqashiso ngeenciniba zambini; elinye iqashiso libe lelikaYehova, elinye libe lelikaAtsale.', 'XHO22'),
(3, 16, 9, 'Woyizisa uAron ibhokhwe eliphume nayo iqashiso aze ayenze njengomnikelo wesono.', 'XHO22'),
(3, 16, 10, 'Ke yona ibhokhwe eliphume nayo iqashiso lebhokhwe ka-Asazele, yoyiziswa iphila phambi koYehova ukuze kucanyagushelwe kuyo ize iyikhulule njengombingelelo entlango.', 'XHO22'),
(3, 16, 11, 'uAron umele azise inkunzi yenkomo yomnikelo wakhe wesono aze acamagushele izono zakhe nezendlu yakhe aze ayixhele inkunzi yenkomo yomnikelo wakhe wesono.', 'XHO22'),
(3, 16, 12, 'Makathabathe ugcedevu luzele ngamalahle avuthayo esibingelelweni, phambi koYehova, azalise izandla zakhe ngesiqhumiso esitshiswayo, asingenise ngaphaya komkhusane;', 'XHO22'),
(3, 16, 13, 'Makabeke isiqhumiso phezu komlilo phambi koYehova, ukuze ilifu lesiqhumiso lisibekele isivalo soBungqina, ukuze angafi.', 'XHO22'),
(3, 16, 14, 'Wolithabatha igazi lenkunzi yenkomo, alitshize ngomnwe wakhe esigqumeni, asingise empumalanga; yaye phambi kwesihlalo sokucamagusha wafefa igazi ngomnwe wakhe izihlandlo ezisixhenxe.', 'XHO22'),
(3, 16, 15, 'Umele ayixhele ibhokhwe yomnikelo wesono wasesidlangalaleni, alizise igazi layo ngaphaya komkhusane, enze kwelo gazi njengoko enzileyo ngegazi lenkunzi yenkomo, aze atshize ngalo phezu kwesigubungelo naphambi kwenceba isihlalo:', 'XHO22'),
(3, 16, 16, 'wayicamagushela ingcwele ngenxa yemvukelo yoonyana bakaSirayeli, nangenxa yezikreqo zabo ngezono zabo zonke; enjenjalo kwintente yokuhlangana, ehleli phakathi kwabo phakathi kwemigquba yabo.', 'XHO22'),
(3, 16, 17, 'Makungabikho bani ententeni yokuhlangana ekungeneni kwakhe ukuze acamagushele engcweleni, ade aphume ukuze azicamagushele yena nendlu yakhe nebandla lonke likaSirayeli.', 'XHO22'),
(3, 16, 18, 'Makasondele esibingelelweni phambi koYehova, azigubungele izono zakhe; uthabathe egazini lenkunzi entsha yenkomo, nasegazini lenkunzi yebhokhwe, utshize ezimpondweni zesibingelelo;', 'XHO22'),
(3, 16, 19, 'Wofefa igazi ngomnwe wakhe izihlandlo ezisixhenxe, asihlambulule, asingcwalise, ebunqambini boonyana bakaSirayeli;', 'XHO22'),
(3, 16, 20, 'Akugqiba ukugubungela izono zengcwele nentente yokuhlangana nesibingelelo, umele azise ibhokhwe ephilileyo.', 'XHO22'),
(3, 16, 21, 'Wocinezela uAron izandla zakhe zozibini phezu kwentloko yenkunzi yebhokhwe ephilileyo, abuthethe phezu kwayo bonke ubugwenxa boonyana bakaSirayeli, nezikreqo zabo zonke eziphathelene nezono zabo zonke, ayibeke phezu kwentloko yenkunzi yebhokhwe exhonti, ayithumele endaweni yokuhlambela isandla sendoda elungileyo entlango;', 'XHO22'),
(3, 16, 22, 'Inkunzi leyo yebhokhwe yobuthwala ke phezu kwayo bonke ubugwenxa babo, iye ezweni elingenammi; ayikhulule inkunzi yebhokhwe leyo entlango apho.', 'XHO22'),
(3, 16, 23, 'Wangena uAron ententeni yokuhlangana, wazihluba iingubo zelinen entle abezakuzinxiba ekungeneni kwakhe engcweleni, wazishiya apho.', 'XHO22'),
(3, 16, 24, 'Wowuhlamba umzimba wakhe ngamanzi engcweleni, ambathe izambatho zakhe, aphume, enze idini lakhe elinyukayo nomnikelo otshiswayo wabantu, azicamagushele izono zakhe nezabantu;', 'XHO22'),
(3, 16, 25, 'Beka amanqatha edini lesono emlilweni ophezu kwesibingelelo;', 'XHO22'),
(3, 16, 26, 'Lowo wayikhululayo inkunzi yebhokhwe ka-Azazele, wozihlamba iimpahla zakhe, ahlambe umzimba wakhe emanzini, aze ke angene eminqubeni.', 'XHO22'),
(3, 16, 27, 'Ke inkunzi entsha yenkomo yedini lesono, nenkunzi yebhokhwe yedini lesono, ezigazi laziswe ukuba kuxolelwe engcweleni, wozikhupha ezo zinto engcweleni; Izikhumba zazo, nenyama yazo, nomswane wazo, baya kuzitshisa ngomlilo.', 'XHO22'),
(3, 16, 28, 'Lowo ulitshisileyo ufanele ahlambe iimpahla zakhe, ahlambe nomzimba wakhe emanzini, emva koko angene enkampini.', 'XHO22'),
(3, 16, 29, 'Lo woba ngummiselo ongunaphakade kuni: Ngenyanga yesixhenxe, ngolweshumi enyangeni leyo, noyicinezela imiphefumlo yenu, ningenzi namnye umsebenzi ngayo, nokuba ngowelizwe lenu, nokuba ndingowelizwe lakowenu, nokuba ndingowasemzini ohleli phakathi kwenu.', 'XHO22'),
(3, 16, 30, 'Umbingeleli uya kunicamagushela ngaloo mini, ukuba anihlambulule, nihlanjwe ezonweni zenu zonke phambi koYehova.', 'XHO22'),
(3, 16, 31, 'Mayibe yisabatha yophumlo kuni, niyixhwalekise imiphefumlo yenu ngokomyalelo omiswe ngonaphakade.', 'XHO22'),
(3, 16, 32, 'Umbingeleli womthambisa, amngcwalise ukuba abe ngumbingeleli esikhundleni sikayise, acamagushele izono, anxibe iingubo zelinen emhlophe, ezingcwele;', 'XHO22'),
(3, 16, 33, 'azigubungele izono zengcwele, acamagushele izono zentente yokuhlangana, nesibingelelo, agubungele izono zababingeleli, nezono zabantu bonke bebandla;', 'XHO22'),
(3, 16, 34, 'Woba ngumthetho wexesha elingenammiselo kuni ukucamagushela izono zoonyana bakaSirayeli kube kanye ngonyaka ngenxa yezono zabo zonke. Wenza njengoko uYehova wamwisela umthetho uMoses.', 'XHO22'),
(3, 17, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 17, 2, 'Thetha kuAron, koonyana bakhe, nakoonyana bonke bakaSirayeli, uthi kubo, Lilo eli ilizwi uYehova awise umthetho ngalo, ukuthi:', 'XHO22'),
(3, 17, 3, 'Umntu ngomntu wendlu kaSirayeli, xa athe waxhela inkomo, nemvana, netakane lebhokhwe emhadini, nokuba lixhelelwe emhadini,', 'XHO22'),
(3, 17, 4, 'Ungayizisi emnyango wentente yokuhlangana, ukuba usondeze umbingelelo kuYehova phambi kwentente kaYehova; igazi lobalelwa endodeni; uphalaze igazi; loo mphefumlo wonqanyulwa, ungabikho ebantwini bakowawo;', 'XHO22'),
(3, 17, 5, 'ngokuba oonyana bakaSirayeli boyizisa ezweni imibingelelo yabo, abayizisayo ezweni, ukuba bayizise kuYehova emnyango wentente yokuhlangana, kumbingeleli, bayizise ngoxolo iminikelo kuYehova.', 'XHO22'),
(3, 17, 6, 'Umbingeleli wolitshiza igazi layo esibingelelweni sikaYehova, emnyango wentente yokuhlangana, aqhumisele ngamanqatha, libe livumba elithozamisayo kuYehova.', 'XHO22'),
(3, 17, 7, 'Mabangabi sayibingelela imibingelelo yabo kwiidemon, abahenyuze kuzo; Woba ngummiselo ongunaphakade kubo ezizukulwaneni zabo.', 'XHO22'),
(3, 17, 8, 'Uze uthi kubo, Bonke bendlu kaSirayeli, nokuba ziintlanga eziphambukele phakathi kwenu, osukuba enyusa idini elinyukayo, nombingelelo;', 'XHO22'),
(3, 17, 9, 'uze ungalizisi emnyango wentente yokuhlangana, ukuba ulinikele kuYehova; kwanaloo mphefumlo wonqanyulwa, ungabikho ebantwini bakowawo.', 'XHO22'),
(3, 17, 10, 'Elowo wendlu kaSirayeli, nakubaphambukeli abaphambukele phakathi kwenu, osukuba esidla igazi nokuba yintoni na; Ndiya kuwujonga loo moya unxanelw’ igazi, ndiwunqumle phakathi kwabantu bawo.', 'XHO22'),
(3, 17, 11, 'Ngokuba umphefumlo wenyama usegazini wona. Mna ndaninika lona esibingelelweni, ukuba kucanyagushelwe imiphefumlo yenu; ngokuba ligazi elenza buthathaka umphefumlo.', 'XHO22'),
(3, 17, 12, 'Ngenxa yoko ndithe koonyana bakaSirayeli, Makungabikho namnye umphefumlo kuni odla igazi; nomphambukeli ophambukele phakathi kwenu makangalidli igazi.', 'XHO22'),
(3, 17, 13, 'Bonke koonyana bakaSirayeli, nakubaphambukeli phakathi kwenu, osukuba ezingela inyamakazi, nokuba yiyiphi intaka edliwayo, nokuba yiyiphi na, nokuba yiyiphi na, nokuba yiyiphi na, nokuba yiyiphi na, nokuba yiyiphi na, nokuba yiyiphi na, nokuba yiyiphi na into; Woliphalaza igazi layo, aliselele ngomhlaba.', 'XHO22'),
(3, 17, 14, 'Kuba obu bubomi benyama yonke; igazi layo ngumphefumlo wakhe; ngenxa yoko ndithe koonyana bakaSirayeli, Ize ningadli gazi lanyama nokuba yiyiphi; ngokuba umphefumlo wenyama yonke ligazi layo; Bonke abalidlayo bonqanyulwa.', 'XHO22'),
(3, 17, 15, 'Wonke ubani osukuba esidla into ezifeleyo, neqwengiweyo, engongosenzalwelweni nasengumphambukeli, wozihlamba iingubo zakhe, ahlambe emanzini, abe yinqambi, kude kube ngelo xesha, woba yinqambi icocekile.', 'XHO22'),
(3, 17, 16, 'Ukuba ke akathanga azihlambe, akawuhlamba umzimba wakhe; uya kubuthwala khona ubugwenxa bakhe.', 'XHO22'),
(3, 18, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 18, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 18, 3, 'Ize ningenzi ngokokwenza kwelizwe laseYiputa, enihleli kulo; ze ningenzi ngokwemisebenzi yelizwe lakwaKanan, endinisa kulo; ningahambi ngemimiselo yabo.', 'XHO22'),
(3, 18, 4, 'Yenzani imithetho yam, niyigcine imiyalelo yam, nihambe ngayo; NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 18, 5, 'Ngoko ke yigcineni imithetho yam, namasiko am; ukuba umntu uthe wenjenjalo, wohlala khona: ndinguYehova.', 'XHO22'),
(3, 18, 6, 'Makungabikho namnye kuni usondelayo kummelwane wakhe, ukuze atyhile ihlazo lakhe; NdinguYehova.', 'XHO22'),
(3, 18, 7, 'Musa ukulityhila ihlazo likayihlo, nehlazo likanyoko; Ndingunyoko; musa ukulibhengeza ihlazo lakhe.', 'XHO22'),
(3, 18, 8, 'Musa ukulityhila ihlazo lomfazi kayihlo; Ndineentloni ngoyihlo.', 'XHO22'),
(3, 18, 9, 'Emehlweni odade wenu, nasentombini kayihlo, nasentombini kanyoko, nokuba izalelwe emzini, nokuba kusemzini, uze ungalibheki ihlazo labo.', 'XHO22'),
(3, 18, 10, 'Musa ukulityhila ihlazo lentombi yonyana wakho, nentombi yentombi yakho, ngokuba lihlazo lakho.', 'XHO22'),
(3, 18, 11, 'Ihlazo lentombi yomkayihlo, eyazalelwa uyihlo, ingudade wenu lowo; musa ukulibhengeza ihlazo lakhe.', 'XHO22'),
(3, 18, 12, 'Musa ukulityhila ihlazo lodade boyihlo; Ndizalana kakhulu noyihlo.', 'XHO22'),
(3, 18, 13, 'Uze ungalityhili ihlazo lonyokokazi, ngokuba yinyama yonyoko leyo.', 'XHO22'),
(3, 18, 14, 'Uze ungalityhili ihlazo lomzalwana kayihlo, ungasondeli kumfazi wakhe; ngumakazi wakho.', 'XHO22'),
(3, 18, 15, 'Musa ukulityhila ihlazo lomolokazana wakho; ngumfazi wonyana wakho lowo; musa ukulibhengeza ihlazo lakhe.', 'XHO22'),
(3, 18, 16, 'Musa ukulityhila ihlazo lomfazi womzalwana wakho; shame usisi wakho.', 'XHO22'),
(3, 18, 17, 'Uhlazo lomfazi nentombi yakhe uze ungalityhili, nentombi yonyana wakhe, nentombi yentombi yakhe, uze ungayizeki, ukuba ubutyhile ihlazo layo; ngokuba bengamakholwane akhe;', 'XHO22'),
(3, 18, 18, 'Umfazi womntakwayo uze ungamthabathi, ukuze umthuthumbise, ukuze atyhile ihlazo lakhe phambi kwabantu ebomini bakhe.', 'XHO22'),
(3, 18, 19, 'Uze ungasondeli emfazini, esekungcoleni kwakhe, esekude nje ngenxa yokungcola kwakhe.', 'XHO22'),
(3, 18, 20, 'Ngaphezu koko, uze ungalali nomfazi wedlelane lakho ukuze ukwenze inqambi ngaye.', 'XHO22'),
(3, 18, 21, 'Embewuni yakho uze ungavumeli namnye ukuba amtshise uMoleki; ungalihlambeli igama loThixo wakho; NdinguYehova.', 'XHO22'),
(3, 18, 22, 'Uze ungayilali indoda njengokuba kulalwa umfazi: ngamasikizi lawo.', 'XHO22'),
(3, 18, 23, 'Nize ningalali nanye into ezitho zine, ukuba niyenze inqambi; inkazana mayingami ecaleni kwento ezitho zine, ukuze izekwe phezu kwayo; bubugqwirha.', 'XHO22'),
(3, 18, 24, 'Musani ukuzenza iinqambi ngezo nto zonke, kuba zenziwa iinqambi ngezo nto zonke iintlanga endizigqogqayo ebusweni benu;', 'XHO22'),
(3, 18, 25, 'Nehlabathi lingcolile; ke ngoko ndiya kubuvelela ubugwenxa babo, kwaye umhlaba uya kubatyhila abo bahleli kulo.', 'XHO22'),
(3, 18, 26, 'Gcinani imithetho yam, namasiko am, ningenzi nalinye kuloo masikizi; Makungabikho mntu wakowenu ophambukele kuni;', 'XHO22'),
(3, 18, 27, 'Kuba abantu belizwe ababekho phambi kwenu bawenza onke la masikizi, lenziwa inqambi ilizwe;', 'XHO22'),
(3, 18, 28, 'ukuze linganihlanzi ilizwe ekulenzeni kwenu inqambi, njengoko lizihlanzileyo iintlanga eziphambi kwenu.', 'XHO22'),
(3, 18, 29, 'Kuba bonke abasukuba besenza nalinye kuloo masikizi, oomoya abazenzayo ezo zinto, bonqanyulwa, bangabikho phakathi kwabantu bakowawo.', 'XHO22'),
(3, 18, 30, '“Ngoko ke ize niyigcine imithetho yam, ukuze ningenzi nanye kwezi zinto zicekisekayo ezenziwa phambi kwenu, ningazenzi iinqambi ngazo: ndinguYehova uThixo wenu.', 'XHO22'),
(3, 19, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 19, 2, 'Thetha kwibandla lonke loonyana bakaSirayeli, uthi kubo, Yibani ngcwele; ngokuba ndingcwele, mna Yehova, uThixo wenu.', 'XHO22'),
(3, 19, 3, 'Yoyikani ngamnye unina noyise nize nizigcine iisabatha zam: ndinguYehova uThixo wenu.', 'XHO22'),
(3, 19, 4, 'Musa ukutyeka kwizithixo, ungazenzeli uthixo othululwayo; NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 19, 5, 'Ukuba wenza umbingelelo wokubulela kuYehova, nowenza ngokuthanda kwakho;', 'XHO22'),
(3, 19, 6, 'Wodliwa ngomhla wokuwusondeza kwakho, nangengomso; ukuba ithe yahlala kwada kwaba lusuku lwesithathu, yotshiswa ngomlilo.', 'XHO22'),
(3, 19, 7, 'Ukuba kuthe kwadliwa ngomhla wesithathu, kuyimbozisa; ayiyi kwamkelwa.', 'XHO22'),
(3, 19, 8, 'Ke othe wayidla, wobuthwala ubugwenxa bakhe, ngokuba uyenze inqambi ingcwele kaYehova; loo moya wonqanyulwa, ungabikho ebantwini bakowawo.', 'XHO22'),
(3, 19, 9, 'Ekuvutheni kwenu isivuno selizwe lenu, uze ungavuni emagumbini omhlaba wakho, ungasivuni isivuno sakho.', 'XHO22'),
(3, 19, 10, 'Uze ungasikhi esidiliyeni sakho, ungavuni zonke iidiliya esidiliyeni sakho; makasale ke ngenxa yosizana nowasemzini: ndinguYehova, uThixo wenu.', 'XHO22'),
(3, 19, 11, 'Musani ukuba, yaye ningaqhathani, yaye ningakhohlisani.', 'XHO22'),
(3, 19, 12, 'Musani ukufunga ubuxoki egameni lam, ungalihlambeli igama loThixo wakho; NdinguYehova.', 'XHO22'),
(3, 19, 13, 'Musa ukumxokisa ummelwane wakho, ungamqhathi;', 'XHO22'),
(3, 19, 14, 'Musa ukuqalekisa isithulu, okanye ukhubeke phambi kwemfama, kodwa woyike uThixo wakho: ndinguYehova.', 'XHO22'),
(3, 19, 15, 'Musa ukwenza ububi enkundleni; musa ukububeka ubuso bosweleyo, ungabubeki buso basweleyo; mgwebe ngobulungisa ummelwane wakho.', 'XHO22'),
(3, 19, 16, 'Musani ukuhambahamba uyintlebi phakathi kwabantu bakowenu; uze ungalimisi igazi leewenu: ndinguYehova.', 'XHO22'),
(3, 19, 17, 'Musa ukubathiya umzalwana wakho entliziyweni yakho; nokuba ungade umkhalimele ummelwane wakho, ungazithwalisi sono kuye.', 'XHO22'),
(3, 19, 18, 'Musa ukuziphindezela, ungabathwali oonyana babantu bakowenu entliziyweni yakho; kodwa umthande ummelwane wakho njengoko uzithanda ngako. NdinguYehova.', 'XHO22'),
(3, 19, 19, 'Gcinani imithetho yam. Musa ukuyixhela imfuyo yakho ngeendlela ezahlukeneyo; uze ungahlwayeli mbewu engumxube emhlabeni wakho; nesambatho esixutywe nesilika noboya unganxitywa kuwe.', 'XHO22'),
(3, 19, 20, 'Xa athe wamlala inkazana elikhoboka, ebotshelelwe eyindoda, engekakhululwa ngentlawulelo, nokuba uthe wakhululwa ngentlawulelo; uya kubethwa; bangabulawa, ngokuba ebengekakhululeki.', 'XHO22'),
(3, 19, 21, 'Wolisondeza idini lakhe letyala kuYehova emnyango wentente yokuhlangana, inkunzi yemvu, ibe lidini letyala;', 'XHO22'),
(3, 19, 22, 'Umbingeleli wozigubungela izono zakhe ngenkunzi leyo yemvu yedini letyala phambi koYehova ngenxa yesono sayo, eyone ngaso; wosixolelwa ke isono sakhe.', 'XHO22'),
(3, 19, 23, 'Ukuba nithe nangena ehlabathini, natyala zonke iintlobo zemithi ukuba niyidle, nimele nizithabathe njengengekavuthwa iziqhamo zayo; iminyaka emithathu woba ubumenemene kuni; mayingadliwa.', 'XHO22'),
(3, 19, 24, 'Kodwa ngonyaka wesine zonke iziqhamo zayo ziya kuba ngcwele ukuze kudumise uYehova.', 'XHO22'),
(3, 19, 25, 'Ngomnyaka wesihlanu nozidla iziqhamo zayo, ukuze zinivelisele; NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 19, 26, 'Ize ningadli gazi;', 'XHO22'),
(3, 19, 27, 'Musani ukuzijika iintlontlo zentloko yenu, ningalaphuli isihlwitha seendevu zenu;', 'XHO22'),
(3, 19, 28, 'Musani ukuzigqalisela ngokwabafileyo, ningenzi nemiqondiso; NdinguYehova.', 'XHO22'),
(3, 19, 29, 'Musa ukukrexeza nentombi yakho, uyenze ihenyu; hleze ihlabathi liwele eburheletyweni, lizaliswe bubugwenxa.', 'XHO22'),
(3, 19, 30, 'Gcinani iisabatha zam, niyikhusele ingcwele yam: ndinguYehova.', 'XHO22'),
(3, 19, 31, 'Musani ukuyibazela imimoya yabafileyo, musani ukuhlambela ngamatola; NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 19, 32, 'Yima phambi kwengwevu, ububeke ubuso bexhego, umoyike uThixo wakho: ndinguYehova.', 'XHO22'),
(3, 19, 33, 'Xa athe waphambukela kuni ezweni lenu, ize ningamphazamisi.', 'XHO22'),
(3, 19, 34, 'Ke umphambukeli ophambukele kuni woba njengozalelwe phakathi kwenu, umthande ngoko uzithanda ngako; ngokuba naningabaphambukeli nani ezweni laseYiputa. NdinguYehova, uThixo wenu.', 'XHO22'),
(3, 19, 35, 'Ze ningenzi okusesikweni ekugwebeni, nokuba kukuphakama, nokuba kungakanani na, nokuba kungakanani na?', 'XHO22'),
(3, 19, 36, 'Woba nesikali sodwa, ne-efa elungileyo, nehin elungileyo; NdinguYehova uThixo wakho, onikhuphileyo ezweni laseYiputa.', 'XHO22'),
(3, 19, 37, 'Ize niyigcine ke imithetho yam yonke, nemithetho yam yonke, nenze oku: ndinguYehova.', 'XHO22'),
(3, 20, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 20, 2, 'Yithi koonyana bakaSirayeli, elowo koonyana bakaSirayeli, nakubaphambukeli abaphambukele kwaSirayeli, othe wamnika uMoleki inxalenye yoonyana bakhe; inene umelwe kukubulawa; abantu belo zwe bamgibisela ngamatye.', 'XHO22'),
(3, 20, 3, 'Ke mna ndiya kumkhangela loo mntu, ndimnqamle, angabikho phakathi kwabantu bakowabo; ngokuba ubanike oonyana bakhe kwimilingo, ukuba bayenze inqambi ingcwele yam, balihlambele igama lam elingcwele.', 'XHO22'),
(3, 20, 4, 'Ukuba abantu belizwe eli bathe bawafihla amehlo aloo mntu, ekumnikeni koonyana bakhe uMoleki, abambulala;', 'XHO22'),
(3, 20, 5, 'Ke mna ndiya kumchasa loo mntu nakwintsapho yakhe ndize ndimtshabalalise yena nabo bonke abahenyuza emva kwakhe ngokukrexeza noMoleki phakathi kwabantu bakowabo.', 'XHO22'),
(3, 20, 6, 'Yaye umoya ophethukela kwimimoya yabafileyo nakubathakathi ukuba uhenyuze emva kwabo, ndowujonga loo moya ndize ndiwunqumle ebantwini bakowawo.', 'XHO22'),
(3, 20, 7, 'Zingcwaliseni nibe ngcwele, ngokuba ndinguYehova uThixo wenu.', 'XHO22'),
(3, 20, 8, 'Gcinani imithetho yam, niyenze: ndinguYehova oningcwalisayo.', 'XHO22'),
(3, 20, 9, 'Umntu ngomntu, xa athe watshabhisa uyise nonina, wobulawa afe; utshabhise uyise nonina; igazi lakhe malibe phezu kwakhe.', 'XHO22'),
(3, 20, 10, 'Indoda esukuba ikrexeza umkamntu, ikrexeze nomfazi wayo, inene, loo mntu ukrexeza umkamkazi makabulawe afe.', 'XHO22'),
(3, 20, 11, 'Indoda elala umkayise iyalityhila ihlazo likayise; mababulawe bobabini; amagazi abo abe phezu kwabo.', 'XHO22'),
(3, 20, 12, 'Indoda esukuba imlala umolokazana wayo, mababulawe bafe bobabini; bazingcolisile; amagazi abo abe phezu kwabo.', 'XHO22'),
(3, 20, 13, 'Indoda esukuba iyilala indoda njengokuba kulalwa umfazi, abo benze amasikizi bobabini; mababulawe okunene; amagazi abo abe phezu kwabo.', 'XHO22'),
(3, 20, 14, 'Indoda esukuba izeka umfazi nonina, ngamanyala lawo; mabatshiswe ngomlilo, yona nabo; kungabikho manyala phakathi kwenu.', 'XHO22'),
(3, 20, 15, 'Indoda esukuba ilala inkomo, mayibulawe ife; nixhele inkomo.', 'XHO22'),
(3, 20, 16, 'Inkazana esukuba isondela entweni ezitho zine, yalala phezu kwayo, mayibulawe inkazana leyo; mababulawe okunene; amagazi abo abe phezu kwabo.', 'XHO22'),
(3, 20, 17, 'Indoda esukuba izeka udade wayo, intombi kayise, nokuba yintombi kanina, yalibona ihlazo lakhe, wabubona nodano lwakhe; yinto embi leyo; yaye baya kunqunyulwa phambi kwamehlo abantu bakowayo. Wabhenca ihlazo lodade wabo; uya kubuthwala ubugwenxa bakhe.', 'XHO22'),
(3, 20, 18, 'Xa indoda ithe yangena emandlalweni womfazi owafayo, yalityhila ihlazo lakhe; walityhila iqula lakhe, nequla legazi lakhe walityhila; banqunyulwe bobabini, bangabikho ebantwini bakowabo.', 'XHO22'),
(3, 20, 19, 'Ihlazo lonyokokazi nodade boyihlo uze ungalityhili, ngokuba umtyhilile ummelwane wakho; bobuthwala ubugwenxa babo.', 'XHO22'),
(3, 20, 20, 'Indoda esukuba ilala umkayisekazi, ityhila ubuze boyisekazi; bosithwala isono sabo; baya kufa bengenabantwana.', 'XHO22'),
(3, 20, 21, 'Indoda esukuba izeka umfazi womntakayise, iyinqambi; ulibhekile ihlazo lomzalwana wakhe; baya kufuna abantwana.', 'XHO22'),
(3, 20, 22, 'Ize niyigcine ke imithetho yam yonke, namasiko am onke, niwenze, ukuze linganihlanzi ilizwe elo ndinisa kulo ukuba nihlale kulo.', 'XHO22'),
(3, 20, 23, 'Musani ukuhamba ngeendlela zohlanga olo, endilugxothayo ebusweni benu; ngokuba bazenzile zonke ezo zinto, nam ndakhwebuka kubo.', 'XHO22'),
(3, 20, 24, 'Ndathi ke kuni, Nina niya kulidla ilifa ilizwe labo; ndininikila mna ukuba libe lilifa kuni, ilizwe elibaleka amasi nobusi; NdinguYehova uThixo wenu, onikhuphileyo ezintlangeni.', 'XHO22'),
(3, 20, 25, 'Yenzani ke umahluko phakathi kwehlambulukileyo neyinqambi, neyinqambi neentaka ezihlambulukileyo; ize ningayenzi imiphefumlo yenu ibe nezothe ngeenkomo, nangeentaka, nangenye inyakanyaka yonke endiyinyulileyo eyakho injengenqambi.', 'XHO22'),
(3, 20, 26, 'Niya kuba ngcwele kum, ngokuba mna, Yehova, ndingcwele, ndanahlula kwezinye iintlanga, ukuze nibe ngabam.', 'XHO22'),
(3, 20, 27, 'Indoda nenkazana onomoya wesiphithiphithi, nokuba ngumthakathi, wobulawa afe; babaxuluba ngamatye; amagazi abo abe phezu kwabo.', 'XHO22'),
(3, 21, 1, 'Wathi uYehova kuMoses, Thetha kubabingeleli, oonyana baka-Aron, uthi kubo, Makungabikho mntu uzenza inqambi ngofileyo ebantwini bakowabo.', 'XHO22'),
(3, 21, 2, 'Ngokuba ngenxa yendlu yakhe ekufuphi kuye, ngonina, noyise, nonyana wakhe, nentombi yakhe, nomzalwana wakhe,', 'XHO22'),
(3, 21, 3, 'nodade wabo, intombi ecaleni kwakhe, ingenandoda; angazenza inqambi ngaye.', 'XHO22'),
(3, 21, 4, 'Makangazenzi inqambi, ukuba uthe wangukumkani phakathi kwabantu bakowabo, ukuba amenze inqambi.', 'XHO22'),
(3, 21, 5, 'Mabangazichebi iintloko zabo, bangazichebi iincam zeendevu zabo, bangayichebi imizimba yabo.', 'XHO22'),
(3, 21, 6, 'Mababe ngcwele kuThixo wabo, bangalihlambeli igama loThixo wabo; ngokuba bezisa imibingelelo kaYehova etshiswa ngomlilo, nesonka soThixo wabo; ke ngoko bafanele ukuba ngcwele.', 'XHO22'),
(3, 21, 7, 'Makangazeki mfazi ulihenyukazi, nolihenyukazi; nowaliweyo makangazizeki, ngokuba ungcwele kuThixo wakhe.', 'XHO22'),
(3, 21, 8, 'Ngoko ke mngcwalise; ngokuba nguye ovelisa isonka soThixo wakho; woba ngcwele kuni; ngokuba ndingcwele mna, Yehova uningcwalisayo.', 'XHO22'),
(3, 21, 9, 'Intombi yombingeleli, xa ithe yazenza inqambi ngokukrexeza, imenza inqambi uyise; uya kutshiswa ngomlilo.', 'XHO22'),
(3, 21, 10, 'Kodwa lowo ungumbingeleli omkhulu phakathi kwabazalwana bakhe, ogalelwe ioli yokuthambisa entlokweni yakhe waza wangcwaliselwa ukuba anxibe izambatho, makangayithwali intloko aze azikrazule izambatho zakhe;', 'XHO22'),
(3, 21, 11, 'Uze angangeni nakuwuphi na umntu ofileyo, azenze inqambi ngoyise nokuba ngunina;', 'XHO22'),
(3, 21, 12, 'Makangaphumi engcweleni, angayihlambeli ingcwele yoThixo wakhe; ngokuba isithsaba seoli yokuthambisa yoThixo wakhe siphezu kwakhe: ndinguYehova.', 'XHO22'),
(3, 21, 13, 'Makazeke umfazi osebuntombini bakhe.', 'XHO22'),
(3, 21, 14, 'Makangazeki mhlolokazi, nowaliweyo, nowaliweyo, nolihenyukazi; wozeka intombi ebantwini bakowabo, ibe ngumfazi wayo.', 'XHO22'),
(3, 21, 15, 'Kwaye akabangcolisanga oonyana bakhe phakathi kwabantu bakhe, ngenxa yokuba mna, iNkosi, ndiyamngcwalisa.', 'XHO22'),
(3, 21, 16, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 21, 17, 'Thetha kuAron, uthi, Elowo wenzala yakho nakwimbewu yakhe, abanesafron, mabangasondeli, basondeze ukudla koThixo wabo.', 'XHO22'),
(3, 21, 18, 'Makungasondeli nabani na onesiphako: imfama, nesiqhwala, nonempumlo, nokuba yiyiphi na into embi, makangasondeli;', 'XHO22'),
(3, 21, 19, 'Okanye indoda eyaphukileyo umlenze okanye iingalo ezaphukileyo;', 'XHO22'),
(3, 21, 20, 'okanye onomqolo ogoso, noluthuthu, nonezimvimbi emehlweni, okanye onesithukuthezi, okanye otyumke umqala, okanye otyunyuzwe ngamatye;', 'XHO22'),
(3, 21, 21, 'Makungabikho bani ungekhoyo kumlibo wombingeleli uAron, onokusondela ukuze asondeze iminikelo etshiswayo kaYehova; unesepha; akasondeli ukuze azise ukudla koThixo wakhe.', 'XHO22'),
(3, 21, 22, 'Uya kudla isonka soThixo wakhe, sengcwele kangcwele, kwanabangcwele.', 'XHO22'),
(3, 21, 23, 'Ize ingangeni ngekhuselo, ingasondeli nesibingelelo, ngokuba inesiphako; ukuze angazihlambeli iingcwele zam, ngokuba ndinguYehova obangcwalisayo.', 'XHO22'),
(3, 21, 24, 'Wathetha uMoses kuAron noonyana bakhe, noonyana bonke bakaSirayeli.', 'XHO22'),
(3, 22, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 22, 2, 'Thetha kuAron nakoonyana bakhe, uthi, Zahluleni ezintweni ezingcwele zoonyana bakaSirayeli, ningalenzi inqambi igama lam elingcwele ngezinto abangcwalisele kum; NdinguYehova.', 'XHO22'),
(3, 22, 3, 'Yithi kubo, Bonke oonyana benu ezizukulwaneni zenu, abasukuba besondela ezintweni ezingcwele, abazingcwalisayo oonyana bakaSirayeli kuYehova, beseziinqambi, loo mphefumlo wonqanyulwa, ungabikho ebusweni bam. Nkosi.', 'XHO22'),
(3, 22, 4, 'Ngamnye koonyana baka-Aron uneqhenqa okanye uthunjana oluvuzayo; makangazidli izinto ezingcwele, ade ahlambuluke. Nabani na ochukumise into eyinqambi ngofileyo, nomntu ongowembewu yakhe;', 'XHO22'),
(3, 22, 5, 'Umntu osukuba echukumisa into yonke enyakazelayo, emenza inqambi, nokuba ngumntu ongazenzisiyo inqambi, nokuba yinto yonke eyinqambi anayo;', 'XHO22'),
(3, 22, 6, 'Umphefumlo ochukumise into enjalo uya kuba ngongahlambulukanga kude kuhlwe yaye awuyi kuzidla izinto ezingcwele ngaphandle kokuba uhlambe umzimba wakhe ngamanzi.', 'XHO22'),
(3, 22, 7, 'Emva kokutshona kwelanga uya kuba ehlambulukile, emva koko azidle izinto ezingcwele; kuba kukudla kwakhe.', 'XHO22'),
(3, 22, 8, 'Into efileyo nedliwayo makangayidli, azenze inqambi ngayo; NdinguYehova.', 'XHO22'),
(3, 22, 9, 'Ke ngoko wugcineni umthetho wam, bangazithwalisi sono, bafe ke, ukuba bathe bawuhlambela; NdinguYehova obangcwalisayo.', 'XHO22'),
(3, 22, 10, 'Makungabikho wasemzini udla ingcwele; umkhonzi wababingeleli, nokuba ngumsebenzi wemini, akayi kudla ndawo engcweleni.', 'XHO22'),
(3, 22, 11, 'Umbingeleli xa athe wathenga umntu, waba yintengo yemali yakhe, makadle kuyo, kwanozalelwe endlwini yakhe; baya kuyidla inyama yakhe.', 'XHO22'),
(3, 22, 12, 'Intombi yombingeleli, xa ithe yendela kwindoda yasemzini, mayingawudli umnikelo ongcwele.', 'XHO22'),
(3, 22, 13, 'Intombi yombingeleli xa ithe yangumhlolokazi, nokuba ithe yaqhawula umtshato, ingenamntwana, yabuyela endlwini kayise njengasebutsheni bayo, yodla ukudla kukayise; owasemzini makangayidli.', 'XHO22'),
(3, 22, 14, 'Ukuba ubani uthe wadla engcweleni, engazi, makongeze isahlulo sesihlanu sayo kuko oko, ayinike umbingeleli kunye nengcwele;', 'XHO22'),
(3, 22, 15, 'Abazingcwalisa izipho ezingcwele zoonyana bakaSirayeli, ababezizisa kuYehova;', 'XHO22'),
(3, 22, 16, 'Okanye banokuthwala izipho zabo ezingcwele kwaye bathwale inkohlakalo yesono; kuba mna, Yehova, ndibangcwalisayo.', 'XHO22'),
(3, 22, 17, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 22, 18, 'Thetha kuAron, nakoonyana bakhe, nakoonyana bonke bakaSirayeli, uthi kubo, Elowo kwindlu kaSirayeli, nakubaphambukeli bakwaSirayeli, yoba ngumsondezo wakhe otshangwa ngomlilo ngenxa yezibhambathiso zakhe zonke, nomnikelo wokudla otshiswayo ngomlilo ngenxa yezibhambathiso zakhe zonke, iminikelo yakhe yokuqhutywa yintliziyo, awayinikela kuYehova baya kwenza idini elinyukayo kuYehova;', 'XHO22'),
(3, 22, 19, 'Woyixhela inkunzi egqibeleleyo, inkomo, negusha, nebhokhwe, ngokuthanda kwakho.', 'XHO22'),
(3, 22, 20, 'Kodwa uze ungayibingeleli ngesepha, kuba ayisayi kwamkeleka kuwe.', 'XHO22'),
(3, 22, 21, 'Lowo usondeza umbingelelo wombulelo kuYehova, ukuba ube ngowesibhambathiso esibalulekileyo, okanye ube ngowokuqhutywa yintliziyo, oziinkomo noyimpahla emfutshane, ube ngogqibeleleyo ukuze wamkeleke; isenokungasweleki.', 'XHO22'),
(3, 22, 22, 'Into eyimfama, neyaphukileyo, neqhwala, nesisiqhwala, ize ningazisondezi ezo kuYehova, nedini lazo linyukayo kuYehova ningalinyusi esibingelelweni.', 'XHO22'),
(3, 22, 23, 'Nenkomo negusha enendawo eyolukileyo, neshwabeneyo, ungayenza umbingelelo wokuqhutywa yintliziyo; kodwa ngenxa yokuzinikela akuyi kwamkelwa.', 'XHO22'),
(3, 22, 24, 'Into eyaphukileyo, neyaphukileyo, neyaphukileyo, nanqunyulweyo, ize ningayisondezi kuYehova; nize ningawubingeleli umbingelelo wayo ezweni lenu.', 'XHO22'),
(3, 22, 25, 'Musa ukumnika esandleni solunye uhlanga isonka soThixo wakho; ngokuba ukonakala kwazo kukuzo, namanxeba azo zikuzo; aziyi kwamkeleka kuni.', 'XHO22'),
(3, 22, 26, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 22, 27, 'Inkomo, negusha, nebhokhwe, xa izalwayo, yohlala phantsi konina imihla esixhenxe; yaye ukususela kusuku lwesibhozo kuse phambili iya kwamkelwa njengomnikelo wasemlilweni kuYehova.', 'XHO22'),
(3, 22, 28, 'Enoba yinkomo okanye imazi, ize ningayixheli kwangamini-nye netakane layo.', 'XHO22'),
(3, 22, 29, 'Yaye ukuba nifuna ukwenza umnikelo wombulelo kuYehova, nikelani ngokuthanda kwenu.', 'XHO22'),
(3, 22, 30, 'Wodliwa kwangaloo mini; ungashiyi nto kuyo kude kuse. NdinguYehova.', 'XHO22'),
(3, 22, 31, 'Gcinani imithetho yam, niyenze: ndinguYehova.', 'XHO22'),
(3, 22, 32, 'Yaye akuzange ulithabathele kuwe igama lam elingcwele; ndongcwaliswa phakathi koonyana bakaSirayeli; NdinguYehova oningcwalisayo;', 'XHO22'),
(3, 22, 33, 'onikhuphileyo ezweni laseYiputa, ukuba ndibe nguThixo wenu: ndinguYehova.', 'XHO22'),
(3, 23, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 23, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Amaxesha amisiweyo kaYehova, eniya kuwavakalisa, ngamatheko angcwele;', 'XHO22'),
(3, 23, 3, 'Wosetyenzwa imihla emithandathu, ke umhla wesixhenxe yisabatha kasabatha, yintlanganiso engcwele kuni ningenzi namnye umsebenzi ngayo; yisabatha kaYehova ezindlwini zenu zonke.', 'XHO22'),
(3, 23, 4, 'Ngawo la amatheko kaYehova, iindibano ezingcwele, eniya kuzivakalisa ngexesha elifanelekileyo.', 'XHO22'),
(3, 23, 5, 'Ngosuku lweshumi elinesine enyangeni yokuqala, ngokuhlwa, yipasika kaYehova.', 'XHO22'),
(3, 23, 6, 'Ngosuku lweshumi elinesihlanu enyangeni leyo, ngumthendeleko wezonka ezingenagwele, umthendeleko wozuko lukaYehova; iintsuku ezisixhenxe nodla izonka ezingenagwele.', 'XHO22'),
(3, 23, 7, 'Ngosuku lokuqala noba yintlanganiso engcwele kuni; akukho msebenzi unokwenziwa kuyo.', 'XHO22'),
(3, 23, 8, 'Iintsuku ezisixhenxe nosondeza umsondezo kuYehova; ngomhla wesixhenxe yintlanganiso engcwele; ningenzi namnye umsebenzi ngayo.', 'XHO22'),
(3, 23, 9, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 23, 10, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa nithe nafika kulo ilizwe endininika lona, navuna isivuno senu, nizise kumbingeleli isipha seziqhamo zenu.', 'XHO22'),
(3, 23, 11, 'Uya kuhambela phambili kakhulu phambi koYehova, ukuze kwamkeleke kuni; umbingeleli wosilungiselela ngengomso emva kwesabatha.', 'XHO22'),
(3, 23, 12, 'Nize ninikele imvana emnyaka mnye, egqibeleleyo, ibe lidini elinyukayo kuYehova.', 'XHO22'),
(3, 23, 13, 'Umnikelo wakhe wasemlilweni woba zizahlulo ezibini zesishumi zomgubo ocoliweyo, ogalelwe ioli, ukudla kwasemlilweni, ivumba elithozamisayo kuYehova; umnikelo wakhe othululwayo woba yiwayini, isahlulo sesine sehin.', 'XHO22'),
(3, 23, 14, 'Uze ungadli sonka, nangqolowa igcadiweyo, nangqolowa ikrwada, ide ibe yimini yokuzisa kwakho idini kuThixo wakho; ngumyalelo ongunaphakade ezizukulwaneni zenu emakhayeni enu onke.', 'XHO22'),
(3, 23, 15, 'Zibalele kususela kwakusasa emva kwesabatha, kususela kwimini owazisa ngayo isithungu somnikelo otshangatshangiswayo; iisabatha ezisixhenxe mazigqitywe,', 'XHO22'),
(3, 23, 16, 'kude kube kusasa emva kwesabatha yesixhenxe babale iintsuku ezimashumi mahlanu; nize nizise umnikelo omtsha wasemlilweni kuYehova.', 'XHO22'),
(3, 23, 17, 'Emakhayeni enu maze nithabathe izonka ezibini zezahlulo zeshumi zibe zibini; ube ngumgubo ocolekileyo; kufuneka zibhakwe zinegwele; bangabakhonzi bakaYehova.', 'XHO22'),
(3, 23, 18, 'Wolungisa kunye nesonka eso, iimvana ezimnyaka mnye ezigqibeleleyo, zibe sixhenxe, nenkunzi entsha yenkomo, neenkunzi zezimvu zibe mbini; loba lidini elinyukayo kuYehova, nedini lazo elinyukayo, nomnikelo wazo othululwayo, nomnikelo wazo wokudla ngomlilo, livumba elithozamisayo eluzukweni lukaYehova.', 'XHO22'),
(3, 23, 19, 'Ize nenze enye inkunzi yemvu ibe lidini lesono, neemvana zibe mbini, zibe ngumbingelelo wombulelo;', 'XHO22'),
(3, 23, 20, 'Wozisondeza umbingeleli izonka eziziintlahlela, zibe ngumtshangatshangiso phambi koYehova, ndawonye neemvana ezimbini; yoba ngcwele kuYehova ngenxa yombingeleli.', 'XHO22'),
(3, 23, 21, 'Nadanduluka ngaloo mini, ukuba kubekho intlanganiso engcwele kuni; ningenzi namnye umsebenzi; ngumyalelo ongunaphakade emakhayeni enu onke ezizukulwaneni zenu.', 'XHO22'),
(3, 23, 22, 'Xa nithe naluvuna isivuno selizwe lenu, uze ungawacoci amacala elizwe lakho, ekuvuneni kwakho, ungaloni ukuvuna kwakho; Washiya amahlwempu nabasemzini. uYehova nguThixo wakho.', 'XHO22'),
(3, 23, 23, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 23, 24, 'Thetha koonyana bakaSirayeli, uthi, Ngenyanga yesixhenxe, ngolokuqala enyangeni leyo, yisabatha kuni, isikhumbuzo sokuvuthelwa kwezigodlo, yindibano engcwele.', 'XHO22'),
(3, 23, 25, 'Ize ningenzi namnye umsebenzi ngayo, nowenza ukudla kwasemlilweni kuYehova.', 'XHO22'),
(3, 23, 26, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 23, 27, 'Kwaye ngomhla weshumi kule nyanga yesixhenxe kuya kuba ngumhla woxolo; loba libandla elingcwele kuni; niyixhwalekise imiphefumlo yenu nize nisondeze umnikelo wasemlilweni kuYehova.', 'XHO22'),
(3, 23, 28, 'Ngaloo mini nize ningenzi namnye umsebenzi, kuba lusuku lokucamagushela izono zenu phambi koYehova uThixo wenu.', 'XHO22'),
(3, 23, 29, 'Kuba nabani na ongacinezelwayo ngaloo mini uya kunqunyulwa phakathi kwabantu bakowabo.', 'XHO22'),
(3, 23, 30, 'Wonke umphefumlo, osukuba usenza namnye umsebenzi ngaloo mini, ndowutshabalalisa loo mphefumlo, ungabikho phakathi kwabantu bawo.', 'XHO22'),
(3, 23, 31, 'Ize ningenzi namnye umsebenzi; ibe ngummiselo ongunaphakade ezizukulwaneni zenu emakhayeni enu onke.', 'XHO22'),
(3, 23, 32, 'Mayibe yisabatha kasabatha kuni, nicinezele imimoya yenu; ngomhla wesithoba enyangeni leyo, ngokuhlwa, ukususela ngokuhlwa kude kube ngokuhlwa, noyigcina isabatha yenu.', 'XHO22'),
(3, 23, 33, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 23, 34, 'Thetha koonyana bakaSirayeli, uthi, Ngosuku lweshumi elinesihlanu kule nyanga yesixhenxe, umthendeleko weminquba yimihla esixhenxe, owenzelwa uYehova.', 'XHO22'),
(3, 23, 35, 'Ngomhla wokuqala kobakho intlanganiso engcwele; ningenzi namnye umsebenzi ngayo.', 'XHO22'),
(3, 23, 36, 'Iintsuku ezisixhenxe nosondeza ukudla kwasemlilweni kuYehova; ngomhla wesithoba, amazinyo amabini, niya kuba nebandla elikhethekileyo; nize niwuthabathe umnikelo wasemlilweni kuYehova. Lixesha lokubhiyoza; ningenzi namnye umsebenzi ngayo.', 'XHO22'),
(3, 23, 37, 'Ngawo la amaxesha amisiweyo kaYehova, enowavakalisa abe yintlanganiso engcwele, nizise kuYehova idini elinyukayo, nedini elinyukayo, nomnikelo wokudla, nomnikelo wokudla, nomnikelo othululwayo, yonke loo mini ngemini yayo;', 'XHO22'),
(3, 23, 38, 'ngaphandle kweesabatha zikaYehova nangaphandle kwezipho zenu nangaphandle kwezibopho zenu zonke nangaphandle kweminikelo yenu yonke yokuzithandela eniyinika uYehova.', 'XHO22'),
(3, 23, 39, 'Kanjalo, ngomhla weshumi elinesihlanu enyangeni yesixhenxe, ekuhlanganiseni kwenu iziqhamo zelizwe, nenze umthendeleko wokumzukisa uYehova iintsuku ezisixhenxe.', 'XHO22'),
(3, 23, 40, 'Ngosuku lokuqala zishiyele amasebe emithi emihle, amasebe omthi womnquma namasebe emithi ekufuphi noMlambo; nibe nemihlali phambi koYehova uThixo wenu iintsuku ezisixhenxe.', 'XHO22'),
(3, 23, 41, 'Senze iintsuku ezisixhenxe ngonyaka njengomsitho wokuzukisa uYehova. Ngumyalelo ongunaphakade ezizukulwaneni zenu; niyenze ngenyanga yesixhenxe.', 'XHO22'),
(3, 23, 42, 'Nohlala iintsuku ezisixhenxe eminqubeni; bonke abazalelwe kwaSirayeli mabahlale eminqubeni;', 'XHO22'),
(3, 23, 43, 'ukuze zazi izizukulwana zenu ukuba ndabahlalisa ezintenteni oonyana bakaSirayeli ekubakhupheni kwam ezweni laseYiputa: ndinguYehova uThixo wenu.', 'XHO22'),
(3, 23, 44, 'uMoses wabazisa oonyana bakaSirayeli emithendelekweni kaYehova.', 'XHO22'),
(3, 24, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 24, 2, 'Bawisele umthetho oonyana bakaSirayeli, bazise kuwe ioli eqaqambileyo yeminquma, etshisiweyo yesibane, ukuze zihlale zivutha izibane.', 'XHO22'),
(3, 24, 3, 'Nganeno komkhusane wesingqino, ententeni yokuhlangana, wohlala ezicwangcisa uAron izibane phambi koYehova, kuthabathele ngokuhlwa kude kuse; ibe ngumyalelo ongunaphakade ezizukulwaneni zenu.', 'XHO22'),
(3, 24, 4, 'Uze ubeke izibane esiphathweni saso esihlambulukileyo phambi koYehova.', 'XHO22'),
(3, 24, 5, 'Thabatha umgubo onegwele, wenze ngawo izonka ezilishumi elinazibini; izahlulo zeshumi zibe zibini kumqhathane omnye.', 'XHO22'),
(3, 24, 6, 'Uze uzibeke zibe yimikrozo emibini, ibe mithandathu kukrozo ngalunye, phezu kwetafile ecocekileyo ephambi koYehova.', 'XHO22'),
(3, 24, 7, 'Beka isiqhumiso esisulungekileyo kwikholam nganye, ibe sisikhumbuzo eso phezu kwesonka, umnikelo wasemlilweni kuYehova.', 'XHO22'),
(3, 24, 8, 'Woyicwangcisa rhoqo isabatha phambi koYehova, ishenxisiwe koonyana bakaSirayeli ngomnqophiso wexesha elingenammiselo.', 'XHO22'),
(3, 24, 9, 'Ibe yeka-Aron noonyana bakhe; niyidle endaweni engcwele, kuba iyingcwele kangcwele kulowo uphuma ekudleni kwasemlilweni kukaYehova, ngokomyalelo ongunaphakade.', 'XHO22'),
(3, 24, 10, 'Kwafika unyana womSirayelikazi, onguyise engumYiputa, evela phakathi koonyana bakaSirayeli;', 'XHO22'),
(3, 24, 11, 'Unyana womfazi ongumSirayeli walinyelisa waza waliqalekisa igama likaYehova. Bamzisa kuMoses; Igama lonina belinguShelomiti, intombi kaDibri, wesizwe sakwaDan.', 'XHO22'),
(3, 24, 12, 'Bamfaka entolongweni, ukuze izicamango zeNkosi zaziswe kubo.', 'XHO22'),
(3, 24, 13, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(3, 24, 14, 'Mkhupheni phandle kweminquba lowo utshabhisileyo; bathi bonke abamvayo bacinezela ngezandla zabo phezu kwentloko yakhe, laza lonke ibandla lamxuluba ngamatye.', 'XHO22'),
(3, 24, 15, 'Thetha koonyana bakaSirayeli, uthi, Umntu ngomntu, xa athe wamtshabhisa uThixo wakhe, wosithwala isono sakhe.', 'XHO22'),
(3, 24, 16, 'Olithukayo igama likaYehova, wobulawa afe, limxulube ngamatye ibandla lonke; owasemzini ozalelwe kwelo zwe, xa athe walinyelisa igama likaYehova, wobulawa.', 'XHO22'),
(3, 24, 17, 'Othe wabulala umntu, wobulawa afe;', 'XHO22'),
(3, 24, 18, 'Obethe inkomo yafa, woyilungisa; isilwanyana ngesilwanyana.', 'XHO22'),
(3, 24, 19, 'Umntu xa athe wawisa ummelwane wakhe; njengoko enze ngako, makwenziwe ngokunjalo kuye;', 'XHO22'),
(3, 24, 20, 'iinyembezi ngenyembezi, iliso ngeliso, izinyo ngezinyo;', 'XHO22'),
(3, 24, 21, 'Obethe inkomo yafa, woyibuyisela; ke lowo ubuleleyo makabulawe.', 'XHO22'),
(3, 24, 22, 'Yibani mnye umyalelo kumphambukeli, nakomnye ezweni lakowenu; ngokuba ndinguYehova, uThixo wenu.', 'XHO22'),
(3, 24, 23, 'Wathi uMoses koonyana bakaSirayeli, basikhuphele isiqalekiso ngaphandle kweminquba, bamxulube ngamatye. Benza oonyana bakaSirayeli njengoko uYehova wamwisela umthetho uMoses.', 'XHO22'),
(3, 25, 1, 'Wathetha uYehova kuMoses entabeni yaseSinayi, wathi,', 'XHO22'),
(3, 25, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa nithe nafika kulo ilizwe endininika lona, ilizwe maligcine isabatha, ukumzukisa uYehova.', 'XHO22'),
(3, 25, 3, 'Wowahlwayela amasimi akho iminyaka emithandathu, usithene isidiliya sakho iminyaka emithandathu, uhlanganise iziqhamo zaso;', 'XHO22'),
(3, 25, 4, 'ke umnyaka wesixhenxe woba yisabatha yesabatha kulo ilizwe, isabatha kaYehova; musani ukuhlwayela amasimi enu;', 'XHO22'),
(3, 25, 5, 'Into ehlumelayo ekuvuneni kwakho uze ungayivuni, neediliya zesidiliya sakho uze ungazigawuli, ngokuba ngumnyaka wesabatha kulo ilizwe.', 'XHO22'),
(3, 25, 6, 'Kwaye isabatha yehlabathi iya kuba kukudla kwenu; ngenxa yakho, nekhoboka lakho, nekhobokazana lakho, nangenxa yomsebenzi wemihla ngemihla, nangenxa yomphambukeli wakho ophambukele kuwe;', 'XHO22'),
(3, 25, 7, 'Iinkomo zakho, nezinto ezizitho zine ezisezweni lakho, yonke imveliso yazo yoba kukudla.', 'XHO22'),
(3, 25, 8, 'Zibalele iisabatha ezisixhenxe zeminyaka, iminyaka esixhenxe; ixesha leesabatha zeminyaka esixhenxe liya kuba yiminyaka emashumi mane anesithoba kuni.', 'XHO22'),
(3, 25, 9, 'Ngolweshumi enyangeni yesixhenxe, novuthela isigodlo somnyaka wentlokoma; ngomhla wocamagushelo vuthelani isigodlo kulo lonke ilizwe lenu.', 'XHO22'),
(3, 25, 10, 'Nawungcwalisa umnyaka wamashumi omahlanu, navakalisa inkululeko ezweni lonke kubo bonke abemi balo; woba ngumnyaka wentlokoma kuni; nize nibuyele ngamnye elifeni lakhe, ngamnye kwintsapho yakhe.', 'XHO22'),
(3, 25, 11, 'Umnyaka wentlokoma kuni woba ngumnyaka wamashumi omahlanu; uze ungahlwayeli, ungavuni ummi wayo, ungawuthi nomdiliya wakho.', 'XHO22'),
(3, 25, 12, 'Ngokuba lo ngumnyaka wentlokoma; mayibe yinto engcwele kuni; niya kuyidla imveliso yayo endle.', 'XHO22'),
(3, 25, 13, 'Ngalo nyaka weNtlokoma nobuyela elowo ilifa lakhe.', 'XHO22'),
(3, 25, 14, 'Xa uthe wathengisa ngento ethile kummelwane wakho, nokuba uthe wathenga esandleni sommelwane wakho, musani ukucinezelana.', 'XHO22'),
(3, 25, 15, 'Zithenge kummelwane wakho ngokwenani leminyaka emva komnyaka wentlokoma, akuthenge ngokwenani leminyaka yeziqhamo;', 'XHO22'),
(3, 25, 16, 'Linyuse ixabiso layo ngokwenani leminyaka, nilihlise ixabiso layo ngokwenani leminyaka; kuba uthenga ngenani leminyaka yeziqhamo.', 'XHO22'),
(3, 25, 17, 'Musani ukucudisana; kodwa woyike uThixo wakho, kuba ndinguYehova uThixo wenu.', 'XHO22'),
(3, 25, 18, 'Ke ngoko, niya kuyenza imithetho yam, niyigcine imiyalelo yam, niyenze; yaye uya kuhlala ukhuselekile emhlabeni.', 'XHO22'),
(3, 25, 19, 'Ilizwe liya kuvelisa iziqhamo zalo, nidle nihluthe, nihlale kulo nikholosile.', 'XHO22'),
(3, 25, 20, 'Xa nithi, Sodla ntoni na ngomnyaka wesixhenxe? yabonani, asiyi kuhlwayela, singayihlanganisi indyebo yethu;', 'XHO22'),
(3, 25, 21, 'Ndiya kulisikelela ilizwe lakho ngomnyaka wesithandathu, livelise isiqhamo iminyaka emithathu.', 'XHO22'),
(3, 25, 22, 'Uhlwayela ngomnyaka wesibhozo, nidle iziqhamo ezindala kude kube ngumnyaka wesithoba; nodla ingqolowa endala.', 'XHO22'),
(3, 25, 23, 'Ilizwe ze kungathengiswa ngalo, libhange, kuba ilizwe lelam; ngokuba ningabasemzini, ningabaphambukeli kum.', 'XHO22'),
(3, 25, 24, 'Likhululeni ke elo lizwe kulo lonke ilizwe enilihluthileyo.', 'XHO22'),
(3, 25, 25, 'Xa athe umzalwana wakho wanzonza, wathengisa ngento yempahla yakhe, makeze owalakowabo, ayikhulule ngokuyihlawulela loo nto wathengisa ngayo umzalwana wakhe.', 'XHO22'),
(3, 25, 26, 'Ukuba ke umntu uthe akaba namntu wokuyikhulula, ethe waba namandla okukhululwa eyedwa;', 'XHO22'),
(3, 25, 27, 'Wobala iminyaka yokuthengisa kwakhe, aze okuseleyo akubuyisele kulowo wathengisa kuye; ukuba abuyele kwimpahla yakhe.', 'XHO22'),
(3, 25, 28, 'Ukuba akanako ukuyibuyisela kuye, loo nto ithengiswe ngayo yohlala esandleni salowo uyithengileyo, kude kube ngumnyaka wentlokoma; yophuma ngomnyaka wentlokoma, abuyele elifeni lakhe.', 'XHO22'),
(3, 25, 29, 'Umntu xa athe wathengisa ngendlu yokuhlala, isemzini onodonga, woyikhulula ngentlawulelo ude uphele umnyaka wokuthengiswa kwayo; ungekapheli unyaka angayikhulula.', 'XHO22'),
(3, 25, 30, 'Ukuba ithe ayishiywanga ungekapheli unyaka, indlu esesixekweni esibiyelweyo yomelela ngokungenammiselo kulowo wayithengayo ezizukulwaneni zakhe; ayisayi kuphuma ngomnyaka wentlokoma.', 'XHO22'),
(3, 25, 31, 'Ke izindlu zemizi engabiyelwe ngeendonga zobalelwa ekubeni yintsimi yelizwe; zingakhululwa ngentlawulelo, ziphume ngomnyaka wentlokoma.', 'XHO22'),
(3, 25, 32, 'Noko ke, imizi yabaLevi nezindlu zemizi yelifa labo, zoba nokukhululwa ngentlawulelo nanini kubaLevi.', 'XHO22'),
(3, 25, 33, 'Xa athe umntu wathenga kubaLevi indlu ekuthengiswe ngayo, umzi wophuma elifeni lakhe ngomnyaka weNtlokoma; ngokuba izindlu zemizi yabaLevi zililifa labo phakathi koonyana bakaSirayeli.', 'XHO22'),
(3, 25, 34, 'Idlelo lemizi yabo akuyi kuthengiswa ngalo; kuba bubutyebi babo obungunaphakade.', 'XHO22'),
(3, 25, 35, 'Xa athe umzalwana wakho wanzonza, woyela kuwe; myekeni; ewe, nokuba ikwindawo enye okanye kwenye; ukuze ahlale nawe.', 'XHO22'),
(3, 25, 36, 'Musa ukuthabatha nzala kuye, nokuba nzala; kodwa woyike uThixo wakho; ukuze udade wenu ahlale nawe.', 'XHO22'),
(3, 25, 37, 'Musa ukumnika imali yakho nzala, ungamniki nzala yemboleko yakho.', 'XHO22'),
(3, 25, 38, 'NdinguYehova uThixo wenu, onikhuphileyo ezweni laseYiputa, ukuze ndininike ilizwe lakwaKanan, ndibe nguThixo wenu.', 'XHO22'),
(3, 25, 39, 'Xa athe umzalwana wakho wanzonza ekuwe, wazithengisa kuwe; sukumnyanzela ukuba asebenze njengekhoboka.', 'XHO22'),
(3, 25, 40, 'Kodwa njengomsebenzi wosuku, umphambukeli woba nawe aze akukhonze de kube ngunyaka weNtlokoma.', 'XHO22'),
(3, 25, 41, 'Ngoku uya kukushiya wena nabantwana bakhe, abuyele entsatsheni yakhe, abuyele elifeni likayise;', 'XHO22'),
(3, 25, 42, 'Ngokuba ngabakhonzi bam abo, endibakhuphileyo ezweni laseYiputa; makungathengiswa ngawo ebukhobokeni.', 'XHO22'),
(3, 25, 43, 'Musa ukumlawula kalukhuni; kodwa ufanele umoyike uThixo wakho.', 'XHO22'),
(3, 25, 44, 'Isicakakazi sakho nezicakakazi zakho onazo zophuma ezintlangeni ezikungqongileyo; wothenga kuzo amakhoboka namakhobokazana.', 'XHO22'),
(3, 25, 45, 'Nothenga koonyana bolunye uhlanga, abaphambukele phakathi kwenu, kubo, nakwimbewu yabo abaphakathi kwenu, abayizalele ezweni lenu; kwaye kuya kuba bubutyebi bakho.', 'XHO22'),
(3, 25, 46, 'Lithabathe libe lilifa koonyana benu emva kwenu, ukuba libe lilifa labo; baya kuba ngamakhoboka akho ngonaphakade; kodwa kubazalwana benu, oonyana bakaSirayeli, musani ukulawulana.', 'XHO22'),
(3, 25, 47, 'Xa uthe watyeba kuwe umphambukeli, nokuba ngumphambukeli, wathi umzalwana wakho wanzonza ekuwe, wazithengisa kumntu wasemzini, nokuba ngumphambukeli ophambukele kuwe, nokuba kumzalwana wakho ophambukele endlwini yakho;', 'XHO22'),
(3, 25, 48, 'emva kokuba ethengisiwe, wothengwa kwakhona; omnye wabazalwana bakhe angamhlangula.', 'XHO22'),
(3, 25, 49, 'Uyisekazi okanye unyana kayisekazi angamkhulula, okanye nabani na ozalana naye angamkhulula; okanye ukuba unako, unokuzikhulula.', 'XHO22'),
(3, 25, 50, 'Wohlala kwalowo wamthengayo, athabathele emnyakeni wathengisa ngaye kuye, kuse emnyakeni wentlokoma; malibe ngokwenani leminyaka lokukhululwa kwakhe, ngokwexesha lomsebenzi wemihla ngemihla.', 'XHO22'),
(3, 25, 51, 'Ukuba isemininzi iminyaka, woyihlawulela intlawulelo yokukhululwa kwakhe, ngemali leyo yokuthengwa ngayo.', 'XHO22'),
(3, 25, 52, 'Ukuba kuthe kwasala iminyaka embalwa, ukuze kube ngumnyaka wentlokoma, wobala ngokweminyaka yakhe, ayibuyise imali yocamagusho lwakhe ngokweminyaka yakhe.', 'XHO22'),
(3, 25, 53, 'Uya kuba naye njengomsebenzi wemini nomnyaka, yaye akukho bani uya kumlawula ngokulukhuni phambi kwamehlo akho.', 'XHO22'),
(3, 25, 54, 'Ukuba uthe, akakhululwa ngentlawulelo ngabo abo, wophuma ngomnyaka wentlokoma, yena noonyana bakhe.', 'XHO22'),
(3, 25, 55, 'Ngokuba ngabakhonzi bam oonyana bakaSirayeli; ngabakhonzi bam ke endibakhuphileyo ezweni laseYiputa: ndinguYehova, uThixo wenu.', 'XHO22'),
(3, 26, 1, 'Uze ungazenzeli imifanekiso eqingqiweyo, nokuba ngumfanekiso oqingqiweyo, ungazenzeli umfanekiso oqingqiweyo; ngokuba ndinguYehova, uThixo wenu.', 'XHO22'),
(3, 26, 2, 'Gcinani iisabatha zam, niyikhusele ingcwele yam: ndinguYehova.', 'XHO22'),
(3, 26, 3, 'Ukuba nithe nahamba ngemimiselo yam, nayigcina imithetho yam, nayenza;', 'XHO22'),
(3, 26, 4, 'Ndiya kuninisa imvula ngexesha elifanelekileyo, ilizwe livelise iziqhamo zalo, nemithi yelizwe ivelise iziqhamo zayo.', 'XHO22'),
(3, 26, 5, 'Ukubhula kwenu kuya kufika ekuvuneni, ukuvuna kuya kufika ngexesha lokuhlwayela; yaye uya kudla isonka sakho uze waneliseke entliziyweni yakho, uhlale ukhuselekile ezweni lakho.', 'XHO22'),
(3, 26, 6, 'Ndiya kulinika uxolo ilizwe, ulale, kungabikho bani uningcangcazelisayo; Ndowatshabalalisa amarhamncwa ezweni, akusayi kucanda nentshuntshe ezweni lakho.', 'XHO22'),
(3, 26, 7, 'Niya kuzisukela iintshaba zenu, zowe likrele phambi kwenu.', 'XHO22'),
(3, 26, 8, 'Abahlanu kuni baya kubasukela ikhulu, ikhulu kuni lisukele ishumi lamawaka, ziwe likrele iintshaba zenu phambi kwenu.', 'XHO22'),
(3, 26, 9, 'Kuba ndiya kuninikela ingqalelo, ndinandise, ndinandise, ndiwumise umnqophiso wam nani.', 'XHO22'),
(3, 26, 10, 'Niya kudla amaqela amadala, nikhuphe amadala ngenxa entsha;', 'XHO22'),
(3, 26, 11, 'ndiwumise umnquba wam phakathi kwenu, nomoya wam awuyi kunidimaza.', 'XHO22'),
(3, 26, 12, 'Ndohamba phakathi kwenu, ndibe nguThixo wenu, nina nibe ngabantu bam.', 'XHO22'),
(3, 26, 13, 'NdinguYehova uThixo wenu, onikhuphileyo ezweni laseYiputa, ukuba ningabi ngamakhoboka awo; Ndazaphula iimbophelelo zedyokhwe yakho, ndakumisa wathi tye.', 'XHO22'),
(3, 26, 14, 'Ukuba nithe anandiphulaphula, anayenza le mithetho yonke;', 'XHO22'),
(3, 26, 15, 'Ukuba nithe nayicekisa imiyalelo yam emiselweyo, okanye ukuba umphefumlo wenu uyawenyanya izigwebo zam, kangangokuba anayenza yonke imiyalelo yam, nasuka nawaphula umnqophiso wam;', 'XHO22'),
(3, 26, 16, 'Nam ndiya kwenjenjalo nakuni; ndibeke phezu kwenu unkwantyo, nokufa, nezitshixo ezidla amehlo, ezibangele ubutya bentliziyo; yaye niya kuyihlwayelela ilize imbewu yenu, ngenxa yokuba ziya kuyidla iintshaba zenu.', 'XHO22'),
(3, 26, 17, 'Ke mna ndiya kunibona, nibulawe phambi kweentshaba zenu; iintshaba zakho ziya kukulawula; yaye niya kusaba ukuba akukho mntu unisukelayo.', 'XHO22'),
(3, 26, 18, 'Ukuba nithe anandiphulaphula, ndokongeza ukunithethisa kasixhenxe ngenxa yezono zenu.', 'XHO22'),
(3, 26, 19, 'Ndilaphule iqhayiya lamandla enu; izulu lenu ndilenze libe njengesinyithi, ilizwe lenu libe njengobhedu;', 'XHO22'),
(3, 26, 20, 'Amandla enu aya kuphela ngelize, ngenxa yokuba ilizwe lenu aliyi kuthwala isiqhamo salo, nemithi yelizwe ayiyi kuthwala isiqhamo sayo.', 'XHO22'),
(3, 26, 21, 'Ukuba nithe nahamba ngokundichasa, anandiphulaphula; Ndiza kunizisela izigwebo ezisixhenxe ezingakumbi ngokwezono zenu;', 'XHO22'),
(3, 26, 22, 'ndinithumele amarhamncwa elizwe, abathabathe abantwana benu, aqwenge neenkomo zenu, alinciphise inani lenu; neendlela zenu ziya kuba yintlango.', 'XHO22'),
(3, 26, 23, 'Ukuba anithanga niguquke nam kwezo zinto, nandichasa;', 'XHO22'),
(3, 26, 24, 'Ngoku nam ndiya kunichasa, ndinithethise kasixhenxe ngenxa yezono zenu;', 'XHO22'),
(3, 26, 25, 'Ndonizisela ikrele eliphindezela ityala lomnqophiso wam; ekuhlanganiseni kwenu emizini yenu, ndithumele kuni indyikitya yokufa; yaye uya kunikelwa esandleni sotshaba.', 'XHO22'),
(3, 26, 26, 'Ukuba ndithe ndaqhekeza intonga yakho, abafazi abalishumi bosoka isonka sakho ezikweni-nye, basibuyisele kuwe isonka sakho ngomlinganiselo wabo; niya kudla, ningahluthi.', 'XHO22'),
(3, 26, 27, 'Ukuba anithanga nindiphulaphule ezi zinto zonke, nandichasa;', 'XHO22'),
(3, 26, 28, 'Ngoku nam ndiya kuwususa umsindo wam kuni; yaye mna ndiya kunivelela izihlandlo ezisixhenxe ngenxa yezono zenu.', 'XHO22'),
(3, 26, 29, 'Yidleni inyama yoonyana benu, nidle inyama yeentombi zenu.', 'XHO22'),
(3, 26, 30, 'Ndiza kuzitshabalalisa iindawo zenu eziphakamileyo ndiyigawule imifanekiso yenu engcwele ndiziphose izidumbu zenu phezu kwezidumbu zemifanekiso yenu eqingqiweyo, yaye umoya wam uya kunicekisa.', 'XHO22'),
(3, 26, 31, 'Imizi yenu ndoyenza amanxuwa, ndizitshabalalise iingcwele zenu, ndingalivi ivumba lenu lokuthozamisa;', 'XHO22'),
(3, 26, 32, 'ilizwe ndilenze mna kube senkangala, iintshaba zenu ezimi kulo zimangaliswe;', 'XHO22'),
(3, 26, 33, 'Ndonichithachitha phakathi kweentlanga, ndinizingele ikrele, ilizwe lenu libe yintlango, nemizi yenu ibe ngamanxuwa.', 'XHO22'),
(3, 26, 34, 'Liya kwandula ke ilizwe lanelise iisabatha zalo ngemihla yokusenkangala kwalo, nina ke nisezweni leentshaba zenu; ke ilizwe liya kuphumla, laneliswe ziisabatha zalo.', 'XHO22'),
(3, 26, 35, 'Ukuba lisekho, liyaphumla; ngenxa yokuba ibingaphumli ngeesabatha zenu, ekunihlaleni kwenu kuyo.', 'XHO22'),
(3, 26, 36, 'Abasadla ubomi phakathi kwenu, ndothumela kube buhlungu ezintliziyweni zabo emazweni eentshaba zabo; isandi segqabi eliqwengayo siya kubasukela; basabe njengabasaba phambi kwekrele; bawe, kungekho ubasukelayo.', 'XHO22'),
(3, 26, 37, 'Ziya kuwa njengekrele, kungabikho ubasukelayo; yaye aniyi kuba namandla okuma phambi kweentshaba zenu.', 'XHO22'),
(3, 26, 38, 'Nobhubha phakathi kweentlanga, ilizwe leentshaba zenu linitshabalalise.', 'XHO22'),
(3, 26, 39, 'Abaseleyo kuni baya kudana ngenxa yobungendawo babo emazweni eentshaba zenu; nangenxa yobugwenxa booyise, nabo baya kubuna.', 'XHO22'),
(3, 26, 40, 'Bakubalisa ngobugwenxa babo, nobubi booyise, nobugwenxa babo ababenzayo kum, bandishiya kanjalo;', 'XHO22'),
(3, 26, 41, 'Ndaza kanjalo ndahamba ngokuchasene nabo ndaza ndabazisa elizweni leentshaba zabo; xa iintliziyo zabo ezonakeleyo zithotywe, baze ke bafumane isohlwayo senkohlakalo yabo;', 'XHO22'),
(3, 26, 42, 'ndowukhumbula umnqophiso wam noYakobi, nomnqophiso wam noIsake, nomnqophiso wam noAbraham; kwaye ndiya kucinga ngelizwe.', 'XHO22'),
(3, 26, 43, 'Ilizwe labo liya kushiywa laneliswe ziisabatha zalo, xa kuthe kwasenkangala, ababakho kulo;', 'XHO22'),
(3, 26, 44, 'Noko ke, ukuba basezweni leentshaba zabo, andiyi kubashiya, andiyi kubathiya, ndibavele ngesiqalekiso, ndaphule umnqophiso wam nabo; ngokuba ndinguYehova, uThixo wabo.', 'XHO22'),
(3, 26, 45, 'Kodwa ngenxa yabo ndiya kuwukhumbula umnqophiso wookhokho babo endawukhuphayo ezweni laseYiputa phambi kwamehlo eentlanga, ukuba ndibe nguThixo wabo: ndinguYehova.', 'XHO22'),
(3, 26, 46, 'Yiyo leyo imithetho, nemithetho, nemiyalelo, awayenzayo uYehova phakathi kwakhe noonyana bakaSirayeli, entabeni yaseSinayi, ngenkonzo kaMoses.', 'XHO22'),
(3, 27, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(3, 27, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa athe umntu wenza isifungo sasinye, woba ngokaYehova ngokomlinganiso wenu.', 'XHO22'),
(3, 27, 3, 'Ukulinganisela kwakho oyindoda makube kominyaka imashumi mabini ezelwe, kuse kominyaka imashumi mathandathu ezelwe, ukulinganisela kwakho makube ziishekele zesilivere ezimashumi mahlanu, ngokweshekele yengcwele.', 'XHO22'),
(3, 27, 4, 'Ukuba yinkazana, ixabiso lakho makube ziishekele ezimashumi mathathu.', 'XHO22'),
(3, 27, 5, 'Ukuba kuthabathele kumntwana ominyaka mihlanu, kuse kominyaka imashumi mabini, ixabiso lakho loyindoda libe ziishekele ezimashumi mabini, noyinkazana, makube ziishekele ezilishumi.', 'XHO22'),
(3, 27, 6, 'Ukuba kuthabathele kumntwana onyanga-nye, kwesa kumntwana ominyaka mihlanu, ixabiso lakho loyindoda loba ziishekele zesilivere ezintlanu;', 'XHO22'),
(3, 27, 7, 'Ukuba uminyaka imashumi mathandathu ezelwe kwenyusa; ukuba uyindoda, ukulinganisela kwakho makube ziishekele ezilishumi elinesihlanu;', 'XHO22'),
(3, 27, 8, 'Ke ukuba uthe wanzonza kunawe, makabonakale phambi kombingeleli, umbingeleli amlinganisele; umbingeleli amlinganise ngokwamandla akhe.', 'XHO22'),
(3, 27, 9, 'Ukuba sisisilwanyana esinikelwa ngumntu kuYehova, yonke into ayinikelayo kuYehova yoba ngcwele.', 'XHO22'),
(3, 27, 10, 'Ize ingatshintshi, ize ingananwa, elungileyo ngembi, embi ngelungileyo; ukuba uthe wanana ukunanisa inkomo ngenkomo, mayibe ngcwele yena kwanaleyo;', 'XHO22'),
(3, 27, 11, 'Ukuba sisilwanyana esingahlambulukanga angenako ukusibingelela kuYehova, eso silwanyana umele asizise kumbingeleli,', 'XHO22'),
(3, 27, 12, 'umbingeleli wokulinganisa phakathi kwelungileyo nembi; njengoko ulinganisa ngako umbingeleli, woba njalo.', 'XHO22'),
(3, 27, 13, 'Ukuba uthe wayikhulula okunene ngentlawulelo, makongeze isahlulo sesihlanu sexabiso laso.', 'XHO22'),
(3, 27, 14, 'Xa athe umntu wayingcwalisa indlu yakhe ukuba ibe ngcwele kuYehova, umbingeleli uya kugweba phakathi kwelungileyo nembi; njengoko walinganisa ngako umbingeleli, yoqiniseka ngokunjalo.', 'XHO22'),
(3, 27, 15, 'Ukuba lowo wayingcwalisayo uthe waphuma endlwini yakhe, makongeze isahlulo sesihlanu semali elinganiselweyo kuyo, ibe yeyakhe.', 'XHO22'),
(3, 27, 16, 'Ukuba umntu uthe wangcwalisa kuYehova isahlulo selizwe elilifa, silinganise ngokwembewu yalo; yoba ngamashumi amahlanu eeshekele zesilivere ngehomere yembewu yerhasi.', 'XHO22'),
(3, 27, 17, 'Ukuba uthe wayingcwalisa intsimi yakhe, ethabathela emnyakeni weNtlokoma, imele ihlale ngokomlinganiselo wakho.', 'XHO22'),
(3, 27, 18, 'Kodwa ukuba uthe wangcwalisa inxalenye yelizwe lakhe emva konyaka weNtlokoma, umbingeleli makayibale imali yakhe ngokweminyaka eseleyo kude kube ngumnyaka weNtlokoma, iphungulwe kumlinganiso wenu.', 'XHO22'),
(3, 27, 19, 'Ukuba lowo ulingcwalisayo ilizwe uthe walishiya nangayiphi na indlela, makongeze isahlulo sesihlanu sokulinganisela kwakho kuko okulinganiselweyo, liqiniselwe kuye.', 'XHO22'),
(3, 27, 20, 'Ukuba uthe akavuma ukuyikhulula inxalenye yentsimi leyo, nokuba uthe wathengisa ngenxalenye yentsimi leyo komnye umntu, mayingakhululwa ngentlawulelo.', 'XHO22'),
(3, 27, 21, 'Kodwa intsimi leyo ehlwayelweyo, ukuba ithe yaphuma ngomnyaka weNtlokoma, yoba ngcwele kuYehova, njengentsimi; ubutyebi bayo boba bombingeleli.', 'XHO22'),
(3, 27, 22, 'Ukuba umntu uthe wangcwalisa kuYehova intsimi, abeyithengile, kungekhona entsimini yelifa lakhe;', 'XHO22'),
(3, 27, 23, 'umbingeleli amnike inani elilinganiselweyo, kude kuse emnyakeni wentlokoma; yaye nimele ninikele umlinganiselo wenu ngaloo mini njengengcwele kaYehova.', 'XHO22'),
(3, 27, 24, 'Ngomnyaka weNtlokoma, isahlulo somhlaba siya kubuyela kulowo wasithengayo kuye, kumninisimi.', 'XHO22'),
(3, 27, 25, 'Konke ukulinganisela kwakho makube ngokweshekele yengcwele;', 'XHO22'),
(3, 27, 26, 'Ngamazibulo odwa eenkomo, aya kuba ngamazibulo kaYehova, angayi kungcwaliswa mntu; inokuba ziinkomo neegusha, zezeNkosi.', 'XHO22'),
(3, 27, 27, 'Ukuba yeyesilwanyana esingahlambulukanga, woyimisela ngokwexabiso elithelekelelwayo, aze ongeze isahlulo sesihlanu salo; okanye ukuba ayihlawuleki, yithengise ngohlahlo lwabiwo-mali lwakho.', 'XHO22'),
(3, 27, 28, 'Noko ke, nakuphi na ubutyebi asukuba umntu esondeza kuYehova, ezintweni zonke anazo, nokuba ngumntu, nokuba yinto ezizitho zine, nokuba yintsimi yendyebo yakhe, makungathengiswa ngayo, mayingakhululwa nangentlawulelo;', 'XHO22'),
(3, 27, 29, 'Bonke abangcwele abasayi kukhululwa ngentlawulelo; kodwa uya kubulawa.', 'XHO22'),
(3, 27, 30, 'Sonke isishumi somhlaba, sembewu yomhlaba, seziqhamo zomthi, sesikaYehova; ingcwele kuYehova.', 'XHO22'),
(3, 27, 31, 'Ukuba ubani uthe wathanda ukushiya nantoni na esishumini sakhe, makongeze isahlulo sesihlanu saso.', 'XHO22'),
(3, 27, 32, 'Ke sona isahlulo seshumi senkomo nesemazi yenkomo, kuyo yonke into edlula phantsi kwentonga, isishumi soba ngcwele kuYehova.', 'XHO22'),
(3, 27, 33, 'Ize angaqiniseki ukuba phakathi kwelungileyo nembi, angayanani; ayinakukhululwa ngentlawulelo.', 'XHO22'),
(3, 27, 34, 'Yiyo leyo imithetho uYehova uMoses awayiwisela oonyana bakaSirayeli, entabeni yaseSinayi.', 'XHO22'),
(4, 1, 1, 'Wathetha uYehova kuMoses entlango yaseSinayi, ententeni yokuhlangana, ngolokuqala lwenyanga yesibini, ngomnyaka wesibini wokuphuma kwabo ezweni laseYiputa, wathi:', 'XHO22'),
(4, 1, 2, 'Libale lonke ibandla loonyana bakaSirayeli, ngokwezindlu zabo, nangezindlu zooyise, ngenani lamagama abo, yonke into eyindoda ngokwezindlu zabo.', 'XHO22'),
(4, 1, 3, 'Bethabathela kominyaka imashumi mabini ezelwe, banyuse, bonke abaphuma umkhosi kwaSirayeli; wena noAron nibabale ngokwemikhosi yabo.', 'XHO22'),
(4, 1, 4, 'Uya kuhamba nendoda kwisizwe ngasinye; elowo njengentloko yendlu kayise.', 'XHO22'),
(4, 1, 5, 'Ngawo la amagama amadoda aya kuma nani: esizweni sakwaRubhen; uElitsure unyana kaSedeyure;', 'XHO22'),
(4, 1, 6, 'kuSimon; nguShelumiyeli unyana kaTsurishadayi;', 'XHO22'),
(4, 1, 7, 'kwaYuda; uNashon unyana ka-Aminadabhi;', 'XHO22'),
(4, 1, 8, 'eya kwaIsakare; uNataniyeli unyana kaTsuhare;', 'XHO22'),
(4, 1, 9, 'kwaZebhulon; uEliyabhi, unyana kaHelon;', 'XHO22'),
(4, 1, 10, 'koonyana bakaYosefu: nguEfrayim; uElishama, unyana ka-Amihudi, unyana kaManase; uGamaliyeli unyana kaPedasure;', 'XHO22'),
(4, 1, 11, 'kwaBhenjamin; nguAbhidan unyana kaGidiyon;', 'XHO22'),
(4, 1, 12, 'kwaDan; uAhiyezere unyana ka-Amisadayi;', 'XHO22'),
(4, 1, 13, 'kwaAshere; uPagiele, unyana kaOkran;', 'XHO22'),
(4, 1, 14, 'kwaGadi; uEliyasafu, unyana kaDehuweli;', 'XHO22'),
(4, 1, 15, 'kwaNafetali; uAhira, unyana kaEnan.', 'XHO22'),
(4, 1, 16, 'Zizo ezo iintloko zebandla, izikhulu zezindlu zooyise, iintloko zamawaka akwaSirayeli.', 'XHO22'),
(4, 1, 17, 'uMoses noAron bawathabatha la madoda abhaliweyo ngamagama.', 'XHO22'),
(4, 1, 18, 'Walibizela ndawonye lonke ibandla ngolokuqala usuku lwenyanga yesibini, babhala imizalwane yabo ngokwemizalwane yabo, nangemizalwane yabo, ngokwezindlu zooyise, ngenani lamagama, bethabathela kominyaka imashumi mabini ezelwe, banyuse; kulandela ukuba iibhokisi zabo zokuvota zaziwa.', 'XHO22'),
(4, 1, 19, 'Njengoko uYehova wamwiselayo umthetho uMoses, wababala entlango yaseSinayi.', 'XHO22'),
(4, 1, 20, 'Oonyana bakaRubhen, amazibulo kaSirayeli, ngokwezindlu zabo, nangemizalwane yabo, nangokwezindlu zooyise, nangokwezindlu zooyise, namagama abanyulwa babo, bephela, bengamadoda; ubudala nangaphezulu, bonke abanokuthi baye emfazweni;', 'XHO22'),
(4, 1, 21, 'ababalwayo kubo besizwe sakwaRubhen baba ngamashumi omane anamathandathu amawaka, anamakhulu mahlanu.', 'XHO22'),
(4, 1, 22, 'Koonyana bakaSimon, ekubhalwe kuzo ngokwemizalwane yabo, ngokweenzala zabo, nangokwezindlu zooyise, kwabhalwa ngenani lamagama abo, ngokwamagama abo, ekubhalwe kuwo yonke into eyindoda ukuqala kwam ndandineminyaka engamashumi amabini kwaye phezulu, yayingabo bonke ababenako ukuya emfazweni;', 'XHO22'),
(4, 1, 23, 'ababhaliswayo kubo besizwe sakwaSimon baba ngamashumi amahlanu anesithoba amawaka, ananye, anamakhulu mathathu.', 'XHO22'),
(4, 1, 24, 'Koonyana bakaGadi, ngokweentsapho zabo, ngokwenzala yabo, ngokwezindlu zooyise, ngenani lamagama, ukususela kwabanamashumi amabini eminyaka ubudala ukusa kophezulu, bonke abanokuthi bahambe;', 'XHO22'),
(4, 1, 25, 'ababalwayo kubo besizwe sakwaGadi, baba ngamashumi amane anamahlanu amawaka, anamakhulu mathandathu, anamanci mahlanu.', 'XHO22'),
(4, 1, 26, 'Koonyana bakaYuda ngokwemizalwane yabo, ngokweenzala zabo, nangezindlu zooyise, ngenani lamagama, bethabathela kominyaka imashumi mabini ezelwe, banyuse, bonke abanakho ukuphuma;', 'XHO22'),
(4, 1, 27, 'ababhaliswayo kubo kwisizwe sakwaYuda baba ngamashumi amathandathu anesine anamakhulu mathandathu amawaka.', 'XHO22'),
(4, 1, 28, 'Koonyana bakaIsakare ngokwemizalwane yabo, ngokweenzala zabo, nangezindlu zooyise, ngenani lamagama, bethabathela kominyaka imashumi mabini ezelwe, banyuse, bonke ababenokunduluka:', 'XHO22'),
(4, 1, 29, 'ababhaliswayo kubo besizwe sakwaIsakare baba ngamashumi amahlanu anesine amawaka, anamakhulu mane.', 'XHO22'),
(4, 1, 30, 'Koonyana bakaZebhulon, ngokweentsapho zabo, ngokwenzala yabo nangezindlu zooyise, ngenani lamagama, ukususela kwabanamashumi amabini eminyaka ubudala ukusa phezulu, bonke abaphumayo;', 'XHO22'),
(4, 1, 31, 'ababhaliswayo besizwe sakwaZebhulon baba ngamashumi amahlanu anesixhenxe amawaka, anamakhulu mane.', 'XHO22'),
(4, 1, 32, 'Oonyana bakaYosefu, oonyana bakaEfrayim, ngokwemizalwane yabo, nangemizalwane yabo, nangezindlu zooyise, ngenani lamagama, bethabathela kominyaka ilishumi ezelwe, banyuse, bonke abanakho ukuya emfazweni;', 'XHO22'),
(4, 1, 33, 'ababalwayo kubo besizwe sakwaEfrayim, baba ngamashumi amane amawaka, anamakhulu mahlanu.', 'XHO22'),
(4, 1, 34, 'Koonyana bakaManase ngokwemizalwane yabo, inzala yabo, nangezindlu zooyise, ngenani lamagama, bethabathela kominyaka imashumi mabini ezelwe, banyuse, bonke ababengabafuduswa;', 'XHO22'),
(4, 1, 35, 'ababhaliswayo kubo besizwe sakwaManase baba ngamashumi amathathu anesibini amawaka, anamakhulu mabini.', 'XHO22'),
(4, 1, 36, 'Koonyana bakaBhenjamin ngokweenzala zabo, ngokweenzala zabo, nangokwezindlu zooyise, amagama abo bonke abanokurhoxa, kwathabathela kominyaka imashumi mabini ezelwe, kwenyusa;', 'XHO22'),
(4, 1, 37, 'ababalwayo kubo besizwe sakwaBhenjamin, baba ngamashumi amathathu anamahlanu amawaka, anamakhulu mane.', 'XHO22'),
(4, 1, 38, 'Koonyana bakaDan, ngokweentsapho zabo, ngokwenzala yabo nangezindlu zooyise, ngenani lamagama, ukususela kwabanamashumi amabini eminyaka ubudala ukusa phezulu, bonke abo baphumayo;', 'XHO22'),
(4, 1, 39, 'ababhaliswayo kubo besizwe sakwaDan baba ngamashumi amathandathu anesibini amawaka, anamakhulu asixhenxe.', 'XHO22'),
(4, 1, 40, 'Koonyana baka-Ashere ngokwemizalwane yabo, nangemizalwane yabo, nangokwezindlu zooyise, amagama awo onke amadoda asukuba ephuma, ethabathela kominyaka imashumi mabini ezelwe, anyuse:', 'XHO22'),
(4, 1, 41, 'ababhaliswayo kubo besizwe sakwa-Ashere baba ngamashumi amane ananye amawaka, anamakhulu mahlanu.', 'XHO22'),
(4, 1, 42, 'Koonyana bakaNafetali ngokwemizalwane yabo, nangemizalwane yabo, ngokwemizalwane yabo, ngenani lamagama, bethabathela kominyaka imashumi mabini ezelwe, banyuse, bonke abaphumayo;', 'XHO22'),
(4, 1, 43, 'ababalwayo kubo besizwe sakwaNafetali, baba ngamashumi amahlanu anamathathu amawaka, anamakhulu mane.', 'XHO22'),
(4, 1, 44, 'Ngabo abo babalwayo, babalwayo nguMoses, noAron, nezikhulu zakwaSirayeli, ishumi elinababini; elowo ubengowendlu kayise.', 'XHO22'),
(4, 1, 45, 'Babalwa bonke oonyana bakaSirayeli ngokwezindlu zooyise, kwathabathela kominyaka imashumi mabini ezelwe, kwenyusa, bonke abaphuma umkhosi kwaSirayeli;', 'XHO22'),
(4, 1, 46, 'Bonke ababhaliswayo baba ngamakhulu amathandathu anesithathu amawaka, anamakhulu mahlanu, anamanci mahlanu.', 'XHO22'),
(4, 1, 47, 'Ke bona abaLevi, ngokwemilibo yooyise, ababalelwanga kubo.', 'XHO22'),
(4, 1, 48, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 1, 49, 'Kodwa isizwe sakwaLevi uze ungasibaleli, ungalibali inani laso phakathi koonyana bakaSirayeli.', 'XHO22'),
(4, 1, 50, 'Ke abaLevi ubabeke phezu komnquba wesingqino, neempahla zawo zonke, nento yonke ekuwo; bowuthwala umnquba, neempahla zawo zonke; uyikhonze, uyimise ngeenxa zonke emnqubeni.', 'XHO22'),
(4, 1, 51, 'Ekundulukeni komnquba, abaLevi bayidiliza; ekumisweni komnquba, bawumisa abaLevi; yaye umxube osondelayo mawubulawe.', 'XHO22'),
(4, 1, 52, 'Bazimisa ke oonyana bakaSirayeli iintente zabo, elowo nomkhosi wakhe, elowo neflegi yakhe, namaqela abo.', 'XHO22'),
(4, 1, 53, 'Ke abaLevi bomisa intente yabo beyijikeleza intente yesingqino, ukuze kungavuthwa burhalarhume phezu kwebandla loonyana bakaSirayeli; bagcine abaLevi umsebenzi womnquba wesingqino.', 'XHO22'),
(4, 1, 54, 'Benza oonyana bakaSirayeli kanye njengoko uYehova wamwiselayo umthetho uMoses, benjenjalo.', 'XHO22'),
(4, 2, 1, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(4, 2, 2, 'Bamisa oonyana bakaSirayeli ngokwebhanile yendlu yooyise; imise ngeenxa zonke kwintente yokuhlangana.', 'XHO22'),
(4, 2, 3, 'Ngasempumalanga, ngasekuphumeni kwelanga, bozimisa iibhanile zeminquba yakwaYuda, ngokwemikhosi yabo; yaye uNashon, unyana ka-Aminadabhi, uya kuba yinganga yoonyana bakaYuda.', 'XHO22'),
(4, 2, 4, 'Umkhosi wakhe nabakhonzi bakhe baba lishumi elinesine lamawaka, anamakhulu mathandathu.', 'XHO22'),
(4, 2, 5, 'Abomisa ngakuye sisizwe sakwaIsakare; yaye uNataniyeli unyana kaTsuhare uya kuba yinkokeli yoonyana bakaIsakare;', 'XHO22'),
(4, 2, 6, 'umkhosi wakhe, nababalwáyo kubo, ngamashumi omahlanu anesine amawaka, anamakhulu mane.', 'XHO22'),
(4, 2, 7, 'Indlu yakwaZebhulon noEliyabhi, unyana kaHelon, ngukumkani woonyana bakaZebhulon;', 'XHO22'),
(4, 2, 8, 'umkhosi wakhe, nababalwáyo kubo, ngamashumi omahlanu anesixhenxe amawaka, anamakhulu mane.', 'XHO22'),
(4, 2, 9, 'Bonke ke ababalelwa eminqubeni yakwaYuda likhulu elinamanci asibhozo anesithandathu samawaka, ngokwemikhosi yabo. Oku kuya kucaciswa kuqala.', 'XHO22'),
(4, 2, 10, 'Ngecala lasezantsi kwakukho ibhanile yendlu kaRubhen ngokwemikhosi yabo; Inkulu yoonyana bakaRubhen nguElitsure, unyana kaTedure;', 'XHO22'),
(4, 2, 11, 'umkhosi wakhe, nababalwáyo kubo, ngamashumi omané anesithandathu samawaka, anamakhulu mahlanu.', 'XHO22'),
(4, 2, 12, 'Abomisa ngakuye yoba sisizwe sakwaSimon; Inkulu yoonyana bakaSimon nguShelumiyeli, unyana kaTsurishadayi;', 'XHO22'),
(4, 2, 13, 'umkhosi wakhe, nabo babalwayo kuye, ngamashumi omahlanu anesithoba amawaka, anamakhulu mathathu.', 'XHO22'),
(4, 2, 14, 'Isizwe sakwaGadi; yaye uEliyasafu unyana kaRehuweli uya kuba yinkokeli yoonyana bakaGadi;', 'XHO22'),
(4, 2, 15, 'umkhosi wakhe, nabo bábhalwayo kuye, ngamashumi omane anesihlanu amawaka, anamakhulu mathandathu, anamanci mahlanu.', 'XHO22'),
(4, 2, 16, 'Bonke ke ababalelwa eminqubeni yakwaRubhen likhulu, elinamanci mahlanu amawaka, anawakanye, linamakhulu mane, anamanci mahlanu, ngokwemikhosi yabo. Kwaye baya kuthatha inyathelo kwinqanaba lesibini.', 'XHO22'),
(4, 2, 17, 'Yenyuka intente yokuhlangana ndawonye nomkhosi wabaLevi, phakathi kweminquba; njengoko bemisa iintente zabo, bonyuka ngokunjalo, elowo endaweni yakhe ngokwebhanile yakhe.', 'XHO22'),
(4, 2, 18, 'Ibhanile yeminquba yakwaEfrayim yayingasentshonalanga, ngokwamaqela abo; ukumkani woonyana bakaEfrayim nguElishama, unyana ka-Amihudi.', 'XHO22'),
(4, 2, 19, 'Umkhosi wakhe, abo babalwayo kubo, ngamashumi omane amawaka, anamakhulu mahlanu.', 'XHO22'),
(4, 2, 20, 'Kwaye kuya kubakho naye isizwe sakwaManase; Inkulu yoonyana bakaManase nguGamiyeli, unyana kaPedasure;', 'XHO22'),
(4, 2, 21, 'Umkhosi wakhe, nabo babalwayo kuye, ngamashumi omathathu anesibini amawaka, anamakhulu mabini.', 'XHO22'),
(4, 2, 22, 'Ize kulandele isizwe sakwaBhenjamin; yaye uAbhidan, unyana kaGidiyon, uya kuba yinganga yoonyana bakaBhenjamin.', 'XHO22'),
(4, 2, 23, 'Umkhosi wakhe, nabo babalwayo kubo, ngamashumi omathathu anesihlanu amawaka, anamakhulu mane.', 'XHO22'),
(4, 2, 24, 'Bonke ke ababalelwa eminqubeni yakwaEfrayim likhulu elinamanci asibhozo ananye lamawaka, ngokwemikhosi yabo. Kwaye baya kunyuka ukuya kwinqanaba lesithathu.', 'XHO22'),
(4, 2, 25, 'Ibhanile yendlu yakwaDan iya ngasentla, ngokwemikhosi yabo; yaye uAhiyezere unyana ka-Amishadayi uya kuba yinjengele yoonyana bakaDan.', 'XHO22'),
(4, 2, 26, 'Umkhosi wakhe nababhaliswayo kuwo ngamashumi omathandathu anesibini amawaka, anamakhulu asixhenxe.', 'XHO22'),
(4, 2, 27, 'Abo bamisa naye yoba sisizwe sakwa-Ashere; Inkulu yoonyana baka-Ashere nguPagiyeli, unyana kaOkran;', 'XHO22'),
(4, 2, 28, 'Umkhosi wakhe nababhaliswayo kuwo ngamashumi omane amawaka, anawaka-nye, anamakhulu mahlanu.', 'XHO22'),
(4, 2, 29, 'Nesizwe sakwaNafetali; yaye uAhira, unyana kaEnan, uya kuba yinganga yoonyana bakaNafetali;', 'XHO22'),
(4, 2, 30, 'umkhosi wakhe, nababuthiweyo bawo, ngamashumi omahlanu anesithathu samawaka, anamakhulu mane.', 'XHO22'),
(4, 2, 31, 'Bonke ke ababalelwa eminqubeni yakwaDan likhulu elinamanci mahlanu anesixhenxe amawaka, anamakhulu mathandathu. Baya kubuyela emva kwimilinganiselo yabo.', 'XHO22'),
(4, 2, 32, 'Ngabo ke aba babalwayo koonyana bakaSirayeli, ngokwezindlu zooyise; bonke ababalelwa eminqubeni ngokwemikhosi yabo ngamakhulu omathandathu anesithathu samawaka, anamakhulu mahlanu, anamanci mahlanu.', 'XHO22'),
(4, 2, 33, 'Ke abaLevi ababalelwanga phakathi koonyana bakaSirayeli; njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(4, 2, 34, 'Benza oonyana bakaSirayeli kanye njengoko uYehova wamyalelayo uMoses, bayimisa inkampu ngokwendwe yabo, bahamba ke, ngamnye neentsapho zabo nezindlu zooyise.', 'XHO22'),
(4, 3, 1, 'Ngabo aba oonyana baka-Aron noMoses, mini wathetha uYehova kuMoses entabeni yaseSinayi.', 'XHO22'),
(4, 3, 2, 'Ngawo la amagama oonyana baka-Aron; Owamazibulo nguNadabhi, noAbhihu, noElazare, noItamare.', 'XHO22'),
(4, 3, 3, 'Ngawo lawo amagama oonyana baka-Aron, ababingeleli abathanjisiweyo, awabamiselayo ukuba babe ngababingeleli.', 'XHO22'),
(4, 3, 4, 'Bafa ooNadabhi noAbhihu phambi koYehova ekusoseni kwabo umlilo ongesesikweni phambi koYehova, entlango yaseSinayi; yaye uEleyazare noItamare baba ngababingeleli emehlweni ka-Aron uyise.', 'XHO22'),
(4, 3, 5, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 3, 6, 'Sisondeze isizwe sakwaLevi, usimise phambi koAron umbingeleli, ukuba balungiselele kuye.', 'XHO22'),
(4, 3, 7, 'Bawugcina umsebenzi wakhe, nenkonzo yebandla lonke phambi kwentente yokuhlangana, ukuba basebenze umsebenzi wentente;', 'XHO22'),
(4, 3, 8, 'uzigcine zonke iimpahla zentente yokuhlangana, nenkonzo yoonyana bakaSirayeli, ukuba bawufeze umsebenzi wenkonzo yentente;', 'XHO22'),
(4, 3, 9, 'AbaLevi wabanikela uAron noonyana bakhe; zanikwa yena phakathi koonyana bakaSirayeli.', 'XHO22'),
(4, 3, 10, 'Uze unyule uAron noonyana bakhe, babe ngababingeleli; yaye umxube osondelayo mawubulawe.', 'XHO22'),
(4, 3, 11, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 3, 12, 'Yabona, ndathabatha abaLevi phakathi koonyana bakaSirayeli esikhundleni samazibulo onke avula isizalo phakathi koonyana bakaSirayeli; ke ngoko, abaLevi baya kuba ngabam;', 'XHO22'),
(4, 3, 13, 'Ngokuba onke amazibulo ngawam; Ekubaxabeleni kwam onke amazibulo ezweni laseYiputa, ndawanyula onke amazibulo kwaSirayeli, ethabathela emntwini esa enkomeni; yoba yeyam: ndinguYehova.', 'XHO22'),
(4, 3, 14, 'Wathetha uYehova kuMoses entlango yaseSinayi, esithi,', 'XHO22'),
(4, 3, 15, 'Babale oonyana bakaLevi ngokwemizalwane yabo, ngokweenzala zabo;', 'XHO22'),
(4, 3, 16, 'Wawabala ke uMoses ngokomlomo kaYehova, njengoko wamwiselayo umthetho.', 'XHO22'),
(4, 3, 17, 'Ngabo abo oonyana bakaLevi ngokwamagama abo; uGershon noKehati noMerari.', 'XHO22'),
(4, 3, 18, 'Ngawo lawo amagama oonyana bakaGershon ngokwemizalwane yabo; uLibheni noShimehi.', 'XHO22'),
(4, 3, 19, 'Oonyana bakaKehati ngokwemizalwane yabo; nguAmram noIsakare, nguHebron noUziyeli.', 'XHO22'),
(4, 3, 20, 'Oonyana bakaMerari ngokwemizalwane yabo; uMahli kunye noMusi. Yiyo leyo imizalwane yabaLevi, ngokwezindlu zabo.', 'XHO22'),
(4, 3, 21, 'KwaGershon kwakukho intsapho yakwaLibhene nentsapho kaBhashim; Yiyo le ke inzala yamaGershon.', 'XHO22'),
(4, 3, 22, 'Ababhaliswayo ngenani lawo, yonke into eyindoda, ethabathela kunyana onyanga-nye inyuse: ngamawaka asixhenxe, anamakhulu mahlanu.', 'XHO22'),
(4, 3, 23, 'Imizalwane yakwaGershon yayimise intente ngasemva komnquba ngasentshonalanga.', 'XHO22'),
(4, 3, 24, 'Inkulu yendlu yooyise kaGershon nguEliyasafu, unyana kaLayeli.', 'XHO22'),
(4, 3, 25, 'Inkonzo yoonyana bakaGershon ententeni yokuhlangana yoba ngumnquba, nentente, nesigubungelo sayo, nesisitheliso esisingisele emnyango wentente yokuhlangana;', 'XHO22'),
(4, 3, 26, 'namalengalenga entendelezo, nesisitheliso somnyango wentendelezo, ephambi komnquba, phambi kwesibingelelo, nezintya zayo zomsebenzi wawo wonke.', 'XHO22'),
(4, 3, 27, 'KwaKehati kwakukho intsapho yama-Amram nentsapho yamaIshhare nendlu yamaHebhere nentsapho yamaBhawusi; yiyo leyo imizalwane yamaHeti.', 'XHO22'),
(4, 3, 28, 'Ngenani, into yonke eyindoda, ethabathela kunyana onyanga-nye inyuse, ngamawaka osibhozo, anamakhulu mathandathu, abephethe inkonzo yengcwele.', 'XHO22'),
(4, 3, 29, 'Imizalwane yoonyana bakaKehati yomisa intente ngecala lomnquba elingasezantsi;', 'XHO22'),
(4, 3, 30, 'Inkulu yendlu yooyise ngokwemizalwane yamaHeti nguElishafan, unyana kaUziyeli;', 'XHO22'),
(4, 3, 31, 'Umsebenzi wabo yityeya, netafile, nesiphatho sezibane, nezibingelelo, neempahla zengcwele abalungiselela ngazo, nesisitheliso, nomsebenzi wawo wonke.', 'XHO22'),
(4, 3, 32, 'Inkulu yezikhulu zakwaLevi nguElazare, unyana wombingeleli uAron, abavelele abagcina inkonzo yengcwele.', 'XHO22'),
(4, 3, 33, 'KwaMerari kwakukho intsapho yakwaMagali nentsapho yakwaMushi; Yiyo leyo imizalwane yakwaMerari.', 'XHO22'),
(4, 3, 34, 'Ababhaliswayo ngenani lawo, yonke into eyindoda, ethabathela kunyana onyanga-nye ukuya phezulu, baba ngamawaka amathandathu, anamakhulu mabini.', 'XHO22'),
(4, 3, 35, 'Inkulu yendlu yooyise ngokwemizalwane yakwaMerari nguSuriyeli, unyana ka-Abhihayili; yomisa iintente ngecala lomnquba elingasentla.', 'XHO22'),
(4, 3, 36, 'Ngaphantsi kwesandla soonyana bakaMerari, babenyamekela iintsika zomnquba nemiqadi yawo, neentsika zawo, nezihlalo zawo, nemisebenzi yawo yonke, nemisebenzi yawo yonke,', 'XHO22'),
(4, 3, 37, 'neentsika zentendelezo ngeenxa zonke, neziseko zazo, nezikhonkwane zazo, nezintya zazo.', 'XHO22'),
(4, 3, 38, 'Abomisa iintente phambi komnquba ngasempumalanga, phambi kwentente yokuhlangana, nguMoses, noAron, noonyana bakhe, abaphethe umsebenzi wengcwele, enkonzweni yoonyana kwaSirayeli; yaye umxube osondelayo mawubulawe.', 'XHO22'),
(4, 3, 39, 'Bonke ke abaLevi ababalwayo, ababalwayo nguMoses noAron ngokomlomo kaYehova, ngokweenzala zabo, yonke into eyindoda, ethabathela kunyana onyanga-nye inyuse, baba ngamashumi amabini amawaka anesibini.', 'XHO22'),
(4, 3, 40, 'Wathi uYehova kuMoses, Wabale onke amazibulo ento eyindoda koonyana bakaSirayeli, uthabathele konyanga-nye unyuse, ubhale inani lamagama awo.', 'XHO22'),
(4, 3, 41, 'Thabatha abaLevi: ndinguYehova, esikhundleni samazibulo onke phakathi koonyana bakaSirayeli; neenkomo zabaLevi esikhundleni samazibulo onke eenkomo zoonyana bakaSirayeli.', 'XHO22'),
(4, 3, 42, 'Wawabhala ke uMoses onke amazibulo phakathi koonyana bakaSirayeli, njengoko uYehova wamwiselayo umthetho.', 'XHO22'),
(4, 3, 43, 'Onke amazibulo ento eyindoda ngenani lamagama, ethabathela kunyana onyanga-nye anyuse, ababalwayo kubo baba ngamashumi amabini anamabini amawaka, anamakhulu mabini, anamanci mathandathu anesithathu.', 'XHO22'),
(4, 3, 44, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 3, 45, 'Thabatha abaLevi esikhundleni samazibulo onke oonyana bakaSirayeli, nemfuyo yabaLevi esikhundleni semfuyo yabo; babe ngabam abaLevi. NdinguYehova.', 'XHO22'),
(4, 3, 46, 'Kumazibulo oonyana bakaSirayeli kukho amakhulu amabini, anamanci mathandathu anabathathu, angaphezu kwamakhulu amabini, anamanci mathandathu anabathathu abaLevi;', 'XHO22'),
(4, 3, 47, 'Thabatha iishekele zibe ntlanu ngokweshekele yengcwele; (ishekele iziigera ezingamashumi amabini)', 'XHO22'),
(4, 3, 48, 'Wayinika loo mali yokukhululwa ngentlawulelo inani elithe lise, kuAron nakoonyana bakhe;', 'XHO22'),
(4, 3, 49, 'Wayithabatha ke uMoses loo mali yokukhululwa kwabo bakhululwa ngokumiselwa ngabaLevi.', 'XHO22'),
(4, 3, 50, 'Wathabatha imali kumazibulo oonyana bakaSirayeli; iliwaka elinamakhulu amathathu, anamanci mathandathu anantlanu, ngokweshekele yengcwele;', 'XHO22'),
(4, 3, 51, 'uMoses wayinika uAron noonyana bakhe imali leyo yentlawulelo, ngokomlomo kaYehova, njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(4, 4, 1, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(4, 4, 2, 'Babale oonyana bakaKehati koonyana bakaLevi, ngokwezindlu zabo, ngokwezindlu zooyise,', 'XHO22'),
(4, 4, 3, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe, bonke abeza umkhosi ukuba basebenze ententeni yokuhlangana.', 'XHO22'),
(4, 4, 4, 'Nguwo lowo umsebenzi woonyana bakaKehati ententeni yokuhlangana, ezintweni eziyingcwele kanye.', 'XHO22'),
(4, 4, 5, 'Ekundulukeni kweminquba, beze ooAron noonyana bakhe, basithabathe isigqubuthelo, bayisithelise ityeya yesingqino ngaso.', 'XHO22'),
(4, 4, 6, 'Ubeke phezu kwayo isigubungelo sezikhumba zamahlengezi, wande phezu kwayo ilaphu elimfusa, ufake nezibonda zayo.', 'XHO22'),
(4, 4, 7, 'Etafileni yezonka zokubonisa, bendlala ilaphu elimfusa, babeka izitya, neenkamba, neenkamba, neendebe, phezu kwayo; nesonka sam semihla ngemihla sifanele sibe kuso.', 'XHO22'),
(4, 4, 8, 'Batwabulula phezu kwaso ngelaphu elibomvu, basigubungele ngesigqubuthelo sezikhumba zamahlengezi, bafake nezibonda zaso.', 'XHO22'),
(4, 4, 9, 'Bathabatha ilaphu elimfusa baze bagubungele isiphatho sezibane izibane zaso izibane zaso neempahla zaso neempahla zaso zomlilo neempahla zaso zonke zeoli abalungiselela ngazo.', 'XHO22'),
(4, 4, 10, 'Basibeka ndawonye nempahla yaso yonke esigqumeni sezintsu zeebhokhwe, basixhoma esibondeni.', 'XHO22'),
(4, 4, 11, 'Batwabulula ilaphu elimfusa phezu kwesibingelelo segolide, basigquma ngesigqubuthelo sezikhumba zamahlengezi, basixhoma kwizibonda zaso.', 'XHO22'),
(4, 4, 12, 'Bamele bathabathe zonke izixhobo zenkonzo abakhonza ngazo endaweni engcwele, bazifake elaphu elimfusa, bagqume ngesigqubuthelo sesikhumba esitywiniweyo, bazibeke esibondeni.', 'XHO22'),
(4, 4, 13, 'Thabatha uthuthu lwesibingelelo, wandlala ilaphu elimfusa phezu kwaso.', 'XHO22'),
(4, 4, 14, 'Bazibeka zonke iimpahla zakhe abalungiselela phezu kwazo, iingcedevu, neembombo zenyama, nemihlakulwana, nezitya zokutshiza, zonke iimpahla zesibingelelo; baza batwabulula phezu kwaso isigqubuthelo sezikhumba zeentini baza basifaka kwizibonda zaso.', 'XHO22'),
(4, 4, 15, 'Emveni kokuba uAron noonyana bakhe begqibile ukuyigubungela ingcwele nempahla yonke yengcwele ekunyukeni kwabo eminqubeni; Emveni koko boza oonyana bakaKehati, bayithwale; yaye abazange bachukumise nantoni na engcwele, ukuze bangafi. Ezo zinto ngumthwalo woonyana bakaKehati ententeni yokuhlangana.', 'XHO22'),
(4, 4, 16, 'Ke wona umsebenzi kaElazare, unyana ka-Aron umbingeleli, yayiyioli yesiphatho sesibane, nesiqhumiso, nomnikelo wemini le, neoli yokuthambisa, nokumisa umnquba wonke, nezinto zonke ezikuwo ingcwele nempahla yayo.', 'XHO22'),
(4, 4, 17, 'Wathetha uYehova kuMoses nakuAron, esithi,', 'XHO22'),
(4, 4, 18, 'Musani ukuyitshabalalisa imizalwane yoonyana bakaKehati kubaLevi.', 'XHO22'),
(4, 4, 19, 'Yenzani wena kubo, ukuze baphile, bangafi ekusondeleni kwabo kwezona zinto zingcwele: uAron noonyana bakhe bangene, balungiselele elowo nomthwalo wakhe.', 'XHO22'),
(4, 4, 20, 'Kodwa uze ungangeni kubo ugutyungelwe izinto ezingcwele, hleze bafe.', 'XHO22'),
(4, 4, 21, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 4, 22, 'Babale oonyana bakaGershon ngokwemizalwane yabo, ngokweenzala zabo;', 'XHO22'),
(4, 4, 23, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe, babale; bonke abeza kusebenza ententeni yokuhlangana, ukuba basebenze.', 'XHO22'),
(4, 4, 24, 'Nguwo lowo umsebenzi wezizukulwana zoonyana bakaGershon, ekulungiseleleni nasekuthwaleni.', 'XHO22'),
(4, 4, 25, 'Mabawathabathe amalaphu omnquba, nentente yokuhlangana, nesisigubungelo sayo, nesigubungelo sezikhumba eziphezu kwayo, nesigubungelo somnyango wentente yokuhlangana;', 'XHO22'),
(4, 4, 26, 'namawunduwundu entendelezo, namawunduwundu omnyango wesango lentendelezo, nasententeni, nasesibingelelweni ngeenxa zonke, nezintya zazo, neempahla zazo zonke zenkonzo yazo, neempahla zazo zonke bona: baya kukhonza ke.', 'XHO22'),
(4, 4, 27, 'Wonke umsebenzi woonyana bakaGershon woba ngokomlomo ka-Aron noonyana bakhe, yonke imithwalo yabo, nomsebenzi wabo wonke.', 'XHO22'),
(4, 4, 28, 'Nguwo lowo umsebenzi wenkonzo yezizukulwana zoonyana bakaGershon ententeni yokuhlangana, nomsebenzi wabo woba phantsi kwesandla sikaItamare, unyana ka-Aron umbingeleli.', 'XHO22'),
(4, 4, 29, 'Oonyana bakaMerari bababale ngokwemizalwane yabo, ngokwemizalwane yabo;', 'XHO22'),
(4, 4, 30, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe;', 'XHO22'),
(4, 4, 31, 'Nguwo lowo umsebenzi wempahla yabo, ngokwenkonzo yabo yonke ententeni yokuhlangana; neentsika zomnquba, nemiqadi yawo, neentsika zawo, neentsika zawo;', 'XHO22'),
(4, 4, 32, 'neentsika zentendelezo ngeenxa zonke, neziseko zazo, nezikhonkwane zazo, nezintya zazo, neempahla zazo zonke, nemisebenzi yazo yonke yenkonzo;', 'XHO22'),
(4, 4, 33, 'Nguwo lowo umsebenzi wemizalwane yoonyana bakaMerari, ngokomsebenzi wabo wonke ententeni yokuhlangana, esandleni sikaItamare, unyana ka-Aron umbingeleli.', 'XHO22'),
(4, 4, 34, 'uMoses noAron neentloko zebandla bababala oonyana bakaHeti ngokwezindlu zabo, ngokwezindlu zooyise,', 'XHO22'),
(4, 4, 35, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe, bonke abeze ententeni yokuhlangana;', 'XHO22'),
(4, 4, 36, 'Ababhaliswayo ngokweentsapho zabo baba ngamawaka amabini anamakhulu asixhenxe anamashumi mahlanu.', 'XHO22'),
(4, 4, 37, 'Ngawo lawo amagama emizalwane yoonyana bakaKehati, bonke abo basebenza umsebenzi wentente yokuhlangana, awababhalayo uMoses noAron ngokomlomo kaYehova, ngokwenkonzo kaMoses.', 'XHO22'),
(4, 4, 38, 'Ke bona ababhaliswayo koonyana bakaGershon ngokwemizalwane yabo nangokwezindlu zooyise,', 'XHO22'),
(4, 4, 39, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe, bonke abeze ententeni yokuhlangana;', 'XHO22'),
(4, 4, 40, 'ababhaliswayo ngokwezindlu zabo ngokwezindlu zooyise baba ngamawaka amabini, anamakhulu mathandathu, anamanci mathathu.', 'XHO22'),
(4, 4, 41, 'Nali inani lemizalwane yoonyana bakaGershon, yabo bonke abo basebenzayo ententeni yokuhlangana, awababalayo uMoses noAron ngokomlomo kaYehova.', 'XHO22'),
(4, 4, 42, 'Ababhaliweyo ezindlwini zoonyana bakaMerari ngokwezindlu zabo nezindlu zabo;', 'XHO22'),
(4, 4, 43, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, base kominyaka imashumi mahlanu ezelwe, bonke abo basebenza ententeni yokuhlangana;', 'XHO22'),
(4, 4, 44, 'Ababhaliswayo ngokweentsapho zabo baba ngamawaka amathathu anamakhulu mabini.', 'XHO22'),
(4, 4, 45, 'Lilo elo inani lezizukulwana zoonyana bakaMerari, awayibhalayo uMoses noAron ngokomlomo kaYehova, ngenkonzo kaMoses.', 'XHO22'),
(4, 4, 46, 'Bonke ke abaLevi, abababalayo uMoses noAron, nezikhulu zakwaSirayeli ngokwemizalwane yabo, nangezindlu zooyise,', 'XHO22'),
(4, 4, 47, 'bethabathela kominyaka imashumi mathathu ezelwe, banyuse, besa kominyaka imashumi mahlanu ezelwe, bonke abeza kuphuma umkhosi, ukuba bathwale intente yokuhlangana;', 'XHO22'),
(4, 4, 48, 'Abo babhalisiweyo baba ngamawaka asibhozo anamakhulu mahlanu anamashumi asibhozo.', 'XHO22'),
(4, 4, 49, 'Babalwa ngokomlomo kaYehova, ngesandla sikaMoses, elowo ngokwenkonzo yakhe, nangokwendyebo yakhe; babala kuye, njengoko uYehova wamwiselayo umthetho uMoses.', 'XHO22'),
(4, 5, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(4, 5, 2, 'Bawisele umthetho oonyana bakaSirayeli, ukuba babakhuphe eminqubeni onke amadoda aneqhenqa, nabo bonke ababhobhozayo, nabo bonke abaziinqambi ngofileyo;', 'XHO22'),
(4, 5, 3, 'Indoda nomfazi baya kunigxotha; wabakhupha eminqubeni; ukuze bangazingcolisi iibhedi zabo endihleli kuzo.', 'XHO22'),
(4, 5, 4, 'Benjenjalo ke oonyana bakaSirayeli, babakhuphela phandle kweminquba; njengoko uYehova wamwiselayo uMoses umthetho, benjenjalo oonyana bakaSirayeli.', 'XHO22'),
(4, 5, 5, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 5, 6, 'Thetha koonyana bakaSirayeli, uthi, Indoda nokuba yinkazana, xa athe wona umntu, wonile kuYehova, ube netyala loo mntu;', 'XHO22'),
(4, 5, 7, 'Ngoku ke mabasivume isono sabo abasona ngaso; Makahlawule ityala lakhe entlokweni yakhe, ongeze isahlulo sesihlanu sayo kuko oko, ayinike lowo umonileyo.', 'XHO22'),
(4, 5, 8, 'Ukuba indoda leyo ithe ayinamphindezeli wegazi, lophindezelwa ityala elo kuYehova, kumbingeleli; ngenkunzi yemvu yokucamagushela isono, amcamagushele ngayo.', 'XHO22'),
(4, 5, 9, 'Wonke umnikelo wazo zonke izipho ezingcwele zoonyana bakaSirayeli abawuzisa kumbingeleli woba ngowakhe.', 'XHO22'),
(4, 5, 10, 'Elowo ingcwele yakhe yoba yeyakhe; into ubani ayinika umbingeleli, yoba yeyakhe.', 'XHO22'),
(4, 5, 11, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 5, 12, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa athe indoda yaphuma umkamntu, yayona;', 'XHO22'),
(4, 5, 13, 'Indoda leyo yalala naye, yaye oko kufihlakele emehlweni endoda yakhe nommelwane wakhe, waba yinqambi, kwaza kwabakho ubungqina ngakuye, nokuphathwa kakubi kwakhe.', 'XHO22'),
(4, 5, 14, 'Wafikelwa ngumoya wobukhwele, wamkhweletela umkayo, wazenza inqambi; nokuba uthe wafunyanwa ngumoya wobukhwele, wamkhweletela umkayo, angazenzi inqambi;', 'XHO22'),
(4, 5, 15, 'Indoda leyo mayimzise umkayo kumbingeleli, iwuzise umsondezo wayo, isahlulo seshumi se-efa yomgubo werhasi; angagaleli oli phezu kwawo, angabeki siqhumiso phezu kwawo; ngokuba ingumbingelelo wokucamagusha, umbingelelo wesikhumbuzo okhumbuza ububi.', 'XHO22'),
(4, 5, 16, 'Umbingeleli wosondela kuye aze ammise phambi koYehova.', 'XHO22'),
(4, 5, 17, 'Umbingeleli wothabatha amanzi angcwele ngesitya sodongwe; Umbingeleli woluthabatha uthuli, alubeke emgangathweni wentente, aluphose emanzini.', 'XHO22'),
(4, 5, 18, 'Umbingeleli wombeka umfazi lowo phambi koYehova, agqubuthele intloko yomfazi lowo, awubeke esandleni sakhe umnikelo wesikhumbuzo wobukhwele, abe esandleni sakhe amanzi akrakra esiqalekiso eso;', 'XHO22'),
(4, 5, 19, 'amfungise umbingeleli, athi kumfazi, Ukuba anithanga walala nawe, ukuba akuthanga wazenza inqambi ngendoda yakho esikhundleni sendoda yakho, woba ukhululekile kula manzi akrakra esiqalekiso.”', 'XHO22'),
(4, 5, 20, 'Xa uthe waya kwenye esikhundleni sendoda yakho, wazenza inqambi, yalala enye indoda endodeni yakho;', 'XHO22'),
(4, 5, 21, 'Umbingeleli womfungisa isifungo umfazi lowo, athi umbingeleli kumfazi, uYehova wokwenza isiqalekiso nesifungo phakathi kwabantu bakowenu, ukuba uYehova uthe wenza isinqe sakho, nesisu sakho sagoba.', 'XHO22'),
(4, 5, 22, 'Kwaye la manzi esiqalekiso aya kungena emathunjini akho, ukuze athambise isisu sakho aze anyibilikise isinqe sakho. Yathi inkazana, Amen, amen.', 'XHO22'),
(4, 5, 23, 'Umbingeleli wozibhala ezi zishwabulo encwadini, azicime ngamanzi akrakra;', 'XHO22'),
(4, 5, 24, 'Umfazi makasele amanzi akrakra esiqalekiso, angene kuye amanzi esiqalekiso abe krakra.', 'XHO22'),
(4, 5, 25, 'Wowuthabatha umbingeleli umbingelelo esandleni somfazi lowo, awusondeze umbingelelo phambi koYehova, awulungise esibingelelweni;', 'XHO22'),
(4, 5, 26, 'Umbingeleli wothabatha emnikelweni womnikelo wokudla, umnikelo wawo wesikhumbuzo, aqhumisele ngawo ngomlilo esibingelelweni, amnike amanzi umfazi lowo.', 'XHO22'),
(4, 5, 27, 'Ukuba uthe wamgalela loo manzi, ukuba uthe wazenza inqambi, yakrexeza indoda yakhe, angene kuye amanzi esiqalekiso, amcaphukise, isisu sakhe sidumbe, ubole umlambo wakhe; abe sisiqalekiso umfazi phakathi kwabantu bakowabo.', 'XHO22'),
(4, 5, 28, 'Ke ukuba lo mfazi ubengathanga azenze nqambi, uhlambulukile yena; ke uya kukhululeka akhawule.', 'XHO22'),
(4, 5, 29, 'Nguwo lowo umyalelo wobukhwele xa umfazi uthe wangena endaweni yendoda yakhe, wazenza inqambi;', 'XHO22'),
(4, 5, 30, 'nokuba uthe wafikelwa ngumoya wobukhwele, yamkhweletela umkayo, yamzisa loo mfazi phambi koYehova, umbingeleli wowenza kuye wonke lo myalelo.', 'XHO22'),
(4, 5, 31, 'Yoba msulwa indoda leyo, aze yena umfazi abuthwale ubugwenxa bakhe.', 'XHO22'),
(4, 6, 1, 'Wathetha uYehova kuMoses, esithi,', 'XHO22'),
(4, 6, 2, 'Thetha koonyana bakaSirayeli, uthi kubo, Xa indoda nenkazana ithe yazahlula, yenza isifungo somNaziri, ukunyula uYehova;', 'XHO22'),
(4, 6, 3, 'Ufanele angaseli wayini nasiselo sinxilisayo, angaseli wayini naviniga okanye neviniga yesiselo esinxilisayo, angaseli incindi yeediliya, angatyi zidiliya zimanzi okanye iidiliya ezomisiweyo.', 'XHO22'),
(4, 6, 4, 'Ngayo yonke imihla yokhetho lwakhe, aze angadli nantoni na eyenziwe ngeediliya, ukususela kwiimbewu ukusa emacwecweni.', 'XHO22'),
(4, 6, 5, 'Ngemihla yonke yesibhambathiso sakhe, makungafiki blakhwe entlokweni yakhe; ide izaliseke imihla uYehova azinyulele yona, yibani ngcwele neenwele zentloko yakhe zibe nde.', 'XHO22'),
(4, 6, 6, 'Yonke imihla yokunyula kukaYehova akayi kusondela kwabafileyo;', 'XHO22'),
(4, 6, 7, 'Makangazenzi inqambi ngoyise okanye ngonina okanye ngomntakwabo okanye udade wabo xa bathe bafa, kuba ubungcwele boThixo wakhe buphezu kwentloko yakhe.', 'XHO22'),
(4, 6, 8, 'Ngayo yonke imihla yobungcwele bakhe, woba ngcwele kuYehova.', 'XHO22'),
(4, 6, 9, 'Ukuba uthe ubani uthe wafa kwaoko ngenxa yakhe, wayenza inqambi intloko yakhe; woyicheba intloko yakhe ngomhla wokuhlanjululwa kwakhe; woyicheba ngomhla wesixhenxe.', 'XHO22'),
(4, 6, 10, 'Ngomhla wesibhozo wozisa amahobe amabini, nokuba ngamavukuthu abe mabini, kumbingeleli emnyango wentente yokuhlangana.', 'XHO22'),
(4, 6, 11, 'Umbingeleli umele alenze elinye libe ngumnikelo wesono lize elinye libe lidini elinyukayo, azigubungele izono zakhe ngenxa yokuba wonile kofileyo, ayingcwalise intloko yakhe kwangaloo mini.', 'XHO22'),
(4, 6, 12, 'Woyingcwalisa kuYehova imihla yemibingelelo yakhe, azise imvana emnyaka mnye, ibe lidini letyala;', 'XHO22'),
(4, 6, 13, 'Nguwo lo umyalelo wozahluleyo, ekuzalisekeni kwemihla yozahlulo lwakhe, woziswa emnyango wentente yokuhlangana.', 'XHO22'),
(4, 6, 14, 'Wowusondeza umsondezo wakhe kuYehova, imvana emnyaka mnye, egqibeleleyo, ibe lidini elinyukayo, nemvana emnyaka mnye egqibeleleyo, ibe lidini lesono, nenkunzi yemvu egqibeleleyo, ibe ngumbulelo umnikelo;', 'XHO22'),
(4, 6, 15, 'nengobozi yezonka ezingenagwele, nomgubo ocoliweyo onegwele, ugalelwe ioli, nezonkana ezingenagwele, zithanjiswe ioli, nomnikelo wazo wokudla, neminikelo yazo ethululwayo.', 'XHO22'),
(4, 6, 16, 'Wolisondeza umbingeleli phambi koYehova, asondeze idini lakhe lesono nedini lakhe elinyukayo;', 'XHO22'),
(4, 6, 17, 'inkunzi yegusha mayibe ngumnikelo wombulelo kuYehova, kunye nengobozi yezonka ezingenagwele; umbingeleli awenze umnikelo wakhe wokudla, nomnikelo wakhe othululwayo.', 'XHO22'),
(4, 6, 18, 'Woyicheba indoda engumNaziri intloko yebandla lakhe emnyango wentente yokuhlangana, athabathe iinwele zentloko yebandla lakhe, aziphose emlilweni phantsi kombingelelo wombulelo.', 'XHO22'),
(4, 6, 19, 'Umbingeleli wothabatha umkhono obhaptiziweyo wenkunzi yegusha nesonka esingabhakwanga sibe sinye engobozini, nesonka esingabhakwanga sibe sinye, azibeke esandleni somNaziri emva kokuba echetyiwe;', 'XHO22'),
(4, 6, 20, 'alitshangatshangise umbingeleli, libe ngumtshangatshangiso phambi koYehova; Zingcwele ezo kumbingeleli, kunye nencum yedini elinyukayo, kwanesandla sokutyunyuzwa. Emveni koko ozahluleyo angayisela iwayini.', 'XHO22'),
(4, 6, 21, 'Nguwo lowo umyalelo wozahluleyo, obhambathisa umsondezo wakhe kuYehova ngenxa yobungcwele bakhe, ngaphandle koko sinokukufumana isandla sakhe; uya kwenza ngokomyalelo wakhe ngokwesifungo sakhe; ukwahlukana kokuzalwa.', 'XHO22'),
(4, 6, 22, 'Wathetha uYehova kuMoses, esithi:', 'XHO22'),
(4, 6, 23, 'Thetha kuAron nakoonyana bakhe, uthi, Nize nenjenje ukubasikelela oonyana bakaSirayeli, nithi kubo,', 'XHO22'),
(4, 6, 24, 'uYehova akusikelele, akugcine;', 'XHO22'),
(4, 6, 25, 'uYehova uya kubukhanyisa ubuso bakhe kuwe, abe nenceba kuwe.', 'XHO22'),
(4, 6, 26, 'uYehova uya kubuphakamisela kuwe ubuso bakhe, akunike uxolo.', 'XHO22'),
(4, 6, 27, 'Baya kulibeka igama lam phezu koonyana bakaSirayeli; ndibasikelele.', 'XHO22'),
(4, 7, 1, 'Ngemini awawumisa ngayo uMoses umnquba, wawuthambisa, wawungcwalisa, wawungcwalisa, nenkonzo yawo yonke, nesibingelelo nemisebenzi yaso yonke, wasithambisa, wangcwalisa;', 'XHO22'),
(4, 7, 2, 'Ookumkani bakwaSirayeli, iintloko zezindlu zooyise, abaziintloko zezizwe, eziphethe ababalwayo kubo, babebingelela.', 'XHO22'),
(4, 7, 3, 'Zawuzisa umbingelelo wazo phambi koYehova, iinqwelo ezinophahla zantandathu, neenkomo zalishumi elinambini; iyinqwelo yokulwa kwizikhulu ezibini, ibe yinkomo kwisikhulu esinye; bazizisa phambi komnquba.', 'XHO22'),
