INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(10, 7, 24, 'uYoshuwa enamaSirayeli onke, wamthabatha uAkan, unyana kaSeraki, nesilivere, nengubo yokwaleka, nesacholo segolide, noonyana bakhe, neentombi zakhe, neenkomo zakhe, namaesile akhe, nempahla yakhe emfutshane, nentente yakhe, neentombi zakhe zonke ukuba unayo bayisa entilini yeAgore.', 'XHO22'),
(10, 7, 25, 'Wathi uYoshuwa, Yini na ukuba undoyikise? uYehova uya kukoyikisa namhlanje. Onke amaSirayeli amgibisela ngamatye, azitshisa ezo nto ngokubaxuluba ngamatye.', 'XHO22'),
(10, 7, 26, 'Bamfumba phezu kwakhe imfumba enkulu yamatye, unanamhla. Wabuya ke uYehova ekuvutheni komsindo wakhe. Ngenxa yoko, loo ndawo yabizwa ngokuba nguMda weAgore, kude kube namhlanje.', 'XHO22'),
(10, 8, 1, 'Wathi uYehova kuYoshuwa, Musa ukoyika, musa ukoyika; thabatha onke amadoda okulwa, uyigqibe, unyuke uye eAyi; yabona, ndimnikela esandleni sakho ukumkani waseAyi, nabantu bakhe, nomzi wakhe, nelizwe lakhe;', 'XHO22'),
(10, 8, 2, 'wenze kwiAyi nokumkani wayo njengoko wenza ngako kwiYeriko nokumkani wayo; kodwa noyithimba impahla yayo, neenkomo zayo; lalelani umzi ngasemva kwawo.', 'XHO22'),
(10, 8, 3, 'Wazilungiselela uYoshuwa namagorha onke okulwa eAyi; uYoshuwa wanyula amadoda angamakroti angamashumi amathathu amawaka, wawathuma ebusuku.', 'XHO22'),
(10, 8, 4, 'Wabawisela umthetho, wathi, Yabonani, nilele phantsi, nisidla umzi, nasemva komzi; musani ukuya kude nesixeko, kodwa wonke umntu makalungiselele.', 'XHO22'),
(10, 8, 5, 'Mna ke, nabantu bonke abakunye nam, siya kusondela esixekweni;', 'XHO22'),
(10, 8, 6, '(ngokuba osilandela wona), side siwatsalele ngaphandle komzi; kuba baya kuthi, Basaba phambi kwethu njengasekuqaleni; ke ngoko, siya kusaba kubo.', 'XHO22'),
(10, 8, 7, 'Ke kaloku yimani apho nilalele khona, nisithimbe isixeko, kuba uYehova uThixo wenu uya kusinikela esandleni senu.', 'XHO22'),
(10, 8, 8, 'Xa nithe nathabatha umzi, wutshiseni umzi ngomlilo; wenze ngokomyalelo kaYehova. Yabonani, ndiniwisele umthetho.', 'XHO22'),
(10, 8, 9, 'Wabathuma ke uYoshuwa, balalela, bahlala phakathi kweBheteli neAyi, ngasentshonalanga kweAyi; ke uYoshuwa walala phakathi kwabantu ngobo busuku.', 'XHO22'),
(10, 8, 10, 'Wagqiba uYoshuwa kwakusasa, wababala abantu, wenyuka, yena namadoda amakhulu akwaSirayeli, wabakhokela abantu baya eAyi.', 'XHO22'),
(10, 8, 11, 'Benyuka ke bonke abantu bokulwa abenaye, basondela, beza phambi kwesixeko, bamisa iintente ngasentla kweAyi; ke intlambo phakathi kwabo neAyi.', 'XHO22'),
(10, 8, 12, 'Wathabatha amadoda ngathi angamawaka amahlanu, wawalalela phakathi kweBheteli neAyi, ngasentshonalanga komzi.', 'XHO22'),
(10, 8, 13, 'Banyula ke amadoda, impi yonke engasentla komzi, namaxoki angakwicala elingasentshonalanga yesixeko, wangena uYoshuwa entilini ngobo busuku.', 'XHO22'),
(10, 8, 14, 'Wakubona ukumkani waseAyi, wagqiba kwakusasa kwakusasa, aphuma amadoda aloo mzi, aya kulwa noonyana bakaSirayeli, yena nabantu bakhe bonke, ngexesha elimisiweyo ngaseArabha; kodwa wayengazi ukuba emva kwesixeko kwakukho abantu ababemlindile emva kwesixeko.', 'XHO22'),
(10, 8, 15, 'uYoshuwa nabo bonke oonyana bakaSirayeli bazenza ngathi boyisiwe phambi kwabo baza basaba ngendlela eya entlango.', 'XHO22'),
(10, 8, 16, 'Bonke abantu ababeseAyi bahlanganisana ukuba babasukele; bamsukela ke uYoshuwa, baphuma esixekweni.', 'XHO22'),
(10, 8, 17, 'Akwasala eAyi naseBheteli ongaphumanga ukubasukela amaSirayeli; bawushiya umzi uvulekile, basukela oonyana bakaSirayeli.', 'XHO22'),
(10, 8, 18, 'Wathi uYehova kuYoshuwa, Wolule umkhonto osesandleni sakho ngaseAyi; ngokuba ndiya kukunikela esandleni sakho. Wawolulela uYoshuwa umkhonto obusesandleni sakhe kuwo umzi.', 'XHO22'),
(10, 8, 19, 'Basuka abalaleli kamsinya endaweni yabo, bagidima asolula isandla sakhe; bangena esixekweni, bawuthimba, bangxama, basitshisa isixeko.', 'XHO22'),
(10, 8, 20, 'Ajika amadoda aseAyi, abona umsi womzi unyuka usiya ezulwini, akaba namandla okusaba ngapha nangapha abantu ababesabele entlango bakreqa kwabo babebasukela.', 'XHO22'),
(10, 8, 21, 'uYoshuwa nawo onke amaSirayeli babona ukuba abalaleli basithimbile isixeko yaye kunyuka umsi esixekweni, babuya bawabulala amadoda aseAyi.', 'XHO22'),
(10, 8, 22, 'Baphuma abasemzini, beza kubakhawulela; aba phakathi koonyana bakaSirayeli, abanye bengapha, abanye bengapha; yaye bawoyisa, apho abazange bashiye namnye kubo okanye abasindileyo.', 'XHO22'),
(10, 8, 23, 'Ambamba ukumkani waseAyi ephila, amzisa kuYoshuwa.', 'XHO22'),
(10, 8, 24, 'Akugqiba oonyana bakaSirayeli ukubabulala bonke abemi baseAyi ezweni, entlango apho babebasukele khona, bababulala bonke ngohlangothi lwekrele bada babhubha, abuyela eAyi onke amaSirayeli, ayinqoba ngohlangothi lwentshuntshe.', 'XHO22'),
(10, 8, 25, 'Kwathi ke, bonke abawayo ngaloo mini, amadoda nabafazi, baba lishumi elinamabini lamawaka, onke amadoda aseAyi.', 'XHO22'),
(10, 8, 26, 'Kuba uYoshuwa akazange asibuyise isandla sakhe awayewolule ngaso umkhonto wakhe, de wabatshabhisa bonke abemi baseAyi.', 'XHO22'),
(10, 8, 27, 'Kuphela yimfuyo nempahla yesixeko abathinjwa ngayo oonyana bakaSirayeli, ngokwelizwi likaYehova awaliyalelayo uYoshuwa.', 'XHO22'),
(10, 8, 28, 'uYoshuwa wayitshisa iAyi wayenza imfumba engapheliyo, yaba yintlango de kube namhla.', 'XHO22'),
(10, 8, 29, 'Ukumkani waseAyi wazixhoma emthini kwada kwahlwa; kwathi nje ukutshona kwelanga, uYoshuwa wayalela ukuba basihlise isidumbu sakhe emthini baze basiphose kumnyango wesango lesixeko baze bakhe imfumba enkulu yamatye phezu kwaso de kube namhla.', 'XHO22'),
(10, 8, 30, 'uYoshuwa wamakhela uYehova, uThixo kaSirayeli, isibingelelo, entabeni ye-Ebhali;', 'XHO22'),
(10, 8, 31, 'Kanye njengoko uMoses umkhonzi kaYehova wabayalelayo oonyana bakaSirayeli, kanye njengoko kubhaliwe encwadini yomthetho kaMoses, isibingelelo samatye apheleleyo, ekungekho bani unokusiphakamisa nesinyithi; yaye phezu kwayo banikela iminikelo etshiswayo kuYehova, nemibingelelo yombulelo.', 'XHO22'),
(10, 8, 32, 'Wawubhala ematyeni umbhalo womyalelo kaMoses, abewubhala phambi koonyana bakaSirayeli.', 'XHO22'),
(10, 8, 33, 'Onke oonyana bakaSirayeli, namadoda awo amakhulu, nabalungiseleli bawo, nabagwebi bawo, bema kweli cala letyeya nakweliya icala, ngakubabingeleli, nabaLevi abathwele ityeya yomnqophiso kaYehova, nowasemzini, wazalelwa phakathi kwabo; isiqingatha sabo sesa ezintabeni zeGerizim, esinye isiqingatha sabo sibheka entabeni ye-Ebhali; njengoko uMoses umkhonzi kaYehova wabawisela umthetho ngenxa engaphambili, ukuba babasikelele oonyana bakaSirayeli.', 'XHO22'),
(10, 8, 34, 'Emva koko wawafunda onke amazwi omthetho, iintsikelelo neziqalekiso, ngako konke okubhaliweyo encwadini yomthetho.', 'XHO22'),
(10, 8, 35, 'Akubangakho nalinye ilizwi kuwo onke amazwi uMoses awayewayalele, angalilesanga uYoshuwa phambi kwebandla lonke lakwaSirayeli, nabafazi, nabantwana, nabasemzini ababeqhelene nabo.', 'XHO22'),
(10, 9, 1, 'nangenxa yookumkani bonke ababephesheya kweYordan ezintabeni, nasezimbombeni, nasezindaweni zonke zolwandle olukhulu, malungana neLebhanon, namaHeti, nama-Amori, namaKanan, namaPersi amaHivi namaYebhusi eva oko;', 'XHO22'),
(10, 9, 2, 'bahlanganisana ndawonye ukuba balwe noYoshuwa namaSirayeli.', 'XHO22'),
(10, 9, 3, 'Beva abemi baseGibheyon into ayenzileyo uYoshuwa eYeriko naseAyi.', 'XHO22'),
(10, 9, 4, 'Benza ngobuchule baza bahamba baza bazenza oonozakuzaku baza bathabatha iingxowa ezindala kwiidonki zabo neentsuba zewayini ezindala, ezikrazukileyo, zabotshelelwa;', 'XHO22'),
(10, 9, 5, 'neembadada ezonakeleyo, ezigugileyo ezinyaweni, zambethe iingubo ezindala; sonke isonka sabo saba somile kwaye singenanto.', 'XHO22'),
(10, 9, 6, 'Eza kuYoshuwa emkhosini eGiligali, athi kuye nakumadoda akwaSirayeli, Sivela ezweni elikude; ngoku usenza umnqophiso nathi.', 'XHO22'),
(10, 9, 7, 'Athi amadoda akwaSirayeli kumaHebhere, Mhlawumbi nihleli naphakathi kwethu; sothini na ukunimisela umnqophiso?', 'XHO22'),
(10, 9, 8, 'Bathi kuYoshuwa, Singabakhonzi bakho. Wathi uYoshuwa kubo, Ningoobani na? ungowaphi?', 'XHO22'),
(10, 9, 9, 'Bathi kuye, Abakhonzi bakho bavela ezweni elikude ngenxa yegama likaYehova uThixo wakho, ekubeni siyivile ingxelo yakhe, nako konke awakwenzayo eYiputa;', 'XHO22'),
(10, 9, 10, 'nako konke awakwenzayo kookumkani ababini bama-Amori, ababephesheya kweYordan, uSihon ukumkani waseHeshbhon, no-Ogi ukumkani waseBhashan, obeseAshtaroti.', 'XHO22'),
(10, 9, 11, 'Asixelela ngoko amadoda amakhulu, nabemi bonke belizwe lakowethu, esithi, Thabathani ukudla kwenu endleleni, niye kuwakhawulela, nithi kuwo, Singabakhonzi benu; ke ngoko, yenza umnqophiso kunye nathi ngoku.', 'XHO22'),
(10, 9, 12, 'Esi sonka sethu sasithabatha sisoma ezindlwini zethu, sakukudla, ngemini esaphuma ngayo ukuza kuni; ngoku yomile, kwaye uyakrokra;', 'XHO22'),
(10, 9, 13, 'Iintsuba zewayini esazizalisa zisentsha; kwaye khangela, zazikrazukile, kwaye iimpahla zethu nezihlangu zethu zazigugile ngenxa yohambo olude.', 'XHO22'),
(10, 9, 14, 'Loo madoda athabatha ukuziphilisa kwawo, yaye akamceli uYehova.', 'XHO22'),
(10, 9, 15, 'Wenza uxolo nabo uYoshuwa, wabamisela umnqophiso wokubasindisa; zafunga izikhulu zebandla kubo.', 'XHO22'),
(10, 9, 16, 'Kwathi ekupheleni kweentsuku ezintathu emveni kokuba benze umnqophiso nabo, beva ukuba bakufuphi kubo, nokuba babehlala phakathi kwabo.', 'XHO22'),
(10, 9, 17, 'Bemka oonyana bakaSirayeli, bafika emizaneni yabo ngomhla wesithathu. Imizi yabo yayiyiGibheyon, neKefira, neBheroti, neKiriyati-yeharim.', 'XHO22'),
(10, 9, 18, 'Oonyana bakaSirayeli abababanga bona, ngokuba izikhulu zebandla zibe zifunge uYehova uThixo kaSirayeli kubo. Lazikrokrela izikhulu lonke ibandla.', 'XHO22'),
(10, 9, 19, 'Zathi zonke izikhulu kwibandla lonke, Thina sifunge uYehova uThixo kaSirayeli kubo; ke ngoko asisayi kubachukumisa ngoku.', 'XHO22'),
(10, 9, 20, 'Siya kwenjenjalo ke kubo; nathi siya kuwayeka aphile, hleze kufike umsindo phezu kwethu ngenxa yesifungo esabafungela wona.', 'XHO22'),
(10, 9, 21, 'Zathi kubo izikhulu, Mabadle ubomi; kodwa yayingabathanda iinkuni, nabathanda amanzi ebandla lonke; njengoko zathethayo izikhulu kubo.', 'XHO22'),
(10, 9, 22, 'Wababiza ke uYoshuwa, wathetha kubo, wathi, Nisinyulele, nathi, Sikude kakhulu kuni; uhlala phakathi kwethu?', 'XHO22'),
(10, 9, 23, 'Kungenxa yoko le nto niqalekisiweyo, yaye akukho namnye kuni oya kukhululwa ebukhobokeni nakwabo bagawula iinkuni namaqula amanzi endlu kaThixo wam.', 'XHO22'),
(10, 9, 24, 'Bamphendula uYoshuwa, bathi, Baxelelwa abakhonzi bakho ukuba uYehova uThixo wakho wamwisela uMoses umkhonzi wakhe umthetho, ukuba uya kuninika lonke ilizwe, nokuba uya kubatshabalalisa bonke abemi belizwe eliphambi kwenu; ngenxa yoko saba buhlungu kakhulu, boyika ubomi bethu ngenxa yenu, senza le nto.', 'XHO22'),
(10, 9, 25, 'Ngoku niyabona, sisesandleni senu; yenza kuthi njengoko kulungileyo, njengoko kuthe tye emehlweni akho.', 'XHO22'),
(10, 9, 26, 'Wenjenjalo ke kubo, wabahlangula esandleni soonyana bakaSirayeli, ukuze bangababulali.', 'XHO22'),
(10, 9, 27, 'Ngaloo mini uYoshuwa wabenzela loo mini abo bagawula iinkuni namaqula amanzi, ebandleni nasesibingelelweni sikaYehova, kude kube namhla kuloo ndawo abeya kuyinyula.', 'XHO22'),
(10, 10, 1, 'Wathi uAdoni-shekeki, ukumkani waseYerusalem, weva ukuba uYoshuwa uyithimbile iAyi, wamqalekisa; Wenjenjalo kwiYeriko, nakukumkani wayo, nakwiAyi, nakukumkani wayo; nokuthi abemi baseGibheyon benza uxolo namaSirayeli, bahlala phakathi kwawo;', 'XHO22'),
(10, 10, 2, 'Boyika kunene, kuba iGibheyon ibingumzi omkhulu, njengomnye wemizi yakomkhulu, ngokuba inkulu kuneAyi, namadoda akhona angamagorha.', 'XHO22'),
(10, 10, 3, 'Waza uAdoni-tsedeki, ukumkani waseYerusalem, wathumela kuHoham ukumkani waseHebron, nakuPiram, ukumkani waseYarmuti, nakuYafiya ukumkani waseLakishe, nakuDebhiri ukumkani wase-Eglon, esithi,', 'XHO22'),
(10, 10, 4, 'Nyukani nize kum nize nindincede, ukuze siyoyise iGibheyon, ngenxa yokuba yenze uxolo noYoshuwa noonyana bakaSirayeli.', 'XHO22'),
(10, 10, 5, 'Bahlanganisana ookumkani abahlanu bama-Amori, nokumkani waseYerusalem, nokumkani waseHebron, nokumkani waseYarmuti, nokumkani waseLakishe, ukumkani wase-Eglon, balwa, yena nemikhosi yakhe yonke, bamisa phambi kweGibheyon, nilwe imfazwe nayo.', 'XHO22'),
(10, 10, 6, 'Athumela ke amadoda aseGibheyon kuYoshuwa emkhosini eGiligali, esithi, Musa ukusisusa isandla sakho kubakhonzi bakho; yiza kuthi, usihlangule, usincede; ngokuba besibuthelene bonke ookumkani bama-Amori abemiyo ezintabeni;', 'XHO22'),
(10, 10, 7, 'Wenyuka uYoshuwa, waya eGiligali, namadoda onke okulwa, namagorha onke.', 'XHO22'),
(10, 10, 8, 'Wathi uYehova kuYoshuwa, Musa ukuboyika; kuba ndibanikele esandleni sakho; akukho namnye kubo oya kuma ngakuwe.', 'XHO22'),
(10, 10, 9, 'Wafika uYoshuwa kubo ngequbuliso, wenyuka ubusuku bonke, evela eGiligali.', 'XHO22'),
(10, 10, 10, 'uYehova wabaxhokonxa phambi koonyana bakaSirayeli, wababulala ngobulalo olukhulu eGibheyon, wabasukela ngendlela enyukayo yaseBhete-horon, wababulala kwesa eAsheki, wesa eMakeda.', 'XHO22'),
(10, 10, 11, 'Xa babesaba phambi koonyana bakaSirayeli nasendleleni eya eBhete-horon, uYehova wabagibisela amatye amakhulu ukusuka ezulwini ukuya kutsho kwa-Asheki, baza bafa; oonyana bakaSirayeli babulele ngekrele.', 'XHO22'),
(10, 10, 12, 'Wathetha uYoshuwa kuYehova ngomhla uYehova awawanikela ngawo ama-Amori koonyana bakaSirayeli, wathi emehlweni oonyana bakaSirayeli, Langa, yima eGibheyon; nawe, nyanga, entilini yeAyalon.', 'XHO22'),
(10, 10, 13, 'Lama ilanga, yema inyanga, bada abantu baziphindezela impindezelo ezintshabeni zabo. Xa ndithi, kubhaliwe na encwadini yeYazere? Ilanga lema lingashukumi embindini wesibhakabhaka, lingangxamelanga ukutshona, phantse imini yonke.', 'XHO22'),
(10, 10, 14, 'Akuzange kubekho mini injengaleyo phambi kwakhe nasemva koko uYehova waliphulaphula ilizwi lomntu; ngokuba uYehova ebewalwela amaSirayeli.', 'XHO22'),
(10, 10, 15, 'Emva koko uYoshuwa nawo onke amaSirayeli awayekunye naye babuyela enkampini eGiligali.', 'XHO22'),
(10, 10, 16, 'Ke aba kumkani bobahlanu basaba, bazimela emqolombeni waseMakeda.', 'XHO22'),
(10, 10, 17, 'Waxelelwa uYoshuwa, kwathiwa, Kufunyenwe ookumkani abahlanu bezimele emqolombeni waseMakeda.', 'XHO22'),
(10, 10, 18, 'Wathi uYoshuwa, Qengqelani amatye amakhulu emnyango womqolomba, nimise abantu ngakuwo ukuba bawulinde.', 'XHO22'),
(10, 10, 19, 'Zisukeleni iintshaba zenu, nibe ngasemva kwazo; ningabavumeli ukuba bangene emizini yabo, ngokuba uYehova uThixo wenu uwanikele esandleni senu.', 'XHO22'),
(10, 10, 20, 'Akugqiba uYoshuwa noonyana bakaSirayeli ukuwabulala ngogonyamelo olukhulu ade atshatyalaliswa, aza asala angena kwizixeko ezinqatyisiweyo.', 'XHO22'),
(10, 10, 21, 'Lwabuya lonke uhlanga ngoxolo emkhosini kuYoshuwa waseMakeda; akwabakho namnye koonyana bakaSirayeli owathetha ngolwimi lwakhe.', 'XHO22'),
(10, 10, 22, 'Wathi uYoshuwa, Ndivuleleni emqolombeni, nibakhuphe emqolombeni abo kumkani bobahlanu.', 'XHO22'),
(10, 10, 23, 'Benjenjalo ke babazisa kuye abo kumkani bobahlanu basemqolombeni, ukumkani waseYerusalem, nokumkani waseHebron, nokumkani waseYarmuti, nokumkani waseLakishe, nokumkani wase-Eglon.', 'XHO22'),
(10, 10, 24, 'Kwathi, babazisa abo kumkani kuYoshuwa, uYoshuwa wawabiza onke amadoda akwaSirayeli, wathi kubathetheli bamadoda okulwa abehambe naye, Sondelani, nibabeke ezinyaweni zaba kumkani; Basondela babeka iinyawo zabo entanyeni.', 'XHO22'),
(10, 10, 25, 'Wathi uYoshuwa kubo, Musani ukoyika, musani ukoyika; yomelelani, yomelelani, ngokuba uYehova uya kwenza okufanayo kuko konke okulwelayo.', 'XHO22'),
(10, 10, 26, 'Emva koko uYoshuwa waboyisa, wababulala, wabaxhoma emithini emihlanu, baxhonywa emithini kwada kwahlwa.', 'XHO22'),
(10, 10, 27, 'Latshona ilanga, uYoshuwa wayalela ukuba bayikhuphe emithini baza bayiphosa emqolombeni ababezimele kuwo, baza babeka amatye amakhulu emlonyeni womqolomba esekhona nanamhla.', 'XHO22'),
(10, 10, 28, 'uYoshuwa wayithimba iMakeda ngaloo mini waza wayoyisa ngohlangothi lwekrele, nokumkani wayo wambulala ngesiqalekiso, bona nayo yonke imiphefumlo eyayikuyo; Wenza kukumkani waseMakeda njengoko wenza ngako kukumkani waseYeriko.', 'XHO22'),
(10, 10, 29, 'Wemka ke uYoshuwa eMakeda yaye onke amaSirayeli ayekunye naye kwada kwesa eLibhena, alwa neLibhena;', 'XHO22'),
(10, 10, 30, 'uYehova wayinikela yona nokumkani wayo esandleni soonyana bakaSirayeli; Wambulala ngohlangothi lwentshuntshe, nabo bonke abantu ababekuyo; akavuma ukuba kusale bani khona; Wenza kukumkani wakhe njengoko wenza ngako kukumkani waseYeriko.', 'XHO22'),
(10, 10, 31, 'Wemka ke uYoshuwa eLibhena, yena noonyana bonke bakaSirayeli, waya eLakishe, wamisa iintente kuyo, walwa nayo.', 'XHO22'),
(10, 10, 32, 'uYehova wayinikela iLakishe esandleni soonyana bakaSirayeli, bayithimba ngomhla wesibini, bababulala bonke abantu abakuyo ngohlangothi lwekrele, njengoko wenzayo eLibhena.', 'XHO22'),
(10, 10, 33, 'uHoram ukumkani waseGezere wayihlasela iLakishe; uYoshuwa wambulala yena nabantu bakhe, kwada akwasala bani.', 'XHO22'),
(10, 10, 34, 'Waphuma uYoshuwa eLakishe, waya e-Eglon, enamaSirayeli onke; bakungqinga, balwa nawe.', 'XHO22'),
(10, 10, 35, 'Bayithimba ngaloo mini baza bayoyisa ngohlangothi lwekrele, yaye wayiqalekisa yonke imiphefumlo eyayikuyo ngaloo mini, kanye njengoko wenzayo eLagishe.', 'XHO22'),
(10, 10, 36, 'Wemka uYoshuwa e-Eglon, enamaSirayeli onke, waya eHebron; balwa nalo.', 'XHO22'),
(10, 10, 37, 'Bayithimba baza bayibulala ngohlangothi lwekrele, nokumkani wayo nezixeko zayo zonke nayo yonke imiphefumlo eyayikuyo; makangashiyi namnye, njengoko wenzayo e-Eglon; kodwa wayitshabhisa, kwanemimoya yonke eyayikuyo.', 'XHO22'),
(10, 10, 38, 'Wabuyela uYoshuwa eDebhiri, enamaSirayeli onke; walwa.', 'XHO22'),
(10, 10, 39, 'Wayithimba nokumkani wayo nezixeko zayo zonke; ababulala ngohlangothi lwentshuntshe, batshabhisa bonke oomoya ababekuyo; wayengenabani; Wenjenjalo naseHebron, naseDebhiri, nakukumkani wayo; njengoko wenzayo kwiLibhena nokumkani wayo.', 'XHO22'),
(10, 10, 40, 'Waloyisa uYoshuwa lonke ilizwe leentaba, nelasezantsi, nezihlambo, namaqula, nookumkani bonke bakhona;', 'XHO22'),
(10, 10, 41, 'Wawaxabela uYoshuwa, wathabathela eKadeshe-bharneha, wesa eGaza, nelizwe lonke laseGoshen, wesa eGibheyon;', 'XHO22'),
(10, 10, 42, 'Ngoko nangoko uYoshuwa wabathimba bonke abo kumkani namazwe abo, ngenxa yokuba uYehova uTHIXO kaSirayeli wayemlwela uSirayeli.', 'XHO22'),
(10, 10, 43, 'Babuyela uYoshuwa emkhosini eGiligali, enamaSirayeli onke.', 'XHO22'),
(10, 11, 1, 'Weva ke uYabhin, ukumkani waseHatsore, wathumela kuYobhabhi ukumkani waseMadon, nakukumkani waseShimron, nakukumkani ka-Asafu;', 'XHO22'),
(10, 11, 2, 'nakookumkani abangasentla kweentaba namahewu, ngasezantsi kweKineroti, nasesihlanjeni, nasentshonalanga kweDore;', 'XHO22'),
(10, 11, 3, 'namaKanan empumalanga nasentshonalanga, nama-Amori, namaHeti, namaPerizi, namaYebhusi asezintabeni, namaHifi abephantsi kweHermon, ezweni laseMizpa.', 'XHO22'),
(10, 11, 4, 'Bemka bona nemikhosi yabo yonke, into eninzi yabantu, ebaninzi entlabathini naselunxwemeni lolwandle, enamahashe amaninzi, neenqwelo zokulwa.', 'XHO22'),
(10, 11, 5, 'Bahlanganisana abo kumkani bonke, beza bamisa emanzini aseMerom ukuba balwe namaSirayeli.', 'XHO22'),
(10, 11, 6, 'Wathi uYehova kuYoshuwa, Musa ukuboyika; ngokuba ngomso eli xa ndiya kubanikela bonke ababuleweyo phambi koonyana bakaSirayeli; wagawuleni amahashe abo, nizitshise ngomlilo iinqwelo zabo zokulwa.', 'XHO22'),
(10, 11, 7, 'Waza uYoshuwa namadoda okulwa awayekunye naye kwaoko bawaxabela emanzini aseMerom; bawa phezu kwabo.', 'XHO22'),
(10, 11, 8, 'uYehova wabanikela esandleni soonyana bakaSirayeli, bawaxabela, bawasukela, kwada kwesa eTsidon enkulu, kwesa eMisrefoti-mayim, kwesa entilini yeMizpa ngasempumalanga; bawoyisa kwada akwasala namnye.', 'XHO22'),
(10, 11, 9, 'Wenza ke uYoshuwa kubo njengoko uYehova wamwisela umthetho ngako; wawenyusa amahashe abo, wazitshisa ezinqwelweni zabo zokulwa.', 'XHO22'),
(10, 11, 10, 'Wajika uYoshuwa ngelo xesha, wayithimba iHatsore, wambulala ukumkani wayo ngekrele, kuba uHatsore wayefudula eyintloko yazo zonke ezo zikumkani.', 'XHO22'),
(10, 11, 11, 'Ababulala bonke abantu abakuyo ngobushushu bekrele, babaxabela ngekrele; kwakungekho mntu uphefumlayo; wamtshisa uHatsore ngomlilo.', 'XHO22'),
(10, 11, 12, 'uYoshuwa wazithimba zonke izixeko zabo kumkani nabo bonke ookumkani bazo waza wababulala ngohlangothi lwekrele, wababulala ngesiqalekiso, kanye njengoko uMoses umkhonzi kaYehova wayalelayo.', 'XHO22'),
(10, 11, 13, 'Ke imizi eyayishiyeke ezinqabeni zayo amaSirayeli akayitshisanga, yaba yiHatsore kuphela; wamtshisa uYoshuwa.', 'XHO22'),
(10, 11, 14, 'Onke amaxhoba aloo mizi neenkomo bazithimbelayo oonyana bakaSirayeli; kodwa bababulala wonk’ ubani ngohlangothi lwekrele de babatshabalalisa, yaye abazange bavumele nabani na ophefumlayo.', 'XHO22'),
(10, 11, 15, 'Njengoko uYehova wamwiselayo umthetho uMoses umkhonzi wakhe, uMoses wamwisela umthetho ngokunjalo uYoshuwa. Wenjenjalo ke uYoshuwa; makangonakalisi nantoni na uYehova awamwisela umthetho ngayo uMoses.', 'XHO22'),
(10, 11, 16, 'Waloyisa uYoshuwa lonke elo zwe leentaba, nelizwe lonke lasezantsi, nelizwe laseGoshen lonke, leentili, nentlango, neentaba zakwaSirayeli, nezihlambo zakhona;', 'XHO22'),
(10, 11, 17, 'ukusuka kwiNtaba yeHalaki enyuka iye kwaSehire ukuya eBhahali-gadi kwintlambo yaseLebhanon phantsi kweNtaba yeHermon; bonke ookumkani bawo wabathimba, waboyisa, wababulala.', 'XHO22'),
(10, 11, 18, 'uYoshuwa waqhubeka esilwa nabo bonke abo kumkani ixesha elide.', 'XHO22'),
(10, 11, 19, 'Akubangakho mzi wazinikelayo ngoxolo koonyana bakaSirayeli, ingengamaHifi kuphela abemi baseGibheyon; abaseleyo babasa emfazweni.', 'XHO22'),
(10, 11, 20, 'Kuba kwavela kuYehova ukuze azenze lukhuni iintliziyo zawo ukuba amhlasele uSirayeli ngemfazwe, ukuze awabethe ngesiqalekiso esitshabalalisayo, yaye awaba nanceba, ngaphandle kokuba amtshabalalise, kanye njengoko uYehova wayeyalele.', 'XHO22'),
(10, 11, 21, 'Ngelo xesha uYoshuwa weza waza wawatshabalalisa ama-Anaki ezintabeni, eHebron, eDebhiri, eAnabhi nakuzo zonke iintaba zakwaYuda nakwiintaba zonke zakwaSirayeli; Wawaqalekisa uYoshuwa wona nemizi yawo.', 'XHO22'),
(10, 11, 22, 'Akwasala namnye kuma-Anaki ezweni loonyana bakaSirayeli, ngaphandle kweGaza neGati neAshdode.', 'XHO22'),
(10, 11, 23, 'Waliloyisa ke uYoshuwa lonke ilizwe, njengako konke uYehova awakuthethayo kuMoses; uYoshuwa wababela oonyana bakaSirayeli ilifa labo, ngokwezabelo zabo ngokwemizalwane yabo. Yaye ihlabathi laphumla emfazweni.', 'XHO22'),
(10, 12, 1, 'Naba ookumkani belizwe, abababulalayo oonyana bakaSirayeli, balihlutha ilizwe labo phesheya kweYordan, ethabathela emlanjeni oyiArnon, esa entabeni yeHermon, nentlango yonke ngasempumalanga;', 'XHO22'),
(10, 12, 2, 'uSihon, ukumkani wama-Amori, obemi eHeshbhon, wayelawula eArohere, eselunxwemeni lomlambo iArnon, nesiqingatha somlambo, nesiqingatha seGiliyadi, wesa emlanjaneni oyiYabhoki, engumlambo ke lowo. Umda welizwe oonyana baka-Amon;', 'XHO22'),
(10, 12, 3, 'bathabathela entlango, besa elwandle lwaseKineroti ngasempumalanga, besa elwandle lwentlango, besa eLwandle lweTyuwa ngasempumalanga, ngendlela yaseBhete-yeshimoti; kwelasezantsi, phantsi kweAshdode-pisga;', 'XHO22'),
(10, 12, 4, 'nommandla ongaselunxwemeni lukaOgi ukumkani waseBhashan, omnye wamasalela amaRafa, owayehlala eAshtaroti nase-Edreyi;', 'XHO22'),
(10, 12, 5, 'Wayelawula eNtabeni yeHermon naseSaleka naseBhashan yonke ukuya kufikelela kumda wamaGeshuri namaAgati nesiqingatha saseGiliyadi kumda kaSihon ukumkani waseHeshbhon.', 'XHO22'),
(10, 12, 6, 'Bambulala ke uMoses umkhonzi kaYehova, noonyana bakaSirayeli; uMoses, umkhonzi kaYehova, walinika amaRubhen, namaGadi, nolunye uhlanga lwesizwe sakwaManase, ukuba libe lilifa labo.', 'XHO22'),
(10, 12, 7, 'Ngabo aba ookumkani belizwe uYoshuwa noonyana bakaSirayeli aboyisayo ngasempuma kweYordan ngakwicala elingasentshona, ukusuka eBhahali-gadi entlanjeni yaseLebhanon ukuya ezintabeni zeHalaki ezinyukayo eSehire; awathi uYoshuwa wazinika izizwe zakwaSirayeli, ukuba zibe lilifa ngokwezabelo zazo;', 'XHO22'),
(10, 12, 8, 'ezintabeni, nasezihlanjeni, nasezinkqantosini, nasemithonjeni, nasezinkqantosini, nasezweni lasezantsi; namaHeti, nama-Amori, namaKanan, namaPerizi, namaHeti, namaYebhusi;', 'XHO22'),
(10, 12, 9, 'ngukumkani waseYeriko emnye; ukumkani waseAyi ukhangelene neBheteli, inye;', 'XHO22'),
(10, 12, 10, 'ukumkani waseYerusalem yedwa; omnye ngukumkani waseHebron;', 'XHO22'),
(10, 12, 11, 'ukumkani waseYarmuti eyedwa; ukumkani waseLakishe yedwa;', 'XHO22'),
(10, 12, 12, 'ngukumkani wase-Eglon, emnye; omnye ukumkani waseGezere;', 'XHO22'),
(10, 12, 13, 'ngukumkani waseDebiri, emnye; mnye kuphela ukumkani waseGedere;', 'XHO22'),
(10, 12, 14, 'ngukumkani waseHorma, emnye; ukumkani waseAradi yedwa;', 'XHO22'),
(10, 12, 15, 'ngukumkani waseLibhena, emnye; ukumkani waseAdulam yedwa;', 'XHO22'),
(10, 12, 16, 'ngukumkani waseMakeda, emnye; ngukumkani waseBheteli yedwa;', 'XHO22'),
(10, 12, 17, 'ngukumkani waseTapuwage yedwa; mnye kuphela ukumkani wakwaHefere;', 'XHO22'),
(10, 12, 18, 'ngukumkani waseAfeki yedwa; ukumkani kaLazaro yedwa;', 'XHO22'),
(10, 12, 19, 'ngukumkani waseMadon yedwa; omnye ukumkani waseHatsore;', 'XHO22'),
(10, 12, 20, 'omnye ukumkani waseShimron-meron; ukumkani ka-Asafu emnye;', 'XHO22'),
(10, 12, 21, 'ngukumkani waseTahenage yedwa; mnye ukumkani waseMegido;', 'XHO22'),
(10, 12, 22, 'ngukumkani waseKedeshe, emnye; omnye ukumkani waseYokenam yaseKarmele;', 'XHO22'),
(10, 12, 23, 'ukumkani waseDore, elunxwemeni lwaseDore, emnye; ukumkani wezizwe zaseGiligali yedwa;', 'XHO22'),
(10, 12, 24, 'ngukumkani waseTirtsa yedwa; ookumkani abamashumi mathathu ananye bebonke.', 'XHO22'),
(10, 13, 1, 'Ke kaloku uYoshuwa wayeseleyingwevu enkulu, ebudaleni iminyaka; Wathi uYehova kuye, Umdala, ukhulile, yaye kusekho umhlaba omninzi wobutyebi.', 'XHO22'),
(10, 13, 2, 'Lilo eli ke ilizwe eliseleyo: lonke ilizwe lamaFilisti, nelizwe lonke lamaGeshuri;', 'XHO22'),
(10, 13, 3, 'lithabathela eSihore ekufuphi neYiputa, lisa emdeni we-Ekron, ngasentla, apho ababalwayo khona amaKanan, eziizikhulu ezihlanu zamaFilisti; nabaseGaseti, nabaseAshdode, nabase-Eskloni, nabaseGati, nabase-Ekron; kunye ne-Avites:', 'XHO22'),
(10, 13, 4, 'lithabathela ezantsi, lonke ilizwe lamaKanan, nelaseMera, elikhangelene neTsidon, lesa eAfeki, nomda wama-Amori;', 'XHO22'),
(10, 13, 5, 'ilizwe laseGibhile nayo yonke iLebhanon ngasempuma, ukusuka eBhahali-gadi phantsi kweNtaba yeHermon ukuya eHamati.', 'XHO22'),
(10, 13, 6, 'Bonke abemi beentaba ukususela eLebhanon kuse eMisrefoti-mayim nawo onke amaSidon, ndiya kubagxotha phambi koonyana bakaSirayeli; nilabe ngamaqashiso oonyana bakaSirayeli, libe lilifa labo, njengoko ndaniwiselayo umthetho.', 'XHO22'),
(10, 13, 7, 'Uzahlule ngoku eli lizwe, libe lilifa lezizwe ezisithoba ezinesiqingatha sesizwe sakwaManase.', 'XHO22'),
(10, 13, 8, 'Balizuza ilifa oonyana bakaRubhen namaGadi, awabanika lona uMoses nganeno kweYordan ngasempumalanga, njengoko wabanikayo uMoses umkhonzi kaYehova;', 'XHO22'),
(10, 13, 9, 'lithabathela eArohere, eseludinini lomlanjana oyiArnon, nesixeko esisembindini womlambo, nentlango yonke, ithabathele eMedebha, iye eDibhon;', 'XHO22'),
(10, 13, 10, 'nemizi yonke kaSihon ukumkani wama-Amori, obephethe eHeshbhon, wesa emdeni woonyana baka-Amon;', 'XHO22'),
(10, 13, 11, 'nelaseGiliyadi, nelizwe lamaGeshuri, nelama-Aga, nentaba yonke yeHermon, neBhashan yonke, kwesa eSaleka;', 'XHO22'),
(10, 13, 12, 'bonke ubukumkani bukaOgi eBhashan, obephethe iAshtaroti ne-Edreyi, oshiye phakathi kwamaEfrayim; ngokuba bamoyisayo uMoses, babagxotha.', 'XHO22'),
(10, 13, 13, 'Kodwa oonyana bakaSirayeli abazange bawagxothe amaGeshuri namaMagati, kodwa amaGeshuri namaMagati asahlala phakathi koonyana bakaSirayeli de kube namhla.', 'XHO22'),
(10, 13, 14, 'Kuphela sisizwe sakwaLevi, angabanikanga lifa; amadini anyukayo kaYehova, uThixo kaSirayeli, alilifa labo, njengoko wathethayo kubo.', 'XHO22'),
(10, 13, 15, 'uMoses wababela ilifa oonyana bakaRubhen ngokwemizalwane yabo.', 'XHO22'),
(10, 13, 16, 'Umda wabo wathabathela eArohere, eseludinini lomlanjana oyiArnon, nomzi ophakathi komlambo, nentlango yonke, neMedebha;', 'XHO22'),
(10, 13, 17, 'iHeshbhon nemizi yayo yonke entilini; iDibhon, neMoti-bhahali, neBhete-bhahali-mehon;', 'XHO22'),
(10, 13, 18, 'neYahasa, neKedemoti, neMefati;', 'XHO22'),
(10, 13, 19, 'neKiriyatayim, neSibhema, neTsarete-sahare, entabeni yentili;', 'XHO22'),
(10, 13, 20, 'neBhete-pehore, neAshdoti, nePisga, neBhete-yeshimoti;', 'XHO22'),
(10, 13, 21, 'nemizi yonke yentlango, nobukumkani bonke bukaSihon, ukumkani wama-Amori, obephethe eHeshbhon, awambulalayo uMoses, nezikhulu zakwaMidiyan, uEvi, noRekem, noTsure, noHuri, noRebha, abathetheli bakwaMidiyan, noRekem, noHure, noRebha kwaMidiyan uSihon, ohlala kwelo lizwe.', 'XHO22'),
(10, 13, 22, 'uBhileham unyana kaBhehore, umvumi, wababulala oonyana bakaSirayeli ngekrele, phakathi kwabaxabelayo.', 'XHO22'),
(10, 13, 23, 'Umda woonyana bakaRubhen waba yiYordan nommandla wayo. Lilo elo ilifa loonyana bakaRubhen ngokwemizalwane yabo, nemizi yabo, nemizana yabo.', 'XHO22'),
(10, 13, 24, 'uMoses wasinika ilifa isizwe sakwaGadi, oonyana bakaGadi, ngokweenzala zabo.', 'XHO22'),
(10, 13, 25, 'Ilizwe labo laba yiYazere nazo zonke izixeko zaseGiliyadi nesiqingatha selizwe loonyana baka-Amoni ukuya kufikelela eArohere ephambi kweRabha;', 'XHO22'),
(10, 13, 26, 'ukusuka eHeshbhon ukuya eRamati-mizpa naseBhetenim; ukusuka eMahanayim, wesa emdeni weDebiri;', 'XHO22'),
(10, 13, 27, 'nasentilini iBhete-ram, neBhete-nimra, neSukoti, neTsafon, nommandla oseleyo wobukumkani bukaSihon ukumkani waseHeshbhon, neYordan nelizwe layo, kwesa ekupheleni koLwandle oluBomvu. Kinneret, phesheya kweYordan; empuma.', 'XHO22'),
(10, 13, 28, 'Lilo elo ilifa loonyana bakaGadi ngokwemizalwane yabo, imizi yabo, nemizana yabo.', 'XHO22'),
(10, 13, 29, 'uMoses wasinika ilifa isiqingatha sesizwe sakwaManase, yaye laba lilifa lesiqingatha sesizwe soonyana bakaManase ngokwezizukulwana zabo.', 'XHO22'),
(10, 13, 30, 'Ilizwe labo lathabathela eMahanayim, neBhashan yonke, nobukumkani bonke bukaOgi ukumkani waseBhashan, nemizana yonke yakwaYahire eBhashan, imizi emashumi mathandathu;', 'XHO22'),
(10, 13, 31, 'Isiqingatha saseGiliyadi neAshtaroti ne-Edreyi, izixeko zobukumkani bukaOgi eBhashan, yaba yeyoonyana bakaMagire, unyana kaManase, isiqingatha soonyana bakaMagire ngokweentsapho zabo.', 'XHO22'),
(10, 13, 32, 'Ngawo la ke amazwe awawabelayo uMoses njengelifa kwiintlango zakwaMowabhi phesheya kweYordan, iYeriko, ngasempuma.', 'XHO22'),
(10, 13, 33, 'Kodwa uMoses akazange asinike ilifa isizwe sakwaLevi; uYehova, uThixo kaSirayeli, waba lilifa labo, njengoko wathethayo kubo.', 'XHO22'),
(10, 14, 1, 'Ngawo la amazwe abawadlayo oonyana bakaSirayeli ezweni lakwaKanan, abawanikayo umbingeleli uElazare, noYoshuwa unyana kaNun, neentloko zezindlu zoonyana bakaSirayeli, ukuba libe lilifa labo.', 'XHO22'),
(10, 14, 2, 'Ilifa labo lahlulwa ngamaqashiso, kanye njengoko uYehova wayeyalele ngoMoses, izizukulwana ezisithoba nesiqingatha sesizukulwana.', 'XHO22'),
(10, 14, 3, 'Ngokuba uMoses wazinika ilifa izizwe ezibini ezinesiqingatha nganeno kweYordan, kodwa abaLevi akabanikanga lifa phakathi kwazo.', 'XHO22'),
(10, 14, 4, 'Ngokuba oonyana bakaYosefu babezizizwe ezibini, uManase noEfrayim; Ke abaLevi ababanikanga sabelo ezweni elo, babanika kuphela imizi yokuhlala, namadlelo emfuyo yabo, nempahla yabo.', 'XHO22'),
(10, 14, 5, 'Kanye njengoko uYehova wamyalelayo uMoses, benjenjalo oonyana bakaSirayeli baza balaba ilizwe.', 'XHO22'),
(10, 14, 6, 'Beza oonyana bakaYuda kuYoshuwa eGiligali, wathi uKalebhi, unyana kaYefune, umKeni, kuye, Uyalazi wena ilizwi likaYehova, awalithethayo uYehova kuMoses, umfo wakwaThixo, ngawe nangawe, eKadeshe-bharneha.', 'XHO22'),
(10, 14, 7, 'Ndandiminyaka imashumi mane ndizelwe, ekususweni kwam nguMoses umkhonzi kaYehova, eKadeshe-bharneha, ukuba ndeye kulihlola ilizwe; ndaza ndashumayela kuye kwakhona njengoko kwakusentliziyweni yam.', 'XHO22'),
(10, 14, 8, 'Kodwa abazalwana bam, abanyuka nam, bayidimaza intliziyo yabantu, mna ke ndamlandela uYehova uThixo wam ngokupheleleyo.', 'XHO22'),
(10, 14, 9, 'Wafunga ke uMoses ngaloo mini, esithi, Inene, ilizwe elinyathelwe lunyawo lwakho loba lelakho, nelifa loonyana bakho, kude kuse ephakadeni, ngokuba ukuzalisile ukumlandela uYehova uThixo wam.', 'XHO22'),
(10, 14, 10, 'Khangela ke, uYehova undidlise ubomi le minyaka imashumi mane anamihlanu, njengoko wakuthethayo, kususela koko walithethayo uYehova elo lizwi kuMoses, oko oonyana bakaSirayeli babebhadule entlango; ngoku, yabona, ziintsuku ezimashumi asibhozo anambini neminyaka emihlanu.', 'XHO22'),
(10, 14, 11, 'Unanamhla ndisomelele njengamini wandithumayo uMoses; njengoko ayenjalo amandla am oko, asenjalo nangoku amandla am okulwa, kwa-awokuphuma nawokubuya.', 'XHO22'),
(10, 14, 12, 'Ke ngoko ndinike ezo ntaba wathetha zona uYehova ngaloo mini; ngenxa yokuba weva ngaloo mini ukuba ama-Anake ayelapho nokuba izixeko ezikhulu zenziwe iinqaba; ukuba uYehova ungakum, ndokwazi ukubagxotha, njengoko watshoyo uYehova.', 'XHO22'),
(10, 14, 13, 'uYoshuwa wamsikelela, wamnika iHebron uKalebhi unyana kaYefune, ukuba ibe lilifa.', 'XHO22'),
(10, 14, 14, 'Ngoko iHebron yaba lilifa likaKalebhi unyana kaYefune umKenazi de kube namhla, ngenxa yokuba wamlandela ngokupheleleyo uYehova uTHIXO kaSirayeli.', 'XHO22'),
(10, 14, 15, 'Igama leHebron ngenxa engaphambili beliyiKiriyati-arbha; ke uArbha ebeyindoda enkulu phakathi kwama-Anaki. Laphumla ilizwe emfazweni.', 'XHO22'),
(10, 15, 1, 'Lilo elo ke isahlulo sesizwe soonyana bakaYuda ngokweenzala zabo; kuse emdeni wakwaEdom, entlango yeTsin ngasemzantsi, ekupheleni komzantsi.', 'XHO22'),
(10, 15, 2, 'Umda wabo ngasezantsi wathabathela elunxwemeni loLwandle lweTyuwa, kwiNyoba ebheke ezantsi.', 'XHO22'),
(10, 15, 3, 'Yaye waphuma waya ngasemzantsi weMahale-hakrabhim wadlulela eTsin wenyuka waya ngasemzantsi weKadeshe-bharneha wadlulela eHetseron wenyuka waya eAdare wajikeleza iKarkaha.', 'XHO22'),
(10, 15, 4, 'Wegqitha khona apho, waya eAshmon, wegqitha waya emlanjeni waseYiputa; kwaye iindlela zaloo nxweme zazingaselwandle; olu loba lunxweme lwayo olungasezantsi.', 'XHO22'),
(10, 15, 5, 'Umda wasempumalanga luLwandle lweTyuwa, use ekupheleni kweYordan. Umda wabo ngangasentla wathabathela elunxwemeni lolwandle, ekupheleni kweYordan;', 'XHO22'),
(10, 15, 6, 'wenyuka umda waya eBhete-hogela, wegqitha waya emantla eBhete-harabha; yaye umsonto wenyuka waya kwilitye likaBhohan unyana kaRubhen.', 'XHO22'),
(10, 15, 7, 'Umda unyukela eDebhiri usuka entlanjeni yaseAgore uye ngasemntla eGiligali, phambi kwethambeka eliya eAdumim, esemzantsi womEfrate; kwaye ukuphuma kwayo kwaba yi-Enrogele.', 'XHO22'),
(10, 15, 8, 'Wenyuka umda ngomfula woonyana bakaHinom, ngasemzantsi wamaYebhusi; YiYerusalem ke leyo; wenyuka umda waya encotsheni yentaba, malunga nomfula wakwaHinom, ngasentshonalanga, osekuphekeni kwentili yamaRafa ngasentla.', 'XHO22'),
(10, 15, 9, 'Wegqitha umda encotsheni yentaba, waya emthonjeni wamanzi aseNefetota, wegqitha waya emizini yentaba ye-Efron; yaye umda waphuma waya eBhahali, oko kukuthi, iKiriyati-yarim.', 'XHO22'),
(10, 15, 10, 'Umda waphuma entshonalanga eBhahali, waya entabeni yakwaSehire, wegqitha waya egxalabeni leNtaba yeYeharim engasentla: oko kukuthi, iKesalon, wehla waya eBhete-shemeshe, wegqitha waya eTimna.', 'XHO22'),
(10, 15, 11, 'Yaye umda waphuma waya kwicala le-Ekron ngasemntla, yaye umda waphuma waya kufika eShikron wadlulela kwintaba kaBhahali wadlulela eYabheneli; Umda wawuselwandle.', 'XHO22'),
(10, 15, 12, 'Umda wasentshonalanga wawuseLwandle oluKhulu, umda walo. Nguwo lowo umda woonyana bakaYuda ngeenxa zonke, ngokwemizalwane yabo.', 'XHO22'),
(10, 15, 13, 'uKalebhi unyana kaYefune wanikezwa isabelo phakathi koonyana bakaYuda, njengoko uYehova wamwiselayo umthetho uYoshuwa, umzi ka-Arbha, uyise kaEnaki, eyiHebron ke leyo.', 'XHO22'),
(10, 15, 14, 'uKalebhi wabagqogqa oonyana abathathu baka-Anaki, uSeshayi, noAkiman, noTalemayi, oonyana baka-Anaki;', 'XHO22'),
(10, 15, 15, 'Wesuka khona waya kubemi baseDebhiri; igama leDebhiri laliyiKiriyati-sefere ngaphambi koko.', 'XHO22'),
(10, 15, 16, 'Wathi uKalebhi, Othe wayithimba iKiriyati-sefere, wayithimba, ndomnika intombi yam uAhazi, ibe ngumkakhe.', 'XHO22'),
(10, 15, 17, 'Wangena uOteniyeli, unyana kaKenazi, umninawa kaKalebhi, wamnika ke intombi yakhe, uAkisa, yangumkakhe.', 'XHO22'),
(10, 15, 18, 'Efikile ke kuye, wamvuselela ukuba acele umhlaba kuyise; wakhwela eesileni lakhe; Wathi uKalebhi kuye, Ùfuna ntoni na?', 'XHO22'),
(10, 15, 19, 'Wathi, Ndisikelele; ngokuba endinike ilizwe lasezantsi; ndinike imithombo yamanzi. Wamnika amaqula aphezulu namaqula angaphantsi.', 'XHO22'),
(10, 15, 20, 'Lilo elo ilifa lesizwe soonyana bakaYuda ngokweenzala zabo.', 'XHO22'),
(10, 15, 21, 'Imizi yokugqibela yesizwe soonyana bakaYuda emdeni ongezantsi kaEdom yiKabhetsele, ne-Edere, neYagure;', 'XHO22'),
(10, 15, 22, 'noKina, noDimona, noAdada,', 'XHO22'),
(10, 15, 23, 'neKedeshe, neHatsore, neItnan;', 'XHO22'),
(10, 15, 24, 'ooSife, noTeleme, noBheloti,', 'XHO22'),
(10, 15, 25, 'neHatsore, neHadati, neKeriyoti, neHetseron, eyiHatsore ke leyo;', 'XHO22'),
(10, 15, 26, 'uAmam noShema noMolada,', 'XHO22'),
(10, 15, 27, 'neHasargada, neHeshmon, neBhete-paleti;', 'XHO22'),
(10, 15, 28, 'neHatsareshuali, neBher-shebha, neBhisyoti;', 'XHO22'),
(10, 15, 29, 'uBhahali, no-Iyim, noAshem,', 'XHO22'),
(10, 15, 30, 'neElitoladi, neKesile, neHorma;', 'XHO22'),
(10, 15, 31, 'neTsikelage, neMademana, neSansana;', 'XHO22'),
(10, 15, 32, 'neLebhawoti, neSilehim, neAyin, neRimon; iyodwa imizi emashumi mabini anesithoba, inemizana yayo;', 'XHO22'),
(10, 15, 33, 'nasentilini iEshtawoli, neTsoreya, neAsna;', 'XHO22'),
(10, 15, 34, 'neSanoage, ne-Enganim, neTapuge, neEnam;', 'XHO22'),
(10, 15, 35, 'iYarmum neAdulam, iSogo neAzeki,', 'XHO22'),
(10, 15, 36, 'neSharaim, neAditayim, neGedera, neGederotayim; izixeko ezilishumi elinesine nemizana yazo.', 'XHO22'),
(10, 15, 37, 'iSenen, neHadasha, neMigdalagade;', 'XHO22'),
(10, 15, 38, 'neDileyan, neMitspe, neYoketeli;', 'XHO22'),
(10, 15, 39, 'iLakishe, neBhoskate, neEglon;', 'XHO22'),
(10, 15, 40, 'neKabhone, neLamame, neKitlishe,', 'XHO22'),
(10, 15, 41, 'neGederoti, neBhete-dagon, neNahama, neMakeda; izixeko ezilishumi elinesithandathu nemizana yazo.', 'XHO22'),
(10, 15, 42, 'iLibhena, noEtere, noAsan,', 'XHO22'),
(10, 15, 43, 'noYifeta, noAsna, noNesibhi;', 'XHO22'),
(10, 15, 44, 'neKehila, neAgsibe, neMaresa; kukho izixeko ezisibhozo nemizana yazo.', 'XHO22'),
(10, 15, 45, 'iEkron namadlelo ayo, namadlelo ayo;', 'XHO22'),
(10, 15, 46, 'kwathabathela e-Ekron ngaselwandle, ilizwe elilelonke elingaseAshdode, namagxamesi ayo;', 'XHO22'),
(10, 15, 47, 'iAshdode namadlelo ayo, namadlelo ayo, neGaza namadlelo ayo, namadlelo ayo, kwesa emlanjeni waseYiputa, nolwandle olukhulu, nomda walo;', 'XHO22'),
(10, 15, 48, 'nakweleentaba, iSamire, neYatire, neSoko;', 'XHO22'),
(10, 15, 49, 'noDana, noKiryatsana, onguDebiri,', 'XHO22'),
(10, 15, 50, 'noAnabhe, noEstemo, noAnime;', 'XHO22'),
(10, 15, 51, 'neGoshen, neHolon, neGilo; imizi elishumi elinamnye, inemizana yayo.', 'XHO22'),
(10, 15, 52, 'Ama-Arabhu namaDuma namaEsane,', 'XHO22'),
(10, 15, 53, 'neYanum, neBhete-puge, neAfeka;', 'XHO22'),
(10, 15, 54, 'neHumta, neKiriyatarbha, yiHebron ke leyo, neTsore; kukho izixeko ezisibhozo nemizana yazo.', 'XHO22'),
(10, 15, 55, 'iMahon, iKarmele, iZifi, neJuta,', 'XHO22'),
(10, 15, 56, 'neYizereli, neYokedeham, neSanowagi;', 'XHO22'),
(10, 15, 57, 'iKayin, iGibheha, neTimna; izixeko zayo ezilishumi;', 'XHO22'),
(10, 15, 58, 'iHalehule, neBhete-shure, neGedore;', 'XHO22'),
(10, 15, 59, 'neMarati, neBhete-anoti, neElitekon; izixeko ezithandathu nemizana yazo.', 'XHO22'),
(10, 15, 60, 'iKiriyati-bhahali (yiKiriyati-yeharim ke leyo) neRabha; imizi emibini nemizana yayo.', 'XHO22'),
(10, 15, 61, 'Entlango, iBhetarabha, iMidine, neSekaka,', 'XHO22'),
(10, 15, 62, 'neNibhesan, nesixeko saseLetswayi, ne-Engedi; izixeko ezithandathu nemizana yazo.', 'XHO22'),
(10, 15, 63, 'Ke amaYebhusi, abengabemi baseYerusalem, ababanga nako ukuwagqogqa oonyana bakaYuda; kodwa amaYebhusi asaqhubeka ehlala eYerusalem noonyana bakaYuda de kube namhla.', 'XHO22'),
(10, 16, 1, 'Laphuma iqashiso loonyana bakaYosefu eYordan, baya eYeriko, emanzini aseYeriko, ngasempumalanga, entlango enyuka iye eYeriko, ngasentabeni yaseBheteli;', 'XHO22'),
(10, 16, 2, 'Wesuka eBheteli, waya eLuse, wegqitha waya emdeni weArki, waya eAtaroti.', 'XHO22'),
(10, 16, 3, 'Wehla entshonalanga, waya emdeni weYafeleti, waya emdeni weBhete-horon-ezantsi, naseGezere; yaye ukuphuma kwayo kukufuphi nolwandle.', 'XHO22'),
(10, 16, 4, 'Balithabatha ke ilifa labo oonyana bakaYosefu, uManase noEfrayim.', 'XHO22'),
(10, 16, 5, 'Umda woonyana bakaEfrayim ngokweentsapho zabo wawumi ngolu hlobo: Umda welifa labo ngasempuma wawuyiAtarota-dare, uye kufikelela eBhete-horon phezulu;', 'XHO22'),
(10, 16, 6, 'waba umda waya elwandle, wesa eMigmeta ngecala langasentla; umda waphuma ngasempumalanga eTahanati-silo, waya eYanoha ngasempumalanga;', 'XHO22'),
(10, 16, 7, 'Wehla eYanowa, waya eAtaroti naseNahare, wafika eYeriko, wema eYordan.', 'XHO22'),
(10, 16, 8, 'Wesuka umda eTapuge ngasentshonalanga, waya emlanjeni waseKana; yaye indlela yawo yokuphuma yayingaselwandle. Lilo elo ilifa loonyana bakaEfrayim ngokwemizalwane yabo.', 'XHO22'),
(10, 16, 9, 'Imizi yoonyana bakaEfrayim yaba phantsi kwelifa loonyana bakaManase, yonke imizi nemizana yayo.', 'XHO22'),
(10, 16, 10, 'Abawagqogqanga amaKanan abemi eGezere; ke wona amaKanan aqhubeka ehlala phakathi kwamaEfrayim de kube namhla, yaye asahlawulwa.', 'XHO22'),
(10, 17, 1, 'Isizwe sakwaManase besisabaninzi; ngokuba ebengowamazibulo kuYosefu; Lowo nguMagire, amazibulo kaManase, uyise kaGiliyadi; ngokuba ebeyindoda yokulwa, wayidla ilifa iGiliyadi neBhashan.', 'XHO22'),
(10, 17, 2, 'Baba baninzi koonyana bakaManase ngokwemizalwane yabo; koonyana baka-Abhiyezere, noonyana bakaHeleki, noonyana baka-Aseriyeli, noonyana bakaShekem, noonyana bakaHefere, noonyana bakaShemida; ngabo abo oonyana bakaManase, unyana kaYosefu, ngokwemizalwane yabo.', 'XHO22'),
(10, 17, 3, 'Ke uTselofehadi, unyana kaHefere, unyana kaGiliyadi, unyana kaMagire, unyana kaManase, ebengenanyana; eneentombi zodwa; Ngawo la amagama eentombi zakhe: nguMagla, noNowa, noHogla, noMilka, noTirtsa.', 'XHO22'),
(10, 17, 4, 'Beza kuElazare umbingeleli, nakuYoshuwa unyana kaNun, nakubathetheli, bathi, uYehova wamwisela umthetho uMoses, ukuba asinike ilifa phakathi kwabazalwana bethu. Ngoko wabanika ilifa phakathi kwabantakwaboyise ngokomyalelo kaYehova.', 'XHO22'),
(10, 17, 5, 'Zaba lishumi izahlulo kuManase ngaphandle kwelizwe laseGiliyadi, neBhashan ephesheya kweYordan;', 'XHO22'),
(10, 17, 6, 'Ngokuba iintombi zikaManase zabelwa ilifa phakathi koonyana bakhe; nelizwe laseGiliyadi laba nabanye oonyana bakaManase.', 'XHO22'),
(10, 17, 7, 'Umda kaManase wathabathela kwa-Ashere, waya eMigmeta ephambi kweShekem; kwaye umgca uhamba ngqo kubantu base-Entapuag.', 'XHO22'),
(10, 17, 8, 'Ilizwe laseTapuwa laba lelakwaManase, kodwa iTapuwa, emdeni kaManase, yaba yeyoonyana bakaEfrayim;', 'XHO22'),
(10, 17, 9, 'Wehla umda wommandla waya emfuleni iKana, ezantsi komlambo; Le mizi yakwaEfrayim yayiphakathi kwemizi yakwaManase; Umda kaManase waba ngasentla koMlambo, ukuma kwawo waya wema ngolwandle;', 'XHO22'),
(10, 17, 10, 'Ngasezantsi laba lelakwaEfrayim, ngasentla laba lelikaManase; ulwandle lwaba ngumda walo; bahlanganisana kwa-Ashere ngasentla, nakwaIsakare ngasempumalanga.', 'XHO22'),
(10, 17, 11, 'uManase wayidla ilifa iBhete-shehan namadlelo ayo, neIbleyam namadlelo ayo, nabemi beDore namadlelo ayo, nabemi base-Endore namadlelo ayo, nabemi baseTahanaki namadlelo ayo abemi baseMegido namagxamesi ayo: amazwe amathathu.', 'XHO22'),
(10, 17, 12, 'Babengenako oonyana bakaManase ukuyigqogqa loo mizi; kodwa amaKanan afuna ukuhlala kwelo lizwe.', 'XHO22'),
(10, 17, 13, 'Ke oonyana bakaSirayeli bazinikela amandla, bawahlawulisa amaKanan; kodwa akabakhuphanga konke konke.', 'XHO22'),
(10, 17, 14, 'Bathetha oonyana bakaYosefu kuYoshuwa, bathi, Yini na ukuba usinike ilifa lalinye, nesabelo sibe sinye, ekubeni ndiluhlanga olukhulu, uYehova endisikelele unangoku?', 'XHO22'),
(10, 17, 15, 'Wathi uYoshuwa kubo, Ukuba niluhlanga olukhulu, nyukani niye ehlathini, nimise ezweni lamaPersi nelakwaEfrayim, njengokuba ixinene kuni intaba yakwaEfrayim.', 'XHO22'),
(10, 17, 16, 'Bathi oonyana bakaYosefu, Induli le ayisilingene; yaye onke amaKanan awayehlala kwilizwe elisentlanjeni aneenqwelo zokulwa zentsimbi, lawo ayevela eBheteshan needolophu ezilapho nabo babevela entlanjeni eYizereli.', 'XHO22'),
(10, 17, 17, 'Wathi uYoshuwa kwindlu kaYosefu, uEfrayim noManase, Niluhlanga olukhulu, ninamandla amakhulu; mhlawumbi awunalo ikamva elinye.', 'XHO22'),
(10, 17, 18, 'Ke yona intaba iya kuba yeyenu; kuba lungumthi, wowugawula; nezithako zayo ziya kuba zezakho; kuba niya kuwagqogqa amaKanan, nakuba eneenqwelo zentsimbi yaye omelele.', 'XHO22'),
(10, 18, 1, 'Labizelwa ndawonye lonke ibandla loonyana bakaSirayeli eShilo, bayimisa khona intente yokuhlangana. Kwaye ihlabathi loyiswa phambi kwabo.', 'XHO22'),
(10, 18, 2, 'Kwasala phakathi koonyana bakaSirayeli izizwe ezisixhenxe ezazingekanikwa lifa lazo.', 'XHO22'),
(10, 18, 3, 'Wathi uYoshuwa koonyana bakaSirayeli, Kuya kuda kube nini na nilibele ukulidla ilifa ilizwe elo aninikileyo uYehova uThixo wooyihlo?', 'XHO22'),
(10, 18, 4, 'Ndinike amadoda abe mathathu esizweni ngasinye, ndiwathume, anduluke alicenge ilizwe, alibhale ngokwelifa lawo yaye baya kubuyela kum.', 'XHO22'),
(10, 18, 5, 'Aya kulahlula libe ziinxalenye ezisixhenxe: uYuda uya kuhlala kummandla wakhe ngasezantsi, indlu kaYosefu ime kummandla wayo ngasentla.', 'XHO22'),
(10, 18, 6, 'Wolibhala ilizwe libe ziinxalenye ezisixhenxe, nizizise kum ukuqonda, ukuze ndenze ngokwamaqashiso phambi koYehova uThixo wethu.', 'XHO22'),
(10, 18, 7, 'Ke abaLevi abanasabelo kuni; ngokuba ilifa labo bububingeleli bukaYehova; yaye uGadi noRubhen nesiqingatha sesizwe sakwaManase banikwa ilifa labo nganeno kweYordan ngasempumalanga, awabanika lona uMoses umkhonzi kaYehova.', 'XHO22'),
(10, 18, 8, 'Asuka amadoda lawo, emka; uYoshuwa wabawisela umthetho abo baza kulibhala ilizwe, wathi, Hambani nelizwe, nilibhale, nibuyele kum, ndinenzele amaqashiso apha phambi koYehova, Shilo.', 'XHO22'),
(10, 18, 9, 'Ahamba amadoda lawo, alityhutyha ilizwe, alibhala encwadini ngokwemizi yawo, abe ngamacandelo asixhenxe; abuyela kuYoshuwa ekulweni eShilo.', 'XHO22'),
(10, 18, 10, 'Wawawisela amaqashiso uYoshuwa eShilo phambi koYehova, uYoshuwa wababela ilizwe oonyana bakaSirayeli ngokwezabelo zabo khona.', 'XHO22'),
(10, 18, 11, 'Laphuma iqashiso lesizwe soonyana bakaBhenjamin ngokwemizalwane yabo; umda welifa labo waphuma phakathi koonyana bakaYuda noonyana bakaYosefu.', 'XHO22'),
(10, 18, 12, 'Umda wabo wathabathela eYordan; yaye umda unyuka uye kwicala laseYeriko ngasemntla uze unyuke iintaba ngasentshona; ukuma kwawo, kwaba sentlango yaseBhetaven.', 'XHO22'),
(10, 18, 13, 'Umda waphuma khona, waya eLuse, ecaleni kweLuse ngasezantsi: yiBheteli ke lero; yaye umda wehla uye eAtaroti-adare ngasendulini esemzantsi weBhete-horon ezantsi.', 'XHO22'),
(10, 18, 14, 'Wahlaba umda, wajika waya embombeni yolwandle, waya ngasezantsi, usuka entabeni engaseBhete-horon ngasezantsi; waphuma, waya eKiriyati-bhahali, oko kukuthi, iKiriyati-yeharim, umzi woonyana bakaYuda; yayiyiwadi yasentshona.', 'XHO22'),
(10, 18, 15, 'Icala lasezantsi lathabathela ekupheleni kweKiriyati-yeharim, waphuma umda waya entshonalanga, wegqitha waya equleni laseNefetoa.', 'XHO22'),
(10, 18, 16, 'Wehla umda waya ekupheleni kwentaba phambi kwentlambo yonyana kaHinom entlanjeni yeeNefili ngasemntla, yaye wehla waya kwintlambo kaHinom entlanjeni yaseYebhusi uye ngasezantsi wehle uye e-Enerogele;', 'XHO22'),
(10, 18, 17, 'Wesuka entla, waphuma waya e-Enesem, waphuma waya eGeliloti, malunga nethambeka laseAdumim, wehla waya engxondorheni kaBhohan unyana kaRubhen.', 'XHO22'),
(10, 18, 18, 'Wegqitha waya egxalabeni leArabha, waya ngasentla, wehla waya eArabha;', 'XHO22'),
(10, 18, 19, 'wegqitha umda waya egxalabeni laseBhete-hogela ngasentla; umda wegqitha waya encamini engasentla yoLwandle lweTyuwa, ekupheleni kweYordan ngasezantsi; yayilunxweme lwasezantsi.', 'XHO22'),
(10, 18, 20, 'iYordan yaba ngumda walo ngasempumalanga. Lilo elo ilifa loonyana bakaBhenjamin, nelizwe lakhe kwizizukulwana zabo.', 'XHO22'),
(10, 18, 21, 'Imizi yesizwe soonyana bakaBhenjamin ngokwezindlu zabo yayiyiYeriko, neBhete-hogela, nomfula wamaKetsi;', 'XHO22'),
(10, 18, 22, 'neBhete-rabha, neShemarayim, neBheteli;', 'XHO22'),
(10, 18, 23, 'noAvim, nePara, neOfra,', 'XHO22'),
(10, 18, 24, 'neKefarha-amonayi, neOfeni, neGebha; imizi elishumi elinamibini, inemizana yayo;', 'XHO22'),
(10, 18, 25, 'iGibheyon, neRama, neBheroti;', 'XHO22'),
(10, 18, 26, 'neMizpa, neKefira, noMoses;', 'XHO22'),
(10, 18, 27, 'noRekem, noIrepeli, noTarara;', 'XHO22'),
(10, 18, 28, 'neSela, ne-Elefe, neYebhusi (yiYerusalem ke leyo), neGibheya, neKiriyati; izixeko ezilishumi elinesine nemizana yazo. Lilo elo ilifa loonyana bakaBhenjamin ngokweenzala zabo.', 'XHO22'),
(10, 19, 1, 'Laphuma elesibini ilifa kuSimon, emndenini woonyana bakaSimon, ngokwemizalwane yabo; yaye ilifa labo laba phakathi kwelifa loonyana bakaYuda.', 'XHO22'),
(10, 19, 2, 'Ilifa labo laba seBher-shebha neShebha neMolada;', 'XHO22'),
(10, 19, 3, 'noHasare, noBhala, noAshem,', 'XHO22'),
(10, 19, 4, 'neElitoladi, neBhethuli, neHorma;', 'XHO22'),
(10, 19, 5, 'neTsikelage, neBhete-marabhoti, neHatsarsusa;', 'XHO22'),
(10, 19, 6, 'neBhetelebhote, neSaruhen; izixeko ezilishumi elinesithathu nemizana yazo;', 'XHO22'),
(10, 19, 7, 'iAyin, neRimon, neEtere, neAsan; izixeko ezine nemizana yazo,', 'XHO22'),
(10, 19, 8, 'nemizi yonke ejikelezileyo kuloo mizi, kwesa eBhahalati-bhere (yiRama esezantsi ke leyo). Lilo elo ilifa lentsapho yoonyana bakaSimon ngokweenzala zabo.', 'XHO22'),
(10, 19, 9, 'Ilifa loonyana bakaYuda lavela elifeni loonyana bakaSimon; ngokuba isabelo soonyana bakaYuda besisikhulu kubo; ke ngoko, oonyana bakaSimon banikwa ilifa labo.', 'XHO22'),
(10, 19, 10, 'Esinye isahlulo sesithathu saya koonyana bakaZebhulon ngokwemizalwane yabo; umda welifa labo wafika eSaridi.', 'XHO22'),
(10, 19, 11, 'Umda wabo wenyuka waya elwandle, naseMarala, naseDabheseti, nasemlanjeni phambi kweYokenam;', 'XHO22'),
(10, 19, 12, 'waphuma eSaridi, wasinga empumalanga, usinge empumalanga, ude uye emdeni weKislotabhore, waphuma waya eDabherati, unyuka uye eYafiya;', 'XHO22'),
(10, 19, 13, 'Wegqitha khona empumalanga, waya eGita-hefere, wesa eIttakashin, wegqitha eRimon-metoare, waya eNege;', 'XHO22'),
(10, 19, 14, 'umda wajika waya ngasentla, waya eHanaton, wema ngentili yeYaphetheli;', 'XHO22'),
(10, 19, 15, 'neKati, neNahalali, neShimron, neIdala, neBhetelehem: yimizi elishumi elinamibini inemizana yayo.', 'XHO22'),
(10, 19, 16, 'Lilo elo ilifa loonyana bakaZebhulon ngokwemizalwane yabo, loo mizi nemizana yayo.', 'XHO22'),
(10, 19, 17, 'Iqashiso lesine laphuma noIsakare, noonyana bakaIsakare ngokweentsapho zabo.', 'XHO22'),
(10, 19, 18, 'Umda wabo waya eYizereli, neKesuloti, neShunem;', 'XHO22'),
(10, 19, 19, 'namaHafari, neZiyon, neAnaharati,', 'XHO22'),
(10, 19, 20, 'neRabhiti, noKiyon, noAbheshe;', 'XHO22'),
(10, 19, 21, 'neRemete, ne-Enganim, ne-Enehada, neBhete-passes;', 'XHO22'),
(10, 19, 22, 'Umda osemdeni waqhubeka uye kufikelela eTabhore naseSahasim naseBhete-shemeshe; Iziphelo zelizwe labo yiYordan: imizi elishumi elinamithandathu inemizana yayo.', 'XHO22'),
(10, 19, 23, 'Lilo elo ilifa lemizalwane yoonyana bakaIsakare ngokwemizalwane yabo, imizi yabo, nemizana yabo.', 'XHO22'),
(10, 19, 24, 'Lavela iqashiso lesihlanu nesizwe soonyana baka-Ashere ngokwemizalwane yabo.', 'XHO22'),
(10, 19, 25, 'Umda wabo waba yiHelekati, neHali, neBhetene, neAgsafu;', 'XHO22'),
(10, 19, 26, 'noAlameleki, neAmade, neMishahali; Weza eKarmele ngasentshonalanga, naseSihore-libhenati;', 'XHO22'),
(10, 19, 27, 'Wajika waya ngasempuma, ngaseBhete-dagon, wafika kwaZebhulon nakwintlambo yomlambo oqukuqelayo waseYaphetheli kumntla weBhete-temeke neNehiyeli waya eKabhuli ngasekhohlo;', 'XHO22'),
(10, 19, 28, 'neHebron, neRehobhi, neHamon, neKana, neTsidon enkulu;', 'XHO22'),
(10, 19, 29, 'umda waya eRama, wesa emzini onqatyisiweyo oyiTire; yaye umda wommandla wawubheke eHosa; waphuma elwandle, evela ngaselunxwemeni, waya eAgsibe;', 'XHO22'),
(10, 19, 30, 'neUma, neAfeki, neRehobhi: yimizi emashumi mabini anamibini, inemizana yayo.', 'XHO22'),
(10, 19, 31, 'Lilo elo ilifa lemizalwane yoonyana baka-Ashere ngokwemizalwane yabo, loo mizi nemizana yayo.', 'XHO22'),
(10, 19, 32, 'Iqashiso lesithandathu laba leloonyana bakaNafetali, noonyana bakaNafetali ngokweentsapho zabo.', 'XHO22'),
(10, 19, 33, 'Umda wabo wathabathela eHelefe, eAlon, wesa eSahananim, neAdami, neNekebhi, neYabheneli, wesa eLakum; ukuphuma kwawo kwaba yiYordan;', 'XHO22'),
(10, 19, 34, 'Umda waya ngasentshona eAshnot-tare, ukusuka apho waya eHukoki, waya kwaZebhulon ngasemzantsi, waya kwa-Ashere ngasentshona, noYuda neYordan ngasempumalanga.', 'XHO22'),
(10, 19, 35, 'Imizi enqatyisiweyo yiSidim, neSere, neHamati, neRakati, neKinerete;', 'XHO22'),
(10, 19, 36, 'noAdam, neRama, neHatsore,', 'XHO22'),
(10, 19, 37, 'neKedeshe, ne-Edreyi, ne-Enetsore,', 'XHO22'),
(10, 19, 38, 'noYister, neMigdaleli, neHorem, neBhete-shemeshe, neBhete-shemeshe; izixeko ezilishumi elinethoba nemizana yazo.', 'XHO22'),
(10, 19, 39, 'Lilo elo ilifa lentsapho yoonyana bakaNafetali ngokweentsapho zabo, izixeko needolophana.', 'XHO22'),
(10, 19, 40, 'Laphuma iqashiso lesixhenxe nemizalwane yoonyana bakaDan ngokwemizalwane yabo;', 'XHO22'),
(10, 19, 41, 'Ilizwe lelifa labo laba yiTsoreya, ne-Eshtawoli, neIrshemeshe;', 'XHO22'),
(10, 19, 42, 'neShahalabhin, neAyalon, neYetla;', 'XHO22'),
(10, 19, 43, 'neElon, neTimna, ne-Ekron;', 'XHO22'),
(10, 19, 44, 'neEliteki, neGibheton, neBhahalati;', 'XHO22'),
(10, 19, 45, 'noYuda, noBhen-bheraki, neGati-rimon;', 'XHO22'),
(10, 19, 46, 'neMejarkon, neRakon, nomda omalunga neYafo.', 'XHO22'),
(10, 19, 47, 'Umhlaba woonyana bakaDan ubemncinane kubo; ke ngoko, oonyana bakaDan bahamba baya kulwa neLesem baza bayithimba baza bayoyisa ngohlangothi lwekrele baza bayithimba baza bahlala kuyo baza bayibiza iLesem Dan ngegama likaDan uyise.', 'XHO22'),
(10, 19, 48, 'Lilo elo ilifa lemizalwane yoonyana bakaDan ngokwemizalwane yabo, abo bahlali, nemizana yabo.', 'XHO22'),
(10, 19, 49, 'Bakugqiba ukulaba ilizwe ngokwelizwe labo, oonyana bakaSirayeli bamnika uYoshuwa, unyana kaNun, ilifa phakathi kwabo.', 'XHO22'),
(10, 19, 50, 'Ngokomlomo kaYehova bamnika umzi awawubizayo, iTimnati-sera, ekweleentaba lakwaEfrayim; wakha isixeko, wahlala kuso.', 'XHO22'),
(10, 19, 51, 'Ngawo lawo amafa abawabelwa ngeqashiso umbingeleli uElazare, noYoshuwa unyana kaNun, neentloko zezizwe zoonyana bakaSirayeli, ngeqashiso eShilo phambi koYehova, ekungeneni kwentente, njengoko ilizwe laba lilifa yahlulwe ilifa lebandla. Bayeka ke ukulaba ilizwe.', 'XHO22'),
(10, 20, 1, 'Wathetha uYehova kuYoshuwa, wathi,', 'XHO22'),
(10, 20, 2, '“Thetha koonyana bakaSirayeli, uthi, ‘Zishenxiseni izixeko ezikhulu endathetha ngazo kuni ngoMoses;', 'XHO22'),
(10, 20, 3, 'ukuze abalekele khona umbulali obulele umntu ngokungazi; ibe yindawo yenu yokusabela kumphindezeli wegazi.', 'XHO22'),
(10, 20, 4, 'Ukuba uthe wasabela komnye waloo mizi, wema ekungeneni kwesango lomzi, wayithetha indaba yakhe, apho aphulaphuleyo amadoda amakhulu aloo mzi, umse kuwo umzi, umnike indawo, ukuba ahlale kuyo. Pha unokuhlala phakathi kwabo.', 'XHO22'),
(10, 20, 5, 'Ukuba umphindezeli wegazi uthe wamsukela, musa ukumnikela umbulali lowo esandleni sakhe; kuba wabetha ummelwane wakhe engazi, akazange amthiye ngaphambili.', 'XHO22'),
(10, 20, 6, 'Wohlala kuloo mzi ade eme phambi kwebandla, agwetywe, ade afe umbingeleli omkhulu oya kuba elapho ngaloo mihla kwisixeko awayebalekele kuso.', 'XHO22'),
(10, 20, 7, 'Bamisa iKedeshe yaseGalili eNtabeni yakwaNafetali, neShekem kweleentaba lakwaEfrayim, neKiriyatarebha, oko kukuthi, iHebron, ezintabeni zakwaYuda.', 'XHO22'),
(10, 20, 8, 'Ngaphesheya kweYordan, eYeriko ngasempumalanga, banyula iBhete-tsere entlango, kwisizwe sakwaRubhen, neRamoti yaseGiliyadi, kwisizwe sakwaGadi, neGolan eBhashan, kwisizwe sakwaManase.', 'XHO22'),
(10, 20, 9, 'Zizo ezi izixeko ezigcinelwe oonyana bakaSirayeli bonke, nomphambukeli ophambukele phakathi kwabo, ukuze abalekele khona bonke ababulele umntu ngengozi, angabulawa ngumphindezeli wegazi, ade eme phambi kwebandla.', 'XHO22'),
(10, 21, 1, 'Zasondela ke iintloko zezindlu zooyise zabaLevi kuElazare umbingeleli, nakuYoshuwa unyana kaNun, nakwiintloko zezindlu zooyise zoonyana bakaSirayeli.', 'XHO22'),
(10, 21, 2, 'Bathetha kubo eShilo, ezweni lakwaKanan, bathi, uYehova uwise umthetho ngoMoses, ukuba siyinikwe imizi esiya kuhlala kuyo, namadlelo ayo abe ngawempahla emfutshane.', 'XHO22'),
(10, 21, 3, 'Oonyana bakaSirayeli babanika abaLevi ezo mizi, namadlelo ayo, elifeni labo, ngokomlomo kaYehova.', 'XHO22'),
(10, 21, 4, 'Laphuma iqashiso ngemizalwane yamaHeti; Oonyana bombingeleli uAron, abangabaLevi, babela isizwe sakwaYuda, nesizwe sakwaSimon, nesizwe sakwaBhenjamin, imizi elishumi elinamithathu.', 'XHO22'),
(10, 21, 5, 'Abanye oonyana bakaKehati banikwa iintsapho zesizwe sakwaEfrayim nesizwe sakwaDan nesiqingatha sesizwe sakwaManase ngeqashiso.', 'XHO22'),
(10, 21, 6, 'Oonyana bakaGershon banikwa imizi elishumi elinesithathu kwiintsapho zesizwe sakwaIsakare nesizwe sakwa-Ashere nesesizwe sakwaNafetali nakwisiqingatha sesizwe sakwaManase eBhashan.', 'XHO22'),
(10, 21, 7, 'Oonyana bakaMerari ngokwemizalwane yabo banikwa esizweni sakwaRubhen, nakwisizwe sakwaGadi, nakwisizwe sakwaZebhulon, imizi elishumi elinamibini.', 'XHO22'),
(10, 21, 8, 'Oonyana bakaSirayeli babanika abaLevi ezo zixeko namadlelo azo ngeqashiso, kanye njengoko uYehova wabayalelayo ngenkonzo kaMoses.', 'XHO22'),
(10, 21, 9, 'Esizweni soonyana bakaYuda, nakwisizwe soonyana bakaSimon, labelwa le mizi magama;', 'XHO22'),
(10, 21, 10, 'ababenoonyana baka-Aron emizalwaneni yamaKehati, yoonyana bakaLevi, ngokuba laba lelabo iqashiso lokuqala.', 'XHO22'),
(10, 21, 11, 'Babanika umzi ka-Arbha, uyise ka-Enaki, owaseHebron ke lowo, ezintabeni zakwaYuda, namadlelo ayo.', 'XHO22'),
(10, 21, 12, 'Ke lona ilizwe laloo mzi, namagxamesi awo, bawanika uKalebhi unyana kaYefune, ukuba libe yinzuzo yakhe.', 'XHO22'),
(10, 21, 13, 'Babanika oonyana baka-Aron umbingeleli iHebron nedlelo layo njengesixeko esikhululekileyo sokuxhelwa; neLibhena nedlelo layo;', 'XHO22'),
(10, 21, 14, 'neYatire namadlelo ayo, neEstemowa namadlelo ayo;', 'XHO22'),
(10, 21, 15, 'neHolon namadlelo ayo, neDebhiri namadlelo ayo;', 'XHO22'),
(10, 21, 16, 'neAyin nedlelo layo, neYuta nedlelo layo, neBhete-shemeshe nedlelo layo; izixeko ezisithoba kuzo zizwe zibini.', 'XHO22'),
(10, 21, 17, 'Kwisizwe sakwaBhenjamin iGibheyon nedlelo layo, neGebha nedlelo layo,', 'XHO22'),
(10, 21, 18, 'iAnatoti nedlelo layo, neAlmon nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 19, 'Iyonke imizi yoonyana baka-Aron, ababingeleli, yaba yimizi elishumi elinamithathu inamadlelo ayo.', 'XHO22'),
(10, 21, 20, 'Imizalwane yoonyana bakaKehati, abaLevi abaseleyo koonyana bakaKehati, yanikwa isizwe sakwaEfrayim imizi yeqashiso.', 'XHO22'),
(10, 21, 21, 'Kuba babanika iShekem nedlelo layo ezintabeni zakwaEfrayim, njengesixeko esikhululekileyo sokuxhela; neGezere nedlelo layo;', 'XHO22'),
(10, 21, 22, 'neKibhetsayim nedlelo layo, neBhete-horon nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 23, 'Ezweni lesizwe sakwaDan iEliteki nedlelo layo, neGibheton nedlelo layo,', 'XHO22'),
(10, 21, 24, 'iAyalon nedlelo layo, iGati-rimon nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 25, 'Kwisiqingatha sesizwe sakwaManase: iTanaki nedlelo layo, neGathrimon nedlelo layo; izixeko ezibini.', 'XHO22'),
(10, 21, 26, 'Iyonke imizi yaba lishumi, namadlelo ayo, ngokweenzala zoonyana bakaKehati abaseleyo.', 'XHO22'),
(10, 21, 27, 'Oonyana bakaGershon, iintsapho zabaLevi, kwesinye isiqingatha sesizwe sakwaManase banikela iGolan eseBhashan nedlelo layo, njengesixeko esikhululekileyo sokuxhela; neBheshera nedlelo layo; izixeko ezibini.', 'XHO22'),
(10, 21, 28, 'Kwisizwe sakwaIsakare, iKishon nedlelo layo, neDabhere nedlelo layo;', 'XHO22'),
(10, 21, 29, 'iYarmuti nedlelo layo, i-Enganim nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 30, 'Kwisizwe sakwa-Ashere, iMisale nedlelo layo, neAbhedon nedlelo layo,', 'XHO22'),
(10, 21, 31, 'neHelekati nedlelo layo, neRehobhi nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 32, 'Kwisizwe sakwaNafetali, iKedeshe yaseGalili nedlelo layo, ibingumzi wosigezo; neHamoti-dore nedlelo layo, neKartan nedlelo layo; izixeko ezithathu.', 'XHO22'),
(10, 21, 33, 'Iyonke imizi yoonyana bakaGershon ngokwemizalwane yabo yaba yimizi elishumi elinamithathu, inamadlelo ayo.', 'XHO22'),
(10, 21, 34, 'Imizalwane yoonyana bakaMerari, engabaLevi abaseleyo, esizweni sakwaZebhulon, yiYokenam namadlelo ayo, neKarta namadlelo ayo;', 'XHO22'),
(10, 21, 35, 'iDimna nedlelo layo, iNahalale nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 36, 'Kwisizwe sakwaRubhen iBhete-tsere nedlelo layo, neYahasa nedlelo layo;', 'XHO22'),
(10, 21, 37, 'iKedemoti nedlelo layo, neMefahati nedlelo layo; izixeko ezine.', 'XHO22'),
(10, 21, 38, 'Kwisizwe sakwaGadi, iRamoti-giliyadi nedlelo layo, njengesixeko sokuxhela; neMahanayim namadlelo ayo;', 'XHO22'),
(10, 21, 39, 'iHeshbhon nedlelo layo, iYazere nedlelo layo; izixeko ezine zizonke.', 'XHO22'),
(10, 21, 40, 'Iyonke imizi yoonyana bakaMerari ngokwemizalwane yabo, abaseleyo emizalwaneni yabaLevi ngeqashiso labo yaba yimizi elishumi elinamibini.', 'XHO22'),
(10, 21, 41, 'Iyonke imizi yabaLevi ezweni loonyana bakaSirayeli yaba yimizi engamashumi amane anesibhozo, inamadlelo ayo.', 'XHO22'),
(10, 21, 42, 'Loo mizi yonke ibinamadlelo ayo ngeenxa zonke; kwaba njalo ke kuyo yonke le mizi.', 'XHO22'),
(10, 21, 43, 'uYehova wamnika uSirayeli lonke ilizwe awayefungele ukumnika ookhokho bakhe; balidle ilifa, bahlala kulo.', 'XHO22'),
(10, 21, 44, 'uYehova wawaphumza, njengoko wafungayo kooyise; Akwabakho namnye weentshaba zawo ume phezu kwawo; uYehova wazinikela esandleni sawo zonke iintshaba zawo.', 'XHO22'),
(10, 21, 45, 'Akukho nto ingekhoyo uYehova ayithiyileyo kwindlu kaSirayeli; yonke into yenzeka.', 'XHO22'),
(10, 22, 1, 'Wababiza ke uYoshuwa amaRubhen, namaGadi, nesinye isizwe sakwaManase;', 'XHO22'),
(10, 22, 2, 'Wathi kubo, Nikugcinile konke uMoses umkhonzi kaYehova abeniwisele umthetho ngako, naliphulaphula izwi lam ezintweni zonke endiniwisele umthetho ngazo.', 'XHO22'),
(10, 22, 3, 'Kangangeentsuku ezininzi anibalahlanga abazalwana benu, kodwa niye nawugcina umsebenzi womyalelo kaYehova uThixo wenu.', 'XHO22'),
(10, 22, 4, 'Kaloku ke, uYehova, uThixo wenu, ubaphumzile abazalwana benu, njengoko wathethayo kubo; buyani ke, niye ezintenteni zenu, kwelo lizwe lityebileyo, elo waninikayo uMoses, umkhonzi kaYehova, nganeno kweYordan;', 'XHO22'),
(10, 22, 5, 'Gcinani niwugcine umthetho nomthetho, awaniwisela wona uMoses umkhonzi kaYehova, ukuba nimthande uYehova uThixo wenu, nihambe ngeendlela zakhe zonke, niyigcine imithetho yakhe, ninamathele kuye, nimkhonze ngentliziyo yenu yonke iintliziyo nangomphefumlo wakho wonke.', 'XHO22'),
(10, 22, 6, 'Wabasikelela uYoshuwa, wabandulula, baya ezintenteni zabo.', 'XHO22'),
(10, 22, 7, 'uMoses wasinika isiqingatha sesizwe sakwaManase umhlaba waseBhashan, kodwa uYoshuwa wasinika esinye isabelo kubazalwana baso ngasentshona yeYordan. uYoshuwa wababuyisela ezintenteni zabo, wabasikelela;', 'XHO22'),
(10, 22, 8, 'Wathi kubo, Buyelani ezintenteni zenu ninobutyebi obukhulu, ninengqwebo eninzi, nesilivere, negolide, nobhedu, nesinyithi, neengubo ezininzi; Yabelani amaxhoba eentshaba zenu phakathi kwabazalwana benu.', 'XHO22'),
(10, 22, 9, 'Babuya oonyana bakaRubhen, noonyana bakaGadi, nesiqingatha sesizwe sakwaManase, besuka koonyana bakaSirayeli eShilo, esezweni lakwaKanan, baya ezweni laseGiliyadi, ezweni lelifa labo balihlutha ngokomlomo kaYehova, ngokomsebenzi kaMoses.', 'XHO22'),
(10, 22, 10, 'Bafika emdeni weYordan, ezweni lakwaKanan, bakha oonyana bakaRubhen, noonyana bakaGadi, nesinye isahlulo sesizwe sakwaManase, isibingelelo ngaseYordan, isibingelelo esikhulu, esingasetyenzwayo ibonwa ngamehlo.', 'XHO22'),
(10, 22, 11, 'Beva oonyana bakaSirayeli, kusithiwa, Yabonani, oonyana bakaRubhen, noonyana bakaGadi, nesinye isahlulo sesizwe sakwaManase, bakhe isibingelelo malunga nelizwe lakwaKanan, emdeni weYordan, phezu kweYordan umendo woonyana bakaSirayeli.', 'XHO22'),
(10, 22, 12, 'Beva ke oonyana bakaSirayeli, balibizela ndawonye lonke ibandla loonyana bakaSirayeli eShilo, ukuba lilwe nabo.', 'XHO22'),
(10, 22, 13, 'Baza oonyana bakaSirayeli bathumela koonyana bakaRubhen noonyana bakaGadi nakwisiqingatha sesizwe sakwaManase ezweni laseGiliyadi, uFinehasi unyana wombingeleli uEleyazare;', 'XHO22'),
(10, 22, 14, 'Wayenabathetheli abalishumi kwindlu ngendlu, engukumkani nenganga, ezizweni zonke zakwaSirayeli; Elowo wayeyintloko yendlu yooyise kumawaka akwaSirayeli.', 'XHO22'),
(10, 22, 15, 'Beza koonyana bakaRubhen, noonyana bakaGadi, nakwisiqingatha sesizwe sakwaManase, ezweni laseGiliyadi, bathetha kubo, besithi,', 'XHO22'),
(10, 22, 16, 'Kwathiwa kwibandla lonke likaYehova, Bubugwenxa buni na obu, nimeneze ngabo kuThixo kaSirayeli, ekubuyeni kwenu kuYehova namhla, ngokuzakhela isibingelelo, nigwilike nje kuYehova namhla?', 'XHO22'),
(10, 22, 17, 'Ngaba isiphoso sikaPehore sincinane gqitha kuthi, esingekazihlambululi kuso de kube namhlanje, nangona isibetho sabonakala ebandleni likaYehova?', 'XHO22'),
(10, 22, 18, 'Ngaba ubuya kutyeka na namhlanje ekulandeleni uYehova? ngoxa nivukela uYehova namhlanje, ngomso uya kulicaphukela lonke ibandla lakwaSirayeli.', 'XHO22'),
(10, 22, 19, 'Ukuba liyinqambi ilizwe lelifa lenu, hambani niye ezweni lelifa likaYehova, ohleli kulo umnquba kaYehova, nixhamle phakathi kwethu; Ke musa ukugwilika kuYehova, musa ukusiphikisa; wazakhele isibingelelo phambi kwesibingelelo sikaYehova uThixo wethu.', 'XHO22'),
(10, 22, 20, 'Akazange na uAkan, unyana kaSeraki, enze ububi ngenxa yobubi, kwaza kwabakho umsindo phezu kwebandla lonke likaSirayeli? kwaye loo mntu akazange atshabalale ngokungendawo kwakhe yedwa.', 'XHO22'),
(10, 22, 21, 'Baphendula oonyana bakaRubhen, noonyana bakaGadi, nesiqingatha sesizwe sakwaManase, bathi kwiintloko zamawaka akwaSirayeli,', 'XHO22'),
(10, 22, 22, 'uYehova, uThixouThixo, uYehova, uThixouThixo, nguye owaziyo, uya kumazi amaSirayeli; nokugwilika kuYehova, nokona kuYehova (musa ukusisindisa namhla)', 'XHO22'),
(10, 22, 23, 'ukuze sakhe isibingelelo sokubuya simke kuYehova okanye senze umnikelo otshiswayo okanye umnikelo wokudla okuziinkozo phezu kwaso okanye senze imibingelelo yombulelo phezu kwaso, kuya kuba yimfuneko kuYehova ngokwakhe;', 'XHO22'),
(10, 22, 24, 'Besingenakuyenza njani loo nto, ngenxa yokuba besisoyika lo mbandela, saza sathi, Ekuhambeni kwexesha oonyana benu baya kukwazi ukuthetha koonyana bethu, besithi, ‘Nina nengxaki uYehova, uThixo kaSirayeli?', 'XHO22'),
(10, 22, 25, 'Kuba uYehova umise umda weYordan phakathi kwethu nani, nyana bakaRubhen noonyana bakaGadi aninasabelo kuYehova; ukuze abantwana benu bayeke ukumoyika uYehova.', 'XHO22'),
(10, 22, 26, 'Kungenxa yoko le nto sithe: Masilungiselele ngoku ukuze asakhele isibingelelo, singesamadini anyukayo nemibingelelo.', 'XHO22'),
(10, 22, 27, 'Kodwa ukuze kube bubungqina phakathi kwethu nani nakwizizukulwana zethu eziya kulandela emva kwethu, ukuze siyenze inkonzo kaYehova phambi kobuso bakhe ngeminikelo yethu etshiswayo nangemibingelelo yethu nemibingelelo yethu yombulelo; ukuze oonyana benu ngexesha elifanelekileyo bangathi koonyana bethu: “Aninasabelo kuYehova nina.', 'XHO22'),
(10, 22, 28, 'Kungenxa yoko le nto sithe, ukuba bathe basixelela le nto, nakwizizukulwana zethu kwixesha elizayo, sobuya sithi, Khangelani umzekelo wesibingelelo sikaYehova, abasenzayo oobawo, kungekhona umlilo; kungekhona imibingelelo; kodwa ulingqina phakathi kwethu nani.', 'XHO22'),
(10, 22, 29, 'Ngamana uThixo angasithintela ekuvukeleni uYehova nasekuphambukeni kuYehova namhlanje, sakhe isibingelelo sedini elinyukayo neminikelo etshiswayo okanye iminikelo esibingelelweni sikaYehova uThixo wethu phambi komnquba wakhe.', 'XHO22'),
(10, 22, 30, 'Waweva ke uPinehasi umbingeleli, nezikhulu zebandla, neentloko zamawaka akwaSirayeli, ezibe zinaye, amazwi abewathethile oonyana bakaRubhen, noonyana bakaGadi, noonyana bakaManase, bawabona kakuhle.', 'XHO22'),
(10, 22, 31, 'Wathi uPinehasi, unyana wombingeleli uElazare, koonyana bakaRubhen, nakoonyana bakaGadi, nakoonyana bakaManase, Namhla siyabona ukuba uYehova uphakathi kwethu, ngokuba animonanga kuYehova nibakhulule ngoku oonyana bakaSirayeli esandleni sikaYehova.', 'XHO22'),
(10, 22, 32, 'Wabuya ke uPinehasi unyana kaElazare, umbingeleli nezikhulu, koonyana bakaRubhen, nakoonyana bakaGadi, ezweni laseGiliyadi, ezweni lakwaKanan, koonyana bakaSirayeli; bona yazi kwakhona.', 'XHO22'),
(10, 22, 33, 'Lalunga elo lizwi koonyana bakaSirayeli oonyana bakaSirayeli bamdumisa uThixo, àbacinga ukuba babaxabele, balitshabalalise ilizwe ababehleli kulo oonyana bakaRubhen noGadi.', 'XHO22'),
(10, 22, 34, 'Oonyana bakaRubhen noonyana bakaGadi basibiza eso sibingelelo ngokuthi nguEdi, kuba siya kuba lingqina phakathi kwethu lokuba uYehova unguThixo.', 'XHO22'),
(10, 23, 1, 'Kwakuba kudlule imihla emininzi, uYehova wabaphumza oonyana bakaSirayeli emsebenzini wabo wonke. Waluphele uYoshuwa, wabacinezela.', 'XHO22'),
(10, 23, 2, 'Wawabiza uYoshuwa onke amaSirayeli, amadoda awo amakhulu, nabathetheli bawo, nabagwebi bawo, nabathetheli bawo, wathi kubo, Mna ndisendimdala, ndicinezelekile.', 'XHO22'),
(10, 23, 3, 'Nina nikubonile konke abekwenzile uYehova uThixo wenu kwezo ntlanga zonke ngenxa yenu; ngokuba uYehova uThixo wenu nguye onilweleyo.', 'XHO22'),
(10, 23, 4, 'Yabonani, ndizahlula ngamaqashiso ezi ntlanga ziseleyo, zibe lilifa lezizukulwana zenu, zithabathele eYordan, kunye neentlanga zonke endazitshabalalisayo, zesa kulwandle olukhulu ngasentshonalanga.', 'XHO22'),
(10, 23, 5, 'uYehova uThixo wakho wozigqogqa phambi kwakho, azigqogqe phambi kwakho; yaye niya kulidla ilifa ilizwe labo njengoko uYehova uThixo wenu wanithembisayo.', 'XHO22'),
(10, 23, 6, 'Yomelelani kunene, nigcine nenze konke okubhaliweyo encwadini yomyalelo kaMoses, ukuze ningatyeki kuwo, niye ekunene nasekhohlo;', 'XHO22'),
(10, 23, 7, 'ukuze ningangeni ezi ntlanga ziseleyo phakathi kwenu; ningawakhankanyi amagama oothixo bazo, nifunge ngawo, ningabakhonzi, ningabakhonzi.', 'XHO22'),
(10, 23, 8, 'Kodwa namathelani kuYehova uThixo wenu, njengoko nenzileyo unanamhla.', 'XHO22'),
(10, 23, 9, 'Kuba uYehova wagxotha iintlanga ezinkulu nezomeleleyo phambi kwenu, kodwa nina akukho namnye owakhe wema phambi kwenu de kube namhla;', 'XHO22'),
(10, 23, 10, 'indoda enye kuni isukele iwaka; ngokuba uYehova uThixo wenu nguye onilwelayo, njengoko wakuthethayo kuni.', 'XHO22'),
(10, 23, 11, 'Zilumkeleni, ukuze nimthande uYehova uThixo wenu.', 'XHO22'),
(10, 23, 12, 'Kungenjalo ungenza nangayiphi na indlela, ubuye uwathabathe amasalela ezi zizwe, eziseleyo phakathi kwenu, uwathabathe, ungene nawo, nawo abe nawe;', 'XHO22'),
(10, 23, 13, 'Yazini ukuba uYehova, uThixo wenu, akasayi kuphinda azigqogqe ezi ntlanga phambi kwenu; kube ngumgibe nomgibe kuni, kube ziingozi emacaleni enu, noviko emehlweni enu, nide nicinywe kulo mhlaba ulungileyo, aninikileyo uYehova uThixo wenu.', 'XHO22'),
(10, 23, 14, 'Yabonani, ndihamba ngendlela yehlabathi lonke namhlanje; yonke into yenzeke kuwe, akukho nto iye yasilela kuyo.', 'XHO22'),
(10, 23, 15, 'Kuya kuthi ke kuni, njengoko onke amazwi alungileyo, abewathethile uYehova uThixo wenu kuni, anifikele; uYehova wozisela ububi bonke kuwe, akonakalisa kulo mhlaba ulungileyo, aninikileyo uYehova uThixo wakho.', 'XHO22'),
(10, 23, 16, 'Xa nithe nawaphula umnqophiso kaYehova uThixo wenu, awaniwisela umthetho ngawo, nahamba nakhonza thixo bambi, nabanqula; kulapho uvuthe umsindo kaYehova kuni, nitshabalale kamsinya emhlabeni olungileyo aninikileyo.', 'XHO22'),
(10, 24, 1, 'Wazibizela ndawonye uYoshuwa zonke izizwe zakwaSirayeli kwaShekem, wabiza amadoda amakhulu akwaSirayeli, neentloko zawo, nabagwebi bawo, nabalungiseleli bawo; bazimisa phambi koThixo.', 'XHO22'),
(10, 24, 2, 'Wathi uYoshuwa kubo bonke abantu, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ooyihlo babehleli phesheya koMlambo kususela kwamandulo, njengoTera, uyise ka-Abraham, uyise kaNakore; bakhonza thixo bambi.', 'XHO22'),
(10, 24, 3, 'Ndamthabatha uyihlo uAbraham ndamwela umkhukula ndamhambisa kulo lonke ilizwe lakwaKanan ndaza ndayandisa inzala yakhe ndaza ndamnika uIsake.', 'XHO22'),
(10, 24, 4, 'Ndamnika uIsake noYakobi noEsawu; uEsawu ndamnika intaba yakwaSehire, ukuba ayime; ke uYakobi noonyana bakhe behla baya eYiputa.', 'XHO22'),
(10, 24, 5, 'Ndaphinda ndathuma uMoses noAron, ndayicinezela iYiputa, njengoko ndenzayo kuyo; ndaza emva koko ndanikhupha.', 'XHO22'),
(10, 24, 6, 'Ndamkhupha uyihlo eYiputa, weza elwandle; amaYiputa amsukela uyihlo eneenqwelo zokulwa nangabamahashe, ada besa kuLwandle oluBomvu.', 'XHO22'),
(10, 24, 7, 'Bambiza uYehova, wabeka ubumnyama phakathi kwenu namaYiputa, wawazisela ulwandle, wawagubungela; amehlo enu akubona oko ndakwenzayo eYiputa, nahlala entlango imihla emininzi.', 'XHO22'),
(10, 24, 8, 'Ndaningenisa ezweni lama-Amori, abehleli phesheya kweYordan; balwa nani; ndawanikela esandleni senu, ukuze nilidle ilifa ilizwe lawo; Ndazitshabalalisa phambi kwakho.', 'XHO22'),
(10, 24, 9, 'Wesuka uBhalaki unyana kaTsipore, ukumkani wakwaMowabhi, walwa namaSirayeli, wathumela kuBhileham unyana kaBhehore, ukuba aniqalekise.', 'XHO22'),
(10, 24, 10, 'Andivuma ke ukumphulaphula uBhileham; ke ngoko, usakusikelela; ngenxa yoko ndanihlangula esandleni sakhe.', 'XHO22'),
(10, 24, 11, 'Nayiwela iYordan nafika eYeriko, yaye amadoda aseYeriko alwa nani, ama-Amori namaPerizi namaKanan namaHiti namaGirgashi namaHiti namaYebhusi; ndawanikela esandleni sakho.', 'XHO22'),
(10, 24, 12, 'Ndathumela oonomeva phambi kwenu, babagqogqa phambi kwenu, ookumkani bobabini bama-Amori; kodwa kungabi ngekrele lakho, nangesaphetha sakho.', 'XHO22'),
(10, 24, 13, 'Ndaninika ilizwe eningalikhonzanga, nemizi eningayakhanga, nahlala ke kuyo; udla izidiliya nemithi yeminquma, ongayityalanga.', 'XHO22'),
(10, 24, 14, 'Moyikeni ke ngoko uYehova, nimkhonze ngenyaniso nangenyaniso; basuseni oothixo ababekhonza bona ooyihlo phesheya koMlambo naseYiputa; khonza uYehova.', 'XHO22'),
(10, 24, 15, 'Ukuba kubi emehlweni akho ukumkhonza uYehova, zinyulele namhla lowo uya kumkhonza; noothixo ababekhonza bona ooyihlo phesheya koMlambo, noothixo bama-Amori enihleli ezweni lawo; Ke mna nendlu yam siya kukhonza uYehova.', 'XHO22'),
(10, 24, 16, 'Baphendula abantu bathi, Masingemki kuYehova, sikhonze thixo bambi;', 'XHO22'),
(10, 24, 17, 'Ngokuba nguYehova, uThixo wethu, owasikhuphayo ezweni laseYiputa, endlwini yobukhoboka, thina noobawo, owayenzayo le miqondiso mikhulu phambi kwethu, nosigcinayo endleleni yonke esahamba ngayo naphantsi kwethu bonke abantu esacanda kubo;', 'XHO22'),
(10, 24, 18, 'uYehova wabagqogqa bonke abantu phambi kwethu, ama-Amori abehleli ezweni; ke ngoko, nathi siya kukhonza uYehova; ngokuba nguThixo wethu yena.', 'XHO22'),
(10, 24, 19, 'Wathi uYoshuwa ebantwini, Aninako ukumkhonza uYehova; ngokuba nguThixo ongcwele; unguThixo onekhwele; Akayi kunixolela ukreqo lwenu nezono zenu.', 'XHO22'),
(10, 24, 20, 'Ukuba nithe namfulathela uYehova, nakhonza thixo bambi, wojikela kuni, anenzele ububi, anitshabalalise, ngokuba anenzele okulungileyo nina.', 'XHO22'),
(10, 24, 21, 'Bathi abantu kuYoshuwa, Hayi; ke thina siya kukhonza uYehova.', 'XHO22'),
(10, 24, 22, 'Wathi uYoshuwa ebantwini: “Ningamangqina enu ukuba ninyule uYehova ukuba nimkhonze. Bathi ke bona, Singamangqina.', 'XHO22'),
(10, 24, 23, 'Uthi ke, Balahleni oothixo bolunye uhlanga abaphakathi kwenu, nimthobele ngeentliziyo zenu uYehova, uThixo kaSirayeli.', 'XHO22'),
(10, 24, 24, 'Bathi abantu kuYoshuwa, Siya kukhonza uYehova uThixo wethu, siliphulaphule ilizwi lakhe.', 'XHO22'),
(10, 24, 25, 'Wandula ke uYoshuwa wenza umnqophiso nabantu ngaloo mini waza wabamisela umthetho nommiselo kwaShekem.', 'XHO22'),
(10, 24, 26, 'uYoshuwa wawabhala la mazwi encwadini yomthetho kaTHIXO oyinyaniso, wathabatha ilitye elikhulu walibeka apho phantsi komthi womthi wokripthi kwindawo engcwele kaYehova.', 'XHO22'),
(10, 24, 27, 'Wathi uYoshuwa kubo bonke abantu, Yabonani, eli litye liya kuba lingqina lethu; ngokuba niwevile onke amazwi kaYehova, awathethileyo kuthi.', 'XHO22'),
(10, 24, 28, 'Wabandulula ke uYoshuwa abantu, elowo waya elifeni lakhe.', 'XHO22'),
(10, 24, 29, 'Emveni kwezi zinto, wafa uYoshuwa unyana kaNun, umkhonzi kaYehova, eminyaka ilikhulu elinashumi-nye ezelwe.', 'XHO22'),
(10, 24, 30, 'Bamngcwabela emdeni welifa lakhe, eTimnati-sera, ekweleentaba lakwaEfrayim, entla kwenduli yakwaGahazi.', 'XHO22'),
(10, 24, 31, 'Oonyana bakaSirayeli bamkhonza uYehova yonke imihla kaYoshuwa, nayo yonke imihla yamadoda amakhulu awasindayo kuYoshuwa, ibe ayewazi onke amashwa.', 'XHO22'),
(10, 24, 32, 'Amathambo kaYosefu awawanyusayo oonyana bakaSirayeli eYiputa baza awangcwaba kwaShekem entsimini awayithenga uYakobi koonyana bakaHamore uyise kaShekem ngekhulu leeshekele zesilivere; ilifa loonyana bakaYosefu.', 'XHO22'),
(10, 24, 33, 'Wafa ke uElazare unyana ka-Aron; yaye bamngcwabela endulini kaFinehasi unyana wakhe, awayinikwayo kweleentaba lakwaEfrayim.', 'XHO22'),
(11, 1, 1, 'Wafa uYoshuwa, babuza oonyana bakaSirayeli kuYehova, besithi, Ngubani na kuthi oya kuwaxabela amaKanan, aqale alwe nawo?', 'XHO22'),
(11, 1, 2, 'Wathi uYehova, Konyuka uYuda; yabona, ndilinikela ilizwe esandleni sakhe.', 'XHO22'),
(11, 1, 3, 'Wathi uYuda kuSimon umkhuluwa wakhe, Nyuka nam siye endaweni yam, siye kulwa namaKanan; kwaye ndiya kuhamba nawe ekupheleni kwakho. Wahamba naye uSimon.', 'XHO22'),
(11, 1, 4, 'Wenyuka uYuda; yaye uYehova wawanikela esandleni sawo amaKanan namaPersi, aza abulala phakathi kwawo ishumi lamawaka amadoda eBheseki.', 'XHO22'),
(11, 1, 5, 'Bamfumana uAdoni-bhezeki eBhezeki baza balwa naye baza bawabulala amaKanan namaPersi.', 'XHO22'),
(11, 1, 6, 'Wasaba uAdoni-bhezeki; bamsukela, bambamba, bamnqumla oobhontsi bezandla neenzwane.', 'XHO22'),
(11, 1, 7, 'Wathi uAdoni-bhezeki, Ookumkani abangamashumi amathandathu aneshumi ababenqumla oosithupha bezandla noobhontsi behlanganisana phantsi kwetafile yam; njengoko ndenze ngako, wenjenjalo uThixo kum. Bamsa eYerusalem, wafela khona.', 'XHO22'),
(11, 1, 8, 'Oonyana bakaYuda bayingenela iYerusalem, bayithimba, bayibulala ngohlangothi lwekrele, basitshisa neso sixeko.', 'XHO22'),
(11, 1, 9, 'Emva koko behla oonyana bakaYuda baya kulwa namaKanan awayehlala ezintabeni ezimzantsi nasentlanjeni yomlambo oqukuqelayo.', 'XHO22'),
(11, 1, 10, 'Aza amaYuda alwa namaKanan abehleli eHebron; igama leHebron ngenxa engaphambili beliyiKiriyati ka-Arbha; ababulala uSheshayi, noAkiman, noTalemayi.', 'XHO22'),
(11, 1, 11, 'Ukusuka apho wabahlasela abemi baseDebhiri, yaye igama leDebhiri laliyiKiriyati-sefere ngenxa engaphambili.', 'XHO22'),
(11, 1, 12, 'Wathi uKalebhi, Othe wayithimba iKiriyati-sefere, wayithimba, ndomnika intombi yam uAhazi, ibe ngumkakhe.', 'XHO22'),
(11, 1, 13, 'Wangena uOteniyeli, unyana kaKenazi, umzalwana kaKalebhi, wamnika intombi yakhe, uAkisa, yangumkakhe.', 'XHO22'),
(11, 1, 14, 'Efikile ke kuye, wamvuselela ukuba acele umhlaba kuyise; Wathi uKalebhi kuye, Ùfuna ntoni na?', 'XHO22'),
(11, 1, 15, 'Wathi kuye, Ndinike into, ngokuba undinike umhlaba wasezantsi; ndinike imithombo yamanzi. Ke uKalebhi wamnika amaqula aphezulu namaqula angaphantsi.', 'XHO22'),
(11, 1, 16, 'Oonyana bakaMokeni, uyise womkaMoses, benyuka noonyana bakaYuda, besuka emzini wamaSundu, baya entlango yakwaYuda, ezantsi kweAradi baya bahlala phakathi kwabantu.', 'XHO22'),
(11, 1, 17, 'uYuda wahamba noSimon umkhuluwa wakhe, bawabulala amaKanan abehleli eShefati, bayitshabhisa. Igama laloo mzi laliyiHorma.', 'XHO22'),
(11, 1, 18, 'AmaYuda ayithimba iGasa nommandla wayo ngaselwandle, neAshkelon nommandla wayo, ne-Ekron nommandla wayo.', 'XHO22'),
(11, 1, 19, 'uYehova waba noYuda; wabagqogqa abemi bentaba; ke ababanga nako ukubagqogqa abemi bentili, ngokuba babeneemoto zesinyithi.', 'XHO22'),
(11, 1, 20, 'Bamnika uKalebhi eHebron, njengoko wathethayo uMoses, wabathuma khona oonyana bobathathu baka-Anaki.', 'XHO22'),
(11, 1, 21, 'Ke oonyana bakaBhenjamin abawagqogqanga amaYebhusi, abehleli eYerusalem; kodwa amaYebhusi asahlala noonyana bakaBhenjamin eYerusalem de kube namhla.', 'XHO22'),
(11, 1, 22, 'Benyuka nabo abendlu kaYosefu, baya eBheteli, uYehova waba nabo.', 'XHO22'),
(11, 1, 23, 'Indlu kaYosefu yathumela ukuba kuhlolwe iBheteli. (Ke igama laloo mzi ngenxa engaphambili beliyiLuze.)', 'XHO22'),
(11, 1, 24, 'Iintlola ezo zambona umntu ephuma esixekweni, zathi kuye, Sibonise indlela yokungena kulo mzi; soba nenceba kuwe.', 'XHO22'),
(11, 1, 25, 'Akubabonisa indlela yokungena esixekweni, bawuthimba umzi ngohlangothi lwekrele; Bayikhulula indoda leyo, nayo yonke intsapho yayo.', 'XHO22'),
(11, 1, 26, 'Loo ndoda yahamba yaya kwilizwe lamaHeti yaza yakha isixeko, yasibiza ngokuba yiLuse, ligama laso elo de kube namhla.', 'XHO22'),
(11, 1, 27, 'Akazange abe nabemi baseBhete-shehan namadlelo ayo, neTahanaki namadlelo ayo, nabahleli eDore namadlelo ayo, nabemi baseIbleyam namadlelo ayo, nabemi baseMegido namadlelo ayo hayi ukugxotha; kodwa amaKanan afuna ukuhlala kwelo lizwe.', 'XHO22'),
(11, 1, 28, 'Bathi bakomelela oonyana bakaSirayeli, bawahlasela amaKanan, kodwa abazange bawagxothe.', 'XHO22'),
(11, 1, 29, 'uEfrayim akawagqogqanga amaKanan abehleli eGezere; ahlala amaKanan phakathi kwawo eGezere.', 'XHO22'),
(11, 1, 30, 'Naye uZebhulon akazange abagxothe abemi baseKitron nabemi baseNahaloli; amaKanan ahlala phakathi kwabo, emlungiselela.', 'XHO22'),
(11, 1, 31, 'Yaye uAshere akazange abagxothe abemi baseAki nabemi baseSidon okanye iAglabhi neAgisibhi neHelebha neApiki neRehobhi.', 'XHO22'),
(11, 1, 32, 'Kodwa ama-Ashere ahlala phakathi kwamaKanan awayehlala kwelo lizwe, kuba akazange awagxothe.', 'XHO22'),
(11, 1, 33, 'Kananjalo uNafetali akabagqogqanga abemi baseBhete-shemeshe nabemi baseBhete-anati; wahlala phakathi kwamaKanan abehleli kwelo zwe; kodwa abemi baseBhete-shemeshe naseBhete-anati babengabahlawuli berhafu kubo.', 'XHO22'),
(11, 1, 34, 'Ama-Amori amkhwelisa umntwana kweleentaba, ngokuba akabavumelanga ukuba behle baye entilini.', 'XHO22'),
(11, 1, 35, 'Ke ama-Amori ami entabeni yeHeres, eAyalon naseSaalbhim;', 'XHO22'),
(11, 1, 36, 'Umda wama-Amori wathabathela ethambekeni leAkrabhim, eLiwa, wesa encotsheni.', 'XHO22'),
(11, 2, 1, 'Saphuma isithunywa sikaYehova eGiligali, senyuka saya eBhogim, sathi, Ndanikhupha eYiputa, ndanizisa ezweni endafungayo kooyihlo; ndathi, Andiyi kuwaphula umnqophiso wam nawe;', 'XHO22'),
(11, 2, 2, 'Musani ukuhlangana nabemi beli lizwe; nizidilizile izibingelelo zabo, aniliphulaphula ilizwi lam. Uyenzele ntoni lonto?', 'XHO22'),
(11, 2, 3, 'Ndathi ke, Andiyi kubagxotha phambi kwakho; kodwa baya kuba njengameva emacaleni enu, noothixo bazo babe ngumgibe kuni.', 'XHO22'),
(11, 2, 4, 'Sawathetha ke isithunywa sikaYehova la mazwi kubo bonke oonyana bakaSirayeli, baliphakamisa abantu izwi labo, balila.', 'XHO22'),
(11, 2, 5, 'Bayibiza loo ndawo ngokuthi yiBhogim baza babingelela kuYehova apho.', 'XHO22'),
(11, 2, 6, 'Wathi uYoshuwa ebantwini ukuba bahambe; baya oonyana bakaSirayeli elowo elifeni lakhe, ukuba balidle ilifa ilizwe.', 'XHO22'),
(11, 2, 7, 'Abantu bamkhonza uYehova yonke imihla kaYoshuwa nayo yonke imihla yamadoda amakhulu awayesaphila emva koYoshuwa, awayeyibona yonke imisebenzi emikhulu kaYehova awayeyenzele amaSirayeli.', 'XHO22'),
(11, 2, 8, 'Wafa ke uYoshuwa unyana kaNun, umkhonzi kaYehova, eminyaka ilikhulu elinashumi-nye ezelwe.', 'XHO22'),
(11, 2, 9, 'Bamngcwabela emdeni welifa lakhe, eTimnatare, kweleentaba lakwaEfrayim, kumntla wenduli yaseGahazi.', 'XHO22'),
(11, 2, 10, 'Yaye sonke eso sizukulwana sasuswa kooyise, kwaza kwavela esinye isizukulwana emva kwaso, awayengasazi uYehova okanye imisebenzi awayeyenzele uSirayeli.', 'XHO22'),
(11, 2, 11, 'Oonyana bakaSirayeli benza ububi emehlweni kaYehova, bakhonza ooBhahali.', 'XHO22'),
(11, 2, 12, 'Bamnikela umva uYehova uThixo wooyise, owabakhuphayo ezweni laseYiputa, balandela oothixo bolunye uhlanga, oothixo beentlanga ezibangqongileyo, babanqula, bamphikisa uYehova umsindo.', 'XHO22'),
(11, 2, 13, 'Bamlahla ke uYehova, bakhonza uBhahali noAshtaroti.', 'XHO22'),
(11, 2, 14, 'Wavutha umsindo kaYehova ngoonyana bakaSirayeli, waza wabanikela esandleni sabaphangi ababaphangayo, waza wathengisa ngabo esandleni seentshaba zabo ezazibajikelezile, ukuze bangabi saba nakuma phambi kweentshaba zabo.', 'XHO22'),
(11, 2, 15, 'Naphi na apho aya khona, isandla sikaYehova saba nxamnye nawo ngobubi, kanye njengoko wakuthethayo uYehova nanjengoko wayewathethile ngesifungo uYehova; bakhathazeka kakhulu.', 'XHO22'),
(11, 2, 16, 'Ke uYehova wavelisa abagwebi, ababasindisayo esandleni sabo babewaphanga.', 'XHO22'),
(11, 2, 17, 'Noko ke, akabaphulaphulanga abagwebi bawo, asuka akrexeza nabanye oothixo, babanqula kodwa abazange.', 'XHO22'),
(11, 2, 18, 'uYehova wabazisa abagwebi, uYehova waba naloo mgwebi, wabasindisa esandleni seentshaba zabo yonke imihla yaloo mgwebi; ngokuba uYehova wayenovelwano ngabo, walila ngenxa yabacinezeli bawo wabacaphukisa.', 'XHO22'),
(11, 2, 19, 'Kwathi, kwakutshatyalaliswa umgwebi lowo, babuya batshabalalisa ngaphezu kooyise, balandela thixo bambi, babakhonze, baqubude kubo; abazange bazilahle izenzo zabo neentshukumo zabo ezilukhuni.', 'XHO22'),
(11, 2, 20, 'Wavutha ke umsindo kaYehova kuSirayeli; Wathi, Ekubeni olu hlanga luwaphule umnqophiso wam endawumisela ooyise, alwaliphulaphula ilizwi lam;', 'XHO22'),
(11, 2, 21, 'Kananjalo, ukususela ngoku kuse phambili, andiyi kugxotha nabani na phakathi kweentlanga awazishiyayo uYoshuwa ekufeni kwakhe;', 'XHO22'),
(11, 2, 22, 'ukuze ndibacikide oonyana bakaSirayeli, ukuba boyigcina na indlela kaYehova, bahambe ngayo, njengoko bayigcinayo ooyise, abayi kuyigcina, kusini na.', 'XHO22'),
(11, 2, 23, 'Kungenxa yoko le nto uYehova ezishiya ngaphandle kokuzigxotha ngokukhawuleza ezo ntlanga; akabanikela esandleni sikaYoshuwa.', 'XHO22'),
(11, 3, 1, 'Zizo ezi iintlanga awazishiyayo uYehova, ukuze awalinge oonyana bakaSirayeli, oonyana bakaSirayeli bonke ababengazazi zonke iimfazwe zakwaKanan;', 'XHO22'),
(11, 3, 2, 'Kuphela nje ukuze izizukulwana zoonyana bakaSirayeli zazi, ukuze zifundiswe imfazwe, kwanabo babengazi nto ngayo ngaphambili;', 'XHO22'),
(11, 3, 3, 'Zizo ezi iinkosana ezintlanu zamaFilisti nawo onke amaKanan namaSidon namaHeti awayehlala kwiNtaba yeLebhanon ukusuka kwiNtaba yeBhahali-Hermon ukuya kutsho eHamati.', 'XHO22'),
(11, 3, 4, 'Kwafuneka abavavanye bebodwa oonyana bakaSirayeli, ukuze kwazi ukuba babeya kuyiphulaphula na imiyalelo uYehova awayeyinike ookhokho bawo ngoMoses.', 'XHO22'),
(11, 3, 5, 'Bahlala oonyana bakaSirayeli phakathi kwamaKanan, namaHeti, nama-Amori, namaPerizi, namaHeti, namaYebhusi.', 'XHO22'),
(11, 3, 6, 'Bazizeka iintombi zawo zaba ngabafazi bawo, neentombi zawo bazinika oonyana bawo, bakhonza oothixo bawo.', 'XHO22'),
(11, 3, 7, 'Oonyana bakaSirayeli benza ububi emehlweni kaYehova, bamlibala uYehova uThixo wabo, bakhonza ooBhahali namatyholo.', 'XHO22'),
(11, 3, 8, 'Wavutha ke umsindo kaYehova ngoonyana bakaSirayeli, wathengisa ngabo esandleni sikaKushan-ristaima, ukumkani wakwa-Aram phakathi kweMilambo;', 'XHO22'),
(11, 3, 9, 'Babiza oonyana bakaSirayeli kuYehova, wabavelisela umsindisi oonyana bakaSirayeli, wabahlangula, uOteniyeli, unyana kaKenazi, umzalwana kaKalebhi.', 'XHO22'),
(11, 3, 10, 'uMoya kaYehova waba phezu kwakhe, wabakhokela oonyana bakaSirayeli, waphuma waya kulwa kwaye isandla sakhe samoyisa iKusan-Risatime.', 'XHO22'),
(11, 3, 11, 'Lazola ilizwe iminyaka emashumi mane. Wafa uOteniyeli, unyana kaKenazi.', 'XHO22'),
(11, 3, 12, 'Baphinda oonyana bakaSirayeli benza ububi emehlweni kaYehova; yaye uYehova wamomeleza uEglon ukumkani wakwaMowabhi nxamnye noSirayeli, ngenxa yokuba benze ububi emehlweni kaYehova.', 'XHO22'),
(11, 3, 13, 'Wabahlanganisa ndawonye oonyana baka-Amon no-Amaleki, wahamba wawaxabela amaSirayeli, wawuthimba umzi wamasundu.', 'XHO22'),
(11, 3, 14, 'Oonyana bakaSirayeli bamkhonza uEglon, ukumkani wakwaMowabhi, iminyaka elishumi elinesibhozo.', 'XHO22'),
(11, 3, 15, 'Bathi bakubiza oonyana bakaSirayeli kuYehova, wabavelisela umsindisi, uEhudi unyana kaGera, umBhenjamin, indoda elinxele; oonyana bakaSirayeli bathumela izipho ngaye kuEglon, ukumkani wakwaMowabhi.', 'XHO22'),
(11, 3, 16, 'uEhude wazenzela ikrele elintlangothi-mbini, ubude balo likubhite enye; wawubopha phantsi kwengubo yakhe ethangeni lakhe lasekunene.', 'XHO22'),
(11, 3, 17, 'Wasondeza isipho kuEglon ukumkani wakwaMowabhi; uEglon ebengumfo otyebileyo.', 'XHO22'),
(11, 3, 18, 'Wathi akugqiba ukumnika isipho, wathi abo babethwele eso sipho mabahambe.', 'XHO22'),
(11, 3, 19, 'Wabuya emifanekisweni eqingqiweyo eseGiligali, wathi, Ndinelizwi lasentsithelweni nawe, kumkani; Baphuma kuye bonke ababemi phambi kwakhe.', 'XHO22'),
(11, 3, 20, 'Waya kuye uEhude; wahlala egumbini lasehlotyeni, abenalo. Wathi uEhude, Ndinelizwi likaThixo eliza kuwe; Uye waphakama esihlalweni.', 'XHO22'),
(11, 3, 21, 'uEhudi wasolula isandla sakhe sasekhohlo waza walithabatha ikrele ethangeni lakhe lasekunene waza wamhlaba ngalo esiswini.', 'XHO22'),
(11, 3, 22, 'Yangena nencakuba emva kwencakuba; inqatha layigubungela intsimbi, akaba nako ukulikhupha ikrele esiswini sakhe; kwaphuma ukungcola.', 'XHO22'),
(11, 3, 23, 'Waphuma uEhude waya evarandeni, wazivala iingcango zegumbi lokuhlala, wazitshixa;', 'XHO22'),
(11, 3, 24, 'Ephumile ke yena, bafika abakhonzi bakhe; yaye bakubona iingcango zegumbi lokutyela zivaliwe, bathi: “Enyanisweni, ugubungela iinyawo zakhe egumbini lakhe lasehlotyeni.', 'XHO22'),
(11, 3, 25, 'Balinda bada baneentloni, nanko kungavuli mntu ezingcangweni zegumbi lokuhlala; bawuthabatha ke umkhombe, bawuvula, nanko ukumkani wabo ewile emhlabeni, wafa.', 'XHO22'),
(11, 3, 26, 'uEhude wasaba, besazilazila bona, wayigqitha imifanekiso eqingqiweyo, wasabela eSeirati.', 'XHO22'),
(11, 3, 27, 'Uthe akufika wavuthela ngesigodlo kweleentaba lakwaEfrayim, behla naye oonyana bakaSirayeli kweleentaba, wabakhokela.', 'XHO22'),
(11, 3, 28, 'Wathi kubo, Ndilandeleni, ngokuba uYehova uzinikele iintshaba zenu, amaMowabhi, esandleni senu. Behla ke, bamlandela, bawela iYordan baya kwaMowabhi, abavumela mntu ukuba awele.', 'XHO22'),
(11, 3, 29, 'Ngelo xesha babulala kwaMowabhi malunga neshumi lamawaka amadoda, onke amadoda anobukroti, anobukroti; akwasinda mntu.', 'XHO22'),
(11, 3, 30, 'Woyiswa ke amaMowabhi ngaloo mini phantsi kwesandla samaSirayeli; Lazola ilizwe iminyaka emashumi asibhozo.', 'XHO22'),
(11, 3, 31, 'Emva kwakhe yaba nguSamgare, unyana ka-Anati, owabulala kumaFilisti amakhulu amathandathu ngendle yenkomo; wawasindisa amaSirayeli.', 'XHO22'),
(11, 4, 1, 'Baphinda oonyana bakaSirayeli benza ububi emehlweni kaYehova, ekufeni kukaEhude.', 'XHO22'),
(11, 4, 2, 'uYehova wathengisa ngabo, wabanikela esandleni sikaYabhin, ukumkani wakwaKanan, obephethe iHatsore; Ingqwayi-ngqwayi yakhe ibinguSisera, owayehlala eHarosheti yeentlanga.', 'XHO22'),
(11, 4, 3, 'Bambiza oonyana bakaSirayeli kuYehova, ngokuba ebeneenqwelo zesinyithi ezingamakhulu asithoba; Wabacinezela oonyana bakaSirayeli iminyaka emashumi mabini.', 'XHO22'),
(11, 4, 4, 'Kwaye uDebhora, umprofetikazi, umkaLapidoti, nguye owayekhokela amaSirayeli ngelo xesha.', 'XHO22'),
(11, 4, 5, 'Wayehlala phantsi komthi wesundu kaDebhora phakathi kweRama neBheteli kummandla weentaba wakwaEfrayim, yaye oonyana bakaSirayeli benyuka baya kuye ukuze agwebe.', 'XHO22'),
(11, 4, 6, 'Wathumela kuBharaki, unyana ka-Abhinowam, waseKedeshe-nafetali, wathi kuye, Akawisanga mthetho na uYehova, uThixo kaSirayeli, esithi, Yiya entabeni yeTabhore, uthabathe ishumi lamawaka koonyana bakaNafetali, uze? nawe? Koonyana bakaZebhulon?', 'XHO22'),
(11, 4, 7, 'Ndiya kumtsalela kuwe uSisera, injengele yomkhosi kaYabhin, iinqwelo zakhe zokulwa nesihlwele sakhe kuMlambo iKishon; ndimnikele esandleni senu.', 'XHO22'),
(11, 4, 8, 'Wathi uBharaki kuye, Ukuba ùthanda ukuhamba nam, ndohamba; ukuba akuthanga uhambe nam, andiyi kuhamba.', 'XHO22'),
(11, 4, 9, 'Wathi, Ndiya kuhamba nawe; ngokuba uYehova uya kumnikela uSisera esandleni somfazi. Wagqiba uDebhora, wahamba noBharaki ukuya eKedeshe.', 'XHO22'),
(11, 4, 10, 'uBharaki wawabiza amaZebhulon noNafetali, ukuba yiKedeshe; wenyuka eneshumi lamawaka amadoda ngasezinyaweni zakhe, noDebhora wenyuka naye.', 'XHO22'),
(11, 4, 11, 'Wazahlula uHebhere, umKanan, koonyana bakaHobhabhi, uyise kaMoses, kumaKanan, wamisa iintente zakhe, wada wesa kuMoki waseSanayim eKedeshe.', 'XHO22'),
(11, 4, 12, 'Waxelelwa uSisera ukuba uBharaki, unyana ka-Abhinowam, unyuke waya entabeni yeTabhore.', 'XHO22'),
(11, 4, 13, 'uSisera wazihlanganisa zonke iinqwelo zakhe zokulwa, amakhulu asithoba eenqwelo zesinyithi yedwa, nabantu bonke ababenaye, bethabathela eHarosheti yeentlanga, besa emlanjeni oyiKishon.', 'XHO22'),
(11, 4, 14, 'uDebhora wathi kuBharaki: “Yima! kuba le yimini uYehova aya kumnikela ngayo uSisera esandleni sakho. Akanikhokelanga na uYehova? Wehla uBharaki entabeni yeTabhore, elandelwa ngamadoda angamawaka alishumi.', 'XHO22'),
(11, 4, 15, 'uYehova wamdubaduba uSisera neenqwelo zakhe zonke zokulwa, nomkhosi wakhe wonke, ngohlangothi lwekrele phambi koBharaki; Wehla uSisera enqwelweni yakhe, wasaba ngeenyawo.', 'XHO22'),
(11, 4, 16, 'uBharaki wazisukela iinqwelo zokulwa nomkhosi, wesa eHarosheti yeentlanga; akwasala namnye.', 'XHO22'),
(11, 4, 17, 'uSisera wasaba ngeenyawo, waya ententeni kaYaheli, umkaHebhere, umKeni; ngokuba belixolile phakathi koYabhin ukumkani waseHatsore nendlu kaHebhere umKeni.', 'XHO22'),
(11, 4, 18, 'Waphuma ke uYaheli, waya kumkhawulela uSisera, wathi kuye, Khawuze kum, nkosi yam; Musa ukuphaphazela. Uthe xa engena naye ententeni, wamgquma ngengubo.', 'XHO22'),
(11, 4, 19, 'Wathi kuye, Ndiphe amanzi ndisele, ngokuba ndinxaniwe; wayivula intsuba yobisi, wamseza, wayivala.', 'XHO22'),
(11, 4, 20, 'Wathi ke kuye, Yima emnyango ententeni; uya kuthi: Hayi.', 'XHO22'),
(11, 4, 21, 'uYaheli umkaHebhere wathabatha isikhonkwane sentente, waphatha isando ngesandla, wasondela kuye, wabethelela isikhonkwane ezibondeni zakhe, wasibethelela emhlabeni, kuba wayelele ubuthongo. Ngoko wafa.', 'XHO22'),
(11, 4, 22, 'Kwathi, xa uBharaki esasukela uSisera, waphuma uYaheli waya kumkhawulela, wathi kuye, Yiza, ndikubonise indoda uyifunayo. Wathi akungena ententeni yakhe, wabona uSisera elele efile, enesikhonkwane etempileni yakhe.', 'XHO22'),
(11, 4, 23, 'uThixo wamthoba ngaloo mini uYabhin, ukumkani wamaKanan, phambi koonyana bakaSirayeli.', 'XHO22'),
(11, 4, 24, 'Isandla soonyana bakaSirayeli saqhubeka simoyisa uYabhin, ukumkani wamaKanan, bada bamchitha uYabhin, ukumkani wakwaKanan.', 'XHO22'),
(11, 5, 1, 'Baza bavuma ooDebhora noBharaki, unyana ka-Abhinowam, ngaloo mini, besithi,', 'XHO22'),
(11, 5, 2, 'Mdumiseni uYehova, ngenxa yempindezelo yakwaSirayeli, Ekunikeleni kwabo ngokuqhutywa yintliziyo abantu;', 'XHO22'),
(11, 5, 3, 'Yivani, bokumkani; yivani, zidwangube; Ndiya kuvuma kuYehova; Ndiyakuthanda ukuvuma iindumiso kuYehova, uThixo kaSirayeli.', 'XHO22'),
(11, 5, 4, 'uYehova ekuphumeni kwenu kwaSehire, ekunyukeni kwenu ezweni lakwaEdom, Lanyikima ihlabathi, izulu lavuza, amafu avuza amanzi.', 'XHO22'),
(11, 5, 5, 'Iintaba zanyibilika phambi koYehova, kwaneSinayi phambi koYehova, uTHIXO kaSirayeli.', 'XHO22'),
(11, 5, 6, 'Ngemihla kaSamgare, unyana ka-Anati, Ngemihla kaYaheli, bezingahanjwa iindlela, Abahambi babehamba ngeendlela.', 'XHO22'),
(11, 5, 7, 'Abemi bemizi bamisa, bawavala amaSirayeli, ndada ndema mna, Debhora, ndema njengonina kaSirayeli.', 'XHO22'),
(11, 5, 8, 'Bazinyulela oothixo abatsha; kwaliwa emasangweni; Ngaba bekukho ikhaka okanye umkhonto phakathi kwamashumi amane amawaka akwaSirayeli?', 'XHO22'),
(11, 5, 9, 'Intliziyo yam ikubalawuli bakwaSirayeli abazinikele ngokuzithandela phakathi kwabantu. Dumisani uYehova.', 'XHO22'),
(11, 5, 10, 'Yithini, nina bakhwele edizayini ezimhlophe, nina bahlala enkundleni, nina bahamba ngendlela.', 'XHO22'),
(11, 5, 11, 'Abo basindisiweyo kwisandi sabo badubula ngesaphetha kwiindawo zemithombo yamanzi, apho baya kuva izenzo zobulungisa zikaYehova, izenzo zobulungisa azenzayo kubemi bezixeko zakhe zakwaSirayeli, ke abantu bakaYehova baya kuhla baye emasangweni.', 'XHO22'),
(11, 5, 12, 'Vuka, vuka, Debhora; vuka, vuka, vuma ingoma; Suk’ ume, Bharaki, umthabathe umthimbe unyana ka-Abhinowam.', 'XHO22'),
(11, 5, 13, 'Abaseleyo wabamisela ukuba balawule amanene ezintlangeni; uYehova undenze umlawuli wabanamandla.', 'XHO22'),
(11, 5, 14, 'KwaEfrayim ingcambu yabo yaba ngama-Amaleki; emva kwakho, Bhenjamin, phakathi kwabantu bakowenu; kwaMagire kwaphuma abathetheli, kwaZebhulon abo baphethe usiba lombhali.', 'XHO22'),
(11, 5, 15, 'KwaIsakare abathetheli kunye noDebhora; ewe, uIsakare noBharaki; wasiwa entilini ngeenyawo. Kumaqela akwaRubhen kwakukho iingcamango ezinkulu zentliziyo.', 'XHO22'),
(11, 5, 16, 'Kutheni na uhlala ezibayeni zezimvu ukuze uve ukukhala komhlambi? Zazigocagoca kakhulu iintliziyo zamaqela kaRubhen.', 'XHO22'),
(11, 5, 17, 'uGiliyadi wahlala ngaphesheya kweYordan, yaye kwakutheni ukuze uDan aqhubeke ehlala ezinqanaweni? Wahlala uAshere elunxwemeni lolwandle, wahlala ezintenteni zakhe.', 'XHO22'),
(11, 5, 18, 'uZebhulon noNafetali yayingabantu ababeke babeka ubomi babo esichengeni de bafe kwiindawo eziphakamileyo.', 'XHO22'),
(11, 5, 19, 'Ookumkani beza balwa, yaye emva koko ookumkani bakwaKanan balwa eTahanaki emanzini aseMegido; abazange bafumane mali.', 'XHO22'),
(11, 5, 20, 'Bayalwa besemazulwini; Zalwa iinkwenkwezi emendweni yazo noSisera.', 'XHO22'),
(11, 5, 21, 'Umlambo waseKishon wabakhukulisa, umlambo omdala waseKishon. Owu mphefumlo wam, wonakele amandla.', 'XHO22'),
(11, 5, 22, 'Aza akroboza amanqina amahashe, aza anamandla ajulwa.', 'XHO22'),
(11, 5, 23, 'Qalekisani uMerose, satsho isithunywa sikaYehova, baqalekiseni abemi bakhona; ngenxa yokuba bengamncedanga uYehova, uYehova weza kunceda amagorha.', 'XHO22'),
(11, 5, 24, 'Makadunyiswe uYaheli phakathi kwabafazi, UmkaHebhere umKeni, Makasikelelwe phakathi kweentokazi ezisezintenteni.', 'XHO22'),
(11, 5, 25, 'Wacela amanzi, wamnika amasi; weza nebhotolo ngesitya esiswiti.', 'XHO22'),
(11, 5, 26, 'Ubeka isandla sakhe esikhonkwaneni, nesandla sakhe sokunene eluphawulweni lwabasebenzi; wambetha uSisera ngentonga, wamnqumla intloko njengoko ebemhlaba, wambetha ezimpunzini.', 'XHO22'),
(11, 5, 27, 'Waqubuda ezinyaweni zakhe, wawa, walala ubuthongo; Waqubuda ezinyaweni zakhe, wawa; apho wagoba khona, wawa wafa.', 'XHO22'),
(11, 5, 28, 'Walunguza ngefestile unina kaSisera, wamemeza, wathi, Ilibele yini na inqwelo yakhe yokulwa, ingafiki nje? kutheni iimoto zakhe zishiywe lixesha?', 'XHO22'),
(11, 5, 29, 'Amphendula amanenekazi akhe alumkileyo athi:', 'XHO22'),
(11, 5, 30, 'Abakhawulezanga na? abahlulanga amaxhoba; kwindoda yonke intombazana, nokuba ngambini; Neengubo ezimfakamfele kuSisera, ezimfakamfele, ezimfakamfele, ezimfakamfele kumacala omabini, neentamo zabathinjwa?', 'XHO22'),
(11, 5, 31, 'Mazitshabalale zonke iintshaba zakho, Yehova, Ke abakuthandayo mababe njengelanga ekuphumeni kwalo ngamandla alo. Kwaye ihlabathi laphumla iminyaka engamashumi amane.', 'XHO22'),
(11, 6, 1, 'Oonyana bakaSirayeli benza ububi emehlweni kaYehova, uYehova wabanikela esandleni samaMidiyan iminyaka esixhenxe.', 'XHO22'),
(11, 6, 2, 'Izandla zamaMidiyan zaboyisa oonyana bakaSirayeli, yaye ngenxa yamaMidiyan la maSirayeli azenzela imingxuma ezintabeni, nemiqolomba neenqaba.', 'XHO22'),
(11, 6, 3, 'Xa oonyana bakaSirayeli babehlwayela, amaMidiyan ama-Amaleki noonyana baseMpuma benyuka besiya kuwahlasela;', 'XHO22'),
(11, 6, 4, 'Bamisa inkampu nxamnye nabo baza batshabalalisa imveliso yomhlaba de kwafika eGaza, yaye abazange bamshiyele kutya uSirayeli, kwaneegusha okanye nkomo okanye needonki.', 'XHO22'),
(11, 6, 5, 'Benyuka bona nempahla yabo emfutshane, neentente zabo, beza njengeenkumbi ukuba baninzi; ngokuba babengenakubalwa bona neenkamela zabo; bangena ehlabathini ukuba balitshabalalise.', 'XHO22'),
(11, 6, 6, 'AmaSirayeli ahlwempuzeka kakhulu ngenxa yamaMidiyan; babiza oonyana bakaSirayeli kuYehova.', 'XHO22'),
(11, 6, 7, 'Bakhala oonyana bakaSirayeli kuYehova ngenxa yamaMidiyan.', 'XHO22'),
(11, 6, 8, 'uYehova wathuma umprofeti koonyana bakaSirayeli, wathi kubo, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndanikhupha eYiputa, ndanikhupha endlwini yobukhoboka;', 'XHO22'),
(11, 6, 9, 'Ndanihlangula esandleni samaYiputa, nasesandleni sabo bonke abanitshutshisayo, ndaza ndabagxotha phambi kwenu, ndaninika ilizwe labo;', 'XHO22'),
(11, 6, 10, 'ndathi kuni, NdinguYehova, uThixo wenu; musani ukuboyika oothixo bama-Amori, enihleli ezweni lawo, analiphulaphula ilizwi lam.', 'XHO22'),
(11, 6, 11, 'Yaza ingelosi kaYehova yeza yahlala phantsi komthi wesiripine owawuseOfra, kaYowashe umMowabhi; unyana wakhe uGidiyon wayebhula ingqolowa kwisixovulelo sewayini ukuze afihle amaMidiyan.', 'XHO22'),
(11, 6, 12, 'Kwabonakala isithunywa sikaYehova kuye, sathi kuye, uYehova unawe, gorhandini.', 'XHO22'),
(11, 6, 13, 'Wathi uGidiyon kuyo, Camagu, Nkosi yam, ukuba uYehova unathi, yini na ukuba sibe ezi zinto zonke? Iphi na yonke imisebenzi yakhe ebalulekileyo, ababesibalisela ngayo oobawo, bathi, AsinguYehova nalowo wasikhuphayo eYiputa? ngoku uYehova usilahlile, wasinikela esandleni samaMidiyan.', 'XHO22'),
(11, 6, 14, 'uYehova wamkhangela, wathi, Hamba ngala mandla akho, uwahlangule amaSirayeli esandleni samaMidiyan. Andikuthumanga?', 'XHO22'),
(11, 6, 15, 'Wathi yena kuye, Camagu, Nkosi yam, ndowasindisa ngantoni na amaSirayeli? Uyabona, umzi wakowethu ulihlwempu ezweni lakwaManase, mna ndingoyena mncinane endlwini kabawo.', 'XHO22'),
(11, 6, 16, 'Wathi uYehova kuye, Ndiya kuba nawe, yaye uya kuwaxabela amaMidiyan njengamntu mnye.', 'XHO22'),
(11, 6, 17, 'Wathi kuye, Ukuba kaloku ndibabalwe nguwe, ndinike umqondiso wokuba wena uthethe nam.', 'XHO22'),
(11, 6, 18, 'Musa ukumka apha ndide ndize kuwe, ndisithabathe isipho sam, ndisibeke phambi kwakho. Athi: Ndizakulinda ude ubuye.', 'XHO22'),
(11, 6, 19, 'Wangena uGidiyon, walungisa itakane lebhokhwe, nezonka ezingenagwele ze-efa yomgubo ocoliweyo; inyama wayibeka engobozini, umhluzi wawugalela embizeni, wazisa kuye phantsi komthi weturpentine, wazisa.', 'XHO22'),
(11, 6, 20, 'Sathi kuye isithunywa sikaThixo, Thabatha inyama nezonka ezingenagwele, uzibeke phezu kweli litye, uwugalele umhluzi. Wenjenjalo.', 'XHO22'),
(11, 6, 21, 'Saza isithunywa sikaYehova sathabatha incam yentonga esandleni saso, sachukumisa inyama nezonka ezingenagwele; kwaphuma umlilo eliweni, wayitshisa inyama nezonka ezingenagwele. Saza isithunywa sikaNdikhoyo semka emehlweni akhe.', 'XHO22'),
(11, 6, 22, 'Xa uGidiyon wabona ukuba yingelosi kaYehova leyo, uGidiyon wathi: “Owu, Nkosi enguMongami Yehova! ngokuba ndabona isithunywa sikaYehova, sakhangelana ebusweni.', 'XHO22'),
(11, 6, 23, 'Wathi uYehova kuye, Uxolo malube nawe! musani ukoyika, anisayi kufa.', 'XHO22'),
(11, 6, 24, 'uGidiyon wamakhela khona isibingelelo uYehova, wathi igama likaYehova liluxolo; unanamhla iseliOfra yama-Abhiyezere.', 'XHO22'),
(11, 6, 25, 'Ndabona ngobo busuku uYehova wathi kuye: “Thabatha inkunzi yenkomo kayihlo, eyesibini inkunzi eminyaka isixhenxe, usichithe isibingelelo sikaBhahali sikayihlo, uze ugawule ityholo elinalo netyholo;', 'XHO22'),
(11, 6, 26, 'wakhela uYehova uThixo wakho isibingelelo kule ngxondorha endaweni emisiweyo, uthabathe eyesibini inkunzi yenkomo, wenze idini elinyukayo ngeenkuni zehlathi oya kuzigawula.', 'XHO22'),
(11, 6, 27, 'uGidiyon wathabatha amadoda alishumi kubakhonzi bakhe; wenza njengoko uYehova wamwiselayo umthetho ngako; yaye ngenxa yokuba wayeyoyika indlu kayise namadoda aloo mzi, kuba wayengenako ukuyenza loo nto emini, wenjenjalo kusebusuku.', 'XHO22'),
(11, 6, 28, 'Avuka ekuseni amadoda esixeko, abona isibingelelo sikaBhahali sidiliziwe, netyholo elisecaleni laso ligawuliwe, nenkunzi yenkomo yesibini ibingelelwe phezu kwesibingelelo esasakhiweyo.', 'XHO22'),
(11, 6, 29, 'Yathi enye kwenye, Ngubani na lo wenze le nto? Babuza, babuza, bathi, NguGidiyon unyana kaYowashe, wenjenjalo.', 'XHO22'),
(11, 6, 30, 'Athi amadoda aloo mzi kuYowashe, Mkhuphe unyana wakho, afe; ngokuba usidilizile isibingelelo sikaBhahali, wasitshabalalisa yayililifa lehlathi elalilapho.', 'XHO22'),
(11, 6, 31, 'Wathi uYowashe kubo bonke ababemi phambi kwakhe, Niya kumthethelela uBhahali yini na? Ngaba uya kumsindisa? ofuna ukumthethelela makabulawe ngomso; ukuba unguthixo, makazimele, ngokuba kukho odilize isibingelelo sakhe.', 'XHO22'),
(11, 6, 32, 'Wambiza ke uYerubhahali ngaloo mini, wathi, uBhahali makammangalele, ngokuba esonakalisa isibingelelo sakhe.', 'XHO22'),
(11, 6, 33, 'Ahlanganisana onke amaMidiyan nama-Amaleki nabasempumalanga, awela, amisa entilini yaseYizereli.', 'XHO22'),
(11, 6, 34, 'Wehla ke uMoya kaYehova phezu kukaGidiyon, wavuthela isigodlo; uAbhiyezere warhola emva kwakhe.', 'XHO22'),
(11, 6, 35, 'Wathuma abathunywa kumaManase onke; wathumela abathunywa kwa-Ashere, nakwaZebhulon, nakwaNafetali; benyuka baya kubakhawulela.', 'XHO22'),
(11, 6, 36, 'Wathi uGidiyon kuThixo, Ukuba uthe wawahlangula amaSirayeli ngesandla sam, njengoko uthethileyo;', 'XHO22'),
(11, 6, 37, 'Yabona, ndibeka uboya boboya emhlabeni; yaye ukuba ukungunda kusebunweleni kuphela kuze kome kulo lonke ihlabathi ngaphandle, ndiya kwazi ke ukuba uya kumhlangula uSirayeli ngesandla sam, kanye njengoko ubuthethile.', 'XHO22'),
(11, 6, 38, 'Akubanga njalo; ngokuba wavuka kusasa ngesifingo, wabutha uboya, wagalela ukufuma eboyeni, loo mngxuma uzele ngamanzi.', 'XHO22'),
(11, 6, 39, 'Wathi uGidiyon kuThixo, Musa ukuwuyeka umsindo wakho kum, ndithethe kube kanye; mandiyizame, kodwa ngeli xesha ngoboya; makube komile uboya, kubekho umbethe phezu komhlaba.', 'XHO22'),
(11, 6, 40, 'Wenjenjalo uThixo ngobo busuku;', 'XHO22'),
(11, 7, 1, 'Bavuka kusasa ooYerubhahali, onguGidiyon, namadoda onke abenaye, bemisa ngasequleni likaHarodi. Umkhosi wamaMidiyan wawungasentla kwawo, phezu kwenduli kaMore, entilini.', 'XHO22'),
(11, 7, 2, 'Wathi uYehova kuGidiyon, Baninzi abantu onabo, ukuze ndiwanikele amaMidiyan esandleni sabo, hleze bandivukele oonyana bakaSirayeli, bathi, Ndisindisiwe ngesandla sam.', 'XHO22'),
(11, 7, 3, 'Khawuhambe ke umemeze ezindlebeni zabantu, uthi, Bonke abangcangcazelayo makabuye, bemke ezintabeni zaseGiliyadi. Kwabuya ke ebantwini abangamashumi amabini anamabini amawaka; kwasala ishumi lamawaka.', 'XHO22'),
(11, 7, 4, 'Wathi uYehova kuGidiyon, Basebaninzi abantu; Yehla nawo uye emanzini, ndikuvavanyele wona apho. Ndithi ke kuni, Makangahambi nani, makangahambi nani.', 'XHO22'),
(11, 7, 5, 'Wabahlisela abantu emanzini, waza uYehova wathi kuGidiyon: “Nabani na owakhotha amanzi ngolwimi lwakhe njengenja ekhothayo, makamyeke; unjalo wonke umntu oguqa ngamadolo ukuba asele.', 'XHO22'),
(11, 7, 6, 'Inani labo babekhotha, bazibeka izandla emlonyeni wabo, lalingamakhulu amathathu amadoda; abanye abantu baguqa ngamadolo ukuba basele amanzi.', 'XHO22'),
(11, 7, 7, 'Wathi uYehova kuGidiyon, Ndiya kukuhlangula ngala madoda makhulu mathathu, ndiwanikele amaMidiyan esandleni sakho; zihambe zonke ezinye iintlanga, elowo aye endaweni yakhe.', 'XHO22'),
(11, 7, 8, 'Abantu bathabatha ukudla kwabo, baphatha izigodlo zabo esandleni sabo, wathi onke amaSirayeli makaye elowo ententeni yakhe, wawagcina amadoda amakhulu mathathu yaye umkhosi wamaMidiyan wawuphantsi kwakhe entlanjeni.', 'XHO22'),
(11, 7, 9, 'Ngobo busuku uYehova wathi kuye: “Sukuma, uhle uye emkhosini; ngokuba ndiyinikele ezandleni zenu.', 'XHO22'),
(11, 7, 10, 'Ukuba uyoyika ukuhla, yihla uye emkhosini noFura umkhonzi wakho.', 'XHO22'),
(11, 7, 11, 'Kwaye uve into abayithethayo; emva koko izandla zakho ziya komelela ukuba uhle uye enqabeni. Wehla ke yena noPura isicaka sakhe, baya kutsho kumda womkhosi oxhobileyo.', 'XHO22'),
(11, 7, 12, 'AmaMidiyan nama-Amaleki noonyana bonke basempumalanga babelele emlanjeni njengeenkumbi ezininzi; neenkamela zabo bezizininzi njengentlabathi eselunxwemeni lolwandle.', 'XHO22'),
(11, 7, 13, 'Wafika uGidiyon, wabona indoda exelela ummelwane wayo iphupha, yathi, Yabona, ndiphuphe iphupha; yawa, yayibhukuqa, yalala intente ngecala layo.', 'XHO22'),
(11, 7, 14, 'Waphendula umlingane wakhe, wathi, Likrele likaGidiyon, unyana kaYowashe, indoda yakwaSirayeli; uThixo uwanikele esandleni sakhe amaMidiyan nomkhosi wonke.', 'XHO22'),
(11, 7, 15, 'Akuliva uGidiyon iphupha elo, nokutyhilwa kwalo, waguqa, wabuyela ezintlwini zakwaSirayeli, esithi, Yekani! ngokuba uYehova uyinikele imikhosi yakwaMidiyan esandleni sakho.', 'XHO22'),
(11, 7, 16, 'Wawahlula aba zahlulo zontathu amadoda angamakhulu mathathu, wabeka isigodlo esandleni ngasinye, nemiphanda engenanto, nezibane eziphangeni.', 'XHO22'),
(11, 7, 17, 'Wathi kubo, Khangelani kum, nenze kwanjengam; ndithi, ndakuphuma eminqubeni, nenze njengoko ndenza ngako.', 'XHO22'),
(11, 7, 18, 'Ndakuvuthela isigodlo, mna nabo bonke abakunye nam, nani nivuthele izigodlo, nijikeleze eminqubeni, nithi, Ingoma kaYehova, nengoma kaGidiyon!', 'XHO22'),
(11, 7, 19, 'uGidiyon nekhulu lamadoda abenaye bafika ngaphandle kweminquba, ekuqalekeni kweqela lamadoda aphakathi; yaye bemisa nje abalindi, baza bavuthela izigodlo baza bayaphula imiphanda eyayisesandleni sabo.', 'XHO22'),
(11, 7, 20, 'Avuthela izigodlo amalungu omathathu, aziqhekeza imiphanda, aphatha izibane ngezandla zasekhohlo, namaxilongo ngesandla sokunene, ukuba avuthe; baduma, besithi, Ikrele likaYehova nelikaGidiyon!', 'XHO22'),
(11, 7, 21, 'Bema elowo endaweni yakhe, ngeenxa zonke eminqubeni;', 'XHO22'),
(11, 7, 22, 'Amakhulu mathathu avuthela izigodlo, uYehova wabeka ikrele lomntu ngamnye kumlingane wakhe, nakumkhosi wonke;', 'XHO22'),
(11, 7, 23, 'Ahlanganisana amadoda akwaSirayeli kwaNafetali, nakwa-Ashere, nakuManase wonke, awasukela amaMidiyan.', 'XHO22'),
(11, 7, 24, 'uGidiyon wesusa abathunywa ezintabeni zonke zakwaEfrayim, esithi, Yihlani niye kumaMidiyan, niwagxothe amanzi phambi kwawo, nide nise eBhete-bhara, naseYordan. Abuthelana ndawonye onke amadoda akwaEfrayim, akha amanzi, esa eBhete-bhara naseYordan;', 'XHO22'),
(11, 7, 25, 'Bathimba iinkosana ezimbini zakwaMidiyan, uOrebhe noSebe; bambulalela uOrebhi eliweni likaOrebhi, waza uShebhe wawaxabela kwisixovulelo sewayini sikaShehebhi waza wawasukela amaMidiyan waza wayiweza intloko kaOrebhi nekaShebhe eYordan.', 'XHO22'),
(11, 8, 1, 'Athi amadoda akwaEfrayim kuye, Usikhonze, asikubizanga na oko wakuwaxabela amaMidiyan? Kwaye balwa naye kakhulu.', 'XHO22'),
(11, 8, 2, 'Wathi kubo, Ndenze ntoni na ngangani? Intlahlela yeediliya zikaEfrayim ayilungile na, ngaphezu kokuvuna kweediliya zika-Abhiyezere?', 'XHO22'),
(11, 8, 3, 'uThixo wabanikela abathetheli bakwaMidiyan, uOrebhe noShebhi, esandleni senu; ndenze ntoni na xa kuthelekiswa nawe? Wadamba umsindo wabo akuba etshilo.', 'XHO22'),
(11, 8, 4, 'uGidiyon wafika eYordan, wayiwela, yena namadoda angamakhulu amathathu awayekunye naye, ediniwe, kodwa awasukela.', 'XHO22'),
(11, 8, 5, 'Wathi kumadoda aseSukoti, Banikeni isonka aba bandilandelayo; kuba badiniwe, yaye ndisukela uShebha noTsalimuna, ookumkani bakwaMidiyan.', 'XHO22'),
(11, 8, 6, 'Bathi abathetheli baseSukoti, Sesithabathe isandla sikaShebha neTsalimuna sesandleni sakho, ukuze sinike abathetheli bakho isonka?', 'XHO22'),
(11, 8, 7, 'Wathi uGidiyon, Ukuba ke uNdikhoyo uthe wayinikela iShebha neTsalimuna esandleni sam, ndoyiqwenga inyama yenu ngemithana enameva yasentlango.', 'XHO22'),
(11, 8, 8, 'Wenyuka khona wenyuka waya ePenuweli, wathetha kubo kwangokunjalo. Bamphendula abantu basePenuweli njengoko bamphendulayo abantu baseSukoti.', 'XHO22'),
(11, 8, 9, 'Waphinda wathetha kumadoda asePenuweli, wathi, Ekubuyeni kwam ngoxolo, ndoyidiliza le nqaba ende.', 'XHO22'),
(11, 8, 10, 'iSebha neTsalmuna zaziseKarkore nomkhosi wazo owawukunye nawo, amadoda amalunga neshumi elinesihlanu lamawaka, lawo onke awayesele kuwo onke amabhunga oonyana baseMpuma; kuba kwawa ikhulu elinamanci mabini amawaka amadoda arhola ikrele.', 'XHO22'),
(11, 8, 11, 'uGidiyon wenyuka ngendlela yabemi beentente ngasempuma kweNobha naseYogebheha waza wawoyisa umkhosi, kuba umkhosi wawukhuselekile.', 'XHO22'),
(11, 8, 12, 'Wabaleka uShebha noTsalimuna, wabasukela, wababamba ookumkani ababini bakwaMidiyan, uShebha noTsalimuna, waludubaduba lonke uhlanga.', 'XHO22'),
(11, 8, 13, 'Wabuya uGidiyon unyana kaYowashe edabini, lingekaphumi ilanga;', 'XHO22'),
(11, 8, 14, 'Wabamba umfana wamadoda aseSukoti, wacebisana naye; Wamcacisela abathetheli baseSukoti, namadoda amakhulu akhe, amadoda amashumi asixhenxe.', 'XHO22'),
(11, 8, 15, 'Weza kumadoda aseSukoti, wathi, Sebha noSalemuna, ngabo enaniqalekisayo ngabo, nisithi, iSebha neSalemuna sele zisesandleni sakho na, ukuba siwanike amadoda lawo na? ngoobani abadinwe sisonka?', 'XHO22'),
(11, 8, 16, 'Wawathabatha amadoda amakhulu aloo mzi, nemithana enameva, nenkunzane entlango, wawafundisa amadoda aseSukoti kunye nawo.', 'XHO22'),
(11, 8, 17, 'Wayidiliza inqaba ende yasePenuweli, wababulala abantu baloo mzi.', 'XHO22'),
(11, 8, 18, 'Wathi kuSebha noTsalimuna, Ngoobani na abantu enababulalayo eTabhore? Aphendula athi, Babenjengawe; wonke umntu wayekhangeleka njengabantwana benkosi.', 'XHO22'),
(11, 8, 19, 'Wathi, Babengabazalwana bam abo, oonyana bakama; ehleli nje uYehova, ukuba benibasindisile, ngendinganibulalanga.', 'XHO22'),
(11, 8, 20, 'Wathi kuYetere, amazibulo akhe, Vuka, ubabulale. Ke umfana akazange alirhole ikrele lakhe, kuba wayesoyika, kuba wayesengumfana.', 'XHO22'),
(11, 8, 21, 'Bathi ooSebha noTsalimuna, Hlalani nisibulale, ngokuba njengoko anjalo umntu, bunjalo ubugorha bakhe. Wesuka ke uGidiyon wawabulala uShebha noTsalimuna, wazithabatha ezo zivatho zeentamo zeenkamela zabo.', 'XHO22'),
(11, 8, 22, 'Athi amadoda akwaSirayeli kuGidiyon, Silawule, wena nonyana wakho, nonyana wonyana wakho, ngokuba usisindisile esandleni samaMidiyan.', 'XHO22'),
(11, 8, 23, 'Wathi uGidiyon kuwo, Andiyi kunilawula mna; akayi kunilawula nonyana wam; uYehova uya kunilawula.', 'XHO22'),
(11, 8, 24, 'Wathi uGidiyon kuwo, Ndicela nto-nye kuni; (ngokuba babenamajikazi egolide, kuba babengamaIshmayeli.)', 'XHO22'),
(11, 8, 25, 'Baphendula bathi, Siya kubanika ngokuqhutywa yintliziyo. Kwaye bandlala ilaphu baza ngamnye waphosa amakhonkco exhoba lakhe.', 'XHO22'),
(11, 8, 26, 'Ubunzima bamakhonkco egolide awabucelayo baba liwaka elinamakhulu asixhenxe eeshekele zegolide; ngaphandle kwezaa ntsimbi namacici, neengubo ezimfusa ezibe zikookumkani bamaMidiyan, nemixokelelwane esezintanyeni zeenkamela zabo.', 'XHO22'),
(11, 8, 27, 'uGidiyon wenza ilaphu ngayo, wayibeka eOfra, idolophu yakowabo, aza ahenyuza khona onke amaSirayeli, yaza yaba ngumgibe kuGidiyon nakwindlu yakhe.', 'XHO22'),
(11, 8, 28, 'Athotywa ke amaMidiyan phambi koonyana bakaSirayeli, akaphinda aziphakamise iintloko zawo. Lazola ilizwe iminyaka emashumi mane ngemihla kaGidiyon.', 'XHO22'),
(11, 8, 29, 'uYerubhahali, unyana kaYowashe, waya wahlala endlwini yakhe.', 'XHO22'),
(11, 8, 30, 'uGidiyon ubenoonyana abamashumi amathandathu besiza sakhe, ngokuba ebenabafazi abaninzi.', 'XHO22'),
(11, 8, 31, 'Neshweshwe lakhe, elibe likwaShekem, lamzalela unyana nalo, elathi igama lakhe nguAbhimeleki.', 'XHO22'),
(11, 8, 32, 'Wafa uGidiyon unyana kaYowashe, eyingwevu enkulu, wangcwatyelwa engcwabeni likaYowashe uyise, eOpra yama-Abhiyezere.', 'XHO22'),
(11, 8, 33, 'Kwathi kamsinya nje akuba efile uGidiyon, oonyana bakaSirayeli bajika baza bakrexeza noBhahali baza benza uBhahali-bheriti uthixo wabo.', 'XHO22'),
(11, 8, 34, 'Oonyana bakaSirayeli abamkhumbulanga uYehova uThixo wabo, owabakhululayo esandleni seentshaba zabo zonke ngeenxa zonke.', 'XHO22'),
(11, 8, 35, 'Yaye abazange bayenzele inceba indlu kaYerubhahali, uGidiyon, ngako konke ukulunga awayekwenzele uSirayeli.', 'XHO22'),
(11, 9, 1, 'Waya uAbhimeleki, unyana kaYerubhahali, kuShekem, kubazalwana bonina, wathetha kubo nomzalwane wonke wendlu kayise, wathi,', 'XHO22'),
(11, 9, 2, 'Thethani ezindlebeni zabemi bakwaShekem bonke, nithi, Okona kulungileyo kuni, kukuthi nilawulwe nyana bamashumi amathandathu oonyana bakaYerubhahali, kukuthi nilawulwe yindoda ibe nye, kusini na? nikhumbule ukuba lithambo lenu nenyama yenu.', 'XHO22'),
(11, 9, 3, 'Bawathetha abazalwana bonina loo mazwi akhe onke, ezindlebeni zabemi bakwaShekem bonke, intliziyo yabo yakuthobela ukumlandela uAbhimeleki; ngokuba bathi, Ngumzalwana wethu.', 'XHO22'),
(11, 9, 4, 'Bamnika iishekele zesilivere ezimashumi mathandathu zendlu kaBhahali-bheriti;', 'XHO22'),
(11, 9, 5, 'Weza endlwini kayise eOfra, wababulala abazalwana bakhe, oonyana bakaYerubhahali, amadoda amashumi mathandathu aneshumi, tyeni linye; kwasala uYotam, unyana omncinane kaYerubhahali; ngokuba ebezimele.', 'XHO22'),
(11, 9, 6, 'Bahlanganisana bonke abemi bakwaShekem nendlu yonke yeMilo, baya bamenza ukumkani uAbhimeleki emthini wesikhumbuzo nokwaShekem.', 'XHO22'),
(11, 9, 7, 'Waxelelwa uYotam, waya wema encotsheni yentaba yeGerizim, waliphakamisa izwi lakhe, wadanduluka kubo, wathi kubo, Yivani, bemi bakwaShekem, aze uYehova eve nina.', 'XHO22'),
(11, 9, 8, 'Yaphuma imithi ukuya kumthambisa ukumkani wayo; bathi kumnquma, Silawule.', 'XHO22'),
(11, 9, 9, 'Wathi umnquma kuyo: “Ndiwashiye na amanqatha am abazukisa ngawo uThixo nabantu, ndize ndiye ndivelele imithi?”', 'XHO22'),
(11, 9, 10, 'Yathi imithi kumkhiwane, Yiza, usilawule.', 'XHO22'),
(11, 9, 11, 'Wathi umkhiwane kuyo, Ndingade ndiyincame na incasa yam nencasa yam, ndiye kuyivelele imithi?', 'XHO22'),
(11, 9, 12, 'Yathi imithi kumdiliya, Yiza, usilawule.', 'XHO22'),
(11, 9, 13, 'Wathi umdiliya kuyo, Ndingade ndiyincame na iwayini yam ekholekileyo kuThixo nakubantu, ndiye kuyinyamekela imithi?', 'XHO22'),
(11, 9, 14, 'Yathi yonke imithi kumeva, Yiza, usilawule.', 'XHO22'),
(11, 9, 15, 'Lathi ityholo kwimithi, Ukuba okunene niyandithambisa ndibe ngukumkani wenu, nokholosa ngomthunzi wam;', 'XHO22'),
(11, 9, 16, 'Ke kaloku ukuba nenze ngenyaniso nangenyaniso ngokumenza ukumkani uAbhimeleki, ukuba nimenzele kakuhle uYerubhahali nendlu yakhe, namenza ngokwezandla zakhe;', 'XHO22'),
(11, 9, 17, 'Ngenxa yokuba ubawo wanilwela waza wabeka ubomi bakhe esichengeni ekude, wanihlangula esandleni sikaMidiyan.', 'XHO22'),
(11, 9, 18, 'Kusuke kwavuka namhlanje indlu kayise noonyana bakhe, amadoda angamashumi omathandathu, awabulawelwa lityeni elinye, amenza uAbhimeleki, unyana womkhonzazana wakhe, ukumkani wabemi bakwaShekem, ngokuba engunyana wakho;', 'XHO22'),
(11, 9, 19, 'Ukuba namhlanje nenze ngenyaniso nangenyaniso kuYerubhahali nendlu yakhe, vuyani ngoAbhimeleki, avuye naye ngani;', 'XHO22'),
(11, 9, 20, 'Ukuba akunjalo, mawuphume umlilo kuAbhimeleki, ubatshise abemi bakwaShekem nendlu yeMilo; mawuphume umlilo kubanikazi bakwaShekem nakwindlu yeMilo, umtshise uAbhimeleki.', 'XHO22'),
(11, 9, 21, 'Wasaba ke uYotam, wasaba, wafika eBhere, wahlala khona ngenxa ka-Abhimeleki umninawa wakhe.', 'XHO22'),
(11, 9, 22, 'uAbhimeleki wayengukumkani kwaSirayeli iminyaka emithathu.', 'XHO22'),
(11, 9, 23, 'uThixo wathuma umoya ombi phakathi koAbhimeleki nabemi bakwaShekem; yaye abanini bakwaShekem bamsokolisa uAbhimeleki;', 'XHO22'),
(11, 9, 24, 'ukuze inkohlakalo eyenziwa koonyana bakaYerubhahali abangamashumi asixhenxe ize negazi labo libe phezu koAbhimeleki umzalwana wabo owababulalayo; nabemi bakwaShekem, abamncedayo ukubulala abazalwana babo.', 'XHO22'),
(11, 9, 25, 'Abemi bakwaShekem ezintabeni bamenza amaxoki baza baphanga wonke umntu odlula ngendlela, yaye oku kwaxelwa kuAbhimeleki.', 'XHO22'),
(11, 9, 26, 'Wafika uGayeli unyana kaEbhede, nabazalwana bakhe, baya kwaShekem; abanini bakwaShekem bakholosa ngaye.', 'XHO22'),
(11, 9, 27, 'Ngoko baqhubeka besiya emasimini baza bakhakula izidiliya zabo, bazixovula iidiliya baza benza imihlali, bangena endlwini yothixo wabo, badla, basela, bamqalekisa uAbhimeleki.', 'XHO22'),
(11, 9, 28, 'Wathi uGahali unyana kaEbhede, Ngubani na yena uAbhimeleki? Engubani na yena uShekem, ukuba simkhonze? Akanguye na unyana kaYerubhahali? noZebhule umlungiseleli wakhe? Bakhonzeni oonyana bakaHamore, uyise kaShekem, kuba kutheni na ukuba simkhonze?', 'XHO22'),
(11, 9, 29, 'Kwaye ukuba uThixo angasibeka esi sizwe ezandleni zam! ndimsuse ke uAbhimeleki. Wathi kuAbhimeleki, Yandisa impi yakho, uphume;', 'XHO22'),
(11, 9, 30, 'Waweva uZebhule, ukumkani weso sixeko, amazwi kaGayeli, unyana kaEbhede, wavutha umsindo wakhe.', 'XHO22'),
(11, 9, 31, 'Wathuma abathunywa kuAbhimeleki ngasese, esithi, Nanku uGahali unyana kaEbhede, nabazalwana bakhe, befikile kwaShekem; bawuqinisa umzi, ukuba balwe nawo.', 'XHO22'),
(11, 9, 32, 'Ke kaloku, vuka ebusuku, wena nabantu onabo, ulale uze ulinde ilizwe.', 'XHO22'),
(11, 9, 33, 'Kusasa, ukuphuma kwelanga, vuka kusasa, uwunqamle umzi; yabona ke, ukuba uthe yena namadoda anabo bakuhlasela, wothi ke ngoko uthabathe inyathelo kubo.', 'XHO22'),
(11, 9, 34, 'Wavuka ebusuku uAbhimeleki namadoda onke abenaye, balinda bengamaqela amane malunga noShekem.', 'XHO22'),
(11, 9, 35, 'Waphuma uGayeli unyana kaEbhede, wema ekungeneni kwesango;', 'XHO22'),
(11, 9, 36, 'Wababona uGahali abantu, wathi kuSebhule, Yabona, nanko besiza bevela ezincotsheni zeentaba. Wathi uSebule kuye: Ubona amathunzi eentaba ngathi ngabantu.', 'XHO22'),
(11, 9, 37, 'Waphinda waqokela uGayeli, wathi, Yabona, abantu bayehla bevela ezweni;', 'XHO22'),
(11, 9, 38, 'Wathi uZebhule kuye, Uphi na kaloku umlomo wakho, owathi ngawo, Ngubani na uAbhimeleki ukuba simkhonze? Asingabo aba bantu ubacekisile? Ndiyathandaza, phumani ngoku, nilwe nabo.', 'XHO22'),
(11, 9, 39, 'Waphuma uGahali phambi kwabemi bakwaShekem, walwa noAbhimeleki.', 'XHO22'),
(11, 9, 40, 'uAbhimeleki wamsukela, wasaba ebusweni bakhe;', 'XHO22'),
(11, 9, 41, 'uAbhimeleki waqhubeka ehlala eArum, yaye uZebhule wamgxotha uGahali nabazalwana bakhe ukuze bangahlali kwaShekem.', 'XHO22'),
(11, 9, 42, 'Baphuma ke ngengomso abantu, baya emaphandleni; wamxelela uAbhimeleki.', 'XHO22'),
(11, 9, 43, 'Wawathabatha amadoda lawo, wawahlula aba zahlulo zintathu, walala entsimini, wakhangela, wawabona amadoda ephuma phakathi komzi; wabavukela, waboyisa.', 'XHO22'),
(11, 9, 44, 'uAbhimeleki nesihlwele esasinaye bakhawuleza, bema emnyango wesango lomzi; zabalekela ezinye izihlwele ezibini, zababulala bonke abantu ababelapho.', 'XHO22'),
(11, 9, 45, 'uAbhimeleki walwa nomzi yonke loo mini; wasithimba isixeko, wababulala abantu ababekuso, wasitshabalalisa isixeko, wasigalela ityuwa.', 'XHO22'),
(11, 9, 46, 'Beva ke bonke abanini benqaba ende yakwaShekem, besuka bangena enqabeni yeentaba, yendlu yothixo waseBheriti.', 'XHO22'),
(11, 9, 47, 'Waxelelwa uAbhimeleki, ukuba babuthelene khona bonke abasenqabeni ende yakwaShekem.', 'XHO22'),
(11, 9, 48, 'Wayinyuka uAbhimeleki intaba yaseTalmon, yena nabantu bonke abenabo; uAbhimeleki waphatha izembe ngesandla sakhe, wagawula isebe lomthi, walithabatha, walibeka egxalabeni lakhe, wathi ebantwini ababenaye, Khawulezani nindibone ndisenza le nto yenza njengoko ndenzayo.', 'XHO22'),
(11, 9, 49, 'Ngokufanayo bonke abantu bagawula ngamnye isebe lakhe baza bamlandela uAbhimeleki baza bawabeka kwindawo enqatyisiweyo esezintabeni baza bayitshisa inqaba eyayiphezu kwayo ezintabeni; kangangokuba bafa onke amadoda enqaba yakwaShekem, malunga newaka lamadoda nabafazi.', 'XHO22'),
(11, 9, 50, 'Emva koko uAbhimeleki waya eTebhesi, wayingqinga iTebhesi, wayithimba.', 'XHO22'),
(11, 9, 51, 'Kwaye kukho inqaba ende eliqele phakathi komzi, abalekela khona onke amadoda, nabafazi, nomzi wonke, bazivalela, bazakhela phezu kodonga lwenqaba leyo inde.', 'XHO22'),
(11, 9, 52, 'Wafika uAbhimeleki enqabeni leyo, walwa nayo, wasondela emnyango wenqaba leyo, ukuze ayitshise ngomlilo.', 'XHO22'),
(11, 9, 53, 'Yasuka inkazana yaphosa ilitye lokusila entlokweni ka-Abhimeleki, lamophula ukakayi.', 'XHO22'),
(11, 9, 54, 'Wakhawuleza wabiza umfana, wathi kuye, Rhola ikrele lakho, undibulale, hleze bathi ngam, Ubulewe ngumfazi; Waza isoka lakhe lamtyhala, wabhubha.', 'XHO22'),
(11, 9, 55, 'Abona amadoda akwaSirayeli ukuba ufile uAbhimeleki, aya elowo endaweni yakhe.', 'XHO22'),
(11, 9, 56, 'Ngoko ke uThixo wabuphindezela ububi buka-Abhimeleki awayebenzile kuyise ngokubulala abantakwabo abangamashumi asixhenxe.', 'XHO22'),
(11, 9, 57, 'uThixo wabubuyisela bonke ububi bamadoda akwaShekem ezintlokweni zawo, nesiqalekiso sikaYotam, unyana kaYerubhahali, saba phezu kwawo.', 'XHO22'),
(11, 10, 1, 'Emva koAbhimeleki kwesuka wema uTola unyana kaPuwa, unyana kaDodo, indoda kaIsakare, ukuba awakhusele amaSirayeli; yaye wayehlala eSamire kweleentaba lakwaEfrayim.', 'XHO22'),
(11, 10, 2, 'Wawalawula amaSirayeli iminyaka emashumi mabini anantathu, waza wafa, wangcwatyelwa eSamire.', 'XHO22'),
(11, 10, 3, 'Emveni kwakhe kwalandela uYayiro umGiliyadi, wangukumkani kwaSirayeli iminyaka emashumi mabini anamibini.', 'XHO22'),
(11, 10, 4, 'Wayenoonyana abamashumi mathathu, bekhwele iiesile ezimashumi mathathu; banemizi emashumi mathathu, kuthiwa yiHavoti-yahire, unanamhla, ezweni laseGiliyadi.', 'XHO22'),
(11, 10, 5, 'Wafa ke uYayiro, wangcwatyelwa eKamon.', 'XHO22'),
(11, 10, 6, 'Oonyana bakaSirayeli baphinda benza ububi emehlweni kaYehova, bakhonza ooBhahali, nooAshtoreti, noothixo bakwa-Aram, noothixo baseTsidon, noothixo bakwaMowabhi, noothixo boonyana baka-Amon, noothixo boonyana baka-Amon koonyana baka-Amon. Oothixo bamaFilisti bamfulathela uYehova, abamkhonza.', 'XHO22'),
(11, 10, 7, 'Wavutha umsindo kaYehova kumaSirayeli, wathengisa ngawo, wawanikela esandleni samaFilisti, nasesandleni soonyana baka-Amon.', 'XHO22'),
(11, 10, 8, 'Ngaloo nyaka babacinezela oonyana bakaSirayeli, babaxinzelela phantsi iminyaka elishumi elinesibhozo, bonke oonyana bakaSirayeli ababephesheya kweYordan ezweni lama-Amori aseGiliyadi.', 'XHO22'),
(11, 10, 9, 'Kananjalo oonyana baka-Amon bayiwela iYordan, baya kulwa noYuda noBhenjamin, nendlu kaEfrayim; wabandezeleka kunene uSirayeli.', 'XHO22'),
(11, 10, 10, 'Bakhala oonyana bakaSirayeli kuYehova, besithi, Sonile kuwe, ngokuba simkile thina uThixo wethu, sakhonza ooBhahali.', 'XHO22'),
(11, 10, 11, 'Wathi uYehova koonyana bakaSirayeli, Andinihlangulanga na esandleni samaYiputa, nama-Amori, noonyana baka-Amon, nakumaFilisti?', 'XHO22'),
(11, 10, 12, 'Anixinzelela phantsi amaTsidon, nama-Amaleki, namanyanga; nakhala kum, ndanihlangula esandleni sabo.', 'XHO22'),
(11, 10, 13, 'Noko nindikreqile, nakhonza thixo bambi; ke ngoko, andiyi kuphinda ndinisindise.', 'XHO22'),
(11, 10, 14, 'Hambani niye kubiza oothixo enibanyulileyo; ngamana angakusindisa ngexesha lembandezelo yakho.', 'XHO22'),
(11, 10, 15, 'Bathi oonyana bakaSirayeli kuYehova, Sihlulekile; Sihlangule namhlanje kuphela, siyathandaza.', 'XHO22'),
(11, 10, 16, 'Basusa oothixo bolunye uhlanga phakathi kwabo, bakhonza uYehova; waza umphefumlo wakhe waba buhlungu ngenxa yokutshatyalaliswa kukaSirayeli.', 'XHO22'),
(11, 10, 17, 'Bahlanganisana oonyana baka-Amon, bamisa eGiliyadi. Bahlanganisana oonyana bakaSirayeli, bayimisa iMitspe.', 'XHO22'),
(11, 10, 18, 'Bathi abantu nezikhulu zaseGiliyadi omnye komnye, Ngubani na oya kuqala ahlasele oonyana baka-Amon? yoba yintloko yabemi bonke baseGiliyadi.', 'XHO22'),
(11, 11, 1, 'uYafete waseGiliyadi ube ligorha elinobukroti, ubengunyana wenkazana elihenyukazi; uGiliyadi wazala uYafete.', 'XHO22'),
(11, 11, 2, 'Umfazi waseGiliyadi wamzalela oonyana; Bakhula oonyana bomkakhe, bamgxotha uYafete, bathi kuye, Akuyi kudla ilifa indlu kabawo; ngokuba engunyana womphambukeli.', 'XHO22'),
(11, 11, 3, 'Wasaba uYafete kubazalwana bakhe, waya wahlala ezweni laseTobhi; kwahlanganisana amadoda angenabuntu kwaYafete, aphuma naye.', 'XHO22'),
(11, 11, 4, 'Ekugqibeleni oonyana baka-Amoni balwa noSirayeli.', 'XHO22'),
(11, 11, 5, 'Xa oonyana baka-Amon bawahlasela amaSirayeli, amadoda amakhulu aseGiliyadi aya kuthabatha uYafete ezweni laseTobhi.', 'XHO22'),
(11, 11, 6, 'Kwaye bathi kuYafete: Yiza, ube yinkokeli yethu, ukuze sibe nokulwa noonyana baka-Amon.', 'XHO22'),
(11, 11, 7, 'Wathi uYafete kumadoda amakhulu aseGiliyadi, Asinini na abandithiyayo, bandigxotha endlwini kabawo? nize kum ngoku nibandezelekile?', 'XHO22'),
(11, 11, 8, 'Athi amadoda amakhulu aseGiliyadi kuYafete, Kungenxa yoko le nto sibuyela kuwe, ukuba uhambe nathi, uye kulwa noonyana baka-Amon, ube yinganga yethu phezu kwabemi bonke baseGiliyadi.', 'XHO22'),
(11, 11, 9, 'Wathi uYafete kumadoda amakhulu aseGiliyadi, Ukuba nithe nandibuyisa, ukuze ndilwe noonyana baka-Amon, uYehova wabanikela esandleni sam, mna ndoba yinkokeli yenu na?', 'XHO22'),
(11, 11, 10, 'Athi amadoda amakhulu aseGiliyadi kuYafete, uYehova ulingqina phakathi kwethu, ukuba asithanga senze ngokwelizwi lakho.', 'XHO22'),
(11, 11, 11, 'Wahamba ke uYafete namadoda amakhulu aseGiliyadi; abantu bammisa ukuba abe yintloko nomphathi wabo; Wawathetha uYafete onke amazwi akhe phambi koYehova uMuzi.', 'XHO22'),
(11, 11, 12, 'uYafete wathumela abathunywa kukumkani woonyana baka-Amon, wathi, Yintoni na enam nawe, uzé kulwa nam nje ezweni lam?', 'XHO22'),
(11, 11, 13, 'Wabaphendula ukumkani woonyana baka-Amon kubathunywa bakaYafete, wathi, Kungokuba amaSirayeli alithabathayo ilizwe lam, ekunyukeni kwawo eYiputa, athabathela eArnon esa eYabhoki, esa eYordan; uwabuyisele eluxolweni loo mazwe.', 'XHO22'),
(11, 11, 14, 'Wabuya ke uYafete wathumela abathunywa kukumkani woonyana baka-Amon.', 'XHO22'),
(11, 11, 15, 'Wathi kuye, Kwathiwa nguYafete, Oonyana bakaSirayeli abalithimbanga ilizwe lakwaMowabhi, nelizwe loonyana baka-Amon;', 'XHO22'),
(11, 11, 16, 'Baphuma oonyana bakaSirayeli eYiputa, bahamba entlango, besa eLwandle oluBomvu, bafika eKadeshe;', 'XHO22'),
(11, 11, 17, 'AmaSirayeli athumela abathunywa kukumkani wakwaEdom, esithi, Ndivumele ndicande ezweni lakho, akaphulaphula ukumkani wakwaEdom. Bathumela nakukumkani wakwaMowabhi, naye akavuma; yaye oonyana bakaSirayeli bahlala eKadeshe.', 'XHO22'),
(11, 11, 18, 'Bahamba ke entlango, balijikeleza ilizwe lakwaEdom nelizwe lakwaMowabhi, bafika ngasempuma kwelizwe lakwaMowabhi, bamisa iinkampu ngaphesheya kweArnon, kodwa abazange bangene kumda wakwaMowabhi ngokuba iArnon ibingumda wakwaMowabhi.', 'XHO22'),
(11, 11, 19, 'AmaSirayeli athuma abathunywa kuSihon, ukumkani wama-Amori, ukumkani waseHeshbhon; Wathi uSirayeli kuye, Masicande ezweni lakho, sigoduke.', 'XHO22'),
(11, 11, 20, 'Ke uSihon akakholwa ngamaSirayeli, ukuba acande ezweni lakhe; wabahlanganisa bonke abantu bakhe, wamisa eYatsa, walwa noonyana bakaSirayeli.', 'XHO22'),
(11, 11, 21, 'Yaye uYehova uTHIXO kaSirayeli wamnikela uSihon nabo bonke abantu bakhe esandleni sikaSirayeli, babaxabela; yaye oonyana bakaSirayeli balithimba lonke ilizwe lama-Amori, abemi belo lizwe.', 'XHO22'),
(11, 11, 22, 'Aloyisa lonke ilizwe lama-Amori, athabathela eArnon esa eYabhoki, athabathela entlango esa eYordan.', 'XHO22'),
(11, 11, 23, 'Ke kaloku uYehova, uThixo kaSirayeli, uwagxothile ama-Amori phambi kwabantu bakhe amaSirayeli;', 'XHO22'),
(11, 11, 24, 'Akuyi kulidla ilifa na, alidle ilifa uKamus uthixo wakho? sobadla ilifa bonke abo uYehova uThixo wethu, aya kubagxotha phambi kwethu.', 'XHO22'),
(11, 11, 25, 'Ulungile na wena kunoBhalaki unyana kaTsipore, ukumkani wakwaMowabhi? Ngaba wakha walwa namaSirayeli, okanye wakha walwa nawo?', 'XHO22'),
(11, 11, 26, 'Ngoxa amaSirayeli ayehlala eHeshbhon neziseko zayo neArohere needolophana zayo nazo zonke izixeko ezikunxweme lolwandle lwaseArnon iminyaka engamakhulu amathathu? bekutheni ukuze ungababuyiseli ngoko?', 'XHO22'),
(11, 11, 27, 'Mna andikonanga; nguwe ondenza into embi ngokulwa nam; uYehova uMgwebi uya kugweba namhla phakathi koonyana bakaSirayeli noonyana baka-Amon.', 'XHO22'),
(11, 11, 28, 'Kodwa ukumkani woonyana baka-Amon akawaphulaphula amazwi kaYafete awayemthumele kubo.', 'XHO22'),
(11, 11, 29, 'Waza umoya kaYehova wehla phezu koYafete waza wadlula eGiliyadi nakwaManase wadlula eMizpa aseGiliyadi waza wadlula eMizpa yaseGiliyadi waya koonyana baka-Amoni.', 'XHO22'),
(11, 11, 30, 'uYafete wafunga uYehova, wathi, Ukuba uthe wabanikela kwangoko oonyana baka-Amon esandleni sam,', 'XHO22'),
(11, 11, 31, 'Yonk’ into ephuma emnyango wendlu yam, yondihlasela, ndide ndibuye ndixolile koonyana baka-Amon, ibe yekaYehova, ndiyithabathe ibe lidini elinyukayo.', 'XHO22'),
(11, 11, 32, 'uYafete wahamba phambi koonyana baka-Amon ukuba alwe nabo; uYehova wabanikela esandleni sakhe.', 'XHO22'),
(11, 11, 33, 'Wababulala ngogonyamelo olukhulu ukususela eArohere ukusa eMiniti, izixeko ezingamashumi amabini ukuya kutsho kwiNtlango yezidiliya. Bathotywa ke oonyana baka-Amon phambi koonyana bakaSirayeli.', 'XHO22'),
(11, 11, 34, 'Wafika uYafete eMizpa, yaphuma intombi yakhe, yamkhawulela, ibetha ingqongqo, ingqungqa; yaye ekuphela komntwana wakhe; kuye ebengenanyana nantombi.', 'XHO22'),
(11, 11, 35, 'Akuyibona, wazikrazula iingubo zakhe, wathi, Camagu, ntombi yam! Undithobe kakhulu, ungomnye wabandoyikisayo, ngokuba ndiwuvulile umlomo wam kuYehova, andinakubuya.', 'XHO22'),
(11, 11, 36, 'Wathi kuye, Bawo, uwuvulile umlomo wakho kuYehova; yenza kum njengoko kuphume emlonyeni wakho; ngokuba uYehova eniphindezelele izenzo zenu, nyana baka-Amon.', 'XHO22'),
(11, 11, 37, 'Wathi kuyise, Makube le nto kum: Mandilinde iinyanga zibe mbini, ndinyuke iintaba, ndibulilele ubuntombi bam, mna nabalingane bam.', 'XHO22'),
(11, 11, 38, 'Wathi: “Hamba! Wamnika ikhefu leenyanga zambini, wahamba nabalingane bakhe, walilela ezintabeni;', 'XHO22'),
(11, 11, 39, 'Ekupheleni kweenyanga ezimbini, wabuyela kuyise, wenza kuye ngokwesifungo sakhe, abesithethile; kwaye wayengazi mntu. Yaba lisiko elo kwaSirayeli;', 'XHO22'),
(11, 11, 40, 'iminyaka ngeminyaka, iintombi zakwaSirayeli ziye ziyibolela intombi kaYafete waseGiliyadi, imihla emine ngomnyaka.', 'XHO22'),
(11, 12, 1, 'Ahlanganisana amadoda akwaEfrayim, aya entla, athi kuYafete, Yini na ukuba ubaxabele oonyana baka-Amon, ungasibizanga sihambe nawe? siya kuyitshisa indlu yakho phezu kwakho ngomlilo.', 'XHO22'),
(11, 12, 2, 'Wathi uYafete kubo, Mna nabantu bam sasibambene kakhulu noonyana baka-Amon; ndakubiza, anandihlangula esandleni sabo.', 'XHO22'),
(11, 12, 3, 'Ndithe ndakubona ukuba anindisindisi, ndawunikela umphefumlo wam esandleni sam, ndawela ndaya koonyana baka-Amon, uYehova wabanikela esandleni sam; kungenxa yoko le nto nize kum namhlanje ukuba nilwe. Mna?', 'XHO22'),
(11, 12, 4, 'Wawahlanganisa ke uYafete onke amadoda aseGiliyadi, walwa noEfrayim; amadoda aseGiliyadi amoyisa uEfrayim, ngokuba ebesithi, Nina, madoda aseGiliyadi, nasabayo ezweni lakwaEfrayim, naphakathi kwamaEfrayim namaManase.', 'XHO22'),
(11, 12, 5, 'AmaGiliyadi athabatha inxalenye yeYordan phambi koonyana bakaEfrayim; Athi kuye amadoda aseGiliyadi, UngumEfrayim na? Ukuba uthe: Hayi!', 'XHO22'),
(11, 12, 6, 'Bathi kuye, Yithi, Sibholete, wathi, Sibholete, ngokuba engakwazi ukuthetha kakuhle. Bandula ke bambamba baza bambulalela eYordan, yaye ngelo xesha kwakukho amashumi amane anesibini amawaka akwaEfrayim.', 'XHO22'),
(11, 12, 7, 'uYafete wawakhokela amaSirayeli iminyaka emithandathu. Wafa uYafete waseGiliyadi, wangcwatyelwa komnye umzi waseGiliyadi.', 'XHO22'),
(11, 12, 8, 'Emva kwakhe uIbhesan waseBhetelehem wakhokela amaSirayeli.', 'XHO22'),
(11, 12, 9, 'Wayenoonyana abamashumi mathathu neentombi ezimashumi mathathu; Yaye wawakhokela amaSirayeli iminyaka esixhenxe.', 'XHO22'),
(11, 12, 10, 'Wafa ke uIbhesan, wangcwatyelwa eBhetelehem.', 'XHO22'),
(11, 12, 11, 'Emva kwakhe uElon umZebhulon wayekhokela oonyana bakaSirayeli; waba neminyaka elishumi engukumkani kwaSirayeli.', 'XHO22'),
(11, 12, 12, 'Wafa uElon wakwaZebhulon, wangcwatyelwa eAyalon ezweni lakwaZebhulon.', 'XHO22'),
(11, 12, 13, 'Emva kwakhe uAbhedon unyana kaHileli wasePiraton wayekhokela oonyana bakaSirayeli.', 'XHO22'),
(11, 12, 14, 'Wayenoonyana abamashumi mane, nabazukulwana abamashumi mathathu, ababekhwele iimazi zamaesile ezimashumi mathandathu; yaye wabakhokela oonyana bakaSirayeli iminyaka esibhozo.', 'XHO22'),
(11, 12, 15, 'Wafa ke uAbhedon unyana kaHileli, wasePiraton, wangcwatyelwa ePiraton, ezweni lakwaEfrayim, kweleentaba lama-Amaleki.', 'XHO22'),
(11, 13, 1, 'Baphinda oonyana bakaSirayeli benza ububi emehlweni kaYehova; uYehova wabanikela esandleni samaFilisti iminyaka emashumi mane.', 'XHO22'),
(11, 13, 2, 'Kwaye kukho indoda yaseSore, yesizwe sakwaBhadan, egama belinguManowa; Umkakhe waye eludlolo, eludlolo.', 'XHO22'),
(11, 13, 3, 'Kwabonakala isithunywa sikaYehova kumfazi lowo, sathi kuye, Yabona, uludlolo wena, akuyi kuzala; ngokuba uya kumitha, uzale unyana.', 'XHO22'),
(11, 13, 4, 'Zigcine ke ngoko, ungaseli wayini nasiselo sinxilisayo, ungadli nanye into eyinqambi.', 'XHO22'),
(11, 13, 5, 'Ngokuba uyabona, uya kumitha, azale unyana; ize ingabi nencakuba entlokweni yakhe; ngokuba unyana lowo uya kuba ngumahlulwa kaThixo kwasesizalweni yaye uya kuqalisa ukumhlangula uSirayeli esandleni samaFilisti.', 'XHO22'),
(11, 13, 6, 'Wandula ke umfazi weza wathi endodeni yakhe: “Lo mntu kaTHIXO oyinyaniso uze kum, nembonakalo yakhe yayinjengobuso bengelosi kaTHIXO oyinyaniso, yoyikeka kakhulu; andimbuzanga apho avela khona, akandixelelanga negama lakhe.', 'XHO22'),
(11, 13, 7, 'Wathi kum, Uyabona, uya kumitha, uzale unyana; ke ngoko uze ungaseli wayini nasiselo sinxilisayo, ungadli nanye into eyinqambi; ngokuba le nkwenkwe iya kuba ngumahlulwa kaThixo kwasesizalweni sayo, kude kube ngumhla wokufa kwayo.', 'XHO22'),
(11, 13, 8, 'Wathandaza ke uManowa kuYehova, wathi, Nkosi yam, lo mfo wakwaThixo umthumileyo, makabuyele kuthi, asifundise ukuba simthini na umntwana oya kuzalwa.', 'XHO22'),
(11, 13, 9, 'Waliva uThixo ilizwi likaManowa; Sabuya isithunywa sikaYehova kumfazi ohleli entsimini, uManowa indoda yakhe engenaye.', 'XHO22'),
(11, 13, 10, 'Wagidima umfazi ngokukhawuleza, waya kuxelela indoda yakhe, wathi kuyo, Yabona, le ndoda ibize kum laa mhla, ibonakele kum.', 'XHO22'),
(11, 13, 11, 'Wesuka ke uManowa, wamlandela umkakhe, waya kuloo ndoda, wathi kuyo, Ungulaa mntu na ubethetha nomfazi? Wathi yena: Ndinguye.', 'XHO22'),
(11, 13, 12, 'Wathi uManowa, Makube njalo amazwi akho. Siya kumqeqesha njani umntwana, kwaye siya kwenza ntoni ngaye?', 'XHO22'),
(11, 13, 13, 'Sathi isithunywa sikaYehova kuManowa, Makenze konke endikuthethileyo kumfazi.', 'XHO22'),
(11, 13, 14, 'Maze angadli nto emdiliyeni, angaseli wayini nasiselo sinxilisayo, angadli nanye into engahlambulukanga; konke endimwisele umthetho ngako, makakugcine.', 'XHO22'),
(11, 13, 15, 'Wathi uManowa kwisithunywa sikaYehova, Masikubambe, side sikulungisele itakane lebhokhwe.', 'XHO22'),
(11, 13, 16, 'Sathi isithunywa sikaYehova kuManowa, Nokuba uthe wandinyamekela, andiyi kudla isonka sakho; yaye ukuba uyafuna ukuthabatha umnikelo otshiswayo, uwuse kuYehova. Kuba uManowa wayengazi ukuba yingelosi kaYehova.', 'XHO22'),
(11, 13, 17, 'Wathi uManowa kwisithunywa sikaYehova, Ungubani na igama lakho, ukuze sikuhlonele ekuzalisekeni kwamazwi akho?', 'XHO22'),
(11, 13, 18, 'Sathi kuye isithunywa sikaYehova, Yini na ukuba ulibuze kangaka igama lam, lisithele?', 'XHO22'),
(11, 13, 19, 'Wandula ke uManowa walithabatha itakane lebhokhwe kunye nomnikelo wasemlilweni waza wabingelela kuYehova phezu kweliwa, yaye ingelosi yenza ummangaliso; uManowa nomkakhe bebukele.', 'XHO22'),
(11, 13, 20, 'Lenyuka ilangatye lisuka esibingelelweni, lisiya ezulwini, senyuka isithunywa sikaYehova ngelangatye lesibingelelo. uManowa nenkosikazi yakhe bayijonga bawa ngobuso.', 'XHO22'),
(11, 13, 21, 'Kodwa ingelosi kaYehova ayizange iphinde ibonakale kuManowa nomfazi wakhe. Waqonda ke uManowa ukuba sisithunywa sikaYehova.', 'XHO22'),
(11, 13, 22, 'Wathi uManowa kumkakhe, Siya kufa ngenene, ngokuba simbonile uThixo.', 'XHO22'),
(11, 13, 23, 'Wathi umkakhe kuye, Ukuba uYehova ubefuna ukusibulala, ange engalamkelanga esandleni sethu nedini elinyukayo, angavumi ukuba sizibone zonke ezi zinto, njengoko kunjalo namhla khange.', 'XHO22'),
(11, 13, 24, 'Wazala unyana umfazi lowo, wathi igama lakhe nguSamson; wakhula umntwana, uYehova wamsikelela.', 'XHO22'),
(11, 13, 25, 'Maxa wambi umoya kaYehova uqala ukumqhubela enkampini yakwaDan phakathi kweSoreya ne-Eshtawoli.', 'XHO22'),
(11, 14, 1, 'Wehla uSamson, waya kwaTimna, wabona inkazana ezintombini zamaFilisti, uTimna.', 'XHO22'),
(11, 14, 2, 'Wenyuka, waxelela uyise nonina, wathi, Ndibone inkazana enguTimna, entombini yamaFilisti; ke ngoko mthabathe abe ngumfazi wam.', 'XHO22'),
(11, 14, 3, 'Bathi kuye uyise nonina, Akukho mfazi na ezintombini zabazalwana bakho, nasebantwini bakowethu bonke, ukuba uthe wazeka umfazi kumaFilisti? Wathi uSamson kuyise, Ndithabathele yena; ngokuba undonwabisa kakhulu.', 'XHO22'),
(11, 14, 4, 'Kodwa uyise nonina babengazi ukuba ivela kuYehova xa wayefuna ithuba lokulwa namaFilisti, ekubeni ngelo xesha amaFilisti ayelawula uSirayeli.', 'XHO22'),
(11, 14, 5, 'Wahamba uSamson noyise nonina, baya eTimna, bafika ezidiliyeni zaseTimna; nantso ingonyama imgquma.', 'XHO22'),
(11, 14, 6, 'Wehla phezu kwakhe uMoya kaYehova ngamandla, wamdla edla itakane, kungekho nto esandleni sakhe; Akabaxelelanga uyise nonina into ayenzileyo.', 'XHO22'),
(11, 14, 7, 'Wehla wathetha nenkazana leyo; wamkholisa kunene uSamson.', 'XHO22'),
(11, 14, 8, 'Wabuya weza kumthabatha, wajika, wasibona isidumbu sengonyama; nantso inkitha yeenyosi nobusi emzimbeni wengonyama.', 'XHO22'),
(11, 14, 9, 'Wathabatha esandleni sakhe, wadla, waya kuyise nonina, wabanika, badla; kodwa ungabaxeleli ukuba uthabathe ubusi emhadini wengonyama.', 'XHO22'),
(11, 14, 10, 'Wehla ke uyise waya kuloo nkazana, uSamson walungisa ukudla khona; ngokuba abafana babedla ngokuyenza loo nto.', 'XHO22'),
(11, 14, 11, 'Bakumbona ke, bazisa abalingane abangamashumi amathathu ukuba babe naye.', 'XHO22'),
(11, 14, 12, 'Wathi uSamson kubo, Makhe ndinimisele iqhina; ukuba nithe nandixelela oku ngeentsuku ezisixhenxe zomthendeleko, nalifumana, ndoninika amalaphu angamashumi amathathu, neengubo ezikwakwaba ezimashumi mathathu.', 'XHO22'),
(11, 14, 13, 'Ukuba anithanga nibe nako ukundicombululela, niya kundinika amalaphu angamashumi amathathu, nezinye iingubo ezimashumi mathathu. Bathi kuye, Ndixelele iqhina lakho, silive.', 'XHO22'),
(11, 14, 14, 'Wathi kubo, Ukudla kuphuma kodlayo, nobumnandi buphuma konamandla. Ababa nako ukuyicombulula loo mfihlelo ngeentsuku ezintathu.', 'XHO22'),
(11, 14, 15, 'Kwathi ngomhla wesixhenxe, bathi kumkaSamson, Yizame indoda yakho, isixelele loo mfihlelo, singakutshisi wena nendlu kayihlo; usibizele ukuba sizothatha into esinayo? Ngaba akunjalo?', 'XHO22'),
(11, 14, 16, 'UmkaSamson walila phezu kwakhe, wathi, Undithiyile, akundithandi; Ubenzele iqhina oonyana babantu bakowethu, akwandixelela noko. Athi kuye, Uyabona, ezo zinto andizanga ndimxelele ubawo noma;', 'XHO22'),
(11, 14, 17, 'Walila phambi kwakhe imihla esixhenxe, emthendelekweni wabo; Wamxelela ngomhla wesixhenxe, ngenxa yokuba emonile; wabaxelela abantwana babantu bakowabo loo mfihlelo.', 'XHO22'),
(11, 14, 18, 'Kwathi ngomhla wesixhenxe, lingekatshoni ilanga, athi kuye amadoda aloo mzi, Yintoni na enencasa ngaphezu kobusi? Yintoni na enamandla ngaphezu kwengonyama? Wathi kubo: Ukuba beningalimanga ngenkomo yam, ngeningayifumananga imfihlo yam.', 'XHO22'),
(11, 14, 19, 'Yaye umoya kaYehova wehla phezu kwakhe, yaye wehla waya eAshkelon waza wabulala amadoda angamashumi amathathu phakathi kwawo waza wathabatha amaxhoba awo waza wanika abo babechaza imfihlelo izambatho zabo. Wavutha umsindo wakhe, wenyuka waya endlwini kayise.', 'XHO22'),
(11, 14, 20, 'Ke umkaSamson wanikwa ugxa wakhe, obengumhlobo wakhe;', 'XHO22'),
(11, 15, 1, 'Kwakuba ntsuku, ngexesha lokuvuna ingqolowa, uSamson wamvelela umkakhe, enetakane lebhokhwe; Athi: ndizongena nomfazi wam ekamereni. Akavuma uyise ukuba angene.', 'XHO22'),
(11, 15, 2, 'Wathi uyise, Inene, ndathi, umthiyile ngokupheleleyo; ke ngoko, ndimnikile kumlingane wakho; Usisi wakhe akamhle kunaye? mthabathe endaweni yakhe.', 'XHO22'),
(11, 15, 3, 'Wathi uSamson kuwo, Ndiya kuba njengamaFilisti, ndimsulwa, nokuba ndithe wona ngobugwenxa kuwo.', 'XHO22'),
(11, 15, 4, 'Waya uSamson wabamba amakhulu amathathu oodyakalashe, wawathabatha oodyakalashe, wayijika imisila yabo ngasemva, wafaka umlilo phakathi kwemisila leyo yomibini.', 'XHO22'),
(11, 15, 5, 'Akuba ezitshise umlilo iinkuni, wayiyeka yawela phakathi kwengqolowa eseleyo yamaFilisti, wayitshisa ingqolowa eseleyo, neediliya, kwanesidiliya, neminquma.', 'XHO22'),
(11, 15, 6, 'Athi amaFilisti, Ngubani na lo wenze loo nto? Baphendula bathi, NguSamson, umyeni kaTimna; ngokuba wamthabatha umkakhe, wamnika umlingane wakhe. Enyuka amaFilisti, amtshisa yena noyise ngomlilo.', 'XHO22'),
(11, 15, 7, 'Wathi uSimon kubo, Nokuba nithe nenjenjalo, ndoniphindezela impindezelo kuni, ndandule ke ukuyeka.', 'XHO22'),
(11, 15, 8, 'Wababetha esinqeni nasethangeni ngobubi obukhulu, wehla ke, wahlala eliweni lika-Etam.', 'XHO22'),
(11, 15, 9, 'Enyuka ke amaFilisti, amisa kwaYuda, asasaza eLehi.', 'XHO22'),
(11, 15, 10, 'Athi amadoda akwaYuda, Yini na ukuba nisihlasele? Athi, Sinyuke, siya kubopha uSamson, senze kuye njengoko enze ngako kuthi.', 'XHO22'),
(11, 15, 11, 'Enyuka amadoda angamawaka amathathu, evela kwaYuda, aya engxondorheni yase-Etam, athi kuSamson, Awazi na ukuba amaFilisti asilawule? usenze ntoni? Wathi kuwo, Njengoko enze ngako kum, ndenze ngako nam kuwo.', 'XHO22'),
(11, 15, 12, 'Athi kuye, Sihle, size kukukhonkxa, ukuba sikunikele esandleni samaFilisti. Wathi uSamson kubo, Nindilumkele, ningandibethi;', 'XHO22'),
(11, 15, 13, 'Bathetha kuye bathi, Hayi; kodwa siya kukubopha size sikunikele ezandleni zabo; kodwa ngokuqinisekileyo asiyi kukubulala. Bambopha ngeentsontelo ezimbini ezintsha baza bamnyusela eliweni.', 'XHO22'),
(11, 15, 14, 'Wathi akufika eLehi, amaFilisti amenzela isijwili, uMoya kaYehova waba phezu kwakhe ngamandla, zasuka iintsontelo ezandleni zakhe zaba njengelinen eshiswe ngumlilo, aphuka amathambo ezandleni zakhe.', 'XHO22'),
(11, 15, 15, 'Wafumanana nomhlathi omtsha we-esile, wolula isandla sakhe, wawuthabatha, wabulala ngawo iwaka lamadoda.', 'XHO22'),
(11, 15, 16, 'Wathi uSamson, Ngomhlathi we-esile ndibulele iwaka ngomhlathi wedonki;', 'XHO22'),
(11, 15, 17, 'Wathi akugqiba ukuthetha, wawuphosa umhlathi esandleni sakhe, wathi loo ndawo yiRamatlehi.', 'XHO22'),
(11, 15, 18, 'Wanxanwa kakhulu, wanqula uYehova, wathi, Wena usinike olu sindiso lukhulu esandleni somkhonzi wakho, kwaye ngoku ndiya kufa lunxano ndiwele ezandleni zabangalukanga?', 'XHO22'),
(11, 15, 19, 'uThixo wacanda umngxuma emhlathini, kwaphuma amanzi kuwo; wasela, wabuya umoya wakhe, waphila; ke ngoko, silibiza elo gama ngokuba yiEnehakore, enguLihayi kude kube namhla.', 'XHO22'),
(11, 15, 20, 'Waqhubeka ekhokela amaSirayeli ngemihla yamaFilisti iminyaka engamashumi amabini.', 'XHO22'),
(11, 16, 1, 'uSamson waya eGasa, wabona inkazana elihenyukazi, wahlala kulo.', 'XHO22'),
(11, 16, 2, 'Babikelwa abamenyiweyo, kwathiwa, uSimon uze apha. Bamngqonga, bamlinda ubusuku bonke esangweni lesixeko, bathi cwaka ubusuku bonke, besithi: “Ngomso, kwakuba semini, siya kumbulala.', 'XHO22'),
(11, 16, 3, 'Walala uSamson kwada kwaphakathi kobusuku, wavuka phakathi kobusuku, wazithabatha iingcango zesango lomzi, nemigubasi yomibini yeengcango, wahamba nazo, nemivalo neento zonke, wazibeka phezu kwempahla yakhe.', 'XHO22'),
(11, 16, 4, 'Emva koko wathanda enye inkazana eyayikwintlambo yomlambo waseSoreki, igama layo yayinguDelila.', 'XHO22'),
(11, 16, 5, 'Zandula ke iinkosana zamaFilisti zeza kuye zaza zathi kuye: “Mvavanye uze ubone apho akhoyo amandla akhe angaka, nendlela esinokumoyisa ngayo, ukuze simkhonkxe ukuze simcinezele; sokunika elowo iishekele zesilivere ezilishumi elinanye.', 'XHO22'),
(11, 16, 6, 'Wathi uDelila kuSamson, Ndixelele ukuba avela phi na la mandla akho angaka, nokuba ungakhonkxwa ngantoni na, ukuba akuqobe?', 'XHO22'),
(11, 16, 7, 'Wathi uSimon kuye, Ukuba bathe bandikhonkxa ngemifuno ekrwada esixhenxe, ndothi fehle, ndibe njengomnye umntu.', 'XHO22'),
(11, 16, 8, 'Zandula ke iinkosana zamaFilisti zamzisela imifuno esixhenxe engomileyo, waza wamkhonkxa ngayo.', 'XHO22'),
(11, 16, 9, 'Kwabakho amadoda abemlalele, aza ahlala naye egumbini. Wathi kuye, Samson, amaFilisti aphezu kwakho! Wazicheba ke iinwele zakhe, njengokunqunyulwa kwentsontelo yakuva umlilo; Ngoko amandla akhe ayengaziwa.', 'XHO22'),
(11, 16, 10, 'Wathi uDelila kuSamson: “Yabona, udlale ngam waza wandixokisa; ndixelele ukuba ungabotshwa yintoni.', 'XHO22'),
(11, 16, 11, 'Wathi kuye, Ukuba bathe bandikhonkxa amathambo amatsha, abe kengekasebenzi, ndothi fehle, ndibe njengomnye umntu.', 'XHO22'),
(11, 16, 12, 'uDelila wathabatha iintsontelo ezintsha, wamkhonkxa ngazo, wathi kuye, uSamson, arhawulwe ngamaFilisti; Kwaye kwakukho amaxoki ahlala kwelo gumbi. Yaye wawakrazula ezandleni zakhe njengentsontelo.', 'XHO22'),
(11, 16, 13, 'Wathi uDelila kuSamson, Udlale ngam unangoku, undixokisa; ndixelele ukuba ungabotshwa yintoni. Wathi kuye, Ukuba uthe waluka imisonto esixhenxe yeenwele zentloko yam engutyeni;', 'XHO22'),
(11, 16, 14, 'Wamxhoma esibondeni, wathi kuye, Samson, amaFilisti aphezu kwakho; Wavuka ebuthongweni, wazixhoma ngesikhonkwane somthi nelaphu.', 'XHO22'),
(11, 16, 15, 'Wathi kuye, Ungathini na ukuthi ndiyakuthanda, ukanti intliziyo yakho ayinam? Undithwale kwada kwakathathu, akwandixelela ukuba aphi na amandla akho angaka.', 'XHO22'),
(11, 16, 16, 'Yaye kwathi, yonke imihla embandezela ngamazwi akhe, embandezela, wakhathazeka umphefumlo wakhe, wada wesa ekufeni;', 'XHO22'),
(11, 16, 17, 'Wamxelela konke okube kusentliziyweni yakhe; wathi kuye, Andiyichebanga intloko yakho; ngokuba ndingumNazarete kaThixo kwasesizalweni sikama.', 'XHO22'),
(11, 16, 18, 'Wabona uDelila ukuba umxelele yonke intliziyo yakhe, wathumela, wabiza abathetheli bamaFilisti, wathi, Nyukani kaloku, ngokuba undibonisile yonke intliziyo yakhe. Zeza kuye abathetheli bamaFilisti, bephethe imali ngesandla sazo.', 'XHO22'),
(11, 16, 19, 'Wamlalisa emathangeni akhe; wayibiza indoda, wayicheba iinwele ezisixhenxe zentloko yakhe; waqala wamxina, ephela amandla akhe.', 'XHO22'),
(11, 16, 20, 'Wathi, Samson, amaFilisti aphezu kwakho; Wavuka ebuthongweni wathi, Ndiya kuphuma ndingcangcazela njengakuqala. Wayengazi ke ukuba uYehova umshiyile.', 'XHO22'),
(11, 16, 21, 'Ambamba amaFilisti, amgquma ubuso, amzisa eGaza, amkhonkxa ngemixokelelwane yobhedu; wafela entolongweni.', 'XHO22'),
(11, 16, 22, 'Zaqala iinwele zentloko yakhe zahluma, emva kokuba echetyiwe.', 'XHO22'),
(11, 16, 23, 'Abathetheli bamaFilisti bahlanganisana, ukuba babingelele umbingelelo omkhulu kuDagon uthixo wabo, bavuya; bathi, uThixo wethu umnikele esandleni sethu uSamson, utshaba lwethu.', 'XHO22'),
(11, 16, 24, 'Bambona abantu, badumisa uthixo wabo, bathi, uThixo wethu ulunikele esandleni sethu utshaba lwethu, umtshabalalisi welizwe lethu, obenze babaninzi ababuleweyo kuthi.', 'XHO22'),
(11, 16, 25, 'Kwathi, ngenxa yovuyo lwentliziyo yabo, bathi, Bizani uSamson, asidlalele. Bambiza ke uSamson entolongweni; bammisa phakathi kweentsika.', 'XHO22'),
(11, 16, 26, 'Wathi uSamson kumfana ombambe ngesandla, Ndiyeke ndichukumise iintsika emi ngazo indlu, ndayame ngazo;', 'XHO22'),
(11, 16, 27, 'Indlu ibizele ngamadoda nabafazi; zazikhona zonke iinkosana zamaFilisti; yaye eluphahleni kwakukho malunga namawaka amathathu amadoda nabafazi bebukele ngoxa uSamson wayedlala.', 'XHO22'),
(11, 16, 28, 'Wakhala uSamson kuYehova, wathi, Yehova, Yehova, ndikhumbule, ndivuse kube kanye, Yehova, ndiphindezele impindezelo kumaFilisti ngenxa yamehlo am omabini.', 'XHO22'),
(11, 16, 29, 'Wazibamba uSamson zombini iintsika ezisesazulwini, emi kuzo indlu, emi kuzo, emi kuzo, enye ngesandla sokunene, enye ngesandla sakhe sasekhohlo.', 'XHO22'),
(11, 16, 30, 'Wathi uSamson, Mandife namaFilisti. Wathoba ngobugorha bakhe bonke; yawa indlu phezu kwezikhulu naphezu kwabantu bonke ababekuyo. Ngoko ke abafileyo awababulala ekufeni kwakhe baba baninzi kunabo wababulala esadla ubomi.', 'XHO22'),
(11, 16, 31, 'Behla abazalwana bakhe nendlu kayise yonke, bamthabatha, bamnyusa, bamngcwabela phakathi kweSoreya ne-Eshtawoli, engcwabeni likaManowa uyise. Walawula kwaSirayeli iminyaka emashumi mabini.', 'XHO22'),
(11, 17, 1, 'Kwaye kukho indoda yelasezintabeni zakwaEfrayim egama linguMika.', 'XHO22'),
(11, 17, 2, 'Wandula ke wathi kunina: “Eliwaka elinekhulu leeshekele zesilivere ezathatyathwa kuwe, owaqalekisa ngazo waza wathetha ndisakuva, yabona, isilivere leyo ikum; Ndayithatha. Unina wathi: Ngamana uYehova akusikelele, nyana wam!', 'XHO22'),
(11, 17, 3, 'Xa wayibuyisela kunina isilivere ebunzima buliwaka elinekhulu leeshekele, wathi unina: “Isilivere leyo ndiyinyulele kuYehova ngokupheleleyo, ephuma esandleni sam ibe yeyonyana wam, ukuba enze umfanekiso oqingqiweyo nomfanekiso oqingqiweyo; oku kuwe.', 'XHO22'),
(11, 17, 4, 'Wayibuyisela kunina imali; Unina wathabatha amakhulu amabini eeshekele zesilivere, wawanika umyili, wenza ngazo umfanekiso oqingqiweyo notyhidiweyo; yaye yayisendlwini kaMika.', 'XHO22'),
(11, 17, 5, 'Loo ndoda, uMika, yayinendlu yothixo yaye yenza ilaphu legxalaba ngothixo wendlu yaza yangcwalisa omnye woonyana bayo ukuba abe ngumbingeleli wayo.', 'XHO22'),
(11, 17, 6, 'Ngaloo mihla kwakungekho kumkani kwaSirayeli, kodwa wonk’ ubani wenza oko akubonayo kwakulungile emehlweni akhe.', 'XHO22'),
(11, 17, 7, 'Ke eBhetelehem yakwaYuda kwakukho umfana wesizwe sakwaYuda, engumLevi, engumphambukeli apho.', 'XHO22'),
(11, 17, 8, 'Yaphuma indoda leyo eBhetelehem yakwaYuda, yaphambukela eBhetelehem yakwaYuda, yaya kuphambukela apho ingafumana indawo;', 'XHO22'),
(11, 17, 9, 'Wathi uMika kuyo, Ungowaphi na? Wathi kuye, NdingumLevi waseBhetelehem yakwaYuda;', 'XHO22'),
(11, 17, 10, 'Wathi uMika kuye, Hlala nam, ube ngubawo nombingeleli; ndokunika ishumi leeshekele zesilivere iminyaka ngeminyaka, nengubo yokwaleka, nendlela yokuphila. Wangena ke umLevi.', 'XHO22'),
(11, 17, 11, 'Wavuma umLevi lowo ukuhlala nendoda leyo; umfana lowo waba njengomnye woonyana bakhe kuye.', 'XHO22'),
(11, 17, 12, 'uMika wamngcwalisa umLevi lowo; loo mfana waba ngumbingeleli wakhe lowo, wahlala endlwini kaMika.', 'XHO22'),
(11, 17, 13, 'Wathi uMika, Kaloku ndiyazi ukuba uYehova uya kundenzela okulungileyo; ngokuba ndinomLevi ongumbingeleli wam.', 'XHO22'),
(11, 18, 1, 'Ngelo xesha kube kungekho kumkani kwaSirayeli. Ngelo xesha abantu besizwe sakwaDan babefuna ilifa lokwakha; ngenxa yokuba kwada kwayiloo mini babengekalifumani lonke ilifa labo phakathi kwezizwe zakwaSirayeli.', 'XHO22'),
(11, 18, 2, 'Oonyana bakaDan bathumela amadoda amahlanu emizalwaneni yawo endaweni yawo, amadoda angamakroti aseTsora nase-Eshtawoli ukuba aye kuhlola ilizwe nokulihlola; athi kuwo, Hambani ninyuke emhlabeni.', 'XHO22'),
(11, 18, 3, 'Akubona ukuba besendlwini kaMika, alifanisa ilizwi lendodana engumLevi; Bangena khona, bathi kuye, Uziswe ngubani na apha? wenza ntoni kulendawo? kwaye unantoni apho?', 'XHO22'),
(11, 18, 4, 'Wathi kuwo, Wenjenje uMikaya, wandithabatha, ndaba ngumbingeleli wakhe;', 'XHO22'),
(11, 18, 5, 'Bathi kuye, Cela icebo kuThixo, sazi ukuba le ndlela siyihambayo yosikeleleka na.', 'XHO22'),
(11, 18, 6, 'Wathi umbingeleli kubo, Hambani ninoxolo, phambi koYehova, nihambe apho nihamba khona.', 'XHO22'),
(11, 18, 7, 'Ahamba ke amadoda amahlanu afika eLayishe, ababona abantu behleli khona, bezolile njengamaTsidon; kwaye bekungekho ukhozo lwemostade ezweni lam olwalubahlazisa nakuwuphi na umcimbi; babekude namaTsidon, bengahlangani namntu.', 'XHO22'),
(11, 18, 8, 'Afika kubazalwana bawo eTsora nase-Eshtawoli; bathi abazalwana bawo kuwo, Nithini na?', 'XHO22'),
(11, 18, 9, 'Athi, Vukani, sibaxabele; kuba silibonile ihlabathi, kwaye qaphelani, lilunge kakhulu; kanti uselapho? ungalibazisi ukuya kwaye ungene ukuze ulidle ilifa ilizwe.', 'XHO22'),
(11, 18, 10, 'Ukuba nithe nahamba, nofika ezweni elikhuselekileyo, ezweni elibanzi ngeenxa zombini; indawo apho kungekho nto emhlabeni.', 'XHO22'),
(11, 18, 11, 'Kwaphuma khona amadoda angamakhulu amathandathu esizwe sakwaBhadan, evela eTsora nase-Eshtawoli, exhobe izixhobo zemfazwe.', 'XHO22'),
(11, 18, 12, 'Enyuka ke, amisa eKiriyati-yeharim yakwaYuda; ke ngoko, bayibiza loo ndawo ngokuba yiMahanehadan kude kube namhla; nantso isemva kweKiriyati-yarim.', 'XHO22'),
(11, 18, 13, 'Anduluka apho aya kweleentaba lakwaEfrayim, afika endlwini kaMika.', 'XHO22'),
(11, 18, 14, 'Aphendula ke amadoda amahlanu, abeze kulihlola ilizwe laseLayishe, athi kubazalwana bawo, Niyazi na ukuba kwezi zindlu kukho umfanekiso oqingqiweyo noqingqiweyo? ngoku ke yigqale into omelwe kukuyenza.', 'XHO22'),
(11, 18, 15, 'Ajika ke apho, afika endlwini yendodana engumLevi, endlwini kaMika, aza ayibulisa.', 'XHO22'),
(11, 18, 16, 'Amakhulu amathandathu amadoda axhobe ukulwa, akoonyana bakaDan, ema ekungeneni kwesango.', 'XHO22'),
(11, 18, 17, 'Amadoda amahlanu awayeye kuhlola ilizwe, enyuka angena apho, awuthabatha umfanekiso oqingqiweyo nesambatho esisemagxeni noothixo bendlu nomfanekiso oqingqiweyo; umbingeleli wema ekungeneni kwesango namadoda angamakhulu amathandathu axhobe iimpahla zemfazwe.', 'XHO22'),
(11, 18, 18, 'Emva koko bangena endlwini kaMika baza bathabatha umfanekiso oqingqiweyo ne-efodi noothixo bendlu nomfanekiso oqingqiweyo. Wathi umbingeleli kubo, Nenza ntoni na?', 'XHO22'),
(11, 18, 19, 'Bathi kuye, Yithi tu, ubeke isandla emlonyeni, uhambe nathi, ube ngubawo nombingeleli wethu; Kulungile na kuwe, ukuba ube ngumbingeleli wendlu yamntu mnye, ungumbingeleli wesizwe, nesizwe sakwaSirayeli na?', 'XHO22'),
(11, 18, 20, 'Yaba nemihlali intliziyo yombingeleli, wayithabatha iefodi noothixo bendlu nomfanekiso oqingqiweyo waza wangena phakathi kwabantu.', 'XHO22'),
(11, 18, 21, 'Bajika ke, bahamba, babeka abantwana neenkomo nenqwelo phambi kwabo.', 'XHO22'),
(11, 18, 22, 'Akuba kude nendlu kaMika, amadoda abesezindlwini ezikufuphi nendlu kaMika ahlanganisana, ababamba oonyana bakaDan.', 'XHO22'),
(11, 18, 23, 'Babiza oonyana bakaDan. Ajika ke athi kuMika, Uhlelwe yintoni na, ukufika kwakho unesihlwele esingaka?', 'XHO22'),
(11, 18, 24, 'Wathi yena, Oothixo bam endabenzayo nibathabathile, nemka naye nombingeleli wam; kwaye yintoni enye endinayo? Sathi kum, Yintoni na ingxaki yakho?', 'XHO22'),
(11, 18, 25, 'Bathi kuye oonyana bakaDan, Musa ukulivakalisa ilizwi lakho kuthi, hleze akusukele amadoda anomsindo, uphulukane nomphefumlo wakho nowendlu yakho.', 'XHO22'),
(11, 18, 26, 'Bahamba ke oonyana bakaDan. Wabona uMika ukuba bomelele kunaye, wajika wabuyela endlwini yakhe.', 'XHO22'),
(11, 18, 27, 'Bayithabatha impahla awayeyenzile uMika, nombingeleli lowo wayenaye, beza eLayishe nabantu ababehleli ngoxolo nabahleli bekhuselekile; ababulala ngohlangothi lwekrele, bawutshisa umzi ngomlilo.', 'XHO22'),
(11, 18, 28, 'Bekungekho msindisi, ngokuba bekukude eTsidon, kungekho kuzala; yaye isentilini ekufuphi neBhete-hobhe. Kwaye bakha isixeko baza bahlala kuso.', 'XHO22'),
(11, 18, 29, 'Bathi loo mzi ngowakwaDan, ngegama likaDan, owazalelwa kwaSirayeli; kodwa ngaphambili, igama lesixeko laliyiLayishe.', 'XHO22'),
(11, 18, 30, 'Oonyana bakaDan bazimisela umfanekiso oqingqiweyo, yaye uYonatan unyana kaGershom, unyana kaManase, yena noonyana bakhe baba ngababingeleli besizwe sakwaDan de kwangumhla wokuthinjwa kwelo lizwe.', 'XHO22'),
(11, 18, 31, 'Yaye balungiselela umfanekiso oqingqiweyo kaMika awayewenzile ngayo yonke imihla yendlu yoTHIXO oyinyaniso eyayiseShilo.', 'XHO22'),
(11, 19, 1, 'Ngelo xesha, kungekho kumkani kwaSirayeli, bekukho umLevi owayehlala kweleentaba lakwaEfrayim, wazeka umfazi eBhetelehem yakwaYuda.', 'XHO22'),
(11, 19, 2, 'Wakrexeza naye isithende, wemka kuye, waya endlwini kayise eBhetelehem yakwaYuda, wahlala khona iinyanga zane.', 'XHO22'),
(11, 19, 3, 'Yesuka indoda yalo yalilandela, ukuba ithethe kakuhle nalo, yalibuyisa nekhoboka lalo, namanye amaesile; wayingenisa endlwini kayise, wayibona uyise wentombazana, wakuvuyela ukuyihlangabeza.', 'XHO22'),
(11, 19, 4, 'Uyise womkakhe, uyise wentombi, wayeyigcinele yona; wahlala naye iintsuku zantathu; badla, basela, balala khona.', 'XHO22'),
(11, 19, 5, 'Kwathi ngomhla wesine, bavuka kusasa, ukuba ahambe; wathi uyise wentombi kumyeni wakhe, Yithuthuzele intliziyo yakho ngeqhekeza lesonka, ungashiyi emva.', 'XHO22'),
(11, 19, 6, 'Bahlala phantsi, badla, basela ndawonye, ngokuba wathi uyise wentombi endodeni leyo, Vuka, ulale ubusuku bonke, ize ibe nemihlali intliziyo yakho.', 'XHO22'),
(11, 19, 7, 'Yesuka indoda leyo ukuba ihambe; wayijoka uyise womkayo; ke ngoko, wachitha ubusuku apho kwakhona.', 'XHO22'),
(11, 19, 8, 'Yavuka kusasa ngomhla wesihlanu ukuba ihambe; Wathi uyise wentombazana, Yithuthuzele intliziyo yakho; Bahlala ke kwada kwasemini enkulu, badla bobabini.', 'XHO22'),
(11, 19, 9, 'Yavuka indoda leyo, ukuba ihambe, yona inomkhonzi wayo, nomkhonzi wayo, uyisezala, uyise wentombi yayo, yathi kuyo: “Yabona, le mini iyeza, lala; ilanga liyakhula kude kube sekupheleni, hlala apha, ukuze intliziyo yakho ivuye; ndihambe ngomso ndigoduke.', 'XHO22'),
(11, 19, 10, 'Akuthandanga kulala indoda leyo, yesuka yemka, yafika eYebhusi, eyiYerusalem ke leyo; idizili ezimbini; nesithende sakhe sasikunye naye.', 'XHO22'),
(11, 19, 11, 'Bafika eYebhusi, lalisele litshonile ilanga; Wathi umkhonzi enkosini yakhe, Hamba, singene kulo mzi wamaYebhusi, silale khona.', 'XHO22'),
(11, 19, 12, 'Yathi kuye inkosi yakhe, Masingasuki apha siye kumzi wolunye uhlanga, ongewoonyana bakaSirayeli; siya eGibheha.', 'XHO22'),
(11, 19, 13, 'Wathi kumfana wakuyo, Yiza, sisondele kwenye indawo, silale, nokuba kuseGibheha, nokuba kuseRama.', 'XHO22'),
(11, 19, 14, 'Zahamba, zahamba; latshona ilanga phezu kwabo beseGibheha ezweni lakwaBhenjamin.', 'XHO22'),
(11, 19, 15, 'Baphambukela khona, baya eGibheha, balalisa khona; wangena, wamhlalisa ezitratweni zomzi; kuba bekungekho namnye ubamkelayo ukuba baye kulala endlwini yakhe.', 'XHO22'),
(11, 19, 16, 'Ke kaloku indoda enkulu yeza ivela emsebenzini wayo ngokuhlwa, ivela emasimini, yeza ivela kweleentaba lakwaEfrayim; wayishiya iGibheya njengomphambukeli, kodwa amadoda aloo ndawo ayephuma kwisizwe sakwaBhenjamin.', 'XHO22'),
(11, 19, 17, 'Wawaphakamisa amehlo akhe, wabona umhambi endaweni yembutho yomzi; Yathi indoda enkulu, Uya ngaphi na? ungowaphi?', 'XHO22'),
(11, 19, 18, 'Yathi kuyo, Siyahamba, sivela eBhetelehem yakwaYuda; siya emathambekeni akwaEfrayim; Ndemka khona, ndaya eBhetelehem yakwaYuda; ngoku ke ndihambela endlwini kaYehova; kwaye akukho bani undamkelayo endlwini.', 'XHO22'),
(11, 19, 19, 'Noko ke sikho utyani, neendiza zamaesile ethu; kukho isonka newayini ngenxa yam, nomkhonzazana wakho, nomfana onabakhonzi bakho; ayifuni nto.', 'XHO22'),
(11, 19, 20, 'Yathi indoda enkulu, Uxolo malube nawe; yonke iminqweno yakho mayibe phezu kwam; ungahlali nje esitratweni.', 'XHO22'),
(11, 19, 21, 'Wamsa endlwini yakhe, wabanika ukudla; bazihlamba iinyawo zabo, badla, basela.', 'XHO22'),
(11, 19, 22, 'Kwathi, bakuba benemihlali ezintliziyweni zabo, nango amadoda aloo mzi, oonyana bakaBheliyali, engena endlwini, ankqonkqoza elucangweni, athetha kumninindlu, ixhego, esithi, Zisa usindiso indoda engene endlwini yakho, ukuze simazi.', 'XHO22'),
(11, 19, 23, 'Yaphuma indoda engumninindlu, yaya kubo, yathi kubo, Hayi, bazalwana bam, musani ukukha nenze into embi enjengale; nibone ukuba le ndoda ingene endlwini yam, musani ukwenza obu budenge.', 'XHO22'),
(11, 19, 24, 'Nantsi intombi yam, intombi eyintombi, nesithende sayo; ngoku ndiya kubakhupha, ndikuthobe, ndibenzele okulungileyo emehlweni akho;', 'XHO22'),
(11, 19, 25, 'Akavuma loo madoda ukumphulaphula. Indoda leyo yathabatha isithende sayo, yasizisa kuwo; kwaye bamazi baza bamtshabhisa ubusuku bonke kwada kwasa; lwathi lwakufika, bamkhulula.', 'XHO22'),
(11, 19, 26, 'Wafika umfazi lowo kusasa, waziphosa phantsi phambi komnyango wendlu yendoda, umniniyo, kwada kwasa.', 'XHO22'),
(11, 19, 27, 'Yagqiba inkosi yakhe kusasa, yazivula iingcango zendlu, yaphuma ukuba ihambe; nantso intokazi iwile emnyango wendlu, izandla zayo zisemnyango.', 'XHO22'),
(11, 19, 28, 'Wathi kuye, Yima, sihambe; Kodwa akukho namnye waphendulayo. Yamkhwelisa e-esileni, yesuka indoda leyo, yagoduka.', 'XHO22'),
(11, 19, 29, 'Wathi akungena endlwini yakhe, wathabatha isitshetshe, wasibamba isithende sakhe, wasicanda saziziqwenga ezilishumi elinazibini, wasithumela kulo lonke ilizwe lakwaSirayeli.', 'XHO22'),
(11, 19, 30, 'Bathi bonke abayibonayo, Akuzanga kwenziwe nto inje, akuzanga kubonwe oku, kususela ekunyukeni koonyana bakaSirayeli ezweni laseYiputa, unanamhla; nikela ingqalelo, thabatha icebiso kwaye uthethe ingqondo yakho.', 'XHO22'),
(11, 20, 1, 'Baphuma ke bonke oonyana bakaSirayeli, labizelwa ndawonye ibandla kuYehova Mizpa njengamntu mnye, lithabathela kwaDan lesa eBher-shebha, nasezweni laseGiliyadi.', 'XHO22'),
(11, 20, 2, 'Babonakala ke izikhulu zabantu, zezizwe zonke zakwaSirayeli, ebandleni labantu bakaThixo, bengamakhulu amane amawaka angumqikela, arhola ikrele.', 'XHO22'),
(11, 20, 3, 'Beva ke oonyana bakaBhenjamin ukuba oonyana bakaSirayeli benyuke baya eMizpa. Baza oonyana bakaSirayeli bathi: “Sixelele, kwenzeke ntoni na obu bubi?', 'XHO22'),
(11, 20, 4, 'Waphendula umLevi, indoda yaloo mbulali, wathi, Ndifikile eGibheha yakwaBhenjamin, mna nesithende sam, ukuba ndilale khona.', 'XHO22'),
(11, 20, 5, 'Abemi baseGibheha bandivunukela, bayirhawula indlu phakathi kwam ebusuku, baceba ukundibulala; yaye isithende sam sabanyanzela ukuba bafe.', 'XHO22'),
(11, 20, 6, 'Ndasithabatha isithende sam, ndasinqunqa, ndasithumela kulo lonke ilizwe lelifa lakwaSirayeli; ngokuba enze okuziintloni nobudenge kuSirayeli.', 'XHO22'),
(11, 20, 7, 'Yabonani, bonke oonyana bakaSirayeli; nika iingcebiso kunye neengcebiso zakho apha.', 'XHO22'),
(11, 20, 8, 'Lonke uhlanga lwema njengamntu mnye, lwathi, Asiyi kuya ententeni yakhe, asiyi kungena endlwini yakhe.', 'XHO22'),
(11, 20, 9, 'Kaloku ke siya kuyenza le nto ngeGibheha; siya kuyihlasela ekupheleni;', 'XHO22'),
(11, 20, 10, 'Siya kuthabatha amadoda alishumi kwikhulu ezizweni zonke zakwaSirayeli, nekhulu ewakeni, ewakeni, eshumini lamawaka, ukuba aye kubalungiselela abantu ukudla, abaya kukwenza ekufikeni kwabo eGibheha kwilizwe lakwaBhenjamin.', 'XHO22'),
(11, 20, 11, 'Abuthelana umzi onke amadoda akwaSirayeli, ekhonkxiwe njengamntu mnye.', 'XHO22'),
(11, 20, 12, 'Izizwe zakwaSirayeli zathumela amadoda kwizizwe zonke zakwaBhenjamin, zathi, Bububi buni na obu, benziweyo phakathi kwenu?', 'XHO22'),
(11, 20, 13, 'Ke kaloku sinike la madoda, oonyana bakaBheliyali, baseGibheya, ukuze siwabulale, sisuse ububi kwaSirayeli. Abavuma oonyana bakaBhenjamin ukuliphulaphula ilizwi labazalwana babo, oonyana bakaSirayeli.', 'XHO22'),
(11, 20, 14, 'Bahlanganisana oonyana bakaBhenjamin emizini yaseGibheha, ukuba balwe noonyana bakaSirayeli.', 'XHO22'),
(11, 20, 15, 'Ngelo xesha babhaliswa oonyana bakaBhenjamin ezixekweni amadoda angamashumi amabini anesithandathu amawaka arhola ikrele, ngaphandle kwabemi baseGibheya, ababengamakhulu asixhenxe amadoda anyuliweyo.', 'XHO22'),
(11, 20, 16, 'Kolu hlanga lonke kwakukho amakhulu asixhenxe amadoda awayenyulwe ngasekhohlo; wonk’ ubani wayenokugibisela ngamatye kangangendlela enobubanzi beenwele, engakhange aphoswe nakancinane.', 'XHO22'),
(11, 20, 17, 'Ke amadoda akwaSirayeli, ngaphandle kwamaBhenjamin, abebalwe emakhulu mane amawaka arhola ikrele; bonke babengamagorha.', 'XHO22'),
(11, 20, 18, 'Bandula ke oonyana bakaSirayeli bazilungisa baza benyuka baya endlwini yoTHIXO oyinyaniso baza bacela icebiso kuThixo, bathi: “Ngubani na kuthi oya kunyuka kuqala aye kulwa noonyana bakaBhenjamin? Wathi uYehova, Konyuka kuqala uYuda.', 'XHO22'),
(11, 20, 19, 'Bachitha ingomso oonyana bakaSirayeli, bayimisa iintente ngaseGibheha.', 'XHO22'),
(11, 20, 20, 'Aphuma amadoda akwaSirayeli, aya kulwa namaBhenjamin; yaye amadoda akwaSirayeli agxalathelana ukuya kulwa nawo eGibheya.', 'XHO22'),
(11, 20, 21, 'Baphuma oonyana bakaBhenjamin eGibheha, batshabalalisa ngaloo mini amadoda angamashumi amabini anamabini amawaka koonyana bakaSirayeli.', 'XHO22'),
(11, 20, 22, 'Bazomeleza abantu, amadoda akwaSirayeli, baphinda balwa endaweni ababeyilungisile ngomhla wokuqala.', 'XHO22'),
(11, 20, 23, 'Benyuka oonyana bakaSirayeli, balila phambi koYehova, kwada kwahlwa. Babuza kuYehova, bathi, Ndiphínde na ndisondele ekulweni noonyana bakaBhenjamin, umzalwana wam? Wathi uYehova, Nyukani niye kubo.', 'XHO22'),
(11, 20, 24, 'Basondela oonyana bakaSirayeli, baya kubakhawulela oonyana bakaBhenjamin ngomhla wesibini.', 'XHO22'),
(11, 20, 25, 'Aphuma amaBhenjamin eGibheha, aya kubakhawulela ngomhla wesibini, abulala ishumi elinesibhozo lamawaka amadoda ezweni loonyana bakaSirayeli; bonke barhole amakrele abo.', 'XHO22'),
(11, 20, 26, 'Bandula ke bonke oonyana bakaSirayeli nabo bonke abantu benyuka baya endlwini yoTHIXO oyinyaniso balila baza bahlala phantsi phambi koYehova baza ngaloo mini bazila ukutya kwada kwangokuhlwa baza banikela iminikelo etshiswayo neyombulelo phambi koYehova. NKOSI.', 'XHO22'),
(11, 20, 27, 'Babuza oonyana bakaSirayeli kuYehova, ngokuba ityeya yomnqophiso kaThixo ibilapho ngelo xesha.', 'XHO22'),
(11, 20, 28, 'Ngelo xesha uPinehasi unyana kaElazare, unyana ka-Aron, wema ngakuye, wathi, Ndibuye ndiphume ndiye kulwa noonyana bakaBhenjamin, umzalwana wam, ndiyeke, kusini na? Wathi uYehova, Nyuka; ngokuba ngomso ndiya kubanikela esandleni sakho.', 'XHO22'),
(11, 20, 29, 'Yaye uSirayeli waqhubeka ehlala kufuphi neGibheya.', 'XHO22'),
(11, 20, 30, 'Baxabela oonyana bakaSirayeli oonyana bakaBhenjamin ngomhla wesithathu, bayingenela iGibheha njengokwangaphambili.', 'XHO22'),
(11, 20, 31, 'Baphuma oonyana bakaBhenjamin, baya kubakhawulela abantu, bafuduswa kuwo umzi; Baxabela abantu, bababulala njengokwangaphambili, ezindleleni, ezinyukayo ziye endlwini kaThixo, enye isiya eGibheha emaphandleni, bengamadoda angathi amashumi amathathu akwaSirayeli.', 'XHO22'),
(11, 20, 32, 'Bathi oonyana bakaBhenjamin, Bagxothiwe phambi kwethu njengokwamandulo; Bathi oonyana bakaSirayeli, Masibarhuqele emendweni ophuma kuwo umzi.', 'XHO22'),
(11, 20, 33, 'Anduluka onke amadoda akwaSirayeli endaweni yawo, axhobela imfazwe eBhahali-tamare;', 'XHO22'),
(11, 20, 34, 'Kwafika ishumi lamawaka amadoda ahleliweyo kumaSirayeli onke, eGibheha; idabi lalinzima; babengazi bona ukuba ububi bubafikele.', 'XHO22'),
(11, 20, 35, 'uYehova wawoyisa amaBhenjamin phambi koonyana bakaSirayeli, batshabalalisa oonyana bakaSirayeli ngaloo mini amashumi amabini anesihlanu amawaka, anekhulu lamaBhenjamin; bonke barhole amakrele abo.', 'XHO22'),
(11, 20, 36, 'Oonyana bakaBhenjamin babona ukuba boyisiwe, ngenxa yokuba amadoda akwaSirayeli azinikela koonyana bakaBhenjamin, ngenxa yokuba ayekholose ngamaxoki awayehlala kufuphi neGibheya.', 'XHO22'),
(11, 20, 37, 'Amaxoki akhawuleza, aya eGibheha; yaye abavukeli bahlanganisana ndawonye baza bangqinga isixeko siphela ngomsindo wekrele.', 'XHO22'),
(11, 20, 38, 'Kwabakho ke umqondiso phakathi kwamadoda akwaSirayeli namaxoki, abelindele ukukhupha umsi omkhulu esixekweni.', 'XHO22'),
(11, 20, 39, 'Abuyela edabini amadoda akwaSirayeli, asuka amaBhenjamin abulala, abulala amadoda angathi amashumi amathathu kumadoda akwaSirayeli; kuba bathi: Inene, boyisiwe phambi kwethu njengakwimfazwe yokuqala.', 'XHO22'),
(11, 20, 40, 'Kwathi, ekunyukeni kwelangatye esixekweni kwilifu lomsi, babheka emva kwabo oonyana bakaBhenjamin, bawubona umzi unyuka usiya ezulwini.', 'XHO22'),
(11, 20, 41, 'Abuya amadoda akwaSirayeli, othuka amadoda akwaBhenjamin, ngokuba abona ukuba ewelwe bububi.', 'XHO22'),
(11, 20, 42, 'Ngenxa yoko anikela ikhosi kumadoda akwaSirayeli, abehamba ngendlela eya entlango; yawafumana imfazwe; yaye abo babevela ezixekweni babatshabalalisa phakathi kwabo.', 'XHO22'),
(11, 20, 43, 'Bawarhawula amaBhenjamin, bawasukela, bawanyathelela phantsi, besa eGibheha ukuphuma kwelanga.', 'XHO22'),
(11, 20, 44, 'Kwishumi elinesibhozo lamawaka amadoda akwaBhenjamin; bonke babengamadoda anobukroti.', 'XHO22'),
(11, 20, 45, 'Ajika ke, asabela entlango, engxondorheni yeRimon; basukela ukuya kufikelela eGidomo, babulala kuwo amawaka amabini.', 'XHO22'),
(11, 20, 46, 'Bonke abawayo kumaBhenjamin ngaloo mini baba ngamashumi amabini anesihlanu amawaka, ingamadoda arhola ikrele; bonke babengamadoda anobukroti.', 'XHO22'),
(11, 20, 47, 'Ke ajika asabela entlango engxondorheni eRimon engamakhulu amathandathu amadoda, ahlala engxondorheni yeRimon iinyanga zane.', 'XHO22'),
(11, 20, 48, 'Abuya amadoda akwaSirayeli koonyana bakaBhenjamin, ababulala ngohlangothi lwekrele, kwanabantu bemizi ngemizi, njengenkomo, nabo bonke ababambene ngezandla; Bayitshisa yonke imizi beze ngantoni.', 'XHO22'),
(11, 21, 1, 'Afunga amadoda akwaSirayeli eMizpa, esithi, Akukho kuthi uya kunika umBhenjamin intombi yakhe, ibe ngumkakhe.', 'XHO22'),
(11, 21, 2, 'Beza abantu endlwini yoTHIXO oyinyaniso baza bahlala apho kwada kwahlwa phambi koThixo oyinyaniso, baza baphakamisa amazwi abo baza balila ngokukrakra;', 'XHO22'),
(11, 21, 3, 'Wathi: “Yehova, Thixo kaSirayeli, ngani na ukuba namhlanje kufe esinye isizukulwana kwaSirayeli?', 'XHO22'),
(11, 21, 4, 'Ngemini elandelayo abantu bavuka ekuseni baza bakha isibingelelo apho baza benza iminikelo etshiswayo neyombulelo.', 'XHO22'),
(11, 21, 5, 'Bathi oonyana bakaSirayeli, Ngubani na ezizweni zonke zakwaSirayeli onganyukanga aye kuYehova nebandla? Ngokuba bamfungele isifungo esikhulu onganyukanga aye kuYehova eMitspe, besithi, Inene, uya kubulawa.', 'XHO22'),
(11, 21, 6, 'Badana oonyana bakaSirayeli ngenxa kaBhenjamin, abazalwana babo, besithi, Namhla kutshabalale esinye isizwe sakwaSirayeli.', 'XHO22'),
(11, 21, 7, 'Siya kubathini na abo baseleyo nabafazi, ekubeni thina sifungile uYehova ukuba asiyi kuwanika iintombi zethu zibe ngabafazi bawo?', 'XHO22'),
(11, 21, 8, 'Bathi ke, Ngubani na ezizweni zakwaSirayeli onganyukanga aye kuYehova eMizpa? Nanko ke kungekho mntu eminqubeni yaseYabheshe eGiliyadi, emva kwentlanganiso leyo.', 'XHO22'),
(11, 21, 9, 'Babalwa abantu, nanko kungabangakho namnye kubemi baseYabheshe yaseGiliyadi.', 'XHO22'),
(11, 21, 10, 'Ibandla lathumela khona ishumi elinamabini lamawaka amadoda anobukroti, lawawisela umthetho, lisithi, Hambani niye kubulala abemi baseYabheshe yaseGiliyadi, ngohlangothi lwekrele, kwanabafazi nabantwana.', 'XHO22'),
(11, 21, 11, 'Nantsi into eniya kuyenza: uqalekiseni onke amadoda, nabafazi bonke abathe balala nendoda;', 'XHO22'),
(11, 21, 12, 'Bafumana kubemi baseYabheshe yaseGiliyadi, bafumana amakhulu omane iintombi eziziintombi, ekungaziwanga mntu ukuba abe neentlobano zokwazana nandoda; babazisa ekuthinjweni eShilo esezweni lakwaKanan.', 'XHO22'),
(11, 21, 13, 'Lathumela lonke ibandla, laya kuthetha koonyana bakaBhenjamin ababesengxondorheni yeRimon, ukuba bacele uxolo.', 'XHO22'),
(11, 21, 14, 'Abuya ke amaBhenjamin ngelo xesha; babanika abafazi ababebakhulise kubafazi baseYabheshe eGiliyadi, kodwa ababanelanga.', 'XHO22'),
(11, 21, 15, 'Abantu bazisola ngoBhenjamin, kuba uYehova wayezinqamle izizwe zakwaSirayeli.', 'XHO22'),
(11, 21, 16, 'Athi amadoda amakhulu ebandla, Siya kubathini na abaseleyo abafazi, xa etshatyalalisiwe abafazi bakwaBhenjamin?', 'XHO22'),
(11, 21, 17, 'Bathi, Libe lilifa leembacu zakwaBhenjamin, ukuze singatshabalali isizwe kwaSirayeli.', 'XHO22'),
(11, 21, 18, 'Ke thina asiyi kubanika abafazi beentombi zethu, ngokuba bafungile oonyana bakaSirayeli, besithi, Uqalekisiwe onika umBhenjamin umfazi.', 'XHO22'),
(11, 21, 19, 'Babesithi ke, Niyabona, kukho umthendeleko kaYehova iminyaka ngeminyaka eShilo, ngasentla kweBheteli, ngasempumalanga endleleni ephuma eBheteli, iye kwaShekem, nasezantsi kweLebhona.', 'XHO22'),
(11, 21, 20, 'Abawisela oonyana bakaBhenjamin, besithi, Lalani, nikhangele ezidiliyeni;', 'XHO22'),
(11, 21, 21, 'yabonani, xenikweni iintombi zaseShilo ziphuma zingqungqa, ziphuma ezidiliyeni, zithabathele elowo umkakhe ezintombini zaseShilo, niye ezweni lakwaBhenjamin.', 'XHO22'),
(11, 21, 22, 'Xa ooyise nabanakwazo beza kuthi beze kumangalela kuthi, sothi kubo, Senzeleni inceba ngenxa yethu; ngokuba anibanga namfazwe phakathi kwenu, ngokuba anibanikanga bona ixesha; ukuba netyala.', 'XHO22'),
(11, 21, 23, 'Benjenjalo oonyana bakaBhenjamin, bazeka abafazi ngokwenani labo kwiemvumi ababezithimbile; banduluka, babuyela elifeni labo, bakha imizi, bahlala kuyo.', 'XHO22'),
(11, 21, 24, 'Bemka apho oonyana bakaSirayeli ngelo xesha, baya elowo emizalwaneni yakhe, nasemzalwaneni wakowabo, baphuma khona, waya elowo elifeni lakhe.', 'XHO22'),
(11, 21, 25, 'Ngaloo mihla kube kungekho kumkani kwaSirayeli; elowo wenza unothanda.', 'XHO22'),
(12, 1, 1, 'Ngexesha labagwebi kwabakho indlala ehlabathini. Yahamba indoda yaseBhetelehem yakwaYuda, yaya ezweni lakwaMowabhi, yona nomkayo, noonyana bayo ababini.', 'XHO22'),
(12, 1, 2, 'Igama lale ndoda lalinguElimeleki, igama lomkayo lalinguNahomi, amagama oonyana bayo babengooMahalon noKiliyon bakumaEfrata aseBhetelehem yakwaYuda. Bafika ke elizweni lakwaMowabhi, bema khona.', 'XHO22'),
(12, 1, 3, 'Wafa uElimeleki indoda kaNahomi; wasala noonyana bakhe bobabini.', 'XHO22'),
(12, 1, 4, 'Bazeka abafazi kubafazi bakwaMowabhi; igama lomnye belinguOrpa, igama lomnye belinguRute; bahlala khona iminyaka emalunga neshumi.', 'XHO22'),
(12, 1, 5, 'Bafa kananjalo bobabini ooMalon noKiliyon; Wasala loo mfazi noonyana bakhe bobabini nendoda yakhe.', 'XHO22'),
(12, 1, 6, 'Wagqiba ngoomolokazana bakhe ukubuya ezweni lakwaMowabhi; ngenxa yokuba wayevile kwilizwe lakwaMowabhi ukuba uYehova ubavelele abantu bakhe ngokubanika isonka.', 'XHO22'),
(12, 1, 7, 'Waphuma kuloo ndawo abekuyo, enoomolokazana bakhe bobabini; yaye bagqiba kwelokuba babuyele kwilizwe lakwaYuda.', 'XHO22'),
(12, 1, 8, 'Wathi uNahomi koomolokazana bakhe bobabini, Buyani, aye elowo endlwini yonina; uYehova makanenzele okulungileyo nina, njengoko nenze ngako kum nakwabafileyo.', 'XHO22'),
(12, 1, 9, 'Ngamana uYehova anganinika ukuphumla, ngamnye endlwini yendoda yakhe. Waza wabanga; Baliphakamisa ilizwi labo, balila.', 'XHO22'),
(12, 1, 10, 'Bathi kuye, Hayi, siya kubuyela nawe kubantu bakowenu.', 'XHO22'),
(12, 1, 11, 'Wathi uNahomi, Buyani, zintombi zam; nihamba nam na? Basekho oonyana esizalweni sam, ukuba babe ngamadoda enu?', 'XHO22'),
(12, 1, 12, 'Phakani, zintombi zam, nihambe; ngokuba sendikwaluphalele ukuba nendoda. Ukuba ndithe, Ndisenethemba, nokuba ndithe ndaba ngoyindoda ngobu busuku, ndabuya ndazala oonyana;', 'XHO22'),
(12, 1, 13, 'Ngaba ubuya kubalinda bade bakhule? ungathanda ukuba namadoda? hayi, zintombi zam; kuba ndibuhlungu kakhulu ngenxa yenu kuba isandla sikaYehova siphume nxamnye nam.', 'XHO22'),
(12, 1, 14, 'Bawaphakamisa amazwi abo, babuya balila, uOrpa wamanga uninazala; kodwa uRute wamsindisa.', 'XHO22'),
(12, 1, 15, 'Wathi, Yabona, umolokazana wakho ubuyele kubantu bakowabo nakoothixo bakowabo; buyela emva komolokazana wakho.', 'XHO22'),
(12, 1, 16, 'Wathi uRute, Musa akundibongoza ukuba ndikushiye, ndibuye ndikulandele; ngokuba ndiya kuya apho uya khona; apho uhlala khona, ndiya kuhlala khona; Abantu bakowenu baya kuba ngabantu bam, noThixo wakho abe nguThixo wam.', 'XHO22'),
(12, 1, 17, 'Ndiya kufela apho ufela khona, ndingcwatyelwe khona; Uya kwenjenjalo kum uYehova, nangakumbi, logama nje ukufa kusahlula kuni.', 'XHO22'),
(12, 1, 18, 'Wathi akubona ukuba uzimisele ukuhamba naye, wayeka ukuthetha kuye.', 'XHO22'),
(12, 1, 19, 'Bahamba ke bobabini, bada bafika eBhetelehem. Bakufika eBhetelehem, isixeko sonke saxokozela, sisithi, NguNahomi na lo?', 'XHO22'),
(12, 1, 20, 'Wathi kubo, Musani ukuthi ndinguNahomi; yithini ndinguMara, ngokuba undenze ububi kunene uSomandla.', 'XHO22'),
(12, 1, 21, 'Ndaphuma ndizele, yaye uYehova wandibuyisa ndilambatha; Yini na ukuba nithi ndinguNahomi, ekubeni uYehova endingqinele, anditshutshise uSomandla?', 'XHO22'),
(12, 1, 22, 'Wabuya uNahomi enoRute umMowabhikazi, umolokazana wakhe, ekubuyeni kwakhe evela ezweni lakwaMowabhi; bafika eBhetelehem ekuqaleni kokuvunwa kwerhasi.', 'XHO22'),
(12, 2, 1, 'Ke kaloku uNahomi ubenomninawa wendoda yakhe, eyindoda enkulu emzalwaneni kaElimeleki; igama lakhe lalinguBhohazi.', 'XHO22'),
(12, 2, 2, 'Wathi uRute umMowabhikazi kuNahomi, Makhe ndiye ezweni, ndibhula ingqolowa, andiyi kubabalwa emehlweni akhe. Wathi kuye, Hamba, ntombi yam;', 'XHO22'),
(12, 2, 3, 'Wahamba waya kubhula entsimini emva kwabavuni;', 'XHO22'),
(12, 2, 4, 'Kwathi gqi uBhohazi, evela eBhetelehem, wathi kubemi bomzi, uYehova makabe nani. Bathi kuye: uYehova akusikelele!', 'XHO22'),
(12, 2, 5, 'Wathi uBhohazi kwindodana yakhe ephethe abavuni, Ngokabani na lo mntwana?', 'XHO22'),
(12, 2, 6, 'Waphendula umkhonzi ophethe abalusi, wathi, Yintombi engumMowabhikazi, eze noNahomi ezweni lakwaMowabhi.', 'XHO22'),
(12, 2, 7, 'Wathi, Makhe ndibuthe phantsi kwezithungu emva kwabavuni; Weza ke, wahlala kwakusasa kwada kwangoku, ehleli nje kancinane endlwini.', 'XHO22'),
(12, 2, 8, 'Wathi uBhohazi kuRute: “Awuva na, ntombi yam? Musa ukunyuka uye kwelinye ilizwe, okanye ukusuka apha, kodwa hlala apha nosapho lwam.', 'XHO22'),
(12, 2, 9, 'Amehlo akho makalibone ilizwe abalivunayo, uzilandele; Andithi na amakhwenkwe angakuchukumisi? ukuba unxaniwe, yiya equleni, usele kuloo nto bayirholeyo abafana.', 'XHO22'),
(12, 2, 10, 'Wawa ngobuso bakhe, waqubuda, wathi kuye, Ndibone ngani ubabalo emehlweni akho, ukuba undazi, ndingowasemzini?', 'XHO22'),
(12, 2, 11, 'Waphendula uBhohazi, wathi kuye, Ndiyixelelwe yonke into oyenzileyo kunyokozala, emva kokufa kwendoda yakho, nokushiya kwakho uyihlo nonyoko, nelizwe owazalelwa kulo.', 'XHO22'),
(12, 2, 12, 'Wanga uYehova angakubuyekeza izenzo zakho, uzaliseke umvuzo wakho ovela kuYehova uThixo kaSirayeli, okholose ngaye phantsi kwamaphiko akhe.', 'XHO22'),
(12, 2, 13, 'Wathi, Mandibabalwe nguwe, nkosi yam; ngenxa enokuba undithuthuzele, wathetha kakuhle kumkhonzazana wakho, nangona ndingenjengomnye wabakhonzazana bakho.', 'XHO22'),
(12, 2, 14, 'Wathi uBhohazi kuye, Ngexesha lokudla sondela apha, udle isonka, uncinde iviniga ngeqhekeza lakho. Wahlala phantsi ngasebalusi. Kweza kuye ingqolowa eyomileyo, wadla, wahlutha, wemka.', 'XHO22'),
(12, 2, 15, 'Uthe ke akuphakama ukubawa, uBhohazi wabawisela umthetho abakhonzi bakhe, wathi, Makabawe naphakathi kwabaninzi, musani ukumgxeka.', 'XHO22'),
(12, 2, 16, 'Kanjalo, inxenye yabambalwa iwela phezu kwakhe ngabom, imshiya, ukuze abathabathe, bangamkhalimeli.', 'XHO22'),
(12, 2, 17, 'Wabhula entsimini kwada kwahlwa, wayibhula into abeyibhulile, yanga yiefa yerhasi.', 'XHO22'),
(12, 2, 18, 'Wayithabatha ke, wangena esixekweni, wayibona uninazala into awayeyithabathile;', 'XHO22'),
(12, 2, 19, 'Wathi uninazala kuye, Ubusiya phi na namhlanje? kwaye ubusebenza phi? unoyolo lowo unikhathaleleyo. Wazisa umazala wakhe awayesebenza naye, wathi: Igama lendoda endandisebenza nayo namhlanje nguBoase.', 'XHO22'),
(12, 2, 20, 'Wathi uNahomi kumolokazana wakhe: Ngamana angasikelelwa nguYehova, ongayekanga ukubonisa inceba kwabaphilayo nabafileyo. Wathi uNahomi kuye, Le ndoda ikufuphi kuthi, ingumzalwana wethu;', 'XHO22'),
(12, 2, 21, 'Wathi uRute umMowabhikazi, Kananjalo uthe kum, Namathela kumadodana am, alugqibe lonke uvuno lwam;', 'XHO22'),
(12, 2, 22, 'Wathi uNahomi kuRute, umolokazana wakhe, Kulungile, ntombi yam, ukuba uthe waphuma nomthinjana wakhe, ukuba ungakufiki kumhlaba wolunye uhlanga.', 'XHO22'),
(12, 2, 23, 'Wazimanya neentombi zikaBhohazi ukuba abhule, kwada kwagqitywa ukuvunwa kwerhasi nokuvunwa kwengqolowa; wahlala noninazala.', 'XHO22'),
(12, 3, 1, 'Wathi uNahomi uninazala kuye, Ntombi yam, ndingakufuneli na ukuphumla, uphumle?', 'XHO22'),
(12, 3, 2, 'Ke kaloku uBhohazi akangowesizwe sakowethu; ubeneentombi zini na? Yabona, ngobu busuku uzuza irhasi esandeni.', 'XHO22'),
(12, 3, 3, 'Zihlambe, uthambise, yambatha iingubo zakho, uhle; kodwa uze ungazazisi endodeni leyo, ide igqibe ukudla nokusela.', 'XHO22'),
(12, 3, 4, 'Xa ithe yalala, yinyamekele indawo eya kulala kuyo, ungene, ugubungele iinyawo zayo, ulale; wokuxelela ke yena into omawuyenze.', 'XHO22'),
(12, 3, 5, 'Wathi kuye, Yonke into ondixelela yona ndoyenza.', 'XHO22'),
(12, 3, 6, 'Wehla ke, wenza njengoko abemwisele umthetho uninazala.', 'XHO22'),
(12, 3, 7, 'Wakugqiba ukudla nokusela uBhohazi, yachwayitha intliziyo yakhe, walala ekupheleni kwesithungu; wayivula kakuhle imilenze yakhe, wamlalisa.', 'XHO22'),
(12, 3, 8, 'Ezinzulwini zobusuku indoda yoyika, yajika, yabona umfazi elele ezinyaweni zakhe.', 'XHO22'),
(12, 3, 9, 'Wathi, Ungubani na? Wathi, NdinguRute, umkhonzazana wakho; Yandlala ingubo yakho phezu komkhonzazana wakho; ngokuba ezalamana naye.', 'XHO22'),
(12, 3, 10, 'Wathi, Makasikelelwe nguYehova, ntombi yam, ngenxa enokuba wayenzela imfesane yakho ekupheleni, ngaphezu kweyokuqala, ngenxa yokuba ungalandelanga madodana nje, nokuba ngasweleyo nokuba butyebi.', 'XHO22'),
(12, 3, 11, 'Kaloku ke, ntombi yam, musa ukoyika; Ndiya kwenza nantoni na oyicelayo, kuba yonke idolophana yakowethu iyakwazi ukuba ungumfazi olungileyo.', 'XHO22'),
(12, 3, 12, 'Kuyinyaniso oko, ukuba ndingumzalwana wakho; kodwa kukho isalamane esisondeleyo kum.', 'XHO22'),
(12, 3, 13, 'Lala ngobu busuku; kothi kusasa, ukuba uthe wayenzela indlu le, kulungile; makadlale indima yomkrexezi; ke ukuba akathanga anesabelo kuwe, ndiya kubelana nawe ngesabelo ngeNkosi ephilileyo; lala kude kuse.', 'XHO22'),
(12, 3, 14, 'Walala ezinyaweni zakhe kwada kwasa, wavuka engekamazi omnye. Athi: Akufunekanga yaziwe isizathu sokuba lomfazi eze enkundleni.', 'XHO22'),
(12, 3, 15, 'Wabuya wathi, Sisondeze isigqubuthelo osigubungeleyo, sibambe; Wayibamba, walinganisa iiseha ezintandathu zerhasi, wamnika; waza wangena kuwo umzi.', 'XHO22'),
(12, 3, 16, 'Wathi akufika kuninazala, wathi: “Ungubani na, ntombi yam? Wamxelela konke ekwenzileyo kuye loo ndoda.', 'XHO22'),
(12, 3, 17, 'Wathi, Indinike ezi milinganiselo mithandathu yerhasi; ngokuba wathi kum, Musa ukuya kunyokozala wedwa.', 'XHO22'),
(12, 3, 18, 'Wandula ke wathi: “Yithi cwaka, ntombi yam, ude wazi ukuba kuya kuphela njani oku; ngokuba akayi kuphumla loo mntu, ade ayigqibe loo nto namhla.', 'XHO22'),
(12, 4, 1, 'wenyuka uBhohazi, waya esangweni lomzi, wahlala khona; owathi kubo: Oh! Jika, uhlale phantsi apha. Uye wajika wahlala phantsi.', 'XHO22'),
(12, 4, 2, 'Wathabatha amadoda alishumi kumadoda amakhulu omzi, wathi, Hlalani apha. Bahlala phantsi.', 'XHO22'),
(12, 4, 3, 'Wathi kumkhuluwa wakhe, uNahomi obuyele ezweni lakwaMowabhi uthengise ngentsimi kaElimeleki, umzalwana wethu.', 'XHO22'),
(12, 4, 4, 'Bendizimisele ukukwazisa, ndisithi, Wuthenge phambi kwabemiyo naphambi kwamadoda amakhulu esizwe sakowethu. Ukuba ufuna ukuyishiya, yishiye; ke ukuba uthe akwavuma ukusindulula, ndixelele, ukuze ndiyazi; ndikushiye ngasemva. Athi: Ndiya kuyishiya.', 'XHO22'),
(12, 4, 5, 'Wathi uBhohazi, Ngemini oyithengayo intsimi esandleni sikaNahomi, woyithenga nakuRute, umMowabhikazi, umfazi womfi, ukuba uvuselele igama lomfi elifeni lakhe.', 'XHO22'),
(12, 4, 6, 'Wathi umqokeleli, Andiyi kuzithabathela ngokwam, ukuze ndingalonakalisi ilifa lam; Hlukana nam; ngokuba andinako ukuyikhulula ngentlawulelo.', 'XHO22'),
(12, 4, 7, 'Le yayiyindlela kaSirayeli yosindiso nenguquko, eqinisekisa yonke into; umntu wakhulula imbadada yakhe, wayinika ummelwane wakhe; yaye oko kwaba ngumqondiso kwaSirayeli.', 'XHO22'),
(12, 4, 8, 'Wathi umlindi kuBhohazi, Zithengele. Ngoko ukhulula isihlangu sakhe.', 'XHO22'),
(12, 4, 9, 'Wathi uBhohazi kumadoda amakhulu nakubantu bonke, Ningamangqina namhla, ukuba ndiyithengile yonke impahla kaElimeleki, nekaKiliyon yonke, nekaMagalon, esandleni sikaNahomi.', 'XHO22'),
(12, 4, 10, 'Kananjalo uRute, umMowabhikazi, umMowabhikazi, umMowabhikazi, ndamthenga, ukuba abe ngumkam, ukuze ndiphilise igama lomfi elifeni lakhe, lingacinywa igama lomfi phakathi kwabazalwana bakhe kunye noodade isango lendlu yakhe: namangqina namhla.', 'XHO22'),
(12, 4, 11, 'Bonke abantu ababesesangweni namadoda amakhulu bathi, Singamangqina. uYehova makamenze lo mfazi ungenayo endlwini yakho, abe njengoRakeli nanjengoLeya, bona bayakhayo indlu kaSirayeli bobabini; yaye uqhuba kakuhle e-Efrata, yaye udume eBhetelehem.', 'XHO22'),
(12, 4, 12, 'Indlu yakho mayibe njengendlu kaPeretse, awamzalela uTamare kuYuda, nabantwana abo aya kukunika uYehova ngale ntombi;', 'XHO22'),
(12, 4, 13, 'uBhohazi wamzeka uRute waba ngumfazi; Uthe ke ekungeneni kwakhe kuye, uYehova wamthabatha, wazala unyana.', 'XHO22'),
(12, 4, 14, 'Bathi abafazi kuNahomi, Makabongwe uYehova ongakuyekelanga ukuba uswele umntakwabo namhla, ogama lakhe laduma kwaSirayeli.', 'XHO22'),
(12, 4, 15, 'Yena uya kubuxhasa ubomi bakho, akugcine ebudaleni bakho, kuba ezelwe ngumolokazana wakho okuthandayo, lowo ulunge ngakumbi kuwe kunoonyana abasixhenxe.', 'XHO22'),
(12, 4, 16, 'uNahomi wamthabatha umntwana lowo, wambeka esifubeni sakhe, wamanyisa.', 'XHO22'),
(12, 4, 17, 'Bamthiya igama abafazi ababehlala ngakuye, besithi, uNahomi uzele unyana; Bamthiya igama elinguObhedi. Yena nguyise kaYese, uyise kaDavide;', 'XHO22'),
(12, 4, 18, 'Yiyo le imizalwane kaPeretse: nguPeretse ozala uHetseron;', 'XHO22'),
(12, 4, 19, 'uHetseron wazala uRam, uRam wazala uAminadabhi;', 'XHO22'),
(12, 4, 20, 'uAminadabhi wazala uNagshon, uNagshon wazala uSalmon;', 'XHO22'),
(12, 4, 21, 'uSalmon wazala uBhohazi; uBhohazi wazala uObhedi;', 'XHO22'),
(12, 4, 22, 'uObhedi wazala uYese; uYese wazala uDavide;', 'XHO22'),
(13, 1, 1, 'Kwakukho indoda yaseRamatayim-Sofim, kweleentaba lakwaEfrayim, egama belinguElikana, unyana kaYeroham, unyana kaElihu, unyana kaTohu, unyana kaSufi, umEfrati.', 'XHO22'),
(13, 1, 2, 'Yayinabafazi ababini; igama lomnye belinguAna, igama lomnye belinguPenina; yaye uPenina wayenabantwana, kodwa uHana wayengenabantwana.', 'XHO22'),
(13, 1, 3, 'Le ndoda ibiphuma iminyaka ngeminyaka, iye kuthandaza, ibingelele kuYehova wemikhosi eShilo. Babekhona ke oonyana bakaEli bobabini, uHofeni noPinehasi, ababingeleli bakaYehova.', 'XHO22'),
(13, 1, 4, 'Lafika ixesha likaElikana lokubingelela, wanika umkakhe uPenina, noonyana bakhe bonke, neentombi zakhe, izabelo.', 'XHO22'),
(13, 1, 5, 'Ke yena uAna wamnika isabelo esaneleyo; ngokuba ebemthanda uAna, ke uYehova ebesivalile isizalo sakhe.', 'XHO22'),
(13, 1, 6, 'Yaye utshaba lwakhe lwalusenza intlekisa ngaye ukuze amqhathe, ngenxa yokuba uYehova wayesivalile isibeleko sakhe.', 'XHO22'),
(13, 1, 7, 'Wenza loo nto iminyaka emininzi ekunyukeni kwakhe ukuya endlwini kaYehova, wadlala ngaye; ke ngoko, walila akadla.', 'XHO22'),
(13, 1, 8, 'Wathi uElikana indoda yakhe kuye, Hana, uyalila na? Kutheni ungatyi nje? Yini na ukuba ibe mbi intliziyo yakho? Andilungile na kuwe ngaphezu koonyana abalishumi?', 'XHO22'),
(13, 1, 9, 'Wesuka uAna, emveni kokuba ebedlile eShilo, emveni kokuba besele. uEli umbingeleli wayehleli esitulweni esisecaleni kwentsika yetempile kaYehova.', 'XHO22'),
(13, 1, 10, 'Waba buhlungu kakhulu umphefumlo wakhe, wathandaza kuYehova, wasitsho isililo.', 'XHO22'),
(13, 1, 11, 'Wafunga wathi, Yehova wemikhosi, ukuba uthe wazikhumbula iintsizi zam nomkhonzazana wakho, akwamlibala umkhonzazana wakho, wamnika umkhonzazana wakho umntwana oyinkwenkwe: ndomnika uYehova yonke imihla yobomi bam. Hlala endlwini yakho ubomi bakhe, nencakuba ayiyi kufika entlokweni yakhe.', 'XHO22'),
(13, 1, 12, 'Njengoko wayeqhubeka ethandaza phambi koYehova, uEli wawubona umlomo wakhe.', 'XHO22'),
(13, 1, 13, 'uAna wathetha entliziyweni yakhe; yayibebezela imilebe yodwa, ilizwi lakhe lingavakali; ke ngoko, uEli wacinga ukuba unxilile.', 'XHO22'),
(13, 1, 14, 'Wathi uEli kuye, Koda kube nini na unxila? yisuse iwayini yakho kuwe.', 'XHO22'),
(13, 1, 15, 'Waphendula uAna, wathi, Hayi, nkosi yam, ungumfazi omoya unobunzima; Andiselanga wayini nasiselo sinxilisayo, kodwa ndawuphalaza umphefumlo wam phambi koYehova.', 'XHO22'),
(13, 1, 16, 'Musa ukuthabatha umkhonzazana wakho njengentombi yetshijolo;', 'XHO22'),
(13, 1, 17, 'uEli waphendula wathi: “Hamba ngoxolo, yaye uThixo kaSirayeli wokunika imithandazo yakho oyicelileyo kuye.', 'XHO22'),
(13, 1, 18, 'Wathi, Umkhonzazana wakho makababalwe nguwe. Waya ke umfazi lowo, wadla, ababa saba lusizi ubuso bakhe.', 'XHO22'),
(13, 1, 19, 'Bavuka ekuseni kakhulu baza baqubuda phambi koYehova baza babuya bafika ekhayeni labo eRama; uElikana wamthiya igama elinguHana umkakhe; uYehova wamkhumbula.', 'XHO22'),
(13, 1, 20, 'Ngoko lathi lakufika ixesha lokuba uAna akhawule, wazala unyana, waza wamthiya igama elinguSamuweli, esithi: “Kungenxa yokuba ndamcela kuYehova.', 'XHO22'),
(13, 1, 21, 'Yenyuka indoda leyo inguElikana nendlu yayo yonke, iye kubingelela umbingelelo womnyaka, nesifungo sayo kuYehova.', 'XHO22'),
(13, 1, 22, 'Ke yena uAna akanyukanga; ngokuba wathi endodeni yakhe, Andiyi kunyuka ndinyuke, ade azale umntwana;', 'XHO22'),
(13, 1, 23, 'Wathi uElikana indoda yakhe kuye, Yenza ngokubona kwakho; hlala ude umgxothe; NguYehova yedwa olaziyo ilizwi lakhe. Wahlala ke umfazi lowo, wancancisa unyana wakhe, wada wamlumla;', 'XHO22'),
(13, 1, 24, 'Akuba emlumle, wamthabatha wahamba naye, kunye neenkunzi zeenkomo ezintathu ne-efa enye yomgubo nengxowa yewayini, wamzisa endlwini kaYehova eShilo, yaye umntwana usemncinci.', 'XHO22'),
(13, 1, 25, 'Bayixhela inkunzi entsha, bamsa umntwana kuEli.', 'XHO22'),
(13, 1, 26, 'Wathi, Camagu, nkosi yam, lo gama usadla ubomi umphefumlo wakho, nkosi yam, ndingulaa mfazi wayemi nawe apha, ethandaza kuYehova.', 'XHO22'),
(13, 1, 27, 'Bendithandazela lo mntwana; Wandinika ke uYehova umthandazo endawucelayo kuye.', 'XHO22'),
(13, 1, 28, 'Kungenxa yoko le nto bendiboleka uYehova nam; yonke imihla yobomi bakhe, uya kunikezelwa kuYehova. Yaye wanqula uYehova kanye apho.', 'XHO22'),
(13, 2, 1, 'Wathandaza uAna, wathi, Intliziyo yam idlamkile ngoYehova, Uphondo lwam luphakanyisiwe nguYehova. Uvulekile umlomo wam ngazo iintshaba zam; ngokuba ndivuyela usindiso lwakho.', 'XHO22'),
(13, 2, 2, 'Akukho bani unjengoYehova, ngenxa yokuba akukho bani ngaphandle kwakho, yaye akukho liwa linjengoThixo wethu.', 'XHO22'),
(13, 2, 3, 'Musani ukuthetha ngekratshi; makungaphumi kratshi emlonyeni wenu, ngokuba uYehova nguThixo owazi konke, Yena ngumlinganiso wezenzo zakhe.', 'XHO22'),
(13, 2, 4, 'Izaphetha zamagorha ziqhawukile, Abakhubekayo baboshwe bugorha.', 'XHO22'),
(13, 2, 5, 'Abahluthiyo bezithengisa ngesonka; Bathi ke abalambileyo bayeka, bazala abasixhenxe babeludlolo; waza ke yena unabantwana abaninzi waba buthathaka.', 'XHO22'),
(13, 2, 6, 'NguYehova odlisa ubomi; uyehla aye eHadesi aze anyuke.', 'XHO22'),
(13, 2, 7, 'uYehova ulihlwempu, usisityebi yena; Uyazithoba kwaye aphakamise.', 'XHO22'),
(13, 2, 8, 'Uphakamisa ihlwempu eluthulini, aphakamise umngqibi emgqomeni wothuli, ukuze ammise phakathi kweenkosana aze amenze abe yindlalifa yetrone yozuko; kuba iintsika zehlabathi zezikaYehova, yaye umhlaba phezu kwazo.', 'XHO22'),
(13, 2, 9, 'Iinyawo zabakhe benceba uya kuzigcina, Abangendawo badake ebumnyameni; ngokuba akukho mntu uya kweyisa ngamandla.', 'XHO22'),
(13, 2, 10, 'Abachasi bakaYehova baya kutyunyuzwa; ududuma phezu kwabo emazulwini: uYehova uya kugweba phakathi kwehlabathi; uya kumnika amandla ukumkani wakhe, aluphakamise uphondo lomthanjiswa wakhe.', 'XHO22'),
(13, 2, 11, 'Wesuka uElikana, waya endlwini yakhe; Le nkwenkwana yayikhonza uYehova phambi kombingeleli uEli.', 'XHO22'),
(13, 2, 12, 'Oonyana bakaEli babengamatshijolo; babengamazi uYehova.', 'XHO22'),
(13, 2, 13, 'Isiko lababingeleli phakathi kwabantu lalisithi, xa umntu ebingelela umbingelelo, size isicaka sombingeleli, xa inyama iyaphekwa, ephethe incam yenyama ngesandla sakhe;', 'XHO22'),
(13, 2, 14, 'Makaqhotswe ngepani, nokuba kusembizeni, nokuba kusembizeni, nokuba kusembizeni; yonke into esenyameni, umbingeleli wazithabathela yona. Benjenjalo eShilo noonyana bonke bakaSirayeli ababefike khona.', 'XHO22'),
(13, 2, 15, 'Yaye, ngaphambi kokuba kuqhunyiswe amanqatha, wayesiza umlungiseleli wombingeleli aze athi kwindoda ebingelelayo: “Yinike umbingeleli inyama, yosiwe; kuba akayi kusela kudla kuwe, kodwa ekrwada.', 'XHO22'),
(13, 2, 16, 'Ukuba uthe umntu kuye, Mayingabi namanqatha ngoku, wandule ke uzithabathele, njengoko unqwenela ngako umphefumlo wakho; wathi kuye, Hayi; ke wena undinike ngoku; ukuba akunjalo, ndoyithabatha ngamandla.', 'XHO22'),
(13, 2, 17, 'Ngenxa yoko isono saba bafana saba sikhulu kunene phambi koYehova; ngokuba abantu bawuthiyile umnikelo kaYehova;', 'XHO22'),
(13, 2, 18, 'Ke yena uSamuweli wayesengumntwana phambi koYehova, enxibe isambatho esisemagxeni selinen.', 'XHO22'),
(13, 2, 19, 'Ngaphezu koko, amadoda ayemenzela isambatho sendoda, awayesizisa kuye minyaka le xa wenyuka nomyeni wakhe ukuya kubingelela umnyaka.', 'XHO22'),
(13, 2, 20, 'uEli wamsikelela uElikana nomkakhe, wathi, uYehova makakuphe imbewu ngalo mfazi, ngenxa yalowo wabalelwa kuYehova. Bahamba ke baya endlini yabo.', 'XHO22'),
(13, 2, 21, 'uYehova wamvelela uAna, wamitha, wazala oonyana abathathu, neentombi ezimbini. Ke inkwenkwe uSamuweli yakhula phambi koYehova.', 'XHO22'),
(13, 2, 22, 'uEli ubeseleyindoda enkulu, eyiva yonke into ababeyenza oonyana bakhe kumaSirayeli onke; nendlela ababelala ngayo nabafazi ababehlanganisene emnyango wentente yokuhlangana.', 'XHO22'),
(13, 2, 23, 'Wathi kubo, Yini na ukuba nenze izinto ezinjengezi? Ngokuba ndivile ububi bakho kubo bonke aba bantu.', 'XHO22'),
(13, 2, 24, 'Hayi, nyana bam; kuba asinto ilungileyo endiyivayo: nibangela ukuba abantu bakaYehova baphule umthetho.', 'XHO22'),
(13, 2, 25, 'Ukuba ubani uthe wona umntu, umgwebi uya kumgweba; kodwa ukuba umntu wona kuYehova, ngubani na oya kumkhusela? Kodwa abazange baliphulaphule ilizwi lesikhohlakali kuba uYehova wayefuna ukubabulala.', 'XHO22'),
(13, 2, 26, 'Ngoko umntwana uSamuweli waqhubeka ekhula yaye ekholekile kuYehova nakubantu.', 'XHO22'),
(13, 2, 27, 'Wandula ke weza umfo wakwaThixo kuEli waza wathi kuye: “Utsho uYehova ukuthi, ‘Andizange na ndibonise ukuba ndiyindlu kayihlo ngoxa babeseYiputa endlwini kaFaro?', 'XHO22'),
(13, 2, 28, 'Ndimnyule na phakathi kwezizwe zonke zakwaSirayeli ukuba abe ngumbingeleli kum, ukuze enze umbingelelo phezu kwam, anyuse isiqhumiso, anxibe irhonya phambi kobuso bam? Ndiwanikele na onke amadini anyukayo oonyana bakaSirayeli endlwini kayihlo?', 'XHO22'),
(13, 2, 29, 'Kungenxa yoko le nto ulinyathelayo idini lam, nombingelelo wam endinawo endaweni yam yokuhlala; Uzukise oonyana bakho ngaphezu kwam na, ngokuba ubatyebisa ngemibingelelo yabantu bam amaSirayeli?', 'XHO22'),
(13, 2, 30, 'Ngako oko, utsho uYehova, uThixo kaSirayeli, ukuthi, Ndathi, Indlu yakho nendlu kayihlo yohlala ihamba ecaleni kwam; kodwa Yehova, mntundini, makube lee kum. Kuba ondihlonelayo ndiya kumhlonela, ondidelayo udelekile.', 'XHO22'),
(13, 2, 31, 'Uyabona, iyeza imihla, endiya kusinqumla isandla sakho nesandla sendlu kayihlo, kungabikho ngwevu endlwini kayihlo;', 'XHO22'),
(13, 2, 32, 'ndibone ndisekhaya kubo bonke ubutyebi aya kubunika uSirayeli uThixo; kungabikho ngwevu endlwini yakho ngonaphakade.', 'XHO22'),
(13, 2, 33, 'Indoda yakho, endingayi kucilela kumama, iya kuwadla amehlo akho, ibe buhlungu intliziyo yakho;', 'XHO22'),
(13, 2, 34, 'Nguwo lo umqondiso kuwe, oya kubafikela oonyana bakho bobabini, uHofeni noPinehasi; ngenye imini baya kufa bobabini.', 'XHO22'),
(13, 2, 35, 'Ndiya kuzinyulela umbingeleli othembekileyo, oya kwenza njengoko kusentliziyweni yam, nasemphefumlweni wam; ndimakhele indlu eyomeleleyo; yaye uya kusoloko uhamba ecaleni komthanjiswa wam.', 'XHO22'),
(13, 2, 36, 'Kothi, lowo useleyo endlwini yakho, eze azigobe kuye ngenxa yeqhosha lesilivere nesuntswana lesonka, athi, Khawundifake komnye umsebenzi wobubingeleli, ukuze ndidle iqhekeza lesonka.', 'XHO22'),
(13, 3, 1, 'Unyana kaSamuweli wayekhonza uYehova phambi koEli. Laye lilungile ilizwi likaYehova ngaloo mihla; kwakungekho mbono uvulekileyo.', 'XHO22'),
(13, 3, 2, 'Ngelo xesha uEli wayelele endaweni yakhe; amehlo akhe ayeqale ukuba mnyama, engasaboni;', 'XHO22'),
(13, 3, 3, 'Isibane sikaTHIXO oyinyaniso besingekacimi etempileni kaYehova, apho yayikhona ityeya yoTHIXO oyinyaniso, uSamuweli wayelele;', 'XHO22'),
(13, 3, 4, 'uYehova wambiza uSamuweli, wathi, Ndikho.', 'XHO22'),
(13, 3, 5, 'Wabalekela kuEli, wathi, Ndikho; ngokuba endibizile. Waphendula ke yena wathi, Andikubizanga; lala kwakhona. Wahamba wayolala.', 'XHO22'),
(13, 3, 6, 'uYehova waphinda wambiza uSamuweli. Wesuka ke uSamuweli, waya kuEli, wathi, Ndim lo! ngokuba endibizile. Waphendula wathi, Andikubizanga, nyana wam; lala kwakhona.', 'XHO22'),
(13, 3, 7, 'uSamuweli wayengekamazi uYehova, nelizwi likaYehova lalingekatyhilwa kuye.', 'XHO22'),
(13, 3, 8, 'uYehova wambiza uSamuweli okwesithathu. Waphakama waya kuEli, wathi: “Ndim lo! ngokuba endibizile. Wabona uEli ukuba uYehova umbizile umntwana.', 'XHO22'),
(13, 3, 9, 'Wathi uEli kuSamuweli, Lala, uthi, wakubiza, uthi, Thetha, Yehova; ngokuba uyeva umkhonzi wakho. uSamuweli waya kulala endaweni yakhe.', 'XHO22'),
(13, 3, 10, 'Weza uYehova, wema, wabiza njengangaphambili, wathi, Samuweli, Samuweli! Waphendula uSamuweli, wathi, Thetha; ngokuba uyeva umkhonzi wakho.', 'XHO22'),
(13, 3, 11, 'Wathi uYehova kuSamuweli, Uyabona, ndiya kwenza into kwaSirayeli, eya kuviwa ngabo bonke abayivayo.', 'XHO22'),
(13, 3, 12, 'Ngaloo mini ndiya kuyenza kuEli yonke into endayithethayo ngendlu yakhe; xa ndiqala, ndiya kugqiba kwakhona.', 'XHO22'),
(13, 3, 13, 'Ndathi kuye, Ndiya kuyigweba indlu yakhe kude kuse ephakadeni, ngenxa yobubi ababaziyo; ngokuba babedlala oonyana bakhe, akabanqanda.', 'XHO22'),
(13, 3, 14, 'Ngoko ndabafungela abantu bendlu kaEli, ndathi, Abuyi kuhlanjululwa ububi bendlu kaEli, ngamadini, nangedini elinyukayo, kuphele.', 'XHO22'),
(13, 3, 15, 'Walala uSamuweli kwada kwasa, wazivula ke iingcango zendlu kaYehova. uSamuweli woyika ukubonisa ubuso bakhe kuEli.', 'XHO22'),
(13, 3, 16, 'uEli wambiza uSamuweli, wathi, Samuweli, nyana wam! Waphendula ke yena wathi, Ndilapha.', 'XHO22'),
(13, 3, 17, 'Wathi, Utheni na kuwe uYehova? Ndiyakubongoza, sukundifihlela; uThixo uya kwenza oku kuwe, aqokele ukwenjenje, ukuba uthe wandifihlela nto kuyo yonke into ondixelele yona.', 'XHO22'),
(13, 3, 18, 'uSamuweli wamxelela yonke into, akamfihlela nto. Wathi yena, NguYehova; Makenze into ayibona ifanelekile.', 'XHO22'),
(13, 3, 19, 'Wakhula uSamuweli, uYehova waba naye, akwawa phantsi nalinye ilizwi emazwini akhe emhlabeni.', 'XHO22'),
(13, 3, 20, 'Yaye onke amaSirayeli ukususela kwaDan kuse eBher-shebha azi ukuba uSamuweli umiselwe ukuba abe ngumprofeti kaYehova.', 'XHO22'),
(13, 3, 21, 'Waphinda uYehova wabonakala eShilo, ngenxa yokuba uYehova wazityhila kuSamuweli eShilo ngelizwi likaYehova.', 'XHO22'),
(13, 4, 1, 'Ilizwi likaSamuweli lafika kumaSirayeli onke. Aphuma amaSirayeli, aya kulwa namaFilisti, amisa iintente e-Ebhenezere yaye amaFilisti ayingqinga iAfeki.', 'XHO22'),
(13, 4, 2, 'AmaFilisti aqalisa ukuba buthathaka nxamnye namaSirayeli, yaye xa ayehlangene edabini, amaSirayeli oyiswa phambi kwamaFilisti; yaye babulala elizweni malunga namawaka amane amagorha.', 'XHO22'),
(13, 4, 3, 'Afika amadoda eminqubeni, athi amadoda amakhulu akwaSirayeli, Yini na ukuba uYehova esoyise namhlanje phambi kwamaFilisti? Masihambe siye kuthabatha ityeya yomnqophiso kaYehova eShilo, ukuze isihlangule esandleni seentshaba zethu, ukuba ithe yangena phakathi kwethu.', 'XHO22'),
(13, 4, 4, 'Bathumela abantu eShilo, ukuba kuyithabathe ityeya yomnqophiso kaYehova wemikhosi ohleli phakathi kweekerubhi; Oonyana ababini bakaEli, uHofeni noFinehasi, babelapho netyeya yomnqophiso kaThixo.', 'XHO22'),
(13, 4, 5, 'Yathi yakufika ityeya yomnqophiso kaYehova enkampini, akhwaza kakhulu onke amaSirayeli, kangangokuba umhlaba waphinda wanyikima.', 'XHO22'),
(13, 4, 6, 'Akusiva amaFilisti eso sandi sokukhwaza, athi: “Siyintoni na esi sandi sokukhwaza kwiinkampu zamaHebhere? Beva ke ukuba ityeya kaYehova ifikile enkampini.', 'XHO22'),
(13, 4, 7, 'Oyika amaFilisti, athi, uThixo ungenile emkhosini. Bathi ke bona: Yeha ke thina! ngokuba ibingekho ngaphambili into enje.', 'XHO22'),
(13, 4, 8, 'Yeha ke thina! Ngubani na oya kusihlangula esandleni saba thixo banamandla? ikwangoothixo ababetha amaYiputa ngezigwebo zonke entlango.', 'XHO22'),
(13, 4, 9, 'Zinikeni amandla nize nime njengamadoda, nina maFilisti, ukuze ningawakhonzi amaHebhere njengoko ayekhonza kuni;', 'XHO22'),
(13, 4, 10, 'Alwa ke amaFilisti, oyiswa amaSirayeli, abalekela elowo ententeni yakhe; kwabakho ukubulala kakhulu; ngokuba amashumi amathathu amawaka amaSirayeli abulawa ngeenyawo.', 'XHO22'),
(13, 4, 11, 'Kwathinjwa ke ityeya kaThixo; babulawa oonyana ababini bakaEli, uHofeni noPinehasi.', 'XHO22'),
(13, 4, 12, 'Kwabaleka indoda yakwaBhenjamin, ivela emkhosini, yeza eShilo kwangaloo mini, iingubo zayo zikrazukile, inomhlaba entloko.', 'XHO22'),
(13, 4, 13, 'Ekufikeni kwakhe, wabona uEli ehleli esitulweni ecaleni kwendlela waza wakhangela, kuba intliziyo yakhe yayigubha ngenxa yetyeya yoTHIXO oyinyaniso. Ukufika kwale ndoda kuloo dolophana, yashumayela, yaye yonke loo dolophana yalila.', 'XHO22'),
(13, 4, 14, 'Waliva uEli izwi lokuduma, wathi, Liyintoni na elo lizwi lesiphithiphithi? Yakhawuleza yangena indoda leyo, yamxelela uEli.', 'XHO22'),
(13, 4, 15, 'uEli ebeminyaka imashumi asibhozo anamnye ekhona; amehlo akhe ayemnyama kangangokuba wayengaboni.', 'XHO22'),
(13, 4, 16, 'Yathi indoda leyo kuEli, Ndingulo uvela emkhosini mna, ndibaleke ke mna namhla. Anathi: Kwenzeke ntoni apho nyana wam?', 'XHO22'),
(13, 4, 17, 'Waphendula umthunywa lowo, wathi, Asaba oonyana bakaSirayeli kumaFilisti, kwabakho uxabelo olukhulu ebantwini, bafa oonyana bakho bobabini, uHofeni noPinehasi, yathinjwa ke ityeya kaThixo.', 'XHO22'),
(13, 4, 18, 'Wathi akuthetha ngetyeya yoThixo oyinyaniso, waziphosa esihlalweni esisecaleni kwesango, waza wayaphula intamo, wafa, ngenxa yokuba wayeyindoda endala nenzima. Wawakhokela amaSirayeli iminyaka emashumi mane.', 'XHO22'),
(13, 4, 19, 'Ke kaloku umolokazana wakhe, umkaPinehasi, wayemithi, seleza kuzala; Wathi akuva ukuba ityeya kaThixo ithinjiwe, nokuba uninazala nomyeni wakhe bafile, waguqa, wazala ngokuba intlungu yakhe yamongamela.', 'XHO22'),
(13, 4, 20, 'Ekufeni kwakhe, abafazi ababemi ngakuye bathi kuye, Musa ukoyika; ngokuba uzele unyana. Kodwa akazange aphendule, yaye akazange ayicinge loo nto.', 'XHO22'),
(13, 4, 21, 'Wandula ke wabiza inkwenkwe enguIkabhodi, wathi: “Uzuko lumkile kwaSirayeli, ngenxa yokuba ityeya yoTHIXO oyinyaniso ithinjiwe ngenxa kayisezala nomyeni wakhe.', 'XHO22'),
(13, 4, 22, 'Waza wathi: “Uzuko lumkile kwaSirayeli, ngenxa yokuba ityeya yoTHIXO oyinyaniso ithinjiwe.”', 'XHO22'),
(13, 5, 1, 'AmaFilisti ayithabatha ityeya kaThixo, ayithabatha e-Ebhenezere, ayisa eAshdode.', 'XHO22'),
(13, 5, 2, 'Xa amaFilisti ayithabatha ityeya yoThixo oyinyaniso, ayizisa endlwini kaDagon, ayibeka phambi kukaDagon.', 'XHO22'),
(13, 5, 3, 'Banduluka kusasa eAshdode, babona uDagon ewile ngobuso phambi kwetyeya kaYehova. Bamthabatha uDagon, bambuyisela endaweni yakhe.', 'XHO22'),
(13, 5, 4, 'Bavuka kusasa, nanko uDagon ewile ngobuso phambi kwetyeya kaYehova; uDagon wayenqunyulwe intloko nezandla zakhe zozibini ngasemnyango; yasala isiphunzi sikaDagon yedwa.', 'XHO22'),
(13, 5, 5, 'Kungenxa yoko le nto ababingeleli bakaDagon, nabo bonke abangena endlwini kaDagon, benganyatheli emnyango kaDagon Ashdode, unanamhla.', 'XHO22'),
(13, 5, 6, 'Saba nzima isandla sikaYehova eAshdode, wabatshabalalisa, wababulala ngekrele, eyiAshdode ke leyo, nelizwe lakhona.', 'XHO22'),
(13, 5, 7, 'Akubona amadoda aseAshdode ukuba kunjalo, athi, Ayiyi kuhlala kuthi ityeya yoThixo kaSirayeli, ngokuba isandla sakhe sinzima phezu kwethu, nakuDagon uthixo wethu.', 'XHO22'),
(13, 5, 8, 'Bathumela ukuba babize zonke iinkosana zamaFilisti, bathi, Siyithini na ityeya yoThixo kaSirayeli? Bathi, Ityeya yoThixo kaSirayeli mayisiwe eGati. Bayingenisa khona ityeya yoThixo kaSirayeli.', 'XHO22'),
(13, 5, 9, 'Yaye kwathi, bakuba siwungqingile, isandla sikaYehova saba nxamnye neso sixeko ngentshabalalo enkulu kakhulu; amacandelo.', 'XHO22'),
(13, 5, 10, 'Kungenxa yoko le nto bayithumela ityeya yoTHIXO oyinyaniso e-Ekron. Ithe yakufika ityeya kaThixo e-Ekron, baduma abemi base-Ekron, bathi, Bazizisile kuthi ityeya yoThixo kaSirayeli, ukuze isibulale thina nabantu bethu.', 'XHO22'),
(13, 5, 11, 'Bandula ke bathumela ukuba bahlanganise zonke iinkosana zamaFilisti baza bathi: “Ityeya yoTHIXO kaSirayeli mayibuyele endaweni yayo, ukuze ingasibulali thina nabantu bakowethu; kuba umzi uphela wawubhuqa; Saye sinzima kunene isandla sikaThixo apho.', 'XHO22'),
(13, 5, 12, 'Abethwa amadoda angafanga, kwenyuka kwaya ezulwini ukukhala komzi.', 'XHO22'),
(13, 6, 1, 'Yahlala ke ityeya kaYehova ezweni lamaFilisti iinyanga zasixhenxe.', 'XHO22'),
(13, 6, 2, 'AmaFilisti ababiza ababingeleli nezazi, athi, Siyithini na ityeya kaYehova? Sixelele ngayo, siyise endaweni yayo.', 'XHO22'),
(13, 6, 3, 'Bathi, Ekuyibuyiseni kwenu ityeya yoThixo kaSirayeli, musani ukuyigodusa iyodwa; kodwa mbuyiseni idini letyala; uya kwandula ke uphiliswe, wazi ukuba kutheni singasuswanga isandla sakhe kuwe.', 'XHO22'),
(13, 6, 4, 'Athi, Liyintoni na idini letyala, silibuyisele kuye? Aphendula athi, Zintlanu iinyosi zegolide, neempuku zantlanu zegolide, ngokwenani lezikhulu zamaFilisti;', 'XHO22'),
(13, 6, 5, 'Ize nenze imifanekiso yamatye okusila, nemifanekiso yeempuku zenu ezilonakalisayo ihlabathi; Mzukiseni uThixo kaSirayeli; mhlawumbi wosenza lula isandla sakhe phezu kwenu, naphezu koothixo benu, naphezu kwelizwe lenu.', 'XHO22'),
(13, 6, 6, 'Kutheni na ke nizenza lukhuni iintliziyo zenu njengoko azenza lukhuni iintliziyo zawo amaYiputa noFaro? Xa wayesebenza ngokungummangaliso phakathi kwabo, ngaba abazange bavumele abantu ukuba bafuduke baze bafuduke?', 'XHO22'),
(13, 6, 7, 'Yenzani inqwelo entsha ngoko, nithabathe iimazi ezimbini ezintsha ezanyisayo, ezingekabotshwa, nizibophe enqwelweni, niwabuyisele ekhaya amathole azo.', 'XHO22'),
(13, 6, 8, 'Yithabatheni ityeya kaYehova, niyibeke enqwelweni; neempahla zegolide enizibuyisela kuye, zibe lidini letyala, ebhokisini elisecaleni kwayo; Yekela ihambe.', 'XHO22'),
(13, 6, 9, 'Yabonani, ukuba ithe yenyuka ngendlela yomda wakhe, yaya eBhete-shemeshe, kulapho asenzele khona obu bubi bukhulu; ukuba akunjalo, sazi ukuba ibingesiso isandla sakhe esibethileyo; yayilithuba esenzeka kuthi.', 'XHO22'),
(13, 6, 10, 'Enjenjalo amadoda; wathabatha iinkomo ezimbini ezanyisayo, wazibopha enqwelweni, wawavalela endlwini amathole azo.', 'XHO22'),
(13, 6, 11, 'Bayingenisa ityeya kaYehova enqwelweni, netyeya ineempuku zegolide, nemifanekiso yeempondo zazo ezibukhali.', 'XHO22'),
(13, 6, 12, 'Iinkomo zahamba ngokuthe ngqo ngendlela yaseBhete-shemeshe, zahamba ngendlela, zisidla, aziphambukanga ukuya ekunene nasekhohlo; zawasukela abathetheli bamaFilisti, besa emdeni waseBhete-shemeshe.', 'XHO22'),
(13, 6, 13, 'Abantu baseBhete-shemeshe babevuna ingqolowa yabo entlanjeni, yaye baphakamisa amehlo abo, bayibona ityeya, bakuvuyela ukuyibona.', 'XHO22'),
(13, 6, 14, 'Inqwelo yafika entsimini kaYoshuwa, eMobhete-shemeta, yema apho kwakukho ilitye elikhulu; Bayicanda iinkuni inqwelo, iinkomo zenza idini elinyukayo kuYehova.', 'XHO22'),
(13, 6, 15, 'Bayithabatha abaLevi ityeya kaYehova, netyeya ebinayo izihombo zegolide, bayibeka emthini omkhulu; abantu baseBhete-shemeshe banyusa amadini anyukayo nemibingelelo kuYehova ngaloo mini.', 'XHO22'),
(13, 6, 16, 'Zakubona oko iinkosana ezihlanu zamaFilisti, zabuyela e-Ekron kwangaloo mini.', 'XHO22'),
(13, 6, 17, 'Ngawo la amarhengqa egolide, awawabuyiselayo amaFilisti kuYehova njengedini letyala; elinye libe leleAshdode, elinye libe lelikaGaza, libe lelaseAshkelon elinye, libe lelaseGati, elinye le-Ekron;', 'XHO22'),
(13, 6, 18, 'neempuku zegolide ngokwenani lemizi yonke yamaFilisti, yezikhulu ezihlanu, ezithabathela emizini enqatyisiweyo, nasemizaneni esemaphandleni, zesa etyeni elikhulu lika-Abheli, apho bayibeka khona ityeya kaYehova kuyo. NKOSI elo litye liseleyo unanamhla entsimini kaYoshuwa waseBhete-shemeshe.', 'XHO22'),
(13, 6, 19, 'Wababulala abantu baseBhete-shemeshe, ngenxa yokuba babesiya engxoweni kaYehova, ewe, wabulala amadoda angamashumi amathandathu anesihlanu; ukubulala.', 'XHO22'),
(13, 6, 20, 'Athi amadoda aseBhete-shemeshe, Ngubani na onokuma phambi koYehova uThixo oyiNgcwele? Wonyuka aye kubani na, emke kuthi?', 'XHO22'),
(13, 6, 21, 'Bandula ke bathumela abathunywa kubemi baseKiriyati-yarim ukuya kuthi: “AmaFilisti ayibuyisile ityeya kaYehova; yihla, uzithabathele kuwe.', 'XHO22'),
(13, 7, 1, 'Eza amadoda aseKiriyati-yeharim, ayithabatha ityeya kaYehova, ayisa endlwini ka-Abhinadabhi endulini, anyula uElazare, unyana wakhe, ukuba ayigcine ityeya kaYehova.', 'XHO22'),
(13, 7, 2, 'Ithe iseKiriyati-yeharim ityeya, zanda iintsuku; kuba yaba yiminyaka engamashumi amabini, yaye yonke indlu kaSirayeli yamzilela uYehova.', 'XHO22'),
(13, 7, 3, 'Wathetha uSamuweli kwindlu yonke kaSirayeli, wathi, Xa nithe nabuyela kuYehova ngeentliziyo zenu zonke, basuseni phakathi kwenu oothixo bolunye uhlanga, noAshtaroti, nizimise iintliziyo zenu kuYehova, nikhonze yena yedwa uya kunihlangula esandleni samaFilisti.', 'XHO22'),
(13, 7, 4, 'Bamlahla ke oonyana bakaSirayeli ooBhahali noAshtaroti, bakhonza uYehova yedwa.', 'XHO22'),
(13, 7, 5, 'Wathi uSamuweli, Bahlanganiseleni eMizpa bonke oonyana bakaSirayeli, ndinithandazele kuYehova.', 'XHO22'),
(13, 7, 6, 'Bahlanganisana eMitspe, bakha amanzi, bawagalela phambi koYehova, bazila ukudla ngaloo mini, bathi, Sonile kuYehova. uSamuweli wabawisela umthetho oonyana bakaSirayeli, ukuba beze eMitiva.', 'XHO22'),
(13, 7, 7, 'Xa amaFilisti evayo ukuba oonyana bakaSirayeli baqokelelene ndawonye eMizpa, iinkosana zamaFilisti zamhlasela uSirayeli. Beva oonyana bakaSirayeli, boyika amaFilisti.', 'XHO22'),
(13, 7, 8, 'Bathi oonyana bakaSirayeli kuSamuweli, Musa ukuyeka ukunqula uYehova uThixo wethu, ukuba asigcine, asihlangule esandleni samaFilisti.', 'XHO22'),
(13, 7, 9, 'uSamuweli wathabatha itakane elanyayo, wenza ngalo umbingelelo otshiswayo ngokupheleleyo kuYehova, waza uSamuweli wabiza kuYehova ngenxa yoSirayeli; wamva uYehova.', 'XHO22'),
(13, 7, 10, 'Kwathi, xa uSamuweli wayezisa idini elinyukayo, amaFilisti asondela ukuba alwe noonyana bakaSirayeli; boyiswa ngamaSirayeli.', 'XHO22'),
(13, 7, 11, 'Amadoda akwaSirayeli aphuma eMizpa, awasukela amaFilisti, awabulala, ada aya kufika eBhete-kare.', 'XHO22'),
(13, 7, 12, 'uSamuweli wathabatha ilitye, walibeka phakathi kweMitspe neSene, wathi igama layo yiEbhenezere, wathi, uYehova usincedile unangoku.', 'XHO22'),
(13, 7, 13, 'Oyiswa ke amaFilisti, akaba saphinda eze ezweni lakwaSirayeli; yaye isandla sikaYehova saba nxamnye namaFilisti yonke imihla kaSamuweli.', 'XHO22'),
(13, 7, 14, 'Imizi abeyithimbile amaFilisti kumaSirayeli yabuyela kwaSirayeli, ithabathela e-Ekron yesa eGati; yaye ilizwe lakhe lamkhulula uSirayeli esandleni samaFilisti. Kwabakho uxolo phakathi koonyana bakaSirayeli nama-Amori.', 'XHO22'),
(13, 7, 15, 'uSamuweli waqhubeka ekhokela amaSirayeli yonke imihla yobomi bakhe.', 'XHO22'),
(13, 7, 16, 'Minyaka le wayesiya eBheteli naseGiligali naseMizpa ukuze ayalele amaSirayeli kuzo zonke ezo ndawo.', 'XHO22'),
(13, 7, 17, 'Waye ebuyela eRama; ngokuba indlu yakhe ibikhona; wabawisela umthetho oonyana bakaSirayeli khona; wamakhela khona isibingelelo uYehova.', 'XHO22'),
(13, 8, 1, 'Akuba mdala uSamuweli wabamisa oonyana bakhe baba ngabagwebi kwaSirayeli.', 'XHO22'),
(13, 8, 2, 'Igama lonyana wakhe wamazibulo lalinguYoweli; igama lowesibini belinguAbhiya; bengabagwebi eBher-shebha.', 'XHO22'),
(13, 8, 3, 'Oonyana bakhe abahambanga ngeendlela zakhe, kodwa baphambuka emva kwengeniso baza bamkela izinyobo yaye basigqwetha isigwebo.', 'XHO22'),
(13, 8, 4, 'Ahlanganisana ke onke amadoda amakhulu akwaSirayeli, eza kuSamuweli eRama.', 'XHO22'),
(13, 8, 5, 'Wathi kuye, Yabona, wena waluphele, oonyana bakho abahambi ngeendlela zakho; simisele ngoko ukumkani wokusilawula, njengeentlanga zonke.', 'XHO22'),
(13, 8, 6, 'Kodwa loo nto yaba imbi emehlweni kaSamuweli, xa bathi, Sinike ukumkani ukuba asilawule. uSamuweli wathandaza kuYehova.', 'XHO22'),
(13, 8, 7, 'Wathi uYehova kuSamuweli, Liphulaphule izwi labantu entweni yonke abaya kuyithetha kuwe; ngokuba ingenguwe lo ubagatyileyo;', 'XHO22'),
(13, 8, 8, 'Ngokwemisebenzi yonke abayenzayo ukususela kwimini endabakhupha ngayo eYiputa de kube yile mini yokundishiya kwabo baza bakhonza abanye oothixo, benza okufanayo nakuwe.', 'XHO22'),
(13, 8, 9, 'Liphulaphule ke ngoko izwi labo; bakhalimele, balazi igunya lokumkani oya kubalawula.', 'XHO22'),
(13, 8, 10, 'uSamuweli wabaxelela abantu ababecela ukumkani kuye onke amazwi kaYehova.', 'XHO22'),
(13, 8, 11, 'Wahlabela mgama wathi: “Nantsi indlela yokumkani oya kunilawula: Wobathabatha oonyana benu, abanikele kuye iinqwelo zakhe zokulwa, nabamahashe bakhe; yaye abanye baya kusaba phambi kweenqwelo zakhe.', 'XHO22'),
(13, 8, 12, 'Wommisa abe yingqwayi-ngqwayi yewaka, abe ngumthetheli wamashumi amahlanu; yaye uya kubamisela ukuba bamenzakalisa, baqhekeze isikrweqe sakhe baze benze izixhobo zakhe zemfazwe neenqwelo zakhe zokulwa.', 'XHO22'),
(13, 8, 13, 'Iintombi zenu wozithabatha zibe ngabapheki, nabapheki, nabapheki;', 'XHO22'),
(13, 8, 14, 'Amasimi enu wowathabatha, nezidiliya zenu, neminquma yenu emihle, azinike abakhonzi bakhe;', 'XHO22'),
(13, 8, 15, 'Wothabatha isishumi sembewu yenu, nesezidiliya zenu, azinike abakhonzi bakhe nabakhonzi bakhe;', 'XHO22'),
(13, 8, 16, 'Izicaka zenu nezicakakazi zenu uya kubathabatha, amadodana enu amahle, namaesile enu, enze umsebenzi wakhe ngawo.', 'XHO22'),
(13, 8, 17, 'Uya kuthabatha isishumi sempahla yenu emfutshane, nibe ngabakhonzi bakhe;', 'XHO22'),
(13, 8, 18, 'Niya kukhala ngaloo mini ngenxa yokumkani wenu enizinyuleleyo; yaye uYehova akayi kuniphulaphula ngaloo mini.', 'XHO22'),
(13, 8, 19, 'Abavuma abantu ukuliphulaphula ilizwi likaSamuweli; baphendula bathi, Hayi! ke siya kuba nokumkani osilawulayo;', 'XHO22'),
(13, 8, 20, 'ukuze sibe njengeentlanga zonke; nokuba ukumkani wethu asigwebe, asikhokele, kwaye alwe amadabi ethu.', 'XHO22'),
(13, 8, 21, 'Waweva uSamuweli onke amazwi abantu, waweva ezindlebeni zikaYehova.', 'XHO22'),
(13, 8, 22, 'Wathi uYehova kuSamuweli, Liphulaphule izwi labo, ubenze babe ngukumkani. Wathi uSamuweli kumadoda akwaSirayeli, Maye elowo emzini wakowabo.', 'XHO22'),
(13, 9, 1, 'Kwakukho ndoda yakwaBhenjamin, igama layo belinguKishe, unyana ka-Abhiyeli, unyana kaSerore, unyana kaBhekorati, unyana ka-Afiya, umBhenjamin, igorha.', 'XHO22'),
(13, 9, 2, 'Yayinonyana ogama linguSawule, umfo omtsha, omhle, omhle; bekungekho ndoda ilungileyo phakathi koonyana bakaSirayeli kunaye; ethabathele emagxeni enyusa, wayemde kunabo bonke abantu.', 'XHO22'),
(13, 9, 3, 'Zafa iiesile zikaKishe uyise kaSawule. Wathi uKishe kuSawule unyana wakhe, Thabatha omnye wakubakhonzi, uye kuzifuna iimazi zamaesile.', 'XHO22'),
(13, 9, 4, 'Wahamba waya kweleentaba lakwaEfrayim, wacanda ilizwe laseSalisa, abawabona; balityhutyha ilizwe laseSalem, nalapho zazingekho; yaye walityhutyha ilizwe loonyana bakaBhenjamin, kodwa abazange bababone.', 'XHO22'),
(13, 9, 5, 'Bakufika ezweni laseSufi, uSawule wathi kumfana obenaye, Hamba sibuye; latata akayeki ukuhoya iidonki usicingele.', 'XHO22'),
(13, 9, 6, 'Wathi ke yena kuye, Uyabona, kulo mzi kukho umntu kaThixo; yonke into ayithethayo iyenzeka; masiye khona ngoku; mhlawumbi angasibonisa indlela emasihambe ngayo.', 'XHO22'),
(13, 9, 7, 'Wathi uSawule kumfana lowo, Yabona, sihamba nje, siya kumphathela ntoni na? ngokuba isonka esisezimbizeni zethu siphelile; akukho sathabatha singasisela umfo wakwaThixo into esinayo.', 'XHO22'),
(13, 9, 8, 'Waphinda umfana wamphendula uSawule, wathi, Uyabona, ndinesahlulo sesine seshekele yesilivere; Le nto ndiza kuyinika umfo wakwaThixo, asixelele indlela yethu.', 'XHO22'),
(13, 9, 9, 'Ngemihla yamandulo kwaSirayeli, xa umntu esiya kubuza kuThixo, ubesithi, Yiza siye kumatola; ngokuba lowo kuthiwa ungumprofeti namhla, ubethiwa yimboni ngenxa engaphambili.', 'XHO22'),
(13, 9, 10, 'Wathi uSawule kumfana lowo, Ulungisile ukuthetha; Yiza sihambe, baya kuloo mzi abekhona umfo wakwaThixo.', 'XHO22'),
(13, 9, 11, 'Enyuka esiya emzini, enyuka esiya entabeni, afumana iintombi zisiya kukha amanzi, athi kuwo, Ikho na imboni leyo?', 'XHO22'),
(13, 9, 12, 'Baphendula bathi kubo, Ukhona; niyabona, uphambi kwenu; khawulezani, ngokuba ifikile namhlanje kulo mzi; kuba namhlanje kukho umbingelelo wasesidlangalaleni endaweni ephakamileyo;', 'XHO22'),
(13, 9, 13, 'ekungeneni kwenu esixekweni, noyifumana kwaoko ingekanyuki iye kudla endaweni ephakamileyo; ngokuba abayi kudla abantu, ingekafiki, wawusikelela umbingelelo; baza emva koko badla abo babemenyiwe. Ngoko sukuma ngoku; ngokuba niya kumfumana ngeli xesha.', 'XHO22'),
(13, 9, 14, 'Benyuka baya kuwo umzi, bakungena kuwo umzi, waphuma uSamuweli wabakhawulela, enyuka aye esigangeni.', 'XHO22'),
(13, 9, 15, 'Wathetha uYehova kuSamuweli endlebeni yakhe, kwangemini yangaphambi kokuza kukaSawule, esithi,', 'XHO22'),
(13, 9, 16, 'Ngeli xesha ngomso, ndiya kuthumela kuwe indoda yasezweni lakwaBhenjamin, iyithambise, ibe yinganga yabantu bam amaSirayeli, ibasindise abantu bam esandleni samaFilisti; kuba ndiyibonile.', 'XHO22'),
(13, 9, 17, 'uSamuweli wambona uSawule, wathi uYehova kuye, Nantsi le ndoda bendithethe ngayo kuwe; Uya kubalawula abantu bam.', 'XHO22'),
(13, 9, 18, 'Waya uSawule kuSamuweli esangweni, wathi, Ndixelele, iphi na indlu yemilingo?', 'XHO22'),
(13, 9, 19, 'uSamuweli wamphendula uSawule, wathi, Ndiyimboni; Mtsalele phambi kwam enyangweni; ngokuba namhlanje uya kudla nam, ndikukhulule ngomso, ndikuxelele konke okusentliziyweni yakho.', 'XHO22'),
(13, 9, 20, 'Ke iimazi zamaesile esekuntsuku ntathu namhla zikulahlekile, uze ungakhathali kuzo; ngokuba bafunyenwe. Ziphezu kabani na zonke izinto ezinqwenelekayo zakwaSirayeli? Ayikho kuwe nakuyo yonke indawo endlwini yakho?', 'XHO22'),
(13, 9, 21, 'Waphendula uSawule, wathi, AndingumBhenjamin yini na, oyena mncinane kwizizwe zakwaSirayeli? nesizukulwana sam esincinane kwizizukulwana zonke zesizwe sakwaBhenjamin? Khona kutheni uthetha nam ngolohlobo?', 'XHO22'),
(13, 9, 22, 'uSamuweli wamthabatha uSawule nomfana lowo, wabangenisa egumbini lokuhlala, wababeka kweyona ndawo iphakamileyo phakathi kwabamenyiweyo, bekumashumi amathathu abantu.', 'XHO22'),
(13, 9, 23, 'Wathi uSamuweli kumpheki, Zisa isabelo endakunikayo, endisithethileyo kuwe, ndathi, Sibeke.', 'XHO22'),
(13, 9, 24, 'Umoji-zonka wathabatha igxalaba eliphezu kwayo, wawubeka phambi koSawule. Wathi uSamuweli, Yabona, into eseleyo; Yibeke phambi kwakho udle, ngokuba ibibekelwe nina, kususela koko ndathi, Ndimemile abantu. Wadla ke uSawule noSamuweli ngaloo mini.', 'XHO22'),
(13, 9, 25, 'Behla esigangeni, bangena esixekweni, wathetha uSamuweli kuSawule phezu kwendlu.', 'XHO22'),
(13, 9, 26, 'Bavuka kusasa, uSamuweli wabiza uSawule phezu kwendlu, wathi, Vuka, ndikushiye. Wagqitywa ke uSawule, baphuma bobabini, yena noSamuweli.', 'XHO22'),
(13, 9, 27, 'Kwathi, behla baya ekupheleni komzi, wathi uSamuweli kuSawule, Yitsho kumfana lowo, ukuba ahambe phambi kwethu (wasa ke yena), khawume ithutyana, ndithethe kuwe ilizwi likaYehova uThixo.', 'XHO22'),
(13, 10, 1, 'uSamuweli wathabatha isigodlo seoli, wamgalela entloko, wamanga, wathi, Ndithe mna, uYehova ukuthambisile, ukuba ube yinganga yelifa lakhe?', 'XHO22'),
(13, 10, 2, 'Ekumkeni kwenu namhla kum, niya kufumana amadoda amabini engcwabeni likaRakeli, emdeni wakwaBhenjamin, iSelsa; aya kuthi kuwe, Zifunyenwe iimazi zamaesile obuzifuna; nanko uyihlo eseleyishiyile idizili, ebuhlungu, esithi, Ndiya kuthini na ngonyana wam?', 'XHO22'),
(13, 10, 3, 'Ukuphuma kwakho apho, hamba, ufika entlango yeTabhore, uqubisane namadoda amathathu enyuka esiya kuThixo eBheteli, enye ithwele iibhokhwe ezintathu, enye ithwele izonka ezithathu, enye ithwele igwegwe lehagwe yewayini.', 'XHO22'),
(13, 10, 4, 'Baya kukubulisa bakunike izonka ezibini; oya kukwamkela ezandleni zabo.', 'XHO22'),
(13, 10, 5, 'Emva koko uya kufika endulini kaThixo, apho akhona amajoni amaFilistiya; wothi wakufika esixekweni, uquqele inkitha yabaprofeti, besihla ivela phezulu, iphethe uhadi, nengqongqo, nempempe, nohadi; kwaye baya kuprofeta;', 'XHO22'),
(13, 10, 6, 'uMoya kaYehova uya kuhlela phezu kwakho, uprofete kunye nabo, ube mntu wumbi.', 'XHO22'),
(13, 10, 7, 'Ke, xa ithe yabonakala kuwe le miqondiso, zenzele indawo efanelekileyo; ngokuba uThixo unawe;', 'XHO22'),
(13, 10, 8, 'uhle phambi kwam uye eGiligali; uyabona, ndiyeza kuwe, ukuze ndinyuse amadini anyukayo nemibingelelo yombulelo; hlalani iintsuku zibe sixhenxe, ndide ndize kuni, ndinixelele into eniya kuyenza.', 'XHO22'),
(13, 10, 9, 'Wathi akuthi gu bucala ukuba emke kuSamuweli, uThixo wamguqula, wanantliziyo yimbi; yafika yonke loo miqondiso kwangayo loo mini.', 'XHO22'),
(13, 10, 10, 'Bakufika endulini, wahlangatyezwa yindimbane yabaprofeti; wafikelwa nguMoya kaThixo phezu kwakhe, waprofeta phakathi kwabo.', 'XHO22'),
(13, 10, 11, 'Babona bonke ababemazi ngaphambili, ukuba uprofete phakathi kwabaprofeti, bathi abantu omnye komnye, Uhliwe yintoni na unyana kaKishe? Ngaba naye uSawule ungomnye wabaprofeti?', 'XHO22'),
(13, 10, 12, 'Waphendula umntu wathi, Ngubani na uyise wabo? Ke ngoko kwabakho umzekeliso othi, Ungomnye wabaprofeti na noSawule?', 'XHO22'),
(13, 10, 13, 'Akugqiba ukuprofeta, wafika kwindawo ephakamileyo.', 'XHO22'),
(13, 10, 14, 'Wathi uRangwanaa uSawule kuye nakumfana lowo, Ubusiya phi na? Wathi, Sifuna iiesile; sathi sakubona ukuba abakho, saya kuSamuweli.', 'XHO22'),
(13, 10, 15, 'Wathi ubawo noyisekazi uSawule, Ndixeleleni, ukuba utheni na kuni uSamuweli.', 'XHO22'),
(13, 10, 16, 'Wathi uSawule kuyise, Usixelele ukuba zifunyenwe idizili. Kodwa akazange amxelele ngobukumkani awayethetha ngabo uSamuweli.', 'XHO22'),
(13, 10, 17, 'uSamuweli wabahlanganisela kuYehova abantu eMizpa;', 'XHO22'),
(13, 10, 18, 'Wathi koonyana bakaSirayeli, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndawakhupha amaSirayeli eYiputa, ndanihlangula esandleni samaYiputa, nasesandleni soorhulumente bonke, nasesandleni sabathwali abanitshutshisayo;', 'XHO22'),
(13, 10, 19, 'Namhla nimlahlile uThixo wenu, owanihlangulayo kuzo zonke iimbandezelo zenu, nasekubandezelweni kwenu; wathi kuye, Hayi, simisele ukumkani. Zilungiseleleni ke ngoku phambi koYehova, kunye nezizukulwana zenu, namawaka enu.', 'XHO22'),
(13, 10, 20, 'Wazibizela ndawonye uSamuweli zonke izizwe zakwaSirayeli, kwanyulwa isizwe sakwaBhenjamin.', 'XHO22'),
(13, 10, 21, 'Wasisondeza isizwe sakwaBhenjamin ngokwemizalwane yaso, kwanyulwa umzi wakwaMatri, kwathi uSawule unyana kaKishe wathinjwa; bamfuna, akafunyanwa.', 'XHO22'),
(13, 10, 22, 'Kungenxa yoko le nto baqhubeka bebuza kuYehova enoba yayisaya kufika na apho loo ndoda. Wathi uYehova, Yabona, uzimele phakathi kwezinto.', 'XHO22'),
(13, 10, 23, 'Bagidima, bamthabatha khona; yaye xa wema phakathi kwabantu, wayemde kunabo bonke abantu ukususela egxalabeni ukusa phezulu.', 'XHO22'),
(13, 10, 24, 'Wathi uSamuweli ebantwini bonke, Niyambona na lo umnyulileyo uYehova, ukuba akukho unjengaye ebantwini bonke? Baduma bonke abantu, besithi, uYehova makamsindise ukumkani.', 'XHO22'),
(13, 10, 25, 'Wandula ke uSamuweli wabaxelela abantu indlela yobukumkani waza wayibhala encwadini waza wayibeka phambi koYehova. Waza uSamuweli wathi bonke abantu mabahambe, elowo aye endlwini yakhe.', 'XHO22'),
(13, 10, 26, 'Naye uSawule waya endlwini yakhe eGibheha; wahamba neqela lamadoda antliziyo zichukumisekileyo kuThixo.', 'XHO22'),
(13, 10, 27, 'Ke bona oonyana bakaBheliyali bathi, Wothini na ukusisindisa loo mntu? Bamdela, abamzisela nezipho. Kodwa wathi cwaka.', 'XHO22'),
(13, 11, 1, 'Wenyuka uNahashe, umAmon, wamisa iintente ngaseYabheshe yaseGiliyadi. Athi onke amadoda aseYabheshe kuNahashe, Simisele umnqophiso, sikukhonze.', 'XHO22'),
(13, 11, 2, 'Wathi uNahashe umAmon kubo, Ndonimisela umnqophiso, ndibatyhale amehlo abalungiseleli benu bonke, ndiwahlaze amaSirayeli onke.', 'XHO22'),
(13, 11, 3, 'Athi kuye amadoda amakhulu aseYabheshe, Siphe iintsuku zibe sixhenxe, sithume abathunywa ezweni lonke lakwaSirayeli; ngoku ke, xa kungekho mhlanguli, sophuma siye kuni.', 'XHO22'),
(13, 11, 4, 'Bafika abathunywa eGibheha kaSawule, baluxela udaba ebebeluvile abantu;', 'XHO22'),
(13, 11, 5, 'Nanko uSawule, ephuma elizweni, elandela imihlambi; Wathi uSawule, Banantoni na abantu, ngeli xesha bayalila? Bamxelela ngamadoda aseYabheshe.', 'XHO22'),
(13, 11, 6, 'uMoya kaThixo wamfikela uSawule ekuyiveni kwakhe le nto, wavutha umsindo wakhe kunene.', 'XHO22'),
(13, 11, 7, 'Wazithabatha iinkomo, wazityatya, wathuma abathunywa ezweni lonke lakwaSirayeli, esithi, Bonke abasukuba bengamlandeli uSawule noSamuweli, baya kwenjiwa njalo emazimeni abo. Ukoyikwa kukaYehova kwaba phezu kwabantu, bahamba bentliziyo-nye.', 'XHO22'),
(13, 11, 8, 'Wababala ke uBhezeki, oonyana bakaSirayeli baba ngamakhulu amathathu amawaka, amadoda akwaYuda angamawaka angamashumi amathathu.', 'XHO22'),
(13, 11, 9, 'Bathi kubathunywa ababefikile, Yizani kumadoda aseYabheshe yaseGiliyadi, nithi, ngomso ukufudumala kwelanga, niya kukhululwa. Beza abathunywa, bawaxela amadoda aseYabheshe; kwaye bonwabile.', 'XHO22'),
(13, 11, 10, 'Athi amadoda aseYabheshe, Siya kunihlasela ngomso, yaye niya kwenza kuthi ngokuthanda kwenu.', 'XHO22'),
(13, 11, 11, 'Ngengomso uSawule wabahlula abantu bangamaqela amathathu; kwaye beza phakathi kwamajoni ngomlindo wakusasa, babulala ama-Amon kwada kwasa.', 'XHO22'),
(13, 11, 12, 'Bathi abantu kuSamuweli, Ngubani na obesithi, uSawule uya kusilawula na? Wazise loo madoda, siwabulale.', 'XHO22'),
(13, 11, 13, 'Wathi uSawule, Makungabulawa mntu namhla; ngokuba uYehova uwahlangule namhla amaSirayeli.', 'XHO22'),
(13, 11, 14, 'Wathi uSamuweli ebantwini, Hambani, sihambe siye eGiligali, sibubuyisele khona ubukumkani.', 'XHO22'),
(13, 11, 15, 'Baya bonke abantu eGiligali; bambeka ukumkani uSawule eGiligali phambi koYehova; babingelela apho imibingelelo yombulelo phambi koYehova; yaye uSawule nawo onke amadoda akwaSirayeli bavuya kakhulu apho.', 'XHO22'),
(13, 12, 1, 'Wathi uSamuweli kumaSirayeli onke, Yabonani, ndiliphulaphule izwi lenu ngako konke enakuthethayo kum, ndanimisa nina.', 'XHO22'),
(13, 12, 2, 'Kaloku nanko ukumkani ehamba phambi kwenu. Ke mna ndaluphele, ndiyingwevu; nabo oonyana bam benani; mna ndihambe nani kwasebuncinaneni bam unanamhla.', 'XHO22'),
(13, 12, 3, 'Yabonani, ndim lo ndithathe iesile likabani na? ndilahlekise bani na? Ndakha ndacinezela bani na? ndinyobe kubani na ukuba ndiwamfamekise amehlo am ngayo? ndiya kuyibuyisela kuni.', 'XHO22'),
(13, 12, 4, 'Bathi ke bona, Akusixokisanga, akusicudisanga, akuthabathanga nto esandleni samntu.', 'XHO22'),
(13, 12, 5, 'Wathi kubo, uYehova ulingqina ngani, nomthanjiswa wakhe ulingqina namhla, ukuba anamkelanga nto esandleni sam. Baphendula bathi, Ndilingqina.', 'XHO22'),
(13, 12, 6, 'Wathi uSamuweli ebantwini, NguYehova owabavusayo ooMoses noAron, owabakhuphayo ooyihlo ezweni laseYiputa.', 'XHO22'),
(13, 12, 7, 'Yimani ngoku, ndithethe kuni phambi koYehova ngayo yonke imisebenzi yobulungisa kaYehova, ayenzileyo kuni nakooyihlo.', 'XHO22'),
(13, 12, 8, 'Xenikweni uYakobi waya eYiputa, bathandaza ooyihlo kuYehova; uYehova wathuma ooMoses noAron, babakhupha ooyihlo eYiputa, babamisa kule ndawo.', 'XHO22'),
(13, 12, 9, 'Bamlibala uYehova uThixo wabo, wathengisa ngabo esandleni sikaSisera, umthetheli-mkhosi waseHatsore, nasesandleni samaFilisti, nasesandleni sokumkani wakwaMowabhi; balwa nabo.', 'XHO22'),
(13, 12, 10, 'Banqula uYehova, besithi, Sifekile, ngokuba sikukreqile kuYehova, sakhonza ooBhahali nooAshtaroti; sihlangule esandleni seentshaba zethu, yaye siya kukukhonza.', 'XHO22'),
(13, 12, 11, 'uYehova wathuma ooYerubhahali, noBhedan, noYafete, noSamuweli, wanihlangula esandleni seentshaba zenu ngeenxa zonke, nahlala ngoxolo.', 'XHO22'),
(13, 12, 12, 'Nabona uNahashe, ukumkani woonyana baka-Amon, enixabela, nathi kum, Hayi; kodwa siya kulawulwa ngukumkani, nangona uYehova uThixo wenu engukumkani wenu.', 'XHO22'),
(13, 12, 13, 'Khangelani ke, ukumkani enimnyulileyo, lowo nimlandeleyo; uyabona, uYehova ukwenze ukumkani.', 'XHO22'),
(13, 12, 14, 'Ukuba nithe namoyika uYehova, nimkhonze, naliphulaphula ilizwi lakhe, anawugwilika kuYehova uThixo wenu, nina nokumkani onilawulayo, nimlandele uYehova uThixo wenu.', 'XHO22'),
(13, 12, 15, 'Ke ukuba anithanga niliphulaphule ilizwi likaYehova, nithe nawuphikisa umlomo kaYehova, isandla sikaYehova siya kunichasa, njengokuba sachasa ooyihlo.', 'XHO22'),
(13, 12, 16, 'Yimani ngoku, niyibone le nto inkulu, ayenzayo uYehova phambi kwamehlo enu.', 'XHO22'),
(13, 12, 17, 'Akuvunwa ingqolowa yini na namhla? Ndiya kubiza kuYehova, ahlise iindudumo nemvula; ukuze nibone, nibone ukuba bukhulu ububi benu enibenzileyo emehlweni kaYehova ngokubucela kukumkani.', 'XHO22'),
(13, 12, 18, 'uSamuweli wanqula uYehova; yaye uYehova wathumela iindudumo nemvula ngaloo mini, yaye lonke uhlanga lwamoyika kakhulu uYehova noSamuweli.', 'XHO22'),
(13, 12, 19, 'Bathi bonke abantu kuSamuweli, Bathandazele abakhonzi bakho kuYehova uThixo wakho, ukuze singafi; ngokuba songeze obu bubi ezonweni zethu zonke ngokuzicelela kwethu kukumkani.', 'XHO22'),
(13, 12, 20, 'Wathi uSamuweli ebantwini, Musani ukoyika;', 'XHO22'),
(13, 12, 21, 'Kwaye musani ukutyeka; ngokuba ingamampunge.', 'XHO22'),
(13, 12, 22, 'Ngenxa yokuba uYehova akayi kubafulathela abantu bakhe ngenxa yegama lakhe elikhulu, ngenxa yokuba kwakholeka kuYehova ukunenza abantu bakhe.', 'XHO22'),
(13, 12, 23, 'Ke mna, wanga uThixo angandivimba, ndingoni kuYehova ngokuyeka ukunithandazela; ndonifundisa indlela elungileyo, yobulungisa.', 'XHO22'),
(13, 12, 24, 'Moyikeni uYehova nize nimkhonze ngokwenyaniso ngeentliziyo zenu zonke; ngokuba nizibonile izinto ezinkulu anenzele zona.', 'XHO22'),
(13, 12, 25, 'Ke ukuba nithe naqhubeka nenza ububi, nobhubha, nina nokumkani wenu.', 'XHO22'),
(13, 13, 1, 'uSawule ebelawula kwaSirayeli unyaka wamnye.', 'XHO22'),
(13, 13, 2, 'uSawule wanyula amawaka amathathu amadoda kumaSirayeli; Amawaka amabini kubo anoSawule eMigmas ezintabeni zaseBheteli, iwaka libe noYonatan eGibheha yakwaBhenjamin; Abantu abaseleyo wababuyisela ezintenteni zabo.', 'XHO22'),
(13, 13, 3, 'uYonatan wawuxabela umkhosi wamaFilisti owawuseGebha, aza eva amaFilisti. uSawule wavuthela ngesigodlo kulo lonke ilizwe, esithi, Waveni amaHebhere.', 'XHO22'),
(13, 13, 4, 'Onke amaSirayeli eva ukuba uSawule uyoyisile umkhosi wamaFilisti, nokuba amaSirayeli angamasikizi kumaFilisti. Yaye abantu bahlanganisana ndawonye ukuba balandele uSawule ukuya eGiligali.', 'XHO22'),
(13, 13, 5, 'AmaFilisti ahlanganisana ukuba alwe namaSirayeli, amashumi amathathu amawaka eenqwelo zokulwa, namawaka amathandathu abamahashe, namadoda amaninzi njengalawo asemazantsi elwandle; banduluka baza bamisa iinkampu zabo eMigemas ngasempuma yeBhete-afen.', 'XHO22'),
(13, 13, 6, 'Akubona amadoda akwaSirayeli ukuba aswele (ngenxa yokuba abantu babebandezelekile), abantu bazifihla emiqolombeni nasemahlathini nasemaweni nasezindaweni eziphakamileyo nasemingxunyeni.', 'XHO22'),
(13, 13, 7, 'Amanye amaHebhere awela iYordan esiya kwilizwe lakwaGadi naseGiliyadi. Ke yena uSawule, wayeseseGiligali, yaye bonke abantu babengcangcazela bemlandela.', 'XHO22'),
(13, 13, 8, 'Walinda iintsuku zasixhenxe, ngokwexesha elimisiweyo likaSamuweli; ke uSamuweli akafikanga eGiligali; Baphalala abantu bemka kuye.', 'XHO22'),
(13, 13, 9, 'Wathi uSawule, Zisani kum idini elinyukayo nemibingelelo yombulelo. Wenza idini elinyukayo.', 'XHO22'),
(13, 13, 10, 'Kwathi, akugqiba ukulinyusa idini elinyukayo, nanko uSamuweli efika; Waphuma uSawule waya kumkhawulela, ukuba ambulise.', 'XHO22'),
(13, 13, 11, 'Wathi uSamuweli, Wenza ntoni na? Wathi uSawule, Ngenxa enokuba ndibonile ukuba amadoda echithakele, endishiya, nawe ungezanga ngemihla emisiweyo, namaFilisti abehlanganisene eMigmas;', 'XHO22'),
(13, 13, 12, 'Ndathi ke, AmaFilisti aya kundihlasela eGiligali, ndingathandazanga kuYehova;', 'XHO22'),
(13, 13, 13, 'Wathi uSamuweli kuSawule, Wenze ngobudenge, akwawugcina umthetho kaYehova uThixo wakho owawuwisayo; ngokuba uYehova ebeya kubumisa ubukumkani bakho kwaSirayeli kude kuse ephakadeni.', 'XHO22'),
(13, 13, 14, 'Kodwa ngoku, ubukumkani bakho abuyi kubakho; uYehova wakhangela indoda entliziyweni yayo, wayimisa uYehova ukuba ibe ngumlawuli wabantu bakhe, ngokuba engayigcinanga imithetho kaYehova.', 'XHO22'),
(13, 13, 15, 'Wemka uSamuweli, wenyuka waya eGiligali, waya eGibheha ezweni lakwaBhenjamin. uSawule wawabala amadoda abenaye, ngathi kumakhulu amathandathu amadoda.', 'XHO22'),
(13, 13, 16, 'uSawule, noYonatan unyana wakhe, namadoda abenabo, bahlala eGibheha yakwaBhenjamin, amaFilisti wona amise iintente eMigmas.', 'XHO22'),
(13, 13, 17, 'Ababhuqi baya kuza bephuma kwiinkampu zamaFilisti bengamaqela amathathu, iqela elinye linyuke ngendlela yaseOfura kwilizwe laseShuwali.', 'XHO22'),
(13, 13, 18, 'Esinye isihlwele sabheka endleleni yaseBhete-horon;', 'XHO22'),
(13, 13, 19, 'Akwafunyanwa krele kulo lonke ilizwe lakwaSirayeli, ngokuba amaFilisti ebesithi, AmaHebhere makangazenzele makrele nemikhonto.', 'XHO22'),
(13, 13, 20, 'Ehla onke amaSirayeli aya kumaFilisti, elowo walola isahlulo sakhe, neentolo zakhe, nezembe lakhe, nomsimelelo wakhe.', 'XHO22'),
(13, 13, 21, 'Ke bona babenefayile yeenduku, yeentsimbi, neefolokhwe, nezamazembe, neyezinto zokulola.', 'XHO22'),
(13, 13, 22, 'Ngemini yedabi bekungekho krele nantshuntshe esandleni samadoda onke abenoSawule noYonatan; kodwa kwakukho uSawule noYonatan unyana wakhe.', 'XHO22'),
(13, 13, 23, 'Waza umkhosi wamaFilisti waqhubeka waya kwintlambo yaseMigmas.', 'XHO22'),
(13, 14, 1, 'Kwathi ngamhla uthile, uYonatan unyana kaSawule wathi kumfana lowo uphatha iintonga zakhe, Hamba siye kumagorha amaFilistiya ngaphesheya. Akamxelela uyise.', 'XHO22'),
(13, 14, 2, 'uSawule wayehleli ekupheleni kweGibheha phantsi komrharnate oseMigron; amadoda abenaye ngathi angamakhulu amathandathu amadoda;', 'XHO22'),
(13, 14, 3, 'inguAhiya, unyana ka-Ahitubhi, umkhuluwa kaIkabhodi, unyana kaPinehasi, unyana kaEli, umbingeleli kaYehova waseShilo, enxibe ingubo engaphantsi. Babengazi abantu ukuba uYonatan uyile.', 'XHO22'),
(13, 14, 4, 'Phakathi kwezikroba, abefuna ukuphumela kuzo uYonatan, aye kwizigidimi zamaFilisti, bekukho iliwa elibe neli nganeno; igama lomnye nguBosese, kwaye igama lomnye nguSene.', 'XHO22'),
(13, 14, 5, 'Esinye isahlulo sasibheke entla, sibheka eMigmas, esinye sibheka ezantsi eGibheha.', 'XHO22'),
(13, 14, 6, 'Wathi uYonatan kumfana lowo uphatha iintonga zakhe, Hamba, siwele, siye kwaba balindi ngomzuzu.', 'XHO22'),
(13, 14, 7, 'Wathi umphathi weentonga zakhe kuye, Konke okusentliziyweni yakho, kwenze; uyabona, ndinawe ngokwentliziyo yakho.', 'XHO22'),
(13, 14, 8, 'Wathi uYonatan, Yabona, siya kuya kulaa madoda, sibonakale kuwo.', 'XHO22'),
(13, 14, 9, 'Ukuba bathe kuthi, Yithini, Ngesifike kuwe; siya kuma khona endaweni yethu, singenyuki siye kubo.', 'XHO22'),
(13, 14, 10, 'Ukuba bathi, Nyukani nize kuthi; siya kunyukela khona apho, ngokuba uYehova uwanikele esandleni sethu, yaye oku kuya kuba ngumqondiso kuthi.', 'XHO22'),
(13, 14, 11, 'Babonakala bengamadoda amabini akumaFilistiya, athi amaFilisti, Yabona, amaHebhere ephuma emingxunyeni abezimele kuyo.', 'XHO22'),
(13, 14, 12, 'Abaphendula abalindi ooYonatan nomphathi weentonga zakhe, bathi, Nyukani nize kuthi, sonibonisa. Wathi uYonatan kumfana lowo, Nyuka undilandele; ngokuba uYehova uwanikele esandleni soonyana bakaSirayeli.', 'XHO22'),
(13, 14, 13, 'Wema uYonatan, ekhase izandla neenyawo, umphathi weentonga zakhe esemva kwakhe, waziphosa ngakuYonatan umphathi weentonga zakhe wambulala.', 'XHO22'),
(13, 14, 14, 'Elokuqala waxabela uYonatan nomphathi weentonga zakhe, awaxabela amadoda angathi amashumi mabini, afuye iinkomo ezisisiqingatha sesango.', 'XHO22'),
(13, 14, 15, 'Kwabakho ukunyikima phakathi komkhosi ezweni nakubo bonke abantu;', 'XHO22'),
(13, 14, 16, 'Bakhangela abalindi bakaSawule, baseGibheha yakwaBhenjamin; kwaye khangela, isihlwele sanyibilika, kwaye baqhubeka bebethana.', 'XHO22'),
(13, 14, 17, 'Wathi uSawule kumadoda abenaye, Fundani, nibone ukuba ngubani na ophume kuthi; Akubala, akakho uYonatan nomphathi weentonga zakhe.', 'XHO22'),
(13, 14, 18, 'Wathi usawule kuAhiya, Zisa apha ityeya kaThixo; ngokuba ityeya kaThixo ibinoonyana bakaSirayeli ngaloo mini.', 'XHO22'),
(13, 14, 19, 'Kwathi, esathetha uSawule kumbingeleli, saphuma isandi samaqela amaFilisti; wathi uSawule kumbingeleli, Buyisa isandla sakho.', 'XHO22'),
(13, 14, 20, 'Wahlanganisana uSawule namadoda onke abenaye, eza kulwa;', 'XHO22'),
(13, 14, 21, 'NamaHebhere abekumaFilisti ngaphambili, abenyuka esiya emkhosini, evela emazweni, aba koonyana bakaSirayeli, ababenoSawule noYonatan.', 'XHO22'),
(13, 14, 22, 'Kwanamadoda onke akwaSirayeli, abezimele kweleentaba lakwaEfrayim, eva ukuba amaFilisti asabile, awalandela ekulweni.', 'XHO22'),
(13, 14, 23, 'uYehova wawasindisa ke amaSirayeli ngaloo mini; ukulwa kwada kwesa eBhetaven.', 'XHO22'),
(13, 14, 24, 'Amadoda akwaSirayeli ayedidekile ngaloo mini, kuba uSawule wayebacebisile abantu, esithi, Uqalekisiwe odla inyama, kude kuhlwe, ndize ndiziphindezelele ezintshabeni zam. Ngoko ke abantu akubangakho namnye ukungcamla ukutya.', 'XHO22'),
(13, 14, 25, 'Lonke ilizwe lafikelela ehlathini; kwaye kukho ubusi phezu komhlaba.', 'XHO22'),
(13, 14, 26, 'Afika amadoda ehlathini, avuza ubusi; akasibeka isandla sakhe emlonyeni wakhe, ngokuba abantu bebesoyika isifungo.', 'XHO22'),
(13, 14, 27, 'Akaphulaphula uYonatan ekucebisaneni kukayise ebantwini; ke ngoko, wathabatha incam yentonga awayeyibambe, wayicinezela endlwini yeenyosi, waza wabeka isandla sakhe emlonyeni; kwaye amehlo akhe akhazimla.', 'XHO22'),
(13, 14, 28, 'Yaphendula enye indoda, yathi, Uyihlo ubathethe kakubi abantu, wathi, Uqalekisiwe odla isonka, namhlanje. Kwaye abantu babediniwe.', 'XHO22'),
(13, 14, 29, 'Wathi uYonatan, Ubawo ulizisele isiphithiphithi ilizwe; Yabona, amehlo am aqabukile, ngokuba ndive intwana yezi busi.', 'XHO22'),
(13, 14, 30, 'Kwenzeka njani ukuba namhlanje abantu badle amaxhoba eentshaba zabo baze bawafumane ngesisa? ngokuba amaFilisti ayengabulawanga kangako?', 'XHO22'),
(13, 14, 31, 'Ngaloo mini bawaxabela amaFilisti, ukusuka eMigmas ukuya eAyalon, yaye abantu babediniwe.', 'XHO22'),
(13, 14, 32, 'Baphaphatheka abantu emaxhobeni, bathabatha impahla emfutshane, neenkomo, namathole eenkomo, baxhela, badla negazi;', 'XHO22'),
(13, 14, 33, 'Waxelelwa uSawule, kwathiwa, Uyabona, abantu bayona kuYehova ngokudla igazi; Wathi, Nikreqile; ndiphose ilitye elikhulu namhlanje.', 'XHO22'),
(13, 14, 34, 'Wathi uSawule, Yiyani phakathi kwabantu, uthi kubo, Ziziseni kum elowo inkomo yakhe nomhlambi wakhe, nixhelele apha, nidle; ningoni kuYehova ngokudla igazi. Bonke abantu bazisa elowo iinkomo zakhe ngobo busuku, baxhelela khona.', 'XHO22'),
(13, 14, 35, 'uSawule wamakhela uYehova isibingelelo; sisibingelelo sokuqala awasakhela sona uYehova.', 'XHO22'),
(13, 14, 36, 'Wathi uSawule, Masiwasukele amaFilisti ebusuku, siwaphange kude kube ngomso, kungashiyi namnye kuwo. Bathi ke bona, Yenzani okulungileyo emehlweni enu. Wathi umbingeleli, Masisondele kuThixo.', 'XHO22'),
(13, 14, 37, 'uSawule wabuza kuThixo, esithi, Ndibaleke ndingene na kumaFilisti? wowanikela na esandleni samaSirayeli? Kodwa ngaloo mini akazange amphendule.', 'XHO22'),
(13, 14, 38, 'Wathi uSawule, Yizani apha, nonke nina zibonda zabantu, nive, nibone ukuba siyintoni na esi sono namhlanje?', 'XHO22'),
(13, 14, 39, 'Kuba eneneni ngoYehova ophilayo okhululayo uSirayeli, ukuba kuthe kwenzeka kuYonatan unyana wam, ngokuqinisekileyo uya kufa. Kodwa phakathi kwabantu bonke akubangakho namnye umphendulayo.', 'XHO22'),
(13, 14, 40, 'Wathi kumaSirayeli onke, Yimani ngapha, mna noYonatan unyana wam, sibe ngelinye icala. Bathi abantu kuSawule, Yenza okulungileyo emehlweni akho.', 'XHO22'),
(13, 14, 41, 'Wathi uSawule kuYehova, uThixo kaSirayeli, Yenza amaqashiso. uSawule noYonatan babanjwa, kodwa amadoda asaba.', 'XHO22'),
(13, 14, 42, 'Wathi uSawule, Yenzani iqashiso phakathi kwam noYonatan unyana wam. Wabanjwa ke uYonatan.', 'XHO22'),
(13, 14, 43, 'Wathi uSawule kuYonatan, Ndixelele, yintoni na le nto uyenzileyo? uYonatan wamxelela, wathi, Ndive intwana yobusi ngencam yentonga endiyibambe ngesandla, nanku ndiya kufa.', 'XHO22'),
(13, 14, 44, 'Waphendula uSawule wathi, uThixo makenjenje, aqokele ukwenjenje; inene, uya kufa, Yonatan.', 'XHO22'),
(13, 14, 45, 'Bathi abantu kuSawule, Afe na uYonatan, owenze ngolo hlobo amaSirayeli? Ehleli nje uYehova, akuyi kuwa nonwele lwentloko yakhe emhlabeni; ngokuba usebenzisene noThixo namhlanje. Bamsindisa abantu uYonatan, akafa.', 'XHO22'),
(13, 14, 46, 'Wenyuka uSawule, wawalandela amaFilisti; amaFilisti aya emakhayeni awo.', 'XHO22'),
(13, 14, 47, 'uSawule wabuthabatha ubukumkani bakwaSirayeli waza walwa nazo zonke iintshaba zakhe ezazivela macala onke, walwa noMowabhi noonyana baka-Amoni noEdom nookumkani baseTsobha namaFilisti; kwaye naphi na apho wayejika wayesoyikisa.', 'XHO22'),
(13, 14, 48, 'Wahlanganisa umkhosi, wawaxabela ama-Amaleki, wawahlangula amaSirayeli esandleni sabo babewatshutshisa;', 'XHO22'),
(13, 14, 49, 'Oonyana bakaSawule nguYonatan, noYeshvi, noMalekishuwa; ngawo la amagama eentombi zakhe zombini; igama lowamazibulo nguMerabhe, igama lowemnci nguMikale;', 'XHO22'),
(13, 14, 50, 'Igama lomkaSawule belinguAkinoma, intombi ka-Ahimahatse; igama lomthetheli-mkhosi belinguAbhinere, unyana kaNere, uyisekazi kaSawule.', 'XHO22'),
(13, 14, 51, 'Uyise kaSawule ubenguKishe; uNere, uyise ka-Abhinere, wayengunyana ka-Abhiyeli.', 'XHO22'),
(13, 14, 52, 'Kwaye kukho imfazwe enzima kumaFilisti yonke imihla kaSawule; yaye xa uSawule wabona indoda eyomeleleyo okanye indoda elikroti, wayizisa kuye.', 'XHO22'),
(13, 15, 1, 'Wabuya wathi uSamuweli kuSawule, uYehova undithume ukuba ndikuthambise ube ngukumkani kubantu bakhe amaSirayeli; liphulaphule ke ngoku ilizwi lamazwi kaYehova.', 'XHO22'),
(13, 15, 2, 'Utsho uYehova wemikhosi ukuthi, Ndiyakuqonda oko ama-Amaleki akwenzayo koonyana bakaSirayeli, ukuba abebalindile endleleni ekuphumeni kwabo eYiputa.', 'XHO22'),
(13, 15, 3, 'Ke kaloku hamba uye uwabulale ama-Amaleki, utshabalalise yonke into anayo, ungawacongi; kodwa yixheleleni inkunzi, nemazi, nabantwana, nabantwana, neenkomo, nempahla emfutshane, neenkamela, namaesile.', 'XHO22'),
(13, 15, 4, 'Wawahlanganisa uSawule amadoda, wawabala eTelayim: amakhulu amabini amawaka omqikela, neshumi lamawaka amadoda akwaYuda.', 'XHO22'),
(13, 15, 5, 'Wafika uSawule emzini wama-Amaleki, walala entilini.', 'XHO22'),
(13, 15, 6, 'Wathi uSawule kumaKanan, Sukani, nihle, ningene kuma-Amaleki, hleze ndinitshabalalise nawo; ngenxa yokuba nabenzela inceba bonke oonyana bakaSirayeli ekunyukeni kwabo bephuma eYiputa. Aphuma ke amaKanan kuma-Amaleki.', 'XHO22'),
(13, 15, 7, 'uSawule wawaxabela ama-Amaleki, wathabathela eHavila wesa eShure, ekhangelene neYiputa.', 'XHO22'),
(13, 15, 8, 'Wambamba uAgagi ukumkani wama-Amaleki esaphila, wababulala bonke abantu ngekrele ngesiqalekiso.', 'XHO22'),
(13, 15, 9, 'Kodwa uSawule nabantu bamsindisa uAgagi nezona zilungileyo izimvu neenkomo netyetyisiweyo namatakane eegusha nako konke okulungileyo; hlamba ukungcola.', 'XHO22'),
(13, 15, 10, 'Lafika ilizwi likaYehova kuSamuweli, lisithi:', 'XHO22'),
(13, 15, 11, 'Ndiyaxolisa ngokuba ndimenze ukumkani uSawule, ngenxa yokuba uye wandinikela umva waza akazange ayiphumeze imiyalelo yam. Kwaba buhlungu oko kuSamuweli; wabiza uYehova ubusuku bonke.', 'XHO22'),
(13, 15, 12, 'Wavuka kusasa uSamuweli, waya kukhawulela uSawule; waxelelwa uSamuweli, kwathiwa, uSawule uze eKarmele, wabona ukuba umlungisele indawo; wemka, wehla, wehla waya eGiligali.', 'XHO22'),
(13, 15, 13, 'Weza uSamuweli kuSawule, wathi uSawule kuye, Wanga uYehova akusikelele. Ndawuthobela umyalelo kaYehova.', 'XHO22'),
(13, 15, 14, 'Wathi uSamuweli, Kuyintoni na ukukhala kwempahla emfutshane ezindlebeni zam, nokuhleka kweenkomo endikuvayo?', 'XHO22'),
(13, 15, 15, 'Wathi uSawule, Bazithabatha kuma-Amaleki; ngokuba abantu babesele eyona ilungileyo imihlambi yempahla emfutshane, neenkomo, ukuze babingelele kuYehova uThixo wakho; kwaye abanye sibohlwaya ngesiqalekiso sokugxothwa.', 'XHO22'),
(13, 15, 16, 'Wathi uSamuweli kuSawule, Yima, ndikuxelele oko uYehova akuthethileyo kum ngobu busuku. Wathi ke yena kuye, Khawutsho.', 'XHO22'),
(13, 15, 17, 'Wathi uSamuweli, Akubekwanga yintloko yezizwe zakwaSirayeli yini na, ubusemncinane emehlweni akho? Wakuthambisa uYehova ukuba ube ngukumkani wakwaSirayeli?', 'XHO22'),
(13, 15, 18, 'uYehova wathumela ngendlela, wathi, Hamba uye kubatshabhisa abo boni bama-Amaleki, ulwe nawo, ubatshabalalise.', 'XHO22'),
(13, 15, 19, 'Kutheni na ngoko ningaliphulaphulanga ilizwi likaYehova, nasuka naphaphazela emaxhobeni nenza okubi emehlweni kaYehova?', 'XHO22'),
(13, 15, 20, 'Wathi uSawule kuSamuweli, Ndaliphulaphula ilizwi likaYehova, ndemka ngendlela uYehova ebendithume ngayo, ndamzisa uAgagi ukumkani wama-Amaleki, ndawaqalekisa ama-Amaleki.', 'XHO22'),
(13, 15, 21, 'Kodwa abantu bathabatha emhlambini weebhokhwe neenkomo ezithinjiweyo, ezona zibalaseleyo kwezo zaziqalekisiwe, ukuze babingelele ngazo kuYehova uThixo wakho eGiligali.', 'XHO22'),
(13, 15, 22, 'Wathi uSamuweli, uYehova unonelele amadini anyukayo nemibingelelo, ngaphezu kokuva ilizwi likaYehova? Yabona, ukuphulaphula kulungile ngaphezu kombingelelo, ukuphulaphula kulungile ngaphezu kwamanqatha eenkunzi zeegusha.', 'XHO22'),
(13, 15, 23, 'Ngokuba ukuba neenkani kusisono esinjengokuvumisa, ubungqola bunjengenkonzo yezithixo nemilondekhaya. Ngokuba ulicekisile ilizwi likaYehova, naye ukucekisile ukuba ungabi ngukumkani.', 'XHO22'),
(13, 15, 24, 'Wathi uSawule kuSamuweli, Ndonile; ngokuba ndigqithile emthethweni kaYehova naselizwini lakho; kuba bendisoyika abantu, ndaphulaphula izwi labo.', 'XHO22'),
(13, 15, 25, 'Sixolele isono sam, ubuye nam, ukuze ndimnqule uYehova.', 'XHO22'),
(13, 15, 26, 'Wathi uSamuweli kuSawule, Andiyi kubuya nawe; ngokuba ulicekisile ilizwi likaYehova, naye uYehova ukucekisile, ukuba ungabi ngukumkani wakwaSirayeli.', 'XHO22'),
(13, 15, 27, 'Wathi uSamuweli akujika ukuba emke, wabamba ilaphu lengubo yakhe, yakrazuka.', 'XHO22'),
(13, 15, 28, 'Wathi uSamuweli kuye, Namhla uYehova ubukrazule kuwe ubukumkani bakwaSirayeli, obuniké kobakho.', 'XHO22'),
(13, 15, 29, 'Kananjalo amandla kaSirayeli akaxoki, yaye abaguquki, kuba asingobantu ukuba baguquke.', 'XHO22'),
(13, 15, 30, 'Wathi, Ndihlulekile; Noko ke ndizukise phambi kwamadoda amakhulu abantu bakowethu, naphambi kwamaSirayeli, ubuye nam, ndimnqule uYehova uThixo wakho.', 'XHO22'),
(13, 15, 31, 'Wabuya ke uSamuweli, wamlandela uSawule; uSawule wamnqula uYehova.', 'XHO22'),
(13, 15, 32, 'Wathi uSamuweli, Mziseni kum uAgagi, ukumkani wama-Amaleki ke uAgagi weza kuye enenceba. Wathi uAgage: Inene, iintlungu zokufa ziphelile.', 'XHO22'),
(13, 15, 33, 'Wathi uSamuweli, Njengokuba umkhonto wakho ubadlayo aba bafazi, unyoko uya kubabulala abantwana phakathi kwabafazi. uSamuweli wamnqumla uAgagi phambi koYehova eGiligali.', 'XHO22'),
(13, 15, 34, 'Waya uSamuweli eRama; Wenyuka uSawule waya endlwini yakhe eGibheha, emzini kaSawule.', 'XHO22'),
(13, 15, 35, 'uSamuweli akabuyanga amvelele uSawule, kwada kwayimini yokufa kwakhe; kodwa uSamuweli waba buhlungu ngenxa kaSawule, yaye uYehova waguquka ngenxa yokuba wayemenze uSawule ukumkani wakwaSirayeli.', 'XHO22'),
(13, 16, 1, 'Wathi uYehova kuSamuweli, Uya kumlilela kude kube nini na uSawule, ndimshiyile nje ukuba abe ngukumkani kumaSirayeli? Zalisa isigodlo sakho ngeoli, hamba, ndiya kukuthumela kuYese waseBhetelehem; ngokuba ndizinyulele ukumkani koonyana bakhe.', 'XHO22'),
(13, 16, 2, 'Wathi uSamuweli, Ndothini na ukuya? akuva uSawule, andibulale. Wathi uYehova, Zithabathele ivesi, ithi, Ndize kubingelela kuYehova.', 'XHO22'),
(13, 16, 3, 'Bizela embingelelweni uYese; ndokuxelela mna into oya kuyenza; uze undinike lowo bendikubizele kona.', 'XHO22'),
(13, 16, 4, 'Wenza ke uSamuweli ngokwelizwi likaYehova, wafika eBhetelehem. Akufika kuye amadoda amakhulu aloo mzi, athi, Ufike enoxolo na?', 'XHO22'),
(13, 16, 5, 'Wathi, Ndize kubingelela kuYehova ndinoxolo; zingcwaliseni, nize nam embingelelweni. Wabangcwalisa uYese noonyana bakhe waza wababizela embingelelweni.', 'XHO22'),
(13, 16, 6, 'Ekufikeni kwabo, wakhangela kuEliyabhi, wathi, Okunene, umthanjiswa kaYehova unaye.', 'XHO22'),
(13, 16, 7, 'Wathi uYehova kuSamuweli, Musa ukukhangela ubuso bakhe, nokuphakama kwesiqu sakhe; ngokuba ndimshiyile; ngokuba uYehova akaboni njengoko abonayo umntu; ngokuba umntu ukhangela umphandle, uYehova ukhangela intliziyo.', 'XHO22'),
(13, 16, 8, 'uYese wabiza uAbhinadabhi waza wamvumela ukuba agqithe phambi koSamuweli. Wathi: uYehova akamnyulanga nalo.', 'XHO22'),
(13, 16, 9, 'uYese wamndulula ke uSama. Wathi: uYehova akamnyulanga nalo.', 'XHO22'),
(13, 16, 10, 'Kwakhona uYese wabandulula oonyana bakhe abasixhenxe ukuba badlule phambi koSamuweli. Wathi uSamuweli kuYese, uYehova akamnyulanga lo.', 'XHO22'),
(13, 16, 11, 'Wathi uSamuweli kuYese, Baphelele na oonyana bakho? Wathi, Kusele umncinane; nanko lusalusa iigusha. Wathi uSamuweli kuYese, Thumela abizwe, ngokuba asinakuhlala ade eze apha.', 'XHO22'),
(13, 16, 12, 'Wathumela ukuba aziswe. Ebebomvu ebusweni, emhle, emhle imbonakalo. Wathi uYehova, Suka ume, umthambise; ngokuba nguye;', 'XHO22'),
(13, 16, 13, 'uSamuweli wathabatha uphondo oluneoli waza wamthambisa phakathi kwabazalwana bakhe, yaye umoya kaYehova wehla phezu koDavide ukususela ngaloo mini. Emva koko uSamuweli waphakama waya eRama.', 'XHO22'),
(13, 16, 14, 'Umoya kaYehova wemka kuSawule, waza wankwantyiswa ngumoya ombi kaYehova.', 'XHO22'),
(13, 16, 15, 'Bandula ke abakhonzi bakaSawule bathi kuye: “Yabona, udandathekiswa ngumoya ombi ophuma kuThixo.', 'XHO22'),
(13, 16, 16, 'Kaloku ukumkani wethu makabawisele umthetho abakhonzi bakho phambi kwakho, ukuba bafune indoda elumkileyo ebetha uhadi; ukuba umoya ombi kaThixo ufikile kuwe, ufika ngesandla sakhe, nophiliswa wena.', 'XHO22'),
(13, 16, 17, 'Wathi uSawule kubakhonzi bakhe, Ndiqokeleleni indoda ekwaziyo ukubetha uhadi, niyizise kum.', 'XHO22'),
(13, 16, 18, 'Waphendula omnye wakubakhonzi, wathi, Yabona, ndimbonile unyana kaYese waseBhetelehem okrelekrele, indoda eligorha, esisilumko kweshishini, njengendoda entle; uYehova unaye.', 'XHO22'),
(13, 16, 19, 'uSawule wathumela abathunywa kuYese, esithi, Mthumele eze kum, uDavide, unyana wakho, ohlala emhlambini.', 'XHO22'),
(13, 16, 20, 'uYese wathabatha iesile, kubotshelelwe isonka, nengxowa yewayini, netakane lebhokhwe, wathumela kuSawule ngesandla sikaDavide unyana wakhe.', 'XHO22'),
(13, 16, 21, 'Weza uDavide kuSawule, wema ngakuye, wamthanda kunene; waba ludibi lwezixhobo zakhe.', 'XHO22'),
(13, 16, 22, 'Wathumela uSawule kuYese, esithi, uDavide ume ngakum; ngokuba ubabalwe ndim.', 'XHO22'),
(13, 16, 23, 'Wehla umoya ombi kaThixo kuSawule, walithabatha izembe elingaphakathi uDavide, wabetha ngesandla sakhe;', 'XHO22'),
(13, 17, 1, 'AmaFilisti ayihlanganisa imikhosi yawo ukuba ilwe, ayihlanganisela eSogo yakwaYuda, amisa phakathi kweSogo neAzeka, e-Efese-Damim.', 'XHO22'),
(13, 17, 2, 'uSawule namadoda akwaSirayeli bahlanganisana, bamisa inkampu entlanjeni yoMfula kaElaa baza balwa imfazwe nxamnye namaFilisti.', 'XHO22'),
(13, 17, 3, 'AmaFilisti ayemi kwelinye icala lentaba, amaSirayeli emi kwelinye icala lentaba, kukho intlambo phakathi kwawo.', 'XHO22'),
(13, 17, 4, 'Kwavela igorha elisenkampini yamaFilisti, igama lalo lalinguGoliyati waseGati, obude buziikubhite ezintandathu nomolulo wesandla;', 'XHO22'),
(13, 17, 5, 'enxibe isigcina-ntloko sobhedu entloko, ethwele isigcina-sifuba; ubunzima bengubo bebuziishekele zobhedu ezingamawaka amahlanu;', 'XHO22'),
(13, 17, 6, 'ezinyaweni zayo yayineepani zobhedu, nombhinqo wobhedu phakathi kwamagxa ayo;', 'XHO22'),
(13, 17, 7, 'uluthi lwentshuntshe yayo belungangomthi wabaluki; Intloko yentshuntshe yayo ibiziishekele ezimakhulu mathandathu esinyithi;', 'XHO22'),
(13, 17, 8, 'Wesuka wema, wayibiza imikhosi yakwaSirayeli, wathi kuyo, Yini na ukuba niphume nalungiselela ukulwa kwenu? Andinguye na umFilisti, nabakhonzi bakho uSawule? Zinyuleleni indoda, ihle ize kum.', 'XHO22'),
(13, 17, 9, 'Ukuba ithe yanako ukulwa nam, yandibulala, soba ngabakhonzi kuni; ukuba ndithe ndayoyisa mna, ndayibulala, noba ngabakhonzi kuthi, nisikhonze.', 'XHO22'),
(13, 17, 10, 'Wathi umFilisti, Namhla ndiyawacekisa abathetheli bakwaSirayeli; ndinike indoda, silwe sobabini.', 'XHO22'),
(13, 17, 11, 'Baweva ooSawule namaSirayeli onke amazwi alo mFilisti, boyika, boyika kunene.', 'XHO22'),
(13, 17, 12, 'uDavide ubengunyana waloo mEfrata waseBhetelehem yakwaYuda, ogama belinguYese; ubenoonyana abasibhozo, ibe ke indoda leyo ibiyindoda enkulu phakathi kwabantu ngexesha likaSawule.', 'XHO22'),
(13, 17, 13, 'Bahamba oonyana abathathu bakaYese abakhulu, bamlandela uSawule baya emfazweni; amagama oonyana bakhe abathathu ababeye emfazweni yayinguEliyabhi owamazibulo, noAbhinadabhi owesibini, noShama wesithathu.', 'XHO22'),
(13, 17, 14, 'uDavide wayengoyena mncinane, yaye amadoda amakhulu amathathu alandela uSawule.', 'XHO22'),
(13, 17, 15, 'uDavide ubehamba ebuya kuSawule, esiya kwalusa impahla emfutshane kayise eBhetelehem.', 'XHO22'),
(13, 17, 16, 'UmFilisti lowo wayesondela kusasa nangokuhlwa, wamvelela iintsuku ezimashumi mane.', 'XHO22'),
(13, 17, 17, 'Wathi uYese kuDavide unyana wakhe, Thabathela abakhuluwa bakho iefa yale ngqolowa egcadiweyo, nezonka ezi zilishumi, uzithumele eminqubeni kubakhuluwa bakho;', 'XHO22'),
(13, 17, 18, 'Uze ezi mpahla emfutshane zilishumi, uzizise kumthetheli-waka, ubone ukuba kuqhubeka ntoni na abazalwana bakho, uthabathe kubo isibambiso.', 'XHO22'),
(13, 17, 19, 'uSawule, nabo, namadoda onke akwaSirayeli, babekwintlambo kaEla, balwa namaFilisti.', 'XHO22'),
(13, 17, 20, 'Wavuka kusasa uDavide, wazishiya izimvu kumalusi, wazithabatha, wemka, njengoko abemwisele umthetho uYese; Yafika emhadini xa umkhosi uphumayo usiya edabini, wakhala edabini.', 'XHO22'),
(13, 17, 21, 'Kuba amaSirayeli namaFilisti ayemise imfazwe, umkhosi ukhangelene nomkhosi.', 'XHO22'),
(13, 17, 22, 'uDavide wayishiya inqwelo yakhe esandleni sabalindi, wabalekela emkhosini, wababulisa abazalwana bakhe.', 'XHO22'),
(13, 17, 23, 'Ngoxa wayesathetha nabo, kwathi gqi indoda elikroti, umFilisti waseGati, ogama linguGoliyati, evela ezintendelezweni zamaFilisti, yathetha kwalaa mazwi, waza uDavide weva.', 'XHO22'),
(13, 17, 24, 'Onke amadoda akwaSirayeli ayibona le ndoda, asaba ebusweni bayo yaye oyika kakhulu.', 'XHO22'),
(13, 17, 25, 'Athi amadoda akwaSirayeli, Niyibonile na laa ndoda inyukayo? wenyuka waya kuwadela amaSirayeli; yaye umntu oya kumbulala, ukumkani uya kumtyebisa ngobutyebi obuninzi, amnike intombi aze ayikhulule indlu kayise kwaSirayeli.', 'XHO22'),
(13, 17, 26, 'Wathetha uDavide kumadoda abemi phambi kwakhe, wathi, Ngubani na othe wabulala laa mFilisti, walisusa ihlazo lamaSirayeli? ngenxa yokuba ngubani na laa mFilisti ungalukanga ukuba ade ayidele imiyalelo yoThixo ophilayo?', 'XHO22'),
(13, 17, 27, 'Baphendula abantu ngokwelo lizwi, bathi, Kuya kwenjiwa nje kuloo mntu umbuleleyo.', 'XHO22'),
(13, 17, 28, 'Weva uEliyabhi, umkhuluwa wakhe, ethetha naloo madoda; Wavutha umsindo kaEliyabhi kuDavide, wathi, Ifike apha? Uyishiye nabani na laa mpahla imfutshane imbalwa entlango? Ndiyakwazi mna ukukhukhumala kwakho, nobubi bentliziyo yakho; ngokuba nihle nize kubona imfazwe.', 'XHO22'),
(13, 17, 29, 'Wathi uDavide, Ndenze ntoni na ngoku? Ngaba akukho sizathu?', 'XHO22'),
(13, 17, 30, 'Wamshiya, waya komnye, ethetha kwangokunjalo.', 'XHO22'),
(13, 17, 31, 'Aviwa amazwi awawathethayo uDavide, avakala kuSawule, wadanduluka kuye.', 'XHO22'),
(13, 17, 32, 'Wathi uDavide kuSawule, Makungathambi mntu ngenxa yakhe; umkhonzi wakho lo uya kuya alwe nalo mFilisti.', 'XHO22'),
(13, 17, 33, 'Wathi uSawule kuDavide, Akunako ukumhlasela laa mFilisti umlwe naye; ngokuba usengumfana, ukanti ubeyindoda elwayo kwasebuncinaneni bakhe.', 'XHO22'),
(13, 17, 34, 'Wathi uDavide kuSawule, Umkhonzi wakho ebesalusa impahla emfutshane kayise, kwafika ingonyama nebhere, yathabatha ixhwane emhlambini.', 'XHO22'),
(13, 17, 35, 'Ndaphuma ke, ndayilandela, ndayibetha, ndayigcina emlonyeni wayo; yathi yesuka phezu kwam, ndayibamba ngodevu lwayo, ndayibetha, ndayibulala.', 'XHO22'),
(13, 17, 36, 'Umkhonzi wakho ubulele ingonyama nebhere, yaye lo mFilisti usiyatha uya kuba njengenye yazo, ngenxa yokuba uyidelile imikhosi yoThixo ophilayo.', 'XHO22'),
(13, 17, 37, 'Wathi uDavide, uYehova owandihlangulayo egazini lengonyama nasegazini lebhere, nguye oya kundihlangula esandleni saloo mFilisti. Wathi uSawule kuDavide, Hamba ke, uYehova abe nawe.', 'XHO22'),
(13, 17, 38, 'uSawule wamambathisa uDavide iingubo zakhe, wabeka isigcina-ntloko sobhedu entloko; naye exhobe ngesixhobo.', 'XHO22'),
(13, 17, 39, 'uDavide walibopha ikrele lakhe esikrweqeni sakhe, wazama ukuhamba; ngokuba engangqinanga. Wathi uDavide kuSawule, Andinako ukuhamba nawo; kuba andizange ndizame. uDavide wazithabatha kuye.', 'XHO22'),
(13, 17, 40, 'Wathabatha umsimelelo wakhe ngesandla sakhe, wazinyulela amatye amahlanu agudileyo kwintlambo yomlambo, wawafaka engxoweni engxoweni yakhe yomalusi; isilingi sakhe sisesandleni sakhe, waya kumFilisti.', 'XHO22'),
(13, 17, 41, 'Weza umFilisti, waya kuDavide; indoda ephethe ikhaka yayihamba phambi kwakhe.', 'XHO22'),
(13, 17, 42, 'Xa umFilisti wasingasinga waza wambona uDavide, wamdelela, ngenxa yokuba umfana ebomvu, enembonakalo entle.', 'XHO22'),
(13, 17, 43, 'Wathi umFilisti kuDavide, Ndiyinja na, uze kum uneentonga? UmFilisti wamqalekisa uDavide ngoothixo bakhe.', 'XHO22'),
(13, 17, 44, 'Wathi umFilisti kuDavide, Yiza kum apha, ndiyinike iintaka zezulu namarhamncwa asendle inyama yakho.', 'XHO22'),
(13, 17, 45, 'Wathi uDavide kumFilisti, Wena uza kum unekrele, nentshuntshe, nekhaka; ke mna ndiza kuwe egameni likaYehova wemikhosi, uThixo wemikhosi kaSirayeli.', 'XHO22'),
(13, 17, 46, 'Namhlanje uYehova uya kukunikela esandleni sam; yaye ngokuqinisekileyo ndiya kukoyisa, ndiyinqumle intloko yakho; namhla ndizinike iintaka zezulu namarhamncwa asendle izidumbu zemikhosi yamaFilisti; ukuze ihlabathi lonke lazi ukuba kukho uThixo kwaSirayeli.', 'XHO22'),
(13, 17, 47, 'Liya kwazi lonke eli bandla, ukuba uYehova akahlanguli ngakrele nantshuntshe; ngokuba imfazwe yekaYehova, woninikela esandleni sethu.', 'XHO22'),
(13, 17, 48, 'Akugqiba umFilisti, waya kukhawulela uDavide, wakhawuleza uDavide, wagidima waya emkhosini, esiya kukhawulela umFilisti lowo.', 'XHO22'),
(13, 17, 49, 'uDavide wafaka isandla sakhe epokothweni yakhe, warhola ilitye, waliphosa, wambetha umFilisti leyo ebunzi; wawa ngobuso.', 'XHO22'),
(13, 17, 50, 'uDavide wamxabela umFilisti ngenkwitshi nangelitye, wamhlaba umFilisti lowo, wambulala; ke kungekho krele esandleni sikaDavide.', 'XHO22'),
(13, 17, 51, 'Wagidima ke uDavide, wema ecaleni komFilisti, walithabatha ikrele lakhe, walirhola eludinini lwalo, wambulala, wamnqumla intloko ngalo. Abona ke amaFilisti ukuba lifile igorha lawo, asaba.', 'XHO22'),
(13, 17, 52, 'Amadoda akwaSirayeli nakwaYuda azilungisa, amemeza, awasukela amaFilisti, ada aya kufika entilini yoMnayile, aya kufikelela emasangweni ase-Ekron. Yaye abo bawoyisayo kumaFilisti bawela emhlabeni endleleni eya eSarayim ukuya kufikelela eGati nokuya kufikelela e-Ekron.', 'XHO22'),
(13, 17, 53, 'Babuya oonyana bakaSirayeli ekuwasukeleni amaFilisti, baziphanga iintente zawo.', 'XHO22'),
(13, 17, 54, 'uDavide wayithabatha intloko yomFilisti, wayisa eYerusalem; isikrweqe sakhe wasibeka ententeni yakhe.', 'XHO22'),
(13, 17, 55, 'uSawule wambona uDavide emxabela umFilisti, wathi kuAbhinere umthetheli-mkhosi, Ngunyana kabani na laa mfana, Abhinere? Wathi uAbhinere, Ewe, kumkani, uhleli umphefumlo wakho.', 'XHO22'),
(13, 17, 56, 'Wathi ukumkani, Niyabuza ukuba ngunyana kabani na lo?', 'XHO22'),
(13, 17, 57, 'Ekubuyeni kukaDavide ekumbulaleni umFilisti, uAbhinere wamthabatha, wamzisa phambi koSawule, ephethe intloko yomFilisti ngesandla sakhe.', 'XHO22'),
(13, 17, 58, 'Wathi uSawule kuye, Ungunyana kabani na, mfanandini? Waphendula uDavide, wathi, Ndingunyana womkhonzi wakho uYese waseBhetelehem.', 'XHO22'),
(13, 18, 1, 'Kwathi, akugqiba ukuthetha noSawule, umoya kaYonatan wanamathela entliziyweni kaDavide; uYonatan wamthanda njengokuzithanda kwakhe.', 'XHO22'),
(13, 18, 2, 'uSawule wamthabatha ngaloo mini, akamvumela ukuba abuyele endlwini kayise.', 'XHO22'),
(13, 18, 3, 'Benza umnqophiso ooYonatan noDavide, ngokumthanda kwakhe njengoko ebezithanda.', 'XHO22'),
(13, 18, 4, 'uYonatan wayihluba ingubo yokwaleka abenayo, wayinika uDavide, neengubo zakhe zokulwa, kwesa ekreleni lakhe, kwesa esingxotyeni sakhe, kwesa embhinqweni wakhe.', 'XHO22'),
(13, 18, 5, 'Waphuma uDavide, waya apho wathunywa khona nguSawule, wenza ngengqiqo; uSawule wamenza umphathi wamadoda okulwa. Wakholeka emehlweni abantu bonke, nasemehlweni abakhonzi bakaSawule.', 'XHO22'),
(13, 18, 6, 'Ekufikeni kwabo, xa uDavide wabuyayo ekuxabeleni umFilisti, abafazi beza bevela kuzo zonke izixeko zakwaSirayeli, bevuma ingoma yaye bengqungqa ukuya kuhlangabeza uKumkani uSawule ngamagubu ngovuyo nangeempahla zokuvuma.', 'XHO22'),
(13, 18, 7, 'Abafazi baphendulana, babizana, bathi, uSawule ubulele amawaka akhe, uDavide ubulele amashumi akhe amawaka.', 'XHO22'),
(13, 18, 8, 'Wavutha kunene ngumsindo uSawule; uthi, uDavide bamnika ishumi lamawaka, kodwa bandinika iwaka lodwa; kwaye yintoni anokuyizuza ngaphezu korhulumente?', 'XHO22'),
(13, 18, 9, 'uSawule waqhubeka ekhangele kuDavide ukususela loo mini.', 'XHO22'),
(13, 18, 10, 'Ngengomso umoya ombi kaThixo wamfikela uSawule, waprofeta endlwini; Wawangawangisa ngesandla uDavide njengokwangaphambili; intshuntshe isesandleni sikaSawule.', 'XHO22'),
(13, 18, 11, 'uSawule wabinza ngentshuntshe; ngokuba wathi, Ndiya kumbethelela eludongeni uDavide. uDavide wamshiya kwada kwakabini.', 'XHO22'),
(13, 18, 12, 'uSawule wamoyika uDavide, ngokuba uYehova ubenaye, wamshiya uSawule.', 'XHO22'),
(13, 18, 13, 'Ngenxa yoko uSawule wamsusa kuye, wammisa waba ngumthetheli wewaka; Waphuma wakhawuleza waya ebantwini.', 'XHO22'),
(13, 18, 14, 'uDavide wenza ngengqiqo kuzo zonke iindlela zakhe; uYehova waba naye.', 'XHO22'),
(13, 18, 15, 'Ngoko ke, xa uSawule wabonayo ukuba wayesenza ngobulumko kakhulu, wamoyika.', 'XHO22'),
(13, 18, 16, 'Ke onke amaSirayeli namaYuda ebemthanda uDavide, ngokuba ebengena ephuma phambi kwawo.', 'XHO22'),
(13, 18, 17, 'Wathi uSawule kuDavide, Yabona, uMerabha, intombi yam enkulu, ndiya kunika ibe ngumkakho; kodwa yibani nesibindi ngenxa yam, nilwe iimfazwe zikaYehova ngokuba wathi uSawule, Masingalwi isandla sam kuye, masiye kuye izandla zamaFilisti.', 'XHO22'),
(13, 18, 18, 'Wathi uDavide kuSawule, Ndingubani na? Buyintoni na ubomi bam, nemizalwane kabawo kwaSirayeli, ukuba ndibe ngumyeni kukumkani?', 'XHO22'),
(13, 18, 19, 'Kodwa ngexesha uMerabhe intombi kaSawule eya kunikelwa kuDavide, wanikwa uAdriyeli waseMehola, ukuba abe ngumfazi wakhe.', 'XHO22'),
(13, 18, 20, 'uMikali, intombi kaSawule, ubemthanda uDavide;', 'XHO22'),
(13, 18, 21, 'Wathi uSawule, Ndiya kumnika yona ibe ngumgibe kuye, sibe phezu kwakhe isandla samaFilisti. Wathi uSawule kuDavide, Yena uya kuba ngumyeni kum namhla kwenye kuzo zombini.', 'XHO22'),
(13, 18, 22, 'uSawule wabawisela abakhonzi bakhe umthetho, wathi, Thethani kuDavide ngasese, nithi, Yabona, ukumkani ukunanzile, nabakhonzi bakhe bonke bayakuthanda; kaloku yiba ngumyeni kukumkani.', 'XHO22'),
(13, 18, 23, 'Bawathetha abakhonzi bakaSawule loo mazwi, ephulaphule uDavide. Wathi uDavide, Yinto encinane na le ukuba ube ngumyeni kukumkani; mna kuthiwa ndingomncinane?', 'XHO22'),
(13, 18, 24, 'Bamxelela abakhonzi bakaSawule, besithi, uDavide uthe.', 'XHO22'),
(13, 18, 25, 'Wathi uSawule, Yiyani kuDavide, nithi, Ukumkani akabunqweneli ubukumkani; lilikhulu leemfele zamaFilisti, ukuze aziphindezelele kubakhonzi bokumkani. Kodwa uSawule wayezimisele ukumyeka uDavide abulawe ngamaFilistiya.', 'XHO22'),
(13, 18, 26, 'Abakhonzi bakhe bamxelela uDavide loo mazwi; uDavide wakuvuyela ukuba ngumyeni kukumkani, zibe zingekapheli iintsuku.', 'XHO22'),
(13, 18, 27, 'Waphuma ke uDavide, wahamba, yena namadoda akhe, wabulala kumaFilisti amakhulu amabini amadoda; uDavide wazizisa iintsuba zabo, bamxelela ukumkani zonke iindaba ezo, ukuze abe ngumyeni kukumkani. uSawule wamnika ke uMikali intombi yakhe, yangumkakhe.', 'XHO22'),
(13, 18, 28, 'Wakubona uSawule, wakwazi ukuba uYehova ebenoDavide, nokuba uMikali, intombi kaSawule, ubemthanda.', 'XHO22'),
(13, 18, 29, 'uSawule wamoyika okunye uDavide; yaye uSawule waba lutshaba lukaDavide kuyo yonke indawo.', 'XHO22'),
(13, 18, 30, 'Zandula ke iinkosana zamaFilisti zemka, yaye emva kokuba zihambile, uDavide waqhubeka esenza ngengqiqo ngaphezu kwabo bonke abakhonzi bakaSawule; ukuze igama lakhe libe likhulu.', 'XHO22'),
(13, 19, 1, 'Wathetha uSawule kuYonatan, unyana wakhe, nakubakhonzi bakhe bonke, ukuba bambulale uDavide.', 'XHO22'),
(13, 19, 2, 'uYonatan unyana kaSawule ubekholekile kunene kuDavide; uYonatan wamxelela uDavide, esithi, Uyise uSawule ufuna ukukubulala.', 'XHO22'),
(13, 19, 3, 'Mna ndiya kuphuma ndime nobawo endle, apho ukhona, ndithethe ngawe kubawo; kwaye endikubonayo ndiya kukuxelela.', 'XHO22'),
(13, 19, 4, 'uYonatan wathetha okulungileyo ngoDavide kuSawule uyise, wathi kuye, uYehova makangoni kumkhonzi wakhe uDavide; ngokuba akonanga kuwe, nangenxa yokuba imisebenzi yakhe ibilungile kakhulu kuwe.', 'XHO22'),
(13, 19, 5, 'Wawunikela umphefumlo wakhe esandleni sakhe, wambulala umFilisti. uYehova wawazisa usindiso olukhulu amaSirayeli onke; wabona, wavuya; Yini na ukuba ube netyala legazi elimsulwa, ngokubulala nje uDavide?', 'XHO22'),
(13, 19, 6, 'Waliphulaphula uSawule ilizwi likaYonatan, wafunga uSawule, wathi, Ova uYehova akayi kubulawa.', 'XHO22'),
(13, 19, 7, 'uYonatan wambiza uDavide, uYonatan wamxelela la mazwi onke. uYonatan wamzisa uDavide kuSawule, wahlala ecaleni kwakhe njengangaphambili.', 'XHO22'),
(13, 19, 8, 'Kwabuya kwabakho imfazwe, waphuma uDavide, walwa namaFilisti, wawabulala kakhulu; asaba ebusweni bakhe.', 'XHO22'),
(13, 19, 9, 'Umoya ombi kaYehova wamfikela uSawule ehleli endlwini yakhe, ephethe umkhonto ngesandla, ebetha uDavide ngesandla sakhe.', 'XHO22'),
(13, 19, 10, 'uSawule wazama ukumxhoma ngentshuntshe uDavide eludongeni; Wesuka ebusweni bukaSawule, waluhlaba udonga ngomkhonto wakhe; Wasaba ke uDavide ngobo busuku.', 'XHO22'),
(13, 19, 11, 'Wabuya uSawule wathumela abathunywa endlwini kaDavide ukuba bamlinde, bambulale kusasa; uMikale, umkaDavide, wamxelela, wathi, Ukuba uthe akwawusindisa umphefumlo wakho ngobu busuku, wobulawa ngomso.', 'XHO22'),
(13, 19, 12, 'uMikayeli wamhlisa uDavide ngefestile, wasaba, wasaba.', 'XHO22'),
(13, 19, 13, 'Emva koko uMigale wawuthabatha umfanekiso womfanekiso wawubeka phezu kwebhedi waza wabeka umqamelo woboya bebhokhwe phezu kwelaphu lawo waza wawugubungela ngelaphu.', 'XHO22'),
(13, 19, 14, 'uSawule wasusa abathunywa ukuba baye kumphuthuma uDavide, wathi yena, Uyafa.', 'XHO22'),
(13, 19, 15, 'uSawule waphinda wathuma abathunywa ukuba baye kubona uDavide, esithi, Mziseni kum ngokhuko, ndimbulale.', 'XHO22'),
(13, 19, 16, 'Bakungena abathunywa, bawubona umfanekiso lowo usemandlalweni, nomqamelo woboya bebhokhwe engutyeni yawo.', 'XHO22'),
(13, 19, 17, 'Wathi uSawule kuMikali: “Kutheni na ukuba undixokise ngolu hlobo, ulundulule utshaba lwam, lwasinda lona? uMikali wamphendula uSawule, wathi, Wathi yena kum, Ndiyeke ndihambe; kutheni ndikubulale.', 'XHO22'),
(13, 19, 18, 'Ebalekile uDavide, wasinda, waya kuSamuweli eRama, wamxelela konke abekwenzile uSawule kuye. Wahamba ke yena noSamuweli, baya bahlala eNayoti.', 'XHO22'),
(13, 19, 19, 'Waxelelwa uSawule, kwathiwa, Nanku uDavide eNayoti ngaseRama.', 'XHO22'),
(13, 19, 20, 'uSawule wasusa abathunywa ukuba baye kumphuthuma uDavide; Babona into eninzi yabaprofeti iprofeta, inguSamuweli phezu kwabo, wehla uMoya kaThixo phezu kwabathunywa bakaSawule, baprofeta kananjalo nabo.', 'XHO22'),
(13, 19, 21, 'Waxelelwa uSawule, wasusa abanye abathunywa, baprofeta kwangokunjalo naye. uSawule waphinda wathuma abathunywa okwesithathu, baprofeta kananjalo nabo.', 'XHO22'),
(13, 19, 22, 'Naye waya eRama, wafika equleni elikhulu laseSegu; wabuza wathi, Baphi na ooSamuweli noDavide? Wathi omnye, Khangela, baneNayoti, iRama.', 'XHO22'),
(13, 19, 23, 'Waya apho uNayoti eRama, yaye umoya kaThixo waba phezu kwakhe, yaye waqhubeka eprofeta de waya kufika eNayoti eRama.', 'XHO22'),
(13, 19, 24, 'Wazikhulula naye iingubo zakhe, waprofeta kwangokunjalo phambi koSamuweli, walala eze imini yonke, nobusuku bonke. Bathi ke, Ungomnye wabaprofeti noSawule na?', 'XHO22'),
(13, 20, 1, 'Wabaleka uDavide eNayoti ngaseRama, weza, wathi kuYonatan, Ndenze ntoni na? yintoni ingxaki yam Siyini na isono sam phambi koyihlo, ukuba ufuna ukundibulala?', 'XHO22'),
(13, 20, 2, 'Wathi kuye, Hayi, Thixo; aniyi kufa; yabona, ubawo akazange enze nantoni na enkulu, nokuba incinci, amazise; kanti ubawo ungandifihlela lo mcimbi? Akunjalo.', 'XHO22'),
(13, 20, 3, 'Wabuya wafunga uDavide, esithi, Uyihlo uyazi okunene, ngokuba ndinenceba kuwe; Wathi, Makangazi uYonatan, hleze abe buhlungu; kodwa eneneni ngoYehova ophilayo nomphefumlo wakho uphila, linyathelo elinye kuphela phakathi kwam nokufa.', 'XHO22'),
(13, 20, 4, 'Wathi uYonatan kuDavide, Yonke into oyibizayo umphefumlo wakho, ndoyenza kuwe.', 'XHO22'),
(13, 20, 5, 'Wathi uDavide kuYonatan, Yabona, iya kuthwasa inyanga ngomso; ndindulule ndiye kuzifihla endle, kude kuhlwe ngomhla wesithathu.', 'XHO22'),
(13, 20, 6, 'Ukuba uyihlo uthe wandifuna, wothi wena, Ucele wandibongoza uDavide ukuba ndihambe, ndibaleke ndiye eBhetelehem kowabo; ngokuba kukho umbingelelo womnyaka wemizalwane yonke;', 'XHO22'),
(13, 20, 7, 'Ukuba uthe, Kulungile; Umkhonzi wakho lo uya kuba seluxolweni; ke ukuba uthe wavutha kakhulu ngumsindo, yazi ukuba ucebe ububi.', 'XHO22'),
(13, 20, 8, 'Ngoko ke, menzele inceba umkhonzi wakho; ngokuba umngenise emnqophisweni kaYehova umkhonzi wakho lo nawe; ngoba ungathini ukundisa kuyihlo?', 'XHO22'),
(13, 20, 9, 'Wathi uYonatan, Makube lee kuwe; ukuba ndibe ndibazi ububi acinga ukukwehlela ubawo, ndinge ndixelelwe na?', 'XHO22'),
(13, 20, 10, 'Wathi uDavide kuYonatan, Ngubani na oya kundixelela ezi zinto? okanye kuthekani ukuba uyihlo ukuphendule ngelizwi elikhulu?', 'XHO22'),
(13, 20, 11, 'Wathi uYonatan kuDavide, Hamba siye ezweni. Baphuma bobabini baya emaphandleni.', 'XHO22'),
(13, 20, 12, 'Wathi uYonatan kuDavide, Yehova, Thixo kaSirayeli, ukuba ndithe ndambiza ubawo ngomso nokuba ngowesithathu umhla, ndaza ndabona ukuba ulungile uDavide, andathumela kuwe ukuba ndikubonise oko;', 'XHO22'),
(13, 20, 13, 'Wenjenjalo uYehova kuYonatan, ongezelele; ukuba ubawo uthe wafuna ukunenza okubi, ndonazisa, ndinikhulule, nihambe ninoxolo; uYehova abe nawe, njengokuba ebenobawo.', 'XHO22'),
(13, 20, 14, 'Aniyi kundenzela nceba uYehova ndisadla ubomi nje, ukuze ndingafi.', 'XHO22'),
(13, 20, 15, 'Kodwa ubabalo lwakho aluyi kucima endlwini yam ngonaphakade;', 'XHO22'),
(13, 20, 16, 'Wanqophisana ke uYonatan nendlu kaDavide, esithi, uYehova makawanikele esandleni seentshaba zikaDavide.', 'XHO22'),
(13, 20, 17, 'Waphinda uYonatan ukumfungela uDavide ngokumthanda kwakhe, ngokuba ebemthanda njengomphefumlo wakhe.', 'XHO22'),
(13, 20, 18, 'Wathi uYonatan kuDavide, Ngomso iya kuphuma inyanga, usweleke, ngokuba isihlalo sakho siya kuba sodwa.', 'XHO22'),
(13, 20, 19, 'Ukuba nithe nahlala iintsuku ezintathu, khawulezani nihle, nize kuloo ndawo ubuzimele kuyo, lisaqhubeka elo lizwi, uhlale ngasetyeni le-esile;', 'XHO22'),
(13, 20, 20, 'Ndiza kutolela ecaleni kwalo iintolo zibe ntathu, ngokungathi ndizidubula ngaxeshanye.', 'XHO22'),
(13, 20, 21, 'Yabona, ndiya kuthuma umfana, wathi, Hamba uye kuzifuna iintolo. Ukuba ndithe kwinkwenkwe, Nanzo iintolo zingaphaya kwakho, zithabathe; uze ke, ngokuba uxolo lunawe, akukho ngozi; NgoYehova ophilileyo.', 'XHO22'),
(13, 20, 22, 'Ke ukuba ndithe kwindodana leyo, Nanzo iintolo zingaphaya kwakho; hamba, ngokuba uYehova ukuthumile.', 'XHO22'),
(13, 20, 23, 'Ke yona into esiyithethileyo mna nawe, yabona, uYehova uphakathi kwam nawe kude kuse ephakadeni.', 'XHO22'),
(13, 20, 24, 'uDavide wamngcwabela entsimini, kwathi ekuphumeni kwenyanga, wahlala ukumkani ukuba adle ukudla.', 'XHO22'),
(13, 20, 25, 'Ukumkani wahlala esihlalweni sakhe, njengokwangaphambili, esitulweni esingaseludongeni;', 'XHO22'),
(13, 20, 26, 'Akathetha nto uSawule ngaloo mini, ngokuba wathi: “Uhlelwe yinto embi, akahlambulukanga; Ngokuqinisekileyo akacocekanga.', 'XHO22'),
(13, 20, 27, 'Kwathi ngengomso, ngomhla wesibini wenyanga, ayaba namntu indawo kaDavide. Wathi uSawule kuYonatan unyana wakhe, Yini na ukuba angezanga kudla unyana kaYese izolo nanamhlanje?', 'XHO22'),
(13, 20, 28, 'uYonatan wamphendula uSawule, wathi, uDavide ucele ngokunyamekileyo ukuba ndiye eBhetelehem;', 'XHO22'),
(13, 20, 29, 'Wathi, Ndindulule, ngokuba kukho umbingelelo wemizalwane yakowethu ekhaya; wandiwisela umthetho ke umkhuluwa wam, wathi, Mandiye kubona abazalwana bam ngoko, ukuba ndibabalwe nguwe kutheni angezi etafileni yokumkani.', 'XHO22'),
(13, 20, 30, 'uSawule wavutha ngumsindo kuYonatan, wathi kuye, Nyanandini wendoda engendawo, enenjubaqa, andazi na ukuba kutheni na ukunyula unyana kaYese ukuba abe ngumphangi wakho, nehlazo lokukreqa kukanyoko?', 'XHO22'),
(13, 20, 31, 'Kuba yonke imihla unyana kaYese aya kudla ubomi ngayo emhlabeni, wena nobukumkani bakho akuyi kuqiniseka. Thumela ngoko, umzise kum, ngokuba inene uya kufa.', 'XHO22'),
(13, 20, 32, 'uYonatan wamphendula uSawule uyise, wathi kuye, Yini na ukuba ubulawe? nzeni.', 'XHO22'),
(13, 20, 33, 'uSawule wamjula ngomkhonto ukuba amgwaze; waqonda uYonatan ukuba uyise uceba ukumbulala uDavide.', 'XHO22'),
(13, 20, 34, 'Wema uYonatan etafileni enomsindo omkhulu; ukanti ngomhla wesibini wenyanga leyo, akadla kudla, ngokuba ubebuhlungu ngenxa kaDavide, ngokuba ebemhlazisile uyise.', 'XHO22'),
(13, 20, 35, 'Kwathi kusasa, uYonatan waya ezweni ngexesha elimisiweyo noDavide, nenkwenkwe.', 'XHO22'),
(13, 20, 36, 'Wathi kunyana wakhe, Gidima, uzikhangele iintolo endizixunayo. Yabaleka inkwenkwe leyo, yadubula utolo ecaleni kwayo.', 'XHO22'),
(13, 20, 37, 'Wafika umfana endaweni abedla kuyo uYonatan, uYonatan wambiza umfana emva kwakhe, wathi, Nalo ngaphaya kwakho utolo?', 'XHO22'),
(13, 20, 38, 'Wamemeza uYonatan kumfana, wathi, Khawuleza, khawuleza, musa ukulibala. Unyana kaYonatan wathabatha iintolo, weza enkosini yakhe.', 'XHO22'),
(13, 20, 39, 'Ke yena umntwana ubengazi nto; nguYonatan noDavide ababeyazi loo nto.', 'XHO22'),
(13, 20, 40, 'uYonatan wamnika umpu wakhe unyana wakhe, wathi kuye, Hamba, ubasa kuwo umzi.', 'XHO22'),
(13, 20, 41, 'Ekundulukeni kwenkwenkwe leyo, wema uDavide kwindawo engasemzantsi, yawa ngobuso yaza yaqubuda izihlandlo ezithathu;', 'XHO22'),
(13, 20, 42, 'Wathi uYonatan kuDavide, Hamba unoxolo, ngokuba safunga egameni likaYehova, sathi, uYehova makabe phakathi kwam nawe kude kuse ephakadeni, naphakathi koonyana bam noonyana bakho. Wemka uYonatan, wahamba, wangena uYonatan esixekweni.', 'XHO22'),
(13, 21, 1, 'Weza uDavide kuAhimeleki, umbingeleli waseNobhi. uAhimeleki wagubha phambi koDavide, wathi kuye, Yintoni na le nto uhlala wedwa, ungenamntu?', 'XHO22'),
(13, 21, 2, 'Wathi uDavide kuAhimeleki, umbingeleli, uYehova undinike ilizwi, wathi kum, Makungabikho mntu uyaziyo le nto ndikuthumayo, ndikuwisele umthetho ngayo; ndabeka abakhonzi bam ndaweni ithile.', 'XHO22'),
(13, 21, 3, 'Ngoko yintoni ephantsi kwengalo yakho? ndinike esandleni izonka ezihlanu, nokuba yintoni na ekhoyo.', 'XHO22'),
(13, 21, 4, 'Umbingeleli wamphendula uDavide, wathi, Akukho sonka sisesandleni sam, sodwa esingcwele; ukuba abafana ubuncinane bahlala kude kubafazi.', 'XHO22'),
(13, 21, 5, 'uDavide wamphendula umbingeleli, wathi kuye, Yinyaniso ukuba, ezi ntsuku zintathu ndiphumileyo, iinkazana bezingavumelekile kuthi, izitya zamadodana zingcwele, nesonka siyinto eqhelekileyo, noko kunjalo. Kunamhlanje ke isitya singcwaliswa.', 'XHO22'),
(13, 21, 6, 'Umbingeleli wamnika izonka ezingcwele, ngokuba bekungekho zonka khona, ingesiso izonka zokubonisa, eziziswe phambi koYehova, ukuze kubekwe izonka ezishushu ngemini yokundululwa kwaso.', 'XHO22'),
(13, 21, 7, 'Kwaye kukho omnye wakubakhonzi bakaSawule ngaloo mini, ebanjwe phambi koYehova; igama lakhe lalinguDowegi, umEdom, umthetheli wabalusi bakaSawule.', 'XHO22'),
(13, 21, 8, 'Wathi uDavide kuAhimeleki, Ndathi, Akukho mkhonto nakrele esandleni sakho? kuba ikrele lam okanye izixhobo zam andilizisanga, ngenxa yokuba umsebenzi kakumkani wawungxamisekile.', 'XHO22'),
(13, 21, 9, 'Wathi umbingeleli, Ikrele likaGoliyati umFilisti, owambeyisa entilini kaEla, nali emva kwengubo ende egxalabeni; ukuba uyafuna ukulithabatha, lithabathe; kuba akukho mntu apha. Wathi uDavide, Akunjalo; Yinike mna.', 'XHO22'),
(13, 21, 10, 'Wabaleka uDavide kwangaloo mini ngenxa yokoyika uSawule, waya kuAkishe, ukumkani waseGati.', 'XHO22'),
(13, 21, 11, 'Bathi kuye abakhonzi baka-Akishe, Lo asinguye na uDavide ukumkani wehlabathi? Abavumanga ngaye na, besithi, uSawule ubulele amawaka akhe, uDavide ubulele amawaka akhe?', 'XHO22'),
(13, 21, 12, 'uDavide wawabeka la mazwi entliziyweni yakhe, wamoyika kunene uAkishe ukumkani waseGati.', 'XHO22'),
(13, 21, 13, 'Waziguqula iindlela zakhe phambi kwabo, wazenza isidenge esandleni sabo, wazonwaya iingcango zesango, wawatshicela ezindevini zakhe.', 'XHO22'),
(13, 21, 14, 'Wathi uAkishe kubakhonzi bakhe, Khangelani, niyabona ukuba le ndoda isisihiba; Khona kutheni nimzisa kum?', 'XHO22'),
(13, 21, 15, 'Ndiswele impambano na, le nto niyizise le ndoda phambi kwam? Izakungena endlini yam landoda?', 'XHO22'),
(13, 22, 1, 'Wemka apho ke uDavide, wabalekela emqolombeni waseAdulam; Beva abazalwana bakhe nendlu yakhe yonke, behla baya kuye.', 'XHO22'),
(13, 22, 2, 'Abuthelana kuye onke amadoda abandezelekileyo, namadoda onke anamatyala, namadoda angahluthanga; waba ngumlawuli wabo, ekunye namadoda akumakhulu amane.', 'XHO22'),
(13, 22, 3, 'Wemka apho ke uDavide, waya eMizpe yakwaMowabhi, wathi kukumkani wakwaMowabhi, Ubawo noma makaphume ahlale nawe, ndide ndiyazi into aya kundenzela yona uThixo.', 'XHO22'),
(13, 22, 4, 'Wabazisa phambi kokumkani wakwaMowabhi, bahlala naye yonke imihla abesemboniselweni uDavide entabeni.', 'XHO22'),
(13, 22, 5, 'Wathi umprofeti uGadi kuDavide, Musa ukuhlala endaweni yonqaba ezintabeni; hamba, uye ezweni lakwaYuda. Emva koko uDavide wemka waya kwihlathi laseHereti.', 'XHO22'),
(13, 22, 6, 'Xa uSawule wevayo ukuba uDavide namadoda awayekunye naye bafikile, ngoko uSawule wahlala eGibheya phantsi komthi weRama, ephethe umkhonto wakhe esandleni sakhe, nabo bonke abakhonzi bakhe bemi ngakuye.', 'XHO22'),
(13, 22, 7, 'Wathi uSawule kubakhonzi bakhe ababemi ngakuye, Phulaphulani, maBhenjamin! Unyana kaYese uya kuninika elowo amasimi nezidiliya, animise nonke nibe ngabathetheli-waka nabathetheli-makhulu;', 'XHO22'),
(13, 22, 8, 'ngenxa enokuba ninditshabhise nje nonke, kungekho namnye undixeleleyo ukuba unyana wam uzimase unyana kaYese, akwabakho namnye uvelana nam, engandixelelanga ukuba ukhuliswe ngunyana wam. Undivukile ke umkhonzi, ukuze akrobe njenganamhla?', 'XHO22'),
(13, 22, 9, 'Wathi uDowegi umEdom, obephethe abakhonzi bakaSawule, Ndimbonile unyana kaYese eNobhi, kuAhimeleki, unyana ka-Ahitubhi.', 'XHO22'),
(13, 22, 10, 'Wambuza kuYehova, wamnika ukudla, wamnika nekrele likaGoliyati umFilisti.', 'XHO22'),
(13, 22, 11, 'Wathumela ukumkani, wambiza uAhimeleki unyana ka-Ahitubhi, umbingeleli, nendlu yonke kayise, ababingeleli ababeseNobhe; beza bonke kukumkani.', 'XHO22'),
(13, 22, 12, 'Wathi uSawule: “Phulaphula, nyana ka-Ahitubhi! Waphendula ke yena wathi, Ndikho, nkosi yam.', 'XHO22'),
(13, 22, 13, 'Wathi uSawule kuye, Yini na ukuba undithande, wena nonyana kaYese, umnike isonka nekrele, uthandaze kuThixo ukuba andivuse, ndilale? linda, njenganamhlanje?', 'XHO22'),
(13, 22, 14, 'uAhimeleki wamphendula ukumkani, wathi, Ngubani na kubakhonzi bakho bonke othembeke njengoDavide, umyeni wokumkani, ohamba ngokwesiko lakho, ozukileyo endlwini yakho?', 'XHO22'),
(13, 22, 15, 'Ndaqala na ngoko ukumbuza kuThixo? Ukumkani makangathwali nto umkhonzi wakhe, nakwindlu yonke kabawo, ngokuba umkhonzi wakho ebengazi nto ngayo, nokuba kuncinane.', 'XHO22'),
(13, 22, 16, 'Wathi ukumkani, Uya kufa, Ahimeleki, wena nendlu yakho yonke.', 'XHO22'),
(13, 22, 17, 'Wathi ukumkani kwababemi ngeenyawo nabamlindile, Jikani, nibabulale ababingeleli bakaYehova; ngokuba nesabo isandla siphezu koDavide, nangokuba babesazi oko wabalekayo yena, bengandazisanga. Kodwa abakhonzi bakakumkani bala ukolula izandla zabo ukuze balwe nababingeleli bakaYehova.', 'XHO22'),
(13, 22, 18, 'Wathi ukumkani kuDowegi, Jika, uxabele ababingeleli. Wajika uDoege wakwaEdom, wababulala ababingeleli, wabulala amashumi asibhozo anababini abantu ngaloo mini, bambethe ilinen emhlophe.', 'XHO22'),
(13, 22, 19, 'NoNobhe isixeko sababingeleli wayibulala ngohlangothi lwekrele, amadoda nabafazi, abantwana neentsana, iinkomo, amaesile nezimvu ngohlangothi lwekrele.', 'XHO22'),
(13, 22, 20, 'Omnye woonyana baka-Ahimeleki unyana ka-Akitubhi, ogama linguAbhiyatare, wasinda waza wabaleka emva koDavide.', 'XHO22'),
(13, 22, 21, 'uAbhiyatare wamxelela uDavide ukuba uSawule ubabulele ababingeleli bakaYehova.', 'XHO22'),
(13, 22, 22, 'Wathi uDavide kuAbhiyatare, Bendisazi kwangaloo mini, ukuba, ekho nje uDowegi umEdom, uya kuthi kanye kuSawule, Ndibabulele bonke abantu bendlu kayihlo.', 'XHO22'),
(13, 22, 23, 'Hlala nam, musa ukoyika; ngokuba ofuna umphefumlo wam ufuna umphefumlo wakho; kodwa ngam niya kusindiswa.', 'XHO22'),
(13, 23, 1, 'Kwaxelwa kuDavide, kwathiwa, Nanga amaFilisti ehlasela iKehila, ephanga amasimi.', 'XHO22'),
(13, 23, 2, 'Wabuza uDavide kuYehova, wathi, Ndiye na ndiwabulale laa maFilisti? Wathi uYehova kuDavide, Hamba uye kuwaxabela amaFilisti, usindise iKehila.', 'XHO22'),
(13, 23, 3, 'Athi amadoda kaDavide kuye, Nanku thina sisoyika, sikwaYuda apha; Singangena njani eKehila siye kuhlasela imikhosi yamaFilisti?', 'XHO22'),
(13, 23, 4, 'uDavide waphinda wabuza kuYehova. uYehova wamphendula, wathi, Yihla uye eKehila; kuba ndiya kuwanikela amaFilisti esandleni sakho.', 'XHO22'),
(13, 23, 5, 'uDavide namadoda akhe baya eKehila, bawaxabela amaFilisti, bayithimba imfuyo yawo, bawabulala kakhulu. Wabasindisa ke uDavide abantu baseKehila.', 'XHO22'),
(13, 23, 6, 'Xa uAbhiyatare, unyana ka-Ahimeleki, wabalekayo ukulandela uDavide eKehila, wehla eneqhiya egxalabeni.', 'XHO22'),
(13, 23, 7, 'Waxelelwa uSawule, ukuba uDavide ufikile eKehila. Wathi uSawule, uThixo umnikele esandleni sam; ngokuba nizalwa ngokungena emzini onamasango nezitrato.', 'XHO22'),
(13, 23, 8, 'Wabahlanganisa uSawule bonke abantu ukuba balwe, behle baye eKehila, bangqinge uDavide namadoda akhe.', 'XHO22'),
(13, 23, 9, 'Wazi uDavide ukuba uSawule ubemqhathe ngasese; Wathi kuAbhiyatare umbingeleli, Zisa iefodi apha.', 'XHO22'),
(13, 23, 10, 'Wathi uDavide, Yehova, Thixo kaSirayeli, uvile ngenene umkhonzi wakho, ukuba uSawule ufuna ukuza eKehila, ukuba awonakalise umzi ngenxa yam.', 'XHO22'),
(13, 23, 11, 'Amadoda aseKehila aya kundinikela na esandleni sakhe? Uya kuhla na uSawule njengoko evile umkhonzi wakho? Yehova, Thixo kaSirayeli, mxelele umkhonzi wakho. Wathi uYehova: Uya kuhla.', 'XHO22'),
(13, 23, 12, 'Wathi uDavide, Amadoda aseKehila andinikela na mna namadoda am esandleni sikaSawule? Wathi uYehova, Baya kukunikela esandleni sakho.', 'XHO22'),
(13, 23, 13, 'Emva koko wesuka uDavide namadoda akhe, abantu abamalunga namakhulu amathandathu, besuka eKehila, bahamba naphi na apho babesiya khona. Waxelelwa uSawule, ukuba uDavide ubalekile eKehila; woyisakala ke ukuphuma.', 'XHO22'),
(13, 23, 14, 'uDavide wahlala entlango ezimboniselweni, wahlala entabeni eyintlango yeZifi. uSawule wamkhangela yonke imihla, kodwa uThixo akamnikela esandleni sakhe.', 'XHO22'),
(13, 23, 15, 'Wabona uDavide ukuba uSawule uphume eza kufuna umphefumlo wakhe; uDavide wayesentlango yeZifi entshinyeleni yehlathi.', 'XHO22'),
(13, 23, 16, 'Wesuka uYonatan, unyana kaSawule, waya kuDavide entshinyeleni yehlathi, wasomeleza isandla sakhe kuThixo.', 'XHO22'),
(13, 23, 17, 'Wathi kuye, Musa ukoyika, ngokuba isandla sikaSawule ubawo asiyi kukufumana; Wena uya kuba ngukumkani wakwaSirayeli, mna ndibe nawe; ukanti uSawule uyise uyayazi le nto.', 'XHO22'),
(13, 23, 18, 'Benza umnqophiso bobabini phambi koYehova, waza uDavide wahlala ehlathini, waza uYonatan waya endlwini yakhe.', 'XHO22'),
(13, 23, 19, 'Benyuka abaseZifi, baya kuSawule eGibheha, bathi, uDavide akazizimeze kuthi na ezimboniselweni zehlathi, endulini yeHagila, ngasezantsi kweYesimon?', 'XHO22'),
(13, 23, 20, 'Kaloku, kumkani, yihla ngokomnqweno wonke womphefumlo wakho; kwaye isabelo sethu siya kuba kukumnikela esandleni sokumkani.', 'XHO22'),
(13, 23, 21, 'Wathi uSawule, uYehova makakusikelele; ngokuba undenzele inceba.', 'XHO22'),
(13, 23, 22, 'Yiyani nilungise, nikhangele, nibone apho ahlala khona, nokuba ngubani na owambona khona; kuba ndixelelwe ukuba unobuqhophololo.', 'XHO22'),
(13, 23, 23, 'Nikhangele, nibone zonke iindawo zokuzimela, aya kuzimela kuzo, nize nibuyele kum okunene; ndohamba nani; yaye ukuba ukho ehlabathini, ndiya kumkhangela phakathi kwawo onke amawaka akwaYuda.', 'XHO22'),
(13, 23, 24, 'Bagqiba ke bamrhola uSawule, bamsa eZifi; uDavide namadoda akhe wayesentlango yeMahon, ezantsi kweYesmon.', 'XHO22'),
(13, 23, 25, 'Wahamba uSawule namadoda akhe ukuya kumfuna. Kwaxelwa kuDavide, kwathiwa, Wehla ke waya encotsheni yengxondorha, wahlala entlango yeMahon. Weva uSawule, wamsukela uDavide entlango yeMahon.', 'XHO22'),
(13, 23, 26, 'Wesuka uSawule waya kwelinye icala lentaba; waphuma uDavide namadoda akhe, baya nganeno kwentaba; ngokuba uSawule namadoda akhe babemrhawula uDavide namadoda akhe, ukuba bababambe.', 'XHO22'),
(13, 23, 27, 'Weza umthunywa kuSawule, wathi, Akofa, yiza; ngokuba amaFilisti abelingenele ilizwe.', 'XHO22'),
(13, 23, 28, 'Wabuya ke uSawule ekusukeleni uDavide, waya kuwakhawulela amaFilisti; ngoko ke, loo ndawo bayibiza ngokuba yiSelaama-Hlekoti.', 'XHO22'),
(13, 23, 29, 'Wemka apho uDavide, wahlala ezinqabeni zase-Engedi.', 'XHO22'),
(13, 24, 1, 'Wabuya uSawule kumaFilisti, waxelelwa, kwathiwa, Nanku uDavide entlango yase-Engedi.', 'XHO22'),
(13, 24, 2, 'Wandula ke uSawule wathabatha amadoda angamawaka amathathu awayenyulwe kuSirayeli wonke waza waya kufuna uDavide namadoda akhe emaweni eebhokhwe zelizwe.', 'XHO22'),
(13, 24, 3, 'Wafika ezibayeni zempahla emfutshane, ngasendleleni, kukho umqolomba apho; Wangena uSawule ukuba agubungele iinyawo zakhe, waza uDavide namadoda akhe bahlala emqolombeni.', 'XHO22'),
(13, 24, 4, 'Athi amadoda kaDavide kuye, Nantsi imini, athe uYehova kuwe, Yabona, ndiya kulunikela utshaba lwakho esandleni sakho, wenze ngokokuthanda kwakho. Wesuka wema uDavide, wayinqumla ngasese ingubo yokwaleka yengubo kaSawule.', 'XHO22'),
(13, 24, 5, 'Emva koko, intliziyo kaDavide yaqhubeka ibetha, ngenxa yokuba wayesikile isambatho sangaphezulu sikaSawule.', 'XHO22'),
(13, 24, 6, 'Wathi kumadoda akhe, Ngamana uYehova angandinqanda, ndiyenze le nto enkosini yam, umthanjiswa kaYehova, ndisise isandla sam kuye, ngokuba ndingothanjisiweyo kaYehova.', 'XHO22'),
(13, 24, 7, 'uDavide wabanqanda ngaloo mazwi abakhonzi bakhe, akabavumela ukuba basukele kuSawule. Wemka uSawule emqolombeni, wemka.', 'XHO22'),
(13, 24, 8, 'Wemka naye uDavide, waphuma emqolombeni, wabiza uSawule emva kwakhe, wathi, Nkosi yam, nkosi yam! Wathi uSawule akukhangela ngasemva, uDavide wathoba, waqubuda ngobuso emhlabeni.', 'XHO22'),
(13, 24, 9, 'Wathi uDavide kuSawule, Yini na ukuba uwaphulaphule amazwi akho athi, Nanku uDavide efuna ukukwenzakalisa?', 'XHO22'),
(13, 24, 10, 'Yabona, namhlanje amehlo akho abonile ukuba uYehova ebekunikele namhla esandleni sam emqolombeni; abanye bathi mandikubulale; Ndathi, Andiyi kusolula isandla sam enkosini yam; ngokuba engumthanjiswa kaYehova.', 'XHO22'),
(13, 24, 11, 'Kananjalo, Bawo, khangela, nawu umqukumbelo wengubo yakho esandleni sam; ngenxa enokuba ndiwunqumle umsonto wengubo yakho, andakubulala; yazi wena, wabona ukuba akukho bubi nabugwenxa esandleni sam, andonanga kuwe; ke nifuna umphefumlo wam, ukuba niwuthabathe.', 'XHO22'),
(13, 24, 12, 'uYehova uya kugweba phakathi kwam nani, uYehova andibuyisele kuni; ke isandla sam asiyi kuba phezu kwakho.', 'XHO22'),
(13, 24, 13, 'Njengoko litshoyo iqhalo lakudala ukuthi, Okungendawo kuphuma kongendawo; ke isandla sam asikwazi ukulwa nabo.', 'XHO22'),
(13, 24, 14, 'Uvela kubani na ukumkani wakwaSirayeli? uleqa bani? kwinja efileyo, ukuyibiza.', 'XHO22'),
(13, 24, 15, 'uYehova makabe ngumgwebi nomgwebi phakathi kwam nawe, akhangele, andithethelele etyaleni lam, andisindise esandleni senu.', 'XHO22'),
(13, 24, 16, 'Wagqiba uDavide ukuwathetha la mazwi kuSawule, wathi uSawule, Lilizwi lakho na elo, Davide, nyana wam? Waliphakamisa uSawule ilizwi lakhe, walila.', 'XHO22'),
(13, 24, 17, 'Wathi kuDavide, Ulilungisa wena kunam, ngokuba undenze okulungileyo wena, ndakwenza okubi mna;', 'XHO22'),
(13, 24, 18, 'Wena ubonisile namhla ukuba undenze okulungileyo, ngenxa yokuba ungandibulalanga ekundinikeleni kwam uYehova esandleni sakho.', 'XHO22'),
(13, 24, 19, 'Ukuba umntu uthe walufumana utshaba lwakhe, wolukhulula na? uya kukubuyekeza uYehova ngenxa yento oyenzileyo kum namhla.', 'XHO22'),
(13, 24, 20, 'Kaloku yabona, ndiyazi ukuba uya kuba ngukumkani, bume esandleni sakho ubukumkani bakwaSirayeli.', 'XHO22'),
(13, 24, 21, 'Ndifungele ke uYehova, ukuba akuyi kuyitshabalalisa imbewu yam emva kwam, nokuba akuyi kulitshabalalisa igama lam endlwini kabawo;', 'XHO22'),
(13, 24, 22, 'uDavide wamfungela uSawule. Wagoduka uSawule; kodwa uDavide namadoda akhe benyuka baya kwindawo enqatyisiweyo ezintabeni.', 'XHO22'),
(13, 25, 1, 'Wafa uSamuweli; yaye bonke oonyana bakaSirayeli bahlanganisana baza bamzilela baza bamngcwabela endlwini yakhe eRama. Wagqiba ke uDavide, wehla waya entlango yaseParan.', 'XHO22'),
(13, 25, 2, 'Ke kaloku bekukho indoda yaseMahone, obutyebi bakhe buseKarmele; indoda leyo yayinkulu kakhulu, ineegusha ezingamawaka amathathu, neebhokhwe eziliwaka; waye echeba iigusha zakhe eKarmele.', 'XHO22'),
(13, 25, 3, 'Igama laloo ndoda lalinguNabhali; igama lomkayo nguAbhigali; waye engumfazi onengqondo entle, ebukekayo; waye engowendlu kaKalebhi.', 'XHO22'),
(13, 25, 4, 'Weva uDavide entlango ukuba uNabhali ucheba iigusha zakhe.', 'XHO22'),
(13, 25, 5, 'uDavide wesusa ishumi lamadodana, wathi uDavide kumadodana akhe, Nyukani niye eKarmele, niye kuNabhali, nibulise egameni lam.', 'XHO22'),
(13, 25, 6, 'Nize nithi kulowo uphumeleleyo, Uxolo malube nawe, uxolo malube nawe, uxolo malube nawe, uxolo malube nayo yonke into onayo.', 'XHO22'),
(13, 25, 7, 'Ndivile ngoku ukuba unabahlobo; Ke kaloku, abalusi bakho ababenathi, asibenzanga nto, abaswelanga nto yonke imihla yokuba seKarmele kwabo.', 'XHO22'),
(13, 25, 8, 'Buza kumadodana akho, okuxelela; Makababalwe nguwe abafana emehlweni akho; ngokuba siza ngemini elungileyo; Konke okusesandleni sakho ubanike abakhonzi bakho nonyana wakho uDavide.', 'XHO22'),
(13, 25, 9, 'Afika amadodana kaDavide, amxelela uNabhali onke loo mazwi egameni likaDavide, ayeka.', 'XHO22'),
(13, 25, 10, 'uNabhali wabaphendula abakhonzi bakaDavide, wathi, Ngubani na yena uDavide? Ngubani na unyana kaYese? kule mihla baninzi abakhonzi abaqhatha elowo enkosini yakhe.', 'XHO22'),
(13, 25, 11, 'Ngoku ke ndiya kuthabatha isonka sam, namanzi am, nenyama yam, endayibingelelayo kubachebi bam, ndiyinike abantu endingabaziyo apho baphuma khona?', 'XHO22'),
(13, 25, 12, 'Ajika ke amadodana kaDavide, abuya, amxelela loo mazwi onke.', 'XHO22'),
(13, 25, 13, 'Wathi uDavide kumadoda akhe, Thabathani elowo ikrele lakhe. Babhinqa elowo ikrele lakhe; Naye uDavide wabhinqa ikrele lakhe, waza walandela uDavide, malunga namadoda angamakhulu amana kwasala amakhulu amabini kwabalungileyo.', 'XHO22'),
(13, 25, 14, 'Ke kaloku uAbhigali, umkaNabhali, waxelelwa yindodana ethile kumadodana akhe, yathi, Yabona, uDavide ususe abathunywa entlango, ukuba beze kusikelela ukumkani wethu; wabaqalekisa.', 'XHO22'),
(13, 25, 15, 'Kodwa loo madoda ayesenza okulungileyo kunene kuthi, yaye akazange asenze bubi, yaye asizange siswele nto ngoxa sasisendle.', 'XHO22'),
(13, 25, 16, 'Yayiludonga kuthi, imini nobusuku, yonke imihla esasinabo, sisaluse izimvu.', 'XHO22'),
(13, 25, 17, 'Yazi ngoku, uyinyamekele into oyenzayo; kuba eneneni ububi bufikela ukumkani wethu nendlu yakhe yonke, ekubeni engunyana kaBheliyali ekungekho bani uya kuthetha naye.', 'XHO22'),
(13, 25, 18, 'Wakhawuleza ke uAbhigali, wathabatha amakhulu amabini ezonka, neendebe zambini zewayini, neegusha zantlanu ezilungisiweyo, neeseha zantlanu zengqolowa egcadiweyo, nekhulu lezicumba zeerasintyisi, nekhulu lezonka zamakhiwane zazibini, wazibeka emaesileni.', 'XHO22'),
(13, 25, 19, 'Wathi kubakhonzi bakhe, Gqithelani phambi kwam; uyabona, ndiya kukulandela. Kodwa akazange ayixelele indoda yakhe uNabhali.', 'XHO22'),
(13, 25, 20, 'Ekhwele iesile, wehla waya endaweni yokuzimela endulini, wabona uDavide namadoda akhe besihla besiza kumkhawulela; waqubisana nabo.', 'XHO22'),
(13, 25, 21, 'Wathi uDavide, Ndifumane ndafumana, ndakuba ndizigcine zonke izinto enazo entlango, kungalahleki nto entweni yonke yakhe; wandibuyisela ububi bam ngokulungileyo.', 'XHO22'),
(13, 25, 22, 'uThixo uya kwenza oku ngakumbi kwiintshaba zikaDavide, ukuba ngenye intsasa nditshisa ngomlilo yonke into anayo.', 'XHO22'),
(13, 25, 23, 'uAbhigali wambona uDavide, wakhawuleza wakhwela e-esileni, wawa ngobuso phambi koDavide, waqubuda.', 'XHO22'),
(13, 25, 24, 'Waza waziphosa phantsi ezinyaweni zakhe waza wathi: “Makube phezu kwam obu bubi, nkosi yam, yaye umkhonzazana wakho makabuthethe phambi kwabaphulaphuli bakho” uweve amazwi omkhonzazana wakho.', 'XHO22'),
(13, 25, 25, 'Inkosi yam mayingakhe inikele ingqalelo kulo mntu ulitshijolo, uNabhali, ngenxa yokuba njengoko linjalo igama lakhe, unjalo; Igama lakhe nguNabhali, unebudenge; Ke mna mkhonzi wakho, andibabonanga abafana benkosi yam, obuwathumele.', 'XHO22'),
(13, 25, 26, 'Kaloku, nkosi yam, ehleli nje uYehova, uhleli nje umphefumlo wakho, ekubeni uYehova ekwawisele umthetho wokuba uphalaze igazi, uziphindezelele ngesandla sakho; zifuneni kaloku iintshaba zenu, nabenzi bobubi, uYehova wam, njengoNabhali.', 'XHO22'),
(13, 25, 27, 'Ngoko le ntsikelelo, ayizisileyo umkhonzazana wakho enkosini yam, mayinikwe amadodana alandela inkosi yam.', 'XHO22'),
(13, 25, 28, 'Uze umxolele umkhonzi wakho ukreqo lwakhe; ngokuba, inene, uYehova uya kuyakhela inkosi yam indlu eqinileyo; ngokuba inkosi yam ilwa iimfazwe zikaYehova; ububi abuyi kufunyanwa kuwe yonke imihla yakho.', 'XHO22'),
(13, 25, 29, 'Wesuka umntu, wakusukela, wawufuna umphefumlo wakho; ke wona umphefumlo wenkosi yam wobotshwa esiqhumeni sabahleli kuYehova uThixo wakho; yaye niya kuyilahla imiphefumlo yeentshaba zenu ngokungathi ivela esazulwini somhlaba.', 'XHO22'),
(13, 25, 30, 'Emveni kokuba uYehova wenze enkosini yam konke okulungileyo, awakuthethileyo kuwe, wakumisa ukuba ube yinganga yakwaSirayeli;', 'XHO22'),
(13, 25, 31, 'makungabikho kuwe ukuba buhlungu okanye ube nengqumbo entliziyweni yakho ngenxa yenkosi yam ngokuphalaza igazi ngaphandle kwesizathu okanye ukuziphindezela kwenkosi yam; ukuba uYehova wenze okulungileyo enkosini yam, womkhumbula umkhonzi wakho.', 'XHO22'),
(13, 25, 32, 'Wathi uDavide kuAbhigali, Makabongwe uYehova, uThixo kaSirayeli, okuthumileyo namhla, wandikhawulela.', 'XHO22'),
(13, 25, 33, 'Malibongwe icebo lakho, ubongwe wena ondaleleyo namhla ukuba ndiphalaze igazi, nokuphindezelela ngesandla sam.', 'XHO22'),
(13, 25, 34, 'Kuba ehleli uYehova, uThixo kaSirayeli, ondithinteleyo ukuba ndingenzi nto imbi kuwe, ukuba ubungangxamanga uze kundikhawulela, ange engathanga uNabhali kusasa, angamohlwayanga uYehova idonga.', 'XHO22'),
(13, 25, 35, 'uDavide wayiphatha esandleni sakhe loo nto abemzisile, wathi kuye, Nyuka unoxolo, uye endlwini yakho; uyabona, ndiliphulaphule izwi lakho, ndasithabatha isimo sakho.', 'XHO22'),
(13, 25, 36, 'Waya ke uAbhigali kuNabhali; nanko enesidlo endlwini yakhe, njengesidlo sokumkani; intliziyo kaNabhali yayichwayitile phakathi kwakhe, enxila kunene ke ngoko, makangamxeleli nto, nokuba ingako, kude kube kusasa.', 'XHO22'),
(13, 25, 37, 'Kwathi kusasa, ukuphuma kwewayini kuNabhali nomkakhe, yamxelela loo nto, yafa intliziyo yakhe phakathi kwakhe, waba lilitye.', 'XHO22'),
(13, 25, 38, 'Emva kweentsuku ezimalunga neshumi, uYehova wambulala uNabhali kangangokuba wafa.', 'XHO22'),
(13, 25, 39, 'Weva uDavide ukuba uNabhali ufile, wathi: “Makabongwe uYehova oye wakhusela ungcikivo lukaNabhal nomkhonzi wakhe ebubini; ngenxa yokuba ububi bukaNabhali busentlokweni yakhe. Emva koko uDavide wathumela kuAbhigali ukuba amthabathe abe ngumfazi wakhe.', 'XHO22'),
(13, 25, 40, 'Bafika abakhonzi bakaDavide kuAbhigali eKarmele, bathetha kuye, bathi, uDavide usithume kuwe ukuba akuthabathe, ube ngumkakhe.', 'XHO22'),
(13, 25, 41, 'Wesuka wema, waqubuda ngobuso, wathi, Yabona, umkhonzazana wakho makabe ngumkhonzi wokuhlamba iinyawo zabakhonzi benkosi yam.', 'XHO22'),
(13, 25, 42, 'Wesuka ngokukhawuleza uAbhigali, wakhwela e-esileni; wabalandela abathunywa bakaDavide, waba ngumkakhe.', 'XHO22'),
(13, 25, 43, 'uDavide wathabatha uAkinowam waseYizereli; baba ngabafazi bakhe bobabini nabo.', 'XHO22'),
(13, 25, 44, 'Ke uSawule wamnika uPalti, unyana kaLayishe waseGalim, intombi yakhe, uMikali, umkaDavide.', 'XHO22'),
(13, 26, 1, 'Beza abaseZifi kuSawule eGibheha, bathi, Bendisithi na, uDavide uzimele endulini yeHagila, ngaseYesimon?', 'XHO22'),
(13, 26, 2, 'Wagqiba uSawule, wehla waya entlango yeZifi, enamadoda angamawaka amathathu ahleliweyo akwaSirayeli, eya kufuna uDavide entlango yaseZifi.', 'XHO22'),
(13, 26, 3, 'uSawule wamisa endulini yeHagila, ngakuYesmon, ngasendleleni. Ke uDavide wayehleli entlango, wabona uSawule emlandela entlango.', 'XHO22'),
(13, 26, 4, 'uDavide wasusa iintlola, weva ukuba uSawule uzile kakuhle.', 'XHO22'),
(13, 26, 5, 'Wemka uDavide, waya kuloo ndawo ebemise kuyo uSawule; Wayibona uDavide indawo alele kuyo uSawule, noAbhinere unyana kaNere, umthetheli womkhosi; uSawule wayelele emhadini, yaye abantu bamise iintente kufutshane naye.', 'XHO22'),
(13, 26, 6, 'Waphendula uDavide, wathi kuAhimeleki umHeti, nakuAbhishayi, unyana kaTseruya, umntakwabo-Yowabhi, Ngubani na oya kuhla nam aye kuSawule emkhosini? Wathi uAbhishayi, Ndiya kuhla nawe.', 'XHO22'),
(13, 26, 7, 'Wafika uDavide noAbhishayi ebantwini ebusuku. Nanko uSawule elele emhadini, igxunyekiwe intshuntshe yakhe emhlabeni ngasemhadini, uAbhinere namadoda elele, bemlalele ngaphakathi.', 'XHO22'),
(13, 26, 8, 'Wathi uAbhishayi kuDavide, uThixo ulunikele namhla utshaba lwakho esandleni sakho; Andiyi kumbetha nangentshuntshe nakanye, ndingayi kumeyisa okwesibini.', 'XHO22'),
(13, 26, 9, 'Wathi uDavide kuAbhishayi, Musa ukumtshabalalisa; ngokuba ngubani na ongasolula isandla sakhe kumthanjiswa kaYehova omsulwa?', 'XHO22'),
(13, 26, 10, 'uDavide wongezelela wathi, NgoYehova ovayo, uYehova uya kumvelela; nokuba iya kufika imini yakhe yokufa; okanye uya kuhla aye emfazweni, atshabalale.', 'XHO22'),
(13, 26, 11, 'Wandinqanda uYehova ukuba ndingasoluli isandla sam kumthanjiswa kaYehova; kodwa thabatha umkhonto ngasemphongolweni wakhe nasemnqamlezweni wamanzi, sihambe.', 'XHO22'),
(13, 26, 12, 'uDavide wathabatha intshuntshe, nonqamlezo lwamanzi kumphongolo kaSawule; barhoxa, akwabakho bani ubonayo, nokwaziyo, novukayo; kuba bonke babephethukile; ngokuba balele ebuthongweni obukhulu bukaYehova.', 'XHO22'),
(13, 26, 13, 'Wesuka uDavide, waya kwelinye icala, wema encotsheni yenduli ekude; umgama phakathi kwabo:', 'XHO22'),
(13, 26, 14, 'Wababiza abantu uDavide, noAbhinere, unyana kaNere, wathi, Akuphenduli na, Abhinere? Waphendula uAbhinere, wathi, Ungubani na wena ubiza ukumkani?', 'XHO22'),
(13, 26, 15, 'Wathi uDavide kuAbhinere, Akuyindoda elikroti na? yaye ngubani na onjengawe kwaSirayeli? Yini na ukuba ungayisindisanga inkosi yakho, kumkani? ngokuba kufike umntu ebantwini, eza konakalisa ukumkani wakho.', 'XHO22'),
(13, 26, 16, 'Ayilungile le nto uyenzileyo. Ehleli nje uYehova, nifanelwe kukufa, ngenxa yokuba aniyikhuselanga inkosi yenu, umthanjiswa kaYehova. Ngoku khawujonge apho umkhonto wokumkani kunye nomnqamlezo wamanzi wawukumphongolo wakhe.', 'XHO22'),
(13, 26, 17, 'Walazi uSawule ilizwi likaDavide, wathi, Lilizwi lakho na elo, Davide, nyana wam? Wathi uDavide, Lilo eli ilizwi lam, nkosi yam, nkosi yam!', 'XHO22'),
(13, 26, 18, 'Wathi, Yini na ukuba inkosi yam imsukele ngolu hlobo umkhonzi wayo? Ndenze bubi buni na, siyintoni na esandleni sam?', 'XHO22'),
(13, 26, 19, 'Ke ngoko inkosi yam ukumkani mayikhe iwaphulaphule amazwi omkhonzi wayo. Ukuba uYehova ekuxhobisile kum, makawamkele umbingelelo; Ke oonyana babantu baqalekisiwe phambi koYehova; ngenxa yokuba namhlanje bandigxothile ukuba ndingahlali elifeni likaYehova, besithi, ‘Hamba uye kukhonza abanye oothixo.', 'XHO22'),
(13, 26, 20, 'Ke kaloku igazi lam alifanele liwele emhlabeni phambi koYehova, ngenxa yokuba ukumkani wakwaSirayeli waphuma waya kufuna iintakumba njengokuzingela ezintabeni.', 'XHO22'),
(13, 26, 21, 'Wathi uSawule, Ndidanile; buya, nyana wam Davide, ngokuba andiyi kuba sakwenza nto imbi, ngokuba umphefumlo wam ubunqabile emehlweni akho namhla;', 'XHO22'),
(13, 26, 22, 'Waphendula uDavide, wathi, Nantsi intshuntshe yokumkani; makeze omnye wabafana, ayithabathe.', 'XHO22'),
(13, 26, 23, 'Ngamana uYehova angabuyisela ubulungisa bakhe nokuthembeka kwakhe elowo nalowo, ngenxa yokuba namhlanje uYehova ubunikele esandleni sam, kodwa andivumanga ukusa isandla sam phezu kothanjisiweyo kaYehova.', 'XHO22'),
(13, 26, 24, 'Yabonani, njengoko umphefumlo wenu uqinile emehlweni am, kwada kwayile mini, ngamana umphefumlo wam ungama phambi koYehova, andisindise kuko konke ukubandezeleka.', 'XHO22'),
(13, 26, 25, 'Wathi uSawule kuDavide, Usikelelwe, nyana wam, Davide; ngokuba wenze izinto ezinkulu, woyise. Wahamba ke uDavide, waza uSawule wabuyela endlwini yakhe.', 'XHO22'),
(13, 27, 1, 'Wathi uDavide entliziyweni yakhe, Ndiya kubulawa ngamhla sisandla sikaSawule; uSawule uya kundifuna ezindaweni zonke zakwaSirayeli; ndiya kusinda ke esandleni sakhe.', 'XHO22'),
(13, 27, 2, 'Akugqiba uDavide, enamadoda angamakhulu amathandathu, waya kuAkishe, unyana kaMagogi, ukumkani waseGati.', 'XHO22'),
(13, 27, 3, 'uDavide wahlala noAkishe eGati, yena namadoda akhe, yayileyo nendlu yayo, noDavide nabafazi bakhe bobabini, uAkinowam waseYizereli, noAbhigali waseKarmele, umkaNabhali.', 'XHO22'),
(13, 27, 4, 'Waxelelwa uSawule ukuba uDavide ubalekele eGati, akaba saphinda amfune.', 'XHO22'),
(13, 27, 5, 'Wathi uDavide kuAgishe, Ukuba kaloku ndibabalwe nguwe, mandinikwe indawo ngomnye umzi osemaphandleni, ndihlale khona; Yini na ukuba umkhonzi wakho ahlale nawe emzini wakomkhulu?', 'XHO22'),
(13, 27, 6, 'uAgise wamnika ngaloo mini iTsikelage; ngenxa yoko iTsikelage iye yaba yeyookumkani bakwaYuda, unanamhla.', 'XHO22'),
(13, 27, 7, 'Imihla awahlala kuyo uDavide kwilizwe lamaFilisti yaba ngunyaka neenyanga ezine.', 'XHO22'),
(13, 27, 8, 'Wenyuka ke uDavide namadoda akhe, bawaxabela amaGeshuri, namaGezere, nama-Amaleki; ngokuba ezo ntlanga zazimi kwelo zwe kwakudala, ekufikeni kwenu eShure, sesa ezweni laseYiputa.', 'XHO22'),
(13, 27, 9, 'uDavide waloyisa ilizwe, akashiya ndoda nankazana iphila, wathabatha impahla emfutshane, neenkomo, namaesile, neenkamela, neempahla, wabuyela kuAkishe.', 'XHO22'),
(13, 27, 10, 'Wathi uAgise, Ubusiya phi na namhlanje? Wathi uDavide, Elasezantsi lakwaYuda, nelasezantsi lamaYerameli, nelasezantsi lamaKanan.', 'XHO22'),
(13, 27, 11, 'uDavide akazisa ndoda namfazi ehleli eGati, esithi, Ngoko abasixeleli ukuthi, Wenze loo nto nguDavide; ezweni lamaFilisti.', 'XHO22'),
(13, 27, 12, 'uAkishe wakholwa nguDavide, wathi, Ubenze bangamasikizi abantu bakhe amaSirayeli; ke ngoko, uya kuhlala esisicaka sam.', 'XHO22'),
(13, 28, 1, 'Ngelo xesha amaFilisti ahlanganisana ndawonye ukuba alwe noonyana bakaSirayeli. Wathi uAgise kuDavide, Yazi ukuba uya kuya nam emfazweni, wena namadoda akho.', 'XHO22'),
(13, 28, 2, 'Wathi uDavide kuAgishe, Ewe, uya kuyazi into eya kwenziwa ngumkhonzi wakho. Wathi uAgise kuDavide, Ngenxa yoko ndikwenza ube ngumveleli wentloko yam ngamaxesha onke.', 'XHO22'),
(13, 28, 3, 'Wafa ke uSamuweli; ammbambazelela onke amaSirayeli; amngcwabela eRama emzini wakowabo. uSawule wabakhupha oomoya babafileyo namagqwirha ezweni.', 'XHO22'),
(13, 28, 4, 'Ahlanganisana ke amaFilisti, eza amisa iintente eShunem; uSawule wawahlanganisa onke amaSirayeli, wamisa eGilibhowa.', 'XHO22'),
(13, 28, 5, 'Wayibona uSawule impi yamaFilisti, woyika, yagubha intliziyo yakhe kunene.', 'XHO22'),
(13, 28, 6, 'Wabuza uSawule kuYehova, akamphendula nangamaphupha, nangeeUrim, nangabaprofeti.', 'XHO22'),
(13, 28, 7, 'Wathi uSawule kubakhonzi bakhe, Ndikhangeleleni inkazana enomoya wabafileyo, ndiye kuyo, ndibuze kuyo. Bathi abakhonzi bakhe kuye, Nankuya umfazi e-Enedore enemimoya.', 'XHO22'),
(13, 28, 8, 'uSawule wazenza mntu wumbi, wambatha ngubo zimbi, wahamba enamadoda amabini, baya kufika kuloo ntokazi ebusuku; nyuka, ndikubize.', 'XHO22'),
(13, 28, 9, 'Yathi inkazana leyo kuye, Yabona, uyayazi nawe into ayenzileyo uSawule, ukuba ebebatshabalalisa oomoya abangasekhoyo namagqwirha ehlabathini; Yiyo le nto uwuthiyele umphefumlo wam, ukuze ndife?', 'XHO22'),
(13, 28, 10, 'uSawule wamfungisa uYehova, wathi, NgoYehova ovayo iimbaleki zakho aziyi kuba netyala kuwe.', 'XHO22'),
(13, 28, 11, 'Yathi inkazana leyo, Ndithume bani na kuwe? Wathi, Ndiziseleni uSamuweli.', 'XHO22'),
(13, 28, 12, 'Yambona inkazana uSamuweli, yakhala ngezwi elikhulu; Yatsho inkazana leyo kuSawule, yathi, Undixokile na? ngokuba unguSawule wena.', 'XHO22'),
(13, 28, 13, 'Wathi ukumkani kuye, Musa ukoyika, ngokuba ubonile? Yathi inkazana kuSawule, Ndibone kunyuka izithixo emhlabeni;', 'XHO22'),
(13, 28, 14, 'Wathi kuye, Ungowabani na? Wathi, Kuzalwe indoda enkulu; kwaye uzigqume ngengubo. Waqonda uSawule ukuba nguSamuweli lowo, waqubuda kuye, waqubuda.', 'XHO22'),
(13, 28, 15, 'Wathi uSamuweli kuSawule, Yini na ukuba undikhathaze ngokundivusa? Wathi uSawule, Ndibandezelekile kakhulu; ngokuba amaFilisti esilwa nam, uThixo undishiyile, anisayi kuba sandiphendula ngabaprofeti namaphupha; ke ngoko, ndikubizile, ukuze ube nokundazisa emandikwenze.', 'XHO22'),
(13, 28, 16, 'Wathi uSamuweli, Yini na ukuba undibuze, uYehova ekushiyile nje, wasuke walutshaba lwakho?', 'XHO22'),
(13, 28, 17, 'uYehova wamenzela njengoko wakuthethayo ngam; uYehova ubukrazule ubukumkani esandleni sakho, wabunika obakho, Davide.', 'XHO22'),
(13, 28, 18, 'Ngenxa yokuba ungaliphulaphulanga ilizwi likaYehova, ungawuvumelanga umsindo wakhe ovuthayo kuma-Amaleki, kungenxa yoko le nto uYehova enjenje kuni namhlanje.', 'XHO22'),
(13, 28, 19, 'Ngaphezu koko, uYehova uya kumnikela uSirayeli kunye nawe esandleni samaFilisti; ngomso wena noonyana bakho nibe nam; uYehova uya kuyinikela imikhosi yakwaSirayeli esandleni samaFilisti.', 'XHO22'),
(13, 28, 20, 'Wawa emhlabeni kwaoko uSawule, woyika kunene ngamazwi kaSamuweli, akwabakho mandla kuye; ngokuba ubengadlanga sonka imini yonke, nobusuku bonke.', 'XHO22'),
(13, 28, 21, 'Yeza inkazana leyo kuSawule, yabona ukuba ukhathazeke kakhulu, yathi kuye, Yabona, umkhonzazana wakho uliphulaphule izwi lakho, ndawunikela umphefumlo wam esandleni sam, ndaweva amazwi akho owandixelela wona.', 'XHO22'),
(13, 28, 22, 'Liphulaphule izwi lomkhonzi wakho, ndibeke iqhekeza lesonka phambi kwakho; udle, ube namandla ekuhambeni kwakho.', 'XHO22'),
(13, 28, 23, 'Akavuma ke esithi, Andiyi kudla. Bamnyanzela abakhonzi bakhe kwanenkazana leyo; waliphulaphula ilizwi labo. Uphakame phantsi wahlala ebhedini.', 'XHO22'),
(13, 28, 24, 'Ke kaloku inkazana leyo yayinethole elityetyisiweyo endlwini; waxovula kamsinya, wathabatha umgubo, wawosa ngawo, wosa izonka ezingenagwele ngawo.', 'XHO22'),
(13, 28, 25, 'Wabeka phambi koSawule naphambi kwabakhonzi bakhe; badla ke. Bavuka ke bahamba ngobo busuku.', 'XHO22'),
(13, 29, 1, 'AmaFilisti ayihlanganisa yonke imikhosi yawo eAfeki, aza amaSirayeli amisa ngasequleni eYizereli.', 'XHO22'),
(13, 29, 2, 'Iinkosana zamaFilisti zatyhoboza zingamakhulu namawaka, kodwa uDavide namadoda akhe bahamba noAkishe ukuze bafumane umvuzo.', 'XHO22'),
(13, 29, 3, 'Zathi iinkosana zamaFilisti, Enza ntoni na la maHebhere? Wathi uAkishe kwiinkosana zamaFilisti, Lo asinguye na uDavide, umkhonzi kaSawule, ukumkani wakwaSirayeli, obenam ngale mihla nakule minyaka, ndaza andafumana tyala kuye ukususela ngoko? wandibetha? olusuku?', 'XHO22'),
(13, 29, 4, 'Abathetheli bamaFilisti baba nomsindo ngakuye; zathi kuye iinkosana zamaFilisti, Indoda leyo mayibuyele kuloo ndawo ubuyiwisele umthetho ngayo, ingehli nathi iye kulwa, hleze ilwe nathi edabini hayi ngokuba ebeya kuxolelana ngantoni nenkosi yakhe? Akufanelanga ukuba ibe malunga neentloko zala madoda?', 'XHO22'),
(13, 29, 5, 'Yayingenguye na uDavide lo ababevuma ngaye ekungqungqeni, besithi, uSawule ubulele amawaka akhe, uDavide ubulele amashumi akhe amawaka?', 'XHO22'),
(13, 29, 6, 'uAkishe wambiza uDavide, wathi kuye, Ehleli nje uYehova, ube ulilungisa wena; ngokuba andifumananga bubi kuwe, kususela koko weza kum, unanamhla;', 'XHO22'),
(13, 29, 7, 'Ke ngoko buya, uhambe ngoxolo, hleze ukhubekise izikhulu zamaFilisti.', 'XHO22'),
(13, 29, 8, 'Wathi uDavide kuAkishe, Ndenze ntoni na? yaye ufumene ntoni na kumkhonzi wakho ukususela ekubeni bendikunye nawe kwada kwayile mini yokuba ndingakwazi ukuya kulwa neentshaba zenkosi yam ukumkani?', 'XHO22'),
(13, 29, 9, 'Waphendula uAkishe, wathi kuDavide, Ndiyazi ukuba ulungile emehlweni am njengesithunywa sikaThixo; nokuba bathe abathetheli bamaFilisti, Makangenyuki nathi, aye ekulweni.', 'XHO22'),
(13, 29, 10, 'Ngoko ke, vuka kusasa, nabakhonzi benkosi yakho abaze nawe; kwaye ngokukhawuleza uvuka ekuseni kwaye ukhanyise, hamba.', 'XHO22'),
(13, 29, 11, 'Wavuka kusasa uDavide namadoda akhe, babuyela ezweni lamaFilisti. Yaye amaFilisti ayihlasela iYizereli.', 'XHO22'),
(13, 30, 1, 'Kwathi, akufika uDavide eTsikelage ngomhla wesithathu, afika ama-Amaleki eTsikelage ngasezantsi, neTsikelage, ayixabela iTsikelage, ayitshisa ngomlilo;', 'XHO22'),
(13, 30, 2, 'Abathimba abafazi ababekhona;', 'XHO22'),
(13, 30, 3, 'Wangena ke uDavide namadoda akhe phakathi komzi, bawufumana utshisiwe ngomlilo; abafazi babo noonyana babo neentombi zabo bathinjwa.', 'XHO22'),
(13, 30, 4, 'Baliphakamisa ooDavide namadoda abenaye ilizwi labo, balila, kwada akwabakho mandla kubo okulila.', 'XHO22'),
(13, 30, 5, 'Yaye abafazi ababini bakaDavide bathatyathelwa ekuthinjweni, uAhinowam umYizerel noAbhigali umfazi kaNabhali waseKarmele.', 'XHO22'),
(13, 30, 6, 'Wabandezeleka kunene uDavide; kuba abantu babesithi bamgibisele ngamatye; ngokuba umoya wesizwe siphela ubebandezelekile, elowo ngenxa yoonyana bakhe nangenxa yeentombi zakhe; ke uDavide wazomeleza ngoYehova uThixo wakhe.', 'XHO22'),
(13, 30, 7, 'Wathi uDavide kuAbhiyatare, unyana ka-Ahimeleki, umbingeleli, Ndizise apha ingubo yamagxa; uAbhiyatare wamthabathela khona uDavide iefodi.', 'XHO22'),
(13, 30, 8, 'Wabuza uDavide kuYehova, esithi, Ndiligibele na elo qela? Ndiya kubafumana. Wathi ke yena kuye, Bandulule, ngokuba uya kubambisa, kwaye uya kuphinda ulawule yonke into ngaphandle kokulibala.', 'XHO22'),
(13, 30, 9, 'Wahamba ke uDavide, enamadoda angamakhulu amathandathu, waya kufika emlanjaneni oyiBhesore, apho aseleyo amasalela.', 'XHO22'),
(13, 30, 10, 'Walandela uDavide, yena namadoda angamakhulu amane; ngokuba kwakusele amakhulu amabini amadoda ediniwe, ukuba abe nako ukuwela umlambo iBhesore.', 'XHO22'),
(13, 30, 11, 'Ke kaloku bafumana indoda engumYiputa isezweni, bayizisa kuDavide, bayinika isonka, yadla; bampha amanzi;', 'XHO22'),
(13, 30, 12, 'Bamnika ke icandelo lesicumba somkhiwane, nezicumba ezimbini zeerasintyisi; Ekudleni kwakhe wabuyela kuye umoya wakhe; ngokuba ebengadlanga sonka, engaselanga manzi iimini ezintathu nobusuku obuthathu.', 'XHO22'),
(13, 30, 13, 'Wathi uDavide kuyo, Yekabani na? ungowaphi? Wathi, NdingumYiputa, umkhonzi wendoda engumAmaleki; Indishiyile inkosi yam, ngokuba bendisifa iintsuku ezintathu.', 'XHO22'),
(13, 30, 14, 'Sahlasela elasezantsi lamaKrete, nelakwaYuda, nelasezantsi likaKalebhi; sayitshisa iTsikelage ngomlilo.', 'XHO22'),
(13, 30, 15, 'Wathi uDavide kuyo, Ungandihlisela na kulaa sihlwele? Wathi, Musani ukundilahla phambi koThixo, ukuba ningandibulali, ningandinikele esandleni senkosi yam, ndinihlisele kule ngxokolo.', 'XHO22'),
(13, 30, 16, 'Wamthoba, bachithachitheka kulo lonke elimiweyo, besidla, besela, bememelela ngenxa yamaxhoba onke amakhulu ababewathimbile ezweni lamaFilisti nasezweni lakwaYuda.', 'XHO22'),
(13, 30, 17, 'uDavide wawaxabela ukususela ngokuhlwa kwada kwahlwa ngengomso; akwasinda namnye kubo, ngaphandle kwamakhulu amane amadodana akhwela iinkamela, asabayo.', 'XHO22'),
(13, 30, 18, 'uDavide wazihlangula zonke izinto ezazithinjwe ngama-Amaleki, waza uDavide wabasindisa nabafazi bakhe bobabini.', 'XHO22'),
(13, 30, 19, 'Abazange baswele nto, enoba yencinane okanye enkulu, okanye oonyana okanye iintombi, okanye into ephangiweyo okanye nantoni na ababeyithabathele kubo. uDavide wayibuyisela yonke loo nto.', 'XHO22'),
(13, 30, 20, 'Wayithabatha uDavide yonke imihlambi yempahla emfutshane, neenkomo, ababeyisukela phambi kwemihlambi, bathi, Lixhoba likaDavide eli.', 'XHO22'),
(13, 30, 21, 'uDavide wafika kumakhulu amabini amadoda adiniweyo, engenako ukumlandela uDavide, awawabeka entilini yomlanjana oyiBhesore; yaye baphuma baya kuhlangabeza uDavide nokuhlangabeza amadoda awayekunye naye. Wafika uDavide ebantwini, wababulisa.', 'XHO22'),
(13, 30, 22, 'Aphendula ke onke amadoda amatshijolo namadoda angamatshijolo, abehambe noDavide, athi, Ekubeni bengahambanga nathi, asiyi kubanika amaxhoba esiwakhululeyo; umfazi wakhe abantwana, ukuze babakhaphele, bahambe.', 'XHO22'),
(13, 30, 23, 'Wathi uDavide, Musani ukuyenza loo nto, bazalwana bam, ngento asinike yona uYehova, owasisindisayo, wayinikela ezandleni zethu izihlwele ezazisihlasela.', 'XHO22'),
(13, 30, 24, 'Ngubani na oya kuniva kweli lizwi? Ke njengoko sinjalo isabelo sohlayo ukuya emfazweni, siya kuba njalo isabelo soseleyo emaxhobeni;', 'XHO22'),
(13, 30, 25, 'Ukususela ngaloo mini wakumisa oko umyalelo nommiselo koonyana bakaSirayeli kude kube namhla.', 'XHO22'),
(13, 30, 26, 'Wafika uDavide eTsikelage, wathumela inxenye yexhoba kumadoda amakhulu akwaYuda kubahlobo bakhe, esithi, Yabonani, kukho isipho senu ekuthinjweni kweentshaba zikaYehova;', 'XHO22'),
(13, 30, 27, 'neBhetele, nelasezantsi kweRamoti, neYatire;', 'XHO22'),
(13, 30, 28, 'nabaseArohere, nabaseTsipmoti, nabaseEstemowa;', 'XHO22'),
(13, 30, 29, 'nabaseRagali, nasemizini yamaYerameli, nasemizini yamaKanan;', 'XHO22'),
(13, 30, 30, 'namaHorma, namaKorashi, namaAtage;', 'XHO22'),
(13, 30, 31, 'naseHebron, nasezindaweni zonke abehamba kuzo uDavide namadoda akhe.', 'XHO22'),
(13, 31, 1, 'Alwa amaFilistiya namaSirayeli, asaba amadoda akwaSirayeli phambi kwamaFilisti, afa entabeni yeGilibhowa.', 'XHO22'),
(13, 31, 2, 'AmaFilisti athana mbende noSawule noonyana bakhe; yaye amaFilisti ambulala uYonatan noAbhinadabhi noMalekishuwa, oonyana bakaSawule.', 'XHO22'),
(13, 31, 3, 'Kwaba nzima ukulwa kuSawule; bambetha abatoli yaye wenzakala kakhulu ngenxa yabantu abantsundu.', 'XHO22'),
(13, 31, 4, 'Wathi uSawule kumfana lowo, Rhola ikrele lakho, undibulale ngalo; masithi mhlawumbi bazakufika aba bantu, bandihlabe bandiqalekise. Akavuma umphathi weentonga zakhe; ngokuba ebesoyika kunene. Walithabatha ke uSawule ikrele, waziwisa phezu kwalo.', 'XHO22'),
(13, 31, 5, 'Uthe umphathi weentonga zakhe, akubona ukuba ufile uSawule, waziwisa phezu kwekrele lakhe, wafa kunye naye.', 'XHO22'),
(13, 31, 6, 'Wafa ke uSawule noonyana bakhe abathathu, nomphathi weentonga zakhe, namadoda akhe onke ngamini-nye.', 'XHO22'),
(13, 31, 7, 'Abona amadoda akwaSirayeli abengaphesheya kwentlambo, nabengaphesheya kweYordan, ukuba asabile amadoda akwaSirayeli, ufile uSawule noonyana bakhe, aphuma kuloo mizi, asaba; namaFilisti aya kwakha kuyo.', 'XHO22'),
(13, 31, 8, 'Ngengomso xa amaFilistiya eza kubhunyula ababuleweyo, amfumana uSawule noonyana bakhe bobathathu, bewile entabeni yeGilibhowa.', 'XHO22'),
(13, 31, 9, 'Bandula ke bamnqumla intloko baza bazikhulula izikrweqe zakhe baza bazithumela kwilizwe lamaFilisti ngeenxa zonke ukuba lazise endlwini yemifanekiso yabo eqingqiweyo nasebantwini.', 'XHO22'),
(13, 31, 10, 'Azibeka iintonga zakhe ezindlwini zika-Ashtaroti, awubethelela umzimba wakhe eludongeni lwaseBhete-shane.', 'XHO22'),
(13, 31, 11, 'Beva abemi baseYabheshe yaseGiliyadi akwenzileyo amaFilisti kuSawule;', 'XHO22'),
(13, 31, 12, 'Avuka ke onke amadoda anobukroti, ahamba ubusuku bonke, awuthabatha umzimba kaSawule, nemizimba yoonyana bakhe, eludongeni lwaseBhete-shan, eza eYabheshe, ayitshisa khona.', 'XHO22'),
(13, 31, 13, 'Awathabatha amathambo abo, awangcwabela phantsi komthi eYabheshe, azila ukudla iintsuku zasixhenxe.', 'XHO22'),
(14, 1, 1, 'Wafa uSawule, ekubuyeni kukaDavide ekuwabulaleni ama-Amaleki, uDavide wamxabela uShikele iintsuku zambini;', 'XHO22'),
(14, 1, 2, 'Kwathi ngomhla wesithathu, kwaphuma indoda eminqubeni, zazikrazukile iingubo zikaSawule, inomhlaba entloko; wathi akufika kuDavide, waziphosa phantsi, waguqa.', 'XHO22'),
(14, 1, 3, 'Wathi uDavide kuyo, Ungowaphi na? Wathi kuye, Ndisindisile emkhosini wamaSirayeli.', 'XHO22'),
(14, 1, 4, 'Wathi uDavide kuyo, Kuthekeni na? Nceda undixelele. Wathi, Kungokuba abantu babesoyika ukuphuma emfazweni, kwaza kwawa abantu abaninzi, bafa; NoSawule noYonatan unyana wakhe bafa.', 'XHO22'),
(14, 1, 5, 'Wathi uDavide kwindodana ebimxelela, Wazi ngani na, ukuba bafile uSawule, noYonatan unyana wakhe?', 'XHO22'),
(14, 1, 6, 'Yathi indodana ebimxelela, Ekufikeni kwam entabeni yeGilibhowa, nanko uSawule ezihlaba ngomkhonto; yabona iinqwelo zokulwa nabakhweli zimsukela ngamandla.', 'XHO22'),
(14, 1, 7, 'Wathi akubheka, wandibona, wandibiza. Ndaphendula ndathi: Ndim lo.', 'XHO22'),
(14, 1, 8, 'Wathi kum, Ungubani na? Ndathi kuye, NdingumAmaleki.', 'XHO22'),
(14, 1, 9, 'Wabuya wathi kum, Yima ecaleni kwam, undibulale, ngokuba ndizelwe ngengozi, ngokuba ndisahleli kum.', 'XHO22'),
(14, 1, 10, 'Ndema phezu kwakhe ndaza ndambulala, ngenxa yokuba ndandisazi ukuba akayi kuphila emva kokuba esiwile; ndathabatha isithsaba esibe sisentlokweni yakhe, nesithsaba esibe sisesandleni sakhe, ndazizisa apha enkosini yam.', 'XHO22'),
(14, 1, 11, 'uDavide wazibamba iimpahla zakhe, wazikrazula; enjenjalo onke amadoda abenaye.', 'XHO22'),
(14, 1, 12, 'Balila, balila baza bazila ukutya kwada kwahlwa ngenxa kaSawule nangenxa kaYonatan unyana wakhe nangenxa yabantu bakaYehova nangenxa yendlu kaSirayeli; ngokuba bawa likrele.', 'XHO22'),
(14, 1, 13, 'Wathi uDavide kwindodana ebimxelela, Ungowaphi na? Waphendula wathi, Ndingunyana wendoda engumphambukeli, umAmaleki.', 'XHO22'),
(14, 1, 14, 'Wathi uDavide kuyo, Ùtheni ukuba ungoyiki ukusa isandla sakho, umtshabalalise umthanjiswa kaYehova?', 'XHO22'),
(14, 1, 15, 'uDavide wabiza enye yakumadodana, wathi, Yiza uyibulale. Waza wambetha wafa.', 'XHO22'),
(14, 1, 16, 'Wathi uDavide kuyo, Igazi lakho malibe phezu kwentloko yakho; ngokuba wathi, Mna ndimbulele umthanjiswa kaYehova.', 'XHO22'),
(14, 1, 17, 'uDavide wamenzela isijwili uSawule noYonatan unyana wakhe, ngale ngoma.', 'XHO22'),
(14, 1, 18, 'Wathi mabafundiswe oonyana bakaYuda ukusebenzisa isaphetha; nantso kubhaliwe encwadini yOthe Tye.', 'XHO22'),
(14, 1, 19, 'Bubulewe ubuhle bukaSirayeli ezintabeni zakho; Asiwa amagorha!', 'XHO22'),
(14, 1, 20, 'Musani ukukuxela eGati, ningakuxeleli ezitratweni zeAshkelon; ukuze zingavuyi iintombi zamaFilisti, Zingavuyi iintombi zezinye iintlanga.', 'XHO22'),
(14, 1, 21, 'Ezintabeni zaseGilibhowa, kungabikho mbethe, kungabikho mvula kuni, namasimi emibingelelo; Ngokuba likhona ingweletshetshe yamagorha, Ingweletshetshe kaSawule, elahlwe khona, ngathi akathanjiswanga.', 'XHO22'),
(14, 1, 22, 'Egazini labahlatyiweyo namanqatha amagorha, Isaphetha sikaYonatan besingabuyi, nekrele likaSawule belingabuyi lilambatha.', 'XHO22'),
(14, 1, 23, 'uSawule noYonatan babeziinzwana, benomtsalane ebomini babo, nasekufeni kwabo abahlukananga; Babekhawuleza ngaphezu kwamaxhalanga, babenamandla ngaphezu kweengonyama.', 'XHO22'),
(14, 1, 24, 'Zintombi zakwaSirayeli, mlileleni uSawule, Obenambesa ingubo ebomvu, nasekudleni kwenu, Obeninxiba izihombo zegolide ezambathweni zenu.', 'XHO22'),
(14, 1, 25, 'Hayi, ukuwa kwamagorha emfazweni! Wena Yonatan, ubulawelwe ezintabeni zakho;', 'XHO22'),
(14, 1, 26, 'Ndixhalabile ngawe, mzalwana wam Yonatan; ubunomtsalane kum; Kubalukekile ukundithanda kwakho Ngaphezu kokuthanda komntu oyinkazana.', 'XHO22'),
(14, 1, 27, 'Hayi, ukuwa kwamagorha nezixhobo zemfazwe!', 'XHO22'),
(14, 2, 1, 'Emveni koko wabuza uDavide kuYehova, wathi, Ndinyuke na ndiye emzini wakwaYuda? Wathi uYehova kuye, Vuka! Wathi uDavide, Ndinyuke ndiye phi na? Wathi yena: “YiHebron!', 'XHO22'),
(14, 2, 2, 'Wenyuka waya khona uDavide, nabafazi bakhe bobabini, uAkinowam waseYizereli, noAbhigali, umkaNabhali waseKarmele.', 'XHO22'),
(14, 2, 3, 'uDavide wawasusa amadoda akhe abenaye, ukuba anyuke, iyileyo nendlu yayo, ahlala emizini yaseHebron.', 'XHO22'),
(14, 2, 4, 'Eza amadoda akwaYuda, amthambisa khona apho uDavide, ukuba abe ngukumkani wendlu yakwaYuda. Waxelelwa uDavide, kwathiwa, Amadoda aseYabheshe yaseGiliyadi ngawo abeze kuSawule.', 'XHO22'),
(14, 2, 5, 'uDavide wathumela abathunywa kumadoda aseYabheshe yaseGiliyadi, wathi kuwo, Wanga uYehova anisikelele, ngenxa enokuba nenenceba yenu kuSawule inkosi yenu, nayingcwaba.', 'XHO22'),
(14, 2, 6, 'Kaloku, uYehova undenzele inceba ngenene, ndiya kunibuyisela le nceba, ngenxa yokuba nayenzayo loo nto.', 'XHO22'),
(14, 2, 7, 'Ke kaloku mazomelele izandla zenu nibe nesibindi, ngenxa yokuba uSawule ukumkani wenu ufile, yaye nendlu kaYuda indithambisile ukuba ndibe ngukumkani wayo.', 'XHO22'),
(14, 2, 8, 'Ke uAbhinere, unyana kaNere, umthetheli womkhosi kaSawule, wamthabatha uIshbhoshete, unyana kaSawule, wamzisa eMahanayim;', 'XHO22'),
(14, 2, 9, 'Wamenza ukumkani eGiliyadi, naseAsiriya, naseYizereli, nakwaEfrayim, nakwaBhenjamin, nakumaSirayeli onke.', 'XHO22'),
(14, 2, 10, 'uIshe-bhoshete, unyana kaSawule, ubeminyaka imashumi mane ezelwe, ukuba ngukumkani kwakhe kwaSirayeli, waba neminyaka emibini engukumkani. Ke indlu yakwaYuda yamlandela uDavide.', 'XHO22'),
(14, 2, 11, 'Ixesha uDavide awayengukumkani wendlu kaYuda eHebron laliyiminyaka esixhenxe neenyanga ezintandathu.', 'XHO22'),
(14, 2, 12, 'Wemka ke uAbhinere, unyana kaNere, nabakhonzi bakaNkosi-bhoshete, unyana kaSawule, eMahanayim, baya eGibheyon.', 'XHO22'),
(14, 2, 13, 'uYowabhi unyana kaTseruya nabakhonzi bakaDavide baphuma baza baqubisana echibini laseGibheyon; bahlala phantsi, omnye ekweli cala lebhafu, omnye ekweliya icala lebhafu.', 'XHO22'),
(14, 2, 14, 'Wathi uAbhinere kuYowabhi, Makeme amadodana adlale phambi kwethu. Wathi uYowabhi, Mabasuke basuke.', 'XHO22'),
(14, 2, 15, 'Bandula besuka bema baza badlula kwinani lamaBhenjamin alishumi elinesibini kuIshbhoshete unyana kaSawule neshumi elinesibini labakhonzi bakaDavide.', 'XHO22'),
(14, 2, 16, 'Yaye ngamnye wabamba iqabane lakhe ngentloko waza waxabela kulo ngekrele lakhe ethangeni; ngoko ke bawa kunye. Kungenxa yoko le nto loo ndawo kuthiwa yiHelekati-hasurim eGibheyon.', 'XHO22'),
(14, 2, 17, 'Kwabakho idabi eloyikekayo ngaloo mini; woyiswa ke uAbhinere namadoda akwaSirayeli phambi kwabakhonzi bakaDavide.', 'XHO22'),
(14, 2, 18, 'Kwakukho oonyana abathathu bakaTseruya, uYowabhi, noAbhishayi, noAsaheli; yaye uAsaheli wayelula ezinyaweni zakhe njengexhama.', 'XHO22'),
(14, 2, 19, 'uAsayeli wamsukela uAbhinere; xa ehambayo, makangaphambukeli kuAbhinere aye ekunene okanye ekhohlo.', 'XHO22'),
(14, 2, 20, 'Wabheka ngasemva uAbhinere, wathi, UnguAsayeli na? Waphendula ke yena wathi, Ndinguye.', 'XHO22'),
(14, 2, 21, 'Wathi uAbhinere kuye, Jika uye ekunene, nokuba uye ekhohlo, bamba enye kumadodana, uthabathe iintonga zayo. Akavumanga uAsayeli ukutyeka ekumlandeleni.', 'XHO22'),
(14, 2, 22, 'Wabuya wathi uAbhinere kuAsaheli, Musa ukuwela kum; kutheni ndikubethe? Ndothini na ukububeka ubuso bam phambi komzalwana wakho uYowabhi?', 'XHO22'),
(14, 2, 23, 'Akavuma ukutyeka; ngoko ke, uAbhinere wamgwaza ngomva wentshuntshe phantsi kwentamo yakhe; wawa khona, wafela khona.', 'XHO22'),
(14, 2, 24, 'OoYowabhi noAbhishayi bamsukela ke uAbhinere, lada latshona ilanga, bada bafika endulini yeAma, malungana neGiya, endleleni yentlango yaseGibheyon.', 'XHO22'),
(14, 2, 25, 'Bamngqonga uAbhinere oonyana bakaBhenjamin, baba liqela elinye, bema encotsheni yentaba.', 'XHO22'),
(14, 2, 26, 'uAbhinere wambiza uYowabhi, wathi, Uya kuhlala upheliswa na umkhonto? Anazi na, ukuba isiphelo siya kuba krakra? Kuya kuthatha ixesha elingakanani phambi kokuba uyalele abantu ukuba babuyele emva baye kuleqa abazalwana babo?', 'XHO22'),
(14, 2, 27, 'Wathi uYowabhi, NgoYehova ovileyo, ukuba bekungathanga uthethe, bavuka kusasa abantu, elowo kubazalwana bakhe, bamlandela.', 'XHO22'),
(14, 2, 28, 'Wavuthela isigodlo uYowabhi, bema bonke abantu, ababa sabasukela oonyana bakaSirayeli, ababa saphinda balwe.', 'XHO22'),
(14, 2, 29, 'uAbhinere namadoda akhe bahamba ubusuku bonke entlango, bayiwela iYordan, bayiwela yonke iBhitron, bafika eMahanayim.', 'XHO22'),
(14, 2, 30, 'Wabuya ke uYowabhi, wamlandela uAbhinere; wabahlanganisa bonke abantu, kungekho shumi elinesithoba lamadoda noAsaheli kubakhonzi bakaDavide.', 'XHO22'),
(14, 2, 31, 'Ke abakhonzi bakaDavide babulala inxenye yakwaBhenjamin nakumadoda akwa-Abhinere, kwafa amakhulu amathathu anamanci mathandathu amadoda.', 'XHO22'),
(14, 2, 32, 'Bamthabatha uAsaheli, bamngcwabela engcwabeni likayise eBhetelehem. uYowabhi namadoda akhe bahamba ubusuku bonke, bafika eHebron kusasa.', 'XHO22'),
(14, 3, 1, 'Kwaba kude kube yimfazwe phakathi kwendlu kaSawule nendlu kaDavide; uDavide waya esomelela ngokomelela; yasuka yaphelelwa ngamandla indlu kaSawule.', 'XHO22'),
(14, 3, 2, 'uDavide wazalelwa oonyana eHebron, yaye izibulo lakhe yayinguAmnon ka-Akinowam umYizereli;', 'XHO22'),
(14, 3, 3, 'Owesibini ibinguKilabhi, ka-Abhigali, umkaNabhali waseKarmele; owesithathu nguAbhisalom, unyana kaMagi, intombi kaTalemayi, ukumkani waseGeshuri;', 'XHO22'),
(14, 3, 4, 'owesine nguAdoniya, unyana kaHagiti; owesihlanu nguShefatiya, unyana ka-Abhitali;', 'XHO22'),
(14, 3, 5, 'Owesithandathu nguJitream ngoEgla, umkaDavide; Bona bazalelwa uDavide eHebron.', 'XHO22'),
(14, 3, 6, 'Ke kaloku, kwakubon’ ukuba kukho imfazwe phakathi kwendlu kaSawule nendlu kaDavide, uAbhinere wamana ukuzomeleza ngenxa yendlu kaSawule.', 'XHO22'),
(14, 3, 7, 'uSawule ebeneshweshwe eligama linguRitspa, intombi ka-Aya; Wathi uIshe-bhoshete kuAbhinere, Yini na ukuba ungene ngesithende sikabawo?', 'XHO22'),
(14, 3, 8, 'Wavutha kunene ngumsindo uAbhinere ngamazwi kaNkosi-bhoshete, wathi, Ndiyintloko yenja na eyenzela inceba uYuda namhla, kwindlu kaSawule uyihlo, nakubazalwana bakhe, nakubahlobo bakhe, andikwenzanga oko? uyonga? esandleni sikaDavide, ingaba namhlanje undityhola ngetyala laloo mfazi?', 'XHO22'),
(14, 3, 9, 'uThixo uya kumenzela enjenje uAbhinere, aqokele ukwenjenje, ukuba ndiya kwenjenjalo kuye njengoko uYehova wamfungelayo uDavide;', 'XHO22'),
(14, 3, 10, 'ukuba buguqulwe ubukumkani bendlu kaSawule, buqinise itrone kaDavide kwaSirayeli nakwaYuda, kwaDan kuse eBher-shebha.', 'XHO22'),
(14, 3, 11, 'Akaba nakumphendula nelizwi uAbhinere, ngokuba ebemoyika.', 'XHO22'),
(14, 3, 12, 'uAbhinere wasusa abathunywa kuDavide esikhundleni sakhe, esithi, Lilizwe likabani na eli lizwe? uthi, Yenza umnqophiso nam; yabona, isandla sam sinawe, ukuba ndiwazise kuwe onke amaSirayeli.', 'XHO22'),
(14, 3, 13, 'Wathi, Kulungile; Ndiya kwenza isivumelwano nawe, kodwa inye into endiyifunayo kuwe, yile: Akuyi kububona ubuso bam, ukuba uthe wazisa uMikali, intombi kaSawule, wakuza kundibona.', 'XHO22'),
(14, 3, 14, 'uDavide wesusa abathunywa kuIshe-bhoshete, unyana kaSawule, esithi, Myeke uMikali, umkam, endamthenga ngekhulu leemfele zamaFilisti.', 'XHO22'),
(14, 3, 15, 'Wathumela ke iNkosi uBhoshete ukuba amthabathele kuPaltiyeli unyana kaLayishe, indoda yakhe.', 'XHO22'),
(14, 3, 16, 'Yahamba naye indoda yakhe, ilila, kwada kwesa eBhahurim. Wathi uAbhinere kuyo, Hamba ubuye. Uye wabuya.', 'XHO22'),
(14, 3, 17, 'Wathetha uAbhinere kumadoda amakhulu akwaSirayeli, wathi: “Benifuna uDavide abe ngukumkani wenu.', 'XHO22'),
(14, 3, 18, 'Yenza ngoku, ngenxa yokuba uYehova wathetha ngoDavide esithi: “Ngesandla sikaDavide umkhonzi wam ndiya kubahlangula abantu bam amaSirayeli esandleni samaFilisti nasesandleni seentshaba zabo zonke.', 'XHO22'),
(14, 3, 19, 'Kanjalo, uAbhinere wathetha phambi kweendlebe zikaBhenjamin, noAbhinere waqalisa ukuthetha apho uDavide wayesiva yonke into eyayilungile kuSirayeli noko kwakubonakala kulungile kuyo yonke indlu kaBhenjamin.', 'XHO22'),
(14, 3, 20, 'Waya uAbhinere kuDavide eHebron, enamadoda amashumi mabini. uDavide wamlungiselela ukudla uAbhinere namadoda abenaye.', 'XHO22'),
(14, 3, 21, 'Wathi uAbhinere kuDavide, Ndiya kuhamba ndiye ndimhlanganisele ukumkani, inkosi yam, amaSirayeli onke, enze umnqophiso nawe, ukulawule konke okunqwenelwa yintliziyo yakho. uDavide wambuyisa uAbhinere; wahamba enoxolo.', 'XHO22'),
(14, 3, 22, 'Ke kaloku, khangela, abakhonzi bakaDavide nabakaYowabhi babephuma ukuya kuhlangula lowo uneqhenqa yaye babezisa into eninzi yamaxhoba, yaye uAbhinere wayengekho kuDavide eHebron ngokuba wambuyisa, wemka enoxolo.', 'XHO22'),
(14, 3, 23, 'Wafika uYowabhi nomkhosi wonke owawunaye, waxelelwa uYowabhi, kwathiwa, uAbhinere, unyana kaNere, ubeze kukumkani, wathi ukumkani makahambe, wahamba enoxolo.”', 'XHO22'),
(14, 3, 24, 'Waya uYowabhi kukumkani, wathi, Yintoni na le uyenzileyo? nanku uAbhinere eze kuwe; Yini na ukuba umndulule, abe engasekho?', 'XHO22'),
(14, 3, 25, 'Uyamazi uAbhinere unyana kaNere, ekubeni ebeze kukuxokisa, ukuze akwazi ukuphuma kwakho, nokungena kwakho, akwazi konke okwenzayo.', 'XHO22'),
(14, 3, 26, 'Wemka uYowabhi kuDavide, wasusa abathunywa ukuba bamlandele uAbhinere, bambuyisela emhadini waseSira; ke yena uDavide ubengayazi loo nto.', 'XHO22'),
(14, 3, 27, 'Ekubuyeni kukaAbhinere eHebron, uYowabhi wamthabathela bucala esangweni ukuze athethe naye ngasese, waza wamgwaza apho esiswini, kangangokuba wafa ngenxa yegazi lika-Asaheli umntakwabo.', 'XHO22'),
(14, 3, 28, 'Weva uDavide, wathi kamva, Mna nobukumkani bam asisayi kugwetywa ngonaphakade, ngenxa yamagazi ka-Abhinere, unyana kaNere;', 'XHO22'),
(14, 3, 29, 'makabe phezu kwentloko kaYowabhi, naphezu kwendlu yonke kayise; yaye endlwini kaYowabhi kungabikho mhlambi ovuzayo okanye oneqhenqa nokhonya ngomsimelelo okanye owa ngekrele okanye oswele isonka.', 'XHO22'),
(14, 3, 30, 'uYowabhi noAbhishayi umntakwabo bambulala uAbhinere, ngenxa yokuba wayembulele uAsaheli umntakwabo edabini eGibheyon.', 'XHO22'),
(14, 3, 31, 'Wandula ke wathi uDavide kuYowabhi nakuwo onke amadoda awayenaye: “krazulani izambatho zenu nize nibhinqe amarhonya nize nimlilele uAbhinere. NoKumkani uDavide wawulandela ngokwakhe umqobo.', 'XHO22'),
(14, 3, 32, 'Bamngcwabela eHebron uAbhinere; waliphakamisa ukumkani ilizwi lakhe, walila engcwabeni lika-Abhinere bonke abantu bayalila.', 'XHO22'),
(14, 3, 33, 'Ukumkani wamlilela uAbhinere, wathi, Ufile ngokwegeza na uAbhinere?', 'XHO22'),
(14, 3, 34, 'Izandla zakho bezingabotshwanga, neenyawo zakho bezingabotshwanga; bawa njengokuwa kongendawo. Babuya bonke abantu bammbambazelela.', 'XHO22'),
(14, 3, 35, 'Beza bonke abantu ukuba badle uDavide emini enkulu, wafunga uDavide, wathi, Makenjenje uThixo kum;', 'XHO22'),
(14, 3, 36, 'Lonke ilizwe lakubona oko, kwalunga emehlweni abo, ngokuba yonke into abeyenza ukumkani yakholisa bonke abantu.', 'XHO22'),
(14, 3, 37, 'Baqonda ke lonke uhlanga noSirayeli wonke ngaloo mini, ukuba ukubulawa kuka-Abhinere unyana kaNere kwakungengumthetho kakumkani.', 'XHO22'),
(14, 3, 38, 'Wathi ukumkani kubakhonzi bakhe, Anazi na, ukuba namhlanje kufe inkosana nomntu omkhulu kwaSirayeli?', 'XHO22'),
(14, 3, 39, 'Kwaye namhlanje ndibuthathaka, nangona ndathanjiswa ukuba ndibe ngukumkani; la madoda, oonyana bakaTseruya, anamandla kunam;', 'XHO22'),
(14, 4, 1, 'Weva unyana kaSawule ukuba uAbhinere uyibhuqile iHebron, zotha izandla zakhe. Oyika onke amaSirayeli.', 'XHO22'),
(14, 4, 2, 'Unyana kaSawule ubenamadoda amabini angabathetheli bemikhosi; igama lenye belinguBhahana, igama leyesibini belinguRegabhi, oonyana bakaRimon, waseBheroti, koonyana bakaBhenjamin; ngokuba neBheroti kwabalelwa kuyo noBhenjamin;', 'XHO22'),
(14, 4, 3, '(AmaBheroti asabela eGitayim, ahlala khona unanamhla.)', 'XHO22'),
(14, 4, 4, 'Ke kaloku uYonatan unyana kaSawule ubenonyana ofe iinyawo; ubeminyaka mihlanu ezelwe, ukufika kwelizwi likaSawule noYonatan, livela eYizereli; umxhasi wakhe wamthabatha, wabaleka; wathi ebaleka ngokukhawuleza, wawa waba sisiqhwala. Igama lakhe lalinguMefibhoshete.', 'XHO22'),
(14, 4, 5, 'Oonyana bakaRimon waseBheroti, uRekabhi noBhahana, bahamba baza bafika endlwini kaIshe-bhosheti emini emaqanda, elele ebhedini emini enkulu.', 'XHO22'),
(14, 4, 6, 'Bafika phakathi kwendlu, ngathi baya kuthabatha ingqolowa; yaye bamxhoma kubambo lwesihlanu, waza uRegabhe noBhahana umntakwabo babaleka.', 'XHO22'),
(14, 4, 7, 'Bangena ke endlwini, elele esingqengqelweni sakhe egumbini lakhe lokulala; bambetha, bambulala, bamnqumla intloko, bayithabatha intloko yakhe, babarhuqela ethafeni ebusuku, bonke abantu.', 'XHO22'),
(14, 4, 8, 'Bayizisa intloko yeNkosi uBhoshete kuDavide eHebron, bathi kukumkani, Nantsi intloko yeNkosi Bhoshete, unyana kaSawule, utshaba lwakho, obelufuna ukukubulala; namhla uYehova umphindezele ukumkani, inkosi yam, kuSawule, nakoonyana bakhe.', 'XHO22'),
(14, 4, 9, 'uDavide wabaphendula ooRekabhi noBhahana, umkhuluwa wakhe, oonyana bakaRimon waseBheroti, wathi kubo, Ehleli nje uYehova, owukhululeyo umphefumlo wam kuzo zonke iimbandezelo;', 'XHO22'),
(14, 4, 10, 'Wandixelela ngale nto, wathi, Yabona, ufile uSawule; ngokuba ebesithi, uzise iindaba ezilungileyo;', 'XHO22'),
(14, 4, 11, 'Babeka phi na ke abangendawo, ukumbulala umntu olungileyo endlwini yakhe, esililini sakhe? Andiyi kulifuna igazi lakhe ezandleni zenu, ndinisuse emhlabeni?', 'XHO22'),
(14, 4, 12, 'uDavide wabawisela umthetho abakhonzi bakhe, bababulala, babanqumla izandla neenyawo, babaxhoma echibini laseHebron. Bayithabatha ke intloko kaNkosi-bhoshete, bayingcwabela engcwabeni lika-Abhinere eHebron.', 'XHO22'),
(14, 5, 1, 'Zafika ke zonke izizwe zakwaSirayeli kuDavide eHebron, zathi, Uyabona, singamathambo akho nenyama yakho.', 'XHO22'),
(14, 5, 2, 'Ngaphambili, oko uSawule ebengukumkani wethu, ibinguwe ebebakhupha oonyana bakaSirayeli, ubangenise; wathi uYehova kuwe, Wena uya kubanyamekela abantu bam amaSirayeli, wena ube ngumlawuli kwaSirayeli.', 'XHO22'),
(14, 5, 3, 'Aya ke onke amadoda amakhulu akwaSirayeli kukumkani eHebron; Ukumkani uDavide wenza umnqophiso nawo eHebron, phambi koYehova; amthambisa uDavide ukuba abe ngukumkani wakwaSirayeli.', 'XHO22'),
(14, 5, 4, 'uDavide ubeminyaka imashumi mathathu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mane engukumkani.', 'XHO22'),
(14, 5, 5, 'uHebron wayengukumkani kwaYuda iminyaka esixhenxe neenyanga ezintandathu; yaye iYerusalem yalawula iminyaka engamashumi amathathu anesithathu kuSirayeli wonke noYuda.', 'XHO22'),
(14, 5, 6, 'Waza ukumkani namadoda akhe baya eYerusalem kumaYebhusi abengabemi belizwe, bathetha kuDavide, bathi, Ukuba uthe akwasusa iimfama neziqhwala, akuyi kungena apha.', 'XHO22'),
(14, 5, 7, 'uDavide wayithimba inqaba yaseZiyon; sisixeko sikaDavide.', 'XHO22'),
(14, 5, 8, 'Wathetha uDavide ngaloo mini, wathi, Bonke abaya kukhwela emhadini, babulale amaYebhusi, neziqhwala, neemfama, abathiyiweyo entliziyweni kaDavide, ngabo aba yintloko nentloko; Bathi ngoko, Iimfama neziqhwala azinakungena endlwini.', 'XHO22'),
(14, 5, 9, 'uDavide waya kuhlala kuloo nqaba, wayibiza ngokuba sisiXeko sikaDavide. Wazakha uDavide yonke into esuka eMilo naphakathi kwelizwe.', 'XHO22'),
(14, 5, 10, 'uDavide waqhubeka ekhula, yaye uYehova, uThixo wemikhosi, wayenaye.', 'XHO22'),
(14, 5, 11, 'uHiram, ukumkani waseTire, wathumela abathunywa kuDavide, nemisedare, neengcibi zemithi, neengcibi zamatye, bamakhela indlu uDavide.', 'XHO22'),
(14, 5, 12, 'uDavide waqonda ukuba uYehova unyule yena ukuba abe ngukumkani kwaSirayeli nokuba wayebuphakamisile ubukumkani bakhe ngenxa yabantu bakhe amaSirayeli.', 'XHO22'),
(14, 5, 13, 'uDavide wabuya wazeka abafazi nabafazi eYerusalem, ekundulukeni kwakhe eHebron, uDavide wazala abanye oonyana neentombi.', 'XHO22'),
(14, 5, 14, 'Ngawo la amagama abazalelwe eYerusalem; uSamuwa, noShobhabhe, noNatan, noSolomon;', 'XHO22'),
(14, 5, 15, 'noIbhare, noElishuwa, noNefege, noYafiya;', 'XHO22'),
(14, 5, 16, 'noElishama, noEliyada, noElifeleti.', 'XHO22'),
(14, 5, 17, 'Xa amaFilisti evayo ukuba babemthambisile uDavide ukuba abe ngukumkani wakwaSirayeli, onke amaFilisti enyuka esiya kufuna uDavide; Weva ke uDavide, wehla waya entabeni enqatyisiweyo.', 'XHO22'),
(14, 5, 18, 'Afika amaFilisti, athana mbende entilini yamaRafa.', 'XHO22'),
(14, 5, 19, 'Wabuza uDavide kuYehova, esithi, Ndihambe ndiye na kumaFilisti? Ngaba uya kuwanikela esandleni sam? Wathi uYehova kuDavide, Yima, ngokuba ndiya kuwanikela okunene amaFilisti esandleni sakho.', 'XHO22'),
(14, 5, 20, 'Weza uDavide eBhahali-perasim; wahlala khona uDavide kubo, wathi, uYehova undityhoboze njengomlambo, esilwa neentshaba zam phambi kwam ngenxa yoko wathi igama laloo ndawo yiBhahali-peratsim.', 'XHO22'),
(14, 5, 21, 'Bayishiya apho imifanekiso yabo eqingqiweyo; uDavide namadoda akhe bayitshisa;', 'XHO22'),
(14, 5, 22, 'Aphinda enyuka amaFilisti, athana mbende entilini yamaRafa.', 'XHO22'),
(14, 5, 23, 'uDavide wabuza kuYehova, esithi, Musa ukunyuka; kodwa niwajikeleze ngasemva nize niwahlasele ngasemithini yamaqunube.', 'XHO22'),
(14, 5, 24, 'Kwaye kuthiwa ukuba usiva isandi sokunyathelwa phezu kwemithi yommirtile, ngoko uya konwaba; ngokuba ngoku uYehova uya kunikhokelela ekuxabeleni izintlu zamaFilisti.', 'XHO22'),
(14, 5, 25, 'Wenza uDavide njengoko uYehova wamwiselayo umthetho; yaye wawaxabela amaFilisti ukususela eGebha ukuya kutsho eGezere.', 'XHO22'),
(14, 6, 1, 'Wabuya wabahlanganisa uDavide bonke abanyulwa bakwaSirayeli, amashumi amathathu amawaka.', 'XHO22'),
(14, 6, 2, 'Waphuma ke uDavide, wahamba nabantu bonke abenabo, esiya eBhahali yakwaYuda, esiya kuyithabathela khona ityeya kaThixo, ogama ligama likaYehova wemikhosi ohleli phezu kweekerubhi.', 'XHO22'),
(14, 6, 3, 'Bayikhwelisa kwinqwelo entsha ityeya yoTHIXO oyinyaniso baza bayisusa endlwini ka-Abhinadabhi eyayiseGibheya; U-Uza noAhiyo, oonyana baka-Abhinadabhi, babeqhuba imoto entsha.', 'XHO22'),
(14, 6, 4, 'Bayikhupha endlwini ka-Abhinadabhi eseGibheha, ndawonye nobuncwane boThixo oyinyaniso; Waphuma uAjo ekhokela umkhombe.', 'XHO22'),
(14, 6, 5, 'uDavide nendlu yonke kaSirayeli baqamba phambi koYehova, bebetha imithi yonke yesipres, neehadi, nemirhubhe, namagubu, namacangci, namacangci.', 'XHO22'),
(14, 6, 6, 'Ekufikeni kwabo esandeni sikaNagon, u-Uza wolulela isandla sakhe etyeyeni yoTHIXO oyinyaniso wayithabatha; ngokuba iinkomo zaziyithuthile.', 'XHO22'),
(14, 6, 7, 'Wavutha umsindo kaYehova kwaUsa; uThixo wamohlwaya khona ngenxa yobugwenxa bakhe; wafela khona phambi kwetyeya kaThixo.', 'XHO22'),
(14, 6, 8, 'uDavide wavutha ngumsindo ngenxa yokuba uYehova ebemisele u-Uza; kwaye loo ndawo yiPeretsusa kude kube namhlanje.', 'XHO22'),
(14, 6, 9, 'uDavide wamoyika uYehova ngaloo mini, wathi, Yothini na ukuza kum ityeya kaYehova?', 'XHO22'),
(14, 6, 10, 'uDavide akavuma ukuyisa ityeya kaYehova emzini kaDavide; ke uDavide wayiphambukisela endlwini kaObhedi-edom waseGati.', 'XHO22'),
(14, 6, 11, 'Yahlala ke ityeya kaYehova endlwini kaObhedi-edom waseGati iinyanga zantathu; uYehova wamsikelela uObhedi-edom, nendlu yakhe yonke.', 'XHO22'),
(14, 6, 12, 'Waxelelwa ukumkani uDavide, kwathiwa, Uyisikelele uYehova indlu kaObhedi-edom, nempahla yakhe yonke, ngenxa yetyeya kaThixo. Waya uDavide, wayithabatha ityeya kaThixo evuya endlwini kaObhedi-edom, wayisa emzini kaDavide.', 'XHO22'),
(14, 6, 13, 'Xa abathwali bomkhombe kaYehova babehamba izinyuko ezithandathu, wabingelela ngeenkomo nezilwanyana ezityetyisiweyo.', 'XHO22'),
(14, 6, 14, 'uDavide wangqungqa ngamandla akhe onke phambi koYehova; yaye uDavide wayebhinqe iqhiya yelinen.', 'XHO22'),
(14, 6, 15, 'uDavide nendlu yonke kaSirayeli babeyinyusa ke ityeya kaYehova, bevuya, behlokomisa isigodlo.', 'XHO22'),
(14, 6, 16, 'Kwathi ke ekufikeni kwetyeya kaYehova kwisiXeko sikaDavide, uMikali, intombi kaSawule, wavela ngefestile, wabona uKumkani uDavide exhuma esina phambi koYehova; wamcekisa entliziyweni yakhe.', 'XHO22'),
(14, 6, 17, 'Bayingenisa ityeya kaYehova, bayimisa endaweni yayo phakathi kwentente, abeyilungisele uDavide; yaye uDavide wanikela iminikelo etshiswayo neyombulelo phambi koYehova.', 'XHO22'),
(14, 6, 18, 'Akugqiba uDavide ukunyusa amadini anyukayo neyombulelo, wabasikelela abantu, egameni likaYehova wemikhosi.', 'XHO22'),
(14, 6, 19, 'Wababela bonke abantu, kwingxokolo yonke yakwaSirayeli, abafazi namadoda; elowo wamnika iqhekeza lesonka, neqhekeza lesonka, nesuntswana lewayini, nendebe yewayini. Emva koko bonke abantu baya emakhayeni abo.', 'XHO22'),
(14, 6, 20, 'Wabuya ke uDavide, waya kusikelela indlu yakhe. Waphuma uMikali, intombi kaSawule, esiya kukhawulela uDavide, wathi, Hayi, ukuzizukisa kokumkani wamaSirayeli namhla, njengoko ezibonakalisile namhla emehlweni abakhonzazana babakhonzi bakhe, njengoko omnye wamatshijolo ezenza oneentloni.', 'XHO22'),
(14, 6, 21, 'Wandula ke wathi uDavide kuMikali: “Ndandiphambi koYehova owandinyula phambi koyihlo naphambi kwendlu yakhe yonke ukuba andimisele ukuba ndibe ngumlawuli wabantu bakaYehova amaSirayeli; ke ngoko ndiya kuvuma phambi koYehova.', 'XHO22'),
(14, 6, 22, 'Ndiya kuba lisikizi ngaphezu koko, ndibe mncinane emehlweni am; neempelesi ozithethileyo, ndozukiswa zizo.', 'XHO22'),
(14, 6, 23, 'Ke uMikali intombi kaSawule akabanga namntwana kwada kwayimini yokufa kwakhe.', 'XHO22'),
(14, 7, 1, 'Kwathi, ehleli ukumkani endlwini yakhe, uYehova wamphumza ukuba awenze wonke umsebenzi wakhe;', 'XHO22'),
(14, 7, 2, 'Wathi uYehova kuNatan umprofeti, Uyabona, mna ndihleli endlwini yemisedare, ke yona ityeya kaThixo ihleli elinen ecikizekileyo.', 'XHO22'),
(14, 7, 3, 'Wathi uNatan kukumkani, Konke okusentliziyweni yakho, hamba uye ukwenze; ngokuba uYehova unawe.', 'XHO22'),
(14, 7, 4, 'Kwafika ilizwi likaYehova kuNatan ngobo busuku, lisithi:', 'XHO22'),
(14, 7, 5, 'Yiya uthethe kumkhonzi wam uDavide, uthi, Utsho uYehova ukuthi, Nguwe na ke oya kundakhela indlu, yokuba ndihlale kuyo?', 'XHO22'),
(14, 7, 6, 'Ukususela kwixesha endabanyusa ngalo oonyana bakaSirayeli eYiputa de kube namhla andihlalanga nakweyiphi na indlu, kodwa ndandihamba ententeni nasententeni.', 'XHO22'),
(14, 7, 7, 'Ezindaweni zonke endahambahamba kuzo mna noonyana bonke bakaSirayeli, ndathetha ilizwi kwesinye sezizwe zakwaSirayeli, endasiwisela umthetho wokuba sibaluse abantu bam amaSirayeli, ndathi, Yini na ukuba ningandinyamekeli? indlu yomsedare?', 'XHO22'),
(14, 7, 8, 'Ngoko ke wothi kuDavide umkhonzi wam, Utsho uYehova wemikhosi ukuthi, Mna ndakuthabatha esibayeni, emva komhlambi, ukuba ube yinganga yabantu bam amaSirayeli.', 'XHO22'),
(14, 7, 9, 'Ndandinawe naphi na apho wawusiya khona, yaye ndazitshabalalisa zonke iintshaba zakho phambi kwakho ndaza ndakwenzela igama elikhulu njengegama labo bakhulu ehlabathini.', 'XHO22'),
(14, 7, 10, 'Ngaphezu koko, ndiya kubanyulela indawo abantu bam amaSirayeli ndize ndibaqinise, ukuze bahlale endaweni yabo, bangabi sagungqiswa kwakhona; oonyana bobutshijolo bangabi sabacinezela njengangaphambili;', 'XHO22'),
(14, 7, 11, 'Ukususela kwixesha endayalela ngalo abagwebi ukuba babalawule abantu bam amaSirayeli, ndaza ndaninika ukuphumla ezintshabeni zenu zonke. Kwakhona uYehova ukuxelela ukuba uya kukwenzela indlu.', 'XHO22'),
(14, 7, 12, 'Xa ithe yazaliseka imihla yakho, walala noyihlo, ndoyivelisa emva kwakho imbewu yakho, ibonakale esiswini sakho, ndibuqinise ubukumkani bakhe.', 'XHO22'),
(14, 7, 13, 'Nguye oya kulakhela igama lam indlu, ndiyiqinise itrone yobukumkani bakhe, kude kuse ephakadeni.', 'XHO22'),
(14, 7, 14, 'Mna ndiya kuba nguyise kuye, yena abe ngunyana wam; Ukuba uthe wenza ububi, ndombetha ngentonga yabantu, nangentonga yoonyana baka-Adam;', 'XHO22'),
(14, 7, 15, 'Ke yona inceba yam ayiyi kumka kuye, njengoko ndayisusayo kuSawule, endimsusileyo ebusweni bakho.', 'XHO22'),
(14, 7, 16, 'Indlu yakho nobukumkani bakho buya kuma ngonaphakade phambi kwakho; Itrone yakho iya kuzinza ngonaphakade.', 'XHO22'),
(14, 7, 17, 'Njengaloo mazwi onke, ngokwalo mbono, wenjenjalo uNatan ukuthetha kuDavide.', 'XHO22'),
(14, 7, 18, 'Wangena ukumkani uDavide, wahlala phambi koYehova, wathi, Ndingubani na mna, Nkosi yam Yehova? Iyintoni na indlu yam, ukuba ude undizise apha?', 'XHO22'),
(14, 7, 19, 'Bekusekuncinane oko emehlweni akho, Nkosi enguMongami Yehova; Wathetha ixesha elide ngendlu yomkhonzi wakho. Le yindlela yomntu na, Nkosi yam Yehova?', 'XHO22'),
(14, 7, 20, 'Wobuya athini na ke uDavide kuwe? ngokuba wena, Nkosi yam Yehova, uyamazi umkhonzi wakho.', 'XHO22'),
(14, 7, 21, 'Ngenxa yelizwi lakho nangokwentliziyo yakho wenze zonke ezi zinto zinkulu, ukuze umkhonzi wakho azazi.', 'XHO22'),
(14, 7, 22, 'Ngako oko umkhulu, Yehova Yehova, ngokuba akukho unjengawe, akukho Thixo ingenguwe, njengoko sikuvileyo ngeendlebe zethu.', 'XHO22'),
(14, 7, 23, 'Luluphi na uhlanga ehlabathini apha, olunjengohlanga lwenu, olunjengoSirayeli, awazikhululela lona uThixo, ukuba lube luhlanga, luzenzele igama, lulenzele ilizwe lenu izinto ezinkulu ezoyikekayo, phambi kwelizwe lenu phambi kobuso belizwe abantu bakho obakhululeyo ubakhulule? Ufumaneka eYiputa ezintlangeni nakoothixo bazo na?', 'XHO22'),
(14, 7, 24, 'Uqinise abantu bakho amaSirayeli, ukuba babe ngabantu bakho kude kuse ephakadeni; wena Yehova waba nguThixo wabo.', 'XHO22'),
(14, 7, 25, 'Kaloku, Yehova Yehova, ilizwi olithethileyo ngomkhonzi wakho, nangendlu yakhe, limise ngonaphakade, wenze njengoko ukuthethileyo;', 'XHO22'),
(14, 7, 26, 'Malibe likhulu igama lakho kude kuse ephakadeni, kusithiwa, uYehova wemikhosi nguThixo kaSirayeli; Mayizinze indlu kaDavide umkhonzi wakho phambi kwakho.', 'XHO22'),
(14, 7, 27, 'Ngenxa enokuba wena, Yehova wemikhosi, Thixo kaSirayeli, utyhile kumkhonzi wakho, wathi, Ndiya kukwakhela indlu; ngenxa yoko umkhonzi wakho wayifikelela intliziyo yakhe ukuba awuthandaze lo mthandazo.', 'XHO22'),
(14, 7, 28, 'Kaloku, Nkosi yam Yehova, unguye uThixo wena, namazwi akho ayinyaniso;', 'XHO22'),
(14, 7, 29, 'Ke kaloku makukholeke kuwe ukuyisikelela indlu yomkhonzi wakho, ukuze ihlale ihleli phambi kobuso bakho ukusa kwixesha elingenammiselo, ngenxa yokuba wena, iNkosi enguMongami uYehova uthethile; yaye indlu yomkhonzi wakho mayisikelelwe ngentsikelelo yakho ngonaphakade.', 'XHO22'),
(14, 8, 1, 'Emva koko uDavide wawaxabela amaFilisti, waweyisa; uDavide wayithabatha iMetegamma esandleni samaFilisti.', 'XHO22'),
(14, 8, 2, 'Wawaxabela amaMowabhi, wawalinganisa ulutya lokulinganisa, wawawisa; nangeentambo zokulinganisa zibe mbini, ukuze usindise ubomi ngomsonto wonke. Ngoko ke amaMowabhi aba ngabakhonzi bakaDavide abazisa izipho.', 'XHO22'),
(14, 8, 3, 'uDavide wahlala noHadade-Ezere, unyana kaRehobhi, ukumkani waseSobha, ekubuyeni kwakhe, walibuyisela ilizwe lakhe emlanjeni ongumEfrati.', 'XHO22'),
(14, 8, 4, 'uDavide wathimba iwaka leenqwelo zokulwa, namakhulu asixhenxe abamahashe, namashumi amabini amawaka omqikela;', 'XHO22'),
(14, 8, 5, 'Akufika ama-Aram evela eDamasko esiza kumnceda uHadade-Ezere ukumkani waseSobha, uDavide wabulala ama-Aram amashumi amabini anamabini amawaka.', 'XHO22'),
(14, 8, 6, 'uDavide wabeka abalindi eDamasko kwelase-Aram, aza ama-Aram aba ngabakhonzi bakaDavide, abazisa izipho kuye. uYehova wamkhusela uDavide naphi na apho wayesiya khona.', 'XHO22'),
(14, 8, 7, 'uDavide wawathabatha amakhaka egolide awayekubakhonzi bakaHadade-Ezere, wawazisa eYerusalem.', 'XHO22'),
(14, 8, 8, 'eBheta naseBherotayi, izixeko zikaHadadezere, ukumkani uDavide wathabatha ubhedu oluninzi.', 'XHO22'),
(14, 8, 9, 'Weva uToyi, ukumkani waseHamati, ukuba uDavide uyoyisile yonke impi kaHadade-Ezere.', 'XHO22'),
(14, 8, 10, 'Ngoko uYehoram unyana wakhe wathumela kuKumkani uDavide ukuba ambulise aze amsikelele ngenxa yokuba wayelwe noHadadizere waza wamoyisa; ngenxa yokuba uHadadezere wayesilwa noToyi. uYoram weza neempahla zesilivere, nezegolide, nezobhedu;', 'XHO22'),
(14, 8, 11, 'nethe ukumkani uDavide wayingcwalisela uYehova, kunye nesilivere negolide, abeyingcwalisile ezintlangeni zonke awazithimbayo;', 'XHO22'),
(14, 8, 12, 'kuma-Aram, namaMowabhi, noonyana baka-Amon, namaFilisti, nama-Amaleki, namaxhoba kaHadade-Ezere, unyana kaRehobhi, ukumkani waseTsobha.', 'XHO22'),
(14, 8, 13, 'uDavide wazenzela igama ekubuyeni kwakhe ekuwaxabeleni ama-Aram kwiNtlambo yeTyuwa, ishumi elinesibhozo lamawaka.', 'XHO22'),
(14, 8, 14, 'Wamisa abalindi kwaEdom; kulo lonke elakwaEdom wabeka amadoda anamandla, yaye lonke iEdom laba ngabakhonzi bakaDavide. uYehova wamkhusela uDavide naphi na apho wayesiya khona.', 'XHO22'),
(14, 8, 15, 'Waye engukumkani ke uDavide kumaSirayeli onke; uDavide ubesenza okusesikweni nangobulungisa ebantwini bakhe bonke.', 'XHO22'),
(14, 8, 16, 'uYowabhi, unyana kaTseruya, ubephethe umkhosi; nguYoshafati unyana ka-Akiludi, obephethe;', 'XHO22'),
(14, 8, 17, 'uTsadoki unyana ka-Ahitubhi, noAhimeleki, unyana ka-Abhiyatare, babengababingeleli; uSeraya ubengumbhali;', 'XHO22'),
(14, 8, 18, 'uBhenaya unyana kaYehoyada wayephethe amaKrete namaPleti; yaye oonyana bakaDavide babeziintloko.', 'XHO22'),
(14, 9, 1, 'Wathi uDavide, Kusekho mpunde na kwindlu kaSawule, endiya kuyisindisa ngenxa kaYonatan?', 'XHO22'),
(14, 9, 2, 'Ke endlwini kaSawule bekukho isicaka esigama linguTsibha. Bambizela kuDavide, wathi ukumkani kuye, UnguTsibha na? Wathi, Ngumkhonzi wakho lo.', 'XHO22'),
(14, 9, 3, 'Wathi ukumkani, Akukho bani wendlu kaSawule, endinokumbonisa inceba kaThixo? Wathi uTsibha kukumkani, Omnye unyana kaYonatan usisiqhwala.', 'XHO22'),
(14, 9, 4, 'Wathi ukumkani kuye, Uphi na? Wathi uTsibha kukumkani, Nantsiya iLodebhare endlwini kaMagire, unyana ka-Amiyeli;', 'XHO22'),
(14, 9, 5, 'Wathumela ukumkani uDavide ukuba amthabathe endlwini kaMagire unyana ka-Amiyeli, eLodebhare.', 'XHO22'),
(14, 9, 6, 'Weza uMefibhoshete, unyana kaYonatan, unyana kaSawule, kuDavide, wawa ngobuso, waqubuda kuye. Wathi uDavide, Mefibhoshete! Wathi, Nanko umkhonzi wakho!', 'XHO22'),
(14, 9, 7, 'Wathi uDavide kuye, Musa ukoyika; ngokuba ndiya kuba nemfesane kuwe ngenene, ngenxa kaYonatan uyihlo; ndiya kulibuyisela lonke ilizwe likaSawule uyihlo; kwaye niya kusoloko nisidla isonka etafileni yam.', 'XHO22'),
(14, 9, 8, 'Waqubuda, wathi, Uyintoni na umkhonzi wakho, ukuba ukhangele nenja efileyo nje, enjengam nje?', 'XHO22'),
(14, 9, 9, 'Ukumkani wabiza uTsibha, umfana wakwaSawule, wathi kuye, Yonke into ebiyekaSawule, nendlu yakhe yonke, ndiyinike unyana wenkosi yakho.', 'XHO22'),
(14, 9, 10, 'Wena ke, wena noonyana bakho, nabakhonzi bakho, nimtyale umhlaba, nimvelise iziqhamo, adle unyana wokumkani wenu; ke uMefibhoshete, unyana wenkosi yakho, uya kusoloko esidla isonka etafileni yam. UZibha ubenoonyana abalishumi elinabahlanu, nabakhonzi abamashumi mabini.', 'XHO22'),
(14, 9, 11, 'Wathi uTsibha kukumkani, Njengoko ukumkani, inkosi yam, wamwisela umthetho umkhonzi wakhe, uya kwenza ngako umkhonzi wakho. Ke yena uMefibhoshete, utsho ukumkani, uya kudla etafileni yam njengomnye woonyana bokumkani.', 'XHO22'),
(14, 9, 12, 'uMefibhoshete wayenonyana omncinane, ogama linguMika. Yaye bonke abo babehlala endlwini kaTsibha babengabakhonzi bakaMefibhoshete.', 'XHO22'),
(14, 9, 13, 'uMefibhoshete ubehlala eYerusalem, ngokuba ubesidla etafileni yokumkani ngamaxesha onke; wayesisiqhwala emilenzeni yakhe yomibini.', 'XHO22'),
(14, 10, 1, 'Emva koko wafa ukumkani woonyana baka-Amon, kwalawula uHanun, unyana wakhe, esikhundleni sakhe.', 'XHO22'),
(14, 10, 2, 'Wathi uDavide, Mandimenzele inceba uHanun unyana kaNahashe, njengokuba noyise wandenzelayo inceba. Wathumela uDavide ukuba amkhuze ngabakhonzi bakhe ngenxa kayise. Bafika abakhonzi bakaDavide ezweni loonyana baka-Amon.', 'XHO22'),
(14, 10, 3, 'Bathi abathetheli boonyana baka-Amon kuHanun umniniyo, Uthi na wena, Utsho na ukuthi, Uyihlo uDavide ebebeka intuthuzelo kuwe na? uDavide akabathumanga na abakhonzi bakhe kuni, ukuba awuhlole lo mzi, bawuncothule, bawutshabalalise?', 'XHO22'),
(14, 10, 4, 'Wababamba ke uHanun abakhonzi bakaDavide, wabacheba isiqingatha seendevu zabo, wazicanda iingubo zabo, wada waya esinqeni, wada wabandulula.', 'XHO22'),
(14, 10, 5, 'Kwathi, xa bamxelelayo uDavide, wathumela ukuba bawahlangabeze; ngokuba amadoda lawo abeneentloni kakhulu; Wathi ukumkani, Hlalani eYeriko zide zihlume iindevu zenu, nandule ukubuya.', 'XHO22'),
(14, 10, 6, 'Babona oonyana baka-Amon ukuba badakumbile phambi koDavide, bathumela oonyana baka-Amon, baqesha kuma-Aram aseBhete-hobhi, nakuma-Aram aseTsobha, amashumi amabini amawaka amadoda ahamba ngeenyawo, nakukumkani waseMahaka, iwaka lamadoda, ishumi elinesibini lamawaka amadoda ahamba ngeenyawo.', 'XHO22'),
(14, 10, 7, 'Akuyiva le nto uDavide wathumela uYowabhi nomkhosi wonke wamagorha.', 'XHO22'),
(14, 10, 8, 'Baphuma oonyana baka-Amon, balwa idabi ekungeneni kwesango; ama-Aram aseSobha, naseRehobhi, naseIstobhi, naseMahagi, ebeyedwa ezweni.', 'XHO22'),
(14, 10, 9, 'Wabona uYowabhi ukuba imfazwe ifike ngaphambili nangasemva kwakhe;', 'XHO22'),
(14, 10, 10, 'Abanye abantu wabanikela kuAbhishayi umntakwabo, ukuze abalungiselele nxamnye noonyana baka-Amoni.', 'XHO22'),
(14, 10, 11, 'Wathi, Xa athe andifumana ama-Aram, wondinceda; kodwa ukuba oonyana baka-Amoni bomelele kunawe, ndiya kuza ndize ndikuncede.', 'XHO22'),
(14, 10, 12, 'Yiba nesibindi size senze isabelo sethu ukuze kulungelwe uhlanga lwethu nakwizixeko zoThixo wethu, yaye ngamana uYehova angenza ukuthanda kwakhe.', 'XHO22'),
(14, 10, 13, 'Wasondela uYowabhi namadoda abenaye, ukuba bawaxabele ama-Aram; asaba ebusweni bakhe.', 'XHO22'),
(14, 10, 14, 'Xa oonyana baka-Amoni babona ukuba ama-Aram asabile, nabo basaba phambi koAbhishayi baza bangena esixekweni. Wabuya ke uYowabhi koonyana baka-Amon, weza eYerusalem.', 'XHO22'),
(14, 10, 15, 'Akubona ama-Aram ukuba oyiswe ngamaSirayeli, ahlanganisana ndawonye.', 'XHO22'),
(14, 10, 16, 'Wathumela uHadadezere, waya kuwathabatha ama-Aram abephesheya koMlambo, afike eHelam; ekhokelwa nguShobhage, injengele yomkhosi kaHadadezere.', 'XHO22'),
(14, 10, 17, 'Kwaxelwa kuDavide, wawahlanganisa onke amaSirayeli, wayiwela iYordan, waya kufika eHelam. Yaye amaSiriya ahlanganisana ndawonye ukuba alwe noDavide, yaye alwa naye.', 'XHO22'),
(14, 10, 18, 'Asaba ama-Aram phambi koonyana bakaSirayeli; uDavide wabulala kuma-Aram amakhulu asixhenxe amadoda eenqwelo zokulwa, namashumi amane amawaka abamahashe, noSobhage, umthetheli womkhosi, wafela khona.', 'XHO22'),
(14, 10, 19, 'Babona bonke ookumkani bakaHadadezere ukuba uyoyisiwe phambi koonyana bakaSirayeli, baxolelana noonyana bakaSirayeli, babakhonza. Ngoko ke ama-Aram oyika ukubanceda oonyana baka-Amon kwakhona.', 'XHO22'),
(14, 11, 1, 'Wakuzaliseka umnyaka, ngexesha lokulwa kookumkani, uDavide wamthuma uYowabhi, nabakhonzi bakhe, namaSirayeli onke; yaye bababulala oonyana baka-Amoni baza bayibulala iRabha. Kodwa uDavide wayeseseYerusalem.', 'XHO22'),
(14, 11, 2, 'Kwahlwa, wesuka uDavide esingqengqelweni sakhe, wahambahamba phezu kwendlu yokumkani; yaye loo mfazi wayemhle kunene ngembonakalo.', 'XHO22'),
(14, 11, 3, 'Wathumela uDavide ukuba aye kubuza kuloo ntokazi. Wathi omnye, Asinguye na uBhatshebha, intombi kaEliyam, umkaUriya umHeti?', 'XHO22'),
(14, 11, 4, 'uDavide wathuma abathunywa ukuba bamthabathe; wamngena, walala naye; kuba ebehlambulukile ihambo yakhe, wabuyela endlwini yakhe.', 'XHO22'),
(14, 11, 5, 'Wamitha umfazi lowo, wathumela ukuya kuxelela uDavide, esithi, Ndimithi.', 'XHO22'),
(14, 11, 6, 'uDavide wathumela kuYowabhi, esithi, Thumela kum u-Uriya umHeti. uYowabhi wamthumela u-Uriya kuDavide.', 'XHO22'),
(14, 11, 7, 'Weza u-Uriya kuye; uDavide wambuza ukuba unjani na uYowabhi, banjani na abantu, injani na imfazwe.', 'XHO22'),
(14, 11, 8, 'Wathi uDavide ku-Uriya, Yihla uye endlwini yakho, uhlambe iinyawo zakho. Waphuma u-Uriya endlwini yokumkani; walandelwa yinto eninzi yezidlo zokumkani.', 'XHO22'),
(14, 11, 9, 'U-Uriya walala emnyango wendlu yokumkani, nabakhonzi bonke benkosi yakhe, akehla aye endlwini yakhe.', 'XHO22'),
(14, 11, 10, 'Kwaxelwa kuDavide, kwathiwa, U-Uriya akehla aye endlwini yakhe; Yini na ukuba ungehli uye endlwini yakho?', 'XHO22'),
(14, 11, 11, 'Wathi u-Uriya kuDavide, Ityeya namaSirayeli namaYuda ahleli ezintenteni; Inkosi yam uYowabhi, nabakhonzi benkosi yam, babemise iintente ezweni; Ndiya kungena endlwini yam ndidle, ndisele, ndilale nomkam? ukuba niphilile, uphila umphefumlo wakho, andiyi kuyenza loo nto.', 'XHO22'),
(14, 11, 12, 'Wathi uDavide ku-Uriya, Naye usahleli apha, ndokukhulula ngomso. Yaye u-Uriya wayithimba iYerusalem ngaloo mini nangengomso.', 'XHO22'),
(14, 11, 13, 'Wambiza uDavide, wadla, wasela ngakuye wanxila, waphuma ngokuhlwa waya kulala esingqengqelweni sakhe nabakhonzi benkosi yakhe, akehla aye endlwini yakhe.', 'XHO22'),
(14, 11, 14, 'Kwathi kusasa uDavide wabhalela uYowabhi incwadi, wayithumela ngo-Uriya.', 'XHO22'),
(14, 11, 15, 'Wabhala ke encwadini, wathi, Mbekeni u-Uriya phambi kwemfazwe enzima, nimndulule, abuyiswe, afe.', 'XHO22'),
(14, 11, 16, 'Akuwubona uYowabhi umzi lowo, wambeka u-Uriya kwindawo abesazi ukuba kukho amadoda anobukroti.', 'XHO22'),
(14, 11, 17, 'Aphuma amadoda aloo mzi, aya kulwa noYowabhi; kwawa amadoda kubakhonzi bakaDavide; wafa naye u-Uriya umHeti.', 'XHO22'),
(14, 11, 18, 'Emva koko uYowabhi wathumela waza wamxelela uDavide konke malunga nedabi;', 'XHO22'),
(14, 11, 19, 'Wamwisela umthetho umthunywa, wathi, Xa ugqibileyo ukumxelela ukumkani iindaba zemfazwe;', 'XHO22'),
(14, 11, 20, 'Wenyuka umsindo wokumkani, wathi kuni, Ibiyini na ukuba nisondele phakathi komzi ngokulwa? Ubungazi na ukuba zodla eludongeni?', 'XHO22'),
(14, 11, 21, 'Ngubani na owambulala uAbhimeleki unyana kaYerubheseti? Asinguye na owagalela kuye ilitye lokusila ephezu kodonga, wada wafela eTebhetse? Yini na ukuba usondele eludongeni? wathi, Utshabalele umkhonzi wakho u-Uriya umHeti.', 'XHO22'),
(14, 11, 22, 'Wahamba umthunywa, wafika wamxelela uDavide konke abemthume kona uYowabhi.', 'XHO22'),
(14, 11, 23, 'Wathi umthunywa lowo kuDavide, La madoda asoyisa, esifa ezweni, sahlala nawo kwada kwasa ekungeneni kwesango.', 'XHO22'),
(14, 11, 24, 'Abadla eludongeni badla abaluse abakhonzi bakho; kwafa inxenye yakubakhonzi bokumkani, wafa nomkhonzi wakho u-Uriya umHeti.', 'XHO22'),
(14, 11, 25, 'Wathi uDavide kumthunywa lowo, Yithi kuYowabhi, Mayingakuvuthi le nto, ngokuba umkhonto ubulala bonke abantu; qinisa ukulwa kwakho nomzi lowo, utshatyalalisiwe, womeleze ngawo.', 'XHO22'),
(14, 11, 26, 'Weva umka-Uriya ukuba ufile u-Uriya indoda yakhe, wayikhalela indoda yakhe.', 'XHO22'),
(14, 11, 27, 'Lakuzaliseka ixesha lesijwili, wathumela uDavide, wamthabathela endlwini yakhe, waba ngumkakhe, wamzalela unyana. Kodwa into eyenziwa nguDavide yayiphosakele emehlweni kaYehova.', 'XHO22'),
(14, 12, 1, 'uYehova wathumela uNatan kuDavide. Weza kuye, wathi kuye, Kwakukho amadoda amabini kwidolophu enye; omnye usisityebi, omnye elihlwempu.', 'XHO22'),
(14, 12, 2, 'Indoda esisityebi yayinemihlambi yeegusha neenkomo ezininzi.', 'XHO22'),
(14, 12, 3, 'Elihlwempu yayingenanto, kuphela yayinemva nazana encinane inye, ebiyizuzile, yayikhulisa; yadla isonka sayo, yasela endebeni yayo, yalala esifubeni sayo, yanjengentombi kuye.', 'XHO22'),
(14, 12, 4, 'Ke kaloku, kwafika undwendwe kumntu osisityebi, wayeka ukuthabatha empahleni yakhe emfutshane neenkomo zakhe, ukuba amgcine umhambi lowo ubeze kuye; wathabatha ixhwane lendoda elihlwempu, wayilungiselela umntu oze kuyo.', 'XHO22'),
(14, 12, 5, 'Wavutha umsindo kaDavide ngenxa yaloo ndoda; Wathi kuNatan, NgoYehova ovayo, inene, loo mntu wenze loo nto uya kufa.', 'XHO22'),
(14, 12, 6, 'Woyibuyisela imvana ezimvini, ngenxa yokuba eyenzile le nto nangenxa yokuba engenanceba.', 'XHO22'),
(14, 12, 7, 'Wathi uNatan kuDavide, Nguwe loo ndoda. Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndikuthambisile ukuba ube ngukumkani wakwaSirayeli, ndikuhlangule esandleni sikaSawule;', 'XHO22'),
(14, 12, 8, 'ndakunika indlu yenkosi yakho, nabafazi benkosi yakho esifubeni sakho, ndakunika indlu yakwaSirayeli neyakwaYuda; ke ukuba ibithe yancinane, ngendikunike oku, noko;', 'XHO22'),
(14, 12, 9, 'Kutheni na ukuba uwudelele umyalelo kaYehova wokuba wenze okubi emehlweni akhe? wambulala u-Uriya umHeti ngekrele, wamthabatha umkakhe, wangumkakhe, wambulala ngekrele loonyana baka-Amon.', 'XHO22'),
(14, 12, 10, 'Ngoku ikrele aliyi kumka endlwini yakho; ngenxa enokuba undidelile, wamzeka umkaUriya umHeti, wangumkakho.', 'XHO22'),
(14, 12, 11, 'Wathi uYehova, Yabona, ndikukhuphela ububi obuphuma endlwini yakho, ndibathabathe abafazi bakho, basuke ebusweni bakho, ndibanike abakho, ulale nabafazi bakho, phambi kwale mini.', 'XHO22'),
(14, 12, 12, 'Kuba wena ukwenze oko emfihlekweni, kodwa mna ndiya kuyenza le nto phambi kwawo onke amaSirayeli naphambi kwamehlo elanga.', 'XHO22'),
(14, 12, 13, 'Wathi uDavide kuNatan, Ndonile kuYehova. Wathi uNatan kuDavide, uYehova usisusile isono sakho; anisayi kufa.', 'XHO22'),
(14, 12, 14, 'Kodwa ngenxa yokuba ngesi senzo uzinike isizathu esikhulu sokunyelisa iintshaba zikaYehova, lo mntwana umzeleyo uya kufa ngokuqinisekileyo.', 'XHO22'),
(14, 12, 15, 'Waya uNatan endlwini yakhe. uYehova wambetha umntwana lowo umkaUriya wamzalela uDavide, wagula kakhulu.', 'XHO22'),
(14, 12, 16, 'uDavide wathandazela umntwana kuThixo; uDavide wazila ukudla, wangena, walala emhlabeni ubusuku bonke.', 'XHO22'),
(14, 12, 17, 'Asuka amadoda amakhulu endlu yakhe, eza kuye, ukuba amfunqule emhlabeni; akavuma, akadla sonka nabo.', 'XHO22'),
(14, 12, 18, 'Kwathi ngomhla wesixhenxe wafa umntwana. Boyika abakhonzi bakaDavide ukumxelela ukuba umntwana ufile, bathi, Yabona, engaphilanga umntwana, sathetha kuye, akawaphulaphula amazwi ethu; Uzoxakwa njani xa simxelela ukuba umntwana ufile?', 'XHO22'),
(14, 12, 19, 'Wabona uDavide ukuba abakhonzi bakhe bayasebezelana, wabona uDavide ukuba ufile umntwana; Wathi uDavide kubakhonzi bakhe, Ufile na umntwana? Bathi ke bona, Ufile.', 'XHO22'),
(14, 12, 20, 'Emva koko uDavide waphakama emhlabeni waza wazihlamba waza wamthambisa waza wambatha ezinye izambatho waza wangena endlwini kaYehova waqubuda; wacela, babeka isonka phambi kwakhe, wadla.', 'XHO22'),
(14, 12, 21, 'Bathi abakhonzi bakhe kuye, Yintoni na le nto uyenzileyo? Oko ebesahleli umntwana, wazila ukudla, walila; Akuba efile umntwana, wavuka wadla isonka.', 'XHO22'),
(14, 12, 22, 'Wathi, Akuba engasahleli umntwana, ndazila ukudla, ndalila, ngokuba bendisithi, Ngubani na owaziyo, ukuba uThixo angandiba nenceba, aphile umntwana?', 'XHO22'),
(14, 12, 23, 'Efile nje kaloku, yini na ukuba ndibe ndisazila ukudla? ndingambuyisela na ke kuye, ndiya kuye, akasayi kubuyela kum.', 'XHO22'),
(14, 12, 24, 'uDavide wamthuthuzela uBhatshebha umkakhe, wamngena, walala naye; wazala unyana, wathi igama lakhe nguSolomon; uYehova wamthanda.', 'XHO22'),
(14, 12, 25, 'Wathumela ke ngesandla somprofeti uNatan; waza wamthiya igama elinguYedidiya ngenxa kaYehova.', 'XHO22'),
(14, 12, 26, 'uYowabhi wayixabela iRabha yoonyana baka-Amoni waza wasithimba isixeko sasebukhosini.', 'XHO22'),
(14, 12, 27, 'uYowabhi wathumela abathunywa kuDavide, esithi, Ndilwile neRabha, ndawuthimbela umzi wamanzi.', 'XHO22'),
(14, 12, 28, 'Bahlanganiseni ke ngoko abantu abaseleyo, nimise iintente phakathi komzi, nibuye, ukuze ndingawuthimbi umzi, ubizwe ngegama lam.', 'XHO22'),
(14, 12, 29, 'Wabahlanganisa ke uDavide bonke abantu, waya eRabha, walwa, wayoyisa.', 'XHO22'),
(14, 12, 30, 'Wathabatha isithsaba sokumkani wabo entlokweni yakhe, ubunzima betalente yegolide, sinamatye anqabileyo, wasibeka entlokweni kaDavide. Waphuma namaxhoba aloo mzi, aba maninzi kunene.', 'XHO22'),
(14, 12, 31, 'Wawathabatha amadoda akhona, wawafaka ngeesarha, nangamazembe esinyithi, namazembe esinyithi, awacandisa ezikweni lamatye; Wenjenjalo kwimizi yonke yoonyana baka-Amon. Emva koko uDavide nabantu bonke babuyela eYerusalem.', 'XHO22'),
(14, 13, 1, 'Emveni koko ke uAbhisalom, unyana kaDavide, ebenodade wabo oyinzwakazi, ogama linguTamare; uAmnon, unyana kaDavide, ubemthanda.', 'XHO22'),
(14, 13, 2, 'Wakhathazeka uAmnon, wagula ngenxa kaTamare udade wabo; ngokuba ebeyintombi; kwaba nzima kuye uAmnon ukuba amenze nto.', 'XHO22'),
(14, 13, 3, 'Ke kaloku uAmnon ebenomhlobo, ogama linguYonadabhi, unyana kaShimeha, umkhuluwa kaDavide; uYonadabhi ebeyindoda elumke kakhulu.', 'XHO22'),
(14, 13, 4, 'Wathi kuye, Yini na, nyana wokumkani, ukuba ubhitywe imihla ngemihla? awundixeleli? Wathi uAmnon kuye, Ndithanda uTamare, udade boAbhisalom, udade wethu.', 'XHO22'),
(14, 13, 5, 'Wathi uYonadabhi kuye, Lala esililini sakho, uyafa; xa uyihlo eze kukubona, uthi kuye, Yithi kuTamare, udade wethu, eze kundinike inyama, alungise inyama emehlweni am, ukuze ndiyibone, ndiyidle esandleni sakhe.', 'XHO22'),
(14, 13, 6, 'Walala ubuthongo uAmnon, wahliwa kukufa; Wafika ukumkani eza kumbona, wathi uAmnon kukumkani, Yithi kuTamare, udade wethu, eze, andenzele isonka phambi kwam, ndisidle esandleni sakhe.', 'XHO22'),
(14, 13, 7, 'Wathumela uDavide kuTamare endlwini, esithi, Yiya kuAmnon udadewenu, umlungiselele ukudla.', 'XHO22'),
(14, 13, 8, 'Waya uTamare endlwini ka-Amnon udade wabo; walala. Wathabatha umgubo, waxovula, wenza amaqebengwana phambi kwakhe, wawosa amaqebengwana.', 'XHO22'),
(14, 13, 9, 'Wathabatha ipani, wayigalela phambi kwakhe; akavuma ukudla. Wathi uAmnon, Mabandishiye bonke bephela. Baphuma ke ngabanye kuye.', 'XHO22'),
(14, 13, 10, 'Wathi uAmnon kuTamare, Yingenise inyama egumbini, ndiyidle esandleni sakho. Wazithabatha uTamare izonkana eziyolisa intliziyo abezenzile, wawasa egumbini lika-Amnon udade wabo.', 'XHO22'),
(14, 13, 11, 'Wazisondeza kuye ukuba adle, wambamba, wathi kuye, Yiza, ulale nam, mzalwana wam.', 'XHO22'),
(14, 13, 12, 'Waphendula ke wathi kuye, Hayi, dade wethu, musa ukundidlwengula; ngokuba ayinakwenziwa into enjalo kwaSirayeli; musani ukwenza obu budenge.', 'XHO22'),
(14, 13, 13, 'Mna ndingaya phi na ihlazo lam? ke wena, uya kuba njengesidenge kwaSirayeli. Thetha uAko kukumkani; ngokuba akayi kundabela nani.', 'XHO22'),
(14, 13, 14, 'Akavuma ukuliphulaphula ilizwi lakhe, kodwa ngenxa yokuba wamomelele kunaye, wamnyanzela, walala naye.', 'XHO22'),
(14, 13, 15, 'Waza uAmnon wamthiya kunene kunene; ngokuba yaba nkulu intiyo awamthiya ngayo, ngaphezu kothando abemthande ngalo. Wathi uAmnon kuye, Vuka uhambe.', 'XHO22'),
(14, 13, 16, 'Wathi kuye, Akukho tyala; Obu bubi bokundigxotha bukhulu kunabo bonke obenzileyo kum. Akavuma ukumphulaphula.', 'XHO22'),
(14, 13, 17, 'Wabiza umfana, umlungiseleli wakhe, wathi, Thabathani, emke kum le nkazana, nilutshixe ucango emva kwayo.', 'XHO22'),
(14, 13, 18, 'Wayambethe ingubo eqaqambileyo, njengengubo enjalo iintombi zokumkani eziseziintombi, eziziintombi. Waza umlungiseleli wakhe waphuma nayo, waluvala ucango emva kwayo.', 'XHO22'),
(14, 13, 19, 'Waza uTamare wazigalela ngothuthu entloko, wayikrazula isambatho sakhe esimibalabala abesikuye, wabeka isandla sakhe phezu kwentloko yakhe, waqhubeka elila.', 'XHO22'),
(14, 13, 20, 'Wathi uAbhisalom udade kuye, Ubenawe na uAmnon udadewenu? Kodwa khawuthule, dade wethu, ndingumnakwenu; musa ukucinga ngale nto. Wahlala uTamare endlwini ka-Abhisalom udade wabo, ekhathazekile.', 'XHO22'),
(14, 13, 21, 'Waziva ukumkani uDavide zonke ezo nto, wavutha ngumsindo kunene.', 'XHO22'),
(14, 13, 22, 'uAbhisalom akazange athethe okulungileyo okanye okubi kuAmnon umntakwabo, ngenxa yokuba uAbhisalom wamthiya uAmnon ngenxa yokuba wayengqongqo kuTamare udade wabo.', 'XHO22'),
(14, 13, 23, 'Kwathi ke, ekupheleni kweminyaka emibini ezeleyo, waba nabachebi kuAbhisalom eBhahali-hatsore kwaEfrayim; uAbhisalom wamema bonke oonyana bokumkani.', 'XHO22'),
(14, 13, 24, 'uAbhisalom weza kukumkani, wathi, Yabona, nabachebi kumkhonzi wakho; Ukumkani nabakhonzi bakhe makahambe nomkhonzi wakho.', 'XHO22'),
(14, 13, 25, 'Wathi ukumkani kuAbhisalom, Hayi, nyana wam, masiye sonke; ngokuba asinatyala kuwe. Wamjoka, akavuma ukuya; endaweni yoko wamsikelela.', 'XHO22'),
(14, 13, 26, 'Wathi uAbhisalom, Ukuba akunjalo, makahambe nathi uAmnon, umkhuluwa wam. Wathi ukumkani kuye, Yini na ukuba ahambe nawe?', 'XHO22'),
(14, 13, 27, 'Kodwa uAbhisalom waqhubeka emcinezela kangangokuba wavuma ukuba uAmnon nabo bonke oonyana bokumkani bahambe naye.', 'XHO22'),
(14, 13, 28, 'uAbhisalom wabayalela abakhonzi bakhe, esithi: “Nikelani ingqalelo xa intliziyo ka-Amnon inemihlali ngewayini, ndize mna ndithi kuni, ‘Bulalani uAmnon! uze ke umbulale, musani ukoyika. Andikuyalelanga? Yiba nesibindi kwaye ube nesibindi.', 'XHO22'),
(14, 13, 29, 'Enza ke amadodana ka-Abhisalom kuAmnon njengoko uAbhisalom abewawisele umthetho. Besuka bonke oonyana bokumkani, bamkhwelisa elowo imeyile yakhe, babaleka.', 'XHO22'),
(14, 13, 30, 'Kwathi, besendleleni, yafika ingxelo kuDavide, isithi, uAbhisalom ubabulele bonke oonyana bokumkani, akwasala namnye.', 'XHO22'),
(14, 13, 31, 'Wesuka wema ukumkani, wazikrazula iingubo zakhe, waqungquluza emhlabeni; bonke abakhonzi bakhe bemi khona apho, benxibe iingubo ezikrazukileyo.', 'XHO22'),
(14, 13, 32, 'Waphendula uYonadabhi, unyana kaShimeha, umkhuluwa kaDavide, wathi, Mayingathi inkosi yam abulewe onke amadodana angoonyana bokumkani; ngenxa yokuba nguAmnon yedwa owafayo, ekubeni yayitywiniwe ngokomyalelo ka-Abhisalom ukususela ngomhla awamnyanzela ngawo uTamare udade wabo.', 'XHO22'),
(14, 13, 33, 'Ke ngoko ukumkani wam makangawubeki ntliziyo lo mbandela, athi bonke oonyana bokumkani mabafe; ngokuba nguAmnon yedwa ofileyo.', 'XHO22'),
(14, 13, 34, 'Ke uAbhisalom wasaba. Unogada waphakamisa amehlo akhe waza wabona abantu abaninzi benyuka ngendlela besuka endulini emva kwakhe.', 'XHO22'),
(14, 13, 35, 'Wathi uYonadabhi kukumkani, Naba oonyana bokumkani besiza; njengoko watshoyo umkhonzi wakho, kunjalo.', 'XHO22'),
(14, 13, 36, 'Kwathi, akugqiba ukuthetha, bafika oonyana bokumkani, baliphakamisa ilizwi labo, balila; balila ngokukrakra ukumkani nabakhonzi bakhe.', 'XHO22'),
(14, 13, 37, 'Kodwa uAbhisalom wasaba waza waya kuTalemayi unyana ka-Amihudi ukumkani waseGeshuri. uDavide wamzilela unyana wakhe imini yonke.', 'XHO22'),
(14, 13, 38, 'Wasaba ke uAbhisalom, waya eGeshuri, wahlala khona iminyaka emithathu.', 'XHO22'),
(14, 13, 39, 'Intliziyo kaKumkani uDavide yamlangazelela uAbhisalom, ngenxa yokuba wayethuthuzelekile nguAmnon, kuba efile.', 'XHO22'),
(14, 14, 1, 'uYowabhi unyana kaTseruya, wabona ukuba intliziyo yokumkani ikuAbhisalom.', 'XHO22'),
(14, 14, 2, 'Wathumela uYowabhi eTekowa, wathabatha khona inkazana elumkileyo, wathi kuyo, Yambatha iingubo ezimnyama, yambathe iingubo ezimnyama, ungazithambisi ngeoli; yiba njengomfazi olilela ofileyo ixesha elide;', 'XHO22'),
(14, 14, 3, 'uye kukumkani, uthethe kuye wenjenje; uYowabhi wawabeka amazwi emlonyeni wakhe.', 'XHO22'),
(14, 14, 4, 'Wathi umkaTekowa kukumkani, wawa ngobuso, waqubuda, wathi, Sindisa, kumkani.', 'XHO22'),
(14, 14, 5, 'Wathi ukumkani kuye, Uyintoni na umsebenzi wakho? Waphendula wathi, Ndingumhlolokazi; indoda yam ifile.', 'XHO22'),
(14, 14, 6, 'Umkhonzazana wakho ebenoonyana ababini, balwa bobabini endle, akwabakho mhlanguli phakathi kwabo; omnye wambetha omnye, wambulala.', 'XHO22'),
(14, 14, 7, 'Yabona, wonke umzalwane wenyuka phezu komkhonzi wakho, wathi, Masizinikele kulowo umbuleleyo umzalwana wakhe, simbulale ngenxa yomphefumlo womzalwana lowo ambuleleyo; kananjalo siya kutshabalalisa indlalifa, kwaye ke baya kulicima ilahle lam eliseleyo, kwaye bangashiyi gama okanye intsalela yomyeni wam ehlabathini.', 'XHO22'),
(14, 14, 8, 'Wathi ukumkani kwinkazana leyo, Hamba uye endlwini yakho, ndokuwisela umthetho.', 'XHO22'),
(14, 14, 9, 'Wathi umkaTekowa kukumkani, Nkosi yam, kumkani, senziwe ububi kum, nakwindlu kabawo; umsulwa yena ukumkani netrone yakhe.', 'XHO22'),
(14, 14, 10, 'Wathi ukumkani, Bonke baya kunibalisa ibali, nimzise kum; akasayi kuphinda akuchukumise.', 'XHO22'),
(14, 14, 11, 'Wathi, uYehova makamkhumbule uYehova uThixo wakho, ukuba ungabi savuma ukuba amadoda anegazi atshabalalise, angamtshabalalisi unyana wam. Wathi, Ehleli nje uYehova, akuyi kuwa nalunye unwele lonyana wakho emhlabeni.', 'XHO22'),
(14, 14, 12, 'Yathi inkazana, Makhe athethe umkhonzazana wakho enkosini yam ukumkani. Wathi yena, Hambani niye!', 'XHO22'),
(14, 14, 13, 'Yathi inkazana leyo, Yini na ukuba ucinge into enje ngabantu bakaThixo? ngenxa yokuba ukumkani ethetha ngale ndawo nangetyala, ukumkani angambuyiseli endlwini yakhe ogxothiweyo.', 'XHO22'),
(14, 14, 14, 'Ngokuba siya kufa, sibe njengamanzi aphaleleyo emhlabeni, angenakuqokelelwa kwakhona; kananjalo uThixo akahloniphi mntu; Noko ke, uceba icebo ukuze abathinjwa bakhe bangagxothwa bemke kuye.', 'XHO22'),
(14, 14, 15, 'Ke kaloku, le nto ndize kuyithetha le nto enkosini yam ukumkani, kungenxa yokuba abantu bebendisongela; wathi umkhonzi wakho, Makhe ndithethe kukumkani; kunokwenzeka ukuba ukumkani azalisekise isicelo sekhoboka lakhe.', 'XHO22'),
(14, 14, 16, 'Kuba ukumkani uya kuva ukuze asindise ikhoboka lakhe esandleni somntu ofuna ukunditshabalalisa kunye nonyana wam elifeni likaThixo.', 'XHO22'),
(14, 14, 17, 'Wathi umkhonzi wakho, Ilizwi lokumkani, nkosi yam, malibe lelokuthuthuzela; ngokuba inkosi yam ukumkani, inkosi yam, injengesithunywa sikaThixo, esahlula phakathi kokulungileyo nokubi; ngako oko uYehova uThixo wakho uya kuba nawe.', 'XHO22'),
(14, 14, 18, 'Waphendula ukumkani, wathi kwinkazana, Musa ukukha uyikhanyele kum into endikubuzayo. Yathi inkazana, Camagu, nkosi yam, makathethe.', 'XHO22'),
(14, 14, 19, 'Wathi ukumkani, Ndathi, Isandla sikaYowabhi sinawe na kuyo yonke loo nto? Yaphendula inkazana leyo, yathi, Ngemihla yakho yonke, nkosi yam, nkosi yam, akuyi kutyeka, aye ekunene nasekhohlo, kuko konke akuthethileyo inkosi yam ukumkani; ngokuba undiwisele umthetho umkhonzi wakho uYowabhi, kuwo onke la mazwi emlonyeni womkhonzi wakho.', 'XHO22'),
(14, 14, 20, 'Wenjenjalo uYowabhi umkhonzi wakho, ukuba athethe ilizwi; Ke inkosi yam ilumke ngokulumka kwesithunywa sikaThixo, ukuba yazi konke okusehlabathini;', 'XHO22'),
(14, 14, 21, 'Wathi uYehova kuYowabhi, Yabona, ndiyenzile ke loo nto; hamba uye, umbuyise umfana lowo uAbhisalom.', 'XHO22'),
(14, 14, 22, 'Wawa uYowabhi ngobuso bakhe, waqubuda, wabulela kukumkani; Wathi uYowabhi, Uyazi kakuhle namhla, mkhonzi wakho, ukuba ndikwenzele inceba, enkosini yam ukumkani, ngesicelo sokumkani, somkhonzi wakhe.', 'XHO22'),
(14, 14, 23, 'Wemka uYowabhi, waya eGeshuri, wamzisa uAbhisalom eYerusalem.', 'XHO22'),
(14, 14, 24, 'Wathi ukumkani, Makahambe aye endlwini yakhe, angabuboni ubuso bam. uAbhisalom wabuyela endlwini yakhe; akabubona ubuso bokumkani.', 'XHO22'),
(14, 14, 25, 'Ke kumaSirayeli onke bekungekho ndoda injengoAbhisalom ukuba yinzwana, ngokokuze adunyiswe kunene; kuthabathele entendeni yeenyawo kwesa entloko, akusweli nto.', 'XHO22'),
(14, 14, 26, 'Kwathiwa akayinqumli intloko, ngokuba wayeyinqunqa ekupheleni konyaka; ngokuba iinwele bezinzima kuye, wabulinganisa, wabulinganisa ubunzima beenwele zentloko yakhe, zaba ziishekele ezimakhulu mabini, ngokobunzima bokumkani.', 'XHO22'),
(14, 14, 27, 'uAbhisalom wazala oonyana abathathu, nentombi yanye, igama layo belinguTamare; ibingumfazi omile kakuhle.', 'XHO22'),
(14, 14, 28, 'uAbhisalom wahlala eYerusalem iminyaka emibini, engabuboni ubuso bokumkani.', 'XHO22'),
(14, 14, 29, 'uAbhisalom wathumela kuYowabhi, ukuba amthumele kukumkani; akavuma ukuza kuye; Waphinda wathumela okwesibini akavuma ukuza.', 'XHO22'),
(14, 14, 30, 'Wathi kubakhonzi bakhe, Khangelani, umhlaba kaYowabhi, ungakwem, unerhasi khona; uya kukutshisa wena. Bayitshisa abakhonzi baka-Abhisalom intsimi.', 'XHO22'),
(14, 14, 31, 'Akugqiba uYowabhi, weza kuAbhisalom endlwini yakhe, wathi kuye, Yini na ukuba abakhonzi bakho bayitshise intsimi yam?', 'XHO22'),
(14, 14, 32, 'uAbhisalom wamphendula uYowabhi, wathi, Yabona, ndithumele kuwe, ndisithi, Yiza apha, ndikuthumele kukumkani, uthi, Ndimkile na eGeshuri? kwakumnandi xa ndikhona; Makhe ndibubone ubuso bokumkani; ukuba buthe bakho kum ubutshinga, makandibulale.', 'XHO22'),
(14, 14, 33, 'Waya ke uYowabhi kukumkani, wamxelela; Wambiza uAbhisalom, weza kuYehova, waqubuda kuye; ukumkani wamanga uAbhisalom.', 'XHO22'),
(14, 15, 1, 'Emveni koko uAbhisalom wamlungisela iinqwelo zokulwa, namahashe, namadoda angamashumi amahlanu abaleka ngakuye.', 'XHO22'),
(14, 15, 2, 'uAbhisalom wavuka kusasa, wema endleleni ngasesangweni; Kwathi, kwakufika kukumkani ukuba agwebe, uAbhisalom wambiza, wathi, Ungowawuphi na umzi? Wathi, Umkhonzi wakho lo ungowezizwe zakwaSirayeli.', 'XHO22'),
(14, 15, 3, 'Wathi uAbhisalom kuye, Yabona, iindawo zakho zilungile, zinobulungisa; Ke ukumkani akanikwanga ukuba aphulaphule.', 'XHO22'),
(14, 15, 4, 'Wathi uAbhisalom, Ukuba bendithe ndaba ngumgwebi kweli lizwe, beza kum bonke abantu abanendawo kubo, nokuba kubanjwene, bendiya kumgweba.', 'XHO22'),
(14, 15, 5, 'Kwakusithiwa xa kukho umntu osondela kuye ukuze amhlonele, wayesolula isandla sakhe ambambe aze amange.', 'XHO22'),
(14, 15, 6, 'Wenza ke uAbhisalom kumaSirayeli onke, abesiza kukumkani ukuba agwebe;', 'XHO22'),
(14, 15, 7, 'Kwathi, kwakuzaliseka iminyaka emashumi mane, wathi uAbhisalom kukumkani, Ndivumele ndiye, ndisizalise isibhambathiso sam endasenzayo kuYehova eHebron;', 'XHO22'),
(14, 15, 8, 'Kuba wenza isifungo somkhonzi wakho ngoxa ndandisahlala eGeshuri ezweni lakwa-Aram, ndathi, ‘Ukuba uYehova angandibuyisela ngokulungileyo eYerusalem, ndiya kukhonza uYehova.', 'XHO22'),
(14, 15, 9, 'Wathi ukumkani kuye, Hamba unoxolo. Wesuka ke waya eHebron.', 'XHO22'),
(14, 15, 10, 'uAbhisalom wathuma iintlola ezizweni zonke zakwaSirayeli, esithi, Xa nithe naliva izwi lesigodlo, nithi, uAbhisalom ngukumkani waseHebron.', 'XHO22'),
(14, 15, 11, 'uAbhisalom wahamba eYerusalem enamakhulu amabini amadoda abemenyiwe; bahamba ngobululamo babo, bengazi nto.', 'XHO22'),
(14, 15, 12, 'uAbhisalom wathumela, ukuba kubizwe uAhitofele waseGiloni, umphakathi kaDavide, emzini wakhe eGilo, ebingelela imibingelelo. Laza elo cebo laba namandla; ngokuba abantu bebesanda kuAbhisalom.', 'XHO22'),
(14, 15, 13, 'Wafika umthunywa lowo kuDavide, wathi, Iintliziyo zamadoda akwaSirayeli zilandele uAbhisalom;', 'XHO22'),
(14, 15, 14, 'Wathi uDavide kubakhonzi bakhe bonke ababenaye eYerusalem, Sukani, sisabe; ngokuba asinako ukumbaleka ngenye indlela uAbhisalom; khawulezani, nimke, hleze asifumane kwaoko, asihlisele ububi, awuxabele umzi ngohlangothi lwentshuntshe.', 'XHO22'),
(14, 15, 15, 'Bathi abakhonzi bokumkani kukumkani, Yabona, yonke into, aya kuyimisa phambi kokumkani, inkosi yethu, naba bekho abakhonzi bakho.', 'XHO22'),
(14, 15, 16, 'Waphuma ukumkani, yamlandela yonke indlu yakhe. Ukumkani wabashiya abalishumi elinezihibe, ukuba balinde indlu.', 'XHO22'),
(14, 15, 17, 'Waphuma ke ukumkani, bamlandela bonke abantu, baya kuhlala mgama.', 'XHO22'),
(14, 15, 18, 'Bahamba ecaleni kwakhe bonke abakhonzi bakhe; nawo onke amaKrete onke amaPeleti onke amaGati, amadoda angamakhulu amathandathu anesithandathu awayemlandela ukusuka eGati, awela phambi kokumkani.', 'XHO22'),
(14, 15, 19, 'Wathi ukumkani kuItayi Mogati, Yini na ukuba uhambe nathi? buyela endlwini yakho, uhlale nokumkani;', 'XHO22'),
(14, 15, 20, 'Ufike nje izolo, ndikuvumele ukuba uhambe nathi namhlanje? Apho ndiya khona mna, buyani nani nithabathe abazalwana benu; Ubabalo nenyaniso zibe nawe.', 'XHO22'),
(14, 15, 21, 'uItayi wamphendula ukumkani, wathi, NgoYehova ohleliyo, nangokumkani, ukumkani, inkosi yam, ohleliyo, nokuba ezindaweni zonke, ukumkani wam ukumkani uya kuba khona, ekufeni, nasebomini. Ndikhoyo, umkhonzi wakho uya kuba lapho.', 'XHO22'),
(14, 15, 22, 'Wathi uDavide kuItayi, Hamba ugqithe. uItai waMogathe wadlula namadoda akhe onke nabantwana ababenaye.', 'XHO22'),
(14, 15, 23, 'Lonke ilizwe laye lilila ngezwi elikhulu; nokumkani wawuwela umlambo waseKidron, yaye bonke abantu bawela ngendlela eya entlango.', 'XHO22'),
(14, 15, 24, 'uTsadoki nabo bonke abaLevi babekunye naye bethwele ityeya yomnqophiso kaTHIXO oyinyaniso; bayibeka phantsi ityeya kaThixo; Wenyuka uAbhiyatare bada baphuma bonke abantu esixekweni.', 'XHO22'),
(14, 15, 25, 'Wathi ukumkani kuTsadoki, Yibuyisele kuwo umzi ityeya kaThixo; ukuba ndithe ndababalwa nguYehova, wondibuyisa, andibonise yona nendawo yakhe yokuhlala.', 'XHO22'),
(14, 15, 26, 'Ukuba uthe, Andikuvuyeleli; Yabona, ndim lo, undenze njengoko kulungileyo;', 'XHO22'),
(14, 15, 27, 'Wathi ukumkani kuTsadoki umbingeleli, Akuvumi na? buyela kuwo umzi unoxolo, noAhimahatse unyana wakho, noYonatan unyana ka-Abhiyatare, oonyana benu bobabini kunye nawe.', 'XHO22'),
(14, 15, 28, 'Yabona, ndiya kuma ethafeni eliyinkqantosi;', 'XHO22'),
(14, 15, 29, 'Ngoko uTsadoki noAbhiyatare bayibuyisela ityeya yoTHIXO oyinyaniso eYerusalem baza bahlala khona.', 'XHO22'),
(14, 15, 30, 'uDavide wenyuka kwithambeka leNtaba yemiNquma, wenyuka ekhala, egqubuthele intloko, ehamba engenazimbadada; benyuka.', 'XHO22'),
(14, 15, 31, 'Kwaxelwa kuDavide, kwathiwa, uAhitofele ungomnye wabacebi baka-Abhisalom. Wathi uDavide, Yehova, liguqule libe bubudenge icebo lika-Ahitofele.', 'XHO22'),
(14, 15, 32, 'Xa uDavide wafikayo encotsheni yentaba, apho wayethandaza kuThixo, uHushayi umArabhi wamhlangabeza, enxibe isambatho sangaphezulu eside nothuli entloko.', 'XHO22'),
(14, 15, 33, 'Wathi uDavide kuye, Ukuba uthe wahamba nam, woba ngumthwalo kum;', 'XHO22'),
(14, 15, 34, 'ke ukuba uthe wabuyela kuwo umzi, wathi kuAbhisalom, Ndoba ngumkhonzi wakho, kumkani; njengoko bendingumkhonzi kayihlo unangoku, ndoba ngumkhonzi wakho ngokunjalo; wolitshitshisa icebo lika-Ahitofele ngenxa yam.', 'XHO22'),
(14, 15, 35, 'Abanawe na ababingeleli uTsadoki noAbhiyatare? Ngoko ke baxelele ababingeleli uTsadoki noAbhiyatare yonke into oyivayo endlwini yokumkani.', 'XHO22'),
(14, 15, 36, 'Yabona, bakho khona oonyana babo bobabini, uAhimahatse unyana kaTsadoki, noAbhiyatare unyana kaYonatan; nithumele kum yonke into eniyivayo, kunye nabo.', 'XHO22'),
(14, 15, 37, 'Wafika uHushayi, umhlobo kaDavide, esixekweni, noAbhisalom weza eYerusalem.', 'XHO22'),
(14, 16, 1, 'Kwathi, ekuweleni kukaDavide entabeni, wamkhawulela uTsibha, umkhonzi kaMefibhoshete, emkhawulela, eneemazi zamaesile ezisasazekileyo, ezimakhulu mabini, ethwele phezu kwawo izonka ezimakhulu mabini, nekhulu leediliya ezomisiweyo, nekhulu leezidiliya. Yeziqhamo zasehlotyeni, nendebe yewayini.', 'XHO22'),
(14, 16, 2, 'Wathi uYehova kuTsibha, Uthini na ngaloo nto? Wathi uTsibha, Iimazi zamaesile zikulungele ukukhwela endlwini yokumkani; nesonka neziqhamo zasehlotyeni zamadodana; newayini, ukuze basele abatyhafileyo entlango.', 'XHO22'),
(14, 16, 3, 'Wathi ukumkani, Uphi na unyana wenkosi yakho? Wathi uTsibha kukumkani, Yabona, uhlala eYerusalem, ngokuba usithi, Namhla indlu kaSirayeli iya kububuyisela kum ubukumkani bukabawo.', 'XHO22'),
(14, 16, 4, 'Wathi ukumkani kuTsibha, Uyabona, zonke izinto ezikuMefibhoshete zezakho. Wathi uTsiba, Ndiya kuthandaza ngokuzithoba, ukuba ndibabalwe nguwe, nkosi yam, nkosi yam!', 'XHO22'),
(14, 16, 5, 'Wafika ukumkani uDavide eBhahurim, wabona kukho indoda yendlu kaSawule, egama linguShimehi, unyana kaGera;', 'XHO22'),
(14, 16, 6, 'Wamxuluba ngamatye uDavide nabakhonzi bonke bokumkani uDavide; bonke abantu namagorha onke babengasekunene kwakhe nangasekhohlo kwakhe.', 'XHO22'),
(14, 16, 7, 'Ngoko uShimeyi waqhubeka eqalekisa, esithi: “Hamb’ umke, ndoda yamagazi nenyanisekileyo!', 'XHO22'),
(14, 16, 8, 'uYehova wawabuyisela phezu kwakho lonke igazi lendlu kaSawule, owalawula esikhundleni sakhe phezu kwakho; uYehova wabunikela ubukumkani esandleni sika-Abhisalom unyana wakho;', 'XHO22'),
(14, 16, 9, 'Wathi ke uAbhishayi unyana kaTseruya kukumkani, Yini na ukuba le nja ifileyo itshabhise ukumkani wam? mandihambe ndimnqumle intloko.', 'XHO22'),
(14, 16, 10, 'Wathi uYehova, Yintoni na ingxaki yam kuni, nyana bakaZeruya? makatshabhise, ngokuba uYehova wathi kuye, Mtshabhise uDavide! Ngoko ke ngubani oya kuthi: Kutheni le nto uyenzileyo?', 'XHO22'),
(14, 16, 11, 'Wathi uDavide kuAbhishayi nakubakhonzi bakhe bonke, Niyabona, unyana wam owaphuma esizalweni sam ungxamele umphefumlo wam; Unokwenza malini ngoku uBenjamin? makame, aqalekise; ngokuba uYehova ebemwisele umthetho.', 'XHO22'),
(14, 16, 12, 'Mhlawumbi uYehova angabukhangela umvandedwa wam, yaye namhla uYehova wondibuyekeza ngesiqalekiso sakhe.', 'XHO22'),
(14, 16, 13, 'Ekuhambeni kwexesha uDavide namadoda akhe babehamba ngendlela, uShimehi waqhubeka ehamba phambi kwentaba, esiya ngakuye, yaye njengoko wayehamba wamgibisela ngamatye, emphosa nothuli.', 'XHO22'),
(14, 16, 14, 'Ukumkani nabantu bonke ababenaye badinwa, baphumla apho.', 'XHO22'),
(14, 16, 15, 'uAbhisalom nabo bonke abantu, amadoda akwaSirayeli, bafika eYerusalem, enoAhitofele.', 'XHO22'),
(14, 16, 16, 'Weza uHushayi umAram, umhlobo kaDavide, kuAbhisalom, wathi uHushayi kuAbhisalom, Makasindise ukumkani!', 'XHO22'),
(14, 16, 17, 'Wathi uAbhisalom kuHushayi, Kukumthanda kwakho na oku? kutheni ungahambi netshomi yakho?', 'XHO22'),
(14, 16, 18, 'Wathi uHushayi kuAbhisalom, Hayi! kodwa lowo amnyulileyo uYehova, nolu hlanga, namadoda onke akwaSirayeli, ndoba ngowakhe, ndihlale naye.', 'XHO22'),
(14, 16, 19, 'Kananjalo ndiya kukhonza bani na? Akayi kumkhonza na unyana wakhe? njengoko ndamkhonzayo uyihlo, ndiya kuba nawe ngokunjalo.', 'XHO22'),
(14, 16, 20, 'Wathi uAbhisalom kuAhitofele, Thetha into esiya kuyenza.', 'XHO22'),
(14, 16, 21, 'Wathi uAhitofele kuAbhisalom, Ngena ezithendeni zikayihlo, abezishiyile ukuba zigcine indlu; eve onke amaSirayeli ukuba umthiyile uyihlo; zandule ke zomelele izandla zabo bonke abanawe.', 'XHO22'),
(14, 16, 22, 'Bayimisa intente ka-Abhisalom phezu kwendlu; uAbhisalom wamlandela uyise phambi kwamaSirayeli onke.', 'XHO22'),
(14, 16, 23, 'Icebiso lika-Ahitofele, awayelicebisa ngaloo mihla lalifana nomntu obuza ilizwi likaThixo; Icebiso lika-Ahitofele kuDavide noAbhisalom lalinjalo.', 'XHO22'),
(14, 17, 1, 'Wongeza uAhitofele, wathi kuAbhisalom, Makhe ndinyule ishumi elinesibini lamawaka amadoda, ndime, ndibaleke kuDavide ngobu busuku;', 'XHO22'),
(14, 17, 2, 'Ndiya kumhlasela atyhafile, nezandla zakhe zityhafile, ndimoyikise; basabe bonke abantu anabo; yaye ndiya kumoyisa ukumkani kuphela;', 'XHO22'),
(14, 17, 3, 'ndibabuyisele kuwe bonke abantu; umntu omfunayo usengathi babuyile bonke; ngoko bonke abantu baya kuba noxolo.', 'XHO22'),
(14, 17, 4, 'Yalunga loo nto emehlweni ka-Abhisalom nasemehlweni amadoda amakhulu akwaSirayeli.', 'XHO22'),
(14, 17, 5, 'Wathi uAbhisalom, Bizani uHushayi umAram, sive ukuba uya kuthini na ukuthetha kwakhe.', 'XHO22'),
(14, 17, 6, 'Weza uHushayi kuAbhisalom, wathetha uAbhisalom kuye, wathi, Utsho uAhitofele ukuthi; Senze ngokwelizwi lakhe na? Ukuba akukho njalo; uthi.', 'XHO22'),
(14, 17, 7, 'Wathi uHushayi kuAbhisalom, Icebo uAhitofele acebisa lona alilungile okwangoku.', 'XHO22'),
(14, 17, 8, 'Wathi uHusayi, Uyamazi uyihlo namadoda akhe, ukuba bakhohlakele, batyumkile intliziyo, njengebhere ewahlutha amathole ayo endle; kwaye uyihlo yindoda yemfazwe, akafuni kulala nabantu.', 'XHO22'),
(14, 17, 9, 'Yabona, uzimele komnye umngxuma, nokuba kukwenye indawo; kothi ke, xa bathe bafika kuqala, bonke abavileyo bathi, Kukho ukubulawa kwabantu abamlandelayo uAbhisalom.', 'XHO22'),
(14, 17, 10, 'Kwaye kwanendoda eligorha entliziyo injengentliziyo yengonyama, iya kutshata ngokupheleleyo; ngenxa yokuba wonke uSirayeli uyazi ukuba uyihlo ligorha, yaye abo bakunye naye ngamadoda anamandla.', 'XHO22'),
(14, 17, 11, 'Ngoko ke ndicebisa ukuba onke amaSirayeli ahlanganisene kuwe, ethabathela kwaDan esa eBher-shebha, abe yintabalala yentlabathi yam nonxweme lolwandle; nokuba uhambe wedwa edabini.', 'XHO22'),
(14, 17, 12, 'Somkhawulela ke kuloo ndawo aya kufunyanwa kuyo, siwe phezu kwakhe, njengokuwa kombethe emhlabeni; akusayi kusala yena nakumadoda onke anaye.', 'XHO22'),
(14, 17, 13, 'Ngaphezu koko, ukuba ithe yahlala esixekweni, onke amaSirayeli ozisa iintsontelo kweso sixeko, size sibarhuqele emlanjeni de kungafumaneki nalinye ilitye elincinane.', 'XHO22'),
(14, 17, 14, 'Wathi ke uAbhisalom namadoda onke akwaSirayeli, Icebo likaHushayi umAram lilungile kunecebo lika-Ahitofele. Kuba uYehova wayezimisele ukutshabalalisa icebo elilungileyo lika-Ahitofele, ngenjongo yokuba uYehova amzisele intlekele uAbhisalom.', 'XHO22'),
(14, 17, 15, 'Wathi uHushayi kuTsadoki nakuAbhiyatare ababingeleli, uAhitofele umcebisa ngale ndlela uAbhisalom namadoda amakhulu akwaSirayeli; kwaye ndinike iingcebiso.', 'XHO22'),
(14, 17, 16, 'Thumelani ke kamsinya, nixelele uDavide, nithi, Musa ukulalisa enkqantosini; hleze adle ukumkani nabantu bonke abanaye.', 'XHO22'),
(14, 17, 17, 'uYonatan noAkimatse bahlala e-Enerogel; kuba azabonakala ekungeneni kwawo esixekweni baya baxelela ukumkani uDavide.', 'XHO22'),
(14, 17, 18, 'Umfana wababona, waxelela uAbhisalom; Bakhawuleza bobabini, bafika eBhahurim endlwini yendoda enequla eyadini yayo; apho bawela khona.', 'XHO22'),
(14, 17, 19, 'Yasithabatha inkazana isigqubuthelo, yasitwabulula phezu komnyango wequla, yasitwabulula ingqolowa egcadiweyo; ityala lalingaziwa.', 'XHO22'),
(14, 17, 20, 'Bafika abakhonzi baka-Abhisalom kuloo ntokazi endlwini, bathi, Baphi na ooAhimaheyu noYonatan? Yathi intokazi kubo, Welani umlambo; Bafuna, abazifumana, babuyela eYerusalem.', 'XHO22'),
(14, 17, 21, 'Banduluka, benyuka baya equleni, bamxelela ukumkani uDavide, bathi kuDavide, Khawuyeke, ukha amanzi kamsinya; ngokuba uAhitofele ucebe icebo ngani.', 'XHO22'),
(14, 17, 22, 'Emveni koko bagqiba ke ooDavide namadoda onke abenaye, bayiwela iYordan;', 'XHO22'),
(14, 17, 23, 'Uthe uAhitofele akubona ukuba icebo lakhe alilandelwa, wabopha idonki yakhe, wesuka waya endlwini yakhe, esixekweni sakowabo, wayilungisa indlu yakhe, wazixhoma, wafa, wangcwatyelwa engcwabeni likayise.', 'XHO22'),
(14, 17, 24, 'uDavide wafika eMahanayim. uAbhisalom wayiwela iYordan namadoda onke akwaSirayeli.', 'XHO22'),
(14, 17, 25, 'uAbhisalom wammisa uAmasa ukuba abe yingqwayi-ngqwayi esikhundleni sikaYowabhi; uAmasa ebengunyana wendoda egama linguYitra, engumSirayeli, eyangena noAbhigali, intombi kaNahashe, udade boTseruya, unina kaYowabhi.', 'XHO22'),
(14, 17, 26, 'Oonyana bakaSirayeli noAbhisalom bamisa iintente ezweni laseGiliyadi.', 'XHO22'),
(14, 17, 27, 'Wafika uDavide eMahanayim, enoSobhi unyana kaNahashe, waseRabha yoonyana baka-Amon, noMagire unyana ka-Amiyeli, eLodebhare, noBharzilayi waseGiliyadi, waseRogelim;', 'XHO22'),
(14, 17, 28, 'Yiza nezingqengqelo, neengqayi, neengqayi, nerhasi, nomgubo, nengqolowa egcadiweyo, neembotyi, neelentile, nengqolowa egcadiweyo;', 'XHO22'),
(14, 17, 29, 'nobusi, nebhotolo, nempahla emfutshane, namasi eenkomo, kuDavide nakubantu ababenaye ukuba badle; ngokuba bathi, Abantu balambile, badiniwe, banxaniwe entlango.', 'XHO22'),
(14, 18, 1, 'uDavide wawabala amadoda abenaye, wabamisa aba ngabathetheli-waka nabathetheli-makhulu.', 'XHO22'),
(14, 18, 2, 'uDavide wasithumela isahlulo sesithathu sabantu esandleni sikaYowabhi, nesahlulo sesithathu esandleni sika-Abhishayi unyana kaTseruya, umzalwana kaYowabhi, nesahlulo sesithathu esandleni sikaItayi waseGati. Wathi ukumkani ebantwini, Ndophuma nani.', 'XHO22'),
(14, 18, 3, 'Baphendula abantu bathi, Aninako ukumka; ngokuba, xa sithe sasaba, abayi kusigcina; xa sithe safa isiqingatha sethu, ziya kusigcina; kodwa ngoku, nilingana neshumi lamawaka kuthi; ngoko ke, kungcono ukuba usincede siwushiye umzi.', 'XHO22'),
(14, 18, 4, 'Wathi ukumkani kubo, Ndiya kwenza ngokuthanda kwenu. Ukumkani wema esangweni, yaye bonke abantu baphuma ngamakhulu namawaka.', 'XHO22'),
(14, 18, 5, 'uYehova wabawisela umthetho uYowabhi, noAbhishayi, noItayi, wathi, Yibani nenceba kumfana lowo, uAbhisalom, ngenxa yam. Beva ke bonke abantu ukuba ukumkani ubawisele umthetho abathetheli bonke ngoAbhisalom.', 'XHO22'),
(14, 18, 6, 'Aphuma amadoda aya elizweni, aya kukhawulela oonyana bakaSirayeli; kwaliwa ehlathini lakwaEfrayim;', 'XHO22'),
(14, 18, 7, 'Apho kwabulawa abantu bakwaSirayeli phambi kwabakhonzi bakaDavide, kwaza kwabulawa abantu abaninzi ngaloo mini amadoda angamashumi amabini amawaka.', 'XHO22'),
(14, 18, 8, 'Kuba imfazwe yasasazeka kulo lonke ilizwe, nehlathi latshabalalisa abantu abaninzi kunabo batshatyalaliswe ngekrele ngaloo mini.', 'XHO22'),
(14, 18, 9, 'uAbhisalom waqubisana nabakhonzi bakaDavide. uAbhisalom wayekhwele imeyile, yaye imeyile yangena phantsi kwamasebe ashinyeneyo omthi omkhulu wom-oki; wahamba imeyile ebiphantsi kwakhe.', 'XHO22'),
(14, 18, 10, 'Indoda ethile yakubona oko, yamxelela uYowabhi, yathi, Khangela, ndimbonile uAbhisalom ejinga emterebhintini.', 'XHO22'),
(14, 18, 11, 'Wathi uYowabhi kuloo ndoda imxeleleyo, Khangela, umbonile nje, ibiyini na ukuba ungambethi khona? Bendiya kukunika iishekele ezilishumi zesilivere nebhanti.', 'XHO22'),
(14, 18, 12, 'Yathi indoda leyo kuYowabhi, Ndingafanelana ndithabathe iwaka leshekele esandleni sam, andiyi kusithintela isandla sam kunyana wokumkani; ngokuba ukumkani ukuwisele umthetho, wena noAbhishayi noItayi, sithe sakuva, wathi, Dlulani! akukho namnye umchukumisayo umfana uAbhisalom.', 'XHO22'),
(14, 18, 13, 'Okanye, bendiya kuxoka emphefumlweni wam, ngokuba akukho nto ifihlakeleyo kukumkani; ubuya kuwaphakamisa amandla am.', 'XHO22'),
(14, 18, 14, 'Wathi uYowabhi, Andiyi kuhlala kuwe ngolu hlobo. Wathabatha iintolo ezintathu esandleni sakhe waza wamhlaba uAbhisalom entliziyweni ngoxa wayesaphila emthini weturpentine.', 'XHO22'),
(14, 18, 15, 'Bamrhawula abafana abalishumi abanxibe izixhobo zikaYowabhi, baxabela uAbhisalom, bambulala.', 'XHO22'),
(14, 18, 16, 'Wavuthela uYowabhi isigodlo, abuya amadoda lawo, alandela oonyana bakaSirayeli; ngokuba uYowabhi wabanqanda abantu.', 'XHO22'),
(14, 18, 17, 'Bamthabatha uAbhisalom, bamphosa emngxunyeni omkhulu wehlathi, babeka phezu kwakhe imfumba enkulu kakhulu yamatye; asaba onke amaSirayeli, aya elowo ententeni yakhe.', 'XHO22'),
(14, 18, 18, 'Ke kaloku uAbhisalom wayesadla ubomi, wathabatha isimiso, wasimisa entilini yokumkani; ngokuba wathi, Andinanyana uya kulikhumbula igama lam; walibiza ilitye lesikhumbuzo ngegama lakhe; khuleni nanamhla, endlwini ka-Abhisalom!', 'XHO22'),
(14, 18, 19, 'Wathi uAkimaya, unyana kaTsadoki, Mandigidime, ndimxelele ukumkani, ukuba uYehova umgwebele, umgwebele ezintshabeni zakhe.', 'XHO22'),
(14, 18, 20, 'Wathi uYowabhi kuye, Akuyi kuzisa nto namhla, wozisa lizwi ngenye imini; ke namhla aniyi kuzizisa iindaba ezilungileyo, ngokuba unyana wokumkani ufile.', 'XHO22'),
(14, 18, 21, 'Wathi uYowabhi kuKushi, Hamba uye kumxelela ukumkani okubonileyo. uKushi waguqa phambi koYowabhi, wagidima.', 'XHO22'),
(14, 18, 22, 'Wathi uAhimaya, unyana kaTsadoki, kuYowabhi, Mandibaleke nam, ndibaleke ndiye kuKushi. Wathi uYowabhi, Uyabaleka, nyana wam, ubungekazithethi ezi ndaba?', 'XHO22'),
(14, 18, 23, 'Kodwa wathi, Mandibaleke ndibaleke. Wathi kuye, Gijima uAhimahatse wabaleka wehla ngendlela esuka ethafeni waza wambamba uKusi.', 'XHO22'),
(14, 18, 24, 'uDavide wahlala phantsi phakathi kwamasango omabini, umlindi wenyuka waya eluphahleni lwesango eludongeni, waphakamisa amehlo akhe, nanko indoda ibaleka iyodwa.', 'XHO22'),
(14, 18, 25, 'Bamemeza ke abalindi, baxelela ukumkani. Wathi uYehova, Xa iyodwa, inelizwi emlonyeni wayo. Weza ngokukhawuleza, wasondela.', 'XHO22'),
(14, 18, 26, 'Umlindi wabona enye indoda ibaleka; umlindi wabiza umlindi, wathi, Nantsiya indoda ibaleka iyodwa. Wathi ukumkani, Naleyo izisa iindaba.', 'XHO22'),
(14, 18, 27, 'Wathi umlindi, Ndiyabona ukubaleka kwendoda enkulu kufana nokubaleka kuka-Akimeyu, unyana kaTsadoki. Wathi ukumkani, Uyindoda elungileyo, uza neendaba ezilungileyo.', 'XHO22'),
(14, 18, 28, 'Wamemeza uAkimaya, wathi kukumkani, Konke kulungile; Wawa ngobuso phambi kokumkani, wathi, Makabongwe uYehova uThixo wakho, owabasindisayo abo baziphakamiselayo izandla zabo kukumkani, nkosi yam.', 'XHO22'),
(14, 18, 29, 'Wathi uYehova, Usaphilile na umfana, uAbhisalom? Wathi uAhimaha, ekumthumeni kukaYowabhi umkhonzi wokumkani, mna ndingumkhonzi wakho, ndabona ingxokolo enkulu, ndingazi ukuba yintoni na.', 'XHO22'),
(14, 18, 30, 'Wathi ukumkani kuye, Hamba, ume apha. Wajika, wema.', 'XHO22'),
(14, 18, 31, 'Nanko kufika uKusi; Wathi uKusi, Yitsho kukumkani, nkosi yam, ngokuba uYehova uthethe namhla kubo bonke abo bakuphakamisela isandla sakho.', 'XHO22'),
(14, 18, 32, 'Wathi uYehova kuKushi, Usaphilile na umfana, uAbhisalom? Waphendula uKusi, wathi, Iintshaba zokumkani, nkosi yam, nabo bonke abasukele phezulu kuwe ukukwenza into embi, mababe njengaloo ndodana.', 'XHO22'),
(14, 18, 33, 'Waba buhlungu kakhulu ukumkani wenyuka waya egumbini eliphambi kwesango, walila; wahamba, wathi, Nyana wam, Abhisalom, nyana wam, nyana wam, Abhisalom! Akwaba bendinokufela wena, Abhisalom, nyana wam, nyana wam!', 'XHO22'),
(14, 19, 1, 'Kwaxelwa kuYowabhi, kwathiwa, Nanko ukumkani elila, elilela uAbhisalom.', 'XHO22'),
(14, 19, 2, 'Usindiso lwaloo mini lwaba lusizi ebantwini bonke, ngokuba abantu beva ngaloo mini ukuba ukumkani wenze ububi ngonyana wakhe.', 'XHO22'),
(14, 19, 3, 'Ngaloo mini abantu banyebelezela phakathi komzi, njengabasaba emfazweni, njengehlazo.', 'XHO22'),
(14, 19, 4, 'Ukumkani wazigqubuthela ubuso, wadanduluka ukumkani ngezwi elikhulu, wathi, Nyana wam, Abhisalom, Abhisalom, nyana wam, nyana wam!', 'XHO22'),
(14, 19, 5, 'Wangena uYowabhi endlwini yokumkani, wathi, Ubenze inqambi namhla ubuso babakhonzi bakho bonke abakusindisileyo namhla, nomphefumlo woonyana bakho, neentombi zakho, nomphefumlo wabafazi bakho; Yehova, nobomi bezithende zakho;', 'XHO22'),
(14, 19, 6, 'Ngokuthanda iintshaba zakho nangokuthiya abahlobo bakho. Ngokuba namhla uthe, Akukhathali ngabathetheli nabakhonzi; ngenxa yokuba namhlanje ndiyabona ukuba ukuba ebesaphila uAbhisalom size sife sonke namhlanje, ngekulungile kuwe.', 'XHO22'),
(14, 19, 7, 'Vuka ke ngoko, uhambe, uthethe kakuhle nabakhonzi bakho; ngokuba ndifunga uYehova, ndithi, ukuba anithanga niphume, inene, akukho mntu uya kuhlala nani ngobu busuku; kuya kuba kubi ngaphezu kobubi bonke obuzayo wena kwasebuncinaneni bakho kude kube ngoku.', 'XHO22'),
(14, 19, 8, 'Wavuka ukumkani, wahlala esangweni. Baxelelwa abantu bonke, kwathiwa, Nanko ukumkani ehleli esangweni. Beza bonke abantu phambi kokumkani; ngokuba oonyana bakaSirayeli babebaleke, elowo waya ententeni yakhe.', 'XHO22'),
(14, 19, 9, 'Basuka ke bonke abantu baxabana ezizweni zonke zakwaSirayeli, besithi: “uYehova usihlangule esandleni seentshaba zethu, yaye usihlangule esandleni samaFilisti; wabaleka ke ngoku ebusweni buka-Abhisalom, ezweni.', 'XHO22'),
(14, 19, 10, 'uAbhisalom, owasithambisayo ukuba abe ngukumkani, ufele edabini; Yini na ukuba ningathi, nimbuyise ukumkani?', 'XHO22'),
(14, 19, 11, 'Wathumela ukumkani uDavide kuTsadoki nakuAbhiyatare ababingeleli, esithi, Nothi kumadoda amakhulu akwaYuda, nithi, Yini na ukuba nibe ngabokugqibela ekumbuyiseni ukumkani endlwini yakhe? walibona ilizwi lamaSirayeli onke, waya kukumkani endlwini yakhe.', 'XHO22'),
(14, 19, 12, 'Nabazalwana bam, namathambo am, nenyama yam; yini na ukuba nibe ngabokugqibela ekumbuyiseni ukumkani?', 'XHO22'),
(14, 19, 13, 'Wathi kuAmasa, Akulilo ithambo lam, nenyama yam na? Ngamana uThixo wandinika ukuba ungabi kufutshane nabantu bakowethu endaweni kaYowabhi.', 'XHO22'),
(14, 19, 14, 'Wayitsala intliziyo yamadoda onke akwaYuda njengandoda-nye; Bathumela ke ilizwi kukumkani, bathi, Buya, wena nabakhonzi bakho bonke;', 'XHO22'),
(14, 19, 15, 'Wabuya ke ukumkani, wafika eYordan. Afika amaYuda eGiligali, esiya kumkhawulela ukumkani, amthuthuzela ukumkani waseYordan.', 'XHO22'),
(14, 19, 16, 'uShimehi, unyana kaGera, umBhenjamin, waseBhahurim, wehla ngokukhawuleza namadoda akwaYuda, esiya kukhawulela ukumkani uDavide.', 'XHO22'),
(14, 19, 17, 'Wayekunye naye iwaka lamadoda akwaBhenjamin, noTsibha, umkhonzi wendlu kaSawule, noonyana bakhe abalishumi elinesihlanu namashumi amabini kubakhonzi bakhe ababenaye; bayiwela iYordan phambi kokumkani.', 'XHO22'),
(14, 19, 18, 'Wanduluka ngomkhombe, waya endlwini yokumkani, ukuba enze ngoko akubonileyo. uShimehi, unyana kaGera, wawa phambi kokumkani ekuweleni kwakhe iYordan;', 'XHO22'),
(14, 19, 19, 'Wathi kukumkani, Musa ukundigweba ngobugqwetha, nkosi yam, ungabukhumbuli ububi abenzileyo umkhonzi wakho, ekususweni kwenkosi yam ukumkani eYerusalem, ukuze ukumkani angayibeki loo nto entliziyweni yam.', 'XHO22'),
(14, 19, 20, 'Ngokuba umkhonzi wakho lo uyazi, ukuba ndihlulekile; nanku, ndingowokuqala ofikileyo namhla wasendlwini yonke kaYosefu, ukuba ndihle ndiye kumkhawulela ukumkani inkosi yam.', 'XHO22'),
(14, 19, 21, 'Waphendula uAbhishayi unyana kaTseruya, wathi, Anditshongo na ukuthi, uShimehi akayi kubulawa, ngokuba wamtshabhisayo umthanjiswa kaYehova?', 'XHO22'),
(14, 19, 22, 'Wathi uDavide, Yintoni na enam nani, nyana bakaTseruya, ngokuba nide nibachase nje namhla? Kuya kubulawa mntu na kwaSirayeli namhla? kuba andiyazi na ukuba ndingukumkani wakwaSirayeli namhlanje?', 'XHO22'),
(14, 19, 23, 'Wathi ukumkani kuShimehi, Akuyi kufa. Ukumkani wamfungela.', 'XHO22'),
(14, 19, 24, 'Ke uMefibhoshete, unyana kaSawule, wehla waya kumkhawulela ukumkani, akazilungisa iinyawo zakhe, engazichebi neendevu zakhe, engazihlambanga neengubo zakhe, kususela kwimini yokuhamba kokumkani, kwada kwayimini yokubuya kwakhe enoxolo.', 'XHO22'),
(14, 19, 25, 'Ekufikeni kwakhe eYerusalem eza kukhawulela ukumkani, wathi ukumkani kuye, Yini na ukuba ungahambi nam, Mefibhoshete?', 'XHO22'),
(14, 19, 26, 'Wathi, Nkosi yam, nkosi yam, umkhonzi wam undixokisile; ngokuba umkhonzi wakho lo undim ubesithi, Ndiya kulibophela iesile, ndikhwele kulo, ndiye kukumkani; ngokuba umkhonzi wakho lo undim.', 'XHO22'),
(14, 19, 27, 'Wahleba umkhonzi wakho enkosini yam ukumkani; kodwa ukumkani, nkosi yam, unjengesithunywa sikaThixo; wenze ke ngoko kulungileyo emehlweni akho.', 'XHO22'),
(14, 19, 28, 'Ngokuba yonke indlu kabawo yafa phambi kwenkosi yam ukumkani; kanti wena umbekile umkhonzi wakho phakathi kwabadla etafileni yakho. Liliphi ilungelo endisenalo lokubiza ukumkani?', 'XHO22'),
(14, 19, 29, 'Wathi ukumkani kuye, Akuthethi na ngemicimbi yakho? Ndathi: Wena noTsiba, yabelanani ngehlabathi.', 'XHO22'),
(14, 19, 30, 'Wathi uMefibhoshete kukumkani, Makayithabathe yonke into, ngokuba ukumkani, inkosi yam, ubuyele endlwini yakhe enoxolo.', 'XHO22'),
(14, 19, 31, 'uBharsileyi waseGiliyadi wehla eRogelim, wawela nokumkani eYordan, ukuba amthuthuzele eYordan.', 'XHO22'),
(14, 19, 32, 'uBharasileyi ebemdala kakhulu, eminyaka imashumi asibhozo ezelwe; kuba wayeyindoda enkulu kakhulu.', 'XHO22'),
(14, 19, 33, 'Wathi ukumkani kuBharsilayi, Hamba nam, ndokugcina eYerusalem.', 'XHO22'),
(14, 19, 34, 'Wathi uBharisilayi kukumkani, Lixesha lingakanani na elisaseleyo, ukuba ndinyuke ndiye eYerusalem nokumkani?', 'XHO22'),
(14, 19, 35, 'Namhlanje ndiminyaka imashumi asibhozo ubudala, yaye ndinako na ukwahlula phakathi kokulungileyo nokubi? Unako na umkhonzi wakho lo ukuyiva into ayidlayo nayiselayo? ndisenako na ukuva amazwi eemvumi neentombi? Yini na ukuba umkhonzi wakho lo abuye abe ngumthwalo enkosini yam?', 'XHO22'),
(14, 19, 36, 'Umkhonzi wakho lo uya kuyiwela iYordan nokumkani umgama omfutshane; yini na ukuba ukumkani andibuyekeze ngalo mvuzo?', 'XHO22'),
(14, 19, 37, 'Makabuye umkhonzi wakho, ndifele emzini wakowethu, ndingcwatyelwe engcwabeni likabawo noma. Nanko umkhonzi wakho uKiham; makawele nokumkani, nkosi yam; wenze okulungileyo kuwe.', 'XHO22'),
(14, 19, 38, 'Waphendula ukumkani, wathi, uKiham wowela nam, ndikwenzele okulungileyo; nento othe wayicela kum, ndokwenzela.', 'XHO22'),
(14, 19, 39, 'Lonke uhlanga lwawela iYordan; Akufika ukumkani, ukumkani wamanga uBharsilayi, wamsikelela; wagoduka.', 'XHO22'),
(14, 19, 40, 'Wawela ke ukumkani, waya eGiligali, wahamba naye uKiham; yaye bonke abantu bakwaYuda bakhokela ukumkani nenxalenye yesihlanu yabantu bakwaSirayeli.', 'XHO22'),
(14, 19, 41, 'Eza onke amadoda akwaSirayeli kukumkani, athi kukumkani, Yini na ukuba abazalwana bethu, amadoda akwaYuda, bebele kuwe, bamthuthuzele ukumkani nendlu yakhe namadoda onke kaDavide eYordan?', 'XHO22'),
(14, 19, 42, 'Aphendula onke amadoda akwaYuda kumadoda akwaSirayeli, athi, Kungokuba ukumkani ekufuphi kuthi; ngoku uqumbele ntoni ngalento? Ngaba sikhe satya na ngemali yokumkani? Okanye ngaba usinike isipho?', 'XHO22'),
(14, 19, 43, 'Aphendula amadoda akwaSirayeli kumadoda akwaYuda, athi, Sinezabelo ezilishumi kukumkani, kananjalo sinegunya ngaphezu kwenu kuDavide kunani; siyambuyisela ukumkani wethu? Yaye ilizwi lamadoda akwaYuda lalinamandla kunamadoda akwaSirayeli.', 'XHO22'),
(14, 20, 1, 'Kwaye kukho indoda elitshijolo, egama linguShebha, unyana kaBhigi, umBhenjamin; wavuthela isigodlo, wathi, Asinasabelo kuDavide, singenalifa kunyana kaYese. Maye elowo ententeni yakhe, Sirayeli!', 'XHO22'),
(14, 20, 2, 'Amshiya ke onke amadoda akwaSirayeli kuDavide, alandela uShebha unyana kaBhigi; ke amadoda akwaYuda anamathela kukumkani wawo, ethabathela eYordan esa eYerusalem.', 'XHO22'),
(14, 20, 3, 'uDavide wafika endlwini yakhe eYerusalem; Ukumkani wathabatha abafazi abalishumi kwizithende zakhe, abebashiyile ukuba balinde indlu, wababeka elugcinweni, babagcine, akangena nabo. Bavalelwa ke entolongweni kwada kwayimini yokufa kwabo, behleli njengabahlolokazi.', 'XHO22'),
(14, 20, 4, 'Wathi ukumkani kuAmasa, Ndibizele amadoda akwaYuda imihla emithathu, ube apha.', 'XHO22'),
(14, 20, 5, 'uAmasa waya kuwahlanganisa amadoda akwaYuda, walibala ngaphezu kwexesha abemiselwe lona.', 'XHO22'),
(14, 20, 6, 'Wathi uDavide kuAbhishayi, Kaloku uSebha, unyana kaBhigi, uya kusenzela ububi kunoAbhisalom;', 'XHO22'),
(14, 20, 7, 'Aphuma emva kwakhe amadoda kaYowabhi, namaKrete nezigidimi, namagorha onke, aphuma eYerusalem, asukela uShebha unyana kaBhigi.', 'XHO22'),
(14, 20, 8, 'Bafika engxondorheni eliseGibheyon, waphuma uAmasa, wabakhokela. Ke uYowabhi ubeyibhinqile ingubo yakhe abeyinxibile, ebhinqe ikrele lakhe esinqeni sakhe, lingumbhinqo wakhe; uthe akugqiba wawa.', 'XHO22'),
(14, 20, 9, 'Wathi uYowabhi kuAmasa, Usahleli kakuhle na, mzalwana wam? uYowabhi wathabatha iindevu zika-Amasa ngesandla sakhe sokunene, ukuba amange.', 'XHO22'),
(14, 20, 10, 'Akaliphulaphulanga uAmasa ikrele elalisesandleni sikaYowabhi; wafa ke. uYowabhi noAbhishayi umninawa wakhe basukela uShebha, unyana kaBhigi.', 'XHO22'),
(14, 20, 11, 'Wema ke omnye wakubakhonzi bakaYowabhi ngakuye, wathi, Lowo umthandayo uYowabhi, ongakuDavide, makalandele uYowabhi.', 'XHO22'),
(14, 20, 12, 'uAmasa yena wayelele igazi phezu kohola wendlela. Indoda leyo yakubona ukuba abantu bonke bamile, yamthabatha uAmasa emendweni, yamsa emaphandleni, yaphosa ingubo phezu kwakhe, yababona bemile bonke abezayo kuye.', 'XHO22'),
(14, 20, 13, 'Wathi akususwa emendweni, bonke abantu balandela uYowabhi, basukela uShebha unyana kaBhigi.', 'XHO22'),
(14, 20, 14, 'Wacanda ezizweni zonke zakwaSirayeli, wada wesa eAbhele, naseBhete-maga, naseBhabheli yonke; babuthelana ndawonye, bamlandela nabo.', 'XHO22'),
(14, 20, 15, 'Bafika bayingqinga iAbhele-bhete-maga, bawuphosa ngodaka, bema emhadini onke amadoda abenoYowabhi aludiliza udonga, ukuze aludilize.', 'XHO22'),
(14, 20, 16, 'Ke kaloku kwamemeza intokazi elumkileyo ikuloo mzi, isithi, Phulaphulani, phulaphulani! nithi kuYowabhi, Yiza apha, ndithethe nawe.', 'XHO22'),
(14, 20, 17, 'Wafika kuye; yathi loo ntokazi, UnguYowabhi na? Waphendula ke yena wathi, Ndinguye. Wathi kuye, Yiva amazwi omkhonzazana wakho. Waphendula ke yena wathi, Ndiyeva.', 'XHO22'),
(14, 20, 18, 'Wathetha ke wathi, Kwasephakadeni babesithi, Nobuza kuAbheli; bawugqiba ke umcimbi.', 'XHO22'),
(14, 20, 19, 'Ungowabathanda uxolo, nabanyanisileyo kwaSirayeli; ufuna umzi kunye nomama kaSirayeli; Yini na ukuba nidle ilifa likaYehova?', 'XHO22'),
(14, 20, 20, 'Waphendula uYowabhi, wathi, Kukude lee kum, ukuba ndidle, nditshabalale.', 'XHO22'),
(14, 20, 21, 'Umbandela awunjalo, kodwa indoda yentaba yakwaEfrayim, egama linguSebha, unyana kaBhikri, yaphakamisa isandla sayo kukumkani, nxamnye noDavide; mgcine, ndowushiya umzi. Yathi inkazana kuYowabhi, Yabona, intloko yakhe iya kuwa eludongeni, ngenxa yakho.', 'XHO22'),
(14, 20, 22, 'Yaya loo ntokazi kubo bonke abantu ngobulumko bayo. Bayinqumla ke intloko kaShebha, unyana kaBhigi, bayiphosa ngaphandle koYowabhi. Wavuthela isigodlo, baphuma kuloo mzi, elowo waya ententeni yakhe. uYowabhi wabuyela eYerusalem kukumkani.', 'XHO22'),
(14, 20, 23, 'uYowabhi wayephethe umkhosi wonke wakwaSirayeli, yaye uBhenaya unyana kaYehoyada wayephethe amaKrete namaPleti.', 'XHO22'),
(14, 20, 24, 'uAdoram wayephethe irhafu, yaye uYehoshafati unyana ka-Ahiludi wayeyinjengele yenkundla.', 'XHO22'),
(14, 20, 25, 'uSheva ubengumbhali, uTsadoki noAbhiyatare bengababingeleli.', 'XHO22'),
(14, 20, 26, 'Kwakhona, uIra, umYiputa, wayengumlawuli kaDavide.', 'XHO22'),
(14, 21, 1, 'Kwabakho indlala ngemihla kaDavide iminyaka emithathu ilandelelana; uDavide wabuza kuYehova. Wathi uYehova, Kungenxa kaSawule nagazi lakhe, ngokuba wawabulala amaGibheyon.', 'XHO22'),
(14, 21, 2, 'uYehova wababiza abaseGibheyon, wathi kubo, (AbaseGibheyon babengengabo oonyana bakaSirayeli, kodwa babengamasalela ama-Amori; oonyana bakaSirayeli bafunga kubo, waza uSawule walinga ukubabulala ngekhwele lakhe oonyana bakaSirayeli nabakwaYuda.)', 'XHO22'),
(14, 21, 3, 'Wathi uDavide kwabaseGibheyon, Ndinenzele ntoni na? yaye ndiya kwenza umnqophiso ngantoni na ukuze nilisikelele ilifa likaYehova?', 'XHO22'),
(14, 21, 4, 'Athi abaseGibheyon kuye, Asinasilivere nagolide kuSawule nendlu yakhe; yaye ngenxa yethu musani ukubulala nabani na kwaSirayeli. Wathi, Into oyithethayo ndiya kuyenza.', 'XHO22'),
(14, 21, 5, 'Baphendula bathi kukumkani, Yilaa ndoda yasitshabalalisayo, eyayifuna ukusitshabalalisa, ukuze sihlale ezweni lonke lakwaSirayeli.', 'XHO22'),
(14, 21, 6, 'Masinikwe amadoda abe sixhenxe koonyana bakhe, siwaxhome phambi koYehova, iGibheha kaSawule, onyuliweyo nguYehova. Wathi uYehova, Ndiya kubanika.', 'XHO22'),
(14, 21, 7, 'Kodwa ukumkani wamsindisa uMefibhoshete unyana kaYonatan unyana kaSawule ngenxa yesifungo sikaYehova esasiphakathi kwabo, phakathi koDavide noYonatan unyana kaSawule.', 'XHO22'),
(14, 21, 8, 'uYehova wathabatha oonyana ababini bakaRitspa, intombi ka-Aya, awabazalela uSawule, uArmoni noMefibhoshete; noonyana abahlanu bakaMikali, intombi kaSawule, awabanyusela kuAdriyeli, unyana kaBharsilayi, waseMehola;', 'XHO22'),
(14, 21, 9, 'Wabanikela esandleni samaGibheyon, aza abaxhoma endulini phambi koYehova; zawa ndawonye zosixhenxe, zabulawa ngemihla yokuvuna, ekuqaleni, ekuqalekeni kwerhasi isivuno.', 'XHO22'),
(14, 21, 10, 'uRitspa, intombi ka-Aya, wathabatha iingubo ezimnyama, wazandlala phezu kwakhe eweni, kususela ekuqalekeni kokuvuna, ada awela amanzi phezu kwabo, esibhakabhakeni, ukuba zingavuki iintaka zezulu ngokuhlwa phumla, namarhamncwa ebusuku.', 'XHO22'),
(14, 21, 11, 'Waxelelwa uDavide awakwenzayo uRitspa, intombi ka-Aya, umlungiseleli kaSawule.', 'XHO22'),
(14, 21, 12, 'uDavide wahamba waza wawathabatha amathambo kaSawule nonyana wakhe uYonatan kumadoda aseYabheshe yaseGiliyadi, awayewabile kwindawo yembutho yaseBhete-shan, apho amaFilisti ayebaxhome khona, xa amaFilisti ebulala uSawule iGilibhowa.', 'XHO22'),
(14, 21, 13, 'Wawanyusa khona amathambo kaSawule, namathambo kaYonatan unyana wakhe; yaye bawaqokelela amathambo abo babexhonyiwe.', 'XHO22'),
(14, 21, 14, 'Amathambo kaSawule noYonatan unyana wakhe, angcwatyelwa ezweni lakwaBhenjamin, eSela, engcwabeni likaKishe uyise; benza konke abewise umthetho ngako ukumkani. Kwaye ke uThixo wathandazwa ehlabathini.', 'XHO22'),
(14, 21, 15, 'Kananjalo amaFilisti alwa kwakhona noonyana bakaSirayeli; Wehla uDavide, nabakhonzi bakhe balwa naye namaFilisti, ediniwe uDavide.', 'XHO22'),
(14, 21, 16, 'Ke yena uIshbhi-bhenobhi, woonyana bamaRefa, ubunzima bomkhonto wakhe babungamakhulu amathathu eeshekele zobhedu, exhobe ngekrele elitsha, wacinga ukuba umbulele uDavide.', 'XHO22'),
(14, 21, 17, 'Ke uAbhishayi unyana kaTseruya wamnceda, wamxabela umFilisti lowo, wambulala. Aza amadoda kaDavide amfungela, esithi, Uze ungabi sahamba nathi ekulweni, hleze ucime ukukhanya kwaSirayeli.', 'XHO22'),
(14, 21, 18, 'Emva koko kwabakho elinye idabi namaFilisti eGobhi;', 'XHO22'),
(14, 21, 19, 'Kwaphinda kwabakho imfazwe namaFilisti eGobhi, waza uElihanan unyana kaYahare-oregime umBhetelehem umntakwabo Goliyati umGati, umkhonto wakhe wawunjengomthi wabaluki, wawoyisa.', 'XHO22'),
(14, 21, 20, 'Kwabakho ke imfazwe eGati, apho kwakukho indoda emde, ineminwe emithandathu esandleni ngasinye, neenzwane ezintandathu elunyaweni lunye, amashumi amabini anesine ngenani; naye wazalwa sisigebenga.', 'XHO22'),
(14, 21, 21, 'Kwathi, ekulweni kwakhe namaSirayeli, wambulala uYonatan unyana kaShimeha, umkhuluwa kaDavide.', 'XHO22'),
(14, 21, 22, 'Oonyana abane bazalelwa ingxilimbela yaseGati yaye bawa ngesandla sikaDavide nangezandla zabakhonzi bakhe.', 'XHO22'),
(14, 22, 1, 'uDavide wamxelela uYehova amazwi ale ngoma, ekumhlanguni kwakhe uYehova esandleni seentshaba zakhe zonke, nasesandleni sikaSawule.', 'XHO22'),
(14, 22, 2, 'Wathi, uYehova liliwa lam, Imboniselo yam, umsindisi wam;', 'XHO22'),
(14, 22, 3, 'uThixo liliwa lam; Ndiya kuzimela ngaye, ingweletshetshe yam, luphondo losindiso lwam, Ingxonde yam, ndawo yokusabela kwam, umsindisi wam; Uyandisindisa kugonyamelo.', 'XHO22'),
(14, 22, 4, 'Ndinqula uYehova ongowokudunyiswa; ndiya kusinda ezintshabeni zam.', 'XHO22'),
(14, 22, 5, 'Ekundijikeleni kwamaza okufa, Imilambo yobungendawo yandidandathekisa;', 'XHO22'),
(14, 22, 6, 'Iintswelo zobomi bezisondele kum; Izirhintyelo zokufa zandivingca;', 'XHO22'),
(14, 22, 7, 'Ndanqula uYehova ngentliziyo yam, Ndabiza uThixo wam, Waliva etempileni yakhe izwi lam, Ukuzibika kwam kwafika ezindlebeni zakhe.', 'XHO22'),
(14, 22, 8, 'Lanyikima ihlabathi, lanyikima; Zashukuma iziseko zezulu, Ngokuba enomsindo.', 'XHO22'),
(14, 22, 9, 'Kwenyuka umsi emathatheni akhe, kwatsha umlilo emlonyeni wakhe; amalahle abaswe yiyo.', 'XHO22'),
(14, 22, 10, 'Wawathoba amazulu, wehla; Kwaye ke kumnyama phantsi kweenyawo zakhe.', 'XHO22'),
(14, 22, 11, 'Wakhwela phezu kwekerubhi, waphaphazela, wabonakala emaphikweni omoya.', 'XHO22'),
(14, 22, 12, 'Wazenza mnyama iintente, ukuba amsibekele ngaphakathi, amanzi abe mnyama, neengqimba zamafu ezulu.', 'XHO22'),
(14, 22, 13, 'Avutha amalahle omlilo phambi kwakhe.', 'XHO22'),
(14, 22, 14, 'Wadudumisa emazulwini uYehova, Oyena Uphakamileyo walikhupha ilizwi lakhe.', 'XHO22'),
(14, 22, 15, 'Wathuma iintolo, wabaphangalalisa; umbane, wabadubaduba.', 'XHO22'),
(14, 22, 16, 'Yabonakala imijelo yolwandle, zatyhileka iziseko zehlabathi ngokukhalima kukaYehova, ngokuphefumla komoya wamathatha akhe.', 'XHO22'),
(14, 22, 17, 'Wathumela wandithabatha; Wandikhupha emanzini amaninzi;', 'XHO22'),
(14, 22, 18, 'Wandihlangula elutshabeni lwam olunamandla, Kwabandithiyileyo, ngokuba bebomelele kunam.', 'XHO22'),
(14, 22, 19, 'Bandifikela ngemini yokusindeka kwam, uYehova wandixhasa.', 'XHO22'),
(14, 22, 20, 'Wabuya wandisa endaweni ebanzi; Wandisindisa, ngokuba endithandile.', 'XHO22'),
(14, 22, 21, 'uYehova undibize ngokobulungisa bam; ngobunyulu bezandla zam uyandikhulula.', 'XHO22'),
(14, 22, 22, 'Kuba ndizigcinile iindlela zikaYehova, yaye andizange ndiphambuke kuThixo wam ngobungendawo.', 'XHO22'),
(14, 22, 23, 'Kuba yonke imiyalelo yakhe emiselweyo yayiphambi kwam, nakwimiyalelo yakhe emiselweyo andityekanga.', 'XHO22'),
(14, 22, 24, 'Ndandililungisa phambi kwakhe, Ndazinqanda ebubini bam.', 'XHO22'),
(14, 22, 25, 'Ngenxa yoko, uYehova undivuze ngobulungisa bam; ngobunyulu bam phambi kwamehlo akhe.', 'XHO22'),
(14, 22, 26, 'Kowenceba uya kuba nenceba, kwilungisa uzenza ilungisa.', 'XHO22'),
(14, 22, 27, 'Kohlambulukileyo uya kuzibonakalisa uhlambulukile; na kongendawo ukucaphuka kwakho.', 'XHO22'),
(14, 22, 28, 'Amahlwempu aya kukuhlangula, Amehlo akho ajonge abanekratshi, Ukuba ubathobe.', 'XHO22'),
(14, 22, 29, 'Kuba usisibane sam, Yehova, yaye uYehova ukhanyisa ubumnyama bam.', 'XHO22'),
(14, 22, 30, 'Ngenxa yakho ndihambe phakathi kwamatutu, ngenxa yoThixo wam nditsibe eludongeni.', 'XHO22'),
(14, 22, 31, 'uThixo yena, igqibelele indlela yakhe; Ilizwi likaYehova linyibilikisiwe; Uyingweletshetshe kubo bonke abazimela ngaye.', 'XHO22'),
(14, 22, 32, 'Ngokuba ngubani na onguThixo, ingenguye uYehova? Ngubani na oliliwa, ingenguThixo wethu?', 'XHO22'),
(14, 22, 33, 'UloThixo uligwiba lam, uligwiba lam, Umakhileyo indlela yam;', 'XHO22'),
(14, 22, 34, 'Uzifanisayo iinyawo zam nezamaxhama, Undimisayo ezinyaweni zam;', 'XHO22'),
(14, 22, 35, 'Uzifundisayo ukulwa izandla zam; zaza izandla zam zawaphula isaphetha sentsimbi.', 'XHO22'),
(14, 22, 36, 'Undinike ingweletshetshe yosindiso lwakho, Yandomeleza inceba yakho.', 'XHO22'),
(14, 22, 37, 'Wandise amanyathela am phantsi kwam; ukuze zingatyibilizi iinyawo zam.', 'XHO22'),
(14, 22, 38, 'Ndiya kuzisukela iintshaba zam, ndizitshabalalise; andabuya ndade ndawagqiba.', 'XHO22'),
(14, 22, 39, 'Ndazitshabalalisa, ndazibetha, azaba nakuvuka; Zawa phantsi kweenyawo zam.', 'XHO22'),
(14, 22, 40, 'Ngokuba undibophe ngamandla okulwa; Abo bandomelezayo, wabathoba phantsi kwam.', 'XHO22'),
(14, 22, 41, 'Kananjalo undinike intamo yeentshaba zam, Ukuze ndibatshabalalise abandithiyayo.', 'XHO22'),
(14, 22, 42, 'Bakhangela, kungabikho msindisi; kuYehova, kodwa akazange abaphendule.', 'XHO22'),
(14, 22, 43, 'Ndababetha njengothuli lomhlaba, Ndibasasaza njengodaka lwendlela, ndibasasaza.', 'XHO22'),
(14, 22, 44, 'Kananjalo wandisindisa ezibambanweni zabantu bam, wandigcina ndiyintloko yeentlanga; abantu ebendingabazi baya kundikhonza.', 'XHO22'),
(14, 22, 45, 'Oonyana bolunye uhlanga baya kundilulamela; kamsinya nje bakuva, baya kundithobela.', 'XHO22'),
(14, 22, 46, 'Oonyana bolunye uhlanga baya kunqunyulwa, bangcangcazele phakathi kwabo;', 'XHO22'),
(14, 22, 47, 'uYehova uhleli; lelokubongwa iliwa lam; kwaye makaphakanyiswe uThixo oliliwa losindiso lwam.', 'XHO22'),
(14, 22, 48, 'Mna ndinguThixo ondikhululayo, ozithobelayo phantsi kwam iintlanga;', 'XHO22'),
(14, 22, 49, 'Undikhuphayo ezintshabeni zam; Undiphakamisile kwabandiphakamisileyo; Wandihlangula kumcinezeli.', 'XHO22'),
(14, 22, 50, 'Kungenxa yoko le nto ndifuna ukukubonga, Yehova, phakathi kweentlanga, ndilibethele uhadi igama lakho.', 'XHO22'),
(14, 22, 51, 'Uligwiba losindiso lokumkani wakhe, Unenceba kumthanjiswa wakhe, KuDavide, nakwimbewu yakhe kude kuse ephakadeni.', 'XHO22'),
(14, 23, 1, 'Ngawo la amazwi okugqibela kaDavide. Wathetha uDavide, unyana kaYese, yaphakanyiswa indoda, umthanjiswa kaThixo kaYakobi, umdumisi wakwaSirayeli, wathi,', 'XHO22'),
(14, 23, 2, 'uMoya kaYehova uthetha ngam, ilizwi lakhe liselulwimini lwam.', 'XHO22'),
(14, 23, 3, 'Wathi uThixo kaSirayeli, Iliwa lakwaSirayeli lithethile kum, lathi, Olawula abantu uya kuba lilungisa, alawule emoyika uThixo;', 'XHO22'),
(14, 23, 4, 'Unjengokukhanya kokusa, ekuphumeni kwelanga, ngesifingo esingenalifu; njengengca ephuma emhlabeni, Kukukhanya okuqaqambileyo emva kwemvula.', 'XHO22'),
(14, 23, 5, 'Nakuba indlu yam ingenjalo kuThixo; noko ke wenze nam umnqophiso ongunaphakade, osingiselwayo, osisiqinisekiso sezinto zonke; kuba oku kukusindiswa kwam konke kunye nomnqweno wam wonke, nokuba akaphakamisi.', 'XHO22'),
(14, 23, 6, 'Ke bonke oonyana bakaBheliyali baya kuba njengameva ahlabayo, ngokuba awabanjiswa ngazandla.', 'XHO22'),
(14, 23, 7, 'Umntu ozichukumisayo woba nothango lwentsimbi nomkhonto esinqeni sakhe; uzitshise khona ngomlilo.', 'XHO22'),
(14, 23, 8, 'Ngawo la amagama amadoda anamandla awayekunye nawo uDavide: uTahemon owayehleli etroneni, intloko yeenkosana; yayinguAdino, eMoesne; wanqanda umkhonto wakhe kumazinyo angamakhulu alithoba anamabini awawahlabayo kwakanye.', 'XHO22'),
(14, 23, 9, 'Ngasemva kwakhe nguEleyazare unyana kaDodo umAhowa, omnye wamagorha amathathu awayenoDavide, ekulweni kwabo namaFilisti awayelapho ukuba aye kulwa, namadoda akwaSirayeli ahamba.', 'XHO22'),
(14, 23, 10, 'Wesuka wawabulala amaFilisti, sada sadinwa isandla sakhe, sanamathela ekreleni isandla sakhe; uYehova wenza usindiso olukhulu ngaloo mini; abuya amadoda lawo, emlandela, esiya kuphanga kuphela.', 'XHO22'),
(14, 23, 11, 'Emva kwakhe nguShama unyana ka-Age, uMoharari. Yaye amaFilisti ahlanganisana ndawonye, apho intsimi yayizele ziimbotyi; asaba amadoda phambi kwamaFilisti.', 'XHO22'),
(14, 23, 12, 'Kodwa yena wema esazulwini selizwe waza wawukhusela waza wawabulala amaFilisti, yaye uYehova wenza usindiso olukhulu.', 'XHO22'),
(14, 23, 13, 'Kwehla isithathu sabaziintloko emashumini amathathu, beza kuDavide ngexesha lokuvuna emqolombeni waseAdulam;', 'XHO22'),
(14, 23, 14, 'Ngelo xesha uDavide wayesezintabeni kwindawo enqatyisiweyo, yaye abalindi bamaFilisti babeseBhetelehem ngelo xesha.', 'XHO22'),
(14, 23, 15, 'Wanqwena uDavide, wathi, Akwaba bendithe ndasela amanzi equla laseBhetelehem elisesangweni!', 'XHO22'),
(14, 23, 16, 'Atyhoboza umkhosi wamaFilistiya amadoda amathathu lawo, akha amanzi equleni laseBhetelehem elisesangweni, awazisa kuDavide; akavuma ukuwasela, wawagalela phambi koYehova.', 'XHO22'),
(14, 23, 17, 'Wathi, Makube lee kum, Yehova, ukwenza oko! ngoko ke, akazange akuthande ukuwasela. Yiloo nto eyenza loo magorha mathathu.', 'XHO22'),
(14, 23, 18, 'Ke uAbhishayi, umninawa kaYowabhi, unyana kaTseruya, waye eyintloko yabathathu. Wajula umkhonto wakhe kumakhulu amathathu, wababulala; laduma igama lakhe phakathi kwabathathu.', 'XHO22'),
(14, 23, 19, 'Ebengebekeke ngaphezu kwabo bathathu na? ngoko xa wayengumlawuli wabo; kodwa akafikanga kwabathathu bokuqala.', 'XHO22'),
(14, 23, 20, 'uBhenaya unyana kaYehoyada, unyana wendoda enobukroti, waseKabhetseli, owenza izenzo ezininzi, wabulala amadoda amabini akwaMowabhi, njengeengonyama;', 'XHO22'),
(14, 23, 21, 'Wabulala indoda engumYiputa, enenzwakazi; UmYiputa lowo ephethe intshuntshe esandleni sakhe; kodwa wehla waya kuye ephethe intonga waza wawuhlutha umkhonto esandleni somYiputa waza wambulala ngomkhonto wakhe.', 'XHO22'),
(14, 23, 22, 'Le nto yenziwa nguBhenaya unyana kaYehoyada, wayedume phakathi kwamagorha amathathu.', 'XHO22'),
(14, 23, 23, 'Wayebekekile yena ngaphezu kwamashumi amathathu lawo, kodwa akafikanga kwabathathu bokuqala. uDavide wammisa phezu kwabalindi bakhe.', 'XHO22'),
(14, 23, 24, 'uAsaheli, umninawa kaYowabhi, ebengomnye wamashumi omathathu lawo; uElihanan, unyana kaDodo, waseBhetelehem;', 'XHO22'),
(14, 23, 25, 'uSama waseHarodi, noElika waseHarodi,', 'XHO22'),
(14, 23, 26, 'noHele wasePalta, noIra unyana kaIkeki waseTekowa;', 'XHO22'),
(14, 23, 27, 'noAbhiyezere waseMoanatoti, noMebhunayi waseMohusa;', 'XHO22'),
(14, 23, 28, 'Salmone Mowahoke, Maharai Monetofa,', 'XHO22'),
(14, 23, 29, 'noHelebhi, unyana kaBhahana, eMonetofu, noItayi, unyana kaRibhayi, waseGibheha, koonyana bakaBhenjamin;', 'XHO22'),
(14, 23, 30, 'noBhenaya wasePiraton, noHidayi waseMilanjeni yaseGahashe;', 'XHO22'),
(14, 23, 31, 'nguAbhihalibhon waseArbhati, neAsmaveti yaseBharahum,', 'XHO22'),
(14, 23, 32, 'noEliyabhi, unyana kaShahalebhon, koonyana bakaYashen, noYonatan;', 'XHO22'),
(14, 23, 33, 'noShama umMohari, noAkiyam unyana kaSharare umMohari;', 'XHO22'),
(14, 23, 34, 'noElifeleti, unyana ka-Akebhayi, unyana kaGati, noEliyam, unyana ka-Ahitofele waseGilon;', 'XHO22'),
(14, 23, 35, 'noHesrai waseKarmele, noPararayi wamaMowabhi,', 'XHO22'),
(14, 23, 36, 'noIgali, unyana kaNatan waseTsobha, noBhani waseMogadi;', 'XHO22'),
(14, 23, 37, 'noTseleki umAmon, noNaharayi waseBheroti, umkhonzi kaYowabhi, unyana kaTseruya;', 'XHO22'),
(14, 23, 38, 'noIra waseMoitere, noGarebhe waseMoitere,', 'XHO22'),
(14, 23, 39, 'ngu-Uriya umHeti, bebonke bengamashumi amathathu anesixhenxe.', 'XHO22'),
(14, 24, 1, 'Wabuya wavutha umsindo kaYehova kwaSirayeli, wamxhokonxela uDavide kubo, esithi, Hambani niwabale amaSirayeli namaYuda.', 'XHO22'),
(14, 24, 2, 'Wathi uYehova kuYowabhi, umthetheli-mkhosi obenaye, Hamba uye kuzo zonke izizwe zakwaSirayeli, uthabathela kwaDan use eBher-shebha, ubabale abantu, ndilazi inani labantu.', 'XHO22'),
(14, 24, 3, 'Wathi uYowabhi kukumkani, uYehova uThixo wakho makalunyuse uhlanga ngokuphindwe kalikhulu, engenakubalwa, abone namehlo okumkani, inkosi yam; Yini na ukuba ivume inkosi yam ukumkani ngale nto?', 'XHO22'),
(14, 24, 4, 'Laqinisela ilizwi lokumkani kuYowabhi nakubathetheli bamadoda. Waphuma ke uYowabhi nabathetheli bamaphakathi kukumkani, ukuba baye kubabala abantu bakwaSirayeli.', 'XHO22'),
(14, 24, 5, 'Bayiwela iYordan, bamisa eArohere, ngasekunene kwesixeko phakathi koMlambo uGadi, besa eYazere.', 'XHO22'),
(14, 24, 6, 'Baya eGiliyadi nasezweni laseTatimhode; Bafika eDanyan, bajikeleza eSidon.', 'XHO22'),
(14, 24, 7, 'Bafika enqabeni yaseTire, nasezixekweni zonke zamaHivi namaKanan, beza kwelasezantsi lakwaYuda, besa eBher-shebha.', 'XHO22'),
(14, 24, 8, 'Bakuba behamba nelizwe lonke, bafika eYerusalem emva kweenyanga ezisithoba ezineentsuku ezimashumi mabini.', 'XHO22'),
(14, 24, 9, 'uYowabhi wamnika ukumkani inani labantu: amaSirayeli aba ngamakhulu asibhozo amawaka amadoda anobukroti, arhola ikrele; amadoda akwaYuda aba ngamakhulu amahlanu amawaka amadoda.', 'XHO22'),
(14, 24, 10, 'Yaphuka intliziyo kaDavide emveni kokuba ebabalile abantu. Wathi uDavide kuYehova, Ndonile kunene ngale nto ndiyenzileyo; ngokuba ndenze izinto ezininzi zobudenge.', 'XHO22'),
(14, 24, 11, 'Wavuka uDavide kusasa, lafika ilizwi likaYehova kuGadi umprofeti, imboni kaDavide, lisithi,', 'XHO22'),
(14, 24, 12, 'Yiya uthi kuDavide, uYehova wathi, Ndikunika ezithathu; khetha ibe nye kuyo, ndiyenzele wena.', 'XHO22'),
(14, 24, 13, 'Waya ke uGadi kuDavide, wamxelela, wathi kuye, Sele uneminyaka esixhenxe na kulamba ezweni lakho? Okanye wobaleka utshaba lwakho iinyanga zibe ntathu na, lukusukela? Okanye ngaba iintsuku ezintathu zesibetho kwilizwe lakho? kongeza ngoku, ubone into endiya kuyenza kowandithumayo.', 'XHO22'),
(14, 24, 14, 'Wathi uDavide kuGadi, Ndibandezelekile; masiwele kaloku esandleni sikaYehova; ngokuba inkulu inceba yakhe, mandingaweli ezandleni zabantu.', 'XHO22'),
(14, 24, 15, 'uYehova wathoba ke indyikitya yokufa kwaSirayeli, yathabathela ekuseni yesa kwixa elimisiweyo;', 'XHO22'),
(14, 24, 16, 'Xa ingelosi yasolula isandla sayo nxamnye neYerusalem ukuze iyitshabalalise, uYehova waba nosizi ngenxa yaloo ntlekele waza wathi kwingelosi eyayitshabalalisa abantu: “Kwanele, bamba isandla sakho! Ke isithunywa sikaYehova sasihleli etroneni ka-Aravena umYebhusi.', 'XHO22'),
(14, 24, 17, 'Wathetha uDavide kuYehova akusibona isithunywa eso sibabulala abantu, wathi, Yabona, ndonile, ndonile; kodwa ezi zimvu zenze ntoni na? isandla sakho masibe phezu kwam, naphezu kwendlu kabawo.', 'XHO22'),
(14, 24, 18, 'Waya ke uGadi kuDavide ngaloo mini, wathi kuye, Nyuka, uye kummisela uYehova isibingelelo esandeni sika-Aravena umYebhusi.', 'XHO22'),
(14, 24, 19, 'Wenyuka uDavide ngokwelizwi likaGadi, njengoko uYehova abemwisele umthetho ngako.', 'XHO22'),
(14, 24, 20, 'Waya uAravena, wambona ukumkani nabakhonzi bakhe besiza kuye; waphuma uAravena, waqubuda kukumkani ejonge phantsi.', 'XHO22'),
(14, 24, 21, 'Wathi uAravena, Yini na ukuba inkosi yam ukumkani ize kumkhonzi wayo? Wathi uDavide, Ndiya kunithengela isibingelelo, ukuze wakhele uYehova isibingelelo, sithintele abantu isibetho.', 'XHO22'),
(14, 24, 22, 'Wathi uAravena kuDavide, Ukumkani wam makathabathe ukumkani wam, axhelele oko kulungileyo kuye; nanzi iinkomo zokutshisa umlilo, neenkomo, nezinye izinto zeenkomo ukuba zibe ziinkuni.', 'XHO22'),
(14, 24, 23, 'Zonke ezo zinto, ukumkani, uAravena ubezinike ukumkani. Wathi uAravena kukumkani, uYehova uThixo wakho ukwamkele.', 'XHO22'),
(14, 24, 24, 'Wathi ukumkani kuAravena, Hayi! ndiya kusithenga ngexabiso kuwe; yaye ndiya kwenzela uYehova uThixo wam iminikelo etshiswayo endingayihlawulanga. uDavide wathenga isanda neenkomo ngeeshekele zesilivere ezimashumi mahlanu.', 'XHO22'),
(14, 24, 25, 'uDavide wamakhela khona uYehova isibingelelo waza wanikela iminikelo etshiswayo nemibingelelo yombulelo. Ngoko uYehova walithandazela ilizwe, yaye isibetho sayeka kwaSirayeli.', 'XHO22'),
(15, 1, 1, 'Ke kaloku ukumkani uDavide ubeseleyindoda enkulu, edandathekile iminyaka; bamambesa ke, akafudumala.', 'XHO22'),
(15, 1, 2, 'Bathi abakhonzi bakhe kuye, Nkosi, nkosi yam, makakhangele intombi; makame phambi kokumkani, amkhusele, alale esifubeni sakho, ifudumale inkosi yam ukumkani.', 'XHO22'),
(15, 1, 3, 'Bafuna intombi eyinzwakazi ezweni lonke lakwaSirayeli, bafumana uAbhishagi waseShunem, bamzisa kukumkani.', 'XHO22'),
(15, 1, 4, 'Loo ntombi yayiyinzwakazi encamisileyo, yamonga ukumkani, yamlungiselela, ukumkani akayazi.', 'XHO22'),
(15, 1, 5, 'Waza uAdoniya unyana kaHagiti waziphakamisa, wathi, Ndiyalawula; Wazilungisela iinqwelo zokulwa, nabamahashe, namadoda angamashumi amahlanu abaleka phezu kwakhe.', 'XHO22'),
(15, 1, 6, 'Uyise akazange ambethe emehlweni ngokuthi: “Uyenzele ntoni na loo nto? kwaye waye eyindoda entle kakhulu; unina wamzala emva koAbhisalom.', 'XHO22'),
(15, 1, 7, 'Wathetha noYowabhi unyana kaTseruya, noAbhiyatare umbingeleli, balandela uAdoniya.', 'XHO22'),
(15, 1, 8, 'Ke yena uTsadoki umbingeleli, noBhenaya unyana kaYehoyada, noNatan umprofeti, noShimehi, noReyi, namagorha kaDavide, ababanga ngakuAdoniya.', 'XHO22'),
(15, 1, 9, 'uAdoniya waxhelela izimvu neenkomo neenkomo ezityetyisiweyo kwilitye laseSohelete ngase-Enerogele, yaye wamema bonke abantakwabo oonyana bakakumkani nawo onke amadoda akwaYuda abakhonzi bakakumkani.', 'XHO22'),
(15, 1, 10, 'Akambizanga umprofeti uNatan, noBhenaya, namagorha, noSolomon umkhuluwa wakhe.', 'XHO22'),
(15, 1, 11, 'Ngoko uNatan wathetha noBhatshebha, unina kaSolomon, wathi: “Awuvanga na ukuba uAdoniya, unyana kaHagiti, uyalawula ngoku, ukanti inkosi yethu uDavide ayiyazi?', 'XHO22'),
(15, 1, 12, 'Khawuze ke, ndikucebise, uwusindise umphefumlo wakho nowonyana wakho uSolomon.', 'XHO22'),
(15, 1, 13, 'Hamba uye kukumkani uDavide, uthi kuye, Nkosi yam, nkosi yam, ubungamfungelanga na umkhonzazana wakho, wathi, uSolomon unyana wakho uya kuba ngukumkani emveni kwam, kuhlale etroneni yam? Ngoko, kwakutheni ukuze uAdoniya alawule?', 'XHO22'),
(15, 1, 14, 'Uyabona, ndothi, usathetha nokumkani, ndingene nam emva kwakho, ndiwazalise amazwi akho.', 'XHO22'),
(15, 1, 15, 'Wangena uBhatshebha egumbini lokumkani; uAbhishagi waseShuneme ubelungiselela ukumkani.', 'XHO22'),
(15, 1, 16, 'Wathoba uBhatshebha, wawa ngobuso bakhe phambi kokumkani. Wathi ukumkani, Ufuna ntoni na?', 'XHO22'),
(15, 1, 17, 'Wathi kuye, Nkosi, nguwe owamfungelayo uYehova uThixo wakho kumkhonzi wakho, wathi, uSolomon unyana wakho nguye oya kuba ngukumkani emveni kwam, kuhlale etroneni yam.', 'XHO22'),
(15, 1, 18, 'Ngoku ke nanku engukumkani uAdoniya; Kaloku wena nkosi yam kumkani, akukwazi.', 'XHO22'),
(15, 1, 19, 'Wabingelela iinkomo, neenkomo ezityetyisiweyo, neenkomo zaninzi, wababiza bonke oonyana bokumkani, noAbhiyatare umbingeleli, noYowabhi umthetheli-mkhosi; ke akambizanga uSolomon umkhonzi wakho.', 'XHO22'),
(15, 1, 20, 'Wena kumkani, kumkani, amehlo amaSirayeli onke akhangele kuwe, ukuba uwaxelele, ukuba kuhlale bani na etroneni yokumkani, inkosi yam, emveni kwakhe.', 'XHO22'),
(15, 1, 21, 'Kungenjalo, xa ithe yalala kooyise inkosi yam ukumkani, mna noSolomon unyana wam sogqalwa njengabakreqi.', 'XHO22'),
(15, 1, 22, 'Esathetha nokumkani, kwangena umprofeti uNatan.', 'XHO22'),
(15, 1, 23, 'Bamxelela ukumkani, bathi, Lo nguNatan umprofeti. Wathi akungena phambi kokumkani, waguqa phambi kokumkani, ubuso bakhe bubheke emhlabeni.', 'XHO22'),
(15, 1, 24, 'Wathi uNatan, Nkosi yam, ùtshilo na wena ukuthi, NguAdoniya oya kuba ngukumkani emveni kwam, kuhlale etroneni yam?', 'XHO22'),
(15, 1, 25, 'Kuba namhlanje uye wehla waza waxhela iinkomo neenkomo ezityetyisiweyo neenkomo zaninzi waza wabiza bonke oonyana bokumkani nabathetheli-mkhosi noAbhiyatare umbingeleli; nabo babesidla besela phambi kwakhe, besithi, Ukumkani uAdoniya makamkhusele!', 'XHO22'),
(15, 1, 26, 'Kodwa akandibizanga mna mkhonzi wakho, uTsadoki umbingeleli noBhenaya unyana kaYehoyada noSolomon umkhonzi wakho.', 'XHO22'),
(15, 1, 27, 'Le nto yenziwe yinkosi yam na, waza akwamxelela umkhonzi wakho oya kuhlala etroneni yokumkani emva kwakhe?', 'XHO22'),
(15, 1, 28, 'Waphendula ukumkani uDavide, wathi, Ndibizeleni uBhatshebha. Weza kukumkani, wema phambi kokumkani.', 'XHO22'),
(15, 1, 29, 'Wafunga uYehova, wathi, NgoYehova ovayo, owukhululayo umphefumlo wam ekubandezelekeni konke;', 'XHO22'),
(15, 1, 30, 'njengoko ndakufungela uYehova uThixo kaSirayeli, ndathi, uSolomon unyana wakho uya kuba ngukumkani emveni kwam, kuhlale yena esikhundleni sam etroneni yam; ngokuqinisekileyo ndiya kwenjenjalo namhlanje.', 'XHO22'),
(15, 1, 31, 'Wathoba uBhatshebha ngobuso, wazukisa ukumkani, wathi, Makadle ubomi ukumkani wam, ukumkani uDavide, kude kuse ephakadeni.', 'XHO22'),
(15, 1, 32, 'Wathi ukumkani uDavide, Bizani kum uTsadoki umbingeleli, noNatan umprofeti, noBhenaya unyana kaYehoyada. Bafika ke kukumkani.', 'XHO22'),
(15, 1, 33, 'Wathi ukumkani kubo, Bathabatheni abakhonzi benkosi yenu, ndimkhwelele uSolomon, unyana wam, undlebende wam, ndihle ndiye naye eGihon.', 'XHO22'),
(15, 1, 34, 'uTsadoki umbingeleli noNatan umprofeti bamthambisa khona apho ukuba abe ngukumkani wakwaSirayeli; Vuthelani isigodlo nithi, Makadle ubomi ukumkani uSolomon!', 'XHO22'),
(15, 1, 35, 'Mlandeleni ngoku, ukuze ahlale etroneni yam; kuba uya kuba ngukumkani esikhundleni sam, yaye ndiya kumenza umlawuli kaSirayeli noYuda.', 'XHO22'),
(15, 1, 36, 'Waphendula uBhenaya unyana kaYehoyada, wathi kukumkani, Amen, utsho uYehova, uThixo kakumkani, nkosi yam;', 'XHO22'),
(15, 1, 37, 'Njengoko uYehova wayenokumkani wam ukumkani, makabe noSolomon, aze ayiphakamise itrone yakhe ngaphezu kwetrone kaKumkani wam ukumkani uDavide.', 'XHO22'),
(15, 1, 38, 'Wehla ke umbingeleli uTsadoki, noNatan umprofeti, noBhenaya unyana kaYehoyada, namaKrete, nezigidimi, bamkhwelisa uSolomon e-esileni likakumkani uDavide, bamzisa eGihon.', 'XHO22'),
(15, 1, 39, 'uTsadoki umbingeleli wathabatha isigodlo seoli ententeni, wamthambisa uSolomon. Bavuthela isigodlo; bathi bonke abantu, Makasindiswe ukumkani uSolomon.', 'XHO22'),
(15, 1, 40, 'Bamlandela ke bonke abantu, baza abantu babetha iimbande, bevuya ngovuyo olukhulu; lasuka lakrazuka ihlabathi sisandi sabo.', 'XHO22'),
(15, 1, 41, 'Wakuva oko uAdoniya nabamenyiweyo bonke ababenaye, bakugqiba ukudla. Wasiva uYowabhi isandi sesigodlo, wathi, Siyintoni na isandi sesigodlo esiphakathi komzi?', 'XHO22'),
(15, 1, 42, 'Uthe esathetha, kwafika uYonatan unyana ka-Abhiyatare umbingeleli; Wathi uAdoniya kuye, Ngena; ngokuba uyindoda enobukroti, uzisa iindaba ezilungileyo.', 'XHO22'),
(15, 1, 43, 'Waphendula uYonatan, wathi kuAdoniya, Ewe, ukumkani uDavide inkosi yethu umenze ukumkani uSolomon.', 'XHO22'),
(15, 1, 44, 'Ngoko ukumkani wathumela umbingeleli uTsadoki noNatan umprofeti noBhenaya unyana kaYehoyada namaKrete nePeleti kunye naye, baza bamkhwelisa imeyile kakumkani.', 'XHO22'),
(15, 1, 45, 'uTsadoki umbingeleli noNatan umprofeti bamthambisa uGihon ukuba abe ngukumkani; Yingxolo oyivileyo leyo.', 'XHO22'),
(15, 1, 46, 'Kananjalo uSolomon uhleli etroneni yobukumkani;', 'XHO22'),
(15, 1, 47, 'Ngaphezu koko, abakhonzi bakakumkani beza beza kumbonga ukumkani wethu uDavide, besithi, ‘Ngamana uThixo angalenza libe lihle igama likaSolomon kunegama lakho, ayiphakamise itrone yakhe etroneni yakho ngakumbi. Waqubuda ukumkani esililini.', 'XHO22'),
(15, 1, 48, 'Wathi ukumkani, Makabongwe uYehova, uThixo kaSirayeli, endinike namhla indoda yokuba ndihlale etroneni yam, ngokwam mna ndibona ngamehlo.', 'XHO22'),
(15, 1, 49, 'Boyika bonke abamenyiweyo ababenoAdoniya, besuka bema, bahamba yangulowo ngendlela yakhe.', 'XHO22'),
(15, 1, 50, 'uAdoniya wamoyika uSolomon, waphakama, wabamba iimpondo zesibingelelo.', 'XHO22'),
(15, 1, 51, 'Waxelelwa ke uSolomon, kwathiwa, Nanko uAdoniya esoyika ukumkani uSolomon, ethabathe iimpondo zesibingelelo, esithi, Undifungele namhlanje ukumkani uSolomon, ukuba akayi kumbulala ngekrele umkhonzi wakhe umkhonto.', 'XHO22'),
(15, 1, 52, 'Wathi uSolomon, Ukuba uthe wazibonakalisa ukuba ulungile, akuyi kuwa nalunye unwele lwakhe emhlabeni; ke ukuba kuthe kwafunyanwa ubugwenxa kuye, uya kufa.', 'XHO22'),
(15, 1, 53, 'Wathumela ukumkani uSolomon, bamhlisa esibingelelweni. Waya, waqubuda kukumkani uSolomon, wathi uSolomon kuye, Hamba uye endlwini yakho.', 'XHO22'),
(15, 2, 1, 'Isondele imihla yokufa kukaDavide; wamwisela umthetho uSolomon unyana wakhe, wathi,', 'XHO22'),
(15, 2, 2, 'Ndihamba ngendlela yehlabathi lonke; wandule uzinike amandla, ube yindoda;', 'XHO22'),
(15, 2, 3, 'Wugcine umthetho kaYehova uThixo wakho, uhambe ngeendlela zakhe, uyigcine imiyalelo yakhe, nemithetho yakhe, namasiko akhe, nezilumkiso zakhe, njengoko kubhaliweyo emyalelweni kaMoses, ukuze ube nempumelelo entweni yonke oyenzayo uyenza; naphi na apho uya khona;', 'XHO22'),
(15, 2, 4, 'ukuze uYehova aligcine ilizwi lakhe awalithethayo ngam, xa wathi: “Ukuba oonyana bakho banikela ingqalelo kwindlela yabo, baze bahambe enyanisweni ngentliziyo yabo yonke nangomphefumlo wabo uphela, utsho ukuthi: etroneni yakwaSirayeli.', 'XHO22'),
(15, 2, 5, 'Kananjalo uyakwazi awakwenzayo kum uYowabhi unyana kaTseruya, awakwenzayo kubathetheli bobabini bemikhosi yakwaSirayeli, uAbhinere unyana kaNere, noAmasa unyana kaYetere, umthetheli womkhosi wamaSirayeli ukumkani wakwaSirayeli walixhela enoxolo igazi lemfazwe, walikhulula igazi lemfazwe embhinqo wakhe, nasesinqeni sakhe, nasezimbadada ezinyaweni zakhe.', 'XHO22'),
(15, 2, 6, 'Yenza ngokobulumko bakho, ungehli ubushushu bakhe iye kwelabafileyo enoxolo.', 'XHO22'),
(15, 2, 7, 'Ke koonyana bakaBharzilayi waseGiliyadi, uze ube nenceba kubo, babe phakathi kwabadla etafileni yakho; kuba beze ngale ndlela kum xa ndandibaleka ngenxa ka-Abhisalom umntakwenu.', 'XHO22'),
(15, 2, 8, 'Uyabona, unaye uShimehi unyana kaGera, umBhenjamin, waseBhahurim, yena wanditshabhisa ngotshabhiso olukrakra, ekuyeni kwam eMahanayim; ke yena wehla weza kundikhawulela eYordan, ndamfungela uYehova, ndathi, Andiyi kukubulala ngekrele.', 'XHO22'),
(15, 2, 9, 'Ke kaloku musa ukumgqala emsulwa, ekubeni uyindoda elumkileyo yaye ukwazi oko ufanele ukwenze kuye; Intloko yakhe ethotyiweyo ikuwisele kwelabafileyo unegazi.', 'XHO22'),
(15, 2, 10, 'Walala ke uDavide kooyise, wangcwatyelwa emzini kaDavide;', 'XHO22'),
(15, 2, 11, 'Imihla uDavide abengukumkani kwaSirayeli ngayo yaba yiminyaka emashumi mane; waba neminyaka esixhenxe engukumkani eHebron, eseYerusalem iminyaka emashumi mathathu anamithathu.', 'XHO22'),
(15, 2, 12, 'Wahlala ke uSolomon etroneni kaDavide uyise; bazimisela kakhulu ubukumkani bakhe.', 'XHO22'),
(15, 2, 13, 'Waza uAdoniya unyana kaHagiti weza kuBhatshebha unina kaSolomon. Wathi, Nize ngoxolo na? Wathi, Ndinoxolo.', 'XHO22'),
(15, 2, 14, 'Waza wathi: “Kukho into endifuna ukukuxelela yona. Wathi yena, Hambani niye!', 'XHO22'),
(15, 2, 15, 'Wathi: “Uyazi wena ukuba bebubobam ubukumkani, abe ebekhangele kum ukuba ndilawule onke amaSirayeli; kodwa ubukumkani baguquka baba bobomntakwethu, ngenxa yokuba baba bobakhe buvela kuYehova.', 'XHO22'),
(15, 2, 16, 'Kungokunje ndicela nto-nye kuni, ze ningandichasi; Wathi kuye, Yitsho.', 'XHO22'),
(15, 2, 17, 'Wathi, Yitsho kukumkani uSolomon, ngokuba engayi kukulala; ndinike uAbhishagi waseShunem, abe ngumkam.', 'XHO22'),
(15, 2, 18, 'Wathi ke uBhatshebha: “Kulungile! Ndiya kuthetha kukumkani esikhundleni sakho.', 'XHO22'),
(15, 2, 19, 'Waya ke uBhatshebha kukumkani uSolomon, ukuba athethe naye ngoAdoniya. Waza ukumkani wammisa, waqubuda kuye, wahlala etroneni yakhe, wamisa isihlalo sikanina wokumkani; wahlala ngasekunene kwakhe.', 'XHO22'),
(15, 2, 20, 'Wathi, Ndicela nto encinane kuwe; Nceda, ungandixeleli. Wathi uYehova kuye, Cela uma, kuba andiyi kukukhanyela.', 'XHO22'),
(15, 2, 21, 'Wathi, uAbhishagi waseShunem makanikwe uAdoniya, umkhuluwa wakho, abe ngumkakhe.', 'XHO22'),
(15, 2, 22, 'uKumkani uSolomon waphendula wathi kunina: “Bekutheni na ukuze ucele uAdoniya uAbhishage umShunem? bucele kuye ubukumkani; ngokuba engumzalwana wam omkhulu; yena noAbhiyatare umbingeleli, noYowabhi unyana kaTseruya.', 'XHO22'),
(15, 2, 23, 'Ukumkani uSolomon wafunga uYehova, wathi, uThixo uya kwenza le nto kum, ayenze, ukuba uAdoniya akalithethanga elo lizwi elahla umphefumlo wakhe.', 'XHO22'),
(15, 2, 24, 'Ke kaloku, ehleli nje uYehova, owandizisayo, wandihlalisa etroneni kaDavide ubawo, owandenzela indlu, njengoko watshoyo, namhlanje uya kubulawa uAdoniya.', 'XHO22'),
(15, 2, 25, 'Ukumkani uSolomon wathuma uBhenaya unyana kaYehoyada; wamwela phezu kwakhe, wafa.', 'XHO22'),
(15, 2, 26, 'Wathi umbingeleli uAbhiyatare kukumkani, Hamba uye eAnatoti ezweni lakowenu; kuba ufanelwe kukufa, kodwa andiyi kukubulala ngeli xesha, ngenxa yokuba wayithwala ityeya kaYehova phambi kukaDavide ubawo, nangokucinezelwa kwakho kuyo yonk’ into awayecinezelwe ngayo ubawo.', 'XHO22'),
(15, 2, 27, 'Ngoko uSolomon wamndulula uAbhiyatare ngenxa yokuba wayengenguye umbingeleli kaYehova; ukuze alizalise ilizwi likaYehova, awalithethayo ngendlu kaEli Shilo.', 'XHO22'),
(15, 2, 28, 'Ezi ndaba zafika kuYowabhi, ngenxa yokuba uYowabhi wayemshiyile uAdoniya, nangona wayengamlandelanga uAbhisalom. Wasabela uYowabhi ententeni kaYehova, wabamba iimpondo zesibingelelo.', 'XHO22'),
(15, 2, 29, 'Waxelelwa ukumkani uSolomon, ukuba uYowabhi usabele ententeni kaYehova; nanko ngasesibingelelweni. uSolomon wathuma uBhenaya, unyana kaYehoyada, wathi, Mbulale.', 'XHO22'),
(15, 2, 30, 'Waya uBhenaya ententeni kaYehova, wathi kuye, Uthe ukumkani, Phuma. Waphendula ke yena wathi, Hayi; kodwa ndiya kufela apha. Waphinda uBhenaya wamxelela ukumkani, wathi, Utshilo uYowabhi, wenjenje ukuthetha nam.', 'XHO22'),
(15, 2, 31, 'Wathi ukumkani kuye, Yenza into ayithethileyo, umbulale, umngcwabe; ukuze ulisuse kum nakwindlu kabawo igazi elimsulwa, aliphalazile uYowabhi.', 'XHO22'),
(15, 2, 32, 'uYehova uya kulibuyisela entlokweni yakhe igazi lakhe, abawa amadoda amabini angamalungisa, elungile kunaye, awabulala ngentshuntshe, engazi uyise kaDavide, onguAbhinere, unyana kaNere; umthetheli wemikhosi yakwaSirayeli, noAmasa unyana kaYetere, umthetheli womkhosi wakwaYuda.', 'XHO22'),
(15, 2, 33, 'Ngako oko amagazi awo obuyela entlokweni kaYowabhi, nasentlokweni yoonyana bakhe kude kuse ephakadeni; kodwa kuDavide nakwinzala yakhe nakwindlu yakhe nakwitrone yakhe kuya kubakho uxolo oluvela kuYehova ukusa kwixesha elingenammiselo.', 'XHO22'),
(15, 2, 34, 'Wenyuka ke uBhenaya unyana kaYehoyada, wamgwaza, wambulala; wangcwatyelwa endlwini yakhe entlango.', 'XHO22'),
(15, 2, 35, 'Ukumkani wambeka uBhenaya, unyana kaYehoyada, esikhundleni sakhe ukuba aphathe umkhosi; uTsadoki umbingeleli wambeka ukumkani kwigumbi lokudlela lika-Abhiyatare.', 'XHO22'),
(15, 2, 36, 'uYehova wambiza uShimehi, wathi kuye, Zakhele indlu eYerusalem, uhlale khona, ungashiyi khona.', 'XHO22'),
(15, 2, 37, 'Kuba mhla waphumayo, wawuwela umlanjana oyiKidron, uya kwazi ngokuqinisekileyo ukuba uya kufa, inene; igazi lakho liya kuba phezu kwentloko yakho.', 'XHO22'),
(15, 2, 38, 'Wathi uShimehi kukumkani, Lilungile eli lizwi; njengoko ithethileyo ukumkani, inkosi yam, umkhonzi wakho uya kwenza. uShimehi wahlala imihla emininzi eYerusalem.', 'XHO22'),
(15, 2, 39, 'Kwakuba kudlule iminyaka emithathu, babaleka abakhonzi ababini bakaShimehi, baya eAgis unyana kaMaga, ukumkani waseGati. Waxelelwa uShimehi, kwathiwa, Yabona, abakhonzi bakho baseGati;', 'XHO22'),
(15, 2, 40, 'Wesuka uShimehi, wayibopha iesile lakhe, waya eGati eAgishe, esiya kufuna abakhonzi bakhe; wahamba uShimehi, wabathabatha abakhonzi bakhe eGati.', 'XHO22'),
(15, 2, 41, 'Waxelelwa uSolomon ukuba uShimehi wemka eYerusalem, waya eGati, wabuya.', 'XHO22'),
(15, 2, 42, 'Ukumkani wathumela kuShimehi, wathi kuye, Mna andikufungisanga uYehova; ndakugweba mna, ndathi, Mhla waphuma waya ezindaweni zonke, uya kufa na? Wathi ke wena kum, Ilizwi endilivileyo lilungile;', 'XHO22'),
(15, 2, 43, 'Kutheni na ke ukuba ungasizalisekisi isifungo sikaYehova nomyalelo endakuyalela wona?', 'XHO22'),
(15, 2, 44, 'Wathi ukumkani kuShimehi, Uyabazi wena bonke ububi obaziwa yintliziyo yakho, owabenzayo kuDavide ubawo; uYehova uya kubuhlisela ububi bakho entlokweni yakho;', 'XHO22'),
(15, 2, 45, 'Yaye uKumkani uSolomon uya kusikelelwa, yaye itrone kaDavide iya kumiselwa ngokuqinileyo phambi koYehova ukusa kwixesha elingenammiselo.', 'XHO22'),
(15, 2, 46, 'Wawisa umthetho uBhenaya unyana kaYehoyada; eyaphuma yawa phezu kwakhe, wafa. Ubukumkani baqiniselwa esandleni sikaSolomon.', 'XHO22'),
(15, 3, 1, 'uSolomon wakha ubuhlobo noFaro ukumkani waseYiputa, wazeka intombi kaFaro, wayizisa emzini kaDavide, wada wayigqiba ukuyakha indlu yakhe, nendlu kaYehova, nodonga lwaseYerusalem, lwa lonke.', 'XHO22'),
(15, 3, 2, 'Kodwa abantu babebingelela ezigangeni; ngokuba lalingekakhelwa ndlu igama likaYehova kwada kwayiloo mini.', 'XHO22'),
(15, 3, 3, 'uSolomon wamthanda uYehova ngokuhamba emimiselweni kaDavide uyise; wayebingelela ezigangeni zodwa, wenyuka umsi.', 'XHO22'),
(15, 3, 4, 'Ukumkani ubesiya eGibheyon, ukuba abingelele khona; ngenxa yokuba yayiyindawo ephakamileyo kakhulu: uSolomon wanikela iwaka leminikelo etshiswayo kweso sibingelelo.', 'XHO22'),
(15, 3, 5, 'Xa wayeseGibheyon, uYehova wabonakala kuSolomon ebusuku ephupheni, wathi uThixo: “Cela into, ndikuphe;', 'XHO22'),
(15, 3, 6, 'Wathi uSolomon, Umenzele inceba enkulu umkhonzi wakho uDavide, ubawo, njengoko abehamba phambi kwakho enyanisweni, nangobulungisa, nangentliziyo elungileyo; wambambela olu babalo bukhulu, wada wamnika unyana ukuba ahlale etroneni yakhe, njengoko kunjalo namhla.', 'XHO22'),
(15, 3, 7, 'Kaloku, Yehova Thixo wam, wena umenze ukumkani umkhonzi wakho esikhundleni sikaDavide ubawo; ndisengumntwana; Andazi ndiphume okanye ndingene njani.', 'XHO22'),
(15, 3, 8, 'Umkhonzi wakho lo uphakathi kohlanga lwakho olunyuliweyo, uhlanga olukhulu, olungenakubalwa, olungenakubalwa ngenxa yobuninzi.', 'XHO22'),
(15, 3, 9, 'Mnike umkhonzi wakho intliziyo evayo, yokubalawula abantu bakho, ahlule phakathi kokulungileyo nokubi; ngokuba ngubani na ongalugwebayo olu hlanga lwakho lukhulu?', 'XHO22'),
(15, 3, 10, 'Lalunga elo lizwi emehlweni kaYehova, wabuza uSolomon.', 'XHO22'),
(15, 3, 11, 'Wathi ke uThixo kuye, Ekubeni ucele loo nto, akwayicela imihla emininzi; Anibucelanga ubutyebi benu, anicelanga nomphefumlo weentshaba zenu; Ke ucele ukuqonda kwakho ukuba kugwebe;', 'XHO22'),
(15, 3, 12, 'Yabona, ndenze ngokwamazwi akho; niyabona, ndininike intliziyo elumkileyo nenokuqonda; kangangokuba ngaphambi kwakho akuzange kubekho unjengawe, nasemva kwakho kungabikho unjengawe.', 'XHO22'),
(15, 3, 13, 'Ndikunikile oko ungakucelanga, ubutyebi nozuko, ukuze kungabikho unjengawe phakathi kookumkani yonke imihla yakho.', 'XHO22'),
(15, 3, 14, 'Ukuba uthe wahamba ngeendlela zam, wayigcina imithetho yam nemimiselo yam, njengoko wahamba ngako uDavide uyihlo, ndoyolula imihla yakho.', 'XHO22'),
(15, 3, 15, 'Wavuka uSolomon; liphupha. Weza eYerusalem, wema phambi kwetyeya yomnqophiso kaYehova, wenyusa amadini anyukayo nemibingelelo yombulelo, wabenzela isidlo abakhonzi bakhe bonke.', 'XHO22'),
(15, 3, 16, 'Ngelo xesha beza kukumkani amankazana amabini angamahenyukazi, ema phambi kwakhe.', 'XHO22'),
(15, 3, 17, 'Yathi enye inkazana, Camagu, nkosi yam, mna nalo mfazi sihlala ndlwini-nye; ndafika kukho umntwana naye endlini.', 'XHO22'),
(15, 3, 18, 'Wazala ngomhla wesithathu, naye lowo wazala; kwaye sasikunye; kwaye kungekho mntu wasemzini unathi kuloo ndlu, sisodwa thina sobabini kuloo ndlu.', 'XHO22'),
(15, 3, 19, 'Wafa ke umntwana wale nkazana ebusuku; ngokuba wayigubungelayo.', 'XHO22'),
(15, 3, 20, 'Wavuka ezinzulwini zobusuku, wamthabatha unyana wam phambi kwam, elele umkhonzazana wakho, wamlalisa esifubeni sakhe, wambeka esifubeni sam umntwana wakhe ofileyo.', 'XHO22'),
(15, 3, 21, 'Ndavuka kusasa, ukuba ndimanyise umntwana wam, O! kodwa ndathi ndakuyicinga ekuseni ndabona ukuba ayingonyana wam lo ndimzeleyo.', 'XHO22'),
(15, 3, 22, 'Wathi omnye umfazi, Hayi; ngunyana wam lo uhleliyo, unyana wakho ngulowa ufileyo. Wathi yena, Hayi; unyana wakho ngulowa ufileyo, ngunyana wam lo uhleliyo. Batsho phambi kwenkosi.', 'XHO22'),
(15, 3, 23, 'Wathi ukumkani, Kuthiwa, Ngunyana wam lo uhleliyo, unyana wakho ngulowa ufileyo; omnye wathi: Hayi! kodwa unyana wakho ufile, yaye unyana wam ngulo udla ubomi.', 'XHO22'),
(15, 3, 24, 'Wathi ukumkani, Ndithabatheleni ikrele. Balizisa ikrele phambi kokumkani.', 'XHO22'),
(15, 3, 25, 'Wathi ukumkani, Mcandeni kubini lo mntwana uhleliyo, niyinike isiqingatha enye, niyinike isiqingatha;', 'XHO22'),
(15, 3, 26, 'Yathetha le nkazana, ungowayo unyana ohleliyo, kukumkani (ngokuba isisu besikhalela unyana wayo), yathi, Camagu, nkosi yam, senimnike ehleli umntwana; Wathi ke omnye, Mayingabi yeyam, ingabi yeyakho;', 'XHO22'),
(15, 3, 27, 'Waphendula uYehova, wathi, Mnike umntwana ohleliyo; ngumama wam.', 'XHO22'),
(15, 3, 28, 'Asiva onke amaSirayeli isigwebo abesenza ukumkani; amoyika ukumkani, ngokuba ayebona ukuba ubulumko bukaThixo bungaphakathi kwakhe, ukuba agwebe.', 'XHO22'),
(15, 4, 1, 'Ke kaloku ukumkani uSolomon waba ngukumkani kumaSirayeli onke.', 'XHO22'),
(15, 4, 2, 'Ngabo abo abathetheli abenabo; umbingeleli uAzariya, unyana kaTsadoki;', 'XHO22'),
(15, 4, 3, 'ngooElihorefe noAhiya, oonyana bakaSisa, ababhali; uYehoshafati unyana ka-Ahiludi, umphathi wentlanganiso yamatyala;', 'XHO22'),
(15, 4, 4, 'uBhenaya unyana kaJoyada wayeyinjengele yomkhosi, uZadoki noAbhiyatare bengababingeleli;', 'XHO22'),
(15, 4, 5, 'uAsariya, unyana kaNatan, ephethe abalungiseleli;', 'XHO22'),
(15, 4, 6, 'uAhisare wayephethe indlu yakhe, uAdoniram unyana ka-Abheda ephethe irhafu.', 'XHO22'),
(15, 4, 7, 'uSolomon ubenabalungiseleli abalishumi elinababini, kumaSirayeli onke, ababemxhasa ukumkani nendlu yakhe; ngamnye wayezixhasa ngenyanga yakhe enyakeni.', 'XHO22'),
(15, 4, 8, 'Ngawo la amagama abo: ngunyana kaHure, ezintabeni zakwaEfrayim;', 'XHO22'),
(15, 4, 9, 'unyana kaDekare nguMakashe, noShahalebhim, neBhete-shemeshe, noElon-bethanan;', 'XHO22'),
(15, 4, 10, 'unyana kaHesedi nguArubhoti; kuye iSogo nelizwe lonke lakwaHefere;', 'XHO22'),
(15, 4, 11, 'unyana ka-Abhinadabhi, ezweni lonke lakwaDore; nguTafati ke lowo, intombi kaSolomon, umkakhe;', 'XHO22'),
(15, 4, 12, 'nguBhahana unyana ka-Ahiludi; eyakhe yaba yiTahanaki neMegido nayo yonke iBhete-shehan phantsi kweTsartana ephantsi kweYizereli, ukususela eBhete-shehan ukuya eAbheli-mehola ngaphaya kweYokenam;', 'XHO22'),
(15, 4, 13, 'unyana kaGebhere yiRamoti yaseGiliyadi; Yayizizixeko zikaYahire, unyana kaManase, eGiliyadi. Ke ilizwe laseArgobhi eBhashan, bekukho izixeko ezingamashumi amathandathu ezikhulu, ezinodonga nemivalo yobhedu;', 'XHO22'),
(15, 4, 14, 'uAhinadabhi, unyana kaIdo, wazala uMahanayim;', 'XHO22'),
(15, 4, 15, 'nguAhimahatse kwaNafetali; Watshata noBasmathe, intombi kaSolomon, ukuba abe ngumfazi wakhe;', 'XHO22'),
(15, 4, 16, 'nguBhahana unyana kaHushayi kwa-Ashere naseAloti;', 'XHO22'),
(15, 4, 17, 'uYehoshafati unyana kaParuwa, ezweni lakwaIsakare;', 'XHO22'),
(15, 4, 18, 'nguShimehi unyana kaEla, ezweni lakwaBhenjamin;', 'XHO22'),
(15, 4, 19, 'uGebhere unyana kaUri wayesezweni laseGiliyadi, kwilizwe likaSihon ukumkani wama-Amori no-Ogi ukumkani waseBhashan; yaye yayinguye yedwa umlawuli kwelo lizwe.', 'XHO22'),
(15, 4, 20, 'Ayemaninzi amaYuda namaSirayeli, abemaninzi entlabathini naselunxwemeni lolwandle, esidla, esela, ezonwabisa.', 'XHO22'),
(15, 4, 21, 'uSolomon ubelawula ezikumkanini zonke, ethabathela kuwo uMlambo, naselizweni lamaFilisti, wesa nasemdeni weYiputa; bazisa izipho, bamkhonza uSolomon yonke imihla yobomi bakhe.', 'XHO22'),
(15, 4, 22, 'Ukutya kukaSolomon imini enye kwaba ziikhilogram ezimashumi mathathu zomgubo ocoliweyo, namashumi amathandathu eekhilogram zomgubo;', 'XHO22'),
(15, 4, 23, 'neshumi leenkomo ezityetyisiweyo, neenkomo zasemadlelweni ezimashumi mabini, nekhulu leenkomo ezintsha ezityetyisiweyo, kungabalwa amaxhama, namaxhama, namaxhama atyetyisiweyo.', 'XHO22'),
(15, 4, 24, 'Ubephethe ilizwe lonke elinganeno koMlambo, ethabathela eTifsa wesa eAza, nookumkani bonke abaphesheya koMlambo; Wayenoxolo kuzo zonke iindawo.', 'XHO22'),
(15, 4, 25, 'AmaYuda namaSirayeli ayehlala ekhuselekile ngamnye phantsi komdiliya wakhe naphantsi komkhiwane wakhe, ukususela kwaDan kuse eBher-shebha yonke imihla kaSolomon.', 'XHO22'),
(15, 4, 26, 'uSolomon ubenamashumi amane amawaka eenqwelo zokulwa, namawaka alishumi elinamabini amahashe akhwelwayo.', 'XHO22'),
(15, 4, 27, 'Abo bathetheli bamnika ukudla ukumkani uSolomon, nabo bonke abasondela etafileni yokumkani uSolomon, elowo exhasa ngenyanga yakhe; babengaswele nto.', 'XHO22'),
(15, 4, 28, 'Kananjalo bazisa irhasi nengca yamahashe neekati, elowo ngokwesiko lakhe;', 'XHO22'),
(15, 4, 29, 'uThixo wamnika ke uSolomon ubulumko obuninzi nengqondo, nentliziyo enkulu, njengentlabathi eselunxwemeni lolwandle.', 'XHO22'),
(15, 4, 30, 'Ubulumko bukaSolomon bebulunge ngaphezu kobulumko boonyana bonke basempumalanga, naphezu kobulumko bonke bamaYiputa.', 'XHO22'),
(15, 4, 31, 'Ngokuba ubelumkile ngaphezu kwabantu bonke; noEtan waseMosera, noHeman, noKakoli, noDarda, oonyana bakaMahole; udumo lwakhe lwaba sezizweni zonke ezimjikelezileyo.', 'XHO22'),
(15, 4, 32, 'Wathetha amawaka amathathu emizekeliso, zathi neengoma zakhe zaliwaka elinantlanu.', 'XHO22'),
(15, 4, 33, 'Wayethetha ngemithi, eqalela kumsedare waseLebhanon, wesa kwihisope ephuma eludongeni; Wathetha ngezilwanyana, neentaka, nezinambuzane, neentlanzi.', 'XHO22'),
(15, 4, 34, 'Babephuma kuzo zonke iintlanga besiza kuva ubulumko bukaSolomon, kubo bonke ookumkani bomhlaba ababephulaphule ubulumko bakhe.', 'XHO22'),
(15, 5, 1, 'Wathuma uHiram, ukumkani waseTire, abakhonzi bakhe kuSolomon; kuba wayevile ukuba bamthambisile ukuba abe ngukumkani endlwini kayise, ekubeni uHiram wayesoloko emthanda uDavide.', 'XHO22'),
(15, 5, 2, 'uSolomon wathumela umyalezo kuHiram, esithi:', 'XHO22'),
(15, 5, 3, 'Uyazi wena, ukuba uDavide ubawo ubengenako ukulakhela indlu igama likaYehova uThixo wakhe, ngenxa yeemfazwe ezimfikeleyo, wada uYehova wambeka phantsi kweenyawo zakhe.', 'XHO22'),
(15, 5, 4, 'Kaloku ke undiphumzile uYehova, uThixo wam, ngeenxa zonke, akukho lutshaba, akukho ngozi.', 'XHO22'),
(15, 5, 5, 'Yabona, ndiceba ukulakhela indlu igama likaYehova uThixo wam, njengoko wakuthethayo uYehova ubawo uDavide, wathi, Unyana wakho, endiya kumbeka etroneni yakho esikhundleni sakho, nguye oya kundakhela indlu igama.', 'XHO22'),
(15, 5, 6, 'Ngoko ke wisa umthetho, bandimisele imisedare eLebhanon; abakhonzi bam babe nabakhonzi bakho, ndikunike umvuzo ngokwento yonke othe wabawisela umthetho ngayo; ngokuba niyazi nina, ukuba akukho namnye kuthi ogawula imithi njengamaTsidon.', 'XHO22'),
(15, 5, 7, 'Akuweva uHiram amazwi kaSolomon, wavuya kunene, wathi, Makabongwe uYehova namhla, omnike uDavide unyana olumkileyo, wokuba alulawule olu hlanga lukhulu.', 'XHO22'),
(15, 5, 8, 'uHiram wamthuma ke uSolomon, wathi, Ndizibonile izinto ondithume zona, ndikwenze konke okufunayo, ngemisedare nemisipres.', 'XHO22'),
(15, 5, 9, 'Abakhonzi bam boyihlisela elwandle lwaseLebhanon; mna ndiya kumse elwandle, adada, kuloo ndawo uya kundikhubekisa kuyo, ndimse elwandle, umamkele; wondiphilisa ke umnqweno wokunika indlu yam ukudla.', 'XHO22'),
(15, 5, 10, 'uHiram wamnika ke uSolomon imisedare nemisipres ngangokuthanda kwakhe konke.', 'XHO22'),
(15, 5, 11, 'uSolomon wamnika uHiram amashumi amabini amawaka engqolowa ukuba idle indlu yakhe; namashumi amabini eekore zeoli engqushiweyo; uSolomon wayinika uHiram njengaminyaka le.', 'XHO22'),
(15, 5, 12, 'uYehova wamnika ubulumko uSolomon, njengoko wayekuthethile kuye; lwaba luxolo phakathi koHiram noSolomon; benza umnqophiso bobabini.', 'XHO22'),
(15, 5, 13, 'Ukumkani uSolomon waqhubeka erhafisa oonyana bakaSirayeli bonke; unikelo ngamashumi omathathu amawaka amadoda.', 'XHO22'),
(15, 5, 14, 'Wabathuma eLebhanon, ishumi lamawaka ngenyanga, ngokwamaqela; inyanga enye babe seLebhanon, iinyanga ezimbini babe sekhaya; yaye uAdoniram wayeyinjengele yabo bakhethiweyo.', 'XHO22'),
(15, 5, 15, 'uSolomon wayenamashumi amathandathu amawaka abachebi nabachebi beenwele ezintabeni;', 'XHO22'),
(15, 5, 16, 'ngaphandle kwabathetheli babalindi bakaSolomon, ababephethe umsebenzi, babengamawaka amathathu anamakhulu amathathu, ababephethe abantu emsebenzini.', 'XHO22'),
(15, 5, 17, 'Ukumkani wawisa umthetho, bezisa amatye amakhulu, amatye anqabileyo, amatye aqingqiweyo okuseka indlu;', 'XHO22'),
(15, 5, 18, 'Abakhi bakaSolomon nabakhi bakaHiram babadiliza, nabakhi bamatye; balungisa imithi namatye okwakha indlu.', 'XHO22'),
(15, 6, 1, 'Kwathi ngomnyaka wamakhulu amane anamanci asibhozo anababini koonyana bakaSirayeli, baphumayo ezweni laseYiputa, ngomnyaka wesine kaSolomon engukumkani kwaSirayeli, ngenyanga enguSifi, oko kukuthi, inyanga yesibini ukwakha indlu kaYehova.', 'XHO22'),
(15, 6, 2, 'Indlu awayakhela yona uYehova ukumkani uSolomon, ibiziikubhite ezimashumi mathandathu ubude bayo, iziikubhite ezimashumi mabini ububanzi bayo, nokuphakama kwayo kuziikubhite ezimashumi mathathu.', 'XHO22'),
(15, 6, 3, 'Ivaranda, ebiphambi kwebhotwe lendlu, ibiziikubhite ezimashumi mabini, ingangobubanzi bendlu; ububanzi bebuziikubhite ezilishumi phambi kwendlu.', 'XHO22'),
(15, 6, 4, 'Benza iifestile ezibukhali zendlu;', 'XHO22'),
(15, 6, 5, 'Ecaleni kodonga lwendlu wakha amagumbi okuhlangana ngeenxa zonke; wenza namagumbi kuzo zonke iindawo.', 'XHO22'),
(15, 6, 6, 'Igumbi eliphantsi beliziikubhite ezintlanu ububanzi balo; eliphakathi beliziikubhite ezintandathu ububanzi balo; elesithathu laba ziikubhite ezisixhenxe ububanzi balo; ngokuba, ngaphandle kodonga lwendlu, wenza iindonga ezicekethekileyo ezindaweni zonke, ukuze imiqadi ingabotshelelwa ezindongeni zendlu. Indlu.', 'XHO22'),
(15, 6, 7, 'Indlu ekwakhiweni kwayo yakhiwa ngamatye alungisiweyo, ingekaziswa kuyo, ukuze kungavakali sando, nazembe, nasiphi na isixhobo esenziwe ngesinyithi endlwini ekwakhiweni kwayo.', 'XHO22'),
(15, 6, 8, 'Umnyango wegumbi eliphakathi wawukwicala lasekunene lendlu, yaye benyuka ngezinyuko ezijikajikayo ukuya kwigumbi eliphakathi, besuka kweliphakathi besiya kwelesithathu.', 'XHO22'),
(15, 6, 9, 'Wakha ke indlu, wayigqiba; wayakha indlu ngemiqadi neeplanga zemisedare.', 'XHO22'),
(15, 6, 10, 'Emva koko wakha amagumbi kuyo yonke indlu, ukuphakama kweekubhite ezintlanu, yaye phezu kwendlu kwakukho amaplanga omsedare.', 'XHO22'),
(15, 6, 11, 'Lafika ilizwi likaYehova kuSolomon, lisithi:', 'XHO22'),
(15, 6, 12, 'Ngokusingisele kule ndlu uyakhayo, ukuba nihambe ngokwemiyalelo yam nize niyenze izigwebo zam nize niyigcine yonke imiyalelo yam nihambe ngayo; ke ndiya kulimisa kuwe ilizwi lam, endalithetha kuyihlo uDavide.', 'XHO22'),
(15, 6, 13, 'Ndiya kuhlala phakathi koonyana bakaSirayeli, ndingabanikeleli umva abantu bam amaSirayeli;', 'XHO22'),
(15, 6, 14, 'Wayakha ke uSolomon indlu, wayigqiba.', 'XHO22'),
(15, 6, 15, 'Wazakha iindonga zendlu ngaphakathi ngamaplanga omsedare, nomgangatho wendlu kunye neendonga zophahla; wayityabeka ngomthi ngaphakathi, wayityabeka ngemisipres phantsi.', 'XHO22'),
(15, 6, 16, 'Wakha emacaleni endlu iikubhite ezimashumi mabini, emacaleni endlu, ethabathela emgangathweni wesa eludongeni, ngeeplanga zemisedare; kananjalo wayakha ngaphakathi kwendlu engaphakathi, eyingcwele kangcwele.', 'XHO22'),
(15, 6, 17, 'Indlu eyitempile, ebiphambi kwayo ibiziikubhite ezimashumi mane ubude bayo.', 'XHO22'),
(15, 6, 18, 'Yayimisedare kule ndlu ngaphakathi, ihonjiswe ngeentyatyambo neentyantyambo; yayingumsedare yonke; akwabonwa litye.', 'XHO22'),
(15, 6, 19, 'Walungisa igumbi lokuhlanganisana endlwini ngaphakathi endlwini ukuze ibekwe khona ityeya yomnqophiso kaYehova.', 'XHO22'),
(15, 6, 20, 'Umnyango ophambili ubuziikubhite ezimashumi mabini, ubude bawo buziikubhite ezimashumi mabini, nokuphakama kwawo kwakuziikubhite ezimashumi mabini; Wasaleka ngegolide ecocekileyo; nesibingelelo eso senziwe ngomsedare.', 'XHO22'),
(15, 6, 21, 'uSolomon wayigubungela indlu ngaphakathi ngegolide ecocekileyo; Wayaleka ngegolide.', 'XHO22'),
(15, 6, 22, 'Wayaleka ngegolide yonke ke indlu, yada yagqitywa yonke indlu; Wasaleka ngegolide yonke isibingelelo endlwini engaphakathi.', 'XHO22'),
(15, 6, 23, 'Wenza iikerubhi zambini ngomthi womnquma ngaphakathi, zaziikubhite ezilishumi ukuphakama kwazo.', 'XHO22'),
(15, 6, 24, 'Elinye iphiko lekerubhi beliziikubhite ezintlanu, nelinye iphiko lekerubhi beliziikubhite ezintlanu; ziziikubhite ezilishumi, zithabathela encamini yelinye iphiko, zise encamini yelinye iphiko.', 'XHO22'),
(15, 6, 25, 'Enye ikerubhi ibiziikubhite ezilishumi; Ubukhulu bufana nobukhulu bezithunywa zezulu zombini.', 'XHO22'),
(15, 6, 26, 'Ukuphakama kwenye ikherubhi bekuziikubhite ezilishumi, yaye enye ikerubhi bekunjalo.', 'XHO22'),
(15, 6, 27, 'Wazibeka iikerubhi endlwini engaphakathi, oluka amaphiko eekerubhi, lada iphiko lenye lafika kolunye udonga, lathi iphiko lenye ikerubhi lafikelela kolunye udonga; aza amaphiko azo afikelelana esazulwini sendlu.', 'XHO22'),
(15, 6, 28, 'Wazaleka iikerubhi ngegolide;', 'XHO22'),
(15, 6, 29, 'Wathi zonke iindonga zendlu ngeenxa zonke wazikrola imifanekiso eqingqiweyo yeekerubhi, namasundu, neentyantyambo ezidubulayo, ngaphakathi nangaphandle.', 'XHO22'),
(15, 6, 30, 'Umgangatho wendlu wawutyabeka ngegolide ngaphakathi nangaphandle.', 'XHO22'),
(15, 6, 31, 'Umnyango wegumbi elingaphakathi wawenzela iingcango ngeminquma;', 'XHO22'),
(15, 6, 32, 'Iingcango zombini bezizezomnquma; Wakrola kuyo imifanekiso yeekerubhi, namasundu, neentyantyambo, wayityabeka ngegolide, wabeka igolide phezu kweekerubhi namasundu.', 'XHO22'),
(15, 6, 33, 'Wenza isahlulo sesine sodonga lomnyango womnyango wendlu ngemigubasi ngeminquma.', 'XHO22'),
(15, 6, 34, 'Iingcango zombini bezizezemisipres;', 'XHO22'),
(15, 6, 35, 'Wakrola iikherubhi nemithi yesundu neentyantyambo ezintshula kuso waza wasigubungela ngegolide efakwe kumfanekiso oqingqiweyo.', 'XHO22'),
(15, 6, 36, 'Wakha intendelezo engaphakathi ngeengcamba ezintathu zamatye aqingqiweyo, nangoluhlu lwemiqadi yemisedare.', 'XHO22'),
(15, 6, 37, 'Ngomnyaka wesine kwasekwa indlu kaYehova, ngenyanga enguSife;', 'XHO22'),
(15, 6, 38, 'ngomnyaka weshumi elinamnye, ngenyanga enguPoho, eyinyanga yesibhozo leyo, yagqitywa loo ndlu ezintweni zayo zonke, nasezintweni zayo zonke. Wachitha iminyaka esixhenxe eyakha.', 'XHO22'),
(15, 7, 1, 'uSolomon wayakha indlu yakhe iminyaka elishumi elinamithathu, wayigqiba indlu yakhe yonke.', 'XHO22'),
(15, 7, 2, 'Wakha indlu ehlathini laseLebhanon; Yaba ziikubhite ezilikhulu ubude, zaba ziikubhite ezimashumi mahlanu ububanzi, neekubhite ezimashumi mathathu ukuphakama, phezu kwemikrozo emine yeentsika zomsedare, ezinemiqadi echweliweyo yemisedare phezu kweentsika ezo.', 'XHO22'),
(15, 7, 3, 'Kwaye yaxhonywa ngomsedare phezu kwemiqadi eyayiphezu kweentsika ezingamashumi amane anantlanu, ishumi elinesihlanu lukroze ngalunye.', 'XHO22'),
(15, 7, 4, 'Kwakukho iifestile kwimiqolo emithathu, yaye ukukhanya kwakujongene nokukhanya ngokwamanqanaba amathathu.', 'XHO22'),
(15, 7, 5, 'Zonke iingcango nemigubasi yayinamacala amane alinganayo, neefestile;', 'XHO22'),
(15, 7, 6, 'Wenza iivaranda ngeentsika; baba ziikubhite ezimashumi mahlanu ubude, baziikubhite ezimashumi mathathu ububanzi bayo; Iveranda yayiphambi kwazo, yaye ezinye iintsika nemiqadi yayiphambi kwazo.', 'XHO22'),
(15, 7, 7, 'Wenza intendelezo yetrone, apho abegweba khona, nevaranda yamatyala; yaye yayifulelwe ngomsedare ukusuka kwelinye icala lomgangatho ukuya kwelinye.', 'XHO22'),
(15, 7, 8, 'Indlu yakhe awayehlala kuyo yayinenye intendelezo phakathi ngasevarandeni, yasetyenzwa kwangokunjalo. uSolomon wayakhela indlu enjalo intombi kaFaro, awayizekayo ukuba ibe ngumkakhe.', 'XHO22'),
(15, 7, 9, 'Zonke ezo zinto bezizezamatye anqabileyo, ngokomlinganiso wamatye aqingqiweyo, asarhiweyo ngeesarha, ngaphakathi nangaphandle, ethabathela esisekweni esa eluphahleni, nangaphandle esa entendelezweni enkulu.', 'XHO22'),
(15, 7, 10, 'Useko lwalungamatye anqabileyo, amatye amakhulu, amatye eekubhite ezilishumi, amatye eekubhite ezisibhozo.', 'XHO22'),
(15, 7, 11, 'Ngaphezulu bekukho amatye anqabileyo ngokwesilinganiso samatye aqingqiweyo nemisedare.', 'XHO22'),
(15, 7, 12, 'Intendelezo enkulu ejikelezileyo yayineengcamba ezintathu zamatye aqingqiweyo, nocamba lwemisedare, yeyantendelezo engaphakathi yendlu kaYehova, neyeevaranda zendlu.', 'XHO22'),
(15, 7, 13, 'Ukumkani uSolomon wathumela ukuba kuthathwe uHiram eTire.', 'XHO22'),
(15, 7, 14, 'Waye engunyana womhlolokazi wesizwe sakwaNafetali, yaye uyise eyindoda yaseTire, esebenza ngobhedu; yaye wayezele bubulumko nengqiqo nokuqonda ukuze enze yonke imisebenzi ngobhedu. Weza kuKumkani uSolomon waza wawenza wonke umsebenzi wakhe.', 'XHO22'),
(15, 7, 15, 'Wenza iintsika zambini zobhedu: yalishumi elinesibhozo iikubhite ukuphakama kwayo, nomtya oziikubhite ezilishumi elinambini ngeenxa zonke inye.', 'XHO22'),
(15, 7, 16, 'Wenza iintloko ezimbini zobhedu zokuhlala phezu kweentsika ezo: ukuphakama kwentloko enye kuziikubhite ezintlanu, ukuphakama kwentloko enye kuziikubhite ezintlanu;', 'XHO22'),
(15, 7, 17, 'wenze iminatha ngelaphu, nezithsaba, nezithsaba, ezenziwe ngemixokelelwane, zabaphathiswa ababephezu kweentsika; ezisixhenxe kwisahluko esinye, nesixhenxe kwesinye isahluko.', 'XHO22'),
(15, 7, 18, 'Wenza iintsika zambini, zambini ngeenxa zonke emgangathweni; wenjenjalo nakweyesibini intloko.', 'XHO22'),
(15, 7, 19, 'Iminqwazi ebiseentsika ezo, ibingumsebenzi weenyibiba, ngokwevaranda, iziikubhite ezine;', 'XHO22'),
(15, 7, 20, 'Iintloko zeentsika zombini zazineerharnate phezulu, malunga nesisu esibe selangeni; nezithsaba ezimakhulu mabini, zikroze kukrozo lwentloko enye.', 'XHO22'),
(15, 7, 21, 'Wazimisa iintsika phezu kophahla lwetempile, wayimisa intsika ngasekunene, wathi ukuyibiza yiYakin; wayimisa intsika ngasekhohlo, wabiza uBhohazi.', 'XHO22'),
(15, 7, 22, 'Ngaphezu kweentsika ezo bekukho umsebenzi weenyibiba; wagqitywa ke umsebenzi weentsika.', 'XHO22'),
(15, 7, 23, 'Wenza ulwandle olutyhidiweyo: lwaziikubhite ezilishumi, kwathabathela kwelinye icala kwesa kwelinye; ibingqukuva, ukuphakama kwayo kube ziikubhite ezintlanu; yaye umtya oziikubhite ezimashumi mathathu wawujikelezile ngeenxa zonke.', 'XHO22'),
(15, 7, 24, 'Ngaphantsi komjikelo walo kwakukho amaqhina ajikelezileyo, aziikubhite ezilishumi ubude bawo, alujikelezile ulwandle olo; i-buds yenziwa kwimiqolo emibini ukuba yenziwe.', 'XHO22'),
(15, 7, 25, 'Wema phezu kweenkomo ezilishumi elinambini: ezintathu zibheke entla, ezintathu zibheke entshonalanga, ezintathu zibheke ezantsi, ezintathu zibheke empumalanga; kwaye ulwandle lwaba phezu kwabo, namaxhama abo onke ekukho.', 'XHO22'),
(15, 7, 26, 'Ukuphakama kwalo bebungangobubanzi besandla; udini lwalo belwenziwe lwanjengodini lwendebe, lwaneentyantyambo zeenyibiba; lalinamawaka amabini amalulwane.', 'XHO22'),
(15, 7, 27, 'Wenza izixhaso zalishumi ngobhedu; ubude becala elinye baba ziikubhite ezine, ububanzi baba ziikubhite ezine, nokuphakama kwayo kwakuziikubhite ezintathu.', 'XHO22'),
(15, 7, 28, 'Isihlalo sasinje: Zazinezibopho, nemithambo phakathi kwemiphetho.', 'XHO22'),
(15, 7, 29, 'Yaye kwiingcango ezisesazulwini samacala kwakukho iingonyama, iinkunzi zeenkomo neekherubhi, yaye phezu kwezi ngongoma kwakukho intsika, yaye ngaphantsi kweengonyama neenkomo kwakukho izimvu, ezibhityileyo.', 'XHO22'),
(15, 7, 30, 'Inqaba nganye yayinemilenze emine yobhedu neepleyiti zobhedu, neekona zayo zone zinezakhiwo; ngaphantsi komhadi kwakukho uphahla ngapha nangapha.', 'XHO22'),
(15, 7, 31, 'Umlomo wayo wawuyikubhite enye entloko nangaphezulu; emlonyeni wawo kwakukho imifanekiso eqingqiweyo, emacaleni awo, ububanzi, ingengqukuva.', 'XHO22'),
(15, 7, 32, 'Phantsi kweentambo beziziivili ezine; izibonda zeevili ezo zahlangana nesiseko, ukuphakama kwevili bekuyikubhite enye enesiqingatha.', 'XHO22'),
(15, 7, 33, 'Ukwenziwa kweevili bekunjengokwenziwa kweevili zeenqwelo zonke;', 'XHO22'),
(15, 7, 34, 'Kwakukho izixhaso ezine ezimbombeni zone zesiseko esinye, yaye iziseko zaziziziseko.', 'XHO22'),
(15, 7, 35, 'Ngaphezu kwesiseko esijikelezileyo kwakukho isangqa esisisiqingatha sekubhite ukuphakama kwayo;', 'XHO22'),
(15, 7, 36, 'Emacwecweni ayo neziphelo zayo wayebhale iikerubhi, iingonyama namasundu, ngokomlinganiso wayo, nezidanga ngeenxa zonke.', 'XHO22'),
(15, 7, 37, 'Wenza izihlalo zalishumi;', 'XHO22'),
(15, 7, 38, 'Wenza izitya zokutshiza zobhedu zalishumi: isitya esinye sasiziikubhite ezimashumi mane, isitya ngasinye sasiziikubhite ezine, isitya esinye phezu kweentsika ezilishumi.', 'XHO22'),
(15, 7, 39, 'Wabeka izixhaso zantlanu ngasekunene kwendlu, zantlanu ngecala lasekhohlo layo indlu; walubeka ulwandle ngasekunene kwendlu, ngecala lasempumalanga, ngecala lasezantsi.', 'XHO22'),
(15, 7, 40, 'uHuram wenza izitya zokuhlambela, nemihlakulwana, nezitya zokutshiza. Wawugqiba ke uHiram wonke umsebenzi abewenzela ukumkani uSolomon, wendlu kaYehova:', 'XHO22'),
(15, 7, 41, 'iintsika zombini, neembumba ezimbini zentloko, ezibe zeentsika zombini; naentsontelo zombini, ezo zitsala iimbumba ezimbini zeentloko zeentsika ezo;', 'XHO22'),
(15, 7, 42, 'namakhulu amane eenwele zeentsontelo ezimbini, imikrozo emibini yentambo emtya mnye yokuzunguleza iimbumba ezimbini zentloko, ezibe zisezintsikeni zombini;', 'XHO22'),
(15, 7, 43, 'neenqwelo zokuhlambela ezilishumi, nezitya zokuhlambela ezilishumi eenqwelo zokuhlambela;', 'XHO22'),
(15, 7, 44, 'nolwandle olunye, neenkomo ezilishumi elinambini ngaphantsi kolwandle olo;', 'XHO22'),
(15, 7, 45, 'Iimbiza nemihlakulwana nezitya zonke ezi zixhobo uHuram awazenzela ukumkani uSolomon zendlu kaYehova zazizezobhedu olukhazimlisiweyo.', 'XHO22'),
(15, 7, 46, 'uYehova wazigalela entlango yaseYordan, emhlabeni wodongwe phakathi kweSukoti neSartan.', 'XHO22'),
(15, 7, 47, 'Waziyeka uSolomon zonke izitya zingalinganiswanga, ngokuba bezizininzi kakhulu; kwaye ubunzima bomthengi abukamiselwa.', 'XHO22'),
(15, 7, 48, 'uSolomon wazenza zonke iimpahla zendlu kaYehova, nesibingelelo segolide, netafile yegolide ebezikuyo izonka zokubonisa;', 'XHO22'),
(15, 7, 49, 'neziphatho zezibane zegolide ecocekileyo: zazihlanu ngasekunene, zazihlanu ngasekhohlo, phambi kwendlu engaphakathi, neentyantyambo, nezibane, namalaphu egolide;', 'XHO22'),
(15, 7, 50, 'nezitya, nezitya zokutshiza, neenkamba, neengcedevu zegolide ecocekileyo; nemigubasi yendlu engaphakathi yegolide, yengcwele kangcwele, neengcango zendlu leyo eyeyotempile.', 'XHO22'),
(15, 7, 51, 'Wagqitywa ke wonke umsebenzi awawenzela indlu kaYehova uKumkani uSolomon. uSolomon wayizisa imibingelelo, abeyingcwalisile uDavide uyise; nesilivere, negolide, nempahla, wazibeka phantsi kobuncwane bendlu kaYehova.', 'XHO22'),
(15, 8, 1, 'Waza uSolomon wawabizela kuye amadoda amakhulu akwaSirayeli, neentloko zonke zezindlu zooyise, iintloko zezindlu zoonyana bakaSirayeli, kukumkani uSolomon eYerusalem, ukuba ayithabathe kuwo umzi ityeya yomnqophiso kaYehova kaDavide, waseZiyon.', 'XHO22'),
(15, 8, 2, 'Abuthelana kukumkani uSolomon onke amadoda akwaSirayeli emthendelekweni wenyanga enguEtanim: yinyanga yesixhenxe ke leyo.', 'XHO22'),
(15, 8, 3, 'Eza ke onke amadoda amakhulu akwaSirayeli, bayithwala ababingeleli ityeya leyo.', 'XHO22'),
(15, 8, 4, 'Benyuka nayo ityeya kaYehova, nentente yokuhlangana, neempahla zonke zengcwele, ezibe zisententeni, babanyusa ababingeleli nabaLevi.', 'XHO22'),
(15, 8, 5, 'uKumkani uSolomon nebandla lonke lamaSirayeli elalihlanganiselwe kuye phambi kweTyeya, babingelela ngezimvu neenkomo ezingenakulinganiswa okanye zibalwe ngenxa yesihlwele esikhulu.', 'XHO22'),
(15, 8, 6, 'Bayizisa ababingeleli ityeya yomnqophiso kaYehova endaweni yayo, endlwini engaphakathi endlwini, kwiNgcwele kaNgcwele, phantsi kwamaphiko eekherubhi.', 'XHO22'),
(15, 8, 7, 'Ngokuba iikerubhi zaye ziwolule amaphiko azo phezu kwendawo yetyeya, iikerubhi zayisithelisa ityeya nezibonda zayo ngaphezulu.', 'XHO22'),
(15, 8, 8, 'Wazithula ke imithwalo, azabonakala izikali endaweni engcwele phambi kwendlu, zingabonakali ngaphandle; zisekho unanamhla.', 'XHO22'),
(15, 8, 9, 'Kwakungekho nto etyeyeni ngaphandle kwamacwecwe amabini awayewabeke apho uMoses eHorebhe, xa uYehova wenza umnqophiso noonyana bakaSirayeli ekuphumeni kwabo ezweni laseYiputa.', 'XHO22'),
(15, 8, 10, 'Kwathi, ekuphumeni kwababingeleli engcweleni, lasuka ilifu layizalisa indlu kaYehova;', 'XHO22'),
(15, 8, 11, 'ababingeleli ababa nakuma balungiselele ngenxa yelifu, ngokuba ubuqaqawuli bukaYehova bazala endlwini kaYehova.', 'XHO22'),
(15, 8, 12, 'Wathi uSolomon, uYehova wathi, niya kuhlala ebumnyameni.', 'XHO22'),
(15, 8, 13, 'Mna ke ndikwakhele indlu apho uya kuhlala khona, indawo eqinileyo oya kuhlala kuyo ngonaphakade.', 'XHO22'),
(15, 8, 14, 'Wee guququ ukumkani walisikelela lonke ibandla lakwaSirayeli, laza lonke ibandla lakwaSirayeli lema.', 'XHO22');
