INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(15, 8, 15, 'Wathi, Makabongwe uYehova, uThixo kaSirayeli, owathethayo ngomlomo wakhe kuDavide ubawo, wakuzalisa ngesandla sakhe, esithi,', 'XHO22'),
(15, 8, 16, 'Kususela kwimini yokubakhupha kwam abantu bam amaSirayeli eYiputa, andinyulanga mzi ezizweni zonke zakwaSirayeli wokwakha indlu, yokuba libe khona igama lam; ke mna ndinyule uDavide ukuba abe yinganga yabantu bam amaSirayeli.', 'XHO22'),
(15, 8, 17, 'Kwaye kukho entliziyweni kaDavide ubawo ukuthi alakhele indlu igama likaYehova, uThixo kaSirayeli.', 'XHO22'),
(15, 8, 18, 'Wathi uYehova kuDavide ubawo, Ukuba bekusentliziyweni yakho ukuthi ulakhele indlu igama lam, walungisa ngokwentliziyo yakho;', 'XHO22'),
(15, 8, 19, 'Ke wena akuwenzi umzi; kodwa unyana wakho uya kuphuma esinqeni sakho, yena uya kulakhela indlu igama lam.', 'XHO22'),
(15, 8, 20, 'Yaye uYehova walizalisekisa ilizwi lakhe awalithethayo, yaye ndema egumbini likabawo uDavide ndaza ndahlala etroneni yakwaSirayeli, kanye njengoko uYehova wayethethile, ndaza ndalakhela indlu igama likaYehova uThixo kwaSirayeli.', 'XHO22'),
(15, 8, 21, 'Ndayilungisa ityeya apho kwakukho umnqophiso kaYehova awawenzayo nookhokho bethu ekubakhupheni kwakhe ezweni laseYiputa.', 'XHO22'),
(15, 8, 22, 'Wema ke uSolomon phambi kwesibingelelo sikaYehova phambi kwebandla lonke lakwaSirayeli, wazolulela ezulwini izandla zakhe.', 'XHO22'),
(15, 8, 23, 'Wathi, Yehova, Thixo kaSirayeli, akukho Thixo unjengawe emazulwini nasehlabathini, ubagcinelayo umnqophiso nenceba abakhonzi bakho, abahamba phambi kwakho ngentliziyo yabo yonke;', 'XHO22'),
(15, 8, 24, 'owamthembisayo umkhonzi wakho uDavide, ubawo, oko wakuthethayo kuye; uthethe ngomlomo wakho, wakwenza ngesandla sakho njengoko kunjalo namhla.', 'XHO22'),
(15, 8, 25, 'Ngako oko uYehova, uThixo kaSirayeli, umqinisele umkhonzi wakho uDavide ubawo oko wakuthethayo kuye, wathi, Emehlweni am akuyi kufunwa mntu, uhleli etroneni yakwaSirayeli; ukuze oonyana bakho bayigcine indlela yabo, bahambe phambi kwam, njengoko wahamba ngako wena phambi kwam.', 'XHO22'),
(15, 8, 26, 'Kaloku, Thixo kaSirayeli, malinyaniseke ilizwi lakho, owalixelela umkhonzi wakho uDavide ubawo.', 'XHO22'),
(15, 8, 27, 'Kodwa ngaba ngokwenene uThixo uya kuhlala emhlabeni? Yabonani, amazulu, nezulu lawo amazulu, alilingani; Ingakanani le ndlu ndiyakhileyo?', 'XHO22'),
(15, 8, 28, 'Khawuphulaphule umthandazo womkhonzi wakho nesicelo sakhe, Yehova Thixo wam, ukuphulaphule isikhalo nomthandazo athandaza ngawo umkhonzi wakho phambi kwakho namhlanje;', 'XHO22'),
(15, 8, 29, 'ukuze amehlo akho avuleke le ndlu imini nobusuku, nendawo owathi ngayo, ‘Igama lam liya kuba lapho, ukuze niwuphulaphule umthandazo womkhonzi wakho wokwenza kule ndawo.', 'XHO22'),
(15, 8, 30, 'Phulaphula umthandazo womkhonzi wakho, nowoonyana bakho amaSirayeli, abaya kuthandaza ngako kule ndawo; yivani, nina basemazulwini, esakhiweni senu; xa uthe weva, xolela;', 'XHO22'),
(15, 8, 31, 'Xa athe umntu woniwa ngummelwane wakhe, wamfungela, kwaza kwafika isifungo phambi kwesibingelelo sakho kule ndlu;', 'XHO22'),
(15, 8, 32, 'Khawuve ke emazulwini, wenze, ugwebe kubakhonzi bakho, umgwebe ongendawo, uyibeke indlela yakhe entlokweni yakhe; Ilungisa limnike ngokobulungisa bakhe.', 'XHO22'),
(15, 8, 33, 'Xa abantu bakho amaSirayeli boyiswa lutshaba, kuba bekonile, babuya beza kuwe, bathandaza, bathandaza kuwe kule ndlu;', 'XHO22'),
(15, 8, 34, 'Ke kaloku yiva usemazulwini, usixolele isono sabantu bakho amaSirayeli, ubabuyisele kwilizwe owalinika ookhokho babo.', 'XHO22'),
(15, 8, 35, 'Ukuba livalekile izulu, akwabakho mvula, ngokuba bakonile; xa bethandaza kule ndawo kwaye babiza igama lakho kwaye baguquke kwisono sabo ukuba uyabacinezela;', 'XHO22'),
(15, 8, 36, 'Ke kaloku yiva usemazulwini, usixolele isono sabakhonzi bakho nesabantu bakho amaSirayeli, ukuze ubafundise indlela elungileyo abafanele bahambe ngayo, nokunisa imvula ezweni lakho olinike abantu bakho njengelifa.', 'XHO22'),
(15, 8, 37, '“Ukuba kuthe kwakho indlala elizweni, xa kuthe kwakho indyikitya yokufa, izikhukula, iinkumbi, iinkumbi; Xa iintshaba zabo zabangqinga elizweni lemizi yabo; nasiphi na isifo, nokuba siyintoni na;', 'XHO22'),
(15, 8, 38, 'Umthandazo nawo nawuphi na umthandazo othe wacelwa nguye nabani na okanye ngabantu bakho bonke amaSirayeli, bayakwazi ngamnye ukufa kwentliziyo yakhe baze bazolulele izandla zabo kule ndlu;', 'XHO22'),
(15, 8, 39, 'Yiva ke ngoko wena emazulwini, apho uhlala khona, uxolele; ngokuba nguwe wedwa ozaziyo iintliziyo zoonyana babantu;', 'XHO22'),
(15, 8, 40, 'ukuze bakoyike yonke imihla yobomi babo, emhlabeni owawunika oobawo.', 'XHO22'),
(15, 8, 41, 'Ke yena owasemzini, ongengowesizwe sakowenu, osuka kwilizwe elikude ngenxa yegama lakho;', 'XHO22'),
(15, 8, 42, 'Kuba baya kuliva igama lakho elikhulu, nesandla sakho esithe nkqi, nengalo yakho ethe tye, ukuba uthe weza wathandaza ekule ndlu;', 'XHO22'),
(15, 8, 43, 'Yiva xa usemazulwini, kwindawo ohlala kuyo, wenze ngokuvumelana nako konke okubiza ngako iintlanga, ukuze zonke iintlanga zomhlaba zilazi igama lakho, mazikoyike, kanye njengomphefumlo wakho abantu bakwaSirayeli; bazi ukuba igama lakho libizwe phezu kwale ndlu ndiyakhileyo.', 'XHO22'),
(15, 8, 44, 'Xa abantu bakho bathe bazihlasela iintshaba zabo, apho ubathumela khona, bathandaza kuYehova bebhekisa kulo mzi uwunyulileyo, nakule ndlu ndiyakhele igama lakho;', 'XHO22'),
(15, 8, 45, 'Yiva usemazulwini umthandazo wabo nokutarhuzisa kwabo, ubamele esikhundleni sabo.', 'XHO22'),
(15, 8, 46, 'Ukuba bathe bakona kuwe (ngenxa yokuba kungekho bani ungoniyo) uze ube nomsindo kubo uze ubanikele ezandleni zeentshaba, zaza zabathimbela kwilizwe lotshaba; kude okanye kufutshane;', 'XHO22'),
(15, 8, 47, 'Kodwa ukuba bacinga ngelizwe ababethinjelwe kulo, baguquka baze babongoze kuwe ngokunyanisekileyo elizweni labo bathinjwa, besithi: “Sonile senza ububi, senze ububi ububi;', 'XHO22'),
(15, 8, 48, 'abuyela kuwe ngentliziyo yawo yonke, nangomphefumlo wawo wonke, esezweni leentshaba zawo ezaziwathimbile, athandaza kuwe, ebhekisa ezweni lawo owalinika ooyise, umzi owawunyulayo, nendlu endiyakhele igama lakho;', 'XHO22'),
(15, 8, 49, 'Yiva umthandazo wabo nokutarhuzisa kwabo usemazulwini, ekuhlaleni kwakho, ubamele ngenxa yabo.', 'XHO22'),
(15, 8, 50, 'Baxolele abantu bakho abonileyo kuwe, nezikreqo zabo zonke abakona ngazo kuwe, ube nemfesane kubo phambi kwababathimbileyo, ukuze babe nosizi ngabo.', 'XHO22'),
(15, 8, 51, 'Ngokuba ngabantu bakho, nelifa lakho, owabakhuphayo eYiputa ezikweni lesinyithi;', 'XHO22'),
(15, 8, 52, 'ukuze amehlo akho akhangele emthandazweni womkhonzi wakho nomthandazo wabantu bakho amaSirayeli, ukuze ubaphulaphule kuyo yonk’ into abaya kubhenela kuyo kuwe.', 'XHO22'),
(15, 8, 53, 'Ngenxa enokuba wabanyula ezizweni zonke zehlabathi, ukuba babe lilifa lakho, njengoko wathethayo ngoMoses umkhonzi wakho, ekubakhupheni kwakho oobawo eYiputa, Nkosi Yehova.', 'XHO22'),
(15, 8, 54, 'Akugqiba uSolomon ukwenza wonke lo mthandazo nezibongozo kuYehova, wema phambi kwesibingelelo sikaYehova, eguqe ngamadolo nezandla zakhe ezolulele ezulwini.', 'XHO22'),
(15, 8, 55, 'Wesuka wema, walisikelela lonke ibandla lakwaSirayeli ngelizwi elikhulu, wathi:', 'XHO22'),
(15, 8, 56, 'Makabongwe uYehova, obaphumzayo abantu bakhe amaSirayeli ngako konke wakuthethayo; akwasala nalinye ilizwi kuwo onke amazwi akhe alungileyo, awawathethayo ngoMoses umkhonzi wakhe.', 'XHO22'),
(15, 8, 57, 'uYehova uThixo wethu makabe nathi, njengokuba wayenoobawo bethu; makangasishiyi, makangasishiyi;', 'XHO22'),
(15, 8, 58, 'ukuze ayithobele kuye intliziyo yethu ukuba sihambe ngeendlela zakhe zonke, sigcine imiyalelo yakhe, nemithetho yakhe, nezigwebo zakhe, awabawisela umthetho ngazo oobawo.', 'XHO22'),
(15, 8, 59, 'La mazwi am ndiwathandazayo phambi koYehova ngamana abe kufuphi noYehova uThixo wethu imini nobusuku ukuze ayixhase imeko yomkhonzi wakhe nemeko yabantu bakhe amaSirayeli ngamaxesha onke, njengoko kuyimfuneko;', 'XHO22'),
(15, 8, 60, 'ukuze bonke abantu behlabathi bazi ukuba uYehova unguThixo, akukho wumbi.', 'XHO22'),
(15, 8, 61, 'Ke kaloku mazigqibelele iintliziyo zenu kuYehova uThixo wethu, ukuba nihambe ngemithetho yakhe emisiweyo, niyigcine imithetho yakhe, njengoko namhla kunjalo.', 'XHO22'),
(15, 8, 62, 'uYehova namaSirayeli onke bazisa umnikelo phambi koYehova.', 'XHO22'),
(15, 8, 63, 'uSolomon wabingelela umbulelo kuYehova, wawuzisa kuYehova: iinkomo ezingamashumi amabini anesibini amawaka, nekhulu elinamanci amabini lamawaka empalula emfutshane. Waphuma ke ukumkani noonyana bonke bakaSirayeli endlwini kaYehova.', 'XHO22'),
(15, 8, 64, 'Ngaloo mini ukumkani wawungcwalisa umphakathi wentendelezo phambi kwendlu kaYehova; kuba kulapho wazisa khona iminikelo etshiswayo nemibingelelo etshiswayo namanqatha emibingelelo yombulelo, ngenxa yokuba isibingelelo sobhedu esasiphambi koYehova sasincinane kakhulu ukuba singawamkela umnikelo otshiswayo nomnikelo otshiswayo namanqatha imibingelelo yoxolo.', 'XHO22'),
(15, 8, 65, 'Ngelo xesha uSolomon wenza itheko nawo onke amaSirayeli ekunye naye, ibandla elikhulu, ukususela ekungeneni kweHamati ukusa kuMlambo waseYiputa, phambi koYehova uThixo wethu, iintsuku ezisixhenxe neentsuku ezisixhenxe, iintsuku ezilishumi elinesine.', 'XHO22'),
(15, 8, 66, 'Ngosuku lwesibhozo wathi ebantwini mabahambe, baza bambonga ukumkani baza baya ezintenteni zabo bevuya yaye ezintliziyweni zabo benemihlali ngenxa yokulunga konke uYehova awakwenzayo kuDavide umkhonzi wakhe nakubantu bakhe amaSirayeli.', 'XHO22'),
(15, 9, 1, 'Wagqiba uSolomon ukuyakha indlu kaYehova, nendlu yokumkani, nomnqweno wonke kaSolomon, abefuna ukukwenza.', 'XHO22'),
(15, 9, 2, 'uYehova wabonakala kuSolomon okwesibini, njengoko wayebonakele kuye eGibheyon.', 'XHO22'),
(15, 9, 3, 'Wathi uYehova kuye, Ndikuvile ukuthandaza kwakho nokutarhuzisa kwakho okwenzileyo phambi kwam; aya kuhlala elapho amehlo am nentliziyo yam.', 'XHO22'),
(15, 9, 4, 'Ukuba uthe wahamba nam, njengoko wahamba ngako uDavide uyihlo, ngentliziyo ethe tye, nangobulungisa, ukwenze konke endikuwisele umthetho ngako, uyigcine imithetho yam nemimiselo yam;', 'XHO22'),
(15, 9, 5, 'Ke kaloku ndiya kuyiqinisa itrone yobukumkani bakho phezu koSirayeli ukusa kwixesha elingenammiselo, kanye njengoko ndathembisa uyihlo uDavide ndisithi, ‘Akuyi kunqunyukelwa ndoda etroneni yakwaSirayeli.', 'XHO22'),
(15, 9, 6, 'Ke ukuba nithe nabuya kum, nina, noonyana benu, nemithetho yam, nemiyalelo yam, endiyibeke phambi kwenu, anayenza, anakhonza thixo bambi, nabanqula;', 'XHO22'),
(15, 9, 7, 'Ndiza kumtshabalalisa uSirayeli emhlabeni endimnike wona; nale ndlu ndiyingcwalisayo ngenxa yegama lam, ndoyilahla, imke ebusweni bam; uSirayeli uya kuba yintsomi ezintlangeni zonke.', 'XHO22'),
(15, 9, 8, 'Kule ndlu ephakamileyo, bonke abadlulayo kuyo bomangaliswa, bavuthele umlozi; yaye ngokuqinisekileyo baya kuthi: ‘Kwakutheni ukuze uYehova enze eli hlabathi nale ndlu ngolu hlobo?', 'XHO22'),
(15, 9, 9, 'Baphendule bathi, Kungenxa yokuba batyekayo kuYehova uThixo wabo, owabakhuphayo ooyise ezweni laseYiputa, bathabatha oothixo bolunye uhlanga, babanqula, babakhonza; uYehova wabahlisela yonke le ntlekele.', 'XHO22'),
(15, 9, 10, 'Ekupheleni kweminyaka emashumi mabini, uSolomon wayakha izindlu ezimbini indlu kaYehova nendlu yokumkani;', 'XHO22'),
(15, 9, 11, 'Ke uHiram, ukumkani waseTire, wanika uSolomon imisedare nemisipres, nemisipres, negolide, ngangokuthanda kwakhe konke; ngoko ukumkani uSolomon wamnika uHiram izixeko ezimashumi mabini ezweni laseGalili.', 'XHO22'),
(15, 9, 12, 'Wemka ke uHiram eTire, waya kubona imizi abemnike yona uSolomon; kwaye abamkholisanga.', 'XHO22'),
(15, 9, 13, 'Wathi, Yimizi mini na le undinikileyo, mzalwana wam? Wathi lilizwe laseKabhuli unanamhla.', 'XHO22'),
(15, 9, 14, 'uHiram wathumela kukumkani iitalente zegolide ezimashumi mathandathu.', 'XHO22'),
(15, 9, 15, 'Siso esi isizathu sokunyula irhafu, awayisusayo uSolomon; ukuba akhe indlu kaYehova, nendlu yakhe, neMilo, nodonga lweYerusalem, neHatsore, neMegido, neGezere.', 'XHO22'),
(15, 9, 16, 'Wenyuka uFaro ukumkani waseYiputa, wayithabatha iGezere, wayitshisa ngomlilo, wawabulala amaKanan abehleli kuloo mzi, wayinika intombi yakhe, umkaSolomon, yaba sisipho.', 'XHO22'),
(15, 9, 17, 'uSolomon wakha iGezere, neBhete-horon ezantsi;', 'XHO22'),
(15, 9, 18, 'neBhahalati, neTademore entlango, ezweni;', 'XHO22'),
(15, 9, 19, 'nemizi yonke engoovimba uSolomon abenayo, nemizi yeenqwelo zakhe, nemizi yamahashe akhe, awayefuna ukuyakha uSolomon eYerusalem naseLebhanon, nasezweni lonke lolawulo lwakhe;', 'XHO22'),
(15, 9, 20, 'nabo bonke abaseleyo bama-Amori, namaHeti, namaPerizi, namaHeti, namaYebhusi, abengengawo oonyana bakaSirayeli;', 'XHO22'),
(15, 9, 21, 'Oonyana babo abasala emva kwabo elizweni, abo oonyana bakaSirayeli babengabaqalekisanga nokubaqalekisa, uSolomon wabamisela irhafu yenkonzo de kube namhla.', 'XHO22'),
(15, 9, 22, 'Kodwa uSolomon akazange akhonze namnye woonyana bakaSirayeli, kodwa baba ngamadoda alwayo nabakhonzi bakhe neenkosana zakhe nabaphathi bakhe nabaphathi beenqwelo zakhe nabangamadoda ahlala amahashe.', 'XHO22'),
(15, 9, 23, 'Aba yayiziintloko zezigidimi, ababephethe umsebenzi kaSolomon, babengamakhulu amahlanu anamanci mahlanu, ababephethe abantu abenza umsebenzi.', 'XHO22'),
(15, 9, 24, 'Yenyuka ke intombi kaFaro, iphuma kwisiXeko sikaDavide, yaya endlwini yayo awayakhela yona uSolomon; emva koko wakha iMilo.', 'XHO22'),
(15, 9, 25, 'uSolomon wayenikela imibingelelo etshiswayo neyombulelo izihlandlo ezithathu ngonyaka esibingelelweni awayesakhele uYehova, yaye wenza ukuba kutshiswe nesiqhumiso esiphambi koYehova. Wayigqiba ke indlu.', 'XHO22'),
(15, 9, 26, 'Ukumkani uSolomon wakha izinqanawa e-Esion-gebhere, engase-Eloti, elunxwemeni loLwandle oluBomvu, ezweni lakwaEdom.', 'XHO22'),
(15, 9, 27, 'uHiram wathuma abakhonzi bakhe, oomatiloshe abalwazi ulwandle, nabakhonzi bakaSolomon enqanaweni.', 'XHO22'),
(15, 9, 28, 'Bafika eOfire, bathabatha khona igolide, amakhulu amane anamanci mabini eetalente, bazisa kukumkani uSolomon.', 'XHO22'),
(15, 10, 1, 'Xa ukumkanikazi waseShebha weva ibali likaSolomon eliphathelele igama likaYehova, weza kumvavanya ngemibuzo enzima.', 'XHO22'),
(15, 10, 2, 'Wafika eYerusalem enesihlwele esikhulu kunene, esineenkamela ezithwele ubulawu, negolide eninzi, namatye anqabileyo; ekufikeni kwakhe kuSolomon wathetha naye ngayo yonke into eyayisentliziyweni yakhe.', 'XHO22'),
(15, 10, 3, 'uSolomon wamana emxelela yonke imibuzo yakhe;', 'XHO22'),
(15, 10, 4, 'Wabubona ukumkanikazi waseShebha bonke ubulumko bukaSolomon, nendlu awayakhayo,', 'XHO22'),
(15, 10, 5, 'nokudla kwetafile yakhe, nezihlalo zabakhonzi bakhe, nenkonzo yabakhonzi bakhe, nezambatho zabo, nabangcamli bakhe, nezinyuko zakhe abe enyuka ngazo ukuya endlwini kaYehova; akwaba sabakho kuphefumla kuye.', 'XHO22'),
(15, 10, 6, 'Wathi kukumkani, Lilizwi eliyinyaniso, endilivileyo ezweni lam, ngezenzo zakho nobulumko bakho.', 'XHO22'),
(15, 10, 7, 'Andikholwanga kuloo mazwi, ndada ndeza, abona amehlo am; uyabona, andixelelwanga nesiqingatha esi. Ubulumko bakho nempumelelo yakho ilugqithile udaba endiluvileyo.', 'XHO22'),
(15, 10, 8, 'Anoyolo amadoda akho, banoyolo aba bakhonzi bakho bemiyo ecaleni kwakho ngamaxesha onke, bebuva ubulumko bakho.', 'XHO22'),
(15, 10, 9, 'Makabongwe uYehova uThixo wakho, okunyulileyo ukuba akubeke etroneni yakwaSirayeli, ngenxa yokuba uYehova emthanda uSirayeli kude kuse ephakadeni, ngenxa yoko wamenza ukumkani ukuba enze okusesikweni nobulungisa.', 'XHO22'),
(15, 10, 10, 'Ukumkani wanika ukumkani ikhulu elinamanci amabini eetalente zegolide, nasebulawini into eninzi, namatye anqabileyo; akwaba sabakho bulawu bunjengabo, ukumkanikazi waseShebha wabunika ukumkani uSolomon.', 'XHO22'),
(15, 10, 11, 'Iinqanawa zikaHiram ezazithwele igolide evela kwaOfire zeza nemithi yomamangile eninzi namatye axabisekileyo evela kwaOfire.', 'XHO22'),
(15, 10, 12, 'Ukumkani wazenza ngemialmondi iintsika zendlu kaYehova, nezendlu yokumkani, neehadi, nemirhubhe yeemvumi;', 'XHO22'),
(15, 10, 13, 'Ukumkani uSolomon wanika ukumkanikazi waseShebha konke awayekunqwenela, awayekucelile; Wajika waya ezweni lakhe, yena nabakhonzi bakhe.', 'XHO22'),
(15, 10, 14, 'Ubunzima begolide awazuzayo uSolomon ngomnyaka, baba ngamakhulu omathandathu anamanci mathandathu anesithandathu eetalente zegolide.', 'XHO22'),
(15, 10, 15, 'Ngaphezu koko, wayenabarhwebi, nabarhwebi beziqholo, nookumkani bonke baseArabhi, neerhuluneli zelizwe.', 'XHO22'),
(15, 10, 16, 'Ukumkani uSolomon wenza amakhulu amabini eeshekele zegolide ekhandiweyo;', 'XHO22'),
(15, 10, 17, 'Wenza amakhulu amathathu amakhaka egolide ekhandiweyo; Lanye ikhaka legolide elibunzima buziikhilogram ezintathu, waza ukumkani walibeka endlwini yehlathi laseLebhanon.', 'XHO22'),
(15, 10, 18, 'Ngaphezu koko, ukumkani wenza itrone enkulu yophondo lwendlovu waza wayaleka ngegolide esulungekileyo.', 'XHO22'),
(15, 10, 19, 'Itrone leyo ibinezinyuko ezithandathu; itrone leyo ibinomphezulu oyinqila ngasemva, kukho izixhaso kumacala omabini esihlalo eso, kumi iingonyama ezimbini ngasezixhasweni.', 'XHO22'),
(15, 10, 20, 'Kwakukho iingonyama ezilishumi elinambini khona ezinyukweni ezithandathu, ngapha nangapha;', 'XHO22'),
(15, 10, 21, 'Zonke iimpahla zokumkani uSolomon zabe zezegolide, nempahla yonke yendlu yehlathi laseLebhanon yabe iyeyegolide ecikizekileyo; kwakungekho nto yesilivere; kwakungekho nto yayifundwa ngexesha likaSolomon.', 'XHO22'),
(15, 10, 22, 'Ngokuba ukumkani ubeneenqanawa zaseTarshishe elwandle, kananjalo nezikaHiram; zeza kanye ngeminyaka emithathu iinqanawa ezo zaseTarshishe, zizise igolide, nesilivere, neempondo zeendlovu, neenkawu, neepikoko.', 'XHO22'),
(15, 10, 23, 'Ngoko uKumkani uSolomon wabagqwesa bonke ookumkani behlabathi ngobutyebi nangobulumko.', 'XHO22'),
(15, 10, 24, 'Lonke ihlabathi lafuna ukuva uSolomon ubulumko bakhe, awayebunike entliziyweni yakhe uThixo.', 'XHO22'),
(15, 10, 25, 'Elowo wazisa isipho sakhe: iimpahla zesilivere, neempahla zegolide, neempahla, nezixhobo, nobulawu, namahashe, noondlebende, iminyaka ngeminyaka.', 'XHO22'),
(15, 10, 26, 'uSolomon wahlanganisa iinqwelo zokulwa namahashe akhwelwayo, waba newaka elinamakhulu amane eenqwelo zokulwa, namawaka alishumi elinamabini amahashe akhwelwayo;', 'XHO22'),
(15, 10, 27, 'uYehova wayenza isilivere yanjengamatye eYerusalem, nemisedare wayenza yanjengemithombe esezihlanjeni, ukuba mininzi.', 'XHO22'),
(15, 10, 28, 'uSolomon ubenamahashe aseYiputa, nelinen emhlophe; Abarhwebi bokumkani bamkela ilinen emhlophe ngexabiso.', 'XHO22'),
(15, 10, 29, 'Yenyuka inqwelo yokulwa, yaphuma eYiputa, ingamakhulu omathandathu eeshekele zesilivere, nehashe ngekhulu elinamanci mahlanu; bazinika bonke ookumkani bamaHeti, nookumkani bama-Aram.', 'XHO22'),
(15, 11, 1, 'Ukumkani uSolomon wayethanda abafazi abaninzi bezinye iintlanga, nentombi kaFaro, namaMowabhikazi, nama-Amon, namaEdom, namaTsidon, namaHeti;', 'XHO22'),
(15, 11, 2, 'bephakathi kweentlanga, abethe uYehova koonyana bakaSirayeli, Ize ningangeni kuzo, nazo zingangeni kuni;', 'XHO22'),
(15, 11, 3, 'Waba nabafazi abangamakhulu asixhenxe, amakhosazana, nezithende ezingamakhulu amathathu; abafazi bakhe bayityekisa intliziyo yakhe.', 'XHO22'),
(15, 11, 4, 'Ke kaloku kwathi, ngexesha lobudala bukaSolomon, abafazi bakhe bayityekisa intliziyo yakhe ekulandeleni thixo bambi; yaye intliziyo yakhe yayingamlungelanga uYehova uThixo wakhe njengentliziyo kaDavide uyise.', 'XHO22'),
(15, 11, 5, 'uSolomon walandela uAshtoreti, uthixokazi wamaTsidon, noMilkom, isikizi loonyana baka-Amon.', 'XHO22'),
(15, 11, 6, 'uSolomon wenza okubi emehlweni kaYehova, akamlandela uYehova ngokupheleleyo njengoDavide uyise.', 'XHO22'),
(15, 11, 7, 'uSolomon wamakhela uKemoshe, izothe lakwaMowabhi, entabeni ekufuphi neYerusalem, noMologi, izothe loonyana baka-Amon, isiganga.', 'XHO22'),
(15, 11, 8, 'Wenjenjalo ke nakubo bonke abafazi bakhe bezinye iintlanga, ababeqhumisela bebingelela koothixo babo.', 'XHO22'),
(15, 11, 9, 'uYehova ke waba nomsindo kuSolomon, ngokuba ibityekile intliziyo yakhe, yemka kuYehova uThixo kaSirayeli, owayezityhile kuye izihlandlo ezibini;', 'XHO22'),
(15, 11, 10, 'Wamwisela umthetho ngalo mbandela, ukuba angalandeli thixo bambi, angenzi oko uYehova abemwisele umthetho ngako.', 'XHO22'),
(15, 11, 11, 'Wathi ke uYehova kuSolomon, Ekubeni wenze le nto, akwawugcina umnqophiso wam, nemiyalelo yam endakuwiselayo, okwenene, ndiya kubukrazula ubukumkani kuwe, ndibunike umkhonzi wakho.', 'XHO22'),
(15, 11, 12, 'Ke andiyi kukwenza oko ngemihla yakho, ngenxa kaDavide uyihlo; kodwa ndiya kubukrazula esandleni sonyana wakho.', 'XHO22'),
(15, 11, 13, 'Andiyi kubukrazula bonke ubukumkani; unyana wakho ndimnike isizwe esinye ngenxa kaDavide umkhonzi wam, nangenxa yeYerusalem endiyinyulileyo.', 'XHO22'),
(15, 11, 14, 'uYehova wamvelisela uSolomon utshaba, uHadadi umEdom; wayengowoonyana bokumkani wakwaEdom.', 'XHO22'),
(15, 11, 15, 'Kwathi, oko uDavide ebekwaEdom, wenyuka uYowabhi, umthetheli-mkhosi, waya kungcwaba ababuleweyo, wawabulala onke amadoda akwaEdom.', 'XHO22'),
(15, 11, 16, 'Wahlala apho uYowabhi nawo onke amaSirayeli iinyanga zantandathu, wada wayitshabalalisa yonke into eyindoda yakwaEdom.', 'XHO22'),
(15, 11, 17, 'Wabaleka uHadade, enamaEdom kayise, baya eYiputa; Ke uHadade ebengumntwana omncinane.', 'XHO22'),
(15, 11, 18, 'Banduluka kwaMidiyan, beza eParan; owamnika indlu, wamnika ukudla, wamnika namasimi.', 'XHO22'),
(15, 11, 19, 'uHadade wababalwa kakhulu emehlweni kaFaro, kangangokuba wamnika udade bomkakhe, udade boTahepenesi, ukumkanikazi, ukuba abe ngumkakhe.', 'XHO22'),
(15, 11, 20, 'Umsakwabo boTahepenese wamzalela unyana wakhe, uGenubhati, lowo uTahepenase wamanyisa endlwini kaFaro; uGenubhati wahlala endlwini kaFaro phakathi koonyana bakaFaro.', 'XHO22'),
(15, 11, 21, 'Weva uHadade eseYiputa, ukuba uDavide ulele kooyise, ufile noYowabhi umthetheli-mkhosi; wathi uHadade kuFaro, Ndindulule ndiye ezweni lakowethu.', 'XHO22'),
(15, 11, 22, 'Wathi uFaro kuye, Undikhathalele ngantoni na, ukuba uya kubuyela ezweni lakowenu? Waphendula ke yena wathi, Akukho nto; nangona kunjalo, ndivumele ndihambe nokuba kukweliphi na icala.', 'XHO22'),
(15, 11, 23, 'uThixo wamvelisela omnye umchasi onguReson, unyana kaEliyada, owabalekayo phambi koHadadezere, ukumkani waseTsobha, ukumkani wakhe.', 'XHO22'),
(15, 11, 24, 'Wabizela kuye amadoda, waba yingqwayi-ngqwayi yamadoda omkhosi, ekuxabeleni kukaDavide iSobha; baya eDamasko, bahlala khona, bayilawula eDamasko.', 'XHO22'),
(15, 11, 25, 'Waba lutshaba lukaSirayeli yonke imihla kaSolomon, ngaphandle nje kobubi awabenzayo uHadadi; Wawathiya amaSirayeli, wayelawula kwa-Aram.', 'XHO22'),
(15, 11, 26, 'uYerobhoham unyana kaNebhati, umEfrate waseSereda, umkhonzi kaSolomon, ogama likanina lalinguTseruya umhlolokazi, yaye wasiphakamisa isandla sakhe nxamnye nokumkani.', 'XHO22'),
(15, 11, 27, 'Nasi isizathu sokuba amphakamisele isandla ukumkani: uSolomon wayesakha iMilo waza walungisa nemijelo yesiXeko sikaDavide uyise.', 'XHO22'),
(15, 11, 28, 'Ke loo ndoda inguYarobheham ibiligorha; yaye xa uSolomon wabonayo ukuba umfana lowo wayesebenza nzima, wammisela ukuba abe ngumphathi wayo yonke inkonzo yendlu kaYosefu.', 'XHO22'),
(15, 11, 29, 'Ngelo xesha uYerobhoham waphuma eYerusalem, wadibana nomprofeti uAhiya Moshilo endleleni; wambatha ingubo entsha; baye bobabini bebodwa ezweni.', 'XHO22'),
(15, 11, 30, 'uAkija wayithabatha loo ngubo intsha abeyinxibile, wayikrazula yaziziqwenga ezilishumi elinazibini.', 'XHO22'),
(15, 11, 31, 'Wathi kuYarobheham, Zithabathele iziqwenga zibe lishumi; ngokuba utsho uYehova, uThixo kaSirayeli, ukuthi, Yabona, ndiya kubukrazula ubukumkani esandleni sikaSolomon, ndibunike izizukulwana ezilishumi;', 'XHO22'),
(15, 11, 32, 'Kodwa wena uya kuba nesizwe esinye ngenxa kaDavide umkhonzi wam nangenxa yeYerusalem, isixeko endisinyulileyo ezizweni zonke zakwaSirayeli.', 'XHO22'),
(15, 11, 33, 'Ndibukrazula ngenxa enokuba bendishiyile mna, baqubuda kuAshtoreti, uthixo wamaMowabhi, nakuMilikom, uthixo woonyana baka-Amon; àbahamba ngeendlela zam, ukuba benze okuthe tye ngeendlela zam, ukuba benze okuthe tye emehlweni am, nemimiselo yam, namasiko am, njengoDavide uyise.', 'XHO22'),
(15, 11, 34, 'Kodwa andiyi kubuthabatha bonke ubukumkani esandleni sakhe, kodwa ndiya kumenza ukumkani yonke imihla yobomi bakhe ngenxa kaDavide umkhonzi wam endimnyulileyo, ngenxa yokuba uyigcinile imiyalelo yam nemiyalelo yam inamathele.', 'XHO22'),
(15, 11, 35, 'Ndiya kubuthabatha ubukumkani esandleni sonyana wakhe, ndibunike wena izizukulwana ezilishumi;', 'XHO22'),
(15, 11, 36, 'unyana wakhe ndimnike isizwe esinye, ukuze kubekho ukukhanya kuDavide, umkhonzi wam, yonke imihla phambi kwam eYerusalem, umzi endiwunyulileyo ukuba alimise khona igama lam.', 'XHO22'),
(15, 11, 37, 'Ndiya kukuthabatha, ube ngukumkani kanye njengoko unqwenela umphefumlo wakho, ube ngukumkani wakwaSirayeli.', 'XHO22'),
(15, 11, 38, 'Ukuba uthe waphulaphula kuyo yonk’ into endikuyalela yona, wahamba ngeendlela zam, wenze okulungileyo emehlweni am ngokugcina imiyalelo yam emiselweyo nemiyalelo yam, kanye njengoko wenzayo umkhonzi wam uDavide; ukuze ndibe nawe, ndikwakhele indlu eyomeleleyo, njengoko ndamakhelayo uDavide, ndikunike uSirayeli.', 'XHO22'),
(15, 11, 39, 'Ngenxa yoko ndiya kuyicinezela inzala kaDavide, kodwa kungekhona ngonaphakade.', 'XHO22'),
(15, 11, 40, 'uSolomon wazama ukumbulala uYarobheham. Wagqiba uYarobheham, wasabela eYiputa kuShishaki, ukumkani waseYiputa, wayilawula eYiputa, wada wafa uSolomon.', 'XHO22'),
(15, 11, 41, 'Ezinye izinto zikaSolomon, nako konke awakwenzayo, nobulumko bakhe, ndithi, zibhaliwe na encwadini yemicimbi kaSolomon?', 'XHO22'),
(15, 11, 42, 'Ixesha awalawula ngalo uSolomon eYerusalem phezu koonyana bakaSirayeli bonke yaba yiminyaka engamashumi amane.', 'XHO22'),
(15, 11, 43, 'uSolomon walala kooyise, wangcwatyelwa emzini kaDavide uyise; unyana wakhe uRehobhoham waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(15, 12, 1, 'Kwaye Ekufikeni kukaYarobheham eShekem, ngokuba amaSirayeli onke abeye kumenza ukumkani kwaShekem.', 'XHO22'),
(15, 12, 2, 'Weva uYarobheham, unyana kaNebhati, eseYiputa, ngokuba wayebaleke ukumkani uSolomon; wahlala uYarobheham eYiputa,', 'XHO22'),
(15, 12, 3, 'wathumela ukuba ambize. Weza ke uYarobheham nebandla lonke likaSirayeli, bathetha kuRehabheham, bathi,', 'XHO22'),
(15, 12, 4, 'Uyihlo wasithwalela idyokhwe enzima; ke wena yenze lula inkonzo enzima kayihlo, nedyokhwe yakhe enzima ayibeke phezu kwethu, sikukhonze.', 'XHO22'),
(15, 12, 5, 'Wathi kubo, Hambani niye iintsuku zibe ntathu, nibuye nize kum. Kwaye abantu bayahamba.', 'XHO22'),
(15, 12, 6, 'Ukumkani uRehabheham wacebisana namadoda amakhulu, abesakuma phambi koSolomon uyise oko ebesahleli, wathi, Ninika cebo lini na nina, ukuba ndibanike ilizwi aba bantu?', 'XHO22'),
(15, 12, 7, 'Athetha ke kuye, athi, Ukuba uthe namhla waba ngumkhonzi kwaba bantu, wabakhonza, wabaphendula, wathetha amazwi amahle kubo, boba ngabakhonzi bakho kude kuse ephakadeni.', 'XHO22'),
(15, 12, 8, 'Kodwa walishiya icebiso awayelinikwe ngamadoda amakhulu, waya kucebisana nabafana abaziintanga zakhe ababemi ngakuye.', 'XHO22'),
(15, 12, 9, 'Wathi kuwo, Ninika cebo lini na nina, ukuze sibanike ilizwi aba bantu bathethileyo kum, besithi, Yiba lula edyokhweni awayibeka phezu kwethu uyihlo?', 'XHO22'),
(15, 12, 10, 'Athetha ke kuye amadodana aziintanga zakhe, esithi, Yithi kwaba bantu bathethe kuwe, besithi, Uyihlo wayithwala idyokhwe yethu enzima, senzele yona; wothi kubo, Ucikicane wam uya kuba mkhulu kunesinqe sikabawo.', 'XHO22'),
(15, 12, 11, 'Ngoku ke, xa uBawo enithwalise idyokhwe enzima, mna ke ndiya kuyongeza idyokhwe yenu;', 'XHO22'),
(15, 12, 12, 'Baya ke ooYarobheham nabantu bonke kuRehabheham ngomhla wesithathu, njengoko abewise umthetho ngako ukumkani, wathi, Yizani kum ngomhla wesithathu;', 'XHO22'),
(15, 12, 13, 'Ukumkani wabaphendula abantu kalukhuni, walishiya icebo lamadoda amakhulu abemnike lona;', 'XHO22'),
(15, 12, 14, 'Wathetha kubo ngecebo lamadodana, esithi, Ubawo wanithwalisa idyokhwe enzima, mna ke ndiya kuyandisa idyokhwe yenu; Ubawo wayenibetha ngeentsontelo, mna ke ndiya kunibetha ngeekatsi.', 'XHO22'),
(15, 12, 15, 'Akabaphulaphulanga ke abantu ukumkani; ngenxa yokuba lo mbandela wawungokaYehova, ukuze aliphumeze ilizwi lakhe awalithethayo uYehova ngenkonzo ka-Ahiya uMoshilo kuYerobhoham unyana kaNebhati.', 'XHO22'),
(15, 12, 16, 'Akubona ke onke amaSirayeli ukuba ukumkani akawaphulaphuli, abantu bambuyisela ukumkani ilizwi elithi, Sinasabelo sini na kuDavide? Asinalifa kunyana kaYese; Yiya ezintenteni zakho, Sirayeli, ukhangele indlu yakho, Davide; Yaye oonyana bakaSirayeli baya ezintenteni zabo.', 'XHO22'),
(15, 12, 17, 'Ke oonyana bakaSirayeli, ababehleli emizini yakwaYuda, uRehabheham wabalawula.', 'XHO22'),
(15, 12, 18, 'Ukumkani uRehabheham wathuma uAdoram, umbuthi werhafu; yaye bonke oonyana bakaSirayeli bamgibisela ngamatye, wafa. Ngoko ukumkani uRehobhoham wakhawuleza wamkhwelisa enqwelweni waza wasabela eYerusalem.', 'XHO22'),
(15, 12, 19, 'Akreqa ke amaSirayeli kwindlu kaDavide, unanamhla.', 'XHO22'),
(15, 12, 20, 'Evile ke onke amaSirayeli ukuba uYarobheham ubuyile, athumela ambizela ebandleni, amenza ukumkani kumaSirayeli onke; akwabakho mntu wakha wayilandela indlu kaDavide, yaba sisizwe sakwaYuda sodwa.', 'XHO22'),
(15, 12, 21, 'Weza uRehabheham eYerusalem, wayihlanganisa yonke indlu yakwaYuda, nesizwe sakwaBhenjamin, amadoda anamandla, amadoda okulwa alikhulu elinamanci asibhozo anamabini, ukuba aye kulwa nendlu kaSirayeli, ukuba abuyele ezweni ubukumkani kuRehabheham, unyana kaSolomon.', 'XHO22'),
(15, 12, 22, 'Lafika ke ilizwi likaThixo kuShemaya umfo wakwaThixo, lisithi:', 'XHO22'),
(15, 12, 23, 'Yitsho kuRehabheham unyana kaSolomon, ukumkani wakwaYuda, nendlu yonke yakwaYuda, neyakwaBhenjamin, namasalela abantu, ukuthi,', 'XHO22'),
(15, 12, 24, 'Wathi uYehova, Ize ninganyuki niye kulwa nabazalwana benu, oonyana bakaSirayeli; makabuyele elowo endlwini yakhe; ngokuba yeyam le nto. Bandula ke baliphulaphula ilizwi likaYehova baza babuya bahamba ngokwelizwi likaYehova.', 'XHO22'),
(15, 12, 25, 'uYarobheham wayakha iShekem kweleentaba lakwaEfrayim, wahlala kuyo; wemka apho wakha iPnuele.', 'XHO22'),
(15, 12, 26, 'Wathi uYarobheham entliziyweni yakhe, Kaloku ubukumkani buya kubuyela endlwini kaDavide.', 'XHO22'),
(15, 12, 27, 'Ukuba bathe benyuka aba bantu baya eYerusalem, baya kubingelela endlwini kaYehova, intliziyo yaba bantu yobuyela enkosini yabo, kuRehabheham, ukumkani wakwaYuda, bandibulale ke mna, nakuRehabheham ukumkani wakwaYuda Yuda, hamba uYuda.', 'XHO22'),
(15, 12, 28, 'Ukumkani wacinga ukwenza amathole egolide abe mabini, wathi kubo, Ukunyuka siye eYerusalem kunzima; yabona, Sirayeli, oothixo bakho bakukhupha ezweni laseYiputa.', 'XHO22'),
(15, 12, 29, 'Walimisa elinye eBheteli, walimisa elinye kwaDan.', 'XHO22'),
(15, 12, 30, 'Yaba sisono loo nto; ngokuba abantu baqubuda elowo nowabo, besa kwaDan.', 'XHO22'),
(15, 12, 31, 'Wenza indlu ephakamileyo waza wabeka ababingeleli kwelona nqanaba lisezantsi phakathi kwabantu ababengengoonyana bakaLevi.', 'XHO22'),
(15, 12, 32, 'uYarobheham wenza umthendeleko ngenyanga yesibhozo, ngomhla weshumi elinesihlanu enyangeni leyo, onjengomthendeleko wakwaYuda; Wenjenjalo naseBheteli, wabingelela amathole awawenzileyo; wabamisa ababingeleli beziganga awazenzayo eBheteli.', 'XHO22'),
(15, 12, 33, 'Wenza umbingelelo esibingelelweni awasenzayo eBheteli, ngomhla weshumi elinesihlanu wenyanga yesibhozo, ngenyanga leyo abeyicamangile entliziyweni yakhe; Wabenzela umthendeleko oonyana bakaSirayeli, wawusondeza esibingelelweni, waqhumisela.', 'XHO22'),
(15, 13, 1, 'Nanko, wafika umfo wakwaThixo, evela kwaYuda, ngokwelizwi likaYehova, wafika eBheteli, uYarobheham emi phambi kwesibingelelo eqhumisela.', 'XHO22'),
(15, 13, 2, 'Wamemeza esibingelelweni ngelizwi likaYehova, wathi, Wena sibingelelo, sibingelelo, utsho uYehova; Uyabona, kuya kuzalwa umntwana kwindlu kaDavide ogama linguYosiya; uze ubabulale ababingeleli beziganga abaqhumisela phezu kwakho, atshiswe phezu kwakho amathambo abantu.', 'XHO22'),
(15, 13, 3, 'Wanikela umqondiso ngaloo mini, wathi, Nguwo lo umqondiso awuthethileyo uYehova; Yabona, isibingelelo siya kuqwengwa, nothuthu oluphezu kwaso lulahlwe;', 'XHO22'),
(15, 13, 4, 'Weva ukumkani uYarobheham ilizwi lomfo wakwaThixo ekhwaza esibingelelweni esiseBheteli, wasolulela isandla sakhe esibingelelweni, wathi, Mbambeni. Kwanesandla sakhe awayesolulele kuye esomile, ngokokude angabi nako ukusisondeza kuye.', 'XHO22'),
(15, 13, 5, 'Sikrazulwa isibingelelo nothuthu lwesibingelelo, ngokomqondiso awawunikelayo umntu kaTHIXO oyinyaniso ngelizwi likaYehova.', 'XHO22'),
(15, 13, 6, 'Waphendula uYehova, wathi kumfo wakwaThixo, Thandaza kuYehova uThixo wakho, undithandazele, sibuyele kum isandla sam. Wathandaza umfo wakwaThixo kuYehova, saza isandla sokumkani samphilisa kwakhona, waba njengokwangaphambili.', 'XHO22'),
(15, 13, 7, 'Wandula ke ukumkani wathi kumfo wakwaThixo: “Hamba nam siye ekhaya, uze uzithembe, yaye ndiza kukunika umvuzo.', 'XHO22'),
(15, 13, 8, 'Wathi umfo wakwaThixo kukumkani, Nokuba uthe wandinika isiqingatha sendlu yakho, andingekhe ndiye nawe, andingekhe ndidle sonka, andingekhe ndisele manzi kule ndawo.', 'XHO22'),
(15, 13, 9, 'Ngenxa enokuba ndawiselwa umthetho ngelizwi likaYehova, elithi, Uze ungadli sonka, ungaseli manzi, ungabuyi ngendlela obuze ngayo.', 'XHO22'),
(15, 13, 10, 'Wahamba ngenye indlela, akabuya ngendlela abeze ngayo eBheteli.', 'XHO22'),
(15, 13, 11, 'Kwaye kukho apho eBheteli umprofeti othile, owalupheleyo; Beza oonyana bakhe, bamxelela yonke imisebenzi awayenzayo umfo wakwaThixo ngaloo mini, eBheteli amazwi awawathethayo kukumkani, awawaxela nakuyise.', 'XHO22'),
(15, 13, 12, 'Wathi uyise kubo, Nihambe ngayiphi na indlela? Kuba oonyana bakhe babeyibonile indlela awayehamba ngayo umntu kaThixo wokwenene owayevela kwaYuda.', 'XHO22'),
(15, 13, 13, 'Wathi koonyana bakhe, Ndigcineleni iesile. Bayibopha iesile, wakhwela.', 'XHO22'),
(15, 13, 14, 'Wamlandela umntu kaTHIXO oyinyaniso waza wamfumana ehleli phantsi komdiliya, waza wathi kuye: “Unguye na umfo kaTHIXO oyinyaniso owaphuma kwaYuda? Wathi yena: Ndinguye.', 'XHO22'),
(15, 13, 15, 'Wathi kuye, Hamba siye ekhaya, udle isonka.', 'XHO22'),
(15, 13, 16, 'Wathi yena, Andiyi kubuya nawe, andiyi kungena nawe; andiyi kudla sonka, andiyi kusela manzi nawe kule ndawo.', 'XHO22'),
(15, 13, 17, 'Kwathi, ngenxa enokuba ndixelelwe ngelizwi likaYehova, kwathiwa, Uze ungadli sonka, ungaseli manzi khona, ungabuyi ngendlela obuhambe ngayo.', 'XHO22'),
(15, 13, 18, 'Wathi kuye, Ndingumprofeti nam njengawe; sathetha isithunywa sikaYehova kum, sisithi, Mbuyisele endlwini yakho, adle isonka, asele namanzi. Kodwa wamxokisa.', 'XHO22'),
(15, 13, 19, 'Wabuya ke naye, wadla isonka, wasela namanzi endlwini yakhe.', 'XHO22'),
(15, 13, 20, 'Kwathi, besetafileni, kwafika ilizwi likaYehova kumprofeti lowo ubembuyisile.', 'XHO22'),
(15, 13, 21, 'Wabiza umfo wakwaThixo ovela kwaYuda, wathi, Utsho uYehova ukuthi, Ekubeni uwungcikive umlomo kaYehova, akwawugcina umthetho abekuwisele uYehova uThixo wakho;', 'XHO22'),
(15, 13, 22, 'Wabuya wadla isonka, wasela amanzi kuloo ndawo abethethe ngayo uYehova, wathi, Ize ningadli sonka, ningaseli manzi; umzimba wakho awuyi kufikelela engcwabeni likayihlo.', 'XHO22'),
(15, 13, 23, 'Emva kokutya isonka nokusela, wabopha idonki, awayeyibuyisele umprofeti lowo wayembuyisile.', 'XHO22'),
(15, 13, 24, 'Uthe ke akuphuma, wafunyanwa yingonyama endleleni, yambulala; isidumbu sakhe sajulwa endleleni, lema iesile phezu kwalo; nayo ingonyama yema ngasesibatheni.', 'XHO22'),
(15, 13, 25, 'Kwabonakala kudlula amadoda lawo, abona isabatha endleleni, kumi ingonyama phezu komgibe; beza bashumayela kwisixeko awayehlala kuso umprofeti omdala.', 'XHO22'),
(15, 13, 26, 'Wathi akukuva oko umprofeti obembuyisile endleleni, wathi: “Lo ngumfo wakwaThixo oyinyaniso ongaliphulaphulanga ilizwi likaYehova; uYehova wamnika ingonyama, yamqwenga, yambulala ngokwelizwi likaYehova, awalithethayo kuye.', 'XHO22'),
(15, 13, 27, 'Wathetha koonyana bakhe, wathi, Ndigcineleni iesile. Kwaye bamjonga.', 'XHO22'),
(15, 13, 28, 'Waya ke, wawuphosa umzimba wakhe endleleni; ingonyama ayizange iwudle umgibe, ayizange iyiqwenge nedonki.', 'XHO22'),
(15, 13, 29, 'Ngoko umprofeti wawuthabatha umzimba womntu oyinyaniso kaTHIXO oyinyaniso wawukhwelisa e-esileni waza wabuya nawo; Weza ke esixekweni umprofeti lowo useleyindoda enkulu, ukuba ammbambazelele, amngcwabe.', 'XHO22'),
(15, 13, 30, 'Wawubeka umzimba wakhe engcwabeni lakhe; Bamkhalela, besithi: Oh!', 'XHO22'),
(15, 13, 31, 'Wathi ke ekumngcwabeni kwakhe, wathetha koonyana bakhe, esithi, Ndakuba ndifile, yizani, nindingcwabe engcwabeni angcwatyelwe kulo umfo wakwaThixo; wawabeka amathambo phambi kwamathambo akhe.', 'XHO22'),
(15, 13, 32, 'Ngokuba liya kuzaliseka ilizwi elo, awalimemezayo ngelizwi likaYehova esibingelelweni esiseBheteli, nasezigangeni zonke zemizi yakwaSamari.', 'XHO22'),
(15, 13, 33, 'Emva koko uYerobhoham akazange ajike endleleni yakhe embi, kodwa waphinda wamisela abantu abasweleyo phakathi kwabantu ukuba babe ngababingeleli beendawo eziphakamileyo;', 'XHO22'),
(15, 13, 34, 'Eli lizwi laba sisono kwindlu kaYarobheham, ukuba litshatyalaliswe, litshatyalaliswe emhlabeni.', 'XHO22'),
(15, 14, 1, 'Ngelo xesha wahliwa sisifo uAbhiya, unyana kaYarobheham.', 'XHO22'),
(15, 14, 2, 'Wathi uYarobheham kumkakhe, Yeka, uzenze mntu wumbi, ungazi, ngokuba ungumkaYarobheham; waya eShilo; nanku umprofeti uAhiya, owandixelelayo ukuba ndiya kuba ngukumkani wolu hlanga.', 'XHO22'),
(15, 14, 3, 'Thabatha izonka ezilishumi, nesonka, neselwa lobusi, uze kuye; uya kukuxelela okuya kwenzeka kumntwana.', 'XHO22'),
(15, 14, 4, 'Wenjenjalo umkaYarobheham, wesuka waya eShilo, wafika endlwini ka-Ahiya. Ke uAhiya wayengaboni; ngokuba amehlo akhe abenorhatyazo ebuxhegweni bakhe.', 'XHO22'),
(15, 14, 5, 'Wathi uYehova kuAhiya, Nanko umkaYarobheham esiza kuwe, eze kucela kuwe ngonyana wakhe; ngokuba uyagula; mxelele ke: kuba xa engena uzenza omnye umfazi.', 'XHO22'),
(15, 14, 6, 'Weva uAhiya iinyawo zakhe, xa engena emnyango, wathi, Ngena, mkaYarobheham kutheni uzenza omnye umntu? ngokuba ndithunywe kuni ndineendaba ezibuhlungu.', 'XHO22'),
(15, 14, 7, 'Ke, wothi kuYarobheham, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ekubeni ndakuphakamisayo phakathi kwabantu, ndiya kukwenza ukumkani wabantu bam amaSirayeli;', 'XHO22'),
(15, 14, 8, 'Bususe ubukumkani endlwini kaDavide, unike yena; ukanti wawungenjengomkhonzi wam uDavide, owayigcinayo imiyalelo yam nowandilandela ngentliziyo yakhe yonke ukuze enze oko kulungileyo kum amehlo;', 'XHO22'),
(15, 14, 9, 'Kambe ke wena wenze ububi obungaphezu kobo bubekho phambi kwakho, ngenxa enokuba uzenzele izithixo zasemzini, nezithixo ezingento, wadlala ngam, wandiphosa emva komhlana wakho.', 'XHO22'),
(15, 14, 10, 'Ngako oko, yabona, ndiyizisela ububi indlu kaYarobheham, ndinqumle kwaYarobheham olwa nodonga, ongavaliweyo kwaSirayeli, ndiwasuse amasalela endlu kaYehova. uYarobheham, njengomntu osusa ubulongwe bude buphele.', 'XHO22'),
(15, 14, 11, 'OkaYarobheham ofela phakathi komzi, uya kudliwa zizinja; yaye lowo ufela entlango uya kudliwa ziintaka zezulu, ngenxa yokuba uYehova ethethile.', 'XHO22'),
(15, 14, 12, 'Vuka kaloku, uye endlwini yakho; ekungeneni kweenyawo zakho kuwo umzi, iya kufa umntwana.', 'XHO22'),
(15, 14, 13, 'Onke amaSirayeli omzilela aze amngcwabe, kuba nguye yedwa kwaYarobheham oya kungena engcwabeni, ngenxa yokuba kufunyenwe kuye okuthile okulungileyo okuvela kuYehova, uThixo kaSirayeli, endlwini kaYarobheham.', 'XHO22'),
(15, 14, 14, 'Ngaphezu koko, uYehova uya kumvelisela ukumkani wakwaSirayeli oya kuyitshabalalisa indlu kaYarobheham ngaloo mini; kodwa ntoni? nangoku.', 'XHO22'),
(15, 14, 15, 'Ngokuba uYehova uya kuwabetha amaSirayeli, njengengcongolo izanyazanyiswa emanzini, awanqumle amaSirayeli kulo mhlaba ulungileyo, awawunika ooyise, awachithachithele phesheya koMlambo, ngenxa enokuba ezenzele ihlathi umthi ongcwele ogculela uYehova.', 'XHO22'),
(15, 14, 16, 'Yena uya kumkhulula uSirayeli ngenxa yezono zikaYarobheham, awawonisa ngazo amaSirayeli.', 'XHO22'),
(15, 14, 17, 'Wahamba umkaYarobheham, waya eTirtsa; yathi, yakungena emnyango, yafa inkwenkwe;', 'XHO22'),
(15, 14, 18, 'Bamngcwaba; yaye wonke uSirayeli wamlilela ngokwelizwi likaYehova awalithethayo ngomkhonzi wakhe, umprofeti uAkiya.', 'XHO22'),
(15, 14, 19, 'Ezinye izinto zikaYarobheham, ukulwa kwakhe, nokulawula kwakhe, nanzo zibhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli.', 'XHO22'),
(15, 14, 20, 'Imihla awalawula ngayo uYarobheham yaba yiminyaka emashumi mabini anamibini; walala noyise, unyana wakhe uNadabhi waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(15, 14, 21, 'Ke uRehabheham, unyana kaSolomon, waye engukumkani kwaYuda. uRehabheham ubeminyaka imashumi mane anamnye ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinesixhenxe engukumkani eYerusalem, umzi awawunyulayo uYehova ezizweni zonke zakwaSirayeli, ukuba alimise igama lakhe kuwo. Igama lonina belinguNahama umAmon.', 'XHO22'),
(15, 14, 22, 'AmaYuda enza okubi emehlweni kaYehova, amqumbisa ngezono zawo, abenzé ngaphezu kooyise bonke.', 'XHO22'),
(15, 14, 23, 'Kwakhona azakhela iindawo eziphakamileyo nemifanekiso eqingqiweyo neziqu zemithi engcwele kuzo zonke iinduli eziphakamileyo naphantsi kwayo yonke imithi eluhlaza.', 'XHO22'),
(15, 14, 24, 'Kananjalo bekukho amaSodom kwelo lizwe, enza ngokwamasikizi onke eentlanga, awazigqogqayo uYehova phambi koonyana bakaSirayeli.', 'XHO22'),
(15, 14, 25, 'Ngomnyaka wesihlanu wokumkani uRehobhowam, uShishaki ukumkani waseYiputa wayihlasela iYerusalem.', 'XHO22'),
(15, 14, 26, 'Wabuthabatha ubuncwane bendlu kaYehova nobuncwane bendlu yokumkani; wazithabatha zonke, wathabatha onke amakhaka egolide awawenzayo uSolomon.', 'XHO22'),
(15, 14, 27, 'Ukumkani uRehabheham wenza iingweletshetshe zobhedu esikhundleni sazo, wazinika umthetheli wabasiki, umlinda womnyango wendlu yokumkani.', 'XHO22'),
(15, 14, 28, 'Uthe akungena ukumkani endlwini kaYehova, zathabatha abalindi, bazibuyisela egumbini lezigidimi.', 'XHO22'),
(15, 14, 29, 'Ezinye izinto zikaRehabheham, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(15, 14, 30, 'Yaye iyimfazwe phakathi koRehabheham noYarobheham yonke imihla yabo.', 'XHO22'),
(15, 14, 31, 'uRehabheham walala kookhokho bakhe, wangcwatywa nookhokho bakhe kwisiXeko sikaDavide. Igama lonina lalinguNahama, oonyana baka-Amon. uAbhiya, unyana wakhe, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(15, 15, 1, 'Ngomnyaka weshumi elinesithoba anesibini wokumkani uYarobheham unyana kaNebhati, uAbhiya waba ngukumkani kwaYuda.', 'XHO22'),
(15, 15, 2, 'Waba neminyaka emithathu engukumkani eYerusalem. Igama lonina belinguMaga, intombi ka-Abhisalom.', 'XHO22'),
(15, 15, 3, 'Waqhubeka ehamba ezonweni zonke zikayise awazenzayo phambi kwakhe, yaye intliziyo yakhe yayingaphelele kuYehova uThixo wakhe njengentliziyo kaDavide uyise.', 'XHO22'),
(15, 15, 4, 'Kodwa ngenxa kaDavide, uYehova uThixo wakhe wamnika isibane eYerusalem, ukuze avelise unyana wakhe emva kwakhe, ukuze ayiqinise iYerusalem.', 'XHO22'),
(15, 15, 5, 'uDavide wenza okuthe tye emehlweni kaYehova, akatyeka kuko konke awamwisela umthetho ngako yonke imihla yobomi bakhe, ngaphandle kwemeko kaUriya umHeti.', 'XHO22'),
(15, 15, 6, 'Yaye iyimfazwe phakathi koRehabheham noYarobheham yonke imihla yobomi bakhe.', 'XHO22'),
(15, 15, 7, 'Ezinye izinto zika-Abhiya, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda? Kwabakho imfazwe phakathi kuka-Abhiya noYarobheham.', 'XHO22'),
(15, 15, 8, 'Walala uAbhiya kooyise; waza wangcwatyelwa kwisiXeko sikaDavide, waza uAsa unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(15, 15, 9, 'Ngomnyaka wamashumi amabini kaYarobheham ukumkani wakwaSirayeli, uAsa waba ngukumkani kwaYuda.', 'XHO22'),
(15, 15, 10, 'Waba neminyaka emashumi mane anamnye engukumkani eYerusalem. Igama lonina belinguMaga, intombi ka-Abhisalom.', 'XHO22'),
(15, 15, 11, 'uAsa wenza okuthe tye emehlweni kaYehova, njengoDavide uyise.', 'XHO22'),
(15, 15, 12, 'Wawasusa amahenyukazi ehlabathini, wayisusa yonke imifanekiso eqingqiweyo ababeyenzile ooyise;', 'XHO22'),
(15, 15, 13, 'NoMaga unina wayerhoxa ebukumkanini, ngokuba wenza umfanekiso oqingqiweyo etyholweni; uAsa wasitshabalalisa isithixo sakhe, wasitshisa kumlambo iKidron.', 'XHO22'),
(15, 15, 14, 'Kodwa iziganga aziphambukanga; kodwa yona intliziyo ka-Asa yabe iqinile kuYehova yonke imihla yakhe.', 'XHO22'),
(15, 15, 15, 'Wazizisa izipho awayezingcwalisile uyise, awayezingcwalisile endlwini kaYehova, isilivere negolide nempahla.', 'XHO22'),
(15, 15, 16, 'Kwaye kukho imfazwe phakathi kuka-Asa noBhasha ukumkani wakwaSirayeli yonke imihla yabo.', 'XHO22'),
(15, 15, 17, 'uBhahesha ukumkani wakwaSirayeli wawaxabela amaYuda, wayakha iRama, ukuze angavumi ukuba kuphume bani aye kuAsa ukumkani wakwaYuda.', 'XHO22'),
(15, 15, 18, 'Wayithabatha uAsa yonke isilivere negolide eyayisele ebuncwaneni bendlu kaYehova, nasebuncwaneni bendlu yokumkani, wayinikela esandleni sabakhonzi bakhe; ukumkani uAsa wayithumela kunyana kaBhen-hadade. Wathi uTabrimon unyana kaHeziyon, ukumkani wakwa-Aram, ohleli eDamasko:', 'XHO22'),
(15, 15, 19, 'Kukho umnqophiso phakathi kwam nawe, naphakathi kobawo noyihlo; uyabona, ndikuthumelela isipho sesilivere negolide; Yiza, uwaphule umnqophiso wakho noBhasha, ukumkani wakwaSirayeli, wokuba ndihambe.', 'XHO22'),
(15, 15, 20, 'Wandula ke uBhen-hadadi wamphulaphula uKumkani uAsa waza wathumela abathetheli bemikhosi yakhe nxamnye nezixeko zakwaSirayeli, waza woyisa i-Iyon neDan neAbhele-bhete-mahaga nayo yonke iShineroti nelizwe lonke lakwaNafetali.', 'XHO22'),
(15, 15, 21, 'Akukuva oku uBhasha wayeka ukuyakha iRama, wahlala eTirtsa.', 'XHO22'),
(15, 15, 22, 'Waza uKumkani uAsa wakhala phakathi kwawo onke amaYuda; bawasusa amatye eRama, nemithi yayo, awasakha ngayo uBhahesha; yaye ukumkani uAsa wakha ngazo bha yakwaBhenjamin neMizpa.', 'XHO22'),
(15, 15, 23, 'Ezinye izinto zika-Asa, nobugorha bakhe bonke, nako konke awakwenzayo, nemizi awayakhayo, nâzo zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda? Noko ke, ekwaluphaleni kwakhe, wayegula yimilenze yakhe.', 'XHO22'),
(15, 15, 24, 'Ekugqibeleni uAsa walala kookhokho bakhe waza wangcwatywa nookhokho bakhe kwisiXeko sikayise uDavide unyana wakhe uYehoshafati waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(15, 15, 25, 'Ke yena uNadabhi, unyana kaYarobheham, waba ngukumkani wakwaSirayeli ngomnyaka wesibini ka-Asa ukumkani wakwaYuda, waba ngukumkani wakwaSirayeli iminyaka emibini.', 'XHO22'),
(15, 15, 26, 'Wenza okubi emehlweni kaYehova, walandela umzekelo kayise nesono sakhe awamcinezela ngaso uSirayeli.', 'XHO22'),
(15, 15, 27, 'uBhahesa unyana ka-Ahiya, wendlu kaIsakare, wagwilika kuye; uBhahesa wayixabela iGibheton yamaFilisti; ngokuba uNadabhi noonyana bonke bakaSirayeli babeyingqingile iGibheton.', 'XHO22'),
(15, 15, 28, 'Ngomnyaka wesithathu ka-Asa ukumkani wakwaYuda, uBhasha wambulala ke, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(15, 15, 29, 'Ukuqala kwakhe ukulawula, wayibulala yonke indlu kaYarobheham; akasalisa namnye umphefumlo kaYarobheham, wada wamtshabalalisa, ngokwelizwi likaYehova, awalithethayo ngomkhonzi wakhe uAhiya waseShilo:', 'XHO22'),
(15, 15, 30, 'ngenxa yezono zikaYerobhoham awazenzayo waza wamonisa ngazo uSirayeli ngenxa yomsindo wakhe awamcaphukisa ngawo uYehova uTHIXO kaSirayeli.', 'XHO22'),
(15, 15, 31, 'Ezinye izinto zikaNadabhi, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(15, 15, 32, 'Yaye iyimfazwe phakathi kuka-Asa noBhasha ukumkani wakwaSirayeli yonke imihla yabo.', 'XHO22'),
(15, 15, 33, 'Ngomnyaka wesithathu ka-Asa ukumkani wakwaYuda, uBhahesha, unyana ka-Ahiya, waba ngukumkani kumaSirayeli onke eTirtsa, iminyaka emashumi mabini anamine.', 'XHO22'),
(15, 15, 34, 'Wenza okubi emehlweni kaYehova, wahamba ngendlela kaYarobheham, nasesonweni sakhe awawonisa ngaso amaSirayeli.', 'XHO22'),
(15, 16, 1, 'Kwafika ilizwi likaYehova kuYehu, unyana kaHanani, phezu kweBhahesa, lisithi,', 'XHO22'),
(15, 16, 2, 'Ngokuba ndakukhupha eluthulini, ndakwenza ukumkani wabantu bam amaSirayeli; yaye wahamba ngendlela kaYarobheham waza wababeka ityala abantu bam amaSirayeli ngokundikhotyokisa ngezono zabo;', 'XHO22'),
(15, 16, 3, 'Yabona, ndiya kuyishenxisa imbewu kaBhasha, nembewu yendlu yakhe; nendlu yakho ndiya kuyenza ibe njengendlu kaYerobhoham unyana kaNebhati.', 'XHO22'),
(15, 16, 4, 'OngowaseBhesha ofela phakathi komzi, uya kudliwa zizinja; yaye ofela endle wodliwa ziintaka zezulu.', 'XHO22'),
(15, 16, 5, 'Ezinye izinto zikaBhahesha, awakwenzayo, nobugorha bakhe, nâzo zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(15, 16, 6, 'Ekugqibeleni uBhasha walala kookhokho bakhe waza wangcwatywa eTirtsa, yaye uEla unyana wakhe waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(15, 16, 7, 'Ngesandla somprofeti uYehu, unyana kaHanani, kwafika ilizwi likaYehova kuBhasha nendlu yakhe, ngenxa yobubi bonke awabenzayo emehlweni kaYehova, ngokumxhaphaza; hlekisa esandleni sakhe njengendlu kaYarobheham; nangenxa yokuba embulele.', 'XHO22'),
(15, 16, 8, 'Ngomnyaka wamashumi omabini anesithandathu ka-Asa ukumkani wakwaYuda, waba ngukumkani uEla, unyana kaBhasha, kwaSirayeli eTirtsa, iminyaka emibini.', 'XHO22'),
(15, 16, 9, 'Waza umkhonzi wakhe uZimri, umthetheli wesiqingatha seenqwelo zakhe zokulwa, wagwilika kuye eseTirtsa, ekunxileni kwakhe endlwini ka-Arsa, umlindi wendlu yakhe eTirtsa.', 'XHO22'),
(15, 16, 10, 'Wangena uZimri, wamgwaza, wambulala, ngomnyaka wamashumi omabini anesixhenxe ka-Asa ukumkani wakwaYuda, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(15, 16, 11, 'Uthe akuqala ukuba ngukumkani, ehleli etroneni yakhe, wayibulala yonke indlu kaBhahesha; akashiya nabani na eqhekeza udonga, kwanabazalwana bakhe nabahlobo bakhe.', 'XHO22'),
(15, 16, 12, 'Wayitshabalalisa ke uZimri yonke indlu kaBhasha, ngokwelizwi likaYehova, awalithethayo ngoBhasha ngomkhonzi womprofeti uYehu;', 'XHO22'),
(15, 16, 13, 'ngenxa yezono zonke zikaBhasha nezono zikaEla unyana wakhe abona ngazo ababangela ukuba oonyana bakaSirayeli bangabi nasono ngokugculela uYehova uTHIXO kaSirayeli ngomsindo wabo olambathayo.', 'XHO22'),
(15, 16, 14, 'Ezinye izinto zikaEla, nako konke awakwenzayo, ndithi, nanzo zibhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(15, 16, 15, 'Ngomnyaka wamashumi amabini anesixhenxe ka-Asa ukumkani wakwaYuda, uZimri waba ngukumkani eTire iintsuku ezisixhenxe. Amadoda amisa inkampu nxamnye neGibheton yamaFilisti.', 'XHO22'),
(15, 16, 16, 'Yaye amadoda awayewangqinga eva ukuba kwathiwa, uSimeri wenze icebo lemvukelo waza wambulala nokumkani; Onke ke amaSirayeli ammisa ngaloo mini uOmri, umthetheli-mkhosi, ukumkani wakwaSirayeli, ukuba amise emkhosini.', 'XHO22'),
(15, 16, 17, 'Emva koko uOmri wenyuka waya eGibheton nawo onke amaSirayeli awayekunye naye, baza bangqinga iTirtsa.', 'XHO22'),
(15, 16, 18, 'Wathi akubona uZimri ukuba isixeko sithinjiwe, wangena ebhotwe lendlu kakumkani waza wayitshisa indlu kakumkani ngomlilo, wafa;', 'XHO22'),
(15, 16, 19, 'ngenxa yezono zakhe awazenzayo ngokwenza okubi emehlweni kaYehova, wahamba ngendlela kaYarobheham, nangenxa yezono zakhe awazenzayo, esongela amaSirayeli.', 'XHO22'),
(15, 16, 20, 'Ke eminye imicimbi kaZimri nobuqhophololo awabenzayo, akubhalwanga na encwadini yemicimbi yookumkani bakwaSirayeli?', 'XHO22'),
(15, 16, 21, 'Baza bahluka kubini oonyana bakaSirayeli; esinye isiqingatha sabantu salandela uTibheni, unyana kaGinati, ukuba simenze ukumkani; abanye balandela uOmri.', 'XHO22'),
(15, 16, 22, 'Kodwa abantu ababelandela uOmri baboyisa abantu ababelandela uTibheni unyana kaGinati, wafa uTibheni, yaye uOmri waqalisa ukulawula.', 'XHO22'),
(15, 16, 23, 'Ngomnyaka wamashumi omathathu anamnye ka-Asa ukumkani wakwaYuda, uOmri waba ngukumkani wakwaSirayeli iminyaka elishumi elinamibini; Walawula iminyaka emithandathu eThirtsa.', 'XHO22'),
(15, 16, 24, 'Waza wathenga intaba yakwaSamari kuShemere ngeetalente ezimbini zesilivere, wakha kuloo ntaba, wathi loo mzi wawakhayo yiSamari, ngegama likaShemere umninintaba.', 'XHO22'),
(15, 16, 25, 'Kodwa uOmri wenza okubi emehlweni kaYehova, wenza okubi ngakumbi kunabo bonke ababengaphambi kwakhe.', 'XHO22'),
(15, 16, 26, 'Waqhubeka ehamba ngendlela yonke kaYerobhoham unyana kaNebhati nesono sakhe awamzisa kuso uSirayeli, ukuze amqumbise uYehova uTHIXO kaSirayeli ngomsindo wabo olambathayo.', 'XHO22'),
(15, 16, 27, 'Ke eminye imicimbi kaOmri awayenzayo namandla akhe awawenzayo, akubhalwanga na encwadini yemicimbi yookumkani bakwaSirayeli?', 'XHO22'),
(15, 16, 28, 'Ekugqibeleni uOmri walala kookhokho bakhe waza wangcwatywa kwaSamari, yaye uAhabhi unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(15, 16, 29, 'Ngomnyaka wamashumi omathathu anesibini ka-Asa ukumkani wakwaYuda, waba ngukumkani uAhabhi, unyana kaOmri kumaSirayeli; uAhabhi, unyana kaOmri, wayengukumkani kwaSirayeli iminyaka emashumi mabini anamibini eSamariya.', 'XHO22'),
(15, 16, 30, 'uAhabhi unyana kaOmri wenza okubi emehlweni kaYehova ngaphezu kwabo bonke ababengaphambi kwakhe.', 'XHO22'),
(15, 16, 31, 'Yaye kwathi, ngokungathi yinto elula kuye ukuhamba ezonweni zikaYerobhoham unyana kaNebhati, wathabatha uIzebhele unyana kaEtebhahali ukumkani waseTsidon waba ngumfazi wakhe waza waya kumbulala. Bakhonza uBhahali, baqubuda kuye.', 'XHO22'),
(15, 16, 32, 'Wakha isibingelelo sikaBhahali endlwini kaBhahali awayakhayo eSamariya.', 'XHO22'),
(15, 16, 33, 'uAhabhi wenza ihlathi; waphinda uAhabhi wamqumbisa uYehova, uThixo kaSirayeli, ngaphezu kookumkani bonke bakwaSirayeli ababengaphambi kwakhe.', 'XHO22'),
(15, 16, 34, 'Ngexesha lakhe uHiyeli waseBheteli wayakha iYeriko; wawuseka ngoAbhiram, amazibulo akhe, wawamisa amasango ayo ngoSegubhe, unyana wakhe omncinane, ngokwelizwi likaYehova, awalithethayo ngomyalelo kaYoshuwa unyana kaNun.', 'XHO22'),
(15, 17, 1, 'Wathi uEliya waseTishbhe, kubemi baseGiliyadi, kuAhabhi, NgoYehova, uThixo kaSirayeli, ohleliyo, endimi phambi kwakhe, akuyi kubakho mbethe namvula ngale minyaka, kungengalo ilizwi likaYehova.', 'XHO22'),
(15, 17, 2, 'Kwafika ilizwi likaYehova kuye, lisithi:', 'XHO22'),
(15, 17, 3, 'Mka apha, ujike uye empumalanga, uzimele emlanjeni oyiKereti, oseYordan.', 'XHO22'),
(15, 17, 4, 'Uya kusela emlanjeni; ndaza ndayalela amagwababa ukuba adle khona.', 'XHO22'),
(15, 17, 5, 'Wahamba ke wenza ngokwelizwi likaYehova, ngokuba waya wahlala emfuleni womlanjana oyiKereti, ngaseYordan.', 'XHO22'),
(15, 17, 6, 'Amagwababa amzisela isonka nenyama kusasa, isonka nenyama ngokuhlwa; wawusela umlambo.', 'XHO22'),
(15, 17, 7, 'Ekuhambeni kwexesha umlambo oqukuqelayo woma, ngenxa yokuba kwakungekho mvula elizweni.', 'XHO22'),
(15, 17, 8, 'Kwafika ilizwi likaYehova kuye, lisithi:', 'XHO22'),
(15, 17, 9, 'Yiya eTsarfati, ezweni laseTsidon, uhlale khona; yabona, ndimwisele umthetho umhlolokazi okhona, ukuba akubophe.', 'XHO22'),
(15, 17, 10, 'Wavuka ke, waya eSafati. Wathi akufika esangweni lesixeko, nanko umhlolokazi ethwele iinkuni; Wambiza wathi: Ndiziseleni iemele yamanzi ndisele.', 'XHO22'),
(15, 17, 11, 'Eya kuyithabatha, wayibiza wathi, Ndinike iqhekeza lesonka esandleni sakho.', 'XHO22'),
(15, 17, 12, 'Wathi, NgoYehova uThixo wakho ohleliyo, andinasonka, ingengumgubo ongazalisa isandla emphandeni, nentwana yeoli ngesitya; nanku ke nditheza iinkuni ezimbini, ukuba ndiye ndenzele mna nonyana wam, sizidle, sife.', 'XHO22'),
(15, 17, 13, 'Wathi uEliya kuye, Musa ukoyika; hamba uye kwenza njengoko uthethile; kodwa kuqala ndenzele isonkana ngawo, undizisele, wenzele wena nonyana wakho.', 'XHO22'),
(15, 17, 14, 'Ngokuba utsho uYehova, uThixo kaSirayeli, ukuthi, Umgubo osembizani awuyi kuphela, neoli ayiyi kuphela, ide iphele, ide ibe yimini uYehova aya kuyinisa imvula phezu komhlaba.', 'XHO22'),
(15, 17, 15, 'Wahamba ke, wenza ngokwelizwi likaEliya, wadla yena nendlu yakhe imihla emininzi.', 'XHO22'),
(15, 17, 16, 'Kodwa umgubo owawusembizani awuphelanga, neoli eyayisembizeni ayizange iphele, ngokwelizwi likaYehova awayelithethile ngomkhonzi kaEliya.', 'XHO22'),
(15, 17, 17, 'Emveni kwezi zinto, wahliwa sisifo unyana waloo nkazana, engumninindlu; yaye isigulo sakhe saba sibi kangangokuba akwabikho moya kuye.', 'XHO22'),
(15, 17, 18, 'Wathi kuEliya, Yintoni na enam nawe, mfo wakwaThixo? Uza kum ukukhumbula isono sam, umbulale unyana wam?', 'XHO22'),
(15, 17, 19, 'Wathi kuye, Ndinike unyana wakho. Wamsusa esifubeni sakhe, wamsa emkhumbini apho wayehlala khona, wamlalisa esingqengqelweni sakhe.', 'XHO22'),
(15, 17, 20, 'Wanqula uYehova, wathi, Yehova, Thixo wam, umtshabalalisile na umhlolokazi lo bendihleli kuye ngokubulala unyana wakhe?', 'XHO22'),
(15, 17, 21, 'Wathoba phezu komntwana izihlandlo zazithathu, wathandaza kuYehova, esithi, Yehova, Thixo wam, wubuyisele kuye umphefumlo walo mntwana.', 'XHO22'),
(15, 17, 22, 'Waliva uYehova ilizwi likaEliya; wabuyela kuye umoya wenkwenkwe, waphila.', 'XHO22'),
(15, 17, 23, 'uEliya wamthabatha umntwana, wehla naye, waya naye egumbini eliphezulu, wamngenisa endlwini, wamnikela kunina; Wathi uEliya, Uyabona, unyana wakho uhleli.', 'XHO22'),
(15, 17, 24, 'Wathi loo mfazi kuEliya, Kaloku ke ndiyazi ukuba uyindoda yakwaThixo wena, nelizwi likaYehova elisemlonyeni wakho liyinyaniso.', 'XHO22'),
(15, 18, 1, 'Kwathi kwakuba mzuzu, lafika ilizwi likaYehova kuEliya ngomnyaka wesithathu, lathi, Hamba uye kubonana noAhabhi; ndiya kunisa imvula phezu komhlaba.', 'XHO22'),
(15, 18, 2, 'Wahamba waya kubonana uEliya kuAhabhi. Kwabakho ke indlala enkulu kwaSamari.', 'XHO22'),
(15, 18, 3, 'uAhabhi wabiza uObhadiya, intloko yendlu yakhe. (Ke uObhadiya wayemoyika kakhulu uYehova.', 'XHO22'),
(15, 18, 4, 'Xa uIzebhele wabatshabalalisa abaprofeti bakaYehova, uObhadiya wathabatha abaprofeti abalikhulu waza wabafihla emqolombeni babe ngamaqela angamashumi amahlanu waza wabondla ngesonka namanzi.)', 'XHO22'),
(15, 18, 5, 'Wathi uAhabhi kuObhadiya, Hamba uye emhlabeni, emithonjeni yonke yamanzi nasemilanjeni yonke; Mhlawumbi sofumana utyani bokugcina amahashe noondlebende, ukuze singalahlekelwa zizilwanyana zonke.', 'XHO22'),
(15, 18, 6, 'Babelana ngelizwe lokuya kulo; wahamba yedwa uAhabhi ngenye indlela, wahamba yedwa uObhadiya.', 'XHO22'),
(15, 18, 7, 'Uthe uObhadiya esendleleni, wabona uEliya emkhawulela; wambona, wawa ngobuso, wathi, UnguEliya na, nkosi yam?', 'XHO22'),
(15, 18, 8, 'Waphendula ke yena wathi, Ndinguye; hamba uye kuthi enkosini yakho, Nanku elapha uEliya.', 'XHO22'),
(15, 18, 9, 'Wathi, Ndenze ntoni na, ukuba umnikele umkhonzi wakho esandleni sika-Ahabhi ukuba andibulale?', 'XHO22'),
(15, 18, 10, 'NgoYehova uThixo wakho ohleliyo, akubangakho luhlanga nabukumkani, angàthumanga ukuba akufunele ukumkani wam; bathi, Akakho; wafunga kurhulumente nakubantu, ukuba abayi kuyifumana.', 'XHO22'),
(15, 18, 11, 'Kaloku ke uthi, Hamba uye kuthi enkosini yakho, ithi, Nanku elapha uEliya.', 'XHO22'),
(15, 18, 12, 'Ekushiyeni kwam umoya kaYehova wonisa apho ndingaziyo; ukuba ndiye kuxelela uAhabhi, angakufumani, andibulale; kodwa mna, umkhonzi wakho, bendimoyika uYehova ukususela ebutsheni bam.', 'XHO22'),
(15, 18, 13, 'Ayixelelwanga na inkosi yam into endayenzayo xa uIzebhele wayebulala abaprofeti bakaYehova, indlela endabafihla ngayo abaprofeti bakaYehova abalikhulu elinamashumi amahlanu emqolombeni ndaza ndabondla ngesonka namanzi?', 'XHO22'),
(15, 18, 14, 'Kaloku ke uthi, Hamba uye kuthi enkosini yakho, uthi, Nanku elapha uEliya, aze andibulale.', 'XHO22'),
(15, 18, 15, 'Wathi uEliya, Ehleli nje uYehova wemikhosi, endimiyo phambi kwakhe, namhlanje ndiya kuzibonakalisa kuye.', 'XHO22'),
(15, 18, 16, 'uObhadiya waya kumkhawulela ke uAhabhi, wamxelela; wahamba uAhabhi, waya kumkhawulela uEliya.', 'XHO22'),
(15, 18, 17, 'Uthe ke uAhabhi akumbona uEliya, wathi kuye: “Nguwe na lo umoyikisa amaSirayeli?', 'XHO22'),
(15, 18, 18, 'Wathi, Andimoyikisanga amaSirayeli; wena nendlu kayihlo, ngenxa yokuba niyicekisile imithetho kaYehova, walandela uBhahali.', 'XHO22'),
(15, 18, 19, 'Ke kaloku thumela uze uhlanganisele kum wonke uSirayeli eNtabeni yeKarmele, abaprofeti bakaBhahali abangamakhulu amane anamashumi amahlanu namakhulu amane abaprofeti bemithi engcwele abadla etafileni kaIzebhele.', 'XHO22'),
(15, 18, 20, 'Emva koko uAhabhi wathumela ilizwi kubo bonke oonyana bakaSirayeli waza wabahlanganisela ndawonye abaprofeti eNtabeni yeKarmele.', 'XHO22'),
(15, 18, 21, 'Wafika uEliya ebantwini abo bonke, wathi, Koda kube nini na nimi phakathi kweentliziyo ezimbini? ukuba uYehova nguThixo, landelani yena; Ke yena uBhahali landelani yena. Akamphendula isihlwele.', 'XHO22'),
(15, 18, 22, 'Wathi uEliya ebantwini, Kusele mna ndodwa umprofeti kaYehova; Ke abaprofeti bakaBhahali ngamadoda angamakhulu amane anamanci mahlanu.', 'XHO22'),
(15, 18, 23, 'Sinikeni iinkunzi ezintsha zibe mbini; zinyuleleni inkunzi entsha ibe nye, niyityatye, niyibeke phezu kweenkuni, ningafaki mlilo; nenye inkunzi entsha ndiyibeke phezu kweenkuni, ndingabasi mlilo phantsi kwayo.', 'XHO22'),
(15, 18, 24, 'Bizani igama loothixo benu, ndithandaze ke igama likaYehova; yaye uThixo ophendula ngomlilo makabe nguThixo. Baphendula bonke abantu, bathi, Lilungisile ilizwi lakho.', 'XHO22'),
(15, 18, 25, 'Wathi uEliya kubaprofeti bakaBhahali, Zinyuleleni inkunzi entsha ibe nye, niyilungise kuqala; ngokuba nibaninzi; nqulani igama lothixo wenu, ningafaki mlilo phantsi kwenu.', 'XHO22'),
(15, 18, 26, 'Bayithabatha inkunzi yenkomo ababeyinikiwe, balilima, balibiza igama likaBhahali kususela kusasa kwada kwasemini enkulu, besithi: “Owu, Bhahali, siphulaphule! Kodwa akuzange kubekho lizwi, nditsho nempendulo. Kwaye baxhumela phezulu esibingelelweni esasenziwe.', 'XHO22'),
(15, 18, 27, 'Ke kaloku, kwakufika imini enkulu, uEliya wadlala ngabo, wathi, Dumani kakhulu, ngokuba ndinguThixo; mhlawumbi uyathetha, okanye uyaleqa, okanye uyahamba, okanye mhlawumbi ulele kwaye ufuna ukuvuka.', 'XHO22'),
(15, 18, 28, 'Badanduluka kakhulu, bazicenta ngeemela nangezikhali ngokwesiko labo, lada laphalaza igazi phezu kwabo.', 'XHO22'),
(15, 18, 29, 'Kwahlwa, yaye baprofeta de kwafika ixesha lombingelelo wangokuhlwa, akwabakho lizwi okanye uphendulayo okanye nabani na owaphulaphulayo.', 'XHO22'),
(15, 18, 30, 'Wathi uEliya ebantwini bonke, Sondelani kum apha. Basondela kuye bonke abantu. Waza ke wasihlaziya isibingelelo sikaYehova esasidiliziwe.', 'XHO22'),
(15, 18, 31, 'uEliya wathabatha amatye alishumi elinamabini ngokwenani lezizukulwana zoonyana bakaYakobi, elafikayo kubo ilizwi likaYehova, lisithi, Igama lakho liya kuba nguSirayeli;', 'XHO22'),
(15, 18, 32, 'Wakha isibingelelo ngamatye egameni likaYehova; Wenza umsele olingene iiseha ezimbini zembewu, ngeenxa zonke esibingelelweni.', 'XHO22'),
(15, 18, 33, 'Wazilungisa iinkuni, wayityatya inkunzi entsha, wayibeka phezu kweenkuni, wathi, Zalisani iiemele zibe zine ngamanzi, niwagalele phezu kwedini elinyukayo, naphezu kweenkuni.', 'XHO22'),
(15, 18, 34, 'Wathi, Yenzani okwesibini. Bayenza ke okwesibini. Wathi, Yenzani okwesithathu. Bayenza ke okwesithathu.', 'XHO22'),
(15, 18, 35, 'Amanzi ahambahamba ngeenxa zonke esibingelelweni; wawuzalisa nomsele ngamanzi.', 'XHO22'),
(15, 18, 36, 'Ngexesha lombingelelo wangokuhlwa, umprofeti uEliya wasondela waza wathi: “Yehova, Thixo ka-Abraham, kaIsake, noSirayeli, makwazeke namhla ukuba uThixo wakho usezweni lakwaSirayeli; nokuba ndingumkhonzi wakho, ukuze ndizenze zonke ezi zinto ngelizwi lakho.', 'XHO22'),
(15, 18, 37, 'Ndiphendule, Yehova, ndiphendule, bazi aba bantu ukuba wena Yehova unguYehova, nokuba nguwe oyibuyiseleyo intliziyo yabo.', 'XHO22'),
(15, 18, 38, 'Wehla ke umlilo kaYehova, walidla idini elinyukayo, kwaneenkuni, kwanamatye, kwanothuli, wawakhotha namanzi emhadini.', 'XHO22'),
(15, 18, 39, 'Bakukubona oko abantu bonke, bawa ngobuso, bathi: “uYehova nguThixo! Yehova, ndinguThixo.', 'XHO22'),
(15, 18, 40, 'Wathi uEliya kubo, Babambeni abaprofeti bakaBhahali; akukho namnye kubo osindileyo. Bababamba, uEliya wabahlisela emlanjaneni oyiKishon, wababulalela khona.', 'XHO22'),
(15, 18, 41, 'Wathi uEliya kuAhabhi, Vuka, udle, usele; ngokuba kuvakala isandi semvula enkulu.', 'XHO22'),
(15, 18, 42, 'Wenyuka ke uAhabhi, waya kudla, wasela. Wenyuka uEliya, waya encotsheni yeKarmele; waqubuda, wabubeka ubuso bakhe phakathi kwamadolo akhe;', 'XHO22'),
(15, 18, 43, 'Wathi kwindodana yakwakhe, Khawunyuke, ukhangele elwandle; Yenyuka, yakhangela, yathi, Akukho nto. Wathi, Buyela umva, kasixhenxe.', 'XHO22'),
(15, 18, 44, 'Wathi ngomhla wesixhenxe, Nanko, kuphuma intaka elwandle, engathi sisandla somntu; Wathi, Nyuka, uthi kuAhabhi, Lungisa inqwelo yakho, uhle, hleze imvula ingakuyeki.', 'XHO22'),
(15, 18, 45, 'Lasuka laba mnyama izulu ngamafu nomoya, akwabakho mvula inkulu. Wakhwela uAhabhi, waya eYizereli.', 'XHO22'),
(15, 18, 46, 'Sabakho ke isandla sikaYehova kuEliya; wabhinqa esinqeni sakhe waza wabaleka phambi kuka-Ahabhi de waya kufika kumnyango waseYizereli.', 'XHO22'),
(15, 19, 1, 'uAhabhi wamxelela uIzebhele konke awakwenzayo uEliya, nokubabulala kwakhe abaprofeti bonke ngentshuntshe.', 'XHO22'),
(15, 19, 2, 'uIzebhele wathuma umthunywa kuEliya, esithi, Mabenjenje oothixo kum, bande, ukuba ngomso eli xa andiwuwenzanga umphefumlo wakho, ube njengomnye wabo.', 'XHO22'),
(15, 19, 3, 'Uthe akubona oko, wesuka wasaba, esindisa umphefumlo wakhe, wafika eBher-shebha yakwaYuda, wamshiya khona umkhonzi wakhe.', 'XHO22'),
(15, 19, 4, 'Wahamba entlango uhambo lwemini enye, wafika wahlala phantsi konwele; waye yena ecinga ukuba ubeya kufa; Wathi, Kwanele; ngoku, Yehova, wuthabathe umphefumlo wam; ngokuba andilungile kunobawo.', 'XHO22'),
(15, 19, 5, 'Kwathi, elele phantsi konwele, kwabonakala isithunywa sezulu simchukumisa, sathi kuye, Vuka udle.', 'XHO22'),
(15, 19, 6, 'Wakhangela, nantso iqebengwana elosiweyo lesonka, neselwa lamanzi phezu kwentloko yakhe. Watya, wasela, waphinda walala.', 'XHO22'),
(15, 19, 7, 'Sabuya isithunywa sikaYehova okwesibini, samchukumisa, sathi, Vuka, udle; kuba uhambo lukhulu kakhulu kuwe.', 'XHO22'),
(15, 19, 8, 'Wavuka, wadla, wasela, waza ngamandla oko kudla wahamba iimini ezingamashumi amane nobusuku obungamashumi amane ukuya kutsho eHorebhe, intaba yoThixo oyinyaniso.', 'XHO22'),
(15, 19, 9, 'Wangena emqolombeni, walala khona; Nalo izwi likaYehova lifika kuye, lathi kuye, Uyintoni na apha, Eliya?', 'XHO22'),
(15, 19, 10, 'Wandula ke wathi: “Ndandikhweletela kakhulu ngoYehova uTHIXO wemikhosi, ngenxa yokuba oonyana bakaSirayeli bewushiyile umnqophiso wakho, bazidiliza izibingelelo zakho baza bababulala abaprofeti bakho ngekrele; ndasala ndedwa; kwaye bafuna ukuthabatha ubomi bam.', 'XHO22'),
(15, 19, 11, 'Wathi, Phumani, nime entabeni phambi koYehova. Nanko uYehova edlula, kwavuthuza iintaba umoya omkhulu onamandla, wawaqhekeza amawa phambi koYehova; kodwa uYehova wayengekho kumoya, yaye emva komoya kwabakho inyikima kodwa uYehova wayengekho kuloo nyikima.', 'XHO22'),
(15, 19, 12, 'Emva kwendudumo kwabakho umlilo; kodwa uYehova wayengekho emlilweni, yaye emva komlilo kwakukho ilizwi elizolileyo.', 'XHO22'),
(15, 19, 13, 'Akukuva oko uEliya, wabugquma ubuso bakhe ngesambatho sakhe sangaphezulu, waphuma wema emnyango womqolomba nanko kufika kuye ilizwi, lisithi, Wenza ntoni na apho, Eliya?', 'XHO22'),
(15, 19, 14, 'Wathi, Ndenziwe likhwele ngoYehova, uThixo wemikhosi; ngokuba bewushiyile umnqophiso wakho oonyana bakaSirayeli, nezibingelelo zakho bazidiliza, abaprofeti bakho bababulala ngekrele; ndasala ndedwa; kwaye bafuna ukuthabatha ubomi bam.', 'XHO22'),
(15, 19, 15, 'Wathi uYehova kuye, Hamba ubuyele entlango yaseDamasko; ekufikeni kwakho, uthambise uHazayeli, abe ngukumkani wakwa-Aram.', 'XHO22'),
(15, 19, 16, 'uYehu, unyana kaNimsi, wamthambisa ukuba abe ngukumkani wakwaSirayeli; uthambise uElisha, unyana kaShafati, waseAbelemhola, ukuba abe ngumprofeti esikhundleni sakho.', 'XHO22'),
(15, 19, 17, 'Wothi uYehu ambulale osinde ekreleni likaHazayeli; osinde ekreleni likaYehu, uElisha uya kumbulala.', 'XHO22'),
(15, 19, 18, 'Ke ndishiye amaSirayeli angamawaka asixhenxe, angaguqanga amadolo onke kuBhahali, imilebe yonke engekamanga.', 'XHO22'),
(15, 19, 19, 'Wesuka khona, wafumana uElisha, unyana kaShafati, elima, phambi kwakhe ngamahashe alishumi elinamabini; yena engoweshumi elinesibini; uEliya wadlula ngakuye waza waphosa isambatho sakhe sangaphezulu phezu kwakhe.', 'XHO22'),
(15, 19, 20, 'Wazishiya iinkabi, wabaleka emva koEliya, esithi, Makhe ndiye kwanga ubawo noma, ndikulandele ke. Wathi kuye, Buya; ndikwenze ntoni na?', 'XHO22'),
(15, 19, 21, 'Wabuya ke ekumlandeleni, wazithabatha iinkomo, wabingelela, wayipheka inyama yazo ngezidlele zeenkomo, wanika abantu, badla. Wesuka ke, wamlandela uEliya, wamkhonza.', 'XHO22'),
(15, 20, 1, 'uBhen-hadade ukumkani wakwa-Aram wayihlanganisa yonke impi yakhe, enookumkani abamashumi mathathu anababini, namahashe, neenqwelo zokulwa; wenyuka waya kungqinga umzi wakwaSamari, walwa nawo.', 'XHO22'),
(15, 20, 2, 'Wathumela abathunywa kuAhabhi, ukumkani wakwaSirayeli, phakathi komzi, wathi kuye, Utsho uBhen-hadade ukuthi,', 'XHO22'),
(15, 20, 3, 'Isilivere yakho negolide yakho yeyam; nabafazi bakho nabantwana bakho, abona bahle kakhulu, ngabam.', 'XHO22'),
(15, 20, 4, 'Waphendula ukumkani wakwaSirayeli, wathi, Ngokwelizwi lakho, kumkani, nkosi yam; Ndingowakho, nako konke endinako.', 'XHO22'),
(15, 20, 5, 'Babuya abathunywa, bathi, uBhen-hadade uthe, Noko ndithumele kuwe, ndithi, ndinike isilivere yakho, negolide yakho, nabafazi bakho, nabantwana bakho;', 'XHO22'),
(15, 20, 6, 'Kodwa ngomso eli xa ndiya kuthumela abakhonzi bam kuwe, kwaye baya kuyihlola indlu yakho nezindlu zabakhonzi bakho; kwaye nantoni na ekholekileyo emehlweni akho baya kuyinikela esandleni sabo, bayithabathe.', 'XHO22'),
(15, 20, 7, 'Wandula ke ukumkani wakwaSirayeli wabiza onke amadoda amakhulu elizwe waza wathi: “Lumkani nize nibone indlela le ndoda ifuna ngayo ububi, ekubeni ithumele kum ibiza abafazi bam nabantwana bam nesilivere yam negolide yam; nam andamkhanyela.', 'XHO22'),
(15, 20, 8, 'Athi kuye onke amadoda amakhulu, nabantu bonke, Musani ukumphulaphula, musani ukukholwa nguye.', 'XHO22'),
(15, 20, 9, 'Wathi kubathunywa bakaBhen-hadade, Nothi enkosini yam ukumkani, Yonke into obumthumele kuyo umkhonzi wakho ngaphambili, ndoyenza; kodwa le nto andiyi kuyenza. Bahamba abathunywa, babuya bamxelela.', 'XHO22'),
(15, 20, 10, 'Wathumela uBhen-hadade kuye, wathi, Oothixo bandenzile, banda ngokunjalo, ukuba luthe uthuli lwakwaSamari lwazala izandla zabantu bonke abandilandelayo.', 'XHO22'),
(15, 20, 11, 'Waphendula ukumkani wakwaSirayeli, wathi, Yithini kuye, Makangaqhayisi onxiba iingubo zakhe, njengozikhululayo.', 'XHO22'),
(15, 20, 12, 'Waliva uBhen-hadade elo lizwi, esasela, yena nookumkani abo ngasezintenteni, wathi kubakhonzi bakhe, Lungisani. Bawuhlasela umzi.', 'XHO22'),
(15, 20, 13, 'Kwabonakala mprofeti uthile, wafika kuAhabhi ukumkani wakwaSirayeli, wathi, uYehova uthi, Uyibonile na le ngxokolo ingaka? yabonani, ndiyawunikela esandleni senu namhla; nazi ukuba ndinguYehova.', 'XHO22'),
(15, 20, 14, 'Wathi uAhabhi, Ngabani na? Wathi uYehova, Ngamadodana abathetheli bamazwe. Wathi, Ngubani na oya kumisela imfazwe? Waphendula ke yena wathi, Wena!', 'XHO22'),
(15, 20, 15, 'Wawabala amadodana abathetheli bamazwe, aba ngamakhulu amabini anamanci mathathu anamabini; yaye emva kwabo wababala bonke abantu, bonke oonyana bakaSirayeli, bangamawaka asixhenxe.', 'XHO22'),
(15, 20, 16, 'Baphuma ke emva kwemini. Ke uBhen-hadade wanxila ezintenteni, yena nookumkani abamashumi mathathu anababini, ababemncedisa.', 'XHO22'),
(15, 20, 17, 'Aphuma kuqala amadodana abathetheli bamazwe; Wathumela uBhen-hadade ukuba amxelele; kwathiwa, Kufike amadoda kwaSamari.', 'XHO22'),
(15, 20, 18, 'Wathi, Ukuba baphume benoxolo, babambe behleli; ukuba bathe baya emfazweni, babambe behleli;', 'XHO22'),
(15, 20, 19, 'Aphuma ke la madodana abathetheli bamazwe phakathi komzi, enomkhosi osemva kwawo.', 'XHO22'),
(15, 20, 20, 'Elowo wayibulala indoda yakhe, asaba ama-Aram; AmaSirayeli abasukela, waza uBhen-hadade ukumkani wakwa-Aram wasaba ekhwele ihashe nabakhweli.', 'XHO22'),
(15, 20, 21, 'Waphuma ukumkani wakwaSirayeli, wawaxabela amahashe neenqwelo zokulwa, wawaxabela kakhulu ama-Aram.', 'XHO22'),
(15, 20, 22, 'Wafika umprofeti lowa kukumkani wakwaSirayeli, wathi kuye, Hamba uye kuzomeleza, uhlale, ubone into oya kuyenza; ngokuba ekufikeni komthombo, ukumkani wakwa-Aram uya kukuhlasela.', 'XHO22'),
(15, 20, 23, 'Bathi kuye abakhonzi bokumkani bakwa-Aram, Ngoothixo basezintabeni oothixo babo; ke ngoko bomelele kunathi; kodwa mabalwe entlango, yaye ngokuqinisekileyo siya kuboyisa.', 'XHO22'),
(15, 20, 24, 'Yenza le nto: basuse ookumkani elowo endaweni yakhe, ubeke abathetheli ezingontsini zabo;', 'XHO22'),
(15, 20, 25, 'Zibale iintendelezo njengeentendelezo zakho, amahashe abe ngangaloo mahashe, inqwelo ngenqwelo; kwaye siya kulwa nabo entlango, kwaye ngokuqinisekileyo siya kubadlula ngamandla. Waliphulaphula ilizwi labo, wenjenjalo.', 'XHO22'),
(15, 20, 26, 'Kwathi ekuqaleni kwentwasahlobo, uBhen-hadade wawabala ama-Aram, wenyuka waya kulwa noonyana bakaSirayeli eAfeki.', 'XHO22'),
(15, 20, 27, 'Babalwa oonyana bakaSirayeli, baba khona bonke, babalwa; ke ama-Aram alizalisa ilizwe.', 'XHO22'),
(15, 20, 28, 'Weza umfo wakwaThixo, wathetha kukumkani wakwaSirayeli, wathi, Kutsho uYehova ukuthi, Ekubeni ethe ama-Aram, NguThixo weentaba uYehova, kodwa akanguThixo weembombo; yonke le ngxokolo ingaka yoyinikela esandleni senu; nazi ukuba ndinguYehova.', 'XHO22'),
(15, 20, 29, 'Bamisa ke, elowo ekhangelene iintsuku zasixhenxe. Kwabakho imfazwe ngomhla wesixhenxe; oonyana bakaSirayeli babulala kuma-Aram ikhulu lamawaka angumqikela ngamini-nye.', 'XHO22'),
(15, 20, 30, 'Inxenye yasabela eAfeki umzi; lwawa udonga phezu kwamashumi amabini anesixhenxe amawaka amadoda aseleyo. Wasaba uBhen-hadade, wangena engontsini engaphakathi yomzi.', 'XHO22'),
(15, 20, 31, 'Bathi kuye abakhonzi bakhe, Yabona, sivile ukuba ookumkani bendlu kaSirayeli ngookumkani abanenceba; masithi, sibeke iingubo zokulila, sibeke iintshinga ezintlokweni zethu, siphume siye kukumkani wakwaSirayeli;', 'XHO22'),
(15, 20, 32, 'Babhinqa ke iingubo ezimnyama emanqeni, bekhonkxa ngeentsontelo ezintlokweni zabo, baya kukumkani wakwaSirayeli, bathi, Umkhonzi wakho uBhen-hadade ubesithi, Mawusindise umphefumlo wam. Athi: Usaphila? Ngumntakwethu.', 'XHO22'),
(15, 20, 33, 'Loo madoda ayelumke ukuze abone ukuba kuya kuphuma nto na kuye, akhawuleza ukuyibamba; Bathi, Bhen-hadade, mzalwana. Wathi, Yiyani, nimthabathe. Waphuma uBhen-hadade, waya kuye; kwaye makangene emotweni.', 'XHO22'),
(15, 20, 34, 'Wathi uBhen-hadade kuye, Ndiya kuyibuyisa imizi awayithabathayo ubawo kuyihlo; Uya kuzenzela izitrato eDamasko, njengoko wazenzelayo ubawo kwaSamari. Wathi uAhabhi, Ndiya kukundulula nalo mnqophiso. Wenza isivumelwano naye waza wambuyisa.', 'XHO22'),
(15, 20, 35, 'Wathi omnye koonyana babaprofeti kummelwane wakhe ngelizwi likaYehova, Ndibethe! Akavuma loo mntu ukumbetha.', 'XHO22'),
(15, 20, 36, 'Yathi kuye, Ekubeni ungaliphulaphulanga ilizwi likaYehova, yabona, ukuba uthe wemka kum, ingonyama iya kukubulala. Uthe ke akumshiya, wafunyanwa yingonyama, yambulala.', 'XHO22'),
(15, 20, 37, 'Wafumana mntu wumbi, wathi, Ndibethe. Yandula ke indoda leyo yambetha, kangangokuba yamlimaza ngokumbetha.', 'XHO22'),
(15, 20, 38, 'Wahamba ke umprofeti lowo, wamlinda ukumkani endleleni, wazigqubuthela ngothuthu ubuso bakhe.', 'XHO22'),
(15, 20, 39, 'Wadlula ukumkani, wambiza ukumkani, wathi, Umkhonzi wakho lo uvela edabini; nanko kuphenduka enye indoda, yezisa indoda kum, yathi, Gcina le ndoda;', 'XHO22'),
(15, 20, 40, 'Wathi akuxakeka ngumkhonzi wakho lo, wemka. Wathi ukumkani wakwaSirayeli kuye, Umgwebo wakho woba wenjenje; Wenze isigqibo.', 'XHO22'),
(15, 20, 41, 'Wakhawuleza walususa uthuthu ebusweni bakhe; yaye ukumkani wakwaSirayeli wamkhetha njengomnye wabaprofeti.', 'XHO22'),
(15, 20, 42, 'Wathi kuye: “Utsho uYehova ukuthi, Ekubeni uye wakhulula indoda esandleni sakho endayiwisela umthetho wokuba ayitshabalalise, umphefumlo wakho uya kuba sesikhundleni somphefumlo wayo, nabantu bakho esikhundleni sabantu bayo.', 'XHO22'),
(15, 20, 43, 'Waba buhlungu ukumkani wakwaSirayeli waza wacaphuka endlwini yakhe waza waya eSamariya.', 'XHO22'),
(15, 21, 1, 'Emva koko uNabhoti waseYizereli ebenesidiliya eYizereli kufuphi nebhotwe lika-Ahabhi ukumkani waseSamariya.', 'XHO22'),
(15, 21, 2, 'Wathetha uAhabhi kuNabhoti, wathi, Ndinike isidiliya sakho, ndisenze umyezo wemifuno, ngokuba sikufuphi nendlu yam; kwaye ngenxa yoko ndiya kukunika isidiliya esilungileyo kunesi; okanye ukuba kulungile, ndokunika ixabiso layo ngemali.', 'XHO22'),
(15, 21, 3, 'Wathi uNabhoti kuAhabhi, Ngamana uYehova angandithintela ekukunikeni ilifa loobawo!', 'XHO22'),
(15, 21, 4, 'Wangena uAhabhi endlwini yakhe, ethe khunubembe, equmbile, lilizwi abelithethile kuye uNabhoti waseYizereli; ngokuba wathi, Andiyi kukunika ilifa loobawo. Walala esingqengqelweni sakhe, wabujika ubuso bakhe, akadla sonka.', 'XHO22'),
(15, 21, 5, 'Weza kuye uIzebhele umkakhe, wathi kuye, Yini na ukuba ube mbi umphefumlo wakho, ungadli sonka?', 'XHO22'),
(15, 21, 6, 'Wathi kuye, Ngokuba ndithethe noNabhoti waseYizereli, ndathi kuye, Ndinike isidiliya sakho ngemali; okanye, ukuba uyavuma, ndokunika esinye isidiliya esikhundleni saso; waphendula wathi, Andiyi kukunika isidiliya sam.', 'XHO22'),
(15, 21, 7, 'Wathi uIzebhele, umkakhe, kuye, Nguwe na olawula ebukumkanini bakwaSirayeli? Vuka, udle isonka, igcobe intliziyo yakho; Ndiza kukunika isidiliya sikaNabhoti waseYizereli.', 'XHO22'),
(15, 21, 8, 'Wabhala iincwadi egameni lika-Ahabhi waza waziqinisa ngetywina lakhe, wazithumela ezo ncwadi kumadoda amakhulu nakwizidwangube ezazihlala kwisixeko sakhe esasihlala noNabhoti.', 'XHO22'),
(15, 21, 9, 'Wabhala kwezo ncwadi, wathi, Memezani umhla wokudla, nimmise uNabhoti encotsheni yabantu.', 'XHO22'),
(15, 21, 10, 'Kwabekwa phambi kwakhe amadoda amabini angamatshijolo, ukuba angqine ngaye, athi, Uthuke uThixo nokumkani; nimkhuphele phandle, nimxulube ngamatye, afe.', 'XHO22'),
(15, 21, 11, 'Amadoda omzi wakowabo, izikhulu nabanumzana ababehlala emzini wakhe, enza njengoko abewathumele uIzebhele, njengoko kubhaliweyo ezincwadini zakhe;', 'XHO22'),
(15, 21, 12, 'Bavakalisa ukuba kuzilwe ukutya, baza bammisa uNabhoti endaweni ephakamileyo phakathi kwabantu.', 'XHO22'),
(15, 21, 13, 'Kwangena amadoda amabini, oonyana bakaBheliyali, ahlala phambi kwakhe, aza loo madoda angamatshijolo anikela ubungqina nxamnye noNabhoti phambi kwabantu, esithi: “uNabhoti uthuke uThixo nokumkani. Bamkhuphela ngaphandle komzi, bamgibisela ngamatye, wafa.', 'XHO22'),
(15, 21, 14, 'Bathumela kuIzebhele, bathi, uNabhoti ugityiselwe ngamatye, wafa.', 'XHO22'),
(15, 21, 15, 'Wathi uIzebhele akuva ukuba uNabhoti ugityiselwe ngamatye wafa, uIzebhele wathi kuAhabhi: “Sukuma, uthabathe isidiliya sikaNabhoti waseYizereli, abengafuni ukukunika sona ngemali, kuba uNabhoti akaphilanga kodwa bafile.', 'XHO22'),
(15, 21, 16, 'Weva uAhabhi ukuba uNabhoti ufile, wesuka wehla, waya kusilungisa isidiliya sikaNabhoti waseYizereli.', 'XHO22'),
(15, 21, 17, 'Kwafika ilizwi likaYehova kuEliya Motishebhe, lisithi:', 'XHO22'),
(15, 21, 18, 'Suka, uhle, uye kukhawulela uAhabhi ukumkani wakwaSirayeli okwaSamari; Nanko esesidiliyeni sikaNabhoti, apho ubehle waya kusisebenza.', 'XHO22'),
(15, 21, 19, 'Uze uthethe kuye uthi, Utsho uYehova ukuthi, Ubulele na, waba sisityebi na? Uze uthethe kuye, uthi, Utsho uYehova ukuthi, Apho izinja zalixhaphela khona igazi likaNabhoti, ziya kulingcamla izinja igazi lakho, elilelakho;', 'XHO22'),
(15, 21, 20, 'Wathi uAhabhi kuEliya, Undifumene na, lutshaba lwam? Wathi yena, Ndikufumene, ngenxa yokuba uzithengisele ukwenza okubi emehlweni kaYehova.', 'XHO22'),
(15, 21, 21, 'Yabona, ndikuzisela ububi, ndiyithabathile imbewu yakho, ndinqumle kuAhabhi olwela udonga, nomthinjwa oseleyo kwaSirayeli;', 'XHO22'),
(15, 21, 22, 'ndoyenza indlu yakho ibe njengendlu kaYarobheham unyana kaNebhati, nanjengendlu kaBhahesa unyana ka-Ahiya, ngenxa yezingcikivo owamngcikiva ngazo amaSirayeli.', 'XHO22'),
(15, 21, 23, 'Wathetha uYehova ngoIzebhele, wathi, uIzebhele eludongeni lwaseYizereli uya kudliwa zizinja.', 'XHO22'),
(15, 21, 24, 'Nabani na ka-Ahabhi ofela esixekweni uya kudliwa zizinja; yaye nabani na ofela endle wodliwa ziintaka zezulu.', 'XHO22'),
(15, 21, 25, 'Kodwa akukho bani unjengoAhabhi owazithengisela ukwenza okubi emehlweni kaYehova, owazala uIzebhele umfazi wakhe.', 'XHO22'),
(15, 21, 26, 'Waqhubeka esenza izinto ezininzi ezicekisekayo ngokulandela izithixo ezinezothe, kanye njengoko enzayo ama-Amori awayewagxothe uYehova phambi koonyana bakaSirayeli.', 'XHO22'),
(15, 21, 27, 'Akuweva uAhabhi loo mazwi, wazikrazula iingubo zakhe, wabeka ezirhwexayo emzimbeni, wazila ukudla, walala enxibe ezirhwexayo, wahamba ecotha.', 'XHO22'),
(15, 21, 28, 'Lafika ilizwi likaYehova kuEliya waseTishbhe, lisithi:', 'XHO22'),
(15, 21, 29, 'Ubonile na, ukuzithoba kwakhe uAhabhi phambi kwam? ngenxa enokuba ezithobile phambi kwam, andiyi kuzisa bubi ngexesha lakhe; ndiya kuyizisela ububi indlu yakhe ngemihla yonyana wakhe.', 'XHO22'),
(15, 22, 1, 'Bahlala iminyaka emithathu, kungekho mfazwe phakathi kwama-Aram namaSirayeli.', 'XHO22'),
(15, 22, 2, 'Ngomnyaka wesithathu uYehoshafati, ukumkani wakwaYuda, wehla waya kukumkani wakwaSirayeli.', 'XHO22'),
(15, 22, 3, 'Wathi ukumkani wakwaSirayeli kubakhonzi bakhe, Niyazi na ukuba iRamoti yaseGiliyadi yeyethu, ukuba sithe cwaka nje, singayihluthi esandleni sokumkani wakwa-Aram?', 'XHO22'),
(15, 22, 4, 'Wathi kuYehoshafati, Wohamba na nam, siye eRamoti yaseGiliyadi? Wathi uYehoshafati kukumkani wakwaSirayeli, Ndinjengawe; abantu bam banjengabantu bakho, namahashe am anjengamahashe akho.', 'XHO22'),
(15, 22, 5, 'Wathi uYehoshafati kukumkani wakwaSirayeli, Cela ilizwi likaYehova namhla.', 'XHO22'),
(15, 22, 6, 'Wababiza ukumkani wakwaSirayeli abaprofeti, bekumakhulu amane amadoda, wathi kubo, Ndiye kulwa na neRamoti yaseGiliyadi, ndiyikhulule, kusini na? Athi, Nyuka; ngokuba uYehova uya kuyinikela esandleni sokumkani.', 'XHO22'),
(15, 22, 7, 'Wathi uYehoshafati, Akukho mprofeti kaYehova na apha?', 'XHO22'),
(15, 22, 8, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Kusekho enye indoda, enguMika, unyana kaImla, esingabuza ngayo kuYehova; ke mna ndimthiyile; ngokuba ayiprofeti okulungileyo ngam, nokuba ayiyimbi. Wathi uYehoshafati, Makangatsho uYehova.', 'XHO22'),
(15, 22, 9, 'Waza ukumkani wakwaSirayeli wabiza umphathiswa, wathi, Khawuleza uze apha uMika unyana kaImla.', 'XHO22'),
(15, 22, 10, 'Ukumkani wakwaSirayeli noYehoshafati, ukumkani wakwaYuda, behleli elowo etroneni yakhe, benxibe iingubo zabo elubala, ekungeneni kwesango lakwaSamari; yaye bonke abaprofeti babeprofeta phambi kwabo.', 'XHO22'),
(15, 22, 11, 'uZedekiya, unyana kaKenan, wazenzela iimpondo zesinyithi, wathi, Utsho uYehova ukuthi, Ngezi mpondo uya kuwahlaba ama-Aram, uwatshabalalise.', 'XHO22'),
(15, 22, 12, 'Bonke abaprofeti benjenjalo ukuprofeta, besithi, Nyuka uye eRamoti yaseGiliyadi, ube nempumelelo; ngokuba uYehova uya kuyinikela esandleni sokumkani.', 'XHO22'),
(15, 22, 13, 'Umthunywa obembizile uMika wathetha kuye, wathi, Yabona, amazwi abaprofeti athetha kakuhle ngamlomo mnye kukumkani; ilizwi lakho malibe njengelizwi labo, uthethe okulungileyo.', 'XHO22'),
(15, 22, 14, 'Wathi uMika, Ehleli nje uYehova, oko uYehova aya kukuthetha kum, ndiya kukuxela.', 'XHO22'),
(15, 22, 15, 'Wandula ke waya kukumkani. Wathi uYehova kuye, Mika, siye na siye kulwa neRamoti yaseGiliyadi, siyishiye, kusini na? Wathi kuye, Hamba uphumelele, ngokuba uYehova uya kuyinikela esandleni sokumkani.', 'XHO22'),
(15, 22, 16, 'Wathi ukumkani kuye, Makube zizihlandlo ezingaphi na ndikufungela, ukuba akuyi kundixelela nto yimbi, ingeyiyo nyaniso egameni likaYehova?', 'XHO22'),
(15, 22, 17, 'Wathi, Ndibone amaSirayeli onke elusali ezintabeni, njengeegusha ezingenamalusi; Wathi uYehova, Abanankosi; mababuyele elowo endlwini yakhe ngoxolo.', 'XHO22'),
(15, 22, 18, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Bendingatshongo na kuwe ukuthi, akayi kuprofeta okulungileyo ngenxa yam, woprofeta okubi?', 'XHO22'),
(15, 22, 19, 'Wathi, Khanive ilizwi likaYehova: Ndimbonile uYehova ehleli etroneni yakhe, umi wonke umkhosi wasemazulwini phambi kwakhe, ngasekunene kwakhe nangasekhohlo kwakhe.', 'XHO22'),
(15, 22, 20, 'Waza uYehova wathi: “Ngubani na oya kukhokelela uAhabhi enyuka aze abulawelwe eRamoti yaseGiliyadi? Watsho omnye leya, nomnye wathi.', 'XHO22'),
(15, 22, 21, 'Kwaphuma omnye umoya, wema phambi koYehova, wathi, Ndomlukuhla mna.', 'XHO22'),
(15, 22, 22, 'Wathi uYehova kuye, Ngani na? Wathi yena, Ndoya ndibe ngumoya wobuxoki emlonyeni wabaprofeti bakhe bonke. Wathi ke yena, Uze umeyise, uloyise kananjalo; phuma uyekwenza.', 'XHO22'),
(15, 22, 23, 'Uyabona ke, uYehova ungenise umoya wobuxoki emlonyeni wabo baprofeti bakho bonke; uYehova uthethe okubi ngawe.', 'XHO22'),
(15, 22, 24, 'Wesuka uZedekiya, unyana kaKanan, wafika wambetha uMika esidleleni, wathi, Ugqithe ngayiphi na indlela kum uMoya kaYehova, ukuba aye kuthetha kuwe?', 'XHO22'),
(15, 22, 25, 'Wathi uMika, Yabona, woyibona loo mini, ukuba uthe wangena egumbini, wazimela.', 'XHO22'),
(15, 22, 26, 'Wathi ukumkani wakwaSirayeli, Mthabathe uMika, umse kuAmon umphathi womzi, nakuYowashe unyana wokumkani;', 'XHO22'),
(15, 22, 27, 'Wathi ukumkani, Yifakeni entolongweni le ndoda, niyinike isonka sokubandezeleka namanzi okubandezeleka, ndide ndibuye ndinoxolo.', 'XHO22'),
(15, 22, 28, 'Wathi uMika, Ukuba uthe wabuya unoxolo, woba uYehova akathethanga ngam. Wandula ke wathi: Phulaphulani, nonke ngabanye!', 'XHO22'),
(15, 22, 29, 'Wenyuka ukumkani wakwaSirayeli noYehoshafati, ukumkani wakwaYuda, baya eRamoti yaseGiliyadi.', 'XHO22'),
(15, 22, 30, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Ndiya kuzenza mntu wumbi, ndingene ekulweni; kodwa nxibani iimpahla zenu. Wazenza mntu wumbi ukumkani wakwaSirayeli, wangena ekulweni.', 'XHO22'),
(15, 22, 31, 'Ukumkani wakwa-Aram wabawisela umthetho abathetheli beenqwelo zakhe zokulwa ezimashumi mathathu anababini, wathi, Ize ningalwi nomfana nomkhulu; ize ningalwi nokumkani wakwaSirayeli;', 'XHO22'),
(15, 22, 32, 'Bambona uYehoshafati abathetheli beenqwelo zokulwa, bathi, Nanku ukumkani wakwaSirayeli; Bajika ke, beza kulwa naye, uYehoshafati wamcel’ umngeni.', 'XHO22'),
(15, 22, 33, 'Babona abathetheli beenqwelo zokulwa ukuba akanguye ukumkani wakwaSirayeli, bajika, bamsukela;', 'XHO22'),
(15, 22, 34, 'Indoda ethile yatola isaphetha, yasinda, yambulala ukumkani wakwaSirayeli ekuhlanganeni kotolo kuba ndonzakele.', 'XHO22'),
(15, 22, 35, 'Ukulwa kwenyuka loo mini, ukumkani wahlala enqwelweni yakhe emelene nama-Aram, wafa ngokuhlwa;', 'XHO22'),
(15, 22, 36, 'Lakutshona ilanga, kwavakala isikhalo phakathi komkhosi wonke, sisithi, Elowo makaye emzini wakowabo, elowo aye ezweni lakowabo.', 'XHO22'),
(15, 22, 37, 'Wafa ke ukumkani, waziswa kwaSamari; bamngcwabela ukumkani kwaSamari.', 'XHO22'),
(15, 22, 38, 'Bayihlamba inqwelo edikeni lakwaSamari; zalixhapha izinja igazi lakhe; bazihlamba izixhobo zakhe; ngokwelizwi likaYehova awalithethayo.', 'XHO22'),
(15, 22, 39, 'Ezinye izinto zika-Ahabhi, nako konke awakwenzayo, nendlu awayakhayo ngeempondo zeendlovu, nemizi yonke awayakhayo, azibhalwanga na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(15, 22, 40, 'Walala uAhabhi kuyise; unyana wakhe uAhaziya waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(15, 22, 41, 'uYehoshafati, unyana ka-Asa, waba ngukumkani kwaYuda ngomnyaka wesine ka-Ahabhi ukumkani wakwaSirayeli.', 'XHO22'),
(15, 22, 42, 'uYehoshafati ubeminyaka imashumi mathathu anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mabini anamihlanu engukumkani eYerusalem. Igama lonina belinguAsubha, intombi kaSilehi.', 'XHO22'),
(15, 22, 43, 'Wahamba ngeendlela zonke zika-Asa uyise; makangaphambuki ekwenzeni okuthe tye emehlweni kaYehova; ke iziganga azisuswanga; kuba abantu babesabingelela kwiindawo eziphakamileyo yaye beqhumisa.', 'XHO22'),
(15, 22, 44, 'uYehoshafati wenza uxolo nokumkani wakwaSirayeli;', 'XHO22'),
(15, 22, 45, 'Ezinye izinto zikaYehoshafati, nobugorha bakhe awawenzayo, nokulwa kwakhe, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(15, 22, 46, 'Ke amasalela aseSodom, awasalayo ngemihla kayise uAsa, wawagxotha ezweni.', 'XHO22'),
(15, 22, 47, 'Bekungekho kumkani kwaEdom ngelo xesha; ibamba libe ngukumkani.', 'XHO22'),
(15, 22, 48, 'uYehoshafati wakha iinqanawa ezisuka eTarshishe ukuya kwaOfire, ukuba ziqokelele igolide; ke abemka; ngokuba iinqanawa zaphuka e-Esionngeber.', 'XHO22'),
(15, 22, 49, 'Wathi uAhaziya, unyana ka-Ahabhi, kuYehoshafati, Abakhonzi bam mabahambe nabakhonzi bakho ngeenqanawa. Akavuma uYehoshafati.', 'XHO22'),
(15, 22, 50, 'uYehoshafati walala kooyise, wangcwatyelwa kooyise emzini kaDavide uyise; unyana wakhe uYehoram waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(15, 22, 51, 'uAhaziya, unyana ka-Ahabhi, waba ngukumkani wakwaSirayeli eSamariya ngomnyaka weshumi elinesixhenxe kaYehoshafati ukumkani wakwaYuda, waba ngukumkani wakwaSirayeli iminyaka emibini.', 'XHO22'),
(15, 22, 52, 'Wenza okubi emehlweni kaYehova, wahamba ngeendlela zikayise, nangeendlela zikanina, nangeendlela zikaYarobheham unyana kaNebhati, obekhokele kwaSirayeli;', 'XHO22'),
(15, 22, 53, 'Ngenxa yokuba wakhonza uBhahali waza waqubuda kuye waza wamdelela uYehova uThixo kaSirayeli kanye njengoko wenzayo uyise.', 'XHO22'),
(16, 1, 1, 'Wakreqa ke amaMowabhi kumaSirayeli emva kokufa kuka-Ahabhi.', 'XHO22'),
(16, 1, 2, 'uAhaziya wawa efestileni yegumbi lakhe eliphezulu kwaSamari, wahliwa kukufa; wathuma abathunywa, wathi kubo, Hambani niye kubuzisa kuBhahali-zebhubhi, uthixo wase-Ekron, ukuba ndiya kuphila na kwesi sifo.', 'XHO22'),
(16, 1, 3, 'Sathi isithunywa sikaYehova kuEliya waseTishbha, Hamba uye kukhawulela abathunywa bokumkani wakwaSamari, uthethe kubo, uthi, Kungokuba kungekho Thixo kwaSirayeli na, ukuya kubuzisa kuBhahali-zebhule uthixo wase-Ekron?', 'XHO22'),
(16, 1, 4, 'Ngako oko, utsho uYehova ukuthi, Akusayi kuhla kuloo mandlalo ukhwele kuwo, kodwa uya kufa ngonaphakade. Wahamba ke uEliya.', 'XHO22'),
(16, 1, 5, 'Babuyela kuye abathunywa, wathi kubo, Yini na ukuba nibuye?', 'XHO22'),
(16, 1, 6, 'Bathi kuye, Kufike indoda, yeza kusikhawulela, yathi kuthi, Hambani nibuyele kukumkani onithumileyo, nithi kuye, Utsho uYehova ukuthi, Kungokuba kungekho Thixo na? Sirayeli, ukuba uthe wathumela kubuzisa kuBhahali-zebhubhi, uthixo wase-Ekron? ke ngoko, akusayi kuhla kuloo mandlalo unyuke kuwo, inene, uya kufa.', 'XHO22'),
(16, 1, 7, 'Wathi kubo, Ebenjani na ke yena lowo wahlangana nani, wathetha kuni la mazwi?', 'XHO22'),
(16, 1, 8, 'Bathi ke bona kuye, Indoda leyo, eneenwele ezinde, ebhinqe umbhinqo wentlonze esinqeni sayo. Wathi, Lo nguEliya waseTishbhe.', 'XHO22'),
(16, 1, 9, 'Ukumkani wathumela kuye umthetheli-mashumi-mahlanu, enamashumi omahlanu akhe. Wenyuka waya kuye, wambona ehleli encotsheni yenduli; Wathi kuye, Mfo wakwaThixo, uthe ukumkani, Yihla.', 'XHO22'),
(16, 1, 10, 'Waphendula uEliya, wathi kumthetheli-mashumi-mahlanu, Ukuba ke ndingumfo wakwaThixo, makuhle umlilo ezulwini, ukutshise wena namashumi omahlanu akho. Kwehla umlilo ezulwini, wamtshisa yena namashumi omahlanu akhe.', 'XHO22'),
(16, 1, 11, 'Waphinda wamthumela kuye omnye umthetheli-mashumi-mahlanu, enamashumi omahlanu akhe. Waphendula wathi kuye, Mfo wakwaThixo, uthi ukumkani, Yihla ngokukhawuleza.', 'XHO22'),
(16, 1, 12, 'Waphendula uEliya, wathi kubo, Ukuba ke ndingumfo wakwaThixo, makuhle umlilo ezulwini, ukutshise wena namashumi omahlanu akho. Wehla umlilo kaThixo ezulwini, wamtshisa yena namashumi omahlanu akhe.', 'XHO22'),
(16, 1, 13, 'Waphinda wathuma nowesithathu umthetheli-mashumi-mahlanu, enamashumi omahlanu akhe. Wenyuka ke owesithathu umthetheli-mashumi-mahlanu, waguqa ngamadolo phambi koEliya, wathandaza kuye, wathi kuye, Camagu, mfo wakwaThixo, wusindise umphefumlo wam, nomphefumlo waba bakhonzi bakho bamashumi mahlanu! zixabiseke emehlweni akho.', 'XHO22'),
(16, 1, 14, 'Uyabona, kwehla umlilo ezulwini, wabadla ookumkani bobabini babokuqala, kunye namashumi omahlanu abo; ke ngoko umphefumlo wam uya kunqaba emehlweni akho.', 'XHO22'),
(16, 1, 15, 'Sathi isithunywa sikaYehova kuEliya, Yihla naye; musani ukumoyika. Wesuka wehla naye waya kukumkani.', 'XHO22'),
(16, 1, 16, 'Wathi kuye, Kutsho uYehova ukuthi, Kungokuba kungekho Thixo kwaSirayeli na, le nto uthume abathunywa bokubuza kuBhahali-zebhubhe, uthixo wase-Ekron? ke ngoko, akusayi kuhla kuloo mandlalo unyuke kuwo, inene, uya kufa.', 'XHO22'),
(16, 1, 17, 'Wafa ngokwelizwi likaYehova elathethwa nguEliya. Waba ngukumkani uYoram esikhundleni sakhe, ngomnyaka wesibini kaYoram unyana kaYehoshafati, ukumkani wakwaYuda; ngokuba ebengenanyana.', 'XHO22'),
(16, 1, 18, 'Ezinye izinto zika-Ahaziya awazenzayo, azibhalwanga na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(16, 2, 1, 'Xa uYehova wathabatha uEliya wamsa ezulwini ngesaqhwithi, uEliya wemka eGiligali noElisha.', 'XHO22'),
(16, 2, 2, 'Wathi uEliya kuElisha, Khawuhlale apha; ngokuba uYehova undithume eBheteli. Wathi uElisha kuye, NgoYehova ovayo, nangomphefumlo wakho ophilileyo, andiyi kwahlukana nawe. Emva koko behla baya eBheteli.', 'XHO22'),
(16, 2, 3, 'Baphuma oonyana babaprofeti baseBheteli, baya kuElisha, bathi kuye, Uyazi na kodwa ukuba kutheni na namhlanje uYehova ayisuse inkosi yakho entlokweni yakho? Waphendula ke yena wathi, Ewe, ndiyazi; thula.', 'XHO22'),
(16, 2, 4, 'Wathi uEliya kuye, Elisha, hlala apha; ngokuba uYehova undithume eYeriko. Wathi yena, NgoYehova ovayo, nangomphefumlo wakho ophilileyo, andiyi kwahlukana nawe. Bafika eYeriko.', 'XHO22'),
(16, 2, 5, 'Beza oonyana babaprofeti baseYeriko kuElisha, bathi kuye, Uyazi na kodwa ukuthi, yini na ukuba namhla uYehova ayisuse inkosi yakho entlokweni yakho? Waphendula ke yena wathi, Ewe, ndiyazi; thula.', 'XHO22'),
(16, 2, 6, 'Wathi uEliya kuye, Hlala apha; ngokuba uYehova undithume eYordan. Wathi yena, NgoYehova ovayo, nangomphefumlo wakho ophilileyo, andiyi kwahlukana nawe. Bahamba bobabini.', 'XHO22'),
(16, 2, 7, 'Kwahamba amadoda amashumi mahlanu koonyana babaprofeti, ema ekhangele emgama; bema bobabini ngaseYordan.', 'XHO22'),
(16, 2, 8, 'uEliya wayithabatha ingubo yakhe yangaphezulu, wayisonga, wabetha ngayo emanzini;', 'XHO22'),
(16, 2, 9, 'Badlula, wathi uEliya kuElisha, Cela into, ndikwenzele, ingekasuswa kuwe. Wathi uElisa, Zahlulo ezibini zomphefumlo wakho zibe phezu kwam.', 'XHO22'),
(16, 2, 10, 'Wathi, Ucele into enzima; kodwa ukuba undibonile ndisusiwe kuwe, koba njalo nakuwe; ukuba akunjalo, akuyi kuba njalo.', 'XHO22'),
(16, 2, 11, 'Bathe besathetha, kwabonakala inqwelo yomlilo namahashe omlilo, yabadla bobabini; wenyuka uEliya ngomoya ovuthuzayo, waya emazulwini.', 'XHO22'),
(16, 2, 12, 'uElisha wakubona oko, wadanduluka, wathi, Bawo, bawo, nqwelo yokulwa yakwaSirayeli nabakhwele kuyo! Akaba sambona, wazithabatha iingubo zakhe, wazikrazula ngencakuba.', 'XHO22'),
(16, 2, 13, 'Wayithabatha ingubo enkulu kaEliya, eyayiwe kuye, wabuya, wema elunxwemeni lweYordan;', 'XHO22'),
(16, 2, 14, 'Wayithabatha ingubo enkulu kaEliya, eyayiwe kuye, wabetha ngayo emanzini, wathi, Uphi na uYehova, uThixo kaEliya, yena kanye? wawabetha amanzi, ahlukana, wawela uElisha.', 'XHO22'),
(16, 2, 15, 'Bambona oonyana babaprofeti ababeseYeriko, bathi, Umoya kaEliya uhleli kuElisha. Bamhlangabeza baza baqubuda kuye.', 'XHO22'),
(16, 2, 16, 'Bathi kuye, Yabona, kukho amashumi amahlanu amadoda anobukroti nabakhonzi bakho; Mabahambe baye kufuna ukumkani wakho, hleze uMoya kaYehova ungamfukuli, umjulele kwenye intaba, nokuba kwenye intili. Wathi, Musani ukuthumela.', 'XHO22'),
(16, 2, 17, 'Bamjongisa wada waneentloni, besithi: “Roma! Bawathuma ke amadoda amashumi mahlanu; Amfuna iintsuku zantathu, akamfumana.', 'XHO22'),
(16, 2, 18, 'Akufika kuye, ehleli eYeriko, wathi kuwo, Ndibe ndingatshongo na kuni ukuthi, Musani ukuya?', 'XHO22'),
(16, 2, 19, 'Athi amadoda aloo mzi kuElisha, Uyabona, ukuma kwalo mzi kuhle, njengoko ibonayo inkosi yam; ke amanzi akanamntu, nelizwe lilugwadule.', 'XHO22'),
(16, 2, 20, 'Wathi, Ndiziseleni isitya esitsha, nigalele ityuwa kuso. Bayizisa kuye.', 'XHO22'),
(16, 2, 21, 'Waphuma, waya emthonjeni wamanzi, wagalela ityuwa kuwo, wathi, Utsho uYehova ukuthi, Ndiwaqinise la manzi; akusayi kuba sabakho kufa naluqweqwe lwazo.', 'XHO22'),
(16, 2, 22, 'Amanzi lawo aphila unanamhla, ngokwelizwi likaElisha awalithethayo.', 'XHO22'),
(16, 2, 23, 'Wemka khona, waya eBheteli; kwathi esasendleleni, kwathi gqi abantwana baloo mzi, bahlekisa ngaye, bathi kuye, nkqayindini, nyuka! nyuka, ntloko inenkqayi.', 'XHO22'),
(16, 2, 24, 'Wajika, wabakhangela, wawaqalekisa egameni likaYehova. Kwavela iimazi zamabhere ezimbini ehlathini, zazala amashumi amane anambini amathole.', 'XHO22'),
(16, 2, 25, 'Wesuka khona waya entabeni yeKarmele, wesuka khona wabuyela kwaSamari.', 'XHO22'),
(16, 3, 1, 'uYoram, unyana ka-Ahabhi, waba ngukumkani wakwaSirayeli kwaSamari, ngomnyaka weshumi elinesithoba nowesibini kaYehoshafati ukumkani wakwaYuda; waba neminyaka elishumi elinamibini engukumkani.', 'XHO22'),
(16, 3, 2, 'Wenza okubi emehlweni kaYehova; kodwa akaba njengoyise nanjengonina, ngenxa enokuba wawususayo umfanekiso kaBhahali, awawenzayo uyise.', 'XHO22'),
(16, 3, 3, 'Wanamathela ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli; akatyeka kuyo.', 'XHO22'),
(16, 3, 4, 'uMesha, ukumkani wakwaMowabhi, ubengumalusi, wabuyisela kukumkani wakwaSirayeli ikhulu lamawaka eemvana, nekhulu lamawaka eenkunzi zeegusha ezinoboya.', 'XHO22'),
(16, 3, 5, 'Wathi akufa uAhabhi, wakreqa ukumkani wakwaMowabhi kukumkani wakwaSirayeli.', 'XHO22'),
(16, 3, 6, 'Ngelo xesha ukumkani uYoram waphuma eSamariya waza wawabala onke amaSirayeli.', 'XHO22'),
(16, 3, 7, 'Waphuma, wathumela kuYehoshafati ukumkani wakwaYuda, esithi, Undijikile ukumkani wakwaMowabhi; Wohamba nam siye kulwa namaMowabhi? Wathi, Ndiya kunyuka ndiye, ndinjengani; abantu bam banjengabantu bakho, amahashe am anjengamahashe akho.', 'XHO22'),
(16, 3, 8, 'Wathi, Sonyuka ngayiphi na indlela? Wathi, Yindlela yentlango yakwaEdom.', 'XHO22'),
(16, 3, 9, 'Wahamba ke ukumkani wakwaSirayeli, nokumkani wakwaYuda, nokumkani wakwaEdom, bahamba uhambo olude lweentsuku ezisixhenxe;', 'XHO22'),
(16, 3, 10, 'Wathi ukumkani wakwaSirayeli, Yoo! uYehova wabahlanganisa abo kumkani bathathu ukuze abanikele esandleni sikaMowabhi.', 'XHO22'),
(16, 3, 11, 'Wathi uYehoshafati, Akukho mprofeti kaYehova na apha, esinokubuza ngaye kuYehova? Waphendula omnye wakubakhonzi bokumkani wakwaSirayeli, wathi, Lo nguElisha, unyana kaShafati, obegalela amanzi esandleni sikaEliya.', 'XHO22'),
(16, 3, 12, 'Wathi uYehoshafati, Ilizwi likaYehova linaye. Wehla ke ukumkani wakwaSirayeli, noYehoshafati, nokumkani wakwaEdom, baya kuye.', 'XHO22'),
(16, 3, 13, 'Wathi ke uElisha kukumkani wakwaSirayeli, Yintoni na enam nawe? Hambani niye kubaprofeti bakayihlo, nakubaprofeti bakanyoko. Wathi ukumkani wakwaSirayeli kuye, Hayi, ngokuba uYehova ubabizele aba kumkani bathathu, ukuba abanikele esandleni sikaMowabhi.', 'XHO22'),
(16, 3, 14, 'Wathi uElisha, Ehleli nje uYehova wemikhosi, endimi phambi kwakhe, inene, ukuba bendingambonanga uYehoshafati, ukumkani wakwaYuda, ndingakubhekanga khona, ndingakubonanga.', 'XHO22'),
(16, 3, 15, 'Ndiziseleni ke ngoko umkhonzi. Ke kaloku, esamemeza umkhonzi, isandla seNkosi saba phezu kwakhe.', 'XHO22'),
(16, 3, 16, 'Wathi, Utsho uYehova ukuthi, Yizaliseni le ntili ngemingxuma;', 'XHO22'),
(16, 3, 17, 'Ngokuba wathi uYehova, Aniyi kubona moya namvula; kodwa loo ntlambo iya kuzala amanzi, nisele nina, neenkomo zenu, neenkomo zenu.', 'XHO22'),
(16, 3, 18, 'Le nto incinane emehlweni kaYehova: uya kuwanikela amaMowabhi esandleni senu.', 'XHO22'),
(16, 3, 19, 'Noyithimba yonke imizi enqatyisiweyo, nemizi yonke enyuliweyo, niyigawule yonke imithi emihle, niyivingce yonke imithombo yamanzi, niyitshabalalise ngamatye ummandla welizwe elihle ngamatye.', 'XHO22'),
(16, 3, 20, 'Kwathi ngomso, lakuziswa idini elinyukayo, kwafika amanzi endleleni eya kwaEdom, lazala ilizwe ngamanzi.', 'XHO22'),
(16, 3, 21, 'Eva ke onke amaMowabhi ukuba benyukile ookumkani, besiza kulwa nawo, abutha bonke abaxhobileyo, bema emdeni.', 'XHO22'),
(16, 3, 22, 'Bavuka kusasa, ilanga labengezela emanzini, awabona amaMowabhi amanzi ebomvu njengegazi.', 'XHO22'),
(16, 3, 23, 'Bathi ke bona: “Ligazi eli! phanga ngoku, Mowabhi.', 'XHO22'),
(16, 3, 24, 'Bafika ke emkhosini wakwaSirayeli, bagqiba oonyana bakaSirayeli ukuwaxabela amaMowabhi, asaba phambi kwawo; kodwa bemka, bawaxabela amaMowabhi ezweni lawo.', 'XHO22'),
(16, 3, 25, 'Bayidiliza imizi; bawavala onke amaqula, bagawula yonke imithi enencasa kodwa izilingi ziyajikeleza kwaye ziphumelele.', 'XHO22'),
(16, 3, 26, 'Wathi akubona ukumkani wakwaMowabhi ukuba imfazwe inzima kuye, wathabatha amadoda angamakhulu asixhenxe arhola ikrele kunye naye, ukuze atyhobozelele kukumkani wakwaEdom;', 'XHO22'),
(16, 3, 27, 'Wathabatha unyana wakhe wamazibulo, owaba ngukumkani esikhundleni sakhe, wenza idini elinyukayo phezu kodonga. Wavutha kunene ngumsindo uSirayeli, amshiya, abuyela ezweni lawo.', 'XHO22'),
(16, 4, 1, 'Ke kaloku umfazi othile woonyana babaprofeti wakhala kuElisha, esithi, Umkhonzi wakho, indoda yam, ubhubhile; yaye wena uyazi ukuba umkhonzi wakho wayemoyika uYehova, waza umbolekisi weza kubathabatha oonyana bam bobabini ukuba babe ngamakhoboka akhe.', 'XHO22'),
(16, 4, 2, 'Wathi uElisha kuye, Ndikwenzele ntoni na? khawutsho unantoni apha endlini? Wathi, Umkhonzazana wakho akananto endlwini, yingqayi yeoli yodwa.', 'XHO22'),
(16, 4, 3, 'Sathi, Hamba uye kuboleka iiemele ezingenanto kubamelwane bakho bonke; musa ukuboleka iperi.', 'XHO22'),
(16, 4, 4, 'Ekungeneni kwakho, utshixe ucango, wena noonyana bakho, uzikhuphe zonke ezo zitya, uze uzibeke ecaleni.', 'XHO22'),
(16, 4, 5, 'Wasuka ke, wamvalela yena noonyana bakhe, bamzisela izitya; wagabha.', 'XHO22'),
(16, 4, 6, 'Kwathi, zakuzala iingqayi, wathi kunyana wakhe, Ndizise enye into. Wathi kuye, Akusekho umphanda; Yahlala ke ioli.', 'XHO22'),
(16, 4, 7, 'Emva koko weza waxelela umfo wakwaThixo loo nyaniso. Wathi yena, Hamba uye kuthengisa ngeoli, uhlawule ityala lakho, uze wena nabantwana bakho nidle ubomi obunye.', 'XHO22'),
(16, 4, 8, 'Kwathi ngamhla uthile, uElisha wawela, waya eShunem, apho kwakukho umfazi omkhulu; wamnyanzela ukuba adle isonka. Wadlula apho, wajika ukuba adle isonka.', 'XHO22'),
(16, 4, 9, 'Wathi endodeni yakhe, Yabona, ndiyaqonda ukuba lo mfo ungcwele wakwaThixo umana egqitha apha.', 'XHO22'),
(16, 4, 10, 'Masenze igumbi elincinane ngaseludongeni; simbekele ibhedi, netafile, nesitulo, nesibane; wothi, xa athe weza kuthi, ajike.', 'XHO22'),
(16, 4, 11, 'Wafika khona ngamhla uthile, wangena egumbini, walala khona.', 'XHO22'),
(16, 4, 12, 'Wathi kuGehazi umkhonzi wakhe, Biza laa mfazi waseShunem. Uthe akumbiza, wema ecaleni kwakhe.', 'XHO22'),
(16, 4, 13, 'Uthe ke yena kuye, Kungokunje uthi kuye, Uyabona, usinyamekele ngayo yonke le nyameko; Kufuneka wenzelwe ntoni? Niya kusiwa kukumkani, nokuba kukumthetheli wenkundla? Wathi yena: Ndihleli phakathi kwabantu bakowethu.', 'XHO22'),
(16, 4, 14, 'Wathi, Makwenziwe ntoni na? Waphendula uGehazi, wathi, Akanamntwana; nendoda yakhe yaluphele.', 'XHO22'),
(16, 4, 15, 'Wathi, Mbizeni. Uthe xa embiza wema emnyango.', 'XHO22'),
(16, 4, 16, 'Wathi, Ngeli xesha nyakenye uya kunxiba inkwenkwe. Wathi, Hayi, nkosi yam, mfo wakwaThixo, musa ukumxokisa umkhonzazana wakho.', 'XHO22'),
(16, 4, 17, 'Wamitha ke umfazi lowo, wazala unyana ngelo xesha limisiweyo lanyakenye, abelithethile uElisha kuye.', 'XHO22'),
(16, 4, 18, 'Akukhula umntwana, ngamhla uthile waya kuyise kubavuni.', 'XHO22'),
(16, 4, 19, 'Wathi kuyise, Intloko yam, intloko yam! Wathi kumfana, Mse kunina.', 'XHO22'),
(16, 4, 20, 'Wamthabatha, wamsa kunina, wahlala emathangeni akhe kwada kwasemini enkulu, wafa.', 'XHO22'),
(16, 4, 21, 'Wenyuka waya kumlalisa emandlalweni womntu kaTHIXO oyinyaniso waza wamvalela ucango waphuma.', 'XHO22'),
(16, 4, 22, 'Wandula ke wabiza indoda yakhe, wathi: “Ndithumele omnye wabafana nenye yeemazi zamaesile, khe ndibaleke ndiye kumfo wakwaThixo oyinyaniso ndize ndibuye.”', 'XHO22'),
(16, 4, 23, 'Wathi, Ungathini na ukuya kuye namhla? asikuthwasa kwenyanga, asiyisabatha. Wathi yena, Kuya kulunga.', 'XHO22'),
(16, 4, 24, 'Wayibopha iesile, wathi kumfana lowo, Hamba ukhwele; Ungandilibali ngaphandle kokuba ndikuxelele.', 'XHO22'),
(16, 4, 25, 'Wahamba ke waya kufika kumfo wakwaThixo oyinyaniso eNtabeni yeKarmele. Wambona umfo wakwaThixo esekude, wathi kuGehazi umkhonzi wakhe, Nankuya apho umShunemikazi.', 'XHO22'),
(16, 4, 26, 'Gidima uye kumkhawulela, uthi kuye, Uphilile na? Uphilile na umyeni wakho? uphilile umntwana? Waphendula ke wathi, Kulungile.', 'XHO22'),
(16, 4, 27, 'Akufika kumfo kaTHIXO oyinyaniso endulini, wambamba ngeenyawo; wasondela uGehazi ukuba amsunduze. Wathi umfo wakwaThixo, Makalinde; ngokuba umphefumlo wakhe ukrakra phakathi kwakhe, uYehova undifihlele le nto, akandityhilela;', 'XHO22'),
(16, 4, 28, 'Wathi, Ndakha ndacela unyana yini na enkosini yam? Khange ndithi: Ungandixokisi?', 'XHO22'),
(16, 4, 29, 'Wathi kuGehazi, Bhinqa isinqe sakho, uthabathe umsimelelo wam ngesandla sakho, uhambe; xa uthe wadibana nomntu, uze ungambulisi; ukuba uthe wabulisa, uze ungaphindi umphendule; ndibeke intonga yam ebusweni benkwenkwe.', 'XHO22'),
(16, 4, 30, 'Wathi unina womntwana, NgoYehova ovayo, uphile umphefumlo wakho, andiyi kwahlukana nawe. Waphakama wamlandela.', 'XHO22'),
(16, 4, 31, 'Wasondela uGehazi kubo, wawubeka umsimelelo ebusweni benkwenkwe; kodwa kwakungekho lizwi nabaphulaphuli. Ngoko waphinda waya kumhlangabeza waza wamxelela waza wathi: “Umntwana akavukanga.”', 'XHO22'),
(16, 4, 32, 'Wangena ke uElisha endlwini, wafumana inkwenkwe ifile, ilele esingqengqelweni sakhe.', 'XHO22'),
(16, 4, 33, 'Wangena, walutshixa ucango, bebobabini; wathandaza kuYehova.', 'XHO22'),
(16, 4, 34, 'Wenyuka, wangqengqa phezu komntwana lowo, wabeka umlomo wakhe emlonyeni wakhe, namehlo akhe phezu kwamehlo akhe, nezandla zakhe phezu kwezandla zakhe; yafudumala inyama yomntwana.', 'XHO22'),
(16, 4, 35, 'Wabuya, wasinga ngapha kwakanye, wasinga ngapha kwakanye, wahamba endlwini; Yenyuka, wazolula phezu kwakhe, yathimla inkwenkwe izihlandlo ezisixhenxe, yawavula inkwenkwe amehlo ayo.', 'XHO22'),
(16, 4, 36, 'Wabiza uGehazi, wathi, Biza laa mkaShunem. Yiyo loo nto emfownelayo. Efikile ke kuye, wathi, Thabatha unyana wakho.', 'XHO22'),
(16, 4, 37, 'Emva koko wangena waziphosa ezinyaweni zakhe waqubuda waza wamthabatha unyana wakhe waphuma.', 'XHO22'),
(16, 4, 38, 'uElisha wabuya, waya eGiligali. Kwakukho indlala elizweni; oonyana babaprofeti babehleli ngakuye;', 'XHO22'),
(16, 4, 39, 'Waphuma othile, waya kukha imifuno emaphandleni; wafumana umdiliya wasemaphandleni, wakha kuwo, wazala ingubo yakhe, weza, wawunqunqa embizeni, kuba babengazi.', 'XHO22'),
(16, 4, 40, 'Baza bawaphakela amadoda ukuba adle. Bakuba besidla isonka, badanduluka besithi, Mfo wakwaThixo, kufa embizeni! Kwaye abanakuyidla.', 'XHO22'),
(16, 4, 41, 'Wathi, Zisani umgubo. Wagalela embizeni; Wathi, Nika abantu, badle akwabakho bubi embizeni.', 'XHO22'),
(16, 4, 42, 'Kwafika indoda, ivela eBhahali-salilisa, izisele umfo wakwaThixo izonka zerhasi ezimashumi mabini, neengobozi zizele yingqolowa. Wathi, Nika abantu, badle.', 'XHO22'),
(16, 4, 43, 'Wathi umfana lowo, Ndothini na ukulinika ikhulu lamadoda? Wathi, Nika abantu badle, ngokuba uYehova wathi, Baya kudla, basele.', 'XHO22'),
(16, 4, 44, 'Wazibeka phambi kwabo; badla, bashiya ngokwelizwi likaYehova.', 'XHO22'),
(16, 5, 1, 'Ke kaloku uNahaman, umthetheli-mkhosi wokumkani wakwa-Aram, waye eyindoda enkulu phambi kokumkani wakhe, ebekekileyo; ngokuba uYehova wayewanike ngaye ama-Aram; naye wayeligorha elikhaliphileyo, kodwa eneqhenqa.', 'XHO22'),
(16, 5, 2, 'Yaye amaSiriya abalusa abaninz aza alithimba ikhoboka kwilizwe lakwaSirayeli; Walinda umkaNahaman.', 'XHO22'),
(16, 5, 3, 'Wathi kumkakhe, Akwaba uThixo, inkosi yam, ubenomprofeti kwaSamari! ngokuba ubeza kuphiliswa eqhenqeni lakhe.', 'XHO22'),
(16, 5, 4, 'Wafika omnye, wayixelela inkosi yakhe, wathi, Intombi yasezweni lakwaSirayeli ithethe ngokunjalo.', 'XHO22'),
(16, 5, 5, 'Wathi ukumkani wakwa-Aram, Hamba uye, ndithumele incwadi kukumkani wakwaSirayeli. Wemka ke, wathabatha ishumi leetalente zesilivere, namawaka amathandathu eeshekele zegolide, neshumi leengubo zokuhlambela.', 'XHO22'),
(16, 5, 6, 'Wazisa incwadi kukumkani wakwaSirayeli, isithi, Xa ithe yafika kuwe le ncwadi, yabona, ndikuthuma uNahaman umkhonzi wam kuwe, ukuba umphilise eqhenqeni lakhe.', 'XHO22'),
(16, 5, 7, 'Wayilesa ukumkani wakwaSirayeli incwadi, wazikrazula iingubo zakhe, wathi, NdinguThixo obulalayo, osindisayo, lo ubethumele kum ukuba ndiphilise indoda eqhenqeni layo ke ngoko, nikelani ingqalelo, nibone ukuba ufuna ukuya nam njani.', 'XHO22'),
(16, 5, 8, 'Xa uElisha, indoda kaTHIXO oyinyaniso wevayo ukuba ukumkani wakwaSirayeli uzikrazule izambatho zakhe, wathumela umyalezo kukumkani, esithi: “Kutheni ukrazula nje izambatho zakho?” makeze kum kaloku, azi, ngokuba kukho umprofeti kwaSirayeli.', 'XHO22'),
(16, 5, 9, 'Weza uNahaman namahashe akhe neenqwelo zakhe, wema emnyango wendlu kaElisha.', 'XHO22'),
(16, 5, 10, 'uElisha wathuma umthunywa kuye, esithi, Hamba uye kuhlamba izihlandlo ezisixhenxe eYordan, inyama yakho ibuyele kuwe, uhlambuluke.', 'XHO22'),
(16, 5, 11, 'Waqumba uNahaman, wemka, wathi, Yabona, ndithe, Uya kuphuma eze kum, eme, alibize igama likaYehova uThixo wakhe, asibethe isandla sakhe phezu kwendawo eneqhenqa.', 'XHO22'),
(16, 5, 12, 'iAbhana nePapare, imilambo yaseDamasko, ayilungile na, ngaphezu kwamanzi onke akwaSirayeli? ndihlambe apho ndicoceke? Wajika ke wemka enomsindo.', 'XHO22'),
(16, 5, 13, 'Beza abakhonzi bakhe, bathetha kuye, bathi, Bawo, ukuba umprofeti ubethethe into enkulu kuwe, ubungàyi kuyenza yini na? Kangakanani na ke xa athe kuwe, Hlamba, uhlambuluke?', 'XHO22'),
(16, 5, 14, 'Wehla ke waza wabhaptizwa izihlandlo ezisixhenxe eYordan, ngokwelizwi lomntu kaThixo oyinyaniso; yabuya ke inyama yakhe, yanjengenyama yomntwana, wahlambuluka.', 'XHO22'),
(16, 5, 15, 'Wabuyela kumntu kaTHIXO oyinyaniso, yena nebandla lakhe lonke, weza, wema phambi kwakhe; kwaye wathi: Khangela, ngoku ndiyazi ukuba akukho Thixo ehlabathini lonke, ngaphandle kwaSirayeli; Thandaza, wamkele intsikelelo kumkhonzi wakho.', 'XHO22'),
(16, 5, 16, 'Wathi yena, Ehleli nje uYehova, endimi phambi kwakhe, andiyi kukholiswa mntu. Wamjoka ukuba amamkele; akavuma.', 'XHO22'),
(16, 5, 17, 'Wathi uNahaman, Umkhonzi wakho akayi kunikwa yini na oondlebende ababini belizwe? ngokuba umkhonzi wakho akasayi kuba senza dini linyukayo nambingelelo thixweni bambi, ingenguYehova yedwa.', 'XHO22'),
(16, 5, 18, 'Xa kunjalo ke, uYehova uya kumxolela umkhonzi wakho, ukuba ithe yangena inkosi yam endlwini kaRimon ukuba ithandaze khona, yayama esandleni sam, nam ndoqubuda endlwini kaRimon; xa ndiqubuda endlwini kaRimon, aze uYehova amxolele umkhonzi wakho kulo mbandela.', 'XHO22'),
(16, 5, 19, 'Wathi kuye, Hamba unoxolo. Ngoko wemka kuye okwethutyana.', 'XHO22'),
(16, 5, 20, 'Kodwa uGehazi, umkhonzi kaElisha, indoda kaTHIXO oyinyaniso, wathi: “Yabona, inkosi yam imyekile uNahaman umAram lo, ngokungakwamkelanga esandleni sakhe oko wayekuzisile; ehleli nje uYehova, ndiya kumsukela, ndithabathe okuthile kuye.', 'XHO22'),
(16, 5, 21, 'uGehazi wamlandela uNahaman. Wathi uNahaman akumbona ebaleka, wehla emotweni esiya kumkhawulela, wathi: “Uphilile na?', 'XHO22'),
(16, 5, 22, 'Wathi yena, Konke kulungile; iNkosi yam indithumile, isithi, Yabona, kufike kum amadodana amabini akoonyana babaprofeti, evela kweleentaba lakwaEfrayim; banike italente yesilivere, neengubo zangaphantsi zibe mbini.', 'XHO22'),
(16, 5, 23, 'Wathi uNahaman, Kwanele, thabatha zibe mbini iitalente. Wamjoka, wabeka iitalente zambini zesilivere ezingxoweni ezimbini, neengubo zangaphantsi ezimbini, wanika abakhonzi bakhe ababini; baza batwabulula phambi kwakhe.', 'XHO22'),
(16, 5, 24, 'Uthe akufika enqabeni, wayithabatha ezandleni zabo, wayingenisa endlwini; wawandulula amadoda lawo, ahamba.', 'XHO22'),
(16, 5, 25, 'Wangena, wema phambi kwenkosi yakhe. Wathi uElisha kuye, Uvela phi na, Gehazi? Wathi, Umkhonzi wakho lo akazanga ndawo.', 'XHO22'),
(16, 5, 26, 'Wathi kuye, Intliziyo yam ayihambanga nawe yini na, okuya yabuyayo indoda emotweni yayo, yeza kukukhawulela? Lixesha na eli lokwamkela imali, nempahla, neminquma, nezidiliya, nempahla emfutshane, neenkomo, nabakhonzi, nabakhonzi?', 'XHO22'),
(16, 5, 27, 'Iqhenqa likaNahaman liya kunamathela kuwe nakwimbewu yakho ngonaphakade; Waza wabushiya ubuso bakhe, buneqhenqa, bumhlophe njengekhephu.', 'XHO22'),
(16, 6, 1, 'Bathi oonyana babaprofeti kuElisha, Uyabona, le ndawo, sihleli kuyo phambi kwakho, imxinwa;', 'XHO22'),
(16, 6, 2, 'Masihambe siye eYordan, sithabathe khona elowo intonga, sizenzele khona indawo yokuhlala; Waphendula ke yena wathi, Hamba!', 'XHO22'),
(16, 6, 3, 'Wathi omnye, Molo, hamba nabakhonzi bakho. Waphendula ke yena wathi, Ndiya kuhamba.', 'XHO22'),
(16, 6, 4, 'Wahamba ke nabo. Afika eYordan, agawula imithi;', 'XHO22'),
(16, 6, 5, 'Kodwa xa omnye wayegawula umthi, izembe lawela emanzini, waza wadanduluka wathi: “Owu! ngokuba libolekiwe.', 'XHO22'),
(16, 6, 6, 'Wathi umfo wakwaThixo, Iwe phi na? Wambonisa loo ndawo. Wanqumla uluthi, waluphosa khona; yaza yaqubha intsimbi.', 'XHO22'),
(16, 6, 7, 'Ke ngoko wathi: Yithabathe; Wasolula isandla sakhe wayithabatha.', 'XHO22'),
(16, 6, 8, 'Wawaxabela ke ukumkani wama-Aram amaSirayeli, wacebisana nabakhonzi bakhe, wathi, Yoba ndaweni ithile ndilihlathi lam.', 'XHO22'),
(16, 6, 9, 'Wathumela umfo wakwaThixo kukumkani wakwaSirayeli, esithi, Zilindeni, ningacandi kulaa ndawo; ngokuba ama-Aram ehlele khona.', 'XHO22'),
(16, 6, 10, 'Wandula ke ukumkani wakwaSirayeli wathumela kwindawo awayemyalele ukuba alumkise kuyo umfo kaTHIXO oyinyaniso, amgcine khona, kungekhona kanye okanye kabini.', 'XHO22'),
(16, 6, 11, 'Yabhabhama kakhulu intliziyo yokumkani wakwa-Aram yiloo nto; Wabiza abakhonzi bakhe, wathi kubo, Anindibonisi na ukuba ngubani na kuthi omela ukumkani wakwaSirayeli?', 'XHO22'),
(16, 6, 12, 'Wathi omnye wakubakhonzi bakhe, Makungabikho bani, nkosi yam, ingenguElisha, umprofeti okwaSirayeli, omxelela ukumkani wakwaSirayeli amazwi owathetha usengontsini yakho yokulala.', 'XHO22'),
(16, 6, 13, 'Wathi, Yiyani, nikhangele apho akhona, ndimbize. Waxelelwa kwathiwa, Khangela, useDotan.', 'XHO22'),
(16, 6, 14, 'Ngoko wathumela apho amahashe neenqwelo zokulwa nempi enkulu, baza bafika ebusuku, basingqinga isixeko.', 'XHO22'),
(16, 6, 15, 'Xa umkhonzi womntu kaTHIXO oyinyaniso wavuka ekuseni waza waphuma, wabona umkhosi ujikeleze isixeko unamahashe neenqwelo zokulwa. Wathi umlungiseleli wakhe kuye, Oh! siza kwenza ntoni?', 'XHO22'),
(16, 6, 16, 'Wathi, Musani ukoyika; ngokuba baninzi abangakuthi, ngaphezu kwabangakubo.', 'XHO22'),
(16, 6, 17, 'Wathandaza uElisha, wathi, Yehova, vula amehlo akhe, abone. uYehova wawavula amehlo omfana; wakhangela, nantso intaba izele ngamahashe neenqwelo zokulwa zomlilo, zimlindile uElisha phakathi.', 'XHO22'),
(16, 6, 18, 'Behla baya kuye, wathandaza uElisha kuYehova, wathi, Usibethe ngobumfama esi sizwana. Wababetha ke ngobumfama ngokwelizwi likaElisha.', 'XHO22'),
(16, 6, 19, 'Wathi uElisha kubo, Asiyondlela le, asinguwo umzi; ndilandeleni, ndinise kuloo ndoda niyifunayo; Kodwa wabasa kwaSamari.', 'XHO22'),
(16, 6, 20, 'Bakufika kwaSamari, uElisha wathi: “Yehova, wavule amehlo aba bantu, babone. uYehova wawavula amehlo abo, babona; nanko bekwaSamari.', 'XHO22'),
(16, 6, 21, 'Wathi ukumkani wakwaSirayeli kuElisha, akubabona, Ndibaxabele na, ndibaxabele na, bawo? ndiya kubohlwaya.', 'XHO22'),
(16, 6, 22, 'Wathi, Musani ukubohlwaya; Ngaba uyathanda ukubabulala abathinjwa bakho ngekrele lakho nangobutshaba bakho? ubeke isonka namanzi phambi kwabo, badle, basele, baye enkosini yabo.', 'XHO22'),
(16, 6, 23, 'Wabenzela ukutya okuninzi, badla basela, wabayalela ukuba bahambe, baya kukumkani wabo. Ke ngoko imikhosi yama-Aram ayisayi kuphinda ingene ezweni lakwaSirayeli.', 'XHO22'),
(16, 6, 24, 'Emva koko uBhen-hadadi ukumkani wakwa-Aram wawuhlanganisa ndawonye wonke umkhosi wakhe wamajoni waza wenyuka waya kungqinga iSamariya.', 'XHO22'),
(16, 6, 25, 'Kwabakho indlala enkulu eSamariya, yaye, khangela!', 'XHO22'),
(16, 6, 26, 'Kwathi, ewela ukumkani wakwaSirayeli phezu kodonga, yambiza inkazana, yathi, Sindisa, nkosi yam, kumkani.', 'XHO22'),
(16, 6, 27, 'Wathi yena, Engakusindisi nje uYehova, ndokusindisa ngantoni na mna? ukusuka kwiqonga okanye kwisali?', 'XHO22'),
(16, 6, 28, 'Wathi ukumkani kuye, Ukhathazwa yintoni na? Wathi, Lo mfazi uthe kum, Khupha unyana wakho, simdle namhla, sidle unyana wam ngomso.', 'XHO22'),
(16, 6, 29, 'Sampheka ke unyana wam, samdla; ndathi kuye ngengomso, Khupha unyana wakho, simdle; wamngcwaba unyana wakhe.', 'XHO22'),
(16, 6, 30, 'Uthe akuweva ukumkani amazwi entokazi leyo, wazikrazula iingubo zakhe; wadlula eludongeni, kwaye abantu bayibona loo nto, babona ukuba unxibe ilaphu elirhabaxa enyameni yakhe ngaphakathi.', 'XHO22'),
(16, 6, 31, 'Wathi, uThixo enjenje kum, waqokele ukwenjenje, yakuba intloko kaElisha, unyana kaShafati, imi phezu kwakhe namhla.', 'XHO22'),
(16, 6, 32, 'uElisha wayehleli endlwini yakhe, namadoda amakhulu ehleli naye; Ukumkani wathuma umthunywa ukuba amkhaphele; akafika umthunywa lowo kuye, wathi kumadoda amakhulu, Niyabona na, ukubo unyana walo mbulali uthumele ukuba ndithabathe intloko? Niyabona, akufika umthunywa lowo, luvaleni ucango, nilugcine ucango; Isandi seenyawo zenkosi yakhe asisemva kwayo na?', 'XHO22'),
(16, 6, 33, 'Esathetha nawo, kwafika umthunywa kuye, wathi, Yabona, obu bubi buphuma kuYehova; Ndisalindele ntoni kuYehova?', 'XHO22'),
(16, 7, 1, 'Wathi uElisha, Liveni ilizwi likaYehova; Wathi uYehova, Eli xesha ngomso kuya kuthengwa iseha yomgubo ocoliweyo ibe yishekele, neeseha ezimbini zerhasi zibe yishekele kwisango lakwaSamari.', 'XHO22'),
(16, 7, 2, 'Wamphendula umfo wakwaThixo omnye umntu, abesayama esandleni sakhe ukumkani, wathi, Yabona, khona ukuba uYehova wenze iifestile ezulwini, ingabakho na loo nto? Wathi yena, Khangela, uya kuyibona loo nto ngamehlo akho, ungadli ngayo;', 'XHO22'),
(16, 7, 3, 'Ke kaloku kwakukho amadoda amane aneqhenqa ngaphandle kwesango. Athi enye kwenye: “Sihlaleleni na apho side sife?', 'XHO22'),
(16, 7, 4, 'Ukuba sithe, Masingene kulo mzi, kuya kubakho indlala kuwo umzi lo, siya kufela khona; ukuba sithe sahlala apha, siyafa. Khawuze siwe phambi kwemikhosi yama-Aram; ukuba asigcina siphila, sodla ubomi; nokuba bayasibulala, siya kufa.', 'XHO22'),
(16, 7, 5, 'Banduluka ngokuhlwa, baya eminqubeni yama-Aram; afika ekupheleni kweminquba yama-Aram, akwabakho bani khona.', 'XHO22'),
(16, 7, 6, 'Ngokuba uYehova wawuvisa umkhosi wama-Aram isandi seenqwelo zokulwa, nesandi samahashe, nesandi somkhosi omkhulu; bathi omnye komnye, Niyabona, ukumkani wakwaSirayeli usiqeshele ookumkani ukuba balwe nathi amaHeti nookumkani baseYiputa baya kusihlasela.', 'XHO22'),
(16, 7, 7, 'Besuka ke, basaba ngongcwalazi, bazishiya iintente zabo, namahashe abo, namaesile abo, eminqubeni, injengoko injalo, basaba ukuba basindise umphefumlo wabo.', 'XHO22'),
(16, 7, 8, 'Afika ke loo madoda aneqhenqa ekupheleni kweminquba, angena kwenye intente, adla, asela, athabatha khona isilivere negolide neempahla, aziqhusheka; wabuya wangena kwenye intente wayithatha wemka wayifihla.', 'XHO22'),
(16, 7, 9, 'Bathetha omnye komnye besithi, Asihambanga kakuhle; le mini yimini yeendaba ezilungileyo, sizola; ukuba sizilazila kwada kwasa, sokhubeka; Hambani ngoku, siye kuyixelela indlu yokumkani.', 'XHO22'),
(16, 7, 10, 'Beza, babiza abalindi besango lomzi, babaxelela, bathi, Sifikile eminqubeni yama-Aram, sabona ukuba akukho bani khona, kungekho nazwi lamntu, ingengamahashe axhobileyo, nabamahashe iimazi zamaesile neentente zinjengoko bezinjalo.', 'XHO22'),
(16, 7, 11, 'Wababiza ke abamasango; bawaxelela endlwini yokumkani.', 'XHO22'),
(16, 7, 12, 'Wavuka ukumkani ebusuku, wathi kubakhonzi bakhe, Makhe ndinixelele into ayenzileyo kuthi ama-Aram. Bayazi ukuba kutheni silambile; Aphuma ke eminqubeni, aya kuzimela ezweni, esithi, Ukuba bathe baphuma kuwo umzi, sowabamba behleli, singene kuwo umzi.', 'XHO22'),
(16, 7, 13, 'Waphendula omnye wakubakhonzi bakhe, wathi, Inxenye kuthabathe abe mahlanu kumahashe, aseleyo phakathi komzi; Ndithi, banjengengxokolo yonke yoonyana bakaSirayeli abatshabalalayo; masithumele, sibone.', 'XHO22'),
(16, 7, 14, 'Bathabatha amahashe okulwa amabini; Ukumkani wawasukela ama-Aram, wathi, Hambani niye kubona;', 'XHO22'),
(16, 7, 15, 'Bawalandela, bada besa eYordan, nayibona indlela yonke izele ziingubo nezitya, abezilahlile ama-Aram ngenxa yokungxama kwawo. Babuya abathunywa abo, baya kuxelela ukumkani.', 'XHO22'),
(16, 7, 16, 'Aphuma amadoda lawo, aziphanga iintente zama-Aram. Yathi iseha yomgubo ocoliweyo yayeyeshekele, neeseha ezimbini zerhasi zazezeshekele, ngokwelizwi likaYehova.', 'XHO22'),
(16, 7, 17, 'Ukumkani wammisa ukumkani owayesayame esandleni sakhe, ukuba alawule isango; Bamnyathela abantu esangweni, wafa, njengoko watshoyo umfo wakwaThixo, ekuhleni kokumkani, esiza kuye.', 'XHO22'),
(16, 7, 18, 'Wathetha umfo wakwaThixo oyinyaniso kukumkani, wathi: “Ngomso, ukuba kunjalo, imilinganiselo emibini yerhasi ngeshekele, neseha yomgubo ocolekileyo ngeshekele, yoba sesangweni laseSamariya ngeli xesha.', 'XHO22'),
(16, 7, 19, 'Indoda leyo yamphendula umfo wakwaThixo oyinyaniso, yathi, Yabona, ukuba uYehova wenze iifestile ezulwini, ingabakho na into enjalo? Wathi yena, Khangela, uya kuyibona loo nto ngamehlo akho, ungadli ngayo;', 'XHO22'),
(16, 7, 20, 'Yawa ke phezu kwakhe, ngokuba abantu bamnyathele esangweni, wafa.', 'XHO22'),
(16, 8, 1, 'Wathetha ke uElisha kwintokazi eyamvusayo unyana wakhe, wathi, Vuka, uhambe, wena nomzi wakho, uye kuphambukela apho ungaphambukela khona; ngokuba uYehova ubize indlala; kwaye uya kuza ehlabathini kwiminyaka esixhenxe.', 'XHO22'),
(16, 8, 2, 'Wagqiba ke umfazi lowo, wenza ngokwelizwi lomntu kaTHIXO oyinyaniso; Wahamba nentsapho yakhe waza wahlala iminyaka esixhenxe njengomphambukeli kwilizwe lamaFilisti.', 'XHO22'),
(16, 8, 3, 'Ekupheleni kweminyaka esixhenxe leyo, yabuya inkazana leyo ezweni lamaFilisti, yaphuma yaya kuzibika kukumkani ngendlu yayo nentsimi yayo.', 'XHO22'),
(16, 8, 4, 'Wathetha uYehova kuGehazi, umkhonzi womfo wakwaThixo, wathi, Ndixelele zonke izinto ezinkulu azenzileyo uElisha.', 'XHO22'),
(16, 8, 5, 'Yaye xa wayexelela ukumkani indlela awasibuyisela ngayo ebomini isidumbu, khangela! Wathi uGehazi, Nkosi yam, kumkani, yilaa nkazana iphilisileyo uElisha nonyana wayo.', 'XHO22'),
(16, 8, 6, 'Wayibuza ukumkani intokazi leyo; Ukumkani wammisela omnye umphathi, wathi, Buyisa yonke impahla yakhe, neziqhamo zonke zentsimi, oko walishiya ilizwe unangoku.', 'XHO22'),
(16, 8, 7, 'uElisha wafika eDamasko; yaye uBhen-hadadi ukumkani wakwa-Aram wayegula; Kwathiwa, Umfo wakwaThixo uze apha.', 'XHO22'),
(16, 8, 8, 'Wathi uYehova kuHazayeli, Thabatha umnikelo, uye umkhawulela umfo wakwaThixo, umbuze uYehova ngaye, uthi, Ndiya kuphila na kwesi sifo?', 'XHO22'),
(16, 8, 9, 'uHazayeli waya kumkhawulela, wathabatha izipho zonke ezilungileyo zaseDamasko, ubunzima beenkamela ezimashumi mane, weza, wema phezu kwakhe, wathi, Unyana wakho uBhen-hadade, ukumkani wakwa-Aram, undizisile eya kuwe. Wayethunywe ukuba athi: Ngaba ndiya kuphila kwesi sifo?', 'XHO22'),
(16, 8, 10, 'Wathi uElisha kuye, Ewe, ùthi kuye, Uya kuphila ngonaphakade; ke uYehova undibonisile ukuba uya kufa ngonaphakade.', 'XHO22'),
(16, 8, 11, 'Wabutywina ubuso bakhe wada waneentloni, walila umfo wakwaThixo oyinyaniso.', 'XHO22'),
(16, 8, 12, 'Wathi uHazayeli, Ililelani na inkosi yam? Waphendula wathi, Kungokuba ndibazi ububi oya kubenza koonyana bakaSirayeli; nomntwana.', 'XHO22'),
(16, 8, 13, 'Wathi uHazayeli, Uyintoni na umkhonzi wakho, ndiyinja nje, ukuba ndenze into enkulu kangaka? Wathi uElisha, uYehova undibonisile, ukuba uya kuba ngukumkani wakwa-Aram yena.', 'XHO22'),
(16, 8, 14, 'Wamshiya ke uElisha, waya enkosini yakhe; Athi kuye, Utheni na uElisha kuwe? Waphendula ke wathi, Uthe kum, inene, uya kuphila.', 'XHO22'),
(16, 8, 15, 'Ngengomso wathabatha ingubo, wayithi nkxu emanzini, wayaneka phezu kobuso bakhe apho ebefele khona; uHazayeli waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(16, 8, 16, 'Ngomnyaka wesihlanu kaYoram unyana ka-Ahabhi, ukumkani wakwaSirayeli, uYehoshafati engukumkani wakwaYuda, waba ngukumkani uYoram, unyana kaYehoshafati, ukumkani wakwaYuda.', 'XHO22'),
(16, 8, 17, 'Ubeminyaka imashumi mathathu anamibini ezelwe, ukuba ngukumkani kwakhe; Waba neminyaka emashumi asibhozo anamibini engukumkani eYerusalem.', 'XHO22'),
(16, 8, 18, 'Waqhubeka ehamba ngendlela yookumkani bakwaSirayeli, kanye njengoko yahambayo indlu ka-Ahabhi, ngenxa yokuba intombi ka-Ahabhi yayingumfazi wakhe; yaye wenza okubi emehlweni kaYehova.', 'XHO22'),
(16, 8, 19, 'Kodwa uYehova ebengafuni ukumtshabalalisa uYuda, ngenxa kaDavide umkhonzi wakhe, njengoko wathethayo kuye, wathi, uya kusoloko emkhanyisela yena noonyana bakhe.', 'XHO22'),
(16, 8, 20, 'Ngexesha lakhe amaEdom amvukela uYuda, amisela ukumkani wawo.', 'XHO22'),
(16, 8, 21, 'Wagqitha uYehoram, waya eTsahira, eneenqwelo zakhe zonke zokulwa; wavuka ebusuku, wawaxabela amaEdom awayemrhawule ngeenxa zonke, nabathetheli beenqwelo zokulwa; asaba amadoda, aya ezintenteni zawo.', 'XHO22'),
(16, 8, 22, 'Akreqa ke amaEdom kumaYuda, unanamhla. Yaza iLibhena yavukela kwangaxeshanye.', 'XHO22'),
(16, 8, 23, 'Ezinye izinto zikaYoram, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 8, 24, 'uYoram walala kooyise, wangcwatyelwa kooyise emzini kaDavide; unyana wakhe uAhaziya waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 8, 25, 'Ngomnyaka weshumi elinesibini kaYoram unyana ka-Ahabhi ukumkani wamaSirayeli, waba ngukumkani uAhaziya, unyana kaYoram, ukumkani wakwaYuda.', 'XHO22'),
(16, 8, 26, 'uAhaziya ubeminyaka imashumi mabini anamibini ezelwe, ukuba ngukumkani kwakhe; waba namnyaka mnye engukumkani eYerusalem. Igama lonina belinguAtaliya, intombi kaOmri, ukumkani wakwaSirayeli.', 'XHO22'),
(16, 8, 27, 'Waqhubeka ehamba ngendlela yendlu ka-Ahabhi waza wenza okubi emehlweni kaYehova, kanye njengendlu ka-Ahabhi kuba wayengumkhwenyana wendlu ka-Ahabhi.', 'XHO22'),
(16, 8, 28, 'Wahamba noYoram unyana ka-Ahabhi, baya kulwa noHazayeli ukumkani wakwa-Aram eRamoti yaseGiliyadi; yaye amaSiriya amxabela uYehoram.', 'XHO22'),
(16, 8, 29, 'Ukumkani uYoram wabuyela eYizereli, ukuba anyangwe loo manxeba wawafumanayo kuma-Aram eRama, ekulweni kwakhe noHazayeli ukumkani wakwa-Aram. Wehla uAhaziya, unyana kaYoram, ukumkani wakwaYuda, waya kubona uYoram unyana ka-Ahabhi eYizereli, ngokuba ebesifa.', 'XHO22'),
(16, 9, 1, 'Ke kaloku uElisha umprofeti wabiza omnye koonyana babaprofeti, wathi kuye, Bhinqa isinqe sakho, uthabathe le ngxowa yeoli, uye eRamoti yaseGiliyadi.', 'XHO22'),
(16, 9, 2, 'Ufikile khona, nanko uYehu, unyana kaYehoshafati, unyana kaNimshi, ungene, wamnqanda phakathi kwabazalwana bakhe, wamngenisa egumbini;', 'XHO22'),
(16, 9, 3, 'Thabatha isigodlo seoli, umgalele entloko, uthi, Utsho uYehova ukuthi, Ndikuthambisile ukuba ube ngukumkani wakwaSirayeli vula ucango, ubaleke, ungalibali;', 'XHO22'),
(16, 9, 4, 'Wemka umfana lowo, umfana womprofeti, waya eRamoti yaseGiliyadi.', 'XHO22'),
(16, 9, 5, 'Wafika ke, ahlala khona amadindala enkundla; wathi, Ndinelizwi kuwe, kumkani! Wathi uYehu, Ngubani na phakathi kwethu sonke? Wathi, Kuwe, kumkani!', 'XHO22'),
(16, 9, 6, 'Esukile ke, wangena endlwini; wamgalela ioli entloko, wathi kuye, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndikuthambisile ukuba ube ngukumkani woonyana bakaYehova bakaSirayeli.', 'XHO22'),
(16, 9, 7, 'Yibulale indlu ka-Ahabhi inkosi yakho, ndihlawule igazi labakhonzi bam, nabaprofeti, negazi labakhonzi bonke bakaYehova, esandleni sikaIzebhele.', 'XHO22'),
(16, 9, 8, 'Kuba yonke indlu ka-Ahabhi iya kutshabalala, yaye ndiza kumtshabalalisa kuAhabhi lowo ubetha udonga nebanjwa noshiywe kwaSirayeli.', 'XHO22'),
(16, 9, 9, 'Ndoyenza indlu ka-Ahabhi ibe njengendlu kaYarobheham, unyana kaNebhati, nanjengendlu kaBhahesa, unyana ka-Ahiya;', 'XHO22'),
(16, 9, 10, 'Yaye uIzebhele uya kudliwa ziintaka kummandla waseYizereli, yaye akuyi kubakho bani umngcwabayo. Wavula ucango wabaleka.', 'XHO22'),
(16, 9, 11, 'Ephumile uYehu, waya kubakhonzi benkosi yakhe. Kwathiwa kuye, Usahleli kakuhle na? kutheni ize kuwe lendoda iphambeneyo? Wathi kubo, Niyamazi nina loo mntu nelizwi lakhe.', 'XHO22'),
(16, 9, 12, 'Bathi ke bona, Bubuxoki; Wathi, Wenjenje ukuthetha kum, wathi, uYehova wathi, Ndikuthambisile ukuba ube ngukumkani wakwaSirayeli.', 'XHO22'),
(16, 9, 13, 'Bakhawuleza, bathabatha ingubo yomntu wonke, bayibeka ezinyukweni ezingaphantsi kwakhe, bavuthela ngezigodlo, bathi, uYehu ngukumkani.', 'XHO22'),
(16, 9, 14, 'Waza uYehu, unyana kaYehoshafati, unyana kaNimshi, wagwilika kuYoram. (uYoram wayeyilinda iRamoti yaseGiliyadi, yena naye wonke uSirayeli, ngenxa kaHazayeli ukumkani wakwa-Aram.', 'XHO22'),
(16, 9, 15, 'Ukumkani uYoram wabuyela eYizereli, ukuba anyangwe amanxeba awawafumana kuye ama-Aram, ekulweni kwakhe noHazayeli ukumkani wakwa-Aram ilizwe alisindi umzi ekuthethwe ngawo eYizereli.', 'XHO22'),
(16, 9, 16, 'Wakhwela uYehu enqwelweni, waya eYizereli; ngokuba uYoram ubelele khona. Wehla uAhaziya, ukumkani wakwaYuda, esiya kubona uYehoram.', 'XHO22'),
(16, 9, 17, 'Umbulali wayemi phezu kwenqaba yaseYizereli ekhangele isihlwele sikaYehu ekufikeni kwakhe, wathi: “Ndibona isihlwele. Wathi uYoram, Thabatha umkhweli wehashe, umthume ukuba aye kubakhawulela. Wathi, Luxolo na?', 'XHO22'),
(16, 9, 18, 'Waqubisana naye ekhwele ihashe, wathi, Utsho uYehova ukuthi, Luxolo na? Wathi uYehu, Yintoni na enawe noxolo? Wajika emva kwam, watsho umlindi, wathi, Umthunywa ufike kubo, kodwa akabuyi.', 'XHO22'),
(16, 9, 19, 'Wathuma nowesibini umkhweli-hashe, waya kubo, wathi, Utsho uYehova ukuthi, Luxolo na? Wathi uYehu, Yintoni na enawe noxolo? Jikela emva kwam.', 'XHO22'),
(16, 9, 20, 'Waxela umlindi, wathi, Uye wafika kubo, akabuya; ngokuba uqhuba ngobudlongodlongo.', 'XHO22'),
(16, 9, 21, 'Wathi uYehoram, Zilungiselele. Yalungiswa inqwelo yakhe. Waphuma uYehoram ukumkani wakwaSirayeli, noAhaziya ukumkani wakwaYuda, elowo esenqwelweni yakhe, baya kumkhawulela uYehu, bamfumana ezweni likaNabhoti waseYizereli.', 'XHO22'),
(16, 9, 22, 'uYoram wambona uYehu, wathi, Luxolo na, Yehu? Wathi: Luxolo olunjani na, xa ubuhenyu bukaIzebhele, nonyoko, nobugqwirha bakhe, buseninzi?', 'XHO22'),
(16, 9, 23, 'Wazijika uYoram izandla zakhe, wabaleka, wathi kuAhaziya, Ahaziya!', 'XHO22'),
(16, 9, 24, 'uYehu watola ngamandla akhe onke isaphetha sakhe, wamtyhoboza uYoram phakathi kwezandla zakhe, lwaphuma utolo entliziyweni yakhe, wawa enqwelweni yakhe.', 'XHO22'),
(16, 9, 25, 'Wandula ke uYehu wathi kuBhidekare umphathi wakhe: “Mphakamise uze umphose entsimini kaNabhoti waseYizereli;', 'XHO22'),
(16, 9, 26, 'Inene, ndiwabonile izolo amagazi kaNabhoti, namagazi oonyana bakhe, utsho uYehova; ndinibuyisele kule ndawo, utsho uYehova. Ke ngoko, mthabathe, umkhahlele emhlabeni, ngokwelizwi likaYehova.', 'XHO22'),
(16, 9, 27, 'uAhaziya ukumkani wakwaYuda, ekubonile oko, wasabela endlwini yomyezo. uYehu wamlandela, wathi, Nawe umbethe emotweni. Benjenjalo kwinduli eya eGur, eJibheleham. Wasabela eMegido, wafela khona.', 'XHO22'),
(16, 9, 28, 'Abakhonzi bakhe bamsa eYerusalem ngenqwelo, bamngcwabela engcwabeni lakhe kooyise, kwisixeko sikaDavide.', 'XHO22'),
(16, 9, 29, 'Ngomnyaka weshumi elinamnye kaYoram unyana ka-Ahabhi, uAhaziya waba ngukumkani kwaYuda.', 'XHO22'),
(16, 9, 30, 'Uthe uYehu akufika eYizereli, weva uIzebhele; waqaba ubuso bakhe, wanikina intloko, wakroba ngefestile.', 'XHO22'),
(16, 9, 31, 'Wangena uYehu esangweni, wathi, Uhleli ngoxolo na uZimri, wena umbuleleyo inkosi yakhe?', 'XHO22'),
(16, 9, 32, 'Wabuphakamisa ubuso bakhe efestileni, wathi, Ngubani na ophambi kwam? I-WHO? Kwaye kukho amathenwa amabini okanye amathathu, emlindile.', 'XHO22'),
(16, 9, 33, 'Wathi, Mkhahleleni phantsi. Zamkhahlela phantsi, igazi lakhe latshiza eludongeni nasemahasheni, amnyathela.', 'XHO22'),
(16, 9, 34, 'Wangena wadla, wasela; wathi, Hambani niye, nikhangele inkazana eqalekisiweyo, nimngcwabe; ngokuba yintombi yokumkani.', 'XHO22'),
(16, 9, 35, 'Baya kumngcwaba; abafumana nto kuye, yaba lukakayi, neenyawo, namasundu kuphela.', 'XHO22'),
(16, 9, 36, 'Babuya ke bamxelela. Wathi, Lilizwi likaYehova elo, awalithethayo ngoEliya waseTishbha, umkhonzi wakhe, lisithi, Inyama kaIzebhele yodliwa zizinja ezweni laseYizereli.', 'XHO22'),
(16, 9, 37, 'Yaye umzimba kaIzebhele uya kuba njengeliva entsimini kummandla waseYizereli; ukuze bangathi, Lo nguIzebhele.', 'XHO22'),
(16, 10, 1, 'uAhabhi ubenoonyana abamashumi asixhenxe kwaSamari. uYehu wabhala iincwadi, wazithumela kwaSamari kubathetheli baseYizereli, nakumadoda amakhulu, nakubanyusi baka-Ahabhi, wathi,', 'XHO22'),
(16, 10, 2, 'Xa nje ithe yafika kuni le ncwadi, ekubeni oonyana benkosi yenu benani, niseneenqwelo zokulwa, namahashe, nomzi onqatyisiweyo uneentonga;', 'XHO22'),
(16, 10, 3, 'Khangelani oyena mkhulu koonyana bokumkani wenu, nimmise etroneni kayise, niyilwele indlu yokumkani wenu.', 'XHO22'),
(16, 10, 4, 'Boyika kakhulu, bathi, Yabonani, ookumkani ababini bemi ngakuye; sizakulinda njani ngoku?', 'XHO22'),
(16, 10, 5, 'Bathumela ke umlindi wendlu, nomlindi womzi, namadoda amakhulu, nabafundisi boonyana kuYehu, besithi, Singabakhonzi bakho; konke osiwisele umthetho ngako, sokwenza; asiyi kumisa ukumkani; yenza okulungileyo emehlweni akho.', 'XHO22'),
(16, 10, 6, 'Wababhalela eyesibini incwadi, esithi, Ukuba ningakum, niliva ilizwi lam, thabathani iintloko zamadoda, oonyana benkosi yenu, nizizise kum ngomso eYizereli. Oonyana bokumkani, abangamadoda angamashumi asixhenxe, babenamadoda amakhulu omzi awayebakhulisa.', 'XHO22'),
(16, 10, 7, 'Yakufika kubo le ncwadi, babathabatha oonyana bokumkani, babulala amadoda angamashumi asixhenxe, bazifaka iintloko zabo ezingobozini, bamthumela eYizereli.', 'XHO22'),
(16, 10, 8, 'Wafika umthunywa, wamxelela, wathi, Bazizisile iintloko zoonyana bokumkani. Wathi, Yibeke zibe ziimfumba ezimbini ngaphandle kwesango kude kuse;', 'XHO22'),
(16, 10, 9, 'Ngentsasa elandelayo waphuma waza waphakama waza wathi kubo bonke abantu: “Nilungile; yabonani, ndithethile enkosini yam, ndathi, Ndiyibulele; kodwa ngubani obulele bonke aba bantu?', 'XHO22'),
(16, 10, 10, 'Yazini ke ngoku ukuba ilizwi likaYehova awalithethayo uYehova ngendlu ka-Ahabhi aliyi kuwa emhlabeni, ngenxa yokuba uYehova ukwenzile oko wakuthethayo ngomkhonzi wakhe uEliya.', 'XHO22'),
(16, 10, 11, 'uYehu wayibulala yonke enye indlu ka-Ahabhi uYizereli, nabalungiseleli bakhe bonke, nomzalwane wakhe, nababingeleli bakhe, kwada akwamshiya.', 'XHO22'),
(16, 10, 12, 'Wesuka ke, waya kwaSamari. Xa wayekwivenkile yokucheba endleleni.', 'XHO22'),
(16, 10, 13, 'uYehu waqubisana nabazalwana bakhe uAhaziya, ukumkani wakwaYuda, wathi, Ningoobani na nina? Aphendula athi, Singabazalwana baka-Ahaziya; siyehla siye kuthi ndlela-ntle kubantwana bokumkani nakubantwana benkosikazi.', 'XHO22'),
(16, 10, 14, 'Wathi, Babambeni besadla ubomi; Bababamba behleli, bababulala emhadini womchebi, amadoda amashumi mane anamabini; akashiya namnye kubo.', 'XHO22'),
(16, 10, 15, 'Wemka khona, wamtshisa uYonadabhi, unyana kaRekabhi, obeze kumkhawulela; wambulisa wathi kuye: Ithe tye na intliziyo yakho, njengokuba injalo intliziyo yam entliziyweni yakho? Waphendula uYonadabhi, wathi, Injalo. Ukuba kunjalo, ndinike isandla sakho. Wamnika ke isandla sakhe; wamsa kuye ngemoto.', 'XHO22'),
(16, 10, 16, 'Wathi: “Hamba nam, uze uyibone inzondelelo yam ngoYehova. Bamshiya ke emotweni yakhe.', 'XHO22'),
(16, 10, 17, 'Weza kwaSamari, wababulala bonke abaseleyo kuAhabhi kwaSamari, wada wamtshabalalisa ngokwelizwi likaYehova, awalithethayo kuEliya.', 'XHO22'),
(16, 10, 18, 'Wabahlanganisa uYehu bonke abantu, wathi kubo, uAhabhi wamkhonza kancinane uBhahali; uYehu womkhonza kunene.', 'XHO22'),
(16, 10, 19, 'Babizeni ngoko bonke abaprofeti bakaBhahali, abakhonzi bakhe bonke, nababingeleli bakhe bonke. Makungabikho mfuneko, ngokuba ndinombingelelo omkhulu kuBhahali; lowo usweleyo akayi kuphila. uYehu wenza loo nto ngobuqhokolo, ukuze abatshabalalise abakhonzi bakaBhahali.', 'XHO22'),
(16, 10, 20, 'Wathi uYehu, Memezani umthendeleko kaBhahali. Baceba ke.', 'XHO22'),
(16, 10, 21, 'uYehu wathumela ilizwi kuSirayeli wonke, beza bonke abakhonzi bakaBhahali, akwasala namnye ungezanga. Bangena endlwini kaBhahali; yazala indlu kaBhahali, kwathabathela kwelinye icala kwesa kwelinye.', 'XHO22'),
(16, 10, 22, 'Wathi kumgcini weengubo, Ndinike iingubo zabo bonke abakhonza uBhahali. Wabaphathela iingubo.', 'XHO22'),
(16, 10, 23, 'Wangena uYehu noYonadabhi, unyana kaRekabhi, endlwini kaBhahali, wathi kubakhonzi bakaBhahali, Quqelani nikhangele ukuba akukho bakhonzi bakaYehova na apha kuni, ingengabakhonzi bakaBhahali bodwa.', 'XHO22'),
(16, 10, 24, 'Ekungeneni kwabo beza kubingelela imibingelelo namadini anyukayo, uYehu wakhuphela phandle amadoda amashumi asibhozo anamabini, wathi, Xa ithe yasinda indoda kulaa madoda, ndiwazise esandleni senu, othe wayikhulula, umphefumlo wayo woba sesikhundleni somphefumlo wayo.', 'XHO22'),
(16, 10, 25, 'Kwathi, akugqiba ukulenza idini elinyukayo, uYehu wathi kwizigidimi nakumadindala, Ngenani nibabulale; makungaphumi mntu. Abaxabela ngohlangothi lwentshuntshe; bawagxotha amadindala namadindala, baya emzini wendlu kaBhahali.', 'XHO22'),
(16, 10, 26, 'Bandula ke bayikhupha imifanekiso yendlu kaBhahali baza bayitshisa.', 'XHO22'),
(16, 10, 27, 'Zayidiliza ilitye eliqingqiweyo likaBhahali nendlu kaBhahali zaza zayenza indlu esekwe namhla.', 'XHO22'),
(16, 10, 28, 'uYehu wamtshabalalisa ke uBhahali kwaSirayeli;', 'XHO22'),
(16, 10, 29, 'Ke uYehu akatyeka ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli, amathole egolide aseBheteli nakwaDan.', 'XHO22'),
(16, 10, 30, 'Wathi uYehova kuYehu, Ekubeni wenze okuthe tye emehlweni am, wenza ngako konke ngenxa yendlu ka-Ahabhi njengoko ndakwenzayo entliziyweni yam, ndohlala etroneni oonyana bakho besizukulwana sesine kwaSirayeli.', 'XHO22'),
(16, 10, 31, 'Ke uYehu akakunyamekelanga ukuhamba ngomyalelo kaYehova uThixo kaSirayeli ngentliziyo yakhe yonke; ngokuba akatyeka ezonweni zikaYarobheham, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 10, 32, 'Ngaloo mihla uYehova waqalisa ukubanciphisa oonyana bakaSirayeli, yaye uHazayeli waboyisa kuzo zonke iindawo zakwaSirayeli;', 'XHO22'),
(16, 10, 33, 'Ukususela eYordan ukuya ngasempuma, lonke ilizwe laseGiliyadi, amaGadi namaRubhen namaManase, ukusuka eArohere ekufuphi noMlambo iArnon ukusa eGiliyadi naseBhashan.', 'XHO22'),
(16, 10, 34, 'Ezinye izinto zikaYehu, nako konke awakwenzayo, nobugorha bakhe bonke, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(16, 10, 35, 'uYehu walala kooyise, wangcwatyelwa kwaSamari unyana wakhe uYehowahazi waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 10, 36, 'Yaye imihla awalawula ngayo uYehu kwaSirayeli eSamariya yaba yiminyaka engamashumi amabini anesibhozo.', 'XHO22'),
(16, 11, 1, 'Uthe uAtaliya, unina ka-Akasiya, akubona ukuba unyana wakhe ufile, wesuka wayitshabalalisa yonke indlu yakomkhulu.', 'XHO22'),
(16, 11, 2, 'Ke uYoshebha, intombi yokumkani uYoram, udade boAhaziya, wamthabatha uYowashe, unyana ka-Ahaziya, wamba phakathi koonyana bokumkani, ababulawayo; bamfihla yena nomondli wakhe egumbini lika-Ataliya, ukuze angabulawa.', 'XHO22'),
(16, 11, 3, 'Wazimela endlwini kaYehova iminyaka emithandathu. Ke uAtaliya wayelawula ihlabathi.', 'XHO22'),
(16, 11, 4, 'Ngomnyaka wesixhenxe, uYehoyada wathumela ukuba kubizwe abathetheli-makhulu nabathetheli bentlanganiso yamatyala, nabalindi, wabazisa kuye endlwini kaYehova, wenza umnqophiso nabo, wabazisa endlwini kaYehova wababonisa ubutyebi unyana wokumkani.', 'XHO22'),
(16, 11, 5, 'Wabawisela umthetho, esithi, Nize nenze loo nto; nesahlulo sesithathu kuni, ningenayo ngesabatha, soba ngabagcini bendlu yokumkani;', 'XHO22'),
(16, 11, 6, 'esinye isahlulo sesithathu soba sesangweni laseShure; esinye isahlulo sesithathu soba sesangweni emva kwabalindi; yigcine ke indlu, ingawi.', 'XHO22'),
(16, 11, 7, 'Yaye izahlulo ezibini kuni nonke, eniphumayo ngesabatha, ewe, zoyigcina indlu kaYehova kukumkani.', 'XHO22'),
(16, 11, 8, 'Nimphahle ukumkani ngeenxa zonke, elowo ephethe iintonga zakhe ngesandla; owathi ufika emasimini wobulawa; yiba nokumkani ekuphumeni kwakhe nasekungeneni kwakhe.', 'XHO22'),
(16, 11, 9, 'Benza ke abathetheli-khulu njengako konke abewise umthetho ngako uYehoyada umbingeleli, bathabatha elowo amadoda akowabo, abemelwe ukungena ngesabatha, nalawo amele ukuphuma ngesabatha, beza kuYehoyada umbingeleli.', 'XHO22'),
(16, 11, 10, 'Umbingeleli wawanika amakhulu abathetheli iintshuntshe namakhaka kaDavide ukumkani, abesendlwini kaYehova.', 'XHO22'),
(16, 11, 11, 'Bema abalindi, elowo ephethe iintonga zakhe ngesandla, bethabathela kwicala lasekunene lendlu besa kwelasekhohlo lendlu, ngasesibingelelweni nangasendlwini.', 'XHO22'),
(16, 11, 12, 'Wamkhupha unyana kakumkani waza wamthwesa isithsaba waza wanikela ubungqina ngaye; bamenza ukumkani, bamthambisa; baza baqhwaba izandla baza bathi: Ngamana uThixo angamsindisa ukumkani!', 'XHO22'),
(16, 11, 13, 'Uthe uAtaliya akusiva isandi sezigidimi nesabantu, waya ebantwini endlwini kaYehova.', 'XHO22'),
(16, 11, 14, 'Wathi akukhangela, wabona ukumkani emi ngakwilitye lesikhumbuzo ngokwesiko, neenkosana namaxilongo zikunye nokumkani, yaye bonke abantu belizwe babevuya yaye bevuthela amaxilongo; uAtaliya wazikrazula iingubo zakhe, wadanduluka, wathi, Sidengendini!', 'XHO22'),
(16, 11, 15, 'Umbingeleli uYehoyada wabawisela umthetho abathetheli-makhulu, abathenda bentendelezo, wathi kubo, Makahambe aphume ezweni, abulale ongasemva kwakhe ngentshuntshe ngokuba ebesithi umbingeleli, Makangabulawelwa endlwini kaYehova.', 'XHO22'),
(16, 11, 16, 'Bambamba ke; wahamba ngendlela angena ngayo amahashe endlwini yokumkani, wabulawelwa khona.', 'XHO22'),
(16, 11, 17, 'Wandula ke uYehoyada wenza umnqophiso phakathi kukaYehova nokumkani nabantu ukuba babe ngabantu bakaYehova; naphakathi kokumkani nabantu.', 'XHO22'),
(16, 11, 18, 'Bonke abantu behlabathi baya endlwini kaBhahali, bayidiliza; izibingelelo zakhe nemifanekiso yakhe bayiqhekeza; bambulala uMatan, umbingeleli kaBhahali, phambi kwezibingelelo. Umbingeleli wamisela abagcini bendlu kaYehova.', 'XHO22'),
(16, 11, 19, 'Wathabatha amakhulu abathetheli, nabathetheli, nabagcini, nabantu bonke belizwe; bamhlisa ukumkani, bamsa endlwini kaYehova, bangena ngesango lezigidimi endlwini yokumkani. Wahlala etroneni yookumkani.', 'XHO22'),
(16, 11, 20, 'Bavuya bonke abantu belizwe, sazola umzi; bambulalela uAtaliya ngekrele endlwini yokumkani.', 'XHO22'),
(16, 11, 21, 'uYowashe ubeminyaka isixhenxe ezelwe, ukuba ngukumkani kwakhe.', 'XHO22'),
(16, 12, 1, 'Waba ngukumkani uYowashe ngomnyaka wesixhenxe kaYehu; waba neminyaka emashumi mane engukumkani eYerusalem. Igama lonina belinguTsibhiya waseBher-shebha.', 'XHO22'),
(16, 12, 2, 'uYowashe waqhubeka esenza okulungileyo emehlweni kaYehova yonke imihla yakhe awayefundiswa ngayo uYehoyada umbingeleli.', 'XHO22'),
(16, 12, 3, 'Kodwa iindawo eziphakamileyo azizange zishenxiswe;', 'XHO22'),
(16, 12, 4, 'Wathi uYowashe kubabingeleli, Yonke imali yezinto ezingcwele, eziswayo endlwini kaYehova, nemali yabo bonke abaya kubalela, nemali leyo yabalinganiswa bonke abo, nemali leyo yonke eyabakhoyo entliziyweni yakhe ukuba azise endlwini kaYehova;', 'XHO22'),
(16, 12, 5, 'Mabathabathele kubo ababingeleli, elowo ngokokwazi kwakhe, bavale iintanda zendlu, naphi na apho kungafunyanwa khona umsantsa.', 'XHO22'),
(16, 12, 6, 'Kodwa ngomnyaka wamashumi amabini anesithathu wokumkani uYowashe, ababingeleli abazitywini iintanda zendlu.', 'XHO22'),
(16, 12, 7, 'Ukumkani uYowashe wabiza uYehoyada umbingeleli nabanye ababingeleli, wathi kubo, Yini na ukuba ningazitywini iintanda zendlu? uze ungamkeli mali kwabazana nawe, kodwa uyinike abo baphukileyo.', 'XHO22'),
(16, 12, 8, 'Bavuma ababingeleli ukuba bangabi sathabatha mali ebantwini, nokuba iya kulungisa iintanda zendlu.', 'XHO22'),
(16, 12, 9, 'uYehoyada umbingeleli wathabatha ityeya, wagqobhoza umngxuma esiguqweni sayo, wawubeka ecaleni lesibingelelo, ngasekunene ekungeneni komntu endlwini kaYehova; nababingeleli abagcini bomnyango babenayo yonke imali ebiziswe endlwini kaYehova.', 'XHO22'),
(16, 12, 10, 'Bathi bakubona ukuba kukho imali eninzi kwindawo yokugcina ubuncwane, unobhala kakumkani nombingeleli omkhulu benyuka baza bahlala ezingxoweni baza banikela ingxelo ngemali eyayifunyenwe endlwini kaYehova.', 'XHO22'),
(16, 12, 11, 'Bandula ke bayinikela imali emva kokuba ibekwe esandleni sabenzi bomsebenzi ababephethe indlu kaYehova; bayinika abachweli nabakhi ababesebenza endlwini kaYehova.', 'XHO22'),
(16, 12, 12, 'Nabaqingqi bamatye, nabaqingqi bamatye, bathenga imithi, namatye aqingqiweyo, ukuba kutywinwe iintanda zendlu kaYehova, nento yonke eyakhelwayo ukuba kuhlaziywe indlu.', 'XHO22'),
(16, 12, 13, 'Ke akwenziwa zitya zasilivere, nazitya zokutshiza, namaxilongo, nampahla yegolide, nasilivere, neziswe ngayo imali endlwini kaYehova.', 'XHO22'),
(16, 12, 14, 'Kodwa bayinika abasebenzi baza bayilungisa ngayo indlu kaYehova.', 'XHO22'),
(16, 12, 15, 'Ngaphezu koko, babengawabala amadoda awayenike imali ezandleni zawo ukuba anike abasebenzi, kuba ayesebenza enyanisekile.', 'XHO22'),
(16, 12, 16, 'Ke imali yetyala, nemali yesono, ibingaziswa endlwini kaYehova; babengababingeleli.', 'XHO22'),
(16, 12, 17, 'uHazayeli ukumkani wakwa-Aram wenyuka waya kulwa neGati, wayithimba; wajika uHazayeli ukuba enyuke aye eYerusalem.', 'XHO22'),
(16, 12, 18, 'Wazithabatha uYowashe ukumkani wakwaYuda zonke izipho ezingcwele, ababezingcwalisile ooYehoshafati, noYoram, noAhaziya, ooyise, ookumkani bakwaYuda, nezipho zakhe ezingcwele, negolide yonke ebikho, nasebuncwaneni bendlu kaYehova. uYehova nendlu yokumkani, wabathuma kuHazayeli ukumkani wakwa-Aram;', 'XHO22'),
(16, 12, 19, 'Ezinye izinto zikaYowashe, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 12, 20, 'Bandula bema abakhonzi bakhe baza benza iyelenqe baza bambulala uYowashe endlwini kaMilo eyehlela eSila.', 'XHO22'),
(16, 12, 21, 'Bamoyisa ke uYosagare unyana kaShimehati, noYozabhadi unyana kaSomere, abakhonzi bakhe, wafa; yaye bamngcwabela nookhokho bakhe kwisiXeko sikaDavide, waza uAmatsiya unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(16, 13, 1, 'Ngomnyaka wamashumi omabini anamithathu kaYowashe, unyana ka-Ahaziya, ukumkani wakwaYuda, waba ngukumkani uYowashe, unyana kaYehu, kumaSirayeli kwaSamari, iminyaka elishumi elinesixhenxe.', 'XHO22'),
(16, 13, 2, 'Wenza okubi emehlweni kaYehova, walandela izono zikaYarobheham unyana kaNebhati, awawohlwaya ngazo amaSirayeli; akatyeka kuyo.', 'XHO22'),
(16, 13, 3, 'Wavutha umsindo kaYehova kumaSirayeli, wawanikela esandleni sikaHazayeli ukumkani wakwa-Aram, nasesandleni sikaBhen-hadade, unyana kaHazayeli, yonke imihla.', 'XHO22'),
(16, 13, 4, 'Wathandaza uYehowahazi kuYehova, weva uYehova; ngokuba ubeyibonile ingcinezelo yoonyana bakaSirayeli, becinezelwa ngayo ngukumkani wakwa-Aram.', 'XHO22'),
(16, 13, 5, 'uYehova wabanikela oonyana bakaSirayeli umsindisi, baphuma esandleni sama-Aram, bakha ke oonyana bakaSirayeli iintente zabo, njengokwangaphambili.', 'XHO22'),
(16, 13, 6, 'Kodwa abazange baphambuke ezonweni zendlu kaYarobheham, awawonisa ngazo amaSirayeli, baqhubeka behamba kuzo; lisekho ihlathi lakwaSamari.)', 'XHO22'),
(16, 13, 7, 'Akamshiya ebantwini uYehowahazi, yaba ngamashumi amahlanu abamahashe, neshumi leenqwelo zokulwa, neshumi lamawaka omqikela; ngokuba wabatshabalalisa ukumkani wakwa-Aram, wabenza njengothuli lwekhuba.', 'XHO22'),
(16, 13, 8, 'Ezinye izinto zikaYowaka, nako konke awakwenzayo, nobugorha bakhe, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(16, 13, 9, 'Walala uYowashe kooyise; baza bamngcwabela eSamariya, yaye uYowashe unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(16, 13, 10, 'Ngomnyaka wamashumi omathathu anesixhenxe kaYowashe ukumkani wakwaYuda, waba ngukumkani uYowashe, unyana kaYowahazi, kwaSirayeli kwaSamari, iminyaka elishumi elinamithandathu.', 'XHO22'),
(16, 13, 11, 'Wenza okubi emehlweni kaYehova; akatyeka ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli; kodwa waya kuyo.', 'XHO22'),
(16, 13, 12, 'Ezinye izinto zikaYowashe, nako konke awakwenzayo, namandla akhe awalwa ngawo noAmatsiya ukumkani wakwaYuda, azibhalwanga na encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(16, 13, 13, 'Walala uYowashe kooyise; Wahlala uYarobheham etroneni yakhe, uYowashe wangcwatywa nookumkani bakwaSirayeli kwaSamari.', 'XHO22'),
(16, 13, 14, 'uElisha wagula ngenxa yesifo awafa ngaso. Wehla uYowashe, ukumkani wakwaSirayeli, waya kuye, walila ebusweni bakhe, esithi, Bawo, bawo, nqwelo yokulwa yakwaSirayeli, nabakhweli bayo!', 'XHO22'),
(16, 13, 15, 'Wathi uElisha kuye, Thabatha isaphetha notolo; Wamthengela isaphetha notolo.', 'XHO22'),
(16, 13, 16, 'Wathi kukumkani wakwaSirayeli, Sibeke isandla sakho kwisaphetha; Wasibeka isandla sakhe phezu kwayo, uElisha wabeka izandla zakhe phezu kwezandla zokumkani.', 'XHO22'),
(16, 13, 17, 'Waqhubeka wathi: “Vula ifestile esempuma. Watsho eyivula. Wathi uElisha, Yondla; Kwaye wadubula. Wathi, Lutolo losindiso oluphuma kuYehova, lutolo losindiso oluphuma kuma-Aram;', 'XHO22'),
(16, 13, 18, 'Wathi, Thabatha iintolo. Wazithabatha ke. Wathi kukumkani wakwaSirayeli, Betha emhlabeni. Wabetha kathathu waphakama.', 'XHO22'),
(16, 13, 19, 'Umfo wakwaThixo wavutha ngumsindo ngakuye, wathi, Unge utsho kahlanu, nokuba kathandathu; wawaxabela ama-Aram wada wawatshabalalisa; kodwa ngoku uya kuwahlasela kathathu ama-Aram.', 'XHO22'),
(16, 13, 20, 'Wafa ke uElisha, bamngcwaba. Kwaye amaqela amaMowabhi ahlasela ilizwe ekuqaleni konyaka.', 'XHO22'),
(16, 13, 21, 'Ke kaloku, bakuba bengcwaba umntu, babona igquba; bayiphosa indoda leyo engcwabeni likaElisha.', 'XHO22'),
(16, 13, 22, 'uHazayeli ukumkani wakwa-Aram waqhubeka ewaxabela amaSirayeli yonke imihla kaYehowahazi.', 'XHO22'),
(16, 13, 23, 'uYehova waba nemfesane kuwo, waba nemfesane kuwo, waba nemfesane kuwo, ngenxa yomnqophiso wakhe noAbraham noIsake noYakobi, akathanda ukubatshabalalisa, akabashiya phambi kobuso bakhe.', 'XHO22'),
(16, 13, 24, 'Wafa uHazayeli ukumkani wakwa-Aram; unyana wakhe uBhen-hadadi walawula esikhundleni sakhe.', 'XHO22'),
(16, 13, 25, 'uYowashe unyana kaYowakashe wabuya wayithimba esandleni sikaBhen-hadade unyana kaHazayeli, izixeko abezithimbile kuYowaka uyise ngemfazwe. uYowashe wamxabela izihlandlo zazithathu, wazakha izixeko zakwaSirayeli.', 'XHO22'),
(16, 14, 1, 'Ngomnyaka wesibini kaYowashe unyana kaYehowahazi, ukumkani wakwaSirayeli, waba ngukumkani uAmatsiya, unyana kaYowashe ukumkani wakwaYuda.', 'XHO22'),
(16, 14, 2, 'Ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mabini anesithoba engukumkani eYerusalem. Igama lonina belinguYodan waseYerusalem.', 'XHO22'),
(16, 14, 3, 'Wenza okuthe tye emehlweni kaYehova; kodwa kungekhona njengoDavide uyise; Wenza konke awakwenzayo uYowashe uyise.', 'XHO22'),
(16, 14, 4, 'Ke zona iindawo eziphakamileyo zonqulo zange zitshitshiswe, kodwa abantu babesabingelela beqhumisela ezigangeni.', 'XHO22'),
(16, 14, 5, 'Kwathi, bakuqiniseka ubukumkani esandleni sakhe, wababulala abakhonzi bakhe abambulalayo ukumkani uyise.', 'XHO22'),
(16, 14, 6, 'Ke oonyana bababulali akababulalanga, ngoko kubhaliweyo encwadini yomyalelo kaMoses, awawisa umthetho ngawo uYehova, esithi, Aba oyise mabangabulawa ngenxa yoonyana; oonyana mababulawe engabulawanga ukufa kooyise; kodwa elowo uya kubulawa ngesakhe isono.', 'XHO22'),
(16, 14, 7, 'Wabulala ishumi lamawaka amaEdom kwiNtlambo yeTyuwa waza wayithimba iSela edabini, wayibiza ngokuba yiYoketeli de kube namhla.', 'XHO22'),
(16, 14, 8, 'uAmatsiya wathumela abathunywa kuYowashe, unyana kaYowahazi, unyana kaYehu, ukumkani wakwaSirayeli, wathi, Yiza sijongane.', 'XHO22'),
(16, 14, 9, 'uYowashe ukumkani wakwaSirayeli wathumela kuAmatsiya ukumkani wakwaYuda, wathi, iTeshelo yaseLebhanon yathumela kumsedare oseLebhanon, isithi, Unyana wam mnike intombi yakho, ibe ngumkakho.', 'XHO22'),
(16, 14, 10, 'Uwoyisile uEdom, yakhukhumala intliziyo yakho; hlonipha oku, kwaye uhlale ekhaya;', 'XHO22'),
(16, 14, 11, 'Akaphulaphula uAmatsiya; Wenyuka ke uYowashe ukumkani wakwaSirayeli; bakhangelana ubuso, yena noAmatsiya ukumkani wakwaYuda, eBhete-shemeshe ezweni lakwaYuda.', 'XHO22'),
(16, 14, 12, 'uYuda wavutha ngumsindo kuSirayeli; basaba elowo waya ententeni yakhe.', 'XHO22'),
(16, 14, 13, 'uYowashe ukumkani wakwaSirayeli wamthabatha uAmatsiya ukumkani wakwaYuda, unyana kaYowashe, unyana ka-Ahaziya, wamsa eBhete-shemeshe, weza eYerusalem, waludiliza udonga lweYerusalem, wathabathela esangweni lakwaEfrayim, wada wesa eBhete-shemeshe isango likaEfrayim. Isango leKona liziikubhite ezimakhulu mane.', 'XHO22'),
(16, 14, 14, 'Wayithabatha yonke igolide nesilivere nezixhobo zonke ezafunyanwayo endlwini kaYehova nasebuncwaneni bendlu kakumkani namabanjwa, wabuyela kwaSamari.', 'XHO22'),
(16, 14, 15, 'Ke eminye imicimbi kaYowashe oko wakwenzayo namandla akhe nendlela awalwa ngayo noAmatsiya ukumkani wakwaYuda, akubhalwanga na encwadini yemicimbi yookumkani bakwaSirayeli?', 'XHO22'),
(16, 14, 16, 'uYowashe walala kooyise, wangcwatyelwa kwaSamari kookumkani bakwaSirayeli unyana wakhe uYerobhoham waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 14, 17, 'uAmatsiya, unyana kaYowashe, ukumkani wakwaYuda, wahlala iminyaka elishumi elinesihlanu emva kokufa kukaYowashe, unyana kaYehowahazi, ukumkani wakwaSirayeli.', 'XHO22'),
(16, 14, 18, 'Ezinye izinto zika-Amatsiya, ndithi, zibhaliwe na encwadini yemicimbi yookumkani bakwaYuda?', 'XHO22'),
(16, 14, 19, 'Bamceba eYerusalem, wasabela eLakishe; bathumela emva kwakhe eLakishe, bambulalela khona.', 'XHO22'),
(16, 14, 20, 'Bamthwala ngamahashe, wangcwatyelwa eYerusalem kooyise, emzini kaDavide.', 'XHO22'),
(16, 14, 21, 'Bonke abantu bakwaYuda bamthabatha uAzariya, owayeneminyaka elishumi elinesithandathu ubudala, bamenza ukumkani esikhundleni sikayise uAmatsiya.', 'XHO22'),
(16, 14, 22, 'Nguye owayakhayo iElati, wayibuyisela kwaYuda emva kokulala kokumkani kooyise.', 'XHO22'),
(16, 14, 23, 'Ngomnyaka weshumi elinesihlanu ka-Amatsiya unyana kaYowashe, ukumkani wakwaYuda, waba ngukumkani uYarobheham, unyana kaYowashe, ukumkani wakwaSirayeli, kwaSamari, iminyaka emashumi mane anamnye.', 'XHO22'),
(16, 14, 24, 'Wenza okubi emehlweni kaYehova, akatyeka kuzo zonke izono zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 14, 25, 'Yena walibuyisela ilizwe lakwaSirayeli, ethabathela ngaseHamati, wesa elwandle lwentlango, ngokwelizwi likaYehova, uThixo kaSirayeli, awalithethayo ngoYona unyana ka-Amitayi, umprofeti, umkhonzi wakhe kuthekani ngoGathefer.', 'XHO22'),
(16, 14, 26, 'Wakubona uYehova ukuba kukrakra kunene kwaSirayeli;', 'XHO22'),
(16, 14, 27, 'uYehova akazange athi uya kutshabalalisa igama likaSirayeli phantsi kwamazulu, kodwa wamsindisa ngesandla sikaYerobhoham unyana kaYowashe.', 'XHO22'),
(16, 14, 28, 'Amanye amazwi kaYarobheham, nako konke awakwenzayo, nobugorha bakhe, ukulwa kwakhe, nokuyibuyisela kwakhe iDamasko neHamati ezweni lakwaYuda kumaSirayeli, azibhalwanga encwadini yemicimbi kookumkani bakwaSirayeli?', 'XHO22'),
(16, 14, 29, 'Walala uYarobheham kooyise, nookumkani bakwaSirayeli; unyana wakhe uZekariya waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 15, 1, 'Ngomnyaka wamashumi omabini anesixhenxe kaYarobheham ukumkani wakwaSirayeli, waba ngukumkani uAzariya, unyana ka-Amatsiya, ukumkani wakwaYuda.', 'XHO22'),
(16, 15, 2, 'Ubeminyaka ilishumi elinamithandathu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mahlanu anamibini engukumkani eYerusalem. Igama likanina belinguYekoliya waseYerusalem.', 'XHO22'),
(16, 15, 3, 'Wenza okuthe tye emehlweni kaYehova, njengoko wenza ngako uAmatsiya uyise;', 'XHO22'),
(16, 15, 4, 'Ngaphandle kokuba iindawo eziphakamileyo azizange zishenxiswe, abantu babesabingelela kwiindawo eziphakamileyo yaye babenyusa neziqhumiso.', 'XHO22'),
(16, 15, 5, 'uYehova wambetha ukumkani, waba neqhenqa kwada kwayimini yokufa kwakhe, wahlala kwenye indlu. uYotam, unyana wokumkani, ubephethe indlu, ebalawula abantu belizwe.', 'XHO22'),
(16, 15, 6, 'Ezinye izinto zika-Azariya, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 15, 7, 'Walala uAzariya kooyise; yaye bamngcwaba nookhokho bakhe kwisiXeko sikaDavide, yaye uYotham unyana wakhe waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 15, 8, 'Ngomnyaka wamashumi omathathu anesibini ka-Azariya ukumkani wakwaYuda, uZekariya, unyana kaYarobheham, waba ngukumkani kumaSirayeli kwaSamari, iinyanga zantandathu.', 'XHO22'),
(16, 15, 9, 'Wenza okubi emehlweni kaYehova, njengoko benza ngako ooyise; akatyeka ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 15, 10, 'Wandula ke uShalum unyana kaYabheshe wamvukela waza wambulala phambi kwabantu, wambulala waza waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 15, 11, 'Ezinye izinto zikaZekariya, nanzo zibhaliwe encwadini yemicimbi yookumkani bakwaSirayeli.', 'XHO22'),
(16, 15, 12, 'Lilo eli ilizwi likaYehova awalixelela uYehu, lisithi, Kwesesine isizukulwana kuya kuhlala oonyana bakho etroneni yakwaSirayeli. Kwaye kwenzeka.', 'XHO22'),
(16, 15, 13, 'uShalum unyana kaYabheshe waba ngukumkani ngomnyaka wamashumi omathathu anesibhozo kaUziya ukumkani wakwaYuda; waba ngukumkani kwaSamari inyanga yonke.', 'XHO22'),
(16, 15, 14, 'Wenyuka uMenahem unyana kaGadi, esuka eTirtsa, weza kwaSamari, wambulala uShalum unyana kaYabheshe kwaSamari, wambulala, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(16, 15, 15, 'Ezinye izinto zikaShalum, neyelenqe awalicebayo, zibhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli.', 'XHO22'),
(16, 15, 16, 'Waza uMenahem wayixabela iTifsa nento yonke ebikuyo, nelizwe layo, kwathabathela eTirtsa; ngenxa enokuba bengamvulelanga, wasuka waboyisa; wabaqwenga bonke abamithiyo ababekuyo.', 'XHO22'),
(16, 15, 17, 'Ngomnyaka wamashumi omathathu anesithoba ka-Azariya ukumkani wakwaYuda, waba ngukumkani uMenahem, unyana kaGadi, kwaSamari, iminyaka yalishumi.', 'XHO22'),
(16, 15, 18, 'Wenza okubi emehlweni kaYehova, yaye akazange atyeke kwizono zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 15, 19, 'Walingenela ilizwe uPule, ukumkani waseAsiriya; uMenahem wamnika uPule iwaka leetalente zesilivere, ukuze isandla sakhe sibe naye, ukuze abuqinise ubukumkani esandleni sakhe.', 'XHO22'),
(16, 15, 20, 'uMenahem wayibiza imali yamaSirayeli kumadoda onke okulwa, ukuba anike ukumkani waseAsiriya iishekele ezimashumi mahlanu zesilivere. Wabuya ke ukumkani waseAsiriya, akaba sahlala kwelo zwe.', 'XHO22'),
(16, 15, 21, 'Zithini na ezinye izinto zikaMenahem, nako konke awakwenzayo, ndithi, nanzo zibhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli?', 'XHO22'),
(16, 15, 22, 'Walala uMenahem kooyise; unyana wakhe uPeka waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 15, 23, 'Ngomnyaka wamashumi omahlanu ka-Asariya ukumkani wakwaYuda, uPekakiya, unyana kaMenahem, waba ngukumkani kumaSirayeli kwaSamari, iminyaka emibini.', 'XHO22'),
(16, 15, 24, 'Wenza okubi emehlweni kaYehova; akatyeka ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 15, 25, 'Kodwa uPeka unyana kaRemaliya, intloko yakhe, wamvukela waza wambulala eSamariya kwibhotwe lendlu kakumkani, kunye noArgobhe noAri kunye namadoda angamashumi amahlanu akuGiliyadi; wambulala, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(16, 15, 26, 'Ezinye izinto zikaPekaya, nako konke awakwenzayo, nanzo kubhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli.', 'XHO22'),
(16, 15, 27, 'Ngomnyaka wamashumi omahlanu anesibini ka-Asariya ukumkani wakwaYuda, waba ngukumkani uPeka, unyana kaRemaliya, kumaSirayeli kwaSamari, iminyaka emashumi mabini.', 'XHO22'),
(16, 15, 28, 'Wenza okubi emehlweni kaYehova; akatyeka ezonweni zikaYarobheham unyana kaNebhati, awawonisa ngazo amaSirayeli.', 'XHO22'),
(16, 15, 29, 'Ngemihla kaPeka ukumkani wakwaSirayeli, kwafika uTigelate-pilezere ukumkani waseAsiriya, walithimba i-Iyon, neAbhele-bhete-maga, neYanowa, neKedeshe, neHatsore, neGiliyadi, neGalili, lonke ilizwe lakwaNafetali; Wafuduselwa eAsiriya.', 'XHO22'),
(16, 15, 30, 'uHoseya, unyana kaEla, wamvukela uPeka unyana kaRemaliya, wambulala, waza waqalisa ukulawula esikhundleni sakhe ngonyaka wamashumi amabini kaYotham unyana kaUziya.', 'XHO22'),
(16, 15, 31, 'Ezinye izinto zikaPeka, nako konke awakwenzayo, nanzo kubhaliwe encwadini yemicimbi yemihla yookumkani bakwaSirayeli.', 'XHO22'),
(16, 15, 32, 'Ngomnyaka wesibini kaPeka unyana kaRemaliya, ukumkani wakwaSirayeli, waba ngukumkani uYotam, unyana ka-Uziya, ukumkani wakwaYuda.', 'XHO22'),
(16, 15, 33, 'Ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamithandathu engukumkani eYerusalem. Igama lonina belinguYerusha, intombi kaTsadoki.', 'XHO22'),
(16, 15, 34, 'Wenza okuthe tye emehlweni kaYehova, wenza njengoko wenzayo uyise u-Uziya.', 'XHO22'),
(16, 15, 35, 'Ke iziganga azisuswanga; abantu babebingelela ezigangeni, nomsi wawunyuka unyuka. Wakha isango eliphezulu lendlu kaYehova.', 'XHO22'),
(16, 15, 36, 'Ezinye izinto zikaYotam, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 15, 37, 'Ngaloo mihla waqala uYehova ukuthumela uRetsin, ukumkani wakwa-Aram, noPeka, unyana kaRemaliya, ukuba baye kulwa noYuda.', 'XHO22'),
(16, 15, 38, 'uYotam walala kooyise, wangcwatyelwa kooyise emzini kaDavide uyise unyana wakhe uAhazi walawula esikhundleni sakhe.', 'XHO22'),
(16, 16, 1, 'Ngomnyaka weshumi elinesixhenxe kaPeka unyana kaRemaliya, waba ngukumkani uAhazi, unyana kaYotam, ukumkani wakwaYuda.', 'XHO22'),
(16, 16, 2, 'uAhazi ubeminyaka imashumi mabini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamithandathu engukumkani eYerusalem; akenzanga okuthe tye emehlweni kaYehova uThixo wakhe, njengoDavide uyise.', 'XHO22'),
(16, 16, 3, 'Waqhubeka ehamba ngendlela yookumkani bakwaSirayeli, wamvumela unyana wakhe ukuba acande emlilweni, ngokwezinto ezicekisekayo zeentlanga ezo uYehova wazigxothayo phambi koonyana bakaSirayeli.', 'XHO22'),
(16, 16, 4, 'Wenza umbingelelo waza wawunyusa umsi kwiindawo eziphakamileyo nasezindulini naphantsi kwayo yonke imithi eluhlaza.', 'XHO22'),
(16, 16, 5, 'Emva koko uRetsin ukumkani wakwa-Aram noPeka unyana kaRemaliya ukumkani wakwaSirayeli benyuka baya eYerusalem ukuze balwe, bamngqinga uAhazi, kodwa abazange bamoyise.', 'XHO22'),
(16, 16, 6, 'Ngelo xesha uRetsine ukumkani wakwa-Aram wayibuyisela iElati kwa-Aram waza wawagxotha amaYuda e-Elati; Afika ke ama-Aram e-Elati, ahlala khona unanamhla.', 'XHO22'),
(16, 16, 7, 'uAhazi wathumela abathunywa kuTigelate-pilezere, ukumkani waseAsiriya, wathi, Ndingumkhonzi wakho nonyana wakho; nyuka undisindise esandleni sokumkani wakwa-Aram, nasesandleni sokumkani wakwaSirayeli, oya kufikayo ngokuchasene nam.', 'XHO22'),
(16, 16, 8, 'Ngoko uAhazi wayithabatha isilivere negolide eyayifumaneka endlwini kaYehova nakubuncwane bendlu kakumkani waza wayithumela njengesipho kukumkani waseAsiriya.', 'XHO22'),
(16, 16, 9, 'Ukumkani waseAsiriya wamphulaphula, waza ukumkani waseAsiriya wayixabela iDamasko, wayithimba, wabathimba abantu bakhe, wabasa eKire, wambulala uRetsine.', 'XHO22'),
(16, 16, 10, 'Ukumkani uAhazi waya kumkhawulela uTigelate-pilezere, ukumkani waseAsiriya, eDamasko, wasibona isibingelelo eDamasko; Ukumkani uAhazi wathumela ku-Uriya umbingeleli umfanekiso wesibingelelo, nomfanekiso waso, ngokwendlela yaso yonke.', 'XHO22'),
(16, 16, 11, 'Wasakha isibingelelo u-Uriya umbingeleli, njengako konke abemthumele khona uAhazi ukumkani eDamasko; yaye umbingeleli u-Uriya waba nxamnye noKumkani uAhazi waseDamasko.', 'XHO22'),
(16, 16, 12, 'Uthe akufika ukumkani evela eDamasko, wasibona ukumkani isibingelelo eso, yaye ukumkani waya esibingelelweni waza wabingelela phezu kwaso.', 'XHO22'),
(16, 16, 13, 'Walitshisa idini lakhe elinyukayo nomnikelo wakhe wokudla, wathulula umnikelo wakhe othululwayo, wafefa ngegazi lombingelelo wakhe wombulelo esibingelelweni.', 'XHO22'),
(16, 16, 14, 'Wasondeza isibingelelo sobhedu esiphambi koYehova phambi kwendlu, phakathi kwesibingelelo nendlu kaYehova, wasibeka kwicala elingasentla lesibingelelo.', 'XHO22'),
(16, 16, 15, 'Ukumkani uAhazi wamwisela umthetho u-Uriya umbingeleli, wathi, Kwisibingelelo esikhulu qhumisela idini elinyukayo lakusasa, nedini langokuhlwa, nedini elinyukayo lokumkani, nedini lakhe elinyukayo, nedini elinyukayo labantu bonke belizwe, nedini elinyukayo labantu nomnikelo wokudla, neminikelo yazo ethululwayo; lonke igazi ledini elinyukayo, nalo lonke igazi lombingelelo, litshize phezu kwalo; yaye isibingelelo sebandla siya kubonakala kum.', 'XHO22'),
(16, 16, 16, 'Wenza ke u-Uriya umbingeleli njengako konke awamwisela umthetho ngako ukumkani uAhazi.', 'XHO22'),
(16, 16, 17, 'Ukumkani uAhazi wawanqumla ke amasondo eenqwelo zokuhlambela, wayisusa kuzo indebe yokuhlambela; waluthoba ulwandle empahleni yobhedu ebiphantsi kwalo, walubeka endaweni egalelwe ngamatye.', 'XHO22'),
(16, 16, 18, 'Yaye indawo yokuzimela yesabatha ababeyakha endlwini nomnyango wokumkani ophuma ngaphandle, wayishenxisa endlwini kaYehova kukumkani waseAsiriya.', 'XHO22'),
(16, 16, 19, 'Ke eminye imicimbi ka-Ahazi awayenzayo, akubhalwanga na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 16, 20, 'Ekugqibeleni uAhazi walala kookhokho bakhe waza wangcwatywa nookhokho bakhe kwisiXeko sikaDavide; uHezekiya unyana wakhe waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 17, 1, 'Ngomnyaka weshumi elinesibini ka-Ahazi ukumkani wakwaYuda, uHoseya, unyana kaEla, waba ngukumkani wakwaSirayeli eSamariya, iminyaka yasibhozo yedwa.', 'XHO22'),
(16, 17, 2, 'Wenza okubi emehlweni kaYehova, kodwa kungekhona njengookumkani bakwaSirayeli ababengaphambi kwakhe.', 'XHO22'),
(16, 17, 3, 'uShalemanesere ukumkani waseAsiriya walwa naye; uHoseya waba ngumkhonzi wakhe, wampha izipho.', 'XHO22'),
(16, 17, 4, 'Yaye ukumkani waseAsiriya wabona icebo lesiphithiphithi kuHoseya, ngenxa yokuba wathumela abathunywa kukumkani waseYiputa yaye akazange azise isipho kukumkani waseAsiriya njengoko wayesenza minyaka le; ke ngoko, ukumkani waseAsiriya wamvalela entolongweni, waza wamkhonkxa entolongweni.', 'XHO22'),
(16, 17, 5, 'Ukumkani waseAsiriya walityhutyha lonke ilizwe, wayihlasela iSamari, wayingqinga iminyaka emithathu.', 'XHO22'),
(16, 17, 6, 'Ngonyaka wesithoba kaHoseya, ukumkani waseAsiriya wayithimba iSamariya waza wathimba oonyana bakaSirayeli wabasa eAsiriya, wababeka eHala naseHabhore entlanjeni yomlambo waseGoshan nakwizixeko zamaMedi.', 'XHO22'),
(16, 17, 7, 'Ngokuba oonyana bakaSirayeli bonayo kuYehova uThixo wabo, owabakhuphayo ezweni laseYiputa ngesandla sikaFaro ukumkani waseYiputa, boyika thixo bambi;', 'XHO22'),
(16, 17, 8, 'Waqhubeka ehamba ngokwemiyalelo emiselweyo yeentlanga ezo uYehova wazigxothayo phambi koonyana bakaSirayeli nangokwemithetho yookumkani bakwaSirayeli ababeyenzile.', 'XHO22'),
(16, 17, 9, 'Oonyana bakaSirayeli benza ngasese izinto ezingafanelekanga kuYehova uThixo wabo, baza bazakhela iindawo eziphakamileyo kuzo zonke izixeko zabo, ukususela kwinqaba yokulinda ukusa kwisixeko esibiyelwe ngodonga.', 'XHO22'),
(16, 17, 10, 'Bamisa imifanekiso neziqu zemithi engcwele kuzo zonke iinduli eziphakamileyo naphantsi kwayo yonke imithi eluhlaza.', 'XHO22'),
(16, 17, 11, 'Baqhumisa umsi ezigangeni zonke, njengoko zenjenjalo iintlanga awazikhokelayo uYehova phambi kwabo; yaye benza izinto ezimbi ukuze bamcaphukise uYehova.', 'XHO22'),
(16, 17, 12, 'Kuba bakhonza izithixo, awayethe uYehova kubo: Ize ningakwenzi oko.', 'XHO22'),
(16, 17, 13, 'Waqononondisa uYehova kuSirayeli nakwaYuda, ngabaprofeti bonke nangemibono yonke, wathi, Buyani ezindleleni zenu ezimbi, niyigcine imithetho yam nemithetho yam, ngokomyalelo wonke endawumisela ooyihlo, endawumiselayo kuni. Ndithunywe kuni ngabakhonzi bam, abaprofeti.', 'XHO22'),
(16, 17, 14, 'Abaphulaphula, bazenza lukhuni iintamo zooyise abangakholwanga kuYehova uThixo wabo.', 'XHO22'),
(16, 17, 15, 'Bayigatya imithetho yakhe nomnqophiso awawenzayo nookhokho babo, nobungqina bakhe awayebungqinele kubo; balandela ilize, aba nto, balandela iintlanga ezibangqongileyo, awabawiselayo umthetho uYehova ukuba bangenzi.', 'XHO22'),
(16, 17, 16, 'Bayishiya yonke imiyalelo kaYehova uThixo wabo baza bazenzela imifanekiso eqingqiweyo, amathole amabini eenkomo baza benza netyholo baqubuda kuzo zonke izidalwa zasezulwini baza bakhonza uBhahali.', 'XHO22'),
(16, 17, 17, 'Batshisa oonyana babo neentombi zabo emlilweni, bekhafula, nangokukhafula, bazithengisela ukwenza okubi emehlweni kaYehova, ukuba bamqumbise.', 'XHO22'),
(16, 17, 18, 'uYehova wavutha kunene ngumsindo ngakumaSirayeli, wawasusa ebusweni bakhe; akusekho bani, sisizwe sakwaYuda sodwa.', 'XHO22'),
(16, 17, 19, 'KwanamaYuda akazange ayigcine imiyalelo kaYehova uThixo wawo, kodwa aqhubeka elandela imiyalelo emiselweyo kaSirayeli awayeyenzile.', 'XHO22'),
(16, 17, 20, 'Wayilahla ke uYehova yonke indlu kaSirayeli, wabacudisa, wabanikela esandleni sabatshabalalisa, wada wabasusa ebusweni bakhe.', 'XHO22'),
(16, 17, 21, 'Ngokuba wawakrazula amaSirayeli endlwini kaDavide; bamenza ukumkani uYarobheham, unyana kaNebhati; uYarobheham wawagxotha amaSirayeli ekumlandeleni uYehova, wawonisa ngesono esikhulu.', 'XHO22'),
(16, 17, 22, 'Bahamba oonyana bakaSirayeli ezonweni zonke zikaYarobheham, awazenzayo; abazange baphambuke;', 'XHO22'),
(16, 17, 23, 'wada uYehova wamsusa amaSirayeli kuye, njengoko wakuthethayo ngabakhonzi bakhe bonke, abaprofeti. Bafuduselwa ke oonyana bakaSirayeli ezweni labo, basiwa eAsiriya unanamhla.', 'XHO22'),
(16, 17, 24, 'Wandula ke ukumkani waseAsiriya wazisa amadoda avela eBhabhiloni naseKuta naseAwa naseHamati naseSefarvayim waza wawabeka kwizixeko zaseSamariya endaweni yoonyana bakaSirayeli; bayithimba iSamariya baza bahlala kuyo izixeko ezikhoyo.', 'XHO22'),
(16, 17, 25, 'Kwathi ekuhlaleni kwabo khona abamoyika uYehova; uYehova wathumela iingonyama phakathi kwabo, zababulala abanye kubo.', 'XHO22'),
(16, 17, 26, 'Bathetha ngoko kukumkani waseAsiriya, bathi, Izizwe owazigqogqayo, wazinikela ukuba zime emizini yakwaSamari, zona aziyazi indlela yoThixo welo lizwe ke ngoko, wathumela iingonyama phakathi kwabo, zababulala, ngenxa yokuba bengayazi indlela yoThixo wehlabathi.', 'XHO22'),
(16, 17, 27, 'Wawisa umthetho ukumkani waseAsiriya wathi, Thabatha abe mnye kubabingeleli enabakhuphayo khona; mabahambe, bahlale khona, babafundise indlela yoThixo wehlabathi.', 'XHO22'),
(16, 17, 28, 'Weza omnye wababingeleli, ababebafudusile kwaSamari, wahlala eBheteli, wabafundisa ukumoyika uYehova.', 'XHO22'),
(16, 17, 29, 'Luhlanga lwazenzela olwalo uthixo, lwaza lwambeka ezindlwini zezindlu eziphakamileyo, ezo abezakhiwe ngamaSamariya, uhlanga ngohlanga emizini yalo, apho beluhlala khona.', 'XHO22'),
(16, 17, 30, 'Amadoda aseBhabheli enza iSukoti-bhenoti, amadoda aseKuti enza uNergali, amadoda aseHamati enza uAshima.', 'XHO22'),
(16, 17, 31, 'IiAve zenza uNibhas noTartake, yaye amaFarvayim atshisa oonyana bawo emlilweni ngenxa ka-Adrameleki noAnameleki, oothixo baseSefarvayim.', 'XHO22'),
(16, 17, 32, 'Bayamoyika uYehova, babamisele ababingeleli abakhulu abaphuma phantsi kwabo, babaqhumisele ezindlwini eziphakamileyo.', 'XHO22'),
(16, 17, 33, 'uYehova ayemoyika, oothixo bawo ebakhonza njengeentlanga ababefuduse kuzo.', 'XHO22'),
(16, 17, 34, 'Unanamhla asaqhubeka esenza ngokwezithethe zawo, akamhloneli uYehova, angenzi ngokwemithetho yawo, namasiko awo, nomthetho nomthetho, abewise umthetho ngawo uYehova koonyana bakaYakobi, ababewuwisela umthetho lowo wambiza ngokuba nguSirayeli.', 'XHO22'),
(16, 17, 35, 'uYehova wenza umnqophiso nabo, wabawisela umthetho, wathi, Ize ningaboyiki thixo bambi, ningabakhonzi, ningabakhonzi, ningabingeleli kubo.', 'XHO22'),
(16, 17, 36, 'Ke yena uYehova, owanikhuphayo ezweni laseYiputa ngamandla amakhulu, nangesandla esolukileyo, yena yedwa eniya kumoyika, nithandaze kuye, nibingelele kuye;', 'XHO22'),
(16, 17, 37, 'Imithetho nezigwebo nomyalelo nomyalelo anibhalele wona, niwenze ngenyameko kude kuse ephakadeni; nize ningaboyiki thixo bambi;', 'XHO22'),
(16, 17, 38, 'umnqophiso endawenza nani, ize ningawulibali; ningaboyiki thixo bambi.', 'XHO22'),
(16, 17, 39, 'Kodwa moyike uYehova uThixo wakho; anisindise esandleni seentshaba zenu zonke.', 'XHO22'),
(16, 17, 40, 'Kodwa abaphulaphulanga kubo, benza njengangaphambili.', 'XHO22'),
(16, 17, 41, 'Ezi ntlanga zazimoyika uYehova, zikhonza nemifanekiso yazo eqingqiweyo, oonyana bazo noonyana boonyana bazo; njengoko benza ngako ooyise, baseyenza nanamhla.', 'XHO22'),
(16, 18, 1, 'Ngomnyaka wesithathu kaHoseya unyana kaEla, ukumkani wakwaSirayeli, waba ngukumkani uHezekiya, unyana ka-Ahazi ukumkani wakwaYuda.', 'XHO22'),
(16, 18, 2, 'Ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mabini anesithoba engukumkani eYerusalem. Igama lonina belinguAbhi, intombi kaZekariya.', 'XHO22'),
(16, 18, 3, 'Wenza okuthe tye emehlweni kaYehova, njengoko wenzayo uDavide uyise.', 'XHO22'),
(16, 18, 4, 'Wazisusa iindawo eziphakamileyo waza waqhekeza izimiso zamatye wagawula amatyholo waza wayaphula inyoka yobhedu awayenzayo uMoses, ngenxa yokuba kwada kwayiloo mihla oonyana bakaSirayeli babesenza umsi ophuma kuyo, yaye wayibiza ngokuthi. Nehustan.', 'XHO22'),
(16, 18, 5, 'Wakholosa ngoYehova uThixo kaSirayeli; kangangokuba emva kwakhe akuzange kubekho unjengaye kubo bonke ookumkani bakwaYuda nakubo bonke abo babengaphambi kwakhe.', 'XHO22'),
(16, 18, 6, 'Ngenxa enokuba wanamathela kuYehova, akatyeka kuye; wayigcina imithetho abethe uYehova wayiwisela uMoses.', 'XHO22'),
(16, 18, 7, 'uYehova waba naye; naphi na apho aya khona, waphumelela, wagwilika kukumkani waseAsiriya, akamkhonza.', 'XHO22'),
(16, 18, 8, 'Wawaxabela amaFilisti, wada wesa eGaza nasemdeni wayo, ethabathela enqabeni yababoniseli, wesa emzini onqatyisiweyo.', 'XHO22'),
(16, 18, 9, 'Ngomnyaka wesine wokumkani uHezekiya, kwaba ngumnyaka wesixhenxe kaHoseya unyana kaEla, ukumkani wakwaSirayeli, uShalmanesere, ukumkani waseAsiriya, wayifikela iSamari, wayingqinga.', 'XHO22'),
(16, 18, 10, 'Bayithimba ekupheleni kweminyaka emithathu, kwathi ngonyaka wesithandathu kaHezekiya, oko kukuthi, umnyaka wesithoba kaHoseya, ukumkani wakwaSirayeli, yathinjwa iSamari.', 'XHO22'),
(16, 18, 11, 'Wandula ke ukumkani waseAsiriya wabakhokela oonyana bakaSirayeli ukuya kufikelela eAsiriya waza wababeka eHala naseHabhore entlanjeni yomsinga waseGoshan nakwizixeko zamaMedi.', 'XHO22'),
(16, 18, 12, 'Ngenxa yokuba bengaliphulaphulanga ilizwi likaYehova uThixo wabo, basuka bawaphula umnqophiso wakhe, nayo yonke into awawisa umthetho ngayo uMoses, umkhonzi kaYehova, abazange bayenze.', 'XHO22'),
(16, 18, 13, 'Ngonyaka weshumi elinesine kaKumkani uHezekiya, uSenaribhe ukumkani waseAsiriya wazihlasela zonke izixeko ezinqatyisiweyo zakwaYuda waza wazithimba.', 'XHO22'),
(16, 18, 14, 'Wathumela uHezekiya ukumkani wakwaYuda kukumkani waseAsiriya eLakishe, wathi, Ndihlulekile; Jika; oko undibeke khona ndiya kukuthwala. Ukumkani waseAsiriya wamnika uHezekiya ukumkani wakwaYuda amakhulu amathathu eetalente zesilivere, namashumi omathathu eetalente zegolide.', 'XHO22'),
(16, 18, 15, 'uHezekiya wamnika yonke isilivere eyafunyanwa endlwini kaYehova, nasebuncwaneni bendlu yokumkani.', 'XHO22'),
(16, 18, 16, 'Ngelo xesha uHezekiya wayikrola igolide kwiingcango zetempile kaYehova nakwimigubasi awayeyigqume uHezekiya ukumkani wakwaYuda, wayinika ukumkani waseAsiriya.', 'XHO22'),
(16, 18, 17, 'Ukumkani waseAsiriya wasusa uTartan noRabhesarisi noRabheshe eLakishe, baya kukumkani uHezekiya eYerusalem, benempi enzima. Benyuka, beza eYerusalem. Benyukile ke, bafika, bema emseleni wechibi eliphezulu, endleleni ephambili yentsimi yokuhlambela.', 'XHO22'),
(16, 18, 18, 'Bambiza ukumkani, noEliyakim unyana kaHilekiya umgcini wendlu noShebhena unobhala noYowaki unyana ka-Asafu umphathi wenkundla, waphuma waya kubo.', 'XHO22'),
(16, 18, 19, 'Bathi ke ababingeleli kubo, Yithini kuHezekiya, Itsho iNkosi enkulu, ukumkani waseAsiriya, ukuthi, Liyintoni na ithemba lakho?', 'XHO22'),
(16, 18, 20, 'Uthi (uthetha into engento): Ndinenjongo nobugorha bokulwa. Khona umthembile bani ukuba undijike?', 'XHO22'),
(16, 18, 21, 'Uyabona, ukholose ngeYiputa, le ngcongolo evikivekileyo, ethi, othe wayama ngayo, imngene esandleni, imhlabe; uya kuba njalo kuFaro, ukumkani waseYiputa, nakubo bonke abakholose ngaye.', 'XHO22'),
(16, 18, 22, 'Ke ukuba nithi kum, Sikholose ngoYehova uThixo wethu, ndithi, Asinguye na uHezekiya owabonayo izibingelelo eziphakamileyo nezibingelelo zakhe, wathi kumaYuda nakwiYerusalem, Ize niqubude phambi kwesi sibingelelo eYerusalem apha?', 'XHO22'),
(16, 18, 23, 'Ke kaloku, yenza isivumelwano nokumkani wam, ukumkani waseAsiriya, yaye ndokunika amawaka amabini amahashe ukuba unokukhwelisa abakhweli kuwo.', 'XHO22'),
(16, 18, 24, 'Wothini na ke ukufulathela omnye wakubathetheli babakhonzi abancinane benkosi yam, ukholose ngeYiputa ngeenqwelo zokulwa nangabamahashe?', 'XHO22'),
(16, 18, 25, 'Ngokunje ndingaphandle koYehova, ukuze ndime kule ndawo ukuba ndiyonakalise? uYehova uthe kum, Nyuka uye kweli lizwe, ulonakalise.', 'XHO22'),
(16, 18, 26, 'Ngoko uEliyakim unyana kaHilekiya noShebhena noYowaki bathi kuRabheshake: “Thetha kubakhonzi bakho ngesiSiriya; ngokuba siyasiva, anithethi kuthi ngesiYuda, ukuze nive abantu abaseludongeni.', 'XHO22'),
(16, 18, 27, 'Wathi uRabheke kubo, Inkosi yam indithume ukuba ndiwathethe la mazwi enkosini yakho na, nakuwe na? Ndathi ke, akandithumanga na kumadoda abehleli eludongeni, ukuze adle ilindle lawo, asele newayini yawo?', 'XHO22'),
(16, 18, 28, 'Wasukuma uRabheshake waza wadanduluka ngelizwi elikhulu ngolwimi lwesiYuda wathi: “Liveni ilizwi lokumkani omkhulu, ukumkani waseAsiriya!', 'XHO22'),
(16, 18, 29, 'Wathi uYehova, Makanganilukuhli uHezekiya, ngokuba akayi kuba nako ukunihlangula esandleni sakhe.', 'XHO22'),
(16, 18, 30, 'Musani ukumvumela uHezekiya ukuba anithembise uYehova, esithi, uYehova uya kusihlangula okunene, nesi sixeko asiyi kunikelwa esandleni sokumkani waseAsiriya.', 'XHO22'),
(16, 18, 31, 'Musani ukumphulaphula uHezekiya, ngokuba uthe ukumkani waseAsiriya, Yenzani umnqophiso nam ngomnikelo; amanzi equla lakhe;', 'XHO22'),
(16, 18, 32, 'ndide ndize ndinise kwilizwe elinjengelizwe lenu, ilizwe lengqolowa nelewayini, ilizwe lesonka nezidiliya, ilizwe leoli lomnquma nobusi, ukuze niphile, ningafi; Akanicenga uHezekiya ngokuthi, uYehova uya kusisindisa.', 'XHO22'),
(16, 18, 33, 'Bakho na oothixo beentlanga, abalihlanguleyo ilizwe labo esandleni sokumkani waseAsiriya?', 'XHO22'),
(16, 18, 34, 'Baphi na oothixo baseHamati neArpadi? Baphi na oothixo baseSefarvayim, neHena, neIwa? Balihlangule na elakwaSamari esandleni sam?', 'XHO22'),
(16, 18, 35, 'Nguwuphi na koothixo bonke bala mazwe owalihlangulayo ilizwe lakhe esandleni sam, ukuze uYehova ayisindise iYerusalem esandleni sam?', 'XHO22'),
(16, 18, 36, 'Bathi tu abantu, abamphendula; ngokuba ilizwi lokumkani belisithi, Ze ningamphenduli.', 'XHO22'),
(16, 18, 37, 'Weza uEliyakim, unyana kaHilekiya, umgcini wendlu, noShebhena umbhali, noYowaki unyana ka-Asafu, umphathi, kuHezekiya, benxibe iingubo ezikrazukileyo, bamxelela amazwi kaRabheshake.', 'XHO22'),
(16, 19, 1, 'Akuva ukumkani uHezekiya, wazikrazula iingubo zakhe, wazigqubuthela ngezirhwexayo, wangena endlwini kaYehova.', 'XHO22'),
(16, 19, 2, 'Ngoko wathumela uEliyakim owayephethe indlu, uShebhena unobhala namadoda amakhulu ababingeleli benxibe amarhonya kuIsaya umprofeti unyana ka-Amotsi.', 'XHO22'),
(16, 19, 3, 'Bathi kuye, Kwathiwa nguHezekiya, Namhlanje yimini yokusindeka, yeyokugiba, yogiba; ngokuba kuzelwe oonyana, akakho ke amandla okuzala.', 'XHO22'),
(16, 19, 4, 'Mhlawumbi uYehova uThixo wakho angaweva onke amazwi kaRabheshake, othunywe ngukumkani waseAsiriya inkosi yakhe, ukungcikiva uThixo ophilileyo; wowakhalimela ke amazwi uYehova uThixo wakho, awavileyo.', 'XHO22'),
(16, 19, 5, 'Beza ke abakhonzi bokumkani uHezekiya kuIsaya.', 'XHO22'),
(16, 19, 6, 'Wathi uIsaya kubo, Yizani, nithi enkosini yenu, Utsho uYehova ukuthi, Musa ukuwoyika amazwi owavileyo, abawenze kum abakhonzi bokumkani waseAsiriya.', 'XHO22'),
(16, 19, 7, 'Yabona, ndimthumela umoya, ukuba eve udaba olubi, abuyele ezweni lakhe; yaye ndiya kumnqumla ngekrele elizweni lakhe.', 'XHO22'),
(16, 19, 8, 'Ngoko zabuya iinkosana zikaRabheshake zaza zamfumana ukumkani waseAsiriya esilwa neLibhena, ngenxa yokuba wayevile ukuba wayeyishiyile iLakishe.', 'XHO22'),
(16, 19, 9, 'Wathi akuva ngoTiraka, ukumkani wakwaKushi, Nanko ephuma eza kulwa nawe; wabuya wathuma abathunywa kuHezekiya, esithi,', 'XHO22'),
(16, 19, 10, 'Yiza uthethe kuHezekiya, ukumkani wakwaYuda, uthi, Makangakulukuhli uThixo wakho, okholose ngaye wena, usithi, Ayisayi kunikelwa iYerusalem esandleni sokumkani waseAsiriya.', 'XHO22'),
(16, 19, 11, 'Uyabona, ukuvile wena abakwenzayo ookumkani baseAsiriya kumazwe onke, ngokuwatshabhisa; kwaye uya kusindiswa?', 'XHO22'),
(16, 19, 12, 'Oothixo beentlanga ke bazisindisa ooyise na? njengeGoshan, neHaran, neReshefe, noonyana baka-Eden ababeseTelazare?', 'XHO22'),
(16, 19, 13, 'Uphi na ukumkani waseHamati nokumkani waseArpadi nokumkani wesixeko saseSefarvayim saseHena naseIwa?', 'XHO22'),
(16, 19, 14, 'Wathabatha uHezekiya incwadi ebhalwe ngesandla, ephuma kumthunywa lowo, wayifunda; yaye uHezekiya wenyuka waya endlwini kaYehova waza wayivula phambi koYehova.', 'XHO22'),
(16, 19, 15, 'Wathandaza uHezekiya phambi koYehova, wathi, Yehova, Thixo kaSirayeli, uhleli phezu kweekerubhi, unguye wena wedwa uthixo kwizikumkani zonke zehlabathi; Wenza izulu nehlabathi.', 'XHO22'),
(16, 19, 16, 'Yehova, vula indlebe yakho, uve; Yehova, vula amehlo akho, ukhangele, uweve amazwi kaSaneribhe, omthume ukuba angcikive uThixo ophilileyo.', 'XHO22'),
(16, 19, 17, 'Okunene, uYehova, ookumkani baseAsiriya baziphanzisile iintlanga namazwe azo;', 'XHO22'),
(16, 19, 18, 'babanikela oothixo bazo emlilweni, kuba babengethixo, bebengumsebenzi wezandla zomntu, umthi nelitye; babatshabalalisa ke.', 'XHO22'),
(16, 19, 19, 'Ke kaloku, Yehova Thixo wethu, sihlangule esandleni sakhe, zazi zonke izikumkani zehlabathi, ukuba wena umnye, Yehova Thixo.', 'XHO22'),
(16, 19, 20, 'Wathumela ke uIsaya, unyana ka-Amotsi, kuHezekiya, esithi, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndikuvile, undithandazele ngako uSaneribhe, ukumkani waseAsiriya.', 'XHO22'),
(16, 19, 21, 'Lilo eli ilizwi awalithethayo uYehova ngaye; Intombi, intombi enguZiyon, egxeka, ihleka; Intombi enguYerusalem yahlunguzela intloko phezu kwakho.', 'XHO22'),
(16, 19, 22, 'Ngubani na lo umtshabhisileyo, umnyelisileyo? Uliphakamise kubani na ilizwi lakho, uwaphakamisele amehlo akho kubani na? nxamnye naLowo Ungcwele kaSirayeli.', 'XHO22'),
(16, 19, 23, 'Wamngcikiva uYehova ngezithunywa zakho, usithi, Ngobuninzi beenqwelo zam zokulwa ndiqabele encotsheni yeentaba, encamini yeLebhanon, ndayigawula imisedare yayo emide kakhulu, nemisipres yayo; Ndiya kungena ezindlwini zelizwe lakhe, nakwihlathi leKarmele yakhe.', 'XHO22'),
(16, 19, 24, 'Ndimbe, ndasela amanzi abefudula engekho, ndayitshisa ngentende yeenyawo zam yonke imijelo yamanxuwa.', 'XHO22'),
(16, 19, 25, 'Anivanga na ukuba ndandikwenza oku kwakudala nendlela endandikwenza ngayo? Ndiyakwenza oku, ukuba uyenze amanxuwa imizi enqatyisiweyo.', 'XHO22'),
(16, 19, 26, 'Ngeso sizathu, abemi bayo babembalwa, boyiswa baza bahlaziswa; baba yingca yasendle, nengca, nengca phezu kwendlu, nanjengengqolowa ephetshethwayo, ingekantshuli.', 'XHO22'),
(16, 19, 27, 'Kodwa ndiyakwazi ukuhlala kwakho, nokuphuma kwakho, nokungena kwakho, nokundiqumba kwakho.', 'XHO22'),
(16, 19, 28, 'Ngenxa enokuba unyuke umsindo wakho kum, nesandi sakho esi ezindlebeni zam, ndiya kufaka ugwali lwam empumlweni yakho, nomkhala wam emlonyeni wakho, ndikubuyisele ngendlela oweza ngayo.', 'XHO22'),
(16, 19, 29, 'Nguwo lo umqondiso kuwe: lo nyaka kodliwa ummilela wodwa, ngomnyaka wesibini nidle ummilela; ngomnyaka wesithathu nohlwayela, nivune, nityale izidiliya, nidle iziqhamo zazo.', 'XHO22'),
(16, 19, 30, 'Kwaye abasindileyo bendlu yakwaYuda baya kuphinda bendelisele iingcambu phantsi baze bathwale isiqhamo phezulu.', 'XHO22'),
(16, 19, 31, 'Ngokuba amasalela aya kuza evela eYerusalem, nabasabayo entabeni yeZiyon; ubukhwele bukaYehova wemikhosi buya kukwenza oko.', 'XHO22'),
(16, 19, 32, 'Ngako oko, utsho uYehova ngokumkani waseAsiriya, ukuthi, Akayi kungena kulo mzi, akayi kutola notolo kuwo, akayi kuwuzela enengweletshetshe, akayi kuzakhela ludonga.', 'XHO22'),
(16, 19, 33, 'Indlela eze ngayo, uya kubuya ngayo, akasayi kungena kulo mzi; utsho uYehova.', 'XHO22'),
(16, 19, 34, 'Ndiya kuwukhusela lo mzi, ndiwusindise ngenxa yam, nangenxa kaDavide umkhonzi wam.', 'XHO22'),
(16, 19, 35, 'Kwaphuma isithunywa sikaYehova ngobo busuku, saxabela emkhosini wama-Asiriya ikhulu elinamanci asibhozo anesihlanu lamawaka; bavuka kusasa, bezizidumbu ezifileyo bonke.', 'XHO22'),
(16, 19, 36, 'Wasuka uSaneribhe ukumkani waseAsiriya, wabuya, wahlala eNineve.', 'XHO22'),
(16, 19, 37, 'Xa wayequbuda endlwini yothixo wakhe uNiserogi, oonyana bakhe uAdrameleki noTsaretsere bambulala ngekrele, baza basabela kwilizwe laseArarati unyana wakhe uEsar-hadon waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 20, 1, 'Ngelo xesha wahliwa sisifo uHezekiya, wafa. Waya umprofeti uIsaya, unyana ka-Amos, kuye, wathi kuye, Utsho uYehova ukuthi, Yilawule indlu yakho; ngokuba uya kufa, aniyi kuphila.', 'XHO22'),
(16, 20, 2, 'Wabubhekisa eludongeni ubuso bakhe, wathandaza kuYehova, esithi:', 'XHO22'),
(16, 20, 3, 'Khawukhumbule, ndiyakukhunga, Yehova, ukuhamba kwam phambi kwakho, enyanisweni, nangentliziyo ephelelisileyo, ndakwenza okulungileyo emehlweni akho. uHezekiya walila ngokukrakra.', 'XHO22'),
(16, 20, 4, 'Kwathi, engekaphumi uIsaya entendelezweni ephakathi, kwafika ilizwi likaYehova kuye, lisithi:', 'XHO22'),
(16, 20, 5, 'Buya uye kuthi kuHezekiya ukumkani wabantu bam, Utsho uYehova, uThixo kaDavide uyihlo, ukuthi, Ndiwuvile umthandazo wakho, ndizibonile iinyembezi zakho; yabona, ndingumphilisi; ngomhla wesithathu uya kunyuka aye endlwini kaYehova.', 'XHO22'),
(16, 20, 6, 'Ndiya kuyandisa imihla yakho ngeminyaka elishumi elinesihlanu; Ndiya kukuhlangula wena nalo mzi esandleni sokumkani waseAsiriya; ndiwukhusele lo mzi ngenxa yam, nangenxa kaDavide umkhonzi wam.', 'XHO22'),
(16, 20, 7, 'Wathi uIsaya, Thabathani isipha samakhiwane; Bamthabatha, bambeka emkhosini, waphila.', 'XHO22'),
(16, 20, 8, 'Wathi uHezekiya kuIsaya, Uyintoni na umqondiso wokuba uYehova uya kundiphilisa, ndinyuke ndiye endlwini kaYehova ngomhla wesithathu?', 'XHO22'),
(16, 20, 9, 'Wathi uIsaya, Kukho umqondiso ophuma kuYehova, wokuba uya kulenza uYehova ilizwi alithethileyo: Isithunzi sihambe na izinyuko ezilishumi, sibuye na izinyuko ezilishumi?', 'XHO22'),
(16, 20, 10, 'Wathi uHezekiya, Kulula kwisithunzi ukoluka izinyuko ezilishumi; hayi, kodwa isidima sibuyele umva izidanga ezilishumi.', 'XHO22'),
(16, 20, 11, 'Wabiza uIsaya umprofeti kuYehova, wasibuyisela isithunzi izinyuko zalishumi, ebesiziwo encwadini ka-Ahazi.', 'XHO22'),
(16, 20, 12, 'Ngelo xesha uBherodage-Bhiladan unyana kaBhaladan ukumkani waseBhabhiloni wathumela iileta nesipho kuHezekiya, ngenxa yokuba wayevile ukuba uHezekiya wayegula.', 'XHO22'),
(16, 20, 13, 'Wabeva uHezekiya, wababonisa yonke indlu yakhe yexabiso, isilivere, negolide, nobulawu, neoli entle, nendlu yonke yesikrweqe sakhe, nento yonke eyafumanekayo ebuncwaneni bendlu yakhe akwabakho nto endlwini yakhe nakubukumkani bakhe bonke, angabavumelanga uHezekiya ukuba bayibone.', 'XHO22'),
(16, 20, 14, 'Waya umprofeti uIsaya kukumkani uHezekiya, wathi kuye, Atheni na la madoda? bevela phi na ukuza kuwe? Wathi uHezekiya, Baphuma ezweni elikude, eBhabheli.', 'XHO22'),
(16, 20, 15, 'Wathi, Abone ntoni na endlwini yakho? Wathi uHezekiya, Abone into yonke esendlwini yam; akubangakho nto ndingakubonisanga yona ebuncwaneni bam.', 'XHO22'),
(16, 20, 16, 'Wathi uIsaya kuHezekiya, Live ilizwi likaYehova.', 'XHO22'),
(16, 20, 17, 'Uyabona, iyeza imihla, eya kuthinjwa ngayo yonke into esendlwini yakho, nento abayiqwebayo ooyihlo, kwada kwayile mini, basiwe eBhabheli; akuyi kusala nto, utsho uYehova.', 'XHO22'),
(16, 20, 18, 'Koonyana bakho abaya kuphuma kuwe, oya kubazala, bathabathe; yaye ziya kugcinwa kwibhotwe likakumkani waseBhabhiloni.', 'XHO22'),
(16, 20, 19, 'Wathi uHezekiya kuIsaya, Lilungile ilizwi likaYehova olithethileyo. Wathi, Akulungile na ukuba kukho uxolo nenyaniso ngexesha lam?', 'XHO22'),
(16, 20, 20, 'Ezinye iindaba ezingakumbi zikaHezekiya nawo onke amandla akhe nendlela awahlamba ngayo equleni nokuzisa amanzi esixekweni, azibhalwanga na encwadini yemicimbi yookumkani bakwaYuda?', 'XHO22'),
(16, 20, 21, 'Walala uHezekiya kooyise; uManase, unyana wakhe, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(16, 21, 1, 'uManase ubeminyaka ilishumi elinamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mahlanu anamihlanu engukumkani eYerusalem. Igama lonina belinguHefezibha.', 'XHO22'),
(16, 21, 2, 'Wenza okubi emehlweni kaYehova, ngokwamasikizi eentlanga, awazigqogqayo uYehova phambi koonyana bakaSirayeli.', 'XHO22'),
(16, 21, 3, 'Kuba waphinda wazakha iindawo eziphakamileyo ezo uHezekiya uyise wazitshabalalisayo; Wamakhela uBhahali izibingelelo, wenza neAshtaroti, njengoko wenzayo uAhabhi ukumkani wakwaSirayeli; yaqubuda yonke imikhosi yezulu, iyikhonze.', 'XHO22'),
(16, 21, 4, 'Wazakha izibingelelo endlwini kaYehova, awathi uYehova, Ndiya kulimisa eYerusalem igama lam.', 'XHO22'),
(16, 21, 5, 'Wazakhela izibingelelo zonke izidalwa zasezulwini kwiintendelezo zombini zendlu kaYehova.', 'XHO22'),
(16, 21, 6, 'Wabeka unyana wakhe umlilo, wawagcina amaxesha, wasebenzisana nemimoya, noosiyazi; wenza ububi obukhulu emehlweni kaYehova, ukuba amqumbise.', 'XHO22'),
(16, 21, 7, 'Wawumisa umfanekiso oqingqiweyo wetyholo abewenzile, endlwini awayethe uYehova kuDavide nakuSolomon unyana wakhe, Kuyo le ndlu naseYerusalem, endiyinyulileyo ezizweni zonke zakwaSirayeli, iya kukhetha ulibeke ngonaphakade igama lam.', 'XHO22'),
(16, 21, 8, 'Andisayi kuphinda ndilushukumise unyawo lwamaSirayeli emhlabeni endawunika ooyise; ngalo lonke ixesha besenza ngenyameko njengako konke endibawisele umthetho ngako, nangokomyalelo wonke, awabawiselayo uMoses umkhonzi wam.', 'XHO22'),
(16, 21, 9, 'Kodwa abaphulaphulanga kubo, yaye uManase wabahenda ukuba benze okubi ngakumbi kuneentlanga awazitshabalalisayo uYehova phambi koonyana bakaSirayeli.', 'XHO22'),
(16, 21, 10, 'uYehova wathetha ngabakhonzi bakhe, abaprofeti, wathi:', 'XHO22'),
(16, 21, 11, 'Ngenxa yokuba uManase, ukumkani wakwaYuda, wayezenzile ezi zinto zicekisekayo waza wenza okubi ngakumbi kunako konke awakwenzayo ama-Amori ngaphambi kwakhe, waza wamonisa uYuda ngezithixo ezinezothe awayezinqula.', 'XHO22'),
(16, 21, 12, 'Ngako oko, utsho uYehova, uThixo kaSirayeli, ukuthi, Yabona, ndiyihlisela iYerusalem noYuda ububi, abaya kuthi bonke ababuvayo bavingce iindlebe zabo;', 'XHO22'),
(16, 21, 13, 'Ndoyolula ulutya lokulinganisa lwakwaSamari phezu kweYerusalem, nolutya lwendlu ka-Ahabhi; yaye ndiya kuyisula iYerusalem njengokuba umntu esula isitya, asisule aze asiqhekeze.', 'XHO22'),
(16, 21, 14, 'Ndowashiya amasalela elifa lam, ndiwanikele esandleni seentshaba zawo; yaye baya kuba lixhoba nexhoba kuzo zonke iintshaba zabo;', 'XHO22'),
(16, 21, 15, 'Ngokuba benze ububi emehlweni am, bandingcikiva, kususela kwimini abaphuma ngayo ooyise eYiputa unanamhla;', 'XHO22'),
(16, 21, 16, 'Ngaphezu koko, uManase waliphalaza igazi elininzi elimsulwa kangangokuba wayizalisa iYerusalem, kwathabathela kwelinye icala kwesa kwelinye; ngaphandle nje kwesono sakhe awasizisa kuYuda ngokwenza okubi emehlweni kaYehova.', 'XHO22'),
(16, 21, 17, 'Ezinye izinto zikaManase, nako konke awakwenzayo, nesono sakhe awasenzayo, azibhalwanga na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 21, 18, 'Ekugqibeleni uManase walala kookhokho bakhe waza wangcwatywa emyezweni wendlu yakhe, emyezweni kaUza; unyana wakhe uAmon waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(16, 21, 19, 'uAmon ubeminyaka imashumi mabini anamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emibini engukumkani eYerusalem. Igama lonina belinguMesulemet, intombi kaHarosi, waseYotebha.', 'XHO22'),
(16, 21, 20, 'Wenza okubi emehlweni kaYehova, njengoko wenzayo uManase uyise.', 'XHO22'),
(16, 21, 21, 'Waqhubeka ehamba ngendlela yonke awahamba ngayo uyise, wayikhonza imifanekiso eqingqiweyo awayeyikhonza uyise, waqubuda kuyo.', 'XHO22'),
(16, 21, 22, 'Wamnikela umva uYehova uThixo wooyise, akahamba ngendlela kaYehova.', 'XHO22'),
(16, 21, 23, 'Bamceba abakhonzi baka-Amon, bambulala ukumkani lowo endlwini yakhe.', 'XHO22'),
(16, 21, 24, 'Kwaye abantu behlabathi bababulala bonke abo babecebe nxamnye nokumkani uAmon; abantu belizwe bamenza ukumkani uYosiya, unyana wakhe, esikhundleni sakhe.', 'XHO22'),
(16, 21, 25, 'Ezinye izinto zika-Amon awazenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 21, 26, 'Wangcwatyelwa engcwabeni lakhe emhlabeni waseUsha, waba ngukumkani uYosiya, unyana wakhe, esikhundleni sakhe.', 'XHO22'),
(16, 22, 1, 'uYosiya ubeminyaka isibhozo ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mathathu anamnye engukumkani eYerusalem. Igama lonina belinguYedida, intombi ka-Adaya umSkitiya.', 'XHO22'),
(16, 22, 2, 'Wenza okuthe tye emehlweni kaYehova, wahamba ngeendlela zonke zikaDavide uyise, akatyekela ngasekunene nangasekhohlo.', 'XHO22'),
(16, 22, 3, 'Ngomnyaka weshumi elinamnye nowesibini wobukumkani bukaYosiya, ukumkani wathumela uShafan unyana ka-Atsaliya, unyana kaMeshulam, umbhali, endlwini kaYehova, ukuba athi,', 'XHO22'),
(16, 22, 4, 'Nyuka uye kuHilekiya umbingeleli omkhulu, ukuze aqokelele isilivere eziswa endlwini kaYehova, eqokelelwe ngabagcini-masango basesidlangalaleni.', 'XHO22'),
(16, 22, 5, 'Bayinikela esandleni sabenzi bomsebenzi wendlu kaYehova; uyinike abasebenzi bendlu kaYehova, ukuba batywine iintanda zendlu kaYehova;', 'XHO22'),
(16, 22, 6, 'nabachweli, nabakhi, nabaqingqi bamatye, bethenga imithi, namatye aqingqiweyo, ukuba kuhlaziywe indlu.', 'XHO22'),
(16, 22, 7, 'Kodwa imali eyayinikelwe ezandleni zabo ayizange ibalwe, kuba babesebenza ngokunyaniseka.', 'XHO22'),
(16, 22, 8, 'Wathi uHilekiya, umbingeleli omkhulu, kuShafan umbhali, Incwadi yomyalelo ndiyifumene endlwini kaYehova. uHilekiya wayinika uShafan incwadi leyo, wayifunda.', 'XHO22'),
(16, 22, 9, 'Weza ke uShafan umbhali kukumkani, wamxelela ukumkani, wathi, Abakhonzi bakho bayiqokelele imali eyafunyanwa endlwini, bayinikela esandleni sabasebenzi bomsebenzi wabo yendlu kaYehova.', 'XHO22'),
(16, 22, 10, 'Waxela ke uShafan umbhali kukumkani, wathi, Umbingeleli uHilekiya undinike incwadi. uShafane wafunda phambi kokumkani.', 'XHO22'),
(16, 22, 11, 'Uthe akuweva ukumkani amazwi encwadi yomyalelo, wazikrazula iingubo zakhe.', 'XHO22'),
(16, 22, 12, 'Ukumkani wamwisela umthetho umbingeleli uHilekiya, noAhikam, unyana kaShafan, noAkebhore unyana kaMikaya, noShafan umbhali, noAsaya umkhonzi wokumkani, wathi:', 'XHO22'),
(16, 22, 13, 'Yiyani nibuze kuYehova, mna nakubantu, nangenxa yamaYuda onke, malunga namazwi ale ncwadi ifunyenweyo; ngenxa yokuba umsindo kaYehova ovuthayo nxamnye nathi mkhulu, ngenxa yokuba ookhokho bethu abazange baphulaphule ngokwamazwi ale ncwadi, ukuba enze konke okubhaliweyo ngathi.', 'XHO22'),
(16, 22, 14, 'Waya ke umbingeleli uHilekiya, noAhikam, noAkebhore, noShafan, noAsaya, kuHulida umprofeti, umkaShalum, unyana kaTikiva, unyana kaHarhazi, umgcini-ngubo; ebehlala eYerusalem ekholejini; baye bethetha naye.', 'XHO22'),
(16, 22, 15, 'Wathi kubo, Utsho uYehova, uThixo kaSirayeli, ukuthi, Nothi kwindoda eniyithumele kum, nithi,', 'XHO22'),
(16, 22, 16, 'Utsho uYehova ukuthi, Yabona, ndiyizisela ububi le ndawo, naphezu kwabemi bayo, onke amazwi encwadi leyo ayifundileyo ukumkani wakwaYuda.', 'XHO22'),
(16, 22, 17, 'Ngenxa yokuba bandinikele umva baza baqhumisa abanye oothixo ngenxa yam, ukuze bandihleke ngayo yonke imisebenzi yezandla zabo; ke ngoko, umsindo wam uya kuvutha kule ndawo, kwaye awuyi kucinywa.', 'XHO22'),
(16, 22, 18, 'Ke kukumkani wakwaYuda, lowo nimthume ukuba nibuzise kuYehova, nithi kuye, Utsho uYehova, uThixo kaSirayeli, ukuthi, Ngokusingisele kumazwi owavileyo;', 'XHO22'),
(16, 22, 19, 'Ngenxa yokuba intliziyo yakho ibithambile waza wazithoba phambi koYehova, ekuveni kwakho endikuthethileyo nxamnye nale ndawo nanxamnye nabemi bayo, ukuba baya kuba yintlango eqalekisiweyo, neempahla zakho zikrazukile yaye bakhale phambi kwabo amehlo akho. Ndingu; Nam ndikuvile, utsho uYehova.', 'XHO22'),
(16, 22, 20, 'Ngako oko, uyabona, ndiya kukusongela ngooyihlo, ubethwe ngoxolo engcwabeni lakho; angayi kububona amehlo enu bonke ububi endibuzisa kule ndawo. Babuya ke bamxelela ukumkani.', 'XHO22'),
(16, 23, 1, 'Wathumela ukumkani, ahlanganiselwa kuye onke amadoda amakhulu akwaYuda nawaseYerusalem.', 'XHO22'),
(16, 23, 2, 'Wenyuka ukumkani waya endlwini kaYehova, namadoda onke akwaYuda, nabemi bonke baseYerusalem, nababingeleli, nabaprofeti, nabantu bonke, kwathabathela komncinane kwesa komkhulu, wafunda kubo amazwi encwadi yomnqophiso, eyafunyanwa endlwini kaYehova.', 'XHO22'),
(16, 23, 3, 'uYehova wema ecaleni kwelitye lesikhumbuzo waza wenza umnqophiso phambi koYehova wokulandela uYehova nokugcina imiyalelo yakhe namacebo akhe nemithetho yakhe emisiweyo ngentliziyo yonke nangomoya wonke ukuze azalisekise amazwi alo mnqophiso; zibhaliwe kule ncwadi. Kwaye lonke uhlanga lwema emnqophisweni.', 'XHO22'),
(16, 23, 4, 'uYehova wamwisela umthetho uHilekiya, umbingeleli omkhulu, nababingeleli bomthetho wesibini, nabagcini-masango, ukuba bazikhuphe etempileni kaYehova zonke iimpahla zikaBhahali, nezasehlathini, nezinkwenkwezi zonke zezulu, bazitshise ngomlilo umlilo ngaphandle kweYerusalem, ezweni laseKidron, baluzisa uthuthu lwazo eBheteli.', 'XHO22'),
(16, 23, 5, 'Wabasusa ababingeleli bezithixo zookumkani bakwaYuda ababebamisele ukuba baqhumisele kwiindawo eziphakamileyo kwizixeko zakwaYuda nakwiindawo ezijikeleze iYerusalem; baqhumisela uBhahali, ilanga, nenyanga, neenkwenkwezi, nomkhosi wonke wezulu.', 'XHO22'),
(16, 23, 6, 'Wathabatha ityholo lendlu kaYehova elingaphandle kweYerusalem, lalisa emlanjaneni oyiKidron, walitshisela entilini yentlambo yaseKidron, lalisila laluthuthu, lawuphosa umgubo walo emangcwabeni abantwana bakawonke-wonke.', 'XHO22'),
(16, 23, 7, 'Wazidiliza izindlu zaseSodom ezibe zisendlwini kaYehova, apho abafazi babelukela ihlathi amalaphu ajingayo.', 'XHO22'),
(16, 23, 8, 'Wabakhupha bonke ababingeleli emizini yakwaYuda, wazenza inqambi izibingelelo eziphakamileyo, ababeqhumisela kuzo ababingeleli, ethabathela eGebha ase eBher-shebha, wazidiliza nezibingelelo eziphambi kwesango likaYoshuwa irhuluneli yomzi, ebisekunene kwindoda ekungeneni kwesango lomzi.', 'XHO22'),
(16, 23, 9, 'Kodwa ababingeleli beendawo eziphakamileyo babengenyuki baye esibingelelweni sikaYehova eYerusalem, kodwa babesidla izonka ezingenagwele phakathi kwabazalwana babo.', 'XHO22'),
(16, 23, 10, 'Wayenza inqambi iTofete, intlambo yoonyana bakaHinom, ukuze kungabikho nabani na ovumela unyana wakhe okanye intombi yakhe ukuba idlule emlilweni iye kufika eMologi.', 'XHO22'),
(16, 23, 11, 'Wawasusa amahashe ookumkani bakwaYuda, ababewanikele elangeni, emnyango wendlu kaYehova, egumbini likaNataniyeli, ithenwa, nentlambo, neenqwelo zokulwa zelanga zatshiswa ngomlilo.', 'XHO22'),
(16, 23, 12, 'Izibingelelo ezibe ziphezu kwegumbi eliphezulu lika-Ahazi, ababelenzile ookumkani bakwaYuda, nezibingelelo awazenzayo uManase ezintendelezweni zombini zendlu kaYehova, ukumkani wazidiliza, waza waziqhekeza baluphosa uthuli lwabo emlanjaneni oyiKidron.', 'XHO22'),
(16, 23, 13, 'Neenqaba ezinde, eziphambi kweYerusalem, ezilunge ngasekunene kwentaba yentshabalalo, leyo uSolomon ukumkani wakwaSirayeli wazakhela uAshtoreti, isikizi lamaTsidon, noKemoshe, isikizi lamaMowabhi, uMilkom. Ewe, wena umenze inqambi ukumkani, isikizi loonyana baka-Amon.', 'XHO22'),
(16, 23, 14, 'Wayiqhekeza imifanekiso eqingqiweyo, wawagawula amatyholo, wazizalisa iindawo zawo ngamathambo abantu.', 'XHO22'),
(16, 23, 15, 'Ngaphezu koko, wenza isibingelelo eBheteli nendawo ephakamileyo awayonisa ngayo uYerobhoham unyana kaNebhati koonyana bakaSirayeli, wasidiliza isibingelelo nendawo ephakamileyo waza wayitshisa indawo ephakamileyo waza wayiqhekeza umgubo, walitshisa ityholo.', 'XHO22'),
(16, 23, 16, 'Wajika uYosiya, wawakhangela amangcwaba abesentaba, wathumela, wawathabatha amathambo emangcwabeni lawo, wawatshisela phezu kwesibingelelo, wawenza inqambi ngokwelizwi likaYehova, abelithethile umfo wakwaThixo wayeshumayela, lowo wayethetha la mazwi.', 'XHO22'),
(16, 23, 17, 'Wathi, Ligama lini na eli ndilibonayo? Athi kuye amadoda aloo mzi, Eli lingcwaba lomfo wakwaThixo, owayevela kwaYuda, washumayela iindaba ezilungileyo zezinto owazenzayo esibingelelweni saseBheteli.', 'XHO22'),
(16, 23, 18, 'Wathi ke, Mayime; Makungabikho namnye owashukumisa amathambo akhe. Bawayeka emi kanye amathambo akhe, kunye namathambo omprofeti owayevela kwaSamari.', 'XHO22'),
(16, 23, 19, 'Yaye zonke izindlu zeendawo eziphakamileyo zezixeko zaseSamariya, ezo ookumkani bakwaSirayeli ababezakhele ukuze bamqumbise uYehova, uYosiya wazishenxisa waza wenza kuzo ngokwezenzo zakhe zonke awayezenzile eBheteli.', 'XHO22'),
(16, 23, 20, 'Wababulala bonke ababingeleli beziganga ababekhona ezibingelelweni, wawatshisa amathambo abantu phezu kwazo, wabuyela eYerusalem.', 'XHO22'),
(16, 23, 21, 'uYehova wabawisela umthetho abantu bonke, wathi, Yenzani ipasika kuYehova, uThixo wenu, ngoko kubhaliweyo encwadini yalo mnqophiso.', 'XHO22'),
(16, 23, 22, 'Kananjalo bekungenziwanga pasika ngokwale pasika ngemihla yabagwebi ababelawula kwaSirayeli, nangemihla yookumkani bakwaSirayeli, nookumkani bakwaYuda;', 'XHO22'),
(16, 23, 23, 'Kodwa ngomnyaka weshumi elinamnye nowesibini wokumkani uYosiya, kwenziwa le pasika kuYehova eYerusalem.', 'XHO22'),
(16, 23, 24, 'Ngaphezu koko, uYosiya wabashenxisa abasebenzi abanoomoya abalahlekayo nabakhafuli nezithixo nezithixo nawo onke amasikizi awayevavanyiwe kwilizwe lakwaYuda nelaseYerusalem, ukuze azalisekise amazwi omthetho awayebhalwe encwadini Umbingeleli uHilekiya wamfumana endlwini kaYehova.', 'XHO22'),
(16, 23, 25, 'Ngaphambi kwakhe akubangakho kumkani unjengaye, ubuyeleyo kuYehova ngentliziyo yakhe yonke, nangomphefumlo wakhe wonke, nangamandla akhe onke, ngokomyalelo wonke kaMoses; kwaye emva kwakhe akubangakho namnye unjengaye.', 'XHO22'),
(16, 23, 26, 'Ke uYehova akabuyanga ekuvutheni komsindo wakhe, owavutha ngawo umsindo wakhe kuYuda, ngenxa yomsindo wonke abemngcikive ngawo uManase.', 'XHO22'),
(16, 23, 27, 'Wathi uYehova, Ndiya kumsusa uYuda ebusweni bam, njengoko ndawasusayo amaSirayeli, ndiwulahle lo mzi waseYerusalem ndawunyulayo, nendlu le ndathi, Liya kuba kuyo igama lam.', 'XHO22'),
(16, 23, 28, 'Ezinye izinto zikaYosiya, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 23, 29, 'Ngemihla yakhe uFaro-nego, ukumkani waseYiputa, wamxabela ukumkani waseAsiriya ngasemlanjeni ongumEfrati, ukumkani uYosiya wamxabela; yaye wambulalela eMegido akumbona.', 'XHO22'),
(16, 23, 30, 'Abakhonzi bakhe bamthabatha ngenqwelo efileyo, besuka eMegido, bamsa eYerusalem, bamngcwabela engcwabeni lakhe. Abantu belizwe bathabatha uYowakas, unyana kaYosiya, bamthambisa, bamenza ukumkani esikhundleni sikayise.', 'XHO22'),
(16, 23, 31, 'uYowakazi ubeminyaka imashumi mabini anamithathu ezelwe, ukuba ngukumkani kwakhe; waba neenyanga ezintathu engukumkani eYerusalem. Igama lonina belinguHamutali, intombi kaYeremiya, waseLibhena.', 'XHO22'),
(16, 23, 32, 'Wenza okubi emehlweni kaYehova, njengoko benza ngako ooyise.', 'XHO22'),
(16, 23, 33, 'uFaro-nego wamfaka eRibhela, ezweni laseHamati, ukuba angabi nakulawula eYerusalem; walirhumela ilizwe ikhulu leetalente zesilivere, netalente yegolide yanye.', 'XHO22'),
(16, 23, 34, 'uFaro-nego wamenza ukumkani uEliyakim, unyana kaYosiya, esikhundleni sikaYosiya uyise, waliguqula igama lakhe languYoyakim, wamthabatha uYowaka; waya eYiputa, wafela khona.', 'XHO22'),
(16, 23, 35, 'uYehoyakim wamnika uFaro isilivere negolide; wayelirhafisa nje ilizwe, ukuze ahlawule imali ngokomyalelo kaFaro; Wabiza isilivere negolide kubantu belizwe, elowo nerhafu yakhe, ukuze ayinike uFaro-nego.', 'XHO22'),
(16, 23, 36, 'uYehoyakim ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; Waba neminyaka elishumi elinamnye engukumkani eYerusalem. Igama lonina belinguSebuda, intombi kaPedaya waseRuma.', 'XHO22'),
(16, 23, 37, 'Wenza okubi emehlweni kaYehova, njengoko benza ngako ooyise.', 'XHO22'),
(16, 24, 1, 'Ngexesha lakhe uNebhukadenetsare ukumkani waseBhabheli wahlasela uYowakim, waba ngumkhonzi kuye iminyaka emithathu; emveni koko wajika, wamjikela.', 'XHO22'),
(16, 24, 2, 'Yaye uYehova wathumela amatutu amaKhaledi namatutu ama-Aram namatutu amaMowabhi namatutu oonyana baka-Amoni ukuba amhlasele, waza wawathumela kuYuda ukuba amtshabalalise, ngokomthetho woonyana baka-Amoni. Ilizwi likaYehova awalithethayo kubakhonzi bakhe abaprofeti bathethile.', 'XHO22'),
(16, 24, 3, 'Enyanisweni, ngokomyalelo kaYehova kwafika uYuda ukuba amshenxise phambi kwakhe ngenxa yezono zikaManase ngako konke awakwenzayo;', 'XHO22'),
(16, 24, 4, 'nangenxa yegazi elingenatyala, ngenxa yokuba wayizalisa iYerusalem ngegazi elingenatyala; into angazange ayixolele uYehova.', 'XHO22'),
(16, 24, 5, 'Ezinye izinto zikaYehoyakim, nako konke awakwenzayo, ndithi, zibhaliwe na encwadini yemicimbi yemihla yookumkani bakwaYuda?', 'XHO22'),
(16, 24, 6, 'uYehoyakim walala kookhokho bakhe, yaye uYoyakin unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(16, 24, 7, 'Ukumkani waseYiputa akazange abuye ezweni lakhe, kuba ukumkani waseBhabhiloni wayezithimbile zonke izinto zokumkani waseYiputa ukususela kumlambo waseYiputa ukusa kumlambo ongumEfrati.', 'XHO22'),
(16, 24, 8, 'uYehoyakin ubeminyaka ilishumi elinesibhozo ezelwe, ukuba ngukumkani kwakhe; waba neenyanga ezintathu engukumkani eYerusalem. Igama lonina belinguNehusta, intombi kaElinatan, waseYerusalem.', 'XHO22'),
(16, 24, 9, 'Wenza okubi emehlweni kaYehova, njengoko wenzayo uyise.', 'XHO22'),
(16, 24, 10, 'Ngelo xesha abakhonzi bakaNebhukadenetsare ukumkani waseBhabhiloni bayihlasela iYerusalem, yaye isixeko sangqingwa.', 'XHO22'),
(16, 24, 11, 'Wawufikela uNebhukadenetsare ukumkani waseBhabheli, bewungqinga abakhonzi bakhe.', 'XHO22'),
(16, 24, 12, 'Waya uYehoyakin, ukumkani wakwaYuda, waya kukumkani waseBhabheli, yena nonina, nabakhonzi bakhe, nabathetheli bakhe, nabakhonzi bakhe; ukumkani waseBhabheli wamthabatha ke ngomnyaka wesithoba, nowesibini wobukumkani bakhe.', 'XHO22'),
(16, 24, 13, 'Wabuthabatha khona bonke ubuncwane bendlu kaYehova, nobuncwane bendlu yokumkani, neempahla zonke zegolide, awazenzayo uSolomon ukumkani wakwaSirayeli, awazenzayo endlwini kaYehova, wazinqunqa phakathi amaqhekeza utsho uYehova.', 'XHO22'),
(16, 24, 14, 'Wayithimba yonke iYerusalem, nabathetheli bonke, namagorha onke, belishumi lamawaka abathinjwa, bonke abarhwebi, neengcibi zonke zesinyithi; akwasala bani, yaba ngamahlwempu elizwe odwa owasalayo.', 'XHO22'),
(16, 24, 15, 'Wamthabatha uYehoyakin wamsa eBhabheli, nonina wokumkani, nabafazi bokumkani, nabakhonzi bakhe, namagorha elizwe, wabafudusela eYerusalem eBhabheli.', 'XHO22'),
(16, 24, 16, 'Onke ke onke amawaka asixhenxe amadoda okulwa, newaka leengcibi nabakhandi besinyithi, bonke abomeleleyo nabakwaziyo ukulwa, bamthabatha ukumkani waseBhabheli, bamsa eBhabheli.', 'XHO22'),
(16, 24, 17, 'Ukumkani waseBhabheli wamenza uMataniya, umkhuluwa kayise, ukumkani esikhundleni sakhe, waliguqula igama lakhe laba nguZedekiya.', 'XHO22'),
(16, 24, 18, 'uZedekiya ubeminyaka imashumi mabini anamnye ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamnye engukumkani eYerusalem. Igama lonina belinguHamutali, intombi kaYeremiya, waseLibhena.', 'XHO22'),
(16, 24, 19, 'Wenza okubi emehlweni kaYehova, njengoYehoyakim.', 'XHO22'),
(16, 24, 20, 'Kwenzeka ngenxa yomsindo kaYehova eYerusalem nakwaYuda, wada wabagxotha phambi kwakhe, kangangokuba uZedekiya wamvukela ukumkani waseBhabhiloni.', 'XHO22'),
(16, 25, 1, 'Kwathi ngomnyaka wesithoba wodwa wobukumkani bakhe, ngenyanga yeshumi, ngolweshumi enyangeni leyo, wayifikela uNebhukadenetsare ukumkani waseBhabheli, nemikhosi yakhe yonke, wayingqinga iYerusalem; yaye balakha ngeenqaba macala onke.', 'XHO22'),
(16, 25, 2, 'Wangqingwa ke umzi, kwada kwangumnyaka weshumi elinamnye wokumkani uZedekiya.', 'XHO22'),
(16, 25, 3, 'Ngomhla wesithoba wenyanga yesine kwabakho indlala phakathi komzi, ababa nasonka abantu belizwe.', 'XHO22'),
(16, 25, 4, 'Uqhekekile umzi, asaba onke amadoda okulwa ebusuku, ngesango eliphakathi kweendonga zombini, elingasemyezweni wokumkani; AmaKaledi awurhawula umzi, wesuka ukumkani waya entlango.', 'XHO22'),
(16, 25, 5, 'Imikhosi yamaKaledi yamsukela ukumkani, yambamba ezinkqantosini zaseYeriko, yamphalala, yemka kuye yonke impi yakhe.', 'XHO22'),
(16, 25, 6, 'Ambamba ukumkani, amzisa kukumkani waseBhabheli, eRibhela; bamgweba.', 'XHO22'),
(16, 25, 7, 'Ababulala oonyana bakaZedekiya phambi kwamehlo akhe baza bamkrukra amehlo uZedekiya baza bamkhonkxa ngemixokelelwane yobhedu baza bamzisa eBhabhiloni.', 'XHO22'),
(16, 25, 8, 'Ngenyanga yesihlanu, ngomhla wesixhenxe enyangeni leyo, oko kukuthi, ngonyaka weshumi elinesithoba kaKumkani uNebhukadenetsare, ukumkani waseBhabhiloni, uNebhuzaradan umphathi wamafanankosi, umkhonzi kakumkani waseBhabhiloni, weza eYerusalem;', 'XHO22'),
(16, 25, 9, 'Wayitshisa ngomlilo indlu kaYehova, nendlu yokumkani, nezindlu zonke zaseYerusalem, nendlu yabo bonke abakhulu;', 'XHO22'),
(16, 25, 10, 'Yaye ibutho lonke lomkhosi wamaKhaledi elalinomphathi lazaphula iindonga zaseYerusalem ngeenxa zonke.', 'XHO22'),
(16, 25, 11, 'Inxenye yabantu ababesele phakathi komzi, neembacu ezigwilike kukumkani waseBhabheli, namasalela amaninzi, wabathimba uNebhuzaradan, umthetheli wabasiki.', 'XHO22'),
(16, 25, 12, 'Kodwa umthetheli wabasiki wabashiya abathile kwizisweli zelizwe, ukuba babe ngabasebenzi bezidiliya nabalimi.', 'XHO22'),
(16, 25, 13, 'Neentsika zetempile ezazisendlwini kaYehova neebhafu nedike letempile elisendlwini kaYehova, amaKhaledi azaphula aza ayithabatha itempile kuzo aza aya eBhabhiloni.', 'XHO22'),
(16, 25, 14, 'Athabatha neembiza, nemihlakulwana, neenkamba, neendebe, neempahla zonke zobhedu ababelungiselela ngazo.', 'XHO22'),
(16, 25, 15, 'Umthetheli wabasiki wathabatha iimbiza zomlilo, nezitya zokutshiza, nayo yonke loo golide, igolide, nesilivere, nesilivere.', 'XHO22'),
(16, 25, 16, 'Iintsika zambini, nolwandle olunye, neenqwelo zokuhlambela, awazenzela indlu kaYehova uSolomon; Ubhedu lwezo zinto zonke lwalungenabunzima.', 'XHO22'),
(16, 25, 17, 'Ubude bentsika enye bebuziikubhite ezilishumi elinesibhozo, intloko yayo ibiyeyobhedu, intloko yayo ibiyeyobhedu, ukuphakama kwentloko bekuziikubhite ezintathu; Ingqolowa, iingubo, iintloko, zonke ezo nto yayizezobhedu;', 'XHO22'),
(16, 25, 18, 'Umthetheli wabasiki wamthabatha uSeraya, umbingeleli omkhulu, noZefaniya, umbingeleli wesibini, nabagcini-masango bobathathu.', 'XHO22'),
(16, 25, 19, 'Kuloo mzi wathabatha umthetheli-mkhosi obemiselwe ukuba ube ngumveleli wamagorha, namadoda amahlanu kwaphambi kokumkani, awafunyanwayo phakathi komzi, nonobhala wenkundla, obenyule ukumkani abantu belizwe, namadoda amashumi mathandathu kubantu belizwe, awafunyanwayo phakathi komzi;', 'XHO22'),
(16, 25, 20, 'Wayithabatha uNebhuzaradan umthetheli wabasiki, wayisa kukumkani waseBhabheli eRibhela.', 'XHO22'),
(16, 25, 21, 'Ukumkani waseBhabhiloni waboyisa waza wababulala eRibhela kwilizwe laseHamati. Ngenxa yoko uYuda wafuduswa emhlabeni wabo.', 'XHO22'),
(16, 25, 22, 'Ke abantu abasalayo ezweni lakwaYuda, awabashiyayo uNebhukadenetsare ukumkani waseBhabheli, wabamisela uGedaliya, unyana ka-Ahikam, unyana kaShafan, wangukumkani.', 'XHO22'),
(16, 25, 23, 'Beva bonke abathetheli benkundla, bona namadoda abo, ukuba ukumkani waseBhabheli umenze irhuluneli uGedaliya, beza kuGedaliya eMizpa, inguIshmayeli unyana kaNetaniya, noYohanan unyana kaKeraki, noSeraya Unyana kaTanumete, kaMonetofa, noYazaniya unyana womAgati, bona namadoda abo.', 'XHO22'),
(16, 25, 24, 'Wabafungela bona namadoda abo uGedaliya, wathi kubo: “Musani ukoyika ukuba ngabakhonzi bamaKhaledi; hlalani kweli lizwe, nikhonze ukumkani waseBhabheli; kwaye uya kulunga.', 'XHO22'),
(16, 25, 25, 'Kodwa ngenyanga yesixhenxe uIshmayeli unyana kaNetaniya unyana kaElishama wentsapho yasebukhosini weza waza wabulala amadoda alishumi kunye naye noGedaliya, kangangokuba wafa kunye namaYuda namaKhaledi awayekunye naye eMizpa.', 'XHO22'),
(16, 25, 26, 'Besuka ke bonke abantu, bethabathela kumfana besa kwindoda enkulu, nabathetheli bezintlu, beza eYiputa, ngenxa yokoyika amaKaledi.', 'XHO22'),
(16, 25, 27, 'Kwathi ngomnyaka wamashumi omathathu anesixhenxe wokuthinjwa kukaYehoyakin, ukumkani wakwaYuda, ngenyanga yeshumi elinesibini, ngosuku lwamashumi omabini anesixhenxe enyangeni leyo, uEvili-merodaki, ukumkani waseBhabheli, ngomnyaka wokuqalisa kwakhe ukuba ngukumkani, ukuba aphakanyiswe intloko kaYehoyakin, ukumkani wakwaYuda, ephuma entolongweni;', 'XHO22'),
(16, 25, 28, 'Wathetha naye kakuhle, wayimisa itrone yakhe, yangaphezu kweetrone zookumkani ababenaye eBhabheli;', 'XHO22'),
(16, 25, 29, 'Wanxiba iingubo zakhe zasentolongweni, wadla isonka phambi kobuso bakhe yonke imihla yobomi bakhe.', 'XHO22'),
(16, 25, 30, 'Imbonakalo yakhe yaba sisabelo samaxesha onke, abesinikwa ukumkani, isonka semihla ngemihla, yonke imihla yobomi bakhe.', 'XHO22'),
(17, 1, 1, 'uAdam, uSete, uEnoshe,', 'XHO22'),
(17, 1, 2, 'uKenan, noMahalaleli, noYaredi,', 'XHO22'),
(17, 1, 3, 'uEnoki, uMethusela, uLameki,', 'XHO22'),
(17, 1, 4, 'uNowa, noShem, noHam, noYafete.', 'XHO22'),
(17, 1, 5, 'Oonyana bakaYafete nguGomere, noMagogi, noMadayi, noYavan, noTubhali, noMesheki, noTirasi.', 'XHO22'),
(17, 1, 6, 'Oonyana bakaGomere nguAsh. iAshkenazi neRifati neTogarma.', 'XHO22'),
(17, 1, 7, 'Oonyana bakaYafan nguElisha, noTarshishe, namaKiti, namaDodan.', 'XHO22'),
(17, 1, 8, 'Oonyana bakaKama iKushi neMizraim, iPuti neKanan.', 'XHO22'),
(17, 1, 9, 'Oonyana bakaKushi nguSebha, noHafila, noSabheta, noRama, noSabhetega. Oonyana bakaRama nguAsheri. Bona kwaDedan.', 'XHO22'),
(17, 1, 10, 'uKushi wazala uNinaroti; waqala ukuba namandla ehlabathini.', 'XHO22'),
(17, 1, 11, 'uJitrayim wazala amaLudi, nama-Anam, namaHabhabhi, namaNafetu;', 'XHO22'),
(17, 1, 12, 'namaPatrusi, namaKasluhi, apho ayevela khona amaFilisti, namaKafetori.', 'XHO22'),
(17, 1, 13, 'uKanan wazala uSidon, izibulo lakhe, noHeti;', 'XHO22'),
(17, 1, 14, 'namaYebhusi, nama-Amori, namaGirgashi;', 'XHO22'),
(17, 1, 15, 'namaHifi, neenqanawa, nezitya zokutshiza;', 'XHO22'),
(17, 1, 16, 'namaArvadi, namaTsemares, namaMathematika.', 'XHO22'),
(17, 1, 17, 'Oonyana bakaShem nguShem. uElam, noAshure, noArpakishadi, noLudi, noAram, noUtse, noHule, noGetere, noMesheki.', 'XHO22'),
(17, 1, 18, 'uArpakishadi wazala uShela; uShela wazala uHebhere.', 'XHO22'),
(17, 1, 19, 'uHebhere wazalelwa oonyana ababini, igama lomnye belinguPelege; ngokuba ngemihla yakhe lahlukahlukana elimiweyo; igama lomninawa wakhe belinguYoketan.', 'XHO22'),
(17, 1, 20, 'uYoketan wazala uAlemodade, noSelefe, noHasaremete, noYeraki;', 'XHO22'),
(17, 1, 21, 'neHadoram, neUsale, neDikela;', 'XHO22'),
(17, 1, 22, 'noEbhali, noAbhimayeli, noShebha;', 'XHO22'),
(17, 1, 23, 'noOfire, noHafila, noYobhabhi. Bonke ke ngoonyana bakaYoketan.', 'XHO22'),
(17, 1, 24, 'uShem, noArpakishadi, noShela;', 'XHO22'),
(17, 1, 25, 'uEbhere, uPelege, uRehu,', 'XHO22'),
(17, 1, 26, 'noSerugi, noNakore, noTera,', 'XHO22'),
(17, 1, 27, 'uAbram; yena nguAbraham.', 'XHO22'),
(17, 1, 28, 'Oonyana baka-Abraham ngu uIsake noIshmayeli.', 'XHO22'),
(17, 1, 29, 'Zizo ezi ke inzala yabo: owamazibulo kaIshmayeli nguNebhayoti; neKedare, neAdebheli, neMibhesam;', 'XHO22'),
(17, 1, 30, 'uMishma noDuma, uMasa, uHadade, noTema;', 'XHO22'),
(17, 1, 31, 'noYeture, noNafishe, noKedema. Ngabo abo oonyana bakaIshmayeli.', 'XHO22'),
(17, 1, 32, 'Oonyana bakaKetura, isithende sika-Abraham, bamzala uZimran, noYokeshan, noMedan, noMidiyan, noIshbhaki, noShuwagi. Oonyana bakaYokesan nguAsheri; Bona kwaDedan.', 'XHO22'),
(17, 1, 33, 'Oonyana bakaMidiyan nguAsheri; uEva noEfere noEnoki noAbhida noElidaha. Bonke ngoonyana bakaKetura.', 'XHO22'),
(17, 1, 34, 'uAbraham wazala uIsake; Oonyana bakaIsake nguAyiri; uEsawu noSirayeli.', 'XHO22'),
(17, 1, 35, 'Oonyana bakaEsawu nguElifazi, noRehuweli, noYehuse, noYalam, noKora.', 'XHO22'),
(17, 1, 36, 'Oonyana bakaElifazi nguAsheri; uTeman no-Omare, uSefi noGatam, uKenazi noTimna noAmaleki.', 'XHO22'),
(17, 1, 37, 'Oonyana bakaRehuweli nguNahati, noSeraki, noShama, noMisa.', 'XHO22'),
(17, 1, 38, 'Oonyana bakaSehire yayinguLotan noShobhali noTsibheyon noAna noDishon noEzere noDishan.', 'XHO22'),
(17, 1, 39, 'Oonyana bakaLotan nguHori noHomam, uTimna ubengudade boLotan.', 'XHO22'),
(17, 1, 40, 'Oonyana bakaShobale; noAlan, noManahati, uEbhali, uSefi, no-Onam. Oonyana bakaTsibheyon nguAsh uAya, noAna.', 'XHO22'),
(17, 1, 41, 'Oonyana baka-Ana; Dishon noonyana bakaDishon; uAmram noEsbhan noJitran noKeran.', 'XHO22'),
(17, 1, 42, 'Oonyana bakaEzere nguBhilehan, noZavan, noYakan. Oonyana bakaDishan; Utse kunye noAran.', 'XHO22'),
(17, 1, 43, 'Ngabo aba ookumkani ababelawula ezweni lakwaEdom, kungekabikho kumkani ubelawula oonyana bakaSirayeli; uBhela, unyana kaBhehore, igama lomzi wakhe yiDinabha.', 'XHO22'),
(17, 1, 44, 'Wafa uBhela, kwalawula uYobhabhi, unyana kaSeraki, waseBhotsera esikhundleni sakhe.', 'XHO22'),
(17, 1, 45, 'Wafa uYobhabhi, waba ngukumkani uHusam, wasezweni lakwaBhateman;', 'XHO22'),
(17, 1, 46, 'Wafa uHusam, waba ngukumkani uHadadi, unyana kaBhedadi, owawabulalayo amaMidiyan ezweni lakwaMowabhi; yaye igama lomzi wakhe laliyiAwiti.', 'XHO22'),
(17, 1, 47, 'Wafa uHadadi, kwalawula uSamla waseMasereki esikhundleni sakhe.', 'XHO22'),
(17, 1, 48, 'Wafa uSamla, kwalawula uSawule, waseRehobhoti emlanjeni esikhundleni sakhe.', 'XHO22'),
(17, 1, 49, 'Wafa uSawule, kwalawula uBhahali-hanan, unyana ka-Akebhore, esikhundleni sakhe.', 'XHO22'),
(17, 1, 50, 'Wafa uBhahali-hanan, kwalawula uHadade esikhundleni sakhe; igama lomzi wakhe yiPahi; Igama lomkakhe lalinguMehetabheli, intombi kaMatrede, intombi kaMeshahabhi.', 'XHO22'),
(17, 1, 51, 'Naye uHadade wafa. Ke izikhulu zakwaEdom zaba; nenkosana uTimna, nenkosana uAliya, nesikhulu esinguYeteti;', 'XHO22'),
(17, 1, 52, 'noNtente-yakhe, inkosana uEla, nesikhulu esinguPinon;', 'XHO22'),
(17, 1, 53, 'neNkosana uKenazi, neNkosana uTemane, neNkosana uMibhetsare,', 'XHO22'),
(17, 1, 54, 'iNkosana uMagdiel, iNkosana u-Iram. Ziinkosana zakwaEdom ezo.', 'XHO22'),
(17, 2, 1, 'Ngabo abo oonyana bakaSirayeli; uRubhen, uSimon, uLevi noYuda, uIsakare noZebhulon;', 'XHO22'),
(17, 2, 2, 'uDan, uYosefu noBhenjamin, uNafetali, uGadi noAshere.', 'XHO22'),
(17, 2, 3, 'Oonyana bakaYuda nguAsheri; kunye no-Onan noShela; bathathu kwabazalelwa uShuwa, intombi yomKanan. Ke uEre, amazibulo kaYuda, ube enobubi emehlweni kaYehova; wambulala.', 'XHO22'),
(17, 2, 4, 'uTamare umolokazana wakhe wamzalela uPeretse noSaraki. Bonke oonyana bakaYuda baba bahlanu.', 'XHO22'),
(17, 2, 5, 'Oonyana bakaPeretse nguHetseron noHamuli.', 'XHO22'),
(17, 2, 6, 'Oonyana bakaSeraki yayinguSheraki; uZimri, uEtan, uHeman, uKalkoli noDara, bebahlanu.', 'XHO22'),
(17, 2, 7, 'Oonyana bakaKarmi nguAkare, umhlebi kaSirayeli, wenza ubumenemene entweni esingelwe phantsi.', 'XHO22'),
(17, 2, 8, 'Oonyana bakaEtan nguAzariya.', 'XHO22'),
(17, 2, 9, 'Oonyana bakaHetseron awazalelwa kubo; uJerekimeli noRam noKelubhayi.', 'XHO22'),
(17, 2, 10, 'Kwaye uRame wazala uAminata; uAminadabhi wazala uNashon, intloko yoonyana bakaYuda;', 'XHO22'),
(17, 2, 11, 'uNagshon wazala uSalma, uSalma wazala uBhohazi;', 'XHO22'),
(17, 2, 12, 'uBhohazi wazala uObhedi; uObhedi wazala uYese;', 'XHO22'),
(17, 2, 13, 'uYese wazala uEliyabhi, amazibulo akhe, noAbhinadabhi owesibini, noShima owesithathu;', 'XHO22'),
(17, 2, 14, 'owesine uNataniyeli, wesihlanu nguRadayi;', 'XHO22'),
(17, 2, 15, 'noOtsem owesithandathu, noDavide owesixhenxe;', 'XHO22'),
(17, 2, 16, 'Oodade wabo yayinguTseruya noAbhigali. Oonyana bakaTseruya nguAbhishayi, noYowabhi, noAsaheli bathathu.', 'XHO22'),
(17, 2, 17, 'uAbhigali wazala uAmasa; uyise ka-Amasa ebenguYetere umIshmayeli.', 'XHO22'),
(17, 2, 18, 'uKalebhi, unyana kaHetseron, wazala oonyana baka-Azubha umkakhe, noYeriyoti; Ngabo aba oonyana bakhe; iYezere, neSobhabhi, neArdon.', 'XHO22'),
(17, 2, 19, 'Wafa uAsubha, uKalebhi wamthabatha uEfrata, wamzalela uHure.', 'XHO22'),
(17, 2, 20, 'uHure wazala u-Uri, u-Uri wazala uBhetsaleli;', 'XHO22'),
(17, 2, 21, 'Emveni koko wayingena uHetseron intombi kaMagire, uyise kaGiliyadi, wayizeka eminyaka imashumi mathandathu ezelwe; wazala uSegube.', 'XHO22'),
(17, 2, 22, 'uSegubhe wazala uYahire, obenemizi emashumi mabini anamithathu ezweni laseGiliyadi.', 'XHO22'),
(17, 2, 23, 'Wathabatha kubo iGeshuri, nama-Aram, nemizi kaYahire, neKenati namagxamesi ayo: izixeko ezimashumi mathandathu. Bonke aba yayingoonyana bakaMagire, uyise kaGiliyadi.', 'XHO22'),
(17, 2, 24, 'Emva kokufa kukaHetseron eKalebhefrata, uAbhiya, umfazi kaHetseron, wazala uAshure, uyise kaTekowa.', 'XHO22'),
(17, 2, 25, 'Oonyana bakaYerakemeli, amazibulo kaHetseron, nguRam owamazibulo, noBhuna, no-Oren, no-Osem, noAhiya.', 'XHO22'),
(17, 2, 26, 'uYerameli ebenomnye umfazi, ogama belinguAtara; ibingu mama ka Onam.', 'XHO22'),
(17, 2, 27, 'Oonyana bakaRam, amazibulo kaYerameli, nguMahase, noYamin, noEkere.', 'XHO22'),
(17, 2, 28, 'Oonyana bakaOnam nguShemayi noYada noonyana bakaSamayi; uNadabhi noAbisur.', 'XHO22'),
(17, 2, 29, 'Igama lomka-Abhishure lalinguAbhihayile; wamzalela uAgbhane noMolide.', 'XHO22'),
(17, 2, 30, 'Oonyana bakaNadabhi nguSeledi noApayim, kodwa uSelede wafa engenabantwana.', 'XHO22'),
(17, 2, 31, 'Oonyana baka-Apayim nguIshi. Oonyana bakaIshi yayinguIshi. Sheshane. Oonyana bakaSheshan nguSheshan; Ima kancinci.', 'XHO22'),
(17, 2, 32, 'Oonyana bakaYada umntakwaboSamayi yayinguYada. uYetere noYonatan, noYetere wafa engenabantwana.', 'XHO22'),
(17, 2, 33, 'Oonyana bakaYonatan nguAsheri; uPelete noZaza. Ngabo abo oonyana bakaYeramiyeli.', 'XHO22'),
(17, 2, 34, 'uSeshane ebengenanyana, eneentombi zodwa. uSheshane ubenomkhonzi ongumYiputa, ogama belinguYarha.', 'XHO22'),
(17, 2, 35, 'uSeshane wamnika uYarha, umkhonzi wakhe, intombi yakhe, yangumkakhe; wamzalela uAtayi.', 'XHO22'),
(17, 2, 36, 'uAtayi wazala uNatan; uNatan wazala uSabhadi;', 'XHO22'),
(17, 2, 37, 'uSabhadi wazala uEfali; uEfelali wazala uObhedi;', 'XHO22'),
(17, 2, 38, 'uObhedi wazala uYehu; uYehu wazala uAzariya;', 'XHO22'),
(17, 2, 39, 'uAzariya wazala uHelesi, uHeleshe ozala uEleyasa;', 'XHO22'),
(17, 2, 40, 'uElesa wazala uShisamayi; uShisamayi wazala uShalum;', 'XHO22'),
(17, 2, 41, 'uShalum wazala uYekamiya; uYekamiya wazala uElishama;', 'XHO22'),
(17, 2, 42, 'Oonyana bakaKalebhi, umntakwabo Yerameli, yayinguMesha amazibulo akhe, uyise kaSife noonyana bakaMaresa, uyise kaHebron.', 'XHO22'),
(17, 2, 43, 'Oonyana bakaHebron nguKora, noTapuwa, noRekem, noShema.', 'XHO22'),
(17, 2, 44, 'uShema wazala uRaham, uyise kaYokowam, noRekem, uyise kaShema.', 'XHO22'),
(17, 2, 45, 'Unyana kaShemayi nguMahon; uMahon wazala iBhete-tsure.', 'XHO22'),
(17, 2, 46, 'uEva, isithende sikaKalebhi, wazala uHaran, uMoses noGas; uHaran wazala uGase.', 'XHO22'),
(17, 2, 47, 'Oonyana bakaYagedayi nguRegem, noYotam, noGeshan, noPelete, noEfa, noShahafu.', 'XHO22'),
(17, 2, 48, 'uMahaga, isithende sikaKalebhi, wazala uShebhere noTirana.', 'XHO22'),
(17, 2, 49, 'Wazala uShahafe uyise wabaseMademana, noSewa ozala amaMagena, noyise wabaseGibheha; Intombi kaKalebhi ibinguAhazi.', 'XHO22'),
(17, 2, 50, 'Ngabo abo oonyana bakaKalebhi, unyana kaHure, amazibulo kaEfrata; nguSobhali uyise kaKiriyati-yeharim;', 'XHO22'),
(17, 2, 51, 'uSalima, uyise kaBhetelehem, uHarefe, uyise wabaseBhete-gadere.', 'XHO22'),
(17, 2, 52, 'uShobhali uyise kaKiriyati-yeharim wayenoonyana; Abathathu abanesiqingatha abakwaManase.', 'XHO22'),
(17, 2, 53, 'Nemizalwane yaseKiriyati-yeharim; amaBhaite, amaPugi, amaSumati namaYiputa; kubo kwaphuma amaBasariya namaStauli.', 'XHO22'),
(17, 2, 54, 'Oonyana bakaSalma nguAsheri; iBhetelehem, namaNetofi, neAtaroti, nendlu kaYowabhi, nesiqingatha soonyana bakaManaki, namaTsoro.', 'XHO22'),
(17, 2, 55, 'Nenzala yababhali, ababehlala eYabheshe; nabahlambi behlamba, noShimeyi, nooSuga. Ngawo lawo amaKanan, awaphuma eHamati, uyise wendlu kaRekabhi.', 'XHO22'),
(17, 3, 1, 'Ngabo abo oonyana bakaDavide, awabazalelwayo eHebron; owamazibulo nguAmnon ngoAkinowam waseYizereli; owesibini nguDaniyeli ka-Abhigali waseKarmele;', 'XHO22'),
(17, 3, 2, 'Owesithathu nguAbhisalom, unyana kaMagi, intombi kaTalemayi, ukumkani waseGeshuri; owesine nguAdoniya, unyana kaHagiti;', 'XHO22'),
(17, 3, 3, 'owesihlanu nguShefatiya wakwa-Abhitali; owesithandathu nguJitream ngoEgla umkakhe.', 'XHO22'),
(17, 3, 4, 'Abo bathandathu bazalelwa eHebron; waba neminyaka esixhenxe neenyanga ezintandathu engukumkani eYerusalem, waba neminyaka emashumi mathathu anamithathu engukumkani.', 'XHO22'),
(17, 3, 5, 'Wabazalelwa eYerusalem; uShimeha, noShobhabhi, noNatan, noSolomon, bobane; nguBhatshe-shuwa intombi ka-Amiyeli;', 'XHO22'),
(17, 3, 6, 'noIbhare, noElishama, noElifelete;', 'XHO22'),
(17, 3, 7, 'noNoga, noNefege, noYafiya;', 'XHO22'),
(17, 3, 8, 'Kukho uElishama, uEliyada, noElifeleti abasithoba bodwa.', 'XHO22'),
(17, 3, 9, 'Bonke aba yayingoonyana bakaDavide, ngaphandle koonyana bezithende, noTamare udade wabo.', 'XHO22'),
(17, 3, 10, 'Unyana kaSolomon yayinguRehabheham, unyana wakhe uAbhiya, unyana wakhe uAsa, unyana wakhe uYoshafati,', 'XHO22'),
(17, 3, 11, 'nguYoram unyana wakhe, nguAhaziya unyana wakhe, nguYowashe unyana wakhe,', 'XHO22'),
(17, 3, 12, 'uAmatsiya unyana wakhe, uAzariya unyana wakhe, uYotam unyana wakhe,', 'XHO22'),
(17, 3, 13, 'nguAhazi unyana wakhe, nguHezekiya unyana wakhe, nguManase unyana wakhe,', 'XHO22'),
(17, 3, 14, 'unyana wakhe uAmon, unyana wakhe uYosiya.', 'XHO22'),
(17, 3, 15, 'Oonyana bakaYosiya nguYohanan inkulu, noYoyakim owesibini, nguZedekiya owesithathu, nguShalum owesine.', 'XHO22'),
(17, 3, 16, 'Oonyana bakaYehoyakim nguYekoniya unyana wakhe, nguZedekiya unyana wakhe.', 'XHO22'),
(17, 3, 17, 'Oonyana bakaYekoniya nguAsire unyana wakhe, uSalatiyeli;', 'XHO22'),
(17, 3, 18, 'noMalekiram, noPedaya, noSenazare, noYekamiya, noHosama, noNedabhiya.', 'XHO22'),
(17, 3, 19, 'Oonyana bakaPedaya nguZerubhabheli noShimehi; noonyana bakaZerubhabheli; ooMeshulam, noHananiya, noShelomoti udade wabo;', 'XHO22'),
(17, 3, 20, 'noHasubha, no-Oheli, noBheriya, noHasadiya, noYusabhetsede;', 'XHO22'),
(17, 3, 21, 'Oonyana bakaHananiya nguShela; oonyana bakaPelatiya noIsaya: oonyana bakaRefaya, oonyana baka-Arnan, oonyana baka-Obhadiya, oonyana bakaSheganiya.', 'XHO22'),
(17, 3, 22, 'Oonyana bakaSheganiya yayinguShekaniya noonyana bakaShemaya; uHatusi, noJigale, noBharja, noNearjah, noShafati ezintandathu.', 'XHO22'),
(17, 3, 23, 'Oonyana bakaNeriyasi yayinguAriyeli uEliyonayi, noHezekiya, noAsrikam;', 'XHO22'),
(17, 3, 24, 'Oonyana bakaEliyonayi nguHodaya, noEliyashibhi, noPelaya, noAkubhe, noYohanan, noDelaya, noAnani, basixhenxe.', 'XHO22'),
(17, 4, 1, 'Oonyana bakaYuda nguAsheri; noPeretse, noHetseron, noKarmi, noHure, noShobhali.', 'XHO22'),
(17, 4, 2, 'uRehaya, unyana kaShobhali, wazala uYahati; uYahati wazala uAkumayi noLahadi. Yiyo leyo imizalwane yamaSora.', 'XHO22'),
(17, 4, 3, 'Ngabo abakayise uEtam; neYizereli, neIsma, noIdebhas; igama lodade wabo lalinguHaselponi;', 'XHO22'),
(17, 4, 4, 'noPenuweli ozala amaGedore, noEzere ozala uHusa. Ngabo abo oonyana bakaHure, amazibulo kaEfrata, uyise kaBhetelehem.', 'XHO22'),
(17, 4, 5, 'Uyise kaTekowa wayenabafazi ababini, uHela noNaara.', 'XHO22'),
(17, 4, 6, 'uNahara wamzalela uAhuzam, noHefere, noTemeni, noHahashtari. Aba yayingoonyana bakaNahara.', 'XHO22'),
(17, 4, 7, 'Oonyana bakaHela nguSereti noItsohare noEtenan.', 'XHO22'),
(17, 4, 8, 'uKose wazala uAnubhi, noSobeba, neentsapho zika-Akarhali unyana kaHarum.', 'XHO22'),
(17, 4, 9, 'Ke kaloku uYabhetse ebezukile kunabazalwana bakhe; Unina wamthiya igama elinguYabheshe, esithi, Kungokuba ndimzele ndinentlungu.', 'XHO22'),
(17, 4, 10, 'uYabhetse wambongoza uTHIXO kaSirayeli, esithi: “Owu, ukuba ngokwenene uyafuna ukundisikelela waza wandisa ilizwe lam, ukuba isandla sakho sibe phezu kwam nokuba undikhusele ebubini, ukuze ndingandenzakalisanga. Waza uThixo wamnika oko akucelayo.', 'XHO22'),
(17, 4, 11, 'Kelube, umninawa kaShuwa, wazala uMehiri, uyise kaEston.', 'XHO22'),
(17, 4, 12, 'Kwaye uEston wazala uBhetrafa noPaseka noTehina, uyise kaIrinahasi. Ngawo lawo amadoda akwaRega.', 'XHO22'),
(17, 4, 13, 'Oonyana bakaKenazi nguOteniyeli noSeraya; noonyana bakaOteniyeli; Hathath.', 'XHO22'),
(17, 4, 14, 'uMehonotayi wazala uOpra; uSeraya wazala uYowabhi, ozala ooSihlambo samaKerasim; ngokuba bezizingcibi.', 'XHO22'),
(17, 4, 15, 'Noonyana bakaKalebhi, unyana kaYefune; uIru, noEla, noNaham; Oonyana bakaEla nguKenazi.', 'XHO22'),
(17, 4, 16, 'Oonyana bakaYehaleli nguSife noZifa, noTiriya, noAzareli.', 'XHO22'),
(17, 4, 17, 'Oonyana bakaEzra nguYetere, noMerede, noEfere, noYalon; Wazala uMiriyam noSamayi noIshbha, uyise kaEstemowa.', 'XHO22'),
(17, 4, 18, 'uYuda umkakhe wazala uYarede, uyise kaGedore, noHebhere, ozala ooSogo, noYekutiyeli, ozala uSanowa. Ngabo abo oonyana bakaBhitiya, intombi kaFaro, eyazeka uMeredeti;', 'XHO22'),
(17, 4, 19, 'Oonyana bomkakhe uHodya, udade boNaham, nguyise kaKehila umGaram, noEstemowa waseMagati.', 'XHO22'),
(17, 4, 20, 'Oonyana bakaShimon nguAmnon noRina, uBhen-hanan noTilon. Oonyana bakaIshi nguShuheti noBhen-soheti.', 'XHO22'),
(17, 4, 21, 'Oonyana bakaShela, unyana kaYuda, nguEre, uyise kaLeka, noLada, uyise kaMaresa, nemizalwane yendlu yabasebenza kakuhle, yendlu ka-Ashbheha;', 'XHO22'),
(17, 4, 22, 'noYokim namadoda akwaKosebha, noYowashe, noSarafi, ababelawula amaMowabhi, noYashubilehem. Kwaye ezi zizinto zakudala.', 'XHO22'),
(17, 4, 23, 'Ngabo abo ababumbi nabahlala phakathi kwezityalo neentango; bahlala khona nokumkani emsebenzini wakhe.', 'XHO22'),
(17, 4, 24, 'Oonyana bakaSimon nguNemuweli, noYamin, noYaribhi, noSaraki, noSawule,', 'XHO22'),
(17, 4, 25, 'nguShalum unyana wakhe, nguMibhesam unyana wakhe, nguMishma unyana wakhe.', 'XHO22'),
(17, 4, 26, 'Oonyana bakaMishma nguHamuweli unyana wakhe, nguSagure unyana wakhe, nguShimehi unyana wakhe.', 'XHO22'),
(17, 4, 27, 'uShimehi ubenoonyana abalishumi elinabathandathu, neentombi ezintandathu; kodwa abazalwana bakhe ababanga nabantwana baninzi, yaye yonke inzala yabo ayizange yande njengoonyana bakaYuda.', 'XHO22'),
(17, 4, 28, 'Bahlala eBher-shebha naseMolada naseHatsare;', 'XHO22'),
(17, 4, 29, 'noBhiliha, noEsem, noToladi;', 'XHO22'),
(17, 4, 30, 'neBhetuweli, neHorma, neTsikelage;', 'XHO22'),
(17, 4, 31, 'neBhete-marabhoti, neHazarusi, neBhete-bhiri, neShaharayim. Yiyo leyo imizi yabo, wada uDavide waba ngukumkani.', 'XHO22'),
(17, 4, 32, 'Imizi yabo yayiyiEtam, neAyin, neRimon, neToken, neAsan, izixeko ezihlanu;', 'XHO22'),
(17, 4, 33, 'nemizi yabo yonke ebeyiyiphahlile loo mizi, kwesa kwaBhahali. Zizo ezo iindawo zabo zokuhlala nemizalwane yabo.', 'XHO22'),
(17, 4, 34, 'NoMesobhabhe, noYamleki, noYosa unyana ka-Amatsiya;', 'XHO22'),
(17, 4, 35, 'noYoweli, noYehu, unyana kaYosibhiya, unyana kaSeraya, unyana ka-Asiyeli;', 'XHO22'),
(17, 4, 36, 'noEliyohenayi, noYakobi, noYohana, noAsaya, noAdiyeli, noYeshimiyeli, noBhenaya;', 'XHO22'),
(17, 4, 37, 'noSisa, unyana kaZifi, unyana ka-Alon, unyana kaYedaya, unyana kaZimri, unyana kaShemaya;', 'XHO22'),
(17, 4, 38, 'Abo bakhankanyiweyo ngamagama babezizikhulu ngokwemizalwane yabo, nezindlu zooyise zaba ninzi kakhulu.', 'XHO22'),
(17, 4, 39, 'Banduluka besa eGedore ngasempumalanga komfula, besiya kuyifunela utyani imfuyo yabo.', 'XHO22'),
(17, 4, 40, 'Bafumana idlelo elityebileyo, elihle; ngokuba bebemi khona abantu bakwaHam kudala.', 'XHO22'),
(17, 4, 41, 'Beza abamagama abhaliweyo ngemihla kaHezekiya ukumkani wakwaYuda, bazidiliza iintente zabo, nezakhiwo ezafunyanwa khona, baziqalekisa unanamhla, bahlala emagumbini abo okudlela, ngokuba bekungekho namnye iintente amadlelo emihlambi yabo.', 'XHO22'),
(17, 4, 42, 'Abanye kubo, oonyana bakaSimon, bengamakhulu amahlanu, benyuka baya entabeni yakwaSehire, benoPelatiya, noNeriya, noRefaya, noUziyeli, oonyana bakaIshi, bengabathetheli.', 'XHO22'),
(17, 4, 43, 'Bawathimba amasalela ama-Amaleki awayekhululwe, ahlala khona unanamhla.', 'XHO22'),
(17, 5, 1, 'Oonyana bakaRubhen, amazibulo kaSirayeli, yena waba ngamazibulo; Kungokuba wasenza inqambi isilili sikayise, ubuzibulo bakhe banikwa oonyana bakaYosefu, unyana kaSirayeli; ubuzibulo.', 'XHO22'),
(17, 5, 2, 'Kuba uYuda wazenza wanegunya phezu kwabazalwana bakhe, kwaphuma kuye inganga; kodwa ubuzibulo yayibubo bukaYosefu:', 'XHO22'),
(17, 5, 3, 'Ndithi oonyana bakaRubhen, izibulo likaSirayeli, nguHanoki, noPalu, nguHetseron, noKarmi;', 'XHO22'),
(17, 5, 4, 'Oonyana bakaYoweli; unyana wakhe uShemaya, nguGogi unyana wakhe, nguShimehi unyana wakhe,', 'XHO22'),
(17, 5, 5, 'nguMika unyana wakhe, nguRehaya unyana wakhe, nguBhahali unyana wakhe,', 'XHO22'),
(17, 5, 6, 'unyana wakhe uBhehera, awathinjwa nguTigelati-pilenezere, ukumkani waseAsiriya; ibiyinkosana yamaRubhen.', 'XHO22'),
(17, 5, 7, 'Abazalwana ngokwemizalwane yabo emilibeni yokuzalwa ngokwemizalwane yabo, inkulu yooyise ibinguYehiyeli noZekariya,', 'XHO22'),
(17, 5, 8, 'noBhela unyana ka-Asazi, unyana kaShema, unyana kaYoweli, ohleli eArohere, wesa eNebho naseBhahali-meyon;', 'XHO22'),
(17, 5, 9, 'Ngasempuma wahlala entlango ukuya kufikelela kumlambo umEfrate, ngenxa yokuba imihlambi yabo yayimininzi kwilizwe laseGiliyadi.', 'XHO22'),
(17, 5, 10, 'Ngemihla kaSawule bawaxabela amaHagaren, awawa esandleni sabo, ahlala ezintenteni zawo ezweni lonke lasempumalanga laseGiliyadi.', 'XHO22'),
(17, 5, 11, 'Oonyana bakaGadi bahlala malungana nabo ezweni laseBhashan, besa eSalga.', 'XHO22'),
(17, 5, 12, 'uYoweli wayeyintloko, owesibini yayinguShafam, uYahanayi noShafati, eBhashan.', 'XHO22'),
(17, 5, 13, 'Abazalwana babo ngokwezindlu zooyise nguMikayeli, noMeshulam, noShebha, noYorayi, noYagan, noZiya, noHebhere, basixhenxe;', 'XHO22'),
(17, 5, 14, 'Ngabo aba oonyana baka-Abhihayili, unyana kaHuri, unyana kaYarowa, unyana kaGiliyadi, unyana kaMikayeli, unyana kaYese, unyana kaYagdo, unyana kaBhusi;', 'XHO22'),
(17, 5, 15, 'uAki, unyana ka-Abhediyeli, unyana kaGuni, intloko yezindlu zabo.', 'XHO22'),
(17, 5, 16, 'Bahlala eGiliyadi naseBhashan namadlelo ayo, namadlelo onke aseSharon ezweni labo.', 'XHO22'),
(17, 5, 17, 'Bonke aba babebhalwe ngokwemizalwane, ngemihla kaYotam ukumkani wakwaYuda, nangemihla kaYarobheham ukumkani wakwaSirayeli.', 'XHO22'),
(17, 5, 18, 'Oonyana bakaRubhen, noGadi, nesinye isizwe sakwaManase, amagorha aphethe izixhobo nekrele, abatola izaphetha, abenolwazi lwemfazwe, babengamashumi amane amawaka, anamakhulu mane, anamanci mathandathu ukuya emfazweni.', 'XHO22'),
(17, 5, 19, 'Kwaye balwa ngokuchasene noHagare, ngokuchasene noYitare noNifayi noNodabhi.', 'XHO22'),
(17, 5, 20, 'Bancedwa kuwo, kwaza kwanikelwa ooHagare esandleni sabo, nabo bonke ababenabo; ngokuba babekholose ngaye.', 'XHO22'),
(17, 5, 21, 'Bayithabatha imihlambi yabo; iinkamela zabo ngamashumi omahlanu amawaka, anamakhulu mabini anamanci mahlanu, mpahla emfutshane, namawaka amabini amaesile, nekhulu lamawaka amadoda.', 'XHO22'),
(17, 5, 22, 'Kuba babebaninzi ababuleweyo, ngokuba imfazwe leyo ibiyekaThixo; Bahlala ke endaweni yabo kwada kwathinjwa.', 'XHO22'),
(17, 5, 23, 'Oonyana besiqingatha sesizwe sakwaManase babehlala kwelo zwe; banduluka bethabathela eBhashan, besa eBhahali-hermon, naseSenire, besa entabeni yeHermon.', 'XHO22'),
(17, 5, 24, 'Zizo ezi iintloko zezindlu zooyise: uEfere, noIshi, noEliyeli, noAsriyeli, noYeremiya, noHodawa, noYagiyeli, amadoda anobukroti, amadoda anegama, iintloko zezindlu zooyise.', 'XHO22'),
(17, 5, 25, 'Bamona uTHIXO wookhokho babo baza bakrexeza noothixo beentlanga zelo lizwe uThixo wayewazitshabalalisa phambi kwabo.', 'XHO22'),
(17, 5, 26, 'uThixo kaSirayeli wawukhupha umoya kaPule, ukumkani waseAsiriya, nomoya kaTilegati-pilenezere, ukumkani waseAsiriya, wabasusa, amaRubhen, namaGadi, nesiqingatha sesizwe sakwaManase; babazisa eHala, naseHabhore, naseHar, besa emlanjeni oyiGoshan, unanamhla.', 'XHO22'),
(17, 6, 1, 'Oonyana bakaLevi nguAsheri; uGershon, noKehati, noMerari.', 'XHO22'),
(17, 6, 2, 'Oonyana bakaKehati nguAmram, noItsare, noHebron, noUziyeli.', 'XHO22'),
(17, 6, 3, 'Oonyana baka-Amram nguAsheri; uAron noMosis noMiriyam noonyana baka-Aron; uNadabhi noAbhihu, uElazare noItamare.', 'XHO22'),
(17, 6, 4, 'uElazare wazala uPinehasi; uPinehasi wazala uAbhishuwa;', 'XHO22'),
(17, 6, 5, 'uAbhishuwa wazala uBhuki, uBhuki wazala u-Uzi;', 'XHO22'),
(17, 6, 6, 'u-Uzi wazala uSeraya; uSeraya wazala uMerayoti;', 'XHO22'),
(17, 6, 7, 'uMerayoti wazala uAmariya, uAmariya wazala uAkitubhi;', 'XHO22'),
(17, 6, 8, 'uAhitubhi wazala uTsadoki, uTsadoki wazala uAhimahatse;', 'XHO22'),
(17, 6, 9, 'uAhimahatse wazala uAzariya; uAzariya wazala uYohanan;', 'XHO22'),
(17, 6, 10, 'uYohanan nguyise ka-Azariya, obengumbingeleli endlwini awayakhayo uSolomon eYerusalem;', 'XHO22'),
(17, 6, 11, 'uAzariya wazala uAmariya, uAmariya wazala uAkitubhi;', 'XHO22'),
(17, 6, 12, 'uAhitubhi wazala uTsadoki, uTsadoki wazala uShalum;', 'XHO22'),
(17, 6, 13, 'uShalum wazala uHilekiya; uHilekiya wazala uAzariya;', 'XHO22'),
(17, 6, 14, 'uAzariya wazala uSeraya; uSeraya wazala uYosadaki;', 'XHO22'),
(17, 6, 15, 'uYotsadaki waya ekuthinjweni ekumshenxiseni kukaYehova uYuda neYerusalem ngesandla sikaNebhukadenetsare.', 'XHO22'),
(17, 6, 16, 'Oonyana bakaLevi ngu uGershom, noKehati, noMerari.', 'XHO22'),
(17, 6, 17, 'Ngawo lawo amagama oonyana bakaGershom; uLibheni noShimehi.', 'XHO22'),
(17, 6, 18, 'Oonyana bakaKehati nguAmram, noItsare, noHebron, noUziyeli.', 'XHO22'),
(17, 6, 19, 'Oonyana bakaMerari nguMali noMushi. Zizo ezo ke inzala kaLevi ngokwezindlu zooyise.', 'XHO22'),
(17, 6, 20, 'Van Gersom; nguLibheni unyana wakhe, nguYahati unyana wakhe, nguShima unyana wakhe,', 'XHO22'),
(17, 6, 21, 'nguYowaki unyana wakhe, nguIdo unyana wakhe, nguSeraki unyana wakhe, nguYaterayi unyana wakhe.', 'XHO22'),
(17, 6, 22, 'Oonyana bakaKehati nguAminadabhi unyana wakhe, nguKora unyana wakhe, nguAsire unyana wakhe,', 'XHO22'),
(17, 6, 23, 'unyana wakhe nguElikana, nguEbhiyasafu unyana wakhe, noAsire unyana wakhe,', 'XHO22'),
(17, 6, 24, 'nguTahati unyana wakhe, ngu-Uriyeli unyana wakhe, ngu-Uziya unyana wakhe, nguSawule unyana wakhe.', 'XHO22'),
(17, 6, 25, 'Oonyana bakaElikana nguAmasayi noAkimoti.', 'XHO22'),
(17, 6, 26, 'KuElikana: oonyana bakaElikana; unyana wakhe uSofayi, noNahati unyana wakhe;', 'XHO22'),
(17, 6, 27, 'noEliyabhi unyana wakhe, noYeroham unyana wakhe, noElikana unyana wakhe.', 'XHO22'),
(17, 6, 28, 'Oonyana bakaSamuweli nguAsheri; owamazibulo nguVashni noAbhiya.', 'XHO22'),
(17, 6, 29, 'Oonyana bakaMerari nguMageli unyana wakhe, noLibheni, noShimehi unyana wakhe, no-Uza unyana wakhe,', 'XHO22'),
(17, 6, 30, 'noSimeha unyana wakhe, noHagia unyana wakhe, noAsaya unyana wakhe.', 'XHO22'),
(17, 6, 31, 'Aba ngabo uDavide wababeka phezu kwenkonzo yokuvuma endlwini kaYehova emva kokuphumla kwetyeya.', 'XHO22'),
(17, 6, 32, 'Baqhubeka bekhonza ngengoma phambi kwendawo yokuhlala yentente yokuhlangana de uSolomon wayakha indlu kaYehova eYerusalem;', 'XHO22'),
(17, 6, 33, 'Yaye babengabo babelindile nabantwana babo koonyana bamaHeti nguHeman, imvumi, unyana kaYoweli, unyana kaSamuweli;', 'XHO22'),
(17, 6, 34, 'unyana kaElikana, unyana kaYeroham, unyana kaEliyeli, unyana kaTowagi,', 'XHO22'),
(17, 6, 35, 'unyana kaSufi, unyana kaElikana, unyana kaMahati, unyana ka-Amasayi,', 'XHO22'),
(17, 6, 36, 'unyana kaElikana, unyana kaYoweli, unyana ka-Azariya, unyana kaZefaniya,', 'XHO22'),
(17, 6, 37, 'unyana kaTahati, unyana ka-Asire, unyana ka-Abhiyasafu, unyana kaKora;', 'XHO22'),
(17, 6, 38, 'unyana kaItsare, unyana kaKehati, unyana kaLevi, unyana kaSirayeli.', 'XHO22'),
(17, 6, 39, 'uAsafu umzalwana wakhe wema ngasekunene kwakhe, inguAsafu unyana kaBherekiya, unyana kaShimeha;', 'XHO22'),
(17, 6, 40, 'unyana kaMikayeli, unyana kaBhaseya, unyana kaMalekiya,', 'XHO22'),
(17, 6, 41, 'unyana kaEtene, unyana kaSeraki, unyana ka-Adaya,', 'XHO22'),
(17, 6, 42, 'unyana kaEtan, unyana kaShima, unyana kaShimehi;', 'XHO22'),
(17, 6, 43, 'unyana kaYahati, unyana kaGershom, unyana kaLevi.', 'XHO22'),
(17, 6, 44, 'Bemi abazalwana babo, oonyana bakaMerari, ngasekhohlo: uEtan, unyana kaKishe, unyana ka-Abhedi, unyana kaMaluke;', 'XHO22'),
(17, 6, 45, 'unyana kaHashabhiya, unyana ka-Amatsiya, unyana kaHilekiya;', 'XHO22'),
(17, 6, 46, 'unyana ka-Amsi, unyana kaBhani, unyana kaShamere,', 'XHO22'),
(17, 6, 47, 'unyana kaMageli, unyana kaMushi, unyana kaMerari, unyana kaLevi.', 'XHO22'),
(17, 6, 48, 'Nabazalwana babo, abaLevi, bebanyulelwe wonke umsebenzi womnquba wendlu kaThixo.', 'XHO22'),
(17, 6, 49, 'uAron noonyana bakhe babesenza umbingelelo esibingelelweni seminikelo etshiswayo nasesibingelelweni sesiqhumiso, yaye babenyulelwe wonke umsebenzi weNgcwele kaNgcwele nokucamagushela izono zoonyana bakaSirayeli, ngokoko konke uMosis, umkhonzi kaThixo oyinyaniso, owayelapho umyalelo.', 'XHO22'),
(17, 6, 50, 'Ngabo abo oonyana baka-Aron; nguElazare unyana wakhe, nguPinehasi unyana wakhe, nguAbhishuwa unyana wakhe,', 'XHO22'),
(17, 6, 51, 'noBhuki unyana wakhe, no-Usi unyana wakhe, noSeraya unyana wakhe,', 'XHO22'),
(17, 6, 52, 'unyana wakhe uMerayoti, nguAmariya unyana wakhe, nguAhitubhi unyana wakhe,', 'XHO22'),
(17, 6, 53, 'nguTsadoki unyana wakhe, nguAhimahatse unyana wakhe.', 'XHO22'),
(17, 6, 54, 'Zizo ezo iindawo zabo zokuhlala ezintenteni zabo, ngeendawo zabo, ezoonyana baka-Aron, bemizalwane yoonyana bakaKehati; ngokuba laba lelabo iqashiso.', 'XHO22'),
(17, 6, 55, 'Babanika iHebron ezweni lakwaYuda namadlelo ayo ngeenxa zonke.', 'XHO22'),
(17, 6, 56, 'Kodwa umhlaba wesixeko needolophana zaso bawunika uKalebhi unyana kaYefune.', 'XHO22'),
(17, 6, 57, 'Banikwa oonyana baka-Aron imizi yakwaYuda: yiHebron leyo, umzi ongqingiweyo, neLibhena namadlelo ayo, neYatire, ne-Estemowa, namadlelo ayo;', 'XHO22'),
(17, 6, 58, 'neHilene nedlelo layo, neDebiri nedlelo layo,', 'XHO22'),
(17, 6, 59, 'neAsan nedlelo layo, neBhete-shemeshe nedlelo layo;', 'XHO22'),
(17, 6, 60, 'kwisizwe sakwaBhenjamin; neGebha namadlelo ayo, neAlemete namadlelo ayo, neAnatoti namadlelo ayo. Yonke imizi yabo ngokwemizalwane yabo yaba yimizana elishumi elinamithathu.', 'XHO22'),
(17, 6, 61, 'Oonyana bakaKehati, abaseleyo emizalwaneni yeso sizwe, banikwa ngeqashiso imizi elishumi kwesinye kwisiqingatha sesizwe sakwaManase.', 'XHO22'),
(17, 6, 62, 'Ngabo abo oonyana bakaGershom ngokweentsapho zabo kwisizwe sakwaIsakare nakwisizwe sakwa-Ashere nakwisizwe sakwaNafetali nakwisizwe sakwaManase eBhashan.', 'XHO22'),
(17, 6, 63, 'Oonyana bakaMerari banikwa iqashiso imizi elishumi elinamibini, ngokwemizalwane yabo, kwisizwe sakwaRubhen, nakwisizwe sakwaGadi, nakwisizwe sakwaZebhulon.', 'XHO22'),
(17, 6, 64, 'Oonyana bakaSirayeli babanika abaLevi ezo mizi namadlelo ayo.', 'XHO22'),
(17, 6, 65, 'Babanika ngeqashiso imizi leyo yathiywayo esizweni soonyana bakaYuda, nakwisizwe soonyana bakaSimon, nakwisizwe soonyana bakaBhenjamin.', 'XHO22'),
(17, 6, 66, 'Eminye imizalwane yoonyana bakaKehati yaba nemizi yelizwe labo esizweni sakwaEfrayim.', 'XHO22'),
(17, 6, 67, 'Babanika iShekem kweleentaba lakwaEfrayim, nedlelo layo emizini esezantsi banika iGezere namadlelo ayo;', 'XHO22'),
(17, 6, 68, 'neYokemeham nedlelo layo, neBhete-horon nedlelo layo;', 'XHO22'),
(17, 6, 69, 'neAyalon nedlelo layo, neGati-rimon nedlelo layo;', 'XHO22'),
(17, 6, 70, 'kwisiqingatha sesizwe sakwaManase; iAnere nedlelo lakhe, noBhileham nedlelo lakhe, esizweni esiseleyo soonyana bakaKehati.', 'XHO22'),
(17, 6, 71, 'Oonyana bakaGershom kwisiqingatha sesizwe sakwaManase banikwa iGolan, neBhashan namadlelo ayo, neAshtaroti namadlelo ayo;', 'XHO22'),
(17, 6, 72, 'kwisizwe sakwaIsakare; iKedeshe namadlelo ayo, neDabherati namadlelo ayo;', 'XHO22'),
(17, 6, 73, 'neRamoti nedlelo layo, neAnem nedlelo layo;', 'XHO22'),
(17, 6, 74, 'kwisizwe sakwa-Ashere; neMasale nedlelo layo, neAbhedon nedlelo layo;', 'XHO22'),
(17, 6, 75, 'neHukoki nedlelo layo, neRehobhi nedlelo layo;', 'XHO22'),
(17, 6, 76, 'kwisizwe sakwaNafetali; iKedeshe yaseGalili namadlelo ayo, neHamon namadlelo ayo, neKiriyatayim namadlelo ayo.', 'XHO22'),
(17, 6, 77, 'Koonyana bakaMerari, kwisizwe sakwaZebhulon, banikwa iRimon nedlelo layo, neTabhore nedlelo layo.', 'XHO22'),
(17, 6, 78, 'Ngaphesheya kweYordan iYeriko, engasempumalanga kweYordan, yanikwa isizwe sakwaRubhen, iBhete-tsere entlango, nedlelo layo, neYaha namadlelo ayo;', 'XHO22'),
(17, 6, 79, 'neKedemoti nedlelo layo, neMefati nedlelo layo;', 'XHO22'),
(17, 6, 80, 'kwisizwe sakwaGadi; iRamoti yaseGiliyadi namadlelo ayo, neMahanayim namadlelo ayo;', 'XHO22'),
(17, 6, 81, 'neHeshbhon nedlelo layo, neYazere nedlelo layo.', 'XHO22'),
(17, 7, 1, 'Oonyana bakaIsakare nguTola, noPuwa, noYashubhi, noShimron, bebane.', 'XHO22'),
(17, 7, 2, 'Oonyana bakaTola nguAsheri; no-Uzi, noRefaya, noYeriyeli, noYagemayi, noIbhesam, noSemuweli, iintloko zezindlu zooyise zakwaTola; babengamagorha akhaliphileyo kwizizukulwana zabo; inani lawo ngemihla kaDavide laba ngamashumi amabini anamabini amawaka, anamakhulu mathandathu.', 'XHO22'),
(17, 7, 3, 'Oonyana bakaUzi ngoonyana bakaIseraga; uMikayeli, no-Obhadiya, noYoweli, noIsiya, bebahlanu, iintloko zonke.', 'XHO22'),
(17, 7, 4, 'Babenabo ngokwemizalwane yabo, ngokwemizalwane yooyise, kwakukho amashumi amathathu anamathandathu amawaka, amadodana anamandla, ngokuba babenabafazi abaninzi, noonyana abaninzi.', 'XHO22'),
(17, 7, 5, 'Abazalwana babo kuzo zonke izizukulwana zikaIsakare, babengamashumi asibhozo anesixhenxe amawaka, ngokwemizalwane yabo yonke.', 'XHO22'),
(17, 7, 6, 'Oonyana bakaBhenjamin nguElohim; uBhela, noBhekere, noYedihayeli abathathu.', 'XHO22'),
(17, 7, 7, 'Oonyana bakaBhela nguShela; uEsbhon, noUzi, noUziyeli, noYerimoti, noIri, bahlanu; iintloko zezindlu zooyise, amagorha anobukroti; ngokwemilibo yabo yokuzalwa babengamashumi amabini anamabini amawaka, anamanci mathathu anesine.', 'XHO22'),
(17, 7, 8, 'Oonyana bakaBhege uSemira, noYowashe, noEliyezere, noEliyonayi, no-Omri, noYerimoti, noAbhiya, noAnatoti, noAlameti. Bonke ngoonyana bakaBhege.', 'XHO22'),
(17, 7, 9, 'Inani labo ngokweenzala zabo ngokweenzala zabo zeentloko zezindlu zooyise, amagorha anobukroti, babengamashumi amabini amawaka, anamakhulu mabini.', 'XHO22'),
(17, 7, 10, 'Oonyana bakaYedihayeli nguAsheri; Oonyana bakaBhilehan nguBhilehan; uYesu noBhenjamin noEhudi noKanan noSathana noTarshishe noAhishakare.', 'XHO22'),
(17, 7, 11, 'Bonke oonyana bakaYedihayeli, ngokweentloko zezindlu zooyise, amagorha anobukroti, baba lishumi elinesixhenxe lamawaka, anamakhulu mabini, aphuma umkhosiumkhosi.', 'XHO22'),
(17, 7, 12, 'NoSupim noHupim, oonyana bakaIre, noHushim, oonyana baka-Ahhere.', 'XHO22'),
(17, 7, 13, 'Oonyana bakaNafetali nguYagisiyeli noGuni noYezere noShalum oonyana bakaBhiliha.', 'XHO22'),
(17, 7, 14, 'Oonyana bakaManase nguAsriyeli awamzalayo; isithende sakhe saba ngumAram, sazala uMagire, uyise kaGiliyadi.', 'XHO22'),
(17, 7, 15, 'uMagire wazeka udade boHupim noSupim, udade wabo kwathiwa nguMaga, waba ngumkakhe; igama lowesibini belinguTselofehadi; uTselofehadi waye eneentombi.', 'XHO22'),
(17, 7, 16, 'uMaga, umkaMagire, wazala unyana, wathi igama lakhe nguPeres; igama lomninawa wakhe belinguSerese; Oonyana bakhe ngu-Ulam noRakem.', 'XHO22'),
(17, 7, 17, 'Yintoni oonyana beeUlama? Ngabo abo oonyana bakaGiliyadi, unyana kaMagire, unyana kaManase.', 'XHO22'),
(17, 7, 18, 'Udade wabo uHamolekete wazala uYesu, noAbhiyezere, noMahala.', 'XHO22'),
(17, 7, 19, 'Oonyana bakaShemida yayinguAhyan noShekem noLiki noAnjam.', 'XHO22'),
(17, 7, 20, 'Oonyana bakaEfrayim nguSutela, noBherede unyana wakhe, noTahati unyana wakhe, noEliyada unyana wakhe, noTahati unyana wakhe,', 'XHO22'),
(17, 7, 21, 'noZabhadi unyana wakhe, noSutela unyana wakhe, noEzere, noEladi, ababulala abantu baseGati ababezalelwe kwelo zwe, ngokuba babehle beza kuthimba imfuyo yabo.', 'XHO22'),
(17, 7, 22, 'uEfrayim uyise wabenzela isijwili imihla emininzi, beza kuye abazalwana bakhe, beza kumkhuza.', 'XHO22'),
(17, 7, 23, 'Wamngena umfazi wakhe; wakhawula, wazala unyana, wathi igama lakhe nguBheriya;', 'XHO22'),
(17, 7, 24, 'Intombi yakhe ibinguSera, yakha iBhete-horon yasezantsi neyasentla, neUsera.', 'XHO22'),
(17, 7, 25, 'Unyana wakhe yayinguRefa, noRetsefe, noTela unyana wakhe, noTahan unyana wakhe,', 'XHO22'),
(17, 7, 26, 'uLaedan unyana wakhe, uAmihudi unyana wakhe, uElishama unyana wakhe,', 'XHO22'),
(17, 7, 27, 'Hayi, unyana wakhe, uYoshuwa unyana wakhe.', 'XHO22'),
(17, 7, 28, 'Izinto zabo neendawo zabo zokuhlala zaba: iBheteli namadlelo ayo, neNaharan nentshona yeGezere namadlelo ayo; noShekem namadlelo ayo, kwesa eGaza namadlelo ayo;', 'XHO22'),
(17, 7, 29, 'Ngakwilizwe loonyana bakaManase yiBhete-shehan nemida yayo, iTahanaki nemida yayo, iMegido nemida yayo, iDore nemida yayo. Babehleli kule ndawo oonyana bakaYosefu, unyana kaSirayeli.', 'XHO22'),
(17, 7, 30, 'Oonyana baka-Ashere nguAshere; uImna, noYeshuwa, noIshuwa, noBheriya, noSeraki udade wabo.', 'XHO22'),
(17, 7, 31, 'Oonyana bakaBheriya nguHebhere noMalekiyeli, ozala uBhirsaviti.', 'XHO22'),
(17, 7, 32, 'uHebhere wazala uYafelete, noSomere, noHotam, noShuwa udade wabo.', 'XHO22'),
(17, 7, 33, 'Oonyana bakaYafete yipasika, noBhimhali, noAshwati. Ngabo abo oonyana bakaYafelete.', 'XHO22'),
(17, 7, 34, 'Oonyana bakaShamere nguAhi noRoga, uYehubha noAram.', 'XHO22'),
(17, 7, 35, 'Oonyana bomntakwabo yayinguHelem; iSofa kunye noJimna kunye noSeles noAmal.', 'XHO22'),
(17, 7, 36, 'Oonyana bakaSofi; uSuagi noHarnefer noSual noBeri noImra,', 'XHO22'),
(17, 7, 37, 'neBhete-tsere, noHode, noShama, noSilesa, noYitran, noBhera.', 'XHO22'),
(17, 7, 38, 'Oonyana bakaYetere nguYefune, noPispa, noAra.', 'XHO22'),
(17, 7, 39, 'Oonyana bakaUla nguAragi noHaniyeli noResiya.', 'XHO22'),
(17, 7, 40, 'Bonke bephela babengoonyana baka-Ashere, iintloko zezindlu zooyise, amagorha ahleliweyo, angamakroti, iintloko zezikhulu. Inani lesizwe sabaphuma umkhosi lalingamashumi amabini anamathandathu amawaka.', 'XHO22'),
(17, 8, 1, 'uBhenjamin wazala uBhela, izibulo lakhe, uAshbhele owesibini, noAhare owesithathu,', 'XHO22'),
(17, 8, 2, 'eyesine inyoka, neyesihlanu uRafa.', 'XHO22'),
(17, 8, 3, 'Oonyana bakaBhela nguAdare, noGera, noAbhihudi;', 'XHO22'),
(17, 8, 4, 'noAbhishuwa, noNahaman, noAhagi;', 'XHO22'),
(17, 8, 5, 'noGera, noSefufan, noHuram.', 'XHO22'),
(17, 8, 6, 'Ngabo aba oonyana bakaEhudi: zizo ezo iintloko zezindlu zooyise zabemi baseGebha, baya eManahati.', 'XHO22'),
(17, 8, 7, 'Yaba nguNahaman, noAkiya, noGera, owabafudusayo, wazala u-Usa noAkihudi.', 'XHO22'),
(17, 8, 8, 'uSharayim wazala abantwana ezweni lakwaMowabhi, ekubagxotheni kwakhe; Abafazi bakhe yayinguHushim noBhahara.', 'XHO22'),
(17, 8, 9, 'Umkakhe wazala uHodi, noYobhabhi, noSibhiya, noMesha, noMalekam;', 'XHO22'),
(17, 8, 10, 'noYehuse, noSakiya, noMirma. Ngabo abo oonyana bakhe, iintloko zezindlu zooyise.', 'XHO22'),
(17, 8, 11, 'uHushim wazala uAbhitubhi noElipahali.', 'XHO22'),
(17, 8, 12, 'Oonyana bakaElipahali nguAsheri; uHebhere, noMisam, noSkamed, owakha iOno, neLodi namadlelo ayo;', 'XHO22'),
(17, 8, 13, 'noBheriya noShema, ababeziintloko zeentsapho zabemi baseAyalon, abagxotha abemi baseGati.', 'XHO22'),
(17, 8, 14, 'NoAhiyo, noSasaki, noYeromoti;', 'XHO22'),
(17, 8, 15, 'noZebhadiya, noAradi, noAdere;', 'XHO22'),
(17, 8, 16, 'noMikayeli, noIshpa, noYowa, oonyana bakaBheriya;', 'XHO22'),
(17, 8, 17, 'noZebhadiya, noMeshulam, noHizeki, noHebhere;', 'XHO22'),
(17, 8, 18, 'noItsemerayi, noItseliya, noYobhabhi, oonyana bakaElipahali;', 'XHO22'),
(17, 8, 19, 'noYakim, noSigri, noSabhedi;', 'XHO22'),
(17, 8, 20, 'noElihenayi, noSiletayi, noEliyeli;', 'XHO22'),
(17, 8, 21, 'noAdaya, noBheraya, noShimerati, oonyana bakaShimehi;', 'XHO22'),
(17, 8, 22, 'noIspan, noHebhere, noEliyeli;', 'XHO22'),
(17, 8, 23, 'neAbhedon, noSigri, noHanan;', 'XHO22'),
(17, 8, 24, 'noHananiya, noElam, noAntotiya;', 'XHO22'),
(17, 8, 25, 'noYefediya noPenuweli, oonyana bakaSasaki;', 'XHO22'),
(17, 8, 26, 'noSamserayi, noZekariya, noAtaliya;', 'XHO22'),
(17, 8, 27, 'noYaresiya, noEliya, noSigri, oonyana bakaYeroham.', 'XHO22'),
(17, 8, 28, 'Zizo ezo iintloko zooyise ngokwemizalwane yabo, ziziintloko. Bahlala eYerusalem.', 'XHO22'),
(17, 8, 29, 'Uyise kaGibheyon wayehlala eGibheyon; ogama lomkakhe belinguMaaka.', 'XHO22'),
(17, 8, 30, 'Unyana wakhe wamazibulo nguAbhedon, noTsure, noKishe, noBhahali, noNadabhi;', 'XHO22'),
(17, 8, 31, 'neGedore, neAjo, neSager.', 'XHO22'),
(17, 8, 32, 'uMikelo wazala uSimon. Nabo bahlala nabazalwana babo eYerusalem malungana nabo.', 'XHO22'),
(17, 8, 33, 'uNere wazala uKishe, noKishe, uyise kaSawule; uSawule wazala uYonatan, noMalekishuwa, noAbhinadabhi, noEshbhahali.', 'XHO22'),
(17, 8, 34, 'Unyana kaYonatan nguMeribhahali; uMeribhahali wazala uMika.', 'XHO22'),
(17, 8, 35, 'Oonyana bakaMika nguPiton, noMeleki, noTariya, noAgashe.', 'XHO22'),
(17, 8, 36, 'uAhazi wazala uYowada; uYoda wazala uAlemete, noAsmavete, noZimri; uZimri wazala uMoses;', 'XHO22'),
(17, 8, 37, 'uMoses wazala uBhineha; unyana wakhe nguRafa, unyana wakhe uEleyasa, unyana wakhe uAseli.', 'XHO22'),
(17, 8, 38, 'uAsheli ubenoonyana abathandathu; amagama abo ngooAshrikam, noBhokeru, noIshmayeli, noShehariya, no-Obhadiya, noHanan. Bonke ke ngoonyana baka-Asheli.', 'XHO22'),
(17, 8, 39, 'Oonyana bakaHezekile umninawa wakhe ngu-Ulam, izibulo lakhe, noYehuse owesibini, noElifelete.', 'XHO22'),
(17, 8, 40, 'Oonyana baka-Ulama babengamagorha, abazingela ngesaphetha, noonyana abaninzi, noonyana abalikhulu elinamashumi amahlanu. Bonke babephuma koonyana bakaBhenjamin.', 'XHO22'),
(17, 9, 1, 'Bonke ke oonyana bakaSirayeli babalwa ngokwemizalwane; nabo babhaliwe encwadini yookumkani bakwaSirayeli nabakwaYuda abathinjelwa eBhabhiloni ngenxa yokunxaxha kwabo.', 'XHO22'),
(17, 9, 2, 'Abokuqala, ababehlala elifeni labo, ezixekweni zabo, yayingamaSirayeli, nababingeleli, nabaLevi, nabakhonzi betempile.', 'XHO22'),
(17, 9, 3, 'Kwahlala ke inxenye yoonyana bakaYuda, nabanye boonyana bakaBhenjamin, nabanye koonyana bakaEfrayim, nabakwaManase, eYerusalem:', 'XHO22'),
(17, 9, 4, 'ngu-Utayi, unyana ka-Amihudi, unyana kaOmri, unyana kaImri, unyana kaBhani, koonyana bakaPeretse, unyana kaYuda.', 'XHO22'),
(17, 9, 5, 'NabaseSilone; Owamazibulo nguAsaya noonyana bakhe.', 'XHO22'),
(17, 9, 6, 'Koonyana bakaSeraki; uYehuweli nabazalwana bakhe bangamakhulu amathandathu, anamanci asibhozo, bodwa.', 'XHO22'),
(17, 9, 7, 'Koonyana bakaBhenjamin; nguSalu, unyana kaMeshulam, unyana kaHodaviya, unyana kaHasenuage,', 'XHO22'),
(17, 9, 8, 'noIbheneya, unyana kaYeroham, noEla, unyana kaUzi, unyana kaMigri, noMeshulam, unyana kaShefatiya, unyana kaRehuweli, unyana kaIbheniya;', 'XHO22'),
(17, 9, 9, 'Abazalwana babo ngokweenzala zabo babengamakhulu asithoba anamanci mahlanu anabathandathu. Onke loo madoda abeziintloko zezindlu zooyise ezindlwini zooyise.', 'XHO22'),
(17, 9, 10, 'Nakubabingeleli; noYedaya, noYehoyaribhi, noYagin;', 'XHO22'),
(17, 9, 11, 'noAzariya unyana kaHilekiya, unyana kaMeshulam, unyana kaTsadoki, unyana kaMerayoti, unyana ka-Akitubhi, umphathi wendlu yoThixo oyinyaniso;', 'XHO22'),
(17, 9, 12, 'noAdaya, unyana kaYeroham, unyana kaPashkure, unyana kaMalekiya, noMahasayi, unyana ka-Adiyeli, unyana kaYageshera, unyana kaMeshulam, unyana kaMesilemiti, unyana kaImere;', 'XHO22'),
(17, 9, 13, 'nabazalwana babo, iintloko zezindlu zooyise, iwaka elinamakhulu asixhenxe, anamanci mathandathu; maninzi amadoda anobukroti emsebenzini wenkonzo yendlu kaThixo.', 'XHO22'),
(17, 9, 14, 'NakubaLevi; nguShemaya, unyana kaHashubhi, unyana ka-Ashrikam, unyana kaHashabhiya, koonyana bakaMerari;', 'XHO22'),
(17, 9, 15, 'noBhakare, noHerese, noGalali, noMataniya, unyana kaMika, unyana kaSigri, unyana ka-Asafu;', 'XHO22'),
(17, 9, 16, 'no-Obhadiya, unyana kaShemaya, unyana kaGalali, unyana kaYedutun, noBheriya, unyana ka-Asa, unyana kaElikana, obehleli emizini yamaNetofi.', 'XHO22'),
(17, 9, 17, 'Abamasango: ibinguShalum, noAkubhe, noTalemon, noAhiman, nabazalwana babo; uShalum wayeyintloko;', 'XHO22'),
(17, 9, 18, 'othe unangoku elinde esangweni lokumkani, elisingise empumalanga; babengabamasango ngokwamaqela oonyana bakaLevi.', 'XHO22'),
(17, 9, 19, 'uShalum, unyana kaKora, unyana ka-Abhiyasafu, unyana kaKora, nabazalwana bakhe bendlu kayise, amaKora, bephethe inkonzo yenkonzo, nabagcini bamasango entente yokuhlangana nooyise ababephethe inkonzo abo babephethe amatyala kaYehova babengabagcini-masango.', 'XHO22'),
(17, 9, 20, 'uPinehasi, unyana kaElazare, waye eyinganga yabo ngenxa engaphambili, uYehova wayenaye.', 'XHO22'),
(17, 9, 21, 'uZekariya unyana kaMeshelemiya wayengowesango ententeni yokuhlangana.', 'XHO22'),
(17, 9, 22, 'Bonke oonyana abanyulwa ukuba babe ngabagcini-masango babengamakhulu amabini, anashumi-nye linababini. Babalwa ngokwemizalwane yabo emizini yabo, abehleli beyibeka imisebenzi yabo uDavide nemboni uSamuweli.', 'XHO22'),
(17, 9, 23, 'Bona noonyana babo babewagcina amasango endlu kaYehova, eyindlu yomnquba, ngokwezigidimi.', 'XHO22'),
(17, 9, 24, 'Abamasango babekho emacaleni omane, ngasempumalanga, ngasentshonalanga, ngasentla, nangasezantsi.', 'XHO22'),
(17, 9, 25, 'Abazalwana babo emizaneni yabo babengabokuza kubo amaxesha ngamaxesha emva kweentsuku ezisixhenxe;', 'XHO22'),
(17, 9, 26, 'Kuba aba baLevi, abagcini-masango abane abaziintloko, babekwizikhundla zabo, bephethe amagumbi nobuncwane bendlu kaTHIXO oyinyaniso.', 'XHO22'),
(17, 9, 27, 'Bachitha ubusuku bejikeleza indlu yoThixo oyinyaniso, ngenxa yokuba lo myalelo wawuphezu kwabo, yaye wawuvulelwa ntsasa nganye.', 'XHO22'),
(17, 9, 28, 'Inxenye kubo ibiphethe imisebenzi yenkonzo, ukuze bayingenise, bayikhuphe ngokomzekeliso.', 'XHO22'),
(17, 9, 29, 'Kwamiselwa abathile kubo ukuba banyamekele iimpahla neempahla zonke zengcwele, nomgubo, newayini, neoli, neziqhumiso, neziqholo.', 'XHO22'),
(17, 9, 30, 'Koonyana bababingeleli bekukho ababesenza ubulawu.', 'XHO22'),
(17, 9, 31, 'uMatitja, omnye wabaLevi, izibulo likaShalume-Mokore, wayephethe umsebenzi wokwenziwa kweepani.', 'XHO22'),
(17, 9, 32, 'Koonyana bakaHeti, abazalwana babo, ababephethe izonka ezikroziswayo, ukuzilungisa iisabatha ngeesabatha.', 'XHO22'),
(17, 9, 33, 'Zizo ezo iimvumi, iintloko zezindlu zooyise zabaLevi, ezibe zihleli emagumbini okudlela zingakhululekile, ngokuba bezisebenza imini nobusuku.', 'XHO22'),
(17, 9, 34, 'Zizo ezo iintloko zezindlu zooyise zabaLevi, iintloko zezindlu zooyise bahlala eYerusalem.', 'XHO22'),
(17, 9, 35, 'eGibheyon kwahlala uyise kaGibheyon, uYehiyeli, ogama lomkakhe belinguMahega.', 'XHO22'),
(17, 9, 36, 'Unyana wakhe wamazibulo nguAbhedon, noTsure, noKishe, noBhahali, noNere, noNadabhi;', 'XHO22'),
(17, 9, 37, 'neGedore, neAyo, noZekariya, noMikeloti.', 'XHO22'),
(17, 9, 38, 'uMikelo wazala uShimeham. Nabo bahlala nabazalwana babo eYerusalem, ngenxa yabazalwana babo.', 'XHO22'),
(17, 9, 39, 'uNere ke wayenguyise kaKishe; uKishe wazala uSawule; uSawule wazala uYonatan, noMalekishuwa, noAbhinadabhi, noEshbhahali.', 'XHO22'),
(17, 9, 40, 'Unyana kaYonatan nguMeribhahali, uMeribhahali nguyise kaMika.', 'XHO22'),
(17, 9, 41, 'Oonyana bakaMika yayinguPiton noMeleki noTariya noAgashe.', 'XHO22'),
(17, 9, 42, 'uAhazi wazala uYara; uYara wazala uAlemete, noAsmawete, noZimri; uZimri wazala uMoses;', 'XHO22'),
(17, 9, 43, 'uMoses wazala uBhineha; uRefaya unyana wakhe, uEleyasa unyana wakhe, uAsheli unyana wakhe.', 'XHO22'),
(17, 9, 44, 'uAsheli ubenoonyana abathandathu; Ngabo abo oonyana baka-Asheli.', 'XHO22'),
(17, 10, 1, 'Kwasilwa amaFilisti namaSirayeli; yaye amadoda akwaSirayeli asaba phambi kwamaFilisti aza awela ebulewe ezintabeni zaseGilibhowa.', 'XHO22'),
(17, 10, 2, 'AmaFilisti athana mbende noSawule noonyana bakhe; yaye amaFilisti ambulala uYonatan noAbhinadabhi noMalekishuwa, oonyana bakaSawule.', 'XHO22'),
(17, 10, 3, 'Kwaba nzima ukulwa kuSawule; bambetha abatoli, wangxwelerhwa ngabatoli.', 'XHO22'),
(17, 10, 4, 'Wathi uSawule kumfana lowo, Rhola ikrele lakho, undibulale ngalo; mhlawumbi abo bantu bangafika bandihleke. Akavuma umphathi weentonga zakhe; ngokuba ebesoyika kunene. uSawule walithabatha ikrele, waziwisa phezu kwalo.', 'XHO22'),
(17, 10, 5, 'Uthe umphathi weentonga zakhe, akubona ukuba ufile uSawule, waziwisa phezu kwekrele lakhe, wafa.', 'XHO22'),
(17, 10, 6, 'Wafa ke uSawule, noonyana bakhe abathathu, nendlu yakhe yonke;', 'XHO22'),
(17, 10, 7, 'Athi onke amadoda akwaSirayeli abesentilini yoMnayile, abona ukuba asabile, ukuba ufile uSawule noonyana bakhe, ayishiya imizi yawo, asaba; namaFilisti aya kwakha kuyo.', 'XHO22'),
(17, 10, 8, 'Kwathi ngengomso amaFilisti eza kubhunyula ababuleweyo, amfumana uSawule noonyana bakhe bewile ezintabeni zeGilibhowa.', 'XHO22'),
(17, 10, 9, 'Emva kokuba amhlube, athabatha intloko yakhe nezikrweqe zakhe aza athumela kwilizwe lamaFilisti ngeenxa zonke ukuba axele imifanekiso yawo eqingqiweyo nasebantwini ezo ndaba.', 'XHO22'),
(17, 10, 10, 'Azibeka iintonga zakhe endlwini yothixo wawo, ukanti intloko yakhe ayibeka endlwini kaDagon.', 'XHO22'),
(17, 10, 11, 'Beva bonke abaseYabheshe eGiliyadi konke abekwenzile amaFilisti kuSawule.', 'XHO22'),
(17, 10, 12, 'Agqiba ke onke amadoda anobukroti, awuthabatha umzimba kaSawule, nezidumbu zoonyana bakhe, azizisa eYabheshe, awangcwabela amathambo abo phantsi komthi wetalanti eYabheshe, azila ukudla iintsuku zasixhenxe.', 'XHO22'),
(17, 10, 13, 'Wafa ke uSawule ngenxa yokuvukela kwakhe uYehova, enxamnye nelizwi likaYehova angaligcinanga, nangenxa yokuba wacela icebiso kumntu onomoya owaziwayo owathi wambuza;', 'XHO22'),
(17, 10, 14, 'Akamcela uYehova; wambulala ke, wabunika uDavide, unyana kaYese, ubukumkani.', 'XHO22'),
(17, 11, 1, 'Ahlanganisana onke amaSirayeli kuDavide eHebron, athi, Uyabona, singamathambo akho nenyama yakho.', 'XHO22'),
(17, 11, 2, 'Kwakudala, oko uSawule ebengukumkani, ibinguwe ububakhupha oonyana bakaSirayeli, ubangenise;', 'XHO22'),
(17, 11, 3, 'Aya ke onke amadoda amakhulu akwaSirayeli kukumkani eHebron; Wenza umnqophiso nawo uDavide eHebron phambi koYehova; yaye bamthambisa uDavide ukuba abe ngukumkani wakwaSirayeli ngokwelizwi likaYehova ngenkonzo kaSamuweli.', 'XHO22'),
(17, 11, 4, 'uDavide namaSirayeli onke beza eYerusalem: yiYebhusi ke leyo; apho abehlala khona amaYebhusi ezweni abekuwo.', 'XHO22'),
(17, 11, 5, 'Bathi abemi baseYebhusi kuDavide, Akuyi kungena apha. Noko ke, uDavide wayithimba inqaba yaseZiyon, esisixeko sikaDavide.', 'XHO22'),
(17, 11, 6, 'Wathi uDavide, Owathe wawaxabela amaYebhusi kwasentloko, woba yintloko nomlawuli; Kwenyuka kuqala uYowabhi, unyana kaTseruya, waba yinganga.', 'XHO22'),
(17, 11, 7, 'uDavide wahlala emboniselweni; ke ngoko bathi, sisixeko sikaDavide.', 'XHO22'),
(17, 11, 8, 'Wawakha umzi ngeenxa zonke, ethabathela eMilo ngeenxa zonke;', 'XHO22'),
(17, 11, 9, 'uDavide waqhubeka ekhula, kuba uYehova wemikhosi wayenaye.', 'XHO22'),
(17, 11, 10, 'Zizo ezi iintloko zamagorha awayekunye nawo uDavide, awamomelezayo ebukumkanini bakhe noSirayeli wonke ukuba amenze ukumkani, ngokwelizwi likaYehova ngokuphathelele uSirayeli.', 'XHO22'),
(17, 11, 11, 'Lilo eli inani lamagorha kaDavide; noYasobham, noHagmon, intloko yabathetheli; wajula umkhonto wakhe kumakhulu mathathu lawo abewaxabele kwangoko.', 'XHO22'),
(17, 11, 12, 'Ngasemva kwakhe nguEleyazare unyana kaDodo, uMakhi, omnye wamagorha amathathu.', 'XHO22'),
(17, 11, 13, 'Yena wayenoDavide ePas-damim, yaye amaFilisti ayehlanganisene khona ukuze alwe, apho intsimi yayizele yirhasi; basaba abantu phambi kwamaFilisti.', 'XHO22'),
(17, 11, 14, 'Bazifaka esazulwini sengxowa, bayinikela, bawabulala amaFilisti; uYehova wabahlangula ke usindiso olukhulu.', 'XHO22'),
(17, 11, 15, 'Kwehla isithathu sabaziintloko emashumini amathathu, baya eweni kuDavide, emqolombeni waseAdulam; imikhosi yamaFilisti yayimise iintente entilini yamaRafa.', 'XHO22'),
(17, 11, 16, 'uDavide yena esenqabeni, bona abalindi bamaFilisti beseBhetelehem.', 'XHO22'),
(17, 11, 17, 'Wanqwena uDavide, wathi, Akwaba bendithe ndasela amanzi equla laseBhetelehem elisesangweni!', 'XHO22'),
(17, 11, 18, 'Atyhoboza amadoda amathathu empini yamaFilisti, akha amanzi equleni laseBhetelehem elisesangweni, awathabatha awazisa kuDavide; akavuma ukuwasela uDavide; wawaphalazela phambi koYehova;', 'XHO22'),
(17, 11, 19, 'Wathi, Akayi kuyenza le nto uThixo wam; Ndiya kulisela na igazi lala madoda abubeke esichengeni ubomi bawo? ngokuba babazisile ngokubeka ubomi babo esichengeni. Yiyo loo nto wayengafuni ukuyisela. Ezi zinto zintathu bazenza ngamandla amakhulu.', 'XHO22'),
(17, 11, 20, 'uAbhishayi, umninawa kaYowabhi, waye eyintloko yabathathu.', 'XHO22'),
(17, 11, 21, 'Kubo bathathu ubebekeke ngaphezu kwabo bobabini; ngokuba ebengumlawuli wabo; kodwa akafikanga kwabathathu bokuqala.', 'XHO22'),
(17, 11, 22, 'NguBhenaya, unyana kaYehoyada, unyana wendoda enobukroti yaseKabhetseli, ewenza imisebenzi emininzi; wabulala iingonyama ezimbini zakwaMowabhi; wabuya wehla, wayibulala ingonyama emhadini, kungekho khephu.', 'XHO22'),
(17, 11, 23, 'Wabulala indoda engumYiputa, indoda enexesha elide, ekubhite ezintlanu; esandleni somYiputa lowo ephethe intshuntshe enjengentonga yomluki; Wehla ke waya kuye ephethe intonga, wayihlutha intshuntshe esandleni somYiputa lowo, wambulala ngentshuntshe yakhe.', 'XHO22'),
(17, 11, 24, 'Wenjenjalo uBhenaya unyana kaYehoyada; waba nodumo phakathi kwamagorha amathathu.', 'XHO22'),
(17, 11, 25, 'Ubebekekile yena phakathi kwamashumi amathathu lawo, kodwa akafikanga kwabathathu bokuqala; uDavide wammisa phezu kwabalindi bakhe.', 'XHO22'),
(17, 11, 26, 'Amagorha omkhosi ibinguAsaheli, umninawa kaYowabhi, noElihanan, unyana kaDodo, waseBhetelehem;', 'XHO22'),
(17, 11, 27, 'neSamati yaseHarhore, neGileya yasePelone,', 'XHO22'),
(17, 11, 28, 'noIra unyana kaIkeki waseTekowa, noAbhiyezere waseMowantoti;', 'XHO22'),
(17, 11, 29, 'noSibhegayi umHusha, noIlayi umAhowa;', 'XHO22'),
(17, 11, 30, 'noMaharayi waseNetofa, noHeledi unyana kaBhahana waseNetofa;', 'XHO22'),
(17, 11, 31, 'nguItayi unyana kaRibhayi, waseGibheha koonyana bakaBhenjamin, noBhenaya wasePiraton;', 'XHO22'),
(17, 11, 32, 'Entlanjeni yaseGahashe, iAbhiyeli, neArbhati,', 'XHO22'),
(17, 11, 33, 'neAsmavete yamaRumi, noElijabha waseSaalbhon;', 'XHO22'),
(17, 11, 34, 'Oonyana bakaHashem Mogizone nguYonatan unyana kaSage waseMoharare;', 'XHO22'),
(17, 11, 35, 'noAhiya, unyana kaSakare, umMoharari, noElifele, unyana kaUre;', 'XHO22'),
(17, 11, 36, 'noHefere waseMegereta, noAhiya wasePelone;', 'XHO22'),
(17, 11, 37, 'noHetsero waseKarmele, noNaharayi, unyana kaEshbhayi;', 'XHO22'),
(17, 11, 38, 'noYoweli umzalwana kaNatan, noMibhare unyana kaHagiri;', 'XHO22'),
(17, 11, 39, 'noTseleki umAmon, noNaharayi waseBheroti, umkhonzi kaYowabhi, unyana kaTseruya;', 'XHO22'),
(17, 11, 40, 'noIra waseMoitere, noGarebhe waseMoitere,', 'XHO22'),
(17, 11, 41, 'no-Uriya umHeti, noSabhadi unyana ka-Alayi;', 'XHO22'),
(17, 11, 42, 'uAdina unyana kaSisa, umRubhene, inkulu yamaRubhen, enamashumi amathathu;', 'XHO22'),
(17, 11, 43, 'uHanan unyana kaMahagi, noYehoshafati waseMomitna;', 'XHO22'),
(17, 11, 44, 'u-Uziya waseAsterhati, noShama, noYehiyeli, oonyana bakaHotan waseArore;', 'XHO22'),
(17, 11, 45, 'noYedihayeli unyana kaZimri, noYowa umninawa kaMotishe;', 'XHO22'),
(17, 11, 46, 'uEliyeli umMowabhi, noYeribhayi, noYosaviya, oonyana bakaElinaham, noIma umMowabhikazi;', 'XHO22'),
(17, 11, 47, 'nguEliyeli, no-Obhedi, noYasiyeli, baseMesobha.', 'XHO22'),
(17, 12, 1, 'Ngabo aba, abeza kuDavide emzini waseShikelage, esamnqanda ngenxa kaSawule, unyana kaKishe; baba phakathi kwamagorha, abancedi emfazweni.', 'XHO22'),
(17, 12, 2, 'Babexhobe ngezaphetha yaye bedubula ngesandla sasekunene nangesandla sasekhohlo, begibisela ngamatye, batole neentolo ngesaphetha, ababengabazalwana bakaSawule wakwaBhenjamin.', 'XHO22'),
(17, 12, 3, 'Intloko ibinguAhiyezere noYowashe, oonyana bakaShemaha waseGibheha; noYesiyeli noPelete, oonyana baka-Ashmaveti; noBheraga noYehu waseMohanantoti;', 'XHO22'),
(17, 12, 4, 'noIshmaya waseGibheyon, igorha phakathi kwamashumi amathathu anamanci mathathu; noYeremiya, noYagasiyeli, noYohanan, noYozabhadi waseGedera;', 'XHO22'),
(17, 12, 5, 'uEluzayi, noYerimoti, noBhehaliya, noShemariya, noShefatiya Moharufi;', 'XHO22'),
(17, 12, 6, 'noElikana, noIsiya, noAzareli, noYoshere, noYasobham, waseKorge;', 'XHO22'),
(17, 12, 7, 'noYoweli noZebhadiya, oonyana bakaYeroham waseGedore.', 'XHO22'),
(17, 12, 8, 'Ke bona oonyana bakaGadi, bamnyulela uDavide kummandla weentaba, ukusa entlango, amadoda anamandla namadoda emfazwe alungele idabi, awayephethe ikhaka nekhaka, abuso bawo babunjengobuso beengonyama nobuso obunjengobeengonyama nendlu kaGadi umendu njengexhama ezintabeni;', 'XHO22'),
(17, 12, 9, 'uEzere inkulu, noObhadiya owesibini, noEliyabhi owesithathu,', 'XHO22'),
(17, 12, 10, 'owesine nguMishmana, owesihlanu nguYeremiya;', 'XHO22'),
(17, 12, 11, 'owesithandathu nguAtayi, owesixhenxe nguEliyeli;', 'XHO22'),
(17, 12, 12, 'owesibhozo nguYohanan, owesithoba nguElizabhadi;', 'XHO22'),
(17, 12, 13, 'oweshumi nguYeremiya, oweshumi elinanye eMagabhanayi.', 'XHO22'),
(17, 12, 14, 'Zizo ezo iintloko zamaqela oonyana bakaGadi; omncinane ubelingana nekhulu, omkhulu elingana newaka.', 'XHO22'),
(17, 12, 15, 'Ngabo abo bayiwelayo iYordan ngenyanga yokuqala, izele, ihamba phezu kweendonga zayo; yaye asaba kuyo yonke imilambo ngasempuma nasentshona.', 'XHO22'),
(17, 12, 16, 'Abanye boonyana bakaBhenjamin bakaYuda beza entabeni kunye noDavide.', 'XHO22'),
(17, 12, 17, 'Waphuma uDavide, waya kubakhawulela, wathi kubo, Ukuba niza kum ninoxolo, ukuba nindincede, intliziyo yam yonamathela kuni; yaye ukuba uthe weza kundihlangula ezintshabeni zam, kungekho siphoso esandleni sam, uTHIXO wookhokho bethu, yaye uya kukhalimela.', 'XHO22'),
(17, 12, 18, 'Umoya wamfikela uAmasayi, umthetheli wabathetheli, wathi, Singakuwe, Davide, singakuwe, nyana kaYese; uxolo, uxolo malube nani, uxolo malube nani nabancedi bakho; ngokuba uThixo wakho uyakunceda. Emva koko uDavide wabamkela waza wabenza iinkokeli zelo qela.', 'XHO22'),
(17, 12, 19, 'Abanye bakuManase bawela kuDavide, ekuyeni kwakhe namaFilisti ukuba kuliwe noSawule; kodwa ababancedanga; ngokuba abathetheli bamaFilisti babemcebise ukuba ahambe, besithi, Uya kumbeka esichengeni ukumkani wakhe uSawule yeentloko zethu.', 'XHO22'),
(17, 12, 20, 'Kwathi, ekuyeni kwakhe eTsikelage, bamxabela ke uAdena, noYozabhadi, noYedihayeli, noMikayeli, noYozabhadi, noElihu, noSiletayi, amawakawaka abathetheli bakwaManase.', 'XHO22'),
(17, 12, 21, 'Bona bancedisana noDavide ekulweni neqela lamadoda okulwa, ngokuba onke ayengamagorha anobukroti nabathetheli bomkhosi.', 'XHO22'),
(17, 12, 22, 'Kuba ngelo xesha babesiza yonke imihla kuDavide ukumnceda, yada yaba ngumkhosi omkhulu njengomkhosi woThixo oyinyaniso.', 'XHO22'),
(17, 12, 23, 'Ngawo la amanani amaqela axhobele imfazwe, eza kuDavide eHebron, ukuba bubuyiselwe kuye ubukumkani bukaSawule, ngokwelizwi likaYehova.', 'XHO22'),
(17, 12, 24, 'Oonyana bakaYuda bephethe ikhaka nentshuntshe bangamawaka amathandathu, anamakhulu asithoba, anamabini, axhobe ukulwa.', 'XHO22'),
(17, 12, 25, 'Koonyana bakaSimon, amagorha emfazwe ibingamakhulu asixhenxe amawaka.', 'XHO22'),
(17, 12, 26, 'Koonyana bakaLevi ngamawaka amane, anamakhulu mathandathu;', 'XHO22'),
(17, 12, 27, 'Ke uYehoyada waye eyinganga yoonyana baka-Aron, enamawaka amathathu anamakhulu asixhenxe;', 'XHO22'),
(17, 12, 28, 'nomfana, uTsadoki, umkroti, nabathetheli abamashumi mabini anababini endlwini kayise.', 'XHO22'),
(17, 12, 29, 'Koonyana bakaBhenjamin, yintsapho kaSawule, babengamawaka amathathu; ngokuba kwada kwaba ngoku uninzi lwabo lwalusayigcinile indlu kaSawule.', 'XHO22'),
(17, 12, 30, 'Koonyana bakaEfrayim, amadoda anobukroti angamashumi amabini amawaka, anamanci asibhozo anamabini, anegama ezindlwini zooyise.', 'XHO22'),
(17, 12, 31, 'Kwisiqingatha sesizwe sakwaManase lishumi elinesibhozo lamawaka, ababalulwa ngamagama ukuba bamisele uDavide ukumkani.', 'XHO22'),
(17, 12, 32, 'Koonyana bakaIsakare, abawaziyo abawaqondayo amaxesha, ukuba bayakwazi abaya kukwenza oonyana bakaSirayeli; iintloko zabo ibingamakhulu amabini; yaye bonke abazalwana babo babelawulwa ngabo.', 'XHO22'),
(17, 12, 33, 'KwaZebhulon abo balwayo, abanolwazi lwemfazwe nazo zonke izixhobo zemfazwe, amashumi amahlanu amawaka axhobe amajoni; babengaqhathana.', 'XHO22'),
(17, 12, 34, 'KwaNafetali ngabathetheli bewaka, namashumi amathathu anesixhenxe amawaka, aphatha ikhaka neentshuntshe.', 'XHO22'),
(17, 12, 35, 'Kwisizwe sakwaDan abaphuma umkhosi babengamashumi amabini anesibhozo amawaka, anamakhulu mathandathu.', 'XHO22'),
(17, 12, 36, 'Kwisizwe sakwa-Ashere, abaphuma umkhosi, ababengabathetheli bemfazwe, ngamashumi omane amawaka.', 'XHO22'),
(17, 12, 37, 'Ngaphesheya kweYordan, koonyana bakaRubhen namaGadi kwisiqingatha sesizwe sakwaManase, bathabatha izixhobo zalo lonke uhlobo lwemfazwe, ikhulu elinamashumi amabini amawaka.', 'XHO22'),
(17, 12, 38, 'Onke la magorha alungelelanisa umkhosi eza eHebron enentliziyo epheleleyo ukuze amisele uDavide ukumkani wakwaSirayeli wonke; avuma onke amaSirayeli ukumenza ukumkani uDavide.', 'XHO22'),
(17, 12, 39, 'Ahlala khona noDavide iintsuku zantathu, esidla esela; ngokuba abazalwana bawo babewalungiselele.', 'XHO22'),
(17, 12, 40, 'Ngaphezu koko, abo babesondele kubo, ukuya kufikelela kwaIsakare nakwaZebhulon nakwaNafetali bezisa isonka ngeedonki neenkamela neemeyile neenkomo nenyama, umgubo, amaqebengwana amakhiwane nezirasentyisi ezininzi newayini neoli neenkomo nemfuyo ngokuba kwabakho uvuyo kwaSirayeli.', 'XHO22'),
(17, 13, 1, 'uDavide wathetha kubathetheli bamawaka nabamakhulu, nakubathetheli bonke.', 'XHO22'),
(17, 13, 2, 'Wathi uDavide kwibandla lonke lakwaSirayeli, Ukuba kulungile emehlweni enu, ukuba kuthe kwalunga emehlweni enu, kuYehova uThixo wethu, masithumele kubazalwana bethu abaseleyo ezweni lonke lakwaSirayeli, nakubabingeleli kunye nabo nabaLevi emizini yabo nasemadlelweni abo, basihlanganisele ndawonye.', 'XHO22'),
(17, 13, 3, 'Masiyibuyisele kuthi ityeya yoThixo wethu; ngokuba asiyicelanga yona ngemihla kaSawule;', 'XHO22'),
(17, 13, 4, 'Lathi lonke ibandla makwenziwe, ngokuba loo nto ibilungile emehlweni abantu bonke.', 'XHO22'),
(17, 13, 5, 'Wawahlanganisa ke uDavide onke amaSirayeli, wathabathela eShihore ezweni laseYiputa, wada wesa ekungeneni eHamati, ukuze ayithabathe ityeya kaThixo eKiriyati-yarim.', 'XHO22'),
(17, 13, 6, 'Wenyuka ke uDavide, waya eBhahali: yiKiriyati-yeharim ezweni lakwaYuda, enamaSirayeli onke, ukuba ayithabathe khona ityeya kaThixo uYehova ohleli phezu kweekerubhi, ebizwa ngalo igama lakhe.', 'XHO22'),
(17, 13, 7, 'Bayikhwelisa ngenqwelo entsha ityeya kaThixo oyinyaniso endlwini ka-Abhinadabhi, yaye u-Uza noAjo babeyiqhuba inqwelo.', 'XHO22'),
(17, 13, 8, 'uDavide nawo onke amaSirayeli babedlala phambi koThixo wabo ngamandla abo onke nangeengoma ngeehadi ngeehadi namagubu namacangci namaxilongo.', 'XHO22'),
(17, 13, 9, 'Bakufika esandeni sikaKidon, u-Uza wasolula isandla sakhe ukuba ayibambe ityeya; ngokuba iinkomo bezikhubeka.', 'XHO22'),
(17, 13, 10, 'Wavutha umsindo kaYehova kuUsa, wambulala, ngokuba esifake isandla etyeyeni; wafela khona phambi koThixo.', 'XHO22'),
(17, 13, 11, 'uDavide wavutha ngumsindo ngenxa enokuba uYehova ebemisele u-Uza; ke ngoko, loo ndawo kwathiwa yiPerestsa kude kube namhla.', 'XHO22'),
(17, 13, 12, 'uDavide wamoyika uThixo ngaloo mini, wathi, Ndothini na ukuyizisa endlwini yam ityeya kaThixo?', 'XHO22'),
(17, 13, 13, 'uDavide akayisa emzini wakhe ityeya emzini kaDavide; wayisa endlwini kaObhedi-edom waseGati.', 'XHO22'),
(17, 13, 14, 'Yahlala ityeya kaThixo emzini kaObhedi-edom, iinyanga zantathu. uYehova wayisikelela indlu kaObhedi-edom, nako konke awayenako.', 'XHO22'),
(17, 14, 1, 'Ke kaloku uHiram, ukumkani waseTire, wathumela abathunywa kuDavide, nemisedare, neengcibi zamatye, neengcibi zemithi, ukumakhela indlu.', 'XHO22'),
(17, 14, 2, 'uDavide waqonda ukuba uYehova ummisele ukuba abe ngukumkani wakwaSirayeli, ngenxa yokuba ubukumkani bakhe baphakanyiselwa ngenxa yabantu bakhe amaSirayeli.', 'XHO22'),
(17, 14, 3, 'uDavide waphinda wazeka abanye abafazi eYerusalem, waza wazala abanye oonyana neentombi.', 'XHO22'),
(17, 14, 4, 'Ngawo lawo amagama oonyana bakhe, awabazalela eYerusalem; uSamuwa noShobhabhe, uNatan noSolomon,', 'XHO22'),
(17, 14, 5, 'noIbhehare, noElishuwa, noElipaleti;', 'XHO22'),
(17, 14, 6, 'noNoga, noNefege, noYafiya;', 'XHO22'),
(17, 14, 7, 'noElishama, noBheliyadi, noElifelete.', 'XHO22'),
(17, 14, 8, 'Athi akuva amaFilisti ukuba uDavide uthanjiswe waba ngukumkani kumaSirayeli onke, enyuka onke amaFilisti, aya kumfuna uDavide. Weva ke uDavide, waphuma waya kuwakhawulela.', 'XHO22'),
(17, 14, 9, 'Afika amaFilisti, abachithachitha entilini yamaRafa.', 'XHO22'),
(17, 14, 10, 'uDavide wabuza kuYehova, esithi, Ndiwahlasele na amaFilisti? uya kuwanikela na esandleni sam? Wathi uYehova kuye, Yima; ngokuba ndiya kuwanikela esandleni sakho.', 'XHO22'),
(17, 14, 11, 'Benyuka baya eBhahali-peratsim; uDavide wawaxabela khona. Wathi uDavide, uThixo uzigqobhozile iintshaba zam; ngenxa yoko bathi loo ndawo yiBhahali-perasima.', 'XHO22'),
(17, 14, 12, 'Azishiya apho izithixo zawo, waza uDavide wayalela ukuba atshiswe ngomlilo.', 'XHO22'),
(17, 14, 13, 'Aphinda amaFilisti achithakala entilini leyo.', 'XHO22'),
(17, 14, 14, 'Waphinda uDavide wabuza kuThixo; Wathi uThixo kuye, Musa ukubalandela; zijike, uzikhawulele ngasemithini yequnube.', 'XHO22'),
(17, 14, 15, 'Xa uthe waliva isandi sokuhamba emantloko emithi yequnube, phuma uye kulwa; ngokuba uThixo uphumile phambi kwakho, ukuba awuxabele umkhosi wamaFilisti.', 'XHO22'),
(17, 14, 16, 'Wenza uDavide njengoko uYehova wamwisela umthetho ngako, bawuxabela umkhosi wamaFilisti, bathabathela eGibheyon, besa eGezere.', 'XHO22'),
(17, 14, 17, 'Lwaphuma lwaya emazweni onke udaba lukaDavide; yaye uYehova wabeka uloyiko lwakhe kuzo zonke iintlanga.', 'XHO22'),
(17, 15, 1, 'uDavide wazakhela izindlu emzini kaDavide; wayilungiselela indawo ityeya kaThixo, wayimisela intente.', 'XHO22'),
(17, 15, 2, 'Wathi uDavide, Akukho bani unako ukuyithwala ityeya kaThixo, ingengabaLevi; ngokuba abanyule uYehova ukuba bayithwale ityeya kaThixo, bamkhonze ngonaphakade.', 'XHO22'),
(17, 15, 3, 'uDavide wawahlanganisa onke amaSirayeli eYerusalem ukuba athabathe ityeya kaYehova iyise endaweni yayo awayeyilungiselele yona.', 'XHO22'),
(17, 15, 4, 'Wabahlanganisa uDavide oonyana baka-Aron nabaLevi.', 'XHO22'),
(17, 15, 5, 'Koonyana bakaKehati yangu-Eriyeli; U-Uriyeli umthetheli, nabazalwana bakhe, belikhulu elinamanci mabini;', 'XHO22'),
(17, 15, 6, 'koonyana bakaMerari; uAsaya umthetheli, nabazalwana bakhe, bengamakhulu amabini anamanci mabini;', 'XHO22'),
(17, 15, 7, 'koonyana bakaGershom yangu-Asheri; uYoweli umthetheli, nabazalwana bakhe, belikhulu elinamanci mathathu;', 'XHO22'),
(17, 15, 8, 'koonyana bakaElishafan yangu uShemaya umthetheli, nabazalwana bakhe, bengamakhulu amabini;', 'XHO22'),
(17, 15, 9, 'koonyana bakaHebron; nguEliyeli intloko, nabazalwana bakhe, amazinyo amashumi asibhozo anamabini;', 'XHO22'),
(17, 15, 10, 'koonyana bakaUziyeli yangu uAminadabhi umthetheli, nabazalwana bakhe, belikhulu elinashumi-nye linababini.', 'XHO22'),
(17, 15, 11, 'uDavide wabiza uTsadoki noAbhiyatare, ababingeleli nabaLevi, ukuba beze kuUriyeli, noAsaya, noYoweli, noShemaya, noEliyeli, noAminadabhi;', 'XHO22'),
(17, 15, 12, 'Wathi kubo, Iintloko zezindlu zooyise zabaLevi; zihlambululeni nina nabazalwana benu, ukuze niyise ityeya kaYehova, uThixo kaSirayeli, ize endaweni endiyilungisileyo.', 'XHO22'),
(17, 15, 13, 'Ekubeni ningenjinjalo ekuqaleni, uYehova uThixo wethu usitshabalalisile, ngenxa yokuba asimfunanga ngomyalelo wobulungisa.', 'XHO22'),
(17, 15, 14, 'Bazingcwalisa ke ababingeleli nabaLevi, ukuba bayinyuse ityeya kaYehova uThixo kaSirayeli.', 'XHO22'),
(17, 15, 15, 'Bayithwala oonyana bakaLevi ityeya kaThixo, njengoko abemwisele umthetho uMoses ngokwelizwi likaYehova, emagxeni abo, nezibonda ezikuyo.', 'XHO22'),
(17, 15, 16, 'uDavide waqhubeka ethetha neentloko zabaLevi ukuba zikhethe abazalwana babo ukuba babe ziimvumi ezinezixhobo zomculo, iihadi nemirhubhe namacangci abavakalisa ngovuyo.', 'XHO22'),
(17, 15, 17, 'AbaLevi bambeka uHeman unyana kaYoweli; kubazalwana bakhe ibinguAsafu unyana kaBherekiya; koonyana bakaMerari, abazalwana babo nguEtan unyana kaKushiya;', 'XHO22'),
(17, 15, 18, 'Banabazalwana babo bodidi lwesibini, uZekariya, uBhen, uYaheziyeli, uSemiramoti, uYehiyeli, uUni, uEliyabhi, uBhenaya, uMahaseya, uMatitja, uElifeli, uMikeya, uObhedi-edom noYehiyeli, abagcini-masango.', 'XHO22'),
(17, 15, 19, 'Ke iimvumi, ooHeman, noAsafu, noEtan, bakhethwa ukuba badlale amacangci obhedu;', 'XHO22'),
(17, 15, 20, 'noZekariya, noAsiyeli, noSemiramoti, noYehiyeli, noUni, noEliyabhi, noMahaseya, noBhenaya, abaneehadi zeehadi;', 'XHO22'),
(17, 15, 21, 'OoMatitiya, noElifele, noMikeneya, no-Obhedi-edom, noYehiyeli, noAzasiya, belunge nesahlulo sesithathu phezu kwamaSemi.', 'XHO22'),
(17, 15, 22, 'Ke uKenaniya, intloko yabaLevi, ubecula;', 'XHO22'),
(17, 15, 23, 'OoBheriya noElikana babengabamasango etyeya.', 'XHO22'),
(17, 15, 24, 'Yaye uSebhaniya noYoshafati noNataniyeli noAmasayi noZekariya noBhenaya noEliyezere ababingeleli babevuthela amaxilongo phambi kobuncwane boThixo oyinyaniso, yaye uObhedi-edom noYehiya babengabalusi bamasango eNgxowa-mali.', 'XHO22'),
(17, 15, 25, 'uDavide namadoda amakhulu akwaSirayeli nezikhulu baya kuyithabatha ityeya yomnqophiso kaYehova bevuya endlwini kaObhedi-edom.', 'XHO22'),
(17, 15, 26, 'Xa uThixo wanceda abaLevi ekuthwaleni ityeya yomnqophiso kaYehova, babingelela ngeenkunzi zeenkomo ezisixhenxe neenkunzi zeegusha ezisixhenxe.', 'XHO22'),
(17, 15, 27, 'Wayambethe ingubo yokwaleka yelinen ecikizekileyo uDavide, nabaLevi bonke ababethwele ityeya, neemvumi, noKenaniya umphathi wabathwali, neemvumi; ke uDavide ebhinqe ilinen egxalabeni;', 'XHO22'),
(17, 15, 28, 'Ngoko wonke uSirayeli wayiphakamisa ityeya yomnqophiso kaYehova ngovuyo ngesandi sesigodlo samaxilongo namacangci ekhalisa iihadi neehadi.', 'XHO22'),
(17, 15, 29, 'Kwathi ke ekungeneni kwetyeya yomnqophiso kaYehova kwisiXeko sikaDavide, uMikali, intombi kaSawule, wavela ngefestile, wambona uKumkani uDavide esina edlala, wamcekisa entliziyweni yakhe.', 'XHO22'),
(17, 16, 1, 'Bayingenisa ityeya kaThixo, bayimisa esazulwini sentente abeyimisile uDavide, banyusa amadini anyukayo nemibingelelo yombulelo phambi koYehova.', 'XHO22'),
(17, 16, 2, 'Wagqiba ke uDavide ukwenza amadini anyukayo nemibingelelo yombulelo, wabasikelela abantu egameni likaYehova.', 'XHO22'),
(17, 16, 3, 'Wabanika bonke abantu bakwaSirayeli, inkunzi nemazi; elowo wamnika intenda yesonka, nesuntswana lenyama, nendebe yewayini.', 'XHO22'),
(17, 16, 4, 'Wabeka abanye kubaLevi ukuba bakhonze phambi kwetyeya kaYehova, banike ingxelo, badumise, badumise uYehova uThixo kaSirayeli.', 'XHO22'),
(17, 16, 5, 'Yaba nguAsafu intloko, yanguZekariya emva kwakhe, noYehiyeli, noShemiramoti, noYehiyeli, noMatitiya, noEliyabhi, noBhenaya, no-Obhedi-edom; noYaheli ngeehadi neehadi; uAsafu ebetha amacangci;', 'XHO22'),
(17, 16, 6, 'Yaye ababingeleli uBhenaya noYahaziyeli babesoloko bevuthela ngezigodlo phambi kwetyeya yomnqophiso kaTHIXO oyinyaniso.', 'XHO22'),
(17, 16, 7, 'Kwangaloo mini, uDavide waqalisa ukwenza le ndumiso ebulela uYehova ngoAsafu nabazalwana bakhe.', 'XHO22'),
(17, 16, 8, 'Dumisani uYehova, nqulani igama lakhe, zaziseni iintlanga izenzo zakhe.', 'XHO22'),
(17, 16, 9, 'Vumani kuye, mbetheleni ingoma, Xoxani ngemisebenzi yakhe yonke ebalulekileyo.', 'XHO22'),
(17, 16, 10, 'Qhayisani ngegama lakhe elingcwele; mayivuye intliziyo yabamngxameleyo uYehova.', 'XHO22'),
(17, 16, 11, 'Mfuneni uYehova namandla akhe, Funani ubuso bakhe ngamaxesha onke.', 'XHO22'),
(17, 16, 12, 'Cingani ngemisebenzi yakhe ebalulekileyo awayenzayo, Izimanga zakhe nezigwebo zomlomo wakhe;', 'XHO22'),
(17, 16, 13, 'Nina nyana bakaSirayeli, mkhonzi wakhe, nyana bakaYakobi, banyulwa bakhe.', 'XHO22'),
(17, 16, 14, 'NguYehova uThixo wethu yena; zisehlabathini lonke izigwebo zakhe.', 'XHO22'),
(17, 16, 15, 'Khumbulani ngamaxesha onke umnqophiso wakhe; Ilizwi awalimisela iwaka lezizukulwana;', 'XHO22'),
(17, 16, 16, 'Ngokomnqophiso awawenzayo noAbraham, Nokufunga kwakhe kuIsake;', 'XHO22'),
(17, 16, 17, 'Walimisa kuYakobi ukuba libe ngummiselo, KuSirayeli ukuba libe ngumnqophiso ongunaphakade;', 'XHO22'),
(17, 16, 18, 'Wathi, Ndikunika ilizwe lakwaKanan, libe lilifa lenu;', 'XHO22'),
(17, 16, 19, 'Nise nimbalwa, nimbalwa, ningabaphambukeli phakathi kwenu;', 'XHO22'),
(17, 16, 20, 'Behamba besuka kolunye uhlanga, baye kolunye uhlanga, Besuka kobunye ubukumkani, baye kolunye uhlanga;', 'XHO22'),
(17, 16, 21, 'Akavumelanga mntu ukuba abenzakalise; ewe, wabakhalimela ookumkani ngenxa yabo.', 'XHO22'),
(17, 16, 22, 'Wathi, Musani ukubachukumisa abathanjiswa bam, Musani ukubaphatha kakubi abaprofeti bam.', 'XHO22'),
(17, 16, 23, 'Vumani kuYehova, nonke hlabathi; ulwazisa imihla ngemihla usindiso lwakhe.', 'XHO22'),
(17, 16, 24, 'Balisani ezintlangeni uzuko lwakhe; imisebenzi yakhe ebalulekileyo ezintlangeni zonke.', 'XHO22'),
(17, 16, 25, 'Kuba mkhulu uYehova yaye ufanele ukudunyiswa kakhulu; Uyoyikeka yena ngaphezu koothixo bonke.', 'XHO22'),
(17, 16, 26, 'Kuba bonke oothixo beentlanga banqula izithixo, kodwa uYehova wenza amazulu.', 'XHO22'),
(17, 16, 27, 'Uzuko nozuko luphambi kobuso bakhe; amandla novuyo lusendaweni yalo.', 'XHO22'),
(17, 16, 28, 'Mnikeni uYehova, nina zizukulwana zeentlanga, mnikeni uYehova uzuko namandla.', 'XHO22'),
(17, 16, 29, 'Zisani kuYehova uzuko lwegama lakhe; wenze idini, uze phambi kwakhe; nqula uYehova ngendlela ebalaseleyo.', 'XHO22'),
(17, 16, 30, 'Moyikeni ubuso bakhe, nonke hlabathi; umhlaba uya kuzinziswa, ukuba ungashukumi.', 'XHO22'),
(17, 16, 31, 'Malivuye izulu, ligcobe ihlabathi, Zithi iintlanga phakathi kweentlanga, uYehova ngukumkani.', 'XHO22'),
(17, 16, 32, 'Malugqume ulwandle nokuzala kwalo; makavuye amasimi nako konke okukuwo.', 'XHO22'),
(17, 16, 33, 'Iya kwandula ke imithi yehlathi ibe nemihlali phambi koYehova, ngenxa yokuba eze kugweba ihlabathi.', 'XHO22'),
(17, 16, 34, 'Dumisani uYehova; ngokuba elungile; Ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(17, 16, 35, 'Nithi ke, Thixo ongumsindisi wethu, sisindise, usihlanganise, usihlangule ezintlangeni, ukuze sidumise igama lakho elingcwele, sidumise igama lakho.', 'XHO22'),
(17, 16, 36, 'Makabongwe uYehova, uThixo kaSirayeli, kude kuse ephakadeni. Bathi bonke abantu, Amen, badumisa uYehova.', 'XHO22'),
(17, 16, 37, 'Wabashiya apho phambi kwetyeya yomnqophiso kaYehova uAsafu nabazalwana bakhe ukuze basebenze rhoqo phambi kweTyeya, ngokomsebenzi wemihla ngemihla;', 'XHO22'),
(17, 16, 38, 'no-Obhedi-edom nabazalwana babo, bengamashumi amathandathu anesibhozo; uObhedi-edom, unyana kaYedutun, noHosa, bengabamasango;', 'XHO22'),
(17, 16, 39, 'uTsadoki umbingeleli nomntakwabo ababingeleli baphambi komnquba kaYehova kwindawo ephakamileyo eyayiseGibheyon,', 'XHO22'),
(17, 16, 40, 'ukuba basoloko besondeza amadini anyukayo kuYehova, kusasa nangokuhlwa, esibingelelweni sedini elinyukayo, bakwenze konke okubhaliweyo emyalelweni kaYehova, awawumisela amaSirayeli;', 'XHO22'),
(17, 16, 41, 'baba noHeman noYedutun nabanye abanyuliweyo ababizwa ngamagama ukuba badumise uYehova, ngenxa yokuba inceba yakhe ikwixesha elingenammiselo;', 'XHO22'),
(17, 16, 42, 'baba noHeman noYedutun, bephethe amaxilongo, namaxilongo, abo bakhaphayo, neempahla zikaThixo. Oonyana bakaYedutun bengabamasango.', 'XHO22'),
(17, 16, 43, 'Bafuduka bonke abantu, elowo waya endlwini yakhe; wabuya uDavide, waya kusikelela indlu yakhe.', 'XHO22'),
(17, 17, 1, 'Kwathi, ehleli endlwini yakhe uDavide, wathi uDavide kuNatan umprofeti, Uyabona, mna ndihleli endlwini yomsedare; ke yona ityeya yomnqophiso kaYehova ihleli phantsi kwezilaphu.', 'XHO22'),
(17, 17, 2, 'Wathi uNatan kuDavide, Konke okusentliziyweni yakho, kwenze; ngokuba uThixo unawe.', 'XHO22'),
(17, 17, 3, 'Kwathi ngobo busuku, lafika ilizwi likaThixo kuNatan, lisithi:', 'XHO22'),
(17, 17, 4, 'Hamba uye kuthi kumkhonzi wam uDavide, uthi, Utsho uYehova ukuthi, Musa ukundakhela indlu endiya kuhlala kuyo;', 'XHO22'),
(17, 17, 5, 'Ngokuba andihlalanga ndlwini, kususela kulaa mini ndabanyusayo oonyana bakaSirayeli, unanamhla; ephuma emnqubeni aye kwenye, ephuma emnqubeni aye komnye.', 'XHO22'),
(17, 17, 6, 'Ekuhambeni konke endakuhambayo noonyana bakaSirayeli bonke, ndabaxelela omnye wabagwebi bakwaSirayeli, endabawisela umthetho wokuba babaluse abantu bam, ndathi, Yini na ukuba ungandakheli indlu yomsedare?', 'XHO22'),
(17, 17, 7, 'Ngoko ke uze uthi kumkhonzi wam uDavide, Utsho uYehova wemikhosi ukuthi, Mna ndakukhupha esibayeni, ndisemva komhlambi, ukuze ube yinganga yabantu bam amaSirayeli.', 'XHO22'),
(17, 17, 8, 'Ndandinawe naphi na apho wawusiya khona, yaye ndazitshabalalisa zonke iintshaba zakho phambi kwakho, ndaza ndakwenzela igama njengegama labo bakhulu emhlabeni.', 'XHO22'),
(17, 17, 9, 'Ndiya kubalungisela indawo abantu bam amaSirayeli, ndibaqinise, beme endaweni yabo, bangabi sagungqa; kwaye abantwana bobugwenxa abasayi kuphinda babatshabalalise njengangaphambili;', 'XHO22'),
(17, 17, 10, 'kususela kulaa mini ndabamisela abantu bam amaSirayeli abagwebi. Kwaye ndiza kuzoyisa zonke iintshaba zakho. Ngaphezu koko, ndithi kuni, uYehova uya kukwakhela indlu.', 'XHO22'),
(17, 17, 11, 'Xa ithe yazaliseka imihla yakho yokuba uye kooyihlo, ndiya kuyizalela emva kwakho imbewu yakho, iphume koonyana bakho; ndibuzinzise ubukumkani bakhe.', 'XHO22'),
(17, 17, 12, 'Nguye ke oya kundakhela indlu, ndiyizinzise itrone yakhe, kude kuse ephakadeni.', 'XHO22'),
(17, 17, 13, 'Mna ndiya kuba nguyise kuye, yena abe ngunyana wam;', 'XHO22'),
(17, 17, 14, 'Ndiza kummisela endlwini yam nasebukumkanini bam ukusa kwixesha elingenammiselo, netrone yakhe iya kuqiniseka ukusa kwixesha elingenammiselo.', 'XHO22'),
(17, 17, 15, 'Njengaloo mazwi onke, ngokwalo mbono, wenjenjalo ukuthetha uNatan kuDavide.', 'XHO22'),
(17, 17, 16, 'Waya ukumkani uDavide, wahlala phambi koYehova, wathi, Ndingubani na, YehovaYehova, iyintoni na indlu yam, ukuba undizise apha?', 'XHO22'),
(17, 17, 17, 'Kuncinane oko ke emehlweni akho, Thixo; ngenxa yokuba kwanexesha elide wathetha ngendlu yomkhonzi wakho waza wandinyamekela ngokobuhle bendoda ephakamileyo, uYehova uThixo.', 'XHO22'),
(17, 17, 18, 'Wobuya athini na uDavide kuwe ngenxa yomkhonzi wakho? ngokuba uyamazi umkhonzi wakho.', 'XHO22'),
(17, 17, 19, 'Yehova, ngenxa yomkhonzi wakho nangokwentliziyo yakho uzenzile zonke ezi zinto zinkulu ngokwazisa zonke ezi zinto zikhulu.', 'XHO22'),
(17, 17, 20, 'Yehova, akukho unjengawe, akukho Thixo ingenguwe, njengoko konke sikuvileyo ngeendlebe zethu.', 'XHO22'),
(17, 17, 21, 'Luluphi na uhlanga ehlabathini apha olunjengohlanga lwakho lukaSirayeli, awalukhululayo uThixo ukuba lube luhlanga lwakhe, ukuze uzenzele igama elikhulu eloyikekayo ngokugxotha iintlanga phambi kwabantu bakho owabakhululayo kubo? Jiphethe?', 'XHO22'),
(17, 17, 22, 'Ngokuba ubenze abantu bakho amaSirayeli baba ngabantu bakho kude kuse ephakadeni; wena Yehova waba nguThixo wabo.', 'XHO22'),
(17, 17, 23, 'Ngako oko, Yehova, ilizwi olithethileyo ngomkhonzi wakho nangendlu yakhe malihlale limi, wenze njengoko ukuthethileyo;', 'XHO22'),
(17, 17, 24, 'Malinyaniseke, ukuze igama lakho libe likhulu ngonaphakade, ngokuthi, uYehova wemikhosi, uThixo kaSirayeli, uThixo kaSirayeli; nendlu kaDavide umkhonzi wakho mayime phambi kwakho.', 'XHO22'),
(17, 17, 25, 'Ngokuba wena, Thixo wam, wathi kumkhonzi wakho, Uya kumakhela indlu; ngenxa yoko umkhonzi wakho wayifikelela intliziyo yakhe ukuba athandaze phambi kwakho.', 'XHO22'),
(17, 17, 26, 'Kaloku, Yehova, unguThixo wena, yaye umthembise ezi ntsikelelo umkhonzi wakho.', 'XHO22'),
(17, 17, 27, 'Kaloku makukholeke kuwe ukuyisikelela indlu yomkhonzi wakho, ukuba ihlale ihleli phambi kwakho ngonaphakade; ngokuba usikelelwe wena, Yehova;', 'XHO22'),
(17, 18, 1, 'Emva koko uDavide wawaxabela amaFilisti, wawoyisa, wayithimba iGati nemizi yayo esandleni samaFilisti.', 'XHO22'),
(17, 18, 2, 'Wawaxabela uMowabhi; yaye amaMowabhi aba ngabakhonzi bakaDavide abazisa izipho kuwo.', 'XHO22'),
(17, 18, 3, 'uDavide wambulala uHadadezere, ukumkani waseTsobha, wada wesa eHamati, ekuyeni kwakhe ukubuzinzisa ubukumkani bakhe emlanjeni ongumEfrati.', 'XHO22'),
(17, 18, 4, 'uDavide wathimba iwaka leenqwelo zokulwa, namawaka asixhenxe abamahashe, namashumi amabini amawaka omqikela;', 'XHO22'),
(17, 18, 5, 'Xa ama-Aram ephuma eDamasko esiza kumnceda uHadadezere ukumkani waseSobha, uDavide wabulala ama-Aram angamashumi amabini anamabini amawaka.', 'XHO22'),
(17, 18, 6, 'uDavide wamisa abalindi kwa-Aram; yaye amaSiriya aba ngabakhonzi bakaDavide abazisa izipho kuwo. Ngoko uYehova wamkhusela uDavide naphi na apho wayesiya khona.', 'XHO22'),
(17, 18, 7, 'uDavide wawathabatha amakhaka egolide awayekubakhonzi bakaHadadezere, wawazisa eYerusalem.', 'XHO22'),
(17, 18, 8, 'uDavide wathabatha ubhedu oluninzi eTibhati naseKuna, imizi kaHadadezere, awathi uSolomon wenza ngayo ulwandle lobhedu, neintsika, nezinto zobhedu.', 'XHO22'),
(17, 18, 9, 'Weva uToyu ukumkani waseHamati, ukuba uDavide uyixabele yonke impi kaHadadezere, ukumkani waseTsobha;', 'XHO22'),
(17, 18, 10, 'Wathumela unyana wakhe uHadoram kuKumkani uDavide ukuba ambuze ukuphila kwakhe nokubulela ngenxa yokuba wayelwe noHadadizere waza wamoyisa; (ngokuba uHadadezere wasilwa neTou) eneempahla zeentlobo ngeentlobo zegolide, isilivere nobhedu.', 'XHO22'),
(17, 18, 11, 'Kananjalo ukumkani uDavide wanyula uYehova, kunye nesilivere negolide abeyithabathe kuzo zonke ezo ntlanga; kumaEdom, nakuMowabhi, nakoonyana baka-Amon, nakumaFilisti, nakuAmaleki.', 'XHO22'),
(17, 18, 12, 'Ngaphezu koko, uAbhishayi unyana kaTseruya wakwaEdom kwiNtlambo yeTyuwa wabulala ishumi elinesibhozo lamawaka.', 'XHO22'),
(17, 18, 13, 'Wamisa abalindi kwaEdom; yaye onke amaEdom aba ngabakhonzi bakaDavide. Ngoko uYehova wamkhusela uDavide naphi na apho wayesiya khona.', 'XHO22'),
(17, 18, 14, 'uDavide ke walawula uSirayeli ephela, esenza ngokusesikweni nangobulungisa ebantwini bakhe bonke.', 'XHO22'),
(17, 18, 15, 'uYowabhi, unyana kaTseruya, ubephethe umkhosi; noYehoshafati, unyana ka-Akiludi, umphathi;', 'XHO22'),
(17, 18, 16, 'uTsadoki unyana ka-Ahitubhi, noAbhimeleki, unyana ka-Abhiyatare, babengababingeleli; uShavasha ubengunobhala;', 'XHO22'),
(17, 18, 17, 'uBhenaya unyana kaYehoyada wayephethe amaKrete nePleti; yaye oonyana bakaDavide babeyintloko kakumkani.', 'XHO22'),
(17, 19, 1, 'Emveni koko wafa uNahashe ukumkani woonyana baka-Amon, unyana wakhe waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(17, 19, 2, 'Wathi uDavide, Mandimenzele inceba uHanun unyana kaNahashe, njengokuba noyise wandenzela inceba. uDavide wathumela abathunywa ukuba baye kumthuthuzela ngenxa kayise. Bafika abakhonzi bakaDavide ezweni loonyana baka-Amon, uHanun, ukuba bamthuthuzele.', 'XHO22'),
(17, 19, 3, 'Bathi abathetheli boonyana baka-Amon kuHanun, na wena ukuthi, Uyamhlonela na uyihlo uDavide, oko ethume abakhuzi kuwe? Abakhonzi bakhe abeze kuni na, ukuba balihlole ilizwe, ukuba balibhukuqe, balihlole?', 'XHO22'),
(17, 19, 4, 'Ngoko uHanun wabathabatha abakhonzi bakaDavide waza wabacheba waza wasika izambatho zabo waza wanqunqa phakathi ukuya ngasemva waza wabandulula.', 'XHO22'),
(17, 19, 5, 'Kwahamba abantu, bamxelela uDavide okwenziwe kuloo madoda. Wathumela ukuba ahlangatyezwe, kuba loo madoda ayehlazekile kakhulu. Wathi ukumkani, Hlalani eYeriko zide zihlume iindevu zenu, nandule ukubuya.', 'XHO22'),
(17, 19, 6, 'Babona oonyana baka-Amon, ukuba benze amasikizi kuDavide, bathumela ke ooHanun noonyana baka-Amon kwa-Aram ephakathi kweMilambo, nakwa-Aram-maga, naseSobha, iwaka leetalente zesilivere, ukuba baqeshe iinqwelo zokulwa nabamahashe.', 'XHO22'),
(17, 19, 7, 'Baziqeshela amashumi amathathu anamabini amawaka eenqwelo zokulwa, nokumkani waseMaga nabantu bakhe; abo bezayo bamisa ngaseMedebha. Bahlanganisana oonyana baka-Amon emizini yabo, beza kulwa.', 'XHO22'),
(17, 19, 8, 'Akuva ke uDavide, wathumela uYowabhi nomkhosi wonke wamagorha.', 'XHO22'),
(17, 19, 9, 'Baphuma oonyana baka-Amon, baya kulwa phambi kwesango lesixeko; ookumkani ababefikile babebodwa ezweni.', 'XHO22'),
(17, 19, 10, 'Wabona uYowabhi ukuba imfazwe ingxamele kuye, ngaphambili nangasemva;', 'XHO22'),
(17, 19, 11, 'Abaseleyo wabanikela esandleni sika-Abhishayi umkhuluwa wakhe; bagidima baya kubakhawulela oonyana baka-Amon.', 'XHO22'),
(17, 19, 12, 'Wathi, Xa athe andifumana ama-Aram, wondinceda; kodwa ukuba oonyana baka-Amoni bomelele kunawe, ndiya kukunceda mna.', 'XHO22'),
(17, 19, 13, 'Yiba nesibindi size sibe nesibindi ngenxa yohlanga lwethu nangenxa yezixeko zoThixo wethu, yaye ngamana uYehova enze okulungileyo emehlweni akhe.', 'XHO22'),
(17, 19, 14, 'Weza uYowabhi namadoda abenaye kuma-Aram emfazweni; asaba ebusweni bakhe.', 'XHO22'),
(17, 19, 15, 'Xa oonyana baka-Amoni babona ukuba ama-Aram asabile, nabo basaba phambi koAbhishayi umntakwabo baza bangena esixekweni. Wafika uYowabhi eYerusalem.', 'XHO22'),
(17, 19, 16, 'Akubona ama-Aram ukuba anomsindo phambi koonyana bakaSirayeli, athumela abathunywa, bawakhupha ama-Aram awayephesheya koMlambo; nguSofagus, umthetheli womkhosi kaHadadezere, ekhokele kuwo.', 'XHO22'),
(17, 19, 17, 'Waxelelwa uDavide; wawahlanganisa onke amaSirayeli, wayiwela iYordan, wawaxabela, walwa nawo. Xa uDavide wawaxabelayo ama-Aram, aza amhlasela.', 'XHO22'),
(17, 19, 18, 'Asaba ke ama-Aram phambi koonyana bakaSirayeli; uDavide wabulala kuma-Aram amawaka asixhenxe abaqhubi beenqwelo zokulwa, namashumi amane amawaka omqikela, waxabela noSofage, umthetheli womkhosi.', 'XHO22'),
(17, 19, 19, 'Babona abakhonzi bakaHadadezere ukuba babevutha ngumsindo phambi koonyana bakaSirayeli, baxolelana noDavide baza bamkhonza; ama-Aram akasayi kuba sabahlangula oonyana baka-Amon.', 'XHO22'),
(17, 20, 1, 'Wakudlula umnyaka, ngexesha lokulwa kookumkani, uYowabhi wakhokela umkhosi onamandla, walenza laphanzisa ilizwe loonyana baka-Amon, wayingqinga iRabha. Ke uDavide wayehleli eYerusalem. uYowabhi wayoyisa iRabha, wayitshabalalisa.', 'XHO22'),
(17, 20, 2, 'uDavide wasithabatha isithsaba sikakumkani wabo entlokweni yakhe waza wafumana ubunzima baso bebuyitalente yegolide, sinamatye anqabileyo; yabekwa entlokweni kaDavide, wathabatha amaxhoba amaninzi aloo mzi.', 'XHO22'),
(17, 20, 3, 'Wawakhupha amadoda awayekuyo, wawasika ngeesarha, nangeemela zesinyithi, nangeempahla ezibukhali zesinyithi; Wenjenjalo uDavide kwimizi yonke yoonyana baka-Amon. Emva koko uDavide nabantu bonke babuyela eYerusalem.', 'XHO22'),
(17, 20, 4, 'Emva kwemfazwe wawasukela amaFilisti eGezere; Ngelo xesha uSibhegayi kaHuse wambulala uSipayi, omnye woonyana bamaRafa, bazithoba.', 'XHO22'),
(17, 20, 5, 'Kwabuya ke kwabakho ukulwa namaFilisti; uElihanan, unyana kaYayiro, wambulala uLami, umzalwana kaGoliyati waseGati, ontshuntshe yakhe ibinganjengentshuntshe yabaluki.', 'XHO22'),
(17, 20, 6, 'Kwaphinda kwabakho imfazwe eGati, apho kwakukho indoda ephakamileyo, iminwe yayo ingamashumi amabini anesine, emithandathu esandleni ngasinye nemithandathu emlenzeni ngamnye; ekwangunyana wengxilimbela.', 'XHO22'),
(17, 20, 7, 'Kwathi, ekulweni kwakhe noonyana bakaSirayeli, wambulala uYonatan unyana kaShimeha, umkhuluwa kaDavide.', 'XHO22'),
(17, 20, 8, 'Bazalwa kwiRafa iGati; yaye babulawa ngesandla sikaDavide nangesandla sabakhonzi bakhe.', 'XHO22'),
(17, 21, 1, 'Ke kaloku uSathana wabasukela oonyana bakaSirayeli, wamlinga uDavide ukuba awabale amaSirayeli.', 'XHO22'),
(17, 21, 2, 'Wathi uDavide kuYowabhi nakubathetheli babantu, Hambani niye kuwabala amaSirayeli, nithabathele eBher-shebha, nide nise kwaDan; undizisele inani labo, ndilazi.', 'XHO22'),
(17, 21, 3, 'Wathi uYowabhi, uYehova makabandise abantu bakhe kalikhulu kunabo bangabo; kodwa, nkosi yam kumkani, bonke aba asingabakhonzi benkosi yam na? iyifunelani lento nkosi yam? Kutheni ilityala likaSirayeli nje?', 'XHO22'),
(17, 21, 4, 'Laqinisela ilizwi lokumkani kuYowabhi. Wemka uYowabhi, wacanda kumaSirayeli onke, weza eYerusalem.', 'XHO22'),
(17, 21, 5, 'uYowabhi wamnika uDavide inani labantu. Bonke abaphuma kwaSirayeli baba liwaka elinekhulu lamawaka, namaYuda angamawaka angamakhulu amane anamanci mathandathu amawaka.', 'XHO22'),
(17, 21, 6, 'Akababalelanga uLevi noBhenjamin phakathi kwabo, ngokuba ilizwi lokumkani lalisikizi kuYowabhi.', 'XHO22'),
(17, 21, 7, 'Akakholiswanga ke ngulo uThixo; ngoko ke, wawoyisa amaSirayeli.', 'XHO22'),
(17, 21, 8, 'Wathi uDavide kuThixo, Ndonile kunene ngale nto ndiyenzileyo; ke ngoko bususe ububi bomkhonzi wakho; ngokuba ndenze izinto ezininzi zobudenge.', 'XHO22'),
(17, 21, 9, 'Wathetha uYehova kuGadi, imboni kaDavide, wathi:', 'XHO22'),
(17, 21, 10, 'Yiya uthethe kuDavide, uthi, Utsho uYehova ukuthi, Ndikunika izinto ezintathu; zinyulele ibe nye kuzo, ndikwenzele yona.', 'XHO22'),
(17, 21, 11, 'Waya ke uGadi kuDavide, wathi kuye, uYehova uthe, Zinyulele;', 'XHO22'),
(17, 21, 12, 'nokuba yiminyaka emithathu yendlala; nokuba ziinyanga ezintathu ugxothwe ziintshaba zakho, xa umkhonto weentshaba zakho ufika kuwe; nokuba ziintsuku ezintathu ikrele likaYehova, nendyikitya yokufa, ibe sehlabathini; ke isithunywa sikaYehova sawatshabalalisa onke amazwe akwaSirayeli. Kaloku ke, mandinixelele ilizwi endiya kulibuyisela kowandithumayo.', 'XHO22'),
(17, 21, 13, 'Wathi uDavide kuGadi, Ndibandezelekile; mandiwele esandleni sikaYehova; ngokuba zininzi iinceba zakhe, kodwa mandingaweli ezandleni zabantu.', 'XHO22'),
(17, 21, 14, 'uYehova wathumela isibetho kuSirayeli, kwaza kwafa amashumi asixhenxe amawaka amadoda kwaSirayeli.', 'XHO22'),
(17, 21, 15, 'uThixo wathuma isithunywa eYerusalem, ukuba siyitshabalalise; wakubona oko uYehova, wasikwa yimfesane ngenxa yobubi obo, wathi kwisithunywa eso sitshabalalisayo, Kwanele, bamba isandla sakho. Ingelosi kaYehova yayimi etroneni kaOrnan umYebhusi.', 'XHO22'),
(17, 21, 16, 'Wawaphakamisa amehlo akhe uDavide, wayibona ingelosi kaYehova imi phakathi komhlaba nezulu, iphethe ikrele layo ngesandla, isalathe eYerusalem. Emva koko uDavide namadoda amakhulu akwaSirayeli bawa ngobuso, benxibe iingubo ezimnyama.', 'XHO22'),
(17, 21, 17, 'Wathi uDavide kuThixo, Asindim yini na othe mababalwe abantu? ndonile, ndonile, ndonile; kodwa ezi zimvu zenze ntoni na? Yehova, Thixo wam, isandla sakho masibe phezu kwam, nendlu kabawo; kungabi phakathi kwabantu bakho, ukuze batshatyalaliswe.', 'XHO22'),
(17, 21, 18, 'Ke isithunywa sikaNdikhoyo sathi kuGadi makaxelele uDavide ukuba uDavide makanyuke aye kumakhela uNdikhoyo iqonga lamadini esandeni sikaOrnan umYebhusi.', 'XHO22'),
(17, 21, 19, 'Wenyuka uDavide ngokwelizwi likaGadi, awalithethayo egameni likaYehova.', 'XHO22'),
(17, 21, 20, 'Wajika uOrnane, wabona isithunywa; oonyana bakhe abane abakunye naye bazimele. Ke uOrnane wayebhula ingqolowa.', 'XHO22'),
(17, 21, 21, 'Wathi akufika uDavide kuOrnan, uOrnan wakhangela waza wambona uDavide, waphuma esandeni waza waqubuda kuDavide ejonge phantsi.', 'XHO22'),
(17, 21, 22, 'Wathi uDavide kuOrnan, Ndinike esi sandá, ndimakhele khona uYehova isibingelelo; ndinike ngexabiso elizalisekileyo, sithintelwe isibetho sabantu.', 'XHO22'),
(17, 21, 23, 'Wathi uOrnan kuDavide, Yithabathe, inkosi yam ukumkani yenze okulungileyo emehlweni ayo; Yabonani, ndininika iinkomo zibe ngamadini anyukayo, namakhiwane abe ziinkuni, nengqolowa ibe ngumnikelo wokudla; Ndinikela ngako konke.', 'XHO22'),
(17, 21, 24, 'Wathi ukumkani uDavide kuOrnan: “Hayi! kodwa ngokuqinisekileyo ndiya kusithenga ngexabiso elipheleleyo, ngenxa yokuba andiyi kuthabatha oko kukokwakho kuYehova, okanye ndinikele nomnikelo otshiswayo ngaphandle kwentlawulo.', 'XHO22'),
(17, 21, 25, 'uDavide wamnika uOrnan loo ndawo ubunzima beeshekele zegolide ezibunzima bumakhulu mathandathu.', 'XHO22'),
(17, 21, 26, 'Wakhela apho ke uDavide isibingelelo kuYehova, wanyusa amadini anyukayo neyombulelo, wanqula uYehova; wamphendula ngomlilo ophuma ezulwini, phezu kwesibingelelo sedini elinyukayo.', 'XHO22'),
(17, 21, 27, 'Wathetha uYehova kwisithunywa; walifaka ikrele esingxotyeni sakhe.', 'XHO22'),
(17, 21, 28, 'Ngelo xesha akubona uDavide ukuba uYehova umphendule ngesibetho sikaOrnan umYebhusi, wabingelela apho.', 'XHO22'),
(17, 21, 29, 'Kuba ngelo xesha umnquba kaYehova awawenzayo uMoses entlango nesibingelelo somnikelo otshiswayo zazikwindawo ephakamileyo yaseGibheyon.', 'XHO22'),
(17, 21, 30, 'Kodwa uDavide akazange akwazi ukumkhokelela ekubeni abuze kuThixo, kuba wayesoyika ikrele lengelosi kaYehova.', 'XHO22'),
(17, 22, 1, 'Wathi uDavide, Yiyo le indlu kaYehova uThixo; siso esi isibingelelo samadini anyukayo kwaSirayeli.', 'XHO22'),
(17, 22, 2, 'Wathi uDavide mabahlanganiswe abasemzini, abasezweni lakwaSirayeli; waqesha abaqingqi bamatye, ukuba bakrole amatye axholiweyo, okwakha indlu kaThixo.', 'XHO22'),
(17, 22, 3, 'uDavide walungisa isinyithi sasininzi sezikhonkwane zeengcango zamasango nezezinyuko; nobhedu oluninzi, alwaba nakulinganiswa;', 'XHO22'),
(17, 22, 4, 'nemisedare yamininzi; ngokuba abaseTsidon nabaseTire bamzisele yaninzi imisedare uDavide.', 'XHO22'),
(17, 22, 5, 'Wandula ke wathi uDavide: “uSolomon unyana wam mncinane yaye usemtsha, yaye indlu eza kwakhelwa uYehova ifanele ibe yeyeyona inkulu, idumileyo nezukileyo kuwo onke amazwe; Ngoko uDavide walungiselela okuninzi ngaphambi kokufa kwakhe.', 'XHO22'),
(17, 22, 6, 'Emva koko wabiza unyana wakhe uSolomon, wamyalela ukuba amakhele indlu uYehova uThixo kaSirayeli.', 'XHO22'),
(17, 22, 7, 'Wathi uDavide kuSolomon, Nyana wam, ndandizimisele ukulakhela indlu igama likaYehova uThixo wam.', 'XHO22'),
(17, 22, 8, 'Ke lafika kum ilizwi likaYehova, lisithi, Uphalaze igazi elininzi, ulwe iimfazwe ezinkulu; akuyi kulakhela ndlu igama lam, ngokuba uphalaze igazi elininzi ehlabathini phambi kwamehlo am.', 'XHO22'),
(17, 22, 9, 'Yabona, uya kuzalelwa unyana; yena uya kuba yindoda yoxolo; yaye ndiya kumphumza ezintshabeni zakhe zonke ezimjikelezileyo, ngenxa yokuba igama lakhe liya kuba nguSolomon, yaye ndiya kumnika uSirayeli uxolo nokuphumla ngemihla yakhe.', 'XHO22'),
(17, 22, 10, 'Nguye ke oya kulakhela igama lam indlu; yena uya kuba ngunyana wam, mna ndibe nguyise kuye; yaye ndiya kusimisa isihlalo sobukumkani bakhe kwaSirayeli ukusa kwixesha elingenammiselo.', 'XHO22'),
(17, 22, 11, 'Ke kaloku, nyana wam, ngamana uYehova angaba nawe! ube nempumelelo, uyakhe indlu kaYehova uThixo wakho, njengoko wakuthethayo kuwe.', 'XHO22'),
(17, 22, 12, 'NguYehova kuphela okunika ubulumko nokuqonda, nokukuyalela ngoSirayeli, ukuze uwugcine umthetho kaYehova uThixo wakho.', 'XHO22'),
(17, 22, 13, 'Uya kuphumelela apho, ukuba uthe wakugcina ukuyenza imithetho namasiko, awathi uYehova wamwisela uMoses umthetho ngawo ngenxa yamaSirayeli. Yomelela, ukhaliphe; musani ukoyika, musani ukoyika.', 'XHO22'),
(17, 22, 14, 'Uyabona, ekubandezelekeni kwam ndiyilungisele indlu kaYehova ikhulu lamawaka eetalente zegolide, newaka leetalente zesilivere; nobhedu nesinyithi, abungenakulinganiswa nanto; ngokuba izele; Kananjalo ndalungisa imithi namatye; kwaye ungayongeza.', 'XHO22'),
(17, 22, 15, 'Kananjalo baninzi kuwe abasebenzi, abaxholi, nabasebenza ngamatye nemithi, namadoda onke angamagcisa emsebenzini wonke.', 'XHO22'),
(17, 22, 16, 'Igolide, nesilivere, nobhedu, nesinyithi, azinakubalwa yimani ngoku, niyenze, uYehova woba nawe.', 'XHO22'),
(17, 22, 17, 'Waphinda uDavide wabawisela umthetho zonke iinkosana zakwaSirayeli, ukuba zincede uSolomon unyana wakhe, wathi:', 'XHO22'),
(17, 22, 18, 'Akanani na uYehova uThixo wenu? Akaniphumzanga na? ngokuba abemi behlabathi nibasandleni sam; yaye ilizwe loyiswa phambi koYehova naphambi kwabantu bakhe.', 'XHO22'),
(17, 22, 19, 'Ke kaloku zimiseleni ukumquqela uYehova uThixo wenu ngentliziyo yenu nangomphefumlo wenu; yimani niyakhe ingcwele kaYehova uThixo, ukuba iziswe ityeya yomnqophiso kaYehova, neempahla ezikhethekileyo zikaThixo, endlwini eya kwakhelwa igama likaYehova.', 'XHO22'),
(17, 23, 1, 'Ke kaloku, xa uDavide wayesel’ aluphele, ehambisekile ebudaleni, wamenza uSolomon unyana wakhe ukumkani kwaSirayeli.', 'XHO22'),
(17, 23, 2, 'Wazihlanganisa zonke izikhulu zakwaSirayeli nababingeleli nabaLevi.', 'XHO22'),
(17, 23, 3, 'Oonyana bakaSirayeli ukususela eBher-shebha ukusa kwaDan nenani labo ngokwenani labo, indoda ngendoda lalingamashumi amathathu anesibhozo amawaka.', 'XHO22'),
(17, 23, 4, 'Kubo amashumi amabini anesine amawaka aza kuqhubeka nomsebenzi wendlu kaYehova; amawaka amathandathu ngabaphathi nabagwebi;', 'XHO22'),
(17, 23, 5, 'Ngaphezu koko, babengamawaka amane abagcini-masango; bobane mabadumise uYehova ngeempahla endazenzayo, watsho uDavide, ukuba babadumise.', 'XHO22'),
(17, 23, 6, 'uDavide wabahlula bazizigaba ngokoonyana bakaLevi, yanguGershon, noKehati, noMerari.', 'XHO22'),
(17, 23, 7, 'Koonyana bakaGershon nguLadan noShimehi;', 'XHO22'),
(17, 23, 8, 'Oonyana bakaLadan, iintloko, nguYehiyeli, noSetam, noYoweli;', 'XHO22'),
(17, 23, 9, 'Oonyana bakaShimehi yayinguAsh. uShelomiti noHasiyeli noHaran bathathu. Zizo ezo iintloko zezindlu zooyise zakwaLaedan.', 'XHO22'),
(17, 23, 10, 'Oonyana bakaShimehi nguYahati, noSina, noYewuse, noBheriya. Abo bane yayingoonyana bakaShimehi.', 'XHO22'),
(17, 23, 11, 'uYahati wayengomkhulu, noSisa owesibini; ke uYehuse noBheriya bebengenanyana baninzi; ngenxa yoko baba nenani elinye ngokwezindlu zooyise.', 'XHO22'),
(17, 23, 12, 'Oonyana bakaKehati nguAmram, noItsare, noHebron, noUziyeli, bebane.', 'XHO22'),
(17, 23, 13, 'Oonyana baka-Amram nguAsheri; uAron, noMoses, noAron, banyulwa ukuba bazingcwalise izinto ezingcwele, kude kuse ephakadeni, yena noonyana bakhe, baqhumisele ngesiqhumiso phambi koYehova, balungiselele kuye, basikelele egameni lakhe kude kuse ephakadeni.', 'XHO22'),
(17, 23, 14, 'Ngokuphathelele uMoses, umfo wakwaThixo, oonyana bakhe babizwa kwisizwe sakwaLevi.', 'XHO22'),
(17, 23, 15, 'Oonyana bakaMoses yayinguGershom noEliyezere.', 'XHO22'),
(17, 23, 16, 'Koonyana bakaGershom yayinguShebhuele intloko.', 'XHO22'),
(17, 23, 17, 'Oonyana bakaEliyezere nguRehabheya inkulu. uEliyezere akabanga nanyana bambi; ke oonyana bakaRehabheya bebebaninzi kunene.', 'XHO22'),
(17, 23, 18, 'Koonyana bakaItsahare; uShelomite ukumkani.', 'XHO22'),
(17, 23, 19, 'Koonyana bakaHebron; Owokuqala nguYeriya, owesibini nguAmariya, owesithathu nguYahaziyeli, owesine nguYekameyam.', 'XHO22'),
(17, 23, 20, 'Koonyana bakaUziyeli yanguShumiyeli; owokuqala nguMika, noYesiya;', 'XHO22'),
(17, 23, 21, 'Oonyana bakaMerari nguMali noMushi oonyana bakaMagali; uElazare noKis.', 'XHO22'),
(17, 23, 22, 'Wafa uElazare engenanyana, eneentombi zodwa; bababamba abazalwana babo, oonyana bakaKishe.', 'XHO22'),
(17, 23, 23, 'Oonyana bakaMushi; uMageli, noEdere, noYeremoti, bebathathu.', 'XHO22'),
(17, 23, 24, 'Ngabo abo oonyana bakaLevi ngokwezindlu zooyise; neentloko zezindlu zooyise ngenani lamagama, ngokwamagama abo, besenza inkonzo yendlu kaYehova, bethabathela kominyaka imashumi mabini ezelwe, banyuse.', 'XHO22'),
(17, 23, 25, 'Ngokuba wathi uDavide, uYehova, uThixo kaSirayeli, ubaphumzile abantu bakhe, ukuze bahlale eYerusalem kude kuse ephakadeni.', 'XHO22'),
(17, 23, 26, 'NakubaLevi; bangabi sawuthwala umnquba, neempahla zawo zenkonzo.', 'XHO22'),
(17, 23, 27, 'Ngokuba ngokwamazwi kaDavide okugqibela, abaLevi babalwa, kwathabathela kwabaminyaka imashumi mabini bezelwe, kwenyusa.', 'XHO22'),
(17, 23, 28, 'Kuba umsebenzi wabo yayikukukhonza oonyana baka-Aron enkonzweni yendlu kaYehova, ezintendelezweni nasemagumbini okudlela, nokuhlambulula zonke izinto ezingcwele nomsebenzi wenkonzo yendlu kaThixo oyinyaniso:', 'XHO22'),
(17, 23, 29, 'nezonka ezingenagwele, nezonka ezingenagwele, nezonka ezingenagwele, nezonka ezingenagwele, nezonka ezingenagwele, nezonka ezigcadiweyo, nezilinganiso, nesayizi;', 'XHO22'),
(17, 23, 30, 'Vukani imiso ngemiso, nidumise, nidumise uYehova, kwangokunjalo ngokuhlwa;', 'XHO22'),
(17, 23, 31, 'nowokunyusa onke amadini anyukayo kuYehova ngeesabatha, nasekuthwaseni kwenyanga, nangamaxesha emithendeleko, ngokwenani, ngokomthetho abewuwiselwe umthetho, amaxesha onke phambi koYehova.', 'XHO22'),
(17, 23, 32, 'Yaye babenyamekela inkonzo yentente yokuhlangana nenkonzo yengcwele nenkonzo yoonyana baka-Aron, abazalwana babo, enkonzweni yendlu kaYehova.', 'XHO22'),
(17, 24, 1, 'Ngawo la ke amaqela oonyana baka-Aron; uNadabhi noAbhihu, uElazare noItamare.', 'XHO22'),
(17, 24, 2, 'Bafa ooNadabhi noAbhihu phambi koyise, bengenanyana; ngoko ke, uElazare noItamare babengababingeleli.', 'XHO22'),
(17, 24, 3, 'Wabahlula uDavide: yanguTsadoki koonyana bakaElazare, yanguAhimeleki koonyana bakaItamare, ngokwabakhonzi babo ekonzweni yabo.', 'XHO22'),
(17, 24, 4, 'Kwafunyanwa ukuba koonyana bakaElazare, amadoda abalaseleyo kunoonyana bakaItamare; baza ke ngoko bohlukana. Koonyana bakaElazare, iintloko zezindlu zooyise zaba lishumi elinantandathu; oonyana bakaItamare ngokwemizalwane yooyise, abasibhozo.', 'XHO22'),
(17, 24, 5, 'Babahlula ngamaqashiso, aba baba naba; ngokuba abathetheli bengcwele, nabaphathi bendlu kaThixo, yayikoonyana bakaElazare, nakoonyana bakaItamare.', 'XHO22'),
(17, 24, 6, 'uShemaya unyana kaNataniyeli, umbhali kubaLevi, wayibhala phambi kokumkani, nabathetheli, noTsadoki umbingeleli, noAhimeleki unyana ka-Abhiyatare, naphambi kweentloko zezindlu zooyise zababingeleli nezabaLevi elinye libe lelikaElazare, elinye libe lelikaItamare.', 'XHO22'),
(17, 24, 7, 'Iqashiso lokuqala laba noYoyaribhi, elesibini noYedaya;', 'XHO22'),
(17, 24, 8, 'elesithathu noHarim, elesine noSeorim;', 'XHO22'),
(17, 24, 9, 'elesihlanu noMalekiya, elesithandathu noMiyamin;', 'XHO22'),
(17, 24, 10, 'owesixhenxe wazala uHakotsi, owesibhozo uAbhiya;', 'XHO22'),
(17, 24, 11, 'owesithoba wazala uYeshuwa, oweshumi waba nguShekaniya;', 'XHO22'),
(17, 24, 12, 'oweshumi elinanye wazala uEliyashibhi, oweshumi elinesibini uYakim;', 'XHO22'),
(17, 24, 13, 'eleshumi elinesithathu noHupa, eleshumi elinesine noIzebhehabhi;', 'XHO22'),
(17, 24, 14, 'eleshumi elinesihlanu noBhiliga, eleshumi elinesithandathu noImere;', 'XHO22'),
(17, 24, 15, 'eleshumi elinesixhenxe noHesire, eleshumi elinesibhozo noAfese;', 'XHO22'),
(17, 24, 16, 'eleshumi elinesithoba likaPetaya, elamashumi amabini kuHezekile;', 'XHO22'),
(17, 24, 17, 'elamashumi omabini ananye noYagin, elamashumi omabini anesibini noGamule;', 'XHO22'),
(17, 24, 18, 'elamashumi omabini anesithathu noDelaya, elamashumi omabini anesine kumaAsiya.', 'XHO22'),
(17, 24, 19, 'Lulo ke olo ulwalathiso lwenkonzo yabo yokungena endlwini kaNdikhoyo ngokwesiko labo phantsi koyise uAron, ngokomyalelo kaNdikhoyo uThixo kaSirayeli.', 'XHO22'),
(17, 24, 20, 'Ngabo aba oonyana bakaLevi: koonyana baka-Amram; koonyana bakaTsubhayeli; Yehdeja.', 'XHO22'),
(17, 24, 21, 'Ke uRehabhiya: koonyana bakaRehabhiya, owamazibulo nguYese.', 'XHO22'),
(17, 24, 22, 'KumaIshtar; koonyana bakaShelomoti; eYahati.', 'XHO22'),
(17, 24, 23, 'Oonyana bakaHebron nguYeriya inkulu, noAmariya owesibini, noYahaziyeli owesithathu, noYekamam owesine.', 'XHO22'),
(17, 24, 24, 'Koonyana bakaUziyeli yangu koonyana bakaMika nguShamire.', 'XHO22'),
(17, 24, 25, 'Umninawa kaMika nguYese; koonyana bakaYese; uZekariya.', 'XHO22'),
(17, 24, 26, 'Oonyana bakaMerari nguMagali noMushi; oonyana bakaYasiya; Beno.', 'XHO22'),
(17, 24, 27, 'Oonyana bakaMerari ngoYahaseya; uBeno noSoham noZakure noIbri.', 'XHO22'),
(17, 24, 28, 'KwaMageli kwaphuma uElazare, owayengenanyana.', 'XHO22'),
(17, 24, 29, 'Ke yena uKishe, unyana kaKishe nguYeramiyeli.', 'XHO22'),
(17, 24, 30, 'Oonyana bakaMushi; uMageli noEdere noYerimoti. Ngabo abo oonyana babaLevi, ngokwezindlu zooyise.', 'XHO22'),
(17, 24, 31, 'Nabo benza amaqashiso ngabazalwana babo, oonyana baka-Aron, phambi koKumkani uDavide noTsadoki noAhimeleki neentloko zeentsapho zababingeleli nezabaLevi, iintloko zooyise kubazalwana babo, bona bancinane.', 'XHO22'),
(17, 25, 1, 'Kananjalo uDavide nabathetheli bamaqela bazinyulela inkonzo yoonyana baka-Asafu, noHeman, noYedutun, beprofeta ngeehadi, nangeehadi, namacangci; inani labasebenzi ngokwenkonzo yabo laba.', 'XHO22'),
(17, 25, 2, 'Koonyana baka-Asafu yangu nguSakure, noYosefu, noNetaniya, noAsharela, oonyana baka-Asafu, bephethwe nguAsafu, obeprofeta ngomthetho wokumkani.', 'XHO22'),
(17, 25, 3, 'KuYedutun: oonyana bakaYedutun; uGedaliya, uSeri, uIsaya, uHashabhiya noMatitiya, abathandathu, ezandleni zikayise uYedutun, owayeprofeta ngencakuba ukudumisa nokudumisa uYehova.', 'XHO22'),
(17, 25, 4, 'KuHeman: oonyana bakaHeman; noBhukiya, noMataniya, noUziyeli, noZebhuweli, noYerimoti, noHananiya, noHanani, noEliyata, noGidaleti, noRomametisere, noYoshebheka, noMaloti, noHotire, noMahashiyoti;', 'XHO22'),
(17, 25, 5, 'Bonke babengoonyana bakaHeman, imboni yokumkani, emazwini kaThixo oyinyaniso, ukuba aluphakamise uphondo. uThixo wamnika uHeman oonyana abalishumi elinabane, neentombi ezintathu.', 'XHO22'),
(17, 25, 6, 'Bonke babephantsi kwesandla sikayise ekuculeni endlwini kaYehova ngamacangci nangeehadi neehadi zenkonzo yendlu yoTHIXO oyinyaniso, ngokomyalelo kakumkani kuAsafu, uYedutun noYedutun. uHeman.', 'XHO22'),
(17, 25, 7, 'Inani labo kunye nabazalwana babo ababefundiswe ukuvuma iingoma zikaYehova, bonke abazizilumko, lalingamakhulu amabini anamashumi asibhozo anesibhozo.', 'XHO22'),
(17, 25, 8, 'Bawisa amaqashiso amadoda amakhulu, amadoda namakhulu, nabafundi bakhe.', 'XHO22'),
(17, 25, 9, 'Iqashiso lokuqala laphuma noAsafu, noYosefu: yena, nabazalwana bakhe, noonyana bakhe, baba lishumi elinababini;', 'XHO22'),
(17, 25, 10, 'elesithathu noSakure: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 11, 'elesine noIsri: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 12, 'elesihlanu noNetaniya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 13, 'elesithandathu noBhukiya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 14, 'elesixhenxe noYetsarela: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 15, 'elesibhozo noIsaya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 16, 'elesithoba noMataniya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 17, 'eleshumi noShimehi: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 18, 'eleshumi elinanye noAzareli: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 19, 'eleshumi elinesibini noHashabhiya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 20, 'eleshumi elinesithathu noTsubhayeli: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 21, 'eleshumi elinesine noMatitiya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 22, 'eleshumi elinesihlanu noYeremoti: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 23, 'eleshumi elinesithandathu noHananiya: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 24, 'eleshumi elinesixhenxe noYoshebhekasa: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 25, 'eleshumi elinesibhozo noHanani: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 26, 'eleshumi elinesithoba noMaloti: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 27, 'elamashumi omabini noEliyata: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 28, 'elamashumi omabini analinye noHotire: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 29, 'elamashumi omabini anesibini noGidaleti: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 30, 'elamashumi omabini anesithathu noMahashiyoti: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini;', 'XHO22'),
(17, 25, 31, 'elamashumi omabini anesine noRomamtisere: yena noonyana bakhe nabazalwana bakhe baba lishumi elinababini.', 'XHO22'),
(17, 26, 1, 'Ke amaqela abamasango: kumaKorogi nguMeshelemiya unyana kaKora, koonyana baka-Asafu.', 'XHO22'),
(17, 26, 2, 'Oonyana bakaMeshelemiya nguZekariya owamazibulo, noYedihayeli owesibini, noZebhadiya owesithathu, noYateniyeli owesine;', 'XHO22'),
(17, 26, 3, 'owesihlanu uElam, owesithandathu nguYohanan, wesixhenxe uEliyonayi.', 'XHO22'),
(17, 26, 4, 'Oonyana bakaObhedi-edom nguShemaya inkulu, noYozabhadi owesibini, noYowaki owesithathu, noSakare owesine, noNataniyeli owesihlanu;', 'XHO22'),
(17, 26, 5, 'owesithandathu nguAmiyeli, owesixhenxe nguIsakare, wesibhozo nguPeulthayi; ngokuba uThixo ebemsikelele.', 'XHO22'),
(17, 26, 6, 'uShemaya unyana wakhe naye wazala oonyana abalawula indlu kayise iphela, kuba babengamagorha anobukroti.', 'XHO22'),
(17, 26, 7, 'Oonyana bakaShemaya nguShemaya; uOtni noRefayeli no-Obhedi, uElisabadi, abazalwana bakhe babengamadoda anamandla, uElihu noSemágya.', 'XHO22'),
(17, 26, 8, 'Bonke aba yayingoonyana bakaObhedi-edom, bona noonyana babo nabazalwana babo, amadoda anamandla asebenza enkonzweni, ababengamashumi amathandathu anesibini kuObhedi-edom.', 'XHO22'),
(17, 26, 9, 'uMeshelemiya ubenoonyana abalishumi elinesibhozo nabazalwana, amadoda anobukroti.', 'XHO22'),
(17, 26, 10, 'uHosa, omnye koonyana bakaMerari, ubenoonyana; WayenguZimri, intloko, (ngokuba wayengengowamazibulo, nguyise wamenza ukumkani);', 'XHO22'),
(17, 26, 11, 'owesibini nguHilekiya, owesithathu nguTebhaliya, owesine nguZekariya; bebonke oonyana bakaHosa nabazalwana babo baba lishumi elinabathathu.', 'XHO22'),
(17, 26, 12, 'Phakathi kwabo kwakukho amaqela abagcini-masango, phakathi kweenkokeli ezazinabalindi ukuze bakhonze endlwini kaYehova.', 'XHO22'),
(17, 26, 13, 'Bawisa amaqashiso, ethabathela komncinane esa koyena mkhulu, ngokwezindlu zooyise, isango ngalinye.', 'XHO22'),
(17, 26, 14, 'Iqashiso lasempumalanga laphuma noSelemiya; Bamwisela amaqashiso uZekariya unyana wakhe, umphakathi oqiqileyo; laphuma iqashiso lakhe langasentla;', 'XHO22'),
(17, 26, 15, 'uObhedi-edom ngasemzantsi; nakoonyana bakhe indlu ka-Asupim.', 'XHO22'),
(17, 26, 16, 'Iqashiso likaSupime noHosa laphumela ngasentshona, nesango laseSalege, ngendlela enyukayo, ukuze kugadwe abalindi.', 'XHO22'),
(17, 26, 17, 'Ngasempumalanga babebathandathu abaLevi; ngasentla bebane imihla ngemihla; ngasezantsi bebane imihla ngemihla;', 'XHO22'),
(17, 26, 18, 'iParebhare ngasentshona, ezine zisendleleni, ezimbini ziseParbhare.', 'XHO22'),
(17, 26, 19, 'Ngawo lawo amaqela abagcini-masango phakathi koonyana bakaKore noonyana bakaMerari.', 'XHO22'),
(17, 26, 20, 'Phakathi kwabaLevi, uAhiya wayephethe ubuncwane bendlu yoTHIXO oyinyaniso nobuncwane bezinto ezingcwele.', 'XHO22'),
(17, 26, 21, 'Ke oonyana bakaLaedan; Oonyana bakaLaedan umGershon, iintloko zezindlu zooyise zikaLaedan umGershon nguYehiyeli.', 'XHO22'),
(17, 26, 22, 'Oonyana bakaYehiyeli nguAsheri; uSetam nomntakwabo uYoweli, ababephethe ubuncwane bendlu kaYehova.', 'XHO22'),
(17, 26, 23, 'Koonyana baka-Amram noIshre nguHebron noBhawusile;', 'XHO22'),
(17, 26, 24, 'uSebhuweli, unyana kaGershom, unyana kaMoses, ebengumgcini wobuncwane.', 'XHO22'),
(17, 26, 25, 'Abazalwana bakhe bakwaEliyezere; unyana wakhe uRehabhiya, uIsaya unyana wakhe, uYoram unyana wakhe, uZigri unyana wakhe, uShelomoti unyana wakhe.', 'XHO22'),
(17, 26, 26, 'Ukuba uShelomoti nabazalwana bakhe babephethe bonke ubuncwane bezinto ezingcwele ezazingcwaliswe nguKumkani uDavide neentloko zeentsapho zabo nabathetheli bamawaka nabamakhulu nabathetheli bamabhunga.', 'XHO22'),
(17, 26, 27, 'Ke amaxhoba athinjwa emfazweni, akhetha ukuyinyamekela indlu kaYehova.', 'XHO22'),
(17, 26, 28, 'Nazo zonke izinto abezingcwalisile uSamuweli imboni, noSawule unyana kaKishe, noAbhinere unyana kaNere, noYowabhi unyana kaTseruya; wonk’ ubani owayengcwalisa enye into wayephantsi kwesandla sikaShelomoti nabazalwana bakhe.', 'XHO22'),
(17, 26, 29, 'Koonyana bakaIshhariya, uKenaniya noonyana bakhe babe ngabathetheli nabagwebi bemicimbi yakwaSirayeli ngaphandle.', 'XHO22'),
(17, 26, 30, 'KumaHebhere, uHashabhiya nabazalwana bakhe, amadoda anobukroti, abaliwaka elinamakhulu asixhenxe, babenabaphathi phakathi koonyana bakaSirayeli ngaphesheya kweYordan ngasentshona kuwo wonke umsebenzi kaYehova nasenkonzweni kakumkani.', 'XHO22'),
(17, 26, 31, 'KumaHebhere nguYeriya, intloko yamaHebhere ngokweenzala zooyise. Ngomnyaka wamashumi omane wobukumkani bukaDavide bafunwa, kwafunyanwa kubo amagorha eYazere yaseGiliyadi.', 'XHO22'),
(17, 26, 32, 'Abazalwana bakhe, amadoda akhaliphileyo, iintloko zeentsapho zabo babengamawaka amabini anamakhulu asixhenxe, awathi uKumkani uDavide wababeka phezu kwamaRubhen namaGadi nesiqingatha sesizwe sakwaManase kuwo wonke umsebenzi kaThixo nawo wonke umsebenzi kaTHIXO oyinyaniso ikumkani.', 'XHO22'),
(17, 27, 1, 'Oonyana bakaSirayeli ngokwenani labo, abaziintloko zezindlu zooyise, nabathetheli bamawaka nabamakhulu, nabagcini bomkhosi, abalungiselela ukumkani ngokwamaqela onke, abahamba inyanga nenyanga ngenyanga, ngeenyanga yonke into ngonyaka, iqela ngalinye lalingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 2, 'Umphathi weqela lokuqala ngenyanga yokuqala yayinguYasobham unyana kaSabhediyeli, yaye kwisahlulo sakhe yayingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 3, 'Koonyana bakaPeretse wayengumphathi wabo bonke abaphathi bomkhosi ngenyanga yokuqala.', 'XHO22'),
(17, 27, 4, 'Ngenyanga yesibini uDodayi engumMowaki, intloko yebandla lakhe ibinguMikeloti; eqeleni lakhe ibingamashumi amabini anamané amawaka.', 'XHO22'),
(17, 27, 5, 'Umphathi wesithathu kweyesithathu inyanga yayinguBhenaya unyana kaYehoyada umbingeleli omkhulu; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 6, 'Nguye lowo uBhenaya, eyindoda enkulu phakathi kwamashumi omathathu lawo, inamashumi mathathu; unyana wakhe nguAmisabhadi kwiqela lakhe.', 'XHO22'),
(17, 27, 7, 'Owesine ngenyanga yesine ibinguAsaheli umninawa kaYowabhi, inguZebhadiya unyana wakhe emva kwakhe; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 8, 'Owesihlanu umthetheli ngenyanga yesihlanu ibinguSamhuti wakwaMoiserahi; eqeleni lakhe ibinamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 9, 'Owesithandathu ngenyanga yesithandathu ibinguIra unyana kaIkeki waseMotekowa; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 10, 'Umlawuli wesixhenxe wenyanga yesixhenxe yayinguHele wasePelone woonyana bakaEfrayim; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 11, 'Owesibhozo kweyesibhozo inyanga yayinguSibhegayi uMoseti waseBhasharge; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 12, 'Owesithoba ngenyanga yesithoba ibinguAbhiyezere waseManatoti wesizwe sakwaBhenjamin; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 13, 'Oweshumi ngenyanga yeshumi ibinguMaharayi waseMonetofate waseBhaseregi; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 14, 'Oweshumi elinamnye ngenyanga yeshumi elinanye ibinguBhenaya wasePiraton woonyana bakaEfrayim; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 15, 'Oweshumi elinesibini ngenyanga yeshumi elinesibini ibinguHeledayi waseMenotefa waseOtheniyeli; Kwiqela lakhe ibingamashumi amabini anesine amawaka.', 'XHO22'),
(17, 27, 16, 'Kananjalo ezizweni zakwaSirayeli: ukumkani woonyana bakaRubhen nguEliyezere, unyana kaSigri; koonyana bakaSimon nguShefatiya unyana kaMaki;', 'XHO22'),
(17, 27, 17, 'kubaLevi nguHashabhiya unyana kaKemuweli; koonyana baka-Aron yanguTsadoki;', 'XHO22'),
(17, 27, 18, 'KwaYuda, uElihu omnye wabantakwabo Davide; kuIsakare, inguOmri, unyana kaMikayeli;', 'XHO22'),
(17, 27, 19, 'kwaZebhulon nguIshmaya unyana kaObhadiya; kwaNafetali nguYerimoti unyana ka-Aseriyeli;', 'XHO22'),
(17, 27, 20, 'koonyana bakaEfrayim nguHoseya unyana ka-Asaya; kwisiqingatha sesizwe sakwaManase nguYoweli, unyana kaPedaya;', 'XHO22'),
(17, 27, 21, 'kwisiqingatha sesizwe sakwaManase eGiliyadi nguIdo unyana kaZekariya; kwaBhenjamin nguYahaziyeli unyana ka-Abhinere;', 'XHO22'),
(17, 27, 22, 'kwaDan nguAzareli unyana kaYeroham; Ezi yayiziinkosana zezizwe zakwaSirayeli.', 'XHO22'),
(17, 27, 23, 'Kodwa uDavide akazange alibale inani labaneminyaka engamashumi amabini ubudala nangaphantsi, kuba uYehova wayethe uya kubandisa oonyana bakaSirayeli njengeenkwenkwezi zezulu.', 'XHO22'),
(17, 27, 24, 'uYowabhi unyana kaTseruya waqala ukubala, akagqiba, ngokuba umsindo waya koonyana bakaSirayeli ngenxa yabo; yaye elo nani alibhalwanga encwadini yembali yokumkani uDavide.', 'XHO22'),
(17, 27, 25, 'Ophethe ubuncwane bokumkani nguAsmavete unyana ka-Adiyeli; uJonatan unyana ka-Uziya wayephethe ubuncwane bamasimi, nemizi, nemizi, neenqaba.', 'XHO22'),
(17, 27, 26, 'Obephethe abenza umsebenzi wokulima umhlaba nguEzeri unyana kaKelubhi.', 'XHO22'),
(17, 27, 27, 'Obephethe izidiliya nguShimehi waseRama; ngenxa yokukhula kwemidiliya yokugcina iwayini yayinguSabdi Mosifi.', 'XHO22'),
(17, 27, 28, 'Owayephethe imithi yomnquma namakhiwane elizwe elisentlanjeni yayinguBhahali-hanan umGedere, noYowashe wayephethe iintsika zeoli.', 'XHO22'),
(17, 27, 29, 'Ophethe umhlambi odla eSharon nguSiterayi umSharon; yaye ophethe umhlambi owawusezintlanjeni yayinguShafati unyana ka-Adilayi.', 'XHO22'),
(17, 27, 30, 'Owayephethe iinkamela yayinguObhile umIshmayeli, ophethe iimazi zamaesile yayinguYediya umMeronoti.', 'XHO22'),
(17, 27, 31, 'Owayephethe umhlambi yayinguJasise Mohagere. Bonke babengabalawuli bezinto zikaKumkani uDavide.', 'XHO22'),
(17, 27, 32, 'uYonatan, umninawa kaDavide, ubengumphakathi, indoda esisilumko, umbhali; yaye uYehiyeli unyana kaHagmon wayenoonyana bokumkani.', 'XHO22'),
(17, 27, 33, 'uAhitofele, obengumaphakathi kakumkani, noHushayi umAram, ube lidlelane lokumkani.', 'XHO22'),
(17, 27, 34, 'Emva koAhitofele yayinguYehoyada unyana kaBhenaya noAbhiyatare; injengele yomkhosi wokumkani ibinguYowabhi.', 'XHO22'),
(17, 28, 1, 'uDavide wabamisa zonke iinkosana zakwaSirayeli, iinkosana zezizwe, nabathetheli bamaqela omkhosi olungiselela ukumkani, nabathetheli bamawaka, nabathetheli-makhulu, nabagcini bempahla yonke yokumkani, nobuncwane bakhe, neempahla zokumkani ubutyebi koonyana bakhe, nabathetheli, namagorha, namagorha onke, besa eYerusalem.', 'XHO22'),
(17, 28, 2, 'Waphakama ukumkani uDavide, wathi: “Ndiphulaphuleni, bazalwana bam nani bantu bam! isihlalo seenyawo zoThixo wethu, silungiselwe ukwakha.', 'XHO22'),
(17, 28, 3, 'Wathi uThixo kum, Akuyi kulakhela ndlu igama lam, ngokuba uligorha, uphalaze igazi;', 'XHO22'),
(17, 28, 4, 'Ke uYehova, uThixo kaSirayeli, unyule mna phezu kwendlu yonke kabawo, ukuba ndibe ngukumkani wakwaSirayeli kude kuse ephakadeni, ngokuba wamnyula uYuda; nakwindlu kaYuda, kwindlu kabawo; yaye wayebathanda oonyana bakabawo kangangokuba ndazenza ukumkani kuSirayeli wonke.', 'XHO22'),
(17, 28, 5, 'Koonyana bam bonke, ekubeni undinike oonyana abaninzi uYehova, unyule uSolomon, unyana wam, ukuba ahlale etroneni yobukumkani bukaYehova, awalawule amaSirayeli.', 'XHO22'),
(17, 28, 6, 'Wathi kum, uSolomon, unyana wakho, nguye oya kulakhela indlu yam namasimi am; ngokuba ndizinyulele yena ukuba abe ngunyana wam, ndibe nguyise kuye mna;', 'XHO22'),
(17, 28, 7, 'ndibuzinzise ubukumkani bakhe kude kuse ephakadeni, ukuba uthe wayimisa imimiselo yam nemiyalelo yam, njengoko kunjalo namhla.', 'XHO22'),
(17, 28, 8, 'Ke kaloku, phambi kwamehlo kaSirayeli wonke, yimani indibano kaYehova naphambi kwendlebe yoThixo wethu, niyiphengulule yonke imiyalelo kaYehova uThixo wenu, ukuze nilidle ilifa eli lizwe lihle’ nize niyisebenzise libe lilifa koonyana bakho emva kwakho ngonaphakade.', 'XHO22'),
(17, 28, 9, 'Ke wena, nyana wam Solomon, uze umazi uThixo kayihlo uze umkhonze ngentliziyo epheleleyo nangomoya wothando; ngokuba uYehova uyazicikida zonke iintliziyo, uziqonda zonke iingcamango; ukuba nithe namquqela, nofunyanwa; ukuba uthe watyeka kuye, wonilahla kuphele.', 'XHO22'),
(17, 28, 10, 'Lumka ngoku; ngokuba uYehova ukunyule ukuba wakhe indlu engcwele; yomelela, yenze.', 'XHO22'),
(17, 28, 11, 'uDavide wamnika uSolomon unyana wakhe umfanekiso wefreyimu, nowezindlu zakhe, nowobuncwane, nowamagumbi akhe aphezulu, nesamagumbi akhe angaphakathi, nesesigubungelo;', 'XHO22'),
(17, 28, 12, 'nomzekelo wezinto zonke awayenazo emoyeni, oweentendelezo zendlu kaYehova nowamagumbi onke ajikelezileyo, nowobuncwane bendlu yoThixo oyinyaniso, nobuncwane bezipho ezingcwele;', 'XHO22'),
(17, 28, 13, 'nesamaqela ababingeleli, nabaLevi, nawo wonke umsebenzi wenkonzo yendlu kaYehova, nawo wonke umsebenzi wenkonzo yendlu kaYehova.', 'XHO22'),
(17, 28, 14, 'Ubunzima begolide yeempahla zonke, beempahla zonke zeenkonzo ngeenkonzo; nesilivere yemisebenzi yonke yesilivere, ngokobunzima bayo bemisebenzi yeenkonzo zonke;', 'XHO22'),
(17, 28, 15, 'Ubunzima beziphatho zezibane zegolide, nezibane zazo zegolide ngokobunzima besiphatho sezibane, nezibane zaso; neziphatho zesilivere ngokobunzima bazo, besiphatho sezibane, nezibane zaso, ngokomsebenzi wesiphatho ngasinye;', 'XHO22'),
(17, 28, 16, 'negolide ngokobunzima bayo, beetafile zokucwangcisa izonka, ngokwetafile; nesilivere yeetafile zesilivere;', 'XHO22'),
(17, 28, 17, 'nakwiimbombo zenyama, nezitya zokutshiza, neendebe, negolide ecocekileyo; neendebe zegolide ngokobunzima besitya ngasinye; nesilivere ngokobunzima bendebe ngendebe zesilivere;', 'XHO22'),
(17, 28, 18, 'ubunzima begolide esulungekileyo yesibingelelo sesiqhumiso; nomfanekiso wenqwelo, yeekerubhi zegolide, ezolule amaphiko ayo, zisithelisa ityeya yomnqophiso kaYehova.', 'XHO22'),
(17, 28, 19, 'Zonke ezi zinto, watsho uDavide, uYehova wandityhilela ngokubhaliweyo, ngesandla sakhe esiphezu kwam, yonke imisebenzi yalo mzekeliso.', 'XHO22'),
(17, 28, 20, 'Wathi uDavide kuSolomon unyana wakhe, Yomelela, ukhaliphe, wenze musa ukoyika, musa ukoyika, ngokuba uYehova, uYehova, uThixo wam, unawe; akayi kuyishiya, akayi kubuya umva, ade awugqibe wonke umsebenzi wenkonzo yendlu kaYehova.', 'XHO22'),
(17, 28, 21, 'Yabona, amaqela ababingeleli nabaLevi aba nawe kuyo yonke inkonzo yendlu yoThixo oyinyaniso; abathetheli nabantu bonke baya kuthobela wena ngokugqibeleleyo.', 'XHO22'),
(17, 29, 1, 'Wathi ukumkani uDavide kwibandla lonke, uSolomon unyana wam, owanyulwa nguThixo ukuba abe yedwa, usemtsha, uthambile; ke umsebenzi mkhulu, ngokuba le ndlu yobukumkani asiyiyo eyomntu, yeyabantu kaYehova uThixo.', 'XHO22'),
(17, 29, 2, 'Ke kaloku, ngamandla am onke ndiyilungisele indlu kaThixo wam igolide, ibe yeyezinto zegolide, nesilivere yezinto zesilivere, nobhedu lwezinto zobhedu, izinto zentsimbi, izinto zesinyithi, nezomthi kwizinto; ngamaplanga; amatye ebherilo, namatye axholiweyo, namatye abengezelayo, nawo onke amatye anemibala, nawo onke amatye anqabileyo, namatye emarmore, abe maninzi.', 'XHO22'),
(17, 29, 3, 'Kananjalo ngenxa yokuyithanda kwam indlu yoThixo wam, ndinempahla yam, igolide nesilivere, endiyinike indlu kaThixo wam, ngaphandle kwezinto zonke endizilungisele ingcwele indlu,', 'XHO22'),
(17, 29, 4, 'amawaka amathathu eetalente zegolide yegolide yakwaOfire, amawaka asixhenxe eetalente zesilivere ehluziweyo yokugubungela iindonga zezindlu ngazo;', 'XHO22'),
(17, 29, 5, 'Igolide yegolide, neyesilivere, neyemisebenzi yonke, yenziwe ngesandla seengcibi. Yaye ngubani okulungeleyo ukunikela inkonzo yakhe kuYehova namhlanje?', 'XHO22'),
(17, 29, 6, 'Babingelela ke ngokuqhutywa yintliziyo iintloko zezindlu zooyise, neentloko zezizwe zakwaSirayeli, nabathetheli bamawaka nabamakhulu, nabaphathi bomsebenzi wokumkani;', 'XHO22'),
(17, 29, 7, 'Wanikela amawaka amahlanu eetalente zegolide kwinkonzo yendlu kaThixo, needrarhma ezingamawaka alishumi, nesilivere yaziitalente ezingamawaka alishumi, nobhedu lwaziitalente ezingamawaka alishumi elinesibhozo, nesinyithi saziitalente ezingamawaka alikhulu.', 'XHO22'),
(17, 29, 8, 'Abo kwafunyanwa kubo amatye anqabileyo, bawanikela ebuncwaneni bendlu kaYehova ngesandla sikaYehiyeli Mogershone.', 'XHO22'),
(17, 29, 9, 'Bandula ke abantu baba nemihlali ngenxa yokuba babenikele ngokuqhutywa yintliziyo, ngenxa yokuba babenikela imibingelelo kuYehova ngokuvumayo ngentliziyo epheleleyo; NoKumkani uDavide wavuya ngovuyo olukhulu.', 'XHO22'),
(17, 29, 10, 'uDavide wambonga uYehova phambi kwebandla lonke, esithi, Mawubongwe wena, Yehova, Thixo kabawo wethu, uSirayeli, kude kuse ephakadeni.', 'XHO22'),
(17, 29, 11, 'Yehova, ubukhulu, namandla, nozuko, noloyiso, nobungangamsha bobakho; ngokuba yokwakho yonke into esemazulwini nesemhlabeni; bobakho ubukumkani, Yehova; uphakamile wayintloko yeento zonke.', 'XHO22'),
(17, 29, 12, 'Ubutyebi nozuko luvela kuwe, ulawula zonke izinto; kwaye esandleni sakho kukho amandla namandla; kwaye esandleni sakho kukwandisa nokomelela kubo bonke.', 'XHO22'),
(17, 29, 13, 'Ke kaloku, Thixo wethu, sinokubulela kuwe, sidumise igama lakho elibekekileyo.', 'XHO22'),
(17, 29, 14, 'Kodwa ndingubani na mna, yaye bangoobani na abantu bam, ukuba side senze imibingelelo ngokuqhutywa yintliziyo? ngokuba zonke izinto ziphuma kuwe, sakunika okukokwakho.', 'XHO22'),
(17, 29, 15, 'Ngokuba singabaphambukeli phambi kwakho, silundwendwe njengoobawo bethu bonke;', 'XHO22'),
(17, 29, 16, 'Yehova Thixo wethu, yonke le nkuni siyilungisele ukulakhela indlu igama lakho elingcwele, ivela esandleni sakho, yeyakho yonke.', 'XHO22'),
(17, 29, 17, 'Ndiyazi, Thixo wam, ukuba uyayicikida intliziyo, ukholiswa yinyaniso. Mna ke, ekunyaniseni kwentliziyo yam, ndizincame ngokuqhutywa yintliziyo zonke ezi zinto; yaye ngoku ndibone ngovuyo abantu bakho abakhoyo apha, benikela imibingelelo ngenxa yakho.', 'XHO22'),
(17, 29, 18, 'Yehova, Thixo woobawo bethu, uAbraham, noIsake, noSirayeli, kugcine ngonaphakade kwinkumbulo yeengcinga zentliziyo yabantu bakho, uyibhekise kuwe intliziyo yabo;', 'XHO22'),
(17, 29, 19, 'uSolomon unyana wam umnike intliziyo ephelelisekileyo, yokugcina imithetho yakho, nezilumkiso zakho, nemiyalelo yakho, ayenze yonke, alakhe ibhotwe endililungisileyo.', 'XHO22'),
(17, 29, 20, 'Wathi uDavide kwibandla lonke, Khanimbonge uYehova uThixo wenu. Lonke ibandla lamdumisa uYehova uThixo wooyise, laqubuda, laqubuda kuYehova nakukumkani.', 'XHO22'),
(17, 29, 21, 'Babingelela kuYehova baza banikela iminikelo etshiswayo kuYehova ngosuku olulandelayo, iinkunzi zeenkomo eziliwaka, iinkunzi zeegusha eziliwaka, neemvana eziliwaka, ndawonye neminikelo yazo ethululwayo, nemibingelelo emininzi, ngenxa yamaSirayeli onke.', 'XHO22'),
(17, 29, 22, 'Wadla wasela ngovuyo olukhulu phambi koYehova ngaloo mini. uSolomon unyana kaDavide bamenza ukumkani okwesibini, bamthambisa phambi koYehova ukuba abe yirhuluneli, noTsadoki abe ngumbingeleli.', 'XHO22'),
(17, 29, 23, 'Wahlala ke uSolomon etroneni kaYehova njengokumkani esikhundleni sikaDavide uyise, waqhubeka enempumelelo; yaye wonke uSirayeli wamva.', 'XHO22'),
(17, 29, 24, 'Bonke abathetheli, namagorha, noonyana bonke bokumkani uDavide, bazithoba kukumkani uSolomon.', 'XHO22'),
(17, 29, 25, 'uYehova waqhubeka emkhulisa uSolomon ngeyona ndlela ibalaseleyo emehlweni oonyana bakaSirayeli bonke waza wamnika uzuko lwasebukhosini olwalungazange lubekho kuye nawuphi na ukumkani wakwaSirayeli ngaphambili.', 'XHO22'),
(17, 29, 26, 'Ke uDavide, unyana kaYese, waye engukumkani wamaSirayeli onke.', 'XHO22'),
(17, 29, 27, 'Imihla awalawula ngayo amaSirayeli yaba yiminyaka emashumi mane; Waba neminyaka esixhenxe engukumkani eHebron, yaye walawula eYerusalem iminyaka emashumi mathathu anantathu.', 'XHO22'),
(17, 29, 28, 'Wafa eyindoda enkulu, ehambisekile ebudaleni, ehambisekile ebudaleni, enobutyebi nozuko; unyana wakhe uSolomon waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(17, 29, 29, 'Ke izinto zikakumkani uDavide, ezokuqala nezokugqibela, nanzo zibhaliwe encwadini kaSamuweli imboni, encwadini yomprofeti uNatan, encwadini kaGadi imboni;', 'XHO22'),
(17, 29, 30, 'nalo lonke ulawulo namandla akhe namaxesha adluleyo phezu kwakhe naphezu koSirayeli naphezu kwezikumkani zonke zamazwe.', 'XHO22'),
(18, 1, 1, 'uSolomon, unyana kaDavide, wamomeleza ebukumkanini bakhe; uYehova uThixo wakhe waba naye, wamandisa kunene.', 'XHO22'),
(18, 1, 2, 'Wathetha uSolomon kumaSirayeli onke, kubathetheli bamawaka nabamakhulu, nakubagwebi, kwizikhulu zonke zamaSirayeli onke, kwiintloko zezindlu zooyise.', 'XHO22'),
(18, 1, 3, 'Baya ke ooSolomon nebandla lonke esigangeni esibe siseGibheyon; ngenxa yokuba kwakukho intente yokuhlangana kaThixo eyayisenziwa nguMoses umkhonzi kaYehova entlango.', 'XHO22'),
(18, 1, 4, 'Kodwa uDavide wayalela ukuba ityeya kaThixo inyuswe eKiriyati-yarim iziswe kwindawo uDavide awayeyilungiselele yona, ngenxa yokuba wayemise intente eYerusalem.', 'XHO22'),
(18, 1, 5, 'Wasibeka isibingelelo sobhedu, awasenzayo uBhetsaliyeli unyana kaUri, unyana kaHuri, phambi komnquba kaYehova; Wayifuna ke uSolomon nebandla elo.', 'XHO22'),
(18, 1, 6, 'uSolomon wenyuka waya esibingelelweni sobhedu phambi koYehova esasisententeni yokuhlangana waza wenza iminikelo etshiswayo eliwaka phezu kwaso.', 'XHO22'),
(18, 1, 7, 'uThixo wabonakala kuSolomon ngobo busuku, wathi kuye, Cela into, ndikuphe.', 'XHO22'),
(18, 1, 8, 'Wathi uSolomon kuThixo, Inceba yakho wena wenze kuDavide ubawo, wandenza ukumkani esikhundleni sakhe.', 'XHO22'),
(18, 1, 9, 'Ke kaloku, Yehova Thixo, lizalise idinga owalenza kubawo uDavide, kuba undenze ukumkani wohlanga olukhulu njengothuli lomhlaba.', 'XHO22'),
(18, 1, 10, 'Ke kaloku ndinike ubulumko nokwazi, ukuze ndiphume ndingene phambi kwaba bantu; ngokuba ngubani na ongalugwebayo olu hlanga lwakho lukhulu?', 'XHO22'),
(18, 1, 11, 'Wathi uThixo kuSolomon, Ngenxa enokuba loo nto ibisentliziyweni yakho, akwacela ubutyebi, nendyebo, nozuko, nobomi beentshaba zakho, akwathandaza imihla emininzi; ke ucele ubulumko bakho nokwazi kwakho, ukuze ube nako ukubalawula abantu bam endikumisele njengokumkani wabo.', 'XHO22'),
(18, 1, 12, 'Unikwe ubulumko nokwazi; yaye ngokuqinisekileyo ndiya kukunika ubutyebi nobutyebi nozuko olungazange lubekho kookumkani ababengaphambi kwakho, yaye akuyi kubakho namnye emva kwakho.', 'XHO22'),
(18, 1, 13, 'Wandula ke uSolomon walushiya uhambo lwakhe ukuya kwindawo ephakamileyo eyayiseGibheyon waya eYerusalem, esuka phambi kwentente yokuhlangana, waza waqalisa ukulawula uSirayeli.', 'XHO22'),
(18, 1, 14, 'uSolomon wahlanganisa iinqwelo zokulwa namahashe akhwelwayo, waba newaka elinamakhulu amane eenqwelo zokulwa, namawaka alishumi elinamabini amahashe akhwelwayo;', 'XHO22'),
(18, 1, 15, 'uYehova wayenza yaninzi isilivere negolide njengamatye eYerusalem, nemisedare wayenza yanjengemithombe entlango, ukuba mininzi.', 'XHO22'),
(18, 1, 16, 'uSolomon ubenamahashe aseYiputa, nelinen emhlophe; Abarhwebi bokumkani bamkela ilinen emhlophe ngexabiso.', 'XHO22'),
(18, 1, 17, 'Benyuka ke, bathabatha inqwelo yokulwa eYiputa ngamakhulu amathandathu eeshekele zesilivere, nehashe ngekhulu elinamanci mahlanu; ngoko banikela ngamahashe kubo bonke ookumkani bamaHiti nookumkani bama-Aram ngesandla sabo.', 'XHO22'),
(18, 2, 1, 'uSolomon wacinga ukulakhela indlu igama likaYehova, nokubakhela indlu ubukumkani bakhe.', 'XHO22'),
(18, 2, 2, 'uSolomon wabeka amadoda angamashumi amathandathu amawaka ukuba athwale imithwalo, amashumi asibhozo amawaka ukuze agawule ezintabeni, amawaka amathathu anamakhulu amathandathu ukuba abe ngabalindi.', 'XHO22'),
(18, 2, 3, 'Wathumela uSolomon kuHiram ukumkani waseTire, wathi, Njengoko wenza ngako kuDavide ubawo, wathumela kuye imisedare yokuzakhela indlu yokuba ahlale kuyo, wenjenjalo nakum.', 'XHO22'),
(18, 2, 4, 'Yabona, ndilakhela indlu igama likaYehova uThixo wam, ukuba ndingcwaliswe kuye, ndiqhumisele phambi kwakhe, ndiqhumisele ngezonka ezicwangcisiweyo ngamaxesha onke, namadini anyukayo akusasa nangokuhlwa, kusasa nangokuhlwa. Iisabatha nakwinyanga entsha ucinga ntoni? emithendelekweni kaYehova uThixo wethu. Lowo ngummiselo ongunaphakade kuSirayeli.', 'XHO22'),
(18, 2, 5, 'Indlu endiyakhayo inkulu, kuba uThixo wethu mkhulu koothixo bonke.', 'XHO22'),
(18, 2, 6, 'Ngubani na ongamakhela indlu, ekubeni izulu lawo amazulu alingana naye? Ndingubani na ke mna, ukuba ndimakhele indlu, ingekukuba itshise phambi kobuso bakhe?', 'XHO22'),
(18, 2, 7, 'Ndithumele ke indoda elumkileyo, isebenze ngegolide, nesilivere, nobhedu, nesinyithi, nemfusa, nebomvu, nemfusa, ekwaziyo ukungcwaba ndawonye nezilumko ezinam ezweni lakwaYuda neYerusalem, awayilungisayo uDavide ubawo.', 'XHO22'),
(18, 2, 8, 'Ndithumele nemisedare, nemisipres, nemialgumi, evela eLebhanon; ngokuba ndiyazi ukuba abakhonzi bakho banako ukugawula imithi eLebhanon; yabona, abakhonzi bam baya kuba nabakhonzi bakho;', 'XHO22'),
(18, 2, 9, 'Wandilungisela nemithi emininzi, ngokuba indlu endiyakhayo inkulu ngokumangalisayo.', 'XHO22'),
(18, 2, 10, 'Yabona, ndibanika abakhonzi bakho abagawula iinkuni iitoni ezingamawaka angamashumi amabini engqolowa, namashumi amabini amawaka eebhari zerhasi, namashumi amabini amawaka eebhare zewayini, namashumi amabini amawaka emiphanda yeoli.', 'XHO22'),
(18, 2, 11, 'Waphendula uHiram ukumkani waseTire, ngencwadi abeyithumele kuSolomon, wathi, uYehova ubathande abantu bakhe, ubamisele ukumkani wabo.', 'XHO22'),
(18, 2, 12, 'uHuram wathi, Makabongwe uYehova, uThixo kaSirayeli, owenza izulu nehlabathi, omnike ukumkani uDavide unyana osisilumko, oqiqayo, onengqondo, omakhelayo uYehova indlu, umakhiyo ubukumkani bakhe.', 'XHO22'),
(18, 2, 13, 'Kwaye ngoku, ndithumele indoda esisilumko, enengqondo, ivela kubawo uHuram;', 'XHO22'),
(18, 2, 14, 'Yena waye engunyana wenkazana yasezintombini zakwaDan, uyise engumTire, ekwazi ukusebenza ngegolide, nesilivere, nobhedu, nesinyithi, nelitye, nomthi, nemfusa nemfusa, nantsi ke le nto entle nomfusa; niqingqe amangcwaba ezinto zonke, niziqondele zonke icebo, aya kulinikwa, nezilumko zenu, neengcinga zokumkani wam uDavide, uyihlo.', 'XHO22'),
(18, 2, 15, 'Makathumele ke ingqolowa, nerhasi, neoli, newayini, eyathetha ngazo inkosi yam kubakhonzi bayo;', 'XHO22'),
(18, 2, 16, 'Sogawula imithi eLebhanon, njengoko uthanda ngako; uze uyinyuse uyise eYerusalem.', 'XHO22'),
(18, 2, 17, 'uSolomon wababala bonke oonyana bolunye uhlanga, ababesezweni lakwaSirayeli, ngokwenani awababalayo uDavide uyise; kwaye kwafunyanwa ikhulu elinamashumi amahlanu anesithathu samawaka anamakhulu amathandathu.', 'XHO22'),
(18, 2, 18, 'Wamisa amashumi amathandathu amawaka kubo ukuba abe ngabathwali bemithwalo, namashumi asibhozo amawaka ukuba abe ngabalukileyo entabeni, namawaka amathathu anamakhulu amathandathu abalindi ukuba banyule umsebenzi wabantu.', 'XHO22'),
(18, 3, 1, 'uSolomon waqala ukuyakha indlu kaYehova eYerusalem, entabeni yeMoriya, apho uYehova wazityhilayo kuDavide uyise, endaweni awayilungisayo uDavide, esandeni sikaOrnan umYebhusi.', 'XHO22'),
(18, 3, 2, 'Waqala ukwakha ngenyanga yesibini, ngowesibini umhla, ngomnyaka wesine wobukumkani bakhe.', 'XHO22'),
(18, 3, 3, 'Zizo ezi ke izinto awawisa umthetho ngazo uSolomon ukuba ayakhe indlu kaThixo; Ubude beekubhite ngokomlinganiso wokuqala baba ziikubhite ezimashumi mathandathu, ububanzi baba ziikubhite ezimashumi mabini.', 'XHO22'),
(18, 3, 4, 'Ivaranda, ebiphambi kwendlu, bebungangobubanzi bendlu, bebuziikubhite ezimashumi mabini, nokuphakama kwayo kube zilikhulu elinamanci mabini; wayaleka ngaphakathi ngegolide ecocekileyo.', 'XHO22'),
(18, 3, 5, 'Indlu enkulu wayithambisa ngemisipres, wayaleka ngegolide engcwengiweyo, waza wabeka kuyo amasundu nemixokelelwane.', 'XHO22'),
(18, 3, 6, 'Wayivathisa indlu ngamatye anqabileyo ukuyihombisa, negolide leyo ibiyigolide yaseParvayim.', 'XHO22'),
(18, 3, 7, 'Wayaleka indlu, imiqadi, neeplanga zayo, neendonga zayo, neengcango zayo, ngegolide; zakrolwa iikerubhi ezindongeni.', 'XHO22'),
(18, 3, 8, 'Wayakha ke indlu yengcwele kangcwele; Wayaleka amakhulu amathandathu eetalente zegolide ecocekileyo.', 'XHO22'),
(18, 3, 9, 'Ubunzima bezikhonkwane bebuziishekele ezimashumi mahlanu zegolide. Amagumbi aphezulu wawaleka ngegolide.', 'XHO22'),
(18, 3, 10, 'Wenza kwengcwele kangcwele iikerubhi ezimbini ezikroliweyo, wazaleka ngegolide.', 'XHO22'),
(18, 3, 11, 'Amaphiko eekerubhi ubude bawo bebuziikubhite ezimashumi mabini; nelinye iphiko beliziikubhite ezintlanu, ukuya ephikweni lenye ikerubhi.', 'XHO22'),
(18, 3, 12, 'Nelinye iphiko lenye ikerubhi beliziikubhite ezintlanu, lenyuka laya eludongeni lwendlu; nelinye iphiko beliziikubhite ezintlanu, ukuya kuhlangana nephiko lenye ikerubhi.', 'XHO22'),
(18, 3, 13, 'Amaphiko ezo kerubhi abeziikubhite ezimashumi mabini;', 'XHO22'),
(18, 3, 14, 'Wenza umkhusane ngemisonto emsi nemfusa nebomvu, nelinen ecikizekileyo, wenza kuwo iikerubhi.', 'XHO22'),
(18, 3, 15, 'Wenza iintsika zambini phambi kwendlu, zaziikubhite ezimashumi mathathu anantlanu ukuphakama kwazo, nentloko ephezu kwazo zonke iikubhite ezintlanu.', 'XHO22'),
(18, 3, 16, 'Wenza imixokelelwane efana negumbi elingaphakathi, wayibeka emantloko eentsika; wenza iziqhushumbisi ezilikhulu, wazibeka ematyaleni.', 'XHO22'),
(18, 3, 17, 'Wazimisa iintsika phambi kwetempile, enye yangasekunene, enye yangasekhohlo; igama leyasekunene nguYagin, negama leyasekhohlo nguBhohazi.', 'XHO22'),
(18, 4, 1, 'Wenza nesibingelelo sobhedu: baziikubhite ezimashumi mabini ubude baso, baziikubhite ezimashumi mabini ububanzi baso, kwaziikubhite ezilishumi ukuphakama kwaso.', 'XHO22'),
(18, 4, 2, 'Wenza ulwandle olutyhidiweyo: lwaziikubhite ezilishumi, ukusuka kolunye udini lwesa kolunye, kuyiwe kolunye, oluyinqila ngeenxa zonke, lwaziikubhite ezintlanu ukuphakama kwawo; yaye umtya oziikubhite ezimashumi mathathu wawujikelezile ngeenxa zonke.', 'XHO22'),
(18, 4, 3, 'Ngaphantsi kwayo yayiyimbonakalo yenkunzi yenkomo, ngeenxa zonke, eyikubhite enye, elujikelezile ulwandle. Kwakukho imiqolo emibini yeenkomo ezenziwe ngodongwe.', 'XHO22'),
(18, 4, 4, 'Wema phezu kweenkomo ezilishumi elinambini: ezintathu zibheke entla, ezintathu zibheke entshonalanga, ezintathu zibheke ezantsi, ezintathu zibheke empumalanga; kwaye ulwandle lwaba phezu kwabo, namaxhama abo onke engaphakathi.', 'XHO22'),
(18, 4, 5, 'Ubungqingqwa balo bebungangobubanzi besandla; udini lwalo belwenziwe lwanjengodini lwendebe, lwaneentyantyambo zeenyibiba; bathabatha, bagcina amawaka amathathu eeemele.', 'XHO22'),
(18, 4, 6, 'Wenza nezitya zalishumi, wabeka ezintlanu ngasekunene, zantlanu ngasekhohlo, zokuhlambela kuzo ke ulwandle lwaba lolwababingeleli bokuhlamba.', 'XHO22'),
(18, 4, 7, 'Wenza iziphatho zezibane zegolide zalishumi, ukumila kwazo, wazibeka etempileni, ezintlanu ngasekunene, ezihlanu ngasekhohlo.', 'XHO22'),
(18, 4, 8, 'Wenza neetafile zalishumi, wazibeka etempileni, ezintlanu ngasekunene, zantlanu ngasekhohlo. Wenza ikhulu leempahla zegolide.', 'XHO22'),
(18, 4, 9, 'Wenza nentendelezo yababingeleli, nentendelezo enkulu, neengcango zentendelezo; wazigubungela iingcango zazo ngobhedu.', 'XHO22'),
(18, 4, 10, 'Walubeka ulwandle ngecala lasekunene ngecala lasempumalanga, olujonge ezantsi.', 'XHO22'),
(18, 4, 11, 'uHuram wenza iimbiza, nemihlakulwana, nezitya zokutshiza. Wawugqiba ke uHuram umsebenzi abewenzela indlu kaThixo kakumkani uSolomon;', 'XHO22'),
(18, 4, 12, 'Neentsika zombini, neentsika, neentloko ezibe ziphezu kweentsika zombini, nezithsaba zombini zokugubungela iintsika zombini eziphezu kweentsika ezo;', 'XHO22'),
(18, 4, 13, 'neziqhushumbisi ezingamakhulu amane emaweni amabini; Kwisithsaba ngasinye kukho imikrozo emibini yokugubungela iingqukuva ezibe zisezintsikeni zombini.', 'XHO22'),
(18, 4, 14, 'Wenza neenqwelo zokuhlambela, wenza nezitya zokuhlambela phezu kweenqwelo;', 'XHO22'),
(18, 4, 15, 'Ulwandle lwalunye, neenkomo ezilishumi elinambini ngaphantsi kwalo.', 'XHO22'),
(18, 4, 16, 'Neembiza, nemihlakulwana, namahaki, nazo zonke iimpahla zazo, uHuram uyise wazenzela ukumkani uSolomon, zizezendlu kaYehova, ngobhedu olukhazimlisiweyo.', 'XHO22'),
(18, 4, 17, 'uYehova wazigalela entlango yaseYordan emhlabeni wodongwe phakathi kweSukoti neSeredata.', 'XHO22'),
(18, 4, 18, 'uSolomon wazenza zininzi kakhulu zonke ezi zitya; ngokuba ubunzima bobhedu olo babungaziwa.', 'XHO22'),
(18, 4, 19, 'uSolomon wazenza zonke iimpahla zendlu yoThixo oyinyaniso, nesibingelelo segolide neetafile ekwakubekwe kuzo izonka zokubonisa;', 'XHO22'),
(18, 4, 20, 'Neziphatho zezibane, nezibane zazo, ziqhunyiswe ngokwesiko legolide ecocekileyo phambi kwendlu engaphakathi;', 'XHO22'),
(18, 4, 21, 'Iintyatyambo, izibane, nemigangatho, izezegolide, ecocekileyo;', 'XHO22'),
(18, 4, 22, 'nezibane, nezitya zokutshiza, neenkamba, neengcedevu, zegolide ecocekileyo; Umnyango wendlu, neengcango ezingaphakathi zengcwele kangcwele, neengcango zendlu yetempile, izezegolide.', 'XHO22'),
(18, 5, 1, 'Wagqitywa ke wonke umsebenzi awawenzela indlu kaYehova uSolomon; uSolomon wazibeka zonke izinto, abezingcwalisile uDavide uyise; nesilivere, negolide, neempahla zonke, wazibeka phantsi kobuncwane bendlu kaThixo.', 'XHO22'),
(18, 5, 2, 'Wawahlanganisa ke uSolomon eYerusalem amadoda amakhulu akwaSirayeli, neentloko zonke zezizwe, iintloko zezindlu zoonyana bakaSirayeli, neentloko zonke zezizwe zoonyana bakaSirayeli, ukuba bayikhuphe eYerusalem ityeya yomnqophiso kaYehova, emzini kaDavide. iZiyon.', 'XHO22'),
(18, 5, 3, 'Abuthelana ke onke amadoda akwaSirayeli emthendelekweni wokumkani wenyanga yesixhenxe.', 'XHO22'),
(18, 5, 4, 'Eza ke onke amadoda amakhulu akwaSirayeli; bayithwala abaLevi ityeya.', 'XHO22'),
(18, 5, 5, 'Bandula ke bayinyusa ityeya nentente yokuhlangana nazo zonke iimpahla ezingcwele ezazisententeni, babanyusa ababingeleli nabaLevi.', 'XHO22'),
(18, 5, 6, 'uKumkani uSolomon nebandla lonke likaSirayeli elalihlanganisene kuye phambi kweTyeya babingelela ngezimvu neenkomo ezazingenakulinganiswa okanye zibalwe ngenxa yenani.', 'XHO22'),
(18, 5, 7, 'Bayizisa ababingeleli ityeya yomnqophiso kaYehova endaweni yayo, endlwini engaphakathi endlwini, kwiNgcwele kaNgcwele, phantsi kwamaphiko eekherubhi;', 'XHO22'),
(18, 5, 8, 'ngokuba iikerubhi zaye ziwolule amaphiko phezu kwendawo yetyeya, iikerubhi zayisithelisa ityeya nezibonda zayo ngaphezulu.', 'XHO22'),
(18, 5, 9, 'Bayikhupha ke imithwalo yetyeya, ukubonakala kwempahla yetyeya phambi kwegumbi elingaphakathi; kodwa zazingabonakali ngaphandle. Yaye isekho nanamhla.', 'XHO22'),
(18, 5, 10, 'Kwakungekho nto etyeyeni, ngaphandle kwamacwecwe amabini amatye awayewabeke kuyo uMoses xa wayeseHorebhe, xa uYehova wenza umnqophiso noonyana bakaSirayeli ekuphumeni kwabo eYiputa.', 'XHO22'),
(18, 5, 11, 'Ekuphumeni kwababingeleli engcweleni, ngokuba bonke ababingeleli abafumanekayo babengcwalisiwe, àbalindanga ngelo xesha.', 'XHO22'),
(18, 5, 12, 'Ke abaLevi ababeziimvumi, ooAsafu, noHeman, noYedutun, noonyana babo, nabazalwana babo, bambethe ilinen emhlophe, benamacangci, nemirhubhe, nemirhubhe, bemi ngasempumalanga esibingelelweni, benengubo enye ababingeleli abalikhulu elinamashumi amabini bavuthela amaxilongo.', 'XHO22'),
(18, 5, 13, 'Kwathi ke xa abaxilongo neemvumi baba nye, bavakalisa ngazwi-nye, bedumisa, bedumisa uYehova; Bathe bakuphakamisa ilizwi ngamaxilongo, nangamacangci, nangeempahla zabo, bamdumisa uYehova, besithi, Elungile, ewe; ngokuba ingunaphakade inceba yakhe, ekuzalisweni lilifu indlu kaYehova;', 'XHO22'),
(18, 5, 14, 'Ababingeleli ababa nakuma balungiselele ngenxa yelifu, ngokuba ubuqaqawuli bukaYehova bayizalisa indlu kaThixo.', 'XHO22'),
(18, 6, 1, 'Wathi uSolomon, uYehova wathi, niya kuhlala ebumnyameni.', 'XHO22'),
(18, 6, 2, 'Mna ke ndikwakhele indlu, nesakhiwo, kude kuse ephakadeni;', 'XHO22'),
(18, 6, 3, 'Wee guququ ukumkani ngobuso, walisikelela lonke ibandla lakwaSirayeli; lonke ibandla lakwaSirayeli lema.', 'XHO22'),
(18, 6, 4, 'Wathi, Makabongwe uYehova, uThixo kaSirayeli, okwenzileyo oko ngesandla sakhe, awakuthetha ngomlomo wakhe kuDavide ubawo, esithi,', 'XHO22'),
(18, 6, 5, 'Ukususela kwimini endabakhuphayo ngayo abantu bam ezweni laseYiputa, andinyulanga nasinye isixeko kuzo zonke izizwe zakwaSirayeli ukuze ndimakhele indlu igama lam; kwaye andinyulanga mntu ukuba abe yinganga yabantu bam amaSirayeli;', 'XHO22'),
(18, 6, 6, 'ndanyula iYerusalem, ukuba igama lam libe khona; ndanyula uDavide ukuba abe yingànga yabantu bam amaSirayeli.', 'XHO22'),
(18, 6, 7, 'Kwaye kukho entliziyweni kaDavide ubawo ukuthi alakhele indlu igama likaYehova, uThixo kaSirayeli.', 'XHO22'),
(18, 6, 8, 'Wathi uYehova kuDavide ubawo, Ngenxa enokuba kwakukho entliziyweni yakho ukuthi ulakhele indlu igama lam, walungisa, ngokuba bekusentliziyweni yakho oko.', 'XHO22'),
(18, 6, 9, 'Ke wena akuwenzi umzi; kodwa unyana wakho uya kuphuma esinqeni sakho, yena uya kulakhela indlu igama lam.', 'XHO22'),
(18, 6, 10, 'Ngoko uYehova walizalisekisa ilizwi lakhe awalithethayo, ngenxa yokuba ndema egumbini likabawo uDavide ndaza ndahlala etroneni kaSirayeli, kanye njengoko wayethethile uYehova, ndaza ndalakhela indlu igama likaYehova uThixo kaSirayeli.', 'XHO22'),
(18, 6, 11, 'Ndayibeka khona ityeya, engumnqophiso kaYehova, awawenzayo noonyana bakaSirayeli.', 'XHO22'),
(18, 6, 12, 'Wema phambi kwesibingelelo sikaYehova phambi kwebandla lonke lakwaSirayeli, wazolula izandla zakhe.', 'XHO22'),
(18, 6, 13, 'uSolomon wenza ikhuselo lobhedu: ubude balo baba ziikubhite ezintlanu, ububanzi balo baba ziikubhite ezintlanu, nokuphakama kwalo kwaba ziikubhite ezintathu, walimisa esazulwini sentendelezo; wema phezu kwayo, waguqa ngamadolo phambi kwebandla lonke kaSirayeli, wazolulela ezulwini izandla zakhe;', 'XHO22'),
(18, 6, 14, 'wathi, Yehova, Thixo kaSirayeli, akukho Thixo unjengawe emazulwini nasehlabathini; uyawugcina umnqophiso, unenceba kubakhonzi bakho abahamba kuwe ngentliziyo yabo yonke;', 'XHO22'),
(18, 6, 15, 'wena umgcineleyo umkhonzi wakho uDavide, oko wakuthethayo kuye; wathetha ngomlomo wakho, wakuzalisa ngesandla sakho, njengoko kunjalo namhla.', 'XHO22'),
(18, 6, 16, 'Kaloku, Yehova, Thixo kaSirayeli, umqinisele umkhonzi wakho uDavide ubawo, oko wakuthethayo kuye, wathi, Emehlweni am akuyi mfuneko yakuhlala mntu etroneni yakwaSirayeli; noko ke ukuze bagqale oonyana benu endleleni yabo yokuhamba ngomyalelo wam, njengoko nihambe ngako phambi kwam.', 'XHO22'),
(18, 6, 17, 'Kaloku, Yehova, Thixo kaSirayeli, malizalise ilizwi lakho owalithethayo kumkhonzi wakho uDavide.', 'XHO22'),
(18, 6, 18, 'Kodwa ngaba ngokwenene uThixo uya kuhlala nabantu emhlabeni? Yabonani, amazulu, nezulu lawo amazulu, alilingani; iyakuba njani na le ndlu ndiyakhileyo?', 'XHO22'),
(18, 6, 19, 'Ke kaloku, Yehova Thixo wam, wuphulaphule umthandazo womkhonzi wakho nomthandazo wakhe, ukuze umamele isikhalo nomthandazo awuthandazayo umkhonzi wakho phambi kwakho;', 'XHO22'),
(18, 6, 20, 'ukuze amehlo akho avuleke imini nobusuku ngokuphathelele le ndlu, kwindawo othe uya kulimisa kuyo igama lakho; uphulaphule umthandazo athandaza ngawo umkhonzi wakho kule ndawo.', 'XHO22'),
(18, 6, 21, 'Phulaphula izicelo zomkhonzi wakho, nezicelo zabantu bakho amaSirayeli, abaya kuzicela kule ndawo; yiva umkhosi wakho, wena usemazulwini; xa uthe weva, xolela;', 'XHO22'),
(18, 6, 22, 'Xa athe ubani wona ummelwane wakhe, wafunga kuye, wamfungela, kwaza kwafika isifungo phambi kwesibingelelo sakho kule ndlu;', 'XHO22'),
(18, 6, 23, 'yivani ke emazulwini, nenze, nigwebe kubakhonzi benu, nimbuyise ongendawo, niyibuyisele indlela yakhe entlokweni yakhe; ngokugwebela ilungisa, umnike ubulungisa bakhe.', 'XHO22'),
(18, 6, 24, 'Ukuba bathe abantu bakho amaSirayeli bakhweletela iintshaba zabo, kuba bekonile; abaya kubuya bedumisa igama lakho, bethandaza, bethandaza kuwe bekule ndlu;', 'XHO22'),
(18, 6, 25, 'Ke kaloku yiva usemazulwini, usixolele isono sabantu bakho amaSirayeli, ubabuyisele kwilizwe owabanika lona nookhokho babo.', 'XHO22'),
(18, 6, 26, 'Lakuvaleka izulu, akwabakho mvula, ngokuba bakona; kwaye ukuba bayathandaza kule ndawo baze balivume igama lakho kwaye baguquke kwisono sabo ukuba uyabacinezela;', 'XHO22'),
(18, 6, 27, 'yiva ke wena emazulwini, usixolele isono sabakhonzi bakho, nabantu bakho amaSirayeli, ngokuba ubafundise indlela elungileyo abaya kuhamba ngayo; uze unise imvula ezweni lakho olinike abantu bakho njengelifa.', 'XHO22'),
(18, 6, 28, 'Ukuba kuthe kwakho indlala elizweni, xa kuthe kwakho isibetho, xa kuthe kwakho umlilo okanye ukungunda, iinkumbi okanye iinkumbi; Xa iintshaba zawo ziwarhawula emizini yawo; nayiphi na intlungu okanye ukufa okunokuthi kwenzeke:', 'XHO22'),
(18, 6, 29, 'Nakuphi na ukuthandaza nokuthandaza kuye nawuphi na umntu, nokuba ngabantu bakho bonke amaSirayeli, xa baya kwazi elowo intlungu yakhe nentlungu yakhe, aze azolule izandla zakhe endlwini;', 'XHO22'),
(18, 6, 30, 'Ke kaloku yiva usemazulwini, kwindawo ohlala kuyo, uxolele yaye ubuyekeze wonk’ ubani ngokweendlela zakhe zonke, ontliziyo ikwaziyo wena; Ngokuba uyazazi iintliziyo zoonyana babantu;', 'XHO22'),
(18, 6, 31, 'ukuze bakoyike, bahambe ngeendlela zakho, yonke imihla yobomi babo, emhlabeni owawunika oobawo.', 'XHO22'),
(18, 6, 32, 'Ngaphezu koko, ngokuphathelele owasemzini ongengowesizwe sakho amaSirayeli, kodwa oweza evela kwilizwe elikude ngenxa yegama lakho elikhulu nangesandla sakho esomeleleyo nangesandla sakho esithe ngqo; beza kuthandaza kule ndlu;', 'XHO22'),
(18, 6, 33, 'Yivani ngoko usemazulwini, apho uhlala khona, wenze njengoko wabiza ngako owasemzini; ukuze zonke iintlanga zehlabathi zilazi igama lakho, zikoyike, njengabantu bakho amaSirayeli, ukuze zazi ukuba lisahlala likhankanywa igama lakho phezu kwale ndlu ndiyakhileyo.', 'XHO22'),
(18, 6, 34, 'Xa bathe abantu bakho balwa neentshaba zabo ngendlela obathuma kuyo, bathandaza kuwe bebhekisa kulo mzi uwunyulileyo, nakule ndlu ndiyakhele igama lakho;', 'XHO22'),
(18, 6, 35, 'Yiva usemazulwini umthandazo wabo nokutarhuzisa kwabo, ubamele endaweni yabo.', 'XHO22'),
(18, 6, 36, 'Ukuba bathe bakona kuwe (ngenxa yokuba kungekho bani ungoniyo), wacaphuka waza wabanikela esandleni seentshaba zabo, babathimba babasa kwilizwe elikude okanye elikufuphi;', 'XHO22'),
(18, 6, 37, 'Bakukhumbula oko ezweni ababethinjelwe kulo, baguquka, bathandaza kuwe ezweni lokuthinjwa kwabo, besithi, Sihlulekile, senze ububi, senze ububi;', 'XHO22'),
(18, 6, 38, 'ukuba babuyela kuwe ngentliziyo yabo yonke, nangomphefumlo wabo wonke, bevela kwelo zwe bathinjelwe kulo, bathandaza bebhekisa ezweni labo owalinika ooyise, nakulo umzi owawunika ooyise; ndiyinyule yona, nendlu endayakhela igama lakho;', 'XHO22'),
(18, 6, 39, 'Ke kaloku yiva usemazulwini, ewe, kwindawo ohlala kuyo, umthandazo wabo nezibongozo zabo, uze ubalinde, ubaxolele abantu bakho abakonileyo.', 'XHO22'),
(18, 6, 40, 'Kaloku, Thixo wam, makavuleke amehlo akho, iindlebe zakho ziwuphulaphule umthandazo owenziwa kule ndawo.', 'XHO22'),
(18, 6, 41, 'Kaloku, Yehova Thixo, yima endaweni yokuphumla kwakho, wena netyeya yamandla akho; ababingeleli bakho, Yehova, Thixo, mabambathe usindiso, badle okulungileyo abo ubathandayo.', 'XHO22'),
(18, 6, 42, 'Yehova Thixo, musa ukutyeka ebusweni bomthanjiswa wakho; khumbula inceba kaDavide, umkhonzi wakho.', 'XHO22'),
(18, 7, 1, 'Akugqiba uSolomon ukuthandaza, umlilo welula ezulwini, walitshisa idini elinyukayo nemibingelelo; ubuqaqawuli bukaYehova bayizalisa indlu.', 'XHO22'),
(18, 7, 2, 'Ababingeleli ababa nakungena endlwini kaYehova, ngokuba ubuqaqawuli bukaYehova babuzele endlwini kaYehova.', 'XHO22'),
(18, 7, 3, 'Bakubona bonke oonyana bakaSirayeli ukuhla komlilo, nobuqaqawuli bukaYehova buphezu kwendlu, baqubuda emhlabeni, baguqa, badumisa uYehova, besithi, Elungile, ewe, uYehova ulungile. Ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(18, 7, 4, 'Ukumkani nabantu bonke babingelela kuYehova.', 'XHO22'),
(18, 7, 5, 'Wabingelela ukumkani uSolomon amashumi amabini anesibini amawaka eenkomo, ikhulu elinamanci amabini lamawaka empalula emfutshane; Bayingcwalisa ke indlu kaThixo ukumkani nabantu bonke.', 'XHO22'),
(18, 7, 6, 'Ababingeleli babemi ezihlalweni zabo, nabaLevi benezixhobo zikaYehova awazenzayo uKumkani uDavide ukuba badumise uYehova, ngenxa yokuba ububele bakhe bothando bulapho de kwaba lixesha elingenammiselo kaDavide wayidumisa inkonzo yabo; ababingeleli bavuthela ngezigodlo phambi kwabo, ema onke amaSirayeli.', 'XHO22'),
(18, 7, 7, 'uSolomon wawungcwalisa umphakathi wentendelezo ephambi kwendlu kaYehova; kuba kulapho wazisa khona iminikelo etshiswayo namanqatha emibingelelo yombulelo, ngenxa yokuba isibingelelo sobhedu awasenzayo uSolomon sasingenako ukuwufumana umbingelelo otshiswayo, umnikelo otshiswayo namanqatha.', 'XHO22'),
(18, 7, 8, 'Ngelo xesha uSolomon wenza itheko iintsuku ezisixhenxe, nawo onke amaSirayeli awayekunye naye, njengebandla elikhulu kakhulu, ukususela ekungeneni kweHamati ukusa kuMlambo waseYiputa.', 'XHO22'),
(18, 7, 9, 'Benza ngomhla wesibhozo umthendeleko; ukungcwaliswa kwesibingelelo bakwenza iintsuku zasixhenxe, nomthendeleko iintsuku zasixhenxe.', 'XHO22'),
(18, 7, 10, 'Ngosuku lwamashumi amabini anesithathu lwenyanga yesixhenxe wathi ebantwini mabahambe baye ezintenteni zabo bevuya yaye benemihlali ezintliziyweni zabo ngenxa yokulunga uYehova awayekwenzile kuDavide noSolomon nabantu bakhe amaSirayeli.', 'XHO22'),
(18, 7, 11, 'Wayigqiba ke uSolomon indlu kaYehova nendlu yokumkani, yaye yonke into eyathi qatha entliziyweni kaSolomon ukuba ayenze endlwini kaYehova nasendlwini yakhe, wayiphumelelisa.', 'XHO22'),
(18, 7, 12, 'uYehova wabonakala kuSolomon ebusuku, wathi kuye, Ndikuvile ukuthandaza kwakho;', 'XHO22'),
(18, 7, 13, 'Ukuba ndithe ndasivala izulu, ukuba kungabikho mvula, nokuba ndithe ndaziwisela umthetho iinkumbi, ukuba zilidle ilizwe, nokuba ndithe ndathumela ebantwini bam indyikitya yokufa;', 'XHO22'),
(18, 7, 14, 'bathoba ke abantu bam, ababizwa kubo igama lam, bathandaza, bafuna ubuso bam, babuya ezindleleni zabo ezimbi; ndiya kuva ke emazulwini, ndibaxolele izono zabo, ndiliphilise ihlabathi labo.', 'XHO22'),
(18, 7, 15, 'Ovuleka ke amehlo am, iindlebe zam ziwuphulaphule umthandazo owenziwa kule ndawo.', 'XHO22'),
(18, 7, 16, 'Kuba ngoku ndiyinyule le ndlu ndaza ndayingcwalisa, ukuze igama lam libe kuyo ukusa kwixesha elingenammiselo, namehlo am nentliziyo yam iya kusoloko ikuyo.', 'XHO22'),
(18, 7, 17, 'Wena ke, ukuba uthe wahamba ngam, njengoko wahamba ngako uDavide uyihlo, wayenza yonke into endikuwisele umthetho ngayo, uyigcine imithetho yam nemimiselo yam;', 'XHO22'),
(18, 7, 18, 'ndiyiqinise itrone yobukumkani bakho, njengoko ndanqophisana ngako noyihlo uDavide, ndisithi, Akuyi kuswela ndoda yakulawula kwaSirayeli.', 'XHO22'),
(18, 7, 19, 'Ke ukuba nithe natyeka, nayishiya imithetho yam emisiweyo, nemithetho yam, endiyibeke phambi kwenu, nemka nakhonza thixo bambi, naqubuda kubo;', 'XHO22'),
(18, 7, 20, 'ngoku ndiya kubanyothula emhlabeni wam endibanike wona; nale ndlu, ndiyingcwalisele igama lam, ndoyishiya, ndoyishiya, ndiyenze ibe yintsomi nentsomi ezintlangeni zonke.', 'XHO22'),
(18, 7, 21, 'Le ndlu ephakamileyo yobamangalisa bonke abadlulayo kuyo; Wathi, Yini na ukuba uYehova enjenje kweli lizwe, nale ndlu?', 'XHO22'),
(18, 7, 22, 'Kuya kuthi ke kuthi, Kuthiwe, Kungenxa enokuba babuyayo kuYehova uThixo wooyise, owabakhuphayo ezweni laseYiputa, bathabatha oothixo bolunye uhlanga, babanqula, babakhonza; ke ngoko wabazisela yonke le ntlekele.', 'XHO22'),
(18, 8, 1, 'Ke kaloku, kwakuzaliseka iminyaka emashumi mabini, wayakha uSolomon indlu kaYehova nendlu yakhe;', 'XHO22'),
(18, 8, 2, 'uSolomon wayakha imizi leyo uHuram awayibuyiselayo kuSolomon, wabahlalisa kuyo oonyana bakaSirayeli.', 'XHO22'),
(18, 8, 3, 'uSolomon waya eHamati-tsobha waza wayithimba.', 'XHO22'),
(18, 8, 4, 'Wakha iTademore entlango, nemizi yonke enqatyisiweyo wayakha eHamati.', 'XHO22'),
(18, 8, 5, 'Wakha neBhete-horon engasentla neBhete-horon yasezantsi, yaba yimizi enqatyisiweyo, enamasango nemivalo;', 'XHO22'),
(18, 8, 6, 'neBhahalati nazo zonke izixeko ezingoovimba uSolomon awayenazo, nazo zonke izixeko zemfazwe nezixeko zamahashe nayo yonke into awayefuna ukuyakha uSolomon eYerusalem naseLebhanon nakulo lonke ilizwe lolawulo lwakhe.', 'XHO22'),
(18, 8, 7, 'Ke bonke abaseleyo kumaHeti, nama-Amori, namaPerizi, namaHeti, namaYebhusi, abengengawo akwaSirayeli;', 'XHO22'),
(18, 8, 8, 'Kodwa phakathi koonyana babo abasala elizweni emva kwabo, abo oonyana bakaSirayeli bangabatshabalalisanga, bamrhafisa uSolomon de kube namhla.', 'XHO22'),
(18, 8, 9, 'Ke uSolomon akabakhonzanga oonyana bakaSirayeli; kodwa babengamagorha akhe, nabathetheli bakhe, nabathetheli beenqwelo zakhe zokulwa, nabamahashe bakhe.', 'XHO22'),
(18, 8, 10, 'Iingqwayi-ngqwayi zabalindi bokumkani uSolomon, babengamakhulu amabini anamanci mahlanu, ababelawula abantu.', 'XHO22'),
(18, 8, 11, 'uSolomon wathabatha intombi kaFaro, wayisa emzini kaDavide, wayingenisa endlwini abeyakhele; ngokuba wathi, Akahlalanga umfazi wam endlwini kaDavide, ukumkani wakwaSirayeli; kuba iindawo ezingcwele zobuncwane bukaYehova obuya kufika kuyo zingcwele.', 'XHO22'),
(18, 8, 12, 'uSolomon wenyusa amadini anyukayo kuYehova esibingelelweni sikaYehova, abesakha phambi kwevaranda;', 'XHO22'),
(18, 8, 13, 'Ke ngokomlinganiselo othile, imihla ngemihla, ngokomyalelo kaMoses, ngeesabatha, nasekuthwaseni kwenyanga, nangemithendeleko, kathathu ngomnyaka, emthendelekweni wezonka ezingenagwele, nangomthendeleko iiveki, nangomthendeleko weminquba.', 'XHO22'),
(18, 8, 14, 'Ngokomyalelo kayise uDavide wamisela amaqela ababingeleli ukuze balungiselele inkonzo yabo, nabaLevi ukuze babe ngabalungiseleli babo ukuze badumise, balungiselele phambi kwababingeleli kwinkonzo yemihla ngemihla inkonzo nasekungeneni kuwo onke amasango, njengoko wamwiselayo umthetho uDavide umfo wakwaThixo.', 'XHO22'),
(18, 8, 15, 'Abatyekanga kumyalelo wokumkani kubabingeleli nabaLevi ngokuphathelele kuyo nayiphi na imibandela okanye ngokuphathelele ubuncwane.', 'XHO22'),
(18, 8, 16, 'Walungiswa ke wonke umsebenzi kaSolomon de kwayimini yokusekwa kwesiseko sendlu kaYehova de kwagqitywa. Ngoko indlu kaYehova yayigqibelele.', 'XHO22'),
(18, 8, 17, 'Wandula ke uSolomon waya e-Etsiyon-gebhere nase-Eloti, kunxweme lolwandle kwilizwe lakwaEdom.', 'XHO22'),
(18, 8, 18, 'uHiram wamthumelela iinqanawa ngesandla sabakhonzi bakhe, nabakhonzi ababelwazi ulwandle; yaye bahamba nabakhonzi bakaSolomon eOfire baza bathabatha apho amakhulu amane anamashumi amahlanu eetalente zegolide bazizisa kuKumkani uSolomon.', 'XHO22'),
(18, 9, 1, 'Ke kaloku ukumkanikazi waseShebha waliva ilizwi likaSolomon, weza kumlinga uSolomon ngemibuzo enzima eYerusalem, enesihlwele esikhulu kunene, neenkamela ezithwele ubulawu, negolide eninzi, namatye anqabileyo; wathi akufika; Wathetha kuSolomon ngayo yonke into esentliziyweni yakhe.', 'XHO22'),
(18, 9, 2, 'uSolomon wamxelela yonke imibuzo yakhe, yaye akukho nto ifihlakeleyo kuSolomon angazange amxelele yona.', 'XHO22'),
(18, 9, 3, 'Wabubona ukumkanikazi waseShebha ubulumko bukaSolomon, nendlu awayakhayo;', 'XHO22'),
(18, 9, 4, 'nokudla kwetafile yakhe, nezihlalo zabakhonzi bakhe, nenkonzo yabakhonzi bakhe, neempahla zabo; nabakhonzi bakhe neempahla zabo; nokuma kwakhe ukunyuka kwakhe ukuya endlwini kaYehova; akwaba sabakho kuphefumla kuye.', 'XHO22'),
(18, 9, 5, 'Wathi kukumkani, Lilizwi eliyinyaniso, endilivileyo ezweni lam, ngezenzo zakho nobulumko bakho.', 'XHO22'),
(18, 9, 6, 'Andikholwanga ngamazwi abo, ndada ndeza, ababona amehlo am; uyabona, andixelelwanga nesiqingatha esi sobuninzi bobulumko bakho; ngokuba ulugqithile udaba endiluvileyo.', 'XHO22'),
(18, 9, 7, 'Anoyolo amadoda akho, banoyolo aba bakhonzi bakho bemiyo phambi kwakho amaxesha onke, bebuva ubulumko bakho.', 'XHO22'),
(18, 9, 8, 'Makabongwe uYehova uThixo wakho, okunoneleleyo, ukukubeka etroneni yakhe, ube ngukumkani kaYehova uThixo wakho; ngokuba uThixo wakho wawathanda amaSirayeli, ukuba awamise ngonaphakade; wambeka ukumkani wakho phezu kwawo ukuba enze ngokwesiko nangobulungisa.', 'XHO22'),
(18, 9, 9, 'Ukumkani wanika ukumkani ikhulu elinamanci amabini eetalente zegolide, nasebulawini into eninzi, namatye anqabileyo; yaye akuzange kubekho zibulawu zifana nezo ukumkanikazi waseShebha wazinika uKumkani uSolomon.', 'XHO22'),
(18, 9, 10, 'Yaye abakhonzi bakaHiram nabakhonzi bakaSolomon, ababeze negolide evela kwaOfire, beza nemithi yeallum namatye axabisekileyo.', 'XHO22'),
(18, 9, 11, 'Ukumkani wazenza iivaranda zendlu kaYehova, nezendlu yokumkani, ngemialgumi, neehadi, nemirhubhe yeemvumi; yaye akazange abonwe umntu onjalo ngaphambili kwilizwe lakwaYuda.', 'XHO22'),
(18, 9, 12, 'Ukumkani uSolomon wanika ukumkanikazi waseShebha konke awayekunqwenela, konke awayekucelile; ngaphandle koko wayekuzisele ukumkani. Wajika waya ezweni lakhe, yena nabakhonzi bakhe.', 'XHO22'),
(18, 9, 13, 'Ubunzima begolide awazuzayo uSolomon ngomnyaka, baba ngamakhulu omathandathu anamanci mathandathu anesithandathu eetalente zegolide;', 'XHO22'),
(18, 9, 14, 'ingekuko oko kweziswa ngamasela nabarhwebi. Bonke ookumkani bama-Arabhi nabalawuli belizwe babezisa igolide nesilivere kuSolomon.', 'XHO22'),
(18, 9, 15, 'Ukumkani uSolomon wenza amakhulu amabini eeshekele zegolide ekhandiweyo;', 'XHO22'),
(18, 9, 16, 'Wenza amakhulu amathathu amakhaka egolide ekhandiweyo; Ukumkani wawabeka endlwini yehlathi laseLebhanon.', 'XHO22'),
(18, 9, 17, 'Ngaphezu koko, ukumkani wenza netrone enkulu yophondo lwendlovu waza wayaleka ngegolide esulungekileyo.', 'XHO22'),
(18, 9, 18, 'Kwakukho izinyuko ezithandathu ezikhokelela etroneni, kunye nezihlalo zeenyawo zegolide, ezinamathele etroneni leyo, nezixhaso macala omabini esihlalo eso, kumi iingonyama ezimbini ngasezixhasweni.', 'XHO22'),
(18, 9, 19, 'Kwakukho iingonyama ezilishumi elinambini khona ezinyukweni ezithandathu, ngapha nangapha; Akukho nto injengaleyo yenziweyo kuye nawuphi na urhulumente.', 'XHO22'),
(18, 9, 20, 'Zonke iimpahla zokumkani uSolomon zabe zezegolide, nempahla yonke yendlu yehlathi laseLebhanon yabe iyeyegolide ecikizekileyo; kwakungekho nto yayibalwa ngemihla kaSolomon.', 'XHO22'),
(18, 9, 21, 'Ngokuba iinqanawa zokumkani zaya eTarshishe nabakhonzi bakaHiram; Rhoqo ngeminyaka emithathu kwafika iinqanawa ezivela eTarshishe zithwele igolide, isilivere, iimpondo zeendlovu, iinkawu neepikoko.', 'XHO22'),
(18, 9, 22, 'Waba mkhulu ke ukumkani uSolomon, ngaphezu kookumkani bonke behlabathi, ngobutyebi nangobulumko.', 'XHO22'),
(18, 9, 23, 'Bonke ookumkani behlabathi bafuna ukubona ubuso bukaSolomon, ukuba beve ubulumko bakhe, awayebunike entliziyweni yakhe uThixo.', 'XHO22'),
(18, 9, 24, 'Elowo wazisa isipho sakhe: iimpahla zesilivere, neempahla zegolide, neempahla, nemisonto, nobulawu, namahashe, noimeyile, iminyaka ngeminyaka.', 'XHO22'),
(18, 9, 25, 'uSolomon ubenamawaka amane amahashe neenqwelo zokulwa, namawaka alishumi elinamabini amahashe akhwelwayo; wayibeka emizini yemfazwe, nakukumkani waseYerusalem.', 'XHO22'),
(18, 9, 26, 'Ubelawula ookumkani bonke, ethabathela kuwo uMlambo, naselizweni lamaFilisti, wesa nasemdeni waseYiputa.', 'XHO22'),
(18, 9, 27, 'uYehova wayenza isilivere yanjengamatye eYerusalem, nemisedare wayenza yanjengemithombe yasezihlanjeni, ukuba mininzi.', 'XHO22'),
(18, 9, 28, 'Bawazisa amahashe kaSolomon eYiputa, nasezizweni zonke;', 'XHO22'),
(18, 9, 29, 'Ezinye izinto zikaSolomon, ezokuqala nezokugqibela, azibhalwanga na encwadini yomprofeti uNatan, nasesiprofetweni sika-Ahiya waseShilo, nasemibonweni kaIdo, imboni, ngoYarobheham umshumayeli unyana kaNebhati?', 'XHO22'),
(18, 9, 30, 'uSolomon waba ngukumkani kumaSirayeli onke iminyaka emashumi mane eYerusalem.', 'XHO22'),
(18, 9, 31, 'uSolomon walala kooyise, wangcwatyelwa emzini kaDavide uyise; unyana wakhe uRehobhoham waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(18, 10, 1, 'uRehabheham waya kwaShekem; ngokuba amaSirayeli onke abeye kumenza ukumkani kwaShekem.', 'XHO22'),
(18, 10, 2, 'Weva uYarobheham, unyana kaNebhati, eYiputa, apho wayebalekele khona ngenxa kaSolomon ukumkani, wabuya uYarobheham eYiputa;', 'XHO22'),
(18, 10, 3, 'Bathumela ngoko ukuba kumbizwe. Weza uYarobheham namaSirayeli onke, bathetha kuRehabheham, bathi,', 'XHO22'),
(18, 10, 4, 'Uyihlo wasithwalela idyokhwe enzima; ke wena yenze lula inkonzo enzima kayihlo, nedyokhwe yakhe enzima ayibeke phezu kwethu, sikukhonze.', 'XHO22'),
(18, 10, 5, 'Wathi kubo, Yizani kum emveni kweentsuku ezintathu; Kwaye abantu bayahamba.', 'XHO22'),
(18, 10, 6, 'Ukumkani uRehabheham wacebisana namadoda amakhulu, abemi ngakuSolomon uyise oko ebesahleli, wathi, Ninika cebo lini na kum, ukuba ndithe ndabaphendula aba bantu?', 'XHO22'),
(18, 10, 7, 'Athetha ke kuye, athi, Ukuba uthe wabenzela okulungileyo aba bantu, wabavuyisa, wathetha amazwi amahle kubo, boba ngabakhonzi bakho kude kuse ephakadeni.', 'XHO22'),
(18, 10, 8, 'Kodwa walishiya icebiso awayelinikwe ngamadoda amakhulu, wacebisana namadodana aziintanga zakhe, awayemi ngakuye.', 'XHO22'),
(18, 10, 9, 'Wathi kuwo, Ninika cebo lini na kuthi, ukuze sibaguqule aba bantu bathethe kum, besithi, Yisuse idyokhwe awayibeka phezu kwethu uyihlo?', 'XHO22'),
(18, 10, 10, 'Athetha ke kuye amadodana aziintanga zakhe, esithi, Baphendule abantu abathethe kuwe, besithi, Uyihlo wayithwala idyokhwe yethu enzima, usinciphise kancinane; wothi kubo, Ucikicane wam uya kuba mkhulu kunesinqe sikabawo.', 'XHO22'),
(18, 10, 11, 'Kuba, ukuba ubawo unithwalise idyokhwe enzima, mna ke ndiya kongeza edyokhweni yenu;', 'XHO22'),
(18, 10, 12, 'Baya ke ooYarobheham nabantu bonke kuRehabheham ngomhla wesithathu, njengoko wayewise umthetho ngako ukumkani, wathi, Noweza kum ngomhla wesithathu;', 'XHO22'),
(18, 10, 13, 'uYehova wabaphendula kalukhuni; ukumkani uRehabheham walinikela umva ibhunga lamadoda amakhulu;', 'XHO22'),
(18, 10, 14, 'Wathetha kubo ngokwecebo lamadodana, esithi, Ubawo wanithwalisa idyokhwe enzima, mna ke ndiya kongeza kuyo;', 'XHO22'),
(18, 10, 15, 'Kungenxa yoko le nto ukumkani engabaphulaphulanga abantu, ngenxa yokuba lo mbandela wawungokaThixo, ukuze uYehova alizalise ilizwi awayelithethile ngoAhiya uMoshilo kuYerobhoham unyana kaNebhati.', 'XHO22'),
(18, 10, 16, 'Akubona ke onke amaSirayeli ukuba ukumkani akawaphulaphuli, abantu bambuyisela ukumkani ilizwi elithi, Sinasabelo sini na kuDavide? Asinalifa kunyana kaYese; Kaloku ke, Davide, gcina indlu yakho. Aya ezintenteni zawo onke amaSirayeli.', 'XHO22'),
(18, 10, 17, 'Ke oonyana bakaSirayeli, ababehleli emizini yakwaYuda, uRehabheham wabalawula.', 'XHO22'),
(18, 10, 18, 'Ukumkani uRehabheham uthuma uHadoram, umbuthi werhafu; Bamgibisela ngamatye oonyana bakaSirayeli, wafa. Ukumkani uRehabheham wakhawuleza, wamkhwelisa enqwelweni yakhe, wasabela eYerusalem.', 'XHO22'),
(18, 10, 19, 'Akreqa ke amaSirayeli kwindlu kaDavide, unanamhla.', 'XHO22'),
(18, 11, 1, 'Weza uRehabheham eYerusalem, wabizela ndawonye indlu yakwaYuda neyakwaBhenjamin, ikhulu elinamanci asibhozo anesibini amawaka amadoda okulwa, ukuba aye kulwa namaSirayeli, bubuyiselwe ubukumkani kuRehabheham.', 'XHO22'),
(18, 11, 2, 'Lafika ke ilizwi likaYehova kuShemaya umfo wakwaThixo, lisithi:', 'XHO22'),
(18, 11, 3, 'Thetha kuRehabheham unyana kaSolomon, ukumkani wakwaYuda, nakumaSirayeli onke kwaYuda nakwaBhenjamin, uthi,', 'XHO22'),
(18, 11, 4, 'Wathi uYehova, Ize ningenyuki niye kulwa nabazalwana benu; makabuyele elowo endlwini yakhe, ngokuba yenziwe kum le nto. Bawaphulaphula amazwi kaYehova baza babuya edabini nxamnye noYerobhoham.', 'XHO22'),
(18, 11, 5, 'Wahlala uRehabheham eYerusalem, wakha imizi enqatyisiweyo kwaYuda.', 'XHO22'),
(18, 11, 6, 'Wayakha iBhetelehem, ne-Etam, neTekowa;', 'XHO22'),
(18, 11, 7, 'neBhetesera, neSoko, neAdulam;', 'XHO22'),
(18, 11, 8, 'neGati, neMaresha, neSife;', 'XHO22'),
(18, 11, 9, 'neAdoraim, neLakishe, neAseki;', 'XHO22'),
(18, 11, 10, 'iTsoreya, neAyalon, neHebron, imizi enqatyisiweyo kwaYuda nakwaBhenjamin.', 'XHO22'),
(18, 11, 11, 'Wazomeleza iinqaba waza wabeka iirhuluneli kuzo noovimba bokutya neoli newayini.', 'XHO22'),
(18, 11, 12, 'Kwisixeko ngasinye wabeka amakhaka nemikhonto waza wayenza yomelela ngokugqithiseleyo, yaye uYuda noBhenjamin babesondele kuye.', 'XHO22'),
(18, 11, 13, 'Ababingeleli nabaLevi ababekwelakwaSirayeli lonke beza kuye bevela ezindaweni zabo zonke.', 'XHO22'),
(18, 11, 14, 'Kuba abaLevi bawashiya amadlelo abo nendyebo yabo baza beza kwaYuda naseYerusalem, ngenxa yokuba uYarobheham noonyana bakhe babebayekisile ekukhonzeni uYehova njengababingeleli.', 'XHO22'),
(18, 11, 15, 'Wazenzela ababingeleli beziganga, needemon, namathole eenkomo abewenzile;', 'XHO22'),
(18, 11, 16, 'Emva kwabo kuzo zonke izizwe zakwaSirayeli beza eYerusalem ababezimisele ukufuna uYehova uTHIXO kaSirayeli, ukuze benze umbingelelo kuYehova uTHIXO wookhokho babo.', 'XHO22'),
(18, 11, 17, 'Babuqinisa ubukumkani bukaYuda, bamomeleza uRehobhoham unyana kaSolomon iminyaka emithathu; bahamba emanyathelweni kaDavide noSolomon iminyaka emithathu.', 'XHO22'),
(18, 11, 18, 'uRehabheham wazeka uMahalati, intombi kaYerimoti, unyana kaDavide, noAbhihayili, intombi kaEliyabhi, unyana kaYese;', 'XHO22'),
(18, 11, 19, 'owamzalela abantwana; iYeyus neSamariya neZahama.', 'XHO22'),
(18, 11, 20, 'Emva kwakhe wazeka uMaga, intombi ka-Abhisalom; Nguye ke wamzalela uAbhiya, noAtayi, noSisa, noShelomiti.', 'XHO22'),
(18, 11, 21, 'uRehabheham wathanda uMahagi, intombi ka-Abhisalom, ngaphezu kwabafazi bakhe bonke, nezithende zakhe;', 'XHO22'),
(18, 11, 22, 'uRehabheham wamisa uAbhiya unyana kaMahaka, intloko, ukuba abe yintloko kubazalwana bakhe, ngokuba ubefuna ukumenza ukumkani.', 'XHO22'),
(18, 11, 23, 'Wenza ngengqiqo, wabathi saa bonke oonyana bakhe ezweni lakwaYuda nelakwaBhenjamin, emizini yonke enqatyisiweyo; wabanika ukutya okuninzi. Wafuna abafazi abaninzi.', 'XHO22'),
(18, 12, 1, 'Akubuqinisa uRehabheham ubukumkani, womelela, wawulahla umyalelo kaYehova, namaSirayeli onke kunye naye.', 'XHO22'),
(18, 12, 2, 'Kwathi ngomnyaka wesihlanu wokumkani uRehabheham, wenyuka uShishaki, ukumkani waseYiputa, wayifikela iYerusalem, ngokuba yonile kuYehova;', 'XHO22'),
(18, 12, 3, 'ngeenqwelo zokulwa eziliwaka elinamakhulu amabini, namashumi amathandathu amawaka amahashe; yaye abantu abaphuma kunye naye eYiputa babebaninzi; AmaLubhi, amaShukim namaTiyopiya.', 'XHO22'),
(18, 12, 4, 'Wazithimba izixeko ezinqatyisiweyo zakwaYuda, wada waya kufika eYerusalem.', 'XHO22'),
(18, 12, 5, 'Weza uShemaya umprofeti kuRehabheham, nakubathetheli bakwaYuda ababehlanganisene eYerusalem ngenxa kaShishaki, wathi kubo, uYehova wathi, Nina nindishiyile, nam ndinishiyile, ndanishiya esandleni sam kaShishaki.', 'XHO22'),
(18, 12, 6, 'Bazithoba abathetheli bakwaSirayeli nokumkani; bathi, Lilungisa uYehova.', 'XHO22'),
(18, 12, 7, 'Akubona uYehova ukuba bazithobile, lafika ilizwi likaYehova kuShemaya, lisithi, Bazithobile; ke ngoko, andiyi kubatshabalalisa, kodwa ndiya kubanika olunye usindiso; yaye umsindo wam awuyi kuthululelwa eYerusalem ngesandla sikaShishaki.', 'XHO22'),
(18, 12, 8, 'Baya kuba ngabakhonzi bakhe; ukuze bayazi inkonzo yam nenkonzo yoorhulumente belizwe.', 'XHO22'),
(18, 12, 9, 'uShishaki ukumkani waseYiputa wayihlasela iYerusalem waza wabuthabatha ubuncwane bendlu kaYehova nobuncwane bendlu kakumkani; wathabatha yonke into; wawathabatha namakhaka egolide abenziwe nguSolomon.', 'XHO22'),
(18, 12, 10, 'Ukumkani uRehabheham wenza amakhaka obhedu endaweni yawo, wawanika abathetheli bezigidimi, ababegcina umnyango wendlu yokumkani.', 'XHO22'),
(18, 12, 11, 'Ekungeneni kokumkani endlwini kaYehova, zeza izigidimi, zizithabatha, zazibuyisela egumbini lezigidimi.', 'XHO22'),
(18, 12, 12, 'Wathi akuzithoba, umsindo kaYehova wemka kuye, akafuna ukumtshabalalisa ngokupheleleyo; waye uYuda ehambe kakuhle.', 'XHO22'),
(18, 12, 13, 'Ukumkani uRehabheham wazenzela amandla eYerusalem, walawula; ngenxa yokuba uRehobhoham wayeneminyaka engamashumi amane ananye ubudala ekuqaliseni kwakhe ukulawula, yaye walawula iminyaka elishumi elinesixhenxe eYerusalem, isixeko awasinyulayo uYehova kuzo zonke izizwe zakwaSirayeli ukuba asimise igama lakhe apho. Igama lonina belinguNahama umAmon.', 'XHO22'),
(18, 12, 14, 'Wenza okubi, ngokuba engayibhekisanga intliziyo yakhe ukumquqela uYehova.', 'XHO22'),
(18, 12, 15, 'Ke yona imicimbi kaRehobhoham, eyokuqala neyokugqibela, ayibhalwanga na encwadini yomprofeti uShemaya nalowo wabona imibono uIdo ngokuphathelele izizukulwana? Kwaye kukho iimfazwe phakathi koRehabheham noYarobheham.', 'XHO22'),
(18, 12, 16, 'uRehabheham walala kooyise, wangcwatyelwa emzini kaDavide; unyana wakhe uAbhiya walawula esikhundleni sakhe.', 'XHO22'),
(18, 13, 1, 'Ngomnyaka weshumi elinesithoba anesibini wokumkani uYarobheham, uAbhiya waba ngukumkani wakwaYuda.', 'XHO22'),
(18, 13, 2, 'Waba neminyaka emithathu engukumkani eYerusalem. Igama lonina belinguMikaya, intombi kaUriyeli waseGibheha. Kwabakho imfazwe phakathi kuka-Abhiya noYarobheham.', 'XHO22'),
(18, 13, 3, 'uAbhiya wayixhobela imfazwe enempi yamagorha, engamakhulu amane amawaka ahleliweyo;', 'XHO22'),
(18, 13, 4, 'Wema uAbhiya ezintabeni zakwaSemarayim, kweleentaba lakwaEfrayim, wathi, Phulaphulani, Yarobheham, nani maSirayeli nonke!', 'XHO22'),
(18, 13, 5, 'Anazi na ukuba uYehova, uThixo kaSirayeli, wamnika uDavide ubukumkani bakwaSirayeli ngonaphakade, yena noonyana bakhe, ngomnqophiso wetyuwa?', 'XHO22'),
(18, 13, 6, 'Wesuka wema uYarobheham unyana kaNebhati, umkhonzi kaSolomon unyana kaDavide, wagwilika enkosini yakhe.', 'XHO22'),
(18, 13, 7, 'Kwandula ke kwahlanganisana kuye amadoda angenabuntu, oonyana bakaBheliyali, abomelezayo nxamnye noRehobhoham unyana kaSolomon, ngoxa uRehobhoham wayeselula yaye enentliziyo ebuthathaka yaye engakwazi ukumelana nabo.', 'XHO22'),
(18, 13, 8, 'Ke ngoku nicinga ngokubuchasa ubukumkani bukaYehova, obusezandleni zoonyana bakaDavide; yaba yinkamela, inamathole egolide, awawenzela uYarobheham ukuba abe nguthixo kuyo.', 'XHO22'),
(18, 13, 9, 'Asinini na aba bagxothayo ababingeleli bakaYehova, oonyana baka-Aron nabaLevi, naza anibamisela ababingeleli benu ngokwesiko leentlanga zakwamanye amazwe? ukuze nabani na oza kumngcwalisa ngenkunzi yenkomo nangeenkunzi zeegusha ezisixhenxe, abe ngumbingeleli ongenguye uthixo.', 'XHO22'),
(18, 13, 10, 'Ke thina, uYehova nguThixo wethu, asimshiyanga; yaye ababingeleli abakhonza uYehova ngoonyana baka-Aron, yaye abaLevi balindele umsebenzi wabo.', 'XHO22'),
(18, 13, 11, 'Baqhubeka besenza iminikelo etshiswayo nesiqhumiso kuYehova ntsasa nganye nangokuhlwa ngokuhlwa; nezonka zokubonisa bazibeka etafileni ehlambulukileyo; nesiphatho sezibane segolide, kwanezibane zaso zivutha ukuhlwa ngokuhlwa; Ke nina nimcekisile.', 'XHO22'),
(18, 13, 12, 'Kwaye qaphelani, uThixo ngenene unathi kumlawuli wethu, kwaye ababingeleli bakhe baphethe amaxilongo avakalisa ukumbulala ukumkani. Nina nyana bakaSirayeli, musani ukulwa noYehova, uThixo wooyihlo; ngokuba akuyi kuphumelela.', 'XHO22'),
(18, 13, 13, 'Ke uYarobheham wathuma abalaleli emva kwabo; bona babengaphambi kwamaYuda, nabalaleli babesemva kwawo.', 'XHO22'),
(18, 13, 14, 'Abheka ngasemva amaYuda, abona imfazwe ngaphambili nangasemva, anqula uYehova, ababingeleli bavuthela izigodlo.', 'XHO22'),
(18, 13, 15, 'Aduma ke amadoda akwaYuda; ekudumeni kwamadoda akwaYuda, uThixo wamoyisa uYarobheham namaSirayeli onke phambi koAbhiya namaYuda.', 'XHO22'),
(18, 13, 16, 'Basaba oonyana bakaSirayeli ebusweni bamaYuda, uThixo wabanikela esandleni sabo.', 'XHO22'),
(18, 13, 17, 'uAbhiya nabantu bakhe bababulala ngogonyamelo olukhulu, kangangokuba kwaSirayeli aba ngamakhulu amahlanu amawaka amadoda akhethiweyo.', 'XHO22'),
(18, 13, 18, 'Babehleli oonyana bakaSirayeli ngelo xesha, bakhalipha oonyana bakaYuda, ngokuba bayama ngoYehova uThixo wooyise.', 'XHO22'),
(18, 13, 19, 'uAbhiya wamsukela uYarobheham, wayithimba imizi yakhe: iBheteli namadlelo ayo, neYesana namadlelo ayo, nakwaEfrayim namadlelo ayo.', 'XHO22'),
(18, 13, 20, 'Wayengasenamandla uYarobheham ngemihla ka-Abhiya; uYehova wambetha, wafa.', 'XHO22'),
(18, 13, 21, 'Wazomeleza uAbhiya, wazeka abafazi abalishumi elinabane, wazala oonyana abamashumi mabini anababini, neentombi ezilishumi elinantandathu.', 'XHO22'),
(18, 13, 22, 'Ingxelo eqhubekayo ka-Abhiya neendlela zakhe namazwi akhe ichazwe kwibali lomprofeti uIdo.', 'XHO22'),
(18, 14, 1, 'uAbhiya walala noyise, wangcwatyelwa emzini kaDavide; unyana wakhe uAsa waqalisa ukulawula esikhundleni sakhe. Ngemihla yakhe ilizwe lazola iminyaka elishumi.', 'XHO22'),
(18, 14, 2, 'uAsa wenza okulungileyo nokuthe tye emehlweni kaYehova uThixo wakhe.', 'XHO22'),
(18, 14, 3, 'Wazisusa izibingelelo zabanye oothixo neendawo eziphakamileyo, wayidiliza imifanekiso eqingqiweyo waza wagawula namatyholo.', 'XHO22'),
(18, 14, 4, 'AmaYuda awayalela ukuba afune uYehova, uThixo wooyise, aze azalisekise umthetho nemiyalelo.', 'XHO22'),
(18, 14, 5, 'Kwakhona wazisusa iindawo eziphakamileyo nemifanekiso eqingqiweyo kuzo zonke izixeko zakwaYuda, baza ke ubukumkani bazola phambi kwakhe.', 'XHO22'),
(18, 14, 6, 'Wazakha izixeko ezinqatyisiweyo kwaYuda, ngenxa yokuba ilizwe lalizolile, yaye kwakungekho mfazwe ngaloo minyaka; ngokuba uYehova wayemphumzile.', 'XHO22'),
(18, 14, 7, 'Wathi ke kumaYuda, Masiyakhe le mizi, senze iindonga ngeenxa zonke kuyo, sibe neenqaba ezinde, sibe namasango, namasango, libe phambi kwethu ilizwe; ngokuba samquqela uYehova uThixo wethu, samquqela kuye, wasiphumza ngeenxa zonke. Bakha ke baba nempumelelo.', 'XHO22'),
(18, 14, 8, 'uAsa wayenomkhosi wamajoni axhobileyo nezikhali, amakhulu amathathu amawaka avela kwaYuda; kwisizwe sakwaBhenjamin, abaphatha iingweletshetshe, batyeda izaphetha, babengamakhulu amabini, anamanci asithoba anamabini amawaka; bonke babengamagorha akhaliphileyo.', 'XHO22'),
(18, 14, 9, 'Waphuma uSeraki wakwaKushe waya kubo, enempi engamawaka amathathu eenqwelo zokulwa; waya eMarésa.', 'XHO22'),
(18, 14, 10, 'uAsa waya kumhlangabeza, balwa entlanjeni yaseSefata eMaresha.', 'XHO22'),
(18, 14, 11, 'uAsa wabiza kuYehova uThixo wakhe, wathi, Yehova, aniyi kubanceda abaninzi nabasweleyo; sincede, Yehova, Thixo wethu! ngokuba sikholose ngawe, sihlasela esi sihlwele ngegama lakho. Yehova, unguThixo wethu; ungaphoswa mntu.', 'XHO22'),
(18, 14, 12, 'Ngoko uYehova wawabulala amaKushe phambi kuka-Asa naphambi koYuda; asaba amaKushi.', 'XHO22'),
(18, 14, 13, 'uAsa namadoda abenaye bawasukela, bada besa eGerare; ngokuba aqotywa phambi koYehova, naphambi kweentshaba zakhe; baphanga into eninzi yamaxhoba.', 'XHO22'),
(18, 14, 14, 'Bayingqinga yonke imizi ngeenxa zonke eGerare; ngokuba bafikelwa kukunkwantya kukaYehova, bayithimba yonke imizi; ngokuba amaxhoba ebemaninzi kubo.', 'XHO22'),
(18, 14, 15, 'Bahlala ezintenteni zeenkomo, bathimba impahla emfutshane, neenkamela, zininzi, baza babuyela eYerusalem.', 'XHO22'),
(18, 15, 1, 'uMoya kaThixo wamfikela uAzariya, unyana kaOdedi.', 'XHO22'),
(18, 15, 2, 'Waphuma, esiya kukhawulela uAsa, wathi kuye, Ndiphulaphuleni, Asa, nani nonke maYuda namaBhenjamin; uYehova unani ekubeni ninaye; ukuba uthe wamfuna, womfumana; ukuba nithe namshiya, wonishiya nani.', 'XHO22'),
(18, 15, 3, 'Ke kaloku kwabakho ixesha elide uSirayeli engenaye uThixo wokwenyaniso nombingeleli ofundisayo nomthetho.', 'XHO22'),
(18, 15, 4, 'Kwathi, ekubandezelekeni kwawo, abuyela kuYehova, uThixo kaSirayeli, amquqela, wafunyanwa phakathi kwawo.', 'XHO22'),
(18, 15, 5, 'Ngaloo mihla bekungekho luxolo kwabaphumayo, nakubangenayo; zabafikela izijeziso ezikhulu abemi baloo mazwe.', 'XHO22'),
(18, 15, 6, 'Batshatyalaliswa ke abantu, bangabikho phakathi komzi nesixeko, kuba uThixo wayebatshutshisa ngazo zonke iintlekele.', 'XHO22'),
(18, 15, 7, 'Yomelelani; mazingawi izandla zenu; ngokuba umsebenzi wenu unomvuzo.', 'XHO22'),
(18, 15, 8, 'Wathi uAsa akuweva la mazwi nezihlabo zikaOdedi umprofeti, womelela, wazisusa imifanekiso enezothe ezweni lonke lakwaYuda nelakwaBhenjamin, nemizi awazithimbayo kweleentaba lakwaEfrayim, wazihlaziya, wasisusa isibingelelo kaYehova ephambi kwevaranda kaYehova.', 'XHO22'),
(18, 15, 9, 'Wawahlanganisa onke amaYuda namaBhenjamin nabasemzini ababekuwo, bakwaEfrayim, nabakwaManase, nabakwaSimon; ngenxa yokuba baxabela kuye bebaninzi kwaSirayeli, bakubona ukuba uYehova uThixo wakhe wayenaye.', 'XHO22'),
(18, 15, 10, 'Bahlanganisana eYerusalem ngenyanga yesithathu ngomnyaka weshumi elinesihlanu wobukumkani buka-Asa.', 'XHO22'),
(18, 15, 11, 'Babingelela kuYehova ngelo xesha lokuphanga, iinkomo ezingamakhulu asixhenxe namawaka asixhenxe empahla emfutshane.', 'XHO22'),
(18, 15, 12, 'Benza umnqophiso wokumquqela uYehova, uThixo wooyise, ngentliziyo yabo yonke, nangomphefumlo wabo wonke;', 'XHO22'),
(18, 15, 13, 'ukuze wonk’ ubani ongamfuniyo uYehova, uThixo kaSirayeli, abulawe, kuthabathele komncinane kuse kwixhego, kuthabathele kwindoda kuse kwinkazana.', 'XHO22'),
(18, 15, 14, 'Bafunga kuYehova ngezwi elikhulu, nangentswahla, nangamaxilongo, nangamaxilongo.', 'XHO22'),
(18, 15, 15, 'Asivuyela onke amaYuda isifungo eso; ngokuba afunga ngentliziyo yawo yonke, omelela ngokomnqweno wawo wonke; yaye wabonwa kubo, waza uYehova wabanika ukuphumla macala onke.', 'XHO22'),
(18, 15, 16, 'Kananjalo uMaga, unina wokumkani uAsa, wamsusa ebukumkanini, ngokuba wenze umfanekiso oqingqiweyo ehlathini;', 'XHO22'),
(18, 15, 17, 'Ke iziganga azisuswanga kwaSirayeli; kodwa yona intliziyo ka-Asa yayigqibelele yonke imihla yakhe.', 'XHO22'),
(18, 15, 18, 'Wazizisa endlwini kaThixo izinto uyise awayezingcwalisile, awayezingcwalisile: isilivere negolide nempahla.', 'XHO22'),
(18, 15, 19, 'Akwaba sabakho mfazwe, kwada kwaba ngumnyaka wamashumi amathathu anesihlanu wobukumkani buka-Asa.', 'XHO22'),
(18, 16, 1, 'Ngomnyaka wamashumi omathathu anamithandathu wobukumkani buka-Asa, uBhahesha ukumkani wakwaSirayeli wawafikela amaYuda, wayakha iRama, esithi, makangaphumi mntu, angene noAsa ukumkani wakwaYuda.', 'XHO22'),
(18, 16, 2, 'uAsa wathabatha isilivere negolide kubuncwane bendlu kaYehova nobendlu kakumkani waza wathumela kuBhen-hadadi ukumkani wakwa-Aram, owayehlala eDamasko, esithi:', 'XHO22'),
(18, 16, 3, 'Kukho umnqophiso phakathi kwam nawe, phakathi kobawo noyihlo; niyabona, ndinithumela isilivere negolide; hamba, uwaphule umnqophiso wakho noBhahesa ukumkani wakwaSirayeli, ukuba andishiye.', 'XHO22'),
(18, 16, 4, 'uBhen-hadade wamthobela ukumkani uAsa, wathumela iingqwayi-ngqwayi zomkhosi wakhe, ukuba ziye kuhlasela izixeko zakwaSirayeli yaye ayixabela i-Iyon neDana neAlemayim nazo zonke izixeko zakwaNafetali.', 'XHO22'),
(18, 16, 5, 'Akukuva oko uBhahesa, wayeka ukuyakha iRama, wawuyeka umsebenzi wakhe.', 'XHO22'),
(18, 16, 6, 'Ukumkani uAsa wawathabatha onke amaYuda; bawathabatha amatye eRama, nemithi yayo, abesakhiwe ngayo uBhahesha; waza wakha ngayo iGebha neMizpa.', 'XHO22'),
(18, 16, 7, 'Ngelo xesha, weza uHanani, imboni, kuAsa, ukumkani wakwaYuda, wathi kuye, Ekubeni wayame kukumkani wama-Aram, akwakholosa ngoYehova uThixo wakho, ngenxa yoko iintendelezo zelizwe. Wasinda ukumkani wama-Aram isandla sakho.', 'XHO22'),
(18, 16, 8, 'Andithi na ke amaKushi namaLubhi abe ngumkhosi omkhulu weenqwelo zokulwa nabamahashe? kodwa ngenxa yokuba wayama ngoYehova, wawanikela esandleni sakho.', 'XHO22'),
(18, 16, 9, 'Kuba amehlo kaYehova akhangela emhlabeni wonke ukuba omelele ngenxa yabo bantliziyo zizele nguye. Nenze ngobudenge; ke ngoko, niya kuba neemfazwe ukususela ngoku ukuya phambili.', 'XHO22'),
(18, 16, 10, 'uAsa wayiqumbela imboni leyo, wayifaka entolongweni; ngokuba wayemqumbele ngenxa yoko. uAsa wabacinezela abantu ngelo xesha.', 'XHO22'),
(18, 16, 11, 'Yabona, iziganeko zika-Asa, ezokuqala nezokugqibela, nanzo zibhaliwe encwadini yookumkani bakwaYuda nabakwaSirayeli.', 'XHO22'),
(18, 16, 12, 'uAsa waba buthathaka ezinyaweni zakhe ngomnyaka wamashumi omathathu anesibhozo wobukumkani bakhe, kwada kwaba kukhulu ukufa kwakhe; kodwa ekuguleni kwakhe akazange amfune uYehova, kodwa wafuna oogqirha.', 'XHO22'),
(18, 16, 13, 'uAsa walala kooyise, wafa ngomnyaka wamashumi omane anamnye wobukumkani bakhe.', 'XHO22'),
(18, 16, 14, 'Bamngcwabela engcwabeni lakhe abemenzele lona, emzini kaDavide; bamlalisa esingqengqelweni esizele bubulawu, nobobulawu beentlobo zonke, obulungiselwe ngobuchule babaqholi; bamenzela umlilo omkhulu kunene.', 'XHO22'),
(18, 17, 1, 'uYehoshafati, unyana wakhe, waba ngukumkani esikhundleni sakhe, wamomeleza ukuba alwe namaSirayeli.', 'XHO22'),
(18, 17, 2, 'Wamisa umkhosi kuzo zonke izixeko ezinqatyisiweyo zakwaYuda waza wamisa imikhosi yamajoni kwilizwe lakwaYuda nakwizixeko zakwaEfrayim ezo uAsa uyise awazithimbayo.', 'XHO22'),
(18, 17, 3, 'uYehova waba noYehoshafati; ngokuba wahamba ngeendlela zokuqala zikaDavide uyise, akabaquqela ooBhahali;', 'XHO22'),
(18, 17, 4, 'wamquqela uYehova, uThixo kayise, wahamba ngokwemithetho yakhe, kungekhona ngokwezenzo zamaSirayeli.', 'XHO22'),
(18, 17, 5, 'uYehova wabuqinisa ubukumkani esandleni sakhe; yaye onke amaYuda azisa izipho kuYehoshafati; wazuza ilifa ubutyebi nozuko ngokuyintabalala.', 'XHO22'),
(18, 17, 6, 'Yagcoba intliziyo yakhe ngeendlela zikaYehova, wazisusa iziganga, nemithi engcwele kwaYuda;', 'XHO22'),
(18, 17, 7, 'Kwakhona, ngonyaka wesithathu wolawulo lwakhe, wathumela kwiinkosana zakhe, uBhen-hayili, uObhadiya, uZekariya, uNataniyeli noMikaya, ukuba baye kufundisa kwizixeko zakwaYuda.', 'XHO22'),
(18, 17, 8, 'Wabathumela kunye nabo abaLevi, ooShemaya, noNetaniya, noZebhadiya, noAsaheli, noShemiramoti, noYonatan, noAdoniya, noTobhiya, noTobhadoniya, abaLevi; kunye noElishama noYoram, ababingeleli.', 'XHO22'),
(18, 17, 9, 'Baqalisa ukufundisa kwilizwe lakwaYuda yaye benencwadi yomthetho kaYehova yaye bazijikeleza zonke izixeko zakwaYuda baza bafundisa abantu.', 'XHO22'),
(18, 17, 10, 'Ukoyikwa kukaYehova kwazifikela zonke izikumkani zamazwe ajikeleze uYuda, ukuba zingalwi noYehoshafati.', 'XHO22'),
(18, 17, 11, 'AmaFilisti athile azisa izipho kuYehoshafati nemali yerhafu; Ama-Arabhu amzisela imfuyo, amawaka asixhenxe anamakhulu asixhenxe eenkunzi zeegusha, namawaka asixhenxe anamakhulu asixhenxe eebhokhwe.', 'XHO22'),
(18, 17, 12, 'uYoshafati waba mkhulu kunene; wakha iinqaba nezixeko ezingoovimba kwilizwe lakwaYuda.', 'XHO22'),
(18, 17, 13, 'Wayenemicimbi emininzi kwizixeko zakwaYuda, yaye kwakukho amadoda okulwa namagorha anamandla eYerusalem.', 'XHO22'),
(18, 17, 14, 'Lilo eli inani labo ngokwezindlu zooyise: kwaYuda njengabathetheli kwiwaka; uAdna, inkokeli, wayenamadoda okulwa angamakhulu amathathu amawaka.', 'XHO22'),
(18, 17, 15, 'Olandelayo ibinguYohanan, intloko; ibingamakhulu amabini anamanci asibhozo amawaka.', 'XHO22'),
(18, 17, 16, 'Ngakuye nguAmatsiya unyana kaSigri, owazinikela ngokuqhutywa yintliziyo ukuze akhonze uYehova; unamagorha angamakhulu amabini amawaka.', 'XHO22'),
(18, 17, 17, 'NoBhenjamin; uElijada, igorha elinamandla, enamadoda axhobe isaphetha namakhaka angamakhulu amabini amawaka;', 'XHO22'),
(18, 17, 18, 'ngakuye nguYozabhadi, enekhulu elinamanci asibhozo amawaka, axhobele imfazwe.', 'XHO22'),
(18, 17, 19, 'Babekhonza ukumkani, ngaphandle kwabo ukumkani wayebabeke kwizixeko ezinqatyisiweyo kulo lonke elakwaYuda.', 'XHO22'),
(18, 18, 1, 'uYoshafati waye enobutyebi nozuko olukhulu, waza wahamba noAhabhi.', 'XHO22'),
(18, 18, 2, 'Emva kweminyaka emininzi waya kuAhabhi kwaSamari. Yaye uAhabhi wambulala yena nabantu bakhe, enemihlambi yezimvu neenkomo, wamcenga ukuba anyuke naye aye eRamoti yaseGiliyadi.', 'XHO22'),
(18, 18, 3, 'Wathi uAhabhi ukumkani wakwaSirayeli kuYehoshafati ukumkani wakwaYuda, Wohamba na nam, siye eRamoti yaseGiliyadi? Wathi kuye, Ndinjengawe, nabantu bam, banjengabantu bakho; kwaye siya kuba nani edabini.', 'XHO22'),
(18, 18, 4, 'Wathi uYehoshafati kukumkani wakwaSirayeli, Cela ilizwi likaYehova namhla.', 'XHO22'),
(18, 18, 5, 'Ngoko ukumkani wakwaSirayeli wabiza amakhulu amané abaprofeti, wathi kubo, Siye kulwa na neRamoti yaseGiliyadi, ndiyishiye, kusini na? Athi, Nyuka; ngokuba uThixo uya kuyinikela esandleni sokumkani.', 'XHO22'),
(18, 18, 6, 'Wathi uYehoshafati, Akukho mprofeti kaYehova khona; singambuza bani na?', 'XHO22'),
(18, 18, 7, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Kusekho enye indoda, esingabuza ngayo kuYehova; ke mna ndimthiyile; ngokuba ingaprofeti okulungileyo kuye, iprofeta okubi ngamaxesha onke; nguMikaya, unyana kaImla. Wathi uYehoshafati, Makangatsho uYehova.', 'XHO22'),
(18, 18, 8, 'Ukumkani wakwaSirayeli wabiza omnye wabakhonzi bakhe, wathi, Khawulezani, nize noMika unyana kaImla.', 'XHO22'),
(18, 18, 9, 'Ukumkani wakwaSirayeli noYehoshafati, ukumkani wakwaYuda, wamisa omnye wabo etroneni yakhe, bevathe iingubo zabo, bahlala endaweni engenanto, esangweni lakwaSamari; yaye bonke abaprofeti babeprofeta phambi kwabo.', 'XHO22'),
(18, 18, 10, 'uZedekiya, unyana kaKanan, wazenzela iimpondo zesinyithi, wathi, Utsho uYehova ukuthi, Uya kuwagqogqa ngalezi ama-Aram, uwatshabalalise.', 'XHO22'),
(18, 18, 11, 'Bonke abaprofeti benjenjalo ukuprofeta, besithi, Nyuka uye eRamoti yaseGiliyadi, ube nempumelelo; ngokuba uYehova uya kuyinikela esandleni sokumkani.', 'XHO22'),
(18, 18, 12, 'Umthunywa owayebize uMika wathetha kuye, wathi, Yabona, amazwi abaprofeti bemxhelo mnye ukumkani; Ilizwi lakho malibe njengenye yazo, uthethe kakuhle.', 'XHO22'),
(18, 18, 13, 'Wathi uMika, Ehleli nje uYehova, oko aya kukuthetha uThixo wam, ndothetha kona.', 'XHO22'),
(18, 18, 14, 'Wafika kukumkani; wathi ukumkani kuye, Mika, siye na eRamoti yaseGiliyadi ukuba silwe, ndiyishiye, kusini na? Wathi, Phuma, uphumelele, kwaye baya kunikelwa ezandleni zakho.', 'XHO22'),
(18, 18, 15, 'Wathi ukumkani kuye, Makube zizihlandlo ezingaphi na ndikucebisa, ukuba ungandixeleli nto, ingeyiyo nyaniso egameni likaYehova?', 'XHO22'),
(18, 18, 16, 'Wathi, Ndibone amaSirayeli onke elusali ezintabeni, njengezimvu ezingenamalusi; Wathi uYehova, Abanankosi; mababuyele elowo endlwini yakhe enoxolo.', 'XHO22'),
(18, 18, 17, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Ndathi, Bendingakuxelelanga, ndathi, Akayi kuprofeta okulungileyo ngam, koprofeta okubi?', 'XHO22'),
(18, 18, 18, 'Wathi, Liphulaphuleni ke ilizwi likaYehova; Ndayibona iNkosi ihleli etroneni yayo, isithi yonke imikhosi yezulu imi ngasekunene kwayo nangasekhohlo kwayo.', 'XHO22'),
(18, 18, 19, 'Wathi uYehova, Ngubani na oya kuhenda uAhabhi, ukumkani wamaSirayeli, ukuba enyuke aye abulawelwa eRamoti yaseGiliyadi? Omnye wathetha oku, omnye wathi leya.', 'XHO22'),
(18, 18, 20, 'Waphuma omnye umoya, wema phambi koYehova, wathi, Ndiya kumlinga. Wathi uYehova kuye: Ngoba?', 'XHO22'),
(18, 18, 21, 'Wathi yena, Ndophuma ndiye, ndibe ngumoya wobuxoki emlonyeni wabaprofeti bakhe bonke. Wathi uYehova, Mcikide, woyise nawe; phuma uyekwenza.', 'XHO22'),
(18, 18, 22, 'Uyabona ke, uYehova ungenise umoya wobuxoki emlonyeni wabo baprofeti bakho; uthethe ngomsindo kaYehova ngani.', 'XHO22'),
(18, 18, 23, 'Wasondela uZedekiya, unyana kaKanan, wambetha uMika esidleleni, wathi, Ugqithe ngantoni na kum uMoya kaYehova, ukuba aye kuthetha kuwe?', 'XHO22'),
(18, 18, 24, 'Wathi uMika, Yabona, ùya kuzimela egumbini elingaphakathi;', 'XHO22'),
(18, 18, 25, 'Wathi ukumkani wakwaSirayeli, Mthabathe uMika, umse kuAmon umphathi womzi, nakuYowashe unyana wokumkani;', 'XHO22'),
(18, 18, 26, 'Wathi ukumkani, Yifakeni entolongweni le ndoda, niyinike isonka somvandedwa namanzi osizi, ndide ndibuye ndinoxolo.', 'XHO22'),
(18, 18, 27, 'Wathi uMika, Ukuba uthe wabuya unoxolo, woba uYehova akathethanga ngam. Wathi, Yivani, zintlanga zonke;', 'XHO22'),
(18, 18, 28, 'Wenyuka ukumkani wakwaSirayeli noYehoshafati, ukumkani wakwaYuda, baya eRamoti yaseGiliyadi.', 'XHO22'),
(18, 18, 29, 'Wathi ukumkani wakwaSirayeli kuYehoshafati, Ndiya kuzenza mntu wumbi, ndingene ekulweni; kodwa nxibani iimpahla zenu. Wazenza mntu wumbi ukumkani wakwaSirayeli; baya emfazweni.', 'XHO22'),
(18, 18, 30, 'Ukumkani wakwa-Aram wabawisela umthetho abathetheli beenqwelo zokulwa ababenaye, wathi, Ize ningalwi nomncinane nomkhulu; ize ningalwi nokumkani wakwaSirayeli;', 'XHO22'),
(18, 18, 31, 'Bambona uYehoshafati abathetheli beenqwelo zokulwa, bathi, Nanku ukumkani wakwaSirayeli; Bamhlasela, balwa naye. uYehoshafati wamphikisa; uYehova wamnceda, uYehova; waza uThixo wawagxotha kuye.', 'XHO22'),
(18, 18, 32, 'Kwathi, bakubona abathetheli beenqwelo zokulwa ukuba akanguye ukumkani wakwaSirayeli, babuya beza kumsukela.', 'XHO22'),
(18, 18, 33, 'Indoda yatola ngesaphetha ngento elukhuni, yambulala ukumkani wakwaSirayeli ekuhlanganeni kotolo kuba ndonzakele.', 'XHO22'),
(18, 18, 34, 'Ukulwa kwenyuka loo mini; ukumkani wakwaSirayeli wamisa enqwelweni yakhe kuma-Aram kwada kwahlwa; wathi lakutshona ilanga wafa.', 'XHO22'),
(18, 19, 1, 'Wabuya uYehoshafati ukumkani wakwaYuda, waya eYerusalem enoxolo, endlwini yakhe.', 'XHO22'),
(18, 19, 2, 'Waphuma uYehu, unyana kaHanani, imboni, esiya kumkhawulela, wathi kukumkani uYehoshafati, Uyabanceda na abangendawo, ubathande na abathiyi bakaYehova? ngenxa yoko bunifikele uburhalarhume, buphuma ebusweni bukaYehova.', 'XHO22'),
(18, 19, 3, 'Ke wena wafunyanwa izinto ezilungileyo, ngenxa enokuba wasusa amatyholo ezweni, wayijika intliziyo yakho ekumquqeleni uThixo.', 'XHO22'),
(18, 19, 4, 'uYehoshafati wahlala eYerusalem, wabuya waphuma, wabacanda abantu, ethabathela eBher-shebha wesa kweleentaba lakwaEfrayim, wababuyisela kuYehova, uThixo wooyise.', 'XHO22'),
(18, 19, 5, 'Wamisa abagwebi ezweni lonke, emizini yakwaYuda enqatyisiweyo, emizini ngemizi;', 'XHO22'),
(18, 19, 6, 'Wathi kubagwebi, Kulumkeleni enikwenzayo; ngokuba anigwebi bantu, nigweba uYehova oniwe ekugwebeni.', 'XHO22'),
(18, 19, 7, 'Ke ngoko ukoyika uYehova kuya kuba phezu kwenu; Ziboneni ngokwenu, nenze; ngokuba kuYehova uThixo wethu akukho bubi, akukho kuthulula abantu, kungamkeli sicengo.', 'XHO22'),
(18, 19, 8, 'Ngaphezu koko, uYehoshafati wayinikela iYerusalem kubaLevi nakubabingeleli nakwiintloko zeentsapho zoonyana bakaSirayeli ngenxa yomgwebo kaYehova nembambano ekubuyeleni kwabo eYerusalem.', 'XHO22'),
(18, 19, 9, 'Wabawisela umthetho, wathi, Yenzani ngokoyika uYehova ngokunyanisekileyo, nangentliziyo epheleleyo.', 'XHO22'),
(18, 19, 10, 'Ngokuphathelele abazalwana benu, yenzani oku, ningoni.', 'XHO22'),
(18, 19, 11, 'Yabonani, umbingeleli omkhulu uAmariya uya kunilawula kuzo zonke iindawo zeNkosi; noZebhadiya unyana kaIshmayeli, umphathi wendlu yakwaYuda, kwimicimbi yonke yokumkani; nabaLevi boba nani. Yiba nesibindi, yaye uYehova uya kuba nolungileyo.', 'XHO22'),
(18, 20, 1, 'Emveni koko oonyana bakaMowabhi, noonyana baka-Amon, nabanye kunye nabo, ngaphandle koonyana baka-Amon, bamxabela uYehoshafati.', 'XHO22'),
(18, 20, 2, 'Beza ke abanye, bamxelela uYehoshafati, bathi, Uhlaselwa sisihlwele esikhulu, sivela phesheya kolwandle lwama-Aram; bona, abantu baseHadazon-tamare, oko kukuthi i-Engedi.', 'XHO22'),
(18, 20, 3, 'Woyika uYehoshafati waza wamkhangela uYehova, waza wavakalisa usuku lokuzila ukutya kulo lonke elakwaYuda.', 'XHO22'),
(18, 20, 4, 'Wahlanganisana ke amaYuda, efuna uncedo kuYehova; nasemizini yonke yakwaYuda beza kumquqela uYehova.', 'XHO22'),
(18, 20, 5, 'Wema uYehoshafati ebandleni lakwaYuda nelaseYerusalem, endlwini kaYehova, entendelezweni entsha;', 'XHO22'),
(18, 20, 6, 'Wathi, Yehova, Thixo woobawo, asinguwe na onguThixo emazulwini? Asinguwe na olawula zonke izikumkani zeentlanga? Akukho mandla namandla esandleni sakho, akukho bani unokukuchasa?', 'XHO22'),
(18, 20, 7, 'Asinguwe na uThixo wethu, owabagxothayo abemi beli lizwe phambi kwabantu bakho amaSirayeli waza walinika intsapho ka-Abraham umhlobo wakho ukusa kwixesha elingenammiselo?', 'XHO22'),
(18, 20, 8, 'Bahlala kulo, balakhela igama lakho ingcwele, besithi:', 'XHO22'),
(18, 20, 9, 'Ukuba sithe sahlelwa sisihelegu esinjengekrele, nesigwebo, nokuba kukufa, ngendyikitya yokufa, nangendlala, sema phambi kwale ndlu naphambi kobuso bakho, (ngokuba likule ndlu igama lakho), ubize usiva ubunzima; wova, sincede.', 'XHO22'),
(18, 20, 10, 'Kaloku ke naba oonyana baka-Amon, namaMowabhi, neentaba zakwaSehire, owawungathandi ukuba babaxabele oonyana bakaSirayeli ekuphumeni kwabo ezweni laseYiputa, wabanikela umva, akwabatshabalalisa bona;', 'XHO22'),
(18, 20, 11, 'Yabona, ndithi, baya kusibuyekeza ngokuza kusigxotha ebuncwaneni bakho osinikileyo, ukuba sibe lilifa.', 'XHO22'),
(18, 20, 12, 'Thixo wethu, akufuni ukubagweba na? kuba asinamandla okumelana nendimbane enkulu esiza nxamnye nathi; kwaye asazi ukuba senze ntoni na, kodwa amehlo ethu akuwe.', 'XHO22'),
(18, 20, 13, 'Ema ke onke amaYuda phambi koYehova, neentsana zawo, nabafazi bawo, nabantwana bawo.', 'XHO22'),
(18, 20, 14, 'uYahaziyeli unyana kaZekariya unyana kaBhenaya unyana kaYehiyeli unyana kaMataniya umLevi woonyana baka-Asafu wafikelwa ngumoya kaYehova, umoya kaYehova wawuphakathi kwebandla fika;', 'XHO22'),
(18, 20, 15, 'Wathi, Yivani, nonke maYuda, nani bemi baseYerusalem, nawe kumkani Yehoshafati. Utsho uYehova ukuthi, Musani ukoyika, musani ukoyika, musani ukoyika ngenxa yale ngxokolo inje ukuba ninzi; ngokuba imfazwe asiyeyenu, yekaThixo.', 'XHO22'),
(18, 20, 16, 'Yihlani, niye kubabona ngomso; nabo benyuka baya eliweni likaSis; nobafumana ekupheleni komlambo, entlango yaseYeruweli.', 'XHO22'),
(18, 20, 17, 'Ize ningalwi eli dabi; yimani, yimani, nilubone usindiso lukaYehova onani, maYuda neYerusalem; musani ukoyika, musani ukoyika; Bahlaseleni ngomso, ngokuba uYehova unani.', 'XHO22'),
(18, 20, 18, 'Waqubuda uYehoshafati ngobuso, namaYuda onke nabemi baseYerusalem bawa phambi koYehova, baqubuda kuYehova.', 'XHO22'),
(18, 20, 19, 'Basuka bema abaLevi, koonyana bakaKehati, nakoonyana bakaKora, ukuba badumise uYehova, uThixo kaSirayeli, ngezwi elikhulu.', 'XHO22'),
(18, 20, 20, 'Bavuka kusasa ngengomso, baya entlango yeTekowa; bakuba begqibile, wema uYehoshafati, wathi, Ndiphulaphuleni, nina maYuda, nani bemi baseYerusalem; Kholwani ngoYehova uThixo wenu, niya kuqiniseka; kholwani kubaprofeti bakhe, noba nempumelelo.', 'XHO22'),
(18, 20, 21, 'Uthe ke akuba ethethile ebantwini, wamisa iimvumi zozuko lukaYehova, ezidumisa uzuko lobungcwele, ukuba zihambe phambi kwamatyala, zisithi, Dumisani uYehova! Ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(18, 20, 22, 'Yaye kwathi ekuqaliseni kwabo ukumemelela nokuzisola, uYehova wabeka iinkwenkwezi phezu koonyana baka-Amon, nakwaMowabhi, naphezu kweentaba zakwaSehire, ababeza kulwa noYuda; babethwa.', 'XHO22'),
(18, 20, 23, 'Oonyana baka-Amon namaMowabhi basukela phezulu kubemi bentaba yakwaSehire, ukuba babaqalekise, babatshabalalise;', 'XHO22'),
(18, 20, 24, 'Akufika amaYuda emboniselweni esentlango, asikhangela eso sihlwele, abona ukuba zizidumbu, ziwe emhlabeni, kungekho usindileyo.', 'XHO22'),
(18, 20, 25, 'uYehoshafati nabantu bakhe beza kuphanga amaxhoba kubo, bafumana kubo ubutyebi obukhulu, nezidumbu, neempahla ezinqwenelekayo, bazithabathela kubo; ababa nakuhamba nabo; bachitha iintsuku ezintathu beqokelela amaxhoba, nto leyo eyayininzi kakhulu.', 'XHO22'),
(18, 20, 26, 'Ngosuku lwesine bahlanganisana entilini yaseBheraga; ngokuba babemnqula khona uYehova; ke ngoko, indawo leyo yathiywa ngokuba yiMoedi waBeraga kude kube namhlanje.', 'XHO22'),
(18, 20, 27, 'Abuya, onke amadoda akwaYuda nawaseYerusalem, ekhokelwa nguYehoshafati, ukuba abuyele eYerusalem evuya; ngokuba uYehova wabavuyisa ngenxa yeentshaba zabo.', 'XHO22'),
(18, 20, 28, 'Afika eYerusalem enemirhubhe, neehadi, namaxilongo, aya endlwini kaYehova.', 'XHO22'),
(18, 20, 29, 'Ukoyika uThixo kwabakho kubo bonke oorhulumente baloo mazwe bakuva ukuba uYehova ulwe neentshaba zikaSirayeli.', 'XHO22'),
(18, 20, 30, 'Bathi cwaka ke ubukumkani bukaYehoshafati, ngokuba uThixo wakhe ubemphumzile ngeenxa zonke.', 'XHO22'),
(18, 20, 31, 'uYehoshafati waye engukumkani kwaYuda; Ubeminyaka imashumi mathathu anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mabini anamihlanu engukumkani eYerusalem. Igama lonina belinguAsubha, intombi kaSilehi.', 'XHO22'),
(18, 20, 32, 'Waqhubeka ehamba ngendlela kayise uAsa, akazange aphambuke ekwenzeni okulungileyo emehlweni kaYehova.', 'XHO22'),
(18, 20, 33, 'Ke iziganga azisuswanga; ngokuba abantu bebengayilungisanga intliziyo yabo kuThixo wooyise.', 'XHO22'),
(18, 20, 34, 'Ezinye izinto zikaYehoshafati, ezokuqala nezokugqibela, nanzo zibhaliwe encwadini kaYehu unyana kaHanani, ebhalwe encwadini yookumkani bakwaSirayeli.', 'XHO22'),
(18, 20, 35, 'Emva koko uYehoshafati, ukumkani wakwaYuda, wema noAhaziya, ukumkani wakwaSirayeli, owayesenza ububi obukhulu.', 'XHO22'),
(18, 20, 36, 'Wazimanya naye ngokwenza iinqanawa eziya eTarshishe; benza iinqanawa eEsionbhere.', 'XHO22'),
(18, 20, 37, 'Waprofeta uEliyezere, unyana kaDodahu waseMaresha, kuYehoshafati, esithi, Ngokunamathela kwakho kuAhaziya, uyaphule uYehova imisebenzi yakho. Zaphuka ke iinqanawa ezo, azaba nako ukuya eTarshishe.', 'XHO22'),
(18, 21, 1, 'uYehoshafati walala kuyise, wangcwatyelwa kuyise, emzini kaDavide; unyana wakhe uYehoram waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(18, 21, 2, 'Wayenabazalwana bakhe, oonyana bakaYoshafati, uAzariya, noYehiyeli, noZekariya, noAzariya, noMikayeli, noShefatiya; bonke bephela babengoonyana bakaYehoshafati ukumkani wakwaSirayeli.', 'XHO22'),
(18, 21, 3, 'Uyise wabanika izipho ezininzi zesilivere nezegolide, nezinto ezinqwenelekayo, kwanemizi enqatyisiweyo kwaYuda; ke ubukumkani wabunika uYoram; ngokuba ebengowamazibulo.', 'XHO22'),
(18, 21, 4, 'Wesuka uYehoram ebukumkanini bukayise, wazomeleza, wababulala bonke abazalwana bakhe ngekrele, nezinye iinkosana zakwaSirayeli.', 'XHO22'),
(18, 21, 5, 'uYoram ubeminyaka imashumi mathathu anamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka esibhozo engukumkani eYerusalem.', 'XHO22'),
(18, 21, 6, 'Waqhubeka ehamba ngendlela yookumkani bakwaSirayeli kanye njengoko yahambayo indlu ka-Ahabhi, ngenxa yokuba wayengumfazi wentombi ka-Ahabhi; yaye wenza okubi emehlweni kaYehova.', 'XHO22'),
(18, 21, 7, 'Wala uYehova ukuyonakalisa indlu kaDavide, ngenxa yomnqophiso awawenzela uDavide, njengoko wathethayo, ukuba uya kumkhanyisela, yena noonyana bakhe, kude kuse ephakadeni.', 'XHO22'),
(18, 21, 8, 'Ngemihla yakhe amaEdom amvukela uYuda, azenzela ukumkani.', 'XHO22'),
(18, 21, 9, 'Wemka uYoram, nabathetheli bakhe, neenqwelo zakhe zonke zokulwa; wavuka ebusuku, wawabulala amaEdom awayemrhawule ngeenxa zonke, nabaqhubi beenqwelo zokulwa.', 'XHO22'),
(18, 21, 10, 'Akreqa ke amaEdom kumaYuda, unanamhla. Kwangelo xesha neLibhena yagwilika kuye; ngokuba umnikele umva uYehova, uThixo wooyise.', 'XHO22'),
(18, 21, 11, 'Ngaphezu koko, wenza iindawo eziphakamileyo ezintabeni zakwaYuda waza wabangela ukuba abemi baseYerusalem bakrexeze waza wamnyanzela uYuda ukuba enjenjalo.', 'XHO22'),
(18, 21, 12, 'Kwafika kuye incwadi yomprofeti uEliya, isithi, Utsho uYehova, uThixo kaDavide uyihlo, ukuthi, Ngenxa enokuba ungahambanga ekhondweni likayihlo uYehoshafati, nangokuhamba ekhondweni lika-Asa ukumkani wakwaYuda hamba,', 'XHO22'),
(18, 21, 13, 'Kodwa wena wahamba ngendlela yookumkani bakwaSirayeli waza wabangela ukuba uYuda nabemi baseYerusalem bahenyuze, njengamahenyukazi endlu ka-Ahabhi, yaye wababulala nabantakwenu bendlu kayihlo, ababelunge ngakumbi kunaye.', 'XHO22'),
(18, 21, 14, 'Yabona, uYehova uya kubavelela ngesijeziso esikhulu abantu bakho, nabantwana bakho, nabafazi bakho, nobutyebi bakho bonke;', 'XHO22'),
(18, 21, 15, 'Kwaye uya kubanjwa isifo esikhulu ngokugula emathunjini akho, ade amathumbu akho aphume imihla ngemihla ngenxa yokugula.', 'XHO22'),
(18, 21, 16, 'Ngaphezu koko, uYehova wawuxhokonxa umoya wamaFilisti nama-Arabhu awayesondele kumaKushi ukuba ahlasele uYoram.', 'XHO22'),
(18, 21, 17, 'Enyuka ke aya kwaYuda, angena kulo, athabatha yonke impahla eyafumaneka endlwini yokumkani, noonyana bakhe, nabafazi bakhe; engashiyanga nyana wakhe, ngaphandle kukaYowakazi, oyena mncinane koonyana bakhe.', 'XHO22'),
(18, 21, 18, 'Emva kwako konke oku, uYehova wambetha isisu sakhe ngokufa okungenakuphiliswa.', 'XHO22'),
(18, 21, 19, 'Kwathi ke ekuhambeni kwexesha, ekupheleni kweminyaka emibini, amathumbu akhe aphuma ngaphandle ngenxa yesifo sakhe, apho wafa ngenxa yezifo ezibuhlungu. Yaye abantu bakhe abazange babase umlilo njengowookhokho bakhe.', 'XHO22'),
(18, 21, 20, 'Ubeminyaka imashumi mathathu anamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka esibhozo engukumkani eYerusalem, engasweli nto. Bamngcwabela emzini kaDavide, kodwa akangcwatyelwa emangcwabeni ookumkani.', 'XHO22'),
(18, 22, 1, 'Abemi baseYerusalem bamenza ukumkani uAhaziya, unyana wakhe omncinane, esikhundleni sakhe; Ke uAhaziya, unyana kaYehoram, ukumkani wakwaYuda, waba ngukumkani.', 'XHO22'),
(18, 22, 2, 'uAhaziya ubeminyaka imashumi mane anamibini ezelwe, ukuba ngukumkani kwakhe; waba namnyaka mnye engukumkani eYerusalem. Igama lonina belinguAtaliya, intombi kaOmri.', 'XHO22'),
(18, 22, 3, 'Naye wahamba ngendlela yendlu ka-Ahabhi, ngokuba unina ubengumcebisi wakhe, ukuba enze okubi.', 'XHO22'),
(18, 22, 4, 'Wenza okubi emehlweni kaYehova, njengabendlu ka-Ahabhi; ngokuba babengabacebisi bakhe bona emva kokufa kukayise, kwada kwawa.', 'XHO22'),
(18, 22, 5, 'Naye walithobela icebo labo, wahamba noYoram unyana ka-Ahabhi ukumkani wakwaSirayeli, baya kulwa noHazayeli ukumkani wakwa-Aram eRamoti yaseGiliyadi, yaye amaSiriya amoyisa uYoram.', 'XHO22'),
(18, 22, 6, 'Wabuyela eYizereli, ukuba anyangwe amanxeba awawafumana eRama, ekulweni kwakhe noHazayeli ukumkani wakwa-Aram. Wehla uAzariya unyana kaYoram, ukumkani wakwaYuda, waya kubona uYoram unyana ka-Ahabhi eYizereli, ngokuba ebesifa.', 'XHO22'),
(18, 22, 7, 'uThixo wamtshabalalisa uAhaziya ngokuza kwakhe kuYoram; ngenxa yokuba ekufikeni kwakhe waphuma noYehoram ukuya kuhlangabeza uYehu unyana kaNimshi, lowo uYehova wamthambisayo ukuba atshabalalise indlu ka-Ahabhi.', 'XHO22'),
(18, 22, 8, 'Xa uYehu wayengumgwebi wendlu ka-Ahabhi waza wafumana iinkosana zakwaYuda noonyana babantakwabo-Ahaziya ababekhonza uAhaziya, wababulala.', 'XHO22'),
(18, 22, 9, 'Wafuna uAhaziya, bambamba (ezimele kwaSamari), bamzisa kuYehu; bambulala, bamngcwaba; ngokuba besithi, Ngunyana kaYehoshafati, obemquqela uYehova ngentliziyo yakhe yonke. Ngoko abo bendlu ka-Ahaziya babengenawo amandla okuthomalalisa urhulumente.', 'XHO22'),
(18, 22, 10, 'Uthe uAtaliya unina ka-Ahaziya akubona ukuba unyana wakhe ufile, wesuka wasuka wayitshabalalisa yonke intsapho yasebukhosini yendlu yakwaYuda.', 'XHO22'),
(18, 22, 11, 'Ke uYobhati, intombi yokumkani, wamthabatha uYowashe, unyana ka-Ahaziya, wamba phakathi koonyana bokumkani ababulawayo, wammisa yena nomfana wakhe egumbini lokulala. uYehosati, intombi kaKumkani uYehoram, umfazi wombingeleli uYehoyada (kuba wayengudade boAhaziya) wamfihla kuAtaliya ukuze angambulali.', 'XHO22'),
(18, 22, 12, 'Wangcwatyelwa kubo endlwini kaThixo iminyaka emithandathu; ke uAtaliya walilawula ilizwe.', 'XHO22'),
(18, 23, 1, 'Ngomnyaka wesixhenxe, uYehoyada wazomeleza, wathabatha abathetheli-makhulu: ooAzariya unyana kaYeroham, noIshmayeli unyana kaYohanan, noAzariya unyana kaObhedi, noMahaseya unyana ka-Adaya, uElishafati, unyana kaSigri wenze umnqophiso naye.', 'XHO22'),
(18, 23, 2, 'Bawajikeleza ke amaYuda, babahlanganisa abaLevi emizini yonke yakwaYuda, neentloko zezindlu zooyise zooyise zakwaSirayeli, beza eYerusalem.', 'XHO22'),
(18, 23, 3, 'Lonke ibandla lenza umnqophiso nokumkani endlwini yoTHIXO oyinyaniso. Wathi kubo, Yabonani, unyana wokumkani uya kuba ngukumkani, njengoko wakuthethayo uYehova ngoonyana bakaDavide.', 'XHO22'),
(18, 23, 4, 'Nantsi into oya kuyenza; Isahlulo sesithathu kuni bangenayo ngesabatha, kubabingeleli nabaLevi, soba ngabagcini bamasango;', 'XHO22'),
(18, 23, 5, 'nesahlulo sesithathu soba sendlwini yokumkani; esinye isahlulo sesithathu sibe sesangweni lesiseko, lize ibandla lonke libe sezintendelezweni zendlu kaYehova.', 'XHO22'),
(18, 23, 6, 'Ize kungangeni bani endlwini kaYehova, ngaphandle kwababingeleli nabakhonza njengabaLevi; bona bongena, ngokuba bangcwele; kodwa bonke abantu baya kuhlala belindile kuYehova.', 'XHO22'),
(18, 23, 7, 'AbaLevi bamrhawula ukumkani, elowo ephethe iintonga zakhe ngesandla; bonke abangena endlwini bobulawa; woba nokumkani ekungeneni kwakhe, nokuba uthe waphuma.', 'XHO22'),
(18, 23, 8, 'Benza ke abaLevi namaYuda onke njengako konke abewise umthetho ngako uYehoyada umbingeleli; ngokuba uYehoyada umbingeleli akamgxothanga izifundo.', 'XHO22'),
(18, 23, 9, 'Ngaphezu koko, umbingeleli uYehoyada wanikela iinkosana zemikhonto namakhaka nekhulu lamakhaka kaKumkani uDavide, awayesendlwini yoTHIXO oyinyaniso.', 'XHO22'),
(18, 23, 10, 'Wabambathisa bonke abantu, elowo ephethe iintonga zakhe esandleni sakhe, bethabathela kwelokunene lendlu besa kwelasekhohlo lendlu, malunga nesibingelelo nendlu, phambi kokumkani.', 'XHO22'),
(18, 23, 11, 'Bandula ke bamkhupha unyana kakumkani baza bamthwesa isithsaba baza bamnika ubungqina bamenza ukumkani. uYehoyada noonyana bakhe bamthambisa, bathi, uYehova makamsindise ukumkani.', 'XHO22'),
(18, 23, 12, 'Uthe uAtaliya akusiva isandi sokubaleka kwabantu, bedumisa ukumkani, weza ebantwini endlwini kaYehova.', 'XHO22'),
(18, 23, 13, 'Wakhangela, wambona ukumkani emi ngasetyeni lakhe lesikhumbuzo emnyango, nabathetheli nabavutheli-maxilongo benokumkani yaye njengoko babefundiswa ukucula iingoma zokudumisa. Waza uAtaliya wazikrazula iimpahla zakhe waza wathi: “Liqhinga, liqhinga eli!', 'XHO22'),
(18, 23, 14, 'Yaye umbingeleli uYehoyada wabakhupha abathetheli abalikhulu ababephethe umkhosi waza wathi kubo: “Mkhupheni kweli lizwe! yaye nabani na omlandelayo makabulawe ekreleni ngokuba ethe umbingeleli, Musani ukumbulala endlwini kaYehova.', 'XHO22'),
(18, 23, 15, 'Bambamba ke; Ekufikeni kwakhe ekungeneni kwesango lamahashe endlwini yokumkani, bambulalela khona.', 'XHO22'),
(18, 23, 16, 'uYehoyada wenza umnqophiso phakathi kwakhe nohlanga luphela nokumkani, wokuba babe ngabantu bakaYehova.', 'XHO22'),
(18, 23, 17, 'Lwaya lonke uhlanga endlwini kaBhahali, lwayidiliza, nezibingelelo zakhe, nemifanekiso yakhe, lwambulala uMatan, umbingeleli kaBhahali, phambi kwezibingelelo.', 'XHO22'),
(18, 23, 18, 'uYehoyada wawumisa umsebenzi wendlu kaYehova ngesandla sababingeleli, abaLevi, awabahluthayo uDavide endlwini kaYehova, ukuba basondeze amadini anyukayo kaYehova, ngoko kubhaliweyo emyalelweni kaMoses; nangokuvuma iingoma, njengoko uDavide wawisayo umthetho.', 'XHO22'),
(18, 23, 19, 'Wabeka abagcini-masango emasangweni endlu kaYehova, ukuze kungangeni nabani na ongahlambulukanga nganto.', 'XHO22'),
(18, 23, 20, 'Wathabatha amakhulu abathetheli nezidwangube neerhuluneli zohlanga nabo bonke abantu belizwe waza wehla nokumkani wamsa endlwini kaYehova baza bangena ngeSango Eliphakamileyo ukuze bangene endlwini kakumkani baze babeke ukumkani ukumkani etroneni yobukumkani.', 'XHO22'),
(18, 23, 21, 'Bavuya bonke abantu belizwe, umzi wazola, bambulala uAtaliya ngekrele.', 'XHO22'),
(18, 24, 1, 'uYowashe ubeminyaka isixhenxe ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mane engukumkani eYerusalem. Igama likanina lalinguSibja waseBher-shebha.', 'XHO22'),
(18, 24, 2, 'uYowashe waqhubeka esenza okulungileyo emehlweni kaYehova yonke imihla kaYehoyada umbingeleli.', 'XHO22'),
(18, 24, 3, 'uYehoyada wamzekela abafazi ababini; wazala oonyana neentombi.', 'XHO22'),
(18, 24, 4, 'Emva koko uYowashe wemka ukuze ayakhe kwakhona indlu kaYehova.', 'XHO22'),
(18, 24, 5, 'Wababiza ababingeleli nabaLevi, wathi kubo: “Yiyani ezixekweni zakwaYuda, niqokelele imali kumaSirayeli onke, ukuze kulungiswe indlu yoThixo wenu unyaka nonyaka, niwukhawulezise lo mbandela. Kodwa abaLevi babengangxamanga.', 'XHO22'),
(18, 24, 6, 'Ukumkani wambiza uYehoyada umthetheli-waka, wathi kuye, Yini na ukuba ungatshongo kubaLevi, ukuba bazise iminikelo ephuma kwaYuda naseYerusalem, ngokomthetho kaMoses, umkhonzi kaYehova, nowebandla lakwaYuda? Sirayeli, ngaba uthini na kwintente yesingqino?', 'XHO22'),
(18, 24, 7, 'Kuba oonyana baka-Ataliya, umfazi ongendawo, babeyidiliza indlu yoTHIXO oyinyaniso; bazinika ooBhahali zonke izipho ezingcwele zendlu kaYehova.', 'XHO22'),
(18, 24, 8, 'Ngokomlomo wokumkani, benza ityeya baza bayibeka ngaphandle kwesango lendlu kaYehova.', 'XHO22'),
(18, 24, 9, 'Bamemeza kulo lonke elakwaYuda naseYerusalem ukuba kuziswe kuYehova iminikelo uMoses, umkhonzi kaTHIXO oyinyaniso, phezu koonyana bakaSirayeli.', 'XHO22'),
(18, 24, 10, 'Zavuyisana zonke iinkosana nabantu bonke, bazisa, baphosa engxoweni, bada bagqiba.', 'XHO22'),
(18, 24, 11, 'Xa ingxowa-mali yayiziswe kukumkani ngesandla sabaLevi, yaye bakubona ukuba kukho imali eninzi, unobhala kakumkani nentloko yombingeleli omkhulu beza, bagalela engxoweni. Wayithatha wabuya nayo endlini yakhe. Ngoko benza oko yonke imihla kwaye baqokelela imali eninzi.', 'XHO22'),
(18, 24, 12, 'Ukumkani noYehoyada bawanikela kubasebenzi benkonzo yendlu kaYehova; beza neengcibi zamatye, neengcibi zemithi, ukuba ziyihlaziye indlu kaYehova, nesinyithi, nobhedu, zendlu kaYehova.', 'XHO22'),
(18, 24, 13, 'Basebenza ke abenzi bomsebenzi, wagqitywa ke umsebenzi ngabo, bayimisa indlu kaThixo, bayiqinisa.', 'XHO22'),
(18, 24, 14, 'Bakugqiba ke, bayizisa imali eseleyo phambi kokumkani noYehoyada, ekwenziwe ngayo iimpahla zendlu kaYehova, iimpahla zokulungiselela, nezokubingelela kunye, neenkamba, neempahla zegolide nezesilivere. Yaye aqhubeka ezisa iminikelo etshiswayo endlwini kaYehova yonke imihla kaYehoyada.', 'XHO22'),
(18, 24, 15, 'Ke uYehoyada wayeseleyingwevu, ehambisekile ebudaleni, wafa; ubeminyaka ilikhulu elinamanci mathathu ezelwe, ukufa kwakhe.', 'XHO22'),
(18, 24, 16, 'Bamngcwabela kwisiXeko sikaDavide phakathi kookumkani, ngenxa yokuba wenza okulungileyo kuSirayeli, kuThixo nakwindlu yakhe.', 'XHO22'),
(18, 24, 17, 'Emva kokufa kukaYehoyada, beza abathetheli bakwaYuda, baqubuda kukumkani. Wabaphulaphula ke ukumkani.', 'XHO22'),
(18, 24, 18, 'Bayinikela ikhosi indlu kaYehova, uThixo wooyise, bakhonza imithi engcwele noothixo. Afikelwa buburhalarhume amaYuda neYerusalem ngenxa yezikreqo zawo.', 'XHO22'),
(18, 24, 19, 'Wathuma abaprofeti kubo bokubabuyisela kuYehova; kwaye banikela ubungqina nxamnye nabo, kodwa abazange babaphulaphule.', 'XHO22'),
(18, 24, 20, 'uMoya kaThixo wamthi gqubuthu uZekariya, unyana wombingeleli uYehoyada, wema phambi kwabantu, wathi kubo, Kuthiwe ngenxa kaThixo, Yini na ukuba niyiphule imithetho kaYehova, nize ningabi nampumelelo? ngenxa yokuba nimshiyile uYehova, naye unilahlile.', 'XHO22'),
(18, 24, 21, 'Bamvukela baza bamgibisela ngamatye ngokomyalelo kakumkani entendelezweni yendlu kaYehova.', 'XHO22'),
(18, 24, 22, 'Ukumkani uYowashe akakukhumbulanga okulungileyo awakwenzayo uYehoyada kuye, wasuka wambulala unyana wakhe. Ekufeni kwakhe wathi, uYehova makakhangele, abuze kuye.', 'XHO22'),
(18, 24, 23, 'Ekupheleni komnyaka impi yama-Aram yamxabela, yeza kwaYuda naseYerusalem, yabatshabalalisa bonke abathetheli babantu ebantwini, yathinjwa yonke into yabo yalithumela kukumkani waseDamasko.', 'XHO22'),
(18, 24, 24, 'Ngokuba impi yama-Aram yeza inamadoda ambalwa; uYehova wanikela esandleni sawo impi eninzi kunene; ngokuba babemnike umva uYehova, uThixo wooyise. Bamgweba ke uYowashe.', 'XHO22'),
(18, 24, 25, 'Xa bamshiyayo (ngenxa yokuba bamshiya egula kakhulu), abakhonzi bakhe bamenzela iyelenqe ngenxa yegazi loonyana bombingeleli uYehoyada, bambulalela esingqengqelweni sakhe, wafa lowo ufileyo; bamngcwaba emzini kaDavide, kodwa abamngcwabelanga emangcwabeni ookumkani.', 'XHO22'),
(18, 24, 26, 'Babona ke abo babemenzela iyelenqe; noSabhadi, unyana kaShimehati, umAmon, noYozabhadi, unyana kaZimriti, umMowabhi.', 'XHO22'),
(18, 24, 27, 'Ke bona oonyana bakhe neempahla ezinkulu ezazithwalwe kuye, nokuhlaziywa kwendlu yoTHIXO oyinyaniso, nantso ibhaliwe ingxelo yencwadi yookumkani. uAmatsiya unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(18, 25, 1, 'uAmatsiya ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mabini anesibhozo yedwa eYerusalem. Igama lonina belinguYodan waseYerusalem.', 'XHO22'),
(18, 25, 2, 'Wenza okuthe tye emehlweni kaYehova, kodwa kungekhona ngentliziyo epheleleyo.', 'XHO22'),
(18, 25, 3, 'Bakuba buqinile ubukumkani kuye, wababulala abakhonzi bakhe abambulalayo ukumkani uyise.', 'XHO22'),
(18, 25, 4, 'Akababulalanga oonyana babo; wenza njengoko kubhaliweyo emyalelweni, encwadini kaMoses, apho uYehova wawisa umthetho, esithi, Aba yise mabangafe ngenxa yabantwana babo, mabangafe oonyana ngenxa yoonyana babo kodwa elowo uya kufa ngesakhe isono.', 'XHO22'),
(18, 25, 5, 'Ngaphezu koko, uAmatsiya wawahlanganisa ndawonye amaYuda waza wawamisela ukuba abe ngabaphathi bamawaka neentloko zamakhulu ngokwezindlu zooyise kulo lonke elakwaYuda nelakwaBhenjamin; amadoda, ekwazi ukuya emfazweni, enokuphatha umkhonto nekhaka.', 'XHO22'),
(18, 25, 6, 'Waqesha kumaSirayeli ikhulu lamawaka amagorha ngekhulu leetalente zesilivere.', 'XHO22'),
(18, 25, 7, 'Wafika umfo wakwaThixo kuye, wathi, Camagu, kumkani, musa ukuyivumela imikhosi yakwaSirayeli ihambe nawe; ngenxa yokuba uYehova akakho kuSirayeli, oko kukuthi kubo bonke oonyana bakaEfrayim.', 'XHO22'),
(18, 25, 8, 'Ke ukuba ùthanda ukuya, yiya yomelela edabini; uThixo woniwisa phambi kotshaba; ngokuba uThixo unamandla okunceda nawokubhukuqa.', 'XHO22'),
(18, 25, 9, 'Wandula ke uAmatsiya wathi kumfo wakwaThixo oyinyaniso: “Kodwa siya kuthini na ngekhulu leetalente endalinika umkhosi wakwaSirayeli? Waphendula umfo wakwaThixo, wathi, uYehova unako ukukunika okungaphezu koko.', 'XHO22'),
(18, 25, 10, 'uAmatsiya ke wayahlula impi leyo yayize kuye, ivela kwaEfrayim, ukuba ibuyele endaweni yayo; Bavutha ke umsindo wabo kwaYuda, baza babuyela ekhaya bevutha ngumsindo.', 'XHO22'),
(18, 25, 11, 'Yaye uAmatsiya wamomeleza waza wabakhokela abantu bakhe waphuma waya kwiNtlambo yeTyuwa waza wabulala ishumi lamawaka koonyana bakaSehire.', 'XHO22'),
(18, 25, 12, 'Amanye alishumi lamawaka awayesaphila, oonyana bakaYuda bathatyathelwa ekuthinjweni baza basiwa emqhokrweni wengxondorha baza babaphosa phantsi emqhokrweni wengxondorha, kangangokuba baqhekeka bonke.', 'XHO22'),
(18, 25, 13, 'Ke amagorha awawabuyisayo uAmatsiya ukuba angayi naye ekulweni, ayingenela imizi yakwaYuda, ethabathela kwaSamari, esa eBhete-horon, abulala kuyo amawaka amathathu, ayithimba iqashiso.', 'XHO22'),
(18, 25, 14, 'Xa uAmatsiya wabuyayo ekuxabeleni amaEdom, wazisa oothixo boonyana bakaSehire waza wabamisa baba ngoothixo bakhe waza waqubuda phambi kwabo waza wawunyusa umsi.', 'XHO22'),
(18, 25, 15, 'Wavutha ke umsindo kaYehova uAmatsiya, wathumela umprofeti kuye, wathi kuye, Ubuphi na oothixo babantu abangabahlangulanga abantu babo esandleni sakho?', 'XHO22'),
(18, 25, 16, 'Esathetha naye, ukumkani wathi kuye, Ùphuma kukumkani na ngecebo? ibhere; yini na ukuba ubethwe? Wema lo mprofeti wathi: “Ndiyazi ukuba uThixo uzimisele ukukutshabalalisa, ngenxa yokuba ukwenzile oku, akwaliphulaphula icebiso lam.', 'XHO22'),
(18, 25, 17, 'uAmatsiya ukumkani wakwaYuda wacebisa, wathumela kuYowashe, unyana kaYehowahazi, unyana kaYehu, ukumkani wakwaSirayeli, wathi, Yiza sibonane sikhangelane ubuso.', 'XHO22'),
(18, 25, 18, 'uYowashe ukumkani wakwaSirayeli wathumela kuAmatsiya ukumkani wakwaYuda, wathi, uTeshelo waseLebhanon wathumela eKedare waseLebhanon, wathi, Unyana wam mnike intombi yakho, abe ngumkakho; Yadlula irhamncwa laseLebhanon, wayinyathela emeveni.', 'XHO22'),
(18, 25, 19, 'Uthi, Yabona, uwoyisile amaEdom; nentliziyo yakho iphakamele eluzukweni; hlala ekhaya ngoku; Yini na ukuba ude ungene ebubini bakho, wena namaYuda kunye nawe?', 'XHO22'),
(18, 25, 20, 'Akavuma ke uAmasiya ukuva; ngokuba kwakuphuma kuThixo ukubanikela esandleni seentshaba zabo, kuba babefuna oothixo bakwaEtom.', 'XHO22'),
(18, 25, 21, 'Wenyuka ke uYowashe ukumkani wakwaSirayeli; babonana ubuso ngobuso, yena noAmatsiya ukumkani wakwaYuda, eBhete-shemeshe yakwaYuda.', 'XHO22'),
(18, 25, 22, 'AmaYuda aqalisa ukuba nomsindo nxamnye namaSirayeli, yaye ngamnye wasaba waya ententeni yakhe.', 'XHO22'),
(18, 25, 23, 'uYowashe ukumkani wakwaSirayeli wamthabatha uAmatsiya ukumkani wakwaYuda, unyana kaYowashe, unyana kaYehowahazi, eBhete-shemeshe, wamzisa eYerusalem, waluqhekeza udonga lweYerusalem, esangweni lakwaEfrayim. Isango lembombo liziikubhite ezimakhulu mane.', 'XHO22'),
(18, 25, 24, 'Wayithabatha yonke igolide nesilivere nezixhobo zonke ezafunyanwayo endlwini yoTHIXO oyinyaniso kuObhedi-edom nobuncwane bendlu kakumkani namabanjwa, waza wabuyela kwaSamari.', 'XHO22'),
(18, 25, 25, 'uAmatsiya, unyana kaYowashe, ukumkani wakwaYuda, wahlala iminyaka elishumi elinesihlanu emva kokufa kukaYowashe, unyana kaYehowahazi, ukumkani wakwaSirayeli.', 'XHO22'),
(18, 25, 26, 'Ezinye izinto zika-Amatsiya, ezokuqala nezokugqibela, azibhalwanga na encwadini yookumkani bakwaYuda nabakwaSirayeli?', 'XHO22'),
(18, 25, 27, 'Ke kaloku, emveni kwexesha awatyeka ngalo uAmatsiya kuYehova, bamceba eYerusalem; wabalekela eLagishe, bathumela eLagishe, bambulalela khona.', 'XHO22'),
(18, 25, 28, 'Bamthwala ngamahashe, bamngcwabela nooyise emzini wakwaYuda.', 'XHO22'),
(18, 26, 1, 'Bonke abantu bakwaYuda bamthabatha u-Uziya, akubon’ ukuba uminyaka ilishumi linamithandathu ezelwe, bamenza ukumkani esikhundleni sika-Amatsiya uyise.', 'XHO22'),
(18, 26, 2, 'Nguye owakha iEloti, wayibuyisela kwaYuda emva kokulala kokumkani kooyise.', 'XHO22'),
(18, 26, 3, 'U-Uziya ubeminyaka ilishumi elinamithandathu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mahlanu anamibini engukumkani eYerusalem. Igama lonina belinguYekoliya waseYerusalem.', 'XHO22'),
(18, 26, 4, 'Wenza okuthe tye emehlweni kaYehova, njengoko wenzayo uyise uAmatsiya.', 'XHO22'),
(18, 26, 5, 'Wayemlindile uThixo ngemihla kaZekariya, obenengqondo emehlweni kaThixo; yaye ekumfuneni kwakhe uYehova, uThixo wanempumelelo.', 'XHO22'),
(18, 26, 6, 'Waphuma waya kulwa namaFilisti waza waludiliza udonga lwaseGati udonga lwaseYabhen nodonga lwaseAshdode waza wakha izixeko macala onke eAshdode naphakathi kwamaFilisti.', 'XHO22'),
(18, 26, 7, 'uThixo wamncedisa kumaFilisti, nakuma-Arabhi, abehleli eGurbhahali, nakumaMehun.', 'XHO22'),
(18, 26, 8, 'Yaye oonyana baka-Amoni banikela izipho ku-Uziya, yaye igama lakhe lasasazeka ukuya kufikelela naphakathi kweYiputa; ngokuba wazenza nzima kakhulu.', 'XHO22'),
(18, 26, 9, 'U-Uziya wakha iinqaba ezinde eYerusalem kwiSango leKona nakwiSango leNtili nakwimbombo yodonga, wayiqinisa.', 'XHO22'),
(18, 26, 10, 'Kananjalo wakha iinqaba ezinde entlango, wemba amaqula amaninzi; nabalimi nabasebenzi bezidiliya ezintabeni naseKarmele, ngokuba ebethanda ukulima.', 'XHO22'),
(18, 26, 11, 'Ngaphezu koko, u-Uziya wayenomkhosi wamajoni awayesiya emfazweni ngokwamaqela eenkundla, ngokwenani lengxelo yawo uYehiyeli unobhala nenkokeli uMahaseya phantsi kolwalathiso lukaHananiya igosa likakumkani.', 'XHO22'),
(18, 26, 12, 'Inani leentloko zezindlu zamagorha laba ngamawaka amabini anamakhulu amathandathu.', 'XHO22'),
(18, 26, 13, 'Phantsi kwawo yayingamakhulu amathathu anesixhenxe amawaka, anamakhulu mahlanu amawaka, amadoda anamandla, ukuze ancede ukumkani elutshabeni.', 'XHO22'),
(18, 26, 14, 'U-Uziya wawulungisela umkhosi wonke iingweletshetshe, nezikhali, nezigcina-ntloko, namahashe, nezaphetha, nezisawuli.', 'XHO22'),
(18, 26, 15, 'Wenza omatshini eYerusalem, aqingqwe ziingqondi, zaba phezu kweenqaba ezinde nasezindongeni, kukho iintolo namatye amakhulu. Laduma igama lakhe; kuba wancedwa ngendlela emangalisayo wada womelela.', 'XHO22'),
(18, 26, 16, 'Uthe ke akomelela, yaqhayisela intliziyo yakhe ngobubi; ngenxa yokuba wona kuYehova uThixo wakhe waza wangena etempileni kaYehova ukuze atshise isiqhumiso esibingelelweni sesiqhumiso.', 'XHO22'),
(18, 26, 17, 'Kwangena emva kwakhe umbingeleli uAsariya, enamashumi asibhozo ababingeleli bakaYehova, amadoda anobukroti.', 'XHO22'),
(18, 26, 18, 'Bamchasa ukumkani u-Uziya, bathi kuye, Uziya, akakho emandleni akho ukwenzela uYehova isiqhumiso; kokwababingeleli, oonyana baka-Aron, abanyuliweyo ukuba baqhumisele; ngokuba wenze ubumenemene; yaye akuyi kuba yindumiso kaYehova uThixo.', 'XHO22'),
(18, 26, 19, 'Wacaphuka u-Uziya, yaye esandleni sakhe wayephethe imbiza yesiqhumiso ukuze akhuphe isiqhumiso; ngoxa wayenomsindo kubabingeleli, kwavela iqhenqa ebunzi phambi kwababingeleli endlwini kaYehova, ngasesibingelelweni sesiqhumiso.', 'XHO22'),
(18, 26, 20, 'uAsriya umbingeleli omkhulu nababingeleli bonke bekhangele kuye, babona ukuba uneqhenqa ebunzi, bamndulula ke; naye wakhawuleza wemka, ngokuba uYehova ubemkhaliphile.', 'XHO22'),
(18, 26, 21, 'Ukumkani uOsiya ubeneqhenqa, kwada kwayimini yokufa kwakhe, wahlala ezindlwini ngezindlu, ngenxa yokuba ebeneqhenqa; unyana wakhe uYotham wayephethe indlu kakumkani yaye egweba abantu belizwe.', 'XHO22'),
(18, 26, 22, 'Ezinye izinto zika-Osiya, ezokuqala nezokugqibela, zazibhalwe nguIsaya unyana ka-Amos, umprofeti.', 'XHO22'),
(18, 26, 23, 'U-Uziya walala kooyise; bamngcwabela yena nookhokho bakhe engcwabeni lookumkani ngokuba bathi, Uneqhenqa; uYotam, unyana wakhe, waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(18, 27, 1, 'uYotam ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamithandathu engukumkani eYerusalem. Igama lonina belinguYerusha, intombi kaTsadoki.', 'XHO22'),
(18, 27, 2, 'Wenza okuthe tye emehlweni kaYehova, njengoko wenza ngako u-Uziya uyise; ke akangena etempileni kaYehova. Kwaye abantu basenokonakala.', 'XHO22'),
(18, 27, 3, 'Wakha isango eliphakamileyo lendlu kaYehova, eludongeni lweOfeli waba zininzi.', 'XHO22'),
(18, 27, 4, 'Wakha nemizi kweleentaba lakwaYuda, nasemahlathini wakha iinqaba neemboniselo.', 'XHO22'),
(18, 27, 5, 'Wabuya walwa nokumkani woonyana baka-Amon, waboyisa. Oonyana baka-Amon bamnika ngaloo nyaka ikhulu leetalente zesilivere, neshumi lamawaka ezithungu zengqolowa, neshumi lamawaka erhasi. Oonyana baka-Amon bamnika kwaloo nto ngomnyaka wesibini nowesithathu.', 'XHO22'),
(18, 27, 6, 'Wazomeleza uYotam, ngokuba wayeziqinise iindlela zakhe phambi koYehova uThixo wakhe.', 'XHO22'),
(18, 27, 7, 'Ezinye izinto zikaYotam, neemfazwe zakhe zonke, neendlela zakhe, zibhaliwe encwadini yookumkani bakwaSirayeli nabakwaYuda.', 'XHO22'),
(18, 27, 8, 'Ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamithandathu engukumkani eYerusalem.', 'XHO22'),
(18, 27, 9, 'uYotam walala kooyise, wangcwatyelwa emzini kaDavide; unyana wakhe uAhazi walawula esikhundleni sakhe.', 'XHO22'),
(18, 28, 1, 'uAhazi ubeminyaka imashumi mabini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamithandathu engukumkani eYerusalem kodwa akazange enze okulungileyo emehlweni kaYehova njengoDavide uyise.', 'XHO22'),
(18, 28, 2, 'Wahamba ngendlela yookumkani bakwaSirayeli waza waphinda wenzela ooBhahali imifanekiso etyhidiweyo.', 'XHO22'),
(18, 28, 3, 'Ngaphezu koko, wanyusa umsi kwintlambo yonyana kaHinom waza wabatshisa oonyana bakhe ngomlilo, ngokwezinto ezicekisekayo zeentlanga ezo uYehova wazigxothayo phambi koonyana bakaSirayeli.', 'XHO22'),
(18, 28, 4, 'Yaye wenza umbingelelo waza wawunyusa umsi kwiindawo eziphakamileyo nasezindulini naphantsi kwayo yonke imithi eluhlaza.', 'XHO22'),
(18, 28, 5, 'uYehova, uThixo wakhe, wamnikela esandleni sokumkani wakwa-Aram; bameyisa, bathimba into eninzi yabathinjwa, babasa eDamasko. Waphinda wanikelwa esandleni sokumkani wakwaSirayeli, wamxabela ngogonyamelo olukhulu.', 'XHO22'),
(18, 28, 6, 'uPeka unyana kaRemaliya wabulala kumaYuda ikhulu elinamanci amabini lamawaka ngamini-nye, bonke bengamadoda anobukroti; ngokuba bamcekisile uYehova, uThixo wooyise.', 'XHO22'),
(18, 28, 7, 'uSigri, igorha lakwaEfrayim, wabulala uMahaseya unyana wokumkani, noAsrikam umphathi wendlu yakhe, noElikana ongene esikhundleni sikakumkani.', 'XHO22'),
(18, 28, 8, 'Oonyana bakaSirayeli bathimba abafazi abangamakhulu amabini amawaka, oonyana neentombi zabazalwana babo, babaphanga amaxhoba amaninzi, bawazisa amaxhoba lawo kwaSamari.', 'XHO22'),
(18, 28, 9, 'Ke bekukho umprofeti kaYehova, ogama linguOdedi; Wagqithela kwizintlu ezifike kwaSamari, wathi kubo, Yabonani, ngenxa enokuba uYehova, uThixo wooyihlo, ebenoburhalarhume kumaYuda, wawanikela esandleni senu, niwabulala ngomsindo omkhulu ifikelela esibhakabhakeni.', 'XHO22'),
(18, 28, 10, 'Kaloku ke, niza kuhlala phakathi koonyana bakaYuda naseYerusalem, nibe ngamakhoboka namakhobokazana; kodwa akukho namnye phakathi kwenu owonayo kuYehova uThixo wenu?', 'XHO22'),
(18, 28, 11, 'Phulaphulani ke ngoku, nize nibahlangule abathinjwa enibathabathileyo kubazalwana benu, ngenxa yokuba uYehova uniqumbele.', 'XHO22'),
(18, 28, 12, 'Kwesuka ke kwizikhulu zoonyana bakaEfrayim, uAzariya unyana kaYohanan, noBherekiya unyana kaMeshilemoti, noIshkiya unyana kaShalum, noAmasa unyana kaHadlayi, emfazweni;', 'XHO22'),
(18, 28, 13, 'Wathi kubo, Ningabazisi amabanjwa apha; ngokuba, noko sonile kuYehova, uya kuzandisa izono zethu nezikreqo zethu; kuba ukunxaxha kwethu kukhulu yaye kukho umsindo ovuthayo nxamnye noSirayeli.', 'XHO22'),
(18, 28, 14, 'Abaxhobileyo babandulula abathinjwa namaxhoba, baye kumabamba nebandla lonke.', 'XHO22'),
(18, 28, 15, 'Asuka ke amadoda abizwa ngamagama, ababamba ababanjiweyo, ababambathisa bonke ababehamba ze phakathi kwabo, abanxiba, abanxiba iimbadada, abapha ukuba badle, basele, babathambise, baphilise bonke abaswele amandla idizili, yeza nazo baya kubazalwana babo eYeriko, umzi wamasundu; emva koko babuyela kwaSamari.', 'XHO22'),
(18, 28, 16, 'Ngelo xesha uKumkani uAhazi wathumela umyalezo kookumkani baseAsiriya ukuba bamncede.', 'XHO22'),
(18, 28, 17, 'Abuya ke amaEdom, amoyisa uYuda, athimba abathinjwa.', 'XHO22'),
(18, 28, 18, 'AmaFilisti azixabela izixeko ezisezintabeni nezisemzantsi zakwaYuda aza ayithimba iBhete-shemeshe neAyalon neGederoti neSogo namagxamesi ayo neTimna needolophana zayo neGimzo needolophana zayo kwaye bahlala khona.', 'XHO22'),
(18, 28, 19, 'Ngokuba uYehova wawathoba amaYuda ngenxa ka-Ahazi, ukumkani wakwaSirayeli; kuba wamphanga uYuda waza wona kakhulu kuYehova.', 'XHO22'),
(18, 28, 20, 'Wandula ke uTilegati-pilezere ukumkani waseAsiriya weza kuye waza wamkhwankqisa, kodwa akazange amomeleze.', 'XHO22'),
(18, 28, 21, 'Kuba uAhazi wathabatha inxalenye yendlu kaYehova, neyendlu yokumkani, neyobathetheli, wayinika ukumkani waseAsiriya;', 'XHO22'),
(18, 28, 22, 'Ekubandezelekeni kwakhe waqhubeka esona ngakumbi kuYehova;', 'XHO22'),
(18, 28, 23, 'Wabingelela kwizithixo zaseDamasko ezimeyisileyo, wathi, Izithixo zookumkani bama-Aram ziyanceda, ndobingelela ngazo, zindincede mna. Kwaba yintlekele kuye nakumaSirayeli onke.', 'XHO22'),
(18, 28, 24, 'uAhazi waziqokelela zonke izitya zendlu yoTHIXO oyinyaniso waza waziqhekeza iimpahla zendlu yoTHIXO oyinyaniso, wazivala iingcango zendlu kaYehova waza wazenzela izibingelelo kuzo zonke iimbombo zeYerusalem.', 'XHO22'),
(18, 28, 25, 'Kwimizi yonke yakwaYuda wenza iziganga zokutshisa thixo bambi; wamqumbisa uYehova uThixo wooyise.', 'XHO22'),
(18, 28, 26, 'Ezinye izinto zakhe, neendlela zakhe, ezokuqala nezokugqibela, nanzo zibhaliwe encwadini yookumkani bakwaYuda nabakwaSirayeli.', 'XHO22'),
(18, 28, 27, 'Walala uAhazi kooyise, bamngcwabela emzini oyiYerusalem; abamsa emangcwabeni ookumkani bakwaSirayeli; uHezekiya unyana wakhe waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(18, 29, 1, 'uHezekiya ubeminyaka imashumi mabini anamihlanu engukumkani; waba neminyaka emashumi mabini anesibhozo engukumkani eYerusalem. Igama lonina belinguAbhiya, intombi kaZekariya.', 'XHO22'),
(18, 29, 2, 'Wenza okuthe tye emehlweni kaYehova, njengoko wenzayo uDavide uyise.', 'XHO22'),
(18, 29, 3, 'Ngomnyaka wokuqala wobukumkani bakhe, ngenyanga yokuqala, wazivula iingcango zendlu kaYehova, wayilungisa.', 'XHO22'),
(18, 29, 4, 'Wabasondeza ababingeleli nabaLevi, wabahlanganisa entendelezweni yasempumalanga.', 'XHO22'),
(18, 29, 5, 'Wathi kubo, Ndiphulaphuleni, baLevi, zingcwaliseni, yingcwaliseni indlu kaYehova, uThixo wooyihlo, nikususe ubunqambi bengcwele.', 'XHO22'),
(18, 29, 6, 'Kuba oobawo benza ngokungathembeki baza benza okubi emehlweni kaYehova uThixo wethu, baza bamfulathela baza babususa ubuso babo emnqubeni kaYehova baza bajika.', 'XHO22'),
(18, 29, 7, 'Bazitshixa iingcango zevaranda baza bazicima izibane, abazange baqhumisele ngesiqhumiso, yaye abazange banikele iminikelo etshiswayo kuThixo kaSirayeli engcweleni.', 'XHO22'),
(18, 29, 8, 'Ngoko ke wafikelwa yingqumbo kaNdikhoyo phezu koJuda neJerusalem, waza wawanikela ukuba athuthuzeleke, akhwankqiswe, abe sisigculelo, njengoko nikubonayo ngamehlo enu.', 'XHO22'),
(18, 29, 9, 'Yabona, abazalwana bethu bawile likrele, oonyana bethu neentombi zethu nabafazi bethu basekuthinjweni ngenxa yoko.', 'XHO22'),
(18, 29, 10, 'Ngoku ke makhe ndenze umnqophiso noYehova, uThixo kaSirayeli, ukuze abuye kuthi umsindo wakhe.', 'XHO22'),
(18, 29, 11, 'Bonyana bam, musani ukuncama ngoku; ngokuba uYehova uninyule, ukuze nime phambi kwakhe, nimkhonze, nimkhonze, kuphume umsi.', 'XHO22'),
(18, 29, 12, 'Basuka bema abaLevi, ooMahati unyana ka-Amasayi, noYoweli unyana ka-Azariya, koonyana bakaKehati; koonyana bakaMerari nguKishe unyana ka-Abhedi, noAsariya unyana kaYehaleli, nakumaGershon; uYowa, unyana kaSima, noEden, unyana kaYowa;', 'XHO22'),
(18, 29, 13, 'koonyana bakaElishafan; uZimri noYehiyeli; nakoonyana baka-Asafu; uZekariya noMataniya:', 'XHO22'),
(18, 29, 14, 'koonyana bakaHeman; uYehiyeli noShimehi; nakoonyana bakaYedutun; uShemaya noUziyeli.', 'XHO22'),
(18, 29, 15, 'Babahlanganisa ke abazalwana babo, babangcwalisa, beza ngokomyalelo wokumkani ngokwelizwi likaYehova, ukuyihlambulula indlu kaYehova.', 'XHO22'),
(18, 29, 16, 'Ababingeleli bangena endlwini kaYehova ukuze bayihlambulule, baza bazisa konke ukungcola ababekufumana etempileni kaYehova entendelezweni yendlu kaYehova. Bazithabatha ke abaLevi, babasa ngaphandle komlanjana oyiKidron.', 'XHO22'),
(18, 29, 17, 'Bangcwalisa ngomhla wokuqala wenyanga yokuqala, baza ngomhla wesibhozo wenyanga leyo bafika evarandeni kaYehova; yaye bayingcwalisa indlu kaYehova iintsuku ezisibhozo; bagqiba ngomhla weshumi elinesithandathu wenyanga yokuqala.', 'XHO22'),
(18, 29, 18, 'Baya kukumkani uHezekiya, bathi, Siyihlambulule indlu kaYehova yonke, nesibingelelo somnikelo onyukayo, nemisebenzi yayo yonke, netafile yezonka zokubonisa, nemisebenzi yayo yonke.', 'XHO22'),
(18, 29, 19, 'Ngaphezu koko, sizilungisile saza sazingcwalisa zonke izixhobo awazilahlwayo uKumkani uAhazi ebudeni bolawulo lwakhe ngenxa yobugwenxa bakhe, yaye, khangelani, ziphambi kwesibingelelo sikaYehova.', 'XHO22'),
(18, 29, 20, 'Wavuka ukumkani uHezekiya kusasa, wahlanganisa abathetheli besixeko, wenyuka waya endlwini kaYehova.', 'XHO22'),
(18, 29, 21, 'Bazisa iinkunzi zeenkomo zasixhenxe, neenkunzi zeegusha zasixhenxe, neemvana zasixhenxe, neenciniba zasixhenxe, ukuba zibe lidini lesono ngenxa yobukumkani, nangenxa yendawo engcwele, nangenxa yamaYuda. Wabawisela umthetho ababingeleli, oonyana baka-Aron, ukuba bababulalele esibingelelweni sikaYehova.', 'XHO22'),
(18, 29, 22, 'Bazixhela iinkunzi ezintsha zeenkomo, balithabatha ababingeleli igazi, bafefa ngalo esibingelelweni; bekunjalo, emva kokuxhelela iinkunzi zezimvu, balitshiza igazi esibingelelweni; baxhela neemvana, balitshiza igazi esibingelelweni isibingelelo.', 'XHO22'),
(18, 29, 23, 'Bandula ke bazizisa izikhova phambi kokumkani nebandla; bababeka izandla.', 'XHO22'),
(18, 29, 24, 'Bazixhela ababingeleli, baza bacamagushela ngegazi lazo esibingelelweni ukuze bagubungele izono zabo bonke oonyana bakaSirayeli; ngokuba ukumkani wayewise umthetho wokuba kwenziwe amadini anyukayo nedini lesono ngenxa yamaSirayeli onke.', 'XHO22'),
(18, 29, 25, 'Wabamisa abaLevi endlwini kaYehova bephethe amacangci iihadi neehadi, ngokomyalelo kaDavide noGadi imboni kakumkani nomprofeti uNatan; ngokuba bekunjalo ngomthetho kaYehova ngomthetho wakhe abaprofeti.', 'XHO22'),
(18, 29, 26, 'Bema abaLevi beneempahla zikaDavide, ababingeleli benamaxilongo.', 'XHO22'),
(18, 29, 27, 'Wathi uHezekiya, makuziswe idini elinyukayo esibingelelweni; Ekuqalisweni komnikelo otshiswayo, ingoma kaYehova yaqalisa ngamaxilongo nezixhobo ezazibekwe nguDavide ukumkani wakwaSirayeli.', 'XHO22'),
(18, 29, 28, 'Laguqa lonke ibandla, iimvumi zivuma, amaxilongo avuthela; Kwenziwa oko konke lada lagqitywa idini elinyukayo.', 'XHO22'),
(18, 29, 29, 'Bakugqiba ukubingelela, ukumkani nabo bonke ababenaye baguqa baqubuda.', 'XHO22'),
(18, 29, 30, 'Ngaphezu koko, ukumkani uHezekiya nabathetheli babayalela abaLevi ukuba bavume kuYehova amazwi kaDavide noAsafu imboni. Bavuma iingoma zokudumisa, benovuyo, bathoba iintloko zabo, baqubuda.', 'XHO22'),
(18, 29, 31, 'Waphendula uHezekiya, wathi, Zinikele kuYehova ngoku; sondela, uzise imibingelelo neyombulelo endlwini kaYehova. Ibandla lazisa imibingelelo neyombulelo; bonke abantliziyo ziqhutywa yintliziyo, mabawenze amadini anyukayo.', 'XHO22'),
(18, 29, 32, 'Inani lamadini anyukayo elawazisayo ibandla, lalingamashumi amathandathu eenkomo, nekhulu leenkunzi zeegusha, namakhulu amabini eemvana; Elo lonke lidini elinyukayo likaYehova.', 'XHO22'),
(18, 29, 33, 'Izinto ezingcwele zazingamakhulu amathandathu eenkomo, namawaka amathathu empahla emfutshane.', 'XHO22'),
(18, 29, 34, 'Ke ababingeleli babephelelwe, ababa nakuwawisa onke amadini anyukayo; bancedisa abazalwana babo abaLevi, ude ugqitywe umsebenzi, bade bazihlambulule ababingeleli; ngokuba abaLevi babentliziyo iqinile ekwenzeni izithembiso zabo ngaphezu kwababingeleli.', 'XHO22'),
(18, 29, 35, 'Yaye imibingelelo etshiswayo yaba mininzi, kwanamanqatha emibingelelo yombulelo neminikelo ethululwayo yawo wonke umnikelo otshiswayo. Inkonzo yendlu kaYehova yayalelwa ngolo hlobo.', 'XHO22'),
(18, 29, 36, 'uHezekiya nabo bonke abantu bonwabile xa uYehova elulungisa uhlanga, kuba loo nto yenzeka ngequbuliso.', 'XHO22'),
(18, 30, 1, 'Wathumela uHezekiya kuwo onke amaSirayeli namaYuda, wabhala iincwadi eziya kumaEfrayim namaManase, ukuba kuziwe endlwini kaYehova eYerusalem, kwenziwe ipasika kaYehova, uThixo kaSirayeli.', 'XHO22'),
(18, 30, 2, 'Ukumkani nabathetheli bakhe nebandla lonke eYerusalem baceba ukwenza ipasika ngeyesibini inyanga.', 'XHO22'),
(18, 30, 3, 'Kuba babengenakukwenza oko ngelo xesha, ngenxa yokuba ababingeleli babengazingcwalisanga ngokwaneleyo, nabantu abazange bahlanganisane eYerusalem.', 'XHO22'),
(18, 30, 4, 'Loo nto yalunga emehlweni okumkani, nasemehlweni ebandla lonke.', 'XHO22'),
(18, 30, 5, 'Bawisa umthetho, bandulula ukuba bandulule ilizwi koonyana bakaSirayeli, kuthabathele eBher-shebha, kuse kwaDan, beze kumenzele ipasika uYehova, uThixo kaSirayeli eYerusalem; ngokuba yayiseyimihla yamandulo ukwenziwa ipasika kwenziwa ibhaliwe.', 'XHO22'),
(18, 30, 6, 'Bahamba abathetheli neencwadi zokumkani nezabathetheli bakhe, kulo lonke ilizwe lakwaSirayeli nelakwaYuda, nangokwelizwi lokumkani, elithi, Bonyana bakaSirayeli, buyelani kuYehova, uThixo ka-Abraham, kaIsake, kaSirayeli, endinidalileyo uya kubuyela kumasalela akho, abasindileyo esandleni sookumkani baseAsiriya.', 'XHO22'),
(18, 30, 7, 'Musani ukuba njengooyihlo nanjengabazalwana benu abona uYehova uThixo wookhokho babo, owabanika imfumba yamanxuwa, njengani.', 'XHO22'),
(18, 30, 8, 'Musani ukuba lukhuni ngokokwenza ooyihlo; yibani kuYehova, ningene engcweleni yakhe, ayingcwalisayo kude kuse ephakadeni, nimkhonze uYehova uThixo wenu, kubuye kuni ukuvutha komsindo wakhe.', 'XHO22'),
(18, 30, 9, 'Kuba, ukuba nithe naguquka kuYehova, abazalwana benu nabantwana benu bofumana inceba phambi kwabo bathimbe, babuyele kweli lizwe; ngokuba unemfesane, unemfesane uYehova uThixo wenu, wonibuyisa, ukuba nibuyele kuye.', 'XHO22'),
(18, 30, 10, 'Amanani ahamba ukusuka kwesinye isixeko, aye kwesinye, elizweni lakwaEfrayim nelakwaManase, kwesa kwaZebhulon;', 'XHO22'),
(18, 30, 11, 'Bazithoba oonyana baka-Ashere nabakwaManase nakwaZebhulon, beza eYerusalem.', 'XHO22'),
(18, 30, 12, 'Kananjalo isandla sikaThixo sasikho kwaYuda, ukuba abanike ntliziyo-nye yokwenza umthetho wokumkani nabathetheli, ngokwelizwi likaYehova.', 'XHO22'),
(18, 30, 13, 'Kwahlanganisana eYerusalem abantu abaninzi, ukuze benze umthendeleko wezonka ezingenagwele ngenyanga yesibini, balibandla elikhulu kunene.', 'XHO22'),
(18, 30, 14, 'Besuka bema, bazisusa izibingelelo eziseYerusalem, bazisusa zonke izibingelelo zeziqhumiso, baziphosa entilini yomlambo waseKidron.', 'XHO22'),
(18, 30, 15, 'Bandula ke benza ipasika ngosuku lweshumi elinesine lwenyanga yesibini, yaye ababingeleli nabaLevi baba neentloni baza bazingcwalisa, bawazisa amadini anyukayo endlwini kaYehova.', 'XHO22'),
(18, 30, 16, 'Baqhubeka bemi endaweni yabo ngokwesiko labo, ngokomthetho kaMoses, umntu kaTHIXO oyinyaniso;', 'XHO22'),
(18, 30, 17, 'Ngokuba babebaninzi ebandleni ababengangcwaliswanga; Ngoko ke abaLevi babephethe bonke ababengahlambulukanga ngePasika, ukuze bangcwaliswe uYehova.', 'XHO22'),
(18, 30, 18, 'Kuba inkitha yabantu, abaninzi bakwaEfrayim nakwaManase, kwaIsakare nakwaZebhulon, abazange bazihlambulule, kodwa bayidla ipasika ngokuchasene noko kubhaliweyo. Kodwa uHezekiya wabathandazela, esithi, uYehova olungileyo uxolela bonke abantu;', 'XHO22'),
(18, 30, 19, 'oyibhekise intliziyo yakhe ekumquqeleni uThixo uYehova, uThixo wooyise, nakuba kungengokwentlambululo yendawo engcwele.', 'XHO22'),
(18, 30, 20, 'uYehova wamphulaphula uHezekiya waza wabaphilisa abantu.', 'XHO22'),
(18, 30, 21, 'Oonyana bakaSirayeli ababeseYerusalem benza umthendeleko wezonka ezingenagwele imihla yasixhenxe, bevuya kakhulu;', 'XHO22'),
(18, 30, 22, 'Wathi uHezekiya kubaLevi bonke, ababekuqondile ukumazi kakuhle uYehova, wabathuthuzela; yaye ebudeni bomthendeleko badla iintsuku ezisixhenxe baze banikele imibingelelo yombulelo baze bavume izono zabo kuYehova uThixo wooyise.', 'XHO22'),
(18, 30, 23, 'Ibandla lonke laceba ukubhiyozela ezinye iintsuku ezisixhenxe, baza babhiyozela ezinye iintsuku ezisixhenxe ngovuyo.', 'XHO22'),
(18, 30, 24, 'Ngokuba uHezekiya ukumkani wakwaYuda walirhumela ibandla iwaka leenkunzi ezintsha zeenkomo, namawaka asixhenxe empahla emfutshane; Izikhulu zalirhumela ibandla iwaka leenkunzi ezintsha zeenkomo, namawaka alishumi empahla emfutshane, bangcwalisa ababingeleli abaninzi.', 'XHO22'),
(18, 30, 25, 'Lavuya lonke ibandla lakwaYuda, nababingeleli, nabaLevi, nebandla lonke elavela kwaSirayeli, nabasemzini, bevela ezweni lakwaSirayeli, nababehleli kwaYuda.', 'XHO22'),
(18, 30, 26, 'Kwabakho uvuyo olukhulu eYerusalem, ngenxa yokuba ukususela ngemihla kaSolomon unyana kaDavide ukumkani wakwaSirayeli, akuzange kubekho into enjengale eYerusalem.', 'XHO22'),
(18, 30, 27, 'Besuka bema ababingeleli babaLevi, babasikelela abantu; lavakala ilizwi labo, wenyuka nomthandazo wabo waya endaweni yakhe engcwele emazulwini.', 'XHO22'),
(18, 31, 1, 'Kwathi ke konke oko kwenziwe, aphuma onke amaSirayeli abekhona, aya emizini yakwaYuda, aziqhekeza izimiso zamatye, azigawula imithi, azidiliza izibingelelo nezibingelelo kumaYuda onke nakwaBhenjamin, ezweni lakwaEfrayim noManase kwakhona, bada babaqalekisa bonke. Babuyela ke bonke oonyana bakaSirayeli emizini yabo, elowo elifeni lakhe.', 'XHO22'),
(18, 31, 2, 'uHezekiya wawamisa amaqela ababingeleli nabaLevi ngokwamaqela abo, ngalinye ngokwenkonzo yalo, ababingeleli nabaLevi njengeminikelo etshiswayo neyombulelo, ukuze balungiselele, badumise nokudumisa eminyango yeentente Zesibini. Nkosi.', 'XHO22'),
(18, 31, 3, 'Kwakhona, wanyula inxalenye yempahla kakumkani ukuze ibe yiminikelo etshiswayo, iminikelo etshiswayo yakusasa neyangokuhlwa neminikelo etshiswayo yeesabatha neyokuthwasa kwenyanga neyemithendeleko, kanye njengoko kubhaliwe emthethweni kaYehova.', 'XHO22'),
(18, 31, 4, 'Ngaphezu koko, wayalela abantu baseYerusalem ukuba banikele isabelo sababingeleli nabaLevi, ukuze baqine emthethweni kaYehova.', 'XHO22'),
(18, 31, 5, 'Yaye kwathi kamsinya nje ekuphumeni kwalo myalelo oonyana bakaSirayeli bazisa intlahlela yengqolowa, iwayini neoli nobusi nentabalala yazo zonke iziqhamo zelizwe; saye saphuphuma isishumi sezinto zonke.', 'XHO22'),
(18, 31, 6, 'Oonyana bakaSirayeli nabakwaYuda ababehlala kwizixeko zakwaYuda nabo bazisa isishumi seenkomo nesezimvu nesishumi sezinto ezingcwele ezazinyulelwe uYehova uThixo wabo baza baziqokelela zaba ziimfumba.', 'XHO22'),
(18, 31, 7, 'Baqala ngenyanga yesithathu ukuzenza iziseko zemfumba, bagqiba ngenyanga yesixhenxe.', 'XHO22'),
(18, 31, 8, 'Xa uHezekiya neenkosana beza baza bazibona iimfumba zewax uYehova nabantu bakhe amaSirayeli.', 'XHO22'),
(18, 31, 9, 'Wababuza uHezekiya ababingeleli nabaLevi ngeemfumba ezo.', 'XHO22'),
(18, 31, 10, 'Waphendula uAzariya, umbingeleli omkhulu wendlu kaTsadoki, wathi kuye, Kususela oko abantu beqalile ukuzisa imibingelelo endlwini kaYehova, sakwazi ukudla, saza ke saphela; ngokuba uYehova ebeyisikelele indlu yendlu kaYehova abantu kwaye okuseleyo yile venkile inkulu.', 'XHO22'),
(18, 31, 11, 'Waza uHezekiya wayalela ukuba alungiswe amagumbi endlu kaYehova; bazilungiselela ke.', 'XHO22'),
(18, 31, 12, 'Wawuzisa umrhumo, nezishumi, nezipho ezingcwele, enyanisekile; uKonaniya umLevi waba ngukumkani esikhundleni sakhe, yanguShimehi umntakwabo.', 'XHO22'),
(18, 31, 13, 'uYehiyeli noAzasiya noNahati noAsaheli noYerimoti noYozabhadi noEliyeli noIsmagiya noMahati noBhenaya babengabalindi phantsi kwesandla sikaKonaniya noShimehi umntakwabo ngokomyalelo kaKumkani uHezekiya noAzariya intloko yendlu yoTHIXO oyinyaniso.', 'XHO22'),
(18, 31, 14, 'uKora unyana kaImna, umLevi, umlindi wesango elingasempuma, wayephethe izipho zokuzithandela zikaThixo, ukuze anikele umnikelo kaYehova nezona zipho zingcwele.', 'XHO22'),
(18, 31, 15, 'Ecaleni kwakhe yayinguEden noMinyamin noYeshuwa noShemaya uAmariya noShekaniya, kwizixeko zababingeleli, nomsebenzi wabo onyuliweyo, ukuze banike abazalwana babo, abadala nabancinane;', 'XHO22'),
(18, 31, 16, 'ababalelwa emizalwaneni yabo into eyindoda, ethabathela kominyaka mithathu ezelwe kwenyusa, kubo bonke abangenayo endlwini kaYehova, umlinganiso wemini ngangemini yayo, ngokwamaqela abo;', 'XHO22'),
(18, 31, 17, 'Ngokwengxelo yentsapho yababingeleli ngokweentsapho zabo nabaLevi ukususela kwabanamashumi amabini eminyaka ubudala ukusa phezulu, ngokwenkonzo yabo ngokwamaqela abo;', 'XHO22'),
(18, 31, 18, 'ubhale umlibo wokuzalwa koonyana babo bonke, nabafazi babo, noonyana babo, neentombi zabo, kwibandla lonke; ngokuba babezinikele emsebenzini wabo ongcwele.', 'XHO22'),
(18, 31, 19, 'Kananjalo koonyana baka-Aron, ababingeleli ababesedlelweni lemizi yabo emizini yabo, emizini ngemizi, amadoda lawo abizwe ngamagama, ukuba bababele izabelo yonke into eyindoda phakathi kwababingeleli, nakubantu bonke abafumanekayo babalelwa kubaLevi ngokwemizalwane yabo.', 'XHO22'),
(18, 31, 20, 'Wenza loo nto uHezekiya kulo lonke ilizwe lakwaYuda, wenza okulungileyo, nokuthe tye, nokwenyaniso, phambi koYehova uThixo wakhe;', 'XHO22'),
(18, 31, 21, 'Yaye kuwo wonke umsebenzi awawuqalayo wenkonzo yendlu yoTHIXO oyinyaniso nomthetho nomyalelo wokufuna uThixo wakhe wawuwenza ngentliziyo yakhe yonke waza waphumelela.', 'XHO22'),
(18, 32, 1, 'Emveni koko, nokuqinisela kwabo, weza uSaneribhe, ukumkani waseAsiriya, walingenela elakwaYuda, wayingqinga imizi enqatyisiweyo, wathi uya kuyithimba.', 'XHO22'),
(18, 32, 2, 'Wabona uHezekiya ukuba uSenaribhe uyeza, waceba ukuyihlasela iYerusalem.', 'XHO22'),
(18, 32, 3, 'Wacebisana nabathetheli bakhe namagorha akhe, ukuba bawavingce amanzi emithonjeni engaphandle komzi; bamnceda ke.', 'XHO22'),
(18, 32, 4, 'Kwahlanganisana abantu abaninzi, bawavala onke amaqula, nomlanjana oqukuqelayo ezweni, bathi, Yini na ukuba ookumkani baseAsiriya beze bafumane amanzi amaninzi?', 'XHO22'),
(18, 32, 5, 'Wazomeleza, walwakha lonke udonga olwaluwile, waluphakamisa ukuya kutsho kwiinqaba ezinde nolunye udonga ngaphesheya, wayivusa kwakhona iMilo kwisiXeko sikaDavide, wenza neentolo namakhaka amaninzi.', 'XHO22'),
(18, 32, 6, 'Wabeka amadoda emfazwe phezu kwabantu waza wawahlanganisela kuye ebaleni lesango lesixeko waza wathetha nawo ngendlela ethuthuzelayo, wathi:', 'XHO22'),
(18, 32, 7, 'Yomelelani nikhaliphe, musani ukoyika okanye ningoyiki ngenxa kakumkani waseAsiriya nangenxa yaso sonke isihlwele esikunye naye, kuba abo bakunye nathi baninzi ngakumbi kunabo bakunye naye.', 'XHO22'),
(18, 32, 8, 'Kuye kukho isandla senyama; kodwa kuthi uYehova nguThixo wethu ukuba asincede asilwele amadabi ethu. Ke abantu bakholosa ngamazwi kaHezekiya ukumkani wakwaYuda.', 'XHO22'),
(18, 32, 9, 'Emva koko uSenaribhe ukumkani waseAsiriya wathumela abakhonzi bakhe eYerusalem (kodwa wayingqinga iLakishe wonke umkhosi wakhe wamajoni awayekunye naye) kuHezekiya ukumkani wakwaYuda nakuYuda wonke owayeseYerusalem, wathi:', 'XHO22'),
(18, 32, 10, 'Kwathiwa nguSaneribhe, ukumkani waseAsiriya, Ubuthembele ngantoni na, usayingqingile iYerusalem?', 'XHO22'),
(18, 32, 11, 'uHezekiya makanganilahli na, ukuba uzinikele ekufeni yindlala nakunxanwa, esithi, uYehova, uThixo wethu, wosihlangula esandleni sokumkani waseAsiriya?', 'XHO22'),
(18, 32, 12, 'Ndathi, Ndathi, uHezekiya akazisusi iziganga zakhe nezibingelelo zakhe, wathi kumaYuda nakwiYerusalem, Qubudani phambi kwesibingelelo sinye, niqhumisele phezu kwaso?', 'XHO22'),
(18, 32, 13, 'Aniyazi na into endayenzayo noobawo ezintlangeni zonke zamazwe? Baba nako nokuba nako na oothixo bezizwe zala mazwe ukuwahlangula amazwe azo esandleni sam?', 'XHO22'),
(18, 32, 14, 'Nguwuphi na koothixo bonke beentlanga ezabaqalekisayo oobawo owabahlangulayo abantu bakhe esandleni sam, ukuze uThixo wenu anihlangule esandleni sam?', 'XHO22'),
(18, 32, 15, 'Ke kaloku, makanganikhohlisi uHezekiya, anikhohlise ngale ndlela, ningakholwa nguye nangoku; kuba akukho thixo walo naluphi na uhlanga okanye naluphi na ubukumkani oye wakwazi ukuhlangula abantu bako esandleni sam nasesandleni sooyise. Akayi kunihlangula na uThixo wenu esandleni sam?', 'XHO22'),
(18, 32, 16, 'Abakhonzi bakhe baqhubeka bethetha kakubi ngoYehova uThixo nomkhonzi wakhe uHezekiya.', 'XHO22'),
(18, 32, 17, 'Kananjalo wabhala iincwadi zokumnyelisa uYehova, uThixo kaSirayeli, wathetha kakubi ngaye, esithi, Njengoko oothixo bezizwe zakwamanye amazwe bengabasindisanga abantu babo esandleni sam, wenjenjalo uThixouThixo kaHezekiya ayiyi kusindisa abantu bakhe esandleni sam.', 'XHO22'),
(18, 32, 18, 'Bamemeza ngelizwi elikhulu ngentetho yesiYuda kubemi baseYerusalem ababeseludongeni, beboyikisa, beboyikisa; ukuze bawuthimbe umzi.', 'XHO22'),
(18, 32, 19, 'Bathetha ngoThixo weYerusalem njengoothixo bezizwe zehlabathi, umsebenzi wezandla zomntu.', 'XHO22'),
(18, 32, 20, 'Ngoko uKumkani uHezekiya noIsaya unyana ka-Amos umprofeti bathandaza baze babongoze izulu.', 'XHO22'),
(18, 32, 21, 'uYehova wathumela isithunywa sezulu, sawatshabalalisa onke amagorha, neenkosana, nabathetheli, emkhosini wokumkani waseAsiriya. Wabuyela kwelakowabo edanile. Uthe ke akungena endlwini yothixo wakhe, zaza zaphuma ezibilinini zakhe zambulalela khona ngekrele.', 'XHO22'),
(18, 32, 22, 'Ngoko uYehova wamhlangula uHezekiya nabemi baseYerusalem esandleni sikaSaneribhe ukumkani waseAsiriya nasesandleni sabo bonke, wabakhupha macala onke.', 'XHO22'),
(18, 32, 23, 'Abaninzi bazisa izipho kuYehova eYerusalem nezipho kuHezekiya ukumkani wakwaYuda, kangangokuba ukususela ngoku kuse phambili wazukiswa emehlweni eentlanga zonke.', 'XHO22'),
(18, 32, 24, 'Ngelo xesha wayesifa uHezekiya, wayeza kufa, wathandaza kuYehova; wathetha kuye, wamnika umqondiso.', 'XHO22'),
(18, 32, 25, 'Ke uHezekiya akazange abuyekeze ngokwenceba abenzelwe yona; ngokuba intliziyo yakhe yaphakama; ke ngoko, waba nomsindo ngakuye noYuda neYerusalem.', 'XHO22'),
(18, 32, 26, 'Ke uHezekiya wamthoba ngenxa yekratshi lentliziyo yakhe, yena nabemi baseYerusalem, ababafikela ngemihla kaHezekiya uburhalarhume bukaYehova.', 'XHO22'),
(18, 32, 27, 'uHezekiya ube nobutyebi nozuko olukhulu kunene, wazenzela ubutyebi besilivere, negolide, namatye anqabileyo, nobulawu, namakhaka, nawo onke amatye anqabileyo;', 'XHO22'),
(18, 32, 28, 'noovimba bengqolowa, nobewayini, nobeoli; kunye neendlwana zezilwanyana ngeentlobo, kunye neendlwana zeenkomo.', 'XHO22'),
(18, 32, 29, 'Ngaphezu koko, wamnika izixeko nemfuyo yempahla emfutshane, neenkomo ezininzi, ngenxa yokuba uThixo wayemnike ubutyebi obuninzi.', 'XHO22'),
(18, 32, 30, 'uHezekiya lo wawuvala umntla ongasentla wamanzi aseGihon, wawubhekisa ngasentshona kwisixeko sikaDavide. Yaye uHezekiya waqhubeka ephumelela kuyo yonke imisebenzi yakhe.', 'XHO22'),
(18, 32, 31, 'Ke mayela nabathunywa babathetheli baseBhabheli, ababethunyiwe kuye, beza kumbuza ngomangaliso owabakho kwelo lizwe, uThixo wamvavanya, ukuze akwazi konke okusentliziyweni yakhe.', 'XHO22'),
(18, 32, 32, 'Ezinye izinto zikaHezekiya, nokwenza kwakhe ngenceba, nanzo kubhaliwe emehlweni kaIsaya unyana ka-Amos, umprofeti, nasencwadini yookumkani bakwaYuda nabakwaSirayeli.', 'XHO22'),
(18, 32, 33, 'Walala uHezekiya kooyise, bamngcwabela entloko kumangcwaba oonyana bakaDavide; yaye onke amaYuda nabemi baseYerusalem bambeka imbeko ekufeni kwakhe. uManase unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(18, 33, 1, 'uManase ubeminyaka ilishumi elinamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mahlanu anamihlanu engukumkani eYerusalem.', 'XHO22'),
(18, 33, 2, 'Wenza okubi emehlweni kaYehova, njengamasikizi eentlanga, awazigqogqayo uYehova phambi koonyana bakaSirayeli.', 'XHO22'),
(18, 33, 3, 'Wazakha kwakhona iindawo eziphakamileyo ezo uHezekiya uyise wayezidilizile, waza wakhela ooBhahali izibingelelo, wenza nemithi, waqubuda, wakhonza zonke izidalwa zasezulwini.', 'XHO22'),
(18, 33, 4, 'Wazakha izibingelelo endlwini kaYehova, leyo wathi uYehova ngayo, Igama lam liya kuba yiYerusalem ngonaphakade.', 'XHO22'),
(18, 33, 5, 'Wazakhela izibingelelo zonke izidalwa zasezulwini kwiintendelezo zombini zendlu kaYehova.', 'XHO22'),
(18, 33, 6, 'Wabacandisa oonyana bakhe emlilweni, emfuleni wakwaBhen-hinom; kananjalo wayewagcina amaxesha, ehlaba, enemilingo, esebenzelana nemimoya nabakhafuli; wenza ububi obukhulu emehlweni kaYehova, ukuba amqumbise.', 'XHO22'),
(18, 33, 7, 'Wawubeka umfanekiso oqingqiweyo, umfanekiso abewenzile, endlwini kaThixo, awathi uThixo kuDavide nakuSolomon unyana wakhe, Kule ndlu naseYerusalem, endiyinyulileyo phakathi kwezizwe zonke zakwaSirayeli; ndiya kulimisa igama lam ngonaphakade.', 'XHO22'),
(18, 33, 8, 'Andisayi kuba salususa unyawo lukaSirayeli emhlabeni endawushumayezayo kooyihlo-mkhulu; ukuze bakunyamekele ukwenza konke endibayalele kona, ngokomthetho wonke nemiyalelo nezigwebo ngesandla sikaMoses.', 'XHO22'),
(18, 33, 9, 'Yaye uManase wamtshabalalisa uYuda nabemi baseYerusalem nokwenza okubi ngakumbi kuneentlanga ezo uYehova wazitshabalalisayo phambi koonyana bakaSirayeli.', 'XHO22'),
(18, 33, 10, 'uYehova wathetha kuManase nakubantu bakhe, kodwa abazange baphulaphule.', 'XHO22'),
(18, 33, 11, 'uYehova wabazisa abathetheli bempi yokumkani waseAsiriya, ukuba bafike kubo. Bambamba uManase ngameva, bamkhonkxa ngamakhamandela, bamsa eBhabheli.', 'XHO22'),
(18, 33, 12, 'Ke kaloku kwathi, ekubandezelekeni kwakhe, wathandaza kuYehova uThixo wakhe, wazithoba kakhulu phambi koThixo wooyise.', 'XHO22'),
(18, 33, 13, 'Wathandaza kuye, wathandaza kuye, wakuva ukucela kwakhe, wambuyisela eYerusalem ebukumkanini bakhe. Wazi ke uManase ukuba uYehova nguThixo.', 'XHO22'),
(18, 33, 14, 'Emva koko wakha udonga ngaphandle kwesixeko sikaDavide ngasentshona yeGihon entilini, wada wee xhaxhe ngeSango leeNtlanzi, wayijikeleza iOfeli, wayiphakamisa kakhulu kuzo zonke izixeko ezinqatyisiweyo zakwaYuda.', 'XHO22'),
(18, 33, 15, 'Wabasusa ke abanye oothixo nesithixo endlwini kaYehova nesiqhumiso sonke awayesakhe entabeni yendlu kaYehova naseYerusalem, wazilahlela ngaphandle kwesixeko.', 'XHO22'),
(18, 33, 16, 'Walungisa isibingelelo sikaYehova waza wanikela phezu kwaso imibingelelo neyombulelo, waza wayalela amaYuda ukuba akhonze uYehova uThixo kaSirayeli.', 'XHO22'),
(18, 33, 17, 'Kodwa abantu bam babebingelela kwiindawo eziphakamileyo, kodwa babesenzela uYehova uThixo wabo kuphela.', 'XHO22'),
(18, 33, 18, 'Ezinye izinto zikaManase, nomthandazo wakhe kuThixo wakhe, namazwi ababoni ababethetha kuye egameni likaYehova, uThixo kaSirayeli, nanzo zibhaliwe encwadini yookumkani bakwaSirayeli.', 'XHO22'),
(18, 33, 19, 'Nomthandazo wakhe nendlela awaviwa ngayo uThixo kuye, nesono sakhe sonke nokunxaxha kwakhe neendawo awakha kuzo iindawo eziphakamileyo waza watyala izikhondo zemithi engcwele nemifanekiso eqingqiweyo ngaphambi kokuba athobeke. Yabonani, kubhaliwe kwathiwa, phakathi kwemigwebo yeemboni.', 'XHO22'),
(18, 33, 20, 'uManase walala kookhokho bakhe, bamngcwabela endlwini yakhe, yaye uAmon unyana wakhe waba ngukumkani esikhundleni sakhe.', 'XHO22'),
(18, 33, 21, 'uAmon ubeminyaka imashumi mabini anamibini ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emibini engukumkani eYerusalem.', 'XHO22'),
(18, 33, 22, 'Wenza okubi emehlweni kaYehova, njengoManase uyise; uAmon wabingelela yonke imifanekiso eqingqiweyo, awayenzayo uManase uyise, wayikhonza;', 'XHO22'),
(18, 33, 23, 'yaye akazange azithobe phambi koYehova njengoko wazithobayo uManase uyise; kodwa uAmon waqhubeka eqhekeza.', 'XHO22'),
(18, 33, 24, 'Bagwilika kuye abakhonzi bakhe, bambulala endlwini yakhe.', 'XHO22'),
(18, 33, 25, 'Kwaye abantu behlabathi bababulala bonke abo babecebe nxamnye nokumkani uAmon; abantu belizwe bamenza ukumkani uYosiya, unyana wakhe, esikhundleni sakhe.', 'XHO22'),
(18, 34, 1, 'uYosiya ubeminyaka isibhozo ezelwe, ukuba ngukumkani kwakhe; waba neminyaka emashumi mathathu anamnye engukumkani eYerusalem.', 'XHO22'),
(18, 34, 2, 'Wenza okuthe tye emehlweni kaYehova, wahamba emizekweni kaDavide uyise, akatyekela ekunene nasekhohlo.', 'XHO22'),
(18, 34, 3, 'Kuba ngomnyaka wesibhozo wobukumkani bakhe, esemncinane, waqala wamquqela uThixo kaDavide uyise; Ngomnyaka weshumi elinesibini waqala wawahlambulula amaYuda neYerusalem, ukuze azishenxise iziganga, neziqu zemithi, nemifanekiso eqingqiweyo netyhidiweyo.', 'XHO22'),
(18, 34, 4, 'Zazidiliza phambi kwakhe izibingelelo zooBhahali; nezithixo eziphakamileyo ezibe ziphezulu, wazidiliza; neziqu ezingcwele nemifanekiso eqingqiweyo nemifanekiso eqingqiweyo wayiqhekeza waza wayityumza waza wayichithachitha phezu kwamangcwaba abo babebingelela kuyo.', 'XHO22'),
(18, 34, 5, 'Wawatshisa amathambo ababingeleli phezu kwezibingelelo zabo, wawahlambulula ke amaYuda neYerusalem.', 'XHO22'),
(18, 34, 6, 'Wenjenjalo emizini yakwaManase, neyakwaEfrayim, neyakwaSimon, kwesa kwaNafetali, nelizwe layo ngeenxa zonke.', 'XHO22'),
(18, 34, 7, 'Emva kokuba ezidilizile izibingelelo nemithi engcwele nemifanekiso eqingqiweyo wayigawula yaluthuli waza wazigawula zonke izithixo ezazinqulwa kulo lonke ilizwe lakwaSirayeli, wabuyela eYerusalem.', 'XHO22'),
(18, 34, 8, 'Ngomnyaka weshumi elinesibhozo anesibini wobukumkani bakhe, ulihlambulule ilizwe nendlu, wathuma uShafan unyana ka-Azaliya, noMahaseya umphathi womzi, noYowa unyana kaYowaka, umthetheli; ngeenxa zonke esixekweni indlu kaYehova uThixo wakhe.', 'XHO22'),
(18, 34, 9, 'Xa beza kuHilekiya umbingeleli omkhulu baza bayinikela imali eyayiziswe endlwini yoTHIXO oyinyaniso, leyo yayiqokelelwe ngabaLevi ababegcina amasango esandleni sikaManase noEfrayim nakuwo onke amasalela akwaSirayeli kubo bonke abasendlwini kaThixo uYuda noBhenjamin; babuyela eYerusalem.', 'XHO22'),
(18, 34, 10, 'Bayinika abasebenzi ababephethe indlu kaYehova, bayinika abasebenzi abasebenza endlwini kaYehova ukuba bayilungise bayilungise indlu.', 'XHO22'),
(18, 34, 11, 'Balinika iingcibi nabakhi, ukuba kuthengwe amatye axholiweyo, nemithi, ukuba badibanise, bakhe izindlu, ezo bazichithayo ookumkani bakwaYuda.', 'XHO22'),
(18, 34, 12, 'Loo madoda ebesebenza umsebenzi wawo enyanisekile; Abaveleli babo ibinguYahati no-Obhadiya, abaLevi bakoonyana bakaMerari; uZekariya noMeshulam, oonyana bamaHeti, bayifundisa; nabanye abaLevi, bonke ababekwazi ukucula.', 'XHO22'),
(18, 34, 13, 'Babephethe abathwali bemithwalo yaye babegcina isigxina sabo bonke abenza umsebenzi wawo nawuphi na umsebenzi; kubaLevi kwakukho ababhali, amagosa nabagcini-masango.', 'XHO22'),
(18, 34, 14, 'Xa babeyikhupha imali eyayiziswe endlwini kaYehova, umbingeleli uHilekiya wayifumana incwadi yomthetho kaYehova eyanikelwa ngesandla sikaMoses.', 'XHO22'),
(18, 34, 15, 'Waphendula uHilekiya, wathi kuShafan umbhali, Ndifumene incwadi yomyalelo endlwini kaYehova. uHilekiya wayinika uShafan incwadi.', 'XHO22'),
(18, 34, 16, 'uShafane wayizisa incwadi kukumkani, waza ukumkani wayibuyisela kuye, wathi, Konke okuphathiswe esandleni sabakhonzi bakho, bokwenza.', 'XHO22'),
(18, 34, 17, 'Yaye bayiqokelela imali eyafumanekayo endlwini kaYehova, bayibeka esandleni sabalindi nasesandleni sabasebenzi.', 'XHO22'),
(18, 34, 18, 'Waxela ke uShafan umbhali kukumkani, wathi, Umbingeleli uHilekiya undinike incwadi. uShafane wafunda phambi kokumkani.', 'XHO22'),
(18, 34, 19, 'Uthe akuweva ukumkani amazwi omyalelo, wazikrazula iingubo zakhe.', 'XHO22'),
(18, 34, 20, 'Ukumkani wabawisela umthetho uHilekiya, noAhikam, unyana kaShafan, noAbhedon, unyana kaMikaya, noShafan umbhali, noAsaya umkhonzi wokumkani, wathi:', 'XHO22'),
(18, 34, 21, 'Yiyani nibuze kuYehova ngenxa yam, nangenxa yamasalela akwaSirayeli namaYuda, ngokuphathelele amazwi encwadi efunyenweyo; ngenxa yokuba mkhulu umsindo kaYehova ophalazwe phezu kwethu, ngenxa yokuba ookhokho bethu abazange baligcine ilizwi likaYehova ukuba benze yonke into ebhaliweyo kule ncwadi.', 'XHO22'),
(18, 34, 22, 'Waya uHilekiya nabakwakumkani kuHulida umprofetikazi, umkaShalum unyana kaTikwata, unyana kaHatsera, umgcini-ngubo; (ebe ehleli eYerusalem ekholejini) baza bathetha naye ngayo.', 'XHO22'),
(18, 34, 23, 'Wathi kubo, Utsho uYehova, uThixo kaSirayeli, ukuthi, Nothi kwindoda eniyithumele kum, nithi,', 'XHO22'),
(18, 34, 24, 'Wathi uYehova, Yabona, ndiyizisela ububi le ndawo, naphezu kwabemi bayo, zonke iziqalekiso ezibhaliweyo encwadini abayifundayo phambi kokumkani wakwaYuda.', 'XHO22'),
(18, 34, 25, 'Ngenxa yokuba bandinikele umva baza baqhumisa abanye oothixo ngenxa yam, ukuze bandihlekise ngayo yonke imisebenzi yezandla zabo; ke ngoko, umsindo wam uya kuthululwa phezu kwale ndawo, kwaye awuyi kucinywa.', 'XHO22'),
(18, 34, 26, 'Ke ukumkani wakwaYuda, lowo nimthumileyo ukuba abuze kuYehova, nithi kuye, uYehova, uThixo kaSirayeli, uthethile amazwi owavileyo;', 'XHO22'),
(18, 34, 27, 'Ngenxa yokuba intliziyo yakho ibithambile waza wazithoba phambi koThixo ekuveni kwakho amazwi akhe nxamnye nale ndawo nanxamnye nabemi bayo, yaye wazithoba phambi kwakho wakrazula izambatho zakho walila phambi kwam; Nam ndikuvile, utsho uYehova.', 'XHO22'),
(18, 34, 28, 'Uyabona, ndiya kukugubungela nooyihlo, ugqunywe engcwabeni lakho unoxolo, angabuboni amehlo akho bonke ububi endibuzisa kule ndawo, nabemi bayo. Balibuyisela ilizwi lokumkani.', 'XHO22'),
(18, 34, 29, 'Wathumela ukumkani, wabiza onke amadoda amakhulu akwaYuda nawaseYerusalem.', 'XHO22'),
(18, 34, 30, 'Wenyuka ukumkani waya endlwini kaYehova, namadoda onke akwaYuda, nabemi baseYerusalem, nababingeleli, nabaLevi, nabantu bonke, kwathabathela komkhulu kwesa komncinane, wawafunda onke amazwi aloo ncwadi ezindlebeni zabo womnqophiso ofunyenwe endlwini kaYehova.', 'XHO22'),
(18, 34, 31, 'Ukumkani wema endaweni yakhe waza wenza umnqophiso phambi koYehova wokulandela uYehova nokugcina imiyalelo yakhe nezingqino zakhe nemithetho yakhe emisiweyo ngentliziyo yakhe yonke nangomphefumlo wakhe wonke ngokuphathelele amazwi alo mnqophiso abhalwe kule ncwadi.', 'XHO22'),
(18, 34, 32, 'Wabamisa bonke abaseYerusalem nakwaBhenjamin. Yaye abemi baseYerusalem benza ngokomnqophiso kaThixo, uTHIXO wookhokho babo.', 'XHO22'),
(18, 34, 33, 'uYosiya wawasusa onke amasikizi emazweni onke, angawoonyana bakaSirayeli, wabanikela bonke abafumanekayo kwaSirayeli ukuba bakhonze uYehova uThixo wabo. Yonke imihla yobomi bakhe abazange baphambuke ekulandeleni uYehova uTHIXO wookhokho babo.', 'XHO22'),
(18, 35, 1, 'uYosiya wenzela uYehova ipasika eYerusalem; benza ipasika ngomhla weshumi elinesine wenyanga yokuqala.', 'XHO22'),
(18, 35, 2, 'Wabamisa ababingeleli emsebenzini wabo, wabomeleza enkonzweni yendlu kaYehova;', 'XHO22'),
(18, 35, 3, 'Waqhubeka wathi kubaLevi ababefundisa bonke oonyana bakaSirayeli, ababengcwele kuYehova: “Yibekeni ityeya engcwele endlwini awayakhayo uSolomon unyana kaDavide ukumkani wakwaSirayeli; akuyi kuba mthwalo emagxeni enu; Mkhonzeni kaloku uYehova uThixo wenu, nabantu bakhe amaSirayeli;', 'XHO22'),
(18, 35, 4, 'Zilungiseni ngokwemizalwane yooyihlo, ngokwamaqela enu, ngokwesibhalo sikaDavide ukumkani wakwaSirayeli, nangokwesibhalo sikaSolomon unyana wakhe;', 'XHO22'),
(18, 35, 5, 'Yima engcweleni ngokwamaqela emizalwane yooyise, abantu namaqela emizalwane yabaLevi.', 'XHO22'),
(18, 35, 6, 'Ke ngoko bhiyozela iPasika, nizingcwalise nize nilungiselele abazalwana benu, ukuze benze ngokwelizwi likaYehova ngenkonzo kaMoses.', 'XHO22'),
(18, 35, 7, 'uYosiya wabarhumela oonyana babantu, iimvana, neenkunzi zeebhokhwe, zonke ezo nto zezombingelelo wepasika, kubo bonke abafumanekayo, inani lawo elingamawaka amashumi mathathu anesithathu samawaka; ibibubutyebi bokumkani.', 'XHO22'),
(18, 35, 8, 'Iinkosana zakhe zabanikela ngokuzithandela abantu, kubabingeleli nabaLevi: uHilekiya noZekariya noYehiyeli, iintloko zendlu yoThixo oyinyaniso, banikela kubabingeleli amawaka amabini anamakhulu amathandathu anamakhulu mathathu izimvu zombingelelo wepasika iinkomo.', 'XHO22'),
(18, 35, 9, 'Ke uKonaniya, noShemaya, noNataniyeli, abazalwana babo, noHashabhiya, noYehiyeli, noYozabhadi, iintloko zabaLevi, barhumela abaLevi amawaka amahlanu eegusha, namakhulu amahlanu eenkomo zePasika.', 'XHO22'),
(18, 35, 10, 'Yalungiswa ke inkonzo leyo; bema ababingeleli ezindaweni zabo, abaLevi ngokwamaqela abo, ngokomthetho wokumkani.', 'XHO22'),
(18, 35, 11, 'Bayixhela ipasika, ababingeleli bafefa igazi ezandleni zabo, bawathambisa abaLevi.', 'XHO22'),
(18, 35, 12, 'Bayisusa iminikelo etshiswayo ngokwamaqela ezizwe zoonyana babantu, ukuze benze umbingelelo kuYehova, kanye njengoko kubhaliwe encwadini kaMoses. Benza okufanayo nangeenkomo.', 'XHO22'),
(18, 35, 13, 'Ipasika bayitshisa ngomlilo ngokwesiko; ezinye izinto ezingcwele bazithambisa ngeembiza, neepani, nangeepani, babele ke bonke abantu ngokukhawuleza.', 'XHO22'),
(18, 35, 14, 'Emveni koko bazilungisa nababingeleli, ngokuba ababingeleli, oonyana baka-Aron, babesanyusa amadini anyukayo namaqatha, kude kube sebusuku; bazilungisa ke abaLevi, kwanababingeleli, oonyana baka-Aron.', 'XHO22'),
(18, 35, 15, 'Iimvumi, oonyana baka-Asafu, babekwindawo yabo ngokomyalelo kaDavide noAsafu noHeman noYedutun imboni kakumkani; nabamasango bemi kwiminyango ngeminyango; ungatyeki emsebenzini wabo; ngenxa yabazalwana babo, ababelungiselelwe bona ngabaLevi.', 'XHO22'),
(18, 35, 16, 'Ngoko yonke inkonzo kaYehova yalungiswa kwangaloo mini ukuze kubhiyozelwe iPasika nokwenza iminikelo etshiswayo esibingelelweni sikaYehova ngokomyalelo kaKumkani uYosiya.', 'XHO22'),
(18, 35, 17, 'Oonyana bakaSirayeli ababelapho ngelo xesha benza ipasika nomthendeleko wezonka ezingenagwele iintsuku ezisixhenxe.', 'XHO22'),
(18, 35, 18, 'Bekungazanga kwenziwe pasika ngokwalo kwaSirayeli, kususela kwimihla kaSamuweli umprofeti yaye bonke ookumkani bakwaSirayeli abazange benze umbhiyozo ofana nalowo wawubhiyozelwa nguYosiya nababingeleli nabaLevi noYuda wonke noSirayeli ababelapho nabemi baseYerusalem.', 'XHO22'),
(18, 35, 19, 'Le pasika yenziwa ngomnyaka weshumi elinesibhozo wobukumkani bukaYosiya.', 'XHO22'),
(18, 35, 20, 'Emva kwayo yonke loo nto, xa uYosiya wayeyilungisa itempile, uNego, ukumkani waseYiputa, wenyuka waya kulwa neKarkemishe umEfrate; waphuma uYosiya esiya kumkhawulela.', 'XHO22'),
(18, 35, 21, 'Wathuma abathunywa kuye, wathi, Yintoni na enam nawe, kumkani wakwaYuda? Andinilwi namhla, kodwa ndihlasela indlu endiyilwelayo; ngokuba uThixo undiwisele umthetho wokuba ndikhawuleze; musa ukumphazamisa uThixo onam, hleze unitshabalalise.', 'XHO22'),
(18, 35, 22, 'Akavumanga uYosiya ukububhekisa ubuso bakhe kuye, wazenza mntu wumbi, ukuba alwe naye, akawaphulaphula amazwi kaNego, aphuma emlonyeni woThixo oyinyaniso, weza kulwa entilini yaseMegido.', 'XHO22'),
(18, 35, 23, 'Abazingela ngeentolo bazingela ukumkani uYosiya; Wathi ukumkani kubakhonzi bakhe, Ndivumeleni; kuba ndingxwelerhekile.', 'XHO22'),
(18, 35, 24, 'Bamkhupha ke abakhonzi bakhe kuloo nqwelo, bamkhwelisa kweyesibini inqwelo abenayo; Bamsa eYerusalem, wafa, wangcwatyelwa kwelinye lamangcwaba kayise. Onke amaYuda neYerusalem wamzilela uYosiya.', 'XHO22'),
(18, 35, 25, 'uYeremiya wamenzela isijwili uYosiya; zathetha zonke iimvumi neemvumi ezimbononweni zabo ngoYosiya, unanamhla, bazibeka ngokomyalelo omiswe kwaSirayeli kwaye yabona, amazwi kaYeremiya acacisiwe.', 'XHO22'),
(18, 35, 26, 'Ezinye izinto zikaYosiya, nokwenza kwakhe ngenceba, ngoko kubhaliweyo emyalelweni kaYehova;', 'XHO22'),
(18, 35, 27, 'Ke izinto zakhe ezokuqala nezokugqibela, nanzo zibhaliwe encwadini yookumkani bakwaSirayeli nabakwaYuda.', 'XHO22'),
(18, 36, 1, 'Ke kaloku abantu behlabathi bathabatha uYowakas, unyana kaYosiya, bamenza ukumkani eYerusalem esikhundleni sikayise.', 'XHO22'),
(18, 36, 2, 'uYehowahazi ubeminyaka imashumi mabini anamithathu ezelwe, ukuba ngukumkani kwakhe; waba neenyanga ezintathu engukumkani eYerusalem.', 'XHO22'),
(18, 36, 3, 'Ukumkani waseYiputa wahlala eYerusalem, waligweba ilizwe ikhulu leetalente zesilivere netalente enye yegolide.', 'XHO22'),
(18, 36, 4, 'Ukumkani waseYiputa wamenza uEliyakim, umkhuluwa wakhe, ukumkani wakwaYuda neYerusalem, waliguqula igama lakhe laba nguYehoyakim. uNego wamthabatha uYehowahazi, umninawa wakhe, wamsa eYiputa.', 'XHO22'),
(18, 36, 5, 'uYehoyakim ubeminyaka imashumi mabini anamihlanu ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamnye engukumkani eYerusalem; yaye wenza okubi emehlweni kaYehova uThixo wakhe.', 'XHO22'),
(18, 36, 6, 'uNebhukadenetsare ukumkani waseBhabheli wamxabela, wamkhonkxa ngamakhamandela, wamsa eBhabheli.', 'XHO22');
