INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(18, 36, 7, 'uNebhukadenetsare wazisa ezinye zeempahla zendlu kaYehova eBhabhiloni waza wazibeka etempileni yakhe eBhabhiloni.', 'XHO22'),
(18, 36, 8, 'Ezinye izinto zikaYehoyakim, namasikizi akhe awawenzayo, nokwafumanekayo kukuye, zibhaliwe encwadini yookumkani bakwaSirayeli nabakwaYuda; unyana wakhe uYehoyakin waqalisa ukulawula esikhundleni sakhe.', 'XHO22'),
(18, 36, 9, 'uYehoyakin ubeminyaka isibhozo ezelwe, ukuba ngukumkani kwakhe; waba neenyanga ezintathu ezineentsuku ezilishumi engukumkani eYerusalem; yaye wenza okubi emehlweni kaYehova.', 'XHO22'),
(18, 36, 10, 'Ekupheleni konyaka, uKumkani uNebhukadenetsare wathumela wamzisa eBhabhiloni neempahla ezilungileyo zendlu kaYehova, waza wenza uZedekiya umntakwabo kumkani wakwaYuda naseYerusalem.', 'XHO22'),
(18, 36, 11, 'uZedekiya ubeminyaka imashumi mabini anamnye ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamnye engukumkani eYerusalem.', 'XHO22'),
(18, 36, 12, 'Wenza okubi emehlweni kaYehova uThixo wakhe, yaye akazange azithobe phambi komprofeti uYeremiya, owayethetha ngokuphuma emlonyeni kaYehova.', 'XHO22'),
(18, 36, 13, 'Kananjalo wagwilika kukumkani, kuNebhukadenetsare, awayemfungele uThixo;', 'XHO22'),
(18, 36, 14, 'Ngaphezu koko, bonke ababingeleli abakhulu nabantu bonile kunene, ngokwamasikizi onke eentlanga; wayenza inqambi indlu kaYehova, awayingcwalisayo eYerusalem.', 'XHO22'),
(18, 36, 15, 'uYehova, uThixo wooyise, wathumela kubo ngabathunywa bakhe, wesuka wathumela; ngokuba wayenovelwano ngabantu bakhe nendlu yakhe;', 'XHO22'),
(18, 36, 16, 'Kodwa bayabagculela abathunywa bakaTHIXO oyinyaniso bewadelela amazwi akhe yaye benyelisa abaprofeti bakhe de ifike ingqumbo kaYehova nxamnye nabantu bakhe de kungabikho kuphiliswa.', 'XHO22'),
(18, 36, 17, 'Wabazisa ukumkani wamaKaledi, owawabulala amadodana abo ngekrele endlwini yengcwele yabo, akaconga ndodana nantombi, naxhego, nandoda ithe yaca phambi kwabo ubudala abayanga; wabanika bonke esandleni sakhe.', 'XHO22'),
(18, 36, 18, 'Zonke iimpahla zendlu yoThixo oyinyaniso, ezinkulu nezincinane, nobuncwane bendlu kaYehova, nobuncwane bokumkani, nobabathetheli bakhe; Zonke ezo zinto wazizisa eBhabheli.', 'XHO22'),
(18, 36, 19, 'Bayitshisa indlu yoThixo oyinyaniso, baludiliza udonga lwaseYerusalem, bawatshisa ngomlilo onke amabhotwe ayo, bazitshabalalisa zonke iimpahla zayo ezintle.', 'XHO22'),
(18, 36, 20, 'Abasindileyo ekreleni babathimba, babasa eBhabheli; apho baba ngabakhonzi bakhe noonyana bakhe, kwada kwaba sebukumkanini bobukumkani bamaPersi;', 'XHO22'),
(18, 36, 21, 'ukuze kuphume ilizwi likaYehova emlonyeni kaYeremiya, lizaliseke, lide ilizwe lanele ziisabatha zalo;', 'XHO22'),
(18, 36, 22, 'Kwathi ngomnyaka wokuqala kaKoreshi ukumkani wasePersi, ukuze lizaliseke ilizwi likaYehova, awalithethayo ngomlomo kaYeremiya, uYehova wawunyusa umoya kaKoreshi ukumkani wasePersi, ukuba uzaliseke ebukumkanini bakhe bonke, wabhala, esithi,', 'XHO22'),
(18, 36, 23, 'Kwathiwa nguKoreshi ukumkani wasePersi, Zonke izikumkani zehlabathi uzinikiwe nguYehova, uThixo wamazulu; wandiwisela umthetho wokuba ndimakhele indlu eYerusalem, ezweni lakwaYuda. Ngubani na kuni ongowabantu bakhe bonke? Wanga uYehova, uThixo wakhe, angaba naye, anyuke.', 'XHO22'),
(19, 1, 1, 'Ngomnyaka wokuqala kaKoreshi, ukumkani wasePersi, ekuzalisekeni kwelizwi likaYehova ngomlomo kaYeremiya, uYehova wawunyusa umoya kaKoreshi ukumkani wasePersi, ukuba atsho, ekumiselweni kwakhe bonke ubukumkani bakhe kwaye ngokubhala, esithi:', 'XHO22'),
(19, 1, 2, 'Wathi uKoreshi, ukumkani wasePersi, uYehova, uThixo wamazulu, undinike zonke izikumkani zehlabathi; wandiwisela umthetho wokuba ndimakhele indlu eYerusalem, ezweni lakwaYuda.', 'XHO22'),
(19, 1, 3, 'Ngubani na phakathi kwenu ebantwini bakhe bonke? Ngamana uThixo wakhe waba naye, anyuke aye eYerusalem ekwaYuda, ayakhe indlu kaYehova, uThixo kaSirayeli, eYerusalem.', 'XHO22'),
(19, 1, 4, 'Wonk’ ubani oseleyo kuyo nayiphi na indawo aphambukele kuyo njengomphambukeli, amadoda endawo yakhe makamncede ngesilivere negolide nempahla nemfuyo nomnikelo wokuzithandela wendlu kaThixo eYerusalem.', 'XHO22'),
(19, 1, 5, 'Zenyuka ke iintloko zezindlu zooyise zakwaYuda, nezakwaBhenjamin, nababingeleli, nabaLevi, nabo bonke abo uThixo wawuxhokonxayo umoya wabo, baya kuyakha indlu kaYehova eYerusalem.', 'XHO22'),
(19, 1, 6, 'Bonke ababebajikelezile bazomeleza izandla zabo ngeempahla zesilivere, nangegolide, nempahla, neenkomo, nangezinto ezinqwenelekayo, ngaphandle kwayo yonke into eyenziwa ngokuqhutywa yintliziyo.', 'XHO22'),
(19, 1, 7, 'Kananjalo ukumkani uKoreshi wazikhupha iimpahla zendlu kaYehova, awayezithabathe eYerusalem uNebhukadenetsare, wazibeka endlwini yothixo wakhe;', 'XHO22'),
(19, 1, 8, 'Aba ngabo awathi uKoreshi ukumkani wasePersi wabathabatha esandleni sikaMitredati umgcini wobuncwane waza wawafundela uSheshbhatsare inkosana yakwaYuda.', 'XHO22'),
(19, 1, 9, 'Lilo eli inani lazo: izitya zegolide ezimashumi mathathu, izitya zesilivere eziliwaka, nezitshetshe ezimashumi mabini anesithoba;', 'XHO22'),
(19, 1, 10, 'izitya zegolide ezimashumi mathathu, izitya zesilivere zohlobo lwesibini zaba ngamakhulu amane aneshumi, nezinye iimpahla eziliwaka.', 'XHO22'),
(19, 1, 11, 'Zonke iimpahla zegolide nezesilivere zaba ngamawaka amahlanu anamakhulu mane. Yonke le nto yabangela ukuba uSheshbhatsare enyuke nawo, aphume ekuthinjweni okwakuphakanyiswe eBhabhiloni, aye eYerusalem.', 'XHO22'),
(19, 2, 1, 'Ngabo aba oonyana bephondo abashiya abathinjwa, abathinjwa, abo uNebhukadenetsare ukumkani waseBhabhiloni wabathabathela eBhabhiloni waza wabuyela eYerusalem nakwaYuda, ngamnye kwisixeko sakowabo;', 'XHO22'),
(19, 2, 2, 'abeza noZerubhabheli: nguYeshuwa, noNehemiya, noSeraya, noRehelaya, noMordekayi, noBhilesan, noMitspar, noBhigwayi, noRehum, noBhahana. Inani loonyana bakaSirayeli:', 'XHO22'),
(19, 2, 3, 'oonyana bakaParoshe ingamawaka amabini, anekhulu elinamanci asixhenxe anababini;', 'XHO22'),
(19, 2, 4, 'oonyana bakaShefatiya babengamakhulu amathathu anamashumi asixhenxe anesibini;', 'XHO22'),
(19, 2, 5, 'oonyana baka-Aragi babengamakhulu asixhenxe anamashumi asixhenxe anesihlanu;', 'XHO22'),
(19, 2, 6, 'oonyana bakaPahati wakwaMowabhi, koonyana bakaYeshuwa noYowabhi, ibingamawaka amabini, anamakhulu asibhozo, aneshumi elinye elinababini.', 'XHO22'),
(19, 2, 7, 'Oonyana bakaElam babeliwaka elinamakhulu amabini anamashumi mahlanu anesine;', 'XHO22'),
(19, 2, 8, 'Oonyana bakaSathu ibingamakhulu asithoba, anamanci mane anabahlanu;', 'XHO22'),
(19, 2, 9, 'oonyana bakaSakayi ibingamakhulu asixhenxe, anamanci mathandathu;', 'XHO22'),
(19, 2, 10, 'oonyana bakaBhani ibingamakhulu amathandathu, anamanci mane anababini;', 'XHO22'),
(19, 2, 11, 'oonyana bakaBhebhayi ibingamakhulu amathandathu, anamanci mabini anabathathu;', 'XHO22'),
(19, 2, 12, 'oonyana baseAshgade ibiliwaka, elinamakhulu amabini, anamanci mabini anababini;', 'XHO22'),
(19, 2, 13, 'oonyana baka-Adonikam babengamakhulu amathandathu anamashumi mathandathu anesithandathu;', 'XHO22'),
(19, 2, 14, 'oonyana bakaBhigwayi babengamawaka amabini anamanci mahlanu anesithandathu;', 'XHO22'),
(19, 2, 15, 'oonyana baka-Adin babengamakhulu amane, anamanci mahlanu anabane;', 'XHO22'),
(19, 2, 16, 'Oonyana baka-Atere kaHezekiya ibingamashumi asithoba anamnye, anamanci asibhozo anababini;', 'XHO22'),
(19, 2, 17, 'oonyana bakaBheshayi babengamakhulu amathathu anamashumi amabini anesithathu;', 'XHO22'),
(19, 2, 18, 'oonyana bakaYora babelikhulu elineshumi elinesibini;', 'XHO22'),
(19, 2, 19, 'oonyana bakaHashum babengamakhulu amabini anamashumi amabini anesithathu;', 'XHO22'),
(19, 2, 20, 'oonyana baseGibhare ibingamashumi asithoba anabahlanu;', 'XHO22'),
(19, 2, 21, 'oonyana baseBhetelehem babelikhulu elinamashumi amabini anesithathu;', 'XHO22'),
(19, 2, 22, 'amadoda aseNetofi angamashumi amahlanu anesithandathu;', 'XHO22'),
(19, 2, 23, 'amadoda aseAnatoti ayelikhulu elinamashumi amabini anesibhozo;', 'XHO22'),
(19, 2, 24, 'Oonyana baseAsmawete babemashumi mane anababini;', 'XHO22'),
(19, 2, 25, 'oonyana baseKiriyati-yeharim neKefira neBheroti ibingamakhulu asixhenxe, anamanci mane anabathathu;', 'XHO22'),
(19, 2, 26, 'Oonyana baseRama neGabha ibingamakhulu amathandathu, anamanci mabini anamnye.', 'XHO22'),
(19, 2, 27, 'Amadoda aseMigmas ayelikhulu elinamashumi amabini anesibini;', 'XHO22'),
(19, 2, 28, 'amadoda aseBheteli neAyi ibingamakhulu amabini, anamanci mabini anamathathu;', 'XHO22'),
(19, 2, 29, 'Oonyana baseNebho ibingamashumi amahlanu anababini;', 'XHO22'),
(19, 2, 30, 'oonyana baseMagibhishe babelikhulu elinamashumi amahlanu anesithandathu;', 'XHO22'),
(19, 2, 31, 'Oonyana bomnye uElam ibiliwaka elinamakhulu amabini, anamanci mahlanu anabane.', 'XHO22'),
(19, 2, 32, 'Oonyana bakaHarim ibingamakhulu amathathu, anamanci mabini.', 'XHO22'),
(19, 2, 33, 'Oonyana baseLodi neHadide neOno ibingamakhulu asixhenxe, anamanci mabini anabahlanu;', 'XHO22'),
(19, 2, 34, 'oonyana baseYeriko ibingamakhulu amathathu, anamanci mane anabahlanu;', 'XHO22'),
(19, 2, 35, 'oonyana baseSenege ibingamawaka amathathu, anamakhulu mathandathu, anamanci mathathu;', 'XHO22'),
(19, 2, 36, 'Ababingeleli: oonyana bakaYedaya bendlu kaYeshuwa babengamakhulu asithoba anamashumi asixhenxe anesithathu.', 'XHO22'),
(19, 2, 37, 'Oonyana bakaImere ibiliwaka, elinamanci mahlanu anababini;', 'XHO22'),
(19, 2, 38, 'oonyana bakaPashure ibiliwaka, elinamakhulu amabini, anamanci mane anesixhenxe;', 'XHO22'),
(19, 2, 39, 'Oonyana bakaHarim iwaka elineshumi elinesixhenxe;', 'XHO22'),
(19, 2, 40, 'abaLevi: oonyana bakaYeshuwa noKademiyeli koonyana bakaHodawa babengamashumi asixhenxe anesine.', 'XHO22'),
(19, 2, 41, 'Iimvumi, oonyana baka-Asafu ibilikhulu, elinamanci mabini anesibhozo.', 'XHO22'),
(19, 2, 42, 'Oonyana babagcini-masango, oonyana bakaShalum, oonyana baka-Atere, oonyana bakaTalemon, oonyana baka-Akubhi, oonyana bakaHatita, oonyana bakaShobhayi, bebonke belikhulu elinashumi-nye.', 'XHO22'),
(19, 2, 43, 'Abakhonzi betempile: oonyana bakaSiha, oonyana bakaHasufa, oonyana bakaTaboti,', 'XHO22'),
(19, 2, 44, 'nabantwana bakwaKerose, nabantwana bakwaSiha, nabantwana bakwaPadon,', 'XHO22'),
(19, 2, 45, 'nabantwana bakwaLabhan, nabantwana bakwaHagabha, nabantwana bakwa-Akubhi,', 'XHO22'),
(19, 2, 46, 'nabantwana bakwaHagabhi, nabantwana bakwaSalimayi, nabantwana bakwaHanan,', 'XHO22'),
(19, 2, 47, 'noonyana bakaGidele, noonyana bakaGahare, noonyana bakaRehaya,', 'XHO22'),
(19, 2, 48, 'noonyana bakaRetsin, noonyana bakaNekoda, noonyana bakaGasam;', 'XHO22'),
(19, 2, 49, 'nabantwana bakwa-Usa, nabantwana bakwaPasaki, nabantwana bakwaBhesayi,', 'XHO22'),
(19, 2, 50, 'nabantwana bakwa-Asna, nabantwana bakwaMehuni, nabantwana bakwaNefushim,', 'XHO22'),
(19, 2, 51, 'nabantwana bakwaBhakebuke, nabantwana bakwaHakufa, nabantwana bakwaHarure,', 'XHO22'),
(19, 2, 52, 'nabantwana bakwaBhatseluti, nabantwana bakwaMehida, nabantwana bakwaHarsa,', 'XHO22'),
(19, 2, 53, 'nabantwana bakwaBharkose, nabantwana bakwaSisera, nabantwana bakwaTama,', 'XHO22'),
(19, 2, 54, 'oonyana bakaNesiya, oonyana bakaHatifa.', 'XHO22'),
(19, 2, 55, 'Oonyana babakhonzi bakaSolomon: oonyana bakaSotayi, oonyana bakaSofereti, oonyana bakaPeruda;', 'XHO22'),
(19, 2, 56, 'nabantwana bakwaYaela, nabantwana bakwaDarkon, nabantwana bakwaGidel,', 'XHO22'),
(19, 2, 57, 'nabantwana bakwaShefatiya, nabantwana bakwaHatili, nabantwana bakwaPogereti kaSebhayim, nabantwana bakwa-Ami.', 'XHO22'),
(19, 2, 58, 'Bonke abakhonzi betempile noonyana babakhonzi bakaSolomon babengamakhulu amathathu, anamanci asibhozo anamnye.', 'XHO22'),
(19, 2, 59, 'Ngabo abo abenyuka baya eTelemela, eTele-harsha, eKerubhi, eAdane naseImere, kodwa babengenakuyibonisa indlu kayise nenzala yabo ukuba babephuma kwaSirayeli:', 'XHO22'),
(19, 2, 60, 'oonyana bakaDelaya, oonyana bakaTobhiya, oonyana bakaNekoda, babengamakhulu omathandathu, anamanci mahlanu anababini;', 'XHO22'),
(19, 2, 61, 'Koonyana bababingeleli: oonyana bakaHabhaya, oonyana bakaKeshe, oonyana bakaBharzilayi; owazeka umfazi ezintombini zikaBharzilayi waseGiliyadi, wabizwa ngegama lazo.', 'XHO22'),
(19, 2, 62, 'Bakhangela ingxelo yabo phakathi kwabo babebalwe ngokwemilibo yokuzalwa, abayifumana; ke ngoko, benziwe inqambi, bagxothwa kububingeleli.', 'XHO22'),
(19, 2, 63, 'Ngoko umphathi wabayalela ukuba bangazidli ezona zinto zingcwele de kuvele umbingeleli oneUrim neTumim.', 'XHO22'),
(19, 2, 64, 'Lonke ibandla lingamawaka angamashumi amane anamabini, anamakhulu mathathu, anamanci mathandathu;', 'XHO22'),
(19, 2, 65, 'ngaphandle kwezicaka zabo ezi, nezicakakazi zabo ezi, zingamawaka asixhenxe, anamakhulu mathathu, anamanci mathathu anesixhenxe, aneemvumi neemvumi ezimakhulu mabini.', 'XHO22'),
(19, 2, 66, 'Amahashe abo ibingamakhulu asixhenxe, anamanci mathathu anesithandathu; oondlebende babo ibingamakhulu amabini, anamanci mane anabahlanu;', 'XHO22'),
(19, 2, 67, 'iinkamela zabo ibingamakhulu amane, anamanci mathathu anantlanu; amaesile ibingamawaka amathandathu, anamakhulu asixhenxe, anamanci mabini.', 'XHO22'),
(19, 2, 68, 'Ke inxenye yeentloko zezindlu zooyise zakwaSirayeli zabe zisendlwini kaYehova eYerusalem, zazibingelela imibingelelo endlwini kaThixo, ukuba ime endaweni yayo.', 'XHO22'),
(19, 2, 69, 'Banikela ebuncwaneni bomsebenzi igolide engamawaka amashumi amathandathu, nesilivere yangamawaka amahlanu eemina, nekhulu leengubo ezingaphantsi zababingeleli, ngokwamandla abo.', 'XHO22'),
(19, 2, 70, 'Ababingeleli, nabaLevi, nabanye abantu, neemvumi, nabamasango, namaNethini, bahlala emizini yakowabo, oonyana bakaSirayeli bonke emizini yabo.', 'XHO22'),
(19, 3, 1, 'Ithe yakufika inyanga yesixhenxe, oonyana bakaSirayeli besemizini yabo, bahlangana abantu eYerusalem njengamntu mnye.', 'XHO22'),
(19, 3, 2, 'Wesuka wema ooYeshuwa unyana kaYotsadaki, nabazalwana bakhe, ababingeleli, noZerubhabheli unyana kaSalatiyeli, nabazalwana bakhe, basakha isibingelelo soThixo kaSirayeli, ukuze benze amadini anyukayo phezu kwaso, njengoko wenzayo ebhalwe emthethweni kaYehova. uMoses umfo wakwaThixo.', 'XHO22'),
(19, 3, 3, 'Basibeka isibingelelo phezu kwesiseko saso; ngokuba bafikelwa kukunkwantya ngenxa yabemi baloo mazwe; yaye banikela iminikelo etshiswayo kuYehova phezu kwayo, iminikelo etshiswayo yakusasa nangokuhlwa.', 'XHO22'),
(19, 3, 4, 'Babuya benza umthendeleko weminquba, njengoko kubhaliweyo;', 'XHO22'),
(19, 3, 5, 'Emva koko wenza umnikelo otshiswayo othe rhoqo, wokuthwasa kwenyanga nowawo wonke amatheko angcwele kaYehova nawo wonke umntu obingelela ngokuzithandela kuYehova.', 'XHO22'),
(19, 3, 6, 'Baqala ngomhla wokuqala wenyanga yesixhenxe ukwenza amadini anyukayo kuYehova; Kodwa isiseko setempile kaYehova sasingekabekwa.', 'XHO22'),
(19, 3, 7, 'Imali bayinika abaqingqi bamatye nabachweli; nokudla nento eselwayo newayini kubantu baseTsidon nabaseTire, bathabathe imisedare eLebhanon, bayise elwandle lwaseYopa, ngokwesipho sikaKoreshi ukumkani wasePersi.', 'XHO22'),
(19, 3, 8, 'Ngonyaka wesibini wokungena kwabo endlwini yoTHIXO oyinyaniso eYerusalem, ngenyanga yesibini, uZerubhabheli unyana kaShalatiyeli noYeshuwa unyana kaYosadaki abanye abazalwana babo, ababingeleli nabaLevi baqala ke bonke abo babephume ekuthinjweni beza eYerusalem; yaye wamisela abaLevi ukususela kwabaneminyaka engamashumi amabini ubudala ukusa phezulu ukuba benze umsebenzi wendlu kaYehova.', 'XHO22'),
(19, 3, 9, 'Wema ke uYesu noonyana bakhe nabazalwana bakhe, uKademiyeli noonyana bakhe, oonyana bakaYuda, ukukhokela abasebenzi bendlu yoThixo oyinyaniso: oonyana bakaHenadadi noonyana babo nabazalwana babo, abaLevi.', 'XHO22'),
(19, 3, 10, 'Abakhi bayiseka itempile kaYehova, bamisa ababingeleli, bevathe iingubo ezinde, benamaxilongo; nabaLevi, oonyana baka-Asafu, bephethe amacangci, bedumisa uYehova, ngokomyalelo kaDavide ukumkani kwaSirayeli.', 'XHO22'),
(19, 3, 11, 'Bavuma kunye bezohlwaya, bezohlwaya kuYehova; ngokuba elungile, ngokuba ingunaphakade inceba yakhe kuSirayeli. Lwaduma ngodumo olukhulu lonke uhlanga, ekumdumiseni kwabo uYehova, ngenxa yokuba iziseko zendlu kaYehova zisekiwe.', 'XHO22'),
(19, 3, 12, 'Ke isininzi sababingeleli, nabaLevi, neentloko zezindlu zooyise, namadoda amakhulu, awayeyibonile indlu yokuqala ekusekweni kwale ndlu emehlweni abo, balila ngezwi elikhulu; yaye abaninzi baduma ngovuyo.', 'XHO22'),
(19, 3, 13, 'Apho abantu babengenakulihlula ilizwi lovuyo nokukhala kwabantu;', 'XHO22'),
(19, 4, 1, 'Zakuva iintshaba zakwaYuda nezakwaBhenjamin, ukuba bathinjwa bamakhele indlu uYehova, uThixo kaSirayeli.', 'XHO22'),
(19, 4, 2, 'Beza kuZerubhabheli nakwiintloko zezindlu zooyise, bathi kubo, Masakhe ndawonye nani, ngokuba simquqela uThixo wenu njengani; sibingelela kuye kususela kwimihla kaEsar-hadon, ukumkani waseAsiriya, owasizisa apha.', 'XHO22'),
(19, 4, 3, 'Wathi uZerubhabheli, noYoshuwa, neentloko zezindlu zooyise zakwaSirayeli kubo, Anithe nakazani nathi ekumakheleni uThixo wethu indlu; Thina siya kumakhela kunye uYehova, uThixo kaSirayeli, njengoko wasiwisela umthetho ngako uSireshi ukumkani wasePersi.', 'XHO22'),
(19, 4, 4, 'Baza abantu belizwe bazenza buthathaka izandla zoonyana bakaYuda baza babaphazamisa ngesakhiwo.', 'XHO22'),
(19, 4, 5, 'Yaye waqesha abantu abacebisa ukuba balitshitshise icebo labo, yonke imihla kaKoreshi ukumkani wasePersi, kwada kwezisa ebukumkanini bukaDariyo ukumkani wasePersi.', 'XHO22'),
(19, 4, 6, 'Ngexesha lika-Ahaswerosi, ekuqalekeni kobukumkani bakhe, babhala, bebancetheza abemi bakwaYuda nabaseYerusalem.', 'XHO22'),
(19, 4, 7, 'Nangemihla ka-Artashashta uBhislam, noMitredati, noTabhele, nabalingane babo, babhalela uArtashashta ukumkani wasePersi; incwadi leyo yayibhalwe ngesiAram, icaciswa ngesiAram.', 'XHO22'),
(19, 4, 8, 'uRehum umthetheli wenkundla, noShimshayi umbhali, babhala incwadi ngeYerusalem, kuArtashashta ukumkani.', 'XHO22'),
(19, 4, 9, 'Babhala ke ooRehum umthetheli, noShimshayi, umbhali, namakholwane abo; namaDina, namaPersi, namaTarpeli, namaPersi, namaArgi, namaBhabheli, namaShushan, namaHava, namaElam;', 'XHO22'),
(19, 4, 10, 'nezinye iintlanga, awazithimbayo uAsnapare omkhulu, obekekileyo, wazibeka emizini yakwaSamari, nakweminye nganeno koMlambo umEfrati, ngaloo mihla.', 'XHO22'),
(19, 4, 11, 'Yiyo le ikopi yencwadi abayithumela ku-Artashashta ukumkani; Abakhonzi bakho, amadoda aphesheya koMlambo, njalonjalo.', 'XHO22'),
(19, 4, 12, 'Wanga uYehova ayazi ukuba amaYuda anyukileyo evela kuni, eze kuthi eYerusalem, eza kwakha isixeko esigwilikayo, esikhohlakeleyo, azakha iindonga zaso, amisa iziseko.', 'XHO22'),
(19, 4, 13, 'Ke kaloku makayazi ukumkani ukuba, ukuba esi sixeko sakhiwa neendonga zabuya zakhiwa, aziyi kukhulula nayiphi na irhafu okanye irhafu, yaye oko kuya kubeka engozini imali yookumkani.', 'XHO22'),
(19, 4, 14, 'Ngoku ekubeni sinegunya lokuyinyamekela indlu yokumkani, singakubonanga nokungabi nambeko kokumkani, ngoko sithumele ukumqinisekisa ukumkani;', 'XHO22'),
(19, 4, 15, 'ukuze uphengululwe encwadini yemicimbi yooyihlo; niya kufumana encwadini yeZiganeko, nazi ukuba lo mzi ngumzi ozele lukreqo, uyingozi kookumkani bawo namazwe aso, nokuba bavuse imvukelo kuwo. Yini na ukuba lo mzi ubhubhe?', 'XHO22'),
(19, 4, 16, 'Sithi kukumkani, ukuba uthe wakhiwa lo mzi, zamiswa neendonga zawo, anisayi kuba nasabelo njengalo nganeno komlambo;', 'XHO22'),
(19, 4, 17, 'Ukumkani wathumela impendulo kuRehum umthetheli, noShimshayi umbhali, namakholwane abo abehleli kwaSamari, nabanye phesheya koMlambo, wathi, Luxolo lwenu lwenze njalo.', 'XHO22'),
(19, 4, 18, 'Ileta leyo nisithumele yona yacaca phambi kwam.', 'XHO22'),
(19, 4, 19, 'Ndakhupha umyalelo, kwaza kwaphandwa, kwafunyanwa ukuba esi sixeko sidala saba nemvukelo nxamnye nookumkani, yaye kukho imvukelo nemvukelo kuso.', 'XHO22'),
(19, 4, 20, 'Kananjalo eYerusalem bekukho ookumkani abanamandla, nabanegunya kuwo onke amazwe aphesheya koMlambo; kwaye iirhafu, iirhafu kunye neerhafu zihlawulwa kubo.', 'XHO22'),
(19, 4, 21, 'Yazisani ke ngoko, niwamise la madoda, ukuze singakhiwa esi sixeko, ndide ndikhuphe omnye umyalelo ophuma kum.', 'XHO22'),
(19, 4, 22, 'Zilindeni ngoku, hleze nikwenze oko;', 'XHO22'),
(19, 4, 23, 'Ithe yakuleswa imibhalo yencwadi ka-Artashashta ukumkani phambi koRehum noShimshayi umbhali nabalingane babo, benyuka ngokukhawuleza baya eYerusalem kumaYuda, bawamisa ngenkani nangamandla.', 'XHO22'),
(19, 4, 24, 'Waza wamiswa umsebenzi wendlu kaThixo eYerusalem. Yaqhuba ke yangunyaka wesibini wolawulo lukaDariyo ukumkani wasePersi.', 'XHO22'),
(19, 5, 1, 'Ke kaloku abaprofeti, ooHagayi umprofeti, noZekariya unyana kaIdo, baprofeta kumaYuda abekwaYuda neYerusalem, egameni likaThixo kaSirayeli.', 'XHO22'),
(19, 5, 2, 'Ngoko uZerubhabheli unyana kaShalatiyeli noYeshuwa unyana kaYotsadaki baphakama baza baqalisa ukwakha indlu kaThixo eYerusalem, yaye baba nabaprofeti bakaThixo ababebanceda.', 'XHO22'),
(19, 5, 3, 'Ngelo xesha uTatenayi, irhuluneli engasentshona yoMlambo, noSetarbhosnayi namaqabane abo beza kubo baza bathi kubo: “Ngubani na oniyaleleyo ukuba nakhe le ndlu nize nakhe olo donga?', 'XHO22'),
(19, 5, 4, 'Sathi kubo, Ngoobani na amagama amadoda ayakhayo le ndlu?', 'XHO22'),
(19, 5, 5, 'Ke iliso loThixo wabo libe liphezu kwezikhulu zamaYuda, ababa nako ukubathintela, ide loo nto iye kufikelela kuDariyo; Baphendula ke ngokubhala ngayo loo nto.', 'XHO22'),
(19, 5, 6, 'Incwadi leyo, ooTatenayi, ibamba langaneno koMlambo, noShetar-bhosnayi, nabalingane bakhe babaFarisi abaphesheya koMlambo, bayithumelayo kuDariyo ukumkani;', 'XHO22'),
(19, 5, 7, 'Bathumela kuye incwadi leyo; KuDariyo ukumkani, uxolo luphelele!', 'XHO22'),
(19, 5, 8, 'Ngamana iNkosi yazi ukuba sangena kummandla wakwaYuda, endlwini kaThixo omkhulu, eyakhiwe ngamatye amakhulu, yaxhonywa iindonga ezindongeni, yaye lo msebenzi uqhubeka ngokukhawuleza, yaye usaqhubeka abanempumelelo ezandleni zabo.', 'XHO22'),
(19, 5, 9, 'Ngoko sabuza kumadoda amakhulu saza sathi kuwo: “Ngubani na oniyalele ukuba niyakhe le ndlu nize nakhe ezi ndonga?', 'XHO22'),
(19, 5, 10, 'Kananjalo sacela ukuba siqiniselwe namagama awo, ukuze siwabhale phantsi amagama amadoda aziintloko zawo;', 'XHO22'),
(19, 5, 11, 'Basiphendula, bathi, Singabakhonzi bakaThixo wezulu nomhlaba; sakha indlu eyayakhiwe kwiminyaka emininzi eyadlulayo, awayakhayo ukumkani omkhulu wakwaSirayeli.', 'XHO22'),
(19, 5, 12, 'Kodwa emva kokuba oobawo bamqumbisayo uTHIXO wamazulu, babanikela esandleni sikaNebhukadenetsare ukumkani waseKhaledi ukumkani waseBhabhiloni, owayitshabalalisayo le ndlu waza wathimbela abantu eBhabhiloni.', 'XHO22'),
(19, 5, 13, 'Ke ngomnyaka wokuqala kaKoreshi ukumkani waseBhabheli, ukumkani uKoreshi wathi, mayakhiwe le ndlu kaThixo;', 'XHO22'),
(19, 5, 14, 'Neempahla zendlu kaThixo zegolide nezesilivere, awazithabathayo uNebhukadenetsare etempileni eseYerusalem, nasetempileni yaseBhabheli, ukumkani uKoreshi wazithabatha etempileni yaseBhabheli; lalikho negama lokumkani. uSheshbhatsare, awamenza irhuluneli;', 'XHO22'),
(19, 5, 15, 'Wathi kuye, Thabatha ezi mpahla, uye nazo etempileni eseYerusalem, indlu kaThixo yakhelwe endaweni yayo.', 'XHO22'),
(19, 5, 16, 'Waza weza lo Sheshbhatsare, wabeka isiseko sendlu kaThixo eYerusalem;', 'XHO22'),
(19, 5, 17, 'Ke kaloku, ukuba kulungile kukumkani, makuphengululwe endlwini yobuncwane bokumkani eBhabhiloni, enoba ngumbandela kaKoreshi kusini na ukumkani wokunikela umyalelo wokuba yakhiwe le ndlu kaThixo eYerusalem, ukumkani makasithumele ukuthanda kwakhe kulo mbandela.', 'XHO22'),
(19, 6, 1, 'Waza uDariyo ukumkani wawisa umthetho; kwaphicothwa endlwini khona eBhabheli.', 'XHO22'),
(19, 6, 2, 'Kwaye kwafunyanwa incwadi esongwayo eAkemeta, kwibhotwe, kummandla wamaMedi, yaye kuyo kwakukho incwadi ebhalwe ngolu hlobo.', 'XHO22'),
(19, 6, 3, 'Ngonyaka wokuqala kaKoreshi ukumkani, ukumkani uKoreshi wakhupha umyalelo ngokuphathelele indlu yoThixo oyinyaniso eYerusalem: “Imele imiselwe ngokuqinileyo; Ukuphakama kwayo bekuziikubhite ezimashumi mathandathu, nobubanzi bayo bebuziikubhite ezimashumi mathandathu;', 'XHO22'),
(19, 6, 4, 'Yibani neengcamba ezintathu zamatye amakhulu, nocamba lwemithi emitsha; kwaye iindleko zifanele ziphume endlwini yokumkani.', 'XHO22'),
(19, 6, 5, 'Kananjalo iimpahla zendlu kaThixo zegolide nezesilivere, awazithabathayo uNebhukadenetsare etempileni eYerusalem, wazisa eBhabheli, zibuyisiwe zabuyiselwa etempileni eseYerusalem, iyileyo endaweni yaso, nendawo yaso endlwini kaThixo.', 'XHO22'),
(19, 6, 6, 'Ke ngoko, Tatenayi, nkosi nganeno koMlambo, nawe Setar-bhosnayi, namahlakani akho aseFarsi phesheya koMlambo, hlukanani nalapho;', 'XHO22'),
(19, 6, 7, 'wushiyeni umsebenzi wale ndlu kaThixo; irhuluneli yamaYuda namadoda amakhulu amaYuda mayiyakhe le ndlu kaThixo endaweni yayo.', 'XHO22'),
(19, 6, 8, 'Kananjalo ndikuwisela umthetho, wenjenje kumadoda amakhulu amaYuda, ukwakha le ndlu kaThixo: nasebutyebini bokumkani, nasesichumeni nganeno koMlambo, indleko zirhole kwangoko kube kanye kula madoda ngoko abangeni endleleni.', 'XHO22'),
(19, 6, 9, 'Kuya kuba yimihla yabo, iinkunzi ezintsha zeenkomo, neenkunzi zeegusha, neemvana, zamadini anyukayo oThixo wezulu, ingqolowa, netyuwa, newayini, neoli, ngokomyalelo wababingeleli baseYerusalem; ngosuku olunikiweyo ngaphandle kokulibazisa;', 'XHO22'),
(19, 6, 10, 'ukuze benze idini elivumba limnandi kuThixo wamazulu, bathandazele ubomi bokumkani noonyana bakhe.', 'XHO22'),
(19, 6, 11, 'Kananjalo ndawisa umthetho wokuba nabani na othe waliguqula elo lizwi, makagawule umthi endlwini yakhe, awuxhome, axhonywe kuwo; nendlu yakhe mayenziwe udaka ngenxa yayo.', 'XHO22'),
(19, 6, 12, 'uThixo obeke igama lakhe apho uya kubatshabalalisa bonke ookumkani neentlanga eziya kuthabathekela kubo ukutshintsha nokutshabalalisa le ndlu kaThixo eYerusalem. Mna, Dariyo, ndikhuphe umyalelo; makwenziwe ngokukhawuleza.', 'XHO22'),
(19, 6, 13, 'Waza uTatenayi irhuluneli engaphaya koMlambo, uSetarbhosnayi namaqabane abo, benza ngokukhawuleza ngokomyalelo kaDariyo ukumkani.', 'XHO22'),
(19, 6, 14, 'Amadoda amakhulu amaYuda ayesakha yaye aqhubeka ephumelela ngomprofeti uHagayi noZekariya unyana kaIdo. Bakha baza bagqiba ngokomyalelo kaTHIXO kaSirayeli nangokomyalelo kaKoreshi noDariyo noArtashashta ukumkani wasePersi.', 'XHO22'),
(19, 6, 15, 'Le ndlu yagqitywa ngomhla wesithathu wenyanga enguAdare, oko kukuthi, ngonyaka wesithandathu wolawulo lukaDariyo ukumkani.', 'XHO22'),
(19, 6, 16, 'Oonyana bakaSirayeli, ababingeleli, nabaLevi, nabathinjwa, bavula loo ndlu kaThixo bevuya;', 'XHO22'),
(19, 6, 17, 'Ekungcwalisweni kwale ndlu kaThixo, ikhulu leenkunzi ezintsha zeenkomo, namakhulu amabini eenkunzi zeegusha, namakhulu amane eemvana; zalishumi elinazibini izikhova, zedini lesono, ngenxa yamaSirayeli onke, ngokwenani lezizwe zakwaSirayeli.', 'XHO22'),
(19, 6, 18, 'Babamisa ababingeleli ngokwamaqela abo, nabaLevi ngokwamaqela abo, ukuba bakhonze uYehova eYerusalem; njengoko kubhaliweyo encwadini kaMoses.', 'XHO22'),
(19, 6, 19, 'Oonyana basemfudusweni bayenza ipasika ngomhla weshumi elinesine wenyanga yokuqala;', 'XHO22'),
(19, 6, 20, 'Bahlanjululwa ababingeleli nabaLevi ngandawonye, bahlambuluka bonke bephela, bawabingelela amabanjwa onke nabazalwana babo nababingeleli ngenxa yabo ngepasika.', 'XHO22'),
(19, 6, 21, 'Badla ke oonyana bakaSirayeli ababebuyile ekuthinjweni nabo bonke ababezahlule kubunyhukunyhuku beentlanga zehlabathi besiya kufuna uYehova uThixo kaSirayeli;', 'XHO22'),
(19, 6, 22, 'Benza umthendeleko wezonka ezingenagwele imihla yasixhenxe bevuyile, ngokuba uYehova ebebavuyisile, wabanika intliziyo yokumkani waseAsiriya, ukuze izandla zabo zomelezwe emsebenzini wendlu kaThixo kaSirayeli, uThixo kaSirayeli.', 'XHO22'),
(19, 7, 1, 'Emveni koko, ebukumkanini buka-Artashashta ukumkani wasePersi, uEzra, unyana kaSeraya, unyana ka-Azariya, unyana kaHilekiya,', 'XHO22'),
(19, 7, 2, 'unyana kaShalum, unyana kaTsadoki, unyana ka-Ahitubhi,', 'XHO22'),
(19, 7, 3, 'unyana ka-Amariya, unyana ka-Azariya, unyana kaMerayoti,', 'XHO22'),
(19, 7, 4, 'unyana kaSeraya, unyana kaUzi, unyana kaBhuki,', 'XHO22'),
(19, 7, 5, 'unyana ka-Abhishuwa, unyana kaPinehasi, unyana kaElazare, unyana ka-Aron, umbingeleli omkhulu;', 'XHO22'),
(19, 7, 6, 'Wenyuka lo uEzra, evela eBhabheli; wayengoyena mbhali ubalaseleyo emyalelweni kaMoses, awawuwisayo uYehova, uThixo kaSirayeli;', 'XHO22'),
(19, 7, 7, 'Bambi boonyana bakaSirayeli, nababingeleli, nabaLevi, neemvumi, nabamasango, nabakhonzi betempile, benyuka baya eYerusalem ngomnyaka wesixhenxe ka-Artashashta ukumkani.', 'XHO22'),
(19, 7, 8, 'Wafika eYerusalem ngenyanga yesihlanu, ngonyaka wesixhenxe wokumkani.', 'XHO22'),
(19, 7, 9, 'Kuba ngomhla wokuqala wenyanga yokuqala wenyuka ephuma eBhabhiloni, yaye ngomhla wokuqala wenyanga yesihlanu weza eYerusalem, ngokwesandla esilungileyo sikaThixo wakhe esasiphezu kwakhe.', 'XHO22'),
(19, 7, 10, 'uEzra wayezimisele ukufuna umthetho kaYehova nokuwuzalisekisa nokufundisa imithetho nezigwebo kwaSirayeli.', 'XHO22'),
(19, 7, 11, 'Nguwo lo umbhalo wencwadi leyo uKumkani uArtashashta wayinika uEzra, umbingeleli, unobhala, unobhala wamazwi emiyalelo kaYehova nemiyalelo yakhe eyamiselwa kwaSirayeli.', 'XHO22'),
(19, 7, 12, 'uArtashashta, ukumkani wookumkani, kuEzra umbingeleli, umphathi kaThixo wamazulu, uxolo olupheleleyo, ngelo xesha.', 'XHO22'),
(19, 7, 13, 'Ndikhupha umyalelo wokuba bonke oonyana bakaSirayeli nababingeleli babo nabaLevi ebukumkanini bam, abo ngokuzithandela kwabo banqwenela ukunyuka baye eYerusalem, mabahambe nawe.', 'XHO22'),
(19, 7, 14, 'Wena uthunywe ngukumkani namaphakathi akhe abasixhenxe, ukuba uye kuwahlola amaYuda neYerusalem, ngokomthetho kaThixo wakho osesandleni sakho;', 'XHO22'),
(19, 7, 15, 'bathabathe isilivere negolide, abayinika ukumkani namaphakathi akhe, ngokuqhutywa yintliziyo, bayinike uThixo kaSirayeli, ondlu yakhe iseYerusalem;', 'XHO22'),
(19, 7, 16, 'nayo yonke isilivere negolide, oyifumanayo ezweni lonke laseBhabheli, kwaneminikelo yokuqhutywa yintliziyo yabantu, neyababingeleli, abaqhutywa yintliziyo kumbingelelo wendlu kaThixo wabo eYerusalem;', 'XHO22'),
(19, 7, 17, 'ukuze nikhawuleze nithenge iinkunzi zeenkomo neenkunzi zeegusha neemvana neminikelo yazo yasemlilweni neminikelo yazo ethululwayo ngale mali nize niyinikele esibingelelweni sendlu kaThixo wenu eYerusalem.', 'XHO22'),
(19, 7, 18, 'Nantoni na esukuba ilungile kuwe nabazalwana bakho noyenza ngesilivere nangegolide eseleyo, niyenze ngokokuthanda kukaThixo wenu.', 'XHO22'),
(19, 7, 19, 'Ke iimpahla ozinikiweyo zenkonzo yendlu yoThixo wakho, uzigcine phambi koThixo waseYerusalem;', 'XHO22'),
(19, 7, 20, 'Ke yonke into efunekayo yendlu kaThixo wakho, osukuba unethuba lokuyinika, yikhuphe ebuncwaneni bokumkani;', 'XHO22'),
(19, 7, 21, 'Kananjalo, mna, kumkani Artashashta, ndibawisela umthetho bonke abagcini-ndyebo abaphesheya koMlambo, ukuba konke akubizayo uEzra, umbingeleli, owazi ngomthetho kaThixo wamazulu, kukwenze kamsinya;', 'XHO22'),
(19, 7, 22, 'ukusa kwikhulu leetalente zesilivere noku ukusa kwikhulu leeshekele zengqolowa ukusa kwikhulu leengqayi zewayini nokusa kwikhulu leengqayi zeoli netyuwa kungaxelwa ukuba ingakanani na.', 'XHO22'),
(19, 7, 23, 'Yonke into engomthetho kaThixo wamazulu mayenzelwe indlu kaThixo wamazulu ngenyameko; kuba kungani na ukuba bufikelwe buburhalarhume ubukumkani bokumkani noonyana bakhe?', 'XHO22'),
(19, 7, 24, 'Kananjalo ndithi kuni, akuvumelekile ukuba kuhlawulwe irhafu, nokuba kuthatyathwe, nokuba kuthatyathwe, nokuba kulunge kubabingeleli, nabaLevi, neemvumi, nabamasango, nabakhonzi betempile, nabakhonzi bale ndlu kaThixo.', 'XHO22'),
(19, 7, 25, 'Wena ke, Ezra, ngokobulumko bukaThixo wakho obusesandleni sakho, zikhethele abagwebi nabagwebi abanokugweba bonke abantu abaphesheya koMlambo, bonke abayaziyo imithetho kaThixo wakho; ufundisa abangaziyo.', 'XHO22'),
(19, 7, 26, 'Ke bonke abasukuba bengawenzi umthetho kaThixo wakho, nomthetho wokumkani, maliwise isigwebo phambi kwakhe, nokuba sikukufa, nokuba kugxothwa, nokuba kukuhluthwa impahla, nokuba sekusentolongweni.', 'XHO22'),
(19, 7, 27, 'Makabongwe uYehova, uThixo woobawo, oyinikeleyo loo nto entliziyweni yokumkani, ukuba ayihombise indlu kaYehova eYerusalem.', 'XHO22'),
(19, 7, 28, 'Wandenzela inceba phambi kokumkani, namaphakathi akhe, naphambi kwabathetheli bonke bokumkani abangamagorha. Ndomelela njengoko sasiphezu kwam isandla sikaYehova uThixo wam, ndazihlanganisa izikhulu zakwaSirayeli, ukuba zinyuke nam.', 'XHO22'),
(19, 8, 1, 'Zizo ezi iintloko zezindlu zooyise, nemizalwane yabo benyuka nam baya eBhabheli, ebukumkanini buka-Artashashta ukumkani:', 'XHO22'),
(19, 8, 2, 'koonyana bakaPinehasi yanguAsheriyeli; koonyana bakaItamare; koonyana bakaDavide; Hattush;', 'XHO22'),
(19, 8, 3, 'koonyana bakaSeganja, koonyana bakaFaro; uZakariya wayenekhulu elinamashumi amahlanu amadoda abhalisiweyo kwintsapho yakhe;', 'XHO22'),
(19, 8, 4, 'koonyana bakaPahati-mowabhi; uElihunayi unyana kaSeraya ubenamadoda angamakhulu amabini;', 'XHO22'),
(19, 8, 5, 'koonyana bakaSeganja; unyana kaYagasiyeli enamadoda angamakhulu amathathu;', 'XHO22'),
(19, 8, 6, 'nakoonyana baka-Adin; uEbhedi, unyana kaYonatan, ubenamadoda amashumi mahlanu;', 'XHO22'),
(19, 8, 7, 'koonyana bakaElam; uIsaya unyana ka-Ataliya ubenamashumi asixhenxe amadoda;', 'XHO22'),
(19, 8, 8, 'koonyana bakaShefatiya; uZebhadiya unyana kaMikayeli ubenamashumi asibhozo anamabini amadoda;', 'XHO22'),
(19, 8, 9, 'koonyana bakaYowabhi; nguObhadiya unyana kaYehiyeli, enamadoda angamakhulu amabini aneshumi elinesibhozo;', 'XHO22'),
(19, 8, 10, 'koonyana bakaShelomiti; Unyana kaYosefu ubenamadoda alikhulu elinamanci mathandathu;', 'XHO22'),
(19, 8, 11, 'koonyana bakaBhebhayi; uZekariya unyana kaBhebhayi ubenamadoda amashumi mabini anesibhozo;', 'XHO22'),
(19, 8, 12, 'koonyana baseAshgade; uYohanan unyana kaHakatan ubenamadoda alikhulu elinashumi-nye;', 'XHO22'),
(19, 8, 13, 'koonyana bokugqibela baka-Adonikam abamagama angala, nguElifelete, noYehiyeli, noShemaya, benamashumi amathandathu amadoda;', 'XHO22'),
(19, 8, 14, 'nakoonyana bakaBhigwayi; Utai kunye noSabude, namadoda angamashumi asixhenxe.', 'XHO22'),
(19, 8, 15, 'Ndababutha emlanjeni ongena eAhava; sahlala khona iintsuku zantathu; Ndabakhangela abantu nababingeleli, ndafika kungekho namnye woonyana bakaLevi.', 'XHO22'),
(19, 8, 16, 'Ndathuma ooEliyezere, noAriyeli, noShemaya, noElinatan, noYaribhi, noElinatan, noNatan, noZekariya, noMeshulam, abaziintloko; noYoyaribhi noElinatan, amadoda aziingqondi.', 'XHO22'),
(19, 8, 17, 'Ndabathumela ngokomyalelo kuIdo, umphathi wendlu kaSifiya, ndabaxelela into abaya kuyithetha kuIdo, nakubazalwana bakhe, amaBhetelehim, endlwini kaKasifiya, ukuba abakhonzi babo bangabendlu yethu ukuzisa ekhaya kuthi. uThixo.', 'XHO22'),
(19, 8, 18, 'Ngesandla esilungileyo sikaThixo wethu esasiphezu kwethu, basizisela indoda enengqiqo evela koonyana bakaMagali unyana kaLevi, unyana kaSirayeli; noSherebhiya noonyana bakhe nabazalwana bakhe, ishumi elinesibhozo;', 'XHO22'),
(19, 8, 19, 'uHashabhiya wayekunye noIsaya koonyana bakaMerari, abantakwabo noonyana babo abangamashumi amabini;', 'XHO22'),
(19, 8, 20, 'nakubakhonzi betempile, abathe uDavide neenkosana bababeka enkonzweni yabaLevi, abakhonzi betempile abangamakhulu amabini anamashumi amabini; wonke umntu uchazwa ngamagama.', 'XHO22'),
(19, 8, 21, 'Ke kaloku, ngakumlambo iAhawa, ndavakalisa umhla wokuzila ukudla, ukuze sizithobe phambi koThixo wethu, sizifunele indlela yobulungisa, thina noonyana bethu, nobutyebi bethu bonke.', 'XHO22'),
(19, 8, 22, 'Kuba ndandineentloni ukucela kukumkani iqela lamajoni nabakhweli-mahashe ukuba basincede silwe neentshaba endleleni, ngenxa yokuba sathetha nokumkani sathi: “Isandla soThixo wethu silungile kubo bonke abo bamfunayo ke amandla akhe nomsindo wakhe uphezu kwabo bonke ababuyayo kuye.', 'XHO22'),
(19, 8, 23, 'Ngoko sazila ukutya saza sambongoza uThixo wethu ngenxa yoku, waza wasibongoza.', 'XHO22'),
(19, 8, 24, 'Ndabanyula ababingeleli abakhulu abalishumi elinababini: ooSherebhiya, noHashabhiya, nabazalwana babo abalishumi;', 'XHO22'),
(19, 8, 25, 'Wababela isilivere, negolide, nempahla, engumnikelo wendlu kaThixo wethu, lowo ukumkani nabacebisi bakhe nabalungiseleli bakhe namaSirayeli onke abewazisileyo khona.', 'XHO22'),
(19, 8, 26, 'Ndalinganisa nazo amakhulu amathandathu anamanci mahlanu eetalente zesilivere, nekhulu leempahla zesilivere, nekhulu leetalente zegolide;', 'XHO22'),
(19, 8, 27, 'namashumi amabini eendebe zegolide ziliwaka leedrarhima; neempahla ezimbini zobhedu olubengezela, zinqweneleka njengegolide.', 'XHO22'),
(19, 8, 28, 'Ndathi kubo, Ningcwele kuYehova; izinto nazo zingcwele; nesilivere negolide zingumnikelo wabaqhutywa yintliziyo kuYehova, uThixo wooyihlo.', 'XHO22'),
(19, 8, 29, 'Uyigcine, uyigcine, ude uyibeke phambi kwababingeleli abakhulu, nabaLevi, neentloko zezindlu zooyise zoonyana bakaSirayeli, eYerusalem, emagumbini endlu kaYehova.', 'XHO22'),
(19, 8, 30, 'Ababingeleli nabaLevi bawathabatha ke umlinganiso wesilivere negolide nempahla, ukuba bawuzise eYerusalem endlwini yoThixo wethu.', 'XHO22'),
(19, 8, 31, 'Ngomhla weshumi elinesibini wenyanga yokuqala sawushiya umlambo iAhava, saya eYerusalem; kodwa isandla soThixo wethu saba phezu kwethu, wasihlangula esandleni seentshaba nakwabasilindileyo.', 'XHO22'),
(19, 8, 32, 'Safika ke eYerusalem, sahlala khona iintsuku zantathu.', 'XHO22'),
(19, 8, 33, 'Ngosuku lwesine balinganisa ubunzima besilivere, negolide, neempahla zendlu yoThixo wethu, ngesandla sikaMeremoti unyana kaUriya, umbingeleli; WayenoElazare unyana kaPinehasi; kunye noYozabhadi unyana kaYeshuwa, noNowa unyana kaBhinuwi, abaLevi;', 'XHO22'),
(19, 8, 34, 'Ngenani nangobunzima bayo nganye nganye, yaye bonke ubunzima babhalwa phantsi ngelo xesha.', 'XHO22'),
(19, 8, 35, 'Oonyana basemfudusweni basondeza amadini anyukayo kuThixo kaSirayeli: iinkunzi ezintsha zeenkomo zalishumi elinambini, ngenxa yamaSirayeli onke, neenkunzi zezimvu zaba ngamashumi asithoba anantandathu, neemvana zamashumi asixhenxe anesixhenxe, zalishumi elinambini izikhova zesono.', 'XHO22'),
(19, 8, 36, 'Bayinikela imiyalelo kakumkani kubalungiseleli bakakumkani nakwirhuluneli engasempuma yoMlambo umEfrate, yaye baxhasa uhlanga nendlu yoThixo oyinyaniso.', 'XHO22'),
(19, 9, 1, 'Kwathi, kwakusenzeka ezo zinto, beza kum izikhulu, bathi, Oonyana bakaSirayeli, nababingeleli, nabaLevi, abazahlulanga kubantu baloo mazwe, kumaKanan, namaHeti, ukuba benze amasikizi abo namaPersi, namaYebhusi, nama-Amon, namaMowabhi, namaYiputa, nama-Amori.', 'XHO22'),
(19, 9, 2, 'Bazizeka ezintombini zabo, bazizekela bona noonyana babo, yaza yaxubana imbewu engcwele neentlanga zaloo mazwe; ewe, isandla seenkosana neenkosana ibiyeyona inkulu kolu lwaphulo-mthetho.', 'XHO22'),
(19, 9, 3, 'Ndithe ndakuva oko, ndazikrazula iingubo zam nengubo yam, ndazihluthula iinwele zam neendevu zam, ndahlala phantsi ndimangalisiwe.', 'XHO22'),
(19, 9, 4, 'Babuthelana kum bonke begubha ngamazwi oThixo kaSirayeli, ngenxa yobubi bomhambi; ndahlala ndimangalisiwe, kwada kwafika umbingelelo wangokuhlwa.', 'XHO22'),
(19, 9, 5, 'Ndema phambi kombingelelo wangokuhlwa; ndakrazula isambatho sam nesambatho sam, ndaguqa ngamadolo, ndazolulela izandla zam kuYehova uThixo wam,', 'XHO22'),
(19, 9, 6, 'Ndathi, Camagu, Thixo wam, ndidanile, ndibuhlungu, ndakubuphakamisela kuwe ubuso bam, Thixo wam;', 'XHO22'),
(19, 9, 7, 'Kususela kwimihla yoobawo, sisetyaleni elikhulu unanamhla; ngenxa yobugwenxa bethu sanikelwa thina, nookumkani bethu, nababingeleli bethu, esandleni sookumkani bala mazwe, umkhonto, nokuthinjwa, nokuphangwa, nokucukucezwa kobuso bethu, njengoko kunjalo namhla.', 'XHO22'),
(19, 9, 8, 'Kaloku, ngoku, uYehova uThixo wethu, okwethutyana, uya kusihawukela ngokusishiyela abasindileyo, wasishiyela isikhonkwane engcweleni yakhe, ukuze uThixo wethu awakhanyisele amehlo ethu, awaphumze.', 'XHO22'),
(19, 9, 9, 'Kuba besingamakhoboka; Noko ke, uThixo wethu akasishiyanga ebukhobokeni bethu; wesuka wasenzela inceba phambi kookumkani bamaPersi, wasinika umphefumlo, ukuba siyakhe indlu yoThixo wethu, siwalungise amanxuwa ayo, asinikele udonga lokungqinga ilizwe lakwaYuda, iYerusalem.', 'XHO22'),
(19, 9, 10, 'Kaloku, Thixo wethu, sothini na emveni koko? ngokuba siyishiyile imithetho yakho;', 'XHO22'),
(19, 9, 11, 'owawisa umthetho ngawo ngabakhonzi bakho, abaprofeti, wathi, Ilizwe eniya kulidla ilifa lilizwe elingcolileyo, lizele amanqambi abantu baloo mazwe, namasikizi abo, athabathele kwelinye icala ase ekupheleni ngokungcola kwabo.', 'XHO22'),
(19, 9, 12, 'Ke ngoko iintombi zenu musani ukuzendisela koonyana bazo, neentombi zabo ningazithabatheli koonyana benu, ningalufuni uxolo lwazo nokuba yinzuzo kubo; abantwana ngonaphakade.', 'XHO22'),
(19, 9, 13, 'Yaye emva kwako konke okusihleleyo ngenxa yezenzo zethu ezimbi nangenxa yezikreqo zethu ezikhulu, kungenxa yakho, Thixo wethu, othe wasibetha kancinane kunezono zethu ngokufanelekileyo, waza wasinika olu hlobo losindiso.', 'XHO22'),
(19, 9, 14, 'Siya kubuya siyihlambe na imithetho yakho, sinxulumane naba bantu bacekisekayo? Akuyi kuba noburhalarhume na kuthi ude usitshabalalise, kungabikho masalela nasindileyo?', 'XHO22'),
(19, 9, 15, 'Yehova, Thixo kaSirayeli, ulilungisa, ngokuba sisesindile, njengoko kunjalo namhla; yabona, sisecaleni kwakho ebugwenxeni bethu, ngenxa yoko asinakuma phambi kwakho.', 'XHO22'),
(19, 10, 1, 'Ke kaloku akuthandaza uEzra, akukuxela oku, walila, equbudile phambi kwendlu kaThixo, kwabuthelana kuye ibandla elikhulu kakhulu lamadoda, nabafazi, nabantwana, bevela kwaSirayeli; balila ngokukrakra abantu.', 'XHO22'),
(19, 10, 2, 'Waphendula uSheganiya unyana kaYehiyeli, owoonyana bakaElam, wathi kuEzra, Sonile kuThixo wethu, sazeka abafazi bezinye iintlanga ezizweni zeli hlabathi; Noko ke, kukho ithemba kwaSirayeli ngokuphathelele lo mbandela.', 'XHO22'),
(19, 10, 3, 'Ke kaloku masenze umnqophiso noThixo wethu wokuba sisuse bonke abafazi nabantwana abazelwe ngabo, ngokwecebo lenkosi yam nalowo ugubha ngomyalelo woThixo wethu; kwaye kwenziwa ngokomthetho.', 'XHO22'),
(19, 10, 4, 'Vuka; ngokuba le nto yeyakho; nathi siya kuba nani; yomelelani niyenze.', 'XHO22'),
(19, 10, 5, 'Wesuka wema uEzra, wabafungisa ababingeleli abakhulu, nabaLevi, noonyana bonke bakaSirayeli, ukuba benze ngokwelo lizwi; Bafunga ke.', 'XHO22'),
(19, 10, 6, 'uEzra wema phambi kwendlu yoTHIXO oyinyaniso waza wangena kwigumbi likaYohanan unyana kaEliyashibhi, wafika apho, akadla sonka okanye asele manzi; ngokuba wayebuhlungu ngenxa yobugwenxa babo bafuduswayo.', 'XHO22'),
(19, 10, 7, 'Baduma kwaYuda naseYerusalem, kubo bonke oonyana basemfudusweni, ukuba babahlanganisele eYerusalem;', 'XHO22'),
(19, 10, 8, 'kuthi, osukuba engafika emva kwemihla emithathu, ngokwecebo labathetheli namadoda amakhulu, ithinjwe yonke impahla yakhe, ahlulwe ebandleni labathinjwa.', 'XHO22'),
(19, 10, 9, 'Abuthelana eYerusalem onke amadoda akwaYuda nawakwaBhenjamin iintsuku ezintathu yaye iyinyanga entsha, ngomhla wamashumi amabini enyangeni leyo; Bahlala bonke abantu ebaleni lendlu kaThixo, bengcangcazela yile nto, naziimvula ezinkulu.', 'XHO22'),
(19, 10, 10, 'Wesuka wema umbingeleli uEzra waza wathi kubo: “Nenze ubugwenxa yaye nizeke abafazi bezinye iintlanga ngokwandisa isikreqo sikaSirayeli.', 'XHO22'),
(19, 10, 11, 'Mdumiseni kaloku, uYehova, uThixo wooyihlo, nenze ngokuthanda kwakhe, nizahlule kwesi sizukulwana, nakubafazi bezinye iintlanga.', 'XHO22'),
(19, 10, 12, 'Laphendula ke lonke ibandla ngezwi elikhulu, lathi, Sinako ukwenza ngokwelizwi lakho.', 'XHO22'),
(19, 10, 13, 'Kodwa baninzi abantu, yaye ilixesha leemvula ezinkulu, yaye asinako ukuma ngaphandle, yaye asingomsebenzi wemini enye okanye ezimbini, ngenxa yokuba abaninzi bethu baye benza ubugwenxa ngalo mbandela.', 'XHO22'),
(19, 10, 14, 'Mazime ke ngoko izikhulu zebandla lethu lonke, nabazeke abafazi bezinye iintlanga emizini yethu, ngamaxesha amisiweyo, namadoda amakhulu omzi ngamnye, nabagwebi bawo, ide ifike loo mzi uburhalarhume boThixo wethu.', 'XHO22'),
(19, 10, 15, 'NguYonatan unyana ka-Asayeli, noYahasiya unyana kaTikiva, kuphela abalungisayo kuloo ndawo; uMeshulam noSabhetayi umLevi bayabanceda.', 'XHO22'),
(19, 10, 16, 'Benjenjalo oonyana bamabanjwa; Umbingeleli uEzra, iintloko zezindlu zooyise ngokweentsapho zooyise, bonke bephela ngamagama abo, bazinyula baze bahlale phantsi ukuze bahlolisise umcimbi ngomhla wokuqala wenyanga yeshumi.', 'XHO22'),
(19, 10, 17, 'Bawatshabalalisa onke amadoda awayezeke abafazi bezinye iintlanga, ngomhla wokuqala wenyanga yokuqala;', 'XHO22'),
(19, 10, 18, 'Koonyana bababingeleli kwafumaneka abafazi bezinye iintlanga: oonyana bakaYeshuwa unyana kaYotsadaki, nabazalwana babo; noMahaseya, noEliyezere, noYaribhi, noGedaliya.', 'XHO22'),
(19, 10, 19, 'Banika izandla zabo ukuba baqhawule umtshato nabafazi babo; ngokuba bebenetyala, baxhela inkunzi yegusha ngenxa yobugwenxa babo;', 'XHO22'),
(19, 10, 20, 'koonyana bakaImere; uHanani noZebhadiya;', 'XHO22'),
(19, 10, 21, 'koonyana bakaHarim; noMahaseya, noEliya, noShemaya, noYehiyeli, no-Uziya;', 'XHO22'),
(19, 10, 22, 'koonyana bakaPasgure; uElijonayi, noMahaseya, noIshmayeli, noNataniyeli, noYozabhadi, noElasa;', 'XHO22'),
(19, 10, 23, 'nakubaLevi; uYozabhadi, noShimehi, noKelaya, okwanguKelita, noPetaya, noYuda, noEliyezere;', 'XHO22'),
(19, 10, 24, 'nakwiemvumi; noEliyashibhi nakubagcini-masango; uShalum noTeleme noUri;', 'XHO22'),
(19, 10, 25, 'koonyana bakaParosi yangu-Eriyeli; uRamiya, noYesiya, noMalekiya, noMiyamin, noElazare, noMalekiya, noBhenaya;', 'XHO22'),
(19, 10, 26, 'koonyana bakaElam; noMataniya, noZekariya, noYehiyeli, noAbhedi, noYeremiya, noEliya;', 'XHO22'),
(19, 10, 27, 'koonyana bakaSathu; uEliyonayi, uEliyashibhi, uMataniya, uYeremiya, uSabhadi, noAsaya;', 'XHO22'),
(19, 10, 28, 'nakoonyana bakaBhebhayi; uYohanan, uHananiya, uSabhayi noAtlayi;', 'XHO22'),
(19, 10, 29, 'koonyana bakaBhani; noMeshulam, noMaluge, noAdaya, noYashubhi, noShehali, noRamoti;', 'XHO22'),
(19, 10, 30, 'koonyana bakaPahati-mowabhi; nguAdena, noKelali, noBhenaya, noMahaseya, noMataniya, noBhete-saleli, noBhinuwi, noManase;', 'XHO22'),
(19, 10, 31, 'koonyana bakaHarim; uEliyezere, uIshiya, uMalekiya, uShemaya, uSimon,', 'XHO22'),
(19, 10, 32, 'uBhenjamin, uMaleki, noShemariya;', 'XHO22'),
(19, 10, 33, 'koonyana bakaHashum yangu nguMatenayi, noMatatha, noSabhadi, noElifeleti, noYeremiya, noManase, noShimehi;', 'XHO22'),
(19, 10, 34, 'koonyana bakaBhani; uMahadayi, uAmram noUweli,', 'XHO22'),
(19, 10, 35, 'uBhenaya, uBhedeya, uKelu,', 'XHO22'),
(19, 10, 36, 'uVanya, uMeremoti, uEliyashibhi,', 'XHO22'),
(19, 10, 37, 'uMataniya, uMatenayi, noYahasau,', 'XHO22'),
(19, 10, 38, 'noBhani, noBhinuwi, noShimehi;', 'XHO22'),
(19, 10, 39, 'noShelemiya, noNatan, noAdaya,', 'XHO22'),
(19, 10, 40, 'neMakenadebhayi, neShashayi, neSharayi,', 'XHO22'),
(19, 10, 41, 'noAzareli, noShelemiya, noShemariya,', 'XHO22'),
(19, 10, 42, 'uShalum, noAmariya, noYosefu;', 'XHO22'),
(19, 10, 43, 'koonyana baseNebho; uYehiyeli, noMattitja, noSabhadi, noSebhina, noYadawu, noYoweli, noBhenaya.', 'XHO22'),
(19, 10, 44, 'Bonke bazeka abafazi bezinye iintlanga, baza nabanye babo baba nabantwana.', 'XHO22'),
(20, 1, 1, 'Amazwi kaNehemiya unyana kaHagaliya. Ngenyanga enguKislevu, ngomnyaka wamashumi amabini, ndiseShushan komkhulu,', 'XHO22'),
(20, 1, 2, 'Kwafika uHanani, omnye wakubazalwana bam, yena namanye amadoda akwaYuda; ndaza ndababuza ngamaYuda awayekhululekile awayesele ekuthinjweni, nangeYerusalem.', 'XHO22'),
(20, 1, 3, 'Bathi kum, Aphambe, abaseleyo abathinjwa, abaseleyo emhlabeni, balambe kakhulu; Zadilizwa iindonga zeYerusalem, namasango ayo atshiswe ngomlilo.', 'XHO22'),
(20, 1, 4, 'Ndathi ndakuweva la mazwi, ndahlala phantsi ndalila ndaza ndazila kangangeentsuku ezithile ndaza ndazila ukutya ndaza ndathandaza phambi koThixo wezulu.', 'XHO22'),
(20, 1, 5, 'Ndithi, Ndiyathandaza kuwe, Yehova, Thixo wamazulu, Thixo omkhulu, owoyikekayo, obagcinelayo umnqophiso nenceba abamthandayo, abayigcinayo imithetho yakhe;', 'XHO22'),
(20, 1, 6, 'Phulaphula ngoko ezindlebeni zakho, uwavule amehlo akho, ukuze uwuve umthandazo womkhonzi wakho, endiwuthandazayo ngoku phambi kwakho, imini nobusuku, ndiwenzela oonyana bakaSirayeli, nakubakhonzi bakho, nezono zoonyana bakaSirayeli. Sivume ukuba sonile kuwe, mna nendlu kabawo sonile.', 'XHO22'),
(20, 1, 7, 'Sikwenze ngobubi obukhulu, yaye asiyigcinanga imiyalelo nemiyalelo nezigwebo owamwisela umthetho ngazo uMoses umkhonzi wakho.', 'XHO22'),
(20, 1, 8, 'Khumbula ilizwi owamwisela umthetho ngalo uMoses umkhonzi wakho, elithi, Ukuba nithe nameneza, ndonichithachitha phakathi kweentlanga.', 'XHO22'),
(20, 1, 9, 'Kwaye ukuba uyaguqukela kum kwaye uxhase imiyalelo yam kwaye uyenze; Nokuba bathe babakho kuni abathatyathwe esiphelweni sezulu, ndobahlanganisela khona, ndibazise kuloo ndawo ndiyinyulileyo, ukuba ndilimise khona igama lam.', 'XHO22'),
(20, 1, 10, 'Ke aba ngabakhonzi bakho, nabantu bakho, owabakhulula ngamandla akho amakhulu, nangesandla sakho esithe nkqi.', 'XHO22'),
(20, 1, 11, 'Ndiya kukhunga, Yehova, indlebe yakho iwuve umthandazo womkhonzi wakho, nomthandazo wabakhonzi bakho abaloyikayo igama lakho; ubabalo emehlweni ale ndoda. Kaloku mna ndandingumngcamli wokumkani.', 'XHO22'),
(20, 2, 1, 'Ngenyanga enguNisan, ngomnyaka wamashumi amabini ka-Artashashta ukumkani, bekukho iwayini phambi kwakhe, ndayithabatha iwayini leyo, ndayinika ukumkani. Ngoku ndandingenalusizi phambi kwakhe ngaphambili.', 'XHO22'),
(20, 2, 2, 'Wathi ukumkani kum, Kungani na ukuba ubuso bakho bube nje, ukuba bubi, ungasafi nje? ayonto ngaphandle kobuhlungu bentliziyo. Ndiye ndoyika kakhulu.', 'XHO22'),
(20, 2, 3, 'Wathi kukumkani, Ukumkani makadle ubomi ngonaphakade; Yini na ukuba ubuso bam bungabi bubi, ulinxuwa nje umzi, indawo yokungcwaba ooyihlo, amasango awo etshiswe ngomlilo?', 'XHO22'),
(20, 2, 4, 'Wathi ukumkani kum, Ucela ntoni na? Ndathandaza ke kuThixo wamazulu.', 'XHO22'),
(20, 2, 5, 'Ndathi kukumkani, Ukuba ukumkani uthe wakubona kufanelekile, ukuba umkhonzi wakho lo abona kulungile kuwe, ndithumele kwaYuda, kuwo umzi wamangcwaba kabawo, ndiwakhe.', 'XHO22'),
(20, 2, 6, 'Wathi ukumkani kum (ukumkanikazi ehleli naye), Uhambo lwakho loba lolwexesha elingakanani na? kwaye uya kubuya nini? Ngoko ke, ukumkani wavuma ukundithuma; ndamnika ixesha.', 'XHO22'),
(20, 2, 7, 'Kananjalo ndathi kukumkani, Ukuba ukumkani uthe wathanda, mabanikwe iincwadi zam eziya kumabamba anganeno koMlambo, aze andise kwaYuda;', 'XHO22'),
(20, 2, 8, 'nencwadi eya kuAsafu, umgcini wehlathi lokumkani, ukuba andinike imithi yokwenza imiqadi yeengcango zendlu yokumkani, neyodonga lomzi, neyendlu endiya kuyakhela indlu ndiya kuyaba. Wandinika ke ukumkani ngokwesandla sikaThixo esilungileyo esiphezu kwam.', 'XHO22'),
(20, 2, 9, 'Ndafika kumabamba anganeno koMlambo, ndawanika iincwadi zokumkani. Waza ukumkani wathumela nam abathetheli nabamahashe.', 'XHO22'),
(20, 2, 10, 'Xa uSanebhalati umHoron noTobhiya umkhonzi womAmoni bakuyivayo loo nto, baba buhlungu kakhulu xa kwafika enye indoda ukuze incede oonyana bakaSirayeli.', 'XHO22'),
(20, 2, 11, 'Ndafika ke eYerusalem, ndahlala khona iintsuku zantathu.', 'XHO22'),
(20, 2, 12, 'Ndavuka ebusuku, ndinamadoda ambalwa; Andixelelanga mntu into abeyibeke entliziyweni yam uThixo wam, ukuba ndiyenzele eYerusalem; Ndibe ndingenamaqegu, ibilelo lodwa bendikhwele kulo.', 'XHO22'),
(20, 2, 13, 'Ndaphuma ebusuku ngesango likaPhula phambi komhadi wenamba ndaya kwisango laseMoiteli, ndabona udonga lwaseYerusalem ludilika, namasango ayo etshiswe ngomlilo.', 'XHO22'),
(20, 2, 14, 'Ndaphuma ndaya esangweni leQula nechibi lokumkani; akwabakho ndawo yokuba lidlule iqegu endandikhwele kulo.', 'XHO22'),
(20, 2, 15, 'Ndanyuka ebusuku ndaya entilini yomlambo, ndaza ndalugqala udonga, ndabuya ndangena ngesango lomfula, ndabuya.', 'XHO22'),
(20, 2, 16, 'Abaphathi babengazi apho ndandiye khona, nento endayenzayo; andiwaxelanga amaYuda, nababingeleli, nezikhulu, nabathetheli, nabanye abasebenzi.', 'XHO22'),
(20, 2, 17, 'Ndathi kubo, Niyabubona ububi esikubo, ukuba iYerusalem yinxuwa, ukuba iingcango zayo zitshiswe ngomlilo; yizani silwakhe udonga lweYerusalem, singabi saba lihlazo.', 'XHO22'),
(20, 2, 18, 'Ndabaxelela ukulunga kum isandla soThixo wam; namazwi okumkani, awawathethayo kum. Athi: Masilinde, masakhe. Bazomeleza izandla zabo ukuwenza lo msebenzi ulungileyo.', 'XHO22'),
(20, 2, 19, 'Kwathi kamsinya nje akuba uSanebhalati umHoron noTobhiya umkhonzi womAmoni noGeshem umArabhu bakuyivayo le nto, basuka bahlekisa ngathi baza basiphoxa besithi: “Yintoni le niyenzayo? Niya kugwilika na kukumkani?', 'XHO22'),
(20, 2, 20, 'Ndabaphendula, ndathi kubo, uThixo wamazulu uya kusisikelela; ngoko ke, thina bakhonzi bakhe, siya kusuka sakhe; ke nina aninasabelo nalungelo nasikhumbuzo eYerusalem.', 'XHO22'),
(20, 3, 1, 'Wesuka wema uEliyashibhi umbingeleli omkhulu, nabazalwana bakhe ababingeleli, balakha isango lempahla emfutshane; balingcwalisa, bazifaka iingcango zalo; bayingcwalisa, besa enqabeni ende yeMeha, besa enqabeni ende kaHananeli.', 'XHO22'),
(20, 3, 2, 'Amadoda aseYeriko akha malunga naye; Phambi kwabo kwakha uSakure unyana kaImri;', 'XHO22'),
(20, 3, 3, 'Kodwa iSango Leentlanzi lakhiwa ngoonyana bakaHasenaha, nabo bafaka imiqadi yalo baza bafaka iingcango zalo nezitshixo nemivalo yalo.', 'XHO22'),
(20, 3, 4, 'uMeremoti, unyana kaUriya, unyana kaKeshe, emalunga nabo walungisa malungana nabo. Ke bona amaFilisti unyana kaBheriya, unyana kaMeshezabheli, alungisa; ngakubo waphiliswa uTsadoki, unyana kaBhahana.', 'XHO22'),
(20, 3, 5, 'Ngokuchasene nabo, abaLingwayo babuya bachacha; ke iingangamsha zabo azizingenisanga zona iintamo zazo enkonzweni yeNkosi yazo.', 'XHO22'),
(20, 3, 6, 'iSango Elidala lalilungiswa ngoYoyada unyana kaPasaki, noMeshulam unyana kaBhesodiya; bayifaka imiqadi, bazifaka iingcango zalo, nezitshixo zalo, nemivalo yalo.', 'XHO22'),
(20, 3, 7, 'Phezu kwabo uMelatiya waseGibheyon, noYadon waseMeronoti, amadoda aseGibheyon naseMizpa, etroneni yerhuluneli engasentshona yoMlambo umEfrati.', 'XHO22'),
(20, 3, 8, 'U-Uziyeli unyana kaHarehaya, umkhandi wegolide, malungana naye wayelungisa; Ngokuchasene naye, uHananiya unyana womnye wabaqholi wachacha, yaye bayiqinisa iYerusalem ukusa kudonga olubanzi.', 'XHO22'),
(20, 3, 9, 'Ecaleni kwabo, uRefaya unyana kaHure, ukumkani wesiqingatha sesithili saseYerusalem, elungisa;', 'XHO22'),
(20, 3, 10, 'ngakubo kulungiswa nguYedaya unyana kaHarumafi, malungana nendlu yakhe; Kwaye phambi kwakhe waphila uHatus, unyana kaHasabhenja.', 'XHO22'),
(20, 3, 11, 'uMalekiya unyana kaHarim, noHashubhi, unyana kaPahati, wakwaMowabhi, balungisa esinye isithuba, nenqaba ende yeziko.', 'XHO22'),
(20, 3, 12, 'Ngakuye, kwakulungiswa nguShalum unyana kaHaloheshe, ukumkani wesiqingatha sesithili saseYerusalem, yena neentombi zakhe.', 'XHO22'),
(20, 3, 13, 'uDalepoort wambuyisela uHanun nabemi baseSanoage; balakha, bazifaka iingcango zalo nemivalo yalo nezitshixo zalo;', 'XHO22'),
(20, 3, 14, 'Isango laseMoitedi lalilungiswa nguMalekiya, unyana kaRekabhi, umphathi wesithili saseBhete-hakereme; walakha, wazifaka iingcango zalo nezitshixo zalo nemivalo yalo.', 'XHO22'),
(20, 3, 15, 'Isango lequla lalilungiswa nguShalon unyana kaKolehose, ibamba lesithili saseMizpa; walakha, walifulela, wazifaka iingcango zalo, nezitshixo zalo, nemivalo yalo, nodonga lweChibi laseSilowa, emyezweni wokumkani, kwesa ezinyukweni zokuhla ukuya emzini kaDavide.', 'XHO22'),
(20, 3, 16, 'Emva kwakhe uNehemiya unyana ka-Ashbhuki, isiqingatha senkosana yaseBhete-tsure walungisa ukuya kutsho kwindawo ephambi kwengcwaba likaDavide ukuya echibini elalenziwe apho ukuya kufikelela kwindlu yamagorha.', 'XHO22'),
(20, 3, 17, 'Emva kwakhe abaLevi, uRehum unyana kaBhani, bahlaziya. Ecaleni kwakhe uHasábja, umphathi wesiqingatha seKehila, wayibuyisela indawo yakhe.', 'XHO22'),
(20, 3, 18, 'Emva kwakhe kwakha abazalwana babo uBhawayi unyana kaHenadade, inkosana yesiqingatha sesithili saseKehila.', 'XHO22'),
(20, 3, 19, 'Ngaphambi kwakhe uEzere unyana kaYeshuwa, inkosana yaseMizpa wayelungisa enye indawo emalunga nomnyango wendawo yokugcina izikrweqe embombeni yodonga.', 'XHO22'),
(20, 3, 20, 'Emva kwakhe kwakulungiswa nguBharuki, unyana kaSabhayi, esinye isithuba; kwanzima, kwathabathela embombeni yodonga, kwesa emnyango wendlu yombingeleli omkhulu, uEliyashibhi.', 'XHO22'),
(20, 3, 21, 'Emva kwakhe uMeremoti unyana kaUriya unyana kaKoshe walungisa esinye isithuba, ukususela kumnyango wendlu kaEliyashibhi ukusa ekupheleni kwendlu kaEliyashibhi.', 'XHO22'),
(20, 3, 22, 'Baphiliswa emva kwakhe ababingeleli, amadoda a entlango.', 'XHO22'),
(20, 3, 23, 'Emva kwakhe kwakulungiswa nguBhenjamin noHashubhi, malunga nendlu yabo. Emva kwakhe uAzariya, unyana kaMahaseya, unyana ka-Ananiyas, wabuyela endlwini yakhe.', 'XHO22'),
(20, 3, 24, 'Emva kwakhe kwakulungiswa nguBhinuwi unyana kaHenadade, esinye isithuba; kwathabathela endlwini ka-Azariya, kwesa embombeni yodonga, kwesa embombeni.', 'XHO22'),
(20, 3, 25, 'uPalali unyana kaUshayi, emalunga nembombo yodonga, nakwinqaba evelele yendlu yokumkani, ngasentendelezweni yentolongo. Emva kwakhe nguPedaya unyana kaParoshe.', 'XHO22'),
(20, 3, 26, 'Ngaphezu koko, amaNethini ahlala eOfele, ukuya kutsho kummandla ongakwimpuma yeSango laManzi nakwinqaba eveleleyo.', 'XHO22'),
(20, 3, 27, 'Emva koko iTekowa yalungisa enye indawo, malunga nenqaba enkulu ephume ngaphandle, ukuya kudonga lweOfele.', 'XHO22'),
(20, 3, 28, 'Encotsheni yesango lamahashe ababingeleli balungisa, elowo ekhangele endlwini yakhe.', 'XHO22'),
(20, 3, 29, 'Emva kwabo kwakulungiswa nguTsadoki, unyana kaImere, malunga nendlu yakhe; Emva kwakhe wachacha uShemaya unyana kaSeganja, umgcini wesango lasempumalanga.', 'XHO22'),
(20, 3, 30, 'Emva kwakhe kwakulungiswa nguHananiya unyana kaShelemiya, noHanun unyana wesithandathu kaShelafu, esinye isithuba. Kwathi emveni koko, uMeshulam, unyana kaBheriya, wayilungisa indlu yakhe;', 'XHO22'),
(20, 3, 31, 'Emva kwakhe kwakulungiswa nguMalekiya, unyana womkhandi wegolide, ukuya kufikelela kwindawo yabakhonzi betempile nabarhwebi, phambi kwesango leMifkade, kwesa kwithambeka lembombo.', 'XHO22'),
(20, 3, 32, 'Phakathi kwethambeka lembombo ukuya kwiSango leegusha, kukho iingcibi zegolide nabarhwebi.', 'XHO22'),
(20, 4, 1, 'Akuva uSanebhalati ukuba silwakhile udonga, wacaphuka, waba nomsindo kakhulu, wawangcikiva amaYuda.', 'XHO22'),
(20, 4, 2, 'Wathetha phambi kwabazalwana bakhe naphambi kwamaqela aseSamariya, wathi: “Enza ntoni na la maYuda abuthathaka? baya kuziqinisa na? baya kubingelela na? ziya kuphela ngamini-nye? Angawasusa na amatye kwiimfumba zenkunkuma?', 'XHO22'),
(20, 4, 3, 'uTobhiya umAmon wayenaye, wathi, Nokuba yintoni na abayakha ngayo, ukuba kuthe gqi impungutye, iya kuluchitha udonga lwabo lwamatye.', 'XHO22'),
(20, 4, 4, 'Yiva, Thixo wethu; ngokuba sideliwe; kwaye ubuyisele ihlazo labo phezu kweentloko zabo nokubanikela ukuba babe ngabathinjwa kwilizwe lokuthinjwa.', 'XHO22'),
(20, 4, 5, 'Musa ukubugquma ubungendawo babo, ungasivumeli sicinywe isono sabo phambi kwakho, ngenxa yokuba bakwenza intlekisa phambi kwabakhi.', 'XHO22'),
(20, 4, 6, 'Sakha ke udonga; kwahlanganiswa udonga lonke, kwada kwesiqingatha salo; ngokuba amadoda abekhuthalele ukusebenza.', 'XHO22'),
(20, 4, 7, 'Kodwa xa uSanebhalati noTobhiya nama-Arabhu noonyana baka-Amoni noAshdode bevayo ukuba iindonga zeYerusalem zazakhiwe nokuba iintanda zazisele zivaliwe, bavutha ngumsindo.', 'XHO22'),
(20, 4, 8, 'Babhunga bonke bephela, ukuba beze kulwa neYerusalem, bayithintele.', 'XHO22'),
(20, 4, 9, 'Noko ke, sathandaza kuThixo wethu, samisa abalindi nxamnye nabo imini nobusuku ngenxa yabo.', 'XHO22'),
(20, 4, 10, 'Wathi uYuda, Aphelile amandla abathwali, nenkunkuma ininzi; ngoko asinako ukwakha udonga.', 'XHO22'),
(20, 4, 11, 'Zathi iintshaba zethu, khange bazi, ababonanga, sada sangena phakathi kwabo, sababulala, sawumisa umsebenzi.', 'XHO22'),
(20, 4, 12, 'Athi akufika amaYuda awayekunye nawo, asixelela izihlandlo ezilishumi, esithi: “Zonke iindawo eninokubuyela kuzo kuthi, ziya kufika kuni.', 'XHO22'),
(20, 4, 13, 'Ndamisa ezindaweni eziphantsi emva kodonga nasezigangeni, ndamisa abantu ngokwemizalwane yabo, benamakrele abo, nezikhali zabo, nezaphetha zabo.', 'XHO22'),
(20, 4, 14, 'Ndakhangela, ndaphakama, ndathi kubanumzana, neerhuluneli nakwabanye abantu, Musani ukuboyika; khumbulani uYehova omkhulu owoyikekayo, nibalwele abazalwana benu, noonyana benu, neentombi zenu, nabafazi benu, nezindlu zenu.', 'XHO22'),
(20, 4, 15, 'Zithe ke zakuva iintshaba zethu ukuba kuyaziwa kuthi, nokuba uThixo uliphelisile icebo lazo, sabuyela eludongeni, elowo emsebenzini wakhe.', 'XHO22'),
(20, 4, 16, 'Ukususela ngelo xesha isiqingatha sabakhonzi bam sasebenza, esinye isiqingatha sabo siphethe iincula, namakhaka, nezaphetha, nabaninimzi; nezikhulu zazisemva kwendlu yonke yakwaYuda.', 'XHO22'),
(20, 4, 17, 'Abo babesakha eludongeni nabo babethwala imithwalo kunye nabo babezala, ngamnye waqalisa ukusebenza ngesandla esinye, ephethe isixhobo ngesinye isandla.', 'XHO22'),
(20, 4, 18, 'Kubakhi, elowo wabeka ikrele lakhe ecaleni lakhe, kwaye bakha ngale ndlela. Lavuthela ixilongo, waba nam.', 'XHO22'),
(20, 4, 19, 'Ndathi kwizidwangube nakubathetheli nakubo bonke abanye abantu: “Lo msebenzi mkhulu, mkhulu, yaye sahlulwe ngodonga, omnye ukude komnye.', 'XHO22'),
(20, 4, 20, 'Apho nithe nakuva isandi sesigodlo, yiyani khona; uThixo wethu wosilwela.', 'XHO22'),
(20, 4, 21, 'Sandula ke sasebenza emsebenzini, yaye isiqingatha sabo sasiphethe umkhonto ukususela ngesifingo de kube sekuphumeni kweenkwenkwezi.', 'XHO22'),
(20, 4, 22, 'Ndathi ke ebantwini ngelo xesha, Elowo makalale nesicaka sakhe eYerusalem, sibe ngumlindi wethu ebusuku, sisebenze emini.', 'XHO22'),
(20, 4, 23, 'Ke mna, nabazalwana bam, nabakhonzi bam, namadoda omlindi abendilandela, akubangakho namnye uzikhululayo iingubo zakhe;', 'XHO22'),
(20, 5, 1, 'Ke kaloku kwabakho ukukhala okukhulu ebantwini nabafazi babo ngabazalwana babo amaYuda.', 'XHO22'),
(20, 5, 2, 'Bekukho ababesithi, Thina, oonyana bethu, neentombi zethu, sibaninzi; ngoko ke, sibathabathele ingqolowa, ukuze sidle, ukuze siphile.', 'XHO22'),
(20, 5, 3, 'Kwakukho nabanye ababesithi: “Ngenxa yokuswela kwethu siye sabeka amasimi ethu, nezidiliya, nezindlu zethu, ukuze sithenge ingqolowa.', 'XHO22'),
(20, 5, 4, 'Kwakukho nababesithi, Saboleka imali kwirhafu yokumkani, namasimi ethu, nezidiliya zethu;', 'XHO22'),
(20, 5, 5, 'Kungokunje, inyama yethu injengenyama yabazalwana bethu, nabantwana bethu banjengoonyana babo; uyabona, siyabafaka oonyana neentombi zethu ebukhobokeni, neentombi zethu sezikhonziswe; kananjalo akukho semandleni ethu ukubakhulula; kuba abanye abantu banamasimi nezidiliya zethu.', 'XHO22'),
(20, 5, 6, 'Ndaba noburhalarhume obukhulu ekubabizeni kwam, ndaweva la mazwi.', 'XHO22'),
(20, 5, 7, 'Ndacamanga phakathi kwam, ndabakhalimela abathetheli nabathetheli, ndathi kubo, Ningxamele elowo umzalwana wakhe. Ndaza ndabavelisela ibandla elikhulu.', 'XHO22'),
(20, 5, 8, 'Ndathi ke kubo, Sibakhulule, njengoko sinako, abazalwana bethu amaYuda, ebe kuthengiswe ngabo kwiintlanga; kwaye ungathengisa nabantakwenu? okanye ziza kuthengiswa kuthi? Bathe cwaka ke, abafumana nto yokuphendula.', 'XHO22'),
(20, 5, 9, 'Kananjalo ndathi, Akulungile ukuba nenze loo nto; nibe ningafanele na ukuhamba ekoyikeni uThixo wethu, ngenxa yehlazo leentlanga eziziintshaba zethu?', 'XHO22'),
(20, 5, 10, 'Kwangokunjalo, mna nabazalwana bam, nabakhonzi bam, sibe nako ukubiza kubo imali nengqolowa;', 'XHO22'),
(20, 5, 11, 'Babuyisele namhla amasimi abo, nezidiliya zabo, neminquma yabo, nezindlu zabo, nekhulu lemali, nelengqolowa, nelewayini, neoli, enilithabathileyo kubo.', 'XHO22'),
(20, 5, 12, 'Bathi ke bona, Asiyi kukubuyisa oko, asiyi kubiza nto kubo; siya kwenza njengoko utshoyo. Ndababiza ababingeleli, ndabafungela ukuba baya kwenza ngokwelo lizwi.', 'XHO22'),
(20, 5, 13, 'Ndavuthulula ingubo yam, ndathi, uThixo makenjenje, elowo endlwini yakhe, nasemsebenzini wakhe, nabani na ongawufeziyo lo mbandela naye wancothulwa kwangolo hlobo, engaswele nto. Lathi lonke ibandla, Amen, bongani uYehova; Abantu bayenza loo nto.', 'XHO22'),
(20, 5, 14, 'Ngaphezu koko, ukususela kwixesha endamiselwa ngalo ukuba ndibe yirhuluneli yabo kwilizwe lakwaYuda, ukususela kunyaka wamashumi amabini ukuya kutsho kunyaka wamashumi amathathu anesibini ka-Artashashta, oko kukuthi, iminyaka elishumi elinesibini, mna nabantakwethu asizange siwudle umthendeleko.', 'XHO22'),
(20, 5, 15, 'Ke zona iirhuluneli zokuqala ezazikho phambi kwam zathetha kakubi ngabantu, zathabatha kubo isonka newayini, ngaphandle kweeshekele zesilivere ezimashumi mane; ewe, kwanabakhonzi babo ngokwenene babalawula abantu; ke andikwenzanga oko, ngenxa yokoyika uThixo.', 'XHO22'),
(20, 5, 16, 'Ewe, ndawuqhuba umsebenzi wolu donga, yaye asithenganga mhlaba, yaye bonke abakhonzi bam bahlanganisana khona ukuze basebenze.', 'XHO22'),
(20, 5, 17, 'Kananjalo amaYuda, nabathetheli abalikhulu elinamanci mahlanu, babesidla etafileni yam, kwanabo babeze kuthi bevela ezintlangeni ezisijikelezileyo.', 'XHO22'),
(20, 5, 18, 'Kwaye bekulungiselelwa imini enye inkomo, neegusha ezikhethiweyo ezintandathu; kwaye iintaka zazilungiselelwa mna, kwaye kanye ngeentsuku ezilishumi, zonke iintlobo zewayini zaziqokelelwa; noko ke andisifunanga isonka serhuluneli, ngokuba ubukhoboka belunzima phezu kolu hlanga.', 'XHO22'),
(20, 5, 19, 'Ndikhumbulele okulungileyo, Thixo wam, ngako konke endakwenzayo kolu hlanga.', 'XHO22'),
(20, 6, 1, 'Bathi ke ooSanebhalati, noTobhiya, noGeshem, umArabhi, nabathile kuthi, beva ukuba ndilwakhile udonga, akwabakho sikhewu kulo; (nangona ngelo xesha ndingazifakanga iingcango zesango;)', 'XHO22'),
(20, 6, 2, 'Bandithuma ke ooSanebhalati noGeshem, bathi, Yiza, sihlanganisene komnye umzi entlango yeOno; Kodwa babecinga ukundithuthumbisa.', 'XHO22'),
(20, 6, 3, 'Ndathumela abathunywa kubo, ndathi, Ndenza umsebenzi omkhulu, andinakuhla; ungayekiswani umsebenzi ukuba ndiyawushiya ndihle ndize kuni?', 'XHO22'),
(20, 6, 4, 'Bathumela kum izihlandlo zazine; Ndabaphendula kwangolo hlobo.', 'XHO22'),
(20, 6, 5, 'Ke kaloku umkhonzi wakhe uSanebhalati wathumela kum okwesihlanu, ephethe incwadi evulekileyo esandleni sakhe;', 'XHO22'),
(20, 6, 6, 'Kubhaliwe kwathiwa, Kwenziwa udumo phakathi kweentlanga, noGasmu uthi, wena namaYuda nicinga ukuvukela uThixo; ngenxa yoko wakha udonga ukuze abe ngukumkani kubo ngokwamazwi.', 'XHO22'),
(20, 6, 7, 'Wamisela abaprofeti ukuba bathethe ngawe eYerusalem, besithi, ‘Kukho ukumkani kwaYuda, yaye ke la mazwi aya kuxelwa kukumkani. Yiza ngoku, sithethe kunye.', 'XHO22'),
(20, 6, 8, 'Ndathumela kuye, ndathi, Akuzanga kwenziwe nto injengaleyo uyithethileyo; wena ke uyibeke ngokusuka entliziyweni.', 'XHO22'),
(20, 6, 9, 'Ngokuba bebesoyikisa sonke, besithi, Izandla zabo ziya kuwuyeka lo msebenzi, ungenziwa. Yomeleza izandla zam ngoku, Thixo.', 'XHO22'),
(20, 6, 10, 'Ndangena endlwini kaShemaya unyana kaDelaya, unyana kaMehetabheli, elugcinweni; Wathi, Masihlangane endlwini kaThixo, ngaphakathi kwetempile, sivale iingcango zetempile, ngokuba baza kukubulala; ewe, ebusuku baya kuza kukubulala.', 'XHO22'),
(20, 6, 11, 'Ndathi ke, Kubaleke indoda enjengam na? ngubani na onjengam onako ukungena etempileni, ukuba asisindise umphefumlo wakhe? Andiyi kungena.', 'XHO22'),
(20, 6, 12, 'Yabonani, ndaqonda ukuba uThixo akamthumanga; Uthethe esi siprofeto nxamnye nam, ngokuba ebeqeshwe nguTobhiya noSanebhalati.', 'XHO22'),
(20, 6, 13, 'Kungoko ebeqeshelwe ukuze ndoyike, ndenze loo nto, ndone, babe nentetho embi, ukuze banditshabalalise.', 'XHO22'),
(20, 6, 14, 'Bakhumbule, Thixo wam, ooTobhiya noSanebhalati ngokwezenzo zabo, nangomprofetikazi uNowadiya nabanye abaprofeti ababendoyikisa.', 'XHO22'),
(20, 6, 15, 'Lwagqitywa ke udonga ngomhla wamashumi omabini anesihlanu wenyanga enguEluli, ngeentsuku ezimashumi mahlanu anamibini.', 'XHO22'),
(20, 6, 16, 'Kwathi zakukuva oko iintshaba zethu zonke, zakubona oko zonke iintlanga ezisijikelezileyo, zawa emehlweni azo, ngokuba zibonile ukuba lo msebenzi ubewenziwe nguThixo wethu.', 'XHO22'),
(20, 6, 17, 'Kananjalo abanumzana bakwaYuda bazongeza iincwadi eziya kuTobhiya ngaloo mihla, nezikaTobhiya zazifika kubo.', 'XHO22'),
(20, 6, 18, 'Kuba babebaninzi kwaYuda ababemfungele, ngenxa yokuba wayengumkhwenyana kaSheganiya unyana ka-Ara; unyana wakhe uYohanan wazeka intombi kaMoslem unyana kaBheregiya.', 'XHO22'),
(20, 6, 19, 'Baye bendixelela ngemisebenzi yakhe emihle phambi kwam, namazwi am. Kwaye uTobias wathumela iileta zokundigrogrisa.', 'XHO22'),
(20, 7, 1, 'Lwalulwa udonga, ndazifaka iingcango, bamisa abamasango, neemvumi, nabaLevi;', 'XHO22'),
(20, 7, 2, 'Ndabeka uHanani umntakwethu noHananiya ukuba baphathe indlu yobukumkani ukuba balawule iYerusalem, ngenxa yokuba wayeyindoda ethembekileyo neyoyika uThixo ngaphezu kwabaninzi.', 'XHO22'),
(20, 7, 3, 'Ndathi kubo, Musani ukuwavula amasango aseYerusalem, lide lifudumale ilanga; kwaye ngoxa bemi apho, baya kuzivala iingcango baze bahambe; wamisa ababoniseli phakathi kwabemi baseYerusalem, elowo esembonisweni yakhe, elowo wema malunga nendlu yakhe.', 'XHO22'),
(20, 7, 4, 'Umzi ububanzi ngeenxa zombini, ubanzi ngeenxa zombini; abantu bembalwa kuwo, kungakhiwanga zindlu.', 'XHO22'),
(20, 7, 5, 'Yaye uThixo wam wakubeka entliziyweni yam ukuba ndiqokelele ndawonye izikhulu nabalawuli nabantu ukuba babalwe ngokomnombo wam. Ndafumana umlibo wokuzalwa wokuqala, ndawufumana kubhaliwe kwathiwa,', 'XHO22'),
(20, 7, 6, 'Ngabo aba oonyana bephondo ababevela ekuthinjweni, abathinjwa, awabathabathayo uNebhukadenetsare ukumkani waseBhabhiloni waza wababuyisela eYerusalem nakwaYuda, ngamnye kwisixeko sakowabo;', 'XHO22'),
(20, 7, 7, 'abeza noZerubhabheli, uYeshuwa, uNehemiya, uAzariya, uRamaya, uNahamani, uMordekayi, uBhilesan, uMispereti, uBhigwayi, uNehum, uBhahana. Ndithi, inani loonyana bakaSirayeli yaba leli;', 'XHO22'),
(20, 7, 8, 'oonyana bakaParosi babengamawaka amabini anekhulu elinamashumi asixhenxe anesibini;', 'XHO22'),
(20, 7, 9, 'oonyana bakaShefatiya ibingamakhulu amathathu, anamanci asixhenxe anababini;', 'XHO22'),
(20, 7, 10, 'oonyana baka-Aragi ibingamakhulu amathandathu, anamanci mahlanu anababini;', 'XHO22'),
(20, 7, 11, 'oonyana bakaPahati wakwaMowabhi, koonyana bakaYeshuwa noYowabhi, ibingamawaka amabini, anamakhulu asibhozo, aneshumi elinye elinesibhozo.', 'XHO22'),
(20, 7, 12, 'Oonyana bakaElam babeliwaka elinamakhulu amabini anamashumi mahlanu anesine;', 'XHO22'),
(20, 7, 13, 'Oonyana bakaSathu ibilikhulu, elinamanci mane anabahlanu;', 'XHO22'),
(20, 7, 14, 'oonyana bakaSakayi ibingamakhulu asixhenxe, anamanci mathandathu;', 'XHO22'),
(20, 7, 15, 'oonyana bakaBhinuwi babengamakhulu amathandathu anamashumi mane anesibhozo;', 'XHO22'),
(20, 7, 16, 'oonyana bakaBhebhayi babengamakhulu amathandathu anamashumi amabini anesibhozo;', 'XHO22'),
(20, 7, 17, 'oonyana baseAshgade babengamawaka amabini, anamakhulu mathathu, anamanci mabini anababini;', 'XHO22'),
(20, 7, 18, 'Oonyana baka-Adonikam ibingamakhulu amathandathu, anamanci mathandathu anesixhenxe.', 'XHO22'),
(20, 7, 19, 'Oonyana bakaBhigwayi ibingamawaka amabini, anamanci mathandathu anesixhenxe;', 'XHO22'),
(20, 7, 20, 'oonyana baka-Adin ibingamakhulu amathandathu, anamanci mahlanu anabahlanu;', 'XHO22'),
(20, 7, 21, 'Oonyana baka-Atere kaHezekiya ibingamashumi asithoba anamnye, anamanci asibhozo anababini;', 'XHO22'),
(20, 7, 22, 'oonyana bakaHashum babengamakhulu amathathu anamashumi amabini anesibhozo;', 'XHO22'),
(20, 7, 23, 'oonyana bakaBheshayi babengamakhulu amathathu anamashumi amabini anesine;', 'XHO22'),
(20, 7, 24, 'oonyana bakaHarifi ibilikhulu, elinashumi linye linababini.', 'XHO22'),
(20, 7, 25, 'Oonyana baseGibheyon ngamashumi asibhozo anamnye;', 'XHO22'),
(20, 7, 26, 'amadoda aseBhetelehem naseNetofa ayelikhulu elinamashumi asibhozo anesibhozo.', 'XHO22'),
(20, 7, 27, 'Amadoda aseAnatoti ayelikhulu elinamashumi amabini anesibhozo;', 'XHO22'),
(20, 7, 28, 'amadoda aseBhete-semawete amashumi mane anamabini;', 'XHO22'),
(20, 7, 29, 'amadoda aseKiriyati-yeharim neKefira neBheroti ibingamakhulu asixhenxe, anamanci mane anabathathu.', 'XHO22'),
(20, 7, 30, 'Amadoda aseRama neGebha ibingamakhulu amathandathu, anamanci mabini anamnye.', 'XHO22'),
(20, 7, 31, 'Amadoda aseMigmas ayelikhulu elinamashumi amabini anesibini;', 'XHO22'),
(20, 7, 32, 'Amadoda aseBheteli neAyi ibilikhulu, elinamanci mabini anamathathu.', 'XHO22'),
(20, 7, 33, 'Amadoda aseNebho enye inamashumi amahlanu anesibini.', 'XHO22'),
(20, 7, 34, 'Oonyana bomnye uElam ibiliwaka elinamakhulu amabini, anamanci mahlanu anabane.', 'XHO22'),
(20, 7, 35, 'Oonyana bakaHarim ibingamakhulu amathathu, anamanci mabini.', 'XHO22'),
(20, 7, 36, 'Oonyana baseYeriko ibingamakhulu amathathu, anamanci mane anabahlanu;', 'XHO22'),
(20, 7, 37, 'Oonyana baseLode neHadide neOno ibingamakhulu asixhenxe, anamanci mabini anamnye.', 'XHO22'),
(20, 7, 38, 'Oonyana baseSenege ibingamawaka amathathu, anamakhulu asithoba, anamanci mathathu;', 'XHO22'),
(20, 7, 39, 'Ababingeleli: oonyana bakaYedaya bendlu kaYeshuwa ibingamakhulu asithoba, anamanci asixhenxe anabathathu.', 'XHO22'),
(20, 7, 40, 'Oonyana bakaImere ibiliwaka, elinamanci mahlanu anababini;', 'XHO22'),
(20, 7, 41, 'oonyana bakaPashure ibiliwaka, elinamakhulu amabini, anamanci mane anesixhenxe.', 'XHO22'),
(20, 7, 42, 'Oonyana bakaHarim ibiliwaka, elineshumi elinesixhenxe;', 'XHO22'),
(20, 7, 43, 'AbaLevi: oonyana bakaYeshuwa noKademiyeli noonyana bakaHodewa, ibingamashumi asixhenxe anabane.', 'XHO22'),
(20, 7, 44, 'Iimvumi, oonyana baka-Asafu, babelikhulu elinamashumi mane anesibhozo.', 'XHO22'),
(20, 7, 45, 'Abagcini-masango, oonyana bakaShalum, oonyana baka-Atere, oonyana bakaTalemon, oonyana baka-Akubhi, oonyana bakaHatita, oonyana bakaShobhayi, babelikhulu elinamashumi amathathu anesibhozo.', 'XHO22'),
(20, 7, 46, 'Abakhonzi betempile: oonyana bakaSiha, oonyana bakaHasufa, oonyana bakaTaboti,', 'XHO22'),
(20, 7, 47, 'nabantwana bakwaKerose, nabantwana bakwaSiya, nabantwana bakwaPadon,', 'XHO22'),
(20, 7, 48, 'nabantwana bakaLabhana, nabantwana bakwaHagabha, nabantwana bakwaSalimayi;', 'XHO22'),
(20, 7, 49, 'noonyana bakaHanan, noonyana bakaGidele, noonyana bakaGahare,', 'XHO22'),
(20, 7, 50, 'noonyana bakaRewaya, noonyana bakaRetsin, noonyana bakaNekoda,', 'XHO22'),
(20, 7, 51, 'nabantwana bakwaGasam, nabantwana bakwa-Usa, noonyana bakaPasiya;', 'XHO22'),
(20, 7, 52, 'oonyana bakaBhesayi, oonyana bakaMehun, oonyana bakaNefishi,', 'XHO22'),
(20, 7, 53, 'nabantwana bakwaBhakebuke, nabantwana bakwaHakufa, nabantwana bakwaHarure,', 'XHO22'),
(20, 7, 54, 'nabantwana bakwaBhasheliti, nabantwana bakwaMehida, nabantwana bakwaHarsa,', 'XHO22'),
(20, 7, 55, 'nabantwana bakwaBharkose, nabantwana bakwaSisera, nabantwana bakwaTama,', 'XHO22'),
(20, 7, 56, 'oonyana bakaNesiya, oonyana bakaHatifa.', 'XHO22'),
(20, 7, 57, 'Oonyana babakhonzi bakaSolomon: oonyana bakaSotayi, oonyana bakaSofereti, oonyana bakaPerida;', 'XHO22'),
(20, 7, 58, 'nabantwana bakwaYahala, nabantwana bakwaDarkon, nabantwana bakwaGidel,', 'XHO22'),
(20, 7, 59, 'nabantwana bakwaShefatiya, nabantwana bakwaHatili, nabantwana bakwaPogereti-sebhayim, oonyana baka-Amon.', 'XHO22'),
(20, 7, 60, 'Bonke abakhonzi betempile noonyana babakhonzi bakaSolomon babengamakhulu amathathu, anamanci asibhozo anamnye.', 'XHO22'),
(20, 7, 61, 'Ngabo abo abenyuka baya eTelemela, naseTele-haresha, naseKerubhi, naseAdon, naseImere, kodwa babengayixela indlu yoyise, nembewu yabo, ukuba babephuma kwaSirayeli:', 'XHO22'),
(20, 7, 62, 'oonyana bakaDelaya, oonyana bakaTobhiya, oonyana bakaNekoda, babengamakhulu omathandathu, anamanci mane anababini.', 'XHO22'),
(20, 7, 63, 'Kubabingeleli: oonyana bakaHabhaya, oonyana bakaKoshe, oonyana bakaBharzilayi, owatshata enye yeentombi zikaBharzilayi umGiliyadi waza wathiywa ngabo.', 'XHO22'),
(20, 7, 64, 'Bakhangela ingxelo yabo phakathi kwabo babebhalwe emilibeni yokuzalwa, kodwa ayifunyanwa;', 'XHO22'),
(20, 7, 65, 'Ngoko umphathi wabayalela ukuba bangazidli ezona zinto zingcwele de umbingeleli eme eneUrim neTumim.', 'XHO22'),
(20, 7, 66, 'Lonke ibandla lingamawaka angamashumi amane anamabini, anamakhulu mathathu, anamanci mathandathu;', 'XHO22'),
(20, 7, 67, 'ngaphandle kwezicaka zabo ezi, nezicakakazi zabo ezingamawaka asixhenxe, anamakhulu mathathu, anamanci mathathu anesixhenxe, zineemvumi neemvumi ezimakhulu mabini anesihlanu.', 'XHO22'),
(20, 7, 68, 'Amahashe abo ibingamakhulu asixhenxe, anamanci mathathu anesithandathu; iimeyile zabo ibingamakhulu amabini, anamanci mane anabahlanu;', 'XHO22'),
(20, 7, 69, 'iinkamela zabo zazingamakhulu amane, anamanci mathathu anantlanu; amaesile angamawaka amathandathu, anamakhulu asixhenxe, anamanci mabini.', 'XHO22'),
(20, 7, 70, 'Inxenye yeentloko zezindlu zooyise ibisebenza. uTirshata wanikela ebuncwaneni iwaka leedrarhma zegolide, namashumi amahlanu eendebe zokutshiza, namakhulu amahlanu anamanci mathathu eengubo zangaphantsi zababingeleli.', 'XHO22'),
(20, 7, 71, 'Inxenye yeentloko zezindlu zooyise yanikela ebuncwaneni bomsebenzi, amashumi amabini amawaka eedrarhima, nesilivere engamawaka amabini, anamakhulu mabini.', 'XHO22'),
(20, 7, 72, 'Okwanikelwa ngabanye abantu ibe iyigolide engamawaka amashumi amabini eedrarhima, nesilivere engamawaka amabini eemina, namashumi amathandathu anesixhenxe eengubo zangaphantsi zababingeleli.', 'XHO22'),
(20, 7, 73, 'Ababingeleli, nabaLevi, nabamasango, neemvumi, nabantu, nabakhonzi betempile, nawo onke amaSirayeli, bahlala emizini yabo; yafika inyanga yesixhenxe, besemizini yakoonyana bakaSirayeli.', 'XHO22'),
(20, 8, 1, 'Bahlanganisana bonke abantu njengamntu mnye ebaleni lesango lamanzi; bathi kuEzra umbhali, makazise incwadi yomyalelo kaMoses, awawumiselayo uYehova koonyana bakaSirayeli.', 'XHO22'),
(20, 8, 2, 'Umbingeleli uEzra wawuzisa umthetho phambi kwebandla lamadoda nabafazi nabo bonke ababesebeqabuke ingqondo ukuze baphulaphule, ngomhla wokuqala wenyanga yesixhenxe.', 'XHO22'),
(20, 8, 3, 'Wawufunda phambi kwendawo yembutho ephambi kweSango laManzi ukususela kusasa kwada kwasemini emaqanda, phambi kwamadoda nabafazi nabo banokuqonda; zaphulaphula iindlebe zabantu bonke encwadini yomyalelo.', 'XHO22'),
(20, 8, 4, 'Ke umchazi-mthetho uEzra lo wayemi phezu kweqonga lamaplanga, ababemenzele lona; ngakuye kwakumi ooMatiti, noShema, noAnaya, no-Uriya, noHilekiya, noMaseya, ngasekunene kwakhe; ngasekhohlo kwakhe nguPedaya, noMishayeli, noMalekiya, noHashum, noHasbhadan, noZekariya, noMeshulam.', 'XHO22'),
(20, 8, 5, 'uEzra wayivula incwadi phambi kwamehlo abo bonke abantu; (ngokuba ebengaphezu kwabo bonke abantu;) wathi akuba evule, bema ngeenyawo bonke.', 'XHO22'),
(20, 8, 6, 'uEzra wambonga uYehova, uThixo omkhulu. Baphendula bonke abantu, bathi, Amen, amen, ngokuphakamisa izandla zabo; bathoba, baqubuda kuYehova, bekhangele phantsi.', 'XHO22'),
(20, 8, 7, 'OoYehuwa, noBhani, noSherebhiya, noYamin, noAkubhe, noSabhetayi, noHodiya, noMahaseya, noKelita, noAzariya, noYozabhadi, noHanan, noPelaya, nabaLevi, babewuqonda umthetho, nabantu bemi ezindaweni zabo.', 'XHO22'),
(20, 8, 8, 'Bandula ke bawufunda encwadini ngokucacileyo umthetho kaThixo baza bawucacisa, babenza bakuqonde oko ukufundwayo.', 'XHO22'),
(20, 8, 9, 'uNehemiya, owayenguTirshata, nombingeleli uEzra, umbhali, nabaLevi ababefundisa abantu, bathi kubo bonke abantu: “Le yimini engcwele kuYehova uThixo wenu; musani ukulila, musani ukulila. Balila bonke abantu bakuweva amazwi omyalelo lowo.', 'XHO22'),
(20, 8, 10, 'Wathi kubo, Hambani niye nidle okutyebileyo, nisele izinto ezinencasa, nise isabelo kwabangalungiselwanga nto; ngokuba le mini yiNkosi yethu; ngokuba uvuyo lukaYehova ligwiba lenu.', 'XHO22'),
(20, 8, 11, 'Bazolisa abaLevi uhlanga lonke, bathi, Zolani, kuba ingcwele le mini; yaye musani ukuba buhlungu.', 'XHO22'),
(20, 8, 12, 'Ngoko bonke abantu baya kudla, basela, babela izabelo, bavuya, ngenxa yokuba babewaqonda amazwi ababeshunyayelwa kuwo.', 'XHO22'),
(20, 8, 13, 'Ngosuku lwesibini iintloko zeentsapho zabo bonke abantu, ababingeleli nabaLevi, zahlanganisana ndawonye noEzra unobhala ukuze baqonde amazwi omthetho.', 'XHO22'),
(20, 8, 14, 'Bayifumana ingcaciso yomyalelo uYehova abewuwisele umthetho ngesandla sikaMoses, wokuba oonyana bakaSirayeli bahlale eminqubeni ngomthendeleko wenyanga yesixhenxe;', 'XHO22'),
(20, 8, 15, 'bakuxele, kuthiwe emizini yabo yonke naseYerusalem, Yiyani entabeni, nithabathe amasebe omnquma, namasebe ompayini, namasebe ommirtile, namasebe esundu, namasebe emithi eshinyeneyo; yenza iminquba njengoko injalo ibhaliwe.', 'XHO22'),
(20, 8, 16, 'Baphuma ke abantu, bawazisa, bazenzela iminquba, elowo phezu kwendlu yakhe, nasezintendelezweni zabo, nasezintendelezweni zendlu kaThixo, nasendaweni yembutho yeSango laManzi, nasebaleni leSango laManzi isikwere sikaEfrayim. Isango.', 'XHO22'),
(20, 8, 17, 'Lonke ibandla elalibuyile ekuthinjweni lenza iminquba, lahlala phantsi kweminquba leyo; ngenxa yokuba ukususela kwimihla kaYoshuwa unyana kaNun de kube yiloo mini oonyana bakaSirayeli babengenzanga loo nto. Kwaye kwabakho uvuyo olukhulu.', 'XHO22'),
(20, 8, 18, 'Walesa encwadini yomyalelo kaThixo imihla ngemihla, kwathabathela kumhla wokuqala kwezisa kowokugqibela. Babhiyoza iintsuku ezisixhenxe; nangomhla wesithoba nowesibini kwabakho umthendeleko ngokumiselwa.', 'XHO22'),
(20, 9, 1, 'Ngosuku lwamashumi omabini anesine lwaloo nyanga, bahlanganisana oonyana bakaSirayeli, ukuba bazile ukudla, banxibe iingubo ezimnyama, benomhlaba.', 'XHO22'),
(20, 9, 2, 'Bazahlula oonyana bakaSirayeli kuzo zonke iintlanga, besuka bazixela izono zabo, nobugwenxa booyise.', 'XHO22'),
(20, 9, 3, 'Bema endaweni yabo baza bafunda inxalenye yesine yemini encwadini yomthetho kaYehova uThixo wabo; esinye isahlulo sesine sasivuma izono zabo, sinqula uYehova uThixo wabo.', 'XHO22'),
(20, 9, 4, 'Wema ezinyukweni zabaLevi ooYeshuwa, noBhani, noKademiyeli, noShebanja, noBhuni, noSherebhiya, noBhani, noKenani, badanduluka ngezwi elikhulu kuYehova uThixo wabo.', 'XHO22'),
(20, 9, 5, 'AbaLevi, ooYeshuwa, noKademiyeli, noBhani, noHasabhenja, noSherebja, noHodiya, noSebanja, noPetaya, bathi, Sukani nime, nimdumise uYehova uThixo wenu ngonaphakade kanaphakade, nilidumise igama lakhe elibekekileyo, eliphakame ngaphezu kwayo yonke indumiso, nemibongo yonke.', 'XHO22'),
(20, 9, 6, 'Wena, ewe, wena unguYehova wedwa; Wena wenza izulu, nezulu lawo amazulu, nomkhosi walo wonke, umhlaba nento yonke ephezu kwawo, iilwandle neento zonke ezikhona, uzidlisa ubomi zonke ezo; kwaye imikhosi yezulu ikunqula.', 'XHO22'),
(20, 9, 7, 'Wena unguYehova uThixo owanyula uAbram waza wamkhupha eUre yamaKhaledi waza wamthiya igama elinguAbraham;', 'XHO22'),
(20, 9, 8, 'Yaye wayimisa intliziyo yakhe enyanisekileyo phambi kwenu waza wenza umnqophiso kunye naye wokunika ilizwe lamaKanan namaHiti nama-Amori namaPersi namaYebhusi namaGirgashi ukuba alinike lona, ndithi, ‘Ndiza kulinika ilizwe lamaKanan namaHiti nama-Amori namaPersi namaYebhusi namaGirgashi ukuba alinike lona. Yithi, wolinika imbewu yakhe. Yehova, ndiwafezekisile amazwi akho; ngokuba ulilungisa;', 'XHO22'),
(20, 9, 9, 'Ndazibona iintsizi zoobawo eYiputa, ndakuva ukukhala kwabo eLwandle oluBomvu;', 'XHO22'),
(20, 9, 10, 'Wenza imiqondiso nezimanga kuFaro, nakubakhonzi bakhe bonke, nakubantu bonke belizwe lakhe, ngokuba ubusazi ukuba banekratshi kubo; Nantso ke indlela owazenzela ngayo igama, njengokuba kunjalo namhlanje.', 'XHO22'),
(20, 9, 11, 'Walwahlula ulwandle phambi kwabo, baluwela ulwandle kowomileyo; Wabaphosa enzulwini ababandezeli babo, Njengelitye emanzini alwayo.', 'XHO22'),
(20, 9, 12, 'Wabakhokela ngelifu emini; nasebusuku ngesibane somlilo, ukubakhanyisela endleleni abafanele bahambe ngayo.', 'XHO22'),
(20, 9, 13, 'Kananjalo wehla waya entabeni yeSinayi, wathetha kubo usemazulwini, wabanika imiyalelo eyinyaniso, nemiyalelo eyinyaniso, nomyalelo olungileyo, nomyalelo olungileyo;', 'XHO22'),
(20, 9, 14, 'wabazisa ngesabatha yakho engcwele, wabawisela umthetho, nemithetho, nemithetho, ngesandla somkhonzi wakho uMoses.', 'XHO22'),
(20, 9, 15, 'Wabanika isonka sasezulwini ngenxa yokulamba kwabo, wabavelisela namanzi engxondorheni ngenxa yokunxanwa kwabo; wabathembisa ukuba baya kulidla ilifa ilizwe, owabaphakamisela isandla sakho, ukuba wobanika lona.', 'XHO22'),
(20, 9, 16, 'Bakhukhumala bona noobawo bethu, bazenza lukhuni iintamo zabo, àbayiphulaphula imithetho yakho.', 'XHO22'),
(20, 9, 17, 'Kwaye akavumanga ukuphulaphula, kwaye akazange acinge ngemimangaliso yakho owayenzayo phakathi kwabo; Wazenza lukhuni iintamo zabo, wabenza umlawuli ezikreqweni zabo, babuyela ebukhobokeni babo; kodwa wena unguThixo oxolelayo, onemfesane, onemfesane, oqinisekileyo, onenceba enkulu, akwabafulathela.', 'XHO22'),
(20, 9, 18, 'Ewe, xa bazenzela ithole elityhidiweyo, bathi, ‘Nguye lo uThixo wenu owanikhuphayo eYiputa, wanizisela intlekele enkulu;', 'XHO22'),
(20, 9, 19, 'Akubashiyanga ke ngobuninzi benceba yakho entlango; Ayesuki kubo isithunzi selifu emini, ukuba abahambe endleleni; kwanesikhuni somlilo ebusuku ukuze sibabonise ukukhanya nendlela abafanele bahambe ngayo.', 'XHO22'),
(20, 9, 20, 'Wabanika umoya wakho olungileyo, ukuba ubafundise, akuyicima imana yakho emlonyeni wabo, wabanika amanzi ngenxa yenxano labo.', 'XHO22'),
(20, 9, 21, 'Wabaxhasa iminyaka emashumi mane entlango, abaswela nto; iimpahla zabo azonakalanga bubudala, neenyawo zabo azidumbanga.', 'XHO22'),
(20, 9, 22, 'Ngapha koko, wabanika izikumkani neentlanga waza wazahlula zaba zimbombo, ukuze balidle ilifa ilizwe lakwaSihon nelizwe likakumkani waseHeshbhon nelizwe likaOgi ukumkani waseBhashan.', 'XHO22'),
(20, 9, 23, 'Wabandisa oonyana babo njengeenkwenkwezi zezulu, wabazisa kwelo zwe, owafunga kooyise ngalo, ukuba baya kulidla ilifa.', 'XHO22'),
(20, 9, 24, 'Bangena ke aba bantwana, balihlutha ilizwe, wabathoba phambi kwabo abemi belo lizwe, amaKanan, wabanikela esandleni sabo, nookumkani bawo nabantu belo lizwe, ukuba bawenze njengoko bebeya kwenza.', 'XHO22'),
(20, 9, 25, 'Bazithimba izixeko ezinqatyisiweyo nomhlaba otyebileyo baza bathimba izindlu ezizele zizinto zonke, amaqula nambiweyo, nezidiliya nemithi yomnquma nemithi yeziqhamo yamininzi; batye, bahluthe, batyebe, badle ukulunga kwakho okukhulu.', 'XHO22'),
(20, 9, 26, 'Bakhukhumala, bagwilika kuwe, babuya emyalelweni wakho, bababulala abaprofeti bakho ababengqina ukubabuyisela kuwe;', 'XHO22'),
(20, 9, 27, 'Ngenxa yoko wabanikela esandleni seentshaba zabo, ezabaqumbisayo; ngokobuninzi benceba yakho wabanika abasindisi, wabahlangula esandleni seentshaba zabo.', 'XHO22'),
(20, 9, 28, 'Ke ekuphumleni kwabo, baya kuphinda benze ububi phambi kwakho; ngenxa yoko wabanikela esandleni seentshaba zabo, ukuba babalawule; wabakhulula ngokuphindaphindiweyo ngeenceba zakho;', 'XHO22'),
(20, 9, 29, 'Bangqine, ukuba ubabuyisele emyalelweni wakho; ke bona bakhukhumala, abayiphulaphula imithetho yakho; bagqitha emithethweni yakho, awothi umntu ewenzile aphile ngayo; bayenza lukhuni igxalaba neentamo zabo, abaphulaphula.', 'XHO22'),
(20, 9, 30, 'Wabanyamezela iminyaka emininzi, wangqina ngabo ngomoya wakho, ngabaprofeti bakho; noko abeva; ke ngoko, wabanikela ezandleni zabantu baloo mazwe.', 'XHO22'),
(20, 9, 31, 'Ngenxa yenceba yakho enkulu, akubatshabalalisanga kuphele, akwabashiya; ngokuba unguThixo onemfesane, unemfesane.', 'XHO22'),
(20, 9, 32, 'Ke kaloku, Thixo wethu, Thixo omkhulu, onamandla, owoyikekayo, owugcinayo umnqophiso nenceba, mabungabi ncinane phambi kwakho bonke ububi obusifikeleyo, nookumkani bethu, nabathetheli bethu, naphezu kwethu ababingeleli nabaprofeti bethu, noobawo, nabantu bakho bonke, kususela kwimihla yookumkani baseAsiriya, unanamhla.', 'XHO22'),
(20, 9, 33, 'Uyilungisa wena kuko konke okusizelayo; ngokuba yena wenze okulungileyo, ke thina senze okubi.', 'XHO22'),
(20, 9, 34, 'Ookumkani bethu, nabathetheli bethu, nababingeleli bethu, noobawo, abawugcinanga umyalelo wakho, abayiphulaphula imithetho yakho, necebo lakho, owabawisela lona.', 'XHO22'),
(20, 9, 35, 'Ngenxa enokuba bengakukhonzanga ebukumkanini babo, nangobabalo lwakho olukhulu owabanikayo, nasezweni elibanzi, elichumayo, owabanikayo; ababuyanga ezenzweni zabo ezimbi.', 'XHO22'),
(20, 9, 36, 'Yabona, singabakhonzi namhla; nelizwe owalinika oobawo, ukuba sidle iziqhamo zalo nokulunga kwalo, nanku singabakhonzi bakho.', 'XHO22'),
(20, 9, 37, 'Ke lona linika isivuno esikhulu kookumkani obamise phezu kwethu ngenxa yezono zethu; Imizimba yethu neenkomo zethu zezabo ngokuthanda kwabo, kwaye sisendlaleni enkulu.', 'XHO22'),
(20, 9, 38, 'Ngezo zinto zonke senza umnqophiso oqinileyo, sawubhala; ziqinise abathetheli bethu, nabaLevi bethu, nababingeleli bethu;', 'XHO22'),
(20, 10, 1, 'Abo boyiswayo nguNehemiya, noTirshata, unyana kaHagaliya, noZedekiya;', 'XHO22'),
(20, 10, 2, 'uSeraya, uAzariya, uYeremiya,', 'XHO22'),
(20, 10, 3, 'uPashkure, uAmariya, uMalekiya,', 'XHO22'),
(20, 10, 4, 'Hattus, Sebanja, Malug,', 'XHO22'),
(20, 10, 5, 'noHarim, noMeremoti, no-Obhadiya,', 'XHO22'),
(20, 10, 6, 'noDaniyeli, noGineton, noBharuki,', 'XHO22'),
(20, 10, 7, 'uMeshulam, uAbhiya, uMiyamin,', 'XHO22'),
(20, 10, 8, 'noMahaseya, noBhilegayi, noShemaya: abo ke babengababingeleli.', 'XHO22'),
(20, 10, 9, 'AbaLevi nguYeshuwa unyana ka-Asaniya, noBhinuwi koonyana bakaHenadade, noKademiyeli;', 'XHO22'),
(20, 10, 10, 'Abazalwana babo: nguShebhaniya, noHodiya, noKelita, noPelaya, noHanan,', 'XHO22'),
(20, 10, 11, 'nguMika, noRehobhi, noHashabhiya,', 'XHO22'),
(20, 10, 12, 'noSakure, noSherebhiya, noShebhaniya,', 'XHO22'),
(20, 10, 13, 'noHodiya, noBhani, noBhenu;', 'XHO22'),
(20, 10, 14, 'Intloko yoluntu; noParoshe, noPahati wakwaMowabhi, noElam, noSatu, noBhani;', 'XHO22'),
(20, 10, 15, 'noBhuni, noAzegadi, noBhebhayi;', 'XHO22'),
(20, 10, 16, 'noAdoniya, noBhigevayi, noAdin;', 'XHO22'),
(20, 10, 17, 'uAtere, uHezekiya, uAzure,', 'XHO22'),
(20, 10, 18, 'noHodiya, noHashum, noBheshayi,', 'XHO22'),
(20, 10, 19, 'iHarif, neAnatoti, neNebhayi,', 'XHO22'),
(20, 10, 20, 'uMagapi, noMeshulam, noHezire,', 'XHO22'),
(20, 10, 21, 'noMesabhele, noTsadoki, noYaduwa;', 'XHO22'),
(20, 10, 22, 'uPelatiya, uHanan, uAnaya,', 'XHO22'),
(20, 10, 23, 'uHoseya, uHananiya, uHashubhi,', 'XHO22'),
(20, 10, 24, 'neHalosheshe, nePileha, neShobeke,', 'XHO22'),
(20, 10, 25, 'uRehum, uHashabhena, noMahaseya,', 'XHO22'),
(20, 10, 26, 'noAhiya, noHanan, noAnan,', 'XHO22'),
(20, 10, 27, 'uMaluke, uHarim, uBhahana.', 'XHO22'),
(20, 10, 28, 'Ke abanye abantu, ababingeleli, nabaLevi, nabamasango, neemvumi, nabakhonzi betempile, nabo bonke abazahlulayo ebantwini bamazwe, ngokomthetho kaThixo, nabafazi babo, noonyana babo, noonyana babo ziintombi bona; bonke abanolwazi nengqondo;', 'XHO22'),
(20, 10, 29, 'Banamathela kubazalwana babo nakwizikhulu zabo, bafunga ngokunjalo, ukuba baya kuwuthobela umyalelo kaThixo owawuwisayo ngoMoses umkhonzi kaThixo, bayigcine yonke imithetho yeNkosi yethu uYehova yigcineni niyenze, nemithetho yayo, nemiyalelo yayo;', 'XHO22'),
(20, 10, 30, 'nokuba iintombi zethu singaziniki abantu beli lizwe, neentombi zabo singazithabathi koonyana bethu.', 'XHO22'),
(20, 10, 31, 'Ukuba abantu belizwe elo bazisa impahla, nokuba yiyiphi na indlela yokuziphilisa, ukuba bethengise ngayo ngomhla wesabatha, asiyi kuwathenga ngesabatha nangomhla ongcwele; nokuba siwushiye umnyaka wesixhenxe, nesibophelelo salo lonke ityala.', 'XHO22'),
(20, 10, 32, 'Kananjalo sizenzele ngokwethu isahlulo sesithathu seshekele ngomnyaka wenkonzo yendlu yoThixo wethu;', 'XHO22'),
(20, 10, 33, 'nezonka zokubonisa, nomnikelo wokudla wamaxesha onke, nedini elinyukayo lamaxesha onke, lesabatha, nelokuthwasa kwenyanga, nelomthendeleko, nelezinto ezingcwele, neledini lesono, ukuba baxolelwe amaSirayeli, kuwo wonke umsebenzi.', 'XHO22'),
(20, 10, 34, 'Sabawisela umthetho ababingeleli, nabaLevi, nabantu, ukuba bawuzise umsondezo weenkuni endlwini yoThixo wethu, ngokwezindlu zoobawo, ngamaxesha amisiweyo iminyaka ngeminyaka, batshiswe ngawo isibingelelo kaYehova uThixo wethu, njengoko kubhaliweyo emthethweni;', 'XHO22'),
(20, 10, 35, 'sizise iintlahlela zamasimi ethu neentlahlela zeziqhamo zonke zemithi yonke endlwini kaYehova iminyaka ngeminyaka;', 'XHO22'),
(20, 10, 36, 'namazibulo oonyana bethu naweenkomo zethu, ngoko kubhaliweyo emyalelweni, namazibulo eenkomo zethu, nawempahla yethu emfutshane, ukuba siwazise endlwini yoThixo wethu, kubabingeleli abalungiselelayo endlwini yoThixo wethu;', 'XHO22'),
(20, 10, 37, 'neentlahlela zomgrayo wethu, nemirhumo yethu, neziqhamo zemithi yonke, newayini entsha, neoli, sizizise kubabingeleli emagumbini okudlela endlu kaThixo wethu; nesishumi semihlaba yethu kubaLevi, ukuze abaLevi bathabathe isishumi semizi yonke yemizi yethu.', 'XHO22'),
(20, 10, 38, 'Umbingeleli unyana ka-Aron woba nabaLevi, ekuthabatheni abaLevi isishumi;', 'XHO22'),
(20, 10, 39, 'Ngokuba oonyana bakaSirayeli noonyana bakaLevi baya kuzisa umsondezo wengqolowa, newayini entsha, newayini entsha, emagumbini okudlela, apho kukho iimpahla zengcwele, nezababingeleli, nabamasango kunye nezo mvumi yaye asiyi kuyishiya indlu yoThixo wethu.', 'XHO22'),
(20, 11, 1, 'Bahlala abathetheli babantu eYerusalem; Abanye abantu bawisa amaqashiso; kwahlala omnye kwabalishumi eYerusalem kuwo umzi ongcwele, nabanye abasithoba kweminye imizi.', 'XHO22'),
(20, 11, 2, 'Uhlanga lwawasikelela amadoda onke aqhutywa yintliziyo ukuba ahlale eYerusalem.', 'XHO22'),
(20, 11, 3, 'Zizo ezo iirhuluneli zelizwe lakwaYuda, ezibe zihleli eYerusalem; ke emizini yakwaYuda, yahlala elowo elifeni lakhe, emizini yabo, oko kukuthi, amaSirayeli, ababingeleli, nabaLevi, nabakhonzi betempile, noonyana babakhonzi bakaSolomon.', 'XHO22'),
(20, 11, 4, 'Bahlala eYerusalem beboonyana bakaYuda noonyana bakaBhenjamin koonyana bakaYuda; uAtaja, unyana ka-Osiya, unyana kaZekariya, unyana ka-Amariya, unyana kaShefatiya, unyana kaMahalaleli, koonyana bakaPerets;', 'XHO22'),
(20, 11, 5, 'noMahaseya, unyana kaBharuki, unyana kaKoloshe, unyana kaHazaya, unyana ka-Adaya, unyana kaYoyaribhi, unyana kaZekariya, unyana kaShilon.', 'XHO22'),
(20, 11, 6, 'Bonke oonyana bakaPerets ababehlala eYerusalem babengamakhulu amane, anamanci mathandathu anesibhozo, amadoda anobukroti.', 'XHO22'),
(20, 11, 7, 'Ngabo aba oonyana bakaBhenjamin; nguSalu, unyana kaMeshulam, unyana kaYuda, unyana kaPedaya, unyana kaKoliya, unyana kaMahaseya, unyana kaItiyeli, unyana kaIsaya.', 'XHO22'),
(20, 11, 8, 'Emva kwakhe nguGabhayi, noSalayi, ibingamakhulu asithoba, anamanci mabini anesibhozo.', 'XHO22'),
(20, 11, 9, 'uYoweli unyana kaTzigri wayengumboniseli wabo, noYuda unyana kaSenwa, engowesibini kumphathi wesixeko.', 'XHO22'),
(20, 11, 10, 'Kubabingeleli: nguYedaya unyana kaYoyaribhi, noYagin;', 'XHO22'),
(20, 11, 11, 'uSeraya, unyana kaHilekiya, unyana kaMeshulam, unyana kaTsadoki, unyana kaMerayoti, unyana ka-Akitubhi, wayephethe indlu kaThixo.', 'XHO22'),
(20, 11, 12, 'Abazalwana babo abenza imisebenzi yasendlini ibingamakhulu asibhozo, anamanci mabini anababini; noAdaya, unyana kaYeroham, unyana kaPelaliya, unyana ka-Amsi, unyana kaZekariya, unyana kaPashkure, unyana kaMalekiya,', 'XHO22'),
(20, 11, 13, 'nabazalwana bakhe, iintloko zezindlu zooyise, bengamakhulu amabini, anamanci mane anababini; noAmasayi, unyana ka-Azareli, unyana ka-Akashayi, unyana kaMeshilemoti, unyana kaImere;', 'XHO22'),
(20, 11, 14, 'nabazalwana babo, amagorha anobukroti, belikhulu elinamanci mabini anesibhozo; umlindi wabo ke ibinguSabhediyeli, unyana womnye wamadoda amakhulu.', 'XHO22'),
(20, 11, 15, 'KubaLevi yayinguShemaya unyana kaHashubhi, unyana ka-Ashrikam, unyana kaHashabhiya, unyana kaBhuni;', 'XHO22'),
(20, 11, 16, 'Yaye uSabhetayi noYozabhadi kwiintloko zabaLevi babephethe umsebenzi ngaphandle kwendlu yoTHIXO oyinyaniso.', 'XHO22'),
(20, 11, 17, 'uMataniya unyana kaMika unyana kaShabhedi unyana ka-Asafu wayeyintloko yokubulela ngomthandazo; noBhakebhukiya, owesibini kubazalwana bakhe, noAbheda, unyana kaSamuwa, unyana kaGalali, unyana kaYedutun.', 'XHO22'),
(20, 11, 18, 'Bonke abaLevi kumzi ongcwele babengamakhulu amabini, anamanci asibhozo anabane.', 'XHO22'),
(20, 11, 19, 'Ngaphezu koko, abagcini-masango, uAkubhe, uTalemon nabazalwana babo ababegada amasango, babelikhulu elinamashumi asixhenxe anesibini.', 'XHO22'),
(20, 11, 20, 'Amasalela akwaSirayeli, nababingeleli, nabaLevi, babekweminye imizi yonke yakwaYuda, elowo eselifeni lakhe.', 'XHO22'),
(20, 11, 21, 'Ke abakhonzi betempile babehleli eOfele, bephethe ooSika noGishpa.', 'XHO22'),
(20, 11, 22, 'Umlindi wabaLevi eYerusalem ibingu-Uzi unyana kaBhani, unyana kaHashabhiya, unyana kaMataniya, unyana kaMika. Oonyana baka-Asafu babeziimvumi zomsebenzi wendlu kaThixo.', 'XHO22'),
(20, 11, 23, 'Ngokuba umthetho wokumkani ubuphezu kwabo, wokuba kubekho isabelo seemvumi ngemini;', 'XHO22'),
(20, 11, 24, 'uPetaya, unyana kaMeshezabheli, koonyana bakaSeraki, unyana kaYuda, ubesesandleni sokumkani kuyo yonke imicimbi yesizwe.', 'XHO22'),
(20, 11, 25, 'Imizi namadlelo ayo, kwahlala koonyana bakaYuda eKiriyati ka-Arbha namadlelo ayo, neDibhon namadlelo ayo, neYekabhetseli namagxamesi ayo;', 'XHO22'),
(20, 11, 26, 'noYeshuwa, neMolada, neBhete-felete;', 'XHO22'),
(20, 11, 27, 'neHatsareli, neBher-shebha, namagxamesi akhona;', 'XHO22'),
(20, 11, 28, 'neTsikelage, neMekona namagxamesi ayo;', 'XHO22'),
(20, 11, 29, 'neEnrimon, neTsarege, neYarmuti;', 'XHO22'),
(20, 11, 30, 'neSanowa, neAdulam namagxamesi ayo, neLagishe namasimi ayo, neAsheki namagxamesi ayo. Bahlala eBher-shebha besa entilini yakwaHinom.', 'XHO22'),
(20, 11, 31, 'Oonyana bakaBhenjamin waseGebha babehlala eMigmas, naseAya, naseBheteli, nasemizini yayo,', 'XHO22'),
(20, 11, 32, 'neAnatoti, neNobhe, neHananiyas,', 'XHO22'),
(20, 11, 33, 'neHatsore, neRama, neGitayim,', 'XHO22'),
(20, 11, 34, 'iHadide, iSebhoyim, neNebhati,', 'XHO22'),
(20, 11, 35, 'Lode naseOno, emfuleni weengcaphephe;', 'XHO22'),
(20, 11, 36, 'KubaLevi kwakukho amaqela kwaYuda nakwaBhenjamin.', 'XHO22'),
(20, 12, 1, 'Ngabo aba ababingeleli nabaLevi, abenyuka noZerubhabheli unyana kaShalatiyeli, noYoshuwa: uSeraya, noYeremiya, noEzra,', 'XHO22'),
(20, 12, 2, 'noAmariya, noMaluke, noHato,', 'XHO22'),
(20, 12, 3, 'uSheganiya, noRehum, noMeremoti,', 'XHO22'),
(20, 12, 4, 'uIdo, uGineto, uAbhiya,', 'XHO22'),
(20, 12, 5, 'iMiyamine, iMaadja, iBilga,', 'XHO22'),
(20, 12, 6, 'uShemaya noYoyaribhi, noYedaya,', 'XHO22'),
(20, 12, 7, 'uShalu, uAmoki, uHilekiya, uYedaya. Aba yayingababingeleli abakhulu nabazalwana babo ngemihla kaYesu.', 'XHO22'),
(20, 12, 8, 'Ke abaLevi: nguYeshuwa, noBhinuwi, noKademiyeli, noSherebhiya, noYuda, noMataniya, obephethe imibulelo, yena nabazalwana bakhe.', 'XHO22'),
(20, 12, 9, 'Abazalwana babo babemalunga neBhuja noUni, belindile.', 'XHO22'),
(20, 12, 10, 'uYeshuwa wazala uYoyakim; uYoyakim wazala uEliyashibhi; uEliyashibhi wazala uYehoyada;', 'XHO22'),
(20, 12, 11, 'uYoyada wazala uYonatan; uYonatan wazala uYaduwa;', 'XHO22'),
(20, 12, 12, 'Ngemihla kaYehoyakim kwakukho ababingeleli, iintloko zezindlu zooyise: okaSeraya, njengoMeraya; kuYeremiya: uHananiya;', 'XHO22'),
(20, 12, 13, 'uEzra, enguMeshulam; kuAmariya: nguYohanan;', 'XHO22'),
(20, 12, 14, 'Van Melicu, Jonathan; kwaSebanja: uJoseph;', 'XHO22'),
(20, 12, 15, 'kwaHarim, eAdena; eMerayoti, inguHelekayi;', 'XHO22'),
(20, 12, 16, 'eIdo nguZekariya; eGineton nguMeshulam;', 'XHO22'),
(20, 12, 17, 'Koonyana baka-Abhiya nguSigri; ekaMinyamine, ekaMowa, ekaPilitayi;', 'XHO22'),
(20, 12, 18, 'yakwaBhiliga, inguSamuwa; kuShemaya: nguYonatan;', 'XHO22'),
(20, 12, 19, 'kuYoyaribhi: nguMatenayi; ekaYedaya ingu-Uzi;', 'XHO22'),
(20, 12, 20, 'eSalayi, eKalayi; oka-Amoki nguEbhere;', 'XHO22'),
(20, 12, 21, 'kuHilekiya: nguHashabhiya; ekaYedaya inguNataniyeli.', 'XHO22'),
(20, 12, 22, 'AbaLevi ngemihla kaEliyashibhi, uYehoyada noYohanan noYaduwa babhalwa njengeentloko zeentsapho zabo, nababingeleli kwada kwakulawula uDariyo umPersi.', 'XHO22'),
(20, 12, 23, 'Oonyana bakaLevi, iintloko zezindlu zooyise, babebhalwe encwadini yemicimbi, kwada kwayimihla kaYohanan unyana kaEliyashibhi.', 'XHO22'),
(20, 12, 24, 'Iintloko zabaLevi yayinguHashabhiya, uSherebhiya, uYeshuwa unyana kaKademiyeli, nabazalwana babo ababephambi kwabo ukuze benze indumiso nendumiso ngokomyalelo kaDavide umfo wakwaThixo wokwenyaniso.', 'XHO22'),
(20, 12, 25, 'uMataniya, noBhakebhukiya, no-Obhadiya, noMeshulam, noTalemon, noAkubhe babengabagcini bamasango, begcina abagcini bamasango.', 'XHO22'),
(20, 12, 26, 'Kwakungemihla kaYehoyakim unyana kaYeshuwa unyana kaYosadaki nangemihla yerhuluneli uNehemiya nombingeleli uEzra umbhali.', 'XHO22'),
(20, 12, 27, 'Ekusungulweni kodonga lwaseYerusalem, kwafunwa abaLevi kuzo zonke iindawo zabo ukuze babazise eYerusalem, ukuze kusungulwe ngovuyo, ngombulelo ngengoma ngamacangci ngeehadi ngeehadi.', 'XHO22'),
(20, 12, 28, 'Bahlanganisana oonyana beemvumi entlango ngeenxa zonke eYerusalem nasemizini yaseNetofati;', 'XHO22'),
(20, 12, 29, 'nabendlu yaseGiligali, nelizwe laseGebha, neAshmaveti; ngokuba iimvumi zazakha imizi ngeenxa zonke eYerusalem.', 'XHO22'),
(20, 12, 30, 'Bazihlambulula ababingeleli nabaLevi, nabantu, namasango, nodonga olo.', 'XHO22'),
(20, 12, 31, 'Ndanyusa iinkosana zakwaYuda ndanyusa udonga ndaza ndabeka iingoma ezimbini zokudumisa, yaye enye yazo yema ngasekunene kodonga ukuya kwisango laseMohili.', 'XHO22'),
(20, 12, 32, 'Balandela ooHoshaya nesiqingatha sabathetheli bakwaYuda,', 'XHO22'),
(20, 12, 33, 'noAzariya, noEzra, noMeshulam;', 'XHO22'),
(20, 12, 34, 'uYuda noBhenjamin, uShemaya noYeremiya;', 'XHO22'),
(20, 12, 35, 'Koonyana bababingeleli baqupha ukuvuthela ngezigodlo; nguZekariya, unyana kaYonatan, unyana kaShemaya, unyana kaMataniya, unyana kaMikaya, unyana kaSagure, unyana ka-Asafu;', 'XHO22'),
(20, 12, 36, 'Abazalwana bakhe yayinguShemaya noAzareli noMilalayi noGilalayi noMahayi noNataniyeli noYuda noHanani ababephambi kwabo bephethe izixhobo zikaDavide uTHIXO oyinyaniso noEzra umbhali.', 'XHO22'),
(20, 12, 37, 'Bemi ngasesangweni lequla, malungana nabo, benyuka ngezinyuko zomzi kaDavide, ethambekeni lodonga phezu kwendlu kaDavide, besa eSangweni laManzi ngasempuma.', 'XHO22'),
(20, 12, 38, 'Ndaqubisana nabo nenye inyambalala yengoma, mna ngasemva kwabo, ndinesiqingatha sabantu, phezu kodonga, inqaba ende yeziko, sesa eludongeni olubanzi;', 'XHO22'),
(20, 12, 39, 'yaye ukususela encotsheni yeSango lakwaEfrayim ladlula kwiSango Elidala ladlula kwiSango Leentlanzi nakwinqaba ende kaHananeli nenqaba yaseMege ukuya kutsho kwiSango lezimvu; bema esangweni lentolongo.', 'XHO22'),
(20, 12, 40, 'Ngoko izihlwele ezibini zomculo zema endlwini yoTHIXO oyinyaniso, yaye mna nesiqingatha sabalawuli sasikunye nam;', 'XHO22'),
(20, 12, 41, 'nababingeleli; uEliyakim, noMahaseya, uMiniyamin, uMikaya, uEliyohenayi, uZekariya, uHananiya, benamaxilongo;', 'XHO22'),
(20, 12, 42, 'noMahaseya, noShemiya, noElazare, noUzi, noYohanan, noMalekiya, noElam, noEzere. Kwaye iimvumi zavuma ngokuvakalayo, kunye noJísraja, umgcini wabo.', 'XHO22'),
(20, 12, 43, 'Babingelela ngaloo mini imibingelelo emikhulu, bavuya; ngokuba uThixo ubebenzele uvuyo olukhulu; abafazi nabantwana bavuya, kangangokuba ukuvuya kweYerusalem kwavakala kude.', 'XHO22'),
(20, 12, 44, 'Ngelo xesha kwamiselwa abanye abaza kongamela amagumbi obuncwane neminikelo yentlahlela nesishumi, ukuze kuqokelelwe izabelo ezisemthethweni zababingeleli nabaLevi kumasimi ezixeko; uYuda wabavuyela ababingeleli nabaLevi ababelindile.', 'XHO22'),
(20, 12, 45, 'Iimvumi nabagcini-masango bagcina umlindo kaThixo wabo nomlindo wokusulungekiswa ngokomyalelo kaDavide noSolomon unyana wakhe.', 'XHO22'),
(20, 12, 46, 'Ngenxa yokuba ngemihla kaDavide noAsafu kwakudala kwakukho iintloko zeemvumi neengoma zokudumisa nemibulelo ezizukisa uThixo.', 'XHO22'),
(20, 12, 47, 'Onke amaSirayeli ngemihla kaZerubhabheli nangemihla kaNehemiya ayenikela izabelo zeemvumi nabagcini bamasango, suku ngalunye; yaye bathembisa abaLevi ngezinto ezingcwele; yaye abaLevi bakuthembisa oonyana baka-Aron.', 'XHO22'),
(20, 13, 1, 'Kwangaloo mini bafunda encwadini kaMoses phambi kwabantu; kwaye kwafunyanwa kubhaliwe kuyo ukuba ama-Amon namaMowabhi akayi kungena ebandleni likaThixo ngonaphakade;', 'XHO22'),
(20, 13, 2, 'ngokuba engabakhawulelanga oonyana bakaSirayeli enesonka namanzi; asuka aqesha uBhileham, ukuba abaqalekise; ke uThixo wethu wayiguqula ingqalekiso yaba yintsikelelo.', 'XHO22'),
(20, 13, 3, 'Bawuva umyalelo lowo, bayihlula yonke ingxubevange yakwaSirayeli;', 'XHO22'),
(20, 13, 4, 'uEliyashibhi umbingeleli, umgcini wegumbi lendlu yoThixo wethu, ubesisicaka sikaTobhiya.', 'XHO22'),
(20, 13, 5, 'Wamlungiselela igumbi elikhulu, ababebeke kulo ngaphambili iminikelo etshiswayo, isiqhumiso neembiza, nesahlulo seshumi sengqolowa nesewayini neseoli esasetyenziselwa sona wathi, mabanikwe abaLevi neemvumi nabagcini-masango; neminikelo yababingeleli.', 'XHO22'),
(20, 13, 6, 'Ebudeni beli xesha lonke bendingekho eYerusalem, ngenxa yokuba, ngomnyaka wamashumi amathathu anesibini ka-Artashashta ukumkani waseBhabheli, ndeza kukumkani, ndaza emva kweentsuku ezithile ndacela kukumkani.', 'XHO22'),
(20, 13, 7, 'Ndafika eYerusalem ndabuqonda ububi obenziwe nguEliyashibhi kuTobhiya ngokumlungiselela igumbi ezintendelezweni zendlu yoTHIXO oyinyaniso.', 'XHO22'),
(20, 13, 8, 'Ndakhathazeka kakhulu, ngenxa yoko ndazilahlela phandle zonke iimpahla zendlu kaTobhiya ngaphandle kwegumbi.', 'XHO22'),
(20, 13, 9, 'Ndatsho ke, bawahlambulula amagumbi; ndazibuyisela khona iimpahla zendlu kaThixo, namadini anyukayo, neziqhumiso.', 'XHO22'),
(20, 13, 10, 'Ndaqonda ukuba abaLevi babengazinikwanga izabelo zabo; ngokuba abaLevi neemvumi ababesebenzela bona basaba, elowo waya ezweni lakowabo.', 'XHO22'),
(20, 13, 11, 'Ndabakhalimela abathetheli, ndathi, Indlu kaThixo ishiyiwe? Ndabaqokelela ndaza ndazibeka endaweni yazo.', 'XHO22'),
(20, 13, 12, 'Ngoko onke amaYuda azisa isishumi sengqolowa nesewayini neseoli koovimba.', 'XHO22'),
(20, 13, 13, 'Ndamisa phezu kobuncwane phezu kobuncwane: uShelemiya umbingeleli, noTsadoki umbhali, noPedaya kubaLevi; ngakubo nguHanan unyana kaSakure, unyana kaMataniya; ngenxa yokuba bekuthiwe bathembekile, nomsebenzi wabo wabelwe abazalwana babo.', 'XHO22'),
(20, 13, 14, 'Camagu, Thixo wam, ndikhumbule kule ndawo, ungazishiyi iinceba zam, endizenzileyo endlwini kaThixo wam, nakwizenzo zayo.', 'XHO22'),
(20, 13, 15, 'Ngaloo mihla ndabona kwaYuda indlela abathile ababexovula ngayo izixovulelo zewayini ngesabatha nabazizisi abaninzi nabakhwelisa amaesile; newayini, neediliya, namakhiwane, neentlobo zonke zemithwalo, ababezizisa eYerusalem ngomhla wesabatha; yaye ndanikela ubungqina kubo mhla babethengisa ukutya.', 'XHO22'),
(20, 13, 16, 'Kwaye kukho nabafo baseTire, ababemi khona, besiza neentlanzi neento zonke, bethengisa ngazo ngesabatha koonyana bakaYuda nabaseYerusalem.', 'XHO22'),
(20, 13, 17, 'Ndabambana nezikhulu zakwaYuda, ndathi kubo, Bububi buni na obu nibenzayo, niwuzukisa nje umhla wesabatha?', 'XHO22'),
(20, 13, 18, 'Asingooyihlo na abasenzayo, asibangelanga na uThixo wethu yonke le ntlekele phezu kwethu naphezu kwesi sixeko? noko niyandisa ingqumbo kaSirayeli, niyihlambela nje isabatha.', 'XHO22'),
(20, 13, 19, 'Kwathi kwakuba mnyama amasango aseYerusalem ngaphambi kwesabatha, ndathi makavalwe amasango, ndathi makavulwe kuphela emva kwesabatha; emasangweni, kungathwaliswa mthwalo ngomhla wesabatha.', 'XHO22'),
(20, 13, 20, 'Ke abarhwebi, nabathengisa ngeento zonke ezithengiswayo, babehlala ngaphandle kweYerusalem kanye nakabini.', 'XHO22'),
(20, 13, 21, 'Ndaqononondisa ndathi kubo, Yini na ukuba nilale eludongeni? ukuba nithe naphinda, ndonisa isandla. Ukususela ngelo xesha ababanga saphinda beze ngesabatha.', 'XHO22'),
(20, 13, 22, 'Ndathi kubaLevi mabazihlambulule, baze balinde amasango ukuze kungcwaliswe umhla wesabatha. Ndikhumbule, Thixo wam, undixolele ngobuninzi benceba yakho.', 'XHO22'),
(20, 13, 23, 'Ngelo xesha ndawabona amaYuda abezeke abafazi baseAshdode, nama-Amon, namaMowabhi;', 'XHO22'),
(20, 13, 24, 'Ke sona isiqingatha soonyana babo babethetha ulwimi lwaseAshdode, bengakwazi ukuthetha isiYuda, kodwa bethetha ngolwimi lweentlanga zonke.', 'XHO22'),
(20, 13, 25, 'Ndalwa nawo, ndawatshabhisa, ndabetha amadoda kuwo, ndawanyothula iinwele, ndawafungela uThixo, ndathi, Ize ninganikeli iintombi zenu koonyana babo, oonyana benu ningabazekeli oonyana benu, iintombi zabo ingenguwe okanye wena.', 'XHO22'),
(20, 13, 26, 'Wayengone na uSolomon, ukumkani wakwaSirayeli? Noko ke, ezintlangeni ezininzi kwakungekho kumkani unjengaye, ethandwa nguThixo wakhe, yaye uThixo wamenza ukumkani kumaSirayeli onke; nangona kunjalo, uyabagxeka abanye abafazi.', 'XHO22'),
(20, 13, 27, 'Ngaba siya kuniphulaphula na xa nenza obu bubi bukhulu ngokona kuThixo wethu ngokutshata abafazi basemzini?', 'XHO22'),
(20, 13, 28, 'Ke koonyana bakaYehoyada, unyana kaEliyashibhi, umbingeleli omkhulu, bekukho kubo ongumyeni kwaSanebhalati waseHoron; ke ngoko, ndamgxotha kum.', 'XHO22'),
(20, 13, 29, 'Thixo wam, bakhumbule, ngenxa yokuhlambela kwabo ububingeleli, nomnqophiso wobubingeleli, nowobuLevi.', 'XHO22'),
(20, 13, 30, 'Ndabahlambulula ke koonyana bonke bolunye uhlanga, ndamisa izigxina zababingeleli nabaLevi, elowo emsebenzini wakhe;', 'XHO22'),
(20, 13, 31, 'nezomnikelo weenkuni ngamaxesha amisiweyo nezeentlahlela. Ndikhumbule, Thixo wam, ngenxa yam;', 'XHO22'),
(21, 1, 1, 'Ngemihla ka-Assuwerosi ibinguAsuwerosi obelawula amazwe alikhulu elinamanci mabini anesixhenxe, ethabathela eIndiya, wesa kwaKushi,', 'XHO22'),
(21, 1, 2, 'ngelo xesha, ukumkani uAsuwerosi ebehleli etroneni yobukumkani bakhe kumzi waseShushan;', 'XHO22'),
(21, 1, 3, 'Ngomnyaka wesithathu wobukumkani bakhe wabenzela isidlo abathetheli bakhe nabakhonzi bakhe; Igunya lamaPersi namaMedi, nabathetheli, nabathetheli bamazwe, baphambi kwakhe;', 'XHO22'),
(21, 1, 4, 'Ekubonakaliseni kwakhe ubutyebi obuzukileyo bobukumkani bakhe, nobuqaqawuli bobungangamsha bobuhle bakhe imihla emininzi, eziintsuku ezilikhulu elinamanci asibhozo anesibini.', 'XHO22'),
(21, 1, 5, 'Ekupheleni kwaloo mihla, ukumkani wabenzela isidlo bonke abantu ababeseShushan komkhulu iintsuku zasixhenxe, kwathabathela koyena mkhulu kwesa komncinane, entendelezweni yomyezo wendlu yokumkani;', 'XHO22'),
(21, 1, 6, 'Amawunduwundu aba ke ngelinen ecikizekileyo, nemfusa, ebotshelelwe emakhonkcweni esilivere, neentsika zemarmore emhlophe;', 'XHO22'),
(21, 1, 7, 'Basela ngeempahla zegolide (ezingezinye) newayini yokumkani ibeninzi, ngokwesigxina sokumkani.', 'XHO22'),
(21, 1, 8, 'Ukusela kwakusemthethweni; bekungekho bani ukunyanzelayo, ngokuba ukumkani ubemise bonke abagcini bendlu yakhe, ukuba benze njengoko babefuna bonke abantu.', 'XHO22'),
(21, 1, 9, 'Ukumkanikazi uVashti wenzela abafazi isidlo kwibhotwe likakumkani uAhaswerosi.', 'XHO22'),
(21, 1, 10, 'Kwathi ngomhla wesixhenxe, yakuchwayitha intliziyo yokumkani yiwayini, wabawisela umthetho ooMehumane, noBhishta, noHarbhona, noBhigeta, noAbhagta, noSetar, noKarkas, abalungiseleli abasixhenxe phambi kokumkani uAhaswerosi;', 'XHO22'),
(21, 1, 11, 'mabamzise uVashti, ukumkanikazi phambi kokumkani, enesithsaba sobukumkani, ukuba abonise abantu nabathetheli ubuhle bakhe;', 'XHO22'),
(21, 1, 12, 'Akavumanga ukumkanikazi uVashti ukuza ngelizwi lokumkani ngabakhonzi bakhe; ke ngoko, ukumkani waba nomsindo kakhulu, waza wavutha umsindo wakhe ngaphakathi kwakhe.', 'XHO22'),
(21, 1, 13, 'Wandula ke ukumkani wathi kwizilumko ezaziwazi amaxesha, ekubeni isimo sengqondo sikakumkani sasinjalo kubo bonke ababewazi umthetho nokusesikweni.', 'XHO22'),
(21, 1, 14, 'Ngakuye ibinguKarsena, noSetar, neAdmata, noTarshishe, noMerese, noMarsena, noMemukane, iinkosana ezisixhenxe zamaPersi namaMedi, ezabubonayo ubuso bokumkani, ukuba zibe ngowokuqala ukuhlala ebukumkanini.', 'XHO22'),
(21, 1, 15, 'Ngokomthetho, siya kuthini na ngoKumkanikazi uVashti, ekubeni engazange awuphumeze umyalelo kaKumkani uAhaswerosi nabalungiseleli?', 'XHO22'),
(21, 1, 16, 'Waphendula uMemukane phambi kokumkani namaphakathi, wathi, Ukumkanikazi uVashti akone kukumkani yedwa;', 'XHO22'),
(21, 1, 17, 'Kuba esi senzo sokumkanikazi siya kubonakala kubafazi bonke, bawadelele amadoda abo emehlweni abo, xa kuthiwa, Ukumkani uAsuwerosi wathi, mayiziswe phambi kwakhe inkosikazi uVashti, yenjenjalo ungezi.', 'XHO22'),
(21, 1, 18, 'Namhlanje ookumkanikazi basePersi naseMedi baya kuzixela zonke iinkosana zokumkani eziyivileyo le nto yokumkanikazi; Kuya kuvela indelelo eninzi nomsindo.', 'XHO22'),
(21, 1, 19, 'Ukuba kubonakala kulungile kukumkani, mawuhle uye kuye umthetho wasebukhosini, ubhalwe emthethweni wamaPersi namaMedi, ukuze ungaguquki, ukuba uVashti akezi kwakhona phambi kokumkani uAhaswerosi; Ukumkani makanikele ubuncwane bakhe kolungileyo kunaye.', 'XHO22'),
(21, 1, 20, 'Emva komyalelo kakumkani uya kwaziwa ebukumkanini bakhe bonke (ngokuba bukhulu), bonke abafazi bafanele bawahlonele amadoda abo, amakhulu namancinane.', 'XHO22'),
(21, 1, 21, 'Lalunga elo lizwi kukumkani nabathetheli; Wenza ke ukumkani ngokwelizwi likaMemukane.', 'XHO22'),
(21, 1, 22, 'Wathumela iincwadi emazweni onke kakumkani, kwisithili ngasinye ngokweencwadi zaso, kwisizwe ngasinye ngokweentetho zaso, ukuba silawule elowo endlwini yakhe, ukuba abonakale ngokwentetho yaso wonke umntu umntu.', 'XHO22'),
(21, 2, 1, 'Kwathi ke, wakudamba umsindo kakumkani uAhaswerosi, wamkhumbula uVashti, noko abekwenzile, noko abemwisele umthetho ngako.', 'XHO22'),
(21, 2, 2, 'Bathi abakhonzi bokumkani ababelungiselela kuye, Ukumkani makafunelwe iintombi ezinyulu.', 'XHO22'),
(21, 2, 3, 'Ukumkani makanyule abaphathi kuwo onke amaphondo obukumkani bakhe, ukuba bahlanganise zonke iintombi ezinyulu ezibukekayo endlwini yasebukhosini endlwini yabafazi, phantsi kwesandla sikaHege, umlindi wendlu kakumkani, umlindi wendlu kakumkani, banikwa izinto zabo ezihlambulukileyo.', 'XHO22'),
(21, 2, 4, 'Yaye intombi ekholekileyo kukumkani mayibe yinkosikazi esikhundleni sikaVasheti. Yalunga loo nto kukumkani; Yaye wenjenjalo.', 'XHO22'),
(21, 2, 5, 'Ke kaloku kwakukho indoda engumYuda eShushan komkhulu, egama linguMordekayi, unyana kaYayiro, unyana kaShimehi, unyana kaKishe, umBhenjamin;', 'XHO22'),
(21, 2, 6, 'owayethatyathelwe ekuthinjweni eYerusalem nabathinjwa uYekoniya, ukumkani wakwaYuda, lowo uNebhukadenetsare ukumkani waseBhabhiloni wabathabatha waya ekuthinjweni.', 'XHO22'),
(21, 2, 7, 'Nguye owayekhulisa uHadasa, oko kukuthi, uEstere, intombi kayisekazi; ngokuba ebengenayise nanina; kwathi ekufeni kukayise nonina uMordekayi wayithabatha yaba yintombi yakhe.', 'XHO22'),
(21, 2, 8, 'Kwathi kwakuvakala umyalelo nomyalelo kakumkani, yaye ekuhlanganiseni kwabafazi abaninzi ebhotwe eSusan phantsi kwesandla sikaHegayi, noEstere waziswa endlwini kakumkani phantsi kweliso likaHegayi, umlindi wabafazi.', 'XHO22'),
(21, 2, 9, 'Yamkholisa intombi leyo, wamkeleka kuye; wakhawuleza, wamnika iimpahla zakhe zokuhlambela, neento zonke abenazo, neentombi ezisixhenxe zendlu yokumkani, awayeza kuzinikwa; wayinyula yona nomthinjana wayo phezu kweyona ndawo intle kwindlu yabafazi.', 'XHO22'),
(21, 2, 10, 'uEstere akazange abaxelele abantu bakowabo nentsapho yakhe, kuba uMordekayi wayemyalele ukuba angabaxeleli.', 'XHO22'),
(21, 2, 11, 'uMordekayi wayemana ukuhamba imihla ngemihla yonke phambi kwentendelezo yendlu yabafazi, ukuba abone ukuphila kukaEstere, noko kuya kwenzeka kuye.', 'XHO22'),
(21, 2, 12, 'Kwathi, ekufikeni kwentombi nganye kukumkani uAhaswerosi, ineenyanga ezilishumi elinambini ngokwesiko labafazi, ngokuba yayizalisekile imihla yokuhlanjululwa kwabo, zaba ziinyanga ezintandathu ngeoli yemore, neenyanga ezintandathu ngeziqholo, kunye nezinye iimveliso zococeko lobufazi;', 'XHO22'),
(21, 2, 13, 'Zeza ngolo hlobo ke zonke iintombi kukumkani; yonke into ayifunayo wanikwa ukuba ihambe naye, isuke endlwini yabafazi, iye endlwini yokumkani.', 'XHO22'),
(21, 2, 14, 'Wemka ngaloo ngokuhlwa, yaye ngengomso wabuyela kwindlu yabafazi besibini, phantsi kweliso likaShehasegasi, umlindi wendlu kakumkani, umlindi wezithende nokuba ukhankanywe ngegama.', 'XHO22'),
(21, 2, 15, 'Kwathi, kwakufika ixesha likaEstere, intombi ka-Abhihayili, uyisekazi kaMordekayi, obeyithabathe ukuba abe yintombi yakhe, angene kukumkani, akafuna nto, ingenguHegayi, ithenwa lokumkani, umgcini wendlu yenkosi ikumkani abafazi, abanyuliweyo. Yaye uEstere wabonakala ekholekile emehlweni abo bonke ababembona.', 'XHO22'),
(21, 2, 16, 'Wasiwa ke uEstere kukumkani uAhaswerosi, ebhotwe lakhe, ngenyanga yeshumi, oko kukuthi, inyanga enguTebhete, ngomnyaka wesixhenxe wobukumkani bakhe.', 'XHO22'),
(21, 2, 17, 'uYehova wamthanda uEstere ngaphezu kwabafazi bonke; Ngoko wamthwesa isithsaba sobukumkani entlokweni yakhe, wamenza ukumkanikazi esikhundleni sikaVashti.', 'XHO22'),
(21, 2, 18, 'Ukumkani wabenzela bonke abathetheli bakhe nabakhonzi bakhe isidlo esikhulu, isidlo sikaEstere; wakhulula amazwe, wanikela izipho ngokwemeko yokumkani.', 'XHO22'),
(21, 2, 19, 'Zithe ke zahlanganisana okwesibini iintombi ezo, waye uMordekayi ehleli esangweni lokumkani.', 'XHO22'),
(21, 2, 20, 'uEstere akazange ayichaze intsapho yakowabo nentsapho yakowabo; njengoko uMordekayi abemwisele umthetho; ngenxa yokuba uEstere wawuthobela umyalelo kaMordekayi kanye njengoko wayekhuliswe nguye.', 'XHO22'),
(21, 2, 21, 'Ngaloo mihla, ngoxa uMordekayi wayehleli esangweni likakumkani, abamaphakathi ababini bokumkani, uBhigetan noTeres, abagcini-masango, bacaphuka baza bafuna ukumsa izandla ukumkani uAsuwerosi.', 'XHO22'),
(21, 2, 22, 'Yaziwa loo nto nguMordekayi, waxelela uEstere ukumkanikazi; yaye uEstere wavakalisa ukumkani wayo egameni likaMordekayi.', 'XHO22'),
(21, 2, 23, 'Yancinwa loo nto, yafunyanwa; ke ngoko, bobabini baxhonywa emthini;', 'XHO22'),
(21, 3, 1, 'Emva koko ukumkani uAsuwerosi wamphakamisa uHaman, unyana kaHamedata, umAgagi, wamphakamisa, wambeka etroneni yakhe ngaphezu kwabathetheli bonke ababenaye.', 'XHO22'),
(21, 3, 2, 'Bonke abakhonzi bokumkani, ababesesangweni lokumkani, baqubuda, baqubuda kuHaman; ngokuba ukumkani ubewise umthetho wenjenjalo ngaye. Ke yena uMordekayi akaqubuda kuye, akamhlonela;', 'XHO22'),
(21, 3, 3, 'Bathi abakhonzi bokumkani, ababesesangweni lokumkani kuMordekayi, Yini na ukuba uwaphule umthetho wokumkani?', 'XHO22'),
(21, 3, 4, 'Ke kaloku, bathetha naye imini yonke, akabaphulaphula; bamxelela uHaman, ukuba aya kuma na amazwi kaMordekayi, kuba ubebaxelele ukuba yena ungumYuda.', 'XHO22'),
(21, 3, 5, 'Wabona uHaman ukuba uMordekayi akaguqi, nokuba akamzukisa; wavutha ngumsindo uHaman.', 'XHO22'),
(21, 3, 6, 'Wagweba uMordekayi yedwa ukuba amse izandla; ngokuba bambonise abantu bakuloMordekayi; ngoko ke, uHaman wazama ukuwatshabalalisa onke amaYuda kubo bonke ubukumkani baseAsswerosi, abantu bakaMordekayi.', 'XHO22'),
(21, 3, 7, 'Ngenyanga yokuqala, oko kukuthi, inyanga enguNisan, ngonyaka weshumi elinesibini kaKumkani uAhaswerosi, benza iPuri, oko kukuthi, amaqashiso, phambi koHaman yonke imihla nenyanga nganye, kwada kwayinyanga yeshumi elinesibini, oko kukuthi inyanga enguAdare.', 'XHO22'),
(21, 3, 8, 'Wathi uHaman kukumkani uAhaswerosi, Kukho olunye uhlanga oluthe saa, oluthe saa ezintlangeni emazweni onke obukumkani bakho; nemithetho yabo yahlukile kweyabo bonke abantu; nemithetho yokumkani abayigcini; ke ngoko, akulungile ukuba ukumkani anyamezele.', 'XHO22'),
(21, 3, 9, 'Ukuba kulungile kukumkani, makubhalwe ukuba zitshatyalaliswe, yaye ndonikela ishumi lamawaka eetalente zesilivere esandleni sabo banomcimbi wokuzizisa kwindawo yokugcina ubuncwane kakumkani.', 'XHO22'),
(21, 3, 10, 'Ukumkani wawukhulula umsesane wakhe esandleni sakhe, wawunika uHaman, unyana kaHadete, umAgagi, umbandezeli wamaYuda.', 'XHO22'),
(21, 3, 11, 'Wathi ukumkani kuHaman, Unikiwe isilivere, wena nabantu; yenza kubo ngokokuthanda kwakho.', 'XHO22'),
(21, 3, 12, 'Babizwa ke ababhali bakakumkani ngomhla weshumi elinesithathu wenyanga yokuqala, kwaza kwabhalwa ngokoko konke awakuthethayo uHaman kubaphathiswa bakakumkani, neerhuluneli ezaziphethe isithili ngasinye, nakubalawuli bezizwe zonke ezivela kumazwe ngamazwe amanye amazwe isithili ngasinye sinombhalo waso, kwaye isizwe ngasinye sinolwimi lwaso; yabhalwa egameni likaKumkani uAhaswerosi yaza yatywinwa ngomsesane kakumkani.', 'XHO22'),
(21, 3, 13, 'Kwathunyelwa iincwadi kuwo onke amaphondo kakumkani, ukuba kutshatyalaliswe, kubulawe, kutshatyalaliswe onke amaYuda, kuthabathele komncinane kuse kwixhego, abantwana nabafazi, ngamini-nye, ngomhla weshumi elinesithathu lweshumi elinesibini elinesibini yinyanga enguAdare ke, niyiphange imfuyo yabo.', 'XHO22'),
(21, 3, 14, 'Ikopi yomthetho, eyayiza kuwiswa iphondo ngalinye, yaziswa kuzo zonke iintlanga, ukuba ziya kuhlala zilungile loo mini.', 'XHO22'),
(21, 3, 15, 'Amanani aphuma ngokukhawuleza ngokomyalelo kakumkani, yaye umyalelo weza kuSusan ebhotwe. Ukumkani noHaman bahlala phantsi, basela; kodwa isixeko sakwaSusan sasisiphithiphithi.', 'XHO22'),
(21, 4, 1, 'Akukubona konke okwenzekileyo uMordekayi, uMordekayi wazikrazula iingubo zakhe, wambatha iingubo ezimnyama zokuzila nothuthu;', 'XHO22'),
(21, 4, 2, 'Wafika phambi kwesango lokumkani, kuba akukho mntu unokungena esangweni lokumkani, enxibe iimpahla zokuzila.', 'XHO22'),
(21, 4, 3, 'Yaye kuwo onke amaphondo apho umyalelo kakumkani nomyalelo wakhe wawufikele khona, kwakukho ukuzila okukhulu phakathi kwamaYuda, nokuzila ukutya nokulila nokulila; abaninzi balala benxibe amadlavu nothuthu.', 'XHO22'),
(21, 4, 4, 'Zeza ke iimpelesi zikaEstere nabakhonzazana bakhe, bamxelela. Waba buhlungu kakhulu ukumkanikazi; Wathumela iingubo zokumambesa uMordekayi, wamsusa ezirhwexayo;', 'XHO22'),
(21, 4, 5, 'Wandula ke uEstere wabiza uHathage, omnye wabalungiseleli bakakumkani, lowo wayemiselwe ngukumkani ukuba amnyamekele, waza wamyalela ukuba axelele uMordekayi ukuze azi ukuba yintoni kanye kanye le nto yaye kutheni.', 'XHO22'),
(21, 4, 6, 'Waphuma uHataki, waya kuMordekayi ebaleni lesixeko phambi kwesango lokumkani.', 'XHO22'),
(21, 4, 7, 'uMordekayi wamana emxelela konke okumhleleyo, nemali leyo uHaman awayethe uya kuyifaka kuvimba kakumkani, ukuze atshatyalaliswe ngamaYuda.', 'XHO22'),
(21, 4, 8, 'Wamnika nekopi yencwadi leyo yayinikwe uSusan, yokuba batshatyalaliswe, ukuze amxelele uEstere, amazise, amyale ukuba aye kubuza kukumkani kuye nokucela kuye abantu bakhe.', 'XHO22'),
(21, 4, 9, 'Weza uHatage, wamxelela uEstere amazwi kaMordekayi.', 'XHO22'),
(21, 4, 10, 'Wabuya uEstere wathetha kuHathage, wathi makayizise kuMordekayi;', 'XHO22'),
(21, 4, 11, 'Bonke abakhonzi bokumkani nabantu abaseziphaleni zokumkani bayazi ukuba nabani na, nokuba yindoda nokuba yinkazana, othe eze kukumkani entendelezweni engaphakathi, engabizwanga, mnye umthetho wokuba makabulawe, umiselwe ukumkani ngaphandle kwabo ukumkani aya kubaphathela intonga yobukhosi yegolide, ukuze baphile; kodwa andizange ndibizwe ukuba ndiye kukumkani ngaloo mihla imashumi mathathu.', 'XHO22'),
(21, 4, 12, 'Bamxelela uMordekayi amazwi kaEstere.', 'XHO22'),
(21, 4, 13, 'Ngoko uMordekayi wayalela ukuba impendulo inikwe uEstere: “Musa ukuzixelela ukuba uya kusinda wena endlwini yokumkani ngaphezu kwamaYuda onke.', 'XHO22'),
(21, 4, 14, 'Kuba xa nithe nathula cwaka ngeli xesha, ukwaphuka nosindiso luya kubonakala kwenye indawo kumaYuda; ke wena nendlu kayihlo niya kutshabalala; Ngubani na owaziyo, ukuba ungene ebukumkanini ngenxa yexesha elinje?', 'XHO22'),
(21, 4, 15, 'uEstere waxelela uMordekayi, wathi:', 'XHO22'),
(21, 4, 16, 'Yiya uwahlanganise onke amaYuda alapho eSusa, nizile ukudla ngenxa yam; ningadli, ningaseli iintsuku ezintathu, ubusuku nemini. Mna nabakhonzazana bam sozila ngaloo ndlela; ndongena ke kukumkani, kungengamthetho; nokuba nditshabalele, ndiya kutshabalala.', 'XHO22'),
(21, 4, 17, 'Wahamba ke uMordekayi, wenza njengoko uEstere wamwiselayo umthetho;', 'XHO22'),
(21, 5, 1, 'Kwathi ngomhla wesithathu uEstere wambatha ingubo yakhe yobukumkani, wema entendelezweni engaphakathi yendlu yokumkani, malunga nendlu yokumkani; ukumkani ubehleli etroneni yobukumkani bakhe, endlwini yokumkani, malunga nesango lendlu.', 'XHO22'),
(21, 5, 2, 'Ukumkani wambona uEstere ukumkanikazi emi entsimini, wambabala; Waza ukumkani wayolulela intonga yegolide abeyibambe kuEstere. Wasondela uEstere, wachukumisa incam yentonga yobukumkani.', 'XHO22'),
(21, 5, 3, 'Wathi uYehova kuye, Ufuna ntoni na, kumkanikazi Estere? siyintoni na isicelo sakho? uya kunikwa isiqingatha sobukumkani.', 'XHO22'),
(21, 5, 4, 'Wathi uEstere, Ukuba ukumkani uthe wabona kufanelekile, makeze ukumkani namhlanje noHaman esidlweni endimenzele sona.', 'XHO22'),
(21, 5, 5, 'Wathi ukumkani, Makangxame uHaman, enze ngokwelizwi likaEstere. Ke ukumkani noHaman beza esidlweni abesilungiselele uEstere.', 'XHO22'),
(21, 5, 6, 'Wathi ukumkani kuEstere ngasesidlweni sewayini, Uthandazela ntoni na? Siyintoni na isicelo sakho? kude kube sisiqingatha sobukumkani kuya kugqitywa.', 'XHO22'),
(21, 5, 7, 'Waphendula uEstere, wathi, Sisicelo sam esi, nokucela kwam;', 'XHO22'),
(21, 5, 8, 'Ukuba ndithe ndababalwa emehlweni okumkani, ukuba kuthe kwalunga kukumkani, ukuba andinike umthandazo wam, asizalise isicelo sam, makeze ukumkani noHaman esidlweni endibalungisela sona ngomso; ndiya kuza yenza njengoko wathethayo ukumkani.', 'XHO22'),
(21, 5, 9, 'Waza waphuma uHaman ngaloo mini evuya evuya, kodwa akumbona uMordekayi esangweni likakumkani ukuba akazange ayeke yaye akazange angcangcazele ngenxa yakhe, uHaman wazala ngumsindo nxamnye noMordekayi.', 'XHO22'),
(21, 5, 10, 'Wazibamba uHaman, waya endlwini yakhe, wabiza izihlobo zakhe, noSerese umkakhe.', 'XHO22'),
(21, 5, 11, 'uHaman wabaxelela ngozuko lobutyebi bakhe nenani loonyana bakhe nazo zonke izinto awamphakamisa ngazo ukumkani, nendlela aziphakamisa ngayo ngaphezu kweenkosana nabakhonzi bakakumkani.', 'XHO22'),
(21, 5, 12, 'Ngaphezu koko, uHaman wathi: “Ewe, ukumkanikazi uEstere akazange avumele nabani na ukuba abe kunye nokumkani esidlweni awayesilungiselele ngaphandle kwam; yaye ngomso ndiya kumenyelwa kuye nokumkani.', 'XHO22'),
(21, 5, 13, 'Ke yonke le nto ayindincedi nganto, njengoko ndabona uMordekayi umYuda ehleli esangweni lokumkani.', 'XHO22'),
(21, 5, 14, 'Wathi ke uSeres umkakhe nezihlobo zakhe zonke kuye, Umthi mawuphakame ube ziikubhite ezimashumi mahlanu, uthethe kukumkani ngomso, ukuba uMordekayi axhonywe kuwo; emva koko atye kunye nokumkani ngovuyo. Yalunga loo nto emehlweni kaHaman; kwaye wenza ukuba kwenziwe umthi.', 'XHO22'),
(21, 6, 1, 'Ngobo busuku, ukumkani walala ubuthongo, waza wayalela ukuba kuziswe incwadi; zaleswa phambi kokumkani.', 'XHO22'),
(21, 6, 2, 'Kwafunyaniswa ukuba kwakubhaliwe oko uMordekayi wayekuxelile ngokuphathelele uBhigetana noTerese, izicakakazi ezimbini zendlu kakumkani, abagcini-masango, ababefuna ukubulala uKumkani uAhaswerosi.', 'XHO22'),
(21, 6, 3, 'Wathi uYehova, Wenze mbekoni na, nabukhulu buni na uMordekayi? Bathi abakhonzi bokumkani ababelungiselela kuye, akenziwanga nto.', 'XHO22'),
(21, 6, 4, 'Wathi ukumkani, Ngubani na osendlwini? Wangena uHaman entendelezweni engaphandle yendlu yokumkani, esiya kuthetha kukumkani malunga nokuxhonywa kukaMordekayi emthini abemlungisele wona.', 'XHO22'),
(21, 6, 5, 'Bathi abakhonzi bokumkani kuye, Nanko uHaman emi endle. Wathi ukumkani, Makangene.', 'XHO22'),
(21, 6, 6, 'Wangena uHaman, wathi ukumkani kuye, Makwenziwe ntoni na kwindoda, athe ukumkani athanda ukuyibeka imbeko? Wathi uHaman entliziyweni yakhe, Ngubani na ukumkani angathanda ukubeka embekweni ngaphezu kwam?', 'XHO22'),
(21, 6, 7, 'uHaman wamphendula ukumkani, wathi, Kungenxa yokuba indoda leyo ukumkani athanda ukuyihlonela;', 'XHO22'),
(21, 6, 8, 'Maziziswe ingubo yokwaleka yokumkani, nehashe akhwele kulo ukumkani, nesithsaba sobukumkani esithiwe wambu entlokweni yakhe;', 'XHO22'),
(21, 6, 9, 'Esi sambatho nehashe masinikelwe esandleni somnye wezikhulu zokumkani ezibekekileyo, ukuba ambambe lowo athanda ukumbeka ukumkani, akhwele ehashini endaweni yembutho yesixeko wadanduluka phambi kwakhe. Kuya kwenziwa ngokwendoda ukumkani afuna ukuhlonelwa.', 'XHO22'),
(21, 6, 10, 'Wathi ukumkani kuHaman, Khawuleza, uthabathe ingubo leyo nehashe elo, njengoko ukuthethileyo, wenjenjalo kuMordekayi umYuda, ohleli emnyango wokumkani; Makungabikho siphazamiso kuyo yonke into oyithethileyo.', 'XHO22'),
(21, 6, 11, 'Wandula ke uHaman wathabatha isambatho nehashe waza wamambathisa uMordekayi waza wamkhwelisa ehashini ukuya kwindawo yembutho yesixeko waza wadanduluka phambi kwakhe esithi: “Makwenziwe oku kwindoda afuna imbeko kuyo ukumkani.', 'XHO22'),
(21, 6, 12, 'Wabuya uMordekayi weza esangweni lokumkani; Wangxama uHaman, waya endlwini yakhe, elila, egqubuthele intloko.', 'XHO22'),
(21, 6, 13, 'uHaman waxelela uSerese umkakhe, nezihlobo zakhe zonke, konke okumhleleyo. Zathi kuye izilumko zakhe, noSerese umkakhe, Ukuba ungowesizwe samaYuda uMordekayi, lowo uqalileyo ukuwa phambi kwakhe, akuyi kumeyisa; wowa phambi kwakhe.', 'XHO22'),
(21, 6, 14, 'Bathe besathetha naye, beza abalungiseleli bokumkani, bamgibisela uHaman esidlweni ebesilungiselwe nguEstere.', 'XHO22'),
(21, 7, 1, 'Ukumkani noHaman beza kudla noEstere ukumkanikazi.', 'XHO22'),
(21, 7, 2, 'Waphinda ukumkani wathi kuEstere ngomhla wesibini womthendeleko wewayini, Uthandaza ntoni na, kumkanikazi Estere? Siyintoni na isicelo sakho? yaye iya kugqitywa ukusa kwisiqingatha sobukumkani.', 'XHO22'),
(21, 7, 3, 'Waphendula uEstere ukumkanikazi, wathi, Ukuba ndifumene inceba emehlweni akho, kumkani, ukuba ukumkani uthe wavuma, mandinikwe umphefumlo wam ngokucela kwam, nangenxa yabantu bakowethu.', 'XHO22'),
(21, 7, 4, 'Ngenxa yokuba kuthengisiwe ngathi, mna nabantu bakowethu, ukuze sitshatyalaliswe, sibulawe, sisingelwe phantsi. Ukuba bekuthengiswe ngathi ukuba sibe ngamakhoboka namakhobokazana, ndaye ndithule, nangona utshaba lwalungenako ukumbulala ukumkani.', 'XHO22'),
(21, 7, 5, 'Waphendula ukumkani uAsuwerosi, wathi kuEstere ukumkanikazi, Ngubani na lowo?', 'XHO22'),
(21, 7, 6, 'Wathi uEstere, Utshaba notshaba ngulo Haman ungendawo; Ke uHaman wamoyika ukumkani nokumkanikazi.', 'XHO22'),
(21, 7, 7, 'Wesuka ukumkani enomsindo emthendelekweni, waya emyezweni wendlu yokumkani; wesuka wema uHaman, ukuba acele umphefumlo wakhe kuEstere ukumkanikazi; ngokuba ebebona ukuba ukumkani ugwetyelwe ububi.', 'XHO22'),
(21, 7, 8, 'Wabuya ukumkani emyezweni wendlu yobukumkani, waya endaweni yesidlo sewayini; yaye uHaman wawa phezu kwesingqengqelo awayekuso uEstere. Wathi ukumkani, Nokumkanikazi lo uya kumngenisa endlwini esecaleni kwam na? Laphuma elo lizwi emlonyeni wokumkani, babugqubuthela ubuso bukaHaman.', 'XHO22'),
(21, 7, 9, 'Wathetha uHarabhona, omnye wababusi, phambi kokumkani, wathi, Khawukhangele umthi, uHaman abewenzele uMordekayi, owathetha okulungileyo ngokumkani, ubude bawo buziikubhite ezimashumi mahlanu; Wathi ukumkani, Mxhomeni kuwo.', 'XHO22'),
(21, 7, 10, 'Bamxhoma ke uHaman emthini abewulungisele uMordekayi; Wadamba ke umsindo wokumkani.', 'XHO22'),
(21, 8, 1, 'Ngaloo mini, ukumkani uAhaswerosi wamnika ukumkanikazi uEstere indlu kaHaman, umbandezeli wamaYuda. uMordekayi weza phambi kokumkani; ngokuba uEstere ubemxelele into abeyiyo kuye.', 'XHO22'),
(21, 8, 2, 'Ukumkani wawukhulula umsesane wakhe, abewuthabathe kuHaman, wawunika uMordekayi. uEstere wabeka uMordekayi ukuba abe ngumphathi wendlu kaHaman.', 'XHO22'),
(21, 8, 3, 'Wabuya wathetha uEstere phambi kokumkani, wawa ezinyaweni zakhe, elila, wambongoza ukuba abuyeke ububi bukaHaman umAgagi, necebo lakhe awayelicingile ngamaYuda.', 'XHO22'),
(21, 8, 4, 'Waza ukumkani wayolulela intonga kuEstere; Wesuka uEstere, wema phambi kokumkani;', 'XHO22'),
(21, 8, 5, 'Wathi, Akuvuma ukumkani, ndambabala emehlweni akhe, yabonakala ilungile loo nto phambi kokumkani, nam ndakholeka emehlweni akhe; nantso ibhaliwe ezincwadini ezibhalwe nguHaman, unyana wokumkani nguHamedadati umAgagi, owabhala ukuba kutshatyalaliswe amaYuda emazweni onke kakumkani.', 'XHO22'),
(21, 8, 6, 'Ndingathini na ukuba nako ukuyibona intlekele eza kufikela abantu bam? Ndingathini na ukuba nako ukuyibona intshabalalo yosapho lwam?', 'XHO22'),
(21, 8, 7, 'Wathi ukumkani uAsuwerosi kuEstere ukumkanikazi, nakuMordekayi umYuda, Yabonani, indlu kaHaman ndiyinike uEstere; yena wamxhoma emthini, ngenxa yokuba wasa isandla sakhe kumaYuda.', 'XHO22'),
(21, 8, 8, 'Bhalani kumaYuda egameni lokumkani, ngokokuthanda kwenu, nitywine ngomsesane wokumkani; ngokuba incwadi ebibhalwe egameni lokumkani, yatywinwa ngomsesane wokumkani, ayinakuguqulwa.', 'XHO22'),
(21, 8, 9, 'Babizwa ke ababhali bokumkani ngelo xesha ngenyanga yesithathu (yinyanga enguSifan ke leyo), ngomhla wamashumi amabini anesithathu kuyo; Kwabhalwa ngako konke awawawisa umthetho uMordekayi amaYuda, nabathetheli, nabathetheli, namabamba amazwe, athabathela kwelaseIndiya esa kwaKushi, amazwe alikhulu elinamanci mabini anesixhenxe, kwelinye icala, nakwelinye ukuba kubhalwe kuzo zonke iintlanga ngokweentetho zazo, namaYuda ngokweebhalo zawo, nangolwimi lwazo.', 'XHO22'),
(21, 8, 10, 'Yaye wabhala egameni likaKumkani uAhaswerosi waza wayitywina ngesithupha sikakumkani, wathumela iincwadi ngeposi ekhwele amahashe nabakhweli beemeyile neenkamela neenkamela ezincinane.', 'XHO22'),
(21, 8, 11, 'Ukumkani wawawisela umthetho amaYuda akwimizi ngemizi, ukuba ahlanganisane, alinde umphefumlo wawo, ukuba atshabalalise, abulale, atshabalalise onke amandla abantu, nelizwe, abeza kulihlasela, amadodana nabafazi, batshabalalise onke amazimba amandla abantu nelizwe eliya kubahlasela ukuba athimbe kubo njengokuphangwayo;', 'XHO22'),
(21, 8, 12, 'ngamini-nye, emazweni onke kakumkani uAhaswerosi, oko kukuthi, ngomhla weshumi elinesithathu wenyanga yeshumi elinesibini (yinyanga enguAdare ke leyo).', 'XHO22'),
(21, 8, 13, 'Ikopi yomthetho eyayiza kunikwa iphondo ngalinye yaziswa kuzo zonke iintlanga, yaye amaYuda ayemele alungiselele loo mini ukuziphindezela kwiintshaba zawo.', 'XHO22'),
(21, 8, 14, 'Bandula ke ooyise baphuma ngokukhawuleza, bekhwele iimeyile neenkamela, ngokomyalelo kakumkani. Kwaye umyalelo wanikwa kwibhotwe likaSusan.', 'XHO22'),
(21, 8, 15, 'uMordekayi wemka kukumkani wambethe ingubo yobukumkani emfusa nemhlophe, enxibe isithsaba esikhulu segolide, evathe ingubo ende yelinen ecikizekileyo nemfusa; umzi waseShushan wavuya wavuya.', 'XHO22'),
(21, 8, 16, 'KumaYuda kwabakho ukukhanya, nokuvuya, novuyo, nembeko.', 'XHO22'),
(21, 8, 17, 'Kuwo onke amaphondo nakwizixeko ngezixeko, naphi na apho umyalelo kakumkani nomyalelo wakhe wawufikela khona, amaYuda ayenovuyo nemihlali, imibhiyozo nemini nobumnandi. Abantu abaninzi belizwe baba ngamaYuda; ngokuba bafikelwa kukunkwantya ngamaYuda.', 'XHO22'),
(21, 9, 1, 'Ngenyanga yeshumi elinesibini (yinyanga enguAdare ke leyo), ngomhla weshumi elinesithathu kuyo, ekuzalisekeni kwelizwi lokumkani nomthetho wakhe, ngaloo mini iintshaba zamaYuda zaziza kuwahlasela baye, (nangona kwakuchasene noko, ukuba amaYuda ayebalawula abo babewathiyile);', 'XHO22'),
(21, 9, 2, 'AmaYuda ahlanganisana kwizixeko zawo kuwo onke amaphondo kaKumkani uAhaswerosi ukuze abase izandla abo babefuna ububi bawo; kwaye kungekho bani unako ukuma nawo; ngokuba bafikelwa kukunkwantya abantu bonke.', 'XHO22'),
(21, 9, 3, 'Bonke abathetheli bamazwe, nabathetheli, namabamba, nabathetheli bokumkani, bawanceda amaYuda; ngokuba bafikelwa kukoyika uMoretogayi.', 'XHO22'),
(21, 9, 4, 'Kuba uMordekayi wayemkhulu endlwini kakumkani, yaye udumo lwakhe lwaqhubeka lufikelela kuwo onke amaphondo, kuba le ndoda inguMordekayi yayikhula ngakumbi.', 'XHO22'),
(21, 9, 5, 'Azoyisa ke amaYuda zonke iintshaba zawo ngekrele, nangokubulala, nangokutshabalalisa, enza ngokuzithandela kwabo babewathiyile.', 'XHO22'),
(21, 9, 6, 'Ebhotwe uSusan, amaYuda abulala atshabalalisa amakhulu amahlanu amadoda.', 'XHO22'),
(21, 9, 7, 'NeParshandata, noDalfon, noAspata;', 'XHO22'),
(21, 9, 8, 'noPorata, noAdaliya, noAridata;', 'XHO22'),
(21, 9, 9, 'neParmashta, noArisayi, noAridayi, noVayeshata;', 'XHO22'),
(21, 9, 10, 'Oonyana abalishumi bakaHaman unyana kaHamedati, umbandezeli wamaYuda, bababulala; ke abawasa sandla emaxhobeni.', 'XHO22'),
(21, 9, 11, 'Ngaloo mini inani lababuleweyo eShushan leziswa phambi kokumkani.', 'XHO22'),
(21, 9, 12, 'Wathi ukumkani kuEstere ukumkanikazi, eShushan amaYuda abulele, atshabalalisa amakhulu amahlanu amadoda, noonyana abalishumi bakaHaman; babesenza ntoni kwamanye amazwe kakumkani? sithini isicelo sakho? kwaye uya kuyinikwa; okanye siyintoni na isicelo sakho? kwaye iya kwenzeka.', 'XHO22'),
(21, 9, 13, 'Wathi uEstere, Ukuba ukumkani uthe wathanda, bovulelwa amaYuda aseShushan ngokwesiko lanamhlanje ngomso, nokuthi oonyana abalishumi bakaHaman baxhonywe emthini.', 'XHO22'),
(21, 9, 14, 'Wathi ukumkani makwenziwe oko; umthetho wawiswa nguSusan; baxhonywa oonyana abalishumi bakaHaman.', 'XHO22'),
(21, 9, 15, 'Abizelana ndawonye amaYuda aseShushan ngomhla weshumi elinesine wenyanga enguAdare, abulala amakhulu amathathu amadoda eShushan; ke abawasa sandla emaxhobeni.', 'XHO22'),
(21, 9, 16, 'Kodwa amaYuda athile awayekumaphondo kakumkani ahlanganisana aza alinda umphefumlo wawo aza aphumla ezintshabeni zawo aza abulala amashumi asixhenxe anesihlanu amawaka eentshaba zawo, kodwa akazange alibambe ixhoba.', 'XHO22'),
(21, 9, 17, 'Ngosuku lweshumi elinesithathu lwenyanga enguAdare; baphumla ngomhla weshumi elinesine, bawenza umhla wokuchwayitha nowovuyo.', 'XHO22'),
(21, 9, 18, 'Ke amaYuda aseShushan ahlanganisana ngomhla weshumi elinesithathu kuyo, nangomhla weshumi elinesine kuyo; baphumla ngomhla weshumi elinesihlanu, bawenza umhla wokuchwayitha nowovuyo.', 'XHO22'),
(21, 9, 19, 'Ngenxa yoko amaYuda asemizini nasemizini engenaludonga, awenza umhla weshumi elinesine wenyanga enguAdare, ube ngumhla wemivuyo nowovuyo, nomhla wokuchwayitha, nowokuthumelana izabelo.', 'XHO22'),
(21, 9, 20, 'uMordekayi wazibhala ezo zinto, wathumela iincwadi kumaYuda onke, emazweni onke kakumkani uAhaswerosi, akufuphi nakude;', 'XHO22'),
(21, 9, 21, 'ukuba akuqinisele, awugcine umhla weshumi elinesine wenyanga enguAdare, nomhla weshumi elinesihlanu kuyo umnyaka;', 'XHO22'),
(21, 9, 22, 'Kufana nemihla aphumla ngayo amaYuda ezintshabeni zawo, yaza nenyanga yatshintsha kuwo usizi yaba luvuyo, isijwili saba lusuku oluhle nezipho kumahlwempu.', 'XHO22'),
(21, 9, 23, 'Aqala ke amaYuda ukwenza njengoko uMordekayi wayewabhalele;', 'XHO22'),
(21, 9, 24, 'ngenxa yokuba uHaman unyana kaHamedata, umAgagi, utshaba lwamaYuda onke, wayeceba ukuwatshabalalisa amaYuda aze enze iPuri, oko kukuthi, iqashiso, ukuwatshabalalisa nokuwatshabalalisa;', 'XHO22'),
(21, 9, 25, 'Kodwa ekufikeni kukaEstere phambi kokumkani, wayalela ngeencwadi ukuba icebo lakhe elibi awayecebe ngalo ngamaYuda libuyele entlokweni yakhe, yaye yena noonyana bakhe baxhonywa emthini.', 'XHO22'),
(21, 9, 26, 'Yiyo ke loo nto namhlanje beyibiza ngegama lePurim, beyibiza ngokuba yiNgcwele ngenxa yoko, ngenxa yamazwi onke ale ncwadi, nangenxa yoko awakubonayo, awafikayo kuwo ngenxa yale nto,', 'XHO22'),
(21, 9, 27, 'AmaYuda ashumayela yaye azithabathele abazukulwana bawo bonke abo bathelela kuwo, kangangokuba kungeyomfuneko ngawo ukuba agcine ezi ntsuku zimbini ngokwezibhalo zawo nokuhamba ngexesha lawo elimisiweyo minyaka le;', 'XHO22'),
(21, 9, 28, 'ikhunjulwe loo mihla, igcinwe kwizizwe ngezizwe, kwizizwe ngezizwe, kwisithili ngemizi, kwimizi ngemizi; nokuba le mihla yePurim ingatshabalali phakathi kwamaYuda, nokukhunjulwa kwayo akupheli kwinzala yawo.', 'XHO22'),
(21, 9, 29, 'Babhala ke ooEstere ukumkanikazi, intombi ka-Abhihayili, noMordekayi umYuda, babhala ngamandla abo onke, bemisa le ncwadi yesibini yePuri.', 'XHO22'),
(21, 9, 30, 'Wathumela iincwadi kumaYuda onke, akumaphondo alikhulu elinamanci mabini anesixhenxe, obukumkani buka-Ahaswerosi, zinamazwi oxolo nenyaniso,', 'XHO22'),
(21, 9, 31, 'ukuze kuqinisekwe imihla yePurim ngamaxesha ayo amisiweyo, kanye njengoko uMordekayi umYuda noKumkanikazi uEstere babebayalele, nangokuvumelana noko babegqibe ngako kubo nangokwenzala yabo ngokuphathelele ukuzila ukutya nokubizwa kwabo.', 'XHO22'),
(21, 9, 32, 'Umyalelo kaEstere uziqinisela ezo ndawo zePuri; kwaye kubhaliwe encwadini.', 'XHO22'),
(21, 10, 1, 'Ukumkani uAhaswerosi wayerhafisa ilizwe neziqithi zolwandle.', 'XHO22'),
(21, 10, 2, 'Ke zonke izenzo zamandla akhe namandla akhe nengxelo yobukhulu bukaMordekayi awathi ukumkani wamphakamisa kubo, akubhalwanga na encwadini yemicimbi yookumkani bamaMedi namaPersi?', 'XHO22'),
(21, 10, 3, 'Ngenxa yokuba uMordekayi umYuda wayengene ezihlangwini zokumkani uAsuwerosi, wayengoyena mkhulu phakathi kwamaYuda, ekholisekile kuninzi lwabazalwana bakhe, ebanqwenelela okuhle abantu bakowabo, wathetha uxolo kuyo yonke imbewu yakhe.', 'XHO22'),
(22, 1, 1, 'Kwaye kukho ndoda ithile kwelakithi, egama linguYobhi; loo mntu wayelilungisa, elilungisa, emoyika uThixo, etyeka ebubini.', 'XHO22'),
(22, 1, 2, 'Wazalelwa oonyana abasixhenxe, neentombi ezintathu.', 'XHO22'),
(22, 1, 3, 'Imfuyo yakhe yayingamawaka asixhenxe eenkomo, amawaka amathathu eenkamela, namakhulu amahlanu eenkomo, namakhulu amahlanu amaesile, nabakhonzi abaninzi kunene; yaba nkulu ke le ndoda kubo bonke abantu baseMpuma.', 'XHO22'),
(22, 1, 4, 'Oonyana bakhe babeqhele ukuya kwenza isidlo ezindlwini zabo, elowo eyimini yakhe; wathumela ukuba kubizwe oodade babo bobathathu, ukuze badle basele nabo.', 'XHO22'),
(22, 1, 5, 'Zakuzaliseka iintsuku zetheko lawo, wathumela uYobhi, wabangcwalisa, wavuka kusasa, wenyusa amadini anyukayo ngokwenani lawo; ngokuba ebesithi uYobhi, Mhlawumbi oonyana bam bangaba bonile, bamlahla uThixo entliziyweni yabo. Ngoko uYobhi wayesoloko ekwenza oko.', 'XHO22'),
(22, 1, 6, 'Kwathi ngamhla uthile oonyana bakaThixo beza kuzimisa phambi koYehova, wangena noSathana phakathi kwabo.', 'XHO22'),
(22, 1, 7, 'Wathi uYehova kuSathana, Uvela phi na? uSathana wamphendula uYehova, wathi, Ulihambahamba ehlabathini, nasezindaweni zonke ezikulo.', 'XHO22'),
(22, 1, 8, 'Wathi uYehova kuSathana, Ubonile na, umkhonzi wam uYobhi, ukuba akukho unjengaye ehlabathini, indoda elilungisa, elilungisa, emoyikayo uThixo, etyekayo ebubini?', 'XHO22'),
(22, 1, 9, 'uSathana wamphendula uYehova, wathi, uYobhi lo ke woyika uThixo yedwa na?', 'XHO22'),
(22, 1, 10, 'Akubiyelwa nguwe na wena, nendlu yakhe, nento yonke awayenzayo ngeenxa zonke? Wawusikelela umsebenzi wezandla zakhe, banda ubutyebi bakhe ehlabathini.', 'XHO22'),
(22, 1, 11, 'Ke ngoku khawuse isandla sakho, uchukumise yonke into anayo, ukuba akayi kukulahla na wena phambi kwamehlo akho.', 'XHO22'),
(22, 1, 12, 'Wathi uYehova kuSathana, Yabona, konke anako kusesandleni sakho; musa nje ukubeka isandla sakho phezu kwakhe. Wemka ke uSathana, wemka ebusweni bukaYehova.', 'XHO22'),
(22, 1, 13, 'Kwathi ngamhla uthile oonyana bakhe neentombi zakhe babesidla, basela iwayini endlwini yomkhuluwa wabo.', 'XHO22'),
(22, 1, 14, 'Wafika umthunywa lowo kuYobhi, wathi, Iinkomo bezilima, namaesile ebesidla ecaleni lazo.', 'XHO22'),
(22, 1, 15, 'Abaxabela ke amaShebha, abathabatha; ewe, bababulala abakhonzi ngobushushu bekrele; Ndisinde ndedwa, ukuza kukuxelela.', 'XHO22'),
(22, 1, 16, 'Esathetha lo, kwabonakala omnye, wathi, Kuhle umlilo kaThixo ezulwini, watshisa impahla emfutshane nezicaka. Ndisinde ndedwa, ukuza kukuxelela.', 'XHO22'),
(22, 1, 17, 'Esathetha lowo, kwafika nomnye wathi: “AmaKaledi amisa imikhosi emithathu, azingenela iinkamela, azithabatha, ababulala abakhonzi ngohlangothi lwekrele; Ndisinde ndedwa, ukuza kukuxelela.', 'XHO22'),
(22, 1, 18, 'Esathetha lo, kwafika uthile, wathi, Oonyana bakho neentombi zakho babesidla, besela iwayini endlwini yomninawa wabo;', 'XHO22'),
(22, 1, 19, 'nanko kuvela umoya omkhulu ngasentlango, wabetha ezimbombeni zone zendlu, wabawela abafana, bafa; Ndisinde ndedwa, ukuza kukuxelela.', 'XHO22'),
(22, 1, 20, 'Waphakama uYobhi, wazikrazula iimpahla zakhe, wacheba intloko yakhe, wawa phantsi, waqubuda.', 'XHO22'),
(22, 1, 21, 'Wathi, Ndaphuma ndedwa esizalweni sikama, ndiya kubuyela khona ndedwa; uYehova wanika, yaye uYehova uthabathile; Malibongwe igama likaYehova.', 'XHO22'),
(22, 1, 22, 'Kuko konke oko akonanga uYobhi, akamthukanga uThixo.', 'XHO22'),
(22, 2, 1, 'Kwathi ngamhla uthile, oonyana bakaThixo beza kubonakala ebusweni bukaYehova, weza noSathana nabo, ukuba babonakale ebusweni bukaYehova.', 'XHO22'),
(22, 2, 2, 'Wathi uYehova kuSathana, Uvela phi na? uSathana wamphendula uYehova, wathi, Ulihambahamba ehlabathini, ahambahamba kulo.', 'XHO22'),
(22, 2, 3, 'uYehova wathi kuSathana: “Ukubonile na, mkhonzi wam uYobhi, ukuba akukho unjengaye ehlabathini, indoda elilungisa, elilungisa, emoyikayo uThixo, ebuphepha ububi? ukanti usabugcinile ubulungisa bakhe, nokuba undishukumisele ukuba ndimtshabalalise ngelize.', 'XHO22'),
(22, 2, 4, 'uSathana wamphendula uYehova, wathi, Ulusu ngenxa yolusu; yonke into anayo umntu woyinikela ngenxa yomphefumlo wakhe.', 'XHO22'),
(22, 2, 5, 'Ke kaloku khawuse isandla sakho, uchukumise emathanjeni akhe nasenyameni yakhe, ukuba akayi kukulahla na phambi kwamehlo akho.', 'XHO22'),
(22, 2, 6, 'Wathi uYehova kuSathana, Nanko esesandleni sakho; kodwa wusindise umphefumlo wakhe.', 'XHO22'),
(22, 2, 7, 'uSathana wesuka ebusweni bukaYehova, wambetha uYobhi ngamathumba, wathabathela entendeni yeenyawo zakhe wesa etholeni lakhe.', 'XHO22'),
(22, 2, 8, 'Wathabatha isitya sodongwe, wazikrwela ngaso; wahlala eluthuthwini.', 'XHO22'),
(22, 2, 9, 'Wathi umkakhe kuye, Usabambelele na ekugqibeleleni kwakho? Mqalekise uThixo, ufe.', 'XHO22'),
(22, 2, 10, 'Wathi ke yena kuye, Uthetha njengesidenge. Intoni? Siya kwamkela okulungileyo esandleni sikaThixo na, singamkeli ezimbi? Kuko konke oko akonanga uYobhi ngomlomo wakhe.', 'XHO22'),
(22, 2, 11, 'Zathi izihlobo ezithathu zikaYobhi zakuva ngayo yonke le ntlekele emhleleyo, zeza zaya elowo endlwini yakhe; nguElifazi wakwaTeman, uBhiledadi wakwaShuwa, noTsofare wakwaNahama; ngokuba babenzela umnqophiso wokumenza buhlungu, bamthuthuzele.', 'XHO22'),
(22, 2, 12, 'Bawaphakamisela amehlo abo bekude, abamazanga, baphakamisa izwi labo, balila; Bazikrazula iingubo zabo bonke, bagalela ngothuli entloko, belugalela ngothuli entloko.', 'XHO22'),
(22, 2, 13, 'Bahlala naye phantsi iimini zasixhenxe nobusuku bazo; kungekho uthetha naye, ngokuba babebona ukuba umvandedwa wakhe mkhulu kakhulu.', 'XHO22'),
(22, 3, 1, 'Emveni koko uYobhi wawuvula umlomo wakhe, wayiqalekisa imini yakhe.', 'XHO22'),
(22, 3, 2, 'Wathi uYobhi:', 'XHO22'),
(22, 3, 3, 'Mayitshabalale imini endazalwa ngayo, nobusuku ekwakusithiwa ngabo, Kukhawulwe umntwana oyinkwenkwe.', 'XHO22'),
(22, 3, 4, 'Mayibe mnyama loo mini; Kwanga uThixo angàyiboni iphezulu, Nokukhanya kungakhanyi phezu kwayo.', 'XHO22'),
(22, 3, 5, 'Mayihlanjelwe ubumnyama nethunzi lokufa; maliyakhe ilifu phezu kwayo; mabuyoyike ubumnyama belanga.', 'XHO22'),
(22, 3, 6, 'Mabumbethe ubumnyama obo busuku; musani ukuyiphatha imihla yomnyaka, ningafaki inani leenyanga;', 'XHO22'),
(22, 3, 7, 'Yabona, obo busuku mabube lilolo, kungabikho lizwi lovuyo kubo.', 'XHO22'),
(22, 3, 8, 'Aqalekise abaqalekisa ilanga, Abakuphaphameleyo ukukuvusa ukukhala kwabo;', 'XHO22'),
(22, 3, 9, 'Iinkwenkwezi zenu zokuhlwa mazibe mnyama; Makafune ukukhanya, kungabikho; makungakuboni ukusa;', 'XHO22'),
(22, 3, 10, 'Ngokuba bungazivalanga iingcango zesizalo sikama, Bungabusithelisanga ububi emehlweni am.', 'XHO22'),
(22, 3, 11, 'Yini na ukuba ndingafeli esizalweni sikanina? bekutheni ze ndingakhuphanga moya xa ndiphuma esuswini sam?', 'XHO22'),
(22, 3, 12, 'Bekutheni ukuze amadolo am andibambezele? okanye ndincancisa ntoni?', 'XHO22'),
(22, 3, 13, 'Kuba ngoku ngendiba ndilele, ndilele, ndilele; ukuba bendithe cwaka,', 'XHO22'),
(22, 3, 14, 'ookumkani, nabacebisi behlabathi, abazakhela amanxuwa;', 'XHO22'),
(22, 3, 15, 'Nabathetheli ngegolide, ababezalise izindlu zabo ngesilivere;', 'XHO22'),
(22, 3, 16, 'Okanye ngendingaba khona njengeentsana ezifihlakeleyo; njengeentsana ezingazange zakubona ukukhanya kwemini.', 'XHO22'),
(22, 3, 17, 'Kuba apho, abangendawo bayekile ukuxhalaba; nalapho abatyhafileyo bayaphumla.', 'XHO22'),
(22, 3, 18, 'Apho amabanjwa aphumla ndaweni-nye; aziliva ilizwi lomcudisi.', 'XHO22'),
(22, 3, 19, 'Abancinane nabakhulu balapho; yaye ikhoboka likhululekile enkosini yalo.', 'XHO22'),
(22, 3, 20, 'Ngenxa yeso sizathu, unikwe ukukhanya ukuya ekubandezelekeni, kwaye ubomi kwintliziyo eyaphukileyo;', 'XHO22'),
(22, 3, 21, 'abalangazelela ukufa, kusuke kungafiki; bawungcwaba ngaphezu kobutyebi obuselelweyo;', 'XHO22'),
(22, 3, 22, 'Ngoobani abavuyayo nabavuyayo xa befumana ingcwaba?', 'XHO22'),
(22, 3, 23, 'Kutheni le nto ukukhanya kunikwa umntu ondlela yakhe ifihliweyo nokhuselweyo nguThixo?', 'XHO22'),
(22, 3, 24, 'Kuba kufike ukuncwina kwam phambi kokuba ndidle, Nokubhonga kwam kumpompoza njengamanzi.', 'XHO22'),
(22, 3, 25, 'Kuba into endinxunguphala yiyo, indifikele, nento endinxunguphala yiyo indizele.', 'XHO22'),
(22, 3, 26, 'Andikhuselekanga, andiphumlanga, andizolanga; kodwa yafika ingxaki.', 'XHO22'),
(22, 4, 1, 'Waphendula uElifazi wakwaTeman, wathi:', 'XHO22'),
(22, 4, 2, 'Ukuba sizama ukuthetha nawe, ngaba uya kuziva ukhathazekile? ngubani na ongakwaziyo ukuthetha?', 'XHO22'),
(22, 4, 3, 'Qaphela, ubafundise abaninzi, waza wazomeleza izandla ezibuthathaka.', 'XHO22'),
(22, 4, 4, 'Amazwi akho amxhasa lowo wayeza kuwa, waza wawaqinisa amadolo abuthathaka.', 'XHO22'),
(22, 4, 5, 'Kodwa ngoku uzelwe, kwaye udiniwe; uyamchukumisa, uyoyika.', 'XHO22'),
(22, 4, 6, 'Asikuko na ukunkwantya, ithemba lakho, ithemba lakho, nobulungisa beendlela zakho?', 'XHO22'),
(22, 4, 7, 'Khumbula, ngubani na owakha wadaka, kuba emsulwa? Anqunyulwa phi na amalungisa?', 'XHO22'),
(22, 4, 8, 'Njengoko ndabonayo, abahlwayela ubugqwetha, bavuna ubutshinga, bahlwayele ubugqwetha.', 'XHO22'),
(22, 4, 9, 'Umoya kaThixo ubatshabalalisa, Ngokuphefumla komsindo wakhe banyamalale.', 'XHO22'),
(22, 4, 10, 'Ukugquma kwengonyama, nelizwi lengonyama egqumayo, namazinyo eengonyama ezintsha aqhawuke.', 'XHO22'),
(22, 4, 11, 'Ingonyama entsha itshabalala ngenxa yokuswela amaxhoba, namathole engonyama entsha abe ziintsali.', 'XHO22'),
(22, 4, 12, 'Yaziswa kum entsithelweni;', 'XHO22'),
(22, 4, 13, 'Ezinkumbulo zemibono yasebusuku, Xa abantu befikelwa bubuthongo obukhulu;', 'XHO22'),
(22, 4, 14, 'Ndibanjwe kukunkwantya, Kwawarhwaqaza onke amathambo am.', 'XHO22'),
(22, 4, 15, 'Wadlula umoya ebusweni bam; iinwele zenyama yam ziphelile.', 'XHO22'),
(22, 4, 16, 'Wema, ndingasiboni isimo sawo; kwavela umfanekiso phambi kwamehlo am, kwathi cwaka, ndeva ilizwi lisithi:', 'XHO22'),
(22, 4, 17, 'Ngaba umntu ofayo uya kuba lilungisa ngakumbi kunoThixo? Ngaba umntu uya kuba nyulu kunoMdali wakhe?', 'XHO22'),
(22, 4, 18, 'Yabona, akakholwa ngabakhonzi bakhe; wazithetha ngobudenge izithunywa zakhe.', 'XHO22'),
(22, 4, 19, 'Bathini na ke ukuhlala ezindlwini zodongwe, eziziseko zabo ziseluthulini, ezityunyuzwe luthuli?', 'XHO22'),
(22, 4, 20, 'Bagxothwa kususela kusasa kwada kwahlwa;', 'XHO22'),
(22, 4, 21, 'Ubuhle babo abupheleli na kubo? Bafa bengenabulumko.', 'XHO22'),
(22, 5, 1, 'Khawubize, ukuba ukho, wosabela kuwe; Nguwuphi na ke kwabangcwele oya kuphethukela kuye?', 'XHO22'),
(22, 5, 2, 'Ngokuba ingqumbo iyabulala izimathane, Nekhwele liyabulala izinyabi.', 'XHO22'),
(22, 5, 3, 'Ndabona isidenge sikhula, Ndaliqalekisa kwaoko ikhaya laso.', 'XHO22'),
(22, 5, 4, 'Bakude nosindiso abantwana bakhe, batyunyuzwa esangweni, kungabikho msindisi.', 'XHO22'),
(22, 5, 5, 'Kuba abalambileyo badla amaxhoba, bawahluthe ngameva;', 'XHO22'),
(22, 5, 6, 'Nakuba kungekho ntlekele ivela eluthulini, Nobubi abuphumi emhlabeni;', 'XHO22'),
(22, 5, 7, 'Noko ke, umntu uzalelwa ebubini, njengentlantsi ekubalekeni kwayo phezulu.', 'XHO22'),
(22, 5, 8, 'Bendiya kuquqela kuThixo, Ndilibeke kuThixo ityala lam,', 'XHO22'),
(22, 5, 9, 'abenza izinto ezinkulu ezingenakulinganiswa; imimangaliso emininzi.', 'XHO22'),
(22, 5, 10, 'Ulonisa imvula ehlabathini, Uthumela amanzi ehlabathini;', 'XHO22'),
(22, 5, 11, 'Ukuba aphakamise abathobekileyo; ukuze abo bazilileyo baphakanyiswe benoxolo.', 'XHO22'),
(22, 5, 12, 'Uyawatshitshisa amacebo amatshijolo, Zingenzi izenzo zabo izandla zabo.', 'XHO22'),
(22, 5, 13, 'Usenokuba nobulumko emanqeni abo, liguqulwe injongo yabafundisi bobuxoki.', 'XHO22'),
(22, 5, 14, 'Emini bafumana ubumnyama;', 'XHO22'),
(22, 5, 15, 'Uyabahlangula abaziintsizana emkhontoni, Emilebeni yabo, Nasesandleni sowomeleleyo.', 'XHO22'),
(22, 5, 16, 'Kubekho ke ithemba kosweleyo, Ubungendawo buyawuvala umlomo walo.', 'XHO22'),
(22, 5, 17, 'Yabona, unoyolo mntu owohlwaywa nguThixo; ke ngoko anisigwebi isohlwayo sikaSomandla.', 'XHO22'),
(22, 5, 18, 'Kuba wenza buhlungu, abophe; uyaphalusa, ziphilise izandla zakhe.', 'XHO22'),
(22, 5, 19, 'Uya kukusindisa ezimbandezelweni ezintandathu; Kwabasixhenxe akukho bubi bungayi kuchukunyiswa.', 'XHO22'),
(22, 5, 20, 'Uya kukuhlangula endlaleni, nasekufeni, emandleni ekrele.', 'XHO22'),
(22, 5, 21, 'Uya kusitheliswa ekufeni kolwimi, Ungayoyiki intshabalalo yakufika.', 'XHO22'),
(22, 5, 22, 'Imbuqo nendlala uya kuyihleka, ungawoyiki amarhamncwa elizwe;', 'XHO22'),
(22, 5, 23, 'Kuba uya kwenza umnqophiso namawa elizwe, zibe noxolo izilo zelizwe nawe.', 'XHO22'),
(22, 5, 24, 'Uya kwazi ukuba uxolo yintente yakho; wondwendwela ekhaya, ungoni.', 'XHO22'),
(22, 5, 25, 'Uya kwazi nokuba inzala yakho iya kuba ninzi, nenzala yakho ibe njengengca yomhlaba.', 'XHO22'),
(22, 5, 26, 'Uya kufikelela engcwabeni umdala kakhulu, njengesithungu sengqolowa ngexesha laso;', 'XHO22'),
(22, 5, 27, 'Khangela, siyihlolile, injalo loo nto; yiva kwaye wazi ngokuqinisekileyo.', 'XHO22'),
(22, 6, 1, 'Waphendula uYobhi, wathi,', 'XHO22'),
(22, 6, 2, 'Mabulinganiswe ubuhlwempu bam, Nomvandedwa wam ulinganiswe esikalini!', 'XHO22'),
(22, 6, 3, 'Kuba ngoku bekuya kuba nzima ngaphezu kwentlabathi yolwandle; Kungenxa yoko le nto amazwi am aginyiwe.', 'XHO22'),
(22, 6, 4, 'Kuba iintolo zoSomandla zimi kum, Umphefumlo wam wawufinca ubuhlungu bazo; izothuso zikaThixo zihlangene ngam.', 'XHO22'),
(22, 6, 5, 'Iesile liyalila na, xa linomtya? Okanye ngaba iinkomo ziya kulala edlelweni lazo?', 'XHO22'),
(22, 6, 6, 'Iyadliwa na into ejavujavu, ingenatyuwa? Okanye ngaba kukho incasa kwiqanda elimhlophe?', 'XHO22'),
(22, 6, 7, 'Izinto owala ukuzichukumisa umphefumlo wam, zinjengenyama yam elusizi.', 'XHO22'),
(22, 6, 8, 'Owu, ukuba bendinokusifumana isicelo sam; kwaye uThixo uya kundinika into endiyilangazelelayo!', 'XHO22'),
(22, 6, 9, 'Kananjalo kube kuhle emehlweni kaThixo ukunditshabalalisa; ukukhulula isandla sakhe andihlise!', 'XHO22'),
(22, 6, 10, 'Ke kaloku bendiya kuthuthuzeleka; Ewe, bendiya kuziqaqadekisa ndiselusizini makangacongi; Andiwafihlanga amazwi oyiNgcwele.', 'XHO22'),
(22, 6, 11, 'Ayintoni na amandla am, ukuba ndingade ndithembe? Siyintoni na isiphelo sam, ukuze ndibandise?', 'XHO22'),
(22, 6, 12, 'Angamandla amatye na amandla am? Okanye inyama yam lubhedu na?', 'XHO22'),
(22, 6, 13, 'Akukho kum na uncedo lwam? Buphumile kum na bonke ubulumko?', 'XHO22'),
(22, 6, 14, 'Oxhwalekileyo abe nenceba kummelwane wakhe; Ke yena uyatyeka ekumoyikeni uSomandla.', 'XHO22'),
(22, 6, 15, 'Abazalwana bam banginiza njengomlambo oqukuqelayo, badlula njengomlambo oqukuqelayo;', 'XHO22'),
(22, 6, 16, 'omnyama ngenxa yomkhenkce, ekusithele kuwo ikhephu;', 'XHO22'),
(22, 6, 17, 'Ekufudumaleni kwawo, ayaphela; kwakubashushu, zitshabalala endaweni yazo.', 'XHO22'),
(22, 6, 18, 'Iindlela zabo zisuke zalahleka; baba yinto engento, batshabalale.', 'XHO22'),
(22, 6, 19, 'Banduluka amagorha akwaTema, abakwaShebha babalindile.', 'XHO22'),
(22, 6, 20, 'Yadana, ngokuba ibikholosile; beza apho, badanile.', 'XHO22'),
(22, 6, 21, 'Kuba ngoku akukho nto; uyakubona ukuguqa kwam, woyika.', 'XHO22'),
(22, 6, 22, 'Ndithe na, Ndiziseni? okanye: Ndinike ixabiso lempahla yakho?', 'XHO22'),
(22, 6, 23, 'Okanye ndihlangule esandleni sotshaba? okanye: Ndihlangule esandleni sowomeleleyo?', 'XHO22'),
(22, 6, 24, 'Ndifundiseni, ukuze ndizole, ndiqonde apho ndilahleka khona.', 'XHO22'),
(22, 6, 25, 'Hayi indlela anamandla ngayo amazwi afanelekileyo! kodwa ingxabano yakho ikhalimela ntoni?', 'XHO22'),
(22, 6, 26, 'Ngaba uya kukhalinyelwa ngamazwi nentetho yomphefumlo odakumbileyo?', 'XHO22'),
(22, 6, 27, 'Ewe, ucinezela inkedama, Ummbele iqula ummelwane wakho.', 'XHO22'),
(22, 6, 28, 'Yanelisa ngoku, khangela kum; kuba uyabona ukuba bubuxoki.', 'XHO22'),
(22, 6, 29, 'Khanibuye, musa ukuba ngongendawo; ewe, buya, ubulungisa bam bukuyo.', 'XHO22'),
(22, 6, 30, 'Bukho na ububi elulwimini lwam? Ngaba incasa yam ayikwazi ukubona izinto ezingalunganga?', 'XHO22'),
(22, 7, 1, 'Akakho na ixesha emntwini ehlabathini? Ayinjengemihla yomntu ohlambulukileyo na imihla yakhe?', 'XHO22'),
(22, 7, 2, 'Njengomkhonzi oshiywa ngumthunzi, nanjengenxila, elindele umvuzo wengqesho yakhe;', 'XHO22'),
(22, 7, 3, 'Ngoko basikelelwe ngeenyanga eziphuthileyo, nobusuku obutyhafileyo bumiselwe mna.', 'XHO22'),
(22, 7, 4, 'Xa ndileleyo ndithi, Ndiya kuvuka nini na, ubusuku budlule? ndaqina isibindi ndaya ngapha nangapha kwade kwasa.', 'XHO22'),
(22, 7, 5, 'Inyama yam yembeswe iimpethu namanxeba othuli; ulusu lwam lwaphukile, lungamasikizi.', 'XHO22'),
(22, 7, 6, 'Imihla yam ikhawuleza ngaphezu kwenqwelo; Akukho themba.', 'XHO22'),
(22, 7, 7, 'Khumbula ukuba bungumoya ubomi bam; Aliyi kukubona okulungileyo iliso lam.', 'XHO22'),
(22, 7, 8, 'Iliso elindibonayo aliyi kuba sandibona; Amehlo akho akum, ndingekho;', 'XHO22'),
(22, 7, 9, 'Njengoko ilifu litshabalala kwaye linyamalala, ngokunjalo nalowo uhla aye kwelabafileyo akayi kubuya avuke.', 'XHO22'),
(22, 7, 10, 'Akasayi kubuyela endlwini yakhe, nekhaya lakhe lingabi samazi.', 'XHO22'),
(22, 7, 11, 'Ke ngoko, andiyi kuwuvala umlomo wam; Ndiya kuthetha ekubandezelweni komphefumlo wam; Ndiya kuwukhalaza umphefumlo wam ngenxa yobubi;', 'XHO22'),
(22, 7, 12, 'Lulwandle na, ngumnenga na, le nto undibona?', 'XHO22'),
(22, 7, 13, 'Xa ndithi, Wondithuthuzela umyalelo wam, Zolithoza izingqengqelo zam;', 'XHO22'),
(22, 7, 14, 'Ngoku uyandoyikisa ngamaphupha yaye undoyikisa ngemibono.', 'XHO22'),
(22, 7, 15, 'Ngoko ke umphefumlo wam wanyula ukufa nokufa kunobomi bam.', 'XHO22'),
(22, 7, 16, 'Ndiyithiyile loo nto; andiyi kuphila ngonaphakade, andiyi kuma; ngokuba ilize imihla yam.', 'XHO22'),
(22, 7, 17, 'Uyintoni na umntu, ukuba uthe wamenza mkhulu? Umisele intliziyo yakho kuye?', 'XHO22'),
(22, 7, 18, 'Kwaye nokuba umndwendwela yonke intsasa uze umvavanye umzuzu ngamnye?', 'XHO22'),
(22, 7, 19, 'Kuya kude kube nini na ningandishiyi ningandivumeli ndilinde de nditshice?', 'XHO22'),
(22, 7, 20, 'Ndonile; ndithini na ngawe, Msindisi wabantu? yini na ukuba undenze umqondiso kuwe, ukuba ube ngumthwalo kum?', 'XHO22'),
(22, 7, 21, 'Kwaye kungani na ukuba ungasixoleli isikreqo sam uze ubususe ubugwenxa bam? ngokuba ngoku ndiya kulala eluthulini; nindifune kusasa, ndingabikho.', 'XHO22'),
(22, 8, 1, 'Waphendula uBhiledadi wakwaMoses, wathi,', 'XHO22'),
(22, 8, 2, 'Koda kube nini na nithetha ezi zinto? Aya kude abe njengomoya onamandla, amazwi omlomo wakho?', 'XHO22'),
(22, 8, 3, 'uThixo uyagqwetha na okusesikweni? uSomandla uwujikele na umyalelo?', 'XHO22'),
(22, 8, 4, 'Ukuba oonyana bakho bathe bamona kuye, wabashiya ngenxa yobumenemene babo;', 'XHO22'),
(22, 8, 5, 'Ukuba uthe wamquqela uThixo ngexesha elililo, Nomthandazo wakho kuSomandla;', 'XHO22'),
(22, 8, 6, 'Ukuba uhlambulukile, ulungile; inene, uya kuvuka ngenxa yakho, ayenze ibe nempumelelo indawo yokuhlala yobulungisa bakho.', 'XHO22'),
(22, 8, 7, 'Nakuba isiqalo sakho besincinane, kodwa isiphelo sakho siya kukhula kakhulu.', 'XHO22'),
(22, 8, 8, 'Kuba babuze ngenkulungwane yokuqala baze balungiselele ukugocagocwa kooyise.', 'XHO22'),
(22, 8, 9, 'Ngokuba singabayizolo, asazi, ngokuba isithunzi imihla yethu ehlabathini;', 'XHO22'),
(22, 8, 10, 'Abayi kunifundisa na, bathethe kuni, bathethe amazwi entliziyweni yabo?', 'XHO22'),
(22, 8, 11, 'Ngaba iindonga ziyakhula ngaphandle kodaka? Iyahluma na indwe emanzini?', 'XHO22'),
(22, 8, 12, 'Xa luthe lwaluhlaza, lungekanqunywa, luyabuna phambi kwayo yonke imifuno.', 'XHO22'),
(22, 8, 13, 'Zizo ezo iindlela zabo bonke abamlibalayo uThixo; nethemba labahanahanisi liya kutshabalala;', 'XHO22'),
(22, 8, 14, 'abathemba labo liya kubhangiswa, abathemba liyintambo;', 'XHO22'),
(22, 8, 15, 'Wayama ngendlu yakhe, ingemi; uya kuyichukumisa, kodwa ayiyi kuma.', 'XHO22'),
(22, 8, 16, 'Uluhlaza elangeni, Nomasebe akhe ayahluma emyezweni wakhe.', 'XHO22'),
(22, 8, 17, 'Iingcambu zakhe zirhawula imfumba, Ubonela indawo yamatye.', 'XHO22'),
(22, 8, 18, 'Ukuba uthe wamsusa endaweni yakhe, wokhanyela, esithi, Andikubonanga.', 'XHO22'),
(22, 8, 19, 'Khangela, luvuyo lwendlela yakhe, kwaye abanye baya kukhula ehlabathini.', 'XHO22'),
(22, 8, 20, 'Yabona, uThixo akamshiyi ogqibeleleyo, Akasindisi amanginingini;', 'XHO22'),
(22, 8, 21, 'Ade azalise umlomo wakho ngokuhleka, Nomlomo wakho ugcwale ngovuyo.', 'XHO22'),
(22, 8, 22, 'Baya kudana abakuthiyayo; nekhaya labangendawo liya kutshatyalaliswa.', 'XHO22'),
(22, 9, 1, 'Waphendula uYobhi, wathi:', 'XHO22'),
(22, 9, 2, 'Ndiyazi ukuba kuyinyaniso oko; Ke angathini na umntu lo ukuba lilungisa phambi koThixo?', 'XHO22'),
(22, 9, 3, 'Ukuba uthe yena wafuna ukubambana naye, Akayi kuphendula nangelinye ilizwi ewakeni.', 'XHO22'),
(22, 9, 4, 'Unentliziyo elumkileyo, unamandla amakhulu; ngubani na owamshumayezayo, waba nempumelelo?', 'XHO22'),
(22, 9, 5, 'banyikimisa iintaba zingazi; owabagxothayo ngomsindo wakhe.', 'XHO22'),
(22, 9, 6, 'Ulogungqisa ihlabathi endaweni yalo, Zididizele iintsika zalo;', 'XHO22'),
(22, 9, 7, 'Ulothetha elangeni, lingaphumi; kwaye uqinisekise iinkwenkwezi.', 'XHO22'),
(22, 9, 8, 'Uvula izulu yedwa, Unyathela emimangweni yolwandle;', 'XHO22'),
(22, 9, 9, 'Ulowenza iinkwenkwezi zeBhere, namaKroza, nesiLimela, Neengontsi zasezantsi.', 'XHO22'),
(22, 9, 10, 'Owenza izinto ezinkulu ezingekaveliswa; Nezimanga ezingenakubalwa.', 'XHO22'),
(22, 9, 11, 'Yabona, udlula phezu kwam, ndingamboni; uyaqhubeka naye, kodwa andimboni.', 'XHO22'),
(22, 9, 12, 'Yabona, exwila, ngubani na ongamnqandayo? Ngubani na oya kuthi kuye, Wenza ntoni na?', 'XHO22'),
(22, 9, 13, 'Ukuba uThixo akawunqandi umsindo wakhe, Baya kuqubuda kuye abancedisi abanekratshi.', 'XHO22'),
(22, 9, 14, 'Ndinokuthini na xa ndimphendula yaye ndikhetha amazwi am okunxibelelana naye?', 'XHO22'),
(22, 9, 15, 'Okokuba, nangona bendililungisa, andimphenduli; kodwa ndathandaza kumgwebi wam.', 'XHO22'),
(22, 9, 16, 'Ukuba ndithe ndambiza, wandiphendula; nangona kunjalo, andizange ndikholelwe ukuba uliphulaphule ilizwi lam.', 'XHO22'),
(22, 9, 17, 'Kuba undibetha ngesaqhwithi yaye wandisa amanxeba ngaphandle kwesizathu.', 'XHO22'),
(22, 9, 18, 'Angandivumeli ndiphefumle, ke undihluthisa ngobukrakra.', 'XHO22'),
(22, 9, 19, 'Ukuba ndithe ndaqinisa, uthi, Yabona, womelele;', 'XHO22'),
(22, 9, 20, 'Ukuba bendithe ndalilungisa, umlomo wam ubuya kundigweba; Ukuba ndithe, Ndigqibelele, oko koba kukundikhanyela;', 'XHO22'),
(22, 9, 21, 'Ukuba bendigqibelele, andiwuqondi umphefumlo wam; Bendiya kubadela ubomi bam.', 'XHO22'),
(22, 9, 22, 'Le yimbi le nto; ngenxa yoko ndithe, Olilungisa nongendawo uyabatshabalalisa.', 'XHO22'),
(22, 9, 23, 'Ukuba ithe yabulala ngesiquphe indyikitya yokufa, Woyihleka into yabamsulwa.', 'XHO22'),
(22, 9, 24, 'Ihlabathi linikelwe esandleni sabangendawo; Uyabugquma ubuso babagwebi bakhe; ukuba akunjalo, uphi na, ungubani na?', 'XHO22'),
(22, 9, 25, 'Ngoku imihla yam ikhawuleza ngaphezu komqobo; wabaleka engaboni nto ilungileyo.', 'XHO22'),
(22, 9, 26, 'Idlula njengemikhombe yeengcongolo, Njengokhozi luxhelekile kwixhoba lalo.', 'XHO22'),
(22, 9, 27, 'Xa ndithi, Ndiya kuwulibala umsindo wam, Ndiyiyeke ingqumbo yam, ndizole.', 'XHO22'),
(22, 9, 28, 'Ndiyazoyika zonke iinkxwaleko zam, Ndiyazi, ngokuba aniyi kundifuna;', 'XHO22'),
(22, 9, 29, 'Ukuba ndithe ndabangongendawo, yini na ukuba ndifumane ndasebenza?', 'XHO22'),
(22, 9, 30, 'Ukuba ndithe ndazihlamba ngamanzi ekhephu, andazihlambululanga kangako izandla zam;', 'XHO22'),
(22, 9, 31, 'Ke wena uya kundiphosa emhadini, neengubo zam ziya kunuka;', 'XHO22'),
(22, 9, 32, 'Ngenxa yokuba akangomntu njengam ukuba ndimphendule, yaye siya kugwetyelwa.', 'XHO22'),
(22, 9, 33, 'Akukho bani kuthi emini unokubeka isandla phezu kwethu sobabini;', 'XHO22'),
(22, 9, 34, 'Makayisuse intonga yakhe kum, Makungandoyikisi kum.', 'XHO22'),
(22, 9, 35, 'Bendiya kuthetha, ndingamoyiki; ke kum akunjalo.', 'XHO22'),
(22, 10, 1, 'Umphefumlo wam udimele bubomi; Ndiya kukuyeka ukukhalaza kwam; Ndiya kuthetha ngomoya owaphukileyo.', 'XHO22'),
(22, 10, 2, 'Ndithi kuThixo, Musa ukundigweba; Ndibonise ukuba kutheni undixabanisa nje.', 'XHO22'),
(22, 10, 3, 'Ngaba kulungile kuwe xa ucinezela, ukuba udelele umsebenzi wezandla zakho uze uwakhanyele amacebo abangendawo?', 'XHO22'),
(22, 10, 4, 'Unamehlo? Ngaba ubona ngokokubona komntu?', 'XHO22'),
(22, 10, 5, 'Ngaba imihla yakho iyafana neyabanye abantu? Injengemihla yomntu na iminyaka yakho?', 'XHO22'),
(22, 10, 6, 'Xa uthe wabugweba ubugwenxa bam, uya kusigweba na isono sam?', 'XHO22'),
(22, 10, 7, 'Niyazi ukuba andingongendawo; kwaye akukho bani unokuhlangula esandleni sakho.', 'XHO22'),
(22, 10, 8, 'Izandla zakho zindenzile, zinde zonke izinto; noko uyanditshabalalisa.', 'XHO22'),
(22, 10, 9, 'Khumbula ukuba undenze ngokodongwe; uya kundibuyisela eluthulini?', 'XHO22'),
(22, 10, 10, 'Akundithululanga na njengamasi, Wandanda njengamasi?', 'XHO22'),
(22, 10, 11, 'Wandambesa ngolusu nenyama, Wandirhawula ngamathambo nemisipha.', 'XHO22'),
(22, 10, 12, 'Undinike ubomi nenceba, Nokuvelela kwakho kuwukhusele umphefumlo wam.', 'XHO22'),
(22, 10, 13, 'Ezi zinto ke uzibeke entliziyweni yakho;', 'XHO22'),
(22, 10, 14, 'Xa ndonile, wondibona, ungabuxoleli ubugwenxa bam.', 'XHO22'),
(22, 10, 15, 'Ukuba ndithe ndabangongendawo, athi ke mna! nokuba ndilungile, andisayi kuyiphakamisa intloko yam. Ndizele kukudideka; ke ngoko, niyayibona intlungu yam;', 'XHO22'),
(22, 10, 16, 'Kuba luyanda. Uzingela njengengonyama, kwaye kwakhona uzibonakalisa ngendlela engaqhelekanga phezu kwam.', 'XHO22'),
(22, 10, 17, 'Uhlaziye amangqina akho nxamnye nam, yaye uwandise umsindo wakho nxamnye nam; Iinguquko nemfazwe zindichasile.', 'XHO22'),
(22, 10, 18, 'Yini na ke ukuba ukhutshwe esizalweni sikama? Ukuba bendifile, ndingabonwa liso!', 'XHO22'),
(22, 10, 19, 'Ngendiba andikho; Inoba ndathathwa ndasiwa engcwabeni.', 'XHO22'),
(22, 10, 20, 'Ayimfutshane na imihla yam? linda ngoku, ndilinde, ukuze ndithuthuzele kancinane;', 'XHO22'),
(22, 10, 21, 'Phambi kokuba ndihambe, apho ndingayi kubuya khona, ezweni lobumnyama nethunzi lokufa;', 'XHO22'),
(22, 10, 22, 'Ilizwe lobumnyama bunjengobumnyama; ithunzi lokufa ngokuchasa umthetho; apho ukukhanya kububumnyama.', 'XHO22'),
(22, 11, 1, 'Waphendula uTsofare waseNahama, wathi,', 'XHO22'),
(22, 11, 2, 'Akunakuphendulwa na amazwi amaninzi? Ugwetyelwe na umntu ozeleyo?', 'XHO22'),
(22, 11, 3, 'Ngaba ubuxoki bakho bufanele ukubavala umlomo abantu? ukuba uyahleka, akukho mntu unokukuhlazisa?', 'XHO22'),
(22, 11, 4, 'Ngokuba wathi, Iqaqambile imfundiso yam, Ndinyulu emehlweni akho.', 'XHO22'),
(22, 11, 5, 'Kwaye ewe, ukuba uThixo uthe wathetha waza wavula umlomo wakhe ngokuchasene nawe;', 'XHO22'),
(22, 11, 6, 'Makakubonise iimfihlakalo zobulumko, Ukuba buphindeke kabini, bayinto ezimasekileyo; Yazi ke ngoku ukuba uThixo akafuni kuwe ngaphezu kobungendawo bakho.', 'XHO22'),
(22, 11, 7, 'Ngaba unokumfumana uThixo ngokufuna? Unokulinganisa ngokugqibeleleyo uSomandla?', 'XHO22'),
(22, 11, 8, 'Kuphakame njengezulu; ungenza ntoni na ngaphezu kwesihogo; into oyaziyo.', 'XHO22'),
(22, 11, 9, 'Mde kunehlabathi isilinganiso sakhe, sibanzi kunolwandle.', 'XHO22'),
(22, 11, 10, 'Ukuba uthe wanqumla, uyamvalela, nokuba uthe wabutha, ngubani na ongamnqanda?', 'XHO22'),
(22, 11, 11, 'Kuba yena uyabazi abantu abakhohlakeleyo; kwanenkohlakalo uyabubona; akazange acinge ngelo xesha?', 'XHO22'),
(22, 11, 12, 'Kuba umntu okhohlakeleyo uya kuba sisilumko, Kwanokuba umntu uzalwa njengethole.', 'XHO22'),
(22, 11, 13, 'Ukuba uthe wayilungisa intliziyo yakho, Wazolulela kuye izandla zakho;', 'XHO22'),
(22, 11, 14, 'Ukuba kusesandleni sakho ububi, bukhwelelise kude, Makungabikho bugqwetha ezintenteni zakho;', 'XHO22'),
(22, 11, 15, 'Kuba ngoku uya kubuphakamisa ubuso bakho bungenasisihla; ewe, kufuneka nikhuthazeke ningoyiki;', 'XHO22'),
(22, 11, 16, 'Kuba uya kukulibala ukubandezeleka kwakho, ucinge njengamanzi adlulayo;', 'XHO22'),
(22, 11, 17, 'Ukwaluphala kwakho kuya kukhanya ngaphezu kwemini; uya kukhanya, ube njengokusa.', 'XHO22'),
(22, 11, 18, 'Wosindiswa ke, ngokuba sekukho ithemba; ewe, kufuneka umbe ngeenxa zonke kuwe, kwaye uya kuphumla ngokukhuselekileyo.', 'XHO22'),
(22, 11, 19, 'Uya kulala, kungabikho ukoyikayo; ewe, abaninzi baya kukumangalela.', 'XHO22'),
(22, 11, 20, 'Ke wona amehlo abangendawo ophelelwa;', 'XHO22'),
(22, 12, 1, 'Waphendula uYobhi, wathi:', 'XHO22'),
(22, 12, 2, 'Kodwa nina ningabantu, yaye ubulumko buya kufa nani.', 'XHO22'),
(22, 12, 3, 'Nam ke ndinentliziyo njengani; Andingomncinane kuwe; Ewe, ngubani na ongazaziyo izinto ezinjalo?', 'XHO22'),
(22, 12, 4, 'Ndisuke ndanjengomntu oyintlekisa kummelwane wakhe, Engonqula uThixo, andiphendule; indoda elungileyo iyahlekwa.', 'XHO22'),
(22, 12, 5, 'Unjengesibane esidelekileyo, esisentliziyweni yothi cwaka, osisibane sentliziyo yakhe.', 'XHO22'),
(22, 12, 6, 'Iintente zamasela ziba nempumelelo, nabo bagculela uThixo bakhuselekile; abathe uThixo watyeba esandleni sakhe.', 'XHO22'),
(22, 12, 7, 'Kodwa khawubuze ezinkomeni, zokuyala; neentaka zezulu ziya kukuxelela;', 'XHO22'),
(22, 12, 8, 'Thetha nehlabathi, lokuyala, neentlanzi zolwandle zokuxelela.', 'XHO22'),
(22, 12, 9, 'Ngubani na ongaziyo ngezo zinto zonke, ukuba sisandla sikaYehova esizenzileyo?', 'XHO22'),
(22, 12, 10, 'Usesandleni sakhe umoya wezinto zonke eziphilileyo, Nomoya wabantu bonke.', 'XHO22'),
(22, 12, 11, 'Indlebe ayiyi kukucikida na ukuthetha? Nomlomo wayo uyingcamla kweyakhe inyama?', 'XHO22'),
(22, 12, 12, 'Kudala ubulumko; nangobude bemihla ukuqonda.', 'XHO22'),
(22, 12, 13, 'Unobulumko namandla, unecebo nokuqonda;', 'XHO22'),
(22, 12, 14, 'Yabona, iyawa, ayinako ukwakhiwa kwakhona; uvala umntu, kwaye akayi kuvulwa.', 'XHO22'),
(22, 12, 15, 'Yabona, uyawavala amanzi, uyatshisa; Waphinda wabathuma, bawubhukuqa umhlaba.', 'XHO22'),
(22, 12, 16, 'Kuye amandla nobulumko;', 'XHO22'),
(22, 12, 17, 'Ubahlutha abacebi ababhuqiweyo, Azenze izinyabi abagwebi.', 'XHO22'),
(22, 12, 18, 'Ukhulula amathambo ookumkani, Abophe umbhinqo emanqeni abo.', 'XHO22'),
(22, 12, 19, 'Uyabathabatha abathetheli abaphangiweyo, ababhukuqe abanamandla.', 'XHO22'),
(22, 12, 20, 'Uyayishenxisa intetho yabathembekileyo, ayishenxise ingqondo yabadala.', 'XHO22'),
(22, 12, 21, 'Uphalazela amanene ungcikivo, Uwathobe amandla amagorha.', 'XHO22'),
(22, 12, 22, 'Utyhila iinzulu zasebumnyameni, Akhuphe ithunzi lokufa.', 'XHO22'),
(22, 12, 23, 'Wandisa iintlanga, azitshabalalise; Uziphangalalisa iintlanga, aziphanzise;', 'XHO22'),
(22, 12, 24, 'Uzishenxisile iintliziyo zabaphathi beentlanga elimiweyo, Ababhadulisa entlango apho kungekho ndlela.', 'XHO22'),
(22, 12, 25, 'Ziphamza emnyameni kungekho kukhanya, Azibhadulise njengenxila.', 'XHO22'),
(22, 13, 1, 'Yabona, konke oko akubonile amehlo am, Yakuva indlebe yam, yakuqonda.', 'XHO22'),
(22, 13, 2, 'Oko ukwaziyo nam ndiyakwazi: Andimncinci kuwe.', 'XHO22'),
(22, 13, 3, 'Bendiya kuthetha kuSomandla, Ndibe ndinqwenela ukuthetha noThixo.', 'XHO22'),
(22, 13, 4, 'Niyaxoka, ningamagqirha angento nonke niphela;', 'XHO22'),
(22, 13, 5, 'Akwaba ubuthe cwaka nje! kwaye obu bubulumko bakho.', 'XHO22'),
(22, 13, 6, 'Yivani ngoku iingcinga zam, uwubazele indlebe umthandazo womlomo wam.', 'XHO22'),
(22, 13, 7, 'Niya kuthetha ububi na kuThixo? uze uthethe naye ngenkohliso?', 'XHO22'),
(22, 13, 8, 'Uya kubamkela na ubuntu bakhe? Niya kumlwela na uThixo?', 'XHO22'),
(22, 13, 9, 'Kufanelekile na ukuba akugocagoce? Ngaba nawe uhlekisa ngaye na?', 'XHO22'),
(22, 13, 10, 'Wonohlwaya inene, xa namkela abantu emfihlekweni;', 'XHO22'),
(22, 13, 11, 'Ubude bakhe abuyi kukukhwankqisa na? kwaye iimbandezelo zakhe zawela phezu kwenu?', 'XHO22'),
(22, 13, 12, 'Iingcinga zakho zinjengothuthu, nomzimba wakho unjengomzimba wodongwe.', 'XHO22'),
(22, 13, 13, 'Yithini cwaka, ndime, ukuze ndithethe, ndokutyhilelwa kum.', 'XHO22'),
(22, 13, 14, 'Yini na ukuba ndiyibambe inyama yam ngamazinyo am, Umphefumlo wam ndiwubeke esandleni sam?', 'XHO22'),
(22, 13, 15, 'Nokuba uthe wandibulala, ndokholosa ngaye; Ke mna ndiya kuziqinisa iindlela zam phambi kwakhe.', 'XHO22'),
(22, 13, 16, 'Woba lusindiso kum ke, Ngokuba akuyi kuza kuye intshembezana;', 'XHO22'),
(22, 13, 17, 'Phulaphulani ngenyameko ilizwi lam, ukuthetha kwam ngeendlebe zenu.', 'XHO22'),
(22, 13, 18, 'Khawubone, ndilicacisile ityala lam; Ndiyazi ukuba ndiza kutyholwa.', 'XHO22'),
(22, 13, 19, 'Ngubani na oya kubambana nam? Kuba ngoku, ukuba ndithe ndabamba umlomo, ndowukhupha umoya.', 'XHO22'),
(22, 13, 20, 'Musani ukwenza izinto ezimbini kum, ukuze ndingazifihleli kuni;', 'XHO22'),
(22, 13, 21, 'Sisuse kum isandla sakho, Makungandoyikisi ukoyika kwakho;', 'XHO22'),
(22, 13, 22, 'Biza ke, ndisabele mna; okanye mandithi, ndiphendule.', 'XHO22'),
(22, 13, 23, 'Buphi na ubugwenxa bam, nezono zam? Ndazise ukreqo lwam nesono sam.', 'XHO22'),
(22, 13, 24, 'Yini na ukuba ubusithelise ubuso bakho, undifihle elutshabeni lwakho?', 'XHO22'),
(22, 13, 25, 'Ngaba unokuwuqhekeza umthi ogawulweyo apha naphaya? yaye ngaba uya kuleqa iindiza ezomileyo na?', 'XHO22'),
(22, 13, 26, 'Ngokuba uzibhalela kum izinto ezikrakra, Ndibudle ilifa ububiubutsha bam;', 'XHO22'),
(22, 13, 27, 'Ubuye wazifaka iinyawo zam esitokisini, kwaye uyazibona iindlela zam zonke; Ubeke uphawu ezithendeni zeenyawo zam.', 'XHO22'),
(22, 13, 28, 'Utshabalala njengevila, njengengubo edliwe ngamanundu.', 'XHO22'),
(22, 14, 1, 'Umntu ozelwe ngumfazi uneentsuku ezimbalwa ezelwe, ezele ziinkxwaleko.', 'XHO22'),
(22, 14, 2, 'Uphuma njengentyantyambo, abune; ubaleka njengesithunzi, angemi.', 'XHO22'),
(22, 14, 3, 'Yaye ngaba uyawavula amehlo akho ngokuphathelele umntu onjalo uze undimise phambi kwam ematyaleni kunye nawe?', 'XHO22'),
(22, 14, 4, 'Ngubani na ongakhupha ohlambulukileyo koyinqambi? AWUKHO wedwa.', 'XHO22'),
(22, 14, 5, 'Xa imihla yakhe imisiwe, lingekafiki wena inani leenyanga zakhe, Ummisele umda wakhe angakwaziyo ukuwucanda;', 'XHO22'),
(22, 14, 6, 'Myeke, aphumle, achithe imihla yakhe njengenxila.', 'XHO22'),
(22, 14, 7, 'Ngokuba kukho ithemba emthini, xa uthe wagawulwa, wobuya uhlume, Igatya lawo lingatshabalali.', 'XHO22'),
(22, 14, 8, 'Nokuba ingcambu yakhe yonakele emhlabeni, Sife nesiqu sakhe emhlabeni;', 'XHO22'),
(22, 14, 9, 'Kodwa ngevumba lamanzi iya kutyatyamba ize yenze amasebe njengesityalo.', 'XHO22'),
(22, 14, 10, 'Ke yena umntu ufa alahlekwe; Ewe, umntu unika isinyi, kwaye uphi?', 'XHO22'),
(22, 14, 11, 'Njengoko edlula amanzi olwandle, etshatyalaliswe nonogumbe, komile;', 'XHO22'),
(22, 14, 12, 'Ulala ke akavuki; Lide lidlule izulu, abasayi kuvuka ebuthongweni babo.', 'XHO22'),
(22, 14, 13, 'Ukuba ungathanda ukundingcwaba engcwabeni, ndigcine ude uphele umsindo wakho, ndibekele ixesha uze undikhumbule!', 'XHO22'),
(22, 14, 14, 'Ukuba umntu uthe wafa, wophila na? ngayo yonke imihla yokumiselwa kwam, ndolinda kude kufike ukukhululwa kwam.', 'XHO22'),
(22, 14, 15, 'Uya kubiza, ndisabele; wowunqwenela umsebenzi wezandla zakho.', 'XHO22'),
(22, 14, 16, 'Kuba ngoku uyawabala amabanga am; Awukhathali ngesono sam?', 'XHO22'),
(22, 14, 17, 'Ukreqo lwam luvalelwe engxoweni; Ubusebenza ubugwenxa bam.', 'XHO22'),
(22, 14, 18, 'Kwaye eneneni, intaba ewileyo iyatshabalala, neliwa liyasuswa endaweni yalo.', 'XHO22'),
(22, 14, 19, 'Amanzi opha amatye; kwaye uyawatshabalalisa amathemba abantu.', 'XHO22'),
(22, 14, 20, 'Umeyisile ngonaphakade, wagqitha; Uyamtshintsha ubuso umshiye.', 'XHO22'),
(22, 14, 21, 'Oonyana bakhe bayakhazimla, akazi yena; bathotywa, akabaqonda.', 'XHO22'),
(22, 14, 22, 'Kwaye inyama yakhe iya kuba buhlungu kuye, nomoya wakhe ngaphakathi kuye uya kukhala.', 'XHO22'),
(22, 15, 1, 'Waphendula uElifazi wakwaTeman, wathi:', 'XHO22'),
(22, 15, 2, 'Ngaba isilumko sinokuthetha into engento yanto, size sizalise isisu saso ngomoya wasempumalanga?', 'XHO22'),
(22, 15, 3, 'Ngaba ufanele uqiqe ngembudane? okanye ngeelwimi angenakwenza nto ngazo?', 'XHO22'),
(22, 15, 4, 'Ewe, uyakulahla ukunkwantya, athintele umthandazo phambi koThixo.', 'XHO22'),
(22, 15, 5, 'Ngokuba umlomo wakho uthetha ngobugwenxa bakho, Unyule ulwimi lwenkohliso.', 'XHO22'),
(22, 15, 6, 'Asindim okugwebayo ngumlomo wakho; Ewe, umlomo wakho uyangqina ngawe.', 'XHO22'),
(22, 15, 7, 'Ngaba ungowokuqala ukuzalwa? Ngaba wenziwa ngaphambi kweenduli?', 'XHO22'),
(22, 15, 8, 'Wakha wayiva na imfihlelo kaThixo? Wabugcina kuwe na ubulumko bakho?', 'XHO22'),
(22, 15, 9, 'Wazi ntoni na, esingayaziyo thina? uqonde ntoni na, hayi kuthi?', 'XHO22'),
(22, 15, 10, 'Phakathi kwethu kukho amadoda anezimvi namaxhego, amadala kakhulu kunoyihlo.', 'XHO22'),
(22, 15, 11, 'Zincinane na kuwe iintuthuzelo zikaThixo? unayo imfihlo?', 'XHO22'),
(22, 15, 12, 'Yini na ukuba ithabatheke intliziyo yakho? kulapho amehlo akho akhangele khona;', 'XHO22'),
(22, 15, 13, 'Ukuba uthe wawuphikisa umphefumlo wakho kuThixo, ukhupha amazwi anjalo emlonyeni wakho?', 'XHO22'),
(22, 15, 14, 'Yintoni umntu omele ahlambuluke? Nalowo uzelweyo ngumfazi, angaba lilungisa na?', 'XHO22'),
(22, 15, 15, 'Yabona, akakholosi ngabakhe benceba; nezulu alihlambulukile phambi kwamehlo akhe.', 'XHO22'),
(22, 15, 16, 'Ngaphezu koko, umntu osela ubungendawo njengamanzi ulizothe yaye akahlambulukanga?', 'XHO22'),
(22, 15, 17, 'Ndiya kukubonisa, phulaphula kum; kwaye endikubonileyo ndiya kukuxelela;', 'XHO22'),
(22, 15, 18, 'Okwaxelwa zizilumko kooyise, zaza akwafihla;', 'XHO22'),
(22, 15, 19, 'Ngabo bodwa elanikwa bona ihlabathi, Akuzanga kucande mntu wasemzini phakathi kwabo.', 'XHO22'),
(22, 15, 20, 'Yonke imihla yakhe ongendawo uyabandezeleka, Nenani leminyaka liqwetyelwe ingqwangangqwili.', 'XHO22'),
(22, 15, 21, 'Kuvakala ilizwi eloyikekayo ezindlebeni zakhe;', 'XHO22'),
(22, 15, 22, 'Akakholwa ukuba uya kubuya emnyameni; Ke ulindelwe likrele.', 'XHO22'),
(22, 15, 23, 'Wabhadula efuna isonka, wathi, Siphi na? uyazi, ngokuba imini yobumnyama ikufuphi kuye.', 'XHO22'),
(22, 15, 24, 'Iimbandezelo neenkxwaleko ziya kumkhwankqisa; ziya kudlula kuye njengokumkani olungiselela imfazwe.', 'XHO22'),
(22, 15, 25, 'Ngokuba esolulele ngakuThixo isandla sakhe, Unamandla phezu koSomandla.', 'XHO22'),
(22, 15, 26, 'Uya kumgila, ewe, emqaleni wakhe, ebuboya obuninzi bamakhaka akhe.', 'XHO22'),
(22, 15, 27, 'Kuba egqubuthele ubuso bakhe ngamanqatha akhe, Walityebisa icala lakhe.', 'XHO22'),
(22, 15, 28, 'Yaye uye wahlala kwizixeko ezidala nakwizindlu ezingenabemi, ezikufuphi ukuba zishiywe.', 'XHO22'),
(22, 15, 29, 'Akayi kuba sisityebi, nobutyebi bakhe abuyi kubakho, nemfezeko yabo ayiyi kubandisa ehlabathini.', 'XHO22'),
(22, 15, 30, 'Akayi kubushiya ubumnyama; Amasebe akhe aya kuwatshisa amadangatye, Emke ngomoya womlomo wakhe.', 'XHO22'),
(22, 15, 31, 'Okhohliswayo makangakholosi ngelize, ngokuba umvuzo wamampunge uyafana.', 'XHO22'),
(22, 15, 32, 'Uya kugqitywa ngaphambi kwexesha lawo, kwaye isebe lawo aliyi kuba luhlaza.', 'XHO22'),
(22, 15, 33, 'Wovuthulula njengediliya iziqhamo zakhe ezikrwada, avuthulule njengomnquma intyantyambo yakhe.', 'XHO22'),
(22, 15, 34, 'Kuba ibandla leentshembenxa liya kuba yintlango, nomlilo uzitshise iintente zokucenga.', 'XHO22'),
(22, 15, 35, 'Bakhawula ububi, bazalele ilize, Nezizalo zabo zinenkohliso.', 'XHO22'),
(22, 16, 1, 'Waphendula uYobhi, wathi:', 'XHO22'),
(22, 16, 2, 'Ndive izinto ezininzi ezinjengezo; ningabathuthuzeli abangamatshijolo nonke niphela.', 'XHO22'),
(22, 16, 3, 'Ngaba amazwi alambathayo aya kuphela? Okanye yintoni ekomelezayo ukuba uphendule?', 'XHO22'),
(22, 16, 4, 'Nam bendiya kukwazi ukuthetha njengani: ukuba umphefumlo wenu ubusendaweni yomphefumlo wam, bendiya kuhlanganisana amazwi kuni, ndihlunguzele intloko.', 'XHO22'),
(22, 16, 5, 'Ke bendiya kunikhaliphisa ngomlomo wam, Nokukhuza komlomo wam kuzithobe iintlungu zenu.', 'XHO22'),
(22, 16, 6, 'Nokuba ndithe ndathetha, awunqabi umvandedwa wam;', 'XHO22'),
(22, 16, 7, 'Hayi, undithobile ngoku, uyitshabalalisile ingxokolo yam yonke;', 'XHO22'),
(22, 16, 8, 'Undihluthise ngemibimbi, oko oko kube bubungqina nxamnye nam, nokunyuka kwam ukuza kum ubutsha bam, bububungqina bobuso bam.', 'XHO22'),
(22, 16, 9, 'Ondithiyileyo uginya ngomsindo wakhe; Utshixize amazinyo; utshaba lwam lundijongile.', 'XHO22'),
(22, 16, 10, 'Bathetha kum ngemilebe yomlomo wabo; bandibetha esidleleni ngesithuko; bandihlanganisa.', 'XHO22'),
(22, 16, 11, 'uThixo undinikele kwabangendawo, Wandinikela esandleni sabangendawo.', 'XHO22'),
(22, 16, 12, 'Ndaxola, wandahlula; Uphinde wandibamba entanyeni wandibamba wandenza iziqwenga wandimisa njengophawu lwakhe.', 'XHO22'),
(22, 16, 13, 'Bandithiyela ngezaphetha, wazityanda izintso zam, angabuyi umva; Utshica inyongo yam emhlabeni.', 'XHO22'),
(22, 16, 14, 'Undikhohlisa, ugidima eze kum njengegorha.', 'XHO22'),
(22, 16, 15, 'Ndathungela iingubo ezirhwexayo enkwaneni yolusu lwam, Ndalucofa uphondo lwam;', 'XHO22'),
(22, 16, 16, 'Ubuso bam budyobhekile kukulila, Phezu kwamashiya am lithunzi lokufa;', 'XHO22'),
(22, 16, 17, 'Kungabi ngamanyala asezandleni zam; Uqaqambile umthandazo wam.', 'XHO22'),
(22, 16, 18, 'Owu mhlaba, musa ukuligquma igazi lam, nokukhala kwam makungabi nandawo.', 'XHO22'),
(22, 16, 19, 'Kaloku, yabona, lisemazulwini ingqina lam;', 'XHO22'),
(22, 16, 20, 'Abahlobo bam bayanditshata, kodwa iliso lam liyaphalala kuThixo.', 'XHO22'),
(22, 16, 21, 'Hayi, ukuba umntu angamlamlela umntu kuThixo, njengokuba ethetha kummelwane wakhe!', 'XHO22'),
(22, 16, 22, 'Emva kweminyaka embalwa, ndiya kuhamba ngendlela endingayi kubuya ndibuyele kuyo.', 'XHO22'),
(22, 17, 1, 'Umphefumlo wam wonakele, imihla yam iphelile, Ndilungiselelwe amangcwaba.', 'XHO22'),
(22, 17, 2, 'Andinazo na iziqhulo? Iliso lam aliphumli ekuhlekeni kwabo?', 'XHO22'),
(22, 17, 3, 'Hamba, ulale, uwuthabathe umnqophiso wam nawe; ngubani ozondibetha?', 'XHO22'),
(22, 17, 4, 'Ngokuba intliziyo yabo uyifihlele ukuqiqa; ke ngoko, musani ukubaphakamisa.', 'XHO22'),
(22, 17, 5, 'Oncoma izihlobo zakhe uphela amehlo abantwana bakhe.', 'XHO22'),
(22, 17, 6, 'Undenze intlekisa ebantwini; kwaye ngaphambi kokuba ndibe njengegubu.', 'XHO22'),
(22, 17, 7, 'Laza lanorhatyazo iliso lam kukukhathazeka; Amalungu am anjengesithunzi onke ephela.', 'XHO22'),
(22, 17, 8, 'Omangaliswa yiloo nto amalungisa, abamsulwa basukele phezulu kwabahanahanisi.', 'XHO22'),
(22, 17, 9, 'Kanjalo, ilungisa liya kuyigcina indlela yalo, yaye onezandla ezihlambulukileyo uya komelela ngakumbi.', 'XHO22'),
(22, 17, 10, 'Ke nina nonke, buyani, nize; ngokuba andifumananga silumko phakathi kwenu.', 'XHO22'),
(22, 17, 11, 'Iphelile imihla yam, icikizekile amacebo am, neengcinga zentliziyo yam.', 'XHO22'),
(22, 17, 12, 'Bajika ubusuku bube yimini, isikhanyiso esifutshane ngobumnyama.', 'XHO22'),
(22, 17, 13, 'Ukuba ndithe ndathemba, ndithembe elabafileyo, likhaya lam; Ndondlula ibhedi yam kwamnyama.', 'XHO22'),
(22, 17, 14, 'Ndathi kwintshabalalo, Ungubawo; kumpethu: Ungumama wam nodade wethu.', 'XHO22'),
(22, 17, 15, 'Liphi na ke ngoku ithemba lam? Ithemba lam, ngubani na owolibona?', 'XHO22'),
(22, 17, 16, 'Ziya kuhla ziye emivalweni yelabafileyo, Siphumle kunye eluthulini.', 'XHO22'),
(22, 18, 1, 'Waphendula uBhiledadi wakwaMoses, wathi,', 'XHO22'),
(22, 18, 2, 'Kuya kuthatha ixesha elingakanani ukuba uyeke ukuthetha? Phawula, kwaye emva koko siya kuthetha.', 'XHO22'),
(22, 18, 3, 'Kutheni kusithiwa sigqalwa njengeenkomo yaye sigqalwa njengabadelekileyo emehlweni akho?', 'XHO22'),
(22, 18, 4, 'Ukhala ngomsindo wakhe, esithi, Liya kushiywa na ilizwe ngenxa yakho? Linokususwa na iliwa endaweni yalo?', 'XHO22'),
(22, 18, 5, 'Nokukhanya kongendawo kuya kucinywa, Lingabi mhlophe ilangatye lomlilo wakhe.', 'XHO22'),
(22, 18, 6, 'Ukukhanya kuya kwenziwa mnyama ententeni yakhe, nesibane sakhe sicinywe kunye naye.', 'XHO22'),
(22, 18, 7, 'Amanyathelo akhe omelele, necebo lakhe liya kumwisa.', 'XHO22'),
(22, 18, 8, 'Kuba iinyawo zakhe zifakwe esibatheni, wahamba ke esibatheni.', 'XHO22'),
(22, 18, 9, 'Inxila liya kumbamba ngesithende, yaye isela liya kumeyisa.', 'XHO22'),
(22, 18, 10, 'Ubanjiselwe isirhintyelo emhlabeni, Nomgibe endleleni yakhe.', 'XHO22'),
(22, 18, 11, 'Unkwantyo luya kumothusa macala onke luze lumqhubele ezinyaweni zakhe.', 'XHO22'),
(22, 18, 12, 'Amandla akhe aya kufa yindlala, Intshabalalo isondele kuye.', 'XHO22'),
(22, 18, 13, 'Iya kudla ukuqina kolusu lwakhe; owamazibulo ukufa uya kuwadla amandla akhe.', 'XHO22'),
(22, 18, 14, 'Ithemba lakhe liya kuncothulwa ententeni yakhe, Limzise kukumkani wokukhwankqisa.', 'XHO22'),
(22, 18, 15, 'Uya kuhlala ententeni yakhe, kuba ingeyakhe; Indlu yakhe iya kutshizwa isulfure.', 'XHO22'),
(22, 18, 16, 'Ngaphantsi ziya koma iingcambu zawo, linqunyulwe isebe lawo phezulu.', 'XHO22'),
(22, 18, 17, 'Inkumbulo yakhe iya kudaka ehlabathini, angabi nagama ezitratweni.', 'XHO22'),
(22, 18, 18, 'Uya kugxothwa ekukhanyeni, aye ebumnyameni, agxothwe ehlabathini.', 'XHO22'),
(22, 18, 19, 'Akayi kuba nanyana nanzala ebantwini bakowabo, namasalela eminqubeni yakhe.', 'XHO22'),
(22, 18, 20, 'Baya kumangaliswa yimini yakhe abaya kulandela emva kwakhe, njengokuba babesoyika abamandulela.', 'XHO22'),
(22, 18, 21, 'Inene, zizakhiwo zabangendawo, Indawo yabangamaziyo uThixo.', 'XHO22'),
(22, 19, 1, 'Waphendula uYobhi, wathi:', 'XHO22'),
(22, 19, 2, 'Kunini na nisenza isingqala kumphefumlo wam, Nindingcikiva ngamazwi?', 'XHO22'),
(22, 19, 3, 'Ezi zihlandlo zilishumi nindanga kabuhlungu;', 'XHO22'),
(22, 19, 4, 'Ukuba okunene ndonile, ityala lam lihleli phezu kwam.', 'XHO22'),
(22, 19, 5, 'Ukuba okunene nithanda ukuziphakamisa nxamnye nam nokuthetha ngendelelo yam nxamnye nam;', 'XHO22'),
(22, 19, 6, 'Yazini ke ngoku, ukuba uThixo undijikile, Wandirhawula ngomnatha wakhe;', 'XHO22'),
(22, 19, 7, 'Yabona, ndiyakhala, ndikhala, ndingaviwa; Ndiyakhala kakhulu, kodwa akukho sigwebo.', 'XHO22'),
(22, 19, 8, 'Uyivingcile indlela yam, akwabakho ukugqitha; Ezingqushwini zam wabeka ubumnyama.', 'XHO22'),
(22, 19, 9, 'Yalususa uzuko lwam, yathabatha isithsaba entlokweni yam;', 'XHO22'),
(22, 19, 10, 'Wandidiliza ngeenxa zonke, ndemka, Walisusa ithemba lam njengomthi.', 'XHO22'),
(22, 19, 11, 'Wabuya wabuya umsindo wakhe, wandinyusa, Wandigqala njengesinye seentshaba zakhe.', 'XHO22'),
(22, 19, 12, 'Imikhosi yakhe ibingumkhosi wakhe, yaza yalwa nam, Yayirhawula intente yam;', 'XHO22'),
(22, 19, 13, 'Ubasusile abazalwana bam kude kum, Abandaziyo bagxothiwe kum.', 'XHO22'),
(22, 19, 14, 'Intsapho yakowethu iwile, yaye abahlobo bam abadumileyo bandishiyile.', 'XHO22'),
(22, 19, 15, 'Abahlala endlwini yam, nabakhonzazana bam, bathi ndingowasemzini; Ndingumphambukeli emehlweni abo.', 'XHO22'),
(22, 19, 16, 'Ndibize umkhonzi wam, akandiphendula; Ndamncamisa emlonyeni.', 'XHO22'),
(22, 19, 17, 'Umoya wam ucocekile ngenxa yomkam;', 'XHO22'),
(22, 19, 18, 'Ewe, nabancinci badlale ngam; Ndaphakama bathetha ngam.', 'XHO22'),
(22, 19, 19, 'Bonke abahlobo bam bandithiyile, Nabandithandayo bandijikele.', 'XHO22'),
(22, 19, 20, 'Ithambo lam libotshelelwe eluswini lwam nasenyameni yam; Ndisinde ngolusu lwamazinyo am.', 'XHO22'),
(22, 19, 21, 'Tarhuni, bazalwana bam! ngokuba isandla sikaThixo siphezu kwam.', 'XHO22'),
(22, 19, 22, 'Kutheni le nto ninditshutshisa njengoThixo, ninganeli yinyama yam?', 'XHO22'),
(22, 19, 23, 'Owu, ngaba amazwi am abhaliwe ngoku! ukuba nje bezinokushicilelwa encwadini!', 'XHO22'),
(22, 19, 24, 'Sibhalwe eweni, ngesikhonkwane sentsimbi nelothe ngonaphakade;', 'XHO22'),
(22, 19, 25, 'Ngokuba ndiyazi mna ukuba uhleli umsindisi wam, Uya kusuka eme phezu komhlaba ekupheleni kwemihla.', 'XHO22'),
(22, 19, 26, 'Nangona iimpethu ziwudlavula lo mzimba, kodwa enyameni yam ndiya kumbona uThixo.', 'XHO22'),
(22, 19, 27, 'Ndiya kumbona ke, namehlo am aya kumbona, akukho wumbi; nokuba umphefumlo wam ubutshatyalalisiwe kum.', 'XHO22'),
(22, 19, 28, 'Kodwa niya kuthi, ‘Kutheni simtshutshisa nje, ekubeni ingcambu yalo mbandela ifunyenwe kum?', 'XHO22'),
(22, 19, 29, 'Loyikani ikrele, ngokuba ingqumbo iza ngomgwebo womkhonto, Ukuze nazi ukuba kukho umgwebo.', 'XHO22'),
(22, 20, 1, 'Waphendula uTsofare waseNahama, wathi,', 'XHO22'),
(22, 20, 2, 'Ngako oko iingcingangcinga zam ziyandiphendula, Kungenxa yoko le nto ndikhawulezayo.', 'XHO22'),
(22, 20, 3, 'Ndikuvile ukungcikiva kwam, wandiphendula umoya wengqondo yam.', 'XHO22'),
(22, 20, 4, 'Anazi na, ukuba kususela kudala umntu wamiswa ehlabathini?', 'XHO22'),
(22, 20, 5, 'Kuba usindiso lwabangendawo lulufutshane, Ukuvuya kwabangendawo kokwephanyazo?', 'XHO22'),
(22, 20, 6, 'Nangona ubukhulu bakhe bufikelela esibhakabhakeni, intloko yakhe iye emafini;', 'XHO22'),
(22, 20, 7, 'Njengelindle lakhe uya kudaka kuphele; abambonayo bathi, Uphi na?', 'XHO22'),
(22, 20, 8, 'Uya kuphaphazela njengephupha, angafunyanwa; ewe, uya kulahlelwa phandle njengobuso bobusuku.', 'XHO22'),
(22, 20, 9, 'Kwaneliso lambonayo aliyi kuphinda limbone; nesihlalo sakhe asiyi kuphinda simbone.', 'XHO22'),
(22, 20, 10, 'Abantwana bakhe baya kungxamela isisweli, Izandla zakhe zibuyise impahla yakhe.', 'XHO22'),
(22, 20, 11, 'Amathambo akhe ebezele bubugwenxa bobutsha; Bolaliswa phantsi naye eluthulini.', 'XHO22'),
(22, 20, 12, 'Nokuba ububi bunencasa emlonyeni wakhe, Wabufihla phantsi kolwimi lwakhe;', 'XHO22'),
(22, 20, 13, 'Nokuba uthe wayibamba, akaphuma; zigcine nje emlonyeni wakhe.', 'XHO22'),
(22, 20, 14, 'Inyama yakhe ijikile nje emathunjini akhe, yinyongo yomsindo phakathi kwakhe.', 'XHO22'),
(22, 20, 15, 'Uginye ubutyebi, wabutshica; uThixo uya kuyikhupha esiswini sakhe.', 'XHO22'),
(22, 20, 16, 'Uya kwanya ubuthi bezizwe, ulwimi lwezizwe luya kumbulala.', 'XHO22'),
(22, 20, 17, 'Akayi kubona milambo, milambo, zihlambo zobusi, nezigudileyo.', 'XHO22'),
(22, 20, 18, 'Uya kukubuyisa oko akusebenzileyo, angaginyi; ngokomvuzo wakhe olungileyo, akayi kuwudla.', 'XHO22'),
(22, 20, 19, 'Ngenxa yokuba wabacinezela waza wabashiya abasweleyo; ngokuba wayithimba indlu abengayakhanga;', 'XHO22'),
(22, 20, 20, 'Okunene, akayi kuziva ezolile esiswini sakhe, akayi kwenza nto ngaphandle kokuthanda kwakhe.', 'XHO22'),
(22, 20, 21, 'Makungasali nto ekudleni kwakhe; ngoko ke, akukho bani uya kufuna okwakhe.', 'XHO22'),
(22, 20, 22, 'Ebudaleni bakhe uya kudideka;', 'XHO22'),
(22, 20, 23, 'Xa athe wahlutha isisu sakhe, uThixo wowuthululela phezu kwakhe ukuvutha komsindo wakhe, anise imvula phezu kwakhe, esidla.', 'XHO22'),
(22, 20, 24, 'Usabe isixhobo sentsimbi, umgqobhoze isaphetha sentsimbi.', 'XHO22'),
(22, 20, 25, 'Uyatsalwa, uphumele ngaphandle komzimba; ewe, ikrele elibengezelayo liphuma enyongweni yakhe; kufike ukuvuswa kuye.', 'XHO22'),
(22, 20, 26, 'Ubumnyama bonke buya kufihlwa emifihlekweni yabo; umlilo ongatshiyo uya kumtshisa; kuya kuba kubi kohlala ententeni yakhe.', 'XHO22'),
(22, 20, 27, 'Izulu liya kubutyhila ubugwenxa bakhe; kwaye ihlabathi liya kumvukela.', 'XHO22'),
(22, 20, 28, 'Ziya kumka iziqhamo zendlu yakhe, Buphalale ubuncwane bakhe ngemini yomsindo wakhe.', 'XHO22'),
(22, 20, 29, 'Siso eso isabelo somfo wakwaThixo, nelifa awalibelwe nguThixo.', 'XHO22'),
(22, 21, 1, 'Waphendula uYobhi, wathi,', 'XHO22'),
(22, 21, 2, 'Liphulaphuleni ilizwi lam, libe yintuthuzelo yenu.', 'XHO22'),
(22, 21, 3, 'Ndivuse ndithethe; ndatsho emva koko ndiphoxisa.', 'XHO22'),
(22, 21, 4, 'Mna ke, ukukrokra kwam ebantwini? kwaye ukuba kwakunjalo, kutheni umphefumlo wam ungashukunyiswa?', 'XHO22'),
(22, 21, 5, 'Khangela kum, umangalise, ubeke isandla emlonyeni.', 'XHO22'),
(22, 21, 6, 'Ndithi ndakuyicinga le nto, ndikhwankqiswe, Nenyama yam igubhe.', 'XHO22'),
(22, 21, 7, 'Yini na ukuba abangendawo badle ubomi, Babe badala, kananjalo bomelele?', 'XHO22'),
(22, 21, 8, 'Abazukulwana babo bema nabo phambi kwamehlo abo, nabazukulwana babo phambi kwamehlo abo.', 'XHO22'),
(22, 21, 9, 'Izindlu zabo azinkwantyi, Nentonga kaThixo ayibafikeli.', 'XHO22'),
(22, 21, 10, 'Inkunzi yabo yenkomo iyazala, ingatshi; lithole lenkomo, alilishiyi ithole lalo.', 'XHO22'),
(22, 21, 11, 'Ziwazala njengomhlambi, oonyana bazo bayalila.', 'XHO22'),
(22, 21, 12, 'Bathabatha igubu elincinane nezembe, balivuyela isandi sesigodlo.', 'XHO22'),
(22, 21, 13, 'Bayayigqiba imihla yabo besisityebi, Behle ngephanyazo ukuya kwelabafileyo.', 'XHO22'),
(22, 21, 14, 'Ke ngoko, bathi kuThixo: Mka kuthi; kuba asikunanzile ukuzazi iindlela zakho.', 'XHO22'),
(22, 21, 15, 'Uyini na uSomandla, ukuba simkhonze? yaye siya kungenelwa ngantoni ukuba sithandaza kuye?', 'XHO22'),
(22, 21, 16, 'Yabona, izinto zabo ezilungileyo azisesandleni sabo; Iqhinga labangendawo malibe lee kum.', 'XHO22'),
(22, 21, 17, 'Kukangaphi na ke ukuba isibane sabangendawo sicinywe! Kukangaphi na ukubahlela! uThixo uchithachitha usizi ngomsindo wakhe.', 'XHO22'),
(22, 21, 18, 'Bafana neendiza phambi komoya nanjengomququ ophetshethwa sisaqhwithi.', 'XHO22'),
(22, 21, 19, 'uThixo uyabunqanda ubugwenxa bakhe koonyana bakhe; Uphindezela, kwaye uya kwazi.', 'XHO22'),
(22, 21, 20, 'Amehlo akhe aya kukubona ukwaphuka kwabo, Babusele ubushushu boSomandla.', 'XHO22'),
(22, 21, 21, 'Ngokuba yabona ntoni na emva kwayo endlwini yayo, ukuba lichanelwe phakathi inani leenyanga zayo?', 'XHO22'),
(22, 21, 22, 'Ngaba kukho nabani na oya kufundisa uThixo ukwazi? Ke yena ogweba abaphezulu.', 'XHO22'),
(22, 21, 23, 'Omnye ufa engenamandla, ophumle, ezolile.', 'XHO22'),
(22, 21, 24, 'Isifuba sakhe sizele ngamasi, Nomongo wawo utyebile.', 'XHO22'),
(22, 21, 25, 'Umntu ufa enentliziyo eyaphukileyo, engatyanga ncam.', 'XHO22'),
(22, 21, 26, 'Baya kulala eluthulini, iimpethu zibagubungele;', 'XHO22'),
(22, 21, 27, 'Yabonani, ndiyazazi iingcinga zenu, nobungendawo benu;', 'XHO22'),
(22, 21, 28, 'Ngokuba nithi, Iphi na indlu yokumkani? ziphi na izakhiwo ezingendawo?', 'XHO22'),
(22, 21, 29, 'Anibuzanga na kwabo basendleleni na? aniyazi na imiqondiso yabo?', 'XHO22'),
(22, 21, 30, 'Ukuba abangendawo basindiselwe imini yentshabalalo? baya kukhutshelwa ngaphandle kude kube yimini yokuphuphuma komsindo.', 'XHO22'),
(22, 21, 31, 'Ngubani na owoxela indlela yakhe emehlweni akhe? ewe, ngubani na oya kuphindezela kuye?', 'XHO22'),
(22, 21, 32, 'Kodwa uya kusiwa kwelabafileyo, ahlale kwelabafileyo.', 'XHO22'),
(22, 21, 33, 'Aya kuba mnandi kuye amaxolo omfula oqukuqelayo, bamlandele bonke, njengabaninzi abamkhaphayo.', 'XHO22'),
(22, 21, 34, 'Ningathini na ke ukundithuthuzela ngeento ezingento, libubuxoki nje iimpendulo zenu?', 'XHO22'),
(22, 22, 1, 'Waphendula uElifazi wakwaTeman, wathi:', 'XHO22'),
(22, 22, 2, 'Umntu unokumnceda na ke uThixo, njengokuba indoda elumkileyo inokuba luncedo kuye?', 'XHO22'),
(22, 22, 3, 'Kuhle na kuSomandla, ukuba lilungisa? Kumlungele na ukuba uthe wazilungisa iindlela zakho?', 'XHO22'),
(22, 22, 4, 'Wokukhalimela kabukhali na, ngokuba esoyika? Uya ematyaleni nawe na?', 'XHO22'),
(22, 22, 5, 'Ububi bakho abubukhulu na? nobugwenxa benu obungenasiphelo?', 'XHO22'),
(22, 22, 6, 'Kuba uthathe isibambiso kumntakwenu ngaphandle kwentlawulo, wabahluba abo babehamba ze.', 'XHO22'),
(22, 22, 7, 'Ubungamsezi amanzi odiniweyo, Olambileyo ubumvimba isonka.', 'XHO22'),
(22, 22, 8, 'Ukuba ke uthe waba ligorha, ulelona ihlabathi; kwaye bekuhlala indoda elinene.', 'XHO22'),
(22, 22, 9, 'Ubagxotha abahlolokazi bodwa, Zaphuke izandla zeenkedama.', 'XHO22'),
(22, 22, 10, 'Ngenxa yoko, izibatha zifihlwe ngaphakathi, kwaye uloyiko lwentlekele luyothusa;', 'XHO22'),
(22, 22, 11, 'Okanye ubumnyama obungenakububona; kwaye amanzi amaninzi awugubungela.', 'XHO22'),
(22, 22, 12, 'Akakho na uThixo emazulwini? Yekani ukuphakama kweenkwenkwezi!', 'XHO22'),
(22, 22, 13, 'Kwaye uthi: Wayazi njani uThixo loo nto? Unokugweba na, eselifini elimnyama?', 'XHO22'),
(22, 22, 14, 'Amafu asisigqubuthelo sakhe, ukuba angaboni; kwaye uhamba kwisazinge sezulu.', 'XHO22'),
(22, 22, 15, 'Umendo wamandulo uyawugcina na, Bahanjwe ngabangendawo?', 'XHO22'),
(22, 22, 16, 'owatshatyalaliswayo kwakudala, osiseko sagutyungelwa ngunogumbe;', 'XHO22'),
(22, 22, 17, 'abathi kuThixo, Sishiye; Wothini na ke uSomandla kubo?', 'XHO22'),
(22, 22, 18, 'Ke nina nizizalisile izindlu zabo ngezinto ezilungileyo; Ke yona iqhinga labangendawo likude lee kum.', 'XHO22'),
(22, 22, 19, 'Akubona oko amalungisa avuya, Abamsulwa bahlekisa ngawo,', 'XHO22'),
(22, 22, 20, 'nangona ubutyebi bethu bungatshabalali, noko iintsalela zabo zitshiswe ngumlilo.', 'XHO22'),
(22, 22, 21, 'Khawuziqhelisele, ube noxolo;', 'XHO22'),
(22, 22, 22, 'Wamkele umyalelo emlonyeni wakhe, Ugcine amazwi akhe entliziyweni yakho.', 'XHO22'),
(22, 22, 23, 'Ukuba uthe wabuyela kuSomandla, uya kwakhiwa; bususe ubungendawo ezintenteni zakho.', 'XHO22'),
(22, 22, 24, 'Yisuseni igolide ibe njengothuli, negolide yakwaOfire njengamatye equla.', 'XHO22'),
(22, 22, 25, 'Ewe, uSomandla uya kuba likhusi lakho, yaye uya kuba nentabalala yesilivere.', 'XHO22'),
(22, 22, 26, 'Ngokuba uya kuluphakamisela khona uvuyo lwakho kuSomandla, Nobuso bakho kuThixo.', 'XHO22'),
(22, 22, 27, 'Uthandaze kuye, wokuva, Azizalise izibhambathiso zakho.', 'XHO22'),
(22, 22, 28, 'Uze wenze isigqibo, sokulindela wena, Ukukhanya kukhanyisele umendo wakho.', 'XHO22'),
(22, 22, 29, 'Ekubhukuqweni kohlanga uthi, Luyasuka; abalulamileyo uya kubasindisa.', 'XHO22'),
(22, 22, 30, 'Uya kusisindisa isiqithi sabamsulwa, sisindiswe bubunyulu bezandla zakho.', 'XHO22'),
(22, 23, 1, 'Waphendula uYobhi, wathi:', 'XHO22'),
(22, 23, 2, 'Namhlanje kukrakra ukukhala kwam;', 'XHO22'),
(22, 23, 3, 'Owu, bendisazi apho bendinokumfumana khona! ukuze ndingene esihlalweni sakhe!', 'XHO22'),
(22, 23, 4, 'Bendiya kuxoxa ityala lam phambi kwakhe, Ndiwuzalise umlomo wam ngeziganeko.', 'XHO22'),
(22, 23, 5, 'Bendiya kukwazi abeya kundiphendula ngako, Ndiyiqonde into abeya kuyithetha kum.', 'XHO22'),
(22, 23, 6, 'Ingandoyisa na ngamandla ayo amakhulu? Hayi; kodwa ebeya kundibeka amandla.', 'XHO22'),
(22, 23, 7, 'Apho ilungisa liya kuxoxa naye; ndide ndikhululwe kumgwebi wam ngonaphakade.', 'XHO22'),
(22, 23, 8, 'Yabona, ndiya kuqala, angabikho; ngasemva, ndingamboni;', 'XHO22'),
(22, 23, 9, 'ngasekhohlo, apho asebenza khona, ndingamboni; uzimela ngasekunene, ukuba ndingamboni.', 'XHO22'),
(22, 23, 10, 'Ke niyayazi indlela endihamba ngayo; ukuba uthe wandilinga, ndophuma njengegolide.', 'XHO22'),
(22, 23, 11, 'Unyawo lwam lubambe emanyathelweni akhe, Ndayigcina indlela yakhe, andathi gu bucala.', 'XHO22'),
(22, 23, 12, 'Andisuki emyalelweni womlomo wakhe; Ndawathatha amazwi omlomo wakhe ngaphezu kokutya kwam.', 'XHO22'),
(22, 23, 13, 'Ke yena umphefumlo mnye, ngubani na ongambuyisayo? yaye uya kwenza into oyinqwenelayo umphefumlo wakhe.', 'XHO22'),
(22, 23, 14, 'Ngokuba uyakwenza endikumiseleyo; kanjalo zikho kuye izinto ezininzi ezinje.', 'XHO22'),
(22, 23, 15, 'Ndikhwankqisiwe ebusweni bakhe; xa ndicingayo ndiyamoyika.', 'XHO22'),
(22, 23, 16, 'Kuba uThixo uyayithoba intliziyo yam, yaye undenza buhlungu uSomandla.', 'XHO22'),
(22, 23, 17, 'Ngokuba andinqunyulwanga sisithokothoko, nawe akwabugubungelanga ubumnyama ebusweni bam.', 'XHO22'),
(22, 24, 1, 'Yini na ukuba kungafihlwa kuSomandla, Abamaziyo bangayiboni imihla yakhe?', 'XHO22'),
(22, 24, 2, 'Abanye bayayisusa imiqondiso; bathimba imfuyo, baluse ngamandla;', 'XHO22'),
(22, 24, 3, 'Kugqogqa iesile lenkedama, Babambise ngenkomo yomhlolokazi.', 'XHO22'),
(22, 24, 4, 'Kugilwa amahlwempu endleleni; abazimela ndawonye abaziintsizana behlabathi.', 'XHO22'),
(22, 24, 5, 'Yabona, njengamaesile asendle entlango, Baphuma nomsebenzi wabo; vuka ngexesha lokuvuna, intlango ibanike ukudla kwabantwana;', 'XHO22'),
(22, 24, 6, 'Elowo uvuna ingqolowa yakhe entsimini, ahlanganise isivuno sabangendawo.', 'XHO22'),
(22, 24, 7, 'Bahamba ze ebusuku, bengenangubo, Ukuze bangazisitheli engqeleni.', 'XHO22'),
(22, 24, 8, 'Bamanziswe sisiphango seentaba, yaye bawola iliwa ngenxa yokungabi nakhusi.', 'XHO22'),
(22, 24, 9, 'Kuhluthwa iinkedama esifubeni, Bambisane nabasweleyo.', 'XHO22'),
(22, 24, 10, 'Bamshiya eze, bezé, bathimba into eninzi yabantu ababelambile;', 'XHO22'),
(22, 24, 11, 'Abenza ioli ezindongeni zabo, Baxovula ezixovulelweni zabo, banxanwe.', 'XHO22'),
(22, 24, 12, 'Abantu bayancwina phakathi komzi, imiphefumlo yabangxwelerhiweyo yenza isijwili;', 'XHO22'),
(22, 24, 13, 'Ngabo abo bagwilika ekukhanyeni; abazazi iindlela zakhe, abahambi ezindleleni zakhe.', 'XHO22'),
(22, 24, 14, 'Umbulali ovuka ekukhanyeni ubulala usizana nehlwempu, nasebusuku ulisela njengesela.', 'XHO22'),
(22, 24, 15, 'Iliso lomkrexezi lijonge ukuba lityhilwe, athi, Akukho liso liya kundibona, liya kubusithelisa ubuso bakhe.', 'XHO22'),
(22, 24, 16, 'Emini baziqhekeza izindlu zabo, abazakhele bona; abalwazi ukukhanya.', 'XHO22'),
(22, 24, 17, 'Ngokuba intsasa injengethunzi lokufa kubo; ekwazazini kwazo kukho izothuso zethunzi lokufa.', 'XHO22'),
(22, 24, 18, 'Unamendu njengamanzi; liqalekisiwe iqashiso labo emhlabeni; akayiboni indlela eya ezidiliyeni.', 'XHO22'),
(22, 24, 19, 'Imbalela nobushushu butshisa amanzi ekhephu, ngokunjalo nelabafileyo abonayo.', 'XHO22'),
(22, 24, 20, 'Isizalo siya kumlibala; umbungu uya kumdla kamnandi; akayi kukhunjulwa; nobungendawo buya kugawulwa njengomthi.', 'XHO22'),
(22, 24, 21, 'Uthethela oludlolo, akamphathi kakuhle umhlolokazi.', 'XHO22'),
(22, 24, 22, 'Utsala amagorha ngamandla akhe; uvuka, yaye akukho bani uqinisekileyo ukuba uya kuphila.', 'XHO22'),
(22, 24, 23, 'Kwanokuba unikwe ukusindiswa, apho axhomekeke khona; kanti amehlo akhe aphezu kweendlela zabo.', 'XHO22'),
(22, 24, 24, 'Baphakanyiselwa phezulu, bemka, bethotywa; bathatyathwe endleleni njengabaseleyo, banqunyulwa njengobuso bemithanjana.', 'XHO22'),
(22, 24, 25, 'Ukuba kanti akunjalo, ngubani na owondenza ixoki, libe lilize ukuthetha kwam?', 'XHO22'),
(22, 25, 1, 'Waphendula uBhiledadi wakwaMoses, wathi,', 'XHO22'),
(22, 25, 2, 'Ubukumkani nokunkwantya kukuye; Wenza uxolo emimangweni yakhe;', 'XHO22'),
(22, 25, 3, 'Ngaba kukho inani lemikhosi yakhe? Ngubani na ke ongamphakamisiyo?', 'XHO22'),
(22, 25, 4, 'Ukuba kunjalo, angathini na umntu ukugwetyelwa nguThixo? Angathini na ukuba ngohlambulukileyo yena ozelwe ngumfazi?', 'XHO22'),
(22, 25, 5, 'Khangela inyanga, ingakhanyi; ewe, iinkwenkwezi azihlambulukile emehlweni akhe.', 'XHO22'),
(22, 25, 6, 'Ngaba umntu yimpethu na? Ke yena unyana womntu, ngubani na umpethu?', 'XHO22'),
(22, 26, 1, 'Waphendula uYobhi, wathi,', 'XHO22'),
(22, 26, 2, 'Umncede njani obuthathaka? umsindisa njani umntu oswele amandla?', 'XHO22'),
(22, 26, 3, 'Umcebise njani umntu ongenabulumko? kwaye utheni lo mbandela njengoko unjalo?', 'XHO22'),
(22, 26, 4, 'Uwathethe kubani la mazwi? Kuphume umoya kabani na kuni?', 'XHO22'),
(22, 26, 5, 'Kwadaleka izinto ezifileyo phantsi kwamanzi, nabemi bawo;', 'XHO22'),
(22, 26, 6, 'Umzimba uzé phambi kwakhe, Imbubhiso ayinangubo.', 'XHO22'),
(22, 26, 7, 'Ulaneka izulu langasentla enyanyeni, Axhome ihlabathi phezu kokungeni.', 'XHO22'),
(22, 26, 8, 'Ubutha amanzi emafini akhe; ilifu alahlukanga phantsi kwabo.', 'XHO22'),
(22, 26, 9, 'Uphethe ubuso betrone yakhe, waneka ilifu lakhe phezu kwayo.', 'XHO22'),
(22, 26, 10, 'Wawajikeleza amanzi ngemida, kwada kwahlwa, kwada kwahlwa;', 'XHO22'),
(22, 26, 11, 'Iintsika zezulu ziyanyikima, Zingakhulumi kukukhalima kwakhe.', 'XHO22'),
(22, 26, 12, 'Ngamandla akhe ulwahlula ulwandle, Ngengqondo yakhe uloyisa abanekratshi.', 'XHO22'),
(22, 26, 13, 'Wenza amazulu ngomoya wakhe; isandla sakhe sayila inyoka ejijekileyo.', 'XHO22'),
(22, 26, 14, 'Yabona, ngamancam eendlela zakhe lawo; kodwa ngubani na onokuyiqonda indudumo yamandla akhe?', 'XHO22'),
(22, 27, 1, 'uYobhi waqhubeka eqhubeka nomzekeliso wakhe, esithi:', 'XHO22'),
(22, 27, 2, 'NgoThixo ophilileyo, ulisusile ityala lam; noSomandla uwudandathekise umphefumlo wam;', 'XHO22'),
(22, 27, 3, 'Umoya wam uhleli kum, nomoya kaThixo usemathatheni am;', 'XHO22'),
(22, 27, 4, 'Umlomo wam awuthethi bugqwetha, Nolwimi lwam aluthethi nkohliso.', 'XHO22'),
(22, 27, 5, 'Ngamana uThixo anganenzeli ilungisa; Ndide ndife, andiyi kukususa kum ukonakala kwam.', 'XHO22'),
(22, 27, 6, 'Ndinamathele ebulungiseni bam, andibuyi; Intliziyo yam ayizundisinda ndisadla ubomi.', 'XHO22'),
(22, 27, 7, 'Makungabikho utshaba lwam lunjengongendawo, nalowo undivukelayo abe njengongalungisiyo.', 'XHO22'),
(22, 27, 8, 'Kuba liyintoni na ithemba lentshembenxa, ukuba ithe yazuza, xa athe wawunqumla, uThixo wawurhola umphefumlo wayo?', 'XHO22'),
(22, 27, 9, 'Wokuva na uThixo ukuhlahlamba kwakhe, xa efikelwa yimbandezelo?', 'XHO22'),
(22, 27, 10, 'Uya kumkholisa na uSomandla? Uya kuhlala unqula uThixo na?', 'XHO22'),
(22, 27, 11, 'Ndiya kuniyala ngesandla sikaThixo, Andiyi kukukhanyela oku kuSomandla.', 'XHO22'),
(22, 27, 12, 'Yabonani, nizibonile ngokwenu nonke; kutheni ungenanto?', 'XHO22'),
(22, 27, 13, 'Siso eso isabelo sabangendawo, esiphuma kuThixo, nelifa labacinezeli, abaya kusizuza kuSomandla.', 'XHO22'),
(22, 27, 14, 'Kuba bathe banda oonyana bakhe, ngenxa yentshuntshe, Oonyana bakhe abahluthi sisonka.', 'XHO22'),
(22, 27, 15, 'Amathambo akhe aya kungcwatywa kukufa, Abahlolokazi bakhe bangenzi sijwili.', 'XHO22'),
(22, 27, 16, 'Nakuba efumba isilivere njengothuli, Wayilungisa iingubo njengodongwe;', 'XHO22'),
(22, 27, 17, 'Unokuyilungisa, kodwa amalungisa aya kuyinxiba, kwaye abamsulwa baya kwabelana ngayo ngesilivere.', 'XHO22'),
(22, 27, 18, 'Uyakha indlu yakhe njengenundu nanjengephempe elenziwa ngumlindi.', 'XHO22'),
(22, 27, 19, 'Isityebi siya kulala, singaphindi; Uvula amehlo akhe, akakho.', 'XHO22'),
(22, 27, 20, 'Ufikelwa kukunkwantya njengamanzi, Uqhwithela luyamqweqwedisa ebusuku.', 'XHO22'),
(22, 27, 21, 'Ulophu lwasempumalanga luyamthabatha, ahambe, aluqweqwedise endaweni yakhe.', 'XHO22'),
(22, 27, 22, 'Ngokokuba uThixo uya kumlahla, akayi kumconga; angathanda ukubaleka esandleni sakhe.', 'XHO22'),
(22, 27, 23, 'Amadoda aya kuqhwaba izandla, abethe umlozi ukuba emke endaweni yakhe.', 'XHO22'),
(22, 28, 1, 'Kukho nendawo yegolide, ehluzwayo.', 'XHO22'),
(22, 28, 2, 'Kutsalwa isinyithi emhlabeni, kunyibilika ubhedu etyeni.', 'XHO22'),
(22, 28, 3, 'Ulophelisa ubumnyama, Ucikida yonke inzaliseko: Amatye obumnyama nethunzi lokufa.', 'XHO22'),
(22, 28, 4, 'Umlambo uyaphuma kubemi; kwanamanzi aseleyo ezinyaweni atsha, abashiya abantu.', 'XHO22'),
(22, 28, 5, 'Umhlaba wona ubonakala isonka, yaye ngaphantsi kwawo kutsha njengomlilo.', 'XHO22'),
(22, 28, 6, 'Amatye aso yindawo yesafire, yaye luthuli lwegolide.', 'XHO22'),
(22, 28, 7, 'Kukho indlela engaziwayo intaka, Aliboni liso lokhozi;', 'XHO22'),
(22, 28, 8, 'Amathole engonyama akazange ayinyathele, nengonyama enoburhalarhume ayizange idlule kuyo.', 'XHO22'),
(22, 28, 9, 'Isandla sakhe usolulele eweni; Uloncothula iintaba.', 'XHO22'),
(22, 28, 10, 'Waphula imilambo phakathi kweengxondorha; yaye iliso lakhe libona zonke izinto ezibalulekileyo.', 'XHO22'),
(22, 28, 11, 'Uyivingca imilambo, ingaphumi; into efihlakeleyo uyayityhila elukhanyeni.', 'XHO22'),
(22, 28, 12, 'Kodwa buya kufunyanwa phi ubulumko? kwaye kude kangakanani ukuqonda?', 'XHO22'),
(22, 28, 13, 'Umntu akalazi ixabiso layo; yaye akufumaneki kwelabantu baphilayo.', 'XHO22'),
(22, 28, 14, 'Amanzi anzongonzongo athi, Asinto yam, nolwandle luthi, Asinto yam.', 'XHO22'),
(22, 28, 15, 'Awunakuthengwa ngegolide, nesilivere ayinakulinganiswa ngexabiso lawo.', 'XHO22'),
(22, 28, 16, 'Ayinakulingana negolide yakwaOfire, Nebherilo yebherilo nesafire;', 'XHO22'),
(22, 28, 17, 'Igolide nekristale aziyi kulingana, kwaye ukutshintshiselana kwabo akuyi kuba ngamatye egolide ecocekileyo.', 'XHO22'),
(22, 28, 18, 'Akunakuthethwa ngekorale neeperile, kuba ixabiso lobulumko lixabiseke ngaphezu kweeperile.', 'XHO22'),
(22, 28, 19, 'Itopazi yakwaKushi ayinakulinganiswa nabo; ayinakulinganiswa nayo negolide engcwengiweyo.', 'XHO22'),
(22, 28, 20, 'Buvela phi ubulumko? kwaye kude kangakanani ukuqonda?', 'XHO22'),
(22, 28, 21, 'Ngokuba abubonakali emehlweni abaphilileyo bonke, bube bufihlakele ezintakeni zezulu.', 'XHO22'),
(22, 28, 22, 'Intshabalalo nokufa kuthi, Silivile ilizwi lakho ngeendlebe zethu.', 'XHO22'),
(22, 28, 23, 'NguThixo oyiqondayo indlela yabo, Nguye oyaziyo indawo yabo.', 'XHO22'),
(22, 28, 24, 'Kuba ubheka esiphelweni sehlabathi, ubona phantsi kwezulu lonke;', 'XHO22'),
(22, 28, 25, 'ukwenza ubunzima bemiphefumlo; yena ulinganisa amanzi ngomlinganiselo.', 'XHO22'),
(22, 28, 26, 'Ekuwiseni kwakhe umthetho ngayo imvula, nendlela yokuphuma kwemibane yendudumo.', 'XHO22'),
(22, 28, 27, 'Wabona, wavakalisa; yilungise, ewe, yiphendle.', 'XHO22'),
(22, 28, 28, 'Wathi kwindoda leyo, Yabona, ukoyika uYehova bubulumko; Ke ukutyeka ebubini kuyingqondo.', 'XHO22'),
(22, 29, 1, 'uYobhi waqhubeka eqhubeka nomzekeliso wakhe, esithi:', 'XHO22'),
(22, 29, 2, 'Hayi, bendinjengokwenyanga zamandulo, njengakwimihla yokundisindisa kukaThixo;', 'XHO22'),
(22, 29, 3, 'Xa ndakhanyisa isibane sakhe phezu kwentloko yam, Ekucaluleni kwam ebumnyameni ngokukhanya kwakhe;', 'XHO22'),
(22, 29, 4, 'Njengoko bendinjalo ngemihla yam yobutsha, Oko imfihlakalo kaThixo ibiphezu kwentente yam;', 'XHO22'),
(22, 29, 5, 'Engasenam uSomandla, Bendirhawula oonyana bam;', 'XHO22'),
(22, 29, 6, 'Oko bendizihlamba iinyawo zam ngamasi, Iliwa lazithululela imilambo yeoli kum;', 'XHO22'),
(22, 29, 7, 'Xa bendiphuma ngesango kuwo umzi, ndasilungisa isihlalo sam endaweni yembutho;', 'XHO22'),
(22, 29, 8, 'Bandibona abafana, bazimela, asuka ema amadoda amakhulu.', 'XHO22'),
(22, 29, 9, 'Izikhulu azivumanga ukuthetha, zabeka izandla emlonyeni;', 'XHO22'),
(22, 29, 10, 'Iingangamsha zithe cwaka, ulwimi lwazo lunamathele enkalakahleni yazo;', 'XHO22'),
(22, 29, 11, 'Xa indlebe indiva, iyandisikelela; Landibona iliso, langqina kum;', 'XHO22'),
(22, 29, 12, 'Kuba ndandihlangula ihlwempu elililayo, iinkedama nabangenanto yakuzinceda.', 'XHO22'),
(22, 29, 13, 'Yafika intsikelelo yotshabalalayo kum, ndayivuyisa intliziyo yomhlolokazi.', 'XHO22'),
(22, 29, 14, 'Ubulungisa bendibambatha, bundambathe; ugwebo lwam lwaba njengengubo nengubo.', 'XHO22'),
(22, 29, 15, 'Bendingamehlo eemfama, neenyawo zeziqhwala;', 'XHO22'),
(22, 29, 16, 'Ndandinguyise wamahlwempu, ndahlola into endingayaziyo.', 'XHO22'),
(22, 29, 17, 'Bendiwaphula amabamba ongendawo, Bendilikrazula ixhoba emazinyweni akhe.', 'XHO22'),
(22, 29, 18, 'Ndathi ke, Ndiya kufela endlwaneni yam, Ndiyandise imihla yam njengentlabathi.', 'XHO22'),
(22, 29, 19, 'Ingcambu yam iphumela emanzini, Umbethe ulala emasebeni am ubusuku bonke.', 'XHO22'),
(22, 29, 20, 'Uzuko lwam luyahlaziyeka kum, sihlaziyekile isaphetha sam esandleni sam.', 'XHO22'),
(22, 29, 21, 'Abantu bandiphulaphula, balinda, bathi cwaka.', 'XHO22'),
(22, 29, 22, 'Emva kwelizwi lam abaphinda bathethe; yabawela ukuthetha kwam.', 'XHO22'),
(22, 29, 23, 'Kwaye babendilindile njengelindele imvula; bavula umlomo wabo xa bevula imvula yasentwasahlobo.', 'XHO22'),
(22, 29, 24, 'Ukuba bendibahleka, bebengayi kukholwa; nokukhanya kobuso bam akubacimi.', 'XHO22'),
(22, 29, 25, 'Ndabanyula indlela yabo Ndababeka njengomphathi, Ndihleli njengokumkani emkhosini, Njengothuthuzela abo bazilileyo.', 'XHO22'),
(22, 30, 1, 'Kungoku bayandigculela abancinane kunam, Abayise ndabagatyayo ukuba bahlale nezinja zomhlambi wam.', 'XHO22'),
(22, 30, 2, 'Ewe, ubugorha bezandla zabo bondinceda, ngubani na otshabaleleyo?', 'XHO22'),
(22, 30, 3, 'Baba bodwa ngenxa yobuhlwempu nendlala; sabela entlango kwimihla yakudala, amanxuwa namanxuwa;', 'XHO22'),
(22, 30, 4, 'abakhetha imifuno etyholweni neengcambu zejunipha ukuze babe kukudla kwabo.', 'XHO22'),
(22, 30, 5, 'Bagxothwa ebantwini;', 'XHO22'),
(22, 30, 6, 'ukuba bahlale emaweni eentili, emiqolombeni yomhlaba nasemaweni.', 'XHO22'),
(22, 30, 7, 'Amatyholo ayagqabhuka; Babutha phantsi kwerhawu.', 'XHO22'),
(22, 30, 8, 'Bengabantwana abaziziyatha, bengabantwana bamatshijolo; babengaphantsi kwehlabathi.', 'XHO22'),
(22, 30, 9, 'Kwaye ngoku yingoma yabo, ewe, yintetho yabo.', 'XHO22'),
(22, 30, 10, 'Bandithiyile, bayandibaleka, Abanditshicele ebusweni.', 'XHO22'),
(22, 30, 11, 'Ekubeni wayicombululayo intambo yam, wanditshutshisa, bawukhupha nomkhala kuqala.', 'XHO22'),
(22, 30, 12, 'Umfana umi ngasekunene kwam; Basusela kude ezinyaweni zam, Bandijikela iindlela zabo zokwaphula.', 'XHO22'),
(22, 30, 13, 'Bayonakalisa indlela yam, bandizisela ububi, bengenamncedi.', 'XHO22'),
(22, 30, 14, 'Zandifikela njengomlambo; Zandirhawula entlango.', 'XHO22'),
(22, 30, 15, 'Zindifikele iimbandezelo, zasukela umphefumlo wam njengomoya; Yadlula njengelifu intuthuzelo yam.', 'XHO22'),
(22, 30, 16, 'Kwaye ngoku, umoya wam uthululelwe phezu kwam; Ndifikelwe ziintsuku zembandezelo.', 'XHO22'),
(22, 30, 17, 'Amathambo am ahlatywa phakathi kwam ebusuku, Izihlunu zam aziphumli.', 'XHO22'),
(22, 30, 18, 'Ngenxa yobukhulu bokuqina kwesigulo sam, isambatho sam siguquke, sandithandela njengomqala wengubo yam.', 'XHO22'),
(22, 30, 19, 'Wandiphosa eludakeni, ndanjengothuli nothuthu.', 'XHO22'),
(22, 30, 20, 'Ndikhala kuwe, ungandiva; Ndimi, ningandiboni;', 'XHO22'),
(22, 30, 21, 'Uzenza isijorha kum; Undichasile ngesandla esithe nkqi.', 'XHO22'),
(22, 30, 22, 'Undiphakamisela phezulu esibhakabhakeni; Undiyeke ndiyikhwele ndalahla izinto zam.', 'XHO22'),
(22, 30, 23, 'Kuba ndiyazi ukuba uya kundikhokelela ekufeni, nasendlwini yokumiselwa kwabaphilileyo bonke.', 'XHO22'),
(22, 30, 24, 'Kodwa isandla sakhe akasayi kusolulela kwelabafileyo, kwanokuba babiza ukuwa kwakhe.', 'XHO22'),
(22, 30, 25, 'Andimlilelanga na lowo ubandezelekileyo? Ubungenalusizi na umphefumlo wam ngenxa yehlwempu?', 'XHO22'),
(22, 30, 26, 'Kuba ndathemba okulungileyo, ndehlelwa bububi; ndathi ndisalinde ukukhanya, kwafika ubumnyama.', 'XHO22'),
(22, 30, 27, 'Isisu sam siyabila, asiphumli; Yandifikela imihla yeentsizi.', 'XHO22'),
(22, 30, 28, 'Ndalila ngaphandle kwelanga; Ndaphakama ndabiza intlanganiso.', 'XHO22'),
(22, 30, 29, 'Ngumzalwana weempungutye, Nomlingane weenciniba.', 'XHO22'),
(22, 30, 30, 'Ulusu lwam lumnyama, amathambo am agqabhuka bubushushu.', 'XHO22'),
(22, 30, 31, 'Kananjalo izembe lam laba sisikhalo, nelungu lam lobudoda laba lilizwi labazilileyo.', 'XHO22'),
(22, 31, 1, 'Ndenze umnqophiso namehlo am; khona ndicingela intombi?', 'XHO22'),
(22, 31, 2, 'Kuba siyini na indawo kaThixo evela phezulu? Liyintoni na ilifa loSomandla elivela phezulu?', 'XHO22'),
(22, 31, 3, 'Ayikontshabalalo na kwabangendawo? Sibetho esimangalisayo na kwabenzi bobubi?', 'XHO22'),
(22, 31, 4, 'Akaziboni na yena iindlela zam, Awabale onke amabanga am?', 'XHO22'),
(22, 31, 5, 'Ukuba ndihambe ndafumana ndafumana, Lwakhawuleza ukuchukumisa unyawo lwam;', 'XHO22'),
(22, 31, 6, 'Makandilinganise esikalini, akwazi uThixo ubulungisa bam.', 'XHO22'),
(22, 31, 7, 'Ukuba ukunyathela kwam kuyatyeka emendweni, Intliziyo yam ilandela amehlo am, Nebala linamathele ezandleni zam;', 'XHO22'),
(22, 31, 8, 'Makhe ndihlwayele, kudle omnye; Ewe, mayinqunyulwe imbewu yam.', 'XHO22'),
(22, 31, 9, 'Ukuba ibihendekile intliziyo yam ngenxa yomfazi, Ndabheka emnyango wommelwane wam;', 'XHO22'),
(22, 31, 10, 'Umkam makadle ngelinye ixesha, kuqubude phambi kwakhe;', 'XHO22'),
(22, 31, 11, 'Ngokuba bububi obubi; ewe, akulunganga ukohlwaywa ngabagwebi.', 'XHO22'),
(22, 31, 12, 'Kuba loo nto ngumlilo ovuthayo use entshabalalweni yaye uya kuyitshabalalisa yonke imveliso yam.', 'XHO22'),
(22, 31, 13, 'Ukuba ndithe ndalandula ilungelo lomkhonzi wam nelomkhonzazana wam, ekubeni babambene nam;', 'XHO22'),
(22, 31, 14, 'Ndithini na xa evuka uThixo? Ukuba uthe wavela, ndophendula ndithini na?', 'XHO22'),
(22, 31, 15, 'Lowo wandenzayo esizalweni, akamenzanga na? yaye akakho na umntu osidale ngokomfanekiso kanina?', 'XHO22'),
(22, 31, 16, 'Ukuba bendibanqanda abasweleyo kwiminqweno yabo, okanye ndiwagxothe amehlo omhlolokazi;', 'XHO22'),
(22, 31, 17, 'Bendisidle ndedwa iqhekeza lam, ayasidla nenkedama;', 'XHO22'),
(22, 31, 18, 'Ngokuba ndamkhulisa kwasebuncinaneni bam, njengoyise, ndamkhupha esizalweni sikama;', 'XHO22'),
(22, 31, 19, 'Ukuba ndithe ndambona otshabalalayo, engenanto yengubo, nehlwempu lingenanto yokwambatha;', 'XHO22'),
(22, 31, 20, 'Ukuba abengandisikeleli amanqe akhe, Akazifudumeza ngoboya bempahla yam emfutshane;', 'XHO22'),
(22, 31, 21, 'Ukuba ndithe ndasiphakamisela isandla sam kwinkedama, Andiluboni uncedo lwam esangweni.', 'XHO22'),
(22, 31, 22, 'Masiwele ngoku isandla sam egxalabeni lam, nengalo yam mayiphuke ethanjeni.', 'XHO22'),
(22, 31, 23, 'Ngokuba ukutshitsha kukaThixo bekundinkwantyisa, Ndingenako ukukuthwala ngenxa yobukhulu bakhe.', 'XHO22'),
(22, 31, 24, 'Ukuba bendenze igolide inqolonci yam, Ndathi kwigolide ecikizekileyo, Ndikholose;', 'XHO22'),
(22, 31, 25, 'Ukuba bendikuvuyela ukuba buninzi kobutyebi bam, nokuba buninzi kwesandla sam;', 'XHO22'),
(22, 31, 26, 'Ndakubona ilanga liphuma, nenyanga ihamba kakuhle;', 'XHO22'),
(22, 31, 27, 'Yavakala entsithelweni intliziyo yam, Wasanga umlomo wam isandla sam.', 'XHO22'),
(22, 31, 28, 'Kwaye kwaba bubugwenxa obufanele ukohlwaywa ngumgwebi, ngenxa yokuba ndibe ndikhanyela uThixo ophakamileyo.', 'XHO22'),
(22, 31, 29, 'Ukuba bendikuvuyela ukutshabalala kondithiyileyo, Ndaziphakamisa xa efikelwe bububi;', 'XHO22'),
(22, 31, 30, 'Ke andiwuvumelanga umlomo wam ukuba one, ndiwunqwenelele intshwabulo umphefumlo wakhe;', 'XHO22'),
(22, 31, 31, 'Akwaba bebengatshongo abantu bentente yam, ukuthi, Akwaba besinayo inyama yakhe! asiyi kwaneliswa.', 'XHO22'),
(22, 31, 32, 'Undwendwe lwalungahlali esitratweni, kodwa iingcango zam ndandizivulela elundwendwe.', 'XHO22'),
(22, 31, 33, 'Ukuba bendizigqubuthele njengoAdam izono zam, Bendibubeke ubugwenxa bam esifubeni sam;', 'XHO22'),
(22, 31, 34, 'Ngaba bendisoyika isihlwele esikhulu, okanye ngaba indelo yaloo ntsapho yandoyikisayo, kangangokuba ndathi cwaka ndingaphumi emnyango?', 'XHO22'),
(22, 31, 35, 'Owu, umntu uya kundiva! Yabona, umnqweno wam kukuba uSomandla andiphendule, Abandichasayo babhale incwadi.', 'XHO22'),
(22, 31, 36, 'Ndayithwala ngesixhanti, ndasithwala njengesithsaba sam.', 'XHO22'),
(22, 31, 37, 'Inani lamabanga am bendiya kwazisa yena; njengenkosana bendiya kuye.', 'XHO22'),
(22, 31, 38, 'Ukuba ilizwe lakowethu lithe lakhala ngam, nokukhala kwalo;', 'XHO22'),
(22, 31, 39, 'Ukuba ndithe ndadla iziqhamo zawo ngaphandle kwemali, bafa abaniniwo;', 'XHO22'),
(22, 31, 40, 'Makuhlume imithana enameva esikhundleni sengqolowa, Kuhlume ukhula esikhundleni serhasi. Amazwi kaYobhi agqityiwe.', 'XHO22'),
(22, 32, 1, 'Ayeka loo madoda omathathu ukumphendula uYobhi, kuba ubezibona elilungisa emehlweni akhe.', 'XHO22'),
(22, 32, 2, 'Wavutha umsindo kaElihu, unyana kaBharageli, umphathi wesizwe sakwaRam; Wavutha umsindo wakhe kuYobhi, ngokuba wayezigqala elilungisa kunoThixo.', 'XHO22'),
(22, 32, 3, 'Wavutha umsindo wakhe kubahlobo bakhe abathathu, ngenxa enokuba bengafumananga kumphendula, besuka bamgxeka uYobhi.', 'XHO22'),
(22, 32, 4, 'uElihu walinda wada wathetha uYobhi, kuba babebadala kunaye.', 'XHO22'),
(22, 32, 5, 'Wabona uElihu, ukuba akukho mpendulo emlonyeni waloo madoda mathathu, wavutha umsindo wakhe.', 'XHO22'),
(22, 32, 6, 'Waphendula uElihu, unyana kaBharageli, kaBhushe, wathi, Ndimncinane mna, ke nina nibadala kunene; ngoko ndandisoyika yaye ndandingenaso isibindi sokuvakalisa iimvakalelo zam.', 'XHO22'),
(22, 32, 7, 'Ndathi, Kukho imihla yokuthetha, Neminyaka emininzi iya kufundisa ubulumko.', 'XHO22'),
(22, 32, 8, 'Noko umoya usemntwini, Nokuphefumla kukaSomandla kuqondisa.', 'XHO22'),
(22, 32, 9, 'Amadoda amakhulu akasoloko elumkile, namadoda amakhulu akakuqondi okusesikweni.', 'XHO22'),
(22, 32, 10, 'Ngoko ke ndathi, Phulaphulani kum; Nam ndiya kunikela uluvo lwam.', 'XHO22'),
(22, 32, 11, 'Yabona, bendiwalindele amazwi akho; Ndimamele izizathu zenu, nijonge ukuba nithini na.', 'XHO22'),
(22, 32, 12, 'Ndanigqala ke indlebe; Yabonani, akukho namnye kuni umohlwayileyo uYobhi, Wawaphendula amazwi akhe.', 'XHO22'),
(22, 32, 13, 'Musani ukuthi, Sibufumene ubulumko;', 'XHO22'),
(22, 32, 14, 'Akabhekisanga kum amazwi akhe, nam andiyi kumphendula ngamazwi enu.', 'XHO22'),
(22, 32, 15, 'Bathi nqa, ababi saphendula; bayeka ukuthetha.', 'XHO22'),
(22, 32, 16, 'Ndathi ndakulinda (kuba bengathethi, bema, àbaphinda baphendule);', 'XHO22'),
(22, 32, 17, 'Ndathi, Nam ndiya kuphendula okwam, Ndixele uluvo lwam.', 'XHO22'),
(22, 32, 18, 'Kuba ndizele luthuli, umoya phakathi kwam uyandishukumisa.', 'XHO22'),
(22, 32, 19, 'Yabona, isisu sam sinjengewayini engenamoya; seyiza kugqabhuka njengamaqebengwana.', 'XHO22'),
(22, 32, 20, 'Ndiya kuthetha, ukuze ndiphumle; Ndiya kuwuvula umlomo wam, ndiphendule.', 'XHO22'),
(22, 32, 21, 'Andiyi kuzibonakalalisa ngokwam, ndingabizi amagama angcolileyo.', 'XHO22'),
(22, 32, 22, 'Ngokuba andikwazi ukubabiza ngamagama ancomekayo; ngoko ke umdali wam wayesele eza kundithatha.', 'XHO22'),
(22, 33, 1, 'Ngako oko, Yobhi, wabekele indlebe amazwi am;', 'XHO22'),
(22, 33, 2, 'Yabona, ndiyawuvula umlomo wam, Luyathetha ulwimi lwam emlonyeni wam.', 'XHO22'),
(22, 33, 3, 'Amazwi am ophuma entliziyweni yam, Nomlomo wam uxela ukwazi kakuhle.', 'XHO22'),
(22, 33, 4, 'uMoya kaThixo wandenza, Nokuphefumla kukaSomandla kwandiphilisa.', 'XHO22'),
(22, 33, 5, 'Ukuba unako, ndiphendule, yalungise intetho yakho phambi kwam, ume.', 'XHO22'),
(22, 33, 6, 'Uyabona, ndingokwakho ngokwentando yakho; Nam ndenziwe ngodongwe.', 'XHO22'),
(22, 33, 7, 'Yabonani, isilumkiso sam asiyi kukunkwantyisa, nesandla sam asiyi kunicinezela.', 'XHO22'),
(22, 33, 8, 'Ewe, uthethe ezindlebeni zam, ndasiva isandi samazwi akho, ndathi:', 'XHO22'),
(22, 33, 9, 'Ndinyulu, andinasono, ndimsulwa; akukho bubi kum.', 'XHO22'),
(22, 33, 10, 'Yabona, ufuna ikamva lam, undibethe ngotshaba lwakhe;', 'XHO22'),
(22, 33, 11, 'Uzifaka iinyawo zam esitokisini, Uzigculela zonke iindlela zam.', 'XHO22'),
(22, 33, 12, 'Yabona, awulungisile ndawo; ndoniphendula, ndithi, uThixo yena mkhulu kunomntu.', 'XHO22'),
(22, 33, 13, 'Yini na ukuba ubambane naye? ngenxa yokuba engayiphenduliyo imicimbi yakhe.', 'XHO22'),
(22, 33, 14, 'Kuba uThixo uthetha kube kanye, Nangezimbini, kodwa umntu akaboni.', 'XHO22'),
(22, 33, 15, 'Ephupheni, embonweni wasebusuku, xa abantu besindwa bubuthongo obukhulu, abalali ezingqengqelweni;', 'XHO22'),
(22, 33, 16, 'Wandula ke avule iindlebe zabantu aze awuqinise umyalelo wabo,', 'XHO22'),
(22, 33, 17, 'ukuze amshenxise umntu kwinjongo yakhe, afihle ikratshi lomntu.', 'XHO22'),
(22, 33, 18, 'Ugcina umphefumlo wakhe emngxunyeni, Nobomi bakhe bungatshabalali likrele.', 'XHO22'),
(22, 33, 19, 'Ubethwa yintlungu esililini sakhe, nenkxwaleko enkulu yamathambo akhe;', 'XHO22'),
(22, 33, 20, 'Ubomi bakhe bukwale ukudla, Nomphefumlo wakhe wale ukudla okulungileyo.', 'XHO22'),
(22, 33, 21, 'Itshiswe inyama yakhe, ingabonwa; zaphuma iinyawo zakhe ezingabonakaliyo.', 'XHO22'),
(22, 33, 22, 'Ewe, umphefumlo wakhe usondele kwelabafileyo nobomi bakhe kwabatshabalalisayo.', 'XHO22'),
(22, 33, 23, 'Ukuba ke kuthe kwakho isithunywa sezulu kunye naye, umkhumshi ubemnye ewakeni, ukuze abonise umntu inyaniso yakhe;', 'XHO22'),
(22, 33, 24, 'Emva koko waba nosizi kuye waza wathi: “Mhlangule angehli aye emhadini: Ndiyifumene intlawulelo.', 'XHO22'),
(22, 33, 25, 'Iyahlaziyeka inyama yakhe kuneyomntwana; uya kubuyela emihleni yobutsha bakhe;', 'XHO22'),
(22, 33, 26, 'Wothandaza kuThixo, umkholise; ububone ubuso bakhe evuya, ngokuba embuyisela emntwini ubulungisa bakhe.', 'XHO22'),
(22, 33, 27, 'Wondela ebantwini, athi omnye kubo, Ndonile, ndakugwenxa okuthe tye, akwandinceda nto;', 'XHO22'),
(22, 33, 28, 'Uya kuwukhulula umphefumlo wakhe ekuyeni kwelabafileyo, Nobomi bakhe bokubona ukukhanya.', 'XHO22'),
(22, 33, 29, 'Uyabona ke, zonke ezi zinto ubuye asebenze umntu;', 'XHO22'),
(22, 33, 30, 'ukuba abuyisele umphefumlo wakhe kwelabafileyo, Ukhanye ngokukhanya kobomi.', 'XHO22'),
(22, 33, 31, 'Ndibekele indlebe, Yobhi, ndiphulaphule; Yithini cwaka, ndithethe.', 'XHO22'),
(22, 33, 32, 'Ke ukuba unokuthetha, ndiphendule; Thetha, ngokuba ndithanda ukukugwebela.', 'XHO22'),
(22, 33, 33, 'Ukuba akunjalo, phulaphula kum; Yithi cwaka, ndikufundise ubulumko.', 'XHO22'),
(22, 34, 1, 'uElihu waphendula wathi:', 'XHO22'),
(22, 34, 2, 'Yivani, nina zilumko, ukuthetha kwam; ndiphulaphuleni, nina bazi.', 'XHO22'),
(22, 34, 3, 'Ngokuba indlebe iyakucikida ukuthetha, Ikhuhlangubo liyakuva ukudla.', 'XHO22'),
(22, 34, 4, 'Masizigqibe ngokwethu, sazi okulungileyo omnye komnye.', 'XHO22'),
(22, 34, 5, 'Ngokuba wathi uYobhi, Ndililungisa, kodwa uThixo ubususile ubulungisa bam.', 'XHO22'),
(22, 34, 6, 'Ndiyaxoka na, ityala lam? Inxeba lam alinakuphiliswa, akukho lukreqo.', 'XHO22'),
(22, 34, 7, 'Uphi na umfo onjengoYobhi, ugculela njengamanzi aselayo?', 'XHO22'),
(22, 34, 8, 'abahamba nabenzi bokungendawo, abahamba namadoda angendawo.', 'XHO22'),
(22, 34, 9, 'Kuba wathi, Akancedi lutho umntu, ngokuba ekholiswa nguye uThixo.', 'XHO22'),
(22, 34, 10, 'Ngako oko, nina madoda okuqonda, ndiphulaphuleni; Kukude kuThixo ukuba wenze ububi; nokukaSomandla ukwenza ububi.', 'XHO22'),
(22, 34, 11, 'Uya kumbuyekeza ngokwezenzo zakhe, Elowo nalowo ngokwendlela yakhe.', 'XHO22'),
(22, 34, 12, 'Ewe, uThixo akenzi bubi, uSomandla akayi kukujikisa okusesikweni.', 'XHO22'),
(22, 34, 13, 'Ngubani na omnike ubukhosi phezu kwehlabathi? Ngubani na ke yena ophethe ihlabathi lonke?', 'XHO22'),
(22, 34, 14, 'Ukuba ubhekise intliziyo yakhe emntwini, ukuba uhlanganisa umphefumlo wakhe nempefumlo yakhe kuye;', 'XHO22'),
(22, 34, 15, 'Yonke inyama iya kutshabalala kunye, nomntu uya kubuyela eluthulini.', 'XHO22'),
(22, 34, 16, 'Ke, ukuba nithe nakuqonda, liveni oku, liveni ilizwi lamazwi am.', 'XHO22'),
(22, 34, 17, 'Umthiyile wokusesikweni uya kulawula na? Ke yena uya kuligweba na olilungisa?', 'XHO22'),
(22, 34, 18, 'Kufanelekile na ukuthi kukumkani, Ungendawo? nakubathetheli, Ningendawo na?', 'XHO22'),
(22, 34, 19, 'Kwenzeka njani na kulowo ungabumkeliyo ubuso beenkosana aze angazithabathi izityebi phezu kwamahlwempu? ngokuba bonke bangumsebenzi wezandla zakhe.', 'XHO22'),
(22, 34, 20, 'Baya kufa ngoko nangoko, phakathi kobusuku boyike, badlule, namagorha angabanjwa ngesandla.', 'XHO22'),
(22, 34, 21, 'Ngokuba amehlo akhe ondele iindlela zomntu, Uyazibona zonke iindlela zakhe.', 'XHO22'),
(22, 34, 22, 'Akukho bumnyama, akukho thunzi lakufa, ukuba basithele kulo abenzi bobubi.', 'XHO22'),
(22, 34, 23, 'Ngokuba akaxoki ngaphezu kobulungisa bomntu; ukuba eze ematyaleni noThixo.', 'XHO22'),
(22, 34, 24, 'Uya kuzityumza ezingenakubalwa amadoda anamandla, azimise endaweni yawo.', 'XHO22'),
(22, 34, 25, 'Ke ngoko uyazazi izenzo zabo, kwaye uyabaguqula nasebusuku, ukuze babe nokutshabalala.', 'XHO22'),
(22, 34, 26, 'Uya kubohlwaya ngokwabangendawo emehlweni abantu;', 'XHO22'),
(22, 34, 27, 'Ngenxa enokuba batyekile kuye, abaziqiqa zonke iindlela zakhe;', 'XHO22'),
(22, 34, 28, 'Ngoko bakuvumele kuye ukukhala kosweleyo, Akuve ukukhala kotyumkileyo.', 'XHO22'),
(22, 34, 29, 'Ukuba uthe cwaka, ngubani na owohlisa ishwangusha? Ukuba uyabusithelisa ubuso bakhe, ngubani na ongambonayo? isenokuba ngumcimbi kawonke wonke, nokuba imalunga nomntu omnye;', 'XHO22'),
(22, 34, 30, 'Ukuze angalawuli umntu oyintshembenxa, Ukuba angabanjwa.', 'XHO22'),
(22, 34, 31, 'Kufanelekile ukuthi kuThixo, Ndiguqukile, andisayi kuphinda ndone.', 'XHO22'),
(22, 34, 32, 'Endingakuboniyo, nguwe ondifundisayo; Ukuba ndenze ububi, andiyi kuphinda?', 'XHO22'),
(22, 34, 33, 'Makube sekuqondeni kwakho na? nguye oya kukuhlawula, nokuba uyala, nokuba uthanda; hayi mna: thetha into oyaziyo.', 'XHO22'),
(22, 34, 34, 'Mazithethe kum izilumko, Nezilumko zindive.', 'XHO22'),
(22, 34, 35, 'uYobhi wathetha engenakwazi, namazwi akhe akanabulumko.', 'XHO22'),
(22, 34, 36, 'Akwaba makacikidwe uYobhi kude kuse ekupheleni, Ngenxa yokuphendula kwakhe kwabangendawo;', 'XHO22'),
(22, 34, 37, 'Ngokuba siphikise isono sakhe, uqhwaba izandla phakathi kwethu, maninzi amazwi akhe ngoThixo.', 'XHO22'),
(22, 35, 1, 'Wathetha uElihu, wathi:', 'XHO22'),
(22, 35, 2, 'Ngaba ndinyanisile na xa usithi: ‘Ubulungisa bam bukhulu kunobukaThixo?', 'XHO22'),
(22, 35, 3, 'Ngokuba wathi, Wozuza ntoni na? Nondinceda ngantoni na, ukuba ndithe ndahlanjululwa esonweni sam?', 'XHO22'),
(22, 35, 4, 'Ndiya kukuphendula mna, wena nabalingane bakho abanawe.', 'XHO22'),
(22, 35, 5, 'Khangela phezulu ezulwini, ubone; ubone amafu aphezu kwakho;', 'XHO22'),
(22, 35, 6, 'Ukuba uthe wona, unatyala lini na kuye? okanye ukuba izikreqo zakho zanda, uya kuthini na ngaye?', 'XHO22'),
(22, 35, 7, 'Ukuba uthe walungisa, umnika ntoni na? Wamkela ntoni na esandleni sakho?', 'XHO22'),
(22, 35, 8, 'Onjengawe buyamonakalisa ububi bakho; nobulungisa bakho bumnceda unyana womntu.', 'XHO22'),
(22, 35, 9, 'Ngenxa yeembandezelo ezininzi bathi abacinezelweyo; Bakhala esandleni sowomeleleyo.', 'XHO22'),
(22, 35, 10, 'Kodwa akukho bani uthi: “Uphi na uThixo, uMdali wam, Lowo ucula ebusuku?', 'XHO22'),
(22, 35, 11, 'Ngubani na osifundisa okulungileyo ngaphezu kweenkomo zehlabathi, Osenze silumke ngaphezu kweentaka zezulu?', 'XHO22'),
(22, 35, 12, 'Babiza khona, akwabakho usabelayo, Ngenxa yekratshi labangendawo.', 'XHO22'),
(22, 35, 13, 'Ngokwenene, uThixo akeva nto, noSomandla akayi kunikela ingqalelo kuyo.', 'XHO22'),
(22, 35, 14, 'Nokuba nithe aniyi kumbona, isigwebo siphambi kwakhe; ke ngoko, ukholose kuYe.', 'XHO22'),
(22, 35, 15, 'Kodwa ngoku, ngenxa yokuba bekungenjalo, ubethe ngomsindo wakhe; nangona kunjalo, akazi kakhulu.', 'XHO22'),
(22, 35, 16, 'Ke ngoko, uYobhi uwuvula ilize umlomo wakhe; udla ngokusebenzisa amagama angazinto.', 'XHO22'),
(22, 36, 1, 'uElihu wongeza wathi:', 'XHO22'),
(22, 36, 2, 'Khaninyamezele okwethutyana, ndokubonisa ukuba andiyi kuthetha ngokukaThixo.', 'XHO22'),
(22, 36, 3, 'Ndiya kukuphakamisela entweni ekude ukwazi kwam, Ndigwebe kuMenzi wam.', 'XHO22'),
(22, 36, 4, 'Okunene, akayi kuxoka amazwi am;', 'XHO22'),
(22, 36, 5, 'Yabona, uThixo unamandla, akamcekisi umntu; Womelele ngokomelela nangobulumko.', 'XHO22'),
(22, 36, 6, 'Akawucongi umphefumlo wabangendawo; uyabagwebela abalulamileyo.', 'XHO22'),
(22, 36, 7, 'Akawabhekiseli amehlo akhe kumalungisa, uhleli nookumkani etroneni yakhe; ewe, uzimise ngonaphakade, ziphakame.', 'XHO22'),
(22, 36, 8, 'Babekhonkxiwe ngemixokelelwane, ngeentsontelo zembandezelo;', 'XHO22'),
(22, 36, 9, 'Emva koko ubabonisa izenzo zabo nezikreqo zabo abazenzileyo.', 'XHO22'),
(22, 36, 10, 'Uzityhilela uqeqesho iindlebe zabo, Ukuba babuye ebubini.', 'XHO22'),
(22, 36, 11, 'Ukuba bathe beva, bamkhonza, boyigqiba imihla yabo ekulungeni, neminyaka yabo benoyolo.', 'XHO22'),
(22, 36, 12, 'Kodwa ukuba abevanga, bobhubha likrele, bafe bengenakwazi.', 'XHO22'),
(22, 36, 13, 'Ke abantliziyo iziintshembenxa bazeka kade umsindo; abalili xa ebakhonkxa.', 'XHO22'),
(22, 36, 14, 'Bafa besebancinane, umphefumlo wabo ube phakathi koyinqambi.', 'XHO22'),
(22, 36, 15, 'Uyamhlangula oxhwalekileyo ekubandezelekeni kwakhe, uzityhile iindlebe zabo ekubandezelekeni kwabo.', 'XHO22'),
(22, 36, 16, 'Naye ebeya kunisusa ezimbandezelweni, anise endaweni ebanzi, engenaxhala; nokubekwa etafileni yakho kuya kuzala ngamanqatha.', 'XHO22'),
(22, 36, 17, 'Ke wena ugweba abangendawo; Umgwebo nobulungisa kukubambela.', 'XHO22'),
(22, 36, 18, 'Ukuba ke ingqumbo ikhona, zikhangeleleni ngokwenu, hleze ithabatheke ngemivumbo yakhe; ke yona intlawulelo enkulu ayiyi kukuhlangula.', 'XHO22'),
(22, 36, 19, 'Uya kuhoya na ubutyebi bakho? hayi, igolide, nkqu noSomandla.', 'XHO22'),
(22, 36, 20, 'Akubunqwenelanga ubusuku bokunqunyulwa kwabantu ezindaweni zabo.', 'XHO22'),
(22, 36, 21, 'Zilumkele, ungabhekisi ububi; ngokuba unyule bona endaweni yokubandezeleka.', 'XHO22'),
(22, 36, 22, 'Yabona, uThixo uphezulu ngamandla akhe;', 'XHO22'),
(22, 36, 23, 'Ngubani na omphathise indlela yakhe? Ngubani na ongathi, Wenze ububi?', 'XHO22'),
(22, 36, 24, 'Khumbula ukuba ukhuthaza umsebenzi wakhe obonwa ngabantu.', 'XHO22'),
(22, 36, 25, 'Wonke umntu uyayibona; umntu uyabona kude.', 'XHO22'),
(22, 36, 26, 'Yabona, uThixo mkhulu, kodwa asimazi; Nenani leminyaka yakhe alinakubalwa.', 'XHO22'),
(22, 36, 27, 'Ngokuba ethambisa amathontsi amanzi; Zinise ngomphunga wazo;', 'XHO22'),
(22, 36, 28, 'owehlisa amafu, azalise umntu;', 'XHO22'),
(22, 36, 29, 'Ngubani na okuqondayo ukuqengqeleka kwamafu, nesandi somnquba wakhe?', 'XHO22'),
(22, 36, 30, 'Yabona, waneka phezu kwakhe ukukhanya kwakhe, Azigubungele ngeenzulu zolwandle.', 'XHO22'),
(22, 36, 31, 'Kuba ngezo nto ugweba abantu; unika inyama eninzi.', 'XHO22'),
(22, 36, 32, 'Ugubungela ngamafu ukukhanya; wathi kungabikho kukhanya kwelo lifu ligqobhozayo.', 'XHO22'),
(22, 36, 33, 'Isandi sayo siyayixela, Nokutshayela, nomphunga.', 'XHO22'),
(22, 37, 1, 'Intliziyo yam iyagubha ngenxa yoko, Iyishiyile indawo yayo.', 'XHO22'),
(22, 37, 2, 'Liveni ngenyameko isandi selizwi lakhe, nesandi esiphuma emlonyeni wakhe.', 'XHO22'),
(22, 37, 3, 'Ukuthumela phantsi kwezulu lonke, Nombane walo uye eziphelweni zehlabathi.', 'XHO22'),
(22, 37, 4, 'Latsho laduma ilizwi; ududuma ngezwi lobungangamsha bakhe; Akayi kuwavala, xa livakalayo izwi lakhe.', 'XHO22'),
(22, 37, 5, 'uThixo ududuma ngezwi lakhe ngokumangalisayo; izinto ezinkulu azenzayo thina asinakuziqonda.', 'XHO22'),
(22, 37, 6, 'Kuba uthi kwikhephu, Yiba sehlabathini; kuyafana nangeemvula ezinkulu, neemvula zamandla akhe.', 'XHO22'),
(22, 37, 7, 'Uyomeleza isandla sabantu bonke; ukuze bonke abantu bawazi umsebenzi wakhe.', 'XHO22'),
(22, 37, 8, 'Emva koko izilwanyana zingena emingxunyeni zize zihlale ezindaweni zazo.', 'XHO22'),
(22, 37, 9, 'Kukho umoya ovuthuzayo, ovela ezantsi, nasentla, ukubanda.', 'XHO22'),
(22, 37, 10, 'Ngokuphefumla kukaThixo kuza ingqele, Nobubanzi bamanzi ngoku.', 'XHO22'),
(22, 37, 11, 'Kananjalo uya lophelisa ilifu ngokusela amanzi; utwabulula ilifu lakhe elibengezelayo;', 'XHO22'),
(22, 37, 12, 'Kwaye iguqulwa ngokwentando yakhe, ukuze babe nako ukwenza yonke into abayalela yona, kulo lonke ihlabathi lehlabathi.', 'XHO22'),
(22, 37, 13, 'Uyakuvumela ukuba kufike, nokuba kuthethiwe, nokuba kungenxa yelizwe lakhe, nokuba kwenziwe inceba.', 'XHO22'),
(22, 37, 14, 'Yiva oku, Yobhi, uphakame, ugqale imisebenzi ebalulekileyo kaThixo.', 'XHO22'),
(22, 37, 15, 'Uyazi na ukuwabeka kukaThixo, Nokubengezelisa kwawo ilifu lakhe?', 'XHO22'),
(22, 37, 16, 'Uyawazi na umlinganiso wamafu, nemisebenzi ebalulekileyo yoSokwazi?', 'XHO22'),
(22, 37, 17, 'Zifudumala kangakanani na iimpahla zakho, xa ulalisa elimiweyo ngomoya wasezantsi;', 'XHO22'),
(22, 37, 18, 'Wasasaza na phezu kwawo isibane esilukhuni, njengesipili esigalelwe phezu kwabo?', 'XHO22'),
(22, 37, 19, 'Sifundise into esiya kuyithetha kuye; ngokuba singenako ukuzibamba iintetho ngenxa yobumnyama.', 'XHO22'),
(22, 37, 20, 'Kuya kuthiwa, ndiyathetha kuye? ukuba umntu uthe wathetha, wodliwa.', 'XHO22'),
(22, 37, 21, 'Ngoku abantu abakuboni ukukhanya okuqaqambileyo emafini, kodwa umoya uyadlula uze uwahlambulule.', 'XHO22'),
(22, 37, 22, 'Ubuhle buvela entla; kuThixo kukho ubukhulu obuwoyikekayo.', 'XHO22'),
(22, 37, 23, 'Ukuba sithe samchukumisa uSomandla, asiyi kumfumana; Akayi kuxinzelela phantsi.', 'XHO22'),
(22, 37, 24, 'Kungenxa yoko le nto abantu bemoyika;', 'XHO22'),
(22, 38, 1, 'uYehova wamphendula uYobhi esemoyeni, wathi,', 'XHO22'),
(22, 38, 2, 'Ngubani na lo usitha icebo ngamazwi okungazi?', 'XHO22'),
(22, 38, 3, 'Ke ngoko, bhinqa amanqe akho njengendoda; ngokuba ndiya kunibuza, nindiphendule.', 'XHO22'),
(22, 38, 4, 'Ubuphi na wena ukulidala kwam ihlabathi? yithi, ukuba unengqiqo.', 'XHO22'),
(22, 38, 5, 'Ngubani na olinganise imilinganiso yalo, uyazi na? okanye ngubani obeke umda?', 'XHO22'),
(22, 38, 6, 'Zimiswe entwenini na iziseko zalo? ngubani na owabeka ilitye lesiseko;', 'XHO22'),
(22, 38, 7, 'Xa amakhwezi evuma kunye, Yaye bonke oonyana bakaThixo banemihlali?', 'XHO22'),
(22, 38, 8, 'Ngubani na owaluvalayo ulwandle ngeengcango Ekutyhobozeni kwalo, kwaphuma esizalweni?', 'XHO22'),
(22, 38, 9, 'iEner ndiyenze ilifu libe yingubo yakhe, Ubumnyama bamenze ingubo yakhe;', 'XHO22'),
(22, 38, 10, 'Nizidilizile iindawo zam emiselweyo, niyifakile imivalo neengcango;', 'XHO22'),
(22, 38, 11, 'Wathi, Uya kufika apha, ungegqithi; Aya kupheliswa amaza enu akratshayo?', 'XHO22'),
(22, 38, 12, 'Umise ingomso ngeemini zakho; kwathi ngesifingo wayityhila indawo yakhe;', 'XHO22'),
(22, 38, 13, 'Ukuze soyise iziphelo zehlabathi, Bavuthululwe kulo abangendawo?', 'XHO22'),
(22, 38, 14, 'Lwaba njengodongwe lokutywina; kwaye zema njengengubo.', 'XHO22'),
(22, 38, 15, 'Abangendawo bavinjwa ukukhanya kwabo, Saphuke isandla esiphakamileyo.', 'XHO22'),
(22, 38, 16, 'Ngaba ungene emithonjeni yolwandle? Okanye usuke waya kufuna amanzi anzongonzongo na?', 'XHO22'),
(22, 38, 17, 'Ngaba uwavulelwe na amasango okufa? Wakha wawabona na amasango ethunzi lokufa?', 'XHO22'),
(22, 38, 18, 'Ububonile na ububanzi behlabathi? utsho ukuba wazi yonke into.', 'XHO22'),
(22, 38, 19, 'Iphi na indlela yokukhanya? ukuba bubumnyama, iphi na indawo yabo;', 'XHO22'),
(22, 38, 20, 'Ukuba ubuse emdeni wayo, Uzazi neendlela eziya endlwini yayo?', 'XHO22'),
(22, 38, 21, 'Ngaba uyakwazi oko, ngenxa yokuba wawuzelwe ngelo xesha? okanye kungenxa yokuba imihla yakho mininzi?', 'XHO22'),
(22, 38, 22, 'Wakha wabuzuza na ubuncwane bekhephu? okanye bubone ubutyebi besichotho;', 'XHO22'),
(22, 38, 23, 'Ukuba ndiwuqwebele ixesha lembandezelo, imini yemfazwe nemfazwe?', 'XHO22'),
(22, 38, 24, 'Kwahlula ntoni na ukukhanya, ukwaneka umoya wasempumalanga ehlabathini?', 'XHO22'),
(22, 38, 25, 'oshenxisiwe unogumbe wamanzi endleleni, nombane wamafu nendlela;', 'XHO22'),
(22, 38, 26, 'Imvula emhlabeni, kungekho mntu; enkqantosini, kungekho mntu;', 'XHO22'),
(22, 38, 27, 'ukuze azalise ilizwe eliyinkangala, eliphanzileyo; nokuseza ihlumelo lengca?', 'XHO22'),
(22, 38, 28, 'Ngaba kuyanetha nobawo? ngubani na owenza amathontsi ombethe?', 'XHO22'),
(22, 38, 29, 'Uphuma kubani umkhenkce? kunye nobuluhlaza obubandayo besibhakabhaka, ngubani owenze?', 'XHO22'),
(22, 38, 30, 'Amanzi ayaqina njengelitye, Amanzi abe ngumkhenkce.', 'XHO22'),
(22, 38, 31, 'Unokuyibopha na ingoma yesiTyalo, Uzicombulule iimbophelelo zeOrion?', 'XHO22'),
(22, 38, 32, 'Unokuzala na uMatsaroti ngexesha lakhe? Ungámalathisa na iArkturo noonyana bayo?', 'XHO22'),
(22, 38, 33, 'Uyayazi na imimiselo yezulu? Ngaba unokuthelekelela indlela aza kulilawula ngayo ihlabathi?', 'XHO22'),
(22, 38, 34, 'Unokuliphakamisela emafini na izwi lakho, ukuze amanzi amaninzi akugubungele?', 'XHO22'),
(22, 38, 35, 'Unokuthumela na umbane, ahambe, athi kuwe, Singabathi aba?', 'XHO22'),
(22, 38, 36, 'Ngubani na omise ubulumko? Ngubani na onike ukuqonda entliziyweni?', 'XHO22'),
(22, 38, 37, 'Ngubani na onokufunda iingqimba zamafu ngobulumko? Ngubani na onokuchitha izulu;', 'XHO22'),
(22, 38, 38, 'Xa luqina uthuli, Ukuhlanganisana kwamatye?', 'XHO22'),
(22, 38, 39, 'Woyizingela na inyamakazi ingonyama? okanye wanelise inkanuko yabafazi;', 'XHO22'),
(22, 38, 40, 'Xa belala emingxunyeni yabo, behleli entsithelweni ukuba babukele?', 'XHO22'),
(22, 38, 41, 'Ngubani na onika amagwababa ukudla? xa abantwana bakhe bakhala kuThixo, balahleka ngenxa yokuswela inyama.', 'XHO22'),
(22, 39, 1, 'Uyazi ukuba iibhokhwe zasematyeni zizala nini? okanye ungaphawula xa ixhama lizala?', 'XHO22'),
(22, 39, 2, 'Uyazibala na iinyanga ude uzigqibe? Uyalazi na ixesha labo lokuzalwa?', 'XHO22'),
(22, 39, 3, 'Zigobile, zazala amathole azo, Zabulahla umvandedwa wazo.', 'XHO22'),
(22, 39, 4, 'Abantwana babo banesimo esilungileyo, bakhule nengqolowa; bayaphuma, ababuyeli kubo.', 'XHO22'),
(22, 39, 5, 'Ngubani na olikhulule iesile lasendle? Ngubani na ozithukululeyo izintso ze-esile lasendle?', 'XHO22'),
(22, 39, 6, 'indlu yayo ndiyenze inxuwa, nelizwe libe yintlango, iziseko zalo;', 'XHO22'),
(22, 39, 7, 'Uyayidela ingxokolo yomzi, Akayiva kukukhala komqhubi.', 'XHO22'),
(22, 39, 8, 'Intaba bubutyani bayo, ifuna yonke into eluhlaza.', 'XHO22'),
(22, 39, 9, 'Inyathi iyakuthanda ukukulungiselela, ihlale emkhumbini wakho?', 'XHO22'),
(22, 39, 10, 'Unokuyibopha na inyathi ngomsele emhadini? okanye uya kundilandela iintili?', 'XHO22'),
(22, 39, 11, 'Unokukholosa na ngayo, ngokuba emakhulu amandla ayo? okanye uzowushiya kuye umsebenzi wakho?', 'XHO22'),
(22, 39, 12, 'Wokholwa na kuyo, ukuba yoyizisa imbewu yakho endlwini, iyibuthele kuvimba wakho?', 'XHO22'),
(22, 39, 13, 'Uzinike amaphiko amahle iipikoko? Amaphiko neentsiba zenciniba?', 'XHO22'),
(22, 39, 14, 'yona ishiya amaqanda ayo emhlabeni, iwafudumeza abe luthuli;', 'XHO22'),
(22, 39, 15, 'Ulibale ukuba unyawo luya kumtyumza, Nento egxothiweyo.', 'XHO22'),
(22, 39, 16, 'Ubenze balukhuni abantwana bakhe, bengabakhe; imigudu yakhe ililize, akukho koyika;', 'XHO22'),
(22, 39, 17, 'Ngokuba uThixo wenze yabulibala ubulumko, akabunika ukuqonda.', 'XHO22'),
(22, 39, 18, 'Indlela eliziphakamisa ngayo, liyalidela ihashe nomkhweli walo.', 'XHO22'),
(22, 39, 19, 'Linike ihashe amandla na? Owambethe intamo yakhe ngemibane?', 'XHO22'),
(22, 39, 20, 'Ngaba uya kuyingcangcazelisa njengenkumbi? Ukukhanya kweempumlo zayo kuyoyikeka.', 'XHO22'),
(22, 39, 21, 'Uhamba emfuleni, uvuye ngamandla; uya kukhawulela amadoda axhobileyo.', 'XHO22'),
(22, 39, 22, 'Liyakuhleka ukunkwantya, alinkwantyi; akawufulathela umkhonto.', 'XHO22'),
(22, 39, 23, 'Iyabhonga phezu kwakhe indudumo, Umkhonto obenyezelayo, nengweletshetshe.', 'XHO22'),
(22, 39, 24, 'Utshisa ihlabathi ngomsindo nangengqumbo, Akakholwa ukuba sisandi sesigodlo.', 'XHO22'),
(22, 39, 25, 'Xa iphakathi kwamaxilongo, ithi: “Ha! waliva mgama ivumba lemfazwe, nezwi labathetheli nelokumkani.', 'XHO22'),
(22, 39, 26, 'Unokuphaphazela ukhetshe ngobulumko bakho, Yolule amaphiko ayo ezantsi?', 'XHO22'),
(22, 39, 27, 'Ngaba ixhalanga linyuka ngomyalelo wakho, Liyiphakamise indlu yalo phezulu?', 'XHO22'),
(22, 39, 28, 'Udla ubomi, uhleli elulwalweni, Elulwalweni naselulwalweni;', 'XHO22'),
(22, 39, 29, 'Lisuka apho likhangele ixhoba, namehlo alo akhangele emgama.', 'XHO22'),
(22, 39, 30, 'Kwaye abantwana bakhe abancinci bamunca igazi, kwaye apho bakhoyo ababuleweyo, kulapho bakhoyo.', 'XHO22'),
(22, 40, 1, 'Waphinda uYehova wamphendula uYobhi, wathi,', 'XHO22'),
(22, 40, 2, 'Obambana noSomandla ngaba uya kumfundisa na? Omkhalimelayo uThixo makaphendule;', 'XHO22'),
(22, 40, 3, 'uYobhi wamphendula uYehova, wathi:', 'XHO22'),
(22, 40, 4, 'Yabona, ndinetyala; Ndingathini ukukuphendula? Ndiya kubeka isandla sam emlonyeni wam.', 'XHO22'),
(22, 40, 5, 'Ndathetha ngasihlandlo sithile; kodwa andiyi kuphendula, ewe, kabini; kodwa andiyi kuhambela phambili.', 'XHO22'),
(22, 40, 6, 'uYehova wamphendula uYobhi esemoyeni, wathi:', 'XHO22'),
(22, 40, 7, 'Ke ngoko, bhinqa amanqe akho njengendoda; ndiya kukubamba, ndikuxelele kum.', 'XHO22'),
(22, 40, 8, 'Ngaba nawe uya kusiphelisa isigwebo sam? Uza kundigweba na, ukuze wena ube lilungisa?', 'XHO22'),
(22, 40, 9, 'Unaso na isandla esinjengesikaThixo? Uya kugquma na ngelizwi elinjengelakhe?', 'XHO22'),
(22, 40, 10, 'Khawuzihombise ngobuhle nobungangamela; Yambatha uzuko nobuhle.', 'XHO22'),
(22, 40, 11, 'Lahla ingqumbo yakho, ubheke kubo bonke abakhukhumeleyo, ulithobe.', 'XHO22'),
(22, 40, 12, 'Khangela bonke abantu abanekratshi, umthobe; banyhashe abangendawo esikhundleni sabo.', 'XHO22'),
(22, 40, 13, 'Bangcwabele ndawonye eluthulini; Ububophe ubuso babo emfihlekweni.', 'XHO22'),
(22, 40, 14, 'Ke kaloku, ndiya kukuxelela, Ukuba isandla sakho sokunene sinokukusindisa.', 'XHO22'),
(22, 40, 15, 'Khawubone ke iBhehemoti endayenza yanawe; udla utyani njengenkomo.', 'XHO22'),
(22, 40, 16, 'Khawubone, amandla ayo asemanqeni ayo, Nokuqina kwayo kusesangweni lesisu sayo.', 'XHO22'),
(22, 40, 17, 'Ishukuma umsila wayo njengomsedare; izihlunu zamathambo alo zihlanganisene ndawonye.', 'XHO22'),
(22, 40, 18, 'Iinyawo zayo zifana nobhedu; Amathambo ayo anjengemivalo yentsimbi.', 'XHO22'),
(22, 40, 19, 'Yintlahlela yeendlela zikaThixo: Lowo uyenzayo uya kufika ekreleni lakhe kuyo.', 'XHO22'),
(22, 40, 20, 'Ewe, iintaba ziyivelisela ukudla, apho adlala khona onke amarhamncwa elizwe.', 'XHO22'),
(22, 40, 21, 'Ulala phantsi kwemithi enomthunzi, phantsi kweengcongolo nengca.', 'XHO22'),
(22, 40, 22, 'Imithi ethunzi imgubungela ngomthunzi wayo; imingcunube yomlambo imjikelezile.', 'XHO22'),
(22, 40, 23, 'Yabona, uyawusela umlambo, akangxami; ngethemba lokuyikhupha iYordan emlonyeni wayo.', 'XHO22'),
(22, 40, 24, 'Uyibamba ngamehlo, Ugqobhoza impumlo yakhe kwizirhintyelo.', 'XHO22'),
(22, 41, 1, 'Unokuyikhupha na iLeviyatan ekuthinjweni? okanye uyaluthoba ulwimi lwakhe ngentambo?', 'XHO22'),
(22, 41, 2, 'Ngaba unokuyifaka isithuba empumlweni? Ugqobhoze umhlathi ngentonga?', 'XHO22'),
(22, 41, 3, 'Ngaba uya kutsala umdla ngakumbi kuwe? Angathetha amazwi alula na kuwe?', 'XHO22'),
(22, 41, 4, 'Ingenza umnqophiso na nawe? Woyithabatha ibe ngumkhonzi wakho na ngonaphakade?', 'XHO22'),
(22, 41, 5, 'Wodlala na nayo njengentaka? okanye ufuna ukumbophisa ezintombini zakho?', 'XHO22'),
(22, 41, 6, 'Angadliwa na amaqabane akhe? Angarhweba ngayo na phakathi kwabarhwebi?', 'XHO22'),
(22, 41, 7, 'Unokusizalisa na isikhumba sayo ngeentsimbi ezitsolo? Intloko yayo inemikhonto yeentlanzi?', 'XHO22'),
(22, 41, 8, 'Beka isandla sakho kuyo; khumbula ukulwa, ungaphindi ulwe.', 'XHO22'),
(22, 41, 9, 'Yabona, ithemba lakhe lingamampunge;', 'XHO22'),
(22, 41, 10, 'Akukho sijorha esingaba nako ukusivusa; ngoku, ngubani na onokuma ngakum?', 'XHO22'),
(22, 41, 11, 'Ngubani na ondithinteleyo ekumbuyekezeni? Konke okuphantsi kwamazulu onke yeyam.', 'XHO22'),
(22, 41, 12, 'Andiyi kuzifihla ngamalungu ayo, Nokuqina kwayo, nobuhle bayo.', 'XHO22'),
(22, 41, 13, 'Ngubani na onokuwutyhila umphambili wesambatho sakhe? Ngubani na ongamkhawulela enemikhala yomibini?', 'XHO22'),
(22, 41, 14, 'Iingcango ebusweni bayo, ngubani na onokuzivula? Amazinyo ayo ayoyikeka kuyo yonke indawo.', 'XHO22'),
(22, 41, 15, 'Iliqhayiya amaxolo ayo, evalwe ngetywina eliqinileyo.', 'XHO22'),
(22, 41, 16, 'Enye isondelelene kangangokuba nomoya awunakungena phakathi kwayo.', 'XHO22'),
(22, 41, 17, 'Bamanyene, benamathela omnye komnye, ukuze bangehlukani.', 'XHO22'),
(22, 41, 18, 'Ekuphiliseni kwakhe kukhanya ukukhanya, Namehlo akhe anjengeenkophe zesifingo.', 'XHO22'),
(22, 41, 19, 'Emlonyeni wayo kuphuma izikhuni ezitshangazayo, namalangatye omlilo.', 'XHO22'),
(22, 41, 20, 'Kuphuma umsi emathatheni ayo, ngathi kusembizeni okanye ngepani.', 'XHO22'),
(22, 41, 21, 'Umoya wayo uvuthisa amalahle, Kuphuma ilangatye emlonyeni.', 'XHO22'),
(22, 41, 22, 'Entanyeni yayo kuhleli amandla, Neentlungu zijika zibe luvuyo phambi kwayo.', 'XHO22'),
(22, 41, 23, 'Izintsu zenyama yayo zihlangene, ziqinile; azikwazi ukushukuma.', 'XHO22'),
(22, 41, 24, 'Iqine njengelitye intliziyo yayo; ewe, iqine njengelitye eliphantsi lekalikali.', 'XHO22'),
(22, 41, 25, 'Ekuphakameni kwayo, ziyankwantya iinjengele; bazihlambulule ngenxa yokreqo.', 'XHO22'),
(22, 41, 26, 'Ikrele lomlilo alinakulubamba: Umkhonto, notolo, nakrele.', 'XHO22'),
(22, 41, 27, 'Uthabatha isinyithi njengengca, Nobhedu njengomthi obolileyo;', 'XHO22'),
(22, 41, 28, 'Utolo lwesaphetha aluyi kuyishiya, abe ngumququ amatye okusawula kuyo.', 'XHO22'),
(22, 41, 29, 'Iifayile kuthiwa ziindiza, Ihleka xa izanyazanyiswa umkhonto.', 'XHO22'),
(22, 41, 30, 'Ngaphantsi kwawo amatye abukhali; usasaza izinto ezibukhali eludakeni.', 'XHO22'),
(22, 41, 31, 'Iyibiliza njengembiza amanzi; Ulolwenza ulwandle lube yimbiza yokutshisa;', 'XHO22'),
(22, 41, 32, 'Wenza indlela yokukhanya ngasemva; umntu unokucinga ukuba ubunzulu buzuba.', 'XHO22'),
(22, 41, 33, 'Akukho namnye unjengaye ehlabathini, odalwe engenaloyiko.', 'XHO22'),
(22, 41, 34, 'Ibona konke okuphakamileyo, Ingukumkani kubo bonke oonyana abanekratshi.', 'XHO22'),
(22, 42, 1, 'uYobhi wamphendula uYehova, wathi,', 'XHO22'),
(22, 42, 2, 'Ndiyazi ke ukuba ungenza nantoni na;', 'XHO22'),
(22, 42, 3, 'Ngubani na oyifihlayo icebo engenakwazi? ke ngoko, ndathi andizange ndiqonde; izinto ezindimangalisa kakhulu, ebendingazazi.', 'XHO22'),
(22, 42, 4, 'Yivani, ndithethe mna; ndiya kukubamba, ndikuxelele kum.', 'XHO22'),
(22, 42, 5, 'Ndive ngendlebe yakho, Kungoku iliso lam likubonile.', 'XHO22'),
(22, 42, 6, 'Kungenxa yoko le nto ndizithiyileyo, kwaye ndiyazohlwaya, ndiseluthulini naseluthuthwini.', 'XHO22'),
(22, 42, 7, 'Wawathetha uYehova la mazwi kuYobhi, wathi uYehova kuElifazi wakwaTeman, Umsindo wam uyavutha kuwe, nakubahlobo bakho bobabini, ngenxa enokuba anithethanga ngobulungisa ngam, njengoYobhi umkhonzi wam.', 'XHO22'),
(22, 42, 8, 'Ngoku ke zithabatheleni iinkunzi ezintsha zeenkomo zibe sixhenxe, neenkunzi zeegusha zibe sixhenxe, niye kumkhonzi wam uYobhi, nizenzele idini elinyukayo; uYobhi, umkhonzi wam, uya kunithandazela; kuba ndiya kukholiswa nguye, ukuze ndingenzi kuni ngobudenge benu, ekubeni aningobulungisa njengoko umkhonzi wam uYobhi watshoyo ngam.', 'XHO22'),
(22, 42, 9, 'Baya ke ooElifazi wakwaTeman, noBhiledadi wakwaShuwa, noTsofare waseNahama, benza njengoko uYehova wabawisela umthetho ngako; NoYehova wamamkela uYobhi.', 'XHO22'),
(22, 42, 10, 'uYehova wayitshintsha imeko kaYobhi xa wathandazela abahlobo bakhe; Waphinda uYehova wamnika uYobhi loo nto wayenayo.', 'XHO22'),
(22, 42, 11, 'Beza kuye bonke abazalwana bakhe, noodade bakhe bonke, nabo bonke abebazi, badla isonka endlwini yakhe; bamlilela, bemthuthuzela ngenxa yobubi bonke, abemhlisele bona uYehova;', 'XHO22'),
(22, 42, 12, 'Ngoko uYehova wakusikelela ukuphela kukaYobhi ngaphezu kwesiqalo sakhe, ngenxa yokuba wayenezimvu ezilishumi elinesine lamawaka neenkamela ezingamawaka amathandathu namahashe angamawaka neenkomo namaesile aliwaka.', 'XHO22'),
(22, 42, 13, 'Waba noonyana abasixhenxe, neentombi ezintathu.', 'XHO22'),
(22, 42, 14, 'Eyokuqala wathi igama elinguYemima; igama lowesibini nguKetsiya; igama leyesithathu nguKeren-hapuke.', 'XHO22'),
(22, 42, 15, 'Ehlabathini lonke akuzange kubekho mfazi wayemhle njengeentombi zikaYobhi, yaye uyise wazinika ilifa phakathi kwabantakwabo.', 'XHO22'),
(22, 42, 16, 'Emva koko uYobhi waphila iminyaka elikhulu elinamashumi amane, wabona oonyana bakhe noonyana bakhe, ukusa kwizizukulwana ezine.', 'XHO22'),
(22, 42, 17, 'Wafa ke uYobhi, emdala, ehambisekile ebudaleni.', 'XHO22'),
(23, 1, 1, 'Hayi, uyolo lomntu ongahambiyo ngecebo labangendawo, ongemiyo endleleni yaboni, ongahlaliyo etroneni yabagxeki.', 'XHO22'),
(23, 1, 2, 'Yena unonelela umyalelo kaYehova; kwaye ucamanga ngomyalelo wakhe imini nobusuku.', 'XHO22'),
(23, 1, 3, 'Unjengomthi omiliselwe phezu kwemijelo yamanzi, Onika isiqhamo sawo ngexesha lawo; igqabi lawo aliyi kubuna; yaye konke akwenzayo kophumelela.', 'XHO22'),
(23, 1, 4, 'Akanjalo ongendawo, unjengamalahle aphetshethwa ngumoya.', 'XHO22'),
(23, 1, 5, 'Ngoko ke, abangendawo abayi kuma ekugwetyweni, yaye ebandleni lamalungisa akukho boni.', 'XHO22'),
(23, 1, 6, 'Ngokuba uYehova uyayazi indlela yamalungisa, kodwa indlela yabangendawo iya kudaka.', 'XHO22'),
(23, 2, 1, 'Yini na ukuba iintlanga zenze ingxokozelo, abantu bacinge into engeyakonto?', 'XHO22'),
(23, 2, 2, 'Ookumkani behlabathi bazilungiselele, nezikhulu zicebisana kunye uYehova nomthanjiswa wakhe;', 'XHO22'),
(23, 2, 3, 'Masiyiqhawule imixokelelwane yabo, siwaphose kuthi amathambo abo.', 'XHO22'),
(23, 2, 4, 'Lowo uhleli emazulwini uya kuhleka; uYehova uya kubagculela.', 'XHO22'),
(23, 2, 5, 'Uya kuthetha kubo ngomsindo wakhe, abathuthumbise ngokuphuphuma komsindo wakhe;', 'XHO22'),
(23, 2, 6, 'Noko ke, mna ke ndimmisile ukumkani wam entabeni yam engcwele yaseZiyon.', 'XHO22'),
(23, 2, 7, 'Ndiya kuwuvakalisa umgwebo: uYehova uthe kum, UnguNyana wam wena; namhlanje ndikwazisile.', 'XHO22'),
(23, 2, 8, 'Cela kum, ndikunike iintlanga zibe lilifa lakho, neziphelo zehlabathi zibe bubutyebi bakho.', 'XHO22'),
(23, 2, 9, 'Zaphule ngentonga yentsimbi; uya kuziqhekeza njengembiza yombumbi.', 'XHO22'),
(23, 2, 10, 'Yibani ngobulumko, nina bokumkani;', 'XHO22'),
(23, 2, 11, 'Mkhonzeni uYehova ninoloyiko, nigcobe ningcangcazela.', 'XHO22'),
(23, 2, 12, 'Mangeni uNyana, hleze aqumbe, atshabalale endleleni, Ukuba uvutha umsindo wakhe umzuzwana. Hayi, uyolo lwabo bonke abazimela ngaye!', 'XHO22'),
(23, 3, 1, 'Ingoma kaDavide, ekubalekeni kwakhe kuAbhisalom, unyana wakhe; Baninzi abasukela phezulu kum.', 'XHO22'),
(23, 3, 2, 'Baninzi abathi kumphefumlo wam, Akunto yanto kuThixo; Sela.', 'XHO22'),
(23, 3, 3, 'Wena, Yehova, uyingweletshetshe yam; ubungangamsha bam nozuko lwentloko yam.', 'XHO22'),
(23, 3, 4, 'Ndidanduluka kuYehova ngezwi lam, Andiphendule entabeni yakhe engcwele. Sela.', 'XHO22'),
(23, 3, 5, 'Ndalala phantsi, ndalala ubuthongo; Ndivukile; ngokuba uYehova ebendixhasa.', 'XHO22'),
(23, 3, 6, 'Andiyi kuboyika amashumi amawaka abantu abahlanganisene kum.', 'XHO22'),
(23, 3, 7, 'Vuka, Yehova; Ndisindise, Thixo wam, ngokuba uzibetha zonke iintshaba zam emhlathini; Uyawaphula amazinyo abangendawo.', 'XHO22'),
(23, 3, 8, 'Usindiso lolukaYehova, intsikelelo yakho ibe phezu kwabantu bakho. Sela.', 'XHO22'),
(23, 4, 1, 'Kumvumi waseNeginoti, umhobe kaDavide. Ndibabale, uwuve umthandazo wam.', 'XHO22'),
(23, 4, 2, 'Nina, nyana babantu, kunini na uzuko lwam luhlazisa? Kunini na nithanda amampunge, nifuna umvuzo? Sela.', 'XHO22'),
(23, 4, 3, 'Yazini ke ukuba uYehova umthambisile ilungisa; uYehova uya kuva xa ndimbiza.', 'XHO22'),
(23, 4, 4, 'Yoyikani, ningoni; thethani neentliziyo zenu ezinkukweni zenu, nithi cwaka. Sela.', 'XHO22'),
(23, 4, 5, 'Zisani imibingelelo yobulungisa, nikholose ngoYehova.', 'XHO22'),
(23, 4, 6, 'Baninzi abathi, Ngubani na oya kusibonisa okulungileyo? Nkosi, phakamisa ukukhanya kobuso bakho.', 'XHO22'),
(23, 4, 7, 'Uyivuyise intliziyo yam ngaphezu kwexesha lokwanda kwengqolowa newayini yabo.', 'XHO22'),
(23, 4, 8, 'Ndiya kulala phantsi ndixolile, ndilale ubuthongo, kuba wena, Yehova, nguwe wedwa ondivumela ukuba ndihlale ngoxolo.', 'XHO22'),
(23, 5, 1, 'Kumvumi kaNehiloti, Ingoma kaDavide.', 'XHO22'),
(23, 5, 2, 'Live ilizwi lokuzibika kwam, Nkosi yam, Thixo wam, ngokuba ndithandaza kuwe.', 'XHO22'),
(23, 5, 3, 'Yehova, uya kuliva izwi lam kwakusasa; kusasa ndiya kuwubhekisa kuwe umthandazo wam, ndibheke phezulu.', 'XHO22'),
(23, 5, 4, 'Ngokuba wena akunguThixo onanze okungendawo; abuyi kuphambukela kuwe ububi.', 'XHO22'),
(23, 5, 5, 'Ayiyi kuba phambi kwamehlo akho izimathane; ubathiyile bonke abenza okubi.', 'XHO22'),
(23, 5, 6, 'Bapheliseni abathetha ububi; uYehova uya kulicekisa umntu ophalaza igazi nokhohlisayo.', 'XHO22'),
(23, 5, 7, 'Ke mna ndiya kungena endlwini yakho ngobukhulu benceba yakho, ndiqubude, ndikoyike etempileni yakho engcwele.', 'XHO22'),
(23, 5, 8, 'Yehova, ndikhaphe ngobulungisa bakho ngenxa yeentshaba zam; yenza indlela yakho phambi kobuso bam.', 'XHO22'),
(23, 5, 9, 'Ngokuba emlonyeni wabo akukho nto emlonyeni; ingaphakathi labo libi kakhulu; Lingcwaba elivulekileyo umqala wabo; badumisa ngolwimi lwabo.', 'XHO22'),
(23, 5, 10, 'Batshabalalise, Thixo; mabawe kwinjongo yabo; bagxothe ngobuninzi bobugwenxa babo; ngokuba bagwilikile kuwe.', 'XHO22'),
(23, 5, 11, 'Mabanoyolo bonke abakholose ngawe; Mabahlale bevuya, ngokuba ubakhusela; mabavuye ngawe abalithandayo igama lakho.', 'XHO22'),
(23, 5, 12, 'Ngokuba wena, Yehova, uyalisikelela ilungisa; Uya kumngqonga ngovuyo njengekhaka.', 'XHO22'),
(23, 6, 1, 'Yehova, musa ukundohlwaya unomsindo, musa ukundibetha ngomsindo;', 'XHO22'),
(23, 6, 2, 'Ndibabale, Yehova; ngokuba ndibuthathaka. Nkosi, ndiphilise; ngokuba amathambo am aphukile.', 'XHO22'),
(23, 6, 3, 'Umphefumlo wam ukhwankqiswe kakhulu; Ke wena, Yehova, koda kube nini na?', 'XHO22'),
(23, 6, 4, 'Buya, Yehova, uwusindise umphefumlo wam; ndisindise ngenxa yenceba yakho.', 'XHO22'),
(23, 6, 5, 'Ngokuba ekufeni akusayi kukhunjulwa; ngubani na oya kukudumisa kwelabafileyo?', 'XHO22'),
(23, 6, 6, 'Ndidiniwe kukuncwina kwam; ubusuku bonke ndondlala umandlalo wam; Ndimanzisa umandlalo wam ngeenyembezi zam.', 'XHO22'),
(23, 6, 7, 'Iliso lam liyalila kukukhathazeka; Úyaluphele ngenxa yeentshaba zam zonke.', 'XHO22'),
(23, 6, 8, 'Sukani kum, nina nonke benzi bobutshinga ngokuba uYehova ulivile ilizwi lokuzibika kwam.', 'XHO22'),
(23, 6, 9, 'uYehova wawuva umthandazo wam; uYehova uya kuwamkela umthandazo wam.', 'XHO22'),
(23, 6, 10, 'Ziya kudana, zibe noburhalarhume zonke iintshaba zam; wabuya nazo zaphakama ngoko nangoko iintloni.', 'XHO22'),
(23, 7, 1, '(Ixilongo likaDavide, awavuma ngalo kuYehova ilizwi likaKushi umBhenjamin.) Yehova, Thixo wam, ndikholose ngawe; Ndisindise kubo bonke abandisukelayo, ndisindise,', 'XHO22'),
(23, 7, 2, 'ukuze angawuqwengi njengengonyama umphefumlo wam, awuqwenge, kungabikho bani uwusindisayo.', 'XHO22'),
(23, 7, 3, 'Yehova, Thixo wam, ukuba ndikwenzile oku; ukuba kukho ububi ezandleni zam;', 'XHO22'),
(23, 7, 4, 'Ukuba ndimbuyisele ububi obexolelene nam; Ewe, ndalusindisa utshaba lwam ngaphandle kwesizathu.', 'XHO22'),
(23, 7, 5, 'Maluwusukele utshaba umphefumlo wam, luwuthimbe; Makabunyashele phantsi ubomi bam, Abubeke eluthulini uzuko lwam. Sela.', 'XHO22'),
(23, 7, 6, 'Khawusuk’ ume, Yehova, unomsindo, uziphakamise ngenxa yomsindo weentshaba zam, uphakame ngenxa yam, wenze isigwebo osiyaleleyo.', 'XHO22'),
(23, 7, 7, 'Liya kukurhawula ibandla leentlanga; ke ngoko, guqukani ngenxa yabo.', 'XHO22'),
(23, 7, 8, 'uYehova uya kugweba iintlanga; ndigwebe, Yehova, ngokobulungisa bam, nangokokulunga okukum.', 'XHO22'),
(23, 7, 9, 'Mabuphele ububi babangendawo; kodwa uqinise amalungisa, ngokuba uThixo olilungisa uyazicikida iintliziyo nezintso.', 'XHO22'),
(23, 7, 10, 'Ingweletshetshe yam ikuThixo, Umsindisi wabantliziyo zithe tye.', 'XHO22'),
(23, 7, 11, 'uThixo ngumgwebi olilungisa, Uqumbele abangendawo yonke imini.', 'XHO22'),
(23, 7, 12, 'Ukuba ongendawo uthe akabuya, ulilolile ikrele lakhe; wamisa isaphetha sakhe wasifunda.', 'XHO22'),
(23, 7, 13, 'Wabuya walungisa izixhobo zokufa; Uluchanaba lutolo lwakhe kubacinezeli.', 'XHO22'),
(23, 7, 14, 'Yabona, uzelwe engendawo, wabamkela ububi, wazala ubuxoki;', 'XHO22'),
(23, 7, 15, 'Wemba iqula, wemba, wawela emngxunyeni abewumbile.', 'XHO22'),
(23, 7, 16, 'Umsindo wakhe uya kubuyela entlokweni yakhe, nogonyamelo lwakhe lunge entanyeni yakhe.', 'XHO22'),
(23, 7, 17, 'Ndiya kubulela kuYehova ngenxa yobulungisa bakhe, Ndilidumise igama likaYehova oPhezukonke.', 'XHO22'),
(23, 8, 1, 'Kumvumi waseGiti, Ingoma kaDavide umise ubuqaqawuli bakho ngaphezu kwezulu.', 'XHO22'),
(23, 8, 2, 'Emlonyeni wabantwana nabanyayo unike amandla ngenxa yeentshaba zakho, Ukuze udambise utshaba nomphindezeli.', 'XHO22'),
(23, 8, 3, 'Ndalibona izulu lakho, umsebenzi weminwe yakho, inyanga neenkwenkwezi ozicebileyo;', 'XHO22'),
(23, 8, 4, 'Uyintoni na umntu, ukuba uthe wamkhumbula? Unyana womntu, womvelela nini na?', 'XHO22'),
(23, 8, 5, 'Ngokuba umhlise kancinane kunezithunywa zezulu, wamthwesa uzuko nembeko.', 'XHO22'),
(23, 8, 6, 'Ummisile ukuba alawule izenzo zezandla zakho; Zonke izinto uzibeke phantsi kweenyawo zakhe;', 'XHO22'),
(23, 8, 7, 'Yonke impahla emfutshane, neenkomo zonke, ewe, namarhamncwa asendle;', 'XHO22'),
(23, 8, 8, 'Iintaka zezulu, neentlanzi zolwandle, nento yonke edlula ngeendlela zolwandle.', 'XHO22'),
(23, 8, 9, 'Yehova, Nkosi yethu, hayi indlela elizuke ngayo igama lakho ehlabathini lonke!', 'XHO22'),
(23, 9, 1, 'Ndiya kubulela kuwe, Yehova, ngentliziyo yonke; Ndiya kuyazisa yonke imisebenzi yakho ebalulekileyo.', 'XHO22'),
(23, 9, 2, 'Ndiya kuvuya ndidlamke ngawe; Ndiya kulidumisa igama lakho, Wena usenyangweni.', 'XHO22'),
(23, 9, 3, 'Ekubuyeni kweentshaba zam, ziya kuwa zize zitshabalale phambi kobuso bakho.', 'XHO22'),
(23, 9, 4, 'Ngokuba ubulifezile ubulungisa bam, nebango lam; uhleli phezu kwetrone, ugweba ngobulungisa.', 'XHO22'),
(23, 9, 5, 'Uzikhalimele iintlanga, wabatshabalalisa abangendawo, uliphanzisile igama labo ngonaphakade kanaphakade.', 'XHO22'),
(23, 9, 6, 'Lutshabandini, amanxuwa aphele, udilize imizi engamanxuwa; Yatshabalala kunye nabo inkumbulo.', 'XHO22'),
(23, 9, 7, 'Ke yena uYehova uya kuma ngonaphakade, ilungiselwe ugwebo itrone yakhe.', 'XHO22'),
(23, 9, 8, 'Uya kuligweba ihlabathi ngobulungisa, Uya kugweba abantu ngobulungisa.', 'XHO22'),
(23, 9, 9, 'uYehova uya kuba yindawo yokusabela kotyunyuziweyo, ihlathi ngamaxesha eembandezelo.', 'XHO22'),
(23, 9, 10, 'Yaye abo balaziyo igama lakho baya kukholosa ngawe, ngenxa yokuba wena, Yehova, akubashiyanga abo bakufunayo.', 'XHO22'),
(23, 9, 11, 'Vumani kuYehova ohlala eZiyon; Xelani ezintlangeni izenzo zakhe.', 'XHO22'),
(23, 9, 12, 'Xa ecela igazi, uyacinga ngako; Akakulibalanga ukukhala kwabathobekileyo.', 'XHO22'),
(23, 9, 13, 'Ndibabale, Yehova; Kulumkele ukucinezeleka kwam ngenxa yabandithiyileyo, wena wondiphakamisayo emasangweni okufa.', 'XHO22'),
(23, 9, 14, 'Ukuze ndiyimeme yonke imibongo yakho Emasangweni entombi uZiyon; ndigcobe ngosindiso lwakho.', 'XHO22'),
(23, 9, 15, 'Iintlanga zatshona emhadini awawenzayo; mabazifihle, lubanjwe unyawo lwabo.', 'XHO22'),
(23, 9, 16, 'uYehova uyaziwa ngomgwebo wakhe; Ongendawo ubanjiswa zizenzo zezandla zakhe. I-Higgaion. Sela.', 'XHO22'),
(23, 9, 17, 'Baya kubuyela kwelabafileyo abangendawo, Kwaiintlanga zonke ezimlibalayo uThixo;', 'XHO22'),
(23, 9, 18, 'Ngokuba aliyi kuhlala lilityalwa ihlwempu; Ithemba lehlwempu aliyi kutshabalala naphakade.', 'XHO22'),
(23, 9, 19, 'Vuka, Yehova; ubani makangathwali mthwalo; mazigwetywe iintlanga phambi kwamehlo akho.', 'XHO22'),
(23, 9, 20, 'Zoyike, Yehova, ukuze zazi iintlanga ukuba zingabantu. Sela.', 'XHO22'),
(23, 10, 1, 'Yehova, ngubani na olinde kude? Yini na ukuba nizifihle ngamaxesha engxingongo?', 'XHO22'),
(23, 10, 2, 'Ngekratshi lakhe ongendawo uyatshutshisa amahlwempu; mababanjwe kumacebo abo.', 'XHO22'),
(23, 10, 3, 'Kuba ongendawo uyaqhayisa ngomnqweno wentliziyo yakhe yaye uyamsikelela onekhwele amthiyileyo uYehova.', 'XHO22'),
(23, 10, 4, 'Ongendawo akayi kubuza kuThixo, enekratshi lobuso bakhe; uThixo akakho kuzo zonke iingcinga zakhe.', 'XHO22'),
(23, 10, 5, 'Iindlela zakhe zihlala zilukhuni; Izigwebo zakho ziphezu kobuso bakhe; Uzivule zonke iintshaba zakhe.', 'XHO22'),
(23, 10, 6, 'Wathi entliziyweni yakhe, Andisayi kushukuma; ngokuba andiyi kubandezeleka naphakade.', 'XHO22'),
(23, 10, 7, 'Umlomo wakhe uzele sisiqalekiso nenkohliso nenkohliso; phantsi kolwimi lwakhe bububi nokuzingca.', 'XHO22'),
(23, 10, 8, 'Uhleli ezinqabeni zemizi; ubulala omsulwa entsithelweni; amehlo akhe ajonge kwihlwempu emfihlakalweni.', 'XHO22'),
(23, 10, 9, 'Ulalela njengengonyama emhadini wayo; ulalela ukuba abambise abaziintsizana; Uyalibambisa ihlwempu xa elirhuqela emgibeni walo.', 'XHO22'),
(23, 10, 10, 'Uyagoba, azithobe, liwile ihlwempu namandla alo.', 'XHO22'),
(23, 10, 11, 'Wathi entliziyweni yakhe, uThixo ulibele; Uyabusithelisa ubuso bakhe; soze ayibone.', 'XHO22'),
(23, 10, 12, 'Vuka, Yehova; Yehova, phakamisa isandla sakho; musani ukubalibala abathobekileyo.', 'XHO22'),
(23, 10, 13, 'Kutheni abangendawo bemdela uThixo? wathi entliziyweni yakhe: Akuyi kufuna.', 'XHO22'),
(23, 10, 14, 'Wabona; ngenxa enokuba ubona ububi nobubi ukuba ububuyisele ngesandla sakho; amahlwempu azinikela kuwe; ungumncedi wenkedama.', 'XHO22'),
(23, 10, 15, 'Usaphule isandla songendawo nongendawo; funani ububi bakhe, nide nibufumane.', 'XHO22'),
(23, 10, 16, 'uYehova yiNkosi ngonaphakade kanaphakade; zatshabalala iintlanga elizweni lakhe.', 'XHO22'),
(23, 10, 17, 'Umnqweno wabalulamileyo uwuvile, Nkosi; Uya kuzilungisa iintliziyo zabo, ive indlebe yakho,', 'XHO22'),
(23, 10, 18, 'ukuba ugwebe iinkedama noxinzelelweyo, Angabi sabacinezela umntu wasehlabathini.', 'XHO22'),
(23, 11, 1, 'Umhobe kaDavide. Ndikholose ngoYehova; Ningathini na ukuthi kumphefumlo wam, Balekela entabeni yakho njengentaka?', 'XHO22'),
(23, 11, 2, 'Ngokuba, yabonani, abangendawo batola isaphetha sabo, Bexunele iintolo zabo entanjeni, Ukuba bazingele abathe tye entsithelweni.', 'XHO22'),
(23, 11, 3, 'Xa iziseko zithe zagungxulwa, lingenza ntoni na ilungisa?', 'XHO22'),
(23, 11, 4, 'uYehova usetempileni yakhe engcwele, itrone kaYehova isemazulwini;', 'XHO22'),
(23, 11, 5, 'uYehova uyalilinga ilungisa, kodwa abangendawo nabathanda ugonyamelo bawuthiyile umoya wakhe.', 'XHO22'),
(23, 11, 6, 'Uya kuyinisa phezu kwabangendawo imigibe, nomlilo, nesulfure, nesaqhwithi esoyikekayo; ibe sisabelo sendebe yabo.', 'XHO22'),
(23, 11, 7, 'Ngokuba uYehova ulilungisa uthanda ubulungisa; Amalungisa ayabubona ubuso bawo.', 'XHO22'),
(23, 12, 1, 'Kukumkani wamaSemina, ingoma kaDavide ngokuba ilungisa lihambile; ngokuba abathembekileyo batshabalele phakathi koonyana babantu.', 'XHO22'),
(23, 12, 2, 'Bafumana bethetha elowo nowakhe; ngomlomo wenkohliso, nangeentliziyo ezimbini.', 'XHO22'),
(23, 12, 3, 'uYehova uya kuyinqumla yonke imilebe yomlomo wenkohliso, nolwimi olukhukhumeleyo.', 'XHO22'),
(23, 12, 4, 'Abathe, Siya koyisa ngolwimi lwethu; imilomo yethu yeyethu; ngubani na osilawulayo?', 'XHO22'),
(23, 12, 5, 'Ngenxa yokubhuqwa kweentsizana, ngenxa yokuncwina kweentsizana, Ndiya kuma ngoku, utsho uYehova; Ndiza kumbeka endaweni ekhuselekileyo ukuze ambethe.', 'XHO22'),
(23, 12, 6, 'Amazwi kaYehova ngamazwi anyulu, Ayisilivere enyibilikisiweyo emhlabeni, Ehluzwe kasixhenxe.', 'XHO22'),
(23, 12, 7, 'Basindise, Yehova, basindise kwesi sizukulwana kuse kwixesha elingenammiselo.', 'XHO22'),
(23, 12, 8, 'Abangendawo babaleka ngeenxa zonke; Ke abalihlazo baya kuphakanyiswa.', 'XHO22'),
(23, 13, 1, 'KuYehova weengoma. Kunini na, Yehova, undilibele? rhoqo? Kunini na ubusithelisa ubuso bakho kum?', 'XHO22'),
(23, 13, 2, 'Kunini na ndihleli ndicinga phezu komphefumlo wam, ngeengqaqambo zentliziyo yam? Koda kube nini na luziphakamisa utshaba lwam?', 'XHO22'),
(23, 13, 3, 'Bona, undiphulaphule, Yehova, Thixo wam; Khanyisa amehlo am, ukuze ndingalali ubuthongo bokufa;', 'XHO22'),
(23, 13, 4, 'Makungabikho utshaba lwam luthi, Ndimoyisile; Bavuya ke abandisukelayo, ekushukumeni kwam;', 'XHO22'),
(23, 13, 5, 'Ke ndikholose ngenceba yakho; iya kuvuya intliziyo yam ngosindiso lwakho.', 'XHO22'),
(23, 13, 6, 'Ndiyathanda ukuculela uYehova, kuba undenze ndalunga.', 'XHO22'),
(23, 14, 1, 'Kumongameli, ingoma kaDavide. Bonakele, benza izinto ezimasikizi, akukho namnye wenza okulungileyo.', 'XHO22'),
(23, 14, 2, 'uYehova ukhangela koonyana babantu esemazulwini, ukuba ukho na oqiqayo, omquqelayo uThixo.', 'XHO22'),
(23, 14, 3, 'Bonke balahleka, benziwe inqambi bonke bephela; Akukho wenza okulungileyo, eyedwa.', 'XHO22'),
(23, 14, 4, 'Abanakwazi na bonke abenzi bokungendawo? abadla abantu bam njengoko badla isonka, bengambongozi uYehova.', 'XHO22'),
(23, 14, 5, 'Bafikelwa luloyiko olukhulu, ngokuba uThixo usesizukulwaneni samalungisa.', 'XHO22'),
(23, 14, 6, 'Icebo lehlwempu ulihlazile, Ngokuba uYehova ulihlathi lalo.', 'XHO22'),
(23, 14, 7, 'Ukuba beluphuma eZiyon usindiso lukaSirayeli! ekusibuyiseleni kukaYehova isabelo sabantu bakhe, uya kuvuya uYakobi, avuye noSirayeli.', 'XHO22'),
(23, 15, 1, '(Indumiso kaDavide.) Yehova, ngubani na owohlala ententeni yakho? Ngubani na owohlala entabeni yakho engcwele?', 'XHO22'),
(23, 15, 2, 'Ngulowo uhamba ngobulungisa nowenza okusesikweni nothetha inyaniso ngentliziyo yakhe.', 'XHO22'),
(23, 15, 3, 'Ongathethiyo ngolwimi lwakhe, akamenzi nto imbi ummelwane wakhe, akamngcikivi ummelwane wakhe.', 'XHO22'),
(23, 15, 4, 'Umntu odelekileyo kwawakhe amehlo; kodwa uyabahlonela abamoyikayo uYehova. Ofungayo uya kuzithoba, akaguquguquki.', 'XHO22'),
(23, 15, 5, 'Ongayisebenzisiyo imali yakhe ekufuneni inzuzo, makangamkeli intlawulo kwabamsulwa. Lowo uzenzayo ezi zinto akayi kushukunyiswa.', 'XHO22'),
(23, 16, 1, '(iMiktame kaDavide.) Thixo, ndisindise, Ngokuba ndikholose ngawe.', 'XHO22'),
(23, 16, 2, 'Wathi ke umphefumlo wam kuYehova, UyiNkosi yam; Ukulunga kwam akwenzeki kuwe;', 'XHO22'),
(23, 16, 3, 'Ke kwabo bangcwele ehlabathini, kwabakhulu, endikholisiweyo ngabo ezintweni zonke.', 'XHO22'),
(23, 16, 4, 'Iimbandezelo zabo ziya kufikelela kwabasukela uthixo wasemzini; Andiyi kuyizisa iminikelo yabo ethululwayo yegazi, namagama abo andiyi kuwathetha ngomlomo wam.', 'XHO22'),
(23, 16, 5, 'NguYehova isahlulo selifa lam, nendebe yam; uyaligcina ikamva lam.', 'XHO22'),
(23, 16, 6, 'Zibotshelelwe kum izintya zokulinganisa; ewe, ndinelifa elilungileyo.', 'XHO22'),
(23, 16, 7, 'Ndiya kumbonga uYehova ondenzele injongo; Zindifundise izintso zam ebusuku.', 'XHO22'),
(23, 16, 8, 'Ndimbeka phambi kwam uYehova ngamaxesha onke; Ngokuba ungasekunene kwam, andiyi kushukunyiswa.', 'XHO22'),
(23, 16, 9, 'Ke ngoko, intliziyo yam iyagcoba, nozuko lwam luyagcoba; nenyama yam iya kuphumla inethemba.', 'XHO22'),
(23, 16, 10, 'Ngokuba akuyi kuwushiya umphefumlo wam kwelabafileyo; kwaye akuyi kumyekela oyiNgcwele wakho ukuba abone ukubola.', 'XHO22'),
(23, 16, 11, 'Wondazisa indlela yobomi; phambi kobuso bakho kuzele yimincili; Ekunene kwakho yimihlali engunaphakade.', 'XHO22'),
(23, 17, 1, 'Yiva, Yehova, umgwebi, kubekele indlebe ukuhlahlamba kwam, uwubazele indlebe umthandazo wam ongaphumiyo emlonyeni wenkohliso.', 'XHO22'),
(23, 17, 2, 'Mawuphume ebusweni bakho isigwebo sam; Amehlo akho abone kwaoko.', 'XHO22'),
(23, 17, 3, 'Uyicikidile intliziyo yam; Wandivelela ebusuku; Undicikidile, akwafumana nto; Ndagqiba kwelokuba umlomo wam awuzukwaphuka.', 'XHO22'),
(23, 17, 4, 'Ke yona imisebenzi yabantu, ngelizwi lomlomo wakho, ndizithintele iindlela zomonakalisi.', 'XHO22'),
(23, 17, 5, 'Gcina iindlela zam ngeendlela zakho, Ukunyathela kwam makungatyibiliki.', 'XHO22'),
(23, 17, 6, 'Ndinqula wena, ngokuba undiva, Thixo; ndibekele indlebe, uliphulaphule izwi lam.', 'XHO22'),
(23, 17, 7, 'Zibalule iinceba zakho, wena ubakhululayo abazimela ngawe, ngesandla sakho sokunene, kwabasukela phezulu.', 'XHO22'),
(23, 17, 8, 'Ndigcine njengentshula yeliso, ndisibekele ngomthunzi wamaphiko akho;', 'XHO22'),
(23, 17, 9, 'Ngenxa yabangendawo abandisukelayo, ngenxa yeentshaba zam ezindibulalayo.', 'XHO22'),
(23, 17, 10, 'Bagutyungelwe ngamanqatha abo; Ngomlomo wazo zithetha ngekratshi.', 'XHO22'),
(23, 17, 11, 'Zindijikelezile ngoku ekuhambeni kwethu; bawaphose phantsi amehlo abo;', 'XHO22'),
(23, 17, 12, 'Njengengonyama isukela ixhoba, nanjengengonyama ifuna ukuzimela;', 'XHO22'),
(23, 17, 13, 'Khawusuk’ ume, Yehova, umcinezele, umwise; Wusindise umphefumlo wam kongendawo, olikrele lakho.', 'XHO22'),
(23, 17, 14, 'Kubantu besandla sakho, Yehova, beentlanga zelimiweyo, abanesabelo kobu bomi, abasisu sabo usizalise ngobuncwane bakho obufihliweyo; bondle abantwana babo, Bonke ubutyebi babo babushiyele abantwana babo.', 'XHO22'),
(23, 17, 15, 'Mna ndiya kububona ubuso bakho ndisebulungiseni; Ndiya kwanela ekuvukeni kwam, ndisebumeni bakho.', 'XHO22'),
(23, 18, 1, 'Kumvumi, Ingoma kaDavide, umkhonzi kaYehova, owawathethayo amazwi ale ngoma kuYehova, ekubahlanguleni kukaYehova esandleni seentshaba zakhe zonke, nasesandleni sikaSawule; Wathi, Ndiya kukuthanda, Yehova, mandla am.', 'XHO22'),
(23, 18, 2, 'uYehova liliwa lam, igwiba lam, umsindisi wam; Thixo wam, liwa lam endizimela ngaye; Ngweletshetshe yam, luphondo losindiso lwam, nenqaba yam ephakamileyo.', 'XHO22'),
(23, 18, 3, 'Ndinqula uYehova ongowokudunyiswa; ndiya kusinda ezintshabeni zam.', 'XHO22'),
(23, 18, 4, 'Izibetho zokufa zandirhawula, Nemilambo yabangendawo yandikhwankqisa.', 'XHO22'),
(23, 18, 5, 'Izibindi zelabafileyo zandibhijela, zaza izirhintyelo zokufa zandirhintyela.', 'XHO22'),
(23, 18, 6, 'Ndanqula uYehova ngentliziyo yam, Ndanqula uThixo wam; Waliva etempileni yakhe izwi lam, Ukuzibika kwam kwafika kuye ezindlebeni zakhe.', 'XHO22'),
(23, 18, 7, 'Lanyikima ihlabathi, lanyikima; Iziseko zeentaba zashukuma, Zahexa, ngokuba Enomsindo.', 'XHO22'),
(23, 18, 8, 'Kwenyuka umsi emathatheni akhe, kwatsha umlilo emlonyeni wakhe; amalahle abaswe yiyo.', 'XHO22'),
(23, 18, 9, 'Wabuya walithoba izulu, wehla, Yangamafu amnyama phantsi kweenyawo zakhe.', 'XHO22'),
(23, 18, 10, 'Wakhwela ekerubhini, wabhabha; ewe, waphaphazela ngamaphiko omoya.', 'XHO22'),
(23, 18, 11, 'Wenze ubumnyama baba lisithe lakhe; Intente eyayimgqume ngaphakathi yayingamanzi amnyama namafu angqingqwa esibhakabhakeni.', 'XHO22'),
(23, 18, 12, 'Ngokukhanya okuphambi kwakhe, adlula amafu akhe, Isichotho namalahle omlilo.', 'XHO22'),
(23, 18, 13, 'Wadudumisa esibhakabhakeni uYehova, walivakalisa uMthetheleli-lizwi ilizwi lakhe; isichotho namadangatye.', 'XHO22'),
(23, 18, 14, 'Wathuma iintolo zakhe, wabaphangalalisa; wabaphosa imibane, wabadubaduba.', 'XHO22'),
(23, 18, 15, 'Kwabonakala imijelo yamanzi, Kwatyhileka iziseko zehlabathi ngokukhalima kwakho, Yehova, Ngokuphefumla komoya wempumlo yakho.', 'XHO22'),
(23, 18, 16, 'Wolula isandla ephezulu, wandibamba, Wandirhuqela emanzini amaninzi.', 'XHO22'),
(23, 18, 17, 'Wandihlangula elutshabeni lwam olunamandla, Kwabandithiyileyo, ngokuba bebomelele kunam.', 'XHO22'),
(23, 18, 18, 'Bandifikela ngemini yeshwangusha lam, uYehova wandixhasa.', 'XHO22'),
(23, 18, 19, 'Wandisa endaweni enkulu; Wandisindisa, ngokuba endithandile.', 'XHO22'),
(23, 18, 20, 'uYehova undibize ngokobulungisa bam; Wandenza ngokobunyulu bezandla zam.', 'XHO22'),
(23, 18, 21, 'Kuba ndizigcinile iindlela zikaYehova, yaye andizange ndiphambuke kuThixo wam.', 'XHO22'),
(23, 18, 22, 'Ngokuba bonke ubunile bakhe ngakum, andiwususanga ubunile bakhe kum.', 'XHO22'),
(23, 18, 23, 'Ndandililungisa phambi kwakhe, Ndazinqanda ebubini bam.', 'XHO22'),
(23, 18, 24, 'Ngako oko, uYehova undibize ngokobulungisa bam, nangokobunyulu bezandla zam phambi kwamehlo akhe.', 'XHO22'),
(23, 18, 25, 'Woba nenceba kuwe; kwindoda elungileyo uzenza ixoki;', 'XHO22'),
(23, 18, 26, 'Kohlambulukileyo uya kuzibonakalisa uhlambulukile; kwaye ngempazamo uya kuzichaza.', 'XHO22'),
(23, 18, 27, 'Ngokuba wena uyabasindisa abantu ababandezelekileyo; ke yena uya kulithoba imbonakalo ephakamileyo.', 'XHO22'),
(23, 18, 28, 'Ngokuba uyasilumeka isibane sam wena: uYehova, uThixo wam, uyabukhanyisela ubumnyama bam.', 'XHO22'),
(23, 18, 29, 'Kuba bendifudula ndicanda iqela labachasene nomthetho ngenxa yenu; yaye ngoThixo wam ndatsiba eludongeni.', 'XHO22'),
(23, 18, 30, 'uThixo yena, igqibelele indlela yakhe; Ilizwi likaYehova linyibilikisiwe; Uyingweletshetshe kubo bonke abazimela ngaye.', 'XHO22'),
(23, 18, 31, 'Ngokuba ngubani na onguThixo, ingenguye uYehova? okanye ngubani na oliliwa ngaphandle koThixo wethu?', 'XHO22'),
(23, 18, 32, 'UloThixo undibhinqise ubukroti, Oyilungiselayo indlela yam;', 'XHO22'),
(23, 18, 33, 'Uzifanisayo iinyawo zam nezamaxhama, Undimisa emimangweni yam;', 'XHO22'),
(23, 18, 34, 'Uzifundisayo ukulwa izandla zam, Zaphule isaphetha sesinyithi izandla zam.', 'XHO22'),
(23, 18, 35, 'Undinikileyo ingweletshetshe yosindiso lwakho, Sandibamba isandla sakho sokunene, Yandiphakamisa inceba yakho.', 'XHO22'),
(23, 18, 36, 'Uwatwabulule amanyathela am phantsi kwam, Aziyi kuhexa iinyawo zam.', 'XHO22'),
(23, 18, 37, 'Ndazisukela iintshaba zam, ndazifumana;', 'XHO22'),
(23, 18, 38, 'Ndabahlaba, ababa nakuvuka; zawa phantsi kweenyawo zam.', 'XHO22'),
(23, 18, 39, 'Undibophe ubugorha bokulwa; Abo bandomelezayo, wababeka phantsi kwam.', 'XHO22'),
(23, 18, 40, 'Ubuye wandinika intamo yeentshaba zam; ukuze ndibatshabalalise abandithiyayo.', 'XHO22'),
(23, 18, 41, 'Bakhala, akwabakho mhlanguli: KuYehova, akabaphendula.', 'XHO22'),
(23, 18, 42, 'Ndabacola njengothuli oluqhutywa ngumoya; Ndabalahla njengothuli lwesitrato.', 'XHO22'),
(23, 18, 43, 'Wandihlangula ezimbandezelweni zabantu; undenze intloko yeentlanga; abantu ebendingabazi baya kundikhonza.', 'XHO22'),
(23, 18, 44, 'Besakuva ngam, bondiva; Oonyana bolunye uhlanga baya kundilulamela.', 'XHO22'),
(23, 18, 45, 'Oonyana bolunye uhlanga baya kunkwantya, bankwantye ngeendawo zabo.', 'XHO22'),
(23, 18, 46, 'uYehova uhleli; lelokubongwa iliwa lam; kwaye ngamana uThixo wosindiso lwam angenziwa mkhulu.', 'XHO22'),
(23, 18, 47, 'Mna ndinguThixo ovuzayo, ozithobelayo phantsi kwam iintlanga.', 'XHO22'),
(23, 18, 48, 'Undihlangulayo ezintshabeni zam, Ewe, undiphakamisayo kwabasukela phezulu kum; Wandihlangula kumcinezeli.', 'XHO22'),
(23, 18, 49, 'Ngako oko, Yehova, ndiya kubulela kuwe phakathi kweentlanga, Ndivume kwigama lakho.', 'XHO22'),
(23, 18, 50, 'Ulokhulisa usindiso lokumkani wakhe; Uze ube nenceba kumthanjiswa wakhe, kuDavide, nakwimbewu yakhe kude kuse ephakadeni.', 'XHO22'),
(23, 19, 1, 'Kukumkani weemvumi, Umhobe kaDavide. Izulu libalisa uzuko lukaThixo; nesibhakabhaka sixela umsebenzi wezandla zakhe.', 'XHO22'),
(23, 19, 2, 'Emini uthetha ulwimi, ubusuku nemini uthetha ukwazi.', 'XHO22'),
(23, 19, 3, 'Akukho ntetho, nantetho, kungekho lizwi livakaliswayo;', 'XHO22'),
(23, 19, 4, 'Indumiso yabo iye ehlabathini lonke, Namazwi abo eziphelweni zehlabathi. Wamisa umnquba khona,', 'XHO22'),
(23, 19, 5, 'onjengomyeni, xa ephuma egumbini, agcobe njengegorha, ukuba ibaleke elugqatsweni.', 'XHO22'),
(23, 19, 6, 'Ukuphuma kwalo kuthabathele ekupheleni kwezulu, ukujikeleza kwalo kuse ekupheleni; akukho nto ifihlakeleyo kuye ubushushu balo.', 'XHO22'),
(23, 19, 7, 'Umyalelo kaYehova ugqibelele, uhlaziya umphefumlo; Ubungqina bukaYehova buqinisekile, bunika abaziziyatha ubulumko.', 'XHO22'),
(23, 19, 8, 'Imithetho kaYehova inobulungisa, ivuyisa intliziyo; Umyalelo kaYehova unyulu, uvula amehlo.', 'XHO22'),
(23, 19, 9, 'Ukoyika uYehova kucocekile, kumi ngonaphakade; Imigwebo kaYehova iyinyaniso yaye inobulungisa.', 'XHO22'),
(23, 19, 10, 'Zinokubekeka ngaphezu kwegolide embiweyo, nakwegolide engcwengiweyo; zimnandi ngaphezu kobusi nenqatha lobusi.', 'XHO22'),
(23, 19, 11, 'Kananjalo umkhonzi wakho ulumkiswa ngazo; ukuzigcina kunomvuzo omkhulu.', 'XHO22'),
(23, 19, 12, 'Ngubani na onokuziqonda iimpazamo zakhe? Ndihlambulule kwiimpazamo ezifihlakeleyo.', 'XHO22'),
(23, 19, 13, 'Mgcine umkhonzi wakho ezonweni zobuqhophololo; abayi kuba nagunya kum; ndonyaniseka, ndimsulwa ebugwenxeni obukhulu.', 'XHO22'),
(23, 19, 14, 'Makakholeke amazwi omlomo wam, nezicamango zentliziyo yam, emehlweni akho, Yehova, mandla am, Msindisi wam.', 'XHO22'),
(23, 20, 1, 'Umhobe kaDavide malikugcine igama likaThixo kaYakobi;', 'XHO22'),
(23, 20, 2, 'Makathumele uncedo oluvela engcweleni, amnike amandla aseZiyon;', 'XHO22'),
(23, 20, 3, 'Wawakhumbula onke amadini akho anyukayo, wawakholise amadini akho anyukayo; Sela.', 'XHO22'),
(23, 20, 4, 'Nikela intliziyo yakho, Uwafeze onke amacebo akho.', 'XHO22'),
(23, 20, 5, 'Siya kumemelela ngosindiso lwakho, Siphakamise iziganga zethu egameni loThixo wethu; Ngamana uYehova angazenza zonke izicelo zakho.', 'XHO22'),
(23, 20, 6, 'Ndiyazi ngoku ukuba uYehova ngumsindisi womthanjiswa wakhe; uya kumgweba ngamandla okusindisa esandla sakhe sokunene emazulwini akhe angcwele.', 'XHO22'),
(23, 20, 7, 'Abanye bakholose ngeemoto, abanye ngamahashe, kodwa thina siya kulikhumbula igama likaYehova, uThixo wethu.', 'XHO22'),
(23, 20, 8, 'Zawa phantsi, kodwa savuka saza sema nkqo.', 'XHO22'),
(23, 20, 9, 'Yehova, sindisa; Ukumkani asiphendule ngemini yokubiza kwethu.', 'XHO22'),
(23, 21, 1, 'Umhobe kaDavide. iNkosi iya kuvuya ngamandla akho, Yehova; yaye hayi indlela oya kugcoba ngayo ngosindiso lwakho!', 'XHO22'),
(23, 21, 2, 'Umnike umnqweno wentliziyo yakhe, akwalandula isicelo somlomo wakhe. Sela.', 'XHO22'),
(23, 21, 3, 'Ngokuba umkhawulela ngeentsikelelo ezilungileyo; Umnike isithsaba segolide ecocekileyo.', 'XHO22'),
(23, 21, 4, 'Wacela ubomi kuwe, wamnika ke, imihla emide ukusa kwixesha elingenammiselo.', 'XHO22'),
(23, 21, 5, 'Lukhulu uzuko lwakhe ngosindiso lwakho; wamnika indili nobukhulu.', 'XHO22'),
(23, 21, 6, 'Ngokuba umenze wasikelelwa ngonaphakade; Umenzele uvuyo olukhulu ebusweni bakho.', 'XHO22'),
(23, 21, 7, 'Ngokuba ukumkani ukholose ngoYehova, ngenxa yenceba yOsenyangweni akayi kushukunyiswa.', 'XHO22'),
(23, 21, 8, 'Isandla sakho siya kubamba zonke iintshaba zakho; isandla sakho sokunene siya kubafumana abakuthiyayo.', 'XHO22'),
(23, 21, 9, 'Uya kubenza babe njengeziko lomlilo, ngexesha lomsindo wakho; uYehova uya kubatshisa ngomsindo wakhe, nomlilo ubatshise.', 'XHO22'),
(23, 21, 10, 'Wositshabalalisa isiqhamo sabo emhlabeni, nabantwana babo phakathi koonyana babantu;', 'XHO22'),
(23, 21, 11, 'Kuba bacebe ububi ngawe, bacebe icebo eliyingozi abangayi kuligqiba.', 'XHO22'),
(23, 21, 12, 'Ke ngoko, zikhwebule kubo, xa uthe wabaxunela iintolo zakho ezintanjeni.', 'XHO22'),
(23, 21, 13, 'Phakamisa, Yehova, ngamandla akho; Ngoko ke siya kumemelela, sidumise amandla akho.', 'XHO22'),
(23, 22, 1, 'Kumongameli. Umhobe kaDavide. Thixo wam, Thixo wam, undishiye ngani na? Yini na ukuba ude ungandincedi, uthethe ngokubhonga kwam?', 'XHO22'),
(23, 22, 2, 'Thixo wam, ndibiza imini, ungeva; ebusuku, andiphumli.', 'XHO22'),
(23, 22, 3, 'Ke nina ningcwele, nina nimiyo emideni yakwaSirayeli;', 'XHO22'),
(23, 22, 4, 'Oobawo bakholosa ngawe, bakholosa ngawe, wabasindisa;', 'XHO22'),
(23, 22, 5, 'Babiza kuwe, basindiswa; bakholose ngawe, àbadana.', 'XHO22'),
(23, 22, 6, 'Mna ke ndingumbungu, andenzi bubi; sisingcikivo soluntu, into edelekileyo ebantwini.', 'XHO22'),
(23, 22, 7, 'Bonke abandibonayo bayandihleka; bakhupha imilomo yabo, bahlunguzela iintloko zabo, bathi:', 'XHO22'),
(23, 22, 8, 'Wakholosa ngoYehova ukuba amsindise; makamsindise, ngokuba emthandile.', 'XHO22'),
(23, 22, 9, 'Ke nguwe owandithabatha esizalweni sikanina; Wandenza ndakholelwa esibelekweni sikamama.', 'XHO22'),
(23, 22, 10, 'Mna ndilahlwe kuwe kwasesizalweni sikama; Wena unguThixo wam kwasesizalweni sikama.', 'XHO22'),
(23, 22, 11, 'Musa ukuba kude nam; ngokuba isondele intlekele; ngokuba ngamampunge.', 'XHO22'),
(23, 22, 12, 'Ndingqongwe ziinkunzi ezininzi, iinkunzi ezintsha zeBhashan zandirhawula.', 'XHO22'),
(23, 22, 13, 'Zindakhamele umlomo wazo njengengonyama egqumayo;', 'XHO22'),
(23, 22, 14, 'Ndiphalele njengamanzi, aphukile onke amathambo am; Intliziyo yam injengomthwebeba; yanyibilika esuswini sam.', 'XHO22'),
(23, 22, 15, 'Omile njengohlangothi lweziko amandla am; Ulwimi lwam lunamathele emhlathini wam; Wandibeka eluthulini lokufa.', 'XHO22'),
(23, 22, 16, 'Ngokuba zindirhawule izinja, Ingxokolo yongendawo indirhawule; bagqobhoza izandla zam neenyawo zam.', 'XHO22'),
(23, 22, 17, 'Ndinokuwabala onke amathambo am;', 'XHO22'),
(23, 22, 18, 'Babelana ngeengubo zam, bazenza amaqashiso ngempahla yam;', 'XHO22'),
(23, 22, 19, 'Ke wena, Yehova, musa ukuba kude; Ungamandla am, khawuleza undincede.', 'XHO22'),
(23, 22, 20, 'Wusindise umphefumlo wam emkhontweni; Intanda yam isemandleni enja.', 'XHO22'),
(23, 22, 21, 'Ndisindise emlonyeni wengonyama, Ngokuba uve iimpondo zenyathi.', 'XHO22'),
(23, 22, 22, 'Ndiya kulixela igama lakho kubazalwana bam; ndiya kukudumisa ebandleni.', 'XHO22'),
(23, 22, 23, 'Nina nimoyikayo uYehova, mdumiseni; Nonke, nzala kaYakobi, mzukiseni; moyikeni, nonke nina nzala kaSirayeli.', 'XHO22'),
(23, 22, 24, 'Kuba azidelekanga, azibanga nazothe kuye ukubandezeleka kolusizana; akabusithelisa ubuso bakhe kuye; wambiza, weva.', 'XHO22'),
(23, 22, 25, 'Indumiso yam ibe kuwe ebandleni elikhulu, Nezibhambathiso zam ndiya kuzizalisa phambi kwabamoyikayo.', 'XHO22'),
(23, 22, 26, 'Abalulamileyo baya kudla bahluthe; abamngxameleyo baya kudumisa uYehova; iya kuphila intliziyo yakho ngonaphakade.', 'XHO22'),
(23, 22, 27, 'Ziya kukhumbula zonke iziphelo zehlabathi, zibuyele kuYehova, iguqe phambi kwakho yonke imizalwane yeentlanga.', 'XHO22'),
(23, 22, 28, 'Kuba ubukumkani bobukaYehova, yaye nguye umlawuli weentlanga.', 'XHO22'),
(23, 22, 29, 'Baya kudla, baguqe bonke abatyebileyo behlabathi; Baya kuguqa phambi kwakhe bonke abatshona eluthulini; akukho namnye unako ukuwugcina uphila umphefumlo wakhe.', 'XHO22'),
(23, 22, 30, 'Imbewu iya kumkhonza; uYehova uya kubalelwa kwesinye isizukulwana.', 'XHO22'),
(23, 22, 31, 'Baya kuza baxele ubulungisa bayo Kubantu abaza kuzalwa, ukuba nguye owenze oko.', 'XHO22'),
(23, 23, 1, '(Indumiso kaDavide.) uYehova ngumalusi wam; Andifuni.', 'XHO22'),
(23, 23, 2, 'Undiphumza emakriweni aluhlaza; Undikhokelela emanzini angawokuphumla.', 'XHO22'),
(23, 23, 3, 'Uyawuhlaziya umphefumlo wam, undikhaphela ezindleleni zobulungisa ngenxa yegama lakhe.', 'XHO22'),
(23, 23, 4, 'Nokuba ndihamba emfuleni wethunzi lokufa, Andiyi koyika bubi, ngokuba unam wena; Intonga yakho nomsimelelo wakho ziyandithuthuzela.', 'XHO22'),
(23, 23, 5, 'Undilungisela isithebe phambi kobuso bam, Ebusweni beentshaba zam; Uyithambisa ngeoli intloko yam; iyaphalala indebe yam.', 'XHO22'),
(23, 23, 6, 'Inene, ndiya kulandelwa kokulungileyo nenceba Yonke imihla yobomi bam, ndihlale endlwini kaYehova yonke imihla.', 'XHO22'),
(23, 24, 1, '(Indumiso kaDavide.) LelikaYehova ihlabathi neento zonke ezikulo; ihlabathi abahlala kulo.', 'XHO22'),
(23, 24, 2, 'Ngokuba waluseka ulwandle, Wayitywina imilambo;', 'XHO22'),
(23, 24, 3, 'Ngubani na oya kunyuka aye entabeni kaYehova? Ngubani na onokuma engcweleni yakhe?', 'XHO22'),
(23, 24, 4, 'Yezandla ezihlambulukileyo, nentliziyo ehlambulukileyo; Ongawuphakamiseli ilize umoya wakhe, Ongafungi ubuxoki.', 'XHO22'),
(23, 24, 5, 'Uya kufumana intsikelelo kaYehova, nokusesikweni kuThixo wosindiso lwakhe.', 'XHO22'),
(23, 24, 6, 'Siso esi isizukulwana sabakufunayo, abafuna ubuso bakho, Yakobi. Sela.', 'XHO22'),
(23, 24, 7, 'Phakamisani iintloko zenu, masango; niziphakamise, minyango yaphakade; yaye ukumkani obekekileyo uya kungena.', 'XHO22'),
(23, 24, 8, 'Ngubani na loo kumkani ubekekileyo? NguYehova, igorha eligorha, nguYehova, igorha emfazweni.', 'XHO22'),
(23, 24, 9, 'Phakamisani iintloko zenu, masango; niphakame, masango angunaphakade! yaye ukumkani obekekileyo uya kungena.', 'XHO22'),
(23, 24, 10, 'Ngubani na lo kumkani ubekekileyo? uNdikhoyo wemikhosi nguNdikhoyo onamandla onke. Sela.', 'XHO22'),
(23, 25, 1, '(Indumiso kaDavide.) Yehova, ndiwuphakamisela kuwe umphefumlo wam.', 'XHO22'),
(23, 25, 2, 'Thixo wam, ndikholose ngawe; Andiyi kudana, andisayi kukha bandoyise iintshaba zam.', 'XHO22'),
(23, 25, 3, 'Ewe, makungabikho namnye uneentloni ngaye okulindeleyo; ihlazo kwabo benza ubugwenxa ngaphandle kwesizathu.', 'XHO22'),
(23, 25, 4, 'Ndazise iindlela zakho, Yehova; Ndifundise iindlela zakho', 'XHO22'),
(23, 25, 5, 'Ndinyathelise enyanisweni yakho, ndifundise, Ngokuba unguThixo ongumsindisi wam; Kudala ndikulindile imini yonke.', 'XHO22'),
(23, 25, 6, 'Zikhumbule iinceba zakho, Yehova, neemfesane zakho; ngokuba babebadala.', 'XHO22'),
(23, 25, 7, 'Musa ukuzikhumbula izono zobutsha bam, nokreqo lwam; ndikhumbule ngenceba yakho, ngenxa yenceba yakho, Yehova.', 'XHO22'),
(23, 25, 8, 'uYehova nguThixo, ulilungisa; ngenxa yoko uya kubafundisa endleleni aboni.', 'XHO22'),
(23, 25, 9, 'Uyabanyathelisa abalulamileyo emasikweni, Abafundise abalulamileyo indlela yakhe.', 'XHO22'),
(23, 25, 10, 'Zonke iindlela zikaYehova ziyinceba nenyaniso kwabagcina umnqophiso wakhe namacebo akhe.', 'XHO22'),
(23, 25, 11, 'Ngenxa yegama lakho, Yehova, xolela ubugwenxa bam; kuba inkulu.', 'XHO22'),
(23, 25, 12, 'Ngubani na ke lo mntu woyika uYehova? uya kumfundisa ngokwendlela ayinyulileyo.', 'XHO22'),
(23, 25, 13, 'Wolala ngoxolo umphefumlo wakhe; nembewu yakhe iya kulidla ilifa ilizwe.', 'XHO22'),
(23, 25, 14, 'Ucweyo lukaYehova nabamoyikayo; nowazisa umnqophiso wakhe kubo.', 'XHO22'),
(23, 25, 15, 'Amehlo am ahlala ekuYehova; kuba nguye oya kuzikhupha iinyawo zam emgibeni.', 'XHO22'),
(23, 25, 16, 'Ndibheke, undibabale; ngenxa yokuba ndindodwa, ndicinezelekile.', 'XHO22'),
(23, 25, 17, 'Amaxhala entliziyo yam aninzi; ndikhuphe engxakini yam!', 'XHO22'),
(23, 25, 18, 'Wujonge umsindo wam nokwaphuka kwam; undixolele zonke izono zam.', 'XHO22'),
(23, 25, 19, 'Zilumkele iintshaba zam; ngokuba baninzi; Bandithiye ngentiyo ekhohlakeleyo.', 'XHO22'),
(23, 25, 20, 'Wusindise umphefumlo wam, undisindise; mandingadani; ngokuba ndikholose ngawe.', 'XHO22'),
(23, 25, 21, 'Mandilondolozele ubulungisa nokusesikweni; ngokuba ndilindele wena', 'XHO22'),
(23, 25, 22, 'Owu Thixo, mhlangule uSirayeli kuzo zonke iimbandezelo zakhe.', 'XHO22'),
(23, 26, 1, '(Indumiso kaDavide.) Lithethe ityala lam, Yehova; ngokuba ndihambe ngobulungisa bam; Kananjalo ndakholosa ngoYehova; ke ngoko, andiyi kutyibilika.', 'XHO22'),
(23, 26, 2, 'Ndigocagoce, Yehova, undicikide; Ndicikide isinqe sam nentliziyo yam.', 'XHO22'),
(23, 26, 3, 'Ngokuba inceba yakho iphambi kwamehlo am, Ndihamba enyanisweni yakho.', 'XHO22'),
(23, 26, 4, 'Andihlali namadoda akhohlakeleyo, Andihambi nabakhohlisi.', 'XHO22'),
(23, 26, 5, 'Ndilithiyile ibandla lamanginingini; kwaye akayi kuhlala nabangendawo.', 'XHO22'),
(23, 26, 6, 'Ndiya kuhlamba izandla zam ngobumsulwa; Ndiya kunqula isibingelelo sakho, Yehova!', 'XHO22'),
(23, 26, 7, 'ukuze ndithethe ndinezwi lokubulela, Ndibalise ngayo yonke imisebenzi yakho ebalulekileyo.', 'XHO22'),
(23, 26, 8, 'Yehova, ndiyakuthanda ukwakha indlu yakho, nendawo yokuhlala kozuko lwakho.', 'XHO22'),
(23, 26, 9, 'Musa ukuwubuthela umphefumlo wam ndawonye naboni, Ubomi bam ndawonye namadoda anamagazi.', 'XHO22'),
(23, 26, 10, 'Kusezandleni zabo ubutshinga, Osandla sokunene sizele sisicengo.', 'XHO22'),
(23, 26, 11, 'Mna ke ndihamba ngobulungisa bam; ndisindise, ube nenceba kum.', 'XHO22'),
(23, 26, 12, 'Unyawo lwam lumi endaweni ebanzi; ndisemabandleni ndiya kumdumisa uYehova.', 'XHO22'),
(23, 27, 1, '(Indumiso kaDavide.) uYehova kukukhanya kwam nomsindisi wam; ndiya koyika bani na? NguYehova inqaba yobomi bam; Ndizakoyika bani?', 'XHO22'),
(23, 27, 2, 'Beza kum abangendawo, neentshaba zam, neentshaba zam, ukuba badle inyama yam, bakhubeka bawa bona;', 'XHO22'),
(23, 27, 3, 'Nokuba ithe yandivingca intendelezo, isuka ingoyiki intliziyo yam; nokuba ndithe ndavuka ndaya emfazweni, ndokholosa ngalo.', 'XHO22'),
(23, 27, 4, 'Ndicele nto-nye kuYehova, ndifuna yona; ukuze ndihlale endlwini kaYehova yonke imihla yobomi bam, ukuze ndibubone ubuqaqawuli bukaYehova, ndihlale etempileni yakhe.', 'XHO22'),
(23, 27, 5, 'Ngokuba wondigusha ententeni yakhe ngexesha lobubi; Undigusha apho esithebeni intente yakhe; Uya kundibeka eweni.', 'XHO22'),
(23, 27, 6, 'Kwaye ngoku, intloko yam iya kuphakama phezu kweentshaba zam ezindirhawulayo; ke ngoko ndiya kunikela imibingelelo yovuyo ententeni yakhe; Ndiyakuthanda ukucula, ewe, ndiyakuthanda ukuculela uzuko lukaYehova.', 'XHO22'),
(23, 27, 7, 'Ndibazele indlebe, Yehova, ndakubiza ngezwi lam; ndibabale, undiphendule.', 'XHO22'),
(23, 27, 8, 'Xa nithi, Funani ubuso bam; Intliziyo yam yathi kuwe, Ndiya kubufuna ubuso bakho, Yehova.', 'XHO22'),
(23, 27, 9, 'Musa ukubusithelisa ubuso bakho kum; Musa ukumgxotha umkhonzi wakho ngomsindo; Uluncedo kum; Musa ukundishiya, Thixo ondisindisayo;', 'XHO22'),
(23, 27, 10, 'Ukuba ubawo noma bandishiyile, Wondithabatha uYehova;', 'XHO22'),
(23, 27, 11, 'Ndifundise indlela yakho, Yehova, Ndikhaphe emendweni othe tye, ngenxa yabandilaleleyo.', 'XHO22'),
(23, 27, 12, 'Musa ukundinikela elitheni leentshaba zam; ngokuba ndiphakanyelwe ngamangqina obuxoki, nabantu abafutha ubutshinga.', 'XHO22'),
(23, 27, 13, 'Ndandidiniwe, andizange ndikholelwe ukuba ndingakubona ukulunga kukaYehova kwihlabathi labo bakuvayo.', 'XHO22'),
(23, 27, 14, 'Kholosa ngoYehova, yiba nesibindi, woyiqinisa intliziyo yakho; Ndithi, lindela kuYehova.', 'XHO22'),
(23, 28, 1, 'Ndinqula wena, Yehova, liwa lam; musa ukuthi cwaka ngam, ukuba anithanga nithule ngam, ndifane nabahla baye emhadini.', 'XHO22'),
(23, 28, 2, 'Live ilizwi lomthandazo wam ekudandulukeni kwam kuwe, Ekuziphakamiseni kwam izandla zam engcweleni yakho engcwele.', 'XHO22'),
(23, 28, 3, 'Musa ukundihambisa kunye nabangendawo, nabangendawo, abathetha uxolo kummelwane wabo, kanti intliziyo yabo inenkohliso.', 'XHO22'),
(23, 28, 4, 'Banike ngokwemisebenzi yabo, ngokobubi bezenzo zabo; Banike umsebenzi wezandla zabo; Babuyisele intlango yabo.', 'XHO22'),
(23, 28, 5, 'Ngenxa yokuba bengayihloneli imisebenzi kaYehova nomsebenzi wezandla zakhe, uya kubatshabalalisa, angabaphakamisi.', 'XHO22'),
(23, 28, 6, 'Makabongwe uYehova, kuba elivile ilizwi lomthandazo wam.', 'XHO22'),
(23, 28, 7, 'NguYehova amandla am nengweletshetshe yam; Yakholosa ngaye intliziyo yam, ndancedwa; ke ngoko, intliziyo yam yonwabe kakhulu; ndiya kumdumisa ngengoma yam.', 'XHO22'),
(23, 28, 8, 'uYehova uligwiba labo, uyingxonde yomthanjiswa wakhe.', 'XHO22'),
(23, 28, 9, 'Khulula abantu bakho, lisikelele ilifa lakho; kwaye uzidlise kwaye uziphakamise ngonaphakade.', 'XHO22'),
(23, 29, 1, 'iNgoma kaDavide.', 'XHO22'),
(23, 29, 2, 'Mnikeni uYehova imbeko kwigama lakhe; nqula uYehova ngendlela ebalaseleyo.', 'XHO22'),
(23, 29, 3, 'Ilizwi likaYehova liphezu kwamanzi: uThixo wozuko uyaduduma, uYehova uphezu kwamanzi amaninzi.', 'XHO22'),
(23, 29, 4, 'Ilizwi likaYehova linamandla; Ilizwi likaYehova lizele bubuhle.', 'XHO22'),
(23, 29, 5, 'Ilizwi likaYehova linyikimisa imisedare; Ewe, uYehova uyayonakalisa imisedare yaseLebhanon.', 'XHO22'),
(23, 29, 6, 'Uyabatsiba njengethole lenkomo; iLebhanon neSiriyon njengenkonyana yenyathi.', 'XHO22'),
(23, 29, 7, 'Ilizwi likaYehova lahlula amadangatye omlilo;', 'XHO22'),
(23, 29, 8, 'Izwi likaYehova liyothusa intlango; uYehova unyikimisa intlango yaseKadeshe.', 'XHO22'),
(23, 29, 9, 'Ilizwi likaYehova lizala ixhama, amahlathi ayatshatyalaliswa, yaye yonke into etempileni yakhe ixela uzuko lwakhe.', 'XHO22'),
(23, 29, 10, 'uYehova uhleli phezu konogumbe; ewe, uYehova uhleli ngonaphakade.', 'XHO22'),
(23, 29, 11, 'uYehova uya kubanika amandla abantu bakhe; uYehova uya kubasikelela abantu bakhe ngoxolo.', 'XHO22'),
(23, 30, 1, 'EkaDavide. EkaDavide. Ndiya kubulela kuwe, Yehova; Ngokuba undirhole, Akwazivumela iintshaba zam ukuba zindivuyelele.', 'XHO22'),
(23, 30, 2, 'Yehova, Thixo wam, ndibize kuwe, wandiphilisa.', 'XHO22'),
(23, 30, 3, 'Yehova, uwuphakamisile umphefumlo wam kwelabafileyo; Wandihlangula ekuhlikeni ndiye emhadini.', 'XHO22'),
(23, 30, 4, 'Vumani kuYehova, nina bangcwele bakhe, mdumiseni uzuko lwakhe olungcwele.', 'XHO22'),
(23, 30, 5, 'Ngokuba yinto yephanyazo umsindo wakhe; Ngobabalo lwakhe kukho ubomi: Ukulila kuhleli ubusuku bunye, Kusasa luvuyo.', 'XHO22'),
(23, 30, 6, 'Ndandisazi ke mna ekuthuthuzelekeni kwam, ukuba andiyi kushukunyiswa naphakade.', 'XHO22'),
(23, 30, 7, 'Yehova, ngenceba yakho uyayomeleza intaba yam; Ubusithelisile ubuso bakho, ndoyika.', 'XHO22'),
(23, 30, 8, 'Ndadanduluka kuwe, Yehova; ndathandaza kuYehova.', 'XHO22'),
(23, 30, 9, 'Kukho nzuzo yani na egazini lam, ekuhleni kwam ndiye emhadini? Luya kukudumisa uthuli na? iya kuthetha inyaniso yaKho?', 'XHO22'),
(23, 30, 10, 'Yehova, ndiphendule, ndibabale; Yehova, yiba ngumncedi wam.', 'XHO22'),
(23, 30, 11, 'Ukuguqule ukukhala kwam kwaba ngumngqungqo; Wathabatha ilaphu lengxowa yam, wandibopha ngovuyo;', 'XHO22'),
(23, 30, 12, 'Lude uzuko lwam luvume uzuko lwakho, lungathi cwaka. Yehova, Thixo wam, ndiya kubulela kuwe ngonaphakade.', 'XHO22'),
(23, 31, 1, 'Kukumkani umhobe kaDavide ungaze undidanise; ndisindise ngobulungisa bakho.', 'XHO22'),
(23, 31, 2, 'Yiwa phantsi ecaleni kwam; khawuleza undisindise; Yiba liliwa eliligwiba kum, nemboniselo yokundisindisa.', 'XHO22'),
(23, 31, 3, 'Ngokuba ingxondorha yam nemboniselo yam nguwe; ke ngenxa yegama lakho, ndikhokele, ndikhokele.', 'XHO22'),
(23, 31, 4, 'Ndihlangule esirhintyelweni ababendilinde ngaso, Ngokuba ungamandla am.', 'XHO22'),
(23, 31, 5, 'Ndiwubeke esandleni sakho umphefumlo wam: ndisindise, Yehova, Thixo oyinyaniso.', 'XHO22'),
(23, 31, 6, 'Ndabathiya abaphulaphula ubuxoki, kodwa ndathembela ngoYehova.', 'XHO22'),
(23, 31, 7, 'Ndiya kuba nemihlali ndigcobe ngenxa yobubele bakho bothando, ngenxa yokuba ukubonile ukubandezeleka kwam; Uyazi ukuba umphefumlo wam usengxakini;', 'XHO22'),
(23, 31, 8, 'Anindinikela esandleni sotshaba; wangena egumbini elikhulu.', 'XHO22'),
(23, 31, 9, 'Ndibabale, Yehova, ngokuba ndibandezelekile; alilile iinyembezi zam, nomphefumlo wam, nesisu sam;', 'XHO22'),
(23, 31, 10, 'Ngokuba ubomi bam buzele yimbandezelo, Neminyaka yam iyancwina;', 'XHO22'),
(23, 31, 11, 'Ndaba sisingcikivo phakathi kweentshaba zam zonke, Ngokukodwa kubamelwane bam; bandibaleka abandibona ndingaphandle.', 'XHO22'),
(23, 31, 12, 'Ndilityelwe njengofileyo entliziyweni, ndinjengembiza eqhekekileyo.', 'XHO22'),
(23, 31, 13, 'Ngokuba ndive intlebendwane yabaninzi, yathi, Kukunxunguphala ngeenxa zonke; njengoko babhunga ngam, Babhunga ukuthabatha umphefumlo wam.', 'XHO22'),
(23, 31, 14, 'Ke mna ndikholose ngawe, Yehova; Ndathi: UnguThixo wam.', 'XHO22'),
(23, 31, 15, 'Asesandleni sakho amaxesha am; ndihlangule esandleni seentshaba zam, nasesandleni sabandisukelayo.', 'XHO22'),
(23, 31, 16, 'Ubuso bakho bukhanyise kumkhonzi wakho; Ngenxa yeenceba zakho ndihlangule.', 'XHO22'),
(23, 31, 17, 'Yehova, musa ukundidanisa; ngokuba ndikubizile; Mabadane abangendawo, bathi cwaka kwelabafileyo.', 'XHO22'),
(23, 31, 18, 'Umlomo wobuxoki mawuthi cwaka; abathetha amalungisa ngokusa, ngendelo.', 'XHO22'),
(23, 31, 19, 'Hayi, ubukhulu bokulunga kwakho, obuqwebele abakoyikayo; Into oyenzileyo kwabo bakholosa ngawe phambi koonyana babantu!', 'XHO22'),
(23, 31, 20, 'Uyabagusha emfihlekweni, Ngenxa yekratshi labantu; Kufuneka uzigcine ziyimfihlo endlwini kungquzulwano lweelwimi.', 'XHO22'),
(23, 31, 21, 'Makabongwe uYehova, kuba undenze isixeko eyomeleleyo ngenceba yakhe emangalisayo.', 'XHO22'),
(23, 31, 22, 'Ngokuba ndathi mna entliziyweni yam, Ndinqanyulwe phambi kwamehlo akho; waliva ilizwi lomthandazo wam ekudandulukeni kwam kuwe.', 'XHO22'),
(23, 31, 23, 'Thandani uYehova, nonke nina zintanda zakhe, ngokuba uYehova ungumlondolozi wabanyanisileyo, uyabavuza abangendawo.', 'XHO22'),
(23, 31, 24, 'Yibani nesibindi, yaye uya kuzomeleza iintliziyo zenu, nonke nina nikholose ngoYehova.', 'XHO22'),
(23, 32, 1, 'iNgoma kaDavide: iMaskil.', 'XHO22'),
(23, 32, 2, 'Hayi, uyolo lomntu ongabunyamezeliyo uYehova kuye ubugwenxa, Omoya wakhe ukhululekileyo ekukhohlisweni.', 'XHO22'),
(23, 32, 3, 'Ndathi ndakuthi cwaka, aluphale amathambo am ngenxa yokubhonga kwam imini yonke.', 'XHO22'),
(23, 32, 4, 'Ngokuba besinzima phezu kwam isandla sakho imini nobusuku, Busuka ubumanzi bam, baba kukubalela kwasehlotyeni. Sela.', 'XHO22'),
(23, 32, 5, 'Ndisivumile isono sam kuwe, andabufihla ubugwenxa bam; Ndathi, Ndithanda ukuzixela izono zam kuYehova; undixolele ububi besono sam. Sela.', 'XHO22'),
(23, 32, 6, 'Ngako oko, onke amalungisa aya kuthandaza kuwe ngexesha lokufunyanwa kwakho; Ewe, emijelweni yamanzi amaninzi, ayisayi kufika kuye.', 'XHO22'),
(23, 32, 7, 'Uyindawo yam yokusabela; Uya kundihlangula embandezelweni; Uya kundirhawula ngeengoma zosindiso. Sela.', 'XHO22'),
(23, 32, 8, 'Ndiya kuniyala, ndinibonise indlela enifanele ukuhamba ngayo; Ndiya kukukhokela ngeliso lam.', 'XHO22'),
(23, 32, 9, 'Musani ukuba njengehashe, noondlebende, ukungabi nangqondo; abamlomo wabo uvingcelwe ngokuluma nangomkhala, hleze bafike kuni.', 'XHO22'),
(23, 32, 10, 'Uza kufikelwa ziimbandezelo ezininzi, kodwa okholose ngoYehova ujikelezwe yinceba.', 'XHO22'),
(23, 32, 11, 'Vuyani kuYehova, gcobani, nina malungisa, gcobani, nonke nina bantliziyo zithe tye.', 'XHO22'),
(23, 33, 1, 'Memelelani kuYehova, nina malungisa, Ngokuba imnandi indumiso kumalungisa.', 'XHO22'),
(23, 33, 2, 'Mdumiseni uYehova ngencakuba; vumani kuye ngohadi nangomrhubhe ontambo zilishumi.', 'XHO22'),
(23, 33, 3, 'Mvumeleni ingoma entsha; dlala kamnandi ngengxolo eninzi.', 'XHO22'),
(23, 33, 4, 'Ngokuba lithe tye ilizwi likaYehova; yonke imisebenzi yakhe uyenzele inyaniso.', 'XHO22'),
(23, 33, 5, 'Uthanda ubulungisa nokusesikweni, Uzele yinceba kaYehova ihlabathi.', 'XHO22'),
(23, 33, 6, 'Lenzeka izulu ngelizwi likaYehova; nomkhosi wawo wonke ngomoya womlomo wakhe.', 'XHO22'),
(23, 33, 7, 'Ulobutha njengemfumba amanzi olwandle; Ugcina amanzi koovimba.', 'XHO22'),
(23, 33, 8, 'Loyike uYehova elimiweyo; Mabamoyike bonke abemi behlabathi.', 'XHO22'),
(23, 33, 9, 'Ngokuba wathetha, kwabakho; wayalela, yahlala imile.', 'XHO22'),
(23, 33, 10, 'uYehova uyawatshitshisa amacebo eentlanga; Uyawatshabalalisa amacebo abantu.', 'XHO22'),
(23, 33, 11, 'Injongo kaYehova ingunaphakade, iingcamango zentliziyo yakhe zisuka kwesinye isizukulwana ziye kwesinye.', 'XHO22'),
(23, 33, 12, 'Hayi, uyolo lohlanga oluThixo walo unguYehova; nabantu abanyulileyo, ukuba babe lilifa kuye.', 'XHO22'),
(23, 33, 13, 'uYehova uqondele esemazulwini; uyababona bonke abantwana babantu.', 'XHO22'),
(23, 33, 14, 'Esendaweni yakhe yokuhlala uyababona bonke abemi behlabathi.', 'XHO22'),
(23, 33, 15, 'Uyayikholisa intliziyo yabo; Ubona zonke izenzo zabo.', 'XHO22'),
(23, 33, 16, 'Akukho kumkani usindiswayo ngobuninzi bamaphakathi; iqhawe alisindi ngamandla amakhulu.', 'XHO22'),
(23, 33, 17, 'Ihashe alinakusinda, yaye ngenxa yobugorha balo alinakumsindisa umntu.', 'XHO22'),
(23, 33, 18, 'Yabona, amehlo kaYehova aphezu kwabamoyikayo, abakholose ngenceba yakhe;', 'XHO22'),
(23, 33, 19, 'Ukuba akhulule imiphefumlo yabo ekufeni, Abasindise endlaleni.', 'XHO22'),
(23, 33, 20, 'Umphefumlo wethu ulindele kuYehova; Uncedo lwethu nengweletshetshe yethu nguye.', 'XHO22'),
(23, 33, 21, 'Ngokuba iya kuvuya ngaye intliziyo yethu, Sikholosile nje ngegama lakhe elingcwele.', 'XHO22'),
(23, 33, 22, 'Yehova, inceba yakho mayibe phezu kwethu, njengoko sikholose ngawe.', 'XHO22'),
(23, 34, 1, 'Umhobe kaDavide, ekuphambukeni kwakhe kuAbhimeleki, wamndulula, wemka.', 'XHO22'),
(23, 34, 2, 'Uya kuqhayisa umphefumlo wam ngoYehova;', 'XHO22'),
(23, 34, 3, 'Mbongeni uYehova kunye nam, siliphakamise kunye igama lakhe.', 'XHO22'),
(23, 34, 4, 'Ndamfuna uYehova, wandiva, Wandihlangula kuzo zonke iimbandezelo zam.', 'XHO22'),
(23, 34, 5, 'Bakhangela kuye, bakhazimla, baza ubuso babo baba nazintloni.', 'XHO22'),
(23, 34, 6, 'Ladanduluka elihlwempu, weva uYehova, wayihlangula kuzo zonke iimbandezelo zayo.', 'XHO22'),
(23, 34, 7, 'Ingelosi kaYehova yehla ibajikeleza abo bamoyikayo, ize ibasindise.', 'XHO22'),
(23, 34, 8, 'Ngcamlani nibone ukuba ulungile uYehova; Hayi, uyolo lomntu okholose ngaye!', 'XHO22'),
(23, 34, 9, 'Moyikeni uYehova, nina bangcwele bakhe, Kuba abaswele nto abamoyikayo.', 'XHO22'),
(23, 34, 10, 'Iingonyama ezintsha ziswele, zilambile; ke bona abamfunayo uYehova abasweli nto ilungileyo.', 'XHO22'),
(23, 34, 11, 'Yizani, bonyana, ndiphulaphuleni, ndinifundise ukumoyika uYehova.', 'XHO22'),
(23, 34, 12, 'Ngubani na umntu obuthandayo ubomi, othanda imihla emininzi, ukuze abone okulungileyo?', 'XHO22'),
(23, 34, 13, 'Gcina ulwimi lwakho ebubini, Nomlomo wakho enkohliso.', 'XHO22'),
(23, 34, 14, 'Suka ebubini, wenze okulungileyo; funa uxolo, uluphuthume.', 'XHO22'),
(23, 34, 15, 'Amehlo kaYehova akumalungisa, uyakuva ukukhalela kwawo uncedo.', 'XHO22'),
(23, 34, 16, 'Ubuso bukaYehova bujonge abenzi bokubi, ukuba akuphelise ehlabathini ukukhunjulwa kwabo.', 'XHO22'),
(23, 34, 17, 'Amalungisa ayabiza, uyaweva uYehova, Awahlangule kuzo zonke iimbandezelo zawo.', 'XHO22'),
(23, 34, 18, 'Usondele uYehova kwabantliziyo yaphukileyo; usindise abatyhafileyo intliziyo.', 'XHO22'),
(23, 34, 19, 'Zininzi iimbandezelo zelungisa, kodwa uYehova uyalihlangula kuzo zonke.', 'XHO22'),
(23, 34, 20, 'Uyawagcina onke amathambo alo; akukho namnye kubo owaphukileyo.', 'XHO22'),
(23, 34, 21, 'Ububi buya kubabulala abangendawo, nabathiyayo amalungisa babe yintlango.', 'XHO22'),
(23, 34, 22, 'uYehova uyawukhulula umphefumlo wabakhonzi bakhe, yaye akukho namnye kwabakholose ngaye oya kuba yintlango.', 'XHO22'),
(23, 35, 1, '(Indumiso kaDavide.) Yehova, babambene nababambene nam, Ulwe nababambana nam.', 'XHO22'),
(23, 35, 2, 'Thabathani ikhaka nekrele; lindani uncedo lwam.', 'XHO22'),
(23, 35, 3, 'Betha ikrele, ubanqande abandisukelayo; Uthi kumphefumlo wam, Ndilusindiso lwakho.', 'XHO22'),
(23, 35, 4, 'Mababe neentloni badane abawufunayo umphefumlo wam;', 'XHO22'),
(23, 35, 5, 'Mababe njengomsonto ophetshethwa ngumoya, sivuthele phezu kwabo isithunywa sikaYehova.', 'XHO22'),
(23, 35, 6, 'Mayibe mnyama indlela yabo, ibe mtyibilizi, Masidlule phezu kwabo isithunywa sikaYehova.', 'XHO22'),
(23, 35, 7, 'Ngokuba basigqumele mna isibatha sabo ngelize esihogweni, abawumbeleyo umphefumlo wam ngelize.', 'XHO22'),
(23, 35, 8, 'Ukonakala makuwele phezu kwakhe ngequbuliso; nithabathe kuphela oko akufihlayo; makawe kwakuloo ntshabalalo.', 'XHO22'),
(23, 35, 9, 'Wogcoba ke umphefumlo wam ngoYehova; liya kulunandipha usindiso lwayo.', 'XHO22'),
(23, 35, 10, 'Onke amathambo am aya kuthi, Yehova, ngubani na onjengawe, umsindisi wosizana kowomeleleyo kunalo, usizana nosizana kwabambano lwalo?', 'XHO22'),
(23, 35, 11, 'Kwavela amangqina obuxoki; bandinyanzela ukuba ndenze izinto endingazaziyo.', 'XHO22'),
(23, 35, 12, 'Babuyisela ububi bam esikhundleni sokulungileyo, bawonakalisa umphefumlo wam.', 'XHO22'),
(23, 35, 13, 'Ke mna, xa babesifa, ingubo yam ibiluhlaza; Ndawuthoba umoya wam ngokuzila ukudla; umthandazo wam wabuyela esifubeni sam.', 'XHO22'),
(23, 35, 14, 'Ndaziphatha njengomhlobo wam, okanye udade wethu, Ndazithoba, njengombambazelela unina.', 'XHO22'),
(23, 35, 15, 'Ekubandezelekeni kwam bavuya, bahlanganisana ndawonye; ndahlule, ungayeki.', 'XHO22'),
(23, 35, 16, 'Batshixizela amazinyo nabahanahanisi emithendelekweni.', 'XHO22'),
(23, 35, 17, 'Yehova, koda kube nini na uyibona? Wusindise umphefumlo wam ekubhujisweni kwabo, zintanda zam.', 'XHO22'),
(23, 35, 18, 'Ndiya kubulela kuwe ebandleni elikhulu; Ndiya kukudumisa phakathi kwabantu abaninzi.', 'XHO22'),
(23, 35, 19, 'Mabangandivuyeleli iintshaba zam, Bangaphelelwa lithemba abandithiyayo.', 'XHO22'),
(23, 35, 20, 'Kuba abathethi luxolo, kodwa bashumayela ubuqhophololo kubantu abaseluxolweni elizweni.', 'XHO22'),
(23, 35, 21, 'Ewe, bandikhamisele umlomo wabo, besithi, Tshotsho, ayabona, amehlo ethu abonile;', 'XHO22'),
(23, 35, 22, 'Ubonile, Yehova; ithe cwaka; Nkosi, musa ukuba kude kum.', 'XHO22'),
(23, 35, 23, 'Khawusuk’ ume, undigwebe, undigwebe, Thixo wam, Nkosi yam;', 'XHO22'),
(23, 35, 24, 'Lithethe ityala lam, Yehova Thixo wam, ngobulungisa bakho; kwaye abandithandi.', 'XHO22'),
(23, 35, 25, 'Abatsho entliziyweni yabo ukuthi, Oh, nathi siyafuna; Mabangathi: Simdlile.', 'XHO22'),
(23, 35, 26, 'Mabame bedanile, bakhungatheke kunye Abavuyela ububi bam; banxibe ihlazo nehlazo, abandihlazisayo.', 'XHO22'),
(23, 35, 27, 'Mabavuye, bavuye, Ababuthandayo ubulungisa bam; ewe, mabahlale besithi, Makabe mkhulu uYehova, obonelele ngobutyebi bomkhonzi wakhe.', 'XHO22'),
(23, 35, 28, 'Ulwimi lwam luya kuxela ubulungisa bakho nendumiso yakho, imini yonke.', 'XHO22'),
(23, 36, 1, 'Umhobe kaDavide: umkhonzi kaYehova. Uthi okungendawo kongendawo entliziyweni yam, Akukho koyika Thixo phambi kwamehlo akhe.', 'XHO22'),
(23, 36, 2, 'Ngokuba uyazikhohlisa kwawakhe amehlo, bude bube lisikizi ububi bakhe.', 'XHO22'),
(23, 36, 3, 'Amazwi omlomo wakhe abubutshinga nenkohliso; wayeka ukuba sisilumko nokwenza okulungileyo.', 'XHO22'),
(23, 36, 4, 'Usila ububi esililini sakhe; uziphatha ngendlela engalunganga; akayi kucekisa ububi.', 'XHO22'),
(23, 36, 5, 'Yehova, isemazulwini inceba yakho; Ukuthembeka kwakho kufikelela esibhakabhakeni.', 'XHO22'),
(23, 36, 6, 'Ubulungisa bakho bunjengeentaba ezinkulu; Zinzulu kakhulu izigwebo zakho; Yehova, uyamkhusela umntu nesilwanyana.', 'XHO22'),
(23, 36, 7, 'Hayi, ubukhulu benceba yakho, Thixo! ngenxa yoko oonyana babantu bakholose ngomthunzi wamaphiko akho.', 'XHO22'),
(23, 36, 8, 'Bayahlutha kunene bubutyebi bendlu yakho; ubaseze emlanjeni wovuyo lwakho.', 'XHO22'),
(23, 36, 9, 'Ngokuba iqula lobomi likuwe; ekukhanyeni kwakho sobona ukukhanya.', 'XHO22'),
(23, 36, 10, 'Qhubekela phambili inceba yakho kwabo ubaziyo; nobulungisa bakho yintliziyo ethe tye.', 'XHO22'),
(23, 36, 11, 'Makungandichukumisi unyawo lwabazidlayo, Singandicinezeli ngesandla sabangendawo.', 'XHO22'),
(23, 36, 12, 'Bawile abasebenzi bobutshinga; ziphoswa phantsi, azinakuvuka.', 'XHO22'),
(23, 37, 1, '(Indumiso kaDavide.) Musa ukubaxhalela amatshijolo, musa ukubamonela abenzi bobubi.', 'XHO22'),
(23, 37, 2, 'Ngokuba baya kusikwa kamsinyane njengengca, babune njengohlaza.', 'XHO22'),
(23, 37, 3, 'Kholosa ngoYehova, wenze okulungileyo; niya kuphila ezweni, nidle amadlelo odwa.', 'XHO22'),
(23, 37, 4, 'Vuyani ke ngoYehova; wokunika okunqwenelwa yintliziyo yakho.', 'XHO22'),
(23, 37, 5, 'Yiyekele kuYehova indlela yakho; ukholose ngaye kwakhona; kwaye uya kukwenza ukuba kwenzeke.', 'XHO22'),
(23, 37, 6, 'Uya kubonakalisa ubulungisa bakho njengokukhanya, nomgwebo wakho njengemini;', 'XHO22'),
(23, 37, 7, 'Yithi cwaka kuYehova, umlinde ngomonde; Musani ukumxhalela lowo unempumelelo endleleni yakhe, nalowo uceba ububi.', 'XHO22'),
(23, 37, 8, 'Yeka ukuba nomsindo, wuyeke umsindo; musa ukuba noburhalarhume nangayiphi na indlela;', 'XHO22'),
(23, 37, 9, 'Ngokuba abangendawo baya kubhuqwa; Ke bona abakholose ngoYehova, baya kulidla ilifa ilizwe.', 'XHO22'),
(23, 37, 10, 'Kusaya kuba mzuzwana, angabikho ongendawo; Ewe, woyiqwalasela indawo yakhe, ibe akuyi kubakho.', 'XHO22'),
(23, 37, 11, 'Kwaye abalulamileyo baya kulidla ilifa ihlabathi; baziyolise ngobutyebi boxolo.', 'XHO22'),
(23, 37, 12, 'Ongendawo ulenzela amayelenqe ilungisa, Ulitshixizela amazinyo.', 'XHO22'),
(23, 37, 13, 'iNkosi iya kumhleka, Ngokuba ibona ukuba iyeza imini yakhe.', 'XHO22'),
(23, 37, 14, 'Barhola amakrele abangendawo, barhola izaphetha zabo, Ukuba babhukuqe usizana nehlwempu, Babulale abahamba ngobulungisa.', 'XHO22'),
(23, 37, 15, 'Ikrele labo liya kungena ezintliziyweni zabo, zaphuke izaphetha zabo.', 'XHO22'),
(23, 37, 16, 'Intwana enayo ilungisa ilunge ngakumbi kunendyebo yabantu abaninzi abangendawo.', 'XHO22'),
(23, 37, 17, 'Kuba izandla zabangendawo ziya kwaphulwa, kodwa uYehova umxhasi wamalungisa.', 'XHO22'),
(23, 37, 18, 'uYehova uyayazi imihla yamalungisa, nelifa lawo liya kuba ngunaphakade.', 'XHO22'),
(23, 37, 19, 'Abayi kudana ngexesha lentlekele, yaye ngemihla yendlala baya kuhlutha.', 'XHO22'),
(23, 37, 20, 'Ke bona abangendawo baya kutshabalala, neentshaba zikaYehova zibe njengamanqatha eemvana baya kutshabalala emsi.', 'XHO22'),
(23, 37, 21, 'Ongendawo uyaboleka angabuyiseli, kodwa ilungisa linenceba lize liphe.', 'XHO22'),
(23, 37, 22, 'Ngokuba abasikelelweyo nguye baya kulidla ilifa ihlabathi; nabo batsalwe nguye baya kunqunyulwa.', 'XHO22'),
(23, 37, 23, 'Ukunyathela kwelungisa kuqiniswa nguYehova, yaye uyakholiswa yindlela yakhe.', 'XHO22'),
(23, 37, 24, 'Nokuba ithe yawa, ayiyi kuqubuda isiqalekiso, kuba uYehova umxhasile ngesandla sayo.', 'XHO22'),
(23, 37, 25, 'Ndandimtsha, kungokunje ndimdala; Ke andizanga ndilibone ilungisa lishiywa, nembewu yalo ifuna isonka.', 'XHO22'),
(23, 37, 26, 'Uhlala ebabala, eboleka; yasikelelwa imbewu yakhe.', 'XHO22'),
(23, 37, 27, 'Suka ebubini, wenze okulungileyo; kwaye uhlale ngonaphakade.', 'XHO22'),
(23, 37, 28, 'Ngokuba uYehova uthanda okusesikweni, Angabashiyi abo bamthandayo; Ke yona imbewu yongendawo iya kutshabalala.', 'XHO22'),
(23, 37, 29, 'Amalungisa aya kuwudla ilifa umhlaba aze ahlale kuwo ngonaphakade.', 'XHO22'),
(23, 37, 30, 'Umlomo welungisa uxela ubulumko, Nolwimi lwalo luthetha okuthe tye.', 'XHO22'),
(23, 37, 31, 'Umyalelo woThixo walo usentliziyweni yalo; Akukho kunyathela kwakhe kuya kuhexa.', 'XHO22'),
(23, 37, 32, 'Ongendawo uyalikhangela ilungisa, afune ukulibulala;', 'XHO22'),
(23, 37, 33, 'uYehova akayi kumyekela esandleni sakhe, angamgwebi ekugwetyweni kwakhe.', 'XHO22'),
(23, 37, 34, 'Thembela kuYehova, umqinise; Wokukhupha, ulidle ilifa ilizwe; ekutshatyalalisweni kongendawo uya kubona.', 'XHO22'),
(23, 37, 35, 'Ndithe ndakubona ukuba womelele, wazikhukula njengomthi wothuthu oluhlaza.', 'XHO22'),
(23, 37, 36, 'Wafa, nanko engasekho; ndamfuna, akafunyanwa.', 'XHO22'),
(23, 37, 37, 'Qonda ogqibeleleyo, ulikhangele ilungisa; Ngokuba izivela-mva zoxolo.', 'XHO22'),
(23, 37, 38, 'Ke bona abangendawo batshatyalaliswe kuphele; isiphelo sabangendawo sigqityiwe.', 'XHO22'),
(23, 37, 39, 'Usindiso lwamalungisa luphuma kuYehova; Uligorha labo ekuzalweni kwabo.', 'XHO22'),
(23, 37, 40, 'uYehova uya kubanceda, abasindise; Uya kubahlangula kongendawo, abasindise, Ngokuba bekholose ngaye.', 'XHO22'),
(23, 38, 1, '(Indumiso kaDavide, isisikhumbuzo.) Yehova, musa ukundohlwaya unomsindo, musa ukundibetha ngomsindo.', 'XHO22'),
(23, 38, 2, 'Ngokuba zitshone kum iintolo zakho, Sandibetha isandla sakho.', 'XHO22'),
(23, 38, 3, 'Akukho mpilo enyameni yam, ngenxa yomsindo wakho; Akukho kuphumla emathanjeni am ngenxa yesono sam.', 'XHO22'),
(23, 38, 4, 'Ngokuba ubugwenxa bam bugqithile entlokweni yam; bandithwele njengomthwalo onzima.', 'XHO22'),
(23, 38, 5, 'Iyanuka, iyabola amanxeba am, Ngenxa yokumatha kwam.', 'XHO22'),
(23, 38, 6, 'Ndithingaza; Ndithobeke kakhulu; Ndiya kulila imini yonke.', 'XHO22'),
(23, 38, 7, 'Kuba amanqe am azele bububi, Akukho mzimba uphilileyo enyameni yam.', 'XHO22'),
(23, 38, 8, 'Ndithambile, ndityumkile kakhulu; Ndabhomboloza kukudana kwentliziyo yam.', 'XHO22'),
(23, 38, 9, 'Yehova, wonke umnqweno wam uphambi kwakho; akusithele kuwe ukuncwina kwam.', 'XHO22'),
(23, 38, 10, 'Intliziyo yam iphelile, ndiphelelwe ngamandla; nokukhanya kwamehlo am kuphelile kum.', 'XHO22'),
(23, 38, 11, 'Izithandane zam nezihlobo zam zikude nentlungu yam; abazalwana bam bema mgama.', 'XHO22'),
(23, 38, 12, 'Bandithiyele abafuna umphefumlo wam; Ke bona abangxamele ububi bam bathetha ububi, Bacinga inkohliso imini yonke.', 'XHO22'),
(23, 38, 13, 'Ke mna ndinjengesithulu, andiva; yaye ndaba njengesimumu esingawuvuli umlomo waso.', 'XHO22'),
(23, 38, 14, 'Ngoko ndaba njengongevayo, nomlomo ungevayo;', 'XHO22'),
(23, 38, 15, 'Ngokuba ndikholose ngawe, Yehova; Uya kuva, Yehova, Thixo wam.', 'XHO22'),
(23, 38, 16, 'Ngenxa enokuba ndithi, Phulaphula kum, hleze bandivuyelele; Ekutyibilikeni konyawo lwam, bayandisukela.', 'XHO22'),
(23, 38, 17, 'Ngokuba ndizimisele ukuyeka, Umvandedwa wam uhleli unam.', 'XHO22'),
(23, 38, 18, 'Ngokuba ndiya kubuxela ububi bam; Ndiya kuzisola ngesono sam.', 'XHO22'),
(23, 38, 19, 'Ke zona iintshaba zam zihleli, zomelele;', 'XHO22'),
(23, 38, 20, 'Abandibuyekeza ngobubi ngobubi, baziintshaba zam; kuba ndilandela okulungileyo.', 'XHO22'),
(23, 38, 21, 'Musa ukundishiya, Yehova, Thixo wam, musa ukubuya umva kum.', 'XHO22'),
(23, 38, 22, 'Khawuleza undincede, Yehova, lusindiso lwam.', 'XHO22'),
(23, 39, 1, 'KuYedutun; umhobe kaDavide. Ndiya kuwuvala umlomo wam, xa abangendawo bekunye nam.', 'XHO22'),
(23, 39, 2, 'Ndaba sisidenge, ndazola, ndazola, ndalungisa; nentlungu yam yashukuma.', 'XHO22'),
(23, 39, 3, 'Intliziyo yam iyaphuphuma, ndakukhumbula umlilo olenyayo; ndiye ndathetha ngolwimi lwam.', 'XHO22'),
(23, 39, 4, 'Ndazise, Yehova, isiphelo sam, nomlinganiso wemihla yam, ukuba injani na; ukuze ndazi ukuba ndibuthathaka kangakanani.', 'XHO22'),
(23, 39, 5, 'Yabona, uyenze imihla yam yangangobubanzi besandla; iminyaka yam ayinto yanto phambi kwakho; inene, wonke umntu ngamampunge awakhe amandla. Sela.', 'XHO22'),
(23, 39, 6, 'Inene, wonke umntu uhamba nomyalezo ongento; ewe, bakhwankqiswe ngamampunge; ehlanganisa ubutyebi, akazi nokuba ngubani na oya kububutha.', 'XHO22'),
(23, 39, 7, 'Ngoku ke ndilindele ntoni na, Nkosi yam? ithemba lam likuwe.', 'XHO22'),
(23, 39, 8, 'Ndisindise elukreqweni lwam lonke; sukundenza isidenge.', 'XHO22'),
(23, 39, 9, 'Ndisisidenge, umlomo wam andiwuvuli; ngokuba uyenzile.', 'XHO22'),
(23, 39, 10, 'Bususe kum ubugwenxa bakho; Ndinqamkile ngokubetha kwesandla sakho.', 'XHO22'),
(23, 39, 11, 'Wakumthethisa umntu ngenxa yobugwenxa, Nobuhle bakhe buphela njengengxokolo; Eneneni, wonke umntu akanaxabiso. Sela.', 'XHO22'),
(23, 39, 12, 'Yehova, wubekele indlebe umthandazo wam, ukubekele indlebe ukutarhuzisa kwam; Musa ukuthi cwaka kwiinyembezi zam; ngokuba ndingumphambukeli kuwe, ndilundwendwe njengooyihlo bonke.', 'XHO22'),
(23, 39, 13, 'Ndenzele inceba, ndihlaziyeke amandla am, ndingekamki apha, ndingabikho.', 'XHO22'),
(23, 40, 1, 'Ndithembele kuYehova; Wathoba kum, wakuva ukuzibika kwam.', 'XHO22'),
(23, 40, 2, 'Ubuye wandirhola emhadini woyikekayo, eludakeni, Wazimisa eliweni iinyawo zam, wazimisa iindlela zam.', 'XHO22'),
(23, 40, 3, 'Wabeka emlonyeni wam ingoma entsha, uzuko loThixo wethu; abaninzi baya kukubona oko, boyike, bakholose ngoYehova.', 'XHO22'),
(23, 40, 4, 'Hayi, uyolo lomntu okholose ngoYehova, ongahloneliyo abanekratshi, ongaguqukiyo ebuxokini.', 'XHO22'),
(23, 40, 5, 'Yehova, Thixo wam, mininzi imisebenzi yakho ebalulekileyo, oyenzileyo, neengcinga zakho kuthi; akuyi kubalelwa kuwe; ukubala.', 'XHO22'),
(23, 40, 6, 'Umbingelelo nomnikelo wokudla, akuwuthandanga; uzivulile iindlebe zam; Idini elinyukayo, nedini lesono, anilithandanga.', 'XHO22'),
(23, 40, 7, 'Ndathi, Yabona, ndiyeza; encwadini kubhalwe eyam;', 'XHO22'),
(23, 40, 8, 'Ndiyakuvuyela ukwenza ukuthanda kwakho, Thixo wam; nomyalelo wakho usentliziyweni yam.', 'XHO22'),
(23, 40, 9, 'Ndithethe ubulungisa ebandleni elikhulu; Yabona, andiwuvala umlomo wam, Yehova, uyazi wena.', 'XHO22'),
(23, 40, 10, 'Ubulungisa bakho andibugqumanga phakathi kwentliziyo yam; Ndikwazisile ukuthembeka kwakho nosindiso lwakho; Andizange ndikufihlele inceba yakho nenyaniso yakho ebandleni elikhulu.', 'XHO22'),
(23, 40, 11, 'Yehova, musa ukukuvimba kum; Inceba yakho nokuthembeka kwakho kundigcine ngamaxesha onke.', 'XHO22'),
(23, 40, 12, 'Ngenxa yokuba ububi obungenakulinganiswa bundijikele; Buninzi kuneenwele zentloko yam; ke ngoko, intliziyo yam iyandilangazelela.', 'XHO22'),
(23, 40, 13, 'Ngamana uYehova wandisindisa; Nkosi, khawuleza undincede.', 'XHO22'),
(23, 40, 14, 'Mabadane kunye, babe neentloni abazungula umphefumlo wam ukuze bawutshabalalise; Mababuye umva, babe neentloni abandenze into embi.', 'XHO22'),
(23, 40, 15, 'Makabe nomsindo kubo ngenxa yehlazo labo abathi kum: “Kulungile ke!', 'XHO22'),
(23, 40, 16, 'Mababe nemihlali bavuye ngawe bonke abakufunayo; mabahlale bathethe abaluthandayo usindiso lwakho, besithi, Makabe mkhulu uYehova!', 'XHO22'),
(23, 40, 17, 'Ke mna ndilusizana, ndilihlwempu; Noko ke, uYehova undikhumbule: Umncedi wam noMsindisi wam nguwe; Thixo wam, musa ukulibala.', 'XHO22'),
(23, 41, 1, 'Kumvumi, ingoma kaDavide.', 'XHO22'),
(23, 41, 2, 'uYehova uya kumgcina, amnike ubomi; Wosikelelwa yena ehlabathini, ungamnikeli ekuthandeni kweentshaba zakhe.', 'XHO22'),
(23, 41, 3, 'uYehova uya kumomeleza ezingqengqelweni zakhe zobubi;', 'XHO22'),
(23, 41, 4, 'Ndathi ke mna, Yehova, ndibabale; Philisa umphefumlo wam; kuba ndonile kuwe.', 'XHO22'),
(23, 41, 5, 'Iintshaba zam zithetha kakubi ngam, zisithi, Uya kufa nini na, lidake igama lakhe?', 'XHO22'),
(23, 41, 6, 'Xa eze kundibona, uthetha ilize; uthi, ukuba uthe waphuma waya kwelasemzini.', 'XHO22'),
(23, 41, 7, 'Bathetha ngam bonke abandithiyayo; Bacinga ububi ngam.', 'XHO22'),
(23, 41, 8, 'Bathi, unamathele kukufa okubi; ngoku exokile, akasayi kuvuka.', 'XHO22'),
(23, 41, 9, 'Ewe, nomhlobo wam ebendikade ndimazi, ebendikholose ngaye, obesidla isonka sam, undiphakamisele isithende sakhe.', 'XHO22'),
(23, 41, 10, 'Kodwa wena, Yehova, yiba nenceba kum, ndikhokele, ndibuyele kubo.', 'XHO22'),
(23, 41, 11, 'Ndiyazi ngale nto ukuba uyandithanda, Ngokuba alundoyisi utshaba lwam.', 'XHO22'),
(23, 41, 12, 'Ke mna uya kundixhasa ngokuthembeka kwam, Undimisa phambi kwakho ngonaphakade.', 'XHO22'),
(23, 41, 13, 'Makabongwe uYehova, uThixo kaSirayeli, kususela kwaphakade kude kuse ephakadeni. Amen, kwaye Amen.', 'XHO22'),
(23, 42, 1, 'Kumvumi: yoonyana bakaKora;', 'XHO22'),
(23, 42, 2, 'Umphefumlo wam unxanela uThixo, uThixo ophilileyo;', 'XHO22'),
(23, 42, 3, 'Iinyembezi zam zaba kukudla kwam imini nobusuku, Bakuthi kum imihla yonke, Uphi na uThixo wakho?', 'XHO22'),
(23, 42, 4, 'Ndathi ndakuziqonda ezo zinto, ndiwuthulule umoya wam ngaphakathi kwam; ngenxa yokuba ndaya kukubona endlwini kaThixo, ndinezwi lovuyo nenguquko, nesihlwele esibhiyozayo.', 'XHO22'),
(23, 42, 5, 'Kungani na ukuba usibekeke, mphefumlo wam? kanti ukhathazekile ngam? Kholosa ngoThixo, ngokuba ndisaya kubuya ndibulele kuye, ngenxa yoncedo lobuso bakhe.', 'XHO22'),
(23, 42, 6, 'Thixo wam, umoya wam udandathekile phakathi kwam; ngako oko, ndiya kukukhumbula ezweni laseYordan, nasezintabeni zeHermon, endulini yeMisar.', 'XHO22'),
(23, 42, 7, 'Amanzi anzongonzongo abiza imithombo ngesandi semilambo yakho; Adlule phezu kwam onke amaza akho alwayo namaza alwayo.', 'XHO22'),
(23, 42, 8, 'Emini uYehova uya kuyiwisela umthetho inceba yakhe, Nasebusuku ukuvuma ingoma yakhe kunam, Nomthandazo wam kuThixo wobomi bam.', 'XHO22'),
(23, 42, 9, 'Ndithi ke kuThixo oyingxondorha yam, Yintoni na le nto undenzé yona? Ndiya kuzila na ngokuxina kotshaba?', 'XHO22'),
(23, 42, 10, 'Njengomkhonto emathanjeni am, zindingcikiva iintshaba zam; Bathi kum imihla yonke, Uphi na uThixo wakho?', 'XHO22'),
(23, 42, 11, 'Kungani na ukuba usibekeke, mphefumlo wam? kanti ukhathazekile ngam? Kholosa ngoThixo, ngokuba ndisaya kubuya ndibulele kuye, olusindiso lobuso bam, noThixo wam.', 'XHO22'),
(23, 43, 1, 'Lithethe ityala lam, Thixo, ngenxa yabantu abanobubi; Ndihlangule endodeni ekhohlisayo, enobutshinga.', 'XHO22'),
(23, 43, 2, 'Ngokuba unguThixo oyinqaba yam; undishiyelani? Ndiya kuzila na ngokuxina kotshaba?', 'XHO22'),
(23, 43, 3, 'Thumela ukukhanya kwakho nenyaniso yakho; mabandikhaphe; bazise entabeni yakho engcwele nasezintenteni zakho.', 'XHO22'),
(23, 43, 4, 'Ngoko ke ndiya kusondela esibingelelweni sikaThixo, kuThixo wovuyo lwam olukhulu; ewe, ndiya kubulela kuwe emnqubeni, Thixo wam.', 'XHO22'),
(23, 43, 5, 'Kungani na ukuba usibekeke, mphefumlo wam? kanti ukhathazekile ngam? Kholosa ngoThixo, ngokuba ndisaya kubuya ndibulele kuye, olusindiso lobuso bam, noThixo wam.', 'XHO22'),
(23, 44, 1, 'Kumvumi woonyana bakaKora, uMasgile. Thixo, sivile ngeendlebe zethu, ukuba oobawo basibalisela ngomsebenzi owawenzayo ngemihla yabo, kwamandulo.', 'XHO22'),
(23, 44, 2, 'Oko wazigqogqayo iintlanga ngesandla sakho, wabatyala bona; owabacinezelayo, wabagxotha abantu.', 'XHO22'),
(23, 44, 3, 'Kuba abazange balidle ilifa ilizwe ngekrele labo, nesandla sabo asibasindisanga, kodwa isandla sakho sasekunene nesandla sakho nokukhanya kobuso bakho, ngenxa yokuba wawunenceba kubo.', 'XHO22'),
(23, 44, 4, 'Nkosi yam, Thixo, misela uYakobi usindiso.', 'XHO22'),
(23, 44, 5, 'Siya kuzicinezela ngawe iintshaba zethu; egameni lakho siya kubanyathela abasukela phezulu kuthi.', 'XHO22'),
(23, 44, 6, 'Ngokuba andikholose ngesaphetha sam; Andisindiswanga nekrele lam.', 'XHO22'),
(23, 44, 7, 'Kodwa wena wasihlangula kwiintshaba zethu nakwabo bazihlazisayo iintshaba zethu.', 'XHO22'),
(23, 44, 8, 'Siya kubulela kuThixo yonke imihla, sidumise igama lakho ngonaphakade. Sela.', 'XHO22'),
(23, 44, 9, 'Ke usishiyile, wasihlazisa; kwaye ungayishiyi imikhosi yethu.', 'XHO22'),
(23, 44, 10, 'Uyatyeka ezintshabeni zethu, Abasithiyileyo baphange.', 'XHO22'),
(23, 44, 11, 'Wasinika njengeegusha ezidliwayo; Wasichithachitha ezintlangeni.', 'XHO22'),
(23, 44, 12, 'Wathengisa ngabantu bakho, akwazuza, Akwalinyusa ixabiso labo.', 'XHO22'),
(23, 44, 13, 'Usenze into elungcikivo kubamelwane bethu, Isigculelo kwabasingqongileyo.', 'XHO22'),
(23, 44, 14, 'Usenze intlekisa phakathi kweentlanga, sahlunguzela iintloko zethu phakathi kweentlanga.', 'XHO22'),
(23, 44, 15, 'Ihlazo lam liphambi kwam ngamaxesha onke, ukudana kobuso bam kugutyungelwe;', 'XHO22'),
(23, 44, 16, 'Ngenxa yelizwi lomnyelisi nomhlebi; ngenxa yotshaba nomphindezeli.', 'XHO22'),
(23, 44, 17, 'Konke oko kwasihlela; kanti asikulibalanga, asenzanga buxoki emnqophisweni wakho.', 'XHO22'),
(23, 44, 18, 'Ayiphambukanga intliziyo yethu, Ukunyathela kwethu akuthanga gu bucala emendweni wakho;', 'XHO22'),
(23, 44, 19, 'Nangona wasityumza kwilizwe leempungutye Wasigubungela ngethunzi lokufa.', 'XHO22'),
(23, 44, 20, 'Ukuba sililibele igama loThixo wethu, Sazolulela kuthixo wolunye uhlanga izandla zethu;', 'XHO22'),
(23, 44, 21, 'Ngaba uThixo akayi kuyicikida na? ngokuba uyazazi izinto ezifihlakeleyo zentliziyo.', 'XHO22'),
(23, 44, 22, 'Ewe, ngenxa yakho sibulawa imini yonke; yiyo loo nto sesifana neegusha eziya kuxhelwa nje.', 'XHO22'),
(23, 44, 23, 'Vuka, ulele ntoni na, Nkosi? suk’ ume, ungasishiyi ngonaphakade.', 'XHO22'),
(23, 44, 24, 'Ubusitheliselani na ubuso bakho, Ukulibale ukubandezeleka kwethu, nokubandezeleka kwethu?', 'XHO22'),
(23, 44, 25, 'Ngokuba umphefumlo wethu ubotshelelwe eluthulini, Izisu zethu zinamathele emhlabeni.', 'XHO22'),
(23, 44, 26, 'Yima, usincede, usihlangule ngenxa yenceba yakho.', 'XHO22'),
(23, 45, 1, 'Kumvumi kaSosannim, eyoonyana bakaKora; ingoma yothando lukaMasigiliya. Intliziyo yam ilungile; umbhali olungisiweyo.', 'XHO22'),
(23, 45, 2, 'Umhle yena ngaphezu koonyana babantu; Kuphalala inceba emlonyeni wakho; ke ngoko, uThixo akusikelele ngonaphakade.', 'XHO22'),
(23, 45, 3, 'Bhinqa ikrele lakho esinqeni sakho, Gorha, ngozuko nobukhulu bakho.', 'XHO22'),
(23, 45, 4, 'Ukhwele ebukhosini bakho, ngenxa yenyaniso, nobumsulwa, nobulungisa; esokunene sakho siya kukuyala izinto ezoyikekayo.', 'XHO22'),
(23, 45, 5, 'Iintolo zakho zitsolile entliziyweni yeentshaba zokumkani; ngazo baya kuwa abantu phantsi kwakho.', 'XHO22'),
(23, 45, 6, 'Itrone yakho, Thixo, ingunaphakade kanaphakade; Yintonga yobukumkani bakho yokwenene.', 'XHO22'),
(23, 45, 7, 'Uthanda ubulungisa, uthiye okungendawo; Ngenxa yoko, uThixo, uThixo wakho, ukuthambise Ngeoli yolonwabo ngaphezu kwabalingane bakho.', 'XHO22'),
(23, 45, 8, 'Zinevumba lemore nekasiya neengubo zakho zonke, Emabhotweni ophondo lwendlovu, ezikunanzileyo.', 'XHO22'),
(23, 45, 9, 'Iintombi zokumkani zibe phakathi kwabafazi bakho abazukileyo; Ukumkanikazi umi ngasekunene kwakho, Enxibe igolide yaseOfire.', 'XHO22'),
(23, 45, 10, 'Yiva, ntombi, uve, uthobe indlebe yakho; ululibale uhlanga lwakho nendlu kayihlo;', 'XHO22'),
(23, 45, 11, 'uYehova wobunqwenela kunene ubunzwakazi bakho, ngokuba eyiNkosi yakho; niqubude kuye.', 'XHO22'),
(23, 45, 12, 'Intombi yaseTire iya kuba khona nesipho; nezityebi phakathi kweentlanga ziya kucela kuwe inceba.', 'XHO22'),
(23, 45, 13, 'Intle intombi yokumkani ngaphakathi, izambatho zayo ziyigolide engcwengiweyo.', 'XHO22'),
(23, 45, 14, 'Wosiwa kukumkani ambethe iingubo; Iintombi, amadlelane akhe amlandelayo, ziya kusiwa kuwe.', 'XHO22'),
(23, 45, 15, 'Baya kuziswa benovuyo novuyo; baya kungena endlwini yokumkani.', 'XHO22'),
(23, 45, 16, 'Endaweni yooyihlo kuya kubakho abantwana bakho, oya kubamisa babe ngabathetheli ehlabathini lonke.', 'XHO22'),
(23, 45, 17, 'Ndiya kukhunjulwa ngegama lakho kwizizukulwana ngezizukulwana; ngenxa yoko ziya kukubonga iintlanga ngonaphakade kanaphakade.', 'XHO22'),
(23, 46, 1, 'Kumvumi woonyana bakaKora: Ingoma kaAlamoti. uThixo ulihlathi, uligwiba kuthi;', 'XHO22'),
(23, 46, 2, 'Ke ngoko, asisayi koyika, nokuba umhlaba uyanyikima, kwanokuba iintaba zishenxisiwe embindini wolwandle;', 'XHO22'),
(23, 46, 3, 'Ayagquma anyikima amanzi, Iintaba zizamazama ngenxa yokugquma kwawo. Sela.', 'XHO22'),
(23, 46, 4, 'Kukho umlambo, ophuma kuwo umzi kaThixo, oyingcwele yeminquba yOsenyangweni.', 'XHO22'),
(23, 46, 5, 'uThixo uphakathi kwawo; akayi kuxengaxenga: uThixo uya kumnceda, nakwangaphambili.', 'XHO22'),
(23, 46, 6, 'Zashukuma iintlanga, noorhulumente bashukunyiswa; Waliva ilizwi lakhe, lanyibilika ihlabathi.', 'XHO22'),
(23, 46, 7, 'uYehova wemikhosi unathi; Ingxonde yethu nguThixo kaYakobi. Sela.', 'XHO22'),
(23, 46, 8, 'Yizani niyibone imisebenzi kaYehova, intshabalalo ayenzileyo ehlabathini.', 'XHO22'),
(23, 46, 9, 'Ulophelisa iimfazwe kude kuse eziphelweni zehlabathi; waphula isaphetha, waphula umkhonto; inqwelo yokulwa uyitshise emlilweni.', 'XHO22'),
(23, 46, 10, 'Yekani, nazi ukuba ndinguThixo mna;', 'XHO22'),
(23, 46, 11, 'uYehova wemikhosi unathi; Ingxonde yethu nguThixo kaYakobi. Sela.', 'XHO22'),
(23, 47, 1, 'Kumvumi, ingoma yoonyana bakaKora bhomboloza kuThixo ngezwi loloyiso.', 'XHO22'),
(23, 47, 2, 'Ngenxa yokuba uYehova, uMthetheleli owongamileyo uyoyikeka; UnguKumkani omkhulu wehlabathi lonke.', 'XHO22'),
(23, 47, 3, 'Uzinyathela phantsi kwethu iintlanga, nezizwe phantsi kweenyawo zethu;', 'XHO22'),
(23, 47, 4, 'Uya kulingcwalisa ilifa lethu, Uzuko lukaYakobi awamthandayo. Sela.', 'XHO22'),
(23, 47, 5, 'uThixo uyenyuka eduma, uYehova enesandi sesigodlo.', 'XHO22'),
(23, 47, 6, 'Vumani nibonge uThixo, nibonge uThixo; vumani uhadi lweNkosi yethu, vumani iingoma.', 'XHO22'),
(23, 47, 7, 'Ngokuba uThixo yiNkosi yehlabathi lonke; ukuvuma iindumiso ngengqondo.', 'XHO22'),
(23, 47, 8, 'uThixo ulawula iintlanga, Ulohlala etroneni yakhe engcwele.', 'XHO22'),
(23, 47, 9, 'Bahlanganisene ookumkani bezizwe, ewe, uhlanga loThixo ka-Abraham, ngenxa yokuba iingweletshetshe zehlabathi zezikaThixo; uphakame kakhulu.', 'XHO22'),
(23, 48, 1, '(Ingoma neendumiso zoonyana bakaKora.) uYehova mkhulu, ufanele ukudunyiswa kwisixeko soThixo wethu, entabeni yakhe engcwele.', 'XHO22'),
(23, 48, 2, 'Ilungile imeko, yimihlali yelimiweyo liphela, Intaba yaseZiyon yasentla, Umzi weNkosi enkulu.', 'XHO22'),
(23, 48, 3, 'uThixo ezingxandeni zakhe waziwa ukuba yingxonde.', 'XHO22'),
(23, 48, 4, 'Kuba nabo ookumkani babendawonye, bawela kunye;', 'XHO22'),
(23, 48, 5, 'Bakubona ke oko, bakhwankqiswa; bayacaphuka, bakhawuleze.', 'XHO22'),
(23, 48, 6, 'Bangenwa luloyiko khona nentlungu enjengeyomfazi ozalayo.', 'XHO22'),
(23, 48, 7, 'Uzaphula ngomoya wasempumalanga iinqanawa zaseTarshishe.', 'XHO22'),
(23, 48, 8, 'Njengoko sikuvileyo, sibone kunjalo Emzini kaYehova wemikhosi, emzini woThixo wethu: uThixo uya kuwuzinzisa ngonaphakade. Sela.', 'XHO22'),
(23, 48, 9, 'Khumbula iinceba zakho, Thixo, etempileni yakho;', 'XHO22'),
(23, 48, 10, 'Egameni lakho, Thixo, Injalo indumiso yakho, Yesa eziphelweni zehlabathi; Isandla sakho sokunene sizele bubulungisa.', 'XHO22'),
(23, 48, 11, 'Mayivuye intaba yaseZiyon, Zivuye iintombi zakwaYuda ngemigwebo yakho;', 'XHO22'),
(23, 48, 12, 'Yijikelezeni iZiyon, niyijikeleze; yithiya iinqaba zakhe.', 'XHO22'),
(23, 48, 13, 'Khangela iinqaba zakhe, zigqale iingxande zakhe ezinde; ukuze ube nokuxela kwisizukulwana esilandelayo.', 'XHO22'),
(23, 48, 14, 'Kuba lo Thixo nguThixo wethu ngonaphakade kanaphakade: Uya kusikhokelela ekufeni.', 'XHO22'),
(23, 49, 1, 'Kumvumi, ingoma yoonyana bakaKora yivani, nonke bemi belimiweyo;', 'XHO22'),
(23, 49, 2, 'Abaphantsi nabaphezulu, abazizityebi nabangamahlwempu kunye.', 'XHO22'),
(23, 49, 3, 'Umlomo wam uya kuthetha ubulumko; kwaye iingcinga zentliziyo yam ziya kukuqonda.', 'XHO22'),
(23, 49, 4, 'Ndiya kuyinqumla indlebe yam emzekelisweni; Ndiya kulivulela entsimbini ilizwi lam elimnyama.', 'XHO22'),
(23, 49, 5, 'Ndoyikelani na emihleni yobubi, Xa ubugwenxa babandibambisayo bujikelezile?', 'XHO22'),
(23, 49, 6, 'Abo bakholose ngobutyebi babo, nozuko lwabo ngobuninzi bobutyebi;', 'XHO22'),
(23, 49, 7, 'Akukho namnye unako ukumkhulula ngentlawulelo umzalwana wakhe, angamnikeli uThixo intlawulelo ngaye.', 'XHO22'),
(23, 49, 8, '(Ngokuba usindiso lwemiphefumlo yabo lubalulekile, luhlala ngonaphakade.)', 'XHO22'),
(23, 49, 9, 'ukuze ahlale ngonaphakade, angayiboni intshabalalo.', 'XHO22'),
(23, 49, 10, 'Kuba uyabona ukuba izilumko ziyafa, nesidenge nomgxeki bayatshabalala baze bayishiyele abanye indyebo yabo.', 'XHO22'),
(23, 49, 11, 'Iingcinga zabo ezingaphakathi zezokuba izindlu zabo zihlala kude kuse ephakadeni, neendawo zabo zokuhlala ukusuka kwesinye isizukulwana kuye kwesinye; amazwe azo bawabiza ngamagama.', 'XHO22'),
(23, 49, 12, 'Ke yena obekekileyo akahlali; nifana nezilwanyana ezitshabalalayo.', 'XHO22'),
(23, 49, 13, 'Yiyo le indlela yabo, nokumatha kwabo, nembewu yabo ivumelana namazwi abo. Sela.', 'XHO22'),
(23, 49, 14, 'Babuthe ngokweegusha emangcwabeni; ukufa kuya kubondla; yaye amalungisa aya kubalawula ngomso; nobuhle babo buya kutshatyalaliswa kwelabafileyo, bungabikho ebudodeni babo.', 'XHO22'),
(23, 49, 15, 'Kodwa uThixo uya kuwuhlangula umphefumlo wam emandleni eHadesi, ngenxa yokuba uya kundamkela. Sela.', 'XHO22'),
(23, 49, 16, 'Musani ukoyika ukuba umntu uthe waba sisityebi, yande indlu yakhe;', 'XHO22'),
(23, 49, 17, 'Kuba xa ithe yafa, ayisayi kuthabatha nto; abuyi kuhla emva kwakhe uzuko lwakhe.', 'XHO22'),
(23, 49, 18, 'Nangona wawusikelela umphefumlo wakhe ngoxa wayesaphila, yaye abantu baya kukudumisa xa uthe wazenzela okulungileyo.', 'XHO22'),
(23, 49, 19, 'Uya kuya esizweni sooyise; abayi kukubona ukukhanya kwemini.', 'XHO22'),
(23, 49, 20, 'Umntu obekekileyo, ongaqondiyo, ufana nezilwanyana ezitshabalalayo.', 'XHO22'),
(23, 50, 1, '(Iindumiso ka-Asafu.) Uthethile uYehova, uThixo oligorha, ebiza elimiweyo ethabathela empumalanga ese entshonalanga.', 'XHO22'),
(23, 50, 2, 'eZIyon, ubuhle obugqibeleleyo, uThixo ubengezela.', 'XHO22'),
(23, 50, 3, 'uThixo wethu uyeza, akayi kuthi tu; umlilo uya kuvutha kufuphi naso, uvutha macala onke.', 'XHO22'),
(23, 50, 4, 'Uya kulibiza izulu phezulu, nehlabathi, ukuba ligwebe phakathi kwabantu bakhe.', 'XHO22'),
(23, 50, 5, 'Bahlanganiseleni kum abam benceba; owanqophisana nam ngamadini.', 'XHO22'),
(23, 50, 6, 'Izulu lixela ubulungisa bakhe, Ngokuba uThixo ngumgwebi. Sela.', 'XHO22'),
(23, 50, 7, 'Phulaphulani, bantu bam, ndithethe; Sirayeli, ndiyangqina ngawe, ndithi, NdinguThixo, uThixo wakho.', 'XHO22'),
(23, 50, 8, 'Andiyi kunikhalimela ngenxa yeminikelo yenu okanye iminikelo yenu etshiswayo ebiphambi kwam ngamaxesha onke.', 'XHO22'),
(23, 50, 9, 'Andiyi kuthabatha nanye inkunzi yenkomo endlwini yakho, kwanenkunzi yebhokhwe esibayeni sakho.', 'XHO22'),
(23, 50, 10, 'Ngokuba zezam zonke iinyamakazi zehlathi, Neenkomo ezisezintabeni eziliwaka.', 'XHO22'),
(23, 50, 11, 'Zonke iintaka zasezintabeni ndiyazazi, namarhamncwa asendle ngawam.', 'XHO22'),
(23, 50, 12, 'Ukuba bendilambile, ndibe ndingayi kukuxelela; ngokuba lelam elimiweyo nenzaliseko yalo.', 'XHO22'),
(23, 50, 13, 'Ndidle inyama yeenkunzi eziziimbalasane na? Ndisele igazi leenkunzi zeebhokhwe na?', 'XHO22'),
(23, 50, 14, 'Bulelani kuThixo; uze uzalisekise imbopheleleko yakho kOyena Uphakamileyo.', 'XHO22'),
(23, 50, 15, 'Undibize ngemini yembandezelo, Ndiya kukusindisa, undizukise.', 'XHO22'),
(23, 50, 16, 'Ke kongendawo uthi uThixo, Wothini na ukuyixela intetho yam, Uwuthabathe ngomlomo wakho umnqophiso wam?', 'XHO22'),
(23, 50, 17, 'ngenxa yokuba uluthiyile uqeqesho, wawaphosa emva kwakho amazwi am.', 'XHO22'),
(23, 50, 18, 'Xa ubona isela, uvumelana nalo, ukrexeza;', 'XHO22'),
(23, 50, 19, 'Umlomo wakho uwuyekela ebubini, Nolwimi lwakho lufunga inkohliso;', 'XHO22'),
(23, 50, 20, 'Uthi uhlale uthethe ngomzalwana wakho; uyanyelisa unyana kanyoko.', 'XHO22'),
(23, 50, 21, 'Uzenzile ezo nto, ndathi tu; Ubusithi ndifana nawe kodwa ndikukhalimele, ndikuqeqeshe phambi kwakho.', 'XHO22'),
(23, 50, 22, 'Khanive, nina bamlibalayo uThixo, ukuze ndinganiqwengi, kungabikho unihlangulayo.', 'XHO22'),
(23, 50, 23, 'Lowo uzisa uzuko uyandizukisa; lowo uyikhokelayo indlela yakhe ndiya kumazisa usindiso lukaThixo.', 'XHO22'),
(23, 51, 1, 'Eyokuvuma ingoma kaDavide, ekuzeni kukaNatan umprofeti kuye, wangena uBhatshebha ngobuninzi benceba yakho, ndikhulule elukreqweni lwam.', 'XHO22'),
(23, 51, 2, 'Ndihlambulule ebugwenxeni bam, Ndihlambulule esonweni sam.', 'XHO22'),
(23, 51, 3, 'Ngokuba ndiyalwazi mna ukreqo lwam, Nesono sam siphambi kwam ngamaxesha onke.', 'XHO22'),
(23, 51, 4, 'Ndonile kuwe wedwa, ndenzé ububi emehlweni akho, ukuze ube lilungisa ekuthetheni kwakho, ube lilungisa ekugwebeni kwakho.', 'XHO22'),
(23, 51, 5, 'Yabona, mna ndidalelwe ebugwenxeni; ukanti umama undamkele esonweni.', 'XHO22'),
(23, 51, 6, 'Qaphela, unqwenela inyaniso ngaphakathi, kwaye uya kufunda ubulumko emfihlekweni.', 'XHO22'),
(23, 51, 7, 'Ndihlambulule ngehisope, ndihlambuluke; Ndihlambe, ndibe mhlophe kunekhephu;', 'XHO22'),
(23, 51, 8, 'Mandingabi naluvuyo novuyo; ukuze amathambo asibhozo abe nemihlali.', 'XHO22'),
(23, 51, 9, 'Sithelisa ubuso bakho ezonweni zam, uzicime zonke izono zam.', 'XHO22'),
(23, 51, 10, 'Dala, Thixo, intliziyo ehlambulukileyo; uhlaziye umoya onobulungisa phakathi kwam.', 'XHO22'),
(23, 51, 11, 'Musa ukundilahla ebusweni bakho; yaye musa ukuwususa kum umoya wakho oyingcwele.', 'XHO22'),
(23, 51, 12, 'Buyisela uvuyo losindiso lwakho; kwaye undixhase ngomoya wakho okhululekileyo.', 'XHO22'),
(23, 51, 13, 'Ndobafundisa abangendawo iindlela zakho; baya kubuyela kuwe aboni.', 'XHO22'),
(23, 51, 14, 'Thixo, Thixo ongumsindisi wam, ndihlangule etyaleni legazi, Logcoba ulwimi lwam ngobulungisa bakho.', 'XHO22'),
(23, 51, 15, 'Yehova, vula umlomo wam; Nomlomo wam uya kuxela indumiso yakho.', 'XHO22'),
(23, 51, 16, 'Ngokuba akufuni mbingelelo; kungenjalo bendiya kunikela ngayo. Anikholiswanga lidini elinyukayo.', 'XHO22'),
(23, 51, 17, 'Imibingelelo kaThixo ngumoya owaphukileyo; intliziyo eyaphukileyo, eyaphukileyo, Thixo, akuyi kuyidela.', 'XHO22'),
(23, 51, 18, 'Yenzele okulungileyo iZiyon; zakha iindonga zeYerusalem.', 'XHO22'),
(23, 51, 19, 'Kwandule ukuyonwabela imibingelelo yobulungisa, amadini anyukayo, namadini anyukayo; Uze wenze idini ngenkunzi yenkomo esibingelelweni sakho.', 'XHO22'),
(23, 52, 1, 'Uqhayiselani na ngobubi, wena gorha? Inceba kaThixo ikho imihla yonke.', 'XHO22'),
(23, 52, 2, 'Ulwimi lwakho luthetha kakubi; njengencakuba ebukhali esebenza ngobuqhetseba.', 'XHO22'),
(23, 52, 3, 'Uthanda okubi kunokulungileyo; kunye nokuxoka endaweni yokuthetha inyaniso. Sela.', 'XHO22'),
(23, 52, 4, 'Uthanda onke amazwi atshabalalisayo, Lulwimindini lunenkohliso.', 'XHO22'),
(23, 52, 5, 'uThixo uya kukutshabalalisa kuphele; Uya kukuthabatha akukhuphe endaweni ohlala kuyo, akutshabalalise ezweni labaphilileyo. Sela.', 'XHO22'),
(23, 52, 6, 'Ke wona amalungisa aya kubona, oyike, amhleke;', 'XHO22'),
(23, 52, 7, 'Yabona, ngumntu lowo uThixo angamnikiyo amandla akhe; ke wakholosa ngobuninzi bobutyebi bakhe, womelela ebubini bakhe.', 'XHO22'),
(23, 52, 8, 'Ke mna ndinjengomnquma oluhlaza endlwini kaThixo; Ndikholose ngenceba kaThixo ngamaxesha onke, kanaphakade.', 'XHO22'),
(23, 52, 9, 'Ndiya kubulela kuwe ngonaphakade, ngokuba ukwenzile oku; kwaye ndiya kulithemba igama lakho; ngokuba kuhle kwabangcwele bakho.', 'XHO22'),
(23, 53, 1, 'Eyokuqiqa kaMahalate, ethi: iMaskili, umbongo kaDavide. Sathi ke igeza entliziyweni yalo, Akukho Thixo. Bonakalisile, benze amasikizi; akukho wenza okulungileyo.', 'XHO22'),
(23, 53, 2, 'uThixo uqondele esemazulwini, Uqondele koonyana babantu, Ukubona ukuba ukho na onengqondo, Oquqela kuThixo.', 'XHO22'),
(23, 53, 3, 'Babuya umva elowo; bangcole ngokupheleleyo; Akukho wenza okulungileyo, eyedwa.', 'XHO22'),
(23, 53, 4, 'Abanakwazi na abenzi bokubi? abathi badle abantu bam xa badla isonka; babengababizeli kuThixo.', 'XHO22'),
(23, 53, 5, 'Oyika kakhulu apho kungekho loyiko, ngenxa yokuba uThixo wayewathi saa amathambo alowo wayemise inkampu nxamnye nani; ubadanisile, ngokuba uThixo ubadanisile.', 'XHO22'),
(23, 53, 6, 'Ukuba beluphuma eZiyon usindiso lukaSirayeli! Xa uThixo ebuyisela abantu bakhe ukuthinjwa, uya kuvuya uYakobi noSirayeli.', 'XHO22'),
(23, 54, 1, 'Kumvumi waseNeginoti, uMaskili, ingoma kaDavide, ekuzeni kweZifi kuSawule, zisithi, uDavide akasimemele na kuthi? amandla.', 'XHO22'),
(23, 54, 2, 'Thixo, yiva umthandazo wam; yibekeleni indlebe intetho yomlomo wam.', 'XHO22'),
(23, 54, 3, 'Kuba abasemzini basukele phezulu kum, nabacinezeli bafuna umphefumlo wam; abambekanga uThixo phambi kwabo. Sela.', 'XHO22'),
(23, 54, 4, 'Yabona, uThixo ngumncedi wam; uYehova unabo bawuxhasayo umphefumlo wam.', 'XHO22'),
(23, 54, 5, 'Uya kubuyekeza ngobubi iintshaba zam; batshabalele enyanisweni yakho.', 'XHO22'),
(23, 54, 6, 'Ndithanda ukuzinikela kuwe ngokuqhutywa yintliziyo; Ndiyakuthanda ukudumisa igama lakho, Yehova; ngokuba ilungile.', 'XHO22'),
(23, 54, 7, 'Ngokuba undihlangule kuzo zonke iimbandezelo, Neliso lam libone ikhwele labandibandezelayo.', 'XHO22'),
(23, 55, 1, 'Kumvumi waseNeginoti, uMaskile, umhobe kaDavide musa ukuwufihla umthandazo wam.', 'XHO22'),
(23, 55, 2, 'Ndibekeleni indlebe, nindiphulaphule; Ndiyalila ngokulila kwam, ndikhupha ilizwi;', 'XHO22'),
(23, 55, 3, 'Ngenxa yelizwi lotshaba, ngenxa yokuxinzelelwa phantsi ngabangendawo; Ngokuba bandiqengqela ubutshinga, Bandithiya ngomsindo.', 'XHO22'),
(23, 55, 4, 'Intliziyo yam iyazibhijabhija phakathi kwam, Izothuso zokufa zindiphethe.', 'XHO22'),
(23, 55, 5, 'Ndafikelwa lunkwantyo nokungcangcazela, ndafikelwa kukuxhalaba.', 'XHO22'),
(23, 55, 6, 'Ndathi, Ukuba bendinamaphiko njengehobe! ngokuba bendiya kubhabha ndiphumle.', 'XHO22'),
(23, 55, 7, 'Yabona, bendiya kubalekela kude, ndihlale entlango; Sela.', 'XHO22'),
(23, 55, 8, 'Bendiya kukhawuleza, ndisinde Emoyeni ovuthuzayo nasesiphangweni;', 'XHO22'),
(23, 55, 9, 'Yehova, batshabalele, banqumla ulwimi lwabo, ngokuba ndibone ubutshinga bokuxabana phakathi komzi.', 'XHO22'),
(23, 55, 10, 'Imini nobusuku bawujikeleza ngeenxa zonke iindonga zawo; kukho ububi nobuhlwempu kuyo.', 'XHO22'),
(23, 55, 11, 'Ubuvila bukuyo; ubuqhophololo nenkohliso azishiyi ezitratweni zayo.', 'XHO22'),
(23, 55, 12, 'Ngokuba asilutshaba olu lundithethisayo; ke bendizokwazi ukunxiba; ingabi ngothiyayo, lowo uzidlayo kum; kulapho ngendimfihle khona.', 'XHO22'),
(23, 55, 13, 'Kodwa nguwe, intanga yam, umhlobo wam, nomhlobo wam;', 'XHO22'),
(23, 55, 14, 'Samkela icebiso elihle kunye saza saya kunye endlwini kaThixo.', 'XHO22'),
(23, 55, 15, 'Makubafikelwe kukufa, Mabehle baye kwelabafileyo besaphilile; ngokuba ububi busemizini yabo naphakathi kwabo.', 'XHO22'),
(23, 55, 16, 'Mna ke, ndiya kubhenela kuThixo; yaye uYehova uya kundihlangula.', 'XHO22'),
(23, 55, 17, 'Ndiya kuthandaza ngokuvakalayo ebusuku nakusasa nasemini enkulu, yaye uya kuliva ilizwi lam.', 'XHO22'),
(23, 55, 18, 'Wawusindisa umphefumlo wam unoxolo, ekulweni kwabo nxamnye nam, ngokuba bebaninzi kum.', 'XHO22'),
(23, 55, 19, 'uThixo uya kuva, abacinezele, ohleli kumaxesha amandulo. Sela. Ngenxa yokuba abananguquko, ngoko abamoyiki uThixo.', 'XHO22'),
(23, 55, 20, 'Wazolulela izandla zakhe kwababenoxolo naye; Wawaphula umnqophiso wakhe.', 'XHO22'),
(23, 55, 21, 'Ubuthelezi amazwi omlomo wabo, Ke yimfazwe entliziyweni yakhe; Amazwi akhe ayekhanye ngaphezu kweoli, likrele elibukhali.', 'XHO22'),
(23, 55, 22, 'Umthwalo wakho wulahlele kuYehova, yaye yena wokuxhasa; Akayi kuliyekela ilungisa lishukunyiswe.', 'XHO22'),
(23, 55, 23, 'Wena ke, Thixo, uya kubahlisela emngxunyeni wesihogo; kodwa ndikholose ngawe.', 'XHO22'),
(23, 56, 1, 'Iculo likaYonatarokokim, uMikitam kaDavide, ekumthimbeni kwakhe amaFilisti eGati. Osilwa imini yonke uyanditshutshisa.', 'XHO22'),
(23, 56, 2, 'Iintshaba zam bezindidla yonke imini, Ngokuba baninzi abalwa nam, Wena usenyangweni.', 'XHO22'),
(23, 56, 3, 'Nokuba ndinkwantya kangakanani na, ndiya kukuthemba;', 'XHO22'),
(23, 56, 4, 'Ndiya kulidumisa ilizwi likaThixo, Ndikholose ngoThixo; Andiyi koyika ukuba inyama inokundenza ntoni na.', 'XHO22'),
(23, 56, 5, 'Imihla yonke bayawachitha amazwi am; zonke iingcinga zabo zindichasile.', 'XHO22'),
(23, 56, 6, 'Baqhubile, bayasithela, Babonisela ukunyathela kwam, Ngokokuba belinde umphefumlo wam.', 'XHO22'),
(23, 56, 7, 'Baya kusinda na ngobugqwetha? Bahlise ngomsindo, Thixo abantu;', 'XHO22'),
(23, 56, 8, 'Ukubhadula kwam unokuxela, Iinyembezi zam uziphalazela esityeni sakho; Ayikho encwadini yakho na?', 'XHO22'),
(23, 56, 9, 'Ekubizeni kwam kuwe, zobuya zibuye iintshaba zam; endikwaziyo; ngokuba uThixo unam.', 'XHO22'),
(23, 56, 10, 'Ndilidumisa ngoThixo ilizwi lakhe, Ndilidumisa ngoYehova ilizwi.', 'XHO22'),
(23, 56, 11, 'Ndikholose ngoThixo, ndingoyiki;', 'XHO22'),
(23, 56, 12, 'Thixo, isithembiso sakho siphezu kwam; ndiya kukubonga.', 'XHO22'),
(23, 56, 13, 'Ngokuba uwuhlangule ekufeni umphefumlo wam; Akuyi kuzikhulula na iinyawo zam ekukhubekeni, Ukuba ndihambe phambi koThixo ekukhanyeni kobomi?', 'XHO22'),
(23, 57, 1, 'Kumvumi, uAletaschithe, uMictam kaDavide, ekubalekeni kwakhe uSawule emqolombeni. Ndiya kusabela emaphikweni de ezi ntlekele ziphele.', 'XHO22'),
(23, 57, 2, 'Ndinqula uThixo Osenyangweni; kuThixo ondenzela yonke into.', 'XHO22'),
(23, 57, 3, 'Uya kuthuma esemazulwini, andisindise ehlazweni lalowo uthanda ukuginya. Sela. uThixo uya kuthuma inceba yakhe nenyaniso yakhe.', 'XHO22'),
(23, 57, 4, 'Umphefumlo wam usezingonyameni; ndiya kulala phantsi kwabatshileyo emlilweni, Koonyana babantu abamazinyo aziintshuntshe neentolo, Abalulwimi lulikrele elibukhali.', 'XHO22'),
(23, 57, 5, 'Ziphakamise ngaphezu kwezulu, Thixo; Uzuko lwakho malube phezu kwehlabathi lonke.', 'XHO22'),
(23, 57, 6, 'Bawuthiyele umgibe ukunyathela kwam; umoya wam ulahlekile; bemba umngxuma phambi kobuso bam, bawela kuwo. Sela.', 'XHO22'),
(23, 57, 7, 'Izimasekile intliziyo yam, Thixo, izimasekile intliziyo yam, Ndithanda ukucula nokudumisa.', 'XHO22'),
(23, 57, 8, 'Khawusuk’ ume, nkosi yam; vuka, luhadi nohadi; Ndiya kuvuka kusasa.', 'XHO22'),
(23, 57, 9, 'Ndiya kubulela kuwe, Yehova, phakathi kweentlanga; phakathi kwezizwe ndiyathanda ukucula kuwe.', 'XHO22'),
(23, 57, 10, 'Ngokuba inkulu yesa ezulwini, inceba yakho, yesa esibhakabhakeni ukuthembeka kwakho.', 'XHO22'),
(23, 57, 11, 'Ziphakamise ngaphezu kwezulu, Thixo; Ngamana uzuko lwaKho lube ngaphezu kwehlabathi lonke.', 'XHO22'),
(23, 58, 1, 'Kumvumi, Altaschithe, Miktame van Davida. Nigweba ngobulungisa na, nyana babantu?', 'XHO22'),
(23, 58, 2, 'Ewe, wenza ububi entliziyweni yakho; Ubulinganisa ububi bezandla zakho ehlabathini.', 'XHO22'),
(23, 58, 3, 'Abangendawo bahlukanisiwe kwasesizalweni; ezelwe, balahleka, bathethe ubuxoki.', 'XHO22'),
(23, 58, 4, 'Ubuhlungu babo bunjengobuhlungu benyoka;', 'XHO22'),
(23, 58, 5, 'abo bangaliphulaphuliyo ilizwi lezazi, ababanga nako ukumamela ngobu bulumko;', 'XHO22'),
(23, 58, 6, 'Thixo, uwaphule, Yehova, amazinyo abo emlonyeni wabo, Wawaphule amazinyo amakhulu eengonyama.', 'XHO22'),
(23, 58, 7, 'Mabanyibilike njengamanzi aqukuqelayo; Akusityeda isaphetha sakhe ekutoleni ngeentolo zakhe, mazibe ziingceba.', 'XHO22'),
(23, 58, 8, 'Ngamnye kubo makadlule njengodongwe olunyibilikayo, njengomfazi ozalayo, ukuze bangaliboni ilanga.', 'XHO22'),
(23, 58, 9, 'Zingekaveli iinkuni iimbiza zakho, uya kuzisusa njengesaqhwithi, besadla ubomi ngomsindo wakhe.', 'XHO22'),
(23, 58, 10, 'Abanoyolo abalungileyo, xa ebona impindezelo; iinyawo zakhe uya kuzihlamba egazini labangendawo.', 'XHO22'),
(23, 58, 11, 'Ngoko uthile uya kuthi: “Eneneni, kukho umvuzo welungisa; Inene, nguThixo ogwebayo ihlabathi.', 'XHO22'),
(23, 59, 1, 'EyokuQala kweemvumi, kaAltaskite, kaMikitam kaDavide, ekuthumeleni kukaSawule, ukuba bayigcine indlu kumbulali. Thixo wam, ndihlangule ezintshabeni zam;', 'XHO22'),
(23, 59, 2, 'Ndihlangule kwabenzi bobubi, Ndihlangule ebantwini bamagazi.', 'XHO22'),
(23, 59, 3, 'Kuba, yabonani, bawondla umphefumlo wam; kungekhona ngenxa yokunxaxha okanye isono sam, Yehova.', 'XHO22'),
(23, 59, 4, 'Bagidime balungisa, andinatyala; sukuma, undincede, ubone;', 'XHO22'),
(23, 59, 5, 'Wena Yehova, Thixo wemikhosi, Thixo kaSirayeli, khawusuk’ ume, uzivelele zonke iintlanga; uze ungamcongi nabani na ongendawo. Sela.', 'XHO22'),
(23, 59, 6, 'Babuya ngokuhlwa, beduma njengenja, bewujikeleza umzi.', 'XHO22'),
(23, 59, 7, 'Yabona, ziyaxokozela ngemilomo yazo; amakrele asemlonyeni wabo; kuba bathi, Kuva bani na?', 'XHO22'),
(23, 59, 8, 'Ke wena, Yehova, uya kubahleka; niya kuzigculela zonke iintlanga.', 'XHO22'),
(23, 59, 9, 'Ngenxa yamandla akhe ndithembele kuwe, Ngokuba uThixo ulihlathi lam.', 'XHO22'),
(23, 59, 10, 'uThixo wam uya kundikhawulela ngenceba yam; uThixo uya kundibonisa oko ndikunqwenelayo ezintshabeni zam.', 'XHO22'),
(23, 59, 11, 'Musa ukubabulala, ukuze bangalibaleki abantu bam; Bachithachithe ngamandla akho; Banike elabafileyo, Nkosi, ngweletshetshe yethu.', 'XHO22'),
(23, 59, 12, 'Ngenxa yesono somlomo wabo, nangenxa yelizwi lomlomo wabo, mabaqhayise ngekratshi labo, nangenxa yesiqalekiso nobuxoki;', 'XHO22'),
(23, 59, 13, 'Babhangise ngomsindo, batshabalalise, bangabikho; bazi ukuba uThixo ulawula noYakobi, wesa eziphelweni zehlabathi. Sela.', 'XHO22'),
(23, 59, 14, 'Mababuye ngokuhlwa; mababhomboloze okwenja, bahambe nomzi.', 'XHO22'),
(23, 59, 15, 'Mababhadule befuna ukudla, Babhadule ukuba abahluthanga.', 'XHO22'),
(23, 59, 16, 'Ke mna ndiya kuvuma ngamandla akho; ewe, ngomso ndiya kuvuma ngenceba yakho, Kuba ubuyingxonde kum, nehlathi lam ngemini yokubandezelwa kwam.', 'XHO22'),
(23, 59, 17, 'Uligwiba lam, ndikubethela uhadi, ungamandla am, ngokuba uThixo ulihlathi lam, unguThixo onenceba.', 'XHO22'),
(23, 60, 1, 'Kumvumi waseShushaneduti, uMictam kaDavide, ukuba afundise; Ekubambeni kwakhe nama-Aramna-Harayim nama-Aramesobha, ekubuyeni kukaYowabhi, wabulala ishumi elinamabini lamawaka amaEdom kwiNtlambo yeTyuwa. Thixo, usilahlile! Usichithachithe, unomsindo; Jikela kuthi kwakhona.', 'XHO22'),
(23, 60, 2, 'Ulinyikimisile ihlabathi; Uwaphule; Philisa ukwaphuka kwayo; kuba iyangcangcazela.', 'XHO22'),
(23, 60, 3, 'Ubaxelele abantu bakho izinto ezilukhuni, Usiseze iwayini emnandi;', 'XHO22'),
(23, 60, 4, 'Ubanike ibhanile abakoyikayo, Ukuba bayiphakamise ngenxa yenyaniso. Sela.', 'XHO22'),
(23, 60, 5, 'Ukuze lowo umthandayo asindiswe; Sindisa ngesandla sakho sokunene, undive.', 'XHO22'),
(23, 60, 6, 'uThixo uthethe engcwele; Ndiya kumemelela uShekem, ndiyilinganisele intili yaseSukoti.', 'XHO22'),
(23, 60, 7, 'Ngowam uGiliyadi, ngowam uManase; uEfrayim sisigwiba sentloko yam; uYuda umazi womyalelo wam;', 'XHO22'),
(23, 60, 8, 'uMowabhi sisitya sam; Ndiyiphosa kwaEdom imbadada yam; Filistiya, uloyiso lwam;', 'XHO22'),
(23, 60, 9, 'Ngubani na owondisa emzini onqatyisiweyo? ngubani na owondikhaphela kwaEdom?', 'XHO22'),
(23, 60, 10, 'Thixo, akufuni na oko sikulahlekeleyo? Akuphumanga na wena, Thixo, nemikhosi yethu?', 'XHO22'),
(23, 60, 11, 'Sincede ekubandezelekeni kwethu, ngokuba uncedo lomntu luyinto engento.', 'XHO22'),
(23, 60, 12, 'NgoThixo siya kwenza ngenkalipho, kuba yena uya kuzinyhasha iintshaba zethu.', 'XHO22'),
(23, 61, 1, 'Kumvumi kaNegina, Ingoma kaDavide wubazele indlebe umthandazo wam.', 'XHO22'),
(23, 61, 2, 'Ndiya kudanduluka kuwe ndiseziphelweni zehlabathi, ekukhwankqisweni kwentliziyo yam; ndise eweni eliphezu kwam.', 'XHO22'),
(23, 61, 3, 'Ngokuba ulihlathi lam, Uyinqaba ende eliqele phambi kotshaba.', 'XHO22'),
(23, 61, 4, 'Ndiya kuhlala ententeni yakho ngonaphakade; Ndizimela ngesithe lamaphiko akho. Sela.', 'XHO22'),
(23, 61, 5, 'Ngokuba wena, Thixo, usivile isibhambathiso sam; Ubanike ilifa abaloyikayo igama lakho.', 'XHO22'),
(23, 61, 6, 'Uya kuyolula imihla yobomi bokumkani, neminyaka yakhe njengezizukulwana ngezizukulwana;', 'XHO22'),
(23, 61, 7, 'Uya kuphila ngonaphakade phambi koThixo; Mlungisele inceba nenyaniso, amkhusele.', 'XHO22'),
(23, 61, 8, 'Ngoko ndiya kulibethela uhadi igama lakho ngonaphakade, Ndizizalise izibhambathiso zam yonke imihla.', 'XHO22'),
(23, 62, 1, 'EkaYedutun umhobe kaDavide. Ewe, umphefumlo wam ulindele kuThixo, usindiso lwam luvela kuye.', 'XHO22'),
(23, 62, 2, 'Nguye yedwa iliwa lam, umsindisi wam; ulihlathi lam; Andizushukuma kakhulu.', 'XHO22'),
(23, 62, 3, 'Kunini na niceba ububi emntwini? niya kubulawa nonke; njengodonga olugotywayo, nanjengothango oludilikayo.', 'XHO22'),
(23, 62, 4, 'Babhunga ke ngokumphosa phezulu; banandipha ubuxoki; basikelela ngomlomo, kanti embilinini bayatshabhisa. Sela.', 'XHO22'),
(23, 62, 5, 'Thozamela kuThixo yedwa, mphefumlo wam; ngokuba ithemba lam livela kuye.', 'XHO22'),
(23, 62, 6, 'Nguye yedwa iliwa lam, umsindisi wam; ulihlathi lam; Andiyi kushukuma.', 'XHO22'),
(23, 62, 7, 'KuThixo usindiso lwam nozuko lwam;', 'XHO22'),
(23, 62, 8, 'Kholosani ngaye ngamaxesha onke; Phalazani intliziyo yenu phambi kwakhe, zintlanga; Sela.', 'XHO22'),
(23, 62, 9, 'Okwenene, abantu abaphantsi bangamampunge, nabaphakamileyo bangamanga;', 'XHO22'),
(23, 62, 10, 'Musani ukukholosa ngokucudisa, ningalambathi nento ephangiweyo; yakwanda ubutyebi, musa ukuyibekela ntliziyo yona.', 'XHO22'),
(23, 62, 11, 'uThixo wathetha kwakanye; Ndaliva kabini; la mandla ngakaThixo.', 'XHO22'),
(23, 62, 12, 'Nenceba yeyakho, Nkosi; ngokuba uyababuyekeza elowo ngokomsebenzi wakhe.', 'XHO22'),
(23, 63, 1, 'Ingoma kaDavide, oko wayesentlango yakwaYuda. Thixo, unguThixo wam; Ndiya kuquqela kuwe kwakusasa; umphefumlo wam ukungxamele, inyama yam inxanelwe ezweni elingumqwebedu, elibharhileyo, elingenamanzi;', 'XHO22'),
(23, 63, 2, 'ukuba ndiwabone amandla akho nobuqaqawuli bakho, njengoko ndakubonayo usendaweni engcwele.', 'XHO22'),
(23, 63, 3, 'Ngokuba inceba yakho ilungile ngaphezu kobomi, Umlomo wam uya kukudumisa.', 'XHO22'),
(23, 63, 4, 'Ndiya kukubonga lo gama ndidla ubomi, Ndiphakamise izandla zam egameni lakho.', 'XHO22'),
(23, 63, 5, 'Umphefumlo wam uhluthi njengomongo namanqatha; nomlomo wam uya kukudumisa ngemilebe yovuyo.', 'XHO22'),
(23, 63, 6, 'Ndithi ndakukhumbula esililini sam, ndikukhumbule emlindweni wasebusuku.', 'XHO22'),
(23, 63, 7, 'Ngokuba uluncedo lwam, ngenxa yoko ndidlamkile esithunzini samaphiko akho.', 'XHO22'),
(23, 63, 8, 'Umphefumlo wam uyakulangazelela, isandla sakho sokunene siyandixhasa.', 'XHO22'),
(23, 63, 9, 'Kwaye abo bafuna ukutshabalalisa umphefumlo wam baya kuya kwiindawo ezisezantsi zomhlaba.', 'XHO22'),
(23, 63, 10, 'Baya kuwa likrele; siya kuba sisabelo seempungutye.', 'XHO22'),
(23, 63, 11, 'Ke yena ukumkani uya kuvuya ngoThixo; Othetha ubuxoki uya kuqhayisa; Ke wona umlomo wabaxokayo uya kuvinjwa.', 'XHO22'),
(23, 64, 1, 'Thixo, yiva izwi lam emthandazweni wam, londoloza ubomi bam, ndingaloyiki utshaba.', 'XHO22'),
(23, 64, 2, 'Ndisithelise egquguleni labangendawo; Ngokukreqa kwabenzi bobubi;', 'XHO22'),
(23, 64, 3, 'abalola ulwimi lwabo njengomkhonto, abalola izaphetha zabo, ukuze bazingele ngeentolo, amazwi akrakra;', 'XHO22'),
(23, 64, 4, 'Batole abagqibeleleyo ngasese; ziluse ngequbuliso, zingankwantyi.', 'XHO22'),
(23, 64, 5, 'Bazomelezela ububi; Athi: Ngubani oza kubabona?', 'XHO22'),
(23, 64, 6, 'Baphengulula ubutshinga; Baphengulula bechwayitile: Iingcamango neentliziyo zabo zinzulu.', 'XHO22'),
(23, 64, 7, 'Ke yena uThixo uya kubatola ngotolo; baya kwenzakala ngephanyazo.', 'XHO22'),
(23, 64, 8, 'Kwaye baya kuluyekela phezu kwabo ulwimi lwabo, basabe bonke abababonayo.', 'XHO22'),
(23, 64, 9, 'Basuka boyike bonke, bawuvakalise umsebenzi kaThixo; ngokuba baya kuyiqiqa imisebenzi yakhe;', 'XHO22'),
(23, 64, 10, 'Ilungisa liya kuvuya ngoYehova, likholose ngaye; baya kuqhayisa bonke abantliziyo zithe tye.', 'XHO22'),
(23, 65, 1, 'Kumvumi, iNgoma kaDavide. Thixo, yindumiso kuwe, Thixo, sifezeke kuwe isifungo.', 'XHO22'),
(23, 65, 2, 'Wena uwuvayo umthandazo, yonke inyama iya kuza kuwe.', 'XHO22'),
(23, 65, 3, 'Bundoyisile ubugwenxa; Cima ukreqo lwethu;', 'XHO22'),
(23, 65, 4, 'Hayi, uyolo lomntu omnyulileyo, wamvumela ukuba eze kuwe, ukuba ahlale ezintendelezweni zakho; Siya kwaneliswa kukulunga kwendlu yakho, kwetempile yakho engcwele.', 'XHO22'),
(23, 65, 5, 'Usiphendula ngezenzo ezoyikekayo zobulungisa, Thixo osisindisayo; elilithemba leziphelo zonke zehlabathi, nelaseziphelweni zolwandle;', 'XHO22'),
(23, 65, 6, 'Ulobopha iintaba ngamandla akhe; uzibophelele ngamandla ku:', 'XHO22'),
(23, 65, 7, 'ulodambisa isandi solwandle, isandi samaza alo, nokuxokozela kwabantu;', 'XHO22'),
(23, 65, 8, 'Nabahleli ekupheleni bayoyika imiqondiso yakho; Uyakonwabela ukuphuma ekuseni nangokuhlwa.', 'XHO22'),
(23, 65, 9, 'Uyawuvelela umhlaba, uwunyakamise; Uwutyebisa kakhulu ngomlambo kaThixo ozele ngamanzi; Ubalungisela ingqolowa ukuba ubalungiselele.', 'XHO22'),
(23, 65, 10, 'Uyawankcenkceshela amathambeka ayo, Uyawaqinisa amaqukuqela awo; Uwuthambisa ngemvula; Uyayisikelela ihlumelo layo.', 'XHO22'),
(23, 65, 11, 'Iwudumisa umnyaka ngesithsaba sokulunga kwakho; kwaye iindlela zakho zivuza amanqatha.', 'XHO22'),
(23, 65, 12, 'Ziwa emakriweni asemathafeni, Iinduli zigcobe ngeenxa zonke.', 'XHO22'),
(23, 65, 13, 'Amakriwa anxibe iinkomo; neentili zigutyungelwe yingqolowa; bamemelela ngovuyo, kananjalo bavuma ingoma.', 'XHO22'),
(23, 66, 1, 'Kukumkani weemvumi, Ingoma, nokuba yingoma;', 'XHO22'),
(23, 66, 2, 'Libetheleni uhadi igama lakhe, mdumiseni indumiso yakhe.', 'XHO22'),
(23, 66, 3, 'Yithini kuThixo, Hayi, ilishwa ngezenzo zakho! ngenxa yobukhulu bamandla akho ziya kuhanahanisa kuwe iintshaba zakho.', 'XHO22'),
(23, 66, 4, 'Lonke ihlabathi liya kukunqula, Bakubethele uhadi; baya kulibethela uhadi igama lakho. Sela.', 'XHO22'),
(23, 66, 5, 'Yizani, nibone imisebenzi kaThixo, Eyoyikekayo ngezenzo zakhe koonyana babantu.', 'XHO22'),
(23, 66, 6, 'Waluguqula ulwandle lwayinto eyomileyo, bawuwela umlambo ngeenyawo; apho savuya ngaye.', 'XHO22'),
(23, 66, 7, 'Ulolawula ngobugorha bakhe ngonaphakade; amehlo akhe akhangele ezintlangeni; Abawexuki abafanele babe nekratshi. Sela.', 'XHO22'),
(23, 66, 8, 'Bizani kuThixo wethu, zintlanga, livakalise ilizwi lakhe;', 'XHO22'),
(23, 66, 9, 'Ulodlisa ubomi bethu, Olungazivumeli lukhubekise unyawo lwethu.', 'XHO22'),
(23, 66, 10, 'Ngokuba usicikidile, Thixo; Usinyibilikisile njengokunyibilikiswa kwesilivere;', 'XHO22'),
(23, 66, 11, 'Usingenise emgibeni; Ubeke inkxwaleko emanqeni ethu.', 'XHO22'),
(23, 66, 12, 'Ukhwelise abantu entlokweni yethu; sicande emlilweni nasemanzini, nasuka nasisa endaweni etyebileyo;', 'XHO22'),
(23, 66, 13, 'Ndiya kungena endlwini yakho ndinamadini anyukayo; Ndiya kuzizalisa kuwe izibhambathiso zam;', 'XHO22'),
(23, 66, 14, 'Owuvulayo umlomo wam, Othethe umlomo wam ekubandezelweni kwam;', 'XHO22'),
(23, 66, 15, 'Ndiya kukwenzela amadini anyukayo eemvana ezityebileyo, nesiqhumiso seenkunzi zeegusha; Ndiya kubingelela iinkunzi zeenkomo neebhokhwe. Sela.', 'XHO22'),
(23, 66, 16, 'Yizani, nive, ndinixelele, nonke nina bamoyikayo uThixo, awenzele umphefumlo wam.', 'XHO22'),
(23, 66, 17, 'Ndadanduluka kuye ngomlomo wam, waza wazukiswa ngolwimi lwam.', 'XHO22'),
(23, 66, 18, 'Xa ndicinga ubutshinga entliziyweni yam, akandigwebi uYehova;', 'XHO22'),
(23, 66, 19, 'Hayi ke, uThixo undivile; waliva ilizwi lomthandazo wam.', 'XHO22'),
(23, 66, 20, 'Makabongwe uThixo, ongawuyekanga umthandazo wam, nenceba yakhe kum.', 'XHO22'),
(23, 67, 1, 'Kumbhexeshi-kwayara. Umhobe. Wanga uThixo angasisikelela; makabukhanyise ubuso bakhe phezu kwethu; Sela.', 'XHO22'),
(23, 67, 2, 'Ukuze yaziwe ehlabathini indlela yakho, Usindiso lwakho phakathi kweentlanga zonke.', 'XHO22'),
(23, 67, 3, 'Mazikubonge iintlanga, Thixo; mabakubonge bonke abantu.', 'XHO22'),
(23, 67, 4, 'Mazigcobe zigcobe iintlanga, ngokuba ùya kugweba phakathi kweentlanga ngobulungisa, Uzilawule ehlabathini. Sela.', 'XHO22'),
(23, 67, 5, 'Mazikubonge iintlanga, Thixo; mabakubonge bonke abantu.', 'XHO22'),
(23, 67, 6, 'Umhlaba uyivelisile indyebo yawo; Wosisikelela uThixo, uThixo wethu.', 'XHO22'),
(23, 67, 7, 'uThixo uya kusisikelela; zimoyike zonke iziphelo zehlabathi.', 'XHO22'),
(23, 68, 1, 'Kumongameli, ingoma kaDavide mababaleke kuye abamthiyayo.', 'XHO22'),
(23, 68, 2, 'Bagxothe njengokufutha komsi; batshabalala abangendawo phambi komlilo, njengekhasa phambi komlilo.', 'XHO22'),
(23, 68, 3, 'Ke wona amalungisa makavuye; mabagcobe phambi koThixo, ewe, mabagcobe ngokugqithileyo.', 'XHO22'),
(23, 68, 4, 'Vumani uzuko lukaThixo, nibethe uhadi igama lakhe; Mdumiseni uhleli emazulwini, egameni lakhe nguYehova, nigcobe phambi kobuso bakhe.', 'XHO22'),
(23, 68, 5, 'Uyise weenkedama, umgwebi wabahlolokazi, nguThixo kwisakhiwo sakhe esingcwele.', 'XHO22'),
(23, 68, 6, 'uThixo nguhlalisa endlwini ababebodwa, Ngukhuphela emakhamandeleni abakhonkxiwe; Ke bona ababandezeli bahleli emhlabeni owomileyo.', 'XHO22'),
(23, 68, 7, 'Thixo, ekubahambiseni kwakho abantu bakho, Ekucaleni kwakho enkangala; Cella:', 'XHO22'),
(23, 68, 8, 'Lanyikima ihlabathi, nezulu lawa phambi koThixo; iSinayi sagubha phambi koThixo, uThixo kaSirayeli.', 'XHO22'),
(23, 68, 9, 'Wena, Thixo, wehlisela izantyalantyala zemvula, Walizinzisa ilifa lakho ngexesha lembandezelo.', 'XHO22'),
(23, 68, 10, 'Lahlala khona ibandla lakho; Wena, Thixo, ubalungisele abaphulaphuli ngobubele bakho;', 'XHO22'),
(23, 68, 11, 'Wathi uYehova, Babaninzi ke abanikelayo.', 'XHO22'),
(23, 68, 12, 'Zasaba ngokukhawuleza iinkosi, nenkazana ehleli endlwini yawahlula amaxhoba.', 'XHO22'),
(23, 68, 13, 'Nokuba uthe walala phakathi kweembiza, wofana namaphiko evukuthu alekwe ngesilivere, Neentsiba zalo ezalekwe ngegolide emthubi.', 'XHO22'),
(23, 68, 14, 'Ekubadubadubeni kukaSomandla ookumkani kulo, Koba mhlophe njengekhephu leTsalimon.', 'XHO22'),
(23, 68, 15, 'Intaba kaThixo injengentaba yaseBhashan; Yintaba ephakamileyo njengentaba yaseBhashan.', 'XHO22'),
(23, 68, 16, 'Niyatsiba na, zintaba ziphakamileyo? yinduli apho athanda ukuhlala khona uThixo; ewe, uYehova uya kuhlala apho ngonaphakade.', 'XHO22'),
(23, 68, 17, 'Iinqwelo zokulwa zikaThixo zizigidi ezingamashumi amabini, ewe, amawaka ezithunywa zezulu; uYehova uphakathi kwabo, njengokuba eseSinayi engcweleni.', 'XHO22'),
(23, 68, 18, 'Nenyuka waya ezigangeni, wathimba abathinjwa; ukwamkela izipho ebantwini; ewe, nakuzo iintshaba, ukuba ahlale uYehova uThixo phakathi kwazo.', 'XHO22'),
(23, 68, 19, 'Makabongwe uYehova, osithwalisa imisebenzi emihle imihla yonke, uThixo wosindiso lwethu! Sela.', 'XHO22'),
(23, 68, 20, 'uThixo wethu nguThixo wosindiso; yaye ngaYehova amanzi aphuma ekufeni.', 'XHO22'),
(23, 68, 21, 'Kodwa uThixo uya kuziqhekeza iintloko zeentshaba zakhe nolukakayi oluneenwele eziqhubeka elukreqweni.', 'XHO22'),
(23, 68, 22, 'Wathi uYehova, Ndiya kuyilanda iBhashan, ndibabuyisele abantu bam emazantsi elwandle;', 'XHO22'),
(23, 68, 23, 'luthiwe nkxu unyawo lwakho egazini leentshaba zakho, naselulwimini lwezinja zakho.', 'XHO22'),
(23, 68, 24, 'Mazibone iindlela zakho, Thixo; nokuhamba koThixo wam, iNkosi yam, engcweleni.', 'XHO22'),
(23, 68, 25, 'Phambili iimvumi, ngasemva izixhobo zomkhosi; phakathi kwabo kukho amantombazana adlala amagubu.', 'XHO22'),
(23, 68, 26, 'Dumisani uYehova emabandleni, Yehova, Thende likaSirayeli.', 'XHO22'),
(23, 68, 27, 'Ukhona uBhenjamin umncinane, nabathetheli babo, abathetheli bakwaYuda nebhunga labo, iinkosana zakwaZebhulon, neenkosana zakwaNafetali.', 'XHO22'),
(23, 68, 28, 'uThixo wakho wakunika amandla; kuqinise, Thixo, oko usenzele kona.', 'XHO22'),
(23, 68, 29, 'Ngenxa yetempile yakho eYerusalem, ookumkani baya kukuzisela izipho;', 'XHO22'),
(23, 68, 30, 'xabelani ingxokolo yezikhali, nengxokolo yeenkunzi ezintsha zeenkomo, namathole eenkomo, bade banikele amaqhosha esilivere elowo; ukubachithachitha abantu abathanda imfazwe.', 'XHO22'),
(23, 68, 31, 'Kuya kuphuma abathetheli eYiputa; iKushi iya kuzolulela ngokukhawuleza izandla zayo kuThixo.', 'XHO22'),
(23, 68, 32, 'Memelelani uzuko lukaThixo, zikumkani zehlabathi; Memelelani uzuko lukaYehova; Cella:', 'XHO22'),
(23, 68, 33, 'Kulo uphezu kwezulu lamazulu, owayekho kwamandulo; yabona, ukhupha ilizwi Lakhe, kwaye lilizwi elinamandla.', 'XHO22'),
(23, 68, 34, 'Mnikeni amandla uThixo, Uzuko lwakhe luphezu kwaSirayeli, Buphezu kwamafu amandla akhe.', 'XHO22'),
(23, 68, 35, 'Thixo, uwoyikekayo ezingcweleni zakho; UloThixo kaSirayeli, ubanika amandla nokomelela abantu bakhe. Makabongwe uThixo.', 'XHO22'),
(23, 69, 1, 'Kukumkani waseSosannim: Umhobe kaDavide ngokuba amanzi andingene emphefumlweni.', 'XHO22'),
(23, 69, 2, 'Nditshonile eludakeni olunzulu, akukho ndawo yokuma; Ndangena emanzini anzongonzongo, apho imisinga yafika kum.', 'XHO22'),
(23, 69, 3, 'Ndidiniwe kukulila, womile umqala wam, amehlo am adiniwe kukulinda uThixo wam.', 'XHO22'),
(23, 69, 4, 'Abandithiyayo ngelize baninzi ngaphezu kweenwele zentloko yam; Ababethanda ukunditshabalalisa baziintshaba zam, bomelele; ndiye ndabuyisela into endingayithathanga.', 'XHO22'),
(23, 69, 5, 'Wena, Thixo, uyakwazi ukumatha kwam; nezono zam azigushekile kuwe.', 'XHO22'),
(23, 69, 6, 'Mabangadani ngam abakuthembayo, Nkosi yam, Yehova wemikhosi; Mabangadani ngam abakufunayo, Thixo kaSirayeli.', 'XHO22'),
(23, 69, 7, 'Ngokuba ndithwele ihlazo ngenxa yakho; ihlazo ligqubuthele ubuso bam.', 'XHO22'),
(23, 69, 8, 'Ndingumphambukeli kubazalwana bam, Ndingumphambukeli koonyana bakama.', 'XHO22'),
(23, 69, 9, 'Ngokuba lindidlile ikhwele ngenxa yendlu yakho; yaye ihlazo labakukhubekisileyo lawela phezu kwam.', 'XHO22'),
(23, 69, 10, 'Mna, ndalila, ndizila ukudla, ndilila;', 'XHO22'),
(23, 69, 11, 'Kananjalo ndazenza iingubo zam ezimnyama; Ndaba ngumzekeliso kubo.', 'XHO22'),
(23, 69, 12, 'Bathetha ngam abahleli esangweni; ndabe ndinxilile ingoma.', 'XHO22'),
(23, 69, 13, 'Ke mna ndithandaza kuwe, Yehova, ngexesha elamkelekileyo; Ndiphendule, Thixo, ngobuninzi benceba yakho, Nenyaniso yosindiso lwakho.', 'XHO22'),
(23, 69, 14, 'Ndisindise eludakeni, unganditshoni; Ndihlangule kwabandithiyayo, nasemanzini anzongonzongo.', 'XHO22'),
(23, 69, 15, 'Mawungandintywiliseli umlambo, Ungandiginyi nomlambo, Ungandivali umlomo wequla phezu kwam.', 'XHO22'),
(23, 69, 16, 'Ndiphendule, Yehova! ngokuba ilungile inceba yakho.', 'XHO22'),
(23, 69, 17, 'Musa ukubusithelisa ubuso bakho kumkhonzi wakho; ngokuba ndibandezelekile; khawuleza undive', 'XHO22'),
(23, 69, 18, 'Sondela emphefumlweni wam, uwuhlangule; ndisindise ezintshabeni zam.', 'XHO22'),
(23, 69, 19, 'Wena uyakwazi ukungcikivwa kwam, nokucukucezwa kwam, nokudelwa kwam; Abandichasayo banawe bonke.', 'XHO22'),
(23, 69, 20, 'Ingcikivo yayaphula intliziyo yam; ndaye ndeva intlungu; nabathuthuzeli, kodwa andabafumana.', 'XHO22'),
(23, 69, 21, 'Ukudla bandinika inyongo; xa ndinxaniwe, bandinika iviniga.', 'XHO22'),
(23, 69, 22, 'Isithebe sabo phambi kwabo masibe ngumgibe, Kube kulunge kubo kube ngumgibe.', 'XHO22'),
(23, 69, 23, 'Amehlo abo amnyama, ukuba angaboni; amanqe abo ahlala engcangcazela.', 'XHO22'),
(23, 69, 24, 'Baphalazele ngomsindo wakho, Ubaqweqwedise umsindo wakho.', 'XHO22'),
(23, 69, 25, 'Isakhiwo sabo masibe senkangala; yaye makungabikho bani uhlala emhadini wabo.', 'XHO22'),
(23, 69, 26, 'Ngokuba bayamtshutshisa lowo umbethayo; bathetha ngeentlungu zabo nibabuleleyo.', 'XHO22'),
(23, 69, 27, 'Wongeza ubugwenxa ebugwenxeni babo, Abangena ebulungiseni bakho.', 'XHO22'),
(23, 69, 28, 'Mabacinywe encwadini yobomi, Bangabhalwa namalungisa.', 'XHO22'),
(23, 69, 29, 'Ke ndilusizana, ndibuhlungu; Ngamana usindiso lwakho, Thixo, malundiphakamise.', 'XHO22'),
(23, 69, 30, 'Ndiya kulidumisa igama likaThixo ngokuvuma, Ndilidumise ngokubulela.', 'XHO22'),
(23, 69, 31, 'Nako oku kuya kumkholisa uYehova njengenkunzi yenkomo okanye inkunzi yenkomo eneempondo neenzipho.', 'XHO22'),
(23, 69, 32, 'Abathobekileyo bobona, bavuye, Nentliziyo emfunayo uThixo iphile.', 'XHO22'),
(23, 69, 33, 'Ngenxa yokuba uYehova uyaphulaphula abathobekileyo yaye akabadeleli amabanjwa akhe.', 'XHO22'),
(23, 69, 34, 'Malimdumise izulu nomhlaba, Ulwandle nento yonke enambuzelayo kulo.', 'XHO22'),
(23, 69, 35, 'Kuba uThixo uya kuyikhulula ngentlawulelo iZiyon, aze azakhe izixeko zakwaYuda, ukuze bahlale khona baze bazidle ilifa.', 'XHO22'),
(23, 69, 36, 'Ilidle ilifa imbewu yabakhonzi bakhe, Bahlale kulo abalithandayo igama lakhe.', 'XHO22'),
(23, 70, 1, 'Kukumkani weemvumi, umhobe kaDavide khawuleza undincede, Yehova.', 'XHO22'),
(23, 70, 2, 'Mabadane badane abawufunayo umphefumlo wam;', 'XHO22'),
(23, 70, 3, 'Mabajike kwihlazo labo, bathi, Tshotsho!', 'XHO22'),
(23, 70, 4, 'Mababe nemihlali bavuye ngawe bonke abakufunayo; nabaluthandayo usindiso lwakho bathi, Mkhulu uThixo!', 'XHO22'),
(23, 70, 5, 'Ke mna ndilusizana, ndilihlwempu; yiza kum, Thixo; Umncedi wam noMsindisi wam nguwe; Nkosi, musa ukulibala;', 'XHO22'),
(23, 71, 1, 'Yehova, ndikholose ngawe; ungaze undiyeke ndikhathazeke.', 'XHO22'),
(23, 71, 2, 'Ndisindise ngobulungisa bakho, ndisindise; ndiphulaphule, ndisindise.', 'XHO22'),
(23, 71, 3, 'Yiba lihlathi lam elingunaphakade, endizimela ngalo ngamaxesha onke; Wathi mandisindiswe; Ngokuba ingxondorha yam nemboniselo yam nguwe.', 'XHO22'),
(23, 71, 4, 'Ndisindise, Thixo wam, esandleni songendawo, Esandleni songendawo, esijorha.', 'XHO22'),
(23, 71, 5, 'Kuba ulithemba lam, Yehova, ulithemba lam kwasebuncinaneni bam.', 'XHO22'),
(23, 71, 6, 'Ndakhuliswa nguwe kwasesizalweni sikanina; Nguwe owandikhupha esizalweni sikama; uya kuhlala eyindumiso yam.', 'XHO22'),
(23, 71, 7, 'Oko kungummangaliso kwabaninzi; Ke wena ulihlathi lam elinqabileyo.', 'XHO22'),
(23, 71, 8, 'Umlomo wam mawuzaliswe yindumiso yakho, Nozuko lwakho imini yonke.', 'XHO22'),
(23, 71, 9, 'Musa ukundishiya ebudaleni; musa ukundishiya xa ephelile amandla.', 'XHO22'),
(23, 71, 10, 'Ngokuba zithetha ngam iintshaba zam; Abawulalelayo umphefumlo wam bacebisana kunye;', 'XHO22'),
(23, 71, 11, 'Wathi, uThixo umlahlile; msukeleni nimbambe; ngokuba akukho bani umsindisayo.', 'XHO22'),
(23, 71, 12, 'Thixo, musa ukuba kude nam; Owu Thixo wam, khawuleza undincede.', 'XHO22'),
(23, 71, 13, 'Mabadane, batshatyalaliswe, abawuchasayo umphefumlo wam; Mabathiwe wambu ngocukucezo nehlazo abafuna ububi bam.', 'XHO22'),
(23, 71, 14, 'Ke mna ndiya kuhlala ndikholose ngawe, Ndiya kukubonga kakhulu.', 'XHO22'),
(23, 71, 15, 'Umlomo wam uya kubalisa ngobulungisa bakho, nosindiso lwakho yonke imihla; kuba andiwazi amanani ayo.', 'XHO22'),
(23, 71, 16, 'Ndiya kuhamba ngamandla eNkosi uYehova; Ndibiza ubulungisa bakho kuwe wedwa.', 'XHO22'),
(23, 71, 17, 'Thixo, undifundisile kwasebuncinaneni bam, Kude kube ngoku ndiyayixela imisebenzi yakho ebalulekileyo.', 'XHO22'),
(23, 71, 18, 'Ke, ukuba ndide ndaluphele ndibe yingwevu, Thixo, musa ukundishiya; Ndide ndisazise esi sizukulwana amandla akho, nobugorha bakho kubo bonke abezayo.', 'XHO22'),
(23, 71, 19, 'Kananjalo ubulungisa bakho buye bafika phezulu, Thixo, wena uzenzayo izinto ezinkulu; Owu Thixo, ngubani na onjengawe!', 'XHO22'),
(23, 71, 20, 'Wena, owandiphilisayo kwiimbandezelo ezinkulu, oziphetheyo, uyandiphilisa, undirhole ezinzonzobileni zamanzi ehlabathi;', 'XHO22'),
(23, 71, 21, 'Uya kubandisa ubukhulu bam, andithuthuzele ngeenxa zonke;', 'XHO22'),
(23, 71, 22, 'Nam ndiya kukudumisa ngohadi, Inyaniso yakho, Thixo wam; Ndiya kukubethela uhadi, Wena Ngcwele kaSirayeli;', 'XHO22'),
(23, 71, 23, 'Uya kuvuya kakhulu umlomo wam, ndakukubethela uhadi; nomphefumlo wam owukhululeyo;', 'XHO22');
