INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(23, 71, 24, 'Yaye ulwimi lwam luya kuthetha ngobulungisa bakho imini yonke, ngenxa yokuba behlazekile, ngenxa yokuba abo bafuna ububi bam bahlazekile.', 'XHO22'),
(23, 72, 1, '(Indumiso ka-Solomon.) Thixo, mnike ukumkani imigwebo yakho, mnike unyana wokumkani ubulungisa bakho.', 'XHO22'),
(23, 72, 2, 'Uza kugweba abantu bakho ngobulungisa, Nabaziintsizana zakho ngokwesiko.', 'XHO22'),
(23, 72, 3, 'Iintaba mazibathwalele uxolo abantu, neenduli zibe ngubulungisa;', 'XHO22'),
(23, 72, 4, 'Makagwebe ityala labaziintsizana ebantwini, asindise oonyana bamahlwempu, abaxinzelele phantsi abacudisi.', 'XHO22'),
(23, 72, 5, 'Ziya kukoyika ukuma kwelanga nenyanga, kususela kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 72, 6, 'Uya kuhla njengemvula phezu kotyani obugudiweyo, njengemvula enyakamisa umhlaba.', 'XHO22'),
(23, 72, 7, 'Ngemihla yakhe amalungisa aya kuphumelela; noxolo olukhulu kuyo yonke inyanga.', 'XHO22'),
(23, 72, 8, 'Uya kulawula ethabathela elwandle ase elwandle, ethabathela kuwo uMlambo ase eziphelweni zehlabathi.', 'XHO22'),
(23, 72, 9, 'Baya kuqubuda kuye abemi bomqwebedu; iintshaba zakhe ziya kukhotha uthuli.', 'XHO22'),
(23, 72, 10, 'Ookumkani baseTarshishe neziqithi baya kuzisa izipho; ookumkani bakwaShebha nabakwaSebha baya kupha izipho.', 'XHO22'),
(23, 72, 11, 'Baqubude kuye bonke ookumkani; zonke iintlanga ziya kumkhonza.', 'XHO22'),
(23, 72, 12, 'Ngokuba uya kulisindisa ihlwempu xa ebiza; namahlwempu namahlwempu.', 'XHO22'),
(23, 72, 13, 'Uya kuba nemfesane kolusizana nolihlwempu, ayisindise imiphefumlo yamahlwempu;', 'XHO22'),
(23, 72, 14, 'Uya kuwukhulula umphefumlo wabo enkohliso, nasekugonyamelweni, negazi labo lixabiseke emehlweni akhe.', 'XHO22'),
(23, 72, 15, 'Uya kuphila, anikwe esinye isahlulo segolide sikaShebha; Yaye yonke imihla uya kudunyiswa.', 'XHO22'),
(23, 72, 16, 'Intabalala yengqolowa iya kuwuzalisa umhlaba encotsheni yeentaba; iziqhamo zawo ziya kunyikima njengeLebhanon, nesixeko siya kutyatyamba njengengca.', 'XHO22'),
(23, 72, 17, 'Igama lakhe liya kuba ngunaphakade; igama lakhe liya kuhlala likho logama lisekhona ilanga, yaye abantu baya kusikelelwa ngaye; zonke iintlanga ziya kuthi ninoyolo.', 'XHO22'),
(23, 72, 18, 'Makabongwe uYehova uThixo, uThixo kaSirayeli, osebenza imiqondiso yedwa.', 'XHO22'),
(23, 72, 19, 'Kwaye igama lakhe elizukileyo malisikelelwe ngonaphakade, kwaye ihlabathi lonke malizaliswe bubuqaqawuli bakhe; Amen, kwaye Amen.', 'XHO22'),
(23, 72, 20, 'Izalisekile imithandazo kaDavide, unyana kaYese;', 'XHO22'),
(23, 73, 1, 'Umhobe ka-Asafu. Ulungile kwaphela uThixo kuSirayeli, Kwabantliziyo zinyulu.', 'XHO22'),
(23, 73, 2, 'Ke mna, imilenze yam isiphantse ukuba ukuphuma; Phantse ukutyibilika ukunyathela kwam.', 'XHO22'),
(23, 73, 3, 'Kuba bendibamonela abaziziyatha, Ndakukubona ukuphumelela kwabangendawo.', 'XHO22'),
(23, 73, 4, 'Ngokuba abanazibophelelo ekufeni kwabo;', 'XHO22'),
(23, 73, 5, 'Abakho sengxakini njengabanye abantu; kwaye abaxhatshazwa njengabanye abantu.', 'XHO22'),
(23, 73, 6, 'Ngoko ke ikratshi labo libakhonkxile ngomxokelelwane; ukugonyamela kubathe wambu ngokwengubo.', 'XHO22'),
(23, 73, 7, 'Amehlo abo akhazimla, akhazimla;', 'XHO22'),
(23, 73, 8, 'Bonakalisile, baxela ububi; bathetha ngokuzidla.', 'XHO22'),
(23, 73, 9, 'Bawubhekisa ezulwini umlomo wabo, nolwimi lwabo lwahamba emhlabeni.', 'XHO22'),
(23, 73, 10, 'Babuyela apha abantu bakhe;', 'XHO22'),
(23, 73, 11, 'Bathi ke bona, Wazi ngani na uThixo? Kukho ukwazi na kOsenyangweni?', 'XHO22'),
(23, 73, 12, 'Yabona, abangendawo bayaphumelela ehlabathini; bandise ubutyebi.', 'XHO22'),
(23, 73, 13, 'Sifumane sayiqaqambisa intliziyo yethu kanti, Sazihlamba izandla zethu ngokuba msulwa;', 'XHO22'),
(23, 73, 14, 'Kuba benditshutshiswa imini yonke, ndikhalinyelwa imiso ngemiso.', 'XHO22'),
(23, 73, 15, 'Xa ndithi, Ndiya kuthetha; yabona, ndiya kusikhubekisa esizukulwaneni soonyana bakho.', 'XHO22'),
(23, 73, 16, 'Ndathi ke ndakucinga ukuba ndikwazi oko, ndaba buhlungu kakhulu;', 'XHO22'),
(23, 73, 17, 'ndide ndingene engcweleni kaThixo; ndiye ndaliqonda ikamva labo.', 'XHO22'),
(23, 73, 18, 'Inene, ubamise ezindaweni ezibuthelezi, ubahlisele entshabalalweni;', 'XHO22'),
(23, 73, 19, 'Hayi, ukuphanziswa kwabo ngephanyazo; baphanza ngokupheleleyo luloyiko.', 'XHO22'),
(23, 73, 20, 'Njengephupha ekuqabukeni komntu; ngako oko, Nkosi, wowudela umfanekiso wabo.', 'XHO22'),
(23, 73, 21, 'Ngoko intliziyo yam yaba buhlungu, ndahlatywa ezintso zam.', 'XHO22'),
(23, 73, 22, 'Bendisisidenge, ndingazi nto, Ndinjengenkomo phambi kwakho.', 'XHO22'),
(23, 73, 23, 'Mna ke ndihlala ndinani; Wandibamba ngesandla sasekunene.', 'XHO22'),
(23, 73, 24, 'Uya kundikhapha ngecebo lakho, Undithabathe emveni koku, ndibe nozuko.', 'XHO22'),
(23, 73, 25, 'Ndinabani na emazulwini? kwaye akukho namnye ehlabathini endimnqwenelayo, ngaphandle kwakho.', 'XHO22'),
(23, 73, 26, 'Ingaphela inyama yam nentliziyo yam: Iliwa lentliziyo yam, nesabelo sam, nguThixo ngamaxesha onke.', 'XHO22'),
(23, 73, 27, 'Ngokuba abanyawukileyo kuwe baya kudaka; ubabhangisile bonke abakrexezayo kuwe.', 'XHO22'),
(23, 73, 28, 'Kodwa ukusondela kuThixo kulungile kum: Ndikholose ngeNkosi uYehova, Ukuze ndiyibalise yonke imisebenzi yakho.', 'XHO22'),
(23, 74, 1, 'Yini na, Thixo, ukuba usishiye ngonaphakade? Wavutha umsindo wakho izimvu zedlelo lakho?', 'XHO22'),
(23, 74, 2, 'Cinga ngebandla lakho, owalithenga kumaxesha amandulo; intonga yelifa lakho oyikhulule ngokuyihlawulela; le ntaba yaseZiyon, ubumi kuyo.', 'XHO22'),
(23, 74, 3, 'Phakamisa unyawo lwakho emanxuweni angunaphakade; nako konke okwenziwa lutshaba endaweni engcwele.', 'XHO22'),
(23, 74, 4, 'Zigquma iintshaba zakho phakathi kwebandla lakho; bamisa iiflegi zabo, ukuba zibe yimiqondiso.', 'XHO22'),
(23, 74, 5, 'Umfo othile wayedume ngokuphakamisa amazembe emithini eshinyeneyo.', 'XHO22'),
(23, 74, 6, 'Kodwa ngoku bakhawuleza bayiqhekeza imifanekiso yakhe eqingqiweyo ngamazembe neehamile.', 'XHO22'),
(23, 74, 7, 'Bagalela umlilo engcweleni yakho, Eyinqambi ngokudiliza isakhiwo segama lakho.', 'XHO22'),
(23, 74, 8, 'Bathi ezintliziyweni zabo, Mabagqithe kunye; Bazitshisa zonke izindlu zesikhungu zikaThixo ehlabathini.', 'XHO22'),
(23, 74, 9, 'Imiqondiso yethu asiyiboni; akusekho mprofeti, akukho namnye kuthi owaziyo ukuba iya kufika nini na.', 'XHO22'),
(23, 74, 10, 'Kunini na, Thixo, umbandezeli edela? Utshaba luhleli luligiba igama lakho?', 'XHO22'),
(23, 74, 11, 'Uyasifinyeza na isandla sakho, ewe, esokunene sakho? yithathe esifubeni sakho.', 'XHO22'),
(23, 74, 12, 'Ngokuba uThixo nguKumkani wam kususela kwamandulo, Ulosindisayo phakathi kwehlabathi.', 'XHO22'),
(23, 74, 13, 'Nguwe owahlula ulwandle ngamandla akho; Owaphula iintloko zoodyakalashe emanzini.', 'XHO22'),
(23, 74, 14, 'Nguwe owaphula iintloko zeLeviyatan waza wazinika ukuba zibe kukudla kwabantu abahlala entlango.', 'XHO22'),
(23, 74, 15, 'Nguwe owahlula iqula noNogumbe, Watshisa imilambo emikhulu;', 'XHO22'),
(23, 74, 16, 'Yeyakho imini, bobakho ubusuku; Nguwe owalungisa ukukhanya nelanga.', 'XHO22'),
(23, 74, 17, 'Wadala yonke imida yehlabathi, uMenzi wehlobo nobusika.', 'XHO22'),
(23, 74, 18, 'Khumbula ukuba utshaba luqalekisiwe, Yehova, yaye abantu abaziziyatha bangcikive igama lakho.', 'XHO22'),
(23, 74, 19, 'Musa ukuwunikela umphefumlo wehobe lakho kwingidi yabangendawo; ungalibali ngonaphakade ibandla lamahlwempu akho.', 'XHO22'),
(23, 74, 20, 'Phulaphulani umnqophiso, ngokuba iindawo ezimnyama zomhlaba zizele zizakhiwo zobungendawo.', 'XHO22'),
(23, 74, 21, 'Ocinezelweyo akayi kubuya eneentloni; malidumise igama lakho olusizana nolihlwempu.', 'XHO22'),
(23, 74, 22, 'Zivuse, Thixo, ngenxa yebango lakho; cinga ukuba isidenge sikungcikiva yonke imihla.', 'XHO22'),
(23, 74, 23, 'Musa ukulilibala ilizwi leentshaba zakho: ingxokozelo yabasukela phezulu kuwe ihlala ikhula.', 'XHO22'),
(23, 75, 1, 'Kumbhexeshi-kwayara, umhobe ka-Asafu. Siyabulela kuwe, Thixo, siyabulela; ngokuba likufuphi igama lakho, Iya kubalisa imisebenzi yakho ebalulekileyo.', 'XHO22'),
(23, 75, 2, 'Xa ndamkelekileyo ebandleni, ndiya kugweba ngobulungisa;', 'XHO22'),
(23, 75, 3, 'Liya kuqotywa ihlabathi, nabemi balo, ndizizinzisile iintsika zalo. Sela.', 'XHO22'),
(23, 75, 4, 'Ndathi ke kwabazizinyabi, Musani ukuba zizidenge; nakubangendawo;', 'XHO22'),
(23, 75, 5, 'Musani ukuluphakamisa uphondo lwakho; ungathethi ngentamo elukhuni.', 'XHO22'),
(23, 75, 6, 'Ngokuba uzuko aluphumi empumalanga, nasentshonalanga, nasezantsi;', 'XHO22'),
(23, 75, 7, 'Ke yena uThixo nguMgwebi; uyamthoba omnye, aphakamise omnye.', 'XHO22'),
(23, 75, 8, 'Kuba indebe isesandleni sikaYehova, iwayini ebomvu; uzele ngumxube; uya kuyiphalaza kuwo, kodwa intsipho yawo, bonke abangendawo behlabathi baya kuyitshabalalisa bayisele.', 'XHO22'),
(23, 75, 9, 'Ke mna ndiya kuthetha ngonaphakade; Ndiyathanda ukucula kuThixo kaYakobi.', 'XHO22'),
(23, 75, 10, 'Ndiya kuzinqumla zonke iimpondo zabangendawo; ke iimpondo zamalungisa ziya kuphakanyiswa.', 'XHO22'),
(23, 76, 1, 'Kumongameli waseNeginoti; Ingoma ka-Asafu. Waziwa uYuda uThixo; likhulu igama lakhe kwaSirayeli.', 'XHO22'),
(23, 76, 2, 'Umnquba wakhe useSalem, Nekhaya lakhe liseZiyon.', 'XHO22'),
(23, 76, 3, 'Wayaphula apho iintolo, nengweletshetshe, nekrele, nemfazwe. Sela.', 'XHO22'),
(23, 76, 4, 'Uzukile, mhle ngaphezu kweentaba zenyama.', 'XHO22'),
(23, 76, 5, 'Baphangiwe abantliziyo inamandla, balala ubuthongo, Akazifumana izandla zawo onke amagorha.', 'XHO22'),
(23, 76, 6, 'Ngokukhalima kwakho, Thixo kaYakobi, Siwe bubuthongo inqwelo yokulwa nehashe.', 'XHO22'),
(23, 76, 7, 'Wena, ewe, ufanele woyike, yaye ngubani na onokuma phambi kwakho xa uqala ukucaphuka?', 'XHO22'),
(23, 76, 8, 'Uthumele umgwebo uphuma emazulwini; Loyika ihlabathi, lazola;', 'XHO22'),
(23, 76, 9, 'Xa uThixo emele umgwebo, ukuze asindise bonke abalulamileyo behlabathi. Sela.', 'XHO22'),
(23, 76, 10, 'Inene, umsindo womntu uya kubulela kuwe;', 'XHO22'),
(23, 76, 11, 'Yenza isibhambathiso, ubuyisele kuYehova uThixo wakho;', 'XHO22'),
(23, 76, 12, 'Uya kuwutshabalalisa umoya wabathetheli; Uyoyikeka kookumkani behlabathi.', 'XHO22'),
(23, 77, 1, 'kuYedutun ingoma ka-Asafu wandiva ke.', 'XHO22'),
(23, 77, 2, 'Ngemini yokubandezeleka kwam ndamfuna uYehova, amathumba am ebusuku asuka aqhawuka, yaye akazange ayeke; umphefumlo wam wala ukuthuthuzeleka.', 'XHO22'),
(23, 77, 3, 'Ndacinga ngoThixo, ndakhwankqiswa; Sela.', 'XHO22'),
(23, 77, 4, 'Uwamfamekisa amehlo am; Ndididekile, andikwazi kuthetha.', 'XHO22'),
(23, 77, 5, 'Ndacinga ngemihla yamandulo, iminyaka yamandulo;', 'XHO22'),
(23, 77, 6, 'Ndikhumbula ukubetha kwam uhadi ebusuku, Ndithetha entliziyweni yam, Uphengulula umphefumlo wam.', 'XHO22'),
(23, 77, 7, 'uYehova uya kunishiya na ngonaphakade? akayi kuba nanceba ngakumbi na?', 'XHO22'),
(23, 77, 8, 'Iphele yaphela na inceba yakhe? Lingunaphakade na idinga lakhe?', 'XHO22'),
(23, 77, 9, 'uThixo ukulibele na ukwenza inceba? Ngaba uzivalele ngomsindo iinceba zakhe? Sela.', 'XHO22'),
(23, 77, 10, 'Ndathi ke, Bubuthathaka bam obu, Iminyaka yesandla sokunene sOsenyangweni ndiya kuyikhumbula.', 'XHO22'),
(23, 77, 11, 'Ndikhankanya izenzo ezincamisayo zikaYehova; Ndiya kuyikhumbula imimangaliso yakho yamandulo.', 'XHO22'),
(23, 77, 12, 'Ndicamngca ngemisebenzi yakho yonke, Ndicamanga ngezenzo zakho zonke.', 'XHO22'),
(23, 77, 13, 'Thixo, isebungcweleni indlela yakho; Ngubani na onguThixo omkhulu njengoThixo wethu?', 'XHO22'),
(23, 77, 14, 'UnguThixo, umenzi wemimangaliso; Ubuxelile ubugorha bakho ezintlangeni.', 'XHO22'),
(23, 77, 15, 'Wabasindisa ngesandla sakho abantu bakho, oonyana bakaYakobi noYosefu. Sela.', 'XHO22'),
(23, 77, 16, 'Wawabona amanzi, Thixo, wawabona amanzi; boyika; namaqula angcangcazela.', 'XHO22'),
(23, 77, 17, 'Amafu agalela amanzi, umoya ukhupha isandi; ziphumele ngaphandle iintolo zakho.', 'XHO22'),
(23, 77, 18, 'Izwi lendudumo yakho labakho esibhakabhakeni, Imibane yalikhanyisa ihlabathi, Lanyikima ihlabathi, lanyikima.', 'XHO22'),
(23, 77, 19, 'Indlela yakho yaba selwandle, nasemanzini amaninzi indlela yakho, akwaziwa ukunyathela kwakho.', 'XHO22'),
(23, 77, 20, 'Wabakhokela njengomhlambi abantu bakho, Ngesandla sikaMoses noAron.', 'XHO22'),
(23, 78, 1, '(Maschil ka-Asafu.) Bantu bam, wubekeleni indlebe umyalelo wam; yibekeleni indlebe intetho yomlomo wam.', 'XHO22'),
(23, 78, 2, 'Ndiya kuwuvula umlomo wam ngomzekeliso;', 'XHO22'),
(23, 78, 3, 'Oko sikuvileyo, nesikwaziyo, oko basixelela kona oobawo;', 'XHO22'),
(23, 78, 4, 'Asiyi kuzifihla koonyana babo, yaye isizukulwana esilandelayo siya kubalisa iingoma zikaYehova nangamandla akhe nemimangaliso yakhe awayenzayo.', 'XHO22'),
(23, 78, 5, 'Ngokuba wamisa umqondiso kwaYakobi, Wabeka umyalelo kwaSirayeli, Awawumisela oobawo, Ukuba bawazise oonyana babo.', 'XHO22'),
(23, 78, 6, 'Ukuze siwazi isizukulwana esizayo, oonyana abaya kuzalwa; abo banokuma, babaxelele abantwana babo ezo zinto;', 'XHO22'),
(23, 78, 7, 'ukuze bakholose ngoThixo, bangayilibali imisebenzi yakhe, bayigcine imithetho yakhe.', 'XHO22'),
(23, 78, 8, 'Musa ukuba njengooyise, isizukulwana esiyinjubaqa, esineenkani; isizukulwana esingazilungisanga iintliziyo zaso, esimoya ungaqinisekanga kuThixo.', 'XHO22'),
(23, 78, 9, 'Oonyana bakaEfrayim, bexhobile, beziintoli ezinezaphetha, babuya ngemini yemfazwe.', 'XHO22'),
(23, 78, 10, 'Abawugcinanga umnqophiso kaThixo, Abavumanga ukuhamba ngomyalelo wawo;', 'XHO22'),
(23, 78, 11, 'Ke yena wayilibala imisebenzi yakhe, kwanemiqondiso awababonisa yona.', 'XHO22'),
(23, 78, 12, 'Wenza izinto ezingaqhelekanga emehlweni ooyise, Ezweni laseYiputa, ezweni laseTsohan.', 'XHO22'),
(23, 78, 13, 'Walahlula ulwandle, wabathuthuzela; wamisa amanzi njengemfumba.', 'XHO22'),
(23, 78, 14, 'Wabakhokela ngelifu emini nasebusuku, ngokukhanya komlilo.', 'XHO22'),
(23, 78, 15, 'Wawacanda amawa entlango, wawaseza njengamanzi enzonzobila;', 'XHO22'),
(23, 78, 16, 'Wakhupha imilambo engxondorheni, wawahambisa amanzi njengemilambo.', 'XHO22'),
(23, 78, 17, 'Baqhubeka besona kuye ngokugculela Oyena Uphakamileyo entlango.', 'XHO22'),
(23, 78, 18, 'Babongoza uThixo ngentliziyo yabo, Ngokuwubizela umphefumlo wabo ukudla.', 'XHO22'),
(23, 78, 19, 'Ewe, bathetha ngokuchasene noThixo; bathi, Unako na uThixo ukulungisa isithebe entlango?', 'XHO22'),
(23, 78, 20, 'Yabona, walibetha iliwa; Anako na ukunika isonka? Wobanika na abantu bakhe inyama?', 'XHO22'),
(23, 78, 21, 'Weva ke uYehova, wacaphuka;', 'XHO22'),
(23, 78, 22, 'Ngokuba bengakholwanga kuThixo, Bengakholosanga ngosindiso lwakhe;', 'XHO22'),
(23, 78, 23, 'Wawamisela amafu phezulu, Wazivula iingcango zezulu;', 'XHO22'),
(23, 78, 24, 'Wabatshiza imana ukuba badle, Wabanika iinkozo zezulu.', 'XHO22'),
(23, 78, 25, 'Abantu badla ukudla kwezithunywa zezulu; Wabanika inyama.', 'XHO22'),
(23, 78, 26, 'Wavuthela umoya wasempumalanga ezulwini, Wawuvuthela ngamandla akhe umoya wasezantsi;', 'XHO22'),
(23, 78, 27, 'Wabanisela phezu kwabo inyama njengothuli, neentaka ezineentsiba njengentlabathi yolwandle.', 'XHO22'),
(23, 78, 28, 'Kwaye wawa esazulwini sokhuseleko lwabo kufuphi nekhaya labo.', 'XHO22'),
(23, 78, 29, 'Badla bahlutha, ngokuba wabanikayo inkanuko yabo;', 'XHO22'),
(23, 78, 30, 'Abahlukananga neminqweno yabo. Kodwa ngelixa inyama yabo isesemlonyeni wabo.', 'XHO22'),
(23, 78, 31, 'Wavutha umsindo kaThixo phezu kwabo, Wabulala abatyebileyo kubo, waboyisa abanyuliweyo bakwaSirayeli.', 'XHO22'),
(23, 78, 32, 'Noko ke, basesona, yaye abazange bayikholelwe imimangaliso yakhe.', 'XHO22'),
(23, 78, 33, 'Ngako oko uyitshitshisile ililize imihla yabo, Neminyaka yabo esizalweni.', 'XHO22'),
(23, 78, 34, 'Yaye xa wababulalayo, bamfuna; yaye babuya baza babuza kuThixo kusengaphambili.', 'XHO22'),
(23, 78, 35, 'Bakhumbula ukuba uThixo uliliwa labo, uThixo Osenyangweni ungumkhululi wabo.', 'XHO22'),
(23, 78, 36, 'Ke bamlukuhla ngemilebe yomlomo wabo, Bamxokisa ngolwimi lwabo;', 'XHO22'),
(23, 78, 37, 'Ngenxa yokuba intliziyo yabo yayinganyanisekanga kuye, yaye babenganyanisekanga emnqophisweni wakhe.', 'XHO22'),
(23, 78, 38, 'Ke yena owayenemfesane, wabaxolela ubugwenxa babo, akabatshabalalisa;', 'XHO22'),
(23, 78, 39, 'Wakhumbula ukuba kukudla kodwa; umoya odlulayo ungabuyi.', 'XHO22'),
(23, 78, 40, 'Hayi, ukuba bamenze intlekisa entlango, ukuba bamenzakalisa entlango!', 'XHO22'),
(23, 78, 41, 'Babuya ke bamlinga uThixo, Bamgiba oyiNgcwele kaSirayeli.', 'XHO22'),
(23, 78, 42, 'Abasikhumbula isandla sakhe, namhla awabahlangulayo ezintshabeni zabo;', 'XHO22'),
(23, 78, 43, 'Wenza imiqondiso yakhe eYiputa, Nezimanga zakhe ezweni laseTsohan.', 'XHO22'),
(23, 78, 44, 'Bayiguqula imijelo yabo, yaba ligazi; nemilambo yabo, ayinakuyisela.', 'XHO22'),
(23, 78, 45, 'Wathumela kubo zonke iintlobo zeembovane, zadla; kunye nezinambuzane ezabatshabalalisayo.', 'XHO22'),
(23, 78, 46, 'Izilimo zazo wayinika iinkumbi ezandayo, nomsebenzi wazo wawunika iinkumbi.', 'XHO22'),
(23, 78, 47, 'Wazitshabalalisa izidiliya zabo ngesichotho, Nemithombe yabo ngeqabaka.', 'XHO22'),
(23, 78, 48, 'Wayinikela kwisichotho imfuyo yabo, Nemfuyo yabo ngemibane.', 'XHO22'),
(23, 78, 49, 'Waphosa phezu kwabo ukuvutha komsindo wakhe, nomsindo, nengqumbo, nembandezelo, Ethumela phakathi kwabo izithunywa ezikhohlakeleyo.', 'XHO22'),
(23, 78, 50, 'Wenza indlela yokuphuphuma komsindo wakhe; Akawuconga umphefumlo wabo ekufeni, Wabunikela umphefumlo wabo ekufeni;', 'XHO22'),
(23, 78, 51, 'Wawabulala onke amazibulo eYiputa; Owokuqala amandla abo ezintenteni zikaKama;', 'XHO22'),
(23, 78, 52, 'Abantu bakhe wabafudusa njengezimvu, Wabaqhuba njengomhlambi entlango.', 'XHO22'),
(23, 78, 53, 'Wabakhapha bekholosile, ukuba bangoyiki; Ke zona iintshaba zabo zagutyungelwa lulwandle.', 'XHO22'),
(23, 78, 54, 'Wabasa eludinini lwengcwele yakhe, kule ntaba sayithengayo isandla sakhe sokunene.', 'XHO22'),
(23, 78, 55, 'Kanjalo, wazigxotha iintlanga phambi kwazo, wazahlula ilifa lazo ngomgca wolutya, waza wazihlalisa ezintenteni izizwe zakwaSirayeli.', 'XHO22'),
(23, 78, 56, 'Bamngcikiva uThixo Osenyangweni, Abaligcina icebo lakhe.', 'XHO22'),
(23, 78, 57, 'Basuka baguquka benza ubumenemene njengooyise; batyeka njengesaphetha esityeketyeke.', 'XHO22'),
(23, 78, 58, 'Bamqumbisa ngobude babo, bambeka ngemifanekiso yabo eqingqiweyo.', 'XHO22'),
(23, 78, 59, 'Akukuva oko uThixo, wavutha ngumsindo, wawathiya amaSirayeli.', 'XHO22'),
(23, 78, 60, 'Wawushiya umnquba waseShilo, Intente abeyimisile phakathi kwabantu;', 'XHO22'),
(23, 78, 61, 'Uwanikele ekuthinjweni amandla akhe, Nozuko lwakhe esandleni sotshaba.', 'XHO22'),
(23, 78, 62, 'Kwakhona wabanikela abantu bakhe ekreleni; Waba noburhalarhume kwilifa lakhe.', 'XHO22'),
(23, 78, 63, 'Abafana babo batsha ngumlilo; neentombi zabo zingazeki.', 'XHO22'),
(23, 78, 64, 'Ababingeleli babo bawa likrele; nabahlolokazi babo abakhalazi.', 'XHO22'),
(23, 78, 65, 'Wavuka ngokomntu oleleyo, njengegorha elirhawulwa yiwayini.', 'XHO22'),
(23, 78, 66, 'Wazixabela ngasemva iintshaba zakhe; Wabazisela ukubandezeleka okungunaphakade.', 'XHO22'),
(23, 78, 67, 'Ngaphezu koko, wawucekisa umnquba kaYosefu, akasinyula isizwe sakwaEfrayim.', 'XHO22'),
(23, 78, 68, 'Yanyula isizwe sakwaYuda, Intaba yaseZiyon eyayithandayo.', 'XHO22'),
(23, 78, 69, 'Yayakha njengokuphakama kwezulu ingcwele yakhe, Njengokwehlabathi alidalayo ngonaphakade.', 'XHO22'),
(23, 78, 70, 'Kwakhona wanyula uDavide umkhonzi wakhe, waza wamkhupha entolongweni.', 'XHO22'),
(23, 78, 71, 'Yamkhuphela emva kwempahla emfutshane, ukuba aluse abantu bayo, namaSirayeli elifeni lakhe.', 'XHO22'),
(23, 78, 72, 'Kwaye wabondla ngokobunyulu bentliziyo yakhe; wabakhokela ngobulumko bezandla zakhe.', 'XHO22'),
(23, 79, 1, '(Indumiso ka-Asafu.) Thixo, oonyana bolunye uhlanga bangene elifeni lakho; ziyenze inqambi itempile yakho engcwele; bayenza iYerusalem yangumwewe.', 'XHO22'),
(23, 79, 2, 'Zinike izidumbu zabakhonzi bakho ukuba zibe kukudla kweentaka zezulu, kukudla kwabakho benceba kumarhamncwa elizwe.', 'XHO22'),
(23, 79, 3, 'Baliphalaze igazi labo njengamanzi ngeenxa zonke eYerusalem; akwabakho bani wokungcwaba.', 'XHO22'),
(23, 79, 4, 'Senziwe isingcikivo kubamelwane bethu, Isigculelo nesigculelo kwabasingqongileyo.', 'XHO22'),
(23, 79, 5, 'Koda kube nini, Yehova? Uya kuqumba ngonaphakade? Ikhwele lakho liya kuvutha njengomlilo na?', 'XHO22'),
(23, 79, 6, 'Phalaza umsindo wakho ezintlangeni ongazaziyo, nasezikumkanini ezingalibizanga igama lakho.', 'XHO22'),
(23, 79, 7, 'Kuba bamdlile uYakobi baza bayonakalisa indawo yakhe yokuhlala.', 'XHO22'),
(23, 79, 8, 'Musa ukuzikhumbula izono zethu zangaphambili; Inceba yakho mayibonakale kuthi ngokukhawuleza, Ngokuba sithobeke kakhulu.', 'XHO22'),
(23, 79, 9, 'Thixo ongumsindisi wethu, sincede ngenxa yegama lakho, Usisindise, Usihlambulule ezonweni zethu ngenxa yegama lakho.', 'XHO22'),
(23, 79, 10, 'Yini na ukuba zithi iintlanga, Uphi na uThixo wabo? Makwazeke phakathi kweentlanga phambi kwamehlo ethu, impindezelo yegazi labakhonzi bakho abaphalaziweyo.', 'XHO22'),
(23, 79, 11, 'Makufike phambi kwakho ukuncwina kombanjwa; Khusela abafileyo ngamandla akho amakhulu;', 'XHO22'),
(23, 79, 12, 'Abamelwane bethu yibeke ezifubeni zabo izihlandlo zibe sixhenxe, Ukungcikiveka kwabo, bakungcikive ngayo, Yehova;', 'XHO22'),
(23, 79, 13, 'Ngenxa yoko siya kubulela kuwe ngonaphakade, thina bantu bakho, nezimvu zedlelo lakho; Siya kuyixela indumiso yakho kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 80, 1, '(Indumiso ka-Asafu.) Phulaphula, Malusi kaSirayeli, wena mthundeziyo uYosefu njengomhlambi; Wena uhleli phakathi kweekerubhi, luzuko.', 'XHO22'),
(23, 80, 2, 'Phakamisa amandla akho phambi koEfrayim noBhenjamin noManase, uze usisindise;', 'XHO22'),
(23, 80, 3, 'Guquka, Thixo, bukhanyise ubuso bakho; kwaye siya kusindiswa.', 'XHO22'),
(23, 80, 4, 'Yehova, Thixo wemikhosi, Kunini na uqumba ngenxa yemithandazo yabantu bakho?', 'XHO22'),
(23, 80, 5, 'Ubadlisa isonka seenyembezi; wabaseza iinyembezi ezininzi.', 'XHO22'),
(23, 80, 6, 'Ubangela ukuba sixabane nabamelwane bethu, Zisigculele iintshaba zethu.', 'XHO22'),
(23, 80, 7, 'Thixo wemikhosi, silawule, Malukhanye ubuso bakho; kwaye siya kusindiswa.', 'XHO22'),
(23, 80, 8, 'Wavelisa iidiliya eYiputa, wazigxotha iintlanga, wazityala;', 'XHO22'),
(23, 80, 9, 'Ubulungisela indawo, wendelisa iingcambu, walizalisa ilizwe.', 'XHO22'),
(23, 80, 10, 'Zagutyungelwa iintaba ngumthunzi wawo, nemisedare yakhe emasebeni ayo.', 'XHO22'),
(23, 80, 11, 'Wawatwabulula amasebe awo elwandle, namahlamvu awo afika eMlanjeni.', 'XHO22'),
(23, 80, 12, 'Ngoko kutheni na ukuze uzidilize iindonga zeendonga zakhe, ukuze bonke abadlula ngendlela bakugqogqe?', 'XHO22'),
(23, 80, 13, 'Udliwa ziihagu zehlathi, udliwa ziinyamakazi;', 'XHO22'),
(23, 80, 14, 'Buya, Thixo wemikhosi, uqondele usemazulwini, ubone;', 'XHO22'),
(23, 80, 15, 'nesidiliya eso satyala sona sisandla sakho sokunene, isebe oxhaswe ngalo.', 'XHO22'),
(23, 80, 16, 'Itshiswe ngomlilo, yatshatyalaliswa; baya kudaka ngenxa yobugwenxa bobuso bakho.', 'XHO22'),
(23, 80, 17, 'Isandla sakho masibe phezu kwendoda engasekunene kwakho, kunyana womntu owenze nzima.', 'XHO22'),
(23, 80, 18, 'Ke thina asiyi kukufulathela; sisindise, sinqule igama lakho.', 'XHO22'),
(23, 80, 19, 'Yehova, Thixo wemikhosi, sibuyise; Khanyisa ubuso bakho; kwaye siya kusindiswa.', 'XHO22'),
(23, 81, 1, 'Kukumkani waseGiti, umhobe ka-Asafu. Vumani nibonge uThixo, amandla ethu, nivuye ngozuko loThixo kaYakobi.', 'XHO22'),
(23, 81, 2, 'Thabathani ingoma nize negubu, isixhobo somculo esimnandi esinohadi.', 'XHO22'),
(23, 81, 3, 'Vuthelani isigodlo ekuphumeni kwenyanga, Ngexesha elimisiweyo, ngemini yomthendeleko wethu.', 'XHO22'),
(23, 81, 4, 'Kuba lo yayingummiselo kuSirayeli nomthetho kaThixo kaYakobi.', 'XHO22'),
(23, 81, 5, 'Wasivakalisa ke esi singqino kuYosefu, ekundulukeni kwakhe ezweni laseYiputa, ndeva ulwimi endingalwaziyo.', 'XHO22'),
(23, 81, 6, 'Ndawususa umthwalo egxalabeni lam, Zababaleka izandla zakhe emahasheni.', 'XHO22'),
(23, 81, 7, 'Ubukhala ubuntlungu, ndakusindisa; Ndakuphendula esitheni lendudumo, Ndakucikida emanzini aseMeribha. Sela.', 'XHO22'),
(23, 81, 8, 'Yivani, bantu bam, ndiqononondise kuni; Sirayeli, ukuba uthe waphulaphula kum;', 'XHO22'),
(23, 81, 9, 'Makungabikho thixo wasemzini phakathi kwenu; ninganquli thixo wumbi.', 'XHO22'),
(23, 81, 10, 'NdinguYehova uThixo wakho, owakukhuphayo ezweni laseYiputa; vula umlomo wakho, ndiwuzalise.', 'XHO22'),
(23, 81, 11, 'Ke bona abantu bam abaliphulaphula izwi lam; yaye oonyana bakaSirayeli abazange bafune nabani na kum.', 'XHO22'),
(23, 81, 12, 'Ngoko ke ndabanikela kwiminqweno yeentliziyo zabo, baza bahamba ngokwecebo labo.', 'XHO22'),
(23, 81, 13, 'Akwaba abantu bam bebendiva, Akwaba uSirayeli wahamba ngendlela yam!', 'XHO22'),
(23, 81, 14, 'Bendiya kuzoyisa ngokukhawuleza iintshaba zabo ndize ndinikele isandla sam nxamnye nabo bachasene nabo.', 'XHO22'),
(23, 81, 15, 'Abo bamthiyileyo uYehova bebeya kuzithoba kuye, kodwa ixesha labo liya kuba ngunaphakade.', 'XHO22'),
(23, 81, 16, 'Ufanele kubondla ngeyona ngqolowa ilungileyo, mna ndinihluthise ngobusi obuphuma eweni.', 'XHO22'),
(23, 82, 1, 'Indumiso ka-Asafu. uThixo umi ebandleni lamagorha; ugweba phakathi koothixo.', 'XHO22'),
(23, 82, 2, 'Kunini na ugweba ngobugqwetha, Umkholisa ongendawo? Sela.', 'XHO22'),
(23, 82, 3, 'Khusela oxhwalekileyo neenkedama, wenze ubulungisa kwabaxhwalekileyo nabangamahlwempu.', 'XHO22'),
(23, 82, 4, 'Sindisa olusizana nolihlwempu, Bahlangule esandleni songendawo.', 'XHO22'),
(23, 82, 5, 'Abazi, kwaye abayi kuqonda; bahamba ebumnyameni, zigungxule zonke iziseko zehlabathi.', 'XHO22'),
(23, 82, 6, 'Ndathi, Noothixo; yaye nonke ningoonyana bOyena Uphakamileyo.', 'XHO22'),
(23, 82, 7, 'Kodwa niya kufa njengabantu, niwe njengomnye weenkosana.', 'XHO22'),
(23, 82, 8, 'Khawusuk’ ume, Thixo, gweba elimiweyo; Ngokuba uya kuzidla ilifa wena zonke iintlanga.', 'XHO22'),
(23, 83, 1, 'Ingoma ka-Asafu, musa ukuthi cwaka, Thixo; musa ukuthi cwaka, ungathi cwaka, Thixo.', 'XHO22'),
(23, 83, 2, 'Ngokuba, yabona, iintshaba zakho ziyaxokozela, nabakuthiyayo baphakamise iintloko zabo.', 'XHO22'),
(23, 83, 3, 'Bacebe abantu bakho amayelenqe, benza iyelenqe ngabantu bakho;', 'XHO22'),
(23, 83, 4, 'Bathi, Yizani, siwatshabalalise, abe luhlanga; ukuze lingabi sakhunjulwa igama likaSirayeli.', 'XHO22'),
(23, 83, 5, 'Kuba bavumelene, benza umnqophiso nxamnye nawe;', 'XHO22'),
(23, 83, 6, 'nezindlu zakwaEdom namaIshmayeli; amaMowabhi namaHagari;', 'XHO22'),
(23, 83, 7, 'iGebhali, noAmon, noAmaleki; amaFilisti nabemi baseTire;', 'XHO22'),
(23, 83, 8, 'Bazingenela ke, bazingela oonyana bakaLote; Sela.', 'XHO22'),
(23, 83, 9, 'Yenza kuwo njengoko wenzayo kumaMidiyan; ooSisera noYabhin, entlanjeni yaseKishon;', 'XHO22'),
(23, 83, 10, 'abatshabalalayo e-Enedore, Baba ngumgquba womhlaba.', 'XHO22'),
(23, 83, 11, 'Iinkosana zabo mazibe njengo-Orebhe noZebhe, nazo zonke iinkosana zabo zibe njengoShebha noTsalimuna;', 'XHO22'),
(23, 83, 12, 'bathi, Masizithimbe izindlu zikaThixo.', 'XHO22'),
(23, 83, 13, 'Thixo wam, benze njengevili; njengeendiza phambi komoya.', 'XHO22'),
(23, 83, 14, 'Njengomlilo utshisa iinkuni, iintaba zitshiswa lilangatye;', 'XHO22'),
(23, 83, 15, 'Ngoku ke bakhukuliseni ngoqhwithela lwakho, Ubakhwankqise ngesiphango sakho.', 'XHO22'),
(23, 83, 16, 'Zalisa ubuso babo ngokudana; ukuze balifune igama lakho, Yehova.', 'XHO22'),
(23, 83, 17, 'Mabadane, bahlale besoyika; ewe, mabame badane, bathi shwaka;', 'XHO22'),
(23, 83, 18, 'Ukuze abantu bazi ukuba wena, ogama linguYehova, nguwe wedwa Oyena Uphakamileyo phezu kwehlabathi lonke.', 'XHO22'),
(23, 84, 1, 'Kumvumi waseGiti, Ingoma yoonyana bakaKora;', 'XHO22'),
(23, 84, 2, 'Umphefumlo wam uwakhumbule amasimi kaYehova, efile; Intliziyo yam nenyama yam ikhala kuThixo ophilileyo.', 'XHO22'),
(23, 84, 3, 'Intaka mayiyifumanele indlu, ifumane indluindlu yayo, apho ingabeka khona amathole ayo, izibingelelo zakho, Yehova wemikhosi, Nkosi yam, Thixo wam.', 'XHO22'),
(23, 84, 4, 'Hayi, uyolo lwabahlala endlwini yakho; baya kuhlala bekudumisa. Sela.', 'XHO22'),
(23, 84, 5, 'Hayi, uyolo lomntu omandla akhe akuwe; abandlela zisentliziyweni yabo.', 'XHO22'),
(23, 84, 6, 'Odlula entilini yeBhaka, yenzeni iqula; imvula ikwazalisa amadama.', 'XHO22'),
(23, 84, 7, 'Bayanda amandla, elowo uzibonisa iZiyon phambi koThixo.', 'XHO22'),
(23, 84, 8, 'Yehova, Thixo wemikhosi, wuve umthandazo wam; yiva, Thixo kaYakobi! Sela.', 'XHO22'),
(23, 84, 9, 'Owu Thixo, uyibonile ingweletshetshe yethu, Ububonile ubuso bomthanjiswa wakho.', 'XHO22'),
(23, 84, 10, 'Ngokuba imini enye ezintendelezweni zakho ilungile kunewaka; Ndinyule ukuba semnyango endlwini kaThixo wam, Kunokuhlala ngaphakathi kweentente zokungendawo.', 'XHO22'),
(23, 84, 11, 'Ngokuba ulilanga nekhaka uYehova uThixo; uYehova uya kubabala, azukise; Akayi kubandeza okulungileyo kwabahamba ngobulungisa.', 'XHO22'),
(23, 84, 12, 'Yehova wemikhosi, Hayi, uyolo lomntu okholose ngawe!', 'XHO22'),
(23, 85, 1, 'Kumvumi, ingoma yoonyana bakaKora.', 'XHO22'),
(23, 85, 2, 'Ububuxolela ubugwenxa babantu bakho, Ubuziselela zonke izono zabo. Sela.', 'XHO22'),
(23, 85, 3, 'Wena ukurhole wonke umsindo wakho; Ukujikile ekuvutheni komsindo wakho.', 'XHO22'),
(23, 85, 4, 'Guquka, Thixo ongumsindisi wethu, uwuphelise kuthi umsindo wakho.', 'XHO22'),
(23, 85, 5, 'Uya kuqumbela ngonaphakade na? Ngaba iUU iya kusasaza ingqumbo ukusuka kwesinye isizukulwana ukuya kwesinye?', 'XHO22'),
(23, 85, 6, 'Awufuni na ukusiphilisa kwakhona, ukuze abantu bakho bavuye ngenxa yakho?', 'XHO22'),
(23, 85, 7, 'Yehova, sibonise inceba yakho, siphe usindiso lwakho.', 'XHO22'),
(23, 85, 8, 'Ndiya kuva oko akuthethayo uYehova uThixo, ngenxa enokuba ethetha uxolo kubantu bakhe nakwinceba yakhe; kodwa musani ukubuyela ekusweleni ukuqonda.', 'XHO22'),
(23, 85, 9, 'Inene, lusondele usindiso lwakhe kwabamoyikayo; ukuze uzuko luhlale ezweni lakowethu.', 'XHO22'),
(23, 85, 10, 'Inceba nenyaniso zimanye; ubulungisa noxolo ziyancamisana.', 'XHO22'),
(23, 85, 11, 'Inyaniso iya kukhula ehlabathini; kwaye ubulungisa buya kuhla buphuma ezulwini.', 'XHO22'),
(23, 85, 12, 'Ewe, uYehova uya kusinika okulungileyo; ilizwe lethu livelise iziqhamo zalo.', 'XHO22'),
(23, 85, 13, 'Ubulungisa buya kumkhaphela; yaye uya kusikhokelela endleleni yokunyathela kwakhe.', 'XHO22'),
(23, 86, 1, 'Umthandazo kaDavide.', 'XHO22'),
(23, 86, 2, 'Wusindise umphefumlo wam; ngokuba ndingcwele mna; Thixo wam, msindise umkhonzi wakho lo ukholose ngawe.', 'XHO22'),
(23, 86, 3, 'Ndibabale, Nkosi yam, Ngokuba ndidanduluka kuwe yonke imini.', 'XHO22'),
(23, 86, 4, 'Wuvuyise umphefumlo womkhonzi wakho, Ngokuba ndiwuphakamisela kuwe, Nkosi yam, umphefumlo wam.', 'XHO22'),
(23, 86, 5, 'Ngenxa yokuba wena, Yehova, ulungile, ukulungele ukuxolela; nenceba enkulu kubo bonke abakunqulayo.', 'XHO22'),
(23, 86, 6, 'Yehova, wuve umthandazo wam; uliphulaphule izwi lomthandazo wam.', 'XHO22'),
(23, 86, 7, 'Ngemini yokubandezelwa kwam ndinqula wena, ngokuba uya kundiphendula.', 'XHO22'),
(23, 86, 8, 'Phakathi koothixo akukho unjengawe, Yehova; kwaye akukho misebenzi ifana neyakho.', 'XHO22'),
(23, 86, 9, 'Zonke iintlanga ozenzileyo ziya kuza zize ziqubude phambi kwakho, Yehova; kwaye ndiya kulizukisa igama lakho.', 'XHO22'),
(23, 86, 10, 'Ngokuba umkhulu, wenza ngokubalulekileyo; Wena wedwa unguThixo.', 'XHO22'),
(23, 86, 11, 'Ndiyalele, Yehova, indlela yakho; Ndiya kuhamba enyanisweni yakho; yibandakanye intliziyo yam ukuba ndiloyike igama lakho.', 'XHO22'),
(23, 86, 12, 'Ndiya kubulela kuwe, Yehova, Thixo wam, ngentliziyo yonke; kwaye ndiya kulizukisa igama lakho ngonaphakade.', 'XHO22'),
(23, 86, 13, 'Ngokuba inceba yakho ibe inkulu kum, wawusindisa umphefumlo wam endlwini yofileyo.', 'XHO22'),
(23, 86, 14, 'Thixo, abakhukhumali basukele phezulu kum; akwayibeka phambi kwabo.', 'XHO22'),
(23, 86, 15, 'Ke wena, Yehova, unguThixo onemfesane, unobubele, uyiqinisele intliziyo, umkhulu ngenceba nenyaniso.', 'XHO22'),
(23, 86, 16, 'Guquka, undibabale; mnike amandla umkhonzi wakho, Msindise unyana womkhonzazana wakho.', 'XHO22'),
(23, 86, 17, 'Ndiboniseni umqondiso olungileyo; ukuze abandithiyileyo babone, badane, ngokuba ukholose ngam, Yehova, wandithuthuzela.', 'XHO22'),
(23, 87, 1, '(Ingoma yoonyana bakaKora). Isiseko sakhe sisezintabeni ezingcwele.', 'XHO22'),
(23, 87, 2, 'Amasango eZiyon uYehova uwathanda ngaphezu kwezakhiwo zonke zikaYakobi.', 'XHO22'),
(23, 87, 3, 'Kuthethwa iinto ezizukileyo ngawe, Mzi kaThixo. Sela.', 'XHO22'),
(23, 87, 4, 'uRahabhi noBhabheli ndiya kubabiza kwabandaziyo; Yabona, iFilisti neTire, kunye noKushi; Le ndoda izalelwe khona.', 'XHO22'),
(23, 87, 5, 'Kuya kuthiwa ngeZiyon, Lo mntu nalowa wazalelwa khona, yaye uMthetheli Omkhulu uya kukuqinisekisa oko.', 'XHO22'),
(23, 87, 6, 'uYehova, xa ebhalela abantu, uya kufunda ukuba lo mntu wazalelwa khona. Sela.', 'XHO22'),
(23, 87, 7, 'Ziya kubakho khona iimvumi neemvumi; Onke amathende am anawe.', 'XHO22'),
(23, 88, 1, 'Ingoma yoonyana bakaKore, nemvumi kaMahalate-Lenon, ingoma kaHeman Moesra. Yehova, Thixo ondisindisayo, Ndikhala phambi kwakho ubusuku nemini.', 'XHO22'),
(23, 88, 2, 'Mawuze phambi kwakho umthandazo wam; kubekele indlebe ukuhlahlamba kwam;', 'XHO22'),
(23, 88, 3, 'Ngokuba umphefumlo wam uhluthi zizinto ezimbi, Ubomi bam bufike esichengeni sokufa.', 'XHO22'),
(23, 88, 4, 'Ndibalelwe nabahle baya emhadini; Ndifana nendoda engenamandla.', 'XHO22'),
(23, 88, 5, 'Bakhululwe kwabafileyo, njengababuleweyo, belele engcwabeni, ongasabakhumbuliyo, abanqunyulweyo esandleni sakho.', 'XHO22'),
(23, 88, 6, 'Undibeke emhadini ongaphantsi, esithokothokweni, enzonzobileni.', 'XHO22'),
(23, 88, 7, 'Umsindo wakho unam, Undibethele onke amaza akho alwayo. Sela.', 'XHO22'),
(23, 88, 8, 'Abo ndibaziyo ubasusile kum; Undenze ndalisikizi kubo; Ndivalelwe ngaphandle, andikwazi ukuphuma.', 'XHO22'),
(23, 88, 9, 'Iliso lam liyalila kukubandezeleka, Ndidanduluka kuwe, Yehova, yonke imihla, Ndizolulela kuwe izandla zam.', 'XHO22'),
(23, 88, 10, 'Uyathanda na ukwenza imiqondiso kwabafileyo? Baya kuvuka abafileyo na, babulele kuwe? Sela.', 'XHO22'),
(23, 88, 11, 'Inceba yakho uyixelelwa kwabafileyo na? Niya kutshabalala na?', 'XHO22'),
(23, 88, 12, 'Yaziswa emnyameni na imisebenzi yakho ebalulekileyo? Nobulungisa bakho ehlabathini lonke na?', 'XHO22'),
(23, 88, 13, 'Ke mna ndadanduluka kuwe, Yehova; nomthandazo wam uya kunikhusela ngomso.', 'XHO22'),
(23, 88, 14, 'Uwulahlelani na, Yehova, umphefumlo wam? uyabusithelisa ubuso bakho kum?', 'XHO22'),
(23, 88, 15, 'Ndilusizana, ndiyinto efayo kwasebuncinaneni bam; ngelixa ndinyamezele izoyikiso zakho, ndididekile.', 'XHO22'),
(23, 88, 16, 'Ubushushu bakho buphezu kwam; Ukoyika kwakho kundidlile.', 'XHO22'),
(23, 88, 17, 'Zindijikelezile njengamanzi imini yonke; Zandirhawula kunye.', 'XHO22'),
(23, 88, 18, 'Intanda nomhlobo ubasusile kude kum, abo ndibaziyo ebumnyameni.', 'XHO22'),
(23, 89, 1, 'Iinceba zikaYehova ndiya kuvuma ngazo ngonaphakade; Ndiya kuxela ukuthembeka kwakho ngomlomo wam kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 89, 2, 'Ngokuba ndithi, Inceba iya kwakhelwa ngonaphakade; Ukuthembeka kwakho kuya kukugcinela emazulwini.', 'XHO22'),
(23, 89, 3, 'Ndifungele umnyulwa wam umnqophiso, ndifungele umkhonzi wam uDavide,', 'XHO22'),
(23, 89, 4, 'Ndiya kuyizimasa ngonaphakade imbewu yakho, Ndiyakhe itrone yakho kwizizukulwana ngezizukulwana. Sela.', 'XHO22'),
(23, 89, 5, 'Yehova, izulu liya kuyincoma imisebenzi yakho ebalulekileyo, nawe tshata ebandleni labangcwele.', 'XHO22'),
(23, 89, 6, 'Kuba ngubani na ezulwini onokuthelekiswa noYehova? Ngubani phakathi koonyana bengxilimbela onokuthelekiswa noYehova?', 'XHO22'),
(23, 89, 7, 'Uyoyikeka kakhulu uThixo ebandleni labangcwele, Uyoyikeka kubo bonke abamkhawulelayo.', 'XHO22'),
(23, 89, 8, 'Yehova, Thixo wemikhosi, Ngubani na uYehova onjengawe, oligorha? Ukuthembeka kwakho kukujikelezile?', 'XHO22'),
(23, 89, 9, 'Ulobukumkani phezu kolwandle olulwayo; Ekuphakameni kwamaza akho, uyawadambisa.', 'XHO22'),
(23, 89, 10, 'Nguwena wamtyumza uRahabhi njengowisiweyo; Uzithi saa iintshaba zakho ngesandla sakho esithe nkqi.', 'XHO22'),
(23, 89, 11, 'Lelakho izulu, likwalelakho ihlabathi; ihlabathi nenzaliseko yalo, Wadala lona.', 'XHO22'),
(23, 89, 12, 'Uphakamisile umntla nomzantsi, iTabhore neHermon zivuye egameni lakho.', 'XHO22'),
(23, 89, 13, 'Unesandla esithe nkqi, isandla sakho somelele, esokunene sakho siphakamile.', 'XHO22'),
(23, 89, 14, 'Isitulo setrone yakho bubulungisa nobulungisa; inceba nenyaniso iya kukukhokela.', 'XHO22'),
(23, 89, 15, 'Hayi, uyolo lwabantu abakwaziyo ukonwaba; baya kuhamba ekukhanyeni kobuso bakho, Yehova.', 'XHO22'),
(23, 89, 16, 'Baya kugcoba egameni lakho imini yonke, baphakame ngobulungisa bakho.', 'XHO22'),
(23, 89, 17, 'Ngokuba isihombo samandla abo nguwe, Uphondo lwethu luphakamile ngokuthanda kwakho.', 'XHO22'),
(23, 89, 18, 'Ngokuba uYehova uyindawo yethu yokusabela; OyiNgcwele kaSirayeli ukumkani wethu.', 'XHO22'),
(23, 89, 19, 'Wathetha kowengcwele wakho ngombono, wathi, Ndincede, ndandoyisa; Ndimphakamisile umnyulwa phakathi kwezizwe.', 'XHO22'),
(23, 89, 20, 'Ndimfumene umkhonzi wam uDavide; ndamthambisa ngeoli yam engcwele.', 'XHO22'),
(23, 89, 21, 'Ngubani na owositywina isandla sam; kwaye isandla sam siya kumomeleza.', 'XHO22'),
(23, 89, 22, 'Utshaba aluyi kuthetha kuye; ke yena unyana wobugqwetha akamcinezeli.', 'XHO22'),
(23, 89, 23, 'Ndiza kuzoyisa iintshaba zakhe phambi kobuso bakhe, ndibathwaxe kakubi abo bamthiyileyo.', 'XHO22'),
(23, 89, 24, 'Kodwa inyaniso yam nenceba yam inaye, nophondo lwakhe luphakame egameni lam.', 'XHO22'),
(23, 89, 25, 'Ndiya kusisa elwandle isandla sakhe, Emilanjeni esokunene sakhe.', 'XHO22'),
(23, 89, 26, 'Yena uya kundibiza, athi, Bawo, unguThixo wam, neliwa lokundisindisa.', 'XHO22'),
(23, 89, 27, 'Ndiya kumenza owamazibulo, Osenyangweni kookumkani behlabathi.', 'XHO22'),
(23, 89, 28, 'Ndiya kumgcinela inceba yam ngamaxesha onke, Ungabi naye umnqophiso wam;', 'XHO22'),
(23, 89, 29, 'Ndiya kuyenza igcineke imbewu yakhe, netrone yakhe ibe njengemihla yamazulu.', 'XHO22'),
(23, 89, 30, 'Bathe oonyana bakhe babuya emyalelweni wam, abahamba ngemimiselo yam;', 'XHO22'),
(23, 89, 31, 'Xa bathe bayaphula imithetho yam, abayigcina imithetho yam;', 'XHO22'),
(23, 89, 32, 'Ndoluvelela ukreqo lwabo ngentonga, nobubi babo ngemivumbo;', 'XHO22'),
(23, 89, 33, 'Kodwa andiyi kubuhlutha ububele bam kuye, yaye andiyi kubuyeka ukuthembeka kwam.', 'XHO22'),
(23, 89, 34, 'Andiyi kuwaphula umnqophiso wam, nelizwi eliphume emlonyeni wam andiyi kulijika.', 'XHO22'),
(23, 89, 35, 'Ndafunga kwangaxesha lithile ngobungcwele bam, ndathi, Inene, andiyi kumxokisa uDavide;', 'XHO22'),
(23, 89, 36, 'Oonyana bakhe baya kuphila ngonaphakade, Netrone yakhe njengelanga phambi kwam.', 'XHO22'),
(23, 89, 37, 'Iya kuma ngonaphakade njengenyanga, ibe lingqina elithembekileyo emazulwini. Sela.', 'XHO22'),
(23, 89, 38, 'Kodwa wena wamgatya waza wamthiya, waqumbela umthanjiswa wakho.', 'XHO22'),
(23, 89, 39, 'Uwaphule umnqophiso womkhonzi wakho; Usixabhele isithsaba sakhe ngokusisa phantsi.', 'XHO22'),
(23, 89, 40, 'Izitshabalalisile zonke iinqaba zakhe; Uziphanzisile iinqaba zalo.', 'XHO22'),
(23, 89, 41, 'Bayamphanga bonke abadlula ngendlela; Ulihlazo kummelwane wakhe.', 'XHO22'),
(23, 89, 42, 'Usiphakamisile isandla sokunene sababandezeli bakhe; Uzivuyisile zonke iintshaba zakhe.', 'XHO22'),
(23, 89, 43, 'Ubuye waluguqula uhlangothi lwekrele lakhe, akwamyeka ekulweni;', 'XHO22'),
(23, 89, 44, 'Niluphelisile uzuko lwakhe, Netrone yakhe niyiwisele phantsi emhlabeni;', 'XHO22'),
(23, 89, 45, 'Uyinqamle imihla yobutsha bakhe; Umdanisile. Sela.', 'XHO22'),
(23, 89, 46, 'Koda kube nini na, Yehova? Uya kuzifihla ngonaphakade na? Uvutha njengomlilo na umsindo wakho?', 'XHO22'),
(23, 89, 47, 'Khumbula ukuba ixesha lam lifinyele;', 'XHO22'),
(23, 89, 48, 'Ngubani na umntu ongenakububona ubomi nokufa? Uya kuwusindisa na umphefumlo wakhe esandleni selabafileyo? Sela.', 'XHO22'),
(23, 89, 49, 'Zingaphi na iinceba zakho zangaphambili, Yehova, Owazithethayo kuDavide ngokuthembeka kwakho?', 'XHO22'),
(23, 89, 50, 'Owu, Nkosi, khumbula ukudana kwabakhonzi bakho; Ndothini na ukuthwala ingcikivo yamagorha onke esifubeni sam;', 'XHO22'),
(23, 89, 51, 'Ezo iintshaba zakho zimnyelise nabo, Yehova! abawanyelise ngawo amanyathelo omthanjiswa wakho.', 'XHO22'),
(23, 89, 52, 'Makabongwe uYehova ngonaphakade. Amen, kwaye Amen.', 'XHO22'),
(23, 90, 1, '(Umthandazo kaMoses, mfo wakwaThixo.) Nkosi, wena ulikhaya lethu kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 90, 2, 'Iintaba azikazalwa, kungakhathaliseki nokuba uyalidala na ihlabathi nehlabathi, ukususela kwixesha elingenammiselo ukusa kwixesha elingenammiselo, wena unguThixo.', 'XHO22'),
(23, 90, 3, 'Umrholela umntu entshabalalweni; Wathi, Buyani, nyana babantu!', 'XHO22'),
(23, 90, 4, 'Ngokuba iwaka leminyaka emehlweni akho linjengemini yezolo, xa iphela, nanjengomlindo wasebusuku.', 'XHO22'),
(23, 90, 5, 'Uyabaqweqwedisa njengoMkhukula; bafana nobuthongo; kusasa banjengengca.', 'XHO22'),
(23, 90, 6, 'Kusasa buyatyatyamba, buhlume; ngokuhlwa iyasikwa yomiswe.', 'XHO22'),
(23, 90, 7, 'Ngenxa yokuba sibhangisiwe ngumsindo wakho, siyawoyika umsindo wakho.', 'XHO22'),
(23, 90, 8, 'Ububeke ubugwenxa bethu phambi kwakho, Nezono zethu uzibeke ekukhanyeni kobuso bakho.', 'XHO22'),
(23, 90, 9, 'Ngokuba yonke imihla yethu iphele ngomsindo wakho; iminyaka yethu siyichitha njengebali elibaliswayo.', 'XHO22'),
(23, 90, 10, 'Imihla yeminyaka yethu yiminyaka elishumi elinamithandathu; kwaye ukuba yiminyaka engamashumi asibhozo ngenxa yobunzima, kwangokunjalo amandla ayo ngumsebenzi nosizi; kuba iza kunqunyulwa ngokukhawuleza, kwaye siyayibhabha.', 'XHO22'),
(23, 90, 11, 'Ngubani na owaziyo amandla omsindo wakho? ngokoyikeka kwenu, ukwanjalo umsindo wenu.', 'XHO22'),
(23, 90, 12, 'Ngoko ke sifundise ukuyibala imihla yethu, khon’ ukuze sizuze ubulumko.', 'XHO22'),
(23, 90, 13, 'Buya, Yehova, kude kube nini na? ibe yeyabakhonzi bakho.', 'XHO22'),
(23, 90, 14, 'Sihluthise kwakusasa ngenceba yakho; ukuze sigcobe, sivuye yonke imihla yethu.', 'XHO22'),
(23, 90, 15, 'Usivuyele njengemihla owasicinezela ngayo, Nangokweminyaka esabona ububi ngayo.', 'XHO22'),
(23, 90, 16, 'Mayibonakale kubakhonzi bakho imisebenzi yakho, Nozuko lwakho koonyana babo.', 'XHO22'),
(23, 90, 17, 'Ukulunga kukaYehova uThixo wethu makube kuthi, kusiqinisele isenzo sezandla zethu; ewe, umsebenzi wezandla zethu uyangqina.', 'XHO22'),
(23, 91, 1, 'Ulolala emthunzini woSomandla.', 'XHO22'),
(23, 91, 2, 'Ndithi ke kuYehova, Uyindawo yam yokusabela, negwiba lam; Ndiya kumthemba.', 'XHO22'),
(23, 91, 3, 'Ewe, uya kukuhlangula emgibeni womthiyeli, nakwindyikitya yokufa ebetha intliziyo.', 'XHO22'),
(23, 91, 4, 'Wokugubungela ngeentsiba zakhe, Ukholose phantsi kwamaphiko akhe; Yingweletshetshe yakho nengweletshetshe yakhe.', 'XHO22'),
(23, 91, 5, 'Musa ukoyika ngenxa yonkwantyiso lwasebusuku; nokuba ungumzingeli obhabha emini;', 'XHO22'),
(23, 91, 6, 'kananjalo asiyindyikitya yokufa ehamba ebumnyameni; nokuba yinkunkuma echitha imini.', 'XHO22'),
(23, 91, 7, 'Kuya kuwa iwaka ecaleni kwakho, neshumi lamawaka ngasekunene kwakho; kodwa ayiyi kusondela kuwe.', 'XHO22'),
(23, 91, 8, 'Uya kubona kuphela ngamehlo akho, Uwubone umvuzo wongendawo.', 'XHO22'),
(23, 91, 9, 'Ngokuba umenzile uYehova, indawo yam yokusabela, umenze Oyena Uphakamileyo waba likhaya lakho;', 'XHO22'),
(23, 91, 10, 'Akuyi kwehlelwa bubi, ukufa akuyi kusondela ententeni yakho.', 'XHO22'),
(23, 91, 11, 'Ngokuba uya kuziwisela umthetho izithunywa zakhe ukuba zikugcine ezindleleni zakho zonke.', 'XHO22'),
(23, 91, 12, 'Ziya kukuthwala ngezandla, Hleze ubetheke etyeni unyawo lwakho.', 'XHO22'),
(23, 91, 13, 'Uya kuzinyathela ingonyama nezizwe; uya kunyathela ingonyama nenamba.', 'XHO22'),
(23, 91, 14, 'Ngokuba endithanda, ndomsindisa; Ndiya kumphakamisa, ngokuba elazi igama lam.', 'XHO22'),
(23, 91, 15, 'Wondibongoza, ndiphendule, Ndoba naye embandezelweni; ndiya kumkhulula ngentlawulelo, ndiya kumzukisa;', 'XHO22'),
(23, 91, 16, 'Ndiya kumanelisa imihla emide, ndilubone usindiso lwam.', 'XHO22'),
(23, 92, 1, '(Indumiso okanye Ingoma yomhla wesabatha.) Kulungile ukudumisa uYehova nokuculela igama lakho, Wena usenyangweni!', 'XHO22'),
(23, 92, 2, 'Ukutyhila inceba yakho kwakusasa, Nokuthembeka kwakho ubusuku bonke;', 'XHO22'),
(23, 92, 3, 'Umrhubhe ontambo zilishumi, nomrhubhe; ngohadi ngesandi esimnandi.', 'XHO22'),
(23, 92, 4, 'Ngokuba uyandivuyisa, Yehova, ngomsebenzi wakho; ndiya kukoyisa ngemisebenzi yezandla zakho.', 'XHO22'),
(23, 92, 5, 'Hayi, ukuba zininzi izenzo zakho, Yehova! zinzulu kakhulu iingcinga zakho.', 'XHO22'),
(23, 92, 6, 'Umntu owoyikayo akazi; kwaye isidenge asiyiqondi.', 'XHO22'),
(23, 92, 7, 'Ekukhuleni kongendawo njengengca, Naxa bonke abenzi bobubi bephumelela; oko kukuthi, baya kutshatyalaliswa ngonaphakade.', 'XHO22'),
(23, 92, 8, 'Kodwa wena, Yehova, ungOyena Uphakamileyo ukusa kwixesha elingenammiselo.', 'XHO22'),
(23, 92, 9, 'Ngokuba uyabona, uYehova iintshaba zakho, ngokuba, uyabona, iintshaba zakho ziva kutshabalala; bonke abenzi bobubi baya kutshabalala.', 'XHO22'),
(23, 92, 10, 'Uya kuluphakamisa uphondo lwam njengolwenqu; Ndigalelwe ioli entsha.', 'XHO22'),
(23, 92, 11, 'Iliso lam liya kukubona ukunqwenela kwam izenzo zam, iindlebe zam ziya kuva abasukela phezulu kum abangendawo.', 'XHO22'),
(23, 92, 12, 'Ilungisa liya kuphumelela njengomthi wesundu, njengomsedare waseLebhanon.', 'XHO22'),
(23, 92, 13, 'Abo batyalwe endlwini kaYehova baya kuphumelela ezintendelezweni zoThixo wethu.', 'XHO22'),
(23, 92, 14, 'Baya kuqhubeka bethwala isiqhamo ebudaleni; baya kutyeba, bophe;', 'XHO22'),
(23, 92, 15, 'Ukuze abonise ukuba ulilungisa uYehova: Uliwa lam, akukho bubi kuye.', 'XHO22'),
(23, 93, 1, 'uYehova ngukumkani, sambethe ngobuhle; uYehova wambathise ubugorha abebhambathise ngabo; Liqinile ihlabathi, ngokokude lingabi nakushukuma.', 'XHO22'),
(23, 93, 2, 'Itrone yakho iqinile kususela kwaphakade, wena wawukho kususela kwaphakade.', 'XHO22'),
(23, 93, 3, 'Imilambo iphakamisile, Yehova, Imilambo iphakamisile ingxolo yayo; Imilambo iwaphakamisa amaza ayo.', 'XHO22'),
(23, 93, 4, 'uYehova unamandla ngaphezu kwesandi samanzi amaninzi, Ngaphezu kwamaza alatyuzayo olwandle.', 'XHO22'),
(23, 93, 5, 'Anyanisekile ke amacebo akho: Ingcwele indlu yakho, Yehova, kude kuse ephakadeni.', 'XHO22'),
(23, 94, 1, 'uYehova uThixo, impindezelo yeyakhe; Zibonise Thixo, impindezelo;', 'XHO22'),
(23, 94, 2, 'Ziphakamise, Mgwebi wehlabathi, Ubavuze abanekratshi.', 'XHO22'),
(23, 94, 3, 'Yehova, kuya kude kube nini na abangendawo bekho, kuya kude kube nini na abangendawo bekhona?', 'XHO22'),
(23, 94, 4, 'Kunini na bethetha amazwi arhabaxa? Baqhayise bonke abangendawo?', 'XHO22'),
(23, 94, 5, 'Yehova, bayabaxovula abantu bakho, balicinezele ilifa lakho.', 'XHO22'),
(23, 94, 6, 'Babulala umhlolokazi nowasemzini, Nenkedama babulala.', 'XHO22'),
(23, 94, 7, 'Bathi ke bona, uYehova akaboni, akaboni uThixo kaYakobi.', 'XHO22'),
(23, 94, 8, 'Qondani, nina zinyabi phakathi kwabantu; nani zinyabi, niya kulumka nini na?', 'XHO22'),
(23, 94, 9, 'Onendlebe akayi kuva na? Lowo wadala iliso, akayi kubona na?', 'XHO22'),
(23, 94, 10, 'Lowo wohlwaya iintlanga, akayi kuzohlwaya na? Ofundisa abantu ukwazi, akayi kukwazi na?', 'XHO22'),
(23, 94, 11, 'uYehova uyazazi iingcinga zabantu, ukuba zingumphunga.', 'XHO22'),
(23, 94, 12, 'Yehova, hayi, uyolo lomntu okuthethisayo, omfundisa umyalelo wakho;', 'XHO22'),
(23, 94, 13, 'ukuze umphumze ngeemini zobubi, ade ambelwe isihogo ongendawo.', 'XHO22'),
(23, 94, 14, 'Kuba uYehova akayi kubagxotha abantu bakhe, engayi kulishiya nelifa lakhe.', 'XHO22'),
(23, 94, 15, 'Kodwa okusesikweni kuya kubuyela ebulungiseni, kwaye bonke abantliziyo zilungileyo bayakulandela.', 'XHO22'),
(23, 94, 16, 'Ngubani na oya kumelana nabenzi bobubi, ngenxa yam? okanye ngubani na oya kumelana nam nxamnye nabenzi bobubi?', 'XHO22'),
(23, 94, 17, 'Ukuba akathanga uYehova andincede, Umphefumlo wam ubuzola.', 'XHO22'),
(23, 94, 18, 'Xa ndithi, Lwatyibilika unyawo lwam; Yehova, inceba yakho indixhase.', 'XHO22'),
(23, 94, 19, 'Ebuninzini beengcinga-ngcinga zam, iintuthuzelo zakho ziyawuyolisa umphefumlo wam.', 'XHO22'),
(23, 94, 20, 'Ingahlangana nawe na itrone yolweyeliselo, Wena uceba ububi ngomyalelo?', 'XHO22'),
(23, 94, 21, 'Balihlanganyela umphefumlo welungisa, baligwebe igazi elingenatyala.', 'XHO22'),
(23, 94, 22, 'Kodwa uYehova uyindawo yam yokusabela; yaye uThixo wam liliwa endisabela kulo.', 'XHO22'),
(23, 94, 23, 'Kwaye uya kuzisa inkohlakalo yabo phezu kwabo aze abatshabalalise ebubini babo; Ewe, uYehova uThixo wethu uya kubatshabalalisa.', 'XHO22'),
(23, 95, 1, 'Yizani simdumise uYehova, sigcobe eweni losindiso lwethu.', 'XHO22'),
(23, 95, 2, 'Masize phambi kobuso bakhe sinombulelo, simdumise ngeendumiso.', 'XHO22'),
(23, 95, 3, 'Ngokuba uYehova nguThixo omkhulu, yiNkosi enkulu ngaphezu koothixo bonke.', 'XHO22'),
(23, 95, 4, 'Ubunzulu bomhlaba busesandleni sakhe; namandla eentaba ngawakhe.', 'XHO22'),
(23, 95, 5, 'Ulolwakhe ulwandle, nguyena walwenzayo, Nomhlaba owomileyo zawenza izandla zakhe.', 'XHO22'),
(23, 95, 6, 'Yizani, masiqubude, sithobe; masiguqe phambi koYehova, uMdali wethu.', 'XHO22'),
(23, 95, 7, 'Ngokuba nguThixo wethu yena; yaye thina singabantu bedlelo lakhe nezimvu zesandla sakhe. Namhlanje ukuba ufuna ukuva ilizwi lakhe,', 'XHO22'),
(23, 95, 8, 'Musani ukuzenza lukhuni iintliziyo zenu, njengasesihendweni, nasekuhendweni entlango.', 'XHO22'),
(23, 95, 9, 'Bandicikida ooyihlo, bandicikida, bayibona imisebenzi yam.', 'XHO22'),
(23, 95, 10, 'Kangangamashumi amane eminyaka ndibuhlungu ngenxa yesi sizukulwana, ndathi: “Olu luhlanga oluye lwaphambuka ezintliziyweni zalo, yaye abazazi iindlela zam.', 'XHO22'),
(23, 95, 11, 'Ndibafungile ndinomsindo, ukuba abayi kungena ekuphumleni kwam;', 'XHO22'),
(23, 96, 1, 'Vumani kuYehova ingoma entsha; Vumani kuYehova, nonke hlabathi.', 'XHO22'),
(23, 96, 2, 'Vumani kuYehova, lidumiseni igama lakhe; vakalisani usindiso lwakhe imihla ngemihla.', 'XHO22'),
(23, 96, 3, 'Balisani ezintlangeni zonke uzuko lwakhe, Balisani ezizweni zonke imisebenzi yakhe ebalulekileyo.', 'XHO22'),
(23, 96, 4, 'Ngokuba mkhulu uYehova, efanelwe yindumiso eninzi; Uyoyikeka ngaphezu koothixo bonke.', 'XHO22'),
(23, 96, 5, 'Kuba bonke oothixo beentlanga banqula izithixo, kodwa uYehova wenza amazulu.', 'XHO22'),
(23, 96, 6, 'Isihomo nesithozela siphambi kwakhe, amandla nobuhle busengcweleni yakhe.', 'XHO22'),
(23, 96, 7, 'Mnikeni uYehova, nina zintlanga, mnikeni uYehova uzuko namandla;', 'XHO22'),
(23, 96, 8, 'Zisani kuYehova uzuko lwegama lakhe; zidinile, ningene ezintendelezweni zakhe.', 'XHO22'),
(23, 96, 9, 'Mnquleni uYehova ngencasa engcwele; moyikeni, nonke hlabathi.', 'XHO22'),
(23, 96, 10, 'Yithini ezintlangeni, Nithi, uYehova ngukumkani; uya kugweba iintlanga ngobulungisa.', 'XHO22'),
(23, 96, 11, 'Malivuye izulu, ligcobe ihlabathi; Malugqume ulwandle, lugcwale.', 'XHO22'),
(23, 96, 12, 'Intsimi mayivuye, nento yonke ekulo; yandule yonke imithi yehlathi ivuye;', 'XHO22'),
(23, 96, 13, 'Phambi kobuso bukaYehova, ngokuba esiza, ngokuba esiza kuligweba ihlabathi; Uya kuligweba elimiweyo ngobulungisa, Nezizwe ngenyaniso yakhe.', 'XHO22'),
(23, 97, 1, 'uYehova ngukumkani; malibe nemihlali ihlabathi; izihlwele zaseziqithini mazivuye.', 'XHO22'),
(23, 97, 2, 'Ngamafu nesithokothoko ngeenxa zonke kuye, ubulungisa nokusesikweni zisakha itrone yakhe.', 'XHO22'),
(23, 97, 3, 'Kuphuma umlilo phambi kwakhe uze utshise iintshaba zakhe kuyo yonke indawo.', 'XHO22'),
(23, 97, 4, 'Ikhanyisa elimiweyo imibane yakhe, Labona elimiweyo, lanyikima.', 'XHO22'),
(23, 97, 5, 'Iintaba zanyibilika njengomthwebeba phambi koYehova, Phambi kweNkosi yehlabathi lonke.', 'XHO22'),
(23, 97, 6, 'Izulu lixela ubulungisa bakhe, Zibubone ubuqaqawuli bakhe zonke iintlanga.', 'XHO22'),
(23, 97, 7, 'Badanile bonke abakhonza umfanekiso oqingqiweyo, Abaqhayisa ngeento ezingento; mnquleni, nonke oothixo.', 'XHO22'),
(23, 97, 8, 'Ivile iZiyon, yavuya; yaye iintombi zakwaYuda zigcobe ngezigwebo zakho, Yehova!', 'XHO22'),
(23, 97, 9, 'Kuba wena, Yehova, ungaphezu kwehlabathi lonke, ungaphezu koothixo bonke.', 'XHO22'),
(23, 97, 10, 'Nina nithanda uYehova, kuthiyeni okubi; Ulondoloza umphefumlo wabo bamthandayo; Uyabasindisa esandleni sabangendawo.', 'XHO22'),
(23, 97, 11, 'Ukukhanya kuhlwayelwe ilungisa, Uvuyo luhlwayelelwe abantliziyo zithe tye.', 'XHO22'),
(23, 97, 12, 'Vuyani ngoYehova, nina malungisa; kwaye masibe nombulelo ngokukhumbula uzuko lwakhe.', 'XHO22'),
(23, 98, 1, '(iNdumiso.) Mvumeleni ingoma entsha uYehova; ngokuba wenzé imisebenzi ebalulekileyo, isandla sakhe sokunene, nesandla sakhe esingcwele simnike uloyiso.', 'XHO22'),
(23, 98, 2, 'uYehova uluxelile usindiso lwakhe; Wabubonakalisa ubulungisa bakhe emehlweni eentlanga.', 'XHO22'),
(23, 98, 3, 'Wayikhumbula inceba yakhe, nokuthembeka kwakhe kwindlu kaSirayeli; zonke iziphelo zehlabathi ziyalubona ke usindiso loThixo wethu.', 'XHO22'),
(23, 98, 4, 'Dumani kuYehova, nonke hlabathi;', 'XHO22'),
(23, 98, 5, 'Vumani kuYehova ngezwi; ngentambo kunye nelizwi lendumiso.', 'XHO22'),
(23, 98, 6, 'Vuthelani izigodlo nezigodlo phambi koYehova, Yehova.', 'XHO22'),
(23, 98, 7, 'Malugqume ulwandle nokuzala kwalo; ihlabathi abahlala kulo.', 'XHO22'),
(23, 98, 8, 'Mayimemelele imilambo, zivuye kunye iinduli;', 'XHO22'),
(23, 98, 9, 'phambi koYehova; ngokuba weza kugweba ihlabathi; Uya kuligweba elimiweyo ngobulungisa, nezizwe ngobulungisa.', 'XHO22'),
(23, 99, 1, 'uYehova ngukumkani; bangcangcazele abantu! Uhleli phakathi kweekerubhi; malishukume ihlabathi.', 'XHO22'),
(23, 99, 2, 'uYehova mkhulu eZiyon; kwaye ungaphezu kwabantu bonke.', 'XHO22'),
(23, 99, 3, 'Mabalidumise igama lakho elikhulu eloyikekayo; ngokuba ungcwele wena.', 'XHO22'),
(23, 99, 4, 'Ubugorha bokumkani buthanda okusesikweni; Nguye omisela ubulungisa, umenze ngokwesiko nobulungisa kwaYakobi.', 'XHO22'),
(23, 99, 5, 'Mdumiseni uYehova, uThixo wethu, niqubude ezinyaweni zakhe; ngokuba ungcwele yena.', 'XHO22'),
(23, 99, 6, 'uMoses noAron phakathi kwababingeleli bakhe, noSamuweli phakathi kwabalinqulayo igama lakhe; banqula uYehova, wabaphendula.', 'XHO22'),
(23, 99, 7, 'Wathetha kubo efini, Bawagcina amacebo akhe, nemiyalelo yakhe abanika yona.', 'XHO22'),
(23, 99, 8, 'Wabaphendula, Yehova, Thixo wethu, wathi, UnguThixo owaxolelayo, nakuphi na ukuphindezela izenzo zabo.', 'XHO22'),
(23, 99, 9, 'Mdumiseni uYehova uThixo wethu, niqubude entabeni yakhe engcwele; ngokuba ungcwele uYehova uThixo wethu.', 'XHO22'),
(23, 100, 1, 'Umhobe woguquko. Vumani kuYehova, nonke zintlanga.', 'XHO22'),
(23, 100, 2, 'Mkhonzeni uYehova nivuya; esiza phambi kobuso bakhe evuya.', 'XHO22'),
(23, 100, 3, 'Uyazi na ukuba uYehova nguThixo; nguye owasenzayo, asisithi; singabantu bakhe nezimvu zedlelo lakhe.', 'XHO22'),
(23, 100, 4, 'Ngenani emasangweni akhe nibulela, Ezintendelezweni zakhe nibulela; bulelani, nilidumise igama lakhe.', 'XHO22'),
(23, 100, 5, 'Ngokuba uYehova ulungile; Inceba yakhe ingunaphakade; kwaye inyaniso yakhe isuka kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 101, 1, '(Indumiso kaDavide.) Ndiyathanda ukucula ngenceba nobulungisa; Ndiyakuthanda ukucula indumiso yakho, Yehova.', 'XHO22'),
(23, 101, 2, 'Ndiya kwenza ngengqiqo ngendlela egqibeleleyo; Uya kuza nini kum? Ndiya kuhamba endlwini yam ngentliziyo epheleleyo.', 'XHO22'),
(23, 101, 3, 'Andiyi kubeka nanye ububi phambi kwamehlo am; ayinakunamathela kum.', 'XHO22'),
(23, 101, 4, 'Intliziyo enobubi iya kuphuma kum; Abangendawo andiyi kubazi.', 'XHO22'),
(23, 101, 5, 'Omhlebayo ummelwane wakhe ngasese, Lowo ndiya kumbhangisa; Ozidlayo, ozidlayo, andiyi kubandezeleka;', 'XHO22'),
(23, 101, 6, 'Amehlo am akwaba ngabathembekileyo ehlabathini, Ukuba bahlale nam; Ohamba ngendlela ethe tye nguye oya kundikhonza.', 'XHO22'),
(23, 101, 7, 'Lowo ukhohlisayo makangahlali endlwini yam; Othetha ubuxoki akayi kulibala phambi kwamehlo am.', 'XHO22'),
(23, 101, 8, 'Ndiya kubatshabalalisa kuqala bonke abangendawo belizwe; ukuze ndibatshabalalise bonke abangendawo emzini kaYehova.', 'XHO22'),
(23, 102, 1, 'Umthandazo wolusizana xa ephelelwe lithemba, uphalaza iinyembezi zakhe phambi koYehova. Yehova, wuve umthandazo wam, Kufike kuwe ukuzibika kwam.', 'XHO22'),
(23, 102, 2, 'Musa ukubusithelisa ubuso bakho kum ngemini yokubandezelwa kwam; Mamela kum; xa ndibizayo, khawuleza undiphendule.', 'XHO22'),
(23, 102, 3, 'Ngokuba iyaphela imihla yam njengomsi; Amathambo am avutha njengeziko.', 'XHO22'),
(23, 102, 4, 'Yomile njengengca, intliziyo yam yomile; kangangokuba ndilibele nokudla isonka sam.', 'XHO22'),
(23, 102, 5, 'Ngenxa yesandi sokuncwina kwam, Amathambo am athe nca eluswini lwam.', 'XHO22'),
(23, 102, 6, 'Ndifana nexhalanga lasentlango, Ndifana nesikhova sasentlango.', 'XHO22'),
(23, 102, 7, 'Ndakhangela, ndaba njengongqatyana phezu kwendlu;', 'XHO22'),
(23, 102, 8, 'Imini yonke ziyandigiba iintshaba zam; Abandiqumbeleyo bandifungele.', 'XHO22'),
(23, 102, 9, 'Ngokuba ndidle uthuthu njengesonka, Into endiyiselayo ndiyiphithikeze ngeenyembezi;', 'XHO22'),
(23, 102, 10, 'Ngenxa yomsindo wakho nangenxa yomsindo wakho, ngenxa yokuba undifunqule wandiphosa phantsi.', 'XHO22'),
(23, 102, 11, 'Injengethunzi lesithunzi imihla yam; ndoma njengengca.', 'XHO22'),
(23, 102, 12, 'Ke wena, Yehova, uya kuba lapho ngonaphakade; Nesikhumbuzo sakho kwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 102, 13, 'Uya kuvuka uze ube nenceba ngeZiyon, ngenxa yokuba ixesha lokuyikholisa, ewe, ixesha elimisiweyo lifikile.', 'XHO22'),
(23, 102, 14, 'Ngokuba abakhonzi bakho bawavuyelele amatye ayo, Bagcobe ngothuli lwayo.', 'XHO22'),
(23, 102, 15, 'Ziya kuloyika iintlanga igama likaYehova, nookumkani bonke behlabathi baloyike uzuko lwakho.', 'XHO22'),
(23, 102, 16, 'Xa uYehova eyakha iZiyon, uya kubonakala esebuqaqawulini bakhe.', 'XHO22'),
(23, 102, 17, 'Uya kuwuva umthandazo weentsizana, Awucekise umthandazo wabo.', 'XHO22'),
(23, 102, 18, 'Oku kuya kubhalelwa isizukulwana esilandelayo, kuze kudalwe abantu abaza kudumisa uYehova.', 'XHO22'),
(23, 102, 19, 'Ngokuba uqondele esendaweni ephakamileyo yengcwele yakhe; uYehova walibona esezulwini;', 'XHO22'),
(23, 102, 20, 'Ekuveni ukuhlahlamba kombanjwa; ukuba abakhulule abagwetyelwe ukufa;', 'XHO22'),
(23, 102, 21, 'Ukuvakalisa eZiyon igama likaYehova, nendumiso yakhe eYerusalem;', 'XHO22'),
(23, 102, 22, 'Xa iintlanga noorhulumente behlanganisana ukuze bakhonze uYehova.', 'XHO22'),
(23, 102, 23, 'Wawatyhafisa amandla am endleleni; uyinqumle imihla yam.', 'XHO22'),
(23, 102, 24, 'Ndathi, Thixo wam, musa ukundisusa phakathi kwemihla yam; Iminyaka yakho ikwizizukulwana ngezizukulwana.', 'XHO22'),
(23, 102, 25, 'Wena usoloko udala ihlabathi, Namazulu angumsebenzi wezandla zakho.', 'XHO22'),
(23, 102, 26, 'Ezo zinto ziya kudaka, wena ke ume; ewe, bonke baya konakala njengengubo; baya kutshintshwa njengengubo, baya kutshintshwa;', 'XHO22'),
(23, 102, 27, 'Kodwa usewedwa, neminyaka yakho ayiyi kuphela.', 'XHO22'),
(23, 102, 28, 'Oonyana babakhonzi bakho bosala, nembewu yabo yomelele phambi kwakho.', 'XHO22'),
(23, 103, 1, 'Umhobe kaDavide.', 'XHO22'),
(23, 103, 2, 'Mbonge uYehova, mphefumlo wam, Ungayilibali yonke impatho yakhe entle.', 'XHO22'),
(23, 103, 3, 'Uloxolela zonke izono zakho; ophilisa zonke izifo zakho;', 'XHO22'),
(23, 103, 4, 'Ulokhulula ubomi bakho esihogweni; othwele isithsaba senceba nesithsaba;', 'XHO22'),
(23, 103, 5, 'Ulowuzalisa umlomo wakho ngezinto ezilungileyo; ukuze buhlaziyeke ubutsha bakho njengobokhozi.', 'XHO22'),
(23, 103, 6, 'uYehova wenza okusesikweni nobulungisa kubo bonke abacinezelweyo.', 'XHO22'),
(23, 103, 7, 'Umazisi weendlela zakhe kuMoses, oonyana bakaSirayeli izenzo zakhe ezincamisileyo.', 'XHO22'),
(23, 103, 8, 'uYehova unenceba, unobabalo, uzeka kade umsindo, uzele yinceba.', 'XHO22'),
(23, 103, 9, 'Akayi kuhlala exabana, akawugcini umsindo wakhe ngonaphakade.', 'XHO22'),
(23, 103, 10, 'Akasenzanga ngokwezono zethu; kwaye akasiphindezeli ngokwezigwenxa zethu.', 'XHO22'),
(23, 103, 11, 'Ngokuba injengokuphakama kwezulu phezu komhlaba, Ukuba namandla, inceba yakhe kwabamoyikayo;', 'XHO22'),
(23, 103, 12, 'Njengokuba impumalanga ikude lee entshonalanga, Ubukhwelelisile kuye ubugwenxa bethu;', 'XHO22'),
(23, 103, 13, 'Kunjengokusikwa yimfesane koyise kubantwana, ukusikwa yimfesane kukaYehova kwabamoyikayo.', 'XHO22'),
(23, 103, 14, 'Ngokuba uyasazi isimo sethu; ukhumbula ukuba siluthuli.', 'XHO22'),
(23, 103, 15, 'Umntu yena, injengotyani imihla yakhe; njengentyantyambo yelizwe, uyadubula ngolo hlobo.', 'XHO22'),
(23, 103, 16, 'Ngokuba umoya uvuthuza phezu kwayo, ingabikho; nendawo yayo ayisayi kuphinda yaziwe.', 'XHO22'),
(23, 103, 17, 'Ke yona inceba kaYehova ingunaphakade kwabamoyikayo, Nobulungisa bakhe bukoonyana boonyana;', 'XHO22'),
(23, 103, 18, 'Kwabawugcinayo umnqophiso wakhe, Kwabazikhumbulayo iziyalezo zakhe ukuba bazenze.', 'XHO22'),
(23, 103, 19, 'uYehova uyizinzisile emazulwini itrone yakhe; nobukumkani bakhe bulawula phezu kwento yonke.', 'XHO22'),
(23, 103, 20, 'Mdumiseni uYehova, nina zithunywa zakhe, nina balawuli bobugorha, balenzayo ilizwi lakhe, niphulaphula isandi selizwi lakhe.', 'XHO22'),
(23, 103, 21, 'Mdumiseni uYehova, nonke nina bhunga lakhe; nina bakhonzi bakhe, abenza ukuthanda kwakhe okulungileyo.', 'XHO22'),
(23, 103, 22, 'Mdumiseni uYehova, nonke zenzo zakhe, Ezindaweni zonke zobukumkani bakhe; Mbonge uYehova, mphefumlo wam;', 'XHO22'),
(23, 104, 1, 'Mbonge uYehova, mphefumlo wam. Yehova, Thixo wam, umkhulu kakhulu; wambeswe ubungangamsha nobungangamsha.', 'XHO22'),
(23, 104, 2, 'Owanambesa ngokukhanya njengengubo, Waneka izulu njengeqhiya;', 'XHO22'),
(23, 104, 3, 'Ulomisa emanzini imiqadi yezindlu zakhe, Ulowenza amafu inqwelo yakhe, Ulohamba ngamaphiko omoya;', 'XHO22'),
(23, 104, 4, 'Ulozenza izithunywa zakhe imimoya; abakhonzi bakhe bangumlilo otshayo;', 'XHO22'),
(23, 104, 5, 'owalidalayo ihlabathi, ukuba lingashukunyiswa naphakade kanaphakade.', 'XHO22'),
(23, 104, 6, 'Waligubungela ngomsinga wamanzi njengengubo, Ema amanzi phezu kweentaba.', 'XHO22'),
(23, 104, 7, 'Ungayithethisi kabukhali, yabaleka; kwakuva ukuduma kwakho, zimka ngokukhawuleza.', 'XHO22'),
(23, 104, 8, 'Enyuka aya ezintabeni; ehla ezihlanjeni, aya kuloo ndawo ubuwalungisele yona.', 'XHO22'),
(23, 104, 9, 'Ubamisele umda abangenakuwuwela; hleze babuye bawugubungele umhlaba.', 'XHO22'),
(23, 104, 10, 'Ulothumela imithombo ezintlanjeni, Ephakathi kweenduli;', 'XHO22'),
(23, 104, 11, 'Iseza zonke izilo zelizwe, amahashe elizwe acima unxano lwawo.', 'XHO22'),
(23, 104, 12, 'Kuya kuhlala kuwo iintaka zezulu, zithethe phakathi kwamasebe;', 'XHO22'),
(23, 104, 13, 'Uloseza iintaba kumagumbi akhe; liyahlutha ihlabathi ziziqhamo zezenzo zakho.', 'XHO22'),
(23, 104, 14, 'Ulontshulisela iinkomo utyani, nezityalo zokulungiselela umntu, ukuze kuphume ukudla emhlabeni;', 'XHO22'),
(23, 104, 15, 'newayini evuyisa intliziyo yomntu, neoli ebukhazimlisa ubuso bakhe, nesonka siyixhase intliziyo yomntu.', 'XHO22'),
(23, 104, 16, 'Imithi kaYehova izele yincindi; yimisedare yaseLebhanon awayityalayo;', 'XHO22'),
(23, 104, 17, 'Apho iintaka zakha khona iindlwane zazo, nengwamza, likhaya labo lemisipres.', 'XHO22'),
(23, 104, 18, 'Zizimela zamaxhama iintaba ezinde; kunye namatye e-cones.', 'XHO22'),
(23, 104, 19, 'Inyanga wayimisela ngokwamaxesha; Ilanga liyakwazi ukutshona kwalo.', 'XHO22'),
(23, 104, 20, 'Wenza ubumnyama, kube bubusuku, Anyakazele kuwo onke amarhamncwa elizwe.', 'XHO22'),
(23, 104, 21, 'Iingonyama ezintsha zigquma emaxhobani, Zifune ukutya kwazo kuThixo.', 'XHO22'),
(23, 104, 22, 'Lakuphuma ilanga, ziyabuthana, zizimele emingxunyeni yazo.', 'XHO22'),
(23, 104, 23, 'Umntu uyahamba aye emsebenzini wakhe nasemsebenzini wakhe de kube ngokuhlwa.', 'XHO22'),
(23, 104, 24, 'Hayi indlela emininzi ngayo imisebenzi yakho, Yehova! Uzenze ngobulumko zonke ziphela; Uzele umhlaba bubutyebi bakho.', 'XHO22'),
(23, 104, 25, 'Kukwanjalo kolu lwandle lukhulu, lubanzi ngeenxa zombini, kukho ezirhubuluzayo ezingenakubalwa, ezincinci nezinkulu.', 'XHO22'),
(23, 104, 26, 'Kulapho iinqanawa zihamba khona;', 'XHO22'),
(23, 104, 27, 'Bonke abantu balindele wena; ukuba unokubanika ukutya ngexesha elifanelekileyo.', 'XHO22'),
(23, 104, 28, 'Obanika kona, bayakubutha; Uyasivula isandla sakho, ziyahlutha kokulungileyo.', 'XHO22'),
(23, 104, 29, 'Uyabusithelisa ubuso bakho, ziyadana; Uyawususa umoya wazo, ziphume umphefumlo, Zibuyele eluthulini lwazo.', 'XHO22'),
(23, 104, 30, 'Uthumela uMoya wakho, zidalwe, Ubuhlaziye ubuso behlabathi.', 'XHO22'),
(23, 104, 31, 'Uzuko lukaYehova luya kuba lapho ngonaphakade; uYehova uya kukholiswa yimisebenzi yakhe.', 'XHO22'),
(23, 104, 32, 'Uyalikhangela ilizwe, lingcangcazele; Uchukumisa iinduli, atshaye;', 'XHO22'),
(23, 104, 33, 'Ndithanda ukudumisa uYehova ngoxa ndisaphila;', 'XHO22'),
(23, 104, 34, 'Makube nencasa kuye ukucamngca kwam; Mna ndiya kugcoba ngoYehova.', 'XHO22'),
(23, 104, 35, 'Mabatshatyalaliswe aboni ehlabathini, bangabi sabakho abangendawo. Mbonge uYehova, mphefumlo wam! Dumisani uYehova.', 'XHO22'),
(23, 105, 1, 'Mdumiseni uYehova; nqulani igama lakhe, nizazise ezintlangeni izenzo zakhe.', 'XHO22'),
(23, 105, 2, 'Vumani uzuko lwakhe, mbetheleni uhadi; xelani yonke imisebenzi yakhe ebalulekileyo.', 'XHO22'),
(23, 105, 3, 'Qhayisani ngegama lakhe elingcwele; mayivuye intliziyo yabamngxameleyo uYehova.', 'XHO22'),
(23, 105, 4, 'Mfuneni uYehova namandla akhe; nisoloko nifuna ubuso bakhe.', 'XHO22'),
(23, 105, 5, 'Qwalaselani imimangaliso awayenzayo; izimanga zakhe nezigwebo zomlomo wakhe;', 'XHO22'),
(23, 105, 6, 'Nina nyana baka-Abraham, umkhonzi wakhe, Nyana bakaYakobi, banyulwa bakhe!', 'XHO22'),
(23, 105, 7, 'NguYehova uThixo wethu yena; Zisehlabathini lonke izigwebo zakhe.', 'XHO22'),
(23, 105, 8, 'Wawukhumbula ngonaphakade umnqophiso wakhe, Ilizwi awalimisela iwaka lezizukulwana.', 'XHO22'),
(23, 105, 9, 'Wanqophisana noAbraham, Nokufunga kwakhe kuIsake;', 'XHO22'),
(23, 105, 10, 'Walimisa kuYakobi ukuba libe ngummiselo, KuSirayeli ukuba libe ngumnqophiso ongunaphakade.', 'XHO22'),
(23, 105, 11, 'Wathi, Ndikunika ilizwe lakwaKanan, libe lilifa lenu.', 'XHO22'),
(23, 105, 12, 'Xa babengamadoda ambalwa ngenani; ewe, bambalwa kakhulu, nabasemzini kuloo nto.', 'XHO22'),
(23, 105, 13, 'Bahamba besuka kolunye uhlanga, baye kolunye uhlanga, Besuka kobunye ubukumkani, baye kolunye uhlanga;', 'XHO22'),
(23, 105, 14, 'Akavumelanga mntu ukuba abenzakalise; ewe, wabakhalimela ookumkani ngenxa yabo;', 'XHO22'),
(23, 105, 15, 'Wathi, Musani ukubachukumisa abathanjiswa bam, Musani ukubaphatha kakubi abaprofeti bam.', 'XHO22'),
(23, 105, 16, 'Ngaphezu koko, wabiza indlala ehlabathini; wasiqhekeza sonke isonka.', 'XHO22'),
(23, 105, 17, 'Wathuma indoda ukuba ibaqhubele, inguYosefu;', 'XHO22'),
(23, 105, 18, 'Owabayilimaza imilenze ngamakhamandela, wafakwa entsimbini;', 'XHO22'),
(23, 105, 19, 'Lada lafika ilizwi lakhe, ilizwi likaYehova lamlinga.', 'XHO22'),
(23, 105, 20, 'uYehova wathumela ukuba amkhulule; nomphathi wabantu, wamndulula.', 'XHO22'),
(23, 105, 21, 'Wammisa ukuba abe ngukumkani wendlu yakhe, nomlawuli wabo bonke ubutyebi bakhe.', 'XHO22'),
(23, 105, 22, 'Ukubakhonkxa abathetheli bakhe ngokuzithandela kwakhe; abalungiseleli bakhe abafundise ubulumko.', 'XHO22'),
(23, 105, 23, 'Naye uSirayeli wangena eYiputa; uYakobi waphambukela ezweni laseHama.', 'XHO22'),
(23, 105, 24, 'Wabandisa kakhulu abantu bakhe; wabenza bomelela ngaphezu kweentshaba zabo.', 'XHO22'),
(23, 105, 25, 'Waziguqula iintliziyo zabo ukuba babathiye abantu bakhe, abaqhathe abakhonzi bakhe.', 'XHO22'),
(23, 105, 26, 'Wathuma uMoses umkhonzi wakhe; noAron owamnyulayo.', 'XHO22'),
(23, 105, 27, 'Benza kubo kubo imiqondiso yakhe, Nezimanga zakhe ezweni lakwaKama.', 'XHO22'),
(23, 105, 28, 'Wathumela ubumnyama, kwaba mnyama; kwaye abazange bavukele ilizwi lakhe.', 'XHO22'),
(23, 105, 29, 'Wawaguqula amanzi aba ligazi, wazibulala iintlanzi zabo.', 'XHO22'),
(23, 105, 30, 'Ilizwe labo livelisa amasele amaninzi, ezindlwini zookumkani babo.', 'XHO22'),
(23, 105, 31, 'Wathetha, kwafika iintlobo ngeentlobo zeempukane neengcongconi ezweni labo.', 'XHO22'),
(23, 105, 32, 'Wabanika imvula isichotho, namalangatye omlilo ezweni labo.', 'XHO22'),
(23, 105, 33, 'Wabetha nezidiliya zabo nemikhiwane yabo; bagawule imithi yelizwe labo.', 'XHO22'),
(23, 105, 34, 'Wathetha, kwaza kwafika iinkumbi ezandayo, nabangenakubalwa.', 'XHO22'),
(23, 105, 35, 'Zayidla yonke imifuno yelizwe labo, zadla iziqhamo zomhlaba wabo.', 'XHO22'),
(23, 105, 36, 'Wabazuza kunye nabo bonke abamazibulo belizwe labo, Abagwebi bobugorha babo bonke.', 'XHO22'),
(23, 105, 37, 'Wakhupha isilivere negolide kubo, akwabakho mntu uswele amandla emizalwaneni yabo.', 'XHO22'),
(23, 105, 38, 'Avuya amaYiputa ekuphumeni kwawo, kuba ebesoyika ukuwa.', 'XHO22'),
(23, 105, 39, 'Walivula ilifu njengekhuselo; nomlilo ukhanyise ebusuku.', 'XHO22'),
(23, 105, 40, 'Abantu bacela, waza wazisa izagwityi waza wabondla ngesonka sasezulwini.', 'XHO22'),
(23, 105, 41, 'Wavula iliwa, kwaphuma amanzi; baya endaweni eyomileyo njengomlambo.', 'XHO22'),
(23, 105, 42, 'Kuba wayecinga ngesithembiso sakhe esingcwele nangoAbraham umkhonzi wakhe.', 'XHO22'),
(23, 105, 43, 'Wabakhupha ke abantu bakhe bevuya, nabanyulwa bakhe bevuya.', 'XHO22'),
(23, 105, 44, 'Babanika amazwe ezizwe, bawudla ilifa umsebenzi wabantu;', 'XHO22'),
(23, 105, 45, 'ukuze bayigcine imithetho yakhe, bayigcine imiyalelo yakhe. Dumisani uYehova.', 'XHO22'),
(23, 106, 1, 'Dumisani uYehova. Dumisani uYehova; ngokuba elungile, ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(23, 106, 2, 'Ngubani na onokuzixela izenzo zobugorha zikaYehova? Ngubani na onokuyicengceleza yonke imibongo yakhe?', 'XHO22'),
(23, 106, 3, 'Hayi, uyolo lwalowo ulugcinayo ubulungisa, Owenza ubulungisa ngamaxesha onke!', 'XHO22'),
(23, 106, 4, 'Ndikhumbule, Yehova, ngenxa yokulunga kwakho okwenzela abantu bakho; ndivelele ngosindiso lwakho;', 'XHO22'),
(23, 106, 5, 'Ukuze ndibone inyhweba yabanyulwa bakho, Ndivuye ekuvuyeleleni kwabantu bakho, Ndiqhayise ngelifa lakho.', 'XHO22'),
(23, 106, 6, 'Sonile kunye noobawo, senze ubugwenxa, senze ubugwenxa.', 'XHO22'),
(23, 106, 7, 'Oobawo abayiqiqanga eYiputa imisebenzi yakho ebalulekileyo; ababucinganga ubuninzi beenceba zakho; kodwa wamgculela ngaselwandle, kananjalo nangaseSchelfsee.', 'XHO22'),
(23, 106, 8, 'Kodwa wabasindisa ngenxa yegama lakhe, ukuze abazise ubungangamsha bakhe.', 'XHO22'),
(23, 106, 9, 'Wayikhalimela iShelfsee, yoma; wabakhokelela enzulwini, ngathi bahamba entlango.', 'XHO22'),
(23, 106, 10, 'Wabahlangula esandleni sabamthiyileyo, Wabahlangula esandleni seentshaba.', 'XHO22'),
(23, 106, 11, 'Amanzi abagubungela ababandezeli babo; akwasala namnye kubo.', 'XHO22'),
(23, 106, 12, 'Bakholwa ke ngamazwi akhe; bacula umbongo wakhe.', 'XHO22'),
(23, 106, 13, 'Bazilibala ngokukhawuleza izenzo zakhe; abazange balindele icebiso lakhe.', 'XHO22'),
(23, 106, 14, 'Ke abo bathanda entlango, Bamlinga uThixo entlango;', 'XHO22'),
(23, 106, 15, 'Wabanika ke ngokwesicelo sabo; ke yena wathumela ukutyumza emiphefumlweni yabo.', 'XHO22'),
(23, 106, 16, 'Bammonela uMoses eminqubeni, nangoAron ongcwele kaYehova.', 'XHO22'),
(23, 106, 17, 'Wavuleka umhlaba, wamginya uDatan, wawugubungela umkhosi ka-Abhiram.', 'XHO22'),
(23, 106, 18, 'Watsha umlilo wathi kwingxokolo yabo; idangatye liyabadla abangendawo.', 'XHO22'),
(23, 106, 19, 'Benza ithole eHorebhe, baqubuda emfanekisweni otyhidiweyo.', 'XHO22'),
(23, 106, 20, 'Balwananisa uzuko lwabo ngembonakalo yenkunzi yenkomo, edla utyani.', 'XHO22'),
(23, 106, 21, 'Bamlibala uThixo, umsindisi wabo, Owenza izinto ezinkulu eYiputa;', 'XHO22'),
(23, 106, 22, 'Imisebenzi ebalulekileyo ezweni laseKama Nezinto ezoyikekayo eLwandle oluBomvu.', 'XHO22'),
(23, 106, 23, 'Kungoko wathi uya kubatshabalalisa, ukuba wesukanga uMoses, umnyulwa wakhe, wesuka wema phezu kwakhe ethubeni, ukuba awuthintele umsindo wakhe, ukuba angatshabalalisi.', 'XHO22'),
(23, 106, 24, 'Ewe, balicekisa ilizwe elihle, Abakholwa lilizwi lakhe.', 'XHO22'),
(23, 106, 25, 'Bakrokra ezintenteni zabo, àbaliphulaphula ilizwi likaYehova.', 'XHO22'),
(23, 106, 26, 'Wabaphakamisela ke isandla sakhe, ukuba abaphose entlango;', 'XHO22'),
(23, 106, 27, 'ukuba ayitshabalalise inzala yabo phakathi kweentlanga, ayichithachithe emazweni.', 'XHO22'),
(23, 106, 28, 'Bazibandakanya noBhahali-pehore, Badla imibingelelo yabafileyo;', 'XHO22'),
(23, 106, 29, 'Bamqumbisa ngezenzo zabo; sabakho ke kubo isibetho.', 'XHO22'),
(23, 106, 30, 'Wesuka wema uPinehasi, wasiphumeza eso sibetho;', 'XHO22'),
(23, 106, 31, 'Kwaye wabalelwa ekubeni lilungisa ukususela kwisizukulwana kuye kwisizukulwana kuse ephakadeni.', 'XHO22'),
(23, 106, 32, 'Bamqumbisa ke ngamanzi omkhosi, wagalala uMoses ngenxa yabo.', 'XHO22'),
(23, 106, 33, 'Ngokuba bawuqumbise umphefumlo wakhe, Wathetha ngobudenge ngomlomo wakhe.', 'XHO22'),
(23, 106, 34, 'Abazange bazitshabalalise iintlanga awayebawisele umthetho ngazo uYehova.', 'XHO22'),
(23, 106, 35, 'Kodwa bahlangana nabeentlanga, baza bafunda imisebenzi yabo.', 'XHO22'),
(23, 106, 36, 'Bakhonza oothixo bazo ababuxoki, baba ngumgibe kubo.', 'XHO22'),
(23, 106, 37, 'Babingelela ngoonyana babo nangeentombi zabo kwiidemon;', 'XHO22'),
(23, 106, 38, 'Baphalaza igazi elimsulwa, igazi loonyana babo neleentombi zabo, ababebingelelwe koothixo abangcolileyo bakwaKanan; lahlanjelwa ilizwe ligazi.', 'XHO22'),
(23, 106, 39, 'Bazenza iinqambi ngezenzo zabo, bakrexeza ngezinto abaziqilileyo.', 'XHO22'),
(23, 106, 40, 'Wavutha ke umsindo kaYehova kubantu bakhe, kangangokuba walizonda ilifa lakhe.', 'XHO22'),
(23, 106, 41, 'Wabanikela ezandleni zeentlanga; balawulwa ngababathiyileyo.', 'XHO22'),
(23, 106, 42, 'Zabacinezela iintshaba zabo baza boyiswa phantsi kwezandla zabo.', 'XHO22'),
(23, 106, 43, 'Wabahlangula izihlandlo ezininzi; kodwa basuka bamenza intlekisa ngeengcebiso zabo, bethobekile ngobubi babo.', 'XHO22'),
(23, 106, 44, 'Wakubona ukubandezeleka kwabo, Akuva ukuhlahlamba kwabo.', 'XHO22'),
(23, 106, 45, 'Wawukhumbula umnqophiso wakhe kubo, Wazohlwaya ngenxa yobuninzi beenceba zakhe.', 'XHO22'),
(23, 106, 46, 'Waba nosizi ngabo bonke abo babebathimbile.', 'XHO22'),
(23, 106, 47, 'Yehova, Thixo wethu, sihlangule, sibuthe ezintlangeni, Ukuze sidumise igama lakho elingcwele, Sizingce ngendumiso yakho.', 'XHO22'),
(23, 106, 48, 'Makabongwe uYehova, uThixo kaSirayeli, kususela kwaphakade kude kuse ephakadeni, bathi bonke abantu, Amen! Dumisani uYehova.', 'XHO22'),
(23, 107, 1, 'Mdumiseni uYehova, ngokuba elungile, Ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(23, 107, 2, 'Kutshiwo ngabakhululwa bakaYehova, Awabakhululayo esandleni sotshaba;', 'XHO22'),
(23, 107, 3, 'Bahlanganisana emazweni ehlabathi, empumalanga nasentshonalanga, nasentla, nasezantsi.', 'XHO22'),
(23, 107, 4, 'Balamka entlango, ngendlela esecaleni; abafumana sixeko sokuhlala.', 'XHO22'),
(23, 107, 5, 'Indlala nokunxanwa kwawenza watyhafa umoya wabo phakathi kwabo.', 'XHO22'),
(23, 107, 6, 'Bamnqula ke uYehova embandezelweni yabo, Wabahlangula ekubandezelekeni kwabo.', 'XHO22'),
(23, 107, 7, 'Wabakhapha ngendlela ethe tye, ukuze bangene kumzi wokuhlala.', 'XHO22'),
(23, 107, 8, 'Akwaba abantu bebenokudumisa uYehova ngokulunga kwakhe nangemimangaliso ayenzele oonyana babantu!', 'XHO22'),
(23, 107, 9, 'Ngokuba wawudlisa umphefumlo olambileyo, Wawudlisa okulungileyo umphefumlo olambileyo.', 'XHO22'),
(23, 107, 10, 'Abahleli emnyameni nasethunzini lokufa, bekhonkxiwe ziimbandezelo nesinyithi;', 'XHO22'),
(23, 107, 11, 'Ngokuba baphikise amazwi kaThixo, Balidela icebo lOsenyangweni;', 'XHO22'),
(23, 107, 12, 'Ke ngoko, wazithambisa iintliziyo zabo ngobunzima; bawa, akwabakho mncedi.', 'XHO22'),
(23, 107, 13, 'Bandula ke babiza kuYehova ekubandezelekeni kwabo, waza wabahlangula ekubandezelekeni kwabo.', 'XHO22'),
(23, 107, 14, 'Wabakhupha emnyameni nasethunzini lokufa, Waziqhawula iimbophelelo zabo.', 'XHO22'),
(23, 107, 15, 'Akwaba abantu bebenokudumisa uYehova ngokulunga kwakhe nangemimangaliso ayenzele oonyana babantu!', 'XHO22'),
(23, 107, 16, 'Ngokuba ezaphule iingcango zobhedu, nemivalo yesinyithi;', 'XHO22'),
(23, 107, 17, 'Izimathane zicudiswa lukreqo lwazo nangenxa yobugwenxa babo.', 'XHO22'),
(23, 107, 18, 'Umphefumlo wabo ukwale konke ukudla; basondela emasangweni okufa.', 'XHO22'),
(23, 107, 19, 'Bakhala ke kuYehova embandezelweni yabo, Wabahlangula ekubandezelekeni kwabo.', 'XHO22'),
(23, 107, 20, 'Wathumela ilizwi lakhe, wabaphilisa, Wabahlangula ekufeni.', 'XHO22'),
(23, 107, 21, 'Akwaba abantu bebenokudumisa uYehova ngokulunga kwakhe nangemimangaliso ayenzele oonyana babantu!', 'XHO22'),
(23, 107, 22, 'Mababingelele imibingelelo yombulelo, Bancokole ngezenzo zakhe benemihlali.', 'XHO22'),
(23, 107, 23, 'Abaya elwandle ngeenqanawa, Abarhwebi emanzini amaninzi;', 'XHO22'),
(23, 107, 24, 'Bazibona izenzo zikaYehova, nemimangaliso yakhe enzulwini.', 'XHO22'),
(23, 107, 25, 'Kuba uwise umthetho, aphakamise umoya wesaqhwithi, sinyuse amaza aso.', 'XHO22'),
(23, 107, 26, 'Benyuka baya emazulwini, babuya behla emanzini anzongonzongo; Umphefumlo wabo uyanyibilika ngenxa yokubandezeleka.', 'XHO22'),
(23, 107, 27, 'Bayahexa njengenxila, baphelelwe zingqondo.', 'XHO22'),
(23, 107, 28, 'Bakhala ke kuYehova embandezelweni yabo, Wabakhupha embandezelweni yabo.', 'XHO22'),
(23, 107, 29, 'Ulodambisa isaqhwithi, azole amaza aso;', 'XHO22'),
(23, 107, 30, 'Ngoku bayavuya, kuba edambile; yiyo loo nto ebazisa kwizibuko abalinqwenelayo.', 'XHO22'),
(23, 107, 31, 'Akwaba nje abantu bebenokudumisa uYehova ngenxa yokulunga kwakhe nemimangaliso yakhe kubantwana babantu!', 'XHO22'),
(23, 107, 32, 'Mabamphakamise ebandleni labantu, Bamdumise ebandleni lamadoda amakhulu.', 'XHO22'),
(23, 107, 33, 'Wenza imilambo enkqantosini, Nemithombo yamanzi emhlabeni owomileyo;', 'XHO22'),
(23, 107, 34, 'Ilizwe elityebileyo malibe lugwali, ngenxa yezinto ezimbi zabemi balo.', 'XHO22'),
(23, 107, 35, 'Uloguqula intlango ibe yimithombo yamanzi, Umqwebedu ube yimithombo yamanzi.', 'XHO22'),
(23, 107, 36, 'Wadlisa khona abalambileyo, ukuze balungise umzi abangahlala kuwo;', 'XHO22'),
(23, 107, 37, 'Bahlwayeleni emasimini, nityale izidiliya ezineziqhamo.', 'XHO22'),
(23, 107, 38, 'Wabasikelela naye, banda kunene; kwaye abazange batshayele.', 'XHO22'),
(23, 107, 39, 'Bathotywa kananjalo bathotywa yingcinezelo, ukubandezeleka nosizi.', 'XHO22'),
(23, 107, 40, 'Ugalela indelo phezu kweenkosana kwaye azenze zibhadulise entlango apho kungekho ndlela.', 'XHO22'),
(23, 107, 41, 'Kodwa yena uyamphakamisa ekubandezelekeni kwabasweleyo, Amenzele iintsapho njengomhlambi.', 'XHO22'),
(23, 107, 42, 'Amalungisa aya kubona aze avuye, nabo bonke ububi buya kuwuvala umlomo wabo.', 'XHO22'),
(23, 107, 43, 'Lowo usisilumko nofuna ukuzigcina ezi zinto uya kubuqonda ububele bukaYehova.', 'XHO22'),
(23, 108, 1, '(Ingoma kaDavide.) Thixo, izimasekile intliziyo yam; ndiya kuvuma, ndidumise, kwanozuko lwam.', 'XHO22'),
(23, 108, 2, 'Suk’ ume, mrhubhe nengqongqo, Ndivuke kusasa.', 'XHO22'),
(23, 108, 3, 'Yehova, ndiya kukubonga phakathi kweentlanga, ndiya kukudumisa phakathi kweentlanga.', 'XHO22'),
(23, 108, 4, 'Ngokuba inkulu ngaphezu kwezulu inceba yakho, Yada yesa esibhakabhakeni inyaniso yakho.', 'XHO22'),
(23, 108, 5, 'Ziphakamise ngaphezu kwezulu, Thixo, Buphezu kwehlabathi lonke ubuqaqawuli bakho;', 'XHO22'),
(23, 108, 6, 'Ukuze zisindiswe iintánda zakho, Sindisa ngesandla sakho sokunene, undiphendule.', 'XHO22'),
(23, 108, 7, 'uThixo uthethe engcwele; Ndiya kumemelela uShekem, ndiyilinganisele intili yaseSukoti.', 'XHO22'),
(23, 108, 8, 'Ngowam uGiliyadi; ngowam uManase; uEfrayim sisigwiba sentloko yam; uYuda umazi womyalelo wam;', 'XHO22'),
(23, 108, 9, 'uMowabhi sisitya sam; Ndiya kuyiphosa kwaEdom imbadada yam; ndiya kuloyisa amaFilisti.', 'XHO22'),
(23, 108, 10, 'Ngubani na owondisa emzini onqatyisiweyo? ngubani na owondikhaphela kwaEdom?', 'XHO22'),
(23, 108, 11, 'Thixo, akufuni na oko sikulahlekeleyo? Ke wena, Thixo, akuyi kuhamba nemikhosi yethu na?', 'XHO22'),
(23, 108, 12, 'Sincede ekubandezelekeni kwethu, ngokuba uncedo lomntu luyinto engento.', 'XHO22'),
(23, 108, 13, 'NgoThixo siya kwenza ngokungafihlisiyo, ngokuba uya kuzinyathela iintshaba zethu.', 'XHO22'),
(23, 109, 1, 'Umhobe kaDavide. Yithi tu, Thixo wendumiso yam;', 'XHO22'),
(23, 109, 2, 'Ngokuba uvulwe kum umlomo wongendawo, nomlomo wabakhohlisayo; bathetha ubuxoki ngam.', 'XHO22'),
(23, 109, 3, 'Bandijikele ngeentetho zentiyo; kwaye wandilwela ngaphandle kwesizathu.', 'XHO22'),
(23, 109, 4, 'Ngenxa yothando lwam bayandichasa, ke mna ndithandaza.', 'XHO22'),
(23, 109, 5, 'Babuyisela ububi bam esikhundleni sokulungileyo, nentiyo ngokuthanda kwam.', 'XHO22'),
(23, 109, 6, 'Bekani ongendawo phezu kwakhe, makeme uSathana ngasekunene kwakhe.', 'XHO22'),
(23, 109, 7, 'Ekugwetyweni kwakhe uyagwetywa, Nomthandazo wakhe uba sisono.', 'XHO22'),
(23, 109, 8, 'Imihla yakhe mayibe mifutshane; umntu makahlale esihlalweni sakhe.', 'XHO22'),
(23, 109, 9, 'Oonyana bakhe mababe ziinkedama, Nomkakhe abe ngumhlolokazi.', 'XHO22'),
(23, 109, 10, 'Babhadule becela abantwana bakhe ngamaxesha onke; mabafune isonka eminqubeni yabo engamanxuwa.', 'XHO22'),
(23, 109, 11, 'Umenzi-bubi makayithabathe yonke into anayo; Oonyana bolunye uhlanga bawuphange umsebenzi wakhe.', 'XHO22'),
(23, 109, 12, 'Makungabikho uba nenceba kuye;', 'XHO22'),
(23, 109, 13, 'Batshabalale oonyana bakhe; igama labo malicinywe kwisizukulwana esizayo.', 'XHO22'),
(23, 109, 14, 'Mabukhunjulwe phambi koYehova izinto ezimbi zooyise; nesono sikanina asiyi kucinywa.', 'XHO22'),
(23, 109, 15, 'Mababe phambi kobuso bukaYehova ngamaxesha onke, ukuze aphelise ukukhunjulwa kwabo emhlabeni.', 'XHO22'),
(23, 109, 16, 'Ngenxa enokuba ebengacinganga nceba, ebetshutshisa amahlwempu namahlwempu, ebabulala abantliziyo zaphukileyo.', 'XHO22'),
(23, 109, 17, 'Njengoko wayethanda ukuqalekisa, kwamfikela; njengoko engakuvuyeli ukusikelelwa, makube kude kuye.', 'XHO22'),
(23, 109, 18, 'Njengoko ethe wanxiba isiqalekiso njengesambatho sakhe, masingene embilinini wakhe njengamanzi nanjengeoli emathanjeni akhe.', 'XHO22'),
(23, 109, 19, 'Makube njengengubo kuye, azithe wambu phezu kwayo, nanjengombhinqo, athi aziphose kuwo;', 'XHO22'),
(23, 109, 20, 'Nguwo lowo umvuzo wababandezeli bam kuYehova, nowabathetha okubi ngomphefumlo wam.', 'XHO22'),
(23, 109, 21, 'Kodwa, Yehova, Yehova, ndenzele ngenxa yegama lakho; Ngokuba inceba yakho ilungile, ndihlangule.', 'XHO22'),
(23, 109, 22, 'Ngokuba ndilusizana, ndilihlwempu, Intliziyo yam ityumkile phakathi kwam.', 'XHO22'),
(23, 109, 23, 'Ndihamba njengesithunzi ekutshoneni kwakho; Ndiphetshethwa njengentethe;', 'XHO22'),
(23, 109, 24, 'Amadolo am aluphele amandla; nenyama yam ibhinqile emanqatheni.', 'XHO22'),
(23, 109, 25, 'Ndibe lihlazo kubo; xa bendijonga banikina intloko.', 'XHO22'),
(23, 109, 26, 'Ndincede, Yehova, Thixo wam, ndisindise ngokwenceba yakho;', 'XHO22'),
(23, 109, 27, 'bazi ukuba sisandla sakho; ukuba nguwe, Yehova, owenze loo nto.', 'XHO22'),
(23, 109, 28, 'Makaqalekise bona, akusikelele wena; ukuba bathe bema, mabadane; kodwa makavuye umkhonzi wakho.', 'XHO22'),
(23, 109, 29, 'Abandichasayo baya kwambatha ihlazo, Bazithi wambu ihlazo njengengubo.', 'XHO22'),
(23, 109, 30, 'Ndiya kumdumisa kakhulu uYehova ngomlomo wam; ewe, ndiya kumdumisa phakathi kwezihlwele.', 'XHO22'),
(23, 109, 31, 'Kuba emi ekunene kolihlwempu, Ukuba amsindise kwabawugxekayo umphefumlo wakhe.', 'XHO22'),
(23, 110, 1, 'Yathi iNkosi eNkosini yam, Hlala ngasekunene kwam, Iintshaba zakho ndizenze isihlalo seenyawo zakho.', 'XHO22'),
(23, 110, 2, 'Intonga yamandla akho uya kuyithumela uYehova, ivela eZiyon; lawula phakathi kweentshaba zakho.', 'XHO22'),
(23, 110, 3, 'Abantu bakho baya kuzinikela ngokuzithandela ngomhla wamandla akho, ngobuhle obungcwele kwasesibelekweni; unombethe wobutsha bakho.', 'XHO22'),
(23, 110, 4, 'Ufungile uYehova, akayi kuzohlwaya: Ungumbingeleli ngonaphakade, Ngokohlobo lukaMelkitsedeke.', 'XHO22'),
(23, 110, 5, 'uYehova ngasekunene kwakho uya kuboyisa ookumkani ngemini yomsindo wakhe.', 'XHO22'),
(23, 110, 6, 'Uya kugweba phakathi kweentlanga, azalise iindawo ngezidumbu; uya kuphalusa iintloko zezizwe ezininzi.', 'XHO22'),
(23, 110, 7, 'Uya kusela emlanjeni endleleni; ngenxa yoko iya kuphakamisa intloko.', 'XHO22'),
(23, 111, 1, 'Dumisani uYehova. Ndiyakuthanda ukudumisa uYehova ngentliziyo yam yonke, phakathi kwebandla lamalungisa nebandla.', 'XHO22'),
(23, 111, 2, 'Mikhulu imisebenzi kaYehova, yaye ifunwa ngabo bonke abayithandayo.', 'XHO22'),
(23, 111, 3, 'Ubekekile umsebenzi wakhe, unobungangamsha, nobulungisa bakhe bungunaphakade.', 'XHO22'),
(23, 111, 4, 'Wenza abantu bakhumbule imimangaliso yakhe: uYehova unenceba, unenceba.', 'XHO22'),
