INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(27, 18, 3, 'Nonke bemi belimiweyo, nani bahleli ehlabathini, ekuwuphakamiseni kwakhe ibhanile ezintabeni; xa evuthela ixilongo, uyeva.', 'XHO22'),
(27, 18, 4, 'Kuba utsho uYehova kum ukuthi: “Ndiza kuphumla, yaye ndiya kuqiniseka kwindawo yam yokuhlala njengobushushu botyani nanjengelifu lombethe ekufudumaleni kwesivuno.', 'XHO22'),
(27, 18, 5, 'Kuba ngaphambi kokuvuna, xa amathupha agqibela, naxa iidiliya ezimuncu zivuthwa entyantyambo, ufanele awagawule amasebe ngeemela zokuthena imithi, awasuse amasebe, awanqumle.', 'XHO22'),
(27, 18, 6, 'Oba kunye aya kushiywa abe ngawamaxhalanga eentaba, abe ngawamarhamncwa elizwe, afike kuwo iintaka ehlotyeni, achithe ubusika phezu kwawo onke amarhamncwa elizwe.', 'XHO22'),
(27, 18, 7, 'Ngelo xesha, isipho esivela kuYehova wemikhosi siya kuphuma kuhlanga oluthe saa nolwahlulelene, nakuhlanga olwalunzima ukususela ekuqaleni de kube ngoku; luhlanga olulinganiselweyo, olunyathelweyo, olulizwe lalo latshatyalaliswa yimilambo, lwesa endaweni yegama likaYehova wemikhosi, entabeni yeZiyon.', 'XHO22'),
(27, 19, 1, 'Isigidimi esivela eYiputa. Yabona, uYehova ekhwele efini elinamendu, engena eYiputa;', 'XHO22'),
(27, 19, 2, 'Ndiya kuwaxhokonxa amaYiputa phezu kwamaYiputa, alwe elowo nomzalwana wakhe, elowo nomzalwana wakhe; Isixeko siyavukelana nesixeko, yaye ubukumkani buvukela ubukumkani.', 'XHO22'),
(27, 19, 3, 'Umoya weYiputa uya kutshabalala phakathi kwayo; kwaye ndiya kulitshitshisa icebo lakhe, kwaye baya kufuna oothixo, nabakhafuli, nemimoya yabafileyo, nabakhafuli.', 'XHO22'),
(27, 19, 4, 'Ndiya kuwanikela amaYiputa esandleni sokumkani olukhuni; nokumkani okhohlakeleyo uya kubalawula,’ utsho uYehova, uYehova wemikhosi.', 'XHO22'),
(27, 19, 5, 'Amanzi aya kuphela elwandle, umlambo ube senkangala, wome;', 'XHO22'),
(27, 19, 6, 'iya kude imilambo; aya kuba namanzi, ome imithombo yamanzi; umququ neeflegi ziya kubuna.', 'XHO22'),
(27, 19, 7, 'Iingcongolo eziphambi kwehlumelo, emlonyeni wehlumelo, nayo yonke into edliwe lihluma, iya koma, itshabalale, ingabi sabakho.', 'XHO22'),
(27, 19, 8, 'Abalobi baya kulila, nabo bonke abo baphosa amagwegwe emilanjeni baya kubhomboloza, nabo baneka iminatha phezu kwamanzi baya kudandatheka.', 'XHO22'),
(27, 19, 9, 'Ngaphezu koko, baya kudana abo basebenza ngelinen ecikizekileyo, nabaluki beminatha.', 'XHO22'),
(27, 19, 10, 'Yaye ngokuqinisekileyo baya kuqhiphuka umbilini abo benza iintlanzi namachibi eentlanzi.', 'XHO22'),
(27, 19, 11, 'Eneneni, iinkosana zaseTsowane ziziziyatha, icebo labacebisi abalumkileyo bakaFaro lidelekile; Ningathini na ukuthi kuFaro, Ndingunyana wezilumko, ndingunyana wookumkani bamandulo?', 'XHO22'),
(27, 19, 12, 'Baphi na? Zingaphi na izilumko zakho? yaye ngoku baya kukuxelela, yaye bayakwazi oko uYehova wemikhosi akucebileyo ngeYiputa.', 'XHO22'),
(27, 19, 13, 'Bazizimathane abathetheli baseTsohan, balahlekane abathetheli baseNofe; bawalahlekisile amaYiputa, amakhaya ezizwe zawo.', 'XHO22'),
(27, 19, 14, 'uYehova wafaka kuyo umoya ombi, yalahlekisa iYiputa ekwenzeni kwayo konke, njengokugxadazela kwenxila emhlanzweni walo.', 'XHO22'),
(27, 19, 15, 'Yaye iYiputa ayiyi kuba namsebenzi onokwenziwa yintloko nomsila, isebe okanye ityholo.', 'XHO22'),
(27, 19, 16, 'Ngaloo mini iJiputa yoba njengamankazana, yoyike, ngenxa yesandla sikaNdikhoyo uSomandla-onke esilingisa phezu kwayo.', 'XHO22'),
(27, 19, 17, 'Ilizwe lakwaYuda liya kuyinkwantyisa iYiputa; wonke umntu othi uzoloyika icebo likaYehova lamabhunga alicwangciseyo.', 'XHO22'),
(27, 19, 18, 'Ngaloo mini imizi emihlanu ezweni lamaYiputa iya kuthetha intetho yelakwaKanan, ifunge uYehova wemikhosi; omnye uya kubizwa ngokuba ngumzi Wotshabalalo.', 'XHO22'),
(27, 19, 19, 'Ngaloo mini kuya kubakho isibingelelo sikaYehova phakathi kwelizwe laseYiputa nelitye lesikhumbuzo emdeni kaYehova.', 'XHO22'),
(27, 19, 20, 'Oko koba ngumqondiso nobungqina kuYehova wemikhosi ezweni laseYiputa; ngokuba baya kukhala kuYehova ngenxa yabacinezeli, abathumele umsindisi omkhulu, abahlangule.', 'XHO22'),
(27, 19, 21, 'uYehova uya kuzazisa eYiputa, amazi uYehova amaYiputa ngaloo mini, azise imibingelelo namadini anyukayo; ewe, yenza isibhambathiso kuYehova, usizalise.', 'XHO22'),
(27, 19, 22, 'uYehova uya kuwavelela amaYiputa; Uya kubetha, aphilise; baya kubuyela kuYehova, yena eve, abaphilise.', 'XHO22'),
(27, 19, 23, 'Ngaloo mini kuya kubakho uhola wendlela ophuma eYiputa uye eAsiriya;', 'XHO22'),
(27, 19, 24, 'Ngelo xesha uSirayeli uya kuba ngowesithathu kunye neYiputa neAsiriya, njengentsikelelo phakathi kwelizwe;', 'XHO22'),
(27, 19, 25, 'awasikelela uYehova wemikhosi, esithi, Nisikelelwe abantu bam eYiputa, nomsebenzi wezandla zam eAsiriya, nelifa lam kwaSirayeli.', 'XHO22'),
(27, 20, 1, 'Ngomnyaka wokufika kukaTartan eAshdode, wathunywa nguSargon, ukumkani waseAsiriya, ukuba aye kulwa neAshdode, ayithimba;', 'XHO22'),
(27, 20, 2, 'Ngelo xesha, wathetha uYehova ngoIsaya, unyana ka-Amotsi, wathi, Yiya, ukhulule ingubo emanqeni akho, uzikhulule iimbadada ezinyaweni zakho. Wayenza ke, ehamba ze, enganxibanga nto ezinyaweni zakhe.', 'XHO22'),
(27, 20, 3, 'Wathi uYehova, Njengokuba uIsaya, umkhonzi wam, wahamba ze iminyaka emithathu, enganxibanga zihlangu iminyaka emithathu, waba ngumqondiso nomqondiso phezu kweYiputa nakwaKushi;', 'XHO22'),
(27, 20, 4, 'Uya kuthi ke ukumkani waseAsiriya athabathe abathinjwa baseYiputa nabathinjwa bamaKushi, abaselula nabakhulileyo, beze, benganxibanga zimbadada, ewe, iindonga zizele, ukuze iYiputa ihlazeke.', 'XHO22'),
(27, 20, 5, 'Baya kunkwantya baze babe neentloni ngenxa yeKushi ababeyilindele nangenxa yozuko lwabo eYiputa.', 'XHO22'),
(27, 20, 6, 'Kwaye abemi besi siqithi baya kuthi ngaloo mini: Qaphelani, eli lithemba lethu apho sisabela khona ukuze sibone uncedo lokukhululwa kukumkani waseAsiriya; sosinda njani na?', 'XHO22'),
(27, 21, 1, 'Isihlabo sentlango yolwandle. Njengezaqhwithi zasezantsi zivuthuza; ngoko liphuma entlango, ezweni eloyikekayo.', 'XHO22'),
(27, 21, 2, 'Ndaxelelwa umbono onzima; Ishishini elinganyanisekanga liyinkohliso, nabangenyanisekanga bayarhwaphiliza. Nyuka, Elam, ngqinga, Medi; Ndayeka yonke incwina yakhe.', 'XHO22'),
(27, 21, 3, 'Ngenxa yoko, ndiyaqaqanjelwa amanqe am; Ndaba buhlungu kakhulu xa ndiyibona.', 'XHO22'),
(27, 21, 4, 'Yaduma intliziyo yam, yandothusa; ubusuku bolonwabo lwam baguquka baluloyiko kum.', 'XHO22'),
(27, 21, 5, 'Kulungiswa isithebe, kuboniselwa inqaba, kudle, kusele; Sukumani, zidwangube, phakamisani ikhaka lenu;', 'XHO22'),
(27, 21, 6, 'Ngokuba uYehova uthe kum, Yiya umise umboniseli, uthethe into ayibonayo.', 'XHO22'),
(27, 21, 7, 'Wabona inqwelo yokulwa, nabamahashe, nabakhweli bamaesile, nabakhweli beenkamela; yaye waphulaphula ngenyameko ngentlonelo enkulu.', 'XHO22'),
(27, 21, 8, 'Wadanduluka wathi, Ngonyama, Nkosi, ndisamise emboniselweni emini, ndihleli ezintenteni zam ubusuku bonke.', 'XHO22'),
(27, 21, 9, 'Nantso, kusiza inqwelo yokulwa yamadoda, nabamahashe; Wathi, Iwile iBhabheli, iwile iBhabheli; Wayiqhekeza yonke imifanekiso eqingqiweyo yoothixo bakhe.', 'XHO22'),
(27, 21, 10, 'Owu sanda sam, ngqolowa yesanda sam! endikuvileyo kuYehova wemikhosi, uThixo kaSirayeli, ndinixelele.', 'XHO22'),
(27, 21, 11, 'Umthwalo wamaDuma. Wandibiza ekwaSehire, wathi, Mlindi, bunjani na ubusuku? Mlindi, kuthekani na ngobusuku?', 'XHO22'),
(27, 21, 12, 'Uthi umlindi, Kuyeza ukusa, kuhlwa; xa ufuna ukubuza, uyabuza; buya, yiza.', 'XHO22'),
(27, 21, 13, 'Isigidimi esingeArabhi. Niya kuchitha ubusuku kwihlathi laseArabhiya, nina balingane abahambahambayo bakwaDedan.', 'XHO22'),
(27, 21, 14, 'Baziseleni amanzi abanxaniweyo, abemi belizwe lakwaTema, banqande obaleka nesonka sabo.', 'XHO22'),
(27, 21, 15, 'Kuba babebalekile ikrele, ikrele eliyingozi, nesaphetha esitshiswayo, neembandezelo zemfazwe.', 'XHO22'),
(27, 21, 16, 'Kuba uthi uYehova kum: “Emva konyaka omnye, ngokweminyaka yomqeshwa, nalo lonke uzuko lwakwaKedare luya kuphela.', 'XHO22'),
(27, 21, 17, 'Aya kuncipha amasalela enani lezaphetha zamagorha, oonyana bakaKedare; ngokuba utsho uYehova, uThixo kaSirayeli.', 'XHO22'),
(27, 22, 1, 'Umthwalo woburhabaxa bomphezulu. Kuthekani ngawe xa unyukela eluphahleni?', 'XHO22'),
(27, 22, 2, 'Wena uzeleyo zizithukuthuku, sixekondini esizele zizithukuthezi, mzi wolonwabo; ababuleweyo kuni ababulawanga ngekrele, ababulawanga emfazweni.', 'XHO22'),
(27, 22, 3, 'Bonke ookumkani bakho basabile kunye, bakhonkxwa ngabatoli; bonke abafunyanwa kuwe bakhonkxwa kunye ekubalekeleni kwabo kude.', 'XHO22'),
(27, 22, 4, 'Ngoko ke, ndathi: Ndoyise! Ndiya kulila ngokukrakra, ndingazikhathazi ngenxa yokuphangwa kwentombi yabantu bakowethu.', 'XHO22'),
(27, 22, 5, 'Kuba yimini yokubandezeleka neyokunyathelwa neyokudubadubeka kweNkosi enguMongami uYehova wemikhosi entlanjeni yombono, yokudilizwa kweendonga nokuhlaba umkhosi ezintabeni.', 'XHO22'),
(27, 22, 6, 'uElam wawuthabatha umphongolo, neenqwelo zokulwa zamadoda nabamahashe;', 'XHO22'),
(27, 22, 7, 'Kuya kuthi ke iintili zakho ozinyulileyo zizaliswe ziinqwelo, nabakhweli bamahashe bagxalathele esangweni.', 'XHO22'),
(27, 22, 8, 'Wasisusa isigqubuthelo sakwaYuda, wasikhangela ngaloo mini isikrweqe sendlu yehlathi.', 'XHO22'),
(27, 22, 9, 'Niwabonile ke amanxuwa omzi kaDavide, ukuba maninzi; wawabutha amanzi echibi lasezantsi;', 'XHO22'),
(27, 22, 10, 'Nizibalile izindlu zaseYerusalem, nizidilizile ezo zindlu, ukuze niluqinise udonga;', 'XHO22'),
(27, 22, 11, 'Nenza isisele samanzi echibi elidala phakathi kweendonga zombini, anajongana nomenzi walo, nalowo walenzayo kwakudala.', 'XHO22'),
(27, 22, 12, 'Ngaloo mini iNkosi enguMongami uYehova wemikhosi ibiza ukulila nokuzila nokucheba nokunxiba iimpahla zokuzila.', 'XHO22'),
(27, 22, 13, 'Yabonani, kukho uvuyo nemivuyo kwabaxhelwa iinkomo, kuxhelwe izimvu, kudle inyama, kusele iwayini; masidle, sisele; kuba ngomso siya kufa.', 'XHO22'),
(27, 22, 14, 'Waza uYehova wemikhosi wazityhila ezindlebeni zam wathi: “Eneneni, obu bubi abuyi kususwa kuni nide nife,” itsho iNkosi enguMthetheleli, uYehova wemikhosi.', 'XHO22'),
(27, 22, 15, '“Itsho iNkosi enguMongami uYehova wemikhosi ukuthi: “Hamba uye kulo mgcini-ndyebo, kuShebhena, ophethe indlu, uthi:', 'XHO22'),
(27, 22, 16, 'Uyintoni na apha? Unabani na apha, ozenzele ingcwaba apha, njengokuba ezimbela ingcwaba phezulu, ezimbele isakhiwo eweni?', 'XHO22'),
(27, 22, 17, 'Uyabona, uYehova uya kukusa ekuthinjweni okukhulu, akugubungele kude kuse ephakadeni;', 'XHO22'),
(27, 22, 18, 'Uya kuyijika, uyiphose njengebhola ezweni elikhulu, ufele khona, zibe lihlazo leenqwelo zokulwa zendlu yenkosi yakho.', 'XHO22'),
(27, 22, 19, 'Ndiza kunigxotha nisuke endaweni yenu, yaye ngokuqinisekileyo uya kunisasaza ningabikho endaweni yenu.', 'XHO22'),
(27, 22, 20, 'Kuya kuthi ngaloo mini, ndibize umkhonzi wam, uEliyakim, unyana kaHilekiya;', 'XHO22'),
(27, 22, 21, 'Ndiza kumambesa isambatho sakho ndize ndimbophe ngebhanti lakho, ndibunikele ubukumkani bakho esandleni sakhe, yaye uya kuba nguyise wabemi baseYerusalem nowendlu kaYuda.', 'XHO22'),
(27, 22, 22, 'Ndiya kusibeka isitshixo sendlu kaDavide egxalabeni lakhe; ngokunjalo uya kuvula kungabikho uvalayo; uya kuvala kungabikho uvulayo.', 'XHO22'),
(27, 22, 23, 'Ndiya kumbethelela njengesikhonkwane endaweni etywiniweyo; ibe yitrone yenkqubela yendlu kayise.', 'XHO22'),
(27, 22, 24, 'Baya kubuxhoma phezu kwakhe bonke ubunzima bendlu kayise, abazukulwana, nabantwana, nempahla emfutshane, kuthabathele ezindebeni, kuse empahleni yonke.', 'XHO22'),
(27, 22, 25, 'Ngaloo mini, utsho uYehova wemikhosi, siya kususwa isikhonkwane esibethelelwe endaweni etywiniweyo, sigawulwe, siwe; nomthwalo owawukuyo uya kunqunyulwa, ngenxa yokuba uYehova ethethile.', 'XHO22'),
(27, 23, 1, 'Umthwalo weTire. Bhombolozani, zinqanawa zaseTarshishe! Ngokuba ichithakele, akusekho ndlu, akusekho mnyango; bakutyhilelwa kuvela ezweni lamaKitim.', 'XHO22'),
(27, 23, 2, 'Yithini cwaka, bemi besi siqithi; wena uzaliswe ngabarhwebi baseTsidon, abawela ulwandle.', 'XHO22'),
(27, 23, 3, 'Yayisemanzini amaninzi imbewu yakwaSihore, ukuvuna koMnayile kwaba ngumvuzo wakhe; ke yona imicimbi yeentlanga.', 'XHO22'),
(27, 23, 4, 'Dana, Tsidon, kuba luthethile ulwandle, namandla olwandle, lusithi, Andiyi kuzala mntwana, andiyi kukondla ntombi, andiyi konyusa zintombi.', 'XHO22'),
(27, 23, 5, 'Njengengxelo ephathelele iYiputa, ngokuqinisekileyo baya kubandezeleka kakhulu ngenxa yamarhe angeTire.', 'XHO22'),
(27, 23, 6, 'eTarshishe; bhombolozani, nina bemi basesiqithini!', 'XHO22'),
(27, 23, 7, 'Kunje na kuwe, wena wonwabileyo, owasemandulo kwakudala? iinyawo zakhe ziya kumsa kude, ukuba abe ngumphambukeli.', 'XHO22'),
(27, 23, 8, 'Ngubani na lo ucebe iqhinga ngeTire, ngomzi wesithsaba, obarhwebi bawo bangabathetheli, obarhwebi bawo bangamanene elimiweyo?', 'XHO22'),
(27, 23, 9, 'uYehova wemikhosi ucinge ukungcolisa ubungangamsha balo lonke uzuko, ukuba acukuceze zonke iingangamsha zehlabathi;', 'XHO22'),
(27, 23, 10, 'Canda kulo ilizwe lakowenu njengoMnayile, ntombi inguTarshishe;', 'XHO22'),
(27, 23, 11, 'Isandla sakhe usolulele phezu kolwandle, wazinyikimisa izikumkani; uYehova wayalela eso sixeko sabarhwebi ukuba sitshabalalise iinqaba zaso.', 'XHO22'),
(27, 23, 12, 'Wathi: “Awuyi kuba sabuya uvuye, ntombindini idlwengulweyo, ntombi yaseTsidon! nalapho akuyi kuba nakuphumla.', 'XHO22'),
(27, 23, 13, 'Kukho ilizwe lamaKaledi; Akwabakho olu hlanga, ade ama-Asiriya abamisela ukuba babe bona babemi entlango; bazakha iinqaba zakhe ezinde, bezakha iingxande zakhe ezinde; kwaye uyitshabalalise.', 'XHO22'),
(27, 23, 14, 'Lilani, zinqanawa zaseTarshishe, ngokuba kubhuqiwe amandla enu.', 'XHO22'),
(27, 23, 15, 'Kuya kuthi ngaloo mini, ilityalwe iTire iminyaka engamashumi asixhenxe, njengemihla yokumkani omnye; emva kweminyaka engamashumi asixhenxe, iTire iya kumemelela njengehenyukazi.', 'XHO22'),
(27, 23, 16, 'Musani ukuhamba niwujikeleze umzi, nililibale ihenyukazi; vumani iingoma ezimnandi, vumani iingoma ezininzi, ukuze ukhunjulwe.', 'XHO22'),
(27, 23, 17, 'Ekupheleni kweminyaka engamashumi asixhenxe uYehova uya kuyivelela iTire, ize iguquke ukuze ifumane umvuzo ize ikrexeze nazo zonke izikumkani zehlabathi.', 'XHO22'),
(27, 23, 18, 'Yaye umsebenzi wakhe nomvuzo wakhe woba ngcwele kuYehova; ayinakugcinwa okanye igcinwe; ngokuba umcimbi wakhe uya kuba ngowabahleli phambi koYehova, badle ukudla okuninzi, neempahla zokuhomba.', 'XHO22'),
(27, 24, 1, 'Yabona, uYehova uyalichithachitha ihlabathi, ulenze kube senkangala, ulenza ligwilike, uyabachithachitha abemi balo.', 'XHO22'),
(27, 24, 2, 'Kuya kuba njengoko kube njalo ebantwini, kube njalo kumbingeleli; njengekhoboka, injalo inkosi yaso; njengentombi, njengenkosikazi yayo; njengomthengi, njengomthengisi; njengomboleki, njengomboleki; njengoko umbolekisayo, unjalo kuye obolekayo.', 'XHO22'),
(27, 24, 3, 'Ilizwe liya kuphanziswa ngokupheleleyo lize litshatyalaliswe ngokupheleleyo, ngenxa yokuba uYehova elithethile eli lizwi.', 'XHO22'),
(27, 24, 4, 'Ihlabathi lidanile, lidanile, ihlabathi lidanile, lidanile, abaqhankqalazi behlabathi badanile.', 'XHO22'),
(27, 24, 5, 'Kananjalo ihlabathi lihlanjelwe phakathi kwabemi balo; ngenxa yokuba baphule imithetho, bawuguqula umthetho, bawaphula umnqophiso ongunaphakade.', 'XHO22'),
(27, 24, 6, 'Ngoko ke, isiqalekiso silidlile ilizwe, batshabalale abemi balo; ke ngoko, abemi behlabathi bayatshiswa, kwaye kukho abantu abambalwa abaseleyo.', 'XHO22'),
(27, 24, 7, 'Iwayini entsha ilungile, iidiliya zibunile, bonke abantu abavuyayo bayancwina.', 'XHO22'),
(27, 24, 8, 'Iphelile imihlali yamagubu, iphelile ingxokozelo yabadlamkileyo, iphelile imihlali yengqongqo.', 'XHO22'),
(27, 24, 9, 'Abayi kusela wayini bevuma ingoma; isiselo siya kuba krakra kwabasiselayo.', 'XHO22'),
(27, 24, 10, 'Uqhekekile umzi osenyanyeni, zivalekile zonke izindlu ukuba zingangenwa mntu.', 'XHO22'),
(27, 24, 11, 'Kubakho ukukhala ngewayini ezitratweni; lonke uvuyo luba mnyama, uvuyo lwehlabathi luphelile.', 'XHO22'),
(27, 24, 12, 'Umzi usele walinxuwa, nesango liphanzisiwe;', 'XHO22'),
(27, 24, 13, 'Kuya kuba njalo emhlabeni phakathi kwabantu, kuya kubakho ukunyikima komnquma, njengokuvuthululwa kweediliya ekupheleni kokuvuna.', 'XHO22'),
(27, 24, 14, 'Ziya kuphakamisa amazwi azo, zivume ubukhulu bukaYehova, ziphakamise izandla zazo elwandle.', 'XHO22'),
(27, 24, 15, 'Ngoko ke, zukisani uYehova ngomlilo, igama likaYehova, uTHIXO kaSirayeli, eziqithini zolwandle.', 'XHO22'),
(27, 24, 16, 'Sivile iingoma ezivela eziphelweni zomhlaba zisithi: “Uzuko malube kwilungisa. Ndathi ke mna, Ukubhitya kwam, ukubhitya kwam! abarhwebi abangamaqhophololo benza ngobuqhophololo; ewe, abarhwebi abanobuqhophololo benze ubuqhophololo obukhulu.', 'XHO22'),
(27, 24, 17, 'Ufikelwe lunkwantyo, nomgongxo, nesibatha, wena mmi wehlabathi;', 'XHO22'),
(27, 24, 18, 'Kuya kuthi, othe walibaleka ilizwi elinkwantyisayo, eyele emgongxweni; onyuka ephuma emhadini, abanjiswe sisibatha; ngokuba iifestile eziphezulu zivuliwe, zanyikima iziseko zehlabathi.', 'XHO22'),
(27, 24, 19, 'Ihlabathi liya kugungxulwa litshatyalaliswe, ligungqiswe ngokupheleleyo ihlabathi;', 'XHO22'),
(27, 24, 20, 'Umhlaba uya kugxadazela njengenxila, yaye ngokuqinisekileyo kuya kushukuma njengendlu yangasese; ukreqo lwakhe luya kuba nzima phezu kwakhe; liya kuwa, lingabi savuka.', 'XHO22'),
(27, 24, 21, 'Ngaloo mini uYehova uya kubavelela ikhusi kwelona zulu liphakamileyo nookumkani bomhlaba emhlabeni.', 'XHO22'),
(27, 24, 22, 'Baya kuhlanganiswa njengamabanjwa emhadini, bavalelwe entolongweni, banikelwe, kwakuba ziintsuku ezininzi.', 'XHO22'),
(27, 24, 23, 'Iya kuthi ke inyanga ime ngxi, nelanga liya kuba neentloni, xa uYehova wemikhosi uya kulawula eNtabeni yeZiyon naseYerusalem ngozuko phambi kwamadoda akhe amakhulu.', 'XHO22'),
(27, 25, 1, 'Yehova, unguThixo wam; Ndithanda ukukuphakamisa, ukudumisa igama lakho; ngokuba wenze imisebenzi ebalulekileyo; icebo lakho elidala liyinyaniso kwaye liyinyaniso.', 'XHO22'),
(27, 25, 2, 'Ngokuba umzi uwenze walinxuwa; Umzi onqatyisiweyo, ulinxuwa, ibhotwe lolunye uhlanga alinakuba sisixeko; soze yakhiwe.', 'XHO22'),
(27, 25, 3, 'Ngako oko ziya kukuhlonela iintlanga ezinamandla, ikoyike imizi yezizwe ezinobungcwangu.', 'XHO22'),
(27, 25, 4, 'Ngokuba waba yinqaba kolusizana, waba yinqaba kofunyenweyo ekubandezelekeni kwakhe, ihlathi esiphangweni, umthunzi womgxobhozo, xa umoya wamagorha usisaqhwithi eludongeni.', 'XHO22'),
(27, 25, 5, 'Uyidambisile ingxokozelo yabasemzini, njengokufudumeza kwendawo ebaleleyo; Kwanobushushu ethunzini lelifu, Isebe labanamandla liyathotywa.', 'XHO22'),
(27, 25, 6, 'uYehova wemikhosi uya kwenzela zonke iintlanga isidlo esityebileyo kule ntaba, isidlo sewayini ecocekileyo, esinemongo, newayini engqushiweyo.', 'XHO22'),
(27, 25, 7, 'Wosichitha ke kule ntaba isigqubuthelo esizigqubutheleyo zonke iintlanga, nesigqubuthelo esizigqubutheleyo iintlanga zonke.', 'XHO22'),
(27, 25, 8, 'Uya kuginya ukufa ngoloyiso; izisule iNkosi uYehova iinyembezi ebusweni bonke; yaye uya kusishenxisa isisongelo sabantu bakhe ehlabathini lonke, ngenxa yokuba uYehova ethethile.', 'XHO22'),
(27, 25, 9, 'Kwaye ngaloo mini kuya kuthiwa: Qaphela, lo nguThixo wethu; sithembele kuye, wosihlangula. NguYehova; sithembele kuye, sogcoba, sivuye ngosindiso lwakhe.', 'XHO22'),
(27, 25, 10, 'Kuba isandla sikaYehova siya kuhlala kule ntaba, noMowabhi uya kunyathelwa phantsi kwakhe, kanye njengokunyathelwa kwengca phambi kwendawo yokulahla inkunkuma.', 'XHO22'),
(27, 25, 11, 'Aya kuzolula izandla zawo phezu kwawo, njengokuba indadi izaneka izandla ukuba idade;', 'XHO22'),
(27, 25, 12, 'Uya kuzigungxula iinqaba ezinde ezinde, azidilize, azidilize emhlabeni, eluthulini.', 'XHO22'),
(27, 26, 1, 'Ngaloo mini kuya kuvunywa le ngoma ezweni lakwaYuda; Sinesixeko esiliqele; usindiso luya kumkhela uThixo iindonga neendonga.', 'XHO22'),
(27, 26, 2, 'Vulani amasango, ukuba angene amalungisa agcina inyaniso.', 'XHO22'),
(27, 26, 3, 'Ocinga ngawe uya kumgcina enoxolo, kuba ekholose ngawe.', 'XHO22'),
(27, 26, 4, 'Kholosa ngoYehova ngonaphakade, kuba uYehova unamandla angunaphakade.', 'XHO22'),
(27, 26, 5, 'Ngokuba uyabatshabalalisa abahlala phezulu; umzi ophakamileyo uyawumisa; yibeke phantsi, yibeke phantsi; yizise eluthulini.', 'XHO22'),
(27, 26, 6, 'Unyawo luya kukunyathela, iinyawo zabasweleyo, amanyathelo amahlwempu.', 'XHO22'),
(27, 26, 7, 'Indlela yamalungisa inobulungisa; Wena ulilungisa uyawulinganisela umendo wamalungisa.', 'XHO22'),
(27, 26, 8, 'Ewe, silindele kuwe, Yehova, emendweni wemigwebo yakho; umnqweno wemiphefumlo yethu kukukhumbula igama lakho.', 'XHO22'),
(27, 26, 9, 'Ngomphefumlo wam ndakunqwenela ebusuku; ewe, ngomoya wam ophakathi kwam, ndiya kukufuna kamsinya, ngokuba ekufikeni kwemigwebo yakho phezu komhlaba, baya kufunda ubulungisa abemi behlabathi.', 'XHO22'),
(27, 26, 10, 'Makabekelwe ongendawo, angafundi bulungisa; ezweni lobulungisa uya kwenza ububi, angabuboni ubukhulu bukaYehova.', 'XHO22'),
(27, 26, 11, 'Nkosi, xa siphakamile isandla sakho, aziyi kukubona; ewe, umlilo weentshaba zakho uya kuwatshisa.', 'XHO22'),
(27, 26, 12, 'Yehova, nguwe osinike uxolo, ngokuba yonke imisebenzi yethu uyenze kuthi.', 'XHO22'),
(27, 26, 13, 'Yehova, Thixo wethu, besiphethwe ngabanye ookumkani ngaphandle kwakho; kodwa ngawe wedwa siya kulinqula igama lakho.', 'XHO22'),
(27, 26, 14, 'Abafileyo abo abasayi kuba saphila; bafile, abasayi kuvuka; ngoko ke, wabavelela, wabatshabalalisa, watshabalalisa konke ukukhunjulwa kwabo.', 'XHO22'),
(27, 26, 15, 'Ubandisile abantu, Yehova, ubandisile abantu; Uzukisiwe; Ubuzise eziphelweni zehlabathi;', 'XHO22'),
(27, 26, 16, 'Yehova, babekuvelela embandezelweni, bathulula umthandazo ngesohlwayo sakho.', 'XHO22'),
(27, 26, 17, 'Njengomfazi omithiyo oseza kuzala, uba sentlungwini yaye ukhalela ezindleleni zakhe; saba njalo emehlweni akho, Yehova.', 'XHO22'),
(27, 26, 18, 'Samitha, sisezintlungwini, sazala umphefumlo ngokufuziselayo; asilisindisanga ihlabathi; abemi behlabathi abakhubekanga.', 'XHO22'),
(27, 26, 19, 'Abafileyo bakho baya kuphila, nesidumbu sam siya kuvuka; sukani nimemelele, nina bahleli eluthulini; kuba uya kuba ngumbethe wezityalo umbethe wakho, nehlabathi libavuthulule abangasekhoyo.', 'XHO22'),
(27, 26, 20, 'Hambani, bantu bam, ningene emagumbini enu angaphakathi, nivale iingcango zenu; zimela umzuzwana, ude uphele umsindo.', 'XHO22'),
(27, 26, 21, 'Kuba, yabonani, uYehova uphuma endaweni yakhe, ukuze abavelele abemi behlabathi ngenxa yobubi babo;', 'XHO22'),
(27, 27, 1, 'Ngaloo mini uYehova uya kuyibetha iLeviyatan, inyoka elomeleleyo, neLeviyatan, inyoka ephinyaphinyelayo, ngekrele layo elinzima, likhulu, elo lomeleleyo; liya kuyibulala inamba elwandle.', 'XHO22'),
(27, 27, 2, 'Vumani kuye namhla, uthi, Isidiliya sewayini ebomvu;', 'XHO22'),
(27, 27, 3, 'Mna, iNkosi, ndiya kunisindisa; ndiya kusinkcenkceshela yonke iminithi; ukuba ndiwugcine imini nobusuku, ukuze kungabikho mntu uwonakalisayo.', 'XHO22'),
(27, 27, 4, 'Ubushushu abukho kum; Bendiya kucanda kuwo, ndiwatshise kunye.', 'XHO22'),
(27, 27, 5, 'Makawathabathe amandla am, axolelane nam; wenze uxolo nam.', 'XHO22'),
(27, 27, 6, 'Abo baphuma kwaYakobi uya kwendelisela iingcambu, uSirayeli aqhame iintyatyambo, axhobe, awuzalise umhlaba ngeziqhamo.', 'XHO22'),
(27, 27, 7, 'Ngaba wayembetha na xa wayebetha abo babembetha? Wabulawa na ngokubulawa kwababulewe nguye?', 'XHO22'),
(27, 27, 8, 'Uneqabane lakho, ukuba lithe labonakala, wolwa nalo; awubambe umoya wakhe ngemini yolophu lwasempumalanga.', 'XHO22'),
(27, 27, 9, 'Ngenxa yoko ububi bukaYakobi buya kuhlanjululwa; kwaye esi sisiqhamo sonke sokususa isono sakhe; ukuba uthe wawenza onke amatye esibingelelo njengekalika ephukileyo, ayiyi kuma iziphunzi zemithi nemifanekiso eqingqiweyo.', 'XHO22'),
(27, 27, 10, 'Ke wona umzi onqatyisiweyo uya kuba yintlango, nekhaya laba yintlango; ithole liya kudla khona, liguge, liwadle amasebe awo.', 'XHO22'),
(27, 27, 11, 'Ukuba amasebe awo abuna, ophulwa; beza abafazi abo bazitshisa; ngokuba bengabantu abangenangqondo; ngoko ke uMenzi wabo akayi kuba nanceba kubo, noMdali wabo akayi kuba nanceba kubo.', 'XHO22'),
(27, 27, 12, 'Ngaloo mini uYehova uya kunibetha, ethabathela emlanjeni, ase emlanjeni waseYiputa, nihlanganiselwe ngabanye ngabanye, nina nyana bakaSirayeli.', 'XHO22'),
(27, 27, 13, 'Ngaloo mini kuya kukhaliswa ixilongo elikhulu, yaye abo babesele beza kutshabalala kwilizwe laseAsiriya nabo babethinjwe kwilizwe laseYiputa baya kuza, banqule uYehova entabeni engcwele eYerusalem.', 'XHO22'),
(27, 28, 1, 'Yeha ke, isithsaba sekratshi, amanxila akwaEfrayim, ebengezela ngaso intyantyambo etyatyambileyo, encotsheni yeentlambo ezichumileyo zababuleweyo yiwayini!', 'XHO22'),
(27, 28, 2, 'Yabona, iNkosi inendoda eyomeleleyo, eyomeleleyo, eya kulithoba ihlabathi ngesandla sayo njengesiphango sesichotho, njengesiphango esibhubhisayo, njengokuphalazeka kwamanzi amaninzi.', 'XHO22'),
(27, 28, 3, 'Siya kunyashwa isithsaba sekratshi, amanxila akwaEfrayim;', 'XHO22'),
(27, 28, 4, 'Kwaye ukukhanya kokukhanya okusentlanjeni etyebileyo kuya kuba yintyatyambo edubulayo, nanjengesiqhamo esikhawulezayo ngaphambi kokuba kufike ihlobo; athi oyibonayo, abone, isesandleni sakhe, ayidle;', 'XHO22'),
(27, 28, 5, 'Ngaloo mini uYehova wemikhosi uya kuba phakathi kwamasalela abantu bakhe isithsaba sokuhomba, nesangqawe sokuhomba;', 'XHO22'),
(27, 28, 6, 'unjengomoya wogwebo kwabahleli ematyaleni, nobugorha kwabangenisa imfazwe esangweni.', 'XHO22'),
(27, 28, 7, 'Kwanabo ke baphambukile ewayinini, bahexile ekunxileni; umbingeleli nomprofeti bayotywe sisiselo esinxilisayo, badleke yiwayini, bayahexa sisiselo esinxilisayo; bayalahleka ngemibono, bayagexa ekugwebeni.', 'XHO22'),
(27, 28, 8, 'Kuba zonke iitafile zizele ngumhlanzo nomdaka, akukho ndawo ihlambulukileyo.', 'XHO22'),
(27, 28, 9, 'Uya kufundisa bani na ukwazi? Ngubani na oya kumqondisa loo mfundiso? abalunyulweyo ebisini, abalunyulweyo ebisini.', 'XHO22'),
(27, 28, 10, 'Ngokokuba umthetho umelwe kukuba ngumthetho phezu komthetho, umthetho ube ngumthetho phezu komthetho; umgca phezu komgca, umgca phezu komgca; incinci apha, kwaye incinci phaya.', 'XHO22'),
(27, 28, 11, 'Kuba uya kuthetha kwaba bantu ngemilebe yolunye ulwimi, nangalulwimi lumbi;', 'XHO22'),
(27, 28, 12, 'owathi kubo, Nantsi indawo yokuphumla, phumzani abadiniweyo; kwaye olu lusindiso; abavuma ukuva.', 'XHO22'),
(27, 28, 13, 'Ke ilizwi likaYehova laba ngumthetho kubo, umthetho phezu komthetho; umgca phezu komgca, umgca phezu komgca; intwana apha, intwana phaya; ukuze bahambe bawe umva, baphuke, babanjiswe, babanjiswe.', 'XHO22'),
(27, 28, 14, 'Liphulaphuleni ke ilizwi likaYehova, nina bagxeki, nina balawuli baseYerusalem.', 'XHO22'),
(27, 28, 15, 'Ngokuba nisithi, Sinqophisene nokufa, sivumelene nelabafileyo; ukuba kuthe kwabakho ulwaphulo olukhulu, aluyi kusiwela;', 'XHO22'),
(27, 28, 16, 'Ngako oko itsho iNkosi uYehova ukuthi, Yabona, ndibeka eZiyon ilitye, ilitye locikido, ilitye elinqabileyo lembombo, loseko olusekiweyo; lowo ukholwayo akayi kungxama.', 'XHO22'),
(27, 28, 17, 'Ndiya kubeka isiko elutyani lokulinganisa, nobulungisa ndibeke ulutya lokulinganisa;', 'XHO22'),
(27, 28, 18, 'Uya kubhangiswa umnqophiso wenu nokufa, ungabikho umnqophiso wenu neNkosi; xa kudlula indyikitya yokufa, iya kunyathelwa phantsi.', 'XHO22'),
(27, 28, 19, 'Ukususela ekundulukeni kwayo, iya kunithabatha; ngokuba ingomso liyadlula, imini nobusuku; kwaye kuya kucaphukisa ukuqonda oko.', 'XHO22'),
(27, 28, 20, 'Ngokuba iibhedi zimfutshane kunomntu, nengubo yokwaleka incinane kunokuba azisongele ngayo.', 'XHO22'),
(27, 28, 21, 'Kuba uYehova uya kuma, njengasentabeni yePersim; uya kucaphuka njengasentilini yeGibheyon, ukuba enze isenzo sakhe, isenzo sakhe esibalulekileyo; yaye uyawufeza umsebenzi wakhe, isenzo sakhe esimangalisayo.', 'XHO22'),
(27, 28, 22, 'Musani ukuhlekisa ngoku, ukuze zingatywinwa iintambo zenu; kuba ndisivile ngeNkosi enguMongami uYehova wemikhosi ukutshabalala kwehlabathi lonke.', 'XHO22'),
(27, 28, 23, 'Phulaphulani kwaye niphulaphule ilizwi lam; liphulaphuleni ilizwi lam.', 'XHO22'),
(27, 28, 24, 'Umlimi ulima iimini ezi zonke na, ukuba ahlwayele? Uya kuzivula na iibhokisi zelizwe lakhe, aziqhekeze na?', 'XHO22'),
(27, 28, 25, 'Akathi na, akuba eyiqondile imbonakalo yayo, azilahle iimpethu, aze alahle ikumin, aze afefe ingqolowa yokuqala, nerhasi, nokhula endaweni yazo?', 'XHO22'),
(27, 28, 26, 'Kuba uThixo wakhe uyamcebisa aze amfundise.', 'XHO22'),
(27, 28, 27, 'Ngenxa yokuba imibhobho ayikrolwanga ngesikroba, ayivulelwanga ivili yenqwelo ikumin; ke iimpethu zibulawa ngentonga, nekumin ngentonga.', 'XHO22'),
(27, 28, 28, 'Ingqolowa iya kucolwa; kuba ayiyi kuyibhula, ayiyi kuyaphula ngevili yenqwelo yakhe yokulwa, ayiyi kuyaphula nabakhweli bayo.', 'XHO22'),
(27, 28, 29, 'Nale nto iphuma kuYehova wemikhosi, obalaseleyo ngecebo, nomsebenzi oncomekayo.', 'XHO22'),
(27, 29, 1, 'Yeha, iAriyeli, Ariyeli, mzi kaDavide! ukwanda ngonyaka; wazibingelela.', 'XHO22'),
(27, 29, 2, 'Ndiya kuyihlisela ishwangusha iAriyeli, kubekho ukuncwina nokubandezeleka, ndibe njengeAriyeli;', 'XHO22'),
(27, 29, 3, 'Ndiza kumisa inkampu ngakuwe ndize ndikurhangqe ngentaba ndize ndikumisele iinqaba;', 'XHO22'),
(27, 29, 4, 'uya kuphoswa emhlabeni, uthethe usemhlabeni, ukuthetha kwakho kudambe eluthulini, nelizwi lakho liphume emhlabeni njengelomntu onomoya wokuvumisa, ukuthetha kwakho kuhlebeze eluthulini.', 'XHO22'),
(27, 29, 5, 'Ingxokolo yabakuphambukeli bakho iya kuba njengothuli olucolekileyo, ingxokolo yamagorha ibe njengomququ ophephukayo; ewe, kuya kwenzeka kanye.', 'XHO22'),
(27, 29, 6, 'uYehova wemikhosi uya kunivelela ngeendudumo, nangenyikima, nangesandi esikhulu, nangesaqhwithi, nangesaqhwithi, nangelangatye lomlilo otshabalalisayo.', 'XHO22'),
(27, 29, 7, 'Yoba njengephupha ebusuku, ingxokolo yeentlanga zonke eziyiphumela umkhosi iAriyeli;', 'XHO22'),
(27, 29, 8, 'Koba njengokuthi olambileyo aphuphe, akhangele, adle; kodwa uvuka ulambatha umphefumlo wakhe, mhlawumbi njengomntu onxaniweyo aphuphe, akhangele, asele; evuka, nanko ediniwe, nomphefumlo wakhe ulambile; kuya kuba njalo ngengxokolo yeentlanga zonke ezilwa ngokuchasene neNtaba yeZiyon.', 'XHO22'),
(27, 29, 9, 'Yiba wena, umangaliswe; biza kwaye ubize; Banxilile kungewayini; bayaphuthaphutha, kodwa hayi ngesiselo esinxilisayo.', 'XHO22'),
(27, 29, 10, 'Ngenxa yokuba uYehova ukulalise ebuthongweni obunzulu yaye uyawavala amehlo akho; Wagubungela abaprofeti benu neenkokeli zenu, iimboni zenu.', 'XHO22'),
(27, 29, 11, 'Kuwe, umbono wezinto zonke ufana namazwi encwadi etywiniweyo, athi umntu ayinike umphengululi, esithi: “Yifunde; Wathi yena, Andinako; ngokuba iqinisekisiwe.', 'XHO22'),
(27, 29, 12, 'Kwaye incwadi inikwe umntu ongafundanga, kwaye kuthiwa: Yifunde, kwaye athi, Andifundanga.', 'XHO22'),
(27, 29, 13, 'Ngako oko wathi uYehova, Ngenxa enokuba aba bantu besondela kum ngomlomo wabo, bendibeke ngemilebe yomlomo wabo; kanti intliziyo yabo ikude lee kum, nokundoyika kwabo kungomlomo wabantu.', 'XHO22'),
(27, 29, 14, 'Ngako oko, yabona, ndiya kuphinda ndibaphathe aba bantu ummangaliso, ummangaliso, ummangaliso; butshone ubulumko bezilumko zabo, isithele ingqondo yezilumko zabo.', 'XHO22'),
(27, 29, 15, 'Yeha ke abo banqwenela ukufihla iingcinga zabo kuYehova, benze imisebenzi yabo emnyameni, bathi, Ngubani na osibonayo? kwaye ngubani esimaziyo?', 'XHO22'),
(27, 29, 16, 'Ewe, ukutshintshisa kwenu kuya kuba njengodongwe lombumbi; kuba umsebenzi walowo uyenzileyo uthi, Akandenzanga na kum? Okanye ngaba icebo lakhe lithi, Akaqondi?', 'XHO22'),
(27, 29, 17, 'Asiloxesha lifutshane na ukuba iLebhanon ibe yintsimi echumayo, nentsimi echumayo igqalwe njengehlathi?', 'XHO22'),
(27, 29, 18, 'Ngaloo mini baya kuva amazwi encwadi, namehlo eemfama abone ebumnyameni nasebumnyameni.', 'XHO22'),
(27, 29, 19, 'Kwaye abalulamileyo baya kukwandisa ukugcoba kwabo eNkosini, kwaye amahlwempu phakathi kwabantu aya kugcoba ngoyiNgcwele kaSirayeli.', 'XHO22'),
(27, 29, 20, 'Ngokuba ubhuqiwe umngcangcazelisi, utshabalele umgxeki, batshatyalaliswe bonke abalindele ubutshinga.', 'XHO22'),
(27, 29, 21, 'Owenza umntu ngobugwenxa ngenxa yelizwi amthiyele owohlwayayo esangweni, Limkeke ilungisa ilize.', 'XHO22'),
(27, 29, 22, 'Ngako oko, kwathiwa nguYehova, owamkhululayo uAbraham ngendlu kaYakobi, uYakobi akayi kudana, ngoku, nobuso bakhe abuyi kuba buthuntu.', 'XHO22'),
(27, 29, 23, 'Kwaye ekuboneni kwabantwana bakhe, umsebenzi wezandla zam, phakathi kwabo, baya kulingcwalisa igama lam, bamngcwalise oyiNgcwele kaYakobi, bamoyike uThixo kaSirayeli.', 'XHO22'),
(27, 29, 24, 'Kwanemiphefumlo ebhadulayo iya kuqonda, nabo bakhalazayo baya kufunda ukufunda.', 'XHO22'),
(27, 30, 1, 'Yeha ke aba baqhayisi, utsho uYehova; kwaye uyigqume ngesigqubuthelo, kungengamphefumlo wam, ukuze bongeze isono phezu kwesono;', 'XHO22'),
(27, 30, 2, 'owehla waya eYiputa, akabuza emlonyeni wam; ukuba bomeleze ngamandla kaFaro, bakholose ngomthunzi weYiputa!', 'XHO22'),
(27, 30, 3, 'Ngoko ke amandla kaFaro aya kuba lidano kuni, nikholose ngomthunzi weYiputa ngehlazo lenu.', 'XHO22'),
(27, 30, 4, 'Kuba iinkosana zakhe zaziseTsowan, noonozakuzaku bakhe bafike eHanes.', 'XHO22'),
(27, 30, 5, 'Bonke bephela baneentloni ngabantu abangabancedi nganto, kungekho ncedo naluncedo, kodwa beneentloni, behlazekile.', 'XHO22'),
(27, 30, 6, 'Umyalezo wamarhamncwa asezantsi: Ezweni leembandezelo neembandezelo, apho kuphuma khona ingonyama entsha, nengonyama entsha, nenyoka, nenyoka ephaphazelayo, ubutyebi babo baya kubuthwala emagxeni amaesile, nobutyebi babo bube phezu kwamagxa ama-esile ubutyebi obusemagxeni abo imihlambi yeenkamela, kubantu abangenakuyinceda nangayiphi na indlela.', 'XHO22'),
(27, 30, 7, 'AmaYiputa ke anceda ngelize, nangelize; ngenxa yoko ndadanduluka ndathi, Amandla abo ngawokuhlala bathi cwaka.', 'XHO22'),
(27, 30, 8, 'Yiya ngoku, ukubhale etafileni ephambi kwabo, ukubhale encwadini, kube khona ngonaphakade kanaphakade.', 'XHO22'),
(27, 30, 9, 'Ngabantu abazele lukreqo, ngoonyana bobuxoki, ngoonyana abangevayo isiyalo sikaYehova;', 'XHO22'),
(27, 30, 10, 'abathi kwiimboni, Ze ningaboni; nakubaprofeti bathi, Musani ukusiprofetela iinyaniso, ningasiprofeti ubuxoki, ningasiprofeti ubuxoki;', 'XHO22'),
(27, 30, 11, 'Sukani endleleni, phumani endleleni, Lowo Ungcwele kaSirayeli umi phambi kwethu.', 'XHO22'),
(27, 30, 12, 'Ngenxa yeso sizathu, kwathiwa ngoyiNgcwele kaSirayeli: Ngenxa yokuba nilidelile eli lizwi, kwaye nakholosa ngengcinezelo nenkohlakalo, kwaye nihleli kuyo;', 'XHO22'),
(27, 30, 13, 'Ngako oko, obo bungendawo kuni buya kuba njengexesha eliza kuwa, elaphulwa eludongeni olude, oluqhekezwa ngokuqhwanyaza kwalo.', 'XHO22'),
(27, 30, 14, 'Kwaye uya kuwuqhekeza njengokuqhekezwa kwesitya sombumbi akayi kusinda, ukuze ngokudubula kwakhe kungafunyanwa ilaphu lokubasa umlilo eziko, nokuba kukha amanzi equleni.', 'XHO22'),
(27, 30, 15, 'Ngokuba itsho iNkosi, uYehova oyiNgcwele kaSirayeli, ukuthi, Ekuphumleni nasekuphumleni uya kugcinwa; ngokuzolileyo nangethemba liya kuba ngamandla akho, kwaye awuzange ufune.', 'XHO22'),
(27, 30, 16, 'Wathi ke yena, Hayi; ngokuba siya kubaleka ngamahashe; ke ngoko, niya kubaleka, nithi: Siya kukhwela kwiimbaleki; ngoko ke, abo banisukelayo baya kungxama.', 'XHO22'),
(27, 30, 17, 'Niya kubaleka niliwaka, ekukhalimeni komnye; uya kusinda esohlwayweni sabahlanu, ude uhlale njengesibane encotsheni yentaba, nanjengebhanile endulini.', 'XHO22'),
(27, 30, 18, 'Ngako oko uYehova uya kukha alinde ukuze abe nenceba kuni, aphakame, ukuze abe nemfesane kuni; ngokuba nguThixo wogwebo uYehova; hayi, uyolo lwabo bonke abalindele kuye!', 'XHO22'),
(27, 30, 19, 'Ngokuba iintlanga ziya kuhlala eZiyon, eYerusalem; akusenako ukukhala; ukuba akeva, wophendula wena.', 'XHO22'),
(27, 30, 20, 'Nokuba uYehova aninike isonka sobandezelweyo namanzi okubandezeleka, abalusi benu bangabi saphemba ekoneni, amehlo enu esababona abalusi benu;', 'XHO22'),
(27, 30, 21, 'iindlebe zakho zive ilizwi ngasemva kwakho, lisithi, Nantsi indlela; hambani ngayo, nokuba nisinga ekunene, nokuba nisinga ekhohlo.', 'XHO22'),
(27, 30, 22, 'Ize nenze inqambi isigqubuthelo semifanekiso yenu eqingqiweyo yesilivere, nesingxobo semifanekiso yenu segolide; wothi kuyo, Hamba uhambe.', 'XHO22'),
(27, 30, 23, 'Wokunika imvula imbewu yakho, uwuhlwayele umhlaba; nesonka semveliso yomhlaba, sibe sityebi, sibe ninzi; iinkomo zakho ziya kudla ngaloo mini emadlelweni abanzi.', 'XHO22'),
(27, 30, 24, 'Kwangokunjalo iinkomo neemazi zamaesile eziwugcinayo umhlaba ziya kudla ingca ecocekileyo, eselwayo ngemihlakulo, nesefo.', 'XHO22'),
(27, 30, 25, 'Kuya kubakho imijelo nemisinga yamanzi ezintabeni zonke ezinde naphezu kweenduli zonke eziphakamileyo, ngemini yembulalo enkulu, ekuweni kweenqaba ezinde.', 'XHO22'),
(27, 30, 26, 'Kananjalo ukukhanya kwenyanga kuya kuba njengokukhanya kwelanga, nokukhanya kwelanga kube njengokukhanya kweentsuku ezisixhenxe ngokuphindwe kabini, ngemini yokubopha kukaYehova abantu bakhe, apholise inxeba yenxeba labo.', 'XHO22'),
(27, 30, 27, 'Yabona, igama likaYehova liyeza, livela kude, evutha ngumsindo, nomthwalo wakhe unzima; umlomo wakhe uzele ngumsindo, nolwimi lwakhe lunjengomlilo odlayo.', 'XHO22'),
(27, 30, 28, 'Ukuphefumla kwakhe njengomlambo otyhutyhayo, kuya kufikelela esazulwini sentamo, ukuze azitshabalalise iintlanga ngesihluzo samampunge; kwaye kuya kubakho umkhala emihlathini yeentlanga ezibalahlekisayo.', 'XHO22'),
(27, 30, 29, 'Niya kuyivuma ingoma njengobusuku bomthendeleko; nokuchwayitha kwentliziyo njengokuhamba komntu ngesigodlo ukuya entabeni kaYehova, kuMthetheli wakwaSirayeli.', 'XHO22'),
(27, 30, 30, 'uYehova uya kulivakalisa ilizwi lakhe elinendili, alise isandla sakhe esiseleyo, ngokuvutha komsindo wakhe, nokulenya komlilo otshabalalisayo, ekuchithachitheni isaqhwithi nesichotho.', 'XHO22'),
(27, 30, 31, 'Kuba ngelizwi likaYehova umAsiriya obethe ngentonga uya koyiswa.', 'XHO22'),
(27, 30, 32, 'Kuyo yonke indawo apho intsika ayimisayo uYehova phezu kwayo, kuya kubakho amagubu namacangci; uya kulwa naye ngeemfazwe ezingcangcazelisayo.', 'XHO22'),
(27, 30, 33, 'Ngokuba ibikhona iTofete kwakudala; ewe, ilungiselwe ukumkani; wawunzulu, wawenza banzi; imfumba yakhe ingumlilo neenkuni ezininzi; uMoya kaYehova, njengomlambo wesulfure, uwutshisa ngomlilo.', 'XHO22'),
(27, 31, 1, 'Yeha, abehla baye eYiputa ukuba bacele uncedo; abo bakhwele amahashe bakholosa ngeemoto, ngokuba zininzi; nabamahashe, ngokuba beqine kunene; kodwa abakhangeli kuLowo Ungcwele kaSirayeli, yaye abamfuni uYehova!', 'XHO22'),
(27, 31, 2, 'Ulumkile ke naye; uya kuzisa ububi, angawakhumbuli amazwi akhe; uya kuvuka endlwini yenginingini, amandla noncedo lwamanginingini.', 'XHO22'),
(27, 31, 3, 'AmaYiputa ngabantu, akangoThixo; amahashe awo yinyama, akamoya. Ukuba uYehova uyasolula isandla sakhe, umncedi uya kuwa, abo bancedwayo baya kuwa, yaye bonke xa bebonke baya kusilela.', 'XHO22'),
(27, 31, 4, 'Ngokuba utsho uYehova kum ukuthi, Njengengonyama, njengengonyama, igquma ixhoba layo, njengoko bekhala ngako abalusi abaninzi, ayiyi kubawoyika amazwi abo, ayiyi kuthotywa singxokozelo eyabo ayikho. uYehova wemikhosi uya kuhla ngolo hlobo ukuze alwele intaba yaseZiyon nenduli yayo.', 'XHO22'),
(27, 31, 5, 'uYehova wemikhosi uya kuyikhusela iYerusalem njengeentaka; kwaye uya kusikhusela asisindise; kwaye uya kuyigcina.', 'XHO22'),
(27, 31, 6, 'Buyelani kulowo nigwilike kuye oonyana bakaSirayeli;', 'XHO22'),
(27, 31, 7, 'Kuba ngaloo mini baya kuyilahla elowo imifanekiso yakhe yesilivere, nemifanekiso yakhe yegolide, ezonisileyo izandla zenu.', 'XHO22'),
(27, 31, 8, 'Ke kaloku uAsiriya uya kuwa likrele, kungengakrele; Ikrele, asilamntu, liya kumtshabalalisa; kodwa uya kusaba phambi kwekrele, nabafana bakhe baya koyika.', 'XHO22'),
(27, 31, 9, 'Uya kungena enqabeni yakhe esoyika, neenkosana zakhe ziya koyika ibhanile,” utsho uYehova, omlilo uyiZiyon neziko lakhe liseYerusalem.', 'XHO22'),
(27, 32, 1, 'Yabona, ukumkani uya kulawula ngokobulungisa, abathetheli bathethele ngobulungisa;', 'XHO22'),
(27, 32, 2, 'Umntu abe njengendawo yokuzimela xa umoya ubhudlayo, abe njengendawo yokuzimela esiphangweni; njengemijelo yamanzi emhlabeni owomileyo, njengomthunzi wengxondorha enkulu ezweni elibharhileyo.', 'XHO22'),
(27, 32, 3, 'Akayi kuba mnyama amehlo ababonayo; zibazeke iindlebe zabevayo.', 'XHO22'),
(27, 32, 4, 'Intliziyo yabangxamileyo iyakuqonda ukwazi, ulwimi lwabathintithayo lulungiselwe ukuthetha kakuhle.', 'XHO22'),
(27, 32, 5, 'Osweleyo akuyi kuba sathiwa ulilungisa, okanye kuthiwe unesisa;', 'XHO22'),
(27, 32, 6, 'Kuba umntu osisidenge uya kuthetha ububi nentliziyo yakhe yenza ububi ngokuhanahanisa nokumxokisa uYehova, ukuze athulule umphefumlo wolambileyo, yaye uya kusitshabalalisa isiselo sonxaniweyo.', 'XHO22'),
(27, 32, 7, 'Kananjalo izixhobo zongendawo zingendawo: uceba ukutshabalalisa isisweli ngobuxoki, naxa ihlwempu lithetha inyaniso.', 'XHO22'),
(27, 32, 8, 'Kodwa ke abantu abakhululekileyo bacinga ngezinto ezinkulu kwaye uya kuma kwizinto zesisa.', 'XHO22'),
(27, 32, 9, 'Vukani, bathuthuzeli; liveni ilizwi lam, zintombi zingakhathaliyo; yiva intetho yam.', 'XHO22'),
(27, 32, 10, 'Niya koyika imihla emininzi, neminyaka emininzi, zintokazi zingakhathaliyo; ngokuba ukuvuna kuya kuphela;', 'XHO22'),
(27, 32, 11, 'Yibani nemihlali, zintombi zingakhathaliyo; yoyikani, nina bangenamsebenzi; khululani, khululani, bhinqani ezinqeni zenu ilaphu elirhabaxa.', 'XHO22'),
(27, 32, 12, 'Baya kulilela ilize ngenxa yamasimi anqwenelekayo, ngenxa yediliya eneziqhamo.', 'XHO22'),
(27, 32, 13, 'Kuya kuhluma ameva neenkunzane ezweni labantu bam; ewe, nasezindlwini zonke zovuyo kumzi onoyolo;', 'XHO22'),
(27, 32, 14, 'Kuba iingxande ezinde zilahliwe; uninzi lwesixeko luya kuhlala; iinqaba nemboniselo zibe yimihadi ngonaphakade, zibe yimihadi yamahashe asendle, zibe lidlelo leenkomo;', 'XHO22'),
(27, 32, 15, 'ade agalele phezu kwethu uMoya ophuma phezulu, isuke intlango ibe yintsimi echumayo, ibe lintsimi echumayo njengehlathi.', 'XHO22'),
(27, 32, 16, 'Kulapho buya kuhlala ubulungisa entlango, buhlale ubulungisa ezweni elichumayo.', 'XHO22'),
(27, 32, 17, 'Umsebenzi wobulungisa uya kuba luxolo; ukuze umgwebo ube ngowoxolo, uqiniseke ngonaphakade.', 'XHO22'),
(27, 32, 18, 'Kwaye abantu bam baya kuhlala ekwakhiweni koxolo nakwiindawo ezikhuselekileyo nasezindaweni zokuphumla ezithe cwaka;', 'XHO22'),
(27, 32, 19, 'Xa kukho isichotho, yihla uye ehlathini; yaye isixeko siya kuhlala endaweni ephantsi.', 'XHO22'),
(27, 32, 20, 'Hayi, uyolo lwenu bahlwayela ngasemanzini onke, banyathela ngamanqina eenkomo naweesile.', 'XHO22'),
(27, 33, 1, 'Yeha, nina baphangi, ningaphangi; yaye benza isidubedube, kodwa abazange benze isidubedube! ukuba uyayeka ukuphanga, uya kuphangwa; yaye ukuba uyayeka ukwenza ukunganyaniseki, baya kwenza ubuqhophololo kuwe.', 'XHO22'),
(27, 33, 2, 'Yehova, yiba nenceba kuthi; besikulindele; masibe ngamahlwempu kubo rhoqo ekuseni, kwaye siyaphilisa ngamaxesha okubandezeleka.', 'XHO22'),
(27, 33, 3, 'Kuyasaba abantu ngenxa yesandi sesiphithiphithi; ngenxa yokuphakama kwakho ziyaphangalaliswa iintlanga.', 'XHO22'),
(27, 33, 4, 'Yaye amaxhoba enu aya kuhlanganiswa, njengoko izihlwele ziqokelelana; uya kubagila njengeenkumbi.', 'XHO22'),
(27, 33, 5, 'uYehova uphakamile; ngokuba yena ehleli phezulu; uyizalisile iZiyon ngokusesikweni nangobulungisa.', 'XHO22'),
(27, 33, 6, 'Kuya kubakho ukuqiniseka kwamaxesha akho, nobutyebi bosindiso, nobulumko, nokwazi; ukoyika uYehova bubuncwane bakhe.', 'XHO22'),
(27, 33, 7, 'Yabona, amagorha abo ayankqangaza ngaphandle; abathunywa boxolo baya kulila ngokukrakra.', 'XHO22'),
(27, 33, 8, 'Iindlela zezonakele, umhambi umile; Uwaphule umnqophiso, uzidelile iidolophu, akakhathalelanga mntu.', 'XHO22'),
(27, 33, 9, 'Ihlabathi lithe thaa, lilusizi, iLebhanon ineentloni, ishwabene; iSharon isuke yaba njengenkqantosi; neBhashana neKarmele zibutha iziqhamo zazo.', 'XHO22'),
(27, 33, 10, 'Kungokunje ndiya kuma, utsho uYehova; ngoku ndiya kuzinyusa; ngoku ndiya kuziphakamisa.', 'XHO22'),
(27, 33, 11, 'Niya kumitha isikhotha, nizale iindiza; umoya wenu uya kunitshisa njengomlilo.', 'XHO22'),
(27, 33, 12, 'Iintlanga ziya kuba njengekalika etshisiweyo; baya kutshiswa ngomlilo, njengameva ahlahliweyo.', 'XHO22'),
(27, 33, 13, 'Yivani, nina madoda, into endiyenzileyo; nani basondeleyo, niwazi amandla am.', 'XHO22'),
(27, 33, 14, 'Bayankwantya aboni baseZiyon; Uloyiko lwabamangalisa abahanahanisi. Ngubani na kuthi oya kuhlala emlilweni ovuthayo? Ngubani na kuthi oya kuhlala emlilweni ongunaphakade?', 'XHO22'),
(27, 33, 15, 'Ohamba ngobulungisa, othetha ubulungisa; ocekisayo ubutyebi bobubi, ovuthulula izandla zakhe ukuba angamkeli isicengo, ovingca iindlebe zakhe ukuba eve igazi, ocima amehlo akhe ukuba abone ububi;', 'XHO22'),
(27, 33, 16, 'Uya kuhlala ezindaweni eziphakamileyo; Inqaba yakhe yingxonde yeengxondorha; uya kunikwa isonka; amanzi akhe omelela.', 'XHO22'),
(27, 33, 17, 'Amehlo akho aya kumkhangela ukumkani ebuqaqawulini bakhe, Elimiweyo elikude.', 'XHO22'),
(27, 33, 18, 'Intliziyo yakho iya kucamanga kukoyika; Uphi umbhali? uphi umamkeli obebala iinqaba?', 'XHO22'),
(27, 33, 19, 'uhlanga lomlilo akuyi kulubona, uhlanga oluntetho inzulu kunokuba ulubona; Ngolwimi olukhubekisayo, eningenakuluqonda.', 'XHO22'),
(27, 33, 20, 'Khangela phezu kweZiyon, isixeko somtshato wethu; ezintongeni zawo akuyi kuncothuka, neentambo zawo zonke aziyi kunqunyulwa.', 'XHO22'),
(27, 33, 21, 'Kodwa apho kuthi uYehova ozukileyo uya kuba yindawo enemilambo ebanzi nemisinga equkuqelayo; apho kungangeni khona mkhombe ubheqayo, kungekho nanqanawa yobukroti inokudlula khona.', 'XHO22'),
(27, 33, 22, 'Ngenxa yokuba uYehova enguMgwebi wethu, uYehova nguMmisi-mthetho wethu, uYehova nguKumkani wethu; yena wosisindisa.', 'XHO22'),
(27, 33, 23, 'Izalisekile imisebenzi yakho; aziyibopheleli iseyile yazo, aziyitwabululi iseyile; kulapho kwabiwe khona amaxhoba amaninzi amaxhoba iziqhwala ziya kuphanga.', 'XHO22'),
(27, 33, 24, 'Akanako ummi wakhona ukuthi, Ndiyafa; abantu abahlala khona bobuxolelwe ubugwenxa babo.', 'XHO22'),
(27, 34, 1, 'Sondelani, zintlanga, nive; yivani, zintlanga; malive ihlabathi nenzaliseko yalo; elimiweyo nenzaliseko yalo.', 'XHO22'),
(27, 34, 2, 'Ngokuba umsindo kaYehova uphezu kweentlanga zonke, nobushushu bakhe buphezu komkhosi wazo wonke; Wababetha ngesiqalekiso, wabanikela ekusikweni.', 'XHO22'),
(27, 34, 3, 'Ababuleweyo baya kungcwatywa, kunyuke ukunuka kwabo ezidumbini zabo, zigutyungelwe iintaba ligazi labo.', 'XHO22'),
(27, 34, 4, 'Lonke ibhunga lezulu liya kutshabalala, izulu lisongwe njengombhalo, onke amacandelo alo awile njengegqabi lomdiliya eliwayo, nanjengekhiwane eliwa emkhiwaneni.', 'XHO22'),
(27, 34, 5, 'Ngokuba ezulwini ikrele lam liya kuhlanjwa; yabona, liya kuhla phezu koEdom, naphezu kwabantu endisiqalekiso, ibe ngumgwebo.', 'XHO22'),
(27, 34, 6, 'Umkhonto kaYehova uzele ligazi, utyetyiswe ngamanqatha, uligazi leemvana nezeenciniba, namanqatha ezintso zeenkunzi zeegusha; ngenxa yokuba uYehova unombingelelo eBhotsera nokuxhela okukhulu kwilizwe lakwaEdom.', 'XHO22'),
(27, 34, 7, 'Kuya kuhla iinqu kunye nabo, iinkunzi ezintsha, kunye neenkomo; yaye ilizwe labo liya kumanziswa ligazi, nothuli lwabo luya kutyeba.', 'XHO22'),
(27, 34, 8, 'Ngokuba le yimini yempindezelo kaYehova, unyaka wempindezelo ibango leZiyon.', 'XHO22'),
(27, 34, 9, 'Imilambo yabo iya kuba ngumthwebeba, uthuli lwabo lube yisalfure, ilizwe labo libe yitela etshayo.', 'XHO22'),
(27, 34, 10, 'Aliyi kucinywa imini nobusuku; umsi wayo uya kunyuka ngonaphakade; Ihlala isenkangala kwizizukulwana ngezizukulwana; akuyi kucanda mntu kulo, kuse emaphakadeni asemaphakadeni.', 'XHO22'),
(27, 34, 11, 'Ke bona ububi nobukrakra bobudla ilifa; kuhlale khona isikhova nehlungulu, abeke phezu kwalo irhengqa lesidubedube namatye angamampunge.', 'XHO22'),
(27, 34, 12, 'Baya kubizela ebukumkanini izidwangube zakhe, kungabikho bani khona, zonke iinkosana zakhe zibe lilize.', 'XHO22'),
(27, 34, 13, 'Kuya kuhluma imithana enameva ezingxandeni zakhe, amarhawu namakhakakhaka ezingxandeni zakhe ezinde;', 'XHO22'),
(27, 34, 14, 'Amarhamncwa omqwebedu okunene aya kuqubisana namarhamncwa esi siqithi, nenjengele ibize iqabane layo; nesikhova siya kuphumla khona size siyifumanele indawo yokuphumla.', 'XHO22'),
(27, 34, 15, 'Kulapho inyoka ejuba ijuba iya kwenza indlwane yayo, izale amaqanda, ifukame emthunzini wayo; yaye amaxhalanga aya kuhlanganisana, elowo neqabane lakhe.', 'XHO22'),
(27, 34, 16, 'Khangelani incwadi kaYehova, niyilese; ngokuba ndiwuwisele umthetho umlomo wam, ukuba uzihlanganise nomoya wakhe.', 'XHO22'),
(27, 34, 17, 'Wabalawula ngamaqashiso, Sabatyakatya ke isandla sakhe; baya kulidla ilifa ngonaphakade, bahlale kulo ukusuka kwesinye isizukulwana kuse kwesinye.', 'XHO22'),
(27, 35, 1, 'Inemihlali intlango nomqwebedu; iyagcoba inkqantosi, iyadubula njengentyatyambo.', 'XHO22'),
(27, 35, 2, 'Liya kutyatyamba kakhulu, libe nemihlali, ewe, ngovuyo nemihlali; ziya kububona ubuqaqawuli bukaYehova, nozuko loThixo wethu.', 'XHO22'),
(27, 35, 3, 'Yomelezani izandla eziwileyo, niwaqinise amadolo agexayo.', 'XHO22'),
(27, 35, 4, 'Yithini kwabantliziyo zinkwantyayo, Yomelelani, ningoyiki; yabonani, uThixo wenu uyeza enempindezelo, nguThixo ophindezelayo; uya kuza kunihlangula.', 'XHO22'),
(27, 35, 5, 'Aya kuvulwa amehlo eemfama, zivulwe iindlebe zezithulu;', 'XHO22'),
(27, 35, 6, 'Isiqhwala siya kutsiba njengexhama, nolwimi lwesimumu luya kukhala; kuba amanzi aya kugalela engobozini, nemilambo engobozini.', 'XHO22'),
(27, 35, 7, 'Umhlaba owomileyo wojika ube lidike, owomileyo ube yimithombo yamanzi; Kuba apho zilala khona iimpungutye, kuya kubakho utyani ngeengcongolo neengcongolo.', 'XHO22'),
(27, 35, 8, 'Kuya kubakho umendo, kubekho umendo, kuthiwe ukubizwa kwayo, yindlela engcwele le oyinqambi akayi kudlula kuyo; iya kuba yeyabo bona: abahambayo nokuba bezizidenge, abasayi kumka kuyo.', 'XHO22'),
(27, 35, 9, 'Akuyi kubakho ngonyama khona, aliyi kuphuma rhamncwa khona, aliyi kufunyanwa khona; ke abakhululwa ngokuhlawulelwa baya kuhamba khona;', 'XHO22'),
(27, 35, 10, 'Kwaye abakhululwa beNkosi baya kubuya kwaye baya kuza eZiyon beneengoma novuyo olungayi kubuyela kwiintloko zabo; baya kubafumana imihlali nemivuyo, sisabe isingqala nokuncwina.', 'XHO22'),
(27, 36, 1, 'Ngomnyaka weshumi elinesine wobukumkani bukaHezekiya, ukumkani waseAsiriya uSenaribhe wayithimba yonke imizi enqatyisiweyo yakwaYuda, wayingenela.', 'XHO22'),
(27, 36, 2, 'Ukumkani waseAsiriya wathumela abathetheli baseLakishe eYerusalem kukumkani uHezekiya, benempi enkulu. Wema kwitonela lechibi eliphezulu, kwindlela enkulu yomyezo womalusi.', 'XHO22'),
(27, 36, 3, 'Waphuma waya kuye uEliyakim unyana kaHilekiya umgcini wendlu noShebhena unobhala noYowaki unyana ka-Asafu umphathi wenkundla.', 'XHO22'),
(27, 36, 4, 'Wathi kubo uRabheshake, Yithini kuHezekiya, Itsho iNkosi enkulu, ukumkani waseAsiriya, ukuthi, Liyintoni na ithemba lakho?', 'XHO22'),
(27, 36, 5, 'Ndithi (kakade la ngamazwi alambathayo), ndinengqiqo namandla emfazwe; ngoku ukholose ngabani na, ukreqile nje kum?', 'XHO22'),
(27, 36, 6, 'Uyabona, ukholose ngeYiputa, ngentonga yengcongolo evikivekileyo; eya kuthi, xa athe umntu ayame ngayo, imngene esandleni, imhlabe; uya kuba njalo kuFaro, ukumkani waseYiputa, nakubo bonke abakholose ngaye.', 'XHO22'),
(27, 36, 7, 'Ke ukuba nithi kum, Sikholose ngoYehova uThixo wethu, ndithi, Asinguye na uHezekiya owadiliza izibingelelo eziphakamileyo nezibingelelo zakhe, wathi kumaYuda nakwiYerusalem, Ize niqubude phambi kwesi sibingelelo sodwa?', 'XHO22'),
(27, 36, 8, 'Ke kaloku, yenza isibambiso kukumkani wam, ukumkani waseAsiriya, yaye ndokunika amawaka amabini amahashe, ukuba uthe wabeka abakhweli-mahashe ecaleni kwakho.', 'XHO22'),
(27, 36, 9, 'Wothini na ke ukulahla omnye wakubathetheli babakhonzi abancinane benkosi yam, ukholose ngeYiputa ngeenqwelo zokulwa nangabamahashe?', 'XHO22'),
(27, 36, 10, 'Yaye ngoku ndiliphakamisile eli hlabathi ngaphandle kukaYehova ukuba ndilitshabalalise? uYehova uthe kum, Nyuka uye kweli lizwe, ulonakalise.', 'XHO22'),
(27, 36, 11, 'Wathi uEliyakim, noShebhena, noYowaki kuRabheshake, Thetha kubakhonzi bakho ngesi-Aram; ngokuba siyayiqonda, anithethi kuthi ngesiYuda ezindlebeni zabantu abaseludongeni.', 'XHO22'),
(27, 36, 12, 'Wathi uRabheshake, Inkosi yam indithume ukuba ndiwathethe la mazwi enkosini yakho na, nakuwe na? Ndathi ke, akandithumanga na kumadoda abehleli eludongeni, ukuze adle ilindle lawo, asele newayini yawo?', 'XHO22'),
(27, 36, 13, 'Wesuka wema uRabheki, wadanduluka ngelizwi elikhulu ngolwimi lwamaYuda, wathi: “Waveni amazwi okumkani omkhulu, ukumkani waseAsiriya.', 'XHO22'),
(27, 36, 14, 'Utsho uYehova ukuthi, Makanganilukuhli uHezekiya, ngokuba akayi kuba nako ukunihlangula.', 'XHO22'),
(27, 36, 15, 'Musani ukumvumela uHezekiya anikholise ngoYehova ngokuthi, Inene, uYehova wosisindisa; lo mzi awuyi kunikelwa esandleni sokumkani waseAsiriya.', 'XHO22'),
(27, 36, 16, 'Musani ukumphulaphula uHezekiya, ngokuba uthi kukumkani waseAsiriya, Yenza umnqophiso nam ngomnikelo, uphume kum; badle elowo iidiliya zakhe, badle elowo umkhiwane wakhe, basele elowo amanzi equla lakhe;', 'XHO22'),
(27, 36, 17, 'ndide ndize ndinise ezweni elinjengelizwe lenu, ilizwe lengqolowa newayini entsha, ilizwe lesonka nezidiliya.', 'XHO22'),
(27, 36, 18, 'Zilumkeleni, hleze anilahlekise uHezekiya, esithi, uYehova wosisindisa. Oothixo beentlanga balihlangule na ilizwe labo esandleni sokumkani waseAsiriya?', 'XHO22'),
(27, 36, 19, 'Baphi na oothixo baseHamati neArfadi? Baphi na oothixo baseSefarvayim? Balihlangule na elakwaSamari esandleni sam?', 'XHO22'),
(27, 36, 20, 'Nguwuphi na koothixo bonke bala mazwe owalihlangulayo ilizwe lakhe esandleni sam, ukuze uYehova ayisindise iYerusalem esandleni sam?', 'XHO22'),
(27, 36, 21, 'Bathi cwaka ke, abamphendula nelizwi elinye; ngokuba umthetho wokumkani ubusithi, Musani ukumphendula.', 'XHO22'),
(27, 36, 22, 'uEliyakim unyana kaHilekiya owayephethe indlu uShebhena unobhala noYowaki unyana ka-Asafu irhuluneli beza kuHezekiya beneengubo ezikrazukileyo baza bamxelela amazwi kaRabheshake.', 'XHO22'),
(27, 37, 1, 'Akuva ukumkani uHezekiya, wazikrazula iingubo zakhe, wazigqubuthela ngezirhwexayo, wangena endlwini kaYehova.', 'XHO22'),
(27, 37, 2, 'Wathuma uEliyakim, umgcini wendlu, noShebhena umbhali, nababingeleli abakhulu, bezibhinqe ezirhwexayo, kuIsaya umprofeti, unyana ka-Amos.', 'XHO22'),
(27, 37, 3, 'Bathi kuye, Kwathiwa nguHezekiya, Namhlanje yimini yokusindeka, yeyokugiba, yogiba; ngokuba kuzelwe oonyana, akakho ke amandla okuzala.', 'XHO22'),
(27, 37, 4, 'Mhlawumbi uYehova uThixo wakho uya kuwava amazwi kaRabheshake, lowo ukumkani waseAsiriya, inkosi yakhe, emthumeleyo ukungcikiva uTHIXO ophilayo, yaye uya kuwakhalimela amazwi uYehova uThixo wakho awavileyo; ke ngoko, phakamisa umthandazo wakho ngenxa yentsalela.', 'XHO22'),
(27, 37, 5, 'Beza ke abakhonzi bokumkani uHezekiya kuIsaya.', 'XHO22'),
(27, 37, 6, 'Wathi uIsaya kubo, Yizani, nithi enkosini yenu, Utsho uYehova ukuthi, Musa ukuwoyika amazwi owavileyo, abawenze kum abakhonzi bokumkani waseAsiriya.', 'XHO22'),
(27, 37, 7, 'Yabona, ndimthumela umoya, ukuba eve udaba olubi, abuyele ke ezweni lakhe; yaye ndiya kumnqumla ngekrele elizweni lakhe.', 'XHO22'),
(27, 37, 8, 'Ngoko zabuya iinkosana zikaRabheshake zaza zamfumana ukumkani waseAsiriya esilwa neLibhena, ngenxa yokuba wayevile ukuba wayeyishiyile iLakishe.', 'XHO22'),
(27, 37, 9, 'Weva uTiraka, ukumkani wakwaKushi, wathi, Uphumile weza kulwa nawe. Wathi akukuva oko, wathumela abathunywa kuHezekiya, esithi,', 'XHO22'),
(27, 37, 10, 'Yiza uthethe kuHezekiya, ukumkani wakwaYuda, uthi, Makangakulukuhli uThixo wakho, okholose ngaye wena, usithi, Ayisayi kunikelwa iYerusalem esandleni sokumkani waseAsiriya.', 'XHO22'),
(27, 37, 11, 'Uyabona, ukuvile wena abakwenzayo ookumkani baseAsiriya kumazwe onke, ngokuwatshabhisa; kwaye uya kusindiswa?', 'XHO22'),
(27, 37, 12, 'Ngaba oothixo beentlanga ezazitshabalalisa ookhokho babasindisa, iGoshan neHaran neRetsefe noonyana base-Eden ababeseTelazare?', 'XHO22'),
(27, 37, 13, 'Uphi na ukumkani waseHamati nokumkani waseArpadi nokumkani wesixeko saseSefarvayim neHena neIwa?', 'XHO22'),
(27, 37, 14, 'Wawathabatha uHezekiya amaphepha encwadi esandleni sabathunywa, wawafunda; yaye uHezekiya wenyuka waya endlwini kaYehova waza wayivula phambi koYehova.', 'XHO22'),
(27, 37, 15, 'uHezekiya wathandaza kuYehova wathi:', 'XHO22'),
(27, 37, 16, 'Yehova wemikhosi, Thixo kaSirayeli, wena uhleli phezu kweekerubhi, unguThixo wena wedwa kwizikumkani zonke zehlabathi; Wenza izulu nehlabathi.', 'XHO22'),
(27, 37, 17, 'Thoba, Yehova, uve; vula amehlo akho, Yehova, ubone, uve onke amazwi kaSaneribhe, awathumele ukungcikiva uThixo ophilileyo.', 'XHO22'),
(27, 37, 18, 'Okunene, uYehova, ookumkani baseAsiriya baziphanzisile zonke iintlanga namazwe azo;', 'XHO22'),
(27, 37, 19, 'babanikela oothixo bazo emlilweni, kuba babengethixo, bebengumsebenzi wezandla zomntu, umthi nelitye; babatshabalalisa ke.', 'XHO22'),
(27, 37, 20, 'Ke kaloku, Yehova Thixo wethu, sihlangule esandleni sakhe, ukuze zonke izikumkani zehlabathi zazi ukuba nguwe wedwa uYehova.', 'XHO22'),
(27, 37, 21, 'Wathumela ke uIsaya, unyana ka-Amotsi, kuHezekiya, esithi, uYehova, uThixo kaSirayeli, uthe, njengoko undithandazele ngako, ngenxa kaSaneribhe ukumkani waseAsiriya;', 'XHO22'),
(27, 37, 22, 'Lilo eli ilizwi awalithethayo uYehova ngaye; Intombi, intombi enguZiyon, egxeka, ihleka; Intombi enguYerusalem yahlunguzela intloko phezu kwakho.', 'XHO22'),
(27, 37, 23, 'Ngubani na lo umtshabhisileyo, umnyelisileyo? Uliphakamise kubani na ilizwi lakho, uwaphakamisele amehlo akho kubani na? nxamnye naLowo Ungcwele kaSirayeli.', 'XHO22'),
(27, 37, 24, 'Ungcikivile uYehova ngabakhonzi bakho, usithi, Ngobuninzi beenqwelo zam zokulwa ndiqabele mna encotsheni yeentaba, ekupheleni kweLebhanon; ndiya kuyigawula imisedare yakhe emide kakhulu, nemisipres yakhe; yaye ndiya kungena kwindawo ephakamileyo yelizwe lakhe nakwihlathi leKarmele yakhe.', 'XHO22'),
(27, 37, 25, 'Ndemba, ndasela amanzi; ndayitshisa ngokunyathela konyawo lwam yonke imilambo engamanxuwa.', 'XHO22'),
(27, 37, 26, 'Anikuva na kwangemihla yamandulo ukuba ndenze le nto; kwamandulo, ukuba ndandikudalile? Ndiyakwenza oku, ukuba uyenze amanxuwa imizi enqatyisiweyo.', 'XHO22'),
(27, 37, 27, 'Ngeso sizathu, babengabemi abanamandla amancinane, besoyika kwaye beneentloni;', 'XHO22'),
(27, 37, 28, 'Kodwa ndiyakwazi ukuhlala kwakho, nokuphuma kwakho, nokungena kwakho, nokundiqumba kwakho.', 'XHO22'),
(27, 37, 29, 'Ngenxa enokuba unyuke umsindo wakho kum, nesandi sakho esi ezindlebeni zam, ndiya kufaka isando sam empumlweni yakho, nomkhala wam emlonyeni wakho, ndikubuyisele ngendlela oweza ngayo.', 'XHO22'),
(27, 37, 30, 'Ke lo ngumqondiso kuwe: Kulo nyaka kodliwa ummilela wodwa; ngomnyaka wesibini nowuqala, ngomnyaka wesithathu nohlwayela, nivune, nityale izidiliya, nidle iziqhamo zazo.', 'XHO22'),
(27, 37, 31, 'Kwaye abasindileyo bendlu yakwaYuda baya kuphinda bendelisele iingcambu phantsi baze bathwale isiqhamo phezulu.', 'XHO22'),
(27, 37, 32, 'Ngokuba amasalela aya kuza evela eYerusalem, nabasabayo entabeni yeZiyon; ubukhwele bukaYehova wemikhosi buya kukwenza oko.', 'XHO22'),
(27, 37, 33, 'Ngako oko, utsho uYehova ngokumkani waseAsiriya, ukuthi, Akayi kungena kulo mzi, akayi kutola notolo kuwo, akayi kuwuzela enengweletshetshe, akayi kuwukhela udonga lwangaphambili.', 'XHO22'),
(27, 37, 34, 'Uya kubuya ngendlela eze ngayo, yaye akayi kungena kulo mzi, utsho uYehova.', 'XHO22'),
(27, 37, 35, 'Ngokuba ndiya kuwukhusela lo mzi, ndiwusindise ngenxa yam, nangenxa kaDavide, umkhonzi wam.', 'XHO22'),
(27, 37, 36, 'Kwaphuma ke isithunywa sikaYehova, saxabela eminqubeni yama-Asiriya ikhulu elinamanci asibhozo anesihlanu lamawaka; bavuka kusasa, bezizidumbu ezifileyo bonke.', 'XHO22'),
(27, 37, 37, 'Ke kaloku uSenaribhe, ukumkani waseAsiriya, wemka, waya kuhlala eNineve.', 'XHO22'),
(27, 37, 38, 'Kwathi, ethandaza endlwini yothixo wakhe uNiserogi, bambulala ngekrele oonyana bakhe, uAdrameleki noSaretsere; yaye basabela kwilizwe lakwa-Aram, yaye uEsar-hadon unyana wakhe walawula esikhundleni sakhe.', 'XHO22'),
(27, 38, 1, 'Ngelo xesha wahliwa sisifo uHezekiya, wafa. Waya umprofeti uIsaya, unyana ka-Amos, kuye, wathi kuye, Utsho uYehova ukuthi, Zilawule indlu yakho, ngokuba uya kufa, akuyi kuphila.', 'XHO22'),
(27, 38, 2, 'uHezekiya wabubhekisa eludongeni ubuso bakhe, wathandaza kuYehova.', 'XHO22'),
(27, 38, 3, 'Ndathi mna, Camagu, Yehova, ndikhumbule ukuhamba kwam phambi kwakho ngokunyaniseka nangentliziyo epheleleyo, ndakwenza okulungileyo emehlweni akho. uHezekiya walila ngokukrakra.', 'XHO22'),
(27, 38, 4, 'Kwafika ilizwi likaYehova kuIsaya, lisithi:', 'XHO22'),
(27, 38, 5, 'Wathi kuHezekiya, Utsho uYehova, uThixo kaDavide uyihlo, ukuthi, Ndiwuvile umthandazo wakho, ndizibonile iinyembezi zakho; uyabona, ndiya kongezelela kwimihla yakho ishumi elinesihlanu leminyaka.', 'XHO22'),
(27, 38, 6, 'Ndiza kukusindisa wena nesi sixeko esandleni sokumkani waseAsiriya, yaye ndiya kusikhusela esi sixeko.', 'XHO22'),
(27, 38, 7, 'Nangu ke umqondiso kuwe, uphuma kuYehova, ukuba uya kulenza uYehova elo lizwi alithethileyo;', 'XHO22'),
(27, 38, 8, 'Yabona, ndiyasibuyisa isithunzi sezinyuko esawayo kumzekelo ka-Ahazi, ngezinyuko ezilishumi. Labuya ke ilanga izinyuko ezilishumi ngokwezinyuko elisatshona kuzo.', 'XHO22'),
(27, 38, 9, 'Into eyabhalwa nguHezekiya ukumkani wakwaYuda, xa wayesifa, wachacha ekufeni kwakhe:', 'XHO22'),
(27, 38, 10, 'Ndathi ke ekupheleni kwemihla yam, Ndiya kuya emasangweni elabafileyo;', 'XHO22'),
(27, 38, 11, 'Ndathi, Andiyi kumbona uYehova, uYehova, ezweni labamvayo;', 'XHO22'),
(27, 38, 12, 'Umoya wam uphelile, uphelile kum, njengentente yomalusi; Ndibuchebile ubomi bam njengomluki; uya kundithabatha ngesifo esibulalayo; Uya kundidlisa ubomi imini nobusuku.', 'XHO22'),
(27, 38, 13, 'Ndathi kwada kwasa, uya kuwaqhekeza onke amathambo am njengengonyama; Uya kundidlisa ubomi imini nobusuku.', 'XHO22'),
(27, 38, 14, 'Ndalila njengompelwane; amehlo am ayaphela kukubheka phezulu; ndizenze ngathi.', 'XHO22'),
(27, 38, 15, 'Ndiya kuthini na? wathetha nam, wayenza ngokwakhe; Ndiya kuyihamba kakuhle iminyaka yam, ndinomoya owaphukileyo.', 'XHO22'),
(27, 38, 16, 'Nkosi yam, bubomi bomphefumlo wam; wondibuyisa ke, undiphilise.', 'XHO22'),
(27, 38, 17, 'Yabona, bendiba nomvandedwa ngenxa yoxolo, kodwa ngokuthanda umphefumlo wam wawusindisa esihogweni sesihogo, ngenxa enokuba wazilahlela emva kwakho zonke izono zam.', 'XHO22'),
(27, 38, 18, 'Kuba iHadesi aliyi kukudumisa, ukufa akuyi kukudumisa; abahla baye emhadini abayi kukholwa yinyaniso yakho.', 'XHO22'),
(27, 38, 19, 'Lowo udla ubomi, lo udla ubomi, nguye oya kukudumisa, njengoko ndisenjenjalo namhla: ubawo uya kubazisa inyaniso yakho oonyana bakho.', 'XHO22'),
(27, 38, 20, 'uYehova ebethanda ukundisindisa; ngoko ke sobetha uhadi yonke imihla yobomi bethu endlwini kaYehova.', 'XHO22'),
(27, 38, 21, 'Ngokuba wathi uIsaya, Mabathabathe isipha samakhiwane, bawafake njengodaka, niphiliswe.', 'XHO22'),
(27, 38, 22, 'Wathi uHezekiya, Uyintoni na umqondiso wokuba ndiya kunyuka ndiye endlwini kaYehova?', 'XHO22'),
(27, 39, 1, 'Ngelo xesha uMerodage-bhaladan unyana kaBhaladan, ukumkani waseBhabheli, wathumela incwadi nezipho kuHezekiya; ngokuba ubevile ukuba ubesifa, wabuya wachacha.', 'XHO22'),
(27, 39, 2, 'Wakholiswa ngabo uHezekiya, wababonisa indlu yakhe yengqwebo, isilivere, negolide, nobulawu, neoli entle, nendlu yakhe yonke yeentonga zakhe, nento yonke eyayikho ebuncwaneni bakhe akwabakho nto endlwini yakhe nakubukumkani bakhe bonke, angabavumelanga uHezekiya ukuba bayibone.', 'XHO22'),
(27, 39, 3, 'Waya umprofeti uIsaya kukumkani uHezekiya, wathi kuye, Atheni na la madoda? bevela phi na ukuza kuwe? Wathi uHezekiya, Beza kum, bevela ezweni elikude, eBhabheli.', 'XHO22'),
(27, 39, 4, 'Wathi, Abone ntoni na endlwini yakho? Wathi uHezekiya, Abone into yonke esendlwini yam; akubangakho nto ndingababonisanga yona ebuncwaneni bam.', 'XHO22'),
(27, 39, 5, 'Wathi uIsaya kuHezekiya, Liphulaphule ilizwi likaYehova wemikhosi;', 'XHO22'),
(27, 39, 6, 'Yabona, iyeza imihla yaxa konke okusendlwini yakho noko kwaqwetywa ngooyihlo kwada kwayile mini kuya kuthinjwa ngayo kusiwe eBhabhiloni; akuyi kusala nto, utsho uYehova.', 'XHO22'),
(27, 39, 7, 'Koonyana bakho abaya kuphuma kuwe, oya kubazala, ubasuse; yaye ziya kugcinwa kwibhotwe likakumkani waseBhabhiloni.', 'XHO22'),
(27, 39, 8, 'Wathi uHezekiya kuIsaya, Lilungile ilizwi likaYehova olithethileyo. Waqhubeka wathi: Kuba kuya kubakho uxolo nenyaniso ngemihla yam.', 'XHO22'),
(27, 40, 1, 'Thuthuzelani, bathuthuzeleni abantu bam, utsho uThixo wenu.', 'XHO22'),
(27, 40, 2, 'Ziveni nikhululekile ukuthetha neYerusalem, nimemeze kuyo, nithi kuphelile ukulwa kwayo, nokuba buxolelwe ububi bayo; ngokuba wamkela esandleni sikaYehova kabini ngenxa yezono zakhe zonke.', 'XHO22'),
(27, 40, 3, 'Yivani odandulukayo entlango, lisithi, Yigeceni entlango indlela kaYehova;', 'XHO22'),
(27, 40, 4, 'Yonke imifula iya kuphakanyiswa, zonke iintaba neenduli zonke zigungxulwe; iindawo ezimagqagala zibe zithabazi, iindawo ezimagqagala zibe zithabazi;', 'XHO22'),
(27, 40, 5, 'Bubonakala ubuqaqawuli bukaYehova, ibubone inyama yonke kunye, ngokuba uthethile umlomo kaYehova.', 'XHO22'),
(27, 40, 6, 'Ilizwi lathi: Wowu. Anathi: Ndibize ntoni? Yonke inyama iyingca, bonke ubuhle bayo bunjengentyantyambo yasendle;', 'XHO22'),
(27, 40, 7, 'Buyoma utyani, iyabuna intyantyambo; ngokuba umoya kaYehova uvuthuza kubo; ngokwenene, banjani abantu.', 'XHO22'),
(27, 40, 8, 'Buyoma utyani, iyabuna intyantyambo; ke lona ilizwi likaThixo wethu lithembekile ngonaphakade.', 'XHO22'),
(27, 40, 9, 'Wena Ziyon, mshumayeli weendaba ezilungileyo, nyuka uye entabeni ephakamileyo; Yerusalem, mshumayeli weendaba ezilungileyo, liphakamise ngamandla ilizwi lakho; ziphakamiseni, musani ukoyika; uthi kwimizi yakwaYuda, Nanko uThixo wenu;', 'XHO22'),
(27, 40, 10, 'Uyabona, iNkosi uYehova iya kuza, inesandla esithe nkqi, isandla sayo siya kuyilawula; yabona, umvuzo wakhe unawo, esebenza phambi kwakhe.', 'XHO22'),
(27, 40, 11, 'Iya kuwalusa umhlambi wayo njengomalusi; uya kuwabutha amatakane esandleni sakhe, awathwale ngesifuba sakhe, abathundeze ngocoselelo abathuthuzeli.', 'XHO22'),
(27, 40, 12, 'Ngubani na owalinganisa amanzi kumqolo wesandla sakhe, walinganisa amazulu ngentende yesandla sakhe, wathabatha uthuli lomhlaba ngomlinganiselo walinganisa iintaba ngesikali neenduli ngesikali?', 'XHO22'),
(27, 40, 13, 'Ngubani na owalathisa uMoya kaYehova, wamfundisa, ingenguye umcebisi wakhe?', 'XHO22'),
(27, 40, 14, 'Wacebisana nabani na, owamfundisa nowamfundisa indlela yomgwebo nowamfundisa ukwazi nowamazisa indlela yokuqonda?', 'XHO22'),
(27, 40, 15, 'Yabona, iintlanga zibalelwa ekubeni zinjengethontsi emphandeni, njengesuntswana esikalini; yabona, iziqithi uzigqala njengento encinane.', 'XHO22'),
(27, 40, 16, 'iLebhanon ayilingene kubaswa, neenkomo zayo azilingene kubalidini elinyukayo.', 'XHO22'),
(27, 40, 17, 'Zonke iintlanga zinjengento engento phambi kwakhe; kuye zibalelwa ekubeni ngamampunge namampunge.', 'XHO22'),
(27, 40, 18, 'Ningamfanekisa ke nabani na uThixo? okanye unokumfanisa?', 'XHO22'),
(27, 40, 19, 'Umfanekiso oqingqiweyo utyhidwa yingcibi, awaleke ngegolide umnyibilikisi, enze nemixokelelwane yesilivere.', 'XHO22'),
(27, 40, 20, 'Ihlwempu likhetha umthi ongento yanto; ufuna umsebénzi olichule wokulungisa lo mfanekiso uqingqiweyo.', 'XHO22'),
(27, 40, 21, 'Beningazi na? Anivanga na? Anixelelwanga na kwasekuqaleni? Aniqondanga na na iziseko zehlabathi?', 'XHO22'),
(27, 40, 22, 'Nguye lo uhleli esazulwini sehlabathi, abemi balo banjengeenkumbi; waneka izulu njengeqhiya, walaneka njengentente yokuhlala;', 'XHO22'),
(27, 40, 23, 'Kuyabatshabalalisa abathetheli; Uyabatshabalalisa abagwebi behlabathi.', 'XHO22'),
(27, 40, 24, 'Ewe, aziyi kumiselwa; ewe, azinakutyalwa; ewe, uhlanga lwabo aluyi kumila emhlabeni; uya kubuya azivule, zome, zithabathe umoya ovuthuzayo njengeendiza.', 'XHO22'),
(27, 40, 25, 'Ke kaloku niya kundifanisa, ndilingane nabani? Watsho uNgcwele.', 'XHO22'),
(27, 40, 26, 'Phakamisani amehlo enu, nikhangele, ukuba ngubani na owazidalayo ezo zinto; Uzibiza zonke ziphela ngamagama, ngenxa yobukhulu bamandla akhe, ngenxa enokuba ekhaliphile ngamandla; akukho namnye usilelayo.', 'XHO22'),
(27, 40, 27, 'Yini na ukuba uthi, Yakobi, Indlela yam isithele kuYehova, nebango lam limkile kuThixo wam?', 'XHO22'),
(27, 40, 28, 'Beningazi na? Ngaba akuvanga na ukuba uThixo ongunaphakade, uYehova, uMdali weziphelo zomhlaba, akaphelelwa lithemba okanye aphelelwe lithemba? akukho ukuhlolwa kwengqondo kuye.', 'XHO22'),
(27, 40, 29, 'Umnika otyhafileyo amandla; nalowo ungenamandla uyawongezelela ukuqina kwawo.', 'XHO22'),
(27, 40, 30, 'Namadodana adinwe atyhafe, amadodana akhubeke aphele.', 'XHO22'),
(27, 40, 31, 'Ke bona abakholose ngoYehova bona amandla amatsha; baya kunyuka ngamaphiko njengexhalanga; baya kugidima, bangadinwa; baya kuhamba bangatyhafi.', 'XHO22'),
(27, 41, 1, 'Yithini cwaka phambi kwam, nina ziqithi; izizwe mazihlaziye amandla azo; makasondele kubo; mabathethe ke; masisondele kunye ematyaleni.', 'XHO22'),
(27, 41, 2, 'Ngubani na ovuse empumalanga lowo ukhawulelwa bubulungisa ezinyaweni zakhe? Wabanika ikrele lakhe njengothuli, nesaphetha sakhe sanjengeengcongolo ezivuthelweyo;', 'XHO22'),
(27, 41, 3, 'Wabasukela, wagqitha enoxolo; naye ke akahamba ngeenyawo.', 'XHO22'),
(27, 41, 4, 'Ngubani na owakwenzayo oku, owabiza izizukulwana kwasentloko? ndinguYehova, ingowokuqala, nowokugqibela; Nguye lowo.', 'XHO22'),
(27, 41, 5, 'Iziqithi zakubona oko, zoyika; zagungqa iziphelo zehlabathi, zasondela, zemka.', 'XHO22'),
(27, 41, 6, 'Umntu uyancedisa ummelwane wakhe; Wathi elowo kumzalwana wakhe, Yomelela.', 'XHO22'),
(27, 41, 7, 'Ingcibi yemithi yomeleza umnyibilikisi wegolide, nalowo uyinyibilikisayo elucwecweni; yaye uyibethelele ngezikhonkwane, ukuze ingashukumi.', 'XHO22'),
(27, 41, 8, 'Ke wena Sirayeli, ungumkhonzi wam, wena Yakobi, endikunyulileyo, mbewu ka-Abraham, umhlobo wam.', 'XHO22'),
(27, 41, 9, 'Endakubambayo kwaseziphelweni zehlabathi, endakubizayo phakathi kookumkani bakho, ndathi kuwe, Ungumkhonzi wam wena; Mna ndaninyula, andinilahlanga;', 'XHO22'),
(27, 41, 10, 'Musani ukoyika; ngokuba ndinani; sukoyika; ngokuba nguThixo wenu; Ndiya kukomeleza; ewe, ndiya kukunceda; ewe, ndiya kukuxhasa ngesandla sam sokunene sobulungisa bam.', 'XHO22'),
(27, 41, 11, 'Yabona, bonke abakusukelayo baya kusuka beneentloni, bedanile; nabo basukuzana nawe baya kutshabalala.', 'XHO22'),
(27, 41, 12, 'Uya kuwafuna, ungawafumani amadoda alwa nawe;', 'XHO22'),
(27, 41, 13, 'Ngokuba mna, Yehova Thixo wakho, ndiya kubamba ngesandla sakho sokunene, ndithi kuwe, Musa ukoyika; Ndiza kukunceda.', 'XHO22'),
(27, 41, 14, 'Musa ukoyika, mbungu Yakobi, nani madoda akwaSirayeli; ndiya kukunceda mna, utsho uYehova, uMkhululi wakho oyiNgcwele kaSirayeli.', 'XHO22'),
(27, 41, 15, 'Yabona, ndikwenza inkabi yenkomo, inamazinyo;', 'XHO22'),
(27, 41, 16, 'Uya kuzivuthela, umoya uzithabathe, umoya ovuthuzayo uziphangalalise; yaye niya kugcoba ngoYehova nigcobe ngoyiNgcwele kaSirayeli.', 'XHO22'),
(27, 41, 17, 'Xa amahlwempu namahlwempu ekhangele amanzi, aze angabikho, nolwimi lwawo lome lunxano, mna, iNkosi, ndiya kubaphendula, kwaye mna, Thixo kaSirayeli, andiyi kubuya umva kubo.', 'XHO22'),
(27, 41, 18, 'Ndiya kuyivula imilambo eluqayini, nemithombo ezihlanjeni; Ndiya kuyijika intlango ibe lichibi lamanzi, nomhlaba ongumqwebedu ube yimithombo yamanzi.', 'XHO22'),
(27, 41, 19, 'Ndiya kutyala entlango imisedare, nemithombe, nemimirtile, neminquma; Ndiya kutyala enkqantosini umpayini, ndingabi mipayini, ingenguye enkqantosini;', 'XHO22'),
(27, 41, 20, 'ukuze babone, bazi, babone, baqonde ukuba isandla sikaYehova sikwenzile oko, nokuba Lowo Ungcwele kaSirayeli nguye owenze loo nto.', 'XHO22'),
(27, 41, 21, 'Lifundeni ityala lenu, utsho uYehova; ziziseni izizathu zenu, utsho uKumkani kaYakobi.', 'XHO22'),
(27, 41, 22, 'Mabawukhuphe, basixelele okuya kuhla; okanye usazise okuzayo.', 'XHO22'),
(27, 41, 23, 'Xelani izinto ezizayo, ukuze sazi ukuba ningoothixo: Ewe, yenzani okulungileyo okanye nenze okubi, ukuze soyike size sibone kunye.', 'XHO22'),
(27, 41, 24, 'Yabonani, ningamampunge; ulisikizi oninyulayo;', 'XHO22'),
(27, 41, 25, 'Mna ndizele owasentla, oya kuza; empumalanga yena uya kundibiza igama lam, abathetheli beze njengodongwe, baxovule udongwe njengombumbi.', 'XHO22'),
(27, 41, 26, 'Ngubani na owakuxelayo oko kwasekuqaleni? kwaye kuqala, masithi: Ngaba ulungile? Ewe, akukho bani unaziyo, ewe, akukho bani ukuxelelayo, ewe, akukho bani uwavayo amazwi akho.', 'XHO22'),
(27, 41, 27, 'Ndim endaqala ndathi kwiZiyon, Yabona, ndikho, ndayinika iYerusalem umshumayeli weendaba ezilungileyo.', 'XHO22'),
(27, 41, 28, 'Ngokuba ndabona, akwabakho bani; naphakathi kwabo bekungekho mcebisi uphendulayo, xa ndibabuzayo.', 'XHO22'),
(27, 41, 29, 'Yabona, bonke bephela bangamampunge; Zingamampunge izenzo zabo, imifanekiso yabo ethululwa ngumoya nochuku.', 'XHO22'),
(27, 42, 1, 'Yabonani, umkhonzi wam endimxhasayo; umnyulwa wam, okholwa nguye umphefumlo wam; ndibeke uMoya wam phezu kwakhe; uya kugweba phakathi kweentlanga.', 'XHO22'),
(27, 42, 2, 'Akayi kunkqangaza, aliphakamise, alivakalise ezitratweni ilizwi lakhe.', 'XHO22'),
(27, 42, 3, 'Ingcongolo ephotheneyo akayi kuyaphula;', 'XHO22'),
(27, 42, 4, 'Akayi kusilela, akayi kuncama, ade aligwebe ihlabathi; neziqithi ziya kulindela umyalelo wakhe.', 'XHO22'),
(27, 42, 5, 'Utsho uThixo, uYehova, owadala izulu, alaneke; walifundisa ihlabathi nezinto eziphuma kulo; Nguye yena obanika abantu abakuyo ukuphefumla, ubanike umoya kwabahamba kuyo;', 'XHO22'),
(27, 42, 6, 'Mna, Yehova, ndikubizile ngokobulungisa, ndakubamba ngesandla, ndiya kukugcina, ndikwenze umnqophiso weentlanga, isikhanyiso seentlanga;', 'XHO22'),
(27, 42, 7, 'ukuba uvule amehlo eemfama, ukuba ukhuphe abakhonkxiweyo ezintolongweni, nabahleli ebumnyameni entolongweni.', 'XHO22'),
(27, 42, 8, 'NdinguYehova, lilo elo igama lam; andiluniki omnye uzuko lwam, nendumiso yam andiyiniki imifanekiso eqingqiweyo.', 'XHO22'),
(27, 42, 9, 'Yabona, bayeza abaphambili, ndixela izinto ezintsha; ingekantshuli ndiyanixelela.', 'XHO22'),
(27, 42, 10, 'Vumani kuYehova ingoma entsha, indumiso yakhe kwaseziphelweni zehlabathi, nina bahla baye elwandle, neento zonke ezikulo; iziqithi nabemi bazo.', 'XHO22'),
(27, 42, 11, 'Mayiphakamise izwi intlango nemizi yayo, imizi emiyo amaKedare; mabamemelele abemi bengxondorha, bagcobe ezincotsheni zeentaba.', 'XHO22'),
(27, 42, 12, 'Mabamzukise uYehova, bavakalise indumiso yakhe eziqithini.', 'XHO22'),
(27, 42, 13, 'uYehova uya kuphuma njengegorha, uya kuxhoxha ikhwele njengegorha; Uya kubiza, ewe, agqume; uya kuzoyisa iintshaba zakhe.', 'XHO22'),
(27, 42, 14, 'Ndathula ixesha elide, ndathi tu; Ndathula ndaziphatha; ngoku, ndiya kukhala njengomfazi oneendevu; kwaoko ndiya kutshabalalisa, ndidle.', 'XHO22'),
(27, 42, 15, 'Ndozitshabalalisa iintaba neenduli, ndiyitshise yonke imifuno yazo; imilambo ndiya kuyenza iziqithi, ndiwatshise amadike.', 'XHO22'),
(27, 42, 16, 'Ndozihambisa iimfama ngendlela ezingayaziyo, ndizihambise ngendlela ezingayaziyo; Ndiya kubakhapha ngeendlela abangazaziyo; ndiya kubenza bube khaphukhaphu ubumnyama phambi kwabo, ndizenze zithi tye izinto ezigoso. Ezo zinto ndiya kuzenza kubo, ndingazishiyi.', 'XHO22'),
(27, 42, 17, 'Baya kuguquka badane kakhulu, abakholosa ngemifanekiso eqingqiweyo, abathi kwetyhidiweyo, Noothixo bethu!', 'XHO22'),
(27, 42, 18, 'Yivani, nina zithulu; khangelani, zimfamandini, yabona.', 'XHO22'),
(27, 42, 19, 'Ngubani na oyimfama, ingenguye umkhonzi wam? okanye sisithulu njengomthunywa wam endimthumileyo? Ngubani oyimfama ofezeke, oyimfama njengomkhonzi kaYehova?', 'XHO22'),
(27, 42, 20, 'Ubona izinto ezininzi, akazigqali; zivule iindlebe zakhe, akeva.', 'XHO22'),
(27, 42, 21, 'uYehova unonelele ubulungisa bakhe; uya kuwubamba umyalelo, awuhloniphe.', 'XHO22'),
(27, 42, 22, 'Ke olu luhlanga oluphangiweyo, oluphangiweyo; bonke babanjiwe emhadini, bafihlwe ezintolongweni; Ndilixhoba, akukho msindisi; Akwabakho uthi, Musa ukubuya.', 'XHO22'),
(27, 42, 23, 'Ngubani na kuni oya kuva oko? Ngubani na oya kuva, eve kwixesha elizayo?', 'XHO22'),
(27, 42, 24, 'Ngubani na onikele uYakobi ukuba aphangwe, owamnikela uSirayeli kumasela? AsingoYehova na esone kuye? ngenxa yokuba bengahambi ngeendlela zakhe, bengawugcinanga umthetho wakhe.', 'XHO22'),
(27, 42, 25, 'Wathululela phezu kwakhe ukuvutha komsindo wakhe, namandla emfazwe; wamtshisa, akakunyamekela oko ngentliziyo.', 'XHO22'),
(27, 43, 1, 'Kwaye ngoku, itsho njalo iNkosi, uMdali wakho, Owu Yakobi, noMdali wakho, Owu Sirayeli: Musa ukoyika, kuba ndikukhulule, ndikubizile ngegama lakho; ungowam.', 'XHO22'),
(27, 43, 2, 'Nokuba uthe wacanda emanzini, ndoba nawe; nasemilanjeni, ayisayi kukukhukulisa; ukuba uthe waphuma emlilweni, akuyi kurhawuka; nedangatye aliyi kukutshisa wena.', 'XHO22'),
(27, 43, 3, 'Kuba ndinguYehova uThixo wakho, Lowo Ungcwele kaSirayeli, uMsindisi wakho, ndinikele ngeYiputa ukuba ibe yintlawulelo yakho, iTiyopiya neShebha ngenxa yakho.', 'XHO22'),
(27, 43, 4, 'Ngenxa enokuba uzukile kum, uzukile, uzukile ke mna, ndakuthanda; ngoko ke, ndiya kuninika abantu nobomi.', 'XHO22'),
(27, 43, 5, 'Musa ukoyika, ngokuba ndinawe; Ndiya kuyizisa imbewu yakho ivela empumalanga, ndikubuthe nasentshonalanga;', 'XHO22'),
(27, 43, 6, 'Ndiya kuthi kumntla, Suka; nakumzantsi ndithi, Musa ukuzibamba; nazisa oonyana bam bevela kude, neentombi zam ziye eziphelweni zehlabathi;', 'XHO22'),
(27, 43, 7, 'Wonke umntu obizwa ngegama lam, ngenxa yokuba ndidala uzuko lwam, ndamdala mna; ewe, ndiyenzile.', 'XHO22'),
(27, 43, 8, 'Khupha iimfama ezinamehlo, nabazizithulu beneendlebe;', 'XHO22'),
(27, 43, 9, 'mazihlanganisane zonke iintlanga, zihlangane iintlanga; Nguwuphi na ke kubo onokuzixela ezi zinto, asibonise owokuqala? mabaze namangqina abo, bagwetyelwe; okanye uve besithi, Yinyaniso le.', 'XHO22'),
(27, 43, 10, 'Namangqina am, utsho uYehova, nomkhonzi wam endimnyulileyo, ukuze nazi, nikholwe ndim, niqonde ukuba ndim;', 'XHO22'),
(27, 43, 11, 'Mna ndinguYehova; akukho msindisi ingendim.', 'XHO22'),
(27, 43, 12, 'Ndashumayela ndaza ndanikela ubungqina nangona kwakungekho thixo wasemzini phakathi kwenu; ngako oko, namangqina am, utsho uYehova, ukuba ndinguThixo.', 'XHO22'),
(27, 43, 13, 'Ewe, phambi kwamhla bendinaye; akukho bani unokuwuhlutha esandleni sam. Ndiza kusebenza, kwaye ngubani oya kukuvumela?', 'XHO22'),
(27, 43, 14, 'Kutsho uYehova, umkhululi wakho, oyiNgcwele kaSirayeli, ukuthi; Ngenxa yenu ndithumele eBhabheli, ndaza ndazithoba zonke iingangamsha zabo, namaKaledi, ngeenqanawa zokumemelela kwawo.', 'XHO22'),
(27, 43, 15, 'Mna, Yehova, ndingoyiNgcwele wenu, uMdali kaSirayeli, iNkosi yenu.', 'XHO22'),
(27, 43, 16, 'Utsho uYehova, umenzi wendlela elwandle, indlela emanzini amaninzi;', 'XHO22'),
(27, 43, 17, 'umkhuphi wenqwelo yokulwa, nehashe, nemikhosi, namandla; balala phantsi kunye, abayi kuvuka; babhuqiwe, bafile njengentsontelo.', 'XHO22'),
(27, 43, 18, 'Musani ukucinga ngezontlandlolo, musani ukucinga ngezo nto ezidala;', 'XHO22'),
(27, 43, 19, 'Yabonani, ndiya kwenza into entsha; ngoku iya kuxhobisa; ubungathanga uyazi? Ndiya kwenza indlela enkangala, nemilambo enkqantosini;', 'XHO22'),
(27, 43, 20, 'Izilo zelizwe ziya kundizukisa, iimpungutye neenciniba, ngenxa yokuba ndenza amanzi entlango nemilambo entlango ukuze basele abantu bam, abanyuliweyo bam.', 'XHO22'),
(27, 43, 21, 'Ndim ozenzele olu hlanga; baya kuyixela indumiso yam.', 'XHO22'),
(27, 43, 22, 'Akundinqulanga ke, Yakobi; kodwa wadinwa ndim, Sirayeli.', 'XHO22'),
(27, 43, 23, 'Akundizisanga iimvana zamadini akho anyukayo; akundizukisanga ngemibingelelo yakho. Andikwenzanga ukuba ukhonze ngambingelelo kwaye andikukhathazi ngesiqhumiso.', 'XHO22'),
(27, 43, 24, 'Akundithengelanga ngcongolo emnandi ngemali, akundanelisanga ngamanqatha exhoba lakho; ke undikhonzisile ngezono zakho, undithobile ngobugwenxa bakho.', 'XHO22'),
(27, 43, 25, 'Ndim ocima izikreqo zakho ngenxa yam, kwaye andizikhumbuli izono zakho.', 'XHO22'),
(27, 43, 26, 'Ndikhumbule; masilwe; shumayela, kwaye uya kugwetyelwa.', 'XHO22'),
(27, 43, 27, 'Uyihlo wokuqala wonile, nabafundisi bakho bandicekisile.', 'XHO22'),
(27, 43, 28, 'Ndabenza inqambi ke abathetheli abangcwele, ndamnikela uYakobi kwisiqalekiso nesingcikivo sikaSirayeli.', 'XHO22'),
(27, 44, 1, 'Kaloku ke yiva, Yakobi, mkhonzi wam; namaSirayeli endiwanyulileyo.', 'XHO22'),
(27, 44, 2, 'uYehova, uMenzi wakho, uMbumbi kwasesizalweni, wathi, Uya kukunceda; Musa ukoyika, mkhonzi wam Yakobi; noYeshurun, endimnyulileyo.', 'XHO22'),
(27, 44, 3, 'Kuba ndiya kugalela amanzi konxaniweyo, nemisinga emhlabeni owomileyo; Ndiya kuthulula uMoya wam phezu kwendlu yakho, nentsikelelo yam phezu kwembewu yakho.', 'XHO22'),
(27, 44, 4, 'Baya kuhluma njengengca, njengemingcunube ngasemijelweni yamanzi.', 'XHO22'),
(27, 44, 5, 'Omnye uya kuthi: “LelikaYehova; omnye uya kumbiza ngegama likaYakobi; omnye asayine uYehova ngesandla sakhe, ambize ngegama likaSirayeli.', 'XHO22'),
(27, 44, 6, 'Utsho uYehova, uKumkani wakwaSirayeli, uMkhululi wakhe, uYehova wemikhosi; ndingowokuqala, ndingowokugqibela; akukho Thixo ingendim.', 'XHO22'),
(27, 44, 7, 'Ngubani na onokubiza njengam, aze axele aze akuqinisekise ngenxa yam, ekubeni ndabasusayo abantu bamandulo? kwaye okuzayo, nokuzayo, makwazeke.', 'XHO22'),
(27, 44, 8, 'Musani ukoyika nokoyika; namangqina am. Akukho Thixo na ingendim? ewe, akukho Thixo; Andinaluvo.', 'XHO22'),
(27, 44, 9, 'Abayili bemifanekiso eqingqiweyo bonke bephela baluchuku; nezinto zabo ezinqwenelekayo aziyi kuphumelela; kwaye bona bangamangqina abo; ababoni, abazi; ukuze beme badane.', 'XHO22'),
(27, 44, 10, 'Ngubani na owenze uthixo nomfanekiso oqingqiweyo?', 'XHO22'),
(27, 44, 11, 'Yabona, onke amadlelane akhe adanile, abasebenzi bangabantu; mabaqokelelene ndawonye, beme; ukanti baya kunkwantya, badane kunye.', 'XHO22'),
(27, 44, 12, 'Umnyibilikisi wenza amalahle ngesando, usebenza ngehamile, usebenza ngamandla ezandla zakhe; ewe, ulambe, abe namandla; akaseli manzi kwaye udiniwe.', 'XHO22'),
(27, 44, 13, 'Ingcibi yemithi yolula umyalelo wayo; uthengisa ngomsonto; Isilungisa ngemilinganiselo, isibetha ngekhampasi, isenze ngokomfanekiselo wendoda, ngokobuhle bomntu; ukuba ingahlala endlini.', 'XHO22'),
(27, 44, 14, 'Uzakhela imisedare aze athabathe umsipres nompleyini awenze lukhuni ngayo phakathi kwemithi yehlathi; uhlwayela uthuthu, nemvula iyamtyisa.', 'XHO22'),
(27, 44, 15, 'Wothi ke umntu atshe; ewe, ebebhaka isonka; ewe, wenza uthixo, uyamnqula; wenza umfanekiso oqingqiweyo, awuwele phezu kwawo.', 'XHO22'),
(27, 44, 16, 'Inxalenye yawo uyitshisa ngomlilo; uyidla inyama kunye nesahlulo sayo; makatye ahluthe; ewe, wabuya wachwayita, wathi, Tshotsho, ndiyotha, ndiwubonile umlilo;', 'XHO22'),
(27, 44, 17, 'Okuseleyo kuwo wenza uthixo, umfanekiso wakhe oqingqiweyo; uqubuda kuyo, aqubude kuyo, athandaze kuyo, athi, Ndihlangule; ngokuba unguThixo wam.', 'XHO22'),
(27, 44, 18, 'Bebengazi, bengaqondi, ngokuba ebevingcile amehlo abo ukuba bangaboni; neentliziyo zabo, ukuze bangaqondi;', 'XHO22'),
(27, 44, 19, 'kungabikho bani ugqibe entliziyweni yakhe, kungabikho kwazi, kungabikho kuqonda, ukuba kuthi, Inxalenye yawo ndiyitshisile ngomlilo; ewe, ndabhaka isonka emalahleni awo; ndosa inyama, ndadla; oseleyo kuwo ndiya kuwenza amasikizi na? ndingawela esiqwini somthi?', 'XHO22'),
(27, 44, 20, 'Uvusa uthuthu; Intliziyo enenkohliso imlahlekisile, ukuba angawusindisi umphefumlo wakhe; ithi, Akukho buxoki na esandleni sam sokunene?', 'XHO22'),
(27, 44, 21, 'Khawuve oku, Yakobi, Sirayeli; ngokuba ungumkhonzi wam; Ndikudalile; ungumkhonzi wam; Sirayeli, akuyi kulityalwa ndim.', 'XHO22'),
(27, 44, 22, 'Ndilucimile njengelifu elimnyama ukreqo lwakho, nezono zakho njengelifu elimnyama; buyela kum; ngokuba ndikukhulule ngokukuhlawulela;', 'XHO22'),
(27, 44, 23, 'Memelelani, nina mazulu; ngokuba uYehova ekwenzile; gcobani, zinzulu zehlabathi. Dumani, zintaba, hlathi, nani nonke mithi ekulo, ngokuba uYehova emhlawulele wamkhulula uYakobi, wamzukisa kwaSirayeli.', 'XHO22'),
(27, 44, 24, 'Utsho uYehova, uMkhululi wakho, uMdaluli wakho, uMenzi wakho kwasesizalweni sikanyoko, ukuthi, Mna Yehova ndinguMenzi wezinto zonke; owaneka isibhakabhaka yedwa; nto leyo iyodwa eyandisa ihlabathi;', 'XHO22'),
(27, 44, 25, 'uMtshitshisi wemiqondiso yabaphololoza into engeyiyo; abo bajikisa izilumko, bakuguqule kube kukumatha ukwazi;', 'XHO22'),
(27, 44, 26, 'uMmisi welizwi lomkhonzi wakhe, uMzalisi wecebo labathunywa bakhe; abathi kwiYerusalem, Iya kubuya yakhiwe; nakwimizi yakwaYuda: Iya kukhiwa, ndiwamise amanxuwa ayo;', 'XHO22'),
(27, 44, 27, 'uNdithi kumanzi enzonzobila, Yitshani, ndiyomise imilambo yakho;', 'XHO22'),
(27, 44, 28, 'owathi kuKoreshi, Ngumalusi wam lowo, oya kukufeza konke ukuthanda kwam; ndithi kwiYerusalem, Uya kubuyiswa; nakwitempile, Iziseko zakho ziya kuzinza.', 'XHO22'),
(27, 45, 1, 'Wathi uYehova kumthanjiswa wakhe, kuKoreshi, endimbambe ngesandla sakhe sokunene, ukuba ndizinyathele iintlanga phambi kwakhe; ndiwakhulule amanqe ookumkani, ukuba ndivule iingcango ezimbini phambi kwakhe; neengcango mazingavalwa;', 'XHO22'),
(27, 45, 2, 'Ndiya kukwalathisa, ndilungelelanise iindawo ezimagoso; ndiya kuwaphula amasango obhedu, ndiwaphule imivalo yesinyithi;', 'XHO22'),
(27, 45, 3, 'ndikunike ubuncwane obusemnyameni, neengqwebo eziselelweyo, ukuze wazi ukuba mna Yehova ndinguThixo kaSirayeli, okubiza ngegama lakho.', 'XHO22'),
(27, 45, 4, 'Ngenxa kaYakobi, umkhonzi wam, ngenxa kaSirayeli, abanyulwa bam, ndakubiza ngegama lakho; ndikubize ngegama nokuba awundazi.', 'XHO22'),
(27, 45, 5, 'NdinguYehova, akukho wumbi, akukho Thixo ingendim; Ndikubophile, ungandazi.', 'XHO22'),
(27, 45, 6, 'Ukuze bazi, bethabathela empumalanga nasentshonalanga, ukuba akukho wumbi; NdinguYehova, akakho omnye.', 'XHO22'),
(27, 45, 7, 'Ndenza ukukhanya, ndoyise ubumnyama; Ndidala uxolo, ndidala ubutshinga; Mna, Yehova, ndizenza zonke ezi zinto.', 'XHO22'),
(27, 45, 8, 'Yihlani, mazulu, linise izulu ubulungisa; Mna Yehova ndikwenzile.', 'XHO22'),
(27, 45, 9, 'Yeha, oxabana noMenzi wakhe! Abaninzi baye baxabana nabaninzi behlabathi. Lunokuthi na udongwe kumenzi walo, Wenza ntoni na? Awunazandla na umsebenzi wakho?', 'XHO22'),
(27, 45, 10, 'Yeha, lowo uthi kuyise, Uzala ntoni na? Uzele na?', 'XHO22'),
(27, 45, 11, 'Utsho uYehova, oyiNgcwele kaSirayeli, uMenzi wakhe, ukuthi, Ndibuze ukuba yintoni na eya kubahlela oonyana bam, undiyalele ngomsebenzi wezandla zam.', 'XHO22'),
(27, 45, 12, 'Ndim owenza ihlabathi, ndadala abantu phezu kwalo; Mna, izandla zam, lotwaneka izulu, nomkhosi walo lonke ndawuwisela umthetho.', 'XHO22'),
(27, 45, 13, 'Mna ndimvelisile ngobulungisa, ndiya kuzilungelelanisa iindlela zakhe zonke; yena uya kuwakha umzi wam, abakhulule abathinjwa bam, kungengaxabiso, kungengamvuzo; utsho uYehova wemikhosi.', 'XHO22'),
(27, 45, 14, 'Utsho uYehova ukuthi, Umsebenzi weYiputa, nomsebenzi wakwaKushi namaShebha, amadoda amade, uya kuza kuwe ube ngowenu; ziya kukulandela; baya kuza ekuthinjweni, bakhubeke kuwe; baya kukuwola, besithi, Inene, uThixo ukuwe; akukho wumbi, akukho Thixo.', 'XHO22'),
(27, 45, 15, 'Inyaniso, unguThixo ozisithelisayo, Thixo kaSirayeli, uMkhululi.', 'XHO22'),
(27, 45, 16, 'Baya kudana, babuye babe neentloni bonke bephela;', 'XHO22'),
(27, 45, 17, 'Ke uSirayeli uya kusindiswa nguYehova ngosindiso olungunaphakade;', 'XHO22'),
(27, 45, 18, 'Kuba uYehova walenza le izulu izulu, ukuthi, Yena uThixo wenza, wenza ihlabathi; Ukuzinzisile ke, akukudalelwanga ukuba kube khona, ukwenze ukuba ube nabemi ndinguYehova; kwaye akukho mntu wumbi.', 'XHO22'),
(27, 45, 19, 'Andithethanga entsithelweni, endaweni emnyama yelizwe; anditshongo koonyana bakaYakobi ukuthi, Ndiquqeleni enyanyeni; Mna, Yehova, ndivakalisa ubulungisa, ndivakalisa ubulungisa.', 'XHO22'),
(27, 45, 20, 'Hlanganisani nize; sondelani kunye, nina nisabayo ezintlangeni.', 'XHO22'),
(27, 45, 21, 'Yitsho, usondele kubo; ewe, mabathethe bodwa, bathi, Ngubani na owathetha ezi zinto kwakudala? yathethwa ngubani ukusukela ngoko? Andinguye na uYehova? akukho wumbi ngaphandle kwam; uThixo onobulungisa, onguMsindisi; akukho mntu ngaphandle kwam.', 'XHO22'),
(27, 45, 22, 'Bhekani kum, nisindiswe, nonke ziphelo zehlabathi; ngokuba ndinguThixo, akukho wumbi.', 'XHO22'),
(27, 45, 23, 'Ndizifungile, ilizwi liphume emlonyeni ngobulungisa, alibuyanga lathi, Aya kuguqa kum onke amadolo, zifunge mna zonke iilwimi;', 'XHO22'),
(27, 45, 24, 'Okunene uya kuthi, KuYehova ndinobulungisa namandla; baya kuza kuye abantu; baya kudana bonke abamqumbelayo.', 'XHO22'),
(27, 45, 25, 'Bonke oonyana bakaSirayeli baya kugwetyelwa kuYehova, bazukiswe.', 'XHO22'),
(27, 46, 1, 'uBhele waqubuda, uNebho waqubuda; imifanekiso yabo yayiphezu kweenkomo neenkomo; iimoto zakho zahanjiswa; ngumthwalo kwinkomo ediniweyo.', 'XHO22'),
(27, 46, 2, 'Baguqa, baqubuda kunye; ababanga nako ukuwuzisa umthwalo, kodwa bona ngokwabo bathinjwa.', 'XHO22'),
(27, 46, 3, 'Ndiphulaphuleni, ndlu kaYakobi, nani nonke masalela endlu kaSirayeli, nina nizelwe eluxolweni, nina bazelwe eluxombeni.', 'XHO22'),
(27, 46, 4, 'Kwaye kude kube lixesha lenu ndinguye; kude kuse ezimvini, ndiya kunithwala; ndenze, ndiya kubeka; ndonithwala, ndinisindise.', 'XHO22'),
(27, 46, 5, 'Niya kundifanisa nabani na, Niya kundifanisa, nindifanise nabani na, ukuze sifane nathi?', 'XHO22'),
(27, 46, 6, 'Bayiphosa engxoweni igolide, bayilinganisa isilivere ngesikali, baqesha umnyibilikisi; umenze uthixo; baqubuda, ewe, bathandaze.', 'XHO22'),
(27, 46, 7, 'Bamthwala emagxeni, bamthwala, bambeka endaweni yakhe, wema; akayi kuyishiya indawo yakhe; ewe, uya kubiza kuye, angaphenduli, okanye amsindise ekubandezelekeni kwakhe.', 'XHO22'),
(27, 46, 8, 'Khumbulani lo mbandela, nibonise amadoda enu; Khumbulani, nina bakreqi!', 'XHO22'),
(27, 46, 9, 'Khumbulani izinto zokuqala zamandulo, kuba ndinguThixo, akakho omnye; ndinguThixo, akukho unjengam;', 'XHO22'),
(27, 46, 10, 'ukuba ndixele isiphelo, kwasekuqaleni, ekungazanga kubekho kwakukudala, uthi, Icebo lam liya kuma, ndikwenze konke ukuthanda kwam;', 'XHO22'),
(27, 46, 11, 'Ubiza intaka empumalanga, ezweni elikude, wempatho yam; ewe, bendithe, Ndiya kubuya ndikho; Ndiyicwangcisile, ndiza kuyenza nam.', 'XHO22'),
(27, 46, 12, 'Phulaphulani, nina madoda omeleleyo, nikude nobulungisa;', 'XHO22'),
(27, 46, 13, 'Ndisondeza ubulungisa bam; ayiyi kuba kude, nosindiso lwam aluyi kulibala; yaye ndiya kubeka usindiso eZiyon kuSirayeli, uzuko lwam.', 'XHO22'),
(27, 47, 1, 'Yihla, uhlale eluthulini, nkazanandini, ntombi inguBhabheli, hlala phantsi emhlabeni; akukho trone, ntombi ingamaKaledi, ngokuba akuyi kuba sathiwa ululamile, uthambile;', 'XHO22'),
(27, 47, 2, 'Thabatha umgubo, usile; vula izitshixo zakho, vula umlenze wakho, khupha isinqe, unqumle isinqe.', 'XHO22'),
(27, 47, 3, 'Kuya kubonakala amampunge akho, kubonakale ukucukucezwa kwakho; Ndiza kuziphindezela, andizudibana nawe njengomntu.', 'XHO22'),
(27, 47, 4, 'uMkhululi wethu ke, igama lakhe nguYehova wemikhosi, oyiNgcwele kaSirayeli.', 'XHO22'),
(27, 47, 5, 'Zithulele, ngena ebumnyameni, ntombi yamaGalati; ngokuba akuyi kuba sabizwa ngokuba ungumfazi wezikumkani.', 'XHO22'),
(27, 47, 6, 'Ndaba noburhalarhume kubantu bam, ndalenza inqambi ilifa lam, ndalinikela ezandleni zakho; wawubeke idyokhwe enzima kakhulu kwangemihla yakudala.', 'XHO22'),
(27, 47, 7, 'Wathi, Ndiya kuba ngumfazi ngonaphakade, ukuze ungazibekeli entliziyweni ezi zinto, ungalicingi ikamva labo.', 'XHO22'),
(27, 47, 8, 'Ngoko ke yivani, nina bathanda iziyolo, niqhayisayo, nina nithi entliziyweni yenu, Ndim kwaphela, akukho wumbi; andiyi kuba ngumhlolokazi, andiyi kukwazi ukulahlekelwa ngabantwana;', 'XHO22'),
(27, 47, 9, 'Ziya kukuhlela ezo zinto zombini, ngamini-nye, ukungabi nabantwana nobuhlolokazi;', 'XHO22'),
(27, 47, 10, 'Ngokuba ukholose ngezinto zakho ezimbi; uthe: Akukho bani undibonayo. Bubulumko bakho nokwazi kwakho okukuphambanisileyo; wathi entliziyweni yakho, Ndim kwaphela, akukho wumbi.', 'XHO22'),
(27, 47, 11, 'Ke ngoko, ububi buya kunifikela; akuyi kulazi apho liphuma khona, wofikelwa bububi; aniyi kukwazi ulibazisa, kuwe ngequbuliso isiphanziso ongayi kukwazi.', 'XHO22'),
(27, 47, 12, 'Khawuyeke nokukhafula kwakho okukhulu, nokukhafula kwakho okuninzi, ozibophe ngako kwasebuncinaneni bakho; ukuba kunjalo, uya kuba nako ukwenza inzuzo, ukuba kunjalo, uya kuba nako ukuphumelela.', 'XHO22'),
(27, 47, 13, 'Udiniwe bubuninzi bezicwangciso zakho; Ke kaloku, mazibaleke iinkwenkwezi, mabavuke abavumisi ngeenkwenkwezi, bakusindise kwezo zinto ziya kukuhlela;', 'XHO22'),
(27, 47, 14, 'Yabona, baya kuba njengeendiza; umlilo uya kubatshisa; abanako ukuzisindisa emandleni elangatye;', 'XHO22'),
(27, 47, 15, 'Kuya kuba njalo nakuwe owawusebenza naye, abarhwebi bakho kwasebutsheni bakho; akukho bani uya kukuhlangula.', 'XHO22'),
(27, 48, 1, 'Liveni eli lizwi, nina bendlu kaYakobi, enibizwa ngalo igama likaSirayeli, naphuma emanzini akwaYuda, nina nishumayelayo egameni likaYehova, nibiza uThixo kaSirayeli, kodwa kungenganyaniso; nangenyaniso ubulungisa abukho.', 'XHO22'),
(27, 48, 2, 'Ngenxa enokuba bezibiza, bephuma emzini ongcwele, behleli kuThixo kaSirayeli; NguYehova wemikhosi igama lakhe.', 'XHO22'),
(27, 48, 3, 'Izinto zokuqala ndazixela kwasekuqaleni; zaphuma emlonyeni wam, ndazibonisa; Ndenze ngokungacwangciswanga, kwaye kwenzeka.', 'XHO22'),
(27, 48, 4, 'Kuba ndiyazi ukuba uneenkani, nentamo yakho ingumsipha wesinyithi, nebunzi lakho lubhedu;', 'XHO22'),
(27, 48, 5, 'Kananjalo ndinixelele kwasekuqalekeni; ingekabonakali, ndakubonise yona, ukuze ungathi, Wenze le nto uThixo wam, umfanekiso wam oqingqiweyo nomtyhido wam bawumisile.', 'XHO22'),
(27, 48, 6, 'Nivile; ziboneni zonke ezi zinto; kwaye awuyi kuthetha? Ukususela ngoku ndikubonise izinto ezintsha, izinto ezifihlakeleyo, ongazazi.', 'XHO22'),
(27, 48, 7, 'Zidalwa ngoku, azidalwanga kwasekuqaleni; akubavanga naphambi kwelanga; hleze uthi, Yabona, bendizazi.', 'XHO22'),
(27, 48, 8, 'Ewe, aneva; ewe, ubungazi; ibingavulekanga indlebe yakho kususela koko; kuba ndandisazi ukuba uya kwenza ngobuqhophololo uze kuthiwe ungumkreqi kwasesibelekweni.', 'XHO22'),
(27, 48, 9, 'Ngenxa yegama lam ndiya kuwubekela bucala umsindo wam, nangenxa yendumiso yam ndiya kuwucima, ukuze ndingonakalisi.', 'XHO22'),
(27, 48, 10, 'Uyabona, ndikuhlambulule, akwaba ngasilivere; ndikunyule ezikweni lembandezelo;', 'XHO22'),
(27, 48, 11, 'Ngenxa yam, ewe, ngenxa yam, ndiya kukwenza; lingáthini na ukungcoliswa igama lam? andiluniki omnye uzuko lwam.', 'XHO22'),
(27, 48, 12, 'Ndiphendule, Yakobi, Sirayeli, ukuhlahlamba kwam; Ndinguye; ndingowokuqala, ndingowokugqibela;', 'XHO22'),
(27, 48, 13, 'Isandla sam saseka ihlabathi, esokunene sam saneke izulu; xa ndizibiza, zema kunye.', 'XHO22'),
(27, 48, 14, 'Zihlanganiseni, niphulaphule; Nguwuphi na kubo owathetha ezo zinto? uYehova wayemthanda; Uya kwenza okulungileyo kwiBhabhiloni, yaye isandla sakhe siya kuba nxamnye namaKhaledi.', 'XHO22'),
(27, 48, 15, 'Mna, ewe, ndithethile; ewe, ndimbizile; ndimzisile, kwaye uya kuyiphumelelisa indlela yakhe.', 'XHO22'),
(27, 48, 16, 'Sondelani kum, nive oku; Kwasekuqaleni andithethanga emfihlekweni; ngokuba ndikho, ndikho; nangoku iNkosi uYehova indithumile ngomoya wayo.', 'XHO22'),
(27, 48, 17, 'Utsho uYehova, umkhululi wakho, oyiNgcwele kaSirayeli; NdinguYehova, uThixo wakho, ungokufundisa okulungileyo, ohamba ngendlela oya kuhamba ngayo.', 'XHO22'),
(27, 48, 18, 'Akwaba ubuyibazele indlebe imiyalelo yam! uxolo lwakho lwaba njengomlambo, nobulungisa bakho njengamaza olwandle;', 'XHO22'),
(27, 48, 19, 'Yayinjengentlabathi imbewu yakho, nabantwana ezibilinini zakho babe njengohlalutye lwakho; aliyi kunqunyulwa litshabalale igama lakhe phambi kwam.', 'XHO22'),
(27, 48, 20, 'Phumani eBhabheli, babalekeni kumaKaledi; uthi, uYehova umkhulule ngokumhlawulela umkhonzi wakhe uYakobi.', 'XHO22'),
(27, 48, 21, 'Abanxanwanga oko ebahambisa entlango; Wabakhuphela amanzi engxondorheni; Waphinda walicanda iliwa, kwaphuma amanzi.', 'XHO22'),
(27, 48, 22, 'Akukho luxolo kwabangendawo, utsho uYehova.', 'XHO22'),
(27, 49, 1, 'Yivani, ziqithi; yivani, zintlanga ezikude; uYehova wandibiza kwasesizalweni sam; walibiza igama lam kwasesizalweni sikama.', 'XHO22'),
(27, 49, 2, 'Wawenza umlomo wam wanjengekrele elibukhali; Wandisithelisa esithunzini sesandla sakhe, wandenza ingcongolo ekhazimlisiweyo; wandifihla engutyeni;', 'XHO22'),
(27, 49, 3, 'wathi kum, Ungumkhonzi wam, Sirayeli, endiya kuqhayisa ngaye.', 'XHO22'),
(27, 49, 4, 'Ndathi mna, Ndifumane ndabulaleka, ndawachitha amandla am, ndafumana ndazixhamla; Ke lona ibango lam likuYehova, nomthetho wam unoThixo wam.', 'XHO22'),
(27, 49, 5, 'Kaloku ke, utsho uYehova, owandidalayo kwasesizalweni sam, ukuba ndibe ngumkhonzi kuye, ukuze ndibuyisele uYakobi kuye; engekahlanganiselwa ndawonye uSirayeli, mna ndiya kuzukiswa emehlweni kaYehova, uThixo wam abe khona amandla am.', 'XHO22'),
(27, 49, 6, 'Wathi, Kuyinto encinane ukuba ube ngumkhonzi kum wokuvusa izizwe zakwaYakobi, ubuyise abagcini bakwaSirayeli; Ndiya kukukhanyisela iintlanga, ukuze ube lusindiso kum isiphelo sehlabathi.', 'XHO22'),
(27, 49, 7, 'Utsho uYehova, uMkhululi kaSirayeli, oyiNgcwele wakhe, kowacekisayo umntu, kothiyiweyo ziintlanga, kumkhonzi wabathetheli, ookumkani baya kukubona oko, basuke beme; ngenxa yayo abathetheli baya kuguqa yeNkosi ethembekileyo, yoyiNgcwele kaSirayeli, yokunyula nina.', 'XHO22'),
(27, 49, 8, 'Utsho uYehova ukuthi, Ndakuva ngexesha lenceba, ndakunceda ngemini yosindiso; abazukulwana abalahliweyo;', 'XHO22'),
(27, 49, 9, 'Yithi kubathinjwa, Phumani; zibonakaliseni nisebumnyameni. Baya kudla emendweni, namadlelo abo abe sezigangeni zonke;', 'XHO22'),
(27, 49, 10, 'Abayi kulamba, abayi kunxanwa; ubushushu nelanga aziyi kubabetha; ngokuba Lowo unenceba kubo uya kubakhokelela, abakhokelele emithonjeni yamanzi.', 'XHO22'),
(27, 49, 11, 'Ndiya kuzenza zonke iintaba zam zibe yindlela, umendo wam uphakame.', 'XHO22'),
(27, 49, 12, 'Yabonani, baya kuvela kude; yabona, beza bevela entla nasentshonalanga; nakwilizwe lamaSinime.', 'XHO22'),
(27, 49, 13, 'Memelelani, nina mazulu; kwaye ugcobe, hlabathi! memelelani ngovuyo, zintaba; ngenxa yokuba uYehova wabathuthuzela abantu bakhe waza waba nenceba kwabo basweleyo.', 'XHO22'),
(27, 49, 14, 'Ke yathi iZiyon, uYehova undishiyile, iNkosi yam indishiyile.', 'XHO22'),
(27, 49, 15, 'Umfazi angalulibala yini na usana lwakhe, ukuba angabi namfesane kunyana wesizalo sakhe? Nokuba bathe balibala, andiyi kukulibala mna.', 'XHO22'),
(27, 49, 16, 'Yabona, ndikubhalele ezintendeni zezandla zam; iindonga zakho zihlala zingam.', 'XHO22'),
(27, 49, 17, 'Oonyana bakho baya kukhawuleza; iintshaba zakho nabakonakalisayo baya kuphuma kuwe.', 'XHO22'),
(27, 49, 18, 'Phakamisa amehlo akho ngeenxa zonke, ubone; Ndihleli nje, utsho uYehova, uze uwanxibe onke njengezivatho, ubophelelekile kuwe njengomtshakazi.', 'XHO22'),
(27, 49, 19, 'Kuba amanxuwa akho, namanxuwa akho, nelizwe lakho elinxuwa, licuthene kakhulu, ngenxa yabemi balo, babe kude abakudlayo.', 'XHO22'),
(27, 49, 20, 'Baya kuthi ezindlebeni zakho oonyana bakho, owakubazala, wakuba ubhujelwe, Le ndawo imxinwa; ndinike indawo yokuhlala.', 'XHO22'),
(27, 49, 21, 'Wothi ke entliziyweni yakho, Ngubani na lo undizalele lo? kwaye ngubani ovelise oku? Yabona, ndindedwa; khangela, babephi na?', 'XHO22'),
(27, 49, 22, 'Itsho iNkosi uYehova ukuthi, Yabonani, ndiyasiphakamisela ezintlangeni isandla sam, ndiyiphakamisele ezintlangeni ibhanile yam, zibazise oonyana bakho ngesandla sabo, neentombi zakho zithwalwe emagxeni abo.', 'XHO22'),
(27, 49, 23, 'Ookumkani baya kuba ngooyihlo ababakhulisayo, nabafazi babo babe ngodade babakhulisi bakho; baya kuqubuda ngobuso, bakhothe uthuli lweenyawo zakho; wazi ukuba ndinguYehova, ngenxa yokuba abo bakholose ngam abayi kudana.', 'XHO22'),
(27, 49, 24, 'Kuya kuthatyathwa na amaxhoba kwigorha, okanye umbanjwa uya kusindiswa ngomthetho?', 'XHO22'),
(27, 49, 25, 'Wathi uYehova, Nabathinjiweyo bamaRefa baya kubanjwa, namaxhoba omngcangcazelisi aya kusindiswa;', 'XHO22'),
(27, 49, 26, 'Abakuxinzelela phantsi ndobadlisa inyama yabo; baya kunxila ligazi labo njengewayini enencasa, yazi yonke inyama, ukuba mna, Yehova, ndinguMkhululi wakho, uMkhululi wakho, iNgxaki kaYakobi.', 'XHO22'),
(27, 50, 1, 'Wathi uYehova, Liphi na isiqinisekiso sikanyoko endamlahlayo? ndithengise ngani kubanamatyala kum? Yabona, uzithengisile ngenxa yobugwenxa bakho; yaye unyoko walahlwa ngenxa yezono zakho.', 'XHO22'),
(27, 50, 2, 'Yini na ukuba ndifike kungekho mntu? ndithe xa ndifowuna akwaphendula mntu? Ngaba ingalo yam imfutshane na, ukuba ingasindisi? okanye andinawo amandla okusindisa? Yabona, ngokukhalima kwam ndiyalugqatsa ulwandle, nemilambo ibe yintlango; ziyabola iintlanzi zabo, ngokuba kungekho manzi, ziyafa lunxano.', 'XHO22'),
(27, 50, 3, 'Ndambesa izulu ngezimnyama zokuzila, ndilenze libe yingubo yobumnyama.', 'XHO22'),
(27, 50, 4, 'iNkosi uYehova indinike ulwimi lwabafundileyo, ukuze ndikwazi ukuthetha ilizwi ngexesha elililo kodiniweyo;', 'XHO22'),
(27, 50, 5, 'iNkosi uYehova yayivula indlebe yam, yaye andizange ndivukele okanye ndiwufulathele.', 'XHO22'),
(27, 50, 6, 'Ndiwunikela umhlana wam ukuba ubethwe, nezidlele zam kwabaxheliweyo beenwele zabo; andibusithelisanga ubuso bam ehlazweni nasekutshicelweni.', 'XHO22'),
(27, 50, 7, 'Ngokuba iNkosi uYehova iya kundinceda; ke ngoko, andiyi kuba nazintloni;', 'XHO22'),
(27, 50, 8, 'Usondele kondixelelayo; ngubani na oya kubambana nam? Masime ndawonye: Ngubani na ummangaleli wam? makeze kum.', 'XHO22'),
(27, 50, 9, 'Yabona, iNkosi uYehova iyandinceda; ngubani na owondigweba? yabona, bonke bephela baya konakala njengengubo; amanundu aya kubadla.', 'XHO22'),
(27, 50, 10, 'Ngubani na kuni owoyika uYehova, ophulaphula izwi lomkhonzi wakhe, ahambe ebumnyameni, kungekho kukhanya? makakholose ngegama likaYehova, ahlale ngoThixo wakhe.', 'XHO22'),
(27, 50, 11, 'Yabonani, nonke niphela, nina baphemba umlilo, baxhobe iintolo ezinomlilo, hambani niye elangatyeni lomlilo wenu, nasezintolweni ezinomlilo enizivuthisileyo. Kufuneka uyifumane esandleni sam; uya kulala ngeentlungu.', 'XHO22'),
(27, 51, 1, 'Phulaphulani kum, nina babumelayo ubulungisa, nina bamquqelayo uYehova; khangela ilitye ekroliweyo kulo, nequla lomhadi ekumbiwe kulo.', 'XHO22'),
(27, 51, 2, 'Bhekani uyihlo uAbraham noSara abanizalayo, ngokuba ndathi engoyedwa, ndamsikelela, ndabandisa.', 'XHO22'),
(27, 51, 3, 'Ngokuba uYehova uya kuyithuthuzela iZiyon, uwathuthuzele onke amanxuwa ayo; uya kuliguqula ilizwe lakhe libe njenge-Eden, nelizwe layo libe njengomyezo kaYehova; Kuya kufunyanwa imihlali nemivuyo kuyo, imibulelo nezwi lendumiso.', 'XHO22'),
(27, 51, 4, 'Ndiphulaphuleni, bantu bam; ndiphulaphuleni, bantu bam; ngokuba umyalelo uya kuphuma kum, ndiwumise umyalelo wam, ube sisikhanyiso ebantwini.', 'XHO22'),
(27, 51, 5, 'Busondele ubulungisa bam; Kuvele usindiso lwam, zaye izandla zam ziya kubagweba abantu; iziqithi ziya kulindela kum, kwaye ziya kuthembela esandleni sam.', 'XHO22'),
(27, 51, 6, 'Phakamiselani amehlo enu emazulwini uze ukhangele emhlabeni, ngenxa yokuba amazulu aya kuthi shwaka njengomsi, nomhlaba uya kuguga njengesambatho, nabo bahleli kuwo baya kufa ngolo hlobo; kodwa usindiso lwam luya kuba ngonaphakade, nobulungisa bam abuyi konakaliswa.', 'XHO22'),
(27, 51, 7, 'Phulaphulani kum, nina nibaziyo ubulungisa, nina bantu banesiyalo sam entliziyweni yabo; Musani ukuzoyika izingcikivo zabantu, ningazoyiki izithuko zabo.', 'XHO22'),
(27, 51, 8, 'Kuba amanundu aya kubadla njengengubo, umbungu ubadle njengoboya begusha; ke bona ubulungisa bam buya kuba ngunaphakade, nosindiso lwam lube kwizizukulwana ngezizukulwana.', 'XHO22'),
(27, 51, 9, 'Vuka, vuka, nxiba amandla, ngalo kaYehova! yisuk’ ume ngokweemini zasephakadeni, kwizizukulwana zasephakadeni. Asinguwe na owabulala uRahabhi, wayibetha ingwenya?', 'XHO22'),
(27, 51, 10, 'Asinguwe na owatshisayo ulwandle, amanzi anzongonzongo kakhulu; ukuba amanzi anzongonzongo elwandle enze indlela yokuba kudlule abahlawulelweyo?', 'XHO22'),
(27, 51, 11, 'Ke abakhululwa bakaYehova baya kubuya beze eZiyon benemihlali; novuyo olungunaphakade luya kuba phezu kweentloko zabo; ulonwabo novuyo baya kulufumana; nobuhlwempu nentswelo iya kusaba.', 'XHO22'),
(27, 51, 12, 'Mna ndinguye uMthuthuzeli wenu;', 'XHO22'),
(27, 51, 13, 'Kwaye umlibale uYehova, uMdali wakho, uMenzi wezulu, uMseki wehlabathi; nahlala ninkwantya imini yonke bubushushu bomcudisi, ngokokuba seningxamele ukutshabalalisa? buphi na ke ubushushu bomcudisi?', 'XHO22'),
(27, 51, 14, 'Umbanjwa ungxamela ukukhululwa, angafi emhadini, nokuba siphele isonka sakhe.', 'XHO22'),
(27, 51, 15, 'Ke mna ndinguYehova, uThixo wakho, uMlatyuzisi wolwandle, agqume amaza alo, igama lakhe nguYehova wemikhosi.', 'XHO22'),
(27, 51, 16, 'Ndiwabekile amazwi am emlonyeni wakho, ndakugubungela ngesithunzi sesandla sam, ukuba ndityale amazulu, ndiseke iziseko zehlabathi, ndithi kwiZiyon, Ningabantu bam.', 'XHO22'),
(27, 51, 17, 'Zivuse, zivuse, zivuse, Yerusalem wena uyiseleyo indebe yobushushu bakhe esandleni sikaYehova; Usele intlenge yendebe engcangcazelayo, wayijija.', 'XHO22'),
(27, 51, 18, 'Bekungekho namnye uyithundezayo iYerusalem, phakathi koonyana bonke eyabazalayo; bekungekho namnye usibambe ngesandla sakhe phakathi koonyana bonke abo wabakhulisayo.', 'XHO22'),
(27, 51, 19, 'Zazimbini izinto zakuhlela; ngubani na oya kukuvela? imbuqo, nembubhiso, nendlala, nekrele; ndinithuthuzele ngabani na?', 'XHO22'),
(27, 51, 20, 'Oonyana bakho bawile, balele ezimbombeni zezitrato zonke, njengenkunzi yenkomo ibanjiswe ngomnatha; bazele ngumsindo kaYehova, bubugwenxa boThixo wakho.', 'XHO22'),
(27, 51, 21, 'Ngako oko yiva oku ngoku, wena uludwayi, unxilileyo, kungengawayini;', 'XHO22'),
(27, 51, 22, 'itsho iNkosi yakho uYehova, uThixo wakho, omela abantu bakhe, ukuthi, Yabona, esandleni sakho ndiphethe indebe ehexisayo, indebe enombilini yomsindo wam; awusenakuphinda usele.', 'XHO22'),
(27, 51, 23, 'Ndiya kuwunikela esandleni sabanitshutshisayo; abathi kumphefumlo wakho, Qoqa, siwele; Walala njengomhlaba, nanjengendlela kwabo badlulayo.', 'XHO22'),
(27, 52, 1, 'Vuka, vuka; nxiba amandla akho, Ziyon! Nxiba iingubo zakho zokuhomba, Yerusalem, mzi ungcwele, ngokuba ukususela ngoku akuyi kungena kuwe ongalukanga, noyinqambi.', 'XHO22'),
(27, 52, 2, 'Ziphakamise, usuke eluthulini; Suk’ ume, uhlale phantsi, Yerusalem; zikhulule iimbophelelo zentamo yakho, mthinjwa, ntombi inguZiyon!', 'XHO22'),
(27, 52, 3, 'Ngokuba utsho uYehova ukuthi, Kwathengiswa ngani ngelize; kwaye uya kusala ungenayo nesenti.', 'XHO22'),
(27, 52, 4, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Abantu bam babehle baya eYiputa ukuze baphambukele khona; yaye iAsiriya yabacinezela ngaphandle kwesizathu.', 'XHO22'),
(27, 52, 5, 'Kaloku ke yintoni na enamapha, utsho uYehova, bethatyathwe ngelize nje abantu bam? mabathi abalawuli babo, utsho uYehova; negama lam linyeliswa yonke imihla.', 'XHO22'),
(27, 52, 6, 'Ke ngoko, abantu bam baya kulazi igama lam; ke ngoko, baya kwazi ngaloo mini ukuba ndim lowo uthethayo; khangela, ndim.', 'XHO22'),
(27, 52, 7, 'Hayi, ukuba zintle kwazo ezintabeni iinyawo zabashumayela iindaba ezilungileyo, abavakalisa uxolo; lowo uzisa iindaba ezilungileyo, lowo uvakalisa usindiso; abathi kwiZiyon, Ngukumkani uThixo wakho!', 'XHO22'),
(27, 52, 8, 'Phulaphula ilizwi labagcini bakho; baya kuvuma ngazwi-nye, ngenxa yokuba baya kubona ngamehlo xa uYehova eyibuyisa iZiyon.', 'XHO22'),
(27, 52, 9, 'Memelelani nivume kunye, manxuwa aseYerusalem; ngokuba uYehova ubathuthuzele abantu bakhe, wayikhulula ngentlawulelo iYerusalem.', 'XHO22'),
(27, 52, 10, 'uYehova usityhilile isandla sakhe esingcwele phambi kwamehlo eentlanga zonke; yaye zonke iziphelo zehlabathi ziya kulubona ke usindiso loThixo wethu.', 'XHO22'),
(27, 52, 11, 'Mkani, mkani apho, musani ukuyichukumisa into eyinqambi; evela kuye; Hlambuluka, nina bathwali beempahla zikaYehova.', 'XHO22'),
(27, 52, 12, 'Kuba musani ukumka kamsinya, musani ukuqhwesha; ngokuba uYehova uya kukukhokela; yaye uThixo kaSirayeli woba ngumvuzo wenu.', 'XHO22'),
(27, 52, 13, 'Yabonani, umkhonzi wam uya kwenza ngengqiqo;', 'XHO22'),
(27, 52, 14, 'Abaninzi bamangaliswa nguwe; ubuso bakhe bonakele ngaphezu kobabo bonke abantu, nembonakalo yakhe ibinobubi kunoonyana babantu.', 'XHO22'),
(27, 52, 15, 'Uya kuzichithachitha ke iintlanga ezininzi; ookumkani baya kuthi khamnqa kuye; kuba oko bangekakuxeleli, baya kukubona; nento abangayivanga baya kuva.', 'XHO22'),
(27, 53, 1, 'Ngubani owamkele ingxelo yethu? sityhileke kubani na isandla sikaYehova?', 'XHO22'),
(27, 53, 2, 'Kuba uya kuntshula phambi kobuso bakhe njengegatya, nanjengengcambu emhlabeni owomileyo; kwaye xa siza kumbona, asinakunqwenela nto ilunge ngakumbi.', 'XHO22'),
(27, 53, 3, 'Udeliwe, ushiyiwe ngabantu; umntu olusizi kunye nomntu oqhele ukuba lusizi; sabusithelisa ubuso bethu kuye, ngokungathi kunjalo; udeliwe, asimthabatha;', 'XHO22'),
(27, 53, 4, 'Okwenene, uzithabathele kuye iintsizi zethu, wazithwala iintlungu zethu;', 'XHO22'),
(27, 53, 5, 'Kodwa wahlatywa ngenxa yezikreqo zethu, wacinezelwa ngenxa yobugwenxa bethu; isibetho esizisela uxolo sawela phezu kwakhe; nangemivumbo yakhe siphilisiwe thina.', 'XHO22'),
(27, 53, 6, 'Sonke silahlekile njengezimvu; elowo walahleka ngendlela yakhe; uYehova wabuzisa kuye ubugwenxa bethu sonke.', 'XHO22'),
(27, 53, 7, 'Wacudiswa, waba buhlungu, engawuvuli umlomo wakhe; Njengemvana usiwa ekuxhelweni, nanjengegusha esisidenge phambi kwabachebi bayo, wenjenjalo ukungawuvuli umlomo wakhe.', 'XHO22'),
(27, 53, 8, 'Wakhululwa entolongweni nasekugwetyweni; Ngubani na oya kuyixela isini sakhe? ngokuba wanqanyulwa, waba sehlabathini labaphilileyo; ubethweswa ubugwenxa besizwe sakowethu.', 'XHO22'),
(27, 53, 9, 'Lenza ingcwaba lakhe laba nongendawo, nosisityebi ekufeni kwakhe; ngokuba akenzanga bubi, kungekho nkohliso emlonyeni wakhe.', 'XHO22'),
(27, 53, 10, 'Kodwa uYehova wafuna ukumtyumza; waba buhlungu; ukuba uthe wenza idini lesono ngenxa yomphefumlo wakhe, wobabona abantwana bakhe, ayolule imihla yakhe, ibe nempumelelo inkoliseko kaYehova esandleni sakhe.', 'XHO22'),
(27, 53, 11, 'Wokubona ukusebenza nzima komphefumlo wakhe, aneliswe; Ngokwazi kwakhe uya kumenza ilungisa kakhulu umkhonzi wam olilungisa; Uya kubuthwala ubugwenxa babo.', 'XHO22'),
(27, 53, 12, 'Ngoko ke ndiya kumabela isahlulo kunye namadoda amakhulu, alabe amaxhoba namagorha; ngokuba wawuthulula umoya wakhe ekufeni, wabalelwa kwabangendawo; wathwala izono zabaninzi, wabathandazela abakreqi.', 'XHO22'),
(27, 54, 1, 'Memelela, ludlolo, ludlolo; qhwaba izandla, duma ngezwi elikhulu, wena ungamithiyo; ngokuba baninzi abantwana boshiywa, ngaphezu kwabantwana bomfazi onendoda; utsho uYehova.', 'XHO22'),
(27, 54, 2, 'Yandise indawo yentente yakho, wandise amalaphu ezakhiwo zakho;', 'XHO22'),
(27, 54, 3, 'Kuba uya kutsiba uye ekunene nasekholo; oonyana bakho bazidle ilifa iintlanga, bahlale emizini engamanxuwa.', 'XHO22'),
(27, 54, 4, 'Musani ukoyika; ngokuba aniyi kudana, aniyi kuma; kuba uya kulilibala ihlazo lobutsha bakho yaye akuyi kuba salikhumbula ihlazo lobuhlolokazi bakho.', 'XHO22'),
(27, 54, 5, 'Ngokuba indoda yakho nguMenzi wakho; NguYehova wemikhosi igama lakhe; noMkhululi wakho, oyiNgcwele kaSirayeli; Uya kubizwa ngokuba nguThixo wehlabathi lonke.', 'XHO22'),
(27, 54, 6, 'Ngenxa yokuba uYehova ukubize njengomfazi oshiyiweyo nomphefumlo onentlungu, nanjengebhinqa eliselula xa elahliwe, utsho uThixo wakho.', 'XHO22'),
(27, 54, 7, 'Ndakushiya umzuzwana omncinane; ndiya kukubutha ngemfesane enkulu.', 'XHO22'),
(27, 54, 8, 'Ngomkhukula woburhalarhume ndabusithelisa ubuso bam kuwe okwephanyazo; ndiya kuba nemfesane kuwe ngenceba engunaphakade; utsho uMkhululi wakho uYehova.', 'XHO22'),
(27, 54, 9, 'Kuba kum kunjengamanzi kaNowa, kuba njengoko ndafungayo ukuba amanzi kaNowa akasayi kuphinda adlule phezu komhlaba; ngoko ndafunga ukuba andiyi kunicaphukela, ndinikhalimele kakhulu.', 'XHO22'),
(27, 54, 10, 'Ngokuba ziya kushukuma iintaba, neenduli zishukume; yona inceba yam ayiyi kumka kuwe, ungashukumi nomnqophiso wam woxolo; utsho onemfesane kuwe, uYehova.', 'XHO22'),
(27, 54, 11, 'Owu mashwa, baqhutywa sisaqhwithi naze ningathuthuzeleki, yabona, ndiwabeka amatye akho abe nemibala emihle, neziseko zakho ndizizinzise ngesafire.', 'XHO22'),
(27, 54, 12, 'Ndozenza iingcango zakho ngeagati, namasango akho ngamatye, nemida yakho yonke ngamatye anqabileyo.', 'XHO22'),
(27, 54, 13, 'Bonke abantwana bakho baya kuba ngabafundi bakaYehova; yaye luya kuba lukhulu uxolo lwabantwana bakho.', 'XHO22'),
(27, 54, 14, 'Uya kuqiniselwa ngobulungisa; uya kuba kude namaxhala; ngokuba akuyi koyika, akuyi kunkwantya; ngokuba ayiyi kusondela kuwe.', 'XHO22'),
(27, 54, 15, 'Yabona, bayaqukana, bahlanganisane, andikho mna kuloo nto; bonke abanibuthayo baya kuwa ngenxa yakho.', 'XHO22'),
(27, 54, 16, 'Yabona, mna ndadala ingcibi etshisa amalahle emlilweni, ivelise impahla ngokweshishini layo; ndadala nomonakalisi wokuyonakalisa.', 'XHO22'),
(27, 54, 17, 'Akukho nto iya kwenziwa kuwe ayiyi kuphumelela; kwaye zonke iilwimi ziya kusukuma phezu kwakho kwaye zibagwebe. Lilo eli ilifa labakhonzi bakaYehova, nobulungisa babo buphuma kum, utsho uYehova.', 'XHO22'),
(27, 55, 1, 'Bonke abanxaniweyo, beza emanzini, nabangenamali; yizani, nithenge, nidle; Ewe, yizani nithenge iwayini nobisi ngaphandle kwemali nangaphandle kwexabiso.', 'XHO22'),
(27, 55, 2, 'Yini na ukuba nilinganisele imali ngezinto ezingezizo izonka? nomsebenzi wenu kwinto enganelisiyo? Phulaphulani kum ninyameke, nidle okulungileyo, uvuye umphefumlo wenu ngokugqithileyo.', 'XHO22'),
(27, 55, 3, 'Guqani, nize kum; yivani, uphile umphefumlo wenu; yaye ndiya kwenza umnqophiso ongunaphakade nani, iinceba zikaDavide.', 'XHO22'),
(27, 55, 4, 'Qaphelani, ndimmisele ukuba abe lingqina labantu, inkokeli nenkokeli yabantu.', 'XHO22'),
(27, 55, 5, 'Yabona, uya kubiza uhlanga ongalwaziyo, neentlanga ebezingakwazi ziya kubalekela kuwe ngenxa kaYehova uThixo wakho nangenxa yaLowo Ungcwele kaSirayeli; ngokuba enizukisile.', 'XHO22'),
(27, 55, 6, 'Funani uYehova engekafunyanwa, nimbize esekufuphi.', 'XHO22'),
(27, 55, 7, 'Ongendawo makajike endleleni yakhe, ongendawo abuye ezicamangweni zakhe; makaguqukele kuYehova, woba nemfesane kuye; ibuyele kuThixo wethu, ngokuba woxolela futhi.', 'XHO22'),
(27, 55, 8, 'Kuba iingcinga zam asizizo iingcinga zenu, neendlela zenu asizizo iindlela zam, utsho uYehova.', 'XHO22'),
(27, 55, 9, 'Inene, njengokuba izulu liphezu komhlaba, ngokunjalo iindlela zam ziziphakamele iindlela zenu, neengcinga zam ziziphakamele iingcinga zenu.', 'XHO22'),
(27, 55, 10, 'Kuba njengoko kusihla imvula nekhephu ezulwini, ingabuyeli khona, kodwa iwunyakamise umhlaba, iwukhulise, iwukhulise, ukuze inike imbewu umhlwayeli, imnike isonka odlayo.', 'XHO22'),
(27, 55, 11, 'Kuya kuba njalo ngelizwi lam eliphuma emlonyeni wam: aliyi kubuyela kum ndedwa, kodwa liya kukwenza ukuthanda kwam, likuphumelelise konke endikuthume lona.', 'XHO22'),
(27, 55, 12, 'Kuba niya kuphuma ninovuyo, nikhokelwa ninoxolo; iintaba neenduli ziya kunyikima ngovuyo phambi kwenu, nemithi yelizwe ibethe izandla.', 'XHO22'),
(27, 55, 13, 'Endaweni yemithi enameva kuya kuhluma umsipres, esikhundleni semithana kuhlume ummirtile; kwaye kuYehova kuya kuba njengegama, njengomqondiso ongunaphakade ongenakunqunyulwa.', 'XHO22'),
(27, 56, 1, 'Wathi uYehova, Gcinani isiko, nenze ubulungisa; ngokuba lusondele ukuba luze usindiso lwam, nobulungisa bam buza kutyhilwa.', 'XHO22'),
(27, 56, 2, 'Hayi, uyolo lomntu okwenzayo oku, nonyana womntu oyibambileyo; ogcina isabatha, ukuba angayihlambeli; osibambayo isandla sakhe singenzi nanye into embi.', 'XHO22'),
(27, 56, 3, 'Makangathethi ke unyana wolunye uhlanga, onamathele kuYehova, esithi, uYehova undahlule ngokupheleleyo ebantwini bakhe; Makangatsho umkhonzi ukuthi, Yabona, ngumthi owomileyo.', 'XHO22'),
(27, 56, 4, 'Kuba utsho uYehova kumathenwa agcina iisabatha zam nabanyula into endiyithandayo nababambelele ngokuqinileyo emnqophisweni wam;', 'XHO22'),
(27, 56, 5, 'ndobanika endlwini yam nasezindongeni zam indawo negama elilungileyo ngaphezu koonyana neentombi; ndiya kubanika igama elingunaphakade, elingenakunqanyulwa.', 'XHO22'),
(27, 56, 6, 'Yaye oonyana bolunye uhlanga abanamathela kuYehova ukuze bakhonze yena nokuthanda igama likaYehova ukuze babe ngabakhonzi bakhe, wonk’ ubani ogcina isabatha ukuze ayihlambele nobambelela emnqophisweni wam;', 'XHO22'),
(27, 56, 7, 'ndobazisa entabeni yam engcwele, bavuye endlwini yam yokuthandaza; amadini abo anyukayo neminikelo yabo yokholeka esibingelelweni sam; ngokuba indlu yam iya kubizwa ngokuba yindlu yokuthandaza yabantu bonke.', 'XHO22'),
(27, 56, 8, 'Itsho iNkosi uYehova, ekubabuthayo abagxothiweyo bakwaSirayeli, ukuthi, Noko ke ndiya kuhlanganisela kuye nabanye ngaphaya kwababuthiweyo bakhe;', 'XHO22'),
(27, 56, 9, 'Onke amarhamncwa asendle yizani adle, nawo onke amarhamncwa ehlathi.', 'XHO22'),
(27, 56, 10, 'Abahlebi bakhe baziimfama, àbazi bonke; lala, xoka, ngathi ukozela.', 'XHO22'),
(27, 56, 11, 'Ewe, bona banjengeebhokhwe ezilambileyo, ezingahluthiyo, babe ngabalusi abangaqondiyo; bonke bephela bakhangela kwezabo iindlela, elowo ekhangela kweyakhe inzuzo, kowayo ummelwane.', 'XHO22'),
(27, 56, 12, 'Wathi ke, Yizani, ndiya kuthabatha iwayini, sizalise ngesi siselo sinxilisayo; nengomso liya kuba njenganamhla, linde kunene.', 'XHO22'),
(27, 57, 1, 'Ilungisa liyatshabalala; akukho mntu ke ukunyamekelayo oko ngentliziyo; kwaye abantu abanenceba bayasuswa, ngaphandle nje kokuba amalungisa asusiwe ebubini obuzayo.', 'XHO22'),
(27, 57, 2, 'Uya kungena enoxolo; baya kuphumla kwiimandlalo zabo, ngamnye ohamba ngobulungisa bakhe.', 'XHO22'),
(27, 57, 3, 'Ke sondelani apha, nina nyana bemilingo, nzala yehenyukazi nehenyukazi.', 'XHO22'),
(27, 57, 4, 'Udlala nabani? Uwuvula kubani na umlomo wakho, ulukhuphele phandle ulwimi lwakho? Aningabantwana bokreqo na, sisizukulwana sobuxoki?', 'XHO22'),
(27, 57, 5, 'Izithixo yini na, phantsi kwemithi yonke eluhlaza, nibaxhela abantwana ezimbombeni zamatye?', 'XHO22'),
(27, 57, 6, 'Isahlulo sakho siphantsi kwamatye agudileyo omlambo; yabona, kukuphela kwakho; Wabagalela nento eselwayo, bazisa nomnikelo oshiswa emlilweni. Ngaba kufuneka ndithuthuzeleke ngale nto?', 'XHO22'),
(27, 57, 7, 'Uzandlala iimandlalo zakho entabeni ende; wenyuka nawe ukuya kwenza idini.', 'XHO22'),
(27, 57, 8, 'Wasibeka isikhumbuzo sakho nasemva kweengcango nemigubasi, ngenxa yokuba wazibonakalisa komnye uthile ungenguye mna waza wenyuka; watwabulula iimandlalo zakho, wanqophisana nabo; wawuthanda iibhedi zabo apho wawubabone khona.', 'XHO22'),
(27, 57, 9, 'Wahambela kukumkani unevumba elimnandi, walinyusa ivumba lakho, wazithuma kude izigidimi zakho, wazithoba ukuya kwelabafileyo.', 'XHO22'),
(27, 57, 10, 'Udiniwe bubuninzi bendlela yakho; kodwa akutshongo ukuthi, Akukho themba; ufumene ubomi esandleni sakho; ke ngoko, anibanga lusizi.', 'XHO22'),
(27, 57, 11, 'Ubusoyika bani okanye ubusoyika bani na, ukuba uxoke ungandicingeli yaye ungakubeki oko entliziyweni yakho? Andithi cwaka na kwakudala, ungandoyiki?', 'XHO22'),
(27, 57, 12, 'Ndiya kubuxela mna ubulungisa bakho, nezenzo zakho; ngokuba abayi kuninceda.', 'XHO22'),
(27, 57, 13, 'Ekubizeni kwakho, mazikusindise izihlwele zakho; kodwa umoya uya kubathabatha bonke; kodwa okholosa ngam uya kulidla ilifa ilizwe, ayidle ilifa intaba yam engcwele;', 'XHO22'),
(27, 57, 14, 'Ndiya kuthi, Dumisani, zukisani, lungisani indlela, susani ukreqo emendweni wabantu bam.', 'XHO22'),
(27, 57, 15, 'Kuba kuthiwa, Osenyangweni uhleli ngonaphakade, ogama lingcwele; Ndihleli endaweni ephakamileyo engcweleni, nomoya othobekileyo, ukuze ndihlaziye umoya wabathobekileyo, ndihlaziye iintliziyo zabantliziyo zaphukileyo.', 'XHO22'),
(27, 57, 16, 'Kuba andiyi kuhlala ndiphikisana, kwaye andiyi kuhlala ndinomsindo, kuba umoya uya kuphelelwa ngamandla phambi kwam kunye nemimoya endiyenzileyo.', 'XHO22'),
(27, 57, 17, 'Ndaba noburhalarhume kububi bokubawa kwakhe, ndambetha;', 'XHO22'),
(27, 57, 18, 'Iindlela zakhe ndizibonile: ndiya kumphilisa; Ndiza kumkhokela kwakhona ndize ndibuyisele intuthuzelo kuye nabo bazilileyo bakhe.', 'XHO22'),
(27, 57, 19, 'Ndidala iziqhamo zomlomo; Uxolo, uxolo kwabakude nakwabakufuphi, utsho uYehova; ndiya kumphilisa.', 'XHO22'),
(27, 57, 20, 'Ke bona abangendawo banjengolwandle oluvuzayo, alunakuzola; amanzi alo avelisa udaka nodaka.', 'XHO22'),
(27, 57, 21, 'Akukho luxolo kwabangendawo, utsho uThixo wam.', 'XHO22'),
(27, 58, 1, 'Danduluka uphimisele, ungathinteleki; phakamisa izwi lakho njengesigodlo, ubaxelele abantu bam ububi babo, uyixelele indlu kaYakobi izono zayo.', 'XHO22'),
(27, 58, 2, 'Ke bona baya kundifuna imihla yonke, bakuvuyele ukuzazi iindlela zam; njengohlanga olwenze okuthe tye, olungatyekiyo emyalelweni woThixo walo; bacela kum imiyalelo yomthetho; bayakuvuyela ukusondela kuThixo.', 'XHO22'),
(27, 58, 3, 'Bathi sizile ukudla, ungaboni na wena? Yini na ukuba siyicinezele imiphefumlo yethu, nibe ningazi? Yabona, ngomhla wokuzila kwakho, ukholekile kuwe, unqwenele yonke imigudu yakho;', 'XHO22'),
(27, 58, 4, 'Yabonani, nizila ukudla, nibambana, nibetha ngenqindi elikhohlakeleyo; musani ukuzila njengoko nisenzayo namhla, ukuze livakale ilizwi lenu.', 'XHO22'),
(27, 58, 5, 'Ngaba kukuzila endikunyulileyo na? Ngaba ngumhla wokuba umntu awucinezele umphefumlo wakhe? Ukuthokombisa intloko njengexhalanga na, waza wandlala ezirhwexayo nothuthu phantsi kwendlu yakho? Ngaba uyathanda ukuyibiza ngokuba yimini yokuzila ukutya ekholisa uYehova?', 'XHO22'),
(27, 58, 6, 'Asikokuzila endikunyulileyo na? Ukukhulula amakhamandela obugwenxa, ukukhulula imithwalo enzima, ukukhulula abatyumkileyo, ukwaphule zonke iidyokhwe?', 'XHO22'),
(27, 58, 7, 'Asikuko na ukuba unike olambileyo isonka sakho, nokuba ungenise amahlwempu agxothiweyo endlwini yakho? xa ubona umntu eze, uyamambesa; ungayifihli inyama yakho?', 'XHO22'),
(27, 58, 8, 'Kuya kwandula ke ukukhanya kwakho kuphume njengokusa, buhlume kamsinya ubomi bakho; nobulungisa bakho buya kuhamba phambi kwakho; Uzuko lukaYehova luya kuba ngumvuzo wakho.', 'XHO22'),
(27, 58, 9, 'Uya kwandula ubize, asabele uYehova; uya kubiza, athi, Ndikho! Ukuba uthe wayisusa idyokhwe, nokukhomba ngomnwe, nokuthetha nje, phakathi kwenu;', 'XHO22'),
(27, 58, 10, 'ukuba uthe wawelulela umphefumlo wakho kolambileyo, wawudlisa umphefumlo ocinezelweyo; apho ukukhanya kwakho kuya kukhanya ebumnyameni, nobumnyama bakho bube njengemini.', 'XHO22'),
(27, 58, 11, 'uYehova uya kuhlala ekwalathisa, uwuhluthise umphefumlo wakho kowomileyo, watyebisa amathambo akho; yaye uya kuba njengomyezo onyakanyiswa yimvula nanjengomthombo omanzi angatshiyo.', 'XHO22'),
(27, 58, 12, 'Abaya kuba ngabakho baya kwakha amanxuwa akudala; ukubizwa kwakhe kuthiwe, nguMcinzi weentanda, nguMlungisi weendlela;', 'XHO22'),
(27, 58, 13, 'Ukuba uthe walubuyisa unyawo lwakho kwisabatha, ukuba uwubone umhla wam ongcwele; uthi ukuyibiza isabatha luvuyo, ingcwele kaYehova; kwaye umhlonele ngokungazenzi iindlela zakho kwaye ungafuni ukuthanda kwakho kwaye ungathethi amazwi akho.', 'XHO22'),
(27, 58, 14, 'Apho uya kugcoba ngoYehova; yaye ndiya kukuzisa kwiindawo eziphakamileyo zomhlaba ndikubophelele elifeni likaYakobi uyihlo, ngenxa yokuba umlomo kaYehova uthethile kuwe.', 'XHO22'),
(27, 59, 1, 'Yabonani, isandla sikaYehova asisifutshane ukuba sihlangule; indlebe yakhe ayinzima, ukuba ingevi;', 'XHO22'),
(27, 59, 2, 'Bubugwenxa benu obunahlukanisileyo noThixo wenu, zizono zenu ezibusithelisileyo ubuso bakhe kuni, ukuba angevi.', 'XHO22'),
(27, 59, 3, 'Kuba izandla zenu zidyobhekile ligazi, neminwe yenu bubungendawo; nomlomo wenu uthetha ubuxoki, ulwimi lwenu ludumzela ububi.', 'XHO22'),
(27, 59, 4, 'Akukho bani ufuna okusesikweni, akukho namnye umthethelelayo inyaniso; bakhukhumele, bathetha ubuxoki; bakhawula ububi, bazale ubutshinga.', 'XHO22'),
(27, 59, 5, 'Baqandusela amaqanda, baluka izindlu zokuluka; Lowo udla amaqanda abo uya kufa;', 'XHO22'),
(27, 59, 6, 'Iingubo zabo aziyi kuba yimpahla, nemisebenzi yabo abayi kuzinxiba; izenzo zabo zizenzo zobutshinga, nenkohliso isezandleni zabo.', 'XHO22'),
(27, 59, 7, 'Iinyawo zabo zigidimela ebubini, zikhawuleze ukuya kuphalaza igazi elimsulwa; iingcinga zabo ziingcinga zobutshinga; emendweni wabo yimbuqo nentshabalalo.', 'XHO22'),
(27, 59, 8, 'Abayazi indlela yoxolo; akukho okusesikweni ezindleleni zabo; bazenzele iindlela ezigoso; ongena khona akalwazi uxolo.', 'XHO22'),
(27, 59, 9, 'Ke ngoko, umthetho ukude kuthi, nobulungisa abufikeleli kuthi; sifuna ukukhanya, kodwa sihamba ebumnyameni.', 'XHO22'),
(27, 59, 10, 'Siyaphuthaphutha njengeemfama eludongeni, siyaphuthaphutha ngokungathi asinamehlo; siyakhubeka emini njengasebusuku; sisemanxuweni njengabafileyo.', 'XHO22'),
(27, 59, 11, 'Sonke sigquma njengamabhere, sibhomboloza njengamahobe; silindele umgwebo, akukho; usindiso, kodwa lukude kuthi.', 'XHO22'),
(27, 59, 12, 'Ngenxa yokuba izikreqo zethu zibe zikhulu phambi kwakho, nezono zethu ziphezu kwethu, ngenxa yokuba izikreqo zethu zinathi; kwaye siyabazi ubugwenxa bethu;', 'XHO22'),
(27, 59, 13, 'Ngokona kuYehova nangokuthetha ubuxoki nokubuya umva kuThixo wethu, ukuthetha ingcinezelo nemvukelo, ukuthetha inkohliso namazwi obuxoki asuka entliziyweni.', 'XHO22'),
(27, 59, 14, 'Ubuye umva umyalelo, nobulungisa bumi kude, ngokuba inyaniso ikhubekile ezitratweni, nobulungisa abuyi kungena.', 'XHO22'),
(27, 59, 15, 'Ewe, inyaniso iyakhanyelwa; otyekayo ebubini uya kuba lixhoba lakhe.', 'XHO22'),
(27, 59, 16, 'Wathi akubona ukuba akukho mntu, wamangaliswa ukuba kungekho mntu; ke ngoko, isandla sakhe samzisela usindiso; nobulungisa bakhe bamxhasa.', 'XHO22'),
(27, 59, 17, 'Kuba wanxiba ubulungisa ngokwengubo yentsimbi, nesigcina-ntloko sosindiso entloko; wambatha iingubo zempindezelo, wambatha ikhwele njengengubo yokwaleka.', 'XHO22'),
(27, 59, 18, 'Ngokwezenzo zabo uya kubuyisela umsindo kwabo balwa naye, ababuyekezele impindezelo abakhonzi bakhe; eziqithini uya kuyibuyisela imbuyekezo.', 'XHO22'),
(27, 59, 19, 'Baya kuloyika ke igama likaYehova entshonalanga, nozuko lwakhe empumalanga. Xa utshaba lusiza njengonogumbe, uMoya kaYehova uya kuluphakamisela ibhanile.', 'XHO22'),
(27, 59, 20, 'Uya kuza umsindisi eZiyon nakuYakobi ababuyayo ebubini,” utsho uYehova.', 'XHO22'),
(27, 59, 21, 'Mna ke nguwo lo umnqophiso wam nabo, utsho uYehova; Umoya wam uphezu kwakho, namazwi am endiwabeke emlonyeni wakho, akayi kumka emlonyeni wakho, nasemlonyeni wembewu yakho, nasemlonyeni wembewu yakho, utsho uYehova, kususela ngoku kude kuse ephakadeni.', 'XHO22'),
(27, 60, 1, 'Vuka, ukhanye; ngokuba kufikile ukukhanya kwakho, nobuqaqawuli bukaYehova bukhanyisile phezu kwakho.', 'XHO22'),
(27, 60, 2, 'Ngokuba, uyabona, ubumnyama buya kuwugubungela umhlaba, nobumnyama beentlanga; kodwa uYehova uya kubonakala kuwe, bubonakale ubuqaqawuli bakhe phezu kwakho.', 'XHO22'),
(27, 60, 3, 'Iintlanga ziya kuza ekukhanyeni kwakho, nookumkani beze eluzukweni lovuko lwakho.', 'XHO22'),
(27, 60, 4, 'Phakamisa amehlo akho ezindaweni zonke, ubone; bonke bayahlanganisana, besiza kuwe; oonyana bakho bavela kude, neentombi zakho zidilize ngakuwe.', 'XHO22'),
(27, 60, 5, 'Uya kwandula ke ubone, ugqabhuke kunye, yoyike intliziyo yakho, yande; ngokuba ubutyebi bolwandle buya kubuyela kuwe, nobutyebi beentlanga buya kuza kuwe.', 'XHO22'),
(27, 60, 6, 'Inkitha yeenkamela iya kukugubungela, iinkamela zamaMidiyan nezakwaEfa; wonke uShebha uya kuza; baya kuzisa igolide nesiqhumiso; yaye baya kuxela iindumiso zikaYehova.', 'XHO22'),
(27, 60, 7, 'Yonke imihlambi yakwaKedare ibuthelana kuwe, iinkunzi zeegusha zakwaNebhayoti ziya kukulungiselela; baya kunyuka baya esibingelelweni sam bevuya, ndiyihombise indlu yozuko lwam.', 'XHO22'),
(27, 60, 8, 'Ngubani na, ephaphazela njengelifu, njengamavukuthu ephaphazela ezifestileni zawo?', 'XHO22'),
(27, 60, 9, 'Ziya kuhamba iziqithi phambi kwam, neenqanawa zaseTarshishe, zibazise oonyana bakho bevela kude, benesilivere yabo negolide yabo, egameni likaYehova uThixo wakho, koyiNgcwele kaSirayeli; undizukisile.', 'XHO22'),
(27, 60, 10, 'Iintlanga ziya kuzakha iindonga zakho, ookumkani bazo bakukhonze; kuba ndikubethe ndinomsindo, kodwa ndaba nenceba kuwe ngokuthanda kwam.', 'XHO22'),
(27, 60, 11, 'Ngenxa yoko aya kuhlala evuliwe amasango akho; imini nobusuku abuyi kuvalwa; ukuze abantu baziswe kuwe imikhosi yeentlanga, nookumkani bazo beze kuziswa.', 'XHO22'),
(27, 60, 12, 'Kuba uhlanga norhulumente ongakukhonzeyo uya kutshabalala; ewe, ezo ntlanga ziya kutshatyalaliswa ngokupheleleyo.', 'XHO22'),
(27, 60, 13, 'Ubuqaqawuli beLebhanon buya kuza kuwe, umsipres, umpayini netyeya kunye, ukuze ihombise indawo yengcwele yam; kwaye ndiya kuyenza ngcwele indawo yeenyawo zam.', 'XHO22'),
(27, 60, 14, 'Oonyana babacinezeli bakho baya kuza kukuphulaphula; Baya kuqubuda ezinyaweni zakho bonke abakugibayo; yaye baya kuyibiza ngokuba sisiXeko sikaYehova, iZiyon yaLowo Ungcwele kaSirayeli.', 'XHO22'),
(27, 60, 15, 'Ukuba uthe wacekiswa, wathiywa, akwadlula bani kuwe, ndiya kukwenza isihomo esingunaphakade, esiyolisayo kwizizukulwana ngezizukulwana.', 'XHO22'),
(27, 60, 16, 'Uya kwanya amasi eentlanga, wanye ibele lokumkani; wazi ukuba mna Yehova ndinguMkhululi wakho, uMkhululi wakho, ingxolo kaYakobi.', 'XHO22'),
(27, 60, 17, 'Endaweni yobhedu ndizisa igolide, esikhundleni sesinyithi ndizisa isilivere, ubhedu lube ngamatye, esikhundleni samatye isinyithi; Yaye abakhonzi bakho ndiya kubenzela uxolo nobulungisa abakhonzi bakho.', 'XHO22'),
(27, 60, 18, 'Akuyi kuba savakala lugonyamelo ezweni lakho, nembubhiso nangcinezelo ezweni lakho; iindonga zakho uya kuthi luSindiso, namasango akho uthi yiNdumiso.', 'XHO22'),
(27, 60, 19, 'Ilanga aliyi kuba saba sisikhanyiso kuwe emini; nenyanga ayiyi kukukhanyisela ngenxa yozuko, kodwa uYehova uya kuba sisikhanyiso sakho esingunaphakade, uThixo wakho abe luzuko lwakho.', 'XHO22'),
(27, 60, 20, 'Ilanga lakho aliyi kuba satshona; nenyanga yakho ayisayi kubakho, ngenxa yokuba uYehova uya kuba sisikhanyiso esingunaphakade kuwe, ziphele iintsuku zesijwili sakho.', 'XHO22'),
(27, 60, 21, 'Bonke abantu bakho bayakuba ngamalungisa, balidle ilifa ilizwe ngonaphakade, behlume lesityalo sam, umsebenzi wezandla zam, ukuze ndihombe.', 'XHO22'),
(27, 60, 22, 'Omncinanana uya kuba liwaka, nomncinci abe luhlanga olunamandla; Mna, Yehova, ndiya kukukhawulezisa oko ngexesha elifanelekileyo.', 'XHO22'),
(27, 61, 1, 'uMoya kaYehova uphezu kwam; ngenxa yokuba uYehova endithambisele ukuba ndishumayele iindaba ezilungileyo kwabalulamileyo; Undithume ukuba ndiphilise abantliziyo ityumkileyo, ndivakalise inkululeko kubathinjwa, ndibhengeze ukukhululwa entolongweni;', 'XHO22'),
(27, 61, 2, 'ukuxela umnyaka wenkoliseko kaYehova, nomhla wempindezelo yoThixo wethu; ukuthuthuzela bonke abanesijwili;', 'XHO22'),
(27, 61, 3, 'Ukuba abekwe eZiyon amalungisa, ukuba anikwe uzuko esikhundleni sothuthu, neoli yemihlali esikhundleni sesijwili, ingubo yendumiso esikhundleni somphefumlo odakumbileyo; ukubizwa kwabo kuthiwe yimithi yobulungisa, etyelwe nguYehova, ukuze kuzukiswe yena.', 'XHO22'),
(27, 61, 4, 'Baya kwakha amanxuwa angunaphakade, bavuse iindawo ezichithakeleyo zangaphambili;', 'XHO22'),
(27, 61, 5, 'Baya kuma abasemzini, baluse imihlambi yenu, oonyana bezinye iintlanga babe ngabalimi benu, nabezidiliya zenu.', 'XHO22'),
(27, 61, 6, 'Ke nina ukubizwa kwenu, kuya kuthiwa ningababingeleli bakaYehova; abantu baya kunibiza ngokuba ningabakhonzi boThixo wethu; niya kudla ubutyebi beentlanga, nibe nozuko nozuko lwazo.', 'XHO22'),
(27, 61, 7, 'Ngenxa yehlazo lenu, niya kwamkela kabini; basidle ilifa isabelo sabo bedidekile; ke ngoko, baya kuba nobutyebi obuphindiweyo ezweni labo; baya kuba novuyo olungunaphakade.', 'XHO22'),
(27, 61, 8, 'Kuba mna, Yehova, ndithanda ubulungisa, ndikuthiyile ubusela njengomlilo; ndiya kuwumisela ngenyaniso umsebenzi wabo, ndenze umnqophiso ongunaphakade nabo.', 'XHO22'),
(27, 61, 9, 'Yaye imbewu yabo iya kwaziwa phakathi kweentlanga, nembewu yabo phakathi kweentlanga; bonke abababonayo baya kwazi ukuba bangabantwana abasikelelwe nguYehova.', 'XHO22'),
(27, 61, 10, 'Ndinemihlali ndanemihlali ngoYehova, umphefumlo wam uya kugcoba ngoThixo wam; ngokuba wandambesa ingubo yosindiso, wandambesa ngengubo yokwaleka yobulungisa, nanjengomyeni ovatho izivatho, nanjengomtshakazi ozihombisa izivatho zakhe.', 'XHO22'),
(27, 61, 11, 'Kuba njengoko ilizwe livelisa iimbewu zalo, njengoko umyezo otyalwe kuwo ukhula; ngoko iNkosi uYehova iya kuzisa ubulungisa nendumiso phambi kweentlanga zonke.', 'XHO22'),
(27, 62, 1, 'Ngenxa yeZiyon andiyi kuthi tu, ngenxa yeYerusalem andiyi kuzola, bude bubonakale njengokukhanya ubulungisa bayo, nosindiso lwayo njengesikhuni esidangazelayo;', 'XHO22'),
(27, 62, 2, 'Iintlanga ziya kububona ubulungisa bakho, nookumkani bonke babubone ubuqaqawuli bakho; uya kubizwa ngegama elitsha, eliya kubizwa ngumlomo kaYehova;', 'XHO22'),
(27, 62, 3, 'Uya kuba sisithsaba esiqaqambileyo esandleni sikaYehova nonkontsho lwasebukhosini esandleni soThixo wakho.', 'XHO22'),
(27, 62, 4, 'Akuyi kuba sathiwa unguLosweli; nelizwe lakho akuyi kuba sathiwa yiNahatoti; ngokuba uYehova ekuvuyele, lithembeke ilizwe lakho.', 'XHO22'),
(27, 62, 5, 'Njengokuba indodana izeka intombi, oonyana bakho baya kuzeka wena; njengokuba umyeni enemihlali ngomtshakazi, uThixo wakho uya kuba nemihlali ngawe.', 'XHO22'),
(27, 62, 6, 'Phezu kweendonga zakho, Yerusalem, ndimise abagcini: imini yonke nobusuku bonke abayi kuze bathi quthu; Nina babiza kuYehova, musani ukuthi cwaka;', 'XHO22'),
(27, 62, 7, 'Musani ukumnika ukuphumla, ade ayimise iYerusalem, ayenze ibe sisihombo ehlabathini.', 'XHO22'),
(27, 62, 8, 'uYehova ufunge ngesandla sakhe sokunene, nesandla sakhe esithe nkqi, esithi, Andisayi kuba sazinika iintshaba zakho ingqolowa yakhe, ukuba ibe kukudla kwakho; noonyana bolunye uhlanga bafanele bangayiseli iwayini yakho odiniweyo ngayo.', 'XHO22'),
(27, 62, 9, 'Ke bona abawabuthayo baya kuzidla, badumise uYehova; yaye abo bayisongileyo baya kuyisela kwiintsapho zam ezingcwele.', 'XHO22'),
(27, 62, 10, 'Phumani ngamasango; lungisani indlela yabantu; lahlani, tshabalalisani umendo; susa amatye; phakamisa umgangatho kubantu.', 'XHO22'),
(27, 62, 11, 'Yabona, uYehova uthethile wesa eziphelweni zehlabathi, wathi, Yithini kwintombi enguZiyon, Yabona, usindiso lwakho luyeza; yabona, umvuzo wakhe unawo, esebenza phambi kwakhe.', 'XHO22'),
(27, 62, 12, 'Ukubizwa kwabo kuya kuthiwa, ngabantu abangcwele, abakhululwe nguYehova; ukubizwa kwakho kuthiwe, Uswelekelwe, ngumzi ongashiyiweyo.', 'XHO22'),
(27, 63, 1, 'Ngubani na lo wakwaEdom, onxibe iimpahla ezimbejembeje zaseBhotsera? Ongena engutyeni yakhe, ohamba ebuninzini bamandla akhe? Mna othetha ubulungisa ndinegunya lokusindisa.', 'XHO22'),
(27, 63, 2, 'Kutheni sibomvu isambatho sakho, nezambatho zakho zibe njengezomntu oxovula iwayini etyebileyo?', 'XHO22'),
(27, 63, 3, 'Isixovulelo ndisixovule ndedwa; kwakungekho nanye kwiintlanga ezazikunye nam; igazi lawo liya kutshizwa ezingubeni zam, ndizidyobhise zonke iingubo zam.', 'XHO22'),
(27, 63, 4, 'Kuba umhla wokukhulula usentliziyweni yam, nomnyaka wokukhulula kwam ngokuhlawulela ufikile.', 'XHO22'),
(27, 63, 5, 'Ndahamba ke, akwabakho mncedi; kwaye ndamangaliswa ukuba kwakungekho bani ukuxhasayo; sandisindisa ke isandla sam; yaye usizi lwam, lwandixhasa.', 'XHO22'),
(27, 63, 6, 'Ndiya kuzinyhasha iintlanga ndinomsindo, ndizinxilise ndinomsindo, ndiwawise emhlabeni amandla azo.', 'XHO22'),
(27, 63, 7, 'Ndithanda ukuthetha ubabalo lukaYehova, indumiso kaYehova ngako konke asenzele kona uYehova, nokulunga okukhulu kwindlu kaSirayeli awayibonakalisayo kubo ngokobabalo lwakhe nangokwengxokolo yeentlanga ububele bakhe.', 'XHO22'),
(27, 63, 8, 'Ngokuba wathi, Bangabantu bam okwenyaniso, oonyana abangaxokiyo; ngoko ke waba nguMsindisi wabo.', 'XHO22'),
(27, 63, 9, 'Kuko konke ukubandezeleka kwabo babecinezelwe, yaza ingelosi yobuso bakhe yabasindisa; ngothando lwakhe nangenceba yakhe wabakhulula ngokumisela yena; wawathwala, wawathwala yonke imihla yamandulo.', 'XHO22'),
(27, 63, 10, 'Bamphikisa ke uMoya wakhe oyiNgcwele; ke waba lutshaba lwabo, walwa nabo.', 'XHO22'),
(27, 63, 11, 'Wacinga ngeemini zamandulo, uMoses nabantu bakhe, wathi, Uphi na lowo wabakhuphayo elwandle, umalusi womhlambi wakhe? Uphi yena owafaka umoya oyingcwele kuye?', 'XHO22'),
(27, 63, 12, 'owabakhokela ngasekunene kukaMoses ngesandla sakhe esikhazimlayo, waza wawahlulahlula amanzi phambi kwabo ukuba enze igama lakhe ukusa kwixesha elingenammiselo?', 'XHO22'),
(27, 63, 13, 'Ngubani na owabakhokelela enzulwini, njengehashe entlango, ukuba bangakhubeki?', 'XHO22'),
(27, 63, 14, 'Njengenkomo ehla iye entilini, uMoya kaYehova wayiphumza; wenjenje ukubakhokela abantu bakho, ukuze uzenzele igama elizukileyo.', 'XHO22'),
(27, 63, 15, 'Khangela usemazulwini, ubone ukuba usententeni yakho engcwele enobungangamsha; kungakanani na kum ukuzondelela kwakho, namandla akho, nesandi sezibilini zakho, neenceba zakho? babanjwe?', 'XHO22'),
(27, 63, 16, 'Yena ungubawo wethu okunene, nakuba uAbraham engasazi, noSirayeli engasazi; Wena, Yehova, ungubawo, wena ungumsindisi wethu; igama lakho likho kwasephakadeni.', 'XHO22'),
(27, 63, 17, 'Yini na, Yehova, ukuba usilahlekise ezindleleni zakho, uyiqaqadekise intliziyo yethu, ukuze singakoyiki wena? Buya ngenxa yabakhonzi bakho, izizukulwana zelifa lakho.', 'XHO22'),
(27, 63, 18, 'Abantu bakho abangcwele balihlutha umzuzwana; ababandezeli bethu bayayihlambela ingcwele yakho.', 'XHO22'),
(27, 63, 19, 'Singabakho; akubalawulanga; ababizwa ngegama lakho.', 'XHO22'),
(27, 64, 1, 'Ukuba uthe wawaqhekeza amazulu, wehla, zowa iintaba phambi kwakho;', 'XHO22'),
(27, 64, 2, 'Njengokutshisa komlilo onyibilikayo, nomlilo uwatshisa amanzi; ukuze lazise igama lakho ezintshabeni zakho, zigungqe iintlanga phambi kwakho.', 'XHO22'),
(27, 64, 3, 'Ekwenzeni kwakho izinto ezoyikekayo, ebesingazilindelanga, wehla, iintaba zazamazama phambi kobuso bakho.', 'XHO22'),
(27, 64, 4, 'Kususela kwasephakadeni, abantu abevanga, abamamelanga nangendlebe, ababonanga ngeliso uThixo, ingenguwe, izinto owazilungisela abo balindele kuye.', 'XHO22'),
(27, 64, 5, 'Udibana nomntu onoyolo, esenza okusesikweni, Okukhumbulayo ezindleleni zakho; yabona, unomsindo; ngokuba soyisakele; umonde ukhona, yaye siya kusindiswa.', 'XHO22'),
(27, 64, 6, 'Ke thina sinjengento eyinqambi sonke siphela, nanjengengubo emdaka yonke ubulungisa bethu; siya kubuna sonke njengegqabi; nobugwenxa bethu busithabathe njengomoya.', 'XHO22'),
(27, 64, 7, 'Akwabakho ulinqulayo igama lakho, uziphakamisayo ukuba abambelele kuwe; ngokuba ubusithelisile ubuso bakho kuthi, wasitshisa ngobugwenxa bethu.', 'XHO22'),
(27, 64, 8, 'Kaloku ke, Yehova, ungubawo; siludongwe, yaye wena ungumenzi wethu; thina sonke singumsebenzi wesandla sakho.', 'XHO22'),
(27, 64, 9, 'Nkosi, musa ukuva ubunzima obukhulu, kwaye ungacingi ububi ngonaphakade; uyabona, siyathandaza kuwe, singabantu bakho sonke.', 'XHO22'),
(27, 64, 10, 'Imizi yobungcwele bakho iyinxuwa, iZiyon ilinxuwa, iYerusalem ilinxuwa.', 'XHO22'),
(27, 64, 11, 'Indlu yethu engcwele, entle, ababeyidumisela kuyo oobawo, iye yatshiswa ngomlilo, yaye zonke izinto esizithandayo zitshatyalalisiwe.', 'XHO22'),
(27, 64, 12, 'Yehova, ngaba uya kuwukhumbula lo mbandela? ufuna ukuthula usicinezele kakhulu?', 'XHO22'),
(27, 65, 1, 'Ndifunyenwe ngababengandibuzi; Ndazenza ndafunyanwa ngababengandifuni; Ndathi, Yabonani, ndingowohlanga olungabizwanga igama lam;', 'XHO22'),
(27, 65, 2, 'Izandla zam ndizolulela imini yonke kubantu abaziinjubaqa, abahamba ngendlela yobutshinga, abalandela ezabo iingcinga;', 'XHO22'),
(27, 65, 3, 'Luhlanga oluhlala lundigculela emehlweni am; ababingelela emiyezweni, abaqhumisela ezibingelelweni zezitena;', 'XHO22'),
(27, 65, 4, 'abo babehlala emangcwabeni nabachitha ubusuku kumatye ezikhumbuzo, besidla inyama yehagu, yaye ezityeni zabo kukho isuphu yezinto ezilizothe;', 'XHO22'),
(27, 65, 5, 'Bathi, Yima wedwa, ungasondeli kum; ngokuba ndingcwele kunani. Ngumsi empumlweni yam, ngumlilo ovutha imini yonke.', 'XHO22'),
(27, 65, 6, 'Yabona, kubhaliwe phambi kobuso bam, kwathiwa, Andiyi kuthi tu, kodwa ndiya kubabuyekeza izifuba zabo.', 'XHO22'),
(27, 65, 7, 'Ubugwenxa benu, nobugwenxa booyihlo kunye, utsho uYehova, oqhumiseleyo ezintabeni, wangcikiva ezindulini; ngako oko umsebenzi wabo wokuqala ndiya kuwulinganisela ngesifuba sabo.', 'XHO22'),
(27, 65, 8, 'Wathi uYehova, Njengokuba bufunyanwa ubulembu ngexesha elililo, athi umntu, Musa ukonakalisa; kuba ithamsanqa likuyo; Ndiya kwenjenjalo ngenxa yabakhonzi bam, ukuze ndingonakalisi bonke.', 'XHO22'),
(27, 65, 9, 'Ndiya kuvelisa imbewu kaYakobi, indlalifa yentaba yam kwaYuda; balidle ilifa abanyulwa bam, bahlale khona abakhonzi bam;', 'XHO22'),
(27, 65, 10, 'iSharon ibe likriwa, intili yeAgore ibe yindawo yemfuyo yabantu bam abandifunayo;', 'XHO22'),
(27, 65, 11, 'Ke nina nimkileyo kuYehova, nayilibalayo intaba yam engcwele, nilungisela elo qela isithebe, niselisela ngokwenani lalo.', 'XHO22'),
(27, 65, 12, 'Ngoko ke ndiya kunifundela ikrele, yaye niya kuqubuda nonke phambi kokubulawa; ngokuba ndakubizayo, anasabela; ndathetha, aneva; kodwa wenza okubi emehlweni am, wazikhethela into endingayithandiyo.', 'XHO22'),
(27, 65, 13, 'Ngako oko itsho iNkosi uYehova ukuthi, Yabonani, abakhonzi bam baya kudla, ke nina niya kulamba; yabonani, abakhonzi bam baya kusela, ke nina niya kunxanwa; yabonani, abakhonzi bam baya kuvuya, ke nina niya kudana.', 'XHO22'),
(27, 65, 14, 'Yabonani, abakhonzi bam baya kumemelela ngokuchwayitha kwentliziyo, ke nina niya kulila nixheke umphefumlo, nilile nintlungu yomphefumlo.', 'XHO22'),
(27, 65, 15, 'Shiya igama lakho libe sisiqalekiso kubanyulwa bam, kuba iNkosi enguMongami uYehova iya kunibulala ize ibize abakhonzi bayo ngelinye igama;', 'XHO22'),
(27, 65, 16, 'ukuba lowo uzisikeleleyo ehlabathini uya kuzisikelela ngoThixo oyinyaniso; yaye lowo ufungayo ehlabathini uya kufunga uThixo oyinyaniso; ngokuba zilityelwe iintlekele zangaphambili, zisithele emehlweni am.', 'XHO22'),
(27, 65, 17, 'Ngokuba, niyabona, ndidala amazulu amatsha nehlabathi elitsha; angakhunjulwa awokuqala, angenyuki athi qatha entliziyweni.', 'XHO22'),
(27, 65, 18, 'Kodwa yibani nemihlali nigcobe ngonaphakade ngenxa yoko ndikudalayo;', 'XHO22'),
(27, 65, 19, 'Ndiya kugcoba ngeYerusalem, ndigcobe ngenxa yabantu bam;', 'XHO22'),
(27, 65, 20, 'Akuyi kuba savela usana, nexhego elingayigqibanga imihla yalo; ngokuba inkwenkwe iya kufa ikwiminyaka elikhulu; ke yena umoni iminyaka elikhulu uya kuqalekiswa.', 'XHO22'),
(27, 65, 21, 'Baya kwakha izindlu, bahlale kuzo; yaye baya kutyala amasimi eediliya baze badle iziqhamo zawo.', 'XHO22'),
(27, 65, 22, 'Abayi kwakha bakha bambi; abayi kutyala mntu wumbi, badle kuwo; kuba iya kuba njengemihla yomthi imihla yabantu bam, badle into esetyenzwe zizandla zabo abanyulwa bam.', 'XHO22'),
(27, 65, 23, 'Abayi kukukhonza ilize, abayi kuzalela ukhwankqiso; kuba bayinzala yabasikelelweyo bakaYehova, nenzala yabo iya kuba kubo.', 'XHO22'),
(27, 65, 24, 'Kuya kuthi bengekabizi, ndisabele; bathi besathetha, ndive.', 'XHO22'),
(27, 65, 25, 'Kuya kudla ndawonye ingcuka nemvana, ingonyama idle utyani njengenkomo, nothuli lube kukudla kwenyoka; Aziyi kwenzakalisa okanye zonakalise yonke intaba yam engcwele, utsho uYehova.', 'XHO22'),
(27, 66, 1, 'Wathi uYehova, Amazulu ayitrone yam, ihlabathi sisihlalo seenyawo zam. Iphi na le ndlu undakhela yona? Iphi na indawo yam yokuphumla?', 'XHO22'),
(27, 66, 2, 'Ngokuba sizenzile zonke ezo zinto isandla sam, zabakho ke zonke ezo zinto, utsho uYehova; Ndiya kukhangela kulo mntu ulihlwempu, utyhafileyo emoyeni, ogubhayo ngelizwi lam.', 'XHO22'),
(27, 66, 3, 'Oxhela inkomo ufana noxhela umntu; Obingelela imvana ufana nonqumla intamo yenja; Osondeza idini lomlilo ufana nosondeza igazi lehagu; Oqhumisa isiqhumiso ufana nosikelela into ebubutshinga. Bezinyulele nje iindlela zabo, umphefumlo wabo wawananza amazothe abo:', 'XHO22'),
(27, 66, 4, 'nam ke ndozinyulela iziphoso zabo, ndibazisele ububi babo; ngokuba ndibize, akwabakho usabelayo; ndithe xa ndithetha abandimamela basuka benza okubi emehlweni am bazikhethela into endingayithandiyo.', 'XHO22'),
(27, 66, 5, 'Liveni ilizwi likaYehova, nina nigubhayo lilizwi lakhe; Oonyana benu abanithiyayo, abanigxothayo kubo ngenxa yegama lam, bathi, Makazukiswe uYehova, kodwa uya kubonakala ngenxa yovuyo lwenu, badane.', 'XHO22'),
(27, 66, 6, 'Lilizwi lesandi esiphuma esixekweni; lilizwi eliphuma etempileni; lilizwi likaYehova ophindezelayo iintshaba zakhe.', 'XHO22'),
(27, 66, 7, 'Ngaphambi kokuba azale, wazala; ingekafiki inimba yakhe, wazala umntwana oyinkwenkwe.', 'XHO22'),
(27, 66, 8, 'Ngubani na okhe weva kunjalo? Ngubani na okhe wabona izinto ezinjalo? Ngaba ihlabathi liya kuvelisa ngenye imini? Kuya kuzalwa uhlanga kwaoko? ngenxa yokuba kamsinya nje yakuba iZiyon yazala, iyazala abantwana bayo.', 'XHO22'),
(27, 66, 9, 'Ndiya kuzala na, ndingazali na? Wathi uYehova, Ndingasizalisa na, ndisivale isizalo sikanina? utsho uThixo wakho.', 'XHO22'),
(27, 66, 10, 'Vuyisanani neYerusalem, vuyani nayo, nonke bamthandayo; Vuyisanani naye, nonke nina nimzilayo;', 'XHO22'),
(27, 66, 11, 'ukuze nanye nihluthe libele lentuthuzelo yakhe; ukuze nisele ubisi nize nixhamle ubutyebi bozuko lwakhe.', 'XHO22'),
(27, 66, 12, 'Ngokuba utsho uYehova ukuthi, Yabonani, ndimnika uxolo njengomlambo, nozuko lweentlanga njengomlambo oqukuqelayo; uya kwanya khona, udle emacaleni akhe, ubethe amadolo akhe.', 'XHO22'),
(27, 66, 13, 'Njengothuthuzela unina, ndiya kwenjenjalo mna ukunithuthuzela; yaye niya kuthuthuzeleka eYerusalem.', 'XHO22'),
(27, 66, 14, 'Kuya kuthi, akukubona, ibe nemihlali intliziyo yakho, namathambo akho adubule njengesityalo; yaye isandla sikaYehova siya kwaziwa kubakhonzi bakhe, nomsindo wakhe kwiintshaba zakhe.', 'XHO22'),
(27, 66, 15, 'Kuba, yabonani, uYehova uya kuza ngomlilo, zinjengesaqhwithi iinqwelo zakhe zokulwa, ukuze anyamezele ubushushu bakhe ngomsindo, nokukhalima kwakhe ngesithombo somlilo.', 'XHO22'),
(27, 66, 16, 'Kuba uYehova uya kulwa nayo yonke inyama ngomlilo nangekrele lakhe, yaye baya kuba baninzi abo boyisiweyo nguYehova.', 'XHO22'),
(27, 66, 17, 'Abo bazingcwalisayo, bazihlambululele imiyezo, emva komthi ophakathi, badle inyama yehagu, nezinto ezinezothe, neempuku, baya kutshabalala kunye; utsho uYehova.', 'XHO22'),
(27, 66, 18, 'Ngokuba ndiyazazi izenzo zabo neengcinga zabo; Kuya kufika ndihlanganise zonke iintlanga neelwimi; kwaye baya kuza kubona ubuqaqawuli bam.', 'XHO22'),
(27, 66, 19, 'Ndiya kwenza umqondiso phakathi kwazo yaye abo basindileyo kubo ndiya kubathumela ezintlangeni, iTarshishe nePule neLude, abo bazingela ngesaphetha, iTubhali noYavan, kwiziqithi ezikude ezithe thaa akazange abe khona wakuva ukusebeza kwam, akabubona ubuqaqawuli bam; yaye baya kubuvakalisa ubungcwele bam phakathi kweentlanga.', 'XHO22'),
(27, 66, 20, 'Baya kubazisa bonke abazalwana benu, bevela ezintlangeni zonke, bekhwele emahasheni, nangeenqwelo zokulwa, nangeenqwelo zokulwa, nangeenqwelo zokulwa, nangezilwanyana ezinamendu, entabeni yam engcwele eYerusalem, utsho uYehova; umnikelo ngesitya esihlambulukileyo endlwini kaYehova.', 'XHO22'),
(27, 66, 21, 'Ndiza kubathabatha babe ngababingeleli nabaLevi,’ utsho uYehova.', 'XHO22'),
(27, 66, 22, 'Kuba kanye njengoko amazulu amatsha nomhlaba omtsha endiya kuwenza uya kuma phambi kwam,’ utsho uYehova, iya kwenjenjalo ukuma intsapho yenu negama lenu.', 'XHO22'),
(27, 66, 23, 'Ukususela kwenye inyanga ukusa kwenye, ukususela kwisabatha ukuya kwenye, yonke inyama iya kuza ukuze iqubude phambi kwam,” utsho uYehova.', 'XHO22'),
(27, 66, 24, 'Baya kuphuma babone izidumbu zamadoda angcatshiweyo; kuba iimpethu zabo aziyi kufa, nomlilo wabo ungacimi; ibe lisikizi kwinyama yonke.', 'XHO22'),
(28, 1, 1, 'Amazwi kaYeremiya unyana kaHilekiya, wakubabingeleli baseAnatoti, ezweni lakwaBhenjamin:', 'XHO22'),
(28, 1, 2, 'Kwafika ilizwi likaYehova kuye ngemihla kaYosiya, unyana ka-Amon, ukumkani wakwaYuda, ngomnyaka weshumi elinesithathu wobukumkani bakhe.', 'XHO22'),
(28, 1, 3, 'Kwathi ngemihla kaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, kwada kwaba sekupheleni konyaka weshumi elinanye kaZedekiya unyana kaYosiya, ukumkani wakwaYuda, de kwabakho ukufakwa entolongweni kweYerusalem ngenyanga yesihlanu.', 'XHO22'),
(28, 1, 4, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 1, 5, 'Ndandikwazi ndingekakudali kwasesizalweni; ungekaphumi embilinini, ndakungcwalisa, ndakumisa waba ngumprofeti weentlanga.', 'XHO22'),
(28, 1, 6, 'Ndaza ndathi: “Owu Nkosi Yehova! yabona, andikwazi ukuthetha kuba ndingumntwana.', 'XHO22'),
(28, 1, 7, 'Wathi uYehova kum, Musa ukuthi, Ndisengumntwana: uze uye kuyo yonke into endikuthuma kuyo, uthethe yonke into endikuwisela umthetho ngayo.', 'XHO22'),
(28, 1, 8, 'Musa ukuboyika, kuba ndinawe ukuba ndikusindise, utsho uYehova.', 'XHO22'),
(28, 1, 9, 'Wasolula uYehova isandla sakhe, wachukumisa umlomo wam. Wathi uYehova kum, Yabona, ndibeke amazwi am emlonyeni wakho.', 'XHO22'),
(28, 1, 10, 'Uyabona, ndikumise namhla ukuba ulawule iintlanga noorhulumente, ukuba unyothule, udilize, utshabalalise, udilize, wakhe, umise;', 'XHO22'),
(28, 1, 11, 'Kwakhona kwabonakala ilizwi likaYehova kum lisithi: “Yeremiya, ubona ntoni na? Ndathi: Ndibona intonga yomthi wom-amangile.', 'XHO22'),
(28, 1, 12, 'Wathi uYehova kum, Ubone walungisa, ngokuba ndikhawuleze ukulenza ilizwi lam.', 'XHO22'),
(28, 1, 13, 'Kwafika ilizwi likaYehova kum ngokwesibini, lisithi, Uyabona na? Ndathi, Ndibona imbiza; ubuso bakhe bubheke ngasentla.', 'XHO22'),
(28, 1, 14, 'Wathi uYehova kum, Kuya kubonakala ububi buvela ngasentla, bube phezu kwabemi bonke behlabathi.', 'XHO22'),
(28, 1, 15, 'Ngokuba, yabona, ndibiza zonke izizwe zezikumkani zasentla, utsho uYehova; ziya kuza, zimise iyileyo itrone yayo ekungeneni kwamasango aseYerusalem nasezindongeni zayo zonke ngeenxa zonke, nasezixekweni zonke zakwaYuda.', 'XHO22'),
(28, 1, 16, 'Ndiya kuzithetha kubo izigwebo zam ngenxa yobubi babo bonke, ngenxa yokuba bendishiyile baza batshisa abanye oothixo ngomsi baza baqubuda kwimisebenzi yezandla zabo.', 'XHO22'),
(28, 1, 17, 'Bhinqa isinqe sakho, ume, uthethe kubo konke endikuwisela umthetho ngako; uze ungaboyiki, hleze ndikudanise phambi kwabo.', 'XHO22'),
(28, 1, 18, 'Ngokuba, yabona, ndikwenze namhla umzi oyinqaba, intsika yesinyithi, udonga lobhedu kulo lonke ilizwe, kookumkani bakwaYuda, nakubathetheli balo, nakubabingeleli balo, nakubantu belizwe.', 'XHO22'),
(28, 1, 19, 'Kwaye baya kulwa nawe; kodwa abayi kukoyisa; ngokuba ndinawe, utsho uYehova, ukuze ndikusindise.', 'XHO22'),
(28, 2, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(28, 2, 2, 'Yiyani nixele ezindlebeni zeYerusalem, nithi, uYehova uthethile; Ndicinga ngawe, ububele bobutsha bakho, uthando lomtshato wakho, xa wawundilandela entlango, kumhlaba ongalimiyo.', 'XHO22'),
(28, 2, 3, 'Ubengcwele uSirayeli kuYehova, wentlahlela yongeniselo lwakhe; bonke abayidlayo bozeka ityala; bofikelwa bububi,” utsho uYehova.', 'XHO22'),
(28, 2, 4, 'Liveni ilizwi likaYehova, nina bendlu kaYakobi, nonke zizwe zendlu kaSirayeli;', 'XHO22'),
(28, 2, 5, 'Utsho uYehova ukuthi, Bafumana bubi buni na kum ooyihlo, bandishiyayo, balandela amampunge;', 'XHO22'),
(28, 2, 6, 'Abatshongo ukuthi, Uphi na uYehova owasikhuphayo ezweni laseYiputa, owasikhokela entlango, ezweni eliyinkqantosi, ezweni elingumqwebedu, ezweni lenxano, nelethunzi lokufa, kumhlaba omthubi apho kungadlulanga mntu, ekungazanga kuhlale mntu khona?', 'XHO22'),
(28, 2, 7, 'Ndaningenisa ezweni elichumayo, ukuba nidle iziqhamo zalo, nezinto ezilungileyo zalo; nafika nalenza inqambi ilizwe lam, nelifa lam laba lisikizi.', 'XHO22'),
(28, 2, 8, 'Ababingeleli abatshongo ukuthi, Uphi na uYehova? nababebambe umthetho abandazi;', 'XHO22'),
(28, 2, 9, 'Ngako oko, andiyi kuthetha kuni, utsho uYehova, ukuba ndilwe noonyana boonyana benu.', 'XHO22'),
(28, 2, 10, 'Kuba welani eziqithini zamaKiti, nibone; thumelani kwaKedare, nigqale, nikhangele, ukuba zikho na izinto ezinjalo na;', 'XHO22'),
(28, 2, 11, 'ukuba uhlanga lwakhe lwananisa na ngoothixo balo, bengethixo ke bona? kodwa abantu bam baluxabisile uzuko lwabo ngento engancedi nganto.', 'XHO22'),
(28, 2, 12, 'Mangaliswani, mazulu, yile nto, noyike kakhulu, nicaphuke kakhulu;', 'XHO22'),
(28, 2, 13, 'Ngokuba benze ububi obubini abantu bam; Bandishiyile mna, thende lamanzi aphilileyo; bazimbele amaqula, amaqula aqhekekileyo, abangenamanzi.', 'XHO22'),
(28, 2, 14, 'uSirayeli ungumkhonzi na? Ngaba ulikhoboka elizalelwe kuloo ndawo? yini na ukuba abhuqwe?', 'XHO22'),
(28, 2, 15, 'Iingonyama ezintsha zagquma, zakhala ngenxa yakhe, zalenza laba senkangala ilizwe lakhe; imizi yakhe yatshiswa, ayaba nabemi.', 'XHO22'),
(28, 2, 16, 'Oonyana baseNofe nabaseTahapanese nabo basiqhekeza isithsaba sakho.', 'XHO22'),
(28, 2, 17, 'Asinguwe na lo wenze le nto kuwe, ngokumfulathela uYehova uThixo wakho, oko ubuhamba ngendlela?', 'XHO22'),
(28, 2, 18, 'Unantoni na ke endleleni yaseYiputa, ukuba usele amanzi oMnayile? Okanye yintoni na enawe nendlela yaseAsiriya, ukuba usele amanzi oMlambo lowo?', 'XHO22'),
(28, 2, 19, 'Buya kukhalimela ububi bakho, bukhalimele ububi bakho; ke yazi uze ubone ukuba kubi nekrakra ukuba umnikele umva uYehova uThixo wakho yaye akukho loyiko kuwe, itsho iNkosi enguMongami uYehova wemikhosi.', 'XHO22'),
(28, 2, 20, 'Ngokuba ndiyaphule idyokhwe yakho, ndizophula izitropu zakho; wathi wena, Andiyi kutsiba; nihambahamba ezindulini zonke ezinde, naphantsi kwemithi yonke eluhlaza;', 'XHO22'),
(28, 2, 21, 'Ke mna ndikutyala umdiliya wohlobo, imbewu yenyaniso yonke; utheni na ukundenza umdiliya obolileyo wolunye uhlanga?', 'XHO22'),
(28, 2, 22, 'Nokuba uthe wahlamba ngesepha yetyuwa wathabatha isepha eninzi, kodwa ububi bakho buphambi kobuso bam,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(28, 2, 23, 'Ungathini na ukuthi, Andizenzanga nqambi, andimlandelanga uBhahali? Yibone indlela yakho emfuleni, ukwazi okwenzileyo; Uqhuba intombi enamendu, ihamba ngomkhondo wayo;', 'XHO22'),
(28, 2, 24, 'Ewe, yimazi ye-esile lasendle, eqhele intlango, ethi ngokuthanda kwayo, isezele impepho; Ngubani na onokulijika ithuba lakhe? bonke abayifunayo soze badinwe; bomfumana ngenyanga yakhe.', 'XHO22'),
(28, 2, 25, 'Luthintele unyawo lwakho lunxitywe iimbadada, nomqala wakho unganxanwa; kodwa uthe, Akukho themba kuba ndandibathanda abasemzini, ndaza ndabalandela.', 'XHO22'),
(28, 2, 26, 'Njengokudana kwesela xa lifunyenwe, kunjalo ukudana kwendlu kaSirayeli; bona, nookumkani babo, nabathetheli babo, nababingeleli babo, nabaprofeti babo;', 'XHO22'),
(28, 2, 27, 'Uthi kwimizalwane, Ungubawo; nasetyeni, Nandikhupha; ngokuba bandinikela ikhosi, abandinikelanga buso; kodwa ngexesha lokubandezeleka kwabo baya kuthi, Vuka, sisindise!', 'XHO22'),
(28, 2, 28, 'Baphi na kaloku oothixo bakho ozenzeleyo? Mabavuke ukuba abakusindisanga ngexesha lobubi bakho; ngenxa yokuba oothixo bakho balingana nenani lezixeko zakho, Yuda!', 'XHO22'),
(28, 2, 29, 'Yini na ukuba nindimangalele? Nonke nindingcatshile; utsho uYehova.', 'XHO22'),
(28, 2, 30, 'Ndifumane ndababetha oonyana benu; abavumanga kukhalinyelwa; ikrele lenu labadla abaprofeti benu njengengonyama eqwengayo.', 'XHO22'),
(28, 2, 31, 'Owu bantu, likhangeleni ilizwi likaYehova. Yintlango na kwaSirayeli? ilizwe lobumnyama? Ngako oko bathi abantu bam: Singookumkani; asiyi kuphinda size kuwe?', 'XHO22'),
(28, 2, 32, 'Iyazilibala na intombi izivatho zayo, umtshakazi uyazilibala na iingubo zakhe? ke bona abantu bam bandilindile imihla engenakubalwa.', 'XHO22'),
(28, 2, 33, 'Yini na ukuba niyeke ukufuna uthando? ngenxa yoko ubafundisa abangendawo iindlela zakho.', 'XHO22'),
(28, 2, 34, 'Kananjalo elucwecweni lwakho kwafunyanwa igazi lemiphefumlo yamahlwempu amsulwa;', 'XHO22'),
(28, 2, 35, 'Uthi ke, Ndimsulwa, wophuma kum umsindo wakhe. Yabona, ndiya kubambana nawe ngenxa yokuba usithi, Andonanga.', 'XHO22'),
(28, 2, 36, 'Yini na ukuba ungxame ukuyijika indlela yakho? yaye uya kuba neentloni ngeYiputa, kanye njengoko waba neentloni ngeAsiriya.', 'XHO22'),
(28, 2, 37, 'Ewe, phuma kuye, zibeke izandla zakho entlokweni yakho, ngokuba uYehova ulilahlile ithemba lakho, nawe akuyi kuphumelela kulo.', 'XHO22'),
(28, 3, 1, 'Babesithi, Ukuba indoda ithe yamala umfazi wayo, waza umfazi wamala, wendela kwenye indoda, inokumbuyela na loo ndoda? Ngaba elo lizwe aliyi kungcoliswa kakhulu? ke ukrexeze nezithandane ezininzi; buyela kum, utsho uYehova.', 'XHO22'),
(28, 3, 2, 'Phakamisa amehlo akho, ubone apho ungalala khona. Wahlala nabo ezindleleni, njengomArabhi entlango; yaye ulenze inqambi ihlabathi ngobuhenyu bakho nobungendawo bakho.', 'XHO22'),
(28, 3, 3, 'Zanqamka ke iimvula, akwabakho zimvula zamva; ubunebunzi lehenyukazi, akwavuma ukuba neentloni.', 'XHO22'),
(28, 3, 4, 'Awuyi kundibiza na ngoku, uthi, Bawo, usingcikivo sobutsha bam?', 'XHO22'),
(28, 3, 5, 'Uya kuwugcina na umsindo wakhe ngonaphakade? Ngaba uya kuhlala kude kube sekupheleni? Yabona, uthethe, kwaye wenze okubi kangangoko unakho.', 'XHO22'),
(28, 3, 6, 'Wathi uYehova kum ngemihla yokumkani uYosiya, Ukubonile na akwenzileyo umbandezeli kaSirayeli? enyuka zonke iintaba ezinde, naphantsi kwemithi yonke eluhlaza, wahenyuza khona.', 'XHO22'),
(28, 3, 7, 'Akwenza konke oko, ndathi, Buyela kum. Kodwa akazange abuye. Wayibona le nto uYuda, udade onemvukelo.', 'XHO22'),
(28, 3, 8, 'Ndabona, ngenxa yokukrexeza kwakhe umbandezeli kaSirayeli, ndamala, ndamnika incwadi yokwahlukana; Ke yena uYuda, umninawa wakhe, yena ongathembekanga, wasuka naye wakrexeza.', 'XHO22'),
(28, 3, 9, 'Kwathi ke ngenxa yekratshi lawo, wawenza inqambi umhlaba, wenza udaka, namatye, neentonga;', 'XHO22'),
(28, 3, 10, 'Kodwa ngenxa yoko konke oko, uYuda, umninawa wakhe, akazange abuyele kum ngentliziyo yakhe yonke; kodwa ngenkohliso, utsho uYehova.', 'XHO22'),
(28, 3, 11, 'Waza uYehova wandiphendula wathi: “uSirayeli onemvukelo ungqineke elilungisa kunoYuda ongathembekanga.', 'XHO22'),
(28, 3, 12, 'Yiya umemeze la mazwi entla, uthi, Buya, Sirayeli unezothe, utsho uYehova; andiyi kuwisa umsindo wam phezu kwakho; kuba yinceba leyo, utsho uYehova, yaye andiyi kuba nomsindo ukusa kwixesha elingenammiselo.', 'XHO22'),
(28, 3, 13, 'Buvume nje ububi bakho ngenxa yokuba nonile kuYehova uThixo wakho, wazihambisa iindlela zakho kwabasemzini, phantsi kwemithi yonke eluhlaza, analiphulaphula izwi lam; utsho uYehova.', 'XHO22'),
(28, 3, 14, 'Guqukani, nyana bangendawo, utsho uYehova; kuba ndingumtshakazi, yaye ndiya kukuthabatha wedwa esixekweni nababini entsatsheni, ndinizise eZiyon;', 'XHO22'),
(28, 3, 15, 'ndininike abalusi bentliziyo yam, banaluse ngokwazi nangengqiqo.', 'XHO22'),
(28, 3, 16, 'Kothi nanda nande ehlabathini ngelo xesha, utsho uYehova, bangabi sathi, Ityeya yomnqophiso kaYehova; kwaye ayiyi kungena kwiingcinga zabo; khumbula ukuba; kwaye abazange bayivelele; kwaye ayisayi kuphinda yenziwe.', 'XHO22'),
(28, 3, 17, 'Ngelo xesha baya kuyibiza iYerusalem ngokuba yitrone kaYehova; zibuthelane khona zonke iintlanga ngenxa yegama likaYehova eYerusalem; bangabi sahamba ngeengcinga zentliziyo yabo embi.', 'XHO22'),
(28, 3, 18, 'Ngaloo mihla baya kuhamba abantu bendlu kaYuda kunye noonyana bendlu kaSirayeli, bahlangane bevela ezweni lasentla, beze ezweni endalinika ooyihlo ukuba libe lilifa.', 'XHO22'),
(28, 3, 19, 'Ndathi ke mna, Ndothini na ukukumisa phakathi koonyana, ndikunike ilizwe elilungileyo, ilifa elilungileyo phakathi kwamatyala eentlanga? ndathi, Nindibize ngoyise; kwaye andiyi kundishiya.', 'XHO22'),
(28, 3, 20, 'Inene, kanye njengoko umfazi eyala indoda yakhe ngenkohliso, ngokunjalo nindiqhawule ngokuthembeka, nina ndlu kaSirayeli, utsho uYehova.', 'XHO22'),
(28, 3, 21, 'Kwavakala ilizwi kwiindawo eziphakamileyo, isikhalo nemithandazo yoonyana bakaSirayeli, ngenxa yokuba babephambukile endleleni yabo baza bamlibala uYehova uThixo wabo.', 'XHO22'),
(28, 3, 22, 'Buyani, nyana baneenkani, ndizinyange iimbandezelo zenu. Uyabona, siza kuwe; ngokuba wena, Yehova, unguThixo wethu.', 'XHO22'),
(28, 3, 23, 'Ngokwenene, usindiso luthenjiswe ilize ukusuka emathafeni nasezintabeni; ewe, usindiso lukaSirayeli lukuYehova uThixo wethu.', 'XHO22'),
(28, 3, 24, 'Kuba izenzo zoobawo zawugqiba kwasebuncinaneni bethu; impahla yabo emfutshane, neenkomo zabo, noonyana babo, neentombi zabo.', 'XHO22'),
(28, 3, 25, 'Silala ecukucezweni, ihlazo lethu lisibekele; kuba sonile kuYehova uThixo wethu, thina nookhokho bethu ukususela ebutsheni bethu de kube namhla, yaye asiliphulaphulanga ilizwi likaYehova uThixo wethu.', 'XHO22'),
(28, 4, 1, 'Sirayeli, ukuba uthe waguquka, utsho uYehova, zohlwaya ngenxa yam;', 'XHO22'),
(28, 4, 2, 'Fungani nithi, NgoYehova ohleliyo, ngobulungisa, nangobulungisa; zaye iintlanga ziya kuzisikelela ngaye, ziqhayise ngaye.', 'XHO22'),
(28, 4, 3, 'Kuba wathi uYehova kumadoda akwaYuda naseYerusalem, ‘Likhumbeni ilizwe elilinyiweyo, yaye ningahlwayeli emithaneni enameva.', 'XHO22'),
(28, 4, 4, 'Namathelani kuYehova, yalusani ulwaluko lwentliziyo yenu, madoda akwaYuda, bemi baseYerusalem, ukuze umsindo wam ungapheli njengomlilo, uvuthe, kungabikho uwucimayo ngenxa yobubi bezinto zenu ezimbi izenzo.', 'XHO22'),
(28, 4, 5, 'Xelela kumaYuda, uxelele iYerusalem; nithi, Vuthelani isigodlo elizweni; bizani, hlanganisani, nithi, Hlanganisani, singene emizini enqatyisiweyo.', 'XHO22'),
(28, 4, 6, 'Phakamisani ibhanile eZiyon; buyani, musani ukulinda; ngokuba entla ndizisa ububi, nembubhiso enkulu.', 'XHO22'),
(28, 4, 7, 'Ingonyama iwushiyile umngxunya wayo, kunduluke umonakalisi weentlanga; uyishiyile indawo yakhe, ukuze aliphanzise ilizwe lakho; nezixeko zakho ziya kuba ngamanxuwa, zingabi namnini.', 'XHO22'),
(28, 4, 8, 'Bhinqani ngamarhonya, nilile nisenza isijwili, ngenxa yokuba umsindo kaYehova awujikanga kuthi.', 'XHO22'),
(28, 4, 9, 'Kuya kuthi ngaloo mini, utsho uYehova, iphele intliziyo yokumkani nentliziyo yabathetheli; bathi manga ababingeleli, nabaprofeti bathi manga.', 'XHO22'),
(28, 4, 10, 'Ndathi ke, Awu, Nkosi Yehova! Ngenene ululahlekisile olu hlanga neYerusalem ngokuthi: Niya kuba noxolo; xa ikrele lifika emoyeni.', 'XHO22'),
(28, 4, 11, 'Ngelo xesha kuya kuthiwa kwaba bantu nakwiYerusalem, Umoya owomileyo encotsheni yentlango uyifikele intombi yabantu bam, ungavuthuzi, ungahlambululi;', 'XHO22'),
(28, 4, 12, 'Kwanomoya onamandla uvela kwezo ndawo, uya kundifikela; nangoku ndiya kubagweba.', 'XHO22'),
(28, 4, 13, 'Yabona, unyuka njengamafu, zinjengesaqhwithi iinqwelo zokulwa zawo; anamendu kunamaxhalanga amahashe awo. Yeha ke thina! ngokuba sibhuqiwe.', 'XHO22'),
(28, 4, 14, 'Yihlambulule ububi intliziyo yakho, Yerusalem, ukuze usindiswe. Iingcinga zakho ezingenanto ziya kuhlala ixesha elingakanani kuwe?', 'XHO22'),
(28, 4, 15, 'Kuba ilizwi liphuma kwaDan, livakalisa uyeha evela ezintabeni zakwaEfrayim.', 'XHO22'),
(28, 4, 16, 'Xela iintlanga; Yabonani, vakalisani ngeYerusalem, nithi, Abalindi beza bevela ezweni elikude, bevakalisa ilizwi labo emizini yakwaYuda.', 'XHO22'),
(28, 4, 17, 'Bachasene nayo yonke into, njengabalusi; ngokuba wakreqa kum, utsho uYehova.', 'XHO22'),
(28, 4, 18, 'Indlela yakho neentlondi zakho zakuzisela ezo zinto; bububi bakho, ngokuba bukrakra, ngokuba bufike entliziyweni yakho.', 'XHO22'),
(28, 4, 19, 'Mandla am, mathumbu am! Ndibuhlungu entliziyweni yam; Iyaxokozela intliziyo yam phakathi kwam; Andikwazi ukuthi cwaka, kuba usivile isandi sesigodlo, umoya wam, ukuhlaba umkhosi.', 'XHO22'),
(28, 4, 20, 'Kuxelwa intshabalalo kantshabalalo; ngokuba ihlabathi liphela litshabalele; ngoko nangoko iintente zam zadilizwa kunye nekhethini zam ngomzuzwana.', 'XHO22'),
(28, 4, 21, 'Kuya kude kube nini na ndibona ibhanile, ndisiva isandi sesigodlo?', 'XHO22'),
(28, 4, 22, 'Kuba bamathile abantu bam, mna abandazi; ngoonyana abangenamandla, abangenangqondo; balumkile ekwenzeni ububi, kodwa ekwenzeni okulungileyo abaqondi.', 'XHO22'),
(28, 4, 23, 'Ndalibona ihlabathi, nalo, lisenyanyeni, lisesikweni; nezulu, akwabakho kukhanya kulo.', 'XHO22'),
(28, 4, 24, 'Ndazibona iintaba, nanzo zinyikima, neenduli zonke zinyikima.', 'XHO22'),
(28, 4, 25, 'Ndakhangela, ndabona ukuba akukho mntu, neentaka zonke zezulu zibalekile.', 'XHO22'),
(28, 4, 26, 'Ndakhangela, ndaza ndabona ilizwe elityebileyo linjengentlango, nezixeko zalo zonke zazitshatyalalisiwe phambi kobuso bukaYehova nangenxa yomsindo wakhe.', 'XHO22'),
(28, 4, 27, 'Kuba utsho uYehova ukuthi: “Ilizwe lonke liya kuba yinkangala ephanzileyo; kodwa andiyi kukuphelisa.', 'XHO22'),
(28, 4, 28, 'Ngenxa yoko ihlabathi liya kukhala, libe mnyama izulu phezulu;', 'XHO22'),
(28, 4, 29, 'Isixeko sonke siyasaba ngenxa yesandi sabakhweli-mahashe nesabatoli; baya kungena emahlathini banyuke ematyeni;', 'XHO22'),
(28, 4, 30, 'Ukuba uyeba, uya kuthini na? Nokuba uthe wayambetha ingubo emfusa, nokuba uthe wahomba ngezihombo zegolide, nokuba ubuqabe ubuso bakho ngepeyinti, uya kwenzelwa ilize; abakuthandayo baya kukucekisa, bafuna umphefumlo wakho.', 'XHO22'),
(28, 4, 31, 'Ngokuba ndisivile izwi lomfazi onenimba, nenimba, kubuhlungu kuzala umntwana olizibulo, izwi lentombi enguZiyon, lidanduluka, izolulela izandla zayo, lisithi, Athi ke mna! ngokuba umphefumlo wam udinwe ngababulali.', 'XHO22'),
(28, 5, 1, 'Zityhutyheni izitrato zaseYerusalem; khanikhangele, yazini, nibugocagoce ububanzi bayo, ukuba nomfumana na, ukuba ukho na owenza okusesikweni, ofuna ukunyanisa; kwaye ndiya kukuxolela.', 'XHO22'),
(28, 5, 2, 'Nokuba bangathi: “NgoYehova ophilayo! okunene bafunga ubuxoki.', 'XHO22'),
(28, 5, 3, 'Yehova, ngaba amehlo akho akamiliselwe enyanisweni? Wababetha, ababi buhlungu; Wabatshabalalisa, abavuma ukohlwaywa; babenza lukhuni ubuso babo njengelitye; abavuma ukubuya.', 'XHO22'),
(28, 5, 4, 'Ndathi ke mna, Ndilihlwempu; baziziyatha, ngokuba abayazi indlela kaYehova, nomthetho woThixo wabo.', 'XHO22'),
(28, 5, 5, 'Ndiya kuya kumadoda amakhulu, ndithethe nawo; ngokuba bebeyazi indlela kaYehova, nomyalelo woThixo wabo; ke bona bayaphule idyokhwe, bawaqhawula amakhamandela;', 'XHO22'),
(28, 5, 6, 'Ngako oko iya kubabulala ingonyama yehlathi, ibaqweqwedise nengcuka yangokuhlwa; Ingwe yolinda imizi yabo; bonke abaphuma khona baya kuqwengwa; ngokuba buninzi ubugwenxa babo namasikizi abo anda.', 'XHO22'),
(28, 5, 7, 'Ndothini na ukukuxolela kulo mbandela? Oonyana bakho bandishiyile, bafunga abangethixo;', 'XHO22'),
(28, 5, 8, 'Babefana namahashe atyetyisiweyo kwakusasa, elowo esukela umkakhe.', 'XHO22'),
(28, 5, 9, 'Mandingaveleli na ke ngenxa yezo zinto? utsho uYehova, ungaphindezeli na ke umphefumlo wam ebantwini abanje?', 'XHO22'),
(28, 5, 10, 'Nyukani ezindongeni zayo, nonakalise; kodwa akupheli; susa amacala ayo; ngokuba àbabakaYehova.', 'XHO22'),
(28, 5, 11, 'Ngenxa yokuba inginizile indlu kaSirayeli nendlu kaYuda, utsho uYehova.', 'XHO22'),
(28, 5, 12, 'Bamkhanyela uYehova, besithi, Akakho; kwaye akukho bubi buya kusihlela; asiyi kubona krele nandlala;', 'XHO22'),
(28, 5, 13, 'abaprofeti baya kuba ngumoya, kungabikho lizwi kubo; kuya kwenziwa kubo.', 'XHO22'),
(28, 5, 14, 'Ngako oko utsho uYehova, uThixo wemikhosi, ukuthi, Ekubeni ulithethileyo elo lizwi, yabona, ndiwenza umlilo amazwi omlomo wakho, naba bantu babe ziinkuni, ubadle.', 'XHO22'),
(28, 5, 15, 'Yabonani, ndinizisela abantu bakude, nina bendlu kaSirayeli, utsho uYehova; luhlanga oluqinileyo, luhlanga oludala, luhlanga oluntetho ungayaziyo, ongayivayo ukuba luthetha ntoni na.', 'XHO22'),
(28, 5, 16, 'Indlu yabo injengengcwaba elivulekileyo, bonke bephela bangamagorha.', 'XHO22'),
(28, 5, 17, 'Luya kudla ukuvuna kwakho nesonka sakho, oluya kudliwa ngoonyana bakho neentombi zakho; liya kudla impahla yakho emfutshane neenkomo zakho; baya kudla izidiliya zakho nemikhiwane yakho; baya kuzidiliza ngekrele imizi yakho enqatyisiweyo, abakholose ngayo.', 'XHO22'),
(28, 5, 18, 'Ke ngaloo mihla, utsho uYehova, andiyi kunitshabalalisa.', 'XHO22'),
(28, 5, 19, 'Yaye ukuba nithi: “Kutheni uYehova, uThixo wethu, esenzela zonke ezi zinto? Wathi kubo, Njengokuba nindishiyile mna, nakhonza oothixo bolunye uhlanga ezweni lenu, niya kukhonza abasemzini ezweni elingelenu.', 'XHO22'),
(28, 5, 20, 'Kuxeleni kwindlu kaYakobi, nikuxelele uYuda, nithi,', 'XHO22'),
(28, 5, 21, 'Khanive, bantu bazizidenge, nani baswele ukuqonda; banamehlo, kodwa ababoni; baneendlebe, kodwa abeva;', 'XHO22'),
(28, 5, 22, 'Anindoyiki na? utsho uYehova. Aniyi kungcangcazela na phambi kwam, mna ndayibeka intlabathi elunxwemeni lolwandle, ngomthetho ongunaphakade, ukuba ningaweleki kuwo? ukuba zithe zigquma, aziyi kucanda na khona?', 'XHO22'),
(28, 5, 23, 'Ke aba bantu banentliziyo eneenkani, eneenkani; baphakame bahamba.', 'XHO22'),
(28, 5, 24, 'Bakuthi ezintliziyweni zabo, Masimoyike uYehova uThixo wethu, oninisa imvula yokuqala neyokugqibela ngexesha lakhe; Usigcinela iiveki ezimisiweyo zokuvuna.', 'XHO22'),
(28, 5, 25, 'Ubugwenxa benu bunilahlekisile kwezo zinto, nezono zenu zinilukuhlileyo kokulungileyo.', 'XHO22'),
(28, 5, 26, 'Kuba phakathi kwabantu bam kufunyanwa abantu abangendawo; babeka isibatha, babambisa abantu.', 'XHO22'),
(28, 5, 27, 'Njengendlwane ezele ziintaka, izindlu zabo zizele yinkohliso; bathi bakukhula baba zizityebi.', 'XHO22'),
(28, 5, 28, 'Bazizityebi, baqaqambileyo, ewe, bayigqithisele imisebenzi yabangendawo; kwaye abaligwebi ityala lehlwempu.', 'XHO22'),
(28, 5, 29, 'Mandingaveleli na ke ngenxa yezo zinto? Wathi uYehova: Umphefumlo wam awuyi kuziphindezela na kolo hlanga?', 'XHO22'),
(28, 5, 30, 'Kwenziwe into ekhwankqisayo neyamasikizi ehlabathini;', 'XHO22'),
(28, 5, 31, 'Abaprofeti baprofeta ubuxoki, nababingeleli banobukhosi ngamandla abo; kwaye abantu bam bangathanda ukuba kube njalo; uya kwenza ntoni na ekupheleni kwayo?', 'XHO22'),
(28, 6, 1, 'Hlabani ndawonye, nyana bakaBhenjamin, niye eYerusalem, nivuthele isigodlo eTekowa, nivuse isimangalo eBhete-hakereme; ngokuba kuvele ububi, nembubhiso enkulu, entla.', 'XHO22'),
(28, 6, 2, 'Intombi yaseZiyon ndayifanisa nenzwakazi enobubele.', 'XHO22'),
(28, 6, 3, 'Kofika abalusi nemihlambi yabo; bagxumekele iintente zabo ngeenxa zonke; bafanele bondle ngamnye kweyakhe indawo.', 'XHO22'),
(28, 6, 4, 'Yingcwaliseleni imfazwe; vuka, sinyuke emva kwemini. Yeha ke thina! kuba imini iyemka, kuba amathunzi angokuhlwa asankenteza.', 'XHO22'),
(28, 6, 5, 'Vukani, singene ebusuku, sizitshabalalise iingxande zakhe ezinde;', 'XHO22'),
(28, 6, 6, 'Ngokuba utsho uYehova wemikhosi ukuthi, Yigawuleni imithi, niyilungisele iYerusalem intaba; niyatshutshiswa phakathi kwabo nonke.', 'XHO22'),
(28, 6, 7, 'Njengokuba umthombo umpompoza amanzi alo, wenjenjalo ukumpompoza ububi balo; Kuvakala ugonyamelo nokuphanga kuyo; phambi kwam ziingxwabangxwaba namanxeba angunaphakade.', 'XHO22'),
(28, 6, 8, 'Ngamana ungaqhubeka ufundiswa, Yerusalem, ukuze umphefumlo wam ungakushiyi; ukuze ndikwenze kube senkangala, libe lilizwe elingenabemi.', 'XHO22'),
(28, 6, 9, 'Utsho uYehova wemikhosi ukuthi, Baya kuwanqumla amasalela akwaSirayeli njengomdiliya; Buyisa isandla sakho njengobutha iidiliya esixovulelweni.', 'XHO22'),
(28, 6, 10, 'Ndiya kuthetha kubani na, ndibaqononondise, beve? yabonani, azilukanga iindlebe zabo, abeva; yabona, ilizwi likaYehova lisisingcikivo kubo; abanaluvuyo kuyo.', 'XHO22'),
(28, 6, 11, 'Ngoko ndizele ngumsindo kaYehova; Ndidiniwe kukuzibamba, ndiyiphalazele phezu kwabantwana abangaphandle nebandla labafana kunye;', 'XHO22'),
(28, 6, 12, 'Ziya kugqithela kubambi izindlu zabo, namasimi abo nabafazi babo kunye; kuba ndiya kusolula isandla sam nxamnye nabemi belizwe,” utsho uYehova.', 'XHO22'),
(28, 6, 13, 'Kuthabathela kwabancinane kwesa kwabakhulu, bonke bephela babawa; baqalela kumprofeti ukuya kumbingeleli, bonke bephela benza ngenkohliso.', 'XHO22'),
(28, 6, 14, 'Baliphilisa nje kancinane inxeba lentombi yabantu bam, besithi: “Luxolo, luxolo! kungekho luxolo.', 'XHO22'),
(28, 6, 15, 'Baya kudaniswa, ngokuba benze amasikizi; ewe, babengenazintloni konke konke kwaye babengenazintloni; ke ngoko baya kuwa phakathi kwabawayo; ekubaveleleni kwam baya kuwa, utsho uYehova.', 'XHO22'),
(28, 6, 16, 'Utsho uYehova ukuthi, Yimani ezindleleni, nikhangele, nibuze ngeendlela zakudala, ukuba yiyiphi na indlela yokulungileyo, yiyani kuyo, noyifumanela ukuphumla imiphefumlo yenu; Bathi ke bona, Asiyi kucanda kuyo.', 'XHO22'),
(28, 6, 17, 'Ndabuya ndanimisela abagadi, ndathi, Liveni isandi sesigodlo; Bathi ke bona, Asiyi kuva.', 'XHO22'),
(28, 6, 18, 'Ngako oko yivani, zintlanga, wazi, wena bandla, okubahlelayo;', 'XHO22'),
(28, 6, 19, 'Yiva, hlabathi, yabona, ndisizisela intlekele esi sizwana, ewe, isiqhamo seengcinga zaso, ngenxa yokuba singawaphulaphulanga amazwi am nomthetho wam, kodwa batyekile kuwo.', 'XHO22'),
(28, 6, 20, 'Yini na ukuba kuza kum isiqhumiso esivela kwaShebha, Nengcongolo evumba limnandi, evela ezweni elikude? Akandikholisi namadini enu anyukayo, neminikelo yenu etshiswayo ayimnandi kum.', 'XHO22'),
(28, 6, 21, 'Ngako oko, utsho uYehova ukuthi, Yabona, ndibangcikiva aba bantu, bakhubeke aba ooyise noonyana, kunye; ummelwane wakhe nomhlobo wakhe baya kutshabalala.', 'XHO22'),
(28, 6, 22, 'Wathi uYehova, Yabonani, kuya kuvela uhlanga lwasentla, uhlanga olukhulu luya kubonakala ekupheleni kwehlabathi.', 'XHO22'),
(28, 6, 23, 'Baqubule isaphetha nentshuntshe; bakhohlakele, abananceba; ilizwi labo ligquma njengolwandle; yaye bakhwele emahasheni, bexhobe njengamadoda alwa nawe, ntombi yaseZiyon!', 'XHO22'),
(28, 6, 24, 'Siluvile udaba olu, lokuthi, izandla zethu zinciphekile; sicinezelekile naziintlungu njengozalayo.', 'XHO22'),
(28, 6, 25, 'Musani ukungena entsimini, ningahambi ngendlela; ngokuba ikrele lotshaba liyijikelezile;', 'XHO22'),
(28, 6, 26, 'Ntombi yabantu bam, bhinqa iingubo zokuzila, uzigalele ngothuthu; nilililele okuphela kwamzeleyo isijwili esikrakra; ngokuba ngequbuliso siya kusihlasela utshaba.', 'XHO22'),
(28, 6, 27, 'Ndikwenze inqaba nenqaba phakathi kwabantu bam, ukuze uyazi indlela yabo, ubacikide.', 'XHO22'),
(28, 6, 28, 'Bonke bephela baziinjubaqa, behamba ngongcikivo, nangobhedu nesinyithi; bonke bayatshabalalisa.', 'XHO22'),
(28, 6, 29, 'Ukufudumala kutshiswa ngumlilo, ilothe itshiswa ngumlilo; unyibilikela phantsi umnyibilikisi, ngokuba ongendawo akakhululwa ngentlawulelo.', 'XHO22'),
(28, 6, 30, 'Abantu baya kubabiza ngokuba yisilivere edelekileyo, ngenxa yokuba uYehova ebacekisile.', 'XHO22'),
(28, 7, 1, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 7, 2, 'Yima esangweni lendlu kaYehova, udanduluke khona apho, uthi, Liphulaphuleni ilizwi likaYehova, nonke nina maYuda angenayo ngezi ngcango ukuba anqule uYehova.', 'XHO22'),
(28, 7, 3, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Zilungiseni iindlela zenu nezenzo zenu, ndinibeke kule ndawo.', 'XHO22'),
(28, 7, 4, 'Musani ukukholosa ngamazwi obuxoki, athi, Yitempile kaYehova, itempile kaYehova, itempile kaYehova, yiyo leyo.', 'XHO22'),
(28, 7, 5, 'Ngokuba, ukuba nithe naziguqula iindlela zenu neentlondi zenu, ukuba nithe naziguqula ngokupheleleyo; xa ugweba ngokugqibeleleyo phakathi komntu nommelwane wakhe;', 'XHO22'),
(28, 7, 6, 'Ukuba uthe akwamcudisa owasemzini, nenkedama, nomhlolokazi, akwaphalaza igazi elimsulwa kule ndawo, akwalandela thixo bambi, ukuba kube kubi kuwe;', 'XHO22'),
(28, 7, 7, 'ndinibeke kule ndawo, ezweni endalinika ooyihlo, kude kuse ephakadeni;', 'XHO22'),
(28, 7, 8, 'Yabonani, nikholose ngamazwi obuxoki, angakuncediyo;', 'XHO22'),
(28, 7, 9, 'Ngaba niya kuba, nibulale nikrexeze nifunge ngobuxoki niqhumisele ngesiqhumiso kuBhahali nilandele abanye oothixo eningabaziyo?', 'XHO22'),
(28, 7, 10, 'Nothi ke nime ngakum kule ndlu ibizwa ngegama lam, nisithi, Sinikelwe ukuba senze onke la masikizi?', 'XHO22'),
(28, 7, 11, 'Le ndlu ibizwa ngegama lam yaba ngumqolomba wamasela na emehlweni enu? Yabonani, ndibonile; utsho uYehova.', 'XHO22'),
(28, 7, 12, 'Ngoku ke yiyani endaweni yam eShilo, apho ndilibeke khona kuqala igama lam, nize nibone oko ndakwenzayo khona ngenxa yobubi babantu bam amaSirayeli.', 'XHO22'),
(28, 7, 13, 'Kaloku ke, ngenxa yokuba niyenzile yonke le misebenzi, utsho uYehova, ndathetha kuni, ndavuka kusasa, ndathetha, anaphulaphula; ndinibizile, anandiphendula;', 'XHO22'),
(28, 7, 14, 'Ndiya kwenza kule ndlu ibizwa ngegama lam, enikholose ngayo, nakule ndawo ndaninika yona nina nooyihlo, njengoko ndenzayo kwiShilo.', 'XHO22'),
(28, 7, 15, 'Ndiya kunigxotha phambi kwam, njengoko ndabagxothayo abazalwana benu bonke, oonyana bakaEfrayim bonke.', 'XHO22'),
(28, 7, 16, 'Wena ke musa ukubathandazela aba bantu, ungaphimiseli kuhlahlamba nakuthandaza ngenxa yabo, ungandithandazi, ngokuba andiyi kuva;', 'XHO22'),
(28, 7, 17, 'Anikuboni na abakwenzayo ezixekweni zakwaYuda nasezitratweni zaseYerusalem?', 'XHO22'),
(28, 7, 18, 'Oonyana baphatha iinkuni, ooyise baphemba umlilo, abafazi baxovula intlama, ukuba enzelwe izonkana ukumkanikazi wezulu, bathululele thixweni bambi ngesiselo, ukuze bandiqumbise.', 'XHO22'),
(28, 7, 19, 'Ngaba bayandicel’ umngeni? utsho uYehova; asingabo na ababadanisayo ebusweni babo?', 'XHO22'),
(28, 7, 20, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabona, umsindo wam nobushushu bam buya kuphalazelwa kule ndawo, phezu kwabantu, naphezu kweenkomo, naphezu kwemithi yelizwe, naphezu kweziqhamo zelizwe, naphezu kweziqhamo zehlabathi; uya kuvutha, ungacimi.', 'XHO22'),
(28, 7, 21, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Zisani amadini enu anyukayo kumadini enu anyukayo, nidle inyama;', 'XHO22'),
(28, 7, 22, 'Kuba andizange ndithethe nookhokho benu yaye andizange ndibawisele umthetho ngomhla wokubakhupha kwam ezweni laseYiputa ngokuphathelele iminikelo etshiswayo okanye imibingelelo.', 'XHO22'),
(28, 7, 23, 'Nantsi into endibawisele umthetho ngayo: Phulaphulani ilizwi lam: ndoba nguThixo wenu, nina nibe ngabantu bam; hambani ngeendlela zonke endiniwisele umthetho ngazo, ukuze nibe nempumelelo.', 'XHO22'),
(28, 7, 24, 'Kodwa abazange baphulaphule yaye abazange bazinqumle iindlebe zabo, kodwa bahamba ngamacebo neengcinga zentliziyo yabo embi baza babuya umva, ababheka phambili.', 'XHO22'),
(28, 7, 25, 'Ukususela kwimini abaphuma ngayo ookhokho benu ezweni laseYiputa de kube namhla, ndibathumele kuni bonke abakhonzi bam, abaprofeti, ndivuka ekuseni yonke imihla, ndize ndibathumele kuni.', 'XHO22'),
(28, 7, 26, 'Ke abaphulaphula kum, abazinqumla iindlebe zabo, bazenza lukhuni iintamo zabo; benze okubi kunooyise.', 'XHO22'),
(28, 7, 27, 'Ke ngoko, baxelele onke la mazwi; abavuma ukukuphulaphula; kwaye ubabize; kodwa abayi kukuphendula.', 'XHO22'),
(28, 7, 28, 'Ke uya kuthi kubo, Lulo olu uhlanga olungaliphulaphuliyo ilizwi likaYehova uThixo walo, olungayivumiyo intethiso; inyaniso yalahleka kwaye yanqunyulwa emlonyeni wabo.', 'XHO22'),
(28, 7, 29, 'Cheka iinwele zakho, Yerusalem, uzilahle, uphakamise umvandedwa enyangweni; ngokuba uYehova usilahlile isizukulwana somsindo wakhe, uwujikile umhlana.', 'XHO22'),
(28, 7, 30, 'Ngokuba oonyana bakaYuda benze ububi emehlweni am, utsho uYehova; babeke izinto zabo ezinezothe endlwini ebizwa ngegama lam, ukuze bayenze inqambi.', 'XHO22'),
(28, 7, 31, 'Bakha iindawo eziphakamileyo zaseTofete, kwintlambo yonyana kaHinom, ukuze batshise oonyana babo neentombi zabo ngomlilo; into endingabawiselanga mthetho ngayo, ibingathanga qatha entliziyweni yam.', 'XHO22'),
(28, 7, 32, 'Ngako oko, uyabona, iyeza imihla, utsho uYehova, abangasayi kuba sathi yiTofete, ngumfula wakwaBhen-hinom; ngokuba baya kungcwatyelwa eTofete, ngokungabikho ndawo.', 'XHO22'),
(28, 7, 33, 'Izidumbu zaba bantu ziya kuba kukudla kweentaka zezulu nokwezilo zelizwe; kwaye akukho bani uya kubakrazula.', 'XHO22'),
(28, 7, 34, 'Ndiya kuliphelisa ilizwi lovuyo nelizwi lovuyo, ilizwi lomyeni nelizwi lomtshakazi ezixekweni zakwaYuda nasezitratweni zaseYerusalem, kuba ilizwe liya kuba yintlango.', 'XHO22'),
(28, 8, 1, 'Ngelo xesha, utsho uYehova, baya kuwakhupha emangcwabeni abo amathambo ookumkani bakwaYuda, namathambo eenkosana, namathambo ababingeleli, namathambo abaprofeti, namathambo ezizwe abemi baseYerusalem.', 'XHO22'),
(28, 8, 2, 'Ziya kuwaneka phambi kwelanga nenyanga, naphambi kwezulu lonke, ezo babezithandayo, nababezikhonza, nababezilandela, nababe befuna, baqubuda kuye; abayi kuhlanganiswa, abayi kungcwatywa; baya kuba lundwendwe emhlabeni.', 'XHO22'),
(28, 8, 3, 'Kuya kunyula ukufa phakathi kwamasalela esi sizukulwana singendawo, abaseleyo ezindaweni zonke endiwagxothele kuzo, utsho uYehova wemikhosi.', 'XHO22'),
(28, 8, 4, 'Yaye uya kuthi kubo, ‘Utsho uYehova; Ngaba baya kuwa baze bangavuki? Uya kuthi gu bucala, angabuyi na?', 'XHO22'),
(28, 8, 5, 'Kutheni aba bantu baseYerusalem bebuyela umva luwexuko lukanaphakade? bebambelele kuloo nkohliso, bayala ukubuya.', 'XHO22'),
(28, 8, 6, 'Ndabaza indlebe, ndeva, bathetha kakuhle; akukho bani uzohlwayayo ngeento zakhe ezimbi, athi, Yintoni na le ndiyenzileyo? elowo wajika indlela yakhe, ekungeneni kwehashe edabini.', 'XHO22'),
(28, 8, 7, 'Ewe, impungutye ezulwini iyawazi amaxesha ayo amisiweyo; nogwali, nogwali, nogwali, baligqale ixesha lokufika kwabo; ke bona abantu bam abalazi isiko likaYehova.', 'XHO22'),
(28, 8, 8, 'Ningathini na ukuthi, Sizizilumko, nomyalelo kaYehova unathi? Yabonani, nifumane nenze le nto; Usiba lwababhali luyinto engeyanto.', 'XHO22'),
(28, 8, 9, 'Zidanile izilumko, zisoyika, zivalelwe entolongweni; yabona, ziyalicekisa ilizwi likaYehova; Bungakanani na ke ubulumko bazo?', 'XHO22'),
(28, 8, 10, 'Ngako oko abafazi babo ndiya kubanikela kubambi, namasimi abo ndiwanike abawahluthayo; ngokuba, bethabathela komncinane besa komkhulu, bonke bephela bazinikele kwabasweleyo, bethabathela kumprofeti besa kumbingeleli, bonke bephela benza ngenkohliso.', 'XHO22'),
(28, 8, 11, 'Bakuphilisa kancinane ukwaphuka kwentombi yabantu bam, besithi, Luxolo, luxolo; kungekho luxolo.', 'XHO22'),
(28, 8, 12, 'Baya kudaniswa, ngokuba benze amasikizi; ewe, babengenazintloni konke konke kwaye babengenazintloni; ke ngoko baya kuwa phakathi kwabawayo; ekuveleni kwabo baya kutshabalala, utsho uYehova.', 'XHO22'),
(28, 8, 13, 'Ndiya kubatshabalalisa ngenene, utsho uYehova; kungabikho zidiliya emdiliyeni, kungabikho makhiwane emkhiwaneni, amagqabi aya kubuna; kwaye izinto endibanike zona ziya kudlula kubo.', 'XHO22'),
(28, 8, 14, 'Sihlaleleni na? zihlanganiseni, singene emizini enqatyisiweyo, sizole khona; ngokuba uYehova uThixo wethu usithumbile, wasiseza ngamanzi anetyhefu; ngokuba sonile kuYehova.', 'XHO22'),
(28, 8, 15, 'Besithembe uxolo, akwabakho nto ilungileyo; nangenxa yexesha lokuphiliswa, kwaye yabona, kukho ukubandezeleka!', 'XHO22'),
(28, 8, 16, 'Ukukhala kwamahashe akhe kuviwe kwaDan; ngokuba beya kulidla ihlabathi neento zonke ezikulo; isixeko nabemi baso.', 'XHO22'),
(28, 8, 17, 'Ngokuba, yabonani, ndithumela phakathi kwenu iinyoka, imilomo engenakukhafulwa, zinilume; utsho uYehova.', 'XHO22'),
(28, 8, 18, 'Ukuba bendifuna ukuzithuthuzela ngenxa yokuba buhlungu, intliziyo yam idandathekile ngaphakathi kwam.', 'XHO22'),
(28, 8, 19, 'Liveni ilizwi lesikhalo sentombi yabantu bam ngokuphathelele abo bahlala kwilizwe elikude: uYehova akakho na eZiyon? Inkosi yakhe ayinaye na? Yini na ukuba bandingcikive ngemifanekiso yabo eqingqiweyo, nangezinto ezingento zolunye uhlanga?', 'XHO22'),
(28, 8, 20, 'Ukuvuna kudlule, ihlobo lidlule, yaye asikasindiswa.', 'XHO22'),
(28, 8, 21, 'Ngokuba ndibuhlungu ngenxa yokwaphuka kwentombi yabantu bakowethu; Ndimnyama; Ndothuka.', 'XHO22'),
(28, 8, 22, 'Akukho mafutha aqholiweyo na eGiliyadi? akukho gqirha apho? Kutheni na ukuba impilo yentombi yabantu bakowethu ingalungi?', 'XHO22'),
(28, 9, 1, 'Hayi, ngeyaba manzi intloko yam, amehlo am abe lithende leenyembezi; bendikhalela ababuleweyo bentombi yabantu bakowethu imini nobusuku.', 'XHO22'),
(28, 9, 2, 'Akwaba bendinendlu yabaphambukeli entlango; Ndiya kubashiya abantu bam, ndibashiye! ngokuba bonke ngabakrexezi, libandla labahenyuzi.', 'XHO22'),
(28, 9, 3, 'Balutyedile ulwimi lwabo, njengesaphetha sabo, ukuba bathethe ubuxoki; kuba basuka ebubini baye ebubini, yaye mna abandazi,’ utsho uYehova.', 'XHO22'),
(28, 9, 4, 'Elowo makamlinde ummelwane wakhe, ningamthembi umzalwana wenu; ngokuba abazalwana benu baya kunginizela elowo, elowo ummelwane wakhe ehamba enyelisa.', 'XHO22'),
(28, 9, 5, 'Elowo uyaxoka kummelwane wakhe, abathethi nyaniso;', 'XHO22'),
(28, 9, 6, 'Ukuhlala kwakho kuphakathi kwenkohliso; ngenkohliso abavumi ukundazi; utsho uYehova.', 'XHO22'),
(28, 9, 7, 'Ngako oko, utsho uYehova wemikhosi ukuthi, Yabona, ndiyabanyibilikisa, ndibacikide; ngokuba ndiya kuthini na ngentombi yabantu bakowethu?', 'XHO22'),
(28, 9, 8, 'Lutolo olutsolileyo ulwimi lwabo; lithetha inkohliso; umntu uthetha uxolo nommelwane wakhe ngomlomo wakhe, kodwa entliziyweni yakhe ubeka umgcini wakhe.', 'XHO22'),
(28, 9, 9, 'Mandingabohlwaya na ke ngenxa yezo zinto? Wathi uYehova: Umphefumlo wam awuyi kuziphindezela na kolo hlanga?', 'XHO22'),
(28, 9, 10, 'Ndiya kuphakamisa isikhalo nesikhalo phezu kweentaba, ndikhale phezu kwamakriwa entlango, ngenxa yokuba etshisiwe, kungabikho ugqithayo kuwo; kodwa abantu abanakuliva izwi lenkomo; iintaka zezulu neenkomo zasaba; abasekho.', 'XHO22'),
(28, 9, 11, 'Ndiya kuyenza iYerusalem ibe sisikhundla seempungutye; nezixeko zakwaYuda ndiya kuzenza zibe yinkangala ephanzileyo, engenamniniyo.', 'XHO22'),
(28, 9, 12, 'Ngubani na indoda elumkileyo, ikuqonde oko? yaye ngubani na lo umlomo kaYehova uthethe naye ukuba amxelele oku, ngenxa yokuba ihlabathi litshatyalalisiwe, litshiswe njengemfumba yerhonya, ekungekho bani udlulayo kuyo?', 'XHO22'),
(28, 9, 13, 'Utsho uYehova ukuthi, Ngenxa enokuba bebuyile emyalelweni wam, endawubeka phambi kwabo, abaliphulaphula izwi lam, abahamba ngawo;', 'XHO22'),
(28, 9, 14, 'Kodwa baqhubeka behamba ngokotyekelo lwentliziyo yabo noBhahali ababefundiswe ngookhokho babo.', 'XHO22'),
(28, 9, 15, 'Ngako oko, utsho uYehova wemikhosi, uThixo kaSirayeli; Yabona, ndiyabadlisa aba bantu ingqolowa, ndibaseze inyongo;', 'XHO22'),
(28, 9, 16, 'Ndiya kubaphangalalisa phakathi kweentlanga ababengazazi bona nooyise; yaye ndiza kuthumela ikrele emva kwabo ukuze libatshabalalise.', 'XHO22'),
(28, 9, 17, 'Utsho uYehova wemikhosi ukuthi, Zigcineni, nibize iintombi ezimnyama, zize; Thumelani izilumko zize;', 'XHO22'),
(28, 9, 18, 'basililele, bahle iinyembezi amehlo ethu, atyityizele amanzi amashiya ethu.', 'XHO22'),
(28, 9, 19, 'Kuba ilizwi lokulila livakele liphuma eZiyon, lisithi: Hayi indlela esonakalisa ngayo! sidanile, ngokuba silishiyile ihlabathi, ngokuba izakhiwo zethu zisigxothile.', 'XHO22'),
(28, 9, 20, 'Ngoko ke, liphulaphuleni ilizwi likaYehova, bafazi, niphulaphule ilizwi lomlomo wakhe, nifundise iintombi zenu ingoma yesijwili, yaye ngamnye kummelwane wakhe makafundise ingoma yesijwili.', 'XHO22'),
(28, 9, 21, 'Ngokuba ukufa kungene ngeefestile zethu;', 'XHO22'),
(28, 9, 22, 'Uthi, Utsho uYehova ukuthi, Izidumbu zabantu ziya kuwa njengomgquba entsimini, nanjengomva wesandla sabavuni, kungabikho mntu uzibuthayo.', 'XHO22'),
(28, 9, 23, 'Wathi uYehova, Isilumko masingaqhayisi ngobulumko baso, igorha malingaqhayisi ngamandla alo, nesityebi masingaqhayisi ngobutyebi baso.', 'XHO22'),
(28, 9, 24, 'Oqhayisayo ke makaqhayise ngako oko, ukuze andicacisele, endazi mna ukuba ndinguYehova, owenza inceba, nesiko, nobulungisa, ehlabathini; kuba ndiyakholiswa koku,” utsho uYehova.', 'XHO22'),
(28, 9, 25, 'Yabona, iyeza imihla, utsho uYehova, endiya kubohlwaya ngayo bonke abalukileyo kwanakwabangalukileyo;', 'XHO22'),
(28, 9, 26, 'amaYiputa, nakwaYuda, namaEdom, noonyana baka-Amon, namaMowabhi, neendawo zonke ezimiyo entlango; kuba zonke ezi ntlanga azalukanga, yaye yonke indlu kaSirayeli ayilukanga ngentliziyo.', 'XHO22'),
(28, 10, 1, 'Liphulaphuleni ilizwi likaYehova, ndlu kaSirayeli;', 'XHO22'),
(28, 10, 2, 'Wathi uYehova, Musani ukufunda iindlela zeentlanga, ningayoyiki imiqondiso yezulu; ngokuba iintlanga ziya kuboyika.', 'XHO22'),
(28, 10, 3, 'Kuba azinto zanto izithethe zabantu, ekubeni umntu egawula umthi ehlathini, umsebenzi wezandla zakhe usebenza ngezembe.', 'XHO22'),
(28, 10, 4, 'Bazivathisa ngesilivere nangegolide; basibethelela ngezikhonkwane nangezando, ukuba singashukumi.', 'XHO22'),
(28, 10, 5, 'Zintle njengesundu, azithethi; kufuneka bathwale kuba abakwazi ukuhamba. Musani ukuboyika; ngokuba azinakwenza bubi, azikuboni okulungileyo.', 'XHO22'),
(28, 10, 6, 'Ngokuba akukho unjengawe, Yehova; Umkhulu wena, yaye igama lakho likhulu ngamandla.', 'XHO22'),
(28, 10, 7, 'Ngubani na ongekhe akoyike, Nkosi yezizwe? ngokuba yeyakho, ngenxa yokuba phakathi kwezilumko zonke zeentlanga nakubo bonke oorhulumente bazo akukho unjengawe.', 'XHO22'),
(28, 10, 8, 'Ke bona baziziyatha, baziziyatha; imihlambi ifunda into engento.', 'XHO22'),
(28, 10, 9, 'Iziswe isilivere ephekiweyo ivela eTarshishe, negolide evela eUfazi, njengomsebenzi wengcibi nowezandla zotoli; Yonke ke le nto yenziwa zizilumko.', 'XHO22'),
(28, 10, 10, 'Kodwa uYehova nguThixo oyinyaniso, nguThixo ophilayo, nguKumkani ongunaphakade; ihlabathi liyanyikima buburhalarhume bakhe, neentlanga aziyi kuba nako ukubuthwala ubushushu bakhe.', 'XHO22'),
(28, 10, 11, 'Uthi kubo, Bona oothixo abangalenzanga izulu nehlabathi, baya kudaka ehlabathini, phantsi kweli zulu.', 'XHO22'),
(28, 10, 12, 'Wenza elimiweyo ngamandla akhe, waliseka ngobulumko bakhe elimiweyo, waneka izulu ngengcinga yakhe.', 'XHO22'),
(28, 10, 13, 'Akulikhupha ilizwi lakhe ingxolo yamanzi ezulwini, unyusa umphunga uye esiphelweni sehlabathi; Wenza imibane emvula, urhola umoya koovimba bakhe.', 'XHO22'),
(28, 10, 14, 'Wonke umntu unesibindi ngokwazi; Wonke umkhohlisi udaniswa ngumfanekiso oqingqiweyo, ngokuba umfanekiso wakhe ubuxoki, awunamoya.', 'XHO22'),
(28, 10, 15, 'Kungamampunge nje, kungumsebenzi oyinkohliso; ngexesha lokundwendwela kwabo baya kutshabalala.', 'XHO22'),
(28, 10, 16, 'Isabelo sikaYakobi asinjengazo, ngokuba nguMenzi weento zonke yena; uSirayeli ngumsimelelo welifa lakhe; NguYehova wemikhosi igama lakhe.', 'XHO22'),
(28, 10, 17, 'Hlanganisani ubutyebi benu emaphandleni, bemi bonqaba;', 'XHO22'),
(28, 10, 18, 'Ngokuba wathi uYehova, Yabona, ndiya kubalahla abemi behlabathi, kwangelo xesha, ndibadubadube, balifumane.', 'XHO22'),
(28, 10, 19, 'Yeha ke mna, ngenxa yokwaphuka kwam! Inxeba lam libuhlungu, kodwa ndathi: Inene, kubuhlungu, ndiya kukuthwala.', 'XHO22'),
(28, 10, 20, 'Intente yam ibhuqiwe, neentambo zam zonke ziqhawukile; oonyana bam bandishiyile, abasekho; akukho wakuyimisa intente yam, waneka iingubo zam.', 'XHO22'),
(28, 10, 21, 'Kuba abalusi baba ziziyatha, abamquqela uYehova; ke ngoko, abayi kuphumelela, kwaye zonke izimvu zabo ziya kuchithwa-chithwa.', 'XHO22'),
(28, 10, 22, 'Yabonani, kuya kufika isandi seziqhamo, nengxokozelo enkulu, iphuma ezweni lasentla, ukuba imizi yakwaYuda ibe yintlango, nokuba libe liqolo leempungutye.', 'XHO22'),
(28, 10, 23, 'Ndiyazi, Yehova, ukuba ayikho semandleni akhe indlela yomntu; akukho bani uya kuzilungisa iimpahla zakhe.', 'XHO22'),
(28, 10, 24, 'Ndikhalimele, Yehova, kodwa kube ngokwesiko; kungabi ngomsindo wakho, hleze utshabalalise.', 'XHO22'),
(28, 10, 25, 'Phalaza umsindo wakho ezintlangeni ongazaziyo, naphezu kwezizwe ezingalinquliyo igama lakho; ngokuba amdlile uYakobi, amdla, amginya, nendlu yakhe yajika yaba senkangala.', 'XHO22'),
(28, 11, 1, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 11, 2, 'Waveni amazwi alo mnqophiso, nithethe kumadoda akwaYuda, nakubemi baseYerusalem;', 'XHO22'),
(28, 11, 3, 'Wathi kubo, Utsho uYehova, uThixo kaSirayeli; Uqalekisiwe umntu ongawathobeliyo amazwi alo mnqophiso;', 'XHO22'),
(28, 11, 4, 'endabawisela umthetho ngako ooyihlo, ekubakhupheni kwam ezweni laseYiputa, ezikweni lesinyithi, ndathi, Liphulaphuleni ilizwi lam, nenze njengoko ndiniwisela umthetho ngako; niya kuba ngabantu bam, mna ndibe nguThixo wenu;', 'XHO22'),
(28, 11, 5, 'ukuze ndisimise isifungo endasifungela ooyihlo, sokuba ndibanike ilizwe elibaleka amasi nobusi, njengoko kunjalo namhla. Ndaphendula ndathi, Makube njalo, Yehova!', 'XHO22'),
(28, 11, 6, 'Wathi uYehova kum, Wamemezeni onke la mazwi emizini yakwaYuda, nasezitratweni zaseYerusalem, uthi, Waveni amazwi alo mnqophiso, niwenze.', 'XHO22'),
(28, 11, 7, 'Kuba ndabakhalimela kakhulu ookhokho benu ngomhla endabakhupha ngawo ezweni laseYiputa de kwayile mini, ngoxa ndandivuka ekuseni ndaza ndabaluleka ndisithi, ‘Liphulaphuleni ilizwi likabani.', 'XHO22'),
(28, 11, 8, 'Kodwa abazange baphulaphule, abazange bazinqumle iindlebe zabo, kodwa ngamnye wahamba ngobubi bentliziyo yakhe engendawo; ke ngoko, ndiya kubazisela onke amazwi omnqophiso endibawisele umthetho wokuba bawenze; kodwa abazange.', 'XHO22'),
(28, 11, 9, 'Wathi uYehova kum, Kufunyenwe iyelenqe phakathi kwamadoda akwaYuda naphakathi kwabemi baseYerusalem.', 'XHO22'),
(28, 11, 10, 'Babuyela ezonweni zooyise, ababengavumi ukuwaphulaphula amazwi am; alandela thixo bambi ukuze babakhonze; Indlu kaSirayeli nendlu kaYuda ziwaphule umnqophiso wam endawenza nooyise;', 'XHO22'),
(28, 11, 11, 'Ngako oko utsho uYehova ukuthi, Yabona, ndibazisela ububi abangayi kuba nako ukuphuma kubo; nokuba babiza kum, andiyi kuva.', 'XHO22'),
(28, 11, 12, 'Ngoko izixeko zakwaYuda nabemi baseYerusalem baya kuhamba baze bakhale koothixo abaqhumisela kubo, kodwa abayi kubasindisa ngexesha lokubandezeleka kwabo.', 'XHO22'),
(28, 11, 13, 'Kuba oothixo bakho bangangenani lemizi yakho, Yuda; yaye ngokwenani lezitrato zaseYerusalem nazakha izibingelelo zehlazo, izibingelelo zokuqhumisela kuBhahali.', 'XHO22'),
(28, 11, 14, 'Wena ke, musa ukubathandazela aba bantu, ungaphimiseli kuhlahlamba nakuthandaza ngenxa yabo; ngokuba andiyi kuva xa bekhala kum ngeembandezelo zabo.', 'XHO22'),
(28, 11, 15, 'Yintoni na endikholisiweyo yiyo endlwini yam, ekubeni ehlazisa abaninzi, nenyama engcwele ikushiyile? ukuba wenze ububi, uya konwaba.', 'XHO22'),
(28, 11, 16, 'iNkosi ukubize ngegama, umnquma oluhlaza, obukekayo, nesiqhamo esimnandi; kwavakala isandi sengxokozelo enkulu, wafaka umlilo kuwo, aqhawuka amasebe awo.', 'XHO22'),
(28, 11, 17, 'Kuba uYehova wemikhosi, owakutyalayo, uthethe intlekele ngawe ngenxa yobubi bendlu kaSirayeli nendlu kaYuda, abazenzileyo kubo ngokundicaphukisa ngokuqhumisela kuBhahali.', 'XHO22'),
(28, 11, 18, 'uYehova wandibiza, ndaza ndazi;', 'XHO22'),
(28, 11, 19, 'Ndaba njengemvana esiwa ekuxhelweni; Bendingazi ke bona ukuba bacebe ngam, besithi, Masiwonakalisa umthi nesiqhamo sawo, siwunqumle ezweni labaphilileyo, lingabi sathiwa igama lawo.', 'XHO22'),
(28, 11, 20, 'Kodwa, Yehova wemikhosi, ogweba ngobulungisa, ozicikidayo izintso nentliziyo, mandiyibone impindezelo yakho kubo, ngenxa yokuba ndinixelele ngetyala lam.', 'XHO22'),
(28, 11, 21, 'Wathi ke uYehova ngabantu baseAnatoti, abawunqwenelayo umphefumlo wakho, abo bathi, Uze ungaprofeti egameni likaYehova, hleze sikubulale ngezandla zethu.', 'XHO22'),
(28, 11, 22, 'Ngako oko, utsho uYehova wemikhosi ukuthi, Yabonani, ndiya kubavelela; amanene aya kubulawa ngekrele; oonyana babo neentombi zabo ziya kufa yindlala;', 'XHO22'),
(28, 11, 23, 'Akuyi kubakho masalela kubo, ngenxa yokuba ndiwazisela intlekele amadoda aseAnatoti ngonyaka wokukohlwaya kwawo.', 'XHO22'),
(28, 12, 1, 'Unyanisile, Yehova, xa ndibambeneyo nawe; Andiyi kuthetha kuwe ngezigwebo zakho; kungani na ukuba indlela yabangendawo ibe nempumelelo? Kutheni bonwabile bonke abo basebenza ngenkohliso?', 'XHO22'),
(28, 12, 2, 'Ubatyalile, bendelwa neengcambu; ayahluma, ewe, axakathe isiqhamo; Ikufuphi emlonyeni wabo, ikude nezintso zabo.', 'XHO22'),
(28, 12, 3, 'Ke wena, Yehova, uyandazi; Wandibona, wayicikida intliziyo yam ngawe; bahlube njengeegusha eziya kuxhelwa; balungiselele umhla wokubulawa.', 'XHO22'),
(28, 12, 4, 'Kuya kude kube nini na ilizwe lilila, nezityalo zalo lonke ilizwe loma ngenxa yobungendawo babemi? izilwanyana neentaka ezitshabaleleyo; ngokuba bathi, Akayi kusibona isiphelo sethu.', 'XHO22'),
(28, 12, 5, 'Ukuba ugidima nabahamba ngeenyawo, bakudinisa, wothini na ukukhuphisana namahashe? yaye ukuba banihlisele ishwangusha kwilizwe loxolo enilithembileyo, niya kwenza ntoni na kumathambeka aseYordan?', 'XHO22'),
(28, 12, 6, 'Ngokuba nabazalwana bakho, nabendlu kayihlo, banginizile kuwe; ewe, bayakubiza emva kwakho; musa ukukholwa ngabo, nokuba bathetha amazwi amnandi kuwe.', 'XHO22'),
(28, 12, 7, 'Ndilishiyile ikhaya lam, ndililahlile ilifa lam; Intanda yomphefumlo wam ndiyinikele esandleni seentshaba zayo.', 'XHO22'),
(28, 12, 8, 'Ilifa lam linjengengonyama yasehlathini kum; luyandibiza; yiyo loo nto ndandiyithiya.', 'XHO22'),
(28, 12, 9, 'Ilifa lam linjengentaka edla inyama, xa lijikelezwe ziintaka ezingeenxa zonke kulo; yizani, hlanganisani onke amarhamncwa elizwe, yizani nidle.', 'XHO22'),
(28, 12, 10, 'Abalusi abaninzi basonakalisile isidiliya sam;', 'XHO22'),
(28, 12, 11, 'Balenza laba senkangala, labe liyinkangala; libharhile lonke ilizwe, ngokuba akukho mntu ukubekela ntliziyo oko.', 'XHO22'),
(28, 12, 12, 'Ababhuqi bafikelele ezindongeni zonke zentlango, ngokuba umkhonto kaYehova uya kubulala, kuthabathele esiphelweni sehlabathi kuse esiphelweni sehlabathi; akukho nyama iya kuba luxolo.', 'XHO22'),
(28, 12, 13, 'Bahlwayela ingqolowa, bavuna ameva; babandezelekile, kodwa akukho nto ilungileyo; yaye baya kuba neentloni ngemali eniyifumanayo ngenxa yokuvutha komsindo kaYehova.', 'XHO22'),
(28, 12, 14, 'Wathi uYehova ngabamelwane bam bonke abanobubi, abalichukumisayo ilifa, endilidle ilifa abantu bam amaSirayeli; Yabona, ndiyabahlangula emhlabeni wabo nendlu kaYuda ndiya kuyihlangula phakathi kwabo.', 'XHO22'),
(28, 12, 15, 'Emva kokuba ndibanyothule, ndiya kubuya, ndibe nemfesane kubo, ndibuyisele elowo elifeni lakhe, elowo emhlabeni wakhe.', 'XHO22'),
(28, 12, 16, 'Akwaba bebengazifunda iindlela zabantu bam, bafunge igama lam, bathi, Ehleli nje uYehova! njengoko babafundisayo abantu bam ukuthetha ngoBhahali; baya kwakhiwe khona phakathi kwabantu bam.', 'XHO22'),
(28, 12, 17, 'Ke ukuba bathe abeva, ndolubetha olo hlanga ngesiqalekiso, ndilutshabalalise; utsho uYehova.', 'XHO22'),
(28, 13, 1, 'Wathi uYehova kum, Yiya uzithengele ilinen emhlophe, uyibhinqe, ungayifaki emanzini.', 'XHO22'),
(28, 13, 2, 'Ngoko ndathabatha ibhanti ngokwelizwi likaYehova ndaza ndabhinqa esinqeni sam ngalo.', 'XHO22'),
(28, 13, 3, 'Laphinda lafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 13, 4, 'Thabatha umbhinqo osesinqeni sakho, uhambe uye emEfrati, uwufihle khona emhadini wengxondorha.', 'XHO22'),
(28, 13, 5, 'Ndaya ke, ndawufihla emEfrati, njengoko uYehova wandiwisela umthetho ngako.', 'XHO22'),
(28, 13, 6, 'Kwadlula imihla emininzi, wathi uYehova kum, Suka uye emEfrati, ubuthabathe ubutyebi endakuwisela umthetho ngako, ukuba uwufihle khona.', 'XHO22'),
(28, 13, 7, 'Ndaya ke emEfrati, ndemba, ndathabatha ingqolowa kuloo ndawo bendiyifihle kuyo; nalo ibhanti laphukile, akwanceda nto.', 'XHO22'),
(28, 13, 8, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 13, 9, 'Utsho uYehova ukuthi, Ndiya kuliphelisa ikratshi lakwaYuda, ikratshi elikhulu laseYerusalem.', 'XHO22'),
(28, 13, 10, 'Aba bantu bangendawo, bangavumiyo ukuweva amazwi am, bahamba ngokweminquba yentliziyo yabo, balandela thixo bambi ukuze babakhonze, babakhonze: baya kuba njengalo mbhinqo wamampunge;', 'XHO22'),
(28, 13, 11, 'Ngokuba njengokuba umbhinqo ubopha indoda esinqeni, ndenjenjalo ukuyibopha kum yonke indlu kaSirayeli, nendlu yonke kaYuda, utsho uYehova; ukuze babe ngabantu bam, negama, nendumiso, nozuko, kodwa abevanga.', 'XHO22'),
(28, 13, 12, 'Ke ngoko, baxelele eli lizwi; Utsho uYehova, uThixo kaSirayeli, ukuthi, Zonke iingqayi ziya kuzaliswa ngewayini;', 'XHO22'),
(28, 13, 13, 'Wothi ke kubo, Utsho uYehova ukuthi, Yabona, ndibazalisa ngesinxilo bonke abemi beli lizwe, nookumkani abahleli etroneni kaDavide, nababingeleli, nabaprofeti, nabo bonke abahleli eYerusalem;', 'XHO22'),
(28, 13, 14, 'ndibaqobe, ooyise noonyana kunye, utsho uYehova; andiyi kuba nanceba, andiyi kuthenga, andiyi kuba nanceba, ndibatshabalalise.', 'XHO22'),
(28, 13, 15, 'Phulaphulani niphulaphule; musani ukuzidla, ngokuba ethethile uYehova.', 'XHO22'),
(28, 13, 16, 'Mzukiseni uYehova uThixo wenu, engekadali ubumnyama, engekabetheki ezintabeni zocolothi iinyawo zenu;', 'XHO22'),
(28, 13, 17, 'Ukuba anithanga niphulaphule, wokhala umphefumlo wam entsithelweni ngenxa yoqhankqalazo lwenu; antywizise amehlo am, antywizise iinyembezi, ngenxa yokuba umhlambi kaYehova uthinjiwe.', 'XHO22'),
(28, 13, 18, 'Yithi kukumkani nakwinkosikazi, Zithobeni, hlala phantsi; ngokuba baya kuhla abathetheli bakho, isithsaba sozuko lwakho.', 'XHO22'),
(28, 13, 19, 'Imizi yasemzantsi iya kuvalwa, kungabikho uvulayo; Wonke uYuda uya kuthinjwa, asiwe ekuthinjweni ngokupheleleyo.', 'XHO22'),
(28, 13, 20, '“Phakamisa amehlo akho, ukhangele kwabo bangasentla: Uphi na umhlambi ozinikiweyo, umhlambi wakho omhle?', 'XHO22'),
(28, 13, 21, 'Wothini na, xa athe wakubetha? ngokuba wabafundisa ukuba babe ngabalawuli nabaphathi bakho;', 'XHO22'),
(28, 13, 22, 'Naxa uthe entliziyweni yakho, Kungani na ukuba ndifikelwe zezi zinto? Ngenxa yobukhulu bobugwenxa bakho, zihlutywe iingubo zakho, zihlutywe izithende zakho.', 'XHO22'),
(28, 13, 23, 'UmKushi unokuluguqula na ulusu lwakhe, nengwe inokuwaguqula na amabala ayo? kulapho ke niyakwazi ukwenza okulungileyo, nina baqhele ukwenza okubi.', 'XHO22'),
(28, 13, 24, 'Ndiya kubaphangalalisa ke, njengeendiza eziphephuka nomoya wentlango;', 'XHO22'),
(28, 13, 25, 'Sisabelo sakho eso, isabelo sakho somlinganiso ophuma kum, utsho uYehova; ngokuba undilibele, wakholosa ngobuxoki.', 'XHO22'),
(28, 13, 26, 'Ngoko ke ndiya kukuhluba iingubo zakho, zingabikho ebusweni bakho, kubonakale ukucukucezwa kwakho.', 'XHO22'),
(28, 13, 27, 'Ndakubona ukukrexeza kwakho, ndakuthoba, elihlazo lobuhenyu bakho, nezothe zakho ezindulini zelizwe. Yeha, wena Yerusalem! Aniyi kuhlanjululwa na? iyakuba nini?', 'XHO22'),
(28, 14, 1, 'Ilizwi likaYehova elafika kuYeremiya ngokuphathelele ubuthathaka.', 'XHO22'),
(28, 14, 2, 'uYuda uyalila, namasango akhe adandathekile; ezimnyama emhlabeni; kwenyuka ukukhala kweYerusalem.', 'XHO22'),
(28, 14, 3, 'Iingangamsha zabo zithuma abantwana bazo emanzini; bafika emhadini, abafumana manzi; babuya zilambatha izitya zabo; badanile, baneentloni, bagqubuthele intloko.', 'XHO22'),
(28, 14, 4, 'Ngenxa yokuba umhlaba uqhekekile, kuba kungabangakho mvula ehlabathini, abalimi badanile, bagqubuthele intloko.', 'XHO22'),
(28, 14, 5, 'Nembande yazala endle, yawushiya, ngokuba kwakungekho luhlaza.', 'XHO22'),
(28, 14, 6, 'Ema ezigangeni amahashe asendle, awuthi cwaka njengoodyakalashe; amehlo aso ayengaboni, kuba kwakungekho ngca.', 'XHO22'),
(28, 14, 7, 'Wena, Yehova, ukuba senze okungekho sikweni, kwenze ngenxa yegama lakho; ngokuba zininzi iimbandezelo zethu; sonile kuwe.', 'XHO22'),
(28, 14, 8, 'Wena themba likaSirayeli, uMsindisi wakhe ngexesha lentlekele, kutheni na ukuba ube njengomphambukeli elizweni nanjengowasemzini olala ebuthongweni?', 'XHO22'),
(28, 14, 9, 'Yini na ukuba ube njengendoda ekhwankqisayo, igorha elingenakusindisa; ke wena, Yehova, uphakathi kwethu, negama lakho libizwe phakathi kwethu; ungasishiyi.', 'XHO22'),
(28, 14, 10, 'Wathi uYehova kolu hlanga, Babethanda ukulahleka, abazinqanda iinyawo zabo; ke ngoko, uYehova akakholiswa ngabo; ngoku niya kubukhumbula ububi babo, nizohlwaye ngenxa yezono zabo;', 'XHO22'),
(28, 14, 11, 'Wathi uYehova kum, Musa ukuluthandazela olu hlanga;', 'XHO22'),
(28, 14, 12, 'Ukuba bathe bazila ukudla, andiyi kuva ukuhlahlamba kwabo; nokuba bazisa idini elinyukayo nedini elinyukayo, andiyi kukholiswa ngabo; kodwa ndiya kubatshabalalisa ngekrele nangendlala nangendlala yokugula.', 'XHO22'),
(28, 14, 13, 'Ndaza ndathi: “Owu Nkosi Yehova! yabonani, abaprofeti bathi kubo, Aniyi kulibona ikrele, aniyi kulamba; ndiya kuninika uxolo oluqinileyo kule ndawo.', 'XHO22'),
(28, 14, 14, 'Wathi uYehova kum, Abaprofeti baprofeta ubuxoki egameni lam; andibathumanga, andibawiselanga mthetho, andithethanga kubo; ezintliziyweni zabo.', 'XHO22'),
(28, 14, 15, 'Wathi ke uYehova kubaprofeti abaprofeta egameni lam, andibathumanga, ndathi, Akuyi kubakho mkhonto ungapheleyo kweli lizwe; Abo baprofeti baya kutshatyalaliswa likrele nayindlala.', 'XHO22'),
(28, 14, 16, 'Nabantu abaprofetayo baya kukhutshelwa phandle ezitratweni zaseYerusalem ngenxa yendlala nekrele; bangabangcwabi bona, nabafazi babo, noonyana babo, neentombi zabo; ngokuba ndiya kuthulula ubugwenxa babo phezu kwabo.', 'XHO22'),
(28, 14, 17, 'Ke ngoko, baxelele eli lizwi; Amehlo am akasayi kuphalala iinyembezi ubusuku nemini, angaphezi;', 'XHO22'),
(28, 14, 18, 'Ndathi ndakuphuma ndaya entsimini, wabona abo boyisiweyo likrele; ndithi ndakungena kuwo umzi, ndibone abafa yindlala! Ewe, umprofeti nombingeleli balahlekile kwihlabathi abangalaziyo.', 'XHO22'),
(28, 14, 19, 'Umlahle kwaphela na uYuda? Wayithiya na iZiyon umphefumlo wakho? Usibethile na, kungabikho kuphiliswa? Sithembe uxolo, akulungile; nangenxa yexesha lokuphiliswa, kodwa, khangela!', 'XHO22'),
(28, 14, 20, 'Yehova, siyabazi ububi bethu, nokungendawo koobawo; ngokuba sonile kuwe.', 'XHO22'),
(28, 14, 21, 'Musa ukuba lisikizi kuthi, ngenxa yegama lakho; ungayideli itrone yozuko lwakho; Khumbula, ungawaphuli umnqophiso wakho nathi.', 'XHO22'),
(28, 14, 22, 'Phakathi kweentlanga ezingento, ngaba ukho na onokunisa imvula? Ngaba isibhakabhaka siyana? Asinguwe na, Yehova, Thixo wethu? ke ngoko siya kukulinda, ngokuba nguwe ozenzileyo ezi zinto.', 'XHO22'),
(28, 15, 1, 'Wathi ke uYehova kum, Kungafanelana kumi ooMoses noSamuweli phambi kwam, ubungebheki oko iingcinga zam kwaba bantu; zisuse phambi kwam, uhambe.', 'XHO22'),
(28, 15, 2, 'Kuya kuthi, xa bathe kuwe, Siya ngaphi na? uthi kubo, uYehova uthethile; Abo bafanelwe kukufa baya ekufeni; abafanele umkhonto mababe nekrele; Abo bafanelwe yindlala, mabalambe; nabo bafanele ukuthinjwa, mabahambe baye ekuthinjweni.', 'XHO22'),
(28, 15, 3, 'Ndiya kubavelisela iintlobo ezine, utsho uYehova: umkhonto wokubulala, nezinja ukuba zidle, neentaka zezulu namarhamncwa, ukuba zidle, zonakalise.', 'XHO22'),
(28, 15, 4, 'Ndobasusa ezikumkanini zonke zehlabathi, ngenxa kaManase unyana kaHezekiya, ukumkani wakwaYuda, ngenxa yoko wakwenzayo eYerusalem.', 'XHO22'),
(28, 15, 5, 'Ngokuba ngubani na oya kuba nenceba kuwe, Yerusalem? ngubani na oya kunililela? ngubani na oya kuphuma akubuze ukuba unjani na?', 'XHO22'),
(28, 15, 6, 'Undishiyile, utsho uYehova, ubuyile; ke ngoko, ndiya kusolula isandla sam phezu kwakho, ndikutshabalalise; Ndidiniwe kukubhaka.', 'XHO22'),
(28, 15, 7, 'Ndiya kubahluza emasangweni elimiweyo; Ndiya kubaphanga oonyana babo, ndibatshabalalise abantu bam, ngokuba abaguqukanga ezindleleni zabo.', 'XHO22'),
(28, 15, 8, 'Abahlolokazi babo baninzi kum kunentlabathi yolwandle; Ngokukhawuleza ndamjula phezu kwayo kunye noloyiko phezu komzi.', 'XHO22'),
(28, 15, 9, 'Lowo wazala abasixhenxe akasekho; wawuncama umoya; latshona ilanga lakhe phambi kwemini enkulu; waphakama eneentloni, waphakama; nentsalela yabo ndiya kuyinikela ekreleni phambi kweentshaba zabo,” utsho uYehova.', 'XHO22'),
(28, 15, 10, 'Yeha ke mna, Mama, ngenxa yokuba undenze umntu wengxabano, umntu wengxabano nehlabathi liphela! andibolekisanga nanzala, yaye abantu babengandiboleki nanzala; kodwa bonke bephela bandingcikiva.', 'XHO22'),
(28, 15, 11, 'Wathi uYehova, Okunene, kuya kuba kuhle okuseleyo kwakho; utshaba lwakho ndiya kulubuza ngexesha lobubi nangexesha lembandezelo.', 'XHO22'),
(28, 15, 12, 'Ngaba intsimbi iya kwaphula intsimbi yasentla nentsimbi?', 'XHO22'),
(28, 15, 13, 'Ubuncwane bakho nobuncwane bakho ndobunikela ukuba buphangwe, nangenxa yezono zakho zonke, nangenxa yamazwe akho onke.', 'XHO22'),
(28, 15, 14, 'Ndiya kukuwelisa neentshaba zakho, uye ezweni ongalaziyo, ngokuba umlilo ophenjwe ngumsindo wam uya kukutshisa.', 'XHO22'),
(28, 15, 15, 'Wena, Yehova, uyakwazi oku; ndikhumbule, ndivelele, baxelele abandisukelayo; musa ukundisusa ekunyamezeleni kwakho; yazi ukuba ndohlwaywa ngawe.', 'XHO22'),
(28, 15, 16, 'Afumaneka amazwi akho, ndawadla; Ilizwi lakho kum laba yimihlali nemivuyo yentliziyo yam, ngokuba ndabizwa ngegama lakho, Yehova, Thixo wemikhosi.', 'XHO22'),
(28, 15, 17, 'Andihlalanga phakathi kwebandla labagxeki, andikonwabanga; Ndindedwa ngenxa yesandla sakho, ngokuba undizalisile ngomsindo.', 'XHO22'),
(28, 15, 18, 'Yini na ukuba umvandedwa wam uphele, nenxeba lam libulale, lingavumi ukuphola? Ngaba uya kuba njengexoki kum na njengamanzi aqukuqelayo?', 'XHO22'),
(28, 15, 19, 'Ngako oko, utsho uYehova ukuthi, Ukuba uthe waguquka, ndokubuyisa, ubuye ume phambi kwam; kodwa aniguquki kubo.', 'XHO22'),
(28, 15, 20, 'Ndiya kubenzela udonga aba bantu benziwe iinqaba zobhedu; baya kulwa nawe, bangabi nakukweyisa; kuba ndinawe ukuba ndikuhlangule ndize ndikusindise,” utsho uYehova.', 'XHO22'),
(28, 15, 21, 'Ndiya kukuhlangula esandleni songendawo, ndikuhlangule esandleni sowomeleleyo.', 'XHO22'),
(28, 16, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(28, 16, 2, 'Uze ungazeki mfazi, ungabi nanyana nazintombi kule ndawo.', 'XHO22'),
(28, 16, 3, 'Ngokuba utsho uYehova ngoonyana neentombi ezizalelwa kule ndawo, nangoonina ababazalayo, nangooyise ababazalela kweli lizwe, ukuthi,', 'XHO22'),
(28, 16, 4, 'Baya kufa ukufa okubuhlungu; abayi kulila; kwaye abayi kungcwatywa; kwaye baya kuba njengemvukelo ehlabathini, kwaye baya kutshatyalaliswa likrele nendlala; izidumbu zabo zibe kukudla kweentaka zezulu nokwamarhamncwa.', 'XHO22'),
(28, 16, 5, 'Ngokuba wathi uYehova, Musani ukungena endlwini yobumnyama, musani ukuya kumbambazela, musani ukubalilela; ngokuba ndilususile kwaba bantu uxolo lwam, inceba nemfesane yam;', 'XHO22'),
(28, 16, 6, 'Kuya kufa abakhulu nabancinane kweli lizwe; abayi kungcwatywa, abayi kumbambazela, bengayi kuzicenta, bangazihlubi ngenxa yabo.', 'XHO22'),
(28, 16, 7, 'Ke yena umntu akayi kuzikrazula ngenxa yesijwili sakhe, ukuze abathuthuzele ngenxa yofileyo; yaye akukho bani ubanika indebe yentuthuzelo ukuba bayisele ngenxa kayise nonina.', 'XHO22'),
(28, 16, 8, 'Uze ungangeni endlwini yomthendeleko, ukuba uhlale nabo, udle, usele.', 'XHO22'),
(28, 16, 9, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, ndiya kuyithumela kule ndawo phambi kwamehlo enu nangexesha lenu izwi lemihlali nezwi lemivuyo, izwi lomyeni nezwi lomyeni; umyeni khawume umtshakazi.', 'XHO22'),
(28, 16, 10, 'Xa uthe wawathetha kwaba bantu la mazwi onke, baza bathi kuwe, Yini na ukuba uYehova athethe obu bubi bukhulu ngathi ngathi? Buyini na ubugwenxa bethu? Siyini na isono sethu esone ngaso kuYehova uThixo wethu?', 'XHO22'),
(28, 16, 11, 'Uze uthi kubo, Kungenxa yokuba ooyihlo bendishiyile, utsho uYehova; balandela thixo bambi, babakhonza, babanqula, bandinikela ikhosi, abandigcina umthetho;', 'XHO22'),
(28, 16, 12, 'Nenza okubi ngaphezu kooyihlo; kuba, yabonani, elowo kuni uhamba ngobubi bentliziyo yakhe enobubi, ukuze bangandiva.', 'XHO22'),
(28, 16, 13, 'Ndiya kunilahla kweli lizwe, ndinise ezweni eningalaziyo, nina nooyihlo; bakhonze thixo bambi apho ubusuku nemini; andiyi kuba nanceba kuwe khona.', 'XHO22'),
(28, 16, 14, 'Ngako oko, utsho uYehova, iyeza imihla ekungayi kuba sathiwa, Ngeva iNkosi, owabakhuphayo oonyana bakaSirayeli ezweni laseYiputa;', 'XHO22'),
(28, 16, 15, 'ehleli nje uYehova, owabanyusayo oonyana bakaSirayeli ezweni lasentla, nasemazweni onke awawagxothela kuwo; yaye ndiza kubabuyisela kwilizwe labo endalinika ookhokho babo.', 'XHO22'),
(28, 16, 16, 'Yabonani, ndithumela ababambisi beentlanzi abaninzi, beze bababambise, utsho uYehova; emva koko ndizise abazingeli abaninzi, beze babazingele ezintabeni zonke, nasezindulini zonke, nasezimfanteni zeengxondorha.', 'XHO22'),
(28, 16, 17, 'Ngokuba amehlo am azibonile zonke iindlela zabo; azisithele kum, nenkohlakalo yazo ayifihlakele kwamehlo am.', 'XHO22'),
(28, 16, 18, 'Kwaye ndiya kuqala ngokububuyekeza ububi babo nesono sabo kabini; ngenxa yokuba belenze inqambi ilizwe lam, belizalisa ilifa lam ngezidumbu zabo ezinezothe.', 'XHO22'),
(28, 16, 19, 'Yehova, mandla am, nqaba yam, ndawo yokusabela kwam ngomhla weshwangusha, ziya kuza kuwe iintlanga, zivela eziphelweni zehlabathi, zisithi, Oobawo badla ilifa lobuxoki, nenkohlakalo, nento engento.', 'XHO22'),
(28, 16, 20, 'Umntu uya kuzenzela oothixo yini na, ongenguye oothixo?', 'XHO22'),
(28, 16, 21, 'Ngako oko, yabonani, ndiya kubazisa, ndibazise isandla sam namandla am; zazi ukuba igama lam linguYehova.', 'XHO22'),
(28, 17, 1, 'Isono sikaYuda sibhalwe ngenkxola yesinyithi, nencam yedayimani; kubhaliwe ecwecweni lentliziyo yabo, nasezimpondweni zezibingelelo zenu;', 'XHO22'),
(28, 17, 2, 'oonyana babo bezikhumbula izibingelelo zabo, nezibingelelo zabo ngasemithini eluhlaza, ezindulini ezinde.', 'XHO22'),
(28, 17, 3, 'Wena, ntaba yam yelizwe, ndiya kuyinikela imfuyo yakho nobuncwane bakho bonke ukuba buthinjwe, nezakhiwo zakho eziphakamileyo zibhukuqwe emazweni akho onke.', 'XHO22'),
(28, 17, 4, 'Uya kuliyeka nawe ilifa lakho endakunika lona; ndikwenze ukuba ukhonze iintshaba zakho ezweni ongalaziyo; ngokuba uwuvuthe ngomsindo wam, uya kuvutha ngonaphakade.', 'XHO22'),
(28, 17, 5, 'Utsho uYehova ukuthi, Uqalekisiwe umntu okholose ngomntu, owenza inyama ngesandla, ontliziyo imkayoyo kuYehova;', 'XHO22'),
(28, 17, 6, 'Ngenxa yokuba uya kuba njengozwilakhe entlango, angayiboni xa kufika okulungileyo; uya kuhlala ezindaweni ezomileyo entlango, ezweni letyuwa elingenammi.', 'XHO22'),
(28, 17, 7, 'Hayi, uyolo lomntu okholose ngoYehova, Okholose ngoYehova!', 'XHO22'),
(28, 17, 8, 'Kuba uya kuba njengomthi otyalwe ngasemanzini nonabisa iingcambu zawo ngasemlanjeni, yaye akayi kubona xa kufika ubushushu, kodwa amagqabi awo aya kuba luhlaza; kwaye ayiyi kuphela ngonyaka wembalela, ingayeki ukuthwala isiqhamo.', 'XHO22'),
(28, 17, 9, 'Intliziyo inenkohliso ngaphezu kweento zonke, nentliziyo inenkohliso; ngubani na onokuyazi?', 'XHO22'),
(28, 17, 10, 'Mna, Yehova, ndiyayicikida intliziyo, ndiyazicikida izintso, ukuze ndinike elowo ngokwesiko lakhe, ngokwesiqhamo seentlondi zakhe.', 'XHO22'),
(28, 17, 11, 'Njengonomyayi ohleli phezu kwamaqanda, akawaqandusela; ngako oko, osisityebi, engengobulungisa, uya kubushiya phakathi kwemihla yakhe, aze ekugqibeleni abe sisidenge.', 'XHO22'),
(28, 17, 12, 'Itrone leyo ibiphezulu kwasekuqalekeni, indawo yengcwele yethu;', 'XHO22'),
(28, 17, 13, 'Themba likaSirayeli, Yehova, bonke abatyekayo kuwe, baya kudana, nabandishiyileyo, babhalwe phantsi, ngokuba bemshiyile uYehova, umthombo wamanzi aphilileyo.', 'XHO22'),
(28, 17, 14, 'Ndiphilise, Yehova, ndiphile; ndisindise, ndisinde, ngokuba indumiso yam nguwe.', 'XHO22'),
(28, 17, 15, 'Yabona, bathi kum, Liphi na ilizwi likaYehova? mayize ngoku.', 'XHO22'),
(28, 17, 16, 'Mna ke, andikusukelanga ukuba ngumalusi emva kwakho; uyazi: into egqithileyo emlonyeni wam ibiphambi kwakho.', 'XHO22'),
(28, 17, 17, 'Musa ukundothusa: ulithemba lam ngemini yembandezelo.', 'XHO22'),
(28, 17, 18, 'Mabadane abandisukelayo, andiyi kudana; mabeme ngoloyiko, andiyi koyika; bazizisele imini yeshwangusha, ubatshabalalise ngobubi obuphindiweyo.', 'XHO22'),
(28, 17, 19, 'Wathi uYehova kum, Hamba uye kuma esangweni loonyana bohlanga, abangena ngalo ookumkani bakwaYuda, bephuma, nasemasangweni onke aseYerusalem;', 'XHO22'),
(28, 17, 20, 'Wathi kubo, Liphulaphuleni ilizwi likaYehova, nina kumkani bakwaYuda, nani maYuda nonke, nani nonke bemi baseYerusalem, bangenayo ngala masango.', 'XHO22'),
(28, 17, 21, 'Utsho uYehova ukuthi, Zigcineni, ningathwali mthwalo ngomhla wesabatha, ningangeni ngamasango aseYerusalem;', 'XHO22'),
(28, 17, 22, 'Ngomhla wesabatha ize ningawukhuphi umthwalo ezindlwini zenu, ningenzi namnye umsebenzi ngawo; niwungcwalise umhla wesabatha, njengoko ndabawiselayo umthetho ooyihlo.', 'XHO22'),
(28, 17, 23, 'Kodwa abazange baphulaphule, yaye abazange bazinqumle iindlebe zabo, kodwa bazenza lukhuni iintamo zabo ukuze bangeva okanye bangavumi ukuyalelwa.', 'XHO22'),
(28, 17, 24, '“Ukuba nithe naphulaphula ngenyameko, utsho uYehova, ningangeni nanye into emasangweni esi sixeko ngomhla wesabatha, kodwa ukuze nilungcwalise umhla wesabatha, ningenzi namnye umsebenzi kuni;', 'XHO22'),
(28, 17, 25, 'Kuya kwandula ke emasangweni esi sixeko kungene ookumkani neenkosana ezihleli etroneni kaDavide, zikhwele iinqwelo namahashe, zona neenkosana zazo, amadoda akwaYuda nabemi baseYerusalem; kwaye esi sixeko siya kuhlala ngonaphakade.', 'XHO22'),
(28, 17, 26, 'Baya kuza bevela emizini yakwaYuda, ezindaweni ezingeenxa zonke eYerusalem, ezweni lakwaBhenjamin, entlango, ezintabeni, nasezantsi, bezisa amadini anyukayo, nemibingelelo, nemibingelelo, neziqhumiso, nemibingelelo yoxolo dumisani endlwini kaYehova.', 'XHO22'),
(28, 17, 27, 'Ukuba anithanga nindiphulaphule, ukuba niwungcwalise umhla wesabatha, anathwalisa mthwalo, ningangeni ngamasango aseYerusalem ngomhla wesabatha; ndiya kuphemba umlilo emasangweni ayo, utshise iingxande ezinde zeYerusalem, ongacimiyo.', 'XHO22'),
(28, 18, 1, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 18, 2, 'Suka uhle, uye endlwini yombumbi, ndiya kukwazisa khona amazwi am.', 'XHO22'),
(28, 18, 3, 'Ndehla ke, ndaya endlwini yombumbi, ndambona esenza umsebenzi weevili ezijikelezayo.', 'XHO22'),
(28, 18, 4, 'Sachithwa isitya abesenza ngodongwe, esandleni sombumbi; ke ngoko, wenza enye into, njengoko kwakuthandeka emehlweni ombumbi, ukuba ayenze.', 'XHO22'),
(28, 18, 5, 'Kwabonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 18, 6, 'Andithi na nina, ndlu kaSirayeli, ndinimise njengolo mbumbi? utsho uYehova. Yabonani, njengodongwe esandleni somenzi, ninjalo nina esandleni sam, nina ndlu kaSirayeli!', 'XHO22'),
(28, 18, 7, 'Kunini na ndithetha ngohlanga nobukumkani, ukuba ndibutshabalalise, ndibutshabalalise?', 'XHO22'),
(28, 18, 8, 'Ukuba abantu endibathethe nxamnye nabo bayaguquka kubungendawo babo, nam ndiya kuzohlwaya ngenxa yobubi ebendicinga ukubenza kubo.', 'XHO22'),
(28, 18, 9, 'Ngelo xesha ndithetha ngohlanga nobukumkani, ukuba ndilwakhe, ndibuqinise;', 'XHO22'),
(28, 18, 10, 'Ukuba bathe benza ububi emehlweni am, abaliphulaphula ilizwi lam, ndozohlwaya ngenxa yokulunga, ndathi, ndobabonisa kona.', 'XHO22'),
(28, 18, 11, 'Hamba ngoku, uthethe kumadoda akwaYuda, nakubemi baseYerusalem, uthi, uYehova uthethile; Yabonani, ndinicingela ububi, ndinicingela okulungileyo; Yisuseni elowo endleleni yakhe egwenxa, nizilungise iindlela zenu nezenzo zenu, ukuze zilunge.', 'XHO22'),
(28, 18, 12, 'Bathi bona, Akukho themba, ngokuba siya kuhamba ngokwecebo lethu, elowo enze amacebo entliziyo yakhe embi.', 'XHO22'),
(28, 18, 13, 'Ngenxa yoko, wathi uYehova, Buzani ezintlangeni, nivile na into enjengale: Intombi enyulu yakwaSirayeli yenze into ecekisekayo kunene.', 'XHO22'),
(28, 18, 14, 'Ikhephu laseLebhanon liya kulishiya na iliwa lelizwe? okanye amanzi abandayo aphuma komnye ummandla ashiywe?', 'XHO22'),
(28, 18, 15, 'Ngokuba bendishiyile abantu bam;', 'XHO22'),
(28, 18, 16, 'ukwenza ihlabathi labo libe yinkangala nentsebenziswano engenasiphelo; bonke abadlulayo baya kuma bemangalisiwe, bahlunguzele iintloko.', 'XHO22'),
(28, 18, 17, 'Njengomoya wasempumalanga, ndobaphangalalisa phambi kweentshaba zabo; ndisuse ubuso babo, ndingabi savela ebusweni babo, ngemini yobubi babo.', 'XHO22'),
(28, 18, 18, 'Bathi ke, Yiza uhlabele uYeremiya; ngokuba awuyi kuphela umyalelo kumbingeleli, necebo lesilumko, nelizwi lomprofeti yizani simbethe ngolwimi, singaliphulaphuli ilizwi lakhe.', 'XHO22'),
(28, 18, 19, 'Ndiphendule, Yehova, uliphulaphule izwi lababambene nam.', 'XHO22'),
(28, 18, 20, 'Kuya kuvuzwa ngobubi na ngokulungileyo? ngokuba bewumbele isihogo umphefumlo wam. Khumbula ukuma kwam phambi kwakho ukuba ndithethe kakuhle ngabo, ndiwubuyise umsindo wakho kubo.', 'XHO22'),
(28, 18, 21, 'Ngako oko banikele oonyana babo endlaleni, igazi labo ulinikele kugonyamelo lwekrele; abafazi babo bangabi nabantwana, babe ngabahlolokazi; amadoda abo makabulawe; abafana babo babulawa ngekrele emfazweni.', 'XHO22'),
(28, 18, 22, 'Makuvakale ukukhala ezindlwini zabo, xa ubazisela ibandla ngesiquphe; ngokuba bambe umngxuma wokundibambisa, iinyawo zam bazifihle esibatheni.', 'XHO22'),
(28, 18, 23, 'Kodwa, Yehova, uyawazi onke amacebo abo okundibulala; musa ukubuxolela ubugwenxa babo, ungasicimi isono sabo ebusweni bakho, kodwa bavumele baguquke phambi kwakho; wenjenjalo kubo ngexesha lomsindo wakho.', 'XHO22'),
(28, 19, 1, 'Wathi uYehova, Yiya uthabathe imbiza yodongwe kumbumbi, uwuthabathe emadodeni amakhulu abantu, nakumadoda amakhulu ababingeleli;', 'XHO22'),
(28, 19, 2, 'Phuma uye emfuleni wakwaBhen-hinom, osekungeneni kwesango lasempumalanga, uwagqithisele khona amazwi endiya kuwathetha kuwe.', 'XHO22'),
(28, 19, 3, 'Uthi, Liveni ilizwi likaYehova, nina bokumkani bakwaYuda nani bemi baseYerusalem; Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, le ndawo ndiyizisela ububi; kuba oya kuliva, ziya kubetha iindlebe zakhe;', 'XHO22'),
(28, 19, 4, 'ngenxa yokuba baye bandinikela umva baza banikela umva kule ndawo baza batshisa thixo bambi kuyo ngenxa yabanye oothixo ababengabazi bona nookhokho babo, nookumkani bakwaYuda, baza bayizalisa le ndawo ngabathile.', 'XHO22'),
(28, 19, 5, 'Kwakhona bakha iindawo eziphakamileyo zikaBhahali ukuze babatshise oonyana babo emlilweni njengomnikelo otshiswayo kuBhahali, into endingabayalelanga yona okanye ndingayithethanga nendingayenzanga njengoko kwakuthe qatha entliziyweni yam.', 'XHO22'),
(28, 19, 6, 'Ngako oko, uyabona, iyeza imihla, utsho uYehova, engasayi kuba sabizwa le ndawo ngokuthi yiTofete, nomfula wakwaBhen-hinom; kuya kuthiwa yiNtlambo yokuBulala.', 'XHO22'),
(28, 19, 7, 'Ndiya kuliphelisa icebo lakwaYuda neYerusalem kule ndawo; Ndobahlisa ngekrele phambi kweentshaba zabo, nasesandleni sabawufunayo umphefumlo wabo; yaye ndiya kuwanika amanqatha azo kwiintaka zasendle namarhamncwa asendle abe kukutya kwawo.', 'XHO22'),
(28, 19, 8, 'Ndiya kusenza esi sixeko sibe linxuwa nesigculelo; bonke abadlulayo baya kumangaliswa, bengcikive bonke ubugwenxa bakhe.', 'XHO22'),
(28, 19, 9, 'Ndiza kubadlisa inyama yoonyana babo nenyama yeentombi zabo, yaye ngamnye uya kudla inyama yeqabane lakhe ngenxa yongqingo nokubandezeleka eziya kubohlwaya ngako iintshaba zabo nabo bafuna umphefumlo wabo yona.', 'XHO22'),
(28, 19, 10, 'Yaphula ke ikhonkco phambi kwamehlo amadoda ahamba nawe;', 'XHO22'),
(28, 19, 11, 'Wothi ke kubo, Utsho uYehova wemikhosi ukuthi, Yabonani, ndingowasenkangala; Ndiya kwenjenje ukusiqhekeza esi sizwe nesi sixeko, njengeziko elingenako ukunyanyeka; yaye baya kulingcwabela eTofete kude kungabi sabakho ndawo yakungcwaba.', 'XHO22'),
(28, 19, 12, 'Ndiya kuyenza le ndawo, utsho uYehova, nakubemi bayo, ndiwenze lo mzi ube njengeTofete.', 'XHO22'),
(28, 19, 13, 'Ziya kwenziwa inqambi izindlu zaseYerusalem nezindlu zookumkani bakwaYuda, njengendlu yaseTofete, ngenxa yezindlu zonke eziqhumayo, ezinyuka ziye ezintendelezweni zezulu, zithululele thixweni bambi.', 'XHO22'),
(28, 19, 14, 'uYeremiya wafika evela eTofete, apho uYehova wayemthumele khona ukuba aprofete; wema entendelezweni yendlu kaYehova; wathi kubo bonke abantu:', 'XHO22'),
(28, 19, 15, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, ndiwuzisela lo mzi, nemizi yawo yonke, bonke ububi endibuthethileyo ngabo, ngenxa enokuba bezenza lukhuni iintamo zabo, ukuba bangaliphulaphula ilizwi lam amazwi.', 'XHO22'),
(28, 20, 1, 'uPaskure, unyana wombingeleli uImere, umphathi wendlu kaYehova, weva uYeremiya eprofeta la mazwi.', 'XHO22'),
(28, 20, 2, 'Wandula ke uPashure wambulala uYeremiya umprofeti waza wamfaka esitokisini esasikwisango eliphakamileyo lakwaBhenjamin, lendlu kaYehova.', 'XHO22'),
(28, 20, 3, 'Ngengomso uPashure wamzisa uYeremiya endlwini yobuncwane. Wathi uYeremiya kuye, uYehova akathi unguPashure, uthi nguMagorissabhibhi.', 'XHO22'),
(28, 20, 4, 'Ngokuba etshilo uYehova ukuthi, Yabona, ndikwenza into embi wena, nakubahlobo bakho bonke; yaye baya kuwa likrele leentshaba zabo, yaye aya kukubona amehlo akho; yaye ndiza kumzisa wonke uYuda esandleni sam wokumkani waseBhabheli, abafudusele eBhabheli, ababulale ngekrele.', 'XHO22'),
(28, 20, 5, 'Ngaphezu koko, ndiza kuwanikela onke amandla esi sixeko nawo wonke umsebenzi waso nezinto zonke zaso ezinqwenelekayo nobuncwane bonke bookumkani bakwaYuda ndibunikele esandleni seentshaba zabo eziya kusiphanga zize zibathimbe bazise eBhabheli.', 'XHO22'),
(28, 20, 6, 'Wena ke, Pashure, nabo bonke abahleli endlwini yakho, niya kuthinjwa; yaye ngokuqinisekileyo uya kufika eBhabhiloni, ufele khona ungcwatyelwe khona, wena nabo bonke abahlobo bakho obaprofetele ubuxoki.', 'XHO22'),
(28, 20, 7, 'Yehova, undikhohlisile, ndaza ndazivumela ukuba ndikhohliswe; Unamandla kunam, woyisile; Ndenza iziqhulo yonke imihla, wonke umntu uyandihlekisa.', 'XHO22'),
(28, 20, 8, 'Ngokuba ndithethe, ndikhala, ndinogonyamelo nokubhuqa; kuba ilizwi likaYehova lenziwe intlekisa nesigculelo kum yonke imihla.', 'XHO22'),
(28, 20, 9, 'Ndathi ke, Andisayi kuphinda ndithethe ngaye, andisayi kuthetha ngegama lakhe. Ke ilizwi lakhe lalinjengomlilo ovuthayo, ovalelwe emathanjeni am entliziyweni yam;', 'XHO22'),
(28, 20, 10, 'Ngokuba ndive intlebendwane yabaninzi, bathi, Kunxunguphalo ngeenxa zonke. Baya kusixela, kwaye siya kuxela. Bandibona bonke abo ndibaziyo xa ndisukuma, ndisithi: Mhlawumbi angalingwa, simoyise, siziphindezele kuye.', 'XHO22'),
(28, 20, 11, 'Ke uYehova unam njengegorha elinamandla; ke ngoko, abandisukelayo baya kukhubeka baze bangoyisi; ngokuba abayi kuba nampumelelo;', 'XHO22'),
(28, 20, 12, 'Ke wena, Yehova wemikhosi, ulicikidayo ilungisa, obona izintso nentliziyo, mandiyibone impindezelo yakho kubo, ngokuba ndinivulele ityala lam.', 'XHO22'),
(28, 20, 13, 'Vumani kuYehova, mbongeni uYehova, Ngokuba ewuhlangule umphefumlo wolishwa esandleni samanginingini.', 'XHO22'),
(28, 20, 14, 'Mayiqalekiswe imini endazalwa ngayo; Ngamana ungabongwa umhla owandizala ngawo umama.', 'XHO22'),
(28, 20, 15, 'Uqalekisiwe lowo umshumayeli weendaba ezilungileyo kuyise, esithi, Kuzelwe unyana; imenza onwabe kakhulu.', 'XHO22'),
(28, 20, 16, 'Yaye loo ndoda mayibe njengezixeko uYehova azitshabalalisayo ize ingabi nakuguquka;', 'XHO22'),
(28, 20, 17, 'Ngokuba engandibulalanga kwasesizalweni; okanye umama ube lingcwaba lam, ithanga lakhe lihlale likhulu kunelam.', 'XHO22'),
(28, 20, 18, 'Ibiyini na ukuba ndiphume esizalweni, ukuze ndibone umsebenzi nokwaphuka, nemihla yam iphelele ekudaneni?', 'XHO22'),
(28, 21, 1, 'Ilizwi likaYehova elafikayo kuYeremiya, ekuthumeni kokumkani uZedekiya kuye uPashure unyana kaMelekiya, noZefaniya unyana kaMahaseya, umbingeleli, kuye, lisithi,', 'XHO22'),
(28, 21, 2, 'Masithandaze kuYehova; ngokuba uNebhukadenetsare, ukumkani waseBhabheli, esilwa nathi; ukuba uYehova uyasenzela yonke imimangaliso yakhe, uya kusinikela umva.', 'XHO22'),
(28, 21, 3, 'Wathi uYeremiya kubo: “Yizani kuZedekiya, nithi:', 'XHO22'),
(28, 21, 4, 'Utsho uYehova, uThixo kaSirayeli, ukuthi, Yabonani, ndiya kuzibuyisa izixhobo zemfazwe ezisezandleni zenu, enilwa ngazo ukumkani waseBhabheli, aningqingile ke amaKaledi ngaphandle kweendonga; uya kubabuyisela phakathi kwabahlanganiselwe kulo mzi.', 'XHO22'),
(28, 21, 5, 'Ndiya kulwa nani ngesandla esolukileyo, nangesandla esithe nkqi, ngomsindo nangobushushu, nangobushushu obukhulu;', 'XHO22'),
(28, 21, 6, 'Ndiya kubatshabalalisa abemi balo mzi, abantu neenkomo; ziya kufa sisibetho.', 'XHO22'),
(28, 21, 7, 'Emva koko, utsho uYehova, ndiya kumnikela uZedekiya ukumkani wakwaYuda, nabakhonzi bakhe, nabantu, namasalela esi sixeko, kwindyikitya yokufa ngekrele, ndiwabulale kakumkani uNebhukadenetsare ukuba asindise. eBhabheli nasesandleni seentshaba zabo nasesandleni sabafuna umphefumlo wabo; yaye uya kuboyisa ngohlangothi lwekrele; Akayi kuba nanceba kubo, akayi kuba namfesane kubo, akayi kuba namfesane kubo.', 'XHO22'),
(28, 21, 8, 'Wathi kwaba bantu, Utsho uYehova; Yabonani, ndikubonisa indlela yobomi nendlela yokufa.', 'XHO22'),
(28, 21, 9, 'Bonke abahlala kulo mzi baya kufa likrele, nayindlala, nayindyikitya yokufa; ke yena othe waya kuwahlasela amaKaledi aningqingayo, wophila, ube lixhoba kuye umphefumlo wakhe.', 'XHO22'),
(28, 21, 10, 'Ngokuba lo mzi ndiwuvelele ububi, andawukhangela okulungileyo, utsho uYehova; iya kunikelwa esandleni sokumkani waseBhabheli, ayitshise ngomlilo.', 'XHO22'),
(28, 21, 11, 'Indlu yokumkani wakwaYuda uthi, Liveni ilizwi likaYehova;', 'XHO22'),
(28, 21, 12, 'Nina bendlu kaDavide, utsho uYehova, ukuthi, Kusasa phumezeni umgwebo, nimsindise ophangiweyo esandleni somcudisi, ukuze umsindo wam ungaphumi njengomlilo, uvuthe, kungabikho ucimayo ngenxa yomlilo ububi bezenzo zakho.', 'XHO22'),
(28, 21, 13, 'Yabonani, ndibambene nani, bemi bentili, beliwa lehewu, utsho uYehova; abathi, Ngubani na oya kusihlasela? Ngubani na owongena emizini yethu?', 'XHO22'),
(28, 21, 14, 'Ke ndonivelela ngokweentlondi zenu, utsho uYehova; Ndiza kufaka umlilo ehlathini lakhe, kwaye uya kutshisa yonke into ngeenxa zonke kulo.', 'XHO22'),
(28, 22, 1, 'Wathi uYehova; Yihla uye endlwini yokumkani wakwaYuda, uthethe khona eli lizwi,', 'XHO22'),
(28, 22, 2, 'uthi, Liveni ilizwi likaYehova, kumkani wakwaYuda, uhleliyo etroneni kaDavide, wena, nabakhonzi bakho, nabantu bakho, abangenayo ngala masango.', 'XHO22'),
(28, 22, 3, 'Wathi uYehova; Yenzani ngokwesiko nangobulungisa, nibasindise abo baphangweyo esandleni somcudisi; musani ukwenza ububi, musani ukuthabatha owasemzini, nenkedama, nomhlolokazi, ningaphalazi gazi limsulwa kule ndawo.', 'XHO22'),
(28, 22, 4, 'Ngokuba ukuba nithe nayenza okunene le nto, bongena ngamasango ale ndlu ookumkani, abahleli etroneni kaDavide, bekhwele ezinqwelweni nasemahasheni, yena nabakhonzi bakhe, nabantu bakhe;', 'XHO22'),
(28, 22, 5, 'Ke ukuba anithanga niweve la mazwi, ndizifungile mna, utsho uYehova, ukuba le ndlu iya kuba linxuwa.', 'XHO22'),
(28, 22, 6, 'Ngokuba utsho uYehova kwindlu yokumkani wakwaYuda, ukuthi, UlelaseGiliyadi wena kum, uyincopho yeLebhanon; kodwa okunene ndiya kukwenza intlango, imizi engenabemi.', 'XHO22'),
(28, 22, 7, 'Ndiya kunilungisela abonakalisi, elowo abe neempahla zakhe, bagawule imisedare yakho ekhethekileyo, bayiphose emlilweni.', 'XHO22'),
(28, 22, 8, 'Kuya kudlula kwesi sixeko izizwe ezininzi, yaye ngasinye siya kuthi kwesaso: “Kutheni uYehova esenze saba sikhulu kangaka esi sixeko?', 'XHO22'),
(28, 22, 9, 'Baya kuphendula bathi, Kungenxa yokuba bewushiyile umnqophiso kaYehova uThixo wabo, baqubuda thixweni bambi, babakhonza.', 'XHO22'),
(28, 22, 10, 'Musani ukumbambazela ofileyo, ningamzileli; kodwa lilani ngokukrakra ngenxa yalowo uhambayo, ngenxa yokuba akasayi kuze abuye okanye alibone ilizwe lakhe.', 'XHO22'),
(28, 22, 11, 'Kuba utsho uYehova ngokuphathelele uShalum unyana kaYosiya, ukumkani wakwaYuda, owayelawula esikhundleni sikaYosiya uyise, owashiya le ndawo, ukuthi: “Akayi kuphinda abuyele khona;', 'XHO22'),
(28, 22, 12, 'Kambe ke uya kufela apho abanjwe khona, angabi saphinda alibone eli lizwe.', 'XHO22'),
(28, 22, 13, 'Yeha, owakha indlu yakhe ngobungendawo, namagumbi akhe okuhlala ngobugwenxa; osebenzela uwabo engahlawulwa, angamniki umsebenzi wakhe;', 'XHO22'),
(28, 22, 14, 'owathi, Ndiya kuzakhela indlu ebanzi, enamagumbi aphezulu anombilini; ndiya kummbela iifestile; Wayaleka ngemisedare, wayiqabela ngengqombela.', 'XHO22'),
(28, 22, 15, 'Uya kulawula na, xa unxibe umsedare? Uyihlo ebengadli na, asele, enze ngokusesikweni nangobulungisa, kwamhambela kakuhle;', 'XHO22'),
(28, 22, 16, 'Wayelilungisa ityala losizana nolihlwempu; wahlutha; ibingekuko ukundazi na? utsho uYehova.', 'XHO22'),
(28, 22, 17, 'Kodwa amehlo akho nentliziyo yakho akakhange akhangele nje kuphela ukubawa kwakho nokuphalazwa kwegazi elimsulwa nengcinezelo nogonyamelo ukuze wenze oko.', 'XHO22'),
(28, 22, 18, 'Wathi ke uYehova ngoYowakim unyana kaYosiya, ukumkani wakwaYuda, Musani ukumkhalela, nithi, Yoo, mzalwana wam! okanye, Dadewethu! musani ukumkhalela, nisithi: Oh! okanye: Oh uzuko lwakhe!', 'XHO22'),
(28, 22, 19, 'Uya kungcwatywa kwindawo yokungcwaba iesile, arhuqwe aze alahlwe ngaphandle kwamasango aseYerusalem.', 'XHO22'),
(28, 22, 20, 'Nyuka uye eLebhanon, ubize; uphakamise ilizwi lakho eBhashan uze ukhwaze ukwikona yemida, kuba bonke abo ubathandayo batshabalele.', 'XHO22'),
(28, 22, 21, 'Ndathetha kuni, ninoyolo; wathi, Andifuni kuva. Yindlela yakho leyo kwasebuncinaneni bakho, ukuba ungaliphulaphulanga ilizwi lam.', 'XHO22'),
(28, 22, 22, 'Umoya uya kubatshabalalisa bonke abalusi bakho, nabo bakuthandayo baya kuya ekuthinjweni; inene, ngelo xesha niya kudana, nibe neentloni ngenxa yobungendawo benu bonke.', 'XHO22'),
(28, 22, 23, 'Wena mmi waseLebhanon, owakha indlwane yakho ngemisedare, hayi indlela oya kusinda ngayo xa ufikelwa yintlungu, inimba yomfazi ozalayo!', 'XHO22'),
(28, 22, 24, 'Ndihleli nje, utsho uYehova, ukuba uKoniya unyana kaYehoyakim, ukumkani wakwaYuda, ubengumsesane wokutywina esandleni sam, sokunene, bendiya kumtsalela khona;', 'XHO22'),
(28, 22, 25, 'Ndiya kukunikela esandleni sabo bafuna umphefumlo wakho, nasesandleni sabo uboyikayo, nasesandleni sikaNebhukadenetsare ukumkani waseBhabheli, nasesandleni samaKaledi.', 'XHO22'),
(28, 22, 26, 'Ndonigxothela kwelinye ilizwe, wena nonyoko owakuzalayo; uya kufela khona.', 'XHO22'),
(28, 22, 27, 'Kodwa kwilizwe abafuna ukubuyela kulo, abasayi kubuyela kulo.', 'XHO22'),
(28, 22, 28, 'Ngaba lo mfo uKoniya ngumfanekiso odelekileyo owaphukileyo? Ngaba sisitya esingenaluvuyo? Yini na ukuba bajulwe, yena nabantwana bakhe, baphoswe ezweni abangalaziyo?', 'XHO22'),
(28, 22, 29, 'Lizwe, lizwe, lizwe, live ilizwi likaYehova!', 'XHO22'),
(28, 22, 30, 'Wathi uYehova, Lo mfo mbaleleni ukuba akanamntwana, yindoda engenampumelelo emihleni yayo;', 'XHO22'),
(28, 23, 1, 'Yeha ke, abalusi abatshabalalisa, bachithachithe umhlambi wedlelo lam! utsho uYehova.', 'XHO22'),
(28, 23, 2, 'Ngako oko, utsho uYehova, uThixo kaSirayeli, ngenxa yabalusi abaluse abantu bam; Wazichithachitha umhlambi wam, wazigxotha, akwazivelela; yabonani, ndiya kunivelela ngenxa yobubi beentlondi zenu; utsho uYehova.', 'XHO22'),
(28, 23, 3, 'Ndiya kuwabutha amasalela omhlambi wam emazweni onke endawachithachithela kuwo, ndiwabuyisele emadlelweni awo; kwaye baya kwanda kwaye bande;', 'XHO22'),
(28, 23, 4, 'ndiwamisele abalusi abaya kuwalusa; yaye abasayi koyika okanye bankwantye, yaye abayi kuswela nto,” utsho uYehova.', 'XHO22'),
(28, 23, 5, 'Yabonani, iyeza imihla, utsho uYehova, endiya kummisela uDavide iHlumelo elililungisa, libe ngukumkani ongukumkani, enze yena ngengqiqo, enze ubulungisa nokusesikweni ehlabathini.', 'XHO22'),
(28, 23, 6, 'Ngemihla yakhe uYuda uya kukhululwa noSirayeli ekhuselekile; negama lakhe aya kubizwa ngalo, lilo eli: uYehova ububulungisa bethu.', 'XHO22'),
(28, 23, 7, 'Ngako oko, utsho uYehova, iyeza imihla abangayi kuba sathi, NguYehova ovayo, owabakhuphayo oonyana bakaSirayeli ezweni laseYiputa;', 'XHO22'),
(28, 23, 8, 'Ke ngoYehova ophilileyo, owabakhuphayo oonyana bendlu kaSirayeli ezweni lasentla, nasemazweni onke endabafudusela kuwo; kwaye baya kuhlala emhlabeni wabo.', 'XHO22'),
(28, 23, 9, 'Intliziyo yam yaphukile phakathi kwam ngenxa yabaprofeti; Ayanyikima onke amathambo am; Ndifana nenxila nanjengomntu owethwe yiwayini ngenxa kaYehova nangenxa yamazwi akhe angcwele.', 'XHO22'),
(28, 23, 10, 'Ngokuba ilizwe lizele ngabakrexezi; ngokuba ilizwe likhalela isifungo; iindawo ezinqwenelekayo zentlango ngumqwebedu, nendlela yawo imbi, nokuqina kwawo akukuhle.', 'XHO22'),
(28, 23, 11, 'Ngokuba umprofeti nombingeleli abangcwele; ndizifumene nasendlwini yam izinto zabo ezimbi; utsho uYehova.', 'XHO22'),
(28, 23, 12, 'Ngoko umendo wabo uya kuba njengeendlela ezibuthelezi esithokothokweni; baya kusukelwa bawe khona; kuba ndiya kubazisela intlekele, unyaka wokukohlwaya kwabo,” utsho uYehova.', 'XHO22'),
(28, 23, 13, 'Ndabona ubudenge babaprofeti bakwaSamari; baprofeta ngoBhahali, babandwendwisa abantu bam amaSirayeli.', 'XHO22'),
(28, 23, 14, 'Ndabona into eyoyikekayo kubaprofeti baseYerusalem: bayakrexeza yaye bahamba ebuxokini; Bazomelezile izandla zenginingini, ukuba kungabuyi mntu ebubini bakhe; kum bonke banjengeSodom, nabemi bayo banjengeGomora.', 'XHO22'),
(28, 23, 15, 'Ke ngoko, kwathethwa yiNkosi yemikhosi kubaprofeti; Yabona, ndiyabadlisa amanqatha, ndibaseze inyongo; kuba kubaprofeti baseYerusalem kuphuma okungendawo, kwaya kulo lonke ihlabathi.', 'XHO22'),
(28, 23, 16, 'Utsho uYehova wemikhosi ukuthi, Musani ukuwaphulaphula amazwi abaprofeti abaniprofetelayo; bakwenza into engento; bathetha umbono wentliziyo yabo, ongaphumi emlonyeni kaYehova.', 'XHO22'),
(28, 23, 17, 'Bathi kwabandigxekayo, uYehova uthe, Niya kuba noxolo; nakubo bonke abahambayo ngobubi bentliziyo yabo, bathi, Aniyi kuwela ebubini;', 'XHO22'),
(28, 23, 18, 'Kuba ngubani na wema entendelezweni kaYehova waza walibona waliva ilizwi lakhe? Ngubani na olivayo ilizwi lakhe, waliva?', 'XHO22'),
(28, 23, 19, 'Yabona, umoya ovuthuzayo womsindo kaYehova, uyaphuma, sisiphango esinamandla; iya kuwa nzima phezu kweentloko zabangendawo.', 'XHO22'),
(28, 23, 20, 'Awuyi kubuya umsindo kaYehova, ade azizalise iingcinga zentliziyo yakhe; ekupheleni kosuku kufuneka unikele ingqalelo kuyo.', 'XHO22'),
(28, 23, 21, 'Andibathumanga abaprofeti abo, kugidime bona; Andithethanga kubo, kodwa bona baprofeta.', 'XHO22'),
(28, 23, 22, 'Ukuba bathe basuka bama elucengeni lwam, bababeka abantu bam amazwi am, babuya baguquka endleleni yabo embi, nasebubini bezenzo zabo.', 'XHO22'),
(28, 23, 23, 'Utsho uYehova, ndingabi nguThixo wakude na?', 'XHO22'),
(28, 23, 24, 'Ngubani na ongamfihlayo ezindaweni ezifihlakeleyo, ukuba ndingamboni? utsho uYehova. Andizalise izulu nomhlaba? utsho uYehova.', 'XHO22'),
(28, 23, 25, 'Ndikuvile abakuthethayo abaprofeti, abaprofeta ubuxoki egameni lam, besithi, Ndiphuphile, ndiphuphile.', 'XHO22'),
(28, 23, 26, 'Koda kube nini na ezintliziyweni zabaprofeti abaprofeta ubuxoki? ewe, bangabenzi bokukhohlisa kweentliziyo zabo;', 'XHO22'),
(28, 23, 27, 'bacinga ngokubalibazisa abantu bam igama lam ngamaphupha abo, abawalawulayo koonyana babo, njengoko ooyise balilibalayo igama lam ngenxa kaBhahali.', 'XHO22'),
(28, 23, 28, 'Umprofeti onephupha makalawule iphupha elo; Lowo unelizwi lam, makathethe ilizwi lam enyanisekile. Uyintoni na umququ kwingqolowa? utsho uYehova.', 'XHO22'),
(28, 23, 29, 'Alinjengomlilo na ilizwi lam? utsho uYehova; nanjengesando esiqhekeza ilitye?', 'XHO22'),
(28, 23, 30, 'Ngako oko yabonani, ndibambene nabaprofeti, utsho uYehova, abawebayo amazwi am, elowo kwelakhe amazwi.', 'XHO22'),
(28, 23, 31, 'Yabonani, ndibambene nabaprofeti, utsho uYehova, abathetha ngeelwimi zabo, bathi, Uthetha yena.', 'XHO22'),
(28, 23, 32, 'Yabonani, ndichasene nabaprofeta amaphupha abuxoki, utsho uYehova, bawalawule, babalahlekise abantu bam ngobuxoki babo, nobuxoki babo; andibathumanga, ndingabawiselanga mthetho; ngenxa yoko abayi kulunceda olu hlanga, utsho uYehova.', 'XHO22'),
(28, 23, 33, 'Ke kaloku, xa babuzayo aba bantu, nokuba ngumprofeti, nokuba ngumbingeleli, bathi, Siyintoni na isihlabo sikaYehova? wandule uthi kubo, Uyintoni na umthwalo? nam ndiya kunishiya, utsho uYehova.', 'XHO22'),
(28, 23, 34, 'Umprofeti, nombingeleli, nabantu, abathi, Isihlabo sikaYehova, ndiya kuyivelela loo ndoda nendlu yayo.', 'XHO22'),
(28, 23, 35, 'Nithi elowo kummelwane wakhe, elowo kumzalwana wakhe, nithi, Utheni na uYehova ukuphendule? nokuthi: Wathini uYehova?', 'XHO22'),
(28, 23, 36, 'Musani ukuba sathetha ngomthwalo kaYehova, kuba ilizwi lomntu wonke liya kuba ngumthwalo kuye; kuba niwajikile amazwi kaThixo ophilayo, kaYehova wemikhosi, uThixo wethu.', 'XHO22'),
(28, 23, 37, 'Uze uthi kumprofeti, ‘Ukuphendule wathini uYehova? nokuthi: Wathini uYehova?', 'XHO22'),
(28, 23, 38, 'Kodwa ekubeni nisithi: ‘Sisigidimi sikaYehova! ngako oko, utsho uYehova; Ekubeni lithetha eli gama, lithi: Icebo likaYehova;', 'XHO22'),
(28, 23, 39, 'Ngako oko, yabonani, ndiya kunilibala kuphele, ndinilahle, nina nomzi endaninika wona, nina nooyihlo, ndinigxothe phambi kobuso bam;', 'XHO22'),
(28, 23, 40, 'ndiya kukuzisela ingcikivo engunaphakade, ihlazo elingunaphakade, elingenakulityalwa.', 'XHO22'),
(28, 24, 1, 'uYehova wandibonisa, nanzo iingobozi ezimbini zamakhiwane zimi phambi kwetempile kaYehova, zimi ngasemva koNebhukadenetsare ukumkani waseBhabheli, noYekoniya unyana kaYehoyakim, ukumkani wakwaYuda, nabathetheli bakwaYuda, kunye nabachweli, bathinjwa nemivalo yesinyithi yaseYerusalem, bayisa eBhabheli.', 'XHO22'),
(28, 24, 2, 'Enye ingobozi ibinamakhiwane alunge kunene, enjengamakhiwane asele evuthiwe; enye ingobozi ibinamakhiwane amaninzi amabi, engenakudliwa ngokuba ebemabi kakhulu.', 'XHO22'),
(28, 24, 3, 'Wathi uYehova kum, Ubonile na, Yeremiya? Ndathi, Amakhiwane; amakhiwane alungileyo, alunge kakhulu; kwaye embi, embi kakhulu, engatyiwayo, embi kakhulu.', 'XHO22'),
(28, 24, 4, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 24, 5, 'Utsho uYehova, uThixo kaSirayeli, ukuthi; Njengaloo makhiwane alungileyo, ndiya kwenjenjalo ukubazi abathinjwa bakwaYuda, endibafudusileyo kule ndawo, ndibase ezweni lamaKaledi, ngenxa yabo.', 'XHO22'),
(28, 24, 6, 'Ndiya kulimisela okulungileyo iliso lam kubo, ndibabuyisele kweli lizwe; ndiya kubakha, ndingabagungxuli; ndibatyale, ndingabagungxuli.', 'XHO22'),
(28, 24, 7, 'Ndobanika nentliziyo yokundazi, ukuba ndinguYehova; kwaye baya kuba ngabantu bam, mna ndibe nguThixo wabo; ngokuba baya kubuyela kum ngentliziyo yabo yonke.', 'XHO22'),
(28, 24, 8, 'Njengamakhiwane amabi, angenakudliwa ngobubi bawo; Inene, utsho uYehova ukuthi, Ndiya kumnikela uZedekiya ukumkani wakwaYuda, nabathetheli bakhe, namasalela aseYerusalem aseleyo kweli lizwe, nabemi belizwe laseYiputa.', 'XHO22'),
(28, 24, 9, 'Kwaye ndiza kubanikela ukuba bangcangcazele ezikumkanini zonke zehlabathi ngenxa yobubi babo, ngokunyelisa nokugculela nangehlazo kuzo zonke iindawo endiya kubagxothela kuzo.', 'XHO22'),
(28, 24, 10, 'Ndiza kubathumelela ikrele nendlala nendyikitya yokufa bade badlule emhlabeni endawunika bona nooyise.', 'XHO22'),
(28, 25, 1, 'Ilizwi elafikayo kuYeremiya ngokusingisele ebantwini bonke bakwaYuda, ngomnyaka wesine wobukumkani bukaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, ongumnyaka wokuqala wobukumkani bukaNebhukadenetsare ukumkani waseBhabheli.', 'XHO22'),
(28, 25, 2, 'Oko wakuthethayo uYeremiya umprofeti kubo bonke abantu bakwaYuda, nakubo bonke abemi baseYerusalem, esithi,', 'XHO22'),
(28, 25, 3, 'Kususela kumnyaka weshumi elinesithathu kaYosiya unyana ka-Amon, ukumkani wakwaYuda, kuzise kule mini, le minyaka imashumi mabini anamithathu, kwafika ilizwi likaYehova kum, lathetha nawe, lisithi, ndivuka kusasa, ndivuke kusasa, ndivuke ekuseni. Ndiyathetha; kodwa aniphulaphulanga.', 'XHO22'),
(28, 25, 4, 'uYehova wathumela kuni bonke abakhonzi bakhe, abaprofeti, wavuka kusasa, wabathuma; kodwa aniphulaphulanga, neendlebe zenu anibathobelanga.', 'XHO22'),
(28, 25, 5, 'Bathi ke bona, Guqukani elowo endleleni yakhe embi, nasebubini beentlondi zenu, nihlale ezweni elo, aninikileyo uYehova, nina nooyihlo, kude kuse ephakadeni;', 'XHO22'),
(28, 25, 6, 'ningalandeli thixo bambi, ningabakhonzi, ningabakhonzi, ningandikhonzi ngomsebenzi wezandla zenu; andiyi kukwenza nto imbi.', 'XHO22'),
(28, 25, 7, 'Aniphulaphulanga kum, utsho uYehova; ukuze uwusebenzise umsebenzi wezandla zakho kuni.', 'XHO22'),
(28, 25, 8, 'Ngako oko, utsho uYehova wemikhosi ukuthi, Ngenxa enokuba ningawaphulaphulanga amazwi am,', 'XHO22'),
(28, 25, 9, 'Yabonani, ndithumela kuNebhukadenetsare ukumkani waseBhabheli, umkhonzi wam, zonke izizwe zasentla, utsho uYehova, ukuba ndibazisele, ukuba bahlasele eli lizwe, nabemi balo, nabamelwane bawo onke abamelwane iintlanga Uya kubabetha ngesiqalekiso, abenze imimangaliso nesigculelo nesiphanziso ukusa kwixesha elingenammiselo.', 'XHO22'),
(28, 25, 10, 'Ndiya kulisusa kubo izwi lemivuyo nezwi lemivuyo, izwi lomyeni nezwi lomtshakazi, nesandi sogwali nokukhanya kwesibane.', 'XHO22'),
(28, 25, 11, 'Kwaye lonke ihlabathi liya kuba lilizwe elihle; ezi ntlanga ziya kumkhonza ukumkani waseBhabheli iminyaka emashumi asixhenxe.', 'XHO22'),
(28, 25, 12, 'Emva kweminyaka engamashumi asixhenxe ndiya kumkhalimela ukumkani waseBhabhiloni nolo hlanga ngenxa yobungendawo babo nangenxa yelizwe lamaKhaledi, utsho uYehova, ndilenze libe senkangala kude kube sekupheleni kwexesha elilinganiselweyo,', 'XHO22'),
(28, 25, 13, 'ndowazisa kweli lizwe onke amazwi am endawathethayo ngalo, nayo yonke into ebhaliweyo kule ncwadi, ayiprofetileyo uYeremiya ngeentlanga zonke.', 'XHO22'),
(28, 25, 14, 'Kuba iintlanga ezininzi nookumkani abakhulu baya kukhonza ngabo, yaye ndiya kubavuza ngokwezenzo zabo nangokwezenzo zezandla zabo.', 'XHO22'),
(28, 25, 15, 'Ngokuba utshilo kum uYehova, uThixo kaSirayeli, ukuthi, Thabatha le ndebe yewayini yobushushu esandleni sam, zisele ngayo iintlanga zonke, endikuthumela kuzo;', 'XHO22'),
(28, 25, 16, 'Ziya kusela, zihexe, zigeze, ngenxa yekrele endilithumela phakathi kwazo.', 'XHO22'),
(28, 25, 17, 'Ndayithabatha ke indebe esandleni sikaYehova, ndazinika amanzi zonke iintlanga, awayendithumele kuzo uYehova:', 'XHO22'),
(28, 25, 18, 'oko kukuthi, iYerusalem, nemizi yakwaYuda, nookumkani bayo, nabathetheli bayo, ukuba benziwe iimpanza, nommangaliso, nesigculelo, nentshabhiso; kunjani namhlanje;', 'XHO22'),
(28, 25, 19, 'uFaro ukumkani waseYiputa, nabakhonzi bakhe, nabathetheli bakhe, nabantu bakhe bonke;', 'XHO22'),
(28, 25, 20, 'nomxukuxela wonke, nookumkani bonke belizwe lakwaUtse, nookumkani bonke belizwe lamaFilisti, neAshkelon, neAsa, ne-Ekron, namasalela aseAshdode;', 'XHO22'),
(28, 25, 21, 'uEdom, noMowabhi, noonyana baka-Amon;', 'XHO22'),
(28, 25, 22, 'nookumkani bonke baseTire, nookumkani bonke baseTsidon, nookumkani bonke beziqithi eziphesheya kolwandle;', 'XHO22'),
(28, 25, 23, 'uDedan, noTema, noBhuse, nabo bonke abakweleembombo zone;', 'XHO22'),
(28, 25, 24, 'nookumkani bonke bama-Arabhi, nookumkani bonke bolunye uhlanga, ababemi entlango;', 'XHO22'),
(28, 25, 25, 'nookumkani bonke bakwaZimri, nookumkani bonke bakwaElam, nookumkani bonke bamaMedi;', 'XHO22'),
(28, 25, 26, 'nookumkani bonke belasentla, abakufuphi nabakude, elowo nowabo, nezikumkani zonke zehlabathi eziphezu komhlaba, asele emva kwabo ukumkani waseSesaki.', 'XHO22'),
(28, 25, 27, 'Ngako oko uthi kubo, Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, ndinguYehova, uThixo kaSirayeli; Sela, unxile, usele, uwe, ungabuyi uvuke ngenxa yekrele endilithumela phakathi kwenu.', 'XHO22'),
(28, 25, 28, 'Ukuba bathe abavuma ukuyithabatha indebe esandleni sakho ukuba bayisele, wothi kubo, Utsho uYehova wemikhosi; Ngokuqinisekileyo uya kusela.', 'XHO22'),
(28, 25, 29, 'Ngokuba, yabonani, sendiqalile ukuwenza ububi emzini obizwa ngegama lam; Aniyi kuba msulwa, ngokuba ikrele ndilibizela bonke abemi behlabathi; utsho uYehova wemikhosi.', 'XHO22'),
(28, 25, 30, 'Ke ngoko, profeta onke amazwi ngokuchasene nabo, uze uthi kubo: iNkosi iya kubharhula phezulu kwaye iliphakamise ilizwi layo ebudodeni bayo obungcwele; uya kubharhula phezu kwentente yakhe; njengabo baxovula umdiliya, uya kuphakamisa isikhalo kubo bonke abemi behlabathi.', 'XHO22'),
(28, 25, 31, 'Ingxolo iye eziphelweni zehlabathi; ngokuba uYehova ubambene neentlanga, uya kulwa nenyama yonke; abangendawo ubanikela ekreleni; utsho uYehova.', 'XHO22'),
(28, 25, 32, 'Utsho uYehova wemikhosi ukuthi, Yabonani, ububi buya kuphuma kolunye uhlanga, buye kolunye;', 'XHO22'),
(28, 25, 33, 'Yaye ngaloo mini, abo baya koyiswa nguYehova, baya kususela kwesinye isiphelo sehlabathi ukusa kwesinye isiphelo sehlabathi; abayi kumbambazelelwa, abayi kuhlanganiswa, bangcwatywe; iya kugcinwa emhlabeni.', 'XHO22'),
(28, 25, 34, 'Lilani, balusi, nilile; Zigaleleni eluthuthwini, zintloko zomhlambi; kwaye uya kuwa njengento ethandekayo.', 'XHO22'),
(28, 25, 35, 'Abalusi baya kufuna indlela yokusaba, neenkokeli zomhlambi aziyi kusinda.', 'XHO22'),
(28, 25, 36, 'Ilizwi lokukhala kwabalusi liya kuvakala isikhalo sezikhulu zomhlambi, ngenxa yokuba uYehova ulitshabalalisile idlelo labo.', 'XHO22'),
(28, 25, 37, 'Neendawo zokuhlala zoxolo ziphangiwe ngenxa yokuvutha komsindo kaYehova.', 'XHO22'),
(28, 25, 38, 'Uyishiyile indawo yakhe yokuzimela njengengonyama, ngenxa yokuba ilizwe labo libe yinkangala ephanzileyo ngenxa yokuvutha komsindo womcinezeli nangenxa yomsindo wakhe.', 'XHO22'),
(28, 26, 1, 'Ekuqaleni kobukumkani bukaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, kwafika eli lizwi, liphuma kuYehova, lisithi,', 'XHO22'),
(28, 26, 2, 'Wathi uYehova; Yima entendelezweni yendlu kaYehova, uthethe kuyo yonke imizi yakwaYuda, eya kuqubuda endlwini kaYehova, onke amazwi endikuwisela umthetho wokuba uwathethe kubo; ayinciphisi igama:', 'XHO22'),
(28, 26, 3, 'Ukuba kunjalo, baya kuphulaphula baze baguquke, ngamnye endleleni yakhe embi, ukuze ndizisole ngobubi endicinga ukubenza kubo ngenxa yobubi bezenzo zabo.', 'XHO22'),
(28, 26, 4, 'Wothi kubo, uYehova uthethile; njengokungathi anindiphulaphulanga, ukuba nihambe ngomyalelo wam endiwubeka phambi kwenu;', 'XHO22'),
(28, 26, 5, 'ukuba niwaphulaphule amazwi abakhonzi bam, abaprofeti, endibathumele kuni, bavuka kusasa, babathuma, anaphulaphula;', 'XHO22'),
(28, 26, 6, 'Ndiza kuyenza le ndlu ibe njengeShilo ndize ndisenze esi sixeko sibe sisiqalekiso kuzo zonke iintlanga zomhlaba.', 'XHO22'),
(28, 26, 7, 'Beva ke uYeremiya ababingeleli, nabaprofeti, nabantu bonke, ethetha la mazwi endlwini kaYehova.', 'XHO22'),
(28, 26, 8, 'Kwathi, akugqiba uYeremiya ukuthetha konke awayemwisele umthetho uYehova ukuba akuthethe ebantwini bonke, bambamba ababingeleli, nabaprofeti, nabantu bonke, besithi, Inene, uya kufa.', 'XHO22'),
(28, 26, 9, 'Yini na ukuba uprofete egameni likaYehova, usithi, Le ndlu iya kuba njengeShilo, nesi sixeko sibe linxuwa, singabi nammi? Yaye bonke abantu bahlanganisana kuYeremiya endlwini kaYehova.', 'XHO22'),
(28, 26, 10, 'Bakuyiva loo nto abathetheli bakwaYuda, bemka endlwini yokumkani, benyuka baya endlwini kaYehova, bahlala phantsi ekungeneni kwesango elitsha lendlu kaYehova.', 'XHO22'),
(28, 26, 11, 'Ababingeleli nabaprofeti bathetha kwiinkosana nakubantu bonke, bathi: “Le ndoda ifanelwe kukufa; ngokuba uprofetele kulo mzi, njengoko nive ngeendlebe zenu.', 'XHO22'),
(28, 26, 12, 'Wathetha uYeremiya kubathetheli bonke nakubantu bonke, esithi, uYehova undithume ukuba ndiprofete ngale ndlu nakulo mzi onke amazwi eniwavileyo.', 'XHO22'),
(28, 26, 13, 'Kaloku ke, zilungiseni iindlela zenu neentlondi zenu, niliphulaphule ilizwi likaYehova uThixo wenu; yaye uYehova uya kuzohlwaya ngenxa yobubi athethe ngabo.', 'XHO22'),
(28, 26, 14, 'Mna, yabonani, ndisesandleni senu; yenza kum njengoko uthanda, njengoko uthanda.', 'XHO22'),
(28, 26, 15, 'Niyazi ke ukuba, ukuba nithe nandibulala, inene, wobeka igazi elimsulwa phezu kwenu, naphezu kwalo mzi, naphezu kwabemi bawo; ngokuba uYehova undithume kuni ukuba ndiwathethe onke la mazwi ezindlebeni zakho.', 'XHO22'),
(28, 26, 16, 'Bathi iinkosana nabantu bonke kubabingeleli nakubaprofeti, Le ndoda ayifanelwe sisigwebo sokufa, ngokuba ithethe kuthi egameni likaYehova uThixo wethu.', 'XHO22'),
(28, 26, 17, 'Kwasuka kwema ke amadoda kumadoda amakhulu elizwe, athetha kulo lonke ibandla labantu, esithi:', 'XHO22'),
(28, 26, 18, 'uMikaya waseMorashe waprofeta ngemihla kaHezekiya ukumkani wakwaYuda, wathi kubantu bonke bakwaYuda, Utsho uYehova wemikhosi ukuthi, iZiyon iya kutyalwa njengentsimi, iYerusalem ibe ngumwewe; intaba yendlu yona ibe njengokuphakama kwehlathi.', 'XHO22'),
(28, 26, 19, 'Bambulala na uHezekiya ukumkani wakwaYuda, namaYuda onke? Akoyikanga uYehova na, wambuza uYehova, waza uYehova wazohlwaya ngenxa yobubi abebuthethile ngabo? Ngoko ke sinokwenza ububi obukhulu kwimiphefumlo yethu.', 'XHO22'),
(28, 26, 20, 'Kananjalo bekukho indoda eyayiprofeta egameni likaYehova, u-Uriya unyana kaShemaya, waseKiriyati-yeharim, eprofeta ngalo mzi nangeli lizwe, ngokwamazwi onke kaYeremiya.', 'XHO22'),
(28, 26, 21, 'Waweva ukumkani uYehoyakim, namagorha akhe onke, nabathetheli bonke, amazwi akhe; ukumkani wafuna ukumbulala; Weva ke u-Uriya woyika, wasaba, waya eYiputa.', 'XHO22'),
(28, 26, 22, 'Ukumkani uYehoyakim wathumela amadoda eYiputa, uElinatan unyana ka-Akebhore, namanye amadoda eYiputa.', 'XHO22'),
(28, 26, 23, 'Bamkhupha u-Uriya eYiputa, bamzisa kukumkani uYehoyakim; abo bambulala ngomkhonto, isidumbu sakhe basiphosa emangcwabeni abantu nje abaqhelekileyo.', 'XHO22'),
(28, 26, 24, 'Ke isandla sika-Ahikam, unyana kaShafan, besinoYeremiya, ukuze banganikelwa esandleni sabantu ukuba bambulale.', 'XHO22'),
(28, 27, 1, 'Ekuqalekeni kobukumkani bukaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, kwafika ilizwi likaYehova kuYeremiya, lisithi,', 'XHO22'),
(28, 27, 2, 'Wathi uYehova kum; Zenzele imixokelelwane needyokhwe, uzibeke entanyeni yakho;', 'XHO22'),
(28, 27, 3, 'Wazithumela kukumkani wakwaEdom, nakukumkani wakwaMowabhi, nakukumkani woonyana baka-Amon, nakukumkani waseTire, nakukumkani waseTsidon, kunye nabathunywa abafika eYerusalem kuZedekiya ukumkani kwaYuda;', 'XHO22'),
(28, 27, 4, 'Wabawisela umthetho wokuba bathi kwinkosi yabo, Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Uya kuthi kubanini bakho:', 'XHO22'),
(28, 27, 5, 'Ndenza umhlaba ngamandla am amakhulu nangesandla sam esoluliweyo, umntu nerhamncwa elisemhlabeni, ndawunika lowo ndibone ukulunga kwawo.', 'XHO22'),
(28, 27, 6, 'Kaloku ke onke la mazwe ndiwanikele esandleni sikaNebhukadenetsare ukumkani waseBhabheli, umkhonzi wam; namarhamncwa asendle ndamnika ukuba amkhonze.', 'XHO22'),
(28, 27, 7, 'Zonke iintlanga ziya kumkhonza yena, nonyana wakhe, nonyana wonyana wakhe, kude kufike ixesha lelizwe lakhe, zikhonze ngaye iintlanga ezininzi nookumkani abakhulu.', 'XHO22'),
(28, 27, 8, 'Kuya kuthi ke, uhlanga nobukumkani obungayi kumkhonza uNebhukadenetsare yedwa, ukumkani waseBhabheli, obungayifakiyo intamo yabo edyokhweni yokumkani waseBhabheli, ndiluvelele olo hlanga, Yehova; ngekrele nangendyikitya yokufa, ndide ndibatshabalalise ngesandla sakhe.', 'XHO22'),
(28, 27, 9, 'Ngoko ke musani ukubaphulaphula abaprofeti benu, namatola enu, namaphupha enu, nabathandazeli benu, nabakhafuli benu, abathi kuni, Andiyi kumkhonza ukumkani waseBhabheli.', 'XHO22'),
(28, 27, 10, 'Kuba baniprofetela ubuxoki, ukuze nigxothwe ezweni lenu; nokuba bendiya kunigxotha, nitshabalale.', 'XHO22'),
(28, 27, 11, 'Ke zona iintlanga ezibeka iintamo zazo edyokhweni kakumkani waseBhabhiloni zize zimkhonze, ndiya kuzola elizweni lazo,’ utsho uYehova; baya kulityala, bahlale kulo.', 'XHO22'),
(28, 27, 12, 'Kananjalo uZedekiya ukumkani wakwaYuda ndamxelela ngokwaloo mazwi onke, ndathi, Zingeniseni iintamo zenu edyokhweni yokumkani waseBhabheli, nimkhonze yena nabantu bakhe, nobomi bakhe.', 'XHO22'),
(28, 27, 13, 'Kutheni na ukuba nife, wena nohlanga lwakho, ngekrele, ngendlala nangendyikitya yokufa, njengoko watshoyo uYehova ngohlanga ukuba ukumkani waseBhabhiloni akayi kumkhonza?', 'XHO22'),
(28, 27, 14, 'Ngoko musani ukuwaphulaphula amazwi abaprofeti abathetha kuni, besithi, ‘Aniyi kumkhonza ukumkani waseBhabhiloni, kuba baniprofetela ubuxoki.', 'XHO22'),
(28, 27, 15, 'Ngokuba andibathumanga, utsho uYehova; bona baprofeta ubuxoki egameni lam; Ndonigxotha ke, ndinitshabalalise, nina nabaprofeti abaniprofetelayo.', 'XHO22'),
(28, 27, 16, 'Ndaphinda ndathetha kubabingeleli nakwaba bantu bonke, ndathi, Utsho uYehova ukuthi; Musani ukuwaphulaphula amazwi abaprofeti benu abaniprofetelayo, besithi, Yabona, impahla yendlu kaYehova iza kukhawuleza ibuyiselwe eBhabheli; ngokuba baniprofetela ubuxoki.', 'XHO22'),
(28, 27, 17, 'Musani ukubaphulaphula; mkhonzeni ukumkani waseBhabheli, niphile; bekutheni ukuze eso sixeko sitshatyalaliswe?', 'XHO22'),
(28, 27, 18, 'Kodwa ukuba bangabaprofeti, yaye ilizwi likaYehova likubo, mabangene kuYehova wemikhosi, ukuze iimpahla eziseleyo endlwini kaYehova nasendlwini kakumkani wakwaYuda zingene bona neYerusalem, musani ukuya eBhabheli.', 'XHO22'),
(28, 27, 19, 'Kuba utsho uYehova wemikhosi ngokuphathelele iintsika nolwandle ngokuphathelele iziseko nangamasalela eempahla ezishiywe kwesi sixeko:', 'XHO22'),
(28, 27, 20, 'engathatyathwanga nguNebhukadenetsare ukumkani waseBhabheli, ekumthabatheni kwakhe uYekoniya, unyana kaYehoyakim, ukumkani wakwaYuda, wamsa eYerusalem, wamsa eBhabheli, nabathetheli bonke bakwaYuda nabaseYerusalem;', 'XHO22'),
(28, 27, 21, 'Ewe, utsho uYehova wemikhosi, uThixo kaSirayeli, ngokusingisele kwimpahla eseleyo endlwini kaYehova, nasendlwini yokumkani wakwaYuda naseYerusalem;', 'XHO22'),
(28, 27, 22, 'Baza kuthinjelwa eBhabhiloni, bahlale khona de kube yimini endibavelela ngayo,” utsho uYehova; ndiya kubanyusa khona, ndibabuyisele kule ndawo.', 'XHO22'),
(28, 28, 1, 'Kwangaloo mnyaka, ekuqaleni kobukumkani bukaZedekiya ukumkani wakwaYuda, ngomnyaka wesine, ngenyanga yesihlanu, wathetha kum uHananiya, unyana womprofeti uAsiriya, waseGibheyon. Wathi endlwini kaYehova, phambi kwababingeleli nabantu bonke,', 'XHO22'),
(28, 28, 2, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ndiyayaphula idyokhwe yokumkani waseBhabheli.', 'XHO22'),
(28, 28, 3, 'Emva kweminyaka emibini ndiya kuzibuyisela kule ndawo zonke iimpahla zendlu kaYehova awayezithabathe kule ndawo uNebhukadenetsare ukumkani waseBhabhiloni waza wazisa eBhabhiloni.', 'XHO22'),
(28, 28, 4, 'Ndiza kubabuyisela kule ndawo uYekoniya unyana kaYehoyakim ukumkani wakwaYuda nabo bonke abathinjwa bakwaYuda ababeye eBhabhiloni,” utsho uYehova; kuba ndiya kuyaphula idyokhwe yokumkani waseBhabhiloni.', 'XHO22'),
(28, 28, 5, 'Wathi uYeremiya umprofeti kuHananiya umprofeti, emehlweni ababingeleli, nasemehlweni abantu bonke ababemi endlwini kaYehova:', 'XHO22'),
(28, 28, 6, 'Wathi uYeremiya umprofeti, Amen, uyayenza le nto uYehova; uwalimisile uYehova amazwi awawaprofeteleyo, wokuba azibuyisele kule ndawo iimpahla zendlu kaYehova, neento zonke ezibe zithinjelwe eBhabheli.', 'XHO22'),
(28, 28, 7, 'Noko ke, niyaliva na ngoku eli lizwi ndilithethayo nisaliva naphambi kweendlebe zabo bonke abantu?', 'XHO22'),
(28, 28, 8, 'Abaprofeti bamandulo ababengaphambi kwam naphambi kwakho baprofeta imfazwe, nobubi, nendyikitya yokufa, kumazwe amaninzi nanamandla amakhulu.', 'XHO22'),
(28, 28, 9, 'Umprofeti oprofeta uxolo, ukuba ilizwi lomprofeti libonakala, loo mprofeti uya kwaziwa ukuba nguYehova omthumileyo.', 'XHO22'),
(28, 28, 10, 'Waza umprofeti uHananiya wayisusa idyokhwe entanyeni kaYeremiya umprofeti waza wayaphula.', 'XHO22'),
(28, 28, 11, 'Wathetha uHananiya phambi kwabantu bonke, wathi, Utsho uYehova; Ndiya kwenjenje ukuyaphula idyokhwe kaNebhukadenetsare ukumkani waseBhabheli, ezintanyeni zeentlanga zonke iminyaka emibini. Wahamba ke uYeremiya umprofeti.', 'XHO22'),
(28, 28, 12, 'Kwafika ilizwi likaYehova kuYeremiya umprofeti emva kokuba umprofeti uHananiya eyaphule idyokhwe entanyeni kaYeremiya umprofeti lisithi:', 'XHO22'),
(28, 28, 13, 'Yiya uthethe kuHananiya, uthi, uYehova uthethile; Uyaphule idyokhwe yomthi; ke uya kubenza idyokhwe yentsimbi.', 'XHO22'),
(28, 28, 14, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ndibeka idyokhwe yesinyithi ezintanyeni zezo ntlanga zonke, ukuze zimkhonze uNebhukadenetsare ukumkani waseBhabheli; ziya kumkhonza, ndimnike amarhamncwa elizwe.', 'XHO22'),
(28, 28, 15, 'Emva koko umprofeti uYeremiya wathi kuHananiya umprofeti: “Phulaphula, Hananiya! uYehova akalithumelanga; nisuka nilukholwe ngobuxoki olu hlanga.', 'XHO22'),
(28, 28, 16, 'Ngako oko, utsho uYehova ukuthi, Yabona, ndiya kukulahla emhlabeni; uya kufa kulo nyaka, ngokuba efundise ukugwilika kuYehova.', 'XHO22'),
(28, 28, 17, 'Wafa ke umprofeti uHananiya ngenyanga yesixhenxe yaloo nyaka.', 'XHO22'),
(28, 29, 1, 'Ngawo la amazwi encwadi, awayithumelayo uYeremiya umprofeti eYerusalem, kumadoda amakhulu asele ekuthinjweni, nakubabingeleli, nakubaprofeti, nakubantu bonke, awabafudusayo uNebhukadenetsare eYerusalem ukuya eBhabhiloni;', 'XHO22'),
(28, 29, 2, 'Emva koko ukumkani uYekoniya nokumkanikazi nabalungiseleli neenkosana zakwaYuda nezaseYerusalem nabachweli nabakhandi besinyithi bemka eYerusalem;', 'XHO22'),
(28, 29, 3, 'ngesandla sikaElasa unyana kaShafan, noGemariya unyana kaHilekiya, awathi uZedekiya ukumkani wakwaYuda wabathumela eBhabheli kuNebhukadenetsare ukumkani waseBhabheli, esithi,', 'XHO22'),
(28, 29, 4, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Kubathinjwa bonke endibafudusileyo eYerusalem, ndabafudusela eBhabheli;', 'XHO22'),
(28, 29, 5, 'Yakhani izindlu, nihlale kuzo; tyalani imiyezo nidle iziqhamo zayo;', 'XHO22'),
(28, 29, 6, 'Zekani abafazi, nizale oonyana neentombi; bathabatheleni abafazi oonyana benu, nizendisele amadoda iintombi zenu, zizale oonyana neentombi; ukuze wande khona, unganciphi.', 'XHO22'),
(28, 29, 7, 'Nizamele uxolo lomzi endinifudusele kuwo, niwuthandazele kuYehova; ngokuba niya kuba noxolo ngoxolo lwawo.', 'XHO22'),
(28, 29, 8, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Musani ukulahlekiswa ngabaprofeti benu, namatola enu aphakathi kwenu, ningawaphulaphuli amaphupha enu eniwaphuphayo;', 'XHO22'),
(28, 29, 9, 'Ngokuba baniprofetela ubuxoki egameni lam; andibathumanga, utsho uYehova.', 'XHO22'),
(28, 29, 10, 'Kuba uYehova uthe, Yakuzaliseka iminyaka emashumi asixhenxe eBhabheli, ndize ndikhangele kuni, ndilimise ilizwi lam elilungileyo, ndinibuyisele kule ndawo.', 'XHO22'),
(28, 29, 11, 'Ngokuba ndiyazazi iingcinga endizicingayo ngani, utsho uYehova: iingcinga zoxolo, ezingezizo ezobubi, ukuba ndininike ithemba.', 'XHO22'),
(28, 29, 12, 'Niya kwandula ke nibize kum, nithandaze kum, ndiniphulaphule.', 'XHO22'),
(28, 29, 13, 'Niya kundifuna, nindifumane, ukuba nithe nandifuna ngentliziyo yenu yonke;', 'XHO22'),
(28, 29, 14, 'Ndofunyanwa phakathi kwenu, utsho uYehova; Ndiya kukubuyisa ukuthinjwa kwenu, ndinibuthe ezintlangeni zonke, nasezindaweni zonke endanichithangala kuzo, utsho uYehova; ndinibuyisele endaweni endanifudusela kuyo.', 'XHO22'),
(28, 29, 15, 'Ngokuba nisithi, uYehova usivelisele abaprofeti eBhabheli;', 'XHO22'),
(28, 29, 16, 'Yazini into ayithethayo uYehova ngokumkani ohleli etroneni kaDavide, nakubantu bonke abahlala kwesi sixeko, nangabantakwenu abangazange baye nawe ekuthinjweni;', 'XHO22'),
(28, 29, 17, 'Utsho uYehova wemikhosi ukuthi, Yabonani, ndibathumela ikrele, nendlala, nendyikitya yokufa, ndibenze babe njengamakhiwane amabi, angenakudliwa ngokuba mabi kunene;', 'XHO22'),
(28, 29, 18, 'Ndiya kubatshutshisa ngekrele, ngendlala, nangendyikitya yokufa, ndibanyikimise ezikumkanini zonke zehlabathi, babe yintshabhiso, nommangaliso, nesigculelo, nesingcikivo phakathi kwabo zonke iintlanga endibagxothele kuzo.', 'XHO22'),
(28, 29, 19, 'Ngenxa enokuba bengawaphulaphulanga amazwi am, utsho uYehova, endawathuma kubo ngabakhonzi bam abaprofeti, ndavuka kusasa, ndabathuma; aniphulaphulanga; utsho uYehova.', 'XHO22'),
(28, 29, 20, 'Liveni ilizwi likaYehova, nonke nina bathinjwa, endinithabathileyo eYerusalem, ndabasa eBhabheli.', 'XHO22'),
(28, 29, 21, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ngoAhabhi unyana kaKolaya, nakuZedekiya unyana kaMahaseya, abaprofeta ubuxoki egameni lam; Yabona, ndiyabanikela esandleni sikaNebhukadenetsare ukumkani waseBhabheli; ubabulale phambi kwakho;', 'XHO22'),
(28, 29, 22, 'Yaye ngokuqinisekileyo baya kuqalekiswa ngabo bonke abathinjwa bakwaYuda abaseBhabhiloni, besithi, ‘uYehova uya kukwenza ube njengoZedekiya nanjengoAhabhi owatshisa ukumkani waseBhabhiloni emlilweni;', 'XHO22'),
(28, 29, 23, 'ngenxa enokuba benze ububi kwaSirayeli, babakrexeza abafazi babamelwane babo, bathetha amazwi obuxoki egameni lam, endingabawiselanga methetho ngawo nam ndiyazi, kwaye ndilingqina, utsho uYehova.', 'XHO22'),
(28, 29, 24, 'Thetha kuShemaya waseMonelam, uthi,', 'XHO22'),
(28, 29, 25, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ngenxa enokuba uthumele iincwadi egameni lakho kubantu bonke abaseYerusalem, nakuZefaniya unyana kaMahaseya, umbingeleli, nakubabingeleli bonke; kwaye wathi:', 'XHO22'),
(28, 29, 26, 'uYehova ukumisele ukuba ube ngumbingeleli esikhundleni sombingeleli uYehoyada ukuba ube ngumlindi endlwini kaYehova, ngenxa yokuba nabani na ophambeneyo nozenza umprofeti, umvalela entolongweni aze avalelwe.', 'XHO22'),
(28, 29, 27, 'Kutheni ke ngoko ungamkhalimelanga uYeremiya waseAnatoti, ozenza umprofeti kuni?', 'XHO22'),
(28, 29, 28, 'Kungenxa yoko le nto wasithumela eBhabheli, esithi, Kuya kuba lixesha elide ukuthinjwa; yakhani izindlu nihlale kuzo; tyalani imiyezo nidle iziqhamo zayo.', 'XHO22'),
(28, 29, 29, 'Umbingeleli uZefaniya wayifunda le leta ngoxa umprofeti uYeremiya wayephulaphule.', 'XHO22'),
(28, 29, 30, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 29, 31, 'Thumela kubo bonke abathinjwa, uthi, Utsho uYehova ngoShemaya waseNehelam; Ngokuba eniprofete uShemaya, ndingamthumanga mna, wanenza nakholwa bubuxoki.', 'XHO22'),
(28, 29, 32, 'Ngako oko, wathi uYehova, Yabona, ndimvelela uShemaya waseNehelam nembewu yakhe; akayi kuba nandoda iya kuhlala phakathi kwaba bantu; angayi kubona okulungileyo endikwenzela abantu bam, utsho uYehova; ngokuba wafundisa ukugwilika kuYehova.', 'XHO22'),
(28, 30, 1, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 30, 2, 'Utsho uYehova, uThixo kaSirayeli, ukuthi, Wabhale encwadini onke amazwi endiwathethileyo kuwe;', 'XHO22'),
(28, 30, 3, 'Ngokuba, uyabona, iyeza imihla, utsho uYehova, endiya kukubuyisa ukuthinjwa kwabantu bam amaSirayeli namaYuda,” utsho uYehova; yaye ndiza kubabuyisela kwilizwe endalinika ookhokho babo, yaye baya kulidla ilifa.', 'XHO22'),
(28, 30, 4, 'Ngawo la ke amazwi, awawathethayo uYehova ngoSirayeli noYuda.', 'XHO22'),
(28, 30, 5, 'Kuba uYehova wathi, Sive ilizwi eligubhisayo, elokunkwantya, asililo eloxolo.', 'XHO22'),
(28, 30, 6, 'Uyabuza ngoku, ubonile na ukuba indoda leyo, ikhulelwe? Yini na ukuba ndibone abafo bonke, izandla zabo zisemanqeni abo njengomfazi ozalayo, ubuso bonke buthe matshamatsha?', 'XHO22'),
(28, 30, 7, 'yho! ngokuba inkulu loo mini, akubangakho namnye unjengayo; lixesha lembandezelo kuYakobi; ke wena uya kukhululwa kuyo.', 'XHO22'),
(28, 30, 8, 'Kuba ngaloo mini, utsho uYehova wemikhosi, ndiya kuyaphula idyokhwe yakhe entanyeni yakho, ndiziqhawule izitropu zakho, bangabi samkhonza oonyana bolunye uhlanga;', 'XHO22'),
(28, 30, 9, 'bakhonze uYehova uThixo wabo, noDavide ukumkani wabo, endiya kubavelisela yena.', 'XHO22'),
(28, 30, 10, 'Ke wena musa ukoyika, wena mkhonzi wam Yakobi, utsho uYehova; musa ukoyika, Sirayeli; ngokuba, uyabona, ndiya kukuhlangula kwakude, nembewu yakho ezweni ethinjelwe kulo; yaye uYakobi uya kubuya aphumle, aphumle, kungabikho bani uya kumothusa.', 'XHO22'),
(28, 30, 11, 'Ngokuba ndinawe, utsho uYehova, ukuba ndikuhlangule; ukuba ndithe ndazitshabalalisa zonke iintlanga endakuphangalalisela kuzo, andiyi kutshabalalisa wena; kodwa ndokhalimela kude kube ngoku, ndingakushiyi msulwa.', 'XHO22'),
(28, 30, 12, 'Kuba utsho uYehova ukuthi, Inxeba lakho alinakuphiliswa, inxeba lakho libuhlungu.', 'XHO22'),
(28, 30, 13, 'Akukho bani unokulinda ukuba ukhonkxwe; awunayeza.', 'XHO22'),
(28, 30, 14, 'Bonke abakuthandayo bakulibele; abafuni Wena; ngokuba ndikubethile ngobetho lotshaba, ngokobetho lwabanobujorha, ngenxa yobuninzi bobubi bakho; ngokuba zininzi izono zenu.', 'XHO22'),
(28, 30, 15, 'Uyalila na ngenxa yokubandezeleka kwakho? Asikuko nokuba uyabulala umvandedwa wakho, ngenxa yobuninzi bobugwenxa bakho; ngenxa yobuninzi bezono zenu, ndenzé kwanjalo kuni.', 'XHO22'),
(28, 30, 16, 'Ngoko ke bonke abakudlayo baya kutshabalala; kwaye zonke iintshaba zakho ziya kuya ekuthinjweni, ngasinye sazo; yaye nabani na okuphangayo uya kuphangwa, nabo bonke abo bakuphangayo ndiya kubanikela babe lixhoba.', 'XHO22'),
(28, 30, 17, 'Ngokuba ndiya kukuphilisa, ndiya kuyiphilisa imivumbo yakho, utsho uYehova; ngokuba bathi ungowasemzini, besithi, iZiyon ayifunwa mntu.', 'XHO22'),
(28, 30, 18, 'Wathi uYehova, Yabona, ndiya kuzibuyisa iintente zikaYakobi, ndiya kuzibabalwa ngezakhiwo zakhe; yaye isixeko siya kwakhiwa phezu kwethemba laso, nebhotwe liya kuma endleleni yaso.', 'XHO22'),
(28, 30, 19, 'Kwaye ukusuka kubo kuya kuqala umbulelo nelizwi labo bavuyayo; ndibandise, bangabi mbalwa; Ndiya kuphinda ndibazukise, yaye abayi kujongelwa phantsi.', 'XHO22'),
(28, 30, 20, 'Oonyana babo boba njengokwamandulo, nebandla labo lomelele phambi kwam, ndibavelele bonke abaxini babo.', 'XHO22'),
(28, 30, 21, 'Iingangamsha zabo ziya kuvela kubo, umlawuli wabo aphume kubo; ndimsondeze, asondele kum; utsho uYehova.', 'XHO22'),
(28, 30, 22, 'Niya kuba ngabantu bam, mna ndibe nguThixo wenu;', 'XHO22'),
(28, 30, 23, 'Yabonani, umoya ovuthuzayo weNkosi, uya kuzalisa; liya kuwa phezu kweentloko zabangendawo;', 'XHO22'),
(28, 30, 24, 'Awuyi kubuya umsindo kaYehova, ade ayenze, ade ayimise iminkqangiyelo yentliziyo yakhe; ekupheleni kosuku kufuneka unikele ingqalelo kuyo.', 'XHO22'),
(28, 31, 1, 'Ngelo xesha, utsho uYehova, ndiya kuba nguThixo wezizwe zonke zakwaSirayeli, zibe ngabantu bam bona.', 'XHO22'),
(28, 31, 2, 'Wathi uYehova, Bababalwa entlango abantu abasindileyo ekreleni; uSirayeli, xa ndaya kumphumza.', 'XHO22'),
(28, 31, 3, 'uYehova wazityhila kum kwakudala, wathi, Ewe, ndikuthandile ngothando olungunaphakade; ngenxa yoko ndinitsalele imfesane.', 'XHO22'),
(28, 31, 4, 'Ndiya kubuya ndikwakhe, wakheke, ntombi inguSirayeli; uya kubuya uzihombise ngeentente zakho, uphume umemelele.', 'XHO22'),
(28, 31, 5, 'Uya kuqhubeka utyala izidiliya ezintabeni zakwaSamari; abalimi baya kutyala baze bazitye njengezinto eziqhelekileyo.', 'XHO22'),
(28, 31, 6, 'Ngokuba kuya kufika imini, abaya kumemelela ngayo abalusi ezintabeni zakwaEfrayim, besithi, Sukani nime, sinyuke siye eZiyon kuYehova, uThixo wethu.', 'XHO22'),
(28, 31, 7, 'Ngokuba utsho uYehova ukuthi, Vumani kuYakobi, gcobani phakathi kookumkani beentlanga; vakalisani, dumisani, nithi, Basindise, Yehova, abantu bakho, amasalela akwaSirayeli.', 'XHO22'),
(28, 31, 8, 'Yabona, ndiyabazisa bevela ezweni lasentla, ndibahlanganisele eziphelweni zehlabathi; isihlwele esikhulu siya kubuyela khona.', 'XHO22'),
(28, 31, 9, 'Baya kuza beneenyembezi, ndibakhokelele ngemithandazo; Ndiya kubakhapha emijelweni yamanzi, emendweni othe tye, abangayi kukhubeka kuwo; kuba ndinguyise kaSirayeli, yaye uEfrayim lizibulo lam.', 'XHO22'),
(28, 31, 10, 'Liveni ilizwi likaYehova, zintlanga, nilivakalise nakwiziqithi ezikude, uthi, Umchithachithi kaSirayeli uya kuwabutha, awagcine njengomalusi womhlambi wakhe.', 'XHO22'),
(28, 31, 11, 'Kuba uYehova wamhlangula uYakobi waza wamhlangula esandleni salowo unamandla kunaye.', 'XHO22'),
(28, 31, 12, 'Ngako oko baya kuza bacule ngeZiyon, begalela ukulunga kukaYehova, kunye nengqolowa, newayini entsha, neoli, nempahla emfutshane, neenkomo; umphefumlo wabo uya kuba njengomyezo onyakanyiswa yimvula; kwaye abayi kulila konke konke.', 'XHO22'),
(28, 31, 13, 'Iya kwandula intombi ibe nemihlali ekungqungqeni, amadodana namadoda amakhulu kunye;', 'XHO22'),
(28, 31, 14, 'Ndowanelisa imiphefumlo yababingeleli ngeoli, bahluthe abantu bam zizinto zam ezilungileyo; utsho uYehova.', 'XHO22'),
(28, 31, 15, 'Wathi uYehova, Kuvakele izwi eRama, isikhalo nesikhalo esikrakra; uRakeli, owayekhalela abantwana bakhe, akavuma kuthuthuzelwa ngenxa yabantwana bakhe, ngokuba babengekho.', 'XHO22'),
(28, 31, 16, 'Utsho uYehova ukuthi, Lithinteleni ilizwi lenu, lingakhali, ningalili amehlo enu, ngokuba umvuzo womsebenzi wenu uya kuba khona, utsho uYehova; yaye baya kubuya kwilizwe lotshaba.', 'XHO22'),
(28, 31, 17, 'Kunethemba ekupheleni kwakho, utsho uYehova, ukuba oonyana bakho baya kubuyela emideni yabo.', 'XHO22'),
(28, 31, 18, 'Ndimvile ngenene uEfrayim ekhala, esithi; Wandibetha, ndaqeqeshwa njengenkunzi yenkomo, engaqhelananga nedyokhwe; ngokuba wena, Yehova, unguThixo wam.', 'XHO22'),
(28, 31, 19, 'Ndathi ndakuzohlwaya, ndazohlwaya; ndathi xa ndiza kufa, ndabetha esinqeni sam; Ndadana, ewe, ndadana, ngokuba bendithwele ihlazo lobutsha bam.', 'XHO22'),
(28, 31, 20, 'Ngunyana wam na uEfrayim? Ndingumntwana onomtsalane? Kuba oko ndithethe kakubi ngaye, ndisamkhumbula; ke ngoko, isisu sam siphazamisekile nguye; inene, ndiya kuba nenceba kuye, utsho uYehova.', 'XHO22'),
(28, 31, 21, 'Zenzele imiqondiso yendlela, zenzele iimfumba eziphakamileyo; yibhekise intliziyo yakho emendweni, endleleni ohambe ngayo; buya, ntombi inguSirayeli, buyela emizini yakowenu.', 'XHO22'),
(28, 31, 22, 'Kunini na unxaxha, ntombi yotshaba? ngokuba uYehova udale into entsha ehlabathini: Umfazi uya kuchasa indoda.', 'XHO22'),
(28, 31, 23, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Noko ke, baya kuthetha olu lwimi ezweni lakwaYuda, nasezixekweni zalo, ekukubuyiseni kwam ukuthinjwa kwabo; uYehova akusikelele, wena sakhiwo sobulungisa, nawe ntaba engcwele!', 'XHO22'),
(28, 31, 24, 'Aya kuhlala amaYuda nemizi yawo yonke kunye, abalimi nabafuyi.', 'XHO22'),
(28, 31, 25, 'Kuba ndiyawanelisa umphefumlo otyhafileyo, yaye ndiyawanelisa wonke umphefumlo osentlungwini.', 'XHO22'),
(28, 31, 26, 'Ngaphezu koko, ndavuka, ndabona; ubuthongo bam bebumnandi kum.', 'XHO22'),
(28, 31, 27, 'Yabona, kuza imihla, utsho uYehova, endiya kuhlwayela ngayo imbewu yabantu nembewu yenkomo, utsho uYehova, indlu kaSirayeli nendlu kaYuda;', 'XHO22'),
(28, 31, 28, 'Kuya kuthi ke, ndibaphaphele, ukuba ndikrazule, ndiqhekeze, nditshabalalise, ndibacudise; ndobaphaphela ukuba ndakhe, ndityale; utsho uYehova.', 'XHO22'),
(28, 31, 29, 'Ngelo xesha akuyi kuba sathiwa, Aba ooyise badle iidiliya ezibolileyo, amazinyo oonyana ahexa;', 'XHO22'),
(28, 31, 30, 'elowo uya kufa ngenxa yobubi bakhe; umntu odla iidiliya ezimuncu, uya kuba buthelezi amazinyo akhe;', 'XHO22'),
(28, 31, 31, 'Yabona, kuza imihla, utsho uYehova, endiya kwenza ngayo umnqophiso omtsha nendlu kaSirayeli; nakwindlu kaYuda;', 'XHO22'),
(28, 31, 32, 'kungekhona ngenxa yomnqophiso endawenzela ooyise, ekubabambeni kwam ngesandla, ukuba ndibakhuphe ezweni laseYiputa; bawaphule umnqophiso wam, ndibe ndiyindoda kubo; utsho uYehova.', 'XHO22'),
(28, 31, 33, 'Nguwo ke lo umnqophiso, endiya kuwenza nendlu kaSirayeli; Emveni kwaloo mihla, utsho uYehova, ndiya kuwubeka umyalelo wam embilinini yabo, ndiwubhale ezintliziyweni zabo; yena uya kuba nguThixo wabo, bona babe ngabantu bam.', 'XHO22'),
(28, 31, 34, 'Abayi kuba safundisana elowo umzalwana wakhe, esithi, Mazini uYehova; ngokuba bonke bephela baya kundazi, kuthabathela komncinane kuse koyena mkhulu phakathi kwabo; ngokuba ndiya kubaxolela ubugwenxa babo ndingabi sasikhumbula isono sabo.', 'XHO22'),
(28, 31, 35, 'Utsho uYehova, onika ilanga libe sisikhanyiso emini, onika imithetho yenyanga neenkwenkwezi njengokukhanya ebusuku, uMlatyuzisi wolwandle, ekugqumeni kwamaza alo; NguYehova wemikhosi igama lakhe;', 'XHO22'),
(28, 31, 36, 'Ukuba ithe yasuka loo mithetho phambi kwam, utsho uYehova, bophela oonyana bakaSirayeli bangabi luhlanga phambi kwam ngamaxa onke.', 'XHO22'),
(28, 31, 37, 'Utsho uYehova ukuthi, Ukuba lingaba nokulinganiswa izulu phezulu, zibe nokulinganiswa iziseko zehlabathi phantsi komhlaba, nam ndobalahla yonke imbewu kaSirayeli, ngenxa yento yonke abayenzileyo; utsho uYehova.', 'XHO22'),
(28, 31, 38, 'Yabonani, iyeza imihla, utsho uYehova, oya kuthi umzi lowo wakhelwe uYehova, kuthabathele enqabeni ende kaHananeli, kuse esangweni lembombo;', 'XHO22'),
(28, 31, 39, 'luphume ulutya lokulinganisa phambi kwayo, uye endulini yeGarebhe, lujike luye ePooko;', 'XHO22'),
(28, 31, 40, 'Yonke intlambo yezidumbu nothuthu nawo onke amasimi ukuya kutsho entlanjeni yomsinga waseKidron, ukuya kutsho kwimbombo yeSango Lamahashe ngasempuma, yoba yinto engcwele kuYehova; aliyi kuphinda lonakale, lingabi sachithwa.', 'XHO22'),
(28, 32, 1, 'Ilizwi likaYehova elafikayo kuYeremiya, ngomnyaka weshumi kaZedekiya ukumkani wakwaYuda: loo mnyaka ke ngumnyaka weshumi elinesibhozo kaNebhukadenetsare.', 'XHO22'),
(28, 32, 2, 'Ngenxa yokuba ngelo xesha iintendelezo zokumkani waseBhabhiloni zaziyingqingile iYerusalem, yaye umprofeti uYeremiya wayevalelwe kwintendelezo yentolongo yendlu kakumkani wakwaYuda.', 'XHO22'),
(28, 32, 3, 'Ngenxa enokuba uZedekiya ukumkani wakwaYuda wambhalela, wathi, Yini na ukuba uprofete, usithi, Utsho uYehova ukuthi, Yabona, ndiyawunikela lo mzi esandleni sokumkani waseBhabheli, ukuba awuthimbe;', 'XHO22'),
(28, 32, 4, 'uZedekiya, ukumkani wakwaYuda, akayi kusinda esandleni samaKaledi, kodwa ngokuqinisekileyo uya kunikelwa esandleni sokumkani waseBhabhiloni yaye umlomo wakhe uya kuthetha umlomo nomlomo naye, namehlo akhe abone amehlo akhe;', 'XHO22'),
(28, 32, 5, 'amse uZedekiya eBhabheli, ahlale khona ndide ndimvelele, utsho uYehova; nokuba nithe nalwa namaKaledi, aniyi kuphumelela.', 'XHO22'),
(28, 32, 6, 'uYeremiya wathi: “Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(28, 32, 7, 'Yabona, uHanameli unyana kaShalum, uyihlokazi, uza kuwe, athi, Yithenge intsimi yam eseAnatoti, ngokuba kulunge kuwe ukuyithenga.', 'XHO22'),
(28, 32, 8, 'Weza kum uHanameli, unyana kabawokazi, phambi kweyadi yentolongo ngokwelizwi likaYehova, wathi kum, Yithenge intsimi yam eseAnatoti, esezweni lakwaBhenjamin; Ilifa lelakho, usindiso lolwakho; uzithengele yona. Ndaza ke ndazi ukuba lilizwi likaYehova elo.', 'XHO22'),
(28, 32, 9, 'Ndawuthenga umhlaba kuHanameli, unyana kabawokazi, eAnatoti, ndamnika iishekele zesilivere ezilishumi elinesixhenxe.', 'XHO22'),
(28, 32, 10, 'Ndabubhala ubungqina ndaza ndabuqinisekisa ndaza ndathabatha amangqina ndaza ndayilinganisela imali yakhe esikalini.', 'XHO22'),
(28, 32, 11, 'Ndaluthabatha ke ngoko uxwebhu lwentengo, lwatywinwa ngokusemthethweni, lwatywinwa ngokusemthethweni, ndaluvula.', 'XHO22'),
(28, 32, 12, 'uBharuki unyana kaNeriya, unyana kaMahaseya, ndamnika phambi kukaHanameli unyana kabawokazi, nasemehlweni amangqina abesayine incwadi yentengiso. Onke amaYuda awayephila kwinkundla yentolongo.', 'XHO22'),
(28, 32, 13, 'Ndamwisela umthetho uBharuki phambi kwawo, ndathi:', 'XHO22'),
(28, 32, 14, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Thabatha ezi ncwadi, incwadi yentengo, itywiniwe, ivuliwe; zibeke engqayini yodongwe, ukuze zihlale iintsuku ezininzi.', 'XHO22'),
(28, 32, 15, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Kuya kuthatyathwa izindlu, namasimi, nezidiliya, kweli lizwe;', 'XHO22'),
(28, 32, 16, 'Ndalunikela uxwebhu lwentengo kuBharuki unyana kaNeriya, ndathandaza kuYehova ndathi:', 'XHO22'),
(28, 32, 17, 'Owu Yehova! yabona, walenza izulu nehlabathi ngamandla akho amakhulu, nangesandla sakho esolukileyo, akukho ukuthintela.', 'XHO22'),
(28, 32, 18, 'Unemfesane kumawaka, ubuphindezela ubugwenxa booyise esondweni loonyana babo emva kwabo; igama lakhe nguThixo omkhulu, onamandla, uYehova wemikhosi;', 'XHO22'),
(28, 32, 19, 'Umkhulu ngomnqweno wakho, uyasebenza ngamandla, ngokuba amehlo akho aphezu kweendlela zonke zoonyana babantu, ukuba unike ulowo ngokwendlela yakhe, nangokweziqhamo zezenzo zakhe;', 'XHO22'),
(28, 32, 20, 'owenza imiqondiso nezimanga ezweni laseYiputa unanamhla, kumaSirayeli naphakathi kwezinye iintlanga; wazenzela igama njengoko kunjalo namhla;', 'XHO22'),
(28, 32, 21, 'Wabakhupha abantu bakho amaSirayeli kwilizwe laseYiputa ngemiqondiso nezimanga ngesandla esomeleleyo ngesandla esoluliweyo nangoloyiko olukhulu;', 'XHO22'),
(28, 32, 22, 'Wabanika eli lizwe wabafungelayo ooyise, ukuba uya kubanika, ilizwe elibaleka amasi nobusi;', 'XHO22'),
(28, 32, 23, 'Bangena ke, balihlutha; kodwa abaliphulaphula izwi lakho, abahambanga ngomyalelo wakho; abakwenza konke owabawisela umthetho ngako ukuba bakwenze; ngenxa yoko nibazisele obu bubi bonke.', 'XHO22'),
(28, 32, 24, 'Yabona, iintaba zingenile kulo mzi, ukuba ziwuthimbe; isixeko sanikelwa esandleni samaKaledi ayesilwa, ngenxa yekrele, nendlala, nendyikitya yokufa; kwaye khangela kwaye khangela.', 'XHO22'),
(28, 32, 25, 'Wathi kum, Nkosi Yehova, zithengele intsimi ngemali, ungqinise amangqina; ngenxa yokuba isixeko sasinikelwe esandleni samaKhaledi.', 'XHO22'),
(28, 32, 26, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 32, 27, 'Yabona, ndinguYehova, uThixo wenyama yonke; ingaba ikhona into efana nam?', 'XHO22'),
(28, 32, 28, 'Ngako oko, utsho uYehova ukuthi, Yabona, ndiyawunikela lo mzi esandleni samaKaledi, nasesandleni sikaNebhukadenetsare ukumkani waseBhabheli, ukuba awungene.', 'XHO22'),
(28, 32, 29, 'AmaKhaledi alwa nesi sixeko aya kuza asitshise esi sixeko aze asitshise ndawonye nezindlu aqhumisela phezu kophahla lukaBhahali yaye athululela iminikelo ethululwayo kwabanye oothixo ukuze andigculele.', 'XHO22'),
(28, 32, 30, 'Ngokuba oonyana bakaSirayeli noonyana bakaYuda baye besenza ububi bodwa phambi kwam kwasebuncinaneni babo; ngokuba oonyana bakaSirayeli bendiqumbisile ngezenzo zezandla zabo; utsho uYehova.', 'XHO22'),
(28, 32, 31, 'Ngenxa enokuba lo mzi waba yintlekisa kum ngenxa yomsindo wam nomsindo wam, kususela ekuwakhiweni kwawo kude kube namhla; ukuba ndiyisuse ebusweni bam,', 'XHO22'),
(28, 32, 32, 'ngenxa yobubi bonke boonyana bakaSirayeli, noonyana bakaYuda, ababenzayo ukuze bandiqumbise, bona, nookumkani babo, nabathetheli babo, nababingeleli babo, nabaprofeti babo, namadoda akwaYuda, nabemi basezweni iYerusalem.', 'XHO22'),
(28, 32, 33, 'Bandinikela ikhosi, abandinikela ikhosi; nangona ndandibafundisa ukuvuka ekuseni ndize ndibafundise, kodwa abazange baphulaphule ukuze bamkele umyalelo.', 'XHO22'),
(28, 32, 34, 'Babeka izinto zabo ezinezothe endlwini ebizwa ngegama lam, ukuze bayenze inqambi;', 'XHO22'),
(28, 32, 35, 'Bakha iindawo eziphakamileyo zikaBhahali kwintlambo yonyana kaHinom, ukuze batshise oonyana babo neentombi zabo emlilweni ngenxa kaMoleki; endingabawiselanga mthetho ngayo, engathanga qatha entliziyweni yam, ukuba benze eli masikizi, ukuba bawagrogrise amaYuda.', 'XHO22'),
(28, 32, 36, 'Kaloku ke, utsho uYehova, uThixo kaSirayeli, ukuthi, ngenxa yalo mzi, uthi ngawo, Uya kunikelwa esandleni sokumkani waseBhabheli ngekrele, nangendlala, nangendyikitya yokufa;', 'XHO22'),
(28, 32, 37, 'Yabona, ndiyababutha emazweni onke, endabachithachitha kuwo ngomsindo wam, nangomsindo wam, nangobushushu bam; ndiya kubabuyisela kule ndawo, ndibahlalise ngoxolo.', 'XHO22'),
(28, 32, 38, 'Kwaye baya kuba ngabantu bam, mna ndibe nguThixo wabo;', 'XHO22'),
(28, 32, 39, 'ndibanike intliziyo enye, nendlela enye, ukuze bandoyike imihla yonke, ngenxa yabo, nangenxa yabantwana babo emva kwabo;', 'XHO22'),
(28, 32, 40, 'Ndiya kubenzela umnqophiso ongunaphakade, ukuba ndingatyeki kubo ekwenzeni okulungileyo; ndibeke ukundoyika entliziyweni yabo, ukuze bangandishiyi.', 'XHO22'),
(28, 32, 41, 'Ewe, ndoba nemihlali ngabo, ndibenzele okulungileyo, ndibatyale kweli lizwe ngentliziyo yam yonke, nangomphefumlo wam wonke.', 'XHO22'),
(28, 32, 42, 'Ngokuba wathi uYehova, Njengoko ndabaziselayo aba bantu bonke obu bubi bukhulu kangaka, ndenjenjalo ukubazisela konke okulungileyo endikuthethayo ngabo.', 'XHO22'),
(28, 32, 43, 'Kuya kuthengwa imihlaba kweli lizwe nithi, Liyintlango, elingenamntu nankomo; wanikelwa esandleni samaKaledi.', 'XHO22'),
(28, 32, 44, 'Abantu baya kuthenga imihlaba ngemali baze batyikitye amaxwebhu baze bawatywine, bathabathe amangqina kwilizwe lakwaBhenjamin nakwiindawo ezijikeleze iYerusalem nakwizixeko zakwaYuda nakwizixeko ezisezintabeni nakwizixeko zakwaBhenjamin intlambo yomlambo oqukuqelayo nakwiidolophana ezikude kwintlambo yomhlaba nemizi engasezantsi, ngokuba ndiya kuliguqula iqashiso layo, utsho uYehova.', 'XHO22'),
(28, 33, 1, 'Kwafika ilizwi likaYehova kuYeremiya okwesibini, esentendelezweni yentolongo, lathi,', 'XHO22'),
(28, 33, 2, 'utsho uYehova, uMenzi wabo, uYehova, uMenzi wabo, ukuba akuqinise; uYehova ligama lakhe;', 'XHO22'),
(28, 33, 3, 'Ubundibizile, ndakusabela, ndikubonise izinto ezinkulu nezinqabileyo, ongazaziyo.', 'XHO22'),
(28, 33, 4, 'Ngokuba utsho uYehova, uThixo kaSirayeli, ngazo izindlu zalo mzi, nangezindlu zookumkani bakwaYuda, abawe ezintabeni, nalikrele;', 'XHO22'),
(28, 33, 5, 'Baza kulwa namaKaledi, kodwa ukuze bazalise izidumbu zabantu endababulala ngomsindo wam nangomsindo wam, nangenxa yobubi babo bonke ndabusithelisa ubuso bam kwesi sixeko.', 'XHO22'),
(28, 33, 6, 'Yabona, ndibazisela usindiso nonyango, ndibaphilise, ndibatyhilele uxolo oluninzi nenyaniso.', 'XHO22'),
(28, 33, 7, 'Ndiza kubabuyisa abathinjiweyo bakwaYuda nabathinjwa bakwaSirayeli ndize ndibakhe njengangaphambili.', 'XHO22'),
(28, 33, 8, 'Ndiya kubahlambulula ebubini babo bonke, abone ngabo kum; yaye ndiza kubaxolela bonke ubugwenxa babo abandone ngabo nabandone ngabo.', 'XHO22'),
(28, 33, 9, 'Kuya kuba ligama lam lokuvuya, indumiso, nembeko kum, phambi kwezizwe zonke zehlabathi eziya kukuva konke okulungileyo endiwenzela kona; yaye baya koyika baze bangcangcazele ngenxa yako konke okulungileyo nangenxa yayo yonke impumelelo endimenzela yona.', 'XHO22'),
(28, 33, 10, 'Utsho uYehova ukuthi, Kuya kubuya kuviwe kule ndawo, le nto nithi iya kuba yintlango, ingabi nankomo, emizini yakwaYuda, nasezitratweni zaseYerusalem, kube senkangala, ingenamntu, ingenamntu umhlali ongenasilwanyana;', 'XHO22'),
(28, 33, 11, 'Ilizwi lemihlali nezwi lemivuyo; izwi lomyeni nezwi lomtshakazi, nezwi labathi, Dumisani uYehova wemikhosi, ngokuba elungile uYehova; ngokuba ingunaphakade inceba yakhe, nabazisa imibingelelo yendumiso endlwini kaYehova. Ngokuba ndiya kukubuyisa ukuthinja kwelizwe njengoko bekunjalo ekuqaleni; utsho uYehova.', 'XHO22'),
(28, 33, 12, 'Utsho uYehova wemikhosi ukuthi, Kule ndawo ilinxuwa, ingenamntu, ingenankomo, nasemizaneni yonke, kuya kubuya kubekho abathengisi abalusela imihlambi yabo.', 'XHO22'),
(28, 33, 13, 'Emizini yasezintabeni, nasemizini yasezihlanjeni, nasemizini yelasezantsi, nasezweni lakwaBhenjamin, nakwee ngeenxa zonke eYerusalem, nasemizini yakwaYuda, iya kubuya idlule impahla emfutshane phantsi kwezandla zakhe oxela iindaba ezilungileyo yabonani, utsho uYehova.', 'XHO22'),
(28, 33, 14, 'Yabona, kuza imihla, utsho uYehova, endiya kulimisa ilizwi elilungileyo, endalithethayo kwindlu kaSirayeli nendlu kaYuda.', 'XHO22'),
(28, 33, 15, 'Ngaloo mihla nangelo xesha ndiya kumhlumisela uDavide iHlumelo lobulungisa; yaye uya kwenza okusesikweni nobulungisa ehlabathini.', 'XHO22'),
(28, 33, 16, 'Ngaloo mihla uya kukhululwa ngentlawulelo uYuda, neYerusalem yona iya kuhlala ikhuselekile; yaye lilo eli igama aya kubizwa ngalo: uYehova bubulungisa bethu.', 'XHO22'),
(28, 33, 17, 'Kuba kusithiwa nguYehova, uDavide akadinganga mntu wakuhlala etroneni yendlu kaSirayeli;', 'XHO22'),
(28, 33, 18, 'Kanjalo, ababingeleli babaLevi mabangabi nandoda phambi kwam esondeza iminikelo etshiswayo neminikelo yokudla okulula, enikela imibingelelo rhoqo.', 'XHO22'),
(28, 33, 19, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 33, 20, 'Utsho uYehova ukuthi, Ukuba uthe wawaphula umnqophiso wam wemini, nomnqophiso wam wobusuku, akwabakho mini nabusuku ngexesha labo;', 'XHO22'),
(28, 33, 21, 'Ke ngoko umnqophiso wam noDavide umkhonzi wam mawuwaphulwe, angabi nanyana ungukumkani etroneni yakhe; nabaLevi, nababingeleli, abakhonzi bam.', 'XHO22'),
(28, 33, 22, 'Njengokuba izulu lingenakubalwa, ingenakulinganiswa intlabathi yolwandle, ndiya kwenjenjalo ukuyandisa imbewu kaDavide, umkhonzi wam, nabaLevi abandikhonzayo.', 'XHO22'),
(28, 33, 23, 'Ngapha koko, kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 33, 24, 'Akukuboni na abakuthethayo aba bantu, xa babesithi, Izizukulwana ezibini, awazinyulayo uYehova, uzicekisile? bakubagxeka abantu bam, bangabi saba luhlanga phambi kwabo.', 'XHO22'),
(28, 33, 25, 'Wathi uYehova, Umnqophiso wam ubunjengemini nobusuku, ukuba andiyi kuyimisa imimiselo yezulu neyehlabathi;', 'XHO22'),
(28, 33, 26, 'Ndoyilahla ke nembewu kaYakobi noDavide umkhonzi wam, ukuba ndingathabathi namnye wasembewini yakhe oyinkosi yembewu ka-Abraham noIsake noYakobi; ngokuba ndiya kukubuyisa ukuthinjwa kwabo, nenceba yabo phezu kwabo.', 'XHO22'),
(28, 34, 1, 'Kwafika ilizwi likaYehova kuYeremiya, akubon’ ukuba uNebhukadenetsare ukumkani waseBhabheli, enempi yakhe yonke, nezikumkani zonke zelimiweyo phantsi kobukumkani bakhe, ziyilwa neYerusalem zonke iintlanga nemizi yayo, lisithi,', 'XHO22'),
(28, 34, 2, 'Utsho uYehova, uThixo kaSirayeli, ukuthi, Yiya, uthethe kuZedekiya ukumkani wakwaYuda, uthi kuye, Utsho uYehova ukuthi, Yabona, ndikuthabathe; Uyabona, lo mzi ndiya kuwunikela esandleni sokumkani waseBhabheli, awutshise ngomlilo.', 'XHO22'),
(28, 34, 3, 'Akuyi kusinda esandleni sakhe, kodwa uya kubanjwa, unikelwe esandleni sakhe; amehlo akho aya kuwabona amehlo okumkani waseBhabhiloni, yaye uya kuthetha ngomlomo wakho nangomlomo wakho, yaye uya kuya eBhabhiloni.', 'XHO22'),
(28, 34, 4, 'Live ilizwi likaYehova, Zedekiya, kumkani wakwaYuda; Utsho uYehova ngani ukuthi, Aniyi kufa likrele,', 'XHO22'),
(28, 34, 5, 'uya kufa unoxolo; ngokutshiselwa kooyihlo, ookumkani bokuqala ababekho ngaphambi kwakho bakutshise ngesiqhumiso; baya kukhalela wena, besithi, Oh! kuba ndithethe ilizwi,’ utsho uYehova.', 'XHO22'),
(28, 34, 6, 'Wawathetha ke uYeremiya umprofeti onke la mazwi kuZedekiya ukumkani wakwaYuda, eYerusalem.', 'XHO22'),
(28, 34, 7, 'Xa umkhosi wamajoni kakumkani waseBhabhiloni wawusilwa neYerusalem nazo zonke izixeko zakwaYuda ezazisele, iLakishe neAsheki, ngenxa yokuba ezi zixeko zokusabela zazisele kwizixeko zakwaYuda.', 'XHO22'),
(28, 34, 8, 'Lilo eli ilizwi elafikayo likaYehova kuYeremiya, emveni kokuba ukumkani uZedekiya wenza umnqophiso nabantu bonke ababeseYerusalem, ukuba bakhale ukuba bakhululwe;', 'XHO22'),
(28, 34, 9, 'elowo alikhulule ikhoboka lakhe, elowo alikhulule ikhobokazana lakhe elingumHebhere nokuba ngumHebherekazi; ukuze kungabikho namnye umkhonzayo, ongumYuda ongumzalwana wakhe.', 'XHO22'),
(28, 34, 10, 'Zakuva zonke iinkosana nabantu bonke ababengene emnqophisweni ukuba elowo akhulule ikhoboka lakhe, elowo ikhobokazana lakhe, angabi sabakho, bathobela; bahamba ke.', 'XHO22'),
(28, 34, 11, 'Kodwa emva koko bajika baza babuyisa ikhoboka nekhobokazana ababebakhulule, babacinezela ngamakhoboka namakhobokazana.', 'XHO22'),
(28, 34, 12, 'Lafika ke ilizwi likaYehova kuYeremiya, livela kuYehova, lisithi:', 'XHO22'),
(28, 34, 13, 'Utsho uYehova, uThixo kaSirayeli, ukuthi, Ndenza umnqophiso nooyihlo, ekubakhupheni kwam ezweni laseYiputa, endlwini yobukhoboka, ndathi,', 'XHO22'),
(28, 34, 14, 'Ekupheleni kweminyaka esixhenxe, elowo makashiye umzalwana wakhe ongumHebhere lowo kwathengiswa kuwe; ukuba uthe wakhonza iminyaka emithandathu, makahambe eze kuwe;', 'XHO22'),
(28, 34, 15, 'Nangoku ke najika nenza okuthe tye emehlweni am ngokuthi, elowo makakhulule ummelwane wakhe; yaye wenza umnqophiso phambi kwam endlwini ebizwa ngegama lam.', 'XHO22'),
(28, 34, 16, 'Nabuya ke nalenza inqambi igama lam, nabuyela elowo ikhoboka lakhe, nabuyela elowo ikhobokazana lakhe, enalikhulula ngokuqhutywa yintliziyo, nazinikela kubo, ukuba babe ngabakhonzi kuni, nabakhonzi benu.', 'XHO22'),
(28, 34, 17, 'Ngako oko, utsho uYehova ukuthi, Anindiphulaphulanga ngokuthi nibize inkululeko, elowo ngenxa yomzalwana wakhe; yabonani, ndininika inkululeko, utsho uYehova, yokuphuma ekreleni, nasendyikityeni yokufa, nasendlaleni; ndiya kukuphakamisa ezikumkanini zonke zehlabathi;', 'XHO22'),
(28, 34, 18, 'Ndowanikela amadoda awaphule umnqophiso wam, angawagcinanga amazwi omnqophiso awawenzayo phambi kwam, abeya kulityatya ithole, acanda phakathi kwalo;', 'XHO22'),
(28, 34, 19, 'neenkosana zakwaYuda neenkosana zaseYerusalem, abalungiseleli nababingeleli nabo bonke abantu belizwe abadlula phakathi kwenxalenye yethole lenkomo;', 'XHO22'),
(28, 34, 20, 'ndibanikele esandleni seentshaba zabo, nasesandleni sabafuna umphefumlo wabo; izidumbu zabo zibe kukudla kweentaka zezulu nokwamarhamncwa.', 'XHO22'),
(28, 34, 21, 'Ndiza kumnikela uZedekiya ukumkani wakwaYuda nabathetheli bakhe esandleni seentshaba zabo nasesandleni sabafuna umphefumlo wabo nasesandleni somkhosi wokumkani waseBhabhiloni ophuma kuni.', 'XHO22'),
(28, 34, 22, 'Yabona, ndiwisa umthetho, utsho uYehova, ndibabuyisele kulo mzi; baya kulwa nawe, bathimbe, bakutshise ngomlilo; nezixeko zakwaYuda ndiya kuzenza zibe yinkangala ephanzileyo, engenammi.', 'XHO22'),
(28, 35, 1, 'Kwafika ilizwi likaYehova kuYeremiya ngemihla kaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, lisithi,', 'XHO22'),
(28, 35, 2, 'Yiya endlwini yamaRekabhi, uthethe nawo, uwangenise endlwini kaYehova, kwelinye igumbi, uwaseze iwayini.', 'XHO22'),
(28, 35, 3, 'Ndandula ke ndathabatha uYashaniya unyana kaYeremiya unyana kaHabhasiniya nabazalwana bakhe noonyana bakhe bonke nendlu yonke yamaRekabhi;', 'XHO22'),
(28, 35, 4, 'Ndabazisa endlwini kaYehova, kwigumbi loonyana bakaHanan, unyana kaIgedaliya, umfo wakwaThixo, elalikwigumbi leenkosana elaliphezu kwegumbi likaMahaseya unyana wakhe kaSalum, yaba umgcini-sango:', 'XHO22'),
(28, 35, 5, 'Ndabeka phambi koonyana bendlu yamaRekabhi iimbiza ezizele yiwayini nezitya ndaza ndathi kubo: “Selani iwayini.', 'XHO22'),
(28, 35, 6, 'Bathi bona, Asiyi kusela wayini, ngokuba uYonadabhi unyana kaRekabhi, ubawo, wasiwisela umthetho, wathi, Ze ningaseli wayini naphakade, nina noonyana benu;', 'XHO22'),
(28, 35, 7, 'ningakhi ndlu, ningahlwayeli mbewu, ningatyala nesidiliya, ningabi nanto yakwenza; hlalani ezintenteni yonke imihla yenu; ukuze niphile imihla emininzi emhlabeni eningabaphambukeli kuwo.', 'XHO22'),
(28, 35, 8, 'Saliphulaphula ke ilizwi likaYonadabhi unyana kaRekabhi, ubawo, kuko konke awasiwisela umthetho ngako ukuba singaseli wayini yonke imihla yethu, thina, nabafazi bethu, noonyana bethu, neentombi zethu;', 'XHO22'),
(28, 35, 9, 'nokuba bangasakhela izindlu ukuba sihlale kuzo; kananjalo asinasidiliya, nantsimi nambewu;', 'XHO22'),
(28, 35, 10, 'Ke thina sasihlala ezintenteni, saphulaphula, senza ngako konke awasiwisela umthetho ngako uYonadabhi ubawo.', 'XHO22'),
(28, 35, 11, 'Kwathi, ekungeneni kukaNebhukadenetsare ukumkani waseBhabheli kweli lizwe, sathi, Yizani, singene eYerusalem ngenxa yemikhosi yamaKaledi, ngenxa yemikhosi yama-Aram; hlalani eYerusalem.', 'XHO22'),
(28, 35, 12, 'Kwafika ilizwi likaYehova kuYeremiya, lisithi:', 'XHO22'),
(28, 35, 13, 'Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ewe, uthi kumadoda akwaYuda, nakubemi baseYerusalem, Aniwuphulaphulanga na lo mthetho, ukuba niwaphulaphule amazwi am? utsho uYehova.', 'XHO22'),
(28, 35, 14, 'Azaliseka amazwi kaYonadabhi, unyana kaRekabhi, awabawisela oonyana bakhe ukuba bangaseli wayini; ngokuba abaseli nanamhla, bawuthobela umthetho kayise kodwa anindiphulaphulanga.', 'XHO22'),
(28, 35, 15, 'Ndathumela kuni bonke abakhonzi bam, abaprofeti, abavuka kusasa, babathuma, besithi, Guqukani, elowo endleleni yakhe embi, nilungise iintlondi zenu, ningalandeli thixo bambi; musani ukuzenza khonzani, nihlale ezweni endalinika nina nooyihlo; kodwa anindiphulaphulanga, anaphulaphula nam.', 'XHO22'),
(28, 35, 16, 'Ngokuba oonyana bakaYonadabhi unyana kaRekabhi bawugcinile umthetho kayise, abebawisele umthetho ngawo; ke olu hlanga alundiphulaphulanga.', 'XHO22'),
(28, 35, 17, 'Ngako oko utsho uYehova, uThixo wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, ndiwazisela amaYuda, nabemi bonke baseYerusalem, bonke ububi endibuthethileyo kubo, ngenxa enokuba ndithethile kubo kodwa wabatshabalalisa abeva; Ndababiza, kodwa abazange baphendule.', 'XHO22'),
(28, 35, 18, 'Wathi uYeremiya kwindlu yamaRekabhi, Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ngokuba uwuthobele umthetho kaYonadabhi uyihlo, wayigcina yonke imithetho yakhe, wenza ngako konke abewise umthetho ngako;', 'XHO22'),
(28, 35, 19, 'Ngako oko utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, uYonadabhi, unyana kaRekabhi, akayi kuswela mntu wokuma phambi kwam ngonaphakade.', 'XHO22'),
(28, 36, 1, 'Ngomnyaka wesine wobukumkani bukaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, kwafika ilizwi likaYehova kuYeremiya, lisithi,', 'XHO22'),
(28, 36, 2, 'Thabatha incwadi esongwayo, ubhale kuyo onke amazwi endiwathethileyo kuwe, ngokusingisele kuSirayeli, nakuYuda, nakuzo zonke iintlanga, kususela kwimini endathetha ngayo kuwe, kususela kwimihla kaYosiya kude kube ngoku usuku.', 'XHO22'),
(28, 36, 3, 'Mhlawumbi indlu kaYuda ingabuva bonke ububi endicinga ukubenza kubo; ukuze babuye elowo endleleni yakhe embi; ukuze ndibuxolele ubugwenxa babo nesono sabo.', 'XHO22'),
(28, 36, 4, 'uYeremiya wabiza uBharuki unyana kaNeriya, wawabhala encwadini uBharuki onke amazwi kaYehova, awaphuma emlonyeni kaYeremiya, abemxelele yona.', 'XHO22'),
(28, 36, 5, 'uYeremiya wamwisela uBharuki umthetho, esithi, Mna ndivalelwe; andiyi kungena endlwini kaYehova;', 'XHO22'),
(28, 36, 6, 'Yiya ke, uwalese eloo ncwadi uyibhale emlonyeni wam, amazwi kaYehova, apho bawevayo abantu endlwini kaYehova ngomhla wokuzila ukudla; funda apho asaphulaphule onke amaYuda asuka ezilalini zawo.', 'XHO22'),
(28, 36, 7, 'Mhlawumbi baya kuwuzisa umthandazo wabo phambi koYehova baze babuye, ngamnye endleleni yakhe ephosakeleyo, ngenxa yokuba ingqumbo nomsindo awuthethileyo uYehova ngolu hlanga mkhulu.', 'XHO22'),
(28, 36, 8, 'uBharuki unyana kaNeriya wenza ngokomyalelo kaYeremiya umprofeti, wawalesa encwadini amazwi kaYehova endlwini kaYehova.', 'XHO22'),
(28, 36, 9, 'Ngomnyaka wesihlanu wobukumkani bukaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, ngenyanga yesithoba, kwavakaliswa ukuba bazile ukudla bonke abantu baseYerusalem, nabo bonke abantu abaphuma ezweni lakwaYuda iilali zesixeko. uYuda waya eYerusalem.', 'XHO22'),
(28, 36, 10, 'Wawalesa uBharuki amazwi kaYeremiya encwadini, endlwini kaYehova, egumbini likaGemariya, unyana kaShafan, umbhali, entendelezweni ephezulu, ekungeneni kwesango elitsha lendlu kaYehova. uYehova, phambi kwabantu bonke.', 'XHO22'),
(28, 36, 11, 'Waweva uMika, unyana kaGemariya, unyana kaShafan, onke amazwi kaYehova encwadini.', 'XHO22'),
(28, 36, 12, 'Ngoko wehla waya endlwini kakumkani kwigumbi lonobhala, waza wazibona zonke iinkosana zihleli apho, uElishama unobhala noDelaya unyana kaShemaya noElinatan unyana ka-Akibhore noGemariya unyana kaShafan. noZedekiya unyana kaHananiya, nabathetheli bonke;', 'XHO22'),
(28, 36, 13, 'Wawathetha uMika kuwo onke amazwi abewevile ekuleseni kukaBharuki incwadi phambi kwabantu.', 'XHO22'),
(28, 36, 14, 'Bonke abathetheli bathumela kuBharuki uYehudi unyana kaNetaniya, unyana kaShelemiya, unyana kaKushi, besithi, Incwadi leyo uyilesileyo phambi kwabantu, yiphathe ngesandla sakho, uze. uBharuki unyana kaNeriya wayithabatha incwadi ngesandla sakhe, weza kubo.', 'XHO22'),
(28, 36, 15, 'Bathi kuye, Khawuhlale, uyilese ezindlebeni zethu. Wafunda ke uBharuki ezindlebeni zabo.', 'XHO22'),
(28, 36, 16, 'Kwathi, bakuweva onke loo mazwi, boyika bobabini, bathi kuBharuki, Lo mazwi siya kumxelela ukumkani onke.', 'XHO22'),
(28, 36, 17, 'Kwaye bambuza uBharuki baza bathi: “Sixelele, wawabhala njani na onke la mazwi aphuma emlonyeni wakhe?', 'XHO22'),
(28, 36, 18, 'Wabaphendula uBharuki, wathi, Loo mazwi wawathetha kum onke loo mazwi ngomlomo wakhe, ndawabhala encwadini ngeinki.', 'XHO22'),
(28, 36, 19, 'Bathi abathetheli kuBharuki, Hamba uye kuzimela, wena noYeremiya; kwaye musa ukwazisa nabani na apho ukhoyo.', 'XHO22'),
(28, 36, 20, 'Bangena entendelezweni yokumkani, bayibeka incwadi esongwayo egumbini likaElishama umbhali, bawathetha onke amazwi ukumkani ubephulaphule.', 'XHO22'),
(28, 36, 21, 'Ukumkani wathuma uYehudi, ukuba ayithabathe incwadi esongwayo; wayithabatha ke egumbini likaElishama umbhali. uYehudi wafunda apho wayephulaphule ukumkani, nazo zonke iinkosana zazimi phambi kokumkani.', 'XHO22'),
(28, 36, 22, 'Ukumkani wayehleli endlwini yasebusika ngenyanga yesithoba, umlilo usavutha phambi kwakhe.', 'XHO22'),
(28, 36, 23, 'Kwathi, uYehudi wathabatha izikhwebu ezintathu nokuba zine, wazinqunqa ngemela, waziphosa emlilweni obekusasa, yada yawela indlebe yonke emlilweni obukuyo nakusasa.', 'XHO22'),
(28, 36, 24, 'Abankwantyanga, abazikrazulanga iingubo zabo, kwanabakhonzi bokumkani ababeweva loo mazwi onke.', 'XHO22'),
(28, 36, 25, 'Ke uElinatan noDelaya noGemariya bambongoza ukumkani ukuba angayitshisi incwadi esongwayo, akabaphulaphula.', 'XHO22'),
(28, 36, 26, 'Ukumkani wabawisela umthetho ooYerakemeli unyana wokumkani, noSeraya unyana ka-Asriyeli, noShelemiya unyana ka-Abhedeli, ukuba bambambe uBharuki umbhali, noYeremiya umprofeti; uYehova wabazimeza.', 'XHO22'),
(28, 36, 27, 'Kwafika ilizwi likaYehova kuYeremiya emva kokuba ukumkani eyitshisile incwadi esongwayo namazwi abewabhalile uBharuki emlonyeni kaYeremiya, lisithi:', 'XHO22'),
(28, 36, 28, 'Thabatha enye incwadi, ubhale kuyo onke amazwi okuqala, abesencwadini yokuqala awayitshisayo uYehoyakim ukumkani wakwaYuda.', 'XHO22'),
(28, 36, 29, 'Uze uthi kuYehoyakim ukumkani wakwaYuda, Utsho uYehova ukuthi, Utsho uYehova ukuthi, Utsho uYehova ukuthi, Utsho uYehova ukuthi, NdinguYehova; Uyitshisile incwadi, wathi, Yini na ukuba ubhale kuyo, usithi, Ukumkani waseBhabheli uya kuza na, alonakalise eli lizwe, atshabalalise abantu neenkomo kulo?', 'XHO22'),
(28, 36, 30, 'Ngako oko, wathetha uYehova ngoYehoyakim ukumkani wakwaYuda, wathi, Akuyi kubakho bani uya kuhlala etroneni kaDavide; isidumbu sakhe siya kulahlelwa elwandle emini, ebusuku yingqele.', 'XHO22'),
(28, 36, 31, 'Kwaye ndiya kumvelela yena nezicaka zakhe nezicaka zakhe ngenxa yobugwenxa babo; yaye ndiza kubazisela bona nabemi baseYerusalem namadoda angendawo akwaYuda konke endikuthethileyo ngabo; ke abeva.', 'XHO22'),
(28, 36, 32, 'uYeremiya wathabatha enye incwadi esongwayo, wayinika uBharuki unyana kaNeriya, umbhali; owawabhalayo kuyo, ephuma emlonyeni kaYeremiya, onke amazwi encwadi awayitshisayo uYehoyakim ukumkani wakwaYuda ngomlilo; kongezelelwa kuwo amazwi amaninzi anjengawo.', 'XHO22'),
(28, 37, 1, 'Ukumkani uZedekiya, unyana kaYosiya, waba ngukumkani esikhundleni sikaKoniya unyana kaYehoyakim, owabekwa nguNebhukadenetsare ukumkani waseBhabheli ezweni lakwaYuda.', 'XHO22'),
(28, 37, 2, 'Kodwa yena nabakhonzi bakhe okanye abantu belizwe abazange bawaphulaphule amazwi kaYehova awayewathethile ngoYeremiya umprofeti.', 'XHO22'),
(28, 37, 3, 'Ukumkani uZedekiya wathuma ooYehukali unyana kaShelemiya, noZefaniya, unyana kaMahaseya, umbingeleli, kuYeremiya umprofeti, esithi, Masithandaze kuYehova uThixo wethu.', 'XHO22'),
(28, 37, 4, 'uYeremiya ebengena ephuma ebantwini, ngokuba bebengamfakanga entolongweni.', 'XHO22'),
(28, 37, 5, 'Emva koko imikhosi kaFaro yemka eYiputa, yaye akuva elo lizwi amaGalati awayengqinge iYerusalem, ayishiya iYerusalem.', 'XHO22'),
(28, 37, 6, 'Kwafika ilizwi likaYehova kuYeremiya umprofeti, lisithi:', 'XHO22'),
(28, 37, 7, 'Utsho uYehova, uThixo kaSirayeli, ukuthi, Uze uthi kukumkani wakwaYuda, lowo uthume kum ukuba andibuze; Yabonani, imikhosi kaFaro ephume isiza kuninceda, iya kubuyela ezweni layo eYiputa;', 'XHO22'),
(28, 37, 8, 'AmaKaledi aya kubuya alwe nesi sixeko, asithimba, asitshise ngomlilo.', 'XHO22'),
(28, 37, 9, 'Wathi uYehova, Musani ukuzikhohlisa, nithi, AmaKaledi aya kusishiya ngonaphakade, ngokuba akayi kumka.', 'XHO22'),
(28, 37, 10, 'Kuba, ukuba nibe niyixabele yonke impi yamaKaledi alwayo nani, kwasala kubo abangxwelerhiweyo, kodwa bebeya kuma elowo ententeni yakhe, bawutshise lo mzi ngomlilo.', 'XHO22'),
(28, 37, 11, 'Ekuphumeni komkhosi wamaKaledi eYerusalem ngenxa yokoyika umkhosi kaFaro.', 'XHO22'),
(28, 37, 12, 'Wemka uYeremiya eYerusalem, esiya ezweni lakwaBhenjamin, ukuba azinyulele phakathi kwabantu.', 'XHO22'),
(28, 37, 13, 'Kwathi, esesangweni lakwaBhenjamin, nanko kukho umthetheli wabasiki, ogama linguIriya, unyana kaShelemiya, unyana kaHananiya; wambamba uYeremiya umprofeti, wathi, Uwela esandleni samaKaledi.', 'XHO22'),
(28, 37, 14, 'Wathi uYeremiya, Bubuxoki; andizinikezele kumaKaledi. Akazange amphulaphule, ngoko uIriya wamthabatha uYeremiya wamsa kwiinkosana.', 'XHO22'),
(28, 37, 15, 'Ngoko iinkosana zamqumbela uYeremiya, zamoyisa zaza zamfaka entolongweni endlwini yonobhala uYonatan, ngenxa yokuba wayeyenze indlu yentolongo.', 'XHO22'),
(28, 37, 16, 'Wangena uYeremiya equleni nasezindlwini; wahlala khona uYeremiya imihla emininzi.', 'XHO22'),
(28, 37, 17, 'Wathumela ukumkani uZedekiya ukuba amkhuphe; Ukumkani wambuza endlwini yakhe ngasese, wathi, Kukho lizwi na liphuma kuYehova? Wathi uYeremiya, Ewe, ngokuba usithi, Uya kunikelwa esandleni sokumkani waseBhabheli.', 'XHO22'),
(28, 37, 18, 'Ngaphezu koko, uYeremiya wathi kuKumkani uZedekiya: “Ndone ngantoni na kuwe okanye kubakhonzi bakho okanye kolu hlanga, ukuba nindifake entolongweni nje?', 'XHO22'),
(28, 37, 19, 'Baphi na abaprofeti benu ababeniprofetela, besithi, Ukumkani waseBhabheli akayi kunihlasela, akayi kulihlasela eli lizwe?', 'XHO22'),
(28, 37, 20, 'Ngako oko, yiva, nkosi yam, nkosi yam; Ngamana umthandazo wam wamkelekile emehlweni akho; ukuze ungandivumeli ndibuyele endlwini kaYonatan umbhali, ukuze ndingafele khona.', 'XHO22'),
(28, 37, 21, 'Ke kaloku ukumkani uZedekiya wayalela uYeremiya ukuba abuyele kwiYadi yentolongo, amnike iqhekeza lesonka esisesitratweni sombhaki yonke imihla, de siphele isonka esixekweni. Ngoko uYeremiya wahlala eyadini yentolongo.', 'XHO22'),
(28, 38, 1, 'Ke kaloku uShefatiya unyana kaMatan, noGedaliya, unyana kaPashkure, noYukali unyana kaShelemiya, noPashure unyana kaMalekiya, baweva amazwi abewathetha uYeremiya ebantwini bonke, esithi,', 'XHO22'),
(28, 38, 2, 'Utsho uYehova ukuthi, Bonke abaseleyo kulo mzi bobulawa likrele, nayindlala, nayindyikitya yokufa; ophuma aye kumaKaledi wophila; ngokuba umphefumlo wakhe uya kuba lixhoba, aphile.', 'XHO22'),
(28, 38, 3, 'Utsho uYehova ukuthi, Esi sixeko siya kunikelwa ngonaphakade esandleni somkhosi wokumkani waseBhabhiloni, yaye ngokuqinisekileyo uya kusithimba.', 'XHO22'),
(28, 38, 4, 'Bathi ke abathetheli kukumkani, Siyakucela kuwe, ukuba mayibulawe le ndoda, ngokuba wenjenje ukuxengxeza izandla zamagorha aseleyo kulo mzi, nezandla zabantu bonke, ngokuthetha ngokwaloo mazwi; yada indoda yenza njalo engafuni okulungileyo kwesi sizwe, kodwa ezimbi.', 'XHO22'),
(28, 38, 5, 'Wathi ukumkani uZedekiya, Yabonani, usezandleni zenu, ngokuba akangokumkani onako ukwenza nantoni na kuni.', 'XHO22'),
(28, 38, 6, 'Bandula ke bamthabatha uYeremiya baza bamphosa equleni likaMalekiya unyana kakumkani kakumkani, elalikwintendelezo yentolongo; bamehlisa uYeremiya ngeentsontelo. Bekungekho manzi equleni, ludaka lodwa; ngoko ke, uYeremiya watshona eludakeni.', 'XHO22'),
(28, 38, 7, 'Xa uEbhedi-melege, umKushi, omnye wabalungiseleli ababesendlwini kakumkani, weva ukuba uYeremiya ufakwe equleni; Ukumkani wahlala esangweni lakwaBhenjamin;', 'XHO22'),
(28, 38, 8, 'Waphuma uEbhedi-meleki endlwini yokumkani, wathetha kukumkani, esithi,', 'XHO22'),
(28, 38, 9, 'Nkosi yam kumkani, la madoda enze ububi ngako konke akwenzileyo kuYeremiya umprofeti, lowo amphosayo equleni; unjengobulawa yindlala kuloo ndawo akuyo, ngokuba akusekho sonka kulo mzi.', 'XHO22'),
(28, 38, 10, 'Ukumkani wamwisela umthetho uEbhedi-meleki wakwaKushi, wathi, Thabatha apha amadoda amashumi mathathu, umkhuphe uYeremiya umprofeti emhadini, engekafi.', 'XHO22'),
(28, 38, 11, 'Wawathabatha ke amadoda lawo uEbhedi-melege, wangena endlwini yokumkani ngaphantsi kwengxowa yobuncwane, wathabatha khona izikhondo zelaphu neempahla ezibolileyo, wazithobela emhadini kuYeremiya ngeentsontelo.', 'XHO22'),
(28, 38, 12, 'Wathi uEbhedi-meleki wakwaKushi kuYeremiya, Beka la malaphu amadala namalaphu abolileyo ezingxoweni zakho phantsi kweentambo. Wenjenjalo uYeremiya.', 'XHO22'),
(28, 38, 13, 'Bandula ke bamtsala uYeremiya ngeentsontelo baza bamkhupha emhadini, yaye uYeremiya waqhubeka ehlala ephambi kweyadi yentolongo.', 'XHO22'),
(28, 38, 14, 'Wathumela ukumkani uZedekiya, wamzisa kuye uYeremiya umprofeti, ekumnyango wesithathu wendlu kaYehova; Wathi ukumkani kuYeremiya, Ndiya kukubuza zwi-nye; ungafihli nto kum.', 'XHO22'),
(28, 38, 15, 'Wathi uYeremiya kuZedekiya, Ukuba ndithe ndakuxelela, akuyi kundibulala na? nokuba ndikucebise anindiva na?', 'XHO22'),
(28, 38, 16, 'Ukumkani uZedekiya wamfungela uYeremiya ngasese, esithi, uYehova ovayo, owasenzela lo mphefumlo, andiyi kukubulala, andiyi kukunikela esandleni sala madoda angawuboniyo umphefumlo wakho.', 'XHO22'),
(28, 38, 17, 'Wathi uYeremiya kuZedekiya, Utsho uYehova, uThixo wemikhosi, uThixo kaSirayeli, ukuthi, Yabona, ndinguYehova, uThixo wemikhosi, Thixo kaSirayeli. Ukuba uthe waphuma waphuma waya kubathetheli bokumkani waseBhabheli, wophila umphefumlo wakho, nalo mzi ungatshiswa ngomlilo; niphile wena nendlu yakho.', 'XHO22'),
(28, 38, 18, 'Ke ukuba anithanga niphume niye kubathetheli bokumkani waseBhabheli, lo mzi wonikelwa esandleni samaKaledi, awutshise ngomlilo, ke nina aniyi kusinda esandleni sawo.', 'XHO22'),
(28, 38, 19, 'Wandula ke uKumkani uZedekiya wathi kuYeremiya: “Ndiyawoyika amaYuda athe awela kumaKhaledi, hleze andinikele esandleni sawo aze andigculele.', 'XHO22'),
(28, 38, 20, 'Wathi uYeremiya, Akayi kukusindisa. Liphulaphuleni ilizwi likaYehova endinixelela lona; uya kuphila kakuhle, uphile umphefumlo wakho.', 'XHO22'),
(28, 38, 21, 'Ke ukuba uthe akwavuma ukuphuma, lilo eli ilizwi andibonise lona uYehova:', 'XHO22'),
(28, 38, 22, 'Yabona, bonke abafazi abaseleyo endlwini yokumkani wakwaYuda baya kusiwa kwiinkosana zokumkani waseBhabheli, bathi bona, Abahlobo bakho bakuxabele, bakoyisa; zitshone eludakeni iinyawo zenu, zijike.', 'XHO22'),
(28, 38, 23, 'Ke ngoko, bonke abafazi bakho nabantwana bakho baya kubanikela kumaKaledi; yaye akuyi kusinda esandleni sawo, yaye ngokuqinisekileyo uya kubanjwa sisandla sokumkani waseBhabhiloni; awutshise lo mzi ngomlilo.', 'XHO22'),
(28, 38, 24, 'Wathi uZedekiya kuYeremiya, La mazwi makangaziwa mntu, ukuze ungafi.', 'XHO22'),
(28, 38, 25, 'Ukuba ke bathe bakuva abathetheli ukuba ndithethile kuwe, beza kuwe, bathi kuwe, Khawusixelele oko ukuthethileyo kukumkani; musa ukusifihla kuthi, asiyi kukubulala; nento athe wakuxelela yona ukumkani:', 'XHO22'),
(28, 38, 26, 'uthi kubo, Ndisicelile isicelo sam kukumkani, ukuze angandivumeli ndibuyele endlwini kaYonatan, ndifele khona.', 'XHO22'),
(28, 38, 27, 'Beza kuYeremiya bonke abathetheli, bambuza; wabaxelela ngokwaloo mazwi onke abemwisele umthetho ngawo ukumkani. Bayeka ukuthetha naye; ngokuba loo nto ayiqondwanga.', 'XHO22'),
(28, 38, 28, 'uYeremiya wahlala phambi kweyadi yentolongo kwada kwayimini eyathinjwa ngayo iYerusalem, waza waba lapho ekuthinjweni kweYerusalem.', 'XHO22'),
(28, 39, 1, 'Ngomnyaka wesithoba wobukumkani bukaZedekiya ukumkani wakwaYuda, ngenyanga yeshumi, uNebhukadenetsare ukumkani waseBhabheli wayingqinga iYerusalem, nomkhosi wakhe wonke, wayingqinga.', 'XHO22'),
(28, 39, 2, 'Ngomnyaka weshumi elinamnye kaZedekiya, ngenyanga yesine, ngosuku lwesithoba enyangeni leyo, wawiswa umzi.', 'XHO22'),
(28, 39, 3, 'Zangena ke zonke iinkosana zokumkani waseBhabheli, zahlala esangweni eliphakathi: ooNergali-saretsere, noSamgarnebho, noSarsegim, noRabhesarisi, noNergali-tseretsere, noRabhege, nabanye abathetheli bokumkani waseBhabheli.', 'XHO22'),
(28, 39, 4, 'Wababona uZedekiya ukumkani wakwaYuda, namadoda onke okulwa, basaba baphuma phakathi komzi ebusuku ngendlela yomyezo wokumkani, ngesango eliphakathi kweendonga zombini, baphuma phandle kwindlela ecacileyo.', 'XHO22'),
(28, 39, 5, 'Umkhosi wamaKaledi wabasukela, wamthimba uZedekiya ezinkqantosini zaseYeriko; emva kokuba embambile, amzisa kuNebhukadenetsare, ukumkani waseBhabhiloni, eRibhela kwilizwe laseHamati, apho waba ngumgwebi wakhe.', 'XHO22'),
(28, 39, 6, 'Wandula ukumkani waseBhabhiloni wababulala oonyana bakaZedekiya uRibhela phambi kwamehlo akhe; zabulala zonke izidwangube zakwaYuda ukumkani waseBhabheli.', 'XHO22'),
(28, 39, 7, 'Ngaphezu koko, wamkrukra amehlo uZedekiya, wamkhonkxa ngemixokelelwane ukuze amse eBhabhiloni.', 'XHO22'),
(28, 39, 8, 'AmaKaledi ayitshisa indlu yokumkani, nezindlu zabantu, azidiliza neendonga zeYerusalem.', 'XHO22'),
(28, 39, 9, 'Wawathabatha uNebhuzaradan umthetheli wabasiki, wabathabatha amasalela abantu ababesele esixekweni, nabaseleyo, wabafudusela eBhabheli, nabanye abantu;', 'XHO22'),
(28, 39, 10, 'Ke uNebhuzaradan, umthetheli wabasiki, wabashiya ezweni lakwaYuda inxenye yabantu abazizisweli, ababengenanto, wabanika izidiliya namasimi ngelo xesha.', 'XHO22'),
(28, 39, 11, 'uNebhukadenetsare ukumkani waseBhabheli wamwisela umthetho uNebhuzaradan, umthetheli wabasiki, ngoYeremiya, esithi,', 'XHO22'),
(28, 39, 12, 'Mbambe, umgcine, ungamenzi nto imbi; kodwa nenze kuye njengoko athethayo kuni.', 'XHO22'),
(28, 39, 13, 'Wathuma uNebhuzaradan umthetheli wabasiki, benoNebhusabhanon, noRabhesarisi, noNergali-saretsere, umRabhege, nabathetheli bonke bokumkani waseBhabheli;', 'XHO22'),
(28, 39, 14, 'Bathumela ke, bamthabatha uYeremiya entendelezweni yentolongo, bamnikela kuGedaliya, unyana ka-Ahikam, unyana kaShafan, ukuba ambuyisele endlwini yakhe; ke ngoko, wahamba waya kuhlala phakathi kwabantu.', 'XHO22'),
(28, 39, 15, 'Kwafika ilizwi likaYehova kuYeremiya ngoxa wayevalelwe eyadini yentolongo, lisithi:', 'XHO22'),
(28, 39, 16, 'Yiya uthethe kuEbhedi-melege umKushi, uthi, Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Utsho uYehova wemikhosi, uThixo kaSirayeli; Yabona, ndiwushumayeza lo mzi ububi, asiwulungele; ziya kuzaliseka phambi kwenu ngaloo mini.', 'XHO22'),
(28, 39, 17, 'Ke mna ndiya kunisindisa ngaloo mini,’ utsho uYehova, yaye akuyi kunikelwa ezandleni zabantu oboyikayo.', 'XHO22'),
(28, 39, 18, 'Kuba ngokuqinisekileyo ndiya kukusindisa, yaye akuyi kuwa likrele, kodwa umphefumlo wakho uya kuba lixhoba lakho, ngenxa yokuba ukholose ngam,’ utsho uYehova.', 'XHO22'),
(28, 40, 1, 'Kwafika ilizwi likaYehova kuYeremiya, ekumziseni kwakhe uNebhuzaradan, umthetheli wabasiki, ekhonkxiwe phakathi kwabathinjwa bonke baseYerusalem nabakwaYuda, ababefudusiwe kude ekuthinjweni eBhabhiloni.', 'XHO22'),
(28, 40, 2, 'Umthetheli wabasiki wamthabatha ke uYeremiya, wathi kuye, uYehova uThixo wakho uthethe obu bubi ngale ndawo;', 'XHO22'),
(28, 40, 3, 'Waza ke uYehova wakwenza njengoko wakuthethayo; ngokuba nonile kuYehova, analiphulaphula izwi lakhe; ngenxa yoko yaba le nto.', 'XHO22'),
(28, 40, 4, 'Yabona, namhlanje ndiya kuyikhulula imixokelelwane ebisesandleni sakho; Ukuba kuthe kwalunga kuwe ukuya nam eBhabheli, yiza; ndoninyamekela kakuhle; ke ukuba kubi emehlweni akho ukuya nam eBhabheli, yiyeke; uyabona, ihlabathi liphela liphambi kwakho; apho kuthe kwalunga emehlweni akho ukuba uye khona, yiya khona.', 'XHO22'),
(28, 40, 5, 'Uthe engekabuyi, wathi, Buyela kuGedaliya, unyana ka-Ahikam, unyana kaShafan, lowo ukumkani waseBhabheli ammiseleyo ukuba abe ngumphathi wemizi yakwaYuda, uhlale naye kude kube sekufeni izizwe; apho kubonakala kufanelekileyo ukuba uye khona. Ingqwayi-ngqwayi yabalindi yamnika ukutya nomvuzo, yamkhulula.', 'XHO22'),
(28, 40, 6, 'uYeremiya waya kuGedaliya unyana ka-Ahikam eMizpa; waya wahlala naye phakathi kwabantu ababesele elizweni.', 'XHO22'),
(28, 40, 7, 'Beva bonke abathetheli beintendelezo zelizwe, bona namadoda abo, ukuba ukumkani waseBhabheli umise uGedaliya unyana ka-Ahikam, ukuba abe ngumlawuli welizwe, umise amadoda, nabafazi, nabantwana ekunyamekeleni kwakhe nabasweleyo belizwe, ababengabathinjelwa eBhabheli;', 'XHO22'),
(28, 40, 8, 'Beza ke kuGedaliya uMizpa, onguIshmayeli, unyana kaNetaniya, noYohanan noYonatan, oonyana bakaKareha, noSeraya, unyana kaTanumet, noonyana bakaEfayi, noMonetofa, noYeshaniya unyana ka. Owu waseMahaka, bona namadoda abo.', 'XHO22'),
(28, 40, 9, 'uGedaliya unyana ka-Ahikam, unyana kaShafan, wabafungela namadoda abo, esithi, Musani ukoyika ukuwakhonza amaKaledi; hlalani kweli lizwe, nikhonze ukumkani waseBhabheli, konilungela.', 'XHO22'),
(28, 40, 10, 'Mna ke, yabona, ndihlala eMizpa ukuze ndikhonze amaKhaledi aya kuza kuthi; kodwa wena, qokelela iwayini neziqhamo zasehlotyeni neoli uze uzigalele ezityeni zakho uze uhlale kwizixeko zakho ozoyisile.', 'XHO22'),
(28, 40, 11, 'Kwangokunjalo, eva onke amaYuda awayekwaMowabhi, noonyana baka-Amon, namaEdom, nabebekumazwe onke, ukuba ukumkani waseBhabheli uwashiyile amasalela akwaYuda, nokuba umisele unyana kaGedaliya ukuba abe likrele ityala kubo ka-Ahikam, unyana kaShafan;', 'XHO22'),
(28, 40, 12, 'Ke onke amaYuda abuya kuzo zonke iindawo awayethinjelwe kuzo aza eza kwilizwe lakwaYuda kuGedaliya eMizpa aqokelela iwayini eninzi neziqhamo zasehlotyeni ezininzi.', 'XHO22'),
(28, 40, 13, 'Ngaphezu koko, uYohanan unyana kaKareya nabo bonke abaphathi-mkhosi ababekwelo lizwe beza kuGedaliya eMizpa.', 'XHO22'),
(28, 40, 14, 'Wathi kuye, Uyazi na ukuba uBhahali, ukumkani woonyana baka-Amon, uthume uIshmayeli unyana kaNetaniya, ukuba akubulale? Kodwa uGedaliya unyana ka-Ahikam akazange abakholelwe.', 'XHO22'),
(28, 40, 15, 'Waza uYohanan unyana kaKareya wathetha kuGedaliya ngasese eMizpa, wathi: “Mandimbulale uIshmayeli unyana kaNetaniya, kungabikho bani waziyo; Yini na ukuba ade akubulale, ukuze kuvakaliswe onke amaYuda ahlanganiseneyo kuwe, atshatyalaliswe amasalela akwaYuda?', 'XHO22'),
(28, 40, 16, 'Wathi uGedaliya unyana ka-Ahikam kuYohanan unyana kaKareha, Musa ukuyenza loo nto, ngokuba uthetha ngobuqhophololo ngoIshmayeli.', 'XHO22'),
(28, 41, 1, 'Ngenyanga yesixhenxe, kweza uIshmayeli unyana kaNetaniya, unyana kaElishama, owendlu yokumkani, nabathetheli bokumkani, enamadoda alishumi, kuGedaliya unyana ka-Akikam, eMitspe; yaye apho badla isonka ndawonye eMisepa.', 'XHO22'),
(28, 41, 2, 'Wesuka ke uIshmayeli unyana kaNetaniya, namadoda alishumi abenaye, bambulala uGedaliya, unyana ka-Ahikam, unyana kaShafan, ngentshuntshe; bambulala, lowo ubemwisele ukumkani waseBhabheli yiba yirhuluneli ilizwe uye.', 'XHO22'),
(28, 41, 3, 'uIshmayeli wawabulala onke amaYuda abenaye, noGedaliya, noMitspa, namaKaledi afumanekayo khona, namadoda okulwa.', 'XHO22'),
(28, 41, 4, 'Ngosuku lwesibini emva kokumbulala kwakhe uGedaliya, kwakungekho mntu waziyo,', 'XHO22'),
(28, 41, 5, 'kweza abanye abavela kwaShekem, eShilo naseSamariya, amadoda angamashumi asibhozo, echetyiwe iindevu zawo, neempahla zawo zikrazulwe yaye zichetyiwe, ephethe iminikelo neziqhumiso ezandleni zawo, ukuze azizise endlwini kaYehova.', 'XHO22'),
(28, 41, 6, 'Waphuma eMizpa uIshmayeli unyana kaNetaniya, eya kuwakhawulela, emana emana edanduluka; waqubisana nawo, wathi kuwo, Yizani kuGedaliya unyana ka-Ahikam.', 'XHO22'),
(28, 41, 7, 'Bakufika phakathi esixekweni, uIshmayeli unyana kaNetaniya wababulala waza wabaphosa emhadini, yena namadoda awayenaye.', 'XHO22'),
(28, 41, 8, 'Ke kuwo kwafunyanwa amadoda alishumi, awathi kuIshmayeli, Musa ukusibulala, ngokuba sinobuncwane kweli lizwe: ingqolowa, nerhasi, neoli, nobusi. Wayindulula, akababulala phakathi kwabazalwana babo.', 'XHO22'),
(28, 41, 9, 'Umhadi awazilahlela kuwo uIshmayeli zonke izidumbu zamadoda awawabulalayo ngenxa kaGedaliya, ngumhadi awawenzayo ukumkani uAsa, ngenxa yokumoyika kwakhe uBhasha ukumkani wakwaSirayeli; waza uIshmayeli unyana kaNetaniya wayizalisa ngababuleweyo.', 'XHO22'),
(28, 41, 10, 'Waza uIshmayeli wabathimba yonke intsalela yabantu ababeseMizpa, iintombi zikakumkani nabo bonke abantu ababesele eMizpa, abo uNebhuzaradan umphathi wamafanankosi wababeka phantsi kolawulo lukaGedaliya unyana ka-Ahikam; noIshmayeli unyana waseNetaniya wabafudusa, wemka waya koonyana baka-Amon.', 'XHO22'),
(28, 41, 11, 'Weva uYohanan unyana kaKareha, nabathetheli bonke bentendelezo, ababenaye, bonke ububi abenzileyo uIshmayeli unyana kaNetaniya.', 'XHO22'),
(28, 41, 12, 'Bawathabatha onke amadoda, baya kulwa noIshmayeli unyana kaNetaniya, bamfumana emanzini amaninzi aseGibheyon.', 'XHO22'),
(28, 41, 13, 'Kwathi, akumbona uYohanan unyana kaKariya, nabathetheli bonke bemikhosi ababenaye, onke amadoda abenoIshmayeli, avuya.', 'XHO22'),
(28, 41, 14, 'Ajika ke onke amadoda awayemthimbile uIshmayeli eMizpa, abuya eza kuYohanan unyana kaKareha.', 'XHO22'),
(28, 41, 15, 'Ke uIshmayeli unyana kaNetaniya wabaleka, enamadoda asibhozo kuYohanan, waya koonyana baka-Amon.', 'XHO22'),
(28, 41, 16, 'uYohanan unyana kaKareha wathabatha kunye naye bonke abathetheli bempi ababenaye, namasalela onke abantu, awabakhululayo wabakhululayo kuIshmayeli unyana kaNetaniya, eMizpa, ekuzaleni kwakhe uGedaliya unyana wakhe ka-Akikame, owabulawayo ewe, amadoda okulwa, nabafazi, nabantwana, namathenwa, awayebabuyisele eGibheyon;', 'XHO22'),
(28, 41, 17, 'Ngoko bemka baya kuhlala kumzi waseKihamam kufuphi neBhetelehem ukuze bangene eYiputa.', 'XHO22'),
(28, 41, 18, 'Ngenxa yamaKhaledi, ngenxa yokuba ayewoyika, ekubeni uIshmayeli unyana kaNetaniya wamxabelayo uGedaliya unyana ka-Akimkam, lowo wayemiswe ngukumkani waseBhabhiloni ukuba abe yirhuluneli yelizwe.', 'XHO22'),
(28, 42, 1, 'Beza ke bonke abathetheli bamaqela, ooYohanan unyana kaKareha, noIshaniya unyana kaHoshaya, nabantu bonke, kwathabathela komncinane kwesa komkhulu;', 'XHO22'),
(28, 42, 2, 'Wathi kuYeremiya umprofeti: “Masikholeke kuwe isicelo sethu, usithandazele kuYehova uThixo wakho nangenxa yala masalela onke; (kuba simbalwa kwabaninzi, njengoko asibonayo amehlo akho;)', 'XHO22'),
(28, 42, 3, 'Ukuze uYehova uThixo wakho asibonise indlela esinokuhamba ngayo nento esinokuyenza.', 'XHO22'),
(28, 42, 4, 'Wathi uYeremiya umprofeti kubo, Ndivile; yabonani, ndiya kuthandaza kuYehova uThixo wenu ngokwamazwi enu; nento yonke aya kukuphendula yona uYehova, ndonixelela; Andiyi kukumisa nganto.', 'XHO22'),
(28, 42, 5, 'Bathi bona kuYeremiya, uYehova woba lingqina elinyanisileyo, elithembekayo, ngathi, ukuba asithanga sikwenze konke, awokuthumayo kuthi uYehova uThixo wakho.', 'XHO22'),
(28, 42, 6, 'Nokuba lilungile okanye libi, siya kuliphulaphula ilizwi likaYehova uThixo wethu esikuthumela kuye; ukuze izinto zisihambele kakuhle, ukuba siyaliphulaphula ilizwi likaYehova uThixo wethu.', 'XHO22'),
(28, 42, 7, 'Emva kweentsuku ezilishumi lafika ilizwi likaYehova kuYeremiya.', 'XHO22'),
(28, 42, 8, 'Wabiza uYohanan unyana kaKareha, nabathetheli bonke bamatyala ababenaye, nabantu bonke, kwathabathela komncinane kwesa komkhulu;', 'XHO22'),
(28, 42, 9, 'Wathi kubo, Utsho uYehova, uThixo kaSirayeli, enindithumele kuye, ukuba ndiyise isicelo senu kuye;', 'XHO22'),
(28, 42, 10, 'Ukuba nithe nabuya nahlala kweli lizwe, ndonakha, ndinganigungxuli; ngokuba ndiyazohlwaya ngenxa yobubi endibenzileyo kuni.', 'XHO22'),
(28, 42, 11, 'Musani ukumoyika ukumkani waseBhabheli, enimoyikayo; Musani ukumoyika, utsho uYehova, ngokuba ndinani, ukuba ndinihlangule, ndinisindise esandleni sakhe.', 'XHO22'),
(28, 42, 12, 'Ndiya kuba nenceba kuni, ukuze abe nenceba kuni, anibuyisele ezweni lenu.', 'XHO22'),
(28, 42, 13, 'Ke ukuba nithe, Asihlali kweli lizwe, asivumi ukuliphulaphula ilizwi likaYehova uThixo wenu;', 'XHO22'),
(28, 42, 14, 'Yithi, Hayi; Ke siya kuya ezweni laseYiputa, apho singayi kubona mfazwe khona, singayi kusiva isandi sesigodlo, singalambelanga sonka; siya kuhlala khona;', 'XHO22'),
(28, 42, 15, 'Liveni ke ilizwi likaYehova, masalela akwaYuda; Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Ukuba uthe wabubhekisa ubuso bakho eYiputa ngonaphakade, waphambukela khona;', 'XHO22'),
(28, 42, 16, 'Liya kwandula ke ikrele eniloyikayo lifike kuni apho ezweni laseYiputa, nendlala eniyoyikayo inilandele apho eYiputa; uya kufela khona.', 'XHO22'),
(28, 42, 17, 'Kuya kuba njalo nakumadoda onke abubhekise ubuso bawo eYiputa, ukuba aphambukele khona; baya kufa likrele, nayindlala, nayindyikitya yokufa; yaye akuyi kubakho namnye kubo oya kusala okanye asinde kwintlekele endiya kuyizisela yona.', 'XHO22'),
(28, 42, 18, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Njengoko waphalazekayo ubushushu bam nobushushu bam phezu kwabemi baseYerusalem; wophalazelwa ke umsindo wam phezu kwenu, ekungeneni kwenu eYiputa; abe lihlazo, nesimanga, nentshabhiso, nesikizi; kwaye anisayi kuphinda niyibone le ndawo kwakhona.', 'XHO22'),
(28, 42, 19, 'Utshilo uYehova ngani, masalela akwaYuda; Musani ukuya eYiputa; yazi ngokuqinisekileyo kuba ndikulumkisile namhlanje.', 'XHO22'),
(28, 42, 20, 'Ngenxa enokuba besuka bankwantya ezintliziyweni zenu, ekundithumeleni kuYehova uThixo wenu, nisithi, Sithandazele kuYehova uThixo wethu; yaye njengako konke aya kukuthetha uYehova uThixo wethu, sixelele oku, yaye siya kukwenza.', 'XHO22'),
(28, 42, 21, 'Kwaye ngoku, ndikuxelele ngayo namhlanje; kodwa aniliphulaphulanga ilizwi likaYehova uThixo wenu, nako konke awandithumayo ngako kuni.', 'XHO22'),
(28, 42, 22, 'Ke kaloku yazini ngokuqinisekileyo ukuba niya kufa likrele, ngendlala nangendyikitya yokufa kuloo ndawo nifuna ukuya kuyo nize nihlale njengabaphambukeli.', 'XHO22'),
(28, 43, 1, 'Kwathi, akugqiba uYeremiya ukuwathetha ebantwini bonke, onke amazwi kaYehova uThixo wabo, abemthumele kubo uYehova uThixo wabo, onke la mazwi.', 'XHO22'),
(28, 43, 2, 'Bathi ooAzariya unyana kaHoshaya, noYohanan unyana kaKareha, namadoda onke akhukhumeleyo, kuYeremiya, wathi, Uyaxoka; uYehova uThixo wethu akakuthumanga ukuba uthi, Musani ukuya eYiputa ukuba niphambukele khona;', 'XHO22'),
(28, 43, 3, 'Noko ke, uBharuki unyana kaNeriya wathi kuwe nxamnye nathi, ‘Sizinikele esandleni samaKhaledi, ukuze asibulale asithabathele ekuthinjweni eBhabhiloni.', 'XHO22'),
(28, 43, 4, 'uYohanan unyana kaKareya nazo zonke iintloko zomkhosi nabo bonke abantu abazange baliphulaphule ilizwi likaYehova lokuba bahlale kwilizwe lakwaYuda.', 'XHO22'),
(28, 43, 5, 'Kodwa uYohanan unyana kaKareya nabo bonke abaphathi-mkhosi bathabatha onke amasalela akwaYuda awayebuyile kuzo zonke iintlanga awayegxothelwe kuzo ukuba aye kuhlala ezweni lakwaYuda;', 'XHO22'),
(28, 43, 6, 'amadoda, nabafazi, nabantwana, neentombi zokumkani, nabo bonke ooNebhuzaradan, umthetheli wabasiki, noGedaliya unyana ka-Ahikam, unyana kaShafan, noYeremiya umprofeti, noBharuki unyana kaNeriya; abo bashiyekileyo.', 'XHO22'),
(28, 43, 7, 'Bafika ke ezweni laseYiputa, ngokuba bengaliphulaphulanga ilizwi likaYehova; bafika ke eTagpannesi.', 'XHO22'),
(28, 43, 8, 'Kwafika ilizwi likaYehova kuYeremiya Tagepanese, lisithi:', 'XHO22'),
(28, 43, 9, 'Thabatha ngesandla sakho amatye amakhulu, uze ulufihle udongwe ezikweni lezitena, ekungeneni kwendlu kaFaro waseTagepanese, phambi kwamehlo amadoda akwaYuda;', 'XHO22'),
(28, 43, 10, 'Wathi kubo, Utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yabonani, ndinguYehova, uThixo kaSirayeli; Yabona, ndithumela ukuba kuthatyathwe uNebhukadenetsare ukumkani waseBhabheli, umkhonzi wam, ndiyibeke itrone yakhe phezu kwala matye ndiwaqhushekileyo; uya kuyitwabulula intente yakhe yobukumkani phezu kwabo.', 'XHO22'),
(28, 43, 11, 'Uya kuthi ke akufika aloyise ilizwe laseYiputa, asindise abo bafe befe; kwaye abo bafanele ukuthinjwa baya ekuthinjweni; yaye abo bafanelwe likrele bathabatha ikrele.', 'XHO22'),
(28, 43, 12, 'Ndiya kuzitshisa izindlu zoothixo baseYiputa; uya kubatshisa, abathinjiweyo; uya kumka khona unoxolo.', 'XHO22'),
(28, 43, 13, 'Uya kuzitshabalalisa izigodo zaseBhete-shemeshe, ezisezweni laseYiputa; nezindlu zoothixo baseYiputa uya kuzitshisa ngomlilo.', 'XHO22'),
(28, 44, 1, 'Ilizwi elafikayo kuYeremiya ngokusingisele kumaYuda onke abehleli ezweni laseYiputa, abehleli eMigdoli, naseTagepanese, naseNofu, nasezweni lasePatrosi, lalisithi,', 'XHO22'),
(28, 44, 2, 'utsho uYehova wemikhosi, uThixo kaSirayeli; Nibubonile bonke ububi endayiziselayo iYerusalem, nemizi yonke yakwaYuda; yabona ke namhla kusenkangala, akuhlali mntu khona.', 'XHO22'),
(28, 44, 3, 'Ngenxa yobubi ababenzayo ngokundicekisa ngokutshisa isiqhumiso nangokukhonza thixo bambi ababengabazi, bona okanye nina okanye ooyihlo.', 'XHO22'),
(28, 44, 4, 'Ndathumela kuni bonke abakhonzi bam, abaprofeti, ndavuka kusasa, ndabathuma, ndathi, Musani ukuwenza la masikizi, ndiwathiyileyo.', 'XHO22'),
(28, 44, 5, 'Kodwa abevanga, abazinqumla iindlebe zabo, ukuba babuye ebubini babo, ukuze isiqhumiso sinyuke siye thixweni bambi.', 'XHO22'),
(28, 44, 6, 'Ngoko ke baphalazwa umsindo wam nobushushu bam ezixekweni zakwaYuda nasezitratweni zaseYerusalem zaba senkangala, njengoko kunjalo namhla.', 'XHO22'),
(28, 44, 7, 'Ngako oko utsho uYehova, uThixo wemikhosi, uThixo kaSirayeli; Yenzani ke obu bubi bukhulu emphefumlweni wenu, ngokutshabalalisa indoda, nomfazi, umntwana nosana, ezweni lakwaYuda, kungasali namnye;', 'XHO22'),
(28, 44, 8, 'Ngenxa enokuba nindiqumbise ngezenzo zezandla zenu, ngokuqhumisela thixweni bambi ezweni laseYiputa, enanihleli kulo, ukuze niziqhayise, nibe sisiqalekiso nesingcikivo ezintlangeni zonke zeentlanga emhlabeni?', 'XHO22'),
(28, 44, 9, 'Nizilibele na izinto ezingendawo zooyihlo, izinto ezingendawo zookumkani bakwaYuda nezenzo ezingendawo zabafazi babo nezenzo ezingendawo zabafazi benu ababenzayo kwilizwe lakwaYuda nasezitratweni zelizwe lakwaYuda ilizwe laseYerusalem?', 'XHO22'),
(28, 44, 10, 'Abakhange bathotywe de kube namhla, yaye abazange boyike yaye abahambanga emthethweni wam nangemiyalelo yam emiselweyo endayibeka phambi kwenu’ phambi kookhokho benu.', 'XHO22'),
(28, 44, 11, 'Ngoko ke utsho uNdikhoyo onamandla onke, uThixo kaSirayeli. Uyabona, ndikhangele kuwe ukuba wenze ububi, uwatshabalalise onke amaYuda.', 'XHO22'),
(28, 44, 12, 'Ndiya kuwathabatha amasalela akwaYuda, abubhekise ubuso bawo ezweni laseYiputa ukuba aphambukele khona, atshabalale onke, awe ezweni laseYiputa; baya kutshatyalaliswa likrele nayindlala; baya kufa likrele nayindlala, kuthabathela komncinane kuse komkhulu; ibe yintlekisa, ummangaliso, nentshabhiso, nehlazo.', 'XHO22'),
(28, 44, 13, 'Kuba ndiza kubohlwaya abemi belizwe laseYiputa njengoko ndayivelelayo iYerusalem ngekrele nangendlala nangendyikitya yokufa.', 'XHO22'),
(28, 44, 14, 'Ngokokuze kungasindi namnye kumasalela akwaYuda, azileyo ukuba aphambukele ezweni laseYiputa, ukuba abuyele ezweni lakwaYuda, angxamele ukubuyela kulo, ukuba ahlale khona; akukho bani uya kubuya ngaphandle kwabo basabileyo.', 'XHO22'),
(28, 44, 15, 'Amphendula uYeremiya onke amadoda, abesazi ukuba abafazi bawo baqhumisela thixweni bambi, nabafazi bonke ababemi khona, abantu bonke ababehleli ezweni laseYiputa, ababehleli ezweni laseYiputa, basePatrosi, bathi:', 'XHO22'),
(28, 44, 16, 'Ke lona ilizwi enilithethileyo kuthi egameni likaYehova, asiyi kukuphulaphula.', 'XHO22'),
(28, 44, 17, 'Noko ke, yonke into ephuma emlonyeni wethu, siya kuyitshisa ngomsi ukumkanikazi wezulu, sizigalele iziselo zakhe njengoko senjenjalo, thina, noobawo, nookumkani bethu, nabathetheli bethu; izixeko kwaYuda nasezitratweni zaseYerusalem; ngokuba sasinokutya okuninzi ngelo xesha, kwaye sasingcathu, singaboni bubi.', 'XHO22'),
(28, 44, 18, 'Kodwa ukususela oko sayekayo ukuqhumisela kukumkanikazi wezulu nokumthululela iminikelo ethululwayo, sasiswele yonke into yaye satshatyalaliswa likrele nendlala.', 'XHO22'),
(28, 44, 19, 'Xa saqhumisela kukumkanikazi wezulu saza samgalela umnikelo othululwayo, sasimenzela isonka sokumnqula nokumthulula nomnikelo wakhe othululwayo, sasingengobayeni bethu na?', 'XHO22'),
(28, 44, 20, 'Wathi uYeremiya ebantwini bonke, kumadoda nakubafazi, nakubantu bonke abamphendulayo, wathi,', 'XHO22'),
(28, 44, 21, 'Isiqhumiso enasizisa kwizixeko zakwaYuda nasezitratweni zaseYerusalem, nina nookhokho benu nookumkani benu neenkosana zenu nabantu belizwe, uYehova akazange azikhumbule, nam andizange ndingene ingqondo yakhe?', 'XHO22'),
(28, 44, 22, 'ukuze uYehova angabi saba nako ukukunyamezela ngenxa yobubi bezenzo zenu nangenxa yezinto ezicekisekayo enazenzayo; ngenxa yeso sizathu, ilizwe lakho libe yinkangala, isimanga, nesiqalekiso, lingenammi, njengoko linjalo namhlanje.', 'XHO22'),
(28, 44, 23, 'Ngenxa yokuba naphakamisa isiqhumiso nona kuYehova, analiphulaphula ilizwi likaYehova, anahamba ngomthetho wakhe nangemiyalelo yakhe emiselweyo nangobungqina bakhe; ke ngoko, obu bubi buwile njengokuba bunjalo namhlanje.', 'XHO22'),
(28, 44, 24, 'Ngaphezu koko, uYeremiya wathi kubo bonke abantu nakubo bonke abafazi: “Liveni ilizwi likaYehova, nonke nina maYuda akwilizwe laseYiputa!', 'XHO22'),
(28, 44, 25, 'utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Nina nabafazi benu niyathetha ngomlomo wenu, nangezandla zenu nikuzàlise, nisithi, Masenze izibhambathiso zethu, esabhambathisa ngazo, zokuqhumisela kukumkanikazi wezulu, simthululele iminikelo ethululwayo:', 'XHO22'),
(28, 44, 26, 'zimiseni nizimise kambe izibhambathiso zenu; zenzeni nizenze kambe izibhambathiso zenu. Ngako oko liveni ilizwi likaYehova, nonke maYuda, ahleliyo ezweni leYiputa: Yabonani, ndifungé igama lam elikhulu, utsho uYehova, inene, aliyi kuba sabizwa igama lam ngumlomo kabani wakwaYuda ezweni lonke leYiputa, esithi, Ihleli nje iNkosi.', 'XHO22'),
(28, 44, 27, 'Yabonani, ndiwaphaphela ukuba kube kubi, kungalungi, agqityelwe onke amadoda akwaYuda ezweni laseYiputa, likrele nayindlala, ade aphele.', 'XHO22'),
(28, 44, 28, 'Baya kubuya ezweni leYiputa abasindé ekreleni, baye ezweni lakwaYuda bembalwa; alàzi onke amasalela akwaYuda, azileyo ezweni leYiputa ukuba aphambukele kulo, ukuba liliphi na ilizwi eliya kuma khona: lelam, lelabo, kusini na.', 'XHO22'),
(28, 44, 29, 'Nangu ke umqondiso kuni, utsho uYehova, wokuba ndiya kunivelela kule ndawo, ukuze nazi ukuba amazwi am aya kuma, animele ububi.', 'XHO22'),
(28, 44, 30, 'Utsho uYehova ukuthi, Yabonani, ndiyamnikela uFaro-hofra, ukumkani weYiputa, esandleni seentshaba zakhe, esandleni sabafuna umphefumlo wakhe, njengoko ndamnikelayo uZedekiya ukumkani wakwaYuda esandleni sikaNebhukadenetsare ukumkani waseBhabheli, utshaba lwakhe, olube lufuna umphefumlo wakhe.', 'XHO22'),
(28, 45, 1, 'Ilizwi, awalithetha uYeremiya umprofeti uBharuki, unyana kaNeriya, ekuwabhaleni kwakhe la mazwi encwadini, laxelwa nguYeremiya, ngomnyaka wesine kaYehoyakim unyana kaYosiya, ukumkani wakwaYuda, ukuthi,', 'XHO22'),
(28, 45, 2, 'Wathi uYehova, uThixo kaSirayeli kuwe, Bharuki!', 'XHO22'),
(28, 45, 3, 'Ubusithi, Yeha ke mna! ngokuba uYehova wongeze isingqala kumvandedwa wam; Ndidiniwe kukuncwina kwam, andifumani kuphumla.', 'XHO22'),
(28, 45, 4, 'Uze uthi kuye, ‘Utsho uYehova; Yabona, into endiyakhileyo ndiyayigungxula, into endiyityeleyo ndiyayigungxula, nalo lonke ilizwe;', 'XHO22'),
(28, 45, 5, 'Ngaba uzifunela izinto ezinkulu? musani ukubakhangela; ngokuba, yabonani, ndiyizisela ububi inyama yonke, utsho uYehova; ke ndokunika umphefumlo wakho ube lixhoba, apho uya khona.', 'XHO22'),
(28, 46, 1, 'Into eyafikayo kuYeremiya umprofeti, ililizwi likaYehova ngokusingisele kwiintlanga.', 'XHO22'),
(28, 46, 2, 'NangeYiputa, nangemikhosi kaFaro-nego ukumkani waseYiputa, ebisemlanjeni ongumEfrati, iKarkemishe, owamoyisayo uNebhukadenetsare ukumkani waseBhabheli, ngomnyaka wesine kaYehoyakim unyana kaYosiya, ukumkani wakwaYuda.', 'XHO22'),
(28, 46, 3, 'Nikani ikhaka nekrele, nisondele ekulweni;', 'XHO22'),
(28, 46, 4, 'Yondla amahashe; yimani, bamahashe, yimani ninxibe izigcina-ntloko; hlabani imikhonto ninxibise iibrigandines.', 'XHO22'),
(28, 46, 5, 'Yini na ukuba ndibabone besoyika, barhoxe? yaye amadoda awo anamandla othuka aza asaba ngokukhawuleza yaye akazange akhangele ngasemva, ngenxa yokuba kwakunkwantya macala onke, utsho uYehova.', 'XHO22'),
(28, 46, 6, 'Makangasabe onamendu, makungasindi amagorha; baya kukhubeka, bawe ngasentla emlanjeni ongumEfrati.', 'XHO22'),
(28, 46, 7, 'Ngubani na unyuka njengomlambo, amanzi awo aqukuqela njengemilambo?', 'XHO22'),
(28, 46, 8, 'iYiputa iyenyuka njengomlambo, namanzi ayo ajika njengemilambo; Wathi, Ndiya kunyuka, ndiwugqume umhlaba; Ndiya kuwutshabalalisa umzi nabemi bawo.', 'XHO22'),
(28, 46, 9, 'Nyukani, mahashe; nobushushu, zinqwelo zokulwa! kwaphuma amagorha; AmaKushi namaLubhi ephethe ikhaka; namaBhaledi abambe kwaye azingele ngesaphetha.', 'XHO22'),
(28, 46, 10, 'Kuba le yimini yoMlawuli, iNkosi uYehova wemikhosi, imini yempindezelo, ukuze aziphindezelele kubachasi bakhe; liya kudla ikrele, lihluthe, linxile ligazi labo; Umbingelelo kwilizwe langasentla, kufuphi nomlambo ongumEfrati.', 'XHO22'),
(28, 46, 11, 'Nyuka uye eGiliyadi uze uthabathe ioli yobulawu, ntombi enyulu yaseYiputa; uya kusebenzisa amayeza amaninzi ngelize; ngokuba akuyi kuphila.', 'XHO22'),
(28, 46, 12, 'Iintlanga zikuvile ukucukucezwa kwakho, ukukhala kwakho kwazalisa ihlabathi; ngokuba igorha lixhotyiwe, zawa zombini kunye.', 'XHO22'),
(28, 46, 13, 'Ilizwi uYehova awalixelela umprofeti uYeremiya ngokuphathelele isibakala sokuba uNebhukadenetsare, ukumkani waseBhabhiloni, wayeza kufika aze aloyise ilizwe laseYiputa.', 'XHO22'),
(28, 46, 14, 'Xelelani kwiYiputa, nimemeze eMigdoli, nivakalise eNofu naseTagepanese; nithi, Suk’ ume, zilungise; ngokuba ikrele liya kukonakalisa yonke indawo.', 'XHO22'),
(28, 46, 15, 'Yini na ukuba atshabalale amagorha akho? abazange bayeke, kuba uYehova wayebagxothile.', 'XHO22'),
(28, 46, 16, 'Wabhukuqa abaninzi, omnye wawa phezu komnye, bathi, Sukani, sibuyele ebantwini bakowethu, ezweni esazalelwa kulo, ngenxa yekrele elidlavulayo.', 'XHO22'),
(28, 46, 17, 'Baduma khona, besithi, Faro, kumkani waseYiputa! ugqithile umda wexesha.', 'XHO22'),
(28, 46, 18, 'Ndihleli nje, utsho ukumkani ogama lakhe linguYehova wemikhosi ukuthi, Inene, uya kuza enjengeTabhore phakathi kweentaba, enjengeKarmele ngaselwandle;', 'XHO22'),
(28, 46, 19, 'Zilungiseleleni ukuya ekuthinjweni, mmi, ntombi iyiYiputa; ngokuba iNofe iya kuba linxuwa, ibe linxuwa, ingabi nammi.', 'XHO22'),
(28, 46, 20, 'iYiputa injengethokazi elihle, kodwa iyeza imbuqo; ivela ngasentla.', 'XHO22'),
(28, 46, 21, 'Abakhonzi bakhe basemathunjini akhe njengeenkomo ezityetyisiweyo; ngokuba nabo bajika, basaba kunye; abazange balinde, kuba imini yokutshatyalaliswa kwabo yayifikile kubo, kwanexesha lokubatyelela.', 'XHO22'),
(28, 46, 22, 'Ilizwi layo liya kushukuma njengenyoka; kuba baya kunyuka benomkhosi baze bayixabele ngamazembe, njengabagawuli beenkuni.', 'XHO22'),
(28, 46, 23, 'Baya kuligawula ihlathi lakhe, utsho uYehova, nangona lingayi kugocwagocwa; ngokuba baninzi ngaphezu kweenkumbi, nakuba bengabalwa.', 'XHO22'),
(28, 46, 24, 'Intombi eyiYiputa iya kudana; uya kunikelwa esandleni sabantu basentla.', 'XHO22'),
(28, 46, 25, 'Utsho uYehova wemikhosi, uThixo kaSirayeli; Yabona, ndiyayibetha inkitha yaseNo, noFaro, neYiputa, noothixo bayo, nookumkani bayo; uFaro nabo bonke ababekholose ngaye;', 'XHO22'),
(28, 46, 26, 'Ndiya kubanikela esandleni sabafuna umphefumlo wabo, nasesandleni sikaNebhukadenetsare ukumkani waseBhabheli, nasesandleni sabakhonzi bakhe; yaye emva koko ndiya kunikwa mna njengakwimihla yakudala,’ utsho uYehova.', 'XHO22'),
(28, 46, 27, 'Ke wena musa ukoyika, mkhonzi wam Yakobi; musa ukoyika, Sirayeli; ngokuba, uyabona, ndiya kukukhulula, ndikuhlawulele kude nasembewini yakho ezweni lokuthinjwa kwabo; noYakobi uya kubuya abe ngoxolo, aphumle, kungabikho umothusayo;', 'XHO22'),
(28, 46, 28, 'Musa ukoyika, mkhonzi wam Yakobi, utsho uYehova, ngokuba ndinawe; ngokuba ndiya kuzitshabalalisa zonke iintlanga endikuphalalisele kuzo; kodwa andiyi kukonakalisa, ndiya kukohlwaya ngokulinganayo; kodwa andiyi kukuyekela umsulwa.', 'XHO22'),
(28, 47, 1, 'Ilizwi likaYehova elafikayo kuYeremiya umprofeti, kumaFilisti, engekayixabeli uFaro iGasa.', 'XHO22'),
(28, 47, 2, 'Wathi uYehova; Yabona, amanzi aya kunyuka ephuma entla, abe ngumkhukula okhukulayo, awugubungela umhlaba nenzaliseko yawo; umzi nabemi bawo baya kumemelela khona amadoda, balile bonke abemi belizwe.', 'XHO22'),
(28, 47, 3, 'Ngenxa yesandi samanqina amahashe akhe anobukroti, nangenxa yesandi seenqwelo zakhe zokulwa, isandi samavili akhe, ooyise ababheneli kubantwana ngenxa yezandla ezithe tye;', 'XHO22'),
(28, 47, 4, 'ngenxa yemini ezayo yokuphangwa onke amaFilisti nokutshabalalisa wonke umncedi oseleyo eTire naseSidon, ngenxa yokuba uYehova uya kuwaphanga amaFilisti, intsalela yelizwe lakwaKatore.', 'XHO22'),
(28, 47, 5, 'Inkqayi yeGasa; Yatshatyalaliswa iAshkelon namasalela entili yabo; koda kube nini uzibulala?', 'XHO22'),
(28, 47, 6, 'Krele likaYehova, koda kube nini na ukuba uthi cwaka? Buyela ebhedini yakho, phumla kwaye uzolile.', 'XHO22'),
(28, 47, 7, 'Ungathini na ukuthi cwaka, uYehova ekuwisele umthetho nje ngeAshkelon naselunxwemeni? kulapho Wanyula khona.', 'XHO22'),
(28, 48, 1, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Yeha iNebho! ngokuba yaphukile; Yahlaziswa iKiriyatayim, yathinjwa; uMisgabe uneentloni kwaye uyoyika.', 'XHO22'),
(28, 48, 2, 'Akuyi kuba sabakho ndumiso ivela kuMowabhi: iHeshbhon iceba ububi nxamnye naye; masiyigqibe ebahlalini. Nawe Madmene wonqanyulwa; ikrele liya kukusukela.', 'XHO22'),
(28, 48, 3, 'Kuya kubakho isikhalo eHoronayim, imbubhiso nokwaphuka okukhulu.', 'XHO22'),
(28, 48, 4, 'Utshabalele uMowabhi; abantwana bakhe abancinci balila.', 'XHO22'),
(28, 48, 5, 'Ngokuba kuya kuhlala kulila entabeni yaseLuhiti; ngenxa yokuba ekuhleni ukuya eHoronayim iintshaba zasiva isikhalo sentshabalalo.', 'XHO22'),
(28, 48, 6, 'Sabani, sizani imiphefumlo yenu, nibe njengomlimi entlango.', 'XHO22'),
(28, 48, 7, 'Ngenxa enokuba ukholose ngezenzo zakho nangobutyebi bakho, nawe uya kuthinjwa; yaye uKemoshe uya kuya ekuthinjweni nababingeleli bakhe neenkosana zakhe.', 'XHO22'),
(28, 48, 8, 'Umbhuqi uya kuzifikela zonke izixeko, kungasindi mzi; idake intili, nethafa libe senkangala, njengoko watshoyo uYehova.', 'XHO22'),
(28, 48, 9, 'Mnikeni amaphiko uMowabhi, asabe, asinde; kuba izixeko zakhe ziya kuba ngamanxuwa, akukho bani uya kuhlala kuzo.', 'XHO22'),
(28, 48, 10, 'Uqalekisiwe owenza umsebenzi kaYehova ngobuqhophololo, aqalekiswe onqanda ikrele lakhe egazini;', 'XHO22'),
(28, 48, 11, 'Wayezolile uMowabhi kwasebutsheni bakhe, yaye wayehleli esitulweni sakhe, yaye akazange athululelwe ukusuka kwesinye isitya ukuya kwesinye, okanye ekuthinjweni; ke ngoko, incasa yakhe yahlala kuye, nevumba lakhe aliguqukanga.', 'XHO22'),
(28, 48, 12, 'Ngako oko, yabonani, iyeza imihla, utsho uYehova, endiya kuthumela abahambi kubo, babagxothe, bayichithe imiphanda yabo, baziqhekeze iingxowa zabo.', 'XHO22'),
(28, 48, 13, 'Uya kuhlaziswa uMowabhi ngenxa kaKemoshe, kanye njengoko indlu kaSirayeli yahlaziswayo ngenxa yesifungo sayo saseBheteli.', 'XHO22'),
(28, 48, 14, 'Ningathini na ukuthi, Singamagorha, singamagorha emfazwe?', 'XHO22'),
(28, 48, 15, 'Uya kuphangwa uMowabhi, uthinjwa emizini yakhe, abafana bakhe abanyuliweyo basiwe ekubulaweni; utsho uYehova, ogama lakhe linguYehova wemikhosi.', 'XHO22'),
(28, 48, 16, 'Kusondele ukusindeka kukaMowabhi, ukusindeka kwakhe ngokukhawuleza;', 'XHO22'),
(28, 48, 17, 'Lilileni ngenxa yakhe nonke; bathi bonke abalaziyo igama lakhe, Hayi, ukwaphulwa kwenduku eyomeleleyo, intonga yokuhomba!', 'XHO22'),
(28, 48, 18, 'Intombi yakho ehlala eDibhon yehla eluzukweni lwakho yaye isoloko inxaniwe; kuba umtshabalalisi wakwaMowabhi uya kunihlasela, azitshabalalise iinqaba zenu.', 'XHO22'),
(28, 48, 19, 'Yima endleleni, mmi waseArohere, ubone; buza kulowo ubalekileyo nosindileyo, athi, Kwenzeke ntoni na?', 'XHO22'),
(28, 48, 20, 'uneentloni uMowabhi; lilani, lilani; Xelelani iArnon ukuba uphangiwe uMowabhi;', 'XHO22'),
(28, 48, 21, 'Kwafika umgwebo kulo ilizwe; neHolon, neYahasa, neMefahati;', 'XHO22'),
(28, 48, 22, 'neDibhon, neNebho, neBhete-dibhelatayim;', 'XHO22'),
(28, 48, 23, 'neKiriyatayim, neBhete-gamule, neBhete-mehon;', 'XHO22'),
(28, 48, 24, 'neKeriyoti, neBhotsera, nemizi yonke yelizwe lakwaMowabhi, ekude kwanekufuphi.', 'XHO22'),
(28, 48, 25, 'Uphondo lukaMowabhi lwaphukile, nengalo yakhe yaphukile,” utsho uYehova.', 'XHO22'),
(28, 48, 26, 'Mnxiliseni, ngokuba eqhayise ngoYehova; Uya kuziqikaqika uMowabhi emhlanzweni wakhe, naye uya kuba yintlekisa.', 'XHO22'),
(28, 48, 27, 'Wayengeyintlekisa na kuwe uSirayeli? Wafunyanwa phakathi kwamasela na? kuba oko nithetha ngaye, umke evuya.', 'XHO22'),
(28, 48, 28, 'Phumani ezixekweni, nihlale emaweni, nina bemi bakwaMowabhi, nibe njengevukuthu elakhela ecandeni emlonyeni wequla.', 'XHO22'),
(28, 48, 29, 'Silivile ikratshi likaMowabhi, oyena unekratshi, ikratshi lakhe nekratshi lakhe nekratshi lakhe nekratshi lentliziyo yakhe.', 'XHO22'),
(28, 48, 30, 'Mna ndiwazi umsindo wakhe, utsho uYehova; kodwa akuyi kuba njalo; ubuxoki bakhe abuyi kusebenza ngolo hlobo.', 'XHO22'),
(28, 48, 31, 'Ngako oko ndiya kumlilela uMowabhi, ndilile ngenxa kaMowabhi wonke ephela; Intliziyo yam iyakhala ngenxa yamadoda akwaKoreshi.', 'XHO22'),
(28, 48, 32, 'Ndiya kuzibika kuwe ngokukhala kweYasere, mdiliya waseSibhema; ehlotyeni iziqhamo zakho neediliya zakho zigaleleke umbhuqi.', 'XHO22'),
(28, 48, 33, 'Kwaye kwashenxiswa imivuyo nemigcobo kwilizwe elikhulu nelizwe lakwaMowabhi; ndayiyeka iwayini ezinyukweni; akukho namnye uya kunyathela ngovuyo; ukuduma kwabo akuyi kuba yimivuyo.', 'XHO22'),
(28, 48, 34, 'Ukususela kwisikhalo esisuka eHeshbhon ukusa e-Elehali ukuya eYahaze, ukusuka eTsoware ukusa eHoronayim baphakamisa amazwi abo njengethokazi lenkomo elineminyaka emithathu ubudala; ngokuba amanzi aseNimrim aya kuba yintlango.', 'XHO22'),
(28, 48, 35, 'Ndomtshabalalisa ke uMowabhi, utsho uYehova, obingelela koothixo bakhe ezigangeni, oqhumisela umsi.', 'XHO22'),
(28, 48, 36, 'Ngako oko intliziyo yam iya kulila njengogwali kuMowabhi; intliziyo yam iya kukhala izigodlo kumadoda akwaKoreshi;', 'XHO22'),
(28, 48, 37, 'Kuba zonke iintloko ziya kuchetywa, zonke iindevu zichetyiwe; bonke bephela banamanxeba ezandleni, benxibe ezirhwexayo ezinqeni zabo.', 'XHO22'),
(28, 48, 38, 'Eluphahleni lonke lwakwaMowabhi nasezitratweni zakhe kuya kukhwazwa, ngenxa yokuba ndimaphule uMowabhi njengento engonwabanga,’ utsho uYehova.', 'XHO22'),
(28, 48, 39, 'Baya kunkqangaza, besithi, Hayi, ukutyumzwa! Utheni na ukubuya uMowabhi eneentloni! ngenxa yoko uMowabhi ube yintlekisa, uyoyikiswa ngeenxa zonke.', 'XHO22'),
(28, 48, 40, 'Ngokuba utsho uYehova ukuthi, Yabona, utshaba lutshwebeleza njengexhalanga, lolule amaphiko alo phezu koMowabhi.', 'XHO22'),
(28, 48, 41, 'iKeriyoti ithinjiwe, neenqaba ezinqatyisiweyo zithinjiwe, yaye ngaloo mini intliziyo yamagorha akwaMowabhi iya kuba njengentliziyo yomfazi ezindleleni zakhe.', 'XHO22'),
(28, 48, 42, 'uMowabhi uya kutshatyalaliswa angabi saba luhlanga, ngenxa yokuba uqhayise ngoYehova.', 'XHO22'),
(28, 48, 43, 'Kuya kuwelwa lunkwantyo nomgongxo nesibatha, wena mmi wakwaMowabhi,” utsho uYehova.', 'XHO22'),
(28, 48, 44, 'Osaba unkwantyo weyela emhadini; onyuka ephuma emhadini, abanjiswe ngumgibe; kuba ndiza kumzisela uMowabhi unyaka wokukohlwaya,” utsho uYehova.', 'XHO22'),
(28, 48, 45, 'Abo basabileyo bema phantsi komthunzi weHeshbhon ngenxa yamandla, kodwa kuya kuphuma umlilo uvela eHeshbhon nedangatye phakathi kukaSihon, litshise imbombo yakwaMowabhi neentloko zabo bacaphukileyo.', 'XHO22'),
(28, 48, 46, 'Yeha, wena Mowabhi! abantu bakwaKamoshe batshabalele, ngenxa yokuba oonyana bakho baye bathinjwa, neentombi zakho zisiwe ekuthinjweni.', 'XHO22'),
(28, 48, 47, 'Kodwa ke ekupheleni kwemihla ndiya kulibuyisela iqashiso lakwaMowabhi,” utsho uYehova. Seza kufika apha isigwebo sikaMowabhi.', 'XHO22'),
(28, 49, 1, 'Ngoonyana baka-Amon, utsho uYehova ukuthi, uSirayeli akananyana na? akanayo na indlalifa? Ngoko kwakutheni ukuze ukumkani wabo athabathe uGadi aze abahlalise abantu bakhe ezixekweni zakhe?', 'XHO22'),
(28, 49, 2, 'Ngako oko, yabonani, iyeza imihla, utsho uYehova, endiya kumbulala ngayo umkhosi iRabha yoonyana baka-Amon; liya kuba yimfumba yenkangala, nemizi yalo itshiswe ngomlilo; kulapho uSirayeli aya kudla ilifa leendlalifa zakhe, utsho uYehova.', 'XHO22'),
(28, 49, 3, 'Lila, Heshbhon, ngokuba ibhuqiwe iAyi; khalani, zintombi zaseRabha, bhinqani iingubo ezimnyama; khalani nibaleke niye ezindongeni; ngokuba ukumkani wabo uya kuthinjwa, kunye nababingeleli bakhe nabathetheli bakhe.', 'XHO22'),
(28, 49, 4, 'Ngubani na oqhayisa ngeentlambo zakho, entilini yakho, ntombi yombandezeli? owakholosa ngobutyebi bakhe, wathi, Ngubani na oya kuza kum?', 'XHO22'),
(28, 49, 5, 'Yabona, ndiya kukuzisela unkwantyo, itsho iNkosi, uYehova wemikhosi, ngenxa yabo bonke abangeenxa zonke kuwe; nigxothwe elowo kuni kwakanye; yaye akukho bani uya kubabutha ababhaduli.', 'XHO22'),
(28, 49, 6, 'Emva koko ndiya kubabuyisa abathinjwa boonyana baka-Amoni,” utsho uYehova.', 'XHO22'),
(28, 49, 7, 'NgoEdom, utsho uYehova wemikhosi, ukuthi, Akusekho bulumko na kwaTeman? Litshabalele na icebo lezilumko? Bumkile na ubulumko babo?', 'XHO22'),
(28, 49, 8, 'Sabani, jikani, hlalani enkolontyweni, bemi bakwaDedan; kuba ndiya kumzisela intlekele uEsawu ngexesha lokumxabela kwam.', 'XHO22'),
(28, 49, 9, 'Ukuba abavuni beediliya bafika kuwe, abayi kuzishiya ngasemva na? njengamasela ebusuku, aya konakalisa ade ahluthe.', 'XHO22'),
(28, 49, 10, 'Ngokuba mna ndimtyhilile uEsawu, ndizithiyile iindawo zakhe ezisitheleyo, angabi nako ukuzimela; Abantwana bakhe baphangiwe, nabazalwana bakhe nabamelwane abakunye naye, kwaye akakho apho.', 'XHO22'),
(28, 49, 11, 'Shiya iinkedama zakho, ndozidlisa ubomi; nabahlolokazi bakho bakholose ngam.', 'XHO22'),
(28, 49, 12, 'Ngokuba utsho uYehova ukuthi, Yabona, ababengenalungelo lakuyisela indebe, mabasele; kanti nguwe oya kuhamba ungohlwaywa? akuyi kuba yimfuneko kuwe, kodwa inene uya kusela kuyo.', 'XHO22'),
(28, 49, 13, 'Ngokuba ndizifungile mna, utsho uYehova, ndathi, iBhotsera iya kuba linxuwa, nento edelekileyo, nentshabalalo, nentshabhiso; kwaye zonke izixeko zakhe ziya kuba yinkangala ngonaphakade.', 'XHO22'),
(28, 49, 14, 'Ndeva izwi liphuma kuYehova, kwathunyelwa umthunywa ezintlangeni, esithi, Zihlanganiseni, nimhlasele, nihlale edabini.', 'XHO22'),
(28, 49, 15, 'Ngokuba, uyabona, ndikwenza ube ngowamncinanana ezintlangeni, nodelekileyo ezintlangeni.', 'XHO22'),
(28, 49, 16, 'Ukunkwantya kwakho kukukhohlisile, nekratshi lentliziyo yakho likukhohlisile, Wena uhleli ezimfanteni zamawa, wena ubambelele kwindawo ephakamileyo yenduli; nokuba uthe wakhela phezulu njengexhalanga indlwane yakho, ndokuhlisa nalapho; utsho uYehova.', 'XHO22'),
(28, 49, 17, 'uEdom abe yintlango; bonke abadlulayo baya kumangaliswa, bengcikive bonke ubugwenxa bakhe.', 'XHO22'),
(28, 49, 18, 'Kanye njengoko yatshatyalaliswayo iSodom neGomora nezixeko ezingabamelwane, utsho uYehova, akuyi kuhlala mntu khona, makungahlali mntu khona.', 'XHO22'),
(28, 49, 19, 'Yabona, utshaba luya kunyuka njengengonyama, luphume eYordan emlanjeni, luye ekhayeni lamagorha; ndosuka ndimbaleke kwaoko; ngubani na ke umnyuliweyo, endinokuthi ndimwise? ngokuba ngubani na onjengam? ngubani na oya kundinika ixesha? Nguwuphi na umalusi onokuma phambi kwam?', 'XHO22'),
(28, 49, 20, 'Ngako oko yiva icebo likaYehova, olishumayele kuEdom; nezicwangciso zakhe azicwangcisele abemi bakwaTemane: Ewe, baya kuvelisa abancinane emhlambini; Ngokuqinisekileyo uya kuzenza kube senkangala izakhiwo zabo kunye nabo.', 'XHO22'),
(28, 49, 21, 'Ihlabathi liyanyikima sisithonga sokuwa kwabo; isandi sokukhala kwabo sivakala eLwandle oluBomvu.', 'XHO22'),
(28, 49, 22, 'Yabona, utshaba luyanyuka njengexhalanga, lutshwebeleza, luwolule amaphiko alo phezu kweBhotsera, intliziyo yamagorha akwaEdom ngaloo mini iya kuba njengentliziyo yomfazi ezindleleni zakhe.', 'XHO22'),
(28, 49, 23, 'Ngokusingisele kwiDamasko. Ihlazekile iHamati neArpadi, ngokuba kuviwe iindaba ezimbi; badimazekile; kukho usizi elwandle; aliyi kuthi tu.', 'XHO22'),
(28, 49, 24, 'iDamasko yatyhafa, yajika, yasaba; wafikelwa ziintlungu nozalayo.', 'XHO22'),
(28, 49, 25, 'Utheni na, umzi wendumiso, umzi wemivuyo yam!', 'XHO22'),
(28, 49, 26, 'Ngako oko amadodana akhe aya kuza ezintendelezweni zakhe, atshabalale onke amagorha ngaloo mini; utsho uYehova wemikhosi.', 'XHO22'),
(28, 49, 27, 'Ndiya kulufaka umlilo eludongeni lweDamasko, uzitshise iingxande ezinde zikaBhen-hadade.', 'XHO22'),
(28, 49, 28, 'Ngokuphathelele uKedare nezikumkani zaseHatsore uNebhukadenetsare ukumkani waseBhabhiloni aya kuboyisa, utsho uYehova ukuthi: “Sukuma, nyuka uye kwaKedare uze ubaphange abantu baseMpuma.”', 'XHO22'),
(28, 49, 29, 'Iintente zabo nemfuyo yabo bozithabatha; bawathabatha amalaphu abo entente, neempahla zabo zonke, neenkamela zabo; baya kuthi kubo, Sifikelwe kukoyika;', 'XHO22'),
(28, 49, 30, 'Sabani, balekani kude, hlalani ezinzulwini zentliziyo, bemi baseHatsore, utsho uYehova; ngenxa yokuba uNebhukadenetsare ukumkani waseBhabhiloni ecebe icebo ngani yaye ucebe icebo ngani.', 'XHO22'),
(28, 49, 31, 'Sukani nime, ninyuke niye kwabatyebileyo, abahleli kamnandi, utsho uYehova, abangenaminyango nemiqobo, abahlala bodwa.', 'XHO22'),
(28, 49, 32, 'Iinkamela zabo ziya kuphangwa, nemfuyo yabo eninzi iya kuphangwa; ndiya kubachithachitha abasemacaleni kuyo yonke imimoya; yaye ndiza kuyizisa intshabalalo yabo macala onke,” utsho uYehova.', 'XHO22'),
(28, 49, 33, 'iHatsore iya kuba lilizwe leempungutye, ibe yintlango ngonaphakade; akuyi kuhlala mntu khona, yaye akuyi kuhlala mntu khona.', 'XHO22'),
(28, 49, 34, 'Ilizwi likaYehova elafika kuYeremiya umprofeti ngokuphathelele uElam ekuqaleni kolawulo lukaZedekiya ukumkani wakwaYuda, lalisithi:', 'XHO22'),
(28, 49, 35, 'Utsho uYehova wemikhosi ukuthi, Yabonani, ndiyasaphula isaphetha sakwaElam, intloko yamandla abo.', 'XHO22'),
(28, 49, 36, 'Ndiya kuzisa kwaElam imimoya yomine evela kwiimbombo zone zezulu ndize ndibachithachithe kuyo yonke loo mimoya; yaye akuyi kubakho luhlanga bangayi kufika kulo abathinjwa bakwaElam.', 'XHO22'),
(28, 49, 37, 'Ndomqhiphukisa umbilini uElam phambi kweentshaba zakhe, naphambi kwabafuna imiphefumlo yabo; ndibazisele ububi, ukuvutha komsindo wam, utsho uYehova; yaye ndiza kuthumela ikrele emva kwabo ukuze libatshabalalise.', 'XHO22'),
(28, 49, 38, 'Ndiza kuyibeka kwaElam itrone yam, nditshabalalise ukumkani neenkosana apho,” utsho uYehova.', 'XHO22'),
(28, 49, 39, 'Kodwa ke ekupheleni kwemihla ndiya kulibuyisela iqashiso lakwaElam,” utsho uYehova.', 'XHO22'),
(28, 50, 1, 'Ilizwi uYehova awalithethayo ngeBhabhiloni nelizwe laseGalati ngomprofeti uYeremiya.', 'XHO22'),
(28, 50, 2, 'Xelani ezintlangeni, yazisani, niphakamise ibhanile; vakalisani, musani ukuzifihla; nithi, Ithinjiwe iBhabheli, uhlazekile uBhele, waqotywa uMerodaki; zihlazekile izigodo zabo, zaphukile imifanekiso yabo eqingqiweyo.', 'XHO22'),
(28, 50, 3, 'Ngokuba abantu basentla basukele phezulu kulowo walenza kube senkangala ilizwe labo, kungamiwa mntu kulo; baya kuphuma bahambe, abantu neenkomo.', 'XHO22'),
(28, 50, 4, 'Ngaloo mihla nangelo xesha, utsho uYehova, oonyana bakaSirayeli baya kuhamba kunye noonyana bakaYuda, balile; baya kuhamba baye kufuna uYehova uThixo wabo.', 'XHO22'),
(28, 50, 5, 'Baya kubuza indlela yaseZiyon, ubuso babo bubheke kuyo, besithi, Yizani, nisithelele kuYehova emnqophisweni ongunaphakade ongayi kulityalwa.', 'XHO22'),
(28, 50, 6, 'Abantu bam baba ngumhlambi weegusha ezilahlekileyo; abalusi babo babandwendwisa, babandwendwisa ezintabeni; besuka entabeni, baya endulini, beyilibele indawo yabo yokuphumla.', 'XHO22'),
(28, 50, 7, 'Bonke abawafumanayo bawadla; baza abachasi babo bathi: ‘Asizange sikhubeke, ngenxa yokuba bamonile uYehova, isiseko sobulungisa, uYehova, ithemba lookhokho babo.', 'XHO22'),
(28, 50, 8, 'Mkani phakathi kweBhabhiloni nize nilishiye ilizwe lamaKhaledi nize nibe njengeebhokhwe ezikhokela umhlambi.', 'XHO22'),
(28, 50, 9, 'Kuba, yabonani, ndinyusa ibandla leentlanga ezinkulu ezweni lasentla, ukuba lihlasele iBhabheli; uya kuthatyathwa khona; Iintolo zabo zibe njengezegcisa; akukho namnye uya kubuya elambatha.', 'XHO22'),
(28, 50, 10, 'Loba lixhoba elamaKaledi; bonke abayithabathayo baya kuhlutha; utsho uYehova.', 'XHO22'),
(28, 50, 11, 'Ngokuba navuya, ngenxa yokuba ninoyolo, nina batshabalalisi belifa lam;', 'XHO22'),
(28, 50, 12, 'uya kudana kakhulu unyoko; uya kuba neentloni lowo wakuzalayo; yabona, isiphelo seentlanga siya kuba yintlango, umhlaba ongumqwebedu, nenkqantosi.', 'XHO22'),
(28, 50, 13, 'Ngenxa yomsindo kaYehova ayisayi kumiwa, kodwa iya kuba yinkangala ephanzileyo;', 'XHO22'),
(28, 50, 14, 'Zingcwaliseleni imfazwe neBhabheli yonke indawo; bonke abazingelayo utshaba bayamzingela, abangaziyeki utolo; ngokuba wonile kuYehova.', 'XHO22'),
(28, 50, 15, 'Dumani ngayo kuyo yonke indawo; wanikela isandla sakhe; ziwile iziseko zayo, zidilikile iindonga zayo; ngokuba yimpindezelo kaYehova; phindezelani kuye; yenzani kuye njengoko enze ngako.', 'XHO22'),
(28, 50, 16, 'Mbhubhiseni umhlwayeli eBhabheli, nosebenzisa irhengqa ngexesha lokuvuna; ngenxa yokoyika ikrele elicinezelayo, elowo uya kubheka ebantwini bakowabo, basabele elowo ezweni lakowabo.', 'XHO22'),
(28, 50, 17, 'Uyigusha elusali uSirayeli; iingonyama zamsukela; engekadliwa ngukumkani waseAsiriya; waza ekugqibeleni lo Nebhukadenetsare, ukumkani waseBhabhiloni, wawaphula amathambo akhe.', 'XHO22'),
(28, 50, 18, 'Ngoko ke utsho uNdikhoyo onamandla onke, uThixo kaSirayeli. Yabona, ndiya kumbetha ukumkani waseBhabheli nelizwe lakhe, njengoko ndambethayo ukumkani waseAsiriya;', 'XHO22'),
(28, 50, 19, 'Ndiza kumbuyisela uSirayeli endaweni yakhe yokuhlala, yaye ngokuqinisekileyo uya kudla eKarmele naseBhashan, nomphefumlo wakhe waneliswe eNtabeni yakwaEfrayim naseGiliyadi.', 'XHO22'),
(28, 50, 20, 'Ngaloo mihla nangelo xesha, utsho uYehova, buya kufunwa ububi bukaSirayeli, bungabikho; nezono zakwaYuda zingafunyanwa, ngenxa yokuba ndiya kubaxolela abo ndibagcinayo.', 'XHO22'),
(28, 50, 21, 'Nyuka uye kwilizwe laseMeratayim ukuze ulwe nalo, nakubemi basePekodi; nibatshabalalise, nibatshabhise ngesiqalekiso, utsho uYehova; nenze njengoko ndaniwiselayo umthetho.', 'XHO22'),
(28, 50, 22, 'Kukho isithonga semfazwe elizweni, nesokwaphuka okukhulu.', 'XHO22'),
(28, 50, 23, 'Hayi, ukunqamka kwawo elimiweyo liphela, ukuqhekezwa kwawo! Hayi, ukwenziwa ummangaliso kweBhabheli phakathi kweentlanga!', 'XHO22'),
(28, 50, 24, 'Ndikubekele isibatha, wabanjiswa, wena Bhabheli, ungazi; wafunyanwa, nawe wathinjwa, ngokuba ulwe noYehova.', 'XHO22'),
(28, 50, 25, 'uYehova wazivula iintonga zakhe, wazizisa izixhobo zomsindo wakhe; kuba lo ngumsebenzi weNkosi enguMongami uYehova wemikhosi kwilizwe lamaKhaledi.', 'XHO22'),
(28, 50, 26, 'Yizani kuye, nivale ngeenxa zonke, nivule ubuncwane bakhe, nimtyumze njengemfumba yeemfumba, nimtshabalalise ngesiqalekiso; kungabikho nto iseleyo kuye.', 'XHO22'),
(28, 50, 27, 'Zibulaleni zonke iinkunzi zalo ezintsha zeenkomo; mabehle baye kuxhelwa nje; yeha kubo! ngokuba ifikile imini yabo, ixesha lokuvelelwa kwabo.', 'XHO22'),
(28, 50, 28, 'Ilizwi labasabayo nabasindileyo ezweni laseBhabheli, ukutsho impindezelo kaYehova, uThixo wethu, iZiyon, nempindezelo yetempile yakhe.', 'XHO22'),
(28, 50, 29, 'Bahlanganiseleni eBhabheli abatoli besaphetha, bonke abatoli beentshaba, bayirhawule ngeenxa zonke; makungasindi nto; Mvuze ngokwemisebenzi yakhe; yenzani kuye njengoko wenza ngako, ngokuba eqhayisele kuYehova, koyiNgcwele kaSirayeli.', 'XHO22'),
(28, 50, 30, 'Ngako oko amadodana ayo aya kuza ezitratweni, onke amagorha ayo anqunyulwe ngaloo mini; utsho uYehova.', 'XHO22'),
(28, 50, 31, 'Yabonani, ndinimangalele, nina bakhukhumeleyo, itsho iNkosi uYehova wemikhosi; ngokuba ifikile imini yakho, ixesha lokukuvelela kwam.', 'XHO22'),
(28, 50, 32, 'Kwaye abanekratshi baya kukhubeka bawe, kungabikho ubaphakamisayo; Ndiya kuphemba umlilo ezixekweni zakhe, umtshise macal’ onke.', 'XHO22'),
(28, 50, 33, 'Utsho uYehova wemikhosi ukuthi, Bacudiswa oonyana bakaSirayeli noonyana bakaYuda kunye, babathimba bonke ababathimbileyo; abavumi ukubakhulula.', 'XHO22'),
(28, 50, 34, 'Umsindisi wabo womelele; NguYehova wemikhosi igama lakhe; Yena uya kubagweba aphele, eliphumza ilizwe, akhwankqise abemi baseBhabheli.', 'XHO22'),
(28, 50, 35, 'Kuza unkwantyo kumaKhaledi,’ utsho uYehova, “ukuba alwe nabemi baseBhabhiloni neenkosana zayo nezilumko zayo.', 'XHO22'),
(28, 50, 36, 'Ikrele malibafikele amaxoki; ikrele maliwafikele amagorha akhe; baya koyika.', 'XHO22'),
(28, 50, 37, 'Ikrele lamahashe abo, neenqwelo zabo zokulwa, neempahla zonke ezikuwo; baya kuba njengabafazi; ikrele malibufikele ubuncwane bakhe; yaye baya kuphangwa.', 'XHO22'),
(28, 50, 38, 'Ukubalela makuwafikele amanzi alo; baya kubuna, ngokuba lilizwe lemifanekiso eqingqiweyo, abayithandayo nemifanekiso yabo.', 'XHO22'),
(28, 50, 39, 'Ngako oko kuya kuhlala khona amarhamncwa asemathafeni, namarhamncwa eziqithi, kuhlale iinciniba khona; kwaye akuyi kubakho abemi ngonaphakade; kwaye akuyi kubakho mmi kwisizukulwana ngesizukulwana.', 'XHO22'),
(28, 50, 40, 'Njengoko wayitshabalalisayo uThixo iSodom neGomora, nemimelwane yayo, utsho uYehova; ngako oko akuyi kuhlala mntu khona, akuyi kuphambukela nyana womntu khona.', 'XHO22'),
(28, 50, 41, 'Yabonani, kuya kuvela uhlanga lwasentla, lube luhlanga olukhulu, kuxhotywe ookumkani abaninzi emazweni;', 'XHO22'),
(28, 50, 42, 'Baphethe isaphetha nentshuntshe; bakhohlakele, abananceba; ilizwi labo liya kugquma njengolwandle, yaye baya kukhwela emahasheni, ngamnye exhobe njengendoda yokulwa, nxamnye nawe, ntombi yaseBhabhiloni.', 'XHO22'),
(28, 50, 43, 'Ukumkani waseBhabheli waluva udaba lwabo, zoma izandla zakhe;', 'XHO22'),
(28, 50, 44, 'Yabona, utshaba luya kunyuka njengengonyama ephuma emanzini aseYordan, luye ekhayeni eliyingxondorha; ke kwaoko ndiya kubenza basabe ebusweni bakhe; ngubani na ke umnyuliweyo, endinokuthi ndimwise? ngokuba ngubani na onjengam? ngubani na oya kundinika ixesha? Nguwuphi na umalusi onokuma phambi kwam?', 'XHO22'),
(28, 50, 45, 'Ngako oko yivani icebo likaYehova, alicebileyo ngeBhabheli; necebo lakhe alicebileyo ngelizwe lamaKaledi: Inene, baya kuzala abancinane emhlambini; inene, uya kuwenza amanxuwa amakhaya abo kunye nabo.', 'XHO22'),
(28, 50, 46, 'Ngenxa yesandi sokuthinjwa kweBhabhiloni ihlabathi liyanyikima, nesikhalo sivakala phakathi kweentlanga.', 'XHO22'),
(28, 51, 1, 'Wathi uYehova; Yabona, ndixhoxha umoya womonakalisi phezu kweBhabheli, nabasukela phezulu kum;', 'XHO22'),
(28, 51, 2, 'yaye ndiza kuthumela abaxhasi eBhabhiloni abaya kuyivuthela nelize nelizwe layo; ngokuba bomngqonga ngemini yentlekele.', 'XHO22'),
(28, 51, 3, 'Umtoli makatyesele isaphetha sakhe kogobileyo, nakoziphakamisayo emehlweni akhe; alibacongi abafana balo; uwutshabalalise wonke umkhosi wakhe ngesiqalekiso.', 'XHO22'),
(28, 51, 4, 'Baya kuwa ababuleweyo ezweni lamaKaledi, nababuleweyo ezitratweni zalo.', 'XHO22'),
(28, 51, 5, 'Ngokuba uSirayeli noYuda babengabashiyi uThixo wabo, uYehova wemikhosi; nangona ilizwe labo lalizele zizono koyiNgcwele kaSirayeli.', 'XHO22'),
(28, 51, 6, 'Sabelani eBhabheli, nisindise elowo umphefumlo wakhe; ngokuba eli lixesha lempindezelo kaYehova; uya kumnika imbuyekezo.', 'XHO22'),
(28, 51, 7, 'iBhabheli yaba yindebe yegolide esandleni sikaYehova, ilinxilisa lonke elimiweyo; zasela iintlanga iwayini yakhe; ngoko ke, izizwe ziyageza.', 'XHO22'),
(28, 51, 8, 'iBhabhiloni yawa ngoko nangoko yaza yatshatyalaliswa; mlileleni; thabatha iyeza lentlungu yakhe, ukuba kunjalo, unokuphila.', 'XHO22'),
(28, 51, 9, 'Ngesiyinyanga iBhabheli, ayiphiliswanga; Makahambe, elowo aye ezweni lakowabo; ngokuba isigwebo sakhe safika emazulwini, samphakamisa, sasinga ezulwini.', 'XHO22'),
(28, 51, 10, 'uYehova ubuvelisile ubulungisa bethu; yizani nisixelele umsebenzi kaYehova, uThixo wethu, iZiyon.', 'XHO22'),
(28, 51, 11, 'Danyaza iintolo; qokelela amakhaka; uYehova wawuxhokonxa umoya wookumkani bamaMedi, ngokuba umnkqangiyelo wakhe ukwiBhabheli, ukuba ayonakalise ngokuba yimpindezelo kaYehova, yimpindezelo yetempile yakhe.', 'XHO22'),
(28, 51, 12, 'Misa iindonga zaseBhabheli ngebhanile, qinisani abalindi, xhobisani abalindi, nilungise imizi, ngokuba uYehova ekhumbulile, wenza njengoko wakuthethayo kwabahlala eBhabheli.', 'XHO22'),
(28, 51, 13, 'Wena uhleli emanzini amaninzi, unobuncwane obuninzi, kufikile ukuphela kwakho, nomlinganiselo wokubawa kwakho.', 'XHO22'),
(28, 51, 14, 'uYehova wemikhosi wafunga ngaye, wathi, Inyaniso, ndiya kukuzalisa ngabantu njengeenkumbi; yaye baya kukhala ngawe.', 'XHO22'),
(28, 51, 15, 'Wenza elimiweyo ngamandla akhe, waliseka elimiweyo ngobulumko bakhe, waneka izulu ngengqondo yakhe.', 'XHO22'),
(28, 51, 16, 'Akulivakalisa ilizwi lakhe, maninzi amanzi esibhakabhakeni; wanyusa umphunga waya eziphelweni zehlabathi;', 'XHO22'),
(28, 51, 17, 'Elowo udela okwakhe ukwazi; Wonke umkhohlisi udaniswa ngumfanekiso oqingqiweyo, ngokuba umfanekiso wakhe ubuxoki, awunamoya.', 'XHO22'),
(28, 51, 18, 'Kungamampunge lawo, sisenzo senkohliso; ngexesha lokundwendwela kwabo baya kutshabalala.', 'XHO22'),
(28, 51, 19, 'Isabelo sikaYakobi asinjengazo; ngokuba nguMenzi wezinto zonke yena, noSirayeli ntonga yelifa lakhe; NguYehova wemikhosi igama lakhe.', 'XHO22'),
(28, 51, 20, 'Wena usisixengxe sam, usisikrweqe sam sokulwa; ngokuba ndiya kuzihlekeza ngawe iintlanga, ndizonakalise ngawe izikumkani;', 'XHO22'),
(28, 51, 21, 'Ndiya kulaphula ngawe ihashe nomkhweli walo; ndiya kuyiqhekeza ngawe inqwelo yokulwa nomkhweli wayo;', 'XHO22'),
(28, 51, 22, 'ndihlekeze ngawe indoda nomfazi; ndihlekeza ngawe indoda enkulu nomtsha; ndihlekeze ngawe amadodana nomthinjana;', 'XHO22'),
(28, 51, 23, 'Ndiya kumtyumza kunye umalusi nomhlambi wakhe; Ndiya kumtyumza ngawe umlimi neenkomo zakhe; ndibaqhekeze ngawe abaphathi nabaphathi;', 'XHO22'),
(28, 51, 24, 'Ndiza kukubuyekeza iBhabhiloni nabo bonke abemi bamaKhaledi ngenxa yobubi babo bonke ababenze eZiyon phambi kwamehlo enu,” utsho uYehova.', 'XHO22'),
(28, 51, 25, 'Yabona, ndinento ngakuwe, ntaba ikrazukileyo, utsho uYehova, elonakalisileyo lonke ihlabathi; ndiya kusolula isandla sam phezu kwakho, ndikutyumze emaweni, ndikwenze intaba etshisiweyo;', 'XHO22'),
(28, 51, 26, 'abayi kuthabatha kuwe ilitye lembombo, nalitye lasiseko; kodwa uya kuba yintlango ukusa kwixesha elingenammiselo,” utsho uYehova.', 'XHO22'),
(28, 51, 27, 'Phakamisani ibhanile elizweni, vuthelani isigodlo ezintlangeni; zingcwaliseleni iintlanga; Misela umphathi phezu kwakhe; ukwenza amahashe eze njengehashe.', 'XHO22'),
(28, 51, 28, 'Zingcwaliseni iintlanga nxamnye nayo, ookumkani bamaMedi neenkosana zakhe zonke iinkosana zakhe nelizwe lonke eliphantsi kolawulo lwakhe.', 'XHO22'),
(28, 51, 29, 'Ilizwe liya kunyikima lize likhale, ngenxa yokuba lonke icebo likaYehova liya kuzaliseka nxamnye neBhabhiloni, ukuze ilizwe laseBhabhiloni libe yintlango, lingabi nammi.', 'XHO22'),
(28, 51, 30, 'Ayeka ukulwa, ahlala ezinqabeni zawo; amandla abo ephelile; babenjengabafazi; bazitshisa izakhiwo zakhe; imivalo yakhe yaphukile.', 'XHO22'),
(28, 51, 31, 'Kuya kuqubisana nomthunywa omnye, kuqubisane nomnye umthunywa, ukuya kuxelela ukumkani waseBhabheli ukuba umzi wakhe uthinjwe nganeno;', 'XHO22'),
(28, 51, 32, 'Zema izitrato, ingcongolo isitsha ngumlilo, namagorha ankwantya.', 'XHO22'),
(28, 51, 33, 'Ngokuba utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, Intombi eyiBhabheli injengesibetho esi, lixesha lokuvuna; Kwaba mzuzwana ke, lafika ixesha lokuvuna kwakhe.', 'XHO22'),
(28, 51, 34, 'Wadla uNebhukadenetsare ukumkani waseBhabheli, wandisusa, wandenzela isidlo, saginywa njengengwenya, wahlutha isisu sakhe ngokutya kwam, wandigxotha.', 'XHO22'),
(28, 51, 35, 'Ugonyamelo olwenziwe kum, nenyama yam yiBhabheli, baya kuthi abemi baseZiyon; negazi lamaKaledi, iYerusalem yothi.', 'XHO22'),
(28, 51, 36, 'Ngako oko, utsho uYehova ukuthi, Yabona, ndikuthethelele, ndikugwebele, ndikugwebele; ndiya kulomisa iilwandle zalo, ndilomise ithende layo;', 'XHO22'),
(28, 51, 37, 'iBhabheli iya kuba ngumwewe wamatye, nendlu yeempungutye, izinto eziwothusayo, nezothuso, ingabi nammi.', 'XHO22'),
(28, 51, 38, 'Zibharhula kunye njengengonyama, zibhomboloza njengamathole engonyama.', 'XHO22'),
(28, 51, 39, 'Ekutshisweni kwawo ndiya kubenzela itheko ndize ndibanxilise, ukuze bonwabe baze balale ubuthongo obungunaphakade bangavuki,” utsho uYehova.', 'XHO22'),
(28, 51, 40, 'Ndiya kubathobela ekuxhelweni njengamatakane, njengeenkunzi zeegusha neenciniba.', 'XHO22'),
(28, 51, 41, 'Yathatyathwa njani iSesage! Hayi, ukudunyiswa kwehlabathi lonke! Hayi indlela eye yamangalisa ngayo iBhabhiloni phakathi kweentlanga!', 'XHO22'),
(28, 51, 42, 'Ulwandle luphezu kweBhabhiloni, lugutyungelwe ngamaza ayo amaninzi.', 'XHO22'),
(28, 51, 43, 'Imizi yakhe yaba linxuwa, yaba lilizwe lomqwebedu nenkqantosi, lilizwe elingumqwebedu, ekungacandi bani kulo.', 'XHO22'),
(28, 51, 44, 'Ndiya kumvelela uBhele eBhabheli, ndiyirhole emlonyeni wakhe into uyiseleyo; kwaye iintlanga aziyi kuba sahlanganisana kuye; Ewe, nodonga lwaseBhabheli luya kuwa.', 'XHO22'),
(28, 51, 45, 'Phumani kuyo, bantu bam, nize wonk’ ubani asindise umphefumlo wakhe kwingqumbo kaYehova;', 'XHO22'),
(28, 51, 46, 'ize ingatyhafi intliziyo yenu, noyike iindaba eziya kuvakala ezweni; Kuya kubakho amarhe unyaka omnye, emva koko ngomnye unyaka kuze kufike amarhe nogonyamelo elizweni, umlawuli evukelana nomlawuli.', 'XHO22'),
(28, 51, 47, 'Ngako oko, uyabona, iyeza imihla endiya kuyigweba ngayo imifanekiso eqingqiweyo yaseBhabheli; nelizwe lakhe lonke liya kuba neentloni, nabo bonke ababuleweyo bakhe bawe phakathi kwakhe.', 'XHO22'),
(28, 51, 48, 'Liya kuvuya izulu nomhlaba nayo yonke into ekuzo ngenxa yeBhabhiloni, ngenxa yokuba ababhuqi bayo beza kuyo bevela ngasemntla,” utsho uYehova.', 'XHO22'),
(28, 51, 49, 'Njengoko iBhabhiloni ibhukuqa ababuleweyo bakwaSirayeli, nababuleweyo behlabathi lonke baya kuwa ngendlela efanayo eBhabhiloni.', 'XHO22'),
(28, 51, 50, 'Nina nisindileyo ekreleni, bakani, musani ukuma; mkhumbuleni uYehova nisekude, ithi qatha ezintliziyweni zenu iYerusalem.', 'XHO22'),
(28, 51, 51, 'Sidanile, ngokuba sive indelo; ihlazo ligqubuthele ubuso bethu; ngokuba abasemzini bangene engcweleni yendlu kaYehova.', 'XHO22'),
(28, 51, 52, 'Ngako oko, yabonani, iyeza imihla, utsho uYehova, endiya kuyigweba ngayo imifanekiso yakhe eqingqiweyo, bancwine ababuleweyo ezweni lakhe.', 'XHO22'),
(28, 51, 53, 'Nokuba iBhabheli ithe yenyuka yaya ezulwini, nokuba ithe yomelela inqaba yayo, abangendawo bosuka bayifikele, bephuma kum; utsho uYehova.', 'XHO22'),
(28, 51, 54, 'Kuvakele isandi sesikhalo eBhabheli, nentshabalalo enkulu ephuma ezweni lamaKaledi.', 'XHO22'),
(28, 51, 55, 'Ngenxa yokuba uYehova wayibhuqa iBhabhiloni waza wayiphelisa ingxolo enkulu kuyo; agqume amaza akho njengamanzi amaninzi, sivakala isandi sesandi sawo;', 'XHO22'),
(28, 51, 56, 'Ngokuba umbhuqi wayifikela, iBhabheli ke, namagorha ayo athinjwa, zaphulwe zonke izaphetha zayo; ngokuba uYehova, uThixo wempindezelo, uya kukubuyisela ngonaphakade.', 'XHO22'),
(28, 51, 57, 'Ndiyabanxilisa abathetheli bayo nezilumko zayo, nabathetheli bayo, nabathetheli bayo, namagorha ayo; baya kulala ubuthongo obungunaphakade yaye abayi kuvuka,” utsho uYehova ogama lakhe linguYehova wemikhosi.', 'XHO22'),
(28, 51, 58, 'Utsho uYehova wemikhosi ukuthi, Udonga olubanzi lweBhabheli luya kudilizwa kuphele, amasango ayo aphakamileyo atshiswe ngomlilo; kwaye abantu baya kuzidlisa ilize, nabantu emlilweni, yaye baya kulamba.', 'XHO22'),
(28, 51, 59, 'Ilizwi awathi uYeremiya umprofeti wamwisela umthetho ngalo uSeraya, unyana kaNeriya, unyana kaMahaseya, ekuyeni kwakhe eBhabheli noZedekiya ukumkani wakwaYuda, ngomnyaka wesine wobukumkani bakhe; Ke uSeraya lo ke ebeyinkosana ezolileyo.', 'XHO22'),
(28, 51, 60, 'uYeremiya wabubhala encwadini bonke ububi obuya kuza eBhabheli, onke la mazwi abhaliweyo eBhabheli.', 'XHO22'),
(28, 51, 61, 'uYeremiya waqhubeka wathi kuSeraya: “Ukuba uthe wafika eBhabhiloni waza wabona, wawafunda onke la mazwi;', 'XHO22'),
(28, 51, 62, 'Uze uthi, Awu, Yehova, uthethe kakubi ngale ndawo ukuba uyitshabalalise, ingasali kuyo, kungasali mntu, kuthabathele nenkomo; yona iya kuba yintlango ngonaphakade.', 'XHO22'),
(28, 51, 63, 'Wakugqiba ukuyifunda le ncwadi, uze ubophe ilitye kuyo, uyiphose esazulwini somlambo umEfrati.', 'XHO22'),
(28, 51, 64, 'Uya kuthi, Iya kutsho ke iBhabheli ukuthi xumbu kwayo, ingaphumi ngenxa yobubi endiya kuyizisela bona; kwaye baya kudinwa. Amazwi kaYeremiya asamile.', 'XHO22'),
(28, 52, 1, 'uZedekiya ubeminyaka imashumi mabini anamnye ezelwe, ukuba ngukumkani kwakhe; waba neminyaka elishumi elinamnye engukumkani eYerusalem. Igama lonina belinguHamutali, intombi kaYeremiya, waseLibhena.', 'XHO22'),
(28, 52, 2, 'Wenza okubi emehlweni kaYehova, njengoYehoyakim.', 'XHO22'),
(28, 52, 3, 'Kwathi ngenxa yomsindo kaYehova kwenzeka eYerusalem nakwaYuda, wada wabagxotha ebusweni bakhe, kangangokuba uZedekiya wamvukela ukumkani waseBhabhiloni.', 'XHO22'),
(28, 52, 4, 'Ngonyaka wesithoba wolawulo lwakhe, ngenyanga yeshumi, ngomhla weshumi enyangeni leyo, uNebhukadenetsare ukumkani waseBhabhiloni wayihlasela iYerusalem nomkhosi wakhe wonke waza wayingqinga waza wakha iinqaba ngokuchasene nayo ukujikeleza.', 'XHO22'),
(28, 52, 5, 'Wangqingwa ke umzi, kwada kwangumnyaka weshumi elinamnye wokumkani uZedekiya.', 'XHO22'),
(28, 52, 6, 'Ngenyanga yesine, ngomhla wesithoba enyangeni leyo, yaba nkulu indlala esixekweni, kangangokuba abantu belizwe babengenasonka.', 'XHO22'),
(28, 52, 7, 'Wabhujiswa ke umzi, asaba onke amadoda anobukroti, aphuma phakathi komzi ebusuku ngesango eliphakathi kweendonga zombini ezisemyezweni wokumkani; Yaye amaKaledi ayekufuphi isixeko ngeenxa zonke, kwaye waqhubeka entlango.', 'XHO22'),
(28, 52, 8, 'Umkhosi wamaKaledi wamsukela ukumkani, wamthimba uZedekiya ezinkqantosini zaseYeriko; yaphangalala yonke impi yakhe, yemka kuye.', 'XHO22'),
(28, 52, 9, 'Bandula ke bambamba ukumkani baza bamzisa kukumkani waseBhabhiloni eRibhela kwilizwe laseHamati; apho wamgweba khona.', 'XHO22'),
(28, 52, 10, 'Ukumkani waseBhabheli wababulala oonyana bakaZedekiya ekhangele; Kananjalo wazibulala zonke iinkosana zakwaYuda eRibhela.', 'XHO22'),
(28, 52, 11, 'Wacinywa amehlo kaZedekiya; yaye ukumkani waseBhabhiloni wamkhonkxa ngemixokelelwane waza wamsa eBhabhiloni waza wamfaka entolongweni de kwayimini yokufa kwakhe.', 'XHO22'),
(28, 52, 12, 'Ngenyanga yesihlanu, ngomhla weshumi enyangeni leyo, oko kukuthi, unyaka weshumi elinesithoba kaNebhukadenetsare ukumkani waseBhabhiloni, uNebhuzaradan umphathi wamafanankosi, owayesebenzela ukumkani waseBhabhiloni, weza eYerusalem;', 'XHO22'),
(28, 52, 13, 'Yatshiswa indlu kaYehova, nendlu yokumkani; zatsha zonke izindlu zaseYerusalem, nezindlu zonke zamadoda amakhulu.', 'XHO22'),
(28, 52, 14, 'Lonke ibutho lomkhosi lamaKaledi, elinomthetheli wabacibi-mlilo, lazidiliza zonke iindonga zaseYerusalem ngeenxa zonke.', 'XHO22'),
(28, 52, 15, 'uNebhuzaradan umthetheli wabasiki, namahlwempu abantu, namasalela abantu, abaseleyo phakathi komzi, nabathinjwa abazinikele kukumkani waseBhabheli, nenxenye yabaninzi ababakhokelayo kude njengamabanjwa.', 'XHO22'),
(28, 52, 16, 'Ke uNebhuzaradan umthetheli wabasiki, wabashiya abazizisweli belizwe, ukuba babe ngabalimi nabalimi.', 'XHO22'),
(28, 52, 17, 'Ke iintsika zobhedu zendlu kaYehova, nezitya zokuhlambela, nentlabathi yobhedu, ezibe zisendlwini kaYehova, amaGalati aziqhekeza, aluthabatha kuzo lonke ubhedu olo, aya nazo eBhabheli.', 'XHO22'),
(28, 52, 18, 'Neembiza, nemihlakulwana, neeemele, nezitya zokutshiza, neenkamba, neempahla zonke zobhedu ebekulungiselelwa ngazo.', 'XHO22'),
(28, 52, 19, 'Neenkamba, neengcedevu, neenkamba, neepani, neziphatho, neenkamba, neendebe; igolide leyo ibiyigolide, nesilivere leyo, yamthabatha umthetheli wabakhandi.', 'XHO22'),
(28, 52, 20, 'Iintsika zambini, nolwandle olunye, neshumi elinambini leenkomo zobhedu, ezibe ziphantsi kweentsika ezo, awazenzayo ukumkani uSolomon endlwini kaYehova, alwaba nakulinganiswa ubhedu lwezo mpahla zonke.', 'XHO22'),
(28, 52, 21, 'Neentsika ezo, ukuphakama kwenye intsika bekuziikubhite ezilishumi elinesibhozo; nomtya ubuziikubhite ezilishumi elinambini ngeenxa zonke; ubungqingqwa bayo bebuyiminwe emine; ibingumngxuma.', 'XHO22'),
(28, 52, 22, 'Kwaye kukho intloko yobhedu phezu kwayo; ukuphakama kwentloko enye bekuziikubhite ezintlanu, intloko ephothiweyo nezithsaba ezijikelezileyo, zazezobhedu. Ikholamu yesibini kunye neziqhushumbisi zazinje.', 'XHO22'),
(28, 52, 23, 'Iirharnati ezingamashumi asithoba anesithandathu, ngapha nangapha; zonke iintsontelo zelinen ephothiweyo zaba likhulu ngeenxa zonke.', 'XHO22'),
(28, 52, 24, 'Umthetheli wabasiki wamthabatha uSeraya, umbingeleli omkhulu, noZefaniya, umbingeleli wesibini, nabagcini-masango bobathathu.', 'XHO22'),
(28, 52, 25, 'Yathabatha ithenwa phakathi komzi, umthetheli wamadoda okulwa; kwafunyanwa amadoda asixhenxe kukumkani phakathi komzi; nonobhala wamatyala, owabahlanganisayo abantu belizwe; namadoda angamashumi amathandathu kubantu belizwe, awafunyanwayo phakathi komzi.', 'XHO22'),
(28, 52, 26, 'Wabathabatha ke uNebhuzaradan umthetheli wabasiki, wabasa kukumkani waseBhabheli, eRibhela.', 'XHO22'),
(28, 52, 27, 'Ukumkani waseBhabhiloni waboyisa waza wababulala eRibhela kwilizwe laseHamati. Ngoko uYuda wathinjwa wemka kwilizwe lakhe.', 'XHO22'),
(28, 52, 28, 'Ngabo aba abantu awabafudusayo uNebhukadenetsare: ngomnyaka wesixhenxe, amaYuda angamawaka amathathu, anamanci mabini anamathathu;', 'XHO22'),
(28, 52, 29, 'Ngomnyaka weshumi elinamnye wobukumkani bukaNebhukadenetsare, wasusa eYerusalem amakhulu asibhozo, anamanci mathathu anamabini amadoda.', 'XHO22'),
(28, 52, 30, 'Ngomnyaka wamashumi omabini anesithathu wobukumkani bukaNebhukadenetsare, uNebhuzaradan umthetheli wabasiki wathabatha amakhulu asixhenxe anamanci mane anesihlanu abantu ekuthinjweni phakathi kwamaYuda; bonke abantu babengamawaka amane anamakhulu amathandathu.', 'XHO22'),
(28, 52, 31, 'Kwathi ngomnyaka wamashumi omathathu anesixhenxe wokuthinjwa kukaYehoyakin, ukumkani wakwaYuda, ngenyanga yeshumi elinesibini, ngosuku lwamashumi omabini anesihlanu enyangeni leyo, uEvili-merodaki, ukumkani waseBhabheli, ngomnyaka wokuqala wobukumkani bakhe. Waba ngukumkani phezu kwentloko kaYehoyakin, ukumkani wakwaYuda, wamkhupha entolongweni;', 'XHO22'),
(28, 52, 32, 'Wathetha kakuhle naye, wayimisa itrone yakhe, yangaphezu kweetrone zookumkani ababenaye eBhabheli.', 'XHO22'),
(28, 52, 33, 'Wanxiba izambatho zakhe zasentolongweni, waza waqhubeka esidla isonka phambi kobuso bakhe yonke imihla yobomi bakhe.', 'XHO22'),
(28, 52, 34, 'Umphako wakhe wawunikwa imihla ngemihla, kukumkani waseBhabheli, kwada kwayimini yokufa kwakhe, yonke imihla yobomi bakhe.', 'XHO22'),
(29, 1, 1, 'Hayi, ukuma komzi obemi khona! hayi indlela awayefana ngayo nomhlolokazi! Oyena mkhulu phakathi kweentlanga nokumkanikazi esahlukweni, utheni na ukuba abe ngumgwebi!', 'XHO22'),
(29, 1, 2, 'Ulila kakhulu ebusuku, iinyembezi zakhe zisezidleleni zakhe; kuzo zonke izithandwa zakhe akukho mthuthuzeli; Bonke abahlobo bakhe bamngcatshile baza baba ziintshaba zakhe.', 'XHO22'),
(29, 1, 3, 'uYuda wathinjwa ngenxa yokubandezeleka nangenxa yobukhoboka obuninzi; uhleli ezintlangeni, akabuboni ukuphumla; bonke abasukeli bakhe bambamba enyanyeni.', 'XHO22'),
(29, 1, 4, 'Izitrato zaseZiyon ziyakhala, ngokuba akukho namnye uzayo emithendelekweni; onke amasango ayo aludaka; ababingeleli bakhe bayancwina, Iintombi zakhe zinosizi, yena ebuhlungu.', 'XHO22'),
(29, 1, 5, 'Abachasi bakhe baninzi, ababandezeli bakhe banempumelelo; ngokuba uYehova wamxina ngenxa yezikreqo zakhe ezininzi; oonyana bakhe bathinjwa phambi kweentshaba.', 'XHO22'),
(29, 1, 6, 'Bonke ubuhle bayo buphumile entombini enguZiyon;', 'XHO22'),
(29, 1, 7, 'iYerusalem yazikhumbula, ngexesha leshwangusha layo neentsizi zayo, zonke izinto zayo ezinqwenelekayo ebinazo kwamandulo, Ekuweni kwabantu bayo phambi kotshaba, akwabakho mncedi; Abachasi bambona baza bahlekisa ngaye ngeesabatha zakhe.', 'XHO22'),
(29, 1, 8, 'iYerusalem yenze isono esikhulu; wasuswa ke ngoko; Bonke ababelihlonela babelidela, kuba bebona ukungcola kwalo; ewe wancwina wajonga ngasemva.', 'XHO22'),
(29, 1, 9, 'Ubunqambi bakhe busezingutyeni zakhe; akacingi ngekamva lakhe lokugqibela; ke ngoko, wemka ngendlela engaqhelekanga, engenamncedi. Yehova yibone inkxwaleko yam, ngokuba utshaba luzizukisile.', 'XHO22'),
(29, 1, 10, 'Utshaba lwesa isandla salo phezu kwezinto zalo zonke ezizithandayo, ngenxa enokuba libona iintlanga zingena engcweleni yakhe, owuwise umthetho wena, ukuba zingangeni ebandleni lakho.', 'XHO22'),
(29, 1, 11, 'Bonke abantu bakhe bayancwina, befuna isonka; babanika iilekese njengokutya kokuhlaziya umoya; yabona, Yehova, zigcine; ngokuba ndisuke ndaba ngongendawo.', 'XHO22'),
(29, 1, 12, 'Akunto na kuni nonke nina nidlula ngendlela? Khangelani nibone, ukuba kukho na umvandedwa onjengam, wenziweyo kum, andicinezele ngawo uYehova ngemini yomsindo wakhe.', 'XHO22'),
(29, 1, 13, 'Ephezulu, wathumela umlilo emathanjeni am, wawagubungela; Uzithiyele iinyawo zam, wandiguqula; Ebendiphethe kakubi ndidiniwe imini yonke.', 'XHO22'),
(29, 1, 14, 'Idyokhwe yezikreqo zam ibotshwe sisandla sakhe; Uwaphule amandla am, uYehova undinikele esandleni sabo, endingenakusuka kubo.', 'XHO22'),
(29, 1, 15, 'uYehova uwanyhashe onke amagorha phakathi kwam; Umeme ibandla ukuba laphule amadodana am; iNkosi yam ixovule intombi enguYuda, njengesixovulelo sewayini.', 'XHO22'),
(29, 1, 16, 'Ndiyazilela ezi zinto; iliso lam, iliso lam liphalaza amanzi, Ngokuba ekude kum umthuthuzeli owenza umphefumlo wam; Babhidekile abantwana bam, ngokuba utshaba loyisile.', 'XHO22'),
(29, 1, 17, 'iZiyon iyazolula izandla zayo, ayinamthuthuzeli; uYehova wawisa umthetho ngoYakobi, ukuba bamtyelele ngaphakathi; iYerusalem injengomfazi osexesheni phakathi kwabo.', 'XHO22'),
(29, 1, 18, 'uYehova ulilungisa; ngokuba bendiphikise umlomo wakhe.', 'XHO22'),
(29, 1, 19, 'Ndibabizile abandithandayo, basuka bandixokisa; ababingeleli bam namadoda amakhulu am bazinikele kuwo umzi, xa babefuna ukudla kwabo, ukuba bawubuyise umphefumlo wabo.', 'XHO22'),
(29, 1, 20, 'Khangela, Yehova! ngokuba ndibandezelekile; isisu sam asiphumli; Intliziyo yam iguqukile phakathi kwam; ngokuba bendiqhayisa kakhulu; ngaphandle baphanga umkhonto, Endlwini kukufa njengokufa.', 'XHO22'),
(29, 1, 21, 'Bandivile ukuba ndiyancwina; akukho bani undithuthuzelayo; Zonke iintshaba zam zibuvile ububi bam; bayavuya kuba eyenzile; Uya kuyizisa imini oyimemileyo, ziya kuba njengam ke.', 'XHO22'),
(29, 1, 22, 'Mabufike phambi kwakho bonke ububi bazo; uze wenze kuzo kanye njengoko undenze ngako ngenxa yokunxaxha kwam konke, ngenxa yokuba zininzi izono zam nentliziyo yam ivaliwe.', 'XHO22'),
(29, 2, 1, 'iNkosi iyigubungele intombi enguZiyon ngefu ngomsindo wayo, izikhuphe ezulwini ubuhle bukaSirayeli, yayisa emhlabeni; ayisayi kusikhumbula isihlalo seenyawo zayo ngemini yomsindo wayo.', 'XHO22'),
(29, 2, 2, 'Wayiphanzisa yonke iminquba kaYakobi uYehova, akaba namfesane kuye; Ngomsindo wakhe wazibhukuqa iimboniselo zentombi enguYuda; Wawakhahlela phantsi; Wabenza inqambi ubukumkani nabathetheli babo.', 'XHO22'),
(29, 2, 3, 'Ngomsindo wakhe wazitshabalalisa zonke iimpondo zikaSirayeli, Wasusa isandla sakhe sokunene elutshabeni, Wamtshisa uYakobi njengomlilo olenya ngeenxa zonke.', 'XHO22'),
(29, 2, 4, 'Isityedile isaphetha sayo njengotshaba; Wema ngasekunene kwakhe, enjengombandezeli, wayibulala yonke into enayo emehlweni akhe, ententeni yentombi enguZiyon; Wawuphalaza njengomlilo umsindo wakhe.', 'XHO22'),
(29, 2, 5, 'uYehova waba njengotshaba, esidla uSirayeli, ewaginya zonke iingxande zakhe ezinde;', 'XHO22'),
(29, 2, 6, 'Wawuhluba umsimelelo wakhe ngogonyamelo, ngokungathi uphuma entsimini; wazitshabalalisa iindawo zakhe zokuhlangana; uYehova uyilibele imithendeleko neesabatha zaseZiyon, yaye ekuvutheni komsindo wakhe, uye wamcekisa ukumkani nombingeleli.', 'XHO22'),
(29, 2, 7, 'iNkosi yam isilahlile isibingelelo sayo, iyisimbile ingcwele yayo, izinikele iindonga zeengxande zayo ezinde esandleni sotshaba; benza ingxolo endlwini kaYehova njengakwimini yomthendeleko.', 'XHO22'),
(29, 2, 8, 'uYehova ucinge ukonakalisa udonga lwentombi enguZiyon; Wolula umsonto, asatyeka enzonzobileni isandla sakhe; ke ngoko, wenza iindonga zikhala; badanile kunye.', 'XHO22'),
(29, 2, 9, 'Amasango akhe atshonile emhlabeni; Amazwi akhe atshabalalisa, ayaphula; ukumkani wayo nabathetheli bayo baphakathi kweentlanga; umthetho awusekho; nabaprofeti bayo ababuboni ubuso bukaYehova.', 'XHO22'),
(29, 2, 10, 'Ahlala phantsi athi cwaka amadoda amakhulu entombi enguZiyon; bagalela uthuli entloko; banxibe iingubo ezimnyama; Iintombi zaseYerusalem zithobe iintloko zazo.', 'XHO22'),
(29, 2, 11, 'Amehlo am azele zinyembezi, ziyabila isisu sam, intliziyo yam ibuhlungu ngenxa yokwaphuka kwentombi yabantu bakowethu; ngokuba besifa isiqaqa abantwana neentsana ezitratweni zomzi.', 'XHO22'),
(29, 2, 12, 'Bathi koonina, Iphi na ingqolowa newayini? Baphakanyiswa njengabangxwelerhiweyo ezitratweni zomzi, Ekuziphalazeni kwemiphefumlo yabo ezifubeni zoonina.', 'XHO22'),
(29, 2, 13, 'Ndiya kungqina ngantoni na kuwe? Ndikufanekise nantoni na, ntombi yaseYerusalem? Ndibona ntoni na kuwe, ukuze ndikuthuthuzele, ntombindini iyintombi, inguZiyon? ngokuba kukhulu ngangolwandle ixesha lakho; ngubani na onokukuphilisa?', 'XHO22'),
(29, 2, 14, 'Abaprofeti bakho bakubonisa izinto ezililize nezibubudenge, yaye ababutyhilanga ububi bakho ukuze bakuthintele ukuthinjwa; ke ndanibona imithwalo ebuxoki, nokugxothwa.', 'XHO22'),
(29, 2, 15, 'Bonke abadlulayo bancoma; Bavuthela umlozi, bahlunguzela iintloko ngentombi enguYerusalem, besithi, Ngulo mzi na lo, lo kuthiwa kuphela kwenzwakazi, yimihlali yehlabathi lonke?', 'XHO22'),
(29, 2, 16, 'Zonke iintshaba zakho zikwakhamisela umlomo; babetha umlozi kwaye batshixizela amazinyo; bathi, Simdlile; ngenene le yimini ebesiyilindele; siyifumene, sayibona.', 'XHO22'),
(29, 2, 17, 'Wenza ke uYehova njengoko wayecinga; Wasifeza isigwebo, awayesiwisele umthetho kwakudala, esithi, Wawa, akananceba; uluvuyisile utshaba lwakho, Luphakamise uphondo lwababandezeli bakho.', 'XHO22'),
(29, 2, 18, 'Intliziyo yabo ikhala kuYehova, Ludonga lwentombi enguZiyon, maziphume iinyembezi njengomlambo imini nobusuku linganqabi iliso lakho.', 'XHO22'),
(29, 2, 19, 'Vuka, ukhale ebusuku; yiphalaze njengamanzi intliziyo yakho phambi koYehova, ekuqaleni kwemilindo; ziphakamisele kuyo izandla zakho, ngenxa yomphefumlo wabantwana bakho, ababulawa yindlala emantloko ezitrato zonke.', 'XHO22'),
(29, 2, 20, 'Khangela, Yehova, ubone ukuba wenze kubani na le nto. Ngaba abafazi baya kudla iziqhamo zabo nabantwana ixesha elide? Ngaba umbingeleli nomprofeti baya kubulawa engcweleni kaYehova?', 'XHO22'),
(29, 2, 21, 'Balele phantsi emhlabeni amadoda amakhulu; Umthinjana wam namadodana am awile likrele; Wababulala ngemini yomsindo wakho; nabulala, akwacoleka.', 'XHO22'),
(29, 2, 22, 'Nakubiza ukundoyika kwam ezindaweni zonke, ngokwemini yomthendeleko, akwabakho usindayo, usalayo, ngemini yomsindo kaYehova;', 'XHO22'),
(29, 3, 1, 'Mna ke ndingumntu obone intlupheko ngentonga yokuphuphuma komsindo wakhe.', 'XHO22'),
(29, 3, 2, 'Wandikhokela wandisa ebumnyameni, akwaba sekukhanyeni.', 'XHO22'),
(29, 3, 3, 'Okunene, undijikile; undijikela imini yonke isandla sakhe.', 'XHO22'),
(29, 3, 4, 'Uyondlekile inyama yam nolusu lwam; uwaphule amathambo am.', 'XHO22'),
(29, 3, 5, 'Wandakhela phezu kwam waza wandirhangqa ngenyongo nenkathazo.', 'XHO22'),
(29, 3, 6, 'Undibeke ezindaweni ezimnyama, njengabafayo kwakudala;', 'XHO22'),
(29, 3, 7, 'Undibiyele ngothango, ukuba ndingabi nakuphuma; Wayenza nzima ityathanga lam.', 'XHO22'),
(29, 3, 8, 'Nokuba ndibize, ndidanduluke, awuwuva umthandazo wam.', 'XHO22'),
(29, 3, 9, 'Uzibiyele iindlela zam ngamatye aqingqiweyo, uzenze zaba gwenxa iindlela zam.', 'XHO22'),
(29, 3, 10, 'Kum ube njengebhere elalele, enjengengonyama izimele;', 'XHO22'),
(29, 3, 11, 'Uzitshintshile iindlela zam, wandiqwenga; Undenze inkqantosi.', 'XHO22'),
(29, 3, 12, 'Wasigoba isaphetha sakhe, wandimisa ndibe lutolo lotolo.', 'XHO22'),
(29, 3, 13, 'Wafaka iintolo zekhondom yakhe ezintso zam.', 'XHO22'),
(29, 3, 14, 'Ndisuke ndayintlekisa kubantu bonke bakowethu; bacula imini yonke.', 'XHO22'),
(29, 3, 15, 'Undihluthise ngezinto ezikrakra, wandiseza incindi enencasa.', 'XHO22'),
(29, 3, 16, 'Ubuye wawatyumza ngohlalu amazinyo am, wandixinzelela phantsi ngothuthu.', 'XHO22'),
(29, 3, 17, 'Uwukhwelisile umphefumlo wam eluxolweni; Ndiyilibele impumelelo.', 'XHO22'),
(29, 3, 18, 'Ndathi ke, Amandla am nethemba lam likuYehova;', 'XHO22'),
(29, 3, 19, 'Zikhumbule iimbandezelo zam, neembandezelo zam, nentlabathi, nenyongo;', 'XHO22'),
(29, 3, 20, 'Umphefumlo wam uyazikhumbula unangoku ezo zinto, usibekekile kum.', 'XHO22'),
(29, 3, 21, 'Ndikhumbula oku engqondweni yam, ngenxa yoko ndinethemba.', 'XHO22'),
(29, 3, 22, 'Ziinceba zikaYehova le nto ingasayi kutshabalala, ngokuba ingunaphakade inceba yakhe.', 'XHO22'),
(29, 3, 23, 'Iyahlaziyeka imiso ngemiso; kukhulu ukuthembeka kwakho.', 'XHO22'),
(29, 3, 24, 'Isabelo sam nguYehova, utsho umphefumlo wam; ke ngoko, ndiya kumthemba.', 'XHO22'),
(29, 3, 25, 'uYehova unguThixo kwabakholose ngaye, kumphefumlo omquqelayo.', 'XHO22'),
(29, 3, 26, 'Kulungile ukuba umntu alindele usindiso lukaYehova, aze alinde ngokuthe cwaka.', 'XHO22'),
(29, 3, 27, 'Kuyilungele indoda ukuba iyithwale idyokhwe kwasebutsheni bayo.', 'XHO22'),
(29, 3, 28, 'Uhleli yedwa, wonwabile;', 'XHO22'),
(29, 3, 29, 'Uwubeka eluthulini umlomo wakhe; ukuba kunjalo, kusenokubakho ithemba.', 'XHO22'),
(29, 3, 30, 'Unika lowo umbethayo isidlele sakhe; uzele ziintloni.', 'XHO22'),
(29, 3, 31, 'Ngenxa yokuba uYehova akayi kumshiya ngonaphakade.', 'XHO22'),
(29, 3, 32, 'Nokuba uthe wenza umvandedwa, woba nemfesane ngokobuninzi benceba yakhe.', 'XHO22'),
(29, 3, 33, 'Kuba uyabacudisa oonyana babantu ngokuzithandela nangentuthumbo.', 'XHO22'),
(29, 3, 34, 'Ukunyathela phantsi kweenyawo zakhe bonke abathinjwa behlabathi;', 'XHO22'),
(29, 3, 35, 'Uyaphambuka egunyeni lakhe phambi koPhezukonke,', 'XHO22'),
(29, 3, 36, 'uYehova akakuthandi ukumjongela phantsi umntu kwishishini lakhe.', 'XHO22'),
(29, 3, 37, 'Ngubani othethayo, yaye yintoni eyenzekayo xa uYehova engayaleli?', 'XHO22'),
(29, 3, 38, 'Akukho okubi nokulungileyo emlonyeni wOsenyangweni na?', 'XHO22'),
(29, 3, 39, 'Yini na ukuba umntu akhalaze ngenxa yezono zakhe?', 'XHO22'),
(29, 3, 40, 'Masizihlole, sizivavanye iindlela zethu, size sibuyele kuYehova.', 'XHO22'),
(29, 3, 41, 'Masizinyuse iintliziyo zethu nezandla zethu, sizise kuThixo osemazulwini;', 'XHO22'),
(29, 3, 42, 'Sikreqile, sigwilikile; Anixolanga.', 'XHO22'),
(29, 3, 43, 'Wazambesa ngomsindo, wasitshutshisa; Wabulala, akwaba nanceba.', 'XHO22'),
(29, 3, 44, 'Uzigqubuthele ngelifu, ukuze ungafiki umthandazo wethu.', 'XHO22'),
(29, 3, 45, 'Usenze amatyotyombe nenkunkuma phakathi kwabantu.', 'XHO22'),
(29, 3, 46, 'Zisakhamisele umlomo wazo zonke iintshaba zethu;', 'XHO22'),
(29, 3, 47, 'Sifikelwe kukunkwantya, nemigibe, nokwaphuka nokwaphuka.', 'XHO22'),
(29, 3, 48, 'Iliso lam lihla imijelo yamanzi, ngenxa yokwaphuka kwentombi yabantu bakowethu.', 'XHO22'),
(29, 3, 49, 'Iliso lam liyaphalala, aliyi kuma;', 'XHO22'),
(29, 3, 50, 'de uYehova wakhangela phantsi waza wabona esezulwini.', 'XHO22'),
(29, 3, 51, 'Iliso lam liyayibetha intliziyo yam, ngenxa yeentombi zonke zomzi wakowethu.', 'XHO22'),
(29, 3, 52, 'Zindizingele njengentaka iintshaba zam ngelize;', 'XHO22'),
(29, 3, 53, 'Babubulalela umphefumlo wam emhadini, bandigibisela ngamatye.', 'XHO22'),
(29, 3, 54, 'Amanzi ahamba phezu kwentloko yam; Ndathi ke, Nditshabalele.', 'XHO22'),
(29, 3, 55, 'Ndibize igama lakho, Yehova, ndisemhadini.', 'XHO22'),
(29, 3, 56, 'Ulivile ilizwi lam; musa ukuyifihla indlebe yakho emphefumlweni wam, ekuzibikeni kwam.', 'XHO22'),
(29, 3, 57, 'Usondele imini endakubizayo; ubusithi, Musa ukoyika.', 'XHO22'),
(29, 3, 58, 'Owu, Yehova, uya kuyiphikisa imicimbi yomphefumlo wam; Wabusindisa ubomi bam.', 'XHO22'),
(29, 3, 59, 'Yehova, ukubonile ukungalungisi kwam; ligwebe ityala lam ngenxa yam.', 'XHO22'),
(29, 3, 60, 'Uyibonile yonke impindezelo yabo, uzivile zonke iingcinga zabo ngam.', 'XHO22'),
(29, 3, 61, 'Yehova, ukuvile ukudelela kwabo, uzivile zonke iingcinga zabo ngam;', 'XHO22'),
(29, 3, 62, 'Umlomo wabakreqayo kum, Namayelenqe abo ngam imini yonke.', 'XHO22'),
(29, 3, 63, 'Khangela indlela abahlala ngayo kunye nendlela abama ngayo; Ngumculo wabo.', 'XHO22'),
(29, 3, 64, 'Yehova, bavuze ngokomsebenzi wezandla zabo.', 'XHO22'),
(29, 3, 65, 'Banike ubuthuntu bentliziyo, intshwabulo yakho mayibe kubo;', 'XHO22'),
(29, 3, 66, 'Basukeleni nibatshabalalise ngomsindo phantsi kwamazulu kaYehova.', 'XHO22'),
(29, 4, 1, 'Hayi, ukuba mfiliba kwegolide! Hayi, ukuguquguquka kwegolide ecikizekileyo! amatye engcwele aphalazwe ezitratweni zonke.', 'XHO22'),
(29, 4, 2, 'Zintanda, Ziyon, xa kuthelekiswa negolide engcwengiweyo, Hayi, ukubalelwa ekuthini zizitya zodongwe, umsebenzi wezandla zombumbi!', 'XHO22'),
(29, 4, 3, 'Kwaneemlonya ziwarhola amabele, Zanyise amathole azo; Ke yona intombi yabantu bam isuke yaba sisijorha njengenciniba entlango.', 'XHO22'),
(29, 4, 4, 'Ulwimi losana lunamathela ekhuhlangubeni lakhe linxano; Abantwana bacela isonka, akukho ubakhathaleleyo.', 'XHO22'),
(29, 4, 5, 'Ababedlile babonakala ezitratweni;', 'XHO22'),
(29, 4, 6, 'Kuba isohlwayo sobubi bentombi yabantu bam sikhulu kunesohlwayo sesono seSodom, eyabhukuqwayo ngephanyazo, akwabikho zandla ziseleyo kuyo.', 'XHO22'),
(29, 4, 7, 'Babeqaqambile abaNaziri bakhe ngaphezu kwekhephu, bemhlophe ngaphezu kobisi, beyingqombela kuneerubhi emzimbeni, ukubengezela kwabo kunjengesafire.', 'XHO22'),
(29, 4, 8, 'Ubuso babo bumnyama ngaphezu kwamalahle; Abanakwa ezitratweni; ulusu lwabo lutshele emathanjeni abo; yoma yanjengentonga.', 'XHO22'),
(29, 4, 9, 'Abo babulewe ngekrele balunge ngakumbi kunabo babulawa yindlala, ngenxa yokuba ngabo baluvulayo ucango, boyisiwe ngenxa yokunqongophala kweziqhamo zelizwe.', 'XHO22'),
(29, 4, 10, 'Izandla zomfazi onenceba ziseza abantwana; kukudla kwabo ekubhujisweni kwentombi yabantu bam.', 'XHO22'),
(29, 4, 11, 'uYehova uwufezile umsindo wakhe; Uwuphalazile umsindo wakhe waza wafaka umlilo eZiyon, wazitshisa neziseko zayo.', 'XHO22'),
(29, 4, 12, 'Bebengayi kukholwa ookumkani belimiweyo, nabemi bonke belimiweyo, ukuba amasango aseYerusalem alutshaba;', 'XHO22'),
(29, 4, 13, 'ngenxa yezono zabaprofeti bakhe, nangenxa yezinto ezimbi zababingeleli bakhe, abaphalaza igazi lamalungisa kuyo;', 'XHO22'),
(29, 4, 14, 'Babebhadula ezitalatweni njengeemfama, bedyobhekile ligazi, ukuze abantu bangabi nakuchukumisa neempahla zabo.', 'XHO22'),
(29, 4, 15, 'Bathi kuwo, Hambani; ayicocekanga; mkani, mkani, musani ukuphatha; ekubalekeni kwabo, babhadula ezintlangeni, bathi, Abayi kubuya baphambukele khona.', 'XHO22'),
(29, 4, 16, 'Umsindo kaYehova wabahlula; akasayi kuba sabakhangela; abakhange babe nesimilo sababingeleli, bengawathandi amadoda amakhulu.', 'XHO22'),
(29, 4, 17, 'Thina, amehlo ethu akhangele uncedo lwethu olungamampunge; ngokuqaphela kwethu sasilindele isizwe esingenako ukusigcina.', 'XHO22'),
(29, 4, 18, 'Bazingela amanyathela ethu, ukuba singahambi ezitratweni zethu; ngokuba kufikile ukuphela kwethu.', 'XHO22'),
(29, 4, 19, 'Abasisukelayo banamendu ngaphezu kwexhalanga lezulu;', 'XHO22'),
(29, 4, 20, 'Umoya wamathatha ethu, umthanjiswa kaYehova, ubambeke equleni labo, ebesithi ngaye, Siya kuhlala emthunzini wakhe ezintlangeni.', 'XHO22'),
(29, 4, 21, 'Yiba nemihlali ugcobe, ntombi yakwaEdom, uhleliyo ezweni lakowethu; idlule phezu kwakho indebe; uya kunxila ukhulule.', 'XHO22'),
(29, 4, 22, 'Isohlwayo sobungendawo bakho sigqityiwe, ntombi yaseZiyon! Akayi kuphinda athimbe; Ubuvelelele ububi bakho, ntombi yakwaEdom! uya kuzityhila izono zakho.', 'XHO22'),
(29, 5, 1, 'Kukhumbule, Yehova, okusihleleyo; Lumka, ulibone ihlazo lethu.', 'XHO22'),
(29, 5, 2, 'Ilifa lethu lidlulele kwabasemzini, nezindlu zethu kolunye uhlanga;', 'XHO22'),
(29, 5, 3, 'Siziinkedama neenkedama, Ooomama bethu ngabahlolokazi.', 'XHO22'),
(29, 5, 4, 'Amanzi ethu siwasele ngemali; iinkuni zethu zathengiswa.', 'XHO22'),
(29, 5, 5, 'Zicinezelekile iintamo zethu: Sisebenza nzima, asiphumli.', 'XHO22'),
(29, 5, 6, 'Sasiza kumaYiputa nama-Asiriya, ukuba ahluthe ngesonka;', 'XHO22'),
(29, 5, 7, 'Oobawo bonile, abasekho; sabuthwala ubugwenxa babo.', 'XHO22'),
(29, 5, 8, 'Besilawulwa ngabakhonzi, kungekho usihlangulayo esandleni sabo.', 'XHO22'),
(29, 5, 9, 'Isonka sethu sisithabatha ngokubeka umphefumlo wethu esichengeni, ngenxa yekrele entlango.', 'XHO22'),
(29, 5, 10, 'Ulusu lwethu lumnyama njengeziko Ngenxa yokulamba.', 'XHO22'),
(29, 5, 11, 'Bazukisa abafazi baseZiyon, Umthinjana emizini yakwaYuda.', 'XHO22'),
(29, 5, 12, 'Baxhonywe abathetheli ngezandla; ubuso bamadoda amakhulu buhlazekile.', 'XHO22'),
(29, 5, 13, 'Bathabatha abafana ukuba basile, abantwana bawa phantsi kweenkuni.', 'XHO22'),
(29, 5, 14, 'Amadoda amakhulu ema esangweni, amadodana eneengoma zawo;', 'XHO22'),
(29, 5, 15, 'Iphelile imihlali yentliziyo yethu; Umngqungqo wethu ube sisikhalo.', 'XHO22'),
(29, 5, 16, 'Siwele isithsaba entlokweni yethu; Yeha ke thina xa sonile!', 'XHO22'),
(29, 5, 17, 'Ngenxa yoko idiniwe intliziyo; ngenxa yezo zinto amehlo ethu anorhatyazo.', 'XHO22'),
(29, 5, 18, 'Ngenxa yentaba yaseZiyon, kuba ibharhile, Kuhamba iimpungutye phezu kwayo.', 'XHO22'),
(29, 5, 19, 'Yehova, uhleli ngonaphakade; Itrone yakho ikwisizukulwana ngesizukulwana.', 'XHO22'),
(29, 5, 20, 'Yini na ukuba usilibale ngonaphakade, Usishiye kude kube kude?', 'XHO22'),
(29, 5, 21, 'Yehova, masizohlwaye ngawe, size sizohlwaye; hlaziya imihla yethu njengesiqhelo.', 'XHO22'),
(29, 5, 22, 'Kodwa yena usilahlile okunene; unomsindo omkhulu kuthi.', 'XHO22'),
(30, 1, 1, 'Ngomnyaka wamashumi omathathu, ngenyanga yesine, ngolwesihlanu usuku enyangeni leyo, ndakubon’ ukuba ndiphakathi kwabathinjwe emlanjeni oyiKebhare, avuleka amazulu, ndabona imibono kaThixo.', 'XHO22'),
(30, 1, 2, 'Ngomhla wesihlanu enyangeni leyo, oko kukuthi, umnyaka wesihlanu wekratshi lokumkani uYehoyakin.', 'XHO22'),
(30, 1, 3, 'Lafika ilizwi likaYehova ngokungqalileyo kuHezekile unyana kaBhusi umbingeleli kwilizwe lamaKhaledi kufuphi nomlambo iKebhare; saba phezu kwakhe khona isandla sikaYehova.', 'XHO22'),
(30, 1, 4, 'Ndabona, nanko kukho umoya ovuthuzayo, uvela ngasentla, uvela ngelifu elikhulu, nomlilo ogubungelayo, kukukhanya okumhlophe, kuphuma phakathi komlilo;', 'XHO22'),
(30, 1, 5, 'Kwabakho ke kuwo umzekeliso wezinto eziphilileyo zone. Imbonakalo yawo ibinje; babenemo yomntu.', 'XHO22'),
(30, 1, 6, 'Inye ibe inobuso obune, inye ibe inamaphiko amane.', 'XHO22'),
(30, 1, 7, 'Imilenze yazo ibiyimilenze ethe tye; umphantsi weenyawo zazo ubunjengomphantsi wenqina lethole, zibengezela ngokwembonakalo yobhedu olukhazimlisiweyo.', 'XHO22'),
(30, 1, 8, 'Zazinezandla zomntu phantsi kwamaphiko azo emacaleni azo omane; zone kuzo zazinobuso namaphiko.', 'XHO22'),
(30, 1, 9, 'Amaphiko azo abehlangene; ababuyanga; bahamba baya phambili ngabanye.', 'XHO22'),
(30, 1, 10, 'Ke imfano yobuso bazo ibinobuso obune bomntu, nobuso bengonyama bune ngasekunene; zibe zinobuso benkomo zone ngasekhohlo; zinobuso bokhozi zone.', 'XHO22'),
(30, 1, 11, 'Ubuso bazo babunjalo, namaphiko azo ayelukile; iingalo zombini zazihlangene, zombini zazigqubuthele imizimba yazo.', 'XHO22'),
(30, 1, 12, 'Bonke baya ngaphambili; apho sukuba umoya ubufuna ukuya khona, zaya khona; kwaye abazange babuye ekuhambeni kwabo.', 'XHO22'),
(30, 1, 13, 'Imfano yezinto eziphilileyo ezo, imbonakalo yazo, ibingathi ngamalahle omlilo abasiweyo; ibingathi yimbonakalo yezikhuni ezitshangazayo; kwabengezela umlilo, kwaphuma imibane emlilweni.', 'XHO22'),
(30, 1, 14, 'Izinto eziphilileyo bezigidima zibuye, ngokokubonakala kombane.', 'XHO22'),
(30, 1, 15, 'Ndakhangela ezintweni eziphilileyo, ndabona iivili emhlabeni, ecaleni kwezinto eziphilileyo, zinganye iivili kubuso ngobuso bazo obune.', 'XHO22'),
(30, 1, 16, 'Ukubonakala kweevili nokusetyenzwa kwazo kwakunjengokubonakala kweevili nokubukeka kwazo zone, zibe zifana ngokufanayo, ukubonakala kwazo nokusetyenzwa kwazo kwakunjengevili phakathi kwevili.', 'XHO22'),
(30, 1, 17, 'Ekuhambeni kwakhe wahamba kumacala awo omane, yaye akazange ajike ekuhambeni kwakhe.', 'XHO22'),
(30, 1, 18, 'Amakhonkco awo ebephakame ngokoyikeka; Amakhonkco azo ebezele ngamehlo ngeenxa zonke kuzo zone.', 'XHO22'),
(30, 1, 19, 'Zakuhamba izinto eziphilileyo, zahamba nazo iivili kunye nazo; zakusuka izinto eziphilileyo emhlabeni, zasuka nazo iivili.', 'XHO22'),
(30, 1, 20, 'Apho sukuba ubusiya khona umoya, zaya apho sukuba umoya ubusiya khona; zasuka iivili, ngokokuba umoya wento ephilileyo ubukho ezivilini ezo.', 'XHO22'),
(30, 1, 21, 'Bakuba bemkile, naye wemka; zema, zema; zakusuka emhlabeni, zasuka iivili kunye nazo, ngokokuba umoya wezinto eziphilileyo ubukho ezivilini ezo.', 'XHO22'),
(30, 1, 22, 'Umfanekiselo wezulu phezu kweentloko zezidalwa eziphilileyo ube ufana nelitye lekristale eyoyikekayo, phezu kwentloko yazo;', 'XHO22'),
(30, 1, 23, 'Ngaphantsi kwesibhakabhaka amaphiko ayewolule, elinye emva kwelinye, iyileyo inamabini agubungeleyo kweli cala, elinye inamabini ayigubungeleyo imizimba yazo kweli cala.', 'XHO22'),
(30, 1, 24, 'Ekuhambeni kwazo ndeva isandi samaphiko azo, sinjengesandi samanzi amaninzi, ngathi sisandi soSomandla, sisandi sentetho, ngathi sisandi somkhosi; zakuma, zawathoba amaphiko azo.', 'XHO22'),
(30, 1, 25, 'Kuthe ke kwakuvakala izwi liphuma entlokweni yazo, zesuka zawathoba amaphiko azo.', 'XHO22'),
(30, 1, 26, 'Ngaphezu kwendawo ephezu kweentloko zazo, kwabonakala kunjengetrone, wanga lilitye lesafire;', 'XHO22'),
(30, 1, 27, 'Ndabona ke, ngathi yimbonakalo yobhedu, ngathi yimbonakalo yomlilo kuyo, kuthabathela embonakalweni yesinqe kwenyusa; ukukhanya macala onke.', 'XHO22'),
(30, 1, 28, 'Ngathi yimbonakalo yomnyama obakhoyo efini mini ngemvula, kwabonakala ukukhanya, njengako yonke indawo. Le yimbonakalo yembonakalo yobuqaqawuli bukaYehova. Ndithe ndakuyibona le nto, ndawa ngobuso, ndaliva ilizwi lomntu othethayo.', 'XHO22'),
(30, 2, 1, 'Wathi kum, Nyana womntu, yima ngeenyawo zakho, ndithethe nawe.', 'XHO22'),
(30, 2, 2, 'Kwangena ke kum umoya ethetha nam, wandimisa ndema, ndamva ethetha nam.', 'XHO22'),
(30, 2, 3, 'Waza wahlabela mgama wathi kum: “Nyana womntu, ndikuthuma koonyana bakaSirayeli, kubantu abazele yimvukelo, abajikileyo kum; bona bandingcatshile bona nooyise, unanamhla.', 'XHO22'),
(30, 2, 4, 'Ngokuba ngabantwana abangenabuntu, abantliziyo ilukhuni ndikuthuma kubo; uze uthi kubo, Itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 2, 5, 'Yaye bona, ukuba bafuna ukuva okanye abayeke baqhubeke (kuba yindlu yemvukelo), baya kwazi ukuba bekukho umprofeti phakathi kwabo.', 'XHO22'),
(30, 2, 6, 'Wena ke, nyana womntu, musa ukuboyika, ungawoyiki amazwi abo; ungawoyiki amazwi abo, ungawoyiki amehlo abo, ngokuba yindlu ezele lukreqo.', 'XHO22'),
(30, 2, 7, 'Wowathetha kubo amazwi am, kungakhathaliseki ukuba bathe baweva, nokuba bathe bayama, ngokuba beyienkani ezinkulu.', 'XHO22'),
(30, 2, 8, 'Wena ke, nyana womntu, yiva endikuthethayo kuwe; musa ukuba nekratshi, njengaloo ndlu ineenkani; vula umlomo wakho, utye endikunika kona.', 'XHO22'),
(30, 2, 9, 'Ndathi ndakukhangela, ndabona isandla sithunyelwe kum; nantso incwadi kuyo;', 'XHO22'),
(30, 2, 10, 'Wayityhila ke phambi kwam; kwaye kubhaliwe ngaphakathi nangaphandle, kwaye kubhaliwe ukulila nokulila kunye namashwa.', 'XHO22'),
(30, 3, 1, 'Wathi ke kum, Nyana womntu, kudle okufumanayo; yidle le ncwadi isongwayo, uye uthethe kwindlu kaSirayeli.', 'XHO22'),
(30, 3, 2, 'Ndawuvula ke umlomo wam, wandidlisa loo ncwadi isongwayo;', 'XHO22'),
(30, 3, 3, 'Wathi kum, Nyana womntu, sidle isisu sakho, uzalise isisu sakho ngomsebenzi endikunikayo; Ndayidla ke; emlonyeni wam ebumnandini njengobusi.', 'XHO22'),
(30, 3, 4, 'Wathi kum, Nyana womntu, hamba uye kwindlu kaSirayeli, uthethe kubo amazwi am.', 'XHO22'),
(30, 3, 5, 'Kuba anithunyelwanga kubantu abalulwimi lunzima, nabalulwimi lunzima, nithunyelwe kwindlu kaSirayeli;', 'XHO22'),
(30, 3, 6, 'Asikuko ngenxa yabantu abaninzi abalulwimi lumbi, abalulwimi lunzima, abamagama angavakaliyo; Inene, ukuba bendikuthume kubo, ngebekuvile.', 'XHO22'),
(30, 3, 7, 'Akavuma ke indlu kaSirayeli ukukuphulaphula; ngenxa yokuba abafuni kundiphulaphula, ngenxa yokuba yonke indlu kaSirayeli yonakele nentliziyo yabo ilukhuni.', 'XHO22'),
(30, 3, 8, 'Yabona, ndibenza lukhuni ubuso bakho, njengobuso babo; ibunzi lakho libe lukhuni, kwanjengebunzi labo;', 'XHO22'),
(30, 3, 9, 'Ndilenza laba lukhuni ngaphezu kwelitye ibunzi lakho; musa ukuboyika, ungaqhiphuki umbilini ngamehlo abo, nokuba yindlu ezele lukreqo.', 'XHO22'),
(30, 3, 10, 'Waqhubeka wathi kum: “Nyana womntu, wathabathele entliziyweni yakho onke amazwi am endiya kuwathetha kuwe, uweve ngeendlebe zakho.', 'XHO22'),
(30, 3, 11, 'Yiya kubathinjwa, koonyana babantu bakowenu, uthethe kubo, uthi kubo, Itsho iNkosi uYehova; okanye baya kuva, okanye bayeke.', 'XHO22'),
(30, 3, 12, 'Wandithabatha uMoya, ndeva emva kwam izwi elikhulu, lisithi, Mabubongwe ubuqaqawuli bukaYehova, endaweni yabo.', 'XHO22'),
(30, 3, 13, 'Ndeva nesandi samaphiko ezinto eziphilileyo, elo libetha elo, nesandi seevili ngakuzo, nesandi sesandi esikhulu.', 'XHO22'),
(30, 3, 14, 'Wandifunqula uMoya, wandisusa; Ke sona isandla sikaYehova saba phezu kwam.', 'XHO22'),
(30, 3, 15, 'Ndafika kubathinjwa eTelabhibhi, ababehleli ngasemlanjeni oyiKebhare, ndahlala khona, ndahlala khona iintsuku zasixhenxe, ndimangalisiwe, phakathi kwabo.', 'XHO22'),
(30, 3, 16, 'Ekupheleni kweentsuku ezisixhenxe kwabonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 3, 17, 'Nyana womntu, ndikwenze umveleli wendlu kaSirayeli; live ilizwi emlonyeni wam, ubalumkise ngokuphuma kum.', 'XHO22'),
(30, 3, 18, 'Ukuba ndithe kongendawo, Inene, uya kufa; ungathethi naye umoni, ulumkise umoni ngendlela yakhe embi, ukuba awusindise umphefumlo wakhe; Ongendawo uya kufa ngobungendawo bakhe; ke igazi lakhe ndolibiza esandleni sakho.', 'XHO22'),
(30, 3, 19, 'Wena ke, xa uthe wamvuselela ongendawo, akabuya ebubini bakhe, nangendlela yakhe engendawo, wofa ngobugwenxa bakhe; kodwa uwuhlangule umphefumlo wakho.', 'XHO22'),
(30, 3, 20, 'Kananjalo, ukuba lithe ilungisa libuya ebulungiseni balo, lenza okungendawo, ndibeke isikhubekiso phambi kwalo, liya kufa; ngokuba ungamlumkisanga, wofa ngenxa yesono sakhe, nangenxa yobulungisa bakhe abenzileyo abayi kukhunjulwa; ke igazi lakhe ndolibiza esandleni sakho.', 'XHO22'),
(30, 3, 21, 'Wena ke, xa uthe walivuselela ilungisa, ukuba lingoni, alona ke: lophila ngokuba livuselelwe; kwaye uwuhlangule umphefumlo wakho.', 'XHO22'),
(30, 3, 22, 'Saye isandla sikaYehova phezu kwam; Wathi kum, Yima, uye entlango, ndothetha nawe khona.', 'XHO22'),
(30, 3, 23, 'Ndesuka ndema, ndaphuma ndaya entilini, ndabona ubuqaqawuli bukaYehova bumi khona, bunjengobuqaqawuli endabubonayo emlanjeni oyiKebhare, ndawa ngobuso.', 'XHO22'),
(30, 3, 24, 'Kwangena uMoya kum, wandimisa, wathetha nam, wathi, Hamba uye kuzivalela endlwini yakho.', 'XHO22'),
(30, 3, 25, 'Wena ke, nyana womntu, yabona, baya kukunika imixokelelwane, bakubophe ngayo, ungaphumi phantsi kwayo;', 'XHO22'),
(30, 3, 26, 'Ndiya kulunamathelisa ulwimi lwakho emlonyeni wakho, ukuze ube sisimumu, ungabi sisikhalimela esinamandla, ngenxa yokuba yindlu ezele yimvukelo.', 'XHO22'),
(30, 3, 27, 'Kodwa ndakuthetha nawe, ndiya kuwuvula umlomo wakho, uze uthi kubo, ‘Itsho iNkosi enguMongami uYehova; Lowo uvayo makeve; nothe wayishiya, makame; ngokuba yindlu ezele zizikreqo.', 'XHO22'),
(30, 4, 1, 'Wena ke, nyana womntu, thabatha icwecwe, ulibeke ecaleni kwakho, uwuphose kulo umzi waseYerusalem;', 'XHO22'),
(30, 4, 2, 'wakhele yena ikhusi phezu kwayo, uyifunqule induli encinane; Umise intsika, ubeke iinkunzi zeegusha macala onke.', 'XHO22'),
(30, 4, 3, 'Uthabathe ipani yesinyithi, uyimise ibe ludonga lwesinyithi phakathi kwakho nomzi lowo; beka ubuso bakho phambi kwayo, wena uya kungqingwa, ikungqinge. Iya kuba ngumqondiso kwindlu kaSirayeli.', 'XHO22'),
(30, 4, 4, 'Lala ngecala lakho lasekhohlo, ububeke ubugwenxa bendlu kaSirayeli phezu kwalo; ngokuba inani leentsuku enilala ngalo, niya kubuthwala ububi babo.', 'XHO22'),
(30, 4, 5, 'Kuba iminyaka yobubi babo ndiyenza ukuba ibe ngangenani leentsuku, iintsuku ezimakhulu mathathu anamanci asithoba; ngaloo ndlela uya kubuthwala ubugwenxa bendlu kaSirayeli.', 'XHO22'),
(30, 4, 6, 'Uya kuthi wakuba ukufezile oko, ulale ngecala lakho lasekunene, ubuthwale ubugwenxa bendlu kaYuda iintsuku ezimashumi mane;', 'XHO22'),
(30, 4, 7, 'Ngoko ke, bhekisa ubuso bakho kungqingo lweYerusalem, ugqubuthele isandla sakho, uprofete ngayo.', 'XHO22'),
(30, 4, 8, 'Yabona, ndikufaka imixokelelwane, ukuze ungatyeki, uye kwelinye, ude uzigqibe iintsuku zokungqinga kwakho;', 'XHO22'),
(30, 4, 9, 'Thabatha ingqolowa, nerhasi, neembotyi, neentlumayo, namazimba, namazimba, uzigalele esityeni sinye, wenze isonka ngazo ngokwenani leentsuku oya kulala ngazo ngecala lakho, iintsuku ezimakhulu mathathu anamanci asithoba kufuneka uzidle.', 'XHO22'),
(30, 4, 10, 'Ukudla kwakho oya kukudla, makube ziishekele ezimashumi mabini ngemini; yitya qho.', 'XHO22'),
(30, 4, 11, 'Sela amanzi ngokomlinganiselo, isahlulo sesithandathu sehin; rhoqo kwaye emva koko kufuneka usele.', 'XHO22'),
(30, 4, 12, 'Udla njengesonka serhasi, usosa ngobulongwe bomntu phambi kwamehlo akhe.', 'XHO22'),
(30, 4, 13, 'Wathi uYehova, Oonyana bakaSirayeli baya kusidla siyinqambi ezintlangeni, endiya kubachithachithela kuzo.', 'XHO22'),
(30, 4, 14, 'Ndaza ndathi: “Owu Nkosi Yehova! Yabona, umphefumlo wam awenziwanga nqambi; Akukho nyama inezothe yakha yangena emlonyeni wam.', 'XHO22'),
(30, 4, 15, 'Wathi kum, Uyabona, ndikunika ithokazi lenkomo, elinjengesiphene; wosilungisa isonka sakho ngaso.', 'XHO22'),
(30, 4, 16, 'Wathi ke kum, Nyana womntu, yabona, ndiyawaphula umsimelelo osisonka eYerusalem, basidle isonka ngomlinganiselo, benesithukuthezi; bawasele amanzi ngomlinganiselo nangokumangalisayo;', 'XHO22'),
(30, 4, 17, 'ngokokude baswele isonka namanzi, bamangalise omnye ngenxa yomnye, batshabalale ngenxa yobungendawo babo.', 'XHO22'),
(30, 5, 1, 'Wena ke, nyana womntu, thabatha ikrele elibukhali, uthabathe icala lencakuba, uligqithisele entlokweni yakho, nasesilevini sakho; ngoku thatha isikali sokulinganisa kwaye wahlule iinwele.', 'XHO22'),
(30, 5, 2, 'Isahlulo sesithathu somzi uze usitshise ngomlilo, zakuzaliseka iintsuku zongqingo; thabatha inxalenye yesithathu, uyihlabe ngemela, uze usasaze isahlulo sesithathu emoyeni, ndirhole ikrele emva kwabo.', 'XHO22'),
(30, 5, 3, 'Wothatha enye yazo, uyibophe ezingxoweni zakho;', 'XHO22'),
(30, 5, 4, 'ubuye uthabathe kuzo, uziphose emlilweni, uzitshise ngomlilo; kuba kuya kuphuma umlilo kuyo yonke indlu kaSirayeli.', 'XHO22'),
(30, 5, 5, 'Itsho iNkosi uYehova ukuthi, Le Yerusalem ndiyimise phakathi kweentlanga, namazwe angeenxa zonke kuyo.', 'XHO22'),
(30, 5, 6, 'Uyicekisile imithetho yam ngaphezu kweentlanga, nemithetho yam ngaphezu kwamazwe angeenxa zonke kuye; ngenxa yokuba bayicekisile imiyalelo yam nemithetho yam, abahamba ngayo.', 'XHO22'),
(30, 5, 7, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ngenxa enokuba ninempumelelo ngaphezu kweentlanga ezingeenxa zonke kuni, anahamba ngemimiselo yam, anayigcina imithetho yam, anenza ngokwemithetho yeentlanga ezingeenxa zonke kuni;', 'XHO22'),
(30, 5, 8, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabonani, ndiya kulwa nani, ndenze izigwebo kuni emehlweni eentlanga.', 'XHO22'),
(30, 5, 9, 'Ndiya kwenza kuwe endingazanga ndikwenze ngaphambili, endingayi kubuya ndikwenze ngenxa yamasikizi akho onke.', 'XHO22'),
(30, 5, 10, 'Ngako oko ooyise baya kudla oonyana phakathi kwenu, oonyana badle ooyise; Ndiza kukuwisa izigwebo ndize ndiyichithachithe yonke intsalela yakho koomoya bonke.', 'XHO22'),
(30, 5, 11, 'Ngoko ke ndadla ubomi, itsho iNkosi uYehova; Ekubeni ingcwele yam uyenze inqambi ngamasikizi akho onke, nangamasikizi akho onke, ndokuthoba; aliyi kuba nanceba iliso lam, ndingabi nazintloni.', 'XHO22'),
(30, 5, 12, 'Isahlulo sesithathu kuni siya kufa yindyikitya yokufa, yaye siya kutshatyalaliswa yindlala phakathi kwenu; isahlulo sesithathu siya kuwa ngumkhonto ecaleni kwakho; esinye isahlulo sesithathu ndisichithachithe emimoyeni yonke, ndirhole ikrele emva kwabo.', 'XHO22'),
(30, 5, 13, 'Wophela ke umsindo wam, ndibeke umsindo wam kubo, ndize ndithuthuzeleke; bazi ukuba mna, Yehova, ndithethile ngenzondelelo yam, emva kokuba ndiwukhuphele kubo umsindo wam.', 'XHO22'),
(30, 5, 14, 'Ndiya kukwenza ube senkangala, udelekile ezintlangeni ezingeenxa zonke kuwe, emehlweni abo bonke abadlulayo.', 'XHO22'),
(30, 5, 15, 'Oko kuya kuba lihlazo nesingcikivo, sibe sisohlwayo nommangaliso kwiintlanga ezingeenxa zonke kuwe, ekukuveleni kwam ngomsindo nangobushushu, nangalohlwaya kalukhuni. Mna Yehova ndithethile:', 'XHO22'),
(30, 5, 16, 'ekuzithumeni kwam kuwe iintolo ezimbi zendlala, eziya kubatshabalalisa, ndibathumele ukuba batshabalalise; Ndokwandisa ukulamba kwenu, ndiwaphule umsimelelo osisonka senu;', 'XHO22'),
(30, 5, 17, 'Ndiya kuthumela kuwe indlala namarhamncwa, akuphange; idlule phezu kwakho indyikitya yokufa negazi; ndinizisele ikrele. Mna Yehova ndithethile.', 'XHO22'),
(30, 6, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 6, 2, 'Nyana womntu, bhekisa ubuso bakho ezintabeni zakwaSirayeli, uprofete kuzo;', 'XHO22'),
(30, 6, 3, 'uthi, Zintaba zakwaSirayeli, liveni ilizwi leNkosi uYehova; Itsho iNkosi uYehova kwiintaba, neenduli, nemilambo, nezihlambo; Yabona, ndikuzisela umkhonto, ndiya kuzitshabalalisa iziganga zakho;', 'XHO22'),
(30, 6, 4, 'Ziya kuphanziswa izibingelelo zenu nemifanekiso yenu eqingqiweyo iya kwaphulwa; yaye nina nithe nabethwa phambi kwezithixo zenu ezinezothe ndiya kunitshabalalisa.', 'XHO22'),
(30, 6, 5, 'Ndizibeke emhlabeni izidumbu zoonyana bakaSirayeli phambi kwezigodo zabo; yaye ndiza kuwachithachitha amathambo enu ngeenxa zonke ezibingelelweni zenu.', 'XHO22'),
(30, 6, 6, 'Kuzo zonke iindawo enihlala kuzo izixeko ziya kuba ngamanxuwa, neziganga zibe ngamanxuwa; ukuze zidilizwe zize zitshatyalaliswe izibingelelo zenu, ukuze yaphulwe ize itshatyalaliswe imifanekiso yenu eqingqiweyo, nemifanekiso yenu eqingqiweyo igawulwe nemisebenzi yenu.', 'XHO22'),
(30, 6, 7, 'Nababuleweyo baya kuwa phakathi kwenu, nazi ukuba ndinguYehova.', 'XHO22'),
(30, 6, 8, 'Ndiya kushiya amasalela, ukuze kubekho kuni abasindileyo ekreleni phakathi kweentlanga, ekuchithwachithweni kwenu emazweni.', 'XHO22'),
(30, 6, 9, 'Abo basindileyo kuwe baya kundikhumbula phakathi kweentlanga abaya kuthinjelwa kuzo, ngenxa yokuba ndaphukile ngenxa yeentliziyo zabo zoonongogo abandishiyileyo, nangenxa yamehlo abo alandela izithixo zabo baqubuda baqubuda; babulewe ngenxa yobubi ababenzileyo ngamasikizi abo onke.', 'XHO22'),
(30, 6, 10, 'Baya kwazi ukuba ndinguYehova, ukuba ndingatshongo ilize ukuba ndiya kubenza obu bubi kubo.', 'XHO22'),
(30, 6, 11, 'Itsho iNkosi uYehova ukuthi, Qhwabanisa isandla sakho, unyathele ngonyawo lwakho, usithi, Yeha, kuwo onke amasikizi endlu kaSirayeli! ngokuba baya kuwa likrele, nayindlala, nayindyikitya yokufa.', 'XHO22'),
(30, 6, 12, 'Okude uya kufa yindyikitya yokufa; okufuphi uya kuwa ngomkhonto; yaye lowo uhlala elinganiselwe uya kufa yindlala; ngoko ke ndiya kubukhuphela ubushushu bam kubo.', 'XHO22'),
(30, 6, 13, 'Niya kwazi ukuba ndinguYehova, bakuba ababuleweyo babo bephakathi kwezigodo zabo ngeenxa zonke ezibingelelweni zabo, ezindulini zonke ezinde, ezincotsheni zonke zeentaba, naphantsi kwemithi yonke eluhlaza, naphantsi kwemioki yonke ethe shinyi, ababekuyo iintanda zayiqhola ivumba elithozamisayo kuzo zonke izithixo zazo.', 'XHO22'),
(30, 6, 14, 'Ndiya kusolula isandla sam phezu kwabo, ndilenze ilizwe libe yintlango, intlango enjengentlango yaseDibhela, ezindaweni zonke zokuhlala kwabo; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 7, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 7, 2, 'Wena ke, nyana womntu, itsho iNkosi enguMongami uYehova kwilizwe lakwaSirayeli; Ekugqibeleni, isiphelo sifike kwiimbombo zone zelizwe.', 'XHO22'),
(30, 7, 3, 'Ke kaloku isiphelo sikufikele, yaye ngokuqinisekileyo ndiya kuthumela umsindo wam kuwe ndize ndikugwebe ngokweendlela zakho ndize ndikwenzele izinto zonke ezicekisekayo.', 'XHO22'),
(30, 7, 4, 'Aliyi kuba nanceba kuwe iliso lam, ndingakucongi; ke ndiya kuzibuyisela phezu kwakho iindlela zakho, abe phakathi kwakho amasikizi akho; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 7, 5, 'Itsho iNkosi uYehova ukuthi, Nanku ububi, ububi, bubi bunye.', 'XHO22'),
(30, 7, 6, 'Isiphelo sifikile, isiphelo sifikile; umlindi; Yabona, ifikile;', 'XHO22'),
(30, 7, 7, 'Nina bemi beli lizwe, nifike kusasa; ixesha lifikile, imini yentlekele isondele, kungekhona isikhalo seentaba.', 'XHO22'),
(30, 7, 8, 'Ndowuthululela kuwe ngokukhawuleza umsindo wam, ndize ndiwuhlisele kuwe umsindo wam; ke ndiya kukugweba ngokweendlela zakho, ndiwabeke phezu kwakho amasikizi akho onke.', 'XHO22'),
(30, 7, 9, 'Aliyi kuba nanceba iliso lam, ndingabi nanceba; ke ndiya kuwabeka phezu kwakho ngokweendlela zakho, nangamasikizi akho phakathi kwakho; nazi ukuba ndinguYehova owohlwayayo.', 'XHO22'),
(30, 7, 10, 'Yabona, ilanga liyeza; kwasa kwadlula; yopha intonga, kwavela ikratshi.', 'XHO22'),
(30, 7, 11, 'Ugonyamelo luvele luswazi lwentswela-bulungisa;', 'XHO22'),
(30, 7, 12, 'Lifikile ilixa, isondele imini; othengayo makangavuyi, nothengisayo makangalili; ngokuba ukuvutha komsindo kuyayizela ingxokolo yakhe yonke.', 'XHO22'),
(30, 7, 13, 'Kuba othengisayo akanakubuyela entweni abethengise ngayo, nokuba ubesadla ubomi phakathi kwabadla ubomi; ngenxa yokuba umbono uchaphazela isihlwele esilapho, esingayi kubuya; akukho bani uya kumxhasa ebubini bobomi bakhe.', 'XHO22'),
(30, 7, 14, 'Bavuthela isigodlo, balungisa yonke into; kodwa akukho namnye uya kulwa, ngokuba umsindo wam ukuyo yonke ingxokolo yakhe.', 'XHO22'),
(30, 7, 15, 'Ngaphandle ikrele, ngendyikitya yokufa nendlala ngaphakathi; oselizweni uya kubulawa ngentshuntshe; ophakathi komzi uya kudliwa yindlala nayindyikitya yokufa.', 'XHO22'),
(30, 7, 16, 'Kodwa abo basabayo kuzo baya kusaba baye ezintabeni njengamahobe aphuma ezintlanjeni, ekhalelana ngenxa yesiphoso sakhe.', 'XHO22'),
(30, 7, 17, 'Zonke izandla ziya kuba buthathaka, nawo onke amadolo aya kuba buthathaka njengamanzi.', 'XHO22'),
(30, 7, 18, 'Bazibhinqise ngamarhonya, babhinqe iintlungu; yaye ebusweni babo bonke kuya kubakho ihlazo, neentloko zabo zonke zibe ziinkqayi.', 'XHO22'),
(30, 7, 19, 'Isilivere yabo baya kuyiphosa ezitratweni, negolide yabo ithinjwe; isilivere yabo negolide yabo ayiyi kuba nako ukubasindisa ngemini yokuphuphuma komsindo kaYehova; abayi kwanelisa umphefumlo wabo, bazalise izisu zabo ngokuba sisono sabo eso.', 'XHO22'),
(30, 7, 20, 'Isihombo sakhe, abesibeke ngokobuhle; kodwa bawenzela kuzo izinto zabo ezinezothe, nezingamasikizi; ke ngoko, ndiyibeke kude kubo.', 'XHO22'),
(30, 7, 21, 'Ndiya kuyinikela esandleni sabasemzini ibe lixhoba, nakwabangendawo behlabathi ukuba ibe lixhoba; baya kulihlambela;', 'XHO22'),
(30, 7, 22, 'ke ndiya kubanikela umva, bayenze inqambi indawo yam yokuzimela, kuba amasela aya kungena kuyo, ayenze inqambi.', 'XHO22'),
(30, 7, 23, 'Yenza umxokelelwane, ngokuba ilizwe liliphalaza igazi, nomzi uzele lugonyamelo.', 'XHO22'),
(30, 7, 24, 'Ngoko ke ndiya kuzisa ezona ntlanga zikhohlakeleyo, yaye ziya kuzidla ilifa izindlu zazo; neendawo zabo ezingcwele ziya kuba ziinqambi.', 'XHO22'),
(30, 7, 25, 'Intshabalalo iyeza; yaye baya kufuna uxolo, lungabikho.', 'XHO22'),
(30, 7, 26, 'Kufike ububi phezu kobubi, kubekho udaba phezu kodaba; baya kubufuna apho ubuso bomprofeti; ke wona umthetho uya kutshabalala kumbingeleli, nakwintlanganiso yamadoda amakhulu.', 'XHO22'),
(30, 7, 27, 'Ukumkani uya kulila, nesikhulu sibe senkangala, ziyekelele izandla zabantu belizwe eli; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 8, 1, 'Ngomnyaka wesithandathu, ngenyanga yesithandathu, ngolwesihlanu enyangeni leyo, ndihleli endlwini yam, namadoda amakhulu akwaYuda ehleli ngakum, sawa phezu kwam isandla seNkosi uYehova.', 'XHO22'),
(30, 8, 2, 'Ndabona, ndabona into efana nembonakalo yomlilo, kuthabathele esinqeni sayo kwehlisa, ingumlilo; kuthabathele esinqeni sayo kunyuse, ibingathi yimbonakalo eqaqambileyo yemfumba etyheli.', 'XHO22'),
(30, 8, 3, 'Wathabatha isandla, wandibamba ngentamo yentloko yam; uMoya wandithabatha phakathi komhlaba nezulu, wandizisa phambi kobuso bukaThixo eYerusalem, ekungeneni kwesango eliphakathi, elibheke ngasentla; apho kwakukho isihlalo somfanekiso wekhwele, elizala ikhwele.', 'XHO22'),
(30, 8, 4, 'Nabo ke bukhona ubuqaqawuli bukaThixo kaSirayeli, bunjengombono endawubonayo entilini leyo.', 'XHO22'),
(30, 8, 5, 'Wathi kum, Nyana womntu, phakamisela amehlo akho ngasentla. Ndawaphosa amehlo am kwindlela esinga entla, ndabheka ngasentla kwesango lesibingelelo, kukho lo mfanekiso usemantla emnyango.', 'XHO22'),
(30, 8, 6, 'Wathi kum, Nyana womntu, uyakubona na abakwenzayo? Ngawaphi na la masikizi amakhulu, le iwenzayo apha indlu kaSirayeli, ukuze ndide ndimke engcweleni yam? kodwa guqukani kwakhona, kwaye niya kubona iinyikima ezinkulu.', 'XHO22'),
(30, 8, 7, 'Wandisa esangweni lentsimi; ndajonga, ndabona umngxuma eludongeni.', 'XHO22'),
(30, 8, 8, 'Wathi kum, Nyana womntu, khawugqobhoze eludongeni; ndagqobhoza eludongeni, ndabona ucango lukhona.', 'XHO22'),
(30, 8, 9, 'Wathi kum, Ngena, uwabone amasikizi abawenzayo apha.', 'XHO22'),
(30, 8, 10, 'Ndangena, ndakhangela; nanzo zonke izinambuzane, nezilwanyana eziziinqambi, nazo zonke izithixo zendlu kaSirayeli, zithululelwe eludongeni yonke.', 'XHO22'),
(30, 8, 11, 'Kumi phambi kwazo amadoda amakhulu angamashumi asixhenxe endlu kaSirayeli, noYasaniya unyana kaShafan emi phakathi kwawo, iyileyo iphethe imbiza yayo yesiqhumiso esandleni; kwenyuka ilifu lesiqhumiso.', 'XHO22'),
(30, 8, 12, 'Wathi ke kum, Nyana womntu, ukubonile na akwenzayo emnyameni amadoda amakhulu endlu kaSirayeli, iyileyo egumbini layo lokuphumla? ngokuba besithi, uYehova akasiboni; uYehova uwushiyile umhlaba.', 'XHO22'),
(30, 8, 13, 'Wabuya wathi kum, Guquka, ubone amasikizi amakhulu abawenzayo.', 'XHO22'),
(30, 8, 14, 'Wandisa ekungeneni kwesango lendlu kaYehova elingasentla; naba ke abafazi behleli phantsi, belilela uTamo.', 'XHO22'),
(30, 8, 15, 'Wathi kum, Ubonile na, nyana womntu? guqukani, wobona ubunzima ngaphezu koku.', 'XHO22'),
(30, 8, 16, 'Wandisa entendelezweni ephakathi yendlu kaYehova, ndabona emnyango wetempile kaYehova, phakathi kwevaranda nesibingelelo, amadoda angathi amashumi mabini anamahlanu, eyinikele umva itempile evela kuYehova. Yehova, ubuso bazo bubheke empumalanga; baye balinqula ilanga empumalanga.', 'XHO22'),
(30, 8, 17, 'Wathi kum, Ubonile na, nyana womntu? Yinto encinane na kwindlu kaYuda, ukwenza amasikizi akwenzayo khona? kuba balizalisa ilizwe ngogonyamelo baza babuya bandicekisa; nanzo zigalela iqhina empumlweni.', 'XHO22'),
(30, 8, 18, 'Ke ngoko, nam ndiya kwenza ngomsindo;', 'XHO22'),
(30, 9, 1, 'Wadanduluka ngelizwi elikhulu ezindlebeni zam, esithi, Basondele abathetheli balo mzi, elowo ephethe isixhobo sakhe sokutshabalalisa.', 'XHO22'),
(30, 9, 2, 'Nanko, kusiza amadoda amathandathu, evela ngendlela yesango eliphezulu elisingise entla, iyileyo iphethe isixhobo sokulwa ngesandla sayo kubo indoda yambethe ilinen ecikizekileyo, inophondo lombhali ecaleni layo; bangena, bema esibingelelweni sobhedu.', 'XHO22'),
(30, 9, 3, 'Ubuqaqawuli bukaThixo kaSirayeli benyuka busuka kwiikherubhi eziphezu kwayo baya emnyango wendlu. Wabiza indoda eyambethe ilinen emhlophe, enophondo lombhali ecaleni layo;', 'XHO22'),
(30, 9, 4, 'Wathi uYehova kuyo, Yiya esazulwini somzi, esazulwini seYerusalem, ubeke uphawu emabunzini abantu abancwinayo, abakhalayo ngenxa yamasikizi onke awenzekayo phakathi kwayo.', 'XHO22'),
(30, 9, 5, 'Ithe ke kwabanye, bendisakuva, yathi, Phumani phakathi komzi, nimbulale; ungalicongi iliso lakho, ungacongi.', 'XHO22'),
(30, 9, 6, 'Amaxhego, amadodana, namaxhego, nabantwana, nabafazi, kodwa ningasondeli nakwindoda inophawu; niqale endaweni yam engcwele. Baqala ke ngabantu abadala ababephambi kwendlu.', 'XHO22'),
(30, 9, 7, 'Wathi ke kubo, Yenzeni inqambi indlu le, niwazalise amasimi ngababuleweyo; phuma Baphuma ke, babulala kuwo umzi.', 'XHO22'),
(30, 9, 8, 'Kwathi ekubulaweni kwabo, ndathi ndiselapho, ndawa ngobuso, ndakhala, ndisithi, Awu, Nkosi Yehova! Uya kuwatshabalalisa na onke amasalela akwaSirayeli ngokuwuthululela komsindo wakho phezu kweYerusalem?', 'XHO22'),
(30, 9, 9, 'Wathi kum, Ububi bendlu kaSirayeli nobendlu kaYuda bukhulu kunene, nelizwe lizele ligazi, nomzi uzele yinkohliso; ngokuba besithi, uYehova ulilahlile ihlabathi, uYehova akaboni.', 'XHO22'),
(30, 9, 10, 'Ke ngoko emehlweni am aliyi kuba nanceba iliso lam kubo, ndingabi namfesane kubo, ndibanikele entlokweni yabo;', 'XHO22'),
(30, 9, 11, 'Kwabonakala indoda enxibe le nto, inophondo olumnyama ecaleni layo, ixela udaba, isithi, Ndenze njengoko ubundithethile.', 'XHO22'),
(30, 10, 1, 'Ndabona, ndabona ezulwini phezu kweentloko zeekerubhi, kunjengelitye lesafire, ngathi ngukubonakala kwetrone;', 'XHO22'),
(30, 10, 2, 'Wathetha kwindoda eyambethe ilinen emhlophe, wathi, Ngena phakathi kweevili ezijikelezayo phantsi kweekerubhi, uwagcwalise amalahle avuthayo ngesandla sakho phakathi kweekerubhi, uwasasaze phezu komzi. Uye wangena phambi kwamehlo am.', 'XHO22'),
(30, 10, 3, 'Ekungeneni kwendoda leyo, iikerubhi bezimi ngasekunene kwendlu; ilifu layizalisa intendelezo engaphakathi.', 'XHO22'),
(30, 10, 4, 'Benyuka ubuqaqawuli bukaYehova, besuka ezikerubhini, bema emnyango wendlu yazala lilifu indlu, nentsimi yazala bubuqaqawuli bobuqaqawuli bukaYehova.', 'XHO22'),
(30, 10, 5, 'Kuvakala isandi samaphiko eekerubhi, siye kufikelela entendelezweni engaphandle, sinjengezwi loSomandla akuthetha.', 'XHO22'),
(30, 10, 6, 'Kwathi, wayiwisela umthetho indoda, ebethe isigqubuthelo, wathi, Faka umlilo phakathi kweenyawo, phakathi kweekerubhi; Wangena ke wema ecaleni kwamavili.', 'XHO22'),
(30, 10, 7, 'Yasolula enye ikerubhi isandla sayo phakathi kweekerubhi, emlilweni ophakathi kweekerubhi, yawuthabatha, yawunikela esandleni salowo wayambethe ilinen emhlophe; wayithatha waphuma.', 'XHO22'),
(30, 10, 8, 'Ngaphakathi kweekerubhi kwabonakala into efana nesandla somntu, phantsi kwamaphiko azo.', 'XHO22'),
(30, 10, 9, 'Ndakhangela, ndabona, ndabona iivili ezine zisecaleni leekerubhi, ivili enye isecaleni lekerubhi enye, ivili enye isecaleni lekerubhi enye; Ukubonakala kweevili bekunjengembonakalo yelitye lekrizolite.', 'XHO22'),
(30, 10, 10, 'Ke ukubonakala kwazo zone kwakufana nqwa, kwanga kukho ivili phakathi kwevili.', 'XHO22'),
(30, 10, 11, 'Ekuhambeni kwakhe wahamba kumacala azo omane; aziphambuka, zahamba apho abekhangele khona ukumkani; azijika ekuhambeni kwazo.', 'XHO22'),
(30, 10, 12, 'Umzimba wawo uphela nomhlana wawo nezandla zawo namaphiko azo neenyawo zazo zonke zazizele ngamehlo, ewe, neenyawo zazo zozine.', 'XHO22'),
(30, 10, 13, 'Iivili ezo zabizwa phambi kweendlebe zam, kwathiwa, Livili wena.', 'XHO22'),
(30, 10, 14, 'Inye ibe inobuso obune: ubuso bokuqala ibubuso bekerubhi, ubuso besibini ibubuso bomntu; obesithathu ibubuso bengonyama, obesine ibubuso bokhozi.', 'XHO22'),
(30, 10, 15, 'Zesuka iikerubhi; Esi sisidalwa esiphilayo endasibona kumlambo iKebhare.', 'XHO22'),
(30, 10, 16, 'Kwathi, ekuhambeni kweekerubhi, zahamba iivili ecaleni lazo; kwanaxa iikherubhi zawaphakamisa amaphiko azo ukuba zinyuke ziye emhlabeni, namavili ayengaphumi nakuzo.', 'XHO22'),
(30, 10, 17, 'Bema, bema; zathi zakusuka, zasuka nazo, ngokuba umoya wento ephilileyo ube ukho kuzo.', 'XHO22'),
(30, 10, 18, 'Baphuma ke ubuqaqawuli bukaYehova, emnyango wendlu, bema phezu kweekerubhi.', 'XHO22'),
(30, 10, 19, 'Iikerubhi zawaphakamisa amaphiko azo, zesuka emhlabeni phambi kwamehlo am; yaye ubuqaqawuli bukaThixo kaSirayeli babuphezu kwawo.', 'XHO22'),
(30, 10, 20, 'Yilaa nto iphilileyo, ndayibonayo phantsi koThixo kaSirayeli emlanjeni oyiKebhare; ndazi ke ukuba ziziikerubhi.', 'XHO22'),
(30, 10, 21, 'Inye ibe inobuso obune, inye ibe inamaphiko amane; nemfano yezandla zomntu ibiphantsi kwamaphiko azo.', 'XHO22'),
(30, 10, 22, 'Ke imfano yobuso bazo, ibe ibobaa buso ndabubonayo emlanjeni oyiKebhare, nokubonakala kwazo nazo ngokwazo.', 'XHO22'),
(30, 11, 1, 'Wandithabatha uMoya, wandisa emnyango wendlu kaYehova, osempumalanga, okhangele empumalanga; phakathi kwabo ndabona uYashaniya unyana ka-Ashure, noPelatiya unyana kaBhenaya, iintloko zabantu.', 'XHO22'),
(30, 11, 2, 'Waqhubeka wathi kum: “Nyana womntu, la ngamadoda aceba ububi naceba amayelenqe kwesi sixeko.', 'XHO22'),
(30, 11, 3, 'Abathi, Ayikufuphi; masakhe izindlu; lo mzi yimbiza, thina ke siyinyama.', 'XHO22'),
(30, 11, 4, 'Ngako oko profeta ngabo, profeta, nyana womntu;', 'XHO22'),
(30, 11, 5, 'Wehla phezu kwam uMoya kaYehova, wathi kum, Thetha; Wathi uYehova; Nithi, ndlu kaSirayeli, ndiyayazi yonke into ethe qatha entliziyweni yenu.', 'XHO22'),
(30, 11, 6, 'Nibandisile ababuleweyo kulo mzi, nazizalisa izitrato zawo ngababuleweyo.', 'XHO22'),
(30, 11, 7, 'Ngako oko itsho iNkosi uYehova ukuthi, Nina nibasiweyo, nibabeke khona bayinyama, lo mzi uyimbiza; ndiya kunikhupha kuyo.', 'XHO22'),
(30, 11, 8, 'Niloyika ikrele; yaye ndiza kunizisela ikrele,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 11, 9, 'Ndiya kunikhupha kuyo, ndininikele esandleni soonyana bolunye uhlanga, ndinivelele nina.', 'XHO22'),
(30, 11, 10, 'Niya kuwa likrele; ndiya kunigweba emdeni wakwaSirayeli; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 11, 11, 'Esi sixeko asiyi kuba yimbiza yenu, nani aniyi kuba yinyama kuso; ndiya kunigweba emdeni wakwaSirayeli.', 'XHO22'),
(30, 11, 12, 'Niya kwazi ke ukuba ndinguYehova, ngenxa yokuba anihambanga ngokwemiyalelo yam emisiweyo naza nenza ngokwesiko leentlanga ezinijikelezileyo.', 'XHO22'),
(30, 11, 13, 'Ndathi ndakuprofeta, wafa uPelatiya unyana kaBhenaya. Ndawa ngobuso, ndadanduluka ngelizwi elikhulu, ndathi: “Owu, Nkosi Yehova! Uya kuwatshabalalisa na ke amasalela akwaSirayeli?', 'XHO22'),
(30, 11, 14, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 11, 15, 'Nyana womntu, abazalwana bakho, noonyana bakho, abendlu yakho, nakwindlu yonke kaSirayeli, bona abo bathi kubo abemi baseYerusalem, Mkani uYehova, niye ezweni lethu anikwe ukuba sisityebi.', 'XHO22'),
(30, 11, 16, 'Ngako oko ithi, Itsho iNkosi uYehova ukuthi, Ekubeni ndibaphangalalisa phakathi kweentlanga, ndibaphangalalise emazweni, ndoba njengengcwele kubo emazweni abafika kuwo.', 'XHO22'),
(30, 11, 17, 'Ngako oko itsho iNkosi uYehova ukuthi, Ndiya kunibutha phakathi kweentlanga, ndinibuthe emazweni enilusali kuwo, ndininike umhlaba wakwaSirayeli.', 'XHO22'),
(30, 11, 18, 'Baya kufika khona, bawasuse onke amasikizi ayo, nawo onke amasikizi ayo;', 'XHO22'),
(30, 11, 19, 'Ndiya kubanika intliziyo enye ndibeke umoya omtsha ngaphakathi kwenu; ndiya kuyisusa intliziyo yelitye enyameni yabo, ndibanike intliziyo yenyama;', 'XHO22'),
(30, 11, 20, 'ukuze bahambe ngeendlela zam, bagcine imithetho yam, bayenze; yaye baya kuba ngabantu bam, nam ndibe nguThixo wabo.', 'XHO22'),
(30, 11, 21, 'Ke bona abantliziyo ilandela intliziyo yezinto zabo ezinezothe, nezingamasikizi abo, ndiya kuyibuyisela indlela yabo entlokweni yabo; itsho iNkosi uYehova.', 'XHO22'),
(30, 11, 22, 'Iikerubhi zawaphakamisa amaphiko azo, kwaneevili ngakuzo; yaye ubuqaqawuli bukaThixo kaSirayeli babuphezu kwawo.', 'XHO22'),
(30, 11, 23, 'Benyuka ubuqaqawuli bukaYehova, besuka phakathi kwesixeko, bema entabeni engasempumalanga kuso isixeko.', 'XHO22'),
(30, 11, 24, 'Emva koko umoya wandithabatha waza wandithabatha embonweni ngomoya kaThixo wandisa eKhaledi kumabanjwa. Ngoko umbono endawubonayo wenyuka wemka kum.', 'XHO22'),
(30, 11, 25, 'Ndawaxelela ngamabanjwa zonke izinto uYehova awayendibonise zona.', 'XHO22'),
(30, 12, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 12, 2, 'Nyana womntu, uhlala endlwini ezele luvukelo enamehlo okubona kodwa angaboni; baneendlebe zokuva, abeva; ngokuba bayindlu eneenkani.', 'XHO22'),
(30, 12, 3, 'Ukuba kunjalo, nyana womntu, zilungise izinto zakho, uzibeke, uzibeke phambi kwamehlo abo emini; uyishiye indawo yakho, uye ndaweni yimbi emehlweni abo;', 'XHO22'),
(30, 12, 4, 'Likhupheni ngoko impahla yenu phambi kwamehlo abo emini, njengento enokuthatyathwa; yaye baphume ngokuhlwa phambi kwamehlo abo njengabaphuma ekuthinjweni.', 'XHO22'),
(30, 12, 5, 'Uyatyhoboza eludongeni emehlweni abo;', 'XHO22'),
(30, 12, 6, 'Yithwale emagxeni akho phambi kwamehlo akho, uyikhuphe ngokuhlwa; bugqume ubuso bakho, ukuze ungaliboni ihlabathi; ngokuba ndikwenze umqondiso kwindlu kaSirayeli.', 'XHO22'),
(30, 12, 7, 'Ndenza njengoko ndiwiselwe umthetho ngako: Ndazikhupha izinto zam emini, njengempahla yokuthinjwa, ngokuhlwa ndalugqobhoza udonga ngesandla; Ndayikhupha ngorhatya, ndayithwala egxalabeni phambi kwamehlo abo.', 'XHO22'),
(30, 12, 8, 'Kwafika ilizwi likaYehova kum kusasa, lisithi:', 'XHO22'),
(30, 12, 9, 'Nyana womntu, indlu kaSirayeli, indlu eneenkani, ayitshongo na kuwe ukuthi, Wenza ntoni na?', 'XHO22'),
(30, 12, 10, 'Wathi kubo, Itsho iNkosi uYehova; Eli lizwi lisingisele kwisikhulu saseYerusalem nakwindlu yonke kaSirayeli ephakathi kwayo.', 'XHO22'),
(30, 12, 11, 'Yithi, Ndisisimanga sakho; njengoko ndenze ngako, baya kwenziwa ngokunjalo; baya kumka, bathinjwe.', 'XHO22'),
(30, 12, 12, 'Isikhulu esiphakathi kwabo siya kuyithwala ngegxalaba, sihambe ngokuhlwa; baya kulugqobhoza udonga ukuyikhupha; uya kugqubuthela ubuso bakhe, ukuze angaliboni ihlabathi ngamehlo akhe.', 'XHO22'),
(30, 12, 13, 'Ndiya kuyiwela, ibanjiswe ngumnatha wam; Ndiya kumsa eBhabheli, ezweni lamaKaledi; noko ke, akayi kuyibona, nokuba ithe yafela khona.', 'XHO22'),
(30, 12, 14, 'Ndiya kuchithachitha imiphefumlo yonke ngeenxa zonke kuye, kunye namaqela akhe onke; ndirhole ikrele emva kwabo;', 'XHO22'),
(30, 12, 15, 'bazi ukuba ndinguYehova, ekubaphangalaliseni kwam ezintlangeni, ndibaphangalalisele emazweni;', 'XHO22'),
(30, 12, 16, 'Ndoshiya amadoda ambalwa, asinde ekreleni, nasendlaleni, nasendyikityeni yokufa; ukuze bawaxele onke amasikizi abo ezintlangeni abaya kufika kuzo; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 12, 17, 'Ngaphezu koko, kwabonakala ilizwi likaYehova kum lisithi:', 'XHO22'),
(30, 12, 18, 'Nyana womntu, sidle isonka sakho ungcangcazela, uwasele amanzi akho ungcangcazela yaye unesithukuthezi;', 'XHO22'),
(30, 12, 19, 'Wathetha kwizizwe zehlabathi, wathi, Itsho iNkosi uYehova ngokusingisele kubemi baseYerusalem, nomhlaba wakwaSirayeli; Isonka sabo bosidla bexhalabile, bawasele amanzi abo bemangalisiwe, nelizwe lakhe libe senkangala, kuko konke okukulo, ngenxa yogonyamelo lwabo bonke abemi balo.', 'XHO22'),
(30, 12, 20, 'Izixeko ezinabemi ziya kuphanziswa, nelizwe libe yintlango; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 12, 21, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 12, 22, 'Nyana womntu, uthini na wena emhlabeni wakwaSirayeli, ukuthi, Imihla iyoluka, ubuso bonke buphela?', 'XHO22'),
(30, 12, 23, 'Wandula ke uthi kubo, ‘Itsho iNkosi enguMongami uYehova; Ndiya kuyiphelisa le mizekeliso, ingabi sathetha ngayo kwaSirayeli; wathi kubo, Isondele imihla, nesiphelo sombono wonke.', 'XHO22'),
(30, 12, 24, 'Kuba akuyi kuba sabakho buso obulambathayo okanye ukuvumisa ngenkohliso phakathi kwendlu kaSirayeli.', 'XHO22'),
(30, 12, 25, 'Ngokuba ndinguYehova; ndiya kuthetha, nelizwi endiya kulithetha libe yinyaniso; aliyi kuba sabakho, ngokuba ngemihla yenu, ndlundini ineenkani, ndithetha ilizwi, ndilenze; itsho iNkosi uYehova.', 'XHO22'),
(30, 12, 26, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 12, 27, 'Nyana womntu, yabona, indlu kaSirayeli ithi, Umbono awubonayo yena lo, ngowemihla emininzi; uprofetele amaxesha akude.', 'XHO22'),
(30, 12, 28, 'Ngako oko uthi kubo, Itsho iNkosi uYehova ukuthi, Akayi kulityaziswa nalinye ilizwi lam; ilizwi endilithethileyo liya kwenzeka; itsho iNkosi uYehova.', 'XHO22'),
(30, 13, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 13, 2, 'Nyana womntu, profeta kubaprofeti bakwaSirayeli abaprofetayo, uthi kwabaprofeta ngeentliziyo zabo, Liveni ilizwi likaYehova!', 'XHO22'),
(30, 13, 3, 'Itsho iNkosi uYehova ukuthi; Yeha, abaprofeti abaziziyatha, abalandela owabo umphefumlo, abangaboni nto!', 'XHO22'),
(30, 13, 4, 'Baba njengeempungutye entlango abaprofeti bakho;', 'XHO22'),
(30, 13, 5, 'Ayizange inyuke iye emathubeni, yaye ayizange iyenzele uthango indlu kaSirayeli ukuze ime edabini ngomhla kaYehova.', 'XHO22'),
(30, 13, 6, 'Babona ukuvumisa okukhohlakeleyo, nokuvumisa okukhohlakeleyo, abathi, uYehova uthethile, uYehova angabathumanga; kwaye bathembisa abanye ukuba baya kuliqinisa ilizwi.', 'XHO22'),
(30, 13, 7, 'Anibonanga mbono ulilize na, anivumisanga ngobuxoki na, ngoxa nisithi, ‘uYehova uthethile; nokuba andithethi?', 'XHO22'),
(30, 13, 8, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ekubeni nithethe into ekhohlakeleyo, nibone amanga; ngako oko, yabonani, ndinengxaki nani;', 'XHO22'),
(30, 13, 9, 'Kwaye isandla sam siya kuba phezu kwabaprofeti ababona amampunge nabanobuxoki; Sirayeli; nazi ukuba ndiyiNkosi uYehova.', 'XHO22'),
(30, 13, 10, 'Ngenxa yokuba bebalahlekisa abantu bam ngokuthi: “Luxolo! akwabakho luxolo; elowo esakha udonga, kodwa ke abanye besakha ngodongwe olungenakwenziwa.', 'XHO22'),
(30, 13, 11, 'Xelela abalilelayo ngodongwe olungagungqiyo, ukuba liya kuwa; nawe, sichotho esikhulu, uya kuwa; nomoya wesaqhwithi uya kuyiqweqwedisa.', 'XHO22'),
(30, 13, 12, 'Yabonani, xa luthe lwawa udonga, akuyi kuthiwa kuni, Luphi na udonga olo benidla ngalo?', 'XHO22'),
(30, 13, 13, 'Ngako oko itsho iNkosi uYehova ukuthi, Ndiya kuyiqweqwedisa ngomoya woqhwithela ngomsindo wam; kuya kubakho imvula eninzi ngomsindo wam, nesichotho esikhulu ngomsindo wam, ukuba nditshabalalise.', 'XHO22'),
(30, 13, 14, 'Ndiya kuluchitha udonga olwakhiwe ngodongwe olungaqiniyo, ndiluwise emhlabeni, sityhileke isiseko salo, luwe, nitshabalale kulo; yazini ukuba ndinguYehova.', 'XHO22'),
(30, 13, 15, 'Ndiya kuwufikela umsindo wam eludongeni nakwabo bayakhe ngodongwe olungenakwenziwa, ndithi kuni, Udonga alusekho, nabakhi balo abasayi kuba sabakho;', 'XHO22'),
(30, 13, 16, 'Aba ngabaprofeti bakwaSirayeli ababeprofeta ngeYerusalem, bebona uxolo ebusweni bayo, kungekho luxolo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 13, 17, 'Wena ke, nyana womntu, bhekisa ubuso bakho kwiintombi zabantu bakowenu, eziprofeta okweentliziyo zazo; uprofete kubo;', 'XHO22'),
(30, 13, 18, 'Wathi, Itsho iNkosi uYehova; Yeha ke abafazi abathunga iingalo zonke iingalo nabenza amalaphu eentloko zabo bonke ubukhulu ukuze bazingele imiphefumlo! Nithiyele na imiphefumlo yabantu bam, niyigcine imiphefumlo eza kuni?', 'XHO22'),
(30, 13, 19, 'Niya kundenza inqambi na phakathi kwabantu bam ngenxa yokuzalisa isandla kwerhasi nangenxa yamaqhekeza esonka, ukuze nibulale imiphefumlo ebingafanga nokusindisa imiphefumlo eyalayo ukuphila ukuze iqhathe abantu bam ababuvayo ubuxoki benu?', 'XHO22'),
(30, 13, 20, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabonani, ezigumeni zenu nizithiyileyo imiphefumlo yabantu, nibasabisa ngazo; Ndiya kuwukrazula ezandleni zenu, ndiya kuyishiya imimoya, kunye nemimoya ukuba ukuzingela ukuzingela. Bhabha uye kude.', 'XHO22'),
(30, 13, 21, 'Ndiya kuzikrazula iingubo zenu, ndibasindise abantu bam esandleni senu, bangabi saba sezandleni zenu ukuba zizingele; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 13, 22, 'Ngenxa enokuba niyenza buhlungu intliziyo yelungisa ngobuxoki; wazomeleza izandla zakhe ezonakeleyo, ukuba angabuyeli endleleni yakhe embi, ngokumthembisa ubomi.', 'XHO22'),
(30, 13, 23, 'Ngako oko aniyi kuphinda nibone into ekhohlakeleyo nokuvumisa; kuba ndiya kubasindisa abantu bam esandleni senu, nazi ukuba ndinguYehova.', 'XHO22'),
(30, 14, 1, 'Ke kaloku kweza kum amadoda amakhulu akwaSirayeli, ahlala ngakum.', 'XHO22'),
(30, 14, 2, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 14, 3, 'Nyana womntu, la madoda anyuse izigodo zawo ezintliziyweni zawo, abeka ubugwenxa bawo phambi kwawo; ndithethe nabo kwaphela?', 'XHO22'),
(30, 14, 4, 'Ngoko ke thetha nabo uthi kubo: ‘Itsho iNkosi uNdikhoyo ukuthi: Elowo wendlu kaSirayeli, othe wanyusa izigodo zakhe entliziyweni yakhe, owabeka phambi kobuso bakhe isingcikivo sobubi bakhe, weza kumprofeti; Mna, Yehova, ndiya kumphendula lowo uzayo, ngobuninzi bemifanekiso yakhe eqingqiweyo;', 'XHO22'),
(30, 14, 5, 'ukuze ndingenise indlu kaSirayeli ezintliziyweni zabo, ngenxa yokuba bonke bandinikele umva ngezithixo zabo ezinezothe.', 'XHO22'),
(30, 14, 6, 'Ngako oko yithi kwindlu kaSirayeli, Itsho iNkosi uYehova ukuthi, Guqukani, nizilahle izigodo zenu; ubuye kuwo onke amasikizi akho.', 'XHO22'),
(30, 14, 7, 'Elowo wendlu kaSirayeli, nowasemzini ophambukele kwaSirayeli, othe wazahlula kum, nozahlulayo kum, nonyusa izigodo zakhe entliziyweni yakhe, nobeka phambi kwendlu yakhe isikizi lenkohlakalo yakhe weza kumprofeti ukuba abuze kuye. Ngam; Mna, Yehova, ndiya kumphendula ngokuphuma kum.', 'XHO22'),
(30, 14, 8, 'Ndoyikhangela loo ndoda, ndiyenze ibe ngumqondiso nomzekeliso, ndiyinqumle ebantwini bam; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 14, 9, 'Ukuba umprofeti uthe wamlukuhla, ekuthetheni kwakhe, mna Yehova ndimqhathile loo mprofeti, ndosolulela kuye isandla sam, ndimtshabalalise phakathi kwabantu bam amaSirayeli.', 'XHO22'),
(30, 14, 10, 'Kwaye baya kuluthwala ubugwenxa babo; sibe njengokohlwaywa komprofeti lowo umquqelayo;', 'XHO22'),
(30, 14, 11, 'ukuze indlu kaSirayeli ingabi sabuya ukubuya kum, ingabi sazenza iinqambi ngezikreqo zayo zonke; ukuze babe ngabantu bam, ndibe nguThixo wabo; itsho iNkosi uYehova.', 'XHO22'),
(30, 14, 12, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 14, 13, '“Nyana womntu, ukuba ilizwe lithe lashiywa, ndosolula isandla sam phezu kwalo, ndiwaphule umsimelelo osisonka, ndilizisele indlala kulo, nditshabalalise abantu neenkomo kulo.', 'XHO22'),
(30, 14, 14, 'Kwanokuba la madoda mathathu, uNowa, uDaniyeli noYobhi, ebekho, ebeya kuzisindisa kuphela ngobulungisa bawo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 14, 15, 'Ukuba ndithe ndathumela iindudumo ezweni, zalonakalisa, laba yintlango, kungabikho udlulayo phakathi kwamarhamncwa;', 'XHO22'),
(30, 14, 16, 'Nokuba ebekho la madoda mathathu, ndihleli nje, itsho iNkosi uYehova, ebengayi kusindisa nyana nantombi; ngabo kuphela abaya kusindiswa, kodwa ilizwe libe senkangala.', 'XHO22'),
(30, 14, 17, 'Nokuba ndithe ndalizisela ikrele elo lizwe, ndathi, ‘Ikrele! ukuze nditshabalalise kulo umntu nenkomo.', 'XHO22'),
(30, 14, 18, 'Nokuba ebekho phakathi kwenu la madoda mathathu, ndihleli nje, itsho iNkosi uYehova, ebengayi kusindisa nyana nantombi, kodwa aya kusindiswa yedwa.', 'XHO22'),
(30, 14, 19, 'Nokuba ndithe ndathumela indyikitya yokufa kwelo lizwe, ndathulula ubushushu bam phezu kwalo ngegazi, ukuze nditshabalalise kulo umntu nenkomo;', 'XHO22'),
(30, 14, 20, 'bekho apho ooNowa noDaniyeli noYobhi, ndihleli nje, itsho iNkosi uYehova, bebengayi kusindisa nyana nantombi; baya kukhulula umphefumlo wabo kuphela ngobulungisa babo.', 'XHO22'),
(30, 14, 21, 'Kuba ithi iNkosi enguMongami uYehova: “Kuya kuba njani na ukuba ndithumele izigwebo zam ezine ezibuhlungu eYerusalem, ezizezi, ikrele nendlala neendudumo nendyikitya yokufa ukuze kupheliswe kuyo umntu nesilwanyana?', 'XHO22'),
(30, 14, 22, 'Yabonani, kuya kubakho abasindileyo kuwo, abangoonyana neentombi; yabonani, baya kuphuma beze kuni, niyibone indlela yabo neentlondi zabo; yaye ngokuqinisekileyo niya kuthuthuzeleka ngenxa yentlekele endiyizisele iYerusalem nangenxa yako konke endikuzisele kona.', 'XHO22'),
(30, 14, 23, 'Baya kunithuthuzela, xa niyibonayo indlela yabo neentlondi zabo; yaye niya kwazi ukuba yonke into endiyenzileyo andiyenzanga ngaphandle kwesizathu,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 15, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 15, 2, 'Nyana womntu, umdiliya ulungile na ngaphezu kweminye imithi, namasebe emithi yehlathi?', 'XHO22'),
(30, 15, 3, 'Kuya kuthatyathwa umthi na kuwo wokusebenza umsebenzi? okanye baya kuxhoma isikhonkwane kuso na?', 'XHO22'),
(30, 15, 4, 'Yabona, kufakwe emlilweni njengokudla; umlilo utshisa iincam zayo zombini, nombindi wayo urhawuke. Ngaba iwufanele nawuphi na umsebenzi?', 'XHO22'),
(30, 15, 5, 'Yabona, xa ibifile, ibingenamsebenzi; Ukuba ungawutshiselwa ngumlilo, ubungafanelanga na kuwo nawuphi na umsebenzi?', 'XHO22'),
(30, 15, 6, 'Ngako oko itsho iNkosi uYehova ukuthi, Njengomdiliya phantsi kwemithi yehlathi, endiwutshisayo emlilweni, ukuba ube ziinkuni, ndenjenjalo ukubanika abemi baseYerusalem;', 'XHO22'),
(30, 15, 7, 'ndibubhekise ubuso bam kubo; baya kuphuma komnye umlilo, badle umlilo wumbi; nazi ukuba ndinguYehova xa ndibubhekisa ubuso bam kubo.', 'XHO22'),
(30, 15, 8, 'Ndiya kulenza ilizwe kube senkangala, ngenxa yokuba banyanisile; itsho iNkosi uYehova.', 'XHO22'),
(30, 16, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 16, 2, 'Nyana womntu, iYerusalem yazi amasikizi ayo,', 'XHO22'),
(30, 16, 3, 'uthi, Itsho iNkosi uYehova kwiYerusalem; Ukuzalwa kwakho nenzala yakho kuphuma ezweni lakwaKanan; uyihlo waye engumAmori, unyoko engumHetikazi.', 'XHO22'),
(30, 16, 4, 'Ukuzalwa kwakho ke, ngemini yokuzalwa kwakho ayinqunyulwanga inkaba yakho, akuhlanjwanga ngamanzi ukuba ukhohlise; ubungebanga netyuwa konke konke, akuchaphazelekanga konke konke.', 'XHO22'),
(30, 16, 5, 'Akubangakho liso linenceba kuwe, ukuze wenze noko inye kwezo zinto kuwe, ukuba abe nenceba kuwe; ke wena waphoswa ezweni, ngenxa yento elisikizi kuwe, ngomhla wokuzalwa kwakho.', 'XHO22'),
(30, 16, 6, 'Ndithe ndigqitha kuwe, ndakubona udyobhekile egazini lakho, ndathi kuwe, Usegazini lakho njalo, phila; Ewe, ndathi kuwe usegazini lakho, Phila!', 'XHO22'),
(30, 16, 7, 'Ndakwenza wandisa njengembewu yelizwe, wanda, wahluma, wafikelela eehokweni ezintle;', 'XHO22'),
(30, 16, 8, 'Ndagqitha kuwe, ndakubona, yabona, ixesha lakho lilixesha lothando; Ndatwabulula ingubo yam phezu kwakho, ndabamba ubuze bakho; ndakufungela, ndenza umnqophiso nawe, itsho iNkosi uYehova; waba ngowam ke.', 'XHO22'),
(30, 16, 9, 'Ndanihlamba ke ngamanzi; Ndilihlambe amagazi akho, ndakuthambisa ngeoli.', 'XHO22'),
(30, 16, 10, 'Ndakwambesa ngelaphu elilukiweyo, ndakunxiba izixathula zezikhumba zamahlengezi, ndakujikelisa ngelinen ecikizekileyo, ndakwambesa isilika.', 'XHO22'),
(30, 16, 11, 'Ndakuhombisa ngezacholo, ndakufaka izacholo ezandleni, nomxokelelwane emqaleni wakho.', 'XHO22'),
(30, 16, 12, 'Ndakunika ikhonkco lempumlo, namajikazi ezindlebeni zakho, nesithsaba sokuhomba entloko.', 'XHO22'),
(30, 16, 13, 'Wavatha ke igolide nesilivere; Iingubo zakho zaba zezelinen ecikizekileyo, nezesilika, nezilukiweyo; wadla umgubo ocoliweyo nobusi neoli, yaye umhle kakhulu, yaye waphumelela ebukumkanini.', 'XHO22'),
(30, 16, 14, 'Udumo lwakho ludume phakathi kweentlanga ngenxa yobuhle bakho, ngenxa yokuba lwazaliseka ngenxa yobuhle bam endakunikayo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 16, 15, 'Kodwa wena wakholosa ngobuhle bakho waza wahenyuza ngenxa yegama lakho, yaye waphalaza ubuhenyu bakho kubo bonke abadlula ngendlela; ibiyeyakhe.', 'XHO22'),
(30, 16, 16, 'Wathabatha ezingutyeni zakho, wahombisa iingubo zakho ezimibalabala, wakrexeza kuyo; aziyi kuza, kwaye aziyi kubakho.', 'XHO22'),
(30, 16, 17, 'Wathabatha izihombo zakho ezintle zegolide yam nesilivere yam endakunikayo, wazenzela imifanekiso yabantu waza wakrexeza nayo.', 'XHO22'),
(30, 16, 18, 'Wathabatha iingubo zakho ezilukiweyo, wazambatha; Ioli yam nesiqhumiso sam ubeke phambi kwayo.', 'XHO22'),
(30, 16, 19, 'Ukudla kwam endikunikileyo, oko kukuthi, umgubo ocoliweyo, neoli, nobusi, endakudlisayo, ùkwenze ube livumba elithozamisayo; Kwaba njalo, itsho iNkosi uYehova.', 'XHO22'),
(30, 16, 20, 'Wathabatha oonyana bakho neentombi zakho, owazizalela mna, wabingelela ngazo ukuba badle. Ngaba ukukrexeza kwakho yinto encinci?', 'XHO22'),
(30, 16, 21, 'Nibabulele ke oonyana bam waza wabanikela emlilweni?', 'XHO22'),
(30, 16, 22, 'Kuzo zonke izinto zakho ezingcolileyo neqhayiya lakho, awuzange uyikhumbule imihla yozuko lwakho, xa wawungenazintloni, wawumbi, yaye wawungcoliswe ligazi lakho.', 'XHO22'),
(30, 16, 23, 'Oku kwenzeka emva kwabo bonke ububi bakho—yeha, yeha nina, itsho iNkosi enguMongami uYehova!', 'XHO22'),
(30, 16, 24, 'kananjalo wazakhela indawo entle, wazenzela iindawo eziphakamileyo ezitratweni zonke.', 'XHO22'),
(30, 16, 25, 'Wazakhela iindawo eziphakamileyo kuzo zonke izidima zendlela, wabenza ubuhle bakho baba lizothe, watyhila iinyawo zakho kuye wonk’ ubani odlula ngendlela, yaye ngokufuthi wakrexeza neyakho.', 'XHO22'),
(30, 16, 26, 'Wahenyuza namaYiputa, angabamelwane nawe, inyama enkulu; ndakwandisa ubuhenyu bakho ukuba undiqumbise.', 'XHO22'),
(30, 16, 27, 'Yabona, kungenxa yoko le nto ndolulela phezu kwakho isandla sam, ndaza ndakunciphisa ukutya obukudlayo, ndaza ndakunikela kwintando yabo bakuthiyayo, iintombi zamaFilisti ezazineentloni ngenxa yezono zakho indlela yakho elihlazo.', 'XHO22'),
(30, 16, 28, 'Waphinda wakrexeza nama-Asiriya, ngenxa yokuba unganelanga; ewe, wenza okufanayo kubo, kodwa noko akwanelanga.', 'XHO22'),
(30, 16, 29, 'Wasuka wahenyuza kwesa kwaKanan kwesa kwelamaKaledi; kanti akwanelisekanga yiyo.', 'XHO22'),
(30, 16, 30, 'Hayi, ukuthamba kwentliziyo yakho, itsho iNkosi uYehova; ngokuba wenze zonke ezo zinto, umsebenzi wehenyukazi elilawulayo;', 'XHO22'),
(30, 16, 31, 'ukuze wakhe uzuko lwakho emantloko eendlela zonke, wenze ukuphakama kwakho ezitratweni zonke; anibanga njengehenyukazi, ningcikiva umvuzo;', 'XHO22'),
(30, 16, 32, 'Kunjengomfazi okrexezayo, owamkela abasemzini!', 'XHO22'),
(30, 16, 33, 'Onke amahenyukazi anika izipho, kodwa wena ubanika izipho zakho zonke intanda yakho, ubaqeshe, ukuze beze kuwe bevela ngeenxa zonke ngenxa yobuhenyu bakho.', 'XHO22'),
(30, 16, 34, 'Kwahlukile kuwe, nakubamankazana eburheletyweni bakho, ekungekho namnye ukulandelayo ngokuhenyuza;', 'XHO22'),
(30, 16, 35, 'Ngako oko, henyukazi, live ilizwi likaYehova.', 'XHO22'),
(30, 16, 36, '“Itsho iNkosi enguMongami uYehova ukuthi, ‘Ngenxa yokuba ukungcola kwakho kuphalaziwe yaye amampunge akho atyhilwe ngokuhenyuza kwakho nabantu obathandayo nakubo bonke oothixo abangahlambulukanga bezinto zakho ezicekisekayo nangegazi koonyana bakho owabanikayo;', 'XHO22'),
(30, 16, 37, 'Ngako oko, uyabona, ndiya kubahlanganisa bonke abo ubathandayo, owabathandayo, nabo bonke owabathandayo, kunye nabo bonke owabathiyayo; Kwaye ndiya kubahlanganisa ngeenxa zonke kuwe kwaye ndiya kulityhila kubo ihlazo lakho, ukuze balibone lonke ihlazo lakho.', 'XHO22'),
(30, 16, 38, 'Ndiya kukugweba ngokwesiko labatshabhisi bomtshato, baphalaze igazi; Ndiya kuninika igazi lomsindo nekhwele.', 'XHO22'),
(30, 16, 39, 'Ndiya kukunikela esandleni sabo, bayitshabalalise indawo yakho enkulu, bazitshabalalise iziganga zakho; baya kukuhluba iingubo zakho, bazithabathe izihombo zakho zokuhomba, bakushiye uze.', 'XHO22'),
(30, 16, 40, 'Kwaye baya kukunyusela isihlwele, bakuxulube ngamatye, baze bakuhlabe ngamakrele abo.', 'XHO22'),
(30, 16, 41, 'Ziya kuzitshisa izindlu zakho ngomlilo, zikugwebe phambi kwabafazi abaninzi; Ndiya kukuyeka ukuhenyuza, ungabi sahlawula;', 'XHO22'),
(30, 16, 42, 'ndiwubeke phezu kwakho umsindo wam, limke ikhwele lam kuwe, ndithule, ndingabi saqumba.', 'XHO22'),
(30, 16, 43, 'Ngenxa yokuba akuyikhumbulanga imihla yobutsha bakho, kodwa undikhwankqise kuzo zonke ezi zinto; yabona, ndiya kukubeka ngokwendlela yakho entlokweni yakho, itsho iNkosi uYehova; yaye musani ukusenza esi senzo silihlazo ngaphezu kwawo onke amasikizi enu.', 'XHO22'),
(30, 16, 44, 'Yabona, wonk’ ubani osebenzisa imizekeliso ya kukusebenzisa ngale mizekeliso, esithi: “Injalo unina, injalo intombi yakhe.', 'XHO22'),
(30, 16, 45, 'Wena uyintombi yonyoko, oyithiyileyo indoda yakhe nabantwana bakhe; wena ungudade woodade benu, abamthiyileyo amadoda abo nabantwana babo; unyoko ngumHetikazi, uyihlo engumAmori.', 'XHO22'),
(30, 16, 46, 'Udade wenu omkhulu nguSamari namagxamesi akhe ahleli ngasekhohlo kwakho; nomsakwenu omncinane kuwe, ehleli ngasekunene kwakho, yiSodom namagxamesi ayo.', 'XHO22'),
(30, 16, 47, 'Kodwa akuhambanga ngeendlela zabo ungenzanga ngokwezinto zabo ezicekisekayo, kodwa ngokungathi yinto encinane, wonakalise ngakumbi kuzo zonke iindlela zakho.', 'XHO22'),
(30, 16, 48, 'Ndihleli nje, itsho iNkosi uYehova, iSodom udade wenu, yona namagxamesi ayo, ayenzanga le nto uyenzileyo, wena neentombi zakho.', 'XHO22'),
(30, 16, 49, 'Yabona, inkohlakalo yaseSodom, umzalwana wayo, yabe iliqhayiya, nokutya okutyebileyo, nokutyeba okukhulu, ikuye namagxamesi akhe;', 'XHO22'),
(30, 16, 50, 'Kwaye babeqhayisa kwaye benyelisa phambi kwam; ke ngoko, ndawathabatha njengoko ndibona kufanelekile.', 'XHO22'),
(30, 16, 51, 'NoSamari akonanga ngokwesiqingatha sezono zakho; kodwa wena uye wawandisa amasikizi akho ngaphezu kwabo, waza wabenza oodade wenu bangamalungisa ngamasikizi akho onke ozenzileyo.', 'XHO22'),
(30, 16, 52, 'Nani ke nibagwebe abazalwana benu, lithwaleni ihlazo lezono zenu, enone zona ezimbi kunezabo; bona balungile kunawe; oodade.', 'XHO22'),
(30, 16, 53, 'Ekubabuyiseni kwam abathinjwa babo, abathinjwa baseSodom nezixeko eziyingqongileyo, nabathinjwa baseSamariya neentombi zayo, ndiya kubabuyisela phakathi kwabo abathinjwa bakho;', 'XHO22'),
(30, 16, 54, 'ukuze ulithwale ihlazo lakho, udane ngenxa yeento zonke ozenzileyo, ngokuba ubathuthuzele.', 'XHO22'),
(30, 16, 55, 'Xa oonyana bakho, iSodom namagxamesi ayo, babuyela emhlabeni wabo wokuqala, noSamari namagxamesi akhe babuyela ezweni labo lamandulo, nawe neentombi zakho niza kubuyela ezweni lakowenu.', 'XHO22'),
(30, 16, 56, 'Ngenxa enokuba iSodom umsakwenu ayizange ikhankanywe emlonyeni wakho ngemini yekratshi lakho.', 'XHO22'),
(30, 16, 57, 'Abukabonwa ububi bakho, njengakwimihla yokugculelwa kweentombi zama-Aram, neyabangeenxa zonke kuye, neentombi zamaFilisti, ebezikucunula ezindaweni zonke.', 'XHO22'),
(30, 16, 58, 'Ulithwele ihlazo lakho nezinto zakho ezicekisekayo,” utsho uYehova.', 'XHO22'),
(30, 16, 59, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Nawe ndiya kwenza njengoko wenze ngako, wena osidelileyo isifungo ngokwaphula umnqophiso.', 'XHO22'),
(30, 16, 60, 'Ndiya kuwukhumbula umnqophiso wam endawenza nawe ngemihla yobuncinane bakho, ndenze umnqophiso ongunaphakade nawe.', 'XHO22'),
(30, 16, 61, 'Apho niya kukukhumbula ukushukuma kwenu, kwaye niya kuba neentloni ekubamkeleni kwenu abantwana benu, abadala benu nabancinane benu; Ndokunika bona babe ziintombi zakho, kodwa bangabi ngomnqophiso wakho.', 'XHO22'),
(30, 16, 62, 'Ndiwumise umnqophiso wam nani; nazi ukuba ndinguYehova;', 'XHO22'),
(30, 16, 63, 'ukuze ucinge uze ume uneentloni uze ungabi sawuvula umlomo wakho kwakhona ngenxa yehlazo lakho, ukuba ndiya kuphinda ndikuxolelanise ngenxa yako konke okwenzileyo,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 17, 1, 'Kwafika uMoya kaYehova kum, wathi,', 'XHO22'),
(30, 17, 2, 'Nyana womntu, yenza iqhina, uthethe ngomzekeliso kwindlu kaSirayeli;', 'XHO22'),
(30, 17, 3, 'Utsho ukuthi, Itsho iNkosi uYehova ukuthi, Yabona, ndiwuthabathel’ indlebe; Ixhalanga elikhulu, elimaphiko makhulu, namaphiko amade, lizele ziintsiba ezimibalabala, leza eLebhanon, lathabatha incopho yomthi womsedare.', 'XHO22'),
(30, 17, 4, 'Wagawula amahlamvu awo aphezulu wawazisa ezweni labarhwebi; Wayibeka emzini wabarhwebi.', 'XHO22'),
(30, 17, 5, 'Nathabatha imbewu emhlabeni nayityala emhlabeni otyebileyo; ibeke ngasemanzini amakhulu ibeke okomthi wehobe.', 'XHO22'),
(30, 17, 6, 'Yahluma, yaba ngumdiliya onabileyo ombonakalo intle, ojikela ekupheleni, neengcambu zawo zibe phantsi kwawo; waba ngumdiliya, waqhama, wavelisa amasebe.', 'XHO22'),
(30, 17, 7, 'Kwabakho elinye ixhalanga elikhulu, elimaphiko makhulu, linoboya obuninzi; nanku lo mdiliya unabela iingcambu zawo, unabela neengcambu zawo, wawanyakamisa imisele yamasimi awo;', 'XHO22'),
(30, 17, 8, 'Ubutyelwe kuwo umhlaba omhle, ngasemanzini amaninzi, ukuze wenze isiqhamo, ukuba ube yidiliya elungileyo.', 'XHO22'),
(30, 17, 9, 'Ngaba nithi, Itsho iNkosi enguMongami uYehova ukuthi; Ngaba iya kuphumelela? Ke yena akayi kuzinyothula na iingcambu zawo, azinqumle iziqhamo zawo, ubune? Iya kubuna onke amagqabi omthombo wayo, nokuba ingenawo amandla amakhulu okanye abantu abaninzi bokuwuncothula ngeengcambu zawo.', 'XHO22'),
(30, 17, 10, 'Yabona, ukuba luthi lwatyalwa, lomila na? Awuyi koma na, ukuba uthe wafikelwa lulophu lwasempumalanga? Iya koma emseleni obukhule kuwo.', 'XHO22'),
(30, 17, 11, 'Kwafika umoya kaYehova kum, wathi:', 'XHO22'),
(30, 17, 12, 'Yithi ke kwindlu eneenkani, Anikwazi na ukuba kuyintoni na oko? Yithi kubo, Yabonani, ukumkani waseBhabheli weza eYerusalem, wamthabatha ukumkani wakhe nabathetheli bakhe, wabasa naye eBhabheli;', 'XHO22'),
(30, 17, 13, 'Wathabatha koonyana bokumkani, wenza umnqophiso naye, wamfungela; woyisa iingxilimbela zehlabathi.', 'XHO22'),
(30, 17, 14, 'Ke ubukumkani bube buncinane, ukuba ungabuphakamisi kodwa ngokubugcina.', 'XHO22'),
(30, 17, 15, 'Wasuka ke wagwilika kuye ngokuthumela abakhonzi bakhe eYiputa, ukuze amnike amahashe namadoda amaninzi. Ngaba uya kuphumelela? Wosinda na owenze ezo zinto? Okanye ngaba uza kuyiqhekeza aze asinde?', 'XHO22'),
(30, 17, 16, '“Mna ndihleliyo, itsho iNkosi enguMongami uYehova, ndiya kufela endaweni ahleli kuyo ukumkani, lowo wamenza ukumkani, osifumba sakhe wasidelayo, nowaphuleyo ikamva lakhe, ewe, uya kufa naye eBhabhiloni.', 'XHO22'),
(30, 17, 17, 'Ke yena uFaro, enengxokolo yakhe enamandla nengxokolo yakhe enkulu, akayi kulwa naye ngokwakha iintaba, nangokukha iimboniselo zokutshabalalisa abantu abaninzi.', 'XHO22'),
(30, 17, 18, 'Ekubeni, esidelile isifungo ukuba uyaphule, yabona, unike isandla sakhe, wenza zonke ezo zinto; akayi kusinda.', 'XHO22'),
(30, 17, 19, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ndihleli nje, isifungo sakhe awasaphulileyo, nesifungo sam awaphuleyo, ndiya kumnika entlokweni yakhe.', 'XHO22'),
(30, 17, 20, 'Yaye ngokuqinisekileyo ndiya kuyihlasela, yaye ngokuqinisekileyo iya kubanjiswa ngumnatha wam, ndiyizise eBhabhiloni ndize ndilwe nayo imfazwe ngenxa yokunxaxha kwayo endone ngako.', 'XHO22'),
(30, 17, 21, 'Kwaye bonke abasabileyo bakhe ngomphefumlo wakhe wonke ndiya kuwa kunye nelizwe, kwaye amasalela ayakuba ziintsali emazweni onke; nazi ukuba ndinguYehova othethileyo.', 'XHO22'),
(30, 17, 22, '“Itsho iNkosi enguMongami uYehova ukuthi: “Ndiya kuthabatha indawo ephakamileyo yomsedare ndiyibeke; ndowunqumla umthi encotsheni yamasebe awo, ndiwutyale entabeni ephakamileyo, entle kunene;', 'XHO22'),
(30, 17, 23, 'Ndiya kulityala ke entabeni ende yakwaSirayeli, livelise iziqhamo, libe ngumsedare olungileyo; phantsi kwawo kuhlale zonke iintaka zeentlobo ngeentlobo; emthunzini wakhe baya kuhlala.', 'XHO22'),
(30, 17, 24, 'Yazi yonke imithi yelizwe, ukuba mna Yehova ndimise umthi omde, ndahluma umthi omncinane, ndomisa umthi omanzi, ndiwuqhamisile umthi owomileyo; Mna Yehova ndithethile, ndenza.', 'XHO22'),
(30, 18, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 18, 2, 'Iyintoni na kuni le mizekeliso ngomhla wamaSirayeli, nisithi, Ooyise badla iidiliya ezibolileyo, aba buthelezi amazinyo oonyana?', 'XHO22'),
(30, 18, 3, 'Ndihleli nje, utsho uYehova, anisayi kuba sazekelisa ngale mizekeliso ezweni lakwaSirayeli.', 'XHO22'),
(30, 18, 4, 'Yabonani, yonke imiphefumlo yeyam; unjengomphefumlo kayise, unjalo umphefumlo wonyana wam; umphefumlo owonayo umele ufe.', 'XHO22'),
(30, 18, 5, 'Kodwa ukuba umntu uthe walungisa, wenza ubulungisa nobulungisa,', 'XHO22'),
(30, 18, 6, 'Wayengadli ezintabeni, engawaphakamiseli amehlo akhe emifanekisweni eqingqiweyo yendlu kaSirayeli, engamhlambeli umfazi wommelwane, engasondeli nakumfazi wenyanga;', 'XHO22'),
(30, 18, 7, 'akacudisa mntu, wasibuyisela isibambiso sakhe konetyala, akaphanga mntu, wasinika olambileyo isonka sakhe, wamambesa oze ngengubo;', 'XHO22'),
(30, 18, 8, 'Obengasebenzisi nzala, akathabathanga nkongozelo, osigcineyo isandla sakhe ebubini, ugweba ngenyaniso phakathi komntu nomntu;', 'XHO22'),
(30, 18, 9, 'wahamba ngemimiselo yam, wawagcina amasiko am, ukuba enze ngenyaniso; ulilungisa, inene, uya kuphila; itsho iNkosi uYehova.', 'XHO22'),
(30, 18, 10, 'Ngokungathi wazala unyana wesela, umbulali, wenza ngolo hlobo komnye wabo;', 'XHO22'),
(30, 18, 11, 'Ke ukuba akenzanga nanye kwezo zinto, wodlela ezintabeni, umhlambele umfazi wommelwane wakhe;', 'XHO22'),
(30, 18, 12, 'Wacudisa usizana nolihlwempu, wathinjwa, akasibuyisa isibambiso, wawaphakamisela kwizigodo amehlo akhe, wenza amasikizi;', 'XHO22'),
(30, 18, 13, 'Wayesebenzisa inzala, ethabatha inzala; uyakuphila ke? akayi kuphila; bayawenza onke amasikizi; inene, uya kufa; igazi lakhe malibe phezu kwakhe.', 'XHO22'),
(30, 18, 14, 'Yabonani ke, ethe wazala unyana othe wazibona zonke izono zikayise azenzileyo, walinda, ukuba angenzi ngolo hlobo;', 'XHO22'),
(30, 18, 15, 'ongadliyo ezintabeni, angawaphakamiseli amehlo akhe kwizigodo zendlu kaSirayeli, akamenza inqambi umfazi wommelwane wakhe;', 'XHO22'),
(30, 18, 16, 'Nokuba uthe wacudisa abantu, angasilulameli isibambiso, angaxhakamfuli; isonka sakhe asinike olambileyo, amgubungele oze ngengubo;', 'XHO22'),
(30, 18, 17, 'owabamba isandla sendoda elihlwempu, yona engathabathanga nzala, nanzala, neyayigcina imali yam, wahamba ngemali yam; akayi kufa ngobugwenxa bukayise; uya kuphila.', 'XHO22'),
(30, 18, 18, 'Ke yena uyise, ngenxa yokuba wamcinezela ngenkohlakalo waza waphanga umntakwabo waza wenza okungafanelekanga phakathi kwabantu bakowabo, yabona, naye uya kufa ngenxa yobungendawo bakhe.', 'XHO22'),
(30, 18, 19, 'Ke nina nithi, Ngani na? Unyana akabuthwali na ubugwenxa bukayise? Ukuba inkwenkwe ithe yenza ngokwesiko nangobulungisa, yayigcina yonke imithetho yam, ukuba iyayenza, iphila.', 'XHO22'),
(30, 18, 20, 'Umphefumlo owenza isono kuya kufa wona. Unyana akayi kuthwala ubugwenxa bukayise, noyise akayi kubuthwala ubugwenxa bonyana;', 'XHO22'),
(30, 18, 21, 'Kodwa ukuba umntu ongendawo uthe waguquka ezonweni zakhe zonke azenzileyo waza wayigcina yonke imiyalelo yam emisiweyo waza wenza ubulungisa nobulungisa, ngokuqinisekileyo uya kuphila, akayi kufa.', 'XHO22'),
(30, 18, 22, 'Bonke ubugwenxa bakhe abenzileyo abuyi kubizwa kuye; uya kuphila ngobulungisa bakhe abenzileyo.', 'XHO22'),
(30, 18, 23, 'Kumnandi na kum ukufa kongendawo? itsho iNkosi uYehova, akukuko nokuba abuye ezindleleni zakhe, aphile?', 'XHO22'),
(30, 18, 24, 'Kodwa ukuba ilungisa liyaguquka ebulungiseni balo, lenze ngokungekho sikweni, lenze ngokwamasikizi awenzileyo ongendawo, ngaba liya kuphila na? Bonke ubulungisa bakhe abenzileyo abunakubizwa;', 'XHO22'),
(30, 18, 25, 'Ke nina nithi, Indlela kaYehova ayilungelelene. Yivani, ndlu kaSirayeli; Indlela yam ayilunganga? Ngaba iindlela zenu azilunganga?', 'XHO22'),
(30, 18, 26, 'Xa ilungisa liguquka ebulungiseni balo, lenza ngobugqwetha, lize life ngenxa yoko; ngenxa yobubi bakhe abenzileyo, uya kufa.', 'XHO22'),
(30, 18, 27, 'Kanjalo, ukuba uthe waba mbi, wabangela ububi abenzileyo, wenza okulungileyo nobulungisa, uya kuwugcina umphefumlo wakhe uphila.', 'XHO22'),
(30, 18, 28, 'Ekubeni ebuyile ezikreqweni zakhe zonke azenzileyo waza wabuya, ngokuqinisekileyo uya kuphila, akayi kufa.', 'XHO22'),
(30, 18, 29, 'Yathi ke indlu kaSirayeli, Indlela kaYehova ayilungelelene. Iindlela zam azilungelelene na, ndlu kaSirayeli? Ngaba iindlela zenu azilunganga?', 'XHO22'),
(30, 18, 30, 'Ngoko ke ndiya kunigweba, nina bendlu kaSirayeli, ngamnye ngendlela yakhe, itsho iNkosi enguMongami uYehova. Guqukani, niguquke kuzo zonke izikreqo zenu; ngoko abuyi kunifikela ububi.', 'XHO22'),
(30, 18, 31, 'Lahla zonke izikreqo zakho okreqe ngazo kuwe; nizenzele intliziyo entsha nomoya omtsha; Yini na ukuba nife, ndlu kaSirayeli?', 'XHO22'),
(30, 18, 32, 'Kuba andikholiswanga kukufa komntu ofayo, itsho iNkosi enguMongami uYehova. Ngoko ke, guquka, uphile.', 'XHO22'),
(30, 19, 1, 'Ngaba uzihlabe na izikhulu zakwaSirayeli?', 'XHO22'),
(30, 19, 2, 'Wathi, Uyintoni na unyoko? Yayiyingonyamakazi, ilele phakathi kweengonyama, ilundla amathole ayo phakathi kweengonyama.', 'XHO22'),
(30, 19, 3, 'Yakhulisa elinye lamantshontsho ayo, yaba livukuthu, yafunda ukubambisa inyamakazi; yatya abantu.', 'XHO22'),
(30, 19, 4, 'Neentlanga zeva ngaye; Wabanjiswa ngumgibe wabo, bamthabatha bamsa ezweni laseYiputa ngamakhamandela.', 'XHO22'),
(30, 19, 5, 'Yakubona ukuba ulibele, iphelelwe lithemba, yathabatha elinye lamathole ayo, yalenza ingonyama.', 'XHO22'),
(30, 19, 6, 'Yahamba neengonyama, yaba yingonyama, yafunda ukubambisa ixhoba, yadla abantu;', 'XHO22'),
(30, 19, 7, 'Wazi ukuba izindlu zookumkani babo zibunile, wayichitha imizi yabo; elimiweyo laba yintlango nenzaliseko yalo, ngesandi sesandi salo.', 'XHO22'),
(30, 19, 8, 'Zamhlasela iintlanga, ziphuma emazweni onke, zagaleleka kuye; wabanjiswa ngumgibe wabo.', 'XHO22'),
(30, 19, 9, 'Bamfaka amakhamandela, bamsa kukumkani waseBhabheli;', 'XHO22'),
(30, 19, 10, 'Unyoko unjengomdiliya, otyelwe ngasemanzini, ekuphumleni kwakho;', 'XHO22'),
(30, 19, 11, 'Waba neentonga zobukumkani ezomeleleyo zokuwalawula;', 'XHO22'),
(30, 19, 12, 'Wesuka waqumba, wakhahlelwa emhlabeni, lwaza ulophu lwasempumalanga lwatshisa iziqhamo zakhe; iintonga zawo ezomeleleyo ziqhawukile, zome; umlilo ubatshisa.', 'XHO22'),
(30, 19, 13, 'Ngoku ke utyelwe entlango, ezweni elingumqwebedu, elomileyo.', 'XHO22'),
(30, 19, 14, 'Kuphuma umlilo emahlamvini amasebe awo, uzitshise iziqhamo zawo, akwabakho msimelelo unamandla okuba ngumsimelelo wakomkhulu. Kukukhala, kwaye kuya kuba sisikhalo.', 'XHO22'),
(30, 20, 1, 'Kwathi ngomnyaka wesixhenxe, ngenyanga yesihlanu, ngolweshumi enyangeni leyo, kwafika amadoda, evela kumadoda amakhulu akwaSirayeli, ukuba acele kuYehova, ahlala nam.', 'XHO22'),
(30, 20, 2, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 20, 3, 'Nyana womntu, thetha namadoda amakhulu akwaSirayeli, uthi kuwo, uYehova uthethile; Uza kuthetha nam? Ndihleli nje, andiyi kubuzwa nini, utsho uYehova;', 'XHO22'),
(30, 20, 4, 'Nyana womntu, uya kubagweba na, uya kubagweba na? mabawazi amasikizi ooyise;', 'XHO22'),
(30, 20, 5, 'Wathi kubo, Itsho iNkosi uYehova ukuthi; Ekubanyuleni kwam uSirayeli, ndasiphakamisa isandla sam koonyana bendlu kaYakobi, endazazisa kubo ezweni laseYiputa, ndasiphakamisela kubo isandla sam, ndathi, NdinguYehova, uThixo wenu;', 'XHO22'),
(30, 20, 6, 'Mhla ndabaphakamisayo isandla sam ndibakhupha ezweni laseYiputa ndize ndibase kwilizwe endalibonayo, lizele lubisi nobusi, eliligugu kuwo onke amazwe.', 'XHO22'),
(30, 20, 7, 'Ndathi kubo, Lahlani elowo kwizinto ezinezothe phambi kwakhe, ningazenzi iinqambi ngoothixo abangcolileyo baseYiputa; NdinguYehova, uThixo wenu.', 'XHO22'),
(30, 20, 8, 'Kodwa bandijikele, abazange bandiphulaphule; àbazilahla izinto ezinezothe emehlweni abo, ababashiya oothixo baseYiputa; umsindo wam kubo ezweni laseYiputa.', 'XHO22'),
(30, 20, 9, 'Koko ndenzayo ngenxa yegama lam, ukuze lingahlanjelwa phambi kweentlanga ababephakathi kwazo, endazazisa kuzo phambi kwamehlo abo, ukuba ndibakhuphe ezweni laseYiputa.', 'XHO22'),
(30, 20, 10, 'Kungenxa yoko le nto ndabakhuphayo ezweni laseYiputa, ndabasa entlango.', 'XHO22'),
(30, 20, 11, 'Ndabanika imiyalelo yam, ndabazisa amasiko am, awothi umntu ewenzile aphile ngawo.', 'XHO22'),
(30, 20, 12, 'Kananjalo ndabanika iisabatha zam, ukuba zibe ngumqondiso phakathi kwam nabo, ukuze bazi ukuba mna Yehova ndiya bangcwalisa.', 'XHO22'),
(30, 20, 13, 'Indlu kaSirayeli yesuka indichasile entlango; abahamba ngemithetho yam, bayicekisa imithetho yam, awothi umntu ewenzile, aphile ngayo; Iisabatha zam zihlanjelwe kakhulu;', 'XHO22'),
(30, 20, 14, 'Oko ke ndenze ngenxa yegama lam, ukuze lingadyojwa phambi kweentlanga, endizikhuphileyo emehlweni azo.', 'XHO22'),
(30, 20, 15, 'Kananjalo mna ndabaphakamisela isandla entlango, ndathi, Andiyi kubangenisa ezweni endabanika lona, eligubungele amasi nobusi, eliligugu kumazwe onke;', 'XHO22'),
(30, 20, 16, 'Ngenxa yokuba beyicekisile imimiselo yam baza abahamba ngemimiselo yam, bazenza inqambi iisabatha zam, ngenxa yokuba intliziyo yabo yalandela izithixo zabo ezinezothe.', 'XHO22'),
(30, 20, 17, 'Kodwa laba nenceba iliso lam kubo, ukuze ndingabatshabalalisi, ndingabatshabalalisi entlango.', 'XHO22'),
(30, 20, 18, 'Ndathi ke koonyana babo, Musani ukuhamba ekhondweni looyihlo, ningayigcini imithetho yabo, ningazenzi iinqambi ngezigodo zabo.', 'XHO22'),
(30, 20, 19, 'NdinguYehova, uThixo wenu; hambani ngemimiselo yam, niyigcine imiyalelo yam, niyenze;', 'XHO22'),
(30, 20, 20, 'uzingcwalise iisabatha zam; ube ngumqondiso phakathi kwam nani, ukuze nazi ukuba ndinguYehova uThixo wenu.', 'XHO22'),
(30, 20, 21, 'Kodwa aba bantu bandivukela, abahamba ngemimiselo yam emisiweyo yaye abawagcinanga amasiko am ukuze bawenze, awothi nabani na owenzayo aphile ngawo; bazihlambele iisabatha zam; Ndathi ke, Ndiya kuthulula ubushushu bam phezu kwabo, ndenzele kubo umsindo wam entlango.', 'XHO22'),
(30, 20, 22, 'Ndasibuyisa ke isandla sam, ndenza ngenxa yegama lam, ukuba lingahlanjelwa emehlweni eentlanga, endabagxothayo emehlweni azo.', 'XHO22'),
(30, 20, 23, 'Kananjalo ndasiphakamisela kubo isandla sam entlango, ukuba ndibachithachithe ezintlangeni, ndibachithachithe emazweni;', 'XHO22'),
(30, 20, 24, 'ngenxa yokuba bengazange bayigcine imimiselo yam eyayimisiwe, kodwa bayigatya imimiselo yam eyayimisiwe baza bazenza inqambi iisabatha zam, namehlo abo alandela imifanekiso eqingqiweyo kaYehova uyise.', 'XHO22'),
(30, 20, 25, 'Ngenxa yoko ndabamisela imithetho engalunganga, nemiyalelo abangenakuphila ngayo;', 'XHO22'),
(30, 20, 26, 'Ndabenza inqambi ngezipho zabo, ndabanikela emlilweni yonke into evula isibeleko, ukuze ndibaguqule babe yintlango, ukuze bazi ukuba ndinguYehova.', 'XHO22'),
(30, 20, 27, '“Ngoko ke, nyana womntu, thetha kwindlu kaSirayeli, uthi kubo, ‘Itsho iNkosi enguMongami uYehova. Kodwa ngenxa yale nto bandingcikiva ooyihlo ngokundingcatsha.', 'XHO22'),
(30, 20, 28, 'Ndabazisa kwelo zwe ndabaphakamisela isandla sam ukuba ndibanike lona, basuka bakhangela zonke iinduli ezinde, nemithi yonke eshinyeneyo, babingelela khona, baziswa kukuhleka kwabo idini; kwaye apho benza ivumba elimnandi, kwaye bagalela iziselo zabo apho.', 'XHO22'),
(30, 20, 29, 'Ndathi kubo, Niya kufika phi na? Igama lawo ke kuthiwa yiBhama, unanamhla.', 'XHO22'),
(30, 20, 30, 'Ngako oko yithi kwindlu kaSirayeli, Itsho iNkosi uYehova; Nizenzé iinqambi na ngokwesiko looyihlo? bakrexeze ngokulandela amasikizi abo?', 'XHO22'),
(30, 20, 31, 'Kuba xa nizisa izipho zenu, xa nibatshisa oonyana benu emlilweni, nizenze iinqambi ngezithixo zenu zonke ezinezothe enizinqulayo unanamhla; Ndihleli nje, itsho iNkosi uYehova, andisayi kubuzwa nini;', 'XHO22'),
(30, 20, 32, 'Ke yona into ethe qatha entliziyweni yakho, ayiyi kubakho, xa nithi, Siya kuba njengeentlanga, njengezizwe zehlabathi, sisebenzela umthi namatye.', 'XHO22'),
(30, 20, 33, 'Ndiphila nje, itsho iNkosi enguMongami uYehova, ngokuqinisekileyo ndiya kunilawula ngesandla esomeleleyo nangengalo eyoluliweyo nangomsindo othululwayo.', 'XHO22'),
(30, 20, 34, 'Ndiza kunikhupha ezintlangeni ndize ndinihlanganisele kumazwe enisasazeke kuwo ngesandla esomeleleyo nangesandla esoluliweyo nangomsindo othululwayo.', 'XHO22'),
(30, 20, 35, 'Ndiya kunisa entlango yeentlanga, ndilwe nani khona, ubuso ngobuso;', 'XHO22'),
(30, 20, 36, 'Njengoko ndabalwelayo ooyihlo kwintlango yaseYiputa, ndiya kwenjenjalo ukunilwela, itsho iNkosi uYehova.', 'XHO22'),
(30, 20, 37, 'Ndiya kunidlula phantsi kwentonga, ndinifake kwimbophelelo yomnqophiso.', 'XHO22'),
(30, 20, 38, 'Ndiya kubatshabalalisa phakathi kwenu abagwilikayo nabanginizayo; ndiya kubakhupha ezweni lokuphambukela kwabo, bangayi ke ezweni lakwaSirayeli; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 20, 39, '“Nina ke, bendlu kaSirayeli, itsho iNkosi enguMongami uYehova ukuthi, Hambani, niye kukhonza elowo izithixo zakhe ezingento, nize emveni koko, anindiphulaphula; kodwa ize ningalenzi inqambi igama lam elingcwele ngezipho zenu nangezigodo zenu.', 'XHO22'),
(30, 20, 40, 'Ngokuba entabeni yam engcwele, entabeni ende yakwaSirayeli, itsho iNkosi uYehova, kulapho iya kundikhonza khona yonke indlu kaSirayeli, bonke abasehlabathini; khona apho ndiya kubakholisa, ndifune khona iminikelo yenu, nemirhumo yezipho zenu, nezipho zenu zonke ezingcwele.', 'XHO22'),
(30, 20, 41, 'Ndiya kukholiswa livumba lenu elithozamisayo kuni, ekunikhupheni kwam ezintlangeni, ndinibuthe emazweni enilusali kuwo; ndongcwaliswa kuni phambi kweentlanga.', 'XHO22'),
(30, 20, 42, 'Niya kwazi ukuba ndinguYehova ekuniziseni kwam kwilizwe lakwaSirayeli, kwilizwe endaphakamisa isandla sam ukuba ndilinike ookhokho benu.', 'XHO22'),
(30, 20, 43, 'Niya kuzikhumbula ke ezo iindlela zenu nangezenzo zenu zonke, enizenze iinqambi ngazo; ube neentloni emehlweni akho ngenxa yobubi bonke obenzileyo.', 'XHO22'),
(30, 20, 44, 'Niya kwazi ke ukuba ndinguYehova ekunisebenzeleni kwam ngenxa yegama lam, kungekhona ngenxa yeendlela zenu ezimbi nangenxa yezenzo zenu ezimbi, nina bendlu kaSirayeli,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 20, 45, 'Ngaphezu koko, kwabonakala ilizwi likaYehova kum lisithi:', 'XHO22'),
(30, 20, 46, 'Nyana womntu, bhekisa ubuso bakho kwelasezantsi, uliwise ilizwi lakho usingise ezantsi, uprofete ngehlathi lelizwe lasezantsi;', 'XHO22'),
(30, 20, 47, 'Uthi kwihlathi lasemzantsi, Live ilizwi likaYehova; Itsho iNkosi uYehova ukuthi; Yabona, ndifaka umlilo phakathi kwakho, utshise yonke imithi eluhlaza phakathi kwakho, nemithi yonke eyomileyo;', 'XHO22'),
(30, 20, 48, 'Kwaye yonke inyama iya kubona ukuba mna, iNkosi, ndiwutshisile; awuyi kucinywa.', 'XHO22'),
(30, 20, 49, 'Ndathi ke, Owu Nkosi Yehova! Bathi kum, Akathethi ngemizekeliso na?', 'XHO22'),
(30, 21, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 21, 2, 'Nyana womntu, bhekisa ubuso bakho eYerusalem, ilizwi lakho lithethe ezingcweleni, uprofete ngomhlaba wakwaSirayeli;', 'XHO22'),
(30, 21, 3, 'Uthi kwilizwe lakwaSirayeli, uYehova uthethile; Yabona, ndinendawo ngawe: ndirhola ikrele lam esingxotyeni salo, ndinqumle kuwe olilungisa nongendawo;', 'XHO22'),
(30, 21, 4, 'Ukususela ngelo xesha ndiya kumtshabalalisa kuwe olilungisa nongendawo, ngoko ke ikrele lam liya kuphuma esingxotyeni salo ukuze lilwe nayo yonke inyama ukususela emzantsi kuse emntla;', 'XHO22'),
(30, 21, 5, 'yazi yonke inyama, ukuba mna Yehova ndilirholile ikrele lam esingxotyeni salo; aliyi kubuya libuye.', 'XHO22'),
(30, 21, 6, 'Nyana womntu, uyancwina, ngokuba zaphukile izinqe; bancwine ngeentlungu phambi kwamehlo abo.', 'XHO22'),
(30, 21, 7, 'Xa bathe kuwe, Uyancwina na? ukuze niphendule, nithi, Ngenxa yelizwi elo; ngokuba iya kuza, zinyibilike zonke iintliziyo; yabona, iyeza, iya kubakho; itsho iNkosi uYehova.', 'XHO22'),
(30, 21, 8, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 21, 9, 'Nyana womntu, profeta uthi, uYehova uthethile; Yithi, Ikrele, ikrele liloliwe, lirholile;', 'XHO22'),
(30, 21, 10, 'Limiselwe ukubulala kakhulu; ilungele ukukhanya: ngaba sinokuvuya? idela intonga yonyana wam njengemithi yonke.', 'XHO22'),
(30, 21, 11, 'Wanikela ukuba libanjwe; eli krele liloliwe, yaye sele eza kulinikela esandleni sombulali.', 'XHO22'),
(30, 21, 12, 'Lila ulile, nyana womntu; ngokuba liya kuba phezu kwabantu bam, libe phezu kwezikhulu zonke zakwaSirayeli; kobakho ukuhlatywa komkhonto phezu kwabantu bam; wandule ubethe esinqeni.', 'XHO22'),
(30, 21, 13, 'Hi ke, ukuba ikrele liyayidela intonga? ayisayi kuba sabakho; itsho iNkosi uYehova.', 'XHO22'),
(30, 21, 14, 'Wena ke, nyana womntu, profeta, uqhwabe izandla, ikrele maligoqwe okwesithathu, ikrele labangcangcazelisiweyo; ngumkhonto wamadoda amakhulu abekekileyo, Ahamba ezimele.', 'XHO22'),
(30, 21, 15, 'Ndiwamisele ikrele emasangweni abo onke, ukuze intliziyo yabo ibe buthathaka, nezifuba zabo zibe ngamanxuwa. Ah! ikhazimlisiwe, isongelwe ukuba ihlanjwe.', 'XHO22'),
(30, 21, 16, 'Hamba uye kuwe ngandlela yimbi, okanye uye ekunene, okanye ekhohlo, apho sukuba ubuso bakho buya khona.', 'XHO22'),
(30, 21, 17, 'Nam ndiya kuqhwaba izandla zam, ndiwuthobe umsindo wam; Mna Yehova ndithethile.', 'XHO22'),
(30, 21, 18, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 21, 19, '“Wena ke, nyana womntu, ukulungele ukuhamba ngeendlela ezimbini, ukuze lifike ikrele likakumkani waseBhabhiloni, zombini ziphume ezweni elinye; uzikhethele indawo, uyikhethe ekuqaleni kwendlela eya esixekweni.', 'XHO22'),
(30, 21, 20, 'Gecani indlela, ukuze ikrele lifike eRabhati yoonyana baka-Amon, nekaYuda, eYerusalem, umzi oyinqaba.', 'XHO22'),
(30, 21, 21, 'Kuba ukumkani waseBhabhiloni wayemi ekudibaneni kweendlela, ekuqalekeni kweendlela ezimbini, ukuze avumise;', 'XHO22'),
(30, 21, 22, 'Esandleni sakhe sokunene uhlabe isihlabo ngeYerusalem, ukuba kumiswe abathetheli, kuvulwe umlomo wokubulala, kuphakanyiswe izwi lokuhlaba umkhosi, kumiswe imiqadi emasangweni, kwakhiwe iintaba, kwakhiwe inqaba.', 'XHO22'),
(30, 21, 23, 'Kubo ibe njengesihlabo esikhohlakeleyo phambi kwamehlo abo, kwabafunge isifungo;', 'XHO22'),
(30, 21, 24, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ngenxa enokuba nikukhumbule okungendawo kwenu, ekutyhilekeni kwezikreqo zenu, ukuze zibonakale izono zenu emisebenzini yenu yonke; ngokuba, ndathi, uya kuza esikhumbuzweni, uya kubanjwa ngesandla.', 'XHO22'),
(30, 21, 25, '“Wena ke, kumkani ongendawo wakwaSirayeli, omini ifike ekupheleni kobubi.', 'XHO22'),
(30, 21, 26, 'Itsho iNkosi uYehova ukuthi, Khulula unkontsho, sithulwe nesithsaba; akuyi kuba njalo: phakamisani othobekileyo, bathobe abaphakamileyo.', 'XHO22'),
(30, 21, 27, 'Ndiya kuyibuyisela, ndiyibuyise, ndiyibuyisele; kwaye akuyi kubakho de kufike lowo ulilungelo lakhe; kwaye ndiya kumnika yona.', 'XHO22'),
(30, 21, 28, 'Wena ke, nyana womntu, profeta, uthi, ‘Itsho iNkosi enguMongami uYehova ngokusingisele koonyana baka-Amoni, ngokuphathelele indelo yabo; nani nithi, Umkhonto urholelwe umkhonto; ngokuba igazi liqhekeziwe ukuba lisila ngenxa yokukhanya.', 'XHO22'),
(30, 21, 29, 'Bakubona unyanye, bethetha ubuxoki, ukuze bakuse ezintanyeni zabatyhoboziweyo, abangendawo iphelileyo imini yabo.', 'XHO22'),
(30, 21, 30, 'Ndiya kuyibuyisela esingqengqelweni sayo na? Ndiya kukugweba endaweni owadalwa kuyo, ezweni owazalelwa kulo.', 'XHO22'),
(30, 21, 31, 'Ndiza kuwuthululela phezu kwakho umsindo wam, ndikuvuthulule ngomlilo womsindo wam ndikunikele esandleni samadoda akhohlakeleyo nezilumko ukuba atshabalalise.', 'XHO22'),
(30, 21, 32, 'Uya kuba kukudla komlilo; igazi lakho liya kuba sezweni; akuyi kuba sakhunjulwa, ngokuba mna Yehova ndithethile.', 'XHO22'),
(30, 22, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 22, 2, 'Uyawugweba na ke, nyana womntu, umzi wamagazi? ewe, mbonise zonke izinto zakhe ezoyikekayo.', 'XHO22'),
(30, 22, 3, 'Uze uthi, Itsho iNkosi uYehova ukuthi, Lo mzi uphalaza igazi khona, ukuze lifike ixesha lawo, wenze umfanekiso oqingqiweyo ukuba uwenze inqambi.', 'XHO22'),
(30, 22, 4, 'Unetyala legazi lakho olithululeyo; uzenze inqambi ngezigodo zakho ozenzileyo; uyisondeza imihla yakho, wayifikelela iminyaka yakho; ngenxa yoko ndikwenze isingcikivo ezintlangeni, nesigculelo kumazwe onke.', 'XHO22'),
(30, 22, 5, 'Abo basondeleyo kuwe nabo bakude nawe baya kukugculela, abo banegama elingcolileyo nabanomsindo kakhulu.', 'XHO22'),
(30, 22, 6, 'Yabona, iinkosana zakwaSirayeli, ngasinye sasisesandleni saso ukuphalaza igazi.', 'XHO22'),
(30, 22, 7, 'Zikhanyiselwe kuwe nguyise nonina; Angabaphambukeli phakathi kwenu; kuni batshutshisa iinkedama nabahlolokazi.', 'XHO22'),
(30, 22, 8, 'Uzidelile izinto zam ezingcwele, neesabatha zam uzihlambele.', 'XHO22'),
(30, 22, 9, 'Phakathi kwenu kukho abahlekayo ukuphalazwa kwegazi; adle phezu kwakho ezintabeni; bayakuhlazisa.', 'XHO22'),
(30, 22, 10, 'Zifumene kuwe ihlazo looyise; babathobile ngawe abangcwele.', 'XHO22'),
(30, 22, 11, 'Wenza amasikizi emfazini wommelwane wakhe; omnye umenza inqambi umolokazana wakhe ngokuhlazeka; yaye omnye wenu uhlaze udade wabo, intombi kayise.', 'XHO22'),
(30, 22, 12, 'Bathabathe izipho kuwe ukuze baphalaze igazi; uthabathe inzala yemboleko-mali, ubathabatha abantu bakho ngokudelela; ke mna undilahlile; itsho iNkosi uYehova.', 'XHO22'),
(30, 22, 13, 'Yabona, ndibeke isandla sam phezu kobutyebi bakho obumenemeneyo, obenzileyo, naphezu kwamagazi akho obuphakathi kwakho.', 'XHO22'),
(30, 22, 14, 'Intliziyo yakho iya kuma na? Izandla zakho ziya kuthi nkqi na, ngemihla endiya kukuphatha ngayo? Mna Yehova ndithethile, yaye ndiya kwenza.', 'XHO22'),
(30, 22, 15, 'Ndiza kukuchithachitha phakathi kweentlanga ndize ndikuchithachithe kwamanye amazwe ndize ndikususe kuni ukungahlambuluki kwakho.', 'XHO22'),
(30, 22, 16, 'Uya kulidla ilifa ilifa lakho phambi kwamehlo eentlanga, nazi ukuba ndinguYehova.', 'XHO22'),
(30, 22, 17, 'Kwabonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 22, 18, 'Nyana womntu, indlu kaSirayeli yaba sisigqubuthelo kum; lonke ubhedu nebhekile nentsimbi nelothe esazulwini seziko; yayiziingqekembe zesilivere.', 'XHO22'),
(30, 22, 19, 'Ngako oko itsho iNkosi uYehova ukuthi, Ngenxa enokuba nigwetywe amagwebu nonke niphela, yabonani, ndiya kunibutha eYerusalem.', 'XHO22'),
(30, 22, 20, 'Ekuhlanganiseni kwabo isilivere, nobhedu, nesinyithi, nelothe, nebhekile, phakathi eziko, ukuba kuqhunyiswe umlilo kuyo, inyibilikiswe; ke ngoko ndiya kunibutha ndinomsindo, ndinomsindo, ndinishiye khona, ndininyibilikise.', 'XHO22'),
(30, 22, 21, 'Ewe, ndiya kunibutha, ndinivuthe emlilweni wokuvutha komsindo wam, ninyibilike kuwo.', 'XHO22'),
(30, 22, 22, 'Njengoko isilivere inyibilika eziko, iya kunyibilikiswa eziko; nazi ukuba mna, Yehova, ndiwuphalazile umsindo wam phezu kwenu.', 'XHO22'),
(30, 22, 23, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22');
