INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(30, 22, 24, 'Nyana womntu, uthi kulo, Nililizwe eliyinqambi, elinganelwayo ngomhla wengqumbo;', 'XHO22'),
(30, 22, 25, 'Licebo labaprofeti bakhe kulo; njengengonyama ebharhulayo, edla ixhoba layo; badle imiphefumlo; bathabatha ubutyebi nezinto ezixabisekileyo; bamenza abahlolokazi khona.', 'XHO22'),
(30, 22, 26, 'Ababingeleli balo bayawuhlambela umyalelo wam, bazihlambele iingcwele zam; abacalula phakathi kwengcwele nento eyinqambi, abacalula okuyinqambi nokuhlambulukileyo, bazifihle iisabatha zam emehlweni am, ndazenza inqambi phakathi kwabo.', 'XHO22'),
(30, 22, 27, 'Iinkosana zayo phakathi kwayo zinjengeengcuka eziqwenga ixhoba layo, ukuze ziphalaze igazi zize zitshabalalise imiphefumlo ukuze zizuze ubutyebi bobuxoki.', 'XHO22'),
(30, 22, 28, 'Abaprofeti bayo bawugubungela ngodongwe olungaqinanga yaye babaprofetela ubuxoki besithi: ‘Itsho iNkosi enguMongami uYehova, kodwa uYehova akathethanga.', 'XHO22'),
(30, 22, 29, 'Abantu belizwe babecinezela yaye bephanga yaye becinezela olihlwempu nosweleyo, bemcinezela owasemzini ngokungekho sikweni.', 'XHO22'),
(30, 22, 30, 'Ndakhangela kubo indoda eyayinokwakha uthango ize ime ethubeni phambi kwam ikhusele ilizwe, ukuze ndingalitshabalalisi; kodwa andizange ndifumane nto.', 'XHO22'),
(30, 22, 31, 'Ngoko ke ndathulula ubushushu bam phezu kwabo; Ndibatshise ngomlilo wokuvutha komsindo wam; ndibeke indlela yabo entlokweni yabo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 23, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 23, 2, 'Nyana womntu, kwakukho abafazi ababini, iintombi zamfazi mnye.', 'XHO22'),
(30, 23, 3, 'Baqhubeka behenyuza eYiputa; bahenyuza ebutsheni babo, apho bacinezelwa khona amabele, yaye bawenza amakhoboka amabele obunyulu babo.', 'XHO22'),
(30, 23, 4, 'Amagama abo ke nguNtente-yakhe omkhulu kubo, noNtente-yam-kuye umsakwabo; baba ngabam, bazala oonyana neentombi. Amagama abo yayinga; ElakwaSamari nguOhola, iYerusalem nguNtente-yakhe.', 'XHO22'),
(30, 23, 5, 'Yaye uOhola waqhubeka esenza ngathi yeyam; Wawanqwenela ama-Asiriya abamelwane bakhe, amasikizi akhe.', 'XHO22'),
(30, 23, 6, 'Bazambethe iingubo ezinde ezimsi, nabathetheli, nabathetheli, amadodana angamagatyana, amadodana ehashe onke.', 'XHO22'),
(30, 23, 7, 'Ngoko wahenyuza kubo, nabo bonke abanyuliweyo baseAsiriya, nabo bonke awayebanqwenela; wazenza inqambi ngezigodo zabo.', 'XHO22'),
(30, 23, 8, 'Akabuyanga kubuhenyu bakhe, awaphuma nabo eYiputa; kuba babelala naye ebutsheni bakhe, yaye bawaphula amabele obunyulu bakhe, yaye bathululela ubuhenyu babo phezu kwakhe.', 'XHO22'),
(30, 23, 9, 'Ndamnikela ke esandleni sentanda yakhe, esandleni sama-Asiriya, awayebalangazelela.', 'XHO22'),
(30, 23, 10, 'Bamhluba ubuntombi bakhe, bathabatha oonyana bakhe neentombi zakhe, bambulala ngekrele; ngokuba babemgweba.', 'XHO22'),
(30, 23, 11, 'Wabona uNtente-yakhe, u-Aholibha, ukuba uthando lwakhe lungcolile kunaye, nobuhenyu bakhe bungaphezu kobuhenyu bomninawa wakhe.', 'XHO22'),
(30, 23, 12, 'Wayewathanda ama-Asiriya nabamelwane bakhe nabaphathi-mikhosi abanxibe kakuhle neerhuluneli, abakhweli bamahashe, bonke abafana abanomtsalane.', 'XHO22'),
(30, 23, 13, 'Ndabona ukuba uzenze inqambi, bahamba kwicala elinye bobabini.', 'XHO22'),
(30, 23, 14, 'Wongeza ke ukuhenyuza kwakhe, ngenxa enokuba ebona amadoda egalelwa eludongeni, imifanekiso yamaKaledi yazala ibomvu.', 'XHO22'),
(30, 23, 15, 'Babhinqa amanqina ezinqeni zabo, baba baninzi abathwele iingubo ezimibalabala entloko, zonke izikhulu zabona ngokwesiko lamaKaledi, ilizwe lokuzalwa kwawo.', 'XHO22'),
(30, 23, 16, 'Wawabona ngamehlo akhe, wabanqwena, wathuma abathunywa kwelamaKaledi.', 'XHO22'),
(30, 23, 17, 'Eza ke amaKaledi kuye, esesililini sokuncokolisa, amenza inqambi ngobufebe babo; wazenza inqambi ngabo, yemka kubo intliziyo yakhe.', 'XHO22'),
(30, 23, 18, 'Wakulahla ke ukuhenyuza kwakhe, wawalahla amampunge akhe;', 'XHO22'),
(30, 23, 19, 'Kodwa waqhubeka elandisa ubuhenyu bakhe ngokukhumbula imihla yobutsha bakhe xa wayehenyuza ezweni laseYiputa.', 'XHO22'),
(30, 23, 20, 'Ngenxa yokuba ebakhumbula abo babathandayo, abanyama yabo injengenyama yamahashe, nabangumthonyama ngathi ngumsinga wamahashe.', 'XHO22'),
(30, 23, 21, 'Wamana ukhwaza, ukhumbula ihlazo lobutsha bakho, ekunyatheleni kwakho amathole amaYiputa, ngenxa kayihlo ebutsheni bakho.', 'XHO22'),
(30, 23, 22, 'Ngako oko, Ntente-yam-kuye, itsho iNkosi uYehova; Yabona, ndikuxhoxhela izithandwa zakho, lowo ujikileyo kubo umphefumlo wakho, ndize ndibazise nxamnye nawe macala onke;', 'XHO22'),
(30, 23, 23, 'Onke amaKaledi, namaKaledi, noPekodi, noShowa, noKowa, nama-Asiriya onke anabo, amadodana onke anqwenelekayo, nabathetheli, namabamba, amadoda amakhulu, abathetheli nabamahashe, bonke bekhwele emahasheni;', 'XHO22'),
(30, 23, 24, 'Baya kukufikela ngeenqwelo zokulwa, neenqwelo zokulwa, neevili, kwaye ngengxokolo yabantu bakubeke iingweletshetshe, ingweletshetshe, nengweletshetshe, nengxokolo; ndiwumise phambi kwabo umyalelo, banigwebe ngezigwebo zabo.', 'XHO22'),
(30, 23, 25, 'Ndonimisela ikhwele lam, baphathe kuni; Baya kususa impumlo yakho neendlebe zakho; namasalela akho aya kuwa likrele; baya kuthabatha oonyana bakho neentombi zakho; amathambo akho aya kutshiswa ngumlilo.', 'XHO22'),
(30, 23, 26, 'Baya kukuhluba iingubo zakho, bazithabathe izihombo zakho zokuhomba.', 'XHO22'),
(30, 23, 27, 'Ndiza kuliphelisa ihlazo lakho nohenyuzo lwakho owalikhupha ezweni laseYiputa, ukuze ungawaphakamiseli amehlo akho kuzo, ungabi sayicinga iYiputa.', 'XHO22'),
(30, 23, 28, 'Ngokuba itsho iNkosi uYehova ukuthi, Yabona, ndiya kukunikela esandleni sabo ubathiyileyo, esandleni sabo ibajikileyo intliziyo yakho.', 'XHO22'),
(30, 23, 29, 'Baya kukuphatha ngentiyo, bawuthabathe wonke umsebenzi wakho, bakushiye wedwa; lityhileke ihlazo lobuhenyu bakho, ihlazo lakho, nobuhenyu bakho.', 'XHO22'),
(30, 23, 30, 'Ndiya kuzenza ezi zinto kuni, ngenxa yokuba nikrexezile neentlanga nangenxa yokuba nizenze iinqambi ngezithixo zazo ezinezothe.', 'XHO22'),
(30, 23, 31, 'Uhambe ngendlela yomsakwenu; ke ngoko, ndiya kuyinikela indebe yakhe esandleni sakho.', 'XHO22'),
(30, 23, 32, 'Itsho iNkosi enguMongami uYehova ukuthi: “Uya kuyisela endebeni enzulu nenkulu kadade wenu; Uya kuba yintlekisa, intlekisa; inabaninzi.', 'XHO22'),
(30, 23, 33, 'Uya kuzala kukunxila nakukubandezeleka, yindebe yembandezelo neyokwaphula kwindebe yomzalwana wakho uSamari.', 'XHO22'),
(30, 23, 34, 'Uya kuyisela uze uyiginye, yaye uya kuziqhekeza iziqwenga zayo, uzikrazule namabele, ngenxa yokuba nditshilo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 23, 35, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ekubeni undilibele, wandiphosa emva komhlana wakho, uya kulithwala ihlazo lakho nobuhenyu bakho.', 'XHO22'),
(30, 23, 36, 'Wathi uYehova kum, Nyana womntu, uya kubagweba na ooNtente-yakhe noNtente-yam-kuye? ewe, exela izingcikivo zabo kubo;', 'XHO22'),
(30, 23, 37, 'bakrexezile, izandla zabo zinegazi; bakrexeza nezigodo zabo; oonyana babo, ababazalela mna kum, babatshisa ngomlilo.', 'XHO22'),
(30, 23, 38, 'Benjenjalo kum: bayenze inqambi ingcwele yam kwangaloo mini, bazenza inqambi iisabatha zam;', 'XHO22'),
(30, 23, 39, 'Ngenxa yokuba bazixhela izithixo zabo ezinezothe ababenqulwa ngoonyana babo, bangena kwangaloo mini engcweleni yam ukuze bayenze inqambi; kwaye yabona, besenza njalo endlwini yam.', 'XHO22'),
(30, 23, 40, 'Wathuma amadoda akude, kwathunyelwa kuwo umthunywa; yabona, ziyeza; lowo uzihlambisileyo, wawagquma amehlo akho, wahomba izivatho;', 'XHO22'),
(30, 23, 41, 'wahlala esingqengqelweni esilungileyo, wawulungiselela itafile, wabeka phezu kwayo isiqhumiso sam neoli yam;', 'XHO22'),
(30, 23, 42, 'Kwabakho izwi lesihlwele, lithuthuzelayo; athi amaSabhaya aziswa entlango, enesihlwele esikhulu, enezacholo ezandleni nezizithsaba zokuhomba entloko.', 'XHO22'),
(30, 23, 43, 'Ndathi kumfazi oselehagele ekukrexezeni, Baya kukrexeza na, yena nabo?', 'XHO22'),
(30, 23, 44, 'Bangena ke kuye njengoko babengenamfazi okrexezayo; Bangena ke kuNtente-yakhe, noNtente-yam-kuye, bahenyuza abafazi abo.', 'XHO22'),
(30, 23, 45, 'Ke amadoda angamalungisa aya kubagweba ngokwesiko lamahenyukazi, nangokwesiko labafazi abaphalaza igazi labo; ngokuba bebakrexeza, banegazi ezandleni zabo.', 'XHO22'),
(30, 23, 46, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Ndiza kubangela ukuba isihlwele sibahlasele ndize ndisinikele ukuba sisisuse sibatshabalalise.', 'XHO22'),
(30, 23, 47, 'Isihlwele siya kubagibisela ngamatye, sibathumele ngamakrele aso; nibabulale oonyana babo neentombi zabo, nitshise izindlu zabo ngomlilo.', 'XHO22'),
(30, 23, 48, 'Ngoko ndiza kuluphelisa igama elibi ehlabathini, ukuze bonke abafazi bafundiswe ukuba bangenzi ngokwezithuko zakho.', 'XHO22'),
(30, 23, 49, 'Baya kunibuyekeza ngokwezinto zenu ezimbi, nezono zezigodo zenu niya kuzithwala; nazi ukuba ndiyiNkosi uYehova.', 'XHO22'),
(30, 24, 1, 'Ngomnyaka wesithoba wodwa, ngenyanga yeshumi, ngolweshumi enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 24, 2, 'Nyana womntu, libhale igama laloo mini uthi: Ngaloo mini ukumkani waseBhabhiloni wayihlasela iYerusalem.', 'XHO22'),
(30, 24, 3, 'Yixele umzekeliso kwindlu yababandezeli, uthi kubo, iNkosi uYehova ithethile; Beka imbiza, yibeke kwaye ugalele amanzi kuyo:', 'XHO22'),
(30, 24, 4, 'Hlanganisela kuyo iinyama zayo; zonke iinyama ezilungileyo, umlenze negxalaba; yizalise ngamathambo akhethiweyo.', 'XHO22'),
(30, 24, 5, 'Thabatha itakane elikhethekileyo legusha, utshise amathambo phantsi kwalo, lizale kakuhle, amathambo alo aphekwe kulo.', 'XHO22'),
(30, 24, 6, 'Ngako oko wathi uYehova, Yeha, umzi wamagazi, imbiza enamathumba, ekungaphumiyo ilindle kuyo! yikhuphe iinyama zayo; musa ukuvumela kakhulu ukuba kuwele kuyo.', 'XHO22'),
(30, 24, 7, 'Ngokuba igazi lakhe likuso; yibeke phezu kwelitye; akasiphosa phantsi, ukuba asigubungele ngothuli;', 'XHO22'),
(30, 24, 8, 'Ukuze kuvuswe umsindo, impindezelo; igazi lakhe ndilibeke phezu kwelitye, ukuze lingagqunywa.', 'XHO22'),
(30, 24, 9, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yeha, umzi ozele ligazi! Ndiza kuphakamisa imfumba yomlilo.', 'XHO22'),
(30, 24, 10, 'Thetha iinkuni, faka umlilo, yitshiseni inyama nobulawu, atshe amathambo;', 'XHO22'),
(30, 24, 11, 'uyibeke phezu kwamalahle ayo, ingasenanto, ukuze luvuthe lutshe ubhedu lwayo, bunyibilike ubunqambi bayo phakathi kwayo, lidle intlenge yayo.', 'XHO22'),
(30, 24, 12, 'Wadinwa kukuthetha ubuxoki, nomsindo wakhe omkhulu awuzange uphume kuye; ilindle lakhe liya kuba semlilweni.', 'XHO22'),
(30, 24, 13, 'Kukho ukudana ebunqambini bakho; ngenxa enokuba ndikuhlambulule, akwahlambuluka, akusayi kuphinda uhlanjululwe ezonweni zakho, ndide ndibeke ubushushu bam phezu kwakho.', 'XHO22'),
(30, 24, 14, 'Mna Yehova ndithethile; Kuya kubakho ntoni na, ndiya kuyenza; andiyi kubuya, andiyi kuba nanceba, andiyi kuzohlwaya; baya kukugweba ngokweendlela zakho nangokwezenzo zakho, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 24, 15, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 24, 16, 'Nyana womntu, yabona, ndiwususa umnqweno wamehlo akho ngesibetho; kodwa ze ningambambazeli, nilile;', 'XHO22'),
(30, 24, 17, 'Musani ukulila, musani ukumbambazela ofileyo;', 'XHO22'),
(30, 24, 18, 'Ndathetha ke nabantu kusasa, kwahlwa, wafa umkam; kusasa ndenza njengoko ndiyalelwe.', 'XHO22'),
(30, 24, 19, 'Bathi abantu kum, Akuthandi na ukusixelela, ukuba ezi zinto zithini na kuthi, wakuba wenjenjalo?', 'XHO22'),
(30, 24, 20, 'Ndabaphendula ndathi, Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 24, 21, 'Yithi kwindlu kaSirayeli, Itsho iNkosi uYehova ukuthi, Yabonani, ndiyayenza inqambi ingcwele yam, nobukhulu bamandla enu, nenkangeleko yamehlo enu, nento oyinqwenelayo umphefumlo wenu; Oonyana bakho neentombi zakho eziseleyo kuwe ziya kuwa likrele.', 'XHO22'),
(30, 24, 22, 'Niya kwenza njengoko ndenzayo: Musani ukuwuvala umlomo wenu, nidle isonka sabantu;', 'XHO22'),
(30, 24, 23, 'iintsontelo zenu ziya kuba phezu kwentloko yenu, neembadada zenu zibe sezinyaweni zenu; musani ukulila, musani ukulila; ke niya kulila ngenxa yobugwenxa benu, nilile omnye komnye.', 'XHO22'),
(30, 24, 24, 'uHezekile woba ngumqondiso kuni; niya kwenza njengako konke awakwenzayo; yaye iya kuza, nazi ukuba ndiyiNkosi enguMongami uYehova.', 'XHO22'),
(30, 24, 25, 'Wena ke, nyana womntu, akuyi kuba naliphi na ixesha endiya kuwasusa ngalo amandla abo, imihlali yozuko lwabo, nento enqwenelekayo yamehlo abo, nento abayithembileyo ngoonyana neentombi zabo;', 'XHO22'),
(30, 24, 26, 'ngelo xesha kuya kufika osindileyo kuwe, eve ngeendlebe zakho?', 'XHO22'),
(30, 24, 27, 'Ngaloo mini uya kuvuleka umlomo wakho, kulowo ukhululweyo, uthethe, ungabi saba sisidenge; Yena uya kuba ngumqondiso kubo; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 25, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 25, 2, 'Nyana womntu, bhekisa ubuso bakho koonyana baka-Amon, uprofete kubo;', 'XHO22'),
(30, 25, 3, 'Wathi koonyana baka-Amon, Liveni ilizwi leNkosi uYehova. Itsho iNkosi uYehova ukuthi; Ngokuba uthe: Hayi! nasezweni lakwaSirayeli, oko bekusenkangala; nabendlu yakwaYuda, ekufuduselweni kwabo ekuthinjweni;', 'XHO22'),
(30, 25, 4, 'Yabona, ndikunika abantu basempumalanga, ukuba babe lilifa labo, baqinise iingxande zabo ezinde kuwe, bazakhele kuwe; baya kudla iziqhamo zakho, basele amasi akho.', 'XHO22'),
(30, 25, 5, 'Ndiya kuyenza iRabha ibe yindawo yeenkamela, eloonyana baka-Amon ndilenze libe yindawo yemfuyo; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 25, 6, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Ngenxa yokuba niqhwabe izandla nangqisha ngeenyawo nanemihlali ezintliziyweni zenu ngako konke ukuxhalaba kwenu ngomhlaba wakwaSirayeli;', 'XHO22'),
(30, 25, 7, 'Yabona, ngenxa yoko ndisolula phezu kwakho isandla sam, ndikunikela ezintlangeni, ukuba ube lixhoba; Ndiya kuninqumla ezintlangeni, ndinitshabalalise kwezinye izizwe; ndiya kukutshabalalisa; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 25, 8, 'Itsho iNkosi uYehova ukuthi, Ngenxa enokuba bathe ooMowabhi noSehire, Yabona, injengeentlanga zonke indlu kaYuda;', 'XHO22'),
(30, 25, 9, 'Ngako oko, yabona, ndiya kulivula icala lakwaMowabhi, emizini, emizini yakhe, ngasemdeni wakhe, ubuhle belizwe, iBhete-yeshimoti, neBhahali-mehon, neKiriyatayim;', 'XHO22'),
(30, 25, 10, 'Ndiza kubanika abantu baseMpuma kunye noonyana baka-Amoni, yaye ndiza kubanika ubutyebi, ukuze oonyana baka-Amoni bangabalwa phakathi kweentlanga.', 'XHO22'),
(30, 25, 11, 'Ndiya kumvelela uMowabhi; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 25, 12, 'Itsho iNkosi uYehova ukuthi, Ngenxa enokuba amaEdom ephindezelela impindezelo yendlu kaYuda, enze isono esikhulu, ephindezela kubo;', 'XHO22'),
(30, 25, 13, 'Ngako oko, itsho iNkosi uYehova ukuthi, Ndiya kusolula isandla sam kwaEdom, nditshabalalise kulo umntu nenkomo; Ndiya kuyijika ibe yintlango yakwaTeman; yaye abantu bakwaDedan baya kuwa ngekrele.', 'XHO22'),
(30, 25, 14, 'Ndiya kuphindezela kwaEdom ngesandla sabantu bam amaSirayeli, benze kuEdom ngokomsindo wam nangokomsindo wam; yaye baya kuyazi impatho yam, itsho iNkosi uYehova.', 'XHO22'),
(30, 25, 15, 'Itsho iNkosi uYehova ukuthi; ngokuba amaFilisti ebephindezela impindezelo, ephindezela ngenkohlakalo yentliziyo, ukuba atshabalalise ngenxa yentiyo yakudala;', 'XHO22'),
(30, 25, 16, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabona, ndiya kusolula isandla sam phezu kwamaFilisti, ndiwanqumle amaKrete, ndiwatshabalalise amasalela aselunxwemeni lolwandle.', 'XHO22'),
(30, 25, 17, 'Ndiya kubazisela usindiso olukhulu ngesohlwayo esoyikekayo; bazi ukuba ndinguYehova, ekuthetheni kwam usindiso lwam kubo.', 'XHO22'),
(30, 26, 1, 'Ngomnyaka weshumi elinamnye, ngolokuqala enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 26, 2, 'Nyana womntu, ngenxa enokuba iTire yathi ngeYerusalem, Yabona, liqhekekile, amasango abantu! waguqukela kuM; Ndiya kwaneliswa, ngoku ubhuqiwe.', 'XHO22'),
(30, 26, 3, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabona, ndikunqambi, Tire; ndiya kuxhokonxela iintlanga ezininzi ngawe, njengokuba ulwandle luphakamisa amaza alo;', 'XHO22'),
(30, 26, 4, 'zizigungxule iindonga zaseTire, zizigungxule iinqaba zakho ezinde; yaye ndiya kulusula uthuli lwakhe ndize ndimenze abe njengencopho yeliwa.', 'XHO22'),
(30, 26, 5, 'Iya kuba yindawo yokwaneka iminatha phakathi elwandle, ngenxa yokuba nditshilo, itsho iNkosi enguMongami uYehova, ibe iya kuba lixhoba ezintlangeni.', 'XHO22'),
(30, 26, 6, 'Neentombi zam ezisemaphandleni ziya kubulawa ngekrele; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 26, 7, 'Ngokuba itsho iNkosi uYehova ukuthi, Yabona, iTire ndiyizisa uNebhukadenetsare ukumkani waseBhabheli, evela entla, ukumkani wookumkani, enamahashe, neenqwelo zokulwa, nabamahashe, namaqela abantu, nabantu abaninzi.', 'XHO22'),
(30, 26, 8, 'Iintombi zakho ezisemaphandleni uya kuzibulala ngekrele; uya kukwakhela inqaba, ayiqinise intaba, aphakamise ingweletshetshe.', 'XHO22'),
(30, 26, 9, 'Iinqwelo zokulwa uya kubeka phezu kweendonga zakho, azidilize iinqaba zakho ezinde ngeentsimbi zakhe;', 'XHO22'),
(30, 26, 10, 'Ngenxa yobuninzi bamahashe akhe, uthuli lwawo luya kukugubungela; Iindonga zakho ziya kunyikima ngesandi sabakhweli-mahashe, nesevili, neseenqwelo zokulwa, ekungeneni kwabo emasangweni akho, njengokuba abantu bebengena emzini wobubi.', 'XHO22'),
(30, 26, 11, 'Ngamanqina amahashe akhe uya kuzigqusha zonke izitrato zakho; uya kubabulala abantu bakho ngekrele, namalengalenga akho awe emhlabeni.', 'XHO22'),
(30, 26, 12, 'Baya kubuphanga ubutyebi bakho baze bathengise impahla yakho njengento ephangiweyo, bazidilize neendonga zakho nezindlu zakho ezintle baya kuzidiliza, eyakho nemithi yakho nothuli lwakho baya kuyigibisela emanzini.', 'XHO22'),
(30, 26, 13, 'Ndoyiphelisa ingxokozelo yeengoma zakho; nesandi samakrele akho asisayi kuba saviwa.', 'XHO22'),
(30, 26, 14, 'Ndiya kukwenza intswahla yeliwa, ube yindawo yokwaneka iminatha; akuyi kuba sakhiwa, ngokuba mna Yehova ndithethile; itsho iNkosi uYehova.', 'XHO22'),
(30, 26, 15, '“Itsho iNkosi enguMongami uYehova kwiTire ukuthi: “Iziqithi aziyi kunyikima na xa sivakala isandi sokuwa kwakho, nasekukhaleni kwababuleweyo, nasekubulaweni kwabantu kuwe?', 'XHO22'),
(30, 26, 16, 'Ziya kuhla zonke izikhulu zaselwandle ezitroneni zazo, zibeke emhlabeni iingubo zazo, zikhulule iingubo zazo ezilukiweyo; baya kwambatha ukungcangcazela; baya kuhlala phantsi bengcangcazela umzuzu ngamnye kwaye beme besoyika.', 'XHO22'),
(30, 26, 17, 'Baya kukubethela uhadi, bathi kuwe, Hayi, ukuphanziswa kwakho, wena bekukho oomatiloshe, wena mzi wodumo, ubunamandla ngolwandle, wena nabemi bawo, aba baqhiphula umbilini bonke bephela!', 'XHO22'),
(30, 26, 18, 'Ziyanyikima iziqithi ngomhla wokuwa kwakho; ewe, iziqithi eziselwandle ziya kushukuma xa uhamba.', 'XHO22'),
(30, 26, 19, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Ukuba ndiya kukwenza ube sisixeko esiphanzileyo, njengezixeko ezingenabantu; ekuwiseni kwam unogumbe phezu kwenu, namanzi amaninzi phezu kwenu;', 'XHO22'),
(30, 26, 20, 'ukuba ndithe ndakuhlisa nabahle baya emhadini, kwanabantu bamandulo, ndakuhlisela emihadini yehlabathi, ezindaweni ezingezizo zamandulo, nabehla baya emhadini emhadini, ukuba ungabi nabemi; kwaye ndiya kugcoba kwihlabathi labaphilayo;', 'XHO22'),
(30, 26, 21, 'Ndiya kwenza into eyoyikekayo kuwe, ungabi sabakho; nokuba uthe wafunwa, awusayi kufunyanwa, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 27, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 27, 2, 'Kaloku, nyana womntu, yibethelele isimbonono iTire;', 'XHO22'),
(30, 27, 3, 'Ithi kwiTire, wena uhleli ngaselwandle, wena mrhwebi wezizwe zeziqithi ezininzi, itsho iNkosi uYehova ukuthi, Yabona, ndihleli elwandle, kwanawe, kungabikho ukusindisayo, kuthabathele khona ikrele; Uthe wena, Tire, Bubuhle obugqibeleleyo.', 'XHO22'),
(30, 27, 4, 'Imida yakho isesazulwini solwandle, Ubuhle bakho bupheleliswe ngabakhi bakho.', 'XHO22'),
(30, 27, 5, 'Bazenza zonke iiplanga zeenqanawa zakho ngemisipres yaseSenire; bathabatha imisedare yaseLebhanon ukuba banibale.', 'XHO22'),
(30, 27, 6, 'Iintsika zakho bazenza ngemioki yaseBhashan; Abaninzi baseAsiriya benza iibhedi zakho ngeempondo zeendlovu, eziqithini zamaKiti.', 'XHO22'),
(30, 27, 7, 'Ilinen ecikizekileyo, ephothiweyo yaseYiputa yaba iyiluko eyalukwayo, yaba yiluko lwakho; umbala ozuba nomfusa weziqithi zika-Elisa zezona zinto zikunxibisayo.', 'XHO22'),
(30, 27, 8, 'Abemi baseTsidon neArvadi baye baba ngoomateloshe bakho; Izilumko zakho, Tire, zibe nawe, zaba ngoomatiloshe bakho.', 'XHO22'),
(30, 27, 9, 'Amadoda amakhulu aseGebhali nezilumko zakhe zazinawe, emagumbini enu okuhlala; yonke imikhombe yolwandle yayiyeyakho, noomateloshe bayo, ukuba bahluthe ishishini lakho.', 'XHO22'),
(30, 27, 10, 'AmaPersi, namaLudi, namaPuti ebesemkhosini wakho, engamadoda akho okulwa; baxhoma phezu kwakho ingweletshetshe nesigcina-ntloko; Bazisa uzuko lwakho.', 'XHO22'),
(30, 27, 11, 'Amadoda aseArvade nomkhosi wakho wamajoni ayejikeleze iindonga zakho, namaGamade ayekwiinqaba zakho ezinde; baxhoma iingweletshetshe zabo ezindongeni zakho zonke; Ubuhle bakho bugqibelele.', 'XHO22'),
(30, 27, 12, 'iTarshishe yaba ngumrhwebisana wakho ngenxa yobuninzi bobuncwane obuninzi; baberhweba ngesilivere, nangesinyithi, nangenkcenkce, nangelothe.', 'XHO22'),
(30, 27, 13, 'OoYafane noTubhali noMesheki baye bengabarhwebeli bakho;', 'XHO22'),
(30, 27, 14, 'Usapho lakwaTogarma lwaludla ngokurhweba ngeengxowa zakho ngamahashe, abakhweli neenqwelo-mafutha.', 'XHO22'),
(30, 27, 15, 'Oonyana bakaDedan babengabarhwebi nawe; Iziqithi ezininzi bezingumsebenzi wesandla sakho; bakuzisela iimpondo zeendlovu nobhedu njengesipho.', 'XHO22'),
(30, 27, 16, 'Ama-Aram ayerhwebisana nawe ngenxa yobuninzi bemisebenzi yakho owayenzayo; bathenga kuwe iiemeraldi, nezimfusa, nezilukiweyo, nelinen ecikizekileyo, nekorale, neagati.', 'XHO22'),
(30, 27, 17, 'ElakwaYuda nelizwe lakwaSirayeli lalirhwebelana nawe; Baberhweba ngengqolowa yaseMiniti nasePanag, nobusi, nangeoli, nangebhalsam.', 'XHO22'),
(30, 27, 18, 'iDamasko yarhwebelana nawe, ngenxa yemisebenzi yakho emininzi, ngenxa yentabalala yabo bonke ubutyebi; kwiwayini yaseHelbone kunye noboya obumhlophe.', 'XHO22'),
(30, 27, 19, 'OoDan noYavan, ababehamba babuya bazakha ezintlanganisweni zakho; bekukho intsimbi ekhandiweyo, ikasiya nomhlwa;', 'XHO22'),
(30, 27, 20, 'uDedane uberhwebelana nawe ngeengubo zenqwelo ezinqabileyo.', 'XHO22'),
(30, 27, 21, 'Elakwa-Arabhi nezikhulu zonke zakwaKedare zakukhonza ngamatakane eegusha neenkunzi zeegusha neenciniba; ngokuba babengabarhwebeli bakho.', 'XHO22'),
(30, 27, 22, 'Abarhwebi bakwaShebha nabakwaRama babengabarhwebeli bakho;', 'XHO22'),
(30, 27, 23, 'iHaran neKane ne-Eden, abarhwebi bakwaShebha, iAsiriya nabaseKilemade, babengabarhwebi nawe;', 'XHO22'),
(30, 27, 24, 'babengabarhwebeli bakho ngeengubo ezirhwexayo, ezilukiweyo, nangeengxowa zamalaphu amahle, ziqhinwe ngamaqamelo emisedare, ekurhwebeni kwakho.', 'XHO22'),
(30, 27, 25, 'Iinqanawa zaseTarshishe zazicula ngawe kwindawo yentengiso yakho, yaye wawuzele luzuko nozuko olukhulu esazulwini solwandle.', 'XHO22'),
(30, 27, 26, 'Unyawo lwakho lukusa emanzini amakhulu, ulophu lwasempumalanga lwakulaphula elwandle.', 'XHO22'),
(30, 27, 27, 'Ubutyebi bakho nempahla yakho ethengiswayo, iimpahla zakho ezithengiswayo, oomatiloshe bakho noomatiloshe bakho, oovimba bakho nabarhwebi beempahla zakho ezithengiswayo nawo onke amadoda akho emfazwe akunye nawe nawo wonke umkhosi wakho naphakathi kwakho uya kuwela elwandle ngemini yokutshabalala kwakho.', 'XHO22'),
(30, 27, 28, 'Ngenxa yesandi sokuduma kwabaqhubi bakho, amadlelo ayanyikima.', 'XHO22'),
(30, 27, 29, 'Bonke ababheqi, oomatiloshe, nabo bonke oomatiloshe elwandle, baya kuhla enqanaweni, beme emhlabeni;', 'XHO22'),
(30, 27, 30, 'Livakale ilizwi labo ngani, bambambazele kakrakra, bazigalele ngothuli entloko, bazigalele ngothuthu;', 'XHO22'),
(30, 27, 31, 'Baya kuhluba umzimba wakho ngokupheleleyo, babhinqe ezirhwexayo, bakumbambazelele ngentliziyo ebuhlungu, batsho ngesikhalo esikrakra.', 'XHO22'),
(30, 27, 32, 'Yaye ekuzileni kwabo baya kukuhlabela isimbonono baze benze isijwili ngenxa yakho besithi: ‘Sisiphi na isixeko esinjengeTire, esinjengaleyo yomile esazulwini solwandle?', 'XHO22'),
(30, 27, 33, 'Ekuphumeni kwakho ubutyebi bakho elwandle, wazala ngabantu abaninzi; Ngobuninzi bobuncwane bakho neshishini lakho ubenze batyebisa ookumkani behlabathi.', 'XHO22'),
(30, 27, 34, 'Imini iya kugaleleka elwandle ezantsi kwamanzi, ishishini lakho nabantu bakho bonke bawele kuwe.', 'XHO22'),
(30, 27, 35, 'Bonke abemi beziqithi baya kumangaliswa nguwe, nookumkani bazo babe namanwele kunene, ubuso babo bunkwantye.', 'XHO22'),
(30, 27, 36, 'Abarhwebi basentlangeni baya kukubethela umlozi; uya kuba yinto eyoyikekayo, ungabi sabakho.', 'XHO22'),
(30, 28, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 28, 2, 'Nyana womntu, yithi kwinganga yeTire, Itsho iNkosi uYehova ukuthi, Ngenxa enokuba intliziyo yakho iphakamile, usithi, NdinguThixo, ndihleli etroneni kaThixo embilinini yolwandle; nangona ungumntu ungengoThixo, nokuba ubeka intliziyo yakho njengentliziyo kaThixo.', 'XHO22'),
(30, 28, 3, 'Yabona, ulumke ngaphezu koDaniyeli; akukho mfihlo banokuyifihla kuwe.', 'XHO22'),
(30, 28, 4, 'Ngobulumko bakho nangengqondo yakho uzenzele ubutyebi, wafumana igolide nesilivere ebuncwaneni bakho.', 'XHO22'),
(30, 28, 5, 'Ngobuninzi bobulumko bakho ubandisile ubuhandiba bakho, nangezenzo zakho, yaphakama intliziyo yakho ngobutyebi bakho.', 'XHO22'),
(30, 28, 6, 'Ngako oko itsho iNkosi uYehova ukuthi, Ekubeni uyenze yanjengentliziyo yoThixo intliziyo yakho;', 'XHO22'),
(30, 28, 7, 'Ngako oko, uyabona, ndikuzisela iintlanga ezinamandla, ezintlangeni, ziphakamise imikhonto yazo ekuzukiseni ubulumko bakho, ziluhlambele uzuko lwakho.', 'XHO22'),
(30, 28, 8, 'Baya kukuhlisela emhadini, ufe ukufa kwababuleweyo elwandle.', 'XHO22'),
(30, 28, 9, 'Usatsho ukuthi kulowo ukubulaliyo, NdinguThixo? uya kuba yindoda, ungabi nguThixo esandleni salowo ukubulaliyo.', 'XHO22'),
(30, 28, 10, 'Uya kufa ukufa kwabangendawo ngesandla seentlanga; ngokuba nditshilo, itsho iNkosi uYehova.', 'XHO22'),
(30, 28, 11, 'Kwabonakala ilizwi likaYehova kum lisithi:', 'XHO22'),
(30, 28, 12, 'Nyana womntu, kubekele indlebe ukuhlahlamba kokumkani waseTire, uthi kuye, Itsho iNkosi uYehova ukuthi, Yabona, mna Yehova, ndikuvile; Uzalisa isilumko nobuhle obugqibeleleyo.', 'XHO22'),
(30, 28, 13, 'Ubuse-Eden emyezweni kaThixo; Onke amatye anqabileyo ayeyingubo yakho: isardiyo, netopazi, nedayimani, namalahle, nebherilo, neyaspisi, nesafire, nesmarado, nekalike, negolide; ngawe ngemini ophumelele ngayo.', 'XHO22'),
(30, 28, 14, 'Wena uyikerubhi ethanjisiweyo, engumkhuseli; Ndanibeka ngolu hlobo: usentabeni engcwele kaThixo; wawuhamba usihla phakathi kwamatye anomlilo.', 'XHO22'),
(30, 28, 15, 'Ulilungisa ezindleleni zakho kususela kokoyiswa, kwada kwafumaneka ubungendawo kuwe;', 'XHO22'),
(30, 28, 16, 'Ngobuninzi bomsebenzi wakho uzele kukugonyamela, wona ke; ngako oko, ndiya kuniphosa entabeni kaThixo, nina bangendawo; Ndiya kukutshabalalisa, wena kerubhi eliyinqaba, phakathi kwamatye anomlilo.', 'XHO22'),
(30, 28, 17, 'Intliziyo yakho iphakamile ngenxa yobuhle bakho, wabuchitha ubulumko bakho kunye nozuko lwakho; Ndiya kukuwisa, ndikubeke phambi kookumkani, ukuze bakubone.', 'XHO22'),
(30, 28, 18, 'Uzihlambele iingcwele zakho ngobuninzi bezono zakho, ngobubi beshishini lakho; ndikhuphe umlilo phakathi kwakho, ukudle, ndikwenze ube luthuthu emhlabeni phambi kwamehlo abo bonke abakubonayo.', 'XHO22'),
(30, 28, 19, 'Bonke abakwaziyo phakathi kweentlanga baya kumangaliswa nguwe; uya kuba yinto eyoyikekayo, ungabi sabakho.', 'XHO22'),
(30, 28, 20, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 28, 21, 'Nyana womntu, bhekisa ubuso bakho eTsidon, uprofete kuyo;', 'XHO22'),
(30, 28, 22, 'Wathi, Itsho iNkosi uYehova ukuthi, Itsho iNkosi uYehova; Khangela, ndinengxaki nawe, Sidon! yaye ndiya kuzukiswa phakathi kwenu, bazi ukuba ndinguYehova xa ndisifeza isohlwayo sakhe ndize ndingcwaliswe kuso.', 'XHO22'),
(30, 28, 23, 'Ndiya kuthumela kuwo indyikitya yokufa negazi ezitratweni zawo; yaye abo bawonileyo baya kugwetywa phambi kwakhe ngekrele elikuwo macala onke; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 28, 24, 'Akuyi kuba sabakho luviko libukhali kwindlu kaSirayeli, naluviko lubuhlungu kubo bonke ababavelelayo, ababacekisayo; bazi ukuba ndiyiNkosi uYehova.', 'XHO22'),
(30, 28, 25, 'Itsho iNkosi uYehova ukuthi, Ekubahlanganiseni kwam abantu bendlu kaSirayeli ezintlangeni abaphangalaliswe kuzo, ndoba nemihlali, ndibabone emehlweni eentlanga, bahlale ezweni labo endibanike lona kumkhonzi wam. uYakobi unayo.', 'XHO22'),
(30, 28, 26, 'Baya kuhlala kulo bekholosile, bakhe izindlu, batyale nezidiliya; ewe, baya kuphila bengafihlisi, ekwenzeni kwam izigwebo kubo bonke ababacekisayo ababathabathayo; bazi ukuba ndinguYehova uThixo wabo.', 'XHO22'),
(30, 29, 1, 'Ngomnyaka weshumi, ngenyanga yeshumi, ngolweshumi elinesibini enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 29, 2, 'Nyana womntu, bhekisa ubuso bakho kuFaro ukumkani waseYiputa, uprofete ngaye, nangeYiputa yonke iphela.', 'XHO22'),
(30, 29, 3, 'Thetha, uthi, Utsho uYehova; Yabona, ndinengxaki nawe, Faro ukumkani waseYiputa, inamba enkulu, ilele emilanjeni yayo, yathi, Umlambo wam ngowam, ndizenzele mna.', 'XHO22'),
(30, 29, 4, 'Ndiya kufaka amagwegwe emihlathini yakho, ndizinamathisele eqongeni lakho iintlanzi zemilambo yakho, ndikukhuphe emilanjeni yakho, ndizinamathisele elusu lwakho zonke iintlanzi zemilambo yakho.', 'XHO22'),
(30, 29, 5, 'Ndiya kukulahlela entlango, wena neentlanzi zonke zemijelo yakho; niya kuwa emhlabeni; anihlanganiswanga nina, anihlanganizwanga; ndaninika amarhamncwa asendle neentaka zezulu ukuba ube kukudla kwazo.', 'XHO22'),
(30, 29, 6, 'Yaye bonke abemi baseYiputa baya kwazi ukuba ndinguYehova, ngenxa yokuba wayeyingcongolo kwindlu kaSirayeli.', 'XHO22'),
(30, 29, 7, 'Bakubamba ngesandla, wasuka waphuka, wakrazuka onke amagxa abo; kwaye xa bexhomekeke kuwe, waphuka kwaye wamisa zonke izinqe zabo.', 'XHO22'),
(30, 29, 8, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabona, ndikuzisela umkhonto, ndinqumle kuwe umntu nenkomo;', 'XHO22'),
(30, 29, 9, 'Ilizwe laseYiputa liya kuba yinkangala ephanzileyo; bazi ukuba ndinguYehova, ngenxa enokuba usithi, Umlambo ngowam, ndiwenze mna.', 'XHO22'),
(30, 29, 10, 'Ngoko ke, yabona, ndinxamnye nawe nemijelo yakho, yaye ndiza kulenza ilizwe laseYiputa libe yinkangala ephanzileyo, ukususela kwinqaba yaseSiena ukusa kumda wakwaKushe.', 'XHO22'),
(30, 29, 11, 'Akuyi kucanda lunyawo lwamntu kuyo, akuyi kucanda lunyawo lwenkomo kuyo, ayisayi kwakhiwe iminyaka emashumi mane.', 'XHO22'),
(30, 29, 12, 'Ndiya kulenza kube senkangala ilizwe laseYiputa phakathi kwamazwe abharhileyo, nemizi yalo phakathi kwemizi engamanxuwa ibe ngamanxuwa iminyaka emashumi mane; ndiwachithachithe amaYiputa ezintlangeni, ndiwachithachithe emazweni.', 'XHO22'),
(30, 29, 13, 'Kodwa ithi iNkosi enguMongami uYehova: “Kuza kuthiwa sekudlule iminyaka engamashumi amane ukususela oko ndawaqokelelayo amaYiputa ezintlangeni awayechithachitheke kuzo.', 'XHO22'),
(30, 29, 14, 'Ndiya kubabuyisa abathinjiweyo eYiputa, ndibabuyisele ezweni lasePatrose, ezweni lokuhlala kwabo; kwaye baya kuba norhulumente omncinci apho.', 'XHO22'),
(30, 29, 15, 'Baya kuba ngoorhulumente abaphantsi; aniyi kuba saziphakamisa iintlanga, ngenxa enokuba ndiya kuzinciphisa, ukuze zingabi sazilawula iintlanga.', 'XHO22'),
(30, 29, 16, 'Ayisayi kuba saba yithemba lendlu kaSirayeli, ekukhumbuleni ububi bayo ekuboneleleni kwayo; kodwa baya kwazi ukuba ndiyiNkosi uYehova.', 'XHO22'),
(30, 29, 17, 'Kwathi ngomnyaka wamashumi omabini anesixhenxe, ngenyanga yokuqala, ngolokuqala enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 29, 18, 'Nyana womntu, uNebhukadenetsare ukumkani waseBhabhiloni wawunikela umkhosi wakhe wamajoni inkonzo enkulu nxamnye neTire; kodwa akazange afumane mvuzo, nkqu nomkhosi wakhe waseTire, ngenxa yenkonzo yakhe isetyenziswe ngokuchasene:', 'XHO22'),
(30, 29, 19, 'Ngako oko itsho iNkosi uYehova ukuthi, Yabona, ilizwe laseYiputa ndilinikela kuNebhukadenetsare ukumkani waseBhabheli; liya kuyithimba into eninzi yalo, liphange amaxhoba ayo, liyiphange impahla yayo; kwaye oku kuya kuba ngumvuzo womkhosi wakhe.', 'XHO22'),
(30, 29, 20, 'Ndamnika ilizwe laseYiputa umsebenzi wakhe, ngokuba ubekhonza mna; itsho iNkosi uYehova.', 'XHO22'),
(30, 29, 21, 'Ngaloo mini ndiya kuyikhulisa uphondo lwendlu kaSirayeli, yaye ndiya kukunika ukuvulwa komlomo phakathi kwabo; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 30, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 30, 2, 'Nyana womntu, profeta uthi, Itsho iNkosi uYehova ukuthi, Lilani, yeha ke loo mini!', 'XHO22'),
(30, 30, 3, 'Ngokuba ikufuphi imini, ikufuphi imini kaYehova, yimini yamafu; iya kuba lixesha leentlanga.', 'XHO22'),
(30, 30, 4, 'Umkhonto uya kufika phezu kweYiputa; kuya kubakho iintlungu ezinkulu kwaKushi, ekuyisuseni ababuleweyo eYiputa ingxokolo yayo, zichithwe iziseko zayo;', 'XHO22'),
(30, 30, 5, 'AmaTiyopiya namaLibhiya namaLidiya nabo bonke abantu abaxubene neKubhan namadoda elizwe awenze umnqophiso aya kuwa ngekrele kunye nawo.', 'XHO22'),
(30, 30, 6, 'Utsho uYehova ukuthi, Baya kuwa abaxhasi beYiputa; lihle iqhayiya lamandla akhe; ukusuka enqabeni yaseSiena baya kuwa likrele khona, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 30, 7, 'Woba yinkangala ephanzileyo phakathi kwamazwe abharhileyo, nemizi yawo ibe phakathi kwemizi engamanxuwa;', 'XHO22'),
(30, 30, 8, 'bazi ukuba ndinguYehova, ekufakeni kwam umlilo eYiputa, baphuke bonke abancedi bayo.', 'XHO22'),
(30, 30, 9, 'Ngaloo mini abathunywa bam bohamba ngeenqanawa, ukuze boyikise amaKushi angazange akhathale, azibhijabhije ngokwawo njengakwimini yeYiputa, kuba, khangela!', 'XHO22'),
(30, 30, 10, 'Itsho iNkosi uYehova ukuthi; Ndiya kuyitshabalalisa isihlwele saseYiputa ngesandla sikaNebhukadenetsare, ukumkani waseBhabhiloni.', 'XHO22'),
(30, 30, 11, 'Yena nabantu bakhe, aboyikeka phakathi kwezizwe, baya kuziswa ukuba balitshabalalise ihlabathi; baya kuwarhola amakrele abo eYiputa, balizalise ilizwe ngababuleweyo.', 'XHO22'),
(30, 30, 12, 'Ndoyomisa imijelo yoMnayile, ndithengise ngelizwe ezandleni zabangendawo; yaye ndiya kulenza kube senkangala ilizwe nenzaliseko yalo ngesandla sabasemzini; Mna Yehova ndithethile.', 'XHO22'),
(30, 30, 13, 'Itsho iNkosi uYehova ukuthi, Ndiya kuzitshabalalisa izigodo ezizizithixo, ndizisuse eNofe izigodo zazo; kwaye akuyi kuba sabakho nkosana ezweni laseYiputa, yaye ngokuqinisekileyo ndiya kubangela uloyiko ezweni laseYiputa.', 'XHO22'),
(30, 30, 14, 'Ndiya kuyenza iPatrosi ibe yintlango ndize ndiyitshise ngomlilo iTsowane ndize ndiphumeze isohlwayo esinguNo;', 'XHO22'),
(30, 30, 15, 'ndowuthulula ubushushu bam phezu kweSin, inqaba yaseYiputa; kwaye ndiya kwenza uninzi lwe-No.', 'XHO22'),
(30, 30, 16, 'Ndiya kufaka umlilo eYiputa, izibilini zesono zibe krakra, uNowa aqwengwe, iNofu ibe sentlungwini yonke imihla.', 'XHO22'),
(30, 30, 17, 'Amadodana aseAfene nasePibheshete aya kuwa likrele, yona ke loo mizi ithinjwe.', 'XHO22'),
(30, 30, 18, 'Imini iya kuba mnyama eTehafene, ekuzaphuleni kwam idyokhwe yaseYiputa; nokukhanya kwamandla akhe kuya kuphela kuye, ilifu liya kumgubungela, iintombi zakhe zisiwe ekuthinjweni.', 'XHO22'),
(30, 30, 19, 'Ndiya kuwabetha amaYiputa, azi ukuba ndinguYehova.', 'XHO22'),
(30, 30, 20, 'Kwathi ngomnyaka weshumi elinamnye, ngenyanga yokuqala, ngolwesixhenxe enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 30, 21, 'Nyana womntu, ndasaphula isandla sikaFaro ukumkani waseYiputa; uyabona, ayiyi kubotshwa ukuba iphole, ukuba ibotshwe kusongwa, igawulwe ukuba ibambe umkhonto.', 'XHO22'),
(30, 30, 22, 'Ngako oko, itsho iNkosi uYehova ukuthi, Yabona, ndinendawo noFaro ukumkani waseYiputa nekrele ndiya kuliwisa esandleni sakhe.', 'XHO22'),
(30, 30, 23, 'Ndowaphangalalisela amaYiputa ezintlangeni, ndiwachithachithe emazweni;', 'XHO22'),
(30, 30, 24, 'Ndiya kuzomeleza izandla zokumkani waseBhabheli, ndibeke ikrele lam esandleni sakhe; kodwa ndiya kuzaphula izandla zikaFaro, aze ancwine phambi kwakhe ngokugcuma komntu owenzakeleyo ofayo.', 'XHO22'),
(30, 30, 25, 'Ndiya kuzomeleza izandla zokumkani waseBhabheli, ziwe izandla zikaFaro; bazi ukuba ndinguYehova ekunikeleni kwam ikrele lam esandleni sokumkani waseBhabhiloni, lize lolulele kwilizwe laseYiputa.', 'XHO22'),
(30, 30, 26, 'Ndowaphangalalisela amaYiputa ezintlangeni, ndiwachithachithe emazweni; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 31, 1, 'Kwathi ngomnyaka weshumi elinamnye, ngenyanga yesithathu, ngolokuqala enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 31, 2, 'Nyana womntu, thetha kuFaro, ukumkani waseYiputa, nengxokolo yakhe; Ufana nabani ngokobukhulu bakho?', 'XHO22'),
(30, 31, 3, 'Yabona, uAsiriya ebengumsedare waseLebhanon, omasebe mahle, eyingubo yokwaleka, emide; umphezulu wawo waba phakathi kwamasebe ashinyeneyo.', 'XHO22'),
(30, 31, 4, 'Amphakamisile amanzi, umkhukula wamphakamisa, imilambo yakhe yahamba ngeenxa zonke elutyalweni lwakhe, nemilambo yakhe yahamba yonke imithi yelizwe.', 'XHO22'),
(30, 31, 5, 'Ngenxa yoko ukuphakama kwawo kwaba phezu kwemithi yonke yelizwe, amasebe awo anda, aba made, ngenxa yamanzi amaninzi ekuhlumeni kwawo.', 'XHO22'),
(30, 31, 6, 'Zonke iintaka zezulu zakha indlu yazo emasebeni akhe, zonke izilwanyana zelizwe zazalela phantsi kwamasebe awo, zaza zonke iintlanga ezinkulu zahlala emthunzini wawo.', 'XHO22'),
(30, 31, 7, 'Ubumhle ubukhulu bawo, ngobude bamasebe awo, kuba ingcambu yawo ibikufuphi namanzi amaninzi.', 'XHO22'),
(30, 31, 8, 'Imisedare eyayisemyezweni kaThixo yayingenakuwufihla; yaye kwakungekho mthi emyezweni kaThixo owawufana nawo ngobuhle bawo.', 'XHO22'),
(30, 31, 9, 'Ndawenza mhle ngobuninzi bamasebe awo, kangangokuba yonke imithi yase-Eden eyayisentsimini yoTHIXO oyinyaniso yaba nomona ngayo.', 'XHO22'),
(30, 31, 10, 'Ngako oko itsho iNkosi uYehova ukuthi, Ngenxa enokuba eziphakamisile, wayiphakamisa intloko yakhe phakathi kweengqimba zamafu, yazinyusa intliziyo yakhe ekuphakameni kwakhe;', 'XHO22'),
(30, 31, 11, 'Kungenxa yoko le nto ndamnikela esandleni sengxokozelo yeentlanga; inene, uya kuqhubana naye; Ndamgxotha ngenxa yobungendawo bakhe.', 'XHO22'),
(30, 31, 12, 'Bamtshabalalisa ke abasemzini, abakhohlakeleyo ezintlangeni, bamshiya; kuzo zonke iintaba nezihlambo amasebe akhe awela, amasebe akhe aqhaqheka kuyo yonke imilambo yelizwe; yaye bonke abantu behlabathi behla emthunzini wakhe baza bamfulathela.', 'XHO22'),
(30, 31, 13, 'Ekutshabalaleni kwawo, ziya kuhlala zonke iintaka zezulu, zibe emasebeni awo zonke izilo.', 'XHO22'),
(30, 31, 14, 'Ukuze kungabikho namnye umthi wamanzi owuphakamisayo ube phezulu, nencam yawo ingakhuli phakathi kwamasebe ashinyeneyo, nokuba yonke imithi yawo ingabi phezu kwawo wonke umntu oseza amanzi; ngokuba bonke bephela benikelwe ekufeni, kwelasezantsi ilizwe, phakathi koonyana babantu, nabahle baya kwelabafileyo.', 'XHO22'),
(30, 31, 15, 'Itsho iNkosi uYehova ukuthi; Ekuhleni kwakhe kwelabafileyo, ndenza ubumnyama; Ndayibangela iLebhanon izilile ngenxa yakhe, yaye yonke imithi yelizwe yadandatheka ngenxa yakhe.', 'XHO22'),
(30, 31, 16, 'Ngesithonga sokuwa kwakhe ndazingcangcazelisa iintlanga, ekumhliseleni kwam ukuya kwelabafileyo, nabahle baya emhadini; nemithi yase-Eden, eyona nto ingcwele, neyona intle yaseLebhanon, yonke esela amanzi, iya kuhluthwa kwelasezantsi ilizwe.', 'XHO22'),
(30, 31, 17, 'Nabo behla naye ukuya kwelabafileyo, phakathi kwababuleweyo ngekrele; nabazizisweli zakhe, ababehleli emthunzini wakhe phakathi kweentlanga.', 'XHO22'),
(30, 31, 18, 'Ulingana nabani na ngozuko nangobukhulu phakathi kwemithi yase-Eden? kodwa uya kuphoswa phantsi nemithi yase-Eden kweyona nxalenye isezantsi yomhlaba; phakathi kwabangalukileyo uya kulala nababuleweyo ngekrele. NguFaro ke lowo, nengxokolo yakhe yonke; itsho iNkosi uYehova.', 'XHO22'),
(30, 32, 1, 'Kwathi ngomnyaka weshumi elinesibini, ngenyanga yeshumi elinesibini, ngolokuqala enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 32, 2, 'Nyana womntu, menzele isimbonono uFaro ukumkani waseYiputa, uthi kuye, Unjengehobe leentlanga, unjengentlanzi enkulu elwandle; waphuma nemijelo yakho, namanzi ngeenyawo zakho, wayihlambela imijelo yabo.', 'XHO22'),
(30, 32, 3, 'Itsho iNkosi uYehova ukuthi; Ngako oko ndiya kuzizisa phezu kwakho ndinengxokolo yeentlanga ezininzi; baya kunitsala ngomnatha wam.', 'XHO22'),
(30, 32, 4, 'Ndiya kukulahlela ezweni, ndikujule emhlabeni, zihlale phezu kwakho zonke iintaka zezulu, ndiwazalise amarhamncwa elizwe ngawe.', 'XHO22'),
(30, 32, 5, 'Ndiya kuyibeka phezu kweentaba inyama yakho, ndizalise iintlambo ngobude bakho.', 'XHO22'),
(30, 32, 6, 'Ndiya kulinyakamisa ilizwe odada kulo ngegazi lakho, linyuke lize ezintabeni; imilambo iya kuzala ngawe.', 'XHO22'),
(30, 32, 7, 'Ndiya kulisibekela izulu ekunikhupheni kwam, nizenze mnyama iinkwenkwezi zaso; ndiya kulisibekela ilanga ngamafu, nenyanga ingakhanyisi ngokukhanya kwayo.', 'XHO22'),
(30, 32, 8, 'Ndiza kuzenza zibe mnyama zonke izibane eziqaqambileyo esibhakabhakeni ngenxa yakho nobumnyama phezu kwelizwe lakho,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 32, 9, 'Ndiza kuzikhathaza iintliziyo zeentlanga ezininzi ekuziseni kwam intshabalalo phakathi kweentlanga kumazwe obungawazi.', 'XHO22'),
(30, 32, 10, 'Ndiya kuzimangalisa ngawe iintlanga ezininzi, babe nokoyika kunene ookumkani bazo, ekulinyuseni kwam ikrele lam phambi kwabo; baya kugubha ngamaxesha onke, elowo ngenxa yomphefumlo wakhe, ngemini yokuwa kwakho.', 'XHO22'),
(30, 32, 11, 'Kuba itsho iNkosi enguMongami uYehova ukuthi, ‘Umkhonto wokumkani waseBhabhiloni uya kukuhlasela.', 'XHO22'),
(30, 32, 12, 'Ndiya kusiwisa isihlwele sakho ngamakrele abo banamandla, abacinezeli beentlanga, bephela;', 'XHO22'),
(30, 32, 13, 'Kwaye yonke imfuyo yakhe ndiya kuyitshabalalisa emanzini amaninzi; nonyawo lomntu aluyi kuba sabaphazamisa, namanqina eenkomo akasayi kuba sabaphazamisa.', 'XHO22'),
(30, 32, 14, 'Ndiya kuwenza abe nzulu amanzi ayo, nemilambo yayo iqukuqele njengeoli,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 32, 15, 'Xa ndilenza ilizwe laseYiputa libe yintlango ndize ndithabathe ilizwe elizeleyo, ukuba ndiya kuboyisa bonke abemi balo, baya kwazi ke ukuba ndinguYehova.', 'XHO22'),
(30, 32, 16, 'Siso esi isikhalo abaya kumkhalela ngaso: Iintombi zeentlanga ziya kumkhalela; baya kuyimbambazelela iYiputa nengxokolo yayo yonke; itsho iNkosi uYehova.', 'XHO22'),
(30, 32, 17, 'Ngomnyaka weshumi elinesibini, ngolweshumi elinesihlanu enyangeni leyo, kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 32, 18, 'Nyana womntu, yililela ingxokolo yeYiputa, uyizise kwelabafileyo, yona neentombi zeentlanga, kwese eziphelweni zehlabathi, nabahla baye emhadini.', 'XHO22'),
(30, 32, 19, 'Ngubani oyena umhle ngaphezu kwakhe? yihla, uye kulala nabahlobo bakho.', 'XHO22'),
(30, 32, 20, 'Baya kuwa phakathi kwababuleweyo ngekrele; wanikwa umkhonto; yiqweqwediseni, nengxokolo yayo yonke.', 'XHO22'),
(30, 32, 21, 'Ziya kuthetha kuye iinjengele zamagorha eHadesi, ndawonye nabancedi bakhe, bathi, balele, balele, abangalukileyo, babulewe ngekrele.', 'XHO22'),
(30, 32, 22, 'Ukhona uAsiriya nengxokolo yakhe yonke; Angaphakathi kuye amangcwaba akhe; mabawe likrele bonke ababuleweyo;', 'XHO22'),
(30, 32, 23, 'abekwe emacaleni omhadi, namangcwaba ayo amaninzi phakathi; bonke ababuleweyo, abawe likrele, abalothusayo ilizwe labahleliyo.', 'XHO22'),
(30, 32, 24, 'Ukhona uElam, nengxokolo yakhe yonke engeenxa zonke engcwabeni lakhe, bonke ababuleweyo, abawa ngekrele, abehlayo waya ezindaweni ezisezantsi bengalukile, ababesothusa ilizwe labahleliyo; noko ke, babeneentloni ngabo kunye nabo behla beza kubo.', 'XHO22'),
(30, 32, 25, 'Bamenzela isilili phakathi kwababuleweyo, yena nebandla lakhe lonke; Azele nguye amangcwaba akhe: bonke abangalukileyo, bebulewe ngekrele; nakubeni uloyiko lwabo lwalusehlabathini labaphilayo, ukanti babenentloni ngabo nabo babehlela ezantsi esihogweni; Ubekwe phakathi kwabo boyisiweyo.', 'XHO22'),
(30, 32, 26, 'Ukhona uMesheki, noTubhali, nengxokolo yakhe yonke; baphahlwe ngamangcwaba akhe: bonke bengalukile, bebulewe ngekrele; ngokuba besothuse ilizwe labahleliyo.', 'XHO22'),
(30, 32, 27, 'Kwaye abayi kuba nabuhlobo kunye namageza awileyo kwabo bangembanga abo babahlisela emhadini nezixhobo zabo zemfazwe; babeke amakrele abo phantsi kweentloko zabo; ubugwenxa babo buwafikele emathanjeni abo, nakuba besoyikwa ngamagorha ezweni labahleliyo.', 'XHO22'),
(30, 32, 28, 'Ewe, uya kwaphulwa phakathi kwabangalukanga, yaye uya kudibana nababuleweyo ngekrele.', 'XHO22'),
(30, 32, 29, 'Ukhona uEdom, nookumkani bakhe, nabathetheli bakhe bonke, abatshabalalayo emandleni abo ngababuleweyo ngekrele; baya kulala kwabangalukileyo nabehle baye emhadini.', 'XHO22'),
(30, 32, 30, 'Zikhona iinkosana zasentla, zonke ziphela, namaTsidon onke, ezehlileyo zabulawa; ngenxa yokunkwantya kwabo baya kudana ngenxa yamandla abo; yaye balala benganqunyulwanga kunye nabo babulewe ngekrele, yaye bathwele ihlazo labo kunye nabo behlayo baya enkampini.', 'XHO22'),
(30, 32, 31, 'uFaro uya kubabona aze athuthuzeleke ngenxa yesihlwele sakhe sonke, esinguFaro nawo wonke umkhosi wakhe wamajoni owoyiswe ngekrele,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 32, 32, 'Kuba ndibangele ukuba koyikwe kum kwilizwe labaphilayo, yaye uya kulala phakathi kwabangalukanga kwanabo babulewe ngekrele, uFaro nesihlwele sakhe sonke,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 33, 1, 'Kwaphinda kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 33, 2, 'Nyana womntu, thetha koonyana babantu bakowenu, uthi kubo, Ukuba ndithe ndalizisela ikrele elo lizwe, xa abantu belo lizwe bathabatha enye indoda emideni yabo, bammisela ukuba ibagcine;', 'XHO22'),
(30, 33, 3, 'Yalibona ikrele lilifikela ilizwe elo, yavuthela isigodlo, yabalumkisa abantu;', 'XHO22'),
(30, 33, 4, 'bonke abathe baliva isandi sesigodlo, akalumkisa; ukuba lithe lafika ikrele, lamthabatha, igazi lakhe loba phezu kwentloko yakhe.', 'XHO22'),
(30, 33, 5, 'Wasiva isandi sesigodlo, akalumkisa; igazi lakhe malibe phezu kwakhe. Kodwa lowo umlumkisayo uya kuwusindisa umphefumlo wakhe.', 'XHO22'),
(30, 33, 6, 'Ukuba ke umlindi uthe walibona ikrele lisiza, akalivuthela isigodlo, abantu abalumkiswa; ukuba lithe lafika ikrele, labulala omnye kubo, urhoxile ebubini bakhe; ke igazi lakhe ndolibiza esandleni somboniseli lowo.', 'XHO22'),
(30, 33, 7, 'Wena ke, nyana womntu, ndikwenze umboniseli kwindlu kaSirayeli; ngoko ke umele uve ilizwi eliphuma emlonyeni wam kwaye ubalumkise ngokuphuma kum.', 'XHO22'),
(30, 33, 8, 'Ukuba ndithe kongendawo, Wena ungendawo, inene, uya kufa; ukuba uthe akwamlumkisa ongendawo ngendlela yakhe, wofa ongendawo ngobungendawo bakhe; ke igazi lakhe ndolibiza esandleni sakho.', 'XHO22'),
(30, 33, 9, 'Wena ke, xa uthe wamvuselela ongendawo ngendlela yakhe, ukuba emke kuyo; ukuba uthe akabuya endleleni yakhe, wofa ngobugwenxa bakhe; kodwa uwuhlangule umphefumlo wakho.', 'XHO22'),
(30, 33, 10, 'Ngako oko, nyana womntu, thetha kwindlu kaSirayeli; Nithetha ngolu hlobo, nithi, Xa izikreqo zethu nezono zethu ziphezu kwethu, yaye sibunile ngenxa yazo, singathini na ukuphila?', 'XHO22'),
(30, 33, 11, 'Yithi kubo, Ndihleli nje, itsho iNkosi uYehova, andiyi kukuvuyela ukufa kongendawo; ukuze ongendawo aguquke endleleni yakhe nasebomini bakhe. Yini na ukuba nife, ndlu kaSirayeli?', 'XHO22'),
(30, 33, 12, 'Ngako oko, nyana womntu, yithi koonyana babantu bakowenu, Ubulungisa belungisa abuyi kulisindisa ngemini yesono salo; ngenxa yobubi bakhe; yaye ilungisa aliyi kukwazi ukuphila ubulungisa balo ngemini yokona kwalo.', 'XHO22'),
(30, 33, 13, 'Ukuba ndithe kwilungisa, lophila; ukuba ke uthe akholosa ngobulungisa bakhe, enze ngobugqwetha, abuyi kukhunjulwa ubulungisa bakhe; kodwa ngenxa yobugwenxa bakhe abenzileyo, uya kufa ngabo.', 'XHO22'),
(30, 33, 14, 'Kananjalo anditsho ukuthi kongendawo, Inene, uya kufa; xa athe waguquka esonweni sakhe, wenza ubulungisa nobulungisa;', 'XHO22'),
(30, 33, 15, 'Ukuba uthe waba mbi, uya kukubuyisa isibambiso, abuyise into ephangiweyo, ahamba ngemimiselo yakhe, akenzi ububi; inene, uya kuphila, akayi kufa.', 'XHO22'),
(30, 33, 16, 'Zonke izono zakhe awona ngazo aziyi kukhunjulelwa kuye; inene, uya kuphila.', 'XHO22'),
(30, 33, 17, 'Bathi ke oonyana babantu bakowenu, Indlela kaYehova ayilungelelene, nendlela yabo ayilungelelene.', 'XHO22'),
(30, 33, 18, 'Xa umntu olilungisa eguquka ebulungiseni bakhe, enze okungekho bulungisa, uya kufa ngenxa yoko.', 'XHO22'),
(30, 33, 19, 'Kodwa ukuba ongendawo uyabuya kubungendawo bakhe, enze ubulungisa nobulungisa, uya kuphila ngabo.', 'XHO22'),
(30, 33, 20, 'Ke nina nithi, Indlela kaYehova ayilungelelene. Nina ke, ndlu kaSirayeli, ndiya kunigweba elowo ngokweendlela zakhe;', 'XHO22'),
(30, 33, 21, 'Ngomnyaka weshumi elinesibini wokuthinjwa kwethu, ngenyanga yeshumi, ngolwesihlanu enyangeni leyo, kwafika kum osindileyo ovela eYerusalem, wathi, Umzi ubulewe.', 'XHO22'),
(30, 33, 22, 'Yaye isandla sikaYehova saba phezu kwam ngokuhlwa, ngaphambi kokuba bafike abakhululekileyo; Wawuvula umlomo wam, wada weza kum kusasa; wavuleka umlomo wam, ndangabi saba sisidenge.', 'XHO22'),
(30, 33, 23, 'Kwabonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 33, 24, 'Nyana womntu, bathetha abo babehleli emanxuweni elizwe lakwaSirayeli, besithi, uAbraham wayeyedwa, walidla ilifa elimiweyo; kodwa thina sibaninzi; linikwe thina ilizwe ukuba libe lilifa.', 'XHO22'),
(30, 33, 25, 'Ngako oko uthi kubo, Itsho iNkosi uYehova ukuthi, Nidla igazi, amehlo enu niwaphakamisele ezigodweni zenu, niphalaze igazi; kwaye uya kulidla ilifa ilizwe?', 'XHO22'),
(30, 33, 26, 'Nimi phezu kwekrele lenu, nenza izinto ezicekisekayo, nimenza inqambi umfazi ngamnye; kwaye uya kulidla ilifa ilizwe?', 'XHO22'),
(30, 33, 27, 'Yithi kubo, Itsho iNkosi uYehova; Ndihleli nje, amanxuwa aya kuwa likrele, nosendle ndimnikele ukuba adle amarhamncwa, nabasemboniselweni nasemiqolombeni bambulale isifo sendyikitya yesifo esisulelayo.', 'XHO22'),
(30, 33, 28, 'Ndiya kulenza kube senkangala ilizwe, buphele ubungangamsha bamandla alo; neentaba zakwaSirayeli ziya kuba yintlango, ukuze kungabikho bani udlulayo kuzo;', 'XHO22'),
(30, 33, 29, 'bazi ukuba ndinguYehova, ndakulenza kube senkangala ilizwe, ngenxa yamasikizi onke abawenzileyo.', 'XHO22'),
(30, 33, 30, 'Wena ke, nyana womntu, oonyana babantu bakowenu basathetha ngawe ezindongeni naseminyango yezindlu, bethetha elowo nomzalwana wakhe, besithi, Yiza, uve ukuba lithini na elo lizwi; yeNkosi phumani.', 'XHO22'),
(30, 33, 31, 'Beza kuwe njengokuza kwabantu, bahlala phambi kwakho ngokwabantu bam, baweva amazwi akho, abavuma ukwenza; kuba benza uthando olukhulu ngomlomo wabo, kodwa iintliziyo zabo zilandela ukubawa kwabo.', 'XHO22'),
(30, 33, 32, 'Yabona, kubo ufana nengoma emnandi kakhulu, ozwi limyoli, okwaziyo ukukubetha uhadi; ngokuba bawevile amazwi akho, abaweva.', 'XHO22'),
(30, 33, 33, 'Kwakuba njalo, yabona, iya kufika; bazi ukuba umprofeti ubephakathi kwabo.', 'XHO22'),
(30, 34, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 34, 2, 'Nyana womntu, profeta kubalusi bakwaSirayeli, uthi kubo, Itsho iNkosi uYehova kubalusi; Yeha ke, abalusi bakwaSirayeli, abazalusa ngokwabo! Abalusi mabangawalusi na umhlambi?', 'XHO22'),
(30, 34, 3, 'Nidla amanqatha, ninxiba izinxibo zoboya, abatyebileyo nibaxhela; kodwa aniwalusi umhlambi.', 'XHO22'),
(30, 34, 4, 'Ezigulayo anizomelezanga, ezifayo aniziphilisanga, ezaphukileyo anizibophanga, ezigxothiweyo anizibuyisanga, nezilahlekileyo anizifunanga; kodwa wabalawula ngogonyamelo nangenkohlakalo.', 'XHO22'),
(30, 34, 5, 'Zaba lusali ngokuswela umalusi, zaba kukudla kwamarhamncwa onke elizwe, zaba lusali.', 'XHO22'),
(30, 34, 6, 'Iigusha zam zaduka ezintabeni zonke, nasezindulini zonke ezinde, zabe zithe saa emhlabeni wonke, akwabakho uzifunayo nongakufunayo.', 'XHO22'),
(30, 34, 7, 'Ngako oko, balusi, liveni ilizwi likaYehova;', 'XHO22'),
(30, 34, 8, 'Ndihleli nje, itsho iNkosi uYehova, ngokuba umhlambi wam ube lixhoba, umhlambi wam ube kukudla kwamarhamncwa onke elizwe, ngenxa yokuba kungekho malusi, nabalusi bam bengawukhathaleli umhlambi wam kodwa abalusi bayazalusa ngokwabo, kwaye abawavumeli umhlambi wam udle;', 'XHO22'),
(30, 34, 9, 'Ngoko ke, balusi, liphulaphuleni ilizwi likaYehova;', 'XHO22'),
(30, 34, 10, 'Itsho iNkosi uYehova ukuthi; Yabonani, ndinento ngakubalusi; yaye ndiya kuwubiza umhlambi wam ezandleni zabo ndize ndikuyekise ukwalusa umhlambi wam; bangabi sazalusa bona abalusi; kuba ndiya kuwusindisa umhlambi wam emlonyeni wabo, ukuze ungabi kukudla kwabo.', 'XHO22'),
(30, 34, 11, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Yabona, ndiya kuzigocagoca izimvu zam ndize ndizifune.', 'XHO22'),
(30, 34, 12, 'Njengomalusi ewugcinayo umhlambi wakhe, ngemini yokubakho kwakhe phakathi kweegusha zakhe ezichithakeleyo; ndiya kuwufuna umhlambi wam, ndiwusindise ezindaweni zonke othe saa kuwo ngemini yamafu nobumnyama.', 'XHO22'),
(30, 34, 13, 'Ndiya kuzikhupha ezintlangeni ndize ndizibuthe emazweni ndize ndizise emhlabeni wazo, ndiwadlise ezintabeni zakwaSirayeli emilanjeni nasezindaweni zonke ezimiweyo zelizwe.', 'XHO22'),
(30, 34, 14, 'Ndiya kuwalusela ebutyanini obulungileyo, abe yindlwane ezintabeni ezinde zakwaSirayeli; ziya kubutha emadlelweni alungileyo, zidle ekriweni elityebileyo ezintabeni zakwaSirayeli.', 'XHO22'),
(30, 34, 15, 'Ndiza kuzondla izimvu zam ndize ndizenze zibe nemihlali,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 34, 16, 'Into elahlekileyo ndiya kuyifuna; ke etyebileyo, neyomeleleyo, ndiya kuyitshabalalisa; ndiya kuzalusa ngokusesikweni.', 'XHO22'),
(30, 34, 17, '“Nina ke, mhlambi wam, itsho iNkosi enguMongami uYehova, ukuthi, Yabonani, ndiya kugweba phakathi kwemazi yenkomo neemazi, naphakathi kweenkunzi zeegusha neenciniba.', 'XHO22'),
(30, 34, 18, 'Yinto encinane na kuni, ukuthi nidle utyani obulungileyo, nikunyhashe oko kuseleyo ngamanqina enu? nisele amanzi anzongonzongo, nihlambe aseleyo ngamanqina enu?', 'XHO22'),
(30, 34, 19, 'Umhlambi wam udla obunyhashiweyo ngamanqina akho; basela into oyinqambi ngeenyawo zakho.', 'XHO22'),
(30, 34, 20, 'Ngako oko, itsho iNkosi uYehova kubo ukuthi; yabona, ndiya kugweba phakathi kwamanqatha natyebileyo;', 'XHO22'),
(30, 34, 21, 'Ngokuba nabetha ngecala lakho nangegxalaba, nayibetha yonke imilwelwe ngeempondo zakho, wada wayichithachitha;', 'XHO22'),
(30, 34, 22, 'Ndiya kuwusindisa umhlambi wam, ungabi saba lixhoba; ndiya kugweba phakathi kweenkomo neemazi.', 'XHO22'),
(30, 34, 23, 'Ndiya kumisa phezu kwawo umalusi abe mnye, awaluse, umkhonzi wam uDavide; Uya kuwalusa, abe ngumalusi wawo;', 'XHO22'),
(30, 34, 24, 'Mna Yehova ndiya kuba nguThixo wawo, umkhonzi wam uDavide abe yisikhulu phakathi kwawo; Mna Yehova ndithethile.', 'XHO22'),
(30, 34, 25, 'Ndiya kuwenza umnqophiso woxolo ndiwatshabalalise amarhamncwa amabi emhlabeni, yaye ngokuqinisekileyo aya kuhlala entlango ekhuselekile aze alale ehlathini.', 'XHO22'),
(30, 34, 26, 'Ndowenza wona, kwaneendawo ezingeenxa zonke endulini yam, ube yintsikelelo; ndiya kunisa imvula ngexesha layo; kuya kubakho iimvula zentsikelelo.', 'XHO22'),
(30, 34, 27, 'Umthi welizwe wothwala isiqhamo sawo, ilizwe livelise ukudla kwalo, bakhuseleke ezweni labo; bazi ukuba ndinguYehova, ekuwaphuleni kwam amathambo edyokhwe yabo, ndibahlangule isandla sabo babekhonza ngokwabo.', 'XHO22'),
(30, 34, 28, 'Akayi kuba saba lixhoba leentlanga, angadli amarhamncwa elizwe; kodwa baya kuhlala bekhuselekile, yaye akukho bani uya kuboyikisa.', 'XHO22'),
(30, 34, 29, 'Ndiya kubavelisela isityalo esinegama, ukuze bangabi sabulawa yindlala ehlabathini, bangabi sathwala ihlazo leentlanga.', 'XHO22'),
(30, 34, 30, 'Baya kwazi ukuba mna Yehova Thixo wabo ndinabo, bangabantu bam bona, indlu kaSirayeli; itsho iNkosi uYehova.', 'XHO22'),
(30, 34, 31, 'Nina ke, zimvu zam, zimvu zedlelo lam, nani bantu; ndinguThixo wenu; itsho iNkosi uYehova.', 'XHO22'),
(30, 35, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 35, 2, 'Nyana womntu, bhekisa ubuso bakho entabeni yakwaSehire, uprofete;', 'XHO22'),
(30, 35, 3, 'Uthi kuyo, Utsho uYehova; Yabona, ntaba yakwaSehire, ndiya kulwa nawe, ndisolulele kuwe isandla sam, ndikwenze kube senkangala;', 'XHO22'),
(30, 35, 4, 'Ndiza kuzenza izixeko zakho zibe yinkangala, yaye ngokuqinisekileyo uya kuba yintlango; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 35, 5, 'Ngenxa yokuba waba nentiyo engunaphakade waza waliphalaza igazi loonyana bakaSirayeli ngogonyamelo lwekrele ngexesha lentlekele yabo, ngexesha lokuphela kobubi babo;', 'XHO22'),
(30, 35, 6, 'Ngako oko, ndihleli nje, utsho uYehova, ndiya kukumisela igazi, igazi likulandele;', 'XHO22'),
(30, 35, 7, 'Ngoko ndiya kuyenza iNtaba yaseSehire ibe yinkangala ephanzileyo, ndibatshabalalise abo badlulayo kuyo nababuyayo.', 'XHO22'),
(30, 35, 8, 'Ndiya kuzizalisa iintaba zakhe ngababuleweyo bakhe; ezindulini zakho, nasemifuleni yakho, nasezihlanjeni zakho zonke, ababuleweyo ngekrele bawe.', 'XHO22'),
(30, 35, 9, 'Ndiya kukwenza kube senkangala kuwe ngonaphakade, nemizi yakho ingabuyi; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 35, 10, 'Ngenxa enokuba usithi, Iintlanga ezi zombini namazwe la omabini ngawam, sowadla ilifa; xa uYehova wayekho.', 'XHO22'),
(30, 35, 11, 'Ngako oko ndihleli nje, itsho iNkosi uYehova, ndiya kwenza kuwe ngokomsindo wakho, nekhwele lakho, owenze kubo ngentiyo yakho; kwaye ndiya kuzazisa kubo xa ndikugweba.', 'XHO22'),
(30, 35, 12, 'Uya kwazi ukuba ndinguYehova, ndikuvile konke ukugiba kwakho, owakuthethayo ngeentaba zakwaSirayeli, usithi, Ndiyintlango; siyinikelwe ukuba siyidle.', 'XHO22'),
(30, 35, 13, 'Undiqhayise ngemilebe yomlomo wakho, uwandisile amazwi akho kum, ndiwevile.', 'XHO22'),
(30, 35, 14, 'Itsho iNkosi uYehova ukuthi; Xa ihlabathi liphela livuya, ndiya kukwenza intlango.', 'XHO22'),
(30, 35, 15, 'Njengoko walidlayo ilifa lendlu kaSirayeli, ngenxa yokuba ibiyintlango, ndiya kwenjenjalo kuwe; uya kuba yintlango, ntaba yakwaSehire, nelakwaEdom lonke liphela; zazi ukuba ndinguYehova.', 'XHO22'),
(30, 36, 1, 'Wena ke, nyana womntu, profeta ngazo iintaba zakwaSirayeli, uthi, Zintaba zakwaSirayeli, liveni ilizwi likaYehova!', 'XHO22'),
(30, 36, 2, 'Wathi uYehova; Ngokuba utshaba lwathi ngawe, Hayi!', 'XHO22'),
(30, 36, 3, 'uprofete uthi, Utsho uYehova; Ngenxa enokuba bakwenze intlango, bakudla ngeenxa zonke, ukuze ube bubutyebi bamasalela eentlanga, ususwe emlonyeni wabo bathethayo, ube lihlazo zabantu.', 'XHO22'),
(30, 36, 4, 'Ngoko ke, nina zintaba zakwaSirayeli, liveni ilizwi likaYehova; Kwatsho uYehova kwiintaba, neenduli, nemilambo, nakwiimbombo, nakwimiwewe engamanxuwa, nakwimizi engamanxuwa, eyaba lixhoba, into yokugculelwa ngamasalela eentlanga ezingeenxa zonke;', 'XHO22'),
(30, 36, 5, 'Ngako oko, utsho uYehova ukuthi, Ngenxa yomlilo wekhwele lam, ndithethe ngawo amasalela eentlanga nakwaEdom lonke elakwaEdom, elilenze ilifa lalo ilizwe lam, bechwayithile, benobubi bentliziyo yabo yonke, benobubi, baya kuza kunigxotha njengokuphanga.', 'XHO22'),
(30, 36, 6, 'Ke kaloku, profeta ngokuphathelele ilizwe lakwaSirayeli, uthi kuzo iintaba neenduli, imilambo neentlambo, ‘Itsho iNkosi enguMongami uYehova ukuthi, ‘Itsho iNkosi enguMongami uYehova ukuthi, ‘Itsho iNkosi enguMongami uYehova. Yabonani, ndithethe ngekhwele lam, nomsindo wam, ngenxa enokuba nithwele ihlazo leentlanga.', 'XHO22'),
(30, 36, 7, 'Ngako oko, utsho uYehova ukuthi, Mna ndisiphakamisile isandla sam; iintlanga ezingeenxa zonke kuni, ziya kuthwala ihlazo lazo.', 'XHO22'),
(30, 36, 8, 'Ke nina, zintaba zakwaSirayeli, niya kuvelisa amasebe, nibanike abantu bam amaSirayeli iziqhamo zabo; ngokuba seleza kuza.', 'XHO22'),
(30, 36, 9, 'Ngokuba, uyabona, ndingakuwe; ndiya kubuyela kuwe, uhlwayelwe, utyalwe;', 'XHO22'),
(30, 36, 10, 'Ndiya kwandisa abantu phezu kwenu, indlu kaSirayeli iphela, bonke, nezixeko ziya kuba nabemi neendawo eziphanzileyo ziya kwakhiwa.', 'XHO22'),
(30, 36, 11, 'Ndiya kwandisela abantu neenkomo phezu kwenu; kwaye baya kwanda bathwale isiqhamo; ndiya kukwenza ube ngoobutyebi bakho obudala, ndikwenze ulunge ngakumbi kunoko wawuqalileyo; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 36, 12, 'Ndiya kubahambisa abantu phezu kwenu, bantu bam amaSirayeli; baya kulihlutha, libe lilifa labo, ungabi salithabatha ebantwini.', 'XHO22'),
(30, 36, 13, 'Utsho uYehova ukuthi, Ngenxa enokuba besithi kuwe, Ilizwe lakho liyazitshabalalisa iintlanga, liziphanga iintlanga zakho;', 'XHO22'),
(30, 36, 14, 'Ngako oko anisayi kuphinda nidle abantu nize niziphange iintlanga zenu, itsho iNkosi uYehova.', 'XHO22'),
(30, 36, 15, 'Andiyi kuphinda ndizivumele iintlanga zibe neentloni zeentlanga kuwe, yaye aniyi kuba sathwala ihlazo leentlanga, yaye aniyi kuba saziyekela ziwile iintlanga zenu; utsho uYehova.', 'XHO22'),
(30, 36, 16, 'Ngaphezu koko, kwabonakala ilizwi likaYehova kum lisithi:', 'XHO22'),
(30, 36, 17, 'Nyana womntu, xa indlu kaSirayeli yabe ihleli emhlabeni wayo, bawenza inqambi ngokweendlela zabo nangezenzo zabo;', 'XHO22'),
(30, 36, 18, 'Ngoko ke ndaphalaza ubushushu bam phezu kwabo ngenxa yegazi abaliphalaza elizweni naphezu kwezigodo zabo abalenze inqambi ngazo.', 'XHO22'),
(30, 36, 19, 'Ndazivakalisa ezintlangeni, zaziswa ke ezizweni; Ndabagweba ngokweentshukumo zabo, nangezenzo zabo.', 'XHO22'),
(30, 36, 20, 'Bafika ezintlangeni ababeye kuzo, balihlambela igama lam elingcwele ngokuthi kubo: “Aba ngabantu bakaYehova, yaye balishiya ilizwe lakhe.”', 'XHO22'),
(30, 36, 21, 'Ndaba nemfesane egameni lam elingcwele, ebeyilihlambele indlu kaSirayeli ezintlangeni abaya kuzo.', 'XHO22'),
(30, 36, 22, 'Ngako oko yithi kwindlu kaSirayeli, uYehova uthethile; Andikwenzi oku ngenxa yenu, nina ndlu kaSirayeli; ndikwenza ngenxa yegama lam elingcwele, enilenze inqambi ezintlangeni enaye kuzo.', 'XHO22'),
(30, 36, 23, 'Ndiya kulingcwalisa igama lam elikhulu, elingcwalisiweyo phakathi kweentlanga, enilingcwalisileyo phakathi kwazo; zazi iintlanga ukuba ndinguYehova,’ utsho uYehova, ekuzingcwaliseni kwam kuni phambi kwamehlo azo.', 'XHO22'),
(30, 36, 24, 'Kuba ndiya kunithabatha ezintlangeni, ndinibuthe emazweni onke, ndinise emhlabeni wenu.', 'XHO22'),
(30, 36, 25, 'Ndiya kunitshiza ngamanzi amhlophe, nihlambuluke; Ndiya kunihlambulula kuko konke ukungcola kwenu nakomoya wenu ongcolileyo.', 'XHO22'),
(30, 36, 26, 'Ndoninika intliziyo entsha, ndininike umoya omtsha ngaphakathi kwenu; ndiyisuse intliziyo yelitye enyameni yenu, ndininike intliziyo yenyama.', 'XHO22'),
(30, 36, 27, 'Ndiza kufaka umoya wam kuni ndize ndinihambise ngemimiselo yam, nize niyigcine imigwebo yam nize niyifeze;', 'XHO22'),
(30, 36, 28, 'niya kuhlala ezweni endalinika ooyihlo; nibe ngabantu bam, mna ndibe nguThixo wenu.', 'XHO22'),
(30, 36, 29, 'Ndiya kunisindisa kuyo yonke inkunkuma yenu, ndibize ukutya ndize ndikwandise, kungabikho ndlala kuni.', 'XHO22'),
(30, 36, 30, 'Ndiya kuyandisa iziqhamo zemithi, nemveliso yelizwe, ukuze ningabuyi namkeliswe isingcikivo sendlala ezintlangeni.', 'XHO22'),
(30, 36, 31, 'Wozikhumbula ke iindlela zakho ezimbi, neentlondi zakho ezingalungileyo, uzithiye ngenxa yobugwenxa bakho nangenxa yamasikizi akho.', 'XHO22'),
(30, 36, 32, 'Andiyi kukwenza oku ngenxa yenu, utsho uYehova; danani, danani ngenxa yeendlela zenu, nina ndlu kaSirayeli.', 'XHO22'),
(30, 36, 33, 'Wathi uYehova, Ngaloo mini, ndinihlambulule ezonweni zenu zonke, ndinihlalise emizini, akhiwe amanxuwa.', 'XHO22'),
(30, 36, 34, 'Ilizwe ebeliphanzile liya kulinywa, ngoxa liyinkangala phambi kwamehlo abo bonke abadlulayo.', 'XHO22'),
(30, 36, 35, 'Baya kuthi, Eli lizwe, bekusenkangala kulo, lisuke laba njengomyezo wase-Eden; namanxuwa, nemizi engamanxuwa, namanxuwa abe zimboniselo, ezinabemi.', 'XHO22'),
(30, 36, 36, 'Ziya kwazi ke iintlanga eziseleyo ezikujikelezileyo ukuba mna, Yehova, ndakha iindawo eziphanzileyo, ndize ndityale indawo ebiphanzile; Mna Yehova ndithethile, yaye ndiya kwenza.', 'XHO22'),
(30, 36, 37, 'Wathi uYehova, Ndisaya kuyibuza loo nto kwindlu kaSirayeli, ukuba ndiyenze kubo; ndiya kubandisa njengomhlambi;', 'XHO22'),
(30, 36, 38, 'njengomhlambi ongcwele, njengomhlambi waseYerusalem ngamaxesha ayo; izixeko eziphanzileyo ziya kuzaliswa yimihlambi yabantu, ukuze bazi ukuba ndinguYehova.', 'XHO22'),
(30, 37, 1, 'Saba phezu kwam isandla sikaYehova, wandikhupha ngoMoya kaYehova, wandibeka emfuleni ozele ngamathambo;', 'XHO22'),
(30, 37, 2, 'Wandijikajika, ndawajika ndawabona emaninzi entilini; nantso omile kakhulu.', 'XHO22'),
(30, 37, 3, 'Wathi ke kum, Nyana womntu, aya kuphila na ke la mathambo? Ndaphendula ndathi, Nkosi Yehova, nguwe owaziyo.', 'XHO22'),
(30, 37, 4, 'Wathi ke kum, Profeta kula mathambo, uthi kuwo, Mathambondini omileyo, liveni ilizwi likaYehova.', 'XHO22'),
(30, 37, 5, '“Itsho iNkosi uNdikhoyo ngala mathambo niyabona, ndiniphefumlela, niphile;', 'XHO22'),
(30, 37, 6, 'Ndiya kunifaka umsipha, ndinenze inyama phezu kwenu, ndinambese ulusu, ndifake umoya kuni, niphile; nazi ukuba ndinguYehova.', 'XHO22'),
(30, 37, 7, 'Ndaprofeta njengoko bendiwiselwe umthetho ngako; Ndithe ndingekaprofeti, kwavakala isandi, nanko kushukuma, esondela amathambo, ithambo kwelinye ethanjeni.', 'XHO22'),
(30, 37, 8, 'Ndawabona, ndabona izihlunu nenyama kubonakala kuwo, lususu lwawagubungela ngaphezulu, kodwa kungekho mphefumlo kuwo.', 'XHO22'),
(30, 37, 9, 'Waza wahlabela mgama wathi kum: “Profeta nxamnye nomoya, profeta, nyana womntu, uthi kumoya, ‘Itsho iNkosi enguMongami uYehova; Phuma kwimimoya yomine, moya, uphefumlele phezu kwababuleweyo, ukuze baphile.', 'XHO22'),
(30, 37, 10, 'Ndaprofeta ke ngoko bendiwisele umthetho ngako; wangena umoya kuwo; aphila, ema ngeenyawo zawo engumkhosi omkhulu.', 'XHO22'),
(30, 37, 11, 'Wathi ke kum, Nyana womntu, la mathambo yindlu kaSirayeli yonke; Yabona, bathi, Amathambo ethu omile, nethemba lethu litshabalele; sinqanyulelwe amalungu ethu.', 'XHO22'),
(30, 37, 12, 'Ngako oko profeta, uthi kubo, Itsho iNkosi uYehova; Yabonani, bantu bam, ndiya kuwavula amangcwaba enu, ndininyuse niphume emangcwabeni enu, ndinise emhlabeni wakwaSirayeli.', 'XHO22'),
(30, 37, 13, 'Niya kwazi ukuba ndinguYehova, ekuwavuleni kwam amangcwaba enu, bantu bam, ndinikhuphe emangcwabeni enu;', 'XHO22'),
(30, 37, 14, 'Ndofaka uMoya wam kuni, niphile, ndinibeke emhlabeni wenu; nazi ukuba mna Yehova ndithethile, ndikwenzile; utsho uYehova.', 'XHO22'),
(30, 37, 15, 'Laphinda labonakala ilizwi likaYehova kum, lisithi:', 'XHO22'),
(30, 37, 16, '“Wena ke, nyana womntu, thabatha intonga ibe nye, ubhale kuyo ukuthi, EkaYuda neyoonyana bakaSirayeli, abahlobo bakhe; uthabathe enye intonga, ubhale kuyo, uthi, EkaYosefu, umthi wakwaEfrayim, nendlu yonke kaSirayeli, amadlelane akhe;', 'XHO22'),
(30, 37, 17, 'Wozibeka kwintonga-nye; kwaye ziya kuba nto-nye esandleni sakho.', 'XHO22'),
(30, 37, 18, 'Bakuthetha nawe oonyana babantu bakowenu, bathi, Akuthandi na ukusixela na?', 'XHO22'),
(30, 37, 19, 'Yithi kubo, Itsho iNkosi uYehova ukuthi, Yabonani, ndiya kuwuthabatha umthi kaYosefu, osesandleni sikaEfrayim, nezizwe zakwaSirayeli, abazalwana bakhe, ndizibeke ngakuye kwimithi yakwaYuda, ndiwenzele umthi lowo baya kwenza umthi omnye ube mnye esandleni sam.', 'XHO22'),
(30, 37, 20, 'Izinti oya kubhala kuzo zibe sesandleni sakho phambi kwamehlo abo.', 'XHO22'),
(30, 37, 21, 'Wathi kubo, Itsho iNkosi uYehova ukuthi; Yabonani, ndiya kubathabatha oonyana bakaSirayeli phakathi kweentlanga abahamba kuzo, ndibabuthe ezimbombeni zonke, ndibase emhlabeni wabo.', 'XHO22'),
(30, 37, 22, 'Ndibenze luhlanga lunye emhlabeni, ezintabeni zakwaSirayeli; ukumkani omnye uya kuba ngukumkani wabo bonke bephela, bangabi saba ziintlanga ezimbini, bangabi sahlukana babe zizikumkani ezibini;', 'XHO22'),
(30, 37, 23, 'Abayi kubuya bazenze iinqambi ngemifanekiso yabo eqingqiweyo, nangezinto zabo ezinezothe, nangezikreqo zabo zonke; ndibahlangule kuzo zonke izakhiwo zabo, abone kuzo, ndibahlambulule; baya kuba ngabantu bam, mna ndibe nguThixo wabo.', 'XHO22'),
(30, 37, 24, 'Umkhonzi wam uDavide woba ngukumkani wabo; yaye bonke baya kuba nomalusi omnye; nabo baya kuhamba ngemimiselo yam, bagcine imiyalelo yam, bayenze.', 'XHO22'),
(30, 37, 25, 'Bohlala ezweni elo, ndalinika umkhonzi wam uYakobi, ababemi kulo ooyihlo; Baya kuhlala khona, bona, nabantwana babo, nabantwana babantwana babo, kude kuse ephakadeni; noDavide umkhonzi wam uya kuba ngumlawuli wabo ukusa kwixesha elingenammiselo.', 'XHO22'),
(30, 37, 26, 'Ndiya kwenza umnqophiso woxolo nabo; uya kuba ngumnqophiso ongunaphakade kubo; Ndiya kubabeka, ndibandise, ndibeke ingcwele yam kubo ngonaphakade.', 'XHO22'),
(30, 37, 27, 'Umnquba wam woba nabo; ewe, ndiya kuba nguThixo wabo, babe ngabantu bam bona.', 'XHO22'),
(30, 37, 28, 'Yaye iintlanga ziya kwazi ukuba ndinguYehova oningcwalisayo uSirayeli, ngenxa yokuba indawo yam engcwele iya kuba phakathi kwabo ukusa kwixesha elingenammiselo.', 'XHO22'),
(30, 38, 1, 'Kwafika ilizwi likaYehova kum, lisithi,', 'XHO22'),
(30, 38, 2, 'Nyana womntu, bhekisa ubuso bakho kuGogi, ezweni lakwaMagogi, inkosana enkulu yakwaMesheki noTubhali, uprofetele kuye.', 'XHO22'),
(30, 38, 3, 'Uthi, uYehova uthe; Yabona, ndinengxaki nawe, Gogi, nkosi enkulu yaseMeshege naseTubhali.', 'XHO22'),
(30, 38, 4, 'Ndiza kukubuyisa ndize ndikufake amagwegwe emihlathini yakho, ndikukhuphe nomkhosi wakho wonke, amahashe nabakhweli-mahashe, bonke abaxhobe ngazo zonke iintlobo zesikrweqe, ewe, isihlwele esikhulu esinamakhaka namakhaka, bonke bephela uphethe amakrele:', 'XHO22'),
(30, 38, 5, 'namaPersi, namaKushi, namaLubhi; bonke bephela bephethe ingweletshetshe nezigcina-ntloko;', 'XHO22'),
(30, 38, 6, 'uGomere nemizalwane yakhe yonke; nendlu kaTogarma entla, nomkhosi wakhe wonke, nezizwe ezininzi ezinawe.', 'XHO22'),
(30, 38, 7, 'Lunga, uzilungise, wena nengxokolo yakho yonke, ihlanganiselwe kuwe, ube ngumboniseli wayo.', 'XHO22'),
(30, 38, 8, 'Emva kwemihla emininzi uya kuvelelwa; Ekupheleni kweminyaka, uya kungena ezweni elabuyela kulo ikrele, lihlanganiselwe ezintlangeni ezininzi, uye kwiintaba zakwaSirayeli ezahlala zisenkangala; Ithatyathwe kuzo zonke iintlanga, yaye bonke baya kuhlala bekhuselekile.', 'XHO22'),
(30, 38, 9, 'Uya kusuk’ ume njengesaqhwithi, njengelifu eliwugubungelayo umhlaba, wena namaqela akho onke, nezizwe ezininzi ezinawe.', 'XHO22'),
(30, 38, 10, 'Itsho iNkosi uYehova ukuthi; Kwakhona kuya kwenzeka ukuba ngexesha elifanayo izinto ziya kuza engqondweni yakho, kwaye uya kucinga ingcinga embi;', 'XHO22'),
(30, 38, 11, 'Uya kuthi, Ndiya kunyuka ndiye ezweni lamaphandle; Ndiya kubafikela abaphumli, abahleli ngoxolo, bonke abahleli bengenazindonga, bengenazithintelo naminyango;', 'XHO22'),
(30, 38, 12, 'ukuphangwa nokuphangwa kwempahla ephangiweyo; ukuze ubuyisele isandla sakho kwiindawo eziphanzileyo ezimiweyo ngoku, nakwizizwana ezihlanganiselwe kwizizwana ezinemfuyo nempahla, ezihlala esazulwini selizwe.', 'XHO22'),
(30, 38, 13, 'OoShebha noDedan nabarhwebi baseTarshishe nabarhwebi bonke bakhona baya kuthi kuwe, Uze koyisa na? Ulihlanganisile na ihlokondiba lakho ukuba liphange amaxhoba? Kuhluthwe isilivere negolide, kuhluthwe iinkomo, neempahla, nokuhlutha impahla eninzi?', 'XHO22'),
(30, 38, 14, 'Ngako oko profeta, nyana womntu, uthi kuGogi, uYehova uthethile; Akuyi kuba njalo na ngelo xesha, ekuhlaleni kwabantu bam amaSirayeli ngoxolo?', 'XHO22'),
(30, 38, 15, 'Uya kuphuma endaweni yakho ezingontsini zasentla, wena neentlanga ezininzi ezinawe, zonke zikhwele emahasheni, isihlwele esikhulu, nomkhosi omkhulu onamandla.', 'XHO22'),
(30, 38, 16, 'Uya kubafikela abantu bam amaSirayeli njengelifu eligubungele ilizwe; kuya kuthi ekupheleni kwemihla, ndikuzisele ezweni lam, zindazi iintlanga, ekuzingcwaliseni kwam kuwe, Gogi, emehlweni azo.', 'XHO22'),
(30, 38, 17, 'Wathi uYehova, Nguwe na lowa, ndandithetha yena ngeemini zamandulo ngomlomo wabakhonzi bam, abaprofeti bakwaSirayeli, ababeprofeta iminyaka kwezo mini ngelithi, Ndiya kukuzisa ukuba ulwe nabo?', 'XHO22'),
(30, 38, 18, 'Ngelo xesha uGogi uya kuhlasela ilizwe lakwaSirayeli,’ utsho uYehova, ngokuqinisekileyo ndiya kuvutha umsindo wam phambi kwamehlo am.', 'XHO22'),
(30, 38, 19, 'Ke ndathi ndinekhwele, ndinomlilo wokuvutha komsindo wam, Kuya kubakho inyikima enkulu ngaloo mini emhlabeni wakwaSirayeli;', 'XHO22'),
(30, 38, 20, 'ngenxa yam ziya kudidizela phambi kwam iintlanzi zolwandle, neentaka zezulu, nezilo zelizwe, nezinambuzane zonke ezinambuzelayo emhlabeni, nezizwe zonke zehlabathi; neentaba ziya kushukuma liya kuwa, namatye aya kuwa, kwaye zonke iindonga ziya kuwa emhlabeni.', 'XHO22'),
(30, 38, 21, 'Ndiya kumbiza ngokuba likrele ezintabeni zam zonke, utsho uYehova; ikrele lomntu liya kuma kumzalwana wakhe.', 'XHO22'),
(30, 38, 22, 'Ndiya kulwa naye ngesibetho nangegazi; yaye ndiza kunisa imvula phezu kwakhe naphezu kwamaqela akhe naphezu kweentlanga ezininzi ezikunye naye nesichotho esikhulu, umlilo nesulfure.', 'XHO22'),
(30, 38, 23, 'Ngoko ke ndiya kuzizukisa, ndizingcwalise; Ndiya kwaziwa emehlweni eentlanga ezininzi, zazi ukuba ndinguYehova.', 'XHO22'),
(30, 39, 1, 'Wothi ke, nyana womntu, profeta kuGogi, uthi, uYehova uthethile; Yabona, ndinengxaki nawe, Gogi, nkosi enkulu yaseMeshege naseTubhali.', 'XHO22'),
(30, 39, 2, 'Ndiya kukubuyisa, ndikushiye isahlulo sesithandathu, ndikunyuse use entla, ndikuse ezintabeni zakwaSirayeli.', 'XHO22'),
(30, 39, 3, 'Isaphetha sakho ndiya kusibetha esandleni sakho sasekhohlo, neentolo zakho ziwe esandleni sakho sasekunene.', 'XHO22'),
(30, 39, 4, 'Uya kuwa ezintabeni zakwaSirayeli, wena namaqela akho onke, neentlanga ezinawe; ndikunikele kwiintaka zeentlobo ngeentlobo, nakukudla kwamarhamncwa asendle.', 'XHO22'),
(30, 39, 5, 'Uya kuwa emhlabeni, ngokuba ndithethile, utsho uYehova.', 'XHO22'),
(30, 39, 6, 'Ndiza kubaphosa umlilo kwaMagogi naphakathi kwabo bahlala ngokungakhathali eziqithini, ukuze bazi ukuba ndinguYehova.', 'XHO22'),
(30, 39, 7, 'Ndiya kulazisa igama lam elingcwele phakathi kwabantu bam amaSirayeli; kwaye andisayi kubavumela ukuba balihlambele igama lam elingcwele; zazi iintlanga ukuba ndinguYehova, Lowo Ungcwele kaSirayeli.', 'XHO22'),
(30, 39, 8, 'Yabona, le nto iyeza, iya kubakho; utsho uYehova; yile mini bendithetha ngayo.', 'XHO22'),
(30, 39, 9, 'Ke abemi bezixeko zakwaSirayeli baya kuphuma batshise izixhobo, iingweletshetshe, iingweletshetshe, izaphetha, neentolo, neentonga, neencula, bazitshise ngomlilo iminyaka esixhenxe.', 'XHO22'),
(30, 39, 10, 'Ukuze bangathathi iinkuni ezweni, bagawule nanye ematyholweni; ngokuba baya kuzitshisa izikrweqe ngomlilo; yaye ngokuqinisekileyo baya kubatshabalalisa abo babewatshabalalisa nabo babewatshabalalisa,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 39, 11, 'Kuya kuthi ngaloo mini ndimnike uGogi indawo yokungcwaba kwaSirayeli, intlambo yabarhwebi ngakwimpuma yolwandle; kwaye iya kuzivala iimpumlo zabakhweli; ngcwaba uGogi nabantu bakhe bonke, bakubize ngokuba yintlambo yeHamongogi.', 'XHO22'),
(30, 39, 12, 'Indlu kaSirayeli yabangcwaba iinyanga ezisixhenxe, ukuze balihlambulule ilizwe;', 'XHO22'),
(30, 39, 13, 'bonke abantu belizwe eli baya kubangcwaba; yaye liya kuba ligama labo ngemini yokuzukiswa kwam,” utsho uYehova.', 'XHO22'),
(30, 39, 14, 'Bahlula amadoda asebenza ngokusisigxina, ahambahamba engcwabeni ilizwe, nalawo ahambahamba ehlabathini lonke ukuze alihlambulule; ekupheleni kweenyanga ezisixhenxe baya kufuna.', 'XHO22'),
(30, 39, 15, 'Abamahashe baya kucanda ilizwe, ukuba uthe wabona ithambo lomntu, makamise uphawu ecaleni kwalo, lide ingcwaba alingcwabe emfuleni weHamongoge.', 'XHO22'),
(30, 39, 16, 'Igama lesixeko liya kuba yiHamona. Ngoko baya kulicoca ilizwe.', 'XHO22'),
(30, 39, 17, '“Wena ke, nyana womntu, utsho uYehova; Zixeleleni zonke iintaka ezineentsiba, nakumarhamncwa onke asendle, nithi, Buthani, yizani; zihlanganiseni ngeenxa zonke embingelelweni wam endinizisa khona, umbingelelo omkhulu ezintabeni zakwaSirayeli, ukuze nidle inyama, nisele igazi.', 'XHO22'),
(30, 39, 18, 'Niya kudla inyama yamagorha nisele negazi leenkosana zomhlaba, eleenkunzi zeegusha neleenkunzi zeegusha neleenciniba neleenkunzi zenkomo nelezilwanyana ezityetyisiweyo zaseBhashan.', 'XHO22'),
(30, 39, 19, 'Niya kudla amanqatha, nihluthe, nisele igazi nide ninxile embingelelweni wam endinienzele khona.', 'XHO22'),
(30, 39, 20, 'Niya kwanela etafileni yam ngamahashe neenqwelo zokulwa, namagorha namadoda onke okulwa; itsho iNkosi uYehova.', 'XHO22'),
(30, 39, 21, 'Ndiza kunika uzuko lwam phakathi kweentlanga, yaye zonke iintlanga ziya kuwubona umgwebo wam endiya kuwenza nesandla sam endiya kusisa phezu kwazo.', 'XHO22'),
(30, 39, 22, 'Ngoko indlu kaSirayeli iya kwazi ukuba ndinguYehova uThixo wayo ukususela ngaloo mini.', 'XHO22'),
(30, 39, 23, 'Yaye iintlanga ziya kwazi ukuba abo bendlu kaSirayeli bathinjwa ngenxa yokunxaxha kwabo, ngenxa yokuba baye bandikhonza ngokungathembeki; bawa ke bonke likrele.', 'XHO22'),
(30, 39, 24, 'Ndabenza ngokokungahlambuluki kwabo nanjengokunxaxha kwabo, ndaza ndabusithelisa ubuso bam kubo.', 'XHO22'),
(30, 39, 25, 'Ngako oko, utsho uYehova ukuthi, Kaloku ndiyasibuyisela isabelo sikaYakobi, ndisikwe yimfesane ngenxa yendlu yonke kaSirayeli, ndibe nobukhwele ngegama lam elingcwele;', 'XHO22'),
(30, 39, 26, 'Emva koko balithwala ihlazo labo nako konke ukunxaxha kwabo aboniswe ngako, ngoxa babehleli kwilizwe labo bekhuselekile, kungekho bani uboyikisayo.', 'XHO22'),
(30, 39, 27, 'Ekubabuyiseni kwam ezintlangeni, ndibabuthe emazweni eentshaba zabo, ndibangcwalise emehlweni eentlanga ezininzi;', 'XHO22'),
(30, 39, 28, 'bazi ukuba ndinguYehova uThixo wabo, owabafudusayo phakathi kweentlanga;', 'XHO22'),
(30, 39, 29, 'Andiyi kuphinda ndibufihle ubuso bam kubo, ngenxa yokuba ndithulule umoya wam phezu kwendlu kaSirayeli,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 40, 1, 'Ngomnyaka wamashumi omabini anamihlanu wokuthinjwa kwethu, ekuqaleni komnyaka, ngolweshumi enyangeni leyo, ngomnyaka weshumi elinesine wokuthinjwa komzi, ngaloo mini saba phezu kwam isandla sikaYehova wandizisa khona.', 'XHO22'),
(30, 40, 2, 'Wandisa ezweni lakwaSirayeli phambi koThixo, wandimisa entabeni ephakame kakhulu, ngaseludongeni lomzi ngasezantsi;', 'XHO22'),
(30, 40, 3, 'Wandisa khona, ndabona indoda enjengokubonakala kobhedu, iphethe esandleni sayo intsontelo yelinen emhlophe, noswazi lokulinganisa; wema esangweni.', 'XHO22'),
(30, 40, 4, 'Wathi kum loo mntu, Nyana womntu, khangela ngamehlo akho, uve ngeendlebe zakho, uyibhekise intliziyo yakho ezintweni zonke endikubonisa zona; ngokuba uziswe apha ngenjongo yokukubonisa; yixelele indlu kaSirayeli konke okubonayo.', 'XHO22'),
(30, 40, 5, 'Kwakukho udonga ngaphandle kwendlu, olujikelezileyo ngeenxa zonke, isesandleni somntu lowo ingcongolo yokulinganisa, iziikubhite ezintandathu, iyileyo iyikubhite enye, nobubanzi besandla; wabulinganisa ububanzi besakhiwo, ingcongolo enye; nokuphakama, ingcongolo enye.', 'XHO22'),
(30, 40, 6, 'Weza esangweni elibheke empumalanga, wenyuka izinyuko zalo, walinganisa isango lesango, laba yingcongolo enye ububanzi; nomnye umnyango waba yingcongolo enye ububanzi.', 'XHO22'),
(30, 40, 7, 'Negumbi elincinane ngalinye laba yingcongolo enye ubude, layingcongolo enye ububanzi; phakathi kwamagumbi iikubhite ezintlanu; nomgubasi ekungeneni komnyango wawuyingcongolo enye kuphela.', 'XHO22'),
(30, 40, 8, 'Walinganisa iivaranda zesango elingaphakathi, zaba yingcongolo enye.', 'XHO22'),
(30, 40, 9, 'Walinganisa ivaranda yesango ke, yaziikubhite ezisibhozo; iimbombo zalo zaba ziikubhite ezimbini; nevaranda yesango yona yayingaphakathi.', 'XHO22'),
(30, 40, 10, 'Ke amagumbi esango elisempumalanga aba mathathu nangapha, aba mathathu nangapha; zaba mlinganiso mnye omathathu, zaba mlinganiso mnye iiplanga zeminyango, zaba mlinganiso mnye ngapha nangapha.', 'XHO22'),
(30, 40, 11, 'Wabulinganisa ububanzi bomnyango wesango, baziikubhite ezilishumi; ubude besango, buziikubhite ezilishumi elinantathu.', 'XHO22'),
(30, 40, 12, 'Ke umda phambi kwamagumbi wawuyikubhite enye ngapha, wayikubhite enye nangapha umda; amagumbi okulinda amathandathu ngapha, amagumbi amathandathu nangapha.', 'XHO22'),
(30, 40, 13, 'Walinganisa isango, ethabathela eluphahleni lwamagumbi okulinda, esa eluphahleni lwamanye; laba ziikubhite ezimashumi mabini anantlanu ububanzi, ethabathela kwelinye umnyango esa komnye.', 'XHO22'),
(30, 40, 14, 'Iintsika wazenza zangamashumi omathandathu eekubhite, yeza emgubasini wentendelezo ejikelezileyo ngeenxa zonke esangweni.', 'XHO22'),
(30, 40, 15, 'Ukusukela phambi kwesango lokungena phambi kwevaranda yesango eliphakathi kwaba ziikubhite ezimashumi mahlanu.', 'XHO22'),
(30, 40, 16, 'Kwakukho iifestile ezicutheneyo emagumbini nakwiimbombo zawo, ngaphakathi kwesango macala onke, ukusa evarandeni, neefestile ngaphakathi macala onke, yaye kwikona yesango ngalinye kwakukho imithi yesundu.', 'XHO22'),
(30, 40, 17, 'Wandisa entendelezweni engaphandle, ndawabona amagumbi nomgangatho wentendelezo, ejikelezileyo ngeenxa zonke; amagumbi angamashumi amathathu kumgangatho wokuqala.', 'XHO22'),
(30, 40, 18, 'Indawo egandayiweyo, emacaleni amasango, ibheke kubude bamasango, ibe yeyangaphantsi.', 'XHO22'),
(30, 40, 19, 'Walinganisa ububanzi, ethabathela phambi kwesango lentendelezo ephantsi, esa phambi kwelentendelezo ephakathi ngaphandle, zalikhulu leekubhite ngasempumalanga nangasentla.', 'XHO22'),
(30, 40, 20, 'Isango lentendelezo engaphandle elijonge ngasemntla, wabulinganisa ubude balo nobubanzi balo;', 'XHO22'),
(30, 40, 21, 'amagumbi akhe okulinda aba mathathu nangapha, emathathu nangapha; iimbombo zalo, neevaranda zalo, zaba ngangomlinganiso womnyango wokuqala; baba ziikubhite ezimashumi mahlanu ubude, baziikubhite ezimashumi mabini anantlanu ububanzi.', 'XHO22'),
(30, 40, 22, 'Iifestile zalo, neevaranda zalo, namasundu alo, zaba ngangomlinganiso wesango elibheke empumalanga; Benyuka khona ngezinyuko ezisixhenxe; nevaranda yakhe ibikufuphi nazo.', 'XHO22'),
(30, 40, 23, 'Isango lentendelezo ephakathi lalimalunga nesango langasentla nelasempumalanga; walinganisa ethabathela kwelinye isango, esa kwelinye, iikubhite ezilikhulu.', 'XHO22'),
(30, 40, 24, 'Wandisa ngasemzantsi, kukho isango elingasezantsi; walinganisa iikona zeengcango zalo, neevaranda zalo, kwangokwaloo milinganiso.', 'XHO22'),
(30, 40, 25, 'Bekukho iifestile kulo nakwiivaranda zalo, zijikelezile ngeenxa zonke, njengeziya iifestile; zaba ziikubhite ezimashumi mahlanu ubude, zaba ziikubhite ezimashumi mabini anantlanu ububanzi.', 'XHO22'),
(30, 40, 26, 'Kwakukho izinyuko ezisixhenxe zokunyuka uye kulo, neevaranda zalo zaziphambi kwazo;', 'XHO22'),
(30, 40, 27, 'Bekukho isango entendelezweni ephakathi, elisingise ezantsi;', 'XHO22'),
(30, 40, 28, 'Wandisa entendelezweni ephakathi ngesango lasezantsi; walinganisa umnyango wasezantsi, waba njalo;', 'XHO22'),
(30, 40, 29, 'namagumbi alo okulinda, neekona zalo, neevaranda zalo, kwangokwaloo milinganiso; ubude buziikubhite ezimashumi mahlanu, ububanzi buziikubhite ezimashumi mabini anantlanu.', 'XHO22'),
(30, 40, 30, 'Ivaranda ejikelezayo ibiziikubhite ezimashumi mabini anantlanu ubude, yaba ziikubhite ezintlanu ububanzi.', 'XHO22'),
(30, 40, 31, 'Ivaranda yayo yayikhangele entendelezweni ephandle; bekukho amasundu ezikoneni zalo, nomnyango walo ube nezinyuko ezisibhozo.', 'XHO22'),
(30, 40, 32, 'Wandisa entendelezweni ephakathi, esingise empumalanga; walinganisa isango, kwaba ngokomlinganiselo;', 'XHO22'),
(30, 40, 33, 'namagumbi alo okulinda, neekona zalo, neevaranda zalo, kwangokwaloo mlinganiso; ubude buziikubhite ezimashumi mahlanu, ububanzi buziikubhite ezimashumi mabini anantlanu.', 'XHO22'),
(30, 40, 34, 'Ivaranda yayo yayikhangele entendelezweni ephandle; bekukho amasundu emigubasini yeminyango yalo, ngapha nangapha; yaye ukunyuka ukuya kuyo kwakukho izinyuko ezisibhozo.', 'XHO22'),
(30, 40, 35, 'Wandisa esangweni lasentla, walilinganisa ngaloo milinganiso;', 'XHO22'),
(30, 40, 36, 'amagumbi alo, neevaranda zalo, neevaranda zalo, neefestile zalo, zijikelezile ngeenxa zonke; zaba ziikubhite ezimashumi mahlanu ubude, zaba ziikubhite ezimashumi mabini anantlanu ububanzi.', 'XHO22'),
(30, 40, 37, 'Izakhelo zayo zaba entendelezweni ephandle; bekukho amasundu emigubasini yeminyango yalo, ngapha nangapha; yaye ukunyuka ukuya kuyo kwakukho izinyuko ezisibhozo.', 'XHO22'),
(30, 40, 38, 'Amagumbi neengcango zawo bezisezimbombeni zeminyango, apho bekuhlanjwa khona amadini anyukayo.', 'XHO22'),
(30, 40, 39, 'Evarandeni yesango bekukho iitafile ezimbini ngapha, iitafile ezimbini nangapha, zokunikela amadini anyukayo, nawesono, nawetyala.', 'XHO22'),
(30, 40, 40, 'Ngaphandle, konyukayo esiya ekungeneni kwesango langasentla, bekukho iitafile ezimbini; nakwelinye icala ngasevarandeni yesango bekukho iitafile ezimbini.', 'XHO22'),
(30, 40, 41, 'Bekukho iitafile ezine ngapha, iitafile ezine nangapha, ngecala lesango; Iitafile ezisibhozo, zokubingelela kuzo imibingelelo yabo;', 'XHO22'),
(30, 40, 42, 'Iitafile ezine zaba zezamatye aqingqiweyo ombingelelo otshiswayo, ubude bube yikubhite enye enesiqingatha, ububanzi buyikubhite enye enesiqingatha, ukuphakama bekuyikubhite enye; bazibeka kuzo izixhobo zokuxhela amadini anyukayo nexhoba.', 'XHO22'),
(30, 40, 43, 'Amagwegwe angangobubanzi besandla ngaphakathi, ebethelelwe ngeenxa zonke; yaye phezu kweetafile kwakukho inyama yedini.', 'XHO22'),
(30, 40, 44, 'Ngaphandle kwesango elingaphakathi kwakukho amagumbi eemvumi entendelezweni engaphakathi esecaleni kwesango lasemntla; elinye lingasecaleni lesango lasempumalanga, libheke entla.', 'XHO22'),
(30, 40, 45, 'Wathi kum, Eli gumbi, likhangele ezantsi, lelababingeleli abalungiselela indlu;', 'XHO22'),
(30, 40, 46, 'Igumbi elibheke ngasentla lelababingeleli abalungiselela esibingelelweni; ngoonyana bakaTsadoki, koonyana bakaLevi, abasondela kuYehova ukuba balungiselele kuye.', 'XHO22'),
(30, 40, 47, 'Wayilinganisa intsimi, baba ziikubhite ezilikhulu ubude bayo, nobubanzi bayo baba ziikubhite ezilikhulu; nesibingelelo siphambi kwendlu.', 'XHO22'),
(30, 40, 48, 'Wandisa evarandeni yendlu, walinganisa isango ngevaranda: iikubhite zontlanu ngapha, iikubhite zontlanu nangapha; ububanzi besango babuziikubhite ezintathu ngapha, iikubhite ezintathu nangapha ileyisi.', 'XHO22'),
(30, 40, 49, 'Ubude bevaranda baba ziikubhite ezimashumi mabini, ububanzi baba ziikubhite ezilishumi elinanye ngasezinyukweni wandisa kwizinyuko ababedla ngokunyuka kuzo, bekukho iintsika kwelinye icala lembombo, enye ngaphaya.', 'XHO22'),
(30, 41, 1, 'Wandisa etempileni, walinganisa imigubasi; Ububanzi bube buntandathu phezu apho, ububanzi bube buntandathu ngapha, njengobubanzi bomnquba.', 'XHO22'),
(30, 41, 2, 'Ububanzi bomnyango bebuziikubhite ezilishumi; amacala omnyango aba ziikubhite ezintlanu ngapha, iikubhite ezintlanu nangapha; wabulinganisa ubude bayo, baziikubhite ezimashumi mane, ububanzi baba ziikubhite ezimashumi mabini.', 'XHO22'),
(30, 41, 3, 'Wangena, walinganisa umphezulu womnyango, waziikubhite ezimbini; umnyango waba ziikubhite ezintandathu; ububanzi bomnyango buziikubhite ezisixhenxe.', 'XHO22'),
(30, 41, 4, 'Wabulinganisa ubude bayo, baziikubhite ezimashumi mabini; zaba ziikubhite ezimashumi mabini ububanzi phambi kwendlu; yathi kum, Le yingcwele kangcwele.', 'XHO22'),
(30, 41, 5, 'Walulinganisa udonga lwendlu, lwaziikubhite ezintandathu; ububanzi bendawo yokwayama baba ziikubhite ezine, obujikelezileyo ngeenxa zonke.', 'XHO22'),
(30, 41, 6, 'Amagumbi endawo yokwayama aba mathathu, igumbi phezu kwegumbi, aba mashumi mathathu ngokulandelelana; yaye ayengena eludongeni lwendlu emagumbini asemacaleni ajikelezayo ukuze awaxhase, kodwa ayengabambeleli eludongeni lwendlu.', 'XHO22'),
(30, 41, 7, 'Kwaphoswa khona uphokozo, olusa emagumbini endawo yokwayama; ngokuba ibiyijikeleza indlu le ngeenxa zonke; ngenxa yoko benyuka ububanzi bendlu, inyuka isuka kweyangaphantsi, iye kwelikhulu eliphakathi.', 'XHO22'),
(30, 41, 8, 'Ndabona kwasendlwini le indawo ephakamileyo, ejikelezileyo ngeenxa zonke;', 'XHO22'),
(30, 41, 9, 'Ukuphakama kodonga lwegumbi elingaphandle bekuziikubhite ezintlanu; indawo eseleyo yaba yindawo yamagumbi endawo yokwayama.', 'XHO22'),
(30, 41, 10, 'Phakathi kwamagumbi ibe ibububanzi beekubhite ezimashumi mabini obujikelezileyo, bayijikeleza indlu ngeenxa zonke.', 'XHO22'),
(30, 41, 11, 'Iminyango yendawo yokwayama yayikhangele endaweni eshiyekileyo; omnye umnyango ukhangele ngasentla, omnye umnyango ukhangele ngezantsi; ububanzi bendawo eseleyo baba ziikubhite ezintlanu, bajikeleza ngeenxa zonke.', 'XHO22'),
(30, 41, 12, 'Isakhiwo esibe siphambi kwendawo eqhiwukileyo, sasiziikubhite ezimashumi asixhenxe ububanzi baso ngecala lasentshonalanga; udonga lwesakhiwo eso lwaluziikubhite ezintlanu ububanzi balo, lwajikeleza ngeenxa zonke, neekubhite ezimashumi asithoba ubude.', 'XHO22'),
(30, 41, 13, 'Wayilinganisa ke indlu leyo, yaba ziikubhite ezilikhulu ubude bayo; indawo ejikelezileyo, nesakhiwo eso, neendonga zaso, zaba ziikubhite ezilikhulu ubude;', 'XHO22'),
(30, 41, 14, 'Ububanzi bomphambili wendlu nobendawo yecala elisingise empuma baba ziikubhite ezilikhulu.', 'XHO22'),
(30, 41, 15, 'Wabulinganisa ubude besakhiwo endaweni engasese, ebe singasemva kwaso, neevaranda zaso ngapha nangapha, iikubhite ezilikhulu;', 'XHO22'),
(30, 41, 16, 'Iiplanga ezicekethekileyo neefestile, ngeenxa zonke kuzo zontathu, zijikelezile emnyango, zaba ngamaplanga ngeenxa zonke; kwathabathela emgangathweni wesa ezifestileni;', 'XHO22'),
(30, 41, 17, 'kwada kwasa phezulu emnyango, endlwini ngaphakathi nangaphandle, naseludongeni lonke, ngaphakathi nangaphandle, kwaba yimilinganiso.', 'XHO22'),
(30, 41, 18, 'Kwaye kwenziwe iikerubhi namasundu, yaba ngumthi wesundu phakathi kweekerubhi neekerubhi; Ikerubhi nganye inobuso obubini;', 'XHO22'),
(30, 41, 19, 'yabubuso bendoda leyo bubheke esundwini ngapha, yabubuso bengonyama bubheke esundwini ngapha; yayakhiwe yonke indlu ngeenxa zonke;', 'XHO22'),
(30, 41, 20, 'Zenziwa zathabathela emgangathweni wesa phezu komnyango, iikerubhi namasundu, eludongeni lwetempile.', 'XHO22'),
(30, 41, 21, 'Imigubasi yetempile, yayinamacala amane alinganayo phambi kwengcwele; Imbonakalo yomnye, injengokubonakala kwenye.', 'XHO22'),
(30, 41, 22, 'Isibingelelo somthi sasiziikubhite ezintathu ukuphakama kwayo, nobude baso baba ziikubhite ezimbini; iimbombo zalo, nobude balo, neendonga zalo, bezizezemithi; wathi kum, Le yitafile ephambi koYehova.', 'XHO22'),
(30, 41, 23, 'Bezimbini iingcango zetempile nezengcwele.', 'XHO22'),
(30, 41, 24, 'Iminyango leyo ibinamaphiko amabini, iingcango zizijinga ezimbini; iiflegi ezimbini komnye umnyango, neeflegi ezimbini komnye umnyango.', 'XHO22'),
(30, 41, 25, 'Yaye phezu kwayo, kwiingcango zetempile, kwaye kwenziwe iikerubhi namasundu ezindongeni kwakukho amaplanga ashinyeneyo ngaphandle kweveranda.', 'XHO22'),
(30, 41, 26, 'Iifestile ezicekethekileyo namasundu zazingapha nangapha, emacaleni evaranda nasemagumbini endawo yokwayama kuyo indlu, nezinyuko.', 'XHO22'),
(30, 42, 1, 'Waphuma nam, waya entendelezweni engaphandle, ngendlela yasentla; waza wandisa kwigumbi elimalunga negumbi elahlukileyo, elisecaleni kwesakhiwo eso, kwicala elingasentla.', 'XHO22'),
(30, 42, 2, 'Phambi kobude obuziikubhite ezilikhulu kwakukho umnyango ongasemntla, ububanzi buziikubhite ezingamashumi amahlanu.', 'XHO22'),
(30, 42, 3, 'Malunga neekubhite ezimashumi mabini zentendelezo ephakathi, malunga nombekelelo wamatye wentendelezo engaphandle, bekukho iivaranda, zikhangelene, zineevaranda ezintathu.', 'XHO22'),
(30, 42, 4, 'Phambi kwamagumbi kwakukho indawo yokuma, ububanzi bayo buziikubhite ezilishumi; wona amasango awo ngangasentla.', 'XHO22'),
(30, 42, 5, 'Ke amagumbi aphezulu ayemafuphine, kuba iivaranda beziphakamile kunawaphantsi naphakathi esakhiweni;', 'XHO22'),
(30, 42, 6, 'Ngokuba ayekwimigangatho emithathu, kodwa ayengenazintsika njengeentsika zasemasimini; ngenxa yoko isakhiwo sasikumgangatho ophantsi kunowasezantsi nophakathi ukusuka emhlabeni.', 'XHO22'),
(30, 42, 7, 'Udonga olube lungaphandle kwamagumbi okudlela, olukhangele entendelezweni engaphandle, phambi kwaloo magumbi, lwaba ziikubhite ezimashumi mahlanu ubude.', 'XHO22'),
(30, 42, 8, 'Ngokuba ubude bamagumbi entendelezo engaphandle baba ziikubhite ezimashumi mahlanu; nanko ke kukho amasimi alikhulu ngasetempileni.', 'XHO22'),
(30, 42, 9, 'Ngaphantsi kwala magumbi, umnyango wawukhangele empumalanga xa umntu ephuma entendelezweni engaphandle.', 'XHO22'),
(30, 42, 10, 'Ekuphakameni kodonga lwentendelezo, kusingise empumalanga, ekhangelene nendawo esecaleni, ephambi kwesakhiwo.', 'XHO22'),
(30, 42, 11, 'Indlela phambi kwawo yayibonakala injalo amagumbi, esingise entla, nobude bawo nobubanzi bawo; kwaye zonke iilwandle zazimi ngendlela yazo neengcango zazo.', 'XHO22'),
(30, 42, 12, 'Njengoko injalo iminyango yawo lawo, injalo neminyango yamagumbi abe ekhangele ngezantsi;', 'XHO22'),
(30, 42, 13, 'Wahlabela mgama wathi kum: “Amagumbi angasentla nangasezantsi phambi kwendawo engasese, ngawo lawo amagumbi angcwele, apho ababingeleli abasondela kuYehova baya kudlela khona izinto ezingcwele; nomnikelo wokudla, nedini lesono, nedini letyala; ngokuba le ndawo ingcwele.', 'XHO22'),
(30, 42, 14, 'Bakungena ababingeleli, abaphumi engcweleni, baye entendelezweni engaphandle; ngokuba bangcwele; yaye baya kunxiba ezinye iimpahla baze basondele kwizinto zabantu.', 'XHO22'),
(30, 42, 15, 'Wathi akugqiba ukuyilinganisa indlu engaphakathi, wandizisa esangweni elibheke empumalanga, walinganisa ngeenxa zonke;', 'XHO22'),
(30, 42, 16, 'Walinganisa icala lasempumalanga ngengcongolo yokulinganisa; laba ziikubhite ezimakhulu mahlanu ngenduku yokulinganisa.', 'XHO22'),
(30, 42, 17, 'Walinganisa icala langasentla; iikubhite ezimakhulu mahlanu, ngengcongolo yokulinganisa, ngeenxa zonke.', 'XHO22'),
(30, 42, 18, 'Walinganisa icala lasezantsi, iikubhite ezimakhulu mahlanu, ngentonga yokulinganisa.', 'XHO22'),
(30, 42, 19, 'Wabheka ecaleni lasentshonalanga, walinganisa amakhulu amahlanu eekubhite ngentonga yokusila.', 'XHO22'),
(30, 42, 20, 'Wayilinganisa emacaleni omane, yaba ludonga ngeenxa zonke;', 'XHO22'),
(30, 43, 1, 'Yandisa esangweni, isango elisingise empumalanga;', 'XHO22'),
(30, 43, 2, 'Nabo ke ubuqaqawuli bukaThixo kaSirayeli, buvela empumalanga, nelizwi lakhe linjengesandi samanzi amaninzi;', 'XHO22'),
(30, 43, 3, 'Imbonakalo ke endayibonayo, yayinjengembonakalo leya, ndayibonayo ukuba ndiya kuwonakalisa umzi; ubuso bebunjengobuso endabubonayo emlanjeni oyiKebhare; ndawa ngobuso.', 'XHO22'),
(30, 43, 4, 'Ubuqaqawuli bukaYehova bangena endlwini ngesango elibheke empumalanga.', 'XHO22'),
(30, 43, 5, 'Wandithabatha umoya, wandisa entendelezweni engaphakathi; nabo ke ubuqaqawuli bukaYehova buyizalisile indlu.', 'XHO22'),
(30, 43, 6, 'Ndaliva lithetha nam ngaphakathi endlwini; yema indoda leyo ecaleni kwam.', 'XHO22'),
(30, 43, 7, 'Wathi kum, Nyana womntu, nantsi indawo yetrone yam, indawo yeenyawo zam, apho ndiya kuhlala khona phakathi koonyana bakaSirayeli ngonaphakade, lingayenzi inqambi igama lam elingcwele indlu kaYehova. Sirayeli kwakhona nookumkani bazo neempahla zabo, nezidumbu zookumkani bazo kwiindawo zabo ezizukileyo;', 'XHO22'),
(30, 43, 8, 'ngokubeka iingcango zabo ezingcangweni zam, nemigubasi yazo emigubasini yeminyango yam, balubeka eludongeni phakathi kwam nabo, balenze inqambi igama lam elingcwele ngamasikizi abo abawenzileyo; ke ngoko, ndabatshabalalisa ngomsindo wam.', 'XHO22'),
(30, 43, 9, 'Ngoku ke mabasuse ubuhenyu babo nezidumbu zookumkani babo kum, ndihlale phakathi kwabo ngonaphakade.', 'XHO22'),
(30, 43, 10, 'Nyana womntu, indlu kaSirayeli yibonise indlu, ukuze badane ngenxa yobugwenxa babo; kwaye ulinganise umzekelo.', 'XHO22'),
(30, 43, 11, 'Ukuba bathe baliva ihlazo ngenxa yezinto zonke abazenzileyo, babonise ukuma kwendlu le, nembonakalo yayo, neevaranda zayo, neengcango zayo, neengcango zayo, nezimo zayo zonke, nemiyalelo yayo yonke, nezimo zayo zonke, nezimo zayo zonke imithetho yayo, uyibhale phantsi bazenze phambi kwazo, bakugcine konke ukuma kwayo, nemimiselo yayo yonke, bayigqibe.', 'XHO22'),
(30, 43, 12, 'Nguwo lo umyalelo wayo indlu; Entla kwentaba, umda wayo uya kuba yingcwele kangcwele nguwo lo umyalelo wayo indlu.', 'XHO22'),
(30, 43, 13, 'Yiyo le imilinganiso yesibingelelo ngokwekubhite: yikubhite yikubhite nobubanzi besandla; umzantsi woba yikubhite enye, ububanzi bube yikubhite enye; yoba ngasentla kwesibingelelo.', 'XHO22'),
(30, 43, 14, 'Kuthabathela emgangathweni kuse esitulweni esiphantsi, koba ziikubhite ezimbini ukuphakama, nobubanzi bube yikubhite enye; kuthabathela esihlalweni esincinane kuye kwesikhulu, koba ziikubhite ezine, ububanzi bube yikubhite enye.', 'XHO22'),
(30, 43, 15, 'Ke ngoko, isibingelelo siya kuba ziingcango ezine; ukusuka esibingelelweni ukuya phezulu kuya kubakho iimpondo ezine.', 'XHO22'),
(30, 43, 16, 'Ubude besibingelelo boba ziikubhite ezilishumi elinambini, ububanzi baso bube ziikubhite ezilishumi elinambini, amacala aso alingane.', 'XHO22'),
(30, 43, 17, 'Ubude balo boba ziikubhite ezilishumi elinesine, ububanzi bube ziikubhite ezilishumi elinesine kumacala alo omane; Ukujikeleza komandlalo koba sisiqingatha sekubhite; umzantsi wawo wawuwenziwe wamnye, ngapha nangapha; nezinyuko zayo ziya kubheka ngasempuma.', 'XHO22'),
(30, 43, 18, 'Waqhubeka wathi kum: “Nyana womntu, itsho iNkosi enguMongami uYehova; Yiyo le imimiselo yesibingelelo ekusenziwe kwaso, ukwenziwa kwaso amadini anyukayo, nokutshizwa kwaso ngegazi.', 'XHO22'),
(30, 43, 19, 'Nikani ababingeleli abangabaLevi koonyana bakaTsadoki, abaya kundikhonza, inkomo ibe lidini lesono; itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 43, 20, 'Thabatha egazini layo, uliqabe ezimpondweni zaso zone, nasezimbombeni zone zomzi, naseludinini ngeenxa zonke; ke ngoko, yicoce, icoce.', 'XHO22'),
(30, 43, 21, 'Uthabathe inkunzi entsha yenkomo yedini lesono, uyitshise endaweni emisiweyo yayo indlu, ngaphandle kwengcwele.', 'XHO22'),
(30, 43, 22, 'Ngomhla wesibini wosondeza inkunzi yebhokhwe exhonti egqibeleleyo, ibe lidini lesono; bahlambulule isibingelelo njengoko behlanjululwe ngenkomo.', 'XHO22'),
(30, 43, 23, 'Wakuba ugqibile ukuyicoca, wozisa inkunzi entsha, inkomo egqibeleleyo, nenkunzi yegusha egqibeleleyo;', 'XHO22'),
(30, 43, 24, 'Wowusondeza phambi koYehova, ababingeleli bagalele ityuwa phezu kwawo, bawenze ube lidini elinyukayo kuYehova.', 'XHO22'),
(30, 43, 25, 'Iintsuku ezisixhenxe uya kunikela inkunzi yebhokhwe exhonti ibe lidini lesono ngemini; ilungiselwe inkunzi entsha, nenkunzi yemvu, empahleni emfutshane, zigqibelele zonke.', 'XHO22'),
(30, 43, 26, 'Iintsuku ezisixhenxe bosihlambulula isibingelelo, basihlambulule; kwaye mabazithembise bona.', 'XHO22'),
(30, 43, 27, 'Emva kwezo ntsuku, ngomhla wesibhozo, ababingeleli bowenza amadini enu anyukayo, nemibingelelo yenu yombulelo esibingelelweni; yaye ndiya kukholiswa nguwe, itsho iNkosi uYehova.', 'XHO22'),
(30, 44, 1, 'Wandibuyisela emnyango wengcwele ongaphandle, okhangele empumalanga; kwaye yavalwa.', 'XHO22'),
(30, 44, 2, 'Wathi uYehova kum; Olu cango lumele luvalwe, lungavulwa, kungangeni bani ngalo; ngokuba uYehova, uThixo kaSirayeli, ungene ngalo, liya kuvalwa ke.', 'XHO22'),
(30, 44, 3, 'Le nto yokumkani; sihlale khona isikhulu, sidle isonka phambi koYehova; wangena ngevaranda yesango waphuma wahamba.', 'XHO22'),
(30, 44, 4, 'Yandisa ngendlela yesango lasentla, phambi kwendlu; Ndakhangela, nango ubuqaqawuli bukaYehova buyizalisile indlu kaYehova, ndawa ngobuso.', 'XHO22'),
(30, 44, 5, 'Wathi uYehova kum: “Nyana womntu, phulaphula, ubone ngamehlo akho, uve ngeendlebe zakho, konke endikuxelela kona ngemithetho yonke yendlu kaYehova, nemithetho yayo yonke; uze unikele ingqalelo emnyango wendlu, nayo yonke indlela yokuphuma engcweleni.', 'XHO22'),
(30, 44, 6, 'Uze uthi kubavukeli, kwindlu kaSirayeli, ‘Itsho iNkosi enguMongami uYehova; Kwanele ngawo onke amasikizi enu, ndlu kaSirayeli.', 'XHO22'),
(30, 44, 7, 'Ngenxa enokuba ningenise abasemzini engcweleni yam, njengesiqalekiso entliziyweni yam, nesiqalekiso enyameni yam, ukuba babe sengcweleni yam, ukuze niyenze inqambi eyindlu yam, ekusikhupheni kwenu isonka sam, namanqatha, negazi; bandimisa ke ngenxa yamasikizi akho onke.', 'XHO22'),
(30, 44, 8, 'Aniyigcinanga inkonzo yengcwele yam;', 'XHO22'),
(30, 44, 9, 'Utsho uYehova ukuthi, Makungabikho mntu wasemzini ontliziyo ikrexezayo, ongathembekanga ngenyama, oya kungena engcweleni yam, ongowasemzini ophakathi koonyana bakaSirayeli.', 'XHO22'),
(30, 44, 10, 'Ke bona abaLevi, abakreqayo kum, ekundwendweni koonyana bakaSirayeli, abandwendwela kude kum, balandela izigodo zabo; bobuthwala nabo ubugwenxa babo.', 'XHO22'),
(30, 44, 11, 'Boba ngabakhonzi engcweleni yam, nabagcina amasango endlu le, nabalungiselelayo kuyo indlu; bobingelela idini elinyukayo nombingelelo wabantu, beme phambi kwabo, balungiselele.', 'XHO22'),
(30, 44, 12, 'Ngenxa yokuba babebakhonza phambi kwemifanekiso yabo eqingqiweyo baza babangela ukuba indlu kaSirayeli iwekele ebubini; ngako oko ndibaphakamisele isandla sam, itsho iNkosi uYehova, ukuba babuthwale ubugwenxa babo.', 'XHO22'),
(30, 44, 13, 'Abayi kufika kum ukuze bandenzele ububingeleli, bangasondeli kwiingcwele zam zonke, kwingcwele kangcwele; baya kulithwala ihlazo labo, namasikizi abo abawenzileyo.', 'XHO22'),
(30, 44, 14, 'Ndiza kubamisa phezu kwenkonzo yendlu ngokwenkonzo yayo yonke nangokwezinto zonke ezenzelwa kuyo.', 'XHO22'),
(30, 44, 15, 'Ke bona ababingeleli babaLevi, oonyana bakaTsadoki, abawugcinayo umsebenzi wengcwele yam, ekundinikeleni kwabo oonyana bakaSirayeli, bosondela bona kum, ukuba bandilungiselele, beme ngakum, ukuba bandinike ioli kunye namafutha igazi, itsho iNkosi uYehova.', 'XHO22'),
(30, 44, 16, 'Bongena bona engcweleni yam, basondele esithebeni sam, ukuba bandikhonze, bagcine inkonzo yam.', 'XHO22'),
(30, 44, 17, 'Ekungeneni kwabo ngamasango entendelezo ephakathi, bonxiba ilinen emhlophe; kungabikho boya kuzo ekukhonzeni kwabo emasangweni entendelezo ephakathi, nakwintendelezo ephakathi.', 'XHO22'),
(30, 44, 18, 'Bafanele bathwale iinkanda zelinen entloko, babhinqe iibhulukhwe zelinen ezinqeni zabo; musa ukuzibophelela kuyo nantoni na ebilayo.', 'XHO22'),
(30, 44, 19, 'Ekuphumeni kwabo baya entendelezweni engaphandle, entendelezweni engaphandle kwabantu, bazihluba iingubo zabo ababefudula belungiselela ngazo, bazibeke emagumbini angcwele, bambathe ngubo zimbi; yaye abazange bazihlambe ngeempahla zabo.', 'XHO22'),
(30, 44, 20, 'Mabangazichebi iintloko zabo, bangaziyeki zibe nde iinwele zabo; baya kulinganisa iintloko zabo kuphela.', 'XHO22'),
(30, 44, 21, 'Kananjalo akukho mbingeleli unako ukusela iwayini ekungeneni kwakhe kwintendelezo ephakathi.', 'XHO22'),
(30, 44, 22, 'Abafanele bathabathe abafazi babo mhlolokazi okanye oshiywe lixesha, kodwa bafanele bathabathe iintombi zesizwe sendlu kaSirayeli okanye umhlolokazi owayenombingeleli ngaphambili.', 'XHO22'),
(30, 44, 23, 'Ubafundise abantu bam, ukubahlula okungcwele kokungangcwele, ukuba bahlule phakathi kwento ehlambulukileyo nento ehlambulukileyo;', 'XHO22'),
(30, 44, 24, 'Yaye ekulweni baya kuma ematyaleni; kwaye baya kuligweba ngokwemithetho yam; niyigcine imiyalelo yam nemimiselo yam emabandleni am onke; bazingcwalise iisabatha zam.', 'XHO22'),
(30, 44, 25, 'Abasondelanga emntwini ofileyo, ukuba bamenze inqambi; kodwa ngoyise, nonina, nonyana, nentombi yakhe, nomzalwana wakhe, nodade wabo, abangabanga nandoda, bangazenza iinqambi bona, bona ngokwabo.', 'XHO22'),
(30, 44, 26, 'Emva kokuhlanjululwa kwakhe, babalelwa iintsuku ezisixhenxe.', 'XHO22'),
(30, 44, 27, 'Yaye ngemini yokungena kwakhe endaweni engcwele, kwintsapho elapho ukuze ikukhonze endaweni engcwele, umele azise umnikelo wakhe wesono,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 44, 28, 'Oko koba lilifa labo, ibe ndim ilifa labo; musani ukubanika ubutyebi kwaSirayeli; Yimpahla yabo.', 'XHO22'),
(30, 44, 29, 'Idini elinyukayo, nedini lesono, nedini letyala, bolidla bona; yaye yonke into engcwalisiweyo kwaSirayeli yoba yeyabo.', 'XHO22'),
(30, 44, 30, 'Iingqalo kwiinto zonke zokuqala, nokusondezwa kwasemlilweni kweento zonke zokudla kwenu kwasemlilweni, kweentlobo zonke, yoba yeyombingeleli; umnike umbingeleli intlama yakho yokuqala, ukuze asikelele endlwini yakho.', 'XHO22'),
(30, 44, 31, 'Ababingeleli mabangayidli yonke into ezifeleyo neqwengiweyo, nokuba kusezintakeni, nokuba kusezinkomeni.', 'XHO22'),
(30, 45, 1, 'Ukuba nilabela ilizwe ngeqashiso, nosondeza isahlulo esikhethekileyo kuYehova kulo ilizwe; Ubude babuziikubhite ezingamashumi amabini anesihlanu amawaka, nobubanzi buziikubhite ezilishumi lamawaka. Yoba ngcwele emideni yayo yonke ngeenxa zonke.', 'XHO22'),
(30, 45, 2, 'Kubo apho yoba ngamakhulu amahlanu obude, ibe ngamakhulu amahlanu ububanzi, ibe macala onke; nedlelo lakhe laba ziikubhite ezimashumi mahlanu ngeenxa zonke.', 'XHO22'),
(30, 45, 3, 'Ngalo mlinganiso wokulinganisa ubude amashumi amabini anesihlanu amawaka, nobubanzi bamawaka alishumi; kwaye kuyo kuya kubakho iNgcwele yeeNgcwele kunye neNgcwele yeeNgcwele.', 'XHO22'),
(30, 45, 4, 'Indawo engcwele kulo ilizwe iya kuba yeyababingeleli, abakhonzi bengcwele, abasondela ukuba bakhonze uYehova; yoba yindawo yezindlu zabo, nendawo engcwele yengcwele.', 'XHO22'),
(30, 45, 5, 'Amashumi amabini anesihlanu amawaka ubude, neshumi lamawaka ububanzi, libe lelabaLevi, abakhonzi bendlu, libe lilifa labo iindawo ezimashumi mabini.', 'XHO22'),
(30, 45, 6, 'Nosabela isixeko sibe ngamawaka amahlanu obubanzi, namashumi amabini anesihlanu amawaka ubude, ngokomrhumo wengcwele; uya kuba ngowendlu yonke kaSirayeli.', 'XHO22'),
(30, 45, 7, 'Soba nesabelo sokumkani kwelinye icala, nangaphaya komsondezo wengcwele, nobutyebi besixeko, ecaleni komsondezo wengcwele, naphambi kwelizwe lesixeko ngasentshonalanga ngakwicala lasentshonalanga nasempumalanga, kude kuse empumalanga; ubude bujongane nenye indawo, kuthabathela kumda wasentshona kuse emdeni wasempumalanga.', 'XHO22'),
(30, 45, 8, 'Ilizwe loba yindyebo yakhe kwaSirayeli, nezikhulu zam zingabuyi zibe sabaxinzelela phantsi abantu bam; yaye ilizwe eliseleyo baya kulinika indlu kaSirayeli ngokweentsapho zabo.', 'XHO22'),
(30, 45, 9, 'Itsho iNkosi uYehova ukuthi, Niyanele, zikhulu zakwaSirayeli;', 'XHO22'),
(30, 45, 10, 'Uya kuba nomlinganiselo olungileyo nebhafu elungileyo nebhafu elungileyo.', 'XHO22'),
(30, 45, 11, 'Iefa nesiqu mazibe ngumlinganiso mnye, ukuze umbhobho uthabathe isahlulo seshumi sehomere, ne-efa ithabathe isahlulo seshumi sehomere; iqabane lakhe malilandele ihomere.', 'XHO22'),
(30, 45, 12, 'Ishekele yoba ziigera ezimashumi mabini; Amashumi amabini eeshekele, amashumi amabini anesihlanu eeshekele, neshumi elinesihlanu leeshekele kuni.', 'XHO22'),
(30, 45, 13, 'Kuko oku ukudla kwasemlilweni eniya kuwuzisa; nesahlulo sesithandathu se-efa yehomere yengqolowa, wanikela isahlulo sesithandathu se-efa yehomere yerhasi.', 'XHO22'),
(30, 45, 14, 'Umlinganiso weoli, webhate yeoli, wenze isahlulo seshumi sebhate yonkozo, ezibhate ezilishumi ehomereni ke; kuba iibhate ezilishumi yihomere.', 'XHO22'),
(30, 45, 15, 'Nexhwane libe linye empahleni emfutshane engamakhulu amabini, emakriweni achumileyo akwaSirayeli; libe lidini elinyukayo, libe lidini elinyukayo, libe lidini lombulelo, ukubacamagushela; itsho iNkosi uYehova.', 'XHO22'),
(30, 45, 16, 'Bonke abantu belizwe eli bosinikela lo mnikelo kwisikhulu sakwaSirayeli.', 'XHO22'),
(30, 45, 17, 'Yaye yoba yeyesikhulu iminikelo etshiswayo neminikelo yokudla neminikelo ethululwayo emithendelekweni nangeenyanga ezintsha nangesabatha ngayo yonke imithendeleko yendlu kaSirayeli; uya kulilungiselela idini lesono nomnikelo wokudla, nedini elinyukayo, nemibingelelo yoxolo, yokucamagusha ngenxa yezono zendlu kaSirayeli.', 'XHO22'),
(30, 45, 18, 'Itsho iNkosi uYehova ukuthi, Ngenyanga yokuqala, ngolokuqala enyangeni leyo, wothabatha inkunzi entsha, inkomo egqibeleleyo, uyihlambulule ingcwele;', 'XHO22'),
(30, 45, 19, 'Umbingeleli wocaphula egazini ledini lesono, aliqabe emigubasini yendlu le, nasezimbombeni zone zesibingelelo, nasemigubasini yentendelezo ephakathi;', 'XHO22'),
(30, 45, 20, 'Uze wenjenjalo nangolwesixhenxe enyangeni leyo, ngenxa yabo bonke abaphambukayo, nabamsulwa; ngoko ke kufuneka ulungelelanise indlu.', 'XHO22'),
(30, 45, 21, 'Ngeyokuqala inyanga, ngosuku lweshumi elinesine enyangeni leyo, noba nepasika, umthendeleko weentsuku ezisixhenxe; zodliwa izonka ezingenagwele.', 'XHO22'),
(30, 45, 22, 'Ngaloo mini isikhulu solungiselela inkunzi entsha yenkomo, ibe lidini lesono ngenxa yaso nangenxa yabantu bonke belizwe.', 'XHO22'),
(30, 45, 23, 'Ngeentsuku ezisixhenxe zomthendeleko nosondeza kuYehova idini elinyukayo: iinkunzi ezintsha zeenkomo zibe sixhenxe, neenkunzi zeegusha ezigqibeleleyo zibe sixhenxe; nenkunzi yebhokhwe exhonti ibe lidini lesono yonke imini.', 'XHO22'),
(30, 45, 24, 'Wenza ukudla kwasemlilweni, iefa yengqolowa, ne-efa enkunzini yegusha, nehin yeoli nge-efa.', 'XHO22'),
(30, 45, 25, 'Ngenyanga yesixhenxe, ngosuku lweshumi elinesihlanu enyangeni leyo, uya kwenza okufanayo ngomthendeleko weentsuku ezisixhenxe, ngokwedini lesono, nangokwedini elinyukayo, nangokweminikelo yokudla, nangokweoli.', 'XHO22'),
(30, 46, 1, 'Itsho iNkosi uYehova ukuthi; Isango lentendelezo ephakathi, elikhangele empumalanga, lovulwa iintsuku ezintandathu zokusebenza; kodwa ngomhla wesabatha liya kuvulwa, nangomhla wokuthwasa kwenyanga livulwe.', 'XHO22'),
(30, 46, 2, 'Isikhulu songena ngevaranda yesango elingaphandle, simi emgubasini wesango, ababingeleli balungiselele idini laso elinyukayo, nemibingelelo yaso yombulelo, siqubude ekungeneni kwesango; nandule ke uphume; kodwa isango aliyi kuvalwa kude kuhlwe.', 'XHO22'),
(30, 46, 3, 'Ngendlela efanayo, abantu belizwe baya kuqubuda emnyangweni weli sango phambi koYehova ngeesabatha nasekuthwaseni kwenyanga.', 'XHO22'),
(30, 46, 4, 'Umnikelo otshiswayo esiya kuwuzisa isikhulu kuYehova ngomhla wesabatha, woba ziimvana ezintandathu ezigqibeleleyo, nenkunzi yegusha egqibeleleyo;', 'XHO22'),
(30, 46, 5, 'Umnikelo wokudla woba yiefa enkunzini yegusha, nomnikelo wokudla kuzo iimvana ube ngangoko sinako ukukunika, ibe yihin yeoli kuyo iefa.', 'XHO22'),
(30, 46, 6, 'Ngosuku lokuthwasa kwenyanga, liya kuba yinkunzi entsha yenkomo, egqibeleleyo, neemvana ezintandathu, nenkunzi yegusha; baya kufuna isiphene.', 'XHO22'),
(30, 46, 7, 'Solungiselela ukudla kwasemlilweni, iefa enkunzini entsha, iefa enkunzini yegusha, kuzo iimvana esifunyenweyo isandla saso, ibe yihin yeoli kuyo iefa.', 'XHO22'),
(30, 46, 8, 'Ekungeneni kwesikhulu, songena ngevaranda yesango, siphume ngendlela yaso.', 'XHO22'),
(30, 46, 9, 'Kodwa ekufikeni kwabantu belizwe phambi koYehova ebudeni bemithendeleko, lowo ungena ngeSango laseMntla ukuze athandaze uya kuphuma ngeSango lasemzantsi; yaye lowo ungena ngesango lasemzantsi uya kuphuma ngesango lasemntla.', 'XHO22'),
(30, 46, 10, 'Yaye isikhulu phakathi kwabo songena ekungeneni kwabo; bathi ekuphumeni kwabo, baphume.', 'XHO22'),
(30, 46, 11, 'Emithendelekweni nasemithendelekweni umnikelo wokudla okuziinkozo woba yiefa enkunzini yenkomo, ne-efa kuyo inkunzi yegusha, nakuzo iimvana, ibe ngangoko anokunikela ngako, ibe yihin yeoli kuyo iefa.', 'XHO22'),
(30, 46, 12, 'Ukuba ukumkani uthe walungiselela ukwenza idini elinyukayo, nokuba yimibingelelo yombulelo kuYehova, womvulela isango elibheke empumalanga, alilungiselele idini lakhe elinyukayo nemibingelelo yakhe yombulelo, njengoko ebesenza ngomhla wesabatha; kufuneka uphume ngoku; yaye emva kokuba ephumile, omnye uyavala isango.', 'XHO22'),
(30, 46, 13, 'Uze wenze idini elinyukayo kuYehova ngemini, ngemvana emnyaka mnye, egqibeleleyo; woyilungisa imiso ngemiso.', 'XHO22'),
(30, 46, 14, 'Wolungiselela yona imiso ngemiso, ukudla kwasemlilweni, sibe sisahlulo sesithandathu se-efa, neoli ibe sisahlulo sesithathu sehin yokuthambisa umgubo ocoliweyo; loba lidini elinyukayo lamaxesha onke kuYehova, libe ngumthetho ongunaphakade.', 'XHO22'),
(30, 46, 15, 'Ngoko ntsasa nganye bamele balungiselele imvana nomnikelo wokudla okuziinkozo neoli njengomnikelo otshiswayo othe rhoqo.', 'XHO22'),
(30, 46, 16, 'Itsho iNkosi uYehova; Ukuba ukumkani uthe wampha omnye woonyana bakhe isipho, ilifa lakhe loba ngoonyana bakhe; liya kuba lilifa labo.', 'XHO22'),
(30, 46, 17, 'Ukuba sithe sampha omnye kubakhonzi baso isipho elifeni laso, soba sesalowo kude kube ngumnyaka wenkululeko; emva koko iya kubuyela kukumkani, libe lilifa loonyana bakhe kubo.', 'XHO22'),
(30, 46, 18, 'Isikhulu asibanyathelanga ilifa labantu, ukuba sibagxothe empahleni yabo; Kodwa uya kubanika oonyana bakhe ilifa ebutyebini bakhe, ukuze bangabi lusali abantu bam, elowo elifeni lakhe.', 'XHO22'),
(30, 46, 19, 'Emva kokundizisa emnyango osecaleni lesango, emagumbini okudlela angcwele ababingeleli, akhangele ngasemntla;', 'XHO22'),
(30, 46, 20, 'Waqhubeka wathi kum: “Le yindawo apho ababingeleli baya kuphekela khona idini letyala nedini lesono, apho baya kuphekela khona idini elinyukayo; ukuze bangayikhuphi ngaphandle, ukuba bangcwalise abantu.', 'XHO22'),
(30, 46, 21, 'Wandisa entendelezweni engaphandle, wandisa emagumbini omane entendelezo; nanko kukho intsimi ezimbombeni zentsimi.', 'XHO22'),
(30, 46, 22, 'Emagumbini omane entendelezo kwakukho iintendelezo ezihlangeneyo, ubude baso buziikubhite ezimashumi mane, ububanzi buziikubhite ezimashumi mathathu;', 'XHO22'),
(30, 46, 23, 'Kwaye kwenziwa intsika ngeenxa zonke kuzo zone;', 'XHO22'),
(30, 46, 24, 'Wathi kum, Ezi ziindawo zabapheki, apho abakhonzi bendlu baya kupheka khona umbingelelo wesidlangalala.', 'XHO22'),
(30, 47, 1, 'Yandibuyisela emnyango wayo indlu; Ndabona amanzi ephuma phantsi komnyango wendlu, esingise empumalanga; ngokuba umphambili wendlu ubumi ngasempumalanga, amanzi abe evela ezantsi ecaleni lasempumalanga kuyo indlu, ngecala lasezantsi leziko.', 'XHO22'),
(30, 47, 2, 'Wandisa ngendlela yesango langasentla, wandihambisa ngendlela engaphandle, wesa esangweni elingaphandle, ngendlela ekhangele empumalanga; ndabona amanzi empompoza kwicala lasekunene.', 'XHO22'),
(30, 47, 3, 'Xa indoda leyo iphethe umtya esandleni sayo, yaphuma yasinga empumalanga, yalinganisa iwaka leekubhite, yandisa emanzini; amanzi enyuke efikelela emaqatheni.', 'XHO22'),
(30, 47, 4, 'Wabuya walinganisa iwaka, wandithuthuzela emanzini; amanzi ayefika emadolweni. Wabuya walinganisa iwaka, wandithuthuzela; amanzi ebefika esinqeni.', 'XHO22'),
(30, 47, 5, 'Walinganisa iwaka; ibingumlambo endingenakuwuwela, kuba amanzi ebenyuka njengamanzi odada, njengomlambo ongawelekiyo.', 'XHO22'),
(30, 47, 6, 'Wathi kum, Ubonile na, nyana womntu? Yandibuyisela elunxwemeni lomlambo.', 'XHO22'),
(30, 47, 7, 'Ekubuyeni kwam, nanko kukho imithi emininzi elunxwemeni lomlambo, ngapha nangapha;', 'XHO22'),
(30, 47, 8, 'Yathi kum, La manzi aqukuqela esiya ezweni lasempumalanga, ehla entlango, angena elwandle;', 'XHO22'),
(30, 47, 9, 'Kuya kuthi ke, yonke into ephilileyo, enyakazelayo ezindaweni zonke othe wafika kuzo umlambokazi lowo, iphile; zibe zininzi kunene iintlanzi; ngokuba osuka efike khona la manzi, ngokuba ophiliswa; kwaye yonke into iya kuphila apho ufika khona umlambo.', 'XHO22'),
(30, 47, 10, 'Kuya kuma ababambisi beentlanzi phezu kwawo, bathabathele e-En-gedi, baye e-En-heglayim; iya kuba yindawo yokwaneka iminatha; ziya kuba ngokohlobo lwazo iintlanzi zabo, njengeentlanzi zoLwandle Olukhulu, ukuba zininzi kunene.', 'XHO22'),
(30, 47, 11, 'Ke iindawo zalo ezinodaka nemigxobhozo yalo aziyi kuphiliswa; baya kunikwa ityuwa.', 'XHO22'),
(30, 47, 12, 'Kufuphi nomlambo, ngaseludinini lwawo, ngapha nangapha, yonke imithi iya kuhluma edliwayo, engabuni amagqabi, nesiqhamo sawo singatshi; liya kuthwala isiqhamo esitsha ngeenyanga zalo, ngokuba amanzi alo aphuma engcweleni;', 'XHO22'),
(30, 47, 13, 'Itsho iNkosi uYehova ukuthi, Yiyo le imida eniya kuyidla ilifa ilizwe ngokwezizwe ezilishumi elinazibini zakwaSirayeli: uYosefu woba namacandelo amabini.', 'XHO22'),
(30, 47, 14, 'Ilizwe niya kulidla ilifa, elowo kummelwane wakhe, ngokuba ndasiphakamisa isandla sam ukuba ndilinike ooyihlo; loba lilifa lenu eli lizwe.', 'XHO22'),
(30, 47, 15, 'Nguwo lo umda welizwe langasentla, wothabathela kuLwandle Olukhulu ngendlela yeHetolone, ukuba baye eSedade;', 'XHO22'),
(30, 47, 16, 'neHamati, neBherota, neSibrayim, phakathi komda weDamasko nomda waseHamati; eHatsarhathikone elunxwemeni lwaseHaurane.', 'XHO22'),
(30, 47, 17, 'Umda uthabathela elwandle, uthabathele eHatsare-enon, umda waseDamasko; Licala langasentla ke.', 'XHO22'),
(30, 47, 18, 'Ngecala lasempumalanga wolinganisa iHawuran neDamasko neGiliyadi, uthabathela ezweni lakwaSirayeli, iYordan; Licala lasempumalanga ke eli.', 'XHO22'),
(30, 47, 19, 'Ngecala lasezantsi ukuya ngasezantsi, uthabathela kwaTamare, wesa emanzini aseMeribhane-Kadeshe, umlambo uya kufika eLwandle oluKhulu. Licala lasezantsi ke elo, ukuya ngasezantsi.', 'XHO22'),
(30, 47, 20, 'Ngasentshonalanga koLwandle Olukhulu, ukusuka emdeni, kude kuse eHamati. Licala lasentshonalanga ke elo.', 'XHO22'),
(30, 47, 21, 'Zabelani ke eli lizwe ngokwezizwe zakwaSirayeli.', 'XHO22'),
(30, 47, 22, 'Lahlula ngamaqashiso, libe lilifa kuni, nakubaphambukeli abaphambukele kuni, abazele oonyana phakathi kwenu; kuwe baya kuba njengabazalelwe kwelo zwe phakathi koonyana bakaSirayeli; aya kudla ilifa ndawonye nawe phakathi kwezizwe zakwaSirayeli.', 'XHO22'),
(30, 47, 23, 'Nomnika ilifa lakhe emzalwaneni womphambukeli, ophambukele kuni; itsho iNkosi uYehova.', 'XHO22'),
(30, 48, 1, 'Ngawo la amagama ezizwe. Ithabathele ekupheleni ngasentla, iye emdeni wendlela yaseHetelone, iye eHamati naseHatsare-enon, umda waseDamasko ngasentla, use emdeni waseHamati; Kuba la ngamacala alo ngasempumalanga nasentshonalanga; inxalenye kaDan.', 'XHO22'),
(30, 48, 2, 'Ngasemdeni kaDan, kuthabathela kumda wasempumalanga, kuse emdeni wasentshona, nguAshere.', 'XHO22'),
(30, 48, 3, 'Ngasemdeni ka-Ashere, kuthabathela ecaleni lasempumalanga, kuse kumda wasentshonalanga, iNafetali ibe nye.', 'XHO22'),
(30, 48, 4, 'Ngasemdeni kaNafetali, kuthabathela ecaleni lasempumalanga, kuse emdeni wasentshona, yoba nguManase.', 'XHO22'),
(30, 48, 5, 'Ngasemdeni kaManase, kuthabathela ecaleni lasempumalanga, kuse emdeni wasentshona, uEfrayim elinye.', 'XHO22'),
(30, 48, 6, 'Ngasemdeni kaEfrayim, kuthabathela empumalanga kuse entshonalanga, nguRubhen.', 'XHO22'),
(30, 48, 7, 'Ngasemdeni kaRubhen, kuthabathela kumda wasempumalanga, kuse emdeni wasentshonalanga: elakwaYuda.', 'XHO22'),
(30, 48, 8, 'Ngasemdeni wakwaYuda, kuthabathela kumda wasempumalanga, kuse kumda wasentshona, umrhumo oya kuwuzisa woba ngamashumi amabini anamahlanu amawaka eekubhite ububanzi, nobude bulingane nelinye icala, ukuthabathela empumalanga kuse entshonalanga nengcwele iya kuba kuyo.', 'XHO22'),
(30, 48, 9, 'Umnikelo wasemlilweni oya kuwuzisa kuYehova ufanele ube ngamashumi amabini anesihlanu amawaka ubude neshumi lamawaka ububanzi.', 'XHO22'),
(30, 48, 10, 'Lo mnikelo ungcwele uya kuba ngowababingeleli; ngasentla ibe ngamashumi amabini anamahlanu amawaka ubude, nasentshona ibe lishumi lamawaka ububanzi, nangasempumalanga ibe lishumi lamawaka ububanzi, nangasezantsi ingamashumi amabini anesihlanu amawaka ubude yaye ingcwele kaYehova iya kuba lapho ngayo.', 'XHO22'),
(30, 48, 11, 'Ibe yeyababingeleli abangcwalisiweyo, oonyana bakaTsadoki; abahlambulukileyo enkonzweni yam, abalahlekanga ekulahlekeni koonyana bakaSirayeli, njengoko balahlekayo abaLevi.', 'XHO22'),
(30, 48, 12, 'Lo mbingelelo welizwe elibingelelweyo woba yeyona nto ingcwele kubo emdeni wabaLevi.', 'XHO22'),
(30, 48, 13, 'Ke endaweni yababingeleli, abaLevi boba ngamashumi amabini anamahlanu amawaka ubude, neshumi lamawaka ububanzi; bonke ubude bube ngamashumi amabini anamahlanu amawaka, nobubanzi bube lishumi lamawaka.', 'XHO22'),
(30, 48, 14, 'Ize bangayithengisi, banganibanisi ngayo, banganikeli amazibulo elizwe elo, ngokuba ingcwele kuYehova.', 'XHO22'),
(30, 48, 15, 'Okuseleyo kumawaka amahlanu ububanzi, ethabathela kumawaka angamashumi amabini anamahlanu, kube yindawo eyinqambi yesixeko, yeyokuhlala nedlelo; isixeko soba sesazulwini sayo.', 'XHO22'),
(30, 48, 16, 'Zizo ke ezi izilinganiso zakhe; Umda wasemntla ungamawaka amane anamakhulu amahlanu, nomda wasemzantsi ungamawaka amane anamakhulu amahlanu, nasempuma amawaka amane anamakhulu mahlanu, nomda wasentshona ube ngamawaka amane anamakhulu mane anesihlanu.', 'XHO22'),
(30, 48, 17, 'Idlelo lesixeko ngasentla liya kuba ngamakhulu amabini anamashumi amahlanu, ngasezantsi libe ngamakhulu amabini anamashumi amahlanu, ngasempuma libe ngamakhulu amabini anamashumi amahlanu, nasentshonalanga libe ngamakhulu amabini anamashumi amahlanu.', 'XHO22'),
(30, 48, 18, 'Okuseleyo entlango kumrhumo ongcwele koba ngamawaka alishumi ngasempuma, neshumi lamawaka ngasentshonalanga; ibe ichasene nomsondezo ongcwele; yaye imveliso yawo yoba kukutya kwabo bakhonza esixekweni.', 'XHO22'),
(30, 48, 19, 'Abasebenza esixekweni baya kusilungiselela ezizweni zonke zakwaSirayeli.', 'XHO22'),
(30, 48, 20, 'Umnikelo wokudla okuziinkozo uphela ube ngamashumi amabini anesihlanu amawaka, anamashumi amabini anesihlanu amawaka; ninikele umnikelo wokudla ongcwele kwangokunjalo nangempahla yesixeko.', 'XHO22'),
(30, 48, 21, 'Okuseleyo koba kokumkani, ngaphaya komrhumo ongcwele, nakomhlaba wesixeko, ukuya kutsho kumashumi amabini anesihlanu amawaka edini lomlilo ngasempumalanga, ngasentshonalanga, kumda wasentshonalanga, kumda wewaka elinamanci mabini anamahlanu. Yehova, ngokuchasene nesabelo sokumkani; loba lidini elingcwele; nengcwele yendlu yoba sesazulwini sayo.', 'XHO22'),
(30, 48, 22, 'Kumhlaba wabaLevi, ukususela kumhlaba wesixeko, phakathi komda wenkosi, phakathi komda wakwaYuda nomda wakwaBhenjamin, kuya kuba ngowokumkani.', 'XHO22'),
(30, 48, 23, 'Ke zona izizwe eziseleyo, ukususela kumda osempuma ukusa kumda wasentshona, uBhenjamin uya kuba nesabelo.', 'XHO22'),
(30, 48, 24, 'Emdeni wakwaBhenjamin, kuthabathela kumda wasempumalanga, kuse kumda wasentshonalanga, uSimon woba nesabelo esinye.', 'XHO22'),
(30, 48, 25, 'Ngasemdeni kaSimon, kuthabathela kumda wasempumalanga, kuse emdeni wasentshona, nguIsakare.', 'XHO22'),
(30, 48, 26, 'Ngasemdeni wakwaIsakare, kuthabathela ecaleni lasempumalanga, kuse emdeni wasentshona, iZebhulon ibe nye.', 'XHO22'),
(30, 48, 27, 'Ngasemdeni wakwaZebhulon, ukusuka empumalanga kuye entshonalanga, nguGadi.', 'XHO22'),
(30, 48, 28, 'Umda kaGadi, ngasezantsi, ngasezantsi umda, wothabathela kwaTamare, use emanzini emfazwe eKadeshe, use umlambo, use kuLwandle oluKhulu.', 'XHO22'),
(30, 48, 29, 'Lilo eli ilizwe oya kuzabela izizwe zakwaSirayeli njengelifa, yaye ezi zizabelo zazo, itsho iNkosi enguMongami uYehova.', 'XHO22'),
(30, 48, 30, 'Zizo ezo iimbombo zomzi, ngecala langasentla: ngamawaka amane anamakhulu mahlanu eekorale;', 'XHO22'),
(30, 48, 31, 'amasango omzi ongokwamagama ezizwe zakwaSirayeli; ngamasango amathathu ngasentla; isango likaRubhen liya kuba linye, isango likaYuda libe linye, isango likaLevi libe linye.', 'XHO22'),
(30, 48, 32, 'Ngasempumalanga koba ngamawaka amane anamakhulu amahlanu, abe namasango amathathu; Isango likaYosefu liya kuba linye, isango likaBhenjamin liya kuba linye, isango likaDan libe linye.', 'XHO22'),
(30, 48, 33, 'Ngecala lasezantsi ngamawaka amane anamakhulu amahlanu eekubhite, namasango amathathu; ibe lisango likaSimon, elinye libe lisango likaIsakare, libe lisango likaZebhulon;', 'XHO22'),
(30, 48, 34, 'Ngecala lasentshonalanga ngamawaka amane anamakhulu mahlanu, namasango awo mathathu; libe lisango likaGadi, libe lisango lika-Ashere, libe lisango lakwaNafetali.', 'XHO22'),
(30, 48, 35, 'Koba ngathi ishumi elinesibhozo lamawaka eemilinganiselo, yaye igama laloo mzi ukususela ngaloo mini liya kuthi: uYehova ulapho.', 'XHO22'),
(31, 1, 1, 'Ngomnyaka wesithathu wobukumkani bukaYehoyakim ukumkani wakwaYuda, kweza uNebhukadenetsare ukumkani waseBhabheli eYerusalem, wayingqinga.', 'XHO22'),
(31, 1, 2, 'uYehova wamnikela esandleni sakhe uYehoyakim ukumkani wakwaYuda, nenxalenye yempahla yendlu kaTHIXO oyinyaniso, leyo wazizisa kwilizwe laseShinare endlwini yothixo wakhe; yaye ezo zinto wazizisa kwindawo yokugcina ubuncwane bothixo wakhe.', 'XHO22'),
(31, 1, 3, 'Wathi ukumkani kuAspenazi, umthetheli wabalungiseleli bakhe, makazise koonyana bakaSirayeli, nakwimbewu yokumkani, nabathetheli;', 'XHO22'),
(31, 1, 4, 'Abantwana abangenasiphako kubo, kodwa abathandekayo nabanobuchule kubo bonke ubulumko neexhalanga zolwazi nokuqonda kwenzululwazi, ababenokuma endlwini yokumkani nabo bathi xa ndibafundisa imfundo nolwimi yamaKaledi.', 'XHO22'),
(31, 1, 5, 'Ukumkani ubebanika into yemini ngangemini yayo, ekudleni kokumkani nasewayinini yakhe; wabondla iminyaka emithathu, ukuze ekupheleni beme phambi kokumkani.', 'XHO22'),
(31, 1, 6, 'Phakathi kwabo kwakukho oonyana bakaYuda, uDaniyeli, uHananiya, uMishayeli, noAzariya;', 'XHO22'),
(31, 1, 7, 'Umphathiswa wamthiya igama, ngenxa yokuba wamthiya igama elinguDaniyeli ngokuba nguBheleshatsare; uHananiya njengoShadraki; noMishayeli kaMeshaki; uAzariya wathi nguAbhede-nego.', 'XHO22'),
(31, 1, 8, 'uDaniyeli wagqiba entliziyweni yakhe, ukuba angazingcolisi ngokudla kokumkani nangewayini ayiselayo; ngoko ke wambongoza umantyi ukuba angamngcolisi.', 'XHO22'),
(31, 1, 9, 'uYehova wamenzela inceba nemfesane uDaniyeli kumthetheli-waka wababusi.', 'XHO22'),
(31, 1, 10, 'Umthetheli wenkundla wathi kuDaniyeli, Ndiyamoyika ukumkani, inkosi yam, omise eniya kukudla, nento eniya kuyisela; Uze uyifake intloko yam kukumkani.', 'XHO22'),
(31, 1, 11, 'Wathi ke uDaniyeli kuMeltsare, owayemise umphathi-mkhosi ukuba avele uDaniyeli, uHananiya, uMishayeli noAzariya, wathi:', 'XHO22'),
(31, 1, 12, 'Balinge abakhonzi bakho iintsuku ezilishumi; asinika imifuno ukuba sidle, namanzi ukuba asele.', 'XHO22'),
(31, 1, 13, 'Ubuso bethu mabube bubuso bakho, nobuso babantwana abadla ukudla kokumkani, wenze kubakhonzi bakho ngokuthanda kwakho.', 'XHO22'),
(31, 1, 14, 'Wabavumela kuloo nto, wabacikida iintsuku ezilishumi.', 'XHO22'),
(31, 1, 15, 'Ekupheleni kweentsuku ezilishumi, yabonakala imbonakalo yabo intle, betyebile, ngaphezu kwabantwana bonke abadla ukudla kokumkani.', 'XHO22'),
(31, 1, 16, 'Wathabatha ke uMeltsare inxalenye yenyama yabo newayini ababefanele ukuyisela; bawanikela amandla abo.', 'XHO22'),
(31, 1, 17, 'Aba bantwana bane ke uThixo ubanike ukwazi, nokwazi, nobulumko; kwaye uDaniyeli wayeyiqonda yonke imibono namaphupha.', 'XHO22'),
(31, 1, 18, 'Ekupheleni kweentsuku awayethe maziziswe ngazo ukumkani, inkosana yabalungiseleli yabazisa phambi koNebhukadenetsare.', 'XHO22'),
(31, 1, 19, 'Wathetha ukumkani kubo; kubo bonke kwafunyanwa abanjengoDaniyeli, noHananiya, noMishayeli, noAzariya; besakuma phambi kokumkani.', 'XHO22'),
(31, 1, 20, 'Kuyo yonke imibandela yobulumko nengqiqo awababuza yona ukumkani, wafumanisa ukuba babebadlula ngokuphindwe kalishumi bonke abenzi bomlingo nabavumisi ngeenkwenkwezi ababesebukumkanini bakhe buphela.', 'XHO22'),
(31, 1, 21, 'uDaniyeli wahlala kwada kwangumnyaka wokuqala kaKoreshi ukumkani.', 'XHO22'),
(31, 2, 1, 'Ngomnyaka wesibini wobukumkani bukaNebhukadenetsare, uNebhukadenetsare waphupha amaphupha; waphazamiseka umoya wakhe, walala ubuthongo.', 'XHO22'),
(31, 2, 2, 'Ukumkani wathi makubizwe izanuse, nabakhafuli, nabakhafuli, namaKaledi, ukuba bamxelele ukumkani amaphupha akhe. Baya kwandula ke beme phambi kokumkani.', 'XHO22'),
(31, 2, 3, 'Wathi uYehova kubo, Ndiphuphe iphupha;', 'XHO22'),
(31, 2, 4, 'Athi amaKaledi akwa-Aram kukumkani, Yehova, yidla ubomi ngonaphakade! balawulele abakhonzi bakho iphupha elo, solutyhila usindiso.', 'XHO22'),
(31, 2, 5, 'Waphendula ukumkani wathi kumaGalati, Indishiyile le nto; ukuba anithanga nindixelele iphupha, nokutyhilwa kwalo, niya kwenziwa iziqwenga, izindlu zenu zibe ngamanxuwa.', 'XHO22'),
(31, 2, 6, 'Ukuba nithe nalityhila iphupha, nokutyhilwa kwalo, nozuza izipho, nemivuzo, nembeko enkulu kum; ke ngoko, mandilazise iphupha nokutyhilwa kwalo.', 'XHO22'),
(31, 2, 7, 'Aphendula okwesibini athi, uYehova makabalawulele abakhonzi bakhe iphupha elo; sokuxelela ukutyhilwa kwalo.', 'XHO22'),
(31, 2, 8, 'uNdikhoyo waphendula wathi: “Ndiyazi ukuba uza kukhululeka, kuba uyabona ukuba le nto indishiyile.', 'XHO22'),
(31, 2, 9, 'Kodwa ukuba anithanga nindichazele elo phupha, mnye kuphela umyalelo kuni: kuba nilungiselele amazwi obuxoki nawonakeleyo ukuba niwathethe phambi kobuso bam de kutshintshe ixesha; nindixelele ke iphupha elo, ndazi ke ukuba nondixelela ukutyhilwa kwalo.', 'XHO22'),
(31, 2, 10, 'Aphendula amaKaledi phambi kokumkani, athi, Akukho mntu ehlabathini lonke uyixelayo indaba kakumkani;', 'XHO22'),
(31, 2, 11, 'Yinto engaqhelekanga leyo ukumkani ayifunayo; akukho bani unokuyixela phambi kokumkani, ingengako oothixo abakhaya lingekho senyameni.', 'XHO22'),
(31, 2, 12, 'Waqumba ke ukumkani, waba noburhalarhume obukhulu, wathi mazibulawe zonke izilumko zaseBhabheli.', 'XHO22'),
(31, 2, 13, 'Kwaphuma umthetho wokuba mazibulawe izilumko; kwaye babefuna ukuba uDaniyeli namahlakani akhe babulawe.', 'XHO22'),
(31, 2, 14, 'Wandula ke uDaniyeli wathetha ngecebo nangobulumko kuAriyogi umthetheli wamafanankosi kakumkani owayephumele ukubulala izilumko zaseBhabhiloni.', 'XHO22'),
(31, 2, 15, 'Waphendula wathi kuAriyoge, umthetheli-mkhosi wokumkani, Yini na ukuba umthetho onje ukuba bukhali kokumkani? Emva koko uArioges wayityhilela le nto kuDaniyeli.', 'XHO22'),
(31, 2, 16, 'Waza uDaniyeli wangena, wacela ukumkani ukuba amnike ixesha, aze amcacisele ukumkani.', 'XHO22'),
(31, 2, 17, 'Emva koko uDaniyeli waya endlwini yakhe waza waxelela abahlobo bakhe uHananiya noMishayeli noAzariya ngale nto;', 'XHO22'),
(31, 2, 18, 'ukuze bacele inceba kuThixo wamazulu, ngayo le mfihlelo; ukuze bangatshatyalaliswa uDaniyeli namakholwane akhe, ndawonye nezilumko zaseBhabheli.', 'XHO22'),
(31, 2, 19, 'Waza wayityhilelwa uDaniyeli loo mfihlakalo ngombono wasebusuku. Waza uDaniyeli wambonga uThixo wamazulu.', 'XHO22'),
(31, 2, 20, 'Waphendula uDaniyeli wathi, Malibongwe igama likaThixo ngonaphakade kanaphakade, ngokuba ubulumko nobugorha bobakhe.', 'XHO22'),
(31, 2, 21, 'Yena uyawaguqula ke amaxesha namaxesha; uguzula ookumkani, amise ookumkani; Unika ubulumko kwizilumko, unika ukwazi kwabakwaziyo ukuqonda.', 'XHO22'),
(31, 2, 22, 'Yena utyhila into enzulu nezisitheleyo; uyakwazi okusebumnyameni, kuhleli ukukhanya kubo.', 'XHO22'),
(31, 2, 23, 'Ndiyabulela kuwe yaye ndiyakudumisa, Thixo woobawo, ondinike ubulumko namandla yaye ngoku undazise oko besikucelile kuwe, ngenxa yokuba usazisile ngoku umbandela welizwe elingcwele ukumkani.', 'XHO22'),
(31, 2, 24, 'uDaniyeli waya kuAriyogi, lowo wayeyalelwe ngukumkani ukuba atshatyalaliswe izilumko zaseBhabheli waya wathi kuye, Musa ukuzitshabalalisa izilumko zaseBhabheli; Ndizise kukumkani, ukuze ndimxelele ukumkani ingcaciso.', 'XHO22'),
(31, 2, 25, 'uAriyogi wangxama wamsa uDaniyeli kukumkani, wathi kuye, Ndifumene indoda yamabanjwa akwaYuda, ize kubikela ukumkani le ngcaciso.', 'XHO22'),
(31, 2, 26, 'Waphendula uYehova, wathi kuDaniyeli, ogama linguBheletshatsare, Ungakwazi na ukundixelela iphupha endilibonileyo, nokutyhilwa kwalo?', 'XHO22'),
(31, 2, 27, 'Waphendula uDaniyeli phambi kokumkani, wathi, Imfihlakalo abeyicelile ukumkani, izilumko, nabakhwitsi, nabakhwitsi, nabakhafuli, abayi kuyixela kukumkani;', 'XHO22'),
(31, 2, 28, 'Kodwa ke kukho uThixo emazulwini, ozityhilayo iimfihlakalo, onokumazisa ukumkani uNebhukadenetsare okuya kubakho ekupheleni kwemihla. Lilo elo iphupha lenu, nobuso beentloko zenu ezinkukweni zenu;', 'XHO22'),
(31, 2, 29, 'Wena kumkani, izicamango zakho zathi tyaba kuwe, esesililini sakho, ukuba kuya kubakho ntoni na emveni koko; noMtyhili weemfihlakalo uyakwazisa okuya kubakho.', 'XHO22'),
(31, 2, 30, 'Mna le mfihlakalo, ukuyityhilelwa kwam, akungenxa yobulumko endinabo ngaphezu kwabaphilileyo bonke; kungenxa yokuba ndiya kumazisa ukumkani le mfihlakalo, ukuze uzazi izicamango zentliziyo eyakho.', 'XHO22'),
(31, 2, 31, 'Wena kumkani, ukhangele, kwaye ubone, kukho umfanekiso omkhulu. Lo mfanekiso mkhulu, kubengezela kubengezela, ubumi phambi kwakho; nokumila kwawo kwakusoyikeka.', 'XHO22'),
(31, 2, 32, 'Intloko yaloo mfanekiso ibiyigolide esulungekileyo, isifuba sawo nezandla zawo ziyisilivere, isisu sawo namanqe awo elubhedu;', 'XHO22'),
(31, 2, 33, 'Amacala ayo asintsimbi, imilenze yawo yintsimbi, yaye inxalenye yawo ludongwe.', 'XHO22'),
(31, 2, 34, 'Wabona, lada latyumka ilitye, kungengazandla zabantu;', 'XHO22'),
(31, 2, 35, 'Ziya kwaphulwa ndawonye isinyithi, nodongwe, nobhedu, nesilivere, negolide, zibe njengomququ wengqolowa yasehlotyeni; Wazithabatha umoya, azaba nandawo; lathi ilitye elo, libethileyo emfanekisweni, laba yintaba enkulu, lazalisa ihlabathi lonke.', 'XHO22'),
(31, 2, 36, 'Lilo elo iphupha; ingcaciso yayo soyixela phambi kokumkani.', 'XHO22'),
(31, 2, 37, 'Wena kumkani, ungukumkani wookumkani, ngenxa yokuba uThixo wamazulu ukunike ubukumkani, namandla, namandla, nozuko;', 'XHO22'),
(31, 2, 38, 'Apho bahleli khona oonyana babantu, izinto ezizitho zine, neentaka zezulu, wazibeka ezandleni zakho, wakwenza umlawuli wazo zonke. Wena le ntloko yegolide.', 'XHO22'),
(31, 2, 39, 'Emveni kwakho kuya kuvela obunye ubukumkani obuncinane kunawe, nobunye ubukumkani besithathu bobhedu, obuya kulawula ihlabathi lonke.', 'XHO22'),
(31, 2, 40, 'Ubukumkani babo buya kuba njengesinyithi ukomelela, ngenxa enokuba isinyithi saphuka, sityumza yonke into; nanjengentsimbi eqhekeze zonke, uya kwaphuka aze aphuke.', 'XHO22'),
(31, 2, 41, 'Njengoko nizibonayo iinyawo neenzwane, enye inxalenye iludongwe lombumbi, enye inxalenye iyisinyithi, ubukumkani buya kuba bobahlukeneyo; kobakho ukomelela kwesinyithi kuwo, ngenxa enokuba isinyithi sixutywe nodongwe nodongwe.', 'XHO22'),
(31, 2, 42, 'Iinzwane beziyintsimbi ngenxalenye, ziludongwe njengenxalenye, ubukumkani buya komelela kwenye, buqhekezwe kwesinye.', 'XHO22'),
(31, 2, 43, 'Njengoko ubona isinyithi sixutywe nodongwe, baya kuxubana nembewu yomntu; kodwa aziyi kunamathelana, kanye njengoko intsimbi ingaxubani nodongwe.', 'XHO22'),
(31, 2, 44, 'Ngemihla yabo kumkani, uThixo wezulu uya kumisa ubukumkani obungayi konakala naphakade; yaye obo bukumkani abuyi kushiyelwa kwezinye iintlanga, kodwa buya kuzitshabalalisa zonke ezo zikumkani, buze bume ngonaphakade.', 'XHO22'),
(31, 2, 45, 'Nalibonayo, ukuba ilitye laqhekezwa entabeni, kungengazandla, lasiqhekeza isinyithi, nobhedu, nodongwe, nesilivere, negolide; uThixo omkhulu wamxelela ukumkani okuya kwenzeka emva koko.', 'XHO22'),
(31, 2, 46, 'Waza ukumkani uNebhukadenetsare wawa ngobuso bakhe, waqubuda kuDaniyeli, wathi maziziswe kuye umnikelo womlilo nesiqhumiso esimnandi.', 'XHO22'),
(31, 2, 47, 'uYehova wamphendula uDaniyeli, wathi, Yinyaniso ukuba uThixo wakho unguThixo woothixo, uNkosi yookumkani, umtyhili weemfihlakalo, ekubeni ukwazile ukuyityhila le mfihlelo.', 'XHO22'),
(31, 2, 48, 'Waza ukumkani wamenza mkhulu uDaniyeli, wampha izipho ezikhulu ezininzi, wammisa ukuba abe ngumphathi welizwe lonke laseBhabheli, nomthetheli wabathetheli bezilumko zonke zaseBhabheli.', 'XHO22'),
(31, 2, 49, 'Waza uDaniyeli wacela kukumkani, wabamisela ooShadraki, noMeshaki, noAbhednego, ukuba balawule iphondo laseBhabheli; ke uDaniyeli wayehleli esangweni lokumkani.', 'XHO22'),
(31, 3, 1, 'Ukumkani uNebhukadenetsare wenza umfanekiso wegolide: ukuphakama kwawo bekuziikubhite ezimashumi mathandathu, ububanzi bawo baba ziikubhite ezintandathu; wema kwintlango yaseDura, kwisithili saseBhabheli.', 'XHO22'),
(31, 3, 2, 'Ukumkani uNebhukadenetsare wathumela ukuba kubizwe abathetheli, namabamba, namabamba, nabagwebi, nabagcini-ndyebo, nabacebisi, nababusi, namabamba, nawo onke amabamba amazwe, ukuba bamise umfanekiso abewumisile uNebhukadenetsare ukumkani usete uye.', 'XHO22'),
(31, 3, 3, 'Bahlanganisana ke abathetheli, namabamba, nabathetheli, nabagwebi, noonondyebo, nabacebisi, namabamba, namabamba, nabo bonke abathetheli belizwe, ukuba bawutyhilile umfanekiso abewumisile uNebhukadenetsare ukumkani; yaye bema phambi komfanekiso awayewumisile uNebhukadenetsare.', 'XHO22'),
(31, 3, 4, 'Wadanduluka ke umthetheli wathi: “Niyalelwe, zizwe, zizwe, zilwimi;', 'XHO22'),
(31, 3, 5, 'Xa nithe neva isandi sesigodlo, nelembande, nelekitare, nelekitare, nelohadi, nelohadi, nelohlobo lonke lweempahla zokubethwa, niwe phantsi niqubude kumfanekiso wegolide awumisileyo uNebhukadenetsare ukumkani.', 'XHO22'),
(31, 3, 6, 'Wonke umntu ongathanga awe phantsi aze athandaze uya kuphoswa ezikweni elivutha umlilo kwangelo lixa.', 'XHO22'),
(31, 3, 7, 'Ngelo xesha, sakuva lonke uhlanga isandi sembande, nempempe, nempempe, nempempe, nempempe, neempahla zonke zeempahla zonke, nezizwe zonke, nezizwe zonke, neelwimi zonke, zawa phantsi waguqa phantsi umfanekiso wegolide awayewumisile uNebhukadenetsare ukumkani uye.', 'XHO22'),
(31, 3, 8, 'Kungenxa yoko le nto kweza amaKaledi ngelo xesha, awatyhola amaYuda.', 'XHO22'),
(31, 3, 9, 'Athetha athi kukumkani uNebhukadenetsare, Yehova, yidla ubomi ngonaphakade!', 'XHO22'),
(31, 3, 10, 'Wena ke, kumkani, uwise umthetho wokuba bonke abathe baliva izwi lembande, nelogwali, nelohadi, nelohadi, nelohadi, neleempahla zonke zeempahla zonke, bawe phantsi, baqubude kumfanekiso wegolide.', 'XHO22'),
(31, 3, 11, 'Nabani na ongawanga phantsi aze athandaze uya kuphoswa ezikweni elivutha umlilo.', 'XHO22'),
(31, 3, 12, 'Kukho amanye amaYuda, owawamise ukuba alawule iphondo laseBhabheli, angooShadraki, noMeshaki, noAbhednego; la madoda kumkani akakuhoyanga; akababusi oothixo bakho, akaqubudi kumfanekiso wegolide owumisileyo.', 'XHO22'),
(31, 3, 13, 'uNebhukadenetsare, enomsindo, enomsindo, wathi mabaziswe ooShadraki, noMeshaki, noAbhednego. Awazisa la madoda phambi kokumkani.', 'XHO22'),
(31, 3, 14, 'Waphendula uNebhukadenetsare, wathi kubo, Nenza ngabomi na, Shadraki, Meshaki, Abhede-nego, ukuthi ningambusi uthixo wam, aniqubudi kumfanekiso wegolide endiwumisileyo?', 'XHO22'),
(31, 3, 15, 'Xa nithe nalungela, ngexesha lokuva kwenu isandi sogwali, nesempempe, nesohadi, nesehadi, nesemfama, nesazo zonke iintlobo zeempahla zokuvuma, niwe phantsi niqubude kumfanekiso endiwenzileyo; ewe, ukuba anithanga nithobele, nophoswa ezikweni elivutha umlilo; yaye ngubani na uTHIXO oya kunihlangula esandleni sam?', 'XHO22'),
(31, 3, 16, 'Baphendula ooShadraki, noMeshaki, noAbhednego, bathi kukumkani, Nebhukadenetsare, asazi ukuba sikuphendule ngantoni na kweli lizwi;', 'XHO22'),
(31, 3, 17, 'Ukuba kunjalo, uThixo wethu osilungiselelayo wosisindisa ezikweni elivutha umlilo, asihlangule esandleni sakho, kumkani.', 'XHO22'),
(31, 3, 18, 'Ke, ukuba akunjalo, makwazeke kuwe, kumkani, ukuba asiyi kubabusa thixo bakho, asiyi kuqubuda kumfanekiso wegolide owumisileyo.', 'XHO22'),
(31, 3, 19, 'uNebhukadenetsare wazala ngumsindo, nembonakalo yobuso bakhe yeya kuShadraki, noMeshaki, noAbhede-nego; ke ngoko, wathetha waza waziyalela ukuba zisonge ngokuphindwe kasixhenxe kunoko bekukade kusongwa.', 'XHO22'),
(31, 3, 20, 'Wathi kumadoda anamandla omkhosi wakhe, makabophe ooShadraki, noMeshaki, noAbhede-nego, babaphose ezikweni elivutha umlilo.', 'XHO22'),
(31, 3, 21, 'Aza abotshwa la madoda enxibe izambatho zawo zangaphezulu, iibhulukhwe zawo neminqwazi yawo nezinye iimpahla zawo, aze aphoswe ezikweni elivutha umlilo.', 'XHO22'),
(31, 3, 22, 'Kwathi, ngenxa yokuba ilizwi lokumkani belingqongqo, iziko belenziwe lashushu ngokuncamisileyo, ngenxa yoko ilangatye lomlilo lawabulala amadoda athwele uShadraki, noMeshaki, noAbhednego.', 'XHO22'),
(31, 3, 23, 'La madoda omathathu, ooShadraki, noMeshaki, noAbhednego, awela ezikweni elivutha umlilo, ebotshelelwe.', 'XHO22'),
(31, 3, 24, 'Wamangaliswa uNebhukadenetsare ukumkani, wema ngokukhawuleza, wathi kumaphakathi akhe, Asiwaphoselanga na la madoda mathathu akhonkxwe emlilweni? Aphendula athi kukumkani, Yinyaniso, kumkani;', 'XHO22'),
(31, 3, 25, 'Waphendula wathi, Yabonani, ndibone amadoda amane, ehamba emlilweni, engaseva ntlungu; ukubonakala kweyesine kufana noNyana kaThixo.', 'XHO22'),
(31, 3, 26, 'Wasondela uNebhukadenetsare emnyango weziko lomlilo, wathi, Shadraki, Meshaki, Abhede-nego, bakhonzi bOsenyangweni, phumani nize apha. Emva koko uShadraki, uMeshaki noAbhednego baphuma emlilweni.', 'XHO22'),
(31, 3, 27, 'Abathetheli, namabamba, nabathetheli, nabacebi bokumkani, ababehlanganisene khona, bawabona amadoda, lomlilo ungenamandla kuwo, neenwele zingarhanga zirhawuke, neengubo zawo zingatshi eentloko, neengubo zawo zokulwa zingarhawukanga batshintshe, bengaqubhanga ivumba lomlilo lawela phezu kwabo.', 'XHO22'),
(31, 3, 28, 'Wathetha uNebhukadenetsare, wathi, Makabongwe uThixo kaShadraki, kaMeshaki, ka-Abhede-nego, osithumileyo isithunywa sakhe, wabahlangula abakhonzi bakhe, ababekholose ngaye, balijika ilizwi lokumkani, bayikhulula ngentlawulelo imizimba yabo, ukuba bakhonze nawuphi na uthixo, banganquli thixo bambi, bangamkhonzi yinqule kodwa uThixo wabo.', 'XHO22'),
(31, 3, 29, 'Ngoko ndiwisa umthetho wokuba zonke iintlanga, nezizwe zonke, neelwimi zonke, ezithetha kakubi ngoThixo kaShadraki, kaMeshaki, ka-Abhede-nego, zinqanyulwe, izindlu zabo zibe ngamanxuwa; ngokuba kungekho wumbi unjengaye inokukhulula.', 'XHO22'),
(31, 3, 30, 'Ukumkani wabanyusela uShadraki, noMeshaki, noAbhednego, wabasa kwisithili saseBhabheli;', 'XHO22'),
(31, 4, 1, 'uNebhukadenetsare kumkani, ndizibhalele zonke izizwe, neentlanga zonke, neelwimi zonke ezimiyo ehlabathini; Uxolo malube nani.', 'XHO22'),
(31, 4, 2, 'Ndabona ukuba kulungile kum ukwenza imisebenzi ebalulekileyo, nezimanga, awazenzayo uThixo Osenyangweni.', 'XHO22'),
(31, 4, 3, 'Hayi, ukuba mkhulu kwemiqondiso yakhe! Hayi indlela enamandla ngayo imimangaliso yakhe! Ubukumkani bakhe bubukumkani bamaphakade onke, nolawulo lwakhe lukwizizukulwana ngezizukulwana.', 'XHO22'),
(31, 4, 4, 'Mna, Nebhukadenetsare, ndandiphumle endlwini yam yaye ndinempumelelo kwibhotwe lam.', 'XHO22'),
(31, 4, 5, 'Ndabona iphupha, landoyikisa, ndaneengcinga esililini sam, nobuso obusentloko, bandoyikisa.', 'XHO22'),
(31, 4, 6, 'Ndathi ke, maziziswe phambi kwam zonke izilumko zaseBhabheli, ukuba zindityhilele ukutyhilwa kwephupha elo.', 'XHO22'),
(31, 4, 7, 'Beza ke amatola, namatola, namaKaledi, namatola, ndabaxelela iphupha; kodwa abazange bandazise ngengcaciso yayo.', 'XHO22'),
(31, 4, 8, 'Ekugqibeleni wangena phambi kwam uDaniyeli, ogama linguBheleshatsare, ngokwegama lothixo wam, onomoya woothixo abangcwele; ndamxelela iphupha elo, ndathi.', 'XHO22'),
(31, 4, 9, 'Wena Bheleshatsare, sivumisi, ekubeni ndiyazi ukuba umoya woothixo abangcwele unawe yaye akukho mfihlelo ikukhathazayo, ndixelele imibono yephupha lam endilibonileyo nokutyhilwa kwalo.', 'XHO22'),
(31, 4, 10, 'Ubuso bentloko yam bunjengobuso bam esililini sam; Ndajonga ndabona umthi usesazulwini somhlaba, umde kakhulu.', 'XHO22'),
(31, 4, 11, 'Wakhula loo mthi, womelela, ukuphakama kwawo kwafikelela emazulwini, ubuso bawo bafikelela esiphelweni sehlabathi lonke.', 'XHO22'),
(31, 4, 12, 'Amagqabi awo ebemahle, neziqhamo zawo bezizininzi; bekukho ukudla kweento zonke; amarhamncwa elizwe abe emthunzini phantsi kwawo, neentaka zezulu zahlala emasebeni awo, yadla kuwo yonke inyama.', 'XHO22'),
(31, 4, 13, 'Ndabona emibonweni yentloko yam, ndisesililini sam, nanko kusihla umlindi ongowengcwele emazulwini;', 'XHO22'),
(31, 4, 14, 'Wadanduluka kakhulu, wathi, Wugawuleni lo mthi, niwagawule amasebe awo, niwaqhawule amagqabi awo, nizichithachithe iziqhamo zawo; Makuphume phantsi kwawo amarhamncwa, neentaka emasebeni awo.', 'XHO22'),
(31, 4, 15, 'Kodwa sishiyeni isiqu seengcambu zawo emhlabeni, sibotshelelwe ngeqhina lentsimbi nobhedu, kwingca ethambileyo yelizwe; sinyakamiswe ngumbethe wezulu, sibe yinxalenye yezilo zomhlaba.', 'XHO22'),
(31, 4, 16, 'Intliziyo yakhe mayibe yeyomntu, anikwe intliziyo yenkomo; wambetha kasixhenxe.', 'XHO22'),
(31, 4, 17, 'Le nto ihamba ngokwesiko labalindi, nangosiko lwezwi labangcwele, ukuze bazi abaphilileyo, ukuba Osenyangweni unobukumkani obuphezu kwezikumkani zabantu, abunike lowo athanda ukubumisa; kwaye ukhethe yena ngaphezu kwayo amadoda aphantsi.', 'XHO22'),
(31, 4, 18, 'Mna, kumkani Nebhukadenetsare, ndalibona eli phupha. Ke kaloku, wena Bheleshatsare, ndixelele ingcaciso yayo, ngenxa yokuba zonke izilumko zobukumkani bam azinako ukundixelela loo ngcaciso; kodwa unako; ngokuba unomoya woothixo abangcwele.', 'XHO22'),
(31, 4, 19, 'Waza uDaniyeli, ogama libizwa ngokuba nguBheletsatsare, waphazamiseka umzuzwana omnye, zamothusa izicamango zakhe. Wathi ukumkani, Bheleshatsare, musa ukuyixhalela iphupha, nokutyhilwa kwalo; uBheleshatsare waphendula wathi, Nkosi yam, iphupha lisingisele kwabakuthiyileyo, nokutyhilwa kwalo likuzo iintshaba zakho.', 'XHO22'),
(31, 4, 20, 'Umthi owubonileyo, owakhulayo womelela, okuphakama kwawo kwafikelelayo ezulwini, obuso bafika ehlabathini lonke;', 'XHO22'),
(31, 4, 21, 'omagqabi abemahle, neziqhamo zawo bezizininzi, kwaye kukho ukudla komntu wonke; ezahlala phantsi kwawo amarhamncwa, neentaka zezulu zahlala emasebeni awo.', 'XHO22'),
(31, 4, 22, 'Nguwe, kumkani, oye wakhula waza womelela, ngenxa yokuba ubukhulu bakho banda bafikelela emazulwini nolawulo lwakho lwesa eziphelweni zomhlaba.', 'XHO22'),
(31, 4, 23, 'Kwaye ukumkani wabona umlindi nabathembekileyo besihla bevela ezulwini waza wathi: Gawulani umthi nize niwutshabalalise; sishiye isiqu seengcambu zawo emhlabeni, senziwe iziko lesinyithi, nobhedu, engceni ethambileyo yelizwe; sinyakamiswe ngumbethe wezulu, sibe yinxalenye yezilo zelizwe, kude kudlule amaxesha amisiweyo asixhenxe;', 'XHO22'),
(31, 4, 24, 'Yiyo le ingcaciso, kumkani, nomyalelo wOsenyangweni, omfikeleyo ukumkani wam:', 'XHO22'),
(31, 4, 25, 'baya kukugxotha phakathi kwabantu, indlu yakho ibe namarhamncwa, badle utyani njengeenkomo, bakuseze ngombethe wezulu, kudlule amaxesha amisiweyo asixhenxe de yazi ukuba iNkokeli Ephakamileyo inorhulumente kurhulumente wabantu, kwaye eso sipho ifuna ukumnika yona.', 'XHO22'),
(31, 4, 26, 'Kwaye ngenxa yokuba babeyalela ukuba isiqu seengcambu zomthi masishiywe simile; Ubukumkani bakho buya kuqiniselwa kuwe, wakwazi ukuba amazulu anegunya.', 'XHO22'),
(31, 4, 27, 'Ngako oko, nkosi yam, icebo lam malikholeke kuwe, uzitshabalalise izono zakho ngobulungisa, nobugwenxa bakho ngokubabaza abaziintsizana; ukuba ingaba kukwandiswa kokuthula kwakho.', 'XHO22'),
(31, 4, 28, 'Konke oko kwamfikela ukumkani uNebhukadenetsare.', 'XHO22'),
(31, 4, 29, 'Ekupheleni kweenyanga ezilishumi elinambini, waya endlwini yobukumkani baseBhabheli.', 'XHO22'),
(31, 4, 30, 'Waphendula ukumkani wathi, Le asiyiyo na iBhabheli enkulu, endiyakhayo ngamandla am amakhulu, nangobuqaqawuli bobungangamsha bam, ukuba ibe likomkhulu?', 'XHO22'),
(31, 4, 31, 'Lakuba lisekho elo lizwi emlonyeni wokumkani, kwavakala ilizwi ezulwini lisithi: “Wena uxelelwe, kumkani Nebhukadenetsare; Ubukumkani bukushiyile.', 'XHO22'),
(31, 4, 32, 'Baya kukugxotha phakathi kwezizwe, uhlale phakathi kwezilwanyana zelizwe; baya kukudlisa utyani njengeenkomo, kudlule amaxesha amisiweyo asixhenxe, ude wazi ukuba iNgangalala inobukumkani ebukumkanini babantu, imnike lowo imthandayo.', 'XHO22'),
(31, 4, 33, 'Kwathi ngelo lixa ilizwi likaNebhukadenetsare wagxothwa ebantwini, wadla utyani njengeenkomo, umzimba wakhe wanyakanyiswa ngumbethe wezulu, zada iinwele zakhe zakhula, zanjengeentsiba, zanjengexhalanga neenzipho zalo iintaka iintsapho.', 'XHO22'),
(31, 4, 34, 'Ekupheleni kwaloo mihla, mna Nebhukadenetsare ndawaphakamisela ezulwini amehlo am, ingqondo yam yabuyela kwakum, Osenyangweni ndamzukisa, ndamzukisa lowo uhleli ngonaphakade, obukumkani bubukumkani obungunaphakade ubukumkani bakhe bukwizizukulwana ngezizukulwana.', 'XHO22'),
(31, 4, 35, 'Baye bonke abemi behlabathi bebalelwe ekubeni yinto engento; kwaye wenza ngokwentando yakhe amabhunga ezulu naphakathi kwabemi bomhlaba; akukho bani unokusithintela isandla sakhe, athi kuye, Wenza ntoni na?', 'XHO22'),
(31, 4, 36, 'Kwaoko intliziyo yam yabuyela kwakum; ubuqaqawuli bam nobuqaqawuli bam babuyela kum ebuqaqawulini bobukumkani bam; zandifuna abacebisi bam nezikhulu zam; ndaza ndaqiniselwa ebukumkanini bam, ndaza ndongezwa ngobuqaqawuli obugqithisileyo.', 'XHO22'),
(31, 4, 37, 'Kwaye, mna, Nebhukadenetsare, ndiyayincoma, ndiyidumisa, ndiyizukisa iNkosi yamazulu, ozenzo zakhe ziyinyaniso zonke, ozindlela zakhe zigwebayo; kwaye unokubathoba abo bahamba ngekratshi.', 'XHO22'),
(31, 5, 1, 'Ukumkani uBheleshatsare wenzela iirhuluneli zakhe eziliwaka isidlo esikhulu, wasela iwayini phambi kwaloo mawaka.', 'XHO22'),
(31, 5, 2, 'uBheleshatsare wayingcamla iwayini leyo, wathi maziziswe iimpahla zegolide nezesilivere, abezithimbile uNebhukadenetsare uyise etempileni eseYerusalem; wasela kuyo ukumkani nabathetheli bakhe, nabafazi bakhe, nezithende zakhe.', 'XHO22'),
(31, 5, 3, 'Baza bazizisa iimpahla zegolide, ezazithinjwe etempileni yendlu kaThixo eYerusalem; Wayesela khona ukumkani, nabathetheli bakhe, nabafazi bakhe, nabafazi bakhe.', 'XHO22'),
(31, 5, 4, 'Basela iwayini, babadumisa oothixo begolide, abesilivere, nobhedu, nesinyithi, nobemithi, namatye.', 'XHO22'),
(31, 5, 5, 'Kwangelo lixa kwaphuma iminwe esandleni somntu yaza yabhala esiphathweni sezibane eludongeni lwendlu yokumkani, yaye ukumkani wabona inxalenye yesandla esasibhala.', 'XHO22'),
(31, 5, 6, 'Yandula ke inkangeleko yokumkani yatshintsha, zaza zamothusa iingcamango zakhe, kangangokuba amalungu esinqe sakhe aqengqeleka namadolo akhe abethabethana.', 'XHO22'),
(31, 5, 7, 'Ukumkani waliphakamisa izwi lakhe, wabazisa oosiyazi, namaKaledi, nabakhafuli. Wathetha uYehova, wathi kwizilumko zaseBhabheli, Othe wayilesa le ncwadi, wandazisa ukutyhilwa kwayo, uya kunxitywa ingubo ebomvu, nomxokelelwane wegolide emqaleni wakhe, abe ngowesithathu enkosini ubukumkani.', 'XHO22'),
(31, 5, 8, 'Ngoko zonke izilumko zikakumkani zangena, kodwa azizange zikwazi ukuwufunda umbhalo lowo yaye azizange zimazise ukumkani intsingiselo yawo.', 'XHO22'),
(31, 5, 9, 'Wakhathazeka kakhulu ukumkani uBheleshatsare, nembonakalo yakhe yaguquka ngaphakathi kwakhe, nezikhulu zakhe zamangaliswa.', 'XHO22'),
(31, 5, 10, 'Yangena inkosazana endlwini yemigcobo, ngenxa yamazwi okumkani nabathetheli bakhe; wathetha ukumkanikazi, wathi: Owu kumkani, phila ngonaphakade;', 'XHO22'),
(31, 5, 11, 'Kukho indoda ebukumkanini bakho onomoya woothixo abangcwele; nangemihla kayihlo kwafunyanwa kuye ukukhanya, nokuqiqa, nobulumko, obunjengobulumko boothixo; owamenza ukumkani uNebhukadenetsare, uyihlo, ukumkani, ndithi, uyihlo, abe ngukumkani wabakhafuli, nabakhafuli, namaKaledi, nabakhafuli;', 'XHO22'),
(31, 5, 12, 'ngenxa yokuba kwafunyanwa kuye umoya omangalisayo, nokwazi, nengqiqo, ukutyhilwa kwamaphupha, nokutyhilwa kwentetho enzima, ecombulula amathandabuzo, kuDaniyeli lowo ukumkani wambiza ngokuba nguBheletshatsare; makaxelelwe uDaniyeli ngoku, acacise.', 'XHO22'),
(31, 5, 13, 'Waza uDaniyeli wasiwa phambi kokumkani. Wathi uYehova kuDaniyeli, UnguDaniyeli na, omnye wabathinjwa bakwaYuda, abathe ukumkani ubawo wabazisa ezweni lamaYuda?', 'XHO22'),
(31, 5, 14, 'Ndivile ke ngawe, ukuba unomoya woothixo kuwe, nokuba kufunyanwa kuwe ukukhanya, nokuqiqa, nobulumko.', 'XHO22'),
(31, 5, 15, 'Zeziswa phambi kwam izilumko, ezazikhangele iinkwenkwezi, ukuba zifunde esi sibhalo, zindixelele nengcaciso yaso, kodwa aziyityhilanga ingcaciso yaloo nto.', 'XHO22'),
(31, 5, 16, 'Kwaye ndeva ngawe ukuba unako ukwenza iingcaciso nokusombulula amathandabuzo; Ukuba ke uthe wawulesa lo mbhalo, wandazisa ukutyhilwa kwawo, uya kunxitywa ingubo ebomvu, nomxokelelwane wegolide emqaleni wakho, ube ngowesithathu ebukumkanini.', 'XHO22'),
(31, 5, 17, 'Waza wathetha uDaniyeli phambi kokumkani, wathi, Izipho zakho zezakho; ndiya kuyifundela ukumkani incwadi, ndimazise ingcaciso.', 'XHO22'),
(31, 5, 18, 'uYehova, uThixo Osenyangweni wamnika uNebhukadenetsare uyihlo ubukumkani, nobukhulu, nozuko, nozuko;', 'XHO22'),
(31, 5, 19, 'Yaye ngenxa yobukhulu awabunikwayo, zonke izizwe nezizwe nezizwe neelwimi zazingcangcazela yaye zoyika phambi kwakhe; yaye lowo amthandayo wamgcina ephila; kwanabo athanda ukubavusa; naleyo wayefuna ukuyibeka phantsi.', 'XHO22'),
(31, 5, 20, 'Ke xeshikweni intliziyo yakhe yaziphakamisayo, yaqaqadeka intliziyo yakhe, wakhukhumala, wehliswa etroneni yakhe, baluhlutha ubungangamsha bakhe.', 'XHO22'),
(31, 5, 21, 'Kwaye walahlwa phakathi koonyana babantu; intliziyo yakhe yaba njengeyeenkomo, wahlala namahashe asendle; ade azi ukuba uThixo Osenyangweni nguye olawula izikumkani zabantu, umisa lowo athanda ukumnika.', 'XHO22'),
(31, 5, 22, 'Wena ke, nyana wakhe, Bheleshatsare, akuyithobanga intliziyo yakho, nakuba ubukwazi oko konke;', 'XHO22'),
(31, 5, 23, 'Uziphakamisile eNkosini yamazulu; iimpahla zendlu yakhe zazibeka phambi kwakho; nasela iwayini ngazo wena, nabathetheli bakho, nabafazi bakho, namashweshwe akho; ubadumise oothixo besilivere, nabegolide, nabobhedu, nabesinyithi, nabemithi, nabamatye, abangaboniyo, abangeva, nabangakwaziyo; akumzukisanga uThixo, osesandleni sakhe ukuphefumla kwakho, osesandleni sakhe zonke iindlela zakho.', 'XHO22'),
(31, 5, 24, 'Sasuswa kuye inxalenye yesandla; kwabhalwa ke lo mbhalo.', 'XHO22'),
(31, 5, 25, 'Nguwo lo ke umbhalo obhaliweyo: MENE, MENE, TEKELE, UFARSIN.', 'XHO22'),
(31, 5, 26, 'Nantsi ukuchazwa kwetyala: MENE; uThixo ubufundile ubukumkani bakho, waza wabufeza.', 'XHO22'),
(31, 5, 27, 'TEATLE; Ulinganiswe esikalini, kwaye ufunyenwe ubuthathaka.', 'XHO22'),
(31, 5, 28, 'PERES; Bahlulwa ubukumkani bakho, banikwa amaMedi namaPersi.', 'XHO22'),
(31, 5, 29, 'Waza uBheleshatsare wakhupha umyalelo, baza bamnxibisa uDaniyeli ingubo ebomvu nentsimbi yomqala yegolide emqaleni wakhe baza bamxelela ukuba uya kuba ngowabathathu umlawuli ebukumkanini.', 'XHO22'),
(31, 5, 30, 'Ngobo busuku wabulawa uBheleshatsare ukumkani wamaKaledi.', 'XHO22'),
(31, 5, 31, 'uDariyo umMedi wabuthabatha ubukumkani, eminyaka ikumashumi mathandathu anamibini ezelwe.', 'XHO22'),
(31, 6, 1, 'Kwakholeka kuDariyo ukunyula iirhuluneli ezilikhulu elinamanci mabini, ukuba zibe nokulawula kubo bonke ubukumkani.', 'XHO22'),
(31, 6, 2, 'Malunga nabongameli aba bathathu; awathi uDaniyeli waba ngowokuqala kubo, ukuze abathetheli babaphendule, angaphathwa bububi ukumkani.', 'XHO22'),
(31, 6, 3, 'Waza lo Daniyeli wakhethwa ukuba alawule iintloko neenkosana, ngenxa yokuba umoya wawumlungele; ukumkani wacinga ukumenza umphathi kubo bonke ubukumkani.', 'XHO22'),
(31, 6, 4, 'Ngoku iinkokeli nabathetheli bafuna ukufumana isizathu ngakuDaniyeli malunga nobukumkani; kwaye ababona siphoso; ngokuba ebethembekile, akufunyanwanga tyala natyala kuye.', 'XHO22'),
(31, 6, 5, 'Aza athi la madoda, Asiyi kufumana nalinye ithuba ngakulo Daniyeli, ukuba asilifumananga kuye ngenxa yomthetho woThixo wakhe.', 'XHO22'),
(31, 6, 6, 'Bahlanganisana abo bathetheli nabaziirhuluneli kukumkani, bathi kuye, Kumkani Dariyo, phila ngonaphakade!', 'XHO22'),
(31, 6, 7, 'Bonke abaphathi bobukumkani, namabamba, nabathetheli, nabacebisi, namabamba, bacebisana ukuba bawumise umthetho wakomkhulu, benze isigqibo esimisiweyo sokuba nabani na ocela isicelo kuThixo nokuba nguwuphi na, makacele into kuye iintsuku ezimashumi mathathu, kodwa wena, kumkani, uya kuphoswa emhadini weengonyama.', 'XHO22'),
(31, 6, 8, 'Ke kaloku, kumkani, khupha umyalelo uze uwutyobele uxwebhu, ukuze lungaguquki, ngokomthetho wamaMedi namaPersi, ongaguqukiyo.', 'XHO22'),
(31, 6, 9, 'Ngoko ukumkani uDariyo wayibhala loo ncwadi nomthetho.', 'XHO22'),
(31, 6, 10, 'Weva uDaniyeli ukuba incwadi leyo ibisayinwe, wangena endlwini yakhe; iifestile zegumbi lakhe zivuliwe, zikhangele eYerusalem, waguqa ngamadolo kathathu ngemini, ethandaza, ebulela kuThixo wakhe, njengoko ebesenza ngaphambili.', 'XHO22'),
(31, 6, 11, 'Ahlanganisana ke la madoda, amfumana uDaniyeli ethandaza, etarhuzisa phambi koThixo wakhe.', 'XHO22'),
(31, 6, 12, 'Basondela, bathetha kukumkani ngelizwi lokumkani; Akuwubhalanga na umthetho wokuba bonke abasukuba becela into kuThixo nakuwuphi na umntu, ngeentsuku ezimashumi mathathu, ingenguwe kumkani, aphoswe emhadini weengonyama? Waphendula ukumkani, wathi, Loo nto iyinyaniso, ngokomthetho wamaMedi namaPersi, ongaguqukiyo.', 'XHO22'),
(31, 6, 13, 'Aza aphendula athi phambi kokumkani, uDaniyeli, ongomnye wabathinjwa bakwaYuda, akakuphulaphulanga, kumkani, nomthetho owumisileyo; ke ucela isicelo sakhe kathathu ngemini.', 'XHO22'),
(31, 6, 14, 'Wathi ukumkani akuweva loo mazwi, wabandezeleka kunene, wayibhekisa intliziyo yakhe kuDaniyeli ukuba amhlangule; wazama ukumhlangula de kwatshona ilanga.', 'XHO22'),
(31, 6, 15, 'Aza la madoda eza edlongozela kukumkani, athi kukumkani, kumkani, yazi ukuba amaMedi namaPersi anomthetho wokuba kungaguqulwangamthetho kakumkani;', 'XHO22'),
(31, 6, 16, 'Watsho ke ukumkani; bamzisa uDaniyeli, bamjulela emhadini wengonyama. uYehova wathetha kuDaniyeli, wathi, uThixo wakho, ombusa ngamaxesha onke, wokusindisa.', 'XHO22'),
(31, 6, 17, 'Kwaziswa ilitye, labekwa emnyango womhadi; ukumkani wayiqinisa ngomsesane wakhe wokutywina, nangomsesane wokutywina wabathetheli bakhe; ukuze icebo likaDaniyeli lingaguquki.', 'XHO22'),
(31, 6, 18, 'Waza ukumkani waya ebhotweni lakhe, wachitha ubusuku ezila ukudla; yaye akuzange kubekho lizwi phambi kwakhe, waza waphelelwa bubuthongo.', 'XHO22'),
(31, 6, 19, 'Waza ukumkani wavuka kwakusasa, wangxamela ukuya emhadini weengonyama.', 'XHO22'),
(31, 6, 20, 'Yathi yakufika emhadini, yakhala kuDaniyeli; Waphendula ukumkani, wathi kuDaniyeli, Daniyeli, mkhonzi woThixo ophilileyo, uThixo wakho, ombusa ngamaxesha onke, ube nako na ukuhlangula ezingonyameni?', 'XHO22'),
(31, 6, 21, 'Wathi ke uDaniyeli kukumkani, Nkosi, phila ngonaphakade!', 'XHO22'),
(31, 6, 22, 'uThixo wam uthumele ingelosi yakhe, yayivala imilomo yeengonyama, ukuze zingandenzi, ngenxa yokuba phambi kwakhe kufunyenwe ndimsulwa; Andenzanga bubi phambi kwakho, kumkani.', 'XHO22'),
(31, 6, 23, 'Ukumkani wavuya kunene ngakuye, wathi makamkhuphe uDaniyeli emhadini. Wakhutshwa ke uDaniyeli emhadini, akwafunyanwa nanye into embi kuye, ngokuba ebekholwe kuThixo wakhe.', 'XHO22'),
(31, 6, 24, 'Ukumkani wayalela ukuba aziswe amadoda lawo ayemangalele uDaniyeli, aza aphoswa emhadini weengonyama, wona nabantwana bawo, nabafazi bawo; zababamba iingonyama, zawaphula onke amathambo abo, nokuba baye kufika nasezantsi emhadini.', 'XHO22'),
(31, 6, 25, 'Waza uDariyo ukumkani wabhalela kuzo zonke izizwe, neentlanga zonke, neelwimi zonke ezibe zisehlabathini lonke; Uxolo malube nani.', 'XHO22'),
(31, 6, 26, 'Ndikhupha umyalelo wokuba bonke abantu abakurhulumente wonke wobukumkani bam bangcangcazele baze boyike phambi koThixo kaDaniyeli, ngenxa yokuba unguThixo ophilayo ozinzileyo de kube sekupheleni kwexesha, yaye ‘ubukumkani bakhe abuyi kuba bume utshatyalalisiwe, yaye ubukumkani bakhe buya kwanela kude kubezekupheleni.', 'XHO22'),
(31, 6, 27, 'Ulohlangula, usindisa, wenza imiqondiso nezimanga emazulwini nasehlabathini; wamhlangula uDaniyeli emandleni eengonyama.', 'XHO22'),
(31, 6, 28, 'Lo Daniyeli waba nempumelelo ebukumkanini bukaDariyo nasebukumkanini bukaKoreshi umPersi.', 'XHO22'),
(31, 7, 1, 'Ngomnyaka wokuqala wobukumkani bukaBheleshatsare ukumkani waseBhabheli, uDaniyeli waphupha, wabona umbono esililini sakhe; walibhala phantsi iphupha elo, walishwankathela.', 'XHO22'),
(31, 7, 2, 'Wathetha uDaniyeli, wathi, Ndabona embonweni wam ebusuku, nantso imimoya yomine yasezulwini isilwa phezu kolwandle olukhulu.', 'XHO22'),
(31, 7, 3, 'Kwaphuma amarhamncwa amakhulu amane, ephuma elwandle, elinye lahlukile kwelinye.', 'XHO22'),
(31, 7, 4, 'Eyokuqala ibifana nengonyama, inamaphiko okhozi;', 'XHO22'),
(31, 7, 5, 'Ndabona elinye irhamncwa, elesibini, lifana nebhere, limi ngaphesheya, lineembambo ezintathu emlonyeni walo phakathi kwamazinyo alo; Bathi kuye, Vuka udle inyama eninzi.', 'XHO22'),
(31, 7, 6, 'Emva kwezi zinto ndakhangela, ndabona elinye, linjengehlosi, linamaphiko entaka amane emqolo; Irhamncwa elo lalineentloko ezine; wanikwa ukumkani.', 'XHO22'),
(31, 7, 7, 'Emva kwezi zinto ndabona emibonweni yasebusuku: nalo irhamncwa lesine, eloyikekayo, elomeleleyo, elinamandla; wayenamazinyo amakhulu entsimbi; yalityumza, yaphuka, yakunyathela ngamathupha alo; laye lineempondo ezilishumi.', 'XHO22'),
(31, 7, 8, 'Ndazijonga iimpondo, ndabona olunye uphondo oluncinane phakathi kwazo, oluneempondo ezintathu zokuqala ezincothuliweyo nango olo phondo lwalunamehlo anjengamehlo omntu, nomlomo owawuthetha izinto ezinkulu.', 'XHO22'),
(31, 7, 9, 'Ndabona zada zaphoswa phantsi iitrone, nantso iNyongo yezi mini ihleli, ingubo yayo imhlophe njengekhephu, neenwele zentloko yayo zinjengoboya bezimvu obusulungekileyo; Itrone yakhe injengelangatye lomlilo, neevili zayo zinjengomlilo olenyayo.', 'XHO22'),
(31, 7, 10, 'Kwakuphuma umlambo womlilo phambi kwakhe, iwaka lalilungiselela, ishumi lamawaka ngaphezu kweshumi lamawaka lalimi phambi kwakhe; kwagwetywa, zavulwa iincwadi.', 'XHO22'),
(31, 7, 11, 'Ndabona ngenxa yesandi samazwi amakhulu athethwayo ngesigodlo: ndakhangela lada labulawa irhamncwa, umzimba walo watshatyalaliswa, watshiswa ngomlilo ovuthayo.', 'XHO22'),
(31, 7, 12, 'Ke zona ezinye izilwanyana zahluthwa ulawulo lwazo, kodwa zanda imihla ngemihla.', 'XHO22'),
(31, 7, 13, 'Ndabona embonweni wasebusuku, nanko onjengoNyana woMntu esiza esemafini ezulu, esiza eNkosini yemihla, yamsondeza kuye.', 'XHO22'),
(31, 7, 14, 'Wanikwa igunya, nozuko, nobukumkani, ukuze bamkhonze abantu beentlanga zonke, neentlanga zonke, neelwimi zonke; Ubukumkani bakhe bubukumkani obungunaphakade obungayi kudlula, nobukumkani bakhe bungayi konakaliswa.', 'XHO22'),
(31, 7, 15, 'Mna Daniyeli, umphefumlo wam wazibhijabhija phakathi, nentloko yam yandothusa.', 'XHO22'),
(31, 7, 16, 'Ndasondela komnye wababemi khona, ndambuza inyaniso ngezo zinto zonke. Wandixelela ke wandinika ingcaciso yezinto.', 'XHO22'),
(31, 7, 17, 'La marhamncwa makhulu, mane, ngookumkani abane abaya kubonakala emhlabeni.', 'XHO22'),
(31, 7, 18, 'Ke bona abangcwele bOsenyangweni baya kubuthabatha ubukumkani, babudle ilifa ubukumkani kude kuse ephakadeni.', 'XHO22'),
(31, 7, 19, 'Ke kaloku ndinga ndinga ndingangayazi inyaniso ngeli rhamncwa lesine, elahlukileyo kuwo onke, loyikeka kakhulu, elimazinyo asisinyithi, elizinzipho zobhedu; owayidlayo, waqhekeza, wayinyathela ngeenyawo into eseleyo;', 'XHO22'),
(31, 7, 20, 'neempondo ezilishumi ezibe zisentlokweni yaso, kwabonakala ezinye iimpondo, ezintathu kuzo zawa phambi kwaso; uphondo olunamehlo, nomlomo uthetha izinto ezininzi ezinkulu, okubonakala kunamandla ngaphezu kwamadlelane alo.', 'XHO22'),
(31, 7, 21, 'Ndabona, kwaza kwalwa olu phondo lwabangcwele, lwaboyisa;', 'XHO22'),
(31, 7, 22, 'ade afike uMnini-mihla, bagwetywe abangcwele bOsenyangweni; kwaye ixesha liyakufika lokuba abathembekileyo bathabathe ubukumkani.', 'XHO22'),
(31, 7, 23, 'Wathi, Irhamncwa lesine kuya kubakho ubukumkani besine ehlabathini, obungayi kwahluka kuzo zonke ezinye izikumkani, buliphanzise elimiweyo liphela, bulinyathele, bulityumze.', 'XHO22'),
(31, 7, 24, 'Iimpondo ezilishumi eziphuma kobo bukumkani ngookumkani abalishumi abaya kuma, kume omnye emva kwabo; yaye uya kwahluka kowokuqala, yaye uya kuthoba ookumkani abathathu.', 'XHO22'),
(31, 7, 25, 'Uya kuthetha amazwi amakhulu nxamnye nOyena Uphakamileyo aze abatshabalalise abangcwele bOyena Uphakamileyo aze acinge ukutshintsha amaxesha nemithetho, yaye kuya kunikelwa esandleni sakhe ngexesha namathuba nezahlulo zamaxesha.', 'XHO22'),
(31, 7, 26, 'Kodwa ke umgwebo uya kuhlala, kwaye baya kubuthabatha ubukumkani bakhe, baphanzise kwaye batshabalalise kude kube sekupheleni.', 'XHO22'),
(31, 7, 27, 'Ubukumkani, nolawulo, nobukhulu bezikumkani, phantsi kwezulu lonke, buya kunikwa abantu abangcwele bOsenyangweni, obukumkani bubukumkani obungunaphakade, nezikumkani zonke ziya kumkhonza, zimve;', 'XHO22'),
(31, 7, 28, 'Yaza kuphela apha le nto. Ke yena uDaniyeli, izicamango zam zandothusa kakhulu, ubuso bam baguquka; kodwa ndawugcina loo mbandela entliziyweni yam.', 'XHO22'),
(31, 8, 1, 'Ngomnyaka wesithathu wokulawula kukaBheleshatsare ukumkani, mna, Daniyeli wam, ndabona umbono emva kowokuqala owabonakalayo kum.', 'XHO22'),
(31, 8, 2, 'Ndabona embonweni; ndakubona oko, ndiseShushan komkhulu, kummandla wakwaElam; ndabona embonweni, yaye ndandisecaleni komlambo iUlayi.', 'XHO22'),
(31, 8, 3, 'Ndawaphakamisa amehlo am, ndakhangela, nantso inkunzi yegusha imi ngasemlanjeni, ineempondo ezimbini; zaphakama iimpondo zombini; kodwa enye yayiphakame kakhulu kunomnye, kwaye ephakamileyo yayingasemva.', 'XHO22'),
(31, 8, 4, 'Ndabona inkunzi yegusha ityhoboza, ibhekisa entshonalanga, entla, nasezantsi; akwabakho ukuma phambi kwayo, akwabakho unokuhlangula esandleni sayo; kodwa wenza ngokuthanda kwakhe, wakhula.', 'XHO22'),
(31, 8, 5, 'Ndithe ndisakukhumbula oko, kwathi gqi inkunzi yebhokhwe entshonalanga, iye kulo lonke ihlabathi, ingawuchukumisi umhlaba; yaye ibhokhwe yayinophondo olumangalisayo phakathi kwamehlo ayo.', 'XHO22'),
(31, 8, 6, 'Yafika enkunzini yegusha, ineempondo ezimbini, ndayibona imi ngasemlanjeni, yagidimela kuyo ke ngobushushu bamandla ayo.', 'XHO22'),
(31, 8, 7, 'Ndayibona isondela enkunzini yegusha, yoyika kakhulu, yayibetha inkunzi yegusha, yazaphula iimpondo zayo zombini; ayaba namandla inkunzi yegusha ukuma ngakuyo, yasuka yaziphosa enkunzini yegusha waxabela, wayinyathela, akwabakho bani uyihlangulayo inkunzi yegusha esandleni sayo.', 'XHO22'),
(31, 8, 8, 'Inkunzi yebhokhwe yakhula ngokuncamisileyo, yakuba yomelele, lwaphuka uphondo olukhulu; yaza yavela ezine ezimangalisayo emva koomoya abane bezulu.', 'XHO22'),
(31, 8, 9, 'Kolunye kuzo kwavela uphondo oluncinane, lwaba lukhulu ngokuncamisileyo, lwabheka ngasezantsi, nasempumalanga, nasemhlabeni onqabileyo.', 'XHO22'),
(31, 8, 10, 'Yaba nkulu, yada yaya kufika emazulwini; Waziphosa phantsi ezinye izidumbu nezinye iinkwenkwezi, wazinyathela ngeenyawo.', 'XHO22'),
(31, 8, 11, 'Wazenza umphathi wamatyala, wawususa umbingelelo wamaxesha onke, yaphoswa phantsi ingcwele yakhe.', 'XHO22'),
(31, 8, 12, 'Kwaye wanikwa uluhlu lwamajoni ngokuchasene nedini eliqhelekileyo ngenxa yesikreqo, kwaye wayilahla phantsi inyaniso; yaye yenjenjalo, yaba yimpumelelo.', 'XHO22'),
(31, 8, 13, 'Ndamva ongcwele ethetha, wathi ke omnye kowangcwele: “Koda kube nini na lo mbono ungowomnikelo wemihla ngemihla, nosisigwebo esitshabalalisayo, ukuze kunyhashwe ingcwele neenkundla? utshintsho?', 'XHO22'),
(31, 8, 14, 'Wathi kum, Koda kube ziintsuku ezingamawaka amabini anamakhulu mathathu; yandule ukuhlanjululwa ingcwele.', 'XHO22'),
(31, 8, 15, 'Mna, Daniyeli, ndawubona loo mbono, ndaza ke ndifuna ukuwucacisa, ndabona emi phambi kwam enjengokubonakala komntu.', 'XHO22'),
(31, 8, 16, 'Ndeva ilizwi lomntu phakathi konxweme lwaseUlayi, lidanduluka, lisithi, Gabriyeli, mawuqonde lo mbono.', 'XHO22'),
(31, 8, 17, 'Wasondela apho bendimi khona; ekuzeni kwayo ndoyika, ndawa ngobuso; wathi kum, Qonda, nyana womntu; ngokuba lo mbono uya kuba lapho ngemihla yokugqibela.', 'XHO22'),
(31, 8, 18, 'Ithe ekuthetheni kwayo nam, ndozela, ndasuke ndanobuthongo obuthe nca, wandichukumisa, wandimisa.', 'XHO22'),
(31, 8, 19, 'Wathi, Yabonani, ndinixelela okuya kubakho ekupheleni kwengqumbo; ngokuba isiphelo siya kuba ngexesha elimisiweyo.', 'XHO22'),
(31, 8, 20, 'Inkunzi yegusha oyibonileyo ineempondo ezimbini, ngookumkani bamaMedi namaPersi;', 'XHO22'),
(31, 8, 21, 'Ibhokhwe etyetyisiweyo ngukumkani waseGrisi, nophondo olukhulu oluphakathi kwamehlo ayo ngukumkani wokuqala.', 'XHO22'),
(31, 8, 22, 'Ekubeni laphulwe, kuya kuvela ezine phambi kwalo, kuvele oorhulumente abane bephuma eluhlangeni, bengengamandla alo.', 'XHO22'),
(31, 8, 23, 'Kuya kuthi ke ekupheleni kobukumkani babo, bakugqiba abathetheli benze ubumenemene, ukumkani uya kuma enobuso obujorha, enobuso obulukhuni, nezigwebo ezibi.', 'XHO22'),
(31, 8, 24, 'Kwaye amandla akhe aya komelela, kungabi ngokwamandla awo; uya konakalisa ngendlela emangalisayo, aphumelele, enze, atshabalalise anamandla nohlanga olungcwele.', 'XHO22'),
(31, 8, 25, 'Kwaye kwakhona ngecebo lakhe uya kuphumelela emsebenzini esandleni sakhe; kwaye uya kuziphakamisa entliziyweni yakhe, kwaye uya kubatshabalalisa abaninzi ngoxolo; kodwa akayi kubethwa ngesandla.', 'XHO22'),
(31, 8, 26, 'Umbono wokuhlwa kusisa okhankanyiweyo uyinyaniso; kuba iya kuba yimihla emininzi.', 'XHO22'),
(31, 8, 27, 'Mna, Daniyeli, ndanikezela, ndaneentsuku ndisifa; emva koko ndema, ndawenza umsebenzi wokumkani; ndamangaliswa ngumbono lowo, kodwa kungekho bani uqondayo.', 'XHO22'),
(31, 9, 1, 'Ngomnyaka wokuqala kaDariyo unyana ka-Asswerosi, wesizwe samaMedi, owamiselwa ukuba abe ngukumkani wobukumkani bamaKaledi;', 'XHO22'),
(31, 9, 2, 'Ngonyaka wokuqala wolawulo lwakhe, mna, Daniyeli, ndaliqonda iincwadi inani leminyaka elafika ngayo ilizwi likaYehova kumprofeti uYeremiya, ukuze kugqitywe iminyaka esixhenxe yamanxuwa aseYerusalem.', 'XHO22'),
(31, 9, 3, 'Ndabubhekisa ubuso bam eNkosini uYehova, ukuba ndifune umthandazo nezibongozo, ngokuzila ukudla, nangamarhonya, nangothuthu.', 'XHO22'),
(31, 9, 4, 'Ndathandaza kuYehova uThixo wam, ndavuma ndathi, uYehova, uThixo omkhulu, owoyikekayo, mabagcine umnqophiso nenceba abamthandayo, abayigcinayo imithetho yakhe;', 'XHO22'),
(31, 9, 5, 'Sonile senza okungendawo senza okungendawo savukela ngokuphambuka emiyalelweni yakho nakwizigwebo zakho.', 'XHO22'),
(31, 9, 6, 'Asibaphulaphulanga abakhonzi bakho, abaprofeti ababethetha egameni lakho kookumkani bethu, nakubathetheli bethu, nakoobawo, nakubo bonke abantu belizwe.', 'XHO22'),
(31, 9, 7, 'Yehova, kobakho okusesikweni, kodwa ubuso bethu budidekile njenganamhlanje; kumadoda akwaYuda, kubemi baseYerusalem, nakumaSirayeli onke, akufuphi nakude, emazweni onke, enawagxothela kuwo ngenxa yezono zabo.', 'XHO22'),
(31, 9, 8, 'Yehova, sinobuso obuneentloni, nobuso bookumkani bethu, nabathetheli bethu, noobawo bethu, ngokuba sonile kuwe.', 'XHO22'),
(31, 9, 9, 'uYehova, uThixo wethu, unenceba, uyaxolela, nakuba sigwilike kuye;', 'XHO22'),
(31, 9, 10, 'Asizange siliphulaphule ilizwi likaYehova uThixo wethu ukuze sihambe ngokwemithetho yakhe awasinika yona ngenkonzo yabakhonzi bakhe, abaprofeti.', 'XHO22'),
(31, 9, 11, 'Onke ke amaSirayeli awugqithile umyalelo wakho; ngenxa yoko kwaphalazwa phezu kwethu isiqalekiso, nesifungo esibhaliweyo emyalelweni kaMoses, umkhonzi kaThixo, ngokuba sonile kuye.', 'XHO22'),
(31, 9, 12, 'Wawamisa ke amazwi akhe, abewathethile ngathi, nangabagwebi bethu ababesiwisele umthetho, ngokusizisela ububi obukhulu; ngokuba akwenzekanga nto injengeYerusalem phantsi kwezulu lonke.', 'XHO22'),
(31, 9, 13, 'Njengoko kubhaliweyo emyalelweni kaMoses, bonke obu bubi busifikela; ke asimbongozanga uYehova uThixo wethu, ukuba sibuye ebubini bethu, siyiqonde inyaniso yakho.', 'XHO22'),
(31, 9, 14, 'Kungenxa yoko le nto uYehova wayibonayo intlekele waza wayizisa kuthi, ngenxa yokuba ulilungisa uYehova uThixo wethu kuyo yonke imisebenzi yakhe ayenzayo, ngenxa yokuba asiliphulaphulanga ilizwi lakhe.', 'XHO22'),
(31, 9, 15, 'Kaloku ke, Yehova Thixo wethu, owabakhuphayo abantu bakho ezweni laseYiputa ngesandla esithe nkqi, wazenzela igama, njengoko kunjalo namhla; sonile, senze ubugwenxa.', 'XHO22'),
(31, 9, 16, 'Owu, Nkosi, ngokobulungisa bakho bonke, mawubuye umsindo wakho nobushushu bakho, emzini wakho iYerusalem, intaba yakho engcwele, ngenxa yezono zethu, nangenxa yobugwenxa boobawo, iYerusalem, nabantu bakho. Intloni kumntu wonke ophezu kwethu.', 'XHO22'),
(31, 9, 17, 'Thixo wethu, wuve umthandazo womkhonzi wakho, nezibongozo zakhe, ubukhanyise ubuso bakho phezu kwengcwele yakho elinxuwa, ngenxa kaYehova.', 'XHO22'),
(31, 9, 18, 'Thoba indlebe yakho, uve, Thixo wam; vula amehlo akho, ubone amanxuwa ethu, nomzi lowo ubizwa ngegama lakho;', 'XHO22'),
(31, 9, 19, 'Yehova, yiva; Nkosi, xolela; Yehova, yiva, yenza; ngenxa yakho, Thixo wam, musa ukulibala; ngokuba igama lakho libizwa emzini wakho, naphezu kwabantu bakho.', 'XHO22'),
(31, 9, 20, 'Xa ndandithetha ndithandaza ndixela isono sam nesono sabantu bam amaSirayeli ndaza ndasondeza umthandazo wam phambi koYehova uThixo wam ngenxa yentaba engcwele yoThixo wam;', 'XHO22'),
(31, 9, 21, 'Ke kaloku, ndisathetha emthandazweni, indoda uGabriyeli, leyo ndayibonayo embonweni ekuqaleni, yaphaphatheka ngokukhawuleza, yandichukumisa ngexesha lombingelelo wangokuhlwa.', 'XHO22'),
(31, 9, 22, 'Wandazisa, wathetha kum, wathi, Daniyeli, ndize kukunika ubulumko nokuqonda.', 'XHO22'),
(31, 9, 23, 'Kwafika ilizwi ekuqaleni komhlana wakho, ndize kukubonisa; ngokuba uthandwa kakhulu; uze uwuqonde umcimbi kwaye unakekele ubuso.', 'XHO22'),
(31, 9, 24, 'Iiveki ezimashumi asixhenxe zibekwe phezu kwabantu bakowenu, naphezu komzi wakowenu ongcwele, ukuba kufezwe isikreqo, kucinywe izono, ukucanyagushelwa ubugwenxa, kuzise ubulungisa obungunaphakade, nokuwumisa umbono nesiprofeto; ethambisa iintlanga ingcwele yeeNgcwele.', 'XHO22'),
(31, 9, 25, 'Yazi ke ngoku uze uqonde ukuba ukususela kwixesha lokuphuma komyalelo wokuba iYerusalem mayivuswe ize yakhiwe kude kube nguMesiya, iNkosana, iya kuthabatha iiveki ezisixhenxe neeveki ezingamashumi amathandathu anesibini, iya kwakha indlela nodonga, ngamaxesha anzima.', 'XHO22'),
(31, 9, 26, 'Emveni ke kweeveki ezimashumi mathandathu anambini, uKristu uya kutshatyalaliswa, kungabi ngenxa yakhe; Umzi nengcwele uya kutshatyalaliswa ngabantu benganga ezayo; yaye isiphelo saso siya kuba ngomkhukula, kude kube sekupheleni kwemfazwe kuya kubakho izinto eziziinqambi eziqinisekisiweyo.', 'XHO22'),
(31, 9, 27, 'Kwaye uya kuwumisa umnqophiso nabaninzi ngeveki enye; yaye phakathi evekini uya kuwuphelisa umbingelelo nomnikelo wokudla okuziinkozo, yaye ngenxa yokwanda kwezinto ezicekisekayo uya kuyenza ibe yintlango kude kube sekupheleni yaye iya kuphalazwa phezu kwalowo ulilolo.', 'XHO22'),
(31, 10, 1, 'Ngomnyaka wesithathu kaKoreshi ukumkani wasePersi, wabonakalaliswa uDaniyeli, ogama linguBheletshatsare; loo mbandela wawuyinyaniso, kodwa ixesha elimisiweyo lalide; wakuqonda oko, wawuqonda umbono.', 'XHO22'),
(31, 10, 2, 'Ngaloo mihla mna, Daniyeli, ndalila iiveki ezintathu.', 'XHO22'),
(31, 10, 3, 'Andisidlanga sonka sinencasa, kungekho nyama nawayini emlonyeni wam, andizithambisanga, zada zazaliseka iiveki ezintathu.', 'XHO22'),
(31, 10, 4, 'Ngomhla wamashumi omabini anesine wenyanga yokuqala, bendiselunxwemeni lomlambo omkhulu oyiHidekele;', 'XHO22'),
(31, 10, 5, 'Ndaphakamisa amehlo am ndaza ndabona indoda eyambethe ilinen, ebotshwe esinqeni ngegolide esulungekileyo yaseUfasi.', 'XHO22'),
(31, 10, 6, 'Umzimba wayo wawunjengoboya begusha, ubuso bayo bunjengombane, ukubonakala kwayo, namehlo ayo enjengezikhuni ezitshangazayo, izandla zayo neenyawo zayo zinjengobhedu olumibalabala, nesandi samazwi ayo ngathi sisandi sesihlwele.', 'XHO22'),
(31, 10, 7, 'Mna, Daniyeli, ndawubona ndedwa umbono lowo, ngenxa yokuba amadoda abenam engawubonanga umbono; kwabakho iindudumo ezinkulu phezu kwabo, basaba baya kuzimela.', 'XHO22'),
(31, 10, 8, 'Mna ndasala ndedwa, ndawubona umbono lowo mkhulu, ndaphelelwa ngamandla;', 'XHO22'),
(31, 10, 9, 'Ndeva ke isandi samazwi akhe, ndasiva isandi samazwi akhe, ndabiwa bubuthongo obunzulu, ubuso bam bubheke phantsi.', 'XHO22'),
(31, 10, 10, 'Ndabona isandla sindichukumisa, sandimisa emadolweni am nasezandleni zam;', 'XHO22'),
(31, 10, 11, 'Yathi kum, Daniyeli, ndodandini, waqonde amazwi endiwathetha kuwe, ume nkqo, ngokuba ndithunywe kuwe ngoku. Uthe akutsho ndaphakama ndingcangcazela.', 'XHO22'),
(31, 10, 12, 'Yathi kum, Musa ukoyika, Daniyeli, ngenxa yokuba kususela kumhla wokuqala wanikela intliziyo yakho ekuqondeni nasekuqeqesheni phambi koThixo wakho, waweva amazwi akho;', 'XHO22'),
(31, 10, 13, 'Ke umthetheli wobukumkani bamaPersi wandimelana nam imihla emashumi mabini anamnye; uyabona, uMikayeli, omnye wakubathetheli abaziintloko, weza kundinceda; yaye ndandilapho nookumkani basePersi.', 'XHO22'),
(31, 10, 14, 'Ndize kukwazisa okuya kubahlela abantu bakowenu ekupheleni kwemihla; ngenxa yokuba lo mbono ngowemihla emininzi.', 'XHO22'),
(31, 10, 15, 'Ithe ekuthetheni kwayo nam, ndabubhekisa ubuso bam emhlabeni, ndasisidenge.', 'XHO22'),
(31, 10, 16, 'Nanko onjengoonyana babantu, echukumisa umlomo wam; Ndawuvula umlomo wam, ndathetha, ndathi kobemi phambi kwam, Camagu, nkosi yam, emehlweni am inimba yam yandongamela, ndaphelelwa ngamandla.', 'XHO22'),
(31, 10, 17, 'Angathini na ke ukuthetha umkhonzi wenkosi yam enkosini yam? ngenxa yokuba mna, ngoko nangoko amandla awazange aphelele kum, yaye kwakungasekho moya kum.', 'XHO22'),
(31, 10, 18, 'Wabuya ke omnye, wandichukumisa njengomntu, wandomeleza.', 'XHO22'),
(31, 10, 19, 'Wathi, Musa ukoyika, sithandwa sam; Uxolo malube nani, yomelelani, ewe, yomelelani. Wathi ekuthetheni kwakhe nam, ndazomeleza, ndathi, Makathethe ukumkani wam; ngokuba undomelezile.', 'XHO22'),
(31, 10, 20, 'Wathi, Uyazi na into endize ngayo kuwe? ngoku ke ndiya kubuya ndiye kulwa neNkosana yasePersi; ndakugqiba, yabona, iNkosana yaseGrisi isiza.', 'XHO22'),
(31, 10, 21, 'Kodwa ndiya kunibonisa okubhalwe kwiZibhalo eziyinyaniso; akukho bani undixolelayo kwezo zinto, ngaphandle kukaMikayeli, inkosana yenu.', 'XHO22'),
(31, 11, 1, 'Kananjalo, ngomnyaka wokuqala kaDariyo umMedi, ndesuka ukuba ndimomeleze ndimhlanganisele.', 'XHO22'),
(31, 11, 2, 'Kwaye ngoku, ndiya kukuxelela inyaniso. Yabona, kuya kuvuka ookumkani abathathu ePersi; unina uya kuba sisityebi ngaphezu kwabo bonke, yaye ngamandla akhe nobutyebi bakhe uya kubaphakamisa bonke nxamnye nobukhosi bamaGrike.', 'XHO22'),
(31, 11, 3, 'Kuya kuvela ukumkani oligorha, alawule ngamandla amakhulu, enze ngokokuthanda kwakhe.', 'XHO22'),
(31, 11, 4, 'Buya kuthi, xa athe waphakama, buqhekezwe ubukumkani bakhe, bahlule oomoya abane basezulwini; kungabi ngokwembewu yakhe, nangokomthetho wakhe abewulawula; ngokuba buya kususwa ubukumkani bakhe, bube bobambi ngaphandle kwabo.', 'XHO22'),
(31, 11, 5, 'Ukumkani wasezantsi uya komelela omnye wakubathetheli bakhe; uya komelela phezu kwakhe, alawule; ulawulo lwakhe luya kuba lulawulo olukhulu.', 'XHO22'),
(31, 11, 6, 'Kwaye bohlanganisana ekupheleni kweminyaka; ngokuba intombi yokumkani wasezantsi iya kuza kukumkani wasentla, ukuba bahlanganiselane; kodwa amandla ezisweli akayi kulisindisa; yaye akayi kuma, nkqu nesandla sakhe, kodwa uya kunikwa, nabo bamzisayo, nabamzalayo, nalowo wamomelezayo kweli phakade.', 'XHO22'),
(31, 11, 7, 'Kodwa ihlumelo leengcambu zakhe liya kuvela elizweni lakhe, yaye ngokuqinisekileyo uya kuza enempi, angene enqabeni yokumkani wasentla, alwe nabo, aze abuye abuye.', 'XHO22'),
(31, 11, 8, 'Wozisa oothixo bazo, nezikhulu zabo, neempahla zabo ezinqwenelekayo zesilivere, nezegolide, azise eYiputa; yaye uya kuhlala ixesha elide kunokumkani wasentla.', 'XHO22'),
(31, 11, 9, 'Ngako oko ukumkani wasezantsi uya kungena ebukumkanini bakhe, abuyele ezweni lakhe.', 'XHO22'),
(31, 11, 10, 'Oonyana bakhe baya kuphakanyiswa, bahlanganisele ndawonye inkitha yamatyala amakhulu; ngokuqinisekileyo omnye uya kuza akhukuliseke, akhukuliseke; ke uya kubuya ayifikele inqaba yakhe engqushu.', 'XHO22'),
(31, 11, 11, 'Wothi ke ukumkani wasezantsi arhole, axabele ukumkani wasentla; yaye uya kuvelisa isihlwele esikhulu; kodwa isihlwele siya kunikelwa esandleni sakhe.', 'XHO22'),
(31, 11, 12, 'Ukuba uthe wasisusa isihlwele, iya kuphakama intliziyo yakhe; kwaye uya kuwisa amashumi amawaka, kodwa ke ngoko akayi kuzinika amandla.', 'XHO22'),
(31, 11, 13, 'Wobuya ukumkani wasentla, amise isihlwele esikhulu ngakumbi kunesokuqala;', 'XHO22'),
(31, 11, 14, 'Ngelo xesha kuya kusukela phezulu abaninzi ngakukumkani wasezantsi; aziphakamise amasela ohlanga lwakowenu ukuba awumise umbono; kodwa baya kuwa.', 'XHO22'),
(31, 11, 15, 'Ngako oko ukumkani wasentla uya kufika, ayiphakamise intaba, ayithimbe imizi enqatyisiweyo;', 'XHO22'),
(31, 11, 16, 'Lowo umchasayo uya kwenza ngokukholeka kuye, akukho namnye uya kuma phambi kwakhe; uya kuma ezweni eliqaqambileyo eliya kuphanziswa sisandla sakhe.', 'XHO22'),
(31, 11, 17, 'Wojika, angene enamandla obukumkani bakhe bonke, namalungisa kunye naye; uya kwenjenjalo, amnike intombi yabafazi, amtshabalalise; kodwa akayi kuma ecaleni kwakhe, yaye akayi kuba naye.', 'XHO22'),
(31, 11, 18, 'Emva koko uya kujikela ubuso bakhe eziqithini, azithimbe ezininzi; engenakungcikivwa, uya kuyibhekisa kuye.', 'XHO22'),
(31, 11, 19, 'Wojika, abhekise enqabeni yelizwe lakhe, akhubeke, awe, angafunyanwa.', 'XHO22'),
(31, 11, 20, 'Kuya kubakho ukwanda kwerhafu kobutyebi bakhe ebuqaqawulini bobukumkani; kodwa ngeentsuku ezimbalwa uya kutshatyalaliswa, kungengamsindo okanye yimfazwe.', 'XHO22'),
(31, 11, 21, 'Elifeni lakhe kuya kusuka kume odelekileyo, ongayi kumnika uzuko lwabukumkani; Ke yena uya kungena enoxolo, abuthabathe ubukumkani ngokucenga.', 'XHO22'),
(31, 11, 22, 'Kwaye ngezandla zomkhukula baya kukhohliswa, babethwe phambi kobuso bakhe; ewe, nomphathi womnqophiso.', 'XHO22'),
(31, 11, 23, 'Emva kokwenziwa komnqophiso naye, uya kusebenza ngobuqhophololo, ngenxa yokuba uya kunyuka aze omelele ngohlanga oluncinane.', 'XHO22'),
(31, 11, 24, 'Uya kuhamba enoxolo kwiindawo ezichumayo zelizwe; uya kwenza abangakwenzanga ooyise nooyise; Uya kuwabela amaxhoba nako konke okuphangiweyo nobutyebi; ewe, kwaye uya kukhupha imigaqo-nkqubo yakhe ngokuchasene nezakhiwo eziqinileyo, ewe, okwethutyana.', 'XHO22'),
(31, 11, 25, 'Wophakamisela phezulu amandla akhe nentliziyo yakhe kukumkani wasezantsi, enempi enkulu enempi; ukumkani wasezantsi uya kuphuma alwe nemikhosi emikhulu enamandlakazi; akakho, ngokuba baya kumenzela iyelenqe.', 'XHO22'),
(31, 11, 26, 'Ewe, abadla isonka sakhe baya kumtshabalalisa, nebandla lakhe likhukuliswe, bawe bafe abaninzi.', 'XHO22'),
(31, 11, 27, 'Kwaye iintliziyo zabo kumkani babini ziya kuthanda ukwenza okubi, yaye baya kuthetha ubuxoki esithebeni sinye; kodwa ayiyi kuphumelela, kuba kwanangoko isiphelo siya kuba sexesha elimisiweyo.', 'XHO22'),
(31, 11, 28, 'Uya kubuyela ezweni lakowabo enendyebo eninzi; intliziyo yakhe ichasene nomnqophiso ongcwele; yaye uya kwenza izenzo aze abuyele emhlabeni wakhe.', 'XHO22'),
(31, 11, 29, 'Ngexesha elimisiweyo uya kubuya eze ngasezantsi; kodwa ayiyi kufana neyokuqala neyokugqibela.', 'XHO22'),
(31, 11, 30, 'Ziya kuza kuye iinqanawa zamaKitim; ke ngoko, uya kuba buhlungu aze abuye kwaye abenomsindo ngalo mnqophiso; uya kwenjenjalo; uya kubuya abe nokuqonda kwabatyekayo emnqophisweni ongcwele.', 'XHO22'),
(31, 11, 31, 'Izixhobo ziya kuma phambi kwakhe, yaye ziya kuyenza inqambi ingcwele yendawo enqatyisiweyo kwaye ziwususe umbingelelo wemihla ngemihla zibeke izinto ezinezothe eziphanzisayo.', 'XHO22'),
(31, 11, 32, 'Abawuphatha kakubi umnqophiso, wobatshabalalisa ngobuqhophololo; ke bona abantu abamaziyo uThixo wabo baya kuqina, bathabathe inyathelo.', 'XHO22'),
(31, 11, 33, 'Izilumko phakathi kweentlanga ziya kufundisa abaninzi, kodwa ziya kukhubeka imihla emininzi likrele, nelangatye, kukuthinjwa nokuphangwa.', 'XHO22'),
(31, 11, 34, 'Ukuba bathe bawa, bothatyathwa ngoncedo oluncinane, basuke abaninzi babalandele ngokuqhatha.', 'XHO22'),
(31, 11, 35, 'Yoza ke inxenye yabanengqiqo, ukubacikide, ibahlambulule, bayenze mhlophe, kude kube lixesha lokuphela, kuba kusaya ngokwexesha elimisiweyo.', 'XHO22'),
(31, 11, 36, 'Kwaye ukumkani uya kwenza ngokokuthanda kwakhe; uya kuziphakamisa, azinyuse ngaphezu koothixo bonke, athethe imisebenzi ebalulekileyo ngoThixo woothixo, abe nempumelelo, ude uphele umsindo; ngokuba oko kumisiweyo kuya kwenzeka.', 'XHO22'),
(31, 11, 37, 'Akayi kumnyamekela uTHIXO wookhokho bakhe, okanye athabathe inkanuko yabafazi okanye nawuphi na uthixo; ngokuba uya kuzenza mkhulu kunabo bonke.', 'XHO22'),
(31, 11, 38, 'Kodwa ngobutyebi bakhe uya kumzukisa uTHIXO wemikhosi, nothixo ababengamazi ookhokho bakhe, uya kumzukisa ngegolide nangesilivere namatye anqabileyo nangezinto ezithandekayo.', 'XHO22'),
(31, 11, 39, 'Uya kukwenza oko ezinqabeni, enothixo wolunye uhlanga, amaziyo, andise uzuko; kwaye uya kwenza abaninzi balawule, alabe ilizwe ngenzuzo.', 'XHO22'),
(31, 11, 40, 'Ke kaloku, ekupheleni kwemihla, ukumkani wasezantsi uya kuqubisana naye, ukumkani wasentla ahlangabeze kuye njengesaqhwithi, eneenqwelo zokulwa, nabamahashe, neenqanawa ezininzi; kwaye uya kungena emazweni, atshayele, adlule.', 'XHO22'),
(31, 11, 41, 'Wobuya eze ezweni eliqaqambileyo, atshabalale amazwe amaninzi; baya kusinda esandleni sakhe uEdom, noMowabhi, nezikhulu zoonyana baka-Amon.', 'XHO22'),
(31, 11, 42, 'Uya kusolula isandla sakhe emazweni, nelizwe laseYiputa aliyi kusinda.', 'XHO22'),
(31, 11, 43, 'Uya kubulawula ubutyebi begolide, nobesilivere, nobezinto zonke ezinqwenelekayo zaseYiputa, alandele emanyathelweni akhe amaLibhiya namaKushi.', 'XHO22'),
(31, 11, 44, 'Ziya kumkhwankqisa iindaba ezivela empumalanga nasentla; ke ngoko uya kubheka phambili ngomsindo omkhulu ukuba atshabalalise abaninzi kwaye ababethe ngesiqalekiso.', 'XHO22'),
(31, 11, 45, 'Uya kuyimisa iminquba yendlu yakhe yobukumkani elwandle, entabeni engcwele yozuko; ukanti uya kufika ekupheleni kwakhe, kungabikho umncedayo.', 'XHO22'),
(31, 12, 1, 'Ngelo xesha uya kusuka eme uMikayeli, umthetheli omkhulu, omele oonyana babantu bakowenu; kuya kubakho ixesha lonxunguphalo ekungazanga kubekho linjalo kususela koko kwabakho uhlanga, kude kube lelo xesha; kwaye ngelo xesha, abantu bakho baya kusindiswa, bonke abo babhaliweyo encwadini.', 'XHO22'),
(31, 12, 2, 'Kuya kuvuka abaninzi kwabaleleyo eluthulini lomhlaba, bavukele ubomi obungunaphakade, abanye bavukele ihlazo nehlazo elingunaphakade.', 'XHO22'),
(31, 12, 3, 'Izilumko ziya kuqaqamba njengokukhanya kwezulu; yaye abo bakhokelele abaninzi ebulungiseni baya kuba njengeenkwenkwezi ngonaphakade kanaphakade.', 'XHO22'),
(31, 12, 4, 'Ke wena, Daniyeli, mawuthi cwaka kuloo mazwi, uyitywine incwadi kude kube lixesha lokuphela; baninzi abaya kuphengulula, kwande ukwazi.', 'XHO22'),
(31, 12, 5, 'Mna, Daniyeli, ndakhangela, ndabona abanye bemi, omnye enganeno komlambo, omnye engaphesheya komlambo;', 'XHO22'),
(31, 12, 6, 'Wathi omnye kwindoda leyo yambethe ilinen emhlophe, ebiphezu kwamanzi omlambo, Koda kube nini na, ukuphela kwale miqondiso?', 'XHO22'),
(31, 12, 7, 'Ndeva ukuba indoda leyo ebeyinxibile leyo, ebiphezu kwamanzi omlambo, yasiphakamisela ezulwini isandla sayo sokunene nesasekhohlo, yafunga odla ubomi kuse emaphakadeni asemaphakadeni, ukuba kuya kubakho ixesha elimisiweyo namaxesha nesiqingatha; yaye xa athe wawagqiba amandla abantu abangcwele, zonke ezi zinto ziya kugqitywa.', 'XHO22'),
(31, 12, 8, 'Ndakuva, kodwa andaqonda; Ndathi ke, Camagu, Nkosi yam, koba yintoni na ukuphela kwezi zinto?', 'XHO22'),
(31, 12, 9, 'Yathi, Ewe, Daniyeli, ngenxa yokuba amazwi aya kuvalelwa, aqiniselwe kude kube lixesha lokuphela.', 'XHO22'),
(31, 12, 10, 'Abaninzi baya kuhlanjululwa, benziwe mhlophe, bahlanjululwe; ke abangendawo baya kwenza okungendawo; bonke abangendawo abayi kukuqonda; ke zona izilumko ziya kuqonda.', 'XHO22'),
(31, 12, 11, 'Kususela kwimini yokushenxiswa kwenkonzo yamaxesha onke, nasekumisweni kwezinto ezinezothe eziphanzisayo, yoba ziintsuku eziliwaka, elinamakhulu amabini, anamanci asithoba.', 'XHO22'),
(31, 12, 12, 'Unoyolo olinde wafika kwiintsuku eziliwaka, elinamakhulu amathathu, anamanci mathathu anesihlanu.', 'XHO22'),
(31, 12, 13, 'Ke wena, hamba uye ekupheleni, ngokuba uya kuphumla, umile emsebenzini wakho ekupheleni kwemihla.', 'XHO22'),
(32, 1, 1, 'Ilizwi likaYehova elafika kuHoseya unyana kaBhere, ngemihla kaOsiya, kaYotam, ka-Ahazi, kaHezekiya, ookumkani bakwaYuda, nangemihla kaYarobheham unyana kaYowashe, ukumkani wakwaSirayeli.', 'XHO22'),
(32, 1, 2, 'Ukuqala kwelizwi likaYehova ngoHoseya. Wathi uYehova kuHoseya, Hamba uye kuthabatha ihenyukazi noonyana behenyukazi; ngokuba ilizwe lihenyuze kakhulu, lemka ekumlandeleni uYehova.', 'XHO22'),
(32, 1, 3, 'Waya ke, wazeka uGomere intombi kaDibhelayim; wakhawula, wamzalela unyana.', 'XHO22'),
(32, 1, 4, 'Wathi uYehova kuye, Mbize ngokuba nguYizereli; okwethutyana, ndophalaza igazi likaYizereli phezu kwendlu kaYehu ndize ndibuphelise ubukumkani bendlu kaSirayeli.', 'XHO22'),
(32, 1, 5, 'Ngaloo mini ndiya kwaphula isaphetha sakwaSirayeli emfuleni waseYizereli.', 'XHO22'),
(32, 1, 6, 'Waphinda wamitha, wazala intombi. Wathi uYehova kuye, Yithiye igama lokuba nguAkenzelwanga-mfesane, ngokuba andiyi kuphinda ndiyenzele nceba indlu kaSirayeli; kodwa ndiya kubasusa kuphele.', 'XHO22'),
(32, 1, 7, 'Kodwa ndiya kuyenzela inceba indlu kaYuda ndize ndibakhulule ngoYehova uThixo wabo, yaye andiyi kubakhulula ngezaphetha okanye ngekrele okanye ngedabi okanye ngamahashe okanye ngabakhweli-mahashe.', 'XHO22'),
(32, 1, 8, 'Uthe akumlumla uLoruhama, wakhawula wazala unyana.', 'XHO22'),
(32, 1, 9, 'Wathi uThixo, Mbize ngokuba nguLowami, ngokuba aningabantu bam, nam andiyi kuba nguThixo wenu.', 'XHO22'),
(32, 1, 10, 'Ke inani loonyana bakaSirayeli liya kuba ngangentlabathi yolwandle, engalinganiswayo, engabalwayo; nalapho kwathiwa kubo, Aningabantu bam nina, kuya kuthiwa kubo, Ningoonyana bakaThixo ophilileyo.', 'XHO22'),
(32, 1, 11, 'Yaye ngokuqinisekileyo baya kuhlanganisana oonyana bakaYuda noonyana bakaSirayeli baze banyule inkokeli enye, baze balishiye ilizwe, ngenxa yokuba iya kuba nkulu imini kaYizereli.', 'XHO22'),
(32, 2, 1, 'Yithini kubazalwana benu, Wenani! noodade benu, uRuhama.', 'XHO22'),
(32, 2, 2, 'Uze umhlasele udadewenu, kuba akangomfazi wam, nam ndingendoda yakhe asuse uthuthu lwakhe nothuthu lwengqolowa yakhe, alususe phakathi kwengqolowa yakhe;', 'XHO22'),
(32, 2, 3, 'hleze ndimhlube, ndimenze abe njengokuzalwa kwakhe, ndimenze abe njengentlango, ndimmise njengomhlaba ongumqwebedu, ndimbulale ngenxano;', 'XHO22'),
(32, 2, 4, 'andiyi kubaconga oonyana bakhe; ngokuba bengabantwana bobuhenyu.', 'XHO22'),
(32, 2, 5, 'Ngenxa yokuba unina ehenyuzile; udane owabazalayo; ngokuba wathi, Ndiya kubashiya abaziintsizana, abandinika isonka sam, namanzi am, noboya begusha bam, neflakisi yam, neoli yam, nento endiyiselayo.', 'XHO22'),
(32, 2, 6, 'Ngako oko, yabona, ndiya kuyambesa indlela yakho ngameva, ndakhe udonga, ukuze angazifumani iindlela zakhe.', 'XHO22'),
(32, 2, 7, 'Uya kubasukela abo bamthandayo, kodwa abayi kubafumana; uya kuzifuna, angazifumani; athi, Ndiya kuya ndibuyele endodeni yam yokuqala; ngokuba oko bekulungile kum kunangoku.', 'XHO22'),
(32, 2, 8, 'Kuba wayengazi ukuba ndim owayemnike ingqolowa, iwayini neoli nokuba ndim owandisa isilivere negolide awayilungiselela uBhahali.', 'XHO22'),
(32, 2, 9, 'Ngako oko ndiya kubuya, ndiyithabathe ingqolowa yam ngexesha layo, newayini yam entsha ngexesha layo elimisiweyo;', 'XHO22'),
(32, 2, 10, 'Ndolibhenca ihlazo lakhe phambi kwamehlo entanda yakhe, kungabikho umhlangulayo esandleni sam.', 'XHO22'),
(32, 2, 11, 'Ndiza kuluphelisa lonke uvuyo lwakhe, imihla yakhe yokubhiyoza, ukuthwasa kwenyanga kwakhe neesabatha zakhe nawo onke amatheko akhe.', 'XHO22'),
(32, 2, 12, 'Ndiya kuzitshabalalisa izidiliya zakhe, nemikhiwane yakhe, abesithi, Ngumvuzo wam endiwunikiweyo zintanda zam; ndiya kuyenza ityholo, idliwe ngamarhamncwa.', 'XHO22'),
(32, 2, 13, 'Ndiya kumvelela ngemihla yooBhahali, abeqhumisela ngenxa yabo, evathe ijikazi lakhe, nezivatho zakhe, wazishiya nezigodo zakhe, wandilibala; utsho uYehova.', 'XHO22'),
(32, 2, 14, 'Ngoko ke, yabona, ndiya kumlahlekisa ndize ndimse entlango ndize ndithethe naye kamnandi.', 'XHO22'),
(32, 2, 15, 'Ndomnika izidiliya zakhe, nentili yeAkore ibe lisango lethemba; yaye uya kuvuma apho njengakwimihla yobutsha bakhe nanjengemini yokunyuka kwakhe evela kwilizwe laseYiputa.', 'XHO22'),
(32, 2, 16, 'Kuya kuthi ngaloo mini, utsho uYehova, undibize ngokuthi, Ndoda yam; kwaye akuyi kuba sathi ndinguBhahali.', 'XHO22'),
(32, 2, 17, 'Kuba ndiya kuwasusa amagama ooBhahali emlonyeni wakhe, namagama angabi sakhunjulwa.', 'XHO22'),
(32, 2, 18, 'Ngaloo mini ndobenzela umnqophiso namarhamncwa elizwe, neentaka zezulu, namarhamncwa omhlaba; yaye ndiya kubususa ubutshaba nekrele nemfazwe ehlabathini yaye uya kubayeka balale bekhuselekile.', 'XHO22'),
(32, 2, 19, 'Kwaye ndiya kukuthembisa ngonaphakade; ndikuvuze ngobulungisa, nangenceba, nangenceba, nangenceba.', 'XHO22'),
(32, 2, 20, 'Nam ndiza kukutshata umkhonzazana wam, ukuze umazi uYehova.', 'XHO22'),
(32, 2, 21, 'Ngaloo mini ndiya kuva, utsho uYehova, ndiwaphulaphule amazulu, nehlabathi liphulaphule;', 'XHO22'),
(32, 2, 22, 'Umhlaba uya kuyiva ingqolowa, newayini entsha, neoli; yaye ziya kuyiva iYizereli.', 'XHO22'),
(32, 2, 23, 'Ndiya kumtyala emhlabeni; ndiya kuba nenceba kulowo ungenzelwanga nceba; Ndiya kuthi kwabangebantu bam: Nabantu bam; bathi, UnguThixo wam wena.', 'XHO22'),
(32, 3, 1, 'Wathi uYehova kum, Uze umthande umfazi olihenyukazi, ngenxa yokuba uYehova ebathanda oonyana bakaSirayeli, abathe babheka thixweni bambi, abathanda iindebe zewayini;', 'XHO22'),
(32, 3, 2, 'Ndazithengela ke ishumi elinesihlanu leeshekele zesilivere, nehomere yerhasi, nesiqingatha sehomere yerhasi.', 'XHO22'),
(32, 3, 3, 'Ndathi kuye, Uya kundizuza imihla emininzi; musa ukukrexeza, ungabi ngowenye indoda; Nam ndiya kuba nawe.', 'XHO22'),
(32, 3, 4, 'Kuba oonyana bakaSirayeli baya kuhlala imihla emininzi bengenakumkani nenkosana nombingelelo nomfanekiso oqingqiweyo nesambatho samagxa noothixo bendlu.', 'XHO22'),
(32, 3, 5, 'Emveni koko baya kubuya oonyana bakaSirayeli, bamquqele uYehova uThixo wabo, noDavide ukumkani wabo; yaye ngokuqinisekileyo baya kumoyika uYehova nobubele bakhe bothando ekupheleni kwexesha.', 'XHO22'),
(32, 4, 1, 'Liveni ilizwi likaYehova, nyana bakaSirayeli; ngokuba uYehova ubambene nabemi belimiweyo, ngokuba kungekho nyaniso, kungekho nceba, kungekho kwazi uThixo ehlabathini.', 'XHO22'),
(32, 4, 2, 'Ngokutshabhisa, nangokuxoka, nangokubulala, ngokuba, nangokuhenyuza, bagqobhozile, igazi lichukumisa igazi.', 'XHO22'),
(32, 4, 3, 'Ngenxa yoko ihlabathi liya kukhala, zikhwankqise zonke izinto ezimi kulo, kwanamarhamncwa elizwe, neentaka zezulu; ewe, kwaneentlanzi zolwandle ziya kususwa.', 'XHO22'),
(32, 4, 4, 'Kodwa makungabikho mntu ubambana nomnye, okanye uthethise omnye, kuba abantu bakowenu banjengabaxambulisana nombingeleli.', 'XHO22'),
(32, 4, 5, 'Woza ke emini, akhubeke umprofeti kunye nawe ebusuku, ndimtshabalalise unyoko.', 'XHO22'),
(32, 4, 6, 'Bayatshabalala abantu bam ngokungazi; ngokuba ukucekisile ukwazi, ndiya kukucekisa nam, ukuba ungabi ngumbingeleli kum; Wawulibala umyalelo kaThixo wakho; ndobalibala abantwana bakho nam.', 'XHO22'),
(32, 4, 7, 'Bathi bakuba besanda, basuka bagatya, banjalo; ke ngoko uzuko lwabo ndiya kuluguqula lube lihlazo.', 'XHO22'),
(32, 4, 8, 'Badla isono sabantu bam, bayibeka intliziyo yabo ebubini babo;', 'XHO22'),
(32, 4, 9, 'Kuya kuba njengababingeleli, yaye ngokuqinisekileyo ndiya kubohlwaya ngenxa yeendlela zabo ndize ndibabuyekeze ngezenzo zabo.', 'XHO22'),
(32, 4, 10, 'Kuba baya kudla, bangahluthi; baya kukrexeza, bangandi, ngokuba bayekile ukumphulaphula uYehova.', 'XHO22'),
(32, 4, 11, 'Uhenyuzo, newayini, newayini entsha, ziwexula intliziyo;', 'XHO22'),
(32, 4, 12, 'Abantu bam babuzisa ubutyebi babo, umsimelelo wabo ubaxelela; ngokuba umoya wobuhenyu ubalahlekisile, bahenyuze phantsi koThixo wabo.', 'XHO22'),
(32, 4, 13, 'Babingelela ezincotsheni zeentaba, baqhumisele ezindulini, phantsi kwemioki, nemipopulari, namasundu, kuba ulungile umthunzi wayo; ke ngoko, iintombi zenu ziyakrexeza, namadoda enu ayakrexeza.', 'XHO22'),
(32, 4, 14, 'Andiyi kuzivelela iintombi zenu, ngokokuba zihenyuza, nabalingane benu bekrexeza; ngokuba bewaqhawule umtshato namahenyukazi, babingelela namahenyukazi; ngenxa yoko abantu abangaqondiyo baya kukhubeka.', 'XHO22'),
(32, 4, 15, 'Ke ukuba uthe wakrexeza, Sirayeli, makangoni uYuda; musani ukuya eGiligali, musani ukunyuka niye eBhetaven, ningafungi, nithi, Ehleli nje uYehova!', 'XHO22'),
(32, 4, 16, 'Ngokuba ukreqile uSirayeli njengethokazi lenkomo; ngoku, uYehova uya kubalusa njengemvana endaweni ebanzi.', 'XHO22'),
(32, 4, 17, 'uEfrayim uhlobene nezithixo; makame.', 'XHO22'),
(32, 4, 18, 'Isiselo sabo simuncu, bayahenyuza ngamaxesha onke; Amagosa akhe afuna ukuhlazeka, akunike.', 'XHO22'),
(32, 4, 19, 'Umoya umbambile ngamaphiko akhe, kwaye baya kuba neentloni yimibingelelo yabo.', 'XHO22'),
(32, 5, 1, 'Liveni eli lizwi, babingeleli; yivani, ndlu kaSirayeli; yivani, nina bendlu yokumkani; kuba umgwebo wakho uya kunifikela, ngenxa yokuba naba ngumgibe eMizpa nomnatha owawakhiwe eTabhore.', 'XHO22'),
(32, 5, 2, 'Ke zona izixholoxholo zizimisele ukubulala, nangona mna bendikhalimela wonke umntu.', 'XHO22'),
(32, 5, 3, 'Mna ndimazi kakuhle uEfrayim, noSirayeli akafihlakele kum ngokuba ngoku uEfrayim uyakrexeza, uSirayeli uzenze inqambi.', 'XHO22'),
(32, 5, 4, 'Abayi kuziqinisa izenzo zabo ukuze babuyele kuThixo wabo, ngenxa yokuba umoya wobuhenyu uphakathi kwabo, yaye abazange bamazi uYehova.', 'XHO22'),
(32, 5, 5, 'Libonakala ebusweni bakhe iqhayiya likaSirayeli; ngoko ke, uSirayeli noEfrayim baya kukhubeka ngobugwenxa babo; noYuda wokhubeka kunye nabo.', 'XHO22'),
(32, 5, 6, 'Baya kumfuna uYehova benempahla yabo emfutshane neenkomo zabo; kodwa abayi kumfumana; wabashiya.', 'XHO22'),
(32, 5, 7, 'Benza ubumenemene kuYehova, ngenxa yokuba bazala oonyana basemzini; yandule ke inyanga ibadle kunye nesabelo sabo.', 'XHO22'),
(32, 5, 8, 'Vuthelani isigodlo eGibheha, vuthelani isigodlo eRama; khwaza ngezwi elikhulu, Bhetaven, emva kwakho, Bhenjamin!', 'XHO22'),
(32, 5, 9, 'Kuya kuba senkangala kwelakwaEfrayim ngemini yokohlwaya; ezizweni zakwaSirayeli ndixele okuya kwenzeka.', 'XHO22'),
(32, 5, 10, 'Abathetheli bakwaYuda baba njengabo basusa impi; ngoko ke, ndiya kuphalaza ubushushu bam phezu kwabo njengamanzi.', 'XHO22'),
(32, 5, 11, 'Wacinezelwa uEfrayim waza watyunyuzwa ekugwetyweni, ngenxa yokuba wayekulungele ukuwuthobela lo myalelo.', 'XHO22'),
(32, 5, 12, 'Ngoko ke ndiya kuba njengenundu kuEfrayim nanjengokubola kwindlu kaYuda.', 'XHO22'),
(32, 5, 13, 'Xa wasibonayo uEfrayim isifo sakhe, uYuda walibona inxeba lakhe; Akaba nako ukukuphilisa, nokuphilisa inxeba lakho.', 'XHO22'),
(32, 5, 14, 'Kuba ndiya kuba njengengonyama kuEfrayim, nanjengengonyama kwindlu kaYuda; nam ndiya kuziqwenga; ndiya kumthabatha, kungabikho umsindisayo.', 'XHO22'),
(32, 5, 15, 'Ndiya kuhamba ndize ndibuyele endaweni yam bade bavume iziphoso zabo kwaye bafune ubuso bam; ekubandezelekeni kwabo baya kundifuna ngokukhawuleza.', 'XHO22'),
(32, 6, 1, 'Yizani sibuyele kuYehova, kuba eqwengile, wosiphilisa; Ubethile, yaye uya kusibopha.', 'XHO22'),
(32, 6, 2, 'Wosidlisa ubomi, kwakuba ntsuku mbini; wosimisa ngomhla wesithathu, sidle ubomi phambi kwamehlo akhe.', 'XHO22'),
(32, 6, 3, 'Soqonda ke, ukuba sisaqhubeka simazi uYehova; ukuphuma kwakhe kunjengokusa; yaye uya kuza kuthi njengemvula, njengemvula egalela emhlabeni.', 'XHO22'),
(32, 6, 4, 'Ndiya kwenza ntoni na kuwe, Efrayim? Ndiya kwenza ntoni na ngawe, Yuda? Ngokuba ukulunga kwakho kunjengelifu lokusa, elidlula njengombethe wokusa.', 'XHO22'),
(32, 6, 5, 'Ngenxa yoko ndababulala abaprofeti; Ndibabulele ngelizwi lomlomo wam, nezigwebo zakho njengokukhanya okuphumayo.', 'XHO22'),
(32, 6, 6, 'Kuba ndithanda inceba, andithandi mbingelelo; kwaye ukwazi uThixo kungaphezu komlilo.', 'XHO22'),
(32, 6, 7, 'Bawaphula ngokwabantu umnqophiso; apho bandiqhatha.', 'XHO22'),
(32, 6, 8, 'iGiliyadi ngumzi wabenzi bobubi, uzele ligazi.', 'XHO22'),
(32, 6, 9, 'Njengoko lilalela umntu ngamatutu, liya kwandula ke libulale iqela lababingeleli endleleni, ngokuba belihlazo.', 'XHO22'),
(32, 6, 10, 'Ndibone into eyoyikekayo kwindlu kaSirayeli: kukho ukrexezo kwaEfrayim, uSirayeli uzenze inqambi.', 'XHO22'),
(32, 6, 11, 'Nawe, Yuda, wawusilungisile isivuno ekubuyiseleni kwam isahlulo sabantu bakowethu.', 'XHO22'),
(32, 7, 1, 'Xa ndifuna ukumphilisa uSirayeli, batyhileka ububi bukaEfrayim nobubi bakwaSamari; ngokuba bexoka; lingena isela, amatutu aphanga ngaphandle.', 'XHO22'),
(32, 7, 2, 'Abatsho entliziyweni yabo ukuthi ndobukhumbula bonke ububi babo; ziphambi kobuso bam.', 'XHO22'),
(32, 7, 3, 'Bamvuyisile ukumkani ngeento zabo ezimbi, nabathetheli ngobuxoki babo.', 'XHO22'),
(32, 7, 4, 'Bonke bephela bangabakrexezi, njengeziko elibaswa ngumoji-zonka, oyekayo ukunyathela intlama, ide ifukuke.', 'XHO22'),
(32, 7, 5, 'Ngemini yokumkani wethu abathetheli bamxhakamfule ngeendebe zewayini; wasolula isandla sakhe kunye nabagculeli.', 'XHO22'),
(32, 7, 6, 'Bazilungisa iintliziyo zabo njengombumbi bebukele; umpheki wabo uhlala ubusuku bonke; kusasa uyavutha njengomlilo olenyayo.', 'XHO22'),
(32, 7, 7, 'Bonke bephela bavutha njengeziko, batshisa abagwebi babo; bonke ookumkani babo bawa; akukho namnye kubo ondinqulayo.', 'XHO22'),
(32, 7, 8, 'uEfrayim uxunelene nabantu; uEfrayim liqebengwana elingaguqukiyo.', 'XHO22'),
(32, 7, 9, 'Abasemzini bawadle bawadla amandla akhe, ukanti yena akakwazi oko; Ewe, izimvi zilapha naphaya, kodwa akazi yena.', 'XHO22'),
(32, 7, 10, 'Libonakala emehlweni akhe iqhayiya likaSirayeli; akabuyelanga kuYehova uThixo wawo, akamfunanga ngenxa yoko.', 'XHO22'),
(32, 7, 11, 'uEfrayim unjengevukuthu elimathileyo, elingenantliziyo; babiza iYiputa, beza eAsiriya.', 'XHO22'),
(32, 7, 12, 'Ekuhambeni kwabo ndiya kubabetha; ndiya kubahlisa njengeentaka zezulu; ndiya kubohlwaya ngokwentlanganiso yabo;', 'XHO22'),
(32, 7, 13, 'Yeha ke bona! ngokuba babaleka kum, mabafikelwe kukutshabalala ngokuba bandingcatshile; Mna ndibakhulule, basuka bona bathetha ubuxoki ngam.', 'XHO22'),
(32, 7, 14, 'Abandinqulanga ngentliziyo yabo, balililela ezililini zabo;', 'XHO22'),
(32, 7, 15, 'Nangona ndibabophile ndaza ndatywina izandla zabo, kodwa bacinga ububi ngam.', 'XHO22'),
(32, 7, 16, 'Baya kubuya, kungekhona kOsenyangweni; banjengesaphetha esityeketyeke; Baya kuwa likrele abathetheli babo, ngenxa yobulukhuni bolwimi lwabo; ibe yintlekisa kubo ezweni laseYiputa.', 'XHO22'),
(32, 8, 1, 'Faka isigodlo emlonyeni wakho. Uyihlasele njengexhalanga indlu kaYehova, ngenxa yokuba bawaphule umnqophiso wam, bawaphule umyalelo wam.', 'XHO22'),
(32, 8, 2, 'Wondibiza uSirayeli, athi, Thixo wam, siyakwazi, Thixo wethu;', 'XHO22'),
(32, 8, 3, 'Ulahlile uSirayeli izinto ezilungileyo; utshaba malumsukele.', 'XHO22'),
(32, 8, 4, 'Bamise ookumkani, kungengam; Bamise abathetheli, ndingazi mna; isilivere yabo negolide yabo bazenzele izithixo, ukuze batshatyalaliswe.', 'XHO22'),
(32, 8, 5, 'Ithole lakho, elakwaSamari, likulahlile; Umsindo wam uyavutha; Kuya kuthatha ixesha elingakanani ukuba bafezekise ubumsulwa?', 'XHO22'),
(32, 8, 6, 'Kuba nalo laliphuma kwaSirayeli: lalisenziwa ngomsebénzi; ngoko ke, asinguThixo, kodwa lithole laseSamariya eliya kutyunyuzwa.', 'XHO22'),
(32, 8, 7, 'Ngokuba babehlwayele umoya, bavuna umoya osisaqhwithi; ihlumelo aliyi kuvelisa umgubo; ukuba kunjalo, boyidla abasemzini.', 'XHO22'),
(32, 8, 8, 'Utshabalale uSirayeli; ngoku baya kuba phakathi kweentlanga njengesenzo sobubi.', 'XHO22'),
(32, 8, 9, 'Kuba bona banyuke baya eAsiriya, njengeesile lasendle; uEfrayim waqesha izithandwa.', 'XHO22'),
(32, 8, 10, 'Ewe, nakuba beqeshwa ezintlangeni, ndiya kubabutha ngoku, bambambazele kancinane ngenxa yomthwalo wokumkani wabathetheli.', 'XHO22'),
(32, 8, 11, 'Kuba uEfrayim uzandisile izibingelelo zesono;', 'XHO22'),
(32, 8, 12, 'Ndambhalela izinto ezinkulu zomyalelo wam;', 'XHO22'),
(32, 8, 13, 'Babingelela inyama, badla; ke uYehova akakholiswa ngabo; ngoku niya kubukhumbula ububi babo, nizohlwaye ngenxa yezono zabo; baya kubuyela eYiputa.', 'XHO22'),
(32, 8, 14, 'Ngokuba uSirayeli wamlibala uMenzi wakhe, wakha iitempile; noYuda uzandisile izixeko ezinqatyisiweyo, kodwa mna ndiya kuphosa umlilo kwizixeko zakhe, kwaye ngokuqinisekileyo uya kuzitshisa iinqaba zakhe ezinde.', 'XHO22'),
(32, 9, 1, 'Musa ukuvuya, Sirayeli, ngokuvuya kweentlanga; ngokuba ukrexezile, umshiyile uThixo wakho, wawuthanda umvuzo wazo zonke izithungu zengqolowa.', 'XHO22'),
(32, 9, 2, 'Isixovulelo sewayini nesixovulelo sewayini asiyi kubondla, nobulembu buya kutshabalala kubo.', 'XHO22'),
(32, 9, 3, 'Abahlali ezweni likaYehova; kodwa uEfrayim uya kubuyela eYiputa, yaye baya kudla izinto ezingahlambulukanga eAsiriya.', 'XHO22'),
(32, 9, 4, 'Abayi kuzisa iwayini kuYehova, yaye abayi kumkholisa; basuke ababuleweyo babe njengesonka sosizi kubo; Bonke abayidlayo boba ziinqambi; ngokuba ukudla kwabo akuyi kungena endlwini kaYehova ngenxa yomphefumlo wabo.', 'XHO22'),
(32, 9, 5, 'Niya kwenza ntoni na ngemini yomthendeleko nangemini yomthendeleko kaYehova?', 'XHO22'),
(32, 9, 6, 'Kuba, yabonani, bemkile ngenxa yembuqo; iYiputa iya kubabutha, iMofi iya kubangcwaba; ziya kudla ilifa iimbovane zabo zesilivere; kukho ameva ezintenteni zabo.', 'XHO22'),
(32, 9, 7, 'Kufike iimini zokuvelela, kufike iimini zempindezelo; Uya kwazi ke uSirayeli ukuthi: Umprofeti usisidenge, uyageza indoda enomoya, ngenxa yobuninzi bobubi bakho, nangenxa yentiyo yakho eninzi.', 'XHO22'),
(32, 9, 8, 'Ngumboniseli uEfrayim ngakuThixo wam; umprofeti ngumgibe womthiyeli ezindleleni zakhe zonke, ulizothe endlwini yoThixo wakhe.', 'XHO22'),
(32, 9, 9, 'Bacikidekile, njengokuba bekunjalo ngemihla yaseGibheha; ke ngoko, uya kuyikhumbula inkohlakalo yabo kwaye abohlwaye ngenxa yezono zabo.', 'XHO22'),
(32, 9, 10, 'Ndamfumana uSirayeli enjengediliya entlango; Ndabubona uyihlo njengeqabaka kuwokuqala umkhiwane; Ke bona baya eBhahali-pehore, bazahlula beneentloni; namadlavu awo asala ngokokuthanda kwawo.', 'XHO22'),
(32, 9, 11, 'uEfrayim, uzuko lwakhe luya kuphaphazela njengentaka;', 'XHO22'),
(32, 9, 12, 'Nokuba bathe bakhulisa oonyana, ndobaphanga, kungabikho usalayo; ewe, yeha nabo xa ndibashiya!', 'XHO22'),
(32, 9, 13, 'Njengoko ndayibonayo iTire, uEfrayim uzinzile ekriweni, kodwa uEfrayim uya kubakhokelela kumbulali oonyana bakhe.', 'XHO22'),
(32, 9, 14, 'Yehova, banike; wobanika ntoni na? banike izisu ezomileyo namabele.', 'XHO22'),
(32, 9, 15, 'Bonke ububi babo buseGiligali, kuba ndabathiya khona; ngenxa yobubi beentlondi zabo, ndiya kubagxotha endlwini yam, ndingabi sabathanda;', 'XHO22'),
(32, 9, 16, 'Ubethiwe uEfrayim, ingcambu yabo yomile, abayi kuxakatha siqhamo; nokuba bathe bazala, ndosibulala nesiqhamo esinqwenelekayo sesizalo sabo.', 'XHO22'),
(32, 9, 17, 'uThixo wam uya kubashiya, ngenxa yokuba bengakuphulaphulanga, yaye baya kuphalaphala ezintlangeni.', 'XHO22'),
(32, 10, 1, 'uSirayeli ngumdiliya onabileyo, wazixakathela iziqhamo ngokwawo; ngobuninzi beziqhamo zakhe, wándisa izibingelelo; ngokulunga kwelizwe lakhe benza imifanekiso emihle.', 'XHO22'),
(32, 10, 2, 'Yaba buthelezi intliziyo yabo; ngoko ke baya kufunyanwa bebuthathaka; uya kuzidiliza izibingelelo zabo, ayitshabalalise izithixo zabo.', 'XHO22'),
(32, 10, 3, 'Ngokuba ngoku baya kuthi, Asinakumkani, kuba asimoyikanga uYehova; uya kusenza ntoni na ukumkani?', 'XHO22'),
(32, 10, 4, 'Bathetha amazwi yaye bafunga ngobuxoki ngokwenza umnqophiso;', 'XHO22'),
(32, 10, 5, 'Abemi baseSamariya baya kutyhwatyhwa ngenxa yamathole eenkomo aseBhete-aven, ngenxa yokuba abantu bakhona baya kuzilela ngenxa yayo nababingeleli bayo abayenzela isijwili, ngenxa yozuko lwalo, ngenxa yokuba lumkile kulo.', 'XHO22'),
(32, 10, 6, 'Liya kusiwa eAsiriya libe ngumnikelo kukumkani uYarebhi; uya kudana uEfrayim, abe neentloni uSirayeli ngenxa yecebo lakhe.', 'XHO22'),
(32, 10, 7, 'Ke elakwaSamari, ukumkani walo udakile, njengegwebu phezu kwamanzi;', 'XHO22'),
(32, 10, 8, 'Kananjalo iintaba zaseAfene, oko kukuthi, isono sikaSirayeli, ziya kuphanziswa; Kuya kukhula imithana enameva neenkunzane ezibingelelweni zabo; baya kuthi kwiintaba, Sigubungeleni; nakwiinduli, Masiwe.', 'XHO22'),
(32, 10, 9, 'Kususela kwimihla yaseGibheha, Sirayeli wona; kulapho babemi khona;', 'XHO22'),
(32, 10, 10, 'Ndiya kubathethisa ke ngokomnqweno wam; kwaye abantu baya kubahlanganisana ukuba bababophe ngeendlela zabo zombini.', 'XHO22'),
(32, 10, 11, 'uEfrayim unjengethokazi lenkomo eliqeqeshiweyo, elithanda ukudla ingqolowa; ke ndayigqitha intamo yalo entle; Ndiya kukhwela phezu kwaEfrayim; uYuda uya kutyala, uYakobi uya kusiqhekeza isanda sakhe.', 'XHO22'),
(32, 10, 12, 'Hlwayela ubulungisa, uvune inceba; limeni amasimi enu, ngokuba ilixesha lokumquqela uYehova, ade afike aninise ubulungisa.', 'XHO22'),
(32, 10, 13, 'Nahlwayela ubugqwetha, navuna ubugqwetha; nadla iziqhamo zobuxoki, ngokuba ukholose ngendlela yenu, nangobuninzi bamagorha akho.', 'XHO22'),
(32, 10, 14, 'Kuya kubakho isiphithiphithi phakathi kwabantu bakowenu, ziphangwe zonke iinqaba zakho, njengoko uSolomon wayibhulayo iBhete-arbhele, ngemini yemfazwe;', 'XHO22'),
(32, 10, 15, 'Iya kwenjenjalo kuni iBheteli, ngenxa yobubi bezinto zenu ezimbi; ngomso uya kuqalekiswa ukumkani wakwaSirayeli.', 'XHO22'),
(32, 11, 1, 'Oko uSirayeli ebemncinane, ndamthanda, ndambiza unyana wam ngokuba ungumYiputa.', 'XHO22'),
(32, 11, 2, 'Wathi, bababizile, baza barhoxa nabo;', 'XHO22'),
(32, 11, 3, 'Ke mna ndamfundisa uEfrayim ukuhamba, ndamhlangula esandleni sakhe; babengazi ke ukuba ndibaphilise.', 'XHO22'),
(32, 11, 4, 'Ndabatsala ngeentsontelo zomntu, ngeentsontelo zothando; kubo ndaba njengabo basusa idyokhwe emihlathini yabo, yaye ndabanika inyama.', 'XHO22'),
(32, 11, 5, 'Akayi kubuyela kwilizwe laseYiputa, kodwa iAsiriya yoba ngukumkani wakhe, ngenxa yokuba abavumanga ukubuya.', 'XHO22'),
(32, 11, 6, 'Ikrele liya kuba semizini yakhe, liwavuthululele amagatya akhe, liwadle ngenxa yamacebo abo;', 'XHO22'),
(32, 11, 7, 'Abantu bam bayathanda ukundishiya; nokuba banqula uPhezukonke, akukho bani unokumphakamisa.', 'XHO22'),
(32, 11, 8, 'Ndingathini ukukushiya, Efrayim? Ndingathini na ukukusindisa, Sirayeli? Ndingathini na ukukufanisa neAdama? ndikwenze ube njengeTsebhoyim? Yaguquka intliziyo yam phakathi kwam, Umvandedwa wam wavutha kunye.', 'XHO22'),
(32, 11, 9, 'Andiyi kukuvumela ukuvutha komsindo wam kuphele, andiyi kubuya ndimonakalise uEfrayim; ngokuba ndinguThixo, andimntu; Ongcwele phakathi kwenu, kwaye andiyi kungena kuwo umzi.', 'XHO22'),
(32, 11, 10, 'Baya kumlandela uYehova; Ubharhula njengengonyama; ukuba uthe wabharhula, bangcangcazele oonyana basentshonalanga.', 'XHO22'),
(32, 11, 11, 'Baya kuvela eYiputa, begubha njengeentaka, nanjengehobe ezweni laseAsiriya; yaye ndiza kubabeka emakhayeni abo,’ utsho uYehova.', 'XHO22'),
(32, 11, 12, 'uEfrayim undirhawule ngobuxoki, indlu kaSirayeli ngenkohliso; noYuda usengukumkani kuThixo, kunye nabangcwele.', 'XHO22'),
(32, 12, 1, 'uEfrayim udla umoya, ngokusukelana nolophu lwasempumalanga; Ukwandisa imihla ngemihla ukuxoka nokonakala; Bazibandakanya nama-Asiriya, kwaza kwasiwa ioli eYiputa.', 'XHO22'),
(32, 12, 2, 'uYehova ubambene noYuda, umvelela uYakobi ngokweendlela zakhe; uya kumbuyekeza ngokwemisebenzi yakhe.', 'XHO22'),
(32, 12, 3, 'Yena wambamba umninawa wakhe ngesithende esizalweni sikanina, waza ke womelela ngokomelela kuThixo.', 'XHO22'),
(32, 12, 4, 'Ewe, womelela ngesithunywa eso; walila, wathandaza kuye; wamfumana eBheteli, wathetha nathi khona;', 'XHO22'),
(32, 12, 5, 'NdinguYehova, uThixo wemikhosi; uYehova sisikhumbuzo sakhe.', 'XHO22'),
(32, 12, 6, 'Ngoko ke guqukani niye kuThixo wenu; Gcina inceba nokusesikweni, Ukholose ngoThixo wakho ngamaxesha onke.', 'XHO22'),
(32, 12, 7, 'Ungumrhwebi unezikali zenkohliso esandleni sakhe; uyathanda ukucofa.', 'XHO22'),
(32, 12, 8, 'Wathi uEfrayim, Noko ke, ndisisityebi, ndizifumanele okulungileyo; kuyo yonke imisebenzi yam, abayi kufumana bugwenxa kum.', 'XHO22'),
(32, 12, 9, 'Yaye mna, Yehova, uThixo wenu, ezweni laseYiputa, ndiya kunigcina nihlala eminqubeni njengakwimihla yomthendeleko.', 'XHO22'),
(32, 12, 10, 'Ndathetha ngabaprofeti, ndazandisa imibono ndaza ndasebenzisa imizekeliso ngomsebenzi wabaprofeti.', 'XHO22'),
(32, 12, 11, 'Ngaba kukho okungekho sikweni eGiliyadi? okunene, asinto; Babingelela iinkunzi ezintsha zeenkomo eGiligali; nezibingelelo zabo zibe njengemfumba emingxunyeni yelizwe.', 'XHO22'),
(32, 12, 12, 'uYakobi wasabela ezweni lakwa-Aram; uSirayeli wasebenzela inkazana leyo, ekwalusa impahla emfutshane yenkazana leyo.', 'XHO22'),
(32, 12, 13, 'uYehova wamkhupha uSirayeli eYiputa ngomprofeti, waza wasindiswa ngomprofeti.', 'XHO22'),
(32, 12, 14, 'uEfrayim wamgculela kabukhali; ngako oko, igazi lakhe uya kulishiya phezu kwakhe, nehlazo lakhe libuyele eNkosini yakhe.', 'XHO22'),
(32, 13, 1, 'Wathi akuthetha uEfrayim, kwabakho ukothuka; wathi wona kuBhahali, wafa.', 'XHO22'),
(32, 13, 2, 'Baphinda bona, bazenzela imifanekiso eqingqiweyo ngesilivere yabo, bazenzela izithixo njengokuqonda kwabo; yonke loo nto ingumsebenzi weengcibi bathetha ngawo, besithi, Ababingelelayo mabawanga amathole.', 'XHO22'),
(32, 13, 3, 'Ngako oko baya kuba njengelifu lakusasa, nanjengombethe ophuma ngokukhawuleza, nanjengamalaku aphetshethwa emhlabeni sisaqhwithi, nanjengomsi womlilo.', 'XHO22'),
(32, 13, 4, 'Kodwa ndinguYehova uThixo wakho, ngoxa nisezweni laseYiputa, yaye akuyi kwazi thixo ungendim, kuba akukho namnye umsindisi ngaphandle kwam.', 'XHO22'),
(32, 13, 5, 'Mna ndakwazi entlango, ezweni lasekubaleleni.', 'XHO22'),
(32, 13, 6, 'Bahlutha ngokwedlelo labo; bahluthe, ziphakame iintliziyo zabo; ke ngoko, bandikhusele.', 'XHO22'),
(32, 13, 7, 'Ngoko ke ndoba njengengonyama kubo; Ndobabona njengengwe endleleni;', 'XHO22'),
(32, 13, 8, 'Ndothi gaga kubo njengebhere ehluthwe amathole ayo, ndikrazule iintloko zentliziyo yabo, ndibadle khona njengengonyama; irhamncwa liya kubadla.', 'XHO22'),
(32, 13, 9, 'Uphanzile, Sirayeli; kodwa uncedo lwakho lukum.', 'XHO22'),
(32, 13, 10, 'Ndiya kuba ngukumkani wenu; Uphi na omnye wabahlanguli bam ezixekweni zakho zonke? nabagwebi bakho, othe wena, Ndinike ukumkani nabathetheli?', 'XHO22'),
(32, 13, 11, 'Ndininike ukumkani ndinomsindo, ndimsusa emsindweni wam.', 'XHO22'),
(32, 13, 12, 'Bubotshiwe bububi bukaEfrayim; sigutyungelwe isono sakhe.', 'XHO22'),
(32, 13, 13, 'Uya kufika inimba yozalayo; ungunyana wesidenge; ngokuba akafanele ahlale ithuba elide endaweni yokufa kwabantwana.', 'XHO22'),
(32, 13, 14, 'Ndiya kubahlangula esandleni selabafileyo; Ndiya kubahlangula ekufeni; Kufa, ndiya kuba kukufa kwakho; Wena ngcwaba, ndiya kuba linxuwa kuwe; inguquko iya kusithela emehlweni am.', 'XHO22'),
(32, 13, 15, 'Nokuba uthe wavelisa isiqhamo phakathi kwabazalwana bakhe, uya kufika umoya wasempuma, umoya kaYehova unyuke uvela entlango, yaye umthombo wakhe uya koma, nequla lakhe lome; Uya kubuphanga ubuncwane bazo zonke izinto ezithandekayo.', 'XHO22'),
(32, 13, 16, 'ElakwaSamari liya kusalalwa; ngokuba egwilikile kuThixo wakhe; baya kuwa ngomkhonto; iintsana zabo ziya kuchithwa-chithwa, nabamithiyo babo badliwe.', 'XHO22'),
(32, 14, 1, 'Sirayeli, buyela kuYehova, uThixo wakho; ngokuba ukhubekile ebugwenxeni bakho.', 'XHO22'),
(32, 14, 2, 'Wathabathe amazwi, uguquke kuYehova; nithi ke kuye, Bususe bonke ububi, wamkele ngenceba, ukuze sibuyekeze iinkunzi zeenkomo ezintsha, imilomo yethu.', 'XHO22'),
(32, 14, 3, 'uAsiriya akayi kusisindisa; asiyi kukhwela emahasheni, nomsebenzi wezandla zethu asiyi kuba sathi: Thixo wethu, ngokuba inkedama ifumana inceba kuwe.', 'XHO22'),
(32, 14, 4, 'Ndiya kukuphilisa ukukreqa kwabo, ndiya kubathanda ngesisa, ngokuba umsindo wam ubuyile kuye.', 'XHO22'),
(32, 14, 5, 'Ndiya kuba njengombethe kuSirayeli; uya kutyatyamba njengenyibiba, amilise iingcambu njengeLebhanon.', 'XHO22'),
(32, 14, 6, 'Amasebe awo aya kunaba, yaye ubuhle bakhe bufana nomnquma, yaye ivumba lawo elimnandi liya kuba njengelaseLebhanon.', 'XHO22'),
(32, 14, 7, 'Baya kubuya abahlala emthunzini wakhe; baya kudla ubomi njengengqolowa, batyatyambe njengeediliya; isongo sayo siya kuba njengewayini yaseLebhanon.', 'XHO22'),
(32, 14, 8, 'uEfrayim uya kuthi: “Ndinantoni na yakwenza nemifanekiso eqingqiweyo? Ndamva ndaza ndamazi: Ndifana nomsipres oluhlaza. Kum zifunyanwa iziqhamo zakho.', 'XHO22'),
(32, 14, 9, 'Ngubani na olumkileyo, aziqonde ezo zinto? zilumkile na wena? ngokuba zibubulungisa iindlela zikaYehova, yaye amalungisa ahamba ngazo; ke bona abangendawo baya kuwela kuyo.', 'XHO22'),
(33, 1, 1, 'Ilizwi likaYehova elafika kuYoweli, unyana kaPetuweli.', 'XHO22'),
(33, 1, 2, 'Yivani oku, nina madoda makhulu, nive, nonke bemi belizwe; Ngaba kwakunjalo ngemihla yenu, nangemihla yooyihlo?', 'XHO22'),
(33, 1, 3, 'Kuxeleni koonyana benu, oonyana benu bakuxele koonyana babo, oonyana babo bakuxele kwisizukulwana esisemva kwabo.', 'XHO22'),
(33, 1, 4, 'Okuseleyo kumpethu kudliwa ziinkumbi; okuseleyo kwadliwa ziinkumbi; okuseleyo eenkumbini, kwadliwa ngumqikela.', 'XHO22'),
(33, 1, 5, 'Vukani, manxila, nikhale; lilani, nonke baseli bewayini, ngenxa yewayini entsha; ngenxa yokuba isusiwe emlonyeni wakho.', 'XHO22'),
(33, 1, 6, 'Ngenxa enokuba lungene ezweni lam uhlanga olunamandla, olungenakubalwa, olumazinyo amazinyo engonyama, nezidlele zengonyama enkulu.', 'XHO22'),
(33, 1, 7, 'Uwutshabalalisile umdiliya wam, lwawunyothula umkhiwane wam; Wayikhulula wayilahla; amasebe awo amhlophe.', 'XHO22'),
(33, 1, 8, 'Lilile njengentombi eyambethe ukuzila ngenxa yendoda yobutsha bayo.', 'XHO22'),
(33, 1, 9, 'Umnikelo otshiswayo nomnikelo othululwayo ugqityiwe endlwini kaYehova; balila ababingeleli, abakhonzi bakaYehova.', 'XHO22'),
(33, 1, 10, 'Ilizwe libhuqiwe, ilizwe liyakhala; kuba ingqolowa ibhuqiwe, umququ itshile, ioli iyachithwa.', 'XHO22'),
(33, 1, 11, 'Nidanile, balimi; lilani, balimi bewayini, ngenxa yengqolowa nerhasi; ngokuba uvuyo lwehlabathi luphelile.', 'XHO22'),
(33, 1, 12, 'Umdiliya ubunile, umkhiwane womile; umrharnate, nesundu, nom-apile, yonke imithi yelizwe yomile; ngokuba bumile uvuyo loonyana babantu.', 'XHO22'),
(33, 1, 13, 'Zibopheni nina babingeleli; Dumani, bakhonzi besibingelelo; yizani, nilale ebumnyameni bonke ubusuku, bakhonzi boThixo wam;', 'XHO22'),
(33, 1, 14, 'Ngcwalisani umhla wokuzila ukudla, nibize ingqungquthela, niwahlanganise amadoda amakhulu, nabemi bonke belizwe, endlwini kaYehova uThixo wenu, nikhale kuYehova.', 'XHO22'),
(33, 1, 15, 'Yeha loo mini! kuba ikufuphi imini kaYehova, yaye iya kuza njengentshabalalo evela kuSomandla.', 'XHO22'),
(33, 1, 16, 'Ayinqunyulwanga na inyama phambi kwamehlo ethu, nemivuyo nemivuyo endlwini yoThixo wethu?', 'XHO22'),
(33, 1, 17, 'Zibolile iimbewu phantsi kwemidumba, ziqhawukile iingxam, ziqhawukile iingxam; kuba ingqolowa yomile.', 'XHO22'),
(33, 1, 18, 'Hayi indlela izilwanyana ezikhala ngayo! Ikhohliwe imihlambi yeenkomo, kuba ingenatyani; ewe, imihlambi yempahla emfutshane ithe shwaka.', 'XHO22'),
(33, 1, 19, 'Ndikhala kuwe, Yehova, kuba umlilo uwatshisile amakriwa asentlango, nelangatye layirhawula yonke imithi yelizwe.', 'XHO22'),
(33, 1, 20, 'Kwanezilwanyana zelizwe zikhala kuwe, kuba itshile imijelo yamanzi, nomlilo uwatshisile amakriwa asentlango.', 'XHO22'),
(33, 2, 1, 'Vuthelani isigodlo eZiyon, mbulaleni ukumkani entabeni yam engcwele; mabangcangcazele bonke abemi behlabathi, ngokuba isiza imini kaYehova, kuba ikufuphi;', 'XHO22'),
(33, 2, 2, 'Yimini yobumnyama nobumnyama, yimini yamafu nesithokothoko, njengokusa kwesifingo phezu kweentaba, luhlanga olukhulu, olunamandla; alizange libekho, aliyi kubakho nakwizizukulwana ngezizukulwana.', 'XHO22'),
(33, 2, 3, 'Kutsha umlilo phambi kwabo; nasemva kwabo kuvutha amadangatye; ihlabathi linjengomyezo wase-Eden phambi kwabo; ewe, akukho nto iya kusinda.', 'XHO22'),
(33, 2, 4, 'Injengokubonakala kwamahashe imbonakalo yabo; nanjengabamahashe, benjenjalo ukubaleka.', 'XHO22'),
(33, 2, 5, 'Baya kutsiba njengesandi seenqwelo ezincotsheni zeentaba, njengesandi selangatye leendiza ezitsha, njengamagorha amise uluhlu lwedabi.', 'XHO22'),
(33, 2, 6, 'Phambi kobuso babo abantu baya kubandezeleka kakhulu; bonke ubuso buya kuba mnyama.', 'XHO22'),
(33, 2, 7, 'Baya kubaleka njengamagorha; baya kunyuka udonga njengamadoda okulwa; yaye baya kuhamba ngamnye ngendlela yakhe yaye abayi kuphambuka kumaqonga abo.', 'XHO22'),
(33, 2, 8, 'Akukho namnye unako ukutyhala omnye; baya kuhamba elowo ngendlela yakhe, nokuba bathe bawa ngekrele, abayi kunqunyulwa.', 'XHO22'),
(33, 2, 9, 'Baya kubalekela esixekweni; babalekela eludongeni, benyuka ezindlwini; baya kungena ngeefestile njengesela.', 'XHO22'),
(33, 2, 10, 'Umhlaba uyanyikima phambi kwabo; liya kunyikima izulu, libe mnyama ilanga nenyanga, zibe mnyama iinkwenkwezi ekukhanyeni kwazo.', 'XHO22'),
(33, 2, 11, 'uYehova uya kuliphakamisa izwi lakhe phambi kwamaqela akhe, ngokuba mininzi kakhulu iminquba yakhe; ngokuba inkulu imini kaYehova, yoyikeka kunene; ngubani na onyamezeleyo?', 'XHO22'),
(33, 2, 12, 'Ngako oko, itsho iNkosi, nibuye nimke kum ngeentliziyo zenu zonke, ngokuzila, ngokulila, nangobumnyama;', 'XHO22'),
(33, 2, 13, 'nikrazule iintliziyo zenu, ingabi ziingubo zenu, nibuyele kuYehova uThixo wenu;', 'XHO22'),
(33, 2, 14, 'Ngubani na owaziyo ukuba wobuya aguquke, ashiye intsikelelo ngasemva kwakhe; umnikelo wokudla okuziinkozo nomnikelo othululwayo kaYehova uThixo wenu?', 'XHO22'),
(33, 2, 15, 'Vuthelani isigodlo eZiyon, ngcwalisani imini yokuzila, memani ingqungquthela.', 'XHO22'),
(33, 2, 16, 'Hlanganisani abantu, ngcwalisani ibandla, hlanganisani amadoda amakhulu, hlanganisani abantwana nabanyanyisayo; Umyeni makaphume egumbini lakhe, nomtshakazi aphume egumbini lakhe.', 'XHO22'),
(33, 2, 17, 'Bafanele balile ababingeleli, abakhonzi bakaYehova, phakathi kweveranda nesibingelelo, bathi, ‘Bashiye abantu bakho, Yehova, unganikeli ilifa lakho lidelele, ukuze zilawule iintlanga; abaya kuthi phakathi kwabantu, Uphi na uThixo wabo?', 'XHO22'),
(33, 2, 18, 'uYehova uya kuba nekhwele ngelizwe lakhe aze abe nenceba kubantu bakhe.', 'XHO22'),
(33, 2, 19, 'Waphendula uYehova, wathi ebantwini bakhe, Yabonani, ndinithumela ingqolowa, newayini entsha, neoli, eniya kwaneliswa zizo; andiyi kuba sanenza nibe sisingcikivo ezintlangeni.', 'XHO22'),
(33, 2, 20, 'Ndiya kuyishenxisa kuwe imikhosi yasentla, ndiyiqhubele ezweni elingumqwebedu, enkangala, elibheke kulwandle lwaseBhabheli, ngecala lolwandle ngokuba wenza izinto ezinkulu.', 'XHO22'),
(33, 2, 21, 'Musa ukoyika, hlabathi; vuyani nigcobe, ngokuba uYehova wenza izinto ezinkulu.', 'XHO22'),
(33, 2, 22, 'Musani ukoyika, mpahla inkulu yelizwe; kuba amakriwa asentlango ahluma;', 'XHO22'),
(33, 2, 23, 'Nina nyana baseZiyon, yibani nemihlali nigcobe ngoYehova uThixo wenu; ngokuba elungisele imvula yasentwasahlobo, aninike iimvula, iimvula ezinkulu, nezivuvuvuvu ngenyanga yokuqala.', 'XHO22'),
(33, 2, 24, 'Izanda ziya kuzala yingqolowa, namanqatha aphuphume yiwayini neoli;', 'XHO22'),
(33, 2, 25, 'Ndonibuyisela ke iminyaka eyadliwa ziinkumbi, neenkumbi, neenkumbi, neenkumbi, nomkhosi wam omkhulu, endiwuthumele phakathi kwenu.', 'XHO22'),
(33, 2, 26, 'Niya kudla nize nihluthe, nilidumise igama likaYehova uThixo wenu onidale ngendlela emangalisayo; yaye abantu bam abayi kudana naphakade;', 'XHO22'),
(33, 2, 27, 'nazi ukuba ndiphakathi kwamaSirayeli, ukuba ndinguYehova uThixo wenu, akukho wumbi; yaye abantu bam abayi kudana naphakade.', 'XHO22'),
(33, 2, 28, 'Emva koko ndiya kuthulula uMoya wam phezu kwenyama yonke; oonyana benu neentombi zenu baya kuprofeta, amadoda enu amakhulu aphuphe amaphupha, amadodana enu abone imibono.', 'XHO22'),
(33, 2, 29, 'Kwaye phezu kwabakhonzi nabakhonzazana ndothulula uMoya wam ngelo xesha.', 'XHO22'),
(33, 2, 30, 'Ndiya kwenza izimanga ezulwini nasehlabathini, igazi nomlilo nentshinyela yomsi.', 'XHO22'),
(33, 2, 31, 'Ilanga liya kujika libe bubumnyama nenyanga ibe ligazi, ngaphambi kokuba ifike imini kaYehova enkulu neyoyikekayo.', 'XHO22'),
(33, 2, 32, 'Yaye wonk’ ubani obiza egameni likaYehova uya kusindiswa; ngokuba entabeni yeZiyon naseYerusalem kuya kubakho usindiso, njengoko watshoyo uYehova; naphakathi kwabaseleyo kubekho awobabiza uYehova.', 'XHO22'),
(33, 3, 1, 'Kuba, yabonani, ngelo xesha nangelo xesha, ekubabuyiseni kwam abantu bakwaYuda nabaseYerusalem ekuthinjweni;', 'XHO22'),
(33, 3, 2, 'Ndiya kuzihlanganisa zonke iintlanga ndize ndizihlisele entlanjeni yakwaYehoshafati ndize ndilwele nazo apho ngenxa yabantu bam nelifa lam likaSirayeli, owazichithachitha phakathi kweentlanga nowabelahlulayo ilizwe.', 'XHO22'),
(33, 3, 3, 'Benza amaqashiso ngabantu bam; Unyana wamnika ihenyukazi, wathengisa ngentombazana ngewayini, ukuba isele.', 'XHO22'),
(33, 3, 4, 'Yintoni na enam nawe, Tire, nawe, Tsidon, nani mazwe onke asePalestina? uzondinika impindezelo? ukuba uthe wazikhulula, ngokukhawuleza, kamsinya, ndokuphalaza ukuphatha kwakho entlokweni yakho;', 'XHO22'),
(33, 3, 5, 'Ekubeni nithabathe isilivere yam negolide yam, nazisa izinto zam ezinqwenelekayo ezitempileni zenu.', 'XHO22'),
(33, 3, 6, 'Kananjalo oonyana bakaYuda noonyana baseYerusalem bathengise ngani kumaGrike, ukuze bawagxothe ezweni lawo;', 'XHO22'),
(33, 3, 7, 'yabonani, ndiya kubavusa kuloo ndawo nathengisa ngayo ngabo, ndiwenze umvuzo wenu entlokweni yenu.', 'XHO22'),
(33, 3, 8, 'Ndothengisa ngoonyana benu nangeentombi zenu esandleni soonyana bakaYuda, bathengise ngabo kumaShebha, uhlanga olukude, ngokuba uYehova ethethile.', 'XHO22'),
(33, 3, 9, 'Memezani oku phakathi kweentlanga; Zilungiseleleni imfazwe, phakamisani amagorha, mawasondele onke amagorha; mabanyuke.', 'XHO22'),
(33, 3, 10, 'Lahlani amakrele enu amakhulu nezikere zokuthena imithi; oswele amandla makathi, Ndomelele.', 'XHO22'),
(33, 3, 11, 'Buthani nize, zonke iintlanga, nibuthelane ezindaweni zonke; ehle amagorha akho, Yehova.', 'XHO22'),
(33, 3, 12, 'Mazivuke zinyuke iintlanga, ziye entilini yakwaYoshafati; ngokuba ndiya kuhlala phantsi khona, ukuze ndizigwebe zonke iintlanga ngeenxa zonke.', 'XHO22'),
(33, 3, 13, 'Hlalani nerhengqa, ngokuba ukuvuna kuvuthiwe; yiza, yihla; ngokuba isixovulelo sizele, siyaphuphuma ioli; ngokuba ububi babo bukhulu.', 'XHO22'),
(33, 3, 14, 'Izihlwele zisentilini yogwebo, ngokuba isondele imini kaYehova entilini yogwebo.', 'XHO22'),
(33, 3, 15, 'Ilanga nenyanga ziya kuba mnyama, neenkwenkwezi zibe mnyama ekuqaqambeni kwazo;', 'XHO22'),
(33, 3, 16, 'uYehova uya kubharhula eZiyon, akhuphe ilizwi lakhe eYerusalem; amazulu nomhlaba ngokuqinisekileyo uya kunyikima, yaye uYehova uya kuba lithemba labantu bakhe namandla oonyana bakaSirayeli.', 'XHO22'),
(33, 3, 17, 'Nazi ukuba mna Yehova ndinguThixo wenu, ohleli eZiyon, entabeni yam engcwele; iYerusalem yoba ngcwele, abasemzini bangabi sacanda kuyo.', 'XHO22'),
(33, 3, 18, 'Ngaloo mini iintaba ngokuqinisekileyo ziya kuvuza ubulembu, neenduli ziphalale ubisi, nemilambo yonke yakwaYuda iphalale amanzi, nequla liya kuphuma endlwini kaYehova kwaye iya kunkcenkceshela intlambo yaseSitime.', 'XHO22'),
(33, 3, 19, 'iYiputa iya kuba yinkangala ephanzileyo, iEdom ibe yintlango nenkangala, ngenxa yogonyamelo olwenziwa koonyana bakaYuda, ngenxa yokuba bephalaze igazi elimsulwa ezweni labo.', 'XHO22'),
(33, 3, 20, 'Ke yena uYuda uya kuphila ngonaphakade, neYerusalem kuse kwizizukulwana ngezizukulwana.', 'XHO22'),
(33, 3, 21, 'Ndiya kulisula igazi labo endandingalicocanga, ngenxa yokuba uYehova ehleli eZiyon.', 'XHO22'),
(34, 1, 1, 'Amazwi ka-Amosi, omnye wabalusi baseTekowa, awawabonayo kuSirayeli, ngemihla ka-Uziya ukumkani wakwaYuda, nangemihla kaYarobheham unyana kaYowashe, ukumkani wakwaSirayeli, iminyaka emibini phambi konyikimo.', 'XHO22'),
(34, 1, 2, 'Kwaye wathi: iNkosi iya kugquma iseZiyon kwaye iphakamise ilizwi layo eYerusalem; namadlelo abalusi aya kuba lusizi, nenduli yeKarmele ibe lusizi.', 'XHO22'),
(34, 1, 3, 'Wathi uYehova; Ngenxa yezikreqo ezithathu zeDamasko, ngenxa yezine, andiyi kuyeka ukuba netyala; ngokuba iGiliyadi bayibhula ngeempahla zesinyithi:', 'XHO22'),
(34, 1, 4, 'ndiya kuthumela umlilo endlwini kaHazayeli, utshise iingxande ezinde zikaBhen-hadade.', 'XHO22'),
(34, 1, 5, 'Ndiya kuludiliza udonga lweDamasko, ndibatshabalalise abemi bethafa laseAtene, nophethe intonga yobukumkani eBhete-eden; yaye oonyana baka-Aram baya kuthinjwa baye eKire,” utsho uYehova.', 'XHO22'),
(34, 1, 6, 'Wathi uYehova, Ngenxa yezikreqo ezithathu zikaGasa, ngenxa yezine—andiyi kumyeka; ngenxa yokuba bonke bathatyathelwa ekuthinjweni ukuze babanikele kuEdom:', 'XHO22'),
(34, 1, 7, 'ndiya kuphosa umlilo eludongeni lweGaza, uzitshise iingxande ezinde khona.', 'XHO22'),
(34, 1, 8, 'Ndiya kumtshabalalisa ummi waseAshdode, nophethe intonga yobukumkani eAshkelon, ndisibuyisele phezu kwe-Ekron isandla sam; nabaseleyo bamaFilisti baya kutshabalala,’ itsho iNkosi enguMongami uYehova.', 'XHO22'),
(34, 1, 9, 'Wathi uYehova, Ngenxa yezikreqo ezithathu, nokuba zine zeTire, andiyi kukuthintela ekugwetyweni kwayo; ngokuba wabanika uEdom bonke abathinjwa, akawukhumbula umnqophiso wobuzalwana:', 'XHO22'),
(34, 1, 10, 'ndiya kuthumela umlilo eludongeni lweTire, uzitshise iingxande zayo ezinde.', 'XHO22'),
(34, 1, 11, 'Wathi uYehova, Andiyi kuyijika, ngenxa yezono ezithathu nokuba zine zikaEdom; ngokuba wamlandela umkhuluwa wakhe ngekrele, wayilahla yonke inceba yakhe, waba nobushushu bomsindo wakhe, wahlala enomsindo ngonaphakade:', 'XHO22'),
(34, 1, 12, 'ndiya kuthumela umlilo kwaTeman, utshise iingxande ezinde zaseBhotsera.', 'XHO22'),
(34, 1, 13, 'Wathi uYehova, Ngenxa yobugwenxa obuthathu, nokuba bune boonyana baka-Amon, andiyi kukha ndibubethe kubo; ngokuba baqwenga abafazi abamithiyo baseGiliyadi, ukuze bandise ilizwe labo.', 'XHO22'),
(34, 1, 14, 'Ndiya kuphemba umlilo eludongeni lweRabha, utshise iingxande zayo ezinde, kukho uvuyo ngemini yokulwa, kukho umoya ovuthuzayo ngemini yesaqhwithi.', 'XHO22'),
(34, 1, 15, 'Uya kuthinjwa emke ukumkani wabo, yena nabathetheli bakhe kunye; utsho uYehova.', 'XHO22'),
(34, 2, 1, 'Wathi uYehova; Andiyi kukunqanda ubugwenxa bakhe, ngenxa yezikreqo ezithathu nokuba zine zikaMowabhi; ngokuba wawatshisayo amathambo okumkani wakwaEdom, aba yikalika:', 'XHO22'),
(34, 2, 2, 'ndiya kuthumela umlilo kwaMowabhi, utshise iingxande ezinde zaseKeriyoti;', 'XHO22'),
(34, 2, 3, 'Ndomtshabalalisa umgwebi walo, ndibabulale bonke abathetheli balo kunye naye; utsho uYehova.', 'XHO22'),
(34, 2, 4, 'Wathi uYehova, Ngenxa yobugwenxa bukaYuda obuthathu, nokuba bune, andiyi kukuthintela ukohlwaya kwakhe; kuba bawucekisile umthetho kaYehova, abayigcina imiyalelo yakhe, yaye ubuxoki babo babalahlekisisa njengoko bahambayo ookhokho babo.', 'XHO22'),
(34, 2, 5, 'Ndiza kuthumela umlilo kwaYuda, uzitshise iingxande ezinde zeYerusalem.', 'XHO22'),
(34, 2, 6, 'Wathi uYehova, Andiyi kubuya ngenxa yezono ezithathu, nokuba zine, zikaSirayeli; Ngokuba bathengisa ilungisa ngesilivere, namahlwempu ngeembadada ezimbini;', 'XHO22'),
(34, 2, 7, 'abo balangazelela uthuli lomhlaba entlokweni yamahlwempu, abawalahlekisayo endleleni yabalulamileyo, indoda noyise baya kungena nayo, njengomkhonzi omnye, ekuzukiseni igama lam elingcwele.', 'XHO22'),
(34, 2, 8, 'Balala phezu kweempahla ezibanjelwe kwisibingelelo ngasinye, yaye basela iwayini yabo badelekileyo endlwini yothixo wabo.', 'XHO22'),
(34, 2, 9, 'Kodwa ndawatshabalalisa ama-Amori phambi kwawo, abude bawo babulingana nobude bemisedare, nawomeleleyo njengemithi yompleyini; ndasitshabalalisa isiqhamo sawo ngaphezulu, neengcambu zawo ngaphantsi.', 'XHO22'),
(34, 2, 10, 'Ndanikhupha ezweni laseYiputa, ndanikhokelela entlango iminyaka emashumi mane, ukuze nilihluthe ilizwe lama-Amori.', 'XHO22'),
(34, 2, 11, 'Ndabaphakamisa abaprofeti boonyana benu, nabazabazabazayo kumadodana enu. Àkunjalo na, nyana bakaSirayeli? utsho uYehova.', 'XHO22'),
(34, 2, 12, 'Wena ke nabaseza iwayini amaNazarete; wabawisela umthetho abaprofeti, wathi, Musani ukuwisa intetho.', 'XHO22'),
(34, 2, 13, 'Yabona, ndiya kuzifihla phantsi kwakho, njengemoto ezele yingxokolo;', 'XHO22'),
(34, 2, 14, 'Ngako oko ukusaba kwamendu kuya kudaka, igorha lingazimisi amandla akhe, negorha lingazisindisi.', 'XHO22'),
(34, 2, 15, 'Ophethe isaphetha akayi kuma; kodwa lowo uzilayo akanako ukuzisindisa; Ke yena okhwele ihashe akanako ukuzisindisa.', 'XHO22'),
(34, 2, 16, 'Yaye lowo ukhaliphileyo phakathi kwabomeleleyo uya kusinda ngaphandle koloyiko ngaloo mini,’ utsho uYehova.', 'XHO22'),
(34, 3, 1, 'Liveni eli lizwi alithethileyo uYehova ngani, nyana bakaSirayeli, kubantu bonke endibakhuphileyo ezweni laseYiputa, lisithi:', 'XHO22'),
(34, 3, 2, 'Ndikwazile wena wedwa ezizukulwaneni zonke zehlabathi; ke ngoko ndiya kunivelela ngenxa yobugwenxa benu bonke.', 'XHO22'),
(34, 3, 3, 'Bahamba kunye na ababini, bengavani?', 'XHO22'),
(34, 3, 4, 'Ikha ibharhule na ingonyama ehlathini, ingenanto iyiqwengayo? Iya kukhala ingonyama na emhadini wayo, ingabambanga nto?', 'XHO22'),
(34, 3, 5, 'Iya kuwela na intaka endlwini yendlu, kungekho ndlu yayo? Umntu angawuthiyela na umgibe ehlabathini, angaze abambe?', 'XHO22'),
(34, 3, 6, 'Liya kuvuthelwa na isigodlo phakathi komzi, bangoyiki abantu? Kukho ububi na phakathi komzi, abenziwanga nguYehova?', 'XHO22'),
(34, 3, 7, 'Inene, akenzi nto uYehova, akalutyhilanga ucweyo lwakhe kubakhonzi bakhe, abaprofeti.', 'XHO22'),
(34, 3, 8, 'Ingonyama igquma, ngubani na ongoyikiyo? Itsho iNkosi uYehova ukuthi, Ngubani na ongayi kuprofeta?', 'XHO22'),
(34, 3, 9, 'Makwazeke kwizindlu zookumkani baseAshdode, nakwizindlu zobukumkani zelizwe laseYiputa, ukuthi, Zihlanganiseni ezintabeni zakwaSamari, nibone ingxokozelo enkulu phakathi kwayo, nabacudisiweyo phakathi kwayo.', 'XHO22'),
(34, 3, 10, 'Ngenxa yokuba bengakwazi ukwenza okuthe tye, utsho uYehova, lowo wafumba ubungendawo nokuphanga ezindlwini zabo.', 'XHO22'),
(34, 3, 11, 'Ngoko ke, uYehova wathi, Kuya kubakho utshaba ehlabathini; uwakrazule amandla akho kuwe, ziphangwe iingxande zakho ezinde.', 'XHO22'),
(34, 3, 12, 'Wathi uYehova, Njengokuba umalusi ethabatha amathupha amabini emlonyeni wengonyama, nokuba yinxalenye yendlebe; benjenjalo ke oonyana bakaSirayeli, abemiyo kwaSamari, baxolelwe embombeni yesingqengqelo, nasezingqengqelweni zaseDamasko.', 'XHO22'),
(34, 3, 13, 'Yivani, ninikele ubungqina kwindlu kaYakobi, itsho iNkosi enguMongami uYehova, uThixo wemikhosi.', 'XHO22'),
(34, 3, 14, 'Ngaloo mini ndiya kumohlwaya uSirayeli ngenxa yokunxaxha kwakhe kuye, ndiya kuzohlwaya nezibingelelo zaseBheteli; iimpondo zesibingelelo ziya kunqunyulwa ziwe emhlabeni.', 'XHO22'),
(34, 3, 15, 'Ndoyitshabalalisa indlu yasebusika, nendlu yasehlotyeni; zitshabalale izindlu zeempondo zeendlovu, nezindlu ezininzi zitshabalale; utsho uYehova.', 'XHO22'),
(34, 4, 1, 'Liveni eli lizwi, nina mathokazi aseBhashan, aphezu kwentaba yakwaSamari, enitshutshisayo abaziintsizana, nibanyhasha abaziintsizana, abathi kubaninizo, Siphe sisele;', 'XHO22'),
(34, 4, 2, 'uYehova ufunge ubungcwele bakhe, wathi, Kuya kufika imihla apho aya kunitsala ngamagwegwe okubambisa iintlanzi, nembewu yenu ngamagwegwe okubambisa iintlanzi.', 'XHO22'),
(34, 4, 3, 'Uya kuphuma kunye neenkomo, inkomo ibe nye, ibe ngephambi kwayo; ubaphose ebhotweni, utsho uYehova.', 'XHO22'),
(34, 4, 4, 'Yizani eBheteli, niwaphule umthetho; luninzi ukreqo eGiligali; noyizisa imibingelelo yenu imiso ngemiso, nesishumi senu ekupheleni kweminyaka emithathu.', 'XHO22'),
(34, 4, 5, 'Zisani umbingelelo wombulelo onegwele, nivakalise, nivakalise iminikelo yokuqhutywa yintliziyo; ngokuba kunjalo kuni, nyana bakaSirayeli; itsho iNkosi uYehova.', 'XHO22'),
(34, 4, 6, 'Ndaninika ubumsulwa bamazinyo emizini yenu yonke, nokuswela isonka ezindaweni zenu zonke; noko anibuyelanga kum; utsho uYehova.', 'XHO22'),
(34, 4, 7, 'Kananjalo ndayicima imvula, kwakusele iinyanga ezintathu ngaphambi kokuvuna.', 'XHO22'),
(34, 4, 8, 'Ngoko ezimbini okanye ezintathu kwiilali zam zabhadula ukusuka kwidolophana ukuya kwenye ukuya kusela amanzi; kodwa abahlutha; noko anibuyelanga kum; utsho uYehova.', 'XHO22'),
(34, 4, 9, 'Ndanibetha ngokhula nokhula; iya kwanda imiyezo yakho, nezidiliya zakho, namakhiwane akho, neminquma yakho, yandile, idliwa ngamasundu; noko anibuyelanga kum; utsho uYehova.', 'XHO22'),
(34, 4, 10, 'Ndathumela kuni isibetho ngohlobo lwaseYiputa; Ndalinyusa ivumba leemandlalo zenu, leza emathatheni enu; noko anibuyelanga kum; utsho uYehova.', 'XHO22'),
(34, 4, 11, 'Ndabatshabalalisa abanye benu njengoko uThixo wayitshabalalisayo iSodom neGomora, naba njengesikhuni esikhutshwa emlilweni; noko anizohlwayanga; utsho uYehova.', 'XHO22'),
(34, 4, 12, 'Ngako oko ndiya kwenjenjalo kuwe, Sirayeli; ngokuba ndiya kwenjenje ke kuwe, zilungiselele ukuhlangana noThixo wakho, Sirayeli.', 'XHO22'),
(34, 4, 13, 'Ngokuba yabona, ulowenza iintaba, ulowenza umoya, uloxelela umntu iingcinga zakhe, ulowenza mnyama isifingo, ulongena ezindaweni eziphakamileyo zehlabathi, uYehova, uThixo wemikhosi, igama lakhe.', 'XHO22'),
(34, 5, 1, 'Liveni eli lizwi ndilibikayo, ndlu kaSirayeli;', 'XHO22'),
(34, 5, 2, 'Iwile intombi enguSirayeli; akayi kubuya avuke; washiywa ezweni lakowabo; akwabikho mntu umvusayo.', 'XHO22'),
(34, 5, 3, 'Kuba itsho iNkosi enguMongami uYehova ukuthi: “Isixeko esaphuma newaka siya kusala silikhulu, yaye lowo uphume nekhulu uya kusala neshumi kwindlu kaSirayeli.', 'XHO22'),
(34, 5, 4, 'Ngokuba wathi uYehova kwindlu kaSirayeli, Quqelani kum, niphile;', 'XHO22'),
(34, 5, 5, 'musani ukuquqela eBheteli, ningangeni eGiligali, ningaweleli niye eBher-shebha ngenxa yokuba iGiligali ngokuqinisekileyo iya kuya ekuthinjweni, neBheteli iya kutshabalala.', 'XHO22'),
(34, 5, 6, 'Quqelani kuYehova, naniya kuphila; mhlawumbi ithe yatsha njengomlilo wendlu kaYosefu, wayitshisa, akwabakho uwucimayo eBheteli.', 'XHO22'),
(34, 5, 7, 'Wena uguqulayo umgwebo, umise ubulungisa ehlabathini;', 'XHO22'),
(34, 5, 8, 'Quqelani kulowenza isiLimela namaKroza, uloguqula ithunzi lokufa libe ngumso, imini ayenze mnyama, ibe bubusuku; ulobiza amanzi olwandle, awathululele phezu komhlaba: nguYehova igama lakhe.', 'XHO22'),
(34, 5, 9, 'Abo baqinisa imbuqo phezu konamandla, ukuze abaphangi bayifikele inqaba.', 'XHO22'),
(34, 5, 10, 'Bayamthiya owohlwayayo emnyango, bamthiyile othetha ubulungisa.', 'XHO22'),
(34, 5, 11, 'Kususela kwelo xesha, niya kusigqusha isisweli, nithabathe kulo iqashiso lenu; niya kwakha izindlu ngamatye aqingqiweyo, ningahlali kuzo; nityale izidiliya ezilungileyo, ningaseli wayini yazo.', 'XHO22'),
(34, 5, 12, 'Ngokuba ndiyazazi izikreqo zenu ezininzi, nezono zenu ukuba zikhulu kwazo;', 'XHO22'),
(34, 5, 13, 'Ngako oko izilumko ziya kuthi tu ngeli xesha; ngokuba lixesha elibi.', 'XHO22'),
(34, 5, 14, 'Funani okulungileyo, ningafuni okubi, ukuze niphile; aze uYehova, uThixo wemikhosi, abe nani, njengoko nitshoyo.', 'XHO22'),
(34, 5, 15, 'Thiyani okubi, thandani okulungileyo, gcinani ubulungisa esangweni; mhlawumbi uYehova, uThixo wemikhosi, woba nemfesane kumasalela kaYosefu.', 'XHO22'),
(34, 5, 16, 'Ngako oko, utsho uYehova, uThixo wemikhosi, uYehova, ukuthi, Ezitratweni zonke kuya kubakho isijwili; ngeendlela zonke baya kuthi, Yeha! intloko! yaye baya kubiza abalimi bezilile, nabo banako ukuhamba ukusuka ekuzilweni ukuya ekuzilileni.', 'XHO22'),
(34, 5, 17, 'Zonke izidiliya ziya kukhala, ngenxa yokuba ndiya kudlula kuwe,’ utsho uYehova.', 'XHO22'),
(34, 5, 18, 'Yeha, nina banqwenela imini kaYehova! Ngantoni na kuni? Imini kaYehova ibubumnyama, ingabi kukukhanya.', 'XHO22'),
(34, 5, 19, 'Njengoko umntu ebaleka ingonyama, aze ahlangane nebhere; athi xa engena endlwini, asibeke isandla sakhe eludongeni, alunywe yinyoka.', 'XHO22'),
(34, 5, 20, 'Ayiyi kuba bubumnyama na imini kaYehova, ingabi kukukhanya? Sibubumnyama na, obungenakukhanya?', 'XHO22'),
(34, 5, 21, 'Ndiwathiyile, ndiyawacekisa imithendeleko yenu;', 'XHO22'),
(34, 5, 22, 'Ukuba nindizisele amadini anyukayo, namadini akho anyukayo, akakholiswa ngawo; yaye andiyi kukhangela kumbingelelo weenkomo zakho ezityetyisiweyo.', 'XHO22'),
(34, 5, 23, 'Yisuse kum ingxokozelo yeengoma zakho; ngokuba andikuthandi ukuva ukubethwa kweehadi zakho.', 'XHO22'),
(34, 5, 24, 'Makugaleleke umgwebo njengamanzi, nobulungisa njengomlambo oqukuqelayo;', 'XHO22'),
(34, 5, 25, 'Imibingelelo neminikelo yokudla naniyizisa kum na entlango iminyaka emashumi mane, nina bendlu kaSirayeli?', 'XHO22'),
(34, 5, 26, 'Nasuka nathwala umnquba wokumkani wakho, noKiune, imifanekiso yakho, inkwenkwezi yothixo wakho, ozenzele yona.', 'XHO22'),
(34, 5, 27, 'Ngako oko, ndiya kunifudusela ngaphaya kweDamasko; utsho uYehova, ogama lakhe linguThixo wemikhosi.', 'XHO22'),
(34, 6, 1, 'Yeha ke abemi baseZiyon, abakholose ngentaba yakwaSamari, ebizwa ngokuba yintloko yeentlanga, eyaya kuzo indlu kaSirayeli!', 'XHO22'),
(34, 6, 2, 'Yiyani eKalene, nibone; asuke apho, aye eHamati enkulu; Wehla ke waya eGati yamaFilisti. Ngaba ingcono kunaba rhulumente? okanye umda wabo mkhulu kunomda wakho?', 'XHO22'),
(34, 6, 3, 'wena ulonyusayo imini yobubi, usondeza itrone yogonyamelo;', 'XHO22'),
(34, 6, 4, 'Bahlala ezingqengqelweni zeempondo zeendlovu, banabe ezingqengqelweni zabo, badla amatakane empahla emfutshane, namathole eenkomo asemkhumbini;', 'XHO22'),
(34, 6, 5, 'abavuma ngesandi sohadi, nizenzele into yokubetha njengoDavide;', 'XHO22'),
(34, 6, 6, 'abasela iwayini ngezitya baze bazithambise ngeoli eninzi, kodwa abaziva zintlungu ngenxa yokubandezeleka kukaYosefu.', 'XHO22'),
(34, 6, 7, 'Ngoku baya kuya ekuthinjweni kunye nabokuqala abathinjwa, nokutya kwabo baleleyo kuya kuthatyathwa.', 'XHO22'),
(34, 6, 8, 'iNkosi uYehova imfungele, utsho uYehova wemikhosi, yathi, Ndilithiyile iqhayiya likaYakobi, ndizithiyile iingxande zakhe ezinde; ke ngoko, ndiya kusinikezela esi sixeko nako konke okukuso.', 'XHO22'),
(34, 6, 9, 'Ukuba kuthe kwahlala amadoda alishumi ndlwini-nye, aya kufa;', 'XHO22'),
(34, 6, 10, 'Womthabatha uyisekazi waloo mntu, nalowo umtshisileyo, awakhuphele amathambo ngaphandle kwendlu, athi kulowo ungaphakathi endlwini, Kusekho mntu na kuni? kwaye uya kuthi: Hayi!', 'XHO22'),
(34, 6, 11, 'Ngokuba, uyabona, uYehova uwise umthetho, bayibethe indlu enkulu ibe ziintanda, nendlu encinane ibe ziintanda.', 'XHO22'),
(34, 6, 12, 'Amahashe ayabaleka na phezu kwengxondorha? Ngubani na olima iinkomo khona? Ngokuba ukujike okusesikweni kwaba lugonyamelo, nesiqhamo sobulungisa wasiguqula saba ngumhlambi.', 'XHO22'),
(34, 6, 13, 'Nina nivuyela into engento yanto, nithi: “Besingazuzanga iimpondo ngamandla ethu na?', 'XHO22'),
(34, 6, 14, 'Yabonani, ndinivusela uhlanga, ndlu kaSirayeli, utsho uYehova, uThixo wemikhosi; yaye ziya kunixina ukususela ekungeneni kweHamati ukuya kutsho kumlambo iSekaka.', 'XHO22'),
(34, 7, 1, 'Yenjenje ukundibonisa iNkosi uYehova; yabona, udale iinkumbi ekuqalekeni kwehlumelo lokugqibela; yabona, yayisisityalo sokugqibela emva kokugawulwa kokumkani.', 'XHO22'),
(34, 7, 2, 'Kwathi, yakugqiba ukuyidla ingca yelizwe, ndathi mna, Nkosi Yehova, ndixolele; wovuka nabani na uYakobi? kuba uselula.', 'XHO22'),
(34, 7, 3, 'Waba buhlungu uYehova ngenxa yale nto, wathi, Ayinakwenzeka le nto, utsho uYehova.', 'XHO22'),
(34, 7, 4, 'Yenjenje ukundibonisa iNkosi enguMongami uYehova: yaye, khangela!', 'XHO22'),
(34, 7, 5, 'Ndathi ke, Nkosi Yehova, khawuyeke! Ngubani na uYakobi oya kuma? kuba uselula.', 'XHO22'),
(34, 7, 6, 'Wazohlwaya uYehova ngenxa yoko, wathi, Nale nto ayiyi kubakho; itsho iNkosi uYehova.', 'XHO22'),
(34, 7, 7, 'Yandibonisa yona: nantso iNkosi imi eludongeni lwentambo yokulinganisa, iphethe ngesandla ulutya lokulinganisa.', 'XHO22'),
(34, 7, 8, 'Wathi uYehova kum, Uyabona, Amosi na? Ndaza ndathi: Mkhokheli. Wathi uYehova, Yabona, ndibeka ulutya lokulinganisa esazulwini sabantu bam amaSirayeli; Andisayi kuphinda ndiwadlulise.', 'XHO22'),
(34, 7, 9, 'Iintaba zakwaIsake ziya kuba senkangala, neengcwele zakwaSirayeli zibe senkangala; yaye ndiya kusuka ndihlasele indlu kaYerobhoham ngekrele.', 'XHO22'),
(34, 7, 10, 'uAmatsiya, umbingeleli waseBheteli, wathumela kuYarobheham ukumkani wakwaSirayeli, esithi, vakalisa uAmosi kwindlu kaSirayeli; ilizwe alinakuwathwala onke amazwi alo.', 'XHO22'),
(34, 7, 11, 'Kuba utsho uAmosi ukuthi: “uYerobhoham uya kufa likrele, noSirayeli ngokuqinisekileyo uya kuthinjwa asiwe ezweni lakhe.', 'XHO22'),
(34, 7, 12, 'Wathi uAmatsiya kuAmosi, Mboni, hamba, ubalekele ezweni lakwaYuda, udle isonka khona, uprofete khona.', 'XHO22'),
(34, 7, 13, 'Kodwa uze ungabi saphinda uprofete eBheteli, kuba yindlu yokumkani yokuthandaza, ikwayindlu yokumkani.', 'XHO22'),
(34, 7, 14, 'Waphendula uAmosi, wathi kuAmatsiya, Andimprofeti, andinyana wamprofeti; Bendingumalusi weenkomo, ndingumvuni wesikamore;', 'XHO22'),
(34, 7, 15, 'uYehova wandithabatha ndisalusa umhlambi, uYehova wathi kum, Yiya uprofete kubantu bam amaSirayeli.', 'XHO22'),
(34, 7, 16, 'Kaloku live ilizwi likaYehova. Uthi wena, Akuyi kuwisa ntetho kumaSirayeli, lingawi ilizwi lakho phezu kwendlu kaIsake.', 'XHO22'),
(34, 7, 17, 'Ngako oko, utsho uYehova ukuthi, Umfazi wakho uya kuhenyuza phakathi komzi, bawe likrele oonyana bakho neentombi zakho; yaye ngokuqinisekileyo uya kufela kwilizwe eliyinkangala, yaye ngokuqinisekileyo uSirayeli uya kuthinjwa emke emhlabeni wakhe.', 'XHO22'),
(34, 8, 1, 'Yandibonisa iNkosi uYehova: nantso ingobozi ineziqhamo zasehlotyeni.', 'XHO22'),
(34, 8, 2, 'Yathi, Uyabona, Amosi? Ndathi: Yingobozi eneziqhamo zasehlotyeni. Wathi uYehova kum, Isiphelo sibafikele abantu bam amaSirayeli; Andisayi kuphinda ndiwadlulise.', 'XHO22'),
(34, 8, 3, 'Ziya kumemelela iingoma zetempile ngaloo mini, itsho iNkosi uYehova; ziya kuba ninzi izidumbu; ziya kuwagxotha ngokuzola.', 'XHO22'),
(34, 8, 4, 'Yivani oku, nina badla izisweli;', 'XHO22'),
(34, 8, 5, 'Bathi, Kuya kudlula nini na ukuthwasa kwenyanga, sithengise ngengqolowa? Nesabatha, ukuze sihlwayele ingqolowa ngokunciphisa iefa neshekele enkulu, size siguqule izikali ngobuxoki?', 'XHO22'),
(34, 8, 6, 'ukuba sibe nako ukuthenga amahlwempu ngesilivere, namahlwempu ngeembadada ezimbini; ewe, kwaye uthengise inkunkuma yengqolowa?', 'XHO22'),
(34, 8, 7, 'Ufunge uYehova ubukhulu bukaYakobi, esithi, Andiyi kuzilibala izenzo zabo zonke.', 'XHO22'),
(34, 8, 8, 'Aliyi kunyikima na ilizwe ngenxa yoku, baze balile bonke abemi balo? liya kunyuka lonke liphela njengomlambo; liya kulahlwa, linxile njengomlambo waseYiputa.', 'XHO22'),
(34, 8, 9, 'Ngaloo mini, itsho iNkosi enguMongami uYehova, ndiya kulitshonisa ilanga emini enkulu, ndenze ubumnyama ehlabathini emini enkulu.', 'XHO22'),
(34, 8, 10, 'Ndiya kuyijika imithendeleko yenu ibe sisijwili, neengoma zenu zonke zibe sisijwili; Ndiya kubeka ezirhwexayo emanqeni onke, neentloko zonke ndichebe; yaye ndiya kukwenza kube njengentlungu yonyana okuphela kwakhe, nokuphela kwayo kube njengemini yosizi.', 'XHO22'),
(34, 8, 11, 'Yabonani, kuza imihla, itsho iNkosi uYehova, endiya kuthumela ngayo indlala elizweni, ingabi kuko ukulambela isonka, kungabi kuko ukunxanelwa amanzi;', 'XHO22'),
(34, 8, 12, 'Yaye ngokuqinisekileyo baya kuchithachithwa ukusuka elwandle ukusa elwandle, nokusuka emntla ukusa ngasempuma, ngokuqinisekileyo baya kubaleka baye kufuna ilizwi likaYehova, kodwa abayi kulifumana.', 'XHO22'),
(34, 8, 13, 'Ngaloo mini baya kuwa isiduli umthinjana nabafana, linxano;', 'XHO22'),
(34, 8, 14, 'Ngabo abo bafunga isono sakwaSamari, besithi, Ehleli nje uThixo wakho, Dan! nendlela eya eBher-shebha; nabo baya kuwa, bangabuyi bavuke.', 'XHO22'),
(34, 9, 1, 'Ndayibona iNkosi imi ngasesibingelelweni, yathi, Betha ucango oluphezulu, umgubasi uzowushukuma; uwanqumle entloko onke; ndibabulale abokugqibela babo ngekrele;', 'XHO22'),
(34, 9, 2, 'Ukuba bathe bemba emhadini, sobathabatha khona isandla sam; nokuba bathe benyuka baya ezulwini, ndobahlisa.', 'XHO22'),
(34, 9, 3, 'Nokuba bathe bazimela encotsheni yeKarmele, ndobafuna nalapho, ndibathabathe; ukuba bathe babusithelisa ubuso bam ezinzulwini zolwandle, ndothumela inyoka khona, ibalume.', 'XHO22'),
(34, 9, 4, 'Nokuba bathe bathinjwa phambi kweentshaba zabo, ndolithumela khona ikrele, libabulale; ndiwaguqulele kubo amehlo am, kube kubi, kungabi kokulungileyo.', 'XHO22'),
(34, 9, 5, 'uYehova woqhwithela uya kuliphatha ilizwe, libe senkangala, babe buhlungu bonke abemiyo kulo; liya kunyuka lonke liphela njengomlambo; yaye ngokuqinisekileyo baya kurhaxwa ngokungathi barhaxwe ngumlambo waseYiputa.', 'XHO22'),
(34, 9, 6, 'Nguye owakha amagumbi akhe emazulwini, Walimisayo ihlabathi ibhunga lakhe; owabiza amanzi olwandle, aphalaza ehlabathini: igama lakhe nguYehova.', 'XHO22'),
(34, 9, 7, 'Aninjengoonyana bakaKushi na kum, nina nyana bakaSirayeli? utsho uYehova. Andiwakhuphanga na amaSirayeli ezweni laseYiputa? namaFilisti aseKafetori, nama-Aram aseKire?', 'XHO22'),
(34, 9, 8, 'Yabona, amehlo eNkosi uYehova abone ubukumkani obuzele sisono, ndibutshabalalise, bungabikho ehlabathini; kodwa andiyi kuyibetha ngesiqalekiso indlu kaYakobi; utsho uYehova.', 'XHO22'),
(34, 9, 9, 'Kuba ndiwisa umthetho, ndiyihluze indlu kaSirayeli ezintlangeni zonke, njengokuba ihluzwa ingqolowa ngesihlungulo, kungawi nokhozo emhlabeni;', 'XHO22'),
(34, 9, 10, 'Bonke aboni babantu bam baya kunqunyulwa umkhonto, abo bathi, Abuyi kusichukumisa ububi, abuyi kusichukumisa;', 'XHO22'),
(34, 9, 11, 'Ngaloo mini ndiya kuwumisa umnquba owileyo kaDavide, ndizivingce iintanda zawo; ndiya kuwavusa amanxuwa alo, ndilakhe njengokwemihla yamandulo;', 'XHO22'),
(34, 9, 12, 'ukuze bawadle ilifa amasalela akwaEdom, neentlanga zonke ezibizwa ngegama lam; utsho uYehova okwenzayo oko.', 'XHO22'),
(34, 9, 13, 'Yabonani, kuza imihla, utsho uYehova, aya kuthi ngayo umlimi abambe umvuni, athi umlimi-zidiliya abambe umhlwayeli; neentaba ziya kuthontsiza iwayini enencasa, neenduli zonke ziya kunyibilika.', 'XHO22'),
(34, 9, 14, 'Ndiya kubabuyisa abathinjwa babantu bam amaSirayeli, bayakhe imizi engamanxuwa, bahlale kuyo; baya kutyala izidiliya, basele iwayini yazo; baya kutyala imiyezo, badle iziqhamo zayo.', 'XHO22'),
(34, 9, 15, 'Ndobatyala emhlabeni wabo, bangabi sagxothwa emhlabeni wabo endibanike wona; utsho uYehova uThixo wakho.', 'XHO22'),
(35, 1, 1, 'Umbono kaObhadiya. Itsho iNkosi uYehova ngoEdom, ukuthi, Siviwe ilizwi likaYehova, kuthunywe umthunywa ezintlangeni, esithi, Sukani nime, sisukele phezulu kuye, silwe naye.', 'XHO22'),
(35, 1, 2, 'Yabonani, ndikwenze umncinanana ezintlangeni; ugxeka kakhulu.', 'XHO22'),
(35, 1, 3, 'Ukukhukhumala kwentliziyo yakho kukulukuhlile, wena uhleli ezingxondorheni, okhaya liphezulu; othi entliziyweni yakhe, Ngubani na oya kundihlisa?', 'XHO22'),
(35, 1, 4, 'Ukuba uthe wakhela phezulu njengexhalanga, nokuba ithe indlu yakho yabekwa phakathi kweenkwenkwezi, ndokuhlisa nalapho; utsho uYehova.', 'XHO22'),
(35, 1, 5, 'Ukuba amasela ebethe eza kuwe njengamasela ebusuku (hayi, ukunqunyulwa kwakho!), ebengayi kuba anele na? Ukuba abalimi bezidiliya bafike kuwe, babengayi kuzishiya na iidiliya?', 'XHO22'),
(35, 1, 6, 'Hayi indlela ezigocagocwe ngayo izinto zikaEsawu! Hayi ukuphengululeka kwezinto zakhe ezifihlakeleyo!', 'XHO22'),
(35, 1, 7, 'Onke amadoda omnqophiso wakho akufikise emdeni; amadoda abehleli nawe eluxolweni anilukuhla, akoyisa; abadla isonka sakho babeka inxeba phantsi kwakho; akukho ukuqonda kuye.', 'XHO22'),
(35, 1, 8, 'Andiyi kuthi na ngelo xesha, utsho uYehova, ndizitshabalalise izilumko zakwaEdom, nezilumko ezintabeni zakwaEsawu?', 'XHO22'),
(35, 1, 9, 'Aya kugubha amagorha akho, Teman, zigawulwe ngokubulawa zonke iintaba zakwaEsawu.', 'XHO22'),
(35, 1, 10, 'Ngenxa yokumgonyamela kwakho umntakwenu uYakobi, uya kukugubungela, utshatyalaliswe ukusa kwixesha elingenammiselo.', 'XHO22'),
(35, 1, 11, 'Ngemini owama ngayo nxamnye nayo, mini awathi abasemzini bakhupha umkhosi wakhe nabasemzini abangena emasangweni akhe baza bayenzela amaqashiso iYerusalem, nawe wawunjengabawabo.', 'XHO22'),
(35, 1, 12, 'Bekungafanelanga uyikhangele imini yomninawa wakho, mhla waba ngumphambukeli; yaye ubungafanelanga ube nemihlali ngoonyana bakaYuda ngemini yokutshatyalaliswa kwabo; Ungathethi ngokuzidla ngemini yembandezelo.', 'XHO22'),
(35, 1, 13, 'Ubungafanelanga ungene esangweni labantu bam ngemini yeshwangusha labo; ewe, ubungabhekanga kwinkxwaleko yabo ngemini yeshwangusha labo, ungasa izandla zakho phezu kwempahla yabo ngemini yeshwangusha labo;', 'XHO22'),
(35, 1, 14, 'Yaye ubungafanelanga ume ekwahlukaneni kweendlela ukuze ubulale abasindileyo; nabo baseleyo ngemini yembandezelo, uze unganikezeli.', 'XHO22'),
(35, 1, 15, 'Ngokuba isondele imini kaYehova kuzo zonke iintlanga; njengoko wenze ngako, kuya kwenjiwa njalo kuwe; umvuzo wakho uya kubuyela entlokweni yakho.', 'XHO22'),
(35, 1, 16, 'Njengoko naselayo entabeni yam engcwele, ziya kuhlala zisela ngokunjalo zonke iintlanga, zisele, zidle, zibe njengabangahlanjwanga;', 'XHO22'),
(35, 1, 17, 'Ke, entabeni yeZiyon kuya kubakho usindiso, kube yingcwele; nendlu kaYakobi idle ilifa ubutyebi bayo.', 'XHO22'),
(35, 1, 18, 'Yaye indlu kaYakobi iya kuba ngumlilo, nendlu kaYosefu iya kuba sisihombo somlilo, nabo bendlu kaEsawu baya kuba njengomdiza, batshise kuwo baze bawutshabalalise; akuyi kusala nto endlwini kaEsawu; ngokuba watshoyo uYehova.', 'XHO22'),
(35, 1, 19, 'Abavela ezantsi baya kuzidla ilifa iintaba zakwaEsawu; yaye amaFilisti aya kulidla ilifa ilizwe lakwaEfrayim nelizwe lakwaSamari entlango; noBhenjamin uya kuyidla ilifa iGiliyadi.', 'XHO22'),
(35, 1, 20, 'Yaye isiphelo salo mkhosi woonyana bakaSirayeli siya kulithimba ilizwe lakwaKanan kude kuse eTsarefathi; nabathinjwa baseYerusalem abavela eSefaradi baya kudla ilifa izixeko eziya ngasemzantsi.', 'XHO22'),
(35, 1, 21, 'Konyuka abasindisi entabeni yeZiyon, ukuze bazigwebe iintaba zakwaEsawu; ubukumkani buya kuba bobukaYehova.', 'XHO22'),
(36, 1, 1, 'Kwafika ilizwi likaYehova kuYona, unyana ka-Amitayi, lisithi,', 'XHO22'),
(36, 1, 2, 'Suk’ ume, uye eNineve, kuloo mzi mkhulu, ubulale ukumkani phezu kwawo; ngokuba ububi babo bunyuke phambi kwam.', 'XHO22'),
(36, 1, 3, 'Wesuka ke uYona, ebalekela eTarshishe phambi koYehova, wehla waya eYafo; wafumana inqanawa esinge eTarshishe, wayihlawulela indlela yakhe, wangena kuyo ukuze abone iTarshishe, emke ebusweni bukaYehova.', 'XHO22'),
(36, 1, 4, 'Ke uYehova wagalelekisa umoya omkhulu elwandle; kwabakho uqhwithela olukhulu elwandle, yaphuka inqanawa leyo.', 'XHO22'),
(36, 1, 5, 'Baza boyika oomatiloshe baza ngamnye wabiza ngamnye kuthixo wakhe waza wayijulela elwandle impahla ebisenqanaweni ukuze kube lula kubo. uYona wehla waya ecaleni lomkhombe; walala phantsi.', 'XHO22'),
(36, 1, 6, 'Weza ke kuye umthetheli-waka, wathi kuye, Uthini na wena, Rabhi? vuka, ubize uThixo wakho, ukuba usikhumbule uThixo wakho, ukuba singabhubhi.', 'XHO22'),
(36, 1, 7, 'Bathi ke elowo kummelwane wakhe, Yizani, senze amaqashiso, sazi ukuba kungenxa yeni na ukuba sihlelwe bububi. Bawisa amaqashiso, iqashiso laphuma noYona.', 'XHO22'),
(36, 1, 8, 'Bathi kuye, Sixelele ukuba sihlelwe ngubani na obu bubi; yintoni umsebenzi wakho kwaye ungowaphi? lithini ilizwe lakho kwaye ungowasiphi isizwe?', 'XHO22'),
(36, 1, 9, 'Wathi kubo, NdingumHebhere; yaye ndiyamoyika uYehova uTHIXO wamazulu owenza ulwandle nomhlaba owomileyo.', 'XHO22'),
(36, 1, 10, 'Oyika amadoda lawo ngoloyiko olukhulu, athi kuye, Yini na ukuba wenze le nto? Kuba loo madoda ayesazi isizathu sokuba abaleke ebusweni bukaYehova, kuba wayewaxelele.', 'XHO22'),
(36, 1, 11, 'Athi kuye, Masenze ntoni na, ukuze ulwandle lusiyeke? ngokuba ulwandle belugubha, lwagubha.', 'XHO22'),
(36, 1, 12, 'Wathi kuwo, Ndithabatheni, nindijulele elwandle; ngoko ke ulwandle luya kunizolisa, kuba ndiyazi ukuba esi sivuthuvuthu sinifikele, ngenxa yam.', 'XHO22'),
(36, 1, 13, 'Atyhudisa emazeni amadoda, ezama ukuyibuyisela emhlabeni; akhohlwa, ngokuba ulwandle lwalusilwa, lusilwa phambi kwabo.', 'XHO22'),
(36, 1, 14, 'Bathandaza kuYehova, bathi, Siyakuthandaza kuwe, Yehova, ukuba ungabhubhi ngomphefumlo wale ndoda, ungaphalazi igazi elimsulwa phezu kwethu; ngokuba, Yehova, wenze ngokuthanda kwakho.', 'XHO22'),
(36, 1, 15, 'Ambamba ke uYona, amjulela elwandle, lwazola ulwandle.', 'XHO22'),
(36, 1, 16, 'Andula ke loo madoda amoyika ngokugqithiseleyo uYehova aze azise uYehova umbingelelo aze enze izifungo.', 'XHO22'),
(36, 1, 17, 'uYehova wathumela intlanzi enkulu ukuba imginye uYona. uYona wahlala embilinini wentlanzi iimini ezintathu nobusuku obuthathu.', 'XHO22'),
(36, 2, 1, 'uYona wathandaza kuYehova uThixo wakhe esembilinini wentlanzi, wathi,', 'XHO22'),
(36, 2, 2, 'Ndathi, Ndakhala kuYehova ngenxa yokubandezeleka kwam, wandiva; ndisesiswini sobafileyo, ndakhala, waliva ilizwi lam.', 'XHO22'),
(36, 2, 3, 'Ngokuba wawundiphosa enzulwini, embilinini yolwandle; Yandirhawula imilambo, Adlula phezu kwam onke amaza akho alwayo, namaza akho alwayo.', 'XHO22'),
(36, 2, 4, 'Ndathi ke mna, Ndigxothiwe phambi kobuso bakho; Noko ke ndiya kubuya ndiyikhangele etempileni yakho engcwele.', 'XHO22'),
(36, 2, 5, 'Andirhawula amanzi, ada eza emphefumlweni;', 'XHO22'),
(36, 2, 6, 'Ndehla ndaya ezingontsini zeentaba; Lindijikelezile ihlabathi nemithwalo yalo; noko ke ubuphakamisile eboleni ubomi bam, Yehova Thixo wam!', 'XHO22'),
(36, 2, 7, 'Xa umoya wam wawudandathekileyo ngaphakathi kwam, ndacinga ngoYehova, waza umthandazo wam wafika kuwe, etempileni yakho engcwele.', 'XHO22'),
(36, 2, 8, 'Bayirhole inceba abayibonayo into engamampunge,', 'XHO22'),
(36, 2, 9, 'Ke mna ndiya kubingelela kuni ndinelizwi lombulelo; Ndiza kuyihlawula into endiyithembisileyo. Usindiso luvela kuYehova.', 'XHO22'),
(36, 2, 10, 'uYehova wathetha nentlanzi, yaza yamhlamba uYona emhlabeni owomileyo.', 'XHO22'),
(36, 3, 1, 'Lafika ilizwi likaYehova kuYona okwesibini, lisithi,', 'XHO22'),
(36, 3, 2, 'Suk’ ume, uye eNineve, kuloo mzi mkhulu, umemeze ilizwi endikuwisela umthetho ngako.', 'XHO22'),
(36, 3, 3, 'Wesuka ke uYona, waya eNineve ngokwelizwi likaYehova. Yaye iNineve yayisisixeko esikhulu kakhulu, esinohambo lweentsuku ezintathu.', 'XHO22'),
(36, 3, 4, 'uYona waqala wangena kuwo umzi, uhambo losuku olunye, wadanduluka esithi, Zisaya kuba ziintsuku ezimashumi mane, iNineve iwile.', 'XHO22'),
(36, 3, 5, 'Abantu baseNineve bakholwa kuThixo, bawubhengeza umhla wozila ukudla, bambatha iingubo ezimnyama, bethabathela komkhulu besa kwabancinane bawo.', 'XHO22'),
(36, 3, 6, 'Lafika ilizwi elo kukumkani waseNineve, wayishiya itrone yakhe, wayikhulula ingubo yakhe enkulu, wazigquma ngezirhwexayo, wahlala eluthuthwini.', 'XHO22'),
(36, 3, 7, 'Wayixela, waza wayizisa iNineve ngokomyalelo kakumkani nabalungiseleli bakhe, esithi: “Makungabikho mntu nasilwanyana sasekhaya, okanye nkomo okanye gusha, engangcanga nto; ungadli, ungaseli manzi.', 'XHO22'),
(36, 3, 8, 'Mabambathe iingubo ezimnyama umntu nenkomo, akhale kuThixo ngamandla; ewe, makabuye elowo endleleni yakhe embi, nakugonyamelo olusezandleni zakhe.', 'XHO22'),
(36, 3, 9, 'Ngubani na owaziyo ukuba uThixo angazohlwaya, abuye, abuye emsindweni wakhe, ukuze singabhubhi?', 'XHO22'),
(36, 3, 10, 'uThixo wazibona izenzo zabo, ukuba babuyile ezindleleni zabo; uThixo wabenza ububi, abethe uya kubenza kubo; akayenza.', 'XHO22'),
(36, 4, 1, 'Kodwa uYona waba buhlungu kakhulu loo nto, waza wavutha ngumsindo.', 'XHO22'),
(36, 4, 2, 'Wathandaza kuYehova, wathi, Owu Yehova, asilolizwi na endalithethayo ndisezweni lakowethu? Ndabalekela eTarshishe ngenxa engaphambili, kuba ndandisazi ukuba uThixo unemfesane, onemfesane, onobulali, onenceba, nobugwenxa.', 'XHO22'),
(36, 4, 3, 'Wususe ke, Yehova, umoya wam kum; kuba kukulungele ukufa kunokuba udle ubomi.', 'XHO22'),
(36, 4, 4, 'Wathi uYehova, Yinto elungileyo na ukuqumba?', 'XHO22'),
(36, 4, 5, 'Wemka ke uYona esixekweni, wahlala ngasempumalanga kuwo umzi, wazenzela umnquba, wahlala emthunzini phantsi kwawo, wada wabona ukuba kuya kwenzeka ntoni na kuwo umzi.', 'XHO22'),
(36, 4, 6, 'uYehova uThixo wammisela umthi womthombe, wamgcina uYona, ukuze ube ngumthunzi entlokweni yakhe, umkhulule kwiintlungu zakhe. Yaye uYona wakuvuyela kakhulu ukuphatheka kakuhle.', 'XHO22'),
(36, 4, 7, 'Ngengomso uThixo wamisela umbungu, wawubetha umthi, woma.', 'XHO22'),
(36, 4, 8, 'Ekuphumeni kwelanga, uThixo wahambisa umoya wasempumalanga; ilanga labetha entloko kuYona, wadinwa, wafuna ukufa ngaphakathi kwakhe, wathi, Kulungile ukuba ndife kunokuba ndidle ubomi.', 'XHO22'),
(36, 4, 9, 'Wathi uThixo kuYona, Kulungile na ukuba uvuthe ngumsindo ngale ndoda? Wathi: Kulungile ukundiqumbela kude kuse ekufeni.', 'XHO22'),
(36, 4, 10, 'Wathi uYehova, Unenceba kwikhoboka lakho, ubungamkhonzanga, ukuba unganduluki; abavuka ngabusuku bunye, batshabalala ngabusuku bunye;', 'XHO22'),
(36, 4, 11, 'ndingabi nanceba na ke ngenxa yeNineve, loo mzi mkhulu, unabantu abangaphezu kwamashumi amathandathu amawaka, abangakwaziyo ukwahlula ekunene kwekhohlo; neenkomo ezininzi?', 'XHO22'),
(37, 1, 1, 'Ilizwi likaYehova elafika kuMika waseMara, ngemihla kaYotam, ka-Ahazi, kaHezekiya, ookumkani bakwaYuda, awalibonayo ngokusingisele kwelakwaSamari neYerusalem.', 'XHO22'),
(37, 1, 2, 'Yivani, nonke zintlanga; yivani, hlabathi nenzaliseko yalo, ibe lingqina iNkosi uYehova ngani, uYehova etempileni yakhe engcwele.', 'XHO22'),
(37, 1, 3, 'Kuba, yabona, uYehova uya kuyishiya indawo yakhe, ade ahle, anyathele phezu kwemimango yehlabathi.', 'XHO22'),
(37, 1, 4, 'Iintaba ziya kunyibilika phantsi kwakhe, neentlambo ziya kucandeka, njengomthwebeba weenyosi phambi komlilo nanjengamanzi athululelwa eweni.', 'XHO22'),
(37, 1, 5, 'Konke oku kungenxa yesikreqo sikaYakobi nesono sendlu kaSirayeli. Yintoni impazamo kaYakobi? AsingokwaSamari na? Ziyintoni na iintaba zakwaYuda? AsiyoYerusalem na?', 'XHO22'),
(37, 1, 6, 'Ngoko ke ndolenza elakwaSamari libe yimfumba yamasimi, libe njengezidiliya; ndiwaphose amatye alo emfuleni, ndizityhile iziseko zalo.', 'XHO22'),
(37, 1, 7, 'Iya kwaphulwa yonke imifanekiso yakhe eqingqiweyo, utshiswe ngomlilo wonke umvuzo wakhe; ngokuba ubuthiwe emvuthweni wehenyukazi, baya kubuyela emvuzweni wehenyukazi.', 'XHO22'),
(37, 1, 8, 'Ngako oko, ndiya kukhala, ndikhale, ndize, ndize; ndiya kukhala njengempungutye, ndilile njengeenciniba.', 'XHO22'),
(37, 1, 9, 'Kuba inxeba lakhe alinakuphiliswa; ngokuba ibifike kwaYuda; yeza esangweni labantu bam eYerusalem.', 'XHO22'),
(37, 1, 10, 'Musani ukuyikhankanya iGati, musani ukukhala; ziqengqeleka eluthulini endlwini ka-Afra.', 'XHO22'),
(37, 1, 11, 'Hambani, mmi waseSafire, nize, nineentloni; igama lakhe uya kulamkela kuni.', 'XHO22'),
(37, 1, 12, 'Kuba abemi baseMaroti babelindele okulungileyo, kodwa kwakusihla ububi buphuma kuYehova beza esangweni leYerusalem.', 'XHO22'),
(37, 1, 13, 'Bophani inqwelo yokulwa, bemi baseLagishe; kukuqala kwesono sentombi enguZiyon, ngenxa yokuba izikreqo zakwaSirayeli zifunyenwe kuwe.', 'XHO22'),
(37, 1, 14, 'Ngako oko yiphani iMoreshete yaseGati; izindlu zika-Akishe ziya kuba ngubuxoki kookumkani bakwaSirayeli.', 'XHO22'),
(37, 1, 15, 'Ndiya kukuzisela indlalifa, mnini kaMaresa; uya kufika eAdulam, eluzuko lukaSirayeli.', 'XHO22'),
(37, 1, 16, 'Zilungise, uzinyule koonyana bakho abanenceba; zandise ukucheba kwakho njengexhalanga; ngokuba anishiya ekuthinjweni.', 'XHO22'),
(37, 2, 1, 'Yeha ke, abo bacebayo, benza ububi ezililini zabo! xa kunetha imvula, bayayenza loo nto, kuba kusemandleni ezandla zabo.', 'XHO22'),
(37, 2, 2, 'Bacebisana ngamasimi, bazithabatha; nezindlu bazifudusa; bacudisa umfo nendlu yakhe, indoda nelifa layo.', 'XHO22'),
(37, 2, 3, 'Ngako oko, wathi uYehova, Yabonani, ndicinga ububi ngesi sizukulwana, ongayi kuzisusa iintamo zenu kuso; ningahambi ngokuzidla, ngokuba eli xesha libi.', 'XHO22'),
(37, 2, 4, 'Ngelo xesha umntu uya kukhupha umzekeliso ngawo, abhomboloze elila, esithi, Sibhuqiwe ngenene; Usitshintshile isabelo sabantu bam; Uyisuse njani kum! Wayahlulahlula intsimi yethu.', 'XHO22'),
(37, 2, 5, 'Ngoko ke akuyi kuba namntu owenza amaqashiso ebandleni likaYehova.', 'XHO22'),
(37, 2, 6, 'Bathi, Musani ukuwisa intetho, bewisa intetho;', 'XHO22'),
(37, 2, 7, 'Wena kuthiwa uyindlu kaYakobi, uyashukunyiswa na uMoya kaYehova? yinto ayenzayo leyo? Akalungisi na amazwi am kohamba ngobulungisa?', 'XHO22'),
(37, 2, 8, 'Ngelo xesha abantu bam baqhubeka bekhula njengeentshaba: Nibahluba ingubo yokwaleka abo badlula ngoxolo njengabantu abaphambukayo emfazweni.', 'XHO22'),
(37, 2, 9, 'Abafazi babantu bam bayakugxotha ezindlwini zezihombo zabo; ubuhluthe ubungangamsha babantwana babo ngonaphakade.', 'XHO22'),
(37, 2, 10, 'Vuka, uhambe; ngokuba indawo yenu asiyiyo indawo yokuphumla kwenu;', 'XHO22'),
(37, 2, 11, 'Ukuba umntu ohamba nomoya, wathetha ubuxoki, wathetha ubuxoki, wathi, Ndiya kuwisa intetho kuni ngewayini nangesiselo esinxilisayo; kwaye uya kuba ngumprofeti wolu hlanga.', 'XHO22'),
(37, 2, 12, 'Ndiya kukuhlanganisa okunene, Yakobi, uphela; okunene ndiya kuwabutha amasalela akwaSirayeli; ndiya kubabutha njengempahla emfutshane yaseBhotsera, njengomhlambi ophakathi kwedlelo lawo;', 'XHO22'),
(37, 2, 13, 'Unyuke phambi kwabo umbhuqi; batyhoboza, bangena ngesango, baphuma ngalo; yaye ukumkani wabo uya kubakhokela, yaye uYehova uya kubakhokela.', 'XHO22'),
(37, 3, 1, 'Ndathi ke, Khanive, zintloko zakwaYakobi, nani kumkani bendlu kaSirayeli. Asikokwenu na ukwazi umgwebo?', 'XHO22'),
(37, 3, 2, 'othiya okulungileyo, athande okubi; abo bahlinza ulusu lwabo, nenyama yabo emathanjeni abo;', 'XHO22'),
(37, 3, 3, 'nina, bayidlileyo inyama yabantu bam, bahlube ulusu lwabo; bawaphula amathambo abo, bawanqunqa njengembiza, nenyama embizeni.', 'XHO22'),
(37, 3, 4, 'Baya kwandula babize kuYehova, angabaphulaphuli; Ngelo xesha uya kubusithelisa ubuso bakhe kubo, ngokuba benze ububi ngeentlondi zabo;', 'XHO22'),
(37, 3, 5, 'Utsho uYehova ngokusingisele kubaprofeti ababandwendwisayo abantu bam, abatshixiza amazinyo, abadandulukayo, besithi, Luxolo; ke yena ongangeni emlonyeni wabo, bamlungiselele imfazwe.', 'XHO22'),
(37, 3, 6, 'Ngoko ke niya kuba nobusuku, ukuze ningabi nabo; kuya kuba mnyama kuwe, ukuze ungaboni; liya kutshona ilanga kubaprofeti, ibe mnyama imini kubo.', 'XHO22'),
(37, 3, 7, 'Ziya kudana iimboni, zibe neentloni amatola; ewe, bonke baya kuzifihla izipaji zabo; ngokuba akukho mpendulo kaThixo.', 'XHO22'),
(37, 3, 8, 'Ke mna ndizele amandla ngomoya kaYehova, nobulungisa namandla, ukuze ndimxelele uYakobi ukreqo lwakhe, uSirayeli isono sakhe.', 'XHO22'),
(37, 3, 9, 'Liveni eli lizwi, nina zintloko zendlu kaYakobi, nani baphathi bendlu kaSirayeli, nina bakuthiyileyo ubulungisa, bakugqwetha konke okusesikweni.', 'XHO22'),
(37, 3, 10, 'Bayakha iZiyon ngamagazi, neYerusalem ngobugqwetha;', 'XHO22'),
(37, 3, 11, 'Abathetheli bayo bagweba ngenxa yesicengo, nababingeleli bayo bafundisa ngenxa yesicengo, nabaprofeti bayo baprofeta ngenxa yemali; noko baya kuyama ngoYehova, besithi, uYehova akakho na phakathi kwethu? akukho nto imbi inokusehlela.', 'XHO22'),
(37, 3, 12, 'Ngenxa yoko, iZiyon iya kutyalwa njengentsimi ngenxa yenu, iYerusalem ibe yimfumba yamatye, nentaba yayo indlu ibe njengeentaba zehlathi.', 'XHO22'),
(37, 4, 1, 'Ke kaloku, ekupheleni kwemihla, intaba yendlu kaYehova iya kuma ezintabeni, izivelele iinduli; kwaye abantu baya kuthontelana ukuya kuyo.', 'XHO22'),
(37, 4, 2, 'Ziya kuza iintlanga ezininzi, zisithi, Yizani, sinyuke siye entabeni kaYehova, endlwini yoThixo kaYakobi; asiyalele iindlela zakhe, sihambe ngeendlela zakhe; kuba umyalelo uya kuvela eZiyon, nelizwi likaYehova eYerusalem.', 'XHO22'),
(37, 4, 3, 'Uya kugweba phakathi kwezizwe ezininzi, ohlwaye izizwe ezinamandla, avela kude; ziwakhande amakrele azo abe zizitshetshe zeediliya, nezikhali zazo zibe zizikere zokuthena imithi; uhlanga aluyi kuphakamisela olunye uhlanga ikrele, nezizwe zingaphindi zifunde ukulwa.', 'XHO22'),
(37, 4, 4, 'Baya kuhlala elowo phantsi komdiliya wakhe, naphantsi komkhiwane wakhe; yaye akukho bani uya kubothusa, ngenxa yokuba umlomo kaYehova wemikhosi ukuthethile oko.', 'XHO22'),
(37, 4, 5, 'Ngokuba zonke iintlanga ziya kuhamba ngasinye egameni lothixo waso, kwaye thina siya kuhamba egameni likaYehova uThixo wethu, ngonaphakade kanaphakade.', 'XHO22'),
(37, 4, 6, 'Ngaloo mini, utsho uYehova, ndiya kubahlanganisa abathi cwaka, ndibabuthe abagxothiweyo, nabagxothiweyo nam;', 'XHO22'),
(37, 4, 7, 'Abaphukileyo ndiya kubenza amasalela, nabakhulileyo ndibenze uhlanga olunamandla; yaye uYehova uya kubalawula eNtabeni yeZiyon ukususela ngoku kuse kwixesha elingenammiselo.', 'XHO22'),
(37, 4, 8, 'Wena ke, nqaba ende yomhlambi, nduli yentombi enguZiyon, kuya kuza kuwe ubukumkani bokuqala ubukumkani buya kufika entombini enguYerusalem.', 'XHO22'),
(37, 4, 9, 'Yini na ukuba udanduluke ngezwi elikhulu? Akukho kumkani na kuwe? Ngaba umcebisi wakho ufile? ngokuba intlungu injengomfazi onenimba.', 'XHO22'),
(37, 4, 10, 'Zibhijabhije, zala, ntombi inguZiyon, njengozalayo; kuba ngoku uya kusishiya isixeko, uhlale emaphandleni, uye eBhabhiloni; niya kusindiswa khona; uya kukuhlangula uYehova esandleni seentshaba zakho.', 'XHO22'),
(37, 4, 11, 'Kananjalo iintlanga ezininzi zikuhlanganisele kuwe, zisithi, Mayihlanjelwe, ayibone amehlo ethu iZiyon.', 'XHO22'),
(37, 4, 12, 'Kodwa zona ezi azizazi iingcamango zikaYehova, yaye aziliqondi icebo lakhe, kuba uya kuzihlanganisa njengemfumba emhlabeni.', 'XHO22'),
(37, 4, 13, 'Vukani, unxaniwe, ntombi inguZiyon; kuba uphondo lwakho ndilwenza isinyithi, neenzipho zakho zibe lubhedu; uya kwaphula iintlanga ezininzi; yaye indyebo yabo ndiya kuyinikela kuYehova nobutyebi babo kwiNkosi yehlabathi liphela.', 'XHO22'),
(37, 5, 1, 'Kaloku ke, hlanganisana, ntombi yomkhosi; Wasingqinga; baya kumbetha ngentonga umgwebi wakwaSirayeli esidleleni.', 'XHO22'),
(37, 5, 2, '“Kodwa wena, Bhetelehem-efrata, nangona umncinane phakathi kwamawaka akwaYuda, noko ke uya kuphuma kuwe uze kum, yaye mna ndiya kuba yinkokeli kaSirayeli; abazalwayo kususela kwamandulo, kwasephakadeni.', 'XHO22'),
(37, 5, 3, 'Ngako oko uya kuwanikela kubo, kude kube lixesha lokuzala kwayo; abuye ke amasalela abazalwana bakhe, aye koonyana bakaSirayeli;', 'XHO22'),
(37, 5, 4, 'uya kuma, aluse enamandla kaYehova, enobungangamsha begama likaYehova uThixo wakhe; kwaye ziya kuhlala, ngenxa yokuba ngoku uya kuba mkhulu kude kube sekupheleni kwehlabathi.', 'XHO22'),
(37, 5, 5, 'Yoba luxolo loo ndoda, xa ama-Asiriya azileyo ezweni lakowethu; Ukuba uthe wanyathela ezingxandeni zethu ezinde, sonikela kuye abalusi abasixhenxe, namagosa asithoba anamazinyo amabini.', 'XHO22'),
(37, 5, 6, 'Baya kulonakalisa ilizwe laseAsiriya ngekrele, nelizwe lakwaNimrodi emasangweni alo; ngokunjalo uya kusihlangula eAsiriya, xa ayingenelayo ilizwe lakowethu, xa athe wangena ezweni lakowethu.', 'XHO22'),
(37, 5, 7, 'Amasalela akwaYakobi aya kuba phakathi kwezizwe ezininzi, njengombethe ophuma kuYehova, nanjengemvula ebutyanini, engashiyi mntu, engalindeli oonyana babantu.', 'XHO22'),
(37, 5, 8, 'Amasalela akwaYakobi aya kuba phakathi kweentlanga phakathi kwezizwe ezininzi njengengonyama phakathi kwezilo zehlathi, njengehobe phakathi kwemihlambi yeegusha; amaqhekeza, kwaye akukho bani unokukhulula ngentlawulelo.', 'XHO22'),
(37, 5, 9, 'Isandla sakho siya kuboyisa iintshaba zakho, zinqunyulwe zonke iintshaba zakho.', 'XHO22'),
(37, 5, 10, 'Ngaloo mini, utsho uYehova, ndiya kuwatshabalalisa amahashe akho, angabikho phakathi kwakho, ndizitshabalalise iinqwelo zakho zokulwa.', 'XHO22'),
(37, 5, 11, 'Ndiya kuzitshabalalisa izixeko zelizwe lakho, ndizitshabalalise zonke iinqaba zakho.', 'XHO22'),
(37, 5, 12, 'Ndowususa umlingo esandleni sakho; nabakhafuli akusayi kuba sabakho;', 'XHO22'),
(37, 5, 13, 'Ndoyibhangisa imifanekiso yenu eqingqiweyo, ingabikho phakathi kwenu; aniyi kuphinda niqubude kumsebenzi wezandla zenu.', 'XHO22'),
(37, 5, 14, 'Ndiwanyothule amahlathi akho phakathi kwakho, ndiyitshabalalise imizi yakho.', 'XHO22'),
(37, 5, 15, 'Ndiya kuphindezela ezintlangeni ndinomsindo, ndinobushushu, ezingazange zakuva.', 'XHO22'),
(37, 6, 1, 'Yivani into ayithethayo uYehova; Suk’ ume, ubambane neentaba, iinduli zilive ilizwi lakho;', 'XHO22'),
(37, 6, 2, 'Nina zintaba, liveni ilizwi likaYehova, nani ziseko zehlabathi, ngokuba uYehova ubambene nabantu bakhe, uya kulwa noSirayeli.', 'XHO22'),
(37, 6, 3, 'Bantu bam, ndenze ntoni na kuni? kanti ndikudinise ngantoni? ngqinani ngam.', 'XHO22'),
(37, 6, 4, 'Kuba ndanikhupha kwilizwe laseYiputa ndaza ndanikhulula endlwini yobukhoboka; Ndathuma ooMoses, uAron, noMiriyam, ukuba banikhokele.', 'XHO22'),
(37, 6, 5, 'Owu bantu bam, khumbulani oko kwathethwa nguBhalaki ukumkani wakwaMowabhi noko kwaphendulwa nguBhileham unyana kaBhehore ukususela eShitim ukuya eGiligali; ukuze ubazi ubulungisa bukaYehova.', 'XHO22'),
(37, 6, 6, 'Ndiya kumkhawulela uYehova ndinantoni na, ndiqubude phambi koThixo ophakamileyo? Ndiya kumkhawulela ndinamadini anyukayo na, ndinamathole amnyaka mnye na?', 'XHO22'),
(37, 6, 7, 'Wokholiswa na uYehova ngamawaka eenkunzi zeegusha, nangamawaka alishumi emilambo yeoli? Ndomnikela na unyana wam owamazibulo ngenxa yokreqo lwam, isiqhamo sesizalo sam na ngenxa yesono somphefumlo wam?', 'XHO22'),
(37, 6, 8, 'Wena mntu, ukuxelile okulungileyo; Yaye yintoni ayifunayo kuwe uYehova ngaphandle kokuba wenze okulungileyo, uthande uthando, uhambe noThixo wakho ngokuthozama?', 'XHO22'),
(37, 6, 9, 'Izwi likaYehova libhekisa kuwo umzi ukubiza, igama lakho liya kulibona igama lakho; mamela intonga ekuphetheyo.', 'XHO22'),
(37, 6, 10, 'Busekho na ubuncwane bokungendawo endlwini yongendawo, nentwana yamasikizi?', 'XHO22'),
(37, 6, 11, 'Ndobagqala benyulu na, ndinomlinganiselo ombi, ndinengxowa yenkohliso?', 'XHO22'),
(37, 6, 12, 'Kuba ubutyebi bayo buzele lugonyamelo, nabemi bakhona bathethe ubuxoki, nolwimi lwabo lusemlonyeni wabo.', 'XHO22'),
(37, 6, 13, 'Ngoko ke ndiya kunigulisa ngokunibetha, ndinihlaziye ngenxa yezono zenu.', 'XHO22'),
(37, 6, 14, 'Niya kudla, ningahluthi; nokulahlwa kwakho kube phakathi kwakho; uya kubambisa, ungasindisi; nento eniya kuyinika omnye komnye, ndiyinike omnye komnye ikrele.', 'XHO22'),
(37, 6, 15, 'Uya kuhlwayela, ungavuni; uxovule umnquma, kodwa ungazithambisi ngeoli; newayini emnandi, kodwa akuyi kusela wayini.', 'XHO22'),
(37, 6, 16, 'Zigciniwe imithetho kaOmri, nayo yonke imisebenzi yendlu ka-Ahabhi, nihambe ngokwecebo layo; Ndiya kunenza kube senkangala, ndikwenze intlekisa, nabemi bayo; ke ngoko, kufuneka unxibe ingubo yokwaleka yabantu bam.', 'XHO22'),
(37, 7, 1, 'Yeha ke mna! kuba njengokuvunwa kweziqhamo zasehlotyeni, njengokuvunwa kweediliya; akudliwa busika; umphefumlo wam ulangazelela iziqhamo ezivuthiweyo.', 'XHO22'),
(37, 7, 2, 'Umntu olungileyo utshabalele ehlabathini, kwaye akukho malungisa phakathi kwabantu; bonke bafuna igazi; elowo uzingela umzalwana wakhe ngomnatha.', 'XHO22'),
(37, 7, 3, 'Ukuba ngezandla zozibini benze ububi, bacele kukumkani; umgwebi acele umvuzo; nendoda enkulu ixela umnqweno wayo owoyikekayo; Bayivala ke.', 'XHO22'),
(37, 7, 4, 'Olungileyo kubo ufana nqwa nothango, ilungisa libukhali ngaphezu kothango lweminga; iyeza imini yokulinda kwenu; ngoku iza kuba sisidubedube.', 'XHO22'),
(37, 7, 5, 'Musa ukukholosa ngomhlobo, ungathembeli ngamakholwa; Gcina iingcango zomlomo wakho ngenxa yalowo ulele esifubeni sakho.', 'XHO22'),
(37, 7, 6, 'Ngokuba unyana uyambeka uyise, intombi isukela phezulu kunina, umolokazana kusukela phezulu kuninazala; iintshaba zomntu ngabantu bendlu yakhe;', 'XHO22'),
(37, 7, 7, 'Mna ke ndiya kuqwalasela kuYehova; ndiya kulindela kuThixo wokusindiswa kwam; uya kundiva uThixo wam.', 'XHO22'),
(37, 7, 8, 'Musa ukundivuyelela, lutshaba lwam; ukuba ndithe ndawa, ndovuka; kuba, ndihleli ebumnyameni nje, uYehova uya kuba kukukhanya kum.', 'XHO22'),
(37, 7, 9, 'Ukujala kukaYehova ndiya kukuthwala, kuba ndonile kuye, ade andise ematyaleni, andigwebe; Uya kundikhanyisela, ndibubone ubulungisa bakhe.', 'XHO22'),
(37, 7, 10, 'Utshaba lwam luya kubona, lugutyungelwe kukudana, luthi kum, Uphi na uYehova, uThixo wakho? amehlo am aya kumbona; ngoku niya kunyathelwa njengodaka lweendlela.', 'XHO22'),
(37, 7, 11, 'Ngaloo mini ziya kwakhiwa iindonga zakho, ngaloo mini umgwebo uya kuba kude.', 'XHO22'),
(37, 7, 12, 'Ngelo xesha uya kuphinda eze kuwe evela eAsiriya nakwizixeko ezinqatyisiweyo nokususela kwinqaba ukusa kuMlambo nokususela kolunye ulwandle kuse kolunye ulwandle nokususela entabeni ukusa kolunye.', 'XHO22'),
(37, 7, 13, 'Kodwa ilizwe liya kuba yintlango ngenxa yabemi balo, ngenxa yemiphumo yezenzo zabo.', 'XHO22'),
(37, 7, 14, 'Baluse abantu bakho ngentonga yakho, izimvu zelifa lakho, ezizihlalela zodwa ehlathini phakathi kweKarmele; mabadle eBhashan naseGiliyadi, njengokwamandulo.', 'XHO22'),
(37, 7, 15, 'Ndiza kumbonisa imimangaliso njengemihla yokuphuma kwakho kwilizwe laseYiputa.', 'XHO22'),
(37, 7, 16, 'Iintlanga ziya kukubona oku, zibe neentloni ngenxa yobugorha bazo bonke; baya kubeka izandla emlonyeni, iindlebe zabo ziya kuthi cwaka.', 'XHO22'),
(37, 7, 17, 'Ziya kukhotha uthuli njengeenyoka, ziphume emingxunyeni yazo njengeempethu; ziya kumoyika uYehova uThixo wethu, zoyike wena.', 'XHO22'),
(37, 7, 18, 'Ngubani na onguThixo onjengawe, oxolela ubugwenxa, osidelayo isono samasalela elifa lakhe? Akayi kuwugcina umsindo wakhe ngonaphakade, ngokuba ethanda inceba.', 'XHO22'),
(37, 7, 19, 'Uya kuguquka kwakhona kwaye abe nenceba kuthi; Uya kubunyathela ubugwenxa bethu; kwaye uya kuziphosa zonke izono zabo enzulwini yolwandle.', 'XHO22'),
(37, 7, 20, 'Yena uya kuyingqina inyaniso kuYakobi nakuAbraham, ngenceba awababeka ngayo oobawo ngesifungo, kwakudala.', 'XHO22'),
(38, 1, 1, 'Isihlabo saseNineve. Incwadi yobuso kaNahum wase-Elkosite.', 'XHO22'),
(38, 1, 2, 'uYehova nguThixo onobukhwele, ophindezelayo; uYehova ngumphindezeli, unengcwangu; uYehova ngumphindezeli kubachasi bakhe, ezigcinele iintshaba zakhe umsindo wakhe.', 'XHO22'),
(38, 1, 3, 'iNkosi inomonde, inamandla; uYehova unendlela yakhe esaqhwithi nasesiphangweni, yaye amafu luthuli lweenyawo zakhe.', 'XHO22'),
(38, 1, 4, 'Ukhalimela ulwandle, alutshise, ayomise nemilambo yonke; ishwabene iBhashan, iKarmele yomile, intyantyambo yaseLebhanon yomile.', 'XHO22'),
(38, 1, 5, 'Iintaba ziyashukuma ngenxa yakhe, neenduli ziyanyibilika, nomhlaba uyavutha bubuso bakhe, ewe, umhlaba nabo bonke abahlala kuwo.', 'XHO22'),
(38, 1, 6, 'Ngubani na ongemayo phambi komsindo wakhe? yaye ngubani na onokukumisa ukuvutha komsindo wakhe? Umsindo wakhe uphalazeka njengomlilo, namawa ayadilizwa nguye.', 'XHO22'),
(38, 1, 7, 'uYehova nguThixo, uligwiba ngemini yeshwangusha; kwaye uyabazi abo bakholose ngaye.', 'XHO22'),
(38, 1, 8, 'Ke uya kuyitshabalalisa indawo yabo ngomkhukula unogumbe, azisukele iintshaba zabo emnyameni.', 'XHO22'),
(38, 1, 9, 'Uthini ngoYehova? uya kutshabalalisa; ixhala aliyi kuvuka okwesibini.', 'XHO22'),
(38, 1, 10, 'Kuba bofanelana bebuthelana ndawonye ngokweziphingo, benxila njengenxila, baya kutshatyalaliswa njengeendiza ezomileyo.', 'XHO22'),
(38, 1, 11, 'Uphuma kuwe oceba ububi ngoYehova, umcebisi ongendawo.', 'XHO22'),
(38, 1, 12, 'Wathi uYehova, Nokuba bathe cwaka, nokuba baninzi, baya kubhangiswa ekudluleni kwakhe. Nangona ndanixinzelela phantsi, andiyi kuphinda ndinicinezele.', 'XHO22'),
(38, 1, 13, 'Kuba ngoku ndiya kuyaphula idyokhwe yakhe, isuke kuwe, ndiziqhawule imixokelelwane yakho;', 'XHO22'),
(38, 1, 14, 'Yawisa umthetho iNkosi ngani, ukuba akusayi kuba sahlwayelwa nto yimbi yegama lenu; Endlwini yothixo wakho ndowutshabalalisa umfanekiso oqingqiweyo notyhidiweyo; ngokuba enobubi.', 'XHO22'),
(38, 1, 15, 'Yabonani, zisezintabeni iinyawo zalowo uzisa iindaba ezilungileyo, lowo uvakalisa uxolo. Yenza imithendeleko yakho, Yuda, zalisa izibhambathiso zakho; ngokuba akayi kubuya acande kuwe ongendawo; unqunyulwe okulungileyo.', 'XHO22'),
(38, 2, 1, 'Umtyholi uphumile ebusweni bakho; zigcineni izixhobo zenu, zigcineni iindlela, ziqiniseni izinqe zenu, nize womeleze amandla enu kangangoko.', 'XHO22'),
(38, 2, 2, 'Ngokuba uYehova ulususile uzuko lukaYakobi njengobuqaqawuli bukaSirayeli;', 'XHO22'),
(38, 2, 3, 'Ingweletshetshe yamagorha akhe ibomvu, amagorha anxibe iingubo ezibomvu;', 'XHO22'),
(38, 2, 4, 'Iinqwelo zokulwa ziya kungena emabaleni, zime zithe tye, zajongana ezitratweni; usuke wanjengombane, wabaleka njengombane.', 'XHO22'),
(38, 2, 5, 'Uya kuthetha izinto zakhe ezilungileyo; baya kukhubeka endleleni yabo; ziya kugxalathelana ukuya eludongeni lwakhe, ibe ilungile ingxonde yomkhosi wayo.', 'XHO22'),
(38, 2, 6, 'Amasango emilambo aya kuvulwa, lichitheke ibhotwe.', 'XHO22'),
(38, 2, 7, 'Yaye uHushabe uya kuthatyathelwa ekuthinjweni, yena uya kuphakanyiswa, nezalamane zakhe ziya kumsa ngaphambili njengesandi samahobe ahleli esifubeni sawo.', 'XHO22'),
(38, 2, 8, 'Kodwa iNineve injengechibi elidala, kodwa yona iya kubaleka. Linda, linda, baya kulila; kodwa akukho bani uya kubheka ngasemva.', 'XHO22'),
(38, 2, 9, 'Thabathani isilivere, nithinge igolide; kuba akukho kuphela ukubuthwa nokuhomba kweento zonke ezilungileyo.', 'XHO22'),
(38, 2, 10, 'Ulambatha, udidekile, intliziyo iyanyibilika, amadolo akho ayabethana, nesinqe sakho sibuhlungu, ubuso babo bumnyama.', 'XHO22'),
(38, 2, 11, 'Iphi na indawo yokuhlala yeengonyama nedlelo leengonyama, apho ingonyama, ewe, ingonyama entsha, nengonyama entsha, ihambile, kungekho bani ubothusayo?', 'XHO22'),
(38, 2, 12, 'Ingonyama le iqwenga okwanele amathole ayo, iwuzele iingonyamakazi zayo, iwuzalise umzi wayo ngexhoba, nemiqolomba yayo ngamanqina.', 'XHO22'),
(38, 2, 13, 'Yabona, ndinendawo ngawe, utsho uYehova wemikhosi; ndiya kuzitshisa iinqwelo zakhe zokulwa, umkhonto uzidle iingubo zakho; yaye ndiza kulisusa ixhoba lakho emhlabeni, nelizwi labathunywa bakho lingabi saviwa.', 'XHO22'),
(38, 3, 1, 'Yeha, umzi uzele ligazi! uzele bubuxoki nokuphanga; into ethinjiweyo ayimki;', 'XHO22'),
(38, 3, 2, 'Kuvakala isandi sesabhokhwe nesandi samavili nesamahashe abalekayo neenqwelo zokulwa ezibalekayo.', 'XHO22'),
(38, 3, 3, 'Umkhweli wehashe uphakamisa ikrele elibengezelayo, nokubengezela komkhonto; nezidumbu zabo azinakuphela; bakhubeka phezu kwezidumbu zabo.', 'XHO22'),
(38, 3, 4, 'Ngenxa yobuninzi bezihenyuzo zehenyukazi elinethamsanqa, umnini-kukhafula, othengisa iintlanga ngobuhenyu balo, nemizalwane ngobukhafula balo.', 'XHO22'),
(38, 3, 5, 'Yabonani, ndinendawo nani, utsho uYehova wemikhosi; yaye ngokuqinisekileyo ndiya kuyivuna intlawulelo yakho ebusweni bakho ndize ndilibonise ihlazo lakho ezintlangeni nakookumkani ihlazo lakho.', 'XHO22'),
(38, 3, 6, 'Ndiya kunigalela ngothuli, ndinigculele, ndinibeke njengesixhanti.', 'XHO22'),
(38, 3, 7, 'Kuya kuthi, bonke abakubonayo bakoyike, bathi, iNineve ibhuqekile; ngubani na oya kumlilela? ndingabafuna phi na abathuthuzeli bakho?', 'XHO22'),
(38, 3, 8, 'Ngaba ulungile wena ngaphezu kwabemi boMlambo umNayile, ababemi phakathi kwemilambo, ababezungulwe ngamanzi, elunxwemeni lolwandle, eludonga lwayo lululwandle?', 'XHO22'),
(38, 3, 9, 'Ayengamandla ayo amaKushi kwanamaYiputa, engenakuphela ndawo ke; uPute noLubime bebengabancedisi bakho.', 'XHO22'),
(38, 3, 10, 'Ke yena wafuduswa, waya ekuthinjweni; nabantwana bakhe banyathelwa ezimbonjeni zezitrato zonke; yaye amadoda akhe abekekileyo ayelawula, nawo onke amadoda akhe amakhulu ekhonkxiwe ngemixokelelwane.', 'XHO22'),
(38, 3, 11, 'Nawe uya kunxila; uya kungcwatywa, ufune ubungangamsha ngenxa yotshaba.', 'XHO22'),
(38, 3, 12, 'Zonke iinqaba zakho ziya kuba njengemikhiwane enamakhiwane aqalela ekuqaleni; xa zishukunyiswa, zowela emlonyeni walowo uzidlayo.', 'XHO22'),
(38, 3, 13, 'Yabona, abantu bakho bangamankazana phakathi kwakho; amasango elizwe lakho aya kuvulelwa iintshaba zakho; umlilo uya kuzitshisa izitshixo zakho.', 'XHO22'),
(38, 3, 14, 'Lungiselela amanzi ongqingo, qinisa iinqaba zakho; ngena eludakeni, uxovule udongwe, utywine izitena.', 'XHO22'),
(38, 3, 15, 'Apho umlilo uya kukutshisa; ikrele liya kukutshabalalisa, ulidle njengenkumbi ekhothululayo; ukuze ufane neentethe, ube njengeenkumbi.', 'XHO22'),
(38, 3, 16, 'Ubandisile ke abarhwebi bakho ngaphezu kweenkwenkwezi zezulu;', 'XHO22'),
(38, 3, 17, 'Isithsaba sakho sinjengeenkumbi, neenkosana zakho zinjengeenkumbi ezinkulu ezihlala ezindongeni ngemini ebandayo, kodwa ekuphumeni kwelanga ziyabaleka, kungaziwa ukuba ziphi na.', 'XHO22'),
(38, 3, 18, 'Wena kumkani waseAsiriya, abalusi bakho balele; ziya kuhlala eluthulini iingangamsha zakho; bathe phangalala abantu bakho ezintabeni, akukho ubabuthayo.', 'XHO22'),
(38, 3, 19, 'Akukho kuphiliswa emivuzweni yakho; Inxeba lakho linzima; bonke abazivayo iziqhamo zakho baya kukuqhwabela izandla; Ngubani na ke, ukuba ubugwenxa bakho bungahlali buhleli phezu kwakho?', 'XHO22'),
(39, 1, 1, 'Ilizwi awalibonayo umprofeti uHabhakuki.', 'XHO22'),
(39, 1, 2, 'Kunini na, Yehova, ndibiza, ungandiva; ndibize ngogonyamelo, ungahlanguli;', 'XHO22'),
(39, 1, 3, 'Yini na ukuba ubuxele ubugwenxa bam, undikhangelise? kuba phambi kwam ukubhuqa nogonyamelo luphambi kwam;', 'XHO22'),
(39, 1, 4, 'Ke ngoko, umthetho awusalindelwe, nomgwebo awupheli; ngokuba abangendawo bawaxinzelele phantsi amalungisa; ke ngoko ukugwetywa okungalunganga kuyaqhubeka.', 'XHO22'),
(39, 1, 5, 'Khangelani, nina phakathi kweentlanga, nondele, nimangaliswe ngendlela emangalisayo; ngokuba ndiya kwenza umsebenzi ngemihla yenu, eningasayi kukholwa nguwo, nokuba nithe nawuxelelwa.', 'XHO22'),
(39, 1, 6, 'Ngokuba, yabonani, ndixhoxha amaKaledi, laa madoda anobujorha, azizigantsontso;', 'XHO22'),
(39, 1, 7, 'Ziyoyikeka, ziyoyikeka, ziphuma kubo isigwebo nesidima sabo.', 'XHO22'),
(39, 1, 8, 'Amahashe alo anamendu ngaphezu kwamangwe, abukhali ngaphezu kweengcuka zasebusuku; baya kuphaphazela njengokhozi oludlayo.', 'XHO22'),
(39, 1, 9, 'Bonke bephela baya kuza benogonyamelo; ubuso babo buya kwanya njengomoya wasempumalanga, bababuthe abathinjwa njengentlabathi.', 'XHO22'),
(39, 1, 10, 'Baya kugculela ookumkani, nezidwangube zibe yintlekisa kubo; ziya kuzigculela zonke iinqaba; ngokuba baya kubutha uthuli, baluthabathe.', 'XHO22'),
(39, 1, 11, 'Uya kwandula ke atshintshe imbono yakhe, adlule aze acatshukiswe kukuvakalisa amandla akhe kuthixo wakhe.', 'XHO22'),
(39, 1, 12, 'Akuphumi na ukusa kwixesha elingenammiselo, Yehova Thixo wam, Lowo Ungcwele wam? asiyi kufa. uYehova wabagweba; wena Thixo oligorha, ubaqinisele ukuba ubakhalimele.', 'XHO22'),
(39, 1, 13, 'Unamehlo ahlambulukileyo, ukuba angayikhangeli into embi, ukuba ungaboni okubi; Olibonayo inginingini, uthi cwaka, ongendawo akamdli na umntu owadlulayo kuye?', 'XHO22'),
(39, 1, 14, 'Ubenza abantu babe njengeentlanzi zolwandle na, njengezinambuzane ezingenamlawuli?', 'XHO22'),
(39, 1, 15, 'Benyuka bonke bephela, babanyusa endlwini, babambisa ngeminatha yabo, bahlanganisana ngomnatha; ngoko ke, bonwabile, bonwabile.', 'XHO22'),
(39, 1, 16, 'Ngenxa yoko bayazibingelela, ushushu umsi wamantshontsho abo; kuba isabelo sabo sityebile, nokutya kwabo kuninzi.', 'XHO22'),
(39, 1, 17, 'Ngaba ngoku baya kuphosa iminatha yabo baze bacebe ukubulala iintlanga?', 'XHO22'),
(39, 2, 1, 'Mna ndiya kuma emboniselweni yam, ndihlale phezu kwenqaba, ndibonisele, ukuba ndiyibone into aya kundixelela yona, nento endiya kuyijika ngenxa yesohlwayo sam.', 'XHO22'),
(39, 2, 2, 'Wandiphendula uYehova, wathi, Bhala lo mbono, uwukrole emacwecweni, ukuze owulesayo abaleke.', 'XHO22'),
(39, 2, 3, 'Kuba umbono usaya kuba ngowexesha elimisiweyo, ukhawulezela ekupheleni, angathethi wona amanga; ukuba uzilazisile, ulinde yena; ngokuba iya kuza, ayiyi kuphela.', 'XHO22'),
(39, 2, 4, 'Yabona, umoya wakhe ophakemeyo awulungisi kuye; ke lona ilungisa liya kudla ubomi ngokholo lwalo.', 'XHO22'),
(39, 2, 5, 'Kananjalo, emke newayini, uyindoda enekratshi, engahlali endlwini; owandisa umnqweno wakhe njengofileyo, enjengokufa, angahluthi; ke zonke iintlanga ziyahlutha bahlanganiselwa kuye, bahlanganisa bonke izizwe kuye.', 'XHO22'),
(39, 2, 6, 'Aziyi kumhlazisa na zonke ziphela, zimngcikive, zisithi, Uha, lowo wandisa okungekwakhe! ixesha elingakanani? nokumthwalela udongwe olungqingqwa!', 'XHO22'),
(39, 2, 7, 'Abanilumayo abayi kuvuka kwaoko na, baze basuke bangesuki abakuqumbisayo, ube lixhoba labo?', 'XHO22'),
(39, 2, 8, 'Ekubeni wena ubhunyule iintlanga ezininzi, aya kukuphanga onke amasalela eentlanga; ngenxa yamagazi oluntu, nangenxa yogonyamelo olwenzekayo ehlabathini, phakathi komzi, nabemi bawo bonke.', 'XHO22'),
(39, 2, 9, 'Yilahleko ke yalowo uyinyamekelayo indlu yakhe, ukuba akhe indlu yakhe phezulu, akhululeke ebubini!', 'XHO22'),
(39, 2, 10, 'Uyixabele indlu yakho, ngokutshabalalisa iintlanga ezininzi; wonile kumphefumlo wakho.', 'XHO22'),
(39, 2, 11, 'Kuba ilitye eliseludongeni liya kukhala, umthi usabele.', 'XHO22'),
(39, 2, 12, 'Yeha, owakha umzi ngamagazi, owuqinisele umzi ngobungendawo!', 'XHO22'),
(39, 2, 13, 'Yabona, akuphumi kuYehova wemikhosi na ukuthi abantu basebenze umlilo, babulaleke ilize?', 'XHO22'),
(39, 2, 14, 'Kuba ihlabathi liya kuzala kukwazi uzuko lukaYehova, njengamanzi egubungele ulwandle.', 'XHO22'),
(39, 2, 15, 'Yeha, lowo useza ummelwane wakhe, lowo umnika indebe yakho, umnxilisa, ukuze ubone amampunge abo!', 'XHO22'),
(39, 2, 16, 'Uzele luhlazo kuzuko; sela nawe, unxile; indebe yesandla sokunene sikaYehova iya kubuyela kuwe, nozuko lwakho luya kutshicelwa lihlazo.', 'XHO22'),
(39, 2, 17, 'Kuba ugonyamelo lwaseLebhanon luya kukugubungela nokuphangwa kwezilwanyana eziyingcangcazelisayo, ngenxa yamagazi oluntu nangenxa yogonyamelo olwenziwe emhlabeni, isixeko nakubo bonke abemi baso.', 'XHO22'),
(39, 2, 18, 'Unceda ntoni na umfanekiso oqingqiweyo, ukuba uthe wawunqumla umdali wawo? Umfanekiso otyhidiweyo, nomyali wobuxoki, omdali wento engenakuthetha?', 'XHO22'),
(39, 2, 19, 'Yeha lowo uthi kwityholo: “Vuka! kwilitye elithe cwaka: Vuka, uya kufunda! Uyabona, wambeswe yigolide nesilivere; akukho moya kuwe;', 'XHO22'),
(39, 2, 20, 'uYehova ke yena usetempileni yakhe engcwele; lithe cwaka phambi kwakhe ihlabathi lonke.', 'XHO22'),
(39, 3, 1, 'Umthandazo kaHabhakuki umprofeti ngoShigiyon.', 'XHO22'),
(39, 3, 2, 'Yehova, ndilivile ilizwi lakho, ndasuka ndoyika; Yehova, mayiphile imisebenzi yakho phakathi kweminyaka, Yazise phakathi kweminyaka; Unomsindo, khumbula inceba.', 'XHO22'),
(39, 3, 3, 'uThixo weza evela kwaTeman, Lowo Ungcwele evela kwiNtaba yaseParan. Sela. Ubuqaqawuli bakhe bagubungela amazulu, yaye umhlaba wazala yindumiso yakhe.', 'XHO22'),
(39, 3, 4, 'Ubuqaqawuli bakhe bunjengokukhanya; kwaphuma iimpondo esandleni sakhe, yaye amandla akhe efihliwe.', 'XHO22'),
(39, 3, 5, 'Yandulela indyikitya yokufa, yahamba iinyawo zakhe ngamalahle avuthayo.', 'XHO22'),
(39, 3, 6, 'Usukile walilinganisa ihlabathi; zathi saa iintaba ezingunaphakade, zasibekeka iinduli ezingunaphakade; iindlela zakhe zingunaphakade.', 'XHO22'),
(39, 3, 7, 'Ndazibona iintente zakwaKushan zisenkathazweni, neempahla zelizwe lakwaMidiyan zididizela.', 'XHO22'),
(39, 3, 8, 'Ngaba uYehova waba nomsindo nxamnye nemilambo? Ngaba umsindo wakho wawuphezu kwemilambo? Ukujamela kwakho ulwandle na, xa ukhwele amahashe akho, zikholose iinqwelo zakho zokulwa?', 'XHO22'),
(39, 3, 9, 'Sihlulwe saphele isaphetha sakho; ngokwesifungo sezizukulwana ngezizukulwana, nelizwi lakho. Ucanda imilambo ihlabathi;', 'XHO22'),
(39, 3, 10, 'Iintaba zakubona, zanyikima; Isikhukula saphakamisa izwi lakhe, waphakamisa izandla zakhe.', 'XHO22'),
(39, 3, 11, 'Ilanga nenyanga zema endaweni yazo, ngokukhanya kweentolo zakho zadlula ngokukhanya komkhonto wakho.', 'XHO22'),
(39, 3, 12, 'Ihlabathi ulicanda ngomsindo, ubulala iintlanga ngomsindo.', 'XHO22'),
(39, 3, 13, 'Uphume ukubasindisa abantu bakho, ewe, ukubasindisa ngomthanjiswa wakho; Uyaphule intloko endlwini yongendawo, Usihlube isiseko sakhe, use entanyeni. Sela.', 'XHO22'),
(39, 3, 14, 'Ubaxabele abathetheli bemizi yakhe ngeentonga zakho; baphuma njengesaqhwithi, beza kundigubungela; ukonwaba kwabo kwaba njengokuwadla ngasese amahlwempu.', 'XHO22'),
(39, 3, 15, 'Wakhwela amahashe akho elwandle, emanzini amaninzi;', 'XHO22'),
(39, 3, 16, 'Ndathi ndakukuva oko, saxuxuzela isisu sam; Wabengezela umlomo wam ngelizwi elo; Kwangena isihogo emathanjeni am, ndangcangcazela phakathi kwam, Ukuze ndiphumle ngemini yokusindeka;', 'XHO22'),
(39, 3, 17, 'Nokuba umkhiwane awusayi kutyatyamba, nemidiliya ingavelisi siqhamo; umsebenzi womnquma uya kupheliswa, namasimi angayi kuvelisa kudla; izilwanyana ezincinci kufuneka zitshatyalaliswe kusasa, kwaye kungabikho iinkomo kusasa;', 'XHO22'),
(39, 3, 18, 'Ke mna, ndiya kudlamka ndikuYehova, Ndigcobe ndikuThixo umsindisi wam.', 'XHO22'),
(39, 3, 19, 'uYehova ungamandla am, uzenza iinyawo zam, zibe njengezamaxhama, undikhaphela endaweni ephakamileyo kum. Kumongameli wokubethelwa izinto ezineentambo.', 'XHO22'),
(40, 1, 1, 'Ilizwi likaYehova elafika kuZefaniya unyana kaKushi, unyana kaGedaliya, unyana ka-Amariya, unyana kaHezekiya, ngemihla kaYosiya unyana ka-Amon, ukumkani wakwaYuda.', 'XHO22'),
(40, 1, 2, 'Ndiza kuqalekisa yonk’ into ephuma emhlabeni,’ utsho uYehova.', 'XHO22'),
(40, 1, 3, 'Ndiya kutshabalalisa umntu nenkomo; Ndiya kuzitshabalalisa iintaka zezulu, neentlanzi zolwandle, nezikhubekiso zabangendawo; yaye ndiza kumnqumla umntu emhlabeni,’ utsho uYehova.', 'XHO22'),
(40, 1, 4, 'Isandla sam ndiya kusolula phezu koYuda, naphezu kwabemi bonke baseYerusalem; ndiwatshabalalise amasalela kaBhahali kule ndawo, negama leMarim ndawonye nababingeleli;', 'XHO22'),
(40, 1, 5, 'Nabo banqula imizimba yasezulwini phezu kophahla; nabanqulayo, bafunga uYehova, bafunga uMalkame;', 'XHO22'),
(40, 1, 6, 'nabatyekayo kuYehova; nabangamfunanga uYehova, abangambuzanga uYehova.', 'XHO22'),
(40, 1, 7, 'Yithini cwaka phambi kweNkosi enguMongami uYehova, ngenxa yokuba ikufuphi imini kaYehova; ngokuba uYehova uwulungisile umbingelelo, ubarhole abamenyiweyo bakhe.', 'XHO22'),
(40, 1, 8, 'Kuya kuthi ke ngomhla wombingelelo kaYehova, ndibakhalimele abathetheli, noonyana bokumkani, nabo bonke abanxiba ngubo zimbi.', 'XHO22'),
(40, 1, 9, 'Kwangaloo mini ndiya kubohlwaya bonke abagqobhoza iingcango, abazalise izindlu zabaninizo ngogonyamelo nenkohliso.', 'XHO22'),
(40, 1, 10, 'Ngaloo mini,’ utsho uYehova, kuya kubakho isandi sokukhwaza sivela kwiSango Leentlanzi, isandi sokukhwaza sivela kowesibini, nesandi esikhulu esiphuma ezintabeni.', 'XHO22'),
(40, 1, 11, 'Lilani, bemi baseMakate, ngokuba babhuqiwe bonke abarhwebi; bonke abanxiba isilivere baya kutshabalala.', 'XHO22'),
(40, 1, 12, 'Ngelo xesha ndiya kuyigocagoca iYerusalem ngezibane ndize ndibohlwaye abo bahleli ezingqengqelweni zabo, abathi ezintliziyweni zabo: ‘uYehova akenzi okulungileyo okanye okubi.', 'XHO22'),
(40, 1, 13, 'Ngako oko ilifa labo liya kuphangwa, nezindlu zabo zibe ngamanxuwa; baya kwakha izindlu, bangahlali kuzo; baya kutyala izidiliya, kodwa abayi kuyisela iwayini yazo.', 'XHO22'),
(40, 1, 14, 'Isondele imini enkulu yeNkosi, ikufuphi, iyakhawuleza kakhulu, ilizwi lemini yeNkosi; igorha liya kubhomboloza ngokukrakra apho.', 'XHO22'),
(40, 1, 15, 'Loo mini yimini yomsindo, yimini yesibetho nesihelegu, yimini yentshabalalo nentshabalalo, yimini yobumnyama nesithokothoko, yimini yamafu nesithokothoko.', 'XHO22'),
(40, 1, 16, 'Imini yesigodlo nokuhlaba umkhosi nxamnye nezixeko ezinqatyisiweyo nakwiinqaba eziphakamileyo.', 'XHO22'),
(40, 1, 17, 'Ndiya kubazisela ukubandezeleka abantu ukuze bahambe njengeemfama, ngenxa yokuba bonile kuYehova; igazi labo liya kuphalazwa njengothuli, nenyama yabo njengomthi.', 'XHO22'),
(40, 1, 18, 'Isilivere yabo negolide yabo ayiyi kuba nako ukubasindisa ngemini yokuphuphuma komsindo kaYehova; nehlabathi liphela liya kutshiswa ngumlilo wekhwele lakhe;', 'XHO22'),
(40, 2, 1, 'Hlanganisani, ewe, hlanganisani, nina bantu bangathandekiyo;', 'XHO22'),
(40, 2, 2, 'Ngaphambi kokuba uwiswe umyalelo, ngaphambi kokuba loo mini idlule njengomququ, ngaphambi kokuba umsindo kaYehova unifikele, ngaphambi kokuba imini yomsindo kaYehova inifikele.', 'XHO22'),
(40, 2, 3, 'Funani uYehova, nonke balulamileyo belizwe, okwenzileyo okusesikweni kwakhe; funani ubulungisa, funani ululamo; mhlawumbi uya kungcwatywa ngemini yomsindo kaYehova.', 'XHO22'),
(40, 2, 4, 'Kuba iGaza iya kuba sisishiyi, neAshkelon ibe yintlango; baya kuyigxotha iAshdode emini emaqanda, inyothulwe iEkron.', 'XHO22'),
(40, 2, 5, 'Yeha, abemi baselunxwemeni, bemi baseKrete! Ilizwi likaYehova lichasene nani; iKanan, ilizwe lamaFilisti, ndiya kukutshabalalisa, kungabikho mmi.', 'XHO22'),
(40, 2, 6, 'Unxweme lolwandle luya kuba ngamakriwa, neminquba yabalusi, nezibaya zeenkomo;', 'XHO22'),
(40, 2, 7, 'Yaye ilizwe liya kuba lelamasalela endlu yakwaYuda; aya kudla kuyo; baya kulala ezindlwini zaseAshkelon; ngokuba uya kuwavelela uYehova uThixo wawo, awabuyise ekuthinjweni kwawo.', 'XHO22'),
(40, 2, 8, 'Ndikuvile ukungcikiva kukaMowabhi, ukungcikiva koonyana baka-Amon, ababangcikive ngako abantu bam, baziphakamisa phezu kwelizwe labo.', 'XHO22'),
(40, 2, 9, 'Ngako oko, ndihleli nje, utsho uYehova wemikhosi, uThixo kaSirayeli, ukuthi, uMowabhi uya kuba njengeSodom, noonyana baka-Amon babe njengeGomora, nemihadi yetyuwa, nenkangala ephanzileyo, kude kuse ephakadeni; amasalela abantu bam ziya kuzihlutha, namasalela abantu bam azihluthe.', 'XHO22'),
(40, 2, 10, 'Lilo elo ikratshi labo, ngenxa yokuba baye babadelela baza baziphakamisa abantu bakaYehova wemikhosi.', 'XHO22'),
(40, 2, 11, 'uYehova uya kunkwantya kubo, kuba uya kubabulala ngephango bonke oothixo behlabathi; yaye abantu baya kumnqula elowo endaweni yakhe, kuzo zonke iziqithi zeentlanga.', 'XHO22'),
(40, 2, 12, 'Nina, maKushi, niya kubulawa ngekrele lam;', 'XHO22'),
(40, 2, 13, 'Uya kusolula isandla sakhe ngasezantsi, ayitshabalalise iAsiriya; woyijika iNineve ibe yintlango, umqwebedu onjengentlango.', 'XHO22'),
(40, 2, 14, 'Kuya kuhlala phakathi kwabo iintaka, amarhamncwa onke eentlanga; Kuhlala iintaka nokhwalimanzi ezindaweni zabo eziphakamileyo; ilizwi labo liya kumemelela ezifestileni; ukonakala kuya kuba sezingcangweni, ngenxa yokuba kuya kutyhilwa umsebenzi wemisedare.', 'XHO22'),
(40, 2, 15, 'Nguwo lowo umzi onoyolo, owahlala ngokuqhutywa yintliziyo, owathi entliziyweni yawo, Ndim kwaphela, akukho wumbi; Utheni na ukuba ube senkangala, ube sisikhundla samarhamncwa! bonke abadlula kuwo baya kuvuthela impempe, bewangawangisa ngesandla.', 'XHO22'),
(40, 3, 1, 'Yeha, umzi ongcolileyo, ongcolileyo, oxinzelela phantsi!', 'XHO22'),
(40, 3, 2, 'Akaliphulaphulanga lizwi; akavuma ukukhalinyelwa; akamthembanga uYehova; akasondela kuThixo wakhe.', 'XHO22'),
(40, 3, 3, 'Abathetheli balo kulo baziingonyama ezibharhulayo; abagwebi bawo baziingcuka zasebusuku; abawaphuli amathambo kude kuse.', 'XHO22'),
(40, 3, 4, 'Abaprofeti bayo bangamatshijolo, ngabantu abanganyanisekanga;', 'XHO22'),
(40, 3, 5, 'uYehova ulilungisa uphakathi kwawo; akenzi bubi; Imiso ngemiso uphuma phandle, akasileli; Ke yena ongendawo akanazintloni.', 'XHO22'),
(40, 3, 6, 'Ndazitshabalalisa iintlanga; zibhuqiwe iinqaba zabo; ndiziphanzisile iindlela zabo, ukuba kungadluli mntu;', 'XHO22'),
(40, 3, 7, 'Ndathi, Okunene niya kundoyika, nilwamkele uqeqesho; ukuze lingatshabalali ikhaya labo, ndabaxabela ke; bavuka kusasa, bayonakalisa yonke imisebenzi yabo.', 'XHO22'),
(40, 3, 8, 'Ngako oko yilindeleni kum, utsho uYehova, ide ifike imini endiya kuvuka ngayo, ndeyise; Ngokuba injongo yam kukuhlanganisa iintlanga, ndibabuthe ookumkani, ukuze ndiwuthulule phezu kwabo umsindo wam, ewe, konke ukuvutha komsindo wam ngokuba ihlabathi lonke liya kutshiswa ngumlilo wekhwele lam.', 'XHO22'),
(40, 3, 9, 'Kuba ngelo xesha ndiya kuzinika iintlanga intetho enyulu, ukuze zonke zibize igama likaYehova, ukuze zimkhonze ngamxhelo mnye.', 'XHO22'),
(40, 3, 10, 'Bethabathela phesheya kwemilambo yakwaKushi, abathandaza kum, intombi yabantu bam abasasazekileyo, baya kubingelela kum.', 'XHO22'),
(40, 3, 11, 'Ngaloo mini akuyi kudana ngenxa yezenzo zakho zonke, ondiqumbise ngazo; kuba ngelo xesha ndiya kubashenxisa kuni abo banandipha ikratshi lenu, yaye anisayi kuphinda niqhayise ngentaba yam engcwele.', 'XHO22'),
(40, 3, 12, 'Ndiza kubashiya abadakumbileyo nabangamahlwempu phakathi kwenu, yaye baya kukholosa egameni likaYehova.', 'XHO22'),
(40, 3, 13, 'Amasalela akwaSirayeli akayi kwenza bubi, akayi kuxoka; nolwimi lwenkohliso aluyi kufunyanwa emlonyeni wabo; ngokuba baya kudla, bazole, kungabikho ubothusayo.', 'XHO22'),
(40, 3, 14, 'Memelela, ntombi inguZiyon; vuya, Sirayeli; vuya, ugcobe ngentliziyo yakho yonke, ntombi inguYerusalem;', 'XHO22'),
(40, 3, 15, 'uYehova ukususile ukugwetywa kwakho, uzidedisile iintshaba zakho; ukumkani wakwaSirayeli, uYehova, uphakathi kwenu; aniyi kuphinda nibone ububi.', 'XHO22'),
(40, 3, 16, 'Ngaloo mini kuya kuthiwa kwiYerusalem, Musa ukoyika, wena Ziyon: mazingawi izandla zakho.', 'XHO22'),
(40, 3, 17, 'uYehova uThixo wakho uphakathi kwakho; uya kugcoba ngawe, evuya; uya kuphumla eluthandweni lwakhe, akunandiphe ngengoma.', 'XHO22'),
(40, 3, 18, 'Ndiya kubahlanganisa abalidayo emthendelekweni, nabaphuma phakathi kwenu, abaluxanduva kubo ngokudelela.', 'XHO22'),
(40, 3, 19, 'Yabona, ngelo xesha ndiya kuziphelisa zonke izinto ezinitshutshisayo, nabo bema ngxi, ndiya kubasindisa, ndibahlanganise abathinjiweyo; yaye ndiya kufumana indumiso nodumo lwabo kuwo onke amazwe abahlazeke kuwo.', 'XHO22'),
(40, 3, 20, 'Ngelo xesha ndiya kunizisa, ndinibuthe ngexesha elililo; kuba ndiya kunenza nibe negama nendumiso phakathi kweentlanga zonke zomhlaba ekubuyiseleni kwam ukuthinjwa kwenu phambi kwamehlo enu,” utsho uYehova.', 'XHO22'),
(41, 1, 1, 'Ngomnyaka wesibini wobukumkani bukaDariyo, ngenyanga yesithandathu, ngolokuqala enyangeni leyo, kwafika ilizwi likaYehova kuZerubhabheli, ibamba lakwaYuda, unyana kaShalatiyeli, ngoHagayi umprofeti, lisithi, kuYoshuwa, ukumkani wakwaYuda unyana kaYotsadeki, umbingeleli omkhulu, uthi:', 'XHO22'),
(41, 1, 2, 'Utsho uYehova wemikhosi ukuthi, Aba bantu bathi, Ixesha alikabikho, ixesha lokwakhiwa kwendlu kaYehova alikabikho.', 'XHO22'),
(41, 1, 3, 'Kwaza kwafika ilizwi likaYehova ngomprofeti uHagayi, lisithi:', 'XHO22'),
(41, 1, 4, 'Ngaba lixesha lenu na, nina, lokuba nihlale ezindlwini zenu ezinqatyisiweyo, yaye le ndlu sele iyinkangala?', 'XHO22'),
(41, 1, 5, 'Ke kaloku, utsho uYehova wemikhosi ukuthi, Zigqaleni iindlela zenu.', 'XHO22'),
(41, 1, 6, 'Nidle kakhulu, navula kancinane; niyadla, ningahluthi; niyasela, anganeli sisiselo; niyanxiba, kodwa akufudumezi; yaye lowo wamkela intlawulo uya kubaphosela etyaleni elinemingxuma.', 'XHO22'),
(41, 1, 7, 'Utsho uYehova wemikhosi ukuthi, Zigqaleni iindlela zenu.', 'XHO22'),
(41, 1, 8, 'Nyukani niye entabeni, nize nemithi, nakhe indlu; kwaye ndiya kugcoba ngayo, kwaye ndiya kundizukisa, utsho uYehova.', 'XHO22'),
(41, 1, 9, 'Ulinde ixesha elide, nantso i nto incinane; nithe nakuza nayo ekhaya, ndayivuthela. Ngoba? utsho uYehova wemikhosi. Ngenxa yebhodlo lendlu yam, nibalekela endlwini yenu nonke.', 'XHO22'),
(41, 1, 10, 'Ngenxa yoko, isibhakabhaka saba ngumbethe ngenxa yawo, nomhlaba wavingca iziqhamo zawo.', 'XHO22'),
(41, 1, 11, 'Ndabiza imbalela phezu komhlaba, naphezu kweentaba, naphezu kwengqolowa, naphezu kothuli, naphezu kweoli, naphezu kwemveliso yomhlaba, naphezu kwabantu, naphezu kweenkomo, naphezu komsebenzi wonke wezandla.', 'XHO22'),
(41, 1, 12, 'Baliphulaphula ke ooZerubhabheli unyana kaShalatiyeli, noYoshuwa unyana kaYotsadaki, umbingeleli omkhulu, namasalela onke abantu, ilizwi likaYehova uThixo wabo, namazwi kaHagayi umprofeti, njengoko wakuthethayo uYehova uThixo wabo wamthuma; basaba abantu phambi koYehova.', 'XHO22'),
(41, 1, 13, 'Wathetha uHagayi umthunywa kaYehova ebantwini ngelizwi likaYehova, wathi, Ndinani, utsho uYehova.', 'XHO22'),
(41, 1, 14, 'uYehova wawuxhokonxa umoya kaZerubhabheli unyana kaShalatiyeli, irhuluneli yakwaYuda, nomoya kaYoshuwa unyana kaYotsadeki, umbingeleli omkhulu, nomoya wayo yonke intsalela yohlanga; beza basebenza endlwini kaYehova wemikhosi, uThixo wabo;', 'XHO22'),
(41, 1, 15, 'ngosuku lwamashumi omabini anesine lwenyanga yesithandathu, ngomnyaka wesibini kaDariyo ukumkani.', 'XHO22'),
(41, 2, 1, 'Ngenyanga yesixhenxe, ngosuku lwamashumi omabini analunye enyangeni leyo, kwafika ilizwi likaYehova ngoHagayi umprofeti, lisithi,', 'XHO22'),
(41, 2, 2, 'Yithi kuZerubhabheli unyana kaShalatiyeli, ibamba lakwaYuda, nakuYoshuwa unyana kaYotsadaki, umbingeleli omkhulu, nakwabanye abantu, uthi,', 'XHO22'),
(41, 2, 3, 'Ngubani na kuni apha oseleyo, owayibonayo le ndlu isebuqaqawulini bayo bokuqala? Ngoku ubona njani? Ngaba ithelekiswa nayo emehlweni akho?', 'XHO22'),
(41, 2, 4, 'Kaloku ke yomelela, Zerubhabheli, utsho uYehova; yomelela, Yoshuwa nyana kaYosdeki, mbingeleli mkhulu; nomelele, nonke zintlanga zehlabathi, utsho uYehova, nisebenze; ngokuba ndinani, utsho uYehova wemikhosi.', 'XHO22'),
(41, 2, 5, 'Ngokwelizwi elo, endakwenzayo kuni ekuphumeni kwenu eYiputa, ndisaligcinile, noMoya wam uphakathi kwenu; sukoyika.', 'XHO22'),
(41, 2, 6, 'Ngokuba utsho uYehova wemikhosi ukuthi, Kungekudala, umzuzwana ke, ndinyikimise izulu, nehlabathi, nolwandle, nomhlaba owomileyo;', 'XHO22'),
(41, 2, 7, 'ndizinyikimise zonke iintlanga, zifike izinto ezinqwenelekayo zeentlanga zonke; yaye ndiya kuyizalisa ngozuko le ndlu,’ utsho uYehova wemikhosi.', 'XHO22'),
(41, 2, 8, 'Isilivere yeyam, igolide yeyam; utsho uYehova wemikhosi.', 'XHO22'),
(41, 2, 9, 'Ubuqaqawuli bale ndlu yokugqibela buya kuba ngaphezu kobokuqala, utsho uYehova wemikhosi; kule ndawo ndiya kunika uxolo; utsho uYehova wemikhosi.', 'XHO22'),
(41, 2, 10, 'Ngomhla wamashumi amabini anesine wenyanga yesithoba apho kuphela, ngomnyaka wesibini wobukumkani bukaDariyo, kwafika ilizwi likaYehova ngoHagayi umprofeti, lisithi:', 'XHO22'),
(41, 2, 11, 'Utsho uYehova wemikhosi ukuthi, Khanibuze ngomyalelo kubabingeleli, uthi,', 'XHO22'),
(41, 2, 12, 'Ukuba ubani uthe wathabatha inyama engcwele ngomphetho wengubo yakhe, waza ngenxeba lakhe wachukumisa isonka okanye into eselwayo okanye iwayini okanye ioli okanye nayiphi na enye inyama, yoba ngcwele na? Baphendula ababingeleli bathi, Hayi.', 'XHO22'),
(41, 2, 13, 'Wathi ke uHagayi, Ukuba uthe wafika oyinqambi ngenxa yomfi, kwezo zinto, zoba ziinqambi na? Baphendula ababingeleli, bathi, Yonziwa inqambi.', 'XHO22'),
(41, 2, 14, 'Waphendula uHagayi, wathi, Kunjalo kolu hlanga, lube nje olu hlanga phambi kwam, utsho uYehova; kube njalo ke kuwo wonke umsebenzi wezandla zabo; nento abayibingelelayo khona ayihlambulukile.', 'XHO22'),
(41, 2, 15, 'Ngoku ke gqaleni, kuthabathele kule mini kuse phambili, kungekabekwa litye phezu kwelitye etempileni kaYehova.', 'XHO22'),
(41, 2, 16, 'Kususela ngaloo mini, xa umntu ebefike esitheni esimilinganiso imashumi mabini, asuke afumane libe linye ishumi; xa umntu weza kwisixovulelo seoli ukuza kukhupha iiemele ezingamashumi amahlanu kwisixovulelo, zazingamashumi amabini kuphela.', 'XHO22'),
(41, 2, 17, 'Ndanibetha ngombethe, nangombethe, nangesichotho, kuyo yonke imisebenzi yezandla zenu; noko anibuyelanga kum; utsho uYehova.', 'XHO22'),
(41, 2, 18, 'Ke kaloku, nikelani ingqalelo ukususela kulo mhla kuse phambili, ukususela kusuku lwamashumi amabini anesine lwenyanga yesithoba, ewe, ukususela kumhla esabekwa ngawo isiseko setempile kaYehova, nikelani ingqalelo kulo mbandela.', 'XHO22'),
(41, 2, 19, 'Isekho na imbewu eqongeni? ewe, umdiliya, nomkhiwane, nomrharnate, nomnquma, awakavelisi; Ukususela namhlanje ndiya kuthamsanqelisa.', 'XHO22'),
(41, 2, 20, 'Kwafika ilizwi likaYehova kuHagayi ngomhla wamashumi amabini anesine enyangeni leyo, lisithi,', 'XHO22'),
(41, 2, 21, 'Yitsho kuZerubhabheli, ibamba lakwaYuda, ukuthi, Ndiya kulinyikimisa izulu nehlabathi;', 'XHO22'),
(41, 2, 22, 'Ndiya kuyibhukuqa itrone yezikumkani, ndiwatshabalalise amandla oorhulumente beentlanga; ndiya kuzibhukuqa iinqwelo zokulwa nabakhweli bazo; amahashe nabakhweli bawo baya kuwa, ngamnye ngekrele lomntakwabo.', 'XHO22'),
(41, 2, 23, 'Ngaloo mini, utsho uYehova wemikhosi, ndiya kukuthabatha, Zerubhabheli nyana kaSalatiyeli, mkhonzi wam, utsho uYehova, ndikwenze umsesane wokutywina; ngokuba ndikunyule; utsho uYehova wemikhosi.', 'XHO22'),
(42, 1, 1, 'Ngenyanga yesithoba neyesibini, ngomnyaka wesibini kaDariyo, kwafika ilizwi likaYehova kuZekariya unyana kaBherekiya, unyana kaIdo umprofeti, lisithi,', 'XHO22'),
(42, 1, 2, 'uYehova waba nomsindo kunene kooyihlo.', 'XHO22'),
(42, 1, 3, 'Ngako oko uthi kubo, Utsho uYehova wemikhosi ukuthi, “Buyelani kum, utsho uNdikhoyo onamandla onke; nam ndobuyela kuni,” utsho uNdikhoyo onamandla onke.', 'XHO22'),
(42, 1, 4, 'Musani ukuba njengooyihlo, ababebizwayo ngabaprofeti bokuqala, besithi, Utsho uYehova wemikhosi; Guqukani ezindleleni zenu ezimbi nakwizenzo zenu ezibi, kodwa abazange babaphulaphule, yaye abazange bandiphulaphule,’ utsho uYehova.', 'XHO22'),
(42, 1, 5, 'Baphi ooyihlo? nabaprofeti baya kudla ubomi ngonaphakade na?', 'XHO22'),
(42, 1, 6, 'Kodwa amazwi am, nemiyalelo yam, endayimisela abakhonzi bam, abaprofeti, ayibafumananga na ooyihlo? Abuya ke athi, Njengoko uYehova wemikhosi ukwenzileyo kuthi ngokuhamba kwethuba, nangemisebenzi yethu, wenzé kuthi thina.', 'XHO22'),
(42, 1, 7, 'Ngomhla wamashumi amabini anesine wenyanga yeshumi elinanye, oko kukuthi, inyanga enguSebhati, ngomnyaka wesibini kaDariyo, kwafika ilizwi likaYehova kuZekariya unyana kaBheregiya, unyana kaIdo umprofeti, lisithi,', 'XHO22'),
(42, 1, 8, 'Ndakhangela ebusuku, ndabona indoda ekhwele ihashe elibomvu, emi phakathi kwemimirtile esemhlabeni; ngasemva kwakhe ingamahashe abomvu, amfusa, namhlophe.', 'XHO22'),
(42, 1, 9, 'Ndathi ke, Camagu, nkosi yam! Sathi kum isithunywa esibe sithetha nam, Ndokubonisa ukuba yintoni na.', 'XHO22'),
(42, 1, 10, 'Waphendula umntu obemi phakathi kwemimirtile wathi, Aba ngoonyana abathunywe nguYehova ukuba bahambe ehlabathini.', 'XHO22'),
(42, 1, 11, 'Basiphendula isithunywa sikaYehova esimi phakathi kwemimirtile, bathi, Silihambahambe ihlabathi, nalo lonke elimiweyo lizolile.', 'XHO22'),
(42, 1, 12, 'Saphendula ke isithunywa sikaYehova, sathi, Yehova wemikhosi, koda kube nini na unesihe, ngenxa yeYerusalem nemizi yakwaYuda, owayibhavumayo le minyaka imashumi mathandathu?', 'XHO22'),
(42, 1, 13, 'uYehova wayiphendula ingelosi eyayithetha nam ngamazwi alungileyo nathuthuzelayo.', 'XHO22'),
(42, 1, 14, 'Sathi kum isithunywa esibe sithetha nam, Biza, uthi, Itsho iNkosi wemikhosi ukuthi, Itsho iNkosi wemikhosi; Ndinekhwele ngeYerusalem neZiyon, ikhwele elikhulu.', 'XHO22'),
(42, 1, 15, 'Andanelisekanga ncam ngabo baphumlayo beentlanga; ngenxa yokuba andikholiswanga kancinane, nabo ke bancedisa ekubandezelekeni kwabo.', 'XHO22'),
(42, 1, 16, 'Ngako oko, utsho uYehova ukuthi, Ndiyibuyele iYerusalem ngemfesane; Indlu yam iya kwakhelwa khona, utsho uYehova wemikhosi; intambo yokulinganisa iya kolulwa phezu kweYerusalem.', 'XHO22'),
(42, 1, 17, 'Yiya kwakhona uthi: Itsho iNkosi yeMasomomo; Izixeko zam ziya kwandiswa ngempumelelo; yaye uYehova uya kuqhubeka eyithuthuzela iZiyon aze aqhubeke ekhetha iYerusalem.', 'XHO22'),
(42, 1, 18, 'Ndawaphakamisa amehlo am, ndakhangela, nanzo iimpondo ezine.', 'XHO22'),
(42, 1, 19, 'Ndathi kwisithunywa esibe sithetha nam, Yintoni na? Saphendula sathi, Ezi ziimpondo ezichithachithe uYuda, uSirayeli, neYerusalem.', 'XHO22'),
(42, 1, 20, 'Yaza iNkosi yandibonisa abachweli abane.', 'XHO22'),
(42, 1, 21, 'Ndathi ke, Baya kwenza ntoni na aba bantu? Sathetha, sathi, Ezi ziimpondo ezachithachitha uYuda, akwaba sabakho uphakamisa intloko; yakwaYuda ukuba iyichithachithe.', 'XHO22'),
(42, 2, 1, 'Ndaphinda ndawaphakamisa amehlo am, ndakhangela, nanko umntu ephethe ngesandla ulutya lokulinganisa.', 'XHO22'),
(42, 2, 2, 'Ndathi ke, Uya ngaphi na? Wathi kum, Makhe ndiyilinganise iYerusalem, ndibone ububanzi bayo nobude bayo.', 'XHO22'),
(42, 2, 3, 'Yabona, naso isithunywa esibe sithetha nam, siphuma;', 'XHO22'),
(42, 2, 4, 'Wathi kuyo, Gidima, uthethe nalaa ndodana, uthi, iYerusalem iya yakhiwe njengemizi engenakudonga, ngenxa yobuninzi babantu neenkomo zayo.', 'XHO22'),
(42, 2, 5, 'Kuba mna, utsho uYehova, ndiya kuba ludonga lomlilo macala onke nokukhanya ngaphakathi kuyo.', 'XHO22'),
(42, 2, 6, '“Khangela! kuba ndinichithachithe njengemimoya yomine yezulu,” utsho uYehova.', 'XHO22'),
(42, 2, 7, 'Zisindise, Ziyon, ohlala nentombi yaseBhabheli.', 'XHO22'),
(42, 2, 8, 'Kuba utsho uYehova wemikhosi ukuthi, Lo undithumile emva kozuko lweentlanga owazithimbayo; ngokuba ochukumisayo uchukumisa ukhozo lweliso lakhe.', 'XHO22'),
(42, 2, 9, 'Kuba, uyabona, ndiya kulingisa ngesandla sam kuzo, zibe lixhoba labakhonzi bazo; nazi ukuba uYehova wemikhosi undithumile.', 'XHO22'),
(42, 2, 10, 'Memelela, uvuye, ntombi inguZiyon; ngokuba ndiyeza, ndihlale phakathi kwakho; utsho uYehova.', 'XHO22'),
(42, 2, 11, 'Ngaloo mini iintlanga ezininzi ziya kuhlangana noYehova, zibe ngabantu bam; yaye ndiya kuhlala phakathi kwenu, nazi ukuba uYehova wemikhosi undithumile kuni.', 'XHO22'),
(42, 2, 12, 'Yaye uYehova uya kusidla ilifa isabelo sikaYuda emhlabeni ongcwele aze ayinyule kwakhona iYerusalem.', 'XHO22'),
(42, 2, 13, 'Izinto ezizitho zine, nenyama yonke, phambi koYehova, ngokuba unyukile yena engcweleni yakhe.', 'XHO22'),
(42, 3, 1, 'Wandibonisa uYoshuwa, umbingeleli omkhulu, emi phambi kwesithunywa sikaYehova, uSathana emi ngasekunene kwakhe, eza kulwa naye.', 'XHO22'),
(42, 3, 2, 'Wathi uYehova kuSathana, Wanga uYehova akukhalimele, Sathana! ewe, uYehova oyinyulileyo iYerusalem ngamana angayikhalimela. Iziko alicinywanga na emlilweni?', 'XHO22'),
(42, 3, 3, 'uYoshuwa wayenxibe iimpahla ezimdaka waza wema phambi kwengelosi.', 'XHO22'),
(42, 3, 4, 'Waphendula ke wathetha nabemiyo ngakuye, esithi, Mhlubeni iingubo ezimdaka. Wathi kuye, Uyabona, ndibususile ububi bakho kuwe, ndikwambese ingubo ende.', 'XHO22'),
(42, 3, 5, 'Ndathi ke, Mabambeke unkontsho oluhle entloko; Bamfaka unkontsho oluhle entloko, bamambesa; Sema isithunywa sikaYehova.', 'XHO22'),
(42, 3, 6, 'Ingelosi kaYehova yamkhalimela uYoshuwa, isithi:', 'XHO22'),
(42, 3, 7, 'Utsho uYehova wemikhosi ukuthi, Ukuba nithe nahamba ngeendlela zam, nayigcina imithetho yam, nayigweba indlu yam, nazigcina iintendelezo zam, ndoninika izihlalo, ninyathele kuzo.', 'XHO22'),
(42, 3, 8, 'Ke kaloku, khawuphulaphule, wena Yoshuwa, mbingeleli omkhulu, wena namaqabane akho ahleli phambi kwakho, ngenxa yokuba bangabantu abathe manga, ngenxa yokuba ndizisa umkhonzi wam, uLehlomela.', 'XHO22'),
(42, 3, 9, 'Ngokuba nalo ilitye endalibeka phambi koYoshuwa; lityeni linye kuya kubakho amehlo asixhenxe; yabona, ndiyawubhala umbhalo wakhe, utsho uYehova wemikhosi, ndibususe ubungendawo belo lizwe ngamini-nye.', 'XHO22'),
(42, 3, 10, 'Ngaloo mini, utsho uYehova wemikhosi, niya kubizela elowo ummelwane wakhe phantsi komdiliya naphantsi komkhiwane.', 'XHO22'),
(42, 4, 1, 'Saphinda seza isithunywa esibe sithetha nam, sandivusa njengomntu ovuswa ebuthongweni.', 'XHO22'),
(42, 4, 2, 'Wathi kum, Uyabona na? Ndathi, Ndabona, naso isiphatho sezibane segolide yonke, sinesitya phezu kwaso, sinezibane zaso ezisixhenxe phezu kwaso, nemibhobho yosixhenxe yezibane zaso phezu kwaso;', 'XHO22'),
(42, 4, 3, 'neminquma emibini ngakuso, omnye ungasekunene kwesitya, omnye ungasekhohlo.', 'XHO22'),
(42, 4, 4, 'Ndaphendula ndathi, Ndathetha kwisithunywa esibe sithetha nam, ndathi, Yintoni na, nkosi yam?', 'XHO22'),
(42, 4, 5, 'Saphendula isithunywa esibe sithetha nam, sathi kum, Akuyazi na ukuba yintoni na le? Ndathi: Hayi, nkosi yam!', 'XHO22'),
(42, 4, 6, 'Yaza yaphendula yathi kum: “Nali ilizwi likaYehova kuZerubhabheli, lisithi: “Akungabukroti okanye ngamandla, kodwa kungomoya wam,” utsho uYehova wemikhosi.', 'XHO22'),
(42, 4, 7, 'Ungubani na wena, ntaba inkulu? phambi koZerubhabheli uya kuba lithafa, yaye uya kulisusa ilitye lengcwaba lakhe edanduluka esithi, Tarhu, inceba kuwe!', 'XHO22'),
(42, 4, 8, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(42, 4, 9, 'Izandla zikaZerubhabheli zaseka le ndlu; nezandla zakhe ziya kuyigqiba; nazi ukuba uYehova wemikhosi undithumele kuni.', 'XHO22'),
(42, 4, 10, 'Ngokuba ngubani na owoyidela imini yezinto ezincinane? kuba baya kuba nemihlali basibone isitovu esandleni sikaZerubhabheli naba basixhenxe; ngamehlo kaYehova akhangela ehlabathini lonke.', 'XHO22'),
(42, 4, 11, 'Ndaphendula ndathi kubo, Yini na ukuba le minquma mibini ibekwe ngasekunene kwesiphatho sesibane ngasekhohlo?', 'XHO22'),
(42, 4, 12, 'Ndaphendula okwesibini, ndathi kuso, Yini na ukuba ezi mbaxa zimbini zeminquma ziphalaze ioli yegolide emivuzweni emibini yegolide?', 'XHO22'),
(42, 4, 13, 'Sandiphendula, sathi, Akuyazi na ukuba yintoni na? Ndathi: Hayi, nkosi yam!', 'XHO22'),
(42, 4, 14, 'Wathi ke, Aba babini ngabathanjiswa, bema neNkosi yehlabathi lonke.', 'XHO22'),
(42, 5, 1, 'Ndajika ke, ndawaphosa amehlo am, ndakhangela, nantso incwadi iphaphazela.', 'XHO22'),
(42, 5, 2, 'Wathi kum, Uyabona na? Ndathi, Ndibona incwadi esongwayo iphaphazela; baziikubhite ezimashumi mabini ubude bayo, neekubhite ezilishumi ububanzi bayo.', 'XHO22'),
(42, 5, 3, 'Yathi kum, Esi sisiqalekiso esigubungela ihlabathi lonke; ngokuba bonke abasukuba beba ngokukokwabo, baya kutshabalala ngaphaya; yaye othe wafunga uya kutshabalala njengakwelinye icala.', 'XHO22'),
(42, 5, 4, 'Ndiya kuyikhupha, utsho uYehova wemikhosi; iya kungena endlwini yesela, nasendlwini yalowo ufunge ubuxoki egameni lam; Yohlala endlwini yayo, iyitshise ndawonye nemithi yayo namatye ayo.', 'XHO22'),
(42, 5, 5, 'Ingelosi eyayithetha nam yaphuma yaza yathi kum: “Phakamisa amehlo akho uze ubone oko kuphumayo.', 'XHO22'),
(42, 5, 6, 'Ndaza ndathi: Yintoni na le? Athi: Ndim lo uphumileyo. Waza wathi, Nguwo lo umnqophiso wabo phezu kwehlabathi lonke.', 'XHO22'),
(42, 5, 7, 'Nantso italente yelothe inyuswa, nanko kuhleli inkazana esazulwini se-efa leyo.', 'XHO22'),
(42, 5, 8, 'Wathi, Yimbi le; Wayiphosa kwi-efa; waphosa ubunzima belothe emlonyeni wayo.', 'XHO22'),
(42, 5, 9, 'Ndawaphakamisa ke amehlo am, ndakhangela, ndabona abafazi ababini bevela, umoya usemaphikweni abo; ngokuba zazinamaphiko njengengwamza, wayiphakamisa iefa leyo, yaba phakathi komhlaba nezulu.', 'XHO22'),
(42, 5, 10, 'Ndathi kwisithunywa esibe sithetha nam, Bakusa phi na?', 'XHO22'),
(42, 5, 11, 'Wathi kum, Ndiya kwakha indlu ezweni laseShinare; yaye iya kuma ngokuqinileyo, ime apho emhlabeni wayo.', 'XHO22'),
(42, 6, 1, 'Ndajika, ndawaqwalasela amehlo am, ndakhangela, nanzo iinqwelo zokulwa zine, ziphuma phakathi kweentaba ezimbini; yaye iintaba yayiziintaba zobhedu.', 'XHO22'),
(42, 6, 2, 'Enqwelweni yokuqala ibingamahashe abomvu; enqwelweni yesibini ingamahashe amnyama;', 'XHO22'),
(42, 6, 3, 'Enqwelweni yesithathu ibingamahashe amhlophe; enqwelweni yesine ibingamahashe aqingqwe ngomkrolo.', 'XHO22'),
(42, 6, 4, 'Ndasiphendula isithunywa esibe sithetha nam, ndathi, Yintoni na, nkosi yam?', 'XHO22'),
(42, 6, 5, 'Saphendula ke isithunywa, sathi kum, Aba ngoomoya abane bezulu, abafika bema phambi kweNkosi yehlabathi lonke.', 'XHO22'),
(42, 6, 6, 'Kuyo amahashe amnyama, aya kwelasentla; nabamhlophe babalandela; namaqebengwana agcadiweyo aphuma aye ngasezantsi.', 'XHO22'),
(42, 6, 7, 'Yaphuma inkosana yaza yafuna ukuhamba nehlabathi, yaza yathi, Hamba, uhambe nehlabathi. Ngoko bahamba bejikeleza ihlabathi.', 'XHO22'),
(42, 6, 8, 'Sandibiza saza sathetha nam, sathi: “Yabona, abo baphambukela entla bawuthi shwaka umphefumlo wam entla.', 'XHO22'),
(42, 6, 9, 'Kwafika ilizwi likaYehova kum, lisithi:', 'XHO22'),
(42, 6, 10, 'Thabatha kwabo bathinjelwe ekuthinjweni, uHeledayi, uTobhiya noYedaya, abaphuma eBhabhiloni, uze kwangaloo mini, ungene endlwini kaYosiya unyana kaZefaniya;', 'XHO22'),
(42, 6, 11, 'Ke kaloku thabatha isilivere negolide wenze izithsaba uze ubeke entlokweni kaYoshuwa unyana kaYehodeki umbingeleli omkhulu;', 'XHO22'),
(42, 6, 12, 'Thetha kuye uthi, Utsho uYehova wemikhosi ukuthi, Le ndoda, igama layo linguLute; yaye uya kukhula endaweni yakhe aze ayakhe itempile kaYehova.', 'XHO22'),
(42, 6, 13, 'Nguye oya kuyakha itempile kaYehova; uya kuba nozuko, ahlale, alawule etroneni yakhe; abe ngumbingeleli etroneni yakhe, ibe icebo loxolo libe phakathi kwabo bobabini.', 'XHO22'),
(42, 6, 14, 'Kwaye izithsaba ziya kuba zezoHelem noTobhiya noYedaya noHen unyana kaZefaniya njengesikhumbuzo setempile kaYehova.', 'XHO22'),
(42, 6, 15, 'Yaye abo bakude baya kuza beza kwakha itempile kaYehova, yaye ngokuqinisekileyo niya kwazi ukuba uYehova wemikhosi undithumile kuni. Yaye oku kuya kwenzeka ukuba uthe waliphulaphula ngenyameko ilizwi likaYehova uThixo wakho.', 'XHO22'),
(42, 7, 1, 'Ngomnyaka wesine kaDariyo ukumkani, ngolwesine lwenyanga yesithoba apho, kwafika ilizwi likaYehova kuZekariya waseKisilevu;', 'XHO22'),
(42, 7, 2, 'Bathuma ooSeretsere noRegemeleki namadoda abo endlwini kaThixo, ukuba bathandaze kuYehova;', 'XHO22'),
(42, 7, 3, 'bathethe kubabingeleli ababesendlwini kaYehova wemikhosi, nakubaprofeti, bathi, Ndilile na ngenyanga yesihlanu, ndize ndizahlule, njengoko bendisenjenjalo iminyaka engaka?', 'XHO22'),
(42, 7, 4, 'Ke kaloku kwafika ilizwi likaYehova wemikhosi kum, lisithi:', 'XHO22'),
(42, 7, 5, 'Thetha kubantu bonke belizwe eli, nakubabingeleli, uthi, Enere, oko wakuzila ukudla, walila ngenyanga yesihlanu nangeyesixhenxe, ngaloo minyaka imashumi asixhenxe, ukuzila ukudla ngenxa yam na?', 'XHO22'),
(42, 7, 6, 'Xa benisidla nayisela, bekungadli na, bekungàseli nina na?', 'XHO22'),
(42, 7, 7, 'Anifanele nikuve na amazwi kaYehova awawathethayo ngabaprofeti bokuqala ekwakhiweni kweYerusalem yaza yaphumelela nakwizixeko eziyingqongileyo, ngoxa abantu babehlala emzantsi naseBalani leBala?', 'XHO22'),
(42, 7, 8, 'Kwafika ilizwi likaYehova kuZekariya, lisithi:', 'XHO22'),
(42, 7, 9, 'Utsho uYehova wemikhosi ukuthi, Thethani inyaniso, niyenzele inceba, niyenzele inceba, elowo kumzalwana wakhe.', 'XHO22'),
(42, 7, 10, 'Umhlolokazi nenkedama, nomphambukeli, nehlwempu, uze ungamcinezeli; uze ungathethi kakubi ngomzalwana wakho entliziyweni yakhe.', 'XHO22'),
(42, 7, 11, 'Abavumanga ke ukuva, barhola igxalaba labo, bazivala iindlebe zabo, ukuba abeva.', 'XHO22'),
(42, 7, 12, 'Ewe, iintliziyo zabo bazenza zanjengelitye, ukuze bangawuva umyalelo namazwi, awawathumelayo uYehova wemikhosi ngomoya wakhe, ngabaprofeti bokuqala; ke ngoko, umsindo omkhulu uvela kuYehova wemikhosi.', 'XHO22'),
(42, 7, 13, 'Ke ngoko, xa wabizayo, abeva; ngoko ke bakhwaza, yaye andizange ndiphulaphule, utsho uYehova wemikhosi.', 'XHO22'),
(42, 7, 14, 'Kodwa ndabachithachitha ngomoya wesaqhwithi phakathi kweentlanga zonke ababengazazi. Ngenxa yoko ilizwe laba yintlango emva kwabo, ekungacandi mntu kulo, kungabuyi mntu kulo; ngokuba balijika ilizwe elo elihle laba yintlango.', 'XHO22'),
(42, 8, 1, 'Kwafika ilizwi likaYehova wemikhosi kum, lisithi:', 'XHO22'),
(42, 8, 2, 'Utsho uYehova wemikhosi ukuthi, Ndinekhwele ngeZiyon, ikhwele elikhulu;', 'XHO22'),
(42, 8, 3, 'Wathi uYehova; ndibuyele eZiyon; ndiya kuhlala eYerusalem, kuthiwe iYerusalem ngumzi wenyaniso; nentaba kaYehova wemikhosi yintaba engcwele.', 'XHO22'),
(42, 8, 4, 'Utsho uYehova wemikhosi ukuthi, Kusaya kuhlala amadoda amakhulu namaxhegokazi ezitratweni zaseYerusalem, elowo ephethe umsimelelo wakhe iminyaka emininzi;', 'XHO22'),
(42, 8, 5, 'Kuya kuzala izitrato zomzi ngamakhwenkwe namantombazana, edlala ezitratweni zawo;', 'XHO22'),
(42, 8, 6, 'Utsho uYehova wemikhosi ukuthi, Ukuba kuyinto engummangaliso emehlweni amasalela olu hlanga, kule mini yanamhlanje, kuya kuba yinto engummangaliso yini na nasemehlweni am? utsho uYehova wemikhosi.', 'XHO22'),
(42, 8, 7, 'Utsho uYehova wemikhosi ukuthi, Yabonani, ndiya kubasindisa abantu bam ezweni lasempumalanga, naselizweni lasentshonalanga;', 'XHO22'),
(42, 8, 8, 'Ndobazisa, bahlale phakathi kweYerusalem; yaye baya kuba ngabantu bam, nam ndibe nguThixo wabo ngokuthembeka nangobulungisa.', 'XHO22'),
(42, 8, 9, 'Utsho uYehova wemikhosi ukuthi, Izandla zenu mazomelele, nina bawevayo la mazwi, kule mini yaphakade, ngomlomo wabaprofeti, ekusekweni kwendlu kaYehova wemikhosi; itempile yayinokwakhiwa.', 'XHO22'),
(42, 8, 10, 'Ngokuba phambi kwaloo mihla bekungekho mvuzo wabantu, kungekho mvuzo wawungekho; nophumileyo nongenayo, akubangakho luxolo ngenxa yobubi; ngokuba ndibamisele bonke bephela, elowo alwe owakhe.', 'XHO22'),
(42, 8, 11, 'Ngoku ke andiyi kuphinda ndibe phakathi kwamasalela esi sizwe njengokwemihla yakudala,” utsho uYehova wemikhosi.', 'XHO22'),
(42, 8, 12, 'Ngokuba imbewu yoxolo; umdiliya wothwala isiqhamo sawo, nehlabathi linike indyebo yalo, nezulu linike umbethe walo; yaye zonke ezi zinto ndiya kuzinika intsalela yolu hlanga.', 'XHO22'),
(42, 8, 13, 'Njengoko nisuke naba sisiqalekiso ezintlangeni, ndlu kaYuda, nani ndlu kaSirayeli! ngoko ndiya kunisindisa, nibe yintsikelelo; musani ukoyika, mazomelele izandla zenu.', 'XHO22'),
(42, 8, 14, 'Ngokuba utsho uYehova wemikhosi ukuthi, Njengoko ndakhumbulayo ukunivelela, oko ooyihlo bandingcikivayo, utsho uYehova wemikhosi, andazohlwaya ke.', 'XHO22'),
(42, 8, 15, 'Kwakhona ndicinge ukwenza okulungileyo kwiYerusalem nakwindlu kaYuda ngale mihla; sukoyika.', 'XHO22'),
(42, 8, 16, 'Zizo ezo izinto enifanele nizenze; Elowo uxelela ummelwane wakhe inyaniso; gwebani ngenyaniso nangoxolo emasangweni enu.', 'XHO22'),
(42, 8, 17, 'Makungabikho namnye kuni ucinga ububi entliziyweni yakhe ngommelwane wakhe; ningathandi izifungo zobuxoki, ngokuba zonke ezi zizinto endizithiyileyo,” utsho uYehova.', 'XHO22'),
(42, 8, 18, 'Kwafika ilizwi likaYehova wemikhosi kum, lisithi:', 'XHO22'),
(42, 8, 19, 'Utsho uYehova wemikhosi ukuthi, Uzilo lweyesine inyanga, nozilo lweyesihlanu, nozilo lweyesixhenxe, nozilo lweyeshumi, kuya kuba yimihlali, nemivuyo, nemibhiyozo yemihlali endlwini kaYuda; ke ngoko thanda inyaniso noxolo.', 'XHO22'),
(42, 8, 20, 'Utsho uYehova wemikhosi ukuthi, Noko ke, iintlanga nabemi bemizi emininzi baya kuza;', 'XHO22'),
(42, 8, 21, 'Baya kuhamba abemi bomnye baye kwesinye, bathi, Masikhawuleze siye kuthandaza kuYehova, simquqele uYehova wemikhosi; Nam ndiya kuhamba.', 'XHO22'),
(42, 8, 22, 'Ewe, iintlanga ezininzi nezizwe ezinamandla ziya kuza kumquqela uYehova wemikhosi eYerusalem, zimbongoze uYehova.', 'XHO22'),
(42, 8, 23, 'Utsho uYehova wemikhosi ukuthi, Ngelo xesha aya kulithimba amadoda alishumi eelwimi zonke zeentlanga, alithabathe icwecwe lomYuda, esithi, Siya kuhamba nani, ngokuba sivile ukuba kusithiwa, ndikunye nawe.', 'XHO22'),
(42, 9, 1, 'Ilizwi likaYehova liya kuhlala ezweni laseHadrage neDamasko;', 'XHO22'),
(42, 9, 2, 'iHamati yoba ngumda wayo; Tire neTsidon, nilumke kakhulu;', 'XHO22'),
(42, 9, 3, 'ITire yayakhela inqaba yaza yaqokelela isilivere njengothuli negolide esulungekileyo njengodaka lwezitrato.', 'XHO22'),
(42, 9, 4, 'Yabona, uYehova uya kuligqogqa, nobugorha balo uwaphose elwandle; uya kutsha ngumlilo.', 'XHO22'),
(42, 9, 5, 'iAshkelon iya kukubona oko, yoyike; nayo iGasa iya kuyibona ilusizi kakhulu, ne-Ekron; ngokuba liya kudaniswa ithemba lakhe; nokumkani uya kutshabalala eGaza, neAshkelon ayiyi kuthinjwa.', 'XHO22'),
(42, 9, 6, 'Yaye eAshedodi ndiya kuba yindoda yohlobo lwam, ndize ndilitshabalalise ikratshi lamaFilisti.', 'XHO22'),
(42, 9, 7, 'Ndiya kulisusa igazi lakhe emlonyeni wakhe, namasikizi akhe phakathi kwamazinyo akhe; kodwa lowo useleyo uya kuba ngokaThixo wethu, yaye ngokuqinisekileyo uya kuba njengerhuluneli yelizwe lakwaYuda, ne-Ekron ibe njengomYebhusi.', 'XHO22'),
(42, 9, 8, 'Ndoyirhawula indlu yam ngenxa yomkhosi, ngenxa yodlulayo, nangenxa yobuyayo; yaye akukho zwilakhe uya kudlula kubo, ekubeni ngoku ndikubonile oko ngawam amehlo.', 'XHO22'),
(42, 9, 9, 'Vuya kakhulu, ntombi inguZiyon; duma, ntombi inguYerusalem; Yabona, iNkosi yakho iza kuwe; Ulilungisa, unosindiso; ithobekile, ikhwele idonki, nedonki.', 'XHO22'),
(42, 9, 10, 'Ndoziphanzisa iinqwelo zokulwa kwaEfrayim namahashe eYerusalem, zisongele isaphetha semfazwe; athethe uxolo kuzo iintlanga; Ubukumkani bakhe buya kuthabathela elwandle buse elwandle, buthabathele kumlambo iNayile, buzise eziphelweni zehlabathi.', 'XHO22'),
(42, 9, 11, 'Ke wena, ngegazi lomnqophiso wakho ndiya kubakhupha abathinjwa bakho emhadini ongenamanzi;', 'XHO22'),
(42, 9, 12, 'Phethukelani entabeni leyo, nina babanjwa lithemba; namhlanje ndiyathembisa ukuba ndiya kukubuyekeza kabini;', 'XHO22'),
(42, 9, 13, 'Ukuba ndithe ndamelula uYuda, isaphetha sam ndimfakele uEfrayim noonyana bakho, Ziyon, ndiya kubaphakamisa oonyana bakho, Greece, ndikwenze ube njengekrele elinamandla.', 'XHO22'),
(42, 9, 14, 'uYehova uya kubonakala phezu kwabo, luhambe njengombane utolo lwakhe; yaye iNkosi enguMongami uYehova iya kuvuthela ixilongo layo ize ihambe ngezaqhwithi ezisuka emzantsi.', 'XHO22'),
(42, 9, 15, 'uYehova wemikhosi uya kubakhusela; zodliwa, zityunyuzwe ngamatye okusawula; baya kusela, benze isandi njengewayini; baya kuzala ngokweengqayi, nanjengeekona zesibingelelo.', 'XHO22'),
(42, 9, 16, 'Yaye uYehova uThixo wabo uya kubahlangula ngaloo mini njengezimvu zabantu bakhe, ngenxa yokuba baya kuba njengamatye axhonywe njengeflegi ezweni lakhe.', 'XHO22'),
(42, 9, 17, 'Kuba kungakanani na ukulunga kwabo, bungakanani na ubuhle babo! Ingqolowa iya kubavuyisa abafana, moss iimpelesi.', 'XHO22'),
(42, 10, 1, 'Celani imvula kuYehova emvula yasekwindla; ngoko xa uYehova wenza amafu abengezelayo, wobanika imvula kuyo yonke ingca yelizwe.', 'XHO22'),
(42, 10, 2, 'Kuba izithixo ezinezothe ezinqulwayo zithethe ilize, nabaneshologu babone ubuxoki, baxela amaphupha obuxoki; bathuthuzela ilize; zahamba njengomhlambi, zoyika, ngokuba kwakungekho malusi.', 'XHO22'),
(42, 10, 3, 'Umsindo wam uvuthelwe kubalusi, ndaza ndazivelela iinkunzi zeebhokhwe, ngenxa yokuba uYehova wemikhosi wawuvelela umhlambi wakhe, indlu kaYuda, waza wawenza wanjengehashe lakhe elihle edabini.', 'XHO22'),
(42, 10, 4, 'Kuvela kuye ilitye lembombo; kuya kuvela kuye isikhonkwane; kuya kuvela kuye isaphetha; kuya kuvela kuye bonke abakhandanisi ngakunye.', 'XHO22'),
(42, 10, 5, 'Baya kuba njengamagorha anyathela iintshaba zawo eludakeni lwezitrato, emfazweni; yaye baya kulwa, ngenxa yokuba uYehova unabo, nabakhweli-mahashe baya kuba neentloni.', 'XHO22'),
(42, 10, 6, 'Ndiya kuyomeleza indlu kaYuda, ndiyisindise indlu kaYosefu, ndibabuyise, baphile; kuba ndiyabasizela, kwaye baya kuba ngathi andibagatyanga; ngokuba ndinguYehova uThixo wabo, ndiya kubaphulaphula.', 'XHO22'),
(42, 10, 7, 'Yoba njengegorha uEfrayim, yoba nemihlali intliziyo yabo, ngokungathi basele iwayini; ewe, abantwana babo baya kubona, bavuye; iintliziyo zabo ziya kuvuya ngoYehova.', 'XHO22'),
(42, 10, 8, 'Ndiya kubethelela umlozi, ndibabuthe; ngokuba ndibakhulule ngokubahlawulela; baya kwanda, njengoko babesanda ngako.', 'XHO22'),
(42, 10, 9, 'Ndiya kubahlwayela ezintlangeni, bandikhumbule emazweni akude; baya kuhlala nabantwana babo, babuye.', 'XHO22'),
(42, 10, 10, 'Ndobabuyisa ezweni laseYiputa, ndibabuthe eAsiriya; Ndiya kubazisa ezweni laseGiliyadi nelaseLebhanon; kwaye ayiyi kufunyanwa ndawo yabo.', 'XHO22'),
(42, 10, 11, 'Uya kuhamba ebandezelekile embilinini wolwandle, abethe amaza elwandle, yome yonke imithombo yomlambo; Iqhayiya leAsiriya liya kuthotywa, imke intonga yaseYiputa.', 'XHO22'),
(42, 10, 12, 'Ndiya kubomeleza ngoYehova; yaye baya kuhamba egameni lakhe,’ utsho uYehova.', 'XHO22'),
(42, 11, 1, 'Vula iingcango zakho, Lebhanon, ukuba umlilo uyidle imisedare yakho.', 'XHO22'),
(42, 11, 2, 'iBhokola, umsipres; ngokuba kuwe imisedare; ngokuba abanamandla batshatyalalisiwe. Bhombolozani, nina mithi yeturpentine yaseBhashan; ngokuba ihlathi lokuvuna liwile.', 'XHO22'),
(42, 11, 3, 'Yivani ukukhala kwabalusi; ngokuba bubhuqiwe ubungangamsha bazo; lilizwi leengonyama ezibharhulayo; ngokuba laphukile iqhayiya laseYordan.', 'XHO22'),
(42, 11, 4, 'Utsho uYehova, uThixo wam, ukuthi, Yalusa umhlambi;', 'XHO22'),
(42, 11, 5, 'abathi babulawe ngabaninizo, bazigqale njengabamsulwa; bathi: “Makabongwe uYehova! ngokuba zizityebi, nabalusi bawo àbawacongi.', 'XHO22'),
(42, 11, 6, 'Ngokuba andiyi kuphinda ndibaconge abemi belizwe,” utsho uYehova; kodwa qaphelani, ndiya kuwanikela la madoda, ngamnye esandleni sesakhe, nasesandleni sokumkani wakhe, kwaye aya kuloyisa ilizwe, kwaye andiyi kubasindisa esandleni sabo.', 'XHO22'),
(42, 11, 7, 'Ndiya kuwalusa impahla emfutshane exhelwayo, neegusha ezihlwempuzekileyo; Ndathabatha iintonga ezimbini; omnye ndathi nguBuhle, omnye ndathi nguZintya; ndazalusa iigusha.', 'XHO22'),
(42, 11, 8, 'Ndatshabalalisa abalusi abathathu nganyanga-nye; wesuka wemka kubo umoya wam, bandingcatsha kananjalo.', 'XHO22'),
(42, 11, 9, 'Ndathi, Andiyi kunalusa; nento emele kukhutshelwa ngaphandle, mayigqitywe; abanye ke badla inyama yomnye.', 'XHO22'),
(42, 11, 10, 'Ndathabatha intonga yam, uMnandi, ndayaphula, ukuze ndiwaphule umnqophiso wam endanqophisana ngawo neentlanga zonke.', 'XHO22'),
(42, 11, 11, 'Latyhoboza ngaloo mini, zaza izimvu ezisweleyo ezibe zindilindile zazi ukuba lilizwi likaYehova eli.', 'XHO22'),
(42, 11, 12, 'Ndathi kubo, Ukuba nibona ukuba kulungile, ndinikeni umvuzo wam; ukuba akunjalo, yiyeke. Bandilinganisela ke, bandilinganisela amashumi amathathu eesilivere zesilivere.', 'XHO22'),
(42, 11, 13, 'Wathi uYehova kum, Yiphose kumbumbi; Ndathabatha amashumi amathathu esilivere, ndawaphosa endlwini yombumbi wendlu kaYehova.', 'XHO22'),
(42, 11, 14, 'Ndayaphula intonga yam enye, enjengamasebe, ukuze ndibuqhawule ubuhlobo phakathi koYuda noSirayeli.', 'XHO22'),
(42, 11, 15, 'Wathi uYehova kum, Thabatha iintonga zomalusi osisimathane.', 'XHO22'),
(42, 11, 16, 'Ngokuba, yabona, ndivusa ehlabathini umalusi, ongayi kuvelela amadoda amakhulu, ongayi kufuna izisweli, angaphilisi ezaphukileyo, angadlisi ivila; uya kuba nenyama etyebileyo, aziqhawule izikhonkwane zabo.', 'XHO22'),
(42, 11, 17, 'Yeha, umalusi onqula izithixo, owulahlayo umhlambi! ikrele liya kuba sesandleni sakhe, nakwiliso lakhe lasekunene; somile isandla sakhe, neliso lakhe lasekunene liya kuba mnyama ngokupheleleyo.', 'XHO22'),
(42, 12, 1, 'Ilizwi likaYehova kuSirayeli, utsho uYehova, uManeki wezulu, uMseki wehlabathi, owenza umphefumlo womntu kulo;', 'XHO22'),
(42, 12, 2, 'Yabona, iYerusalem ndiyenza ibe yindebe ehexayo ezintlangeni zonke ezingeenxa zonke, ekungqingweni kwayo kwaYuda neYerusalem.', 'XHO22'),
(42, 12, 3, 'Ngaloo mini iYerusalem ndiya kuyenza ilitye elinzima ezintlangeni zonke;', 'XHO22'),
(42, 12, 4, 'Ngaloo mini, utsho uYehova, ndiya kuwabetha ngokugeza onke amahashe nabakhweli bawo; ndiya kuwavula amehlo am phezu kwendlu kaYuda, ndiwabethe onke amahashe ezizwe abe ziimfama.', 'XHO22'),
(42, 12, 5, 'Zothi iinkosana zakwaYuda ezintliziyweni zazo, Bangamandla am abemi baseYerusalem, ngoYehova wemikhosi, uThixo wabo.', 'XHO22'),
(42, 12, 6, 'Ngaloo mini ndiya kuzenza iinkosana zakwaYuda zibe njengombumbi emithini, nanjengezikhuni ezitshangaziswayo phakathi kweemfumba; badle bonke abantu ngeenxa zonke, ngasekunene nangasekhohlo, yakhiwe iYerusalem, endaweni yayo, iYerusalem.', 'XHO22'),
(42, 12, 7, 'Kananjalo uYehova uya kuzisindisa iintente zakwaYuda kuqala, ukuze uzuko lwendlu kaDavide nozuko lwabemi baseYerusalem lungaqhayisi ngoYuda.', 'XHO22'),
(42, 12, 8, 'Ngaloo mini uYehova uya kubakhusela abemi baseYerusalem; noyena oswelekileyo kubo ngaloo mini uya kuba njengoDavide; indlu kaDavide ibe njengoThixo, njengesithunywa sikaYehova phambi kwabo.', 'XHO22'),
(42, 12, 9, 'Yaye ngaloo mini ndiya kuzama ukuzitshabalalisa zonke iintlanga ezihlasela iYerusalem.', 'XHO22'),
(42, 12, 10, 'Ndiya kuthulula uMoya wenceba nomthandazo phezu kwendlu kaDavide, naphezu kwabemi baseYerusalem; bakhangele kum bamhlabileyo, bambambazelele, njengomntu ombambazelayo okuphela kwakhe, bammbambazelele, njengokuba alilela owamazibulo.', 'XHO22'),
(42, 12, 11, 'Ngelo xesha kuya kubakho isikhalo esikhulu eYerusalem, njengesikhalo saseHadaderimon ethafeni laseMegidon.', 'XHO22'),
(42, 12, 12, 'Kuya kukhala ilizwe, izizukulwana ngezizukulwana; intsapho yendlu kaDavide ngaphandle, nabafazi babo ngaphandle; nemizalwane yendlu kaNatan ngokwamaqela, nabafazi babo ngokwamaqela;', 'XHO22'),
(42, 12, 13, 'nemizalwane yendlu kaLevi ngabanye, nabafazi babo ngabanye; kwisizwe sakwaShimehi bebodwa, nabafazi babo bebodwa;', 'XHO22'),
(42, 12, 14, 'Zonke iintsapho eziseleyo, intsapho nganye kumntu ngamnye nabafazi bayo bebodwa.', 'XHO22'),
(42, 13, 1, 'Ngaloo mini kuya kubakho iqula lesono nokungcola kubemi baseYerusalem kwindlu kaDavide nabemi baseYerusalem;', 'XHO22'),
(42, 13, 2, 'Kuya kuthi ngaloo mini, utsho uYehova wemikhosi, ndiwanqumle amagama oothixo ehlabathini, bangabi sakhunjulwa; yaye ndiza kuzohlwaya kunye nabaprofeti naloo moya ungendawo ukuya phesheya.', 'XHO22'),
(42, 13, 3, 'Ukuba umntu uthe waprofeta, uyise nonina abamzeleyo bothi kuye, Akuyi kuphila; ngenxa yokuba uxoke egameni likaYehova, yaye uyise nonina abamzalayo bomhlaba ekuprofeteni kwakhe.', 'XHO22'),
(42, 13, 4, 'Kuya kuthi ngaloo mini, badane abaprofeti, elowo akhangele ubuso bakhe ekuprofeteni kwakhe; kwaye babenganxibanga sambatho esirhabaxa ukuze bakhohlise;', 'XHO22'),
(42, 13, 5, 'Wothi, Andimprofeti, ndingumlimi; ngokuba kwasebuncinaneni bam wandifundisa ukwalusa iinkomo.', 'XHO22'),
(42, 13, 6, 'Kothiwa kuye, Ayini na la manxeba asezandleni zakho? Aphendule ke athi: Ngabo abo ndahlatywa endlwini yabahlobo bam.', 'XHO22'),
(42, 13, 7, 'Suk’ ume, krelendini, vuka, malusi wam, indoda engumhlobo wam, utsho uYehova wemikhosi; betha umalusi, zichithwachithwe izimvu;', 'XHO22'),
(42, 13, 8, '“Kuya kuthi ke ehlabathini lonke,” utsho uYehova, iinxalenye ezimbini zalo ziya kunqunyulwa zize zife; ke owesithathu uya kuhlala kuyo.', 'XHO22'),
(42, 13, 9, 'Esinye isahlulo sesithathu ndiya kusitshisa emlilweni, sinyibilike njengokunyibilikiswa kwesilivere; baya kubiza egameni lam, ndibaphendule mna; abantu bam, bathi, uYehova nguThixo wam.', 'XHO22'),
(42, 14, 1, 'Yabonani, kuza imini kaYehova, eya kwabiwe amaxhoba enu phakathi kwenu.', 'XHO22'),
(42, 14, 2, 'Ndozihlanganisela eYerusalem zonke iintlanga ukuba zilwe; nesixeko siya kuthinjwa, izindlu ziphangwe, nabafazi baya kuhlaziswa; isiqingatha sesixeko siya kuya ekuthinjweni, yaye amasalela abantu akayi kushenxiswa esixekweni.', 'XHO22'),
(42, 14, 3, 'Uya kwandula ke uYehova aphume aze alwe nezo ntlanga, kanye njengoko walwayo ngemini yedabi.', 'XHO22'),
(42, 14, 4, 'Ngaloo mini iinyawo zakhe ziya kuma phezu kweNtaba yemiNquma ephambi kweYerusalem, ngasempumalanga, iNtaba yemiNquma ibe phakathi, ithabathele empumalanga ise entshonalanga; kuya kubakho intaba enkulu intlambo enkulu; isiqingatha sentaba siya kuhambela entla, nesiqingatha sayo siye ngasemzantsi.', 'XHO22'),
(42, 14, 5, 'Nosabela entlanjeni yeentaba; ngokuba intili yeentaba iya kufika eAsale, nisabe njengoko nasabayo ngenxa yendudumo yehlabathi, ngemihla kaUziya ukumkani wakwaYuda, eze uYehova uThixo wam, abe phezu kwenu bonke abathembekileyo.', 'XHO22'),
(42, 14, 6, 'Ngaloo mini ukukhanya akuyi kukhanya, nokuba kumnyama;', 'XHO22'),
(42, 14, 7, 'Kodwa kuya kuba yimini enye eya kwaziwa nguYehova, kungekhona imini okanye ubusuku; kodwa kuya kubakho ukukhanya ngokuhlwa.', 'XHO22'),
(42, 14, 8, 'Kuya kuthi ngaloo mini, kuphume eYerusalem amanzi aphilileyo; isiqingatha sazo siye kulwandle lokuqala, esinye isiqingatha sazo siye kulwandle olungasemva; ihlobo nobusika kuya kuba njalo.', 'XHO22'),
(42, 14, 9, 'uYehova uya kuba ngukumkani wehlabathi lonke; ngaloo mini iya kuba nguYehova yedwa, ogama lakhe linguYehova.', 'XHO22'),
(42, 14, 10, 'Ilizwe lonke liya kuba njengethafa, ukusuka eGebha lise eRimon ezantsi kweYerusalem; liya kuphakanyiswa, lihlale endaweni yalo, lithabathele esangweni lakwaBhenjamin, lise endaweni yesango lokuqala, lise esangweni lembombo, lithabathele enqabeni ende ethabathela kwaHananeli esa ezingontsini zokumkani.', 'XHO22'),
(42, 14, 11, 'Abantu baya kuhlala kulo, yaye akusayi kuphinda kubekho intshabalalo epheleleyo; kodwa iYerusalem iya kumiwa ngoxolo.', 'XHO22'),
(42, 14, 12, 'Siya kuba seso isohlwayo uYehova aya kuzohlwaya ngaso zonke iintlanga ezilwa neYerusalem; Inyama yawo iya kudla emi ngeenyawo zawo, namehlo awo atyele emakrolomeni awo, nolwimi lwawo ludle emlonyeni wawo.', 'XHO22'),
(42, 14, 13, 'Kuya kuthi ngaloo mini kubekho isidubedube esikhulu esivela kuYehova; kwaye baya kubamba elowo ngesandla sommelwane wakhe, kwaye isandla sakhe siya kuba phezulu kunesandla sommelwane wakhe.', 'XHO22'),
(42, 14, 14, 'Naye uYuda uya kulwa eYerusalem; buhlanganiswe ubutyebi beentlanga zonke ezilumelwaneyo, igolide, nesilivere, neempahla, into eninzi kakhulu.', 'XHO22'),
(42, 14, 15, 'Isibetho samahashe, iimeyile, iinkamela, needonki, nazo zonke izilwanyana eziya kuba seminqubeni, ziya kuba njengeso sifo.', 'XHO22'),
(42, 14, 16, 'Yaye wonk’ ubani oseleyo kuzo zonke iintlanga ezazisiza eYerusalem uya kunyuka minyaka le ukuya kunqula uKumkani, uYehova wemikhosi, aze enze umthendeleko weMetlagagana.', 'XHO22'),
(42, 14, 17, 'Nabani na osukuba ephuma kuzo zonke iintsapho zomhlaba osukuba enganyuki aye eYerusalem ukuya kunqula uYehova, uYehova wemikhosi, akayi kunisa mvula nakubo.', 'XHO22'),
(42, 14, 18, 'Ukuba isizwe saseYiputa asinyuki, asizanga, akubangakho mvula; kuya kubakho isohlwayo uYehova aya kuzohlwaya ngaso iintlanga ezingenyukiyo ziye kubhiyozela umthendeleko weminquba.', 'XHO22'),
(42, 14, 19, 'Siya kuba seso isohlwayo sesono seYiputa nesohlwayo sesono seentlanga zonke ezingenyukiyo ziye kubhiyozela umthendeleko weminquba.', 'XHO22'),
(42, 14, 20, 'Ngaloo mini kuya kubhalwe ezintsimbini zamahashe ukuthi: “Ningcwele kuYehova; kwaye iimpahla zendlu kaYehova ziya kuba njengempahla ephambi kwesibingelelo.', 'XHO22'),
(42, 14, 21, 'Ewe, zonke iimbiza eYerusalem nakwaYuda zoba ngcwele kuYehova wemikhosi; bathabathe kuyo bonke abazisa umnikelo ngayo, bagubungele ngayo; yaye ngaloo mini akusayi kuba sabakho mKanan endlwini kaYehova wemikhosi.', 'XHO22'),
(43, 1, 1, 'Isigidimi selizwi likaYehova kuSirayeli ngoMalaki.', 'XHO22'),
(43, 1, 2, 'Ndinithandile, utsho uYehova. Kodwa uthi: Usithande njani? uEsawu ebengengomkhuluwa kaYakobi na? utsho uYehova, kodwa ndathanda uYakobi;', 'XHO22'),
(43, 1, 3, 'Ndamthiya uEsawu, Ndazitshabalalisa iintaba zakhe, Nelifa lakhe laba leleempungutye entlango.', 'XHO22'),
(43, 1, 4, 'Ngoxa uEdom wathi: “Sizizisweli, kodwa siya kubuya siwakhe amanxuwa; utsho uYehova wemikhosi ukuthi, Bona baya kwakha, kodwa mna ndiya kugungxula; yaye baya kuzibiza ngokuba: “Isiphelo sobungendawo yaye: Ngabantu abacaphukeleyo uYehova ngonaphakade.', 'XHO22'),
(43, 1, 5, 'Amehlo enu aya kukubona oko, nithi, Mkhulu uYehova emdeni wakwaSirayeli;', 'XHO22'),
(43, 1, 6, 'Unyana uyambeka uyise, nomkhonzi uyayihlonela inkosi yakhe; ukuba ndinguyihlo, kuphi na ukubekwa kwam? ukuba ndingukumkani, kuphi na ukoyikwa kwam? Utsho uYehova wemikhosi kuni, nina babingeleli, nina balinyelisa igama lam; Nithi ke, Silihlambele ngantoni na igama lakho?', 'XHO22'),
(43, 1, 7, 'Nizisa esibingelelweni sam isonka esiyinqambi; nithi, Sikungcolise ngantoni na? Kuba uthi: Isithebe sikaYehova yinto edelekileyo.', 'XHO22'),
(43, 1, 8, 'Ukuba nizisa umntu oyimfama njengombingelelo, akunto imbi oko? Ukuba nibingelela ngesiqhwala nangesifayo, akunto imbi oko? Linike kaloku irhuluneli yenu; Ngaba uya kuvuya nawe, okanye athathe ubuntu bakho? utsho uYehova wemikhosi.', 'XHO22'),
(43, 1, 9, 'Kaloku ke khanimbongoze uThixo, ukuba abe nenceba kuthi; utsho uYehova wemikhosi.', 'XHO22'),
(43, 1, 10, 'Ngubani na kuni oya kuzitshixa iingcango ngelize? musani ukufaka umlilo kwisibingelelo sam. Aninanzeleli kuni, utsho uYehova wemikhosi; andiyi kuyamkela esandleni senu imibingelelo;', 'XHO22'),
(43, 1, 11, 'Kuba kuthabathela ekuphumeni kwelanga kuse ekutshoneni kwalo, likhulu igama lam phakathi kweentlanga; yaye kuyo yonke indawo kuya kwenziwa isiqhumiso egameni lam nombingelelo osulungekileyo, ngenxa yokuba igama lam liya kuba likhulu phakathi kweentlanga,” utsho uYehova wemikhosi.', 'XHO22'),
(43, 1, 12, 'Ke nina nenze inqambi ngokuthi, Isithebe sikaYehova singcolile; neziqhamo zayo, ewe, ukudla kwayo kulisikizi.', 'XHO22'),
(43, 1, 13, 'Kananjalo nathi, Yabona, kukudinwa okunjani na! ke nina niya kuyicima, utsho uYehova wemikhosi; Bazisa abadumbileyo, nabaziziqhwala, nabafayo; ngoko xa ubingelela; Ndiyithathe kuwe? utsho uYehova.', 'XHO22'),
(43, 1, 14, 'Uqalekisiwe lowo umlukuhlisi, xa kukho iduna emhlambini wakhe, nokuba enze isibhambathiso, asondeza kuYehova into eyonakeleyo; ngokuba ndinguKumkani omkhulu, utsho uYehova wemikhosi, negama lam liyoyikeka phakathi kweentlanga.', 'XHO22'),
(43, 2, 1, 'Ngoku ke lo mthetho ubhekiswa kuni, babingeleli.', 'XHO22'),
(43, 2, 2, 'Ukuba anithanga niphulaphule, anavuma ukulizukisa igama lam entliziyweni yenu, utsho uYehova wemikhosi; ndonithumela isiqalekiso, ndiya kuziqalekisa iintsikelelo zenu; ziqalekise, ngokuba ungazibekeli ntliziyo.', 'XHO22'),
(43, 2, 3, 'Yabona, ndiyayichitha inzala yakho, nditshize umswane ebusweni bakho, nomswane wemithendeleko yakho; yaye omnye uya kunithabatha kunye nayo.', 'XHO22'),
(43, 2, 4, 'Niya kuqonda ke ukuba ndim lo unithumele lo myalelo, ukuze ungabikho umnqophiso wam noLevi; utsho uYehova wemikhosi.', 'XHO22'),
(43, 2, 5, 'Umnqophiso wam naye ububomi noxolo; ndamnika ngenxa yokoyika awandoyika ngako, nangenxa yokoyika igama lam.', 'XHO22'),
(43, 2, 6, 'Umthetho wokwenyaniso wawusemlonyeni wakhe, yaye ububi akuzange kufumaneke emlonyeni wakhe; wahamba nam enoxolo nangobulungisa, etyeka ebubini.', 'XHO22'),
(43, 2, 7, 'Kuba umlomo wombingeleli ngowokugcina ukwazi, nomthetho ufanele kufunwa emlonyeni wakhe, kuba ungumthunywa kaYehova wemikhosi.', 'XHO22'),
(43, 2, 8, 'Ke nina nilahlekile; nikhubekise abaninzi emthethweni; niwaphule umnqophiso kaLevi; utsho uYehova wemikhosi.', 'XHO22'),
(43, 2, 9, 'Ngoko ke nam ndaninenza abadelekileyo nabangenamsebenzi ebantwini bonke, ngenxa yokuba anizigcinanga iindlela zam, kodwa naba nomkhethe emthethweni.', 'XHO22'),
(43, 2, 10, 'Asinguye na ubawo mnye sonke? Xa ndisithi sidalwe nguThixo omnye? Yini na ukuba sone elowo kumzalwana wakhe, siwuhlambele umnqophiso woobawo?', 'XHO22'),
(43, 2, 11, 'uYuda wenze ngobumenemene, kwenziwe amasikizi kwaSirayeli naseYerusalem; kuba uYuda wayebuhlambele ubungcwele bukaYehova awayemthanda waza watshata intombi yothixo wasemzini.', 'XHO22'),
(43, 2, 12, 'Nabani na owenza oko, uYehova uya kumshenxisa ezintenteni zikaYakobi nakulowo ufundisayo nakulowo uzisa umbingelelo kuYehova wemikhosi.', 'XHO22'),
(43, 2, 13, 'Oko ke nenze oku ngokusigubungela isibingelelo sikaYehova ngeenyembezi, ngokulila, nangokukhwaza, ukuze angabi sawukhangela umbingelelo, angawamkeli ngovuyo ezandleni zenu.', 'XHO22'),
(43, 2, 14, 'Ke nina nithi, Ngani na? Ngenxa yokuba uYehova ebelingqina phakathi kwakho nomfazi wobutsha bakho, lowo wonayo kuye; noko ke, liqabane lakho, ngumfazi womnqophiso wakho.', 'XHO22'),
(43, 2, 15, 'Akayenzanga na enye? Nakuba kunjalo, wayenazo iintsalela zomoya. Kwaye kutheni? Ukuze afune imbewu yobuthixo. Ke kaloku, yigcineni imiphefumlo yenu, yaye makungabikho bani owonayo kumfazi wobutsha bakhe.', 'XHO22'),
(43, 2, 16, 'Ngokuba, uYehova, uThixo kaSirayeli, uthi, uluthiyile ulwaluko; ngokuba umntu ugubungela ubugwenxa ngengubo yakhe, utsho uYehova wemikhosi; Zigcineni ke imiphefumlo yenu, ukuze ningenzi ubugqwetha.', 'XHO22'),
(43, 2, 17, 'Nimdinisile uYehova ngamazwi enu. Ke nina nithi, Simhluphe ngantoni? Xa nithi, Bonke abenza okubi balungile emehlweni kaYehova, uyakholiswa ngabo; okanye: Uphi na uThixo womgwebo?', 'XHO22'),
(43, 3, 1, 'Yabona, ndiyasithuma umthunywa wam, agece indlela phambi kwam; Iya kuza ke ngesiquphe etempileni yayo iNkosi leyo niyilindeleyo, uMthunywa womnqophiso enivuya naso; yabona, uyeza, utsho uYehova wemikhosi.', 'XHO22'),
(43, 3, 2, 'Ngubani na ke onokuyinyamezela imini yakuza kwakhe? ngubani na oya kubakho ekubonakaleni kwakhe? ngokuba unjengomlilo womnyibilikisi, unjengesepha yomxovuli;', 'XHO22'),
(43, 3, 3, 'Uya kuhlala ke engumsulungekisi nomsulungekisi wesilivere, abasulungekise oonyana bakaLevi, abahluze njengegolide nesilivere, ukuze bazise umnikelo kuYehova ngobulungisa.', 'XHO22'),
(43, 3, 4, 'Yaye umbingelelo wakwaYuda nowaseYerusalem uya kumkholisa uYehova njengakwimihla yakudala nanjengakwiminyaka yokuqala.', 'XHO22'),
(43, 3, 5, 'Ndiya kusondela kuni ukuba ndigwebe; ndibe lingqina elikhawulezayo kubakhafuli, nakubakrexezi, nakubafungi bobuxoki, nakubacudisa umqeshwa ngenxa yomvuzo wakhe, nakubahlolokazi, nenkedama, nomphambukeli ophambukayo esandleni sakhe sokunene, engoyiki nto mna, utsho uYehova wemikhosi.', 'XHO22'),
(43, 3, 6, 'Kuba mna, Yehova, anditshintshanga; ngoko ke, oonyana bakho bakaYakobi abazange batshabalale.', 'XHO22'),
(43, 3, 7, 'Ukususela kwimihla yookhokho benu niye nayishiya imiyalelo yam emiselweyo, anayigcina. Buyelani kum, ndibuyele kuni, utsho uYehova wemikhosi. Ke nina nithi, Siya kubuyela phi na?', 'XHO22'),
(43, 3, 8, 'Umntu angamtshabalalisa na uThixo? Usandidinga. Kodwa uthi: Sakubela ntoni? Izishumi neminikelo.', 'XHO22'),
(43, 3, 9, 'Uqalekisiwe ngesiqalekiso, ngenxa yokuba undibethe, ewe, olu hlanga luphela.', 'XHO22'),
(43, 3, 10, 'Zisani zonke izishumi ebuncwaneni, ukuze kubekho ukudla endlwini yam; khanindicikide ngale nto, utsho uYehova wemikhosi, ukuba andithanga ndikuvulele na iingcango zezulu, ndinisikelele, ndinivulele iingcango zezulu. Ingakuchitheli kuhle, ukuba akuyi kubakho indawo eyaneleyo yokuyifumana.', 'XHO22'),
(43, 3, 11, 'Ndiya kumvelela odlayo ngenxa yenu, angonakalisi iziqhamo zelizwe lenu; nomdiliya wenu awusayi kusithela emasimini, phambi kwexesha lawo; utsho uYehova wemikhosi.', 'XHO22'),
(43, 3, 12, 'Zonke iintlanga zothi nithamsanqelekile, kuba niya kuba lilizwe eliligugu, utsho uNdikhoyo onamandla onke.', 'XHO22'),
(43, 3, 13, 'Amazwi enu aye aqatha kum, utsho uYehova. Ke nina nithi, Yini na ukuba sithethe kangaka ngawe?', 'XHO22'),
(43, 3, 14, 'Nithi, Kulilize ukumkhonza uThixo; yaye kunceda ngantoni ukuba sigcine umyalelo wakhe size sihambe silusizi phambi koYehova wemikhosi?', 'XHO22'),
(43, 3, 15, 'Kungokunje sithi banoyolo abanekratshi; ewe, abo benza ububi bayaqinisekiswa; Ewe, abo bacelayo kuThixo bayakhululwa.', 'XHO22'),
(43, 3, 16, 'Ke abamoyikayo uYehova bathetha elowo nowabo;', 'XHO22'),
(43, 3, 17, 'Yaye baya kuba ngabam, utsho uYehova wemikhosi, ngaloo mini ndisenza amatye am ayimfuneko; yaye ndiya kubaconga njengomntu oconga unyana wakhe omkhonzayo.', 'XHO22'),
(43, 3, 18, 'Kulapho ke uya kubuyela khona, uhlule phakathi kwelungisa nongendawo, omkhonzayo uThixo nongamkhonziyo.', 'XHO22'),
(43, 4, 1, 'Ngokuba nalo ilanga liphuma livutha njengelanga; basuke bonke abakhukhumeleyo, nabo bonke abenza ubutshinga, babe njengeendiza;', 'XHO22'),
(43, 4, 2, 'Ke kuni, nina baloyikayo igama lam, ilanga lobulungisa liya kuphuma, linophiliso phantsi kwamaphiko alo; yaye niya kuphuma nize nikhule njengamathole asesitalini.', 'XHO22'),
(43, 4, 3, 'Uya kubanyathela abangendawo; kuba baya kuba njengaphantsi kweentende zeenyawo zenu ngemini endiya kukwenza ngayo oku,’ utsho uYehova wemikhosi.', 'XHO22'),
(43, 4, 4, 'Khumbulani umyalelo kaMoses, umkhonzi wam, endamwiselayo eHorebhe ngokusingisele kumaSirayeli onke, okuyimiyalelo namasiko.', 'XHO22'),
(43, 4, 5, 'Yabonani, ndiya kuthumela kuni uEliya umprofeti, ingekafiki imini enkulu eyoyikekayo kaYehova.', 'XHO22'),
(43, 4, 6, 'Woyibuyisela intliziyo yooyise koonyana, neentliziyo zoonyana kooyise, ukuze ndingezi, ndilibethe ihlabathi ngobubi.', 'XHO22'),
(44, 1, 1, 'Incwadi yomlibo wokuzalwa kukaYesu Kristu, unyana kaDavide, unyana ka-Abraham.', 'XHO22'),
(44, 1, 2, 'uAbraham wazala uIsake; uIsake wazala uYakobi; uYakobi wazala uYuda nabazalwana bakhe;', 'XHO22'),
(44, 1, 3, 'uYuda wazala uPeres noZara ngoTamare; uFares wazala uEsrom; uEsrom wazala uAram;', 'XHO22'),
(44, 1, 4, 'uAram wazala uAminadabhi; uAminadabhi wazala uNashon; uNashon wazala uSalmon;', 'XHO22'),
(44, 1, 5, 'uTalmon wazala uBhohazi ngoRakabhi; uBhohazi wazala uObhedi ngoRute; uObhedi wazala uYese;', 'XHO22'),
(44, 1, 6, 'uYese wazala uDavide ukumkani; uDavide ukumkani wazala uSolomon ngomfazi kaUriya;', 'XHO22'),
(44, 1, 7, 'uSolomon wazala uRehobhowam; uRehobhoham wazala uAbhiya; uAbhiya wazala uAsa;', 'XHO22'),
(44, 1, 8, 'uAsa wazala uYoshafati; uYoshafati wazala uYoram; uYoram wazala u-Oziya;', 'XHO22'),
(44, 1, 9, 'u-Oziya wazala uYotam; uYotam wazala uAhazi; uAhazi wazala uHezekiya;', 'XHO22'),
(44, 1, 10, 'uHezekiya wazala uManase; uManase wazala uAmon; uAmon wazala uYosiya;', 'XHO22'),
(44, 1, 11, 'uYosiya wazala uYekoniya nabazalwana bakhe, ngexesha lokufuduselwa kwabo eBhabhilon.', 'XHO22'),
(44, 1, 12, 'Emva kokufuduselwa kwabo eBhabhilon, uYekoniya wazala uSalatiyeli; uSalatiyeli wazala uTsorubhabheli;', 'XHO22'),
(44, 1, 13, 'uZorobhabheli wazala uAbhihudi; uAbhihudi wazala uEliyakim; uEliyakim wazala uAzore;', 'XHO22'),
(44, 1, 14, 'uAzore wazala uTsadoki; uTsadoki wazala uAkim; uAkim wazala uElihudi;', 'XHO22'),
(44, 1, 15, 'uElihudi wazala uElazare; uElazare wazala uMatan; uMatan wazala uYakobi;', 'XHO22'),
(44, 1, 16, 'uYakobi wazala uYosefu indoda kaMariya, ekuthe ngaye kwazalwa uYesu, ekuthiwa nguKristu.', 'XHO22'),
(44, 1, 17, 'Zonke izizukulwana ngoko, ezithabathela kuAbraham zizise kuDavide, zizizukulwana ezilishumi elinazine; nezithabathela kuDavide zizise ekufuduselweni kwabo eBhabhilon, zizizukulwana ezilishumi elinazine; nezithabathela ekufuduselweni kwabo eBhabhilon zizise kuKristu, zizizukulwana ezilishumi elinazine.', 'XHO22'),
(44, 1, 18, 'Ke kaloku ukuzalwa kukaYesu Kristu kwaye kungolu hlobo: Kwathi, akubon’ ukuba unina uMariya useseleganelwe uYosefu, bengekahlangani, wafunyanwa emithi ngoMoya oyiNgcwele.', 'XHO22'),
(44, 1, 19, 'Ke kaloku uYosefu indoda yakhe, eyindoda elilungisa, engathandi ukumvezisa, wanga amala ngasese.', 'XHO22'),
(44, 1, 20, 'Akubon’ ukuba uyazicinga ezi zinto, naso isithunywa seNkosi sibonakala kuye ephupheni, sisithi, Yosefu, nyana kaDavide, musa ukoyika ukumthabathela kuwe uMariya, umfazi wakho; kuba oko kukhawulweyo phakathi kwakhe kungoMoya, uMoya oyiNgcwele.', 'XHO22'),
(44, 1, 21, 'Uya kuzala unyana ke, umbize ngegama elinguYesu; kuba yena eya kubasindisa abantu bakhe ezonweni zabo.', 'XHO22'),
(44, 1, 22, 'Ke kaloku konke oku kwenzeke, ukuze kuzaliseke okwathethwayo yiNkosi ngomprofeti, isithi,', 'XHO22'),
(44, 1, 23, 'Uyabona, intombi le iya kumitha, izale unyana, Bambize ngegama elinguEmanuweli, oko kukuthi ngentetho evakalayo, uThixo unathi.', 'XHO22'),
(44, 1, 24, 'Uthe ke uYosefu, evukile ebuthongweni, wenza ngoko besimmisele ngako isithunywa seNkosi, wamthabathela kuye umkakhe;', 'XHO22'),
(44, 1, 25, 'Akamazi wada wazala unyana wakhe wamazibulo; wamthiya igama elinguYesu.', 'XHO22'),
(44, 2, 1, 'Ke kaloku, akubon’ ukuba uYesu uzelwe eBhetelehem yelakwaYuda, emihleni kaHerode ukumkani, kwabonakala kufika eYerusalem izazi zivela empumalanga, zisithi,', 'XHO22'),
(44, 2, 2, 'Uphi na lo uzelweyo, ukumkani wamaYuda? Kuba sibone inkwenkwezi yakhe empumalanga, size kumnqula.', 'XHO22'),
(44, 2, 3, 'Ezivile ke ezo kumkani uHerode, wakhathazeka, yena nayo yonke iYerusalem naye.', 'XHO22'),
(44, 2, 4, 'Wabahlanganisa ke bonke ababingeleli abakhulu nababhali babantu, wababuza ukuba ubeya kuzalelwa phi na uKristu.', 'XHO22'),
(44, 2, 5, 'Baza bathi kuye, eBhetelehem yelakwaYuda; kuba kubhaliwe ngokunjalo ngomprofeti kwathiwa,', 'XHO22'),
(44, 2, 6, 'Nawe Bhetelehem, mhlaba wakwaYuda, Akunguye nakanye omncinanana phakathi kwabalawuli bakwaYuda, kuba kuwe kuya kuphuma uMlawuli, oya kubalusa abantu bam amaSirayeli.', 'XHO22'),
(44, 2, 7, 'Kwakuba njalo, uHerode, ezibize ngasese izazi ezo, waqondisisa kuzo kakuhle ixesha lokubonakala kwenkwenkwezi leyo;', 'XHO22'),
(44, 2, 8, 'Wazithuma ke eBhetelehem, wathi, Hambani niye nicokise ukubuzisa ngomntwana lowo; xa ke nithe namfumana, ze nindibikele, ukuze nam ndiye kumnqula.', 'XHO22'),
(44, 2, 9, 'Zimvile ke zona ukumkani, zahamba; nantso inkwenkwezi leyo zayibonayo empumalanga izikhokela, yada yaya kuma phezu kwendawo abekuyo umntwana lowo.', 'XHO22'),
(44, 2, 10, 'Zathi ke, zakuyibona inkwenkwezi, zavuya ngovuyo olukhulu.', 'XHO22'),
(44, 2, 11, 'Zingenile endlwini, zambona umntwana enoMariya unina, zawa phantsi, zaqubuda kuye; igolide, nentlaka yokuqhumisa, nemore.', 'XHO22'),
(44, 2, 12, 'Zakuba zihlatyelwe nguThixo ephupheni ukuba zingabuyi ziye kuHerode, zemka ngayimbi indlela ukuya ezweni lakowazo.', 'XHO22'),
(44, 2, 13, 'Zithe xa zimkile ke zona, naso isithunywa seNkosi sibonakala kuYosefu ephupheni, sisithi, Vuka, umthabathe umntwana nonina, usabele eYiputa, ube khona apho ndide ndikuxelele; uya kumfuna umntwana lowo ukuba amtshabalalise.', 'XHO22'),
(44, 2, 14, 'Evukile ke yena, wamthabatha umntwana nonina ebusuku, wemka waya eYiputa.', 'XHO22'),
(44, 2, 15, 'Waba khona apho wada wabhubha uHerode, ukuze kuzaliseke okwathethwayo yiNkosi ngomprofeti isithi, Ndambiza unyana wam ukuba aphume eYiputa.', 'XHO22'),
(44, 2, 16, 'Uthe ke uHerode, akubona ukuba izazi zidlale ngaye, waba nomsindo kunene, wathumela, wababulala bonke abantwana abaseBhetelehem, nakuyo yonke imida yayo, abaminyaka mibini bezelwe nangaphantsi, ngokwesiko lazo. Ixesha awayelibuzile ngenkuthalo kwizilumko.', 'XHO22'),
(44, 2, 17, 'Kwaza kwazaliseka okwathethwayo ngomprofeti uYeremiya, esithi,', 'XHO22'),
(44, 2, 18, 'Kwavakala eRama izwi, isijwili, nesijwili, nesijwili esikhulu, uRakeli elilela abantwana bakhe, engafuni kuthuthuzeleka, ngokuba bengasekho.', 'XHO22'),
(44, 2, 19, 'Uthe ke akuba efile uHerode, sabonakala isithunywa seNkosi sibonakala ephupheni kuYosefu eYiputa.', 'XHO22'),
(44, 2, 20, 'Vuka, umthabathe umntwana nonina, uye ezweni lakwaSirayeli; kuba bafile abo babezonda ubomi bomntwana.', 'XHO22'),
(44, 2, 21, 'Wavuka ke, wamthabatha umntwana nonina, weza ezweni lakwaSirayeli.', 'XHO22'),
(44, 2, 22, 'Uthe ke akuva ukuba uArkelawo ungukumkani kwelakwaYuda esikhundleni sikaHerode uyise, woyika ukuya khona; wathi, ehlatyelwe nguThixo ephupheni, wemka waya kwimimandla yelaseGalili.', 'XHO22'),
(44, 2, 23, 'Ufike wahlala emzini ekuthiwa yiNazarete, ukuze kuzaliseke okwathethwayo ngabaprofeti, kwathiwa, Uya kubizwa ngokuba ungumNazarete.', 'XHO22'),
(44, 3, 1, 'Ngaloo mihla kweza uYohane umbhaptizi, eshumayela entlango yelakwaYuda, esithi,', 'XHO22'),
(44, 3, 2, 'Guqukani, kuba ubukumkani bamazulu busondele.', 'XHO22'),
(44, 3, 3, 'Kuba lo nguye awayethetha yena uIsaya umprofeti, esithi, Izwi lodandulukayo entlango, lisithi, Yilungiseni indlela yeNkosi, Wutyenenezeni umendo wayo.', 'XHO22'),
(44, 3, 4, 'Ke uYohane lowo ingubo yakhe ibiyeyoboya benkamela, enombhinqo wentlonze esinqeni sakhe; kwaye ke ukudla kwakhe kuziinkumbi nobusi basendle.', 'XHO22'),
(44, 3, 5, 'Babephuma ke besiya kuye abaseYerusalem, nabelakwaYuda bonke, nabommandla wonke waseYordan.', 'XHO22'),
(44, 3, 6, 'Babhaptizwa nguye eYordan apho, bezixela izono zabo.', 'XHO22'),
(44, 3, 7, 'Ke kaloku, akubona into eninzi yabaFarisi nabaSadusi besiza ekubhaptizeni kwakhe, wathi kubo, Nzalandini yamarhamba, ngubani na onibonisileyo ukuba niyibaleke ingqumbo ezayo?', 'XHO22'),
(44, 3, 8, 'Velisani ngoko iziqhamo eziyifaneleyo inguquko;', 'XHO22'),
(44, 3, 9, 'Musani ukucinga ukuthi ngaphakathi kwenu, Thina sinobawo uAbraham; kuba ndithi kuni, Unako uThixo kuwo la matye ukumvelisela abantwana uAbraham.', 'XHO22'),
(44, 3, 10, 'Kaloku izembe selibekiwe engcanjini yemithi, ngoko wonke umthi ongavelisi siqhamo sihle, uyagawulwa, uphoswe emlilweni.', 'XHO22'),
(44, 3, 11, 'Mna okunene ndinibhaptizela enguqukweni ngamanzi; kodwa lowo uzayo emva kwam unamandla kunam, ozimbadada ndingakulingeneyo ukuziphatha; yena uya kunibhaptiza ngoMoya oyiNgcwele nangomlilo;', 'XHO22'),
(44, 3, 12, 'Osesandleni sakhe ifolokhwe yakhe isesandleni sakhe, aze asihlambulule isanda sakhe, ayibuthele kuvimba ingqolowa yakhe; Ke wona umququ uya kuwutshisa ngomlilo ongacimiyo.', 'XHO22'),
(44, 3, 13, 'Kuthi ngelo xa afike uYesu eYordan kuYohane, evela eGalili, ukuba abhaptizwe nguye.', 'XHO22'),
(44, 3, 14, 'Kodwa uYohane wayemalela, esithi, Mna kufuneka ndibhaptizwe nguwe nje, uza kum na wena?', 'XHO22'),
(44, 3, 15, 'Uphendule uYesu wathi kuye, Vuma okwakalokunje; kuba kusifanele thina ukwenjenjalo ukuzalisa bonke ubulungisa. Waza wamvumela.', 'XHO22'),
(44, 3, 16, 'Ebhaptiziwe ke uYesu, wenyuka kwaoko emanzini, lasuka lavuleka izulu kuye, wambona uMoya kaThixo esihla njengehobe, esiza phezu kwakhe.', 'XHO22'),
(44, 3, 17, 'Nalo izwi liphuma ezulwini, lisithi, Lo nguNyana wam oyintanda, endikholisiweyo nguye.', 'XHO22'),
(44, 4, 1, 'Waza uYesu wenyuswa nguMoya, wasiwa entlango, ukuba ahendwe nguMtyholi.', 'XHO22'),
(44, 4, 2, 'Akuba ezile ukutya iimini ezimashumi mane nobusuku obumashumi mane, walamba.', 'XHO22'),
(44, 4, 3, 'Efikile ke umhendi kuye, wathi, Ukuba unguNyana kaThixo, yitsho la matye abe zizonka.', 'XHO22'),
(44, 4, 4, 'Waphendula ke yena wathi, Kubhaliwe kwathiwa, Akaphili ngasonka sodwa umntu; uphila ngamazwi onke aphuma emlonyeni kaThixo.', 'XHO22'),
(44, 4, 5, 'uMtyholi wamthabatha wamsa kuwo umzi ongcwele, wammisa eluchochoyini lwetempile.', 'XHO22'),
(44, 4, 6, 'Athi kuye, Ukuba unguye uNyana kaThixo, ziphose ezantsi; kuba kubhaliwe kwathiwa, Uya kuziwisela umthetho ngawe izithunywa zakhe, zaye ziya kukufukula ngezandla, hleze ubetheke ngonyawo lwakho eweni ilitye.', 'XHO22'),
(44, 4, 7, 'Wathi uYesu kuye, Kubhaliwe kwathiwa, Uze ungayihendi iNkosi uThixo wakho.', 'XHO22'),
(44, 4, 8, 'Ubuya uMtyholi amthabathe amse entabeni ephakame kakhulu, ambonise zonke izikumkani zehlabathi, nozuko lwazo;', 'XHO22'),
(44, 4, 9, 'Athi kuye, Zonke ezi zinto ndokunika, ukuba uthe wawa phantsi, waqubuda kum.', 'XHO22'),
(44, 4, 10, 'Waza wathi uYesu kuye, Suka umke, Sathana, kuba kubhaliwe kwathiwa, Uze uqubude eNkosini uThixo wakho, ukhonze yena yedwa.', 'XHO22'),
(44, 4, 11, 'Wasuka ke uMtyholi ukumyeka; nanko kufika izithunywa zezulu zimlungiselela.', 'XHO22'),
(44, 4, 12, 'Ke kaloku, akuva uYesu ukuba uYohane unikelwe entolongweni, wemka waya kwelaseGalili.', 'XHO22'),
(44, 4, 13, 'Esukile eNazarete, weza wahlala eKapernahum, eselunxwemeni lolwandle, emideni yakwaZebhulon neyakwaNafetali;', 'XHO22'),
(44, 4, 14, 'ukuze kuzaliseke okwathethwayo ngoIsaya umprofeti, esithi,', 'XHO22'),
(44, 4, 15, 'Ilizwe lakwaZebhulon, nelizwe lakwaNafetali, ngasendleleni yaselwandle, phesheya kweYordan, eGalili yeentlanga;', 'XHO22'),
(44, 4, 16, 'Abantu abo bahleli ebumnyameni babone ukukhanya okukhulu; nabo babehleli kummandla wethunzi lokufa, ukukhanya bakhanyiselwe.', 'XHO22'),
(44, 4, 17, 'Waqala oko uYesu ukumemeza, esithi, Guqukani, kuba ubukumkani bamazulu busondele.', 'XHO22'),
(44, 4, 18, 'Uthe ke uYesu, ehamba ngaselwandle lwaseGalili wabona abazalwana ababini, uSimon ekuthiwa nguPetros noAndreya, umzalwana wakhe, bephosa umnatha elwandle; kuba babengababambisi beentlanzi.', 'XHO22'),
(44, 4, 19, 'Athi kubo, Ndilandeleni; ndonenza nibe ngababambisi babantu.', 'XHO22'),
(44, 4, 20, 'Bayishiya kwaoko iminatha yabo, bamlandela.', 'XHO22'),
(44, 4, 21, 'Uhambele phambili esuka apho, wabona abanye abazalwana ababini, uYakobi unyana kaZebhedi, noYohane umzalwana wakhe, besemkhombeni noZebhedi uyise, belungisa iminatha yabo; wababiza.', 'XHO22'),
(44, 4, 22, 'Basishiya kwaoko isikhitshana noyise, bamlandela.', 'XHO22'),
(44, 4, 23, 'Waye uYesu elihamba lonke elaseGalili, efundisa ezindlwini zabo zesikhungu, eshumayela iindaba ezilungileyo zobukumkani, ephilisa izifo zonke nezikhuhlane zonke ebantwini.', 'XHO22'),
(44, 4, 24, 'Lwaphuma ke udumo lwakhe lwaya kulo lonke elaseSiriya, babazisa kuye bonke abagulayo ababebanjwe zizifo ngezifo naziintuthumbo ngeentuthumbo, nabaphethwe ziidemon, nabanesithuthwane, nabafe umzimba wabaphilisa.', 'XHO22'),
(44, 4, 25, 'Walandelwa zizihlwele ezininzi zivela eGalili, naseDekapoli, naseYerusalem, nakwelakwaYuda, naphesheya kweYordan.', 'XHO22'),
(44, 5, 1, 'Uthe ke, akuzibona izihlwele ezo, wenyuka intaba; waza akuhlala phantsi, beza kuye abafundi bakhe', 'XHO22'),
(44, 5, 2, 'Wawuvula umlomo wakhe, wabafundisa, esithi,', 'XHO22'),
(44, 5, 3, 'Banoyolo abangamahlwempu ngomoya; ngokuba ubukumkani bamazulu bobabo.', 'XHO22'),
(44, 5, 4, 'Banoyolo abakhedamileyo; ngokuba baya konwatyiswa bona.', 'XHO22'),
(44, 5, 5, 'Banoyolo abanobulali; ngokuba umhlaba baya kuwudla ilifa bona.', 'XHO22'),
(44, 5, 6, 'Banoyolo abalambela banxanelwe ubulungisa; ngokuba baya kuhluthiswa bona.', 'XHO22'),
(44, 5, 7, 'Banoyolo abo banenceba, kuba baya kwenzelwa inceba bona.', 'XHO22'),
(44, 5, 8, 'Banoyolo abahlambulukileyo intliziyo; ngokuba baya kumbona uThixo bona.', 'XHO22'),
(44, 5, 9, 'Banoyolo abaxolisi; ngokuba baya kubizwa ngokuthi bangoonyana bakaThixo bona.', 'XHO22'),
(44, 5, 10, 'Banoyolo abatshutshiswa ngenxa yobulungisa; ngokuba ubukumkani bamazulu bobabo.', 'XHO22'),
(44, 5, 11, 'Ninoyolo nina xa bathe abantu baningcikiva, banitshutshisa, bathetha zonke izinto ezikhohlakeleyo ngani bexoka, ngenxa yam.', 'XHO22'),
(44, 5, 12, 'Vuyani nigcobe kunene; ngokuba umvuzo wenu mkhulu emazulwini; kuba benjenjalo ukubatshutshisa abaprofeti ababekho phambi kwenu.', 'XHO22'),
(44, 5, 13, 'Niyityuwa yehlabathi nina; ke ukuba ityuwa ithe ayaba nasongo, yothiwani na ukuvakaliswa? ayisalungele nto, kukulahlwa phandle, inyathelwe ngabantu.', 'XHO22'),
(44, 5, 14, 'Nina nilukhanyiselo lwehlabathi; Umzi omi phezu kwentaba awunakufihlakala;', 'XHO22'),
(44, 5, 15, 'Kananjalo abantu abasibaneki isibane basibeke phantsi kwesitya; kwaye ibakhanyisela bonke abasendlwini.', 'XHO22'),
(44, 5, 16, 'Malukhanye ngokunjalo ukhanyiselo lwenu phambi kwabantu; ukuze bayibone imisebenzi yenu emihle, bamzukise uYihlo osemazulwini.', 'XHO22'),
(44, 5, 17, 'Ningabi ndize kuchitha umthetho, nokuba ngabaprofeti; andize kuchitha, ndize kuzalisekisa.', 'XHO22'),
(44, 5, 18, 'Kuba inene ndithi kuni, Koda kudlule izulu nehlabathi, kungakhanga kudlule nalinye iganyana nokuba lisuntswana emthethweni, kude kwenzeke konke.', 'XHO22'),
(44, 5, 19, 'Othe ngoko wachitha noko umnye kule mithetho mincinanana, wabafundisa abantu ngokunjalo, kothiwa ungomncinanana ebukumkanini bamazulu; othe wenza wafundisa ngayo, kothiwa ngomkhulu ebukumkanini bamazulu.', 'XHO22'),
(44, 5, 20, 'Kuba ndithi kuni, Ukuba ubulungisa benu abuthanga bugqithe kakhulu kobababhali nabaFarisi, anisayi kukha ningene ebukumkanini bamazulu.', 'XHO22'),
(44, 5, 21, 'Nivile ukuba kwathiwa kumanyange, Uze ungabulali; othe wabulala, woba sisisulu somgwebo;', 'XHO22'),
(44, 5, 22, 'Ke mna ndithi kuni, Wonke umntu ofumana amqumbele umzalwana wakhe, woba sisisulu somgwebo; othe ke kumzalwana wakhe, Ntw’ ingento, woba sisisulu sentlanganiso yamatyala; uya kuba sengozini yesihogo somlilo.', 'XHO22'),
(44, 5, 23, 'Xa sukuba uwusondeza umnikelo wakho esibingelelweni, wathi ulapho wakhumbula ukuba umzalwana wakho unendawo ngakuwe;', 'XHO22'),
(44, 5, 24, 'Sishiye apho isipho sakho phambi kwesibingelelo, uhambe; uxolelana nomzalwana wakho kuqala, wandule ukuza uwusondeze umnikelo wakho.', 'XHO22'),
(44, 5, 25, 'Lungisana kamsinya nokumangaleleyo, usesendleleni naye; hleze ummangaleli lowo akunikele kumgwebi, aze umgwebi akunikele kumsila, uze uphoswe entolongweni.', 'XHO22'),
(44, 5, 26, 'Inene ndithi kuwe, Akusayi kukha uphume khona apho, ude uhlawule nemalanana yokugqibela.', 'XHO22'),
(44, 5, 27, 'Nivile ukuba kwathiwa kumanyange, Uze ungakrexezi.', 'XHO22'),
(44, 5, 28, 'Ke mna ndithi kuni, Wonke umntu okhangela umntu oyinkazana, ukuba amkhanuke, uselemkrexezile entliziyweni yakhe.', 'XHO22'),
(44, 5, 29, 'Ukuba iliso lakho lokunene liyakukhubekisa, linyothule ulilahle kuwe; kuba kukulungele kanye ukuba kutshabalale libe linye emalungwini akho, kunokuba umzimba wakho uphela uphoswe esihogweni.', 'XHO22'),
(44, 5, 30, 'Ukuba ke isandla sakho sokunene siyakukhubekisa, sinqumle usilahle kuwe; kuba kukulungele kanye ukuba kutshabalale libe linye emalungwini akho, kunokuba umzimba wakho uphela uphoswe esihogweni.', 'XHO22'),
(44, 5, 31, 'Kwathiwa ke, Othe wamala umfazi wakhe, makamnike incwadi yokwahlukana.', 'XHO22'),
(44, 5, 32, 'Ke mna ndithi kuni, othe wamala umfazi wakhe ngaphandle kwendawo yombulo, uyamkrexezisa; nothe wazeka owaliweyo, uyakrexeza.', 'XHO22'),
(44, 5, 33, 'Kananjalo nivile ukuba kwathiwa kumanyange, Uze ungafungi;', 'XHO22'),
(44, 5, 34, 'Ke mna ndithi kuni, Maningakhe nifunge nto; nokuba lizulu; ngokuba yitrone kaThixo;', 'XHO22'),
(44, 5, 35, 'Nangomhlaba; nokuba lisihlalo seenyawo zakhe; ngokuba ngumzi woKumkani omkhulu.', 'XHO22'),
(44, 5, 36, 'Uze ungayifungi nentloko yakho, ngokuba ungenamandla okwenza nalunye unwele lube mhlophe okanye lube mnyama.', 'XHO22'),
(44, 5, 37, 'Ilizwi lenu ke malibe nguewe, ewe; Hayi, hayi; kuba okungaphezulu koko kuvela kongendawo.', 'XHO22'),
(44, 5, 38, 'Nivile ukuba kwathiwa, Iliso maliphindezelwe ngeliso, nezinyo ngezinyo.', 'XHO22'),
(44, 5, 39, 'Ke mna ndithi kuni, maningaxhali nongendawo; kodwa othe wakubetha esidleleni sakho sokunene, mguqulele nesinye.', 'XHO22'),
(44, 5, 40, 'Nalowo ufuna ukukumangalela, akuhluthe ingubo yangaphantsi, myekele neyokwaleka.', 'XHO22'),
(44, 5, 41, 'Nosukuba ekunyanzelela imayile enye, hamba naye zibe mbini.', 'XHO22'),
(44, 5, 42, 'Mphe lowo ukucelayo, nalowo ufuna ukuboleka kuwe ungamnikeli umva.', 'XHO22'),
(44, 5, 43, 'Nivile ukuba kwathiwa, Uze umthande ummelwane wakho, uluthiye utshaba lwakho.', 'XHO22'),
(44, 5, 44, 'Ke mna ndithi kuni, Zithandeni iintshaba zenu, basikeleleni abo baniqalekisayo, benzeleni okulungileyo abo banithiyayo, nibathandazele abo banigxekayo banitshutshise;', 'XHO22'),
(44, 5, 45, 'ukuze nibe ngabantwana boYihlo osemazulwini; ngokuba yena elenza ilanga lakhe liphume phezu kwabakhohlakeleyo nabalungileyo, nemvula eyinisa phezu kwabalungisayo nabangalungisiyo.', 'XHO22'),
(44, 5, 46, 'Ukuba nithanda abo banithandayo, ninamvuzo mni na? Abenzi kwaloo nto na nababuthi berhafu?', 'XHO22'),
(44, 5, 47, 'Naxa nithe nabulisa abazalwana benu bodwa, yintoni na egqithiseleyo eniyenzayo? Abenzi benjenjalo na nababuthi berhafu?', 'XHO22'),
(44, 5, 48, 'Ngoko yibani ngabafezekileyo, njengokuba uYihlo osemazulwini efezekile.', 'XHO22'),
(44, 6, 1, 'Lumkani, ubulungisa benu ningabenzeli ebusweni babantu, ngokunga ningabonwa ngabo; okanye, aninamvuzo kuYihlo osemazulwini.', 'XHO22'),
(44, 6, 2, 'Ngoko ke, xa sukuba uliza abantu, ungahlokomisi xilongo phambi kwakho, njengoko benza ngako abahanahanisi ezindlwini zesikhungu, nasezitratweni, ukuze bazukiswe ngabantu. Inene ndithi kuni, Bawufincile umvuzo wabo.', 'XHO22'),
(44, 6, 3, 'Ke wena, xa uthe wasisa, masingakwazi isandla sakho sokhohlo into esiyenzayo esokunene;', 'XHO22'),
(44, 6, 4, 'ukuze amalizo akho abe semfihlekweni, aze uYihlo obona emfihlekweni akubuyisele ekuhleni.', 'XHO22'),
(44, 6, 5, 'Naxa sukuba uthandaza, uze ungabi njengabahanahanisi; ngokuba bethanda ukuthandaza bemi ezindlwini zesikhungu nasezimbombeni zezitrato, ngokokuze babe nokubonwa ngabantu. Inene ndithi kuni, Bawufincile umvuzo wabo.', 'XHO22'),
(44, 6, 6, 'Ke wena, xa sukuba uthandaza, ngena egumbini lakho, uthi, uluvalile ucango lwakho, uthandaze kuYihlo osemfihlekweni; aze uYihlo obona emfihlekweni akubuyisele ekuhleni.', 'XHO22'),
(44, 6, 7, 'Nithandaza njalo ke, maningenzi milembelele ingeyanto, njengabeentlanga; kuba beba boviwa ngenxa yamazwi abo amaninzi.', 'XHO22'),
(44, 6, 8, 'Ngoko maningafani nabo; kuba ekwazi uYihlo enikusweleyo, ningekamceli.', 'XHO22'),
(44, 6, 9, 'Thandazani ngoko nina nenjenje: Bawo wethu osemazulwini, malingcwaliswe igama lakho.', 'XHO22'),
(44, 6, 10, 'Mabufike ubukumkani bakho. Makwenziwe ukuthanda kwakho nasemhlabeni, njengokuba kusenziwa emazulwini.', 'XHO22'),
(44, 6, 11, 'Siphe namhla isonka sethu semihla ngemihla.', 'XHO22'),
(44, 6, 12, 'Usixolele amatyala ethu, njengokuba nathi sibaxolela abanamatyala kuthi.', 'XHO22'),
(44, 6, 13, 'Ungasingenisi ekuhendweni, kodwa usihlangule ebubini. Kaloku ubukumkani bubobakho, namandla, nozuko, kuse emaphakadeni asemaphakadeni. Amen.', 'XHO22'),
(44, 6, 14, 'Kuba xa nithi nibaxolele abantu izigqitho zabo, uYihlo osemazulwini wonixolela nani.', 'XHO22'),
(44, 6, 15, 'Ukuba anibaxoleli abantu izigqitho zabo, noYihlo ke akayi kunixolela iziphoso zenu.', 'XHO22'),
(44, 6, 16, 'Xa sukuba ke nizila ukudla, maningabi njengabahanahanisi, bona bamatshekileyo; kuba babenza bubi ubuso babo, ukuze babonakale ebantwini ukuba bazilile. Inene ndithi kuni, Bawufincile umvuzo wabo.', 'XHO22'),
(44, 6, 17, 'Ke wena, xa uzila ukudla, yithambise intloko yakho, ubuhlambe ubuso bakho;', 'XHO22'),
(44, 6, 18, 'ukuze ungabonakali ebantwini ukuba uzilile, ubonakale kuYihlo osemfihlekweni; aze uYihlo obona emfihlekweni akubuyisele ekuhleni.', 'XHO22'),
(44, 6, 19, 'Musani ukuziqwebela ubutyebi emhlabeni, apho konakalisa inundu nomhlwa, nalapho amasela agqobhozayo ebe;', 'XHO22'),
(44, 6, 20, 'ziqwebeleni ke ubutyebi emazulwini, apho kungonakalisi nundu namhlwa, nalapho amasela angagqobhoziyo ebe.', 'XHO22'),
(44, 6, 21, 'Kuba apho bukhona ubutyebi benu, yoba lapho nentliziyo yenu.', 'XHO22'),
(44, 6, 22, 'Isibane somzimba liliso; xa sukuba iliso lakho ngoko lingenakumbi, umzimba wakho uphela wokhanyiseleka.', 'XHO22'),
(44, 6, 23, 'Kodwa xa sukuba iliso lakho likhohlakele, umzimba wakho uphela woba bubumnyama. Ukuba ngoko ukukhanya okukuwe kububumnyama, bungakanani na ubumnyama obo!', 'XHO22'),
(44, 6, 24, 'Akukho mntu unako ukukhonza nkosi mbini; kuba eya kuyithiya le, athande leya; athi mhlawumbi abambelele kule, ayidele leya; Aninako ukukhonza uThixo nobutyebi.', 'XHO22'),
(44, 6, 25, 'Ngenxa yoko ndithi kuni, Musani ukubuxhalela ubomi benu, nisithi nodla ntoni na; nosela ntoni na; kwanomzimba wenu, nisithi niya kwambatha ntoni na. Ubomi abungaphezulu na kuko ukudla, nomzimba awungaphezulu na kwinto yokwambatha?', 'XHO22'),
(44, 6, 26, 'Yondelani ezintakeni zasezulwini; ngokuba azihlwayeli, azivuni, azibutheli koovimba; ukanti uYihlo osemazulwini uyazondla. Anizigqithile ngokugqithileyo na?', 'XHO22'),
(44, 6, 27, 'Nguwuphi na ke kuni onokuthi ngokuxhala ongeze ebukhulwini bakhe ikubhite ibe nye?', 'XHO22'),
(44, 6, 28, 'Kanjalo yini na ukuba nixhalele into yokwambatha? Qondani ngeenyibiba zasendle, ukuhluma kwazo; azibulaleki, azisonti nokusonta;', 'XHO22'),
(44, 6, 29, 'Ke ndithi kuni, noSolomon kubo bonke ubunewunewu bakhe, wayengavathiswanga nanjenganye kuzo ezi.', 'XHO22'),
(44, 6, 30, 'Ukuba ke ingca, leyo isendle, ethi namhla ibekho, ize ngomso iphoswe eziko, wenjenjalo uThixo ukuyambathisa, akayi kunambathisa kakhulu ngakumbi na, bantundini balukholo luncinane?', 'XHO22'),
(44, 6, 31, 'Musani ukuxhala ngoko, nisithi, Sodla ntoni na? sosela ntoni na? okanye, Siya kwambatha ntoni na?', 'XHO22'),
(44, 6, 32, 'Kuba zonke ezo zinto zingxanyelwe ziintlanga; kuba uYihlo osemazulwini uyazi ukuba ezo zinto zonke ziyafuneka kuni.', 'XHO22'),
(44, 6, 33, 'Funani ke tanci ubukumkani bukaThixo nobulungisa bakhe, zaye zonke ezo zinto ziya kongezelelwa kuni.', 'XHO22'),
(44, 6, 34, 'Ngoko musani ukuxhalela ingomso; kuba ingomso liya kuxhalela okwalo. Buyanele imini leyo ububi bayo.', 'XHO22'),
(44, 7, 1, 'Musani ukugweba, ukuze ningagwetywa.', 'XHO22'),
(44, 7, 2, 'Kuba ngogwebo enigweba ngalo, niya kugwetywa kwangalo nani; nangomlinganiselo enilinganisela ngawo, niya kulinganiselwa kwangawo nani.', 'XHO22'),
(44, 7, 3, 'Yini na ke ukuba usibone isibi esisesweni lomzalwana wakho, kanti wona umqadi okwelakho iliso akuwuqiqi?', 'XHO22'),
(44, 7, 4, 'Okanye ungáthini na ukuthi kumzalwana wakho, Yeka ndisikhuphe isibi esweni lakho; kanti nanku umqadi kwelakho iliso?', 'XHO22'),
(44, 7, 5, 'Mhanahanisindini, wukhuphe kuqala umqadi kwelakho iliso; wandule ukubona kakuhle ukusikhupha isibi eso esweni lomzalwana wakho.', 'XHO22'),
(44, 7, 6, 'Musani ukukunika izinja okungcwele, kanjalo ningaziphosi iiperile zenu phambi kweehagu, hleze zizinyathele ngamanqina azo, zijike ziniqwenge.', 'XHO22'),
(44, 7, 7, 'Celani, nophiwa; funani, nofumana; nkqonkqozani, novulelwa;', 'XHO22'),
(44, 7, 8, 'Kuba wonke umntu ocelayo, uyazuza; nalowo ufunayo, uyafumana; nalowo unkqonkqozayo uya kuvulelwa.', 'XHO22'),
(44, 7, 9, 'Nguwuphi na umntu kuni ongathi, ukuba unyana wakhe uthe wacela isonka, amnike ilitye?', 'XHO22'),
(44, 7, 10, 'Athi, ukuba uthe wacela intlanzi, amnike inyoka?', 'XHO22'),
(44, 7, 11, 'Ukuba ke ngoko nina, nikhohlakele nje, niyakwazi ukubapha abantwana benu izipho ezilungileyo, wobeka phi na yena uYihlo osemazulwini ukugqithisela ngokubapha okulungileyo abo bamcelayo?', 'XHO22'),
(44, 7, 12, 'Zonke izinto ngoko enisukuba ninga bangazenza abantu kuni, yenzani ezikwanjalo nani kubo; kuba oko kungumthetho nabaprofeti.', 'XHO22'),
(44, 7, 13, 'Ngenani ngesango elimxinwa; ngokuba libanzi isango, iphangalele nendlela, esa entshabalalweni, baninzi ke abangena ngalo.', 'XHO22'),
(44, 7, 14, 'Ngokuba limxinwa isango, icuthene nendlela, esa ebomini; bambalwa ke abalifumanayo.', 'XHO22'),
(44, 7, 15, 'Balumkeleni abaprofeti ababuxoki, abeza kuni ngezambatho zeegusha, ngaphakathi ke beziingcuka eziqwengayo.', 'XHO22'),
(44, 7, 16, 'Nobaqonda ngeziqhamo zabo. Ngaba abantu bakha iidiliya emithaneni enameva okanye amakhiwane enkunzaneni?', 'XHO22'),
(44, 7, 17, 'Ngokunjalo wonke umthi olungileyo uvelisa iziqhamo ezihle; ke wona umthi ongenguwo uvelisa iziqhamo ezibi,', 'XHO22'),
(44, 7, 18, 'umthi olungileyo awunakuvelisa ziqhamo zibi, nomthi ongenguwo awunakuvelisa ziqhamo zihle.', 'XHO22'),
(44, 7, 19, 'Wonke umthi ongavelisi ziqhamo zihle uyagawulwa, uphoswe emlilweni.', 'XHO22'),
(44, 7, 20, 'Ngoko ke nobaqonda ngeziqhamo zabo.', 'XHO22'),
(44, 7, 21, 'Asingabo bonke abathi kum, Nkosi, Nkosi, abaya kungena ebukumkanini bamazulu; kodwa ngulowo wenza ukuthanda kukaBawo osemazulwini.', 'XHO22'),
(44, 7, 22, 'Baninzi abaya kuthi kum ngaloo mini, Nkosi, Nkosi, sasingathe na egameni lakho? Sazikhupha na iidemon egameni lakho? Nenze egameni lakho imisebenzi emininzi ebalulekileyo?', 'XHO22'),
(44, 7, 23, 'Ndandule ndibaxelele, ndithi, Andizanga ndinazi; mkani kum, nina nenza okuchasene nomthetho.', 'XHO22'),
(44, 7, 24, 'Ngoko ke wonke umntu owevayo la mazwi am, awenze, uya kufaniswa nendoda eyingqondi, yona yayakhelayo elulwalweni indlu yayo.', 'XHO22'),
(44, 7, 25, 'Yehla imvula, yeza imilambo, yavuthuza imimoya, yagaleleka kuloo ndlu; ayawa, kuba ibisekelwe elulwalweni.', 'XHO22'),
(44, 7, 26, 'Nomntu wonke owevayo la mazwi am, angawenzi, uya kufaniswa nendoda esisiyatha, yona yayakhelayo entlabathini indlu yayo.', 'XHO22'),
(44, 7, 27, 'Yehla imvula, yeza imilambo, yavuthuza nemimoya, yagaleleka kuloo ndlu; yawa; saba sikhulu ukuwa kwayo.', 'XHO22'),
(44, 7, 28, 'Ke kaloku kwathi, xeshikweni uYesu ebewagqibile la mazwi, zakhwankqiswa izihlwele yimfundiso yakhe;', 'XHO22'),
(44, 7, 29, 'kuba ebebafundisa njengonegunya, engafundisi njengabo ababhali.', 'XHO22'),
(44, 8, 1, 'Ke kaloku, akuhla entabeni apho, walandelwa ngabantu abaninzi.', 'XHO22'),
(44, 8, 2, 'Nanko kusiza oneqhenqa, waqubuda kuye, esithi, Nkosi, ukuba uyathanda, unako ukundihlambulula.', 'XHO22'),
(44, 8, 3, 'Wasolula isandla uYesu, wamchukumisa, esithi, Ndiyathanda; Hlambuluka. Lahlambuluka kwaoko iqhenqa lakhe.', 'XHO22'),
(44, 8, 4, 'Athi uYesu kuye, Lumka ungaxeleli bani; hamba uye kuzibonisa kumbingeleli, usondeze umnikelo awamisa wona uMoses, ube bubungqina kubo.', 'XHO22'),
(44, 8, 5, 'Ke kaloku, akubon’ ukuba uYesu ungene eKapernahum, kweza kuye umthetheli-khulu, embongoza, esithi,', 'XHO22'),
(44, 8, 6, 'esithi, Nkosi, isicaka sam silele ekhaya, sinedumbe, sithuthunjelwa ngokunzulu.', 'XHO22'),
(44, 8, 7, 'Athi uYesu kuye, Ndoza ndisiphilise mna.', 'XHO22'),
(44, 8, 8, 'Waphendula umthetheli-khulu, wathi, Nkosi, andikulingene ukuba ude ungene phantsi kophahla lwam; thetha ilizwi lodwa, sophiliswa isicaka sam.', 'XHO22'),
(44, 8, 9, 'Kuba nam ndingumntu ophantsi kwegunya labanye, ndinamasoldati phantsi kwam; ndithi kweli, Yiya, liye; ndithi kweliya, Yiza, lize; nakumkhonzi wam, Yenza oku, akwenze.', 'XHO22'),
(44, 8, 10, 'Evile ke uYesu wamangaliswa, wathi kwabamlandelayo, Inene ndithi kuni, andifumananga lukholo lungaka nakwaSirayeli.', 'XHO22'),
(44, 8, 11, 'Ndithi ke kuni, baninzi abaya kuza bevela empumalanga nasentshonalanga, bangqengqe ndawonye noAbraham noIsake noYakobi ebukumkanini bamazulu.', 'XHO22'),
(44, 8, 12, 'Kodwa bona abantwana bobukumkani baya kukhutshelwa emnyameni ongaphandle; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 8, 13, 'Wathi uYesu kumthetheli-khulu, Hamba; makwenzeke kuwe njengoko ukholiwe. Saphiliswa kwangelo lixa isicaka sakhe.', 'XHO22'),
(44, 8, 14, 'Efikile uYesu kwaPetros, wabona umkhwekazi wakhe elele, egula ngumkhuhlane.', 'XHO22'),
(44, 8, 15, 'Wachukumisa isandla sakhe, yamyeka icesina, wavuka, wabalungiselela.', 'XHO22'),
(44, 8, 16, 'Kuthe ke kwakuhlwa, kwaziswa kuye into eninzi yabaphethwe ziidemon; wabakhupha oomoya abo ngelizwi, wabaphilisa bonke ababesifa;', 'XHO22'),
(44, 8, 17, 'ukuze kuzaliseke okwathethwayo ngoIsaya umprofeti, esithi, Yena ngokwakhe wabuthabatha ubulwelwe bethu, wazithwala izifo zethu.', 'XHO22'),
(44, 8, 18, 'Uthe ke uYesu, akubona abantu abaninzi bemphahlile, wathi makumkiwe kuyiwe ngaphesheya.', 'XHO22'),
(44, 8, 19, 'Kweza mbhali uthile, wathi kuye, Mfundisi, ndiya kukulandela apho ungaya khona.', 'XHO22'),
(44, 8, 20, 'Athi uYesu kuye, Iimpungutye zinemingxuma, neentaka zezulu zineendawo zokuhlala; yena uNyana woMntu akanayo nendawo angalalisa kuyo intloko.', 'XHO22'),
(44, 8, 21, 'Uthe ke omnye wabafundi bakhe kuye, Nkosi, ndivumele ndiye kungcwaba ubawo kuqala.', 'XHO22'),
(44, 8, 22, 'Wathi ke uYesu kuye, Ndilandele; bayeke abafileyo bangcwabe abafileyo babo.', 'XHO22'),
(44, 8, 23, 'Engenile ke emkhombeni, bamlandela abafundi bakhe.', 'XHO22'),
(44, 8, 24, 'Kwasuka kwabakho uqhwithela olukhulu elwandle, ngokokude umkhombe ugutyungelwe ngamaza; waye ke yena elele ubuthongo.', 'XHO22'),
(44, 8, 25, 'Beza abafundi bakhe bamvusa, besithi, Nkosi, sisindise, siyatshabalala.', 'XHO22'),
(44, 8, 26, 'Athi kubo, Yini na ukuba nibe ngamagwala, bantundini balukholo luncinane? Waza wavuka, wayikhalimela imimoya nolwandle; kwabakho ukuzola okukhulu.', 'XHO22'),
(44, 8, 27, 'Bamangaliswa ke abantu abo, besithi, Ngumntu mni na lo, le nto alulanyelwayo nayimimoya nalulwandle?', 'XHO22'),
(44, 8, 28, 'Kuthe akufika ngaphesheya, ezweni lamaGergasi, kwaqubisana naye abantu ababini abaphethwe ziidemon, bephuma emangcwabeni, belugcalagcala, kwada kwayinkohla ukuba kudlule umntu ngaloo ndlela.', 'XHO22'),
(44, 8, 29, 'Basuka bakhala besithi, Yintoni na enathi nawe, Yesu, Nyana kaThixo? Apha uze kusithuthumbisa na phambi kwelona xesha?', 'XHO22'),
(44, 8, 30, 'Bekukho ke mgama kubo ugxuba olukhulu lweehagu lusidla.', 'XHO22'),
(44, 8, 31, 'Zaye ke iidemon zimbongoza, zisithi, Ukuba uyasikhupha, sivumele siye kungena elugxubeni lweehagu olo.', 'XHO22'),
(44, 8, 32, 'Wathi kuwo, Hambani. Zithe ke zakuphuma, zaya kungena elugxubeni lweehagu; lwasuka lonke ugxuba lweehagu lwaphaphatheka, lwehla eweni, lwela elwandle; zatshabalala emanzini.', 'XHO22'),
(44, 8, 33, 'Bathe ke abo babezalusa basaba, baya ekhaya, babika into yonke, kwanendawo yababephethwe ziidemon.', 'XHO22'),
(44, 8, 34, 'Wasuka wonke umzi waphuma, waya kumkhawulela uYesu; bathi bakumbona, bambongoza ukuba emke emideni yabo.', 'XHO22'),
(44, 9, 1, 'Engenile ke emkhombeni, wawela, weza kowabo.', 'XHO22'),
(44, 9, 2, 'Ke kaloku bazisa kuye umntu onedumbe, elele elukhukweni. uYesu, elubona ukholo lwabo, wathi konedumbe; Nyana, yomelela; zixolelwe izono zakho.', 'XHO22'),
(44, 9, 3, 'Kwabonakala kukho abathile bakubachazi-mthetho phakathi kwabo, besithi, Lo uyanyelisa;', 'XHO22'),
(44, 9, 4, 'uYesu, ezibona iingcingane zabo, wathi, Yini na ukuba nicinge izinto ezimbi ezintliziyweni zenu?', 'XHO22'),
(44, 9, 5, 'Kuba kukuphi na okulula ngakumbi, ukuthi, Zixolelwe izono zakho; nokuthi, Vuka, uhambe?', 'XHO22'),
(44, 9, 6, 'Kodwa ukuze nazi ukuba unalo igunya uNyana woMntu lokuxolela izono emhlabeni apha (selesithi konedumbe), Vuka, uthabathe ukhuko lwakho, uye endlwini yakho.', 'XHO22'),
(44, 9, 7, 'Wavuka ke, wemka waya endlwini yakhe.', 'XHO22'),
(44, 9, 8, 'Zikubonile ke oko izihlwele, zamangaliswa, zamzukisa uThixo, obanike abantu igunya elingaka.', 'XHO22'),
(44, 9, 9, 'Edlula apho, uYesu wabona umntu ehleli endaweni yerhafu, okuthiwa nguMateyu, athi kuye, Ndilandele. Wesuka ke, wamlandela.', 'XHO22'),
(44, 9, 10, 'Kwathi, akubon’ ukuba uhleli ngasesithebeni endlwini, kwabonakala into eninzi yababuthi berhafu naboni, yeza yahlala ndawonye noYesu nabafundi bakhe.', 'XHO22'),
(44, 9, 11, 'Bakukubona oko abaFarisi, bathi kubafundi bakhe, Kungani na ukuba umfundisi wenu adle nababuthi berhafu naboni?', 'XHO22'),
(44, 9, 12, 'Evile ke uYesu, wathi kubo, Akufuneki gqirha kwabaphilileyo, lifuneka kwabafayo.', 'XHO22'),
(44, 9, 13, 'Khaniye nifunde ukuba kukuthini na ukuthi, Ndithanda inceba, andithandi mbingelelo; kuba andize kubiza malungisa, ndize kubiza aboni ukuba baguquke.', 'XHO22'),
(44, 9, 14, 'Beza kuye abafundi bakaYohane, besithi, Kungani na ukuba sizile ukudla kaninzi, thina nabaFarisi, kanti bona abafundi bakho abakuzili?', 'XHO22'),
(44, 9, 15, 'Wathi uYesu kubo, Banako yini na abegumbi lomtshakazi ukukhedama, lo gama umyeni asenabo? Kodwa kuya kufika mihla awothi asuswe kubo umyeni, bandule ke ukuzila.', 'XHO22'),
(44, 9, 16, 'Akukho ke ubeka isiziba somchako omtsha engutyeni endala; kuba eso sifakiweyo siyakrazula engutyeni, sibe sibi ngokunye isikroba.', 'XHO22'),
(44, 9, 17, 'Kanjalo ayithiwa iwayini entsha ezintsubeni ezindala; kwenjiwe njalo, zingagqabhuka iintsuba, iphalale iwayini, zitshabalale neentsuba; yona iwayini entsha ithiwa ezintsubeni ezintsha, zigcinakale ezo nto zombini.', 'XHO22'),
(44, 9, 18, 'Esathetha ezi zinto kubo, nanko kusiza umphathi, waqubuda kuye, esithi, Intombi yam ibhubhe ngoku; yiza ubeke isandla sakho phezu kwayo, yophila.', 'XHO22'),
(44, 9, 19, 'Wesuka uYesu wamlandela, kwanabafundi bakhe.', 'XHO22'),
(44, 9, 20, 'Nanko kusiza ntokazi ithile, eyayinethombo legazi iminyaka elishumi elinamibini; yafika ngasemva yachukumisa umqukumbelo wengubo yakhe;', 'XHO22'),
(44, 9, 21, 'kuba ibisithi ngaphakathi kwayo, Ukuba ndithe ndachukumisa ingubo yakhe nje kodwa, ndophiliswa.', 'XHO22'),
(44, 9, 22, 'Wajika uYesu, wayibona, wathi, Ntombi yam, yomelela; ukholo lwakho lukusindisile. Yaphiliswa umfazi kwangelo lixa.', 'XHO22'),
(44, 9, 23, 'Uthe akufika uYesu endlwini yomphathi, wazibona iingwali nabantu abaninzi bephithizela;', 'XHO22'),
(44, 9, 24, 'Athi kubo, Khwelelani; kuba intombazana ayifile, ilele. Basuka bamwa ngentsini.', 'XHO22'),
(44, 9, 25, 'Uthe ke bakukhutshelwa phandle abantu, wangena, wayibamba ngesandla, yaza yavuka intombazana leyo.', 'XHO22'),
(44, 9, 26, 'Lwaza lwaphuma udumo lwale nto, lwaya kuwo wonke loo mhlaba.', 'XHO22'),
(44, 9, 27, 'Kuthe ke akuba edlula apho uYesu, walandelwa ziimfama ezimbini, zidanduluka, zisithi, Senzele inceba, nyana kaDavide.', 'XHO22'),
(44, 9, 28, 'Zithe, akungena endlwini, zeza kufika kuye iimfama, athi kuzo uYesu, Niyakholwa na ukuba ndinako ukukwenza oko? Bathi kuye, Ewe, Nkosi.', 'XHO22'),
(44, 9, 29, 'Wandula ukuwachukumisa amehlo azo, esithi, Ngokokholo lwenu makwenzeke kuni.', 'XHO22'),
(44, 9, 30, 'Aqabuka amehlo abo; Waza uYesu waziyala kalukhuni, esithi, Lumkani ingaziwa mntu le nto.', 'XHO22'),
(44, 9, 31, 'Kodwa zona zaphuma, zalubhengeza udumo lwakhe kuwo wonke loo mmandla.', 'XHO22'),
(44, 9, 32, 'Ke kaloku zakubon’ ukuba ziyaphuma, kwabonakala kuziswa kuye umntu osisidenge, ophethwe yidemon.', 'XHO22'),
(44, 9, 33, 'Yakukhutshelwa ke indimoni, sathetha isisidenge, zamangaliswa izihlwele, zathi, Ayizanga ibonwe into enjalo kwaSirayeli.', 'XHO22'),
(44, 9, 34, 'Kodwa bona abaFarisi bathi, Ukhupha iindimoni ngomphathi weendimoni.', 'XHO22'),
(44, 9, 35, 'Waye uYesu eyihamba yonke imizi nemizana, efundisa ezindlwini zabo zesikhungu, eshumayela iindaba ezilungileyo zobukumkani, ephilisa izifo zonke nezikhuhlane zonke ebantwini.', 'XHO22'),
(44, 9, 36, 'Uthe ke, akuzibona izihlwele, wasikwa yimfesane zizo, ngokuba zibe zityhafile, zichithakele ngokwezimvu ezingenamalusi.', 'XHO22'),
(44, 9, 37, 'Aze athi kubafundi bakhe, Ukuvuna okunene kuninzi, kodwa bona abasebenzi bambalwa.', 'XHO22'),
(44, 9, 38, 'Khungani ngoko eNkosini yokuvuna, ikhuphe abasebenzi, baye ekuvuneni kwayo.', 'XHO22'),
(44, 10, 1, 'Waza wababizela kuye abafundi bakhe abalishumi elinababini, wabanika igunya koomoya abangcolileyo, ukuze babakhuphe, nelokuphilisa izifo nezikhuhlane zonke.', 'XHO22'),
(44, 10, 2, 'Ngawo la ke amagama abapostile abalishumi elinababini; Owokuqala ibinguSimon ekuthiwa nguPetros, noAndreya umzalwana wakhe; uYakobi kaZebhedi, noYohane umzalwana wakhe;', 'XHO22'),
(44, 10, 3, 'uFilipu noBhartolomeyu; uTomas, noMateyu, umbuthi werhafu; uYakobi ka-Alifeyu, noLebheyu, ogama limbi linguTadeyu;', 'XHO22'),
(44, 10, 4, 'uSimon umKanan noYuda Sikariyoti, lowo wamngcatshayo.', 'XHO22'),
(44, 10, 5, 'Aba balishumi elinababini wabathuma uYesu, ebawisela umthetho, esithi, Ze ningasingi endleleni yeentlanga, ningangeni nasemzini wamaSamariya;', 'XHO22'),
(44, 10, 6, 'Kodwa yiyani kanye kwizimvu ezilahlekileyo zendlu kaSirayeli.', 'XHO22'),
(44, 10, 7, 'Ekubeni nihamba, vakalisani, nithi, Ubukumkani bamazulu busondele.', 'XHO22'),
(44, 10, 8, 'Philisani imilwelwe, hlambululani abaneqhenqa, vusani abafileyo, khuphani iidemon; namkele ngesisa, yiphani ngesisa.', 'XHO22'),
(44, 10, 9, 'Ningaphathi golide nasilivere nalubhedu emibhinqweni yenu;', 'XHO22'),
(44, 10, 10, 'nangxowa yandlela, nangubo zangaphantsi ngambini, nambadada, namsimelelo; kuba umsebenzi ufanelwe kukudla kwakhe.', 'XHO22'),
(44, 10, 11, 'Emzini ke enithe nangena kuwo, nokuba kusemzaneni, ize nibuzise ukuba ngubani na kuwo ofanelekileyo; hlalani khona apho nide nimke khona.', 'XHO22'),
(44, 10, 12, 'Xa ningena endlwini, ze nibabulise.', 'XHO22'),
(44, 10, 13, 'Ukuba ke ifanelekile indlu leyo, malube phezu kwayo uxolo lwenu; ukuba ayifanelekile, uxolo lwenu malubuyele kwakuni.', 'XHO22'),
(44, 10, 14, 'Nothe akawamkela, akaweva amazwi enu, nakuphuma kuloo ndlu nakuloo mzi, vuthululani uthuli lweenyawo zenu.', 'XHO22'),
(44, 10, 15, 'Inene ndithi kuni, Koba nokunyamezeleka okomhlaba waseSodom nowaseGomora ngomhla womgwebo, kunokwaloo mzi.', 'XHO22'),
(44, 10, 16, 'Niyabona, mna ndinithuma njengezimvu phakathi kweengcuka; ngoko yibani nobulumko njengeenyoka, nibe njengamahobe ukungabi nabuqhinga.', 'XHO22'),
(44, 10, 17, 'Balumkeleni ke abantu; kuba baya kuninikela ezintlanganisweni zamatyala, banityakatye ezindlwini zabo zesikhungu;', 'XHO22'),
(44, 10, 18, 'niya kusiwa phambi kweerhuluneli nookumkani ngenxa yam, ze kube bubungqina kubo, nakuzo iintlanga.', 'XHO22'),
(44, 10, 19, 'Xa sukuba ke beninikela, ize ningaxhaleli ukuba nothetha ngakuphi na, nokuba nothetha ntoni na, kuba niya kuyinikwa ngelo lixa into nothetha ngayo.', 'XHO22'),
(44, 10, 20, 'Kaloku asinini abathethayo, nguMoya kaYihlo othethayo ngaphakathi kwenu.', 'XHO22'),
(44, 10, 21, 'Ke kaloku umzalwana uya kunikela umzalwana ekufeni, aze uyise anikele umntwana, bathi nabantwana bavukelane nabazali, bababulalise;', 'XHO22'),
(44, 10, 22, 'niya kuthiywa ngabantu bonke ngenxa yegama lam; ke onyamezelayo kwada kwaba kwasekupheleni, uya kusindiswa yena.', 'XHO22'),
(44, 10, 23, 'Xa sukuba ke benitshutshisa kuloo mzi, sabelani kuwumbi; kuba inene ndithi kuni, Aniyi kukha niyigqibe imizi yakwaSirayeli, engekafiki uNyana woMntu.', 'XHO22'),
(44, 10, 24, 'Akukho mfundi mkhulu kunomfundisi wakhe, akukho mkhonzi mkhulu kunenkosi yakhe.', 'XHO22'),
(44, 10, 25, 'Kumanele umfundi ukuba abe njengomfundisi wakhe, nomkhonzi ukuba abe njengenkosi yakhe. Ukuba umninindlu bambize ngoBhelezebhule, akukhona bobabiza ngaye yini na abo bendlu yakhe?', 'XHO22'),
(44, 10, 26, 'Musani ukuboyika ngoko; kuba akukho nto igqunyiweyo ingasayi kutyhilwa; nefihlakeleyo, engasayi kwaziwa.', 'XHO22'),
(44, 10, 27, 'Oko ndikuthetha kuni emnyameni, kuthetheni ekukhanyeni; noko nikuvayo endlebeni, kuxeleni phezu kwezindlu.', 'XHO22'),
(44, 10, 28, 'Ningaboyiki abo babulala umzimba, kodwa bengenako ukuwubulala wona umphefumlo; yoyikani kanye lowo unako ukutshabalalisa umphefumlo kwanomzimba esihogweni somlilo.', 'XHO22'),
(44, 10, 29, 'Abathengiswa ngepeni na oongqatyana ababini? yaye akuyi kuwa namnye kuzo emhlabeni ngaphandle koYihlo.', 'XHO22'),
(44, 10, 30, 'Kwaneenwele ezi zentloko yenu zibaliwe zonke.', 'XHO22'),
(44, 10, 31, 'Musani ukoyika ngoko; nibagqithile oongqatyana abaninzi nina.', 'XHO22'),
(44, 10, 32, 'Wonke umntu ngoko oya kundivuma phambi kwabantu, ndiya kumvuma nam phambi koBawo osemazulwini.', 'XHO22'),
(44, 10, 33, 'Osukuba ke eya kundikhanyela phambi kwabantu, ndiya kumkhanyela nam phambi koBawo osemazulwini.', 'XHO22'),
(44, 10, 34, 'Ningabi ndize kungenisa uxolo emhlabeni; ndize kungenisa luxolo, ndize kungenisa ikrele.', 'XHO22'),
(44, 10, 35, 'Kuba ndize kubambanisa umntu noyise, intombi nonina, umolokazana noninazala.', 'XHO22'),
(44, 10, 36, 'Zaye iintshaba zomntu ziya kuba ngabendlu yakhe.', 'XHO22'),
(44, 10, 37, 'Lowo uthanda uyise nokuba ngunina kunam, akandifanele mna; nalowo uthanda unyana wakhe nokuba yintombi yakhe kunam, akandifanele mna.', 'XHO22'),
(44, 10, 38, 'Nongawuthabathiyo umnqamlezo wakhe, alandele emva kwam, akandifanele mna.', 'XHO22'),
(44, 10, 39, 'Lowo uwufumeneyo umphefumlo wakhe, wolahlekwa nguwo; nalowo uwulahlileyo umphefumlo wakhe ngenxa yam, uya kuwufumana.', 'XHO22'),
(44, 10, 40, 'Lowo wamkela nina, wamkela mna; nalowo wamkela mna, wamkela owandithumayo.', 'XHO22'),
(44, 10, 41, 'Lowo wamkela umprofeti egameni lomprofeti, uya kwamkela umvuzo womshumayeli; nothe wamkela ilungisa ngokuba lililungisa, uya kwamkela umvuzo welungisa.', 'XHO22'),
(44, 10, 42, 'Nothe waseza noko amnye waba bangabona bancinane indebe yodwa yamanzi abandayo egameni lomfundi, inene ndithi kuni, akayi kukha alahlekelwe ngumvuzo wakhe.', 'XHO22'),
(44, 11, 1, 'Kwathi, xeshikweni uYesu ebegqibile ukubamisela abafundi bakhe abalishumi elinababini, wesuka apho waya kufundisa nokushumayela emizini yabo.', 'XHO22'),
(44, 11, 2, 'Ke kaloku uYohane, eyive esentolongweni imisebenzi kaKristu, wesusa bababini kubafundi bakhe,', 'XHO22'),
(44, 11, 3, 'Athi kuye, Wena ungulowo uzayo na, silinde omnye, sini na?', 'XHO22'),
(44, 11, 4, 'Waphendula uYesu wathi kuwo, Hambani niye kumbikela uYohane izinto enizivayo nenizibonileyo;', 'XHO22'),
(44, 11, 5, 'Iimfama zibuya zibone, neziqhwala ziyahamba, abaneqhenqa bayahlanjululwa, nezithulu ziyeva, abafileyo bayavuswa, namahlwempu ayazishunyayezwa iindaba ezilungileyo.', 'XHO22'),
(44, 11, 6, 'Unoyolo othe akakhutyekiswa ndim.', 'XHO22'),
(44, 11, 7, 'Bakubon’ ukuba bayemka abo, waqala uYesu wathi kwizihlwele ngaye uYohane, Naye niphuma nisiya kubona ntoni na entlango? Yayiyingcongolo na, izanyazanyiswa ngumoya?', 'XHO22'),
(44, 11, 8, 'Naye niphume naya kubona ntoni na kodwa? Yabe ingumntu ovethe iingubo ezitofotofo na? Yabonani, abo banxiba iingubo ezitofotofo basezindlwini zookumkani.', 'XHO22'),
(44, 11, 9, 'Naye niphume naya kubona ntoni na kodwa? Umprofeti? Ewe, ndithi kuni, nangaphezu komprofeti.', 'XHO22'),
(44, 11, 10, 'Kuba lo ngulowo kubhaliweyo ngaye, kwathiwa, Yabona, mna ndiyasithuma isithunywa sam ngaphambi kobuso bakho, Esiya kuyilungisa indlela yakho phambi kwakho.', 'XHO22'),
(44, 11, 11, 'Inene ndithi kuni, Phakathi kwabo bazelweyo ngabafazi, akuvelanga mkhulu kunoYohane umbhaptizi; kanti ke nomncinane kwabanye ebukumkanini bamazulu mkhulu kunaye.', 'XHO22'),
(44, 11, 12, 'Kususela kwimihla kaYohane umbhaptizi, kude kube ngoku, ubukumkani bezulu buyagonyamelwa, bathi abagonyamelayo babuthimbe.', 'XHO22'),
(44, 11, 13, 'Kuba bonke abaprofeti nomthetho baprofeta kwada kwakho uYohane.', 'XHO22'),
(44, 11, 14, 'Ukuba niyavuma ukukwamkela oko, yena nguEliya, lo bekuthiwa uza kufika.', 'XHO22'),
(44, 11, 15, 'Lowo uneendlebe zokuva makeve.', 'XHO22'),
(44, 11, 16, 'Ke kaloku ndiya kusifanisa nantoni na esi sizukulwana? Bufana nabantwana abahleli kwiindawo zembutho, bebiza amadlelane abo;', 'XHO22'),
(44, 11, 17, 'Sisithi, Sanigwalela, naza anangqungqa; senze isijwili kuni, anambambazela nina.', 'XHO22'),
(44, 11, 18, 'Kuba uYohane weza engadli, engaseli, basuka bathi, Unedemon.', 'XHO22'),
(44, 11, 19, 'uNyana woMntu yena weza esidla, esela, basuka bathi, Khanibone umntu olidla-kudla, olisela-wayini, umhlobo wababuthi berhafu naboni. Ke ubulumko bugwetyelwe kokuphuma ebantwaneni babo.', 'XHO22'),
(44, 11, 20, 'Waza waqala ukuyidubula loo mizi yayenzeke kuyo inkoliso yemisebenzi yakhe yamandla, ngokuba ingaguqukanga;', 'XHO22'),
(44, 11, 21, 'Yeha, wena Korazin! Yeha, wena Bhetesayida! Ngokuba imisebenzi yamandla eyenzeke kuni, ukuba yayenzeke eTire naseTsidon, ingeyaguqukayo kwakudala loo mizi ngeengubo ezirhwexayo naseluthuthwini.', 'XHO22'),
(44, 11, 22, 'Ke ndithi kuni, Kuya kuba nokunyamezeleka kwiTire nakwiTsidon ngomhla womgwebo kunani.', 'XHO22'),
(44, 11, 23, 'Nawe Kapernahum, uphakanyiselweyo ezulwini, uya kuphoselwa phantsi kwelabafileyo; ngokuba imisebenzi yamandla eyenzeke kuwe, ukuba yayenzeke eSodom, inge ihleli kwada kwaba namhla.', 'XHO22'),
(44, 11, 24, 'Ke ndithi kuni, Kuya kuba nokunyamezeleka okomhlaba waseSodom ngomhla womgwebo kunawe.', 'XHO22'),
(44, 11, 25, 'Ngelo xesha waphendula uYesu wathi, Ndiyabulela kuwe, Bawo, Nkosi yezulu nomhlaba, ngokuba ezi zinto wazifihlayo kwizilumko nakwiingqondi, wazityhilela iintsana;', 'XHO22'),
(44, 11, 26, 'ewe, Bawo, ngokuba kwakholeka phambi kwakho ukuba kube njalo.', 'XHO22'),
(44, 11, 27, 'Zonke izinto zinikelwe kum nguBawo; kanjalo akukho umaziyo uNyana, nguYise yedwa; kanjalo akukho umaziyo uYise, kuphela nguNyana, nalowo asukuba uNyana ethanda ukumtyhilela.', 'XHO22'),
(44, 11, 28, 'Yizani kum nonke nina nibulalekayo, nisindwayo ngumthwalo, ndoninika ukuphumla.', 'XHO22'),
(44, 11, 29, 'Thabathani idyokhwe yam niyithwale, nifunde kum; ngokuba ndinobulali, ndithobekile ngentliziyo; noyifumanela ukuphumla imiphefumlo yenu.', 'XHO22'),
(44, 11, 30, 'Kuba idyokhwe yam imnandi, nomthwalo wam ulula.', 'XHO22'),
(44, 12, 1, 'Ngelo xesha uYesu wacanda emasimini ngesabatha; Baye ke belambile abafundi bakhe, baqala ukukha izikhwebu, badla.', 'XHO22'),
(44, 12, 2, 'Bakukubona oko abaFarasi, bathi kuye: “Khangela, abafundi bakho benza oko kungavumelekileyo ukwenziwa ngesabatha.', 'XHO22'),
(44, 12, 3, 'Wathi kubo, Aniyilesanga na into awayenzayo uDavide, xa walambayo, yena nabo babenaye;', 'XHO22'),
(44, 12, 4, 'okokuba wangena endlwini kaThixo, wazidla izonka ezibekwa phambi koThixo, ekube kungavumelekile ukuba azidle yena, kwanabo babenaye, kuvumelekele ababingeleli bodwa?', 'XHO22'),
(44, 12, 5, 'Anilesanga na emthethweni, ukuba ngesabatha ababingeleli etempileni bayayingcolisa isabatha, bangazeki tyala noko?', 'XHO22'),
(44, 12, 6, 'Ndithi ke kuni, omkhulu kunayo itempile ukho apha.', 'XHO22'),
(44, 12, 7, 'Ukuba ke nibe nikwazile ukuba kuthini na ukuthi, Ndithanda inceba, andithandi mbingelelo, ninge ningabagwebanga abangenatyala.', 'XHO22'),
(44, 12, 8, 'Kuba uNyana woMntu lo uyiNkosi yayo nesabatha.', 'XHO22'),
(44, 12, 9, 'Esukile ke apho, waya kungena endlwini yabo yesikhungu.', 'XHO22'),
(44, 12, 10, 'Kwabonakala kukho umntu owome isandla; Baye ke bembuza besithi, Kuvumelekile na ukuphilisa ngesabatha? ukuze bammangalele.', 'XHO22'),
(44, 12, 11, 'Wathi kubo, Nguwuphi na umntu kuni ongathi, enegusha enye, yaza ukuba yeyela emhadini ngesabatha, angayibambi ayirhole?', 'XHO22'),
(44, 12, 12, 'Ubeke phi na ke umntu ukuyigqitha imvu? Ngoko ke kuvumelekile ukwenza okulungileyo ngesabatha.', 'XHO22'),
(44, 12, 13, 'Athi kuloo mntu, Solule isandla sakho. Wasolula; yabuya yaphila, ifana neyawo.', 'XHO22'),
(44, 12, 14, 'Baza baphuma abaFarisi benza ibhunga ngaye lokuba bamtshabalalise.', 'XHO22'),
(44, 12, 15, 'Uthe ke uYesu, ekwazi oko, wemka, apho; walandelwa ngabantu abaninzi; wabaphilisa bonke.', 'XHO22'),
(44, 12, 16, 'Wabakhalimela ukuba bangamtyhili;', 'XHO22'),
(44, 12, 17, 'ukuze kuzaliseke okwathethwayo ngoIsaya umprofeti, esithi,', 'XHO22'),
(44, 12, 18, 'Mboneni ke umkhonzi wam endimnyulileyo; intanda yam okholisiweyo yiyo umphefumlo wam; ndiya kumbeka uMoya wam phezu kwakhe, waye eya kuzibikela iintlanga umgwebo.', 'XHO22'),
(44, 12, 19, 'Akayi kuphika, akayi kunkqangaza; Akukho bani uya kuliva ilizwi lakhe ezitratweni.', 'XHO22'),
(44, 12, 20, 'Ingcongolo etyumkileyo akayi kuyaphula, nomsonto oqhumayo akayi kuwucima, ade awuphumelelisele ekoyiseni umgwebo;', 'XHO22'),
(44, 12, 21, 'Kaloku ziya kuthembela egameni lakhe iintlanga.', 'XHO22'),
(44, 12, 22, 'Kwaza kwaziswa kuye umntu ophethwe yidemon, eyimfama, esisidenge;', 'XHO22'),
(44, 12, 23, 'Yamangaliswa isihlwele sonke, sathi, Lo asinguye na unyana kaDavide?', 'XHO22'),
(44, 12, 24, 'Bakuva ke oko abaFarasi, bathi: “Lo yena akazikhuphi iidemon, kuphela ngoBhelezebhule umphathi weendimoni.', 'XHO22'),
(44, 12, 25, 'Uthe ke uYesu, ezazi iingcingane zabo, wathi kubo, Ubukumkani bonke obahlulelene bodwa buyaphanza; nomzi wonke nendlu yonke eyahlulelene yodwa ayiyi kuma.', 'XHO22'),
(44, 12, 26, 'Ukuba ke uSathana ukhupha uSathana, wahlulelene yedwa; bothini na ngoko ukuma ubukumkani bakhe?', 'XHO22'),
(44, 12, 27, 'Ukuba mna ndizikhupha iidemon ngoBhelezebhule, bazikhupha ngabani na onyana benu? ngenxa yoko baya kuba ngabagwebi benu bona.', 'XHO22'),
(44, 12, 28, 'Kodwa ukuba ndizikhupha iidemon ngoMoya kaThixo mna, ngoko bunifikele kanti ubukumkani bukaThixo.', 'XHO22'),
(44, 12, 29, 'Angathini na umntu ukuba nako ukungena endlwini yegorha elo, ayiphange impahla yalo, ukuba akathanga tanci alibophe igorha elo? andule ke ukuyiphanga indlu yakhe.', 'XHO22'),
(44, 12, 30, 'Lowo ungengakum, uchasene nam; nalowo ungandibuthisiyo, uyachithachitha.', 'XHO22'),
(44, 12, 31, 'Ngenxa yoko ndithi kuni, Konke ukona nokunyelisa bokuxolelwa abantu; kodwa kona ukunyelisa uMoya oyiNgcwele abasayi kukuxolelwa abantu.', 'XHO22'),
(44, 12, 32, 'Nothe wathetha ilizwi elichasene noNyana woMntu uya kuxolelwa; kodwa othe wathetha elichasene noMoya oyiNgcwele, akasayi kuxolelwa yena, nakweli phakade, nakwelo lizayo.', 'XHO22'),
(44, 12, 33, 'Wenzani umthi ube mhle, nesiqhamo sawo sibe sihle; okanye niwenze mbi umthi, nesiqhamo sawo sibe sibi; ngokuba umthi waziwa ngesiqhamo sawo.', 'XHO22'),
(44, 12, 34, 'Nzalandini yamarhamba, ningathini na ukuba nako ukuthetha izinto ezilungileyo, nikhohlakele nje? kuba umlomo uthetha ngokuphuphuma kwentliziyo.', 'XHO22'),
(44, 12, 35, 'Umntu olungileyo, ebuncwaneni obulungileyo bentliziyo yakhe, ukhupha izinto ezilungileyo; nomntu okhohlakeleyo, ebuncwaneni obukhohlakeleyo, ukhupha izinto ezikhohlakeleyo.', 'XHO22'),
(44, 12, 36, 'Ndithi ke kuni, Onke amazwi angewani abathe bawathetha abantu, baya kuphendula ngawo ngomhla womgwebo;', 'XHO22'),
(44, 12, 37, 'kuba ngawo amazwi akho uya kugwetyelwa, nangawo amazwi akho uya kugwetywa.', 'XHO22'),
(44, 12, 38, 'Baphendula ke abathile bakubabhali nabaFarisi, besithi, Mfundisi, sifuna ukubona umqondiso.', 'XHO22'),
(44, 12, 39, 'Waphendula ke wathi kubo, Isizukulwana esingendawo, esikrexezayo, singxamele umqondiso; kwaye asiyi kunikwa mqondiso, ingenguwo umqondiso kaYona umprofeti.', 'XHO22'),
(44, 12, 40, 'Kuba, njengokuba uYona ebemini ntathu nabusuku buthathu esiswini sentlanzi enkulu; kwangokunjalo uNyana woMntu woba mini ntathu nabusuku buthathu embilinini womhlaba.', 'XHO22'),
(44, 12, 41, 'Amadoda aseNiniva aya kusuka eme ngomgwebo nesi sizukulwana, asigwebe; ngokuba aguquka wona ngomemezo lukaYona; ukanti ke omkhulu kunoYona nanku apha.', 'XHO22'),
(44, 12, 42, 'Ukumkanikazi waseZantsi uya kusuka eme ngomgwebo nesi sizukulwana, asigwebe; ngokuba wavela eziphelweni zomhlaba, esiza kuva ubulumko bukaSolomon; ukanti ke omkhulu kunoSolomon nanku apha.', 'XHO22'),
(44, 12, 43, 'Xa umoya ongcolileyo uthe waphuma emntwini, ucanda ezindaweni ezingenamanzi, ufuna ukuphumla, ungakufumani.', 'XHO22'),
(44, 12, 44, 'Uze uthi, Ndiya kubuyela kwasendlwini yam endaphuma kuyo; uthi ke wakufika uyifumane ize, itshayelwe, ivathisiwe.', 'XHO22'),
(44, 12, 45, 'Wandule ke ukuya uthabathe uze nabanye oomoya, abasixhenxe abakhohlakeleyo kunawo, bangene beme khona; lize ikamva laloo mntu libe libi kunesiqaleko sakhe. Kuya kuba njalo nakwesi sizukulwana singendawo.', 'XHO22'),
(44, 12, 46, 'Esathetha neendimbane ezo, kwathi thaphu unina nabazalwana bakhe, bema ngaphandle, befuna ukuthetha naye.', 'XHO22'),
(44, 12, 47, 'Wathi ke othile kuye, Nanku unyoko nabazalwana bakho, bemi ngaphandle, befuna ukuthetha nawe.', 'XHO22'),
(44, 12, 48, 'Uphendule ke wathi kulowo ubemxelele, Ngubani na uma? Ngoobani bona abazalwana bam?', 'XHO22'),
(44, 12, 49, 'Wasolula isandla sakhe kubafundi bakhe, wathi, Nanku uma nabazalwana bam.', 'XHO22'),
(44, 12, 50, 'Kuba othe wakwenza ukuthanda kukaBawo osemazulwini, nguye lowo ongumntakwethu, nodade wethu, noma.', 'XHO22'),
(44, 13, 1, 'Kwangaloo mini waphuma uYesu endlwini, waya wahlala ngaselwandle.', 'XHO22'),
(44, 13, 2, 'Kwahlanganisana kuye abantu abaninzi, wada yena wangena emkhombeni, wahlala phantsi; yaye yonke indimbane yema elunxwemeni.', 'XHO22'),
(44, 13, 3, 'Wathetha kubo iindawo ezininzi ngemizekeliso, esithi, Yabonani, umhlwayeli waphuma waya kuhlwayela.', 'XHO22'),
(44, 13, 4, 'Ekuhlwayeleni kwakhe, inxalenye yembewu yawa ngasendleleni, zeza iintaka zayidla zayigqiba.', 'XHO22'),
(44, 13, 5, 'Embi yawa ezindaweni ezilulwalwa, apho ibingenamhlaba mninzi;', 'XHO22'),
(44, 13, 6, 'Zathi ke lakuphuma ilanga zagqatswa; zaza ngenxa yokuba zingenangcambu zoma kwaphela.', 'XHO22'),
(44, 13, 7, 'Embi yawa emithaneni enameva; imithana enameva yakhula, yayiminxa.', 'XHO22'),
(44, 13, 8, 'Enye yawa kuwo wona umhlaba olungileyo, yavelisa isiqhamo, enye ikhulu, enye amashumi amathandathu, nenye amashumi amathathu.', 'XHO22'),
(44, 13, 9, 'Lowo uneendlebe zokuva makeve.', 'XHO22'),
(44, 13, 10, 'Beza abafundi bathi kuye, Kungani na ukuba uthethe kubo ngemizekeliso?', 'XHO22'),
(44, 13, 11, 'Waphendula ke wathi kubo, Kungokuba nikuphiwe nina ukuzazi iimfihlelo zobukumkani bamazulu, babe ke bona bengakuphiwanga.', 'XHO22'),
(44, 13, 12, 'Kuba osukuba enako, yena uya kuphiwa, aze abe nako ngokugqithiseleyo; kodwa osukuba engenako, uya kuhluthwa kwanoko anako.', 'XHO22'),
(44, 13, 13, 'Ngoko ke ndithetha nabo ngemizekeliso, kuba bona ababoni; besiva nje abeva, bengaqondi noko.', 'XHO22'),
(44, 13, 14, 'Sizaliseka kubo ke isiprofeto sikaIsaya esithi, Ukuva niya kuva, ningaqondi; nokubona niya kubona, ningaqiqi;', 'XHO22'),
(44, 13, 15, 'Kuba intliziyo yaba bantu ithe fithi kukutyeba, Beva ngeendlebe ezithe nkqi, Bayawacima namehlo abo; hleze babone ngamehlo, beve ngeendlebe, baqonde ngentliziyo, bajike, ndibaphilise.', 'XHO22'),
(44, 13, 16, 'Kodwa anethamsanqa amehlo enu, ngokuba ebona; neendlebe zenu, ngokuba zisiva.', 'XHO22'),
(44, 13, 17, 'Kuba ndithi kuni, Inene, baninzi abaprofeti nabantu abangamalungisa, abanqwenelayo ukuzibona izinto enizibonayo abazibona; nokuva izinto enizivayo, anaziva.', 'XHO22'),
(44, 13, 18, 'Nina ngoko wuveni umzekeliso womhlwayeli:', 'XHO22'),
(44, 13, 19, 'akubon’ ukuba ubani uyaliva ilizwi lobukumkani, engaliqondi, uyafika ongendawo, akuhluthe okuhlwayelwe entliziyweni yakhe; Nguye lo owahlwayelwa ngasendleleni;', 'XHO22'),
(44, 13, 20, 'Lowo ke uhlwayelwe ezindaweni ezilulwalwa, ngulo ulivayo ilizwi, alamkele kwaoko ngovuyo;', 'XHO22'),
(44, 13, 21, 'abe ke engenangcambu ngaphakathi kwakhe, eme umzuzwana kodwa; uthi ke kwakuhla imbandezelo nokuba yintshutshiso ngenxa yelizwi, akhutyekiswe kwaoko.', 'XHO22'),
(44, 13, 22, 'Lowo uhlwayelwe emithaneni enameva, ngulo ulivayo ilizwi; Lithe ke ixhala leli phakade, nokulukuhla kobutyebi, kuliminxe ilizwi, lisuke lingabi nasiqhamo.', 'XHO22'),
(44, 13, 23, 'Lowo ke uhlwayelwe kuwo umhlaba omhle, ngulo ulivayo ilizwi aliqonde; luxakatha isiqhamo, omnye lixakathe ikhulu, omnye amashumi amathandathu, nomnye amashumi amathathu.', 'XHO22'),
(44, 13, 24, 'Wababekela omnye umzekeliso, esithi, Ubukumkani bamazulu bufanekiswa nomntu owahlwayela imbewu entle entsimini yakhe.', 'XHO22'),
(44, 13, 25, 'Kuthe ke, xa baleleyo abantu, lwafika utshaba lwakhe, lwahlwayela umdiza phakathi kwengqolowa, lwemka.', 'XHO22'),
(44, 13, 26, 'Ithe ke yakudubula, yakuba neenkozo, wandula ukubonakala nomdiza.', 'XHO22'),
(44, 13, 27, 'Bezile ke abakhonzi bomninindlu, bathi kuye, Nkosi, ubungahlwayelanga imbewu entle yini na entsimini yakho? luvela phi na ke umdiza?', 'XHO22'),
(44, 13, 28, 'Wathi kubo, Ngumntu olutshaba owenze le nto. Bathi abakhonzi kuye, Uyathanda na ke ukuba siye siwubuthe?', 'XHO22'),
(44, 13, 29, 'Uthe ke yena, Hayi; hleze nithi, xa niwubuthayo umdiza, nincothule nengqolowa kunye nawo.', 'XHO22'),
(44, 13, 30, 'Yekani kukhule ndawonye kokubini, kude kube sekuvuneni; ngexa ke lokuvuna ndothi kubavuni, Buthani kuqala umdiza, niwubophe ube zizithungu, utshiswe, kodwa yona ingqolowa yihlanganiseleni kuvimba wam.', 'XHO22'),
(44, 13, 31, 'Wababekela omnye umzekeliso, esithi, Ubukumkani bamazulu bufana nokhozo lwemostade, awaluthabathayo umntu, waluhlwayela entsimini yakhe.', 'XHO22'),
(44, 13, 32, 'Olona luncinane kuzo zonke iimbewu; kodwa xa luthe lwahluma, luyayidlula imifuno ngobukhulu, lube ngumthi, ngokokude iintaka zezulu zize zihlale emasebeni awo.', 'XHO22'),
(44, 13, 33, 'Wathetha omnye umzekeliso kubo; Ubukumkani bamazulu bufana negwele, awalithabathayo umfazi, walifihla ezilinganisweni zozithathu zomgubo, wada wabiliswa uphela.', 'XHO22'),
(44, 13, 34, 'Ezi ndawo zonke uYesu wazithetha ngemizekeliso kuzo izihlwele; Ubengathethi ke kubo kungengamzekeliso;', 'XHO22'),
(44, 13, 35, 'Ukuze kuzaliseke okwathethwayo ngomprofeti, esithi, Ndiya kuwuvula ngemizekeliso umlomo wam; Ndiya kuthetha izinto ezazifihliwe kwasekusekweni kwehlabathi.', 'XHO22'),
(44, 13, 36, 'Waza uYesu wazindulula izihlwele wangena endlwini. Beza kuye abafundi bakhe, besithi, Sixelele umzekeliso womdiza wentsimi.', 'XHO22'),
(44, 13, 37, 'Waphendula ke wathi kubo, Lo uhlwayela imbewu entle nguNyana woMntu;', 'XHO22'),
(44, 13, 38, 'intsimi ke lihlabathi; imbewu ke entle ngoonyana bobukumkani abo; Umdiza ke ngoonyana bongendawo;', 'XHO22'),
(44, 13, 39, 'Utshaba ke olwawuhlwayelayo wona nguMtyholi; ukuvuna kukupheliswa kwephakade eli; abavuni ke zizithunywa zezulu.', 'XHO22'),
(44, 13, 40, 'Njengokuba ngoko ubuthwa umdiza, utshiswe ngomlilo; kuya kuba njalo ekuphelisweni kweli hlabathi.', 'XHO22'),
(44, 13, 41, 'uNyana woMntu uya kuthuma izithunywa zakhe, zize zibuthe, zikhuphe ebukumkanini bakhe, zonke izikhubekiso, nabo benza okuchasene nomthetho;', 'XHO22'),
(44, 13, 42, 'zibaphose ezikweni elivutha umlilo; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 13, 43, 'Aya kwandula ke amalungisa ukubengezela njengelanga ebukumkanini boYise wawo. Lowo uneendlebe zokuva makeve.', 'XHO22'),
(44, 13, 44, 'Kanjalo ubukumkani bamazulu bufana nobutyebi obufihlwe entsimini, athe umntu wabufihla; athi ke umntu akuyifumana, ayifihle, aze ngenxa yovuyo lwakhe aye athengise ngeento zonke anazo, ayithenge loo ntsimi.', 'XHO22'),
(44, 13, 45, 'Kanjalo ubukumkani bamazulu bufana nomrhwebi, efuna iiperile ezintle;', 'XHO22'),
(44, 13, 46, 'owathi ke akuyifumana iperile inye, exabiso likhulu, wemka waya wananisa ngeento zonke abenazo, wayithenga.', 'XHO22'),
(44, 13, 47, 'Kanjalo ubukumkani bamazulu bufana nomnatha, owaphoswa elwandle, wahlanganisa zonke iintlobo zeentlobo zazo.', 'XHO22'),
(44, 13, 48, 'Ekuthe wazala, bawurholela elunxwemeni, bahlala phantsi, bazibuthela ezintle ezityeni, bazilahla ke ezimbi.', 'XHO22'),
(44, 13, 49, 'Kuya kuba njalo ekuphelisweni kwephakade eli: ziya kuphuma izithunywa zezulu, zibakhethe abangendawo phakathi kwamalungisa;', 'XHO22'),
(44, 13, 50, 'zibaphose ezikweni lomlilo; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 13, 51, 'Athi kubo uYesu, Niziqondile na zonke ezi ndawo? Bathi kuye, Ewe, Nkosi.', 'XHO22'),
(44, 13, 52, 'Wathi kubo, Ngenxa yoko, umbhali wonke ofundiselwe ebukumkanini bamazulu ufana nomntu ongumninindlu, orhola ebuncwaneni bakhe izinto ezintsha kwanezindala.', 'XHO22'),
(44, 13, 53, 'Kwathi, akuyigqiba uYesu le mizekeliso, wanduluka khona apho.', 'XHO22'),
(44, 13, 54, 'Akufika kwelakowabo, wabafundisa endlwini yabo yesikhungu, ngokokude bakhwankqiswe bathi, Lo ubuzuze phi na obu bulumko, nale misebenzi yamandla?', 'XHO22'),
(44, 13, 55, 'Lo asinguye na unyana wengcibi yokwakha? Unina akabizwa ngokuba nguMariya na? Abazalwana bakhe bathi, ooYakobi, noYose, noSimon, noYuda, yini na?', 'XHO22'),
(44, 13, 56, 'Noodade wabo abakho nathi bonke na? Uzizuze phi na phofu lo zonke ezi zinto?', 'XHO22'),
(44, 13, 57, 'Baye bekhubeka kuye. Wathi ke uYesu kubo, Umprofeti akasweli mbeko, kungaba kukowabo, nakokwabo.', 'XHO22'),
(44, 13, 58, 'Akenza misebenzi mininzi yamandla khona apho ngenxa yokungakholwa kwabo.', 'XHO22'),
(44, 14, 1, 'Ngelo xesha uHerode, umphathi wesahlulo sesine selizwe, waluva udaba lukaYesu,', 'XHO22'),
(44, 14, 2, 'wathi kubakhonzi bakhe, Lo nguYohane umbhaptizi; uvukile kwabafileyo; kungoko kubonakala kuye imisebenzi yamandla.', 'XHO22'),
(44, 14, 3, 'Kuba uHerode wambamba uYohane, wambopha, wamfaka entolongweni, ngenxa kaHerodiya, umfazi kaFilipu, umninawa wakhe.', 'XHO22'),
(44, 14, 4, 'Kuba wayethe uYohane kuye, Akuvumelekile ukuba ube naye.', 'XHO22'),
(44, 14, 5, 'Uthe ke eza kumbulala, woyika abantu, ngokuba babesithi ungumprofeti.', 'XHO22'),
(44, 14, 6, 'Kwakubon’ ukuba kwenziwa isikhumbuzo sokuzalwa kukaHerode, yathi intombi kaHerodiya yangqungqa, yamkholisa uHerode.', 'XHO22'),
(44, 14, 7, 'Ekubeni wayithembisa ngesifungo ukuba woyinika oko ingakucelayo.', 'XHO22'),
(44, 14, 8, 'Ke yona, iqhutywe ngunina, yathi, Ndinike apha ngesitya intloko kaYohane umbhaptizi.', 'XHO22'),
(44, 14, 9, 'Waba buhlungu ukumkani, kodwa, ngenxa yesifungo, nangenxa yabo babehleli naye esithebeni, wathi mayinikwe.', 'XHO22'),
(44, 14, 10, 'Wasusa umntu, wamnqumla uYohane intloko entolongweni apho.', 'XHO22'),
(44, 14, 11, 'Yaziswa intloko yakhe ngesitya, yanikwa intombazana leyo, yathi yona yayisa kunina.', 'XHO22'),
(44, 14, 12, 'Beza abafundi bakhe, basithabatha isidumbu, basingcwaba; baya babikela uYesu.', 'XHO22'),
(44, 14, 13, 'Evile ke uYesu, wemka apho ngomkhombe, waya endaweni eyintlango eyedwa.', 'XHO22'),
(44, 14, 14, 'Ephumile ke uYesu, wabona into eninzi yabantu, wasikwa yimfesane ngabo, wayiphilisa imilwelwe yabo.', 'XHO22'),
(44, 14, 15, 'Kuthe ke kwakuhlwa, beza kuye abafundi bakhe, besithi, Le ndawo iyintlango, kanjalo ilixa lise lidlule; zindulule izihlwele, ziye emizaneni, zizithengele ukudla.', 'XHO22'),
(44, 14, 16, 'Wathi kubo uYesu, Akufuneki ukuba zimke; zipheni nina zitye.', 'XHO22'),
(44, 14, 17, 'Bathi ke bona kuye, Asinanto apha, ingezizo izonka ezihlanu neentlanzi ezimbini.', 'XHO22'),
(44, 14, 18, 'Wathi ke yena, Ziziseni kum apha.', 'XHO22'),
(44, 14, 19, 'Wathi izihlwele mazingqengqe phantsi engceni, wazithabatha izonka zozihlanu neentlanzi zombini, wathi, ekhangele ezulwini, wasikelela; waziqhekeza ke izonka ezo, wazinika abafundi bakhe, abafundi ke banika izihlwele.', 'XHO22'),
(44, 14, 20, 'Badla ke bonke bahlutha, kwaza kwaqokelelwa iingobozi ezilishumi elinambini zokutya okuseleyo.', 'XHO22'),
(44, 14, 21, 'Ke kaloku abo babesidla babengamadoda akumawaka amahlanu, ngaphandle kwabafazi nabantwana.', 'XHO22'),
(44, 14, 22, 'Waza uYesu wabanyanzela kwaoko abafundi bakhe ukuba bangene emkhombeni, bamandulele baye phesheya, lo gama azindululayo izihlwele yena.', 'XHO22'),
(44, 14, 23, 'Ezindulule ke izihlwele, wenyuka intaba ngasese, ukuba athandaze. Kwahlwa ke ekhona apho yedwa.', 'XHO22'),
(44, 14, 24, 'Ke kaloku umkhombe wawuselunxwemeni lolwandle, uqhutywa ngamaza, kuba umoya ubuvela ngaphambili.', 'XHO22'),
(44, 14, 25, 'Kuthe ke ngowesine umlindo wobusuku, wesuka waya kubo uYesu, ehamba phezu kolwandle.', 'XHO22'),
(44, 14, 26, 'Bakumbona ke abafundi bakhe ehamba phezu kolwandle, bankwantya, besithi: “Ngumhlola; bakhala kukoyika.', 'XHO22'),
(44, 14, 27, 'Wathetha ke kwaoko uYesu kubo, esithi, Yomelelani; ndim; musani ukoyika.', 'XHO22'),
(44, 14, 28, 'uPetros wamphendula ke wathi, Nkosi, ukuba nguwe, yitsho ndize kuwe ndihamba phezu kwamanzi.', 'XHO22'),
(44, 14, 29, 'Wathi, Yiza. Wehla ke uPetros emkhombeni, wahamba phezu kwamanzi, esiya kuYesu.', 'XHO22'),
(44, 14, 30, 'Kodwa, ebona umoya unamandla, woyika; wathi eqala ukutshona, wakhala, esithi, Nkosi, ndisindise.', 'XHO22'),
(44, 14, 31, 'Wasolula kwaoko ke isandla uYesu, wambamba, wathi kuye, Ehla wena ulukholo luncinane, uthandabuzelani na?', 'XHO22'),
(44, 14, 32, 'Bakuba bengenile emkhombeni, wadamba umoya.', 'XHO22'),
(44, 14, 33, 'Bazile ke abo basemkhombeni, baqubuda kuye, besithi, Inyaniso, unguNyana kaThixo.', 'XHO22'),
(44, 14, 34, 'Bewelile, bafika emhlabeni waseGenesarete.', 'XHO22'),
(44, 14, 35, 'Emazile amadoda aloo ndawo, athumela kuwo wonke loo mmandla, azisa kuye bonke ababesifa.', 'XHO22'),
(44, 14, 36, 'Baza bambongoza uYesu ukuba bachukumise nje kodwa intshinga yengubo yakhe; baza bonke abayichukumisayo baphiliswa kanye.', 'XHO22'),
(44, 15, 1, 'Ngelo xesha beza kuYesu abaseYerusalem ababhali nabaFarisi, besithi,', 'XHO22'),
(44, 15, 2, 'Kungani na ukuba abafundi bakho basigqithe isithethe samanyange? ngokuba abazihlambi izandla zabo, xa badla isonka.', 'XHO22'),
(44, 15, 3, 'Waphendula ke yena wathi kubo, Nani, kungani na ukuba niwugqithe umthetho kaThixo ngenxa yesithethe senu?', 'XHO22'),
(44, 15, 4, 'Kuba uThixo wawisa umthetho, esithi, Mbeke uyihlo nonyoko; wathi kanjalo, Omthethela kakubi uyise nokuba ingunina, makafe ukufa.', 'XHO22'),
(44, 15, 5, 'Ke nina nithi, Othe kuyise nokuba kukunina, Iselingumnikelo kuThixo, into onge uba uncedwa ngayo ndim, ukholisile;', 'XHO22'),
(44, 15, 6, 'ingambeki uyise nonina; yoba msulwa. Niwuphangile ke igunya umthetho kaThixo ngenxa yesithethe senu.', 'XHO22'),
(44, 15, 7, 'Bahanahanisindini, watyapha ukuprofeta ngani uIsaya, esithi,', 'XHO22'),
(44, 15, 8, 'Aba bantu basondela kum ngomlomo wabo, bandibeke ngemilebe yomlomo wabo; kodwa intliziyo yabo ikude lee kum.', 'XHO22'),
(44, 15, 9, 'Bafumana ke bendihlonela, Befundisa iimfundiso eziyimithetho yabantu.', 'XHO22'),
(44, 15, 10, 'Wayibizela kuye indimbane, wathi kubo, Phulaphulani niqonde.', 'XHO22'),
(44, 15, 11, 'Asikokungena emlonyeni okumenza inqambi umntu; ke okuphuma emlonyeni, kuko oko okumenza inqambi umntu.', 'XHO22'),
(44, 15, 12, 'Beza abafundi bakhe bathi kuye, Uyazi na ukuba abaFarisi bathe bakuliva elo lizwi bakhubeka?', 'XHO22'),
(44, 15, 13, 'Waphendula ke wathi, Zonke izityalo ezingatyalwanga nguBawo wasemazulwini ziya kuncothulwa.', 'XHO22'),
(44, 15, 14, 'Bayekeni; ngabakhokeli beemfama abakwaziimfama; Ke kaloku, xa sukuba imfama ikhokela imfama, zombini ziya kweyela emhadini.', 'XHO22'),
(44, 15, 15, 'Waphendula ke uPetros wathi kuye, Sixelele lo mzekeliso.', 'XHO22'),
(44, 15, 16, 'Wathi ke uYesu kubo, Anikabi nakuqonda na nani nangoku?', 'XHO22'),
(44, 15, 17, 'Anikaqondi na ukuba yonke into engena emlonyeni ingena esiswini, ize iphoswe entlanjeni?', 'XHO22'),
(44, 15, 18, 'Ke zona izinto eziphuma emlonyeni, ziphuma kuyo intliziyo; zimenza inqambi umntu.', 'XHO22'),
(44, 15, 19, 'Kuba kuyo intliziyo kuphuma izicamango ezingendawo, ookubulala, ookukrexeza, oomibulo, oobusela, ookungqina ubuxoki, ookunyelisa.', 'XHO22'),
(44, 15, 20, 'Zizo ezo izinto ezimenza inqambi umntu; kodwa kona ukudla ngezandla ezingahlanjwanga akumenzi nqambi umntu.', 'XHO22'),
(44, 15, 21, 'Ephumile khona apho, uYesu wasinga kwimida yaseTire neTsidon.', 'XHO22'),
(44, 15, 22, 'Kwathi thu umKananekazi evela kuloo mida, ekhala esithi, Ndenzele inceba, Nkosi, nyana kaDavide; intombi yam iphethwe yidemon kakubi.', 'XHO22'),
(44, 15, 23, 'Kodwa akamphendulanga nelizwi eli. Beza abafundi bakhe, bamcela besithi, Mndulule; ngokuba uyakhala emva kwethu.', 'XHO22'),
(44, 15, 24, 'Waphendula ke yena wathi, Ndithunywe kwizimvu ezilahlekileyo zendlu kaSirayeli zodwa.', 'XHO22'),
(44, 15, 25, 'Weza ke waqubuda kuye, esithi, Nkosi, ndincede.', 'XHO22'),
(44, 15, 26, 'Uphendule ke wathi, Asinto intle ukusithabatha isonka sabantwana, siphoswe ezinjaneni.', 'XHO22'),
(44, 15, 27, 'Wathi ke yena, Ewe, Nkosi; kuba neenjana zidla kwiimvuthuluka eziwa etafileni yabaninizo.', 'XHO22'),
(44, 15, 28, 'Waphendula ke uYesu wathi kuye, Mfazi, lukhulu ukholo lwakho; makwenzeke kuwe ngokuthanda kwakho. Yaphiliswa intombi yakhe kwangelo lixa.', 'XHO22'),
(44, 15, 29, 'Emka apho uYesu, weza ngaselwandle lwaseGalili; wenyuka intaba, wahlala phantsi khona.', 'XHO22'),
(44, 15, 30, 'Kweza kuye izihlwele ezininzi, zinazo iziqhwala, iimfama, izidenge, izilima, nabanye abaninzi, zababeka phantsi ezinyaweni zikaYesu; wabaphilisa.', 'XHO22'),
(44, 15, 31, 'Zada zamangaliswa izihlwele ezo, zakubona izidenge zithetha, izilima ziphilile, iziqhwala zihamba, neemfama zibona; zamzukisa uThixo kaSirayeli.', 'XHO22'),
(44, 15, 32, 'Ebabizele ke kuye uYesu abafundi bakhe, wathi, Ndisikwa yimfesane ngenxa yezihlwele, ngokuba sekuntsuku ntathu behleli nam, bengenanto bangayidlayo;', 'XHO22'),
(44, 15, 33, 'Bathi ke kuye abafundi bakhe, Sozizuza phi na entlango apho izonka ezingaka, zokuhluthisa isihlwele esingaka?', 'XHO22'),
(44, 15, 34, 'Athi kubo uYesu, Zingaphi na izonka eninazo? Bathi ke bona, Zisixhenxe, neentlanzi ezimbalwa.', 'XHO22'),
(44, 15, 35, 'Wathi kwizihlwele mazihlale phantsi emhlabeni.', 'XHO22'),
(44, 15, 36, 'Wazithabatha ke izonka zosixhenxe neentlanzi, wabulela, waqhekeza, wanika abafundi bakhe, abafundi ke banika izihlwele.', 'XHO22'),
(44, 15, 37, 'Badla bahlutha bonke, kwaza kwaqokelelwa iingobozi ezisixhenxe zizele yintsalela.', 'XHO22'),
(44, 15, 38, 'Ke kaloku abo babesidla babengamawaka amane amadoda, ngaphandle kwabafazi nabantwana.', 'XHO22'),
(44, 15, 39, 'Esindulule ke isihlwele, wangena emkhombeni, weza emacaleni aseMagadala.', 'XHO22'),
(44, 16, 1, 'Bafika ke abaFarisi nabaSadusi, bathi, bemlinga, bacela ukuba ababonise umqondiso ophuma ezulwini.', 'XHO22'),
(44, 16, 2, 'Waphendula ke wathi kubo, Ngokuhlwa nithi, Liya kusa; kuba izulu limlilo ukuba bomvu.', 'XHO22'),
(44, 16, 3, 'Kanjalo kusasa nithi, Namhla luqhwithela; kuba izulu limlilo ukuba bomvu, lisibekele. Bahanahanisindini, niyakwazi ukubucalula ubuso bezulu; Ke imiqondiso yamaxesha aninako na ukuyicalula?', 'XHO22'),
(44, 16, 4, 'Isizukulwana esingendawo, esikrexezayo, sifuna umqondiso; kwaye asiyi kunikwa mqondiso, ingenguwo umqondiso kaYona umprofeti. Wabashiya ke wemka.', 'XHO22'),
(44, 16, 5, 'Bakufika abafundi bakhe ngaphesheya, baye balibele ukuphatha izonka.', 'XHO22'),
(44, 16, 6, 'Wathi ke uYesu kubo, Ligqaleni nililumkele igwele labaFarisi nabaSadusi.', 'XHO22'),
(44, 16, 7, 'Baye becamanga phakathi kwabo, besithi, Kungokuba singaphathanga zonka.', 'XHO22'),
(44, 16, 8, 'Ekwazi ke oko uYesu, wathi kubo, Ehlani, nina balukholo luncinane! Yini na ukuba nicamange, ngokuba ningaphathanga zonka?', 'XHO22'),
(44, 16, 9, 'Anikaqondi na, anizikhumbuli na izonka ezihlanu zamawaka amahlanu, nokuba zazingaphi na iingobozi enazithabathayo?', 'XHO22'),
(44, 16, 10, 'Kwanezonka ezisixhenxe zamawaka amane, nokuba zazingaphi na iingobozi enazithabathayo?', 'XHO22'),
(44, 16, 11, 'Yini na le nto ningaqondi, ukuba bendingatsho ngasonka, ukuthi kuni, manililumkele igwele labaFarisi nabaSadusi?', 'XHO22'),
(44, 16, 12, 'Bandula ukuqonda, ukuba ubengatsho ukuthi mabalumkele igwele lesonka; ubesithi mabalumkele imfundiso yabaFarisi nabaSadusi.', 'XHO22'),
(44, 16, 13, 'Akufika uYesu emacaleni aseKesareya yakwaFilipu, wababuza abafundi bakhe, esithi, Bathi abantu ndingubani na mna, Nyana woMntu?', 'XHO22'),
(44, 16, 14, 'Bayesithi ke bona, Inxenye ithi, UnguYohane umbhaptizi; bambi bathi, UnguYeremiya, mhlawumbi ungomnye wakubaprofeti.', 'XHO22'),
(44, 16, 15, 'Athi kubo, Ke nina nithi ndingubani na?', 'XHO22'),
(44, 16, 16, 'Waphendula ke uSimon Petros wathi, Wena unguye uKristu, uNyana kaThixo ophilileyo.', 'XHO22'),
(44, 16, 17, 'Waphendula uYesu wathi kuye, Unoyolo, Simon kaYona, ngokuba akukutyhilelwanga oku yinyama negazi, ukutyhilelwe nguBawo osemazulwini.', 'XHO22'),
(44, 16, 18, 'Nam ke, ndithi kuwe, UnguPetros wena; ndiya kulakhela phezu kolu lwalwa ibandla lam; kwaye amasango esihogo akayi koyisa ngokuchasene nayo.', 'XHO22'),
(44, 16, 19, 'Ndiya kukunika izitshixo zobukumkani bamazulu; kuthi ke othe wakubopha emhlabeni, kubopheke emazulwini; kuthi othe wakukhulula emhlabeni, kukhululeke emazulwini.', 'XHO22'),
(44, 16, 20, 'Wabayala abafundi bakhe, ukuba bangaxeleli bani ukuba yena unguye uKristu.', 'XHO22'),
(44, 16, 21, 'Waqalela kwelo xa uYesu ukubabonisa abafundi bakhe, okokuba umelwe kukuba aye eYerusalem, eve ubunzima obukhulu kuwo amadoda amakhulu nababingeleli abakhulu, nababhali, abulawe, athi ngomhla wesithathu abuye avuke.', 'XHO22'),
(44, 16, 22, 'Waza uPetros wamthabatha, waqala ukumkhalimela, esithi, Makube lee kuwe, Nkosi;', 'XHO22'),
(44, 16, 23, 'Wajika ke wathi kuPetros, Suka uye emva kwam, Sathana, usisikhubekiso kum; ngokuba ungazinyamekeli ezikaThixo izinto, unyamekela ezabantu.', 'XHO22'),
(44, 16, 24, 'Wandula wathi uYesu kubafundi bakhe, Ukuba kukho othanda ukundilandela, makazincame ngokwakhe, awuthwale umnqamlezo wakhe, andilandele.', 'XHO22'),
(44, 16, 25, 'Kuba othe wathanda ukuwusindisa umphefumlo wakhe, wolahlekelwa nguwo; ke othe wawulahla umphefumlo wakhe ngenxa yam, wowufumana.', 'XHO22'),
(44, 16, 26, 'Kuba, kumnceda ntoni na umntu, ukuba uthe wazuza ihlabathi liphela, waza wonakalelwa ke ngumphefumlo wakhe? Okanye worhola ntoni na umntu, ibe sisananelo somphefumlo wakhe?', 'XHO22'),
(44, 16, 27, 'Kuba uNyana woMntu uza kuza esebuqaqawulini boYise, enezithunywa zakhe, andule ukubavuza bonke ngabanye ngokokwenza kwabo.', 'XHO22'),
(44, 16, 28, 'Inene, ndithi kuni, Kwabemiyo apha kukho abangayi kukha beve kufa, bade bambone uNyana woMntu esiza, esebukumkanini bakhe.', 'XHO22'),
(44, 17, 1, 'Uthi uYesu emveni kweentsuku ezintandathu, athabathe uPetros noYakobi noYohane umzalwana wakhe, abanyuse baye entabeni ende ngasese, bebodwa.', 'XHO22'),
(44, 17, 2, 'Wenziwa waba kumila kumbi phambi kwabo, bathi ubuso bakhe bakhazimla njengelanga, zasuka ke iingubo zakhe zamhlophe njengokukhanya.', 'XHO22'),
(44, 17, 3, 'Nanko kubonakala kubo uMoses noEliya, bethetha naye.', 'XHO22'),
(44, 17, 4, 'Waphendula ke uPetros, wathi kuYesu, Nkosi, kulungile ukuba sibe lapha; omnye ube ngowakho, omnye ube ngokaMoses, omnye ube ngokaEliya.', 'XHO22'),
(44, 17, 5, 'Kwathi, esathetha, kwathi gqi ilifu elikhanyayo, labenzela ithinzi; nalo izwi liphuma efini apho, lisithi, Lo nguNyana wam oyintanda, endikholisiweyo nguye; mveni yena.', 'XHO22'),
(44, 17, 6, 'Bakukuva oko abafundi, bawa ngobuso, boyika kakhulu.', 'XHO22'),
(44, 17, 7, 'Waya ke uYesu, wabachukumisa, wathi, Vukani ningoyiki.', 'XHO22'),
(44, 17, 8, 'Bathe ke bakuwaphakamisa amehlo abo, ababona mntu, yanguYesu yedwa.', 'XHO22'),
(44, 17, 9, 'Bakubon’ ukuba bayehla entabeni apho, wabawisela umthetho uYesu esithi, Ize ningawuxeleli bani umbono lo, ade avuke kwabafileyo uNyana woMntu.', 'XHO22'),
(44, 17, 10, 'Bambuza abafundi bakhe, besithi, Yini na phofu ukuba ababhali bathi, uEliya umelwe kukufika tanci?', 'XHO22'),
(44, 17, 11, 'Waphendula uYesu wathi kubo, Okunene uEliya uya kufika tanci, azimise zonke izinto.', 'XHO22'),
(44, 17, 12, 'Ke ndithi kuni, uEliya uselefikile. Abamazanga, basuka benza kuye konke abakuthandayo; kwangokunjalo naye uNyana woMntu wova ubunzima ngabo.', 'XHO22'),
(44, 17, 13, 'Bandula ukuqonda abafundi ukuba ubethetha kubo ngaye uYohane umbhaptizi.', 'XHO22'),
(44, 17, 14, 'Ke kaloku, bakubon’ ukuba bafikile esihlweleni, kweza kuye indoda, yaqubuda kuye, yathi.', 'XHO22'),
(44, 17, 15, 'Nkosi, menzele inceba unyana wam; ngokuba unesathuthwane, ubandezelekile; kuba futhi esiwa emlilweni, nasemanzini futhi.', 'XHO22'),
(44, 17, 16, 'Ndithe ndamzisa kubafundi bakho, ababa nako ukumphilisa.', 'XHO22'),
(44, 17, 17, 'Waphendula ke uYesu wathi, Sizukulwanandini singakholwayo, sigwenxa, koda kube nini na ndinani? Koda kube nini na ndininyamezele? Mziseni kum apha.', 'XHO22'),
(44, 17, 18, 'Waza uYesu wayikhalimela indimoni leyo; wemka kuye; waphiliswa umntwana kwangelo lixa.', 'XHO22'),
(44, 17, 19, 'Bandula beza abafundi kuYesu bebodwa, bathi, Bekungani na ukuba singabi nako ukuwukhupha thina?', 'XHO22'),
(44, 17, 20, 'Wathi ke uYesu kubo, Bekungenxa yokungakholwa kwenu; kuba inene ndithi kuni, Beningathi, ninokholo olunjengokhozo lwemostade, nithi kule ntaba, Suka apha uye phaya, isuke, ihambe; kwaye iya kufuduswa; akukho nanye into eya kuninqabela.', 'XHO22'),
(44, 17, 21, 'Olu hlobo ke aluphumi, kungengako ukuthandaza nokuzila ukudla.', 'XHO22'),
(44, 17, 22, 'Ke kaloku, bakubon’ ukuba bahleli kwelaseGalili, wathi uYesu kubo, uNyana woMntu uza kunikelwa ezandleni zabantu;', 'XHO22'),
(44, 17, 23, 'baya kumbulala, athi ngomhla wesithathu abuye avuke. Baba buhlungu kakhulu.', 'XHO22'),
(44, 17, 24, 'Ke, bakubon’ ukuba bafikile eKapernahum, kweza kuPetros abo bamkela imali yetempile, bathi, Umfundisi wenu akayirholi na imali yetempile?', 'XHO22'),
(44, 17, 25, 'Athi, Ewe. Akungena endlwini, uYesu wamphangela, esithi, Uthini na wena, Simon? Ookumkani behlabathi bamkela kubaphi na irhafu nomnikelo? koonyana babo, kwabasemzini, kusini na?', 'XHO22'),
(44, 17, 26, 'Athi uPetros kuye, Kwabasemzini. Athi uYesu kuye, Bakhululekile ngoko ke bona abantwana.', 'XHO22'),
(44, 17, 27, 'Kodwa ukuze singabakhubekisi, hamba uye elwandle, uphose umlobothi wakho, uthabathe loo ntlanzi ithe yavela tanci. Wothi wakuwunqika umlomo wayo, ufumane ishekele; yithabathe yona, ubanike, ibe yeyam nawe.', 'XHO22'),
(44, 18, 1, 'Ngelo lixa beza abafundi kuYesu, besithi, Ngubani na ke omkhulu kunabanye, ebukumkanini bamazulu?', 'XHO22'),
(44, 18, 2, 'Waza uYesu wabizela kuye umntwana, wammisa phakathi kwabo.', 'XHO22'),
(44, 18, 3, 'Wathi, Inene, ndithi kuni, Ukuba anithanga niguquke, nibe njengabantwana aba, aniyi kukha ningene ebukumkanini bamazulu.', 'XHO22'),
(44, 18, 4, 'Osukuba ngoko ethe wazithoba njengalo mntwana nguye omkhulu kunabanye ebukumkanini bamazulu.', 'XHO22'),
(44, 18, 5, 'Kanti othe wamkela umntwana onje, noko amnye, egameni lam, wamkela mna;', 'XHO22'),
(44, 18, 6, 'Ke othe wakhubekisa noko amnye waba bancinane bakholwayo kum, ngekumlungele kanye ukuba ubegangxwe ilitye lokusila entanyeni yakhe, antywiliselwe enzulwini yolwandle.', 'XHO22'),
(44, 18, 7, 'Yeha ihlabathi ngenxa yezikhubekiso! Kuba kumele ukuba zifike izikhubekiso; yeha ke loo mntu siza ngaye isikhubekiso!', 'XHO22'),
(44, 18, 8, 'Ukuba ke isandla sakho, nokuba lunyawo lwakho luya kukhubekisa, lunqumle, ululahle kude; kukulungele kanye ukuba ungene ebomini usisiqhwala, mhlawumbi usisilima, kunokuba uthi, unezandla ezibini, mhlawumbi uneenyawo ezimbini, uphoswe emlilweni ongunaphakade.', 'XHO22'),
(44, 18, 9, 'Nokuba iliso lakho liya kukhubekisa, linyothule, ulilahle; kukulungele kanye ukuba ungene ebomini unaliso linye, kunokuba uthi, unamehlo amabini, uphoswe esihogweni somlilo.', 'XHO22'),
(44, 18, 10, 'Lumkani, ningadeli namnye waba bancinane; Kuba ndithi kuni, Izithunywa zabo emazulwini zihlala zibubona ubuso bukaBawo osemazulwini.', 'XHO22'),
(44, 18, 11, 'Kuba uNyana woMntu weza kusindisa oko kulahlekileyo.', 'XHO22'),
(44, 18, 12, 'Nithini na? Ukuba umntu unekhulu lezimvu, ize kulahleke enye kuzo, akazishiyi na ezingamashumi asithoba anesithoba, aye ezintabeni, aye kufuna leyo ilahlekileyo?', 'XHO22'),
(44, 18, 13, 'Ukuba uthe wayifumana, inene ndithi kuni, uyayivuyela leyo ngaphezu kokuvuyela ezo zimashumi asithoba anesithoba, zingalahlekanga.', 'XHO22'),
(44, 18, 14, 'Kwangokunjalo asikukuthanda kukaYihlo osemazulwini, ukuba kulahleke namnye waba bancinane.', 'XHO22'),
(44, 18, 15, 'Ukuba ke umzalwana wakho uthe wakona, yiya umohlwaye inguwe naye ninedwa; ukuba uthe wakuva, woba umzuzile umzalwana wakho.', 'XHO22'),
(44, 18, 16, 'Ke ukuba uthe akeva buya uthabathe nawe abe mnye nokuba babini, ukuze onke amazwi amiswe ngomlomo wamangqina amabini, nokuba ngamathathu.', 'XHO22'),
(44, 18, 17, 'Ukuba ke uthe akabeva, xelela ibandla; ukuba uthe akaliva ibandla, kuwe makabe njengomntu weentlanga nombuthi werhafu.', 'XHO22'),
(44, 18, 18, 'Inene, ndithi kuni, Konke enithe nakubopha emhlabeni, kobotshwa emazulwini;', 'XHO22'),
(44, 18, 19, 'Kanjalo ndithi kuni, Ukuba ababini kuni bathe bavumelana emhlabeni apha ngeendawo zonke abangazicelayo, bozenzelwa nguBawo osemazulwini.', 'XHO22'),
(44, 18, 20, 'Kuba apho bahlanganisene khona egameni lam, nokuba babini nokuba bathathu, ndikho mna apho phakathi kwabo.', 'XHO22'),
(44, 18, 21, 'Weza uPetros kuye wathi, Nkosi, koba futhi kangaphi na umzalwana wam endona, ndimxolela? Koda kube kasixhenxe na?', 'XHO22'),
(44, 18, 22, 'Athi uYesu kuye, Andithi kuwe, koda kube kasixhenxe; ndithi, koda kube kamashumi asixhenxe aphindiweyo kasixhenxe.', 'XHO22'),
(44, 18, 23, 'Ngoko ke ubukumkani bamazulu bufanekiswa nomntu ongukumkani, owafuna ukubalelana nabakhonzi bakhe.', 'XHO22'),
(44, 18, 24, 'Kuthe ke akuqala ukubalelana nabo, kwaziswa kuye omnye onetyala leetalente ezingamawaka alishumi.', 'XHO22'),
(44, 18, 25, 'Kuthe ke ekubeni engenanto yakuhlawula, yathi inkosi yakhe, makuthengiswe ngaye, nangomfazi wakhe, nangabantwana bakhe, nangeento zonke anazo, kuhlawulwe.', 'XHO22'),
(44, 18, 26, 'Wawa phantsi ngoko umkhonzi lowo, waqubuda kuyo, esithi, Nkosi, khawundenze kuhle, ndokuhlawula lonke.', 'XHO22'),
(44, 18, 27, 'Yasikwa ke yimfesane inkosi yaloo mkhonzi, yamkhulula, yamxolela ityala elo.', 'XHO22'),
(44, 18, 28, 'Uphumile ke loo mkhonzi, wafumanana nomnye wabakhonza kunye naye obenetyala kuye leedenariyo ezilikhulu; wambamba, wamkrwitsha, esithi, Ndihlawule ityala onalo.', 'XHO22'),
(44, 18, 29, 'Wawa phantsi ngoko ezinyaweni zakhe lo ubekhonza kunye naye, wamana embongoza, esithi, Khawundenze kuhle, ndokuhlawula lonke.', 'XHO22'),
(44, 18, 30, 'Akavumanga ke; waya wamphosa entolongweni, ade alihlawule ityala elo.', 'XHO22'),
(44, 18, 31, 'Bathe ke abo babekhonza kunye naye, bakubona oko kwenzekileyo, baba buhlungu kakhulu, beza bayityela inkosi yabo konke oko kwenzekileyo.', 'XHO22'),
(44, 18, 32, 'Yaza inkosi yaso, yasibizela kuye, yathi kuso, mkhonzindini ungendawo, ndakuxolela lonke elaa tyala, ekubeni wandibongozayo;', 'XHO22'),
(44, 18, 33, 'ubungamelwe na kukuba ube nenceba nawe kokhonza kunye nawe, njengokuba nam bendinenceba kuwe?', 'XHO22'),
(44, 18, 34, 'Yaqumba inkosi yakhe, yamnikela kubathuthumbisi, ade alihlawule lonke ityala lakhe kuyo.', 'XHO22'),
(44, 18, 35, 'Wothi noBawo wasemazulwini enjenjalo kuni, ukuba anithanga nonke ngabanye nabaxolela abazalwana benu iziphoso zabo ngeentliziyo zenu.', 'XHO22'),
(44, 19, 1, 'Kwathi, xeshikweni uYesu ebewagqibile la mazwi, wanduluka kwelaseGalili, weza emideni yelakwaYuda, phesheya kweYordan.', 'XHO22'),
(44, 19, 2, 'Wayelandelwa ngabantu abaninzi; wabaphilisa khona.', 'XHO22'),
(44, 19, 3, 'Kweza kuye abaFarisi bemlinga, besithi kuye, Kuvumelekile na ukuba indoda imale umfazi wayo nangasiphi na isizathu?', 'XHO22'),
(44, 19, 4, 'Waphendula ke wathi kubo, Aniyilesanga na, ukuba lowo wabadalayo kwasekuqalekeni wabadala bayindoda nenkazana?', 'XHO22'),
(44, 19, 5, 'Wathi, Ngenxa yoko umntu womshiya uyise nonina, anamathele emfazini wakhe, baze abo babini babe nyama-nye?', 'XHO22'),
(44, 19, 6, 'Ngako oko abasebabini, banyama-nye. Oko ke ngoko uThixo akumanyileyo, makungahlulwa mntu.', 'XHO22'),
(44, 19, 7, 'Bathi kuye, Yini na ukuba uMoses awise umthetho wokuthi, makamnike incwadi yokwahlukana, amale?', 'XHO22'),
(44, 19, 8, 'Athi kubo, uMoses ngenxa yobulukhuni beentliziyo zenu wanivumela ukuba nibale abafazi benu; ke ekuqalekeni bekungenjalo.', 'XHO22'),
(44, 19, 9, 'Ndithi ke kuni, Othe wamala umfazi wakhe, kungengaluhenyuzo, wazeka omnye, uyakrexeza;', 'XHO22'),
(44, 19, 10, 'Bathi kuye abafundi bakhe, Ukuba unjalo umcimbi womntu nomfazi, akulungile ukuzeka.', 'XHO22'),
(44, 19, 11, 'Wathi ke yena kubo, Asibantu bonke abaliqondayo eli lizwi, ingengabo banikiweyo.', 'XHO22'),
(44, 19, 12, 'Kuba kukho amathenwa azalwa enjalo kwasesizalweni sonina; kukho namathenwa enziwa abantu amathenwa; kukho namathenwa azithenwayo ngenxa yobukumkani bamazulu. Lowo unako ukukwamkela, makakuthabathe;', 'XHO22'),
(44, 19, 13, 'Kwaza kwaziswa kuye abantwana, ukuze abeke izandla phezu kwabo, abathandazele; basuka abafundi babakhalimela.', 'XHO22'),
(44, 19, 14, 'Wathi ke uYesu, Bayekeni abantwana, ningabaleli ukuza kum; kuba ubukumkani bamazulu bobabo banjalo.', 'XHO22'),
(44, 19, 15, 'Wazibeka izandla phezu kwabo, wemka apho.', 'XHO22'),
(44, 19, 16, 'Kwabonakala kusiza othile, wathi kuye, Mfundisi ulungileyo, yiyiphi na into elungileyo endingenza yona, ukuze ndibe nabo ubomi?', 'XHO22'),
(44, 19, 17, 'Uthe ke yena kuye, Yini na ukuba uthi ndilungile? Akukho ulungileyo, mnye kwaphela, nguThixo. Ukuba ke uthe wangena ebomini, yigcine imithetho.', 'XHO22'),
(44, 19, 18, 'Athi kuye, Yiphi na? Wathi ke uYesu, uze ungabulali, uze ungakrexezi, uze ungebi, uze ungangqini buxoki;', 'XHO22'),
(44, 19, 19, 'Beka uyihlo nonyoko; nawo lo: uze umthande ummelwane wakho njengoko uzithanda ngako.', 'XHO22'),
(44, 19, 20, 'Ithi kuye indodana leyo, Zonke ezo zinto ndazigcina kwasebuncinaneni bam; ndisaswele ntoni na?', 'XHO22'),
(44, 19, 21, 'Wathi uYesu kuyo, Ukuba unga ungafezeka, hamba uye kuthengisa ngeempahla zakho, uphe amahlwempu, woba nobutyebi emazulwini; uze ke undilandele.', 'XHO22'),
(44, 19, 22, 'Ilivile ke indodana ilizwi elo, yemka ibuhlungu; kuba ibinemfuyo eninzi.', 'XHO22'),
(44, 19, 23, 'Waza wathi uYesu kubafundi bakhe, Inene, ndithi kuni, Koba ngenkankulu ukuba isityebi singene ebukumkanini bamazulu.', 'XHO22'),
(44, 19, 24, 'Kananjalo ke ndithi kuni, Kulula ukuba inkamela iphumele entunjeni yenaliti, kunokuba isityebi singene ebukumkanini bukaThixo.', 'XHO22'),
(44, 19, 25, 'Bakuva ke abafundi bakhe, bakhwankqiswa kakhulu, besithi, Ngubani na ke ongasindiswayo?', 'XHO22'),
(44, 19, 26, 'Ondele ke uYesu, wathi kubo, Kubantu le nto ayinakwenzeka; ke kuye uThixo zonke izinto zinako ukwenzeka.', 'XHO22'),
(44, 19, 27, 'Waphendula ke uPetros wathi kuye, Uyabona, thina sishiye iinto zonke sakulandela; siya kuba nantoni na ke?', 'XHO22'),
(44, 19, 28, 'Wathi ke uYesu kubo, Inene, ndithi kuni, Nina nindilandeleyo, ekuzalweni okutsha, xa athe wahlala uNyana woMntu phezu kwetrone yozuko lwakhe, niya kuhlala nani phezu kweetrone ezilishumi elinambini, nigweba ezizweni ezilishumi elinazibini zakwaSirayeli.', 'XHO22'),
(44, 19, 29, 'Umntu wonke oshiye izindlu, nokuba ngabazalwana, nokuba ngoodade, nokuba nguyise, nokuba ngunina, nokuba ngumfazi, nokuba ngabantwana, nokuba ngamasimi, ngenxa yegama lam, uya kwamkela kalikhulu, abudle ilifa ubomi obungunaphakade.', 'XHO22'),
(44, 19, 30, 'Ke baninzi abokuqala abaya kuba ngabokugqibela; baze abokugqibela babe ngabokuqala.', 'XHO22'),
(44, 20, 1, 'Kaloku ubukumkani bamazulu bufana nomntu ongumninindlu, owaphuma ekuseni, waya kuqesha abasebenzi bokuya esidiliyeni sakhe.', 'XHO22'),
(44, 20, 2, 'Akuba evumelene nabasebenzi ngedenariyo ngemini, wabathumela esidiliyeni sakhe.', 'XHO22'),
(44, 20, 3, 'Ephume malunga nelixa lesithathu, wabona abanye bemi endaweni yembutho bengasebenzi.', 'XHO22'),
(44, 20, 4, 'Wathi kubo; Hambani nani niye esidiliyeni, ndoninika okusukuba kufanelekile. Bahamba ke.', 'XHO22'),
(44, 20, 5, 'Wabuya waphuma malunga nelixa lesithandathu nelesithoba, wenza kwangokunjalo.', 'XHO22'),
(44, 20, 6, 'Ephumile ke malunga nelixa leshumi elinanye, wabafumana abanye bemi bengasebenzi; athi kubo, Yini na ukuba nime apha imini yonke ningasebenzi?', 'XHO22'),
(44, 20, 7, 'Bathi kuye, Akukho bani usiqeshileyo. Athi kubo, Hambani nani niye esidiliyeni; kwaye nakuphi na okufanelekileyo niya kukwamkela.', 'XHO22'),
(44, 20, 8, 'Kwakuhlwa, uthi umninisidiliya egoseni lakhe, Babize abasebenzi, ubamkelise, uqala kwabokugqibela ude uye kwabokuqala.', 'XHO22'),
(44, 20, 9, 'Bezile abo bebeqeshwe malunga nelixa leshumi elinanye, bamkela elowo idenariyo.', 'XHO22'),
(44, 20, 10, 'Ke kaloku bakuza abokuqala, baba baya kwamkela into engaphezulu; nabo bamkela elowo idenariyo.', 'XHO22'),
(44, 20, 11, 'Bathe ke bakwamkela, bamkrokrela umninindlu,', 'XHO22'),
(44, 20, 12, 'besithi, Aba bokugqibela basebenze ilixa lalinye, usuke wabalinganisa nathi, thina sithwele ubunzima nobushushu bemini.', 'XHO22'),
(44, 20, 13, 'Uphendule ke wathi komnye wabo, Wethu, andikoni; ubungavumelananga nam ngedenariyo yini na?', 'XHO22'),
(44, 20, 14, 'Thabatha okwakho uhambe; ndiya kumnika lowo wokugqibela kwanjengoko ndikunike ngako.', 'XHO22'),
(44, 20, 15, 'Akuvumelekile na ukuba ndenze endikuthandayo ngokukokwam? Iliso lakho likhohlakele na, ngokuba ndilungile mna?', 'XHO22'),
(44, 20, 16, 'Ngokunjalo abokugqibela baya kuba ngabokuqala, nabokuqala, baya kuba ngabokugqibela; kuba baninzi ababiziweyo, ke bambalwa abanyuliweyo.', 'XHO22'),
(44, 20, 17, 'Ke kaloku, enyuka uYesu esiya eYerusalem, wabathabatha abafundi abalishumi elinababini ngasese endleleni, wathi kubo,', 'XHO22'),
(44, 20, 18, 'Niyabona, siyenyuka siya eYerusalem; waye uNyana woMntu eya kunikelwa kubabingeleli abakhulu nakubabhali, kwaye baya kumgweba ukuba afe,', 'XHO22'),
(44, 20, 19, 'bamnikele kuzo iintlanga ukuba zidlale ngaye, zimtyakatye, zimbethelele emnqamlezweni, athi ngomhla wesithathu abuye avuke.', 'XHO22'),
(44, 20, 20, 'Ngelo xesha kweza kuye unina woonyana bakaZebhedi, enoonyana bakhe, equbuda, ecela into kuye.', 'XHO22'),
(44, 20, 21, 'Wathi ke yena kuye, Uthanda ntoni na? Athi kuye, Yithi aba nyana bam bobabini bahlale, omnye ngasekunene kwakho, omnye ngasekhohlo, ebukumkanini bakho.', 'XHO22'),
(44, 20, 22, 'Uphendule ke uYesu wathi, Aniyazi into eniyicelayo. Ninako na ukuyisela indebe endiza kuyisela, nibhaptizwe ngobhaptizo endibhaptizwa ngalo mna? Bathi kuye, Sinako.', 'XHO22'),
(44, 20, 23, 'Athi kubo, Okunene indebe yam niya kuyisela, nobhaptizo endibhaptizwa ngalo mna niya kubhaptizwa kwangalo; kodwa kona ukuhlala ngasekunene kwam nangasekhohlo kwam asikwam ukupha ngako, asikwam ukupha ngako; kwabo kulungiselwe bona nguBawo.', 'XHO22'),
(44, 20, 24, 'Bekuvile ke oko abalishumi, babacaphukela abo bazalwana bobabini.', 'XHO22'),
(44, 20, 25, 'Ke yena uYesu, ebabizele kuye, wathi, Niyazi ukuba abaphathi beentlanga bayazigagamela, nabo bazizikhulu benza ngegunya kuzo.', 'XHO22'),
(44, 20, 26, 'Ze kungabi njalo ke phakathi kwenu; osukuba enga angaba mkhulu phakathi kwenu, makabe ngumlungiseleli wenu;', 'XHO22'),
(44, 20, 27, 'nosukuba efuna ukuba ngowokuqala phakathi kwenu, makabe ngumlungiseleli wenu;', 'XHO22'),
(44, 20, 28, 'kwanjengokuba uNyana woMntu engezanga kulungiselelwa; weza kulungiselela, nokuncama ubomi bakhe, bube yintlawulelo yokukhulula abaninzi.', 'XHO22'),
(44, 20, 29, 'Bakubon’ ukuba bayaphuma eYeriko, walandelwa yindimbane enkulu.', 'XHO22'),
(44, 20, 30, 'Nanko kuhleli iimfama zimbini ngasendleleni, zathi, zakuva ukuba kudlula uYesu, zadanduluka, zisithi, Senzele inceba, Nkosi, nyana kaDavide.', 'XHO22'),
(44, 20, 31, 'Ke kaloku indimbane yazikhalimela ukuba zithi tu; kwaba kukhona ke zidandulukayo, zisithi, Senzele inceba, Nkosi, nyana kaDavide.', 'XHO22'),
(44, 20, 32, 'Wema uYesu, wazibiza wathi, Ninga ndinganenzela ntoni na?', 'XHO22'),
(44, 20, 33, 'Zithi kuye, Nkosi, ukuba kuvulwe amehlo ethu.', 'XHO22'),
(44, 20, 34, 'Wasikwa ke yimfesane uYesu, wawachukumisa amehlo azo, zaba nokubona kwaoko ngamehlo azo, zamlandela.', 'XHO22'),
(44, 21, 1, 'Ke kaloku, bakusondela eYerusalem, befikile eBhetefage, eNtabeni yemiNquma, uYesu wesusa abafundi bababini, wathi,', 'XHO22'),
(44, 21, 2, 'Athi kubo, Hambani niye emzaneni lo ukhangelene nani, nofumana kwaoko iesile libotshelelwe, linethole; likhululeni nilizise kum.', 'XHO22'),
(44, 21, 3, 'Ukuba kukho othe wathetha into kuni, nothi, Ayafuneka eNkosini; wothi kwaoko azithumele.', 'XHO22'),
(44, 21, 4, 'Konke oku kwenzeke, ukuze kuzaliseke okwathethwayo ngomprofeti, esithi,', 'XHO22'),
(44, 21, 5, 'Yithini kwintombi uZiyon, Yabona, uKumkani wakho uza kuwe, Elulamile, ekhwele e-esileni, nethole le-esile.', 'XHO22'),
(44, 21, 6, 'Bahamba ke abafundi, benza njengoko uYesu wayebayalele ngako,', 'XHO22'),
(44, 21, 7, 'balizisa iesile nethole, babeka iingubo zabo phezu kwazo, bamhlalisa phezu kwazo.', 'XHO22'),
(44, 21, 8, 'Yathi ke inkoliso yezihlwele yazandlala iingubo zayo endleleni; abanye bagawula amasebe emithini, bawandlala endleleni.', 'XHO22'),
(44, 21, 9, 'Zaye ke izihlwele, ebezihamba ngaphambili, nebezilandela, zidanduluka, zisithi, Hosana kuye unyana kaDavide! Makabongwe lowo uzayo egameni leNkosi; Hosana enyangweni.', 'XHO22'),
(44, 21, 10, 'Akungena eYerusalem, wonke umzi wazamazama, usithi, Ngubani na lo?', 'XHO22'),
(44, 21, 11, 'Zathi ke indimbane, Lo nguYesu, umprofeti ongowaseNazarete yelaseGalili.', 'XHO22'),
(44, 21, 12, 'Waza uYesu wangena etempileni kaThixo, wabakhupha bonke ababethengisa nababethenga etempileni, wazibhukuqa iitafile zabatshintshi bemali, nezihlalo zabathengisa ngamahobe.', 'XHO22'),
(44, 21, 13, 'Wathi kubo, Kubhaliwe kwathiwa, Indlu yam iya kubizwa ngokuba yindlu yokuthandaza; Ke nina niyenze umqolomba wezihange.', 'XHO22'),
(44, 21, 14, 'Kweza kuye iimfama neziqhwala, etempileni apho, waziphilisa, wabaphilisa.', 'XHO22'),
(44, 21, 15, 'Bathe ke ababingeleli abakhulu nababhali, bakuyibona imimangaliso awayenzayo, kwanabantwana bedanduluka etempileni, besithi, Hosana kuye uNyana kaDavide: bacaphuka kakhulu,', 'XHO22'),
(44, 21, 16, 'bathi kuye, Uyayiva na into abayithethayo aba? Athi kubo uYesu, Ewe; anizanga nikulese na ukuthi, Emlonyeni weentsana nabanyayo ulungiselele indumiso?', 'XHO22'),
(44, 21, 17, 'Wabashiya, waphuma ngaphandle komzi, waya eBhetani; walala khona.', 'XHO22'),
(44, 21, 18, 'Uthe ke kwakusa, xa abuyela kuwo umzi, walamba.', 'XHO22'),
(44, 21, 19, 'Wathi, ebone mkhiwane uthile ngasendleleni, waya kuwo, akafumana nto kuwo, yangamagqabi odwa; wathi kuwo, Makungaze kuvele siqhamo kuwe naphakade. Wabuna kwangoko umkhiwane lowo.', 'XHO22'),
(44, 21, 20, 'Bakukubona oko abafundi bakhe, bamangaliswa, besithi, Utheni na ukubuna kwangoko umkhiwane?', 'XHO22'),
(44, 21, 21, 'Waphendula uYesu wathi kubo, Inene, ndithi kuni, Ukuba nithe nanokholo, anathandabuza, anisayi kwenza le nto yomkhiwane yodwa; nothi kule ntaba, Thabatheka; uphoseke elwandle; iya kwenziwa.', 'XHO22'),
(44, 21, 22, 'Neento zonke enithe nazicela ekuthandazeni nikholwa, nozamkela.', 'XHO22'),
(44, 21, 23, 'Kuthe akungena etempileni, xa afundisayo, beza kuye ababingeleli abakhulu namadoda amakhulu abantu, besithi, Ezi zinto uzenza ngagunya lini na? ngubani na ke okunike eli gunya?', 'XHO22'),
(44, 21, 24, 'Waphendula ke uYesu wathi kubo, Nam ndiya kubuza kuni zwi-nye; endothi, ukuba nithe nandixelela lona, ndinixelele nam ukuba ndizenza ngagunya lini na ezi zinto.', 'XHO22'),
(44, 21, 25, 'Ubhaptizo lukaYohane lwaluphuma phi na? ezulwini, nokuba ngabantu? Bacamanga ke bona bodwa, besithi, Ukuba sithe, Lwaphuma emazulwini; wothi kuthi, Ibiyini na phofu ukuba ningakholwa nguye?', 'XHO22'),
(44, 21, 26, 'Ukuba ke sithe, Lwaphuma ebantwini; siyaboyika abantu; kuba bonke bebambe ukuthi uYohane ungumprofeti.', 'XHO22'),
(44, 21, 27, 'Baphendula bathi kuYesu, Asazi. Wathi ke kubo, Nam ke andinixeleli ukuba ndizenza ngagunya lini na ezi zinto.', 'XHO22'),
(44, 21, 28, 'Nithini na ke? Umntu othile ube enoonyana ababini; weza kowokuqala, wathi, Mntwana wam, hamba uye kusebenza namhla esidiliyeni sam.', 'XHO22'),
(44, 21, 29, 'Waphendula wathi, Andiyi; waguquka kamva, wemka.', 'XHO22'),
(44, 21, 30, 'Weza nakowesibini, wathetha kwangokunjalo. Waphendula ke yena wathi, Ndiyaya, nkosi;', 'XHO22'),
(44, 21, 31, 'Nguwuphi na kwabo bobabini owenza ukuthanda kukayise? Bathi kuye, Owokuqala. Athi kubo uYesu, Inene ndithi kuni, Bayaniphangela ke ukungena ebukumkanini bukaThixo ababuthi berhafu namahenyukazi.', 'XHO22'),
(44, 21, 32, 'Ngokuba uYohane weza kuni ngendlela yobulungisa, anakholwa nguye; kodwa bakholwa nguye bona ababuthi berhafu namahenyukazi; ke nina, nakukubona oko, anibuyanga nizohlwaye, ukuze nikholwe nguye.', 'XHO22'),
(44, 21, 33, 'Yivani omnye umzekeliso: Bekukho umninindlu othile, owatyala isidiliya, wasibiyela, wemba kuso isixovulelo seediliya, wakha inyango, waqeshisa ngaso kubalimi, wahambela kwelinye.', 'XHO22'),
(44, 21, 34, 'Lithe ke lakusondela ixesha leziqhamo, wathuma abakhonzi bakhe kubalimi abo, ukuya kwamkela iziqhamo zaso.', 'XHO22'),
(44, 21, 35, 'Besuka abalimi babathabatha abakhonzi bakhe, bathi omnye bamtyabula, omnye bambulala, nomnye bamgibisela ngamatye.', 'XHO22'),
(44, 21, 36, 'Wabuya ke wathuma abanye abakhonzi, bebaninzi kunabokuqala, benjenjalo nakubo.', 'XHO22'),
(44, 21, 37, 'Uthe ke kamva wathuma kubo unyana wakhe, esithi, Baya kumhlonela yena unyana wam.', 'XHO22'),
(44, 21, 38, 'Bathe ke bakumbona unyana abalimi, batshono ukuthi, Lo yindlalifa; yizani siyibulale, silihluthe ilifa layo.', 'XHO22'),
(44, 21, 39, 'Bamthabatha, bamkhuphela ngaphandle kwesidiliya, bambulala.', 'XHO22'),
(44, 21, 40, 'Xa athe wafika ngoko umninisidiliya, uya kwenza ntoni na kwabo balimi?', 'XHO22'),
(44, 21, 41, 'Bathi kuye, Uya kubatshabalalisa kakubi abo bangendawo, aze isidiliya eso aqeshise ngaso kubalimi bambi, abomamkelisa iziqhamo ngamaxesha azo.', 'XHO22'),
(44, 21, 42, 'Athi kubo uYesu, Anizanga nikulese na ezibhalweni ukuthi, Ilitye abalicekisayo abakhi, Lelona laba yintloko yembombo; yenziwe yiNkosi le;', 'XHO22'),
(44, 21, 43, 'Kungoko ndithi kuni, Ubukumkani bukaThixo buya kususwa kuni, bunikwe uhlanga oluzivelisayo iziqhamo zabo.', 'XHO22'),
(44, 21, 44, 'Kanjalo lowo uwe phezu kweli litye uya kutyumka, ke lowo lithe lamwela liya kumcola.', 'XHO22'),
(44, 21, 45, 'Bathi ababingeleli abakhulu nabaFarisi, bakuyiva imizekeliso yakhe, baqonda ukuba uthetha ngabo.', 'XHO22'),
(44, 21, 46, 'Baza befuna ukumbamba, basuka boyika abantu, kuba babesithi ungumprofeti.', 'XHO22'),
(44, 22, 1, 'Wabuya waphendula uYesu wathetha kubo ngemizekeliso, wathi,', 'XHO22'),
(44, 22, 2, 'Ubukumkani bamazulu bufanekiswa nomntu ongukumkani, owenzela unyana wakhe umtshato;', 'XHO22'),
(44, 22, 3, 'Wasusa abakhonzi bakhe ukuba baye kubabiza abamenyiweyo, beze emsithweni, kodwa abavumanga ukuza.', 'XHO22'),
(44, 22, 4, 'Wabuya wesusa abanye abakhonzi, esithi, Yitshoni kwabamenyiweyo ukuthi, Yabonani, isidlo sam sendisilungisile; iinkomo zam neento zam ezityetyisiweyo zixheliwe, konke sekulungile; yizani emsithweni.', 'XHO22'),
(44, 22, 5, 'Abakhathalanga ke bona; basuka bemka, omnye waya kweyakhe intsimi, omnye waya ekurhwebeni kwakhe.', 'XHO22'),
(44, 22, 6, 'Bathi ke abaseleyo bababamba abakhonzi bakhe, babaphatha kakubi, bababulala.', 'XHO22'),
(44, 22, 7, 'Evile ke ukumkani, waqumba, wawukhupha umkhosi wakhe, wabatshabalalisa abo babulali, wawutshisa nomzi wabo.', 'XHO22'),
(44, 22, 8, 'Aze athi kubakhonzi bakhe, Umsitho okunene sewulungile wona, kodwa abamenyiweyo bebengafanelekile.', 'XHO22'),
(44, 22, 9, 'Yiyani ngoko ezindleleni, nithi abo nithe nabafumana, nibabize beze emsithweni.', 'XHO22'),
(44, 22, 10, 'Baphuma abo bakhonzi baya ezindleleni, bahlanganisa bonke ababafumanayo, abakhohlakeleyo kwanabalungileyo; wazala umsitho ngabamenyiweyo.', 'XHO22'),
(44, 22, 11, 'Ke kaloku akungena ukumkani, eza kukhangela abamenyiweyo, wabona apho umntu ongambethe ngubo yamsitho.', 'XHO22'),
(44, 22, 12, 'Athi kuye, Wethu, utheni na ukungena apha, ungenáyo ingubo yomsitho? Wabe engathethi.', 'XHO22'),
(44, 22, 13, 'Wathi ukumkani kubalungiseleli, Mbopheni iinyawo nezandla, nimthabathe, nimkhuphele ebumnyameni bangaphandle; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 22, 14, 'Kuba baninzi ababiziweyo, ke bambalwa abanyuliweyo.', 'XHO22'),
(44, 22, 15, 'Baza abaFarisi baya benza ibhunga lokuba bamthiyele ekuthetheni kwakhe.', 'XHO22'),
(44, 22, 16, 'Bathumela kuye abafundi babo benamaHerode, besithi, Mfundisi, siyazi ukuba uyinyaniso, uyifundisa indlela kaThixo ngenyaniso; kananjalo akukhathali nangubani, kuba akukhethi buso bamntu.', 'XHO22'),
(44, 22, 17, 'Sixelele ngoko, uthini na wena? Kuvumelekile na ukumnika irhafu uKesare, akuvumelekile, sini na?', 'XHO22'),
(44, 22, 18, 'Eyazi ke uYesu inkohlakalo yabo, wathi, Nindilingelani na, bahanahanisindini?', 'XHO22'),
(44, 22, 19, 'Ndiboniseni iqhosha lerhafu. Bamzisela ke idenariyo.', 'XHO22'),
(44, 22, 20, 'Athi kubo, Ngokabani na lo mfanekiso nombhalo?', 'XHO22'),
(44, 22, 21, 'Bathi kuye, NgokaKesare. Athi kubo, Ngoko ke buyiselani izinto zikaKesare kuKesare; nezinto zikaThixo kuThixo.', 'XHO22'),
(44, 22, 22, 'Bakukuva ke oko, bamangaliswa, bamshiya, bemka.', 'XHO22'),
(44, 22, 23, 'Kwangaloo mini kweza kuye abaSadusi, bona abo bathi akukho luvuko, bambuza, besithi,', 'XHO22'),
(44, 22, 24, 'besithi, Mfundisi, uMoses wathi, Ukuba ubani uthe wafa engenabantwana, wothi umzalwana wakhe amngene umfazi wakhe, amvelisele imbewu umzalwana wakhe.', 'XHO22'),
(44, 22, 25, 'Ke kaloku kwaye kukho kuthi abazalwana abasixhenxe; waza owokuqala akubon’ ukuba uzekile wabhubha, wathi, kuba engenambewu, wamshiya kumzalwana wakhe umfazi wakhe.', 'XHO22'),
(44, 22, 26, 'Wenjenjalo nowesibini, nowesithathu, kwada kwaya kowesixhenxe.', 'XHO22'),
(44, 22, 27, 'Emveni ke kwabo bonke, wafa naye umfazi.', 'XHO22'),
(44, 22, 28, 'Ngoko eluvukweni uya kuba ngumfazi wawuphi na kweso sixhenxe? kuba bonke babenaye.', 'XHO22'),
(44, 22, 29, 'Waphendula uYesu wathi kubo, Niyalahleka, kuba ningazazi izibhalo kwanamandla kaThixo.', 'XHO22'),
(44, 22, 30, 'Kuba eluvukweni abazeki, abendiswa; banjengezithunywa zikaThixo emazulwini.', 'XHO22'),
(44, 22, 31, 'Ngalo ke uvuko lwabafileyo, anikulesanga na okwathethwayo kuni nguThixo, esithi,', 'XHO22'),
(44, 22, 32, 'Mna ndinguThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi? uThixo akangowabafileyo, ungowabaphilileyo.', 'XHO22'),
(44, 22, 33, 'Zathi izihlwele zakukuva oko, zakhwankqiswa yimfundiso yakhe.', 'XHO22'),
(44, 22, 34, 'Ke kaloku bathe abaFarisi, bakuva ukuba ubethe labakhohla abaSadusi, bahlanganiselana ndawonye.', 'XHO22'),
(44, 22, 35, 'Ke kaloku omnye wabo, ongumqondisi-mthetho, wambuza umbuzo, emlinga, esithi,', 'XHO22'),
(44, 22, 36, 'Mfundisi, owona myalelo mkhulu emthethweni nguwuphi na?', 'XHO22'),
(44, 22, 37, 'Wathi ke uYesu kuye, Uze uyithande iNkosi uThixo wakho ngentliziyo yakho iphela, nangomphefumlo wakho uphela, nangengqondo yakho iphela.', 'XHO22'),
(44, 22, 38, 'Lo ngowokuqala nomkhulu umthetho.', 'XHO22'),
(44, 22, 39, 'Owesibini ke ufana nawo: Uze umthande ummelwane wakho ngoko uzithanda ngako.', 'XHO22'),
(44, 22, 40, 'Kule mithetho yomibini kuxhomekeke umyalelo uphela nabaprofeti.', 'XHO22'),
(44, 22, 41, 'Bakubon’ ukuba bahlanganisene ke abaFarisi, uYesu wababuza,', 'XHO22'),
(44, 22, 42, 'esithi, Nithini na nina ngoKristu? ngunyana kabani na? Bathi kuye, Nyana kaDavide.', 'XHO22'),
(44, 22, 43, 'Athi kubo, Yini na phofu ukuba uDavide ngoMoya ambize ngokuthi, Nkosi, esithi,', 'XHO22'),
(44, 22, 44, 'Yathi iNkosi eNkosini yam, Hlala ngasekunene kwam, Ndide iintshaba zakho ndizenze isihlalo seenyawo zakho?', 'XHO22'),
(44, 22, 45, 'Ukuba uDavide ngoko umbiza ngokuba Nkosi, ungunyana wakhe ngani na?', 'XHO22'),
(44, 22, 46, 'Akubangakho namnye ubenako ukumphendula nezwi eli; kananjalo kususela kuloo mini, akubanga sabakho mntu waba nabuganga bakumbuza nto.', 'XHO22'),
(44, 23, 1, 'Waza uYesu wathetha kwindimbane nakubafundi bakhe,', 'XHO22'),
(44, 23, 2, 'besithi, Ababhali nabaFarisi bahleli esihlalweni sikaMoses;', 'XHO22'),
(44, 23, 3, 'Konke ngoko abasukuba besithi kuni kugcineni, ze nikugcine nikwenze; kodwa ze ningenzi ngokwemisebenzi yabo; kuba bayathetha baze bangenzi.', 'XHO22'),
(44, 23, 4, 'Kuba babopha imithwalo enzima, esindayo, bayibeke emagxeni abantu; kodwa bona abafuni kuzishukumisa nangomnwe lo wabo.', 'XHO22'),
(44, 23, 5, 'Yonke imisebenzi yabo ke bayenza ukuze babonwe ngabantu; bawenza banzi amafilakteriyu abo, bayandise imiqukumbelo yeengubo zabo.', 'XHO22'),
(44, 23, 6, 'Bathanda izihlalo eziphambili esidlweni, nezihlalo eziphambili ezindlwini zesikhungu,', 'XHO22'),
(44, 23, 7, 'nemibuliso ezindaweni zembutho, nokubizwa kwabantu ngokuthi, Rabhi, Rabhi.', 'XHO22'),
(44, 23, 8, 'Ke nina maningabizwa kuthiwe Rabhi, kuba mnye uMkhokeli wenu, uKristu; nina ke nonke ningabazalwana.', 'XHO22'),
(44, 23, 9, 'Ningabizi mntu ngokuba nguyihlo emhlabeni apha; kuba mnye uYihlo wenu, lowo usemazulwini.', 'XHO22'),
(44, 23, 10, 'Kananjalo maningabizwa ngokuba ningabakhokeli; kuba mnye uMkhokeli wenu, uKristu.', 'XHO22'),
(44, 23, 11, 'Omkhulu ke kuni makabe ngumlungiseleli wenu.', 'XHO22'),
(44, 23, 12, 'Osukuba ke eziphakamisa uya kuthotywa; nozithobayo uya kuphakanyiswa.', 'XHO22'),
(44, 23, 13, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! Kuba nibuvala ubukumkani bamazulu phambi kwabantu; kuba aningeni nina, nabangenayo anibavumeli ukuba bangene.', 'XHO22'),
(44, 23, 14, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! ngokuba nizidla izindlu zabahlolokazi, nize ngokunyhwalaza nithandaze uzungu;', 'XHO22'),
(44, 23, 15, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! Ngokuba nijikajika ulwandle nomhlaba ukuze nenze abe mnye ongena ebuYudeni; nithi ke, nakuba nibe, nimenze abe ngumntwana wesihogo ngokuphindiweyo kunani.', 'XHO22'),
(44, 23, 16, 'Yeha, nina bakhokelindini baziimfama! Nina nithi, Othe wafunga itempile, akunto oko; ke othe wafunga igolide yetempile, unetyala.', 'XHO22'),
(44, 23, 17, 'Ziyatha, zimfama, kanene yiyiphi na enkulu? Yigolide, yitempile eyingcwalisayo igolide, kusini na?', 'XHO22'),
(44, 23, 18, 'Kanjalo nothi, Othe wafunga isibingelelo, akunto; ke othe wafunga umnikelo ophezu kwawo, unetyala.', 'XHO22'),
(44, 23, 19, 'Ziyatha, zimfama, kanene yiyiphi na enkulu? Ngumnikelo, sisibingelelo esiwungcwalisayo umnikelo, kusini na?', 'XHO22'),
(44, 23, 20, 'Lowo usifungayo ngoko isibingelelo ufunga sona neento zonke eziphezu kwaso.', 'XHO22'),
(44, 23, 21, 'Nalowo uyifungayo itempile, ufunga yona nomiyo kuyo.', 'XHO22'),
(44, 23, 22, 'Nalowo usifungayo izulu, ufunga itrone kaThixo nohleli phezu kwayo.', 'XHO22'),
(44, 23, 23, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! Kuba ninikela izishumi zetyeleba nedile nekumin, kanti nizishiyile ezona ndawo zinzima zomthetho, umgwebo nenceba nokholo; ezi benimelwe kukuba nizenze, nithi neziya ningazishiyi.', 'XHO22'),
(44, 23, 24, 'Bakhokelindini baziimfama, abahluza imbuzane, niginye inkamela.', 'XHO22'),
(44, 23, 25, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! Kuba nihlambulula umphandle wendebe nesitya, kanti ngaphakathi zizele kukuphanga nokugqithisela.', 'XHO22'),
(44, 23, 26, 'mFarisindini, imfama, hlambulula kuqala okungaphakathi kwendebe nesitya, ukuze nomvandedwa ube ube nyulu.', 'XHO22'),
(44, 23, 27, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! ngokuba nifana namangcwaba aqatywe mhlophe, abonakala emahle ngaphandle, kanti ngaphakathi azele ngamathambo abafileyo nako konke ukungcola.', 'XHO22'),
(44, 23, 28, 'Ngokukwanjalo nani ngaphandle nibonakala ebantwini ukuba ningamalungisa, kanti ngaphakathi nizele luhanahaniso nokuchasa umthetho.', 'XHO22'),
(44, 23, 29, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! ngokuba niwakha amangcwaba abaprofeti, nivathise amangcwaba amalungisa;', 'XHO22'),
(44, 23, 30, 'Kwaye nithi, Ukuba sasikho thina kwimihla yoobawo, singe singabanga nabudlelane nabo ngegazi labaprofeti.', 'XHO22'),
(44, 23, 31, 'Ngoko niyangqina kwanina, ukuba ningoonyana babo babulala abaprofeti.', 'XHO22'),
(44, 23, 32, 'Wuzaliseni ngoko umlinganiso wooyihlo.', 'XHO22'),
(44, 23, 33, 'Zinyokandini, nzalandini yamarhamba, ningáthini na ukuwusaba umgwebo wesihogo?', 'XHO22'),
(44, 23, 34, 'Ngenxa yoko, niyabona, mna ndinithumela abaprofeti nezilumko nababhali; nithi ke inxenye kubo niyibulale, niyibethelele eminqamlezweni; nithi inxenye kubo niyityakatye ezindlwini zenu zesikhungu, niyitshutshise kwimizi ngemizi;', 'XHO22'),
(44, 23, 35, 'ukuze linifikele lonke igazi elinobulungisa, elaphalazwayo emhlabeni, lithabathela egazini lika-Abheli ilungisa, lizise egazini likaZekariya, unyana kaBharakiya, enambulalayo phakathi kwetempile nesibingelelo.', 'XHO22'),
(44, 23, 36, 'Inene, ndithi kuni, Zonke ezo zinto ziya kusihlela esi sizukulwana.', 'XHO22'),
(44, 23, 37, 'Yerusalem, Yerusalem, wena ubabulalayo abaprofeti, ubagibisele ngamatye abo bathunywe kuwe, kufuthi kangakanani na ndithande ukubabuthela ndawonye abantwana bakho, ngohlobo esithi isikhukukazi siwabuthele ndawonye amathole aso ngaphantsi kwamaphiko, anavuma!', 'XHO22'),
(44, 23, 38, 'Yabonani, indlu yenu ishiywa nani ize.', 'XHO22'),
(44, 23, 39, 'Kuba ndithi kuni, Aniyi kuba sandibona emveni koku, nide nithi, Makabongwe lowo uzayo egameni leNkosi.', 'XHO22'),
(44, 24, 1, 'Ephumile uYesu, wemka etempileni; bathe abafundi bakhe beza kumbonisa izakhiwo zetempile.', 'XHO22'),
(44, 24, 2, 'Wathi kubo uYesu, Niyazibona na zonke ezi zinto? Inene, ndithi kuni, Akusayi kusala apha ilitye phezu kwelitye, elingayi kuchithwa.', 'XHO22'),
(44, 24, 3, 'Ke kaloku, akubon’ ukuba uhleli eNtabeni yemiNquma, beza kuye abafundi bebodwa, bathi, Sixelele ukuba ziya kubakho nini na ezo zinto; uya kuba yini na umqondiso wokufika kwakho, nowokupheliswa kwephakade eli?', 'XHO22'),
(44, 24, 4, 'Waphendula uYesu wathi kubo, Lumkani ningalahlekiswa mntu.', 'XHO22'),
(44, 24, 5, 'Kuba baninzi abaya kuza egameni lam, besithi, banguye uKristu; balahlekise abantu abaninzi.', 'XHO22'),
(44, 24, 6, 'Niya kuthi ke nive iimfazwe namarhe eemfazwe; lumkani ningabi navuso, kuba zonke ezo zinto zimele ukuhla; kanti kona ukuphela akukabikho.', 'XHO22'),
(44, 24, 7, 'Kuba uhlanga luya kuvukelana nohlanga, nobukumkani buvukelane nobukumkani; kuya kubakho iindlala, neendyikitya zokufa, nezinyikimo zomhlaba kwiindawo ngeendawo.', 'XHO22'),
(44, 24, 8, 'Zonke ke ezo zinto zikukuqaleka kwenimba.', 'XHO22'),
(44, 24, 9, 'Ngelo xesha baya kuninikela ekubandezelweni, banibulale; niya kuthiywa ziintlanga zonke ngenxa yegama lam.', 'XHO22'),
(44, 24, 10, 'Ngelo xesha baninzi abaya kukhutyekiswa, bangcatshane, bathiyane.', 'XHO22'),
(44, 24, 11, 'Kwaye kuya kuvela abaprofeti ababuxoki babe baninzi, balahlekise abantu abaninzi.', 'XHO22'),
(44, 24, 12, 'Nangenxa yokwanda kokuchasa umthetho, luya kuphola uthando lwabaninzi.', 'XHO22'),
(44, 24, 13, 'Ke onyamezeleyo kwada kwaba sekupheleni, lowo uya kusindiswa.', 'XHO22'),
(44, 24, 14, 'Zaye ezi ndaba zilungileyo zobukumkani ziya kuvakaliswa kulo elimiweyo liphela, zibe bubungqina kuzo zonke iintlanga; kwandule ke kufike ukuphela.', 'XHO22'),
(44, 24, 15, 'Xa nithe nalibona isikizi lesiphanziso, ekuthethwe ngalo ngoDaniyeli umprofeti, limi endaweni engcwele (lowo ulesayo makaqiqe);', 'XHO22'),
(44, 24, 16, 'Ngelo xa mabathi abakwelakwaYuda basabele ezintabeni;', 'XHO22'),
(44, 24, 17, 'Ophezu kwendlu makangehli aye kuthabatha into endlwini yakhe;', 'XHO22'),
(44, 24, 18, 'nosentsimini makangabuyeli emva aye kuthabatha iingubo zakhe.', 'XHO22'),
(44, 24, 19, 'Yeha ke, abamithiyo nabanyisayo ngaloo mihla!', 'XHO22'),
(44, 24, 20, 'Thandazani ke, ukuba intsabo yenu ingabi sebusika nangesabatha.', 'XHO22'),
(44, 24, 21, 'Kuba ngelo xesha kuya kubakho imbandezelo enkulu, ekungazanga kubekho injalo kwasekuqalekeni kwehlabathi, kwada kwaba kalokunje, ekungasayi kubuye kubekho injalo.', 'XHO22'),
(44, 24, 22, 'Kanjalo ukuba ibingayi kuthi loo mihla inqanyulelwe, bekungayi kubakho nyama isindayo; ngenxa ke yabanyuliweyo, loo mihla iya kunqanyulelwa.', 'XHO22'),
(44, 24, 23, 'Ngelo xa ke, ukuba ubani uthe kuni, Nanku apha uKristu, nokuba uthe, Nankuya phaya; musani ukukholwa yiyo.', 'XHO22'),
(44, 24, 24, 'Kuya kuvela ooKristu ababuxoki, nabaprofeti ababuxoki, benze imiqondiso emikhulu nezimanga; ngokokude, ukuba bekunokwenzeka, balahlekise nabanyuliweyo.', 'XHO22'),
(44, 24, 25, 'Yabonani, ndinixelele ngenxa engaphambili.', 'XHO22'),
(44, 24, 26, 'Ukuba ngoko bathi kuni, Nanko entlango, musani ukuphuma niye. Musani ukuphuma; nanko emagumbini afihlakeleyo; musani ukukholwa yiyo.', 'XHO22'),
(44, 24, 27, 'Kuba kwanjengokuba umbane uthi uphume empumalanga, ubonakale kude kuse entshonalanga; koba njalo nokufika koNyana woMntu.', 'XHO22'),
(44, 24, 28, 'Kuba apho sukuba sikhona isidumbu, aya kuhlanganisana khona amaxhalanga.', 'XHO22'),
(44, 24, 29, 'Kwaoko ke, emveni kwembandezelo yaloo mihla, ilanga liya kwenziwa mnyama, ithi nenyanga ifiphale, zithi neenkwenkwezi ziwe ezulwini, athi namandla amazulu azanyazanyiswe.', 'XHO22'),
(44, 24, 30, 'Uya kubonakala ngelo xesha umqondiso woNyana woMntu ezulwini; zimbambazele ngelo xesha zonke izizwe zomhlaba, zimbone uNyana woMntu esiza esemafini ezulu, enamandla, nobuqaqawuli obukhulu.', 'XHO22'),
(44, 24, 31, 'Azithumele izithunywa zakhe zasemazulwini, zinexilongo elisandi sikhulu, zibahlanganisele ndawonye abanyulwa bakhe, baphume kumacala omane omoya, kuthabathela esiphelweni sezulu, kuse kwesinye isiphelo samazulu.', 'XHO22'),
(44, 24, 32, 'Fundani kuwo umkhiwane umzekeliso; xa selithe lathamba isebe lawo, lahluma amagqabi, niyazi ukuba likufuphi ihlobo.', 'XHO22'),
(44, 24, 33, 'Ngokukwanjalo nani, xa nithe nazibona zonke ezo zinto, yazini ukuba kukufuphi, sekusemnyango.', 'XHO22'),
(44, 24, 34, 'Inene ndithi kuni, Asisayi kudlula esi sizukulwana, zingadanga zonke ezo zinto zibekho.', 'XHO22'),
(44, 24, 35, 'Liya kudlula izulu nehlabathi, kodwa amazwi am akayi kudlula.', 'XHO22'),
(44, 24, 36, 'Kodwa akukho bani uwaziyo loo mhla nelo lixa, azazi nezithunywa zamazulu, nguBawo yedwa.', 'XHO22'),
(44, 24, 37, 'Ke kaloku, njengokuba ibinjalo imihla kaNowa, koba njalo nokufika koNyana woMntu.', 'XHO22'),
(44, 24, 38, 'Kuba njengokuba babesithi, ngemihla yangaphambi konogumbe, babesidla, besela, bazeke, besendiswe, kwada kwayimini awangena ngayo emkhombeni uNowa,', 'XHO22'),
(44, 24, 39, 'kwaye abazange baqonde wada wafika unogumbe, wabakhukulisa bonke; koba njalo nokufika koNyana woMntu.', 'XHO22'),
(44, 24, 40, 'Ngelo xesha ababini boba sentsimini; omnye amkelwe, omnye ashiywe.', 'XHO22'),
(44, 24, 41, 'Abafazi ababini boba besila etyeni lokusila; omnye amkelwe, omnye ashiywe.', 'XHO22'),
(44, 24, 42, 'Phaphani ngoko; ngokuba ningazi ukuba lilixa liphi na eza ngalo iNkosi yenu.', 'XHO22');
