INSERT INTO verses (book_id, chapter, verse, text, version) VALUES
(44, 24, 43, 'Yazini ke oku, ukuba ebesazi umninindlu ukuba isela liza ngawuphi na umlindo wobusuku, ange elindile, akavuma ukuba indlu yakhe igqojozwe.', 'XHO22'),
(44, 24, 44, 'Nani ngoko yibani nilungile, ngokuba uNyana woMntu uza ngelixa eningalikhumbuleliyo.', 'XHO22'),
(44, 24, 45, 'Ngubani na ke loo mkhonzi uthembekileyo, uqondayo, eyathi inkosi yakhe yammisa phezu kwabendlu yayo, ukuze abanike ukudla ngexesha elifanelekileyo?', 'XHO22'),
(44, 24, 46, 'Unoyolo loo mkhonzi, iya kuthi yakufika inkosi yakhe imfumane esenjenjalo.', 'XHO22'),
(44, 24, 47, 'Inene ndithi kuni, iya kummisa phezu kwayo yonke impahla yayo.', 'XHO22'),
(44, 24, 48, 'Ukuba ke loo mkhonzi ungendawo uthe entliziyweni yakhe, Inkosi yam iyalibala ukuza;', 'XHO22'),
(44, 24, 49, 'Aqale ukubabetha abakhonza kunye naye, adle, asele namanxila;', 'XHO22'),
(44, 24, 50, 'iya kufika inkosi yaloo mkhonzi ngemini engayilindeleyo, nangelixa angalaziyo;', 'XHO22'),
(44, 24, 51, 'imsike kubini, immisele isabelo sakhe nabahanahanisi; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 25, 1, 'Ngelo xesha ubukumkani bamazulu buya kufanekiswa neentombi ezilishumi, ezazithabathayo izibane zazo, zaphuma zaya kumkhawulela umyeni.', 'XHO22'),
(44, 25, 2, 'Ezintlanu ke kuzo zaye ziziingqondi, ezintlanu ziziziyatha.', 'XHO22'),
(44, 25, 3, 'Ezo ke ezizizidenge zazithabatha izibane zazo, azaphatha oli nazo.', 'XHO22'),
(44, 25, 4, 'Kodwa eziziingqondi zaphatha ioli ezityeni zazo, kwanezibane zazo.', 'XHO22'),
(44, 25, 5, 'Ke kaloku, akubon’ ukuba ulibele umyeni, zozela zonke zalala.', 'XHO22'),
(44, 25, 6, 'Ke kaloku ezinzulwini zobusuku kwamenyezwa, Nanko umyeni esiza; phumani niye kumkhawulela.', 'XHO22'),
(44, 25, 7, 'Zaza zavuka zonke ezo ntombi, zalungisa izibane zazo.', 'XHO22'),
(44, 25, 8, 'Zathi ke eziziziyatha kweziziingqondi, Sipheni kwioli yenu leyo; ngokuba izibane zethu zicima.', 'XHO22'),
(44, 25, 9, 'Zaphendula ke eziziingqondi, zathi, Hayi; hleze anganeli kuthi nakuni; yiyani kanye kwabathengisayo, nizithengele.', 'XHO22'),
(44, 25, 10, 'Kuthe ke, zisaya kuthenga, wafika umyeni; zaza ezo bezisezilungile zangena naye emsithweni, lwavalwa ke ucango.', 'XHO22'),
(44, 25, 11, 'Zithi ke kamva zifike nezinye iintombi ezo, zisithi, Nkosi, Nkosi, sivulele.', 'XHO22'),
(44, 25, 12, 'Waphendula ke yena wathi, Inene ndithi kuni, andinazi nina.', 'XHO22'),
(44, 25, 13, 'Phaphani ngoko, ngokuba ningayazi imini kwanalo ilixa.', 'XHO22'),
(44, 25, 14, 'Kaloku ubukumkani bamazulu bunjengomntu owathi, eza kuhambela kwelinye, wabiza abakhe abakhonzi, wayinikela kubo impahla yakhe.', 'XHO22'),
(44, 25, 15, 'Wathi omnye wamnika iitalente zantlanu, omnye zambini, nomnye yaba nye; elowo ngokwamandla akhe; wanduluka kwaoko.', 'XHO22'),
(44, 25, 16, 'Lowo ke wamkela iitalente ezintlanu, waya wasebenza ngazo, wenza esinye isihlanu seetalente.', 'XHO22'),
(44, 25, 17, 'Kwangokunjalo nalowo wamkela ezimbini, naye wazuza esinye isibini.', 'XHO22'),
(44, 25, 18, 'Ke naye lowo wamkela yanye, waya wemba emhlabeni, wayifihla imali yenkosi yakhe.', 'XHO22'),
(44, 25, 19, 'Emva kwexesha elide, ifike inkosi yabo bakhonzi, ibalelane nabo.', 'XHO22'),
(44, 25, 20, 'Weza lowo wamkela iitalente ezintlanu, wazisa esinye isihlanu seetalente phezu kwazo, esithi, Nkosi, wandinika iitalente zantlanu; khangela, ndizuze esinye isihlanu seetalente phezu kwazo.', 'XHO22'),
(44, 25, 21, 'Yathi ke kuye inkosi yakhe, Hee ke, mkhonzi ulungileyo, uthembekileyo! Ubuthembekile kwiinto ezimbalwa; ndiya kukumisa phezu kweento ezininzi; ngena eluvuyweni lwenkosi yakho.', 'XHO22'),
(44, 25, 22, 'Weza ke nalowo wamkela iitalente ezimbini, wathi, Nkosi, wandinika iitalente zambini; khangela, ndizuze esinye isibini seetalente phezu kwazo.', 'XHO22'),
(44, 25, 23, 'Yathi kuye inkosi yakhe, Hee ke, mkhonzi ulungileyo, uthembekileyo! Ubuthembekile kwiinto ezimbalwa; ndiya kukumisa phezu kweento ezininzi; ngena eluvuyweni lwenkosi yakho.', 'XHO22'),
(44, 25, 24, 'Weza ke nalowo wamkela italente yanye, wathi, Nkosi, bendikwazi wena ukuba ungumntu olukhuni, uvuna apho ubungahlwayelanga khona, ubuthe apho ungelelanga khona;', 'XHO22'),
(44, 25, 25, 'Ndasuka ndoyika, ndaya ndayiselela italente yakho ngomhlaba; nanku unako okwakho.', 'XHO22'),
(44, 25, 26, 'Yaphendula ke inkosi yakhe, yathi kuye, Sisicakandini singendawo, silivila, ubusazi nje ukuba ndivuna apho ndingahlwayelanga khona, ndiqokelele apho ndingahlwayelanga khona;', 'XHO22'),
(44, 25, 27, 'ubumelwe kukuthi ngoko ube ubuyibeke imali yam kubananisi, ndaza ndakufika mna, ndabuya ndakuzuza okwam kunenzala.', 'XHO22'),
(44, 25, 28, 'Mhlutheni ngoko italente leyo, niyinike lowo uneetalente ezilishumi.', 'XHO22'),
(44, 25, 29, 'Kuba wonke umntu onako Uya kunikwa, abe nako ngokugqithiseleyo; ke ongenako, uya kuhluthwa kwanoko anako.', 'XHO22'),
(44, 25, 30, 'Naye umkhonzi ongancedi lutho, mkhupheleni ebumnyameni obungaphandle; khona apho kuya kubakho ukulila nokutshixiza kwamazinyo.', 'XHO22'),
(44, 25, 31, 'Xa ke athe wafika uNyana woMntu esebuqaqawulini bakhe, enazo zonke izithunywa ezingcwele, ngelo xa uya kuhlala phezu kwetrone yobuqaqawuli bakhe;', 'XHO22'),
(44, 25, 32, 'zihlanganiswe phambi kwakhe zonke iintlanga, abahlule abanye kwabanye, njengokuba umalusi ezahlula izimvu ezibhokhweni;', 'XHO22'),
(44, 25, 33, 'athi zona izimvu azimise ngasekunene kwakhe, athi kodwa zona iibhokhwe azimise ngasekhohlo.', 'XHO22'),
(44, 25, 34, 'Uya kuthi ke ukumkani kwabangasekunene kwakhe, Yizani, nina nisikelelweyo nguBawo, nibudle ilifa ubukumkani obalungiselwa nina kwasekusekweni kwehlabathi.', 'XHO22'),
(44, 25, 35, 'Kuba ndalamba, nandipha ukudla; ndanxanwa, nandiseza; ndandingowasemzini, nandingenisa endlwini;', 'XHO22'),
(44, 25, 36, 'ndandize, nandambesa; ndandisifa, nandivelela; ndandisentolongweni, neza kum.', 'XHO22'),
(44, 25, 37, 'Oza amphendule ke amalungisa, esithi, Nkosi, sakubona nini na ulambile, sakonga; unxaniwe, saseza?', 'XHO22'),
(44, 25, 38, 'Sakubona nini na ungowasemzini, sakungenisa endlwini? Uze, sakwambesa?', 'XHO22'),
(44, 25, 39, 'Sakubona nini na ke usifa, okanye usentolongweni, seza kuwe?', 'XHO22'),
(44, 25, 40, 'Aze aphendule ukumkani athi kuwo, Inene ndithi kuni, Ekubeni nithe nenjenjalo nakomnye waba bazalwana bam bangabona bangabona bancinane, nenjenjalo nakum.', 'XHO22'),
(44, 25, 41, 'Uya kuthi nakwabangasekhohlo, Mkani kum, nina baqalekiswa, niye emlilweni ongunaphakade, owalungiselwa uMtyholi nezithunywa zakhe.', 'XHO22'),
(44, 25, 42, 'Kuba ndalamba, anandiseza; ndanxanwa, anandiseza;', 'XHO22'),
(44, 25, 43, 'ndandingowasemzini, anandingenisa ndlwini; ndandize, anandambesa; ndandisifa, ndandisentolongweni, anandivelela.', 'XHO22'),
(44, 25, 44, 'Boza ke bamphendule nabo, besithi, Nkosi, sakubona nini na ulambile, okanye unxaniwe, okanye ungowasemzini, okanye uze, okanye usifa, okanye usentolongweni, asakulungiselela?', 'XHO22'),
(44, 25, 45, 'Aze abaphendule athi, Inene ndithi kuni, Ekubeni ningenjanga njalo nakumnye waba bangabona bangabona bancinane, anenjanga njalo nakum.', 'XHO22'),
(44, 25, 46, 'Abo baya kumka aba baye elubethweni olungunaphakade; ke wona amalungisa aya kuya ebomini obungunaphakade.', 'XHO22'),
(44, 26, 1, 'Kwathi, xeshikweni uYesu ebewagqibile onke la mazwi, wathi kubafundi bakhe,', 'XHO22'),
(44, 26, 2, 'Niyazi ukuba emveni kweentsuku ezimbini kukho ipasika, aze uNyana woMntu anikelwe ukuba abethelelwe emnqamlezweni.', 'XHO22'),
(44, 26, 3, 'Bahlanganisana ngoko ababingeleli abakhulu, nababhali, namadoda amakhulu abantu, endlwini yombingeleli omkhulu okuthiwa nguKayafa.', 'XHO22'),
(44, 26, 4, 'Babhunga ukuba bambambe uYesu ngobuqhophololo, bambulale.', 'XHO22'),
(44, 26, 5, 'Babesithi ke, Kungabi ngomthendeleko, hleze kubekho isaqunge phakathi kwabantu.', 'XHO22'),
(44, 26, 6, 'Kwathi, uYesu eseBhetani, endlwini kaSimon owayeneqhenqa,', 'XHO22'),
(44, 26, 7, 'Kweza kuye umfazi enehlala lelabhastile, linamafutha aqholiweyo axabiso likhulu, wamgalela entloko, akubon’ ukuba uhleli ngasesithebeni.', 'XHO22'),
(44, 26, 8, 'Bekubonile ke oko abafundi bakhe, bacaphuka, besithi, Yeyani na le nkcitho?', 'XHO22'),
(44, 26, 9, 'Kuba la mafutha ngekuthengiswe ngawo ngexabiso elikhulu, kwaphiwa amahlwempu.', 'XHO22'),
(44, 26, 10, 'Ekwazi ke oko, uYesu wathi kubo, Umfazi lo nimaphulelani na? ngokuba undenzele umsebenzi olungileyo.', 'XHO22'),
(44, 26, 11, 'Kuba amahlwempu lawo nihleli ninawo; ke mna anihleli ninam.', 'XHO22'),
(44, 26, 12, 'Kuba xa egalele la mafutha aqholiweyo emzimbeni wam, wenzele ukungcwatywa kwam.', 'XHO22'),
(44, 26, 13, 'Inene ndithi kuni, Apho zithe zavakaliswa khona ezi ndaba zilungileyo kulo lonke ihlabathi, iya kuxelwa nayo le nto ayenzileyo yena, ibe sisikhumbuzo ngaye.', 'XHO22'),
(44, 26, 14, 'Ngelo xesha kwaphuma omnye weshumi elinababini, ekuthiwa nguYuda Sikariyoti, waya kubabingeleli abakhulu,', 'XHO22'),
(44, 26, 15, 'Wathi kubo, Ningandinika ntoni na, ndimnikele kuni? Banqophisana naye ngamaqhosha esilivere amashumi mathathu.', 'XHO22'),
(44, 26, 16, 'Waza kwalapho wafuna ithuba elimlungeleyo lokuze amnikele kubo.', 'XHO22'),
(44, 26, 17, 'Ke kaloku, ngowokuqala umhla wezonka ezingenagwele, beza abafundi kuYesu, besithi kuye, Ùfuna ukuba sikulungisele phi na ukuyidla ipasika?', 'XHO22'),
(44, 26, 18, 'Wathi ke yena, Hambani niye kuwo umzi, kuthile, nithi kuye, Uthi uMfundisi, ixesha lam likufuphi; ndiya kuyenzela kwakho ipasika nabafundi bam.', 'XHO22'),
(44, 26, 19, 'Benza ke abafundi njengoko uYesu wayebayalele ngako; bayilungisa ke ipasika.', 'XHO22'),
(44, 26, 20, 'Kwakuhlwa ke, wahlala ngasesithebeni nabalishumi elinababini.', 'XHO22'),
(44, 26, 21, 'Xa badlayo, wathi, Inene ndithi kuni, omnye kuni aba uya kundingcatsha.', 'XHO22'),
(44, 26, 22, 'Bakuba buhlungu kakhulu, baqala ukuthi kuye bonke ngabanye, Ingaba ndim, yini na, Nkosi?', 'XHO22'),
(44, 26, 23, 'Waphendula ke yena wathi, Osithe nkxu nam isandla esityeni, nguye lowo uya kundingcatsha.', 'XHO22'),
(44, 26, 24, 'uNyana woMntu uyemka okunene, njengoko kubhalwe ngaye; yeha ke, loo mntu uNyana woMntu angcatshwa nguye! Ngekumlungele ukuba ebengazalwanga loo mntu.', 'XHO22'),
(44, 26, 25, 'Waphendula ke uYuda, lowo umngcatshayo, wathi, Ingaba ndim, yini na, Rabhi? Wathi ke yena kuye, Uselutshilo nawe.', 'XHO22'),
(44, 26, 26, 'Bakubon’ ukuba bayadla, uYesu wasithabatha isonka, wathi akusikelela, waqhekeza, wanika abafundi, wathi, Thabathani nidle; ngumzimba wam lo.', 'XHO22'),
(44, 26, 27, 'Wayithabatha nendebe, wabulela, wabanika, esithi, Selani kuyo nonke;', 'XHO22'),
(44, 26, 28, 'kuba ligazi lam eli lomnqophiso omtsha, eliphalala ngenxa yabaninzi, ukuze kuxolelwe izono.', 'XHO22'),
(44, 26, 29, 'Ndithi ke kuni, Andisayi kusela kwesi siqhamo somdiliya, kuthabathela kweli xesha, kude kube yiloo mini sukuba ndisisela nani sisitsha ebukumkanini bukaBawo.', 'XHO22'),
(44, 26, 30, 'Bakuba bevume ingoma yokubonga, baphuma baya eNtabeni yemiNquma.', 'XHO22'),
(44, 26, 31, 'Ngelo xesha uthi uYesu kubo, Nina nonke niya kukhubeka ngam ngobu busuku; ngokuba kubhaliwe kwathiwa, Ndiya kumbetha umalusi, zichithwachithwe izimvu zomhlambi.', 'XHO22'),
(44, 26, 32, 'Kodwa emveni kokuvuka kwam, ndiya kunandulela ndiye kwelaseGalili.', 'XHO22'),
(44, 26, 33, 'Waphendula ke uPetros wathi kuye, Nokuba bonke baya kukhubeka ngawe, andisayi kukha ndikhubeke mna.', 'XHO22'),
(44, 26, 34, 'Athi uYesu kuye, Inene ndithi kuwe, Kwangobu busuku, ingekalili inkuku, uya kundikhanyela kathathu.', 'XHO22'),
(44, 26, 35, 'Athi uPetros kuye, Ndingafanelana ndimelwe kukuba ndife nawe, andingekhe ndikukhanyele. Batsho ke bonke abafundi.', 'XHO22'),
(44, 26, 36, 'Ngelo xesha, ufika uYesu nabo kumhlatyana ekuthiwa yiGetsemane, athi kubafundi, Hlalani phantsi apha, ndikhe ndiye kuthandaza phaya.', 'XHO22'),
(44, 26, 37, 'Wathabatha uPetros noonyana bobabini bakaZebhedi, waqala ukuba buhlungu nokudandatheka kakhulu.', 'XHO22'),
(44, 26, 38, 'Athi kubo, Umphefumlo wam ubuhlungu kakhulu, ngokokude ube sekufeni; hlalani apha, nilinde nam.', 'XHO22'),
(44, 26, 39, 'Waza, ehambele phambili umganyana, wawa ngobuso bakhe, wathandaza, esithi, Bawo, ukuba kunokwenzeka, mayidlule kum le ndebe; noko makungabi njengokuthanda kwam, makube njengokuthanda kwakho.', 'XHO22'),
(44, 26, 40, 'Uyeza kubafundi, abafumane belele, athi kuPetros, Ninje na ukungabi nako ukulinda nam nexa elinye?', 'XHO22'),
(44, 26, 41, 'Phaphani nithandaze, ukuze ningangeni ekuhendweni; umoya wona okunene unentumekelelo, ke yona inyama ithambile.', 'XHO22'),
(44, 26, 42, 'Wabuya wemka okwesibini, wathandaza, esithi, Bawo, ukuba ayinako le ndebe ukudlula kum ndingathanga ndiyisele, makwenzeke ukuthanda kwakho.', 'XHO22'),
(44, 26, 43, 'Afike abafumane bebuye balala, kuba amehlo abo ayenzima bubuthongo.', 'XHO22'),
(44, 26, 44, 'Wabashiya, wabuya wemka, wathandaza okwesithathu, ethetha kwalona elo lizwi.', 'XHO22'),
(44, 26, 45, 'Andule ukuza kubafundi bakhe, athi kubo, Lalani ngoku, niphumle; yabonani, lisondele ilixa lokuba uNyana woMntu anikelwe ezandleni zaboni.', 'XHO22'),
(44, 26, 46, 'Vukani, sihambe; yabonani, usesondele lowo undingcatshayo.', 'XHO22'),
(44, 26, 47, 'Kuthe, esathetha, kwathi gqi uYuda, omnye wabalishumi elinababini; weza enendimbane enkulu inamakrele neentonga, ivela kubabingeleli abakhulu namadoda amakhulu abantu.', 'XHO22'),
(44, 26, 48, 'Ke kaloku lowo umngcatshayo wayebanike umqondiso, esithi, Lowo ndithe ndamanga, nguye;', 'XHO22'),
(44, 26, 49, 'Wafika ke kwaoko kuYesu, wathi, Vuya, Rabhi; wamanga.', 'XHO22'),
(44, 26, 50, 'Athi uYesu kuye, Wethu, yini na ukuba uze? Beza ngoko, bamsa izandla uYesu, bambamba.', 'XHO22'),
(44, 26, 51, 'Waza omnye wabo babenoYesu wolula isandla, warhola ikrele lakhe, wamxabela umkhonzi wombingeleli omkhulu, wamnqamla indlebe.', 'XHO22'),
(44, 26, 52, 'Waza wathi uYesu kuye, Libuyisele endaweni yalo ikrele lakho; kuba bonke abathabatha ikrele baya kutshabalala likrele.', 'XHO22'),
(44, 26, 53, 'Uba andinako na ngoku ukumbongoza uBawo, andikhuphele imikhosi yezithunywa zezulu engaphezu kweshumi elinamibini?', 'XHO22'),
(44, 26, 54, 'Bezingathini na ke oko ukuzaliseka izibhalo ezithi, kumele ukuba kube nje?', 'XHO22'),
(44, 26, 55, 'Ngalo elo lixa wathi uYesu kwindimbane, Niphumele ukuza kubamba mna na, ninamakrele neentonga nje, ngathi niphumele isihange? Bendihleli nani imihla ngemihla, ndifundisa etempileni, anandibamba.', 'XHO22'),
(44, 26, 56, 'Kodwa konke oku kwenzeke, ukuze zizaliseke izibhalo zabaprofeti. Basuka bonke abafundi bamshiya, babaleka.', 'XHO22'),
(44, 26, 57, 'Abo ke bambambayo uYesu, bamrholela kuKayafa, umbingeleli omkhulu, apho babehlanganisene khona ababhali namadoda amakhulu.', 'XHO22'),
(44, 26, 58, 'Ke yena uPetros wamlandela, ebekele kude, kwada kwaya entendelezweni yendlu yombingeleli omkhulu. Engene ngaphakathi, wahlala phantsi namadindala, ukuba abone isiphelo.', 'XHO22'),
(44, 26, 59, 'Ke kaloku, baye ababingeleli abakhulu, namadoda amakhulu, nentlanganiso yamatyala iphelele, befuna ubungqina obubuxoki ngoYesu, ukuze bambulale;', 'XHO22'),
(44, 26, 60, 'nangona afikayo amangqina abuxoki aba maninzi, ababufumananga. Ada ke afika amangqina abuxoki emabini.', 'XHO22'),
(44, 26, 61, 'Athi, Lo wathi, Ndinako ukuyichitha itempile kaThixo, ndibuye ndiyakhe ngeentsuku ezintathu.', 'XHO22'),
(44, 26, 62, 'Wesuka wema umbingeleli omkhulu, wathi kuye, Akuphenduli nto na? bangqina ntoni na aba ngawe?', 'XHO22'),
(44, 26, 63, 'Wathi tu uYesu. Waphendula umbingeleli omkhulu wathi kuye, Ndikufungisa uThixo ophilileyo, ukuba usixelele ukuba unguye na uKristu, uNyana kaThixo.', 'XHO22'),
(44, 26, 64, 'Athi uYesu kuye, Uselutshilo nawe. Ke ndithi kuni, Emveni koku niya kumbona uNyana woMntu, ehleli ngasekunene kwamandla, esiza esemafini ezulu.', 'XHO22'),
(44, 26, 65, 'Waza umbingeleli omkhulu wazikrazula iingubo zakhe, esithi, Unyelisile; sisawafunelani na amangqina? yabonani, ngoku nikuvile ukunyelisa kwakhe.', 'XHO22'),
(44, 26, 66, 'Nithini na? Baphendula ke bona bathi, Unetyala lokufa.', 'XHO22'),
(44, 26, 67, 'Baza bamtshicela ebusweni, bamntlitha, abanye ke bambetha ngentende zezandla.', 'XHO22'),
(44, 26, 68, 'Zithi, Siprofetele, wena Kristu, ukubethileyo ngubani na?', 'XHO22'),
(44, 26, 69, 'Ke kaloku uPetros wayehleli ngaphandle entendelezweni; kweza kuye sicakazana sithile, sisithi, Nawe ubunaye uYesu lo waseGalili.', 'XHO22'),
(44, 26, 70, 'Wakhanyela ke yena phambi kwabo bonke, esithi, Andiyazi into oyithethayo.', 'XHO22'),
(44, 26, 71, 'Ephumile waya esangweni, sambona esinye, sathi kwababekhona, Naye lo ebenaye uYesu waseNazarete.', 'XHO22'),
(44, 26, 72, 'Waphinda wakhanyela efunga, esithi, Andimazi loo mntu.', 'XHO22'),
(44, 26, 73, 'Kwakuba mzuzwana, beza abo babemi khona, bathi kuPetros, Inyaniso, nawe ungowabo; kuba intetho yakho iyakudiza.', 'XHO22'),
(44, 26, 74, 'Waza waqala ukushwabula nokufunga, esithi, Andimazi loo mntu. Yalila kwaoko inkuku.', 'XHO22'),
(44, 26, 75, 'Walikhumbula uPetros ilizwi likaYesu, abelithethile kuye, esithi, Ingekalili inkuku, uya kundikhanyela kathathu. Waza waphuma phandle, walila ngokukrakra.', 'XHO22'),
(44, 27, 1, 'Kwathi kwakusa, babhunga bonke ababingeleli abakhulu namadoda amakhulu abantu ngaye uYesu, ukuba bamsike.', 'XHO22'),
(44, 27, 2, 'Bathe ke bakumbopha, bamrhola, bamnikela kuPontiyo Pilato, irhuluneli.', 'XHO22'),
(44, 27, 3, 'Uthe ke uYuda, lowo umngcatshayo, akubona ukuba ugwetyiwe, wazohlwaya, wawabuyisela loo maqhosha esilivere amashumi mathathu kubabingeleli abakhulu namadoda amakhulu,', 'XHO22'),
(44, 27, 4, 'esithi, Ndonile, kuba ndingcatshe igazi elingenatyala. Bathi ke bona, Iyini na kuthi loo nto? zibonele wena.', 'XHO22'),
(44, 27, 5, 'Wawaphosa phantsi etempileni apho loo maqhosha esilivere, wemka, waya wazixhoma.', 'XHO22'),
(44, 27, 6, 'Bawathabatha ke ababingeleli abakhulu loo maqhosha esilivere, bathi, Akuvumelekile ukuba awaphose emkhumbini wokulondoloza imali, kuba elixabiso legazi.', 'XHO22'),
(44, 27, 7, 'Babhunga ke, bathenga ngawo umhlaba wombumbi, ukuze ube yindawo yokungcwaba abasemzini.', 'XHO22'),
(44, 27, 8, 'Kungoko wabizwayo loo mhlaba ngokuba ngumhlaba wegazi, kwada kwanamhlanje.', 'XHO22'),
(44, 27, 9, 'Kwazaliseka ke okwathethwayo ngomprofeti uYeremiya, esithi, Bawathabatha amaqhosha esilivere amashumi mathathu, ixabiso lalowo uqiqiweyo, lowo waqingqwelwa ngoonyana bakaSirayeli;', 'XHO22'),
(44, 27, 10, 'bawanikela ngenxa yentsimi yombumbi, njengoko yandiyalelayo iNkosi.', 'XHO22'),
(44, 27, 11, 'Wema ke uYesu phambi kwerhuluneli, yambuza irhuluneli isithi, Unguye na uKumkani wamaYuda? Wathi ke uYesu kuye, Nawe utsho.', 'XHO22'),
(44, 27, 12, 'Uthe ke akumangalelwa ngababingeleli abakhulu namadoda amakhulu, akaphendula nto.', 'XHO22'),
(44, 27, 13, 'Aze athi uPilato kuye, Akuziva na ukuba zininzi kwazo iindawo abazingqinayo ngawe?', 'XHO22'),
(44, 27, 14, 'Akazange amphendule nelizwi elinye; yada yamangaliswa kakhulu irhuluneli leyo.', 'XHO22'),
(44, 27, 15, 'Ke kaloku ngemithendeleko irhuluneli ibiqhele ukubakhululela abantu umbanjwa abe mnye, abasukuba bemfuna.', 'XHO22'),
(44, 27, 16, 'Baye ke ngelo xesha benombanjwa obesazeka, ekuthiwa nguBharabhas.', 'XHO22'),
(44, 27, 17, 'Bakubon’ ukuba ngoko bahlanganisene, wathi kubo uPilato, Nithanda ukuba ndinikhululele wuphi na? uBharabhas, uYesu ekuthiwa nguKristu, kusini na?', 'XHO22'),
(44, 27, 18, 'Kuba ebesazi ukuba bamnikele ngomona.', 'XHO22'),
(44, 27, 19, 'Akuba ehleli esihlalweni sokugweba, umfazi wakhe wathumela kuye, esithi, Uze ungabi nakuthini kuloo mntu ulilungisa, kuba namhlanje ndive izinto ezininzi ezinzima ephupheni, ngenxa yakhe.', 'XHO22'),
(44, 27, 20, 'Ke bona ababingeleli abakhulu namadoda amakhulu bazeyisela izihlwele ukuba zicele uBharabhas, zimbulale uYesu.', 'XHO22'),
(44, 27, 21, 'Yaphendula ke irhuluneli yathi kubo, Nguwuphi na kwaba bobabini enithanda ukuba ndinikhululele yena? Bathi ke bona, uBharabhas.', 'XHO22'),
(44, 27, 22, 'Athi uPilato kubo, Ndimenzeni na ke uYesu ekuthiwa nguKristu? Bathi kuye bonke, Makabethelelwe emnqamlezweni.', 'XHO22'),
(44, 27, 23, 'Yathi ke irhuluneli, Kuba enze bubi buni na? Kwaba kukhona ke bakhalayo ngakumbi, besithi, Makabethelelwe emnqamlezweni.', 'XHO22'),
(44, 27, 24, 'Uthe ke uPilato, akubona ukuba akancedi lutho, ebona ukuba sisuke sangakumbi isiphithiphithi, wathabatha amanzi, wahlamba izandla phambi kwezihlwele, esithi, Andinatyala egazini lalo mntu ulilungisa; ziboneleni nina.', 'XHO22'),
(44, 27, 25, 'Baphendula ke bonke abantu, bathi, Igazi lakhe malibe phezu kwethu, naphezu kwabantwana bethu.', 'XHO22'),
(44, 27, 26, 'Waza wabakhululela uBharabhas; wathi ke, emtyakatyile uYesu, wamnikela ukuba abethelelwe emnqamlezweni.', 'XHO22'),
(44, 27, 27, 'Aza amasoldati erhuluneli amthabatha uYesu, amsa endlwini yerhuluneli, alihlanganisela kuye ibutho lawo liphela.', 'XHO22'),
(44, 27, 28, 'Amhluba, amnxiba ingubo ebomvu.', 'XHO22'),
(44, 27, 29, 'Baluka isithsaba sameva, bambeka ngaso entloko, bafaka ingcongolo esandleni sakhe sokunene, baguqa ngamadolo phambi kwakhe, bahlekisa ngaye, besithi: “Awu, Kumkani wamaJuda!', 'XHO22'),
(44, 27, 30, 'Amtshicela, ayithabatha ingcongolo, ambetha entloko.', 'XHO22'),
(44, 27, 31, 'Athi, akuba edlale ngaye, amhluba ingubo leyo, amambathisa kwaezakhe iingubo; amrhola, aya kumbethelela emnqamlezweni.', 'XHO22'),
(44, 27, 32, 'Athe ke ekuphumeni, afumana umntu waseKirene, ogama linguSimon; anyanzela lowo ukuba awuthwale umnqamlezo wakhe.', 'XHO22'),
(44, 27, 33, 'Bakufika kwindawo ekuthiwa yiGologota, oko kukuthi, yindawo yokakayi.', 'XHO22'),
(44, 27, 34, 'Amnika iviniga iphithikezwe nenyongo, ukuba ayisele; wathi akuba eyivile, akavuma ukuyisela.', 'XHO22'),
(44, 27, 35, 'Athi ke, akumbethelela emnqamlezweni, abelana ngeengubo zakhe, esenza amaqashiso, ukuze kuzaliseke okwathethwayo ngomprofeti, esithi, Babelana ngeengubo zam, benza amaqashiso ngesambatho sam.', 'XHO22'),
(44, 27, 36, 'Ahlala phantsi, amlinda khona apho.', 'XHO22'),
(44, 27, 37, 'Babeka ngaphezu kwentloko yakhe ityala lakhe libhaliwe, kwathiwa, lo nguYesu uKumkani wamaYuda.', 'XHO22'),
(44, 27, 38, 'Kwaza kwabethelelwa naye eminqamlezweni izihange ezibini, esinye ngasekunene, nesinye ngasekhohlo.', 'XHO22'),
(44, 27, 39, 'Bamtshabhisa ke abadlulayo, behlunguzela iintloko zabo,', 'XHO22'),
(44, 27, 40, 'besitsho ukuthi, Wena uchitha itempile, uyakhe ngeentsuku ezintathu, zisindise; Ukuba unguNyana kaThixo, yihla emnqamlezweni.', 'XHO22'),
(44, 27, 41, 'Ngokukwanjalo nababingeleli abakhulu bedlala ngaye, benababhali namadoda amakhulu, besithi,', 'XHO22'),
(44, 27, 42, 'Wabasindisa abanye; yena ngokwakhe akanako ukuzisindisa. Ukuba unguKumkani wakwaSirayeli, makehle kalokunje emnqamlezweni, size simkholwe.', 'XHO22'),
(44, 27, 43, 'Wakholosa ngoThixo; makamhlangule ngoku, ukuba uyamthanda; kuba wathi, NdinguNyana kaThixo.', 'XHO22'),
(44, 27, 44, 'Zaye ke nezihange, ezibe zibethelelwe eminqamlezweni naye, zimngcikiva kwangokunjalo.', 'XHO22'),
(44, 27, 45, 'Kuthe ke, kuthabathele kwilixa lesithandathu, kwada kwesa kwilixa lesithoba, kwehla ubumnyama phezu kwawo wonke umhlaba.', 'XHO22'),
(44, 27, 46, 'Ke kaloku, malunga nelixa lesithoba, wadanduluka uYesu ngezwi elikhulu, esithi, Eli! Eli! Lama sabhaktani! Oko kukuthi, Thixo wam, Thixo wam, undishiye ngani na?', 'XHO22'),
(44, 27, 47, 'Inxenye ke yababemi khona, yakuva, yathi, Lo mntu ubiza uEliya.', 'XHO22'),
(44, 27, 48, 'Wesuka wagidima kwaoko omnye kubo, wathabatha imfunxa, wayizalisa ngeviniga, wayifaka engcongolweni, wamseza.', 'XHO22'),
(44, 27, 49, 'Bathe ke bona abanye, Khawuyeke, sibone ukuba uEliya uza kumsindisa na.', 'XHO22'),
(44, 27, 50, 'uYesu, ephinde wadanduluka ngelizwi elikhulu, wawukhupha umoya.', 'XHO22'),
(44, 27, 51, 'Lasuka ikhuselo lengcwele lakrazuka kubini, lithabathela phezulu lada lesa phantsi; Wanyikima umhlaba, namawa acandeka;', 'XHO22'),
(44, 27, 52, 'Amangcwaba avuleka; yaza yavuka imizimba emininzi yabangcwele ababelele;', 'XHO22'),
(44, 27, 53, 'baphuma emangcwabeni emveni kokuvuka kwakhe, bangena kuwo umzi ongcwele, babonakala kuninzi.', 'XHO22'),
(44, 27, 54, 'Ke kaloku umthetheli-khulu, nabo babelinda uYesu naye, bakubona ukunyikima oko, nezo zinto zenzekileyo, boyika kakhulu, besithi, Inyaniso, lo ebenguNyana kaThixo.', 'XHO22'),
(44, 27, 55, 'Kwaye kukho ke khona abafazi abaninzi, bekhangele bemgama, bona babelandele uYesu kwakwelaseGalili, bemlungiselela;', 'XHO22'),
(44, 27, 56, 'ekwakukho phakathi kwabo uMariya waseMagadala, noMariya unina wooYakobi noYose, kwanonina woonyana bakaZebhedi.', 'XHO22'),
(44, 27, 57, 'Kwathi kwakuhlwa, kweza umntu osisityebi waseArimati, ogama linguYosefu, owayengumfundi kaYesu naye.', 'XHO22'),
(44, 27, 58, 'Yena lowo waya kuPilato, wawucela umzimba kaYesu. Waza uPilato wathi makawunikwe umzimba.', 'XHO22'),
(44, 27, 59, 'Ewuthabathile ke umzimba uYosefu, wawusongela kwilinen entle emhlophe.', 'XHO22'),
(44, 27, 60, 'Wawubeka kwelakhe ingcwaba elitsha, abelixhole eweni; waqengqela ilitye elikhulu emnyango wengcwaba elo, wemka.', 'XHO22'),
(44, 27, 61, 'Baye ke bekhona apho uMariya waseMagadala nomnye uMariya, behleli phantsi malunga nengcwaba.', 'XHO22'),
(44, 27, 62, 'Ke kaloku ngengomso, eliyimini yokulungiselela, bahlanganisana ababingeleli abakhulu nabaFarisi kuPilato;', 'XHO22'),
(44, 27, 63, 'besithi, Nkosi, sikhumbule ukuba umlahlekisi lowa wathi esahleli, Emveni kweentsuku ezintathu, ndiya kuvuka.', 'XHO22'),
(44, 27, 64, 'Yitsho ngoko linqatyiswe ingcwaba, kude kube lusuku lwesithathu, hleze abafundi bakhe beze ebusuku, bambe, bathi ke ebantwini, Uvukile kwabafileyo; luze olokugqibela ulahlekiso lube lubi kunolokuqala.', 'XHO22'),
(44, 27, 65, 'Wathi ke uPilato kubo, Ninabo abalindi;', 'XHO22'),
(44, 27, 66, 'Baya ke bona balinqabisa ingcwaba, ngokulitywina ilitye, bekunye nabalindi.', 'XHO22'),
(44, 28, 1, 'Ke kaloku, ekupheleni kwesabatha, ekuseni ngolokuqala usuku lweveki, kweza uMariya waseMagadala nomnye uMariya, beza kulikhangela ingcwaba.', 'XHO22'),
(44, 28, 2, 'Kwasuka kwabakho unyikimo olukhulu lomhlaba, kuba isithunywa seNkosi sehla ezulwini, seza saliqengqa ilitye lesuka emnyango, sahlala phezu kwalo.', 'XHO22'),
(44, 28, 3, 'Imbonakalo yayo ibinjengombane, nesambatho sayo simhlophe njengekhephu.', 'XHO22'),
(44, 28, 4, 'Ngokoyika sona ke bangcangcazela abalindi, baba njengabafileyo.', 'XHO22'),
(44, 28, 5, 'Saphendula ke isithunywa, sathi kubafazi abo, Musani ukoyika nina; kuba ndiyazi ukuba nifuna uYesu, lowo ubebethelelwe emnqamlezweni.', 'XHO22'),
(44, 28, 6, 'Akakho apha; kuba uvukile, njengoko watshoyo; yizani niyibone indawo apho ibilele khona iNkosi;', 'XHO22'),
(44, 28, 7, 'Yiyani ngokukhawuleza, nibaxelele abafundi bakhe ukuthi uvukile kwabafileyo; niyabona, uyanandulela ukuya kwelaseGalili; nombona khona apho; yabonani, ndinixelele.', 'XHO22'),
(44, 28, 8, 'Bephume kamsinya engcwabeni, benoloyiko novuyo olukhulu; wagidima waya kubabikela abafundi bakhe.', 'XHO22'),
(44, 28, 9, 'Ke kaloku, xa baya kubabikela abafundi bakhe, kwathi gqi uYesu wabahlangabeza, esithi, Vuyani! Beza ke bambamba ngeenyawo, baqubuda kuye.', 'XHO22'),
(44, 28, 10, 'Athi ngoko uYesu kubo, Musani ukoyika; yiyani kubatyela abazalwana bam ukuba baye eGalili; bondibona khona apho.', 'XHO22'),
(44, 28, 11, 'Bakubon’ ukuba bayaya ke, kwesuka kwafika abathile babalindi kuwo umzi, bababikela ababingeleli abakhulu zonke izinto ezenzekileyo.', 'XHO22'),
(44, 28, 12, 'Bakubon’ ukuba ke ngoko, bakubon’ ukuba bahlanganisene ndawonye namadoda amakhulu, babhunga, bawanika imali eninzi amasoldati lawo,', 'XHO22'),
(44, 28, 13, 'besithi, Yithini, Beza ebusuku abafundi bakhe, bamba silele.', 'XHO22'),
(44, 28, 14, 'Nokuba ithe yavakala le nto kwirhuluneli, thina soyithomalalisa, senze ningabi naxhala.', 'XHO22'),
(44, 28, 15, 'Ayithabatha ke imali leyo, enza njengoko abexelelwe ngako. Laza elo zwi laduma kumaYuda, kwada kwanamhlanje.', 'XHO22'),
(44, 28, 16, 'Bahamba ke abafundi abalishumi elinamnye, baya eGalili, kuloo ntaba wayebayalele yona uYesu.', 'XHO22'),
(44, 28, 17, 'Baza bakumbona baqubuda kuye, kodwa inxenye yathandabuza.', 'XHO22'),
(44, 28, 18, 'Weza uYesu wathetha kubo, esithi, Linikwe mna lonke igunya emazulwini nasehlabathini.', 'XHO22'),
(44, 28, 19, 'Hambani ngoko niye kuzenza abafundi zonke iintlanga, nibabhaptizela egameni loYise, loNyana, loMoya oyiNgcwele;', 'XHO22'),
(44, 28, 20, 'nibafundisa ukuba bazigcine zonke izinto endiniwisele umthetho ngazo. Niyabona, mna ndinani yonke imihla, kude kube sekuphelisweni kwephakade eli. Amen.', 'XHO22'),
(45, 1, 1, 'Ukuqaleka kwayo ivangeli kaYesu Kristu, uNyana kaThixo.', 'XHO22'),
(45, 1, 2, 'Njengoko kubhaliweyo ebaprofetini, kwathiwa, Yabona, mna ndithuma umthunywa wam ngaphambi kobuso bakho, Oya kuyilungisa indlela yakho phambi kwakho.', 'XHO22'),
(45, 1, 3, 'Ilizwi lodandulukayo entlango, lisithi, Wulungiseni umendo weNkosi, Wutyenenezeni umendo wayo.', 'XHO22'),
(45, 1, 4, 'Kwaye kukho uYohane, ebhaptiza entlango, evakalisa ubhaptizo lwenguquko, ukuze kuxolelwe izono.', 'XHO22'),
(45, 1, 5, 'Laye liphuma lisiya kuye lonke ilizwe lakwaYuda nabaseYerusalem, baye bonke bebhaptizwa nguye eYordan emlanjeni, bezixela izono zabo.', 'XHO22'),
(45, 1, 6, 'Waye ke uYohane ambethe uboya benkamela, enombhinqo wentlonze esinqeni sakhe; Wadla iinkumbi nobusi basendle;', 'XHO22'),
(45, 1, 7, 'Wayeshumayela esithi, Emva kwam kuza lowo unamandla kunam, endingafanelekileyo ukuba ndithobe phantsi, ndiwuthukulule nomtya weembadada zakhe.', 'XHO22'),
(45, 1, 8, 'Mna okunene ndanibhaptiza ngamanzi, kodwa yena uya kunibhaptiza ngoMoya oyiNgcwele.', 'XHO22'),
(45, 1, 9, 'Kwathi ngaloo mihla kweza uYesu, evela eNazarete yelaseGalili, wabhaptizwa nguYohane eYordan apho.', 'XHO22'),
(45, 1, 10, 'Uthe kwaoko, akuba enyuka ephuma emanzini, walibona izulu livulekile, noMoya enjengehobe, esihla phezu kwakhe.', 'XHO22'),
(45, 1, 11, 'Kwabakho nezwi eliphuma ezulwini, lisithi, Wena unguNyana wam oyintanda, endikholisiweyo nguye.', 'XHO22'),
(45, 1, 12, 'Uthe kwaoko uMoya wamkhuphela entlango.', 'XHO22'),
(45, 1, 13, 'Waye esentlango apho iintsuku ezimashumi mane, ehendwa nguSathana; waye kunye namarhamncwa; zaye izithunywa zezulu zimlungiselela.', 'XHO22'),
(45, 1, 14, 'Ke kaloku emveni kokufakwa kukaYohane entolongweni, weza uYesu kwelaseGalili, eshumayela iindaba ezilungileyo zobukumkani bukaThixo,', 'XHO22'),
(45, 1, 15, 'esithi, Lizalisekile ixesha, nobukumkani bukaThixo busondele; guqukani, nikholwe kuzo iindaba ezilungileyo ezi.', 'XHO22'),
(45, 1, 16, 'Uthe ke, ehamba ngaselwandle lwaseGalili, wabona uSimon, noAndreya umzalwana wakhe, bephosa umnatha elwandle; ngokuba babengababambisi beentlanzi.', 'XHO22'),
(45, 1, 17, 'Wathi kubo uYesu, Yizani apha nindilandele; ndonenza nibe ngababambisi babantu.', 'XHO22'),
(45, 1, 18, 'Bayishiya kwaoko iminatha yabo, bamlandela.', 'XHO22'),
(45, 1, 19, 'Ehambele phambili kancinane, wabona uYakobi, lo kaZebhedi, noYohane umzalwana wakhe, besemkhombeni wabo, belungisa iminatha yabo.', 'XHO22'),
(45, 1, 20, 'Wababiza kwaoko, bamshiya uyise uZebhedi enqanaweni nabaqeshwa, bamlandela.', 'XHO22'),
(45, 1, 21, 'Bangena eKapernahum; wathi kwaoko ngomhla wesabatha, wangena endlwini yesikhungu, wafundisa.', 'XHO22'),
(45, 1, 22, 'Bakhwankqiswa kukufundisa kwakhe; ngokuba ebebafundisa njengonegunya, engafundisi njengabo ababhali.', 'XHO22'),
(45, 1, 23, 'Kwaye kukho endlwini yabo yesikhungu umntu onomoya ongcolileyo; wadanduluka wathi,', 'XHO22'),
(45, 1, 24, 'besithi, Siyeke; Yintoni na enathi nawe, Yesu waseNazarete? uze kusitshabalalisa na? Ndiyakwazi wena, lowo unguye, ungoyiNgcwele kaThixo.', 'XHO22'),
(45, 1, 25, 'Wawukhalimela uYesu, esithi, Yithi tu, uphume kuye.', 'XHO22'),
(45, 1, 26, 'Uthe umoya ongcolileyo wambetha wee xhwenene; wakhala ngezwi elikhulu, waphuma kuye.', 'XHO22'),
(45, 1, 27, 'Bakhwankqiswa ke bonke, ngokokude babuzisane, besithi, Yintoni na le nto? Yeyiphi imfundiso entsha? kuba oomoya abangcolileyo aba ubathethela ngegunya, bamthobele.', 'XHO22'),
(45, 1, 28, 'Lwaphuma ke kwaoko udumo lwakhe; lwaya kuwo wonke ummandla welaseGalili.', 'XHO22'),
(45, 1, 29, 'Bakuba bephumile endlwini yesikhungo, bangena endlwini kaSimon noAndreya, benoYakobi noYohane.', 'XHO22'),
(45, 1, 30, 'Ke umkhwekazi kaSimon wayelele phantsi ebanjwe yicesina, baza bamxelela kwaoko.', 'XHO22'),
(45, 1, 31, 'Wasondela kuye, wayibamba ngesandla, wayiphakamisa; yamyeka kwaoko icesina, wabalungiselela.', 'XHO22'),
(45, 1, 32, 'Ke kaloku, kwakuhlwa, xa litshonayo ilanga, bazisa kuye bonke ababesifa, nababephethwe ziidemon.', 'XHO22'),
(45, 1, 33, 'Waye umzi wonke uhlanganisene ndawonye emnyango apho.', 'XHO22'),
(45, 1, 34, 'Waphilisa babaninzi ababesifa zizifo ngezifo; wakhupha needemon ezininzi; waye engazivumeli iidemon ukuba zithethe, kuba bezimazi.', 'XHO22'),
(45, 1, 35, 'Uthe ke kusasa, kusemnyama, wavuka, waphuma, wemka waya endaweni eyintlango, wathandaza khona apho.', 'XHO22'),
(45, 1, 36, 'Bamlandela ooSimon nabo babenaye.', 'XHO22'),
(45, 1, 37, 'Bathe bakumfumana bathi kuye, Bonke bayakufuna.', 'XHO22'),
(45, 1, 38, 'Wathi kubo, Masihambe siye emizini ekufuphi, ukuze nakhona ndivakalise iindaba ezilungileyo, kuba ndiphumele oko.', 'XHO22'),
(45, 1, 39, 'Wayevakalisa ke ezindlwini zabo zesikhungu kulo lonke elaseGalili, ezikhupha needemon.', 'XHO22'),
(45, 1, 40, 'Kweza kuye oneqhenqa, embongoza, esiwa ngamadolo phambi kwakhe, esithi kuye, Ukuba uyathanda, unako ukundihlambulula.', 'XHO22'),
(45, 1, 41, 'uYesu, esikwe yimfesane ke, wasolula isandla, wamchukumisa, wathi kuye, Ndiyathanda; Hlambuluka.', 'XHO22'),
(45, 1, 42, 'Akutsho, lemka kwaoko kuye iqhenqa, wahlambuluka.', 'XHO22'),
(45, 1, 43, 'Wamkhalimela, wamndulula kwaoko;', 'XHO22'),
(45, 1, 44, 'Athi kuye, Lumka ungaxeleli bani nanye into; hamba uye kuzibonisa kumbingeleli, usondeze oko wamisayo uMoses, ngenxa yokuhlanjululwa kwakho, kube bubungqina kubo.', 'XHO22'),
(45, 1, 45, 'Ke yena, akuphuma, waqala ukuyivakalisa kakhulu nokuyibhengeza loo ndawo, ngokokude angabi saba nako uYesu ukungena mzini ekuhleni; waba ngaphandle ezindaweni eziziintlango; babesithi beze kuye bevela ngeenxa zonke.', 'XHO22'),
(45, 2, 1, 'Kwakuba ntsuku, wabuya wangena eKapernahum; kwavakala ukuba usendlini.', 'XHO22'),
(45, 2, 2, 'Kwathi kwaoko kwahlanganisana into eninzi yabantu, ngokokude kungabi sabakho kukhwelelana, nakule mimandla isemnyango; wayelithetha ke kubo ilizwi.', 'XHO22'),
(45, 2, 3, 'Kwafika abantu kuye bezisa onedumbe, ethwelwe ngabané.', 'XHO22'),
(45, 2, 4, 'Bakuba bengenakusondela kuye ngenxa yendimbane, banqika uphahla apho abekhona, baza bakuba begqobhozile, baluhlisa ukhuko abelele kulo lowo unedumbe.', 'XHO22'),
(45, 2, 5, 'Akulubona uYesu ukholo lwabo, wathi konedumbe, Mntwana wam, uzixolelwe izono zakho.', 'XHO22'),
(45, 2, 6, 'Kwaye kukho ababhali abathile behleli, becamanga ezintliziyweni zabo, besithi,', 'XHO22'),
(45, 2, 7, 'Yini na ukuba lo enjenje ukuthetha izinyeliso? Ngubani na onako ukuxolela izono, ingenguye uThixo yedwa?', 'XHO22'),
(45, 2, 8, 'Eqiqe kwaoko uYesu ngomoya wakhe, ukuba benjenjalo ukucamanga ngaphakathi kwabo, wathi kubo, Yini na ukuba nicamange ezi zinto ezintliziyweni zenu?', 'XHO22'),
(45, 2, 9, 'Okukhona kulula phi na, ukuthi konedumbe, Zixolelwe izono zakho; nokuthi, Vuka, uthabathe ukhuko lwakho, uhambe?', 'XHO22'),
(45, 2, 10, 'Ke ukuze nazi ukuba unalo igunya uNyana woMntu lokuxolela izono emhlabeni apha (selesithi konedumbe),', 'XHO22'),
(45, 2, 11, 'Ndithi kuwe, Vuka, uthabathe ukhuko lwakho, uye endlwini yakho.', 'XHO22'),
(45, 2, 12, 'Wavuka kwaoko, waluthabatha ukhuko, waphuma phambi kwabo bonke; Bada bathi nqa bonke, bamzukisa uThixo, besithi, Asizanga sikubone okunje.', 'XHO22'),
(45, 2, 13, 'Wabuya waphuma waya ngaselwandle; zaye zisiza kuye zonke izihlwele;', 'XHO22'),
(45, 2, 14, 'Uthe ke edlula, wabona uLevi, lo ka-Alifeyu, ehleli endaweni yerhafu, wathi kuye, Ndilandele. Wesuka ke wamlandela.', 'XHO22'),
(45, 2, 15, 'Kwathi ekuhlaleni kwakhe ngasesithebeni endlwini yakhe, kwahlala nento eninzi yababuthi berhafu naboni ndawonye noYesu nabafundi bakhe; kuba babebaninzi, baye ke bemlandela.', 'XHO22'),
(45, 2, 16, 'Baye ababhali kwanabaFarisi, bakumbona esidla ndawonye nababuthi berhafu naboni, besithi kubafundi bakhe, Yini na ukuba adle asele nababuthi berhafu naboni?', 'XHO22'),
(45, 2, 17, 'Evile ke uYesu, wathi kubo, Akufuneki gqirha kwabaphili leyo lifuneka kwabafayo. Andize kubiza malungisa, ndize kubiza aboni ukuba baguquke.', 'XHO22'),
(45, 2, 18, 'Baye ke abafundi bakaYohane nababaFarisi bezila ukudla. Beza bathi kuye, Kungani na ukuba abafundi bakaYohane nababaFarisi bazile ukudla, kanti bona abafundi bakho abakuzili?', 'XHO22'),
(45, 2, 19, 'Wathi uYesu kubo, Banako yini na abegumbi lomtshakazi ukuzila ukudla, xa anabo umyeni? ngalo lonke ixesha basenabo umyeni, abanako ukuzila.', 'XHO22'),
(45, 2, 20, 'Kodwa kofika mihla awoza asuswe kubo umyeni, bandule ke ukuzila ngaloo mihla.', 'XHO22'),
(45, 2, 21, 'Akukho ke uthungela isiziba esitsha engutyeni endala;', 'XHO22'),
(45, 2, 22, 'Kanjalo akukho utha iwayini entsha ezintsubeni ezindala; okanye iwayini, leyo intsha, yozigqabhuza iintsuba, iphalale iwayini, zitshabalale neentsuba; yona iwayini entsha imelwe kukuthiwa ezintsubeni ezintsha.', 'XHO22'),
(45, 2, 23, 'NgoMhla wokuPhumla wacanda emasimini. Baqala ke abafundi bakhe ukukha izikhwebu.', 'XHO22'),
(45, 2, 24, 'Baza bathi kuye abaFarisi, Khangela, yini na ukuba benze ngemini yesabatha oko kungavumelekileyo?', 'XHO22'),
(45, 2, 25, 'Wathi kubo, Anizanga niyilese na into awayenzayo uDavide, oko wayeswele, elambile nokulamba, yena nabo babenaye?', 'XHO22'),
(45, 2, 26, 'Okokuba wangena endlwini kaThixo ngemihla ka-Abhiyatare umbingeleli omkhulu, wazidla izonka ezibekwa phambi koThixo, ekungavumelekileyo ukuba zidliwe ngabo bangebabingeleli, wanika nabo babenaye?', 'XHO22'),
(45, 2, 27, 'Wayesithi kubo, Isabatha le yabakho ngenxa yomntu, asingumntu owabakhoyo ngenxa yesabatha.', 'XHO22'),
(45, 2, 28, 'Ngoko ke uNyana woMntu lo uyiNkosi yayo nesabatha.', 'XHO22'),
(45, 3, 1, 'Wabuya wangena endlwini yesikhungu; Kwaye kukho apho umntu onesandla esomileyo.', 'XHO22'),
(45, 3, 2, 'Baye ke bemlalele ukubona ukuba womphilisa na ngesabatha; ukuze bammangalele.', 'XHO22'),
(45, 3, 3, 'Athi kuloo mntu unesandla esomileyo, Phakama uze phakathi.', 'XHO22'),
(45, 3, 4, 'Athi kubo, Okuvumelekileyo ngesabatha kukwenza okulungileyo, kukwenza okubi, kusini na? Kukusindisa, kukubulala, kusini na? Bathi tu ke bona.', 'XHO22'),
(45, 3, 5, 'Akuba ebasingasingile ngengqumbo, ebuhlungu ngenxa yokuqaqadeka kwentliziyo yabo, wathi kumntu lowo, Solule isandla sakho. Wasolula, saphila ke isandla sakhe, saba ngesinye.', 'XHO22'),
(45, 3, 6, 'Baphuma abaFarisi, benza kwaoko ibhunga namaHerode ngaye, lokuba bamtshabalalise.', 'XHO22'),
(45, 3, 7, 'Wemka uYesu nabafundi bakhe, waya elwandle; yamlandela inkitha enkulu yaseGalili nakwelakwaYuda', 'XHO22'),
(45, 3, 8, 'naseYerusalem, nakwaIdume, naphesheya kweYordan; NakwelaseTire neTsidon, inkitha enkulu, yakuva ukuba zininzi kwazo izinto abezenza, yeza kuye.', 'XHO22'),
(45, 3, 9, 'Waza wathi kubafundi bakhe, umkhonjana umlinde ngenxa yendimbane, hleze bamxinzelele phantsi.', 'XHO22'),
(45, 3, 10, 'Kuba wayephilise abantu abaninzi; ngokokude bathi ngunga phezu kwakhe, ukuze bamchukumise bonke ababenezibetho;', 'XHO22'),
(45, 3, 11, 'Nabo oomoya abangcolileyo babesithi, xa sukuba bembona, bawe phantsi kuye, bakhale, besithi, Wena unguye uNyana kaThixo.', 'XHO22'),
(45, 3, 12, 'Wabakhalimela ukuba bangamtyhili.', 'XHO22'),
(45, 3, 13, 'Unyuke ke intaba, abizele kuye abo abethanda ukubabiza yena ngokwakhe; beza kuye.', 'XHO22'),
(45, 3, 14, 'Wamisela abalishumi elinababini, ukuze babe naye, nokuba abathume baye kushumayela;', 'XHO22'),
(45, 3, 15, 'nokuba negunya lokuphilisa izifo, nelokukhupha iidemon;', 'XHO22'),
(45, 3, 16, 'Kwaye uSimon awamthiya ngokuba nguPetros;', 'XHO22'),
(45, 3, 17, 'noYakobi kaZebhedi, noYohane umzalwana kaYakobi; Waza wabathiya igama elinguBhowanerges, oko kukuthi, oonyana beendudumo;', 'XHO22'),
(45, 3, 18, 'noAndreya, noFilipu, noBhartolomeyu, noMateyu, noTomas, noYakobi ka-Alifeyu, noTadeyu, noSimon umKanan,', 'XHO22'),
(45, 3, 19, 'NoYuda Sikariyoti, lowo wamngcatshayo naye, bangena endlwini.', 'XHO22'),
(45, 3, 20, 'Kwabuya kwahlanganisana isihlwele, ngokokude bangabi nakutya isonka.', 'XHO22'),
(45, 3, 21, 'Bakuva ke oogxa bakhe, baphuma besiya kumbamba; kuba babesithi, Uyageza.', 'XHO22'),
(45, 3, 22, 'Baye bona ababhali, ababehle bevela eYerusalem, besithi, UnoBhelezebhule;', 'XHO22'),
(45, 3, 23, 'Wababizela kuye, wathetha nabo ngemizekeliso, esithi, Angathini na uSathana ukukhupha uSathana?', 'XHO22'),
(45, 3, 24, 'Nobukumkani, ukuba buthe bahlulelana bodwa, abunako ukuma obo bukumkani;', 'XHO22'),
(45, 3, 25, 'nendlu, ukuba ithe yahlulelana yodwa, ayinako ukuma loo ndlu;', 'XHO22'),
(45, 3, 26, 'NoSathana, ukuba uthe wazichasa ngokwakhe, wahlulelana yedwa, akanako ukuma, uphelile.', 'XHO22'),
(45, 3, 27, 'Akukho bani unako ukungena endlwini yegorha elo, ayiphange impahla yalo, ukuba akathanga tanci alibophe igorha elo; andule ke ukuyiphanga indlu yakhe.', 'XHO22'),
(45, 3, 28, 'Inene ndithi kuni, Zonke izono baya kuzixolelwa oonyana babantu, kwanezinyeliso abasukuba benyelisa ngazo;', 'XHO22'),
(45, 3, 29, 'Ke yena othe wanyelisa uMoya oyiNgcwele, akanako ukuxolelwa naphakade; unetyala lokugwetywa okungunaphakade.', 'XHO22'),
(45, 3, 30, 'Kuba besithi, Unomoya ongcolileyo.', 'XHO22'),
(45, 3, 31, 'Bafika ngoko abazalwana bakhe nonina, bema ngaphandle, bathumela kuye bembiza.', 'XHO22'),
(45, 3, 32, 'Zaye izihlwele zihleli zimphahlile; bathi kuye, Nanku unyoko nabazalwana bakho ngaphandle, bekufuna.', 'XHO22'),
(45, 3, 33, 'Wabaphendula wathi, Ngubani na uma? Ngoobani na nabazalwana bam?', 'XHO22'),
(45, 3, 34, 'Akuba ebasingasingile ngeenxa zonke ababehleli bemphahlile, wathi, Nanku uma nabazalwana bam.', 'XHO22'),
(45, 3, 35, 'Kuba othe wakwenza ukuthanda kukaThixo, lowo ngumzalwana wam, nodade wethu, noma.', 'XHO22'),
(45, 4, 1, 'Wabuya waqala ukufundisa ngaselwandle. Kwahlanganisana kuye indimbane enkulu, wada yena wangena emkhombeni, wahlala elwandle; saye sonke isihlwele siphezu kolwandle emhlabeni.', 'XHO22'),
(45, 4, 2, 'Wayebafundisa iindawo ezininzi ngemizekeliso. Wayesithi kubo ekufundiseni kwakhe,', 'XHO22'),
(45, 4, 3, 'Phulaphulani; Yabona, umhlwayeli waphuma waya kuhlwayela;', 'XHO22'),
(45, 4, 4, 'kwathi ekuhlwayeleni kwakhe, inxalenye yembewu yawa ngasendleleni, zeza iintaka zezulu zayidla zayigqiba.', 'XHO22'),
(45, 4, 5, 'Enye yawa endaweni elulwalwa, apho ibingenamhlaba mninzi; yantshula ke kwaoko, ngenxa yokuba ingenabunzulu bamhlaba;', 'XHO22'),
(45, 4, 6, 'Kuthe ke, lakuba shushu ilanga, yatsha; yathi ngenxa yokuba ingenangcambu, yoma kwaphela.', 'XHO22'),
(45, 4, 7, 'Yimbi yawa phakathi kwemithana enameva, yaza imithana enameva yakhula, yayiminxa, ayaba nasiqhamo.', 'XHO22'),
(45, 4, 8, 'Embi yawa kuwo wona umhlaba olungileyo, yavelisa isiqhamo, yahluma, yaqhama; yavelisa enye amashumi amathathu, enye amashumi amathandathu, nekhulu enye.', 'XHO22'),
(45, 4, 9, 'Wathi kubo, Lowo uneendlebe zokuva makeve.', 'XHO22'),
(45, 4, 10, 'Ke kaloku, xa abeyedwa, abo babemphahlile, ndawonye nabalishumi elinababini, bambuza umzekeliso lowo.', 'XHO22'),
(45, 4, 11, 'Uthe ke kubo, Nikuphiwe nina ukuyazi imfihlelo yobukumkani bukaThixo;', 'XHO22'),
(45, 4, 12, 'ukuze bebona babone, bangaqiqi; besiva ke beve, bangaqondi; hleze babuye baguquke, baxolelwe izono zabo.', 'XHO22'),
(45, 4, 13, 'Wathi kubo, Aniwazi na lo mzekeliso? Nothini na ke ukuyazi imizekeliso yonke?', 'XHO22'),
(45, 4, 14, 'Uhlwayela imbewu yelizwi.', 'XHO22'),
(45, 4, 15, 'Aba ke basendleleni, apho ilizwi lihlwayelwa khona, ngaba basendleleni, xa bathe beva, afike kwaoko uSathana, alithabathe ilizwi elihlwayelweyo ezintliziyweni zabo.', 'XHO22'),
(45, 4, 16, 'Ngokukwanjalo ngaba abo bahlwayelwa ezindaweni ezilulwalwa; abathi, bakuliva ilizwi, balamkele kwaoko bevuya;', 'XHO22'),
(45, 4, 17, 'babe bengenangcambu ngaphakathi kwabo, beme umzuzwana kodwa; bathi kamva, kwakuhla imbandezelo, nokuba yintshutshiso, ngenxa yelizwi, bakhutyekiswe kwaoko.', 'XHO22'),
(45, 4, 18, 'Aba bahlwayelweyo emithaneni enameva ngaba; abo balivayo ilizwi,', 'XHO22'),
(45, 4, 19, 'Aze amaxhala eli phakade, nokulukuhla kobutyebi, neenkanuko zezinye izinto, zingene ziliminxe ilizwi, lisuke lingabi nasiqhamo.', 'XHO22'),
(45, 4, 20, 'Abo bahlwayelwe kuwo umhlaba omhle ngaba; bathi bona balivayo ilizwi, balamkele, baxakathe isiqhamo, omnye amashumi amathathu, omnye amashumi amathandathu, nomnye ikhulu.', 'XHO22'),
(45, 4, 21, 'Wayesithi kubo, Isibane esi sikha siziswe ukuze sibekwe ngaphantsi kwesitya yini na, nokuba kuphantsi kwesingqengqelo? nengabekwa phezu kwesiphatho sezibane?', 'XHO22'),
(45, 4, 22, 'Akukho nto ifihlakeleyo ingayi kubonakala; kananjalo akubangakho nto ifihliweyo ingafihlelwa ukuba ize ekuhleni.', 'XHO22'),
(45, 4, 23, 'Ukuba ubani uneendlebe zokuva, makeve.', 'XHO22'),
(45, 4, 24, 'Wayesithi kubo, Yilumkeleni into eniyivayo; umlinganiselo enilinganisela ngawo, niya kulinganiselwa ngawo nani, kongezwe kuni nina nivayo.', 'XHO22'),
(45, 4, 25, 'Kuba osukuba enako, uya kunikwa; nosukuba engenako, uya kuhluthwa kwanoko anako.', 'XHO22'),
(45, 4, 26, 'Uthe ke, Ubukumkani bukaThixo bunjengokuba abengathi umntu aphose imbewu emhlabeni;', 'XHO22'),
(45, 4, 27, 'Ubusuku nemini alale, avuke, ize imbewu intshule, ikhule engazi ukuba njani na.', 'XHO22'),
(45, 4, 28, 'Kuba umhlaba uvelisa iziqhamo ngokwawo; kuqala ibe likhaba, kwalandela isikhwebu, ize ibe yingqolowa ezalisekileyo esikhwebini;', 'XHO22'),
(45, 4, 29, 'Xa sithe savuthwa isiqhamo, alifake kwaoko irhengqa, ngokuba kusekusondele ukuvuna.', 'XHO22'),
(45, 4, 30, 'Uthe ke, Siya kubufanekisa nantoni na ubukumkani bukaThixo? Siya kuthelekisa ngamzekeliso mni na?', 'XHO22'),
(45, 4, 31, 'Bunjengokhozo lwemostade, oluthi, xa sukuba luhlwayelwa emhlabeni, lube luloluncinane kuzo zonke iimbewu ezisemhlabeni;', 'XHO22'),
(45, 4, 32, 'luthi ke, xa luthe lwahlwayelwa, lukhule lube lukhulu kuyo yonke imifuno, lwenze amasebe amakhulu, ngokokude iintaka zezulu zibe nako ukuhlala phantsi komthunzi wawo.', 'XHO22'),
(45, 4, 33, 'Wayelithetha kubo ilizwi ngemizekeliso emininzi enjalo, njengoko baye benako ukuva.', 'XHO22'),
(45, 4, 34, 'Ubengathethi ke kubo kungengamzekeliso; ke ubesithi, bakuba bodwa, abacombululele iindawo zonke abafundi bakhe.', 'XHO22'),
(45, 4, 35, 'Kwangaloo mini kwakuhlwa, athi kubo, Masiwele siye ngaphesheya.', 'XHO22'),
(45, 4, 36, 'Basindulula isihlwele, bamthabatha esemkhombeni ngoko. Kwaye ke kukho eminye imikhonjana inaye.', 'XHO22'),
(45, 4, 37, 'Kwabakho uqhwithela olukhulu lomoya; aye ke amaza ephoseka emkhombeni, abetha wafuna ukuzala kwangoku.', 'XHO22'),
(45, 4, 38, 'Ke yena ebesemva emkhombeni, elele emqamelweni; bamvuse, bathi kuye, Mfundisi, akukhathali na, sitshabalala nje?', 'XHO22'),
(45, 4, 39, 'Wavuka, wawukhalimela umoya, wathi kulo ulwandle, Yithi tu, uthi tu. Wadamba umoya, kwabakho ukuzola okukhulu.', 'XHO22'),
(45, 4, 40, 'Wathi kubo, Yini na ukuba nibe nje, ukuba ngamagwala? Yini na ukuba ningabi nalukholo?', 'XHO22'),
(45, 4, 41, 'Boyika ngoloyiko olukhulu, batshono ukuthi, Ngubani na ke lo, le nto alulanyelwa nangumoya nalulwandle?', 'XHO22'),
(45, 5, 1, 'Baza bawelela ngaphesheya kolwandle, kummandla wamaGadara.', 'XHO22'),
(45, 5, 2, 'Akuba ephumile emkhombeni, wahlangatyezwa kwaoko ngumntu onomoya ongcolileyo, ephuma emangcwabeni,', 'XHO22'),
(45, 5, 3, 'owayenekhaya lakhe emangcwabeni; kwaye kungekho bani unako ukumbopha nangemixokelelwane;', 'XHO22'),
(45, 5, 4, 'Ngokuba wayebotshwa futhi ngamakhamandela nemixokelelwane, asuke amatyathanga asuke asuke akhe, awakroboze namakhamandela;', 'XHO22'),
(45, 5, 5, 'Ube ehlala ubusuku nemini esezintabeni nasemangcwabeni ekhala, ezicenta ngamatye.', 'XHO22'),
(45, 5, 6, 'Uthe ke akumbona uYesu esekude, wabaleka, waqubuda kuye.', 'XHO22'),
(45, 5, 7, 'Wadanduluka ngezwi elikhulu, wathi, Yintoni na enam nawe, Yesu, Nyana kaThixo Osenyangweni? Ndikufungisa uThixo, ukuba ungandithuthumbisi.', 'XHO22'),
(45, 5, 8, 'Kuba ebethe kuye, Phuma emntwini apho, moyandini ungcolileyo.', 'XHO22'),
(45, 5, 9, 'Yambuza ke esithi, Ungubani na igama lakho? Waphendula ke yena esithi, NdinguMkhosi igama lam; kuba sibaninzi.', 'XHO22'),
(45, 5, 10, 'Waye ke embongoza kakhulu ukuba angabandululi bemke kwelo zwe.', 'XHO22'),
(45, 5, 11, 'Bekukho ke apho ngasezintabeni ugxuba olukhulu lweehagu lusidla.', 'XHO22'),
(45, 5, 12, 'Zambongoza zonke iindimoni, zisithi, Sithumele ezihagwini, ukuze singene kuzo.', 'XHO22'),
(45, 5, 13, 'Wazivumela kwaoko uYesu. Baphuma ke oomoya abangcolileyo, baya kungena ezihagwini; lwaza ugxuba olo lwaphaphatheka, lwehla eweni, lwela elwandle;', 'XHO22'),
(45, 5, 14, 'Bathe ke abo babezalusa iihagu basaba, babika ekhaya nasemaphandleni. Baphuma ke abantu, baya kuyibona loo nto ihlileyo.', 'XHO22'),
(45, 5, 15, 'Bafike kuYesu, bambone obephethwe ziidemon, lowo ke wayenomkhosi, ehleli, ambathisiwe, enengqondo ephilileyo; baza boyika.', 'XHO22'),
(45, 5, 16, 'Baza abo babeyibonile le nto babacokisela, ukuba kuthekeni na kobephethwe ziidemon; kwanokweehagu.', 'XHO22'),
(45, 5, 17, 'Baqala ukumbongoza ukuba emke emideni yabo.', 'XHO22'),
(45, 5, 18, 'Akuba engene emkhombeni, lowo ubephethwe ziidemon wambongoza ukuba abe naye.', 'XHO22'),
(45, 5, 19, 'Kodwa uYesu akamvumelanga. Usuke wathi kuye, Hamba, uye endlwini yakho kwabakowenu, ubabikele oko zingako ukuba zinkulu izinto ekwenzele zona iNkosi, yaba nenceba kuwe.', 'XHO22'),
(45, 5, 20, 'Wemka, waqala ukuzivakalisa eDekapoli oko zingako ukuba zinkulu izinto uYesu abemenzele zona; bamangaliswa bonke.', 'XHO22'),
(45, 5, 21, 'Kwathi, akubon’ ukuba uYesu uwelile ngomkhombe, wabuya weza kwelinye icala, kwahlanganisana kuye isihlwele esikhulu; waye engaselwandle apho.', 'XHO22'),
(45, 5, 22, 'Kwabonakala kusiza omnye wakubaphathi bendlu yesikhungu, ogama linguYayiro; Uthe ke akumbona, wawa ezinyaweni zakhe.', 'XHO22'),
(45, 5, 23, 'Wambongoza kakhulu, esithi, Intombi yam igulela ukufa; khawuze ubeke izandla phezu kwayo, ukuze iphile; yophila ke.', 'XHO22'),
(45, 5, 24, 'Wahamba naye uYesu; yaye indimbane enkulu imlandela, imxinile.', 'XHO22'),
(45, 5, 25, 'Kwaye kukho ntokazi ithile, eyayinethombo legazi iminyaka elishumi elinamibini.', 'XHO22'),
(45, 5, 26, 'Wayeve ubunzima obuninzi kumagqirha amaninzi, edleke konke abenako, engancedi lutho, esuke wahambela phambili ebubini:', 'XHO22'),
(45, 5, 27, 'yathi yakuva ngaye uYesu, yeza phakathi kwesihlwele ngasemva, yayichukumisa ingubo yakhe;', 'XHO22'),
(45, 5, 28, 'kuba ibisithi, Ukuba ndithe ndachukumisa neengubo zakhe ezi, ndosindiswa.', 'XHO22'),
(45, 5, 29, 'Watsha kwaoko umthombo wegazi layo; yeva emzimbeni wayo ukuba iphilisiwe kweso sibetho.', 'XHO22'),
(45, 5, 30, 'uYesu, ethe kwaoko wawazi ngaphakathi kwakhe amandla aphume kuye, wajika esendimbaneni, wathi, Ngubani na ochukumise iingubo zam?', 'XHO22'),
(45, 5, 31, 'Bathi kuye abafundi bakhe, Ubona nje ukuba isihlwele sikuxinile, utsho na ukuthi, Ngubani na lo undichukumisileyo?', 'XHO22'),
(45, 5, 32, 'Wasingasinga, ukuba ambone lowo uyenzileyo le nto.', 'XHO22'),
(45, 5, 33, 'Ithe ke intokazi leyo, isoyika, ingcangcazela, ikwazi oko kwenzekileyo kuyo, yeza yawa phantsi phambi kwakhe, yamxelela inyaniso yonke.', 'XHO22'),
(45, 5, 34, 'Wathi ke kuyo, Ntombi yam, ukholo lwakho lukusindisile; hamba ngoxolo, phila kwisibetho sakho.', 'XHO22'),
(45, 5, 35, 'Kwathi, esathetha, kwafika amadoda evela kwamphathi wendlu yesikhungu, athi, Ifile intombi yakho; usamkhathazelani na uMfundisi?', 'XHO22'),
(45, 5, 36, 'Kwangoko uYesu, elivile elo zwi lithethwayo, wathi kumphathi wendlu yesikhungu, Musa ukoyika, kholwa kuphela.', 'XHO22'),
(45, 5, 37, 'Akavumela mntu ukuba alandelane naye, kwakuphela nguPetros noYakobi noYohane, umzalwana kaYakobi.', 'XHO22'),
(45, 5, 38, 'Afike endlwini yomphathi wendlu yesikhungu, abone isiphithiphithi, abalilayo nabenza isijwili esikhulu.', 'XHO22'),
(45, 5, 39, 'Akungena athi kubo, Yini na ukuba niphithizele, nilile? Intombazana ayifile, ilele.', 'XHO22'),
(45, 5, 40, 'Basuka bamwa ngentsini. Athi akuba ebakhuphele phandle bonke, amthabathe uyise womntwana nonina, nabo babenaye, angene apho wayelele khona umntwana lowo.', 'XHO22'),
(45, 5, 41, 'Wayibamba ngesandla intombazana leyo, wathi kuyo, Talita, kumi; oko kukuthi ngentetho evakalayo, Ntombazana, ndithi kuwe, Vuka.', 'XHO22'),
(45, 5, 42, 'Yavuka kwaoko intombazana leyo, yahambahamba; kuba ibiminyaka ilishumi elinamibini izelwe. Bathi nqa ngokuthi nqa okukhulu.', 'XHO22'),
(45, 5, 43, 'Wabayala kakhulu ukuba kungaziwa mntu oku; wathi mayiyinikwe idle.', 'XHO22'),
(45, 6, 1, 'Ephumile ke apho, weza kwelakowabo; bamlandela ke abafundi bakhe.', 'XHO22'),
(45, 6, 2, 'Kwathi kwakubakho isabatha, waqala ukufundisa endlwini yesikhungu; baza abaninzi, bakuva, bakhwankqiswa, besithi, Lo uzizuze phi na ezi zinto? Bubulumko buni na obu abunikiweyo, le nto nemisebenzi yamandla engaka yenziwe ngezandla zakhe?', 'XHO22'),
(45, 6, 3, 'Lo akayiyo na ingcibi yokwakha, unyana kaMariya, umzalwana ke kaYakobi noYose noYuda noSimon? Noodade wabo abakho kuthi na apha? Baye bekhubeka kuye.', 'XHO22'),
(45, 6, 4, 'Wathi ke uYesu kubo, Umprofeti akasweli mbeko, kungaba kukowabo, nakwizizalwana zakhe, nakokwabo.', 'XHO22'),
(45, 6, 5, 'Wayengenako ukwenza namnye umsebenzi wamandla khona apho, kwakuphela ukubeka izandla phezu kwemilwelwe embalwa, wayiphilisa.', 'XHO22'),
(45, 6, 6, 'Wamangaliswa kukungakholwa kwabo. Waye ejikeleza imizana, efundisa.', 'XHO22'),
(45, 6, 7, 'Ubabizele kuye abalishumi elinababini, waqala ukubathuma ngababini, ngababini; ebanika igunya koomoya abangcolileyo;', 'XHO22'),
(45, 6, 8, 'Wabathethela ngelithi, mabangaphathi nto yandlela, ibe ngumsimelelo wodwa; nangxowa yamphako, nasonka, namali enxilini;', 'XHO22'),
(45, 6, 9, 'Banxibe iimbadada; ningambathi neengubo zangaphantsi ngambini.', 'XHO22'),
(45, 6, 10, 'Wayesithi kubo, Apho nithe nangena endlwini, hlalani khona nide nimke kuloo ndawo.', 'XHO22'),
(45, 6, 11, 'Abo sukuba benganamkeli, benganiva nokuniva, nakuphuma kuloo ndawo, vuthululani uthuli oluphantsi kweenyawo zenu, kube bubungqina oko kubo. Inene ndithi kuni, Koba nokunyamezeleka okweSodom neGomora ngomhla womgwebo, kunokwaloo mzi.', 'XHO22'),
(45, 6, 12, 'Baphuma bavakalisa ukuba mabaguquke abantu.', 'XHO22'),
(45, 6, 13, 'Bakhupha needemon ezininzi, bayithambisa ngeoli imilwelwe emininzi, beyiphilisa.', 'XHO22'),
(45, 6, 14, 'Wakuva ukumkani uHerode; (kuba lalithe laduma igama lakhe;) wayesithi, Uvukile kwabafileyo uYohane umbhaptizi; ngenxa yoko le misebenzi yamandla iyenzeka ngaye.', 'XHO22'),
(45, 6, 15, 'Baye abanye besithi, NguEliya; Babesithi ke abanye, Ngumprofeti, nokuba ufana nomnye wakubaprofeti.', 'XHO22'),
(45, 6, 16, 'Ekuvile ke oko uHerode, wathi, Lo nguYohane, lowa ndamnqamla intloko mna; uvukile kwabafileyo.', 'XHO22'),
(45, 6, 17, 'Kuba uHerode ngokwakhe wabethe wathuma, wambamba uYohane, wambopha entolongweni, ngenxa kaHerodiya, umfazi kaFilipu umninawa wakhe; kuba ebemzekile.', 'XHO22'),
(45, 6, 18, 'Kuba wayesithi uYohane kuHerode, Akuvumelekile ukuba ube nomfazi womzalwana wakho.', 'XHO22'),
(45, 6, 19, 'Ke kaloku uHerodiya waba nenqala ngakuye, wafuna ukumbulala; kodwa akabanga nako:', 'XHO22'),
(45, 6, 20, 'kuba uHerode wayemoyika uYohane, emazi ukuba uyindoda elilungisa, engcwele, wayemgcinile; wayesithi ke akumva, enze iindawo ezininzi, amve kamnandi.', 'XHO22'),
(45, 6, 21, 'Ke kaloku, kwakufika umhla owamlungelayo, uHerode ngemini yokuzalwa kwakhe wayebenzela isidlo izikhulu zakhe, namabamba akhe amakhulu, namagqala elaseGalili;', 'XHO22'),
(45, 6, 22, 'yakubon’ ukuba ingene intombi kaHerodiya lowo, yangqungqa, yamkholisa uHerode nabo babehleli ndawonye naye. Wathi ukumkani kwintombazana leyo, Ndicele into osukuba uyithanda; ndokupha.', 'XHO22'),
(45, 6, 23, 'Wayifungela ngelithi, Into othe wayicela kum ndokupha, kude kube kwisiqingatha sobukumkani bam.', 'XHO22'),
(45, 6, 24, 'Iphumile ke yathi kunina, Ndicele ntoni na? Wathi ke yena, Intloko kaYohane umbhaptizi.', 'XHO22'),
(45, 6, 25, 'Yena kungena kwaoko ngokungxama kuye ukumkani, yacela, isithi, Ndithanda ukuba undinike kalokunje ngesitya intloko kaYohane umbhaptizi.', 'XHO22'),
(45, 6, 26, 'Ukumkani waba buhlungu kakhulu; noko, ngenxa yesifungo sakhe, nangenxa yabo babehleli ndawonye naye, akathandanga kuyilandulela.', 'XHO22'),
(45, 6, 27, 'Ukumkani ke wasusa umsiki, wamthethela ngelithi, mayiziswe intloko yakhe.', 'XHO22'),
(45, 6, 28, 'Wayizisa intloko yakhe ngesitya, wayinika intombazana leyo; yaza intombazana yayinika unina.', 'XHO22'),
(45, 6, 29, 'Bekuvile oko abafundi bakhe, beza basithabatha isidumbu sakhe, basibeka engcwabeni.', 'XHO22'),
(45, 6, 30, 'Bahlanganisana abapostile kuYesu, bambikela iinto zonke, ezo babezenzile, kwanezo babezifundisile.', 'XHO22'),
(45, 6, 31, 'Wathi kubo, Yizani nina ngasese kwindawo eyintlango, niphumle kancinane; kuba bebaninzi abezayo nabemkayo; babengenalo ke nethutyana lokuba badle.', 'XHO22'),
(45, 6, 32, 'Bemka ngomkhombe ngasese, baya kwindawo eyintlango.', 'XHO22'),
(45, 6, 33, 'Isihlwele sababona bemka bathi abaninzi bamazi, babalekela khona ngeenyawo, bevela kuyo yonke imizi; babaphangela, bathuthelana kuye.', 'XHO22'),
(45, 6, 34, 'Uthe ke akuphuma uYesu, wabona isihlwele esikhulu; wasikwa yimfesane ngabo, ngokuba babenjengezimvu ezingenamalusi; waqala ukubafundisa iindawo ezininzi.', 'XHO22'),
(45, 6, 35, 'Kwakubon’ ukuba seyihambile kakhulu imini, beza kuye abafundi bakhe, bathi, Le ndawo iyintlango, nemini seyihambile kakhulu;', 'XHO22'),
(45, 6, 36, 'bandulule bemke, baye emaphandleni ajikelezileyo nasemizaneni, bazithengele izonka; kuba bengenanto bangayidlayo.', 'XHO22'),
(45, 6, 37, 'Waphendula ke wathi kubo, Bapheni nina batye. Bathi kuye, Simke na siye kuthenga izonka ngeedenariyo ezimakhulu mabini, sibaphe badle?', 'XHO22'),
(45, 6, 38, 'Athi kubo, Zingaphi na izonka eninazo? yiyani nibone. Besazi, bathi, Zintlanu, neentlanzi ezimbini.', 'XHO22'),
(45, 6, 39, 'Wabathethela ngelithi, mababangqengqise bonke, babe ziziqhu ngeziqhu engceni eluhlaza.', 'XHO22'),
(45, 6, 40, 'Bahlala phantsi beziingcamba ngeengcamba ngamakhulu, nangamashumi ngamahlanu.', 'XHO22'),
(45, 6, 41, 'Wazithabatha izonka zozihlanu neentlanzi zombini, wathi ekhangele ezulwini wasikelela, waziqhekeza izonka, wanika abafundi bakhe, ukuba babeke phambi kwabo; wathi neentlanzi zombini wazabela bona bonke.', 'XHO22'),
(45, 6, 42, 'Badla bahlutha bonke.', 'XHO22'),
(45, 6, 43, 'Baza basusa izitya zizele ngamaqhekeza zalishumi elinazibini, basusa nakwiintlanzi.', 'XHO22'),
(45, 6, 44, 'Ababesidla izonka babengamadoda akumawaka amahlanu.', 'XHO22'),
(45, 6, 45, 'Wabanyanzela kwaoko abafundi bakhe ukuba bangene emkhombeni, bamandulele baye ngaphesheya eBhetesayida, lo gama asindululayo isihlwele yena.', 'XHO22'),
(45, 6, 46, 'Akuba ebulisene nabo, wemka waya kuthandaza entabeni.', 'XHO22'),
(45, 6, 47, 'Kwakuhlwa, umkhombe ubuphakathi elwandle, eyedwa emhlabeni.', 'XHO22'),
(45, 6, 48, 'Wababona bebulaleka ekuqhubeni; kuba umoya ububavele ngaphambili. Athi ngolwesine ulindo lobusuku eze kubo, ehamba phezu kolwandle; wanga angacanda kubo.', 'XHO22'),
(45, 6, 49, 'Kodwa bakumbona ehamba phezu kolwandle, bangathi ngumshologu, bakhala;', 'XHO22'),
(45, 6, 50, 'kuba bonke bambona, bakhathazeka. Wathetha kwaoko nabo, wathi kubo, Yomelelani; ndim; musani ukoyika.', 'XHO22'),
(45, 6, 51, 'Wenyuka waya kubo emkhombeni, wadamba umoya. Bathi nqa ngaphakathi kwabo ngokuncamisileyo kunene, bamangaliswa;', 'XHO22'),
(45, 6, 52, 'kuba bengakuqondanga okwezonka; kuba intliziyo yabo yabe iqaqadekile.', 'XHO22'),
(45, 6, 53, 'Bewelile, bafika emhlabeni waseGenesarete, balubeka elunxwemeni.', 'XHO22'),
(45, 6, 54, 'Bakuphuma emkhombeni, bamazi kwaoko;', 'XHO22'),
(45, 6, 55, 'bawubaleka wonke loo mmandla, baqala ukubathwala ngeenkuko abafayo, bebasa apho babemva ukuba ukhona.', 'XHO22'),
(45, 6, 56, 'Nalapho sukuba ebengena khona, nokuba kusemzaneni, nokuba kusemizini, nokuba kusemaphandleni, babesithi, bababeke abafayo ezitratweni, bambongoze ukuba khe bachukumise intshinga yengubo yakhe. Baye ke bephiliswa abo babemchukumisa.', 'XHO22'),
(45, 7, 1, 'Bahlanganisana ke abaFarisi, nabathile kubachazi-mthetho, bevela eYerusalem;', 'XHO22'),
(45, 7, 2, 'Babona inxenye kubafundi bakhe besidla isonka ngezandla eziziinqambi, oko kukuthi, ngezingahlanjwanga, basuka basola.', 'XHO22'),
(45, 7, 3, 'Kuba abaFarisi namaYuda onke abadli, ukuba abakhanga bazihlambe izandla kunene, bebambe isithethe samanyange.', 'XHO22'),
(45, 7, 4, 'Nokuba bavela endaweni yembutho, abadli bengakhanga bahlambe; Kukho nezinye izinto ezininzi, abazamkele ukuba bazibambe, ezinjengeentlambo zeendebe, neebhekile, nezitya zobhedu, neetafile.', 'XHO22'),
(45, 7, 5, 'Bambuza ke abaFarisi nababhali, besithi, Kungani na ukuba abafundi bakho bangahambi ngokwesithethe samanyange, le nto badla isonka ngezandla ezingahlanjwanga?', 'XHO22'),
(45, 7, 6, 'Waphendula ke wathi kubo, Watyapha uIsaya ukuprofeta ngani, bahanahanisi, njengokuba kubhaliwe kwathiwa, Aba bantu bandibeka ngomlomo, Kanti yona intliziyo yabo ikude lee kum.', 'XHO22'),
(45, 7, 7, 'Bafumana ke bendihlonela, Befundisa iimfundiso eziyimithetho yabantu.', 'XHO22'),
(45, 7, 8, 'Nithi, niwuyeka okaThixo umthetho, nibambe isithethe sabantu esinjengeentlambo zeebhekile neendebe; naye nizenza nezinye izinto ezininzi ezinjengezo.', 'XHO22'),
(45, 7, 9, 'Wayesithi kubo, Natyapha kambe niwutshitshise umthetho kaThixo, ukuze nisigcine isithethe senu.', 'XHO22'),
(45, 7, 10, 'Kaloku uMosis wathi: “Beka uyihlo nonyoko; nokuthi, Othuka uyise nokuba ngunina, makafe ukufa;', 'XHO22'),
(45, 7, 11, 'Ke nina nithi, Ukuba umntu uthe kuyise nokuba kukunina, YiKorbhan, oko kukuthi, ingumnikelo kuThixo, into onge uba uncedwa ngayo ndim, ukholisile; uya kukhululwa;', 'XHO22'),
(45, 7, 12, 'anisayi kumvumela ukuba amenzele nto, nokuba nguyise nokuba ngunina;', 'XHO22'),
(45, 7, 13, 'Nilenza litshitshile ilizwi likaThixo ngesithethe senu enasinikayo, naye nizenza izinto ezininzi ezinjengezo.', 'XHO22'),
(45, 7, 14, 'Esibizele kuye sonke indimbane, wathi kubo, Phulaphulani kum nonke, niqonde.', 'XHO22'),
(45, 7, 15, 'Akukho nto ingaphandle komntu ethi ingena kuye ibe nako ukumenza inqambi; zizinto eziphumayo kuye ezimenza inqambi umntu.', 'XHO22'),
(45, 7, 16, 'Ukuba ubani uneendlebe zokuva, makeve.', 'XHO22'),
(45, 7, 17, 'Baye ke, xa ebengene endlwini, evela kuyo indimbane, bembuza abafundi bakhe umzekeliso lowo.', 'XHO22'),
(45, 7, 18, 'Athi kubo, Nikwanje nani na ukuswela ingqondo? Aniqondi na ukuba yonke into engaphandle ingena emntwini, ayinako ukumenza inqambi;', 'XHO22'),
(45, 7, 19, 'Kaloku akungeni entliziyweni yakhe, kungekabi kusesiswini sakhe;', 'XHO22'),
(45, 7, 20, 'Wayesithi ke, Into ephumayo emntwini, yiyo leyo emenza inqambi umntu;', 'XHO22'),
(45, 7, 21, 'Kuba ngaphakathi, kuyo intliziyo yomntu, kuphuma izicamango ezingendawo, ookukrexeza, oomibulo, ookubulala;', 'XHO22'),
(45, 7, 22, 'ubusela, ukubawa, okungendawo, inkohliso, uburheletya, iliso elibi, ukunyelisa, ukukratsha, ukuswela kuqonda;', 'XHO22'),
(45, 7, 23, 'zonke ezi zinto zingendawo ziphuma ngaphakathi, zimenze inqambi umntu.', 'XHO22'),
(45, 7, 24, 'Esukile apho ke, waya emideni yaseTire neyaseTsidon. Wangena endlwini, wanga akungaziwa mntu; akaba nako ukufihlakala noko.', 'XHO22'),
(45, 7, 25, 'Kuba yathi yakuva ngaye intokazi entombi ibinomoya ongcolileyo, yeza yawa ezinyaweni zakhe.', 'XHO22'),
(45, 7, 26, 'Ke kaloku intokazi leyo ingumGrikekazi, umSirofenike ngokuzalwa; yambongoza ukuba ayikhuphe idemon entombini yayo.', 'XHO22'),
(45, 7, 27, 'Uthe ke uYesu kuyo, Khawuyeke kuhluthe abantwana kuqala; kuba akukuhle ukusithabatha isonka sabantwana, siphoswe ezinjaneni.', 'XHO22'),
(45, 7, 28, 'Waphendula ke wathi kuye, Ewe, Nkosi; kuba nazo iinjana phantsi kwetafile zidla kwiimvuthuluka zabantwana.', 'XHO22'),
(45, 7, 29, 'Wathi kuyo, Ngenxa yelo zwi, hamba; idemon iphumile entombini yakho.', 'XHO22'),
(45, 7, 30, 'Yesuka yaya endlwini yayo, yafumana iphumile idemon, intombi ilele phezu kwesi ngqengqelo.', 'XHO22'),
(45, 7, 31, 'Wabuya waphuma emideni yaseTire, waya eTsidon, weza kulwandle lwaseGalili, ecanda phakathi kwemida yaseDekapoli.', 'XHO22'),
(45, 7, 32, 'Kwaziswa kuye umntu osisithulu, osisithulu; bambongoza ukuba abeke isandla sakhe phezu kwakhe.', 'XHO22'),
(45, 7, 33, 'Wamthabatha ke esihlweleni, wayibeka bucala iminwe yakhe ezindlebeni zakhe, watshica, wachukumisa ulwimi lwakhe.', 'XHO22'),
(45, 7, 34, 'Wakhangela ezulwini, wancwina, wathi kuyo, Efata; oko kukuthi, Vuleka.', 'XHO22'),
(45, 7, 35, 'Zavuleka kwaoko iindlebe zaso, zacombuluka iingqambu zolwimi lwaso, sathetha salungisa.', 'XHO22'),
(45, 7, 36, 'Wabayala ukuba mabangaxeleli bani; kodwa okukhona wabayalayo, kwaba kukhona bakuvakalisa ngakumbi.', 'XHO22'),
(45, 7, 37, 'Bakhwankqiswa ngokuncamisileyo kanye, besithi, Uzenze iinto zonke kakuhle; uzenza nezithulu zive, nabangenakuthetha bathethe.', 'XHO22'),
(45, 8, 1, 'Ke kaloku, ngaloo mihla, ekubeni zininzi izihlwele, kungekho nanto idliwayo na, uYesu wababizela kuye abafundi bakhe, wathi kubo,', 'XHO22'),
(45, 8, 2, 'Ndisikwa yimfesane ngenxa yezihlwele; ngokuba sekuntsuku ntathu behleli nam, bengenanto bangayidlayo;', 'XHO22'),
(45, 8, 3, 'ukuba ndithe ndabandulula baya emakhayeni abo bengadlanga, botyhafa endleleni; kuba inxenye yabo ivela kude.', 'XHO22'),
(45, 8, 4, 'Bamphendula abafundi bakhe, besithi, Wokuzuza phi na umntu ukuba nako ukubahluthisa aba ngezonka, entlango apha?', 'XHO22'),
(45, 8, 5, 'Wababuza esithi, Zingaphi na izonka eninazo? Bathi ke bona, Zisixhenxe.', 'XHO22'),
(45, 8, 6, 'Wayalela isihlwele ukuba sihlale phantsi emhlabeni. Wazithabatha izonka zosixhenxe, wabulela, waqhekeza, wanika abafundi bakhe, ukuba babeke phambi kwabo; zazibeka phambi kwabantu.', 'XHO22'),
(45, 8, 7, 'Baye beneentlanzana ezimbalwa; akuba esikelele, wathi mazibekwe phambi kwabo nazo.', 'XHO22'),
(45, 8, 8, 'Badla ke bahlutha, kwaza kwaqokelelwa iingobozi zasixhenxe zamaqhekeza aseleyo.', 'XHO22'),
(45, 8, 9, 'Ke kaloku abo babesidla babemalunga namawaka amane. Wabandulula ke.', 'XHO22'),
(45, 8, 10, 'Wangena kwaoko emkhombeni, nabafundi bakhe, weza emacaleni aseDalmanuta.', 'XHO22'),
(45, 8, 11, 'Baphuma abaFarisi, baqala ukubuzana naye, befuna kuye umqondiso ophuma ezulwini, bemlinga.', 'XHO22'),
(45, 8, 12, 'Encwina kakhulu emoyeni wakhe, wathi, Yini na ukuba esi sizukulwana singxamele umqondiso? Inene, ndithi kuni, Akukho mqondiso uya kunikwa esi sizukulwana.', 'XHO22'),
(45, 8, 13, 'Wabashiya, wabuya wangena emkhombeni, wemka waya ngaphesheya.', 'XHO22'),
(45, 8, 14, 'Ke kaloku abafundi babelibele ukuphatha izonka; kananjalo babengenanto isikhephe emkhombeni ngaphandle kwesonka esinye.', 'XHO22'),
(45, 8, 15, 'Wabayala esithi, Ligqaleni, nililumkele igwele labaFarisi, negwele likaHerode.', 'XHO22'),
(45, 8, 16, 'Baye becamanga bodwa besithi, Kungokuba singenazonka.', 'XHO22'),
(45, 8, 17, 'Ekwazi oko uYesu, wathi kubo, Yini na ukuba nicamange, ngokuba ningenazonka? Anikaqondi na ukuba anikaqondi? Isaqaqadekile na intliziyo yenu?', 'XHO22'),
(45, 8, 18, 'Ninamehlo nje, aniboni na? Nineendlebe nje, aniva na? anikhumbuli na?', 'XHO22'),
(45, 8, 19, 'Oko ndaqhekezela amawaka amahlanu izonka ezihlanu, zazingaphi na iingobozi zizele ngamaqhekeza enazisusayo? Bathi kuye, Balishumi elinababini.', 'XHO22'),
(45, 8, 20, 'Okuya ke ezisixhenxe ndaziqhekezela amawaka amane, zazingaphi na iingobozi zizele ngamaqhekeza enazisusayo? Bathi ke bona, Zisixhenxe.', 'XHO22'),
(45, 8, 21, 'Uthe ke kubo, Phofu yini na ukuba ningaqondi?', 'XHO22'),
(45, 8, 22, 'Ufika eBhetesayida; bazisa imfama kuye, bambongoza ukuba ayichukumise.', 'XHO22'),
(45, 8, 23, 'Wayibamba ngesandla imfama, wayirholela ngaphandle komzi; Watshica emehlweni ayo, wabeka izandla phezu kwayo, wayibuza ukuba ibona nto na?', 'XHO22'),
(45, 8, 24, 'Yaza yakhangela phezulu yathi, Ndiyababona abantu behamba, ngathi yimithi.', 'XHO22'),
(45, 8, 25, 'Wabuya wazibeka izandla phezu kwamehlo ayo, wayikhangelisa phezulu; yabuya yaphila, yababona bonke kakuhle.', 'XHO22'),
(45, 8, 26, 'Wayindulula ukuba iye endlwini yayo, esithi, Uze ungangeni esixekweni, ungathethi nakubani kuwo umzi.', 'XHO22'),
(45, 8, 27, 'Waphuma uYesu nabafundi bakhe, waya emizini yaseKesareya yakwaFilipu; waye endleleni ebabuza abafundi bakhe, esithi kubo, Kuthiwa ngabantu ndingubani na mna?', 'XHO22'),
(45, 8, 28, 'Baphendula ke bona, bathi, UnguYohane umbhaptizi; bambi ke bathi, Ungomnye wakubaprofeti.', 'XHO22'),
(45, 8, 29, 'Athi kubo, Ke nina nithi ndingubani na? Aphendule ke uPetros athi kuye, Wena unguye uKristu.', 'XHO22'),
(45, 8, 30, 'Wabakhalimela ukuba bangaxeleli bani ngaye.', 'XHO22'),
(45, 8, 31, 'Waqala ukubafundisa, okokuba uNyana woMntu umelwe kukuba eve ubunzima obukhulu, alahlwe ngawo amadoda amakhulu nababingeleli abakhulu nababhali, abulawe, athi emveni kweentsuku ezintathu abuye avuke.', 'XHO22'),
(45, 8, 32, 'Elo zwi wayelithetha ngokungafihlisiyo. Waza uPetros wamthabatha, waqala ukumkhalimela.', 'XHO22'),
(45, 8, 33, 'Uthe ke yena, ejikile, wabakhangela abafundi bakhe, wamkhalimela uPetros, esithi, Suka uye emva kwam, Sathana, ngokuba ungazinyamekeli ezikaThixo izinto, unyamekela ezabantu.', 'XHO22'),
(45, 8, 34, 'Esibizele kuye indimbane, kwanabafundi bakhe, wathi kubo, Osukuba ethanda ukundilandela, makazincame, awuthwale umnqamlezo wakhe, andilandele.', 'XHO22'),
(45, 8, 35, 'Kuba othe wathanda ukuwusindisa umphefumlo wakhe, wolahlekelwa nguwo; ke yena othe wawulahla umphefumlo wakhe ngenxa yam, nangenxa yeendaba ezilungileyo ezi, wowusindisa lowo.', 'XHO22'),
(45, 8, 36, 'Kuba komnceda ntoni na umntu, ukuba uthe walizuza ihlabathi liphela, waza wonakalelwa ke ngumphefumlo wakhe?', 'XHO22'),
(45, 8, 37, 'Okanye worhola ntoni na umntu, ibe sisananelo somphefumlo wakhe?', 'XHO22'),
(45, 8, 38, 'Othe waneentloni ngam nangamazwi am, kwesi sizukulwana sikrexezayo, sonayo, uNyana woMntu woba neentloni ngaye lowo, xa athe weza esebuqaqawulini boYise, enezithunywa ezingcwele.', 'XHO22'),
(45, 9, 1, 'Wayesithi kubo, Inene ndithi kuni, Kukho abathile kwabemiyo apha, abangayi kukha beve kufa bona, bade babubone ubukumkani bukaThixo busiza ngamandla.', 'XHO22'),
(45, 9, 2, 'uYesu ke, emveni kweentsuku ezintandathu, athabathe uPetros noYakobi noYohane, abanyuse baye entabeni ende ngasese, bebodwa.', 'XHO22'),
(45, 9, 3, 'Zabengezela iingubo zakhe, zamhlophe njengekhephu; njengokuba kungekho mcoci weempahla emhlabeni unako ukuba mhlophe.', 'XHO22'),
(45, 9, 4, 'Kwabonakala kubo uEliya enoMoses; baye bethetha noYesu.', 'XHO22'),
(45, 9, 5, 'Waphendula ke uPetros wathi kuYesu, Rabhi, kuhle ukuba sibe lapha; masenze iminquba ibe mithathu, omnye ube ngowakho; omnye ube ngowakho, omnye ube ngokaMoses, omnye ube ngokaEliya.', 'XHO22'),
(45, 9, 6, 'Kuba ubengayazi into abengayithethayo; ngokuba bebesoyika kunene.', 'XHO22'),
(45, 9, 7, 'Kwabakho ilifu elibenzela ithinzi, kweza nezwi liphuma efini apho, lisithi, Lo nguNyana wam oyintanda; mveni yena.', 'XHO22'),
(45, 9, 8, 'Basingasinga, ababi sabona mntu, yanguYesu yedwa enabo.', 'XHO22'),
(45, 9, 9, 'Bakubon’ ukuba bayehla entabeni apho, wabayala ukuba bangazityeli bani ezo zinto bazibonileyo, ade avuke kwabafileyo uNyana woMntu.', 'XHO22'),
(45, 9, 10, 'Elo zwi balibamba lakubo bodwa bebuzana ukuba ukuvuka kubo abafileyo kukuthini na.', 'XHO22'),
(45, 9, 11, 'Bambuza besithi, Yini na ukuba ababhali bathi, uEliya umelwe kukufika tanci?', 'XHO22'),
(45, 9, 12, 'Uphendule ke wathi kubo, Okunene uEliya, efike tanci, wobuya azimise zonke izinto; kwaba njani na kubhalwe ngaye uNyana woMntu, ukuba umelwe kukuthi eve ubunzima obukhulu, enziwe into engento?', 'XHO22'),
(45, 9, 13, 'Ke ndithi kuni, ufikile uEliya, baza benza kuye konke abakuthandayo, kwanjengoko kubhaliweyo ngaye.', 'XHO22'),
(45, 9, 14, 'Akufika kubafundi bakhe, wabona indimbane enkulu ibangqongileyo, nababhali bebuzana nabo.', 'XHO22'),
(45, 9, 15, 'Sakumbona sakhwankqiswa kwaoko sonke isihlwele, sabalekela kuye, sambulisa.', 'XHO22'),
(45, 9, 16, 'Wababuza ababhali, esithi, Nibuzana ntoni na nabo?', 'XHO22'),
(45, 9, 17, 'Waphendula omnye wasesihlweleni wathi, Mfundisi, bendizise unyana wam kuwe, enomoya ongathethiyo;', 'XHO22'),
(45, 9, 18, 'apho uthe wamqubula khona, uyamntlala, aze alephuze amagwebu, atshixize amazinyo akhe, asuke ome; ndithethe kubafundi bakho ukuba bawukhuphe; ababa nako.', 'XHO22'),
(45, 9, 19, 'Umphendule ke wathi, Sizukulwanandini singakholwayo, koda kube nini na ndinani? Koda kube nini na ndininyamezele? Mziseni kum.', 'XHO22'),
(45, 9, 20, 'Bamzisa kuye. Akumbona, wesuka kwaoko umoya, wambetha wee xhwenene; yawa emhlabeni, yaqikaqika, ilephuza amagwebu.', 'XHO22'),
(45, 9, 21, 'Wabuza kuyise, ukuba kumgama ungakanani na imhlile le nto? Wathi ke yena, Kwaesengumntwana.', 'XHO22'),
(45, 9, 22, 'Umphose futhi emlilweni, kwanasemanzini, ukuba umtshabalalise; kodwa ukuba unako ukwenza into, sisize, usikwe yimfesane sithi.', 'XHO22'),
(45, 9, 23, 'Wathi uYesu kuye, Ewe, ukuba unokukholwa! Zonke izinto zinokwenzeka kuye okholwayo.', 'XHO22'),
(45, 9, 24, 'Wakhala kwaoko uyise womntwana lowo, wathi eneenyembezi, Ndiyakholwa, Nkosi; kuncede ukungakholwa kwam.', 'XHO22'),
(45, 9, 25, 'Uthe ke uYesu, ebone ukuba isihlwele sibalekela ndaweni-nye, wawukhalimela umoya ongcolileyo, esithi kuwo, Moyandini ungathethiyo usithulu, mna ndiyakuthethela, phuma kuye, ungabi sangena kuye.', 'XHO22'),
(45, 9, 26, 'Wakhala umoya, wambetha wee xhwenene kunene, waphuma kuye; bada abaninzi bathi, Ufile.', 'XHO22'),
(45, 9, 27, 'uYesu ke wambamba ngesandla, wamphakamisa; wavuka.', 'XHO22'),
(45, 9, 28, 'Eye kungena endlwini, bambuza abafundi bakhe ngasese, besithi, Yayiyini na ukuba singabi nako ukuwukhupha thina?', 'XHO22'),
(45, 9, 29, 'Wathi kubo, Olu hlobo alunako ukuphuma nganto, kungengako ukuthandaza nokuzila ukudla.', 'XHO22'),
(45, 9, 30, 'Bephumile apho, babehamba becanda kwelaseGalili; wayengathandi ukuba kwazi bani oko;', 'XHO22'),
(45, 9, 31, 'kuba wayebafundisa abafundi bakhe, esithi kubo, uNyana woMntu unikelwa ezandleni zabantu; baya kumbulala; aze emveni kokuba ebulewe, abuye avuke ngomhla wesithathu.', 'XHO22'),
(45, 9, 32, 'Baye bengakuqondi oko kuthetha; baye besoyika nokumbuza.', 'XHO22'),
(45, 9, 33, 'Weza kufika eKapernahum. Akuba esekhaya, wayebabuza esithi, Nibe nicamanga ntoni na phakathi kwenu endleleni?', 'XHO22'),
(45, 9, 34, 'Bahlala bathi cwaka ke; kuba endleleni babebambene ngokuthi, ngubani na omkhulu phakathi kwabo.', 'XHO22'),
(45, 9, 35, 'Akuba ehleli phantsi, wababiza abalishumi elinababini, wathi kubo, Ukuba ubani uthanda ukuba ngowokuqala, woba ngowokugqibela kwabo bonke, nomlungiseleli wabo bonke.', 'XHO22'),
(45, 9, 36, 'Uthabathe umntwana, wammisa phakathi kwabo, wamwola, wathi kubo.', 'XHO22'),
(45, 9, 37, 'Othe wamkela noko amnye wabantwana abanje, egameni lam, wamkela mna; nothe wamkela mna, akamkeli mna, wamkela lowo wandithumayo.', 'XHO22'),
(45, 9, 38, 'uYohane wamphendula wathi: “Mfundisi, sabona umntu ekhupha iindimoni egameni lakho, kodwa samalela ke, kuba engasilandeli thina.', 'XHO22'),
(45, 9, 39, 'Wathi ke uYesu, Musani ukumalela; kuba akukho namnye uya kwenza umsebenzi wamandla egameni lam, aze abe nako ukuthetha kakubi ngam.', 'XHO22'),
(45, 9, 40, 'Kaloku ongachasiyo ungecala lethu.', 'XHO22'),
(45, 9, 41, 'Kuba othe waniseza nendebe le yamanzi egameni lam, ngokuba ningabakaKristu, inene ndithi kuni, akasayi kuphulukana nomvuzo wakhe.', 'XHO22'),
(45, 9, 42, 'Nothe wakhubekisa noko amnye waba bancinane bakholwayo kum, ngekumlungele kanye ukuba ubegangxwe ilitye lokusila entanyeni yakhe, waphoswa elwandle.', 'XHO22'),
(45, 9, 43, 'Nokuba isandla sakho siyakukhubekisa, sinqumle; kukulungele kanye ukuba ungene ebomini usisilima, kunokuba uthi, unezandla zozibini, uye esihogweni, emlilweni ongacimiyo,', 'XHO22'),
(45, 9, 44, 'apho kumpethu ingafiyo, kumlilo ungacimiyo.', 'XHO22'),
(45, 9, 45, 'Nokuba unyawo lwakho luya kukhubekisa, lunqumle; kukulungele kanye ukuba ungene ebomini usisiqhwala, kunokuba uthi, uneenyawo zombini, uphoswe esihogweni, emlilweni ongacimiyo,', 'XHO22'),
(45, 9, 46, 'apho kumpethu ingafiyo, kumlilo ungacimiyo.', 'XHO22'),
(45, 9, 47, 'Ukuba iliso lakho liya kukhubekisa, linyothule; kukulungele kanye ukungena ebukumkanini bukaThixo unaliso linye, kunokuba uthi, unamehlo amabini, uphoswe esihogweni somlilo,', 'XHO22'),
(45, 9, 48, 'apho kumpethu ingafiyo, kumlilo ungacimiyo.', 'XHO22'),
(45, 9, 49, 'Kuba bonke baya kugalelwa ityuwa ngomlilo, athi onke amadini agalelwe ityuwa ngetyuwa.', 'XHO22'),
(45, 9, 50, 'Ityuwa ilungile yona, kodwa ukuba ithe ityuwa ayaba nabutyuwa, noyivakalisa ngantoni na? Yibani netyuwa phakathi kwenu, nihlale ninoxolo omnye komnye.', 'XHO22'),
(45, 10, 1, 'Esukile apho, uze kungena emideni yakwaYuda, ecanda kweliphesheya kweYordan. Wabuya wabafundisa ngokwesiqhelo.', 'XHO22'),
(45, 10, 2, 'Beza kuye abaFarisi, bambuza ukuba kuvumelekile na ukuba indoda imale umfazi wayo? emlinga.', 'XHO22'),
(45, 10, 3, 'Waphendula ke wathi kubo, Waniwisela mthetho mni na uMoses?', 'XHO22'),
(45, 10, 4, 'Bathi ke bona, uMoses wakuvumela ukubhala incwadi yokwahlukana, nokumala.', 'XHO22'),
(45, 10, 5, 'Waphendula uYesu wathi kubo, Ngokuba lukhuni kwentliziyo yenu, wanibhalela loo mthetho;', 'XHO22'),
(45, 10, 6, 'Kodwa kwasekuqalekeni kwendalo, uThixo wabenza baba yindoda nenkazana.', 'XHO22'),
(45, 10, 7, 'Ngenxa yoko ke, umntu womshiya uyise nonina, anamathele emfazini wakhe;', 'XHO22'),
(45, 10, 8, 'baze abo babini babe nyama-nye. Ngako oko, abasebabini, banyama-nye.', 'XHO22'),
(45, 10, 9, 'Oko ke ngoko uThixo akumanyileyo, makungahlulwa mntu.', 'XHO22'),
(45, 10, 10, 'Endlwini abafundi bakhe babuya bambuza ngayo loo nto.', 'XHO22'),
(45, 10, 11, 'Athi kubo, Othe wamala umfazi wakhe, wazeka omnye, uyakrexeza kulowa.', 'XHO22'),
(45, 10, 12, 'Nokuba umfazi uthe wayala indoda yakhe, wendela kwenye, uyakrexeza.', 'XHO22'),
(45, 10, 13, 'Kwaye bezisa abantwana kuye ukuze abachukumise; basuka abafundi babakhalimela abo babazisayo.', 'XHO22'),
(45, 10, 14, 'Ke kaloku ekubonile oku uYesu, wacaphuka, wathi kubo, Bavumeleni abantwana beze kum, ningabaleli: ngokuba ubukumkani bukaThixo bubobabo banjalo.', 'XHO22'),
(45, 10, 15, 'Inene, ndithi kuni, Othe akabamkela ubukumkani bukaThixo njengomntwana, akayi kukha angene kubo.', 'XHO22'),
(45, 10, 16, 'Waza wabawola, wabeka izandla phezu kwabo, wabasikelela.', 'XHO22'),
(45, 10, 17, 'Ephumile ke, uya endleleni, kweza othile ngokubaleka, wawa ngamadolo kuye, wambuza esithi, Mfundisi ulungileyo, ndenze ntoni na, ukuze ndibudle ilifa ubomi obungunaphakade?', 'XHO22'),
(45, 10, 18, 'Uthe ke uYesu kuye, Yini na ukuba uthi ndilungile? Akukho ulungileyo, mnye kwaphela, nguThixo.', 'XHO22'),
(45, 10, 19, 'Uyayazi imithetho: musa ukukrexeza, musa ukubulala, musa ukuba, musa ukungqina ubuxoki, musa ukulumkisa, beka uyihlo nonyoko.', 'XHO22'),
(45, 10, 20, 'Uphendule ke wathi kuye, Mfundisi, zonke ezo zinto ndazigcina, kwasebuncinaneni bam.', 'XHO22'),
(45, 10, 21, 'Uthe ke uYesu, ondele kuye wamthanda, wathi kuye, Kukho nto-nye uyisweleyo: hamba uye kuthengisa ngeento zonke onazo, uphe amahlwempu, woba nobutyebi ke emazulwini; yiza, uwuthabathe umnqamlezo, uwulandele, mna.', 'XHO22'),
(45, 10, 22, 'Wathi ke khunubembe ngelo zwi, wemka ebuhlungu; kuba ebenemfuyo eninzi.', 'XHO22'),
(45, 10, 23, 'uYesu esingasingile, wathi kubafundi bakhe, Hayi, ukuba ngenkankulu ukuya kungena kwabo banobutyebi ebukumkanini bukaThixo!', 'XHO22'),
(45, 10, 24, 'Bakhwankqiswa ke abafundi ngamazwi akhe. Wabuya waphendula ke uYesu, wathi kubo, Bantwana, hayi, ukuba ngenkankulu ukuthi abo bakholose ngobutyebi bangene ebukumkanini bukaThixo!', 'XHO22'),
(45, 10, 25, 'Kulula ukuba inkamela iphumele entunjeni yenaliti, kunokuba isityebi singene ebukumkanini bukaThixo.', 'XHO22'),
(45, 10, 26, 'Baye bekhwankqiswa ke ngokuncamisileyo, besitshono ukuthi, Ngubani na ongasindiswayo?', 'XHO22'),
(45, 10, 27, 'Ondele ke uYesu, wathi kubo, Kubantu akunakwenzeka; kuye yena uThixo akunakuba kungenzeki; kuba zonke izinto zinako ukwenzeka kuye uThixo.', 'XHO22'),
(45, 10, 28, 'Waqala uPetros ukuthi kuye, Uyabona, thina sishiye iinto zonke sakulandela.', 'XHO22'),
(45, 10, 29, 'Waphendula uYesu wathi, Inene, ndithi kuni, Akukho namnye ushiye indlu, nokuba ngabazalwana, nokuba ngoodade, nokuba nguyise, nokuba ngunina, nokuba ngumfazi, nokuba ngabantwana, nokuba ngamasimi, ngenxa yam nangenxa yeendaba ezilungileyo ezi,', 'XHO22'),
(45, 10, 30, 'ongayi kuthi amkeliswe kalikhulu: ngoku ngeli xesha, izindlu, nabazalwana, noodade, noonina, nabantwana, namasimi, ndawonye neentshutshiso; nakwelizayo iphakade, ubomi obungunaphakade.', 'XHO22'),
(45, 10, 31, 'Ke baninzi abokuqala abaya kuba ngabokugqibela; nabokugqibela abokuqala.', 'XHO22'),
(45, 10, 32, 'Ke kaloku babesendleleni, benyuka besiya eYerusalem; waye uYesu ehamba phambi kwabo. Bathi nqa; Bakulandela ke boyika. Wabuya wabathabatha abalishumi elinababini waqala ukubaxelela izinto eziza kumhlela,', 'XHO22'),
(45, 10, 33, 'besithi, Niyabona, siyenyuka, siya eYerusalem; waye uNyana woMntu eya kunikelwa kubabingeleli abakhulu, nakubabhali; baya kumgweba ukuba afe, bamnikele kuzo iintlanga;', 'XHO22'),
(45, 10, 34, 'Ziya kudlala ngaye, zimtyakatye, zimtshicele, zimbulale, athi ngomhla wesithathu abuye avuke.', 'XHO22'),
(45, 10, 35, 'Kuthi kuze kuye uYakobi noYohane, oonyana bakaZebhedi, besithi, Mfundisi, sithanda ukuba usenzele into esothi siyicele.', 'XHO22'),
(45, 10, 36, 'Wathi ke yena kubo, Nithanda ukuba ndinenzele ntoni na?', 'XHO22'),
(45, 10, 37, 'Bathe ke bona kuye, Siphe ukuhlala, omnye ngasekunene kwakho, nomnye ngasekhohlo kwakho, ebuqaqawulini bakho.', 'XHO22'),
(45, 10, 38, 'Wathi ke uYesu kubo, Aniyazi into eniyicelayo; ninako na ukuyisela indebe endiyiselayo mna? nibhaptizwe ngobhaptizo endibhaptizwa ngalo mna?', 'XHO22'),
(45, 10, 39, 'Bathi ke bona kuye, Sinako. Wathi ke uYesu kubo, Okunene indebe endiyiselayo mna, niya kuyisela; nobhaptizo endibhaptizwa ngalo mna, niya kubhaptizwa kwangalo;', 'XHO22'),
(45, 10, 40, 'Kodwa kona ukuhlala ngasekunene kwam nangasekhohlo kwam, asikwam ukupha ngako; kodwa kuya kunikwa abo kulungiselwe bona.', 'XHO22'),
(45, 10, 41, 'Bevile abalishumi, baqala ukubacaphukela ooYakobi noYohane.', 'XHO22'),
(45, 10, 42, 'Ke kaloku, uYesu ebabizele kuye, uthi kubo, Niyazi ukuba abo kuthiwa balawula iintlanga, banobukhosi kuzo; nabo bazizikhulu zazo benza ngegunya kuzo.', 'XHO22'),
(45, 10, 43, 'Ze kungabi njalo ke phakathi kwenu; osukuba enga angaba mkhulu phakathi kwenu, makabe ngumlungiseleli wenu;', 'XHO22'),
(45, 10, 44, 'nosukuba efuna ukuba ngowokuqala, makabe ngumkhonzi wabo bonke;', 'XHO22'),
(45, 10, 45, 'Kuba noNyana woMntu engezanga kulungiselelwa; weza kulungiselela, nokuncama ubomi bakhe, bube yintlawulelo yokukhulula abaninzi.', 'XHO22'),
(45, 10, 46, 'Baza kufika eYeriko. Ekuphumeni kwakhe eYeriko apho, nabafundi bakhe nendimbane elingeneyo, unyana kaTimeyu, uBhartimeyu, imfama, waye ehleli ngasendleleni engqiba.', 'XHO22'),
(45, 10, 47, 'Wathi akuva ukuba nguYesu waseNazarete, waqala ukudanduluka wathi, Yesu nyana kaDavide, ndenzele inceba.', 'XHO22'),
(45, 10, 48, 'Baye abaninzi bemkhalimela ukuba athi tu; kwaba kokukhona ke wadandulukayo ngakumbi, esithi, Nyana kaDavide, ndenzele inceba.', 'XHO22'),
(45, 10, 49, 'Wema uYesu, wathi makabizwe. Bayibiza ke imfama, besithi kuyo, Yomelela, suk’ ume; uyakubiza.', 'XHO22'),
(45, 10, 50, 'Ithe ke yona, ilahle ingubo yayo, yesuka yeza kuYesu.', 'XHO22'),
(45, 10, 51, 'Aphendule athi kuyo uYesu, Uthanda ukuba ndikwenzele ntoni na? Yathi ke imfama kuye, Nkosi, ukuba ndibuye ndibone.', 'XHO22'),
(45, 10, 52, 'Wathi kuyo uYesu, Hamba uhambe; ukholo lwakho lukusindisile. Yabuya yabona kwaoko, yamlandela uYesu endleleni apho.', 'XHO22'),
(45, 11, 1, 'Ke kaloku, bakusondela eYerusalem, eBhetefage naseBhetani, eNtabeni yemiNquma, wesusa bababini kubafundi bakhe,', 'XHO22'),
(45, 11, 2, 'Athi kubo, Hambani niye emzaneni lo ukhangelene nani; nothi nakungena kuwo, nifumane ithole le-esile libotshelelwe, ekungazanga kuhlale namnye umntu phezu kwalo; mkhululeni nize naye.', 'XHO22'),
(45, 11, 3, 'Nokuba ubani uthe kuni, Yini na ukuba nikwenze oko? yithini iNkosi iyalifuna; wothi kwaoko alithumele apha.', 'XHO22'),
(45, 11, 4, 'Basuka ke baya, balifumana ithole libotshelelwe ngasemnyango ngaphandle, ekuhlanganeni kweendlela; bayamkhulula ke.', 'XHO22'),
(45, 11, 5, 'Baye abathile kwababemi khona besithi kubo, Nenza ntoni na, nilikhulula nje ithole?', 'XHO22'),
(45, 11, 6, 'Bathetha ke kubo njengoko uYesu ebebawisele umthetho ngako; baza babandulula.', 'XHO22'),
(45, 11, 7, 'Balizisa ithole kuYesu; baziphosa iingubo zabo phezu kwalo; wahlala phezu kwakhe.', 'XHO22'),
(45, 11, 8, 'Ke kaloku abaninzi bazandlala iingubo zabo endleleni; baye ke abanye bexhuzula amahlamvu emithini, bewandlala endleleni.', 'XHO22'),
(45, 11, 9, 'Baye abo babehamba ngaphambili, nabo babelandela, bedanduluka, besithi, Hosana; Makabongwe lowo uzayo egameni leNkosi.', 'XHO22'),
(45, 11, 10, 'Mabubongwe ubukumkani bukabawo wethu uDavide, obuzayo egameni leNkosi! Hosana enyangweni!', 'XHO22'),
(45, 11, 11, 'uYesu wangena eJerusalem, waya endlwini kaThixo, waza wasingasinga izinto zonke, kwaza kwakuhlwa, waphuma waya eBhetani nabalishumi elinababini.', 'XHO22'),
(45, 11, 12, 'Uthe ngengomso, bakubon’ ukuba baphumile eBhetani, walamba.', 'XHO22'),
(45, 11, 13, 'Wathi, ebone umkhiwane ukude, unamagqabi, waya kukhangela ukuba angafumana nto na kuwo. Akufika kuwo, akafumana nto, kwakuphela ngamagqabi; kuba belingekafiki ixesha lamakhiwane.', 'XHO22'),
(45, 11, 14, 'Waphendula uYesu wathi kuwo, Makungaze kudle mntu siqhamo kuwe naphakade. Beva abafundi bakhe.', 'XHO22'),
(45, 11, 15, 'Bakufika eJerusalem, waza uYesu wangena endlwini kaThixo, wabakhupha ababethengisa nababethenga endlwini kaThixo, wazibhukuqa neetafile zabatshintshi bemali, nezihlalo zabathengisa ngamahobe.', 'XHO22'),
(45, 11, 16, 'Wayengavumi ukuba kubekho ucanda etempileni ethwele isitya.', 'XHO22'),
(45, 11, 17, 'Wayefundisa, esithi kubo, Akubhaliwe na kwathiwa, Indlu yam iya kubizwa ngokuba yindlu yokuthandaza iintlanga zonke? Ke nina niyenze umqolomba wezihange.', 'XHO22'),
(45, 11, 18, 'Beva ababhali nababingeleli abakhulu; baye befuna ukuba bangathini na ukumtshabalalisa; kuba babemoyika yena, ngokuba indimbane yonke ibikhwankqisiwe yimfundiso yakhe.', 'XHO22'),
(45, 11, 19, 'Kwathi kwakuhlwa, waphuma waya ngaphandle komzi.', 'XHO22'),
(45, 11, 20, 'Kwakusa, xa babedlula, bawubona umkhiwane womile ukususela ezingcanjini.', 'XHO22'),
(45, 11, 21, 'Ekhumbule uPetros, wathi kuye, Rabhi, khangela, umkhiwane owuqalekisileyo womile.', 'XHO22'),
(45, 11, 22, 'Uphendule ke uYesu wathi kubo, Yibani nokholo kuThixo.', 'XHO22'),
(45, 11, 23, 'Inene ndithi kuni, Othe kule ntaba, Funquleka, uphoseke elwandle; engathandabuzi entliziyweni yakhe, asuke akholwe ukuba ezo zinto azithethayo zinokwenzeka; woba nako oko akuthethayo.', 'XHO22'),
(45, 11, 24, 'Ngenxa yoko ndithi kuni izinto enisukuba nizicela, nithandaza, kholwani ukuba ninokuzamkela; noba nazo.', 'XHO22'),
(45, 11, 25, 'Naxa sukuba nimi nithandaza, ukuba ninendawo ngakothile, mxoleleni, ukuze noYihlo osemazulwini anixolele iziphoso zenu.', 'XHO22'),
(45, 11, 26, 'Ke, ukuba anixoleli nina, naye uYihlo osemazulwini akasayi kunixolela iziphoso zenu.', 'XHO22'),
(45, 11, 27, 'Babuya beza eYerusalem; kuthi, akubon’ ukuba uyahamba etempileni, kufike kuye ababingeleli abakhulu, nababhali, namadoda amakhulu;', 'XHO22'),
(45, 11, 28, 'bathi kuye, Uzenza ezi zinto ngagunya lini na? Ngubani na okunike eli gunya lokwenza ezi zinto?', 'XHO22'),
(45, 11, 29, 'Waphendula ke uYesu wathi kubo, Nam ndiya kunibuza zwi-nye, ze nindiphendule; ndonixelela ke, ukuba ndizenza ngagunya lini na ezi zinto.', 'XHO22'),
(45, 11, 30, 'Ubhaptizo lukaYohane lwaluphuma emazulwini, lwaluphuma ebantwini, sini na? ndiphendule.', 'XHO22'),
(45, 11, 31, 'Bacamanga ke bona bodwa, besithi, Ukuba sithe, Lwaluphuma emazulwini; Wothi, Ibiyini na phofu ukuba ningakholwa nguye?', 'XHO22'),
(45, 11, 32, 'Ukuba ke sithe, Lwaphuma ebantwini; baye besoyika abantu; kuba bonke babebambe ukuthi uYohane ubengumprofeti inene.', 'XHO22'),
(45, 11, 33, 'Baphendula bathi kuYesu, Asazi. Waphendula uYesu wathi kubo, Nam ke andinixeleli ukuba ndizenza ngagunya lini na ezi zinto.', 'XHO22'),
(45, 12, 1, 'Waza waqala ukuthetha nabo ngemizekeliso. Ubani watyala isidiliya, wasibiyela ngothango, wemba isixovulelo seediliya, wakha inyango, waqeshisa ngaso kubalimi, wahambela kwelinye.', 'XHO22'),
(45, 12, 2, 'Wathi ngelo xesha, wathuma umkhonzi kubalimi abo, ukuba amkele kubalimi esiqhameni sesidiliya eso.', 'XHO22'),
(45, 12, 3, 'Basuka bona bambamba, bamtyabula, bamndulula elambatha.', 'XHO22'),
(45, 12, 4, 'Wabuya wathuma kubo omnye umkhonzi; lowo bamgibisela ngamatye, bamngxwelerha entloko, bamndulula ehlazekile.', 'XHO22'),
(45, 12, 5, 'Wabuya wathuma omnye; naye bambulala, nabanye abaninzi; inxenye beyityabula, inxenye beyibulala.', 'XHO22'),
(45, 12, 6, 'Uthe ngoko, esenaye unyana emnye, intanda yakhe, wamthuma naye kubo ukugqibela, esithi, Baya kumhlonela yena unyana wam.', 'XHO22'),
(45, 12, 7, 'Kodwa abo balimi bathetha bodwa besithi, Yindlalifa le; yizani simbulale, libe lelethu ilifa.', 'XHO22'),
(45, 12, 8, 'Bamthabatha ke, bambulala, bamkhuphela ngaphandle kwesidiliya.', 'XHO22'),
(45, 12, 9, 'Uya kwenza ntoni na ke ngoko umninisidiliya? Uya kufika abatshabalalise abo balimi, asinikele kubambi isidiliya eso.', 'XHO22'),
(45, 12, 10, 'Anisilesanga na esi sibhalo; Ilitye abalicekisayo abakhi, Lelona laba yintloko yembombo;', 'XHO22'),
(45, 12, 11, 'Le nto yenziwe yiNkosi, yaye iyamangalisa emehlweni ethu?', 'XHO22'),
(45, 12, 12, 'Baza bafuna ukumbamba, basuka boyika izihlwele, kuba baqonda ukuba uwuthethe ngabo lo mzekeliso. Bamshiya bemka.', 'XHO22'),
(45, 12, 13, 'Bathumela kuye abathile bakubaFarisi bakumaHerode, ukuze bambambise ngokuthetha kwakhe.', 'XHO22'),
(45, 12, 14, 'Befikile ke bona, bathi kuye, Mfundisi, siyazi ukuba uyinene, kanjalo akukhathali nangubani; kuba akukhethi buso bamntu, indlela kaThixo ufundisa ngayo ngenyaniso, kuKesare, akunjalo?', 'XHO22'),
(45, 12, 15, 'Sirhole, okanye masingaphi? Ke yena, ekwazi ukuhanahanisa kwabo, wathi kubo, Nindilingelani na? Ndiziseleni idenariyo, ndiyibone.', 'XHO22'),
(45, 12, 16, 'Bayizisa. Athi kubo, Ngokabani na lo mfanekiso nombhalo? Bathi ke bona kuye, NgokaKesare.', 'XHO22'),
(45, 12, 17, 'Waphendula uYesu wathi kubo, Nikelani kaloku izinto zikaKesare kuKesare, nezinto zikaThixo kuThixo. Baye bemangaliswa nguye.', 'XHO22'),
(45, 12, 18, 'Kwafika kuye nabaSadusi, bona abo bathi akukho luvuko; bambuza, besithi.', 'XHO22'),
(45, 12, 19, 'Mfundisi, uMosis wasibhalela wathi: “Ukuba ozalana nothile uthe wafa waza washiya umfazi wakhe waza akashiya bantwana, makathi umzalwana wakhe amzeke umfazi wakhe, amvelisele imbewu ozalana naye.', 'XHO22'),
(45, 12, 20, 'Ke bekukho abantu abasixhenxe bezalana; waza owokuqala wazeka umfazi, wafa akashiya mbewu.', 'XHO22'),
(45, 12, 21, 'Uthe ke owesibini wamzeka, wafa, akashiya mbewu; nowesithathu wenjenjalo.', 'XHO22'),
(45, 12, 22, 'Bamzeka ke bosixhenxe; abashiya mbewu. Ekugqibeleni kwabo bonke, wafa naye umfazi.', 'XHO22'),
(45, 12, 23, 'Ngoko eluvukweni, uya kuba ngumfazi wawuphi na kubo? kuba bosixhenxe babenaye engumfazi.', 'XHO22'),
(45, 12, 24, 'Waphendula uYesu wathi kubo, Anilahleki na, ngenxa yokuba ningazazi izibhalo kwanamandla kaThixo?', 'XHO22'),
(45, 12, 25, 'Kuba, xa bathe babuya bavuka kwabafileyo, abazeki, abendiswa; banjengezithunywa ezisemazulwini.', 'XHO22'),
(45, 12, 26, 'Ngokusingisele kwabafileyo, ukuba baya kuvuka, anilesanga na encwadini kaMoses, ethetha ngetyholo, okokuba wathi uThixo kuye, Mna ndinguThixo ka-Abraham, uThixo kaIsake, uThixo kaYakobi?', 'XHO22'),
(45, 12, 27, 'AkaThixo wabafileyo, unguThixo wabaphilileyo. Nina ke ngoko niyalahleka kakhulu.', 'XHO22'),
(45, 12, 28, 'Kwafika omnye wakubabhali; wabeva bebuzana; waqonda ukuba ubaphendule kakuhle. Wambuza esithi, Owona mthetho uphambili kuyo yonke nguwuphi na?', 'XHO22'),
(45, 12, 29, 'Wamphendula uYesu wathi, Owona ungaphambili kuyo yonke imithetho ngulo: Yiva, Sirayeli; iNkosi uThixo wethu yiNkosi enye;', 'XHO22'),
(45, 12, 30, 'uze uyithande ke iNkosi uThixo wakho ngentliziyo yakho iphela, nangomphefumlo wakho uphela, nangengqondo yakho iphela, nangamandla akho ephela. Nguwo lo wokuqala umthetho.', 'XHO22'),
(45, 12, 31, 'Owesibini ke ufana nawo: Uze umthande ummelwane wakho ngoko uzithanda ngako. Akukho mthetho wumbi mkhulu kunale.', 'XHO22'),
(45, 12, 32, 'Wathi umbhali kuye, Ngxatsho ke, Mfundisi, uthethe inyaniso; ngokuba mnye uThixo; akukho wumbi, nguye yedwa;', 'XHO22'),
(45, 12, 33, 'nokuthi umthande yena ngentliziyo iphela, nangengqiqo iphela, nangomphefumlo uphela, nangamandla ephela, nokuthi umthande ummelwane wakho njengoko uzithanda ngako, kungaphezu kwawo onke amadini atshiswayo kuphele, kwanemibingelelo.', 'XHO22'),
(45, 12, 34, 'uYesu embonile ukuba uphendule ngengqiqo, wathi kuye, Akukude wena ebukumkanini bukaThixo. Akwabakho namnye waba nabuganga bakumbuza nto.', 'XHO22'),
(45, 12, 35, 'Waphendula uYesu, efundisa etempileni, wathi, Batsho ngani na ababhali ukuthi, uKristu ngunyana kaDavide?', 'XHO22'),
(45, 12, 36, 'Kuba uDavide ngokwakhe wathi ngoMoya oyiNgcwele, Yathi iNkosi eNkosini yam, Hlala ngasekunene kwam, Ndide iintshaba zakho ndizenze isihlalo seenyawo zakho.', 'XHO22'),
(45, 12, 37, 'uDavide ngoko ngokwakhe umbiza ngokuthi, Nkosi; Kuvela phi na ke ukuba ngunyana wakhe? Yaye indimbane yamva ngovuyo.', 'XHO22'),
(45, 12, 38, 'Wayesithi kubo ekufundiseni kwakhe, Balumkeleni ababhali, abathanda ukuhamba ngeengubo ezinde, nemibuliso ezindaweni zembutho,', 'XHO22'),
(45, 12, 39, 'nezihlalo eziphambili ezindlwini zesikhungu, neendawo eziphambili ezidlweni;', 'XHO22'),
(45, 12, 40, 'abadla izindlu zabahlolokazi, baze ngokunyhwalaza bathandaze uzungu; abo baya kwamkela ugwebo olugqithiseleyo.', 'XHO22'),
(45, 12, 41, 'uYesu wayehleli malungana nomkhumbi wokulondoloza imali, ekhangela indlela abantu abaphosa ngayo imali emkhumbini wokulondoloza imali; zaye izityebi ezininzi ziphosa kakhulu.', 'XHO22'),
(45, 12, 42, 'Kwafika mhlolokazi uthile ulihlwempu, waphosa iimalanana zambini eziyikodrante.', 'XHO22'),
(45, 12, 43, 'Ebabizele kuye abafundi bakhe, wathi kubo, Inene ndithi kuni, Lo mhlolokazi ulihlwempu uphose ngaphezu kwabo bonke abaphosileyo emkhumbini wokulondoloza imali;', 'XHO22'),
(45, 12, 44, 'kuba bonke baphose bethabatha kwikhupha labo; ukanti yena ekusweleni kwakhe uphose yonke into abenayo, impilo yakhe yonke.', 'XHO22'),
(45, 13, 1, 'Kwakubon’ ukuba uyaphuma etempileni, uthi omnye umfundi wakhe kuye, Mfundisi, uyawabona na la matye angaka, nezi zakhiwo zingaka?', 'XHO22'),
(45, 13, 2, 'Waphendula uYesu wathi kuye, Uyazibona na ezi zakhiwo zikhulu? Akusayi kusala litye phezu kwelitye, elingayi kuchithwa.', 'XHO22'),
(45, 13, 3, 'Ke kaloku, akubon’ ukuba uhleli eNtabeni yemiNquma, malunga netempile, bambuza ngasese uPetros, noYakobi, noYohane, noAndreya, besithi,', 'XHO22'),
(45, 13, 4, 'Sixelele ukuba ziya kubakho nini na ezo zinto? uya kuba yini na umqondiso, xa zonke ezo zinto ziya kuzaliseka?', 'XHO22'),
(45, 13, 5, 'Wabaphendula uYesu waqala ukuthi, Lumkani ningalahlekiswa mntu;', 'XHO22'),
(45, 13, 6, 'Kuba baninzi abaya kuza egameni lam, besithi, banguye uKristu; balahlekise abantu abaninzi.', 'XHO22'),
(45, 13, 7, 'Xa nithe neva iimfazwe namarhe eemfazwe, musani kuba navuso; kanti kona ukuphela akukabikho.', 'XHO22'),
(45, 13, 8, 'Kuba uhlanga luya kuvukelana nohlanga, nobukumkani buvukelane nobukumkani; kuya kubakho iinyikima zomhlaba kwiindawo ngeendawo; kuya kubakho neendlala neenkathazeko; ziingqaleko ke zenimba ezo.', 'XHO22'),
(45, 13, 9, 'Zilumkeleni ke okwenu; kuba baya kuninikela ezintlanganisweni zamatyala; Niya kubethwa ezindlwini zesikhungu, nimiswe phambi kwabalawuli nookumkani ngenxa yam, kube bubungqina kubo.', 'XHO22'),
(45, 13, 10, 'Kumele ukuthi ke ukuqala kuvakaliswe iindaba ezilungileyo ezi kuzo zonke iintlanga.', 'XHO22'),
(45, 13, 11, 'Xa sukuba ke benirhola, beninikela, musani ukuyixhalela kwangaphambili into eniya kuyithetha, ningacamangi nokucamanga; uMoya oyiNgcwele.', 'XHO22'),
(45, 13, 12, 'Ke kaloku umzalwana uya kunikela umzalwana ekufeni, aze uyise anikele unyana; bathi nabantwana bavukelane nabazali, bababulalise;', 'XHO22'),
(45, 13, 13, 'niya kuthiywa ngabantu bonke ngenxa yegama lam; kodwa onyamezeleyo kwada kwaba sekupheleni, lowo uya kusindiswa.', 'XHO22'),
(45, 13, 14, 'Ke kaloku, xa nithe nalibona isikizi lesiphanziso, ekwakuthethwa lona ngoDaniyeli umprofeti, limi apho lingamele ukuba likhona (umfundi makaqiqe), ngelo xesha mabathi abo bakwelakwaYuda basabele ezintabeni;', 'XHO22'),
(45, 13, 15, 'Ophezu kwendlu ke makangehli aye endlwini, angangeni aye kuthabatha into endlwini yakhe;', 'XHO22'),
(45, 13, 16, 'Nalowo usentsimini makangabuyeli emva, aye kuthabatha ingubo yakhe.', 'XHO22'),
(45, 13, 17, 'Yeha ke, abamithiyo nabanyisayo ngaloo mihla!', 'XHO22'),
(45, 13, 18, 'Thandazani ke, ukuba intsabo yenu ingabi sebusika.', 'XHO22'),
(45, 13, 19, 'Kuba yona loo mihla iya kuba yimbandezelo, engazanga ibekho enjalo kwasekuqalekeni kwendalo awayidalayo uThixo, kwada kwakalokunje;', 'XHO22'),
(45, 13, 20, 'Kanjalo ukuba iNkosi ibingayinqamlelanga loo mihla, bekungayi kubakho mntu usindayo; koko ithe ngenxa yabanyulwa ebanyulileyo, yayinqamlela imihla leyo.', 'XHO22'),
(45, 13, 21, 'Ngelo xa ke, ukuba ubani uthe kuni, Nanku apha uKristu; nanko ekho; musani ukukholwa nguye;', 'XHO22'),
(45, 13, 22, 'Kuba kuya kuvela ooKristu ababuxoki, nabaprofeti ababuxoki, benze imiqondiso nemimangaliso yokulahlekisa nabanyuliweyo, ukuba bekunokwenzeka.', 'XHO22'),
(45, 13, 23, 'Lumkani ke nina; yabonani, ndinixelele izinto zonke ngenxa engaphambili.', 'XHO22'),
(45, 13, 24, 'Ngaloo mihla, emva kwaloo mbandezelo, ilanga liya kwenziwa mnyama, ithi nenyanga ingabi mhlophe ukukhanya kwayo;', 'XHO22'),
(45, 13, 25, 'Iinkwenkwezi zezulu ziya kuwa, athi namandla asezulwini azanyazanyiswe.', 'XHO22'),
(45, 13, 26, 'Baya kumbona ke ngelo xesha uNyana woMntu esiza esemafini, enamandla amakhulu nobuqaqawuli.', 'XHO22'),
(45, 13, 27, 'Uya kuzithuma ngelo xesha izithunywa zakhe, abahlanganisele ndawonye abanyulwa bakhe, baphume kumacala omane omoya, kuthabathele esiphelweni somhlaba, kuse esiphelweni sezulu.', 'XHO22'),
(45, 13, 28, 'Fundani kuwo umkhiwane umzekeliso; xa selithe lathamba isebe lawo, lahluma amagqabi, niyazi ukuba likufuphi ihlobo.', 'XHO22'),
(45, 13, 29, 'Ngokukwanjalo nani, xa nithe nazibona ezo zinto zisihla, yazini ukuba kukufuphi, sekusemnyango.', 'XHO22'),
(45, 13, 30, 'Inene ndithi kuni, Asisayi kudlula esi sizukulwana, zingadanga zonke ezo zinto zibekho.', 'XHO22'),
(45, 13, 31, 'Liya kudlula izulu nehlabathi, kodwa amazwi am akayi kudlula.', 'XHO22'),
(45, 13, 32, 'Kodwa akukho bani uwaziyo loo mhla nelo lixa, azazi nezithunywa ezisemazulwini, akazi noNyana lo, kuphela nguYise.', 'XHO22'),
(45, 13, 33, 'Lumkani, hlalani nilindile, nithandaze; ngokuba anilazi ukuba linini na ixesha elo;', 'XHO22'),
(45, 13, 34, 'njengokuba wathi umntu ehambela kwelinye, eshiya indlu yakhe, wabanika igunya abakhonzi bakhe, elowo wamnika umsebenzi wakhe, waza umgcini-mnyango wamwisela umthetho ukuba alinde.', 'XHO22'),
(45, 13, 35, 'Phaphani ngoko, ngokuba ningakwazi ukuba kunini na ukuza komninindlu: kuko ngokuhlwa, kuko phakathi kobusuku, kuko ekulileni kweenkuku, kuko kusasa;', 'XHO22'),
(45, 13, 36, 'hleze afike ngebhaqo, anifumane nilele.', 'XHO22'),
(45, 13, 37, 'Oko ndikutsho kuni, ndikutsho kubo bonke, Lindani.', 'XHO22'),
(45, 14, 1, 'Ke kaloku bekuza kubakho ipasika, izonka ezingenagwele, emveni kweentsuku ezimbini; baye ke ababingeleli abakhulu nababhali befuna ukuba bangathini na ukumbamba ngenkohliso, bambulale.', 'XHO22'),
(45, 14, 2, 'Babesithi ke, Kungabi ngomthendeleko, hleze kubekho isaqunge ebantwini.', 'XHO22'),
(45, 14, 3, 'Kuthe eseBhetani, endlwini kaSimon owayeneqhenqa, akubon’ ukuba uhleli, kweza umfazi enehlala lelabhastile, linamafutha enadusi engangxengelelweyo, exabiso likhulu; walityumza iselwa, wamgalela entloko.', 'XHO22'),
(45, 14, 4, 'Kwaye ke kukho abathile abacaphukayo phakathi kwabo, besithi, Yenzelweni na le nkcitho yamafutha aqholiweyo?', 'XHO22'),
(45, 14, 5, 'Kuba la mafutha bekungathengiswa ngawo ngeedenariyo ezingaphezu kwamakhulu amathathu, kuphiwe amahlwempu. Baye bamkrokrela.', 'XHO22'),
(45, 14, 6, 'Uthe ke uYesu, Myekeni; yini na ukuba nimkhathaze? Undenzele umsebenzi omhle.', 'XHO22'),
(45, 14, 7, 'Kuba amahlwempu lawo nihleli ninawo, nize xa sukuba nithanda, nibe nako ukuwenzela okulungileyo; ke mna anihleli ninam.', 'XHO22'),
(45, 14, 8, 'Yena ukwenzile abenako; uphange weza wawuthambisela ukungcwatywa umzimba wam.', 'XHO22'),
(45, 14, 9, 'Inene ndithi kuni, Apho zithe zavakaliswa khona ezi ndaba zilungileyo kulo lonke ihlabathi, iya kuxelwa nayo le nto ayenzileyo yena, ibe sisikhumbuzo ngaye.', 'XHO22'),
(45, 14, 10, 'uYuda Sikariyoti, omnye kwabeshumi elinababini, wesuka waya kubabingeleli abakhulu, ukuba amnikele kubo.', 'XHO22'),
(45, 14, 11, 'Bakuva ke oko, bavuya, bamthembisa ngokumnika imali. Wayefuna ukuba angathini na ukumnikela ngexesha elimlungeleyo.', 'XHO22'),
(45, 14, 12, 'Ke kaloku, ngowokuqala umhla wezonka ezingenagwele, xa bekubingelelwa imvana yepasika, bathi kuye abafundi bakhe, Unga singaya kulungisa phi na, ukuze uyidle ipasika?', 'XHO22'),
(45, 14, 13, 'Ususa babini kubafundi bakhe, athi kubo, Hambani niye kuwo umzi; nohlangatyezwa ngumntu ethwele umphanda wamanzi; mlandeleni.', 'XHO22'),
(45, 14, 14, 'Xa athe angena, nothi kumninindlu, Uthi uMfundisi, Liphi na igumbi labahambi, apho ndingadlela khona ipasika nabafundi bam?', 'XHO22'),
(45, 14, 15, 'Yena wonibonisa igumbi eliphezulu, elikhulu, landlelwe lalungiswa; silungiseleleni apho.', 'XHO22'),
(45, 14, 16, 'Baphuma abafundi bakhe abo baya kungena kuwo umzi, bafumana kunjengoko wayebaxelele, bayilungisa ipasika.', 'XHO22'),
(45, 14, 17, 'Kwathi kwakuhlwa, eze nabalishumi elinababini.', 'XHO22'),
(45, 14, 18, 'Bakubon’ ukuba bahleli, bayadla, wathi uYesu, Inene, ndithi kuni, omnye kuni aba uya kundingcatsha, lowo ke udla nam.', 'XHO22'),
(45, 14, 19, 'Baqala ke ukuba buhlungu, nokuthi kuye ngabanye, Ingaba ndim, yini na? Wathi omnye, Ingaba ndim na?', 'XHO22'),
(45, 14, 20, 'Uphendule ke wathi kubo, Ngomnye kwabeshumi elinababini, lowo ke uthi nkxu nam esityeni.', 'XHO22'),
(45, 14, 21, 'uNyana woMntu uyemka okunene, njengoko kubhalwe ngaye; yeha ke, loo mntu uNyana woMntu angcatshwa nguye! Ngekumlungele ukuba ebengazalwanga loo mntu.', 'XHO22'),
(45, 14, 22, 'Bakubon’ ukuba bayadla, uYesu wasithabatha isonka, wathi akusikelela, waqhekeza, wabanika, wathi, Thabathani nidle; ngumzimba wam lo.', 'XHO22'),
(45, 14, 23, 'Wayithabatha nendebe, wabulela, wabanika; basela kuyo bonke.', 'XHO22'),
(45, 14, 24, 'Wathi kubo, Ligazi lam eli lomnqophiso omtsha, eliphalala ngenxa yabaninzi.', 'XHO22'),
(45, 14, 25, 'Inene ndithi kuni, Andisayi kubuya ndisele esiqhameni somdiliya, kude kube yiloo mini sukuba ndisisela sisitsha ebukumkanini bukaThixo.', 'XHO22'),
(45, 14, 26, 'Bakuba bevume ingoma yokubonga, baphuma baya eNtabeni yemiNquma.', 'XHO22'),
(45, 14, 27, 'Athi kubo uYesu, Nonke niya kukhubeka ngam ngobu busuku; ngokuba kubhaliwe kwathiwa, Ndiya kubetha kumalusi, zichithwachithwe izimvu.', 'XHO22'),
(45, 14, 28, 'Ke emveni kokuvuka kwam, ndiya kunandulela, ndiye kwelaseGalili.', 'XHO22'),
(45, 14, 29, 'Wathi ke uPetros kuye, Nokuba baya kukhubeka bonke, mna nakanye.', 'XHO22'),
(45, 14, 30, 'Athi uYesu kuye, Inene ndithi kuwe, namhlanje, kwangobu busuku, ingekalili kabini inkuku, uya kundikhanyela kathathu.', 'XHO22'),
(45, 14, 31, 'Kwaba kukhona wathetha wancamisa, esithi, Ndingafanelana ndimelwe kukufa nawe, andingekhe ndikukhanyele. Kwangokunjalo batsho nabo bonke.', 'XHO22'),
(45, 14, 32, 'Bafika kumhlatyana ogama liyiGetsemane. Athi kubafundi bakhe, Hlalani phantsi apha, ndikhe ndithandaze;', 'XHO22'),
(45, 14, 33, 'Wathabatha uPetros noYakobi noYohane, waqala ukuba buhlungu nokudandatheka kakhulu.', 'XHO22'),
(45, 14, 34, 'Athi kubo, Umphefumlo wam ubuhlungu kakhulu, ngokokude ube sekufeni; hlalani apha nilinde.', 'XHO22'),
(45, 14, 35, 'Ehambele phambili umganyana, wawa emhlabeni, wathandazela, ukuba kunokwenzeka, lidlule kuye ilixa elo.', 'XHO22'),
(45, 14, 36, 'Wayesithi, Abha, Bawo, zonke izinto zinokwenzeka kuwe; yigqithise kum le ndebe; noko ke makungabi kokuthandwa ndim, makube kokuthandwa nguwe.', 'XHO22'),
(45, 14, 37, 'Uyeza, abafumane belele; athi kuPetros, Simon, ulele na? Ubungenakulinda na nelixa libe linye?', 'XHO22'),
(45, 14, 38, 'Lindani nithandaze, ukuze ningangeni ekuhendweni; umoya okunene unentumekelelo, ke yona inyama ithambile.', 'XHO22'),
(45, 14, 39, 'Wabuya wemka wathandaza, ethetha kwalona elo lizwi.', 'XHO22'),
(45, 14, 40, 'Uthe akubuya wabafumana bebuye balala, kuba amehlo abo ayenzima bubuthongo; baye bengazi into abangamphendula yona.', 'XHO22'),
(45, 14, 41, 'Ubuye okwesithathu, athi kubo, Lalani ngoku niphumle. Kwanele, ilixa lifikile; yabonani, uNyana woMntu uyanikelwa ezandleni zaboni.', 'XHO22'),
(45, 14, 42, 'Vukani, sihambe; yabonani, lowo undingcatshayo usondele.', 'XHO22'),
(45, 14, 43, 'Kwaoko, esathetha, uthi qatha uYuda, owayengoweshumi elinababini, enendimbane enkulu, inamakrele neentonga, isuka kubo ababingeleli abakhulu, nakubabhali, nakuwo amadoda amakhulu.', 'XHO22'),
(45, 14, 44, 'Ke kaloku lowo umngcatshayo ubebanike umqondiso, esithi, Endithe ndamanga, nguye; mbambeni, nimrhole nimnqandile.', 'XHO22'),
(45, 14, 45, 'Efikile, uye kuye kwaoko, athi, Rabhi, Rabhi; wamanga.', 'XHO22'),
(45, 14, 46, 'Baza bamsa izandla, bambamba.', 'XHO22'),
(45, 14, 47, 'Uthe ke omnye kwababemi khona, warhola ikrele, wamxabela umkhonzi wombingeleli omkhulu, wamnqamla indlebe.', 'XHO22'),
(45, 14, 48, 'Waphendula uYesu wathi kubo, Niphumele ukuza kubamba mna na, ninamakrele neentonga nje, nanga niphumele isihange?', 'XHO22'),
(45, 14, 49, 'Bendinani imihla ngemihla etempileni ndifundisa, anandibamba; kodwa kunje, ukuze zizaliseke izibhalo.', 'XHO22'),
(45, 14, 50, 'Bamshiya ke bonke, babaleka.', 'XHO22'),
(45, 14, 51, 'Walandelwa yindodana ethile, ithe yambu ilinen entle emzimbeni; abafana bambamba.', 'XHO22'),
(45, 14, 52, 'Ithe yayilahla ilinen entle leyo, yabaleka kubo ize.', 'XHO22'),
(45, 14, 53, 'Bamrholela uYesu kumbingeleli omkhulu, bahlanganisana kuye bonke ababingeleli abakhulu, namadoda amakhulu, nababhali.', 'XHO22'),
(45, 14, 54, 'Waza uPetros wamlandela, ebekele kude, wada waya ngaphakathi entendelezweni yendlu yombingeleli omkhulu; waye ehleli phantsi ndawonye namadindala, esotha umlilo.', 'XHO22'),
(45, 14, 55, 'Baye ke ababingeleli abakhulu, nayo intlanganiso yamatyala iphelele, befuna ubungqina ngaye uYesu bokuze bambulalise; akafumana nto.', 'XHO22'),
(45, 14, 56, 'Kuba babebaninzi ababengqina ubuxoki ngaye, baye ubungqina babo bungayelelani.', 'XHO22'),
(45, 14, 57, 'Besuka ke abathile, bengqina ubuxoki ngaye, besithi,', 'XHO22'),
(45, 14, 58, 'Thina samva yena esithi, Mna ndiya kuyichitha le tempile yenziweyo ngezandla, ndize ngeentsuku ezintathu ndakhe enye engenziwe ngazandla.', 'XHO22'),
(45, 14, 59, 'Baye bungayelelani kwangokunjalo nobabo ubungqina.', 'XHO22'),
(45, 14, 60, 'Wesuka wema phakathi umbingeleli omkhulu, wambuza uYesu, esithi, Akuphenduli nento le na? bangqina ntoni na aba ngawe?', 'XHO22'),
(45, 14, 61, 'Wahlala wathi tu, akaphendula. Wabuya umbingeleli omkhulu wambuza, wathi kuye, Unguye na uKristu, uNyana waLowo ungowokubongwa?', 'XHO22'),
(45, 14, 62, 'Uthe ke uYesu, Ndinguye; niya kumbona nokumbona uNyana woMntu, ehleli ngasekunene kwamandla, esiza esemafini ezulu.', 'XHO22'),
(45, 14, 63, 'Usuka ke umbingeleli omkhulu azikrazule iingubo zakhe, athi, Sisawafunelani na amangqina?', 'XHO22'),
(45, 14, 64, 'Nikuvile ukunyelisa; nithini na nina? Bamgweba ke bonke ukuba unetyala lokuba afe.', 'XHO22'),
(45, 14, 65, 'Yaqala inxenye ukumtshicela, nokumgqubuthela ubuso, nokumntlitha, nokuthi kuye, Profeta; aza amadindala ammbetha.', 'XHO22'),
(45, 14, 66, 'Kuthi, akubon’ ukuba uPetros usentendelezweni ezantsi, kweza esinye sezicakazana zombingeleli omkhulu.', 'XHO22'),
(45, 14, 67, 'Ithi yakumbona uPetros esotha, yamjonga, yathi, Nawe ubunaye uYesu waseNazarete.', 'XHO22'),
(45, 14, 68, 'Wakhanyela ke esithi, Andiyazi, andiyiqondi kanye into oyithethayo wena. Waphuma waya evarandeni; yalila inkuku.', 'XHO22'),
(45, 14, 69, 'Waphinda umkhonzazana wambona, waqala ukuthi kwababemi khona, Lo ngowakubo.', 'XHO22'),
(45, 14, 70, 'Waphinda wakhanyela. Kwakuba mzuzwana, babuya abo babemi khona bathi kuPetros, Inyaniso, ungowabo; kuba ungowaseGalili, nokuthetha kwakho kuyafana nokwabo.', 'XHO22'),
(45, 14, 71, 'Waqala ke ukushwabula nokufunga, esithi, Andimazi loo mntu nimthethayo.', 'XHO22'),
(45, 14, 72, 'Yalila okwesibini inkuku. Walikhumbula ke uPetros ilizwi, abelithethile uYesu kuye, lokuthi, Ingekalili kabini inkuku, uya kundikhanyela kathathu. Wathi akucinga, walila.', 'XHO22'),
(45, 15, 1, 'Bathi, kwaoko kwakusa, ababingeleli abakhulu benza ibhunga, ndawonye namadoda amakhulu, nababhali, nentlanganiso yamatyala iphelele; bambopha uYesu bemka naye, bamnikela kuPilato.', 'XHO22'),
(45, 15, 2, 'Wambuza ke uPilato, esithi, Wena unguye na uKumkani wamaYuda? Waphendula ke wathi kuye, Nawe utsho.', 'XHO22'),
(45, 15, 3, 'Bammangalela ke ngeendawo ezininzi ababingeleli abakhulu; kodwa yena akaphendulanga nento.', 'XHO22'),
(45, 15, 4, 'Wabuya ke uPilato wambuza, esithi, Akuphenduli nento le? Khangela ukuba zininzi kwazo iindawo abazingqina ngawe.', 'XHO22'),
(45, 15, 5, 'Kodwa uYesu akabanga saphendula nto; ngokokude amangaliswe uPilato.', 'XHO22'),
(45, 15, 6, 'Ke kaloku ngomthendeleko wayebakhululela umbanjwa abe mnye, abasukuba bemcela.', 'XHO22'),
(45, 15, 7, 'Kwaye kukho ke obekuthiwa nguBharabhas, ebotshwe ndawonye nabanye abenzi besaqunge, ababulalayo nokubulala esaqungeni apho.', 'XHO22'),
(45, 15, 8, 'Sankqangaza isihlwele, saqala ukucela, ukuba enze ngoko abehlala esenza ngako kubo.', 'XHO22'),
(45, 15, 9, 'Wabaphendula ke uPilato, esithi, Nithanda ukuba ndinikhululele ukumkani wamaYuda na?', 'XHO22'),
(45, 15, 10, 'Kuba ebesazi ukuba ababingeleli abakhulu abo bamnikele ngomona.', 'XHO22'),
(45, 15, 11, 'Ke bona ababingeleli abakhulu bayixhokonxa indimbane, ukuba icele ukuba kanye abakhululele uBharabhas.', 'XHO22'),
(45, 15, 12, 'Waphinda waphendula ke uPilato, wathi kubo, Ningamenza ntoni na ke ngoko kulowo nithi unguKumkani wamaYuda?', 'XHO22'),
(45, 15, 13, 'Babuya ke bankqangaza bathi, Mbethelele emnqamlezweni!', 'XHO22'),
(45, 15, 14, 'Wathi ke uPilato kubo, Kuba enze bubi buni na? Kwaba kukhona ke bancamisayo ukunkqangaza, besithi, Mbethelele emnqamlezweni!', 'XHO22'),
(45, 15, 15, 'Kwathi ngoko uPilato, enga angasiyenelisa isihlwele, wabakhululela uBharabhas; wamnikela uYesu, akuba emtyakatyile, ukuba abethelelwe emnqamlezweni.', 'XHO22'),
(45, 15, 16, 'Aza amasoldati amrholela entendelezweni, ebizwa ngokuba yirhuluneli; balibizele ndawonye ibutho lonke.', 'XHO22'),
(45, 15, 17, 'Bamambesa iingubo ezimfusa, baluluka isithsaba sameva, bamfaka ngaso entloko.', 'XHO22'),
(45, 15, 18, 'Baza bathi kuye, Vuya, Kumkani wamaYuda!', 'XHO22'),
(45, 15, 19, 'Ayembetha entloko ngengcongolo; ayemtshicela; ayeguqa ngamadolo, aqubude kuye.', 'XHO22'),
(45, 15, 20, 'Athi akuba edlale ngaye, amhluba ebomvu, amambathisa kwaezakhe iingubo; amrhola, aya kumbethelela emnqamlezweni.', 'XHO22'),
(45, 15, 21, 'Anyanzela umhambi othile, uSimon waseKirene, evela ephandleni, lowo nguyise ka-Alesandire noRufo, ukuba awuthwale umnqamlezo wakhe.', 'XHO22'),
(45, 15, 22, 'Amzisa endaweni yaseGolgota, oko kukuthi ngentetho evakalayo, yindawo yokakayi.', 'XHO22'),
(45, 15, 23, 'Ayemnika iwayini enemore, ukuba ayisele; kodwa akayamkelanga.', 'XHO22'),
(45, 15, 24, 'Athi, akumbethelela emnqamlezweni, abelana ngeengubo zakhe, esenza amaqashiso ngazo, ukuba ulowo uya kuthabatha ntoni na.', 'XHO22'),
(45, 15, 25, 'Laye ke ilixa lesithathu, bambethelela emnqamlezweni.', 'XHO22'),
(45, 15, 26, 'Umbhalo wesimangalo sakhe wawubhalwe ngaphezulu, wawusithi, UKUMKANI WAMAYUDA.', 'XHO22'),
(45, 15, 27, 'Kwaye kubethelelwe kunye naye eminqamlezweni izihange ezibini; omnye ngasekunene kwakhe, nomnye ngasekhohlo kwakhe.', 'XHO22'),
(45, 15, 28, 'Saza sazaliseka isibhalo esithi, Wabalwa nabanxaxha.', 'XHO22'),
(45, 15, 29, 'Bamnyelisa ke abadlulayo, behlunguzela iintloko zabo, besithi, Yeha, wena uchitha itempile, uyakhe ngeentsuku ezintathu!', 'XHO22'),
(45, 15, 30, 'Zisindise, uhle emnqamlezweni.', 'XHO22'),
(45, 15, 31, 'Ngokukwanjalo nababingeleli abaziintloko bahlekisa ngabo kunye nababhali, besithi: “Abanye wabasindisa; yena ngokwakhe akanako ukuzisindisa.', 'XHO22'),
(45, 15, 32, 'uKristu, uKumkani wakwaSirayeli, makehle kalokunje emnqamlezweni, ukuze sibone, sikholwe. Baye nabo babebethelelwe eminqamlezweni kunye naye bemnyelisa.', 'XHO22'),
(45, 15, 33, 'Ke kaloku, lakuba lilixa lesithandathu, kwehla ubumnyama phezu kwawo umhlaba uphela, kwada kwalilixa lesithoba.', 'XHO22'),
(45, 15, 34, 'Ngelixa lesithoba wadanduluka uYesu ngezwi elikhulu, esithi, Eloyi, Eloyi, lama sabhaktani? Oko kukuthi ngentetho evakalayo, Thixo wam, Thixo wam, undishiye ngani na?', 'XHO22'),
(45, 15, 35, 'Inxenye ke yababemi khona yakuva yathi, Niyabona, ubiza uEliya.', 'XHO22'),
(45, 15, 36, 'Wagidima ke omnye, wazalisa imfunxa ngeviniga, wayifaka engcongolweni, wamseza, esithi, Khaniyeke; Makhe sibone ukuba uEliya uza kumthula na.', 'XHO22'),
(45, 15, 37, 'Wadanduluka ngelizwi elikhulu uYesu, wawunikela umoya wakhe.', 'XHO22'),
(45, 15, 38, 'Lathi ikhuselo lengcwele lakrazuka kubini, lithabathela phezulu lada lesa phantsi.', 'XHO22'),
(45, 15, 39, 'Ebonile ke umthetheli-khulu obemi malunga naye, ukuba wenjenjalo ukudanduluka waphuma umphefumlo, uthe, Inyaniso, lo mntu ubenguNyana kaThixo.', 'XHO22'),
(45, 15, 40, 'Kwakukho ke nabafazi, bekhangele bemgama; phakathi kwabo kwakukho uMariya waseMagadala, noMariya unina wooYakobi omnci nokaYose, kwanoSalome;', 'XHO22'),
(45, 15, 41, 'ababesithi, oko ebeseGalili, bamlandele, bamlungiselele; kwanabanye abaninzi, ababenyuke naye ukuya eYerusalem.', 'XHO22'),
(45, 15, 42, 'Ke kaloku, kwakuhlwa, ngokuba kwakuyimini yokulungiselela, oko kukuthi, umhla ongaphambi kwesabatha,', 'XHO22'),
(45, 15, 43, 'kweza uYosefu, owaseArimati, umphakathi obekekileyo, owayebulindile naye ubukumkani bukaThixo; waya kungena ngobuganga kuPilato, wacela umzimba kaYesu.', 'XHO22'),
(45, 15, 44, 'Wamangaliswa ke uPilato kukuba selefile. Ebizele kuye umthetheli-khulu, wambuza ukuba uselenexesha na efile.', 'XHO22'),
(45, 15, 45, 'Ekwazile ke kumthetheli-khulu, wawunikela uYosefu umzimba.', 'XHO22'),
(45, 15, 46, 'Yena, ethenge ilinen ecikizekileyo, wamthula, wamsongela kwilinen entle; wamlalisa engcwabeni elixholwe eweni; waqengqela ilitye emnyango wengcwaba.', 'XHO22'),
(45, 15, 47, 'Babona ke ooMariya waseMagadala, noMariya unina kaYose, apho wayebekwe khona.', 'XHO22'),
(45, 16, 1, 'Yakubon’ ukuba idlule isabatha, ooMariya waseMagadala, noMariya unina kaYakobi, noSalome, bathenga ubulawu, ukuba beze bamthambise.', 'XHO22'),
(45, 16, 2, 'Kwathi, kwakusa, ngolokuqala usuku lweveki, beza engcwabeni, ukuphuma kwelanga.', 'XHO22'),
(45, 16, 3, 'Baye bethetha bodwa, besithi, Ngubani na oya kusiqengqela ilitye, lisuke emnyango wengcwaba?', 'XHO22'),
(45, 16, 4, 'Bathi bakukhangela, babona ukuba liqengqiwe lesuka ilitye; kuba belilikhulu kakhulu.', 'XHO22'),
(45, 16, 5, 'Bakuba bengenile engcwabeni, babona umfana ehleli ngasekunene, ambethe ingubo ende emhlophe; basuka boyika.', 'XHO22'),
(45, 16, 6, 'Athi kubo, Musani ukuqhiphuka umbilini; nifuna uYesu waseNazarete, obebethelelwe emnqamlezweni; Akakho apha; nantsi indawo ababembeke kuyo.', 'XHO22'),
(45, 16, 7, 'Hambani ke niye, nibaxelele abafundi bakhe noPetros ukuthi, uyanandulela ukuya kwelaseGalili; nombona khona apho, njengokuba wayetshilo kuni.', 'XHO22'),
(45, 16, 8, 'Baphuma kamsinya, babaleka engcwabeni; Baye ke bengcangcazela bakhwankqiswa; abathetha nto nakubani; kuba bebesoyika.', 'XHO22'),
(45, 16, 9, 'Uthe ke uYesu, evuke ekufeni kusasa ngolokuqala usuku lweveki, wabonakala kuqala kuMariya waseMagadala, awayekhuphe kuye iidemon zasixhenxe.', 'XHO22'),
(45, 16, 10, 'Waya yena wabikela ababenaye, bakubon’ ukuba bakhedamile, bayalila.', 'XHO22'),
(45, 16, 11, 'Basuka nabo, bakuva ukuba uhleli, nokuba ukhe wabonwa nguye, abakholwa.', 'XHO22'),
(45, 16, 12, 'Emva koko wabonakala ngolunye uhlobo kwababini bakubo, behamba besinga emaphandleni.', 'XHO22'),
(45, 16, 13, 'Nabo baya babikela abanye;', 'XHO22'),
(45, 16, 14, 'Emva koko wabonakala kwabalishumi elinamnye, behleli ngasesithebeni; wakungcikiva ukungakholwa kwabo, nobulukhuni beentliziyo zabo, ngokuba bengakholwanga ngabo babembonile evukile.', 'XHO22'),
(45, 16, 15, 'Wathi kubo, Hambani niye kulo lonke ihlabathi, nizishumayele iindaba ezilungileyo kuyo yonke indalo.', 'XHO22'),
(45, 16, 16, 'Lowo ukholiweyo wabhaptizwa, wosindiswa; ke yena ongakholwayo, uya kugwetywa.', 'XHO22'),
(45, 16, 17, 'Imiqondiso ke eya kulandelana nabo bakholwayo; Egameni lam baya kukhupha iidemon; baya kuthetha ngeelwimi ezintsha;', 'XHO22'),
(45, 16, 18, 'baya kuphatha iinyoka; nokuba bathe basela into ebulalayo, ayisayi kukha ibenzakalise bona; baya kubeka izandla phezu kwemilwelwe, iphile.', 'XHO22'),
(45, 16, 19, 'Ithe ngoko yona iNkosi, emveni kokuthetha kubo, yanyuselwa emazulwini, yahlala ngasekunene kukaThixo.', 'XHO22'),
(45, 16, 20, 'Ke bona baphuma, bavakalisa ezindaweni zonke, iNkosi isebenza nabo, iliqinisela ilizwi ngemiqondiso. Amen.', 'XHO22'),
(46, 1, 1, 'Ekubeni ke bebaninzi abasa isandla sabo, ukuyibeka eludwengeni udaba olukholiweyo phakathi kwethu,', 'XHO22'),
(46, 1, 2, 'njengokuba basinikelayo abo bathi kwasekuqalekeni baba ngamangqina okuzibonela, nabakhonzi balo ilizwi;', 'XHO22'),
(46, 1, 3, 'Kwabonakala kulungile nakum, ndizazi zonke izinto, kwasekuqaleni, ukuba ndikubhalele ngokulandelelana kwazo, Tiyofilo odume kunene,', 'XHO22'),
(46, 1, 4, 'ukuze uyazi kakuhle ingqiniseko yezo zinto uzifundisisiweyo.', 'XHO22'),
(46, 1, 5, 'Ngemihla kaHerode ukumkani wakwaJuda, kwakukho umbingeleli othile, ogama linguZakariya, engoweqela lika-Abhiya, nomfazi wakhe engoweentombi zika-Aron, igama lakhe linguElizabhete.', 'XHO22'),
(46, 1, 6, 'Bebengamalungisa ke bobabini emehlweni kaThixo, behamba ngayo yonke imithetho nemimiselo yeNkosi, bengenakusoleka.', 'XHO22'),
(46, 1, 7, 'Baye bengenamntwana, ngokokuba uElizabhete ebengazali, kanjalo bobabini babesebekhulile.', 'XHO22'),
(46, 1, 8, 'Ke kaloku kwathi, xa wayesenza ububingeleli ngokweqela lakhe, phambi koThixo.', 'XHO22'),
(46, 1, 9, 'Ngokwesiko lobubingeleli, kwaba liqashiso lakhe ukuba aqhumisele ngesiqhumiso, xa angenayo etempileni yeNkosi.', 'XHO22'),
(46, 1, 10, 'Yaye yonke inkitha yabantu ithandaza ngaphandle ngelixa lesiqhumiso.', 'XHO22'),
(46, 1, 11, 'Kwabonakala ke kuye isithunywa seNkosi, simi ngasekunene kwesibingelelo sesiqhumiso.', 'XHO22'),
(46, 1, 12, 'uZekariya wathi akumbona wakhathazeka, wangenelwa luloyiko.', 'XHO22'),
(46, 1, 13, 'Sathi kuye isithunywa eso, Musa ukoyika, Zakariya, kuba umthandazo wakho uviwe; wothi ke umfazi wakho uElizabhete akuzalele unyana, umthiye igama elinguYohane.', 'XHO22'),
(46, 1, 14, 'Uya kuba novuyo novuyo; bathi nabaninzi bakuvuyele ukuzalwa kwakhe.', 'XHO22'),
(46, 1, 15, 'Kuba uya kuba mkhulu emehlweni eNkosi, angakhe asele wayini nasiselo sinxilisayo; uya kuzaliswa nguMoya oyiNgcwele kwasekuzalweni kwakhe.', 'XHO22'),
(46, 1, 16, 'Nento eninzi ke yoonyana bakaSirayeli uya kuyibuyisela eNkosini uThixo wabo.', 'XHO22'),
(46, 1, 17, 'Yena uya kuhamba phambi kobuso bayo, enomoya enamandla kaEliya, ukuba azibuyisele iintliziyo zooyise kubantwana, nabangevayo kuko ukuqonda kwamalungisa; alungisele iNkosi abantu abalungisiweyo.', 'XHO22'),
(46, 1, 18, 'Wathi uZekariya kuso isithunywa, Ndiya kukwazi ngani na oko? ngokuba mna ndimdala, waye nomfazi wam selekhulile.', 'XHO22'),
(46, 1, 19, 'Saphendula ke isithunywa, sathi kuye, Mna ndinguGabriyeli, oma phambi koThixo. Ndithunyiwe ukuba ndithethe kuwe, ndikushumayele ezi ndaba zilungileyo.', 'XHO22'),
(46, 1, 20, 'Uyabona, uya kuba sisidenge, ungabi nako ukuthetha, kude kube yimini eziya kuthi zenzeke ngayo ezi zinto, ngenxa enokuba ungakholwanga ngamazwi am; aya kuzalisekiswa wona ngexesha lawo.', 'XHO22'),
(46, 1, 21, 'Baye abantu bemlindile uZekariya, bemangalisiwe kukulibala kwakhe endaweni engcwele leyo.', 'XHO22'),
(46, 1, 22, 'Uthe ke akuphuma, akaba nako ukuthetha kubo; baqonda ukuba ubone umbono endaweni engcwele apho, waye yena emana ukunqwala kubo, wahlala engathethi.', 'XHO22'),
(46, 1, 23, 'Kwathi, yakuzaliseka imihla yolungiselelo lwakhe, wemka waya endlwini yakhe.', 'XHO22'),
(46, 1, 24, 'Emveni ke kwezo ntsuku, uElizabhete umfazi wakhe wamitha, wazifihla iinyanga zantlanu, esithi,', 'XHO22'),
(46, 1, 25, 'Yenjenje uYehova kum ngemihla eyandikhangelayo, ukuba ndikususe ukungcikiveka kwam phakathi kwabantu.', 'XHO22'),
(46, 1, 26, 'Ngenyanga yesithandathu, isithunywa esinguGabriyeli sathunyelwa nguThixo emzini welaseGalili, ogama liyiNazarete,', 'XHO22'),
(46, 1, 27, 'kwintombi, eganelwe indoda egama linguYosefu, yendlu kaDavide; laye igama lentombi leyo lalinguMariya.', 'XHO22'),
(46, 1, 28, 'Sangena isithunywa kuye, sathi, Vuya, wena ubabalweyo! iNkosi inawe; usikelelwe wena phakathi kwabafazi.', 'XHO22'),
(46, 1, 29, 'Uthe ke akusibona, wee qhiphu umbilini ngenxa yelizwi laso; wayecamanga ukuba ungaba mbuliso mni na lo.', 'XHO22'),
(46, 1, 30, 'Sathi ke isithunywa kuye, Musa ukoyika, Mariya, kuba uThixo ukwenzela isisa.', 'XHO22'),
(46, 1, 31, 'Uyabona, uya kuthabatha esizalweni, uzale unyana, umthiye igama elinguYesu.', 'XHO22'),
(46, 1, 32, 'Yena uya kuba mkhulu, abizwe ngokuba nguNyana wOsenyangweni; iNkosi uThixo iya kumnika itrone kaDavide, uyise.', 'XHO22'),
(46, 1, 33, 'Uya kuba ngukumkani phezu kwendlu kaYakobi kude kube ngunaphakade; ubukumkani bakhe abuyi kuba nasiphelo.', 'XHO22'),
(46, 1, 34, 'Wathi ke uMariya kuso isithunywa, Kuya kuthini na ukubakho oku, ndingazani nandoda nje?', 'XHO22'),
(46, 1, 35, 'Saphendula isithunywa sathi kuye, uMoya oyiNgcwele uya kuza phezu kwakho, athi namandla Osenyangweni akusibekele; kungoko loo nto ingcwele izalwa nguwe iya kubizwa ngokuba nguNyana kaThixo.', 'XHO22'),
(46, 1, 36, 'Uyabona, naye uElizabhete ozalana naye, ukhawule unyana ebudaleni bakhe; yaye le yinyanga yesithandathu kuye, lowo kwakusithiwa uludlolo.', 'XHO22'),
(46, 1, 37, 'Ngokuba akukho nanye into eya kumnqabela yena uThixo.', 'XHO22'),
(46, 1, 38, 'Wathi ke uMariya, Nanko umkhonzazana weNkosi; makube kum ngokwelizwi lakho. Saza isithunywa semka kuye.', 'XHO22'),
(46, 1, 39, 'Ke kaloku, wesuka uMariya ngaloo mihla, wasinga kweleentaba ngobungxamo, emzini wakwaYuda;', 'XHO22'),
(46, 1, 40, 'wangena endlwini kaZekariya, wambulisa uElizabhete.', 'XHO22'),
(46, 1, 41, 'Kwathi, akuwuva uElizabhete umbuliso kaMariya, lwasuka lwaxhuma usana esizalweni sakhe, uElizabhete wazala nguMoya oyiNgcwele;', 'XHO22'),
(46, 1, 42, 'Wadanduluka ngezwi elikhulu, wathi, Ùnentsikelelo wena phakathi kwabafazi; sinentsikelelo nesiqhamo sesizalo sakho.', 'XHO22'),
(46, 1, 43, 'Iyintoni na ke le nto kum, ukuba eze kum unina weNkosi yam?', 'XHO22'),
(46, 1, 44, 'Kuthe, kuthe lakubakho izwi lombuliso wakho ezindlebeni zam, lwasuka lwaxhuma usana ngovuyo esizalweni sam.', 'XHO22'),
(46, 1, 45, 'Unoyolo lowo wakholwayo koko, kuba kuya kubakho inzaliseko yezo zinto zithethiweyo kuye yiNkosi.', 'XHO22'),
(46, 1, 46, 'Wathi ke uMariya, Umphefumlo wam uyenza nkulu iNkosi;', 'XHO22'),
(46, 1, 47, 'Nomoya wam ugcobile ngoThixo uMsindisi wam;', 'XHO22'),
(46, 1, 48, 'Kuba wakukhangela ukuthobeka komkhonzazana wakhe; kuba, uyabona, ziya kuqala kwangoku ukuthi ndingonoyolo zonke izizukulwana.', 'XHO22'),
(46, 1, 49, 'Ngokuba undenzele izinto ezinkulu uSomandla; lingcwele igama lakhe.', 'XHO22'),
(46, 1, 50, 'Inceba yakhe ikwizizukulwana ngezizukulwana Kwabo bamoyikayo.', 'XHO22'),
(46, 1, 51, 'Wenze amandla ngengalo yakhe; ubachithachithe abanekratshi ekucingeni kweentliziyo zabo.', 'XHO22'),
(46, 1, 52, 'Ubathobile abanamandla ezitroneni zabo, wabaphakamisa abathobekileyo.', 'XHO22'),
(46, 1, 53, 'Wahluthisa abalambileyo ngezinto ezilungileyo; wabandulula abazizityebi belambatha.', 'XHO22'),
(46, 1, 54, 'Wasiza uSirayeli, umkhonzi wakhe, ngokukhumbula inceba yakhe;', 'XHO22'),
(46, 1, 55, 'Njengoko wathethayo koobawo bethu, kuAbraham nakwimbewu yakhe ngonaphakade.', 'XHO22'),
(46, 1, 56, 'Wahlala ke uMariya naye iinyanga ngathi zaba ntathu, waza wabuyela endlwini yakhe.', 'XHO22'),
(46, 1, 57, 'Ke kaloku, ixesha likaElizabhete lazaliseka lokuba azale; wazala unyana.', 'XHO22'),
(46, 1, 58, 'Bakuva abamelwane nemizalwane yakhe, ukuba iNkosi iyenze nkulu inceba yayo kuye; baye bevuyisana naye.', 'XHO22'),
(46, 1, 59, 'Kwathi ngomhla wesibhozo, beza kumalusa umntwana; Bambiza ngegama likayise uZekariya.', 'XHO22'),
(46, 1, 60, 'Waphendula unina, wathi, Hayi; uya kubizwa ngokuba nguYohane.', 'XHO22'),
(46, 1, 61, 'Bathi kuye, Akukho namnye ubizwa ngelo gama kwimizalwane yakho.', 'XHO22'),
(46, 1, 62, 'Baye ke benqwala kuyise besithi, angathanda ukuba athiweni na ukubizwa.', 'XHO22'),
(46, 1, 63, 'Wacela itafile, wabhala esithi, Igama lakhe nguYohane. Bamangaliswa bonke.', 'XHO22'),
(46, 1, 64, 'Wavuleka ke kwaoko umlomo wakhe, lwathukululeka nolwimi lwakhe, wamana ethetha, ebonga uThixo.', 'XHO22'),
(46, 1, 65, 'Kwehla ukoyika phezu kwalo lonke umelwane lwabo, yaye kulo lonke eleentaba lakwaYuda kuxoxwa ezi zinto.', 'XHO22'),
(46, 1, 66, 'Bathi bonke abazivayo, bazibeka entliziyweni yabo, besithi, Azi uya kuba yintoni na lo mntwana? Saye isandla seNkosi sinaye.', 'XHO22'),
(46, 1, 67, 'uZakariya uyise wazala nguMoya oyiNgcwele, waprofeta esithi,', 'XHO22'),
(46, 1, 68, 'Makabongwe uNdikhoyo, uThixo kaSirayeli; ngokuba ubavelele, wabakhulula abantu bakhe;', 'XHO22'),
(46, 1, 69, 'Yasiphakamisela uphondo losindiso Endlwini kaDavide umkhonzi wakhe;', 'XHO22'),
(46, 1, 70, 'njengoko wathethayo ngomlomo wabaprofeti bakhe abangcwele, ababekho kwasephakadeni;', 'XHO22'),
(46, 1, 71, 'ukuze sihlangulwe ezintshabeni zethu, nasesandleni sabo bonke abasithiyayo;', 'XHO22'),
(46, 1, 72, 'Ukubenzela inceba oobawo bethu, nokukhumbula umnqophiso wakhe ongcwele;', 'XHO22'),
(46, 1, 73, 'Isifungo awasifungela ubawo uAbraham,', 'XHO22'),
(46, 1, 74, 'ukuze asinike, ukuba sihlangulwe esandleni seentshaba zethu, simkhonze singenaloyiko;', 'XHO22'),
(46, 1, 75, 'Ngobungcwele nangobulungisa phambi kwakhe, yonke imihla yobomi bethu.', 'XHO22'),
(46, 1, 76, 'Nawe, mntwana, uya kubizwa ngokuba ungumprofeti wOsenyangweni, kuba uya kuhamba phambi kobuso beNkosi, ulungise iindlela zayo;', 'XHO22'),
(46, 1, 77, 'Ukubazisa abantu bakhe usindiso ngoxolelo lwezono zabo;', 'XHO22'),
(46, 1, 78, 'Ngenceba yoThixo wethu; ekwasiveleleyo kokusa kwasenyangweni;', 'XHO22'),
(46, 1, 79, 'Ukuba ukukhanya kwabahleli emnyameni nasethunzini lokufa, Ukuba uzikhokele iinyawo zethu endleleni yoxolo.', 'XHO22'),
(46, 1, 80, 'Wakhula umntwana, waya esomelela emoyeni; wayesezintlango, kwada kwayimini yokubonakalaliswa kwakhe kuSirayeli.', 'XHO22'),
(46, 2, 1, 'Ke kaloku kwathi ngaloo mihla, kwaphuma ummiselo kuKesare Awugusto othi, malibhalwe encwadini yomlibo wokuzalwa lonke elimiweyo.', 'XHO22'),
(46, 2, 2, 'Olu lubhalo lwaba lolokuqala, akubon’ ukuba uKireniyo uyirhuluneli yeSiriya.', 'XHO22'),
(46, 2, 3, 'Baphuma ke bonke, besiya kubhaliswa, elowo waya kowabo.', 'XHO22'),
(46, 2, 4, 'Wenyuka ke noYosefu, evela kwelaseGalili, kumzi oyiNazarete, waya kwelakwaYuda, emzini kaDavide, ekuthiwa yiBhetelehem; ngokuba ebengowendlu kaDavide, engowemizalwane yakhe;', 'XHO22'),
(46, 2, 5, 'esiya kubhalwa, enoMariya, umfazi lowo wayemganelwe, selekhulelwe.', 'XHO22'),
(46, 2, 6, 'Ke kaloku kwathi, xa bakhona, yazaliseka imihla yokuba azale.', 'XHO22'),
(46, 2, 7, 'Wamzala unyana wakhe wamazibulo, wamsongela eziqhiyeni, wamlalisa emkhumbini wesitali, ngokuba babengenandawo endlwini yabahambi.', 'XHO22'),
(46, 2, 8, 'Kwaye kukho abalusi kwakwelo lizwe, behlala ezindle, belinda ebusuku imihlambi yabo.', 'XHO22'),
(46, 2, 9, 'Babona ngesithunywa seNkosi simi phezu kwabo, bathi nobuqaqawuli beNkosi bababengezelela ngeenxa zonke; besuka boyika ngokoyika okukhulu.', 'XHO22'),
(46, 2, 10, 'Sathi kubo isithunywa, Musani ukoyika; kuba, yabonani, ndinishumayeza iindaba ezilungileyo zovuyo olukhulu, oluya kuba kubo bonke abantu.', 'XHO22'),
(46, 2, 11, 'Nizalelwe namhlanje kwisixeko sikaDavide uMsindisi, onguKrestu iNkosi.', 'XHO22'),
(46, 2, 12, 'Lo ngumqondiso kuni; Nofumana lusana lusongelwe eziqhiyeni, lulele emkhumbini.', 'XHO22'),
(46, 2, 13, 'Kwabakho ke ngesiquphe, ndawonye nesithunywa eso, inkitha yomkhosi wasemazulwini, idumisa uThixo, isithi,', 'XHO22'),
(46, 2, 14, 'Uzuko kuThixo enyangweni, Uxolo emhlabeni, Inkolelo ebantwini.', 'XHO22'),
(46, 2, 15, 'Ke kaloku kwathi, zakumka kubo izithunywa zisiya ezulwini, abo abalusi bathi omnye komnye, Masiye eBhetelehem apho, siyibone le nto ihlileyo, esiyazisiweyo nguYehova.', 'XHO22'),
(46, 2, 16, 'Beza ngobungxamo, bamfumana uMariya, kwanoYosefu, nalo usana lulele emkhumbini wesitali.', 'XHO22'),
(46, 2, 17, 'Bakuyibona loo nto, balibhengeza ilizwi elabe lithethwe kubo ngaye lo mntwana.', 'XHO22'),
(46, 2, 18, 'Bathi bonke abo bevayo, bamangaliswa zezo zinto zabe zithethwe kubo ngabalusi.', 'XHO22'),
(46, 2, 19, 'Waye ke uMariya ezigcina zonke ezi zinto eziguquguqula entliziyweni yakhe.', 'XHO22'),
(46, 2, 20, 'Babuya abalusi, bemzukisa bemdumisa uThixo, ngenxa yazo zonke izinto ababezivile nababezibonile, njengoko kwabe kuthethiwe kubo.', 'XHO22'),
(46, 2, 21, 'Xeshikweni ke zazizalisekile iintsuku ezisibhozo zokuba umntwana aluswe, wathiywa igama elinguYesu, elo wabelithiyiwe sisithunywa sezulu, engekamithwa nokumithwa.', 'XHO22'),
(46, 2, 22, 'Zithe ke zakuzaliseka iintsuku zokuhlanjululwa kwakhe ngokomthetho kaMosis, bamsa eJerusalem, ukuba bammise phambi kweNkosi.', 'XHO22'),
(46, 2, 23, 'Njengoko kubhaliweyo emthethweni weNkosi, kwathiwa, Yonke into eliduna, evula isizalo, kuthiwe ingcwele eNkosini;', 'XHO22'),
(46, 2, 24, 'nokuba benze idini njengoko kutshiwoyo emthethweni kaYehova, isibini samahobe, nokuba ngamavukuthu amabini.', 'XHO22'),
(46, 2, 25, 'Kwabonakala kukho umntu eYerusalem, ogama linguSimon; waye loo mntu elilungisa, emoyika uThixo, elinde intuthuzelo kaSirayeli; waye noMoya oyiNgcwele ephezu kwakhe.', 'XHO22'),
(46, 2, 26, 'Wayetyhilelwa nguMoya oyiNgcwele, ukuba akayi kubona kufa, engekamboni uKristu weNkosi.', 'XHO22'),
(46, 2, 27, 'Weza ngaye uMoya etempileni; kwathi, ekumngeniseni kwabo abazali umntwana uYesu, ukuba bamenzele ngokwesiko lomthetho.', 'XHO22'),
(46, 2, 28, 'Wamwola ngeengalo zakhe, wambonga uThixo, wathi,', 'XHO22'),
(46, 2, 29, 'Kaloku, Nkosi yam, uyamndulula ngoku umkhonzi wakho enoxolo, ngokwelizwi lakho.', 'XHO22'),
(46, 2, 30, 'Ngokuba amehlo am alubonile usindiso lwakho,', 'XHO22'),
(46, 2, 31, 'Owulungisileyo phambi kobuso babantu bonke;', 'XHO22'),
(46, 2, 32, 'Isikhanyiso sokukhanyisela iintlanga, nesihombo sabantu bakho amaSirayeli.', 'XHO22'),
(46, 2, 33, 'Bamangaliswa ooYosefu nonina zezo zinto zithethwayo ngaye.', 'XHO22'),
(46, 2, 34, 'uSimon wabasikelela, wathi kuMariya unina, Uyabona, lo umiselwe ukuwa nokuvuka kwabaninzi kwaSirayeli; nokuba ngumqondiso ochasiweyo;', 'XHO22'),
(46, 2, 35, 'ukuze ube nokutyhilwa izicamango eziphuma ezintliziyweni ezininzi; ewe ke, nowakho umphefumlo ikrele liya kuwutyhutyha.', 'XHO22'),
(46, 2, 36, 'Kwaye kukho noAna umprofetikazi, intombi kaFanuweli, wesizwe sika-Ashere. Yena waye eselekhulile kakhulu, waye ke ebehleli nendoda iminyaka esixhenxe, ethabathela ebuntombini bakhe.', 'XHO22'),
(46, 2, 37, 'Eye yena engumhlolokazi ominyaka ngathi imashumi asibhozo anamine, obengemki etempileni, ekhonza uThixo ngokuzila ukudla nangokukhunga ubusuku nemini.', 'XHO22'),
(46, 2, 38, 'Wathi yena ngokwakhe, weza kwangelo lixa, wabulela kuYehova; wayethetha ngayo kubo bonke abo babelinde ukukhululwa ngentlawulelo, eYerusalem.', 'XHO22'),
(46, 2, 39, 'Bakuba bezigqibile zonke izinto ngokomthetho weNkosi, babuyela kwelaseGalili, emzini wabo iNazarete.', 'XHO22'),
(46, 2, 40, 'Ke kaloku wakhula umntwana, waya esomelela emoyeni, ezele bubulumko; lwaye nobabalo lukaThixo luphezu kwakhe.', 'XHO22'),
(46, 2, 41, 'Abazali bakhe babehamba iminyaka ngeminyaka besiya eYerusalem emthendelekweni wepasika.', 'XHO22'),
(46, 2, 42, 'Kuthe, xa wayeneminyaka elishumi elinamibini ezelwe, benyuka baya eYerusalem ngokwesiko lomthendeleko lowo.', 'XHO22'),
(46, 2, 43, 'Bakuba bezigqibile ke iintsuku ezo, ekubuyeni kwabo, umntwana uYesu wasalela eYerusalem. Babengazi ke uYosefu nonina.', 'XHO22'),
(46, 2, 44, 'Babeba ke naye uphakathi kwesihlwele, bahamba uhambo lwemini enye; Baye ke bemfuna phakathi kwemizalwane nabazana nabo.', 'XHO22'),
(46, 2, 45, 'Bathe bakuba bengamfumani, babuyela eYerusalem, bemfuna.', 'XHO22'),
(46, 2, 46, 'Kwathi, emva kweentsuku ezintathu, bamfumana etempileni, ehleli phakathi kwabafundisi, ebaphulaphula, ebabuza.', 'XHO22'),
(46, 2, 47, 'Basuka bonke ababemphulaphula bathi nqa yingqondo yakhe na ziimpendulo zakhe.', 'XHO22'),
(46, 2, 48, 'Bakumbona bakhwankqiswa. Unina wathi kuye, Mntwana wam, yini na ukuba usenjenje? uyabona, uyihlo nam besikufuna sibuhlungu.', 'XHO22'),
(46, 2, 49, 'Wathi kubo, Benindifunela ntoni na? Beningazi na ukuba ndimelwe kukuba ndibe semicimbini kaBawo?', 'XHO22'),
(46, 2, 50, 'Ke bona abayiqondanga into awayeyithetha kubo.', 'XHO22'),
(46, 2, 51, 'Wehla nabo, waya kufika eNazarete, wabalulamela. Waye ke unina ezigcina zonke ezi zinto entliziyweni yakhe.', 'XHO22'),
(46, 2, 52, 'uYesu wayehambela phambili ebulumkweni nasebukhulwini, nasekuthandweni nguThixo nangabantu.', 'XHO22'),
(46, 3, 1, 'Ke kaloku, ngomnyaka weshumi elinesihlanu wobukumkani bukaTibheriyo Kesare, inguPontiyo Pilato irhuluneli yelakwaYuda, inguHerode umphathi wesahlulo sesine esilelaseGalili, inguFilipu ke umzalwana wakhe umphathi wesahlulo sesine esilelaseItureya nelaseTrakoniti, inguLisaniyo umphathi wesahlulo sesine esilelaseAbhilene,', 'XHO22'),
(46, 3, 2, 'Ke bona ababingeleli abakhulu ooAnas noKayafa, lafika ilizwi likaThixo kuYohane unyana kaZekariya entlango.', 'XHO22'),
(46, 3, 3, 'Weza kuwo wonke ummandla waseYordan, evakalisa ubhaptizo lwenguquko ukuze kuxolelwe izono;', 'XHO22'),
(46, 3, 4, 'Njengoko kubhaliweyo encwadini yamazwi kaIsaya umprofeti, esithi, Izwi lodandulukayo entlango, lisithi, Yilungiseni indlela yeNkosi, Wutyenenezeni umendo wayo.', 'XHO22'),
(46, 3, 5, 'Zonke iintlambo ziya kugungxulwa, zonke iintaba neenduli zigungxulwe; zibe magoso-goso, neendlela ezimagqagala zibe lula;', 'XHO22'),
(46, 3, 6, 'Yaye yonke inyama iya kulubona usindiso lukaThixo.', 'XHO22'),
(46, 3, 7, 'Wayesithi ngoko kuzo izihlwele ezaziphuma zisiza kubhaptizwa nguye, Nzalandini yamarhamba, ngubani na owanibonisayo, ukuba niyibaleke ingqumbo ezayo?', 'XHO22'),
(46, 3, 8, 'Velisani ngoko iziqhamo eziyifaneleyo inguquko; ize ningaqali nithi ngaphakathi kwenu, Thina sinobawo uAbraham; kuba ndithi kuni, Unako uThixo kuwo la matye ukumvelisela abantwana uAbraham.', 'XHO22'),
(46, 3, 9, 'Kaloku izembe selibekiwe engcanjini yemithi, ngoko wonke umthi ongavelisi siqhamo sihle, uyagawulwa, uphoswe emlilweni.', 'XHO22'),
(46, 3, 10, 'Zaye izihlwele zambuza, zisithi, Masenze ntoni na ke?', 'XHO22'),
(46, 3, 11, 'Aphendule ke athi kubo, Lowo uneengubo zangaphantsi ezimbini, makebele ongenayo: nalowo uneengubo zangaphantsi ezimbini, makabele ongenayo; nalowo unokudla, makenze kwangokunjalo.', 'XHO22'),
(46, 3, 12, 'Kweza ke nababuthi berhafu ukuba babhaptizwe, bathi kuye, Mfundisi, masenze ntoni na?', 'XHO22'),
(46, 3, 13, 'Wathi kubo, Ningabizi nto ngaphezu kwenikumiselweyo.', 'XHO22'),
(46, 3, 14, 'Ayebuza kuye ke namasoldati, esithi, Masenze ntoni na ke thina? Wathi kubo, Musani ukungcangcazelisa mntu, ningadli mntu; yanelani ngumvuzo wenu.', 'XHO22'),
(46, 3, 15, 'Bakubon’ ukuba abantu babelindile, becamanga bonke ezintliziyweni zabo ngaye uYohane, ukuba angaba lo unguye na uKrestu;', 'XHO22'),
(46, 3, 16, 'Waphendula uYohane wathi kubo bonke, Mna okunene ndinibhaptiza ngamanzi; kodwa uyeza lowo unamandla kunam, endingafanelekileyo ukuba ndiwuthukulule nomtya weembadada zakhe: uya kunibhaptiza ngoMoya oyiNgcwele nangomlilo yena;', 'XHO22'),
(46, 3, 17, 'Osesandleni sakhe ifolokhwe yakhe isesandleni sakhe, aze asihlambulule isanda sakhe, ayibuthele kuvimba ingqolowa; ke wona umququ uya kuwutshisa ngomlilo ongacimekiyo.', 'XHO22'),
(46, 3, 18, 'Waye ke ebavuselela nangezinye izinto ezininzi, eshumayela iindaba ezilungileyo ebantwini.', 'XHO22'),
(46, 3, 19, 'Ke kaloku uHerode, umphathi wesahlulo sesine selizwe, ohlwayiwe nguye ngenxa kaHerodiya umkaFilipu umninawa wakhe, nangenxa yazo zonke izinto ezimbi abezenzile uHerode;', 'XHO22'),
(46, 3, 20, 'wongezelela le kuzo zonke, yokuba amvalele entolongweni uYohane.', 'XHO22'),
(46, 3, 21, 'Ke kaloku, bakubhaptizwa abantu bonke, kwathi, ebhaptiziwe naye uYesu, ethandaza, lavuleka izulu;', 'XHO22'),
(46, 3, 22, 'Wehla phezu kwakhe uMoya oyiNgcwele, enembonakalo yehobe, kweza nezwi eliphuma ezulwini, lisithi, Wena unguNyana wam oyintanda; Ndikholiswa nguwe.', 'XHO22'),
(46, 3, 23, 'uYesu ngokwakhe waqala eneminyaka engathi imashumi mathathu ezelwe, enjengonyana kaYosefu, unyana kaHeli,', 'XHO22'),
(46, 3, 24, 'owayengunyana kaMatati, unyana kaLevi, unyana kaMeleki, unyana kaYana, unyana kaYosefu,', 'XHO22'),
(46, 3, 25, 'owayengunyana kaMatatiya, unyana ka-Amos, unyana kaNahum, unyana kaEsli, unyana kaNage,', 'XHO22'),
(46, 3, 26, 'unyana kaMahati, unyana kaMatatiya, unyana kaShimehi, unyana kaYosefu, unyana kaYuda,', 'XHO22'),
(46, 3, 27, 'obengunyana kaYowana, unyana kaResa, unyana kaTsorubhabheli, unyana kaSalatiyeli, unyana kaNeri,', 'XHO22'),
(46, 3, 28, 'owayengunyana kaMeleki, unyana ka-Adi, unyana kaKosam, unyana kaElimodam, unyana kaEre,', 'XHO22'),
(46, 3, 29, 'owayengunyana kaYose, unyana kaEliyezere, unyana kaYorim, unyana kaMatati, unyana kaLevi,', 'XHO22'),
(46, 3, 30, 'obengunyana kaSimon, unyana kaYuda, unyana kaYosefu, unyana kaYonan, unyana kaEliyakim,', 'XHO22'),
(46, 3, 31, 'owayengunyana kaMeleya, unyana kaMenan, unyana kaMatatha, unyana kaNatan, unyana kaDavide,', 'XHO22'),
(46, 3, 32, 'owayengunyana kaYese, unyana kaObhedi, unyana kaBhohazi, unyana kaSalmon, unyana kaNashon,', 'XHO22'),
(46, 3, 33, 'unyana ka-Aminadabhi, unyana ka-Aram, unyana ka-Etserom, unyana kaPeres, unyana kaYuda,', 'XHO22'),
(46, 3, 34, 'owayengunyana kaYakobi, unyana kaIsake, unyana ka-Abraham, unyana kaTara, unyana kaNahore,', 'XHO22'),
(46, 3, 35, 'owayengunyana kaSaruki, unyana kaRagawu, unyana kaFaleki, unyana kaHebhere, unyana kaSala,', 'XHO22'),
(46, 3, 36, 'owayengunyana kaKenan, unyana ka-Arpakishadi, unyana kaSem, unyana kaNowa, unyana kaLameki,', 'XHO22'),
(46, 3, 37, 'unyana kaMatusela, unyana kaEnoki, unyana kaYaredi, unyana kaMalaleli, unyana kaKenan,', 'XHO22'),
(46, 3, 38, 'Owayengunyana kaEnoshe, owayengunyana kaSete, owayengunyana ka-Adam, owayengunyana kaThixo.', 'XHO22'),
(46, 4, 1, 'Ke kaloku uYesu, ezele nguMoya oyiNgcwele, wabuya eYordan, wesiwa nguMoya entlango.', 'XHO22'),
(46, 4, 2, 'Wantsuku zimashumi mane ehendwa nguMtyholi. Akadla nto ngezo ntsuku; zakuphela ke, walamba.', 'XHO22'),
(46, 4, 3, 'Wathi uMtyholi kuye, Ukuba unguNyana kaThixo, yitsho kweli litye libe sisonka.', 'XHO22'),
(46, 4, 4, 'Wamphendula uYesu wathi, Kubhaliwe kwathiwa, Akaphili ngasonka sodwa umntu; uphila ngamazwi onke kaThixo.', 'XHO22'),
(46, 4, 5, 'uMtyholi, emnyuse entabeni ephakamileyo, wambonisa zonke izikumkani zelimiweyo, ngesiquphe sexesha.', 'XHO22'),
(46, 4, 6, 'Wathi uMtyholi kuye, Ndokunika bonke obu bukhosi bazo nozuko lwazo; ngokuba bunikelwe kum; nosukuba ndithanda ukumnika.', 'XHO22'),
(46, 4, 7, 'Ukuba ke ngoko uthe wandinqula, zonke ezo nto ziya kuba zezakho.', 'XHO22'),
(46, 4, 8, 'Waphendula uYesu wathi kuye, Suka umke, uye emva kwam, Sathana, ngokuba kubhaliwe kwathiwa, Uze uqubude eNkosini uThixo wakho, ukhonze yena yedwa.', 'XHO22'),
(46, 4, 9, 'Wamsa eYerusalem, wammisa eluchochoyini lwetempile, wathi kuye, Ukuba unguye uNyana kaThixo, ziphose ezantsi phaya.', 'XHO22'),
(46, 4, 10, 'Kuba kubhaliwe kwathiwa, Uya kuziwisela umthetho ngawe izithunywa zakhe, ukuba zikugcine;', 'XHO22'),
(46, 4, 11, 'Kwaye ziya kukufukula ngezandla, Hleze ubetheke etyeni ngonyawo lwakho.', 'XHO22'),
(46, 4, 12, 'Waphendula uYesu wathi kuye, Kuthiwe, Uze ungayihendi iNkosi uThixo wakho.', 'XHO22'),
(46, 4, 13, 'Waza uMtyholi, ekugqibile konke ukuhenda, wesuka kuye umzuzu.', 'XHO22'),
(46, 4, 14, 'Wabuya uYesu enamandla oMoya, weza kwelaseGalili; lwaye lwaphuma udumo lwakhe;', 'XHO22'),
(46, 4, 15, 'Wayefundisa ezindlwini zabo zesikhungu, ezukiswa ngabo bonke.', 'XHO22'),
(46, 4, 16, 'Weza kufika eNazarete, apho wayekhulele khona, wathi ngokwesiko lakhe ngomhla wesabatha, wangena endlwini yesikhungu, wesuka wema ukuba alese.', 'XHO22'),
(46, 4, 17, 'Waza wanikwa incwadi yomshumayeli uIsaya. Eyityhilile ke incwadi, wafumana indawo le kubhalwe kuyo,', 'XHO22'),
(46, 4, 18, 'uMoya weNkosi uphezu kwam, Ethe ngenxa yoko yandithambisela ukushumayela iindaba ezilungileyo kumahlwempu. Indithume ukuba ndiphilise abantliziyo ityumkileyo, ndivakalise inkululeko kubathinjwa, nokubuyiselwa kokubona kwabaziimfama, ndindulule abaqobekileyo bekhululekile;', 'XHO22'),
(46, 4, 19, 'Ndivakalise umnyaka owamkelekileyo weNkosi.', 'XHO22'),
(46, 4, 20, 'Eyisongile ke incwadi, wayibuyisela kwisicaka, wahlala phantsi. Aye amehlo abo bonke abasendlwini yesikhungu enamathele kuye.', 'XHO22'),
(46, 4, 21, 'Waqala ukuthi kubo, Namhlanje esi sibhalo sizalisekile ezindlebeni zenu.', 'XHO22'),
(46, 4, 22, 'Baye bonke bemngqinela, bemangaliswa ngamazwi amnandi aphuma emlonyeni wakhe. Ayesithi, Lo asinguye na unyana kaYosefu?', 'XHO22'),
(46, 4, 23, 'Wathi kubo, Kakade niya kuwuthetha kum lo mzekeliso uthi, Gqirha, ziphilise; okungangoko sikuve kwenzeke eKapernahum, kwenze nakowenu apha.', 'XHO22'),
(46, 4, 24, 'Wathi ke, Inene, ndithi kuni, Akukho mprofeti wamkelekileyo kowabo.', 'XHO22'),
(46, 4, 25, 'Kodwa inene ndithi kuni, Babebaninzi abahlolokazi kwaSirayeli ngemihla kaEliya, oko lalivingcekile izulu iminyaka emithathu eneenyanga ezintandathu, xeshikweni kwakukho indlala enkulu kulo lonke ilizwe;', 'XHO22'),
(46, 4, 26, 'Kodwa uEliya akathunywanga nakumnye kubo; wathunywa eTsarfati yakwaTsidon kumhlolokazi, kwakuphela.', 'XHO22'),
(46, 4, 27, 'Kwaye kukho abantu abaninzi abaneqhenqa kwaSirayeli ngexesha likaElisha umprofeti; akwahlanjululwanga namnye kubo, ingenguNahaman umAram.', 'XHO22'),
(46, 4, 28, 'Bathi bonke ababesendlwini yesikhungu, bakuziva ezi zinto, bazala ngumsindo.', 'XHO22'),
(46, 4, 29, 'Besuka bamkhuphela ngaphandle komzi, bamrholela elungqamekweni lwentaba, owakhelwe kuyo umzi wabo, ukuze bamphose ezantsi eweni.', 'XHO22'),
(46, 4, 30, 'Kodwa yena wacanda phakathi kwabo wemka.', 'XHO22'),
(46, 4, 31, 'Wehla waya eKapernahum, idolophu yelaseGalili, waza wafundisa abantu ngesabatha.', 'XHO22'),
(46, 4, 32, 'Baye ke bekhwankqiswa kukufundisa kwakhe; ngokuba ilizwi lakhe libe linegunya.', 'XHO22'),
(46, 4, 33, 'Kwaye kukho endlwini yesikhungu apho umntu onomoya wedemon engcolileyo;', 'XHO22'),
(46, 4, 34, 'besithi, Siyeke; Yintoni na enathi nawe, Yesu waseNazarete? uze kusitshabalalisa na? Ndiyakwazi wena ukuba ungubani; Lowo Ungcwele kaThixo.', 'XHO22'),
(46, 4, 35, 'Wawukhalimela uYesu, esithi, Yithi tu, uphume kuye. Ithe ke idemon yamntlala esazulwini, yaphuma kuye, ingamenzakalisanga.', 'XHO22'),
(46, 4, 36, 'Bakhwankqiswa ke bonke, bathetha bodwa, besithi, Lilizwi lini na eli? kuba ubathethela ngegunya nangamandla oomoya abangcolileyo aba, baze baphume.', 'XHO22'),
(46, 4, 37, 'Lwaye luphuma udaba lwakhe, lusiya kuzo zonke iindawo zaloo mmandla.', 'XHO22'),
(46, 4, 38, 'Esukile endlwini yesikhungu, wangena endlwini kaSimon. Waye ke umkhwekazi kaSimon ebanjwe ngumkhuhlane omkhulu; bambongoza ngenxa yakhe.', 'XHO22'),
(46, 4, 39, 'Wema phezu kwakhe, wawukhalimela umkhuhlane; Wavuka kwangoko ke, wabalungiselela.', 'XHO22'),
(46, 4, 40, 'Kuthe ke bakubon’ ukuba liyatshona ilanga, bonke abo babenabafayo zizifo ngezifo, babazisa kuye; wabeka izandla phezu kwabo bonke ngabanye, wabaphilisa.', 'XHO22'),
(46, 4, 41, 'Zaye ke neendimoni ziphuma kwabaninzi, zikhala zisithi, Wena unguye uKristu, uNyana kaThixo. Uthe ezikhalimela, akazivumela ukuba zithethe; kuba bezimazi ukuba unguye uKristu.', 'XHO22'),
(46, 4, 42, 'Kwakuba sekusile, waphuma waya endaweni eyintlango. Zaye zimfuna izihlwele, zeza kuye, zamnqanda ukuba angemki kuzo.', 'XHO22'),
(46, 4, 43, 'Uthe ke kuzo, Ndimelwe kukuba ndizishumayele iindaba ezilungileyo zobukumkani bukaThixo nakweminye imizi, ngokuba ndithunywe oko.', 'XHO22'),
(46, 4, 44, 'Wayevakalisa ke ezindlwini zesikhungu kwelaseGalili.', 'XHO22'),
(46, 5, 1, 'Ke kaloku kwathi, zakubon’ ukuba simxinile abantu, ukuba balive ilizwi likaThixo, wesuka wema ngasedikeni laseGenesarete.', 'XHO22'),
(46, 5, 2, 'Wabona imikhombe emibini imi ngasedikeni apho; baye ababambisi beentlanzi bephumile kuyo, behlamba iminatha yabo.', 'XHO22'),
(46, 5, 3, 'Uthe ke uYesu, engene komnye umkhombe, owawungokaSimon, wamcela ukuba awuke akhwelise kancinane emhlabeni. Wahlala phantsi, wabafundisa abantu emkhombeni.', 'XHO22'),
(46, 5, 4, 'Akupheza ke ukuthetha, wathi kuSimon, Khwelelisela enzulwini, nihlise iminatha yenu, kubanjiswe.', 'XHO22'),
(46, 5, 5, 'Waphendula uSimon wathi kuye, Mongameli, sibulaleke ubusuku buphela, asabamba nento; kodwa ngelakho ilizwi ndiya kuwuhlisa umnatha.', 'XHO22'),
(46, 5, 6, 'Bathi, bakukwenza oko, bavingcela inkitha enkulu yeentlanzi; wawuqhaqheka ke umnatha wabo.', 'XHO22'),
(46, 5, 7, 'Baza bakhweba abalingane babo ababekomnye umkhombe, ukuba beze kubancedisa. Beza, bayizalisa imikhombe yomibini, yada yafuna ukutshona.', 'XHO22'),
(46, 5, 8, 'Ekubonile ke oko uSimon Petros, wawa emadolweni kaYesu, esithi, Mka kum; ngokuba ndiyindoda engumoni, Yehova;', 'XHO22'),
(46, 5, 9, 'Kuba wathi makatha, kwanabo bonke ababenaye, ngobanjiso lweentlanzi ezo bazibambisayo.', 'XHO22'),
(46, 5, 10, 'Kwakwanjalo ke noYakobi noYohane, oonyana bakaZebhedi, ababengamadlelane noSimon. Wathi ke uYesu kuSimon, Musa ukoyika; uthabathela kweli xesha, uya kubambisa abantu.', 'XHO22'),
(46, 5, 11, 'Bakuba ke bezikhiphile izikhitshana, bashiya konke, bamlandela.', 'XHO22'),
(46, 5, 12, 'Kwathi ekomnye umzi, babona ngendoda ezele liqhenqa; yathi yakumbona uYesu, yawa ngobuso, yambongoza isithi, Nkosi, ukuba uyathanda, unako ukundihlambulula.', 'XHO22'),
(46, 5, 13, 'Wasolula isandla, wamchukumisa, esithi, Ndiyathanda, hlambuluka. Lathi kwaoko iqhenqa lemka kuye.', 'XHO22'),
(46, 5, 14, 'Wayithethela ngelithi, mayingaxeleli bani; wathi, Hamba uye kuzibonisa kumbingeleli, usondeze idini, ngenxa yokuhlanjululwa kwakho, njengoko wamisayo uMoses, kube bubungqina kubo.', 'XHO22'),
(46, 5, 15, 'Kwaba kukhona luphuma udumo lwakhe; kwahlanganisana izihlwele ezikhulu, ukuze zimve, nokuba ziphiliswe nguye ebulwelweni bazo.', 'XHO22'),
(46, 5, 16, 'Waye ke yena emana ukuzisithelisa entlango, wathandaza.', 'XHO22'),
(46, 5, 17, 'Ke kaloku kwathi ngamini ithile wafundisa, kuhleli abaFarisi nabafundisi-mthetho, ababephume kuyo yonke imizana yelaseGalili, nelakwaYuda, naseYerusalem; ekhoyo ukubaphilisa.', 'XHO22'),
(46, 5, 18, 'Kwabonakala amadoda ethwele ngokhuko umntu ofe umzimba, efuna ukumngenisa endlwini, ambeke phambi kwakhe.', 'XHO22'),
(46, 5, 19, 'Athi, akuba engafumani ndawo angamngenisa ngayo ngenxa yendimbane, akhwela phezu kwendlu, agqobhoza ezifulelweni, amhlisa ke phakathi, amhlisa ngokhuko, ambeka phakathi, phambi koYesu.', 'XHO22'),
(46, 5, 20, 'Akulubona ke ukholo lwawo, wathi kuye, Mntundini, uzixolelwe izono zakho.', 'XHO22'),
(46, 5, 21, 'Baqala ukucamanga ababhali nabaFarisi, besithi, Ngubani na lo uthetha izinyeliso? Ngubani na onako ukuxolela izono, ingenguye uThixo yedwa?', 'XHO22'),
(46, 5, 22, 'Eziqiqile ke uYesu izicamango zabo, waphendula wathi kubo, Nicamanga ntoni na ezintliziyweni zenu?', 'XHO22'),
(46, 5, 23, 'Okukhona kulula kukuphi na, kukho ukuthi, Zixolelwe izono zakho; nokuthi, Vuka uhambe?', 'XHO22'),
(46, 5, 24, 'Ke ukuze nazi, ukuba unalo igunya uNyana woMntu lokuxolela izono emhlabeni apha (selesithi konedumbe), Ndithi kuwe, Vuka, uthabathe ukhuko lwakho, uye endlwini yakho.', 'XHO22'),
(46, 5, 25, 'Usuke wema kwangoko phambi kwabo, wayithabatha loo nto abelele kuyo, wemka waya endlwini yakhe, ezukisa uThixo.', 'XHO22'),
(46, 5, 26, 'Besuka bathi nqa bonke, bamzukisa uThixo, bazala kukoyika, besithi, Sibone imimangaliso namhlanje.', 'XHO22'),
(46, 5, 27, 'Emva kwezi zinto waphuma, wabona umbuthi werhafu, ogama linguLevi, ehleli endaweni yerhafu, wathi kuye, Ndilandele.', 'XHO22'),
(46, 5, 28, 'Ekushiye konke, wesuka wamlandela.', 'XHO22'),
(46, 5, 29, 'uLevi ke wamenzela itheko elikhulu endlwini yakhe. Kwaye kukho inkitha yababuthi berhafu nabanye abantu ababehleli ndawonye nabo.', 'XHO22'),
(46, 5, 30, 'Baye ababhali babo nabaFarisi bebakrokrela abafundi bakhe, besithi, Kungani na ukuba nidle nisele nababuthi berhafu naboni?', 'XHO22'),
(46, 5, 31, 'Uphendule uYesu wathi kubo, Akufuneki gqirha kwabaphili leyo; kodwa ngabafayo.', 'XHO22'),
(46, 5, 32, 'Andize kubiza malungisa, ndize kubiza aboni ukuba baguquke.', 'XHO22'),
(46, 5, 33, 'Baza bathi kuye, Kungani na ukuba abafundi bakaYohane bazile ukudla futhi, benze imithandazo, benjenjalo nababaFarisi; kodwa abakho bayadla basele?', 'XHO22'),
(46, 5, 34, 'Wathi ke kubo, Ninako yini na ukubazilisa ukudla abegumbi lomtshakazi, xa anabo umyeni?', 'XHO22'),
(46, 5, 35, 'Kodwa kofika mihla awoza asuswe kubo umyeni, bandule ke ukuzila ngaloo mihla.', 'XHO22'),
(46, 5, 36, 'Ke kaloku wathetha umzekeliso kubo; Akukho ke ubeka isiziba sengubo entsha engutyeni endala; okanye, wothi nentsha ikrazule, sithi nesiziba esitsha singavisisani nendala.', 'XHO22'),
(46, 5, 37, 'Kananjalo akukho mntu uyitha kwiimvaba ezindala iwayini entsha engekabili; okanye iwayini, leyo intsha, yozigqabhuza iintsuba, iphalale iwayini, zitshabalale neentsuba.', 'XHO22'),
(46, 5, 38, 'Kodwa iwayini entsha ifanele ukuthiwa ezintsubeni ezintsha; kwaye zombini zigciniwe.', 'XHO22'),
(46, 5, 39, 'Kanjalo akukho uthi, ebesele endala, athande entsha kwaoko, kuba uthi, Endala yeyona inencasa.', 'XHO22'),
(46, 6, 1, 'Kwathi ngesabatha yokuqala, wacanda emasimini; Baza abafundi bakhe bakha izikhwebu, badla, bezihlikihla ezandleni zabo.', 'XHO22'),
(46, 6, 2, 'Abathile baFarasi babesithi kubo: “Yini na ukuba nenze oko kungavumelekileyo ngesabatha?', 'XHO22'),
(46, 6, 3, 'Wabaphendula uYesu wathi, Aniyilesanga na into awayenzayo uDavide, xa walambayo, yena nabo babenaye?', 'XHO22'),
(46, 6, 4, 'Okokuba wangena endlwini kaThixo, wazithabatha izonka ezibekwa phambi koThixo, wazidla, wanika nabo babenaye, ekungavumelekileyo ukuba zidliwe, ingengabo ababingeleli bodwa?', 'XHO22'),
(46, 6, 5, 'Wayesithi kubo, uNyana woMntu lo uyiNkosi yayo nesabatha.', 'XHO22'),
(46, 6, 6, 'Kwathi ke kaloku, nangenye isabatha, wangena endlwini yesikhungu, wafundisa. Kwaye kukho apho umntu owome isandla sokunene.', 'XHO22'),
(46, 6, 7, 'Baye ke bemlalele ababhali nabaFarisi, ukuba wophilisa na ngesabatha; ukuze bafumane indawo yokummangalela.', 'XHO22'),
(46, 6, 8, 'Kodwa yena ezazi iingcamango zabo, wathi kuloo mntu unesandla esomileyo, Phakama, umele phakathi. Uye waphakama wema.', 'XHO22'),
(46, 6, 9, 'Wathi ngoko uYesu kubo, Ndiya kunibuza zwi-nye; Okuvumelekileyo ngesabatha kukwenza okulungileyo, kukwenza okubi, kusini na? kukusindisa ubomi, kukubulala, kusini na?', 'XHO22'),
(46, 6, 10, 'Akuba ebasingasingile bonke, wathi kumntu lowo, Solule isandla sakho. Wenjenjalo; saphila ke isandla sakhe, saba nesesinye.', 'XHO22'),
(46, 6, 11, 'Basuka ke bona baphambana; baye bethetha bodwa, ukuba bangamenza ntoni na uYesu.', 'XHO22'),
(46, 6, 12, 'Kwathi ke ngaloo mihla, waphuma wasinga entabeni, esiya kuthandaza. Wayebudlulisa ubusuku bonke ethandaza kuThixo.', 'XHO22'),
(46, 6, 13, 'Kwathi kwakusa, wababizela kuye abafundi bakhe, wanyula kubo abalishumi elinababini, awababiza ngokuba ngabapostile;', 'XHO22'),
(46, 6, 14, 'uSimon, awamthiya igama elinguPetros, noAndreya umzalwana wakhe, noYakobi noYohane, noFilipu, noBhartolomeyu,', 'XHO22'),
(46, 6, 15, 'uMateyu noTomas, uYakobi ka-Alifeyu, noSimon ekuthiwa nguZelote,', 'XHO22'),
(46, 6, 16, 'uYuda kaYakobi, noYuda Sikariyoti, owaba ngumngcatshi.', 'XHO22'),
(46, 6, 17, 'Wehla nabo, wema ethafeni, nenkitha yabafundi bakhe, nenkitha enkulu yabantu, bephuma kulo lonke elakwaYuda, neYerusalem, nangaselwandle lwaseTire neTsidon, ababeze kuva kuye ukuba baphiliswe kwizifo zabo;', 'XHO22'),
(46, 6, 18, 'Kwanabo babekhathazwa ngoomoya abangcolileyo, baye bephiliswa.', 'XHO22'),
(46, 6, 19, 'Zaye izihlwele zonke zifuna ukumchukumisa; ngokuba kube kuphuma amandla kuye, abaphilise bonke.', 'XHO22'),
(46, 6, 20, 'Waza yena wawaphakamisela amehlo akhe kubo abafundi bakhe, wathi, Ninoyolo, nina mahlwempu; ngokuba bobenu ubukumkani bukaThixo.', 'XHO22'),
(46, 6, 21, 'Ninoyolo, nina nilambayo kalokunje; ngokuba niya kuhluthiswa. Ninoyolo, nina nililayo kalokunje; ngokuba niya kuhleka.', 'XHO22'),
(46, 6, 22, 'Ninoyolo nina xa bathe abantu banithiya, naxa bathe banigxotha kubo, baningcikiva, balilahlela phandle igama lenu ngokungathi likhohlakele, ngenxa yakhe uNyana woMntu.', 'XHO22'),
(46, 6, 23, 'Vuyani ngaloo mini, nisuke imitsi; kuba, niyabona, umvuzo wenu mkhulu emazulwini; babesenjenjalo ooyise kubo abaprofeti.', 'XHO22'),
(46, 6, 24, 'Kodwa yeha, nina zityebi! ngokuba niyifumene intuthuzelo yenu.', 'XHO22'),
(46, 6, 25, 'Yeha, nina bahluthiyo! ngokuba niya kulamba. Yeha, nina bahlekayo kalokunje! ngokuba niya kukhedama nilile.', 'XHO22'),
(46, 6, 26, 'Yeha nina, xa bathe bathetha kakuhle ngani bonke abantu! kuba ooyise babo babesenjenjalo kubaprofeti ababuxoki.', 'XHO22'),
(46, 6, 27, 'Ke mna ndithi kuni nindivayo, Zithandeni iintshaba zenu, yenzani okulungileyo abo banithiyayo;', 'XHO22'),
(46, 6, 28, 'Basikeleleni abo baniqalekisayo, nibathandazele abo banigxekayo.', 'XHO22'),
(46, 6, 29, 'Lowo ukubetha esidleleni, mnike nesinye; nothabatha ingubo yakho yokwaleka, ungali neyangaphantsi.', 'XHO22'),
(46, 6, 30, 'Yiphe wonke umntu ocelayo kuwe; nalowo uzithabathayo iimpahla zakho, uze ungaphindi uzicele kuye.', 'XHO22'),
(46, 6, 31, 'Enithanda ukuba abantu benze ngako kuni, yenzani nani kwangokunjalo kubo.', 'XHO22'),
(46, 6, 32, 'Ukuba nithanda abo banithandayo, ninambulelo mni na? kuba naboni bayabathanda abo babathandayo.', 'XHO22'),
(46, 6, 33, 'Xa nithi nenze okulungileyo kwabanenzela okulungileyo, ninambulelo mni na? ngokuba naboni benza kwaloo nto.', 'XHO22'),
(46, 6, 34, 'Xa niboleka abo nithembe ukwamkela kubo, ninambulelo mni na? Kuba naboni bayababoleka aboni, ukuze babuye bazuze okwakhona.', 'XHO22'),
(46, 6, 35, 'Ke nina, zithandeni iintshaba zenu, nenze okulungileyo; niboleke ningathembe lutho. Woba mkhulu ke umvuzo wenu, nize nibe ngoonyana bOsenyangweni; ngokuba yena enobubele kwabangabuleliyo nabangendawo.', 'XHO22'),
(46, 6, 36, 'Ngoko ke yibani nenceba, njengokuba naye uYihlo enenceba.', 'XHO22'),
(46, 6, 37, 'Musani ukugweba, naye ningasayi kugwetywa; musani ukusingela phantsi, naye ningasayi kusingelwa phantsi; xolelani, naniya kuxolelwa;', 'XHO22'),
(46, 6, 38, 'Yiphani, naniya kuphiwa nani; umlinganiselo omhle, ohlohliweyo, ohlunguzelweyo, ophalalayo, nowuphiwa esondweni lengubo yenu. Kuba umlinganiselo enilinganisela ngawo, niya kulinganiselwa kwangawo nani.', 'XHO22'),
(46, 6, 39, 'Ke kaloku wathetha umzekeliso kubo, esithi, ingakhe ibe nako na imfama ukukhokela imfama? Aziyi kweyela emhadini zombini na?', 'XHO22'),
(46, 6, 40, 'Akukho mfundi mkhulu kunomfundisi wakhe; ke wonke ogqibeleleyo uya kuba njengomfundisi wakhe.', 'XHO22'),
(46, 6, 41, 'Yini na ke ukuba usibone isibi esisesweni lomzalwana wakho, kanti akuwuqiqi umqadi okwelakho iliso?', 'XHO22'),
(46, 6, 42, 'Ungathini na ukuba nako ukuthi kumzalwana wakho, Mzalwana, yeka ndisikhuphe isibi esisesweni lakho, ungawuboni nje wena umqadi okwelakho iliso? Mhanahanisindini, wukhuphe kuqala umqadi kwelakho iliso, wandule ke ukubona kakuhle ukusikhupha isibi esisesweni lomzalwana wakho.', 'XHO22'),
(46, 6, 43, 'Kuba akukho mthi mhle uvelisa isiqhamo esibi; kananjalo akukho mthi mbi uvelisa isiqhamo esihle.', 'XHO22'),
(46, 6, 44, 'Kuba wonke umthi waziwa ngesiqhamo sawo. Kuba abantu abawakhi makhiwane emithaneni enameva, bangakhi zidiliya etyholweni;', 'XHO22'),
(46, 6, 45, 'Umntu olungileyo, ebuncwaneni obulungileyo bentliziyo yakhe, uvelisa oko kulungileyo; nomntu okhohlakeleyo, ebuncwaneni obukhohlakeleyo bentliziyo yakhe, uvelisa oko kukhohlakeleyo; kuba umlomo wakhe uthetha ngokuphuphuma kwentliziyo.', 'XHO22'),
(46, 6, 46, 'Yini na ke ukuba nindibize ngokuthi, Nkosi, Nkosi, nibe ningazenzi izinto endizithethayo?', 'XHO22'),
(46, 6, 47, 'Lowo uzayo kum, aweve amazwi am, awenze, ndonibonisa lowo afana naye;', 'XHO22'),
(46, 6, 48, 'Ufana nomntu owakha indlu, wemba nzulu, wabeka isiseko phezu kolwalwa. Kwathi ke, kwakubakho unogumbe, wagaleleka kuloo ndlu umlambo, awaba nako ukuyishukumisa;', 'XHO22'),
(46, 6, 49, 'Kodwa lo uwevileyo akawenza, ufana nomntu owakhe indlu phezu komhlaba, kungekho siseko; wagaleleka kuyo umlambo, yawa kwaoko; kwaba kukhulu ukuwa kwaloo ndlu.', 'XHO22'),
(46, 7, 1, 'Ke kaloku, emveni kokuba ebewagqibile onke amazwi akhe, besiva abantu, wangena eKapernahum.', 'XHO22'),
(46, 7, 2, 'Kwaye kukho umkhonzi womthetheli-khulu othile, owabethandwa nguye, esifa, eza kubhubha.', 'XHO22'),
(46, 7, 3, 'Uthe ke akuva ngaye uYesu, wathumela kuye amadoda amakhulu amaYuda, emcela ukuba eze kumphilisa umkhonzi wakhe.', 'XHO22'),
(46, 7, 4, 'Akufika ke kuYesu, ambongoza enyamekile, esithi, Ukufanele oko lowo ungamenzelayo oko;', 'XHO22'),
(46, 7, 5, 'Kuba uyaluthanda uhlanga lwethu, wasakhela nendlu yesikhungu.', 'XHO22'),
(46, 7, 6, 'Wahamba ke uYesu nabo. Akubon’ ukuba ke akasekude endlwini, umthetheli-khulu wathuma izihlobo kuye, esithi kuye, Nkosi, ungazikhathazi, kuba andikulingene ukuba ungade ungene phantsi kophahla lwam;', 'XHO22'),
(46, 7, 7, 'Kungoko nam ndingakufanelanga ukuda ndize kuwe; thetha ngezwi lodwa, sophiliswa isicaka sam.', 'XHO22'),
(46, 7, 8, 'Kuba nam ndingumntu omiswe phantsi kwegunya labanye, ndinamasoldati aphantsi kwam; ndithi kweli, Yiya, liye; ndithi kweliya, Yiza, lize; nakumkhonzi wam, Yenza oku, akwenze.', 'XHO22'),
(46, 7, 9, 'Ezivile ke uYesu ezi zinto, wamangaliswa nguye, wajika, wathi kwindimbane ebimlandela, Ndithi kuni, andifumananga lukholo lungaka nakwaSirayeli.', 'XHO22'),
(46, 7, 10, 'Abo babethunyiwe babuyela endlwini, bamfumana umkhonzi obengumlwelwe ephilile.', 'XHO22'),
(46, 7, 11, 'Kwathi ngengomsomnye waya emzini ekuthiwa yiNayin; Kwaye kuhamba naye into eninzi yabafundi bakhe, nendimbane enkulu.', 'XHO22'),
(46, 7, 12, 'Ke kaloku, akusondela esangweni lomzi lowo, kwabonakala kuthwalelwa phandle umntu ofileyo, ekuphela konyana ozalwa ngunina; waye yena engumhlolokazi. Yaye nendimbane elingeneyo yomzi inaye.', 'XHO22'),
(46, 7, 13, 'Ithe ke iNkosi yakumbona, yasikwa yimfesane ngenxa yakhe, yathi kuye, Musa ukulila.', 'XHO22'),
(46, 7, 14, 'Yafika ke yalichukumisa ithala, bema ke ababethwele. Ithe ke yona, Mfana, ndithi kuwe, Vuka!', 'XHO22'),
(46, 7, 15, 'Wavuka wahlala umfi lowo, waza wathetha. Waza wamnikela kunina.', 'XHO22'),
(46, 7, 16, 'Baza bankwantya bonke, bamzukisa uThixo, besithi, Kuvele umprofeti omkhulu phakathi kwethu; nokuthi, uThixo ubavelele abantu bakhe.', 'XHO22'),
(46, 7, 17, 'Lwaphuma ke olo ludaba lungaye, lwahamba kwelakwaYuda liphela, nakuloo mmandla wonke.', 'XHO22'),
(46, 7, 18, 'Bazibika kuYohane abafundi bakhe zonke ezi zinto.', 'XHO22'),
(46, 7, 19, 'Ebizele kuye babini bathile kubafundi bakhe, uYohane wabathuma kuYesu esithi, Wena ungulowo uzayo na? okanye sikhangele enye?', 'XHO22'),
(46, 7, 20, 'Efikile kuye amadoda lawo, athi, uYohane umbhaptizi usithume kuwe esithi, Wena ungulowo uzayo na, wena? okanye sikhangele enye?', 'XHO22'),
(46, 7, 21, 'Ke kaloku, kwangelo lixa, waphilisa abaninzi kuzo izifo, nakuzo izibetho, nakoomoya abakhohlakeleyo; wathi ke iimfama ezininzi wazibabala ukubona.', 'XHO22'),
(46, 7, 22, 'Waphendula uYesu wathi kuwo, Hambani niye kumbikela uYohane izinto enizibonileyo nenizivileyo; okokuba iimfama zibuya zibone, iziqhwala ziyahamba, abaneqhenqa bayahlanjululwa, izithulu ziyeva, abafileyo bayavuswa, amahlwempu ayazishunyayezwa iindaba ezilungileyo.', 'XHO22'),
(46, 7, 23, 'Unoyolo othe akakhutyekiswa ndim.', 'XHO22'),
(46, 7, 24, 'Ke kaloku, bakubon’ ukuba bemkile abathunywa bakaYohane, waqala wathi kwisihlwele ngaye uYohane, Naye niphume nisiya kubona ntoni na entlango? Yayiyingcongolo na, izanyazanyiswa ngumoya?', 'XHO22'),
(46, 7, 25, 'Naye niphume naya kubona ntoni na kodwa? Yaye ingumntu ovethe iingubo ezitofotofo na? Yabonani, abo banxiba iingubo eziqaqambileyo, badle ubuncwane, ngabasezindlwini zookumkani.', 'XHO22'),
(46, 7, 26, 'Naye niphume naya kubona ntoni na kodwa? Umprofeti? Ewe, ndithi kuni, node wagqithisa kumprofeti.', 'XHO22'),
(46, 7, 27, 'Lo ngulowo kubhaliweyo ngaye, kwathiwa, Yabona, mna ndiyasithuma isithunywa sam ngaphambi kobuso bakho, Esiya kuyilungisa indlela yakho phambi kwakho.', 'XHO22'),
(46, 7, 28, 'Kuba ndithi kuni, Akukho namnye, kwabazelweyo ngabafazi, umprofeti omkhulu kunoYohane umbhaptizi; kanti ke nomncinanana kwabanye ebukumkanini bukaThixo, mkhulu kunaye.', 'XHO22'),
(46, 7, 29, 'Bathi bonke abantu, bakuva, nababuthi berhafu, bagwebela uThixo, bebhaptiziwe ngobhaptizo lukaYohane.', 'XHO22'),
(46, 7, 30, 'Kodwa bona abaFarisi nabaqondisi-mthetho bazitshitshisela icebo likaThixo, kuba bengabhaptizwanga nguye.', 'XHO22'),
(46, 7, 31, 'Ithe ke iNkosi, Ndiya kubafanisa nantoni na ke ngoko abantu besi sizukulwana? kwaye zifana nantoni?', 'XHO22'),
(46, 7, 32, 'Bafana nabantwana abahleli endaweni yembutho, bekhwazana besithi, Sanigwalela, naza anangqungqa; senze isijwili kuni, analila nina.', 'XHO22'),
(46, 7, 33, 'Kuba uzile uYohane umbhaptizi engadli sonka, engaseli wayini; nithi ke, Unedemon.', 'XHO22'),
(46, 7, 34, 'Uzile uNyana woMntu esidla, esela; nithi, Khanibone umntu olidla-kudla, olisela-wayini, umhlobo wababuthi berhafu naboni!', 'XHO22'),
(46, 7, 35, 'Kodwa ubulumko bugwetyelwe kokuphuma ebantwaneni babo bonke.', 'XHO22'),
(46, 7, 36, 'Ke kaloku omnye umFarisi ubemcela ukuba adle naye; waza wangena endlwini yomFarisi lowo, wahlala phantsi.', 'XHO22'),
(46, 7, 37, 'Kwabonakala kukho mfazi emzini apho, obengumoni, esazi ukuba uYesu uhleli endlwini yomFarisi lowo, wazisa ihlala lelabhastile, linamafutha aqholiweyo.', 'XHO22'),
(46, 7, 38, 'Wema ezinyaweni zakhe ngasemva, elila; waqala ukuzinyela iinyawo zakhe ngeenyembezi, ezisula ngeenwele zentloko yakhe, ezanga kakhulu iinyawo zakhe, ezithambisa ngamafutha lawo.', 'XHO22'),
(46, 7, 39, 'Akukubona ke oko umFarisi lowo ubembizile, wathetha ngaphakathi kwakhe, esithi, Lo mntu, ukuba ube engumprofeti, ange esazi ukuba ngumfazi ongubani na, oluhlobo luni na, lo umchukumisayo; ngokuba ungumoni.', 'XHO22'),
(46, 7, 40, 'Uphendule uYesu wathi kuye, Hina, Simon, ndinendawo yokuthetha kuwe. Wathi ke yena, Mfundisi, yitsho.', 'XHO22'),
(46, 7, 41, 'Kwaye kukho mboleki-mali uthile, enabantu ababini abanamatyala kuye; omnye wayenetyala leedenariyo ezimakhulu mahlanu, omnye ke zimashumi mahlanu.', 'XHO22'),
(46, 7, 42, 'Bakuba ke bengenanto yokuhlawula, usuke wabaxolela bobabini. Ndixelele ngoko, nguwuphi na kubo oya kumthanda kakhulu?', 'XHO22'),
(46, 7, 43, 'Waphendula uSimon wathi, Ndiba ngulowo uxolelwe ngokungaphezulu. Wathi ke yena kuye, Ugwebe walungisa.', 'XHO22'),
(46, 7, 44, 'Wajika wakhangela kumfazi, wathi kuSimon, Uyambona na lo mfazi? Ndingene endlwini yakho, akundinikanga manzi eenyawo zam; ke yena uzinyele iinyawo zam ngeenyembezi, wazisula ngeenwele zentloko yakhe.', 'XHO22'),
(46, 7, 45, 'Akundanganga wena; ke yena, kuseloko ndangenayo, akayekanga ukuzanga kakhulu iinyawo zam.', 'XHO22'),
(46, 7, 46, 'Intloko yam akuyithambisanga ngaoli; ke yena iinyawo zam uzithambise ngamafutha aqholiweyo.', 'XHO22'),
(46, 7, 47, 'Ngenxa yoko ndithi kuwe, Izono zakhe uzixolelwe, bezizininzi; ke yena oxolelwe kancinane, uthanda kancinane;', 'XHO22'),
(46, 7, 48, 'Wathi ke kuyo, Zixolelwe izono zakho.', 'XHO22'),
(46, 7, 49, 'Baqala abo babehleli naye esithebeni ukuthi ngaphakathi kwabo, Ngubani na lo oxolela izono?', 'XHO22'),
(46, 7, 50, 'Wathi ke kumfazi, Ukholo lwakho lukusindisile; hamba ngoxolo.', 'XHO22'),
(46, 8, 1, 'Kwathi emveni koko, wayihamba imizi ngemizi nemizana, evakalisa iindaba ezilungileyo zobukumkani bukaThixo. Babenaye abalishumi elinababini.', 'XHO22'),
(46, 8, 2, 'Kwanabafazi abathile, ababephiliswe koomoya abakhohlakeleyo nasebulwelweni, uMariya ekuthiwa nguMagadala, ekwakuphume kuye iidemon ezisixhenxe,', 'XHO22'),
(46, 8, 3, 'noYohana, umfazi kaKuza, igosa likaHerode, noSusana, nabanye abaninzi; bona babemlungiselela ngeempahla zabo.', 'XHO22'),
(46, 8, 4, 'Kuthe ke kwahlanganisana kuye isihlwele esikhulu, besiza kuye bephuma kwimizi ngemizi, ubethetha ngomzekeliso.', 'XHO22'),
(46, 8, 5, 'Umhlwayeli waphuma waya kuyihlwayela imbewu yakhe. Ekuhlwayeleni kwakhe, inxalenye yembewu yawa ngasendleleni; yaza yanyathelwa, zathi iintaka zezulu zayidla zayidla.', 'XHO22'),
(46, 8, 6, 'Embi yawa elulwalweni; ithe ke yahluma, yatsha ngenxa yokungabi namanzi.', 'XHO22'),
(46, 8, 7, 'Embi yawa emithaneni enameva; imithana enameva yakhula kunye nayo, yayiminxa.', 'XHO22'),
(46, 8, 8, 'Yimbi yawa kuwo wona umhlaba olungileyo, yahluma, yavelisa isiqhamo kalikhulu. Uthe akuba ezithethile ezi zinto, wakhala esithi, Lowo uneendlebe zokuva makeve.', 'XHO22'),
(46, 8, 9, 'Bambuza ke abafundi bakhe, besithi, Ungaba uyintoni na lo mzekeliso?', 'XHO22'),
(46, 8, 10, 'Uthe ke yena, Nikuphiwe nina ukuzazi iimfihlelo zobukumkani bukaThixo; ukuze bebona nje bangaboni, besiva nje bangaqondi.', 'XHO22'),
(46, 8, 11, 'Ke kaloku lo mzekeliso uthi: Imbewu lilizwi likaThixo.', 'XHO22'),
(46, 8, 12, 'Aba ngasendleleni ngabo bavayo; andule ke afike uMtyholi, alithabathe ilizwi ezintliziyweni zabo, ukuze bangakholwa, basindiswe.', 'XHO22'),
(46, 8, 13, 'Abo ke baselulwalweni ngabasakuthi, bakuliva, balamkele ngovuyo ilizwi; aba ke abanangcambu, bona bakholwa umzuzwana, ze bathi ngexa lokuhendwa balahle.', 'XHO22'),
(46, 8, 14, 'Leyo ke yawayo emithaneni enameva, ngabo bathi, belivile, bahambe benamaxhala nobutyebi neziyolo zobu bomi, baminxwe, baze bangavelisi siqhamo.', 'XHO22'),
(46, 8, 15, 'Le ke isemhlabeni omhle, ngabo bona bathi ngentliziyo entle, elungileyo, bakuliva ilizwi, balibambe, bavelise iziqhamo benomonde.', 'XHO22'),
(46, 8, 16, 'Akukho ke uthi, esilumekile isibane, asisibeke ngesitya, athi mhlawumbi asibeke ngaphantsi kwesingqengqelo; usixhoma esiphathweni saso, ukuze abo bangenayo bakubone ukukhanya.', 'XHO22'),
(46, 8, 17, 'Kuba akukho nto ifihlakeleyo ingayi kubonakala; kananjalo akukho nto ifihlakeleyo ingayi kwaziwa, ize ekuhleni.', 'XHO22'),
(46, 8, 18, 'Lumkani ngoko ukuva eniva ngako; kuba osukuba enako, uya kunikwa; osukuba ke engenako, uya kuhluthwa kwanoko asukuba ekuko.', 'XHO22'),
(46, 8, 19, 'Ke kaloku kwafika kuye unina nabazalwana bakhe, baza ababa nako ukufikelela kuye ngenxa yendimbane.', 'XHO22'),
(46, 8, 20, 'Wabikelwa ke, kwathiwa, Unyoko nabazalwana bakho bemi ngaphandle, befuna ukukubona.', 'XHO22'),
(46, 8, 21, 'Uphendule ke wathi kubo, Uma nabazalwana bam ngaba bona balivayo ilizwi likaThixo, balenze.', 'XHO22'),
(46, 8, 22, 'Kwathi ngamhla uthile, wangena emkhombeni, kwanabafundi bakhe, wathi kubo, Masiwele siye ngaphesheya kolwandle. Banduluka ke.', 'XHO22'),
(46, 8, 23, 'Ke kaloku, bakubon’ ukuba bayahamba ngomkhombe, walala ubuthongo. Kwehla uqhwithela lomoya edikeni; bazaliswa ngamanzi, baba semngciphekweni.', 'XHO22'),
(46, 8, 24, 'Beza ke bamvusa, besithi, Mfundisi, nkosi, siya kufa. Wavuka ke, wawukhalimela umoya nokulatyuza kwamaza; zapheza, kwabakho ukuzola.', 'XHO22'),
(46, 8, 25, 'Wathi kubo, Luphi na ukholo lwenu? Basuka boyika, bamangaliswa, batshono ukuthi, Ngubani na lo? kuba uyalela kwanemimoya namanzi, zimthobele.', 'XHO22'),
(46, 8, 26, 'Bafika kumhlaba wamaGadara, okhangelene nelaseGalili.', 'XHO22'),
(46, 8, 27, 'Akuba ephumile emhlabeni, wahlangatyezwa ngumntu othile ophuma esixekweni, obeneendimoni ixesha elide, enganxibanga ngubo imbi, engahlali endlwini, emane esemangcwabeni.', 'XHO22'),
(46, 8, 28, 'Ithe ke yakumbona uYesu, yakhala kunene, yawa phambi kwakhe, yathi ngezwi elikhulu, Yini na enam nawe, Yesu, Nyana kaThixo Osenyangweni? Ndiyakukhunga, ungandithuthumbisi.', 'XHO22'),
(46, 8, 29, '(Kuba wayewuthethele umoya ongcolileyo ukuba uphume emntwini; kuba futhi wawumbamba, ekhonkxiwe ngamakhamandela namakhamandela, waye wawaqhawula amakhamandela, wakhutshelwa entlango nguMtyholi.)', 'XHO22'),
(46, 8, 30, 'uYesu wambuza esithi, Ungubani na igama lakho? Uthe ke yena, NdinguMkhosi; ngokuba bekungene kuye iidemon ezininzi.', 'XHO22'),
(46, 8, 31, 'Wayembongoza ukuba angazithetheli ziye kungena enzonzobileni.', 'XHO22'),
(46, 8, 32, 'Bekukho ke apho ugxuba lweehagu ezininzi, lusidla entabeni; zaye zimbongoza, ukuba azivumele ziye kungena kuzo. Wabavumela ke.', 'XHO22'),
(46, 8, 33, 'Ziphumile ke iidemon emtwini lowo, zangena ezihagwini; lwaza ugxuba olo lwaphaphatheka, lwehla eweni, lwela edikeni, lwarhaxwa.', 'XHO22'),
(46, 8, 34, 'Bakukubona ke okuhlileyo abo babezalusa, basaba, baya babika ekhaya nasemaphandleni.', 'XHO22'),
(46, 8, 35, 'Baphuma ke abantu, baya kukubona okuhlileyo; beza kuYesu, bamfumana ehleli umntu lowo, beziphume kuye iidemon, ambathisiwe, enengqondo ephilileyo, engasezinyaweni zikaYesu; baza boyika.', 'XHO22'),
(46, 8, 36, 'Baza nabo babeyibonile le nto bababikela ukuba utheni na ukuphiliswa obephethwe ziidemon.', 'XHO22'),
(46, 8, 37, 'Yamcela yonke inkitha yommandla wabaseGadara, ukuba emke kubo; kuba babebanjwe kukoyika okukhulu. Wenyuka ke waya emkhombeni, wabuya wabuya.', 'XHO22'),
(46, 8, 38, 'Ke kaloku indoda leyo, zaye ziphume kuyo iidemon, yayimkhunga ukuba ibe naye.', 'XHO22'),
(46, 8, 39, 'Buyela endlwini yakho, ubacacisele oko zingako izinto uThixo akwenzele zona. Wemka, wavakalisa kuwo wonke umzi oko zingako izinto uYesu ayenzele zona.', 'XHO22'),
(46, 8, 40, 'Ekubuyeni kwakhe uYesu, iindimbane zamamkela ngovuyo; kuba bonke babemlindile.', 'XHO22'),
(46, 8, 41, 'Kwabonakala kusiza indoda egama linguYayiro, yaye yona ingumphathi wendlu yesikhungu, yawa ezinyaweni zikaYesu, yambongoza ukuba aye kungena endlwini yayo.', 'XHO22'),
(46, 8, 42, 'Kuba yabe inentombi, iyizala inye, eminyaka ngathi ilishumi linamibini izelwe, yaye yona iqhawuka. Ke kaloku, akubon’ ukuba uyahamba abantu bamxina.', 'XHO22'),
(46, 8, 43, 'Kwaye umfazi owayenethombo legazi iminyaka elishumi elinamibini, eyayichithe yonke impilo yakhe kumagqirha, ebengenako ukuphiliswa bani,', 'XHO22'),
(46, 8, 44, 'yeza ngasemva, yachukumisa umqukumbelo wengubo yakhe, lee qoko kwangoko ithombo legazi lakhe.', 'XHO22'),
(46, 8, 45, 'Wathi uYesu, Ngubani na lo undichukumisileyo? Bakhanyela bonke, wathi uPetros nabo babenaye, Mongameli, isihlwele sikuxinile, sikuxinile, utsho na ukuthi, Ngubani na lo undichukumisileyo?', 'XHO22'),
(46, 8, 46, 'Uthe ke uYesu, Ukho ondichukumisileyo; kuba mna ndiyawazi amandla aphumileyo kum.', 'XHO22'),
(46, 8, 47, 'Ithe ke intokazi leyo, yakubona ukuba ayifihlakele, yeza igubha, yawa phambi kwakhe, yaxela emehlweni abo bonke abantu, ukuba bekungasizathu sini na ebimchukumisile, nokuphiliswa kwayo kwangoko.', 'XHO22'),
(46, 8, 48, 'Uthe ke yena kuyo, Yomelela, ntombi yam, ukholo lwakho lukusindisile; hamba ngoxolo.', 'XHO22'),
(46, 8, 49, 'Kwathi, esathetha, kufika othile, evela kwamphathi wendlu yesikhungu, esithi kuye, Ibhubhile intombi yakho; musani ukumkhathaza uMfundisi.', 'XHO22'),
(46, 8, 50, 'Evile ke uYesu, wamphendula wathi, Musa ukoyika; kholwa kuphela; wophiliswa.', 'XHO22'),
(46, 8, 51, 'Engenile ke endlwini, akavumelanga mntu ukuba angene naye, kwaba kuphela nguPetros, noYakobi, noYohane, noyise wentombi, nonina.', 'XHO22'),
(46, 8, 52, 'Baye ke bonke balila, beyimbambazelela. Wathi ke yena, Musani ukulila; Akafile, ulele.', 'XHO22'),
(46, 8, 53, 'Basuka bamwa ngeentsini, besazi ukuba ifile.', 'XHO22'),
(46, 8, 54, 'Uthe ke yena, wabakhuphela phandle bonke, wayibamba ngesandla, wabiza esithi, Ntombi, vuka.', 'XHO22'),
(46, 8, 55, 'Wabuya umoya wayo, yesuka yema kwangoko;', 'XHO22'),
(46, 8, 56, 'Bakhwankqiswa ke abazali bayo; wabayala ukuba bangayixeleli bani le nto yenzekileyo.', 'XHO22'),
(46, 9, 1, 'Ke kaloku, ebabizele ndawonye abafundi bakhe abalishumi elinababini, wabanika amandla negunya kuzo zonke iidemon, nokuphilisa izifo.', 'XHO22'),
(46, 9, 2, 'Wabathuma ukuba babuvakalise ubukumkani bukaThixo, bayiphilise imilwelwe.', 'XHO22'),
(46, 9, 3, 'Wathi kubo, Ningaphathi nto yandlela, namisimelelo, nangxowa yamphako, nasonka, namali; ningabi nangubo zangaphantsi ngambini;', 'XHO22'),
(46, 9, 4, 'Nendlu enithe nangena kuyo, hlalani khona, nimke khona.', 'XHO22'),
(46, 9, 5, 'Abo sukuba benganamkeli, yithini nakuphuma kuloo mzi, niluvuthulule nothuli ezinyaweni zenu, kube bubungqina oko kubo.', 'XHO22'),
(46, 9, 6, 'Bephumile ke, bayihambahamba imizi, bezishumayela iindaba ezilungileyo, bephilisa ezindaweni zonke.', 'XHO22'),
(46, 9, 7, 'Ke kaloku uHerode, umphathi wesahlulo sesiné selizwe, waziva zonke izinto ezenziwayo nguye;', 'XHO22'),
(46, 9, 8, 'kuthiwe ke ngabanye, Kubonakale uEliya; kuthiwe ngabanye, Kubuye kwavuka umprofeti wakuwo amanyange.', 'XHO22'),
(46, 9, 9, 'Wathi uHerode, uYohane mna ndamnqamla intloko; lo ke ngubani na, ndiva kuthethwa izinto ezinje ngaye? Wayefuna ke ukumbona.', 'XHO22'),
(46, 9, 10, 'Bebuyile abapostile, bamcacisela ukuba zinkulu kwezinto ababezenzile. Wabathabatha ke, wemka waya nabo ngasese kwindawo eyintlango yomzi ekuthiwa yiBhetesayida.', 'XHO22'),
(46, 9, 11, 'Sithe ke isihlwele, yakukwazi oko, samlandela; waza wabamkela, wathetha kubo ngobukumkani bukaThixo, wabaphilisa ababeswele ukuphila.', 'XHO22'),
(46, 9, 12, 'Ke kaloku, kwakubon’ ukuba selithambekile ke ilanga, beza abalishumi elinababini, bathi kuye, Zindulule izihlwele, ukuze ziye emizini nasemaphandleni, zilale, zifumane ukudla; ngokuba sisentlango apha indawo.', 'XHO22'),
(46, 9, 13, 'Uthe ke yena kubo, Bapheni nina batye. Bathi ke bona, Asisenanto ingeyiyo izonka ezihlanu neentlanzi ezimbini; ngaphandle kokuba siye kubathengela inyama aba bantu bonke.', 'XHO22'),
(46, 9, 14, 'Kuba amadoda ngathi ayemawaka mahlanu. Wathi ke kubafundi bakhe, Bangqengqise babe ngamaqela ngamashumi ngamahlanu, babe ngamahlanu.', 'XHO22'),
(46, 9, 15, 'Benjenjalo, babangqengqisa bonke.', 'XHO22'),
(46, 9, 16, 'Ezithabathile ke izonka zozihlanu neentlanzi zombini, wakhangela ezulwini, wazisikelela, waqhekeza; wamana enika abafundi, ukuba babeke ezihlweleni.', 'XHO22'),
(46, 9, 17, 'Batya bahlutha bonke, kwaza kwasuswa okuseleyo kubo izitya zamaqhekeza zalishumi elinazibini.', 'XHO22'),
(46, 9, 18, 'Ke kaloku kwathi, ekubeni ethandaza eyedwa, bantontelana kuye abafundi, wabuza kubo, esithi, Izihlwele ezi zithi ndingubani na?', 'XHO22'),
(46, 9, 19, 'Baphendula ke bona bathi, UnguYohane umbhaptizi; abanye ke bathi, NguEliya; abanye ke bathi, kubuye kwavuka umprofeti wakuwo amanyange.', 'XHO22'),
(46, 9, 20, 'Athi kubo, Ke nina nithi ndingubani na? Waphendula ke uPetros wathi, NguKristu kaThixo.', 'XHO22'),
(46, 9, 21, 'Wabayala kalukhuni, ebathethela ngelithi, le nto mabangayithethi nakubani;', 'XHO22'),
(46, 9, 22, 'esithi, uNyana woMntu umelwe kukuba eve ubunzima obukhulu, alahlwe ngawo amadoda amakhulu nababingeleli abakhulu nababhali, abulawe, athi ngomhla wesithathu avuke.', 'XHO22'),
(46, 9, 23, 'Wayesithi ke kubo bonke, Ukuba ubani uthanda ukundilandela, makazincame, awuthwale umnqamlezo wakhe imihla ngemihla, andilandele ke.', 'XHO22'),
(46, 9, 24, 'Kuba othe wathanda ukuwusindisa umphefumlo wakhe, wolahlekelwa nguwo; ke othe wawulahla umphefumlo wakhe ngenxa yam, wowusindisa lowo.', 'XHO22'),
(46, 9, 25, 'Kumnceda ntoni na umntu ukuthi azuze ihlabathi liphela, abe ke ezilahlile, mhlawumbi ezonakalisile?', 'XHO22'),
(46, 9, 26, 'Kuba othe waneentloni ngam, nangawo amazwi am, uNyana woMntu woba neentloni ngaye lowo, xa athe weza esebuqaqawulini bakhe, nobukaYise, nobezithunywa ezingcwele.', 'XHO22'),
(46, 9, 27, 'Ndithi ke kuni, Inene, kukho abathile kwabemiyo apha, abangayi kukha beve kufa bona, bade babubone ubukumkani bukaThixo.', 'XHO22'),
(46, 9, 28, 'Ke kaloku kuthe, emveni kwezi zinto, malunga neentsuku ezisibhozo, wathabatha uPetros noYohane noYakobi; wenyuka wava entabeni, esiya kuthandaza.', 'XHO22'),
(46, 9, 29, 'Kwathi, ekuthandazeni kwakhe, ubuso bakhe babonakala bububumbi, nengubo yakhe yamhlophe yabengezela.', 'XHO22'),
(46, 9, 30, 'Kwabonakala amadoda amabini ethetha naye, angooMoses noEliya,', 'XHO22'),
(46, 9, 31, 'ababebonakala besebuqaqawulini; babethetha ngophumo lwakhe, abeza kulufeza eYerusalem.', 'XHO22'),
(46, 9, 32, 'Ke kaloku uPetros, nabo babenaye, babesindwa bubuthongo; baza ke bakuthi balulu, babubona ubuqaqawuli bakhe, naloo madoda mabini abemi naye.', 'XHO22'),
(46, 9, 33, 'Ekwahlukaneni kwawo kuye, wathi uPetros kuye uYesu, Mongameli, kuhle ukuba sibe lapha, omnye ube ngowakho, omnye ube ngokaMoses, omnye ube ngokaEliya, engayazi into ayithethayo.', 'XHO22'),
(46, 9, 34, 'Kuthe ke esathetha kwabakho ilifu, labenzela ithinzi. Boyika ke ekungeneni kwabo efini apho.', 'XHO22'),
(46, 9, 35, 'Kwabakho nezwi liphuma efini, lisithi, Lo nguNyana wam oyintanda; mveni yena.', 'XHO22'),
(46, 9, 36, 'Lakuba libekho izwi elo, wafunyanwa uYesu eyedwa. Bagcina ke bona, ababikela mntu ngaloo mihla kwizinto ababezibonile.', 'XHO22'),
(46, 9, 37, 'Kwathi ngengomso, bakubon’ ukuba bayehla entabeni, wahlangatyezwa sisihlwele esikhulu.', 'XHO22'),
(46, 9, 38, 'Kwabonakala kuvela indoda esihlweleni apho, yankqangaza isithi, Mfundisi, ndiyakukhunga, mbone unyana wam;', 'XHO22'),
(46, 9, 39, 'Uyabona, ubanjwe ngumoya, akhale ngesiquphe; isuke imkrazule, alenze amagwebu, kube nzima ukuba emke kuye.', 'XHO22'),
(46, 9, 40, 'Ndabakhunga ke abafundi bakho ukuba bawukhuphe; ababa nako.', 'XHO22'),
(46, 9, 41, 'Waphendula uYesu wathi, Sizukulwanandini singakholwayo, sigwenxa, koda kube nini na ndikuni, ndininyamezele? Mzise apha unyana wakho.', 'XHO22'),
(46, 9, 42, 'Akubon’ ukuba useza ke, isuke idemon yamntlala, yambetha, wathi xhwenene kunene. Wawukhalimela uYesu umoya ongcolileyo, waphila umntwana, wambuyisela kuyise.', 'XHO22'),
(46, 9, 43, 'Bakhwankqiswa ke bonke bubungangamela bukaThixo. Bakubon’ ukuba bamangalisiwe bonke zizinto zonke awazenzayo uYesu, wathi kubafundi bakhe.', 'XHO22'),
(46, 9, 44, 'Wabekeleni nina iindlebe la mazwi; kuba uNyana woMntu uza kunikelwa ezandleni zabantu.', 'XHO22'),
(46, 9, 45, 'Kodwa bona babengaliqondi eli, lizwi; laye lifihliwe kubo, ukuze bangaliboni. Babesoyika nokumbuza ngelo lizwi.', 'XHO22'),
(46, 9, 46, 'Kwabakho ke ukubuzana phakathi kwabo, kokuthi, nguwuphi na kubo omkhulu;', 'XHO22'),
(46, 9, 47, 'Ekubonile ke uYesu ukucamanga kwentliziyo yabo, uthabathe umntwanana, wammisa ngakuye.', 'XHO22'),
(46, 9, 48, 'Wathi kubo, Othe wamamkela lo mntwanana egameni lam, wamkela mna; nothe wamkela mna, wamkela owandithumayo; kuba lowo umncinane phakathi kwenu nonke, ngoyena mkhulu.', 'XHO22'),
(46, 9, 49, 'Uphendule ke uYohane wathi, Mfundisi, sabona umntu ekhupha iidemon egameni lakho; samalela ke, ngokuba engasilandeli thina.', 'XHO22'),
(46, 9, 50, 'uYesu wathi kuye: “Musani ukumalela, kuba ongachasiyo ungecala lethu.', 'XHO22'),
(46, 9, 51, 'Ke kaloku kwathi, lakufika ixesha lokunyuselwa kwakhe phezulu, wazimisela ngokuqinileyo ukuya eYerusalem.', 'XHO22'),
(46, 9, 52, 'Wathuma abathunywa phambi kobuso bakhe; baya ke, bangena kwidolophana yamaSamariya, ukuba bamlungiselele.', 'XHO22'),
(46, 9, 53, 'Akamamkela, ngokuba ubuso bakhe bebubheke eYerusalem.', 'XHO22'),
(46, 9, 54, 'Bekubonile ke oko abafundi bakhe, ooYakobi noYohane bathe, Nkosi, uyafuna na ukuba sitsho, kuhle umlilo ezulwini, ubatshise kuphele, njengoko noEliya wenjenjalo?', 'XHO22'),
(46, 9, 55, 'Wathi guququ, wabakhalimela, wathi, Anazi ukuba ningabomoya onjani na;', 'XHO22'),
(46, 9, 56, 'kuba uNyana woMntu engaze kutshabalalisa miphefumlo yabantu, uze kusindisa. Baza baya mzaneni wumbi;', 'XHO22'),
(46, 9, 57, 'Ke kaloku kwathi, bakubon’ ukuba bayahamba ngendlela, wathi othile kuye, Ndiya kukulandela, apho uthe waya khona, Nkosi.', 'XHO22'),
(46, 9, 58, 'Wathi uYesu kuye, Iimpungutye zinemingxuma, neentaka zezulu zineendlwana; yena uNyana woMntu akanayo nendawo angalalisa kuyo intloko.', 'XHO22'),
(46, 9, 59, 'Wathi ke kuwumbi, Ndilandele. Uthe ke yena, Nkosi, ndivumele ndiye kungcwaba ubawo kuqala.', 'XHO22'),
(46, 9, 60, 'Wathi uYesu kuye, Bayeke abafileyo, bangcwabe abafileyo babo; ke wena hamba uye kushumayela ubukumkani bukaThixo.', 'XHO22'),
(46, 9, 61, 'Wathi ke nomnye, Ndiya kukulandela, Nkosi; kodwa ndivumele ndiye kuqala, ndiye kubulisa abo basendlwini yam.', 'XHO22'),
(46, 9, 62, 'Uthe ke uYesu kuye, Akukho namnye usibekileyo isandla sakhe epuluweni, abe emana ekhangela kokusemva, ubulungeleyo ubukumkani bukaThixo.', 'XHO22'),
(46, 10, 1, 'Ke kaloku, emveni kwezi zinto, iNkosi yabalula yamisela abanye kanjalo abamashumi asixhenxe, yabathuma ngababini ngababini phambi kobuso bayo, kuyo yonke imizi neendawo ebiza kuya kuzo yona.', 'XHO22'),
(46, 10, 2, 'Yayisithi ngoko kubo, Ukuvuna okunene kuninzi, kodwa bona abasebenzi bambalwa; khungani ngoko eNkosini yokuvuna, ikhuphe abasebenzi, baye ekuvuneni kwayo.', 'XHO22'),
(46, 10, 3, 'Hambani; niyabona, mna ndinithuma njengeemvana phakathi kweengcuka.', 'XHO22'),
(46, 10, 4, 'Musani ukuphatha ngxowa yamali, nangxowa yamphako, nazimbadada, yaye ningabulisi mntu endleleni.', 'XHO22'),
(46, 10, 5, 'Endlwini enisukuba ningene kuyo, yithini kuqala, Uxolo malube kule ndlu.', 'XHO22'),
(46, 10, 6, 'Ukuba angaba ulapho owoxolo, uxolo lwenu lohlala phezu kwakhe; ukuba ke akunjalo, lobuyela kwakuni.', 'XHO22'),
(46, 10, 7, 'Hlalani ke kuloo ndlu, nisidla nisela izinto abanazo; kuba umsebenzi uwufanele umvuzo wakhe. Musani ukuphuma niye kwindlu ngendlu.', 'XHO22'),
(46, 10, 8, 'Emzini ke enisukuba ningena kuwo, banamkele, yidlani izinto abazibeka phambi kwenu;', 'XHO22'),
(46, 10, 9, 'Niyiphilise imilwelwe ekhona, nithi kubo, Busondele kuni ubukumkani bukaThixo.', 'XHO22'),
(46, 10, 10, 'Emzini ke enithe nangena kuwo, baza àbanamkeli, phumani niye ezitratweni zawo, nithi,', 'XHO22'),
(46, 10, 11, 'Kwanothuli lomzi wenu oluncamileyo, siyaluvuthululela kuni; noko ke kwazini oko, ukuba busondele kuni ubukumkani bukaThixo.', 'XHO22'),
(46, 10, 12, 'Ke ndithi kuni, Koba nokunyamezeleka okweSodom ngaloo mini, kunokwaloo mzi.', 'XHO22'),
(46, 10, 13, 'Yeha, wena Korazin! Yeha, wena Bhetesayida! Ngokuba imisebenzi yamandla eyenzeke kuni, ukuba yayenzeke eTire naseTsidon, ingeyaguqukayo kwakudala loo mizi, ihleli ngeengubo ezirhwexayo naseluthuthwini.', 'XHO22'),
(46, 10, 14, 'Kodwa koba nokunyamezeleka okweTire nokweTsidon ngomgwebo kunani.', 'XHO22'),
(46, 10, 15, 'Nawe Kapernahum, uphakanyiselweyo ezulwini, uya kuphoswa phantsi kwelabafileyo.', 'XHO22'),
(46, 10, 16, 'Lowo univayo uva mna; Lowo unigibayo, ugiba mna; lowo ke ugiba mna, ugiba owandithumayo.', 'XHO22'),
(46, 10, 17, 'Babuya ke abamashumi asixhenxe bevuya, besithi, Nkosi, needemon ezi zisithobele egameni lakho.', 'XHO22'),
(46, 10, 18, 'Uthe ke kubo, Bendimbona uSathana njengombane esiwa ezulwini.', 'XHO22'),
(46, 10, 19, 'Niyabona, ndininika igunya lokunyathela phezu kweenyoka noonomadudwane, naphezu kwawo onke amandla alo utshaba; kanjalo akukho nto ingakhe inenzakalise.', 'XHO22'),
(46, 10, 20, 'Noko ke maningakuvuyeli oku, kokuba oomoya benithobele; yithini kanye ke kugcobani; ngokuba amagama enu ebhaliwe emazulwini.', 'XHO22'),
(46, 10, 21, 'Ngelo lixa wagcoba uYesu ngomoya, wathi, Ndiyabulela kuwe, Bawo, Nkosi yezulu nomhlaba; ngokuba ezi zinto uzifihlile kwizilumko nakwiingqondi, wazityhilela iintsana; ngokuba kwakholeka phambi kwakho ukuba kube njalo.', 'XHO22'),
(46, 10, 22, 'Zonke izinto zinikelwe kum nguBawo; kanjalo akukho waziyo ukuba ungubani na uNyana, nguYise yedwa; nokuba ungubani na uYise, nguNyana yedwa, nalowo asukuba uNyana ethanda ukumtyhilela.', 'XHO22'),
(46, 10, 23, 'Wathi guququ kubafundi bakhe, wathi ngasese, Anethamsanqa wona amehlo abona izinto enizibonayo.', 'XHO22'),
(46, 10, 24, 'Kuba ndithi kuni, Baninzi abaprofeti nookumkani ababethande ukubona izinto enizibonayo abazibona; nokuva izinto enizivayo, anaziva.', 'XHO22'),
(46, 10, 25, 'Kwabonakala kusuka umqondisi-mthetho othile, emlinga nokumlinga, esithi, Mfundisi, ndenze ntoni na ukuze ndibudle ilifa ubomi obungunaphakade?', 'XHO22'),
(46, 10, 26, 'Uthe ke yena kuye, Emthethweni kubhalwe ntoni na? Ufunda njani?', 'XHO22'),
(46, 10, 27, 'Waphendula ke wathi, Uze uyithande iNkosi uThixo wakho ngentliziyo yakho iphela, nangomphefumlo wakho uphela, nangamandla akho ephela, nangengqondo yakho iphela, nommelwane wakho njengoko uzithanda ngako.', 'XHO22'),
(46, 10, 28, 'Uthe ke kuye, Uphendule walungisa; yenza oko, wobudla ubomi.', 'XHO22'),
(46, 10, 29, 'Ke yena, ethanda ukuzigwebela, wathi kuYesu, Ngubani na ongummelwane wam?', 'XHO22'),
(46, 10, 30, 'Uphendule ke uYesu, wathi, Umntu othile wayesihla evela eYerusalem, esiya eYeriko. Wagaxeleka ezihangeni, ezamhluba iingubo, zamngxwelerha, zemka, zimshiya engathi uza kufa.', 'XHO22'),
(46, 10, 31, 'Ke kaloku kwathi, kwehla mbingeleli uthile ngaloo ndlela, wathi akumbona wacezela phaya.', 'XHO22'),
(46, 10, 32, 'Kwangokunjalo ke nomLevi, akufika kuloo ndawo, weza wambona, wacezela phaya.', 'XHO22'),
(46, 10, 33, 'Uthe ke umSamariya othile, eseluhambeni lwakhe, wafika kuye;', 'XHO22'),
(46, 10, 34, 'Waya kuye, wawabopha amanxeba akhe, egalela ioli newayini, wamkhwelisa eqegwini lakhe, wamsa endlwini yabahambi, wamonga.', 'XHO22'),
(46, 10, 35, 'Akumka ngengomso, warhola iidenariyo zambini wanika umninindlu, wathi kuye, Uze umonge; nento othe wadleka kuyo, ndokubuyekeza ekubuyeni kwam.', 'XHO22'),
(46, 10, 36, 'Nguwuphi na kwabo bathathu ngoko, othi wena, waba ngummelwane walowo wagaxeleka ezihangeni?', 'XHO22'),
(46, 10, 37, 'Uthe ke yena, Ngulowo wamenzela inceba. Wathi ngoko uYesu kuyo, Hamba uye kwenjenjalo nawe.', 'XHO22'),
(46, 10, 38, 'Ke kaloku kwathi, ekuhambeni kwabo, yena wangena mzini uthile, yathi ke intokazi ethile egama linguMarta, yamamkela ngobubele endlwini yayo.', 'XHO22'),
(46, 10, 39, 'Yaye ke inodade wabo ogama linguMariya, owathi yena wahlala ngasezinyaweni zikaYesu, waliphulaphula ilizwi lakhe.', 'XHO22'),
(46, 10, 40, 'uMarta ke yena wakruthakrutheka kukulungiselela okuninzi. Wema ke kufuphi wathi, Nkosi, akukhathali na, kukuba umsakwethu andiyekele ndilungiselele ndedwa? Yitsho kuye ukuba andincede.', 'XHO22'),
(46, 10, 41, 'Waphendula ke uYesu wathi kuye, Marta, Marta, uzixhalabise ngezinto ezininzi;', 'XHO22'),
(46, 10, 42, 'Inye yona into efunekayo; uMariya ke unyule eso sabelo silungileyo, singayi kususwa kuye.', 'XHO22'),
(46, 11, 1, 'Kwathi, esendaweni ethile ethandaza, akupheza, wathi omnye umfundi wakhe kuye, Nkosi, sifundise ukuthandaza, kwanjengoko uYohane wabafundisayo abafundi bakhe.', 'XHO22'),
(46, 11, 2, 'Wathi kubo, Xa sukuba nithandaza, yithini, Bawo wethu osemazulwini, malingcwaliswe igama lakho. Mabufike ubukumkani bakho. Makwenzeke ukuthanda kwakho nasemhlabeni, njengokuba kusenziwa emazulwini.', 'XHO22'),
(46, 11, 3, 'Siphe imihla ngemihla isonka sethu semihla ngemihla.', 'XHO22'),
(46, 11, 4, 'Sixolele izono zethu; kuba nathi sibaxolela bonke abanamatyala kuthi. Ungasingenisi ekuhendweni; usihlangule ebubini.', 'XHO22'),
(46, 11, 5, 'Wathi kubo, Nguwuphi na kuni ongathi, enomhlobo, aze aye kuye phakathi kobusuku, athi kuye, Sihlobo, ndiboleke izonka zibe zithathu;', 'XHO22'),
(46, 11, 6, 'kuba ke umhlobo wam uphambukele kum eseluhambeni, ndaye ndingenanto ke ndingayibeka phambi kwakhe?', 'XHO22'),
(46, 11, 7, 'Aphendule lowo wangaphakathi athi, Musa ukundikhathaza; seluvaliwe ucango, nabantwana bam basesililini nam; andinako ukuvuka ndikunike.', 'XHO22'),
(46, 11, 8, 'Ndithi kuni, Nokuba akayi kuvuka amnike, ngakuba engumhlobo wakhe, noko ngenxa yozingiso lwakhe olungenantloni wovuka, amnike inani alisweleyo.', 'XHO22'),
(46, 11, 9, 'Nam ndithi kuni, Celani, nophiwa; funani, nofumana; nkqonkqozani, novulelwa.', 'XHO22'),
(46, 11, 10, 'Kuba wonke umntu ocelayo, uyazuza; nalowo ufunayo, uyafumana; nalowo unkqonkqozayo uya kuvulelwa.', 'XHO22'),
(46, 11, 11, 'Nguwuphi na ke uyise kuni oya kuthi, unyana akucela isonka, amnike ilitye; Athi, ukuba uthe wacela intlanzi, amnike inyoka esikhundleni sentlanzi?', 'XHO22'),
(46, 11, 12, 'Athi, ukuba uthe wacela iqanda, amnike unomadudwane?', 'XHO22'),
(46, 11, 13, 'Ukuba ke ngoko nina, nikhohlakele nje, niyakwazi ukubapha abantwana benu izipho ezilungileyo, wobeka phi na ke yena uYihlo osemazulwini ukugqithisela ukubapha uMoya oyiNgcwele abo bamcelayo?', 'XHO22'),
(46, 11, 14, 'Wayekhupha idemon, yaye ke isisidenge. Kwathi, yakuphuma idemon, sathetha isisidenge; bamangaliswa abantu.', 'XHO22'),
(46, 11, 15, 'Kodwa abathile kubo bathi, Uzikhupha iidemon ngoBhelezebhule, umphathi weedemon.', 'XHO22'),
(46, 11, 16, 'Baye ke bemlinga, befuna kuye umqondiso ophuma ezulwini.', 'XHO22'),
(46, 11, 17, 'Ke kaloku yena, ezazi iingcamango zabo, wathi kubo, Bonke ubukumkani obahlulelene bodwa buyaphanza; nendlu eyahlulelene nendlu, iyawa.', 'XHO22'),
(46, 11, 18, 'Ukuba ke noSathana wahlulelene yedwa, bothini na ukuma ubukumkani bakhe? Ngokuba nithi, ndizikhupha iidemon ngoBhelezebhule.', 'XHO22'),
(46, 11, 19, 'Ukuba mna ndizikhupha iidemon ngoBhelezebhule, bazikhupha ngabani na onyana benu? ngenxa yoko baya kuba ngabagwebi benu bona.', 'XHO22'),
(46, 11, 20, 'Ke ukuba ndizikhupha iidemon ngomnwe kaThixo, ngoko bunifikele kanti ubukumkani bukaThixo.', 'XHO22'),
(46, 11, 21, 'Xa igorha lixhobile, lilinda ibhotwe lalo, impahla yalo iseluxolweni;', 'XHO22'),
(46, 11, 22, 'Xa lithe lafika igorha elinamandla kunalo, laloyisa, lolihlutha zonke izikrweqe zalo ebelikholose ngazo, liwabe amaxhoba alo.', 'XHO22'),
(46, 11, 23, 'Lowo ungengakum, uchasene nam; nalowo ungandibuthisiyo, uyachithachitha.', 'XHO22'),
(46, 11, 24, 'Xa umoya ongcolileyo uthe waphuma emntwini, ucanda ezindaweni ezingenamanzi, ufuna ukuphumla; uze wakuba ungakufumani, usuke uthi, Ndiya kubuyela kwasendlwini yam endaphuma kuyo.', 'XHO22'),
(46, 11, 25, 'Uthi wakufika, uyifumane itshayelwe, ivathisiwe.', 'XHO22'),
(46, 11, 26, 'Wandule ukuya kuthabatha abanye oomoya abasixhenxe abakhohlakeleyo kunawo, bangene ke bahlale khona; bangene ke bahlale khona; lize ikamva laloo mntu libe libi kunesiqaleko sakhe.', 'XHO22'),
(46, 11, 27, 'Ke kaloku kwathi, ekuzithetheni kwakhe ezi zinto, intokazi ethile yaliphakamisa izwi layo esihlweleni apho, yathi kuye, Sinoyolo isizalo esakubambayo, namabele owawanyayo.', 'XHO22'),
(46, 11, 28, 'Uthe ke yena, Yithi kanye, Banoyolo abo balivayo ilizwi likaThixo baligcine.', 'XHO22'),
(46, 11, 29, 'Ke kaloku, zakubon’ ukuba ziqukene iindimbane, waqala ukuthi, Esi sizukulwana singendawo, singxamele umqondiso; kwaye asiyi kunikwa mqondiso, ingenguwo umqondiso kaYona umprofeti.', 'XHO22'),
(46, 11, 30, 'Kuba, njengokuba uYona waba ngumqondiso kwabaseNineve, uya kuba kwanjalo uNyana woMntu kwesi sizukulwana.', 'XHO22'),
(46, 11, 31, 'Ukumkanikazi waseZantsi uya kusuka eme ngomgwebo namadoda esi sizukulwana, awagwebe; ngokuba wavela eziphelweni zomhlaba, esiza kuva ubulumko bukaSolomon; ukanti ke omkhulu kunoSolomon nanku apha.', 'XHO22'),
(46, 11, 32, 'Amadoda aseNineve aya kusuka eme ngomgwebo nesi sizukulwana, asigwebe; ngokuba aguquka wona ngomemezo lukaYona; ukanti ke omkhulu kunoYona nanku apha.', 'XHO22'),
(46, 11, 33, 'Akukho ke uthi, esibanekile isibane, asibeke endaweni efihlakeleyo, nokuba kuphantsi kwesitya; usibeka esiphathweni saso, ukuze abo bangenayo bakubone ukukhanya.', 'XHO22'),
(46, 11, 34, 'Isibane somzimba liliso. Xa sukuba iliso lakho lingenakumbi, umzimba wakho uphela uyakhanyiseleka; kodwa xa sukuba iliso lakho lingendawo, umzimba wakho uba bubumnyama.', 'XHO22'),
(46, 11, 35, 'Ngoko xunela ekuthini ukukhanya okukuwe kungabi bubumnyama.', 'XHO22'),
(46, 11, 36, 'Ukuba ngoko umzimba wakho uphela ukhanyiselekile, ungenandawo imnyama, uya kukhanyiseleka uphela, njengaxenikweni sithe isibane sakukhanyisela ngokudangazela kwaso.', 'XHO22'),
(46, 11, 37, 'Ke kaloku, akuba ethethile, umFarisi othile wayemcela ukuba aye komulela kuye. Ungenile ke wahlala phantsi.', 'XHO22'),
(46, 11, 38, 'Uthe ke akubona oku, umFarisi wamangaliswa kukuba engathanga kuqala ahlambe, phambi kokuba adle.', 'XHO22'),
(46, 11, 39, 'Yathi ke iNkosi kuye, Kungokunje, nina baFarisi nihlambulula umphandle wendebe nesitya; kodwa umbilini wakho uzele kukuphanga nokungendawo.', 'XHO22'),
(46, 11, 40, 'Ziyatha, lo wawenza ingaphandle akawenzanga na owangaphakathi?', 'XHO22'),
(46, 11, 41, 'Kodwa ke yenzani amalizo ngokwezinto eninazo; niyabona, zonke izinto zihlambulukile kuni.', 'XHO22'),
(46, 11, 42, 'Yeha nina, baFarisi! Ngokuba ninikela izishumi zetyeleba nezenxina nezemifuno yonke, nize nidlule kuwo umgwebo nokuthanda uThixo; ezi benimelwe kukuthi nizenze, nithi neziya ningazishiyi.', 'XHO22'),
(46, 11, 43, 'Yeha nina, baFarisi! Kuba nithanda izihlalo eziphambili ezindlwini zesikhungu, nemibuliso ezindaweni zembutho.', 'XHO22'),
(46, 11, 44, 'Yeha nina, babhali nabaFarisi, bahanahanisindini! ngokuba ninjengamangcwaba angabonakaliyo, abahambá phezu kwawo bengawazi.', 'XHO22'),
(46, 11, 45, 'Waphendula ke omnye wabaqondisi-mthetho, wathi kuye, Mfundisi, uzithethile nje ezo zinto, usiphatha kakubi nathi.', 'XHO22'),
(46, 11, 46, 'Uthe ke yena, Yeha, nani baqondisi-mthetho! Kuba abantu nibathwalisa imithwalo enzima, nibe ke nina ngokwenu ningayichukumisi imithwalo leyo nangamnye umnwe wenu.', 'XHO22'),
(46, 11, 47, 'Yeha nina! Ngokuba niyawakha amangcwaba abaprofeti, babe ke ooyihlo bebabulele.', 'XHO22'),
(46, 11, 48, 'Inene niyangqina ukuba niyayivumela imisebenzi yooyihlo; kuba bona bababulala okunene, nibe ke nina niwakha amangcwaba abo.', 'XHO22'),
(46, 11, 49, 'Ngenxa yoko nobulumko bukaThixo bathi, Ndiya kubathumela abaprofeti nabapostile, bathi inxenye kubo bayibulale, bayitshutshise;', 'XHO22'),
(46, 11, 50, 'ukuze kubizwe kwesi sizukulwana igazi labo bonke abaprofeti elaphalazwayo, kwasekusekweni kwehlabathi;', 'XHO22'),
(46, 11, 51, 'Ukususela kwigazi lika-Abheli ukuya kutsho kwigazi likaZakariya, owatshabalala phakathi kwesibingelelo nendlu kaThixo. Inene ndithi kuni, liya kubizwa kwesi sizukulwana.', 'XHO22'),
(46, 11, 52, 'Yeha nina, baqondisi-mthetho! ngokuba nisithabathile isitshixo sokwazi; anangena ngokwenu, naba ke nabangenayo nabalela.', 'XHO22'),
(46, 11, 53, 'Ke kaloku, akuba ezithethile ezi zinto kubo, ababhali nabaFarisi baqala ukumvuselela kakhulu, nokumcaphukisa ukuba athethe izinto ezininzi,', 'XHO22'),
(46, 11, 54, 'bemlalele, befuna ukunqakula into ephuma emlonyeni wakhe, ukuze bammangalele.', 'XHO22'),
(46, 12, 1, 'Kuthe ngoko, akubon’ ukuba ahlanganisene ndawonye amawaka-waka abantu, ngokokude banyathelane, waqala ukuthi kubafundi bakhe, Phambi kwako konke, lilumkeleni igwele labaFarisi, eliluhanahaniso.', 'XHO22'),
(46, 12, 2, 'Akukho nto ke igqunyiweyo, engasayi kutyhilwa; nefihlakeleyo, ayiyi kwaziwa.', 'XHO22'),
(46, 12, 3, 'Ngoko ke konke enikuthethe emnyameni, kuya kuviwa emhlotsheni; nokuthethe endlebeni emagumbini, kumenyezwe phezu kwezindlu.', 'XHO22'),
(46, 12, 4, 'Ndithi ke kuni, zihlobo zam, Musani ukuboyika abo babulala umzimba, baze emva koko bangabi nanto yimbi bangayenzayo.', 'XHO22'),
(46, 12, 5, 'Ndiya kunibonisa ke enomoyika; yoyikani lowo uthi emva kokubulala, abe negunya lokuphosa esihogweni. Ewe, ndithi kuni, Yoyikani yena lowo.', 'XHO22'),
(46, 12, 6, 'Abathengiswa ngeepeni ezimbini na oongqatyana abahlanu? Ukanti akukho namnye kubo olityelweyo emehlweni kaThixo?', 'XHO22'),
(46, 12, 7, 'Kwaneenwele ezi zentloko yenu zibaliwe zonke. Musani ukoyika ngoko; nibagqithile oongqatyana abaninzi nina.', 'XHO22'),
(46, 12, 8, 'Ndithi ke kuni, Wonke umntu othe wandivuma phambi kwabantu, kwanoNyana woMntu uya kumvuma phambi kwezithunywa zikaThixo.', 'XHO22'),
(46, 12, 9, 'Ondikhanyeleyo ke emehlweni abantu, wokhanyelwa emehlweni ezithunywa zikaThixo.', 'XHO22'),
(46, 12, 10, 'Nothe wathetha ilizwi elichasene noNyana woMntu uya kuxolelwa; kodwa lowo unyelise uMoya oyiNgcwele, akasayi kuxolelwa.', 'XHO22'),
(46, 12, 11, 'Xa sukuba ke benizisa ezindlwini zesikhungu, nakwirhuluneli, nakwabanegunya, ize ningaxhaleli ukuba ningaziphendulela ngokunjani na, nangantoni na, nokuba ningathethani na.', 'XHO22'),
(46, 12, 12, 'uMoya oyiNgcwele uya kunifundisa kwangelo lixa izinto enimelwe kukuthi nithethe zona.', 'XHO22'),
(46, 12, 13, 'Ke kaloku uthe ubani kuye endimbaneni, Mfundisi, yitsho kumzalwana wam, ahlulelane nam ilifa.', 'XHO22'),
(46, 12, 14, 'Uthe ke yena kuye, Mntundini, ndimiswe ngubani na ukuba ndibe ngumahluli nomabi phakathi kwenu?', 'XHO22'),
(46, 12, 15, 'Wathi kubo, Lumkani, nizilinde ngasekubaweni; ngokuba ubomi bomntu abumi ngabuninzi bempahla yakhe.', 'XHO22'),
(46, 12, 16, 'Wathetha ke umzekeliso kubo, esithi, Umhlaba womntu othile osisityebi wachuma kakhulu;', 'XHO22'),
(46, 12, 17, 'wayecamanga phakathi kwakhe, esithi, Ndiya kuthini na; ngokuba andinandawo ndiya kuzihlanganisela kuyo iziqhamo zam?', 'XHO22'),
(46, 12, 18, 'Wathi, Nantsi into endiya kuyenza; ndiya kubawisa phantsi oovimba bam, ndakhe abangaphezulu kwabo; ndizihlanganisele khona zonke iziqhamo zam, nempahla yam;', 'XHO22'),
(46, 12, 19, 'ndithi kuwo umphefumlo wam, Mphefumlo, unezinto ezininzi ezilungileyo, ezibekelwe iminyaka emininzi; phumla, udle, sela, yiba nemihlali.', 'XHO22'),
(46, 12, 20, 'Uthe ke uThixo kuye, Msweli-kuqonda, ngobu busuku umphefumlo wakho uya kubizwa kuwe; ziya kuba zezikabani na ke ezo zinto uzilungisileyo?', 'XHO22'),
(46, 12, 21, 'Unjalo lowo uziqwebela ubutyebi, engenabutyebi kuThixo.', 'XHO22'),
(46, 12, 22, 'Wathi ke kubo abafundi bakhe, Ngenxa yoko ndithi kuni, Musani ukubuxhalela ubomi benu, ngokuthi ningadla ntoni na; kwanomzimba, ngokuthi ningambatha ntoni na.', 'XHO22'),
(46, 12, 23, 'Ubomi bukhulu kunokudla, nomzimba kunento yokwambatha.', 'XHO22'),
(46, 12, 24, 'Qwalaselani amahlungulu la, ngokuba akahlwayeli, akavuni; kuwo akukho qonga navimba; nina nibeke phi na ke, ukuzigqitha iintaka?', 'XHO22'),
(46, 12, 25, 'Nguwuphi na ke kuni onokuthi ngokuxhala ongeze ebukhulwini bakhe ikubhite ibe nye?', 'XHO22'),
(46, 12, 26, 'Ukuba ngoko aninako ukwenza neyona nto incinane, yini na ukuba nizixhalele ezinye?', 'XHO22'),
(46, 12, 27, 'Qondani ngeenyibiba, ukuhluma kwazo; azibulaleki, azisonti nokusonta; ke ndithi kuni, uSolomon kubo bonke ubunewunewu bakhe, wayengavathiswanga nanjenganye kuzo ezi.', 'XHO22'),
(46, 12, 28, 'Ukuba ke ingca, leyo isendle, ethi namhla ibekho, ize ngomso iphoswe eziko, wenjenjalo uThixo ukuyambesa; wobeka phi na ke kuni, bantundini balukholo luncinane?', 'XHO22'),
(46, 12, 29, 'Nize ningafuni ukuba ningadla ntoni na, nisele ntoni na;', 'XHO22'),
(46, 12, 30, 'ngokuba zonke ezo nto zingxanyelwe ziintlanga zehlabathi; uYihlo ke uyazi ukuba niziswele ezo zinto.', 'XHO22'),
(46, 12, 31, 'Funani ke ubukumkani bukaThixo; zaye zonke ezo zinto ziya kongezelelwa kuni.', 'XHO22'),
(46, 12, 32, 'Musani ukoyika, mhlambi mncinane; ngokuba kukholekile kuYihlo ukuninika ubukumkani.', 'XHO22'),
(46, 12, 33, 'Thengisani ngeempahla zenu, niphe amalizo; zilungiseleni iingxowa ezingagugiyo, ubutyebi obungapheliyo emazulwini, apho kungasondeliyo sela, kungonakalisi nanundu;', 'XHO22'),
(46, 12, 34, 'kuba apho bukhona ubutyebi benu, yoba lapho nentliziyo yenu.', 'XHO22'),
(46, 12, 35, 'Mazibhinqiwe izinqe zenu, nezibane zenu zivutha;', 'XHO22'),
(46, 12, 36, 'nani nibe njengabantu abayilindileyo inkosi yabo, xeshikweni iya kubuya emsithweni; ukuze, yakufika inkqonkqoze, bayivulele kwaoko.', 'XHO22'),
(46, 12, 37, 'Banoyolo abo bakhonzi, eyothi yakufika inkosi, ibafumane belindile. Inene ndithi kuni, iya kubhinqa, ibangqengqise bona, ize isondele ibalungiselele.', 'XHO22'),
(46, 12, 38, 'Nokuba ithe yeza ngowesibini umlindo wobusuku, nokuba ithe yeza ngowesithathu umlindo, yafika kunjalo, banoyolo abo bakhonzi.', 'XHO22'),
(46, 12, 39, 'Kwazini ke oku, ukuba umninindlu ebesazi ukuba isela liza ngaliphi na ilixa, ange elindile, akayiyekela indlu yakhe igqojozwe.', 'XHO22'),
(46, 12, 40, 'Nani ngoko yibani nilungile, ngokuba uNyana woMntu uza ngelixa eningalikhumbuleliyo.', 'XHO22'),
(46, 12, 41, 'Waza wathi uPetros kuye, Nkosi, lo mzekeliso uwuthetha kuthi, uwuthetha kubo bonke, kusini na?', 'XHO22'),
(46, 12, 42, 'Ithe ke iNkosi, Ngubani na ke elo gosa lithembekileyo, liqondileyo, eyothi inkosi ilimise phezu kwabendlu yayo, ukuze libanike umamkelo wabo wokudla ngexesha elifanelekileyo?', 'XHO22'),
(46, 12, 43, 'Unoyolo loo mkhonzi, iya kuthi yakufika inkosi yakhe imfumane esenjenjalo.', 'XHO22'),
(46, 12, 44, 'Inene ndithi kuni, iya kummisa phezu kwayo yonke impahla yayo.', 'XHO22'),
(46, 12, 45, 'Ukuba ke loo mkhonzi uthe entliziyweni yakhe, Inkosi yam iyalibala ukuza; aqale ukubabetha abakhonzi nabakhonzazana, adle, asele, anxile;', 'XHO22'),
(46, 12, 46, 'Iya kufika inkosi yaloo mkhonzi ngemini angayilindeleyo, nangelixa angalaziyo, imxabele kubini, immisele isabelo sakhe kwabangakholwayo.', 'XHO22'),
(46, 12, 47, 'Yena ke loo mkhonzi, ubekwazi ukuthanda kwenkosi yakhe, akalungisa, akenza ngokuthanda kwayo, uya kutyatyulwa ngemivumbo emininzi.', 'XHO22'),
(46, 12, 48, 'Ke yena obengazi, wenza ke okufanele imivumbo, uya kutyatyulwa ibe mbalwa. Kubo bonke abanikwe okukhulu, kuya kufunwa okukhulu kubo; nakulowo kubekwe okukhulu kuye, kuya kubizwa okungaphezulu.', 'XHO22'),
(46, 12, 49, 'Ndize kuphosa umlilo ehlabathini; Ndiya kuthini na, ukuba sele uphethiwe?', 'XHO22'),
(46, 12, 50, 'Kukho ke lubhaptizo ndifanele ukubhaptizwa ngalo; Asikuko nokuba ndicinezelekile lude lufezeke!', 'XHO22'),
(46, 12, 51, 'Niba ndize kungenisa uxolo na ehlabathini? Ndithi kuni, Hayi; ndisuke ndaweni-nye;', 'XHO22'),
(46, 12, 52, 'Kuba kuya kuthi, kuthabathela kwalapha, kubekho abahlanu ndlwini-nye bebambene, abathathu bebambene nababini, nababini bebambene nabathathu.', 'XHO22'),
(46, 12, 53, 'Kuya kubambana uyise nonyana, unyana noyise; unina nentombi, intombi nonina; uninazala nomolokazana wakhe, umolokazana noninazala;', 'XHO22'),
(46, 12, 54, 'Wayesithi ke nakuzo izihlwele, Xa nithe nalibona ilifu liphuma entshonalanga, nithi kwaoko, Kuza isiphango; kwaye kunjalo.', 'XHO22'),
(46, 12, 55, 'Naxa sukuba nibona umoya wasezantsi uvuthuza, nithi, Kuya kubakho ulophu; kwaba njalo.', 'XHO22'),
(46, 12, 56, 'Bahanahanisindini, niyakwazi ukubucalula ubuso bezulu nomhlaba; Yini na ke ukuba ningalicaluli nje eli xesha?', 'XHO22'),
(46, 12, 57, 'Yini na ke ukuba nina ngokwenu ningakwahluli okububulungisa?', 'XHO22'),
(46, 12, 58, 'Xa uya nommangaleli wakho kumantyi, nisendleleni, yithi nisendleleni, qiniseka ukuba uzihlangule kuye; hleze akurholele emgwebini, aze umgwebi akunikele epoliseni, aze igosa likuphose entolongweni.', 'XHO22'),
(46, 12, 59, 'Ndithi kuwe, Akusayi kukha uphume khona apho, ude uhlawule nemalanana yokugqibela.', 'XHO22'),
(46, 13, 1, 'Ke kaloku kwaye kukho abathile ngelo xesha, ababembikela amaGalili, agazi uPilato waliphithikeza namadini awo.', 'XHO22'),
(46, 13, 2, 'Waphendula uYesu wathi kubo, Niba na loo maGalili ebengaboni ngaphezu kwawo onke amaGalili, ngokuba eve ubunzima obunjalo?', 'XHO22'),
(46, 13, 3, 'Ndithi kuni, Hayi; nothi, xa sukuba ningaguquki, nitshabalale kwangokunjalo nonke.', 'XHO22'),
(46, 13, 4, 'Hi, bona abo balishumi elinesibhozo, bawelwa yinqaba eseSilowa, yababulala; niba na babenetyala ngaphezu kwabo bonke abantu abemiyo eYerusalem?', 'XHO22'),
(46, 13, 5, 'Ndithi kuni, Hayi; nothi, xa sukuba ningaguquki, nitshabalale kwangokunjalo nonke.', 'XHO22'),
(46, 13, 6, 'Wathetha ke nalo mzekeliso; Ubani wayenomkhiwane, utyelwe esidiliyeni sakhe; weza efuna isiqhamo kuwo, akafumana nto.', 'XHO22'),
(46, 13, 7, 'Wathi ke kumsebenzi-sidiliya, Uyabona, ndiminyaka mithathu ndiza ndifuna isiqhamo kulo mkhiwane, ndingafumani nto; wugawule; yini na ukuba uwuqobe umhlaba?', 'XHO22'),
(46, 13, 8, 'Aphendule ke yena wathi kuye, Nkosi, wuyeke nangalo mnyaka, ndide khendimbe ndiwurhaqe, ndigalele umgquba.', 'XHO22'),
(46, 13, 9, 'Ukuba ke uthe wavelisa isiqhamo, wowuyeka; ke ukuba akubanga njalo, wobuya uwugawule.', 'XHO22'),
(46, 13, 10, 'NgoMhla wokuPhumla wayefundisa kwenye yezindlu zesikhungu.', 'XHO22'),
(46, 13, 11, 'Kwabonakala kukho ntokazi ibinomoya Wobulwelwe iminyaka elishumi elinesibhozo; yaye igobile, ingenako kanye ukuziphakamisa.', 'XHO22'),
(46, 13, 12, 'Eyibonile ke uYesu, wayibiza wathi kuyo, Ntokazindini, khululeka ebulwelweni bakho.', 'XHO22'),
(46, 13, 13, 'Wayibeka ke izandla; yoluka kwangoko, yazukisa uThixo.', 'XHO22'),
(46, 13, 14, 'Waphendula umphathi wendlu yesikhungu, ecaphuka, ngokuba uYesu waphilisa ngesabatha, wathi ebantwini, Mithandathu imihla ekufanele kusetyenzwa ngayo; yizani ke ngoko niphiliswe, kungabi ngesabatha usuku.', 'XHO22'),
(46, 13, 15, 'Yamphendula ke ngoko iNkosi, yathi, Mhanahanisindini, akathi na elowo kuni ayikhulule ngesabatha inkomo yakhe, nokuba liesile lakhe, emkhumbini wesitali, alirhole aye kuyiseza?', 'XHO22'),
(46, 13, 16, 'Lo ke, eyintombi ka-Abraham nje, ayibophileyo uSathana, khangela, le minyaka ilishumi elinesibhozo, ubengamele ukukhululwa na kulo mabophe ngomhla wesabatha?', 'XHO22'),
(46, 13, 17, 'Akuba ezithethile ezi zinto, badaniswa bonke abachasene naye; zathi zonke izihlwele zavuya ngazo zonke izinto ezizukileyo ezenziwa nguye.', 'XHO22'),
(46, 13, 18, 'Wayesithi ke, Bufana nantoni na ubukumkani bukaThixo? ndiya kulifanisa nantoni na?', 'XHO22'),
(46, 13, 19, 'Bunjengokhozo lwemostade, awaluthabathayo umntu, waluphosa emyezweni wakhe; yahluma, yaba ngumthi omkhulu; zathi iintaka zezulu zahlala emasebeni awo.', 'XHO22'),
(46, 13, 20, 'Wabuya wathi, Ndiya kubufanisa nantoni na ubukumkani bukaThixo?', 'XHO22'),
(46, 13, 21, 'Bufana negwele, awalithabathayo umfazi, walifihla ezilinganisweni zozithathu zomgubo, wada wabiliswa uphela.', 'XHO22'),
(46, 13, 22, 'Waye ecanda kwimizi ngemizi, nemizana ngemizana, efundisa, esinga eYerusalem.', 'XHO22'),
(46, 13, 23, 'Uthi ke omnye kuye, Nkosi, bambalwa na abasindiswayo? Wathi kubo.', 'XHO22'),
(46, 13, 24, 'Uthe ke yena kubo, Zamani ukungena ngesango elimxinwa; ngokuba ndithi kuni, baninzi abaya kufuna ukungena, bakhohlwe.', 'XHO22'),
(46, 13, 25, 'Xa athe wavuka umninindlu, waluvala ucango, naza naqala ukuma ngaphandle, nankqonkqoza elucangweni, nisithi, Nkosi, Nkosi, sivulele; Aphendule athi kuni, Andinazi apho nivela khona;', 'XHO22'),
(46, 13, 26, 'Noqala nithi, Sadla, sasela, phambi kwakho, wafundisa nasezitratweni zethu.', 'XHO22'),
(46, 13, 27, 'Athi, Ndithi kuni, andinazi apho nivela khona; mkani kum, nina nonke basebenzi bobutshinga.', 'XHO22'),
(46, 13, 28, 'Khona apho kuya kubakho ukulila nokutshixiza kwamazinyo, xa nithe nabona uAbraham, noIsake, noYakobi, nabo bonke abaprofeti, ebukumkanini bukaThixo; nibe ke nina nikhutshelwe phandle.', 'XHO22'),
(46, 13, 29, 'Baya kuza bevela empumalanga nasentshonalanga, nasentla, nasezantsi, bangqengqe ebukumkanini bukaThixo.', 'XHO22'),
(46, 13, 30, 'Yabonani, bakho abokugqibela abaya kuba ngabokuqala; bakho nabokuqala abaya kuba ngabokugqibela.', 'XHO22'),
(46, 13, 31, 'Kwangaloo mini kweza abaFarisi abathile, besithi kuye, Phuma umke apha, ngokuba uHerode ufuna ukukubulala.', 'XHO22'),
(46, 13, 32, 'Wathi kubo, Yiyani nithi kuloo mpungutye, Uyabona, ndikhupha iidemon, ndifinyeze iimpiliso, namhla nangomso; ndithi ngowesithathu umhla ndifeze.', 'XHO22'),
(46, 13, 33, 'Kodwa ndimelwe kukuthi ndihambe namhlanje nangomso nangolandelayo, ngokuba akangekhe umprofeti atshabalale ngaphandle kweYerusalem.', 'XHO22'),
(46, 13, 34, 'Yerusalem, Yerusalem, ebabulalayo abaprofeti, ibagibisele ngamatye abo bathunywe kuyo! kufuthi kangakanani na ndithande ukubahlanganisela ndawonye abantwana bakho, ngohlobo esithi isikhukukazi siwabuthele ndawonye amathole aso ngaphantsi kwamaphiko, anavuma!', 'XHO22'),
(46, 13, 35, 'Yabonani, indlu yenu ishiywa nani ize. Inene, ndithi kuni, Aniyi kuba sandibona, kude kube lixesha eniya kuthi, Makabongwe lowo uzayo egameni leNkosi.', 'XHO22'),
(46, 14, 1, 'Kwathi, akuba engene ngesabatha endlwini yomnye wakubaphathi babaFarisi, ukuba adle isonka, bahlala bemlalele bona.', 'XHO22'),
(46, 14, 2, 'Kwabonakala kukho phambi kwakhe mntu uthile unegalimoya.', 'XHO22'),
(46, 14, 3, 'Waza uYesu wathetha kubachazi-mthetho nabaFarasi, esithi: “Ngaba kuvumelekile na ukuphilisa ngesabatha?', 'XHO22'),
(46, 14, 4, 'Bathi cwaka ke bona. Wamthabatha, wamphilisa, wamndulula;', 'XHO22'),
(46, 14, 5, 'Wabaphendula esithi, Ngubani na kuni othi, eneesile, nokuba yinkomo, yeyele emhadini, aze angayirholi kwaoko ngomhla wesabatha?', 'XHO22'),
(46, 14, 6, 'Ababa nako ukumphendula kwezo zinto.', 'XHO22'),
(46, 14, 7, 'Ke kaloku wathetha umzekeliso kwababemenyiwe, akugqala ukunyula kwabo izilili zobukhulu; esithi kubo.', 'XHO22'),
(46, 14, 8, 'Xa uthe wamenyelwa emsithweni ngubani, musa ukungqengqa esililini sobukhulu; hleze obekekileyo kunawe abe ubemenyiwe nguye;', 'XHO22'),
(46, 14, 9, 'eze lowo ukumemileyo, athi kuwe, Khwelela lo mntu; uze uqale ukuthabatha indawo esekugqibeleni udanile.', 'XHO22'),
(46, 14, 10, 'Xa uthe wamenywa, hamba uye uhlale kweyona ndawo iphantsi, ukuze, xa athe weza lowo ukumemileyo, athi kuwe, Sihlobo, nyukela ngasentla; wandule ke ukuba nozuko emehlweni abo bahleli ndawonye nawe.', 'XHO22'),
(46, 14, 11, 'Ngokuba wonke umntu oziphakamisayo, uya kuthotywa; nozithobayo uya kuphakanyiswa.', 'XHO22'),
(46, 14, 12, 'Wayesithi ke nakulowo ummemileyo, Xa sukuba usenza isidlo sasemini, nokuba sesangokuhlwa, musa ukubiza izihlobo zakho, nabazalwana bakho, nemizalwane yakho, nabamelwane abazizityebi; hleze nabo babuye bakumeme, ize ibe yimbuyekezo kuwe.', 'XHO22'),
(46, 14, 13, 'Yithi xa sukuba usenza isidlo, umeme amahlwempu, izilima, iziqhwala, iimfama;', 'XHO22'),
(46, 14, 14, 'Woba nentsikelelo; ngokuba àbanako ukubuyekeza kuwe; kuba uya kubuyekezwa wena eluvukweni lwamalungisa.', 'XHO22'),
(46, 14, 15, 'Ke kaloku, akuziva ezo zinto omnye kwabo babehleli naye, wathi kuye, Unoyolo oya kudla isonka ebukumkanini bukaThixo.', 'XHO22'),
(46, 14, 16, 'Uthe ke yena kuye, Umntu othile wenza isidlo esikhulu, wamema abaninzi.', 'XHO22'),
(46, 14, 17, 'Wasusa umkhonzi wakhe ngelixa lesidlo, ukuba aye kuthi kwabamenyiweyo, Yizani; ngokuba zonke izinto sezilungile.', 'XHO22'),
(46, 14, 18, 'Baqala bonke ngakunye ukuzilanduleka. Wathi owokuqala kuye, Ndithenge intsimi, kufuneka ukuba ndiye kuyibona; ndiyakucela, mandibe ngowabalandulelwayo.', 'XHO22'),
(46, 14, 19, 'Wathi omnye, Ndithenge iinkomo zeedyokhwe ezintlanu, ndiya kuziva; ndiyakucela mandibe ngowabalandulelwayo.', 'XHO22'),
(46, 14, 20, 'Wathi omnye, Ndizeke umfazi, ngenxa yoko andinakuza.', 'XHO22'),
(46, 14, 21, 'Efikile loo mkhonzi, wayibikela ke inkosi yakhe ezo ndawo. Waqumba ke umninindlu, wathi kumkhonzi wakhe, Phuma kamsinya uye ezitratweni nakwizitrato zomzi, ungenise apha amahlwempu, nezilima, neziqhwala, neemfama.', 'XHO22'),
(46, 14, 22, 'Wathi umkhonzi, Nkosi, kwenzeke njengoko ubuyalele, ukanti kusekho indawo.', 'XHO22'),
(46, 14, 23, 'Yathi inkosi kumkhonzi, Phuma uye ezindleleni nasezintangweni, ubanyanzele bangene, ukuze indlu yam izale.', 'XHO22'),
(46, 14, 24, 'Kuba ndithi kuni, akukho nanye kuloo madoda abemenyiwe, eya kusiva isidlo sam.', 'XHO22'),
(46, 14, 25, 'Kwaye kuhamba naye izihlwele ezikhulu; wajika, wathi kuzo.', 'XHO22'),
(46, 14, 26, 'Ukuba ubani uza kum, angamthiyi uyise, nonina, nomfazi, nabantwana, nabazalwana, nodade, ewe, nobakhe ubomi, akanakuba ngumfundi wam.', 'XHO22'),
(46, 14, 27, 'Nosukuba engawuthwali umnqamlezo wakhe, andilandele, akanakuba ngumfundi wam.', 'XHO22'),
(46, 14, 28, 'Kuba nguwuphi na kuni othi, ethanda ukwakha inqaba, angakhe ahlale phantsi kuqala, abale indleko, ukuba unazo na izinto zokuyigqiba?', 'XHO22'),
(46, 14, 29, 'Hleze, akubon’ ukuba usibekile isiseko, angabi nakugqiba, bathi bonke ababonayo baqale ukuhlekisa ngaye;', 'XHO22'),
(46, 14, 30, 'besithi, Lo mntu waqala ukwakha, wakhohlwa ukugqiba.', 'XHO22'),
(46, 14, 31, 'Kananjalo nguwuphi na ukumkani othi, esiya kuhlangana nomnye ukumkani emfazweni, angakhe ahlale phantsi kuqala, acinge ukuba unako na, enashumi-nye lamawaka, ukuhlangabeza omzelayo enamashumi amabini amawaka?', 'XHO22'),
(46, 14, 32, 'Ukuba ke akanako, uthi esekude, athume isigidimi, acele iindawo zoxolo.', 'XHO22'),
(46, 14, 33, 'Ngokukwanjalo ngoko, wonke umntu kuni ongazishiyiyo zonke iimpahla zakhe, akanakuba ngumfundi wam.', 'XHO22'),
(46, 14, 34, 'Ityiwa ilungile, kodwa ukuba ithe ayaba nasongo, yothiwani na ukuvakaliswa?', 'XHO22'),
(46, 14, 35, 'Awuwulungele nomhlaba, awuwulungele nomgquba; kodwa abantu bayawukhupha. Lowo uneendlebe zokuva makeve.', 'XHO22'),
(46, 15, 1, 'Ke kaloku, babesondela kuye bonke ababuthi berhafu naboni, ukuba bamve.', 'XHO22'),
(46, 15, 2, 'Baye bekrokra abaFarisi nababhali, besithi, Lo wamkela aboni, adle nabo.', 'XHO22'),
(46, 15, 3, 'Wathetha lo mzekeliso kubo, esithi,', 'XHO22'),
(46, 15, 4, 'Nguwuphi na umntu kuni othi, enekhulu lezimvu, ize kulahleke enye kuzo, angazishiyi ezingamashumi asithoba anesithoba entlango, asinge kuleyo ilahlekileyo, ade ayifumane?', 'XHO22'),
(46, 15, 5, 'Athi ke akuyifumana, ayibeke emagxeni akhe, evuya.', 'XHO22'),
(46, 15, 6, 'Athi akufika ekhaya, ababizele ndawonye abahlobo nabamelwane, esithi kubo, Vuyisanani nam; ngokuba ndiyifumene imvu yam ebilahlekile.', 'XHO22'),
(46, 15, 7, 'Ndithi kuni, ngokukwanjalo kuya kubakho uvuyo emazulwini ngomoni eba mnye oguqukayo, kunamashumi asithoba anesithoba amalungisa, angaswele nguquko.', 'XHO22'),
(46, 15, 8, 'Kanti nguwuphi na umfazi eneedrarhima zilishumi, ongethi, ukuba ithe yalahleka enye idrarhima, alumeke isibane, ayitshayele indlu, afune enyamekile, ade ayifumane?', 'XHO22'),
(46, 15, 9, 'Athi akuyifumana, ababizele ndawonye abahlobo nabamelwane, esithi, Vuyisanani nam; kuba ndiyifumene intwana ebindilahlekile.', 'XHO22'),
(46, 15, 10, 'Ngokukwanjalo ndithi kuni, kubakho uvuyo emehlweni ezithunywa zikaThixo ngomoni eba mnye oguqukayo.', 'XHO22'),
(46, 15, 11, 'Uthe ke, Umntu othile ube enoonyana ababini.', 'XHO22'),
(46, 15, 12, 'Wathi omnci wabo kuyise, Bawo, ndinike isahlulo sempahla esilunge nam. Wababela ke ukudla kwakhe.', 'XHO22'),
(46, 15, 13, 'Kuthe kungekabi ntsuku zininzi, wahlanganisa into yonke unyana omnci, wesuka kwelakowabo, waya ezweni elikude;', 'XHO22'),
(46, 15, 14, 'Akubon’ ukuba udle konke, kwabakho indlala enzima kwelo zwe; waqala ukuswela.', 'XHO22'),
(46, 15, 15, 'Waya wanamathela komnye kubemi belo zwe; wathi yena wamthumela emihlabeni yakhe, ukuba aye kwalusa iihagu.', 'XHO22'),
(46, 15, 16, 'Ubenqwenela ke ukusizalisa isisu sakhe ngeengxam ezabe zidliwa ziihagu; bekungekho mntu umphayo.', 'XHO22'),
(46, 15, 17, 'Ke kaloku, akuba nokuziqonda, wathi, Kanene bangakanani na abaqeshwa bakabawo abadikwa zizonka, kanti ke mna ndiphela yindlala.', 'XHO22'),
(46, 15, 18, 'Ndiya kusuka ndiye kubawo, ndithi kuye, Bawo, ndonile kuwo amazulu, nasemehlweni akho.', 'XHO22'),
(46, 15, 19, 'Andisafanele kubizwa ngonyana wakho; ndénze ndibe njengomnye kubaqeshwa bakho.', 'XHO22'),
(46, 15, 20, 'Wesuka ke waya kuyise. Ke kaloku, akubon’ ukuba usekude lee, uyise wambona, wasikwa yimfesane, wagidima, wawa entanyeni yakhe, wamanga.', 'XHO22'),
(46, 15, 21, 'Uthe ke unyana kuye, Bawo, ndonile kuwo amazulu, nasemehlweni akho; andisafanele kubizwa ngokuba ndingunyana wakho.', 'XHO22'),
(46, 15, 22, 'Uthe ke uyise kubakhonzi bakhe, Rholani ingubo ende, eyona intle, nimambathise; afake umsesane esandleni sakhe, neembadada ezinyaweni zakhe.', 'XHO22'),
(46, 15, 23, 'Nizise ithole, eliya lityetyisiweyo, nilixhele; sidle, sibe nemihlali;', 'XHO22'),
(46, 15, 24, 'Ngokuba lo nyana wam ubefile, wabuya waphila; ubelahlekile, wafunyanwa. Baqala ke ukuba nemihlali.', 'XHO22'),
(46, 15, 25, 'Ke kaloku unyana wakhe omkhulu ebesentsimini. Wathi xa esondela endlwini, weva iingoma nokungqungqa.', 'XHO22'),
(46, 15, 26, 'Wabizela kuye omnye kubakhonzi, wambuza ukuba zingaba ziyini na ezi zinto.', 'XHO22'),
(46, 15, 27, 'Uthe ke yena kuye, Umninawa wakho ufikile; uyihlo ke uxhele ithole, eliya lityetyisiweyo; ngokuba ebuye wamzuza ephilile.', 'XHO22'),
(46, 15, 28, 'Waqumba akavuma ukungena. Waphuma uyise, wambongoza.', 'XHO22'),
(46, 15, 29, 'Waphendula ke yena wathi kuyise, Uyabona, le minyaka ingakanana ndiyakukhonza, andikhange ndigqithe mthetho wakho;', 'XHO22'),
(46, 15, 30, 'kodwa xenikweni afikileyo lo nyana wakho, wayidlayo wayigqiba impilo yakho namahenyukazi, umxhelele ithole eliya lityetyisiweyo.', 'XHO22'),
(46, 15, 31, 'Uthe ke yena kuye, Mntwana wam, wena uhleli unam, nazo zonke izinto zam zezakho.', 'XHO22'),
(46, 15, 32, 'Kufanelekile ukuba senze imihlali, sivuye; ngokuba umninawa wakho lo ubefile, wabuya waphila; ubelahlekile, wafunyanwa.', 'XHO22'),
(46, 16, 1, 'Ke kaloku, wayesithi nakubafundi bakhe, Kwaye kukho umntu othile osisityebi, obenegosa; laza lona lamangalelwa kuye, lokuba uyichithe impahla yakhe.', 'XHO22'),
(46, 16, 2, 'Elibizile ke, wathi kulo, Yintoni na le ndiyivayo ngawe? Khawenze ingxelo yobugosa bakho; kuba akusayi kuba saba ligosa.', 'XHO22'),
(46, 16, 3, 'Lathi ke igosa ngaphakathi kwalo, Ndiya kuthini na? Ngokuba inkosi yam iyabuhlutha ubugosa kum; ukungqiba ndineentloni.', 'XHO22'),
(46, 16, 4, 'O! ndiyayazi into endiya kuyenza, ukuze xa ndithe ndakhutshwa ebugoseni, bandamkele ezindlwini zabo.', 'XHO22'),
(46, 16, 5, 'Libabizele kulo ngabanye abanetyala enkosini yalo, lathi kowokuqala, Unatyala lingakanani na enkosini yam?', 'XHO22'),
(46, 16, 6, 'Uthe ke yena, Likhulu leekore zeoli. Wathi kuye, Thabatha incwadi yakho, uhlale phantsi kamsinya, ubhale amashumi amahlanu.', 'XHO22'),
(46, 16, 7, 'Aze athi komnye, Wena ke unatyala lingakanani na? Uthe ke yena, Likhulu leekore zengqolowa. Wathi kuye, Thabatha incwadi yakho, ubhale amashumi asibhozo.', 'XHO22'),
(46, 16, 8, 'Yaza inkosi leyo yalincoma igosa elingalungisiyo, ngokuba lenza ngokuqonda; ngokuba bona abeli phakade kwesabo isizukulwana banokuqonda, ngaphezu kwabo abokukhanya.', 'XHO22'),
(46, 16, 9, 'Nam ndithi kuni, Zenzeleni izihlobo ngobutyebi bokungalungisi; ukuze xa nithe naphelelwa, banamkele eminqubeni engunaphakade.', 'XHO22'),
(46, 16, 10, 'Othembekileyo kokona kuncinane, uthembekile nakokukhulu; nongalungisiyo kokuncinane, akalolungisa nakokukhulu.', 'XHO22'),
(46, 16, 11, 'Ukuba ngoko anibanga nakuthembeka ebutyebini bokungalungisi, ngubani na owonithemba kobo buyinyaniso?', 'XHO22'),
(46, 16, 12, 'Ukuba anibanga nakuthembeka koko komnye, ngubani na oya kuninika okukokwenu?', 'XHO22'),
(46, 16, 13, 'Akukho mkhonzi unako ukukhonza nkosi mbini; kuba eya kuyithiya le, athande leya; athi mhlawumbi abambelele kule, ayidele leya; Aninako ukukhonza uThixo nobutyebi.', 'XHO22'),
(46, 16, 14, 'Ke kaloku baye beziva ezo ndawo zonke nabo abaFarisi, amathanda-mali; baye bemsinekela ke.', 'XHO22'),
(46, 16, 15, 'Wathi kubo, Nina ningabazigwebelayo emehlweni abantu; kodwa uThixo uyazazi iintliziyo zenu; kuba oko kuphakanyisiweyo phakathi kwabantu, kulisikizi emehlweni kaThixo.', 'XHO22'),
(46, 16, 16, 'Umthetho nabaprofeti babekho kwada kwakuYohane; kuselokoko, ubukumkani bukaThixo buyashunyayelwa, bonke bayangena kubo ngokugonyamela.', 'XHO22'),
(46, 16, 17, 'Ke kulula ukuba lidlule izulu nomhlaba, kunokuba kuwe nalinye isuntswana lomthetho.', 'XHO22'),
(46, 16, 18, 'Othe wamala umfazi wakhe, wazeka omnye, uyakrexeza; nomntu wonke ozeka owaliweyo endodeni, uyakrexeza.', 'XHO22'),
(46, 16, 19, 'Ke bekukho umntu othile osisityebi, obevatha ezibomvu nezelinen ecikizekileyo, esenza imihlali yokuzuka kwakhe imihla yonke.', 'XHO22'),
(46, 16, 20, 'Kwaye kukho inqindi ethile egama linguLazaro, eyayibekwe esangweni lakhe, ezele zizilonda;', 'XHO22'),
(46, 16, 21, 'Linqwenela ukuhluthiswa ngeemvuthuluka eziwa etafileni yesityebi eso; zibe zisithi izinja, zize zikhothe izilonda zaso.', 'XHO22'),
(46, 16, 22, 'Lafa ke ihlwempu elo, lasiwa zizithunywa zezulu esifubeni sika-Abraham; safa ke nesityebi, sangcwatywa.', 'XHO22'),
(46, 16, 23, 'Esesihogweni, wawaphakamisa amehlo akhe, esezintuthukweni, wambona uAbraham ekude, noLazaro esesifubeni sakhe.', 'XHO22'),
(46, 16, 24, 'Samemeza sona sathi, Bawo, Abraham, ndenzele inceba, uthume uLazaro, ukuba athi nkxu incam yomnwe wakhe emanzini, aphozise ulwimi lwam; ngokuba ndiyathuthunjelwa kweli langatye.', 'XHO22'),
(46, 16, 25, 'Uthe ke uAbraham, Mntwana wam, khumbula ukuba wena wazifincelela ebomini bakho izinto zakho ezilungileyo, noLazaro ngokukwanjalo izinto ezimbi; ngoku ke yena uyathuthuzelwa, wena ke uyathuthunjelwa.', 'XHO22'),
(46, 16, 26, 'Ngaphezu koko ke, phakathi kwethu nani kuthiwe zinzi umsantsa omkhulu, ukuze aba bathanda ukuphumela, besuka apha besiya kuni, bangabi nako; kananjalo abanako ukuwela beze kuthi, abaphuma nalapho.', 'XHO22'),
(46, 16, 27, 'Sathi, Ndiyakucela ngoko, bawo, ukuba umthume endlwini kabawo;', 'XHO22'),
(46, 16, 28, 'Kuba ndinabazalwana abahlanu; ukuze abaqononondise, ukuze bangathi nabo beze kule ndawo yentuthumbo.', 'XHO22'),
(46, 16, 29, 'Athi uAbraham kuso, BanoMoses nabaprofeti; mabeve bona.', 'XHO22'),
(46, 16, 30, 'Sithe ke sona, Hayi, bawo Abraham; ukuba kuthe kwaya kubo ovela kwabafileyo, boguquka.', 'XHO22'),
(46, 16, 31, 'Uthe ke kuso, Ukuba uMoses nabaprofeti ababeva, abayi koyiseka, nokuba kuthe kwavuka bani kwabafileyo.', 'XHO22'),
(46, 17, 1, 'Wathi ke kubo abafundi, Azinakuba zingezi izikhubekiso; yeha ke, loo mntu ziza ngaye!', 'XHO22'),
(46, 17, 2, 'Ngekumlungele kanye ukuba agangxwe ilitye lokusila entanyeni yakhe, wajulelwa elwandle, kunokuba akhubekise noko amnye kwaba bancinane.', 'XHO22'),
(46, 17, 3, 'Zilumkeleni; ukuba ke umzalwana wakho uthe wakona, mkhalimele; ukuba uthe waguquka, mxolele.', 'XHO22'),
(46, 17, 4, 'Nokuba uthe wakona kasixhenxe ngemini, waza wabuyela kuwe kasixhenxe ngemini, esithi, Ndiyaguquka, utsho uYehova; uze umxolele.', 'XHO22'),
(46, 17, 5, 'Bathi ke abapostile eNkosini, Songezelele elukholweni.', 'XHO22'),
(46, 17, 6, 'Ithe ke iNkosi, Ukuba beninokholo olunjengokhozo lwemostade, beningathi kulo mthombe, Ncothuka, utyaleke phakathi elwandle; yaye ifanele ikuthobele.', 'XHO22'),
(46, 17, 7, 'Nguwuphi na ke kuni othi, enomkhonzi elima, mhlawumbi esalusa, oya kuthi, akungena evela entsimini, athi kwaoko kuye, Yiza uhlale phantsi;', 'XHO22'),
(46, 17, 8, 'Ongayi kuthi kanye kuye, Lungisa into endiya kuyidla, uze ubhinqe, undilungiselele, ndide ndidle ndisele; wothi emva koko udle usele wena?', 'XHO22'),
(46, 17, 9, 'Anganombulelo kuloo mkhonzi yini na, ngokuba enze izinto azimiselweyo? Andijongi.', 'XHO22'),
(46, 17, 10, 'Ngokukwanjalo nani, xa nithe nazenza zonke izinto enazimiselwayo, yithini, Singabakhonzi abangancedi lutho;', 'XHO22'),
(46, 17, 11, 'Kwathi ekuhambeni kwakhe ukuya eYerusalem, wacanda phakathi kwelaseSamariya nelaseGalili.', 'XHO22'),
(46, 17, 12, 'Akuba engene mzini uthile, amhlangabeza amadoda alishumi aneqhenqa, amela mgama.', 'XHO22'),
(46, 17, 13, 'Wona aphakamisa ilizwi, esithi, Yesu, Nkosi, senzele inceba.', 'XHO22'),
(46, 17, 14, 'Ewabonile ke, wathi kuwo, Hambani niye kuzibonakalalisa kubabingeleli. Kwathi ke, ekuhambeni kwabo, bahlanjululwa.', 'XHO22'),
(46, 17, 15, 'Ithe ke enye kuwo, yakubona ukuba iphilisiwe, yabuya imzukisa uThixo ngezwi elikhulu.', 'XHO22'),
(46, 17, 16, 'Yawa ngobuso ezinyaweni zakhe, ibulela kuye; yaye yona ingumSamariya.', 'XHO22'),
(46, 17, 17, 'Uphendule ke uYesu wathi, Bebengahlanjululwanga balishumi na? Baphi na ke abalithoba?', 'XHO22'),
(46, 17, 18, 'Akufumanekanga na babuyileyo, besiza kuzukisa uThixo, ngulo wolunye uhlanga yedwa na?', 'XHO22'),
(46, 17, 19, 'Wathi kuyo, Vuka, uhambe; ukholo lwakho lukusindisile.', 'XHO22'),
(46, 17, 20, 'Ke kaloku, akubuzwa ngabaFarisi ukuba ubukumkani bukaThixo buya kufika nini na, wabaphendula wathi, Ubukumkani bukaThixo abufiki bunokulolongeka.', 'XHO22'),
(46, 17, 21, 'Kananjalo abasayi kuthi, Nanku apha! okanye, nankuya phaya! Kuba, yabonani, ubukumkani bukaThixo bungaphakathi kwenu.', 'XHO22'),
(46, 17, 22, 'Wathi ke kubo abafundi, Kuya kufika mihla, niya kunqwenela ngayo ukuwubona noko umnye umhla woNyana woMntu, ningawuboni;', 'XHO22'),
(46, 17, 23, 'baya kuthi ke kuni, Nanku apha; nokuba, yabonani; musani ukubalandela, ningawalandeli;', 'XHO22'),
(46, 17, 24, 'Kuba kwanjengokuba umbane ubaneka, uphuma kwelinye icala eliphantsi kwezulu, ukhanye usingise kwelinye eliphantsi kwezulu; uya kuba njalo uNyana woMntu ngomhla wakhe.', 'XHO22'),
(46, 17, 25, 'Kodwa kuqala umelwe kukuthi eve ubunzima obukhulu, alahlwe sesi sizukulwana.', 'XHO22'),
(46, 17, 26, 'Njengokuba kwaye kuhle ngemihla kaNowa, koba kwanjalo nangemihla yoNyana woMntu.', 'XHO22'),
(46, 17, 27, 'Babesidla, babesela, babezeka, babesendisa, kwada kwayimini awangena ngayo uNowa emkhombeni, wafika unogumbe, wabatshabalalisa bonke.', 'XHO22'),
(46, 17, 28, 'Kwaba kwanjalo nangemihla kaLothe; babesidla, babesela, babethenga, babethengisa, babetyala, babesakha;', 'XHO22'),
(46, 17, 29, 'Kodwa ngemini awaphuma ngayo uLote eSodom, kwana umlilo nesulfure ezulwini, batshatyalaliswa bonke.', 'XHO22'),
(46, 17, 30, 'Kuya kuba kwanjengoko ngemini atyhileka ngayo uNyana woMntu.', 'XHO22'),
(46, 17, 31, 'Ngaloo mini, oya kuba phezu kwendlu, zibe iimpahla zakhe zisendlwini, makangehli aye kuzithabatha; nosentsimini makangabuyeli emva.', 'XHO22'),
(46, 17, 32, 'Khumbulani umkaLote.', 'XHO22'),
(46, 17, 33, 'Othe wafuna ukuwusindisa umphefumlo wakhe, wolahlekelwa nguwo; yaye othe wawulahla umphefumlo wakhe, wowusindisa.', 'XHO22'),
(46, 17, 34, 'Ndithi kuni, Ngobo busuku ababini boba sililini sinye; omnye amkelwe, omnye ashiywe.', 'XHO22'),
(46, 17, 35, 'Abafazi ababini boba besila ndawonye; omnye amkelwe, omnye ashiywe.', 'XHO22'),
(46, 17, 36, 'Amadoda amabini aya kube esentsimini; omnye amkelwe, omnye ashiywe.', 'XHO22'),
(46, 17, 37, 'Baphendula ke bathi kuye, Phi na, Nkosi? Wathi ke kubo, Apho ukhoyo umzimba, aya kuhlanganisana khona amaxhalanga.', 'XHO22'),
(46, 18, 1, 'Ke kaloku, wayethetha nomzekeliso kubo, wokuba bamelwe kukuhlala bethandaza, bangethi amandla;', 'XHO22'),
(46, 18, 2, 'Esithi, Kwaye kukho umgwebi emzini othile, ongamoyikiyo uThixo, ongamhloneleyo nomntu.', 'XHO22'),
(46, 18, 3, 'Kwaye kukho umhlolokazi othile kuloo mzi, yeza kuye isithi, Ndikhulule kummangaleli wam.', 'XHO22'),
(46, 18, 4, 'Kwaba mzuzu engavumi; kodwa kamva wathi ngaphakathi kwakhe, Nakuba ndingamoyiki uThixo, ndingamhlonele nomntu;', 'XHO22'),
(46, 18, 5, 'noko, ngenxa yokuba lo mhlolokazi endibulala, ndomlungisa, ndimkhulule, hleze ade eze andaphule.', 'XHO22'),
(46, 18, 6, 'Ithe ke iNkosi, Kuveni okuthethwa ngumgwebi ongalungisiyo.', 'XHO22'),
(46, 18, 7, 'Ke yena uThixo akayi kukha abalungise na abanyulwa bakhe, abakhala kuye imini nobusuku, noko azeka kade umsindo ngabo?', 'XHO22'),
(46, 18, 8, 'Ndithi kuni, Uya kubalungisa, abakhulule kamsinya. Kodwa ke akufika uNyana woMntu, wolufumana na ke ukholo ehlabathini apha?', 'XHO22'),
(46, 18, 9, 'Ke kaloku wathetha lo mzekeliso kwabathile, ababekholose ngokuthi ngaphakathi kwabo bangamalungisa, bebadela abanye.', 'XHO22'),
(46, 18, 10, 'Amadoda amabini enyuka aya etempileni, esiya kuthandaza; omnye engumFarisi, omnye engumbuthi werhafu.', 'XHO22'),
(46, 18, 11, 'UmFarisi, ezimise yedwa, wayethandaza esithi, Thixo, ndiyabulela kuwe, ukuba ndingenjengabanye abantu, abaphangi, abangemalungisa, abakrexezi; ndingenjengaye nalo mbuthi werhafu.', 'XHO22'),
(46, 18, 12, 'Ndizila ukudla kabini ngeveki, ndinikela izishumi zako konke endikuzuzayo.', 'XHO22'),
(46, 18, 13, 'Waye umbuthi werhafu emi kude, engathandi nokuphakamisela amehlo akhe ngasezulwini, wasuke wabetha esifubeni sakhe, esithi, Thixo, yiba netarhu kum, mna moni.', 'XHO22'),
(46, 18, 14, 'Ndithi kuni, Wehla lo waya endlwini yakhe, egwetyelwe yena kunalowa; ngokuba wonke umntu oziphakamisayo uva kuthotywa; nozithobayo uya kuphakanyiswa.', 'XHO22'),
(46, 18, 15, 'Kwaye ke bezisa kuye neentsana zabo, ukuze azichukumise; bebonile ke abafundi bakhe, babakhalimela.', 'XHO22'),
(46, 18, 16, 'Ke yena uYesu wazibizela kuye, wathi, Bavumeleni abantwana beze kum, ningabaleli; kuba ubukumkani bukaThixo bubobabo banjalo.', 'XHO22'),
(46, 18, 17, 'Inene, ndithi kuni, Othe akabamkela ubukumkani bukaThixo njengomntwana, akasayi kukha angene kubo.', 'XHO22'),
(46, 18, 18, 'Umphathi othile wambuza, esithi, Mfundisi ulungileyo, mandenze ntoni na, ukuze ndibudle ilifa ubomi obungunaphakade?', 'XHO22'),
(46, 18, 19, 'Uthe ke uYesu kuye, Yini na ukuba uthi ndilungile? Akukho ulungileyo, mnye kwaphela, nguThixo.', 'XHO22'),
(46, 18, 20, 'Uyayazi imithetho: musa ukukrexeza, musa ukubulala, musa ukuba, musa ukungqina ubuxoki, beka uyihlo nonyoko.', 'XHO22'),
(46, 18, 21, 'Uthe ke yena, Zonke ezo zinto ndazigcina kwasebuncinaneni bam.', 'XHO22'),
(46, 18, 22, 'Ekuvile ke oko uYesu, wathi kuye, Kusekho nto-nye uyisweleyo; zonke izinto onazo thengisa ngazo, wabele amahlwempu; woba nobutyebi ke emazulwini; uze ke undilandele.', 'XHO22'),
(46, 18, 23, 'Ezivile ke ezo ndawo, waba buhlungu kakhulu; kuba ube esisityebi kunene.', 'XHO22'),
(46, 18, 24, 'uYesu akumbona ebuhlungu kakhulu, wathi, Hayi, ukuba ngenkankulu ukuya kungena kwabo banobutyebi ebukumkanini bukaThixo!', 'XHO22'),
(46, 18, 25, 'Kuba kulula ukuba inkamela iphumele entunjeni yenaliti, kunokuba isityebi singene ebukumkanini bukaThixo.', 'XHO22'),
(46, 18, 26, 'Bathi ke abo bevayo, Ngubani na ke ongasindiswayo?', 'XHO22'),
(46, 18, 27, 'Uthe ke yena, Izinto ezingenakwenzeka kubantu, zinokwenzeka kuye uThixo.', 'XHO22'),
(46, 18, 28, 'Uthe ke uPetros, Uyabona, thina sishiye iinto zonke sakulandela.', 'XHO22'),
(46, 18, 29, 'Wathi kubo, Inene, ndithi kuni, Akukho namnye ushiye indlu, nokuba ngabazali, nokuba ngabazalwana, nokuba ngumfazi, nokuba ngabantwana, ngenxa yobukumkani bukaThixo;', 'XHO22'),
(46, 18, 30, 'ongasayi kwamkela izinto eziphindaphindiweyo ngalo eli xesha, aze ngelo lizayo iphakade amkele ubomi obungunaphakade.', 'XHO22'),
(46, 18, 31, 'Ke kaloku ubathabathile abalishumi elinababini, wathi kubo, Niyabona, siyenyuka, siya eYerusalem; zaye ziya kuphelela zonke izinto ezibhaliweyo ngabaprofeti, kuye uNyana woMntu.', 'XHO22'),
(46, 18, 32, 'Kuba uya kunikelwa kuzo iintlanga, kudlalwe ngaye, aphathwe kakubi, atshicelwe;', 'XHO22'),
(46, 18, 33, 'Baya kumtyakatya, bambulale, athi ngomhla wesithathu abuye avuke.', 'XHO22'),
(46, 18, 34, 'Bona abaqondanga nento kwezi zinto; laye neli lizwi lifihlakele kubo, babengazazi izinto ezithethwayo.', 'XHO22'),
(46, 18, 35, 'Kwathi ke ekusondeleni kwakhe eYeriko, kuhleli imfama ethile ngasendleleni, ingqiba.', 'XHO22'),
(46, 18, 36, 'Ithe yakuva kudlula isihlwele, yamana ibuza ukuba ingaba yintoni na le.', 'XHO22'),
(46, 18, 37, 'Bayixelela ke ukuba kudlula uYesu waseNazarete.', 'XHO22'),
(46, 18, 38, 'Yabhomboloza isithi, Yesu, nyana kaDavide, ndenzele inceba.', 'XHO22'),
(46, 18, 39, 'Baye abo babehamba ngaphambili beyikhalimela, ukuba ithi tu; kwaba kukhona ke yona ikhalayo ngakumbi, isithi, Nyana kaDavide, ndenzele inceba.', 'XHO22'),
(46, 18, 40, 'Wema ke uYesu, wathi mayiziswe kuye,', 'XHO22'),
(46, 18, 41, 'esithi, Ufuna ukuba ndikwenzele ntoni na? Ithe ke yona, Nkosi, ukuba ndibuye ndibone.', 'XHO22'),
(46, 18, 42, 'uYesu wathi kuyo, Buya ubone; ukholo lwakho lukusindisile.', 'XHO22'),
(46, 18, 43, 'Kwangoko yabuya yabona, yamlandela imzukisa uThixo; bathi bonke abantu, bakukubona oko, badumisa uThixo.', 'XHO22'),
(46, 19, 1, 'Waye engene uYesu, ecanda eYeriko.', 'XHO22'),
(46, 19, 2, 'Kwakho ndoda ibizwa ngegama elinguZakeyu; yaye yona iyintloko yababuthi berhafu; yaye isisityebi yona.', 'XHO22'),
(46, 19, 3, 'Yayifuna ukumbona uYesu, ukuba ngubani na; ayaba nako ngenxa yendimbane, ngokuba enesiqu esincinane.', 'XHO22'),
(46, 19, 4, 'Wabaleka ke, wakhwela emthini womthombe ukuba ambone, kuba wayeza kudlula ngaloo ndlela.', 'XHO22'),
(46, 19, 5, 'Akufika kuloo ndawo, wakhangela phezulu uYesu, wayibona, wathi kuyo, Zakeyu, khawuleza uhle; kuba namhlanje ndimelwe kukuhlala endlwini yakho.', 'XHO22'),
(46, 19, 6, 'Wakhawuleza ke, wehla, wamamkela ngobubele, evuya.', 'XHO22'),
(46, 19, 7, 'Baye bonke, bakuyibona loo nto, bekrokra, besithi, Ungene waya kulala kwindoda engumoni.', 'XHO22'),
(46, 19, 8, 'Wema ke uZakeyu, wathi kuyo iNkosi; Uyabona, isiqingatha sempahla yam, Nkosi, ndisinika amahlwempu; ukuba ndidle into kabani na ngobuxoki, ndimbuyisela kane.', 'XHO22'),
(46, 19, 9, 'Uthe ke uYesu kuye, Namhlanje kufike usindiso kule ndlu, ngokokuba naye engunyana ka-Abraham.', 'XHO22'),
(46, 19, 10, 'Kuba uNyana woMntu weza kufuna nokusindisa oko kulahlekileyo.', 'XHO22'),
(46, 19, 11, 'Ke kaloku, bakubon’ ukuba bayaziva ezi zinto, uqokele wathetha umzekeliso, ngenxa yokuba ebekufuphi eYerusalem, nangokuba babesiba ubukumkani bukaThixo buza kubonakala kwangoko.', 'XHO22'),
(46, 19, 12, 'Uthe ngoko, Umntu othile olinene wahambela ezweni elikude, eya kuzamkelela ubukumkani, aze abuye.', 'XHO22'),
(46, 19, 13, 'Ubabizile ke abakhonzi bakhe abalishumi, wabanika iimina zalishumi, wathi kubo, Rhwebani, ndide ndifike.', 'XHO22'),
(46, 19, 14, 'Ke abemi bomzi wakhe babemthiyile, bathumela izigidimi emva kwakhe, besithi, Asifuni ukuba lo abe ngukumkani phezu kwethu.', 'XHO22'),
(46, 19, 15, 'Kwathi akuba ebuyile, ebamkele ubukumkani, wathi, mababizelwe kuye abo bakhonzi wayebanike imali leyo, ukuze azi ukuba unantsi urhwebe ntoni na.', 'XHO22'),
(46, 19, 16, 'Weza ke owokuqala, esithi, Nkosi, imina yakho yenze iimina zalishumi.', 'XHO22'),
(46, 19, 17, 'Uthe kuye, Hee ke, mkhonzi ulungileyo; ngokuba waba nokuthembeka entweni encinanana, yiba negunya kwimizi elishumi.', 'XHO22'),
(46, 19, 18, 'Weza nowesibini, esithi, Nkosi, imina yakho yenze iimina zantlanu.', 'XHO22'),
(46, 19, 19, 'Wathi nakuye, Nawe yiba phezu kwezixeko ezihlanu.', 'XHO22'),
(46, 19, 20, 'Weza nomnye esithi, Nkosi, nantsi imina yakho endibe ndinayo, ndiyibeke eqhiyeni;', 'XHO22'),
(46, 19, 21, 'kuba ndibe ndikoyika wena, ngokuba ungumntu obungqabangqaba, uthabatha into ongayibekanga, uvune into ongayihlwayelanga.', 'XHO22'),
(46, 19, 22, 'Athi ke kuye, Ndiya kukugweba ngokuphuma emlonyeni wakho, mkhonzindini ungendawo; ubusazi nje ukuba mna ndingumntu obungqabangqaba, othabatha into angayibekanga, avune into angayihlwayelanga;', 'XHO22'),
(46, 19, 23, 'ibiyini na phofu, ukuba ungayibeki imali yam ebhankini, ndaza mna ekufikeni ndaba ndiyibize inenzala?', 'XHO22'),
(46, 19, 24, 'Wathi kwababemi khona, Mhlutheni imina leyo, niyinike lowo uneemina ezilishumi.', 'XHO22'),
(46, 19, 25, 'Bathi ke kuye, Nkosi, uneemina ezilishumi.', 'XHO22'),
(46, 19, 26, 'Kuba ndithi kuni, Wonke umntu onako uya kunikwa; ongenako, uya kuhluthwa kwanoko anako.', 'XHO22'),
(46, 19, 27, 'Ke zona ezo ntshaba zam zazingathandanga ukuba ndibe ngukumkani phezu kwazo, ziziseni apha, nizincinithe phambi kwam.', 'XHO22'),
(46, 19, 28, 'Waye, akuba ezithethile ezi zinto, ehamba ngaphambili, enyuka esiya eYerusalem.', 'XHO22'),
(46, 19, 29, 'Kwathi, akuba esondele eBhetefage naseBhetani, entabeni ekuthiwa yeyemiNquma, wesusa bababini kubafundi bakhe,', 'XHO22'),
(46, 19, 30, 'esithi, Hambani niye emzaneni lo ukhangelene nani; nothi, nakungena kuwo, nifumane iesile libotshelelwe, ekungazanga kuhlale namnye umntu phezu kwalo; likhululeni nilizise apha.', 'XHO22'),
(46, 19, 31, 'Nokuba ubani uyanibuza, esithi, Yini na ukuba nilikhulule? nothi kuye, Liyafuneka eNkosini.', 'XHO22'),
(46, 19, 32, 'Basuka ke baya abo babethunyiwe, bafumana kunjengoko abekutshilo kubo.', 'XHO22'),
(46, 19, 33, 'Ke kaloku, bakubon’ ukuba bayalikhulula ithole, bathi abaninilo kubo, Nilikhululelani na ithole?', 'XHO22'),
(46, 19, 34, 'Bathi ke bona, Liyafuneka eNkosini.', 'XHO22'),
(46, 19, 35, 'Balizisa kuYesu, baphosa iingubo zabo phezu kwethole elo, bamkhwelisa uYesu.', 'XHO22'),
(46, 19, 36, 'Akubon’ ukuba uyahamba ke, bamana bezandlala iingubo zabo endleleni.', 'XHO22'),
(46, 19, 37, 'Ke kaloku, akubon’ ukuba selesondele ekuhleni kwayo iNtaba yemiNquma, yaqala yonke inkitha yabafundi yavuya, yadumisa uThixo ngezwi elikhulu, ngayo yonke imisebenzi yamandla abayibonayo;', 'XHO22'),
(46, 19, 38, 'besithi, Makabongwe lo Kumkani uzayo egameni leNkosi;', 'XHO22'),
(46, 19, 39, 'Bathi kuye abathile bakubaFarisi esihlweleni apho, Mfundisi, bakhalimele abafundi bakho.', 'XHO22'),
(46, 19, 40, 'Waphendula wathi kubo, Ndithi kuni, kuthe cwaka aba, kunkqangaze amatye la.', 'XHO22'),
(46, 19, 41, 'Akusondela, wawubona umzi, wawulilela,', 'XHO22'),
(46, 19, 42, 'esithi, Akwaba ubusazi nawe lo kwangayo yona le mini yakho, izinto ezizezoxolo lwakho! kungoku zifihliwe emehlweni akho.', 'XHO22'),
(46, 19, 43, 'Kuba uya kufikelwa yimihla, eziya kuthi iintshaba zakho zikubiyele ngothango, zikurhangqe, zikuxine ngeenxa zonke;', 'XHO22'),
(46, 19, 44, 'zikukhahlele phantsi wena, nabantwana bakho phakathi kwakho; zingashiyi litye phezu kwelitye kuwe; ngokuba ungalazanga ixesha lokuvelelwa kwakho.', 'XHO22'),
(46, 19, 45, 'Wayengena etempileni, waqala ukubakhupha abo babethengisa kuyo, nababethenga;', 'XHO22'),
(46, 19, 46, 'esithi kubo, Kubhaliwe kwathiwa, Indlu yam yindlu yokuthandaza; ke nina niyenze umqolomba wezihange.', 'XHO22'),
(46, 19, 47, 'Wayefundisa imihla ngemihla etempileni apho. Baye ke ababingeleli abakhulu, nababhali, kwanabaziintloko zabantu, befuna ukumtshabalalisa;', 'XHO22'),
(46, 19, 48, 'Baye ke bengafumani nto banokuyenza, kuba bonke abantu babemlalele ngenyameko.', 'XHO22'),
(46, 20, 1, 'Kwathi ngenye imini, akubon’ ukuba uyabafundisa abantu etempileni, eshumayela iindaba ezilungileyo, wafikelwa ngababingeleli abakhulu nababhali, namadoda amakhulu.', 'XHO22'),
(46, 20, 2, 'Bathetha kuye besithi, Sixelele; uzenza ngagunya lini na ezi zinto? Ngubani na ke okunike eli gunya?', 'XHO22'),
(46, 20, 3, 'Waphendula ke wathi kubo, Nam ndiya kubuza kuni zwi-nye; kwaye undiphendule:', 'XHO22'),
(46, 20, 4, 'Ubhaptizo lukaYohane lwaluphuma emazulwini, lwaluphuma ebantwini, sini na?', 'XHO22'),
(46, 20, 5, 'Bacamanga ke bona bodwa, besithi, Ukuba sithe, Lwaluphuma emazulwini; Wothi, Ibiyini na phofu ukuba ningakholwa nguye?', 'XHO22'),
(46, 20, 6, 'Ukuba ke sithe, Lwaphuma ebantwini; bonke abantu bosigibisela ngamatye; kuba beyisekile ukuba uYohane ubengumprofeti.', 'XHO22'),
(46, 20, 7, 'Baphendula ngokuthi, abazi ukuba luvela phi na.', 'XHO22'),
(46, 20, 8, 'Wathi ke uYesu kubo, Nam ke andinixeleli ukuba ndizenza ngagunya lini na ezi zinto.', 'XHO22'),
(46, 20, 9, 'Waza waqala wathetha ebantwini lo mzekeliso; Ubani watyala isidiliya, waqeshisa ngaso kubalimi, wahambela kwelinye ilizwe umzuzu owaneleyo.', 'XHO22'),
(46, 20, 10, 'Wathi ngelo xesha, wathuma umkhonzi kubalimi, ukuba bamnike esiqhameni sesidiliya; ke bona abalimi bamtyabula, bamndulula elambatha.', 'XHO22'),
(46, 20, 11, 'Wabuya wathuma omnye umkhonzi. Bamtyabula ke nalowo, bamhlazisa, bamndulula elambatha.', 'XHO22'),
(46, 20, 12, 'Waphinda wathuma nowesithathu; naye lowo bamngxwelerha, bamkhuphela ngaphandle.', 'XHO22'),
(46, 20, 13, 'Uthe ke umninisidiliya, Ndiya kuthini na? Ndiya kuthuma unyana wam oyintanda; mhlawumbi bomhlonela yena.', 'XHO22'),
(46, 20, 14, 'Bathe ke bakumbona abalimi, bacamanga bodwa, besithi, Yindlalifa le; yizani, siyibulale, ukuze ilifa libe lelethu.', 'XHO22'),
(46, 20, 15, 'Bamkhuphela ngaphandle kwesidiliya, bambulala. Uya kuthini na ke ngoko kubo umninisidiliya?', 'XHO22'),
(46, 20, 16, 'Uya kufika abatshabalalise abo balimi, asinikele bambi isidiliya eso. Bakuva ke bona, bathi, Makube lee!', 'XHO22'),
(46, 20, 17, 'Wondela ke kubo, wathi, Yintoni na phofu le ibhaliweyo: Ilitye abalicekisayo abakhi, Lelona laba yintloko yembombo?', 'XHO22'),
(46, 20, 18, 'Bonke abathe bawa phezu kweli litye, botyumka; ke lowo lithe lamwela liya kumcola.', 'XHO22'),
(46, 20, 19, 'Baza ababingeleli abakhulu nababhali bafuna ukumsa izandla ngelo lixa; basuka boyika abantu, kuba baqonda ukuba uwuthethe ngabo lo mzekeliso.', 'XHO22'),
(46, 20, 20, 'Bamcupha ke, bathuma iintlola ezizenza amalungisa, ukuze zimbambise ngokuthetha, ukuze bamnikele kurhuluneli nakwigunya lerhuluneli.', 'XHO22'),
(46, 20, 21, 'Zambuza ke zisithi, Mfundisi, siyazi ukuba uthetha ufundise ngobulungisa, ungakhethi buso bamntu, usuka ufundise indlela kaThixo ngenyaniso.', 'XHO22'),
(46, 20, 22, 'Kuvumelekile na ukuba simnike irhafu uKesare, akuvumelekile, sini na?', 'XHO22'),
(46, 20, 23, 'Ebuqondile kakuhle ubuqhetseba bazo, wathi kuzo, Nindilingelani na?', 'XHO22'),
(46, 20, 24, 'Ndiboniseni idenariyo. Inomfanekiso nombhalo kabani na? Baphendula bathi, NgokaKesare.', 'XHO22'),
(46, 20, 25, 'Uthe ke kubo, Nikelani kaloku izinto zikaKesare kuKesare, nezinto zikaThixo kuThixo.', 'XHO22'),
(46, 20, 26, 'Ababa nako ukumbamba amazwi akhe phambi kwabantu; bamangaliswa yimpendulo yakhe; bathi cwaka.', 'XHO22'),
(46, 20, 27, 'Kwafika ke kuye abathile bakubaSadusi, bona abo bakuphikayo ukuba kukho uvuko; bambuza,', 'XHO22'),
(46, 20, 28, 'besithi, Mfundisi, uMoses wasibhalela wathi, Ukuba ozalana nothile uthe wafa enomfazi, wafa ke lowo engenabantwana, maze ozalana naye amzeke umfazi lowo, amvelisele imbewu umzalwana wakhe.', 'XHO22'),
(46, 20, 29, 'Ke bekukho abantu abasixhenxe bezalana; waza owokuqala wazeka umfazi, wafa engenabantwana.', 'XHO22'),
(46, 20, 30, 'Nowesibini wamzeka umfazi lowo, wafa naye engenabantwana.', 'XHO22'),
(46, 20, 31, 'Nowesithathu wamzeka; Benjenjalo ke bosixhenxe, bafa bengashiyanga bantwana.', 'XHO22'),
(46, 20, 32, 'Emveni ke kwabo bonke, wafa naye umfazi.', 'XHO22'),
(46, 20, 33, 'Eluvukweni ngoko, ungumfazi wawuphi na kubo? kuba abasixhenxe bebenaye engumfazi.', 'XHO22'),
(46, 20, 34, 'Waphendula ke uYesu wathi kubo, Abeli phakade bayazeka, bayendiswa;', 'XHO22'),
(46, 20, 35, 'ke bona abo kuthiwe bafanele ukufika kwelo phakade, nakukuvuka kwabafileyo, abazeki, abendiswa;', 'XHO22'),
(46, 20, 36, 'abasenakubuya bafe; kuba bengangezithunywa zezulu; bangoonyana bakaThixo, bengabovuko njalo.', 'XHO22'),
(46, 20, 37, 'Ke kaloku, le ndawo yokuba abafileyo bayavuka, noMoses wayithi thupha kwindawo ethetha ngetyholo, xenikweni athi, iNkosi inguThixo ka-Abraham, inguThixo kaIsake, inguThixo kaYakobi.', 'XHO22'),
(46, 20, 38, 'AkaThixo ke wabafileyo, ungowabaphilileyo; kuba bonke baphile ngaye.', 'XHO22'),
(46, 20, 39, 'Baphendula ke abathile bakubabhali, bathi, Mfundisi, utyaphile ukutsho.', 'XHO22'),
(46, 20, 40, 'Baye ke bengasenabuganga bakumbuza nento le.', 'XHO22'),
(46, 20, 41, 'Wayesithi kubo, Batsho ngani na ukuthi, uKristu ngunyana kaDavide?', 'XHO22'),
(46, 20, 42, 'Uthi noDavide encwadini yeeNdumiso, Yathi iNkosi eNkosini yam, Hlala ngasekunene kwam;', 'XHO22'),
(46, 20, 43, 'Ndide iintshaba zakho ndizenze isihlalo seenyawo zakho.', 'XHO22'),
(46, 20, 44, 'uDavide ngoko umbiza ngokuthi, Nkosi, ungunyana wakhe ngani na?', 'XHO22'),
(46, 20, 45, 'Ke kaloku, bakubon’ ukuba bayava abantu bonke, wathi kubafundi bakhe.', 'XHO22'),
(46, 20, 46, 'Balumkeleni ababhali, abathanda ukuhamba ngezambatho ezinde, nemibuliso ezindaweni zembutho, nezihlalo eziphambili ezindlwini zesikhungu, neendawo eziphambili ezidlweni;', 'XHO22'),
(46, 20, 47, 'abadla izindlu zabahlolokazi, baze ngokunyhwalaza bathandaze uzungu; abo baya kwamkela ugwebo olugqithiseleyo.', 'XHO22'),
(46, 21, 1, 'Waza wabona izityebi ziphosa iminikelo yazo emkhumbini wokulondoloza imali.', 'XHO22'),
(46, 21, 2, 'Wabona ke mhlolokazi uthile ulihlwempu, ephosa apho iimalanana ezimbini.', 'XHO22'),
(46, 21, 3, 'Wathi, Inene ndithi kuni, Lo mhlolokazi ulihlwempu uphose ngaphezu kwabo bonke;', 'XHO22'),
(46, 21, 4, 'Kuba bonke aba baphose eminikelweni kaThixo, bethabatha kwintabalala yabo; ukanti lo, ekusweleni kwakhe, uphose yonke impilo abenayo.', 'XHO22'),
(46, 21, 5, 'Ke kaloku, kwakubon’ ukuba abathile bayathetha ngayo itempile, ukuba ihonjiswe ngamatye amahle nangeminikelo, wathi,', 'XHO22'),
(46, 21, 6, 'Ezi zinto nizibona nje, kuya kufika imihla ekungayi kusala litye phezu kwelitye, elingayi kuchithwa.', 'XHO22'),
(46, 21, 7, 'Bambuza ke besithi, Mfundisi, ziya kubakho nini na ke ezi zinto? uya kuba yintoni na umqondiso, xa ezo zinto ziya kubakho?', 'XHO22'),
(46, 21, 8, 'Wathi ke yena, Lumkani ningalahlekiswa; kuba baninzi abaya kuza egameni lam, besithi, banguye uKristu; ixesha lisondele; musani ukubalandela ngoko.', 'XHO22'),
(46, 21, 9, 'Xa nithe neva iimfazwe neziphithiphithi, musani ukunkwantya; kuba ezo zinto zimele ukuthi zihle kuqala; kodwa isiphelo asiyi kuba kwaoko.', 'XHO22'),
(46, 21, 10, 'Wandula wathi kubo, Uhlanga luya kuvukelana nohlanga, nobukumkani buvukelane nobukumkani.', 'XHO22'),
(46, 21, 11, 'Kuya kubakho iinyikima zomhlaba ezinkulu kwiindawo ngeendawo, neendlala, neendyikitya zokufa; kuya kubakho izinto ezoyikekayo, kwanemiqondiso emikhulu evela ezulwini.', 'XHO22'),
(46, 21, 12, 'Ngaphambili ke kwazo zonke ezo zinto, baya kunisa izandla, banitshutshise, beninikela ezindlwini zesikhungu nasezintolongweni, nisiwe phambi kookumkani neerhuluneli ngenxa yegama lam.', 'XHO22'),
(46, 21, 13, 'Oko ke kuya kuthi kube bubungqina kuni.', 'XHO22'),
(46, 21, 14, 'Kumiseni ngoko ezintliziyweni zenu, ukuba ningakucingi ngaphambili ukuziphendulela.', 'XHO22'),
(46, 21, 15, 'Kuba mna ndiya kuninika umlomo nobulumko, abangayi kuba nako ukubuphikisa nokumelana nabo, bonke abanichasileyo.', 'XHO22'),
(46, 21, 16, 'Ke kaloku niya kunikelwa nangabazali, nangabazalwana, nayimizalwane, nazizihlobo; bathi inxenye kuni babulawe;', 'XHO22'),
(46, 21, 17, 'niya kuthiywa ngabantu bonke ngenxa yegama lam.', 'XHO22'),
(46, 21, 18, 'Akusayi kutshabalala nonwele olu entlokweni yenu.', 'XHO22'),
(46, 21, 19, 'Ekunyamezeleni kwenu, noyizuza imiphefumlo yenu.', 'XHO22'),
(46, 21, 20, 'Xa nithe nayibona iYerusalem irhawulwe yimikhosi, yazini oko ukuba kusondele ukuphanza kwayo.', 'XHO22'),
(46, 21, 21, 'Ngelo xa mabathi abakwelakwaYuda basabele ezintabeni; nabaphakathi kwawo mabaphume bemke; nabasemaphandleni mabangangeni kuwo.', 'XHO22'),
(46, 21, 22, 'Kuba yimihla yempindezelo leyo, ukuze kuzaliseke konke okubhaliweyo.', 'XHO22'),
(46, 21, 23, 'Yeha ke, abamithiyo nabanyisayo ngaloo mihla! kuba kuya kubakho ingxakeko enkulu ezweni, nengqumbo kwaba bantu;', 'XHO22'),
(46, 21, 24, 'Baya kuwa ngohlangothi lwekrele, bathinjwe, basiwe kuzo zonke iintlanga, neYerusalem inyathelelwe phantsi ziintlanga, ade azaliseke amaxesha eentlanga.', 'XHO22'),
(46, 21, 25, 'Kuya kubakho imiqondiso elangeni nasenyangeni nasezinkwenkwezini; nasemhlabeni kubekho ukuxinezeleka kweentlanga, zithingaza; kugquma ulwandle namaza;', 'XHO22'),
(46, 21, 26, 'Abantu besifa kukoyika nakukulindela izinto ezilizelayo elimiweyo; kuba amandla ezulu aya kuzanyazanyiswa.', 'XHO22'),
(46, 21, 27, 'Baya kumbona ke ngelo xesha uNyana woMntu esiza ngelifu, enamandla nobuqaqawuli obukhulu.', 'XHO22'),
(46, 21, 28, 'Ke kaloku, zakubon’ ukuba ezi zinto ziyaqala ukuhla, sukani nime, niziphakamise iintloko zenu; kuba iyasondela inkululeko yenu.', 'XHO22'),
(46, 21, 29, 'Ke kaloku wathetha umzekeliso kubo; Wukhangele umkhiwane nemithi yonke;', 'XHO22'),
(46, 21, 30, 'xa iselithe yavelisa amagqabi, niyabona nazi ngokwenu ukuba ihlobo selikufuphi.', 'XHO22'),
(46, 21, 31, 'Kwangokunjalo nani, xa nithe nazibona ezi zinto zisihla, yazini ukuba bukufuphi ubukumkani bukaThixo.', 'XHO22'),
(46, 21, 32, 'Inene ndithi kuni, Asisayi kudlula esi sizukulwana, zide zihle zonke ezi zinto.', 'XHO22'),
(46, 21, 33, 'Liya kudlula izulu nehlabathi, kodwa amazwi am akayi kudlula.', 'XHO22'),
(46, 21, 34, 'Zilumkeleni ke, hleze zisindwe iintliziyo zenu kukuzingxala, nakukunxila, nangamaxhala obu bomi, ize loo mini inifikele ngebhaqo.', 'XHO22'),
(46, 21, 35, 'Kuba iya kubazela njengomgibe bonke abahleli phezu kwehlabathi lonke.', 'XHO22'),
(46, 21, 36, 'Phaphani ngoko, amaxesha onke nikhunga, ukuze kuthiwe nikufanele ukuzisaba zonke ezo zinto ziza kuhla, nokumiswa phambi koNyana woMntu.', 'XHO22'),
(46, 21, 37, 'Ke kaloku, emini ebefundisa etempileni; waphuma ebusuku, waya wahlala entabeni ekuthiwa yeyemiNquma.', 'XHO22'),
(46, 21, 38, 'Baye bonke abantu besiza kuye kwakusasa etempileni, ukuba bamve.', 'XHO22'),
(46, 22, 1, 'Ke kaloku, wawusondela umthendeleko wezonka ezingenagwele, ekuthiwa yipasika.', 'XHO22'),
(46, 22, 2, 'Baye ababingeleli abakhulu nababhali befuna ukuba bangathini na ukumbulala; ngokuba bebesoyika abantu.', 'XHO22'),
(46, 22, 3, 'Wangena ke uSathana kuYuda, ogama limbi linguSikariyoti, ongowenani labalishumi elinababini.', 'XHO22'),
(46, 22, 4, 'Wemka, waya wathetha nababingeleli abakhulu nabaphathi betempile, ukuba angáthini na ukumnikela kubo.', 'XHO22'),
(46, 22, 5, 'Bavuya ke, banqophisana naye ukumnika imali,', 'XHO22'),
(46, 22, 6, 'wabavumela, wamana efuna ithuba elimlungeleyo lokumnikela kubo, kungekho siphendu.', 'XHO22'),
(46, 22, 7, 'Wafika ke umhla wezonka ezingenagwele, ekwakumele ukuba kubingwe ngawo imvana yepasika.', 'XHO22'),
(46, 22, 8, 'Wesusa uPetros noYohane, esithi, Hambani niye kusilungiselela ipasika, ukuze siyidle.', 'XHO22'),
(46, 22, 9, 'Bathe ke bona kuye, Ùfuna ukuba siyilungise phi na?', 'XHO22'),
(46, 22, 10, 'Uthe ke yena kubo, Yabonani, nakungena kuwo umzi lo, nohlangatyezwa ngumntu ethwele umphanda wamanzi; mlandeleni, niye endlwini angena kuyo.', 'XHO22'),
(46, 22, 11, 'Nothi kumninindlu, Uthi uMfundisi kuwe, Liphi na igumbi labahambi, apho ndingadlela khona ipasika nabafundi bam?', 'XHO22'),
(46, 22, 12, 'Yena wonibonisa igumbi eliphezulu, elikhulu, landlelwe; lungisani apho ke.', 'XHO22'),
(46, 22, 13, 'Bemka ke, bafumana kunjengoko ebetshilo kubo, bayilungisa ipasika.', 'XHO22'),
(46, 22, 14, 'Uthe ke lakufika ilixa, wahlala phantsi, nabapostile abalishumi elinababini kunye naye.', 'XHO22'),
(46, 22, 15, 'Wathi kubo, Asikuko nokuba ndinqwenile ukuyidla nani le pasika, ndingekabuva ubunzima;', 'XHO22'),
(46, 22, 16, 'Kuba ndithi kuni, Andiyi kubuya ndibe sadla kuyo, ide izaliseke ebukumkanini bukaThixo.', 'XHO22'),
(46, 22, 17, 'Wayithabatha nendebe, wabulela, wathi, Yithabatheni, nabelane ngayo;', 'XHO22'),
(46, 22, 18, 'Kuba ndithi kuni, Andisayi kusela kwisiqhamo somdiliya, bude bufike ubukumkani bukaThixo.', 'XHO22'),
(46, 22, 19, 'Wathabatha isonka, wathi akubulela, wasiqhekeza, wabanika, esithi, Ngumzimba wam lo, onikelwa ngenxa yenu; oku kwenzeleni ukundikhumbula.', 'XHO22'),
(46, 22, 20, 'Kwangokunjalo nayo indebe wayithabatha, kwakukhovwa ukudliwa, esithi, Le ndebe ingumnqophiso omtsha osegazini lam, eliphalalela nina.', 'XHO22'),
(46, 22, 21, 'Kodwa niyabona, isandla sondingcatshayo sinam etafileni apha.', 'XHO22'),
(46, 22, 22, 'uNyana woMntu okunene uyemka njengoko kumisiweyo; kodwa ke, yeha, loo mntu angcatshwa nguye!', 'XHO22'),
(46, 22, 23, 'Baqala bona ukubuzana phakathi kwabo, ukuba ingaba ke nguwuphi na kubo, oza kuyenza le nto.', 'XHO22'),
(46, 22, 24, 'Ke kaloku kwabakho nokubangisana phakathi kwabo, kokuthi, nguwuphi na kubo ekungathiwa ngomkhulu.', 'XHO22'),
(46, 22, 25, 'Uthe ke yena kubo, Ookumkani beentlanga bayazigagamela; kwanabo benza ngegunya kuzo, kuthiwa ngabancedi.', 'XHO22'),
(46, 22, 26, 'Ze ningabi njalo; omkhulu kuni makabe njengomnci; nomkhulu, makabe njengokhonzayo.', 'XHO22'),
(46, 22, 27, 'Kuba nguwuphi na omkhulu: ngulowo uhleli esithebeni, ngulowo ulungiselelayo, kusini na? Asinguye na ohleli esithebeni? phakathi kwenu ndinjengomlungiseleli.', 'XHO22'),
(46, 22, 28, 'Nina ke ningabo bahlala nam ekuhendweni kwam;', 'XHO22'),
(46, 22, 29, 'Nam ndinimisela ubukumkani, njengokuba uBawo endimisele mna;', 'XHO22'),
(46, 22, 30, 'ukuze nidle, nisele, esithebeni sam ebukumkanini bam, nihlale ezitroneni, nigweba izizwe ezilishumi elinazibini zakwaSirayeli.', 'XHO22'),
(46, 22, 31, 'Ithe ke iNkosi, Simon, Simon, uyabona, uSathana ubenibanga, ukuba anele njengengqolowa.', 'XHO22'),
(46, 22, 32, 'Ke mna ndikhungile ngawe, ukuba ukholo lwakho lungapheli; nawe ke, xa ubuye wabuya, uze ubazimase abazalwana bakho.', 'XHO22'),
(46, 22, 33, 'Uthe ke yena kuye, Nkosi, sendikulungele ukuya nawe nasentolongweni, nasekufeni.', 'XHO22'),
(46, 22, 34, 'Ithe ke yona, Ndithi kuwe, Petros, ayisayi kulila namhlanje inkuku, ungekakhanyeli kathathu ukuba uyandazi.', 'XHO22'),
(46, 22, 35, 'Yathi kubo, Okuya ndanithuma ningenangxowa yamali, nangxowa yamphako, nazimbadada, khe naswela nto na? Bathi ke bona, Akukho nto.', 'XHO22'),
(46, 22, 36, 'Ithe ke kubo, Kungokunje, lowo unengxowa yemali makayithabathe, enjenjalo nonengxowa yomphako; nalowo usweleyo, makathengise ngengubo yakhe, athenge ikrele.', 'XHO22'),
(46, 22, 37, 'Kuba ndithi kuni, Oku kubhaliweyo kusamele ukufezeka kum, khona oku: Wabalwa nabachasi-mthetho;', 'XHO22'),
(46, 22, 38, 'Bathi ke bona, Nkosi, uyabona, nanga amakrele emabini. Uthe ke yena kubo, Kwanele.', 'XHO22'),
(46, 22, 39, 'Waphuma ke, waya ngokwesiko lakhe eNtabeni yemiNquma; Bamlandela ke nabafundi bakhe.', 'XHO22'),
(46, 22, 40, 'Akufika kuloo ndawo, wathi kubo, Thandazani, ukuze ningangeni ekuhendweni.', 'XHO22'),
(46, 22, 41, 'Waziqhiwula yena kubo umgama ongangokugibisela ilitye, waguqa ngamadolo, wathandaza,', 'XHO22'),
(46, 22, 42, 'esithi, Bawo, ukuba uyavuma, mayidlule le ndebe, kum; kodwa makungabi kukuthanda kwam, makube kokwakho okwenzekayo.', 'XHO22'),
(46, 22, 43, 'Kwabonakala ke kuye isithunywa, sivela ezulwini, simomeleza.', 'XHO22'),
(46, 22, 44, 'Esentlungwini enkulu, kwaba kukhona wathandaza enyamekile; kwasuka ukubila kwakhe kwaba njengamathontsi egazi, esiwa emhlabeni.', 'XHO22'),
(46, 22, 45, 'Wavuka ekuthandazeni, weza kubafundi bakhe, wabafumana belele ngokuba buhlungu kwentliziyo.', 'XHO22'),
(46, 22, 46, 'Wathi kubo, Yini na ukuba nilale? vukani nithandaze, ukuze ningangeni ekuhendweni.', 'XHO22'),
(46, 22, 47, 'Kwathi, esathetha, kwathi gqi indimbane, waye nalowo kuthiwa nguYuda, omnye weshumi elinababini, ehamba phambi kwabo; wasondela kuYesu eza kumanga.', 'XHO22'),
(46, 22, 48, 'Uthe ke uYesu kuye, Yuda, umngcatsha uNyana woMntu ngokumanga na?', 'XHO22'),
(46, 22, 49, 'Ke kaloku abo babemphahlile, bakukubona okuza kuhla, bathi kuye, Nkosi, sixabele na ngekrele?', 'XHO22'),
(46, 22, 50, 'Omnye kubo abo wamxabela umkhonzi wombingeleli omkhulu, wamnqamla indlebe yokunene.', 'XHO22'),
(46, 22, 51, 'Waphendula ke uYesu wathi, Yekani, kwanele. Wayichukumisa indlebe yakhe, wamphilisa.', 'XHO22'),
(46, 22, 52, 'Uthe ke uYesu kubabingeleli abakhulu, nabaphathi betempile, namadoda amakhulu, ababeze kuye, Niphume ninamakrele neentonga na, ngathi niphumele isihange?', 'XHO22'),
(46, 22, 53, 'Bendinani imihla ngemihla etempileni, anolula zandla ngakum; kambe eli lilixa lenu, negunya lobumnyama.', 'XHO22'),
(46, 22, 54, 'Bambambile ke, bamrhola, bamsa endlwini yombingeleli omkhulu; Waza uPetros walandela ekude.', 'XHO22'),
(46, 22, 55, 'Ke kaloku, bakubon’ ukuba babase umlilo phakathi kwentendelezo, behleli phantsi ndawonye, uthe wahlala uPetros phakathi kwabo.', 'XHO22'),
(46, 22, 56, 'Ke umkhonzazana othile umbonile ehleli ebandezini, wamjonga wathi, Nalo ube enaye.', 'XHO22'),
(46, 22, 57, 'Wakhanyela ke yena, esithi, Ntokazindini, andimazi.', 'XHO22'),
(46, 22, 58, 'Kwakuba mzuzwana, omnye wambona wathi, Nawe ungowabo. Wathi ke uPetros, Mntundini, andinguye.', 'XHO22'),
(46, 22, 59, 'Kwakuba kuhambe ilixa ngathi linye, omnye uqinisele wathi, Inyaniso, nalo ubenaye; kuba ukwangowaseGalili.', 'XHO22'),
(46, 22, 60, 'Uthe ke uPetros, Mntundini, andiyazi into oyithethayo. Kwaoko, esathetha, yalila inkuku.', 'XHO22'),
(46, 22, 61, 'Yajika iNkosi, yondela kuPetros. Walikhumbula ke uPetros ilizwi leNkosi, njengoko ibithe kuye, Ingekalili inkuku, uya kundikhanyela kathathu.', 'XHO22'),
(46, 22, 62, 'Waza uPetros, ephume phandle, walila ngokukrakra.', 'XHO22'),
(46, 22, 63, 'Aye amadoda ambambileyo uYesu edlala ngaye, emtyakatya.', 'XHO22'),
(46, 22, 64, 'Athe akuba emgqubuthele, ambetha ebusweni, ambuza esithi, Profeta; ngubani na lo ukubethileyo?', 'XHO22'),
(46, 22, 65, 'Ayethetha nezinye izinto ezininzi ngaye.', 'XHO22'),
(46, 22, 66, 'Kwathi kwakusa, ahlanganisana amadoda amakhulu abantu, nababingeleli abakhulu, nababhali, bamrholela entlanganisweni yawo yamatyala, besithi,', 'XHO22'),
(46, 22, 67, 'Unguye na uKristu? Sixelele. Uthe ke yena kubo, Ukuba ndithe ndanixelela, anisayi kukholwa;', 'XHO22'),
(46, 22, 68, 'nokuba ke ndithe ndanibuza, anisayi kundiphendula, anisayi kundikhulula.', 'XHO22'),
(46, 22, 69, 'Ethabathela kweli xa, uNyana woMntu uya kuba ehleli ekunene kwamandla kaThixo.', 'XHO22'),
(46, 22, 70, 'Bathi ke bonke, Unguye na ke wena uNyana kaThixo? Uthe ke yena kubo, Nina nithi ndinguye.', 'XHO22'),
(46, 22, 71, 'Bathi ke bona, Sisabufunelani na ubungqina? kuba thina ngokwethu sivile ngomlomo wakhe.', 'XHO22'),
(46, 23, 1, 'Yesuka yonke inkitha yabo, yamrholela kuPilato.', 'XHO22'),
(46, 23, 2, 'Aza aqala ukummangalela, esithi, Simfumene lo elahlekisa uhlanga, engavumi ukuba kunikelwe irhafu kuKesare, esithi, unguye uKristu uKumkani.', 'XHO22'),
(46, 23, 3, 'Wambuza ke uPilato, esithi, Wena unguye na uKumkani wamaYuda? Waphendula ke wathi kuye, Nawe utsho.', 'XHO22'),
(46, 23, 4, 'Uthe ke uPilato kubabingeleli abakhulu nakwizihlwele, Andifumani tyala kulo mntu.', 'XHO22'),
(46, 23, 5, 'Kwaba kukhona ke bajokisayo, besithi, Uxhokonxa abantu, efundisa kulo lonke elakwaYuda, eqalele kwelaseGalili, wada wezisa apha.', 'XHO22'),
(46, 23, 6, 'Ke kaloku uPilato, akuva elaseGalili, wabuza ukuba umntu lo ngowaseGalili na.', 'XHO22'),
(46, 23, 7, 'Kwathi, akuqonda ukuba ungowolawulo lukaHerode, wamthumela kuHerode, owayeseseYerusalem naye ngelo xesha.', 'XHO22'),
(46, 23, 8, 'Ke kaloku uHerode, akumbona uYesu, wavuya kunene; kuba bekumzuzu ethanda ukumbona, ngenxa yokuba ebeve kuthethwa izinto ezininzi ngaye; wayethembe ukubona umqondiso usenziwa nguye.', 'XHO22'),
(46, 23, 9, 'Wayembuza ke amazwi amaninzi; kodwa akamphendulanga nento.', 'XHO22'),
(46, 23, 10, 'Bema ke ababingeleli abakhulu nababhali, bammangalela kabukhali.', 'XHO22'),
(46, 23, 11, 'uHerode ke, kwanempi yakhe, wamenza into engento, wadlala ngaye, wamthi wambu ngengubo eqaqambileyo, wamthumela kuPilato.', 'XHO22'),
(46, 23, 12, 'Kwangaloo mini baba zizihlobo ke uPilato noHerode; kuba ngenxa engaphambili babenobutshaba phakathi kwabo.', 'XHO22'),
(46, 23, 13, 'Uthe ke uPilato, ebabizele ndawonye ababingeleli abakhulu, nabaphathi, nabantu,', 'XHO22'),
(46, 23, 14, 'wathi kubo, Nizise kum lo mntu, ngathi ngophambukisa abantu; niyabona ke, ndimncinile mna phambi kwenu, andafumana tyala kulo mntu, kwezo zinto nimmangalele ngazo;', 'XHO22'),
(46, 23, 15, 'KwanoHerode, kuba ndanithumela kuye. Niyabona, akenziwanga nto ifanelwe kukufa.', 'XHO22'),
(46, 23, 16, 'Ndothi ke ngoko, ndakumbetha, ndimkhulule.', 'XHO22'),
(46, 23, 17, 'Kuba umelwe kukuthi abakhululele abe mnye ngomthendeleko.', 'XHO22'),
(46, 23, 18, 'Bankqangaza bonke kwaoko, besithi, Msuse lo, usikhulule uBharabhas,', 'XHO22'),
(46, 23, 19, 'owathi ngenxa yesaqunge esithile esabakho phakathi komzi, nangenxa yokubulala, waphoswa entolongweni.', 'XHO22'),
(46, 23, 20, 'Waphinda ngoko uPilato wathetha kubo, efuna ukumkhulula uYesu.', 'XHO22'),
(46, 23, 21, 'Basuka ke bona bankqangaza, besithi, Mbethelele emnqamlezweni!', 'XHO22'),
(46, 23, 22, 'Uthe ke yena okwesithathu kubo, Kuba enze bubi buni na lo? Andifumananga tyala lakufa kuye; ndothi ngoko, ndakumbetha, ndimkhulule.', 'XHO22'),
(46, 23, 23, 'Bamxina ke bona ngezandi ezikhulu zamazwi, becela ukuba abethelelwe emnqamlezweni; Ayeyisa amazwi abo nezababingeleli abakhulu.', 'XHO22'),
(46, 23, 24, 'Waza uPilato wagweba ngelithi, makwenziwe ngokwesicelo sabo.', 'XHO22'),
(46, 23, 25, 'Wabakhululela ke owayethe ngenxa yesaqunge nokubulala waphoswa entolongweni, lowo babecela yena; wamnikela uYesu ekuthandeni kwabo.', 'XHO22'),
(46, 23, 26, 'Kuthe ke xa bamrholayo, babamba Simon uthile, waseKirene, obevela emaphandleni; bawubeka phezu kwakhe umnqamlezo, ukuba awuthwale emva koYesu.', 'XHO22'),
(46, 23, 27, 'Wayelandelwa ke yinkitha eninzi yabantu, neyabafazi ababemmbambazelela, bemlilela.', 'XHO22'),
(46, 23, 28, 'Wabaguqukela ke uYesu, wathi, Zintombi zaseYerusalem, musani ukundililela mna; kanye zilileleni nina, nabantwana benu;', 'XHO22'),
(46, 23, 29, 'Kuba niyabona, iyeza imihla ekuya kuthiwa ngayo, Banoyolo abaziidlolo, nezizalo ezingazalanga, namabele anganyisanga.', 'XHO22'),
(46, 23, 30, 'Oko baya kuqala bathi ezintabeni, Siweleni; nakwiinduli, Sigubungeleni.', 'XHO22'),
(46, 23, 31, 'Ukuba benza ezi zinto komanzi umthi, kuya kuba njani na kowomileyo?', 'XHO22'),
(46, 23, 32, 'Ke kaloku baberhola nabanye abangabambi, bebabini, bengabenzi bobubi, beya kubulawa kunye naye.', 'XHO22'),
(46, 23, 33, 'Bakufika kwindawo leyo kuthiwa luKakayi, bambethelela emnqamlezweni khona apho uYesu, kwanabenzi bobubi abo, omnye ngasekunene, nomnye ngasekhohlo.', 'XHO22'),
(46, 23, 34, 'Wathi ke uYesu, Bawo, baxolele; kuba abayazi into abayenzayo. Babelana ngeengubo zakhe, benza amaqashiso.', 'XHO22'),
(46, 23, 35, 'Baye bemi abantu bebonela. Babemsinekela ke nabaphathi bamaYuda kunye nabo, besithi, Wabasindisa abanye; makazisindise, ukuba unguye uKristu, umnyulwa kaThixo.', 'XHO22'),
(46, 23, 36, 'Eza ke kuye amasoldati edlala ngaye, emnika iviniga,', 'XHO22'),
(46, 23, 37, 'besithi, Ukuba wena unguye uKumkani wamaYuda, zisindise.', 'XHO22'),
(46, 23, 38, 'Kwaye ke kubhalwe nombhalo phezu kwakhe, ngamagama esiGrike, nawesiRoma, nawesiHebhere, kwathiwa, lo nguKumkani wamaYuda.', 'XHO22'),
(46, 23, 39, 'Ke kaloku, omnye wabenzi bobubi ababexhonyiwe wayemnyelisa, esithi, Ukuba wena unguye uKristu, zisindise, nathi usisindise.', 'XHO22'),
(46, 23, 40, 'Waphendula ke omnye wamkhalimela, esithi, Akumoyiki na wena uThixo, ukwakoku kugwetywa nje?', 'XHO22'),
(46, 23, 41, 'Thina ke okunene sigwetywe ngobulungisa; kuba thina samkela umvuzo wemisebenzi yethu; kodwa yena lo akenzanga nto ingendawo.', 'XHO22'),
(46, 23, 42, 'Wathi kuYesu, Uze undikhumbule, Nkosi, xa uthe weza usebukumkanini bakho.', 'XHO22'),
(46, 23, 43, 'uYesu wathi kuye, Inene, ndithi kuwe, Namhlanje uya kuba nam eParadisi.', 'XHO22'),
(46, 23, 44, 'Ke kaloku, kwakumalunga nelixa lesithandathu, kwaza kwakho ubumnyama phezu kwawo umhlaba uphela, kwada kwalilixa lesithoba.', 'XHO22'),
(46, 23, 45, 'Laba mnyama ilanga, lasuka ikhuselo lengcwele lakrazuka kubini.', 'XHO22'),
(46, 23, 46, 'Wadanduluka ngezwi elikhulu uYesu, wathi, Bawo, ndiyawunikela ezandleni zakho umoya wam. Akuba etshilo, waphuma umphefumlo.', 'XHO22'),
(46, 23, 47, 'Ke kaloku umthetheli-khulu, akukubona okuhlileyo, wazukisa uThixo, esithi, Inene, lo mntu ubelilungisa.', 'XHO22'),
(46, 23, 48, 'Zathi zonke izihlwele ezabe zibuthelene kuloo mbonelo, zakukubona okuhlileyo, zabuya zibetha izifuba zazo.', 'XHO22'),
(46, 23, 49, 'Baye ke bonke abazana naye bemi kude, kwanabafazi ababelandelene naye, bevela kwelaseGalili, bezikhangela ezi zinto.', 'XHO22'),
(46, 23, 50, 'Kwabonakala kukho indoda egama linguYosefu, iphakathi; waye eyindoda elungileyo, elilungisa;', 'XHO22'),
(46, 23, 51, 'Yena waye engavumelananga necebo labo, nesenzo sabo; wayengowaseArimati, umzi wamaYuda, owayebulindile naye ubukumkani bukaThixo;', 'XHO22'),
(46, 23, 52, 'eyathi yona yeza kuPilato, yawucela umzimba kaYesu.', 'XHO22'),
(46, 23, 53, 'Yawuthula, yawusongela kwilinen entle; yawubeka engcwabeni elixholwe etyeni, ekungazanga kulale bani kulo.', 'XHO22'),
(46, 23, 54, 'Ke kaloku loo mhla wawungowokulungiselela, nesabatha ibisondele.', 'XHO22'),
(46, 23, 55, 'Kwathi nabafazi ababehamba naye, bevela kwelaseGalili, balikhangela ingcwaba, nokubekwa komzimba wakhe.', 'XHO22'),
(46, 23, 56, 'Babuya ke, balungisa ubulawu namafutha aqholiweyo. Baphumla ngomhla wesabatha ngokomthetho.', 'XHO22'),
(46, 24, 1, 'Ke kaloku, ngolokuqala usuku lweveki, ngesifingo, beza engcwabeni, bephethe ubulawu ababebulungisile, kukho nabanye abathile kubo.', 'XHO22'),
(46, 24, 2, 'Balifumana ke ilitye liqengqiwe, lesuka engcwabeni.', 'XHO22'),
(46, 24, 3, 'Bakuba bengenile, abawufumananga umzimba weNkosi uYesu.', 'XHO22'),
(46, 24, 4, 'Kwathi, ekukhohlweni kwabo yile nto, kwabonakala amadoda amabini, ebafikela, eneengubo ezibengezelayo.', 'XHO22'),
(46, 24, 5, 'Ke kaloku, bakubon’ ukuba bangenelwe kukoyika, beqondele emhlabeni, athi kubo, Yini na ukuba ophilileyo nimfune phakathi kwabafileyo?', 'XHO22'),
(46, 24, 6, 'Akakho apha, uvukile; khumbulani ukuthetha kwakhe kuni, oko wayeseseGalili,', 'XHO22'),
(46, 24, 7, 'esithi, uNyana woMntu umelwe kukuthi anikelwe ezandleni zabantu aboni, abethelelwe emnqamlezweni, athi ngomhla wesithathu abuye avuke.', 'XHO22'),
(46, 24, 8, 'Bawakhumbula ke amazwi akhe;', 'XHO22'),
(46, 24, 9, 'bebuyile engcwabeni, bazibika zonke ezo zinto kwabalishumi elinamnye, nakubo bonke abanye.', 'XHO22'),
(46, 24, 10, 'YayinguMariya waseMagadala, noYohana, noMariya unina kaYakobi, nabanye abafazi ababenabo, abazixelayo ezo zinto kubapostile.', 'XHO22'),
(46, 24, 11, 'Asuka amazwi abo abonakala ngathi yinto nje engento kubo, baza abakholwa.', 'XHO22'),
(46, 24, 12, 'Esukile ke uPetros, wagidima waya engcwabeni; athi eqondele, azibone iilinen abesongelwe kuzo, zilele zodwa; wemka, emangalisiwe koko kuhlileyo.', 'XHO22'),
(46, 24, 13, 'Kwabonakala kusiya babini bakubo, kwangaloo mini, kumzana ozitadiya zimashumi mathandathu, umgama wawo neYerusalem, ogama liyiEmawusi.', 'XHO22'),
(46, 24, 14, 'Baye bona bexoxa bodwa ngazo zonke izinto ezibe zihlile.', 'XHO22'),
(46, 24, 15, 'Kwathi besathetha bebuzana, uYesu ngenkqu wasondela wahamba nabo.', 'XHO22'),
(46, 24, 16, 'Kuloko amehlo abo ayebanjiwe, ukuba bangamazi.', 'XHO22'),
(46, 24, 17, 'Wathi kubo, Ziindawo zini na ezi nincokola ngazo, nihamba nibe nithe matshamatsha?', 'XHO22'),
(46, 24, 18, 'Uphendule ke omnye, lo ugama linguKleyopa, wathi kuye, Umhambi eYerusalem apha nguwe wedwa na, ukuba ungazazi nje izinto ezihle khona ngazo ezi mini?', 'XHO22'),
(46, 24, 19, 'Wathi kubo, Ziphi na? Bathi ke bona kuye, EzingoYesu waseNazarete, obeyindoda engumprofeti, enobunkunkqele ekwenzeni nasekuthetheni, phambi koThixo nabo bonke abantu;', 'XHO22'),
(46, 24, 20, 'ukuba bathini na ababingeleli abakhulu nabaphathi bethu ukumnikela ukuba agwetywe afe, bambethelela nasemnqamlezweni.', 'XHO22'),
(46, 24, 21, 'Ke kaloku, thina besithembe ukuba nguye oza kukhulula uSirayeli ngentlawulelo; kunjalonje ke, namhla lusuku lwesithathu zenzekile ezo zinto.', 'XHO22'),
(46, 24, 22, 'Asuka nabafazi abathile ababevela kuthi aba basikhwankqisa, ababeyile engcwabeni kwakusasa;', 'XHO22'),
(46, 24, 23, 'Zakuba zingawufumani umzimba wakhe, zeza zisithi, zibone nombono wezithunywa zezulu, ezibe zisithi uhleli.', 'XHO22'),
(46, 24, 24, 'Basuka nabathile ababenathi, baya engcwabeni, bafika kunjengoko babetshilo abafazi; ke yena abambonanga.', 'XHO22'),
(46, 24, 25, 'Wathi ke yena kubo, Oyi!', 'XHO22'),
(46, 24, 26, 'uKrestu ebengamelwe na kukuthi abuve obu bunzima, aze angene eluzukweni lwakhe?', 'XHO22'),
(46, 24, 27, 'Uqalele ke kuMoses nakubo bonke abaprofeti, wabachazela kuzo zonke izibhalo iindawo ezingaye.', 'XHO22'),
(46, 24, 28, 'Basondela kuloo mzana babesiya kuwo; wanga yena uyegqitha.', 'XHO22'),
(46, 24, 29, 'Bamzama, besithi, Hlala nathi; ngokuba kuza kuhlwa, nemini seyimkile. Wangena ukuba ahlale nabo.', 'XHO22'),
(46, 24, 30, 'Kwathi, ekungqengqeni kwakhe ngasesithebeni ndawonye nabo, wasithabatha isonka, wasikelela, waqhekeza, wabanika.', 'XHO22'),
(46, 24, 31, 'Aqabuka amehlo abo, bamazi; Wanyamalala emehlweni abo.', 'XHO22'),
(46, 24, 32, 'Batshono ukuthi, Intliziyo yethu ibingavuthi na ngaphakathi kwethu, xa ebethetha nathi endleleni, naxa ebesityhilela izibhalo?', 'XHO22'),
(46, 24, 33, 'Besuka kwangelo lixa, babuyela eYerusalem, babafumana abalishumi elinamnye, bequkene ndawonye kwanabo babenabo,', 'XHO22'),
(46, 24, 34, 'besithi, iNkosi inene ivukile, yabonakala kuSimon.', 'XHO22'),
(46, 24, 35, 'Baye nabo bezichankcisa izinto zendlela, nokwazeka kwayo kubo ekuqhekezeni isonka.', 'XHO22'),
(46, 24, 36, 'Ke kaloku, bakubon’ ukuba bayazithetha ezo zinto, uYesu ngokwakhe wema phakathi kwabo, wathi kubo, Uxolo malube nani.', 'XHO22'),
(46, 24, 37, 'Besuka bankwantya, bangenelwa kukoyika, beba babone umshologu.', 'XHO22'),
(46, 24, 38, 'Wathi kubo, Yini na ukuba nikhathazeke? Yini na ukuba kunyuke izicamango ezintliziyweni zenu?', 'XHO22'),
(46, 24, 39, 'Khangelani izandla zam neenyawo zam, ngokuba ndim kanye; ngokuba umshologu awunanyama namathambo, njengokuba nindibona ndinawo mna.', 'XHO22'),
(46, 24, 40, 'Utshilo, wababonisa izandla neenyawo zakhe.', 'XHO22'),
(46, 24, 41, 'Ke kaloku, bakubon’ ukuba abakakholwa, ngenxa yovuyo, bemangalisiwe, wathi kubo, Ninanto na idliwayo apha?', 'XHO22'),
(46, 24, 42, 'Bamnika ke intwana yentlanzi eyosiweyo, neyenqatha lobusi.', 'XHO22'),
(46, 24, 43, 'Wathabatha, wadla phambi kwabo.', 'XHO22'),
(46, 24, 44, 'Uthe ke kubo, Ngawo la amazwi endawathethayo kuni ndisenani, okokuba zimelwe kukuzaliseka zonke izinto ezibhaliweyo emthethweni kaMoses, nasebaprofetini, nasezindumisweni, ukuba zizaliseke, mna.', 'XHO22'),
(46, 24, 45, 'Wandula wayivula ingqiqo yabo, ukuze baziqonde izibhalo;', 'XHO22'),
(46, 24, 46, 'wathi kubo, Kubhaliwe ngokunjalo, umelwe ngokunjalo uKristu kukuthi eve ubunzima, abuye avuke kwabafileyo ngomhla wesithathu;', 'XHO22'),
(46, 24, 47, 'nokuba kuvakaliswe egameni lakhe inguquko noxolelo lwezono kuzo zonke iintlanga, kuqalelwa eYerusalem.', 'XHO22'),
(46, 24, 48, 'Nina ke ningamangqina ezi zinto.', 'XHO22'),
(46, 24, 49, 'Yabonani, mna ndilithumela kuni idinga likaBawo; ke nina hlalani kuwo umzi oyiYerusalem, nide nambathiswe amandla aphuma enyangweni.', 'XHO22'),
(46, 24, 50, 'Ke kaloku wabarholela ngaphandle, kwada kwaya eBhetani, waziphakamisa izandla zakhe, wabasikelela.', 'XHO22'),
(46, 24, 51, 'Kwathi, ekubasikeleleni kwakhe, wahlukana nabo, wenyuswa wasiwa emazulwini.', 'XHO22'),
(46, 24, 52, 'Baza bona, bakuba bequbudile kuye, babuyela eYerusalem benovuyo olukhulu.', 'XHO22'),
(46, 24, 53, 'Bamana behleli etempileni, bembonga uThixo. Amen.', 'XHO22'),
(6, 1, 1, 'Ekuqalekeni ube ekho uLizwi, waye uLizwi ekuye uThixo, waye uLizwi enguThixo.', 'XHO22'),
(6, 1, 2, 'Yena lowo ebekho kwasekuqalekeni kuThixo.', 'XHO22'),
(6, 1, 3, 'Izinto zonke zabakho ngaye; engekho yena, akubangakho nanye into ethe yabakho.', 'XHO22'),
(6, 1, 4, 'Kuye kwakukho ubomi; baye obo bomi baba lukhanyiso eluntwini.', 'XHO22'),
(6, 1, 5, 'Olu khanyiso lukhanya ebumnyameni; kwaye ubumnyama abuzange bukoyise ukukhanya kwalo.', 'XHO22'),
(6, 1, 6, 'Kwakho mntu uthunyiweyo evela kuThixo, ugama linguYohane.', 'XHO22'),
(6, 1, 7, 'Yena weza kungqina, engqinela ukukhanya, ukuze bonke bakholwe ngaye.', 'XHO22'),
(6, 1, 8, 'Yena lowo ubengelulo ukhanyiso; weza kungqina ngalo ukhanyiso.', 'XHO22'),
(6, 1, 9, 'Lowo ulukhanyiso lwenene olukhanyisela wonke umntu, wayesiza ehlabathini.', 'XHO22'),
(6, 1, 10, 'Ube esehlabathini ke, ihlabathi labakho ngaye, kodwa ihlabathi alamazi.', 'XHO22'),
(6, 1, 11, 'Weza kwabakhe, baza abakhe abamamkelanga.', 'XHO22'),
(6, 1, 12, 'Ke bonke abamamkelayo wabanika igunya lokuba babe ngabantwana bakaThixo, abo ke bakholwayo kwigama lakhe;', 'XHO22'),
(6, 1, 13, 'Abangazalwanga ngagazi, nangakuthanda kwanyama, nangakuthanda kwandoda; bazalwa nguThixo.', 'XHO22'),
(6, 1, 14, 'uLizwi ke waba ngumntu, waza wahlala phakathi kwethu. Sabubukela ubungangamsha bakhe awabunikwa nguYise, njengoko ekukuphela koNyana wakhe; ubungangamsha ke obuphuphuma ububele nenyaniso.', 'XHO22'),
(6, 1, 15, 'uYohane wangqina ngoLizwi; wadanduluka esithi, Ubenguye lo bendithetha yena, ndisithi, Lowo uzayo emva kwam, usuke waba phambi kwam, ngokuba waye etanci kum.', 'XHO22'),
(6, 1, 16, 'Enzalisekweni yakhe thina sonke samkela kwaubabalo phezu kobabalo.', 'XHO22'),
(6, 1, 17, 'Ngokuba umthetho wawiswa ngoMoses, lwabakho lona ubabalo nenyaniso ngoYesu Kristu.', 'XHO22'),
(6, 1, 18, 'Akukho namnye wakha wambona uThixo; uNyana okuphela kwamzeleyo, lowo usesifubeni sikaYise, nguye ocacise oko.', 'XHO22'),
(6, 1, 19, 'Nabu ke ubungqina bukaYohane, oko amaYuda athuma ababingeleli nabaLevi, bephuma eYerusalem, ukuze bambuze besithi, Wena ungubani na?', 'XHO22'),
(6, 1, 20, 'Wavuma yena, akakhanyela; wavuma ke esithi, Andinguye uKristu mna.', 'XHO22'),
(6, 1, 21, 'Bambuza, besithi, Untoni na ke? UnguEliya na? Wathi yena, Andinguye. Unguye na umprofeti lowo? Wathi yena, Hayi.', 'XHO22'),
(6, 1, 22, 'Athi ngoko kuye, Ungubani na? ukuze sibaphendule abo basithumileyo. Uthini na wena ngawe?', 'XHO22'),
(6, 1, 23, 'Wathi, Mna ndililizwi lodandulukayo entlango, lisithi, Yityenenezeni indlela yeNkosi, njengoko watshoyo uIsaya umprofeti.', 'XHO22'),
(6, 1, 24, 'Baye abo babethunyiwe bengabakubo abaFarisi.', 'XHO22'),
(6, 1, 25, 'Bambuza bathi kuye, Ubhaptizelani na phofu, ukuba wena akunguye uKristu, noEliya, nomprofeti lowo?', 'XHO22'),
(6, 1, 26, 'Wabaphendula uYohane wathi, Mna ndibhaptiza ngamanzi, kodwa phakathi kwenu kumi eningamaziyo nina.', 'XHO22'),
(6, 1, 27, 'Yena ngothe, esiza emva kwam nje, wesuka waba phambi kwam, endingafanelekileyo mna ukuba ndinyobulule umtya wembadada yakhe.', 'XHO22'),
(6, 1, 28, 'Ezi zinto zenzeka eBhetabhara, phesheya kweYordan, apho uYohane wayebhaptiza khona.', 'XHO22'),
(6, 1, 29, 'Ngengomso uYohane umbona uYesu esiza kuye, athi, Nantso iMvana kaThixo, ethwala isuse isono sehlabathi.', 'XHO22'),
(6, 1, 30, 'Nguye lo ndathi ngaye mna, Emva kwam kuza indoda, esuke yaba phambi, kwam; ngokuba yayitanci kum.', 'XHO22'),
(6, 1, 31, 'Ke nam bendingamazi; le nto ndeza mna, ndibhaptiza ngamanzi, kukuze ibonakalaliswe kuSirayeli.', 'XHO22'),
(6, 1, 32, 'uYohane wangqina, esithi, Ndimbonile uMoya, esihla njengehobe, ephuma ezulwini, wahlala phezu kwakhe.', 'XHO22'),
(6, 1, 33, 'Ke nam bendingamazi, kodwa lowo wandithumayo ukuba ndibhaptize ngamanzi, nguye owathi kum: ‘Lowo ùbone uMoya esihla phezu kwakhe, ahlale kuye, nguye obhaptiza ngoMoya oyiNgcwele.’', 'XHO22'),
(6, 1, 34, 'Nam ndibonile, ndingqinile, ukuba lo nguye uNyana kaThixo.', 'XHO22'),
(6, 1, 35, 'Ngengomso ubuye wema uYohane nababini kubafundi bakhe;', 'XHO22'),
(6, 1, 36, 'ondele kuYesu ehamba, uthi, Nantso iMvana kaThixo.', 'XHO22'),
(6, 1, 37, 'Bamva ethetha abafundi ababini, bamlandela uYesu.', 'XHO22'),
(6, 1, 38, 'Ke kaloku ujikile uYesu, ababone belandela, athi kubo, Nifuna ntoni na? Bathi ke bona kuye, Rabhi (oko kukuthi ngentetho evakalayo, Mfundisi), uhlala phi na?', 'XHO22'),
(6, 1, 39, 'Athi kubo, Yizani nibone. Beza, babona ke apho ahlala khona; bahlala naye loo mini;', 'XHO22'),
(6, 1, 40, 'YayinguAndreya, umntakwabo-Simon Petros, omnye wâbo bafundi babini beva ngoYohane, baza bamlandela uYesu.', 'XHO22'),
(6, 1, 41, 'Yena ufumana kuqala owakhe umzalwana, uSimon, athi kuye, Simfumene uMesiya (oko kukuthi ngentetho evakalayo, uKristu).', 'XHO22'),
(6, 1, 42, 'Wamsa kuYesu; uthe ke uYesu wondela kuye, wathi, Wena unguSimon, unyana kaYona; kuya kuthiwa unguKefas, oko kukuthi ngentetho evakalayo, unguKefas.', 'XHO22'),
(6, 1, 43, 'Ngengomso uYesu wafuna ukuphuma aye kwelaseGalili; ufumana uFilipu, athi kuye, Ndilandele.', 'XHO22'),
(6, 1, 44, 'Ke uFilipu ebengowaseBhetesayida, idolophu yakuloAndreya noPetros.', 'XHO22'),
(6, 1, 45, 'uFilipu ufumana uNataniyeli, athi kuye, Lowo uMoses wabhala ngaye emthethweni, kwanabaprofeti, simfumene, uYesu unyana kaYosefu, lowo waseNazarete.', 'XHO22'),
(6, 1, 46, 'Wathi uNataniyeli kuye, Inako na into elungileyo ukuphuma eNazarete? Athi uFilipu kuye, Yiza ubone.', 'XHO22'),
(6, 1, 47, 'uYesu wambona uNataniyeli esiza kuye, athi ngaye, Nanko umSirayeli inyaniso, ekungekho nkohliso kuye.', 'XHO22'),
(6, 1, 48, 'Athi uNataniyeli kuye, Undazi ngantoni na? Waphendula uYesu wathi kuye, Phambi kokuba uFilipu akubize, uphantsi komkhiwane, ndandikubona.', 'XHO22'),
(6, 1, 49, 'Aphendule uNataniyeli athi kuye, Rabhi, wena unguye uNyana kaThixo; wena unguye uKumkani wakwaSirayeli.', 'XHO22'),
(6, 1, 50, 'Waphendula uYesu wathi kuye, Ngokuba ndithe kuwe, Ndandikubona uphantsi komkhiwane, uyakholwa na? uya kubona izinto ezinkulu kunezi.', 'XHO22'),
(6, 1, 51, 'Athi kuye, Inene, inene, ndithi kuni, Emveni koku niya kulibona izulu livulekile, nezithunywa zikaThixo zinyuka zisihla phezu koNyana woMntu.', 'XHO22'),
(6, 2, 1, 'Kwathi ngomhla wesithathu kwabakho umsitho eKana yelaseGalili; waye unina kaYesu ekhona.', 'XHO22'),
(6, 2, 2, 'Wamenyelwa ke noYesu kwanabafundi bakhe emsithweni lowo.', 'XHO22'),
(6, 2, 3, 'Yakuphela iwayini, wathi unina kaYesu kuye, Abanawayini.', 'XHO22'),
(6, 2, 4, 'Uthi uYesu kuye, Yintoni na enam nawe, mfazi? ilixa lam alikafiki.', 'XHO22'),
(6, 2, 5, 'Athi unina kubalungiseleli, Asukuba ekuthetha kuni, kwenzeni.', 'XHO22'),
(6, 2, 6, 'Kwakumi khona ke imiphanda yamatye imithandathu, ngokwentlambululo yamaYuda, engena ngaminye iibhate ezimbini nantathu.', 'XHO22'),
(6, 2, 7, 'Athi kubo uYesu, Yizaliseni imiphanda ngamanzi. Bazizalisa zada zafikelela eludinini.', 'XHO22'),
(6, 2, 8, 'Athi kubo, Yikhani ngoku, nise kwinjoli. Bayithwala ke.', 'XHO22'),
(6, 2, 9, 'Ke kaloku, yakuweva injoli amanzi abenziwe iwayini, engayazi nalapho ivela khona;', 'XHO22'),
(6, 2, 10, 'Yathi kuye, Wonke umntu ubeka kuqala iwayini emnandi, aze akhuphe iwayini emnandi; 22 Xa bathe basela bakholisa, andule ukubeka enganeno kwaleyo; wena uyibandezile iwayini emnandi, kwada kwangoku.', 'XHO22'),
(6, 2, 11, 'Lo mqondiso wakhe wokuqala uYesu wawenza eKana yelaseGalili, walubonakalalisa uzuko lwakhe; bakholwa kuye abafundi bakhe.', 'XHO22'),
(6, 2, 12, 'Emva koko uYesu, nonina, nabantakwabo, nabafundi bakhe, baya eKapernahum, bahlala khona iintsukwana.', 'XHO22'),
(6, 2, 13, 'Yayikufuphi ke ipasika yamaYuda, wenyuka uYesu waya eYerusalem.', 'XHO22'),
(6, 2, 14, 'Wabafumana etempileni ababethengisa ngeenkomo nezimvu namahobe, nabananisa ngemali behleli.', 'XHO22'),
(6, 2, 15, 'Wenze iziniya ngentambo, wabakhupha bonke etempileni, kwanezimvu neenkomo; wayiphalaza imali yabananisi, wazibhukuqa neetafile;', 'XHO22'),
(6, 2, 16, 'Waza wathi nakwabo bathengisa ngamahobe: “Zikhupheni apha ezi zinto; musani ukuyenza indlu kaBawo indlu yokuthengisela.', 'XHO22'),
(6, 2, 17, 'Bakhumbula ke abafundi bakhe, ukuba kubhaliwe kwathiwa, Ukuzondelela indlu yakho kundidlile kanye.', 'XHO22'),
(6, 2, 18, 'Aphendula ke ngoko amaYuda, athi kuye, Usibonisa mqondiso mni na, uzenza nje ezi zinto?', 'XHO22'),
(6, 2, 19, 'Waphendula uYesu wathi kubo, Yichitheni le tempile, mna ke ndiya kuyivusa ngeentsuku ezintathu.', 'XHO22'),
(6, 2, 20, 'Athi ke ngoko amaYuda, Yayiminyaka imashumi mane anesithandathu nje isakhiwa le tempile, ke wena woyivusa ngeentsuku ezintathu na?', 'XHO22'),
(6, 2, 21, 'Kodwa yena ubethetha ngetempile yomzimba wakhe.', 'XHO22'),
(6, 2, 22, 'Kwathi akuvuka kwabafileyo, bakhumbula abafundi bakhe okokuba wayekuthethile oko kubo, basikholwa isibhalo, nelizwi awalithethayo uYesu.', 'XHO22'),
(6, 2, 23, 'Kwathi, xa uYesu wayeseJerusalem kwitheko lePasika, abantu abaninzi bakholwa kulo igama lakhe bakubona imimangaliso ayenzayo.', 'XHO22'),
(6, 2, 24, 'Ke yena uYesu akaziyekelanga kubo, ngenxa yokuba ebazi bonke;', 'XHO22'),
(6, 2, 25, 'Kwakungekho mfuneko yakuba kungqine bani ngomntu, kuba ubekwazi yena okungaphakathi komntu.', 'XHO22'),
(6, 3, 1, 'Kwaye kukho ke umntu wakubaFarisi, ogama linguNikodemo, umphathi wamaYuda.', 'XHO22'),
(6, 3, 2, 'Yena weza kuYesu ebusuku, wathi kuye, Rabhi, siyazi ukuba ungumfundisi ophuma kuThixo; kuba akukho namnye unako ukuyenza le miqondiso uyenzayo wena, ukuba uThixo akanaye.', 'XHO22'),
(6, 3, 3, 'Waphendula uYesu wathi kuye, Inene, inene, ndithi kuwe, Ukuba akathanga umntu azalwe ngokutsha, akanakububona ubukumkani bukaThixo.', 'XHO22'),
(6, 3, 4, 'Uthi uNikodimo kuye, Angathini na umntu ukuzalwa emdala? Unokungena okwesibini na esizalweni sikanina, azalwe?', 'XHO22'),
(6, 3, 5, 'Waphendula uYesu wathi, Inene, inene, ndithi kuwe, Ukuba umntu akathanga azalwe ngamanzi nanguMoya, akanakungena ebukumkanini bukaThixo.', 'XHO22'),
(6, 3, 6, 'Oko kuzelwe yinyama kuyinyama; Oko kuzelwe nguMoya kungumoya.', 'XHO22'),
(6, 3, 7, 'Musa ukumangaliswa kukuba ndithe kuwe, Nimelwe kukuthi nizalwe ngokutsha.', 'XHO22'),
(6, 3, 8, 'Umoya uphepheza apho uthanda khona, ùsive isandi sawo, kodwa ungawazi apho uvela khona, nalapho úya khona: bakwanjalo bonke abazelwe nguMoya.', 'XHO22'),
(6, 3, 9, 'Waphendula uNikodemo wathi kuye, Zingathini na ezi zinto ukuba nokubakho?', 'XHO22'),
(6, 3, 10, 'Waphendula uYesu wathi kuye, Wena, ungumfundisi nje wakwaSirayeli, akuzazi na ezi zinto?', 'XHO22'),
(6, 3, 11, 'Inene, inene, ndithi kuwe, Sithetha esikwaziyo, singqine esikubonileyo; nisuke ningabamkeli ubungqina bethu.', 'XHO22'),
(6, 3, 12, 'Ukuba ndinixelele izinto ezisemhlabeni, naza anakholwa, nothini na ukukholwa, ukuba ndithe ndanixelela izinto ezisemazulwini?', 'XHO22'),
(6, 3, 13, 'Akukho namnye unyukileyo waya emazulwini, ngulowo yedwa wehla ephuma emazulwini, uNyana woMntu, lowo usemazulwini.', 'XHO22'),
(6, 3, 14, 'Njengokuba ke uMoses wayiphakamisayo inyoka entlango, umelwe kukuphakanyiswa ngokunjalo uNyana woMntu;', 'XHO22'),
(6, 3, 15, 'ukuze bonke abakholwayo kuye bangatshabalali, koko babe nobomi obungunaphakade.', 'XHO22'),
(6, 3, 16, 'Kuba wenjenje uThixo ukulithanda kwakhe ihlabathi, ude wancama uNyana wakhe okuphela kwamzeleyo, ukuze bonke abakholwayo kuye bangatshabalali, koko babe nobomi obungunaphakade.', 'XHO22'),
(6, 3, 17, 'Kuba uThixo akamthumanga uNyana wakhe ehlabathini, ukuze aligwebe ihlabathi; ukuze ihlabathi lisindiswe ngaye.', 'XHO22'),
(6, 3, 18, 'Lowo ukholwayo kuye, akagwetywa; ke lowo ungakholwayo, uselegwetyiwe, ngokuba engakholwanga kwigana loNyana okuphela kwamzeleyo uThixo.', 'XHO22'),
(6, 3, 19, 'Ke kaloku, umgwebo nguwo lo, ukuba ukhanyiso lufikile ehlabathini, baza abantu bathanda ubumnyama ngaphezu kokhanyiso; kuba imisebenzi yabo ingendawo.', 'XHO22'),
(6, 3, 20, 'Kuba bonke abenza okubi, bayaluthiya ukhanyiso, bangezi elukhanyisweni, ukuze imisebenzi yabo ingohlwaywa.', 'XHO22'),
(6, 3, 21, 'Ke lowo uyenzayo inyaniso uyeza elukhanyisweni, ukuze imisebenzi yakhe ibonakalaliswe; ngokuba isetyenzelwe kuThixo.', 'XHO22'),
(6, 3, 22, 'Emveni kwezi zinto, weza uYesu nabafundi bakhe emhlabeni wakwaYuda; walibala khona apho nabo, wabhaptiza.', 'XHO22'),
(6, 3, 23, 'Waye ke noYohane ebhaptiza e-Enon, kufuphi neSalem, ngokuba amanzi abemaninzi khona apho; babesiza ke babhaptizwe.', 'XHO22'),
(6, 3, 24, 'Kuba ebengekaphoswa entolongweni uYohane.', 'XHO22'),
(6, 3, 25, 'Kwabakho ke imbuzwano kubafundi bakaYohane namaYuda ngokuhlanjululwa.', 'XHO22'),
(6, 3, 26, 'Beza kuYohane, bathi kuye, Rabhi, lowa wayenawe phesheya kweYordan, umngqinela, yabona, yena uyabhaptiza; beza kuye ke bonke.', 'XHO22'),
(6, 3, 27, 'Waphendula uYohane wathi, Akanakwamkela nto umntu, engayinikwanga ivela emazulwini.', 'XHO22'),
(6, 3, 28, 'Nani ngokwenu niyandingqinela ukuba ndathi, Andinguye uKristu mna; ndathi, Ndithunyiwe phambi kwakhe.', 'XHO22'),
(6, 3, 29, 'Lowo unomtshakazi ngumyeni; isihlobo ke somyeni, esimiyo simve, sivuya kunene ngenxa yezwi lomyeni; ngoko olu luvuyo lwam luzalisekile.', 'XHO22'),
(6, 3, 30, 'Yena umelwe kukuthi ande, ndinciphe ke mna.', 'XHO22'),
(6, 3, 31, 'Lowo uvela phezulu ungaphezu kwabo bonke; lowo ungowehlabathi ungowasemhlabeni, kwaye uthetha okwehlabathi. Lowo uvela ezulwini ungaphezu kwabo bonke;', 'XHO22'),
(6, 3, 32, 'akubonileyo ke wakuva, uyakungqina oko; akukho bani ubamkelayo ubungqina bakhe.', 'XHO22'),
(6, 3, 33, 'Lowo ubamkeleyo ubungqina bakhe, utywine waqinisela ukuthi, uThixo uyinyaniso;', 'XHO22'),
(6, 3, 34, 'kuba lowo uthunywe nguThixo, uthetha amazwi kaThixo; kuba uThixo akamniki uMoya ngomlinganiselo.', 'XHO22'),
(6, 3, 35, 'uYise uyamthanda uNyana; uzinikele zonke izinto esandleni sakhe.', 'XHO22'),
(6, 3, 36, 'Lowo ukholwayo kuNyana, unobomi obungunaphakade; ke lowo ungamviyo uNyana, akayi kubona bomi; ingqumbo kaThixo ihleli phezu kwakhe.', 'XHO22'),
(6, 4, 1, 'Ithe ngoko iNkosi, yakwazi ukuba abaFarisi bavile ukuba uYesu ugqithisile kuYohane ngokwenza nokubhaptiza abafundi;', 'XHO22'),
(6, 4, 2, '(Nakuba uYesu ngokwakhe wayengabhaptizi, kwakubhaptiza abafundi bakhe),', 'XHO22'),
(6, 4, 3, 'Yalishiya yona elakwaYuda, yabuya yaya kwelaseGalili.', 'XHO22'),
(6, 4, 4, 'Kwakufuneka ecande eSamariya.', 'XHO22'),
(6, 4, 5, 'Wafika kwidolophu yaseSamariya ekuthiwa yiSikare, kufuphi nendawo awathi uYakobi wayinika uJosefu unyana wakhe.', 'XHO22'),
(6, 4, 6, 'Lalilapho iqula likaYakobi. Uthe ke ngoko uYesu, ebulalekile kukuhamba, wahlala enjalo phezu komthombo;', 'XHO22'),
(6, 4, 7, 'Kufika intokazi yelamaSamariya, isiza kukha amanzi, athi uYesu kuyo, Ndiphe ndisele.', 'XHO22'),
(6, 4, 8, '(Kuba abafundi bakhe bebemke baya kuthenga ukudla esixekweni.)', 'XHO22'),
(6, 4, 9, 'Ithi ngoko intokazi engumSamariyakazi kuye, Uthini na wena, ungumYuda nje, ukucela kum into eselwayo, ndingumSamariyakazi nje? kuba amaYuda akanazinkqubano namaSamariya.', 'XHO22'),
(6, 4, 10, 'Waphendula uYesu wathi kuyo, Ukuba ubusazi isipho sikaThixo, nokuba ngubani na lo uthi kuwe, Ndiphe ndisele; unge ucele kuye wena, wakupha amanzi aphilileyo.', 'XHO22'),
(6, 4, 11, 'Ithi intokazi kuye, Nkosi, akunanto yakukha, nequla linzulu; uwazuza phi na ke amanzi aphilileyo lawo?', 'XHO22'),
(6, 4, 12, 'Wena umkhulu yini na kunobawo wethu uYakobi, owasinikayo iqula eli, wayesela kulo yena, nabantwana bakhe, nemfuyo yakhe?', 'XHO22'),
(6, 4, 13, 'Waphendula uYesu wathi kuyo, Bonke abasela kula manzi, baya kubuya banxanwe;', 'XHO22'),
(6, 4, 14, 'ke yena othe wasela kuwo amanzi, endiya kumpha wona mna, akasayi kuza anxanwe naphakade; kodwa amanzi, endiya kumpha wona, aya kuba kuye iqula lamanzi ampompozela ebomini obungunaphakade.', 'XHO22'),
(6, 4, 15, 'Ithi intokazi kuye, Nkosi, ndiphe loo manzi, ukuze ndinganxanwa, ndingezi nokuza kukha apha.', 'XHO22'),
(6, 4, 16, 'Athi uYesu kuyo, Hamba uye kubiza indoda yakho, uze apha.', 'XHO22'),
(6, 4, 17, 'Yaphendula intokazi yathi, Andinandoda. Wathi ke uYesu kuyo, Ulungisile ukuthi, Andinandoda;', 'XHO22'),
(6, 4, 18, 'kuba ubunamadoda amahlanu; onayo ngoku asindoda yakho, ùnyanisile ukutsho.', 'XHO22'),
(6, 4, 19, 'Ithi intokazi kuye, Nkosi, ndiyabona ukuba ungumprofeti wena.', 'XHO22'),
(6, 4, 20, 'Oobawo bethu babenqula kule ntaba; nina ke nithi iseYerusalem indawo apho abantu bamelwe kukunqula khona.', 'XHO22'),
(6, 4, 21, 'Athi uYesu kuyo, Ntokazindini, kholwa ndim, ukuba kuza ilixa, eningasayi kuthi nakule ntaba, naseYerusalem, nimnqule uBawo.', 'XHO22'),
(6, 4, 22, 'Nina ninqula eningakwaziyo; thina sinqula esikwaziyo, ngokuba usindiso luphuma kumaYuda.', 'XHO22'),
(6, 4, 23, 'Kodwa kuza ilixa, nangoku selikho, xa abanquli abayinyaniso baya kumnqula uBawo ngoMoya nangenyaniso; kuba uBawo ufuna abanjalo ukumnqula.', 'XHO22'),
(6, 4, 24, 'uThixo unguMoya; abo ke bamnqulayo bamelwe kukumnqula ngoMoya nangenyaniso.', 'XHO22'),
(6, 4, 25, 'Ithi intokazi kuye, Ndiyazi ukuba uMesiya uyeza, ekuthiwa nguKristu; xa athe weza yena, uya kusityela izinto zonke.', 'XHO22'),
(6, 4, 26, 'Athi uYesu kuye, Mna ndithethayo nawe ndinguye.', 'XHO22'),
(6, 4, 27, 'Kwathi kwangoko bafika abafundi bakhe, bamangaliswa kukuba athethe nomntu oyinkazana; noko ke akubangakho namnye owathi. Ufuna ntoni na? okanye, Yini na ukuba uthethe nayo?', 'XHO22'),
(6, 4, 28, 'Yawushiya ngoko umphanda wayo intokazi, yemka yaya ekhaya;', 'XHO22'),
(6, 4, 29, 'Yizani, nibone umntu ondixelele izinto zonke endakha ndazenza; lo akangebi nguye uKristu yini na?', 'XHO22'),
(6, 4, 30, 'Baphuma ke ekhaya, baye besiza kuye.', 'XHO22'),
(6, 4, 31, 'Ke kaloku ngelo xesha abafundi babemthandaza, besithi, Rabhi, yidla.', 'XHO22'),
(6, 4, 32, 'Uthe ke yena kubo, Mna ndinokudla endikudlayo, eningakwaziyo nina.', 'XHO22'),
(6, 4, 33, 'Babethetha bodwa ngoko abafundi, besithi, Ingaba kukho omziseleyo na ukudla?', 'XHO22'),
(6, 4, 34, 'Athi uYesu kubo, Okwam ukudla kukuba ndenze ukuthanda kowandithumayo, ndiwufeze umsebenzi wakhe.', 'XHO22'),
(6, 4, 35, 'Anitsho na ukuthi, Kusele iinyanga ezine, kuvunwe? Yabonani, ndithi kuni, Waphakamiseni amehlo enu, niwakhangele amasimi; ngokuba aselemhlophe ukuba kuvunwe.', 'XHO22'),
(6, 4, 36, 'Novunayo wamkela umvuzo, abuthele iziqhamo ebomini obungunaphakade, ukuze nalowo uhlwayelayo avuye, kunye nalowo uvunayo.', 'XHO22'),
(6, 4, 37, 'Kule nto liyinyaniso iqhalo elithi, Omnye uyahlwayela, nomnye avune.', 'XHO22'),
(6, 4, 38, 'Mna ndanithuma ukuvuna oko ningabulalekanga kuko nina; abanye babulalekile, naza nangena nina ekubulalekeni kwabo.', 'XHO22'),
(6, 4, 39, 'Ke kaloku, kwakholwa kuye into eninzi yamaSamariya kuloo mzi, ngenxa yelizwi lentokazi leyo, yangqinayo yathi, Undityele izinto zonke endakha ndazenza.', 'XHO22'),
(6, 4, 40, 'Akufika ngoko kuye amaSamariya, amcela ukuba ahlale nawo; wahlala khona ke imihla emibini.', 'XHO22'),
(6, 4, 41, 'Into eninzi yakholwa ngakumbi ngenxa yelizwi lakhe.', 'XHO22'),
(6, 4, 42, 'Wayesithi kwintokazi leyo, Asisakholwa ngenxa yokuthetha kwakho, kuba sizivele thina ngokwethu, sazi ukuba, inyaniso, lo unguye uMsindisi wehlabathi, uKristu.', 'XHO22'),
(6, 4, 43, 'Ke kaloku, emveni kweentsuku ezimbini, waphuma khona, wemka waya kwelaseGalili.', 'XHO22'),
(6, 4, 44, 'Kuba uYesu ngokwakhe wangqina, ukuba umprofeti akanambeko kowabo.', 'XHO22'),
(6, 4, 45, 'Xenikweni ngoko wafikayo kwelaseGalili, bamamkela abaseGalili ababezibonile zonke izinto awazenzayo eYerusalem emthendelekweni; kuba babeyile nabo emthendelekweni.', 'XHO22'),
(6, 4, 46, 'Wabuya ngoko weza uYesu eKana yelaseGalili, apho amanzi ebewenze iwayini. Kwaye kukho igosa lakomkhulu elithile, elinyana wayesifa eKapernahum.', 'XHO22'),
(6, 4, 47, 'Lithe lakuva lona ukuba uYesu uphumile kwaYuda, wafika eGalili, laya kuye, lamcela ukuba ehle amphilise unyana walo, kuba ebeza kubhubha.', 'XHO22'),
(6, 4, 48, 'Wathi ngoko uYesu kulo, Ukuba anithanga nibone imiqondiso nemimangaliso, anisayi kukha nikholwe.', 'XHO22'),
(6, 4, 49, 'Lithi kuye igosa lakomkhulu, Nkosi, yihla engekabhubhi umntwana wam.', 'XHO22'),
(6, 4, 50, 'Athi uYesu kuyo, Hamba uhambe; unyana wakho uhleli. Wakholwa umntu lowo lilizwi awalithethayo uYesu kuye, wahamba ke.', 'XHO22'),
(6, 4, 51, 'Ke kaloku, akubon’ ukuba usehla, izicaka zakhe zamhlangabeza, zambikela zisithi, Unyana wakho uhleli.', 'XHO22'),
(6, 4, 52, 'Wabuza ngoko kuzo ilixa aqale ukuchacha ngalo; zathi kuye, Izolo, ngelixa lesixhenxe, yamyeka icesina.', 'XHO22'),
(6, 4, 53, 'Wazi ngoko uyise, ukuba bekukwangelo lixa abethe ngalo uYesu kuye, Unyana wakho uhleli; wakholwa, yena nendlu yakhe iphela.', 'XHO22'),
(6, 4, 54, 'Lo mqondiso waphinda wawenza uYesu, ekuphumeni kwakhe kwelakwaYuda, wafika kwelaseGalili.', 'XHO22'),
(6, 5, 1, 'Emva koko ke kwaye kukho umthendeleko wamaYuda, wenyuka uYesu waya eYerusalem.', 'XHO22'),
(6, 5, 2, 'Ke kaloku kukho eYerusalem apho, ngakwelezimvu isango, ichibi, ekuthiwa ukubizwa yiBhetesda ngesiHebhere, lineevaranda ezintlanu.', 'XHO22'),
(6, 5, 3, 'Kwakulele kuzo inkitha eninzi yemilwelwe: iimfama, iziqhwala, abome umzimba, belinde ukuzanyazanyiswa kwamanzi.', 'XHO22'),
(6, 5, 4, 'Kuba isithunywa sezulu ngaxesha lithile besisihla, singene echibini, siwagxobhagxobhe amanzi; lowo ngoko ubethe angene tanci emva kokugxojwagxojwagxojwa kwamanzi, ubephila nakusiphi na isifo abebanjwe siso.', 'XHO22'),
(6, 5, 5, 'Kwakukho ndoda ithile apho, eyayingumlwelwe iminyaka emashumi mathathu anesibhozo.', 'XHO22'),
(6, 5, 6, 'uYesu embona lowo elele, esazi ukuba uselenexesha elikhulu enjalo, uthi kuye, Uyafuna na ukuba uphile?', 'XHO22'),
(6, 5, 7, 'Waphendula umlwelwe wathi, Nkosi, andinamntu wokuthi, xa athe agxojwagxojwa amanzi, andiphose echibini. Kuthi ke, ndakubon’ ukuba mna ndiyaya, ndiphangelwe ngomnye ukuhla.', 'XHO22'),
(6, 5, 8, 'Athi uYesu kuye, Vuka, uthabathe ukhuko lwakho, uhambe.', 'XHO22'),
(6, 5, 9, 'Usuke kwaoko waphila umntu, waluthabatha ukhuko lwakhe, wahamba. Ke kaloku kwakuyisabatha loo mini.', 'XHO22'),
(6, 5, 10, 'Ayesithi ke amaYuda kophilisiweyo, Yisabatha; akuvumelekile ukuba uthwale ukhuko lwakho.', 'XHO22'),
(6, 5, 11, 'Wawaphendula wathi, Owandiphilisayo, nguye owathi kum, Thabatha ukhuko lwakho, uhambe.', 'XHO22'),
(6, 5, 12, 'Ambuza ngoko athi, Ngubani na yena loo mntu uthe kuwe, Thabatha ukhuko lwakho, uhambe?', 'XHO22'),
(6, 5, 13, 'Ke kaloku, owaphiliswayo ebengazi ukuba ngubani na; kuba uYesu wathi shwaka, kwakubon’ ukuba kukho izihlwele kuloo ndawo.', 'XHO22'),
(6, 5, 14, 'Kwathi emveni kwezi zinto, uYesu umfumana etempileni, athi kuye, Uyabona, uphilile;', 'XHO22'),
(6, 5, 15, 'Wemka umntu lowo, waxela kumaYuda, ukuba nguYesu omenze waphila.', 'XHO22'),
(6, 5, 16, 'Ngenxa yoko ayemtshutshisa uYesu amaYuda, afuna ukumbulala; ngokuba wayesenza ezo zinto ngesabatha.', 'XHO22'),
(6, 5, 17, 'Wawaphendula ke uYesu wathi, uBawo uyasebenza kude kube ngoku, nam ndiyasebenza.', 'XHO22'),
(6, 5, 18, 'Ngenxa yoko ke ayefuna ngakumbi amaYuda ukumbulala; ngokuba ubengaphule sabatha yodwa, wayetshilo nokuthi, uThixo ngowakhe uYise, ezenza alingane noThixo.', 'XHO22'),
(6, 5, 19, 'Waphendula ngoko uYesu, wathi kuwo, Inene, inene, ndithi kuni, uNyana akanakwenza nto ngokwakhe, engathanga abone uYise eyenza; kuba izinto asukuba ezenza yena, uyazenza uNyana kwangokunjalo;', 'XHO22'),
(6, 5, 20, 'Kuba uYise uyamthanda uNyana, embonisa zonke izinto azenzayo yena; uya kumbonisa nemisebenzi emikhulu kunale, ukuze nina nimangaliswe.', 'XHO22'),
(6, 5, 21, 'Kuba, njengoko uYise abavusayo abafileyo, abadlise ubomi; kwangokunjalo noNyana ubadlisa ubomi abo athandayo.', 'XHO22'),
(6, 5, 22, 'Kuba uYise akagwebi namnye; uthe umgwebo wonke wawunikela kuNyana;', 'XHO22'),
(6, 5, 23, 'ukuze bonke bambeke uNyana, kwanjengoko bambekayo uYise. Lowo ungambekiyo uNyana, akambeki uYise owamthumayo.', 'XHO22'),
(6, 5, 24, 'Inene, inene, ndithi kuni, Lowo ulivayo ilizwi lam, akholwe ngulowo wandithumayo, unobomi obungunaphakade; akezi ekugwetyweni; uphume ekufeni wangena ebomini.', 'XHO22'),
(6, 5, 25, 'Inene, inene, ndithi kuni, Kuza ilixa, nangoku selikho, xa abafileyo baya kuliva ilizwi loNyana kaThixo, bathi abalivileyo badle ubomi.', 'XHO22'),
(6, 5, 26, 'Kuba njengokuba uYise enabo ubomi kuye ngokwakhe; ngokunjalo wamnika noNyana ukuba abe nobomi kuye ngokwakhe.', 'XHO22'),
(6, 5, 27, 'Wamnika negunya lokugweba, ngokuba enguNyana woMntu.', 'XHO22'),
(6, 5, 28, 'Musani ukumangaliswa kuko oko; ngokuba kuza ilixa abaya kuthi ngalo bonke abasemangcwabeni balive ilizwi lakhe;', 'XHO22'),
(6, 5, 29, 'Kwaye baya kuphuma; beze eluvukweni lobomi; baye eluvukweni lokugwetywa abo benza izinto ezimbi.', 'XHO22'),
(6, 5, 30, 'Mna ngokwam andinakwenza nto; njengoko ndivayo, ndigweba ngako, nomgwebo wam ububulungisa; ngokuba andifuni kuthanda kwam, ndifuna ukuthanda kukaBawo owandithumayo.', 'XHO22'),
(6, 5, 31, 'Ukuba mna ndisuka ndizingqinele, ubungqina bam abungebi yiyo inene.', 'XHO22'),
(6, 5, 32, 'Kukho omnye ongqinayo ngam; ndiyazi ke, ukuba buyinene ubungqina angqinayo ngam.', 'XHO22'),
(6, 5, 33, 'Nina nithumele kuYohane; uyingqinele ke inyaniso.', 'XHO22'),
(6, 5, 34, 'Mna ke andamkeli bungqina mntwini; ndizithetha ezi zinto, ukuze nina nisindiswe.', 'XHO22'),
(6, 5, 35, 'Yena lowo waye esisibane esidangazelayo, nesikhanyiso, na ke nina nathanda ukuvuya umzuzwana ekukhanyeni kwakhe.', 'XHO22'),
(6, 5, 36, 'Kodwa ndinabo ubungqina obungaphezu kobukaYohane; kuba imisebenzi awandinikayo uBawo ukuba ndiyifeze, yona le misebenzi ndiyenzayo mna, iyangqina ngam, ukuba uBawo undithumile.', 'XHO22'),
(6, 5, 37, 'NoBawo ngokwakhe, yena owandithumayo, ungqinile ngam. Anizanga nilive izwi lakhe nakanye, niyibone nembonakalo yakhe.', 'XHO22'),
(6, 5, 38, 'Ilizwi lakhe alihlali kuni, kuba anikholwa kulowo amthumileyo.', 'XHO22'),
(6, 5, 39, 'Ziphengululeni izibhalo; ngokuba nina niba ninobomi obungunaphakade kuzo; zaye zona zingqina ngam.', 'XHO22'),
(6, 5, 40, 'Nibe ke nina ningathandi ukuza kum, ukuze nibe nobomi.', 'XHO22'),
(6, 5, 41, 'Andamkeli luzuko bantwini.', 'XHO22'),
(6, 5, 42, 'Ndiyanazi nina, ukuba uthando lukaThixo aninalo ngaphakathi kwenu.', 'XHO22'),
(6, 5, 43, 'Mna ndize egameni likaBawo, anindamkeli; ukuba omnye uthe weza ngelilelakhe igama, nomamkela yena.', 'XHO22'),
(6, 5, 44, 'Ningathini na ukuba nako ukukholwa, nisamkelela uzuko omnye komnye nje nina, nibe uzuko lona oluvela kuThixo yedwa ningalufuni?', 'XHO22'),
(6, 5, 45, 'Ningabi ndiya kunimangalela kuBawo mna; ukho onimangalelayo, uMoses, lowo nimthembileyo nina.', 'XHO22'),
(6, 5, 46, 'Ukuba benikholiwe nguMoses, ninge nikholiwe ndim, kuba yena wabhala ngam.', 'XHO22'),
(6, 5, 47, 'Ke ukuba anikholwa zizibhalo zakhe, nothini na ukukholwa ngamazwi am?', 'XHO22'),
(6, 6, 1, 'Emveni kwezi zinto, wemka uYesu, waya phesheya kolwandle lwaseGalili, lwaseTibheriya.', 'XHO22'),
(6, 6, 2, 'Wayelandelwa yindimbane enkulu, kuba yayiyibona imiqondiso yakhe abeyenza kwabafayo.', 'XHO22'),
(6, 6, 3, 'uYesu wenyuka intaba, waza wahlala phantsi nabafundi bakhe.', 'XHO22'),
(6, 6, 4, 'Yaye ke ikufuphi ipasika, umthendeleko wamaYuda.', 'XHO22'),
(6, 6, 5, 'Ewaphakamisile ngoko uYesu amehlo akhe, ebone isihlwele esikhulu sisiza kuye, wathi kuFilipu, Sozithenga phi na izonka, ukuze badle aba?', 'XHO22'),
(6, 6, 6, 'Ubesitsho ke emlinga; kuba ubeyazi yena into abeza kuyenza.', 'XHO22'),
(6, 6, 7, 'uFilipu wamphendula wathi, Izonka zeedenariyo ezimakhulu mabini azibanele, ukuze elowo athabathe intwana.', 'XHO22'),
(6, 6, 8, 'Athi kuye omnye kubafundi bakhe, uAndreya, umzalwana kaSimon Petros,', 'XHO22'),
(6, 6, 9, 'Kukho nkwenkwana apha, inezonka ezihlanu zerhasi neentlanzi ezimbini; kodwa ke ziyintoni na ezo kwabangakanana?', 'XHO22'),
(6, 6, 10, 'Uthe ke uYesu, Bahlaliseni phantsi abantu. Kwaye ke kukho utyani obuninzi kuloo ndawo. Ahlala phantsi ke amadoda, engathi amawaka mahlanu inani.', 'XHO22'),
(6, 6, 11, 'Wazithabatha ke izonka uYesu; wathi akubulela wababela abafundi, bathi ke abafundi babela abo babehleli phantsi, kwaba kwanjalo nakwiintlanzi, ngangokuthanda kwabo.', 'XHO22'),
(6, 6, 12, 'Bakuhlutha ke, wathi kubafundi bakhe, Buthani amaqhekeza aseleyo, ukuze kungonakali.', 'XHO22'),
(6, 6, 13, 'Babutha ngoko, bazalisa iingobozi zalishumi elinazibini ngamaqhekeza abesele kubo ababedlile, ezonka ezihlanu zerhasi.', 'XHO22'),
(6, 6, 14, 'Ngoko abantu abo, bakuwubona umqondiso awawenzayo uYesu, bathi, Lo nguye inyaniso umprofeti lowo uzayo ehlabathini.', 'XHO22'),
(6, 6, 15, 'Uthe ngoko uYesu, esazi ukuba bahlalele ukuza kumbamba ngamandla, bamenze ukumkani, wabuya wemka, waya entabeni Yena yedwa.', 'XHO22'),
(6, 6, 16, 'Ke kaloku, kwakuhlwa, behla abafundi bakhe, baya elwandle.', 'XHO22'),
(6, 6, 17, 'Bengenile emkhombeni, baluwela ulwandle, besiya eKapernahum, kwakusekumnyama, waye uYesu engafikanga kubo.', 'XHO22'),
(6, 6, 18, 'Lwaye nolwandle lusilwa, kananjalo kuvuthuza umoya omkhulu.', 'XHO22'),
(6, 6, 19, 'Bakuba ngoko begweqile izitadiya ezingathi zimashumi mabini anantlanu, nokuba zimashumi mathathu, bambona uYesu ehamba phezu kolwandle, esondela ngasemkhombeni;', 'XHO22'),
(6, 6, 20, 'Athi ke yena kubo, Ndim; musani ukoyika.', 'XHO22'),
(6, 6, 21, 'Bathanda ke bona ukumamkela emkhombeni;', 'XHO22'),
(6, 6, 22, 'Ngengomso indimbane leyo, ibimi phesheya kolwandle, yabona ukuba akukho mkhombe wumbi khona apho, ingenguwo lowo bangene kuwo abafundi bakhe, nokuba uYesu akangenanga nabafundi bakhe emkhombeni, wemka nabafundi bakhe, bemka bodwa;', 'XHO22'),
(6, 6, 23, '(Kodwa kweza eminye imikhombe evela eTibheriya, kufuphi nendawo apho babedle isonka, emva kokuba iNkosi yayibulele)', 'XHO22'),
(6, 6, 24, 'Xa ngoko indimbane yabonayo ukuba uYesu akakho apho nabafundi bakhe, besuka bangena nabo emikhombeni, beza eKapernahum, bemfuna uYesu.', 'XHO22'),
(6, 6, 25, 'Bathe bakumfumana phesheya kolwandle, bathi kuye, Rabhi, ufike nini na apha?', 'XHO22'),
(6, 6, 26, 'Wabaphendula uYesu wathi, Inene, inene, ndithi kuni, Anindifuni ngakuba nabona imiqondiso, nindifuna ngokuba nadla kwezaa zonka, nahlutha.', 'XHO22'),
(6, 6, 27, 'Musani ukusebenzela ukudla okutshabalalayo; sebenzelani ukudla okuhlala kuse ebomini obungunaphakade, aya kunipha kona uNyana woMntu; kuba uYise uThixo utywine waqinisela yena lowo.', 'XHO22'),
(6, 6, 28, 'Bathe ngoko kuye, Masenze ntoni na, ukuze sisebenze imisebenzi kaThixo?', 'XHO22'),
(6, 6, 29, 'Waphendula uYesu, wathi kubo, Umsebenzi kaThixo nguwo lo, ukuba nikholwe kuye othunyiweyo nguye.', 'XHO22'),
(6, 6, 30, 'Bathi ngoko kuye, Wenza mqondiso mni na ke ngoko, ukuze sibone, sikholwe nguwe? Usebenza ntoni na?', 'XHO22'),
(6, 6, 31, 'Oobawo bâtya imana entlango; njengokuba kubhaliwe kwathiwa, Wabanika isonka siphuma ezulwini, ukuba badle sona.', 'XHO22'),
(6, 6, 32, 'Uthe ngoko uYesu kubo, Inene, inene, ndithi kuni, AsinguMoses oninike isonka esiphuma ezulwini; nguBawo oninika isonka esiphuma ezulwini, esiyinyaniso.', 'XHO22'),
(6, 6, 33, 'Kuba isonka sikaThixo ngulowo uhla ephuma ezulwini, alinike ubomi ihlabathi.', 'XHO22'),
(6, 6, 34, 'Baza bathi kuye: “Nkosi, khawusinike eso sonka ngamaxa onke.', 'XHO22'),
(6, 6, 35, 'Wathi ke uYesu kubo, Isonka sobomi esi ndim; lowo uzayo kum, akasayi kulamba; lowo ukholwayo kum, akasayi kunxanwa naphakade.', 'XHO22'),
(6, 6, 36, 'Ndithe ke kuni, Nindibonile nokundibona, anakholwa noko.', 'XHO22'),
(6, 6, 37, 'Konke andinikayo uBawo, kuya kuza kum; nozayo kum, andisayi kukha ndimlahlele phandle;', 'XHO22'),
(6, 6, 38, 'ngokuba ndihle emazulwini, ndingaze kwenza ukuthanda kwam; ndize kwenza ukuthanda kwalowo wandithumayo.', 'XHO22'),
(6, 6, 39, 'Kuko ke oku ukuthanda kowandithumayo, kukaBawo: ukuba konke awandinikayo, ndingalahli nento kuko, ndikuvuse ngomhla wokugqibela.', 'XHO22'),
(6, 6, 40, 'Kuko ke oku ukuthanda kowandithumayo, ukuthi babe nobomi obungunaphakade bonke abambonayo uNyana, bakholwe kuye; ndaye mna ndiya kumvusa ngomhla wokugqibela.', 'XHO22'),
(6, 6, 41, 'Ayemkrokrela ngoko amaYuda, ngokuba wathi, Ndim isonka, esi sehlayo emazulwini.', 'XHO22'),
(6, 6, 42, 'Ayesithi ke, Lo asinguye na kanene uYesu, unyana kaYosefu, esimaziyo thina uyise nonina? Angáthini na ke ngoko ukuthi, Ndihle emazulwini?', 'XHO22'),
(6, 6, 43, 'Waphendula ngoko uYesu wathi kubo, Musani ukukrokra phakathi kwenu.', 'XHO22'),
(6, 6, 44, 'Akukho namnye unako ukuza kum, engathanga uBawo owandithumayo amtsale; ndaye mna ndiya kumvusa ngomhla wokugqibela.', 'XHO22'),
(6, 6, 45, 'Kubhaliwe ebaprofetini kwathiwa, Baye bonke beya kuba ngabafundiswa bakaThixo; Bonke ngoko abathe beva kuBawo, bafunda kuye, bayeza kum.', 'XHO22'),
(6, 6, 46, 'Kungekuba kukho bani umbonileyo uBawo, ingenguye ovela kuThixo; nguye yedwa ombonileyo uBawo.', 'XHO22'),
(6, 6, 47, 'Inene, inene, ndithi kuni, Lowo ukholwayo kum unobomi obungunaphakade.', 'XHO22'),
(6, 6, 48, 'Ndim isonka sobomi.', 'XHO22'),
(6, 6, 49, 'Ooyihlo bayidla imana leyo entlango, bafile noko.', 'XHO22'),
(6, 6, 50, 'Esi sisonka esihla sivela ezulwini, ukuba umntu akudle, angafi.', 'XHO22'),
(6, 6, 51, 'Ndim isonka esiphilileyo, esi sehlayo emazulwini. Ukuba ubani uthe wasidla esi sonka, wodla ubomi ngonaphakade; ke isonka endiya kusinika mna siyinyama yam, endiya kuyinikela ubomi behlabathi.', 'XHO22'),
(6, 6, 52, 'Abambana ngoko amaYuda phakathi kwawo, esithi, Angathini na lo ukusinika inyama yakhe siyidle?', 'XHO22'),
(6, 6, 53, 'Wathi ngoko uYesu kuwo, Inene, inene, ndithi kuni, Ukuba anithanga niyidle inyama yoNyana woMntu, nilisele igazi lakhe, aninabo ubomi kuni.', 'XHO22'),
(6, 6, 54, 'Lowo uyidlayo eyam inyama, alisele elam igazi, unobomi obungunaphakade; ndaye mna ndiya kumvusa ngomhla wokugqibela.', 'XHO22'),
(6, 6, 55, 'Kuba inyama yam ikukudla, inyaniso; negazi lam liluselo, inyaniso.', 'XHO22'),
(6, 6, 56, 'Lowo uyidlayo eyam inyama, alisele elam igazi, uhleli kum, nam ndihleli kuye.', 'XHO22'),
(6, 6, 57, 'Njengokuba uBawo ophilileyo wandithumayo, ndibe nam ndihleli ngaye uBawo, lowo undidlayo, uya kuphila ngam.', 'XHO22'),
(6, 6, 58, 'Siso eso isonka esehla emazulwini; kungenjengokuba ooyihlo bayidlayo imana, bafa noko. Lowo usidlayo esi sonka, uya kuphila ngonaphakade.', 'XHO22'),
(6, 6, 59, 'Ezi zinto wazithetha endlwini yesikhungu, efundisa eKapernahum.', 'XHO22'),
(6, 6, 60, 'Into eninzi ngoko kubafundi bakhe yakuva yathi, Lilizwi elinqabileyo eli; ngubani na onokuyiva?', 'XHO22'),
(6, 6, 61, 'Esazi uYesu ngaphakathi kwakhe, ukuba bayakrokra lilo abafundi bakhe, wathi kubo, Kuyanikhubekisa na oku?', 'XHO22'),
(6, 6, 62, 'Hi na ke, ukuba nithe nambona uNyana woMntu enyukela apho ebefudula ekhona?', 'XHO22'),
(6, 6, 63, 'Ngumoya odlisa ubomi; inyama yona ayincedi lutho. Amazwi la ndiwathethayo kuni mna angumoya, abubomi.', 'XHO22'),
(6, 6, 64, 'Kodwa kukho abathile kuni abangakholwayo. Kuba ebesazi kwasekuqalekeni uYesu, ukuba ngoobani na ababengakholwa, nokuba ngubani na oya kumngcatsha.', 'XHO22'),
(6, 6, 65, 'Wayesithi, Kungenxa yoku ndatshoyo kuni ukuthi, Akukho namnye unako ukuza kum, ukuba akathanga akunikwe nguBawo.', 'XHO22'),
(6, 6, 66, 'Ukususela ngelo xesha into eninzi yabafundi bakhe yabuya umva, ayaba sahamba naye.', 'XHO22'),
(6, 6, 67, 'Wathi ngoko uYesu kwabalishumi elinababini, Nani nifuna ukumka, yini na?', 'XHO22'),
(6, 6, 68, 'Wamphendula ngoko uSimon Petros wathi, Nkosi, sosuka siye kubani na? Nguwe onamazwi obomi obungunaphakade.', 'XHO22'),
(6, 6, 69, 'Nathi sikholiwe, sazile, ukuba wena unguye uKristu, uNyana kaThixo ophilileyo.', 'XHO22'),
(6, 6, 70, 'Wabaphendula uYesu wathi, Andininyulanga na mna, nina balishumi elinababini? Ukanti omnye kuni ungumtyholi?', 'XHO22'),
(6, 6, 71, 'Wayethetha ngoYuda Sikariyoti kaSimon, kuba lowo ibinguye obeza kumngcatsha, engomnye weshumi elinababini.', 'XHO22'),
(6, 7, 1, 'Emveni kwezi zinto, wayehamba uYesu kwelaseGalili; kuba ebengathandi ukuhamba kwelakwaYuda; ngokuba amaYuda ebefuna ukumbulala.', 'XHO22'),
(6, 7, 2, 'Ke kaloku, wawukufuphi umthendeleko wamaYuda weminquba.', 'XHO22'),
(6, 7, 3, 'Bathi ngoko kuye abazalwana bakhe, Gqitha apha, uye kwelakwaYuda, ukuze nabafundi bakho bayibone imisebenzi yakho oyenzayo.', 'XHO22'),
(6, 7, 4, 'Kuba akukho namnye wenza into emfihlekweni, abe yena ngokwakhe efuna ukuba sekuhleni. Ukuba uyazenza ezi zinto, zibonakalise ngokwakho kwihlabathi.', 'XHO22'),
(6, 7, 5, 'Kuba nabazalwana bakhe bebengakholwa kuye.', 'XHO22'),
(6, 7, 6, 'Wathi ngoko uYesu kubo, Ixesha lam alikabikho, ke elenu ixesha lihlala lilungile.', 'XHO22'),
(6, 7, 7, 'Ihlabathi alinakunithiya nina; mna ke liyandithiya, ngokuba mna ndingqina ngalo, ukuba imisebenzi yalo ayindawo.', 'XHO22'),
(6, 7, 8, 'Nyukani nina, niye kulo mthendeleko; mna andikanyuki, ndiye kulo mthendeleko; ngokuba ixesha lam alikazaliseki.', 'XHO22'),
(6, 7, 9, 'Akuba ke ezithethile ezi zinto kubo, wahlala kwelaseGalili.', 'XHO22'),
(6, 7, 10, 'Ke, bakuba benyukile abazalwana bakhe, wenyuka naye, waya emthendelekweni, kungekhona ekuhleni, kubusithela.', 'XHO22'),
(6, 7, 11, 'Ayemfuna ngoko amaYuda emthendelekweni, esithi, Uphi na yena?', 'XHO22'),
(6, 7, 12, 'Kwaye kukho ukukrokra okukhulu ngaye ebantwini; inxenye ibisithi, Ulungile; abanye ke besithi, Hayi; ke yena ulahlekisa abantu.', 'XHO22'),
(6, 7, 13, 'Noko ke bekungekho namnye ubethetha ngaye ngokusekuhleni, ngenxa yokoyika amaYuda.', 'XHO22'),
(6, 7, 14, 'Ke kaloku, kwakubon’ ukuba sekuphakathi komthendeleko, wenyuka uYesu waya etempileni, wafundisa.', 'XHO22'),
(6, 7, 15, 'Amangaliswa amaYuda, esithi, Lo mfo ube nolwazi ngani na, engafundanga nje?', 'XHO22'),
(6, 7, 16, 'Wawaphendula uYesu, wathi, Eyam imfundiso asiyam, yeyalowo wandithumayo.', 'XHO22'),
(6, 7, 17, 'Ukuba kukho othi afune ukukwenza ukuthanda kwakhe, woyazi imfundiso le: ukuba iphuma kuThixo, nokuba mna ndithetha okukokwam, kusini na.', 'XHO22'),
(6, 7, 18, 'Lowo uthetha okwakhe, ufuna olulolwakhe uzuko; ke lowo ufuna uzuko lowamthumayo, uyinene yena, akukho kungalungisi kuye.', 'XHO22'),
(6, 7, 19, 'Umthetho aniwunikelwanga na nguMoses? Kanti ke akukho namnye kuni uwugcinayo umthetho; Yini na ukuba nifune ukundibulala?', 'XHO22'),
(6, 7, 20, 'Saphendula isihlwele sathi, Unedemon; ngubani na ofuna ukukubulala?', 'XHO22'),
(6, 7, 21, 'Waphendula uYesu wathi kubo, Ndenze umsebenzi wamnye, namangaliswa ke nonke.', 'XHO22'),
(6, 7, 22, 'uMoses uninike ulwaluko ngenxa yoku; Kungekuba iphume kuMoses, kungenxa yokuba iphuma kooyihlo. Niyalùsa ke nina nangesabatha.', 'XHO22'),
(6, 7, 23, 'Ukuba umntu uyaluswa ngoMhla woPhumlo, ukuze ungaphulwa umthetho kaMosis. Nindijalele na ngokuba ndiphilise umntu, waphila kwaphela, ngesabatha?', 'XHO22'),
(6, 7, 24, 'Musani ukugweba ngokwembonakalo; gwebani ngomgwebo olungisayo.', 'XHO22'),
(6, 7, 25, 'Babesithi ngoko abathile kwabaseYerusalem, Asinguye na lo bafuna ukumbulala?', 'XHO22'),
(6, 7, 26, 'Niyabona, uthetha ngokuphandle, abathethi nto kuye; bangaba kanti na ke bayazi okwenyaniso abaphathi, ukuba lo nguye uKristu okwenyaniso?', 'XHO22'),
(6, 7, 27, 'Thina siyamazi lo apho avela khona; xa sukuba ke uKristu esiza, akukho namnye uya kumazi apho avela khona.', 'XHO22'),
(6, 7, 28, 'Wadanduluka ngoko uYesu, efundisa etempileni, wathi, Nina niyandazi, nikwazi nalapho ndivela khona; andizanga ngokwam; uyinyaniso owandithumayo, eningamaziyo nina.', 'XHO22'),
(6, 7, 29, 'Ke mna ndiyamazi; ngokuba ndivela kuye, waye yena endithumile.', 'XHO22'),
(6, 7, 30, 'Babefuna ngoko ukumbamba, kodwa akubangakho namnye umsa isandla, ngokuba ilixa lakhe belingekafiki.', 'XHO22'),
(6, 7, 31, 'Ke kaloku, into eninzi esihlweleni yakholwa kuye; yayisithi, uKristu, xa athe wafika, angaba uya kwenza imiqondiso emininzi na kunale ayenzileyo lo?', 'XHO22'),
(6, 7, 32, 'AbaFarisi beva abantu bezikrokrela ezi zinto ngaye; Baza abaFarisi nababingeleli abakhulu bathuma amadindala, ukuba ambambe.', 'XHO22'),
(6, 7, 33, 'Wathi ngoko uYesu kubo, Liselixesha elifutshane endinani, ndize ndiye kowandithumayo.', 'XHO22'),
(6, 7, 34, 'Niya kundifuna, ningandifumani; nithi apho ndikhona mna, ningabi nakufika nina.', 'XHO22'),
(6, 7, 35, 'Athi ngoko amaYuda phakathi kwawo, Uza kuya phi na lo, le nto singayi kumfumana thina? Angaba uza kuya kwiintsali zakwaentlanga, afundise iintlanga?', 'XHO22'),
(6, 7, 36, 'Liyintoni na eli lizwi alithethileyo, lithi, Niya kundifuna, ningandifumani; nithi apho ndikhona mna, ningabi nakufika nina?', 'XHO22'),
(6, 7, 37, 'Ke kaloku, ngomhla wokugqibela, ongowona mkhulu womthendeleko, wema uYesu, wadanduluka esithi, Ukuba kukho othi anxanwe, makeze kum asele.', 'XHO22'),
(6, 7, 38, 'Lowo ukholwayo kum, njengoko satshoyo isibhalo, kuya kumpompoza imilambo yamanzi aphilileyo, iphuma embilinini wakhe.', 'XHO22'),
(6, 7, 39, 'Oku wakutsho ke ngaye uMoya, ababeza kumamkeliswa abo bakholwayo kuye; kuba ebengekamkelwa uMoya oyiNgcwele; ngokuba uYesu ebengekazukiswa.', 'XHO22'),
(6, 7, 40, 'Yaye ngoko into eninzi esihlweleni, yakuliva ilizwi elo, isithi, Lo nguye umprofeti, inyaniso.', 'XHO22'),
(6, 7, 41, 'Baye abanye besithi, Lo nguye uKristu. Babesithi ke abathile, uKristu angavela na kwelaseGalili na?', 'XHO22'),
(6, 7, 42, 'Asitsho na iziBhalo ukuthi uKrestu uyaphuma embewini kaDavide, ephuma eBhetelehem, apho wayekhona uDavide?', 'XHO22'),
(6, 7, 43, 'Ke ngoko sathi qheke kubini isihlwele ngenxa yakhe.', 'XHO22'),
(6, 7, 44, 'Inxenye ke kubo yayithanda ukumbamba; kodwa akubangakho namnye umsa izandla.', 'XHO22'),
(6, 7, 45, 'Afika ngoko amadindala kubabingeleli abakhulu nakubaFarisi; bathi kubo, Yini na ukuba ningezi naye?', 'XHO22'),
(6, 7, 46, 'Aphendula amadindala athi, Akuzanga kuthethe mntu njengoko athetha ngako lo mntu.', 'XHO22'),
(6, 7, 47, 'Bawaphendula ngoko abaFarisi, bathi, Ningaba nilahlekisiwe nani na?', 'XHO22'),
(6, 7, 48, 'Kubaphathi nakubaFarisi kungaba kukho mntu ukholiweyo kuye yini na?', 'XHO22'),
(6, 7, 49, 'Kodwa aba bantu bangawaziyo umthetho baqalekisiwe.', 'XHO22'),
(6, 7, 50, 'Athi kubo uNikodemo (lowo wezayo kuYesu ebusuku, engomnye wabo),', 'XHO22'),
(6, 7, 51, 'Umthetho wethu uyamgweba yini na umntu, ukuba awuthanga uve kuye kuqala, uqonde into ayenzayo?', 'XHO22'),
(6, 7, 52, 'Baphendula bathi kuye, Ungaba ungowaseGalili nawe na? Phengulula ubone, kuba akuveli mprofeti kwelaseGalili.', 'XHO22'),
(6, 7, 53, 'Walowo ke waya kweyakhe indlu.', 'XHO22'),
(6, 8, 1, 'uYesu ke waya eNtabeni yemiNquma.', 'XHO22'),
(6, 8, 2, 'Kwathi kwakusa, wabuya wangena etempileni; baye bonke abantu besiza kuye; wahlala phantsi, wabafundisa.', 'XHO22'),
(6, 8, 3, 'Ababhali nabaFarisi bazisa kuye umfazi obanjwe ekrexeza; bammisa ke esazulwini,', 'XHO22'),
(6, 8, 4, 'bathi kuye, Mfundisi, lo mfazi wabanjwa ekrexeza, kanye kuloo ntlondi.', 'XHO22'),
(6, 8, 5, 'Ke kaloku, emthethweni uMoses wasiyalela ukuba abanjalo bagityiselwe ngamatye;', 'XHO22'),
(6, 8, 6, 'Bakutsho ke oko, bemlinga, ukuze babe nokummangalela. Kodwa uYesu, ethobe phantsi, wabhala ngomnwe emhlabeni, ngokungathi akeva.', 'XHO22'),
(6, 8, 7, 'Uthe ke bakumana bembuza, waphakama, wathi kubo, Ongenasono kuni makaqale amgibisele ngelitye.', 'XHO22'),
(6, 8, 8, 'Wabuya wathoba, wabhala emhlabeni.', 'XHO22'),
(6, 8, 9, 'Bathe ke bona bakuva, besohlwaywa naluvalo lwabo, baphuma ngabanye, beqalela kwabakhulu, kwada kwaba kwabokugqibela. Wasala yedwa uYesu, waye umfazi emi phakathi.', 'XHO22'),
(6, 8, 10, 'Ephakamile ke uYesu, akabona mntu kwakuphela ngumfazi lowo; wathi kuye, Mfazindini, baphi na abo bamangaleli bakho? Akukho ukugwebileyo na?', 'XHO22'),
(6, 8, 11, 'Wathi ke yena, Noko amnye, Nkosi. Wathi ke uYesu kuyo, Nam andikugwebi; hamba, ungabi sona.', 'XHO22'),
(6, 8, 12, 'Wabuya ngoko uYesu wathetha kubo, esithi, Ndim ukhanyiselo lwehlabathi; lowo undilandelayo akasayi kuhamba ebumnyameni; yena uya kuba nalo ukhanyiso lobomi.', 'XHO22'),
(6, 8, 13, 'Bathe ngoko abaFarisi kuye, Wena uyazingqinela; Ingxelo yakho ayinyanisekanga.', 'XHO22'),
(6, 8, 14, 'Waphendula uYesu wathi kubo, Nokuba ndithi ndizingqinele mna ngokwam, buyinene ubungqina bam; ngokuba ndiyazi apho ndavela khona, nalapho ndiya khona; ke nina anazi apho ndivela khona, nalapho ndiya khona.', 'XHO22'),
(6, 8, 15, 'Nina nigweba ngokwenyama; mna andigwebi mntu.', 'XHO22'),
(6, 8, 16, 'Nokuba ndithi ndigwebe mna, umgwebo wam uyinene; ngokuba andindedwa, ndim noBawo owandithumayo.', 'XHO22'),
(6, 8, 17, 'Nasemthethweni wenu ke kubhaliwe kwathiwa, Ubungqina babantu ababini buyinene.', 'XHO22'),
(6, 8, 18, 'Mna ndingulowo uzingqinelayo; uyandingqinela nalowo wandithumayo, uBawo.', 'XHO22'),
(6, 8, 19, 'Bathi ngoko kuye, Uphi na uYihlo? Waphendula uYesu wathi, Anindazi mna; anindazi noBawo; ukuba benindazile mna, ninge nimazile noBawo.', 'XHO22'),
(6, 8, 20, 'Loo mazwi wawathetha uYesu ngasemkhumbini wokulondoloza imali, efundisa endlwini kaThixo. Akubangakho namnye umsa izandla; ngokuba belingekafiki ilixa lakhe.', 'XHO22'),
(6, 8, 21, 'Wabuya ngoko uYesu wathi kubo, Ndiyemka mna; niya kundifuna, nifele esonweni senu; apho ndiya khona mna, aninako ukufika nina.', 'XHO22'),
(6, 8, 22, 'Ayesithi ngoko amaYuda, Angaba uya kuzibulala na? ngokuba esithi, Apho ndiya khona mna, aninako ukufika nina.', 'XHO22'),
(6, 8, 23, 'Wathi kuwo, Nina ningabangaphantsi; mna ndingowaphezulu; nina ningabeli hlabathi; mna andingoweli hlabathi.', 'XHO22'),
(6, 8, 24, 'Ndatsho ngoko kuni ukuthi, Niya kufela ezonweni zenu; kuba ekubeni nithe anakholwa ukuba ndinguye, niya kufela ezonweni zenu.', 'XHO22'),
(6, 8, 25, 'Athi ngoko kuye, Ungubani na? Athi kubo uYesu, Ndiyiloo nto ndayixelayo kuni kwasekuqaleni.', 'XHO22'),
(6, 8, 26, 'Ndineendawo ezininzi zokuthetha nokugweba ngani; lowo wandithumayo uyinene; ndaye mna ndithetha ehlabathini, izinto endazivayo kuye, ndizithethe.', 'XHO22'),
(6, 8, 27, 'Akaqondanga ukuba ebethetha ngoYise kuwo.', 'XHO22'),
(6, 8, 28, 'Wathi ngoko uYesu kuwo, Xa nithe namphakamisa uNyana woMntu, noqonda oko ukuba ndinguye, nokuba andenzi nto ngokwam; ndisuka ndizithethe ezi zinto njengoko wandifundisayo uBawo.', 'XHO22'),
(6, 8, 29, 'Lowo wandithumayo unam; uBawo akandishiyanga ndedwa; kuba mna ndihlala ndizenza izinto ezikholekileyo kuye.', 'XHO22'),
(6, 8, 30, 'Akubon’ ukuba uyazithetha ezi zinto, bakholwa kuye, baba baninzi.', 'XHO22'),
(6, 8, 31, 'Uthe ngoko uYesu kuloo maYuda akholiweyo kuye, Ukuba nina nithe nahlala elizwini lam, noba ningabafundi bam, inyaniso.', 'XHO22'),
(6, 8, 32, 'Naniya kuyazi inyaniso, yaye inyaniso iya kunikhulula.', 'XHO22'),
(6, 8, 33, 'Aphendula athi kuye, Thina siyimbewu ka-Abraham, asizanga sibe ngamakhoboka amntu; utsho ngani na ukuthi, Niya kukhululwa?', 'XHO22'),
(6, 8, 34, 'Wawaphendula uYesu wathi, Inene, inene, ndithi kuni, Bonke abenza isono bangamakhoboka esono.', 'XHO22'),
(6, 8, 35, 'Ikhoboka ke alihlali endlwini ngonaphakade; ngunyana ohlala ngonaphakade.', 'XHO22'),
(6, 8, 36, 'Ngoko ukuba uNyana uthe wanikhulula, noba nikhululekile inene.', 'XHO22'),
(6, 8, 37, 'Ndiyazi ukuba niyimbewu ka-Abraham; ke nifuna ukundibulala, ngokuba ilizwi lam lingenakuhamba kuni.', 'XHO22'),
(6, 8, 38, 'Mna ndithetha endikubonileyo kuBawo; nani ke nenza enikubonileyo kuyihlo.', 'XHO22'),
(6, 8, 39, 'Aphendula athi kuye, Ubawo nguAbraham. Athi kuwo uYesu, Ukuba beningabantwana baka-Abraham, ninge niyenza imisebenzi ka-Abraham.', 'XHO22'),
(6, 8, 40, 'Kaloku ke, nifuna ukubulala mna, mntu unixelele inyaniso endayiva kuThixo; akakwenzanga oko uAbraham.', 'XHO22'),
(6, 8, 41, 'Nina nenza imisebenzi yooyihlo. Athi ngoko kuye, Thina asizelwe ngabuhenyu; mnye uBawo wethu, uThixo.', 'XHO22'),
(6, 8, 42, 'Uthe ke uYesu kuwo, Ukuba uThixo ebenguYihlo, ninge nindithanda; kuba mna ndaphuma kuThixo, ndifikile ke; kuba andizanga ngokwam, nguye owandithumayo.', 'XHO22'),
(6, 8, 43, 'Yini na ukuba ningayiqondi intetho yam? ngenxa yokuba ningenakuliva ilizwi lam.', 'XHO22'),
(6, 8, 44, 'Nina ningaboyihlo uMtyholi, neenkanuko zoyihlo niyathanda ukuzenza. Yena waye esisibulala-mntu kwasekuqaleni, akemi enyanisweni, ngokuba akukho nyaniso kuye. Xa sukuba ethetha ubuxoki, uthetha okukokwakhe; ngokuba ulixoki, noyise wawo.', 'XHO22'),
(6, 8, 45, 'Mna ke, ngokuba ndithetha inyaniso, anikholwa ndim.', 'XHO22'),
(6, 8, 46, 'Ngubani na kuni ondohlwayayo ngenxa yesono? Ukuba ke ndithetha inyaniso, kungani na ukuba ningakholwa ndim?', 'XHO22'),
(6, 8, 47, 'Lowo ungokaThixo, uyaweva amazwi kaThixo; ngenxa yoko aniweva nina, ngokuba ningengabakaThixo.', 'XHO22'),
(6, 8, 48, 'Aphendula ke ngoko amaYuda, athi kuye, Asitsho na ukuthi, ungumSamariya na, kwaye unedemon?', 'XHO22'),
(6, 8, 49, 'Waphendula uYesu wathi, Andinademon mna; ndibeka uBawo; nina ke nibe nindihlaza.', 'XHO22'),
(6, 8, 50, 'Mna ke andifuni luzuko lwam; ukho lowo ufunayo, ugwebayo.', 'XHO22'),
(6, 8, 51, 'Inene, inene, ndithi kuni, Ukuba umntu uthe waligcina ilizwi lam, akasayi kuza abone kufa naphakade.', 'XHO22'),
(6, 8, 52, 'Athi ngoko amaYuda kuye, Kukalokunje, siyazi ukuba unedemon. uAbraham wafa, nabo abaprofeti bafa; wena ke uthi, Ukuba umntu uthe waligcina ilizwi lam, akasayi kuza eve kufa naphakade.', 'XHO22'),
(6, 8, 53, 'Wena ungaba umkhulu yini na kunobawo wethu uAbraham, yena wafayo? nabaprofeti bafa; uzenza bani na?', 'XHO22'),
(6, 8, 54, 'Waphendula uYesu wathi, Ukuba mna ndithi ndizizukise, uzuko lwam alungebi nto; nguBawo ondizukisayo; enithi nguThixo wenu;', 'XHO22'),
(6, 8, 55, 'Kambe animazanga; Mna ke ndiyamazi; nokuba ndithe andimazi, ndoba ndilixoki njengani; ndiyamazi, ndiligcinile nelizwi lakhe.', 'XHO22'),
(6, 8, 56, 'Uyihlo uAbraham wagcobela ukuwubona umhla wam, wawubona, wavuya.', 'XHO22'),
(6, 8, 57, 'Athi ngoko amaYuda kuye, Ungekabi naminyaka imashumi mahlanu nje, umbonile na uAbraham?', 'XHO22'),
(6, 8, 58, 'Wathi kuwo uYesu, Inene, inene, ndithi kuni, Engekabikho uAbraham, mna ndikho kade.', 'XHO22'),
(6, 8, 59, 'Ngoko achola amatye ukuba amgibisele. Wazimela ke uYesu, waphuma etempileni, wacanda phakathi kwabo;', 'XHO22'),
(6, 9, 1, 'Uthe ke edlula, wabona umntu obe eyimfama kwasekuzalweni kwakhe.', 'XHO22'),
(6, 9, 2, 'Bambuza ke abafundi bakhe, besithi, Rabhi, ngubani na owonayo, ngulo, ngabazali bakhe, sini na, ukuze azalwe eyimfama nje?', 'XHO22'),
(6, 9, 3, 'Waphendula uYesu wathi, Akone lo, akone bazali bakhe; uzelwe eyimfama ukuze ibonakalaliselwe kuye.', 'XHO22'),
(6, 9, 4, 'Ndimelwe kukuyisebenza imisebenzi yowandithumayo, kusesemini; kuza ubusuku, xa kungekho namnye unako ukusebenza.', 'XHO22'),
(6, 9, 5, 'Xa ndisesehlabathini, ndilukhanyiselo lwehlabathi.', 'XHO22'),
(6, 9, 6, 'Akuba etshilo, watshica emhlabeni, wenza udaka ngamathe, wawathambisa ngodongwe amehlo emfama leyo.', 'XHO22'),
(6, 9, 7, 'Wathi kuyo, Hamba uye kuhlamba echibini laseSilowa (oko kukuthi ngentetho evakalayo, Thunyiwe). Yemka ngoko, yaya yahlamba, yeza ibona.', 'XHO22'),
(6, 9, 8, 'Baye ngoko abamelwane, nababembona ngaphambili ukuba uyimfama, besithi, Lo asinguye na obehleli engqiba?', 'XHO22'),
(6, 9, 9, 'Inxenye ibisithi, Nguye lo; abanye ke besithi, Ufana naye; yena ke wayesithi, Ndinguye.', 'XHO22'),
(6, 9, 10, 'Bathi ngoko kuye, Atyhilwe na amehlo akho?', 'XHO22'),
(6, 9, 11, 'Waphendula yena wathi, Umntu ekuthiwa nguYesu wenze udaka, waqaba amehlo am, wathi kum, Yiya echibini leSilowa, uhlambe; ndiyile ke ndahlamba, ndabuya ndibona.', 'XHO22'),
(6, 9, 12, 'Bathi ngoko kuye, Uphi na yena? Wathi, Andazi.', 'XHO22'),
(6, 9, 13, 'Bamzisa kubaFarisi lowo ubefudula eyimfama.', 'XHO22'),
(6, 9, 14, 'Ke kaloku kwakuyisabatha, oko uYesu wenza udaka, wawavula amehlo akhe.', 'XHO22'),
(6, 9, 15, 'Babuya ngoko ke bambuza nabaFarisi, ukuba utheni na ukuba abuye abone. Uthe ke yena kubo, Ubeke udaka emehlweni am, ndaza ndahlamba;', 'XHO22'),
(6, 9, 16, 'Babesithi ngoko abathile bakubaFarisi, Lo mntu asingowakwaThixo, ngokuba isabatha akayigcini. Babesithi abanye, Angathini na umntu ongumoni ukwenza imiqondiso engakanana? Kwabakho iyantlukwano phakathi kwabo.', 'XHO22'),
(6, 9, 17, 'Babuya bathi kwimfama, Uthini na wena ngaye, ewavulile nje amehlo akho? Wathi, Ungumprofeti.', 'XHO22'),
(6, 9, 18, 'Akakholwa ngoko amaYuda ngaye, ukuba ebeyimfama, wabuya wabona, ada abiza abazali balowo ubuye wabona.', 'XHO22'),
(6, 9, 19, 'Ababuza esithi, Lo ngunyana wenu na, enithi nina wazalwa eyimfama? Kutheni na ke ngoku ebona?', 'XHO22'),
(6, 9, 20, 'Abazali bakhe bawaphendula bathi, Siyazi ukuba lo ngunyana wethu, kwanokuba ke wazalwa eyimfama;', 'XHO22'),
(6, 9, 21, 'Le nto ke abonayo ngoku asazi; novule amehlo akhe, asimazi thina; Buzani kuye, woziphendulela.', 'XHO22'),
(6, 9, 22, 'Batsho abazali bakhe, ngokuba babesoyika amaYuda; kuba amaYuda abeselenqophisene ngokuthi, Ukuba uthe umntu wamvuma ukuba unguKristu, aze akhutshwe esikhungwini.', 'XHO22'),
(6, 9, 23, 'Bathi ngoko abazali bakhe, Uselemkhulu; buzani kuye.', 'XHO22'),
(6, 9, 24, 'Ambiza ngoko okwesibini umntu obeyimfama, athi kuye, Zukisa uThixo; siyazi thina ukuba lo mntu ungumoni.', 'XHO22'),
(6, 9, 25, 'Uphendule ngoko yena, wathi, Ukuba ungumoni, andazi; ndazi nto-nye, eyokuba ndibe ndiyimfama nje, ngoku ndiyabona.', 'XHO22'),
(6, 9, 26, 'Abuye ke athi kuye, Wenze ntoni na kuwe? utheni na ukuwavula amehlo akho?', 'XHO22'),
(6, 9, 27, 'Wawaphendula wathi, Sendinixelele, naza aneva; nithandelani na ukubuya nive? Ngaba nani nifuna ukuba ngabafundi bakhe na?', 'XHO22'),
(6, 9, 28, 'Amtshabhisa ngoko athi, Nguwe ongumfundi wakhe; Thina singabafundi bakaMoses.', 'XHO22'),
(6, 9, 29, 'Siyazi thina, ukuba uThixo uthethile kuMoses; lo yena asimazi apho avela khona.', 'XHO22'),
(6, 9, 30, 'Waphendula umntu lowo, wathi kubo, Nasi apha isimanga, ukuthi nina ningazi apho avela khona; ukanti uwavulile amehlo am.', 'XHO22'),
(6, 9, 31, 'Siyazi ke ukuba uThixo akabeva aboni; kodwa ukuba umntu uthi amhlonele uThixo, akwenze ukuthanda kwakhe, uyamva lowo.', 'XHO22'),
(6, 9, 32, 'Kwasephakadeni akuzanga kuviwe ukuba umntu wakha wavula amehlo ozelwe eyimfama.', 'XHO22'),
(6, 9, 33, 'Ukuba lo ebengenguye owakwaThixo, ebengenakwenza nto.', 'XHO22'),
(6, 9, 34, 'Aphendula athi kuye, Wena wazalelwa ezonweni kanye; usifundisa ntoni na? Bamkhuphela ngaphandle.', 'XHO22'),
(6, 9, 35, 'Weva uYesu ukuba amkhuphele phandle; Uthe akuba emfumene, wathi kuye, Uyakholwa na kuNyana kaThixo?', 'XHO22'),
(6, 9, 36, 'Uphendule yena wathi, Ongubani na, Nkosi, ukuze ndikholwe kuye?', 'XHO22'),
(6, 9, 37, 'uYesu wathi kuye, Umbonile; nguye lo uthetha nawe.', 'XHO22'),
(6, 9, 38, 'Wathi ke yena, Ndiyakholwa, Nkosi. Waza waqubuda kuye.', 'XHO22'),
(6, 9, 39, 'Wathi uYesu, Mna ndizele ugwebo kweli hlabathi, ukuze abangaboniyo babone; bathi ababonayo babe ziimfama.', 'XHO22'),
(6, 9, 40, 'Baziva ezo nto abakubaFarisi ababenaye, bathi kuye, Singaba siziimfama nathi na?', 'XHO22'),
(6, 9, 41, 'Wathi kubo uYesu, Ukuba beniziimfama, ngeningenasono; kungoku nithi, Siyabona; ngoko ke isono senu sihleli.', 'XHO22'),
(6, 10, 1, 'Inene, inene, ndithi kuni, Ongangeniyo ngalo isango ebuhlantini bezimvu, osuka akhwele ngandawo yimbi, lowo ulisela nesihange.', 'XHO22'),
(6, 10, 2, 'Ke yena ongena ngalo isango ngumalusi wezimvu.', 'XHO22'),
(6, 10, 3, 'Umgcini-sango uyamvulela lowo; nezimvu ziyaliva ilizwi lakhe, azibize ezakhe ngamagama, azikhokelele phandle.', 'XHO22'),
(6, 10, 4, 'Athi akuzikhupha ezakhe izimvu, uhamba phambi kwazo, zize izimvu zimlandele, ngokuba ziyalazi izwi lakhe.', 'XHO22'),
(6, 10, 5, 'Ke owasemzini aziyi kumlandela; ziya kusaba kuye; ngokuba azilazi ilizwi labasemzini.', 'XHO22'),
(6, 10, 6, 'Lo mzekeliso wawuthetha kubo uYesu; ke bona abaziqondanga ukuba zizinto zini na ezo abezithetha kubo.', 'XHO22'),
(6, 10, 7, 'Waphinda ngoko uYesu wathi kubo, Inene, inene, ndithi kuni, Ndim isango lezimvu.', 'XHO22'),
(6, 10, 8, 'Bonke abafika ngaphambi kwam bangamasela nezihange, kodwa izimvu azibeva.', 'XHO22'),
(6, 10, 9, 'Ndim isango; ukuba umntu uthe wangena ngam, wosindiswa; uya kungena ephuma, afumane utyani.', 'XHO22'),
(6, 10, 10, 'Isela alizi lingazele ukuze libe, lixhele, litshabalalise. Mna ndizele ukuze babe nobomi, babe nabo ke ngokugqithiseleyo.', 'XHO22'),
(6, 10, 11, 'Ndim umalusi olungileyo; umalusi olungileyo ubuncamela izimvu ubomi bakhe.', 'XHO22'),
(6, 10, 12, 'Ungumqeshwa, engenguye umalusi, o izimvu ezingezizo ezakhe, uthi akubona ingcuka isiza, azishiye izimvu, asabe;', 'XHO22'),
(6, 10, 13, 'Umqeshwa lo ke ubaleka kuba engumqeshwa engazikhathalele izimvu.', 'XHO22'),
(6, 10, 14, 'Ndim umalusi olungileyo, ndiyazazi ezam, ndiyaziwa zezam.', 'XHO22'),
(6, 10, 15, 'Njengoko endazi uBawo, nam ndiyamazi uBawo, nobomi bam ndibuncamela izimvu.', 'XHO22'),
(6, 10, 16, 'Ndinazo nezinye izimvu, ezingezizo ezobu buhlanti. Kwanazo ndimelwe kukuzizisa; zaye ziya kuliva ilizwi lam; zibe mhlambi mnye, nomalusi abe mnye.', 'XHO22'),
(6, 10, 17, 'Ngenxa yoku uBawo uyandithanda; ngokuba mna ndibuncama ubomi bam, ukuze ndibuye ndibuthabathe.', 'XHO22'),
(6, 10, 18, 'Akukho namnye ubuhluthayo kum, ndiyabuncama ngokwam; ndinegunya lokubuncama, ndinegunya lokubuya ndibuthabathe; lo mthetho ndawamkela kuBawo.', 'XHO22'),
(6, 10, 19, 'Abuya ngoko amaYuda athi qheke kubini ngenxa yala mazwi.', 'XHO22'),
(6, 10, 20, 'Yayisithi ke into eninzi kuwo, Unedemon, uyageza; yini na ukuba nimphulaphule?', 'XHO22'),
(6, 10, 21, 'Abanye bathi, La mazwi asingowomntu unendimoni. Idemon inako na ukuwavula amehlo eemfama?', 'XHO22'),
(6, 10, 22, 'Ke kaloku kwabakho umthendeleko wokuhlaziywa kwetempile eYerusalem; kwaye kusebusika.', 'XHO22'),
(6, 10, 23, 'Waye ehambahamba uYesu etempileni, evarandeni kaSolomon.', 'XHO22'),
(6, 10, 24, 'Amngqonga ngoko amaYuda, athi kuye, Koda kube nini na usiphongomisile? Ukuba unguye uKristu wena, sixelele ngokuphandle.', 'XHO22'),
(6, 10, 25, 'Wawaphendula uYesu wathi, Ndinixelele, noko anikholwa; imisebenzi endiyenzayo mna egameni likaBawo, yona iyangqina ngam.', 'XHO22'),
(6, 10, 26, 'Nina anikholwa; ngokuba aningabo abezimvu zam; njengoko ndanixelelayo.', 'XHO22'),
(6, 10, 27, 'Ezam izimvu ziyaliva ilizwi lam, ndibe nam ndizazi, zindilandela;', 'XHO22'),
(6, 10, 28, 'mna ndizinika ubomi obungunaphakade; azisayi kutshabalala naphakade; akukho namnye uya kuzihlutha esandleni sam.', 'XHO22'),
(6, 10, 29, 'uBawo ondinike zona ungaphezu kwabo bonke; akukho namnye unako ukuzihlutha esandleni sikaBawo.', 'XHO22'),
(6, 10, 30, 'Mna noBawo sibanye.', 'XHO22'),
(6, 10, 31, 'Abuya ngoko amaYuda achola amatye ukuba amxulube.', 'XHO22'),
(6, 10, 32, 'Wawaphendula uYesu wathi, Mininzi imisebenzi emihle endanibonisayo, ivela kuBawo; Nindixuluba ngenxa yawuphi na umsebenzi?', 'XHO22'),
(6, 10, 33, 'Amphendula amaYuda esithi, Asikuxulubeli msebenzi mhle; kungenxa yokunyelisa; nangokuba wena usithi, ungumntu, usuke uzenze uThixo.', 'XHO22'),
(6, 10, 34, 'Wawaphendula uYesu wathi, Akubhaliwe na emthethweni wenu, kwathiwa, Mna ndithe ningoothixo?', 'XHO22'),
(6, 10, 35, 'Ukuba uthe bona bangoothixo, àbafikelwa kubo ilizwi likaThixo, sibe ke sona isibhalo singenakwaphulwa;', 'XHO22'),
(6, 10, 36, 'Nithi nina ngaye lowo uYise wamngcwalisayo, wamthuma ehlabathini, ukuthi, Uyanyelisa; Ngokuba ndithe, NdinguNyana kaThixo?', 'XHO22'),
(6, 10, 37, 'Ukuba andiyenzi imisebenzi kaBawo, musani ukukholwa ndim.', 'XHO22'),
(6, 10, 38, 'Ke ukuba ndiyayenza, nokuba anikholwa ndim, kholwani yiyo yona imisebenzi; ukuze nazi, nikholwe, ukuba uBawo ukum, nam ndikuye.', 'XHO22'),
(6, 10, 39, 'Abuya ngoko afuna ukumbamba; kodwa wasinda esandleni sabo.', 'XHO22'),
(6, 10, 40, 'Wabuya wemka, waya phesheya kweYordan, kwindawo abebhaptizela kuyo uYohane kuqala; wahlala khona.', 'XHO22'),
(6, 10, 41, 'Kweza babaninzi kuye, bathi, uYohane okunene akenzanga namnye umqondiso;', 'XHO22'),
(6, 10, 42, 'Bakholwa kuye, abaninzi apho.', 'XHO22'),
(6, 11, 1, 'Ke kaloku, kwaye kusifa umntu othile, uLazaro waseBhetani, umzi wakulooMariya, noMarta udade wabo.', 'XHO22'),
(6, 11, 2, 'Yaye ke inguloo Mariya, wayithambisayo iNkosi ngamafutha aqholiweyo, wazisulayo iinyawo zayo ngeenwele zakhe, omnakwabo uLazaro wayesifa.', 'XHO22'),
(6, 11, 3, 'Bathumela ngoko oodade babo kuye, besithi, Nkosi, uyabona, lowo umthandayo uyafa.', 'XHO22'),
(6, 11, 4, 'Evile ke uYesu wathi, Esi sifo asiyelele kufeni; singenxa yozuko lukaThixo, ukuze uNyana kaThixo azukiswe ngaso.', 'XHO22'),
(6, 11, 5, 'Ke uYesu wayebathanda ooMarta nomsakwabo, noLazaro.', 'XHO22'),
(6, 11, 6, 'Uthe ngoko akuva ukuba uyafa, wakha okunene wahlala kuloo ndawo abekuyo iintsuku ezimbini.', 'XHO22'),
(6, 11, 7, 'Waza emveni koko wathi kubafundi bakhe, Masibuye siye kwelakwaYuda.', 'XHO22'),
(6, 11, 8, 'Bathi abafundi bakhe kuye, Rabhi, amaYuda ebefuna ngoku ukukuxuluba ngamatye nje; Ùya kubuya uye khona na?', 'XHO22'),
(6, 11, 9, 'Waphendula uYesu wathi, Akalishumi elinamabini na amaxa emini? Ukuba umntu uthi ahambe emini, akakhubeki; ngokuba elubona ukhanyiselo lweli hlabathi.', 'XHO22'),
(6, 11, 10, 'Kodwa ukuba umntu uthi ahambe ebusuku, uyakhubeka; ngokuba ukukhanya kungekho kuye.', 'XHO22'),
(6, 11, 11, 'Wazithetha ezi zinto; aze emveni koko athi kubo, uLazaro, umhlobo wethu, ulele; ndiyaya ukuba ndimvuse ebuthongweni.', 'XHO22'),
(6, 11, 12, 'Bathi ngoko abafundi bakhe, Nkosi, ukuba ulele, wosinda.', 'XHO22'),
(6, 11, 13, 'Ke yena uYesu ubethetha ukufa kwakhe. Bona ke bebeba uthetha ngokulala ubuthongo.', 'XHO22'),
(6, 11, 14, 'Wandula ngoku uYesu wathi kubo ngokungafihilisiyo, uLazaro ufile.', 'XHO22'),
(6, 11, 15, 'Ndiyavuya ke ngenxa yenu, ukuba ndibe ndingekho khona, ukuze nikholwe, masiye kuye noko.', 'XHO22'),
(6, 11, 16, 'Uthe ngoko uTomas, okuthiwa nguDidimo, kubafundi kunye naye, Masiye nathi, ukuze sife naye.', 'XHO22'),
(6, 11, 17, 'Efikile ngoko uYesu, wafika eselesengcwabeni iintsuku zone.', 'XHO22'),
(6, 11, 18, 'Ke kaloku iBhetani ibikufuphi neYerusalem, umgama wayo uzizitadiya ezingathi zilishumi elinantlanu.', 'XHO22'),
(6, 11, 19, 'Kwafika abaninzi bakumaYuda, kwabo babenoMarta noMariya, ukuze babakhuze ngaye umnakwabo.', 'XHO22'),
(6, 11, 20, 'Akuva ngoko uMarta ukuba uYesu uyeza, wamhlangabeza; ke yena uMariya wahlala endlwini.', 'XHO22'),
(6, 11, 21, 'Wathi ngoko uMarta kuYesu, Nkosi, ukuba ubulapha, umnakwethu ange engafanga.', 'XHO22'),
(6, 11, 22, 'Nangoku ndiyazi ukuba, ongathi ukucele kuThixo, oko wokunika uThixo.', 'XHO22'),
(6, 11, 23, 'Athi kuye uYesu, Umnakwenu uya kubuya avuke.', 'XHO22'),
(6, 11, 24, 'Athi uMarta kuye, Ndiyazi ukuba uya kubuya avuke eluvukweni, ngomhla wokugqibela.', 'XHO22'),
(6, 11, 25, 'Wathi uYesu kuye, Ndim uvuko, ndim ubomi; lowo ukholwayo kum, nokuba ubethe wafa, wodla ubomi.', 'XHO22'),
(6, 11, 26, 'Bonke abadla ubomi bekholwa kum, abasayi kufa naphakade; Uyakholwa na koko?', 'XHO22'),
(6, 11, 27, 'Athi kuye, Ewe, Nkosi, mna ndiyakholwa ukuba wena unguye uKristu, uNyana kaThixo, lowo uzayo ehlabathini.', 'XHO22'),
(6, 11, 28, 'Akuba etshilo wemka, waya wabiza uMariya umsakwabo ngasese, wathi, uMfundisi ukho, uyakubiza.', 'XHO22'),
(6, 11, 29, 'Evile ke yena, wasuka ngokukhawuleza waya kuye.', 'XHO22'),
(6, 11, 30, 'Ke kaloku, uYesu ebengekangeni edolophini, kodwa wayesekuloo ndawo wayedibene kuyo noMarta.', 'XHO22'),
(6, 11, 31, 'Athe ngoko amaYuda abenaye endlwini emkhuza, akumbona uMariya, ukuba usuke kamsinya waphuma, amlandela esithi, Uya engcwabeni, ukuya kulila khona.', 'XHO22'),
(6, 11, 32, 'uMariya ngoko, akufika apho uYesu ebekhona, akumbona, wawa ezinyaweni zakhe, esithi kuye, Nkosi, ukuba ubulapha, ange engafanga umnakwethu.', 'XHO22'),
(6, 11, 33, 'Uthe ngoko uYesu, akumbona elila, namaYuda abeze naye elila, wafutheka, wakhathazeka.', 'XHO22'),
(6, 11, 34, 'Athi, Nimbeke phi na? Bathi kuye, Nkosi, yiza ubone.', 'XHO22'),
(6, 11, 35, 'Walila uYesu.', 'XHO22'),
(6, 11, 36, 'Ayesithi ngoko amaYuda, Yabonani ke ukumthanda kwakhe!', 'XHO22'),
(6, 11, 37, 'Ke abathile kuwo bathi, Ubengenako na lo, wawavulayo amehlo emfama, ukwenza ukuba nalo angafi?', 'XHO22'),
(6, 11, 38, 'Abuye ke uYesu afutheke ngaphakathi kwakhe, eze engcwabeni. Laye ke lingumqolomba, kubekwe ilitye phezu kwawo.', 'XHO22'),
(6, 11, 39, 'Athi uYesu, Lisuseni ilitye. Athi kuye uMarta, udade wofileyo, Nkosi, uselenuka, kuba untsuku-ne efile.', 'XHO22'),
(6, 11, 40, 'Athi uYesu kuye, Anditshongo na kuwe ukuthi, Ukuba uthe wakholwa, wolubona uzuko lukaThixo?', 'XHO22'),
(6, 11, 41, 'Balisusa ngoko ilitye apho abelele khona ofileyo. Wawaphakamisa ke amehlo uYesu, wakhangela phezulu, wathi, Bawo, ndiyabulela kuwe kuba undivile.', 'XHO22'),
(6, 11, 42, 'Mna ke bendisazi ukuba uhleli undiva; ke nditsho ngenxa yesihlwele esixhonteleyo, ukuze bakholwe ukuba wena wandithuma.', 'XHO22'),
(6, 11, 43, 'Akuba etshilo, wadanduluka ngezwi elikhulu, wathi, Lazaro, phuma.', 'XHO22'),
(6, 11, 44, 'Waphuma ofileyo, ebotshiwe iinyawo nezandla ngezithandelo, nobuso bakhe bujikelwe ngeqhiya. Athi kubo uYesu, Mkhululeni, nimyeke ahambe.', 'XHO22'),
(6, 11, 45, 'Ithe ngoko into eninzi kumaYuda, ebize kuMariya, yakuzibona izinto azenzileyo uYesu, yakholwa kuye.', 'XHO22'),
(6, 11, 46, 'Ke abathile kuwo bemka baya kubaFarisi, babaxelela izinto azenzileyo uYesu.', 'XHO22'),
(6, 11, 47, 'Bahlanganisa ngoko ababingeleli abakhulu nabaFarisi intlanganiso yamatyala, bathi, Senza ntoni na? ngokuba lo mntu wenza imiqondiso emininzi.', 'XHO22'),
(6, 11, 48, 'Ukuba sithe samyeka ngokunje, bonke baya kukholwa kuye; athi ke amaRoma eze, athimbe indawo yethu, kwanohlanga lwethu.', 'XHO22'),
(6, 11, 49, 'Ke kaloku omnye wabo, uKayafa, engumbingeleli omkhulu ngaloo mnyaka, wathi kubo, Anazi nto nina;', 'XHO22'),
(6, 11, 50, 'Kananjalo aniqondi ukuba kusilungele, ukuba kufe umntu abe mnye ngenxa yabantu, ukuze lungatshabalali uhlanga luphela.', 'XHO22'),
(6, 11, 51, 'Oku ke akakuthethanga kuphuma kuye; wathi, kuba ebengumbingeleli omkhulu ngaloo nyaka, waprofeta ukuba uYesu uza kulufela uhlanga;', 'XHO22'),
(6, 11, 52, 'kungabi ngenxa yohlanga olo lodwa, kube ngenxa yabantwana bakaThixo abalusali, ukuba abahlanganisele ndawonye.', 'XHO22'),
(6, 11, 53, 'Kususela kuloo mini ngoko babhunga ukuba bambulale.', 'XHO22'),
(6, 11, 54, 'Ngoko uYesu akabanga sahamba ekuhleni phakathi kwamaYuda; Wesuka apho, waya ezweni elikufuphi nentlango, emzini ekuthiwa kukwaEfrayim, walibala khona apho nabafundi bakhe.', 'XHO22'),
(6, 11, 55, 'Yayikufuphi ke ipasika yamaYuda; kwenyuka into eninzi iphuma kwelo zwe, yaya eYerusalem phambi kwayo ipasika, ukuze izenze nyulu.', 'XHO22'),
(6, 11, 56, 'Ayemfuna ngoko uYesu, ethetha odwa, emi etempileni, esithi, Nithini na?', 'XHO22'),
(6, 11, 57, 'Ke kaloku ababingeleli abakhulu nabaFarisi babewise umthetho wokuthi, ukuba umntu uthe wazi apho akhona, maze axele, ukuze bambambe.', 'XHO22'),
(6, 12, 1, 'uYesu ngoko, kwiintsuku ezintandathu eziphambi kwayo ipasika, weza eBhetani, apho ebekhona uLazaro owayefile, awamvusayo kwabafileyo.', 'XHO22'),
(6, 12, 2, 'Bamenzela isidlo khona; uLazaro ke ebengomnye wabo babehleli naye etafileni.', 'XHO22'),
(6, 12, 3, 'Wathabatha ke uMariya iponti yamafutha aqholiweyo enadusi engangxengelelweyo, exabiso likhulu; wathambisa iinyawo zikaYesu, wazisula iinyawo zakhe ngeenwele zakhe; yazala ke indlu livumba lamafutha lawo aqholiweyo.', 'XHO22'),
(6, 12, 4, 'Uthe ngoko omnye wakubafundi bakhe, uYuda Sikariyoti, okaSimon, lowo umngcatshayo.', 'XHO22'),
(6, 12, 5, 'Bekungani na ukuba la mafutha aqholiweyo angathengiswanga ngeedenariyo ezimakhulu mathathu, kuphiwe amahlwempu?', 'XHO22'),
(6, 12, 6, 'Watsho ke, kungengakuba ebekhathalele mahlwempu; kodwa ngenxa yokuba ebelisela, inaye ingxowa, ziphatha oko zifakwa kuyo.', 'XHO22'),
(6, 12, 7, 'Wathi ngoko uYesu, Myeke; uwagcinele imini yokungcwatywa kwam.', 'XHO22'),
(6, 12, 8, 'Kuba amahlwempu nihleli ninawo; ke mna anihleli ninam.', 'XHO22'),
(6, 12, 9, 'Yathi ngoko indimbane enkulu yakumaYuda yakwazi ukuba ulapho; yeza kungengenxa kaYesu yedwa, yeza ukuze ibone noLazaro, lowo wamvusayo kwabafileyo.', 'XHO22'),
(6, 12, 10, 'Kodwa ababingeleli abakhulu babhunga ukuze bambulale noLazaro;', 'XHO22'),
(6, 12, 11, 'ngokuba ngenxa yakhe abaninzi bamaYuda bemka, bakholwa kuye uYesu.', 'XHO22'),
(6, 12, 12, 'Ngengomso indimbane eyabe ize emthendelekweni, yakuva ukuba uYesu uyeza eYerusalem,', 'XHO22'),
(6, 12, 13, 'Yathabatha amasebe esundu, yaphuma yaya kumkhawulela, yadanduluka, yathi, Hosana! Makabongwe uKumkani wakwaSirayeli, uzayo egameni leNkosi.', 'XHO22'),
(6, 12, 14, 'Uthe ke uYesu, elifumene iesile, wahlala phezu kwalo; njengoko kubhaliwe kwathiwa,', 'XHO22'),
(6, 12, 15, 'Musa ukoyika, ntombi inguZiyon; yabona, uKumkani wakho uza, ekhwele etholeni le-esile.', 'XHO22'),
(6, 12, 16, 'Ezo zinto ke abaziqondanga abafundi bakhe ukuqala; bathi kodwa akuba ezukisiwe uYesu, bandula ukukhumbula ukuba ezi zinto zibhaliwe ngaye, nokuba bayazenza ezi zinto kuye.', 'XHO22'),
(6, 12, 17, 'Ibe ingqina ngoko indimbane leyo, ibe inaye oko wambizayo uLazaro ukuba aphume engcwabeni, wamvusa kwabafileyo.', 'XHO22'),
(6, 12, 18, 'Ngenxa yoko indimbane yamhlangabeza; ngokuba yayivile ukuba uwenzile lo mqondiso.', 'XHO22'),
(6, 12, 19, 'Bathe ngoko abaFarisi phakathi kwabo, Niyabona na kodwa ukuba anincedi lutho? nalo ihlabathi lilandela yena.', 'XHO22'),
(6, 12, 20, 'Ke kaloku, kwakukho amaGrike athile kwababenyukile, beze kunqula emthendelekweni.', 'XHO22'),
(6, 12, 21, 'Wona ngoko eza kuFilipu waseBhetesayida yelaseGalili, acela kuye, esithi, Nkosi, singa singabona uYesu.', 'XHO22'),
(6, 12, 22, 'Ufika uFilipu axelele uAndreya; bathi kanjalo ooAndreya noFilipu baxelele uYesu.', 'XHO22'),
(6, 12, 23, 'Wabaphendula ke uYesu esithi, Lifikile ilixa lokuba uNyana woMntu azukiswe.', 'XHO22'),
(6, 12, 24, 'Inene, inene, ndithi kuni, Ukuba ukhozo lwengqolowa oluwe emhlabeni aluthanga lufe, lona lona luhlala lungabi nto; ke ukuba luthe lwafa, luthwala isiqhamo esininzi.', 'XHO22'),
(6, 12, 25, 'Lowo uwuthandayo umphefumlo wakhe, wolahlekelwa nguwo; lowo uwuthiyayo umphefumlo wakhe kweli hlabathi, wowulondolozela ubomi obungunaphakade.', 'XHO22'),
(6, 12, 26, 'Ukuba ubani uthi andikhonze, makandilandele; apho ndikhona mna, woba lapho nomkhonzi wam; ukuba ubani uthi andikhonze, wombeka uBawo.', 'XHO22'),
(6, 12, 27, 'Ngoku umphefumlo wam ukhathazekile; ndithini na? Bawo, ndisindise kweli lixa; kungokunje ndizele leli lixa.', 'XHO22'),
(6, 12, 28, 'Bawo, lizukise igama lakho. Kweza ngoko izwi liphuma ezulwini, lisithi, Ndilizukisile, ndiya kubuya ndilizukise.', 'XHO22'),
(6, 12, 29, 'Baye ngoko abantu ababemi khona, bekuva oko, bathi kuyaduduma. Abanye bathi, Kuthethe isithunywa sezulu kuye.', 'XHO22'),
(6, 12, 30, 'Waphendula uYesu wathi, Eli lizwi alibangakho ngenxa yam; libekho ngenxa yenu.', 'XHO22'),
(6, 12, 31, 'Kungoku ukugwetywa kwalo eli hlabathi; kungoku umphathi walo eli hlabathi aya kukhutshelwa phandle.', 'XHO22'),
(6, 12, 32, 'Mna ke, ukuba ndithe ndaphakanyiswa emhlabeni, ndiya kubatsalela bonke kum.', 'XHO22'),
(6, 12, 33, 'Wakutsho ke oku, eqondisa ukuba kukufa kuni na abeza kufa ngako.', 'XHO22'),
(6, 12, 34, 'Samphendula isihlwele sathi, Thina sivile emthethweni, ukuba uKristu yena uhlala ngonaphakade; utsho ngani na wena ukuthi, uNyana woMntu umelwe kukuthi aphakanyiswe? Ungubani na lo Nyana woMntu?', 'XHO22'),
(6, 12, 35, 'Uthe ngoko uYesu kubo, Liselilifutshane ixesha lokuba ukhanyiso lube nani. Hambani nisenokhanyiso, hleze ubumnyama bunifikele; kuba lowo uhamba ebumnyameni akakwazi apho aya khona.', 'XHO22'),
(6, 12, 36, 'Xa nisenokhanyiso, kholwani kulo ukhanyiso, ukuze nibe ngabantwana bokukhanya. Ezi zinto wazithetha uYesu, wemka, wazimela.', 'XHO22'),
(6, 12, 37, 'Ke kaloku, nakuba wayenze imiqondiso engaka phambi kwabo, abakholwanga kuye;', 'XHO22'),
(6, 12, 38, 'ukuze ilizwi likaIsaya umprofeti lizaliseke, awalithethayo esithi, Nkosi, ngubani na okholiweyo ludaba lwethu? Nengalo yeNkosi ityhileke kubani na?', 'XHO22'),
(6, 12, 39, 'Ababa nako ukukholwa ngoko, ngokuba uIsaya ubuya athi,', 'XHO22'),
(6, 12, 40, 'Uwamfamekisile amehlo abo, Uyiqaqadekisile intliziyo yabo; ukuze bangaboni ngamehlo, baqonde ngentliziyo, bajike, ndibaphilise.', 'XHO22'),
(6, 12, 41, 'Ezi zinto wazithetha uIsaya, oko wabubonayo ubuqaqawuli bakhe, wathetha ngaye.', 'XHO22'),
(6, 12, 42, 'Noko ke nakubo abaphathi into eninzi yakholwa kuye; kodwa ngenxa yabaFarisi babengamvumanga, ukuze bangagxothwa esikhungwini;', 'XHO22'),
(6, 12, 43, 'kuba babethanda uzuko oluvela ebantwini, ngaphezu kozuko oluvela kuThixo.', 'XHO22'),
(6, 12, 44, 'Wadanduluka ke uYesu, wathi, Lowo ukholwayo kum, akakholwa kum, ukholwa kulowo wandithumayo.', 'XHO22'),
(6, 12, 45, 'Lowo undibonayo ubona owandithumayo.', 'XHO22'),
(6, 12, 46, 'Mna, lukhanyiso, ndifikile ehlabathini, ukuze bonke abakholwayo kum bangahlali ebumnyameni.', 'XHO22'),
(6, 12, 47, 'Nokuba umntu uthe weva amazwi la am, akakholwa, mna andimgwebi; kuba andize kuligweba ihlabathi, ndize kulisindisa ihlabathi.', 'XHO22'),
(6, 12, 48, 'Lowo undigibayo, angawamkeli amazwi am, unaye omgwebayo; ilizwi endilithethileyo, lilo eliya kumgweba ngomhla wokugqibela.', 'XHO22'),
(6, 12, 49, 'Ngokuba mna andithethanga okuphuma kum; owandithumayo, uBawo ngokwakhe, nguye owandiwisela umthetho wokuba ndithini na, nokuba ndithethe ntoni na.', 'XHO22'),
(6, 12, 50, 'Ndiyazi nokwazi ukuba umthetho wakhe lowo bubomi obungunaphakade. Ngoko ke izinto endizithethayo mna, njengoko uBawo andixeleleyo, ndenjenjalo ukuzithetha.', 'XHO22'),
(6, 13, 1, 'Ke kaloku, phambi komthendeleko wepasika, esazi uYesu ukuba lifikile ilixa lokuba anduluke kweli hlabathi, aye kuYise, ebathandile abakhe abasehlabathini, wabathanda kwada kwasekupheleni.', 'XHO22'),
(6, 13, 2, 'Kwathi, sakuba isidlo sangokuhlwa, eselifakile umtyholi entliziyweni kaYuda Sikariyoti, okaSimon, ukuba amngcatshe.', 'XHO22'),
(6, 13, 3, 'uYesu wayesazi ukuba uYise uzinikele ezandleni zakhe zonke izinto, nokuba waphuma kuThixo, waya kuThixo:', 'XHO22'),
(6, 13, 4, 'usuka esidlweni, azibeke phantsi iingubo zakhe; wathabatha itawuli, wabhinqa.', 'XHO22'),
(6, 13, 5, 'Emva koko wagalela amanzi esityeni sokuhlambela, waqala ukuzihlamba iinyawo zabafundi, nokuzisula ngetawuli abeyibhinqile.', 'XHO22'),
(6, 13, 6, 'Uza ke kuSimon Petros; athi kuye uPetros, Nkosi, zihlanjwe nguwe na iinyawo zam?', 'XHO22'),
(6, 13, 7, 'Waphendula uYesu, wathi kuye, Endikwenzayo mna akukwazi wena okwangoku; wokwazi kamva.', 'XHO22'),
(6, 13, 8, 'Athi uPetros kuye, Akusayi kuzihlamba iinyawo zam, naphakade. Umphendule uYesu, wathi, Ukuba andithanga ndikuhlambe, akunasabelo nam.', 'XHO22'),
(6, 13, 9, 'Athi uSimon Petros kuye, Nkosi, mazingabi ziinyawo zam zodwa, mazibe zizandla nentloko.', 'XHO22'),
(6, 13, 10, 'Uthi uYesu kuye, Kulowo uhlanjiweyo akusafuneki nto, selikukuhlanjwa kweenyawo zodwa; uhlambulukile kwaphela; nani ke nihlambulukile, kodwa aninjalo nonke.', 'XHO22'),
(6, 13, 11, 'Kuba ebemazi lowo umngcatshayo; Wathi ngenxa yoko, Anihlambulukile nonke.', 'XHO22'),
(6, 13, 12, 'Ngoko, xeshikweni ebezihlambile iinyawo zabo, wazithabatha iingubo zakhe, wabuya wahlala phantsi, wathi kubo, Niyayazi na into endiyenzileyo kuni?', 'XHO22'),
(6, 13, 13, 'Nina ukundibiza nithi ndinguMfundisi, nithi ndiyiNkosi; kuba ndinjalo.', 'XHO22'),
(6, 13, 14, 'Ngoko ke, ukuba ndizihlambile iinyawo zenu mna, ndiyiNkosi noMfundisi; nani nifanele ukuhlambana iinyawo.', 'XHO22'),
(6, 13, 15, 'Kuba ndininike umzekelo, ukuze nani nenze, njengoko ndenze ngako mna kuni.', 'XHO22'),
(6, 13, 16, 'Inene, inene, ndithi kuni, Akukho mkhonzi umkhulu kunenkosi yakhe; nalowo uthunyiweyo mkhulu kunalowo umthumileyo.', 'XHO22'),
(6, 13, 17, 'Ukuba niyazazi ezi zinto, ninoyolo ukuba nithi nizenze.', 'XHO22'),
(6, 13, 18, 'Andithethi ngani, nonke; ndiyabazi abo mna ndibanyulileyo; oko kwenzeke ukuze isibhalo sizaliseke, esithi, Lowo udla isonka nam, undiphakamisele isithende sakhe.', 'XHO22'),
(6, 13, 19, 'Ndiqala kwangoku ukunixelela kungekehli, ukuze, xa kuthe kwehla, nikholwe ukuba ndinguye.', 'XHO22'),
(6, 13, 20, 'Inene, inene, ndithi kuni, Lowo wamkela endithe ndamthuma, wamkela mna; nalowo wamkela mna, wamkela owandithumayo.', 'XHO22'),
(6, 13, 21, 'Akuba ezithethile uYesu ezi zinto, wakhathazeka emoyeni wakhe, wangqina wathi, Inene, inene, ndithi kuni, omnye kuni aba uya kundingcatsha.', 'XHO22'),
(6, 13, 22, 'Baye ngoko abafundi bekhangelana, bethingaza ukuba uthetha bani na.', 'XHO22'),
(6, 13, 23, 'Ke kaloku, bekwayame esifubeni sikaYesu omnye umfundi wakhe, obethandwa nguYesu.', 'XHO22'),
(6, 13, 24, 'Unqwala ngoko uSimon Petros kulowo, ukuba abuze ukuba angaba uthetha bani na.', 'XHO22'),
(6, 13, 25, 'Eyame esifubeni sikaYesu, uthi kuye, Nkosi, ngubani na?', 'XHO22'),
(6, 13, 26, 'Waphendula uYesu wathi, Ngulowo ndiya kumnika iqhekeza lesonka, xa ndithe ndalithi nkxu. Elithi nkxu iqhekeza, walinika uYuda Sikariyoti, okaSimon.', 'XHO22'),
(6, 13, 27, 'Waza emveni kweqhekeza elo, wangena uSathana kuye lowo. Wathi ngoko uYesu kuye, Okwenzayo, kwenze kamsinya.', 'XHO22'),
(6, 13, 28, 'Ke kaloku, bekungekho namnye, kwababehleli naye esithebeni, ubesazi ukuba utsho ngani na kuye.', 'XHO22'),
(6, 13, 29, 'Kuba abathile, ekubeni ingxowa ibikuYuda, bebeba uYesu uthi kuye, Thenga ezo zinto sizifunela umthendeleko; nokuba makabanike into amahlwempu.', 'XHO22'),
(6, 13, 30, 'Elithabathile ngoko yena iqhekeza, waphuma kwaoko;', 'XHO22'),
(6, 13, 31, 'Xeshikweni ngoko aphumileyo, uthi uYesu, Kukalokunje, uNyana woMntu uzukisiwe, naye uThixo uzukisiwe kuye.', 'XHO22'),
(6, 13, 32, 'Ukuba ke uThixo uzukisiwe kuye, noThixo uya kumzukisa kuye ngokwakhe, waye eya kumzukisa kwangoku.', 'XHO22'),
(6, 13, 33, 'Bantwana bam, liselilifutshane ixesha lokuba ndibe nani. Niya kundifuna; njengoko sendithe kumaYuda, Apho ndiya khona mna, aninako ukufika nina; ngoko ke ndithi kuni.', 'XHO22'),
(6, 13, 34, 'Ndiniwisela umthetho omtsha, wokuba nithandane; Njengoko ndinithandileyo mna, ukuba nani nithandane.', 'XHO22'),
(6, 13, 35, 'Ngayo le nto baya kwazi bonke, ukuba ningabafundi bam, ukuba nithe nathandana.', 'XHO22'),
(6, 13, 36, 'Wathi uSimon Petros kuye, Nkosi, uya ngaphi na? Umphendule uYesu, wathi, Apho ndiya khona, aninako ukundilandela ngoku; wondilandela emveni koko.', 'XHO22'),
(6, 13, 37, 'Wathi uPetros kuye, Nkosi, kungani na ukuba ndingabi nako ukukulandela ngoku? Ndiya kuwuncamela wena umphefumlo wam.', 'XHO22'),
(6, 13, 38, 'Wamphendula uYesu wathi, Wowuncamela mna na umphefumlo wakho? Inene, inene, ndithi kuwe, Ayisayi kulila inkuku, ungathanga undikhanyele kathathu.', 'XHO22'),
(6, 14, 1, 'Intliziyo yenu mayingakhathazeki; kholwani kuThixo, nikholwe nakum.', 'XHO22'),
(6, 14, 2, 'Endlwini kaBawo zininzi iindawo zokuhlala; ukuba bekungenjalo, ngendanixelelayo. Ndiyemka, ndiya kunilungisela indawo;', 'XHO22'),
(6, 14, 3, 'Ukuba ke ndimkile ndaya kunilungisela indawo, ndiya kubuya ndize, ndinamkelele kum; ukuze apho ndikhona mna, nibe khona nani.', 'XHO22'),
(6, 14, 4, 'Apho ke ndiya khona mna niyakwazi, nendlela niyayazi.', 'XHO22'),
(6, 14, 5, 'Athi uTomas kuye, Nkosi, asazi apho uya khona; singathini na ke ukuyazi indlela?', 'XHO22'),
(6, 14, 6, 'Uthi uYesu kuye, Ndim indlela, ndim inyaniso, ndim ubomi; akukho namnye uzayo kuBawo, engezi ngam.', 'XHO22'),
(6, 14, 7, 'Ukuba benindazile mna, ninge nimazile noBawo; ngoku ke niyamazi, nimbonile nokumbona.', 'XHO22'),
(6, 14, 8, 'Athi uFilipu kuye, Nkosi, sibonise uYihlo, kosanelisa.', 'XHO22'),
(6, 14, 9, 'Athi uYesu kuye, Ndinaxesha lingakanana ndinani nje, akukandazi na, Filipu? Lowo undibonileyo mna umbonile uBawo; utsho ngani na ke wena ukuthi, Sibonise uYihlo?', 'XHO22'),
(6, 14, 10, 'Akukholwa na ukuba mna ndikuye uBawo, abe ke uBawo ekum? Amazwi endiwathethayo mna kuni, andiwathethi ngokuphuma kum; uBawo ke ohleli kum, nguye oyenzayo imisebenzi le.', 'XHO22'),
(6, 14, 11, 'Kholwani ndim, ukuba mna ndikuye uBawo, nokuba uBawo ukum; okanye nikholwe ndim ngenxa yayo imisebenzi leyo.', 'XHO22'),
(6, 14, 12, 'Inene, inene, ndithi kuni, Lowo ukholwayo kum, imisebenzi endiyenzayo mna, uya kuyenza naye yena; nemikhulu kunale uya kuyenza; ngokuba mna ndisiya kuBawo.', 'XHO22'),
(6, 14, 13, 'Nento enithe nayicela egameni lam, ndoyenza yona, ukuze uYise azukiswe ngoNyana.', 'XHO22'),
(6, 14, 14, 'Ukuba nithe nacela into ngegama lam, ndoyenza mna.', 'XHO22'),
(6, 14, 15, 'Ukuba niyandithanda, yigcineni imithetho yam.', 'XHO22'),
(6, 14, 16, 'Mna ndiya kucela kuBawo, aze aninike omnye uMthetheleli, ukuze ahlale nani ngonaphakade.', 'XHO22'),
(6, 14, 17, 'uMoya wenyaniso; lowo ihlabathi lingenako ukumamkela, ngokuba lingamboni, lingamazi nokumazi, ngokuba uhleli nani, kanjalo uya kuba kuni.', 'XHO22'),
(6, 14, 18, 'Andiyi kunishiya niziinkedama; ndiyeza kuni.', 'XHO22'),
(6, 14, 19, 'Liselilifutshane ixesha, ukuze ihlabathi lingabi sandibona; ke nina niyandibona; ngokuba ndisidla ubomi mna, nani niya kudla ubomi.', 'XHO22'),
(6, 14, 20, 'Ngaloo mini niya kwazi nina ukuba ndikuye uBawo, nani nikum, nam ndikuni.', 'XHO22'),
(6, 14, 21, 'Lowo unayo imithetho yam ayigcine, nguye ondithandayo; lowo ke undithandayo, uya kuthandwa nguBawo; nam ndiya kumthanda, ndizibonakalalise kuye.', 'XHO22'),
(6, 14, 22, 'Athi kuye uYuda (ingenguye uSikariyoti), Nkosi, kutheni na, le nto uza kuzibonakalalisa kuthi, ungazibonakalalisi kulo ihlabathi?', 'XHO22'),
(6, 14, 23, 'Waphendula uYesu wathi kuye, Ukuba umntu uyandithanda, woligcina ilizwi lam; wothi uBawo amthande, size kuye, sihlale naye.', 'XHO22'),
(6, 14, 24, 'Lowo ungandithandiyo akawagcini amazwi am; ilizwi enilivayo asililo elam, lelikaBawo owandithumayo.', 'XHO22'),
(6, 14, 25, 'Ezi zinto ndizithethile kuni ndisahleli nani.', 'XHO22'),
(6, 14, 26, 'Ke uMthetheleli, uMoya oyiNgcwele, aya kumthuma uBawo egameni lam, yena uya kunifundisa zonke izinto, anikhumbuze zonke izinto endizithethileyo kuni.', 'XHO22'),
(6, 14, 27, 'Ndishiya uxolo kuni; ndininika uxolo lwam; andininiki njengokunika kwehlabathi. Mayingakhathazeki intliziyo yenu, mayingabi nabugwala.', 'XHO22'),
(6, 14, 28, 'Nivile ukuba ndithe mna kuni, Ndiyemka, ndibuye ndize kuni. Ukuba benindithanda, ngenavuyayo kuba ndathi, Ndiya kuBawo; ngokuba uBawo mkhulu kunam.', 'XHO22'),
(6, 14, 29, 'Ngoku ke ndinixelele kungekehli, ukuze, xa kuthe kwehla, nikholwe.', 'XHO22'),
(6, 14, 30, 'Andisayi kuba sathetha zinto zininzi nani; kuba esiza umphathi weli hlabathi, engenanto nam.', 'XHO22'),
(6, 14, 31, 'Ke, ukuze liqonde ihlabathi ukuba ndiyamthanda uBawo; kwaye njengoko uBawo endiwisele umthetho, ndenjenjalo. Vukani, simke apha.', 'XHO22'),
(6, 15, 1, 'Mna ndingumdiliya wenyaniso, umlimi wawo ke nguBawo.', 'XHO22'),
(6, 15, 2, 'Amasebe onke akum angavelisi siqhamo, uyawasusa;', 'XHO22'),
(6, 15, 3, 'Nina nihlambulukile, ngenxa yelizwi endilithethileyo kuni.', 'XHO22'),
(6, 15, 4, 'Hlalani kum, mna ndihlale kuni. Njengokuba isebe lingenako ukuthwala isiqhamo ngokwalo, lingahlalanga emdiliyeni; aninako ukuphinda nina, ukuba anihlalanga kum.', 'XHO22'),
(6, 15, 5, 'Umdiliya ndim, nina ningamasebe; lowo uhleli kum, ndibe mna ndihleli kuye, yena uthwala isiqhamo esininzi; ngokuba, ningekum, aninakwenza nto.', 'XHO22'),
(6, 15, 6, 'Ukuba umntu uthe akahlala kum, ulahlelwa phandle njengesebe, ome; bazibuthe abantu, baziphose emlilweni, zitshiswe.', 'XHO22'),
(6, 15, 7, 'Ukuba nithe nahlala kum, namazwi am ahlala kuni, nocela into enisukuba niyithanda, niyenzelwe.', 'XHO22'),
(6, 15, 8, 'Uzukiswa uBawo ngale nto, yokuba nithwale isiqhamo esininzi; niya kuba ngabafundi bam ngokunjalo.', 'XHO22'),
(6, 15, 9, 'Njengoko uBawo wandithandayo, nam ndanithanda; hlalani ke eluthandweni lwam.', 'XHO22'),
(6, 15, 10, 'Ukuba nithi niyigcine imithetho yam, hlalani eluthandweni lwam; njengokuba mna ndayigcinayo imithetho kaBawo, ndahlala eluthandweni lwakhe.', 'XHO22'),
(6, 15, 11, 'Ezi zinto ndizithethile kuni, ukuze uvuyo lwam luhlale kuni, luthi novuyo lwenu luzaliseke.', 'XHO22'),
(6, 15, 12, 'Nguwo lo umthetho wam, wokuba nithandane, njengoko ndanithandayo nina.', 'XHO22'),
(6, 15, 13, 'Akukho namnye unalo uthando olungaphezu kolu, lokuba ubomi bakhe abuncamele izihlobo zakhe.', 'XHO22'),
(6, 15, 14, 'Nizizihlobo zam nina, ukuba nithi nikwenze konke endinimisela khona mna.', 'XHO22'),
(6, 15, 15, 'Ngoku ke andinibize ngokuba ningabakhonzi; ngokuba umkhonzi akakwazi okwenziwa yinkosi yakhe; ke mna ndithe nizizihlobo; ngokuba zonke izinto endaziva kuBawo, ndanazisa zona.', 'XHO22'),
(6, 15, 16, 'Aninyula mna; ndim owanyula nina, ndanimisela ukuba nihambe nithwale isiqhamo, size nesiqhamo senu sihlale; ukuze oko enithe nakucela kuBawo egameni lam, aninike.', 'XHO22'),
(6, 15, 17, 'Ndiniwisela lo mthetho wokuba nithandane.', 'XHO22'),
(6, 15, 18, 'Ukuba ihlabathi linithiyile, yazini ukuba lindithiye kuqala kunani.', 'XHO22'),
(6, 15, 19, 'Ukuba beningabehlabathi, ihlabathi linge likuthanda okwalo; ke ngokuba ningengabehlabathi, ndaninyulayo mna kulo ihlabathi, ngenxa yoko linithiyile ihlabathi.', 'XHO22'),
(6, 15, 20, 'Likhumbuleni ilizwi endalithethayo mna kuni, elithi, Akukho mkhonzi umkhulu kunenkosi yakhe. Ukuba banditshutshisile mna, bonitshutshisa nani; Ukuba ilizwi lam baligcina, nelenu boligcina.', 'XHO22'),
(6, 15, 21, 'Ezo zinto ke baya kuzenza kuni ngenxa yegama lam, ngokuba bengamazi owandithumayo.', 'XHO22'),
(6, 15, 22, 'Ukuba bendingezanga ndathetha kubo, ngebengabanga nasono; ngoku abanasikhuselo sesono sabo.', 'XHO22'),
(6, 15, 23, 'Lowo uthiyileyo mna uthiye noBawo.', 'XHO22'),
(6, 15, 24, 'Ukuba bendingenzanga phakathi kwabo imisebenzi engenziwanga mntu wumbi, ngebengabanga nasono; ngoku ke babonile; noko bandithiyile mna noBawo.', 'XHO22'),
(6, 15, 25, 'Kwenzeke ukuze lizaliseke ilizwi elibhaliweyo emthethweni wabo, elithi, Bandithiya kungekho sizathu.', 'XHO22'),
(6, 15, 26, 'Kodwa akufika uMthetheleli, endiya kumthuma mna kuni evela kuBawo, uMoya wenyaniso ophuma kuBawo, wongqina ngam.', 'XHO22'),
(6, 15, 27, 'Nani ke niyangqina, ngokuba beninam kwasekuqalekeni.', 'XHO22'),
(6, 16, 1, 'Ezi zinto ndizithethile kuni, ukuze ningakhutyekiswa.', 'XHO22'),
(6, 16, 2, 'Baya kunigxotha esikhungwini; ewe, kuza ilixa lokuba bonke abanibulalayo, babe bona bakhonza uThixo.', 'XHO22'),
(6, 16, 3, 'Ezo zinto baya kuzenza kuni, ngokuba bengamazanga uBawo kwanam.', 'XHO22'),
(6, 16, 4, 'Ezi zinto ndizithethile kuni, ukuze, xa lithe lafika ilixa, nizikhumbule ukuba mna ndandinixelele. Ezi zinto ke andizithethanga kuni kwasekuqaleni, ngokuba bendinani.', 'XHO22'),
(6, 16, 5, 'Ngoku ke ndiyemka, ndiya kowandithumayo; akukho namnye kuni undibuzayo, athi, Uya phi na?', 'XHO22'),
(6, 16, 6, 'Ngokuba ke ezi zinto ndizithethile kuni, iintlungu ziyizalisile intliziyo yenu.', 'XHO22'),
(6, 16, 7, 'Kodwa mna ndinixelela inyaniso; kunilungele ukuba ndimke; kuba, ndingemkanga, uMthetheleli akayi kuza kuni; ke, ndithe ndemka, ndiya kumthumela kuni.', 'XHO22'),
(6, 16, 8, 'Efikile ke, uya kulohlwaya ihlabathi lidane, ngenxa yesono, nangenxa yobulungisa, nangenxa yomgwebo;', 'XHO22'),
(6, 16, 9, 'ngenxa yesono, ngokokuba bengakholwa kum;', 'XHO22'),
(6, 16, 10, 'ngenxa yobulungisa, ngokokuba ndisiya kuBawo, ize ningabi sandibona;', 'XHO22'),
(6, 16, 11, 'ngenxa yomgwebo, ngokokuba umphathi weli hlabathi egwetyiwe.', 'XHO22'),
(6, 16, 12, 'Ndisenezinto ezininzi zokuthetha kuni, kodwa aninako ukuzithwala ngoku.', 'XHO22'),
(6, 16, 13, 'Xa ke athe wafika yena uMoya wenyaniso, uya kunikhokelela kuyo yonke inyaniso; uya kuthetha athe wakuva, anibikele izinto ezizayo.', 'XHO22'),
(6, 16, 14, 'Yena uya kundizukisa; ngokuba eya kwamkela kokukokwam, aze anibikele ke.', 'XHO22'),
(6, 16, 15, 'Zonke ezo anazo uBawo zezam; ngenxa yoko ndathi, uya kwamkela kokukokwam, aze anibikele ke.', 'XHO22'),
(6, 16, 16, 'Selilixesha elifutshane, ize ningandiboni; kubuye kube lixesha elifutshane, nibuye nindibone, ngokuba mna ndiya kuBawo.', 'XHO22'),
(6, 16, 17, 'Ithe ngoko inxenye yakubafundi bakhe phakathi kwayo, Yintoni na le ayithethayo kuthi, athi, Liselilixesha elifutshane, nibuye ningandiboni; kubuye kube lixesha elifutshane, nibuye nindibone; nokuthi, Mna ndiya kuBawo.', 'XHO22'),
(6, 16, 18, 'Babesithi ngoko, Yintoni na le ayithethayo, athi, Lixesha elifutshane? Asinakuyazi into ayithethayo.', 'XHO22'),
(6, 16, 19, 'Wazi ngoko uYesu ukuba babethanda ukumbuza, wathi kubo, Nibuzana ngale nto na, yokuba ndithe, Selilixesha elifutshane, nibuye ningandiboni; kubuye kube lixesha elifutshane, nibuye nindibone?', 'XHO22'),
(6, 16, 20, 'Inene, inene, ndithi kuni, niya kulila nihlahlambe, ke lona ihlabathi liya kuvuya; niya kuxhalaba nina, ke ukuxhalaba kwenu kuya kuba luvuyo.', 'XHO22'),
(6, 16, 21, 'Umfazi xa aza kuzala, uyaxhalaba, ngokuba ilixa lakhe lifikile; xa athe ke wazala umntwana, akabi sayikhumbula loo mbandezelo, ngenxa yovuyo lokuba kuzelwe umntu ehlabathini.', 'XHO22'),
(6, 16, 22, 'Nani ke ngoko nixhalabile ngokunje; kodwa ndobuya ndinibone; yaye iya kuvuya intliziyo yenu, novuyo lwenu akukho namnye uya kuluhlutha kuni.', 'XHO22'),
(6, 16, 23, 'Ngaloo mini aniyi kundibuza nto. Inene, inene, ndithi kuni, Zonke izinto enithe nazicela kuBawo egameni lam, woninika.', 'XHO22'),
(6, 16, 24, 'Okwangoku anikaceli nto egameni lam; celani, niya kwamkela, ukuze uvuyo lwenu luzaliseke.', 'XHO22'),
(6, 16, 25, 'Ezi zinto ndizithethile kuni ngokuntsonkotha; kuza ilixa endingasayi kuba sathetha kuni ngezafobe; ndiya kunibikela ngokusekuhleni ngaye uBawo.', 'XHO22'),
(6, 16, 26, 'Ngaloo mini niya kucela egameni lam; anditsho kuni ukuthi, mna ndiya kunicelela kuBawo;', 'XHO22'),
(6, 16, 27, 'Kuba uBawo ngokwakhe uyanithanda; ngokuba nina nindithandile, nakholwa ukuba mna ndaphuma kuThixo.', 'XHO22'),
(6, 16, 28, 'Ndaphuma kuBawo, ndeza ehlabathini; ndibuya ndilishiye ihlabathi, ndiye kuBawo.', 'XHO22'),
(6, 16, 29, 'Bathi kuye abafundi bakhe, Uyabona, ngoku uthetha ngokusekuhleni, akuthethi nto ngakuntsonkotha.', 'XHO22'),
(6, 16, 30, 'Ngoku siyazi, ukuba uyazazi zonke izinto, akusafuneki kuwe ukuba ubani akubuze nto; ngaloo nto siyakholwa ukuba waphuma kuThixo.', 'XHO22'),
(6, 16, 31, 'Wawaphendula uYesu wathi, Niyakholwa na ngoku?', 'XHO22'),
(6, 16, 32, 'Niyabona, kuza ilixa, nakaloku lifikile, lokuba nichithwe, elowo aye kowabo, nindishiye ndedwa; ukanti andindedwa, ngokuba uBawo unam.', 'XHO22'),
(6, 16, 33, 'Ezi zinto ndizithethile kuni, ukuze nibe noxolo kum. Ehlabathini niya kuba nembandezelo; kodwa yomelelani; mna ndiloyisile ihlabathi.', 'XHO22'),
(6, 17, 1, 'Ezi zinto wazithetha uYesu, wawaphakamisa amehlo akhe wakhangela ezulwini, wathi, Bawo, lifikile ilixa; mzukise uNyana wakho, ukuze noNyana wakho akuzukise;', 'XHO22'),
(6, 17, 2, 'njengokuba wamnika igunya kubantu bonke, ukuze bonke omnike bona, abanike ubomi obungunaphakade.', 'XHO22'),
(6, 17, 3, 'Bubo ke obu ubomi obungunaphakade, ukuba mabakwazi, wena ukuphela koThixo oyinyaniso, naye uYesu Kristu omthumileyo.', 'XHO22'),
(6, 17, 4, 'Mna ndikuzukisile emhlabeni, ndiwufezile umsebenzi obundinikile ukuba ndiwenze.', 'XHO22'),
(6, 17, 5, 'Ndizukise ke ngoku, wena Bawo, kuwe ngokwakho, ngozuko ebendinalo kuwe, lingekabikho ihlabathi.', 'XHO22'),
(6, 17, 6, 'Ndilibonakalisile igama lakho kubo abantu obundinike bona, bephuma ehlabathini; babengabakho, wabanika mna; Ilizwi lakho baligcinile.', 'XHO22'),
(6, 17, 7, 'Ngoku baqondile, ukuba zonke izinto ondinike zona ziphuma kuwe.', 'XHO22'),
(6, 17, 8, 'Ngokuba amazwi ondinike wona, ndiwanike bona; bona bawathabatha, baqonda ngokwenyaniso ukuba ndaphuma kuwe; bakholwa ukuba wena wandithuma.', 'XHO22'),
(6, 17, 9, 'Ndithandazela bona; andiceleli lona ihlabathi, ndicelela abo undinike bona; ngokuba bengabakho.', 'XHO22'),
(6, 17, 10, 'Ke zonke ezam zezakho, zibe ezakho zezam; Ndizukisiwe ke kubo.', 'XHO22'),
(6, 17, 11, 'Andisekhona ehlabathini, ke bona basesehlabathini; mna ndiza kuwe. Bawo Ongcwele, bagcine ngegama lakho abo undinike bona, ukuze babe banye, njengokuba sibanye.', 'XHO22'),
(6, 17, 12, 'Oko bendinabo ehlabathini mna, bendibalondolozele egameni lakho; owandinika bona ndibagcinile; akukho namnye kubo utshabaleleyo, ingenguye lowo wentshabalalo, ukuze isibhalo sizaliseke.', 'XHO22'),
(6, 17, 13, 'Ngoku ke ndiza kuwe; nezi zinto ndizithetha ehlabathini, ukuze babe nalo uvuyo lwam luzalisekile kubo.', 'XHO22'),
(6, 17, 14, 'Mna ndibanike ilizwi lakho; laza ihlabathi labathiya, ngokuba bengengabo abehlabathi, njengokuba nam ndingenguye owehlabathi.', 'XHO22'),
(6, 17, 15, 'Andiceli ukuba ubasuse ehlabathini; ndicela ukuba ubagcine kuyo inkohlakalo.', 'XHO22'),
(6, 17, 16, 'Abangabo abehlabathi, njengokuba nam ndingenguye owehlabathi.', 'XHO22'),
(6, 17, 17, 'Bangcwalise ngayo inyaniso yakho; ilizwi lakho liyinyaniso.', 'XHO22'),
(6, 17, 18, 'Njengokuba wandithuma ehlabathini, nam ndibathume bona ehlabathini.', 'XHO22'),
(6, 17, 19, 'Ngenxa yabo ke ndiyazingcwalisa mna, ukuze nabo babe ngabangcwalisiweyo yinyaniso.', 'XHO22'),
(6, 17, 20, 'Andiceleli ke aba bodwa, ndicelela nabo baya kukholwa kum ngelizwi labo;', 'XHO22'),
(6, 17, 21, 'ukuze bonke babe banye; njengokuba wena, Bawo, ukum, mna ndikuwe, ukuze nabo babe banye kuthi; ukuze ihlabathi likholwe ukuba wena wandithuma.', 'XHO22'),
(6, 17, 22, 'Uzuko owandinika lona, ndilunike bona; ukuze babe banye, njengokuba thina sibanye;', 'XHO22'),
(6, 17, 23, 'mna ndikubo, wena ukum, ukuze babe ngabazalisekileyo ntweni-nye; ukuze liqonde ihlabathi ukuba wena undithumile, wabathanda, njengokuba wandithandayo nam.', 'XHO22'),
(6, 17, 24, 'Bawo, ndithanda ukuba nabo abo undinike bona, babe nam, apho ndikhona mna; ukuze balubone uzuko lwam owandinika lona; ngokuba wandithanda kwaphambi kokusekwa kwehlabathi.', 'XHO22'),
(6, 17, 25, 'Bawo olilungisa, ihlabathi lona alikwazanga, ke mna ndikwazile; naba bathe bazi ukuba wena wandithuma;', 'XHO22'),
(6, 17, 26, 'ndabazisa negama lakho, ndandiya kubazisa; ukuze uthando ondithande ngalo lube kubo, nam ndibe kubo.', 'XHO22'),
(6, 18, 1, 'Akuba ezithethile uYesu ezi zinto, waphuma nabafundi bakhe, waya phesheya komlanjana oyiKidron, apho kwakukho umyezo, awangena kuwo yena nabafundi bakhe.', 'XHO22'),
(6, 18, 2, 'NoYuda ke, lowo umngcatshayo, ubeyazi indawo leyo, kuba uYesu ubesakubutha futhi khona apho nabafundi bakhe.', 'XHO22'),
(6, 18, 3, 'Athi ngoko uYuda, ethabathe ibutho lamasoldati namadindala kubabingeleli abakhulu nabaFarisi, eze khona enezikhanyiso nezibane neentonga.', 'XHO22'),
(6, 18, 4, 'Ezazi ngoko uYesu zonke izinto ezimzelayo, waphuma, wathi kubo, Nifuna bani na?', 'XHO22'),
(6, 18, 5, 'Bamphendula bathi, uYesu waseNazarete. Athi kubo uYesu, Ndinguye. Waye ke noYuda, lowo umngcatshayo, emi nabo.', 'XHO22'),
(6, 18, 6, 'Akutsho ngoko kubo ukuthi, Ndinguye, bahlehla, bawa emhlabeni.', 'XHO22'),
(6, 18, 7, 'Wabuya ke wababuza esithi, Nifuna bani na? Bathi ke bona, uYesu umNazarete.', 'XHO22'),
(6, 18, 8, 'Waphendula uYesu wathi, Ndinixelele ukuba ndinguye; ukuba ngoko nifuna mna, bayekeni aba bemke;', 'XHO22'),
(6, 18, 9, 'ukuze lizaliseke ilizwi awalithethayo elithi, Kwabo undinike bona, andilahlekwanga namnye.', 'XHO22'),
(6, 18, 10, 'Uthe ngoko uSimon Petros, enekrele, walirhola, wamxabela umkhonzi wombingeleli omkhulu, wamnqamla indlebe yokunene. Igama lesicaka lalinguMalko.', 'XHO22'),
(6, 18, 11, 'Wathi ngoko uYesu kuPetros, Lifake ikrele lakho esingxotyeni salo; indebe andinikileyo uBawo andisayi kuyisela na?', 'XHO22'),
(6, 18, 12, 'Laza elo qela lamajoni nomthetheli-waka namadindala amaYuda bambamba uYesu, bambopha.', 'XHO22'),
(6, 18, 13, 'Wamsa kuAnas kuqala; kuba wayenguyise womkakhe kuKayafa, obengumbingeleli omkhulu ngaloo mnyaka.', 'XHO22'),
(6, 18, 14, 'Ke kaloku uKayafa wayengulowo wabhunga namaYuda ngokuthi, Kuyinto elungileyo, ukuba umntu omnye afele abantu.', 'XHO22'),
(6, 18, 15, 'Ke kaloku uSimon Petros wayemlandela uYesu, naye omnye umfundi. Loo mfundi ke wayesazeka kumbingeleli omkhulu, wangena noYesu entendelezweni yendlu yombingeleli omkhulu.', 'XHO22'),
(6, 18, 16, 'uPetros ke wayemi ngasemnyango ngaphandle. Waphuma ke omnye umfundi, lowo ubesazeka kumbingeleli omkhulu, wathetha nomgcini-sango, wamngenisa uPetros.', 'XHO22'),
(6, 18, 17, 'Sathi ngoko isicakazana esingumgcini-sango kuPetros, Ingaba nawe ungowabafundi balo mntu yini na? Athi, Andinguye.', 'XHO22'),
(6, 18, 18, 'Ke kaloku kwakumi khona abakhonzi namadindala bebase umlilo wamalahle; bamana besotha, waye ke uPetros emi nabo esotha.', 'XHO22'),
(6, 18, 19, 'Umbingeleli omkhulu ngoko wambuza uYesu ngabafundi bakhe, nangayo imfundiso yakhe.', 'XHO22'),
(6, 18, 20, 'Wamphendula uYesu wathi, Mna ndathetha ngokungafihlisiyo kulo ihlabathi; Bendisoloko ndifundisa endlwini yesikhungu nasetempileni, apho amaYuda ebesakubuthelana khona; andithethanga nto emfihlekweni.', 'XHO22'),
(6, 18, 21, 'Yini na ukuba ubuze kum? Buza kwabo bayivileyo into endayithethayo kubo; uyabona, bayakwazi oko ndakuthethayo.', 'XHO22'),
(6, 18, 22, 'Ke kaloku, akuba etshilo, elinye lamadindala, elalimi khona, lammonxoza uYesu, lathi, Wenjenje na ukumphendula umbingeleli omkhulu?', 'XHO22'),
(6, 18, 23, 'Wamphendula uYesu wathi, Ukuba ndithethe kakubi, ngqinela ububi obo; kodwa ukuba ndithethe kakuhle, undibethelani na?', 'XHO22'),
(6, 18, 24, 'uAnas wamthumela ke ebotshiwe kuKayafa umbingeleli omkhulu.', 'XHO22'),
(6, 18, 25, 'Ke kaloku uSimon Petros wayemi esotha. Bathi ngoko kuye, Ingaba nawe ungowabafundi bakhe yini na? Wakhanyela ke wathi, Andinguye.', 'XHO22'),
(6, 18, 26, 'Athi omnye umkhonzi wombingeleli omkhulu, osisalamane soPetros wamnqamla indlebe, Andikubonanga mna yini na emyezweni unaye?', 'XHO22'),
(6, 18, 27, 'Wabuya ke wakhanyela uPetros. Yasuka yalila kwaoko inkuku.', 'XHO22'),
(6, 18, 28, 'Bamsusa ngoko uYesu kuKayafa, bamsa kwindlu yerhuluneli; bekukusasa ke oko; yaye bona abazange bangene endlwini yerhuluneli, ukuze bangazenzi inqambi; ukuze bayidle ipasika.', 'XHO22'),
(6, 18, 29, 'Waphuma ke uPilato waya kubo, wathi, Nimisa tyala lini na kuye lo mntu?', 'XHO22'),
(6, 18, 30, 'Baphendula bathi kuye, Ukuba ebengenguye umenzi wobubi, singe singamnikelanga kuwe.', 'XHO22'),
(6, 18, 31, 'Wathi ngoko uPilato kubo, Mthabatheni nina, nimgwebe ngesiko lenu. Athi ngoko amaYuda kuye, Akuvumelekile kuthi ukuba sibulale umntu;', 'XHO22'),
(6, 18, 32, 'ukuze ilizwi likaYesu lizaliseke awalithethayo, eqondisa ukuba kukufa kuni na abeza kufa ngako.', 'XHO22'),
(6, 18, 33, 'Wabuya ngoko wangena uPilato endlwini yerhuluneli, wambiza uYesu, wathi kuye, Wena unguye na uKumkani wamaYuda?', 'XHO22'),
(6, 18, 34, 'Wamphendula uYesu wathi, Uyithetha le nto iphuma kuwe, wayixelelwa ngabanye ngam, sini na?', 'XHO22'),
(6, 18, 35, 'Waphendula uPilato wathi, Mna ndingumYuda na? Luhlanga lwakowenu nababingeleli abakhulu abakunikeleyo kum; wenze ntoni na?', 'XHO22'),
(6, 18, 36, 'Waphendula uYesu wathi, Ubukumkani bam asibubo obeli hlabathi; ukuba bebububo obeli hlabathi ubukumkani bam, abakhonzi bam bebeya kulwa, ukuze ndinganikelwa kumaYuda; ngoku ke ubukumkani bam asibobalapha.', 'XHO22'),
(6, 18, 37, 'Wathi ngoko uPilato kuye, Hina ke, ungukumkani na wena? Waphendula uYesu wathi, Nawe uthi ndingukumkani. Mna ndizalelwe oku; ndizele oku ehlabathini, ukuze ndiyingqinele inyaniso. Bonke abangabenyaniso bayaliva ilizwi lam.', 'XHO22'),
(6, 18, 38, 'Athi uPilato kuye, Yintoni na yona inyaniso? Akuba etshilo, wabuya waphuma waya kumaYuda, athi kuwo, Mna andifumani nalinye ityala kuye.', 'XHO22'),
(6, 18, 39, 'Ke kaloku ninesiko lokuba ndinikhululele abe mnye ngepasika; ninga ke ndinganikhululela ukumkani wamaYuda na?', 'XHO22'),
(6, 18, 40, 'Babuya ngoko bonke bankqangaza, besithi, Mayingabi ngulo, mayibe nguBharabhas. Waye ke uBharabhas esisihange.', 'XHO22'),
(6, 19, 1, 'Waza ngoko uPilato wamthabatha uYesu, wamtyakatya.', 'XHO22'),
(6, 19, 2, 'Aza amasoldati aluka isithsaba sameva, ambeka entloko, amnxiba ingubo ebomvu,', 'XHO22'),
(6, 19, 3, 'besithi, Vuya, Kumkani wamaYuda! bambetha ke ngezandla.', 'XHO22'),
(6, 19, 4, 'Wabuya ngoko waphuma phandle uPilato; athi kubo, Yabonani, ndimzisa phandle kuni, ukuze nazi ukuba andifumani nalinye ityala kuye.', 'XHO22'),
(6, 19, 5, 'Waphuma phandle ngoko uYesu, enxibe isithsaba sameva nengubo ebomvu; Athi kubo uPilato, Nanko umntu.', 'XHO22'),
(6, 19, 6, 'Bathe ngoko, bakumbona ababingeleli abakhulu namadindala, bankqangaza, besithi, Mbethelele emnqamlezweni! Athi kubo uPilato, Mthabatheni nina, nimbethelele emnqamlezweni; kuba mna andifumani tyala kuye.', 'XHO22'),
(6, 19, 7, 'Amphendula amaYuda athi, Thina sinomthetho; ngomthetho wethu ke ufanele ukufa, ngokuba ezenze uNyana kaThixo.', 'XHO22'),
(6, 19, 8, 'Uthe ngoko uPilato, akuliva elo lizwi, woyika ngakumbi.', 'XHO22'),
(6, 19, 9, 'Wabuya wangena endlwini yerhuluneli, wathi kuYesu, Ungowaphi na wena? Akaphendula ke uYesu kuye.', 'XHO22'),
(6, 19, 10, 'Athi ngoko uPilato kuye, Akuthethi na kum? Akwazi na ukuba ndinegunya lokukubethelela emnqamlezweni, nokuba ndinegunya lokukukhulula?', 'XHO22'),
(6, 19, 11, 'Uphendule uYesu wathi, Unge ungenagunya nakanye kum, ukuba ubungalinikwanga livela phezulu; ngenxa yoko ondinikeleyo kuwe unesono esikhulu ngakumbi.', 'XHO22'),
(6, 19, 12, 'Ngenxa yoko wayefuna uPilato ukumkhulula; kodwa amaYuda ayenkqangaza esithi, Ukuba uthe wamkhulula lo, akusihlobo sikaKesare; bonke abazenza ukumkani bachasa uKesare.', 'XHO22'),
(6, 19, 13, 'Elivile ngoko uPilato eli lizwi, wamzisa phandle uYesu, wahlala esihlalweni sokugweba kwindawo ekuthiwa, siSandlalo saMatye;', 'XHO22'),
(6, 19, 14, 'Ke kaloku kwakuyimini yokulungiselela ipasika, ilixa ke ngathi lilelesithandathu; athi kumaYuda, Nanko uKumkani wenu!', 'XHO22'),
(6, 19, 15, 'Basuka ke bona bankqangaza, besithi, Msuse, mbethelele emnqamlezweni! Athi kubo uPilato, Ndimbethelele emnqamlezweni na uKumkani wenu? Baphendula ababingeleli abakhulu bathi, Asinakumkani ngaphandle kukaKesare.', 'XHO22'),
(6, 19, 16, 'Wandula ngoko ukumnikela kubo, ukuze abethelelwe emnqamlezweni. Bamthabatha ke uYesu, bemka naye.', 'XHO22'),
(6, 19, 17, 'Waphuma ethwele umnqamlezo wakhe waya kwindawo ekuthiwa yiNdawo yoKhakhayi, leyo kuthiwa ngesiHebhere yiGolgota,', 'XHO22'),
(6, 19, 18, 'apho bambethelela khona emnqamlezweni, nabanye naye abangabambi bebabini, ngeenxa zombini, uYesu ke ephakathi.', 'XHO22'),
(6, 19, 19, 'uPilato wabhala nombhalo, wawubeka emnqamlezweni. Kwaye ke kubhaliwe kwathiwa, UYESU waseNazarete, uKumkani wamaYuda.', 'XHO22'),
(6, 19, 20, 'Lo mbhalo ngoko into eninzi yamaYuda yawulesa; ngokuba indawo, uYesu awayebethelelwe kuyo emnqamlezweni, yayikufuphi nomzi; kwaye ke kubhalwe ngesiHebhere, nangesiGrike, nangesiRoma.', 'XHO22'),
(6, 19, 21, 'Bathi ngoko ababingeleli abakhulu bamaYuda kuPilato, Musa ukubhala ukuthi, uKumkani wamaYuda; wathi, NdinguKumkani wamaYuda.', 'XHO22'),
(6, 19, 22, 'Waphendula uPilato wathi, Endikubhalileyo ndikubhalile.', 'XHO22'),
(6, 19, 23, 'Athe ngoko amasoldati, xa abembethelele emnqamlezweni uYesu, azithabatha iingubo zakhe, enza izabelo ezine, laelo isoldati lanesabelo; kwanengubo yangaphantsi. Ke yona ingubo yangaphantsi ibingenamthungo; yayilukiwe iphela, ithabathele phezulu.', 'XHO22'),
(6, 19, 24, 'Atshono ngoko ukuthi, Masingayikrazuli, masiyenze amaqashiso, ukuba yoba yekabani na; ukuze sizaliseke isibhalo esithi, Babelana ngeengubo zam, benza amaqashiso ngesambatho sam. Amasoldati ke ngoko azenza ezi zinto.', 'XHO22'),
(6, 19, 25, 'Ke kaloku kwakumi ngasemnqamlezweni kaYesu unina, nomsakwabo bonina, uMariya umkaKleyopa, noMariya waseMagadala.', 'XHO22'),
(6, 19, 26, 'uYesu ngoko, akumbona unina, naye umfundi abemthanda emi khona, uthi kuye unina, Mfazi, nanko unyana wakho!', 'XHO22'),
(6, 19, 27, 'Aze athi kumfundi lowo, Nanko unyoko! Wathi kwakwelo lixa loo mfundi wamthabatha, wamsa ekhayeni lakhe.', 'XHO22'),
(6, 19, 28, 'Emveni koko, uYesu, esazi ukuba zonke izinto sezifezekile, ukuze sifezeke isibhalo, wathi, Ndinxaniwe.', 'XHO22'),
(6, 19, 29, 'Kwakukho isitya sizele yiviniga; bazalisa imfunxa ngeviniga, bayibeka phezu kwehisope, bayisa emlonyeni wakhe.', 'XHO22'),
(6, 19, 30, 'Xa ngoko uYesu wabeyamkele iviniga, wathi, Kugqityiwe! Wathoba intloko, wawunikela umoya wakhe.', 'XHO22'),
(6, 19, 31, 'Athe ngoko amaYuda, ekubeni yayiyimini yokulungiselela, ukuba imizimba ingahlali emnqamlezweni ngesabatha (kuba loo sabatha ibingumhla omkhulu), acela kuPilato ukuba imilenze yabo yaphulwe, babanjwe, kude.', 'XHO22'),
(6, 19, 32, 'Eza ngoko amasoldati, ayaphula okunene imilenze yowokuqala, neyomnye obebethelelwe emnqamlezweni naye.', 'XHO22'),
(6, 19, 33, 'Kodwa akufika kuYesu, akubona ukuba uselefile, akayaphula imilenze yakhe.', 'XHO22'),
(6, 19, 34, 'Lesuka elinye isoldati lamhlaba ngomkhonto ecaleni, kwaoko kwaphuma igazi namanzi.', 'XHO22'),
(6, 19, 35, 'Nalowo ubonileyo ungqinile, nobungqina bakhe buyinyaniso; naye yena uyazi ukuba uthetha inyaniso, ukuze nani nikholwe.', 'XHO22'),
(6, 19, 36, 'Ezo zinto zenzeka ukuze isibhalo sizaliseke esithi, Akuyi kwaphulwa thambo lakhe.', 'XHO22'),
(6, 19, 37, 'Kanjalo nesinye isibhalo sithi, Baya kukhangela kulowo bamhlabileyo.', 'XHO22'),
(6, 19, 38, 'Ke kaloku emveni koko, uYosefu waseArimati (engumfundi kaYesu, ke enguye ngokufihlakeleyo ngenxa yokoyika amaYuda), wacela kuPilato, ukuba awususe umzimba kaYesu. Waza uPilato wavuma; Weza ngoko, wawuthabatha umzimba kaYesu.', 'XHO22'),
(6, 19, 39, 'Kweza noNikodemo, lowo wezayo kuYesu ekuqaleni ebusuku, ephethe umxube wemore nehaloti, omalunga nekhulu leeponti.', 'XHO22'),
(6, 19, 40, 'Bawuthabatha ke umzimba kaYesu, bawubopha ngezisongelo zelinen, ndawonye nobulawu, njengoko kulisiko lokungcwaba kumaYuda.', 'XHO22'),
(6, 19, 41, 'Ke kaloku, bekukho kuloo ndawo wayebethelelwe kuyo emnqamlezweni umyezo; nasemyezweni apho bekukho ingcwaba elitsha, ekungazanga kubekwe mntu kulo.', 'XHO22'),
(6, 19, 42, 'uYesu ngoko bambeka khona apho, ngenxa yemini yokulungiselela kwamaYuda; kuba ingcwaba belikufuphi.', 'XHO22'),
(6, 20, 1, 'Ngolokuqala usuku lweveki, kuza engcwabeni uMariya waseMagadala kusasa, kusentsundu, alibone ilitye lisusiwe engcwabeni.', 'XHO22'),
(6, 20, 2, 'Agidime ngoko, eze kuSimon Petros nakomnye umfundi, lowo ubethandwa nguYesu, athi kubo, Bayisusile iNkosi engcwabeni; asazi apho bayibeke khona.', 'XHO22'),
(6, 20, 3, 'Waphuma ngoko uPetros nomnye umfundi lowo, besiya engcwabeni.', 'XHO22'),
(6, 20, 4, 'Ke kaloku baye begidima bona bobabini kunye; waza omnye umfundi lowo wamshiya ngamendu uPetros, wafika kuqala engcwabeni.', 'XHO22'),
(6, 20, 5, 'Uthe ke eqonde, wabona iingubo zelinen abesongelwe kuzo zizodwa; akangena.', 'XHO22'),
(6, 20, 6, 'Afike ngoko uSimon Petros emlandela, angene engcwabeni, azibone iingubo zelinen abesongelwe kuzo zizodwa;', 'XHO22'),
(6, 20, 7, 'neqhiya ebisentlokweni yakhe, ingadibene neelinen abesongelwe kuzo, isongiwe yodwa ndaweni ithile.', 'XHO22'),
(6, 20, 8, 'Wandula ngoko wangena nomnye umfundi, lowo ubefike kuqala engcwabeni, wabona, wakholwa;', 'XHO22'),
(6, 20, 9, 'Kuba babengekasiqondi isibhalo esithi, umelwe kukuthi avuke kwabafileyo.', 'XHO22'),
(6, 20, 10, 'Babuya ke bagoduka abafundi.', 'XHO22'),
(6, 20, 11, 'Ke yena uMariya wema ngaphandle ngasengcwabeni elila.', 'XHO22'),
(6, 20, 12, 'Wabona izithunywa zezulu ezibini, zam bethe ezimhlophe, zihleli; esinye ngasentloko, esinye ngasezinyaweni, apho wawulele khona umzimba kaYesu.', 'XHO22'),
(6, 20, 13, 'Zithi zona kuye, Mfazi, ulilelani na? Athi kubo, Ngokuba bayithathile iNkosi yam; andazi apho bayibeke khona.', 'XHO22'),
(6, 20, 14, 'Akutsho wajika, wambona uYesu emi; wayengazi ke ukuba nguYesu.', 'XHO22'),
(6, 20, 15, 'Athi uYesu kuye, Mfazi, ulilelani na? ufuna bani na? Yena, esiba ngumgcini-myezo, uthi kuye, Nkosi, ukuba umsusile apha, ndixelele apho umbeke khona; mna ndiye kumthabatha.', 'XHO22'),
(6, 20, 16, 'Athi uYesu kuye, Mariya. Wajika ke, wathi kuye, Rabhoni; oko kukuthi, Mfundisi.', 'XHO22'),
(6, 20, 17, 'Athi uYesu kuye, Ungandichukumisi; kuba andikanyuki ndiye kuBawo; ke hamba uye kubazalwana bam, uthi kubo, Ndiyenyuka, ndiya kuBawo, kuYihlo; nakuThixo wam, nakuThixo wenu.', 'XHO22'),
(6, 20, 18, 'Afike uMariya waseMagadala, ababikele abafundi ukuba uyibonile iNkosi; yathetha ezi zinto kuye.', 'XHO22'),
(6, 20, 19, 'Kwakuhlwa ngoko, ngolokuqala usuku lweveki, zakubon’ ukuba iingcango zivaliwe, apho babehlanganisene khona abafundi, ngenxa yokoyika amaYuda, wafika uYesu wema phakathi, wathi kubo, Uxolo malube nani.', 'XHO22'),
(6, 20, 20, 'Akuba etshilo wababonisa izandla necala lakhe. Bavuya ngoko abafundi bakuyibona iNkosi.', 'XHO22'),
(6, 20, 21, 'Waphinda ngoko uYesu wathi kubo, Uxolo malube nani; njengokuba uBawo endithumile mna, nam ndiyanithuma nina.', 'XHO22'),
(6, 20, 22, 'Akuba etshilo wabaphefumlela, wathi kubo, Yamkelani uMoya oyiNgcwele;', 'XHO22'),
(6, 20, 23, 'abazono nithe nazixolela, bazixolelwe; nabazono nithe nazibamba, zibambekile.', 'XHO22'),
(6, 20, 24, 'Kodwa uTomas, omnye kwabalishumi elinababini, okuthiwa nguDidimo, wayengenabo oko wafikayo uYesu.', 'XHO22'),
(6, 20, 25, 'Bathi ngoko kuye abanye abafundi, Siyibonile iNkosi. Wathi ke yena kubo, Ukuba andithanga ndilibone inxeba lezikhonkwane ezandleni zayo, ndiwufake umnwe wam enxebeni lezikhonkwane, ndisifake isandla sam ecaleni layo, andisayi kukholwa.', 'XHO22'),
(6, 20, 26, 'Baye emveni kweentsuku ezisibhozo bekwangaphakathi abafundi bakhe, wayenabo noTomas. Afike uYesu, zakubon’ ukuba zivaliwe iingcango, wema phakathi, athi, Uxolo malube nani.', 'XHO22'),
(6, 20, 27, 'Aze athi kuTomas, Ethe apha umnwe wakho, uzibone izandla zam; khawusondeze apha isandla sakho, usifake ecaleni lam; musa ukuba ngongakholwayo, yiba ngokholwayo.', 'XHO22'),
(6, 20, 28, 'Waphendula uTomas, wathi kuye, Nkosi yam, Thixo wam!', 'XHO22'),
(6, 20, 29, 'Athi uYesu kuye, Ngokuba undibonile, Tomas, ukholiwe. Banoyolo abo bangabonanga, bakholwa noko.', 'XHO22'),
(6, 20, 30, 'Mininzi ke okunene neminye imiqondiso awayenzayo uYesu emehlweni abafundi bakhe, engabhalwanga kuyo le ncwadi;', 'XHO22'),
(6, 20, 31, 'Kodwa le ibhalelwe ukuze nikholwe ukuba uYesu unguye uKristu, uNyana kaThixo, nokuze nithi, nikholwa, nibe nabo ubomi egameni lakhe.', 'XHO22'),
(6, 21, 1, 'Emva koko waphinda uYesu wazibonakalalisa kubafundi ngaselwandle lwaseTibheriya, wazibonakalisa ngale ndlela.', 'XHO22'),
(6, 21, 2, 'Babendawonye uSimon Petros, noTomas okuthiwa nguDidimo, noNataniyeli waseKana yelaseGalili, nabakaZebhedi, nabanye kubafundi bakhe bebabini.', 'XHO22'),
(6, 21, 3, 'Athi uSimon Petros kubo, Ndiya kubambisa iintlanzi. Bathi kuye, Nathi sihamba nawe. Baphuma, bangena emkhombeni kwaoko; ababamba nto ngobo busuku.', 'XHO22'),
(6, 21, 4, 'Kwakuba sekusile, wema uYesu elunxwemeni, noko abafundi bebengazi ukuba nguYesu.', 'XHO22'),
(6, 21, 5, 'Athi ngoko uYesu kubo, Bantwana, aninanto idliwayo na? Baphendula bathi kuye, Hayi.', 'XHO22'),
(6, 21, 6, 'Wathi ke yena kubo, Wuphoseni umnatha ngecala lasekunene lomkhombe, nofumana. Baphosa ke; ababa saba nako ukuwutsala, ngenxa yenkitha yeentlanzi.', 'XHO22'),
(6, 21, 7, 'Athi ngoko loo mfundi ubethandwa nguYesu kuPetros, YiNkosi. Uthe ngoko uSimon Petros, akuva ukuba yiNkosi, wabhinqa ingubo yokwaleka (kuba ebeze), waziphosa elwandle.', 'XHO22'),
(6, 21, 8, 'Ke bona abanye abafundi beza ngenqanawa; kuba bebengemgama emhlabeni, ngathi bebekubhite ezimakhulu mabini, bewurhuqa umnatha weentlanzi.', 'XHO22'),
(6, 21, 9, 'Bathi ngoko, bakuwelela emhlabeni, babona umlilo wamalahle ukhona, kukho nentlanzi kuwo, kwanesonka.', 'XHO22'),
(6, 21, 10, 'Athi kubo uYesu, Zisani kwezo ntlanzi nizibambise ngokunje.', 'XHO22'),
(6, 21, 11, 'Wakhwela uSimon Petros, wawutsalela emhlabeni umnatha, uzele ziintlanzi ezinkulu, zilikhulu elinamanci mahlanu anesithathu;', 'XHO22'),
(6, 21, 12, 'Athi kubo uYesu, Yizani nidle. Akubangakho namnye ke kubafundi waba nabuganga bakumbuza esithi, Ungubani na? besazi nje ukuba yiyo iNkosi.', 'XHO22'),
(6, 21, 13, 'Uza ke uYesu, asithabathe isonka, abanike, ayenjenjalo nentlanzi.', 'XHO22'),
(6, 21, 14, 'Oku sekukokwesithathu ezibonakalalisa uYesu kubafundi bakhe, evukile kwabafileyo.', 'XHO22'),
(6, 21, 15, 'Bakomula, athi uYesu kuSimon Petros, Simon kaYona, uyandithanda na ngaphezu kwaba? Athi kuye, Ewe, Nkosi; uyaqonda wena ukuba ndiyakuthanda. Athi kuye, Dlisa amatakane am.', 'XHO22'),
(6, 21, 16, 'Aphinde athi kuye okwesibini, Simon kaYona, uyandithanda na? Athi kuye, Ewe, Nkosi; uyaqonda wena ukuba ndiyakuthanda. Athi kuye, Dlisa izimvu zam.', 'XHO22'),
(6, 21, 17, 'Athi kuye okwesithathu, Simon kaYona, uyandithanda na? Waba buhlungu uPetros, kuba ethe kuye okwesithathu, Uyandithanda na? Wathi kuye, Nkosi, uyazazi zonke izinto wena; uyaqonda wena ukuba ndiyakuthanda. Athi uYesu kuye, Dlisa izimvu zam.', 'XHO22'),
(6, 21, 18, 'Inene, inene, ndithi kuwe, Oko ubusemtsha ubuzibhinqisa ngokwakho, uhambe apho uthanda khona; kodwa xa uthe waluphala, uya kuzolula izandla zakho, ubhinqiswe ngomnye, akuse apho uthanda khona, hayi.', 'XHO22'),
(6, 21, 19, 'Wakutsho ke oku, eqondisa ukuba kukufa kuni na abeya kuzukisa uThixo ngako. Akutsho wathi kuye, Ndilandele.', 'XHO22'),
(6, 21, 20, 'Ejikile ke uPetros, ubone umfundi obethandwa nguYesu elandela, owayama esifubeni sakhe esidlweni, wathi, Nkosi, ngubani na okungcatshayo?', 'XHO22'),
(6, 21, 21, 'uPetros akumbona lowo, uthi kuYesu: “Nkosi, lo mfo uza kwenza ntoni na?', 'XHO22'),
(6, 21, 22, 'Athi uYesu kuye, Ukuba ndithi ndithande ukuba yena ahlale ndide ndifike, iyini na loo nto kuwe? ndilandele wena.', 'XHO22'),
(6, 21, 23, 'Laphuma ngoko eli lizwi, laya kubazalwana, ukuba loo mfundi akayi kufa. Waye uYesu engatshongo kuye ukuthi, Akayi kufa; wathi, Ukuba ndithi ndithande ukuba yena ahlale ndide ndifike, iyini na loo nto kuwe?', 'XHO22'),
(6, 21, 24, 'Nguye lo umfundi ongqinayo ngezi zinto, wazibhala ezi zinto; kanjalo siyazi ukuba ubungqina bakhe buyinene.', 'XHO22'),
(6, 21, 25, 'Kukho ke nezinye izinto ezininzi awazenzayo uYesu, ezingathi zithe zabhalwa ngazinye, ndithi nehlabathi eli alingezibambi iincwadi ezingabhalwayo. Amen.', 'XHO22'),
(7, 1, 1, 'Umbhalo wokuqala ndawenza, Tiyofilo, ngazo zonke izinto awaqala ngazo uYesu ukuzenza kwanokuzifundisa,', 'XHO22'),
(7, 1, 2, 'kwada kwayimini awanyuswa ngayo, emveni kokuba ebawisele umthetho ngoMoya oyiNgcwele abapostile awabanyulayo;', 'XHO22'),
(7, 1, 3, 'awathi kanjalo, emveni kokuva kwakhe ubunzima, wazimisa ephila, ngobungqina obuninzi obungenasiphako, ebonakala kubo iintsuku ezimashumi mane, ethetha izinto ezingabo ubukumkani bukaThixo.', 'XHO22'),
(7, 1, 4, 'Kwathi, ehlanganisene nabo, wabathethela ngelithi, mabangaphumi eYerusalem, balilinde idinga likaBawo, enalivayo kum.', 'XHO22'),
(7, 1, 5, 'Kuba uYohane okunene wabhaptiza ngamanzi; kodwa niya kubhaptizwa ngoMoya oyiNgcwele, kungekabi kade emva kwezi ntsuku.', 'XHO22'),
(7, 1, 6, 'Bona ke ngoko, bebuthelene, babembuza besithi, Nkosi, wobubuyisela ngeli xesha na ubukumkani kuSirayeli?', 'XHO22'),
(7, 1, 7, 'Uthe ke kubo, Asikokwenu ukwazi amaxesha nokuba ngamathuba, awamise ngelakhe igunya uBawo.', 'XHO22'),
(7, 1, 8, 'Niya kwamkela ke amandla, akubon’ ukuba uMoya oyiNgcwele uhlile phezu kwenu; nibe ngamangqina am eYerusalem, kwanakulo lonke elakwaYuda, nelaseSamariya, kude kuse nasekupheleni komhlaba.', 'XHO22'),
(7, 1, 9, 'Ezithethile ezi zinto, bakubon’ ukuba bakhangele, wanyuswa; lathi ilifu lemka naye emehlweni abo.', 'XHO22'),
(7, 1, 10, 'Bakuba beqwalasele ezulwini, njengoko wayenyuka, kwabonakala kumi ngakubo amadoda amabini, eneengubo ezimhlophe;', 'XHO22'),
(7, 1, 11, 'ayesithi ke, Madoda, maGalili, nimeleni na, nijonge ezulwini? Lo Yesu unyusiweyo kuni wasiwa emazulwini, uya kuza ngolu hlobo nimbone esiya ngalo emazulwini.', 'XHO22'),
(7, 1, 12, 'Baza babuyela eYerusalem, bevela entabeni ekuthiwa yeyemiNquma, ekufuphi neYerusalem, uhambo lwesabatha.', 'XHO22'),
(7, 1, 13, 'Bakuba bengenile, banyukela kwigumbi eliphezulu, apho kwakuhlala uPetros, noYakobi, noYohane, noAndreya, uFilipu, noTomas, uBhartolomeyu, noMateyu, noYakobi ka-Alifeyu, noSimon Zelote, uYuda, umzalwana kaYakobi.', 'XHO22'),
(7, 1, 14, 'Aba bonke babezingisa ngamxhelo mnye ekuthandazeni nasekukhungeni, ndawonye nabafazi, noMariya unina kaYesu, kwandawonye naba zalwana bakhe.', 'XHO22'),
(7, 1, 15, 'Ngaloo mihla wema uPetros phakathi kwabafundi, wathi, (inani lamagama xa lihlangene lingathi lilikhulu elinamanci mabini);', 'XHO22'),
(7, 1, 16, 'Madoda, bazalwana, esi sibhalo simelwe kukuba sizaliseke, sathethwayo ngaphambili nguMoya oyiNgcwele ngomlomo kaDavide ngaye uYuda, owaba ngumkhokeli wabo bambambayo uYesu;', 'XHO22'),
(7, 1, 17, 'ngokuba wayebalelwe kuthi, wasizuza isabelo solu lungiselelo.', 'XHO22'),
(7, 1, 18, 'Lowo ke ngoko wazuza umhlaba ngomvuzo wokungalungisi; wawa ngobuso, wagqabhuka phakathi, zaphalala zonke izibilini zakhe.', 'XHO22'),
(7, 1, 19, 'Kwazeka oko kubo bonke abemi baseYerusalem; wada loo mhlaba ngeyabo intetho kuthiwe yiAkeldama, oko kukuthi, ngumhlaba wegazi.', 'XHO22'),
(7, 1, 20, 'Kuba kubhaliwe encwadini yeeNdumiso kwathiwa, Umzi wakhe mawube yintlango, Kungabikho umiyo kuwo; Buthi nobuveleli bakhe buthatyathwe ngomnye.', 'XHO22'),
(7, 1, 21, 'Kungoko athe, kula madoda abehamba nathi ngalo lonke ixesha, yakuba iNkosi uYesu ibingena iphuma phakathi kwethu,', 'XHO22'),
(7, 1, 22, 'iqalele elubhaptizweni lukaYohane, kwada kwayimini awanyuswa ngayo kuthi, kumele ukuba kubekho ongomnye, eya kuba lingqina lokuvuka kwakhe, kunye nathi.', 'XHO22'),
(7, 1, 23, 'Bamisa ababini, uYosefu ogama linguBharsabhas, ogama limbi linguYusto, noMatiya.', 'XHO22'),
(7, 1, 24, 'Bathandaza bathi, Wena, Nkosi, wena uzaziyo iintliziyo zabantu bonke, bonisa ukuba ngaba babini umnyulileyo;', 'XHO22'),
(7, 1, 25, 'ukuze asithabathe isabelo solu lungiselelo nobupostile, awaphambukayo kubo uYuda, ukuze aye kweyakhe indawo.', 'XHO22'),
(7, 1, 26, 'Bawisa amaqashiso abo; laphuma iqashiso noMatiya; wabalelwa kwabapostile abalishumi elinamnye.', 'XHO22'),
(7, 2, 1, 'Ke kaloku, ekuzalisekeni kwawo umhla wepentekoste, baye bonke bemxhelo mnye ndaweni-nye.', 'XHO22'),
(7, 2, 2, 'Kwaza ngequbuliso kwaphuma ezulwini isandi esinjengesomoya ovuthuza ngamandla, sayizalisa yonke indlu ababehleli kuyo.', 'XHO22'),
(7, 2, 3, 'Kwabonakala kubo iilwimi ezabekayo, ngathi zezomlilo, lwaolo lwaolo lwahlala phezu kwabo bonke ngabanye.', 'XHO22'),
(7, 2, 4, 'Bazaliswa bonke nguMoya oyiNgcwele, baqala ukuthetha ngalwimi zimbi, njengoko uMoya wayebanika ukuba bathethe baphimisele.', 'XHO22'),
(7, 2, 5, 'Ke kaloku kwakumi eYerusalem apho amaYuda, amadoda awoyika uThixo, evela kuzo zonke iintlanga eziphantsi kwezulu.', 'XHO22'),
(7, 2, 6, 'Sithe ke sakubakho esi sandi, yabuthelana inkitha, yadubadubeka, ngokuba bonke ngabanye babebeva bona bethetha ngezakomawabo iintetho.', 'XHO22'),
(7, 2, 7, 'Bathi nqa ke bonke, bamangaliswa, wathi omnye komnye, Kanene aba bonke bathethayo asingamaGalili na?', 'XHO22'),
(7, 2, 8, 'Kwenzeka njani ukuba sibeve sonke ngabanye bethetha ngezakomawethu iintetho, esazalelwa kuzo?', 'XHO22'),
(7, 2, 9, 'amaPati, namaMedi, namaElam, nabemi baseMesopotami, nakwelakwaYuda, nakwelaKapadoki, kwelasePonto, nelaseAsiya;', 'XHO22'),
(7, 2, 10, 'elaseFrigi, nelasePamfili, kwelaseJiputa, nakwelaseLibhiya, malunga neKirene, nabasemzini abaseRoma, abangamaYuda nabangeni ebuYudeni;', 'XHO22'),
(7, 2, 11, 'AmaKrete nama-Arabhi, siyabeva bethetha ngeelwimi zethu imisebenzi ebalulekileyo kaThixo.', 'XHO22'),
(7, 2, 12, 'Bathi nqa ke bonke, bakhohlwa, omnye esithi komnye, Azi, ingaba ifuna ukuba yintoni na le nto?', 'XHO22'),
(7, 2, 13, 'Baye abanye bebagculela, besithi, Aba bazele yiwayini entsha;', 'XHO22'),
(7, 2, 14, 'Ke kaloku uPetros, esuke wema nabalishumi elinamnye, waliphakamisa izwi lakhe, wathi kubo, Madoda, maYuda, nani nonke nimiyo eYerusalem, makwazeke oku kuni, niwabekele indlebe amazwi am.', 'XHO22'),
(7, 2, 15, 'Aba abanxilile, njengoko nina niba kunjalo, kuba kuselilixa lesithathu lemini.', 'XHO22'),
(7, 2, 16, 'Kodwa oku koko kwathethwayo ngomprofeti uYoweli.', 'XHO22'),
(7, 2, 17, 'Kuya kuthi ngemihla yokugqibela, utsho uThixo, ndithulule uMoya wam phezu kwenyama yonke, baprofete oonyana benu neentombi zenu, amadodana enu abone imibono, namadoda enu amakhulu aphuphe amaphupha;', 'XHO22'),
(7, 2, 18, 'Naphezu kwabakhonzi bam, naphezu kwabakhonzazana bam, ngaloo mihla ndiya kumthulula uMoya wam; kwaye baya kuprofeta.', 'XHO22'),
(7, 2, 19, 'Ndiya kwenza izimanga ezulwini phezulu, nemiqondiso emhlabeni phantsi; igazi, nomlilo, nentshinyela yomsi;', 'XHO22'),
(7, 2, 20, 'Ilanga liya kujikwa libe mnyama, nenyanga ibe ligazi, phambi kokuba ifike imini yeNkosi enkulu.', 'XHO22'),
(7, 2, 21, 'Baya kuthi bonke abasukuba belinqula igama leNkosi basindiswe.', 'XHO22'),
(7, 2, 22, 'Madoda, maSirayeli, waveni la mazwi; uYesu waseNazarete, indoda eqondakalisiweyo nguThixo kuni, ngemisebenzi yamandla, nangezimanga, nangemiqondiso, awayenzayo uThixo ngayo phakathi kwenu, njengokuba nisazi nani ngokwenu:', 'XHO22'),
(7, 2, 23, 'yena lowo, ekhutshwe ngecebo elimisiweyo likaThixo nangokwazi kwakhe kwenxa engaphambili uThixo, namthabatha, nambethelela emnqamlezweni, namsika ngezandla zabangendawo;', 'XHO22'),
(7, 2, 24, 'athe uThixo wamvusa, eyikhulule inimba yokufa, kuba ebengenako ukubanjwa kuko.', 'XHO22'),
(7, 2, 25, 'Kuba uDavide uthi, esingisele kuye, Ndahlala ndiyibona iNkosi phambi kwamehlo am, Ngokuba ingasekunene kwam, ukuze ndingashukunyiswa.', 'XHO22'),
(7, 2, 26, 'Ngenxa yoko yaba nemihlali intliziyo yam, lwagcoba ulwimi lwam; Ngaphezu koko ke nenyama yam iya kuphumla inethemba;', 'XHO22'),
(7, 2, 27, 'Ngokuba akuyi kuwushiya umphefumlo wam kwelabafileyo, Kananjalo akuyi kumyekela oyiNgcwele wakho ukuba abone ukubola.', 'XHO22'),
(7, 2, 28, 'Wandazisa iindlela zobomi; Uya kundizalisa ngemihlali ngokubona ubuso bakho.', 'XHO22'),
(7, 2, 29, 'Madoda, bazalwana, mandivunyelwe ndithethe kuni ngokuphandle ngomawokhulu uDavide, ukuba wafa wangcwatywa; nengcwaba lakhe likho kuthi unanamhla.', 'XHO22'),
(7, 2, 30, 'Ngoko, ekubeni engumprofeti, esazi nokuba uThixo wamfungela isifungo esithi, esiqhameni sesinqe sakhe ngokwenyama, uya kuvelisa uKristu, ukuba ahlale etroneni yakhe;', 'XHO22'),
(7, 2, 31, 'Ebonile ngenxa engaphambili, wathetha ngako ukuvuka kukaKristu, ukuba umphefumlo wakhe ungashiywanga kwelabafileyo nenyama yakhe ingabonanga kubola.', 'XHO22'),
(7, 2, 32, 'Lo Yesu uThixo wamvusa, esingamangqina ako oko thina sonke.', 'XHO22'),
(7, 2, 33, 'Ephakanyiselwe phezulu nje ngasekunene kukaThixo, elamkele kuye uYise idinga loMoya oyiNgcwele, uthulule le nto niyibonayo, niyivayo ngoku.', 'XHO22'),
(7, 2, 34, 'Kuba uDavide, enganyukanga waya emazulwini, ke uthi ngokwakhe, Yathi iNkosi eNkosini yam, Hlala ngasekunene kwam;', 'XHO22'),
(7, 2, 35, 'Ndide iintshaba zakho ndizenze isihlalo seenyawo zakho.', 'XHO22'),
(7, 2, 36, 'Ngoko mayazi ngokuqinisekileyo yonke indlu kaSirayeli, ukuba uThixo wamenza iNkosi noKristu, kwayena lo Yesu nambethelelayo emnqamlezweni nina.', 'XHO22'),
(7, 2, 37, 'Ke kaloku bathe, bakukuva oko, bahlabeka entliziyweni, bathi kuPetros nakwabanye abapostile, Madoda, bazalwana, sithini na?', 'XHO22'),
(7, 2, 38, 'Wathi uPetros kubo, Guqukani, nibhaptizwe nonke ngabanye egameni likaYesu Kristu, ukuze kuxolelwe izono, namkele isipho soMoya oyiNgcwele.', 'XHO22'),
(7, 2, 39, 'Kaloku idinga likuni, nakubantwana benu, nakubo bonke abakude, esukuba iNkosi uThixo wethu iya kubabiza.', 'XHO22'),
(7, 2, 40, 'Ngamanye amazwi amaninzi wayeqononondisa, ebavuselela esithi, Zisindiseni kwesi sizukulwana sigwenxa.', 'XHO22'),
(7, 2, 41, 'Bathe ke ngoko, abalamkele ilizwi lakhe ngovuyo, babhaptizwa; kongezelelwa kwangaloo mini imiphefumlo engathi imawaka mathathu.', 'XHO22'),
(7, 2, 42, 'Bazingisa ukuzingisa emfundisweni yabapostile, nasebudlelaneni, nasekuqhekezeni isonka, nasemithandazweni.', 'XHO22'),
(7, 2, 43, 'Ke kaloku kwabakho ukoyika kwimiphefumlo yonke; kwenzeka izimanga ezininzi kwanemiqondiso ngabapostile.', 'XHO22'),
(7, 2, 44, 'Ke kaloku bonke ababekholwa babendawonye, benobudlelane ngeento zonke.', 'XHO22'),
(7, 2, 45, 'Baye bethengisa nangemihlaba nangemihlaba yabo, babele bonke abantu njengoko ebesukuba eswele ngako.', 'XHO22'),
(7, 2, 46, 'Babezingisa ukuhlala bemxhelo mnye etempileni imihla ngemihla, beqhekeza isonka kwizindlu ngezindlu, besidla ukudla benemigcobo, benolungelelwano lwentliziyo,', 'XHO22'),
(7, 2, 47, 'bemdumisa uThixo, benonelelwe ngabantu bonke. Yaye iNkosi isongeza ebandleni imihla ngemihla abasindiswayo.', 'XHO22'),
(7, 3, 1, 'Ke kaloku, uPetros, noYohane babenyuka bendawonye, besiya etempileni ngexesha lomthandazo, ililixa lesithoba.', 'XHO22'),
(7, 3, 2, 'Kwaye ke kuthwelwe ndoda ithile, ibisisiqhwala kwasekuzalweni kwayo, ababeyibeka imihla ngemihla emnyango wetempile, lowo kuthiwa Mhle, ukuba icele amalizo kwabangenayo etempileni;', 'XHO22'),
(7, 3, 3, 'eyathi, yakubona uPetros noYohane beza kungena etempileni, yacela ukuba bayilize.', 'XHO22');
